(window.webpackJsonp=window.webpackJsonp||[]).push([[533],{555:function(p){p.exports={chapterTitle:null,title:null,content:"<article><p>\u770b\u7740\u743c\u6069\u7684\u795e\u60c5\uff0c\u624e\u745e\u5c14\u5ffd\u7136\u773c\u795e\u9eef\u4e86\u9eef\uff0c\u504f\u8fc7\u5934\u53bb\u3002\u5f53\u5979\u518d\u5ea6\u628a\u8138\u8f6c\u56de\u6765\u7684\u65f6\u5019\uff0c\u53c8\u5df2\u7ecf\u662f\u7b11\u9765\u5982\u82b1\uff0c\u707f\u70c2\u65e0\u6bd4\u3002\u201c\u597d\u5566\uff0c\u4e0d\u6d6a\u8d39\u65f6\u95f4\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u770b\u4eba\u5bb6\u5973\u5b69\u5b50\u90fd\u7b49\u6025\u4e86\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u5728\u5973\u5deb\u5e08\u8eab\u4e0a\u7eb5\u60c5\u9a70\u9a8b\u7684\u65f6\u5019\uff0c\u743c\u6069\u4e5f\u5e76\u6ca1\u6709\u5fd8\u8bb0\u81ea\u5df1\u7684\u771f\u6b63\u76ee\u7684\uff0c\u4ed6\u4e00\u70b9\u70b9\u5730\u53d1\u6398\u7740\u7ef4\u82e5\u62c9\u7684\u4f53\u5185\u6df1\u5904\uff0c\u610f\u8bc6\u4ee5\u4e24\u4eba\u4ea4\u5408\u5904\u4e3a\u4e2d\u5fc3\uff0c\u5982\u86db\u7f51\u822c\u8513\u5ef6\u5f00\u6765\uff0c\u5c06\u5973\u5deb\u5e08\u5305\u88f9\u5728\u5185\uff0c\u641c\u5bfb\u4ed6\u60f3\u8981\u7684\u76ee\u6807\u3002</p><p>\u7136\u540e\u4ed6\u627e\u5230\u4e86\u3002</p><p>\u5265\u5f00\u5c42\u5c42\u5c01\u9501\uff0c\u743c\u6069\u201c\u770b\u89c1\u201d\u4e86\u67d0\u4e2a\u4e1c\u897f\uff0c\u5b83\u50cf\u662f\u4e00\u56e2\u4e0d\u65ad\u5730\u53d8\u5e7b\u5f62\u6001\u7684\u96fe\u6c14\uff0c\u4ece\u5185\u90e8\u53d1\u51fa\u4e03\u5f69\u6591\u6593\u7684\u4eae\u5149\uff0c\u5728\u5973\u5deb\u5e08\u7684\u610f\u8bc6\u4e2d\u6e38\u79fb\u4e0d\u5b9a\u3002\u65e0\u9700\u4efb\u4f55\u89e3\u91ca\uff0c\u743c\u6069\u4fbf\u660e\u767d\u4e86\u90a3\u662f\u4ec0\u4e48\u3002</p><p>\u5deb\u5e08\u4e4b\u795e\u963f\u7956\u65af\u7684\u795e\u529b\uff1a\u8679\u96fe\u3002</p><p>\u636e\u624e\u745e\u5c14\u8bf4\uff0c\u963f\u7956\u65af\u4ee5\u51e1\u4eba\u4e4b\u8eab\u767b\u4e0a\u795e\u4f4d\u540e\uff0c\u81ea\u201c\u6e90\u201d\u4e2d\u63d0\u53d6\u6700\u7cbe\u7cb9\u7684\u9b54\u529b\uff0c\u4e0e\u81ea\u5df1\u7684\u771f\u540d\u76f8\u878d\u5408\uff0c\u5851\u9020\u51fa\u201c\u8679\u96fe\u201d\uff0c\u5176\u5177\u8c61\u4fbf\u662f\u8fd9\u6837\u4e00\u56e2\u4e03\u5f69\u6591\u6593\u7684\u53d8\u5e7b\u96fe\u6c14\u3002\u81f3\u4e8e\u5176\u6548\u80fd\u5982\u4f55\uff0c\u5bf9\u4e8e\u51e1\u4eba\u6709\u4f55\u4f5c\u7528\uff0c\u9b54\u59ec\u4e5f\u4e0d\u662f\u5f88\u6e05\u695a\uff0c\u81ea\u4ece\u6570\u5343\u5e74\u524d\u5979\u4ece\u7269\u8d28\u754c\u8fd4\u56de\u5730\u72f1\uff0c\u5c31\u4e00\u76f4\u5728\u4e0b\u5c42\u754c\u6d3b\u52a8\uff0c\u5fd9\u4e8e\u548c\u6df1\u6e0a\u6076\u9b54\u3001\u5929\u5802\u795e\u4f7f\u4eec\u8840\u6218\uff0c\u548c\u963f\u7956\u65af\u53ca\u5176\u9009\u6c11\u90fd\u672a\u6253\u8fc7\u4ea4\u9053\uff0c\u53ea\u542c\u8bf4\u662f\u80fd\u591f\u5f3a\u5316\u5deb\u5e08\u7684\u80fd\u529b\u2014\u2014\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u8fd9\u662f\u610f\u6599\u4e4b\u4e2d\u7684\u7b54\u6848\u3002</p><p>\u6240\u8c13\u795e\u529b\uff0c\u6309\u7167\u901a\u884c\u7684\u5b9a\u4e49\uff0c\u662f\u795e\u7947\u4e0e\u6cd5\u5219\u76f8\u878d\u5408\uff0c\u6c47\u805a\u4fe1\u4ef0\uff0c\u6267\u638c\u6743\u67c4\uff0c\u5176\u529b\u91cf\u7684\u5177\u73b0\u3002\u4e5f\u5373\u662f\u8bf4\uff0c\u795e\u529b\u662f\u795e\u7947\u4e4b\u804c\u80fd\u7684\u6295\u5c04\uff0c\u5b83\u4eec\u4e4b\u95f4\u662f\u76f8\u4e92\u5bf9\u5e94\u7684\u5173\u7cfb\u3002\u5c31\u743c\u6069\u6240\u77e5\u9053\u7684\u60c5\u5f62\u6765\u770b\uff0c\u8fd9\u4e2a\u8bf4\u6cd5\u662f\u6709\u9053\u7406\u7684\uff0c\u6bd4\u5982\u8bf4\u4ed6\u6b64\u524d\u6240\u7a83\u53d6\u3001\u4f7f\u7528\u7684\u4e09\u79cd\u795e\u529b\uff0c\u6027\u8d28\u5404\u5f02\uff0c\u800c\u4e14\u90fd\u4e0e\u5176\u795e\u7947\u7684\u804c\u80fd\u76f8\u5173\uff0c\u5df4\u5c14\u7684\u529b\u91cf\u81ea\u7136\u662f\u201c\u6740\u622e\u201d\uff0c\u838e\u5c14\u7684\u5f71\u706b\u80fd\u591f\u8ba9\u4ed6\u5c01\u95ed\u5fc3\u7075\uff0c\u5409\u52d2\u4eca\u7684\u529b\u91cf\u5177\u73b0\u4e3a\u201c\u96f7\u9706\u201d\uff0c\u743c\u6069\u5bf9\u8fd9\u4f4d\u6614\u65e5\u7684\u6069\u745f\u4e3b\u795e\u4e86\u89e3\u4e0d\u591a\uff0c\u4f46\u542c\u8bf4\u4ed6\u5728\u4f4d\u7684\u65f6\u5019\uff0c\u786e\u5b9e\u6267\u638c\u96f7\u9706\u3001\u98ce\u66b4\u4e4b\u7c7b\u7684\u795e\u804c\u3002\u963f\u7956\u65af\u65e2\u7136\u662f\u5deb\u5e08\u4e4b\u795e\uff0c\u5176\u795e\u529b\u4e0e\u201c\u5f3a\u5316\u5deb\u5e08\u80fd\u529b\u201d\u6709\u5173\uff0c\u8fd9\u662f\u5408\u60c5\u5408\u7406\u7684\u63a8\u65ad\u3002</p><p>\u4f46\u5177\u4f53\u662f\u5982\u4f55\u201c\u5f3a\u5316\u201d\u6cd5\u5462\uff1f</p><p>\u5bf9\u4e8e\u8fd9\u4e2a\u95ee\u9898\uff0c\u743c\u6069\u5c31\u63a8\u65ad\u4e0d\u51fa\u6765\u4e86\uff0c\u56e0\u4e3a\u4e25\u91cd\u7f3a\u4e4f\u8d44\u6599\u3002\u6bd5\u7adf\u963f\u7956\u65af\u53ea\u662f\u4e2a\u534a\u795e\uff0c\u4f9d\u9644\u4e8e\u9b54\u6cd5\u5973\u795e\uff0c\u6559\u4f1a\u5c0f\uff0c\u4fe1\u5f92\u5c11\uff0c\u51e0\u4e4e\u6ca1\u4ec0\u4e48\u5f71\u54cd\u529b\uff0c\u5916\u754c\u5bf9\u5176\u4e5f\u6ca1\u591a\u5c11\u4e86\u89e3\u3002\u4e0d\u8fc7\u65e0\u6240\u8c13\uff0c\u65e2\u7136\u63a8\u65ad\u4e0d\u51fa\uff0c\u90a3\u5b9e\u9645\u68c0\u9a8c\u4e00\u4e0b\uff0c\u4e0d\u5c31\u6e05\u695a\u4e86\u3002</p><p>\u5c0f\u5fc3\u7ffc\u7ffc\u5730\uff0c\u743c\u6069\u7684\u610f\u8bc6\u4e00\u70b9\u70b9\u63a5\u8fd1\u201c\u8679\u96fe\u201d\uff0c\u6162\u6162\u6e17\u900f\u5176\u4e2d\uff0c\u7136\u540e\u5206\u5272\u3001\u7f20\u7ed5\u3001\u622a\u53d6\u3001\u6eb6\u5316\u3002\u7c7b\u4f3c\u7684\u4e8b\u60c5\uff0c\u4ed6\u5df2\u7ecf\u6709\u8fc7\u51e0\u6b21\u7ecf\u9a8c\uff0c\u7b97\u5f97\u4e0a\u9a7e\u8f7b\u5c31\u719f\u4e86\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u4ed6\u8fd8\u662f\u4e0d\u7531\u81ea\u4e3b\u5730\u6709\u4e9b\u7d27\u5f20\uff0c\u56e0\u4e3a\u5728\u6b64\u4e4b\u524d\uff0c\u65e0\u8bba\u5df4\u5c14\u8fd8\u662f\u5409\u52d2\u4eca\uff0c\u4ed6\u4eec\u90fd\u662f\u5df2\u7ecf\u9668\u843d\u7684\u795e\u7947\uff0c\u5176\u795e\u529b\u5c5e\u4e8e\u65e0\u4e3b\u4e4b\u7269\uff0c\u800c\u5f71\u706b\u867d\u7136\u6709\u4e3b\uff0c\u5374\u662f\u838e\u5c14\u9001\u7ed9\u743c\u6069\u7684\u793c\u7269\uff0c\u4e8b\u5148\u5df2\u7ecf\u5f97\u5230\u5176\u8bb8\u53ef\u4e86\u3002\u6240\u4ee5\u4e25\u683c\u6765\u8bf4\uff0c\u53ea\u6709\u8fd9\u4e00\u6b21\uff0c\u624d\u662f\u771f\u6b63\u7684\u201c\u7a83\u53d6\u201d\uff0c\u4e43\u662f\u81ea\u4e00\u4f4d\u795e\u7947\u624b\u4e2d\u7a83\u53d6\u5176\u6700\u672c\u8d28\u7684\u529b\u91cf\u3002</p><p>\u201c\u4e0d\u5bf9\uff0c\u8fd9\u4e0d\u662f\u2018\u7a83\u53d6\u2019\uff0c\u201d\u4f5c\u4e3a\u8d44\u6df1\u5927\u9b54\u9b3c\uff0c\u624e\u745e\u5c14\u4e60\u60ef\u6027\u5730\u5bf9\u743c\u6069\u7684\u63aa\u8f9e\u53d1\u8868\u5f02\u8bae\uff0c\u201c\u4f60\u8fd9\u5206\u660e\u662f\u5728\u62a2\u52ab\u3002\u201d</p><p>\u201c\u5514\uff0c\u4e5f\u662f\u554a\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u662f\u963f\u7956\u65af\u7684\u9009\u6c11\uff0c\u9009\u6c11\u6240\u62e5\u6709\u7684\u795e\u529b\u4e0e\u795e\u5b50\u6709\u6240\u4e0d\u540c\uff0c\u5b83\u7531\u795e\u7947\u8d50\u4e88\uff0c\u4e5f\u53ef\u4ee5\u7531\u795e\u7947\u968f\u65f6\u6536\u56de\u3002\u5b9e\u9645\u4e0a\uff0c\u5982\u679c\u4e0d\u662f\u5728\u8fd9\u79cd\u7279\u6b8a\u73af\u5883\u4e0b\uff0c\u743c\u6069\u60f3\u7a83\u53d6\u963f\u7956\u65af\u7684\u795e\u529b\u662f\u5f88\u96be\u7684\uff0c\u6216\u8005\u8bf4\u57fa\u672c\u4e0d\u53ef\u80fd\uff0c\u795e\u7947\u867d\u7136\u672a\u5fc5\u5168\u77e5\u5168\u80fd\uff0c\u4f46\u5bf9\u81ea\u5df1\u7684\u9009\u6c11\uff0c\u5bf9\u81ea\u5df1\u7684\u795e\u529b\uff0c\u5fc5\u5b9a\u662f\u975e\u5e38\u5173\u6ce8\u7684\u3002\u4e00\u65e6\u53d1\u73b0\u60c5\u51b5\u6709\u5f02\uff0c\u5927\u53ef\u4ee5\u7acb\u523b\u5c06\u795e\u529b\u6536\u56de\uff0c\u907f\u514d\u635f\u5931\u3002\u4f46\u5982\u4eca\u51ef\u745f\u7433\u4f7f\u7528\u7b2c\u4e94\u79d8\u5668\uff0c\u5c06\u6574\u4e2a\u9634\u5f71\u8c37\u7b3c\u7f69\u5728\u201c\u5c01\u7edd\u201d\u4e4b\u4e2d\uff0c\u963b\u9694\u5185\u5916\uff0c\u795e\u7947\u4e5f\u96be\u4ee5\u53ca\u65f6\u6536\u56de\u6388\u4e88\u9009\u6c11\u7684\u795e\u529b\u2014\u2014\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u838e\u5c14\u624d\u4f1a\u5c06\u57cb\u85cf\u5728\u624e\u745e\u5c14\u4f53\u5185\u7684\u5f71\u706b\u8f6c\u8d60\u7ed9\u743c\u6069\uff0c\u505a\u4e2a\u987a\u6c34\u4eba\u60c5\u3002\u8003\u8651\u5230\u51ef\u745f\u7433\u4e0e\u743c\u6069\u7684\u5173\u7cfb\uff0c\u8fd9\u663e\u7136\u5df2\u7ecf\u4e0d\u9002\u5408\u79f0\u4e4b\u4e3a\u7a83\u53d6\uff0c\u800c\u662f\u660e\u76ee\u5f20\u80c6\u5730\u5c06\u4e3b\u4eba\u62e6\u5728\u5916\u9762\uff0c\u6d17\u52ab\u5176\u5bb6\u4e2d\u8d22\u7269\u3002</p><p>\u7a83\u53d6\u4e5f\u7f62\uff0c\u62a2\u52ab\u4e5f\u597d\uff0c\u743c\u6069\u548c\u624e\u745e\u5c14\u5176\u5b9e\u90fd\u4e0d\u5728\u610f\uff0c\u53cd\u6b63\u5c31\u90a3\u4e48\u4e00\u56de\u4e8b\u3002\u76f8\u6bd4\u8d77\u6765\uff0c\u6218\u5229\u54c1\u624d\u662f\u771f\u6b63\u503c\u5f97\u5173\u5fc3\u7684\u4e1c\u897f\u3002\u201c\u600e\u4e48\u6837\uff1f\u201d\u624e\u745e\u5c14\u95ee\uff0c\u201c\u6210\u529f\u4e86\u5417\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u4f38\u51fa\u4e00\u6839\u624b\u6307\uff0c\u5fae\u5f31\u7684\u5149\u8292\u81ea\u6307\u5c16\u4eae\u8d77\u6765\uff0c\u6162\u6162\u81a8\u80c0\uff0c\u53d8\u6210\u4e00\u5c0f\u56e2\u7a00\u8584\u7684\u5f69\u8272\u96fe\u6c14\u3002\u201c\u4e0d\u8fc7\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u6ca1\u6709\u611f\u53d7\u5230\u4efb\u4f55\u2018\u5f3a\u5316\u2019\u3002\u201d</p><p>\u201c\u6ca1\u6709\u5f3a\u5316\uff1f\u201d\u9b54\u59ec\u76b1\u7709\uff0c\u201c\u4e0d\u4f1a\u5427\u3002\u201d</p><p>\u201c\u771f\u7684\u3002\u201d</p><p>\u743c\u6069\u8bf4\u7740\uff0c\u968f\u624b\u91ca\u653e\u4e86\u4e00\u4e2a\u7b80\u5355\u7684\u620f\u6cd5\uff0c\u6b63\u5982\u4ed6\u6240\u8a00\uff0c\u65e0\u8bba\u662f\u65bd\u6cd5\u901f\u5ea6\u3001\u6cd5\u672f\u5a01\u529b\u6216\u8005\u5176\u4ed6\u65b9\u9762\uff0c\u90fd\u6ca1\u6709\u4ec0\u4e48\u660e\u663e\u53d8\u5316\u3002\u201c\u8fd9\u6837\u554a\uff0c\u201d\u624e\u745e\u5c14\u601d\u7d22\u7740\uff0c\u201c\u90a3\u5c31\u5e94\u8be5\u662f\u53e6\u6709\u4ed6\u7528\u4e86\u3002\u201d</p><p>\u201c\u6216\u8bb8\u5427\u3002\u201d</p><p>\u963f\u7956\u65af\u7684\u795e\u529b\u201c\u8679\u96fe\u201d\uff0c\u7528\u9014\u662f\u5f3a\u5316\u5deb\u5e08\u7684\u80fd\u529b\u2014\u2014\u8fd9\u4e2a\u5224\u65ad\u5e94\u8be5\u662f\u6ca1\u9519\u7684\u3002\u4f46\u6240\u8c13\u201c\u5deb\u5e08\u7684\u80fd\u529b\u201d\uff0c\u539f\u672c\u5c31\u662f\u4e2a\u975e\u5e38\u5bbd\u6cdb\u7684\u6982\u5ff5\uff0c\u5e76\u4e0d\u4ec5\u4ec5\u6307\u65bd\u6cd5\u3002\u81f3\u4e8e\u5230\u5e95\u662f\u5f3a\u5316\u54ea\u65b9\u9762\uff0c\u4e8b\u5148\u81ea\u7136\u65e0\u4ece\u5f97\u77e5\uff0c\u53ea\u80fd\u78b0\u78b0\u8fd0\u6c14\u3002\u5982\u679c\u8fd0\u6c14\u5b9e\u5728\u7cdf\u7cd5\uff0c\u6bd4\u65b9\u8bf4\u8679\u96fe\u4e13\u95e8\u7528\u4e8e\u5f3a\u5316\u4ea1\u7075\u672f\u548c\u5851\u80fd\u672f\uff0c\u90a3\u743c\u6069\u53ea\u80fd\u65e0\u8bed\uff0c\u5c31\u5f53\u662f\u767d\u5fd9\u4e00\u573a\u4e86\u3002</p><p>\u201c\u4ec0\u4e48\u53eb\u767d\u5fd9\u4e00\u573a\uff0c\u201d\u5bf9\u743c\u6069\u8fd9\u79cd\u5f97\u4e86\u4fbf\u5b9c\u5356\u4e56\u7684\u6001\u5ea6\uff0c\u9b54\u59ec\u63d0\u51fa\u4e25\u8083\u7684\u6279\u8bc4\uff0c\u201c\u81f3\u5c11\u4f60\u662f\u4e0a\u5230\u4e86\u4e00\u4e2a\u6f02\u4eae\u7f8e\u4eba\u513f\u5427\u3002\u201d</p><p>\u8fd9\u4e48\u8bf4\u662f\u6ca1\u9519\u5566\uff0c\u4f46\u8fd8\u662f\u6b63\u4e8b\u8981\u7d27\u3002</p><p>\u6240\u8c13\u6b63\u4e8b\uff0c\u81ea\u7136\u662f\u6307\u641e\u6e05\u695a\u8679\u96fe\u7684\u8fd0\u7528\u65b9\u6cd5\u3002\u743c\u6069\u4ece\u8428\u9a6c\u65af\u7279\u624b\u4e2d\u628a\u7ef4\u82e5\u62c9\u201c\u501f\u201d\u8fc7\u6765\uff0c\u5176\u76ee\u7684\u5f88\u7b80\u5355\uff0c\u5c31\u662f\u6253\u7b97\u4ece\u5979\u8eab\u4e0a\u53d6\u5f97\u5deb\u5e08\u4e4b\u795e\u7684\u795e\u529b\uff0c\u8fdb\u4e00\u6b65\u63d0\u5347\u81ea\u5df1\u3002\u5982\u4eca\u4e1c\u897f\u662f\u5f04\u5230\u624b\u4e86\uff0c\u4f46\u5018\u82e5\u4e0d\u77e5\u5176\u8fd0\u7528\u95e8\u9053\uff0c\u65e0\u6cd5\u5c3d\u5feb\u8f6c\u5316\u4e3a\u771f\u6b63\u7684\u201c\u529b\u91cf\u201d\uff0c\u90a3\u5c31\u4ecd\u7136\u6ca1\u6709\u610f\u4e49\u3002</p><p>\u201c\u90a3\u5c31\u53ea\u80fd\u53bb\u95ee\u5979\u4e86\u3002\u201d\u624e\u745e\u5c14\u8bf4\u3002</p><p>\u795e\u529b\u867d\u7136\u7cbe\u6df1\u5965\u5999\uff0c\u4f46\u5982\u679c\u6709\u5145\u8db3\u7684\u65f6\u95f4\u6765\u7814\u7a76\u5206\u6790\uff0c\u743c\u6069\u81ea\u4fe1\u4e5f\u80fd\u5f04\u51fa\u70b9\u5934\u7eea\u2014\u2014\u4f46\u4ed6\u73b0\u5728\u6700\u7f3a\u4e4f\u7684\u5c31\u662f\u65f6\u95f4\u3002\u4ee5\u76ee\u524d\u7684\u5f62\u52bf\uff0c\u4ed6\u60f3\u8981\u7acb\u523b\u83b7\u77e5\u7b54\u6848\uff0c\u786e\u5b9e\u4e5f\u53ea\u5269\u4e0b\u4e00\u6761\u8def\u53ef\u8d70\uff0c\u5c31\u662f\u4ece\u7ef4\u82e5\u62c9\u53e3\u4e2d\u83b7\u77e5\u7b54\u6848\u3002</p><p>\u53ea\u662f\u5f88\u663e\u7136\uff0c\u7ef4\u82e5\u62c9\u4e0d\u53ef\u80fd\u613f\u610f\u628a\u8fd9\u79cd\u79d8\u5bc6\u5766\u8bda\u76f8\u544a\uff0c\u743c\u6069\u4e00\u65f6\u4e5f\u60f3\u4e0d\u51fa\u6709\u4ec0\u4e48\u529e\u6cd5\u80fd\u8ba9\u5979\u5f00\u53e3\uff0c\u5982\u4eca\u8fd9\u79cd\u5c40\u9762\uff0c\u5a01\u903c\u5229\u8bf1\u4f3c\u4e4e\u90fd\u4e0d\u5408\u9002\u3002\u4e0d\u8fc7\u5e78\u597d\uff0c\u8eab\u8fb9\u8fd8\u6709\u4e00\u4f4d\u7f8e\u4e3d\u7684\u5973\u53c2\u8c0b\u3002</p><p>\u201c\u54e6\uff0c\u60f3\u8981\u5979\u5f00\u53e3\u7684\u8bdd\uff0c\u529e\u6cd5\u591a\u5f97\u662f\u554a\uff0c\u201d\u9b54\u59ec\u8f7b\u63cf\u6de1\u5199\u5730\u8bf4\uff0c\u201c\u770b\u4f60\u559c\u6b22\u54ea\u4e00\u79cd\u4e86\u3002\u201d</p><p>\u201c\u54ea\u79cd\u6700\u7b80\u5355\uff1f\u201d</p><p>\u201c\u6700\u7b80\u5355\u7684\u529e\u6cd5\u81ea\u7136\u5c31\u662f\u76f4\u63a5\u65bd\u6cd5\u63a7\u5236\u5176\u795e\u667a\u4e86\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u7ed9\u6211\u4e94\u5206\u949f\u65f6\u95f4\uff0c\u4fdd\u8bc1\u8ba9\u5979\u95ee\u4e00\u7b54\u5341\uff0c\u7edd\u65e0\u9690\u7792\u3002\u201d</p><p>\u201c\u90a3\u5f88\u597d\u554a\uff0c\u8d76\u5feb\u5f00\u59cb\u5427\u2026\u2026\u7b49\u7b49\uff0c\u201d\u743c\u6069\u72d0\u7591\u5730\u770b\u7740\u5979\uff0c\u6253\u4ea4\u9053\u8fd9\u4e48\u4e45\uff0c\u4ed6\u5bf9\u9b54\u59ec\u7684\u79c9\u6027\u4e5f\u7b97\u6709\u6240\u4e86\u89e3\uff0c\u201c\u662f\u4e0d\u662f\u6709\u4ec0\u4e48\u98ce\u9669\uff1f\u201d</p><p>\u201c\u98ce\u9669\u5012\u6ca1\u6709\uff0c\u767e\u5206\u4e4b\u767e\u6210\u529f\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u4e0d\u8fc7\u5979\u6bd5\u7adf\u662f\u4e2a\u9009\u6c11\uff0c\u5bf9\u7cbe\u795e\u5165\u4fb5\u7684\u62b5\u6297\u529b\u8fd8\u662f\u633a\u5f3a\u7684\uff1b\u6211\u76ee\u524d\u7684\u72b6\u6001\u53c8\u4e0d\u662f\u5f88\u597d\uff0c\u6709\u4e9b\u7cbe\u7ec6\u64cd\u4f5c\u529b\u4e0d\u4ece\u5fc3\uff0c\u53ea\u80fd\u5f3a\u884c\u78be\u538b\u7834\u574f\u2026\u2026\u603b\u4e4b\uff0c\u5c31\u662f\u5979\u7684\u667a\u529b\u4f1a\u53d7\u5230\u4e00\u70b9\u5f71\u54cd\u3002\u4e0d\u8fc7\u65e0\u6240\u8c13\u4e86\uff0c\u53ea\u8981\u4f60\u4e0d\u4ecb\u610f\u63a5\u4e0b\u6765\u5e72\u5979\u7684\u65f6\u5019\uff0c\u5979\u603b\u662f\u671d\u4f60\u50bb\u7b11\u6d41\u53e3\u6c34\u7ffb\u767d\u773c\uff0c\u90a3\u5c31\u95ee\u9898\u4e0d\u5927\u3002\u201d</p><p>\u201c\u4f60\u89c9\u5f97\u6211\u4f1a\u4e0d\u4ecb\u610f\uff1f\u201d\u743c\u6069\u6ca1\u597d\u6c14\u5730\u8bf4\uff0c\u201c\u6709\u6ca1\u6709\u522b\u7684\u529e\u6cd5\uff0c\u5b89\u5168\u70b9\u7684\u3002\u201d</p><p>\u201c\u7528\u836f\u4e5f\u53ef\u4ee5\uff0c\u526f\u4f5c\u7528\u4f1a\u8f7b\u5f88\u591a\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u6211\u624b\u4e0a\u6709\u51e0\u79cd\u5410\u771f\u5242\u7684\u914d\u65b9\uff0c\u5e94\u8be5\u90fd\u80fd\u8ba9\u5979\u8bf4\u51fa\u4f60\u60f3\u8981\u7684\u4fe1\u606f\uff0c\u800c\u4e14\u4e5f\u4e0d\u4f1a\u5bf9\u5979\u9020\u6210\u4f24\u5bb3\uff0c\u53ea\u662f\u70bc\u5236\u8d77\u6765\u633a\u82b1\u65f6\u95f4\uff0c\u6700\u5feb\u7684\u4e5f\u8981\u4e00\u5929\u4e00\u591c\u624d\u80fd\u914d\u6210\u529f\uff0c\u800c\u4e14\u670d\u4e0b\u540e\u8fd8\u8981\u56db\u4e2a\u5c0f\u65f6\u624d\u80fd\u751f\u6548\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4e0d\u884c\uff0c\u201d\u743c\u6069\u518d\u6b21\u5426\u51b3\uff0c\u201c\u6548\u7387\u592a\u4f4e\u4e86\u3002\u201d</p><p>\u201c\u5211\u8baf\u903c\u4f9b\u5982\u4f55\uff1f\u201d</p><p>\u201c\u2026\u2026\u4f60\u4e00\u4e2a\u5927\u9b54\u9b3c\uff0c\u5c45\u7136\u51fa\u8fd9\u4e48\u7b80\u5355\u7c97\u66b4\u7f3a\u4e4f\u6280\u672f\u542b\u91cf\u7684\u4e3b\u610f\uff0c\u4e0d\u6015\u88ab\u6df1\u6e0a\u91cc\u90a3\u5e2e\u5bb6\u4f19\u7b11\u8bdd\u5417\uff1f\u201d</p><p>\u201c\u4e5f\u662f\u554a\uff0c\u201d\u624e\u745e\u5c14\u6258\u7740\u9999\u816e\uff0c\u4eff\u4f5b\u5728\u8ba4\u771f\u601d\u8003\u8d77\u6765\uff0c\u201c\u90a3\u5c31\u6765\u4e2a\u6bd4\u8f83\u6709\u6280\u672f\u542b\u91cf\u7684\u5427\u3002\u4f60\u8d76\u5feb\u591a\u641e\u5979\u51e0\u6b21\uff0c\u641e\u5230\u5979\u795e\u9b42\u98a0\u5012\u6b32\u4ed9\u6b32\u6b7b\uff0c\u81ea\u7136\u5c31\u4ec0\u4e48\u90fd\u80af\u544a\u8bc9\u4f60\u4e86\u3002\u201d</p><p>\u8fd9\u5c31\u662f\u4f60\u6240\u8c13\u7684\u201c\u6bd4\u8f83\u6709\u6280\u672f\u542b\u91cf\u201d\uff1f\u6280\u672f\u542b\u91cf\u5728\u54ea\u91cc\u554a\uff01</p><p>\u201c\u5f53\u7136\u6709\u6280\u672f\u542b\u91cf\u554a\uff0c\u96be\u9053\u4f60\u89c9\u5f97\u53ea\u8981\u5c3a\u5bf8\u591f\u5927\u5c31\u884c\uff1f\u8fd9\u79cd\u8ba4\u8bc6\u53ef\u5c31\u592a\u80a4\u6d45\u4e86\u3002\u201d</p><p>\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u5c3a\u5bf8\u56fa\u7136\u91cd\u8981\uff0c\u6280\u5de7\u4e5f\u4e0d\u53ef\u6216\u7f3a\u2014\u2014\u7b49\u4e00\u4e0b\uff0c\u6211\u4e0d\u662f\u8981\u8ddf\u4f60\u8ba8\u8bba\u8fd9\u4e2a\u2026\u2026\u201d</p><p>\u201c\u90a3\u4f60\u662f\u5bf9\u81ea\u5df1\u5728\u8fd9\u65b9\u9762\u7684\u80fd\u529b\u7f3a\u4e4f\u4fe1\u5fc3\uff1f\u201d</p><p>\u8fd9\u4e0d\u662f\u6709\u4fe1\u5fc3\u6ca1\u4fe1\u5fc3\u7684\u95ee\u9898\u597d\u5427\uff0c\u5bf9\u65b9\u6bd5\u7adf\u662f\u8bad\u7ec3\u6709\u7d20\u7684\u9ad8\u9636\u5deb\u5e08\uff0c\u53c8\u4e0d\u662f\u666e\u901a\u4eba\uff0c\u518d\u600e\u4e48\u88ab\u641e\u5f97\u795e\u9b42\u98a0\u5012\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u4f1a\u628a\u8fd9\u79cd\u673a\u5bc6\u60c5\u62a5\u900f\u9732\u7ed9\u654c\u4eba\u554a\u3002</p><p>\u201c\u662f\u5417\uff1f\u53ef\u662f\u6211\u4ee5\u524d\u5728\u963f\u5f17\u7eb3\u65af\u7684\u65f6\u5019\uff0c\u6bcf\u5929\u95f2\u7740\u65e0\u804a\uff0c\u770b\u8fc7\u4e0d\u5c11\u7269\u8d28\u754c\u7684\u6d41\u884c\u5c0f\u8bf4\uff0c\u91cc\u9762\u7684\u4e3b\u89d2\u4eec\u5168\u90fd\u662f\u51ed\u501f\u8fc7\u4eba\u7684\u5e8a\u4e0a\u80fd\u529b\uff0c\u628a\u654c\u65b9\u5973\u6027\u5f7b\u5e95\u5f81\u670d\uff0c\u81ea\u7136\u5c31\u8eab\u5fc3\u6ca6\u9677\u3001\u5343\u4f9d\u767e\u987a\u2014\u2014\u597d\u50cf\u4e5f\u4e0d\u662f\u5f88\u96be\u7684\u6837\u5b50\u561b\u3002\u201d</p><p>\u2026\u2026\u4f60\u8eab\u4e3a\u5802\u5802\u5730\u72f1\u5927\u516c\u7235\uff0c\u4e0d\u5fd9\u7740\u79ef\u6781\u7b79\u5212\u9634\u8c0b\u5f81\u670d\u4e16\u754c\uff0c\u53cd\u800c\u6574\u5929\u770b\u51e1\u95f4\u7684\u5947\u5e7b\u5c0f\u8bf4\u6253\u53d1\u65f6\u95f4\uff0c\u800c\u4e14\u8fd8\u662f\u5982\u6b64\u4e0d\u7740\u8c03\u7684\u7c7b\u578b\uff0c\u96be\u602a\u6700\u540e\u4f1a\u88ab\u90e8\u5c5e\u653f\u53d8\u641e\u4e0b\u53f0\uff0c\u6211\u73b0\u5728\u7ec8\u4e8e\u53ef\u4ee5\u7406\u89e3\u4e86\u3002</p><p>\u201c\u8bdd\u8bf4\u4f60\u5c31\u6ca1\u6709\u4ec0\u4e48\u65e2\u5b89\u5168\u53c8\u6709\u6548\u7387\u65e2\u6709\u6280\u672f\u542b\u91cf\u540c\u65f6\u53c8\u9760\u8c31\u53ef\u884c\u7684\u529e\u6cd5\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u8981\u6c42\u8fd8\u771f\u591a\uff0c\u201d\u9b54\u59ec\u644a\u4e86\u644a\u624b\uff0c\u201c\u90a3\u5c31\u53ea\u5269\u4e0b\u6700\u540e\u4e00\u62db\u4e86\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8bf4\u670d\u3002\u201d</p><p>\u201c\u8bf4\u670d\uff1f\u201d\u743c\u6069\u6115\u7136\uff0c\u201c\u4f60\u8981\u600e\u4e48\u8bf4\u670d\uff1f\u201d</p><p>\u201c\u81ea\u7136\u662f\u52a8\u4e4b\u4ee5\u60c5\u3001\u6653\u4e4b\u4ee5\u7406\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u771f\u7684\u4e0d\u662f\u5728\u8ddf\u6211\u5f00\u73a9\u7b11\uff1f\u201d</p><p>\u9b54\u9b3c\u64c5\u957f\u6587\u5b57\u6e38\u620f\uff0c\u64c5\u957f\u4ee5\u8a00\u8f9e\u8bf1\u60d1\u51e1\u4eba\uff0c\u624e\u745e\u5c14\u8eab\u4e3a\u9b54\u59ec\uff0c\u5176\u201c\u8bf4\u670d\u201d\u6280\u80fd\u81ea\u7136\u662f\u5f88\u9ad8\u660e\u7684\u3002\u4f46\u518d\u9ad8\u8d85\u7684\u201c\u8bf4\u670d\u201d\u6280\u5de7\uff0c\u603b\u4e5f\u4e0d\u53ef\u80fd\u65e0\u4e2d\u751f\u6709\uff0c\u603b\u8981\u6709\u6240\u51ed\u7c4d\uff0c\u8981\u6709\u53cc\u65b9\u80fd\u591f\u521d\u6b65\u8fbe\u6210\u7684\u5171\u8bc6\u3002\u7136\u800c\u6b64\u65f6\u6b64\u523b\uff0c\u6b64\u60c5\u6b64\u666f\uff0c\u5979\u7684\u51ed\u7c4d\u5728\u54ea\u91cc\uff0c\u53cc\u65b9\u7684\u5171\u8bc6\u53c8\u5728\u4f55\u5904\uff1f</p><p>\u201c\u6240\u4ee5\u4f60\u89c9\u5f97\u6211\u505a\u4e0d\u5230\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u90a3\u8981\u4e0d\u8981\u6253\u4e2a\u8d4c\u8bd5\u8bd5\uff1f\u201d</p><p>\u201c\u2026\u2026\u8d4c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5982\u679c\u6211\u8f93\u4e86\uff0c\u6211\u8d54\u4f60\u5341\u4e2a\u540c\u6837\u7d20\u8d28\u7684\u7f8e\u4eba\u513f\uff1b\u5982\u679c\u6211\u8d62\u4e86\u5462\uff0c\u201d\u5979\u60f3\u4e86\u60f3\uff0c\u201c\u6211\u60f3\u8981\u4e00\u4e2a\u627f\u8bfa\u3002\u201d</p><p>\u201c\u627f\u8bfa\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u8981\u7b54\u5e94\u6211\uff0c\u4ee5\u540e\u4e0d\u7ba1\u53d1\u751f\u4efb\u4f55\u4e8b\u60c5\uff0c\u4e0d\u7ba1\u4f60\u770b\u5230\u4e86\u4ec0\u4e48\uff0c\u542c\u5230\u4e86\u4ec0\u4e48\uff0c\u4e0d\u7ba1\u6211\u505a\u4e86\u4ec0\u4e48\u8ba9\u4f60\u751f\u6c14\u7684\u4e8b\u60c5\uff0c\u4f60\u90fd\u8981\u7ed9\u6211\u89e3\u91ca\u7684\u673a\u4f1a\uff0c\u201d\u9b54\u59ec\u770b\u7740\u9762\u524d\u7684\u5c11\u5e74\uff0c\u201c\u6211\u53ea\u662f\u8981\u8fd9\u4e2a\u673a\u4f1a\uff0c\u597d\u4e0d\u597d\u3002\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u201c\u662f\u4e0d\u662f\u2018\u6211\u2019\u66fe\u7ecf\u6709\u4ec0\u4e48\u4e8b\u8bef\u89e3\u8fc7\u4f60\uff1f\u201d</p><p>\u624e\u745e\u5c14\u7b11\u7740\u6447\u6447\u5934\uff0c\u5374\u6ca1\u6709\u76f4\u63a5\u56de\u7b54\uff0c\u201c\u597d\u4e0d\u597d\uff1f\u201d\u5979\u53c8\u95ee\u3002</p><p>\u201c\u597d\u3002\u201d\u743c\u6069\u70b9\u4e86\u70b9\u5934\u3002</p><p>\u201c\u4e00\u8a00\u4e3a\u5b9a\u54e6\u3002\u201d\u542c\u5230\u743c\u6069\u7684\u627f\u8bfa\uff0c\u9b54\u59ec\u987f\u65f6\u7b11\u9765\u5982\u82b1\uff0c\u4eff\u4f5b\u83b7\u5f97\u4e86\u81ea\u5df1\u68a6\u5bd0\u4ee5\u6c42\u7684\u73cd\u5b9d\u3002\u5979\u62ac\u624b\u62db\u4e86\u62db\uff0c\u8ba9\u6c60\u6c34\u6258\u7740\u5c1a\u5728\u6655\u8ff7\u4e2d\u7684\u5973\u5deb\u5e08\u79fb\u8fc7\u6765\uff0c\u7136\u540e\u5979\u4f38\u51fa\u4e00\u6839\u624b\u6307\uff0c\u62b5\u4f4f\u7ef4\u82e5\u62c9\u7684\u7709\u5fc3\u3002</p><p>\u201c\u4f60\u5e72\u561b\uff1f\u201d</p><p>\u201c\u770b\u770b\u5979\u7684\u68a6\u5883\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u60f3\u8981\u8bf4\u670d\u4e00\u4e2a\u4eba\uff0c\u5148\u5f97\u4e86\u89e3\u5979\u5185\u5fc3\u6df1\u5904\u7684\u6b32\u671b\u6240\u5728\uff0c\u800c\u68a6\u5883\uff0c\u662f\u6b32\u671b\u6700\u597d\u7684\u5c55\u73b0\u3002\u201d</p><p>\u201c\u6211\u89c9\u5f97\u7528\u2018\u6b32\u671b\u2019\u8fd9\u4e2a\u8bcd\u8fc7\u4e8e\u504f\u72ed\u2014\u2014\u5f53\u7136\u8fd9\u4e0d\u662f\u91cd\u70b9\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5979\u73b0\u5728\u6b63\u5728\u505a\u68a6\uff1f\u201d</p><p>\u201c\u5e94\u8be5\u6ca1\u6709\u3002\u201d</p><p>\u201c\u90a3\u4f60\u600e\u4e48\u770b\u5979\u7684\u68a6\u5883\uff1f\u201d</p><p>\u624e\u745e\u5c14\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u7136\u540e\u743c\u6069\u7acb\u523b\u610f\u8bc6\u5230\u81ea\u5df1\u72af\u4e86\u4e2a\u9519\u8bef\uff0c\u4ed6\u9762\u524d\u8fd9\u4f4d\u6e29\u8a00\u6b3e\u6b3e\u3001\u4e56\u5de7\u67d4\u987a\u7684\u4eba\u7c7b\u5973\u5b50\uff0c\u5b9e\u9645\u4e0a\u53ef\u662f\u6614\u65e5\u963f\u5f17\u7eb3\u65af\u7684\u5927\u516c\u7235\uff0c\u4f4d\u5217\u5730\u72f1\u4e5d\u9b54\u541b\u4e4b\u4e00\uff0c\u6709\u8d44\u683c\u4e0e\u8bf8\u795e\u76f8\u63d0\u5e76\u8bba\u7684\u5b58\u5728\u3002\u5728\u5854\u745f\u8c37\u7684\u65f6\u5019\uff0c\u90a3\u4f4d\u76f2\u773c\u5973\u5b69\u838e\u7433\u5a1c\u79f0\u547c\u5979\u4e3a\u201c\u68a6\u534e\u5973\u58eb\u201d\uff0c\u8fd9\u6216\u8bb8\u662f\u79cd\u4f2a\u9970\uff0c\u4f46\u6599\u60f3\u4e5f\u4e0d\u662f\u5168\u65e0\u6765\u7531\u3002</p><p>\u201c\u6709\u610f\u601d\u3002\u201d\u624e\u745e\u5c14\u5ffd\u7136\u8bf4\u3002</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5979\u6709\u4e9b\u6709\u8da3\u7684\u60f3\u6cd5\uff0c\u201d\u624e\u745e\u5c14\u6c89\u541f\u7740\uff0c\u201c\u8ba9\u6211\u60f3\u60f3\uff0c\u55ef\uff0c\u6216\u8bb8\u6211\u7684\u8ba1\u5212\u5e94\u8be5\u7a0d\u4f5c\u4fee\u6b63\u3002\u201d</p><p>\u201c\u4f60\u5230\u5e95\u60f3\u5e72\u561b\uff1f\u201d</p><p>\u201c\u62ed\u76ee\u4ee5\u5f85\u5427\uff0c\u201d\u9b54\u59ec\u81ea\u4fe1\u6ee1\u6ee1\u5730\u8bf4\uff0c\u5c06\u62b5\u5728\u7ef4\u82e5\u62c9\u7709\u5fc3\u7684\u624b\u6307\u6536\u4e86\u56de\u6765\uff0c\u201c\u5979\u9192\u4e86\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u6c89\u5bc2\u7684\u9ed1\u6697\u4e4b\u4e2d\uff0c\u7ef4\u82e5\u62c9\u832b\u7136\u56db\u987e\uff0c\u5468\u56f4\u6ca1\u6709\u4e00\u4e1d\u5149\u4eae\uff0c\u770b\u4e0d\u89c1\u4efb\u4f55\u4e1c\u897f\uff0c\u82e5\u6709\u82e5\u65e0\u7684\u58f0\u97f3\u5728\u8033\u8fb9\u8426\u7ed5\uff0c\u4eff\u4f5b\u9065\u8fdc\u5904\u4f20\u6765\u7684\u98ce\u58f0\u4f4e\u541f\uff0c\u4f46\u4fa7\u8033\u7ec6\u542c\u7684\u8bdd\uff0c\u5374\u53c8\u4ec0\u4e48\u90fd\u6ca1\u6709\u3002\u5973\u5deb\u5e08\u52aa\u529b\u5730\u601d\u7d22\u7740\uff0c\u8bd5\u56fe\u5f04\u6e05\u695a\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\uff0c\u5224\u65ad\u81ea\u5df1\u8eab\u5728\u4f55\u5904\uff0c\u5374\u53d1\u73b0\u4ec0\u4e48\u90fd\u60f3\u4e0d\u8d77\u6765\uff0c\u8111\u6d77\u4e2d\u8fd1\u4e4e\u7a7a\u767d\uff0c\u8bb0\u5fc6\u6a21\u7cca\u4e00\u7247\uff0c\u6df7\u6c8c\u4e0d\u6e05\u3002</p><p>\u201c\u5854\u59d0\u59d0\uff01\u5854\u59d0\u59d0\uff01\u201d</p><p>\u6e05\u8106\u7684\u547c\u5524\u58f0\u5728\u80cc\u540e\u54cd\u8d77\uff0c\u7ef4\u82e5\u62c9\u8f6c\u8fc7\u5934\uff0c\u770b\u89c1\u4e00\u4e2a\u5c0f\u5973\u5b69\u6b63\u6325\u7740\u624b\u671d\u81ea\u5df1\u8dd1\u8fc7\u6765\uff0c\u5927\u7ea6\u516d\u4e03\u5c81\u5e74\u7eaa\uff0c\u8138\u86cb\u7ea2\u6251\u6251\u7684\uff0c\u68b3\u7740\u4e24\u6839\u5c0f\u8fab\u5b50\uff0c\u968f\u7740\u5954\u8dd1\u5728\u8111\u540e\u4e00\u7529\u4e00\u7529\u7684\uff0c\u770b\u8d77\u6765\u5341\u5206\u53ef\u7231\u3002\u968f\u7740\u5c0f\u5973\u5b69\u7684\u63a5\u8fd1\uff0c\u9ed1\u6697\u5982\u6f6e\u6c34\u822c\u5411\u4e24\u4fa7\u8fc5\u901f\u9000\u53bb\uff0c\u65e0\u6570\u788e\u7247\u822c\u5149\u5f71\u4ea4\u7ec7\u7740\uff0c\u62fc\u5408\u6210\u719f\u6089\u7684\u666f\u8c61\uff0c\u542c\u89c9\u4e5f\u5f00\u59cb\u6062\u590d\u3002\u5973\u5deb\u5e08\u53d1\u73b0\u81ea\u5df1\u6b63\u7ad9\u5728\u4e00\u5904\u6d77\u6ee9\u4e0a\uff0c\u96ea\u767d\u7684\u6c99\u5b50\u53cd\u5c04\u7740\u5348\u540e\u7684\u9633\u5149\uff0c\u54d7\u5566\u54d7\u5566\u7684\u6d77\u6d6a\u58f0\u968f\u98ce\u4ece\u8fdc\u5904\u4f20\u6765\uff0c\u8ba9\u5979\u88ab\u51bb\u7ed3\u7684\u67d0\u4e00\u90e8\u5206\u8bb0\u5fc6\u4e5f\u5f00\u59cb\u590d\u82cf\u3002\u201c\u522b\u8dd1\u90a3\u4e48\u5feb\uff0c\u7490\u7490\uff0c\u201d\u5973\u5deb\u5e08\u7b11\u7740\u8bf4\uff0c\u201c\u5c0f\u5fc3\u6454\u5012\u3002\u201d</p><p>\u201c\u7490\u7490\u5f88\u7075\u5de7\u7684\uff0c\u624d\u4e0d\u4f1a\u6454\u5012\u5462\uff0c\u201d\u5c0f\u5973\u5b69\u7b11\u7740\u8dd1\u5230\u5973\u5deb\u5e08\u9762\u524d\uff0c\u62ac\u8d77\u8138\u671b\u7740\u5979\uff0c\u201c\u5854\u59d0\u59d0\uff0c\u4f60\u771f\u7684\u8981\u8d70\u5417\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u90a3\u8c01\u6765\u505a\u5deb\u738b\u5462\uff1f\u201d\u5c0f\u5973\u5b69\u95ee\u3002</p><p>\u201c\u4ee5\u540e\u5c31\u6ca1\u6709\u5deb\u738b\u4e86\u5440\u3002\u201d</p><p>\u201c\u6ca1\u6709\u5deb\u738b\u4e86\uff1f\u201d\u5c0f\u5973\u5b69\u6709\u70b9\u4e0d\u660e\u767d\uff0c\u201c\u90a3\u4ee5\u540e\u8c01\u6765\u5e87\u4f51\u6751\u5b50\u5462\uff1f\u201d</p><p>\u201c\u672c\u6765\u5c31\u4e0d\u9700\u8981\u8c01\u6765\u5e87\u4f51\u554a\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u90a3\u4e2a\u5deb\u738b\u662f\u4e2a\u9a97\u5b50\u5566\uff0c\u4ed6\u6839\u672c\u5c31\u6ca1\u6709\u5e87\u4f51\u6751\u5b50\uff0c\u538b\u6839\u5c31\u662f\u5728\u5265\u524a\u5927\u5bb6\u2014\u2014\u7490\u7490\u4f60\u4e0d\u662f\u4e5f\u7ecf\u5e38\u8bf4\uff0c\u5deb\u738b\u662f\u4e2a\u5927\u574f\u86cb\u5417\u3002\u201d</p><p>\u201c\u55ef\uff0c\u5deb\u738b\u662f\u4e2a\u5927\u574f\u86cb\uff01\u201d\u5c0f\u5973\u5b69\u7528\u529b\u5730\u70b9\u5934\uff0c\u201c\u53ef\u662f\u5854\u59d0\u59d0\u662f\u597d\u4eba\u554a\u2014\u2014\u800c\u4e14\u5854\u59d0\u59d0\u5f88\u5389\u5bb3\uff0c\u628a\u574f\u5deb\u738b\u90fd\u8d76\u8d70\u4e86\u3002\u6240\u4ee5\u5854\u59d0\u59d0\u6765\u505a\u6211\u4eec\u7684\u5deb\u738b\u4e0d\u5c31\u597d\u4e86\uff1f\u8fd9\u6837\u6211\u4eec\u5c31\u6709\u597d\u5deb\u738b\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u4e0d\u662f\u597d\u5deb\u738b\u6216\u8005\u574f\u5deb\u738b\u7684\u95ee\u9898\u2026\u2026\u201d\u7ef4\u82e5\u62c9\u52aa\u529b\u5730\u60f3\u8981\u89e3\u91ca\uff0c\u4f46\u6700\u540e\u8fd8\u662f\u653e\u5f03\u4e86\uff0c\u5bf9\u65b9\u8fd8\u662f\u5c0f\u5b69\u5b50\uff0c\u6709\u4e9b\u4e8b\u60c5\u786e\u5b9e\u4e0d\u592a\u5bb9\u6613\u7406\u89e3\uff0c\u201c\u800c\u4e14\uff0c\u5854\u59d0\u59d0\u8fd8\u6709\u4e8b\u60c5\u8981\u505a\uff0c\u6ca1\u529e\u6cd5\u7559\u4e0b\u6765\u505a\u5deb\u738b\u3002\u201d</p><p>\u201c\u5854\u59d0\u59d0\u8981\u53bb\u505a\u4ec0\u4e48\u5440\uff1f\u201d</p><p>\u201c\u5854\u59d0\u59d0\u8981\u53bb\u505a\u6bd5\u4e1a\u65c5\u884c\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u5b8c\u6210\u6bd5\u4e1a\u65c5\u884c\uff0c\u5854\u59d0\u59d0\u5c31\u662f\u5deb\u5e08\u4e86\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d\u5c0f\u5973\u5b69\u5947\u602a\u5730\u6b6a\u7740\u8111\u888b\uff0c\u201c\u5854\u59d0\u59d0\u73b0\u5728\u4e0d\u5df2\u7ecf\u662f\u5deb\u5e08\u4e86\u5417\u3002\u201d</p><p>\u201c\u8fd8\u4e0d\u662f\uff0c\u53ea\u6709\u5b8c\u6210\u6bd5\u4e1a\u65c5\u884c\uff0c\u624d\u80fd\u5f97\u5230\u5b66\u6821\u7684\u8ba4\u53ef\uff0c\u6210\u4e3a\u771f\u6b63\u7684\u5deb\u5e08\u3002\u5854\u59d0\u59d0\u73b0\u5728\u8fd8\u53ea\u80fd\u7b97\u662f\u4e2a\u5b66\u5f92\u800c\u5df2\u3002\u201d</p><p>\u201c\u54e6\uff0c\u201d\u5c0f\u5973\u5b69\u4f3c\u61c2\u975e\u61c2\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u6240\u4ee5\u5deb\u5e08\u6bd4\u5b66\u5f92\u66f4\u5389\u5bb3\uff1f\u201d</p><p>\u201c\u5443\uff0c\u7b97\u662f\u5427\u3002\u201d</p><p>\u201c\u53ef\u662f\u5854\u59d0\u59d0\u5df2\u7ecf\u5f88\u5389\u5bb3\u4e86\u5440\uff0c\u8fde\u5deb\u738b\u90fd\u6253\u4e0d\u8fc7\u5854\u59d0\u59d0\u3002\u201d</p><p>\u201c\u4e0d\u662f\u5566\uff0c\u662f\u56e0\u4e3a\u90a3\u4e2a\u5bb6\u4f19\u672c\u6765\u5c31\u5f88\u5dee\u52b2\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u5c31\u90a3\u51e0\u624b\u4e09\u811a\u732b\u7684\u620f\u6cd5\uff0c\u4e5f\u6562\u81ea\u79f0\u4ec0\u4e48\u5deb\u738b\uff0c\u8dd1\u5230\u8fd9\u91cc\u6765\u4f5c\u5a01\u4f5c\u798f\uff0c\u771f\u662f\u7b11\u6b7b\u4eba\u4e86\u3002\u201d</p><p>\u201c\u624d\u4e0d\u662f\u5462\uff0c\u5927\u5bb6\u90fd\u5bb3\u6015\u5deb\u738b\uff0c\u53ea\u6709\u5854\u59d0\u59d0\u4e0d\u6015\uff0c\u8fd8\u6253\u8d25\u4e86\u4ed6\uff0c\u5854\u59d0\u59d0\u5c31\u662f\u5f88\u5389\u5bb3\u561b\u3002\u201d</p><p>\u201c\u662f\u5566\u662f\u5566\uff0c\u5854\u59d0\u59d0\u4e5f\u89c9\u5f97\u81ea\u5df1\u5f88\u5389\u5bb3\uff0c\u7ef4\u82e5\u62c9\u7b11\u7740\u8bf4\uff0c\u201c\u4ee5\u540e\u7b49\u5854\u59d0\u59d0\u6210\u4e3a\u4e86\u771f\u6b63\u7684\u5deb\u5e08\uff0c\u5c31\u4f1a\u53d8\u5f97\u66f4\u52a0\u5389\u5bb3\u4e86\u3002\u201d</p><p>\u201c\u53ef\u662f\u5854\u59d0\u59d0\u4e3a\u4ec0\u4e48\u8981\u53d8\u5f97\u66f4\u52a0\u5389\u5bb3\u5462\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u8981\u53d8\u5f97\u66f4\u52a0\u5389\u5bb3\uff0c\u800c\u662f\u8981\u6210\u4e3a\u5deb\u5e08\uff0c\u8fd9\u4e24\u8005\u662f\u4e0d\u4e00\u6837\u7684\u3002\u201d</p><p>\u201c\u6210\u4e3a\u5deb\u5e08\u4e0d\u5c31\u662f\u53d8\u5f97\u66f4\u5389\u5bb3\u5417\uff1f\u201d</p><p>\u201c\u53d8\u5f97\u66f4\u5389\u5bb3\uff0c\u90a3\u662f\u6210\u4e3a\u5deb\u5e08\u7684\u7ed3\u679c\uff0c\u800c\u4e0d\u662f\u52a8\u673a\u2026\u2026\u201d\u7ef4\u82e5\u62c9\u8bd5\u56fe\u89e3\u91ca\uff0c\u4f46\u60f3\u4e86\u60f3\u53c8\u653e\u5f03\u4e86\uff0c\u201c\u8fd9\u4e9b\u4f60\u6682\u65f6\u4e5f\u542c\u4e0d\u61c2\uff0c\u603b\u4e4b\uff0c\u8fd8\u662f\u4e0d\u4e00\u6837\u7684\u3002\u201d</p><p>\u201c\u6240\u4ee5\u5854\u59d0\u59d0\u662f\u60f3\u6210\u4e3a\u5deb\u5e08\u3002\u201d</p><p>\u201c\u5bf9\u554a\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u55ef\uff1f\u201d\u7ef4\u82e5\u62c9\u6123\u4e86\u4e00\u4e0b\u3002</p><p>\u201c\u6211\u662f\u8bf4\uff0c\u201d\u5c0f\u5973\u5b69\u5f88\u8ba4\u771f\u5730\u95ee\uff0c\u201c\u5854\u59d0\u59d0\u4e3a\u4ec0\u4e48\u8981\u6210\u4e3a\u5deb\u5e08\u5462\uff1f\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u2026\u2026\u8981\u6210\u4e3a\u5deb\u5e08\uff1f\u201d\u7ef4\u82e5\u62c9\u4e0b\u610f\u8bc6\u5730\u91cd\u590d\u3002</p><p>\u201c\u5bf9\u554a\uff0c\u5854\u5854\uff0c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u6210\u4e3a\u5deb\u5e08\u5462\uff1f\u201d</p><p>\u4e00\u4e2a\u65b0\u7684\u58f0\u97f3\u5728\u65c1\u8fb9\u54cd\u8d77\uff0c\u7ef4\u82e5\u62c9\u5faa\u58f0\u671b\u53bb\uff0c\u770b\u89c1\u4e86\u8910\u8272\u8863\u670d\u7684\u9752\u5e74\u7537\u5b50\uff0c\u4ed6\u50cf\u662f\u521a\u521a\u4ece\u8fdc\u5904\u5954\u8dd1\u8fc7\u6765\uff0c\u5598\u7740\u6c14\uff0c\u6652\u5f97\u9ed1\u7ea2\u8272\u7684\u76ae\u80a4\u4e0a\u6ee1\u662f\u6c57\u73e0\u3002\u4ed6\u7684\u9762\u5bb9\u5f88\u719f\u6089\uff0c\u7136\u800c\u7ef4\u82e5\u62c9\u5374\u60f3\u4e0d\u8d77\u4ed6\u7684\u540d\u5b57\u3002\u201c\u4f60\u4e0a\u6b21\u8bf4\uff0c\u4e4b\u6240\u4ee5\u8981\u53bb\u94f6\u6708\u57ce\u5b66\u4e60\u9b54\u6cd5\uff0c\u662f\u4e3a\u4e86\u80fd\u591f\u6253\u5012\u5deb\u738b\u3002\u4f60\u4e0a\u6b21\u4e0d\u5c31\u662f\u8fd9\u4e48\u544a\u8bc9\u6211\u7684\u5417\uff0c\u5854\u5854\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u53ef\u662f\u2026\u2026\u201d</p><p>\u201c\u90a3\u73b0\u5728\u5deb\u738b\u5df2\u7ecf\u88ab\u8d76\u8d70\u4e86\uff0c\u201d\u9752\u5e74\u7537\u5b50\u671b\u7740\u5979\u7684\u773c\u775b\uff0c\u201c\u5deb\u738b\u4e0d\u5728\u4e86\uff0c\u6211\u4eec\u53ef\u4ee5\u8fc7\u4e0a\u5b89\u5b81\u65e5\u5b50\u4e86\uff0c\u4f60\u7559\u4e0b\u6765\uff0c\u548c\u6211\uff0c\u548c\u6211\u4eec\u5728\u4e00\u8d77\uff0c\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u4e3a\u4ec0\u4e48\u8fd8\u8981\u8d70\uff0c\u8fd8\u8981\u518d\u53bb\u505a\u90a3\u4ec0\u4e48\u5deb\u5e08\u5462\uff1f\u201d</p><p>\u56e0\u4e3a\u2026\u2026\u56e0\u4e3a\u6211\u7684\u60f3\u6cd5\uff0c\u5df2\u7ecf\u6539\u53d8\u4e86\uff0c\u56e0\u4e3a\u6211\u5df2\u7ecf\u548c\u4ee5\u524d\u4e0d\u4e00\u6837\u4e86\u554a\u3002</p><p>\u94f6\u6708\u57ce\u4e2d\u8fd1\u5341\u5e74\u7684\u523b\u82e6\u7814\u4e60\uff0c\u4e0d\u4ec5\u4ec5\u662f\u8ba9\u5979\u5b66\u4f1a\u4e86\u4ece\u6307\u5c16\u8ff8\u5c04\u706b\u7130\u95ea\u7535\u7684\u6280\u5de7\uff0c\u4e5f\u8ba9\u5979\u7684\u773c\u754c\u5f00\u9614\uff0c\u5fc3\u667a\u6210\u719f\uff0c\u5df2\u7ecf\u4e0d\u518d\u662f\u6614\u65e5\u90a3\u4e2a\u61f5\u61f5\u61c2\u61c2\u7684\u6e14\u6751\u5c11\u5973\u4e86\u3002\u89c1\u8bc6\u5f97\u66f4\u591a\uff0c\u7ecf\u5386\u5f97\u66f4\u591a\uff0c\u4e5f\u601d\u8003\u5f97\u66f4\u591a\uff0c\u5979\u773c\u4e2d\u7684\u4e16\u754c\uff0c\u5df2\u7ecf\u4e0d\u5c40\u9650\u4e8e\u8fd9\u4e2a\u5c0f\u5c0f\u7684\u504f\u50fb\u6e14\u6751\u4e86\u3002\u5979\u5192\u5192\u5931\u5931\u5730\u95ef\u8fdb\u9b54\u6cd5\u6bbf\u5802\u4e4b\u59cb\uff0c\u786e\u5b9e\u662f\u62b1\u7740\u5f88\u5355\u7eaf\u7684\u76ee\u7684\uff0c\u5c31\u662f\u4e3a\u4e86\u80fd\u591f\u6709\u671d\u4e00\u65e5\u56de\u5230\u5bb6\u4e61\uff0c\u6253\u5012\u90a3\u4e2a\u6b3a\u51cc\u5927\u4f19\u7684\u5deb\u738b\uff0c\u8ba9\u4e61\u4eb2\u4eec\u80fd\u591f\u8fc7\u4e0a\u5b81\u9759\u548c\u5e73\u7684\u751f\u6d3b\u2014\u2014\u4f46\u6b64\u65f6\u6b64\u523b\uff0c\u5979\u5fc3\u4e2d\u6240\u60f3\uff0c\u5fc3\u4e2d\u6240\u6c42\uff0c\u5df2\u7ecf\u8fdc\u8fdc\u4e0d\u4ec5\u4e8e\u6b64\uff0c\u5b83\u8d85\u51fa\u773c\u524d\u9752\u5e74\u7537\u5b50\u7684\u7406\u89e3\u8303\u7574\uff0c\u751a\u81f3\u8fde\u90a3\u4e9b\u5deb\u5e08\u540c\u4faa\u4eec\u90fd\u65e0\u6cd5\u7406\u89e3\u5427\u3002</p><p>\u201c\u90a3\u4e48\uff0c\u544a\u8bc9\u6211\uff0c\u5854\u62c9\u590f\xb7\u7ef4\u82e5\u62c9\uff0c\u4f60\u662f\u4e3a\u4e86\u4ec0\u4e48\u800c\u6210\u4e3a\u5deb\u5e08\uff1f\u201d</p><p>\u9752\u5e74\u539f\u672c\u7565\u5e26\u5c16\u7ec6\u7684\u55d3\u97f3\uff0c\u5ffd\u7136\u53d8\u5f97\u4f4e\u6c89\u6d51\u539a\uff0c\u81ea\u5934\u9876\u4f20\u6765\uff0c\u5728\u8033\u7554\u8f70\u9e23\u73af\u7ed5\uff0c\u9707\u5f97\u7ef4\u82e5\u62c9\u604d\u60da\u5931\u795e\u3002\u5f53\u5979\u56de\u8fc7\u795e\u65f6\uff0c\u53d1\u73b0\u773c\u524d\u7684\u5c0f\u5973\u5b69\u4e0d\u89c1\u4e86\uff0c\u9752\u5e74\u7537\u5b50\u4e5f\u4e0d\u89c1\u4e86\uff0c\u84dd\u5929\u3001\u6c99\u6ee9\u3001\u6d77\u6c34\u4e5f\u5168\u90fd\u4e0d\u89c1\u4e86\uff0c\u53d6\u800c\u4ee3\u4e4b\u7684\u662f\u4e00\u5ea7\u5b8f\u4f1f\u6bbf\u5802\uff0c\u5de8\u5927\u7684\u767d\u7389\u77f3\u67f1\u652f\u6491\u7740\u51e0\u4e4e\u9ad8\u4e0d\u53ef\u89c1\u7684\u7a79\u9876\uff0c\u5730\u9762\u94fa\u7740\u5404\u79cd\u51e0\u4f55\u5f62\u72b6\u7684\u5927\u7406\u77f3\uff0c\u5728\u5979\u7684\u4e24\u65c1\uff0c\u65e0\u6570\u4eba\u6c89\u9ed8\u77d7\u7acb\uff0c\u9762\u5bb9\u51b7\u6f20\uff0c\u4f4e\u5934\u4e0d\u8bed\uff0c\u4ed6\u4eec\u90fd\u7a7f\u7740\u7070\u8272\u3001\u9ed1\u8272\u6216\u8005\u7070\u84dd\u8272\u7684\u5bbd\u5927\u888d\u670d\uff0c\u7f00\u7740\u4e00\u4e9b\u95ea\u95ea\u53d1\u5149\u7684\u94f6\u8272\u9970\u7247\u3002\u5728\u7ef4\u82e5\u62c9\u7684\u9762\u524d\uff0c\u662f\u4e00\u56e2\u84dd\u8272\u96fe\u6c14\uff0c\u6f02\u6d6e\u7740\uff0c\u65cb\u8f6c\u7740\uff0c\u53d8\u5e7b\u6210\u5404\u79cd\u5f62\u72b6\u3002</p><p>\u201c\u56de\u7b54\u6211\uff0c\u5973\u5b69\uff0c\u201d\u4f4e\u6c89\u7684\u58f0\u97f3\u518d\u6b21\u4ece\u96fe\u6c14\u4e2d\u4f20\u51fa\uff0c\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6210\u4e3a\u5deb\u5e08\uff1f\u201d</p><p>\u7ef4\u82e5\u62c9\u5145\u6ee1\u656c\u754f\u5730\u770b\u7740\u96fe\u6c14\uff0c\u5979\u77e5\u9053\u90a3\u4e2a\u58f0\u97f3\u7684\u4e3b\u4eba\u662f\u8c01\uff0c\u90a3\u662f\u88ab\u79f0\u4e3a\u201c\u5965\u672f\u4e4b\u4e3b\u201d\u7684\u963f\u7956\u65af\uff0c\u662f\u9b54\u6cd5\u5973\u795e\u7684\u4e08\u592b\u4e0e\u5de6\u53f3\u624b\uff0c\u662f\u5979\u9009\u62e9\u4e0e\u5c0a\u5949\u7684\u795e\u660e\u3002\u5979\u6df1\u6df1\u5730\u8eac\u8eab\uff0c\u7136\u540e\u633a\u8d77\u8170\u80cc\uff0c\u76f4\u89c6\u795e\u660e\uff0c\u9f13\u8db3\u52c7\u6c14\u5f00\u53e3\u3002</p><p>\u201c\u6211\u60f3\u8ba9\u6240\u6709\u4eba\u90fd\u4e86\u89e3\u5deb\u5e08\uff0c\u201d\u5973\u5deb\u5e08\u8bf4\uff0c\u201c\u6211\u60f3\u8ba9\u6240\u6709\u4eba\u90fd\u4e0d\u518d\u8bef\u89e3\u5deb\u5e08\uff0c\u4e0d\u518d\u654c\u89c6\u5deb\u5e08\uff0c\u4e5f\u4e0d\u518d\u5bb3\u6015\u5deb\u5e08\u3002\u201d</p><p>\u5979\u8bf4\u5b8c\uff0c\u9759\u9759\u7ad9\u5728\u539f\u5730\uff0c\u7b49\u5f85\u7740\u795e\u660e\u7684\u8bc4\u5224\u3002\u7136\u800c\u8fc7\u4e86\u597d\u534a\u664c\uff0c\u96fe\u6c14\u4e2d\u6ca1\u6709\u53d1\u51fa\u4efb\u4f55\u58f0\u97f3\uff0c\u751a\u81f3\u8fde\u539f\u672c\u7684\u5f62\u6001\u53d8\u5e7b\u4e5f\u505c\u6b62\u4e86\uff0c\u4eff\u4f5b\u51dd\u56fa\u4e00\u822c\u3002\u5973\u5deb\u5e08\u8be7\u5f02\u8d77\u6765\uff0c\u6b63\u60f3\u662f\u5426\u8981\u51fa\u8a00\u8be2\u95ee\uff0c\u4f4e\u8bed\u58f0\u7a81\u7136\u81ea\u80cc\u540e\u4f20\u6765\u3002</p><p>\u201c\u771f\u6709\u8da3\u3002\u201d</p><p>\u5973\u5deb\u5e08\u970d\u7136\u8f6c\u8eab\uff0c\u7136\u540e\u5979\u770b\u89c1\u4e86\u624e\u745e\u5c14\u3002\u706b\u53d1\u7684\u9b54\u59ec\u4e00\u88ad\u7d2b\u88d9\uff0c\u53cc\u624b\u73af\u62b1\u80f8\u524d\uff0c\u6b63\u9976\u6709\u5174\u81f4\u5730\u770b\u7740\u5979\uff0c\u8138\u4e0a\u7684\u795e\u60c5\u9887\u4e3a\u5947\u5f02\uff0c\u4f3c\u7b11\u975e\u7b11\uff0c\u4ee4\u4eba\u6349\u6478\u4e0d\u900f\u3002\u201c\u6709\u8da3\u7684\u60f3\u6cd5\u3002\u201d\u624e\u745e\u5c14\u518d\u6b21\u8bc4\u4ef7\u3002</p><p>\u201c\u4f60\u2026\u2026\u4f60\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\uff1f\u201d</p><p>\u624e\u745e\u5c14\u6ca1\u6709\u7406\u4f1a\u5973\u5deb\u5e08\u7684\u8d28\u95ee\uff0c\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4e48\u6211\u7ed9\u4f60\u4e00\u4e2a\u5efa\u8bae\uff0c\u5c0f\u59b9\u59b9\uff0c\u201d\u9b54\u59ec\u8bf4\uff0c\u201c\u5173\u6ce8\u90a3\u4e2a\u7537\u4eba\uff0c\u6216\u8bb8\u4ed6\u80fd\u591f\u5e2e\u4f60\u5b9e\u73b0\u68a6\u60f3\u3002\u201d</p><p>\u5173\u6ce8\u2026\u2026\u90a3\u4e2a\u7537\u4eba\uff1f</p><p>\u5973\u5deb\u5e08\u54d1\u7136\u5931\u7b11\uff0c\u201c\u8fd9\u662f\u9b54\u9b3c\u7684\u9884\u8a00\u5417\uff1f\u542c\u8d77\u6765\u5f88\u7c97\u7cd9\u554a\u3002\u201d</p><p>\u201c\u9664\u4e86\u67d0\u4e2a\u603b\u662f\u51fa\u4eba\u610f\u6599\u7684\u5bb6\u4f19\u4e4b\u5916\uff0c\u6211\u5bf9\u51e1\u4eba\u6240\u505a\u8fc7\u7684\u9884\u8a00\uff0c\u51c6\u786e\u7387\u53ef\u662f\u767e\u5206\u4e4b\u767e\u54e6\uff0c\u201d\u624e\u745e\u5c14\u683c\u683c\u7b11\u8d77\u6765\uff0c\u201c\u4e0d\u8fc7\u73b0\u5728\u4f60\u8be5\u9192\u4e86\uff0c\u5c0f\u59b9\u59b9\u3002\u201d</p><p>\u201c\u7b49\u7b49\u2026\u2026\u201d</p><p>\u7ef4\u82e5\u62c9\u8bdd\u97f3\u672a\u843d\uff0c\u4e00\u9635\u5929\u65cb\u5730\u8f6c\u611f\u9aa4\u7136\u88ad\u6765\uff0c\u773c\u524d\u7684\u4e00\u5207\u53d8\u5f97\u6c34\u6676\u822c\u900f\u660e\uff0c\u6084\u65e0\u58f0\u606f\u5730\u788e\u88c2\u6210\u65e0\u6570\u5757\uff0c\u7eb7\u7eb7\u6d12\u843d\u3002\u5468\u56f4\u518d\u6b21\u9677\u5165\u6c89\u6c89\u9ed1\u6697\u4e0e\u6b7b\u4e00\u822c\u7684\u5bc2\u9759\uff0c\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u5fae\u5f31\u7684\u4eae\u5149\u5728\u773c\u524d\u51fa\u73b0\uff0c\u9010\u6e10\u6269\u5927\uff0c\u7136\u540e\u5973\u5deb\u5e08\u53d1\u73b0\u81ea\u5df1\u9192\u4e86\u8fc7\u6765\u3002</p><p>\u7b2c\u4e00\u767e\u4e00\u5341\u8282</p><p>\u7ef4\u82e5\u62c9\u6c89\u9ed8\u4e86\u4e24\u79d2\u949f\uff0c\u7136\u540e\u5c06\u5934\u8f6c\u56de\u6765\uff0c\u548c\u624e\u745e\u5c14\u76f4\u89c6\u7740\uff0c\u201c\u4f60\u7a76\u7adf\u610f\u6b32\u4f55\u4e3a\uff0c\u90aa\u9b54\uff1f\u201d</p><p>\u9b54\u59ec\u839e\u5c14\u5fae\u7b11\uff0c\u4e1d\u6beb\u672a\u56e0\u5973\u5deb\u5e08\u7684\u6001\u5ea6\u800c\u4e0d\u60a6\uff0c\u201c\u6211\u60f3\u4f60\u6216\u8bb8\u6709\u6240\u8bef\u89e3\uff0c\u7ef4\u82e5\u62c9\u5c0f\u59d0\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u4eec\u5e76\u65e0\u6076\u610f\u3002\u201d</p><p>\u201c\u542c\u8d77\u6765\u5f88\u6709\u8bf4\u670d\u529b\uff0c\u201d\u5973\u5deb\u5e08\u8bf4\uff0c\u201c\u5c24\u5176\u5728\u8fd9\u79cd\u573a\u5408\u4e0b\u3002\u201d</p><p>\u201c\u6ca1\u9519\uff0c\u56e0\u4e3a\u6211\u4eec\u662f\u6765\u62ef\u6551\u4f60\u7684\u3002\u201d</p><p>\u201c\u62ef\u6551\u6211\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u624e\u745e\u5c14\u70b9\u5934\uff0c\u201c\u5f53\u7136\uff0c\u4e0d\u4ec5\u4ec5\u662f\u4e3a\u4e86\u62ef\u6551\u4f60\uff0c\u66f4\u662f\u4e3a\u4e86\u7ef4\u62a4\u4e16\u754c\u7684\u548c\u5e73\u4e0e\u7a33\u5b9a\uff0c\u4e3a\u4e86\u5927\u9646\u4f17\u751f\u7684\u798f\u7949\u4e0e\u5b89\u5b81\u3002\u201d</p><p>\u5657\uff01</p><p>\u542c\u5230\u9b54\u59ec\u7684\u8bf4\u6cd5\uff0c\u7ef4\u82e5\u62c9\u56fa\u7136\u662f\u83ab\u540d\u5176\u5999\uff0c\u743c\u6069\u5728\u4e00\u65c1\u4e5f\u540c\u6837\u9519\u6115\u4e0d\u5df2\u3002\u624e\u745e\u5c14\u8fd9\u73a9\u7b11\u4e5f\u672a\u514d\u5f00\u5f97\u592a\u79bb\u8c31\uff0c\u81ea\u5df1\u4ece\u8428\u739b\u65af\u7279\u624b\u91cc\u628a\u7ef4\u82e5\u62c9\u501f\u8fc7\u6765\uff0c\u552f\u4e00\u76ee\u7684\u5c31\u662f\u8981\u901a\u8fc7\u63a8\u5012\u5979\u6765\u7a83\u53d6\u795e\u529b\uff0c\u65e0\u8bba\u4ee5\u4f55\u79cd\u6807\u51c6\u6765\u770b\uff0c\u8fd9\u79cd\u884c\u4e3a\u90fd\u79f0\u4e0d\u4e0a\u5149\u660e\u6b63\u5927\uff0c\u600e\u4e48\u5728\u624e\u745e\u5c14\u53e3\u4e2d\u8bf4\u6765\uff0c\u4e0d\u4ec5\u5802\u800c\u7687\u4e4b\uff0c\u751a\u81f3\u8fd8\u4e0a\u5347\u5230\u4e86\u5173\u7cfb\u4e16\u754c\u548c\u5e73\u8fd9\u79cd\u9ad8\u5ea6\u3002\u4f60\u8fd9\u4e48\u8bf4\u641e\u5f97\u6211\u538b\u529b\u597d\u5927\uff0c\u8bf4\u4e0d\u5b9a\u5230\u65f6\u5019\u4f1a\u4e0d\u4e3e\u7684\u3002</p><p>\u800c\u4e14\uff0c\u4f60\u4e00\u4e2a\u5927\u9b54\u9b3c\uff0c\u8bf4\u4ec0\u4e48\u7ef4\u62a4\u4e16\u754c\u548c\u5e73\u7a33\u5b9a\uff0c\u8fd9\u771f\u7684\u6ca1\u95ee\u9898\u4e48\uff1f</p><p>\u201c\u6ca1\u95ee\u9898\u554a\uff0c\u5bf9\u4e8e\u6211\u4eec\u5730\u72f1\u800c\u8a00\uff0c\u4e00\u4e2a\u7a33\u5b9a\u6709\u5e8f\u7684\u51e1\u95f4\u66f4\u7b26\u5408\u6211\u4eec\u7684\u5229\u76ca\uff0c\u201d\u624e\u745e\u5c14\u56de\u7b54\u5f97\u7406\u6240\u5f53\u7136\uff0c\u201c\u4e0d\u80fd\u6db8\u6cfd\u800c\u6e14\uff0c\u8981\u8bb2\u7a76\u957f\u671f\u7a33\u5b9a\u7684\u53ef\u6301\u7eed\u6027\u53d1\u5c55\u561b\u3002\u201d</p><p>\u867d\u7136\u6ca1\u592a\u542c\u61c2\u4ec0\u4e48\u610f\u601d\uff0c\u4f46\u4f3c\u4e4e\u5f88\u6709\u9053\u7406\u7684\u6837\u5b50\u3002</p><p>\u201c\u6240\u4ee5\u4f60\u62ef\u6551\u4e16\u754c\u7684\u65b9\u5f0f\uff0c\u5c31\u662f\u6002\u607f\u4f60\u7684\u5c0f\u7537\u53cb\u5f3a\u66b4\u6211\uff1f\u201d</p><p>\u201c\u63aa\u8f9e\u6709\u5f85\u5546\u69b7\uff0c\u4e0d\u8fc7\u5927\u81f4\u610f\u601d\u5c31\u662f\u8fd9\u6837\u6ca1\u9519\uff0c\u201d\u624e\u745e\u5c14\u7b11\u7740\u8bf4\uff0c\u201c\u867d\u7136\u542c\u8d77\u6765\u4f3c\u4e4e\u6709\u90a3\u4e48\u4e00\u70b9\u70b9\u5947\u602a\uff0c\u4f46\u6211\u4f1a\u8bc1\u660e\u7ed9\u4f60\u770b\u7684\u3002\u201d</p><p>\u5973\u5deb\u5e08\u51b7\u7b11\uff0c\u4f46\u5f53\u5979\u770b\u89c1\u90a3\u4e00\u56e2\u5fae\u5f31\u8679\u96fe\u5728\u743c\u6069\u7684\u6307\u5c16\u51fa\u73b0\u65f6\uff0c\u8138\u8272\u987f\u65f6\u53d8\u4e86\uff0c\u5148\u662f\u6115\u7136\uff0c\u7ee7\u800c\u6124\u6012\u3002\u201c\u4f60\u600e\u4e48\u4f1a\u2026\u2026\u4f60\u80c6\u6562\u7a83\u53d6\u795e\u529b\uff01\u201d</p><p>\u201c\u662f\u62a2\u593a\uff0c\u4e0d\u662f\u7a83\u53d6\uff0c\u201d\u624e\u745e\u5c14\u7ea0\u6b63\uff0c\u201c\u800c\u4e14\u6211\u89c9\u5f97\u4f60\u66f4\u5e94\u8be5\u5173\u5fc3\u7684\uff0c\u662f\u4ed6\u5982\u4f55\u80fd\u591f\u505a\u5230\u8fd9\u70b9\u5427\uff1f\u201d</p><p>\u201c\u2026\u2026\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u201c\u5c31\u50cf\u4f60\u5df2\u7ecf\u731c\u5230\u7684\u90a3\u6837\uff0c\u201d\u9b54\u59ec\u8bf4\uff0c\u201c\u4ed6\u53ef\u4ee5\u53d6\u8d70\u4f60\u4f53\u5185\u7684\u2018\u8679\u96fe\u2019\uff0c\u51c6\u786e\u5730\u8bf4\uff0c\u4ed6\u53ef\u4ee5\u53d6\u8d70\u4efb\u4f55\u5973\u6027\u9009\u6c11\u6216\u8005\u795e\u5b50\u6240\u62e5\u6709\u7684\u795e\u529b\u2014\u2014\u901a\u8fc7\u505a\u7231\u7684\u65b9\u5f0f\u3002\u975e\u5e38\u522b\u51fa\u5fc3\u88c1\u7684\u624b\u6bb5\uff0c\u662f\u4e0d\u662f\uff1f\u201d</p><p>\u201c\u6807\u51c6\u7684\u90aa\u9b54\u4f5c\u98ce\u3002\u201d\u5973\u5deb\u5e08\u51b7\u51b7\u8bc4\u4ef7\u3002</p><p>\u201c\u8fd9\u53ef\u4e0d\u662f\u6211\u6559\u7684\u54e6\uff0c\u201d\u624e\u745e\u5c14\u8868\u793a\u65e0\u8f9c\uff0c\u201c\u4ed6\u81ea\u5df1\u4e0d\u77e5\u9053\u4ece\u54ea\u91cc\u5b66\u6765\u7684\uff0c\u4e0e\u6211\u65e0\u5173\u3002\u201d</p><p>\u201c\u6211\u53ef\u4ee5\u8bf4\u7269\u4ee5\u7c7b\u805a\u5417\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u8bc4\u4ef7\u5f88\u51c6\u786e\uff0c\u201d\u743c\u6069\u4e0d\u7d27\u4e0d\u6162\u5730\u63d2\u4e86\u53e5\uff0c\u201c\u6211\u4eec\u672c\u6765\u5c31\u662f\u60c5\u4fa3\u3002\u201d</p><p>\u770b\u7740\u9b54\u59ec\u7b11\u76c8\u76c8\u5730\u633d\u7740\u743c\u6069\u7684\u80f3\u818a\uff0c\u4e00\u8138\u751c\u871c\u5e78\u798f\u7684\u7b11\u5bb9\uff0c\u5973\u5deb\u5e08\u4e0d\u7531\u5f97\u4e00\u65f6\u95f4\u6709\u79cd\u5f3a\u70c8\u7684\u865a\u5e7b\u4e0d\u5b9e\u611f\u3002\u201c\u56de\u5230\u6b63\u9898\u5427\uff0c\u201d\u5979\u5b9a\u4e86\u5b9a\u795e\uff0c\u8bf4\uff0c\u201c\u8fd9\u4e9b\u548c\u4f60\u6240\u8c13\u7684\u62ef\u6551\u4e16\u754c\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d</p><p>\u201c\u8428\u739b\u65af\u7279\u4e3a\u4ec0\u4e48\u4fd8\u864f\u4f60\uff0c\u8fd9\u5176\u4e2d\u7684\u539f\u56e0\uff0c\u4f60\u662f\u5426\u6e05\u695a\u5462\uff1f\u201d</p><p>\u201c\u77e5\u9053\u4e00\u70b9\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u4ed6\u4f3c\u4e4e\u662f\u60f3\u53d1\u52a8\u4e00\u4e2a\u7c7b\u4f3c\u2018\u5361\u5c14\u8428\u65af\u4e4b\u5316\u8eab\u2019\u7684\u6cd5\u672f\uff0c\u4ee5\u6211\u4e3a\u5bb9\u5668\u6765\u5f3a\u5236\u795e\u660e\u964d\u4e34\uff0c\u518d\u51fb\u6740\u795e\u660e\u4ee5\u53d6\u800c\u4ee3\u4e4b\u3002\u201d</p><p>\u201c\u6b63\u786e\uff0c\u5982\u679c\u4ed6\u7684\u8ba1\u5212\u5f97\u901e\uff0c\u663e\u7136\u4f1a\u5bf9\u4e16\u754c\u548c\u5e73\u9020\u6210\u4e25\u91cd\u4e0d\u5229\u7684\u5f71\u54cd\uff0c\u4e0d\u662f\u5417\u3002\u201d</p><p>\u201c\u662f\u6ca1\u9519\uff0c\u4f46\u662f\u2026\u2026\u201d</p><p>\u201c\u6240\u4ee5\u6211\u4eec\u8981\u52aa\u529b\u963b\u6b62\u4ed6\uff0c\u5bf9\u5427\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u8981\u963b\u6b62\u4ed6\u6709\u4e24\u79cd\u529e\u6cd5\uff0c\u7b2c\u4e00\u79cd\u662f\u76f4\u63a5\u6253\u5012\u4ed6\uff0c\u8fd9\u4e2a\u6bd4\u8f83\u9ebb\u70e6\uff0c\u5148\u8df3\u8fc7\uff1b\u7b2c\u4e8c\u79cd\u5219\u662f\u91dc\u5e95\u62bd\u85aa\uff0c\u7834\u574f\u5176\u65bd\u6cd5\u8981\u7d20\uff0c\u8ba9\u4ed6\u65e0\u6cd5\u5b8c\u6210\u6cd5\u672f\uff0c\u8fd9\u4e2a\u96be\u5ea6\u5c31\u6bd4\u8f83\u4f4e\u4e86\u3002\u201d</p><p>\u201c\u4f60\u6240\u8c13\u7684\u65bd\u6cd5\u8981\u7d20\u662f\u6307\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5f53\u7136\u5c31\u662f\u4f60\u554a\u3002\u201d</p><p>\u624e\u745e\u5c14\u7b11\u7740\u8bf4\uff0c\u201c\u5bf9\u4e8e\u8428\u739b\u65af\u7279\u8981\u8fdb\u884c\u7684\u6cd5\u672f\u800c\u8a00\uff0c\u4f60\u662f\u6700\u5173\u952e\u8981\u7d20\u4e4b\u4e00\u3002\u4f60\u662f\u9009\u6c11\uff0c\u53ea\u6709\u501f\u52a9\u4f60\u4e0e\u963f\u7956\u65af\u4e4b\u95f4\u7684\u2018\u5173\u8054\u6027\u2019\uff0c\u4ed6\u624d\u80fd\u5f3a\u5236\u795e\u660e\u964d\u4e34\u3002\u4e5f\u5373\u662f\u8bf4\uff0c\u53ea\u8981\u7834\u574f\u4e86\u8fd9\u79cd\u2018\u5173\u8054\u6027\u2019\uff0c\u8428\u739b\u65af\u7279\u5c31\u6ca1\u6cd5\u5b8c\u6210\u6cd5\u672f\u4e86\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u6c89\u9ed8\u7740\uff0c\u5979\u5df2\u7ecf\u660e\u767d\u4e86\u9b54\u59ec\u7684\u610f\u601d\u3002</p><p>\u201c\u963f\u7956\u65af\u7684\u4fe1\u5f92\u867d\u7136\u5c11\uff0c\u4f46\u4e5f\u4e0d\u662f\u53ea\u6709\u4f60\u4e00\u4e2a\uff0c\u201d\u624e\u745e\u5c14\u7ee7\u7eed\u8bf4\uff0c\u201c\u8bba\u4fe1\u4ef0\uff0c\u4f60\u672a\u89c1\u5f97\u6700\u8654\u8bda\uff1b\u8bba\u8d44\u5386\uff0c\u4f60\u672a\u89c1\u5f97\u6700\u6df1\u539a\uff1b\u800c\u8981\u8bba\u4fd8\u864f\u7684\u96be\u5ea6\uff0c\u4f60\u80af\u5b9a\u662f\u6700\u9ad8\u7684\u2014\u2014\u8428\u9a6c\u65af\u7279\u820d\u6613\u6c42\u96be\uff0c\u653e\u7740\u5176\u4ed6\u4eba\u4e0d\u7ba1\uff0c\u975e\u8981\u76ef\u7740\u4f60\uff0c\u5f88\u663e\u7136\uff0c\u4f60\u6709\u67d0\u79cd\u5176\u4ed6\u963f\u7956\u65af\u7684\u4fe1\u5f92\u90fd\u4e0d\u5177\u5907\u7684\u4e1c\u897f\uff0c\u5b83\u5c31\u662f\u8428\u9a6c\u65af\u7279\u7684\u76ee\u6807\uff0c\u6216\u8005\u8bf4\u662f\u4ed6\u8ba1\u5212\u4e2d\u7684\u5173\u952e\u8981\u7d20\u3002\u201d</p><p>\u663e\u7136\uff0c\u8fd9\u6240\u8c13\u7684\u201c\u5173\u952e\u8981\u7d20\u201d\uff0c\u5c31\u662f\u7ef4\u82e5\u62c9\u8eab\u4e3a\u963f\u7956\u65af\u552f\u4e00\u9009\u6c11\uff0c\u72ec\u5bb6\u6240\u6709\u7684\u201c\u8679\u96fe\u201d\u4e86\u3002\u7531\u6b64\u63a8\u8bba\uff0c\u53ea\u8981\u80fd\u591f\u8ba9\u7ef4\u82e5\u62c9\u5931\u53bb\u2014\u2014\u54ea\u6015\u662f\u6682\u65f6\u5931\u53bb\u2014\u2014\u8679\u96fe\uff0c\u8428\u9a6c\u65af\u7279\u7684\u8ba1\u5212\u5c31\u65e0\u6cd5\u7ee7\u7eed\u8fdb\u884c\u4e0b\u53bb\u3002</p><p>\u9009\u6c11\u7684\u795e\u529b\u662f\u795e\u660e\u6240\u6388\u4e88\uff0c\u81ea\u5df1\u65e0\u6cd5\u653e\u5f03\uff0c\u5374\u53ef\u4ee5\u88ab\u795e\u660e\u6536\u56de\uff0c\u6700\u8457\u540d\u7684\u4f8b\u5b50\u5c31\u662f\u8428\u9a6c\u65af\u7279\uff0c\u4ed6\u4e5f\u66fe\u662f\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\uff0c\u56e0\u4e3a\u80cc\u53db\u6b63\u9053\uff0c\u88ab\u963f\u7956\u65af\u4ee3\u8868\u5973\u795e\u964d\u4e34\u51e1\u95f4\uff0c\u6536\u56de\u4e86\u4ed6\u7684\u94f6\u706b\u3002\u4f46\u73b0\u5728\u5374\u4e0d\u884c\uff0c\u7b2c\u4e94\u79d8\u5668\u201c\u4e5d\u91cd\u5730\u72f1\u4e4b\u9f0e\u201d\u521b\u9020\u51fa\u7684\u5c01\u7edd\u7a7a\u95f4\u5c06\u4e00\u5207\u795e\u660e\u963b\u9694\u5728\u5916\uff0c\u8fde\u838e\u5c14\u8fd9\u79cd\u9ad8\u7b49\u795e\u90fd\u8fdb\u4e0d\u6765\uff0c\u66f4\u522b\u63d0\u963f\u7956\u65af\u8fd9\u79cd\u534a\u795e\u4e86\uff0c\u8428\u739b\u65af\u7279\u4e4b\u6240\u4ee5\u8981\u8bf7\u51ef\u745f\u7433\u51fa\u624b\u76f8\u52a9\uff0c\u5176\u4e2d\u4e00\u4e2a\u5f88\u91cd\u8981\u7684\u539f\u56e0\u4e5f\u5c31\u5728\u4e8e\u6b64\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u4ed6\u8fd9\u8fb9\u7d27\u9523\u5bc6\u9f13\u5730\u65bd\u6cd5\uff0c\u90a3\u8fb9\u963f\u7956\u65af\u76f4\u63a5\u5c06\u7ef4\u82e5\u62c9\u7684\u795e\u529b\u6536\u56de\uff0c\u90a3\u6574\u4e2a\u4e8b\u60c5\u5c31\u6210\u5927\u7b11\u8bdd\u4e86\u3002</p><p>\u201c\u672c\u6765\u8fd9\u4ef6\u4e8b\u662f\u633a\u68d8\u624b\u7684\uff0c\u4e0d\u8fc7\u5e78\u597d\u6709\u4ed6\u5728\uff0c\u4e00\u5207\u95ee\u9898\u90fd\u8fce\u5203\u800c\u89e3\uff0c\u201d\u624e\u745e\u5c14\u62cd\u4e86\u62cd\u743c\u6069\u7684\u80a9\u8180\uff0c\u201c\u4f60\u5df2\u7ecf\u770b\u5230\u4e86\uff0c\u4ed6\u53ef\u4ee5\u53d6\u8d70\u4f60\u7684\u8679\u96fe\uff0c\u4e00\u6b21\u641e\u4e0d\u5b9a\uff0c\u591a\u6765\u51e0\u6b21\u5c31\u597d\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u53ea\u8981\u4f60\u80af\u4e56\u4e56\u5f20\u5f00\u817f\u8ba9\u4ed6\u5e72\uff0c\u8fd9\u4e2a\u4e16\u754c\u5c31\u6709\u6551\u4e86\u2014\u2014\u54a6\uff0c\u743c\u6069\uff0c\u4f60\u4e00\u5e45\u76ee\u77aa\u53e3\u5446\u7684\u6837\u5b50\u770b\u7740\u6211\u505a\u4ec0\u4e48\uff1f\u60ca\u559c\u8fc7\u5ea6\u8111\u888b\u62bd\u7b4b\u4e86\uff1f\u201d</p><p>\u201c\u4e0d\uff0c\u6211\u53ea\u662f\u60f3\u8bf4\u2026\u2026\u624e\u745e\u5c14\uff0c\u4f60\u771f\u662f\u592a\u5f3a\u4e86\u3002\u201d</p><p>\u62ef\u6551\u4e16\u754c\u8fd9\u4e48\u4e25\u8083\u7684\u8bdd\u9898\uff0c\u5c45\u7136\u80fd\u591f\u88ab\u9b54\u59ec\u62ff\u6765\u548c\u7537\u5973\u505a\u7231\u8054\u7cfb\u5728\u4e00\u8d77\uff0c\u800c\u4e14\u8fd8\u8054\u7cfb\u5f97\u8fd9\u4e48\u6d51\u7136\u5929\u6210\uff0c\u6beb\u65e0\u7834\u7efd\u75d5\u8ff9\uff0c\u4f5c\u4e3a\u65c1\u542c\u8005\uff0c\u743c\u6069\u5df2\u7ecf\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u8bc4\u4ef7\uff0c\u6700\u540e\u53ea\u80fd\u8bf4\u4e00\u58f0\u4f69\u670d\u3002\u4e0d\u8fc7\u5373\u4fbf\u5982\u6b64\uff0c\u603b\u89c9\u5f97\u8fd8\u662f\u54ea\u91cc\u4e0d\u592a\u5bf9\u52b2\uff0c\u6709\u70b9\u602a\u602a\u7684\u2026\u2026</p><p>\u201c\u6ca1\u4ec0\u4e48\u5566\uff0c\u6240\u8c13\u2018\u7528\u7231\u6551\u4e16\u754c\u2019\u561b\uff0c\u5f88\u6b63\u5e38\u5f88\u52b1\u5fd7\u7684\u5267\u60c5\uff0c\u6545\u4e8b\u91cc\u90fd\u662f\u8fd9\u4e48\u5199\u7684\u3002\u201d</p><p>\u2026\u2026\u7231\u548c\u505a\u7231\u597d\u50cf\u4e0d\u662f\u4e00\u56de\u4e8b\u5427\u3002</p><p>\u201c\u5dee\u4e0d\u591a\u5566\u3002\u201d</p><p>\u201c\u660e\u660e\u5dee\u5f88\u591a\u2026\u2026\u7b97\u4e86\uff0c\u4f60\u8bf4\u5dee\u4e0d\u591a\u5c31\u5dee\u4e0d\u591a\u5427\u3002\u201d</p><p>\u743c\u6069\u660e\u667a\u5730\u653e\u5f03\u4e86\u4e89\u8bba\uff0c\u5c06\u76ee\u5149\u8f6c\u5411\u4f20\u9053\u5deb\u5e08\u3002</p><p>\u624e\u745e\u5c14\u7684\u63a8\u8bba\u542c\u8d77\u6765\u5f88\u6709\u9053\u7406\u2014\u2014\u4f46\u5e76\u975e\u5168\u65e0\u7834\u7efd\u3002\u5c31\u4e8b\u8bba\u4e8b\u800c\u8a00\uff0c\u743c\u6069\u80fd\u591f\u7a83\u53d6\u795e\u529b\uff0c\u8fd9\u70b9\u81ea\u7136\u662f\u4e8b\u5b9e\uff0c\u4e5f\u53ef\u4ee5\u8bc1\u660e\uff0c\u4f46\u201c\u7a83\u8d70\u795e\u529b\u201d\u5374\u672a\u5fc5\u5c31\u7b49\u4e8e\u662f\u5b8c\u5168\u65a9\u65ad\u4e86\u9009\u6c11\u4e0e\u795e\u7947\u4e4b\u95f4\u7684\u8054\u7cfb\uff0c\u4e5f\u672a\u5fc5\u5c31\u771f\u80fd\u7834\u574f\u8428\u739b\u65af\u7279\u7684\u6cd5\u672f\u3002\u5f52\u6839\u7ed3\u5e95\uff0c\u518d\u6709\u9053\u7406\u7684\u201c\u63a8\u8bba\u201d\uff0c\u7ec8\u7a76\u4e5f\u53ea\u662f\u63a8\u8bba\u800c\u5df2\uff0c\u548c\u201c\u4e8b\u5b9e\u201d\u4e0d\u80fd\u5212\u7b49\u53f7\u3002\u66f4\u4f55\u51b5\uff0c\u5f88\u591a\u65f6\u5019\uff0c\u201c\u4e8b\u5b9e\u201d\u8fd9\u79cd\u4e1c\u897f\uff0c\u6070\u6070\u662f\u4e0d\u8bb2\u9053\u7406\u7684\u3002\u6362\u4e86\u743c\u6069\uff0c\u662f\u5426\u4f1a\u76f8\u4fe1\u624e\u745e\u5c14\u7684\u8fd9\u756a\u8bf4\u8f9e\uff0c\u53ea\u6015\u8981\u6253\u4e2a\u5927\u5927\u7684\u95ee\u53f7\u3002</p><p>\u201c\u4f60\u4eec\u7684\u76ee\u7684\u662f\u4ec0\u4e48\uff1f\u201d\u5973\u5deb\u5e08\u7a81\u7136\u95ee\u3002</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u4e00\u4f4d\u591c\u5973\u58eb\u9009\u6c11\uff0c\u4e00\u4f4d\u5730\u72f1\u9b54\u59ec\uff0c\u8981\u6211\u76f8\u4fe1\u4f60\u4eec\u5bf9\u62ef\u6551\u4e16\u754c\u5982\u6b64\u70ed\u5fc3\uff0c\u5b9e\u5728\u662f\u4e0d\u592a\u5bb9\u6613\uff0c\u201d\u5973\u5deb\u5e08\u5e73\u9759\u5730\u8bf4\uff0c\u201c\u4ece\u4f60\u4eec\u7684\u7acb\u573a\u6765\u8bf4\uff0c\u5e94\u8be5\u662f\u770b\u5230\u4e16\u754c\u6bc1\u706d\u624d\u6bd4\u8f83\u5f00\u5fc3\u5427\u3002\u201d</p><p>\u201c\u4e0d\u4e0d\uff0c\u4e16\u754c\u6bc1\u706d\u6709\u4ec0\u4e48\u597d\u671f\u5f85\u7684\uff0c\u6211\u4eec\u53c8\u4e0d\u662f\u6df1\u6e0a\u91cc\u90a3\u5e2e\u5fc3\u7406\u4e0d\u5065\u5eb7\u7684\u7834\u574f\u72c2\uff0c\u201d\u624e\u745e\u5c14\u6447\u6447\u624b\u6307\uff0c\u7ea0\u6b63\u7ef4\u82e5\u62c9\uff0c\u201c\u5f53\u7136\u4e86\uff0c\u5766\u767d\u5730\u8bf4\uff0c\u62ef\u6551\u4e16\u754c\u4ec0\u4e48\u7684\uff0c\u4e5f\u786e\u5b9e\u6ca1\u591a\u5927\u5174\u8da3\u5c31\u662f\u4e86\uff0c\u90a3\u662f\u6b63\u4e49\u82f1\u96c4\u4eec\u7684\u5de5\u4f5c\uff0c\u4f5c\u4e3a\u53ef\u7231\u53c8\u8ff7\u4eba\u7684\u53cd\u6d3e\u89d2\u8272\uff0c\u6211\u4eec\u6ca1\u5fc5\u8981\u8d8a\u4fce\u4ee3\u5e96\uff0c\u201d\u5979\u5ffd\u7136\u7b11\u4e86\u7b11\uff0c\u201c\u5982\u679c\u4f60\u4e00\u5b9a\u8981\u542c\u5230\u4e00\u4e2a\u5408\u7406\u89e3\u91ca\u624d\u5b89\u5fc3\u7684\u8bdd\u2014\u2014\u90a3\u4e48\uff0c\u5c31\u50cf\u4f60\u770b\u89c1\u7684\uff0c\u4ed6\u5bf9\u2018\u8679\u96fe\u2019\u633a\u611f\u5174\u8da3\u3002\u5982\u679c\u4f60\u613f\u610f\u6307\u70b9\u4e00\u4e8c\uff0c\u90a3\u5c31\u6700\u597d\u4e0d\u8fc7\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u662f\u4ed6\u7684\u76ee\u7684\uff0c\u4f60\u5462\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u4f5c\u4e3a\u5973\u53cb\uff0c\u5e2e\u52a9\u7537\u53cb\u63a8\u5012\u4ed6\u770b\u4e2d\u7684\u5973\u4eba\uff0c\u662f\u6211\u7684\u672c\u804c\u5de5\u4f5c\u561b\u3002\u201d</p><p>\u2026\u2026</p><p>\u201c\u6211\u4e0d\u662f\u5f88\u76f8\u4fe1\u4f60\u7684\u8bdd\uff0c\u201d\u6c89\u9ed8\u4e86\u7247\u523b\uff0c\u5973\u5deb\u5e08\u8bf4\uff0c\u201c\u4f46\u6211\u6ca1\u6709\u4ec0\u4e48\u9009\u62e9\u4f59\u5730\uff0c\u662f\u5427\u3002\u201d</p><p>\u201c\u4f3c\u4e4e\u662f\u6ca1\u6709\u3002\u201d\u9b54\u59ec\u627f\u8ba4\u3002</p><p>\u201c\u660e\u767d\u4e86\uff0c\u201d\u5973\u5deb\u5e08\u8bf4\uff0c\u201c\u6211\u53ef\u4ee5\u5408\u4f5c\uff0c\u4f46\u6211\u6709\u4e2a\u6761\u4ef6\u3002\u201d</p><p>\u201c\u8bf4\u6765\u542c\u542c\u3002\u201d</p><p>\u201c\u6211\u8981\u4f60\u79bb\u5f00\u8fd9\u91cc\uff0c\u8ba9\u6211\u548c\u4ed6\u5355\u72ec\u76f8\u5904\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8fd9\u5c31\u662f\u6211\u7684\u6761\u4ef6\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u8ba9\u624e\u745e\u5c14\u79bb\u5f00\uff1f</p><p>\u743c\u6069\u6014\u4e86\u4e00\u4e0b\uff0c\u4ed6\u539f\u672c\u4ee5\u4e3a\u7ef4\u82e5\u62c9\u4f1a\u63d0\u51fa\u201c\u4ece\u8428\u9a6c\u65af\u7279\u624b\u4e2d\u5c06\u5979\u6551\u51fa\u201d\uff0c\u6216\u8005\u201c\u5e26\u4fe1\u7ed9\u51ef\u5c14\u672c\u7b49\u4eba\u201d\uff0c\u8bf8\u5982\u6b64\u7c7b\u7684\u8981\u6c42\u3002\u5b8c\u5168\u6ca1\u60f3\u5230\u5979\u5c45\u7136\u4f1a\u5f00\u51fa\u8fd9\u4e2a\u6761\u4ef6\u2014\u2014\u8fd9\u5bf9\u5979\u6709\u4ec0\u4e48\u610f\u4e49\uff1f</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u4ed6\u4e0b\u610f\u8bc6\u5730\u95ee\u3002</p><p>\u201c\u90a3\u4e9b\u4f60\u4e0d\u53ef\u80fd\u7b54\u5e94\u7684\u6761\u4ef6\uff0c\u8bf4\u4e86\u4e5f\u53ea\u662f\u6d6a\u8d39\u65f6\u95f4\uff0c\u8fd8\u4e0d\u5982\u63d0\u4e2a\u73b0\u5b9e\u70b9\u7684\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u6211\u4e0d\u559c\u6b22\u5979\uff0c\u800c\u4e14\uff0c\u201d\u5979\u54ac\u7740\u5634\u5507\uff0c\u201c\u6211\u4e0d\u60f3\u2026\u2026\u90a3\u79cd\u65f6\u5019\u2026\u2026\u6709\u4eba\u5728\u65c1\u8fb9\u770b\u7740\u3002\u201d</p><p>\u743c\u6069\u6b63\u81ea\u72b9\u8c6b\uff0c\u624e\u745e\u5c14\u5374\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u5c31\u8fd9\u4e2a\u6761\u4ef6\u5417\uff1f\u90a3\u8bf4\u5b9a\u4e86\u3002\u201d</p><p>\u201c\u624e\u745e\u5c14\uff1f\u201d</p><p>\u8fce\u7740\u743c\u6069\u8ff7\u60d1\u7684\u76ee\u5149\uff0c\u9b54\u59ec\u53ef\u7231\u5730\u8038\u4e86\u8038\u80a9\uff0c\u201c\u56e0\u4e3a\u6211\u672c\u6765\u5c31\u51c6\u5907\u8981\u79bb\u5f00\u4e86\u5440\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u7559\u5728\u8fd9\u91cc\u4e5f\u6ca1\u4ec0\u4e48\u4e8b\u53ef\u505a\u5427\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u8fd9\u4e5d\u5929\u91cc\uff0c\u4f60\u5e94\u8be5\u628a\u5168\u90e8\u7cbe\u529b\u90fd\u82b1\u5728\u5979\u8eab\u4e0a\u624d\u5bf9\uff0c\u6211\u53ea\u80fd\u5728\u65c1\u8fb9\u770b\u7740\uff0c\u4f1a\u5f88\u65e0\u804a\u7684\u3002\u800c\u4e14\u6211\u4e5f\u5f88\u4e45\u6ca1\u89c1\u5230\u59d0\u59d0\u4e86\uff0c\u60f3\u53bb\u548c\u5979\u804a\u804a\u5929\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u521a\u521a\u624d\u548c\u5979\u5bc6\u8c08\u8fc7\u4e48\uff0c\u201d\u743c\u6069\u534a\u771f\u534a\u5047\u5730\u62b1\u6028\uff0c\u201c\u8fd8\u90fd\u7792\u7740\u6211\u3002\u201d</p><p>\u201c\u5973\u4eba\u4e4b\u95f4\u603b\u662f\u6709\u5f88\u591a\u6084\u6084\u8bdd\u8981\u8bf4\uff0c\u8fd9\u70b9\u4f60\u4eec\u7537\u4eba\u662f\u6c38\u8fdc\u4e0d\u4f1a\u7406\u89e3\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u597d\u5427\u3002\u201d</p><p>\u624e\u745e\u5c14\u7684\u7406\u7531\u6709\u4e9b\u7275\u5f3a\uff0c\u4f46\u8981\u79bb\u5f00\u7684\u610f\u601d\u5df2\u7ecf\u8868\u8fbe\u5f97\u5f88\u6e05\u695a\uff0c\u800c\u4e14\u5979\u6240\u8bf4\u4e5f\u4e0d\u65e0\u9053\u7406\uff0c\u4e0e\u5176\u5728\u8fd9\u91cc\u5f53\u89c2\u4f17\uff0c\u8fd8\u4e0d\u5982\u51fa\u53bb\u548c\u51ef\u745f\u7433\u804a\u804a\u5929\u3002\u542c\u8bf4\u5979\u4eec\u7684\u5173\u7cfb\u4e0d\u662f\u5f88\u597d\uff0c\u8d81\u6b64\u673a\u4f1a\u591a\u89c1\u89c1\u9762\uff0c\u8c08\u8c08\u5fc3\uff0c\u57f9\u517b\u57f9\u517b\u611f\u60c5\uff0c\u4e5f\u672a\u5c1d\u4e0d\u662f\u597d\u4e8b\u3002</p><p>\u201c\u90a3\u4e48\uff0c\u6211\u5148\u8d70\u4e86\uff0c\u201d\u624e\u745e\u5c14\u7b11\u7740\u671d\u743c\u6069\u6446\u6446\u624b\uff0c\u8d70\u8fdb\u6e29\u6cc9\u65f6\u892a\u4e0b\u653e\u5728\u6c60\u8fb9\u7684\u8863\u88d9\u81ea\u52a8\u98de\u4e86\u56de\u6765\uff0c\u7a7f\u5728\u8eab\u4e0a\uff0c\u906e\u4f4f\u80f4\u4f53\uff0c\u201c\u548c\u5c0f\u59b9\u59b9\u73a9\u5f97\u5f00\u5fc3\u70b9\u54e6\u3002\u201d</p><p>\u9b54\u59ec\u8f6c\u8fc7\u8eab\uff0c\u7136\u540e\u5728\u89c6\u7ebf\u4e2d\u6d88\u5931\uff0c\u6e29\u6cc9\u4e2d\u53ea\u5269\u4e0b\u4e00\u5bf9\u8d64\u88f8\u7684\u7537\u5973\u3002\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u89c9\u5f97\u4e0d\u5e94\u8be5\u518d\u6d6a\u8d39\u65f6\u95f4\u2014\u2014\u6216\u8005\u8bf4\uff0c\u4e4b\u524d\u5b9e\u5728\u5df2\u7ecf\u6d6a\u8d39\u592a\u591a\u7684\u65f6\u95f4\u4e86\u3002\u4e2d\u573a\u4f11\u606f\u5f97\u4e5f\u591f\u4e45\uff0c\u8be5\u7ee7\u7eed\u5f00\u5de5\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u5361\u5c3c\u4e9a\uff08\u7b2c\u516b\u72f1\uff09\u7684\u5927\u516c\u7235\u58a8\u83f2\u65af\u6258\u83f2\u5229\u65af\uff0c\u662f\u5982\u4eca\u5730\u72f1\u91cc\u5728\u4f4d\u7684\u5404\u5927\u9b54\u541b\u4e2d\uff0c\u9664\u963f\u65af\u8482\u6469\u65af\u4e4b\u5916\uff0c\u8d44\u5386\u6700\u8001\u7684\u4e00\u4f4d\u3002\u5176\u4eba\u6027\u60c5\u53e4\u602a\uff0c\u6df1\u5c45\u7b80\u51fa\uff0c\u81ea\u8be9\u201c\u4e0b\u5c42\u754c\u6700\u4f18\u79c0\u7684\u827a\u672f\u5bb6\u3001\u5efa\u7b51\u5e08\u3001\u753b\u5bb6\u548c\u5929\u6587\u5b66\u5bb6\u201d\uff0c\u4ed6\u4e0a\u4efb\u7684\u65f6\u5019\u5efa\u9020\u4e86\u8457\u540d\u7684\u201c\u58a8\u83f2\u65af\u4e4b\u57ce\u201d\uff0c\u7279\u5730\u5728\u57ce\u5821\u6700\u9ad8\u5904\u8bbe\u7f6e\u4e86\u4e00\u5ea7\u5360\u5730\u9887\u5e7f\u7684\u89c2\u661f\u53f0\uff0c\u4e43\u662f\u4e5d\u5c42\u5730\u72f1\u4e2d\u552f\u4e00\u80fd\u591f\u770b\u89c1\u7269\u8d28\u754c\u661f\u7a7a\u7684\u6240\u5728\u3002\u4f0a\u739b\u65af\u5361\u7b2c\u4e94\u79d8\u5668\u6240\u521b\u9020\u51fa\u7684\u201c\u5c01\u7edd\u201d\uff0c\u5c06\u51e1\u95f4\u6f14\u5316\u6210\u4e5d\u91cd\u5730\u72f1\uff0c\u8fd9\u5ea7\u58a8\u83f2\u65af\u4e4b\u57ce\u4e5f\u539f\u6837\u7167\u642c\uff0c\u590d\u5236\u6295\u5f71\u4e86\u8fc7\u6765\u3002</p><p>\u57ce\u5821\u9876\u7aef\u89c2\u661f\u53f0\u4e0a\uff0c\u5a07\u5c0f\u73b2\u73d1\u7684\u9ed1\u8863\u5c11\u5973\u9759\u9759\u5730\u7ad9\u7740\uff0c\u4e0d\u8a00\u4e0d\u52a8\u3002\u5728\u5979\u7684\u9762\u524d\uff0c\u4e00\u5c0a\u4f53\u79ef\u5e9e\u5927\u7684\u9752\u94dc\u5bb9\u5668\u6b63\u60ac\u6d6e\u7740\uff0c\u79bb\u5730\u534a\u5c3a\uff0c\u987a\u65f6\u9488\u65b9\u5411\u7f13\u6162\u5730\u65cb\u8f6c\u3002\u5b83\u4e09\u8db3\u4e24\u8033\uff0c\u65b9\u53e3\u5706\u8179\uff0c\u6bcf\u8f6c\u4e00\u5708\uff0c\u5c31\u6709\u6570\u4e0d\u5c3d\u7684\u7d2b\u8272\u5149\u70b9\u4ece\u4e2d\u6563\u9038\u51fa\u6765\uff0c\u4eff\u4f5b\u96fe\u6c14\u822c\u5f25\u6563\u56db\u5468\uff0c\u5e7b\u5316\u51fa\u534a\u900f\u660e\u7684\u865a\u50cf\uff0c\u6b63\u662f\u743c\u6069\u548c\u7ef4\u82e5\u62c9\u5728\u6e29\u6cc9\u4e2d\u7684\u573a\u666f\u3002\u9ad8\u660e\u7684\u5e7b\u672f\uff0c\u5b9b\u5982\u5c06\u4e24\u5904\u7a7a\u95f4\u91cd\u53e0\u5728\u4e00\u8d77\uff0c\u4e0d\u4ec5\u4ec5\u662f\u753b\u9762\uff0c\u8fde\u58f0\u97f3\u3001\u6c14\u5473\u3001\u6e29\u5ea6\u7b49\u6240\u6709\u7ec6\u8282\u90fd\u5b8c\u7f8e\u5730\u590d\u5236\u8fc7\u6765\uff0c\u8ba9\u5c11\u5973\u53ef\u4ee5\u8eab\u4e34\u5176\u5883\u5730\u65c1\u89c2\u3002</p><p>\u8f7b\u8f7b\u7684\u811a\u6b65\u58f0\u54cd\u8d77\uff0c\u706b\u53d1\u84dd\u88d9\u7684\u9b54\u59ec\u4ece\u95e8\u53e3\u8d70\u4e86\u8fdb\u6765\uff0c\u5979\u624b\u4e2d\u63e1\u7740\u4e00\u6761\u7ec6\u7ec6\u7684\u94f6\u8272\u7ec6\u94fe\uff0c\u4f46\u7cfb\u5728\u94f6\u94fe\u53e6\u4e00\u5934\u7684\u5e76\u975e\u5ba0\u7269\uff0c\u800c\u662f\u4e00\u4e2a\u5168\u8eab\u8d64\u88f8\u7684\u5973\u4eba\uff0c\u8dea\u4f0f\u7740\u722c\u884c\u8ddf\u5728\u9b54\u59ec\u8eab\u540e\uff0c\u9ed1\u8272\u7684\u957f\u53d1\u6563\u4e71\u5730\u4ece\u96ea\u767d\u80a9\u80cc\u4e0a\u5782\u4e0b\uff0c\u906e\u4f4f\u4e86\u5927\u534a\u8fb9\u8138\u86cb\uff0c\u8ba9\u4eba\u770b\u4e0d\u6e05\u5bb9\u8c8c\uff0c\u4f46\u4ece\u8eab\u6750\u5224\u65ad\uff0c\u6beb\u65e0\u7591\u95ee\u662f\u4f4d\u7a88\u7a95\u7f8e\u4eba\u513f\uff0c\u5979\u7684\u8116\u5b50\u4e0a\u5957\u7740\u4e00\u4e2a\u9ed1\u8272\u76ae\u9879\u5708\uff0c\u8fd9\u662f\u5168\u8eab\u4e0a\u4e0b\u552f\u4e00\u7684\u201c\u8863\u7269\u201d\uff0c\u94f6\u94fe\u4fbf\u662f\u7cfb\u5728\u9879\u5708\u4e0a\u3002\u9ed1\u8863\u5c11\u5973\u671d\u8fd9\u8fb9\u77a5\u4e86\u4e00\u773c\uff0c\u201c\u4f60\u4e0d\u966a\u7740\u4ed6\u5417\uff1f\u201d</p><p>\u201c\u8ba9\u4ed6\u81ea\u5df1\u73a9\u73a9\u5427\uff0c\u201d\u624e\u745e\u5c14\u7b11\u7740\u8bf4\uff0c\u201c\u7537\u4eba\u4e0d\u80fd\u770b\u5f97\u592a\u7d27\u7684\uff0c\u59d0\u59d0\u3002\u201d</p><p>\u706b\u7130\u4ece\u5730\u9762\u6d8c\u51fa\u6765\uff0c\u51dd\u805a\u6210\u534e\u4e3d\u7684\u738b\u5ea7\uff0c\u7136\u540e\u624e\u745e\u5c14\u5750\u4e86\u4e0a\u53bb\uff0c\u4e0e\u51ef\u745f\u7433\u9694\u7740\u9752\u94dc\u9f0e\u9065\u9065\u76f8\u5bf9\u3002\u5979\u626f\u4e86\u626f\u94f6\u94fe\uff0c\u90a3\u4f4d\u8d64\u88f8\u7f8e\u5973\u8d76\u5fd9\u56db\u80a2\u5e76\u7528\uff0c\u5c48\u819d\u722c\u884c\u5230\u624e\u745e\u5c14\u7684\u811a\u4e0b\uff0c\u530d\u5310\u7740\uff0c\u5c06\u8138\u7d27\u8d34\u7740\u5730\u9762\uff0c\u4e00\u52a8\u4e5f\u4e0d\u6562\u52a8\u3002\u9b54\u59ec\u6258\u7740\u9999\u816e\uff0c\u770b\u7740\u811a\u4e0b\u7684\u5973\u5b50\uff0c\u76ee\u5149\u4e0d\u65ad\u95ea\u52a8\uff0c\u4f3c\u4e4e\u5728\u601d\u8003\u7740\u4ec0\u4e48\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u5979\u7f13\u7f13\u4fef\u8eab\uff0c\u4f38\u51fa\u4e00\u53ea\u624b\u6258\u7740\u5973\u5b50\u7684\u4e0b\u5df4\uff0c\u5c06\u5979\u7684\u8138\u62ac\u4e86\u8d77\u6765\u3002</p><p>\u90a3\u662f\u4e00\u5f20\u7cbe\u81f4\u7684\u8138\u86cb\uff0c\u7ec6\u800c\u957f\u7684\u7709\u6bdb\uff0c\u633a\u76f4\u7684\u9f3b\u6881\uff0c\u5c0f\u5de7\u7684\u5634\u5507\u548c\u5c16\u5c16\u7684\u4e0b\u5df4\uff0c\u80fd\u9690\u7ea6\u770b\u5f97\u51fa\u6709\u51e0\u5206\u7cbe\u7075\u8840\u7edf\uff0c\u5979\u7684\u773c\u775b\u5f88\u5947\u7279\uff0c\u773c\u767d\u90e8\u5206\u662f\u5929\u84dd\u8272\u7684\uff0c\u773c\u73e0\u5374\u662f\u5b8c\u5168\u900f\u660e\u65e0\u8272\uff0c\u800c\u4e14\u6ca1\u6709\u77b3\u5b54\uff0c\u521d\u770b\u6709\u4e9b\u8be1\u5f02\uff0c\u4f46\u591a\u770b\u51e0\u773c\uff0c\u4fbf\u8ba9\u4eba\u89c9\u5f97\u6709\u79cd\u6444\u4eba\u5fc3\u9b44\u7684\u7f8e\u611f\uff0c\u96be\u4ee5\u5f62\u5bb9\u3002\u201c\u8fd8\u884c\uff0c\u201d\u9b54\u59ec\u4ed4\u7ec6\u7aef\u8be6\u4e86\u534a\u5929\uff0c\u201c\u4f5c\u4e3a\u4e00\u4e2a\u73a9\u5177\u6765\u8bf4\uff0c\u5728\u76f8\u8c8c\u8fd9\u4e00\u9879\u4e0a\uff0c\u9a6c\u9a6c\u864e\u864e\u7b97\u662f\u591f\u683c\u4e86\u3002\u59d0\u59d0\uff0c\u4f60\u89c9\u5f97\u5462\uff1f\u201d</p><p>\u51ef\u745f\u7433\u76b1\u7709\uff0c\u201c\u4f60\u6253\u7b97\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u9001\u4ed6\u4e00\u4e2a\u73a9\u5177\u561b\uff0c\u201d\u9b54\u59ec\u8bf4\uff0c\u201c\u540c\u65f6\u4e5f\u4f5c\u4e3a\u6211\u7684\u2018\u5bb9\u5668\u2019\u2014\u2014\u55ef\uff0c\u4ee5\u5bb9\u5668\u7684\u6807\u51c6\u800c\u8a00\uff0c\u4f3c\u4e4e\u4e0d\u591f\u7ed3\u5b9e\uff0c\u9700\u8981\u518d\u953b\u70bc\u4e00\u4e0b\u3002\u201d</p><p>\u5979\u4f38\u51fa\u4e00\u6839\u624b\u6307\uff0c\u62b5\u5728\u201c\u73a9\u5177\u201d\u7684\u7709\u5fc3\uff0c\u84dd\u8272\u706b\u7130\u81ea\u9b54\u59ec\u7684\u6307\u5c16\u201c\u8f70\u201d\u5730\u5347\u817e\u800c\u8d77\uff0c\u6c79\u6d8c\u5730\u900f\u5165\u8d64\u88f8\u5973\u5b50\u7684\u8eab\u4f53\u3002\u8d64\u88f8\u5973\u5b50\u5267\u70c8\u5730\u98a4\u6817\u7740\uff0c\u9762\u5bb9\u626d\u66f2\uff0c\u663e\u7136\u5728\u627f\u53d7\u6781\u5927\u7684\u75db\u695a\uff0c\u5979\u5f20\u7740\u53e3\uff0c\u5374\u65e0\u6cd5\u53d1\u51fa\u4efb\u4f55\u58f0\u97f3\uff0c\u96ea\u767d\u7684\u808c\u80a4\u4e0a\u6d6e\u73b0\u51fa\u4e00\u9053\u9053\u9752\u8272\u3001\u7d2b\u8272\u548c\u6a58\u7ea2\u8272\u7684\u7eb9\u8def\uff0c\u5f7c\u6b64\u4ea4\u9519\uff0c\u8513\u5ef6\u7f20\u7ed5\uff0c\u4eff\u4f5b\u7f16\u7ec7\u6210\u4e00\u5f20\u86db\u7f51\uff0c\u5c06\u5979\u7d27\u7d27\u5305\u88f9\u5728\u5176\u4e2d\u3002</p><p>\u5927\u7ea6\u8fc7\u4e86\u4e94\u516d\u5206\u949f\uff0c\u624e\u745e\u5c14\u7ec8\u4e8e\u5c06\u624b\u6307\u79fb\u5f00\uff0c\u8d64\u88f8\u5973\u5b50\u8eab\u4e0a\u7684\u7eb9\u8def\u4e5f\u6e10\u6e10\u5730\u6de1\u53bb\uff0c\u6d88\u5931\u4e0d\u89c1\u3002\u201c\u8fd9\u6837\u5c31\u5dee\u4e0d\u591a\u4e86\u3002\u201d\u9b54\u59ec\u6ee1\u610f\u5730\u8bf4\u3002</p><p>\u201c\u592a\u8106\u4e86\u3002\u201d\u51ef\u745f\u7433\u51b7\u51b7\u5730\u8bc4\u4ef7\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u5c0f\u59b9\u53ef\u6ca1\u529e\u6cd5\u548c\u4f60\u6bd4\u554a\uff0c\u201d\u624e\u745e\u5c14\u504f\u4e86\u504f\u5934\uff0c\u8868\u793a\u65e0\u5948\uff0c\u201c\u59d0\u59d0\u4f60\u53ef\u4ee5\u4ece\u4e0d\u540c\u7684\u65f6\u95f4\u8f74\u4e2d\u53ec\u5524\u81ea\u5df1\u4f5c\u4e3a\u5bb9\u5668\uff0c\u5c0f\u59b9\u54ea\u6709\u8fd9\u79cd\u672c\u4e8b\uff0c\u53ea\u80fd\u9a6c\u9a6c\u864e\u864e\u5c06\u5c31\u4e86\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u4e5f\u53ef\u4ee5\u6295\u5f71\u5417\uff1f\u201d\u51ef\u745f\u7433\u6de1\u6de1\u8bf4\uff0c\u201c\u6211\u8bb0\u5f97\u9ad8\u4f4d\u7684\u90aa\u9b54\u90fd\u6709\u8fd9\u80fd\u529b\u5427\uff0c\u4f55\u5fc5\u820d\u6613\u6c42\u96be\uff0c\u591a\u6b64\u4e00\u4e3e\u3002\u201d</p><p>\u624e\u745e\u5c14\u626c\u4e86\u626c\u7709\uff0c\u201c\u59d0\u59d0\u4e0d\u77e5\u9053\u5417\uff1f\u201d\u5979\u4f3c\u4e4e\u5f88\u60ca\u8bb6\u5730\u53cd\u95ee\uff0c\u201c\u6211\u6ca1\u529e\u6cd5\u6295\u5f71\u5230\u7269\u8d28\u754c\u554a\uff0c\u4ece\u6211\u6210\u4e3a\u5927\u9b54\u9b3c\u7684\u90a3\u4e00\u523b\u8d77\uff0c\u8fd9\u9879\u80fd\u529b\u5c31\u88ab\u5c01\u5370\u4e86\u3002\u201d</p><p>\u51ef\u745f\u7433\u660e\u663e\u6014\u4e86\u4e00\u4e0b\uff0c\u6162\u6162\u628a\u8138\u8f6c\u4e86\u8fc7\u6765\uff0c\u8ba4\u8ba4\u771f\u771f\u5730\u51dd\u89c6\u7740\u624e\u745e\u5c14\u770b\u4e86\u534a\u664c\uff0c\u201c\u8c01\u505a\u7684\uff1f\u201d\u5979\u95ee\u3002</p><p>\u201c\u59d0\u59d0\u5728\u660e\u77e5\u6545\u95ee\u5427\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u7b54\u6848\u4e0d\u662f\u663e\u800c\u6613\u89c1\u5417\uff1f\u201d</p><p>\u51ef\u745f\u7433\u6c89\u541f\u7740\uff0c\u6447\u4e86\u6447\u5934\uff0c\u201c\u5979\u5e94\u8be5\u6ca1\u6709\u8fd9\u4e2a\u529b\u91cf\u3002\u201d</p><p>\u201c\u529b\u91cf\u672a\u5fc5\u4e00\u5b9a\u662f\u81ea\u5df1\u638c\u63e1\uff0c\u4e5f\u53ef\u4ee5\u662f\u5411\u4ed6\u4eba\u501f\u53d6\u800c\u6765\uff0c\u201d\u624e\u745e\u5c14\u7b11\u7740\u8bf4\uff0c\u201c\u59d0\u59d0\u4f60\u7d20\u6765\u5b64\u9ad8\u4e0d\u7fa4\uff0c\u72ec\u6765\u72ec\u5f80\uff0c\u5979\u53ef\u662f\u88d9\u4e0b\u4e4b\u81e3\u65e0\u6570\u554a\u3002\u201d</p><p>\u51ef\u745f\u7433\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u4f60\u4e0d\u662f\u5411\u6765\u81ea\u8be9\u7f8e\u8c8c\u4e48\uff0c\u600e\u4e48\u5c31\u4e89\u4e0d\u8fc7\u5979\u5462\uff1f\u201d</p><p>\u624e\u745e\u5c14\u4e00\u7a92\uff0c\u7b11\u5bb9\u987f\u65f6\u53d8\u5f97\u6709\u4e9b\u50f5\u786c\uff0c\u51ef\u745f\u7433\u5e73\u7d20\u4e0d\u5584\u8a00\u8f9e\uff0c\u5076\u5c14\u4e00\u53e5\u5374\u80fd\u564e\u5f97\u4eba\u900f\u4e0d\u8fc7\u6c14\u6765\u3002\u5766\u767d\u5730\u8bf4\uff0c\u7b2c\u4e00\u6b21\u89c1\u5230\u7684\u65f6\u5019\uff0c\u624e\u745e\u5c14\u8fd8\u771f\u6ca1\u628a\u5bf9\u65b9\u653e\u5728\u5fc3\u4e0a\uff0c\u66f4\u6ca1\u60f3\u5230\u90a3\u4f4d\u6e29\u67d4\u5a34\u96c5\u7684\u6587\u9759\u5c11\u5973\uff0c\u5c45\u7136\u6210\u4e86\u81ea\u5df1\u6700\u5927\u7684\u654c\u4eba\uff0c\u800c\u4e14\u8fd8\u8ba9\u5979\u7b11\u5230\u4e86\u6700\u540e\u3002\u81ea\u5df1\u7d20\u6765\u81ea\u8d1f\u806a\u660e\uff0c\u5374\u4e0d\u6599\u6bcf\u4e00\u6b65\u90fd\u88ab\u4eba\u7b97\u5f97\u6b7b\u6b7b\uff0c\u6700\u540e\u66f4\u662f\u6389\u8fdb\u4e00\u4e2a\u5927\u9677\u9631\uff0c\u5230\u73b0\u5728\u90fd\u6ca1\u5b8c\u5168\u722c\u51fa\u6765\u3002\u81ea\u8bde\u751f\u4ee5\u6765\uff0c\u624e\u745e\u5c14\u8fd8\u6ca1\u4e0a\u8fc7\u8fd9\u79cd\u5f53\uff0c\u201c\u8776\u7ffc\u7684\u9b54\u59ec\u201d\u53ef\u6ca1\u6709\u5fc3\u80f8\u5bbd\u5e7f\u7684\u540d\u58f0\uff0c\u867d\u8bf4\u51e0\u5343\u5e74\u8fc7\u53bb\u4e86\uff0c\u8fd9\u53e3\u6c14\u5374\u59cb\u7ec8\u90c1\u7ed3\u5728\u5fc3\uff0c\u7b49\u5f85\u7740\u6709\u671d\u4e00\u65e5\u80fd\u591f\u62a5\u590d\u56de\u6765\u3002</p><p>\u4e0d\u8fc7\u8fd9\u4e00\u6b21\uff0c\u4f3c\u4e4e\u53c8\u8ba9\u5979\u6377\u8db3\u5148\u767b\uff0c\u62a2\u524d\u4e00\u6b65\u5462\u3002</p><p>\u201c\u8bf4\u8d77\u6765\uff0c\u5c0f\u96c5\u5c31\u662f\u6bd4\u6211\u4eec\u806a\u660e\u554a\u2026\u2026\u201d\u624e\u745e\u5c14\u7b11\u7740\u611f\u53f9\uff0c\u6709\u610f\u65e0\u610f\u5730\u770b\u4e86\u5bf9\u9762\u7684\u9ed1\u8863\u5c11\u5973\u4e00\u773c\u3002</p><p>\u201c\u55ef\u3002\u201d\u51ef\u745f\u7433\u6de1\u6de1\u5730\u5e94\u4e86\u4e00\u58f0\u3002</p><p>\u201c\u4e8b\u60c5\u90fd\u662f\u6211\u4eec\u5728\u505a\uff0c\u9ebb\u70e6\u90fd\u662f\u6211\u4eec\u89e3\u51b3\uff0c\u5979\u603b\u662f\u7b49\u7740\u5750\u4eab\u5176\u6210\u5c31\u597d\uff0c\u201d\u9b54\u59ec\u534a\u771f\u534a\u5047\u5730\u62b1\u6028\uff0c\u201c\u771f\u4e0d\u516c\u5e73\u3002\u201d</p><p>\u51ef\u745f\u7433\u4e0d\u7f6e\u53ef\u5426\u3002\u201c\u4f60\u8bf4\u5979\u5df2\u7ecf\u5b8c\u5168\u4e0d\u8bb0\u5f97\u4e86\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u5979\u8fd8\u771f\u662f\u6709\u51b3\u65ad\u3002\u524d\u5c18\u65e7\u4e8b\uff0c\u5c3d\u6570\u629b\u5374\uff0c\u6614\u65e5\u6069\u4ec7\uff0c\u4e00\u5fd8\u7686\u7a7a\uff0c\u201d\u624e\u745e\u5c14\u4f9d\u65e7\u7b11\u76c8\u76c8\u7684\uff0c\u8bed\u6c14\u4e2d\u5374\u6ee1\u662f\u8bbd\u523a\uff0c\u201c\u6240\u4ee5\u6211\u4e00\u76f4\u8bf4\uff0c\u5c0f\u96c5\u6700\u72e1\u733e\u4e86\u3002\u201d</p><p>\u72e1\u733e\u5417\uff1f\u6216\u8bb8\u5427\uff0c\u4f46\u80fd\u591f\u5b8c\u5168\u653e\u5f03\u8fc7\u53bb\u7684\u4e00\u5207\uff0c\u65a9\u65ad\u56e0\u7f18\uff0c\u6cef\u706d\u8bb0\u5fc6\uff0c\u4ece\u5934\u518d\u6765\uff0c\u53ea\u4e3a\u4e86\u4eca\u65e5\u7684\u76f8\u4f34\u76f8\u5b88\uff0c\u8fd9\u4efd\u52c7\u6c14\u548c\u575a\u6301\uff0c\u5373\u4fbf\u662f\u51ef\u745f\u7433\u4e5f\u4e0d\u5f97\u4e0d\u6b63\u89c6\uff0c\u751a\u81f3\u662f\u656c\u754f\uff0c\u53ef\u4e0d\u4ec5\u4ec5\u662f\u201c\u72e1\u733e\u201d\u4e24\u4e2a\u5b57\u80fd\u591f\u6982\u62ec\u7684\u3002</p><p>\u66fe\u7ecf\u662f\u65e0\u8bdd\u4e0d\u8c08\u7684\u95fa\u4e2d\u5bc6\u53cb\uff0c\u7eb5\u7136\u6700\u540e\u53cd\u76ee\u6210\u4ec7\uff0c\u4f46\u5728\u51ef\u745f\u7433\u5fc3\u4e2d\uff0c\u5230\u5e95\u8fd8\u73cd\u85cf\u7740\u90a3\u4e00\u4efd\u53cb\u8c0a\uff0c\u4e0d\u613f\u6076\u8bed\u76f8\u5411\u3002\u800c\u4e14\u771f\u8981\u8bf4\u8d77\u6765\uff0c\u4e24\u4eba\u4e4b\u6240\u4ee5\u8d70\u5230\u90a3\u4e00\u6b65\uff0c\u4e5f\u4e0d\u5168\u662f\u5bf9\u65b9\u7684\u8d23\u4efb\uff0c\u5e73\u5fc3\u800c\u8bba\uff0c\u8fd8\u662f\u81ea\u5df1\u6709\u9519\u5728\u5148\u5427\u3002</p><p>\u51ef\u745f\u7433\u9ed8\u7136\u4e0d\u8bed\u3002\u624e\u745e\u5c14\u77a5\u4e86\u5979\u4e00\u773c\uff0c\u4e0d\u52a8\u58f0\u8272\u3002\u201c\u65e0\u6240\u8c13\u5566\uff0c\u201d\u9b54\u59ec\u81ea\u4fe1\u6ee1\u6ee1\u5730\u8bf4\uff0c\u201c\u8fd9\u4e00\u5c40\u624d\u521a\u521a\u5f00\u59cb\uff0c\u867d\u7136\u88ab\u5979\u62a2\u4e86\u5148\u624b\uff0c\u4f46\u7a76\u7adf\u8c01\u80dc\u8c01\u8d1f\uff0c\u5c1a\u672a\u53ef\u77e5\u5462\uff0c\u81f3\u5c11\u8fd9\u4e00\u6b21\uff0c\u6211\u662f\u7edd\u4e0d\u4f1a\u8f7b\u654c\u4e86\u3002\u201d</p><p>\u201c\u800c\u4e14\u8fd9\u6b21\u4e0d\u7528\u4ee5\u4e00\u654c\u4e8c\u4e86\uff0c\u201d\u5979\u8865\u5145\u4e86\u4e00\u53e5\uff0c\u201c\u59d0\u59d0\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d</p><p>\u6ca1\u5934\u6ca1\u5c3e\u5730\u7a81\u7136\u5192\u51fa\u8fd9\u4e00\u53e5\uff0c\u6362\u4e86\u522b\u4eba\u8fd8\u771f\u8981\u72af\u8ff7\u7cca\uff0c\u4f46\u51ef\u745f\u7433\u81ea\u7136\u4e0d\u4f1a\u3002\u8981\u8bf4\u8d77\u6765\uff0c\u5f53\u5e74\u4e4b\u4e8b\uff0c\u5c82\u6b62\u9b54\u59ec\u5ff5\u5ff5\u4e0d\u5fd8\uff0c\u5979\u53c8\u4f55\u5c1d\u4e0d\u662f\u803f\u803f\u4e8e\u6000\u3002\u53ea\u662f\u2026\u2026\u7ad9\u5728\u4e0d\u540c\u7684\u7acb\u573a\uff0c\u81ea\u6709\u4e0d\u540c\u7684\u7ed3\u8bba\uff0c\u662f\u975e\u5bf9\u9519\u8fd9\u79cd\u4e1c\u897f\uff0c\u539f\u672c\u4e5f\u5c31\u5f88\u96be\u8bf4\u5f97\u6e05\u695a\uff0c\u5c40\u5916\u4eba\u5c1a\u4e14\u96be\u4ee5\u5224\u65ad\uff0c\u4f55\u51b5\u5979\u4eec\u90fd\u662f\u8eab\u9677\u5176\u4e2d\u3002\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u786e\u5b9e\u5982\u624e\u745e\u5c14\u6240\u8a00\uff0c\u81f3\u5c11\u8fd9\u4e00\u6b21\uff0c\u662f\u4e0d\u4f1a\u51fa\u73b0\u4e00\u5bf9\u4e8c\u7684\u5c40\u9762\u4e86\u3002</p><p>\u5f53\u7136\uff0c\u4e5f\u4e0d\u4f1a\u53cd\u8fc7\u6765\u4e8c\u5bf9\u4e00\u5c31\u662f\u4e86\u3002</p><p>\u201c\u5176\u5b9e\u5982\u679c\u53ef\u4ee5\u7684\u8bdd\uff0c\u6211\u8fd8\u771f\u60f3\u548c\u59d0\u59d0\u8054\u624b\uff0c\u201d\u624e\u745e\u5c14\u7528\u60cb\u60dc\u7684\u53e3\u6c14\u8bf4\uff0c\u201c\u90a3\u6837\u7684\u8bdd\uff0c\u5c31\u65e0\u5f80\u800c\u4e0d\u5229\u4e86\u3002\u201d</p><p>\u5bf9\u4e8e\u8fd9\u4e2a\u63d0\u8bae\uff0c\u51ef\u745f\u7433\u8fde\u51fa\u8a00\u62d2\u7edd\u90fd\u6ca1\u5174\u8da3\uff0c\u76f4\u63a5\u6c89\u9ed8\u4ee5\u5bf9\u3002\u624e\u745e\u5c14\u4e5f\u4e0d\u4ee5\u4e3a\u610f\uff0c\u8fd9\u662f\u9884\u6599\u4e4b\u4e2d\u7684\u7ed3\u679c\uff0c\u5979\u539f\u672c\u4e5f\u53ea\u662f\u968f\u53e3\u4e00\u63d0\u7f62\u4e86\u3002</p><p>\u4e24\u4eba\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u96fe\u6c14\u5e7b\u5316\u51fa\u7684\u753b\u9762\u6b63\u4e00\u5e55\u5e55\u5730\u6d41\u52a8\u3002</p><p>\u203b\u203b\u203b</p><p>\u7ef4\u82e5\u62c9\u7a81\u7136\u8bf4\u8bdd\u4e86\u3002\u201c\u770b\u7740\u6211\uff0c\u201d\u5979\u8bf4\uff0c\u58f0\u97f3\u6709\u4e9b\u5947\u602a\u7684\u53d1\u98a4\uff0c\u201c\u770b\u7740\u6211\u7684\u773c\u775b\u3002\u201d</p><p>\u743c\u6069\u4e0d\u89e3\u5176\u610f\uff0c\u4f9d\u8a00\u770b\u53bb\u3002\u53cc\u65b9\u89c6\u7ebf\u76f8\u4ea4\uff0c\u4ed6\u987f\u65f6\u4fbf\u662f\u4e00\u6014\uff0c\u5973\u5deb\u5e08\u7684\u773c\u775b\u539f\u672c\u4e00\u76f4\u90fd\u662f\u7070\u8499\u8499\u7684\uff0c\u50cf\u662f\u7b3c\u7f69\u4e86\u5c42\u6de1\u6de1\u96fe\u6c14\uff0c\u8ba9\u4eba\u96be\u4ee5\u770b\u900f\uff1b\u6b64\u523b\u5374\u662f\u4e91\u5f00\u96fe\u6563\uff0c\u6e05\u6f88\u5982\u6eaa\uff0c\u660e\u4eae\u5f97\u60ca\u4eba\u3002\u5728\u773c\u7738\u6df1\u5904\uff0c\u4eff\u4f5b\u6709\u4e00\u7c92\u83b9\u83b9\u5fae\u5149\u8df3\u8dc3\u95ea\u70c1\uff0c\u6349\u6478\u4e0d\u5b9a\uff0c\u743c\u6069\u7684\u6ce8\u610f\u529b\u4e0d\u7531\u81ea\u4e3b\u5730\u4fbf\u88ab\u5438\u5f15\u8fc7\u53bb\uff0c\u4ed6\u6b63\u8981\u7ec6\u770b\uff0c\u4fbf\u89c1\u90a3\u70b9\u5fae\u5149\u9661\u7136\u6d6e\u4e0a\uff0c\u8f70\u5730\u7efd\u5f00\uff0c\u5239\u90a3\u95f4\u5149\u8292\u5927\u653e\uff0c\u523a\u5f97\u4ed6\u51e0\u4e4e\u773c\u775b\u90fd\u7741\u4e0d\u5f00\u6765\u3002</p><p>\u4e2d\u9677\u9631\u4e86\uff1f</p><p>\u743c\u6069\u8111\u4e2d\u8f6c\u8fc7\u7684\u7b2c\u4e00\u4e2a\u5ff5\u5934\u4fbf\u662f\u5982\u6b64\uff0c\u4f46\u968f\u5373\u4fbf\u53c8\u81ea\u884c\u5426\u5b9a\u4e86\uff0c\u610f\u8bc6\u6ca1\u6709\u611f\u5e94\u5230\u653b\u51fb\u8baf\u606f\uff0c\u9884\u5148\u9644\u5728\u8eab\u4e0a\u7684\u9632\u5fa1\u9b54\u6cd5\u4e5f\u6ca1\u6709\u4efb\u4f55\u53cd\u5e94\u3002\u5927\u7ea6\u4e24\u79d2\u949f\u540e\uff0c\u5149\u8292\u655b\u53bb\uff0c\u4ed6\u7684\u89c6\u89c9\u6e10\u6e10\u6062\u590d\uff0c\u4fbf\u89c1\u5728\u5973\u5deb\u5e08\u7684\u53cc\u773c\u4e4b\u4e2d\uff0c\u6570\u4ee5\u5343\u8ba1\u7684\u5b57\u7b26\u95ea\u95ea\u53d1\u5149\uff0c\u7eb5\u6a2a\u6392\u5217\u6210\u6587\u3002\u90a3\u4e9b\u5b57\u7b26\u660e\u660e\u6781\u5fae\u6781\u5c0f\uff0c\u7136\u800c\u4ed6\u5374\u770b\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u7ea4\u6beb\u4e0d\u5dee\uff0c\u4eff\u4f5b\u662f\u76f4\u63a5\u5370\u5165\u8111\u6d77\u4e2d\u4e00\u822c\u3002\u5306\u5306\u6d4f\u89c8\u4e86\u4e00\u904d\uff0c\u743c\u6069\u7684\u795e\u60c5\u987f\u65f6\u53d8\u5f97\u5f88\u53e4\u602a\u3002</p><p>\u8fd9\u662f\u4e00\u4efd\u8d44\u6599\uff0c\u8bb0\u5f55\u4e86\u4e00\u4e2a\u540d\u4e3a\u201c\u60c5\u6b32\u9ad8\u6da8\u201d\u7684\u5947\u5f02\u6cd5\u672f\uff0c\u987e\u540d\u601d\u4e49\uff0c\u5176\u4f5c\u7528\u4fbf\u662f\u80fd\u591f\u4ee4\u4eba\u53d1\u60c5\u3002\u7c7b\u4f3c\u7684\u4e1c\u897f\uff0c\u743c\u6069\u4e4b\u524d\u4e5f\u4e0d\u662f\u6ca1\u89c1\u8fc7\uff0c\u70db\u5821\u91cc\u5c31\u6536\u85cf\u6709\u4e00\u672c\u300a\u7c89\u7ea2\u4e4b\u4e66\u300b\uff0c\u53f7\u79f0\u662f\u201c\u5de5\u53e3\u9b54\u6cd5\u5927\u5168\u201d\uff0c\u8bb0\u8f7d\u4e86\u4e0a\u767e\u9053\u7528\u4e8e\u5e8a\u7b2b\u4e4b\u95f4\u7684\u6cd5\u672f\uff0c\u4ed6\u521d\u89c1\u65f6\u5174\u594b\u4e0d\u5df2\uff0c\u8fd8\u7792\u7740\u6885\u83f2\u65af\u5077\u5077\u6284\u5f55\u4e86\u4e00\u4efd\uff0c\u6253\u7b97\u6697\u4e2d\u7814\u4e60\uff0c\u4f46\u5b9e\u9645\u8bd5\u8fc7\u624d\u53d1\u73b0\uff0c\u8fd9\u4e9b\u6cd5\u672f\u5927\u591a\u540d\u8fc7\u5176\u5b9e\uff0c\u6548\u679c\u5e73\u5e73\uff0c\u6709\u4e9b\u751a\u81f3\u7eaf\u7cb9\u5c31\u662f\u7528\u6765\u641e\u7b11\u7684\uff0c\u4e0d\u7531\u5f97\u5927\u5931\u6240\u671b\u3002\u7136\u800c\u6b64\u523b\uff0c\u4ed6\u5728\u5973\u5deb\u5e08\u773c\u4e2d\u6240\u770b\u89c1\u7684\u8fd9\u9053\u6cd5\u672f\uff0c\u5374\u660e\u663e\u4e0e\u4e4b\u76f8\u5f02\u3002</p><p>\u65f6\u8fc7\u5883\u8fc1\uff0c\u5f53\u5e74\u53bb\u70db\u5821\u65f6\uff0c\u743c\u6069\u521d\u51fa\u8305\u5e90\uff0c\u521a\u521a\u4ece\u5b66\u9662\u6bd5\u4e1a\uff0c\u5982\u4eca\u5374\u5df2\u7ecf\u662f\u51dd\u6210\u771f\u540d\u7684\u9ad8\u9636\u5deb\u5e08\uff0c\u8fd9\u51e0\u5e74\u5728\u5916\u591a\u6709\u5386\u7ec3\uff0c\u773c\u5149\u89c1\u8bc6\u5747\u8fdc\u975e\u6614\u65e5\u53ef\u6bd4\u3002\u53ea\u662f\u7b80\u5355\u6d4f\u89c8\uff0c\u4ed6\u4fbf\u770b\u51fa\u8fd9\u9053\u201c\u60c5\u6b32\u9ad8\u6da8\u201d\u4e0d\u4e00\u822c\uff0c\u5176\u5b9e\u9645\u6548\u7528\u5982\u4f55\u4e14\u4e0d\u8c08\uff0c\u5355\u5355\u53ea\u8bba\u6587\u5b57\uff0c\u7ed3\u6784\u7ec6\u5bc6\u4e25\u8c28\uff0c\u601d\u8def\u522b\u51fa\u5fc3\u88c1\uff0c\u5220\u7e41\u5c31\u7b80\uff0c\u7528\u8bed\u7cbe\u5230\uff0c\u6beb\u65e0\u679d\u8513\u7e41\u5197\uff0c\u7edd\u975e\u5bfb\u5e38\u624b\u7b14\uff0c\u4e0d\u662f\u968f\u968f\u4fbf\u4fbf\u4ec0\u4e48\u4eba\u90fd\u80fd\u5199\u51fa\u6765\u7684\u2014\u2014\u81f3\u5c11\u743c\u6069\u81ea\u5ea6\u5c31\u529e\u4e0d\u5230\u3002</p><p>\u5728\u8fd9\u4e2a\u4e16\u754c\u4e0a\uff0c\u9b54\u6cd5\u5b66\u867d\u7136\u5df2\u7ecf\u7b97\u662f\u6bd4\u8f83\u53d1\u8fbe\uff0c\u4f46\u8ddd\u79bb\u201c\u6807\u51c6\u5316\u201d\u8fd8\u662f\u6709\u5f88\u957f\u4e00\u6bb5\u8ddd\u79bb\u3002\u50cf\u9634\u9b42\u57ce\u3001\u94f6\u6708\u57ce\u3001\u585e\u5c14\u8fd9\u4e9b\u5730\u65b9\uff0c\u5efa\u6709\u5386\u53f2\u60a0\u4e45\u3001\u540d\u58f0\u663e\u8d6b\u7684\u5deb\u5e08\u5b66\u9662\uff0c\u540c\u5b66\u4e4b\u95f4\u6240\u5b66\u5927\u591a\u76f8\u4f3c\uff0c\u4f46\u8fd9\u6bd5\u7adf\u662f\u5c11\u6570\u60c5\u5f62\u3002\u5927\u591a\u6570\u5deb\u5e08\u90fd\u4e0d\u662f\u5b66\u9662\u6d3e\u51fa\u8eab\uff0c\u6ca1\u6709\u63a5\u53d7\u8fc7\u6b63\u7edf\u6559\u80b2\uff0c\u5f80\u5f80\u662f\u8ddf\u5728\u5bfc\u5e08\u540e\u9762\u505a\u5b66\u5f92\uff0c\u9010\u6e10\u6210\u957f\u8d77\u6765\uff0c\u5176\u6240\u5b66\u901a\u5e38\u5927\u6709\u6b67\u5f02\u3002\u5373\u4fbf\u662f\u5b66\u9662\u6d3e\u51fa\u8eab\u7684\u5deb\u5e08\uff0c\u56e0\u4e3a\u5206\u5c5e\u4e0d\u540c\u4f53\u7cfb\uff0c\u5f7c\u6b64\u6240\u5b66\u4e5f\u4e0d\u5c3d\u76f8\u540c\u3002\u4e3e\u4e2a\u4f8b\u5b50\u6765\u8bf4\uff0c\u540c\u6837\u4e00\u9053\u6cd5\u672f\uff0c\u743c\u6069\u4f1a\uff0c\u51db\u4e5f\u4f1a\uff0c\u65bd\u5c55\u51fa\u6765\u7684\u6548\u679c\u4e5f\u7c7b\u4f3c\uff0c\u4f46\u82e5\u8981\u4ed6\u4eec\u5206\u522b\u9610\u8ff0\u5176\u539f\u7406\u3001\u6280\u6cd5\u3001\u8bc0\u8981\uff0c\u80af\u5b9a\u5c31\u6709\u5f88\u591a\u5730\u65b9\u4e0d\u4e00\u81f4\u3002\u4ece\u67d0\u79cd\u89d2\u5ea6\u6765\u8bf4\uff0c\u8fd9\u4e5f\u7b97\u662f\u201c\u6b8a\u9014\u540c\u5f52\u201d\u2014\u2014\u4f46\u8fd9\u4e9b\u201c\u6b8a\u9014\u201d\uff0c\u5f80\u5f80\u4fbf\u662f\u9ad8\u4e0b\u4e4b\u5206\u6240\u5728\u3002\u5c31\u50cf\u8d70\u8def\uff0c\u76ee\u7684\u5730\u662f\u76f8\u540c\u7684\uff0c\u90a3\u4e48\u8ddd\u79bb\u77ed\u7684\u3001\u884c\u7a0b\u5feb\u7684\u3001\u8d70\u8d77\u6765\u65b9\u4fbf\u7684\u9633\u5173\u5927\u9053\uff0c\u663e\u7136\u5c31\u6bd4\u72ec\u6728\u6865\u6765\u5f97\u9ad8\u660e\uff0c\u800c\u80fd\u591f\u7ed9\u522b\u4eba\u6307\u70b9\u51fa\u9633\u5173\u5927\u9053\u7684\uff0c\u81ea\u7136\u90fd\u662f\u6b64\u9053\u9ad8\u624b\u3002\u743c\u6069\u9650\u4e8e\u7ecf\u9a8c\u5c1a\u6d45\uff0c\u8fd8\u8fbe\u4e0d\u5230\u8fd9\u79cd\u6c34\u5e73\uff0c\u4f46\u773c\u5149\u603b\u662f\u6709\u7684\uff0c\u4e00\u770b\u4fbf\u770b\u51fa\u6765\u3002</p><p>\u7b49\u7b49\uff0c\u73b0\u5728\u4e0d\u662f\u5b66\u672f\u7814\u7a76\u7684\u65f6\u5019\uff0c\u6b64\u60c5\u6b64\u666f\uff0c\u7ef4\u82e5\u62c9\u7a81\u7136\u7ed9\u81ea\u5df1\u770b\u8fd9\u4e2a\uff0c\u5979\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u96be\u4e0d\u6210\u2026\u2026</p><p>\u201c\u4f60\u662f\u8981\u6211\u5bf9\u4f60\u7528\u8fd9\u9053\u6cd5\u672f\uff1f\u201d</p><p>\u5973\u5deb\u5e08\u6ee1\u8138\u901a\u7ea2\uff0c\u4f4e\u4f4e\u5730\u5e94\u4e86\u4e00\u58f0\uff0c\u201c\u55ef\u3002\u201d</p><p>\u8fd9\u5012\u662f\u4e2a\u597d\u529e\u6cd5\u3002\u53ea\u662f\u5df2\u7ecf\u4e8b\u5230\u4e34\u5934\uff0c\u73b0\u5728\u624d\u6765\u5b66\u4e60\u65b0\u6cd5\u672f\uff0c\u672a\u514d\u7f13\u4e0d\u6d4e\u6025\u5427\u3002</p><p>\u201c\u53ea\u8981\u4f60\u540c\u610f\uff0c\u6211\u5c31\u53ef\u4ee5\u5c06\u8fd9\u9053\u6cd5\u672f\u76f4\u63a5\u2018\u4f20\u6388\u2019\u7ed9\u4f60\uff0c\u8fd9\u662f\u8679\u96fe\u7684\u80fd\u529b\u4e4b\u4e00\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u4f60\u4f1a\u7acb\u523b\u638c\u63e1\u5b83\uff0c\u4e0d\u9700\u8981\u518d\u53e6\u884c\u5b66\u4e60\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u65b9\u4fbf\uff1f\u201d\u743c\u6069\u7709\u5934\u5fae\u76b1\uff0c\u201c\u6709\u4ec0\u4e48\u4ee3\u4ef7\uff1f\u201d</p><p>\u5929\u4e0b\u6ca1\u6709\u767d\u5403\u7684\u5348\u9910\uff0c\u66f4\u6ca1\u6709\u90a3\u4e48\u591a\u6377\u5f84\u53ef\u8d70\u3002\u4ee5\u743c\u6069\u73b0\u5728\u7684\u6c34\u5e73\uff0c\u8981\u771f\u6b63\u9886\u4f1a\u638c\u63e1\u4e00\u9053\u65b0\u7684\u6cd5\u672f\uff0c\u5373\u4fbf\u662f\u7b80\u5355\u7684\uff0c\u603b\u4e5f\u5f97\u6f5c\u5fc3\u94bb\u7814\u4e2a\u4e09\u4e94\u65e5\uff0c\u518d\u53cd\u590d\u8bd5\u9a8c\u3001\u7ec3\u4e60\u4e0a\u51e0\u5341\u904d\u65b9\u624d\u53ef\u4ee5\u3002\u7ef4\u82e5\u62c9\u8bf4\u5f97\u5982\u6b64\u8f7b\u6613\uff0c\u53cd\u5012\u8ba9\u4ed6\u4e0d\u653e\u5fc3\u8d77\u6765\uff0c\u8981\u8ffd\u95ee\u7a76\u7adf\u3002</p><p>\u201c\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u4ee3\u4ef7\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u53ea\u662f\u4ee5\u540e\u4f60\uff0c\u6216\u8005\u4ece\u4f60\u8fd9\u91cc\u5b66\u5230\u8fd9\u9053\u6cd5\u672f\u7684\u4eba\uff0c\u5bf9\u5b83\u7684\u6240\u6709\u9610\u53d1\u3001\u53d8\u5316\u548c\u6f14\u7ece\uff0c\u4ee5\u53ca\u65b0\u7684\u5fc3\u5f97\u4f53\u4f1a\u2014\u2014\u5982\u679c\u6709\u7684\u8bdd\uff0c\u6211\u90fd\u4f1a\u77e5\u9053\u3002\u201d</p><p>\u539f\u6765\u5982\u6b64\uff0c\u8fd9\u5c31\u662f\u201c\u4f20\u9053\u5deb\u5e08\u201d\u7684\u542b\u4e49\uff1f</p><p>\u963f\u7956\u65af\u867d\u7136\u53f7\u79f0\u5deb\u5e08\u4e4b\u795e\u3001\u5965\u6cd5\u4e4b\u4e3b\uff0c\u5176\u5b9e\u53ea\u662f\u4e2a\u534a\u795e\uff0c\u4ece\u5c5e\u4e8e\u9b54\u6cd5\u5973\u795e\u9ebe\u4e0b\uff0c\u5728\u795e\u660e\u4f53\u7cfb\u4e2d\u5c5e\u4e8e\u4f4d\u9636\u6700\u4f4e\u7684\u90a3\u4e00\u7c7b\uff0c\u6559\u4f1a\u89c4\u6a21\u6781\u5c0f\uff0c\u884c\u4e8b\u53c8\u504f\u9690\u79d8\uff0c\u5916\u4eba\u5bf9\u5176\u4e86\u89e3\u5e76\u4e0d\u591a\u3002\u963f\u7956\u65af\u7684\u9009\u6c11\u79f0\u201c\u4f20\u9053\u5deb\u5e08\u201d\uff0c\u987e\u540d\u601d\u4e49\uff0c\u804c\u8d23\u662f\u5ba3\u626c\u4f20\u64ad\u5965\u672f\u4e4b\u9053\uff0c\u8fd9\u4e2a\u5927\u5bb6\u81ea\u7136\u90fd\u6e05\u695a\uff0c\u4f46\u5177\u4f53\u7684\u201c\u4f20\u9053\u201d\u65b9\u5f0f\u7ec6\u8282\uff0c\u5c31\u4e0d\u662f\u4eba\u5c3d\u7686\u77e5\u4e86\u3002\u6bd5\u7adf\u80fd\u591f\u88ab\u201c\u4f20\u9053\u201d\u7684\u5bf9\u8c61\uff0c\u5e94\u8be5\u90fd\u662f\u6559\u4f1a\u6210\u5458\u6216\u8005\u9884\u5907\u6210\u5458\uff0c\u50cf\u743c\u6069\u8fd9\u79cd\u516b\u7aff\u5b50\u6253\u4e0d\u7740\u7684\u5916\u4eba\uff0c\u6b63\u5e38\u60c5\u51b5\u4e0b\u662f\u4e0d\u53ef\u80fd\u88ab\u9009\u4e2d\u7684\u3002</p><p>\u603b\u89c9\u5f97\uff0c\u8fd9\u79cd\u201c\u4f20\u9053\u201d\u65b9\u5f0f\uff0c\u6709\u70b9\u50cf\u2026\u2026\u7075\u5438\u602a\u554a\u3002</p><p>\u5e7d\u6697\u5730\u57df\u4e2d\u7684\u7075\u5438\u602a\uff0c\u6bcf\u4e2a\u65cf\u7fa4\u90fd\u6709\u4e00\u4e2a\u201c\u4e3b\u8111\u201d\uff0c\u7075\u5438\u602a\u53ef\u4ee5\u4ece\u4e3b\u8111\u4e2d\u83b7\u53d6\u8d44\u6599\u3001\u5f97\u5230\u6307\u70b9\uff0c\u800c\u5f85\u5176\u6b7b\u540e\uff0c\u6bd5\u751f\u6240\u5b66\u7684\u77e5\u8bc6\u3001\u8bb0\u5fc6\u5e76\u4e0d\u4f1a\u6d88\u5931\uff0c\u800c\u662f\u201c\u56de\u6d41\u201d\u5230\u4e3b\u8111\u4e4b\u4e2d\u3002\u4f20\u9053\u5deb\u5e08\u7684\u65b9\u6cd5\uff0c\u4e0e\u7075\u5438\u602a\u786e\u5b9e\u6709\u5f02\u66f2\u540c\u5de5\u4e4b\u5999\u3002</p><p>\u5f53\u7136\u4e86\uff0c\u90a3\u79cd\u7ae0\u9c7c\u822c\u4e11\u964b\u7684\u7075\u5438\u602a\uff0c\u54ea\u91cc\u6bd4\u5f97\u4e0a\u773c\u524d\u8fd9\u4f4d\u4e00\u4e1d\u4e0d\u6302\u7684\u91d1\u53d1\u7f8e\u4eba\u513f\u53ef\u7231\u5462\uff0c\u4e07\u5206\u4e4b\u4e00\u90fd\u6bd4\u4e0d\u4e0a\u554a\u3002</p><p>\u4ed4\u4ed4\u7ec6\u7ec6\u5730\u53c8\u5c06\u8fd9\u9053\u6cd5\u672f\u4ece\u5934\u5230\u5c3e\u770b\u4e86\u51e0\u904d\uff0c\u786e\u8ba4\u5176\u4e2d\u6ca1\u6709\u4ec0\u4e48\u9677\u9631\uff0c\u743c\u6069\u70b9\u4e86\u70b9\u5934\uff0c\u8868\u793a\u540c\u610f\u63a5\u53d7\u201c\u4f20\u9053\u201d\uff0c\u7136\u540e\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u6574\u4e2a\u6cd5\u672f\u5c31\u5370\u5728\u4e86\u4ed6\u7684\u610f\u8bc6\u4e2d\u3002\u82b1\u4e86\u51e0\u79d2\u949f\u9002\u5e94\u8fd9\u7a81\u5982\u5176\u6765\u7684\u201c\u4fe1\u606f\u201d\uff0c\u4ed6\u5c06\u624b\u653e\u5728\u5973\u5deb\u5e08\u7684\u5c0f\u8179\u4e0a\uff0c\u5f00\u59cb\u548f\u5531\u5492\u6587\u3002</p><p>\u203b\u203b\u203b</p><p>\u636e\u624e\u745e\u5c14\u8bf4\uff0c\u5728\u8fd9\u201c\u6d1e\u5929\u798f\u5730\u201d\u4e4b\u4e2d\uff0c\u65f6\u95f4\u4f1a\u88ab\u6781\u5927\u5730\u5ef6\u957f\uff0c\u539f\u672c\u743c\u6069\u53ea\u6709\u4e00\u5929\u65f6\u95f4\u4e0e\u7ef4\u82e5\u62c9\u76f8\u5904\uff0c\u73b0\u5728\u5219\u88ab\u5f3a\u884c\u62c9\u4f38\u6210\u4e86\u4e5d\u5929\u3002\u5728\u8fd9\u4e5d\u5929\u91cc\uff0c\u6ca1\u6709\u65e5\u591c\u66f4\u66ff\uff0c\u6ca1\u6709\u9634\u9633\u53d8\u5316\uff0c\u743c\u6069\u548c\u7ef4\u82e5\u62c9\u4e5f\u4e0d\u9700\u8981\u996e\u98df\u548c\u7761\u7720\uff0c\u6240\u4ee5\u4ed6\u4eec\u5c31\u53ea\u5269\u4e0b\u4e00\u4ef6\u4e8b\u53ef\u505a\uff0c\u5c31\u662f\u65e0\u4f11\u65e0\u6b62\u5730\u505a\u7231\u3002</p><p>\u5bf9\u4e8e\u743c\u6069\u800c\u8a00\uff0c\u8fd9\u662f\u4ed6\u4ece\u672a\u6709\u8fc7\u7684\u4f53\u9a8c\u2014\u2014\u5766\u767d\u5730\u8bf4\uff0c\u4ed6\u76ee\u524d\u72b6\u6001\u5f88\u4e0d\u597d\u3002</p><p>\u548c\u4e00\u4f4d\u7f8e\u4e3d\u5973\u5b50\u5355\u72ec\u76f8\u5904\u4e5d\u5929\uff0c\u8fd9\u542c\u8d77\u6765\u5f88\u6d6a\u6f2b\u5f88\u7f8e\u597d\uff1b\u80fd\u591f\u548c\u5979\u4e91\u96e8\u4ea4\u6b22\uff0c\u8086\u610f\u9700\u7d22\uff0c\u8fd9\u5c31\u66f4\u4ee4\u4eba\u4e07\u5206\u671f\u5f85\uff0c\u6c42\u4e4b\u4e0d\u5f97\u3002\u4f46\u5982\u679c\u589e\u52a0\u4e00\u9879\u9650\u5b9a\uff0c\u8981\u6c42\u5728\u8fd9\u4e5d\u5929\u5185\uff0c\u4f60\u5fc5\u987b\u548c\u5979\u4e00\u573a\u63a5\u4e00\u573a\u3001\u4e00\u6b21\u53c8\u4e00\u6b21\u5730\u505a\u7231\uff0c\u611f\u89c9\u53c8\u4f1a\u662f\u5982\u4f55\u5462\uff1f</p><p>\u5728\u6ca1\u6709\u5b9e\u9645\u5c1d\u8bd5\u4e4b\u524d\uff0c\u743c\u6069\u5927\u6982\u8fd8\u662f\u4f1a\u89c9\u5f97\u201c\u6ca1\u4ec0\u4e48\u95ee\u9898\u554a\uff0c\u633a\u597d\u7684\u201d\uff0c\u4f46\u73b0\u5728\uff0c\u4ed6\u5df2\u7ecf\u5b8c\u5168\u629b\u5f03\u4e86\u8fd9\u4e2a\u5ff5\u5934\uff0c\u4ed6\u73b0\u5728\u7684\u611f\u89c9\u4e0d\u597d\uff0c\u5f88\u4e0d\u597d\uff0c\u975e\u5e38\u4e0d\u597d\u3002</p><p>\u592a\u7d2f\u4e86\u3002</p><p>\u5e76\u4e0d\u662f\u751f\u7406\u610f\u4e49\u4e0a\u7684\u75b2\u52b3\u2014\u2014\u5728\u8fd9\u6d1e\u5929\u798f\u5730\u4e4b\u4e2d\uff0c\u4ed6\u7684\u7cbe\u529b\u662f\u8fd1\u4e4e\u65e0\u9650\u7684\uff0c\u8089\u830e\u65e0\u8bba\u55b7\u53d1\u591a\u5c11\u6b21\uff0c\u53ea\u8981\u7a0d\u7a0d\u4f11\u606f\u7247\u523b\uff0c\u7acb\u523b\u5c31\u53c8\u80fd\u6062\u590d\u5f97\u751f\u9f99\u6d3b\u864e\u2014\u2014\u95ee\u9898\u6765\u81ea\u4e8e\u5fc3\u7406\u3002</p><p>\u7537\u4eba\u662f\u6b32\u671b\u52a8\u7269\uff0c\u4f46\u6b32\u671b\u7ec8\u7a76\u4e5f\u662f\u6709\u9650\u5ea6\u7684\uff0c\u4ed6\u6709\u65e0\u7a77\u65e0\u5c3d\u7684\u7cbe\u529b\uff0c\u5374\u5e76\u6ca1\u6709\u540c\u6837\u65e0\u7a77\u65e0\u5c3d\u7684\u6b32\u671b\u3002\u53d1\u6cc4\u6b32\u671b\u7684\u65f6\u5019\u81ea\u7136\u662f\u5f88\u723d\uff0c\u4f46\u660e\u660e\u5df2\u7ecf\u53d1\u6cc4\u5b8c\u6bd5\uff0c\u5374\u8fd8\u8981\u4ed6\u7ee7\u7eed\u786c\u633a\u7740\u4e0a\uff0c\u8fd9\u5c31\u4e0d\u662f\u5e78\u798f\uff0c\u800c\u662f\u70e6\u607c\u4e86\u3002</p><p>\u53e6\u5916\uff0c\u8fd9\u4e5f\u4e0e\u201c\u5bf9\u8c61\u201d\u6709\u5173\u3002\u5982\u679c\u9762\u5bf9\u7684\u662f\u73ca\u5609\uff0c\u6216\u8005\u6885\u83f2\u65af\uff0c\u53c8\u6216\u8005\u662f\u51db\u3001\u8299\u857e\u72c4\u8fd9\u6837\u7684\u5973\u5b69\u5b50\uff0c\u743c\u6069\u81ea\u89c9\u60c5\u51b5\u5c31\u4f1a\u597d\u5f88\u591a\uff0c\u4f46\u6b64\u523b\u4ed6\u9762\u5bf9\u7684\u662f\u7ef4\u82e5\u62c9\u3002\u5012\u5e76\u4e0d\u662f\u8bf4\u7ef4\u82e5\u62c9\u4e0d\u591f\u7f8e\u4e3d\u2014\u2014\u5373\u4fbf\u4ee5\u743c\u6069\u8fd9\u6837\u6311\u5254\u7684\u773c\u5149\u6765\u8bc4\u5224\uff0c\u5979\u4e5f\u662f\u4f4d\u4e0d\u6298\u4e0d\u6263\u7684\u7f8e\u4eba\u513f\uff0c\u65e0\u8bba\u5bb9\u8c8c\u6c14\u8d28\uff0c\u90fd\u79f0\u5f97\u4e0a\u4e00\u6d41\u3002\u95ee\u9898\u5728\u4e8e\uff0c\u4ed6\u548c\u7ef4\u82e5\u62c9\u4e4b\u95f4\u6ca1\u6709\u201c\u7231\u201d\u3002</p><p>\u6b32\u671b\u4eff\u4f5b\u6f6e\u6c34\uff0c\u603b\u662f\u5267\u70c8\u800c\u77ed\u6682\u7684\uff0c\u4e0d\u53ef\u80fd\u4e00\u76f4\u6301\u7eed\uff1b\u4f46\u7231\u5219\u4e0d\u7136\uff0c\u5b83\u6216\u8bb8\u76f8\u5bf9\u5e73\u6de1\uff0c\u5374\u80fd\u591f\u957f\u5b58\u4e0d\u8870\u3002\u5982\u679c\u662f\u548c\u81ea\u5df1\u5fc3\u7231\u7684\u5973\u5b69\u5b50\u5728\u4e00\u8d77\uff0c\u4e24\u4e2a\u4eba\u53ef\u4ee5\u95f2\u8c08\u804a\u5929\uff0c\u53ef\u4ee5\u73a9\u7b11\u6597\u5634\uff0c\u53ef\u4ee5\u56de\u5fc6\u5f80\u4e8b\uff0c\u53ef\u4ee5\u89c4\u5212\u660e\u5929\uff0c\u53ef\u4ee5\u5206\u4eab\u79d8\u5bc6\uff0c\u53ef\u4ee5\u4e92\u76f8\u4f9d\u9760\uff0c\u6709\u60c5\u611f\u4e0a\u7684\u4ea4\u6d41\uff0c\u6709\u5f7c\u6b64\u7684\u4fe1\u4efb\u548c\u5bc4\u6258\uff0c\u6bcf\u4e00\u6b21\u7ed3\u5408\u90fd\u53ef\u4ee5\u662f\u5145\u6ee1\u7231\u610f\uff0c\u7075\u8089\u4ea4\u878d\uff0c\u6e29\u99a8\u800c\u751c\u871c\uff0c\u5929\u8352\u5730\u8001\u4eff\u4f5b\u90fd\u4e0d\u7b97\u4ec0\u4e48\uff0c\u4f55\u51b5\u533a\u533a\u4e5d\u5929\u65f6\u95f4\u3002</p><p>\u751a\u81f3\uff0c\u743c\u6069\u6709\u65f6\u5019\u5728\u60f3\uff0c\u54ea\u6015\u53cd\u8fc7\u6765\uff0c\u4e0d\u662f\u7231\uff0c\u800c\u662f\u6068\uff0c\u611f\u89c9\u4e5f\u4f1a\u597d\u5f88\u591a\u3002\u5018\u82e5\u4ed6\u4e0e\u7ef4\u82e5\u62c9\u4e4b\u95f4\u6709\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff0c\u90a3\u81f3\u5c11\u8fd8\u6709\u62a5\u590d\u7684\u5feb\u611f\uff0c\u53ef\u4ee5\u7528\u6765\u4f5c\u4e3a\u652f\u6491\u81ea\u5df1\u7ee7\u7eed\u7684\u52a8\u529b\u3002</p><p>\u4f46\u743c\u6069\u548c\u7ef4\u82e5\u62c9\u4e4b\u95f4\uff0c\u5b8c\u5168\u5c31\u662f\u964c\u8def\u4eba\uff0c\u6ca1\u6709\u534a\u70b9\u5171\u540c\u8bed\u8a00\uff0c\u4e4b\u524d\u7684\u4eba\u751f\u4e5f\u6ca1\u6709\u534a\u70b9\u4ea4\u96c6\uff0c\u66f4\u522b\u8c08\u4ec0\u4e48\u7231\u6216\u8005\u6068\u3002\u6ca1\u6709\u60c5\u611f\u505a\u652f\u6491\uff0c\u5f53\u6700\u521d\u7684\u65b0\u9c9c\u611f\u6e10\u6e10\u892a\u53bb\uff0c\u5f53\u6b32\u671b\u5728\u5979\u4f53\u5185\u53d1\u6cc4\u5f97\u4e00\u5e72\u4e8c\u51c0\uff0c\u5269\u4e0b\u6765\u7684\uff0c\u5c31\u53ea\u6709\u75b2\u5026\u548c\u9ebb\u6728\u4e86\u3002</p><p>\u518d\u8fd9\u6837\u4e0b\u53bb\uff0c\u771f\u7684\u4f1a\u4e0d\u4e3e\u7684\u2026\u2026</p><p>\u4e4b\u6240\u4ee5\u6ca1\u6709\u51fa\u73b0\u8fd9\u79cd\u4e22\u8138\u7684\u7ed3\u679c\uff0c\u4e00\u65b9\u9762\u662f\u6709\u76ee\u6807\uff0c\u6709\u5b9e\u5b9e\u5728\u5728\u7684\u5229\u76ca\uff0c\u6bcf\u4e00\u6b21\u4ea4\u5408\uff0c\u743c\u6069\u90fd\u80fd\u591f\u4ece\u5973\u5deb\u5e08\u4f53\u5185\u5438\u53d6\u5230\u201c\u8679\u96fe\u201d\u2014\u2014\u800c\u4e14\u4ed6\u8fd8\u6709\u4e86\u4e2a\u4ee4\u4eba\u60ca\u559c\u7684\u65b0\u53d1\u73b0\uff0c\u5c31\u662f\u5728\u5973\u65b9\u8fbe\u5230\u9ad8\u6f6e\u65f6\uff0c\u4ed6\u5438\u6536\u795e\u529b\u7684\u6548\u7387\u683c\u5916\u9ad8\uff0c\u6bd4\u201c\u5e73\u5e38\u72b6\u51b5\u201d\u8981\u9ad8\u51e0\u500d\uff0c\u4e4b\u524d\u867d\u7136\u6709\u8fc7\u51e0\u6b21\u7ecf\u9a8c\uff0c\u4f46\u90fd\u6ca1\u6709\u8fd9\u6837\u957f\u65f6\u95f4\u5730\u53cd\u590d\u5c1d\u8bd5\uff0c\u6240\u4ee5\u672a\u66fe\u53d1\u89c9\u3002\u53e6\u4e00\u65b9\u9762\uff0c\u5219\u662f\u9760\u4e86\u7ef4\u82e5\u62c9\u7684\u5e2e\u5fd9\u3002</p><p>\u77ed\u77ed\u51e0\u5929\u4e4b\u5185\uff0c\u743c\u6069\u5df2\u7ecf\u4ece\u5979\u90a3\u91cc\u5b66\u4f1a\u4e86\u5341\u51e0\u79cd\u201c\u5e8a\u4e0a\u4e13\u7528\u201d\u7684\u60c5\u6b32\u6cd5\u672f\uff0c\u82e5\u975e\u6709\u8fd9\u4e9b\u5c42\u51fa\u4e0d\u7a77\u7684\u6cd5\u672f\u8f85\u52a9\uff0c\u52c9\u5f3a\u7ef4\u6301\u4f4f\u65b0\u9c9c\u611f\uff0c\u743c\u6069\u53ea\u6015\u771f\u7684\u8981\u652f\u6491\u4e0d\u4e0b\u53bb\u4e86\u3002</p><p>\u743c\u6069\u4e5f\u5f88\u5947\u602a\uff0c\u5979\u4e00\u4e2a\u672a\u7ecf\u4eba\u4e8b\u7684\u5904\u5973\uff0c\u600e\u4e48\u4f1a\u61c2\u8fd9\u4e48\u591a\u8fd9\u6837\u201c\u6deb\u90aa\u201d\u7684\u6cd5\u672f\u3002\u4ed6\u60f3\u4e0d\u660e\u767d\uff0c\u5fcd\u4e0d\u4f4f\u4fbf\u53bb\u95ee\uff0c\u5973\u5deb\u5e08\u4e00\u5f00\u59cb\u81ea\u7136\u4e0d\u80af\u56de\u7b54\uff0c\u4f46\u88ab\u4ed6\u63a5\u8fde\u641e\u4e0a\u56db\u4e94\u6b21\u9ad8\u6f6e\u540e\uff0c\u4e5f\u5c31\u4e56\u4e56\u5410\u9732\u4e86\u7f18\u7531\u3002</p><p>\u201c\u8fd9\u4e9b\u662f\u4e4b\u524d\u67d0\u4f4d\u4f20\u9053\u5deb\u5e08\u7559\u4e0b\u7684\u9057\u4ea7\u3002\u201d</p><p>\u963f\u7956\u65af\u7684\u9009\u6c11\u66f4\u8fed\u975e\u5e38\u9891\u7e41\uff0c\u81ea\u6559\u4f1a\u521b\u7acb\u4ee5\u6765\u4e00\u5343\u4e8c\u767e\u4f59\u5e74\uff0c\u5df2\u7ecf\u6709\u4e86\u516b\u5341\u4e03\u4f4d\u4f20\u9053\u5deb\u5e08\uff0c\u6263\u9664\u6389\u90a3\u4e9b\u65e0\u4eba\u5728\u4f4d\u7684\u7a7a\u767d\u671f\u95f4\uff0c\u5e73\u5747\u5927\u7ea6\u6bcf\u5341\u5e74\u5c31\u6362\u4e00\u4e2a\u9009\u6c11\u3002\u8fd9\u79cd\u66f4\u65b0\u6362\u4ee3\u7684\u901f\u5ea6\uff0c\u8ba9\u5176\u4ed6\u6240\u6709\u795e\u660e\u7684\u6559\u4f1a\u90fd\u7518\u62dc\u4e0b\u98ce\uff0c\u671b\u5c18\u83ab\u53ca\u3002\u5982\u6b64\u591a\u7684\u4f20\u9053\u5deb\u5e08\uff0c\u4e0d\u4e4f\u7279\u7acb\u72ec\u884c\u4e4b\u8f88\uff0c\u5176\u4e2d\u4fbf\u6709\u4e00\u4f4d\u9a6c\u4f26\u5148\u751f\uff0c\u4ee5\u597d\u8272\u6ee5\u60c5\u800c\u8457\u540d\uff0c\u81ea\u79f0\u66fe\u7ecf\u4e0e\u4e0a\u5343\u540d\u5973\u6027\u6709\u8fc7\u8d85\u53cb\u8c0a\u5173\u7cfb\uff0c\u5176\u70ae\u53cb\u904d\u5e03\u6574\u4e2a\u5927\u9646\uff0c\u8d70\u5230\u54ea\u91cc\u90fd\u4f1a\u88ab\u4eba\u8ffd\u6740\uff0c\u6700\u540e\u636e\u8bf4\u662f\u7a81\u7136\u6709\u4e00\u5929\u65e9\u4e0a\u7761\u9192\uff0c\u89c9\u5f97\u751f\u6d3b\u5df2\u7ecf\u6ca1\u6709\u4efb\u4f55\u610f\u4e49\uff0c\u4e8e\u662f\u6bc5\u7136\u51b3\u7136\u5730\u7ed9\u4e86\u81ea\u5df1\u4e00\u53d1\u89e3\u79bb\u672f\u3002\u6b64\u4eba\u70ed\u8877\u4e8e\u5404\u79cd\u201c\u7c89\u7ea2\u201d\u6cd5\u672f\uff0c\u641c\u96c6\u3001\u6574\u7406\u548c\u53d1\u660e\u4e86\u5171\u8ba1\u8d85\u8fc7\u4e94\u5341\u9053\uff0c\u4f5c\u4e3a\u9057\u4ea7\u7559\u4e86\u4e0b\u6765\u3002</p><p>\u963f\u7956\u65af\u7684\u201c\u8679\u96fe\u201d\uff0c\u6709\u79cd\u975e\u5e38\u5947\u5999\u7684\u201c\u590d\u523b\u201d\u529f\u80fd\u3002\u4f20\u9053\u5deb\u5e08\u81ea\u83b7\u5f97\u8679\u96fe\u7684\u90a3\u4e00\u523b\u8d77\uff0c\u5176\u6240\u6709\u6709\u5173\u9b54\u6cd5\u7684\u77e5\u8bc6\u4e0e\u7ecf\u9a8c\u4fbf\u4f1a\u88ab\u81ea\u52a8\u590d\u5f55\uff0c\u5e76\u50a8\u5b58\u5728\u8679\u96fe\u4e4b\u4e2d\u3002\u4f20\u9053\u5deb\u5e08\u53bb\u4e16\u540e\uff0c\u8679\u96fe\u81ea\u7136\u662f\u88ab\u795e\u660e\u6536\u56de\uff0c\u7136\u540e\u8d50\u4e88\u4e0b\u4e00\u4efb\u9009\u6c11\u3002\u800c\u6bcf\u4e00\u4f4d\u7ee7\u4efb\u7684\u9009\u6c11\uff0c\u5728\u83b7\u5f97\u8679\u96fe\u65f6\uff0c\u4e5f\u81ea\u52a8\u83b7\u5f97\u4e86\u8fd9\u4e9b\u50a8\u5b58\u5176\u4e2d\u7684\u77e5\u8bc6\u4e0e\u7ecf\u9a8c\u3002\u5f53\u7136\uff0c\u6bd5\u7adf\u4e0d\u662f\u81ea\u5df1\u7684\u4e1c\u897f\uff0c\u65e0\u6cd5\u968f\u5fc3\u6240\u6b32\u5730\u8fd0\u7528\uff0c\u4f46\u4f5c\u4e3a\u8d44\u6599\u5e93\u8fd8\u662f\u6ca1\u6709\u95ee\u9898\u7684\u3002</p><p>\u201c\u539f\u6765\u662f\u8fd9\u6837\u554a\uff0c\u767d\u9ad8\u5174\u4e00\u573a\u3002\u201d\u743c\u6069\u53f9\u4e86\u53e3\u6c14\u3002</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u6211\u672c\u6765\u8fd8\u4ee5\u4e3a\uff0c\u4f60\u8868\u9762\u6e05\u7eaf\uff0c\u9aa8\u5b50\u91cc\u5374\u662f\u4e2a\u98ce\u9a9a\u653e\u8361\u7684\u5c0f\u6deb\u5a03\uff0c\u62c5\u5fc3\u5c06\u6765\u7537\u4eba\u5582\u4e0d\u9971\u81ea\u5df1\uff0c\u6240\u4ee5\u5077\u5077\u5b66\u4e86\u8fd9\u4e48\u591a\u8fd9\u79cd\u6cd5\u672f\uff0c\u201d\u743c\u6069\u6545\u610f\u53d6\u7b11\uff0c\u201c\u539f\u6765\u4e0d\u662f\uff0c\u771f\u53ef\u60dc\u5462\u3002\u201d</p><p>\u201c\u2026\u2026\u90a3\u8fd8\u771f\u662f\u4e0d\u597d\u610f\u601d\uff0c\u8ba9\u4f60\u5931\u671b\u4e86\u3002\u201d</p><p>\u201c\u53ef\u662f\u6709\u4e2a\u95ee\u9898\u554a\uff0c\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u6309\u4f60\u6240\u8bf4\uff0c\u8679\u96fe\u4e2d\u50a8\u5b58\u7740\u6240\u6709\u4f20\u9053\u5deb\u5e08\u7684\u5b66\u8bc6\u3002\u6211\u73b0\u5728\u4e5f\u5df2\u7ecf\u5438\u53d6\u4e86\u4e0d\u5c11\u8679\u96fe\uff0c\u4e3a\u4ec0\u4e48\u5b8c\u5168\u6ca1\u611f\u89c9\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u6211\u5c31\u4e0d\u6e05\u695a\u4e86\uff0c\u201d\u5973\u5deb\u5e08\u56de\u7b54\uff0c\u201c\u2018\u8679\u96fe\u2019\u4e4b\u524d\u7684\u6bcf\u4e00\u6b21\u8f6c\u79fb\uff0c\u90fd\u662f\u7531\u795e\u660e\u6307\u5b9a\uff0c\u5728\u6bcf\u4e00\u4efb\u4f20\u9053\u5deb\u5e08\u4e2d\u7ee7\u627f\u3002\u50cf\u4f60\u7528\u8fd9\u79cd\u65b9\u5f0f\u53d6\u5f97\u2026\u2026\u95fb\u6240\u672a\u95fb\uff0c\u4ece\u65e0\u5148\u4f8b\uff0c\u4e2d\u95f4\u4f1a\u6709\u4ec0\u4e48\u53d8\u5316\uff0c\u6211\u4e5f\u65e0\u6cd5\u5224\u65ad\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\u3002\u201d</p><p>\u743c\u6069\u6709\u9887\u4e3a\u90c1\u95f7\uff0c\u4ed6\u6b64\u884c\u4e3b\u8981\u7684\u76ee\u7684\uff0c\u8fd8\u662f\u60f3\u901a\u8fc7\u5438\u53d6\u795e\u529b\u7684\u65b9\u5f0f\u5feb\u901f\u63d0\u5347\u529b\u91cf\uff0c\u4f46\u5c31\u76ee\u524d\u6240\u77e5\u7684\u8679\u96fe\u7684\u4e24\u4e2a\u7528\u9014\uff0c\u4e00\u662f\u201c\u4f20\u9053\u201d\uff0c\u5f88\u6709\u8da3\uff0c\u4f46\u4f3c\u4e4e\u6ca1\u4ec0\u4e48\u7528\uff0c\u81f3\u5c11\u77ed\u671f\u5185\u662f\u6ca1\u7528\uff1b\u4e00\u662f\u201c\u590d\u523b\u201d\uff0c\u5012\u662f\u5f88\u6709\u4ef7\u503c\uff0c\u51e0\u5341\u4f4d\u4f20\u9053\u5deb\u5e08\u7684\u7ecf\u9a8c\u4f20\u627f\uff0c\u5bf9\u4e8e\u4efb\u4f55\u4e00\u540d\u5deb\u5e08\u800c\u8a00\u90fd\u582a\u79f0\u65e0\u4ef7\u4e4b\u5b9d\u2014\u2014\u5374\u53c8\u7531\u4e8e\u672a\u77e5\u539f\u56e0\u65e0\u6cd5\u5b9e\u73b0\u3002\u90a3\u4ed6\u5fd9\u5fd9\u788c\u788c\u4e00\u573a\uff0c\u53c8\u662f\u4e3a\u4e86\u4ec0\u4e48\uff0c\u96be\u4e0d\u6210\u771f\u7684\u662f\u641e\u5973\u4eba\u4e48\u2026\u2026\u867d\u7136\u4e5f\u786e\u5b9e\u641e\u5f97\u5f88\u723d\u5c31\u662f\u4e86\u3002</p><p>\u501f\u6b64\u673a\u4f1a\uff0c\u743c\u6069\u8be2\u95ee\u6709\u5173\u8679\u96fe\u7684\u8be6\u7ec6\u8d44\u6599\u3002</p><p>\u201c\u8679\u96fe\u6709\u4e09\u79cd\u529f\u7528\uff0c\u524d\u4e24\u79cd\u4f60\u90fd\u5df2\u7ecf\u77e5\u9053\u4e86\uff0c\u5373\u662f\u2018\u4f20\u9053\u2019\u548c\u2018\u590d\u523b\u2019\uff0c\u201d\u7ef4\u82e5\u62c9\u5012\u662f\u9075\u5b88\u627f\u8bfa\uff0c\u5e76\u672a\u63a8\u8131\uff0c\u201c\u81f3\u4e8e\u7b2c\u4e09\u79cd\u529f\u7528\uff0c\u6211\u4eec\u79f0\u4e4b\u4e3a\u2018\u865a\u62df\u2019\u3002\u201d</p><p>\u865a\u62df\uff1f</p><p>\u743c\u6069\u987f\u65f6\u6709\u4e86\u79cd\u4e0d\u597d\u7684\u9884\u611f\uff0c\u5c31\u542c\u7ef4\u82e5\u62c9\u89e3\u91ca\u8bf4\uff1a\u201c\u6240\u8c13\u865a\u62df\uff0c\u5373\u662f\u53ef\u4ee5\u4f7f\u7528\u8679\u96fe\u521b\u9020\u4e00\u4e2a\u4e34\u65f6\u7684\u9886\u57df\uff0c\u5728\u8fd9\u4e2a\u9886\u57df\u4e2d\uff0c\u6211\u53ef\u4ee5\u65e0\u9650\u6b21\u6570\u5730\u4f7f\u7528\u4efb\u4f55\u6211\u6240\u4e86\u89e3\u7684\u6cd5\u672f\uff0c\u5305\u62ec\u590d\u523b\u5728\u8679\u96fe\u4e2d\u7684\u90a3\u4e9b\u3002\u201d</p><p>\u54e6\uff0c\u8fd9\u542c\u8d77\u6765\u975e\u5e38\u5389\u5bb3\u554a\u3002\u867d\u8bf4\u4e0d\u80fd\u76f4\u63a5\u63d0\u5347\u6cd5\u672f\u7684\u5a01\u529b\uff0c\u4f46\u80fd\u591f\u8ba9\u5deb\u5e08\u201c\u65e0\u9650\u6b21\u6570\u4f7f\u7528\u6cd5\u672f\u201d\u2014\u2014\u8fd8\u6709\u4ec0\u4e48\u6bd4\u8fd9\u66f4\u79bb\u8c31\u7684\uff1f\u8981\u77e5\u9053\uff0c\u8fd9\u4e2a\u4e16\u754c\u7684\u5deb\u5e08\uff0c\u53ef\u4e0d\u50cf\u743c\u6069\u4ee5\u524d\u73a9\u7684\u6e38\u620f\u91cc\u90a3\u4e9b\u84dd\u6761\u9b54\u6cd5\u5e08\uff0c\u65bd\u6cd5\u6570\u91cf\u662f\u975e\u5e38\u6709\u9650\u7684\uff0c\u8fd9\u4e5f\u662f\u5deb\u5e08\u4eec\u6700\u5927\u7684\u5f31\u70b9\u4e4b\u4e00\u3002\u963f\u7956\u65af\u7684\u201c\u8679\u96fe\u201d\u5c45\u7136\u80fd\u6d88\u9664\u8fd9\u4e2a\u5f31\u70b9\uff0c\u679c\u7136\u4e0d\u6127\u662f\u5deb\u5e08\u4e4b\u795e\u3002</p><p>\u201c\u4f46\u8fd9\u4e2a\u4e34\u65f6\u7684\u9886\u57df\uff0c\u662f\u72ec\u7acb\u4e8e\u73b0\u5b9e\u4e16\u754c\u4e4b\u5916\u7684\u3002\u201d\u7ef4\u82e5\u62c9\u53c8\u8865\u5145\u8bf4\u3002</p><p>\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u610f\u601d\u5c31\u662f\u8bf4\uff0c\u8fd9\u4e2a\u4e34\u65f6\u7684\u9886\u57df\uff0c\u53ea\u5b58\u5728\u4e8e\u4f60\u7684\u610f\u8bc6\u4e4b\u4e2d\uff0c\u65e0\u6cd5\u4e0e\u73b0\u5b9e\u4e16\u754c\u4e92\u76f8\u5e72\u6d89\uff0c\u201d\u5973\u5deb\u5e08\u56de\u7b54\uff0c\u201c\u4f60\u5728\u8fd9\u4e2a\u9886\u57df\u4e2d\uff0c\u786e\u5b9e\u662f\u53ef\u4ee5\u65e0\u9650\u5236\u5730\u4f7f\u7528\u6cd5\u672f\uff0c\u4f46\u5b83\u4eec\u4e0d\u4f1a\u5bf9\u73b0\u5b9e\u4e16\u754c\u4ea7\u751f\u4efb\u4f55\u5f71\u54cd\u3002\u201d</p><p>\u201c\u2026\u2026\u90a3\u8fd9\u8fd8\u6709\u4ec0\u4e48\u610f\u4e49\uff1f\u201d</p><p>\u201c\u6240\u4ee5\u662f\u2018\u865a\u62df\u2019\u800c\u4e0d\u662f\u771f\u6b63\u7684\u6a21\u62df\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u81f3\u4e8e\u8981\u8bf4\u610f\u4e49\uff0c\u5b83\u662f\u7528\u6765\u505a\u65e5\u5e38\u7ec3\u4e60\u7684\u3002\u201d</p><p>\u65e5\u5e38\u7ec3\u4e60\u2026\u2026\u597d\u5427\uff0c\u8fd9\u786e\u5b9e\u662f\u4e2a\u5f88\u597d\u7684\u601d\u8def\u3002</p><p>\u9b54\u6cd5\u7e41\u6742\u7cbe\u6df1\uff0c\u9664\u975e\u771f\u7684\u662f\u5929\u7eb5\u5947\u624d\uff0c\u5426\u5219\u638c\u63e1\u4e00\u9053\u6cd5\u672f\uff0c\u9664\u4e86\u7406\u8bba\u5b66\u4e60\uff0c\u8fd8\u8981\u7ecf\u8fc7\u53cd\u53cd\u590d\u590d\u7684\u5b9e\u8df5\u7ec3\u4e60\u624d\u884c\u3002\u4f46\u5bf9\u4e8e\u8fd9\u4e2a\u4e16\u754c\u7684\u5deb\u5e08\u800c\u8a00\uff0c\u7531\u4e8e\u6ca1\u6709\u84dd\u6761\uff0c\u5fc5\u987b\u901a\u8fc7\u4e00\u5b9a\u65f6\u95f4\u7684\u51c6\u5907\uff0c\u624d\u80fd\u5851\u6210\u6cd5\u672f\uff0c\u800c\u6cd5\u672f\u4e00\u65e6\u91ca\u653e\u51fa\u53bb\uff0c\u5c31\u8981\u518d\u82b1\u5f88\u957f\u65f6\u95f4\u91cd\u65b0\u51c6\u5907\u3002\u8fd9\u5bfc\u81f4\u4e00\u70b9\uff0c\u5c31\u662f\u5bf9\u4e8e\u7edd\u5927\u591a\u6570\u5deb\u5e08\u800c\u8a00\uff0c\u201c\u6cd5\u672f\u7ec3\u4e60\u201d\u7684\u673a\u4f1a\u90fd\u662f\u4e0d\u600e\u4e48\u5145\u8db3\u7684\u3002\u5373\u4fbf\u50cf\u743c\u6069\uff0c\u6709\u201c\u6df1\u5ea6\u6697\u793a\u201d\u7684\u4f5c\u5f0a\u5668\u5728\u624b\uff0c\u53ef\u4ee5\u4e00\u5929\u591a\u6b21\u51c6\u5907\u6cd5\u672f\uff0c\u4f9d\u7136\u8fd8\u662f\u5e38\u5e38\u89c9\u5f97\u81ea\u5df1\u7684\u8bad\u7ec3\u4e0d\u591f\u3002\u4ece\u8fd9\u70b9\u6765\u8bf4\uff0c\u8679\u96fe\u76f8\u5f53\u4e8e\u7ed9\u5deb\u5e08\u521b\u5efa\u4e86\u4e00\u4e2a\u865a\u62df\u7684\u8bad\u7ec3\u573a\uff0c\u867d\u7136\u4e0d\u80fd\u76f4\u63a5\u63d0\u5347\u5b9e\u529b\uff0c\u4f46\u4ece\u957f\u8fdc\u6765\u8bf4\uff0c\u786e\u5b9e\u4ef7\u503c\u975e\u51e1\u3002</p><p>\u53ef\u662f\u8fd9\u4e0d\u662f\u4ed6\u73b0\u5728\u60f3\u8981\u7684\u3002</p><p>\u743c\u6069\u73b0\u5728\u7684\u611f\u89c9\uff0c\u5c31\u50cf\u81ea\u5df1\u662f\u4e00\u4e2a\u5728\u8352\u91ce\u4e2d\u8270\u96be\u8dcb\u6d89\u7684\u65c5\u4eba\uff0c\u53c8\u9965\u53c8\u6e34\uff0c\u671f\u671b\u4e00\u5757\u9762\u5305\u3001\u4e00\u676f\u6c34\uff0c\u7ed3\u679c\u5929\u4e0a\u6389\u4e0b\u6765\u4e00\u5927\u5757\u9ec4\u707f\u707f\u7684\u91d1\u5b50\u3002\u91d1\u5b50\u81ea\u7136\u662f\u597d\u4e1c\u897f\uff0c\u53ea\u8981\u79bb\u5f00\u8352\u91ce\uff0c\u8d70\u5230\u96c6\u5e02\u91cc\uff0c\u9762\u5305\u548c\u6c34\u8981\u591a\u5c11\u6709\u591a\u5c11\u3002\u7136\u800c\u95ee\u9898\u5728\u4e8e\uff0c\u6211\u8981\u5148\u8d70\u51fa\u8352\u91ce\u518d\u8bf4\u2026\u2026</p><p>\u5e76\u975e\u4ed6\u6025\u529f\u8fd1\u5229\uff0c\u53ea\u662f\u65f6\u4e0d\u6211\u5f85\u554a\u3002</p><p>\u53d7\u6b64\u6253\u51fb\uff0c\u743c\u6069\u4e00\u65f6\u95f4\u6709\u4e9b\u6cae\u4e27\uff0c\u4f46\u5f88\u5feb\u4e5f\u5c31\u6062\u590d\u8fc7\u6765\u3002\u4e16\u4e8b\u54ea\u80fd\u6837\u6837\u5c3d\u5982\u4eba\u610f\uff0c\u81ea\u5df1\u6765\u8fd9\u4e00\u8d9f\uff0c\u51ef\u745f\u7433\u4e5f\u89c1\u5230\u4e86\uff0c\u795e\u529b\u4e5f\u5077\u5230\u4e86\uff0c\u987a\u5e26\u8fd8\u4e0a\u4e86\u4e2a\u7f8e\u4eba\u513f\uff0c\u4fbf\u5b9c\u5df2\u7ecf\u5360\u5f97\u4e0d\u5c11\uff0c\u4e0d\u80fd\u592a\u8fc7\u5206\u5962\u6c42\u3002</p><p>\u6240\u4ee5\uff0c\u8be5\u505a\u7684\u4e8b\u60c5\uff0c\u8fd8\u662f\u7ee7\u7eed\u505a\u3002</p><p>\u201c\u7b2c\u51e0\u5929\u4e86\uff1f\u201d\u5f53\u4e00\u6b21\u505a\u7231\u7ed3\u675f\u540e\uff0c\u5973\u5deb\u5e08\u95ee\u3002</p><p>\u201c\u5927\u6982\u662f\u7b2c\u4e03\u5929\u7684\u540e\u534a\u6bb5\u5427\u3002\u201d</p><p>\u50cf\u743c\u6069\u8fd9\u79cd\u5b66\u9662\u51fa\u8eab\uff0c\u53d7\u8fc7\u4e13\u4e1a\u8bad\u7ec3\u7684\u5deb\u5e08\uff0c\u65f6\u95f4\u611f\u90fd\u4e0d\u4f1a\u5dee\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u5728\u8fd9\u79cd\u6f2b\u957f\u3001\u6ca1\u6709\u663c\u591c\u5206\u522b\uff0c\u751a\u81f3\u6ca1\u6709\u534a\u70b9\u5149\u9634\u53d8\u5316\u7684\u73af\u5883\u4e2d\uff0c\u8981\u4ed6\u51c6\u786e\u5224\u65ad\u65f6\u95f4\u7684\u6d41\u901d\uff0c\u8fd8\u662f\u6709\u4e9b\u5f3a\u4eba\u6240\u96be\uff0c\u4ed6\u6bd5\u7adf\u4e0d\u662f\u949f\u8868\uff0c\u53ea\u80fd\u505a\u4e00\u4e2a\u5927\u81f4\u7684\u4f30\u7b97\u3002</p><p>\u201c\u90a3\u4e48\u4e5f\u5c31\u662f\u5feb\u7ed3\u675f\u4e86\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u6b23\u5e03\u66fe\u7ecf\u548c\u6211\u63d0\u8d77\u8fc7\u4f60\uff0c\u201d\u5973\u5deb\u5e08\u5ffd\u7136\u8bf4\uff0c\u201c\u77e5\u9053\u5979\u5bf9\u4f60\u662f\u600e\u4e48\u8bc4\u4ef7\u7684\u5417\uff1f\u201d</p><p>\u201c\u54e6\uff1f\u5979\u600e\u4e48\u8bf4\u7684\uff1f\u201d</p><p>\u743c\u6069\u88ab\u63d0\u8d77\u4e86\u70b9\u5174\u81f4\uff0c\u60f3\u542c\u542c\u6b23\u5e03\u662f\u5982\u4f55\u770b\u5f85\u81ea\u5df1\u7684\uff0c\u6bd5\u7adf\u90a3\u662f\u51db\u7684\u8001\u5e08\u3002\u964c\u751f\u4eba\u7684\u8bc4\u5224\u4ed6\u53ef\u4ee5\u65e0\u89c6\uff0c\u4f46\u5bf9\u4e8e\u719f\u8bc6\u4e4b\u4eba\u7684\u770b\u6cd5\uff0c\u4ed6\u8fd8\u662f\u6709\u4e9b\u5728\u610f\u7684\uff0c\u81f3\u5c11\u4e0d\u80fd\u5b8c\u5168\u65e0\u52a8\u4e8e\u8877\u3002</p><p>\u201c\u5979\u8bf4\u4f60\u662f\u4e2a\u7f3a\u4e4f\u7acb\u573a\u7684\u5bb6\u4f19\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u743c\u6069\u4e00\u65f6\u95f4\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u53cd\u5e94\u3002\u4ed6\u5e76\u672a\u6307\u671b\u6b23\u5e03\u80fd\u5bf9\u4ed6\u6709\u4ec0\u4e48\u9ad8\u7aef\u5927\u6c14\u4e0a\u6863\u6b21\u7684\u8bc4\u8bed\uff0c\u53cc\u65b9\u7acb\u573a\u65e2\u5bf9\u7acb\uff0c\u4ea4\u60c5\u4e5f\u5f88\u52c9\u5f3a\uff0c\u800c\u743c\u6069\u548c\u51db\u7684\u5173\u7cfb\u66f4\u4e0d\u53ef\u80fd\u8ba9\u5979\u6ee1\u610f\u2014\u2014\u54ea\u4e2a\u8001\u5e08\u4f1a\u559c\u6b22\u81ea\u5df1\u5b66\u751f\u7684\u7537\u53cb\u8eab\u8fb9\u7f8e\u5973\u73af\u7ed5\u5462\uff0c\u6b23\u5e03\u65e0\u8bba\u8bf4\u4ed6\u4ec0\u4e48\uff0c\u54ea\u6015\u8bf8\u5982\u201c\u89c1\u5f02\u601d\u8fc1\u7684\u8272\u72fc\u201d\u3001\u201c\u90aa\u795e\u838e\u5c14\u7684\u8d70\u72d7\u201d\u4e4b\u7c7b\uff0c\u4ed6\u90fd\u662f\u6709\u5fc3\u7406\u51c6\u5907\u7684\uff0c\u4e5f\u53ea\u6253\u7b97\u4e00\u7b11\u4e86\u4e4b\u3002\u4f46\u201c\u7f3a\u4e4f\u7acb\u573a\u7684\u5bb6\u4f19\u201d\u2026\u2026\u8fd9\u4e2a\u8bc4\u4ef7\u5230\u5e95\u662f\u4ec0\u4e48\u610f\u601d\u554a\u3002</p><p>\u201c\u610f\u601d\u5f88\u6e05\u695a\u554a\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u5c31\u662f\u8bf4\u4f60\u7f3a\u4e4f\u81ea\u5df1\u7684\u7acb\u573a\u3002\u201d</p><p>\u201c\u2026\u2026\u662f\u5417\uff1f\u201d</p><p>\u743c\u6069\u76b1\u7740\u7709\u5934\uff0c\u8bd5\u7740\u53cd\u7701\u4e86\u4e00\u4e0b\uff0c\u7136\u540e\u53d1\u73b0\u6b23\u5e03\u8fd9\u53e5\u8bdd\u2026\u2026\u8fd8\u771f\u4e0d\u65e0\u9053\u7406\u3002</p><p>\u4ed6\u4e00\u8fb9\u63a5\u53d7\u838e\u5c14\u7684\u5f71\u706b\uff0c\u4e00\u8fb9\u548c\u6b23\u5e03\u7b49\u4eba\u6df7\u5728\u4e00\u8d77\uff1b\u4e0d\u4e45\u524d\u521a\u521a\u6345\u4e86\u8428\u9a6c\u65af\u7279\u4e00\u5200\uff0c\u73b0\u5728\u53c8\u628a\u9152\u8a00\u6b22\u8c08\u7b11\u98ce\u751f\uff1b\u4ed6\u4e0e\u5723\u6b66\u58eb\u60c5\u6295\u610f\u5408\uff0c\u613f\u610f\u4e3a\u4e86\u5979\u51fa\u751f\u5165\u6b7b\uff0c\u53c8\u548c\u5927\u9b54\u9b3c\u537f\u537f\u6211\u6211\uff0c\u552f\u6050\u5929\u4e0b\u4e0d\u4e71\uff1b\u9b54\u6cd5\u5973\u795e\u6559\u4f1a\u8981\u706d\u9f99\u5deb\u6559\uff0c\u4ed6\u5f88\u8d5e\u6210\uff0c\u8428\u9a6c\u65af\u7279\u8981\u6740\u963f\u7956\u65af\uff0c\u4ed6\u4e5f\u6ca1\u610f\u89c1\uff1b\u8ba9\u4ed6\u505a\u597d\u4e8b\uff0c\u6ca1\u6709\u95ee\u9898\uff0c\u8981\u4ed6\u505a\u574f\u4e8b\uff0c\u4e5f\u6ca1\u95ee\u9898\u2014\u2014\u5982\u6b64\u8bf4\u6765\uff0c\u81ea\u5df1\u8fd8\u771f\u662f\u6beb\u65e0\u7acb\u573a\u53ef\u8a00\uff0c\u6bd4\u671d\u4e09\u66ae\u56db\u7684\u7334\u5b50\u53d8\u5316\u90fd\u5feb\u3002</p><p>\u8fd9\u6837\u5b50\u4f3c\u4e4e\u4e0d\u592a\u597d\u554a\u3002</p><p>\u4e00\u4e2a\u4eba\u7f3a\u4e4f\u7a33\u5b9a\u7684\u7acb\u573a\uff0c\u603b\u4e0d\u662f\u4ef6\u4ec0\u4e48\u597d\u4e8b\uff0c\u81f3\u5c11\u743c\u6069\u662f\u8fd9\u4e48\u89c9\u5f97\u7684\u3002\u4f46\u82e5\u8981\u4ed6\u73b0\u5728\u660e\u786e\u81ea\u5df1\u7684\u7acb\u573a\uff0c\u5374\u53c8\u9887\u6709\u96be\u5ea6\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u832b\u7136\u65e0\u63aa\u3002\u52c9\u5f3a\u8981\u8bf4\u7684\u8bdd\uff0c\u5c31\u662f\u201c\u600e\u4e48\u6837\u5bf9\u81ea\u5df1\u6709\u5229\uff0c\u5c31\u600e\u4e48\u505a\u201d\u2014\u2014\u4f46\u8fd9\u4e5f\u80fd\u53eb\u7acb\u573a\u4e48\uff1f</p><p>\u5e78\u597d\uff0c\u7ef4\u82e5\u62c9\u63a5\u4e0b\u6765\u7684\u8bdd\uff0c\u6253\u65ad\u4e86\u4ed6\u7684\u80e1\u601d\u4e71\u60f3\u3002\u201c\u4e0d\u8fc7\u6b23\u5e03\u53c8\u8bf4\u4e86\u53e5\uff0c\u5979\u8bf4\uff1a\u90a3\u4e2a\u5bb6\u4f19\u5bf9\u81ea\u5df1\u7684\u5973\u4eba\u5f88\u5728\u4e4e\u3002\u201d</p><p>\u90a3\u5012\u662f\uff0c\u522b\u7684\u4e0d\u6562\u8bf4\uff0c\u8fd9\u70b9\u743c\u6069\u8fd8\u662f\u6709\u51e0\u5206\u81ea\u4fe1\u7684\u3002\u4f5c\u4e3a\u4e00\u4e2a\u4ee5\u521b\u5efa\u540e\u5bab\u4e3a\u76ee\u6807\u7684\u7537\u4eba\u800c\u8a00\uff0c\u4e0d\u5728\u4e4e\u81ea\u5df1\u5973\u4eba\uff0c\u90a3\u8fd8\u5728\u4e4e\u4ec0\u4e48\uff1f\u96be\u9053\u662f\u5728\u4e4e\u5929\u4e0b\u592a\u5e73\u4e16\u754c\u548c\u8c10\u4e4b\u7c7b\u7684\u5c0f\u4e8b\u60c5\u4e48\u3002</p><p>\u201c\u6240\u4ee5\u5462\uff1f\u201d\u743c\u6069\u770b\u7740\u5973\u5deb\u5e08\uff0c\u5bf9\u65b9\u7a81\u7136\u8bf4\u8d77\u8fd9\u4e2a\u8bdd\u9898\uff0c\u603b\u4e0d\u4f1a\u5f53\u771f\u662f\u968f\u53e3\u95f2\u804a\uff0c\u5e94\u8be5\u6709\u5176\u7528\u610f\u5427\uff0c\u201c\u4f60\u4e5f\u60f3\u505a\u6211\u7684\u5973\u4eba\u5417\uff1f\u201d\u4ed6\u534a\u5f00\u73a9\u7b11\u5730\u95ee\u3002</p><p>\u201c\u6709\u597d\u5904\u5417\uff1f\u201d\u5973\u5deb\u5e08\u53cd\u95ee\u3002</p><p>\u201c\u5443\uff0c\u8fd9\u4e2a\u4e48\u2026\u2026\u201d</p><p>\u743c\u6069\u63c9\u4e86\u63c9\u4e0b\u5df4\uff0c\u4e00\u65f6\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u56de\u7b54\u3002\u505a\u4ed6\u7684\u5973\u4eba\u6709\u4ec0\u4e48\u597d\u5904\u4e48\uff1f\u4f3c\u4e4e\u3001\u6216\u8bb8\u3001\u597d\u50cf\u3001\u5927\u6982\u2026\u2026\u4e5f\u6ca1\u6709\u554a\u3002\u4ed6\u603b\u4e0d\u80fd\u56de\u7b54\u8bf4\u201c\u53ef\u4ee5\u4eab\u53d7\u5230\u9ad8\u8d28\u91cf\u7684\u6027\u751f\u6d3b\u201d\u5427\uff0c\u90a3\u4e5f\u672a\u514d\u592a\u539a\u989c\u65e0\u803b\u4e86\u3002</p><p>\u201c\u4f60\u82e5\u613f\u610f\u6551\u6211\u4e00\u547d\uff0c\u5e2e\u6211\u8131\u79bb\u8428\u9a6c\u65af\u7279\u4e4b\u624b\uff0c\u201d\u5973\u5deb\u5e08\u8bf4\uff0c\u201c\u90a3\u4e48\u8981\u6211\u505a\u4f60\u7684\u5973\u4eba\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u53ef\u4ee5\u3002\u201d</p><p>\u743c\u6069\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u62b1\u6b49\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8fd9\u4e2a\u6211\u65e0\u6cd5\u7b54\u5e94\u3002\u201d</p><p>\u5728\u8428\u9a6c\u65af\u7279\u7684\u8ba1\u5212\u4e2d\uff0c\u4f20\u9053\u5deb\u5e08\u662f\u6838\u5fc3\u7684\u65bd\u6cd5\u8981\u7d20\uff0c\u65e2\u7136\u4ed6\u6562\u628a\u7ef4\u82e5\u62c9\u4ea4\u7ed9\u743c\u6069\uff0c\u81ea\u7136\u9884\u5148\u6709\u6240\u5e03\u7f6e\u9632\u8303\uff0c\u8001\u5deb\u5996\u662f\u7cbe\u795e\u6709\u95ee\u9898\uff0c\u5374\u4e0d\u662f\u667a\u5546\u6709\u969c\u788d\u3002\u81f3\u5c11\u743c\u6069\u5f88\u786e\u5b9a\uff0c\u4ec5\u51ed\u4ed6\u7684\u529b\u91cf\uff0c\u5373\u4fbf\u52a0\u4e0a\u624e\u745e\u5c14\uff0c\u8981\u60f3\u4ece\u8001\u5deb\u5996\u624b\u4e2d\u82f1\u96c4\u6551\u7f8e\uff0c\u53ef\u80fd\u6027\u4e0d\u5927\u3002\u5982\u679c\u6c42\u52a9\u4e8e\u51ef\u745f\u7433\uff0c\u6216\u8bb8\u662f\u53ef\u4ee5\u7684\uff0c\u4f46\u95ee\u9898\u662f\u51ef\u745f\u7433\u80af\u5e2e\u5fd9\u4e48\uff1f\u5373\u4fbf\u51ef\u745f\u7433\u80af\u5e2e\u5fd9\uff0c\u743c\u6069\u53c8\u4e3a\u4f55\u8981\u8fd9\u4e48\u505a\uff1f\u4ed6\u662f\u60f3\u770b\u9f99\u5deb\u6559\u548c\u9b54\u6cd5\u5973\u795e\u6559\u4f1a\u4e24\u8d25\u4ff1\u4f24\uff0c\u81ea\u5df1\u6d51\u6c34\u6478\u9c7c\uff0c\u5c31\u4e2d\u53d6\u5229\uff0c\u800c\u4e0d\u662f\u81ea\u5df1\u5306\u5306\u5fd9\u5fd9\u8df3\u4e0b\u53bb\u5438\u5f15\u4ec7\u6068\u2014\u2014\u4e0d\u7ba1\u662f\u54ea\u4e00\u8fb9\u7684\u4ec7\u6068\u3002\u5f53\u7136\u4e86\uff0c\u5982\u679c\u6362\u505a\u73ca\u5609\u6216\u8005\u6885\u83f2\u65af\u63d0\u8fd9\u79cd\u8981\u6c42\uff0c\u4ed6\u80af\u5b9a\u4e8c\u8bdd\u4e0d\u8bf4\u5c31\u4f1a\u540c\u610f\uff0c\u4f46\u7ef4\u82e5\u62c9\u4e48\u2026\u2026\u8fd8\u662f\u7b97\u4e86\u5427\uff0c\u5927\u5bb6\u53c8\u4e0d\u662f\u5f88\u719f\u3002</p><p>\u201c\u662f\u5417\uff0c\u201d\u5973\u5deb\u5e08\u957f\u957f\u5730\u5410\u4e86\u53e3\u6c14\uff0c\u4eff\u4f5b\u4e0b\u5b9a\u4e86\u67d0\u79cd\u51b3\u5fc3\uff0c\u201c\u5982\u679c\u6211\u8bf4\uff1a\u4f60\u975e\u7b54\u5e94\u4e0d\u53ef\u5462\uff1f\u201d</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u7136\u540e\u8138\u8272\u5fae\u53d8\uff0c\u201c\u4f60\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u201d\u7ef4\u82e5\u62c9\u8f7b\u58f0\u8bf4\uff0c\u201c\u53ea\u662f\u6211\u5bf9\u4f60\u4e0b\u4e86\u4e2a\u8bc5\u5492\u3002\u201d</p><p>\u7b2c\u4e00\u767e\u4e00\u5341\u4e00\u8282</p><p>\u8bc5\u5492\uff1f</p><p>\u743c\u6069\u6115\u7136\u3002\u5728\u8fd9\u4e2a\u5947\u5e7b\u4e16\u754c\uff0c\u9ad8\u660e\u7684\u5deb\u5e08\u786e\u5b9e\u53ef\u4ee5\u8bc5\u5492\u4ed6\u4eba\uff0c\u4e0d\u8db3\u4e3a\u5947\uff0c\u4f46\u4efb\u4f55\u8bc5\u5492\uff0c\u5f52\u6839\u7ed3\u5e95\u90fd\u662f\u9b54\u6cd5\u7684\u8fd0\u7528\uff0c\u800c\u7ef4\u82e5\u62c9\u660e\u660e\u88ab\u5c01\u9b54\u624b\u73af\u5c01\u5370\u4e86\u5168\u90e8\u65bd\u6cd5\u80fd\u529b\uff0c\u5979\u600e\u4e48\u7ed9\u743c\u6069\u4e0b\u8bc5\u5492\u7684\uff1f</p><p>\u865a\u5f20\u58f0\u52bf\uff1f\u4f3c\u4e4e\u4e5f\u4e0d\u50cf\u554a\u3002</p><p>\u743c\u6069\u6c89\u541f\u7740\uff0c\u5feb\u901f\u81ea\u6211\u68c0\u67e5\u4e86\u4e00\u904d\uff0c\u5e76\u6ca1\u53d1\u73b0\u4efb\u4f55\u5f02\u6837\u2014\u2014\u4e0d\u8fc7\u8fd9\u4e5f\u4e0d\u80fd\u8bf4\u660e\u4ec0\u4e48\uff0c\u8bc5\u5492\u8fd9\u79cd\u4e1c\u897f\uff0c\u5f88\u591a\u65f6\u5019\u5c31\u662f\u65e0\u5f62\u65e0\u5f71\uff0c\u65e0\u8e2a\u65e0\u8ff9\u7684\uff0c\u53c8\u4e0d\u50cf\u662f\u4e2d\u6bd2\uff0c\u80fd\u591f\u6709\u660e\u663e\u7684\u53cd\u5e94\u3002\u201c\u4ec0\u4e48\u8bc5\u5492\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u662f\u80fd\u591f\u8ba9\u6211\u5bf9\u4f60\u8a00\u542c\u8ba1\u4ece\u5417\uff1f\u201d</p><p>\u201c\u90a3\u79cd\u4e8b\u60c5\u6211\u53ef\u505a\u4e0d\u5230\uff0c\u201d\u7ef4\u82e5\u62c9\u5fae\u5fae\u51b7\u7b11\uff0c\u201c\u4f46\u4f60\u786e\u5b9e\u5df2\u7ecf\u4e2d\u4e86\u6211\u7684\u8bc5\u5492\uff0c\u8fd9\u70b9\u4e0d\u9700\u8981\u6000\u7591\u3002\u201d</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u201c\u5982\u679c\u4f60\u662f\u5728\u5a01\u80c1\u6211\u7684\u8bdd\u2014\u2014\u5e94\u8be5\u7b97\u662f\u5a01\u80c1\u5427\uff0c\u201d\u4ed6\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u90a3\u5c31\u8bf7\u76f4\u63a5\u4eae\u51fa\u5e95\u724c\u597d\u4e86\uff0c\u6211\u53ef\u6ca1\u90a3\u4e2a\u8010\u5fc3\u731c\u6765\u731c\u53bb\u3002\u201d</p><p>\u201c\u90a3\u597d\u5427\uff0c\u201d\u5973\u5deb\u5e08\u8bf4\uff0c\u201c\u8bc5\u5492\u540d\u4e3a\u2018\u4e13\u60c5\u2019\uff0c\u5b83\u7684\u4f5c\u7528\u5f88\u7b80\u5355\uff1a\u4ece\u6b64\u4ee5\u540e\uff0c\u4f60\u65e0\u6cd5\u518d\u548c\u9664\u4e86\u6211\u4e4b\u5916\u7684\u5176\u4ed6\u4efb\u4f55\u5973\u6027\u505a\u7231\u3002\u201d</p><p>\u201c\u2026\u2026\u542c\u8d77\u6765\u786e\u5b9e\u662f\u5f88\u7279\u522b\u7684\u8bc5\u5492\u3002\u201d</p><p>\u201c\u4e0d\u76f8\u4fe1\u662f\u5417\uff1f\u201d\u5973\u5deb\u5e08\u7b11\u4e86\u7b11\uff0c\u201c\u770b\u6765\u8fd8\u662f\u8981\u4eb2\u8eab\u9a8c\u8bc1\u4e00\u4e0b\u624d\u884c\uff0c\u5bf9\u5417\uff0c\u624e\u745e\u5c14\u5c0f\u59d0\uff1f\u201d</p><p>\u5728\u743c\u6069\u8eab\u65c1\uff0c\u706b\u53d1\u84dd\u88d9\u7684\u9b54\u59ec\u7a81\u5140\u5730\u81ea\u7a7a\u6c14\u4e2d\u51fa\u73b0\uff0c\u624b\u4e2d\u4ecd\u7136\u7275\u7740\u90a3\u53ea\u7f8e\u5973\u72ac\u3002\u5979\u76ef\u7740\u5973\u5deb\u5e08\uff0c\u677f\u7740\u8138\uff0c\u5168\u65e0\u534a\u70b9\u7b11\u610f\uff0c\u743c\u6069\u81ea\u4ece\u8ba4\u8bc6\u5979\u8d77\uff0c\u8fd8\u4ece\u672a\u89c1\u8fc7\u8fd9\u79cd\u4e25\u8083\u6a21\u6837\u3002</p><p>\u4e0d\uff0c\u51c6\u786e\u5730\u8bf4\uff0c\u8fd9\u4e0e\u5176\u8bf4\u662f\u4e25\u8083\uff0c\u4e0d\u5982\u8bf4\u662f\u6124\u6012\u3002</p><p>\u743c\u6069\u7684\u5fc3\u4e2d\u4e00\u6c89\uff0c\u4ed6\u539f\u672c\u8fd8\u62b1\u7740\u4fa5\u5e78\u5fc3\u7406\uff0c\u89c9\u5f97\u7ef4\u82e5\u62c9\u6709\u53ef\u80fd\u662f\u5728\u865a\u8a00\u606b\u5413\uff0c\u4f46\u624e\u745e\u5c14\u7684\u6001\u5ea6\u5df2\u7ecf\u8bf4\u660e\u4e86\u4e00\u5207\u3002\u9b54\u59ec\u6709\u770b\u7834\u4eba\u5fc3\u4e4b\u80fd\uff0c\u82e5\u662f\u7ef4\u82e5\u62c9\u5728\u800d\u8bc8\uff0c\u5979\u4e0d\u4f1a\u662f\u8fd9\u79cd\u53cd\u5e94\u3002</p><p>\u201c\u6709\u8da3\uff0c\u201d\u624e\u745e\u5c14\u51b7\u51b7\u5730\u8bf4\uff0c\u201c\u533a\u533a\u4e00\u4e2a\u51e1\u4eba\uff0c\u5c45\u7136\u4e5f\u80fd\u5728\u6211\u7684\u6ce8\u89c6\u4e0b\u6363\u9b3c\uff0c\u770b\u6765\u6211\u8fd8\u662f\u592a\u5c0f\u770b\u4f60\u4e86\u2014\u2014\u4f60\u7a76\u7adf\u662f\u600e\u4e48\u505a\u5230\u7684\uff1f\u201d</p><p>\u7ef4\u82e5\u62c9\u7684\u65bd\u6cd5\u80fd\u529b\u5b8c\u5168\u88ab\u5c01\u5370\uff0c\u8fd9\u70b9\u624e\u745e\u5c14\u786e\u5b9a\u65e0\u7591\uff0c\u90a3\u4e2a\u5c01\u9b54\u624b\u73af\u53ef\u4e0d\u662f\u8d5d\u54c1\uff0c\u4e43\u662f\u8d27\u771f\u4ef7\u5b9e\u7684\u7cbe\u7075\u795e\u5668\uff0c\u800c\u4e14\u8428\u9a6c\u65af\u7279\u5728\u624b\u73af\u4e0a\u53c8\u9644\u52a0\u4e86\u989d\u5916\u7684\u7981\u5236\u3002\u5b9e\u9645\u4e0a\uff0c\u624e\u745e\u5c14\u4e4b\u524d\u867d\u7136\u79bb\u5f00\u4e86\uff0c\u4f46\u5374\u4e00\u76f4\u5173\u6ce8\u7740\u8fd9\u91cc\u6240\u53d1\u751f\u7684\u4e00\u5207\uff0c\u5979\u53ef\u4ee5\u767e\u5206\u4e4b\u767e\u5730\u80af\u5b9a\uff0c\u4ece\u59cb\u81f3\u7ec8\uff0c\u7ef4\u82e5\u62c9\u90fd\u6ca1\u6709\u91ca\u653e\u4efb\u4f55\u4e00\u4e2a\u54ea\u6015\u662f\u6700\u7b80\u5355\u7684\u6cd5\u672f\uff0c\u66f4\u522b\u8bf4\u8bc5\u5492\u4e86\u3002\u6574\u6574\u4e03\u5929\uff0c\u53ea\u6709\u743c\u6069\u91ca\u653e\u4e86\u5341\u4e09\u9053\u4ece\u7ef4\u82e5\u62c9\u90a3\u91cc\u5b66\u6765\u7684\u201c\u7c89\u7ea2\u201d\u6cd5\u672f\uff0c\u9664\u6b64\u4e4b\u5916\u518d\u65e0\u5176\u4ed6\u4eba\u65bd\u6cd5\u3002</p><p>\u96be\u9053\u8bf4\uff0c\u95ee\u9898\u51fa\u5728\u90a3\u4e9b\u7c89\u7ea2\u6cd5\u672f\u4e0a\uff1f\u7ef4\u82e5\u62c9\u5c06\u8bc5\u5492\u85cf\u5728\u5176\u4e2d\u67d0\u9053\u6cd5\u672f\u4e4b\u4e2d\uff0c\u8ba9\u743c\u6069\u5728\u65bd\u5c55\u65f6\u4e0d\u77e5\u4e0d\u89c9\u7740\u4e86\u9053\uff1f</p><p>\u7136\u800c\u743c\u6069\u53c8\u4e0d\u662f\u7b28\u86cb\uff0c\u4ed6\u6bcf\u6b21\u4ece\u7ef4\u82e5\u62c9\u5904\u83b7\u5f97\u201c\u4f20\u9053\u201d\uff0c\u90fd\u4f1a\u4ed4\u4ed4\u7ec6\u7ec6\u5730\u5bf9\u6cd5\u672f\u8fdb\u884c\u53cd\u590d\u68c0\u67e5\uff0c\u786e\u5b9a\u5176\u4e2d\u7edd\u5bf9\u6ca1\u6709\u5305\u542b\u4efb\u4f55\u5bf9\u4ed6\u4e0d\u5229\u7684\u56e0\u7d20\uff0c\u7136\u540e\u4ed6\u624d\u4f1a\u4f7f\u7528\u3002\u5c06\u4e00\u9053\u6cd5\u672f\u85cf\u533f\u5728\u53e6\u4e00\u9053\u6cd5\u672f\u4e4b\u4e2d\uff0c\u8fd9\u662f\u975e\u5e38\u9ad8\u7aef\u7684\u9b54\u6cd5\u6280\u5de7\uff0c\u743c\u6069\u81ea\u5df1\u76ee\u524d\u662f\u505a\u4e0d\u5230\u7684\uff0c\u4f46\u4ed6\u66fe\u7ecf\u6709\u6240\u8033\u95fb\uff0c\u5728\u9634\u9b42\u57ce\u5deb\u5e08\u5b66\u9662\u65f6\u542c\u6559\u6388\u63d0\u53ca\u8fc7\u3002\u7ef4\u82e5\u62c9\u8eab\u4e3a\u4f20\u9053\u5deb\u5e08\uff0c\u9b54\u6cd5\u9020\u8be3\u5e94\u8be5\u6bd4\u4ed6\u9ad8\u660e\uff0c\u5982\u679c\u8bf4\u80fd\u591f\u505a\u5230\u8fd9\u4e00\u70b9\uff0c\u743c\u6069\u5e76\u4e0d\u610f\u5916\u2014\u2014\u4f46\u82e5\u8bf4\u4ed6\u660e\u660e\u5df2\u7ecf\u5fc3\u6000\u8b66\u60d5\uff0c\u8ba4\u771f\u68c0\u67e5\uff0c\u4f9d\u7136\u534a\u70b9\u65e0\u6cd5\u5bdf\u89c9\uff0c\u8fd9\u4e2a\u5c31\u7edd\u65e0\u53ef\u80fd\u3002\u4f5c\u4e3a\u53d7\u8fc7\u4e13\u4e1a\u5b66\u9662\u8bad\u7ec3\u3001\u51dd\u6210\u771f\u540d\u7684\u9ad8\u9636\u5deb\u5e08\uff0c\u743c\u6069\u8fd9\u70b9\u81ea\u4fe1\u8fd8\u662f\u6709\u7684\u3002</p><p>\u201c\u542c\u8bf4\u8fc7\u2018\u6df7\u6bd2\u2019\u5417\uff1f\u201d\u7ef4\u82e5\u62c9\u95ee\u3002</p><p>\u201c\u6df7\u6bd2\uff1f\u201d</p><p>\u743c\u6069\u5bf9\u8fd9\u4e2a\u8bcd\u6709\u70b9\u5370\u8c61\uff0c\u8bb0\u5f97\u662f\u5728\u4e00\u6b21\u95f2\u804a\u4e2d\u542c\u838e\u73de\u514b\u63d0\u5230\u8fc7\uff0c\u8bf4\u662f\u897f\u571f\u5730\u533a\u67d0\u4e2a\u53eb\u201c\u70c8\u7130\u5315\u9996\u201d\u7684\u523a\u5ba2\u7ec4\u7ec7\u6240\u79d8\u4f20\u7684\u6697\u6740\u6280\u5de7\uff0c\u66fe\u7ecf\u7528\u8fd9\u4e00\u62db\u523a\u6740\u4e86\u79d1\u7c73\u5c14\u7684\u56fd\u738b\u4e0e\u738b\u540e\u3002\u94c1\u738b\u5ea7\u4e00\u76f4\u60f3\u8981\u6a21\u4eff\u5077\u5b66\uff0c\u5374\u59cb\u7ec8\u672a\u80fd\u6210\u529f\u3002\u70c8\u7130\u5315\u9996\u7684\u523a\u5ba2\u4eec\u64c5\u957f\u7528\u6bd2\uff0c\u4ed6\u4eec\u80fd\u591f\u5c06\u51e0\u79cd\u539f\u672c\u5b89\u5168\u65e0\u5bb3\u7684\u4e1c\u897f\uff0c\u6309\u4e00\u5b9a\u65b9\u5f0f\u6df7\u5408\u5728\u4e00\u8d77\uff0c\u5c31\u4f1a\u53d8\u6210\u89c1\u8840\u5c01\u5589\u7684\u5267\u6bd2\uff0c\u8fd9\u4fbf\u662f\u6240\u8c13\u7684\u201c\u6df7\u6bd2\u201d\u3002\u7ef4\u82e5\u62c9\u7a81\u7136\u63d0\u8d77\u8fd9\u4e2a\u8bcd\uff0c\u662f\u4ec0\u4e48\u610f\u601d\uff1f</p><p>\u201c\u4f60\u662f\u8bf4\u2026\u2026\u201d\u4ed6\u8138\u8272\u7a81\u53d8\uff0c\u9690\u7ea6\u731c\u5230\u4e86\u51e0\u5206\u3002</p><p>\u201c\u8fd9\u4e9b\u5929\u6765\uff0c\u4f60\u4e00\u5171\u5bf9\u6211\u4f7f\u7528\u4e86\u5341\u4e09\u9053\u6cd5\u672f\uff0c\u201d\u5973\u5deb\u5e08\u8bf4\uff0c\u201c\u5176\u4e2d\u6709\u516b\u9053\u6cd5\u672f\u662f\u6ca1\u6709\u4efb\u4f55\u95ee\u9898\u7684\uff0c\u800c\u53e6\u5916\u4e94\u9053\uff0c\u5219\u522b\u6709\u7384\u673a\u3002\u201d</p><p>\u5c31\u548c\u201c\u6df7\u6bd2\u201d\u7684\u9053\u7406\u4e00\u6837\uff0c\u8fd9\u4e94\u9053\u7c89\u7ea2\u6cd5\u672f\uff0c\u5355\u72ec\u6765\u770b\u90fd\u6ca1\u6709\u4efb\u4f55\u95ee\u9898\uff0c\u767e\u5206\u4e4b\u767e\u5b89\u5168\u65e0\u5bb3\u2014\u2014\u4f46\u5982\u679c\u6309\u7167\u7279\u5b9a\u7684\u65f6\u95f4\u3001\u6b21\u5e8f\u6765\u65bd\u5c55\uff0c\u5c31\u4f1a\u4ea7\u751f\u4ee4\u4eba\u532a\u5937\u6240\u601d\u7684\u7ed3\u679c\uff0c\u8574\u542b\u5728\u5b83\u4eec\u4e4b\u4e2d\u7684\u90a3\u4e9b\u7b26\u6587\u7247\u6bb5\uff0c\u88ab\u5947\u5999\u5730\u7ec4\u5408\u8fde\u7f00\u8d77\u6765\uff0c\u6700\u7ec8\u5f62\u6210\u4e86\u4e00\u9053\u9488\u5bf9\u65bd\u6cd5\u8005\u81ea\u8eab\u7684\u8bc5\u5492\u3002</p><p>\u743c\u6069\u66fe\u7ecf\u542c\u7ef4\u82e5\u62c9\u8bf4\u8fc7\uff0c\u8fd9\u4e9b\u6cd5\u672f\u90fd\u662f\u4e00\u4f4d\u540d\u53eb\u9a6c\u4f26\u7684\u524d\u8f88\u4f20\u9053\u5deb\u5e08\u7684\u9057\u4ea7\u2014\u2014\u8fd9\u7684\u786e\u662f\u4e8b\u5b9e\u6ca1\u9519\uff0c\u4f46\u743c\u6069\u6240\u4e0d\u77e5\u9053\u7684\u662f\uff1a\u5b83\u4eec\u5e76\u4e0d\u5168\u90fd\u662f\u90a3\u4f4d\u4f20\u9053\u5deb\u5e08\u6240\u53d1\u660e\u7684\u3002\u8ba9\u4ed6\u4e2d\u62db\u7684\u8fd9\u4e94\u4e2a\u6cd5\u672f\uff0c\u4ee5\u53ca\u85cf\u533f\u5728\u5176\u4e2d\u7684\u8fd9\u9053\u201c\u4e13\u60c5\u201d\u8bc5\u5492\uff0c\u5b83\u4eec\u7684\u53d1\u660e\u8005\u662f\u4e00\u4f4d\u53eb\u505a\u5a1c\u5854\u4e3d\u838e\u7684\u5973\u5deb\u5e08\uff0c\u9a6c\u4f26\u662f\u4ece\u5979\u90a3\u91cc\u5b66\u6765\u7684\u3002\u5a1c\u5854\u4e3d\u838e\u51fa\u8eab\u540d\u95e8\uff0c\u66fe\u7ecf\u662f\u94f6\u6708\u57ce\u5deb\u5e08\u5b66\u9662\u6700\u88ab\u770b\u597d\u7684\u5e74\u8f7b\u5929\u624d\uff0c\u5076\u7136\u95f4\u4e0e\u9a6c\u4f26\u9082\u9005\uff0c\u5f88\u5feb\u5760\u5165\u7231\u6cb3\uff0c\u7136\u540e\u5206\u624b\uff0c\u6216\u8005\u8bf4\u662f\u88ab\u5206\u624b\u3002\u4e4b\u540e\u5a1c\u5854\u4e3d\u838e\u628a\u81ea\u5df1\u5173\u5728\u56fe\u4e66\u9986\u91cc\uff0c\u8db3\u8db3\u82b1\u8d39\u4e86\u63a5\u8fd1\u5341\u5e74\u7684\u65f6\u95f4\uff0c\u5b8c\u5168\u653e\u5f03\u5176\u4ed6\u4e00\u5207\uff0c\u6f5c\u5fc3\u94bb\u7814\uff0c\u7ec8\u4e8e\u8bbe\u8ba1\u51fa\u8fd9\u4e2a\u7cbe\u5de7\u7684\u9677\u9631\u3002</p><p>\u6240\u4ee5\u8bf4\uff0c\u5931\u604b\u7684\u5973\u4eba\u662f\u5f88\u53ef\u6015\u7684\u3002</p><p>\u5bf9\u4e8e\u4e00\u822c\u7537\u6027\u800c\u8a00\uff0c\u8fd9\u4e2a\u8bc5\u5492\u6216\u8bb8\u8fd8\u4e0d\u7b97\u592a\u4e25\u91cd\uff0c\u6bd5\u7adf\u53c8\u4e0d\u662f\u5f7b\u5e95\u4e0d\u4e3e\uff0c\u522b\u7684\u5973\u4eba\u4e0d\u80fd\u641e\u4e86\uff0c\u773c\u524d\u8fd9\u4f4d\u8fd8\u662f\u53ef\u4ee5\u7684\uff0c\u7ef4\u82e5\u62c9\u53c8\u4e0d\u96be\u770b\u2014\u2014\u5c82\u6b62\u4e0d\u96be\u770b\uff0c\u6839\u672c\u5c31\u662f\u4e00\u4f4d\u96be\u5f97\u7684\u7f8e\u4eba\u3002\u7136\u800c\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u9ebb\u70e6\u5c31\u5f88\u5927\uff0c\u4ed6\u8eab\u8fb9\u4e00\u5806\u5973\u4eba\uff0c\u54ea\u4e2a\u4e0d\u6bd4\u7ef4\u82e5\u62c9\u66f4\u597d\uff0c\u8981\u4ed6\u4ece\u6b64\u6bcf\u5929\u9762\u5bf9\u7740\u6e29\u67d4\u5927\u65b9\u7684\u73ca\u5609\u59d0\u59d0\u3001\u82f1\u6c14\u51db\u51db\u7684\u6885\u83f2\u65af\u3001\u5a07\u4fcf\u53ef\u7231\u7684\u51db\u3001\u72e1\u9ee0\u5371\u9669\u7684\u838e\u73de\u514b\uff0c\u7b49\u7b49\u7b49\u7b49\uff0c\u7b49\u7b49\u7b49\u7b49\uff0c\u5374\u53ea\u80fd\u770b\uff0c\u4e0d\u80fd\u5403\uff0c\u90a3\u8fd8\u4e0d\u5982\u81ea\u6740\u5f97\u4e86\u3002</p><p>\u5f53\u7136\u743c\u6069\u4e0d\u4f1a\u81ea\u6740\uff0c\u4ed6\u8fd8\u6ca1\u89c9\u5f97\u81ea\u5df1\u5df2\u7ecf\u5230\u4e86\u5c71\u7a77\u6c34\u5c3d\u7684\u5730\u6b65\u3002</p><p>\u201c\u6709\u610f\u601d\uff0c\u201d\u4ed6\u6162\u6162\u8bf4\uff0c\u4e8b\u5df2\u81f3\u6b64\uff0c\u60ca\u6012\u65e0\u7528\uff0c\u6c89\u4f4f\u6c14\u624d\u662f\u6b63\u7ecf\uff0c\u201c\u786e\u5b9e\u662f\u6211\u758f\u5ffd\u4e86\u3002\u4f46\u5982\u679c\u4f60\u4ee5\u4e3a\u8fd9\u6837\u5c31\u80fd\u8981\u631f\u5230\u6211\uff0c\u90a3\u53ef\u662f\u5927\u9519\u7279\u9519\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u7684\u8fd9\u4e2a\u8bc5\u5492\uff0c\u6784\u601d\u5de7\u5999\uff0c\u95fb\u6240\u672a\u95fb\uff0c\u786e\u5b9e\u662f\u975e\u5e38\u4e86\u4e0d\u8d77\u3002\u4f46\u6240\u8c13\u6709\u6cd5\u5c31\u6709\u7834\uff0c\u743c\u6069\u597d\u6b79\u4e5f\u662f\u9ad8\u9636\u5deb\u5e08\uff0c\u591a\u82b1\u70b9\u65f6\u95f4\u7422\u78e8\u7814\u7a76\uff0c\u4e0d\u4fe1\u5c31\u89e3\u4e0d\u5f00\u5b83\u3002\u5c31\u7b97\u81ea\u5df1\u6c34\u5e73\u4e0d\u591f\uff0c\u4e0d\u662f\u8fd8\u53ef\u4ee5\u627e\u5e2e\u624b\u4e48\uff0c\u8010\u745f\u5927\u5965\u672f\u5e08\u662f\u6211\u7684\u8001\u5e08\uff0c\u5730\u72f1\u9b54\u59ec\u662f\u6211\u7684\u5973\u53cb\uff0c\u533a\u533a\u4e00\u4e2a\u51e1\u4eba\u7684\u8bc5\u5492\uff0c\u96be\u9053\u5c31\u5f53\u771f\u641e\u4e0d\u5b9a\uff1f\u53cd\u6b63\u8fd9\u4e2a\u8bc5\u5492\u53c8\u4e0d\u4f1a\u6b7b\u4eba\uff0c\u5927\u4e0d\u4e86\u7981\u6b32\u4e00\u6bb5\u65f6\u95f4\u5c31\u662f\uff0c\u8fd9\u70b9\u8010\u6027\u743c\u6069\u8fd8\u662f\u6709\u7684\u3002\u7ef4\u82e5\u62c9\u60f3\u51ed\u8fd9\u4e2a\u5c31\u80fd\u8981\u631f\u743c\u6069\uff0c\u672a\u514d\u5929\u771f\u3002</p><p>\u201c\u4f60\u8bf4\u5f97\u6ca1\u9519\uff0c\u201d\u5973\u5deb\u5e08\u70b9\u70b9\u5934\uff0c\u201c\u8fd9\u4e2a\u8bc5\u5492\u5e76\u975e\u4e0d\u80fd\u7834\u89e3\uff0c\u53ea\u662f\u65f6\u95f4\u95ee\u9898\u2014\u2014\u4f46\u6211\u6709\u6ca1\u6709\u544a\u8bc9\u4f60\uff0c\u90a3\u4f4d\u524d\u8f88\u4f20\u9053\u5deb\u5e08\u662f\u600e\u4e48\u6b7b\u7684\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u8bf4\u81ea\u6740\u4e48\uff1f\u201d</p><p>\u201c\u786e\u5b9e\u662f\u81ea\u6740\u6ca1\u9519\uff0c\u4f46\u6211\u6ca1\u6709\u8bf4\u4ed6\u4e3a\u4ec0\u4e48\u4f1a\u81ea\u6740\u5427\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u8fd8\u8bf7\u6307\u6559\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u5a1c\u5854\u4e3d\u838e\u6b7b\u4e86\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u662f\u81ea\u6740\u3002\u201d</p><p>\u201c\u54e6\uff0c\u6240\u4ee5\u4ed6\u662f\u6b89\u60c5\uff1f\u201d</p><p>\u201c\u4e0d\uff0c\u201d\u7ef4\u82e5\u62c9\u6447\u6447\u5934\uff0c\u201c\u662f\u56e0\u4e3a\u4ed6\u53d1\u73b0\u4ed6\u89e3\u4e0d\u5f00\u8bc5\u5492\uff0c\u201d\u5979\u7b11\u4e86\u7b11\uff0c\u201c\u8fd9\u4e2a\u8bc5\u5492\u786e\u5b9e\u662f\u53ef\u4ee5\u89e3\u7684\uff0c\u4f46\u524d\u63d0\u662f\u4f5c\u4e3a\u2018\u5bf9\u8c61\u2019\u7684\u6211\u6d3b\u7740\u2014\u2014\u5982\u679c\u6211\u6b7b\u4e86\uff0c\u90a3\u4e48\u5b83\u5c31\u88ab\u6253\u4e0a\u6b7b\u7ed3\uff0c\u6c38\u8fdc\u4e5f\u89e3\u4e0d\u5f00\u4e86\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u8bc5\u5492\u662f\u4e00\u79cd\u5f88\u53ef\u6015\u7684\u4e1c\u897f\u3002</p><p>\u5728\u8fd9\u4e2a\u5947\u5e7b\u4e16\u754c\uff0c\u9b54\u6cd5\u5e76\u4e0d\u662f\u4ec0\u4e48\u7279\u522b\u7f55\u89c1\u4e4b\u7269\uff0c\u4f46\u5bf9\u4e8e\u5f88\u591a\u2014\u2014\u6216\u8005\u8bf4\u7edd\u5927\u591a\u6570\u2014\u2014\u5192\u9669\u8005\u800c\u8a00\uff0c\u4ed6\u4eec\u5b81\u613f\u9762\u5bf9\u4e00\u4e2a\u5deb\u5e08\u4ece\u6307\u95f4\u8ff8\u51fa\u706b\u7130\u95ea\u7535\uff0c\u4e5f\u4e0d\u5e0c\u671b\u88ab\u4e0b\u4e00\u4e2a\u8bc5\u5492\uff0c\u524d\u8005\u867d\u7136\u5371\u9669\uff0c\u7ec8\u7a76\u8fd8\u662f\u6709\u5f62\u6709\u5f71\uff0c\u6709\u8ff9\u53ef\u5faa\u7684\uff0c\u81f3\u5c11\u7b97\u662f\u5728\u201c\u5e38\u8bc6\u201d\u4e4b\u5185\uff0c\u540e\u8005\u5219\u5f80\u5f80\u6084\u65e0\u58f0\u606f\uff0c\u8be1\u79d8\u83ab\u6d4b\uff0c\u8d85\u8d8a\u903b\u8f91\u548c\u60f3\u8c61\u3002\u201c\u771f\u6b63\u4ee4\u4eba\u6050\u60e7\u7684\u4e0d\u662f\u5996\u9b54\uff0c\u800c\u662f\u672a\u77e5\u201d\uff0c\u8fd9\u4e2a\u9053\u7406\u4e0d\u8bba\u653e\u5728\u54ea\u91cc\u90fd\u662f\u6b63\u786e\u7684\u3002</p><p>\u800c\u5728\u8bc5\u5492\u4e4b\u4e2d\uff0c\u6700\u5371\u9669\u7684\u5c31\u662f\u201c\u6b7b\u5492\u201d\u3002</p><p>\u6240\u8c13\u6b7b\u5492\uff0c\u5176\u5b9e\u5e76\u6ca1\u6709\u4e00\u4e2a\u5341\u5206\u660e\u786e\u7684\u5b9a\u4e49\uff0c\u66f4\u591a\u662f\u4e00\u79cd\u7ea6\u5b9a\u4fd7\u6210\u7684\u8bf4\u6cd5\uff0c\u5982\u679c\u975e\u8981\u52c9\u5f3a\u63cf\u8ff0\u7684\u8bdd\uff0c\u5c31\u662f\u201c\u4ee5\u91cd\u5927\u7684\u81ea\u613f\u727a\u7272\uff08\u901a\u5e38\u662f\u6b7b\u4ea1\uff09\u4e3a\u4ee3\u4ef7\u6240\u65bd\u7684\u8bc5\u5492\u201d\u3002\u6bd4\u5982\u8bf4\uff0c\u4e00\u540d\u5deb\u5e08\u4ee5\u81ea\u5df1\u7684\u751f\u547d\u505a\u4ee3\u4ef7\u6765\u8bc5\u5492\u4ed6\u4eba\uff0c\u8fd9\u5c31\u662f\u6b7b\u5492\uff1b\u53c8\u6bd4\u5982\u8bf4\uff0c\u67d0\u4eba\u81ea\u613f\u4ee5\u81ea\u5df1\u4f5c\u4e3a\u796d\u54c1\uff0c\u6362\u53d6\u90aa\u9b54\u5bf9\u4ec7\u654c\u65bd\u52a0\u8bc5\u5492\uff0c\u8fd9\u4e5f\u662f\u6b7b\u5492\uff1b\u518d\u6bd4\u5982\u6885\u83f2\u65af\u8eab\u4e0a\u7684\u8bc5\u5492\uff0c\u662f\u5176\u6bcd\u4eb2\u5728\u4e34\u7ec8\u524d\u4ee5\u7075\u9b42\u6e6e\u706d\u505a\u4ee3\u4ef7\u6240\u4e0b\uff0c\u4e5f\u5c5e\u4e8e\u6b7b\u5492\u3002\u901a\u5e38\u6765\u8bf4\uff0c\u7531\u4e8e\u8fd9\u79cd\u81ea\u6211\u727a\u7272\uff0c\u8bc5\u5492\u7684\u529b\u91cf\u4f1a\u88ab\u6781\u5927\u5730\u5f3a\u5316\uff0c\u4ece\u800c\u8ba9\u4eba\u5f88\u96be\u5bf9\u6297\u3001\u7834\u89e3\u3002\u6885\u83f2\u65af\u6240\u4e2d\u7684\u8bc5\u5492\uff0c\u8fde\u63d0\u5c14\u8fd9\u79cd\u9ad8\u7b49\u795e\u90fd\u675f\u624b\u65e0\u7b56\uff0c\u53ea\u80fd\u5f3a\u884c\u538b\u5236\uff0c\u65e0\u6cd5\u6d88\u9664\u3002</p><p>\u743c\u6069\u73b0\u5728\u4e2d\u7684\u8fd9\u4e2a\u8bc5\u5492\uff0c\u4ece\u67d0\u79cd\u610f\u4e49\u4e0a\u8bf4\uff0c\u5f88\u50cf\u662f\u4e00\u4e2a\u6b7b\u5492\u3002\u53d1\u660e\u8fd9\u4e2a\u8bc5\u5492\u7684\u5973\u5deb\u5e08\u5a1c\u5854\u4e3d\u838e\uff0c\u4e3a\u4e86\u62a5\u590d\u60c5\u4eba\uff0c\u5904\u5fc3\u79ef\u8651\u8ba9\u4ed6\u4e2d\u62db\uff0c\u7136\u540e\u81ea\u6740\u8eab\u4ea1\uff0c\u5bf9\u65b9\u4f5c\u4e3a\u4f20\u9053\u5deb\u5e08\uff0c\u53c8\u80fd\u501f\u52a9\u5386\u4ee3\u5148\u8f88\u7684\u77e5\u8bc6\u4e0e\u7ecf\u9a8c\uff0c\u6700\u540e\u5c45\u7136\u8fd8\u662f\u89e3\u4e0d\u5f00\u8bc5\u5492\uff0c\u5176\u201c\u5f3a\u5ea6\u201d\u53ef\u60f3\u800c\u77e5\u3002\u5f53\u7136\u4e86\uff0c\u7ef4\u82e5\u62c9\u5e94\u8be5\u6ca1\u6709\u81ea\u6740\u7684\u6253\u7b97\uff0c\u4f46\u5979\u82e5\u662f\u88ab\u8428\u9a6c\u65af\u7279\u6740\u4e86\uff0c\u4f1a\u4e0d\u4f1a\u5bf9\u8bc5\u5492\u9020\u6210\u540c\u6837\u7684\u5f71\u54cd\uff0c\u8fd9\u4e2a\u743c\u6069\u5c31\u4e0d\u6562\u786e\u5b9a\u4e86\u3002</p><p>\u600e\u4e48\u529e\uff1f</p><p>\u743c\u6069\u5de6\u601d\u53f3\u60f3\uff0c\u8e0c\u8e87\u4e0d\u51b3\u3002\u597d\u5728\u4ed6\u4ece\u6765\u5c31\u4e0d\u662f\u72ec\u65ad\u4e13\u884c\u7684\u4eba\uff0c\u65e2\u7136\u81ea\u5df1\u62ff\u4e0d\u5b9a\u4e3b\u610f\uff0c\u90a3\u5c31\u53bb\u542c\u542c\u522b\u4eba\u7684\u610f\u89c1\uff0c\u5f80\u5e38\u8fd9\u79cd\u65f6\u5019\uff0c\u4ed6\u4f1a\u53bb\u54a8\u8be2\u8eab\u8fb9\u7684\u53c2\u8c0b\uff0c\u6bd4\u5982\u6885\u83f2\u65af\u3002\u5982\u4eca\u6885\u83f2\u65af\u867d\u7136\u4e0d\u5728\u8eab\u8fb9\uff0c\u4f46\u8fd8\u6709\u624e\u745e\u5c14\u5462\u3002</p><p>\u9b54\u59ec\u7684\u8138\u8272\u5f88\u96be\u770b\uff0c\u5bf9\u6b64\u743c\u6069\u5b8c\u5168\u80fd\u591f\u7406\u89e3\uff0c\u4e0d\u7ba1\u54ea\u4e2a\u5973\u4eba\uff0c\u542c\u5230\u81ea\u5df1\u7537\u53cb\u88ab\u53e6\u4e00\u4e2a\u5973\u4eba\u8bc5\u5492\u6210\u4e0d\u4e3e\uff0c\u60f3\u5fc5\u5fc3\u60c5\u90fd\u4e0d\u4f1a\u6109\u5feb\u5230\u54ea\u91cc\u53bb\uff0c\u6bd5\u7adf\u8fd9\u4e8b\u5173\u81ea\u8eab\u7684\u6027\u798f\u3002\u5012\u662f\u5979\u7275\u6765\u7684\u90a3\u53ea\u7f8e\u5973\u72ac\uff0c\u4e00\u76f4\u4e56\u4e56\u5730\u8db4\u4f0f\u5728\u811a\u8fb9\uff0c\u4e0d\u8a00\u4e0d\u52a8\uff0c\u8ba9\u743c\u6069\u9887\u4e3a\u597d\u5947\uff0c\u4e0d\u77e5\u9053\u624e\u745e\u5c14\u4ece\u54ea\u5f04\u6765\u7684\u2014\u2014\u8bdd\u8bf4\u7f8e\u5973\u72ac\u8fd9\u79cd\u4e1c\u897f\uff0c\u4ed6\u53ea\u6709\u8033\u95fb\uff0c\u4ece\u672a\u76ee\u7779\uff0c\u4eca\u5929\u8fd8\u662f\u7b2c\u4e00\u6b21\u89c1\u5230\u6d3b\u751f\u751f\u7684\u771f\u4eba\u7248\u672c\u5462\u3002</p><p>\u201c\u8bd5\u8bd5\u770b\u3002\u201d\u6c89\u541f\u4e86\u534a\u664c\uff0c\u9b54\u59ec\u6700\u540e\u8bf4\uff0c\u5728\u90a3\u53ea\u7f8e\u5973\u72ac\u7684\u8111\u888b\u4e0a\u8f7b\u8f7b\u62cd\u4e86\u4e00\u4e0b\u3002</p><p>\u7f8e\u5973\u72ac\u8d76\u5fd9\u722c\u884c\u5230\u743c\u6069\u9762\u524d\uff0c\u4e0b\u8eab\u4ecd\u7136\u4fdd\u6301\u7740\u8dea\u59ff\uff0c\u4e0a\u8eab\u633a\u76f4\uff0c\u62ac\u8d77\u8138\u6765\u3002\u743c\u6069\u4f4e\u5934\u770b\u53bb\uff0c\u4e0d\u7531\u5f97\u6709\u4e9b\u60ca\u8bb6\uff0c\u6ca1\u60f3\u5230\u5bf9\u65b9\u5c45\u7136\u8fd8\u662f\u4e2a\u719f\u4eba\u3002\u201c\u838e\u7433\u5a1c\u5c0f\u59d0\uff1f\u201d</p><p>\u624e\u745e\u5c14\u7275\u6765\u7684\u8fd9\u53ea\u7f8e\u5973\u72ac\uff0c\u6b63\u662f\u4e4b\u524d\u5728\u5854\u745f\u8c37\u7684\u90a3\u4f4d\u76f2\u5973\u838e\u7433\u5a1c\uff0c\u6700\u65b0\u4e00\u5c4a\u661f\u4e4b\u82b1\u7684\u51a0\u519b\u3002\u5979\u51fa\u8eab\u4e8e\u5854\u745f\u8c37\u7684\u540d\u95e8\u514b\u91cc\u65af\u591a\u592b\u5bb6\u65cf\uff0c\u7236\u4eb2\u88ab\u90aa\u9b54\u5f15\u8bf1\uff0c\u5f04\u5f97\u5979\u53cc\u76ee\u5931\u660e\uff0c\u540e\u6765\u53c8\u88ab\u838e\u5c14\u5192\u5145\u624e\u745e\u5c14\u6240\u6b3a\u9a97\uff0c\u5e26\u7740\u743c\u6069\u8fdb\u5165\u5c01\u7075\u5854\u6551\u51fa\u4e86\u624e\u745e\u5c14\uff0c\u7ed3\u679c\u88ab\u4ece\u5c01\u5370\u4e2d\u82cf\u9192\u8fc7\u6765\u7684\u9b54\u59ec\u76f4\u63a5\u541e\u566c\u4e86\u3002\u603b\u800c\u8a00\u4e4b\uff0c\u5979\u7684\u4eba\u751f\u5c31\u662f\u4e2a\u60b2\u5267\u3002\u743c\u6069\u672c\u4ee5\u4e3a\u5979\u65e9\u5df2\u7ecf\u7070\u98de\u70df\u706d\uff0c\u4e5f\u61d2\u5f97\u5173\u5fc3\uff0c\u53cd\u6b63\u5927\u5bb6\u53c8\u4e0d\u719f\uff0c\u5374\u6ca1\u60f3\u5230\u5728\u8fd9\u91cc\u53c8\u89c1\u5230\u4e86\uff0c\u8fd8\u88ab\u624e\u745e\u5c14\u8c03\u6559\u6210\u4e86\u7f8e\u5973\u72ac\u3002\u4e0d\u8fc7\u5979\u7684\u773c\u775b\u4f3c\u4e4e\u88ab\u6cbb\u597d\u4e86\uff0c\u5c31\u662f\u6ca1\u6709\u77b3\u5b54\uff0c\u770b\u8d77\u6765\u6709\u4e9b\u8be1\u5f02\u3002</p><p>\u201c\u662f\u7684\uff0c\u4e3b\u4eba\uff0c\u201d\u7f8e\u5973\u72ac\u56de\u7b54\uff0c\u201c\u6211\u662f\u838e\u7433\u5a1c\uff0c\u60a8\u5fe0\u5b9e\u7684\u5973\u5974\u3002\u80fd\u591f\u4f8d\u5949\u60a8\u662f\u6211\u7684\u8363\u5e78\u3002\u201d</p><p>\u201c\u2026\u2026\u201d\u743c\u6069\u770b\u5411\u624e\u745e\u5c14\u3002</p><p>\u201c\u4e00\u4e2a\u73a9\u5177\uff0c\u201d\u9b54\u59ec\u89e3\u91ca\uff0c\u201c\u6b63\u597d\u624b\u5934\u4e0a\u6709\u7d20\u6750\uff0c\u5c31\u505a\u4e86\u4e00\u4e2a\uff0c\u4f60\u5148\u8bd5\u7528\u4e00\u6bb5\u65f6\u95f4\uff0c\u6709\u95ee\u9898\u6211\u518d\u4fee\u6539\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u743c\u6069\u5012\u4e5f\u6ca1\u600e\u4e48\u5728\u610f\uff0c\u53cd\u6b63\u4ed6\u6700\u8fd1\u7ecf\u5e38\u88ab\u4eba\u9001\u793c\uff0c\u62c6\u5305\u88f9\u5df2\u7ecf\u62c6\u4e60\u60ef\u4e86\u3002</p><p>\u5f53\u624e\u745e\u5c14\u66ff\u6362\u4e0b\u838e\u7433\u5a1c\uff0c\u4eb2\u81ea\u4e0a\u9635\uff0c\u5374\u4ecd\u7136\u672a\u80fd\u8ba9\u743c\u6069\u518d\u632f\u96c4\u98ce\u4e4b\u540e\uff0c\u9b54\u59ec\u4e5f\u4e0d\u5f97\u4e0d\u6682\u65f6\u5ba3\u544a\u653e\u5f03\u3002</p><p>\u201c\u51ef\u745f\u7433\u4f1a\u4e0d\u4f1a\u6709\u529e\u6cd5\uff1f\u201d\u743c\u6069\u62b1\u7740\u4e00\u4e1d\u671f\u671b\u95ee\u3002</p><p>\u624e\u745e\u5c14\u6447\u6447\u5934\uff0c\u201c\u59d0\u59d0\u5e76\u4e0d\u64c5\u957f\u89e3\u5492\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\u3002\u201d</p><p>\u743c\u6069\u76b1\u7709\u6c89\u601d\u7740\uff0c\u624e\u745e\u5c14\u9759\u6084\u6084\u5730\u5f85\u5728\u65c1\u8fb9\uff0c\u5e76\u672a\u6253\u6270\u3002\u6c14\u6c1b\u4e00\u65f6\u6709\u4e9b\u6c89\u95f7\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u743c\u6069\u6162\u6162\u62ac\u8d77\u5934\u6765\uff0c\u770b\u7740\u5973\u5deb\u5e08\u3002</p><p>\u201c\u4f60\u6709\u4ec0\u4e48\u6761\u4ef6\uff0c\u201d\u4ed6\u7684\u8bed\u6c14\u5e73\u9759\uff0c\u201c\u8bf4\u6765\u542c\u542c\u3002\u201d</p><p>\u201c\u6761\u4ef6\u5f88\u7b80\u5355\uff0c\u201d\u5973\u5deb\u5e08\u8bf4\uff0c\u201c\u4f60\u6551\u6211\uff0c\u6211\u5c31\u5e2e\u4f60\u89e3\u5f00\u8bc5\u5492\u3002\u201d</p><p>\u201c\u4f46\u4f60\u4ece\u4f55\u5f97\u77e5\u89e3\u5492\u7684\u65b9\u6cd5\uff1f\u201d\u743c\u6069\u8d28\u7591\uff0c\u201c\u90a3\u4e2a\u4f20\u9053\u5deb\u5e08\u4e0d\u662f\u5230\u6b7b\u90fd\u6ca1\u89e3\u5f00\u5417\uff1f\u201d</p><p>\u901a\u5e38\u6765\u8bf4\uff0c\u65bd\u5492\u8005\u5f80\u5f80\u80fd\u591f\u89e3\u5492\uff0c\u6bd5\u7adf\u662f\u81ea\u5df1\u5f04\u51fa\u6765\u7684\u4e1c\u897f\u3002\u7136\u800c\u636e\u7ef4\u82e5\u62c9\u6240\u8a00\uff0c\u8fd9\u4e2a\u8bc5\u5492\u7684\u53d1\u660e\u8005\u5e76\u975e\u90a3\u4f4d\u540d\u4e3a\u9a6c\u4f26\u7684\u524d\u8f88\u4f20\u9053\u5deb\u5e08\uff0c\u5176\u5b9e\u662f\u4ed6\u7684\u60c5\u4eba\u2014\u2014\u800c\u4ed6\u7684\u60c5\u4eba\u663e\u7136\u6ca1\u6709\u7559\u4e0b\u89e3\u5492\u4e4b\u6cd5\uff0c\u81f3\u5c11\u6ca1\u6709\u544a\u8bc9\u9a6c\u4f26\u3002\u7ef4\u82e5\u62c9\u7684\u76f8\u5173\u77e5\u8bc6\u6765\u81ea\u4e8e\u9a6c\u4f26\uff0c\u4f46\u9a6c\u4f26\u5374\u662f\u5230\u6b7b\u90fd\u6ca1\u80fd\u6210\u529f\u89e3\u5492\uff0c\u6700\u7ec8\u7edd\u671b\u81ea\u6740\u3002\u65e2\u7136\u5982\u6b64\uff0c\u5979\u7684\u89e3\u5492\u4e4b\u6cd5\u4ece\u4f55\u800c\u6765\uff1f</p><p>\u201c\u81ea\u7136\u662f\u9a6c\u4f26\u7559\u4e0b\u6765\u7684\u3002\u201d</p><p>\u89c1\u743c\u6069\u4f3c\u4e4e\u4e0d\u4fe1\uff0c\u7ef4\u82e5\u62c9\u7b80\u5355\u89e3\u91ca\u4e86\u51e0\u53e5\u3002\u539f\u6765\u9a6c\u4f26\u81ea\u4ece\u4e2d\u4e86\u8bc5\u5492\uff0c\u81ea\u7136\u662f\u5e9f\u5bdd\u5fd8\u98df\uff0c\u5fc3\u65e0\u65c1\u9a9b\uff0c\u4e13\u5fc3\u81f4\u5fd7\u5730\u60f3\u8981\u89e3\u5492\u3002\u4ed6\u6bd5\u7adf\u662f\u5deb\u5e08\u4e2d\u7684\u7cbe\u82f1\u4eba\u7269\uff0c\u7a77\u5c3d\u591a\u5e74\u4e4b\u529f\uff0c\u786c\u751f\u751f\u5730\u4ece\u7ed3\u679c\u9006\u63a8\u539f\u56e0\uff0c\u5c06\u6574\u4e2a\u8bc5\u5492\u6cd5\u672f\u90fd\u8fd8\u539f\u51fa\u6765\uff0c\u800c\u4e14\u4e5f\u627e\u51fa\u4e86\u7834\u89e3\u65b9\u6cd5\u3002\u552f\u4e00\u7684\u95ee\u9898\u662f\uff0c\u89e3\u5492\u4e4b\u6cd5\u6709\u4e00\u4e2a\u7ed5\u4e0d\u8fc7\u53bb\u7684\u73af\u8282\uff0c\u9700\u8981\u6709\u4e13\u60c5\u7684\u201c\u5bf9\u8c61\u201d\u7684\u53c2\u4e0e\uff0c\u800c\u5979\u5df2\u7ecf\u6b7b\u4e86\u3002\u53d7\u6b64\u6253\u51fb\uff0c\u9a6c\u4f26\u610f\u5fd7\u6d88\u6c89\uff0c\u6700\u7ec8\u9009\u62e9\u4e86\u81ea\u6740\u8eab\u4ea1\u3002</p><p>\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u8428\u9a6c\u65af\u7279\u5e76\u4e0d\u5bb9\u6613\u5bf9\u4ed8\u3002\u201d</p><p>\u201c\u6211\u5e76\u6ca1\u6709\u8981\u6c42\u4f60\u53bb\u5bf9\u6297\u4ed6\uff0c\u201d\u5973\u5deb\u5e08\u8bf4\uff0c\u201c\u4f60\u53ea\u8981\u80fd\u5e26\u6211\u79bb\u5f00\u8fd9\u91cc\u56de\u5230\u9634\u5f71\u9547\u5c31\u53ef\u4ee5\u4e86\u3002\u4f60\u6709\u5927\u9b54\u9b3c\u5e2e\u52a9\uff0c\u5728\u8fd9\u4e2a\u7c7b\u4f3c\u5730\u72f1\u7684\u73af\u5883\u4e2d\u5f88\u6709\u4f18\u52bf\uff0c\u800c\u4e14\u90a3\u4f4d\u51ef\u745f\u7433\u5c0f\u59d0\u4e0d\u662f\u4e0e\u4f60\u5173\u7cfb\u532a\u6d45\u5417\uff1f\u5982\u679c\u80fd\u8bf7\u5230\u5979\u76f8\u52a9\uff0c\u4e00\u5207\u5c31\u66f4\u4e0d\u6210\u95ee\u9898\u4e86\u5427\u3002\u201d</p><p>\u201c\u6216\u8bb8\u3002\u201d</p><p>\u8fd9\u6bb5\u65f6\u95f4\u4e24\u4eba\u9664\u4e86\u505a\u7231\uff0c\u5076\u5c14\u4e5f\u4f1a\u95f2\u8c08\u51e0\u53e5\uff0c\u7ef4\u82e5\u62c9\u542c\u743c\u6069\u63d0\u5230\u8fc7\u51ef\u745f\u7433\uff0c\u80fd\u591f\u63a8\u65ad\u51fa\u4e24\u4eba\u7684\u5173\u7cfb\u5bc6\u5207\u3002\u743c\u6069\u627f\u8ba4\u5979\u6240\u8bf4\u4e0d\u65e0\u9053\u7406\uff0c\u6709\u624e\u745e\u5c14\u76f8\u52a9\uff0c\u5982\u679c\u53c8\u80fd\u8bf4\u52a8\u51ef\u745f\u7433\u5012\u6208\uff0c\u8981\u628a\u7ef4\u82e5\u62c9\u6551\u56de\u9634\u5f71\u9547\uff0c\u5e76\u975e\u6ca1\u6709\u53ef\u80fd\u3002\u95ee\u9898\u53ea\u5728\u4e8e\uff1a\u4ed6\u4e0d\u613f\u610f\u8fd9\u4e48\u505a\u3002</p><p>\u201c\u4f46\u662f\u6709\u4e00\u4e2a\u95ee\u9898\uff1a\u6211\u5148\u8981\u6551\u4f60\uff0c\u7136\u540e\u4f60\u624d\u4f1a\u5e2e\u6211\u89e3\u9664\u8bc5\u5492\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u5f53\u7136\u3002\u201d</p><p>\u201c\u90a3\u4e48\u6211\u5982\u4f55\u4fdd\u8bc1\u4f60\u4f1a\u9075\u5b88\u534f\u8bae\uff1f\u201d</p><p>\u201c\u8fd9\u662f\u4e2a\u597d\u95ee\u9898\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u6216\u8bb8\u4f60\u53ef\u4ee5\u4fe1\u4efb\u6211\uff1f\u201d</p><p>\u201c\u4ece\u59cb\u81f3\u7ec8\uff0c\u6211\u4eec\u4e4b\u95f4\u6709\u4fe1\u4efb\u53ef\u8a00\u5417\uff1f\u201d</p><p>\u201c\u90a3\u4e48\u4f60\u7684\u610f\u601d\u5462\uff1f\u201d</p><p>\u201c\u7b7e\u7ea6\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u7a7a\u8a00\u65e0\u51ed\uff0c\u5951\u7ea6\u4e3a\u636e\u3002\u201d</p><p>\u201c\u5951\u7ea6\uff1f\u201d\u5973\u5deb\u5e08\u54d1\u7136\u5931\u7b11\uff0c\u201c\u4f60\u5c45\u7136\u4f1a\u76f8\u4fe1\u8fd9\u79cd\u4e1c\u897f\uff1f\u201d</p><p>\u743c\u6069\u56de\u62a5\u4ee5\u5fae\u7b11\uff0c\u201c\u901a\u5e38\u6211\u662f\u4e0d\u4fe1\u7684\uff0c\u4f46\u4f60\u4f3c\u4e4e\u5fd8\u4e86\u4e00\u4ef6\u4e8b\uff0c\u7ef4\u82e5\u62c9\u5c0f\u59d0\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6b64\u523b\u8fd9\u91cc\u662f\u5730\u72f1\uff0c\u800c\u6211\u7684\u8eab\u8fb9\uff0c\u6b63\u6709\u4e00\u4f4d\u5927\u9b54\u9b3c\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u9b54\u9b3c\uff0c\u53c8\u540d\u5df4\u7279\u5179\u9b54\uff0c\u5c45\u4f4f\u5728\u4e5d\u5c42\u5730\u72f1\uff0c\u4e0e\u6df1\u6e0a\u4e2d\u7684\u6076\u9b54\uff08\u53c8\u540d\u5854\u7eb3\u5398\u9b54\uff09\u662f\u6b7b\u5bf9\u5934\uff0c\u5176\u79cd\u7c7b\u7e41\u591a\uff0c\u6ce8\u91cd\u79e9\u5e8f\u89c4\u5219\uff0c\u64c5\u957f\u6587\u5b57\u6e38\u620f\uff0c\u559c\u6b22\u901a\u8fc7\u7b7e\u8ba2\u5951\u7ea6\u7684\u65b9\u5f0f\u5f15\u8bf1\u51e1\u4eba\u5815\u843d\uff0c\u83b7\u53d6\u5176\u7075\u9b42\u3002\u4ed6\u4eec\u53d1\u660e\u4e86\u4e00\u79cd\u201c\u7075\u9b42\u5951\u7ea6\u201d\uff0c\u501f\u52a9\u5b87\u5b99\u4e2d\u6700\u672c\u6e90\u7684\u79e9\u5e8f\u529b\u91cf\u800c\u5f62\u6210\uff0c\u4e3b\u8981\u5185\u5bb9\u662f\u9b54\u9b3c\u63d0\u4f9b\u5404\u79cd\u670d\u52a1\uff0c\u6362\u53d6\u51e1\u4eba\u7684\u7075\u9b42\u3002\u7075\u9b42\u5951\u7ea6\u4e00\u65e6\u6210\u7acb\uff0c\u5c31\u662f\u81ea\u7136\u751f\u6548\u7684\uff0c\u4efb\u4f55\u4eba\u90fd\u65e0\u6cd5\u63a8\u7ffb\uff0c\u53ef\u8c13\u662f\u8bda\u5b9e\u5b88\u4fe1\u7684\u5178\u8303\u3002\u7269\u8d28\u754c\u7684\u5deb\u5e08\u4eec\u4ee5\u9b54\u9b3c\u7684\u7075\u9b42\u5951\u7ea6\u4e3a\u84dd\u672c\uff0c\u4e5f\u53d1\u660e\u51fa\u4e86\u4e0d\u5c11\u53d8\u79cd\uff0c\u6bd4\u5982\u9b54\u5ba0\u5951\u7ea6\u3001\u771f\u540d\u5951\u7ea6\uff0c\u7b49\u7b49\u7b49\u7b49\uff0c\u4e0d\u4e00\u800c\u8db3\u3002</p><p>\u611a\u8822\u7684\u51e1\u4eba\u4e0e\u9b54\u9b3c\u7b7e\u7ea6\uff0c\u5984\u56fe\u501f\u52a9\u90aa\u6cd5\u6765\u5b9e\u73b0\u4e0d\u5207\u5b9e\u9645\u7684\u613f\u671b\uff0c\u6700\u7ec8\u4ed8\u51fa\u4e86\u81ea\u5df1\u7684\u7075\u9b42\u2014\u2014\u7c7b\u4f3c\u8fd9\u79cd\u4f20\u8bf4\u6545\u4e8b\uff0c\u5728\u51e1\u95f4\u4e5f\u7b97\u662f\u9887\u4e3a\u6d41\u884c\u3002\u9b54\u9b3c\u4e5f\u7d20\u6765\u4ee5\u201c\u8be1\u8bc8\u7684\u5951\u7ea6\u8005\u201d\u7684\u5f62\u8c61\u800c\u95fb\u540d\uff0c\u4f46\u5f88\u5c11\u6709\u4eba\u77e5\u9053\u7684\u662f\uff1a\u9b54\u9b3c\u4e0d\u4ec5\u4ec5\u53ef\u4ee5\u662f\u5951\u7ea6\u7684\u201c\u5f53\u4e8b\u4eba\u201d\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u4ed6\u4eec\u8fd8\u80fd\u62c5\u4efb\u53e6\u5916\u4e00\u79cd\u89d2\u8272\uff0c\u5373\u662f\u5951\u7ea6\u7684\u201c\u89c1\u8bc1\u8005\u201d\u3002</p><p>\u7269\u8d28\u754c\u4e0e\u5404\u5927\u5916\u5c42\u4f4d\u9762\u6700\u5927\u7684\u4e0d\u540c\u5c31\u5728\u4e8e\uff1a\u5404\u5927\u4f4d\u9762\u90fd\u662f\u6982\u5ff5\u5316\u3001\u610f\u8bc6\u5316\u7684\uff0c\u90fd\u6709\u5176\u201c\u6cd5\u5219\u201d\uff0c\u800c\u4e14\u8fd9\u79cd\u6cd5\u5219\u662f\u771f\u5b9e\u4e0d\u865a\uff0c\u65e0\u5904\u4e0d\u5728\u7684\uff0c\u7c7b\u4f3c\u4e8e\u795e\u8bdd\u4e2d\u6240\u8c13\u7684\u201c\u5929\u6761\u201d\u3001\u201c\u5929\u9053\u201d\u3002\u4e3e\u4f8b\u6765\u8bf4\uff0c\u65e0\u5c3d\u6df1\u6e0a\u7684\u9996\u8981\u6cd5\u5219\u662f\u201c\u90aa\u6076\u201d\u4e0e\u201c\u6df7\u4e71\u201d\uff0c\u50cf\u6885\u83f2\u65af\u4e00\u65e6\u8fdb\u5165\u6df1\u6e0a\uff0c\u5373\u4f1a\u88ab\u4f4d\u9762\u5224\u5b9a\u4e3a\u201c\u79e9\u5e8f\u201d\u3001\u201c\u5584\u826f\u201d\uff0c\u4ece\u800c\u53d7\u5230\u5f3a\u70c8\u7684\u9488\u5bf9\u538b\u5236\u3002\u800c\u7269\u8d28\u754c\u5219\u4e0d\u540c\uff0c\u5b83\u662f\u7269\u8d28\u5316\u7684\uff0c\u53cd\u62bd\u8c61\uff0c\u53cd\u6982\u5ff5\uff0c\u6df7\u6c8c\u6742\u7cc5\uff0c\u5305\u5bb9\u4e07\u8c61\uff0c\u5b8c\u5168\u6ca1\u6709\u6240\u8c13\u7684\u201c\u6cd5\u5219\u201d\u53ef\u8a00\uff0c\u65e0\u8bba\u4f60\u662f\u5584\u826f\u8fd8\u662f\u90aa\u6076\uff0c\u6df7\u4e71\u6216\u8005\u5b88\u5e8f\uff0c\u9ad8\u5c1a\u6216\u8005\u5351\u9119\uff0c\u5149\u660e\u6216\u8005\u7325\u7410\uff0c\u90fd\u53ef\u4ee5\u5728\u7269\u8d28\u754c\u81ea\u7531\u81ea\u5728\u5730\u751f\u6d3b\u3002</p><p>\u540c\u6837\uff0c\u751f\u6d3b\u5728\u5404\u5927\u5916\u5c42\u4f4d\u9762\u7684\u5f02\u754c\u751f\u7269\u4eec\uff0c\u5927\u591a\u4e5f\u90fd\u662f\u6982\u5ff5\u5316\u3001\u610f\u8bc6\u5316\u7684\uff0c\u5f7c\u6b64\u4e4b\u95f4\u7684\u4e00\u4e3e\u4e00\u52a8\uff0c\u4ea4\u6d89\u5f80\u6765\uff0c\u90fd\u4f1a\u53d7\u5230\u4f4d\u9762\u6cd5\u5219\u7684\u7ea6\u675f\uff0c\u800c\u7269\u8d28\u754c\u7684\u51e1\u4eba\u5c31\u65e0\u6b64\u987e\u8651\uff0c\u4ed6\u4eec\u6240\u7b7e\u8ba2\u7684\u5951\u7ea6\uff0c\u6ca1\u6709\u4efb\u4f55\u5929\u7136\u7684\u5f3a\u5236\u7ea6\u675f\u529b\uff0c\u53cc\u65b9\u662f\u5426\u4f1a\u8bda\u5b9e\u9075\u5b88\uff0c\u5b8c\u5168\u4f9d\u8d56\u4e8e\u81ea\u89c9\u81ea\u613f\uff0c\u6216\u8005\u5916\u6765\u7684\u5f3a\u5236\u529b\uff0c\u6bd4\u5982\u6cd5\u9662\u3001\u76d1\u72f1\u548c\u519b\u961f\u3002\u6b63\u56e0\u4e3a\u8fd9\u4e2a\u7f18\u6545\uff0c\u9664\u4e86\u5c11\u6570\u7279\u4f8b\uff0c\u90a3\u4e9b\u5e26\u6709\u201c\u5b88\u5e8f\u201d\u672c\u8d28\u7684\u5f02\u754c\u751f\u7269\u51e0\u4e4e\u4e0d\u4f1a\u4e0e\u51e1\u4eba\u7f14\u7ed3\u534f\u8bae\u2014\u2014\u56e0\u4e3a\u8fd9\u79cd\u534f\u8bae\u53ea\u5bf9\u5f02\u754c\u751f\u7269\u6709\u7ea6\u675f\u529b\uff0c\u5bf9\u51e1\u4eba\u800c\u8a00\u5176\u5b9e\u6ca1\u6709\u610f\u4e49\u3002\u9b54\u9b3c\u80fd\u591f\u5f04\u51fa\u7075\u9b42\u5951\u7ea6\u8fd9\u79cd\u4e1c\u897f\uff0c\u8ba9\u5730\u72f1\u7684\u4f4d\u9762\u6cd5\u5219\u80fd\u591f\u76f4\u63a5\u7ea6\u675f\u5230\u51e1\u4eba\uff0c\u5b9e\u5728\u662f\u975e\u5e38\u4e86\u4e0d\u8d77\u7684\u53d1\u660e\u3002</p><p>\u4f46\u6709\u4e00\u79cd\u65b9\u6cd5\uff0c\u5374\u53ef\u4ee5\u8ba9\u51e1\u4eba\u4e4b\u95f4\u7684\u5951\u7ea6\uff0c\u4e5f\u80fd\u591f\u5177\u5907\u5929\u7136\u7684\u5f3a\u5236\u529b\u548c\u7ea6\u675f\u529b\uff0c\u5373\u662f\u201c\u89c1\u8bc1\u201d\u3002</p><p>\u6b63\u5982\u666e\u901a\u4eba\u4e4b\u95f4\u7f14\u7ed3\u534f\u8bae\uff0c\u53ea\u662f\u4e00\u5f20\u7eb8\uff0c\u4f46\u5373\u4fbf\u53ea\u5b57\u4e0d\u6539\uff0c\u53ea\u8981\u6cd5\u9662\u52a0\u76d6\u5370\u7ae0\uff0c\u7acb\u523b\u5c31\u53d8\u6210\u4e86\u6cd5\u5f8b\u6587\u4e66\u3002\u5951\u7ea6\u539f\u672c\u5c31\u662f\u4e00\u79cd\u201c\u7a0b\u5e8f\u201d\uff0c\u6216\u8005\u8bf4\u201c\u4eea\u5f0f\u201d\uff0c\u91cd\u70b9\u5728\u4e8e\u5f62\u5f0f\uff0c\u5176\u5177\u4f53\u5185\u5bb9\u5982\u4f55\uff0c\u53cd\u800c\u5e76\u975e\u5173\u952e\u6240\u5728\u3002\u5728\u7b26\u5408\u7279\u5b9a\u7a0b\u5e8f\u7684\u524d\u63d0\u4e0b\u2014\u2014\u5728\u5177\u5907\u201c\u79e9\u5e8f\u201d\u6cd5\u5219\u7684\u4f4d\u9762\u4e4b\u4e2d\u7b7e\u7f72\u5951\u7ea6\uff0c\u5e76\u4e14\u7531\u83b7\u5f97\u8be5\u4f4d\u9762\u6240\u8ba4\u53ef\u7684\uff0c\u80fd\u591f\u64cd\u63a7\u6cd5\u5219\u7684\uff0c\u62e5\u6709\u201c\u79e9\u5e8f\u201d\u672c\u8d28\u7684\u5f02\u754c\u751f\u7269\u4f5c\u4e3a\u89c1\u8bc1\u8005\u2014\u2014\u51e1\u4eba\u6240\u7b7e\u7f72\u7684\u5951\u7ea6\uff0c\u4e5f\u5c31\u53ef\u4ee5\u83b7\u5f97\u6cd5\u5219\u7684\u8ba4\u53ef\u548c\u4fdd\u969c\uff0c\u4ece\u800c\u62e5\u6709\u4e86\u7c7b\u4f3c\u4e8e\u7075\u9b42\u5951\u7ea6\u8fd9\u79cd\u5f3a\u5236\u7ea6\u675f\u529b\u548c\u81ea\u6211\u6267\u884c\u529b\u3002</p><p>\u8981\u8fbe\u6210\u8fd9\u4e2a\u524d\u63d0\u6761\u4ef6\u2026\u2026\u6709\u70b9\u96be\u3002</p><p>\u51e1\u4eba\u8981\u8dd1\u53bb\u5916\u5c42\u4f4d\u9762\uff0c\u539f\u672c\u5c31\u4e0d\u5bb9\u6613\uff0c\u8de8\u4f4d\u9762\u65c5\u884c\u53ef\u4e0d\u662f\u8c01\u90fd\u80fd\u505a\u5230\u7684\u4e8b\u60c5\uff1b\u800c\u5177\u5907\u201c\u79e9\u5e8f\u201d\u6cd5\u5219\u7684\u4f4d\u9762\uff0c\u867d\u7136\u4e0d\u662f\u7edd\u65e0\u4ec5\u6709\uff0c\u5374\u4e5f\u5be5\u5be5\u65e0\u51e0\uff0c\u51c6\u786e\u5730\u8bf4\u5c31\u53ea\u6709\u5929\u5802\u5c71\u548c\u4e5d\u5c42\u5730\u72f1\uff0c\u522b\u65e0\u5176\u4ed6\uff1b\u5982\u679c\u8bf4\u524d\u4e24\u4e2a\u6761\u4ef6\u8fd8\u76f8\u5bf9\u5bb9\u6613\u4e9b\uff0c\u8fd8\u6709\u5e0c\u671b\uff0c\u90a3\u4e48\u6700\u540e\u4e5f\u662f\u6700\u5173\u952e\u7684\u4e00\u70b9\uff0c\u8981\u7531\u8be5\u4f4d\u9762\u7684\u201c\u5929\u547d\u8005\u201d\u2014\u2014\u6bd4\u5982\u8bf4\u5929\u5802\u5c71\u7684\u4e09\u5723\u3001\u66e6\u5929\u4f7f\uff0c\u5730\u72f1\u7684\u4e5d\u5927\u9b54\u738b\u2014\u2014\u51fa\u573a\uff0c\u8fd9\u5c31\u5b8c\u5168\u8ba9\u4eba\u7edd\u671b\u4e86\u3002\u5982\u679c\u6709\u8c01\u8ba4\u4e3a\u8fd9\u79cd\u5927\u4eba\u7269\u4f1a\u6574\u5929\u95f2\u7740\u65e0\u804a\uff0c\u559c\u6b22\u8dd1\u6765\u7ed9\u4e24\u4e2a\u51e1\u4eba\u7b7e\u7ea6\u505a\u89c1\u8bc1\uff0c\u90a3\u4e48\u2026\u2026\u4eca\u5929\u5929\u6c14\u54c8\u54c8\u54c8\u3002</p><p>\u7136\u800c\uff0c\u6b64\u65f6\u6b64\u5730\uff0c\u4f3c\u4e4e\u6b63\u662f\u4e5d\u5c42\u5730\u72f1\uff0c\u867d\u7136\u5e76\u975e\u6b63\u54c1\uff0c\u4e43\u662f\u6a21\u62df\uff0c\u4f46\u4e5f\u662f\u5f97\u5230\u4e5d\u72f1\u4e4b\u4e3b\u963f\u65af\u8482\u83ab\u65af\u8ba4\u53ef\u7684\uff1b\u7ad9\u5728\u65c1\u8fb9\u7684\u90a3\u4f4d\u84dd\u88d9\u4e3d\u4eba\uff0c\u4f3c\u4e4e\u6b63\u662f\u4e00\u4f4d\u5927\u9b54\u9b3c\uff0c\u867d\u7136\u56e0\u4e3a\u90e8\u5c5e\u653f\u53d8\u7be1\u4f4d\uff0c\u6b63\u5904\u4e8e\u6d41\u4ea1\u4e4b\u4e2d\uff0c\u4f46\u8d44\u683c\u5c31\u662f\u8d44\u683c\uff0c\u4efb\u4f55\u4eba\u90fd\u65e0\u53ef\u5426\u8ba4\u3002</p><p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u539f\u672c\u6839\u672c\u4e0d\u53ef\u80fd\u8fbe\u6210\u7684\u6761\u4ef6\uff0c\u73b0\u5728\u5df2\u7ecf\u5168\u90fd\u5177\u5907\u9f50\u5168\u4e86\u3002</p><p>\u4f5c\u4e3a\u4f20\u9053\u5deb\u5e08\uff0c\u7ef4\u82e5\u62c9\u81ea\u7136\u77e5\u9053\u201c\u89c1\u8bc1\u201d\u8fd9\u56de\u4e8b\uff0c\u4f46\u5979\u4e4b\u524d\u660e\u663e\u6ca1\u6709\u60f3\u5230\u8fc7\u8fd9\u4e00\u70b9\u3002\u5982\u4eca\u88ab\u743c\u6069\u4e00\u63d0\u9192\uff0c\u987f\u65f6\u5c31\u6014\u4f4f\u4e86\uff0c\u8fc7\u4e86\u51e0\u79d2\u949f\u624d\u56de\u8fc7\u795e\u6765\uff0c\u201c\u4ee3\u4ef7\u5462\uff1f\u201d\u5979\u95ee\u3002</p><p>\u5929\u5802\u5c71\u7684\u4e09\u5723\u9ad8\u9ad8\u5728\u4e0a\uff0c\u4e0d\u4f1a\u8f7b\u6613\u4e3a\u51e1\u4eba\u505a\u5951\u7ea6\u89c1\u8bc1\uff0c\u9664\u975e\u5176\u4e2d\u6d89\u53ca\u5176\u6838\u5fc3\u4fe1\u5f92\uff0c\u6216\u8bb8\u8fd8\u6709\u90a3\u4e48\u4e00\u5206\u534a\u70b9\u7684\u5e0c\u671b\uff1b\u4e5d\u5c42\u5730\u72f1\u7684\u9b54\u738b\u4eec\u76f8\u5bf9\u5bb9\u6613\u88ab\u8bf7\u52a8\u2014\u2014\u4e5f\u53ea\u662f\u76f8\u5bf9\u800c\u8a00\uff0c\u636e\u8bf4\u5386\u53f2\u4e0a\u66fe\u7ecf\u6709\u51e1\u4eba\u6210\u529f\u8fc7\uff0c\u4f46\u5374\u8981\u4ed8\u51fa\u9ad8\u989d\u7684\u4ee3\u4ef7\u3002\u9b54\u9b3c\u4eec\u5411\u6765\u6807\u699c\u5951\u7ea6\u7cbe\u795e\uff0c\u7b49\u4ef7\u4ea4\u6362\uff0c\u7edd\u5bf9\u4e0d\u80af\u505a\u4e8f\u672c\u4e70\u5356\uff0c\u51e1\u662f\u4ed8\u51fa\uff0c\u5fc5\u6709\u76f8\u5e94\u56de\u62a5\u3002\u7ef4\u82e5\u62c9\u5e76\u65e0\u80cc\u7ea6\u6bc1\u8bfa\u7684\u6253\u7b97\uff0c\u5982\u679c\u53ea\u662f\u7b7e\u4e00\u4efd\u5951\u7ea6\uff0c\u5979\u81ea\u7136\u65e0\u6240\u8c13\uff0c\u4f46\u743c\u6069\u8981\u62c9\u624e\u745e\u5c14\u6765\u505a\u89c1\u8bc1\uff0c\u95ee\u9898\u5c31\u53d8\u5f97\u4e25\u91cd\u4e86\u3002</p><p>\u201c\u8fd9\u4e2a\u65e0\u9700\u4f60\u5173\u5fc3\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4ee3\u4ef7\u6211\u6765\u652f\u4ed8\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u201c\u90a3\u597d\u5427\uff0c\u201d\u5979\u52c9\u5f3a\u8bf4\uff0c\u201c\u65e2\u7136\u4f60\u4e0d\u76f8\u4fe1\u6211\uff0c\u90a3\u4e48\u5c31\u7b7e\u7ea6\u4e5f\u65e0\u59a8\u3002\u201d</p><p>\u201c\u6210\u4ea4\u3002\u201d</p><p>\u624e\u745e\u5c14\u4e0d\u77e5\u4ece\u4f55\u5904\u53d6\u51fa\u7eb8\u7b14\uff0c\u743c\u6069\u63a5\u8fc7\uff0c\u6b63\u8981\u4e66\u5199\uff0c\u7ef4\u82e5\u62c9\u7a81\u7136\u8bf4\uff1a\u201c\u6211\u6765\u5199\u3002\u201d</p><p>\u201c\u968f\u4fbf\u3002\u201d</p><p>\u743c\u6069\u5c06\u7eb8\u7b14\u4ea4\u7ed9\u7ef4\u82e5\u62c9\uff0c\u5973\u5deb\u5e08\u601d\u5fd6\u4e86\u7247\u523b\uff0c\u5199\u4e86\u51e0\u884c\u5b57\uff0c\u7136\u540e\u9012\u7ed9\u743c\u6069\u8fc7\u76ee\u3002\u743c\u6069\u63a5\u8fc7\u4e00\u770b\uff0c\u987f\u65f6\u5931\u7b11\uff0c\u201c\u8fd9\u5c31\u662f\u4f60\u62df\u7684\u5951\u7ea6\uff1f\u201d</p><p>\u201c\u6709\u4ec0\u4e48\u95ee\u9898\uff1f\u201d</p><p>\u201c\u592a\u6a21\u7cca\u4e86\uff0c\u4e00\u70b9\u90fd\u4e0d\u7cbe\u786e\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6bd4\u5982\u8fd9\u53e5\uff0c\u4f60\u8bf4\u2018\u743c\u6069\xb7\u5170\u5c3c\u65af\u7279\u8981\u5e2e\u52a9\u5854\u62c9\u590f\xb7\u7ef4\u82e5\u62c9\u8131\u79bb\u5371\u9669\u2019\u2014\u2014\u8fd9\u5c31\u6709\u5f88\u591a\u95ee\u9898\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u663e\u7136\u6ca1\u660e\u767d\u4ed6\u7684\u610f\u601d\uff0c\u4e8e\u662f\u743c\u6069\u5c31\u8fdb\u4e00\u6b65\u505a\u89e3\u91ca\uff0c\u201c\u8131\u79bb\u5371\u9669\u5177\u4f53\u6307\u4ec0\u4e48\uff1f\u5047\u8bbe\u8bf4\uff0c\u6211\u5e2e\u4f60\u79bb\u5f00\u6b64\u5730\uff0c\u6682\u65f6\u6446\u8131\u4e86\u8428\u9a6c\u65af\u7279\u7684\u63a7\u5236\uff0c\u4f46\u5728\u56de\u9634\u5f71\u9547\u7684\u9014\u4e2d\u53c8\u88ab\u4ed6\u6293\u4e86\u56de\u53bb\uff0c\u8fd9\u7b97\u4e0d\u7b97\u6211\u7684\u4e49\u52a1\u5c65\u884c\u5b8c\u6bd5\uff1f\u201d</p><p>\u201c\u81ea\u7136\u4e0d\u7b97\u3002\u201d</p><p>\u201c\u4e5f\u5c31\u662f\u8981\u56de\u5230\u9634\u5f71\u9547\uff1f\u201d\u743c\u6069\u7b11\u4e86\u7b11\uff0c\u201c\u522b\u5fd8\u4e86\uff0c\u5982\u4eca\u8428\u9a6c\u65af\u7279\u6b63\u864e\u89c6\u7708\u7708\u3002\u5047\u8bbe\u8bf4\uff0c\u6211\u5e26\u4f60\u56de\u5230\u4e86\u9634\u5f71\u9547\uff0c\u8428\u9a6c\u65af\u7279\u53c8\u6765\u8fdb\u653b\uff0c\u4f60\u4eec\u6218\u8d25\u4e86\uff0c\u5168\u519b\u8986\u6ca1\uff0c\u4f60\u53c8\u88ab\u4ed6\u6293\u4f4f\u4e86\u3002\u90a3\u6211\u7684\u5951\u7ea6\u4e49\u52a1\u662f\u5426\u7b97\u662f\u5b8c\u6210\uff1f\u53c8\u6216\u8005\u8bf4\uff0c\u6b64\u6b21\u4f60\u4eec\u6218\u80dc\uff0c\u4f46\u8428\u9a6c\u65af\u7279\u662f\u4e2a\u5deb\u5996\uff0c\u4ed6\u4e0d\u4f1a\u88ab\u771f\u6b63\u6d88\u706d\uff0c\u4ed6\u4eca\u5929\u60f3\u505a\u5374\u672a\u80fd\u6210\u529f\u7684\u4e8b\u60c5\uff0c\u660e\u5929\u4ecd\u7136\u8fd8\u4f1a\u7ee7\u7eed\u3002\u5047\u5982\u4e09\u5e74\u4e94\u5e74\u3001\u5341\u5e74\u534a\u5e74\u4e4b\u540e\uff0c\u4f60\u53c8\u88ab\u4ed6\u6293\u4f4f\u4e86\uff0c\u90a3\u6211\u7684\u5951\u7ea6\u4e49\u52a1\u53c8\u662f\u5426\u7b97\u662f\u5b8c\u6210\uff1f\u8fd8\u662f\u6ca1\u6709\u5b8c\u6210\uff1f\u201d</p><p>\u201c\u81ea\u7136\u90fd\u7b97\u4f60\u5b8c\u6210\uff0c\u8fd9\u4e2a\u4f60\u5c3d\u53ef\u4ee5\u653e\u5fc3\uff0c\u201d\u5973\u5deb\u5e08\u4e0d\u5c51\u5730\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u6211\u4e0d\u4f1a\u548c\u4f60\u53bb\u73a9\u5f04\u8fd9\u4e9b\u6587\u5b57\u628a\u620f\u3002\u53ea\u8981\u4f60\u5e26\u6211\u56de\u5230\u9634\u5f71\u9547\uff0c\u90a3\u4f60\u7684\u4e49\u52a1\u5c31\u7b97\u662f\u5b8c\u6210\uff0c\u6211\u7acb\u523b\u4f1a\u7ed9\u4f60\u89e3\u5492\uff0c\u4e4b\u540e\u53d1\u751f\u7684\u4e00\u5207\uff0c\u65e0\u8bba\u6211\u4eec\u662f\u80dc\u662f\u8d25\uff0c\u662f\u751f\u662f\u6b7b\uff0c\u90fd\u4e0e\u4f60\u65e0\u5173\u3002\u201d</p><p>\u867d\u7136\u88ab\u6279\u8bc4\uff0c\u743c\u6069\u5168\u65e0\u6240\u8c13\uff0c\u201c\u8bdd\u8fd8\u662f\u5148\u8bf4\u6e05\u695a\u6bd4\u8f83\u597d\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u90a3\u4f60\u5c31\u4fee\u6539\u4e00\u4e0b\u5427\u3002\u201d</p><p>\u5973\u5deb\u5e08\u968f\u624b\u62b9\u53bb\u4e4b\u524d\u7684\u6587\u5b57\uff0c\u5f00\u59cb\u4e0b\u7b14\uff0c\u4f46\u5199\u4e86\u4e24\u4e2a\u5b57\u4e4b\u540e\uff0c\u5979\u53c8\u505c\u4f4f\u4e86\u3002\u201c\u6216\u8bb8\u4f60\u8bf4\u5f97\u6ca1\u9519\uff0c\u201d\u5979\u51b7\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u786e\u5b9e\u5e94\u8be5\u5199\u5f97\u66f4\u6e05\u695a\u4e00\u4e9b\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u4e0d\u518d\u7406\u776c\u4ed6\uff0c\u72ec\u81ea\u6c89\u541f\u4e86\u534a\u664c\uff0c\u7136\u540e\u518d\u6b21\u52a8\u7b14\u3002\u743c\u6069\u770b\u4e86\u770b\uff0c\u89c1\u5979\u662f\u8fd9\u6837\u5199\u7684\uff1a\u201c\u743c\u6069\xb7\u5170\u5c3c\u65af\u7279\u5e94\u5f53\u5e2e\u52a9\u7ef4\u82e5\u62c9\xb7\u5854\u62c9\u590f\u8131\u79bb\u8428\u9a6c\u65af\u7279\uff08\u4ee5\u53ca\u5176\u90e8\u5c5e\u3001\u76df\u53cb\uff09\u7684\u63a7\u5236\uff0c\u5728\u672a\u53d7\u5230\u81f4\u547d\u4f24\u5bb3\u7684\u60c5\u51b5\u4e0b\u56de\u5230\u9634\u5f71\u9547\u2014\u2014\u9634\u5f71\u9547\u5f53\u65f6\u5e94\u5f53\u662f\u5b89\u5168\u7684\uff0c\u6ca1\u6709\u88ab\u8428\u9a6c\u65af\u7279\uff08\u4ee5\u53ca\u5176\u90e8\u5c5e\u3001\u76df\u53cb\uff09\u6240\u5360\u636e\u2014\u2014\u5e76\u4e14\u89c1\u5230\u4f0a\u5c14\u660e\u65af\u7279\u6216\u8005\u6b23\u5e03\u4e4b\u4e2d\u7684\u4efb\u610f\u4e00\u4eba\u3002\u5f53\u5b8c\u6210\u4e0a\u8ff0\u6761\u4ef6\u4e4b\u540e\uff0c\u7ef4\u82e5\u62c9\xb7\u5854\u62c9\u590f\u5c06\u4f1a\u4e3a\u743c\u6069\xb7\u5170\u5c3c\u65af\u7279\u89e3\u9664\u4e13\u60c5\u8bc5\u5492\u3002\u201d</p><p>\u201c\u5f88\u4e25\u8c28\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u7279\u522b\u5f3a\u8c03\u8981\u89c1\u5230\u4f0a\u5c14\u660e\u65af\u7279\u6216\u8005\u6b23\u5e03\u4e2d\u7684\u4efb\u610f\u4e00\u4eba\u2014\u2014\u83ab\u975e\u4f60\u62c5\u5fc3\u6211\u521a\u628a\u4f60\u5e26\u5230\u9634\u5f71\u9547\u7684\u95e8\u53e3\uff0c\u5c31\u7acb\u523b\u53c8\u628a\u4f60\u6253\u6655\u9001\u7ed9\u8428\u9a6c\u65af\u7279\uff1f\u201d</p><p>\u201c\u662f\u4f60\u63d0\u9192\u6211\u7684\uff0c\u201d\u5973\u5deb\u5e08\u7684\u5634\u89d2\u7fd8\u8d77\uff0c\u201c\u73b0\u5728\u89c9\u5f97\u4f5c\u8327\u81ea\u7f1a\uff1f\u201d</p><p>\u201c\u90a3\u5012\u6ca1\u6709\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u8fc7\u516c\u5e73\u8d77\u89c1\uff0c\u65e2\u7136\u4f60\u589e\u52a0\u4e86\u5982\u6b64\u591a\u7684\u9650\u5236\u524d\u63d0\uff0c\u90a3\u4e48\u6211\u60f3\u6211\u4e5f\u6709\u6743\u529b\u8981\u6c42\u4e00\u9879\u5bf9\u7b49\u56de\u62a5\u3002\u201d</p><p>\u201c\u4f60\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d\u5973\u5deb\u5e08\u8b66\u60d5\u5730\u770b\u7740\u4ed6\u3002</p><p>\u201c\u5728\u6211\u4eec\u7684\u5951\u7ea6\u6ca1\u6709\u5c65\u884c\u5b8c\u6bd5\u4e4b\u524d\u2014\u2014\u4e5f\u5373\u662f\u4f60\u6ca1\u6709\u4e3a\u6211\u89e3\u9664\u8bc5\u5492\u4e4b\u524d\uff0c\u4f60\u5fc5\u987b\u5b8c\u5168\u542c\u547d\u4e8e\u6211\u3002\u201d</p><p>\u5973\u5deb\u5e08\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u201c\u5982\u679c\u662f\u5408\u7406\u7684\u547d\u4ee4\uff0c\u6211\u81ea\u7136\u4f1a\u542c\u4ece\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u7684\u4efb\u4f55\u547d\u4ee4\uff0c\u4f60\u90fd\u5fc5\u987b\u4e0d\u6298\u4e0d\u6263\u5730\u6267\u884c\u3002\u56e0\u4e3a\u5408\u7406\u4e0e\u5426\uff0c\u662f\u6211\u6765\u51b3\u5b9a\uff0c\u800c\u4e0d\u662f\u7531\u4f60\u6765\u5224\u65ad\u7684\u3002\u201d</p><p>\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u540c\u610f\u8fd9\u79cd\u82db\u523b\u7684\u6761\u4ef6\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u8fd9\u5bf9\u4f60\u6709\u597d\u5904\u3002\u4f60\u65e2\u7136\u542c\u6b23\u5e03\u8bf4\u8fc7\u6211\u5728\u65ad\u57df\u9547\u662f\u5982\u4f55\u5bf9\u4ed8\u8428\u9a6c\u65af\u7279\u7684\uff0c\u90a3\u4e48\u4f60\u5c31\u5e94\u8be5\u77e5\u9053\uff0c\u6211\u7ecf\u5e38\u4f1a\u91c7\u53d6\u4e00\u4e9b\u624b\u6bb5\u3002\u5b83\u4eec\u5f80\u5f80\u5728\u4e00\u5f00\u59cb\u770b\u8d77\u6765\u662f\u8352\u8c2c\u7684\uff0c\u5728\u4f60\u770b\u6765\u662f\u5b8c\u5168\u4e0d\u5408\u7406\u7684\uff0c\u4f46\u5374\u80fd\u591f\u6700\u7ec8\u5b9e\u73b0\u76ee\u7684\u3002\u201d</p><p>\u201c\u4f60\u53ef\u4ee5\u89e3\u91ca\u7ed9\u6211\u542c\u3002\u201d</p><p>\u201c\u673a\u4f1a\u8f6c\u77ac\u5373\u901d\uff0c\u8ba1\u5212\u968f\u673a\u5e94\u53d8\uff0c\u6211\u4e0d\u53ef\u80fd\u6709\u65f6\u95f4\u548c\u4f60\u4e00\u4e00\u89e3\u91ca\uff1f\u201d\u743c\u6069\u55e4\u5730\u7b11\u4e86\u4e00\u58f0\uff0c\u201c\u66f4\u4f55\u51b5\uff0c\u4ee5\u4f60\u7684\u667a\u5546\uff0c\u6211\u4e5f\u5f88\u96be\u5411\u4f60\u89e3\u91ca\u6e05\u695a\u3002\u201d</p><p>\u867d\u7136\u88ab\u8d2c\u4f4e\uff0c\u5973\u5deb\u5e08\u5374\u4e1d\u6beb\u672a\u52a8\u6012\uff0c\u201c\u90a3\u597d\u5427\uff0c\u6211\u53ef\u4ee5\u7b54\u5e94\uff0c\u201d\u5979\u60f3\u4e86\u60f3\uff0c\u8bf4\uff0c\u201c\u4f46\u6709\u4e2a\u9650\u5236\u3002\u201d</p><p>\u201c\u8bf4\u5427\u3002\u201d</p><p>\u201c\u6211\u4f1a\u542c\u4ece\u4f60\u7684\u547d\u4ee4\u2014\u2014\u4f46\u4f60\u4e0d\u5f97\u501f\u6b64\u8981\u6c42\u6211\u4e3a\u4f60\u89e3\u9664\u8bc5\u5492\uff0c\u6216\u8005\u63d0\u4f9b\u4e0e\u89e3\u9664\u8bc5\u5492\u76f8\u5173\u7684\u4efb\u4f55\u8d44\u6599\u3002\u201d</p><p>\u201c\u53ef\u4ee5\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90a3\u4e48\u73b0\u5728\u53ef\u4ee5\u7b7e\u7ea6\u4e86\uff1f\u201d</p><p>\u201c\u4f60\u5148\u6765\u3002\u201d</p><p>\u743c\u6069\u5728\u5951\u7ea6\u4e0a\u589e\u52a0\u4e86\u51e0\u53e5\uff0c\u7136\u540e\u9f99\u98de\u51e4\u821e\u5730\u7b7e\u4e0a\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u4ea4\u7ed9\u7ef4\u82e5\u62c9\u3002\u5973\u5deb\u5e08\u63a5\u8fc7\uff0c\u53c8\u53cd\u590d\u770b\u4e86\u4e24\u904d\uff0c\u786e\u8ba4\u65e0\u8bef\uff0c\u65b9\u624d\u7b7e\u4e0a\u81ea\u5df1\u7684\u540d\u5b57\u3002\u4e00\u76f4\u9ed8\u4e0d\u4f5c\u58f0\u7684\u624e\u745e\u5c14\u8d70\u8fc7\u6765\uff0c\u5c06\u624b\u6309\u5728\u5951\u7ea6\u4e0a\u3002</p><p>\u201c\u6211\u4ee5\u963f\u5f17\u7eb3\u65af\u7684\u7edf\u6cbb\u8005\uff0c\u5730\u72f1\u7b2c\u4e00\u5927\u516c\u7235\u7684\u540d\u4e49\uff0c\u89c1\u8bc1\u8fd9\u4efd\u5951\u7ea6\u7684\u6210\u7acb\uff0c\u5e76\u4fdd\u969c\u5176\u5f97\u5230\u4e25\u683c\u7684\u5c65\u884c\u3002\u201d</p><p>\u8d64\u7ea2\u7684\u706b\u7130\u81ea\u9b54\u59ec\u7684\u638c\u5fc3\u6d8c\u51fa\uff0c\u5c06\u7eb8\u711a\u70e7\u6210\u7070\uff0c\u6d88\u5931\u4e0d\u89c1\u3002\u5982\u6b64\u4e00\u6765\uff0c\u8fd9\u4efd\u5951\u7ea6\u5c31\u7b97\u662f\u6b63\u5f0f\u6210\u7acb\uff0c\u65e0\u8bba\u54ea\u4e00\u65b9\u90fd\u65e0\u6cd5\u8fdd\u53cd\uff0c\u5fc5\u987b\u4e0d\u6298\u4e0d\u6263\u5730\u5c65\u884c\u3002</p><p>\u201c\u597d\u4e86\uff0c\u201d\u743c\u6069\u62cd\u4e86\u62cd\u624b\uff0c\u201c\u6b63\u4e8b\u505a\u5b8c\uff0c\u63a5\u4e0b\u6765\u8be5\u5e86\u795d\u4e00\u4e0b\u4e86\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u624e\u745e\u5c14\u968f\u624b\u53ec\u6765\u4e00\u4e2a\u5de8\u5927\u7684\u900f\u660e\u6c34\u6ce1\uff0c\u5c06\u7ef4\u82e5\u62c9\u5305\u88f9\u8d77\u6765\uff0c\u6f02\u6d6e\u5728\u6c34\u9762\u4e0a\uff0c\u8fdc\u8fdc\u5730\u63a8\u5f00\uff0c\u201c\u6765\uff0c\u559d\u70b9\u6a59\u6c41\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u9b54\u59ec\u4e0d\u77e5\u4ece\u54ea\u91cc\u5f04\u6765\u4e24\u676f\u6a59\u6c41\uff0c\u8fd8\u662f\u51b0\u9547\u8fc7\u7684\uff0c\u8fde\u5438\u7ba1\u90fd\u63d2\u597d\u4e86\uff0c\u9012\u4e86\u4e00\u676f\u7ed9\u743c\u6069\u3002\u4e24\u4eba\u80a9\u5e76\u80a9\u5730\u5750\u7740\uff0c\u534a\u664c\u6ca1\u8bf4\u8bdd\uff0c\u743c\u6069\u51fa\u795e\u4e86\u5f88\u4e45\uff0c\u5495\u565c\u5495\u565c\u5730\u4e00\u53e3\u6c14\u5c06\u6a59\u6c41\u559d\u5b8c\uff0c\u968f\u624b\u5c06\u676f\u5b50\u4e22\u5230\u4e00\u65c1\uff0c\u201c\u597d\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u6709\u4ec0\u4e48\u95ee\u9898\u5c31\u95ee\u5427\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u6253\u7b97\u771f\u7684\u548c\u5979\u5408\u4f5c\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u3002\u201d</p><p>\u201c\u55ef\uff0c\u53ef\u662f\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u743c\u6069\u548c\u7ef4\u82e5\u62c9\u6240\u7b7e\u8ba2\u7684\u5951\u7ea6\uff0c\u8868\u9762\u4e0a\u770b\u9887\u4e3a\u516c\u5e73\u5408\u7406\uff0c\u5b9e\u9645\u4e0a\u5c31\u662f\u4e00\u4e2a\u5de8\u5927\u9677\u9631\uff0c\u5168\u65e0\u534a\u70b9\u8bda\u610f\u53ef\u8a00\u3002\u7ef4\u82e5\u62c9\u4e0d\u64c5\u6b64\u9053\uff0c\u8f7b\u6613\u4e3a\u4ed6\u6240\u6b3a\uff0c\u5374\u5982\u4f55\u80fd\u9a97\u5f97\u8fc7\u624e\u745e\u5c14\u8fd9\u79cd\u5927\u9b54\u9b3c\u3002\u5f53\u7136\u743c\u6069\u539f\u672c\u4e5f\u5c31\u6ca1\u6709\u6b3a\u7792\u624e\u745e\u5c14\u7684\u610f\u601d\uff0c\u53cd\u6b63\u9b54\u59ec\u662f\u81ea\u5df1\u4eba\uff0c\u53c8\u4e0d\u4f1a\u62c6\u7a7f\u4ed6\u3002\u624e\u745e\u5c14\u89c1\u4ed6\u5982\u6b64\u884c\u4e8b\uff0c\u81ea\u7136\u77e5\u9053\u4ed6\u538b\u6839\u6ca1\u6709\u548c\u7ef4\u82e5\u62c9\u771f\u6b63\u5408\u4f5c\u7684\u6253\u7b97\u3002\u4f46\u743c\u6069\u505a\u51fa\u8fd9\u4e2a\u51b3\u5b9a\u7684\u7406\u7531\uff0c\u5979\u5c31\u4e0d\u662f\u5f88\u660e\u767d\u4e86\u3002</p><p>\u201c\u539f\u56e0\u4e48\uff0c\u7b80\u5355\u6765\u8bf4\uff0c\u5c31\u662f\u6211\u548c\u5979\u6839\u672c\u6ca1\u6709\u5408\u4f5c\u7684\u57fa\u7840\u3002\u201d</p><p>\u201c\u54e6\uff1f\u201d\u624e\u745e\u5c14\u4e0d\u89e3\uff0c\u201c\u4f60\u8981\u89e3\u5492\uff0c\u5979\u6709\u66ff\u4f60\u89e3\u5492\u7684\u65b9\u6cd5\uff0c\u8fd9\u4e0d\u5c31\u662f\u4f60\u4eec\u53cc\u65b9\u5408\u4f5c\u7684\u57fa\u7840\u5417\uff1f\u201d</p><p>\u201c\u4f46\u5979\u7684\u6761\u4ef6\uff0c\u6211\u6839\u672c\u65e0\u6cd5\u505a\u5230\u554a\u3002\u201d</p><p>\u201c\u4e5f\u4e0d\u4e00\u5b9a\u5427\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u63d0\u51fa\u6765\u7684\u6761\u4ef6\uff0c\u6216\u8005\u8bf4\u5979\u4e3a\u743c\u6069\u89e3\u5492\u7684\u524d\u63d0\uff0c\u662f\u8981\u743c\u6069\u628a\u5979\u4ece\u8428\u9a6c\u65af\u7279\u624b\u91cc\u6551\u51fa\u6765\uff0c\u5e76\u4e14\u5e26\u56de\u9634\u5f71\u9547\uff0c\u5b89\u5168\u5730\u4ea4\u7ed9\u6b23\u5e03\u5979\u4eec\u2014\u2014\u8fd9\u786e\u5b9e\u9887\u6709\u96be\u5ea6\uff0c\u4f46\u624e\u745e\u5c14\u5e76\u4e0d\u89c9\u5f97\u5c31\u80af\u5b9a\u505a\u4e0d\u5230\uff0c\u81f3\u5c11\u5979\u4e0d\u8ba4\u4e3a\u743c\u6069\u4ec5\u4ec5\u56e0\u6b64\u5c31\u9000\u907f\u3002</p><p>\u201c\u4f60\u6ca1\u660e\u767d\u6211\u7684\u610f\u601d\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u6211\u4e0d\u662f\u8bf4\u6211\u6ca1\u6709\u80fd\u529b\u505a\u5230\u8fd9\u70b9\u2014\u2014\u662f\u4e0d\u592a\u5bb9\u6613\uff0c\u4f46\u8fd8\u662f\u53ef\u4ee5\u5c1d\u8bd5\u3002\u6211\u662f\u8bf4\uff0c\u6211\u4e0d\u80fd\u8fd9\u4e48\u505a\u3002\u201d</p><p>\u8981\u4ece\u8428\u9a6c\u65af\u7279\u624b\u91cc\u6551\u4eba\uff0c\u7684\u786e\u96be\u5ea6\u9887\u9ad8\u3002\u7c7b\u4f3c\u7684\u4e8b\u60c5\uff0c\u743c\u6069\u4e4b\u524d\u5728\u65ad\u57df\u9547\u505a\u8fc7\u4e00\u6b21\uff0c\u7ed3\u679c\u4e5f\u6210\u529f\u4e86\uff0c\u4f46\u548c\u73b0\u5728\u7684\u60c5\u5f62\u76f8\u6bd4\uff0c\u8fd8\u662f\u6709\u5f88\u5927\u533a\u522b\u7684\u3002\u65ad\u57df\u9547\u65f6\uff0c\u8428\u9a6c\u65af\u7279\u5b64\u8eab\u5be1\u4eba\uff0c\u743c\u6069\u5374\u548c\u6b23\u5e03\u3001\u8475\u9732\u7b49\u4eba\u6682\u65f6\u7ed3\u76df\uff0c\u5b9e\u529b\u4e0a\u5176\u5b9e\u662f\u5360\u4f18\u7684\uff1b\u73b0\u5982\u4eca\uff0c\u8428\u9a6c\u65af\u7279\u7387\u4f17\u800c\u6765\uff0c\u9f99\u5deb\u6559\u7684\u9ad8\u624b\u4e91\u96c6\u4e8e\u6b64\uff0c\u8fd8\u6709\u8bf8\u5982\u8c0b\u6740\u4e4b\u795e\u9009\u6c11\u3001\u6697\u591c\u9762\u5177\u4e4b\u7c7b\u7684\u540c\u9053\u76f8\u52a9\uff0c\u743c\u6069\u5374\u662f\u52bf\u5355\u529b\u8584\uff0c\u771f\u6b63\u80fd\u4fe1\u4efb\u7684\u4e5f\u5c31\u8eab\u8fb9\u51e0\u4e2a\u5973\u4eba\u3002\u65ad\u57df\u9547\u65f6\uff0c\u8428\u9a6c\u65af\u7279\u5fd7\u5728\u53d6\u5f97\u5370\u7ae0\uff0c\u5b8c\u6210\u9f99\u72c2\u8ff7\u9501\uff0c\u6321\u5728\u4ed6\u9762\u524d\u7684\u4e3b\u8981\u654c\u4eba\u662f\u6b23\u5e03\u548c\u8475\u9732\uff0c\u743c\u6069\u53ea\u662f\u966a\u886c\uff1b\u73b0\u5982\u4eca\uff0c\u8001\u5deb\u5996\u5904\u5fc3\u79ef\u8651\u641e\u51fa\u8fd9\u4e48\u5927\u9635\u4ed7\uff0c\u662f\u8981\u53bb\u89c1\u81ea\u5df1\u7684\u8001\u60c5\u4eba\uff0c\u800c\u7ef4\u82e5\u62c9\u662f\u4ed6\u6574\u4e2a\u8ba1\u5212\u4e2d\u7684\u6700\u5173\u952e\u4e00\u73af\uff0c\u743c\u6069\u82e5\u662f\u5c06\u7ef4\u82e5\u62c9\u6551\u8d70\uff0c\u8428\u9a6c\u65af\u7279\u7684\u4e00\u5207\u7b79\u5212\u90fd\u8981\u843d\u7a7a\uff0c\u5fc5\u5b9a\u548c\u743c\u6069\u4e0d\u6b7b\u4e0d\u4f11\uff0c\u8ffd\u5230\u5929\u6daf\u6d77\u89d2\u4e5f\u8981\u7b97\u8fd9\u7b14\u8d26\u3002\u4e24\u76f8\u6bd4\u8f83\uff0c\u96be\u6613\u7acb\u89c1\u3002</p><p>\u5018\u82e5\u4ec5\u4ec5\u5982\u6b64\uff0c\u5012\u8fd8\u7f62\u4e86\u3002\u4e3a\u4e86\u80fd\u591f\u89e3\u5f00\u8bc5\u5492\uff0c\u4e3a\u4e86\u81ea\u5df1\u7684\u7ec8\u751f\u201c\u6027\u798f\u201d\uff0c\u743c\u6069\u4e5f\u4e0d\u4ecb\u610f\u5192\u4e9b\u98ce\u9669\uff0c\u8fce\u96be\u800c\u4e0a\uff0c\u60f3\u529e\u6cd5\u518d\u9634\u8428\u9a6c\u65af\u7279\u4e00\u6b21\u3002\u4f46\u5373\u4fbf\u4e00\u5207\u987a\u5229\uff0c\u743c\u6069\u5c06\u7ef4\u82e5\u62c9\u6210\u529f\u6551\u51fa\uff0c\u5e26\u56de\u9634\u5f71\u9547\uff0c\u90a3\u63a5\u4e0b\u6765\u4ed6\u4f1a\u9762\u4e34\u66f4\u5927\u7684\u9ebb\u70e6\u3002\u4ed6\u5e2e\u52a9\u8001\u5deb\u5996\u641e\u5b9a\u6cf0\u62c9\u65af\u594e\u5de8\u517d\uff0c\u53d6\u5f97\u201c\u5316\u8eab\u201d\u5fc5\u987b\u7684\u65bd\u6cd5\u6750\u6599\uff0c\u6362\u53d6\u7ef4\u82e5\u62c9\uff0c\u5f3a\u5978\u4e86\u5979\uff0c\u5e76\u4e14\u7a83\u53d6\u5deb\u5e08\u4e4b\u795e\u7684\u795e\u529b\u2014\u2014\u8fd9\u4e9b\u884c\u4e3a\uff0c\u65e0\u8bba\u4ece\u4efb\u4f55\u89d2\u5ea6\u800c\u8a00\uff0c\u90fd\u5c5e\u4e8e\u7edd\u5bf9\u7684\u90aa\u6076\u4e4b\u4e3e\u3002\u5018\u82e5\u8ba9\u7ef4\u82e5\u62c9\u5b89\u5168\u56de\u5230\u9634\u5f71\u9547\uff0c\u90a3\u4ed6\u7684\u6240\u4f5c\u6240\u4e3a\u5982\u4f55\u80fd\u7792\u5f97\u4f4f\uff1f\u51ef\u5c14\u672c\u77e5\u9053\u4e86\u4f1a\u5982\u4f55\uff1f\u6b23\u5e03\u77e5\u9053\u4e86\u4f1a\u5982\u4f55\uff1f\u5982\u679c\u8bf4\u8fd9\u4e9b\u90fd\u8fd8\u4e0d\u600e\u4e48\u8981\u7d27\u7684\u8bdd\u2014\u2014\u6885\u83f2\u65af\u77e5\u9053\u4e86\uff0c\u53c8\u662f\u4f1a\u5982\u4f55\uff1f</p><p>\u201c\u827e\u5f25\u8587\u867d\u7136\u4e0d\u8ba4\u6b7b\u7406\uff0c\u4f46\u5374\u81ea\u6709\u575a\u6301\uff0c\u201d\u743c\u6069\u53f9\u6c14\uff0c\u201c\u5982\u679c\u8ba9\u5979\u77e5\u9053\u4e86\uff0c\u80af\u5b9a\u4f1a\u53d1\u6012\u3002\u60f3\u5230\u90a3\u60c5\u5f62\u6211\u5c31\u4e0d\u5bd2\u800c\u6817\uff0c\u6240\u4ee5\u8fd8\u662f\u7b97\u4e86\u5427\u3002\u201d</p><p>\u201c\u54e6\uff0c\u8fd9\u5012\u662f\u633a\u6709\u8da3\u7684\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u6240\u4ee5\u5bf9\u4e8e\u4f60\u800c\u8a00\uff0c\u827e\u5f25\u8587\u6bd4\u4f60\u7684\u7ec8\u751f\u6027\u798f\u8fd8\u8981\u66f4\u91cd\u8981\uff1f\u6709\u53e5\u8c1a\u8bed\u600e\u4e48\u8bf4\u6765\u7740\uff0c\u4e0d\u8981\u4e3a\u4e00\u9897\u6811\u653e\u5f03\u6574\u4e2a\u68ee\u6797\uff0c\u6211\u5012\u662f\u6ca1\u60f3\u5230\u4f60\u4f1a\u5bf9\u5979\u5982\u6b64\u6df1\u60c5\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u561b\uff0c\u201d\u743c\u6069\u601d\u8003\u4e86\u4e00\u4e0b\uff0c\u201c\u5012\u4e5f\u4e0d\u80fd\u8fd9\u4e48\u8bf4\u5427\u3002\u827e\u5f25\u8587\u5f53\u7136\u975e\u5e38\u91cd\u8981\uff0c\u7ec8\u751f\u6027\u798f\u4ec0\u4e48\u7684\u4e5f\u975e\u5e38\u91cd\u8981\uff0c\u90fd\u5f88\u91cd\u8981\u3002\u201d</p><p>\u505a\u4efb\u4f55\u4e8b\u60c5\uff0c\u603b\u8981\u6743\u8861\u98ce\u9669\uff0c\u8003\u8651\u6210\u672c\u3002\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u88ab\u8bc5\u5492\u4e86\u7684\u786e\u662f\u5f88\u9ebb\u70e6\uff0c\u5f88\u5934\u75bc\uff0c\u4f46\u5018\u82e5\u548c\u6885\u83f2\u65af\u53cd\u76ee\uff0c\u90a3\u540c\u6837\u7cdf\u7cd5\u900f\u9876\u3002\u5982\u679c\u4e00\u5b9a\u8981\u4ed6\u9009\u62e9\u5176\u4e00\u7684\u8bdd\u2014\u2014\u4ed6\u4e24\u6837\u90fd\u4e0d\u60f3\u9009\u3002</p><p>\u201c\u4f46\u4f60\u73b0\u5728\u4e0d\u662f\u5df2\u7ecf\u505a\u51fa\u9009\u62e9\u4e86\u4e48\u3002\u201d</p><p>\u201c\u4e0d\u4e00\u6837\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u533a\u533a\u4e00\u4e2a\u8bc5\u5492\u800c\u5df2\uff0c\u6211\u5c31\u4e0d\u4fe1\u6ca1\u4eba\u80fd\u89e3\u3002\u7b49\u8fc7\u6bb5\u65f6\u95f4\u6211\u53bb\u62dc\u8bbf\u6211\u8001\u5e08\uff0c\u4ed6\u662f\u4ea1\u7075\u5b66\u5927\u5e08\uff0c\u5e94\u8be5\u4f1a\u6709\u529e\u6cd5\u3002\u201d</p><p>\u8bc5\u5492\u6cd5\u672f\u5e76\u4e0d\u5fc5\u7136\u5c5e\u4e8e\u4ea1\u7075\u9b54\u6cd5\uff0c\u4f46\u4ea1\u7075\u9b54\u6cd5\u662f\u516b\u5927\u5b66\u6d3e\u4e2d\u6700\u64c5\u957f\u8bc5\u5492\u7684\u5206\u652f\uff0c\u5965\u6c83\u662f\u7cbe\u7814\u4ea1\u7075\u672f\u7684\u8010\u745f\u5927\u5965\u672f\u5e08\uff0c\u5bf9\u8bc5\u5492\u4e00\u9053\u5fc5\u7136\u6df1\u6709\u5fc3\u5f97\u3002\u743c\u6069\u8bf4\u53bb\u627e\u4ed6\u5e2e\u5fd9\uff0c\u5012\u4e5f\u7b97\u662f\u5bf9\u75c7\u4e0b\u836f\uff0c\u5e76\u975e\u75c5\u6025\u4e71\u6295\u533b\u3002\u4f46\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u90aa\u9b54\u540c\u6837\u662f\u7cbe\u901a\u8bc5\u5492\u7684\u4e13\u5bb6\uff0c\u624e\u745e\u5c14\u4f5c\u4e3a\u5927\u9b54\u9b3c\uff0c\u66f4\u662f\u5176\u4e2d\u7684\u4f7c\u4f7c\u8005\uff0c\u5982\u679c\u5979\u90fd\u89e3\u4e0d\u5f00\u8fd9\u4e2a\u8bc5\u5492\uff0c\u5965\u6c83\u4e5f\u672a\u5fc5\u6709\u591a\u5c11\u628a\u63e1\u3002\u743c\u6069\u8fd9\u4e48\u8bf4\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u662f\u81ea\u6211\u5b89\u6170\u4e86\u3002</p><p>\u201c\u5982\u679c\u8fd9\u8bc5\u5492\u5c31\u662f\u65e0\u6cd5\u53ef\u89e3\u5462\uff1f\u201d</p><p>\u201c\u6211\u89c9\u5f97\u6211\u7684\u8fd0\u6c14\u5e94\u8be5\u6ca1\u90a3\u4e48\u5dee\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5929\u65e0\u7edd\u4eba\u4e4b\u8def\u3002\u201d</p><p>\u201c\u4f60\u8fd9\u662f\u5728\u56de\u907f\u95ee\u9898\uff0c\u6839\u672c\u6ca1\u6709\u6b63\u9762\u9762\u5bf9\u3002\u201d</p><p>\u201c\u65e2\u7136\u80fd\u591f\u56de\u907f\uff0c\u4e3a\u4ec0\u4e48\u975e\u8981\u6b63\u9762\u9762\u5bf9\uff1f\u201d\u743c\u6069\u53cd\u95ee\uff0c\u201c\u524d\u9762\u6709\u6076\u9f99\u62e6\u8def\uff0c\u7ed5\u8fc7\u53bb\u5c31\u662f\u4e86\uff0c\u4f55\u5fc5\u975e\u8981\u8ddf\u5b83\u6b7b\u78d5\uff0c\u6211\u8111\u5b50\u53c8\u6ca1\u95ee\u9898\u3002\u201d</p><p>\u201c\u603b\u6709\u4f60\u56de\u907f\u4e0d\u4e86\uff0c\u7ed5\u4e0d\u8fc7\u53bb\u7684\u65f6\u5019\u554a\u3002\u201d</p><p>\u201c\u90a3\u5c31\u7b49\u4e8b\u5230\u4e34\u5934\u518d\u8bf4\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u7b2c\u4e00\u767e\u4e00\u5341\u4e8c\u8282</p><p>\u5bf9\u4e8e\u743c\u6069\u8fd9\u79cd\u800d\u65e0\u8d56\u7684\u6001\u5ea6\uff0c\u624e\u745e\u5c14\u663e\u7136\u4e0d\u6ee1\u610f\uff0c\u4f46\u5979\u4e5f\u6ca1\u6709\u7ee7\u7eed\u7ea0\u7f20\u3002\u201c\u90a3\u4f60\u5230\u5e95\u662f\u4ec0\u4e48\u6253\u7b97\u5462\uff1f\u201d\u9b54\u59ec\u95ee\uff0c\u201c\u628a\u5979\u8fd8\u7ed9\u8428\u9a6c\u65af\u7279\uff1f\u201d</p><p>\u201c\u4e0d\u884c\uff0c\u201d\u743c\u6069\u6447\u5934\uff0c\u201c\u8428\u9a6c\u65af\u7279\u662f\u8981\u62ff\u5979\u505a\u65bd\u6cd5\u7684\u9053\u5177\uff0c\u6700\u540e\u80af\u5b9a\u6027\u547d\u96be\u4fdd\u3002\u201d</p><p>\u201c\u90a3\u4e0d\u662f\u6b63\u597d\u4e48\uff0c\u4f60\u60f3\u6740\u4eba\u706d\u53e3\uff0c\u5374\u53c8\u601c\u9999\u60dc\u7389\u4e0b\u4e0d\u53bb\u624b\uff0c\u8428\u9a6c\u65af\u7279\u66ff\u4f60\u529e\u4e86\uff0c\u4f60\u5e94\u8be5\u611f\u8c22\u4ed6\u624d\u5bf9\u3002\u201d</p><p>\u201c\u95ee\u9898\u662f\u6211\u6ca1\u60f3\u8981\u6740\u4eba\u706d\u53e3\u554a\u3002\u201d</p><p>\u5982\u679c\u8981\u6740\u4eba\u706d\u53e3\uff0c\u4e8b\u60c5\u5c31\u5f88\u7b80\u5355\u4e86\uff0c\u743c\u6069\u4f55\u5fc5\u8fd8\u8981\u5927\u8d39\u5468\u7ae0\uff0c\u548c\u7ef4\u82e5\u62c9\u7b7e\u4ec0\u4e48\u5951\u7ea6\u3002\u4e4b\u6240\u4ee5\u7ed5\u8fd9\u4e48\u5927\u5f2f\u5b50\uff0c\u81ea\u7136\u662f\u6709\u5176\u7528\u610f\u6240\u5728\u3002\u56e0\u4e3a\u6885\u83f2\u65af\u7684\u5b58\u5728\uff0c\u743c\u6069\u65e0\u6cd5\u7b54\u5e94\u7ef4\u82e5\u62c9\u7684\u6761\u4ef6\uff0c\u4f46\u4ed6\u8fd8\u662f\u5e0c\u671b\u80fd\u591f\u5c3d\u53ef\u80fd\u4fdd\u4f4f\u5973\u5deb\u5e08\u7684\u6027\u547d\uff0c\u5012\u4e0d\u662f\u56e0\u4e3a\u601c\u9999\u60dc\u7389\uff0c\u800c\u662f\u4e3a\u4e86\u81ea\u5df1\u7684\u201c\u6027\u798f\u201d\u3002\u6309\u7167\u7ef4\u82e5\u62c9\u7684\u8bf4\u6cd5\uff0c\u5982\u679c\u5979\u6b7b\u4e86\uff0c\u8fd9\u4e2a\u201c\u4e13\u60c5\u201d\u8bc5\u5492\u5c31\u5f7b\u5e95\u65e0\u6cd5\u53ef\u89e3\uff0c\u743c\u6069\u5bf9\u6b64\u5e76\u4e0d\u5b8c\u5168\u76f8\u4fe1\uff0c\u4f46\u5982\u679c\u53ef\u4ee5\u7684\u8bdd\uff0c\u4ed6\u4e5f\u4e0d\u60f3\u5192\u8fd9\u4e2a\u98ce\u9669\u3002</p><p>\u201c\u90a3\u4f60\u5230\u5e95\u60f3\u5e72\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u6211\u60f3\u6316\u4e2a\u5751\uff0c\u628a\u8fd9\u5e2e\u770b\u7740\u5c31\u8ba8\u538c\u7684\u5bb6\u4f19\u5168\u90fd\u57cb\u8fdb\u53bb\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u4e2a\u5751\u5f97\u591f\u5927\u624d\u884c\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u4f60\u51c6\u5907\u600e\u4e48\u505a\uff1f\u201d</p><p>\u201c\u5766\u767d\u5730\u8bf4\uff0c\u6682\u65f6\u8fd8\u4e0d\u77e5\u9053\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u786e\u5b9e\u4e0d\u77e5\u9053\uff0c\u201d\u743c\u6069\u644a\u5f00\u624b\uff0c\u8bda\u6073\u5730\u8bf4\uff0c\u201c\u6211\u4eec\u638c\u63e1\u7684\u4fe1\u606f\u592a\u5c11\u4e86\u3002\u201d</p><p>\u6316\u5751\u662f\u4e2a\u6280\u672f\u6d3b\uff0c\u80fd\u5426\u6210\u529f\uff0c\u4e3b\u8981\u4e0d\u5728\u4e8e\u529b\u91cf\u7684\u5f3a\u5f31\uff0c\u800c\u5728\u4e8e\u4fe1\u606f\u638c\u63e1\u7a0b\u5ea6\u7684\u9ad8\u4f4e\u3002\u6b63\u9762\u640f\u6597\uff0c\u730e\u4eba\u80af\u5b9a\u6253\u4e0d\u8fc7\u864e\u8c79\uff0c\u4f46\u53ea\u8981\u4e86\u89e3\u5176\u884c\u8d70\u8def\u5f84\uff0c\u719f\u6089\u5176\u751f\u6d3b\u4e60\u6027\uff0c\u4e00\u4e2a\u9677\u9631\u5c31\u8db3\u4ee5\u8ba9\u731b\u517d\u675f\u624b\u5f85\u6bd9\u3002\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u9f99\u5deb\u6559\u4e5f\u7f62\uff0c\u9b54\u6cd5\u5973\u795e\u9009\u6c11\u4e5f\u597d\uff0c\u90fd\u662f\u5e9e\u7136\u5927\u7269\uff0c\u4f46\u672a\u5fc5\u5c31\u4e0d\u53ef\u6218\u80dc\u3002\u65b9\u6cd5\u7528\u5bf9\u4e86\uff0c\u56db\u4e24\u4e5f\u80fd\u62e8\u5343\u65a4\uff1b\u6760\u6746\u627e\u5230\u4e86\uff0c\u661f\u7403\u90fd\u80fd\u64ac\u5f97\u52a8\u3002\u4ee5\u76ee\u524d\u72b6\u51b5\u800c\u8a00\uff0c\u9f99\u5deb\u6559\u548c\u9b54\u6cd5\u5973\u795e\u9009\u6c11\u4e24\u5f3a\u76f8\u4e89\uff0c\u5927\u6218\u4e00\u89e6\u5373\u53d1\uff0c\u53c8\u6709\u5404\u79cd\u5916\u6765\u52bf\u529b\u63d2\u624b\uff0c\u5f62\u52bf\u590d\u6742\uff0c\u5c40\u9762\u6df7\u4e71\uff0c\u6b63\u9002\u5408\u6709\u5fc3\u4eba\u7ffb\u4e91\u8986\u96e8\uff0c\u5c31\u4e2d\u53d6\u5229\u3002\u743c\u6069\u5982\u4eca\u548c\u4e24\u8fb9\u7684\u5173\u7cfb\u90fd\u9887\u4e3a\u5fae\u5999\uff0c\u4f3c\u654c\u975e\u654c\uff0c\u4f3c\u53cb\u975e\u53cb\uff0c\u6e38\u8d70\u5176\u95f4\uff0c\u81ea\u8eab\u4e5f\u7b97\u6709\u4e9b\u8d44\u6e90\uff0c\u5982\u679c\u7b56\u5212\u5f97\u597d\uff0c\u6700\u540e\u6765\u4e2a\u9e64\u868c\u76f8\u4e89\u3001\u6e14\u7fc1\u5f97\u5229\uff0c\u4e5f\u4e0d\u662f\u4e0d\u53ef\u80fd\u3002</p><p>\u5f53\u7136\uff0c\u8fd9\u53ea\u662f\u7406\u8bba\u4e0a\u7684\u6cdb\u6cdb\u800c\u8c08\uff0c\u6d89\u53ca\u5230\u5177\u4f53\u64cd\u4f5c\u5c42\u9762\uff0c\u5c31\u4e0d\u662f\u8fd9\u4e48\u7b80\u5355\u4e86\u3002\u4ee5\u743c\u6069\u76ee\u524d\u6240\u638c\u63e1\u7684\u4fe1\u606f\u8d44\u6599\uff0c\u8981\u60f3\u8bbe\u8ba1\u51fa\u80fd\u591f\u628a\u53cc\u65b9\u90fd\u5751\u8fdb\u53bb\u7684\u9677\u9631\uff0c\u8fd8\u662f\u9887\u6709\u96be\u5ea6\u7684\uff0c\u6216\u8005\u8bf4\u6839\u672c\u5c31\u4e0d\u53ef\u80fd\u3002</p><p>\u201c\u6240\u4ee5\u5f53\u52a1\u4e4b\u6025\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u4eec\u8981\u627e\u4eba\u6765\u5e2e\u5fd9\u3002\u201d</p><p>\u9b54\u59ec\u6014\u4e86\u4e00\u4e0b\uff0c\u201c\u59d0\u59d0\u4e0d\u4f1a\u5e2e\u4f60\u7684\u3002\u201d\u5979\u63d0\u9192\u3002</p><p>\u201c\u662f\u5417\uff1f\u201d\u743c\u6069\u660e\u663e\u6709\u4e9b\u5931\u671b\uff0c\u201c\u4f60\u4e0d\u662f\u8bf4\u5979\u662f\u6211\u59d0\u59d0\u4e48\uff0c\u800c\u4e14\u2026\u2026\u201d</p><p>\u201c\u4f60\u662f\u5979\u6700\u91cd\u8981\u7684\u4eba\uff0c\u8fd9\u70b9\u6bcb\u5eb8\u7f6e\u7591\uff0c\u4f46\u8fd9\u5e76\u4e0d\u610f\u5473\u7740\u4f60\u505a\u4efb\u4f55\u4e8b\u60c5\u5979\u90fd\u4f1a\u65e0\u6761\u4ef6\u652f\u6301\uff0c\u201d\u624e\u745e\u5c14\u89e3\u91ca\uff0c\u201c\u59d0\u59d0\u662f\u4e2a\u975e\u5e38\u6b7b\u8111\u7b4b\u2014\u2014\u6216\u8005\u8bf4\u662f\u4e2a\u975e\u5e38\u6709\u539f\u5219\u7684\u4eba\uff0c\u5979\u65e2\u7136\u5e94\u5141\u8428\u9a6c\u65af\u7279\u5728\u5148\uff0c\u4f60\u5c31\u4e0d\u8981\u6307\u671b\u5979\u4f1a\u51fa\u5c14\u53cd\u5c14\u3002\u201d</p><p>\u201c\u8bf4\u5230\u8fd9\uff0c\u6211\u6709\u4e2a\u95ee\u9898\u4e00\u76f4\u5fd8\u4e86\u95ee\u4f60\uff0c\u5979\u5230\u5e95\u5728\u5e2e\u8428\u9a6c\u65af\u7279\u505a\u4ec0\u4e48\u5462\uff1f\u201d\u743c\u6069\u7a81\u7136\u60f3\u8d77\u6765\uff0c\u201c\u6211\u8fd8\u6ca1\u592a\u641e\u660e\u767d\u3002\u201d</p><p>\u201c\u59d0\u59d0\u5e94\u5141\u8428\u9a6c\u65af\u7279\u7684\u4e8b\u60c5\uff0c\u5e94\u8be5\u5c31\u662f\u53d1\u52a8\u7b2c\u4e94\u79d8\u5668\u7684\u9886\u57df\uff0c\u5e76\u4e14\u7ef4\u6301\u5b83\u5427\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u8428\u9a6c\u65af\u7279\u4e0d\u662f\u8981\u5bf9\u4ed8\u963f\u7956\u65af\u5417\u3002\u5982\u679c\u6ca1\u6709\u4e00\u4e2a\u8db3\u591f\u5b89\u5168\u7684\u73af\u5883\uff0c\u4ed6\u6562\u8fd9\u4e48\u505a\uff0c\u963f\u7956\u65af\u80af\u5b9a\u8981\u7528\u6218\u6597\u5316\u8eab\u964d\u4e34\u6765\u8ffd\u6740\u4ed6\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u8981\u53eb\u4e0a\u51e0\u4e2a\u5e2e\u624b\u3002\u4f46\u7b2c\u4e94\u79d8\u5668\u5c01\u7edd\u5929\u5730\uff0c\u4e00\u65e6\u5f20\u5f00\u9886\u57df\uff0c\u4fbf\u4f1a\u963b\u9694\u4e00\u5207\u795e\u6027\uff0c\u4efb\u4f55\u795e\u7947\u5316\u8eab\u90fd\u65e0\u6cd5\u8fdb\u5165\uff0c\u4e5f\u65e0\u6cd5\u5b58\u5728\u5176\u4e2d\uff0c\u9664\u975e\u5f3a\u884c\u628a\u9886\u57df\u6253\u7834\uff0c\u8fd9\u4e2a\u96be\u5ea6\u592a\u9ad8\u4e86\u3002\u201d</p><p>\u201c\u5982\u679c\u79d8\u5668\u963b\u9694\u4e00\u5207\u795e\u7947\u5316\u8eab\uff0c\u90a3\u8428\u9a6c\u65af\u7279\u53c8\u8981\u5982\u4f55\u4f7f\u7528\u2018\u5316\u8eab\u2019\uff1f\u201d</p><p>\u201c\u4f60\u6709\u4e2a\u6982\u5ff5\u9519\u8bef\uff0c\u55ef\uff0c\u4e5f\u4e0d\u7b97\u4f60\u7684\u9519\uff0c\u662f\u8bed\u8a00\u6f14\u53d8\u548c\u7ffb\u8bd1\u7684\u95ee\u9898\uff0c\u201d\u9b54\u59ec\u95ee\uff0c\u201c\u5728\u8010\u745f\u8bed\u91cc\uff0c\u2018\u5723\u8005\u2019\u600e\u4e48\u8bf4\uff1f\u201d</p><p>\u743c\u6069\u6123\u4e86\u4e00\u4e0b\uff0c\u5728\u8010\u745f\u8bed\u91cc\uff0c\u201c\u5723\u8005\u201d\u548c\u201c\u5316\u8eab\u201d\u662f\u540c\u4e00\u4e2a\u8bcd\uff0c\u53ea\u662f\u4e0d\u540c\u573a\u5408\u8868\u8fbe\u4e0d\u540c\u610f\u601d\uff0c\u901a\u7528\u8bed\u4e2d\u624d\u52a0\u4ee5\u533a\u5206\u3002\u8fd9\u5e76\u4e0d\u5947\u602a\uff0c\u5f88\u591a\u8bed\u8a00\u4e2d\u90fd\u6709\u8fd9\u79cd\u591a\u4e49\u8bcd\uff0c\u743c\u6069\u4e5f\u4ece\u6ca1\u89c9\u5f97\u6709\u4ec0\u4e48\u95ee\u9898\u3002\u4f46\u542c\u624e\u745e\u5c14\u7684\u610f\u601d\uff0c\u8fd9\u5176\u4e2d\u8fd8\u6709\u4ec0\u4e48\u6df1\u5c42\u80cc\u666f\uff1f</p><p>\u201c\u5728\u8010\u745f\u65f6\u4ee3\uff0c\u51c6\u786e\u5730\u8bf4\uff0c\u5728\u5361\u5c14\u8428\u65af\u53d1\u660e\u90a3\u4e2a\u9b54\u6cd5\u4e4b\u524d\uff0c\u538b\u6839\u5c31\u6ca1\u6709\u2018\u5723\u8005\u964d\u4e34\u2019\u8fd9\u56de\u4e8b\uff0c\u5f53\u7136\u4e0d\u4f1a\u6709\u2018\u5723\u8005\u2019\u8fd9\u4e2a\u8bcd\uff0c\u201d\u624e\u745e\u5c14\u8bf4\u8be6\u7ec6\u89e3\u91ca\uff0c\u201c\u5728\u90a3\u4e2a\u65f6\u4ee3\uff0c\u795e\u7947\u8981\u964d\u4e34\u51e1\u95f4\uff0c\u552f\u4e00\u7684\u9014\u5f84\u5c31\u662f\u6d3e\u9063\u5316\u8eab\u3002\u9664\u4e86\u5361\u5c14\u8428\u65af\u90a3\u6b21\u4ee5\u5916\uff0c\u6709\u660e\u6587\u8bb0\u8f7d\u3001\u6709\u636e\u53ef\u67e5\u7684\u6700\u65e9\u7684\u4e00\u6b21\u795e\u7947\u2018\u5723\u8005\u964d\u4e34\u2019\uff0c\u662f\u5927\u6982\u4e03\u767e\u5e74\u524d\uff0c\u9b54\u6cd5\u5973\u795e\u964d\u4e34\u51e1\u95f4\uff0c\u751f\u4e86\u4e03\u4e2a\u5973\u513f\uff0c\u5176\u4e2d\u6709\u51e0\u4e2a\u4f60\u4e5f\u89c1\u8fc7\u3002\u201d</p><p>\u743c\u6069\u660e\u767d\u4e86\u624e\u745e\u5c14\u7684\u610f\u601d\uff0c\u4e5f\u5373\u662f\u8bf4\uff0c\u5361\u5c14\u8428\u65af\u628a\u4ed6\u53d1\u660e\u7684\u9b54\u6cd5\u547d\u540d\u4e3a\u201c\u5316\u8eab\u201d\uff0c\u5176\u5b9e\u5e76\u4e0d\u51c6\u786e\uff0c\u5e94\u8be5\u662f\u201c\u5723\u8005\u964d\u4e34\u201d\u624d\u66f4\u6070\u5f53\uff0c\u53ea\u662f\u5f53\u65f6\u6839\u672c\u6ca1\u6709\u8fd9\u4e2a\u8bcd\uff0c\u53ea\u80fd\u627e\u4e2a\u542b\u4e49\u6700\u63a5\u8fd1\u7684\u3002\u800c\u540e\u4eba\u4ece\u8010\u745f\u8bed\u7ffb\u8bd1\u8fc7\u6765\uff0c\u5c31\u76f4\u63a5\u6309\u5b57\u9762\u610f\u601d\u8bd1\u6210\u4e86\u201c\u5316\u8eab\u201d\u3002</p><p>\u5361\u5c14\u8428\u65af\u7684\u90a3\u4e2a\u9b54\u6cd5\uff0c\u7528\u9014\u662f\u5f3a\u5236\u795e\u7947\u5723\u8005\u964d\u4e34\uff0c\u800c\u975e\u53ec\u5524\u795e\u7947\u5316\u8eab\u3002\u5723\u8005\u662f\u4eba\u975e\u795e\uff0c\u4e0d\u4f1a\u88ab\u79d8\u5668\u6392\u65a5\u3002\u5f53\u7136\uff0c\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u9886\u57df\u4e00\u65e6\u53d1\u52a8\uff0c\u795e\u7947\u7684\u529b\u91cf\u65e0\u6cd5\u76f4\u63a5\u4fb5\u5165\uff0c\u4e5f\u5c31\u65e0\u6cd5\u76f4\u63a5\u964d\u4e34\u5230\u79d8\u5668\u4e4b\u4e2d\u3002\u4f46\u51ef\u745f\u7433\u662f\u79d8\u5668\u7684\u63a7\u5236\u8005\uff0c\u53ea\u8981\u5979\u613f\u610f\uff0c\u5f00\u4e2a\u7279\u522b\u901a\u9053\u662f\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u5979\u662f\u8428\u9a6c\u65af\u7279\u8fd9\u4e2a\u8ba1\u5212\u6210\u8d25\u7684\u5173\u952e\u6240\u5728\uff1f\u201d</p><p>\u201c\u53ef\u4ee5\u8fd9\u4e48\u8bf4\u5427\u3002\u59d0\u59d0\u662f\u4e2a\u5f88\u5b88\u4fe1\u7528\u7684\u4eba\uff0c\u65e2\u7136\u7b54\u5e94\u4e86\uff0c\u800c\u4e14\u662f\u8fd9\u4e48\u91cd\u8981\u7684\u4e8b\u60c5\uff0c\u9664\u975e\u505a\u4e0d\u5230\uff0c\u5426\u5219\u5979\u80af\u5b9a\u8981\u5c3d\u529b\u5b8c\u6210\u3002\u201d</p><p>\u743c\u6069\u82e5\u6709\u6240\u601d\uff0c\u201c\u90a3\u5982\u679c\u6211\u548c\u8428\u9a6c\u65af\u7279\u8d77\u4e86\u51b2\u7a81\uff0c\u8428\u9a6c\u65af\u7279\u8981\u6740\u6211\u5462\uff0c\u5979\u4e5f\u4f1a\u5750\u89c6\u4e0d\u7406\uff1f\u201d</p><p>\u201c\u90a3\u81ea\u7136\u4e0d\u4f1a\uff0c\u201d\u9b54\u59ec\u8bf4\uff0c\u201c\u8fd9\u662f\u4e24\u7801\u4e8b\u3002\u73b0\u5728\u8428\u9a6c\u65af\u7279\u5e76\u65e0\u610f\u5bf9\u4f60\u4e0d\u5229\uff0c\u800c\u662f\u4f60\u8981\u53bb\u627e\u8428\u9a6c\u65af\u7279\u7684\u9ebb\u70e6\uff0c\u59d0\u59d0\u81ea\u7136\u4e0d\u4f1a\u966a\u4f60\u80e1\u95f9\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u53eb\u80e1\u95f9\uff0c\u201d\u743c\u6069\u4e0d\u6ee1\uff0c\u201c\u6211\u6709\u5145\u8db3\u7684\u7406\u7531\u597d\u4e0d\u597d\u3002\u201d</p><p>\u201c\u5728\u5979\u770b\u6765\uff0c\u5c31\u662f\u80e1\u95f9\u3002\u201d</p><p>\u201c\u2026\u2026\u7b97\u4e86\uff0c\u4e0d\u8ba8\u8bba\u8fd9\u4e2a\u95ee\u9898\u3002\u201d</p><p>\u60f3\u8bf7\u51ef\u745f\u7433\u51fa\u624b\u76f8\u52a9\uff0c\u770b\u8d77\u6765\u5e0c\u671b\u4e0d\u5927\u4e86\uff0c\u8fd9\u786e\u5b9e\u5f88\u53ef\u60dc\u3002\u4e0d\u8fc7\u743c\u6069\u5012\u4e5f\u5e76\u6ca1\u6709\u6c14\u9981\uff0c\u8fd9\u539f\u672c\u5c31\u5728\u610f\u6599\u4e4b\u4e2d\uff0c\u53cd\u6b63\u4ed6\u8fd8\u6709\u5907\u7528\u65b9\u6848\u3002\u201c\u5e78\u597d\u6211\u4e0d\u662f\u53ea\u6709\u4e00\u4e2a\u59d0\u59d0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u800c\u4e14\u5e76\u4e0d\u662f\u6240\u6709\u7684\u59d0\u59d0\u90fd\u90a3\u4e48\u6709\u539f\u5219\u3002\u201d</p><p>\u624e\u745e\u5c14\u5148\u662f\u4e0d\u89e3\uff0c\u968f\u5373\u53cd\u5e94\u8fc7\u6765\u3002\u201c\u90a3\u4e48\u6211\u4eec\u5f97\u56de\u53bb\u4e86\u3002\u201d</p><p>\u201c\u55ef\uff0c\u662f\u8be5\u56de\u53bb\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u8fc7\u5728\u6b64\u4e4b\u524d\uff0c\u8fd8\u662f\u628a\u5269\u4f59\u4ef7\u503c\u69a8\u53d6\u5e72\u51c0\u5427\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u201c\u4e5d\u5929\u201d\u4e4b\u540e\uff0c\u51ef\u745f\u7433\u6784\u5efa\u51fa\u7684\u5e7b\u5883\u5d29\u6e83\uff0c\u743c\u6069\u5c06\u7ef4\u82e5\u62c9\u4ea4\u8fd8\u7ed9\u8428\u9a6c\u65af\u7279\u6d3e\u6765\u7684\u4f7f\u8005\uff0c\u5973\u5deb\u5e08\u5df2\u7ecf\u88ab\u6298\u817e\u5f97\u7cbe\u75b2\u529b\u5c3d\uff0c\u6c89\u7761\u4e0d\u9192\u3002\u8fd9\u4e9b\u5929\u6765\uff0c\u743c\u6069\u65e5\u4ee5\u7ee7\u591c\uff0c\u591c\u4ee5\u7ee7\u65e5\uff0c\u51e0\u4e4e\u662f\u4e00\u523b\u65e0\u4f11\uff0c\u4ece\u5979\u4f53\u5185\u6c72\u53d6\u4e86\u5927\u91cf\u7684\u201c\u8679\u96fe\u201d\uff0c\u636e\u624e\u745e\u5c14\u4f30\u7b97\uff0c\u5927\u6982\u76f8\u5f53\u4e8e\u7ef4\u82e5\u62c9\u6240\u6709\u603b\u91cf\u7684\u767e\u5206\u4e4b\u516d\u5341\u5230\u4e03\u5341\u5de6\u53f3\u3002\u51ed\u501f\u8fd9\u4e9b\u7a83\u53d6\u800c\u6765\u7684\u795e\u529b\uff0c\u743c\u6069\u5df2\u7ecf\u53ef\u4ee5\u52c9\u5f3a\u5b8c\u6210\u8679\u96fe\u7684\u7b2c\u4e09\u79cd\u529f\u80fd\u201c\u865a\u62df\u201d\uff0c\u5373\u662f\u5728\u610f\u8bc6\u4e2d\u521b\u9020\u51fa\u4e00\u4e2a\u6a21\u62df\u8bad\u7ec3\u573a\uff0c\u65e0\u9650\u5236\u5730\u8bd5\u9a8c\u4ed6\u6240\u638c\u63e1\u7684\u6cd5\u672f\uff0c\u4f46\u662f\u80fd\u591f\u7ef4\u6301\u7684\u65f6\u95f4\u4e0d\u957f\uff0c\u6bd5\u7adf\u4ed6\u4e0d\u662f\u771f\u6b63\u7684\u5deb\u5e08\u4e4b\u795e\u9009\u6c11\u3002\u81f3\u4e8e\u8679\u96fe\u7684\u53e6\u5916\u4e24\u4e2a\u7528\u9014\uff1a\u201c\u4f20\u9053\u201d\u548c\u201c\u590d\u523b\u201d\uff0c\u76ee\u524d\u4ecd\u65e0\u5934\u7eea\uff0c\u4e0d\u8fc7\u743c\u6069\u4e5f\u4e0d\u4ecb\u610f\uff0c\u53cd\u6b63\u6682\u65f6\u4e5f\u7528\u4e0d\u4e0a\u3002</p><p>\u743c\u6069\u8fd9\u6b21\u6765\uff0c\u660e\u9762\u4e0a\u662f\u4f5c\u4e3a\u4f7f\u8005\u66ff\u51ef\u5c14\u672c\u4f20\u8bdd\uff0c\u8bd5\u56fe\u6536\u4e70\u51ef\u745f\u7433\u8f6c\u6362\u9635\u8425\uff0c\u5f53\u7136\uff0c\u5df2\u7ecf\u88ab\u660e\u786e\u62d2\u7edd\u4e86\u3002\u4e8b\u60c5\u6210\u8d25\u4e14\u4e0d\u8bba\uff0c\u65e2\u7136\u51fa\u4f7f\u4e4b\u4e8b\u5df2\u6bd5\uff0c\u63a5\u4e0b\u6765\u81ea\u7136\u5e94\u8be5\u6253\u9053\u56de\u5e9c\uff0c\u8fd4\u56de\u9634\u5f71\u9547\u4e86\u3002</p><p>\u79bb\u5f00\u4e4b\u524d\uff0c\u743c\u6069\u53c8\u53bb\u62dc\u8bbf\u51ef\u745f\u7433\u3002</p><p>\u5de8\u5927\u7684\u9752\u94dc\u9f0e\u77d7\u7acb\u5728\u89c2\u661f\u53f0\u4e0a\uff0c\u5a07\u5c0f\u73b2\u73d1\u7684\u9ed1\u8863\u5c11\u5973\u6b63\u5750\u5728\u4e00\u53ea\u9f0e\u8db3\u8fb9\uff0c\u91d1\u9cde\u53cc\u7ffc\u7684\u9f99\u86c7\u5728\u5979\u8eab\u65c1\u76d8\u65cb\u6e38\u8d70\uff0c\u5ffd\u9690\u5ffd\u73b0\uff0c\u53e3\u4e2d\u4e0d\u65f6\u5410\u51fa\u4e00\u7c92\u7c92\u7d2b\u8272\u5149\u70b9\uff0c\u5c31\u50cf\u5439\u6ce1\u6ce1\u4e00\u6837\uff0c\u5728\u7a7a\u6c14\u4e2d\u81a8\u80c0\u3001\u53d8\u5927\uff0c\u6700\u7ec8\u7834\u788e\u6d88\u6563\u3002\u5c11\u5973\u5e2d\u5730\u800c\u5750\uff0c\u53cc\u624b\u62b1\u819d\uff0c\u80cc\u9760\u9f0e\u8db3\uff0c\u773c\u8138\u4f4e\u5782\uff0c\u4eff\u4f5b\u662f\u5728\u4f11\u606f\uff0c\u5e73\u9759\u7684\u8138\u8272\u4e2d\u900f\u7740\u4e9b\u8bb8\u75b2\u5026\u3002\u743c\u6069\u653e\u8f7b\u811a\u6b65\uff0c\u8d70\u5230\u5979\u65c1\u8fb9\uff0c\u4e5f\u5728\u5730\u4e0a\u5750\u4e86\u4e0b\u6765\u3002</p><p>\u201c\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d\u5c11\u5973\u5f00\u53e3\u95ee\uff0c\u773c\u775b\u4ecd\u7136\u662f\u95ed\u7740\u7684\u3002</p><p>\u201c\u55ef\uff0c\u8fd9\u6b21\u2026\u2026\u8c22\u8c22\u4f60\u3002\u201d</p><p>\u5c11\u5973\u7741\u5f00\u773c\uff0c\u770b\u4e86\u770b\u4ed6\uff0c\u795e\u60c5\u6709\u4e9b\u5947\u602a\uff0c\u4eff\u4f5b\u539f\u672c\u60f3\u8bf4\u4ec0\u4e48\uff0c\u4f46\u60f3\u4e86\u60f3\u53c8\u653e\u5f03\u4e86\uff0c\u201c\u8fd8\u6709\u5176\u4ed6\u4e8b\u5417\uff1f\u201d</p><p>\u5f53\u7136\u6709\u3002\u743c\u6069\u6765\u89c1\u5979\uff0c\u4e00\u65b9\u9762\u7684\u786e\u662f\u4e3a\u4e86\u9053\u8c22\uff0c\u4f46\u53e6\u4e00\u65b9\u9762\uff0c\u5219\u662f\u7531\u4e8e\u8fd9\u6bb5\u65f6\u95f4\u4ee5\u6765\uff0c\u5fc3\u4e2d\u79ef\u7d2f\u4e86\u65e0\u6570\u8ff7\u60d1\uff0c\u60f3\u8981\u548c\u5979\u6df1\u8c08\u4e00\u6b21\uff0c\u628a\u8fd9\u4e9b\u7591\u95ee\u90fd\u5f04\u6e05\u695a\u3002\u867d\u7136\u8bf4\u5f88\u591a\u4e1c\u897f\uff0c\u5176\u5b9e\u4ed6\u81ea\u5df1\u4e5f\u5df2\u7ecf\u80fd\u591f\u731c\u6d4b\u5f97\u5230\uff0c\u63a8\u65ad\u5f97\u51fa\uff0c\u4f46\u6ca1\u6709\u7ecf\u8fc7\u672c\u4eba\u7684\u786e\u8ba4\uff0c\u7ec8\u7a76\u90fd\u8fd8\u4e0d\u662f\u5b9a\u8bba\u3002</p><p>\u201c\u624e\u745e\u5c14\u544a\u8bc9\u6211\u8bf4\uff0c\u4f60\u662f\u6211\u59d0\u59d0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5728\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\u3002\u201d</p><p>\u201c\u6211\u662f\u4f60\u7684\u59d0\u59d0\uff0c\u4e00\u76f4\u90fd\u662f\uff0c\u201d\u51ef\u745f\u7433\u8bf4\uff0c\u201c\u4e5f\u662f\u4f60\u7684\u59bb\u5b50\uff0c\u5728\u6700\u540e\u4e00\u523b\uff0c\u4f60\u5411\u6211\u6c42\u5a5a\uff0c\u6211\u5df2\u7ecf\u7b54\u5e94\u4e86\u3002\u201d</p><p>\u201c\u53ef\u662f\u6211\u4ec0\u4e48\u90fd\u4e0d\u8bb0\u5f97\uff0c\u201d\u743c\u6069\u82e6\u607c\u5730\u8bf4\uff0c\u201c\u6211\u76f8\u4fe1\u4f60\u4eec\u6240\u8bf4\u662f\u4e8b\u5b9e\uff0c\u53ef\u662f\u6211\u4e00\u70b9\u8bb0\u5fc6\u90fd\u6ca1\u6709\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u4f46\u90a3\u4e0d\u662f\u95ee\u9898\uff0c\u201d\u51ef\u745f\u7433\u8bf4\uff0c\u201c\u6211\u4f1a\u60f3\u529e\u6cd5\u8ba9\u4f60\u627e\u56de\u8bb0\u5fc6\u3002\u800c\u4e14\u65e0\u8bba\u4f60\u662f\u5426\u8bb0\u5f97\uff0c\u4e8b\u5b9e\u662f\u4e0d\u4f1a\u6539\u53d8\u7684\u3002\u201d</p><p>\u743c\u6069\u4e00\u65f6\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\uff0c\u4ed6\u6709\u5f88\u591a\u95ee\u9898\uff0c\u4f46\u4e0d\u77e5\u9053\u4ece\u4f55\u8bf4\u8d77\u3002\u76d8\u7b97\u4e86\u4e00\u4e0b\u63aa\u8f9e\uff0c\u4ed6\u6b63\u5f85\u518d\u5f00\u53e3\uff0c\u7a81\u7136\u8033\u8fb9\u4f20\u6765\u4e00\u58f0\u5947\u5f02\u7684\u9510\u54cd\uff0c\u50cf\u662f\u6709\u4ec0\u4e48\u950b\u5229\u7684\u4e1c\u897f\u9ad8\u901f\u5212\u7834\u7a7a\u6c14\uff0c\u523a\u5f97\u4ed6\u8033\u819c\u751f\u75bc\uff0c\u4e0d\u7531\u81ea\u4e3b\u5730\u76b1\u7d27\u7709\u5934\uff0c\u51ef\u745f\u7433\u5374\u4eff\u4f5b\u5168\u65e0\u6240\u89c9\u3002\u7d27\u63a5\u7740\uff0c\u4e00\u56e2\u9ed1\u8272\u7684\u70df\u96fe\u5728\u7a7a\u6c14\u4e2d\u6d6e\u73b0\u51fa\u6765\uff0c\u65cb\u8f6c\u7740\uff0c\u6700\u540e\u51dd\u805a\u6210\u4eba\u5f62\uff0c\u6b63\u662f\u4e00\u76f4\u8ddf\u968f\u51ef\u745f\u7433\u7684\u90a3\u4f4d\u5438\u8840\u9b3c\uff0c\u6765\u81ea\u201c\u6697\u591c\u9762\u5177\u201d\u7684\u970d\u6587\u3002</p><p>\u5438\u8840\u9b3c\u671d\u7740\u51ef\u745f\u7433\u5fae\u5fae\u8eac\u8eab\u884c\u793c\uff0c\u5374\u5e76\u4e0d\u8bf4\u8bdd\uff0c\u4f3c\u4e4e\u662f\u987e\u5fcc\u5230\u6709\u743c\u6069\u5728\u65c1\u8fb9\u7684\u7f18\u6545\u3002\u51ef\u745f\u7433\u77a5\u4e86\u5438\u8840\u9b3c\u4e00\u773c\uff0c\u201c\u90a3\u5c31\u5148\u8fd9\u6837\u5427\uff0c\u201d\u5c11\u5973\u5bf9\u743c\u6069\u8bf4\uff0c\u201c\u7b49\u6b64\u95f4\u4e8b\u4e86\uff0c\u6211\u81ea\u4f1a\u53bb\u627e\u4f60\uff0c\u6709\u4ec0\u4e48\u4e8b\u5230\u65f6\u5019\u518d\u8bf4\u597d\u4e86\u3002\u201d</p><p>\u743c\u6069\u53ea\u597d\u7ad9\u8d77\u8eab\u6765\uff0c\u201c\u597d\u7684\uff0c\u201d\u4ed6\u8bf4\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u8bed\u6c14\u4fbf\u5e26\u4e0a\u4e86\u51e0\u5206\u656c\u754f\u4e4b\u610f\uff0c\u8fde\u4ed6\u81ea\u5df1\u90fd\u6ca1\u53d1\u89c9\uff0c\u201c\u90a3\u4e48\u6211\u5148\u544a\u8f9e\u4e86\u3002\u201d</p><p>\u201c\u65e0\u8bba\u53d1\u751f\u4ec0\u4e48\uff0c\u90fd\u4e0d\u7528\u5728\u610f\uff0c\u201d\u51ef\u745f\u7433\u8bf4\uff0c\u201c\u6211\u4f1a\u5b89\u6392\u597d\u4e00\u5207\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u77e5\u9053\u4e86\u3002\u201d</p><p>\u743c\u6069\u8eac\u8eab\u884c\u793c\uff0c\u7136\u540e\u8f6c\u8eab\u79bb\u53bb\u3002</p><p>\u203b\u203b\u203b</p><p>\u4e58\u7740\u5de8\u5927\u7684\u706b\u9e1f\uff0c\u743c\u6069\u548c\u624e\u745e\u5c14\u7ff1\u7fd4\u5929\u9645\uff0c\u8de8\u8d8a\u6570\u5c42\u5730\u72f1\uff0c\u5f88\u5feb\u5c31\u56de\u5230\u4e86\u9634\u5f71\u9547\u3002\u4e00\u8def\u90fd\u5f88\u987a\u5229\uff0c\u6ca1\u6709\u9047\u5230\u4efb\u4f55\u771f\u6b63\u503c\u5f97\u5728\u610f\u7684\u969c\u788d\uff0c\u865a\u7a7a\u4e2d\u8bde\u751f\u7684\u90aa\u9b54\u662f\u8d8a\u6765\u8d8a\u591a\uff0c\u968f\u7740\u7ade\u4e89\u8fdb\u5316\uff0c\u4f4d\u9636\u4e5f\u8d8a\u6765\u8d8a\u9ad8\uff0c\u4f46\u8ddd\u79bb\u771f\u6b63\u7684\u5927\u9b54\u9b3c\u8fd8\u5dee\u4e86\u5341\u4e07\u516b\u5343\u91cc\uff0c\u624e\u745e\u5c14\u53ea\u8981\u4e00\u4e2a\u773c\u795e\uff0c\u5c31\u80fd\u8ba9\u5b83\u4eec\u4fef\u9996\u542c\u547d\uff0c\u907f\u4e4b\u4e0d\u53ca\u3002\u4e5f\u6ca1\u6709\u9047\u4e0a\u9f99\u5deb\u6559\u7684\u6210\u5458\uff0c\u53cd\u5012\u662f\u5728\u9634\u5f71\u9547\u7684\u57ce\u95e8\u53e3\u9644\u8fd1\uff0c\u6070\u597d\u906d\u9047\u4e86\u4e00\u5e55\u51fa\u4eba\u610f\u6599\u7684\u573a\u666f\u3002</p><p>\u4ed6\u770b\u89c1\u4e86\u6b23\u5e03\uff0c\u94f6\u53d1\u7684\u98ce\u66b4\u5973\u738b\u660e\u663e\u6b63\u5904\u4e8e\u4e00\u79cd\u6124\u6012\u81f3\u6781\u7684\u72b6\u6001\uff0c\u72c2\u98ce\u73af\u7ed5\u7740\u5979\u7684\u8eab\u4f53\uff0c\u70bd\u70c8\u706b\u7130\u548c\u8000\u773c\u96f7\u7535\u4ece\u5979\u7684\u624b\u638c\u4e2d\u8ff8\u51fa\uff0c\u6574\u4e2a\u4eba\u4eff\u4f5b\u4e00\u9897\u70bd\u70c8\u71c3\u70e7\u7684\u592a\u9633\u3002\u5979\u9ad8\u58f0\u5475\u65a5\u7740\uff0c\u8bed\u97f3\u5316\u4f5c\u80fd\u591f\u7c89\u788e\u5ca9\u77f3\u7684\u9707\u8361\u97f3\u6ce2\uff0c\u671d\u7740\u773c\u524d\u4e0d\u8fdc\u5904\u7684\u9ed1\u888d\u7537\u4eba\u51b2\u53bb\u3002\u7136\u800c\u9ed1\u888d\u7537\u4eba\u53ea\u662f\u53cc\u624b\u5408\u62e2\uff0c\u865a\u63e1\u7740\u4e00\u67c4\u7070\u8272\u7684\u77ed\u6756\uff0c\u900f\u660e\u7684\u692d\u5706\u5f62\u5149\u7f69\u4e0a\u4e03\u5f69\u6d41\u6ea2\uff0c\u4eff\u4f5b\u9633\u5149\u4e0b\u7684\u6c14\u6ce1\uff0c\u5c06\u4ed6\u6574\u4e2a\u4eba\u4fdd\u62a4\u5728\u5185\u3002\u65e0\u8bba\u98ce\u66b4\u5973\u738b\u7684\u653b\u51fb\u518d\u5982\u4f55\u731b\u70c8\uff0c\u5b9b\u5982\u60ca\u6d9b\u9a87\u6d6a\uff0c\u4ed6\u4ecd\u7136\u7a33\u7a33\u5c79\u7acb\uff0c\u4e0d\u52a8\u5206\u6beb\uff0c\u540c\u65f6\u4e5f\u4e00\u8a00\u4e0d\u53d1\u3002</p><p>\u201c\u8fd9\u662f\u5728\u5e72\u5417\uff1f\u201d</p><p>\u743c\u6069\u83ab\u540d\u5176\u5999\u3002\u548c\u5176\u4ed6\u56f4\u89c2\u8005\u4e00\u6837\uff0c\u4ed6\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u4fdd\u6301\u7740\u4e00\u4e2a\u975e\u5e38\u5b89\u5168\uff0c\u540c\u65f6\u4e5f\u662f\u975e\u5e38\u8fdc\u7684\u8ddd\u79bb\uff0c\u4ed4\u7ec6\u89c2\u5bdf\u4e86\u534a\u5929\uff0c\u6700\u7ec8\u786e\u5b9a\u53cc\u65b9\u5e76\u975e\u662f\u5728\u8868\u6f14\uff0c\u800c\u662f\u5f53\u771f\u52a8\u4e86\u771f\u683c\u2014\u2014\u81f3\u5c11\u6b23\u5e03\u8fd9\u8fb9\u662f\u5982\u6b64\u3002\u7136\u540e\u4ed6\u5c31\u8d8a\u53d1\u8ff7\u60d1\u4e0d\u89e3\u8d77\u6765\uff0c\u201c\u4ed6\u4eec\u4e24\u4e2a\u4e0d\u90fd\u662f\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\u5417\uff0c\u600e\u4e48\u5185\u8ba7\u4e86\uff1f\u8c01\u80fd\u544a\u8bc9\u6211\u53d1\u751f\u4ec0\u4e48\u4e8b\u4e86\uff1f\u201d</p><p>\u8fd9\u4e2a\u95ee\u9898\u65e0\u4eba\u56de\u7b54\uff0c\u800c\u4e24\u4f4d\u9009\u6c11\u5728\u5927\u5ead\u5e7f\u4f17\u4e4b\u4e0b\u7684\u6218\u6597\u4e5f\u5e76\u672a\u6301\u7eed\u592a\u4e45\u3002\u53c8\u6709\u4e94\u4e2a\u4eba\u5728\u7a7a\u6c14\u4e2d\u51fa\u73b0\uff0c\u5176\u4e2d\u6709\u4e00\u4f4d\u94f6\u53d1\u5973\u5b50\uff0c\u548c\u6b23\u5e03\u957f\u5f97\u9887\u6709\u51e0\u5206\u76f8\u4f3c\uff0c\u53e6\u5916\u6709\u4e00\u4f4d\u662f\u54c8\u8d1d\u5c14\uff0c\u743c\u6069\u8fdb\u5165\u9886\u57df\u65f6\u7b2c\u4e00\u4e2a\u906d\u9047\u7684\u4eba\u3002\u4ed6\u4eec\u5408\u529b\u6321\u4f4f\u4e86\u6b23\u5e03\u7684\u6cd5\u672f\uff0c\u5e76\u4e14\u5c06\u5979\u6682\u65f6\u538b\u5236\u4f4f\uff0c\u9ed1\u888d\u7537\u4eba\u2014\u2014\u51ef\u5c14\u672c\u2014\u2014\u4e5f\u5b8c\u5168\u6ca1\u6709\u53cd\u51fb\u7684\u610f\u601d\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ece\u5934\u5230\u5c3e\u4ed6\u5c31\u6ca1\u6709\u8fd8\u624b\u8fc7\uff0c\u53ea\u662f\u5355\u65b9\u9762\u88ab\u52a8\u6328\u6253\u3002\u6b23\u5e03\u4ecd\u7136\u663e\u5f97\u975e\u5e38\u6124\u6012\uff0c\u5979\u7528\u624b\u6307\u7740\u51ef\u5c14\u672c\uff0c\u5927\u58f0\u56b7\u56b7\uff0c\u5176\u4ed6\u4eba\u4f3c\u4e4e\u662f\u5728\u529d\u8bf4\uff0c\u51ef\u5c14\u672c\u5219\u5b8c\u5168\u4e0d\u4f5c\u56de\u5e94\u3002\u540e\u6765\u7684\u51e0\u4e2a\u4eba\u663e\u7136\u5e03\u7f6e\u4e86\u9759\u97f3\u7ed3\u754c\uff0c\u5bfc\u81f4\u743c\u6069\u5b8c\u5168\u542c\u4e0d\u5230\u4ed6\u4eec\u7684\u8c08\u8bdd\u5185\u5bb9\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u4ed6\u4eec\u5c31\u5404\u81ea\u79bb\u53bb\u4e86\u3002</p><p>\u6240\u6709\u65c1\u89c2\u8005\u90fd\u5728\u8bae\u8bba\u7eb7\u7eb7\uff0c\u743c\u6069\u540c\u6837\u611f\u89c9\u5f88\u5947\u602a\u3002\u5728\u5927\u8d24\u8005\u4f0a\u5c14\u660e\u65af\u7279\u56e0\u6545\u4e0d\u80fd\u7406\u4e8b\u7684\u60c5\u51b5\u4e0b\uff0c\u51ef\u5c14\u672c\u662f\u76ee\u524d\u9634\u5f71\u9547\u9635\u8425\u7684\u5b9e\u9645\u6700\u9ad8\u8d1f\u8d23\u4eba\uff0c\u743c\u6069\u786e\u5b9e\u542c\u8bf4\u8fc7\uff0c\u56e0\u4e3a\u884c\u4e8b\u4f5c\u98ce\u7684\u7f18\u6545\uff0c\u51ef\u5c14\u672c\u548c\u9b54\u6cd5\u5973\u795e\u7684\u5176\u4ed6\u9009\u6c11\u2014\u2014\u5305\u62ec\u201c\u4e03\u59d0\u59b9\u201d\u2014\u2014\u7684\u5173\u7cfb\u90fd\u4e0d\u662f\u5f88\u597d\uff0c\u9664\u4e86\u83b1\u62c9\u4e4b\u5916\u3002\u5728\u743c\u6069\u548c\u6b23\u5e03\u4e3a\u6570\u4e0d\u591a\u7684\u8c08\u8bdd\u4e2d\uff0c\u98ce\u66b4\u5973\u738b\u4e5f\u6d41\u9732\u51fa\u4e00\u4e9b\u5bf9\u8fd9\u4f4d\u540c\u50da\u7684\u4e0d\u6ee1\u548c\u6307\u8d23\u3002\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u5927\u5bb6\u90fd\u662f\u540c\u4e00\u4e2a\u9635\u8425\uff0c\u57fa\u672c\u7684\u7acb\u573a\u603b\u662f\u4e00\u81f4\u7684\u3002\u90a3\u4e48\u7a76\u7adf\u662f\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u624d\u4f1a\u5bfc\u81f4\u6b23\u5e03\u8fd9\u6837\u66b4\u8d70\u3002\u8981\u77e5\u9053\uff0c\u98ce\u66b4\u5973\u738b\u7684\u884c\u4e3a\uff0c\u5df2\u7ecf\u65e0\u5f02\u4e8e\u5728\u5f3a\u654c\u5f53\u524d\u7684\u60c5\u51b5\u4e0b\uff0c\u5411\u6240\u6709\u4eba\u5ba3\u544a\u81ea\u5df1\u4e00\u65b9\u53d1\u751f\u4e86\u5185\u90e8\u5206\u88c2\uff0c\u8fd9\u53ef\u4e0d\u662f\u4ec0\u4e48\u4ee4\u4eba\u559c\u95fb\u4e50\u89c1\u7684\u4e8b\u60c5\u3002</p><p>\u743c\u6069\u548c\u624e\u745e\u5c14\u8c08\u8bba\u4e86\u51e0\u53e5\uff0c\u4f46\u90fd\u4e0d\u5f97\u8981\u9886\u3002\u8d70\u4e86\u4e00\u6bb5\u8def\uff0c\u624e\u745e\u5c14\u5ffd\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u201c\u4f60\u81ea\u5df1\u56de\u53bb\u5427\uff0c\u6211\u8981\u53bb\u901b\u901b\u8857\u3002\u201d\u5979\u8bf4\u3002</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u9b54\u59ec\u7684\u8eab\u5f71\u5df2\u7ecf\u6d88\u5931\u5728\u7a7a\u6c14\u4e2d\u3002\u743c\u6069\u65e0\u5948\u5730\u8038\u8038\u80a9\uff0c\u6b63\u51c6\u5907\u4e00\u4e2a\u4eba\u56de\u5bb6\uff0c\u5ffd\u7136\u4e00\u4e2a\u4f20\u4ee4\u5175\u8dd1\u8fc7\u6765\uff0c\u8bf4\u51ef\u5c14\u672c\u77e5\u9053\u4ed6\u987a\u5229\u8fd4\u56de\uff0c\u6b63\u5728\u8bae\u4e8b\u5385\u4e2d\u7b49\u5f85\uff0c\u8bf7\u4ed6\u8fc7\u53bb\u89c1\u9762\u3002</p><p>\u201c\u77e5\u9053\u4e86\u3002\u201d</p><p>\u743c\u6069\u539f\u672c\u7684\u6253\u7b97\u662f\u5148\u56de\u4f4f\u5904\u4f11\u606f\uff0c\u7a0d\u665a\u70b9\u518d\u53bb\u627e\u51ef\u5c14\u672c\u3002\u53cd\u6b63\u4ed6\u53c8\u4e0d\u662f\u51ef\u5c14\u672c\u7684\u4e0b\u5c5e\uff0c\u6b64\u6b21\u53ea\u7b97\u662f\u987a\u624b\u76f8\u52a9\uff0c\u6ca1\u5fc5\u8981\u8fd9\u4e48\u79ef\u6781\u3002\u4f46\u73b0\u5728\u65e2\u7136\u5df2\u7ecf\u627e\u4e0a\u95e8\u6765\uff0c\u90a3\u5c31\u53bb\u4e00\u8d9f\u5427\u3002</p><p>\u9634\u5f71\u9547\u5e76\u4e0d\u5927\uff0c\u6ca1\u8fc7\u591a\u4e45\uff0c\u743c\u6069\u5c31\u89c1\u5230\u4e86\u51ef\u5c14\u672c\u3002\u9ed1\u888d\u5deb\u5e08\u7684\u795e\u60c5\u975e\u5e38\u5e73\u9759\uff0c\u5b8c\u5168\u770b\u4e0d\u51fa\u521a\u521a\u548c\u4eba\u5927\u6218\u4e00\u573a\u7684\u8ff9\u8c61\u3002\u7b80\u5355\u5730\u95ee\u5019\u4e4b\u540e\uff0c\u51ef\u5c14\u672c\u95ee\u8d77\u6b64\u884c\u7684\u7ed3\u679c\uff0c\u743c\u6069\u4fbf\u7b80\u660e\u627c\u8981\u5730\u5c06\u51ef\u745f\u7433\u7684\u7b54\u590d\u8f6c\u544a\uff0c\u81f3\u4e8e\u5176\u4ed6\u201c\u7ec6\u679d\u672b\u8282\u201d\uff0c\u50cf\u8428\u9a6c\u65af\u7279\u641e\u5b9a\u4e86\u6cf0\u62c9\u65af\u594e\u3001\u743c\u6069\u63a8\u5012\u4e86\u7ef4\u82e5\u62c9\uff0c\u7b49\u7b49\u7b49\u7b49\uff0c\u90fd\u662f\u4e9b\u4e0d\u503c\u4e00\u63d0\u7684\u5c0f\u4e8b\uff0c\u81ea\u7136\u5c31\u907f\u800c\u4e0d\u8c08\u4e86\u3002</p><p>\u5f97\u77e5\u51ef\u745f\u7433\u62d2\u7edd\uff0c\u51ef\u5c14\u672c\u5012\u4e5f\u6ca1\u6709\u8868\u73b0\u5f97\u591a\u4e48\u5931\u671b\uff0c\u5b9e\u9645\u4e0a\uff0c\u743c\u6069\u89c9\u5f97\u4ed6\u538b\u6839\u5c31\u6ca1\u62b1\u591a\u5c11\u671f\u5f85\u3002\u4e24\u4eba\u6ca1\u4ec0\u4e48\u4ea4\u60c5\uff0c\u81ea\u7136\u4e5f\u4e0d\u53ef\u80fd\u5bd2\u6684\u5ba2\u5957\uff0c\u51e0\u53e5\u8bdd\u8bf4\u5b8c\uff0c\u987f\u65f6\u5c31\u6709\u4e9b\u51b7\u573a\u3002\u743c\u6069\u4e0d\u60f3\u6d6a\u8d39\u65f6\u95f4\uff0c\u6b63\u51c6\u5907\u544a\u8f9e\uff0c\u51ef\u5c14\u672c\u7a81\u7136\u8bf4\uff1a\u201c\u8fd9\u4e24\u5929\u9547\u91cc\u53d1\u751f\u4e86\u4e00\u4e9b\u4e8b\u60c5\uff0c\u51fa\u4e4e\u610f\u6599\u4e4b\u5916\u3002\u201d</p><p>\u201c\u54e6\uff0c\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u743c\u6069\u7684\u5174\u8da3\u88ab\u63d0\u4e86\u8d77\u6765\uff0c\u7136\u800c\u51ef\u5c14\u672c\u53ea\u8bf4\u4e86\u4e00\u53e5\uff0c\u5374\u53c8\u95ed\u53e3\u4e0d\u8a00\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u4ed6\u53c8\u8bf4\uff0c\u201c\u6b23\u5e03\u66fe\u7ecf\u63d0\u5230\u8fc7\uff0c\u4f60\u6709\u4e00\u4f4d\u540c\u4f34\uff0c\u5965\u5609\u83b1\u65af\u5973\u58eb\uff0c\u662f\u6614\u65e5\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u7684\u5927\u5965\u672f\u5e08\u3002\u201d</p><p>\u201c\u5979\u5176\u5b9e\u4e0d\u7b97\u662f\u6211\u7684\u540c\u4f34\uff0c\u201d\u743c\u6069\u60f3\u8981\u89e3\u91ca\uff0c\u4e00\u65f6\u53c8\u4e0d\u77e5\u4ece\u4f55\u8bf4\u8d77\uff0c\u201c\u603b\u4e4b\uff0c\u6211\u548c\u5979\u4e0d\u592a\u719f\u3002\u201d</p><p>\u201c\u5965\u5609\u83b1\u65af\u5973\u58eb\u73b0\u5728\u4e5f\u5728\u9634\u5f71\u9547\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u5fc3\u5934\u8d77\u7591\uff0c\u4e0d\u77e5\u9053\u51ef\u5c14\u672c\u7a81\u7136\u63d0\u8d77\u5965\u5609\u83b1\u65af\u662f\u4ec0\u4e48\u7528\u610f\u3002\u6309\u9053\u7406\u8bf4\uff0c\u4ed6\u4eec\u5e94\u8be5\u65e2\u65e0\u4ea4\u60c5\u4e5f\u65e0\u4ec7\u6028\uff0c\u538b\u6839\u5c31\u6ca1\u4ea4\u96c6\u624d\u5bf9\u3002</p><p>\u201c\u8010\u745f\u745e\u5c14\u662f\u5965\u6cd5\u7684\u6e90\u6d41\u6240\u5728\uff0c\u5927\u5965\u672f\u5e08\u66f4\u662f\u6211\u8f88\u5deb\u5e08\u7684\u5f15\u9053\u8005\uff0c\u5982\u6b64\u5c0a\u8d35\u7684\u5ba2\u4eba\u8385\u4e34\uff0c\u6211\u5fdd\u4e3a\u5730\u4e3b\uff0c\u5374\u6ca1\u80fd\u53ca\u65f6\u62db\u5f85\uff0c\u5b9e\u5728\u662f\u5931\u793c\uff0c\u201d\u51ef\u5c14\u672c\u975e\u5e38\u5ba2\u6c14\u5730\u8bf4\uff0c\u201c\u542c\u8bf4\u5965\u5609\u83b1\u65af\u5973\u58eb\u559c\u6b22\u6e05\u9759\uff0c\u5e73\u5e38\u6df1\u5c45\u7b80\u51fa\uff0c\u6211\u5c31\u4e0d\u5192\u6627\u4e0a\u95e8\u6253\u6270\u4e86\u3002\u8fd9\u91cc\u6709\u4e00\u4efd\u793c\u7269\uff0c\u70e6\u8bf7\u8f6c\u4ea4\uff0c\u5e76\u4ee3\u6211\u81f4\u610f\uff0c\u4e0d\u77e5\u9053\u662f\u5426\u65b9\u4fbf\u3002\u201d</p><p>\u8fd9\u5f53\u7136\u6ca1\u4ec0\u4e48\u4e0d\u65b9\u4fbf\uff0c\u53cd\u6b63\u4e3e\u624b\u4e4b\u52b3\u3002\u4e8e\u662f\u743c\u6069\u4fbf\u4ece\u51ef\u5c14\u672c\u624b\u4e2d\u63a5\u8fc7\u4e00\u4e2a\u5305\u88c5\u7cbe\u81f4\u7684\u793c\u76d2\uff0c\u7136\u540e\u4fbf\u501f\u673a\u8d77\u8eab\u544a\u8f9e\u3002</p><p>\u56de\u5230\u4f4f\u5904\uff0c\u6885\u83f2\u65af\u548c\u51db\u6b63\u51c6\u5907\u51fa\u95e8\uff0c\u770b\u5230\u743c\u6069\u5012\u662f\u9887\u4e3a\u60ca\u559c\u3002\u201c\u4f60\u600e\u4e48\u53bb\u4e86\u90a3\u4e48\u4e45\uff0c\u201d\u51db\u57cb\u6028\uff0c\u201c\u5bb3\u5f97\u827e\u5f25\u8587\u6574\u5929\u90fd\u5fc3\u795e\u4e0d\u5b9a\u7684\u3002\u201d</p><p>\u201c\u54ea\u6709\u3002\u201d\u6885\u83f2\u65af\u8138\u7ea2\u3002</p><p>\u201c\u672c\u6765\u5c31\u662f\uff0c\u201d\u51db\u8bf4\uff0c\u7136\u540e\u770b\u5230\u743c\u6069\u624b\u4e0a\u62ff\u7740\u7684\u793c\u76d2\uff0c\u201c\u54a6\uff0c\u8fd9\u662f\u9001\u7ed9\u6211\u7684\u5417\uff0c\u8c22\u8c22\uff0c\u662f\u88d9\u5b50\u8fd8\u662f\u86cb\u7cd5\uff1f\u6211\u6700\u8fd1\u51cf\u80a5\uff0c\u751c\u98df\u4e0d\u80fd\u5403\u592a\u591a\u2014\u2014\u201d</p><p>\u743c\u6069\u8d76\u5feb\u6253\u65ad\uff0c\u201c\u4e0d\u662f\u9001\u7ed9\u4f60\u7684\u3002\u662f\u522b\u4eba\u7684\uff0c\u6211\u53ea\u662f\u4ee3\u4e3a\u8f6c\u4ea4\uff0c\u91cc\u9762\u662f\u4ec0\u4e48\u6211\u4e5f\u4e0d\u77e5\u9053\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\uff0c\u201d\u51db\u9887\u4e3a\u626b\u5174\uff0c\u201c\u4e5f\u5c31\u662f\u8bf4\u4f60\u51fa\u95e8\u4e00\u8d9f\uff0c\u5c45\u7136\u90fd\u6ca1\u7ed9\u6211\u5e26\u793c\u7269\u56de\u6765\uff1f\u54e6\uff0c\u827e\u5f25\u8587\u4e5f\u6ca1\u6709\u3002\u771f\u8fc7\u5206\uff01\u201d\u5979\u6485\u8d77\u5c0f\u5634\uff0c\u201c\u4f60\u8fd9\u4e2a\u7537\u53cb\u4e00\u70b9\u90fd\u4e0d\u79f0\u804c\uff0c\u65e9\u77e5\u9053\u6211\u5c31\u5e26\u7740\u827e\u5f25\u8587\u79c1\u5954\u4e86\u3002\u201d</p><p>\u2026\u2026\u6211\u53c8\u4e0d\u662f\u53bb\u901b\u8857\u65c5\u6e38\uff0c\u54ea\u6709\u793c\u7269\u5e26\u56de\u6765\u3002\u800c\u4e14\u4f60\u8fd9\u4e2a\u201c\u5e26\u7740\u827e\u5f25\u8587\u79c1\u5954\u201d\u662f\u600e\u4e48\u56de\u4e8b\uff1f</p><p>\u201c\u7b97\u4e86\uff0c\u4e0d\u548c\u4f60\u8ba1\u8f83\u3002\u5bf9\u4e86\uff0c\u4f60\u8fd9\u6b21\u51fa\u53bb\u6ca1\u6709\u5077\u5077\u52fe\u642d\u5176\u4ed6\u5973\u4eba\u5427\u3002\u201d</p><p>\u201c\u6ca1\u2026\u2026\u6ca1\u6709\uff0c\u5f53\u7136\u6ca1\u6709\uff0c\u201d\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u8d76\u5feb\u5426\u8ba4\uff0c\u201c\u4f60\u5e72\u561b\u8fd9\u4e48\u95ee\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u6211\u968f\u4fbf\u95ee\u95ee\u800c\u5df2\u3002\u6ca1\u6709\u5c31\u597d\uff0c\u5982\u679c\u6709\u7684\u8bdd\u8bb0\u5f97\u4e0d\u8981\u5e26\u56de\u5bb6\uff0c\u201d\u51db\u4e00\u672c\u6b63\u7ecf\uff0c\u201c\u7537\u4eba\u51fa\u95e8\u5728\u5916\uff0c\u6ca1\u6709\u4e86\u7ba1\u675f\uff0c\u5c31\u4f1a\u5f88\u5bb9\u6613\u5fc3\u751f\u90aa\u5ff5\u3002\u505a\u59bb\u5b50\u7684\u81ea\u7136\u8981\u591a\u591a\u53ee\u5631\uff0c\u591a\u591a\u63d0\u9192\uff0c\u514d\u5f97\u95f9\u51fa\u4ec0\u4e48\u95ee\u9898\uff0c\u5bfc\u81f4\u5bb6\u5ead\u7ea0\u7eb7\u5c31\u4e0d\u597d\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u4ec0\u4e48\u65f6\u5019\u53d8\u6210\u6211\u2018\u59bb\u5b50\u2019\u4e86\uff1f\u201d</p><p>\u201c\u54a6\uff0c\u4e0d\u662f\u5417\uff1f\u201d\u5c0f\u5973\u5deb\u7741\u5927\u773c\u775b\uff0c\u8be7\u5f02\u8d77\u6765\uff0c\u201c\u6211\u4eec\u4e0d\u662f\u90fd\u5df2\u7ecf\u505a\u8fc7\u5f88\u591a\u6b21\u4e86\uff0c\u8fd9\u6837\u5e94\u8be5\u53ef\u4ee5\u7b97\u592b\u59bb\u4e86\u5427\uff0c\u800c\u4e14\u4e0a\u6b21\u4f60\u8fd8\u628a\u4eba\u5bb6\u7684\u5c41\u80a1\u90fd\u7ed9\u2014\u2014\u201d</p><p>\u6885\u83f2\u65af\u53ca\u65f6\u5c06\u5979\u7684\u5634\u6342\u4f4f\u4e86\u3002\u743c\u6069\u677e\u4e86\u53e3\u6c14\uff0c\u8d76\u5feb\u8f6c\u79fb\u8bdd\u9898\uff0c\u201c\u521a\u624d\u6211\u8fdb\u57ce\u65f6\u770b\u5230\u6b23\u5e03\u548c\u51ef\u5c14\u672c\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4ed6\u4eec\u597d\u50cf\u53d1\u751f\u4e86\u4ec0\u4e48\u77db\u76fe\uff0c\u4f60\u77e5\u9053\u600e\u4e48\u56de\u4e8b\u5417\uff1f\u201d</p><p>\u201c\u83b1\u62c9\u5931\u8e2a\u4e86\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4ece\u73b0\u573a\u7559\u4e0b\u7684\u8ff9\u8c61\u6765\u770b\uff0c\u6781\u6709\u53ef\u80fd\u662f\u8428\u9a6c\u65af\u7279\u4e0b\u5f97\u624b\u3002\u201d</p><p>\u743c\u6069\u53c8\u5413\u4e86\u4e00\u8df3\uff0c\u201c\u83b1\u62c9\u88ab\u8428\u9a6c\u65af\u7279\u6293\u8d70\u4e86\uff1f\u201d</p><p>\u4e03\u59d0\u59b9\u4e4b\u4e2d\uff0c\u743c\u6069\u6bd4\u8f83\u719f\u6089\u7684\u81ea\u7136\u662f\u6b23\u5e03\u548c\u8475\u9732\uff0c\u6bd5\u7adf\u66fe\u7ecf\u6253\u8fc7\u4e00\u6bb5\u65f6\u95f4\u7684\u4ea4\u9053\uff1b\u5176\u4f59\u4e94\u4e2a\u5c31\u90fd\u4ec5\u6709\u8033\u95fb\uff0c\u8c08\u4e0d\u4e0a\u591a\u5c11\u4e86\u89e3\uff0c\u800c\u8fd9\u5176\u4e2d\u5bf9\u83b1\u62c9\u53c8\u7b97\u662f\u76f8\u5bf9\u77e5\u9053\u5f97\u591a\u4e00\u4e9b\u3002\u4e4b\u6240\u4ee5\u5982\u6b64\uff0c\u539f\u56e0\u5f88\u7b80\u5355\uff0c\u4ed6\u7684\u6b7b\u80d6\u5b50\u8001\u5e08\u5965\u6c83\u66fe\u7ecf\u89ca\u89ce\u83b1\u62c9\u7684\u7f8e\u8272\uff0c\u4f01\u56fe\u5229\u7528\u4e00\u4ef6\u90aa\u6076\u795e\u5668\u6765\u63a7\u5236\u83b1\u62c9\uff0c\u5a36\u5979\u4e3a\u59bb\uff0c\u82e5\u4e0d\u662f\u6700\u540e\u51ef\u5c14\u672c\u82f1\u96c4\u6551\u7f8e\u6a2a\u63d2\u4e00\u624b\uff0c\u743c\u6069\u73b0\u5728\u6050\u6015\u5f97\u53eb\u83b1\u62c9\u201c\u5e08\u5a18\u201d\u4e86\u3002\u56e0\u4e3a\u8fd9\u5c42\u6e0a\u6e90\uff0c\u743c\u6069\u5bf9\u83b1\u62c9\u7684\u8d44\u6599\u81ea\u7136\u4e5f\u5c31\u4f1a\u591a\u4e0a\u5fc3\u51e0\u5206\u3002</p><p>\u83b1\u62c9\u662f\u9b54\u6cd5\u5973\u795e\u5bc6\u65af\u62c9\u7684\u5973\u513f\uff0c\u4e03\u59d0\u59b9\u4e4b\u4e00\uff0c\u6392\u884c\u7b2c\u4e94\u3002\u66fe\u7ecf\u6709\u76f8\u5f53\u957f\u7684\u4e00\u6bb5\u65f6\u95f4\uff0c\u5979\u5728\u5317\u5730\u5404\u5904\u6e38\u5386\u3001\u5192\u9669\uff0c\u751a\u81f3\u8fd8\u5efa\u7acb\u8d77\u4e86\u4e00\u4e2a\u540d\u4e3a\u65af\u6258\u5357\u7279\u7684\u5c0f\u56fd\u5bb6\uff0c\u81ea\u5c01\u5973\u738b\uff0c\u540e\u6765\u53c8\u7531\u4e8e\u538c\u5026\u653f\u52a1\u800c\u4e3b\u52a8\u9000\u4f4d\u3002\u83b1\u62c9\u4e00\u5ea6\u88ab\u201c\u89d2\u4e4b\u51a0\u201d\u6240\u63a7\u5236\uff0c\u540e\u6765\u662f\u51ef\u5c14\u672c\u51fb\u788e\u89d2\u4e4b\u51a0\uff0c\u8ba9\u5979\u6062\u590d\u6e05\u9192\uff0c\u5e76\u4e14\u6700\u7ec8\u4e24\u4eba\u7ed3\u4e3a\u592b\u59bb\u3002\u5965\u6c83\u5219\u662f\u7a7a\u5fd9\u4e00\u573a\uff0c\u767d\u767d\u4e3a\u4ed6\u4eba\u505a\u5ac1\u8863\uff0c\u8fd8\u635f\u5931\u4e86\u4e00\u4ef6\u73cd\u8d35\u65e0\u6bd4\u7684\u81f3\u9ad8\u795e\u5668\u3002\u636e\u743c\u6069\u6240\u770b\u5230\u7684\u8d44\u6599\u8bb0\u8f7d\uff0c\u83b1\u62c9\u662f\u4e00\u4f4d\u9020\u8be3\u9ad8\u6df1\u7684\u5927\u5deb\u5e08\uff0c\u4ee5\u7cbe\u901a\u9b54\u6cd5\u7684\u5404\u79cd\u53d8\u5316\u800c\u8457\u79f0\uff0c\u4e00\u9053\u5e73\u51e1\u65e0\u5947\u7684\u6cd5\u672f\uff0c\u5728\u5979\u624b\u4e0a\u5f80\u5f80\u80fd\u591f\u53d1\u6325\u51fa\u4ee4\u4eba\u60ca\u53f9\u7684\u6548\u679c\uff0c\u88ab\u79f0\u4e3a\u201c\u5317\u5730\u7684\u5deb\u672f\u5973\u7687\u201d\u3002\u743c\u6069\u4e0d\u77e5\u9053\u5979\u4e0e\u6b23\u5e03\u8c01\u5f3a\u8c01\u5f31\uff0c\u6599\u60f3\u5373\u4fbf\u7a0d\u900a\uff0c\u4e5f\u4e0d\u4f1a\u76f8\u5dee\u592a\u8fdc\u3002\u8fd9\u6837\u7684\u4eba\u7269\uff0c\u8eab\u5904\u5df1\u65b9\u5927\u672c\u8425\uff0c\u5468\u56f4\u5f3a\u8005\u6797\u7acb\uff0c\u62a4\u536b\u65e0\u6570\uff0c\u8428\u9a6c\u65af\u7279\u5c45\u7136\u80fd\u5c06\u5979\u6293\u8d70\uff0c\u8fd9\u4e5f\u672a\u514d\u592a\u532a\u5937\u6240\u601d\u4e86\u3002</p><p>\u201c\u5177\u4f53\u60c5\u51b5\u5462\uff1f\u201d</p><p>\u201c\u83b1\u62c9\u53bb\u63a2\u671b\u524d\u6bb5\u65f6\u95f4\u53d7\u4f24\u672a\u6108\u7684\u591a\u8299\uff0c\u7ed3\u679c\u9047\u88ad\u5931\u8e2a\u3002\u73b0\u573a\u7559\u4e0b\u4e86\u9f99\u5deb\u6559\u7684\u5370\u8bb0\uff0c\u4ee5\u53ca\u4e00\u9053\u8baf\u606f\uff0c\u8981\u6c42\u51ef\u5c14\u672c\u7528\u67d0\u4e2a\u4e1c\u897f\u53bb\u4ea4\u6362\u3002\u201d</p><p>\u743c\u6069\u6c89\u541f\u4e86\u51e0\u79d2\u949f\uff0c\u201c\u6709\u5185\u5978\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u5e94\u8be5\u662f\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u201c\u90a3\u6b23\u5e03\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u201c\u4e24\u4e2a\u539f\u56e0\uff0c\u5176\u4e00\uff0c\u6b23\u5e03\u8ba4\u4e3a\u5185\u5978\u662f\u90a3\u4f4d\u8ff7\u96fe\u5927\u5e08\u2014\u2014\u4e8b\u5b9e\u4e0a\u5f88\u591a\u4eba\u90fd\u662f\u8fd9\u6837\u8ba4\u4e3a\uff0c\u4f46\u51ef\u5c14\u672c\u575a\u6301\u53cd\u5bf9\uff1b\u5176\u4e8c\uff0c\u6b23\u5e03\u8ba4\u4e3a\u53ef\u4ee5\u7b54\u5e94\u8428\u9a6c\u65af\u7279\u7684\u6761\u4ef6\uff0c\u4e0e\u4e4b\u4ea4\u6613\uff0c\u5148\u5c06\u83b1\u62c9\u6551\u56de\uff0c\u4f46\u51ef\u5c14\u672c\u575a\u6301\u62d2\u7edd\u3002\u51b2\u7a81\u5c31\u8fd9\u6837\u4ea7\u751f\u4e86\u3002\u201d</p><p>\u743c\u6069\u9887\u4e3a\u60ca\u8bb6\uff0c\u201c\u51ef\u5c14\u672c\u62d2\u7edd\u4ea4\u6613\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u8428\u9a6c\u65af\u7279\u8981\u7684\u7a76\u7adf\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d\u743c\u6069\u5947\u602a\uff0c\u201c\u80fd\u591f\u8ba9\u51ef\u5c14\u672c\u8fde\u59bb\u5b50\u7684\u5b89\u5371\u90fd\u80fd\u7f6e\u4e4b\u4e0d\u987e\u3002\u201d</p><p>\u201c\u4e0d\u77e5\u9053\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u8baf\u606f\u4e2d\u6ca1\u6709\u660e\u786e\u6307\u660e\uff0c\u4f46\u5f88\u663e\u7136\u9009\u6c11\u4eec\u90fd\u77e5\u9053\u3002\u51db\u95ee\u8fc7\u5979\u7684\u8001\u5e08\uff0c\u6b23\u5e03\u542b\u7cca\u900f\u9732\u8bf4\uff0c\u51ef\u5c14\u672c\u624b\u91cc\u638c\u63e1\u7740\u67d0\u4ef6\u4e1c\u897f\uff0c\u5bf9\u9f99\u5deb\u6559\u800c\u8a00\u975e\u5e38\u91cd\u8981\uff0c\u6240\u4ee5\u8428\u9a6c\u65af\u7279\u60f3\u8981\u5c06\u5176\u53d6\u56de\u3002\u201d</p><p>\u5bf9\u9f99\u5deb\u6559\u5f88\u91cd\u8981\u7684\u4e1c\u897f\uff1f</p><p>\u743c\u6069\u5bf9\u9f99\u5deb\u6559\u7684\u4e86\u89e3\u5e76\u4e0d\u591a\uff0c\u60f3\u4e86\u7247\u523b\u4e0d\u5f97\u5934\u7eea\uff0c\u4e5f\u5c31\u7f62\u4e86\u3002\u201c\u4f60\u4eec\u8fd9\u662f\u8981\u53bb\u54ea\uff1f\u201d</p><p>\u201c\u6211\u4eec\u53bb\u7ea6\u4f1a\uff0c\u901b\u901b\u8857\uff0c\u987a\u4fbf\u4e70\u70b9\u8863\u670d\u3002\u65e2\u7136\u6ca1\u4eba\u9001\u793c\u7269\uff0c\u6211\u4eec\u53ea\u597d\u81ea\u5df1\u7ed9\u81ea\u5df1\u4e70\u5566\uff0c\u201d\u51db\u63d2\u5634\uff0c\u521a\u624d\u743c\u6069\u548c\u6885\u83f2\u65af\u4e00\u76f4\u5728\u8bf4\u8bdd\uff0c\u628a\u5979\u667e\u5728\u4e00\u8fb9\uff0c\u5979\u660e\u663e\u6709\u4e9b\u4e0d\u5f00\u5fc3\uff0c\u201c\u4f60\u5c31\u4e0d\u7528\u8ddf\u8fc7\u6765\u4e86\u3002\u201d</p><p>\u201c\u54e6\uff0c\u77e5\u9053\u4e86\uff0c\u73a9\u5f97\u5f00\u5fc3\uff0c\u8d2d\u7269\u6109\u5feb\u3002\u201d</p><p>\u201c\u54fc\uff01\u201d</p><p>\u51db\u6c14\u9f13\u9f13\u5730\u62c9\u7740\u6885\u83f2\u65af\u8d70\u4e86\u3002\u743c\u6069\u56de\u81ea\u5df1\u623f\u95f4\u6d17\u4e86\u4e2a\u6fa1\uff0c\u6362\u8eab\u8863\u670d\uff0c\u7136\u540e\u5e26\u4e0a\u793c\u76d2\uff0c\u8d70\u5230\u540e\u9662\uff0c\u63a8\u5f00\u8bad\u7ec3\u5ba4\u7684\u95e8\u3002</p><p>\u73ca\u5609\u6b63\u5728\u7ec3\u5251\uff0c\u5979\u7a7f\u7740\u9ed1\u8272\u7684\u7d27\u8eab\u8863\uff0c\u957f\u53d1\u7b80\u5355\u5730\u5728\u8111\u540e\u633d\u6210\u4e00\u4e2a\u9a6c\u5c3e\uff0c\u5e72\u51c0\u5229\u843d\u4e2d\u900f\u7740\u66f2\u7ebf\u7a88\u7a95\u3002\u966a\u5979\u5bf9\u7ec3\u7684\u662f\u838e\u73de\u514b\uff0c\u9b45\u9b54\u7684\u8863\u7740\u4e00\u5982\u65e2\u5f80\u5730\u66b4\u9732\uff0c\u9732\u51fa\u5927\u7247\u96ea\u767d\u808c\u80a4\u3002\u968f\u7740\u5979\u7684\u7eb5\u8dc3\u8d77\u4f0f\uff0c\u80f8\u524d\u4e00\u5bf9\u51f6\u5668\u6643\u6765\u6643\u53bb\uff0c\u6ce2\u6d9b\u6c79\u6d8c\u3002\u743c\u6069\u89c9\u5f97\u5982\u679c\u5bf9\u624b\u6362\u6210\u81ea\u5df1\uff0c\u6216\u8005\u4efb\u4f55\u4e00\u4e2a\u7537\u4eba\uff0c\u53ea\u6015\u65e9\u5c31\u770b\u5f97\u51fa\u795e\uff0c\u4e00\u8d25\u6d82\u5730\u4e86\u3002</p><p>\u4f46\u73ca\u5609\u4e0d\u662f\u7537\u4eba\uff0c\u6240\u4ee5\u5979\u5bf9\u773c\u524d\u7684\u7f8e\u666f\u65e0\u52a8\u4e8e\u8877\u3002\u5979\u7684\u5251\u5feb\u5f97\u60ca\u4eba\uff0c\u800c\u4e14\u72e0\u8fa3\u5f02\u5e38\uff0c\u6bcf\u4e00\u51fb\u90fd\u662f\u76f4\u523a\u8981\u5bb3\u3002\u838e\u73de\u514b\u624b\u6301\u4e24\u67c4\u77ed\u5251\uff0c\u5de6\u652f\u53f3\u6321\uff0c\u6b65\u6b65\u540e\u9000\uff0c\u663e\u7136\u5df2\u7ecf\u5904\u4e8e\u4e0b\u98ce\u3002</p><p>\u201c\u4e0d\u6253\u4e86\u4e0d\u6253\u4e86\uff0c\u6211\u8ba4\u8f93\u3002\u201d</p><p>\u838e\u73de\u514b\u53cc\u5251\u4ea4\u53c9\uff0c\u683c\u4f4f\u73ca\u5609\u7684\u4e00\u4e2a\u523a\u51fb\uff0c\u7528\u529b\u5c06\u5979\u63a8\u5f00\uff0c\u540c\u65f6\u501f\u52bf\u540e\u8df3\u4e24\u6b65\u3002\u201c\u4f60\u56de\u6765\u4e86\uff0c\u4e3b\u4eba\uff0c\u201d\u9b45\u9b54\u5de7\u7b11\u5029\u516e\u5730\u770b\u7740\u743c\u6069\uff0c\u201c\u54a6\uff0c\u8fd9\u662f\u7ed9\u73ca\u5609\u59d0\u59d0\u7684\u793c\u7269\u5417\uff1f\u6709\u6ca1\u6709\u987a\u4fbf\u7ed9\u6211\u5e26\u4e00\u4efd\uff0c\u6211\u8981\u6c42\u4e0d\u9ad8\uff0c\u968f\u4fbf\u9001\u70b9\u73e0\u5b9d\u9996\u9970\u6211\u5c31\u5f88\u5f00\u5fc3\u4e86\uff0c\u6212\u6307\u554a\u9879\u94fe\u554a\u8033\u73af\u4ec0\u4e48\u7684\u90fd\u884c\uff0c\u6211\u4e0d\u6311\u5254\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u4e0d\u662f\uff0c\u8fd9\u662f\u522b\u4eba\u7684\uff0c\u201d\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u201c\u5bf9\u4e0d\u8d77\uff0c\u4e0b\u6b21\u5fd8\u4e86\u7ed9\u4f60\u4eec\u5e26\u793c\u7269\uff0c\u4e0b\u6b21\u6211\u4e00\u5b9a\u8bb0\u5f97\u3002\u201d</p><p>\u838e\u73de\u514b\u9887\u4e3a\u5931\u671b\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u8fd9\u6837\u554a\u3002\u5bf9\u4e86\uff0c\u4e3b\u4eba\uff0c\u4f60\u597d\u50cf\u7cbe\u795e\u4e0d\u662f\u7279\u522b\u597d\u7684\u6837\u5b50\uff0c\u8981\u591a\u591a\u6ce8\u610f\u8eab\u4f53\u554a\u3002\u4e0d\u7136\u5728\u5e8a\u4e0a\u8868\u73b0\u4e0d\u4f73\uff0c\u53ef\u662f\u4f1a\u8ba9\u4eba\u5931\u671b\u7684\u5462\u3002\u201d</p><p>\u201c\u53cd\u6b63\u4e0d\u4f1a\u8ba9\u4f60\u5931\u671b\u5c31\u884c\u3002\u201d\u743c\u6069\u968f\u53e3\u5e94\u4ed8\u4e86\u4e00\u53e5\uff0c\u6325\u6325\u624b\u8ba9\u5979\u6682\u65f6\u56de\u907f\u3002\u9b45\u9b54\u8038\u8038\u80a9\uff0c\u8f6c\u8eab\u79bb\u5f00\uff0c\u987a\u624b\u5c06\u95e8\u5e26\u4e0a\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u73ca\u5609\u7591\u60d1\uff0c\u201c\u4f60\u627e\u6211\u6709\u4e8b\uff1f\u201d</p><p>\u201c\u59d0\u59d0\u7684\u5251\u672f\u771f\u662f\u8fdb\u6b65\u795e\u901f\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5b8c\u5168\u8d85\u4e4e\u6211\u7684\u60f3\u8c61\u3002\u201d</p><p>\u542c\u5230\u5f1f\u5f1f\u7684\u5938\u5956\uff0c\u73ca\u5609\u7efd\u5f00\u7b11\u5bb9\uff0c\u201c\u6ca1\u6709\u5566\uff0c\u201d\u5979\u5fae\u5fae\u6709\u4e9b\u8138\u7ea2\uff0c\u201c\u521a\u624d\u838e\u73de\u514b\u662f\u6545\u610f\u8ba9\u7740\u6211\u7684\u3002\u201d</p><p>\u743c\u6069\u6447\u6447\u5934\uff0c\u4ed6\u5251\u672f\u5e73\u5e73\uff0c\u4f46\u773c\u5149\u8fd8\u662f\u6709\u7684\u3002\u838e\u73de\u514b\u521a\u624d\u6216\u8bb8\u6ca1\u6709\u5168\u529b\u4ee5\u8d74\uff0c\u4f46\u80af\u5b9a\u6ca1\u6709\u6545\u610f\u653e\u6c34\uff0c\u81f3\u5c11\u5df2\u7ecf\u7b97\u662f\u8ba4\u771f\u4f5c\u6218\uff0c\u5374\u8fd8\u662f\u88ab\u73ca\u5609\u538b\u5236\u4f4f\u4e86\u3002\u743c\u6069\u89c1\u8fc7\u5f88\u591a\u6b21\u838e\u73de\u514b\u51fa\u624b\uff0c\u5979\u662f\u6740\u624b\u51fa\u8eab\uff0c\u6b63\u9762\u683c\u6597\u786e\u5b9e\u975e\u5176\u6240\u957f\uff0c\u4f46\u4e5f\u7edd\u5bf9\u4e0d\u7b97\u5dee\uff0c\u5c31\u743c\u6069\u6240\u89c1\u4e4b\u4e2d\uff0c\u5979\u5df2\u7ecf\u8db3\u4ee5\u8dfb\u8eab\u7b2c\u4e00\u7b49\u7684\u5251\u624b\u4e4b\u5217\u3002\u73ca\u5609\u6b64\u524d\u6ca1\u6709\u534a\u70b9\u57fa\u7840\uff0c\u662f\u8ba4\u8bc6\u5965\u5609\u83b1\u65af\u4e4b\u540e\u624d\u5f00\u59cb\u5b66\u4e60\u5251\u672f\uff0c\u77ed\u77ed\u65f6\u65e5\u5c31\u63d0\u5347\u5230\u8fd9\u79cd\u5730\u6b65\uff0c\u8fd9\u53ea\u80fd\u7528\u532a\u5937\u6240\u601d\u6765\u5f62\u5bb9\u4e86\u3002</p><p>\u743c\u6069\u7684\u76ee\u5149\u5728\u5979\u8138\u4e0a\u7a0d\u4f5c\u505c\u7559\uff0c\u7136\u540e\u79fb\u5f00\uff0c\u201c\u4f60\u8001\u5e08\u5462\uff1f\u201d</p><p>\u201c\u5979\u5728\u4f11\u606f\uff0c\u8ba9\u6211\u4e0d\u8981\u6253\u6270\u3002\u4f60\u627e\u5979\u6709\u4e8b\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u51ef\u5c14\u672c\u5148\u751f\u542c\u8bf4\u5979\u5728\u8fd9\u91cc\uff0c\u9001\u6765\u4e86\u4e00\u4ef6\u793c\u7269\uff0c\u5e76\u5411\u5979\u81f4\u610f\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8981\u4e48\u4f60\u5e2e\u5fd9\u8f6c\u4ea4\u5427\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u73ca\u5609\u4e5f\u6ca1\u6709\u592a\u5728\u610f\uff0c\u968f\u53e3\u7b54\u5e94\uff0c\u6b63\u51c6\u5907\u4f38\u624b\u5c06\u793c\u76d2\u63a5\u8fc7\u6765\uff0c\u5e7d\u7075\u5927\u5965\u672f\u5e08\u7684\u58f0\u97f3\u5ffd\u7136\u4ece\u9065\u8fdc\u7684\u5730\u65b9\u60a0\u60a0\u4f20\u6765\u3002</p><p>\u201c\u95ed\u4e0a\u773c\u775b\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u95ed\u4e0a\u773c\u775b\uff0c\u8fc7\u4e86\u4e24\u79d2\u949f\u518d\u7741\u5f00\uff0c\u7136\u540e\u4ed6\u53d1\u73b0\u81ea\u5df1\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u4ef0\u9762\u8eba\u5728\u5730\u4e0a\uff0c\u5468\u56f4\u9759\u6084\u6084\u7684\uff0c\u4e07\u7c41\u4ff1\u5bc2\uff0c\u552f\u6709\u866b\u9e23\u3002\u7565\u5e26\u751c\u5473\u7684\u9752\u8349\u9999\u6c14\u968f\u98ce\u98d8\u6765\uff0c\u5728\u4ed6\u7684\u5468\u56f4\uff0c\u662f\u4e00\u7247\u9ed1\u6697\uff0c\u5728\u4ed6\u7684\u5934\u9876\uff0c\u662f\u6ee1\u5929\u661f\u8fb0\uff0c\u71a0\u71a0\u8f89\u6620\uff0c\u4eff\u4f5b\u5b9d\u77f3\uff0c\u8ba9\u4eba\u4e0d\u7531\u81ea\u4e3b\u5730\u5c06\u76ee\u5149\u6295\u5165\u5176\u4e2d\uff0c\u4e3a\u4e4b\u6c89\u8ff7\u3002\u4ed6\u957f\u957f\u5730\u5410\u4e86\u53e3\u6c14\uff0c\u5c06\u793c\u76d2\u968f\u624b\u4e22\u5230\u4e00\u65c1\uff0c\u8eab\u4f53\u5b8c\u5168\u653e\u677e\uff0c\u76f4\u5230\u6bcf\u4e00\u6839\u624b\u6307\u90fd\u4eff\u4f5b\u62ac\u4e0d\u8d77\u6765\u3002</p><p>\u5965\u5609\u83b1\u65af\u7684\u58f0\u97f3\u968f\u98ce\u4f20\u6765\uff0c\u98d8\u98d8\u6e3a\u6e3a\uff0c\u5ffd\u8fdc\u5ffd\u8fd1\uff0c\u201c\u8fd9\u662f\u51ef\u5c14\u672c\u8ba9\u4f60\u9001\u8fc7\u6765\u7684\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u5c31\u662f\u8fd9\u4e2a\u3002\u4f60\u770b\u770b\u5305\u88c5\u6709\u65e0\u7834\u635f\uff0c\u7136\u540e\u7b7e\u6536\u4e00\u4e0b\u5427\u2014\u2014\u987a\u4fbf\u8bf4\u53e5\uff0c\u8fd9\u662f\u8d27\u5230\u4ed8\u6b3e\u7684\uff0c\u4ed6\u6ca1\u4ed8\u8fd0\u8d39\u3002\u201d</p><p>\u5927\u5965\u672f\u5e08\u5bf9\u743c\u6069\u7684\u51b7\u7b11\u8bdd\u6ca1\u6709\u53cd\u5e94\uff0c\u6216\u8bb8\u662f\u6839\u672c\u6ca1\u542c\u61c2\uff0c\u5979\u4e00\u8a00\u4e0d\u53d1\u3002\u743c\u6069\u7b49\u5f85\u4e86\u4e00\u4f1a\uff0c\u5fcd\u4e0d\u4f4f\u5148\u5f00\u53e3\u4e86\uff0c\u201c\u8fd8\u6709\u4e8b\u5417\uff1f\u6ca1\u4e8b\u6211\u5148\u8d70\u4e86\u3002\u201d</p><p>\u201c\u6211\u6709\u70b9\u4e8b\u60c5\uff0c\u9700\u8981\u4f60\u53bb\u505a\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u743c\u6069\u66fe\u7ecf\u6b20\u5965\u5609\u83b1\u65af\u4e00\u4e2a\u4eba\u60c5\u3002\u5728\u6765\u9634\u5f71\u8c37\u4e4b\u524d\uff0c\u4ed6\u4ee5\u5965\u6cd5\u7684\u540d\u4e49\uff0c\u5411\u5965\u5609\u83b1\u65af\u8bf7\u6559\u6709\u5173\u7b2c\u4e94\u79d8\u5668\u7684\u8d44\u6599\uff0c\u83b7\u5f97\u4e86\u4e0d\u5c11\u6709\u7528\u4fe1\u606f\u3002\u5bf9\u4e8e\u4e00\u540d\u8010\u745f\u745e\u5c14\u7684\u5deb\u5e08\u800c\u8a00\uff0c\u4e00\u65e6\u5c65\u884c\u4e86\u8fd9\u4e2a\u4eea\u5f0f\uff0c\u5c31\u8981\u627f\u62c5\u76f8\u5e94\u7684\u8d23\u4efb\u3002\u5965\u5609\u83b1\u65af\u73b0\u5728\u5f00\u53e3\uff0c\u743c\u6069\u4fbf\u4e0d\u597d\u76f4\u63a5\u62d2\u7edd\u3002\u5f53\u7136\uff0c\u662f\u5426\u5e94\u5141\uff0c\u8fd8\u5f97\u770b\u5177\u4f53\u60c5\u51b5\u624d\u80fd\u6700\u7ec8\u786e\u5b9a\u3002</p><p>\u201c\u4e0d\u4f1a\u592a\u96be\u3002\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u4f46\u4ecd\u7136\u6ca1\u6709\u8bf4\u660e\u5177\u4f53\u662f\u4ec0\u4e48\u4e8b\u60c5\u3002</p><p>\u201c\u90a3\u597d\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u65e2\u7136\u4f60\u73b0\u5728\u884c\u52a8\u4e0d\u4fbf\uff0c\u4f5c\u4e3a\u665a\u8f88\u7406\u5f53\u6548\u52b3\u3002\u91cd\u8981\u7684\u4e8b\u60c5\u505a\u4e0d\u4e86\uff0c\u5982\u679c\u53ea\u662f\u8dd1\u8dd1\u817f\u9001\u9001\u4fe1\u4ec0\u4e48\u7684\uff0c\u6211\u8fd8\u662f\u53ef\u4ee5\u80dc\u4efb\u7684\u3002\u201d</p><p>\u5965\u5609\u83b1\u65af\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u201c\u4f60\u770b\u51fa\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u4e0d\u591a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u6709\u4e00\u70b9\u662f\u5f88\u660e\u663e\u7684\uff0c\u4f60\u6b63\u5728\u53d8\u5f97\u8870\u5f31\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u5965\u5609\u83b1\u65af\u6de1\u6de1\u5730\u5e94\u4e86\u4e00\u58f0\uff0c\u6ca1\u6709\u7ee7\u7eed\u8bf4\u8bdd\uff0c\u743c\u6069\u4e5f\u95ed\u4e0a\u5634\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5927\u5965\u672f\u5e08\u7684\u58f0\u97f3\u518d\u5ea6\u54cd\u8d77\uff0c\u201c\u4f60\u7684\u89c2\u5bdf\u5012\u662f\u633a\u654f\u9510\u7684\u3002\u201d</p><p>\u201c\u8fc7\u5956\u3002\u201d</p><p>\u201c\u90a3\u4e48\u8bf4\u8bf4\u770b\uff0c\u4f60\u8fd8\u53d1\u73b0\u4e86\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6709\u4e00\u4e9b\uff0c\u4f46\u66f4\u591a\u662f\u731c\u6d4b\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5982\u679c\u4f60\u4e0d\u4ecb\u610f\u7684\u8bdd\uff0c\u6211\u53ef\u4ee5\u8be6\u7ec6\u8bf4\u8bf4\u3002\u201d</p><p>\u201c\u7b2c\u4e00\uff0c\u59d0\u59d0\u5728\u53d8\u5f3a\uff0c\u800c\u4e14\u901f\u5ea6\u5feb\u5f97\u4e0d\u6b63\u5e38\uff0c\u201d\u743c\u6069\u8bed\u901f\u5f88\u6162\uff0c\u4ed6\u5728\u4e00\u8fb9\u8bf4\u4e00\u8fb9\u6574\u7406\u601d\u8def\uff0c\u201c\u5979\u6216\u8bb8\u4f1a\u8ba4\u4e3a\u90fd\u662f\u4f60\u6559\u5bfc\u6709\u65b9\uff0c\u4f46\u6211\u4e0d\u8fd9\u4e48\u60f3\u3002\u201d</p><p>\u201c\u4f60\u600e\u4e48\u60f3\uff1f\u201d</p><p>\u201c\u6211\u5728\u60f3\uff0c\u59d0\u59d0\u5728\u4e0d\u65ad\u53d8\u5f3a\uff0c\u800c\u4f60\u5374\u5728\u4e0d\u65ad\u8870\u5f31\uff0c\u6b64\u6d88\u5f7c\u957f\uff0c\u8fd1\u4e4e\u540c\u6b65\uff0c\u8fd9\u5176\u4e2d\u662f\u5426\u5b58\u5728\u67d0\u79cd\u8054\u7cfb\u3002\u5c31\u597d\u50cf\uff0c\u4f60\u6b63\u5728\u628a\u4f60\u7684\u529b\u91cf\u4f20\u9012\u7ed9\u5979\u4e00\u6837\u3002\u201d</p><p>\u201c\u5982\u679c\u53ef\u4ee5\u7684\u8bdd\uff0c\u6211\u7684\u786e\u4f1a\u8fd9\u4e48\u505a\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u4f46\u6211\u4eec\u90fd\u5f88\u6e05\u695a\uff0c\u529b\u91cf\u6e90\u4e8e\u81ea\u8eab\uff0c\u53ef\u4ee5\u6559\u5bfc\uff0c\u53ef\u4ee5\u4f20\u6388\uff0c\u4f46\u96be\u4ee5\u704c\u8f93\u3002\u201d</p><p>\u201c\u662f\u7684\uff0c\u201d\u743c\u6069\u627f\u8ba4\uff0c\u201c\u6240\u4ee5\u6211\u53ea\u662f\u731c\u6d4b\u3002\u201d</p><p>\u201c\u7ee7\u7eed\u8bf4\u3002\u201d</p><p>\u201c\u7b2c\u4e8c\uff0c\u8fd9\u91cc\u660e\u660e\u8fd9\u4e48\u5371\u9669\uff0c\u4f60\u5bf9\u6b64\u4e5f\u975e\u5e38\u6e05\u695a\uff0c\u5374\u540c\u610f\u5e76\u4e14\u5e2e\u52a9\u5979\u95ef\u8fdb\u6765\uff0c\u8fd9\u5b8c\u5168\u4e0d\u7b26\u5408\u9053\u7406\u3002\u6240\u4ee5\u6211\u60f3\uff0c\u4f60\u5e94\u8be5\u662f\u53e6\u6709\u6253\u7b97\uff0c\u6216\u8bb8\uff0c\u8fd9\u6b63\u5408\u4f60\u610f\u3002\u6bd5\u7adf\u4f60\u5bf9\u2018\u5c01\u7edd\u2019\u975e\u5e38\u4e86\u89e3\uff0c\u8fd9\u91cc\u5bf9\u5176\u4ed6\u4eba\u800c\u8a00\u662f\u9677\u9631\uff0c\u662f\u7981\u9522\uff0c\u5bf9\u4e8e\u4f60\u800c\u8a00\u5374\u53ef\u4ee5\u7b97\u662f\u534a\u4e2a\u4e3b\u573a\u3002\u201d</p><p>\u201c\u54e6\uff0c\u8fd8\u6709\u5417\uff1f\u201d</p><p>\u201c\u7b2c\u4e09\uff0c\u59d0\u59d0\u662f\uff0c\u6216\u8005\u8bf4\u66fe\u7ecf\u662f\u4f60\u548c\u6b27\u8d1d\u4f26\u7684\u5973\u513f\uff0c\u800c\u4e14\u5979\u662f\u2018\u7687\u5ba4\u2019\uff0c\u5bf9\u5427\uff1f\u201d</p><p>\u201c\u6ca1\u9519\u3002\u201d</p><p>\u73ca\u5609\u662f\u5965\u5609\u83b1\u65af\u7684\u5973\u513f\uff0c\u6216\u8005\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u73ca\u5609\u66fe\u7ecf\u662f\u963f\u62c9\u838e\uff0c\u963f\u62c9\u838e\u5219\u662f\u6b27\u8d1d\u62c9\u548c\u5965\u5609\u83b1\u65af\u7684\u5973\u513f\u3002\u6b27\u8d1d\u4f26\u662f\u8010\u745f\u5927\u5965\u672f\u5e08\uff0c\u4f46\u540c\u65f6\u4e5f\u662f\u4e00\u4f4d\u9690\u85cf\u8eab\u4efd\u7684\u4f0a\u739b\u65af\u5361\u7687\u5ba4\uff0c\u963f\u62c9\u838e\u7ee7\u627f\u4e86\u4ed6\u7684\u8840\u7edf\u2014\u2014\u8fd9\u4e9b\u751a\u81f3\u90fd\u5df2\u7ecf\u4e0d\u80fd\u7b97\u662f\u731c\u6d4b\uff0c\u5404\u79cd\u6216\u660e\u6216\u6697\u7684\u7ebf\u7d22\u65e9\u5c31\u6307\u5411\u552f\u4e00\u7684\u7ed3\u8bba\uff0c\u5b83\u662f\u5982\u6b64\u663e\u800c\u6613\u89c1\uff0c\u4e00\u76ee\u4e86\u7136\u3002\u6240\u4ee5\u542c\u5230\u5965\u5609\u83b1\u65af\u4eb2\u53e3\u786e\u8ba4\uff0c\u743c\u6069\u4e5f\u5e76\u6ca1\u6709\u4ec0\u4e48\u60ca\u8bb6\u3002</p><p>\u201c\u8fd8\u6709\u5417\uff1f\u201d</p><p>\u201d\u6ca1\u6709\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u5b9e\u9645\u4e0a\u4ed6\u8fd8\u6709\u66f4\u591a\u7684\u731c\u6d4b\uff0c\u4f46\u89c9\u5f97\u6ca1\u6709\u5fc5\u8981\u518d\u8bf4\u51fa\u6765\uff0c\u8fd9\u4e09\u70b9\u5c31\u5df2\u7ecf\u8db3\u591f\u4e86\uff0c\u8db3\u591f\u8868\u8fbe\u4ed6\u7684\u610f\u601d\uff0c\u201c\u6709\u4ec0\u4e48\u6307\u6559\u5417\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u4f60\u7684\u731c\u6d4b\u633a\u6709\u8da3\uff0c\u800c\u6211\u7684\u610f\u56fe\u4e5f\u5f88\u7b80\u5355\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u6211\u5728\u5c3d\u4e00\u4e2a\u6bcd\u4eb2\u7684\u4e49\u52a1\uff0c\u4fdd\u62a4\u81ea\u5df1\u7684\u5973\u513f\u3002\u201d</p><p>\u201c\u4f60\u662f\u5927\u5965\u672f\u5e08\uff0c\u6709\u80fd\u529b\u4fdd\u62a4\u4efb\u4f55\u4eba\u3002\u201d</p><p>\u201c\u4e16\u9053\u8270\u96be\uff0c\u4eba\u5fc3\u9669\u6076\uff0c\u522b\u4eba\u7ec8\u7a76\u662f\u9760\u4e0d\u4f4f\u7684\uff0c\u7236\u6bcd\u3001\u592b\u59bb\u90fd\u4e0d\u4f8b\u5916\uff0c\u80fd\u591f\u4f9d\u8d56\u7684\u53ea\u6709\u81ea\u8eab\uff0c\u201d\u9ed1\u6697\u4e2d\u7684\u58f0\u97f3\u505c\u987f\u4e86\u7247\u523b\uff0c\u201c\u6211\u5fc5\u987b\u5c06\u5979\u4ece\u8ff7\u68a6\u4e2d\u60ca\u9192\uff0c\u8ba9\u5979\u60f3\u8d77\u771f\u6b63\u7684\u81ea\u5df1\u3002\u201d</p><p>\u201c\u771f\u6b63\u7684\u81ea\u5df1\u3002\u201d\u743c\u6069\u4f4e\u58f0\u91cd\u590d\u3002</p><p>\u201c\u4f60\u8bf4\u5f97\u6ca1\u9519\u3002\u5979\u5728\u53d8\u5f3a\uff0c\u6211\u5728\u8870\u5f31\uff0c\u800c\u8fd9\u4e24\u8005\u786e\u5b9e\u5b58\u5728\u8054\u7cfb\uff1b\u6211\u4e4b\u6240\u4ee5\u5e26\u5979\u6765\u8fd9\u91cc\uff0c\u5f53\u7136\u4e0d\u662f\u4e3a\u4e86\u4f60\uff0c\u800c\u662f\u4e3a\u4e86\u8ba9\u5979\u5c3d\u5feb\u2018\u590d\u751f\u2019\uff0c\u7528\u4f60\u7684\u8bdd\u8bf4\u5c31\u662f\u2018\u6e05\u9192\u2019\u3002\u6700\u540e\uff0c\u6211\u786e\u5b9e\u6709\u4e9b\u4e8b\u60c5\u8981\u4f60\u53bb\u505a\uff0c\u4f5c\u4e3a\u62a5\u916c\uff0c\u6211\u4f1a\u544a\u8bc9\u4f60\u4e00\u4e9b\u4e1c\u897f\uff0c\u5b83\u4eec\u6216\u8bb8\u6beb\u65e0\u4ef7\u503c\uff0c\u4e5f\u6216\u8bb8\u5bf9\u4f60\u6709\u7528\u3002\u6211\u5efa\u8bae\u4f60\u63a5\u4e0b\u6765\u4ed4\u7ec6\u8046\u542c\uff0c\u56e0\u4e3a\u6211\u53ea\u8bf4\u4e00\u904d\u3002\u201d</p><p>\u201c\u597d\u7684\u3002\u201d</p><p>\u201c\u4f60\u7ed9\u4e86\u6211\u4e09\u4e2a\u731c\u6d4b\uff0c\u6240\u4ee5\u6211\u544a\u8bc9\u4f60\u4e09\u4ef6\u4e8b\u60c5\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u7b2c\u4e00\uff0c\u7687\u5ba4\u2018\u6b7b\u800c\u4e0d\u4ea1\u2019\u3002\u201d</p><p>\u7b2c\u4e00\u767e\u4e00\u5341\u4e09\u8282</p><p>\u4e0d\u5931\u5176\u6240\u8005\u4e45\uff0c\u6b7b\u800c\u4e0d\u4ea1\u8005\u5bff\uff0c\u743c\u6069\u77e5\u9053\u8fd9\u4e2a\u8bcd\u7684\u5178\u6545\u51fa\u5904\uff0c\u4f46\u4ed6\u4e0d\u6e05\u695a\u7528\u5728\u8fd9\u91cc\u662f\u4ec0\u4e48\u610f\u601d\u3002</p><p>\u5728\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u5b58\u5728\u7684\u65f6\u4ee3\uff0c\u4ea1\u7075\u9b54\u6cd5\u8fd8\u5904\u4e8e\u840c\u82bd\u72b6\u6001\uff0c\u751f\u6b7b\u7684\u754c\u9650\u6781\u4e3a\u6e05\u695a\uff0c\u51e1\u4eba\u65e0\u6cd5\u8de8\u8d8a\uff0c\u66f4\u65e0\u4ece\u6253\u7834\u3002\u6ca1\u6709\u5deb\u5996\uff0c\u6ca1\u6709\u5438\u8840\u9b3c\uff0c\u6ca1\u6709\u5e7d\u7075\uff0c\u6ca1\u6709\u4efb\u4f55\u529e\u6cd5\u53ef\u4ee5\u8ba9\u6b7b\u8005\u5e38\u9a7b\u51e1\u95f4\u3002\u5f53\u65f6\u751a\u81f3\u8fde\u6b7b\u795e\u90fd\u4e0d\u5b58\u5728\uff0c\u51e1\u4eba\u4e00\u65e6\u901d\u53bb\uff0c\u9664\u4e86\u6781\u5c11\u6570\u8654\u4fe1\u8005\u5f52\u4e8e\u8bf8\u795e\uff0c\u6781\u5c11\u6570\u4e0d\u5e78\u8005\u5760\u5165\u6df1\u6e0a\uff0c\u7edd\u5927\u591a\u6570\u90fd\u662f\u8089\u8eab\u8150\u673d\uff0c\u7075\u9b42\u6d88\u6563\uff0c\u5c31\u6b64\u6cef\u706d\u65e0\u95fb\u3002</p><p>\u4f0a\u739b\u65af\u5361\u7684\u7687\u5ba4\u5374\u638c\u63e1\u7740\u4e00\u79cd\u79d8\u672f\uff0c\u7fd4\u9f99\u79f0\u4e4b\u4e3a\u201c\u8f6e\u56de\u201d\uff0c\u51e4\u51f0\u79f0\u4e4b\u4e3a\u201c\u6d85\u69c3\u201d\uff0c\u8bf4\u6cd5\u867d\u7136\u4e0d\u540c\uff0c\u4f46\u5b9e\u9645\u5e76\u65e0\u533a\u522b\u3002\u8fd9\u79cd\u79d8\u672f\u4e0d\u80fd\u8ba9\u5947\u68b0\u5e08\u957f\u751f\u4e0d\u6b7b\uff0c\u5374\u53ef\u4ee5\u8ba9\u4ed6\u4eec\u6b7b\u800c\u91cd\u751f\u3002\u5177\u4f53\u7684\u7a0b\u5e8f\uff0c\u9996\u5148\u662f\u201c\u6b7b\u201d\uff0c\u8fd9\u7406\u6240\u5f53\u7136\uff0c\u4f46\u5728\u6b7b\u540e\uff0c\u5947\u68b0\u5e08\u7684\u7075\u9b42\u4e0d\u4f1a\u76f4\u63a5\u6d88\u6563\uff0c\u800c\u662f\u4e00\u5206\u4e3a\u4e8c\uff0c\u5176\u4e2d\u6700\u6838\u5fc3\u7684\u90e8\u5206\u4f1a\u5bc4\u5b58\u5728\u67d0\u4e2a\u6307\u5b9a\u7684\u7269\u54c1\u4e4b\u4e2d\uff08\u8fd9\u6709\u70b9\u7c7b\u4f3c\u4e8e\u5deb\u5996\u7684\u547d\u5323\uff09\uff0c\u5176\u4f59\u90e8\u5206\u5219\u4f1a\u5728\u9884\u5148\u51c6\u5907\u597d\u7684\u9b54\u6cd5\u4eea\u5f0f\u7684\u4f5c\u7528\u4e0b\uff0c\u6295\u80ce\u8f6c\u4e16\uff0c\u6700\u7ec8\u4ee5\u5a74\u513f\u5f62\u6001\u518d\u5ea6\u8bde\u751f\uff0c\u8fd9\u662f\u201c\u590d\u6d3b\u201d\uff0c\u4f46\u8fd8\u4e0d\u662f\u771f\u6b63\u7684\u201c\u91cd\u751f\u201d\u3002\u5f85\u5a74\u513f\u957f\u5927\u6210\u4eba\uff0c\u540c\u65f6\u9010\u6b65\u5730\u5c06\u7269\u54c1\u4e2d\u5bc4\u5b58\u7684\u201c\u6838\u5fc3\u201d\u53d6\u56de\uff0c\u4e0e\u81ea\u5df1\u65b0\u7684\u7075\u9b42\u76f8\u878d\u5408\uff0c\u6700\u7ec8\u6210\u578b\uff0c\u524d\u4e16\u7684\u8bb0\u5fc6\u3001\u529b\u91cf\u4e5f\u90fd\u968f\u4e4b\u6062\u590d\uff0c\u201c\u91cd\u751f\u201d\u4e8e\u662f\u771f\u6b63\u5b8c\u6210\u3002</p><p>\u8fd9\u4fbf\u662f\u201c\u6b7b\u800c\u4e0d\u4ea1\u201d\u7684\u542b\u4e49\u3002</p><p>\u743c\u6069\u6c89\u9ed8\u7740\uff0c\u5965\u5609\u83b1\u65af\u7684\u610f\u601d\u5f88\u6e05\u695a\u3002\u73ca\u5609\u662f\u201c\u7687\u5ba4\u201d\uff0c\u5979\u81ea\u7136\u53ef\u4ee5\u8f6c\u4e16\u8f6e\u56de\uff0c\u4f46\u73b0\u5728\u5979\u4ec5\u4ec5\u53ea\u662f\u590d\u6d3b\uff0c\u5e76\u6ca1\u6709\u771f\u6b63\u91cd\u751f\u3002\u963f\u62c9\u838e\u7684\u7075\u9b42\u6838\u5fc3\u8fd8\u6ca1\u6709\u88ab\u53d6\u56de\uff0c\u76f8\u5e94\u7684\u8bb0\u5fc6\u3001\u529b\u91cf\u5e76\u672a\u6062\u590d\uff0c\u6240\u4ee5\u73ca\u5609\u73b0\u5728\u8fd8\u662f\u73ca\u5609\u3002\u4f46\u5018\u82e5\u6709\u671d\u4e00\u65e5\uff0c\u963f\u62c9\u838e\u91cd\u751f\uff0c\u90a3\u4e48\u73ca\u5609\u5462\uff1f</p><p>\u201c\u91cd\u751f\u4e4b\u540e\uff0c\u8fd9\u4e00\u4e16\u7684\u8bb0\u5fc6\u662f\u5426\u4ecd\u7136\u4f1a\u4fdd\u7559\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u4e0d\u4e00\u5b9a\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u6709\u53ef\u80fd\u5168\u90e8\u4fdd\u7559\uff0c\u6709\u53ef\u80fd\u5b8c\u5168\u9057\u5fd8\uff0c\u4e0d\u8fc7\u8fd9\u4e24\u79cd\u6982\u7387\u90fd\u5f88\u4f4e\u3002\u6700\u6709\u53ef\u80fd\u7684\uff0c\u662f\u4fdd\u7559\u4e00\u90e8\u5206\uff0c\u9057\u5fd8\u4e00\u90e8\u5206\u3002\u201d</p><p>\u201c\u90a3\u4e48\u4f60\u73b0\u5728\u662f\u5728\u5524\u9192\u963f\u62c9\u838e\uff1f\u201d</p><p>\u201c\u81ea\u7136\u3002\u201d</p><p>\u201c\u73ca\u5609\u5462\uff1f\u201d</p><p>\u201c\u65e0\u8bba\u5979\u53eb\u4ec0\u4e48\u540d\u5b57\uff0c\u65e0\u8bba\u5979\u8bb0\u5f97\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5979\u90fd\u662f\u6211\u7684\u5973\u513f\uff0c\u5bf9\u4e8e\u6211\u800c\u8a00\uff0c\u8fd9\u6ca1\u6709\u533a\u522b\u3002\u201d</p><p>\u662f\u7684\uff0c\u4f46\u5bf9\u743c\u6069\u800c\u8a00\uff0c\u8fd9\u533a\u522b\u5f88\u5927\u3002</p><p>\u201c\u5979\u7684\u7075\u9b42\u6838\u5fc3\u5bc4\u5b58\u5728\u54ea\u91cc\uff1f\u201d\u743c\u6069\u53c8\u95ee\uff0c\u201c\u4f60\u7684\u90a3\u672c\u300a\u547d\u8fd0\u957f\u591c\u300b\uff1f\u201d</p><p>\u201c\u5bf9\u3002\u201d</p><p>\u743c\u6069\u4e8e\u662f\u660e\u767d\u8fc7\u6765\u3002</p><p>\u963f\u62c9\u838e\u5c06\u7075\u9b42\u6838\u5fc3\u5bc4\u5b58\u5728\u300a\u547d\u8fd0\u957f\u591c\u300b\u4e4b\u4e2d\uff0c\u5979\u73b0\u5728\u6b63\u5728\u9010\u6b65\u53d6\u56de\uff0c\u4f46\u90a3\u672c\u4e66\u540c\u6837\u8fd8\u662f\u5965\u5609\u83b1\u65af\u7684\u5e7d\u7075\u201c\u5bb9\u5668\u201d\u3002\u5982\u6b64\u4e00\u6765\uff0c\u73ca\u5609\u7684\u53d8\u5f3a\u548c\u5965\u5609\u83b1\u65af\u7684\u8870\u5f31\uff0c\u8fd9\u4e24\u4e2a\u539f\u672c\u770b\u4f3c\u6beb\u65e0\u5173\u8054\u7684\u4e8b\u60c5\uff0c\u4fbf\u6709\u4e86\u4ea4\u96c6\u3002\u81f3\u4e8e\u5177\u4f53\u7684\u7ec6\u8282\uff0c\u743c\u6069\u5c31\u4e0d\u5f97\u800c\u77e5\u4e86\uff0c\u6599\u60f3\u5965\u5609\u83b1\u65af\u4e5f\u4e0d\u4f1a\u8bf4\u3002</p><p>\u201c\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u627e\u4e2a\u5b89\u5168\u7684\u5730\u65b9\uff0c\u53cd\u800c\u8981\u6765\u8fd9\u91cc\u5462\uff1f\u201d</p><p>\u201c\u8fd9\u5c31\u662f\u6211\u8981\u544a\u8bc9\u4f60\u7684\u7b2c\u4e8c\u4ef6\u4e8b\u60c5\uff1a\u7b2c\u4e94\u79d8\u5668\u771f\u6b63\u7684\u7528\u9014\u3002\u201d</p><p>\u201c\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u90a3\u4e2a\u8bcd\u5f88\u62d7\u53e3\uff0c\u6b27\u8d1d\u4f26\u548c\u6211\u63d0\u8fc7\u597d\u51e0\u6b21\uff0c\u6211\u603b\u662f\u8bf4\u4e0d\u51c6\u2014\u2014\u4f46\u6211\u60f3\u4f60\u5e94\u8be5\u6ca1\u8fd9\u4e2a\u95ee\u9898\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u6162\u6162\u5730\u5410\u51fa\u51e0\u4e2a\u97f3\u8282\uff0c\u6709\u70b9\u522b\u626d\uff0c\u4f46\u743c\u6069\u8fd8\u662f\u80fd\u591f\u542c\u5f97\u5f88\u6e05\u695a\uff0c\u201c\u5e7d\u3001\u51a5\u3001\u5730\u3001\u5e9c\u3001\u751f\u3001\u6b7b\u3001\u8f6e\u3001\u56de\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u5e7d\u51a5\u5730\u5e9c\uff0c\u751f\u6b7b\u8f6e\u56de\u3002</p><p>\u743c\u6069\u5f53\u7136\u660e\u767d\u8fd9\u4e2a\u8bcd\u2014\u2014\u51c6\u786e\u5730\u8bf4\u662f\u4e24\u4e2a\u8bcd\u2014\u2014\u7684\u610f\u601d\uff0c\u4f46\u4ed6\u4fdd\u6301\u6c89\u9ed8\uff0c\u9759\u9759\u5730\u7b49\u5f85\u7740\u5965\u5609\u83b1\u65af\u7684\u89e3\u91ca\u3002</p><p>\u201c\u8f6e\u56de\uff0c\u6216\u8005\u6d85\u69c3\uff0c\u98ce\u9669\u5f88\u9ad8\u3002\u201d</p><p>\u5728\u6ca1\u6709\u4ea1\u7075\u9b54\u6cd5\u7684\u65f6\u4ee3\uff0c\u4f0a\u739b\u65af\u5361\u201c\u7687\u5ba4\u201d\u7684\u79d8\u672f\uff0c\u6253\u7834\u751f\u6b7b\u754c\u9650\uff0c\u903e\u8d8a\u9634\u9633\u5206\u91ce\uff0c\u8fd1\u4e4e\u8fbe\u5230\u8ba9\u51e1\u4eba\u201c\u6c38\u751f\u4e0d\u6b7b\u201d\u7684\u6548\u679c\u3002\u4f46\u8fd9\u79cd\u79d8\u672f\u5e76\u4e0d\u662f\u6ca1\u6709\u98ce\u9669\u7684\uff0c\u66f4\u4e0d\u662f\u767e\u5206\u4e4b\u767e\u4fdd\u8bc1\u6210\u529f\uff0c\u6070\u6070\u76f8\u53cd\uff0c\u5931\u8d25\u7387\u8fd8\u4e0d\u4f4e\u3002\u800c\u4e14\u201c\u8f6e\u56de\u201d\u6216\u8005\u201c\u6d85\u69c3\u201d\u7684\u6b21\u6570\u8d8a\u591a\uff0c\u51fa\u95ee\u9898\u7684\u6982\u7387\u5c31\u8d8a\u9ad8\uff0c\u5c24\u5176\u662f\u6700\u540e\u4e00\u6b65\uff0c\u5f88\u591a\u4eba\u4e0d\u80fd\u987a\u5229\u53d6\u56de\u201c\u6838\u5fc3\u201d\uff1b\u6216\u8005\u5c3d\u7ba1\u53d6\u56de\uff0c\u5374\u6b8b\u7f3a\u4e0d\u5168\uff1b\u6216\u8005\u5b8c\u6574\u53d6\u56de\uff0c\u5374\u65e0\u6cd5\u4e0e\u65b0\u7684\u7075\u9b42\u76f8\u5bb9\uff1b\u6216\u8005\u8868\u9762\u4e0a\u80fd\u591f\u76f8\u5bb9\uff0c\u5b9e\u9645\u5374\u57cb\u4e0b\u9690\u60a3\uff0c\u591a\u5e74\u4ee5\u540e\u7a81\u7136\u53d1\u4f5c\uff0c\u8ba9\u4eba\u795e\u667a\u9519\u4e71\uff0c\u7b49\u7b49\u7b49\u7b49\uff0c\u5404\u79cd\u95ee\u9898\uff0c\u4e0d\u4e00\u800c\u8db3\u3002</p><p>\u8fd9\u5f88\u6b63\u5e38\uff0c\u4eba\u6bd5\u7adf\u4e0d\u662f\u5355\u7eaf\u7684\u5bb9\u5668\uff0c\u4e0d\u53ef\u80fd\u4ec0\u4e48\u4e71\u4e03\u516b\u7cdf\u7684\u4e1c\u897f\u90fd\u5f80\u91cc\u585e\u3002\u8f6e\u56de\u4e00\u6b21\uff0c\u5c31\u76f8\u5f53\u4e8e\u585e\u8fdb\u6765\u4e00\u5806\u65b0\u4e1c\u897f\uff1b\u5018\u82e5\u8f6e\u56de\u4e2a\u5341\u6b21\u516b\u6b21\uff0c\u7cbe\u795e\u5d29\u6e83\u662f\u5f88\u5408\u7406\u7684\u4e8b\u60c5\u3002</p><p>\u8fd9\u4e2a\u95ee\u9898\u5f88\u9ebb\u70e6\uff0c\u5947\u68b0\u5e08\u4eec\u867d\u7136\u795e\u901a\u5e7f\u5927\uff0c\u5374\u4e5f\u59cb\u7ec8\u6ca1\u6cd5\u89e3\u51b3\uff0c\u76f4\u5230\u67d0\u4e00\u5929\uff0c\u6709\u4eba\u63d0\u51fa\u4e86\u4e00\u4e2a\u60ca\u4eba\u7684\u8bbe\u60f3\u3002</p><p>\u6309\u7167\u8fd9\u4e2a\u8bbe\u60f3\uff0c\u5947\u68b0\u5e08\u501f\u52a9\u4e0b\u5c42\u754c\u90aa\u9b54\u7684\u529b\u91cf\uff0c\u82b1\u8d39\u6570\u5341\u5e74\u65f6\u95f4\uff0c\u521b\u9020\u51fa\u4e86\u4e00\u4e2a\u9b54\u6cd5\u7269\u54c1\u3002\u5b83\u80fd\u591f\u521b\u9020\u51fa\u4e00\u4e2a\u5c01\u7edd\u5929\u5730\u7684\u72ec\u7acb\u201c\u9886\u57df\u201d\uff0c\u6709\u79cd\u79cd\u4e0d\u53ef\u601d\u8bae\u7684\u5a01\u80fd\uff0c\u4f46\u5bf9\u4e8e\u5947\u68b0\u5e08\u800c\u8a00\uff0c\u5176\u6700\u5927\u7684\u4ef7\u503c\u5c31\u5728\u4e8e\uff1a\u5728\u5947\u68b0\u5e08\u7684\u4e3b\u6301\u4e4b\u4e0b\uff0c\u53d1\u52a8\u8fd9\u4e2a\u201c\u9886\u57df\u201d\u7684\u529b\u91cf\u52a0\u4ee5\u5e87\u62a4\uff0c\u8f6c\u4e16\u91cd\u751f\u7684\u98ce\u9669\u4f1a\u88ab\u964d\u5230\u6700\u4f4e\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u96f6\u3002</p><p>\u5f88\u663e\u7136\uff0c\u90a3\u5c31\u662f\u7b2c\u4e94\u79d8\u5668\u201c\u4e5d\u91cd\u5730\u72f1\u4e4b\u9f0e\u201d\u3002\u800c\u6614\u65e5\u5728\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\uff0c\u6267\u638c\u7b2c\u4e94\u79d8\u5668\u7684\u5947\u68b0\u5e08\uff0c\u5219\u88ab\u79f0\u4e3a\u201c\u51a5\u738b\u201d\u3002</p><p>\u201c\u6309\u7167\u5947\u68b0\u5e08\u7684\u7ecf\u9a8c\uff0c\u2018\u91cd\u751f\u2019\u8d8a\u65e9\u8d8a\u597d\uff0c\u6700\u8fdf\u4e0d\u80fd\u8d85\u8fc7\u5341\u516b\u5c81\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u5e74\u9f84\u8d8a\u957f\uff0c\u5931\u8d25\u7684\u6982\u7387\u8d8a\u9ad8\u3002\u201d</p><p>\u8fd9\u662f\u7406\u6240\u5f53\u7136\u7684\uff0c\u91cd\u751f\u662f\u8981\u53d6\u56de\u5bc4\u5b58\u5728\u5916\u7684\u7075\u9b42\u201c\u6838\u5fc3\u201d\uff0c\u76f8\u5f53\u4e8e\u6b64\u751f\u4e0e\u524d\u4e16\u878d\u5408\u3002\u4e00\u4e2a\u676f\u5b50\u5982\u679c\u662f\u7a7a\u7684\uff0c\u5b83\u5f88\u5bb9\u6613\u6ce8\u5165\u6db2\u4f53\uff1b\u4f46\u5982\u679c\u5b83\u672c\u8eab\u5df2\u7ecf\u6ee1\u4e86\uff0c\u90a3\u8981\u518d\u52a0\u6c34\u8fdb\u6765\u5c31\u5f88\u4e0d\u5bb9\u6613\u4e86\u3002\u4eba\u5c31\u50cf\u4e00\u4e2a\u676f\u5b50\uff0c\u5e74\u9f84\u8d8a\u957f\uff0c\u5386\u4e8b\u8d8a\u591a\uff0c\u676f\u5b50\u5c31\u8d8a\u6ee1\uff0c\u60f3\u518d\u6dfb\u52a0\u65b0\u4e1c\u897f\u96be\u5ea6\u5c31\u8d8a\u9ad8\uff0c\u5f3a\u884c\u5f80\u91cc\u704c\u7684\u8bdd\uff0c\u6491\u7206\u70b8\u88c2\u4e5f\u4e0d\u8db3\u4e3a\u5947\u3002</p><p>\u743c\u6069\u8bb0\u5f97\u5f88\u6e05\u695a\uff0c\u81ea\u5df1\u5df2\u7ecf\u5341\u4e03\u5c81\uff0c\u73ca\u5609\u4e0e\u4ed6\u540c\u65e5\u51fa\u751f\u3002\u4e5f\u5373\u662f\u8bf4\uff0c\u73ca\u5609\u73b0\u5728\u624d\u5f00\u59cb\u201c\u91cd\u751f\u201d\uff0c\u98ce\u9669\u975e\u5e38\u9ad8\uff0c\u56e0\u6b64\u80fd\u591f\u964d\u4f4e\u98ce\u9669\u7684\u7b2c\u4e94\u79d8\u5668\u5c31\u975e\u5e38\u91cd\u8981\u4e86\u3002</p><p>\u201c\u90a3\u9700\u8981\u6211\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u9700\u8981\u4f60\u6210\u4e3a\u2018\u51a5\u738b\u2019\u3002\u201d\u5965\u5609\u83b1\u65af\u56de\u7b54\u3002</p><p>\u7b2c\u4e94\u79d8\u5668\u4e0d\u662f\u5168\u81ea\u52a8\u7684\uff0c\u8981\u6253\u5f00\u201c\u5e7d\u51a5\u5730\u5e9c\u201d\uff0c\u5b8c\u6210\u201c\u751f\u6b7b\u8f6e\u56de\u201d\uff0c\u5fc5\u987b\u6709\u201c\u51a5\u738b\u201d\u7684\u4e3b\u6301\u3002\u743c\u6069\u662f\u201c\u7fd4\u9f99\u201d\uff0c\u7b2c\u4e94\u79d8\u5668\u539f\u5c5e\u201c\u7fd4\u9f99\u201d\u4e00\u7cfb\uff0c\u4ed6\u81ea\u7136\u6709\u8d44\u683c\u6765\u505a\u51a5\u738b\uff0c\u95ee\u9898\u5728\u4e8e\uff0c\u73b0\u5728\u7b2c\u4e94\u79d8\u5668\u5e76\u4e0d\u5728\u4ed6\u624b\u91cc\u554a\uff0c\u60f3\u63a7\u5236\u4e5f\u65e0\u4ece\u7740\u624b\u3002</p><p>\u201c\u6240\u4ee5\u662f\u8981\u6211\u53bb\u548c\u51ef\u745f\u7433\u5546\u91cf\uff0c\u8bf7\u5979\u534f\u52a9\uff1f\u201d</p><p>\u73b0\u5728\u638c\u63a7\u7b2c\u4e94\u79d8\u5668\u7684\u662f\u51ef\u745f\u7433\uff0c\u743c\u6069\u5e76\u672a\u548c\u5965\u5609\u83b1\u65af\u63d0\u8d77\u8fc7\u8fd9\u4ef6\u4e8b\uff0c\u4f46\u76f8\u4fe1\u5979\u80af\u5b9a\u6e05\u695a\u3002\u201c\u4e0d\u662f\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u51e4\u51f0\u6ca1\u529e\u6cd5\u53d1\u52a8\u7fd4\u9f99\u79d8\u5668\u7684\u6700\u6838\u5fc3\u529f\u80fd\u3002\u4f60\u5fc5\u987b\u53d6\u800c\u4ee3\u4e4b\u3002\u201d</p><p>\u53d6\u800c\u4ee3\u4e4b\uff1f</p><p>\u201c\u6709\u4eba\u66fe\u7ecf\u548c\u6211\u63d0\u8d77\u8fc7\uff0c\u8bf4\u4e03\u79d8\u5668\u6709\u4e00\u5957\u4eff\u5236\u54c1\uff0c\u79f0\u4e4b\u4e3a\u2018\u4e03\u5f71\u5668\u2019\u3002\u201d\u743c\u6069\u7a81\u7136\u8bf4\u3002</p><p>\u624e\u745e\u5c14\u544a\u8bc9\u743c\u6069\uff0c\u5728\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u672b\u671f\uff0c\u66fe\u7ecf\u6709\u5947\u68b0\u5e08\u4ee5\u4e03\u79d8\u5668\u4e3a\u8303\u672c\u953b\u9020\u51fa\u4eff\u5236\u54c1\uff0c\u79f0\u4e4b\u4e3a\u201c\u4e03\u5f71\u5668\u201d\u3002\u5f71\u5668\u6700\u5927\u7684\u4f18\u52bf\uff0c\u5c31\u662f\u5b83\u6ca1\u6709\u8840\u8109\u9650\u5b9a\uff0c\u5e76\u975e\u201c\u7687\u5ba4\u201d\u4e13\u5c5e\uff0c\u5176\u4ed6\u5947\u68b0\u5e08\u4e5f\u53ef\u4ee5\u4f7f\u7528\u3002</p><p>\u201c\u55ef\uff0c\u662f\u6709\uff0c\u800c\u4e14\u5176\u4e2d\u4e24\u4ef6\u73b0\u5728\u5c31\u5728\u6211\u624b\u91cc\uff0c\u4f46\u5e76\u4e0d\u5305\u62ec\u7b2c\u4e94\u5f71\u5668\uff0c\u201d\u5965\u5609\u83b1\u65af\u77e5\u9053\u743c\u6069\u7684\u610f\u601d\uff0c\u201c\u800c\u4e14\u5f71\u5668\u867d\u7136\u5f88\u4e0d\u9519\uff0c\u4f46\u7ec8\u7a76\u4e0d\u662f\u539f\u7248\uff0c\u6709\u4e9b\u529f\u80fd\u662f\u6ca1\u6709\u7684\u3002\u7b2c\u4e94\u5f71\u5668\u5e76\u4e0d\u5177\u5907\u2018\u5e7d\u51a5\u5730\u5e9c\u2019\u7684\u80fd\u529b\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u743c\u6069\u601d\u8003\u7740\uff0c\u6162\u6162\u6d88\u5316\u8fd9\u4e9b\u4fe1\u606f\u3002\u5965\u5609\u83b1\u65af\u786e\u5b9e\u89e3\u91ca\u4e86\u4e0d\u5c11\u95ee\u9898\uff0c\u4f46\u743c\u6069\u4e0d\u76f8\u4fe1\u5979\u5df2\u7ecf\u5c06\u6240\u6709\u7684\u4e8b\u60c5\u5766\u8bda\u76f8\u544a\uff0c\u5fc5\u7136\u6709\u67d0\u4e9b\u4e1c\u897f\uff0c\u67d0\u4e9b\u5173\u952e\u4fe1\u606f\u88ab\u9690\u7792\u4e86\u3002\u201c\u6709\u4e00\u4ef6\u4e8b\u6211\u5fc5\u987b\u8bf4\u6e05\u695a\uff0c\u201d\u4ed6\u8bf4\uff0c\u51b3\u5b9a\u8bd5\u63a2\u4e00\u4e0b\uff0c\u201c\u6211\u559c\u6b22\u73ca\u5609\u73b0\u5728\u7684\u6837\u5b50\uff0c\u6211\u4e0d\u5e0c\u671b\u5979\u53d8\u6210\u53e6\u5916\u4e00\u4e2a\u4eba\u2014\u2014\u65e0\u8bba\u8fd9\u4e2a\u4eba\u548c\u5979\u6709\u591a\u4e48\u5bc6\u5207\u7684\u5173\u7cfb\u3002\u201d</p><p>\u201c\u55ef\uff0c\u6211\u77e5\u9053\u3002\u201d</p><p>\u201c\u6240\u4ee5\u6211\u4eec\u7684\u76ee\u6807\u662f\u77db\u76fe\u7684\uff0c\u201d\u743c\u6069\u6307\u51fa\uff0c\u201c\u4f60\u5e0c\u671b\u73ca\u5609\u53d8\u6210\u963f\u62c9\u838e\uff0c\u6211\u5e0c\u671b\u5979\u5c31\u662f\u73ca\u5609\u3002\u201d</p><p>\u201c\u4f60\u9519\u4e86\uff0c\u9996\u5148\uff0c\u6211\u5e76\u4e0d\u662f\u5e0c\u671b\u73ca\u5609\u53d8\u6210\u963f\u62c9\u838e\uff0c\u5979\u4eec\u4e8e\u6211\u800c\u8a00\u5e76\u65e0\u5206\u522b\uff1b\u5176\u6b21\uff0c\u65e2\u7136\u4f60\u5e0c\u671b\u73ca\u5609\u4ecd\u7136\u662f\u73ca\u5609\uff0c\u90a3\u5c31\u66f4\u8981\u6309\u6211\u8bf4\u7684\u53bb\u505a\u3002\u56e0\u4e3a\u53ea\u6709\u8fd9\u6837\uff0c\u624d\u80fd\u4fdd\u8bc1\u5979\u8fd9\u4e00\u4e16\u7684\u8bb0\u5fc6\u4f1a\u88ab\u5b8c\u6574\u4fdd\u7559\u4e0b\u6765\u3002\u201d</p><p>\u7b2c\u4e94\u79d8\u5668\u7684\u6838\u5fc3\u529f\u80fd\u201c\u5e7d\u51a5\u5730\u5e9c\u201d\uff0c\u4e0d\u4ec5\u4ec5\u53ef\u4ee5\u4e3a\u8f6e\u56de\u91cd\u751f\u7684\u5947\u68b0\u5e08\u63d0\u4f9b\u5b89\u5168\u4fdd\u969c\uff0c\u800c\u4e14\u53ef\u4ee5\u5c06\u524d\u4e16\u4eca\u751f\u7684\u8bb0\u5fc6\u90fd\u5b8c\u6574\u4fdd\u7559\u4e0b</p><p>\u201c\u6240\u4ee5\u4f60\u6709\u4e24\u79cd\u9009\u62e9\uff0c\u8981\u4e48\u963b\u6b62\u5979\u2018\u91cd\u751f\u2019\uff1b\u8981\u4e48\u5c31\u6309\u6211\u8bf4\u7684\u53bb\u505a\u3002\u201d</p><p>\u743c\u6069\u4e0d\u4f1a\u963b\u6b62\uff0c\u4ed6\u6ca1\u8fd9\u4e2a\u8d44\u683c\uff0c\u4ed6\u53ea\u80fd\u5c0a\u91cd\u73ca\u5609\u81ea\u5df1\u7684\u9009\u62e9\u3002\u7eb5\u7136\u4eb2\u4e3a\u59d0\u5f1f\u3001\u7231\u4fa3\uff0c\u4f46\u6709\u4e9b\u754c\u9650\u662f\u4e0d\u80fd\u903e\u8d8a\u7684\u3002</p><p>\u6240\u4ee5\u4ed6\u5b9e\u9645\u4e0a\u522b\u65e0\u9009\u62e9\u3002</p><p>\u201c\u8fd8\u6709\u4e00\u4e2a\u95ee\u9898\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u542c\u8bf4\uff0c\u7b2c\u4e94\u79d8\u5668\u5728\u53d1\u52a8\u8fc7\u7a0b\u4e2d\u662f\u6ca1\u529e\u6cd5\u81ea\u7531\u66f4\u6362\u64cd\u63a7\u8005\u7684\uff0c\u53ea\u80fd\u901a\u8fc7\u7ee7\u4efb\u8005\u6740\u6b7b\u539f\u64cd\u63a7\u8005\u7684\u65b9\u5f0f\u3002\u201d</p><p>\u201c\u662f\u8fd9\u6837\u6ca1\u9519\u3002\u201d</p><p>\u201c\u4f60\u4e4b\u524d\u66fe\u7ecf\u548c\u6211\u63d0\u8fc7\u4e00\u4e2a\u2018\u9ed1\u6697\u51e4\u51f0\u516c\u4e3b\u4e0e\u6d88\u901d\u4e4b\u9f99\u7684\u4f20\u8bf4\u2019\uff0c\u6211\u60f3\u542c\u542c\u8fd9\u4e2a\u6545\u4e8b\u3002\u201d</p><p>\u201c\u5df2\u7ecf\u6709\u4eba\u5bf9\u4f60\u8bb2\u8fc7\u8fd9\u4e2a\u6545\u4e8b\uff0c\u4e0d\u662f\u5417\uff1f\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u4f60\u60f3\u77e5\u9053\u4ec0\u4e48\uff0c\u6211\u5f88\u6e05\u695a\uff0c\u4f46\u6211\u6ca1\u529e\u6cd5\u7ed9\u4e00\u4e2a\u786e\u5b9a\u7684\u7b54\u6848\u3002\u6211\u4e0d\u662f\u5f53\u4e8b\u4eba\uff0c\u66f4\u4e0d\u662f\u4eb2\u5386\u8005\uff0c\u6211\u542c\u5230\u7684\u8bf4\u6cd5\uff0c\u540c\u6837\u4e5f\u662f\u5df2\u7ecf\u7ecf\u8fc7\u4e86\u5343\u5e74\u6d41\u4f20\u7684\u7248\u672c\u2014\u2014\u800c\u4e14\u8fd8\u4e0d\u6b62\u4e00\u4e2a\u7248\u672c\u3002\u4f46\u4ee5\u6211\u4e4b\u89c1\uff0c\u8fd9\u79cd\u53ef\u80fd\u6027\u786e\u5b9e\u5f88\u9ad8\uff0c\u56e0\u4e3a\u5728\u6240\u6709\u7684\u7248\u672c\u91cc\uff0c\u5c3d\u7ba1\u7ec6\u8282\u90e8\u5206\u5f80\u5f80\u4e92\u76f8\u77db\u76fe\uff0c\u4f46\u6709\u4e00\u70b9\u63cf\u8ff0\u662f\u5171\u901a\u7684\uff1a\u90a3\u4f4d\u7fd4\u9f99\u738b\u5b50\u59ec\u59be\u4f17\u591a\uff0c\u5176\u4e2d\u6709\u4e00\u4f4d\uff0c\u662f\u6765\u81ea\u4e0b\u5c42\u754c\u7684\u5927\u9b54\u9b3c\u3002\u201d</p><p>\u743c\u6069\u89c9\u5f97\u8fd9\u53e5\u8bdd\u5f88\u6709\u9053\u7406\u3002\u9ed1\u6697\u51e4\u51f0\u516c\u4e3b\u7684\u6545\u4e8b\uff0c\u5965\u5609\u83b1\u65af\u548c\u51ef\u5c14\u672c\u90fd\u63d0\u5230\u8fc7\uff0c\u4f46\u4ed6\u4eec\u53c8\u4e0d\u662f\u4eb2\u5386\u8005\uff0c\u4e5f\u662f\u542c\u522b\u4eba\u8f6c\u8ff0\u800c\u6765\u7684\uff0c\u6ca1\u8d44\u683c\u505a\u5224\u65ad\u3002\u771f\u6b63\u6709\u8d44\u683c\u505a\u5224\u65ad\u7684\u662f\u5f53\u4e8b\u4eba\uff0c\u6bd4\u5982\u67d0\u4f4d\u5927\u9b54\u9b3c\uff0c\u522b\u4eba\u4f1a\u8ba4\u9519\uff0c\u5979\u603b\u4e0d\u4f1a\u8ba4\u9519\u3002</p><p>\u4f46\u5982\u679c\u624e\u745e\u5c14\u6240\u8bf4\u5c5e\u5b9e\uff0c\u743c\u6069\u548c\u51ef\u745f\u7433\u5c31\u662f\u59d0\u5f1f\u517c\u7231\u4eba\u7684\u5173\u7cfb\uff0c\u7c7b\u4f3c\u4e8e\u4ed6\u548c\u73ca\u5609\u3002\u6240\u4ee5\u5965\u5609\u83b1\u65af\u7684\u610f\u601d\uff0c\u662f\u8981\u743c\u6069\u4e3a\u4e86\u73ca\u5609\uff0c\u53bb\u6740\u6b7b\u51ef\u745f\u7433\uff1f\u8fd9\u7b97\u4ec0\u4e48\uff0c\u67d0\u79cd\u5f62\u5f0f\u7684\u8003\u9a8c\u4e48\uff1f</p><p>\u201c\u6211\u6ca1\u6709\u90a3\u4e48\u65e0\u804a\uff0c\u201d\u4eff\u4f5b\u770b\u51fa\u743c\u6069\u7684\u5fc3\u601d\uff0c\u5965\u5609\u83b1\u65af\u6de1\u6de1\u8bf4\uff0c\u201c\u66f4\u4f55\u51b5\uff0c\u5982\u679c\u6b64\u523b\u63a7\u5236\u7b2c\u4e94\u79d8\u5668\u7684\uff0c\u771f\u7684\u662f\u90a3\u4f4d\u2018\u9ed1\u6697\u51e4\u51f0\u516c\u4e3b\u2019\uff0c\u5c31\u51ed\u4f60\u73b0\u5728\u8fd9\u70b9\u672c\u4e8b\uff0c\u6839\u672c\u4e0d\u53ef\u80fd\u662f\u5979\u7684\u5bf9\u624b\u3002\u201d</p><p>\u201c\u4f60\u2026\u2026\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u8fd9\u5c31\u662f\u6211\u8981\u544a\u8bc9\u4f60\u7684\u7b2c\u4e09\u4ef6\u4e8b\u60c5\uff1a\u4f60\u6240\u89c1\u5230\u7684\u90a3\u4f4d\u2018\u51ef\u745f\u7433\u2019\uff0c\u5e76\u4e0d\u662f\u672c\u4f53\uff0c\u53ea\u662f\u4e00\u4e2a\u2018\u5bb9\u5668\u2019\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u5bb9\u5668\uff1f</p><p>\u743c\u6069\u5f7b\u5e95\u88ab\u5f04\u7cca\u6d82\u4e86\uff0c\u5b8c\u5168\u4e0d\u77e5\u9053\u5965\u5609\u83b1\u65af\u5728\u8bf4\u4ec0\u4e48\u3002\u4e0d\u8fc7\u5927\u5965\u672f\u5e08\u5f88\u5feb\u505a\u51fa\u4e86\u89e3\u91ca\uff0c\u201c\u6240\u8c13\u5bb9\u5668\uff0c\u5c31\u662f\u4e00\u4e2a\u5047\u8eab\uff0c\u6ce8\u5165\u4e86\u4e00\u70b9\u5947\u68b0\u5e08\u7684\u610f\u8bc6\uff0c\u7c7b\u4f3c\u4e8e\u2026\u2026\u63d0\u7ebf\u5080\u5121\u3002\u201d</p><p>\u8fd9\u662f\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u665a\u671f\u7684\u53d1\u660e\uff0c\u7b97\u662f\u7814\u7a76\u201c\u79d8\u5076\u201d\u8fc7\u7a0b\u4e2d\u610f\u5916\u8bde\u751f\u7684\u526f\u4ea7\u54c1\u3002\u5947\u68b0\u5e08\u4eec\u4e00\u76f4\u81f4\u529b\u4e8e\u521b\u9020\u66f4\u5148\u8fdb\u7684\u70bc\u91d1\u9b54\u50cf\uff0c\u63d0\u51fa\u5404\u79cd\u5404\u6837\u7684\u8bbe\u60f3\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u8bbe\u60f3\u662f\u5c06\u5947\u68b0\u5e08\u81ea\u5df1\u7684\u201c\u610f\u8bc6\u201d\u5206\u79bb\u51fa\u4e00\u90e8\u5206\uff0c\u6ce8\u5165\u9b54\u50cf\u4e4b\u4e2d\uff0c\u4ece\u800c\u8ba9\u5b83\u53d8\u5f97\u66f4\u806a\u660e\u66f4\u667a\u80fd\u3002\u8fd9\u4e2a\u60f3\u6cd5\u8d77\u521d\u5931\u8d25\u4e86\uff0c</p><p>\u90a3\u4e9b\u63a5\u53d7\u4e86\u5947\u68b0\u5e08\u610f\u8bc6\u7684\u9b54\u50cf\u4ecd\u7136\u5f88\u7b28\u5f88\u5446\uff0c\u4f46\u540e\u6765\u53c8\u6709\u4eba\u53e6\u8f9f\u8e4a\u5f84\uff0c\u628a\u9b54\u50cf\u66ff\u6362\u6210\u52a8\u7269\uff0c\u7ed3\u679c\u9887\u6709\u6210\u6548\u3002\u6709\u6bb5\u65f6\u95f4\uff0c\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u4e0a\u5c42\u53d8\u5f97\u8d85\u7ea7\u6709\u7231\u5fc3\uff0c\u6bcf\u4e2a\u5947\u68b0\u5e08\u51fa\u95e8\u90fd\u5e26\u7740\u597d\u51e0\u53ea\u5ba0\u7269\uff0c\u5b83\u4eec\u806a\u660e\u4f36\u4fd0\uff0c\u80fd\u8a00\u5584\u8bed\uff0c\u6df1\u5f97\u4e3b\u4eba\u6b22\u5fc3\u3002\u76f4\u5230\u6709\u4e00\u5929\uff0c\u67d0\u4e2a\u4e27\u5fc3\u75c5\u72c2\u7684\u201c\u5b66\u8005\u201d\u5947\u68b0\u5e08\u4e0d\u6ee1\u8db3\u4e8e\u73b0\u72b6\uff0c\u5e72\u8106\u62ff\u4eba\u6765\u505a\u8bd5\u9a8c\uff0c\u5728\u5931\u8d25\u65e0\u6570\u6b21\uff0c\u201c\u635f\u8017\u201d\u4e86\u8fd1\u767e\u6761\u4eba\u547d\u540e\uff0c\u4ed6\u7ec8\u4e8e\u53d1\u660e\u51fa\u4e86\u540e\u6765\u88ab\u547d\u540d\u4e3a\u201c\u610f\u5fd7\u5ef6\u4f38\u201d\u7684\u6280\u672f\u3002</p><p>\u5229\u7528\u8fd9\u79cd\u6280\u672f\uff0c\u5947\u68b0\u5e08\u53ef\u4ee5\u5c06\u4e00\u4e2a\u4eba\u7c7b\u5236\u4f5c\u6210\u81ea\u5df1\u7684\u66ff\u8eab\u2014\u2014\u8bf4\u7a7f\u4e86\uff0c\u5c31\u662f\u628a\u8fd9\u4e2a\u4eba\u7684\u81ea\u6211\u610f\u8bc6\u5b8c\u5168\u6d17\u53bb\uff0c\u7136\u540e\u6ce8\u5165\u5947\u68b0\u5e08\u7684\u610f\u8bc6\uff0c\u4ece\u800c\u53d8\u6210\u53e6\u4e00\u4e2a\u8fd1\u4f3c\u7684\u81ea\u5df1\uff0c\u5947\u68b0\u5e08\u79f0\u4e4b\u4e3a\u201c\u5bb9\u5668\u201d\u3002</p><p>\u201c\u610f\u5fd7\u5ef6\u4f38\u201d\u5e76\u975e\u7687\u5ba4\u4e13\u5c5e\u7684\u6280\u672f\uff0c\u5b83\u6700\u521d\u7684\u7248\u672c\u5c31\u662f\u4e00\u540d\u201c\u5b66\u8005\u201d\u53d1\u660e\u7684\uff0c\u540e\u6765\u7ecf\u8fc7\u5f88\u591a\u5947\u68b0\u5e08\u7684\u4fee\u8865\u3001\u5b8c\u5584\uff0c\u6700\u7ec8\u6210\u578b\u3002\u5965\u5609\u83b1\u65af\u66fe\u7ecf\u4ece\u6b27\u8d1d\u4f26\u5904\u5b66\u4e60\u8fc7\u8fd9\u95e8\u6280\u827a\uff0c\u867d\u7136\u4e0d\u7b97\u7279\u522b\u7cbe\u901a\uff0c\u4f46\u8fa8\u8ba4\u4e00\u4e2a\u4eba\u662f\u5426\u201c\u5bb9\u5668\u201d\uff0c\u8fd8\u662f\u6ca1\u95ee\u9898\u7684\u3002\u6839\u636e\u5979\u7684\u5224\u65ad\uff0c\u51fa\u73b0\u5728\u743c\u6069\u9762\u524d\u7684\u51ef\u745f\u7433\uff0c\u4e43\u662f\u201c\u5bb9\u5668\u201d\uff0c\u800c\u975e\u672c\u4f53\u3002\u81f3\u4e8e\u672c\u4f53\u4f4d\u4e8e\u4f55\u65b9\uff0c\u5979\u5c31\u65e0\u4ece\u77e5\u6653\u4e86\u3002</p><p>\u201c\u5bb9\u5668\u6709\u4ec0\u4e48\u53ef\u4f9b\u8fa8\u522b\u7684\u7279\u5f81\u5417\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u80fd\u591f\u8ba9\u4eba\u5c06\u5176\u4e0e\u672c\u4f53\u533a\u5206\u5f00\u6765\u3002\u201d</p><p>\u201c\u533a\u522b\u81ea\u7136\u662f\u6709\uff0c\u4f46\u53ea\u6709\u540c\u6837\u638c\u63e1\u8fd9\u95e8\u6280\u827a\u7684\u4eba\u624d\u80fd\u770b\u51fa\u6765\uff0c\u6240\u4ee5\u6211\u65e0\u6cd5\u5411\u4f60\u5c55\u793a\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u743c\u6069\u5c06\u4fe1\u5c06\u7591\uff0c\u6216\u8005\u8bf4\u6000\u7591\u591a\u4e8e\u76f8\u4fe1\u3002\u5e76\u975e\u5965\u5609\u83b1\u65af\u7684\u8bf4\u6cd5\u6709\u4ec0\u4e48\u7834\u7efd\uff0c\u800c\u662f\u5979\u7684\u7acb\u573a\u95ee\u9898\uff0c\u8ba9\u743c\u6069\u6ca1\u529e\u6cd5\u5b8c\u5168\u4fe1\u4efb\u3002\u5c31\u4e8b\u8bba\u4e8b\uff0c\u5965\u5609\u83b1\u65af\u505a\u51fa\u4e00\u9879\u5224\u65ad\uff0c\u5374\u65e0\u6cd5\u8bc1\u660e\u7ed9\u743c\u6069\u770b\uff0c\u90a3\u8fd9\u53ea\u80fd\u7b97\u662f\u4e00\u9762\u4e4b\u8bcd\u3002\u4e00\u9762\u4e4b\u8bcd\u7684\u6548\u529b\uff0c\u5168\u770b\u53cc\u65b9\u7684\u4fe1\u4efb\u7a0b\u5ea6\uff0c\u5018\u82e5\u662f\u6885\u83f2\u65af\u8fd9\u4e48\u8bf4\uff0c\u743c\u6069\u5f53\u7136\u65e0\u6761\u4ef6\u76f8\u4fe1\uff0c\u4f46\u5965\u5609\u83b1\u65af\u4e48\uff0c\u90a3\u8fd8\u662f\u7b97\u4e86\u5427\u3002</p><p>\u4e0d\u8fc7\u4ed6\u4e5f\u6ca1\u5fc5\u8981\u5f53\u573a\u8868\u793a\u6000\u7591\uff0c\u53cd\u6b63\u8bf4\u4e0d\u8bf4\u5728\u4e8e\u5bf9\u65b9\uff0c\u542c\u4e0d\u542c\u5728\u4e8e\u81ea\u5df1\u3002\u201c\u90a3\u4e48\u73b0\u5728\u5230\u5e95\u9700\u8981\u6211\u505a\u4ec0\u4e48\uff1f\u201d\u4ed6\u95ee\u3002\u5965\u5609\u83b1\u65af\u8bf4\u4e86\u534a\u5929\uff0c\u5374\u59cb\u7ec8\u53ea\u662f\u5728\u8c08\u8bba\u5927\u81f4\u65b9\u5411\uff0c\u6ca1\u6709\u5177\u4f53\u7ec6\u8282\u3002\u603b\u4e0d\u81f3\u4e8e\u8981\u743c\u6069\u51b2\u5230\u51ef\u745f\u7433\u9762\u524d\u628a\u5979\u5e72\u6389\u5427\uff1f\u4e14\u4e0d\u8bba\u743c\u6069\u662f\u5426\u613f\u610f\uff0c\u4ed6\u6709\u6ca1\u6709\u8fd9\u4e2a\u672c\u4e8b\u8fd8\u4e24\u8bf4\u5462\u3002</p><p>\u5965\u5609\u83b1\u65af\u6ca1\u6709\u76f4\u63a5\u56de\u7b54\u8fd9\u4e2a\u95ee\u9898\u3002\u201c\u4f60\u8bf4\u8fd9\u662f\u51ef\u5c14\u672c\u9001\u7ed9\u6211\u7684\u793c\u7269\uff1f\u201d\u5979\u7a81\u7136\u8f6c\u6362\u8bdd\u9898\u3002</p><p>\u201c\u662f\u554a\uff0c\u201d\u867d\u7136\u4e0d\u660e\u6240\u4ee5\uff0c\u4f46\u743c\u6069\u8fd8\u662f\u56de\u7b54\uff0c\u201c\u4ed6\u6258\u6211\u8f6c\u4ea4\u4f60\u7684\u3002\u201d</p><p>\u201c\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d</p><p>\u201c\u6211\u53ef\u6ca1\u5077\u5077\u62c6\u8fc7\uff0c\u4f60\u6253\u5f00\u770b\u770b\u4e0d\u5c31\u77e5\u9053\u4e86\u3002\u201d</p><p>\u5965\u5609\u83b1\u65af\u55ef\u4e86\u4e00\u58f0\uff0c\u968f\u5373\u653e\u5728\u5730\u4e0a\u7684\u793c\u76d2\u5305\u88c5\u81ea\u52a8\u6563\u5f00\u4e86\uff0c\u9732\u51fa\u4e00\u4e2a\u6df1\u84dd\u8272\u7684\u6708\u7259\u5f62\u7269\u54c1\uff0c\u50cf\u662f\u6c34\u6676\u8d28\u5730\uff0c\u534a\u900f\u660e\u7684\uff0c\u5df4\u638c\u5927\u5c0f\uff0c\u743c\u6069\u597d\u5947\u5730\u770b\u4e86\u770b\uff0c\u53d1\u73b0\u4e0a\u9762\u8fd8\u523b\u4e86\u4e9b\u6587\u5b57\uff0c\u800c\u4e14\u662f\u53e4\u8010\u745f\u6587\uff0c\u5e76\u975e\u901a\u7528\u8bed\u3002\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u770b\u8d77\u6765\u50cf\u662f\u67d0\u79cd\u7eaa\u5ff5\u54c1\u3002\u201d</p><p>\u201c\u786e\u5b9e\u662f\u7eaa\u5ff5\u54c1\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u8fd9\u662f\u8010\u745f\u745e\u5c14\u5973\u6027\u6743\u76ca\u4fdd\u62a4\u534f\u4f1a\u6210\u7acb\u5178\u793c\u66a8\u7b2c\u4e00\u5c4a\u4ee3\u8868\u5927\u4f1a\u6240\u9881\u53d1\u7684\u7eaa\u5ff5\u54c1\uff0c\u6240\u6709\u4f1a\u5458\u90fd\u6709\u4e00\u4efd\uff0c\u8fd8\u662f\u6211\u4eb2\u81ea\u8bbe\u8ba1\u7684\u3002\u201d</p><p>\u8010\u745f\u745e\u5c14\u5973\u6027\u6743\u76ca\u4fdd\u62a4\u534f\u4f1a\u2026\u2026\u8fd9\u662f\u4ec0\u4e48\uff1f</p><p>\u743c\u6069\u5148\u662f\u6115\u7136\uff0c\u7136\u540e\u60f3\u4e86\u8d77\u6765\uff0c\u5965\u6c83\u66fe\u7ecf\u8ddf\u4ed6\u63d0\u8d77\u8fc7\uff0c\u8bf4\u662f\u4e00\u4e2a\u5973\u6743\u7ec4\u7ec7\uff0c\u6210\u5458\u5f88\u591a\uff0c\u5f71\u54cd\u529b\u6781\u5927\uff0c\u4ee4\u5f88\u591a\u7537\u6027\u2014\u2014\u5c24\u5176\u50cf\u5965\u6c83\u8fd9\u79cd\u5b85\u7537\u2014\u2014\u65e2\u656c\u4e14\u754f\uff0c\u800c\u5965\u5609\u83b1\u65af\u5c31\u662f\u8fd9\u4e2a\u7ec4\u7ec7\u7684\u521b\u59cb\u4eba\u517c\u4f1a\u957f\u3002</p><p>\u4f46\u51ef\u5c14\u672c\u600e\u4e48\u4f1a\u6709\u8fd9\u4e1c\u897f\uff0c\u96be\u4e0d\u6210\u4f60\u4eec\u5973\u6027\u6743\u76ca\u4fdd\u62a4\u534f\u4f1a\u8fd8\u6536\u7537\u6027\u4f1a\u5458\uff1f</p><p>\u201c\u7eaa\u5ff5\u54c1\u672c\u8eab\u5012\u4e0d\u7b97\u7a00\u6709\uff0c\u6211\u8bb0\u5f97\u5f53\u65f6\u5b9a\u505a\u4e86\u56db\u4e07\u4e94\u5343\u591a\u4e2a\uff0c\u5168\u90fd\u53d1\u4e86\u51fa\u53bb\uff0c\u6709\u4e00\u4e9b\u6d41\u4f20\u4e0b\u6765\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u597d\u5947\u602a\u7684\u3002\u201d</p><p>\u56db\u4e07\u4e94\u5343\u591a\u4e2a\u2026\u2026\u679c\u7136\u89c4\u6a21\u4e0d\u5c0f\u3002\u597d\u5427\uff0c\u5047\u8bbe\u51ef\u5c14\u672c\u7684\u67d0\u4f4d\u7956\u5148\u66fe\u7ecf\u53c2\u52a0\u8fc7\u8fd9\u4ec0\u4e48\u5973\u6027\u6743\u76ca\u4fdd\u62a4\u534f\u4f1a\uff0c\u9886\u4e86\u4e00\u4efd\u7eaa\u5ff5\u54c1\uff0c\u5e76\u4e14\u8fd8\u6d41\u4f20\u4e0b\u6765\u2014\u2014\u4f46\u4ed6\u73b0\u5728\u628a\u8fd9\u4e1c\u897f\u9001\u7ed9\u4f60\uff0c\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u96be\u9053\u8868\u793a\u8981\u91cd\u65b0\u5165\u4f1a\uff1f</p><p>\u201c\u6ca1\u4ec0\u4e48\u610f\u601d\uff0c\u4ed6\u62ff\u4e0d\u51c6\u6211\u7684\u7acb\u573a\uff0c\u53c8\u6000\u7591\u83b1\u62c9\u5728\u6211\u624b\u4e0a\uff0c\u6240\u4ee5\u8bd5\u63a2\u4e00\u4e0b\u800c\u5df2\u3002\u201d</p><p>\u524d\u534a\u53e5\u8bdd\u743c\u6069\u53ef\u4ee5\u7406\u89e3\uff0c\u5965\u5609\u83b1\u65af\u73b0\u5728\u518d\u865a\u5f31\uff0c\u6bd5\u7adf\u662f\u66fe\u7ecf\u7684\u8010\u745f\u5927\u5965\u672f\u5e08\uff0c\u53ef\u4ee5\u8bf4\u662f\u5f53\u4eca\u4e16\u4e0a\u767e\u5206\u4e4b\u4e5d\u5341\u4e5d\u7684\u5deb\u5e08\u7684\u8001\u524d\u8f88\uff0c\u800c\u4e14\u8fd8\u548c\u9634\u9b42\u57ce\u6709\u7740\u4e00\u4e9b\u6e0a\u6e90\u3002\u4f5c\u4e3a\u51ef\u5c14\u672c\u800c\u8a00\uff0c\u6b63\u8981\u9762\u4e34\u548c\u5f3a\u654c\u51b3\u6218\u7684\u5173\u5934\uff0c\u81ea\u5df1\u5730\u76d8\u4e0a\u7a81\u7136\u6765\u4e86\u8fd9\u6837\u4e00\u4e2a\u4eba\uff0c\u6709\u6240\u8b66\u60d5\u662f\u6b63\u5e38\u7684\u3002\u6362\u4e86\u743c\u6069\uff0c\u4e5f\u8981\u60f3\u529e\u6cd5\u505a\u4e9b\u63a5\u89e6\uff0c\u786e\u5b9a\u5bf9\u65b9\u7684\u7acb\u573a\u3002\u4f46\u540e\u534a\u53e5\u8bdd\u5c31\u5f88\u83ab\u540d\u5176\u5999\u4e86\uff0c\u83b1\u62c9\u4e0d\u662f\u88ab\u8428\u9a6c\u65af\u7279\u6293\u8d70\u4e86\u4e48\uff0c\u5173\u5965\u5609\u83b1\u65af\u4ec0\u4e48\u4e8b\uff1f</p><p>\u201c\u56e0\u4e3a\u8fd9\u786e\u5b9e\u5173\u6211\u7684\u4e8b\u3002\u201d</p><p>\u2026\u2026\u4ec0\u4e48\uff01</p><p>\u201c\u8fd9\u7b97\u4e00\u4e2a\u9644\u8d60\u7684\u60c5\u62a5\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u8428\u9a6c\u65af\u7279\u6293\u8d70\u7684\uff0c\u53ea\u662f\u4e00\u4e2a\u5192\u724c\u8d27\uff0c\u6216\u8005\u8bf4\u66ff\u8eab\uff1b\u771f\u6b63\u7684\u83b1\u62c9\uff0c\u73b0\u5728\u7684\u786e\u5728\u6211\u624b\u4e0a\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u89c9\u5f97\u4eca\u5929\u78b0\u5230\u7684\u610f\u5916\u5df2\u7ecf\u8db3\u591f\u591a\uff0c\u591a\u5230\u81ea\u5df1\u7406\u5e94\u9ebb\u6728\uff0c\u7136\u800c\u5965\u5609\u83b1\u65af\u7684\u8bdd\u8fd8\u662f\u628a\u4ed6\u5413\u4e86\u4e00\u8df3\uff0c\u201c\u83b1\u62c9\u5728\u4f60\u624b\u4e0a\uff1f\u54ea\u4e2a\u83b1\u62c9\uff1f\u201d</p><p>\u201c\u83b1\u62c9\xb7\u5965\u7f57\u6851\u3002\u201d</p><p>\u5965\u7f57\u6851\u662f\u51ef\u5c14\u672c\u7684\u59d3\u6c0f\uff0c\u6309\u7167\u4e60\u4fd7\uff0c\u83b1\u62c9\u4e0e\u4ed6\u6210\u5a5a\u540e\uff0c\u4fbf\u968f\u4e4b\u6539\u59d3\u2014\u2014\u5176\u5b9e\u8fd9\u6839\u672c\u65e0\u9700\u89e3\u91ca\uff0c\u9634\u5f71\u9547\u73b0\u5728\u54ea\u91cc\u8fd8\u6709\u7b2c\u4e8c\u4e2a\u83b1\u62c9\u3002</p><p>\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\uff1f</p><p>\u8f6c\u773c\u4e4b\u95f4\uff0c\u743c\u6069\u8111\u4e2d\u5df2\u7ecf\u95ea\u8fc7\u4e03\u516b\u79cd\u5047\u8bbe\uff0c\u4f46\u6700\u540e\u4ed6\u653e\u5f03\u4e86\u731c\u6d4b\u3002\u201c\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u5de7\u5408\u800c\u5df2\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u6309\u7167\u5965\u5609\u83b1\u65af\u7684\u8bf4\u6cd5\uff0c\u4e8b\u60c5\u5176\u5b9e\u5f88\u7b80\u5355\uff1a\u5979\u5076\u7136\u51fa\u53bb\u4e00\u8d9f\uff0c\u6070\u5de7\u53d1\u73b0\u9547\u4e2d\u6709\u67d0\u4e2a\u4eba\u662f\u9f99\u5deb\u6559\u7684\u5185\u5978\uff0c\u6b63\u5728\u8ba1\u5212\u7ed1\u67b6\u83b1\u62c9\u505a\u4eba\u8d28\uff0c\u4e8e\u662f\u6697\u4e2d\u4e00\u8def\u8ddf\u968f\u3002\u5185\u5978\u4f0f\u51fb\u83b1\u62c9\uff0c\u987a\u5229\u5f97\u624b\uff0c\u5374\u53d1\u73b0\u6293\u5230\u7684\u662f\u4e2a\u66ff\u8eab\u3002\u771f\u7684\u83b1\u62c9\u968f\u5373\u73b0\u8eab\uff0c\u548c\u5185\u5978\u77ed\u6682\u4ea4\u624b\uff0c\u4e24\u8d25\u4ff1\u4f24\uff0c\u5185\u5978\u5e26\u7740\u5047\u7684\u83b1\u62c9\u9003\u8d70\uff0c\u5965\u5609\u83b1\u65af\u4fbf\u4e58\u673a\u5077\u88ad\uff0c\u5c06\u771f\u7684\u83b1\u62c9\u5236\u4f4f\uff0c\u6293\u4e86\u56de\u6765\u3002</p><p>\u201c\u4f60\u6293\u5979\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u81ea\u7136\u662f\u505a\u4eba\u8d28\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u73b0\u5728\u654c\u6211\u672a\u660e\uff0c\u7acb\u573a\u672a\u5b9a\uff0c\u6211\u4eec\u53c8\u662f\u5728\u5bf9\u65b9\u7684\u5730\u76d8\u4e0a\uff0c\u5f53\u7136\u8981\u6709\u6240\u51c6\u5907\uff0c\u624b\u4e0a\u6ca1\u70b9\u7b79\u7801\u600e\u4e48\u884c\u5462\u3002\u4f60\u4eec\u8fd9\u4e9b\u5e74\u8f7b\u4eba\u6ca1\u7ecf\u9a8c\uff0c\u6211\u53ea\u597d\u4ee3\u52b3\u4e86\u3002\u201d</p><p>\u8fd9\u8bdd\u542c\u8d77\u6765\u4f3c\u4e4e\u5f88\u6709\u9053\u7406\uff0c\u4f46\u53c8\u603b\u89c9\u5f97\u54ea\u91cc\u4e0d\u592a\u5bf9\u52b2\u3002\u672a\u96e8\u7ef8\u7f2a\u81ea\u7136\u662f\u5bf9\u7684\uff0c\u4f46\u4f60\u56e0\u4e3a\u8fd9\u79cd\u7406\u7531\uff0c\u76f4\u63a5\u628a\u4eba\u5bb6\u59bb\u5b50\u6293\u6765\u5f53\u4eba\u8d28\uff0c\u8fd9\u5c82\u4e0d\u662f\u903c\u5bf9\u65b9\u8ddf\u4f60\u7ffb\u8138\u4e48\u3002</p><p>\u5965\u5609\u83b1\u65af\u4e00\u58f0\u51b7\u7b11\uff0c\u201c\u4f60\u89c9\u5f97\u81ea\u5df1\u548c\u90a3\u5e2e\u9009\u6c11\u5173\u7cfb\u5f88\u597d\uff1f\u201d</p><p>\u743c\u6069\u6447\u6447\u5934\u3002</p><p>\u201c\u90a3\u5c31\u662f\u4e86\uff0c\u5927\u5bb6\u6839\u672c\u7acb\u573a\u654c\u5bf9\uff0c\u4eca\u5929\u80fd\u548c\u5e73\u76f8\u5904\uff0c\u660e\u5929\u4e5f\u4f1a\u53cd\u76ee\u6210\u4ec7\u3002\u65e2\u7136\u5982\u6b64\uff0c\u81ea\u7136\u8981\u5148\u4e0b\u624b\u4e3a\u5f3a\uff0c\u4e58\u5176\u4e0d\u5907\u62a2\u5360\u5148\u673a\u518d\u8bf4\u3002\u201d</p><p>\u201c\u2026\u2026\u597d\u5427\uff0c\u201d\u743c\u6069\u5e76\u4e0d\u662f\u5f88\u8d5e\u540c\u5965\u5609\u83b1\u65af\u7684\u770b\u6cd5\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u518d\u591a\u8bf4\u4e5f\u6ca1\u4ec0\u4e48\u610f\u4e49\uff0c\u8fd8\u662f\u5148\u89e3\u51b3\u95ee\u9898\u518d\u8bf4\u3002\u201c\u73b0\u5728\u4eba\u5bb6\u7ed9\u4f60\u9001\u793c\u4e86\uff0c\u6309\u4f60\u7684\u8bf4\u6cd5\u662f\u5728\u8bd5\u63a2\uff0c\u90a3\u4f60\u8981\u5982\u4f55\u56de\u5e94\uff1f\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u9700\u8981\u56de\u5e94\uff1f\u201d\u5965\u5609\u83b1\u65af\u53cd\u95ee\u3002</p><p>\u743c\u6069\u6c89\u9ed8\u7247\u523b\uff0c\u201c\u90a3\u4e2a\u9f99\u5deb\u6559\u7684\u5185\u5978\u662f\u8c01\uff1f\u201d</p><p>\u5965\u5609\u83b1\u65af\u8bf4\u4e86\u4e00\u4e2a\u540d\u5b57\uff0c\u201c\u5982\u679c\u6ca1\u4ec0\u4e48\u5176\u4ed6\u4e8b\uff0c\u5c31\u5230\u6b64\u4e3a\u6b62\u5427\uff0c\u201d\u5979\u4e0b\u9010\u5ba2\u4ee4\uff0c\u201c\u6211\u8981\u4f11\u606f\u4e00\u4f1a\u3002\u201d</p><p>\u201c\u7b49\u7b49\u2026\u2026\u201d</p><p>\u7136\u800c\u5bf9\u65b9\u663e\u7136\u6ca1\u6709\u5f81\u6c42\u610f\u89c1\u7684\u6253\u7b97\uff0c\u8bdd\u97f3\u672a\u843d\uff0c\u743c\u6069\u53ea\u89c9\u5929\u65cb\u5730\u8f6c\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u4ed6\u53c8\u56de\u5230\u4e86\u73ca\u5609\u7684\u8bad\u7ec3\u573a\u4e2d\u3002</p><p>\u5c11\u5973\u6b63\u5728\u7ec3\u5251\uff0c\u4f46\u5bf9\u624b\u4e0d\u662f\u838e\u73de\u514b\uff0c\u800c\u662f\u4e00\u4e2a\u94f6\u8272\u7684\u91d1\u5c5e\u4eba\u5076\uff0c\u8d64\u624b\u7a7a\u62f3\uff0c\u6781\u4e3a\u7075\u6d3b\u3002\u743c\u6069\u770b\u4e86\u4e00\u4f1a\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u8fd9\u4e0d\u662f\u90a3\u4e2a\u2018\u79d8\u5076\u2019\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u8001\u5e08\u628a\u5b83\u4fee\u597d\u4e86\u3002\u201d</p><p>\u5728\u5854\u745f\u8c37\u7684\u65f6\u5019\uff0c\u6709\u4e00\u6b21\u6076\u9b54\u6b27\u51ef\u4e0a\u95e8\uff0c\u9001\u7ed9\u743c\u6069\u4e00\u4e2a\u201c\u79d8\u5076\u201d\u505a\u793c\u7269\u3002\u79d8\u5076\u662f\u53e4\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u665a\u671f\u53d1\u660e\u7684\u4e00\u79cd\u9b54\u6cd5\u9020\u7269\uff0c\u7c7b\u4f3c\u9b54\u50cf\uff0c\u4f46\u7075\u6d3b\u5f97\u591a\uff0c\u4e5f\u667a\u80fd\u5f97\u591a\u3002\u6b27\u51ef\u627e\u5230\u7684\u8fd9\u4e2a\u79d8\u5076\u539f\u672c\u662f\u4e2a\u635f\u574f\u54c1\uff0c\u7ecf\u8fc7\u4fee\u590d\u6062\u590d\u4e86\u884c\u52a8\u80fd\u529b\uff0c\u4f46\u4e0d\u53d7\u63a7\u5236\uff0c\u6b27\u51ef\u548c\u743c\u6069\u90fd\u4e0d\u61c2\u79d8\u5076\u7684\u9a71\u52a8\u4e4b\u6cd5\uff0c\u65e0\u6cd5\u5bf9\u5b83\u4e0b\u8fbe\u547d\u4ee4\u3002\u540e\u6765\u662f\u88ab\u5965\u5609\u83b1\u65af\u8981\u53bb\uff0c\u4e00\u76f4\u6ca1\u6709\u518d\u9732\u9762\uff0c\u743c\u6069\u5dee\u70b9\u90fd\u5c06\u5b83\u5fd8\u4e86\u3002</p><p>\u201c\u600e\u4e48\u611f\u89c9\u2026\u2026\u53d8\u7626\u4e86\uff1f\u201d\u743c\u6069\u7aef\u8be6\u7740\u79d8\u5076\uff0c\u201c\u597d\u50cf\u8fd8\u53d8\u77ee\u4e86\u70b9\u3002\u201d</p><p>\u201c\u79d8\u5076\u662f\u53ef\u4ee5\u4e00\u5b9a\u7a0b\u5ea6\u7684\u53d8\u5316\u5f62\u6001\u7684\uff0c\u201d\u73ca\u5609\u89e3\u91ca\uff0c\u540c\u65f6\u547d\u4ee4\u79d8\u5076\u505c\u6b62\u52a8\u4f5c\uff0c\u7ad9\u5728\u539f\u5730\uff0c\u201c\u8001\u5e08\u548c\u4f60\u90fd\u804a\u4e86\u4ec0\u4e48\u554a\uff0c\u8bf4\u8fd9\u4e48\u4e45\u3002\u201d\u5979\u597d\u5947\u5730\u95ee\u3002</p><p>\u201c\u6211\u5411\u5979\u8bf7\u6559\u4e00\u4e9b\u5386\u53f2\u5178\u6545\u3002\u201d\u743c\u6069\u542b\u7cca\u5176\u8f9e\u3002</p><p>\u201c\u54e6\u3002\u201d</p><p>\u73ca\u5609\u8bc6\u8da3\u5730\u6ca1\u6709\u518d\u8ffd\u95ee\uff0c\u81ea\u987e\u81ea\u5730\u53c8\u5f00\u59cb\u63d0\u5251\u7ec3\u4e60\u3002\u770b\u7740\u5979\u4e00\u5251\u53c8\u4e00\u5251\u5730\u671d\u7740\u7a7a\u6c14\u51fb\u523a\uff0c\u795e\u60c5\u5f02\u5e38\u8ba4\u771f\uff0c\u201c\u59d0\u59d0\uff0c\u4f60\u4e0d\u89c9\u5f97\u5947\u602a\u5417\uff1f\u201d\u743c\u6069\u5fcd\u4e0d\u4f4f\u8bf4\u3002</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u59d0\u59d0\u7684\u8fdb\u6b65\u901f\u5ea6\u2014\u2014\u6211\u5f53\u7136\u76f8\u4fe1\u59d0\u59d0\u7684\u5929\u8d4b\uff0c\u4e5f\u77e5\u9053\u59d0\u59d0\u5f88\u52aa\u529b\u5f88\u523b\u82e6\uff0c\u4f46\u59d0\u59d0\u96be\u9053\u4e0d\u89c9\u5f97\u2026\u2026\u8fd9\u8fd8\u662f\u592a\u5feb\u4e86\uff0c\u5feb\u5f97\u6709\u70b9\u4e0d\u592a\u6b63\u5e38\u5417\uff1f\u201d</p><p>\u73ca\u5609\u5fae\u7b11\uff0c\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u4e0d\u4f1a\u8ddf\u6211\u8bf4\u8fd9\u4e2a\u5462\u3002\u201d</p><p>\u201c\u6211\u2026\u2026\u201d</p><p>\u201c\u6211\u77e5\u9053\u4f60\u7684\u610f\u601d\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u6211\u4e5f\u77e5\u9053\u4f60\u5728\u62c5\u5fe7\u4ec0\u4e48\uff0c\u4f46\u6211\u522b\u65e0\u9009\u62e9\u554a\u3002\u4f60\u6709\u4f60\u7684\u4e16\u754c\uff0c\u800c\u6211\u60f3\u5728\u90a3\u91cc\u5360\u636e\u4e00\u5e2d\u4e4b\u5730\uff0c\u6211\u4e0d\u60f3\u88ab\u6392\u65a5\u51fa\u53bb\u3002\u6211\u60f3\u8981\u80fd\u591f\u8ddf\u4e0a\u4f60\u7684\u6b65\u4f10\uff0c\u60f3\u8981\u80fd\u591f\u968f\u65f6\u7ad9\u5728\u4f60\u8eab\u8fb9\uff0c\u4e0d\u7ba1\u9762\u4e34\u7684\u662f\u98ce\u82b1\u96ea\u6708\uff0c\u8fd8\u662f\u5200\u4e1b\u5251\u96e8\u3002\u6211\u60f3\u505a\u4f60\u7684\u4f34\u4fa3\uff0c\u800c\u4e0d\u662f\u88ab\u4fdd\u62a4\u7684\u5bf9\u8c61\uff0c\u6211\u60f3\u80fd\u591f\u548c\u4f60\u6709\u5171\u540c\u7684\u7ecf\u5386\uff0c\u5171\u540c\u7684\u8bb0\u5fc6\uff0c\u800c\u4e0d\u662f\u6bcf\u6b21\u7559\u5728\u5bb6\u4e2d\uff0c\u4ec0\u4e48\u90fd\u505a\u4e0d\u4e86\uff0c\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\uff0c\u53ea\u80fd\u76fc\u671b\u4f60\u65e9\u70b9\u5f52\u6765\u2014\u2014\u8fd9\u79cd\u611f\u89c9\u5f88\u4e0d\u597d\uff0c\u4f1a\u8ba9\u6211\u70e6\u8e81\uff0c\u8ba9\u6211\u751f\u6c14\uff0c\u8ba9\u6211\u5ac9\u5992\uff0c\u5ac9\u5992\u80fd\u591f\u966a\u4f34\u5728\u4f60\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\uff0c\u201d\u5979\u5fae\u5fae\u4e00\u7b11\uff0c\u201c\u5973\u4eba\u7684\u5ac9\u5992\u5f88\u53ef\u6015\u54e6\uff0c\u743c\u6069\uff0c\u6bd4\u5982\u8bf4\uff0c\u6211\u5c31\u5f88\u5ac9\u5992\u827e\u5f25\u8587\uff0c\u5ac9\u5992\u5f97\u5e0c\u671b\u5979\u6b7b\u6389\u3002\u4f60\u559c\u6b22\u8fd9\u6837\u7684\u59d0\u59d0\u5417\uff1f\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u4ee5\u5bf9\u3002</p><p>\u203b\u203b\u203b</p><p>\u73ca\u5609\u7684\u5b66\u4e60\u8bfe\u7a0b\u5b89\u6392\u5f88\u6ee1\uff0c\u5251\u672f\u4e4b\u540e\u4fbf\u662f\u5360\u661f\u672f\uff0c\u743c\u6069\u4e0d\u4fbf\u6253\u6270\uff0c\u5148\u56de\u81ea\u5df1\u623f\u95f4\u4f11\u606f\u3002\u521a\u521a\u8d70\u5230\u95e8\u53e3\uff0c\u5c31\u542c\u89c1\u91cc\u9762\u4f20\u6765\u5947\u602a\u7684\u52a8\u9759\uff0c\u50cf\u662f\u6709\u5973\u4eba\u5728\u8bf4\u8bdd\uff0c\u5374\u53c8\u542b\u7cca\u4e0d\u6e05\u3002\u4ed6\u9a7b\u8db3\u542c\u4e86\u4e00\u4f1a\uff0c\u63a8\u5f00\u623f\u95e8\uff0c\u770b\u89c1\u4e00\u4f4d\u5c11\u5973\u659c\u8eba\u5728\u6c99\u53d1\u4e0a\uff0c\u795e\u60c5\u604d\u60da\uff0c\u773c\u795e\u8ff7\u79bb\uff0c\u957f\u957f\u7684\u9ed1\u53d1\u62ab\u6563\u5728\u8eab\u4e0b\uff0c\u77ed\u88d9\u5df2\u7ecf\u88ab\u6380\u5230\u8170\u95f4\uff0c\u9732\u51fa\u7c89\u8272\u5185\u88e4\u548c\u96ea\u767d\u5927\u817f\uff0c\u886c\u886b\u7ebd\u6263\u4e5f\u88ab\u89e3\u5f00\u4e86\u51e0\u7c92\uff0c\u80fd\u591f\u770b\u89c1\u9ed1\u8272\u6587\u80f8\uff1b\u800c\u53e6\u4e00\u4f4d\u706b\u53d1\u84dd\u88d9\u7684\u7f8e\u4eba\u513f\u6b63\u538b\u5728\u5979\u8eab\u4e0a\uff0c\u8086\u610f\u8f7b\u8584\uff0c\u4e0a\u4e0b\u5176\u624b\u3002\u743c\u6069\u63a8\u95e8\u8fdb\u6765\uff0c\u4e24\u4eba\u5168\u65e0\u53cd\u5e94\uff0c\u4f9d\u7136\u6545\u6211\uff0c\u4ed6\u4e0d\u5f97\u4e0d\u7528\u529b\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u5c11\u5973\u8fd9\u624d\u60ca\u9192\uff0c\u5239\u90a3\u95f4\u8138\u86cb\u7f9e\u5f97\u901a\u7ea2\uff0c\u614c\u614c\u5f20\u5f20\u5730\u63a8\u5f00\u8eab\u4e0a\u7684\u4eba\uff0c\u5934\u4e5f\u4e0d\u56de\u5730\u51b2\u4e86\u51fa\u53bb\u3002</p><p>\u201c\u4f60\u5c31\u4e0d\u80fd\u665a\u56de\u6765\u4e00\u70b9\u4e48\uff0c\u201d\u624e\u745e\u5c14\u62b1\u6028\uff0c\u201c\u6211\u773c\u770b\u5c31\u8981\u5f97\u624b\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4f60\u522b\u5728\u6211\u7684\u623f\u95f4\u91cc\u554a\u3002\u201d\u743c\u6069\u6ca1\u597d\u6c14\u5730\u8bf4\u3002</p><p>\u201c\u5728\u4f60\u7684\u623f\u95f4\u91cc\u6bd4\u8f83\u6709\u6c14\u6c1b\u561b\u3002\u201d</p><p>\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8fd9\u5f88\u597d\u7406\u89e3\u554a\uff0c\u6bd4\u5982\u8bf4\u4f60\u559c\u6b22\u641e\u4eba\u59bb\uff0c\u67d0\u5929\u78b0\u5230\u4e00\u4e2a\u6781\u54c1\uff0c\u4f60\u4f1a\u600e\u4e48\u505a\u5462\uff1f\u628a\u5979\u5e26\u51fa\u53bb\u5f00\u623f\u95f4\u56fa\u7136\u597d\uff0c\u4f46\u5728\u5979\u81ea\u5df1\u7684\u5367\u5ba4\u91cc\u5c06\u5979\u5c31\u5730\u6b63\u6cd5\uff0c\u8086\u610f\u73a9\u5f04\uff0c\u663e\u7136\u4f1a\u66f4\u52a0\u523a\u6fc0\u5bf9\u5427\u3002\u201d</p><p>\u201c\u7b49\u4e00\u4e0b\uff0c\u6709\u4e00\u70b9\u8981\u8bf4\u6e05\u695a\uff0c\u6211\u4e0d\u559c\u6b22\u4eba\u59bb\uff0c\u81f3\u5c11\u6ca1\u4ec0\u4e48\u7279\u522b\u504f\u7231\u3002\u201d\u743c\u6069\u4e0d\u5f97\u4e0d\u6f84\u6e05\u3002</p><p>\u201c\u4e3a\u4ec0\u4e48\uff0c\u4eba\u59bb\u5f88\u597d\u554a\uff0c\u98ce\u97f5\u6210\u719f\uff0c\u6e29\u67d4\u4f53\u8d34\uff0c\u7ecf\u9a8c\u5145\u8db3\uff0c\u6280\u5de7\u5230\u4f4d\u2014\u2014\u7537\u4eba\u5e94\u8be5\u90fd\u559c\u6b22\u5427\u3002\u5c31\u7b97\u4f60\u6709\u5904\u5973\u60c5\u7ed3\uff0c\u4e5f\u4e0d\u59a8\u788d\u6362\u6362\u53e3\u5473\u561b\u3002\u201d</p><p>\u201c\u8fd9\u54ea\u6709\u4ec0\u4e48\u4e3a\u4ec0\u4e48\u7684\uff0c\u5404\u4eba\u53e3\u5473\u4e0d\u540c\uff0c\u6709\u4ec0\u4e48\u5947\u602a\u7684\u3002\u201d</p><p>\u201c\u4f46\u4f60\u660e\u660e\u5c31\u662f\u559c\u6b22\u4eba\u59bb\u554a\u3002\u201d\u624e\u745e\u5c14\u4e00\u8138\u56f0\u60d1\u5730\u8bf4\u3002</p><p>\u201c\u5582\u5582\uff0c\u4f60\u6709\u4ec0\u4e48\u8bc1\u636e\u8fd9\u4e48\u8bf4\uff0c\u201d\u743c\u6069\u6297\u8bae\uff0c\u201c\u719f\u5f52\u719f\u554a\uff0c\u4f60\u4e71\u8bf4\u8bdd\u6211\u4e00\u6837\u544a\u4f60\u8bfd\u8c24\u3002\u201d</p><p>\u201c\u8bc1\u636e\u4e0d\u5c31\u6446\u5728\u4f60\u9762\u524d\u5417\u3002\u201d</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u4f60\uff1f\u201d</p><p>\u201c\u5bf9\u554a\uff0c\u6211\u662f\u4f60\u4ee5\u524d\u7684\u60c5\u4eba\uff0c\u8bf4\u662f\u4eba\u59bb\u4e5f\u80fd\u52c9\u5f3a\u7b97\u5427\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u4f46\u4f60\u4e0d\u662f\u4e0d\u8bb0\u5f97\u5417\uff1f\u65e2\u7136\u4f60\u5168\u5fd8\u4e86\uff0c\u90a3\u5bf9\u4e8e\u4f60\u800c\u8a00\uff0c\u4ee5\u524d\u7684\u4f60\u5c31\u662f\u4e00\u4e2a\u964c\u751f\u4eba\uff0c\u800c\u6211\u5c31\u662f\u522b\u4eba\u7684\u59bb\u5b50\u3002\u4f60\u641e\u6211\u5c31\u662f\u5728\u641e\u4eba\u59bb\uff0c\u6211\u770b\u4f60\u660e\u663e\u5f88\u559c\u6b22\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u662f\u4e24\u7801\u4e8b\u597d\u5427\uff0c\u201d\u743c\u6069\u60f3\u4e86\u534a\u5929\uff0c\u5b9e\u5728\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u56de\u7b54\uff0c\u53ea\u597d\u542b\u7cca\u8fc7\u53bb\uff0c\u8d76\u5feb\u8f6c\u79fb\u8bdd\u9898\uff0c\u201c\u800c\u4e14\u51db\u4e0e\u5176\u8bf4\u662f\u6211\u7684\u2018\u59bb\u5b50\u2019\uff0c\u4e0d\u5982\u8bf4\u662f\u827e\u5f25\u8587\u7684\u66f4\u786e\u5207\uff1b\u800c\u4e14\u5c31\u7b97\u8981\u641e\u4eba\u59bb\uff0c\u6211\u4e5f\u4e0d\u89c9\u5f97\u5728\u4eba\u5bb6\u5367\u5ba4\u91cc\u4f1a\u6bd4\u8f83\u5f00\u5fc3\uff0c\u65f6\u523b\u63d0\u5fc3\u540a\u80c6\u7684\uff0c\u6015\u88ab\u5979\u4e08\u592b\u56de\u6765\u6349\u5978\uff0c\u8fd9\u79cd\u611f\u89c9\u54ea\u91cc\u597d\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u6837\u624d\u6709\u4e50\u8da3\uff0c\u4e0d\u662f\u5417\u3002\u201d</p><p>\u201c\u62b1\u6b49\uff0c\u6211\u6b23\u8d4f\u4e0d\u4e86\u4f60\u8fd9\u79cd\u5947\u7279\u7684\u7231\u597d\u3002\u201d</p><p>\u201c\u54ce\uff0c\u201d\u9b54\u59ec\u53f9\u6c14\uff0c\u201c\u4f60\u771f\u662f\u7f3a\u4e4f\u5bf9\u751f\u6d3b\u7684\u70ed\u7231\u3002\u201d</p><p>\u201c\u6211\u662f\u4e2a\u6b63\u5e38\u4eba\uff0c\u8c22\u8c22\u3002\u201d</p><p>\u201c\u54e6\uff0c\u770b\u5230\u81ea\u5df1\u521a\u641e\u4e0a\u624b\u7684\u4eba\u59bb\uff0c\u5728\u548c\u81ea\u5df1\u7684\u5973\u53cb\u7684\u5973\u53cb\u5077\u60c5\uff0c\u8fd8\u80fd\u8fd9\u4e48\u4ece\u5bb9\u81ea\u82e5\uff0c\u6c14\u5b9a\u795e\u95f2\u2014\u2014\u8fd9\u771f\u7684\u662f\u4e00\u4e2a\u6b63\u5e38\u4eba\u7684\u5e94\u6709\u53cd\u5e94\u5417\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u867d\u7136\u662f\u4e2a\u6b63\u5e38\u4eba\uff0c\u4f46\u8eab\u8fb9\u5168\u662f\u4e00\u7fa4\u4e0d\u77e5\u6240\u8c13\u7684\u53d8\u6001\u554a\u3002\u201d</p><p>\u743c\u6069\u51b3\u5b9a\u505c\u6b62\u8fd9\u79cd\u95f2\u804a\u626f\u76ae\uff0c\u201c\u4f60\u4e4b\u524d\u5e72\u561b\u53bb\u4e86\uff1f\u201d</p><p>\u201c\u53bb\u901b\u8857\u554a\uff0c\u201d\u624e\u745e\u5c14\u4f38\u51fa\u624b\uff0c\u892a\u4e0b\u8863\u8896\uff0c\u8ba9\u743c\u6069\u770b\u5979\u624b\u8155\u4e0a\u7684\u4e00\u679a\u767d\u91d1\u8272\u7684\u624b\u73af\uff0c\u4e0a\u9762\u6709\u4e00\u4e9b\u82b1\u7eb9\u72b6\u7684\u96d5\u523b\uff0c\u201c\u6f02\u4eae\u5427\uff0c\u800c\u4e14\u5f88\u4fbf\u5b9c\u5462\uff0c\u6211\u51b2\u8001\u677f\u7b11\u4e86\u7b11\uff0c\u5c31\u7ed9\u6211\u6253\u4e86\u516d\u6298\u3002\u201d</p><p>\u2026\u2026\u4f60\u5802\u5802\u5927\u9b54\u9b3c\u53bb\u4e70\u4e1c\u897f\uff0c\u5c45\u7136\u8fd8\u73a9\u8272\u8bf1\uff0c\u8fd8\u6709\u6ca1\u6709\u4e00\u70b9\u8282\u64cd\u4e86\u3002</p><p>\u201c\u4f60\u8fd8\u771f\u662f\u4e00\u70b9\u90fd\u4e0d\u61c2\u5973\u4eba\u7684\u5fc3\u601d\u2014\u2014\u7b97\u4e86\uff0c\u4f60\u4ece\u6765\u5c31\u6ca1\u61c2\u8fc7\uff0c\u201d\u624e\u745e\u5c14\u610f\u5174\u9611\u73ca\uff0c\u201c\u672c\u6765\u770b\u4f60\u8eab\u8fb9\u8fd9\u4e48\u591a\u5973\u5b69\u5b50\uff0c\u597d\u50cf\u90fd\u633a\u5f00\u5fc3\u7684\u6837\u5b50\uff0c\u8fd8\u4ee5\u4e3a\u4f60\u7ec8\u4e8e\u5f00\u7a8d\u4e86\uff0c\u7ed3\u679c\u8fd8\u662f\u8001\u6837\u5b50\u3002\u201d</p><p>\u201c\u5582\u5582\uff0c\u522b\u603b\u662f\u626f\u5f00\u8bdd\u9898\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u5230\u5e95\u5e72\u561b\u53bb\u4e86\uff0c\u522b\u8bf4\u5355\u7eaf\u53ea\u662f\u901b\u8857\uff0c\u8fd9\u79cd\u8bdd\u4f60\u89c9\u5f97\u6211\u4f1a\u76f8\u4fe1\u5417\uff1f\u201d</p><p>\u201c\u4e0d\u4fe1\u4f60\u5c31\u81ea\u5df1\u770b\u554a\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u53cd\u6b63\u4f60\u6709\u6211\u7684\u771f\u540d\u3002\u201d</p><p>\u65e0\u8bba\u5bf9\u4e8e\u5deb\u5e08\u8fd8\u662f\u90aa\u9b54\uff0c\u201c\u771f\u540d\u201d\u90fd\u662f\u81f3\u5173\u91cd\u8981\u4e4b\u7269\uff0c\u8bf4\u662f\u547d\u8109\u6240\u5728\u4e5f\u4e0d\u8fc7\u5206\uff0c\u6b63\u5e38\u6765\u8bf4\uff0c\u5373\u4fbf\u662f\u81f3\u4eb2\u4e5f\u4e0d\u4f1a\u4e92\u76f8\u900f\u9732\u3002\u743c\u6069\u6709\u624e\u745e\u5c14\u7684\u771f\u540d\uff0c\u53ea\u8981\u4ed6\u613f\u610f\uff0c\u53ef\u4ee5\u505a\u5230\u5f88\u591a\u4e8b\u60c5\uff0c\u6bd4\u5982\u8bf4\u968f\u65f6\u611f\u5e94\u5176\u4f4d\u7f6e\u548c\u72b6\u6001\uff0c\u6bd4\u5982\u8bf4\u901a\u8fc7\u4e00\u5b9a\u7684\u9b54\u6cd5\u4eea\u5f0f\u8f85\u52a9\u5c06\u5979\u53ec\u5524\u5230\u8eab\u8fb9\uff0c\u6bd4\u5982\u8bf4\u201c\u67e5\u9605\u201d\u5979\u524d\u4e00\u6bb5\u65f6\u95f4\u7a76\u7adf\u505a\u4e86\u4ec0\u4e48\uff0c\u53bb\u4e86\u4ec0\u4e48\u5730\u65b9\uff0c\u89c1\u4e86\u4ec0\u4e48\u4eba\uff0c\u8bf4\u4e86\u4ec0\u4e48\u8bdd\uff0c\u90fd\u662f\u53ef\u4ee5\u7684\u3002\u4f46\u743c\u6069\u81ea\u7136\u4e0d\u60f3\u8fd9\u4e48\u505a\uff0c\u4ed6\u6216\u8bb8\u662f\u6709\u70b9\u7c97\u5fc3\u5927\u610f\uff0c\u4e0d\u592a\u4f1a\u8ba8\u5973\u5b69\u5b50\u559c\u6b22\uff0c\u4f46\u4ed6\u53c8\u4e0d\u50bb\uff0c\u57fa\u672c\u7684\u4e1c\u897f\u8fd8\u662f\u61c2\u7684\u3002</p><p>\u201c\u597d\u5566\uff0c\u4e0d\u5f00\u73a9\u7b11\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u6211\u53bb\u8c03\u67e5\u4e00\u4e2a\u4eba\u3002\u201d</p><p>\u201c\u8c01\uff1f\u201d</p><p>\u201c\u8ff7\u96fe\u5927\u5e08\u3002\u201d</p><p>\u201c\u4ed6\uff1f\u201d\u743c\u6069\u5947\u602a\uff0c\u201c\u4f60\u8c03\u67e5\u4ed6\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u89c9\u5f97\u5f88\u53ef\u7591\u561b\uff0c\u5c45\u7136\u6709\u8fde\u6211\u90fd\u65e0\u6cd5\u770b\u900f\u7684\u51e1\u4eba\uff0c\u4e00\u5b9a\u662f\u4e2a\u5371\u9669\u5206\u5b50\uff0c\u6240\u4ee5\u8981\u53bb\u8c03\u67e5\u8c03\u67e5\u3002\u201d</p><p>\u660e\u660e\u4f60\u81ea\u5df1\u5c31\u662f\u5934\u53f7\u5371\u9669\u5206\u5b50\u597d\u5427\uff0c\u743c\u6069\u5728\u5fc3\u91cc\u9ed8\u9ed8\u5730\u8bf4\uff0c\u201c\u90a3\u4e48\u4f60\u90fd\u8c03\u67e5\u51fa\u4ec0\u4e48\u4e86\uff1f\u4ed6\u662f\u4ec0\u4e48\u6765\u5386\uff1f\u201d</p><p>\u201c\u6709\u4e00\u4e9b\u7709\u76ee\uff0c\u8fd8\u4e0d\u786e\u5b9a\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u4e0d\u8fc7\u5012\u662f\u641e\u6e05\u695a\u90a3\u5bb6\u4f19\u4e3a\u4ec0\u4e48\u80fd\u963b\u9694\u6211\u7684\u89c2\u5bdf\u4e86\u3002\u4ed6\u8138\u4e0a\u90a3\u4e2a\u94c1\u9762\u5177\uff0c\u662f\u4e0a\u53e4\u7cbe\u7075\u65cf\u7684\u795e\u5668\u2018\u865a\u4f2a\u5047\u9762\u2019\u3002\u201d</p><p>\u201c\u6ca1\u542c\u8bf4\u8fc7\uff0c\u201d\u743c\u6069\u6447\u5934\uff0c\u8fd9\u5012\u4e5f\u4e0d\u80fd\u7b97\u662f\u4ed6\u5b64\u964b\u5be1\u95fb\uff0c\u4e0a\u53e4\u7cbe\u7075\u65cf\u7684\u795e\u5668\u4f17\u591a\uff0c\u636e\u8bf4\u6709\u51e0\u767e\u4ef6\uff0c\u5c31\u7b97\u7cbe\u7075\u53ea\u6015\u90fd\u8bb0\u4e0d\u6e05\u695a\uff0c\u4f55\u51b5\u5916\u4eba\uff0c\u201c\u6709\u4ec0\u4e48\u7528\u9014\u3002\u201d</p><p>\u201c\u4e3b\u8981\u7528\u9014\u5c31\u662f\u9690\u85cf\u548c\u4f2a\u88c5\uff0c\u9690\u85cf\u4f69\u6234\u8005\u7684\u4e00\u5207\u4fe1\u606f\uff0c\u6bd4\u5982\u8bf4\u8eab\u9ad8\u3001\u4f53\u91cd\u3001\u4e09\u56f4\u3001\u601d\u7ef4\u3001\u60c5\u7eea\uff0c\u7b49\u7b49\u7b49\u7b49\uff0c\u603b\u4e4b\uff0c\u5c31\u662f\u8ba9\u4eba\u53d8\u5f97\u5b8c\u5168\u65e0\u6cd5\u89c2\u6d4b\u2014\u2014\u4f60\u6240\u770b\u5230\u7684\uff0c\u53ea\u662f\u4f69\u6234\u8005\u60f3\u8ba9\u4f60\u770b\u5230\u7684\u4f2a\u88c5\u800c\u5df2\u3002\u201d</p><p>\u201c\u641e\u5f97\u8fd9\u4e48\u795e\u79d8\uff1f\u201d\u743c\u6069\u8bf4\uff0c\u201c\u96be\u602a\u6b23\u5e03\u6000\u7591\u4ed6\u662f\u5185\u5978\uff0c\u6362\u4e86\u6211\u6211\u4e5f\u6000\u7591\u3002\u201d</p><p>\u201c\u5185\u5978\u4ec0\u4e48\u7684\u4e0d\u592a\u53ef\u80fd\u5566\uff0c\u8fd9\u79cd\u4e00\u770b\u5c31\u5f88\u50cf\u7684\u53cd\u800c\u90fd\u4e0d\u662f\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u4f20\u5947\u6545\u4e8b\u91cc\u90fd\u662f\u8fd9\u4e48\u8bf4\u7684\u3002\u201d</p><p>\u201c\u4f60\u5c45\u7136\u8fd8\u770b\u4f20\u5947\u6545\u4e8b\uff1f\u201d</p><p>\u201c\u770b\u554a\uff0c\u5426\u5219\u6f2b\u6f2b\u957f\u591c\uff0c\u4f60\u53c8\u4e0d\u51c6\u6211\u53bb\u627e\u7537\u4eba\uff0c\u6211\u600e\u4e48\u6d3e\u9063\u5bc2\u5bde\uff0c\u53ea\u80fd\u770b\u770b\u51e1\u4eba\u5199\u7684\u4f20\u5947\u6545\u4e8b\u4e86\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u53ef\u60dc\u4f60\u4eec\u51e1\u4eba\u5f80\u5f80\u90fd\u6bd4\u8f83\u61d2\uff0c\u4e00\u70b9\u90fd\u4e0d\u5982\u9b54\u9b3c\u52e4\u5feb\uff0c\u66f4\u65b0\u7279\u522b\u6162\uff0c\u8fd8\u7ecf\u5e38\u627e\u7406\u7531\u65ad\u66f4\uff0c\u8bf4\u4ec0\u4e48\u2018\u5982\u679c\u4f11\u520a\u7387\u6ca1\u6709\u8fbe\u5230\u767e\u5206\u4e4b\u4e94\u5341\u5c31\u6ca1\u6709\u672a\u6765\u2019\u4e4b\u7c7b\u7684\u9b3c\u8bdd\u3002\u6709\u6b21\u6211\u770b\u4e00\u672c\u4e66\uff0c\u6b63\u770b\u5230\u6709\u8da3\u7684\u5730\u65b9\uff0c\u4f5c\u8005\u8bf4\u8981\u5916\u51fa\u65c5\u6e38\u5bfb\u627e\u7075\u611f\uff0c\u65e0\u9650\u671f\u505c\u66f4\u3002\u4e8e\u662f\u6211\u5f04\u4e2a\u96ea\u5d29\u628a\u4ed6\u57cb\u4e86\uff0c\u7075\u9b42\u6536\u8fc7\u6765\u8f6c\u5316\u6210\u5c0f\u9b54\u9b3c\uff0c\u5173\u5728\u5c0f\u9ed1\u5c4b\u91cc\uff0c\u6bcf\u5929\u8fc7\u53bb\u62bd\u51e0\u97ad\u5b50\uff0c\u7acb\u523b\u53d8\u5f97\u6587\u601d\u6cc9\u6d8c\uff0c\u4e0b\u7b14\u5982\u98de\uff0c\u8fd8\u6ca1\u4e00\u4e2a\u6708\u5c31\u5199\u5b8c\u4e86\u3002\u201d</p><p>\u743c\u6069\u7a81\u7136\u89c9\u5f97\u83ab\u540d\u7684\u80cc\u4e0a\u53d1\u51b7\uff0c\u201c\u90a3\u4e2a\uff0c\u80fd\u5199\u5b8c\u5c31\u597d\uff0c\u5199\u5b8c\u4e4b\u540e\u4f60\u603b\u8be5\u6709\u5956\u52b1\u5427\u3002\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u7ed3\u5c3e\u8ba9\u6211\u5f88\u4e0d\u6ee1\u610f\uff0c\u5c45\u7136\u628a\u6211\u6700\u559c\u6b22\u7684\u7537\u4e3b\u89d2\u7ed9\u5199\u6b7b\u4e86\uff0c\u6211\u4e00\u6012\u4e4b\u4e0b\u628a\u4ed6\u4e22\u8fdb\u51a5\u6cb3\u91cc\u53bb\u4e86\uff0c\u201d\u624e\u745e\u5c14\u8038\u8038\u80a9\uff0c\u201c\u540e\u6765\u6211\u7d22\u6027\u81ea\u5df1\u52a8\u7b14\uff0c\u5199\u4e86\u4e2a\u5f88\u7f8e\u597d\u7684\u7ed3\u5c40\u2014\u2014\u4f60\u8981\u4e0d\u8981\u770b\u770b\uff1f\u201d</p><p>\u201c\u5443\uff0c\u4e0b\u6b21\u5427\u3002\u201d\u743c\u6069\u8d76\u5feb\u63a8\u8f9e\uff0c\u4ed6\u5bf9\u90a3\u4f4d\u4e0d\u5e78\u7684\u4f5c\u8005\u6df1\u8868\u540c\u60c5\uff0c\u7537\u4e3b\u89d2\u5199\u6b7b\u4e86\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u53c8\u4e0d\u662f\u5973\u4e3b\u89d2\uff0c\u7537\u4e3b\u89d2\u8fd9\u79cd\u4e1c\u897f\uff0c\u539f\u672c\u7684\u4f5c\u7528\u4e0d\u5c31\u662f\u4e32\u5267\u60c5\u4ee5\u53ca\u886c\u6258\u5973\u4e3b\u89d2\u4e48\u3002\u65e2\u7136\u5c0f\u8bf4\u90fd\u7ed3\u5c40\u4e86\uff0c\u6302\u6389\u6b63\u662f\u6b7b\u5f97\u5176\u6240\uff0c\u6709\u4f55\u4e0d\u59a5\u3002\u624e\u745e\u5c14\u7684\u6587\u5b66\u9274\u8d4f\u6c34\u5e73\u5b9e\u5728\u662f\u6709\u5f85\u63d0\u9ad8\u554a\u3002</p><p>\u201c\u4e0d\u770b\u7b97\u4e86\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u4f60\u5462\uff0c\u5e72\u561b\u53bb\u4e86\uff0c\u600e\u4e48\u4e00\u526f\u77e5\u9053\u5f97\u592a\u591a\u968f\u65f6\u4f1a\u88ab\u6740\u4eba\u706d\u53e3\u7684\u795e\u60c5\u3002\u201d</p><p>\u201c\u5514\uff0c\u6709\u51e0\u4ef6\u4e8b\uff0c\u4f60\u6765\u5e2e\u6211\u53c2\u8c0b\u4e00\u4e0b\u3002\u201d</p><p>\u743c\u6069\u4e8e\u662f\u5c06\u5965\u5609\u83b1\u65af\u521a\u624d\u6240\u8bf4\u53c8\u590d\u8ff0\u4e86\u4e00\u904d\u3002\u201c\u9996\u5148\u662f\u51ef\u745f\u7433\u7684\u8eab\u4efd\uff0c\u5965\u5609\u83b1\u65af\u8bf4\u5979\u5e76\u975e\u672c\u4f53\uff0c\u800c\u662f\u2018\u5bb9\u5668\u2019\uff0c\u4f60\u600e\u4e48\u770b\uff1f\u201d</p><p>\u624e\u745e\u5c14\u6258\u816e\u6c89\u601d\u4e86\u534a\u664c\uff0c\u201c\u2018\u5bb9\u5668\u2019\u7684\u8bf4\u6cd5\uff0c\u6211\u95fb\u6240\u672a\u95fb\uff0c\u8bb0\u5fc6\u91cc\u662f\u6ca1\u6709\u8fd9\u79cd\u4e1c\u897f\u7684\u3002\u201d</p><p>\u201c\u4f60\u662f\u8bf4\u5979\u6240\u8a00\u4e0d\u5b9e\uff1f\u201d</p><p>\u201c\u5012\u4e5f\u672a\u5fc5\uff0c\u201d\u9b54\u59ec\u6447\u6447\u5934\uff0c\u201c\u6211\u79bb\u5f00\u7684\u65f6\u5019\u662f\u5e1d\u56fd\u4e2d\u671f\uff0c\u4e4b\u540e\u4e00\u76f4\u5728\u4e0b\u5c42\u754c\uff0c\u6ca1\u600e\u4e48\u592a\u5173\u5fc3\u7269\u8d28\u754c\u7684\u4e8b\u60c5\u3002\u5982\u679c\u662f\u4ed6\u4eec\u540e\u6765\u641e\u51fa\u7684\u4e1c\u897f\uff0c\u6211\u4e0d\u77e5\u9053\u4e5f\u5f88\u6b63\u5e38\u3002\u4ece\u539f\u7406\u4e0a\u770b\uff0c\u4e5f\u7b26\u5408\u5947\u68b0\u5e08\u7684\u624b\u7b14\u3002\u800c\u4e14\u59d0\u59d0\u7684\u72b6\u6001\u786e\u5b9e\u4e0d\u592a\u5bf9\u52b2\uff0c\u6211\u4e4b\u524d\u6ca1\u6709\u592a\u5728\u610f\uff0c\u4f46\u73b0\u5728\u60f3\u60f3\u2026\u2026\u201d\u5979\u987f\u4e86\u987f\uff0c\u201c\u672a\u5fc5\u4e0d\u53ef\u80fd\u3002\u201d</p><p>\u201c\u54e6\uff0c\u90a3\u2018\u91cd\u751f\u2019\u7684\u4e8b\u60c5\u5462\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u5012\u662f\u6ca1\u9519\uff0c\u201d\u624e\u745e\u5c14\u786e\u8ba4\uff0c\u201c\u4f0a\u739b\u65af\u5361\u7684\u7687\u5ba4\u786e\u5b9e\u6709\u91cd\u751f\u79d8\u672f\uff0c\u7b2c\u4e94\u79d8\u5668\u4e5f\u786e\u5b9e\u53ef\u4ee5\u964d\u4f4e\u98ce\u9669\uff0c\u4fdd\u5168\u8bb0\u5fc6\u3002\u867d\u7136\u6211\u5e76\u6ca1\u6709\u4eb2\u773c\u89c1\u8bc6\u8fc7\uff0c\u4f46\u4f60\u66fe\u7ecf\u548c\u6211\u63d0\u53ca\uff0c\u5e94\u8be5\u4e0d\u5047\u3002\u4e0d\u8fc7\u6709\u4e00\u70b9\u4f60\u8981\u6ce8\u610f\uff0c\u7b2c\u4e94\u79d8\u5668\u80fd\u591f\u5c06\u6b64\u4e16\u7684\u8bb0\u5fc6\u5b8c\u6574\u4fdd\u7559\u2014\u2014\u4f46\u4fdd\u7559\u7684\u4ec5\u4ec5\u53ea\u662f\u8bb0\u5fc6\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u8fd9\u4e2a\u4ed6\u65e9\u5df2\u6709\u6240\u9884\u6599\u3002</p><p>\u4f0a\u739b\u65af\u5361\u7687\u5ba4\u7684\u8f6e\u56de\u91cd\u751f\uff0c\u8868\u9762\u4e0a\u770b\u7c7b\u4f3c\u4e8e\u7075\u9b42\u8f6c\u4e16\uff0c\u593a\u820d\u6295\u80ce\uff0c\u4f46\u5176\u5b9e\u5b8c\u5168\u4e0d\u540c\u3002\u593a\u820d\u6295\u80ce\uff0c\u53ea\u662f\u6362\u4e86\u8eaf\u4f53\uff0c\u7cbe\u795e\u3001\u7075\u9b42\u3001\u610f\u8bc6\u90fd\u662f\u540c\u4e00\u7684\uff0c\u81ea\u6211\u8ba4\u77e5\u5e76\u65e0\u969c\u788d\uff1b\u4f46\u7687\u5ba4\u7684\u91cd\u751f\uff0c\u4e43\u662f\u4e0d\u540c\u4eba\u683c\u7684\u878d\u5408\uff0c\u8fd9\u5c31\u5f88\u5bb9\u6613\u51fa\u95ee\u9898\u3002\u201c\u8bb0\u5fc6\u201d\u5176\u5b9e\u5012\u4e0d\u662f\u5173\u952e\uff0c\u4e00\u4e2a\u4eba\u6709\u4e24\u6bb5\u4e0d\u540c\u7684\u8bb0\u5fc6\uff0c\u6216\u8bb8\u4e00\u5f00\u59cb\u4f1a\u56f0\u6270\uff0c\u4f46\u5f88\u5bb9\u6613\u5c31\u4f1a\u9002\u5e94\uff1b\u7531\u60c5\u611f\u3001\u610f\u8bc6\u6784\u6210\u7684\u4eba\u683c\u624d\u662f\u95ee\u9898\u6240\u5728\u3002\u6bd4\u5982\u8bf4\uff0c\u67d0\u4eba\u524d\u4e16\u559c\u6b22\u9752\u6912\uff0c\u6bcf\u987f\u5fc5\u5403\uff0c\u6b64\u4e16\u5374\u8d85\u7ea7\u53cd\u611f\u9752\u6912\uff0c\u559c\u6b22\u9c7c\u4e38\u7c97\u9762\uff0c\u8fd9\u5c31\u6709\u70b9\u9ebb\u70e6\uff1b\u5982\u679c\u67d0\u4eba\u524d\u4e16\u662f\u4e2a\u50e7\u4fa3\uff0c\u70ed\u8877\u4e8e\u5403\u72d7\u8089\uff0c\u6b64\u4e16\u5374\u662f\u4e2a\u5c0f\u52a8\u7269\u4fdd\u62a4\u534f\u4f1a\u6210\u5458\uff0c\u7ecf\u5e38\u9ad8\u901f\u62e6\u8f66\uff0c\u90a3\u9ebb\u70e6\u53ef\u5c31\u5927\u4e86\uff0c\u7cbe\u795e\u5d29\u6e83\u6307\u65e5\u53ef\u5f85\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u81ea\u89c9\u7f6a\u5b7d\u6df1\u91cd\uff0c\u81ea\u6740\u8c22\u7f6a\u4e5f\u672a\u53ef\u77e5\u3002</p><p>\u4eba\u6bd5\u7adf\u4e0d\u662f\u673a\u5668\uff0c\u662f\u6709\u8840\u6709\u8089\u6709\u611f\u60c5\u7684\uff0c\u800c\u8fd9\u201c\u611f\u60c5\u201d\u5c31\u662f\u4e0d\u540c\u4eba\u683c\u878d\u5408\u7684\u6700\u5927\u969c\u788d\u3002\u7b2c\u4e94\u79d8\u5668\u65e2\u7136\u53f7\u79f0\u53ef\u4ee5\u5c06\u201c\u91cd\u751f\u201d\u7684\u98ce\u9669\u964d\u5230\u6700\u4f4e\uff0c\u81ea\u7136\u8981\u4ece\u8fd9\u65b9\u9762\u7740\u624b\u3002\u6362\u4e86\u743c\u6069\u662f\u8bbe\u8ba1\u8005\uff0c\u601d\u8def\u4e5f\u5f88\u7b80\u5355\uff0c\u65e2\u7136\u591a\u91cd\u4eba\u683c\u96be\u4ee5\u878d\u5408\uff0c\u90a3\u5c06\u8f83\u5f31\u7684\u4eba\u683c\u6d88\u706d\u6389\u5c31\u662f\uff0c\u65e2\u7136\u611f\u60c5\u662f\u969c\u788d\uff0c\u90a3\u628a\u611f\u60c5\u6e05\u9664\u6389\u5c31\u662f\u4e86\u3002\u76f8\u6bd4\u8d77\u524d\u4e16\uff0c\u6b64\u4e16\u5e74\u5c81\u5c0f\uff0c\u7ecf\u5386\u5c11\uff0c\u5728\u4e24\u8005\u53ea\u80fd\u9009\u5176\u4e00\u7684\u60c5\u51b5\u4e0b\uff0c\u663e\u7136\u662f\u727a\u7272\u7684\u9996\u9009\u3002</p><p>\u4e5f\u5373\u662f\u8bf4\uff0c\u5018\u82e5\u6309\u7167\u5965\u5609\u83b1\u65af\u7684\u8bbe\u60f3\uff0c\u73ca\u5609\u201c\u91cd\u751f\u201d\u4e4b\u540e\uff0c\u6216\u8bb8\u4f9d\u7136\u8fd8\u4f1a\u8bb0\u5f97\u743c\u6069\uff0c\u4f46\u4e5f\u4ec5\u4ec5\u53ea\u662f\u201c\u8bb0\u5f97\u201d\u800c\u5df2\u3002\u8fd9\u5341\u591a\u5e74\u7684\u76f8\u6fe1\u4ee5\u6cab\uff0c\u4ea6\u4eb2\u4eba\u4ea6\u7231\u4fa3\u7684\u60c5\u611f\u7f81\u7eca\uff0c\u4fbf\u8981\u968f\u98ce\u901d\u53bb\uff0c\u4e0d\u590d\u5b58\u5728\u4e86\u3002</p><p>\u743c\u6069\u65e0\u6cd5\u63a5\u53d7\u3002</p><p>\u201c\u8fd8\u6709\u4ef6\u4e8b\u60c5\uff0c\u201d\u4ed6\u52c9\u5f3a\u632f\u4f5c\u7cbe\u795e\uff0c\u201c\u5965\u5609\u83b1\u65af\u8bf4\u83b1\u62c9\u73b0\u5728\u5728\u5979\u624b\u91cc\uff0c\u4f60\u600e\u4e48\u770b\u3002\u201d</p><p>\u9b54\u59ec\u5fae\u5fae\u76b1\u7709\uff0c\u201c\u4e0d\u597d\u8bf4\uff0c\u201d\u5979\u6c89\u541f\u7740\uff0c\u201c\u6309\u7406\u8bf4\u5979\u6ca1\u5fc5\u8981\u6492\u8c0e\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5e94\u8be5\u4e0d\u5047\u3002\u800c\u4e14\u5979\u5fc5\u5b9a\u6709\u4ec0\u4e48\u76d8\u7b97\uff0c\u4f46\u6211\u4e00\u65f6\u770b\u4e0d\u51fa\u6765\u3002\u201d</p><p>\u201c\u95ee\u9898\u662f\u73b0\u5728\u51ef\u5c14\u672c\u5df2\u7ecf\u6709\u6240\u6000\u7591\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90a3\u6211\u4eec\u662f\u4e0d\u662f\u8981\u65e9\u505a\u51c6\u5907\uff0c\u4e07\u4e00\u2026\u2026\u201d</p><p>\u624e\u745e\u5c14\u6447\u6447\u5934\uff0c\u201c\u8fd9\u4e2a\u5012\u4e0d\u7528\u592a\u62c5\u5fc3\uff0c\u5982\u4eca\u8fd9\u79cd\u5c40\u9762\uff0c\u6ca1\u6709\u771f\u51ed\u5b9e\u636e\uff0c\u51ef\u5c14\u672c\u4e0d\u4f1a\u52a8\u624b\u7684\u3002\u201d</p><p>\u5bf9\u4e8e\u51ef\u5c14\u672c\u800c\u8a00\uff0c\u73b0\u5728\u7684\u5f62\u52bf\u6709\u4e9b\u5fae\u5999\u3002\u83b1\u62c9\u9047\u88ad\u5931\u8e2a\u662f\u4e8b\u5b9e\uff0c\u4f46\u5979\u7a76\u7adf\u843d\u5230\u8c01\u7684\u624b\u91cc\uff0c\u5c31\u5f88\u96be\u8bf4\u4e86\uff0c\u6709\u53ef\u80fd\u8fd8\u662f\u8428\u9a6c\u65af\u7279\uff0c\u4e5f\u6709\u53ef\u80fd\u662f\u7b2c\u4e09\u65b9\u63d2\u624b\u3002\u76ee\u524d\u5728\u9634\u5f71\u9547\uff0c\u80fd\u591f\u79f0\u5f97\u4e0a\u201c\u7b2c\u4e09\u65b9\u201d\u7684\u5176\u5b9e\u5e76\u4e0d\u5c11\uff0c\u743c\u6069\u4e00\u884c\u4eba\u53ea\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u53ea\u662f\u5acc\u7591\u6bd4\u8f83\u5927\u800c\u5df2\uff0c\u51ef\u5c14\u672c\u624b\u4e2d\u5e76\u65e0\u4efb\u4f55\u8bc1\u636e\u3002\u82e5\u5728\u5e73\u65f6\uff0c\u8bc1\u636e\u4ec0\u4e48\u7684\u5012\u4e5f\u4e0d\u91cd\u8981\uff0c\u6709\u5acc\u7591\u5c31\u8db3\u591f\u4e86\uff0c\u5148\u6293\u8d77\u6765\u5ba1\u95ee\u4e00\u756a\u518d\u8bf4\uff1b\u4f46\u73b0\u5728\u662f\u7279\u6b8a\u65f6\u671f\uff0c\u5f3a\u654c\u5f53\u524d\uff0c\u51b3\u6218\u5728\u5373\uff0c\u9634\u5f71\u9547\u4e2d\u50cf\u743c\u6069\u3001\u6885\u83f2\u65af\u8fd9\u6837\u5e76\u4e0d\u96b6\u5c5e\u4e8e\u9b54\u6cd5\u5973\u795e\u6559\u4f1a\uff0c\u53ea\u662f\u51fa\u4e8e\u5404\u79cd\u539f\u56e0\u6765\u52a9\u62f3\u7684\u4e0d\u5c11\uff0c\u51ef\u5c14\u672c\u4e0d\u80fd\u4e0d\u987e\u5fcc\u4ed6\u4eec\u7684\u611f\u53d7\u3002\u5176\u5b9e\u6700\u5173\u952e\u7684\uff0c\u8fd8\u662f\u5728\u4e8e\u5b9e\u529b\uff0c\u743c\u6069\u8fd9\u8fb9\u5e76\u975e\u4ec0\u4e48\u8f6f\u67ff\u5b50\uff0c\u4e00\u4f4d\u9690\u85cf\u5e55\u540e\u7684\u8010\u745f\u5927\u5965\u672f\u5e08\uff0c\u4e00\u4f4d\u6709\u4f4d\u9762\u6cd5\u5219\u52a0\u6301\u7684\u5927\u9b54\u9b3c\uff0c\u8fd9\u4e24\u4eba\u5c31\u5df2\u7ecf\u8db3\u591f\u96be\u7f20\u4e86\uff0c\u743c\u6069\u3001\u6885\u83f2\u65af\u3001\u51db\u7b49\u4eba\u4e5f\u4e2a\u4e2a\u90fd\u975e\u5f31\u8005\uff0c\u4e0d\u662f\u53ef\u4ee5\u4efb\u4eba\u6b3a\u4fae\u7684\u3002\u8fd9\u91cc\u53c8\u4e0d\u662f\u6df1\u6c34\u57ce\uff0c\u5e76\u975e\u51ef\u5c14\u672c\u7684\u81ea\u5bb6\u5730\u76d8\uff0c\u4ed6\u53ea\u662f\u4e34\u65f6\u7684\u9996\u9886\uff0c\u6240\u80fd\u8c03\u52a8\u7684\u529b\u91cf\u9887\u4e3a\u6709\u9650\uff0c\u6709\u4e9b\u4e8b\u60c5\u5c31\u6ca1\u6cd5\u968f\u5fc3\u6240\u6b32\u4e86\u3002</p><p>\u201c\u5f52\u6839\u5230\u5e95\uff0c\u8fd8\u662f\u5b9e\u529b\u6700\u91cd\u8981\uff0c\u201d\u743c\u6069\u611f\u53f9\uff0c\u201c\u90a3\u6211\u4eec\u5c31\u4ec0\u4e48\u90fd\u4e0d\u7528\u505a\uff0c\u9759\u89c2\u5176\u53d8\uff1f\u201d</p><p>\u624e\u745e\u5c14\u60f3\u4e86\u7247\u523b\uff0c\u201c\u9759\u89c2\u5176\u53d8\u672a\u5c1d\u4e0d\u53ef\uff0c\u4f46\u592a\u6d88\u6781\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u89c9\u5f97\u8fd8\u662f\u8981\u8bd5\u63a2\u4e00\u4e0b\u6bd4\u8f83\u597d\u3002\u201d</p><p>\u201c\u5982\u4f55\u8bd5\u63a2\uff1f\u201d</p><p>\u201c\u51ef\u5c14\u672c\u4e0d\u662f\u9001\u4e86\u4e00\u4efd\u793c\u7269\u4e48\uff0c\u793c\u5c1a\u5f80\u6765\uff0c\u5965\u5609\u83b1\u65af\u5973\u58eb\u4e5f\u5e94\u8be5\u56de\u8d60\u70b9\u4ec0\u4e48\u5427\u3002\u201d</p><p>\u201c\u56de\u8d60\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u90fd\u5df2\u7ecf\u51c6\u5907\u597d\u4e86\u5417\uff1f\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u81ea\u7136\u662f\u90a3\u4f4d\u9f99\u5deb\u6559\u5185\u5978\u7684\u540d\u5b57\u3002\u201d</p><p>\u743c\u6069\u786e\u5b9e\u662f\u6709\u8fd9\u4e2a\u60f3\u6cd5\uff0c\u6240\u4ee5\u624d\u4f1a\u7279\u5730\u95ee\u4e86\u5965\u5609\u83b1\u65af\uff0c\u4f46\u5230\u5e95\u8981\u4e0d\u8981\u8fd9\u4e48\u505a\uff0c\u4ed6\u5176\u5b9e\u8fd8\u5728\u72b9\u8c6b\uff0c\u56e0\u4e3a\u4ed6\u63a8\u7b97\u4e0d\u6e05\u695a\u540e\u679c\u3002\u4f46\u624e\u745e\u5c14\u663e\u7136\u4e0d\u8fd9\u4e48\u8ba4\u4e3a\uff0c\u201c\u65e0\u6240\u8c13\u554a\uff0c\u5c40\u9762\u8d8a\u6df7\u4e71\uff0c\u5bf9\u6211\u4eec\u8d8a\u6709\u5229\uff0c\u4e0d\u662f\u5417\uff1f\u201d</p><p>\u201c\u4e5f\u662f\u3002\u201d</p><p>\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u70b9\u5934\u8868\u793a\u8d5e\u540c\u3002\u201c\u8c22\u8c22\u3002\u201d\u4ed6\u8bda\u6073\u5730\u8bf4\u3002</p><p>\u81ea\u5df1\u4e00\u4e2a\u4eba\u8003\u8651\u95ee\u9898\uff0c\u5f80\u5f80\u4f1a\u51fa\u73b0\u5404\u79cd\u7f3a\u6f0f\uff0c\u5f88\u591a\u65f6\u5019\u90fd\u9700\u8981\u6709\u4e2a\u4eba\u5e2e\u5fd9\u53c2\u8c0b\uff0c\u4ea4\u6d41\u5206\u6790\uff0c\u80fd\u591f\u8ba9\u601d\u8def\u66f4\u52a0\u6e05\u695a\u3002\u4e00\u76f4\u4ee5\u6765\uff0c\u5728\u743c\u6069\u8eab\u8fb9\u62c5\u4efb\u8fd9\u4e2a\u201c\u53c2\u8c0b\u201d\u5de5\u4f5c\u7684\u662f\u6885\u83f2\u65af\uff0c\u5979\u806a\u660e\u673a\u667a\uff0c\u89c1\u591a\u8bc6\u5e7f\uff0c\u80fd\u529b\u662f\u6bcb\u5eb8\u7f6e\u7591\u7684\u3002\u4f46\u6709\u4e9b\u4e8b\u60c5\uff0c\u743c\u6069\u5b9e\u5728\u4e0d\u65b9\u4fbf\u548c\u5979\u5546\u91cf\uff0c\u751a\u81f3\u6839\u672c\u4e0d\u80fd\u8ba9\u5979\u77e5\u9053\uff0c\u6bd5\u7adf\u7acb\u573a\u4e0d\u662f\u5b8c\u5168\u4e00\u81f4\u7684\u3002\u9664\u4e86\u6885\u83f2\u65af\u4e4b\u5916\uff0c\u5176\u4ed6\u4eba\u90fd\u96be\u5f53\u6b64\u4efb\u3002\u73ca\u5609\u867d\u7136\u806a\u660e\uff0c\u4f46\u9650\u4e8e\u7ecf\u9a8c\u4e0d\u8db3\uff0c\u9605\u5386\u4e0d\u591f\uff1b\u838e\u73de\u514b\u7b97\u662f\u7cbe\u660e\uff0c\u7acb\u573a\u4e5f\u6ca1\u95ee\u9898\uff0c\u4f46\u5979\u603b\u662f\u51fa\u4e00\u4e9b\u201c\u770b\u8d77\u6765\u5f88\u7f8e\u597d\uff0c\u5b9e\u65bd\u8d77\u6765\u5c31\u4e00\u584c\u7cca\u6d82\u201d\u7684\u998a\u4e3b\u610f\uff0c\u743c\u6069\u5bf9\u5979\u5b9e\u5728\u6ca1\u4ec0\u4e48\u4fe1\u5fc3\uff1b\u81f3\u4e8e\u51db\u2026\u2026\u5c31\u76f4\u63a5\u5ffd\u7565\u5427\u3002\u73b0\u5728\u6709\u4e86\u624e\u745e\u5c14\uff0c\u7b97\u662f\u586b\u8865\u4e86\u8fd9\u4e2a\u7a7a\u767d\u3002</p><p>\u201c\u90a3\u662f\u5f53\u7136\uff0c\u201d\u624e\u745e\u5c14\u7b11\u7740\u8bf4\uff0c\u201c\u66fe\u7ecf\u6709\u4eba\u5bf9\u6211\u8bf4\uff1a\u2018\u6bcf\u4e00\u4e2a\u6210\u529f\u7684\u7537\u4eba\u80cc\u540e\uff0c\u90fd\u6709\u4e24\u4e2a\u652f\u6301\u4ed6\u7684\u5973\u4eba\uff0c\u4e00\u4e2a\u5728\u9633\u5149\u4e0b\uff0c\u4e00\u4e2a\u5728\u6697\u5f71\u4e2d\u2019\u3002\u6211\u4e00\u76f4\u60f3\u505a\u540e\u8005\uff0c\u4e5f\u53ea\u60f3\u505a\u540e\u8005\uff0c\u53ef\u60dc\u59d0\u59d0\u548c\u5c0f\u96c5\u90fd\u4e0d\u4fe1\u2026\u2026\u201d</p><p>\u201c\u5c0f\u96c5\uff1f\u201d\u743c\u6069\u4e00\u6014\uff0c\u201c\u90a3\u662f\u8c01\uff1f\u201d</p><p>\u201c\u4e00\u70b9\u90fd\u4e0d\u8bb0\u5f97\u4e86\u5417\uff1f\u201d\u624e\u745e\u5c14\u770b\u7740\u4ed6\uff0c\u201c\u5979\u662f\u4f60\u7684\u672a\u5a5a\u59bb\uff0c\u662f\u4f60\u6240\u6709\u7684\u5973\u53cb\u4e4b\u4e2d\u552f\u4e00\u5f97\u5230\u59d0\u59d0\u8ba4\u53ef\u7684\uff0c\u662f\u4e00\u4e2a\u2026\u2026\u975e\u5e38\u975e\u5e38\u806a\u660e\u7684\u5973\u4eba\u3002\u201d</p><p>\u743c\u6069\u542c\u51fa\u5979\u8bed\u6c14\u4e2d\u9690\u542b\u7684\u60c5\u7eea\uff0c\u624e\u745e\u5c14\u7684\u8bc4\u4ef7\u5e76\u975e\u5982\u5b57\u9762\u610f\u4e49\u90a3\u6837\u5145\u6ee1\u79f0\u8d5e\uff0c\u201c\u6bd4\u4f60\u8fd8\u8981\u806a\u660e\u5417\uff1f\u201d\u4ed6\u534a\u5f00\u73a9\u7b11\u5730\u95ee\u3002</p><p>\u201c\u6211\u4e0d\u60f3\u627f\u8ba4\uff0c\u4f46\u6211\u786e\u5b9e\u5728\u5979\u624b\u4e0a\u5403\u8fc7\u4e8f\uff0c\u201d\u624e\u745e\u5c14\u5766\u7136\u8bf4\uff0c\u201c\u5982\u679c\u6709\u673a\u4f1a\u7684\u8bdd\uff0c\u6211\u5e0c\u671b\u80fd\u591f\u62a5\u4e00\u7bad\u4e4b\u4ec7\u3002\u201d</p><p>\u201c\u8fd9\u6050\u6015\u662f\u6ca1\u673a\u4f1a\u4e86\u5427\uff0c\u4f0a\u739b\u65af\u5361\u90fd\u706d\u4ea1\u6570\u5343\u5e74\u4e86\uff0c\u4eba\u5bb6\u53ea\u662f\u4e2a\u51e1\u4eba\uff0c\u54ea\u80fd\u6d3b\u5230\u73b0\u5728\u3002\u201d</p><p>\u201c\u90a3\u4e5f\u672a\u5fc5\u5462\u3002\u201d</p><p>\u624e\u745e\u5c14\u660e\u663e\u8bdd\u4e2d\u6709\u8bdd\uff0c\u4f46\u743c\u6069\u4e5f\u6709\u4e9b\u7d2f\u4e86\uff0c\u5b9e\u5728\u61d2\u5f97\u518d\u8d39\u5fc3\u601d\u53bb\u731c\u6d4b\u3002\u201c\u5e7d\u51a5\u5730\u5e9c\uff0c\u201d\u4ed6\u7a81\u7136\u8bf4\uff0c\u201c\u4f60\u5bf9\u8fd9\u4e2a\u4e86\u89e3\u591a\u5c11\uff1f\u201d</p><p>\u201c\u7565\u77e5\u4e00\u4e8c\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u4e0d\u8fc7\u90fd\u662f\u542c\u95fb\uff0c\u5e76\u65e0\u5b9e\u8bc1\u3002\u5012\u662f\u6709\u4e00\u4e2a\u6545\u4e8b\uff0c\u4e0e\u6b64\u6709\u5173\uff0c\u4f60\u6216\u8bb8\u4f1a\u611f\u5174\u8da3\u3002\u201d</p><p>\u201c\u54e6\uff1f\u201d\u743c\u6069\u7684\u786e\u88ab\u52fe\u8d77\u4e86\u5174\u8da3\uff0c\u201c\u4ec0\u4e48\u6545\u4e8b\uff1f\u201d</p><p>\u201c\u4e00\u4e2a\u8bda\u5b9e\u7684\u9b54\u9b3c\u548c\u72e1\u8bc8\u7684\u5947\u68b0\u5e08\u7684\u6545\u4e8b\u3002\u201d</p><p>\u5728\u4e5d\u5c42\u5730\u72f1\u7684\u9ad8\u9636\u9b54\u9b3c\u4e4b\u4e2d\uff0c\u6d41\u4f20\u7740\u4e00\u4e2a\u6545\u4e8b\u3002\u6545\u4e8b\u662f\u8fd9\u6837\u8bf4\u7684\uff1a\u5728\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u51e1\u95f4\u6709\u4e00\u4e2a\u5deb\u5e08\u7edf\u6cbb\u7684\u4eba\u7c7b\u738b\u56fd\uff0c\u5deb\u5e08\u4eec\u62e5\u6709\u5f3a\u5927\u7684\u529b\u91cf\u548c\u65e0\u5c3d\u7684\u8d22\u5bcc\uff0c\u4f46\u5374\u4ecd\u4e0d\u6ee1\u8db3\uff0c\u56e0\u4e3a\u4f5c\u4e3a\u51e1\u4eba\uff0c\u6b7b\u4ea1\u4ecd\u7136\u662f\u4ed6\u4eec\u65e0\u6cd5\u9003\u8131\u7684\u9634\u5f71\u3002</p><p>\u67d0\u4e00\u65e5\uff0c\u4e00\u4f4d\u5deb\u5e08\u7a81\u7136\u6765\u5230\u4e5d\u5c42\u5730\u72f1\uff0c\u5728\u7ecf\u8fc7\u4e00\u756a\u53cb\u597d\u4ea4\u6d89\u4e4b\u540e\uff0c\u5deb\u5e08\u89c1\u5230\u4e86\u4e5d\u72f1\u4e4b\u4e3b\u963f\u65af\u8482\u83ab\u65af\u3002\u5deb\u5e08\u5411\u4e5d\u72f1\u4e4b\u4e3b\u63d0\u8bae\uff0c\u613f\u610f\u5e2e\u52a9\u9b54\u9b3c\u94f8\u9020\u4e00\u4ef6\u795e\u5668\u3002</p><p>\u201c\u4e00\u65e6\u5b83\u94f8\u6210\uff0c\u51e1\u95f4\u6240\u6709\u7684\u667a\u6167\u751f\u7269\uff0c\u90fd\u4f1a\u5728\u6b7b\u4ea1\u4e4b\u524d\u6536\u5230\u4e00\u4efd\u8981\u7ea6\uff0c\u201d\u5deb\u5e08\u4ecb\u7ecd\u8bf4\uff0c\u201c\u5982\u679c\u63a5\u53d7\u8fd9\u4efd\u8981\u7ea6\uff0c\u5219\u5951\u7ea6\u6210\u7acb\u3002\u965b\u4e0b\u5373\u53ef\u4ee5\u636e\u6b64\u5951\u7ea6\uff0c\u6536\u53d6\u4ed6\u4eec\u7684\u7075\u9b42\u3002\u201d</p><p>\u8fd9\u662f\u4e00\u4e2a\u4ee4\u9b54\u9b3c\u65e0\u6cd5\u62d2\u7edd\u7684\u63d0\u8bae\u3002\u5728\u90a3\u4e2a\u65f6\u4ee3\uff0c\u8bf8\u795e\u5bf9\u51e1\u95f4\u7684\u5e72\u6d89\u5e76\u4e0d\u5f3a\uff0c\u800c\u4e14\u6b7b\u795e\u7684\u795e\u4f4d\u7a7a\u60ac\uff0c\u9664\u4e86\u6781\u5c11\u6570\u51e1\u4eba\u6b7b\u540e\u80fd\u591f\u6709\u5e78\u5347\u4e0a\u795e\u56fd\uff0c\u7edd\u5927\u591a\u6570\u90fd\u662f\u4eba\u6b7b\u9b42\u6563\u3002\u56e0\u6b64\u9b54\u9b3c\u7684\u201c\u4e1a\u52a1\u201d\u5f88\u597d\u5f00\u5c55\uff0c\u5f88\u591a\u51e1\u4eba\u4e0d\u7518\u5fc3\u5c31\u6b64\u6e6e\u706d\uff0c\u5b81\u613f\u8f6c\u5316\u4e3a\u90aa\u9b54\u3002\u4f46\u9b54\u9b3c\u6ca1\u529e\u6cd5\u5927\u89c4\u6a21\u8fdb\u5165\u51e1\u95f4\uff0c\u53ea\u80fd\u641e\u201c\u96f6\u552e\u201d\uff0c\u5c0f\u6253\u5c0f\u95f9\uff0c\u4e0d\u6210\u6c14\u5019\u3002\u5982\u679c\u5deb\u5e08\u6240\u8bf4\u7684\u8fd9\u4ef6\u795e\u5668\u5f53\u771f\u80fd\u591f\u94f8\u6210\uff0c\u90a3\u5c31\u76f8\u5f53\u4e8e\u4e5d\u5c42\u5730\u72f1\u7684\u63a8\u9500\u5458\u628a\u5ba3\u4f20\u5355\u53d1\u5230\u4e86\u6bcf\u4e00\u6237\u7684\u5bb6\u95e8\u53e3\uff0c\u800c\u4e14\u8fd8\u662f\u5f53\u573a\u4e0b\u5355\uff0c\u5373\u65f6\u7b7e\u7ea6\uff0c\u7acb\u523b\u5c65\u884c\uff0c\u5176\u610f\u4e49\u4e0d\u8a00\u800c\u55bb\u3002</p><p>\u201c\u542c\u8d77\u6765\u5f88\u597d\uff0c\u201d\u4e5d\u72f1\u4e4b\u4e3b\u95ee\uff0c\u201c\u90a3\u4f60\u4eec\u7684\u6761\u4ef6\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6240\u6709\u2018\u7687\u5ba4\u2019\u2014\u2014\u8fd9\u662f\u6211\u4eec\u62e5\u6709\u2018\u5370\u8bb0\u2019\u7684\u5947\u68b0\u5e08\u7684\u81ea\u79f0\u2014\u2014\u4e0d\u88ab\u5951\u7ea6\u9650\u5236\uff0c\u4e0d\u53d7\u965b\u4e0b\u7ea6\u675f\uff0c\u53ef\u4ee5\u81ea\u4e3b\u51b3\u5b9a\u8f6e\u56de\u8f6c\u4e16\u3002\u201d</p><p>\u4e5d\u72f1\u4e4b\u4e3b\u7b54\u5e94\u4e86\uff0c\u4e3a\u4e86\u94f8\u9020\u8fd9\u4ef6\u88ab\u547d\u540d\u4e3a\u201c\u5e7d\u51a5\u5730\u5e9c\u201d\u7684\u795e\u5668\uff0c\u4ed6\u51c6\u8bb8\u5deb\u5e08\u501f\u7528\u4e5d\u72f1\u7684\u529b\u91cf\u3002\u5deb\u5e08\u82b1\u4e86\u5341\u4e8c\u5e74\u7684\u65f6\u95f4\uff0c\u7ecf\u5386\u4e86\u65e0\u6570\u6b21\u5931\u8d25\uff0c\u6700\u7ec8\u53d6\u5f97\u6210\u529f\u3002\u6309\u7167\u4e8b\u5148\u7684\u7ea6\u5b9a\uff0c\u5e94\u5f53\u7531\u4e5d\u72f1\u4e4b\u4e3b\u5bf9\u795e\u5668\u8d50\u4e88\u90aa\u86c7\u7684\u795d\u798f\uff0c\u7136\u540e\u53cc\u65b9\u5728\u4f4d\u9762\u6cd5\u5219\u7684\u89c1\u8bc1\u4e0b\u5b8c\u6210\u5951\u7ea6\u3002\u4ece\u6b64\u4e4b\u540e\uff0c\u9b54\u9b3c\u6267\u638c\u5e7d\u51a5\u5730\u5e9c\uff0c\u6536\u53d6\u51e1\u4eba\u7684\u7075\u9b42\uff1b\u800c\u201c\u7687\u5ba4\u201d\u8d85\u8131\u751f\u6b7b\uff0c\u81ea\u7531\u8f6e\u56de\u3002</p><p>\u4f46\u5deb\u5e08\u4ece\u672a\u771f\u7684\u6253\u7b97\u548c\u9b54\u9b3c\u5171\u4eab\u8fd9\u4ef6\u795e\u5668\uff0c\u4ed6\u8fdd\u80cc\u4e86\u627f\u8bfa\uff0c\u9003\u79bb\u5730\u72f1\uff0c\u5c06\u795e\u5668\u5e26\u56de\u51e1\u95f4\u3002\u6ca1\u6709\u5f97\u5230\u90aa\u86c7\u795d\u798f\u7684\u795e\u5668\u79f0\u4e0d\u4e0a\u662f\u5706\u6ee1\u5b8c\u5de5\uff0c\u5b83\u53ea\u80fd\u7b97\u662f\u4e00\u4ef6\u534a\u6210\u54c1\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u5b83\u4f9d\u7136\u80fd\u591f\u8fbe\u5230\u5deb\u5e08\u7684\u76ee\u7684\uff1a\u8ba9\u6781\u5c11\u90e8\u5206\u4eba\u2014\u2014\u201c\u7687\u5ba4\u201d\u2014\u2014\u80fd\u591f\u5b89\u5168\u5730\u8f6c\u4e16\u8f6e\u56de\uff0c\u5e76\u4e14\u4fdd\u7559\u8bb0\u5fc6\u3002</p><p>\u201c\u8fd9\u4e2a\u5deb\u5e08\u662f\u8c01\uff1f\u201d\u743c\u6069\u6709\u4e9b\u5fd0\u5fd1\u4e0d\u5b89\u5730\u95ee\u3002</p><p>\u201c\u4e0d\u662f\u4f60\u5566\uff0c\u522b\u90a3\u4e48\u7591\u795e\u7591\u9b3c\uff0c\u201d\u624e\u745e\u5c14\u683c\u683c\u5a07\u7b11\uff0c\u201c\u7b2c\u4e94\u79d8\u5668\u7684\u94f8\u9020\u65f6\u95f4\uff0c\u65e9\u5728\u4f60\u51fa\u751f\u4e4b\u524d\u4e03\u767e\u591a\u5e74\u5462\u3002\u90a3\u4f4d\u5947\u68b0\u5e08\u867d\u7136\u94f8\u9020\u51fa\u79d8\u5668\uff0c\u81ea\u5df1\u5374\u672a\u80fd\u6210\u529f\u8f6c\u4e16\uff0c\u6700\u7ec8\u5927\u9650\u5230\u6765\uff0c\u9b42\u98de\u9b44\u6563\u4e86\u3002\u201d</p><p>\u7b2c\u4e00\u767e\u4e00\u5341\u56db\u8282</p><p>\u743c\u6069\u6c89\u601d\u4e86\u4e00\u4f1a\uff0c\u201c\u6309\u7167\u8fd9\u79cd\u8bf4\u6cd5\uff0c\u7b2c\u4e94\u79d8\u5668\u76ee\u524d\u53ea\u662f\u4e00\u4e2a\u534a\u6210\u54c1\uff0c\u5b83\u8fd8\u53ef\u4ee5\u53d8\u5f97\u66f4\u5f3a\uff1f\u201d</p><p>\u201c\u524d\u63d0\u662f\u8fd9\u4e2a\u6545\u4e8b\u662f\u771f\u5b9e\u7684\u3002\u201d\u624e\u745e\u5c14\u63d0\u9192\u3002</p><p>\u7684\u786e\uff0c\u6545\u4e8b\u7ec8\u7a76\u53ea\u662f\u6545\u4e8b\uff0c\u5e76\u4e0d\u4e00\u5b9a\u5c5e\u5b9e\uff0c\u6216\u8005\u8bf4\uff0c\u5b83\u80af\u5b9a\u4e0d\u662f\u5b8c\u5168\u5c5e\u5b9e\u3002\u6309\u7167\u8fd9\u4e2a\u6545\u4e8b\u7684\u63cf\u8ff0\uff0c\u5947\u68b0\u5e08\u662f\u9634\u9669\u72e1\u8bc8\u7684\uff0c\u80cc\u4fe1\u5f03\u4e49\u7684\uff0c\u800c\u5802\u5802\u4e5d\u72f1\u4e4b\u4e3b\u3001\u9b54\u9b3c\u9886\u8896\uff0c\u7b80\u76f4\u5355\u7eaf\u7684\u5b9b\u5982\u5c0f\u767d\u5154\uff0c\u8fd9\u663e\u7136\u662f\u5728\u641e\u7b11\uff0c\u800c\u4e14\u8fd8\u662f\u4e25\u91cd\u7f3a\u4e4f\u6280\u672f\u542b\u91cf\u7684\u641e\u7b11\u3002\u4f46\u6545\u4e8b\u4e5f\u672a\u5fc5\u662f\u5b8c\u5168\u865a\u6784\uff0c\u6beb\u65e0\u6839\u636e\u3002\u6709\u5173\u201c\u7b2c\u4e94\u79d8\u5668\u201d\u7684\u6765\u5386\uff0c\u743c\u6069\u4e5f\u542c\u4e0d\u6b62\u4e00\u4e2a\u4eba\u63d0\u8fc7\uff0c\u8bf4\u6cd5\u5404\u4e0d\u76f8\u540c\uff0c\u81f3\u5c11\u6709\u4e00\u70b9\u7ec6\u8282\u662f\u5171\u901a\u7684\uff0c\u5947\u68b0\u5e08\u5728\u94f8\u9020\u7b2c\u4e94\u79d8\u5668\u65f6\uff0c\u5f97\u5230\u9b54\u9b3c\u7684\u5e2e\u52a9\u2014\u2014\u8fd9\u5176\u5b9e\u5c31\u5df2\u7ecf\u8bf4\u660e\u5f88\u591a\u95ee\u9898\u4e86\u3002</p><p>\u4e0d\u8fc7\uff0c\u5373\u4fbf\u6545\u4e8b\u5c5e\u5b9e\uff0c\u7b2c\u4e94\u79d8\u5668\u4ecd\u6709\u63d0\u5347\u7a7a\u95f4\uff0c\u90a3\u4e5f\u5f97\u53bb\u627e\u4e5d\u72f1\u4e4b\u4e3b\u5b8c\u6210\u6700\u540e\u7684\u795d\u798f\u3002\u8fd9\u538b\u6839\u4e0d\u73b0\u5b9e\uff0c\u4eba\u5bb6\u65e5\u7406\u4e07\u673a\uff0c\u54ea\u6709\u529f\u592b\u7406\u776c\u4f60\uff0c\u8fd8\u662f\u6d17\u6d17\u7761\u5427\u3002</p><p>\u4e0d\u8fc7\u5728\u7761\u89c9\u4e4b\u524d\uff0c\u8fd8\u662f\u5f97\u5148\u53bb\u529e\u4e8b\uff0c\u6bd5\u7adf\u65f6\u95f4\u7d27\u8feb\uff0c\u5df2\u7ecf\u6ca1\u6709\u591a\u5c11\u53ef\u4f9b\u803d\u8bef\u4e86\u3002\u743c\u6069\u53c8\u53bb\u62dc\u8bbf\u4e86\u4e00\u6b21\u51ef\u5c14\u672c\uff0c\u201c\u4ee3\u8868\u201d\u5965\u5609\u83b1\u65af\u8868\u8fbe\u4e86\u4e00\u756a\u611f\u8c22\uff0c\u8868\u793a\u5bf9\u793c\u7269\u5f88\u6ee1\u610f\uff0c\u7136\u540e\u900f\u9732\u4e86\u9f99\u5deb\u6559\u5185\u5978\u7684\u540d\u5b57\u3002\u51ef\u5c14\u672c\u5f53\u7136\u8981\u95ee\u4ed6\u7684\u4fe1\u606f\u6765\u6e90\uff0c\u743c\u6069\u65e9\u6709\u51c6\u5907\uff0c\u4fbf\u5e72\u8106\u65e0\u6bd4\u5730\u5168\u90e8\u63a8\u5230\u5965\u5609\u83b1\u65af\u8eab\u4e0a\uff0c\u201c\u8fd9\u662f\u5360\u661f\u5e08\u591c\u89c2\u5929\u8c61\uff0c\u5076\u6709\u6240\u5f97\u3002\u201d</p><p>\u51ef\u5c14\u672c\u77e5\u9053\u5965\u5609\u83b1\u65af\u7684\u6765\u5386\uff0c\u81ea\u7136\u4e5f\u77e5\u9053\u5979\u662f\u4e2a\u5360\u661f\u5e08\uff0c\u751a\u81f3\u6709\u8d44\u683c\u8dfb\u8eab\u4e8e\u53e4\u5f80\u4eca\u6765\u6700\u9ad8\u660e\u7684\u9884\u8a00\u5e08\u4e4b\u5217\u3002\u5360\u661f\u672f\u7384\u5999\u96be\u6d4b\uff0c\u53c8\u6beb\u65e0\u9053\u7406\u53ef\u8a00\uff08\u81f3\u5c11\u5728\u7edd\u5927\u591a\u6570\u4eba\u770b\u6765\u662f\u5982\u6b64\uff09\uff0c\u7528\u6765\u89e3\u91ca\u8fd9\u79cd\u6ca1\u6cd5\u89e3\u91ca\u7684\u4e8b\u60c5\u6700\u5408\u9002\u4e0d\u8fc7\u3002\u53cd\u6b63\u51ef\u5c14\u672c\u53c8\u4e0d\u53ef\u80fd\u548c\u5965\u5609\u83b1\u65af\u53bb\u5bf9\u8d28\uff0c\u743c\u6069\u4fe1\u53e3\u5f00\u6cb3\u6beb\u65e0\u538b\u529b\u3002</p><p>\u51ef\u5c14\u672c\u663e\u7136\u4e5f\u88ab\u5f04\u7cca\u6d82\u4e86\uff0c\u4ed6\u4e0d\u77e5\u9053\u743c\u6069\u6b64\u4e3e\u7528\u610f\u7a76\u7adf\u4f55\u5728\uff0c\u662f\u793a\u597d\uff0c\u6291\u6216\u793a\u5a01\uff1f\u4f46\u743c\u6069\u663e\u7136\u6ca1\u6709\u7ee7\u7eed\u6df1\u8c08\u7684\u6253\u7b97\uff0c\u9a6c\u9a6c\u864e\u864e\u628a\u573a\u9762\u8bdd\u8bf4\u5b8c\uff0c\u5c31\u8d76\u5feb\u8d77\u8eab\u544a\u8f9e\u4e86\u3002</p><p>\u518d\u6b21\u56de\u5230\u4f4f\u5904\uff0c\u624e\u745e\u5c14\u53c8\u4e0d\u89c1\u4e86\uff0c\u743c\u6069\u4e5f\u4e0d\u5728\u610f\uff0c\u8131\u4e86\u5916\u5957\uff0c\u8eba\u5728\u5e8a\u4e0a\u51c6\u5907\u4f11\u606f\u7247\u523b\uff0c\u773c\u89d2\u4f59\u5149\u7a81\u7136\u77a5\u89c1\u5367\u5ba4\u7684\u95e8\u88ab\u8f7b\u8f7b\u63a8\u5f00\u4e86\uff0c\u51db\u8e51\u624b\u8e51\u811a\u5730\u6e9c\u8fdb\u6765\u3002\u201c\u743c\u6069\uff0c\u201d\u5979\u538b\u4f4e\u58f0\u97f3\uff0c\u201c\u5979\u4e0d\u5728\u5427\uff1f\u201d</p><p>\u201c\u5979\uff1f\u8c01\uff1f\u201d\u743c\u6069\u6014\u4e86\u4e24\u79d2\u949f\uff0c\u7136\u540e\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u4f60\u8bf4\u624e\u745e\u5c14\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u5979\u4e0d\u5728\u2014\u2014\u5bf9\u4e86\uff0c\u4f60\u4e0d\u662f\u548c\u827e\u5f25\u8587\u901b\u8857\u53bb\u4e86\u4e48\uff0c\u4ec0\u4e48\u65f6\u5019\u53c8\u548c\u5979\u52fe\u642d\u4e0a\u7684\uff1f\u201d</p><p>\u201c\u4eba\u5bb6\u624d\u6ca1\u6709\uff01\u201d\u51db\u6c14\u9f13\u9f13\u5730\u8bf4\uff0c\u201c\u90fd\u662f\u5979\u6363\u7684\u9b3c\u3002\u201d</p><p>\u6309\u7167\u51db\u7684\u8bf4\u6cd5\uff0c\u5979\u548c\u6885\u83f2\u65af\u539f\u672c\u662f\u5728\u901b\u8857\uff0c\u4e2d\u9014\u6885\u83f2\u65af\u88ab\u8bf7\u53bb\u5f00\u4f1a\uff0c\u51db\u53ea\u597d\u4e00\u4e2a\u4eba\u56de\u6765\u3002\u5979\u5728\u5ba2\u5385\u6c99\u53d1\u4e0a\u4f11\u606f\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5c31\u7761\u7740\u4e86\uff0c\u8ff7\u8ff7\u7cca\u7cca\u95f4\u4f3c\u4e4e\u88ab\u8c01\u62b1\u8d77\u6765\uff0c\u53c8\u4eb2\u53c8\u6478\uff0c\u5979\u9690\u7ea6\u95f4\u6709\u6240\u611f\u89c9\uff0c\u5374\u65e0\u6cd5\u6e05\u9192\u3002\u81f3\u4e8e\u540e\u6765\u7684\u4e8b\u60c5\uff0c\u743c\u6069\u5c31\u77e5\u9053\u4e86\u3002</p><p>\u201c\u8fd9\u6837\u554a\uff0c\u201d\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9\u8bf4\u660e\u4f60\u8fd8\u662f\u5f88\u6709\u9b45\u529b\u7684\u561b\uff0c\u8fde\u5927\u9b54\u9b3c\u90fd\u60c5\u4e0d\u81ea\u7981\u3002\u201d</p><p>\u201c\u624d\u4e0d\u8981\u5462\uff0c\u201d\u51db\u626d\u8fc7\u8138\uff0c\u7136\u540e\u53c8\u60f3\u8d77\u4ec0\u4e48\uff0c\u201c\u5bf9\u4e86\uff0c\u743c\u6069\uff0c\u6211\u5c31\u662f\u8981\u8ddf\u4f60\u8bf4\uff0c\u8fd9\u4ef6\u4e8b\u522b\u544a\u8bc9\u827e\u5f25\u8587\u554a\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u53cd\u6b63\u4f60\u53c8\u4e0d\u662f\u81ea\u613f\uff0c\u4e0d\u7b97\u662f\u4f60\u79fb\u60c5\u522b\u604b\u7ea2\u674f\u51fa\u5899\uff0c\u4f60\u62c5\u5fc3\u4ec0\u4e48\u3002\u201d</p><p>\u201c\u53cd\u6b63\u4f60\u4e0d\u8981\u8bf4\u5c31\u662f\u5566\uff01\u201d</p><p>\u201c\u884c\u884c\uff0c\u6211\u4e0d\u8bf4\u3002\u201d</p><p>\u51db\u7684\u60f3\u6cd5\u5f88\u597d\u7406\u89e3\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u8c01\u8981\u662f\u542c\u5230\u81ea\u5df1\u5973\u53cb\u548c\u522b\u7684\u5973\u4eba\u641e\u5728\u4e00\u8d77\uff0c\u5373\u4fbf\u4e0d\u662f\u81ea\u613f\uff0c\u4e5f\u603b\u4e0d\u4f1a\u591a\u4e48\u9ad8\u5174\u3002\u4e0d\u8fc7\u8fd9\u91cc\u4f3c\u4e4e\u6709\u4e2a\u95ee\u9898\uff0c\u4e3a\u4ec0\u4e48\u4f60\u8bb0\u5f97\u8981\u7792\u7740\u827e\u5f25\u8587\uff0c\u5374\u5b8c\u5168\u5ffd\u7565\u4e86\u6211\u7684\u5b58\u5728\uff0c\u867d\u7136\u5927\u5bb6\u90fd\u77e5\u9053\u4f60\u662f\u4e2a\u857e\u4e1d\u8fb9\uff0c\u4f46\u81f3\u5c11\u5728\u660e\u9762\u4e0a\uff0c\u6211\u624d\u662f\u4f60\u7684\u7537\u53cb\u5427\u3002\u4f60\u8fd9\u6837\u539a\u6b64\u8584\u5f7c\uff0c\u5b9e\u5728\u8ba9\u4eba\u5fc3\u7406\u4e0d\u80fd\u5e73\u8861\u554a\u3002</p><p>\u65e2\u7136\u5fc3\u7406\u4e0d\u5e73\u8861\uff0c\u743c\u6069\u4fbf\u51b3\u5b9a\u7565\u65bd\u8584\u60e9\uff0c\u4ee5\u793a\u8b66\u544a\u3002\u4e8e\u662f\u4ed6\u4f38\u624b\u4e00\u628a\u5c06\u51db\u62c9\u5230\u6000\u91cc\uff0c\u5f00\u59cb\u8131\u5979\u7684\u8863\u670d\uff0c\u51db\u8c61\u5f81\u6027\u5730\u6323\u624e\u4e86\u51e0\u4e0b\uff0c\u4e5f\u5c31\u4e56\u4e56\u914d\u5408\u3002\u743c\u6069\u4e00\u65f6\u5174\u8d77\uff0c\u5c06\u4ece\u7ef4\u82e5\u62c9\u90a3\u91cc\u5b66\u6765\u7684\u7c89\u7ea2\u6cd5\u672f\u7528\u4e86\u51e0\u9053\uff0c\u51db\u987f\u65f6\u6e83\u4e0d\u6210\u519b\uff0c\u63a5\u8fde\u6cc4\u4e86\u4e03\u516b\u6b21\uff0c\u5c06\u534a\u8fb9\u5e8a\u5355\u6d78\u5f97\u900f\u6e7f\u3002\u4ed6\u610f\u72b9\u672a\u5c3d\uff0c\u5f85\u8981\u518d\u6218\uff0c\u5c11\u5973\u7684\u4e0b\u4f53\u79c1\u5904\u5df2\u7ecf\u662f\u53c8\u7ea2\u53c8\u80bf\uff0c\u4e0d\u582a\u627f\u53d7\uff0c\u743c\u6069\u4fbf\u5c06\u5979\u6446\u6210\u8dea\u4f0f\u7684\u59ff\u52bf\uff0c\u624b\u6307\u6cbe\u7740\u7231\u6db2\u6324\u5165\u540e\u5ead\uff0c\u6162\u6162\u65cb\u8f6c\uff0c\u4e3a\u5979\u70ed\u8eab\u3002</p><p>\u201c\u4e0d\u884c\u5566\uff0c\u5c41\u80a1\u4e0d\u884c\uff0c\u201d\u51db\u5df2\u7ecf\u662f\u660f\u660f\u6c89\u6c89\uff0c\u53ea\u6b8b\u7559\u7740\u6700\u540e\u4e00\u70b9\u6e05\u9192\uff0c\u762b\u8f6f\u7684\u8eab\u4f53\u5df2\u7ecf\u5b8c\u5168\u65e0\u529b\u6297\u62d2\uff0c\u4f46\u5634\u4e0a\u8fd8\u662f\u8868\u793a\u53cd\u5bf9\uff0c\u201c\u524d\u9762\u968f\u4fbf\u4f60\u7528\uff0c\u4f46\u540e\u9762\u771f\u7684\u4e0d\u884c\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u4e0d\u884c\uff0c\u53c8\u4e0d\u662f\u6ca1\u505a\u8fc7\uff0c\u201d\u743c\u6069\u8868\u793a\u4e0d\u6ee1\uff0c\u201c\u4e0a\u6b21\u7ed9\u4f60\u5c41\u80a1\u5f00\u82de\u7684\u65f6\u5019\uff0c\u4f60\u660e\u660e\u4e5f\u662f\u723d\u5f97\u4e00\u584c\u7cca\u6d82\u3002\u201d</p><p>\u201c\u4e0d\u662f\u5566\uff0c\u662f\u827e\u5f25\u8587\u53ee\u5631\u8fc7\u7684\uff0c\u5979\u5bf9\u6211\u8bf4\u5c41\u80a1\u662f\u7279\u522b\u5956\u52b1\uff0c\u662f\u8981\u7559\u5728\u7279\u522b\u65f6\u523b\u7ed9\u4f60\u505a\u793c\u7269\u7684\uff0c\u4e0d\u80fd\u5e73\u65f6\u5c31\u968f\u4fbf\u8ba9\u4f60\u63d2\u3002\u201d</p><p>\u5582\u5582\uff0c\u8fd9\u79cd\u4e8b\u60c5\u4f60\u4e3a\u4ec0\u4e48\u8981\u542c\u827e\u5f25\u8587\u7684\uff0c\u4e0d\u89c9\u5f97\u592a\u5947\u602a\u4e86\u4e48\u3002</p><p>\u201c\u4e0d\u544a\u8bc9\u5979\u4e0d\u5c31\u884c\u4e86\uff0c\u201d\u743c\u6069\u52aa\u529b\u8bf4\u670d\u5979\uff0c\u201c\u4f60\u4e0d\u8bf4\uff0c\u6211\u4e0d\u8bf4\uff0c\u5979\u600e\u4e48\u4f1a\u77e5\u9053\u3002\u201d</p><p>\u201c\u827e\u5f25\u8587\u5f88\u806a\u660e\uff0c\u80af\u5b9a\u4f1a\u770b\u51fa\u6765\u7684\u3002\u201d</p><p>\u201c\u6211\u4e0d\u7ba1\uff0c\u4f60\u4eca\u5929\u4e0d\u8ba9\u6211\u63d2\u5c41\u80a1\uff0c\u6211\u5c31\u4e0d\u5e2e\u4f60\u4fdd\u5bc6\u3002\u201d</p><p>\u201c\u2026\u2026\u597d\u5427\uff0c\u90a3\u4f60\u8f7b\u70b9\u3002\u201d</p><p>\u5a01\u903c\u5229\u8bf1\u4e4b\u4e0b\uff0c\u51db\u7ec8\u4e8e\u5c48\u670d\u4e86\uff0c\u534a\u63a8\u534a\u5c31\u5730\u8ba9\u743c\u6069\u5728\u5979\u7684\u5c41\u80a1\u91cc\u53c8\u5c04\u4e86\u4e24\u53d1\u3002\u4e00\u756a\u6298\u817e\uff0c\u4e24\u4eba\u90fd\u662f\u75b2\u5026\u81f3\u6781\uff0c\u987e\u4e0d\u4e0a\u53bb\u6d17\u6d74\u4fbf\u76f8\u62e5\u800c\u7720\uff0c\u76f4\u5230\u7b2c\u4e8c\u5929\u65e9\u4e0a\uff0c\u88ab\u6885\u83f2\u65af\u53eb\u9192\u3002</p><p>\u201c\u5feb\u8d77\u6765\uff0c\u4f60\u4eec\u4e24\u4e2a\uff0c\u201d\u5723\u6b66\u58eb\u5c11\u5973\u8bed\u6c14\u4e0d\u60a6\u5730\u8bf4\uff0c\u201c\u8981\u5f00\u6218\u4e86\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u521a\u521a\u88ab\u53eb\u9192\uff0c\u8111\u7b4b\u6709\u70b9\u8fdf\u949d\uff0c\u4e00\u65f6\u95f4\u6ca1\u6709\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u8c01\u548c\u8c01\u5f00\u6218\uff1f\u201d\u4ed6\u4e0b\u610f\u8bc6\u5730\u95ee\u3002\u8bdd\u8bf4\u51fa\u53e3\uff0c\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u6574\u4e2a\u4eba\u4e5f\u968f\u4e4b\u6e05\u9192\uff0c\u201c\u8981\u5f00\u6218\u4e86\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u8d76\u5feb\u8d77\u8eab\u7a7f\u8863\uff0c\u7a81\u7136\u770b\u89c1\u7761\u5728\u81ea\u5df1\u65c1\u8fb9\u7684\u5c11\u5973\u5e76\u4e0d\u662f\u51db\uff0c\u800c\u662f\u838e\u73de\u514b\u3002\u201c\u54a6\uff0c\u4f60\u600e\u4e48\u5728\u8fd9\u91cc\uff1f\u201d\u743c\u6069\u5947\u602a\uff0c\u201c\u51db\u5462\uff1f\u201d</p><p>\u201c\u6628\u665a\u660e\u660e\u662f\u4e3b\u4eba\u4f60\u62b1\u7740\u4eba\u5bb6\u4e0d\u80af\u653e\u624b\uff0c\u975e\u8981\u4eba\u5bb6\u4f8d\u5bdd\uff0c\u73b0\u5728\u53c8\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u201d\u9b45\u9b54\u4e0d\u9ad8\u5174\u5730\u8bf4\uff0c\u201c\u592a\u7edd\u60c5\u4e86\u5427\u3002\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u6211\u6628\u665a\u2026\u2026\u4e0d\u662f\u548c\u51db\u5728\u4e00\u8d77\u4e48\uff1f\u90a3\u5bb6\u4f19\u5462\uff1f\u201d</p><p>\u201c\u4f60\u7761\u7cca\u6d82\u4e86\u5427\uff0c\u51db\u6628\u665a\u4e00\u76f4\u548c\u6211\u5728\u4e00\u8d77\uff0c\u73b0\u5728\u5e94\u8be5\u8fd8\u6ca1\u8d77\u5e8a\uff0c\u201d\u6885\u83f2\u65af\u76b1\u7709\uff0c\u201c\u8fd8\u662f\u51fa\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u201c\u5443\uff0c\u5e94\u8be5\u6ca1\u4e8b\uff0c\u53ef\u80fd\u662f\u6709\u4eba\u548c\u6211\u5f00\u73a9\u7b11\u3002\u201d</p><p>\u743c\u6069\u6643\u4e86\u6643\u8111\u888b\uff0c\u52aa\u529b\u8ba9\u81ea\u5df1\u53d8\u5f97\u6e05\u9192\u4e00\u4e9b\uff0c\u81ea\u5df1\u7684\u8bb0\u5fc6\u5e94\u8be5\u6ca1\u6709\u51fa\u73b0\u6df7\u4e71\uff0c\u4f46\u6885\u83f2\u65af\u548c\u838e\u73de\u514b\u4e5f\u6ca1\u5fc5\u8981\u6545\u610f\u8bf4\u8c0e\uff0c\u90a3\u4e48\u6700\u5408\u7406\u7684\u89e3\u91ca\u5c31\u662f\u81ea\u5df1\u505a\u4e86\u4e2a\u7f8e\u68a6\u3002\u4f46\u8fd9\u4e2a\u68a6\uff0c\u5e94\u8be5\u4e0d\u662f\u7eaf\u7cb9\u7684\u201c\u65e5\u6709\u6240\u601d\uff0c\u591c\u6709\u6240\u68a6\u201d\u90a3\u4e48\u7b80\u5355\u3002</p><p>\u7b97\u4e86\uff0c\u53cd\u6b63\u4e5f\u80fd\u731c\u5230\u662f\u8c01\u5728\u641e\u9b3c\uff0c\u4e0d\u6025\u4e8e\u8fd9\u4e00\u65f6\u3002</p><p>\u743c\u6069\u7a7f\u597d\u8863\u670d\u53bb\u6d17\u6f31\uff0c\u7136\u540e\u53bb\u5ba2\u5385\uff0c\u53d1\u73b0\u4eba\u5df2\u7ecf\u57fa\u672c\u5230\u9f50\u4e86\uff0c\u53ea\u6709\u624e\u745e\u5c14\u4e0d\u5728\u3002\u800c\u4e14\u9664\u4e86\u4ed6\u548c\u51db\u4e4b\u5916\uff0c\u5176\u4ed6\u4eba\u90fd\u662f\u4e00\u8eab\u620e\u88c5\uff0c\u6885\u83f2\u65af\u548c\u838e\u73de\u514b\u4e0d\u5fc5\u8bf4\uff0c\u8fde\u73ca\u5609\u90fd\u7a7f\u7740\u4e00\u4ef6\u6d45\u7eff\u8272\u7684\u76ae\u7532\uff0c\u4e00\u526f\u968f\u65f6\u51c6\u5907\u4e0a\u6218\u573a\u7684\u6837\u5b50\u3002\u89c1\u4ed6\u8fc7\u6765\uff0c\u6885\u83f2\u65af\u4fbf\u5f00\u59cb\u5411\u5927\u5bb6\u901a\u62a5\u60c5\u51b5\u3002</p><p>\u6628\u5929\u591c\u91cc\uff0c\u5f53\u743c\u6069\u5728\u5e8a\u4e0a\u505a\u7f8e\u68a6\u65f6\uff0c\u5916\u9762\u53d1\u751f\u4e86\u5f88\u591a\u4e8b\u60c5\u3002\u51ef\u5c14\u672c\u8fde\u591c\u53ec\u96c6\u5176\u4ed6\u9009\u6c11\u4ee5\u53ca\u76df\u53cb\u5f00\u4f1a\uff0c\u5728\u4f1a\u4e0a\u4ed6\u7a81\u7136\u51fa\u624b\uff0c\u5236\u670d\u4e86\u51ef\u5fb7\u7acb\xb7\u535a\u7eb3\u675c\u65af\uff08\u6587\u5b57\u4e4b\u795e\u8fea\u5948\u5c14\u7684\u9009\u6c11\uff09\uff0c\u5e76\u6307\u63a7\u4ed6\u4e0e\u8428\u9a6c\u65af\u7279\u52fe\u7ed3\uff0c\u662f\u9f99\u5deb\u6559\u7684\u5185\u5978\u3002\u8fd9\u4e2a\u53d8\u6545\u592a\u51fa\u4e4e\u610f\u6599\uff0c\u6240\u6709\u4eba\u90fd\u4e3a\u4e4b\u9707\u60ca\uff0c\u4f46\u5f88\u5feb\uff0c\u88ab\u5236\u670d\u8005\u4fbf\u627f\u8ba4\u4e86\u8fd9\u4e00\u5207\u5c5e\u5b9e\u3002\u539f\u6765\u771f\u6b63\u7684\u535a\u7eb3\u675c\u65af\u65e9\u5728\u4e4b\u524d\u7684\u4e00\u6b21\u6218\u6597\u4e2d\u88ab\u4f0f\u51fb\u6740\u6b7b\uff0c\u4e4b\u540e\u51fa\u73b0\u7684\u8fea\u5948\u5c14\u9009\u6c11\u4e43\u662f\u9f99\u5deb\u6559\u5f92\u5047\u5192\u3002\u5047\u5192\u8005\u540c\u65f6\u4e5f\u627f\u8ba4\u662f\u4ed6\u88ad\u51fb\u5e76\u4fd8\u864f\u4e86\u83b1\u62c9\uff0c\u4f46\u7531\u4e8e\u9634\u5f71\u9547\u9632\u5fa1\u4e25\u5bc6\uff0c\u4ed6\u6ca1\u6709\u673a\u4f1a\u5c06\u4fd8\u864f\u9001\u7ed9\u8428\u9a6c\u65af\u7279\uff0c\u4e00\u76f4\u8fd8\u7559\u5728\u9547\u4e2d\u3002\u83b1\u62c9\u968f\u540e\u88ab\u51ef\u5c14\u672c\u89e3\u6551\u51fa\u6765\uff0c\u4f46\u7531\u4e8e\u53d7\u4f24\u4e25\u91cd\uff0c\u5fc5\u987b\u4f11\u517b\uff0c\u80af\u5b9a\u65e0\u6cd5\u53c2\u52a0\u63a5\u4e0b\u6765\u5bf9\u9f99\u5deb\u6559\u7684\u6218\u6597\u4e86\u3002</p><p>\u201c\u4e0d\u5bf9\u554a\uff0c\u90a3\u5bb6\u4f19\u6293\u5230\u7684\u83b1\u62c9\u4e0d\u662f\u5047\u7684\u4e48\uff0c\u771f\u7684\u5e94\u8be5\u5728\u5965\u5609\u83b1\u65af\u624b\u91cc\u3002\u201d</p><p>\u743c\u6069\u6697\u4e2d\u5600\u5495\u7740\uff0c\u5374\u6ca1\u6709\u8bf4\u51fa\u6765\uff0c\u542c\u6885\u83f2\u65af\u7ee7\u7eed\u8bf4\u3002\u51ef\u5c14\u672c\u6293\u51fa\u4e86\u9f99\u5deb\u6559\u5185\u5978\uff0c\u6551\u56de\u83b1\u62c9\uff0c\u4e00\u65b9\u9762\u9f13\u821e\u58eb\u6c14\uff0c\u540c\u65f6\u7f13\u548c\u4e86\u4e0e\u6b23\u5e03\u7b49\u4eba\u7684\u5173\u7cfb\uff0c\u53e6\u4e00\u65b9\u9762\u4e5f\u501f\u673a\u6e05\u6d17\uff0c\u6392\u9664\u5f02\u5df1\u3002\u4e00\u591c\u4e4b\u95f4\u5c31\u521d\u6b65\u5b8c\u6210\u4e86\u91cd\u65b0\u6574\u5408\uff0c\u7136\u540e\u51b3\u5b9a\u5c31\u5728\u4eca\u5929\u4e0b\u5348\u4e09\u70b9\uff0c\u53d1\u52a8\u4e0e\u8428\u9a6c\u65af\u7279\u7684\u51b3\u6218\u3002</p><p>\u7ec8\u4e8e\u8981\u5f00\u6253\u4e86\u5417\uff1f</p><p>\u743c\u6069\u81ea\u8bed\uff0c\u4e0d\u77e5\u4e3a\u4f55\u5fc3\u4e2d\u5374\u5e76\u4e0d\u7d27\u5f20\uff0c\u751a\u81f3\u6709\u79cd\u677e\u4e86\u53e3\u6c14\u7684\u611f\u89c9\uff0c\u4eff\u4f5b\u4e00\u4ef6\u4e8b\u60c5\u671f\u5f85\u5df2\u4e45\u5374\u8fdf\u8fdf\u4e0d\u81f3\uff0c\u73b0\u5728\u7ec8\u4e8e\u6709\u4e86\u786e\u5207\u6d88\u606f\uff0c\u65e0\u8bba\u662f\u6210\u662f\u8d25\uff0c\u81f3\u5c11\u90fd\u7b97\u662f\u9762\u4e34\u4e86\u7ed3\u4e86\u3002</p><p>\u201c\u600e\u4e48\u6253\uff1f\u201d</p><p>\u6885\u83f2\u65af\u9012\u8fc7\u6765\u4e00\u5f20\u7eb8\uff0c\u743c\u6069\u7ffb\u4e86\u7ffb\uff0c\u4e0a\u9762\u5199\u5f97\u6bd4\u8f83\u7b80\u5355\u3002\u9996\u5148\u662f\u4f5c\u6218\u76ee\u6807\uff0c\u51ef\u5c14\u672c\u544a\u8bc9\u5927\u5bb6\uff0c\u636e\u53ef\u9760\u60c5\u62a5\u53cd\u6620\uff0c\u8428\u9a6c\u65af\u7279\u638c\u63e1\u4e86\u4e00\u79cd\u540d\u4e3a\u201c\u5316\u8eab\u201d\u7684\u90aa\u6076\u6cd5\u672f\uff0c\u5b83\u975e\u5e38\u5f3a\u5927\uff0c\u751a\u81f3\u53ef\u4ee5\u51fb\u6740\u795e\u7947\u3002\u9f99\u5deb\u6559\u8fd9\u6b21\u5c31\u662f\u6253\u7b97\u4f7f\u7528\u8fd9\u79cd\u6cd5\u672f\uff0c\u5411\u6240\u6709\u66fe\u7ecf\u4e0e\u5176\u4e3a\u654c\u7684\u795e\u7947\u590d\u4ec7\u3002\u56e0\u6b64\u8fd9\u4e00\u6218\u7684\u76ee\u6807\uff0c\u5c31\u662f\u8981\u62a2\u5728\u8428\u9a6c\u65af\u7279\u5b8c\u6210\u201c\u5316\u8eab\u201d\u4e4b\u524d\u963b\u6b62\u4ed6\u3002</p><p>\u743c\u6069\u51b7\u7b11\uff0c\u201c\u8981\u4e48\u5c31\u662f\u51ef\u5c14\u672c\u7684\u60c5\u62a5\u4e0d\u5b9e\uff0c\u8981\u4e48\u5c31\u662f\u4ed6\u6545\u610f\u9690\u7792\u2014\u2014\u6211\u76f8\u4fe1\u662f\u540e\u8005\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8428\u9a6c\u65af\u7279\u7684\u771f\u6b63\u76ee\u7684\uff0c\u6839\u672c\u4e0d\u662f\u4ec0\u4e48\u590d\u4ec7\uff0c\u800c\u662f\u4e3a\u4e86\u548c\u9b54\u6cd5\u5973\u795e\u518d\u7eed\u524d\u7f18\uff1b\u81f3\u4e8e\u5177\u4f53\u7684\u624b\u6bb5\uff0c\u5219\u662f\u4ee5\u88ab\u4fd8\u864f\u7684\u4f20\u9053\u5deb\u5e08\u4e3a\u9053\u5177\uff0c\u4f7f\u7528\u2018\u5316\u8eab\u2019\u6cd5\u672f\uff0c\u5f3a\u5236\u5deb\u5e08\u795e\u5723\u8005\u964d\u4e34\uff0c\u5c06\u5176\u51fb\u6740\uff0c\u4ece\u800c\u80fd\u591f\u53d6\u800c\u4ee3\u4e4b\u3002\u201d</p><p>\u4ed6\u5c06\u81ea\u5df1\u8fd9\u6b21\u53bb\u627e\u51ef\u745f\u7433\u6240\u4e86\u89e3\u5230\u7684\u60c5\u62a5\u8bb2\u4e86\u4e00\u904d\uff0c\u5f53\u7136\u6709\u4e9b\u7ec6\u8282\u5b9e\u5728\u4e0d\u65b9\u4fbf\u8bf4\uff0c\u5c31\u53ea\u597d\u7565\u8fc7\u4e0d\u8c08\u4e86\u3002\u5927\u5bb6\u542c\u4e86\uff0c\u5404\u6709\u6240\u601d\uff0c\u6c14\u6c1b\u4e00\u65f6\u95f4\u6709\u4e9b\u6c89\u5bc2\u3002\u51db\u5374\u662f\u5fc3\u601d\u5355\u7eaf\uff0c\u6ca1\u6709\u592a\u591a\u7684\u60f3\u6cd5\uff0c\u968f\u624b\u4ece\u743c\u6069\u624b\u4e2d\u62ff\u8fc7\u90a3\u5f20\u7eb8\uff0c\u770b\u4e86\u51e0\u773c\uff0c\u7a81\u7136\u53eb\u4e86\u8d77\u6765\uff0c\u201c\u54a6\uff0c\u4e3a\u4ec0\u4e48\u6211\u548c\u827e\u5f25\u8587\u4e0d\u5728\u4e00\u8d77\uff1f\u201d\u5979\u4e0d\u9ad8\u5174\u8d77\u6765\uff0c\u201c\u4e0d\u884c\uff0c\u6211\u4e0d\u540c\u610f\u3002\u201d</p><p>\u743c\u6069\u51d1\u8fc7\u6765\u770b\u4e86\u770b\u3002\u51ef\u5c14\u672c\u6240\u62df\u5b9a\u7684\u5177\u4f53\u4f5c\u6218\u8ba1\u5212\uff0c\u662f\u8981\u6c42\u6240\u6709\u4eba\u5404\u81ea\u51c6\u5907\uff0c\u4e0a\u5348\u5341\u4e00\u70b9\u534a\u5728\u6668\u66e6\u795e\u6bbf\u201c\u73ab\u7470\u5927\u5385\u201d\u96c6\u5408\uff0c\u5170\u68ee\u5fb7\u5c14\u7684\u7267\u5e08\u4f1a\u4e3e\u884c\u4eea\u5f0f\uff0c\u4e3a\u51fa\u5f81\u8005\u65bd\u52a0\u795d\u798f\u3002\u4e0b\u5348\u4e09\u70b9\u949f\uff0c\u9634\u5f71\u8c37\u5927\u8d24\u8005\u4f0a\u5c14\u660e\u65af\u7279\u5c06\u4f1a\u53d1\u52a8\u201c\u5b88\u95e8\u4eba\u6c34\u6676\u201d\u7684\u529b\u91cf\uff0c\u5c06\u7b2c\u4e94\u79d8\u5668\u6f14\u5316\u51fa\u7684\u4e5d\u5c42\u5730\u72f1\u5c01\u5370\u51bb\u7ed3\uff0c\u7136\u540e\u5175\u5206\u4e09\u8def\u51fa\u51fb\u3002\u4e00\u8def\u662f\u51ef\u5c14\u672c\u7387\u9886\u4ed6\u7684\u76f4\u5c5e\u90e8\u4e0b\u8fdb\u653b\u7b2c\u4e03\u72f1\u9a6c\u62c9\u591a\u7c73\u5c3c\uff0c\u4e00\u8def\u662f\u98ce\u66b4\xb7\u94f6\u624b\u7387\u9886\u7ad6\u7434\u624b\u8fdb\u653b\u7b2c\u4e8c\u72f1\u8fea\u65af\uff0c\u8fd8\u6709\u4e00\u8def\u662f\u6b23\u5e03\u7387\u9886\u5176\u4ed6\u4eba\u5458\uff0c\u8fdb\u653b\u7b2c\u516d\u72f1\u739b\u5c14\u535a\u5409\u3002</p><p>\u6839\u636e\u60c5\u62a5\u663e\u793a\uff0c\u9f99\u5deb\u6559\u7684\u4e3b\u8981\u57fa\u5730\u5c31\u5728\u8fd9\u4e09\u5904\uff0c\u5176\u4e2d\u7b2c\u4e03\u72f1\u9a6c\u62c9\u591a\u7c73\u5c3c\u662f\u8428\u9a6c\u65af\u7279\u4eb2\u81ea\u5750\u9547\uff0c\u53e6\u5916\u4e24\u5904\u5206\u522b\u7531\u4e00\u4f4d\u90aa\u9f99\u4f8d\u8005\u5e26\u7740\u4e00\u5934\u9f99\u5deb\u5996\uff0c\u4ee5\u53ca\u82e5\u5e72\u90e8\u4e0b\u9547\u5b88\u3002\u6309\u7167\u8fd9\u4efd\u90e8\u7f72\uff0c\u743c\u6069\u7b49\u4eba\u4f1a\u88ab\u6253\u6563\u5f00\u6765\u3002\u5176\u4e2d\u51db\u8ddf\u968f\u5728\u6b23\u5e03\u8eab\u8fb9\uff0c\u743c\u6069\u3001\u6885\u83f2\u65af\u5219\u548c\u51ef\u5c14\u672c\u4e00\u8d77\u3002\u81f3\u4e8e\u73ca\u5609\u3001\u838e\u73de\u514b\uff0c\u6ca1\u6709\u88ab\u63d0\u53ca\uff0c\u4f30\u8ba1\u51ef\u5c14\u672c\u538b\u6839\u6ca1\u628a\u5979\u4eec\u8003\u8651\u5728\u6218\u529b\u4e4b\u5185\u3002\u5176\u5b9e\u838e\u73de\u514b\u539f\u672c\u4e0d\u7b97\u5f31\u8005\uff0c\u4f46\u8fd9\u91cc\u88ab\u7b2c\u4e94\u79d8\u5668\u6f14\u5316\u6210\u4e86\u4e5d\u5c42\u5730\u72f1\uff0c\u4f4d\u9762\u6cd5\u5219\u538b\u5236\u4e4b\u4e0b\uff0c\u838e\u73de\u514b\u7684\u529b\u91cf\u53d7\u5230\u4e25\u91cd\u524a\u5f31\uff0c\u5728\u9634\u5f71\u9547\u4e2d\u8fd8\u597d\u4e9b\uff0c\u56e0\u4e3a\u6709\u5b88\u95e8\u4eba\u6c34\u6676\u7684\u4fdd\u62a4\uff1b\u4e00\u65e6\u51fa\u4e86\u9634\u5f71\u9547\uff0c\u8131\u79bb\u6c34\u6676\u7684\u4fdd\u62a4\u8303\u56f4\uff0c\u53ea\u6015\u8fde\u4e2a\u9f99\u5deb\u6559\u7684\u6742\u5175\u90fd\u96be\u4ee5\u5e94\u4ed8\u3002\u624e\u745e\u5c14\u3001\u5965\u5609\u83b1\u65af\u4e5f\u540c\u6837\u4e0d\u5728\u540d\u5355\u4e0a\uff0c\u8fd9\u4e2a\u5f88\u597d\u7406\u89e3\uff0c\u51ef\u5c14\u672c\u54ea\u6709\u8d44\u683c\u5bf9\u5979\u4eec\u53d1\u53f7\u65bd\u4ee4\u3002</p><p>\u201c\u8fd9\u662f\u4f60\u8001\u5e08\u7684\u610f\u601d\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u672c\u6765\u662f\u5b89\u6392\u6211\u4eec\u5728\u4e00\u8d77\u7684\uff0c\u4f46\u6b23\u5e03\u5973\u58eb\u5f3a\u786c\u575a\u6301\uff0c\u4e00\u5b9a\u8981\u4f60\u548c\u5979\u5728\u4e00\u8d77\uff0c\u8bf4\u5979\u4f5c\u4e3a\u8001\u5e08\uff0c\u8981\u4fdd\u8bc1\u5b66\u751f\u7684\u5b89\u5168\u3002\u201d</p><p>\u743c\u6069\u62ac\u8d77\u5934\uff0c\u548c\u6885\u83f2\u65af\u5bf9\u89c6\u4e86\u4e00\u773c\u3002</p><p>\u201c\u90a3\u5c31\u8fd9\u6837\u5427\uff0c\u201d\u743c\u6069\u5bf9\u51db\u8bf4\uff0c\u201c\u4f60\u5728\u4f60\u8001\u5e08\u8eab\u8fb9\u4e5f\u786e\u5b9e\u6bd4\u8f83\u653e\u5fc3\uff0c\u514d\u5f97\u6211\u4eec\u8fd8\u8981\u7167\u987e\u4f60\u3002\u201d</p><p>\u201c\u8c01\u8981\u4f60\u7167\u987e\u4e86\uff01\u201d\u51db\u4e0d\u6ee1\u5730\u6297\u8bae\u3002</p><p>\u4f46\u6885\u83f2\u65af\u4e5f\u540c\u610f\u743c\u6069\u7684\u610f\u89c1\uff0c\u51db\u53ea\u597d\u59d4\u5c48\u5730\u7b54\u5e94\u4e86\u3002\u8fd9\u65f6\u5019\u838e\u73de\u514b\u8bf4\u8bdd\u4e86\uff0c\u201c\u90a3\u6211\u548c\u73ca\u5609\u59d0\u59d0\u5462\uff1f\u201d</p><p>\u73ca\u5609\u81ea\u7136\u662f\u7559\u5728\u9634\u5f71\u9547\u91cc\uff0c\u743c\u6069\u54ea\u6562\u8ba9\u5979\u4e0a\u6218\u573a\uff0c\u838e\u73de\u514b\u8d1f\u8d23\u4fdd\u62a4\u5979\u3002\u5bf9\u4e8e\u8fd9\u79cd\u5b89\u6392\uff0c\u838e\u73de\u514b\u65e0\u53ef\u65e0\u4e0d\u53ef\uff0c\u73ca\u5609\u5374\u4e0d\u540c\u610f\uff0c\u201c\u6211\u5f53\u7136\u8981\u548c\u6211\u5f1f\u5f1f\u5728\u4e00\u8d77\u3002\u201d</p><p>\u82e5\u662f\u522b\u7684\u4e8b\u60c5\uff0c\u743c\u6069\u4e5f\u5c31\u4f9d\u4e86\uff0c\u4f46\u8fd9\u6b21\u5b9e\u5728\u662f\u4e0d\u6562\u3002\u9634\u5f71\u9547\u4e2d\u672a\u5fc5\u5b89\u5168\uff0c\u4f46\u6bd5\u7adf\u8fdc\u79bb\u6218\u573a\u4e2d\u5fc3\uff0c\u6709\u5965\u5609\u83b1\u65af\u5728\uff0c\u6709\u838e\u73de\u514b\u4ece\u65c1\u534f\u52a9\uff0c\u81ea\u4fdd\u5e94\u8be5\u8fd8\u662f\u53ef\u4ee5\u7684\u3002\u4e00\u65e6\u51fa\u4e86\u9634\u5f71\u9547\uff0c\u5377\u5165\u6df7\u6218\u4e4b\u4e2d\uff0c\u90a3\u53ef\u5c31\u5f88\u96be\u8bf4\u4e86\uff0c\u8fd9\u79cd\u5c42\u7ea7\u7684\u5927\u6218\uff0c\u8c01\u4e5f\u4e0d\u80fd\u4fdd\u8bc1\u81ea\u5df1\u4f1a\u5b89\u7136\u65e0\u6059\uff0c\u4efb\u4f60\u662f\u7edd\u4e16\u5f3a\u8005\uff0c\u7167\u6837\u4e5f\u6709\u53ef\u80fd\u9668\u843d\uff0c\u5371\u9669\u7a0b\u5ea6\u5b9e\u5728\u662f\u592a\u9ad8\u4e86\u3002</p><p>\u201c\u4e0d\u884c\uff01\u59d0\u59d0\u4f60\u8981\u7559\u5728\u8fd9\u91cc\u3002\u201d</p><p>\u743c\u6069\u6001\u5ea6\u975e\u5e38\u575a\u51b3\uff0c\u65e0\u8bba\u73ca\u5609\u600e\u4e48\u8bf4\uff0c\u4ed6\u90fd\u662f\u4e0d\u540c\u610f\u3002\u8fd9\u79cd\u4e8b\u60c5\uff0c\u5176\u4ed6\u4eba\u4e5f\u4e0d\u597d\u63d2\u53e3\uff0c\u50f5\u6301\u4e86\u534a\u664c\uff0c\u73ca\u5609\u6700\u7ec8\u8fd8\u662f\u8ba9\u6b65\u4e86\uff0c\u201c\u90a3\u597d\u5427\uff0c\u6211\u5728\u8fd9\u91cc\u7b49\u4f60\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u4f46\u4f60\u8981\u7b54\u5e94\u6211\uff0c\u4e00\u5b9a\u8981\u5b89\u5168\u56de\u6765\u3002\u201d</p><p>\u201c\u6211\u4fdd\u8bc1\u3002\u201d\u743c\u6069\u7acb\u523b\u8bf4\u3002</p><p>\u73ca\u5609\u70b9\u4e86\u70b9\u5934\uff0c\u4e0d\u518d\u591a\u8bf4\uff0c\u5e26\u7740\u838e\u73de\u514b\u56de\u5230\u81ea\u5df1\u623f\u95f4\u3002</p><p>\u743c\u6069\u7ee7\u7eed\u7814\u7a76\u4f5c\u6218\u8ba1\u5212\uff0c\u7136\u540e\u53d1\u73b0\u4e00\u4e2a\u95ee\u9898\uff0c\u201c\u5927\u8d24\u8005\u5462\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u4ed6\u7528\u5b88\u95e8\u4eba\u6c34\u6676\u51bb\u7ed3\u9886\u57df\u4e4b\u540e\uff0c\u5c31\u4e0d\u53c2\u6218\u4e86\uff1f\u201d</p><p>\u201c\u6ca1\u529e\u6cd5\u518d\u53c2\u6218\u5427\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6211\u542c\u8bf4\u6fc0\u53d1\u5b88\u95e8\u4eba\u6c34\u6676\u5bf9\u5deb\u5e08\u7684\u8d1f\u62c5\u975e\u5e38\u91cd\uff0c\u5927\u8d24\u8005\u5e74\u4e8b\u5df2\u9ad8\uff0c\u53ea\u6015\u2026\u2026\u53cd\u6b63\u60c5\u51b5\u4e0d\u4e50\u89c2\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u51db\u8981\u53bb\u627e\u5979\u7684\u8001\u5e08\u6b23\u5e03\uff0c\u4e00\u4e2a\u4eba\u5148\u51fa\u95e8\uff1b\u743c\u6069\u548c\u6885\u83f2\u65af\u7a0d\u540e\u51fa\u53d1\uff0c\u4ed6\u4eec\u8fd8\u8981\u505a\u4e00\u4e9b\u51c6\u5907\u5de5\u4f5c\uff0c\u6885\u83f2\u65af\u5012\u8fd8\u7f62\u4e86\uff0c\u743c\u6069\u662f\u4e2a\u5deb\u5e08\uff0c\u5018\u82e5\u4e0d\u628a\u9700\u8981\u7528\u7684\u6cd5\u672f\u63d0\u524d\u51c6\u5907\u597d\uff0c\u90a3\u5c31\u76f8\u5f53\u4e8e\u6218\u58eb\u625b\u7740\u6ca1\u6709\u5b50\u5f39\u7684\u706b\u67aa\u4e0a\u6218\u573a\uff0c\u5c5e\u4e8e\u81ea\u5df1\u627e\u6b7b\u3002</p><p>\u201c\u591a\u51c6\u5907\u70b9\u9632\u5fa1\u6cd5\u672f\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u8fd9\u4e00\u4ed7\u6050\u6015\u5c40\u9762\u4f1a\u5f88\u6df7\u4e71\uff0c\u6211\u4e0d\u4e00\u5b9a\u80fd\u987e\u5f97\u4e0a\u4f60\u3002\u201d</p><p>\u743c\u6069\u55ef\u4e86\u4e00\u58f0\uff0c\u201c\u6211\u77e5\u9053\uff0c\u4f60\u4e5f\u8981\u5c0f\u5fc3\u3002\u91cf\u529b\u800c\u884c\uff0c\u4e0d\u8981\u592a\u52c9\u5f3a\u3002\u201d</p><p>\u201c\u653e\u5fc3\u5566\uff0c\u6211\u53c8\u4e0d\u7b28\uff0c\u201d\u5c11\u5973\u5fae\u7b11\uff0c\u201c\u800c\u4e14\u6211\u8fd8\u85cf\u4e86\u5f20\u5e95\u724c\u5462\uff0c\u81ea\u4fdd\u603b\u662f\u6ca1\u95ee\u9898\uff0c\u4e0d\u4f1a\u6709\u4e8b\u7684\u3002\u201d</p><p>\u4ec0\u4e48\u5e95\u724c\uff1f</p><p>\u743c\u6069\u9887\u4e3a\u597d\u5947\uff0c\u4f46\u6885\u83f2\u65af\u4e0d\u8bf4\uff0c\u4ed6\u4e5f\u5c31\u4e0d\u518d\u591a\u95ee\uff0c\u81ea\u987e\u81ea\u5730\u5f00\u59cb\u51c6\u5907\u6cd5\u672f\u3002</p><p>\u203b\u203b\u203b</p><p>\u201c\u73ab\u7470\u5927\u5385\u201d\u4f4d\u4e8e\u9634\u5f71\u9547\u7684\u4e1c\u5317\u89d2\u4e0a\uff0c\u5b83\u662f\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u5728\u9634\u5f71\u8c37\u7684\u552f\u4e00\u4e00\u5ea7\u795e\u6bbf\uff0c\u5386\u53f2\u9887\u4e3a\u60a0\u4e45\uff0c\u6700\u65e9\u53ef\u4ee5\u4e0a\u6eaf\u5230\u4e5d\u767e\u591a\u5e74\u524d\uff0c\u4e0d\u8fc7\u66fe\u7ecf\u51e0\u5ea6\u6bc1\u4e8e\u6218\u706b\uff0c\u6700\u8fd1\u7684\u4e00\u6b21\u662f\u5341\u516d\u5e74\u524d\u7684\u201c\u52a8\u8361\u5e74\u4ee3\u201d\uff0c\u90aa\u795e\u73ed\u6069\u4e0e\u5927\u8d24\u8005\u4f0a\u5c14\u660e\u65af\u7279\u5728\u6b64\u5730\u5c55\u5f00\u4e00\u573a\u6076\u6597\uff0c\u6cd5\u672f\u51b2\u649e\u4ea7\u751f\u7684\u7206\u70b8\u5c06\u534a\u4e2a\u9634\u5f71\u9547\u5937\u4e3a\u5e73\u5730\uff0c\u6668\u66e6\u795e\u6bbf\u4e5f\u672a\u80fd\u5e78\u514d\u3002\u6218\u540e\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u52df\u96c6\u5de8\u8d44\uff0c\u5728\u539f\u5740\u4e0a\u91cd\u5efa\u4e86\u795e\u6bbf\u3002\u7531\u4e8e\u67d0\u4f4d\u8bbe\u8ba1\u5e08\u7684\u4e2a\u4eba\u504f\u597d\uff0c\u65b0\u795e\u6bbf\u6240\u6709\u5efa\u7b51\u7684\u5916\u5899\u5168\u90fd\u7531\u73ab\u7470\u8272\u73bb\u7483\u6784\u7b51\uff0c\u5728\u9633\u5149\u4e0b\u6298\u5c04\u51fa\u5b9b\u5982\u665a\u971e\u822c\u7684\u7eda\u4e3d\u8272\u5f69\uff0c\u7b97\u662f\u9634\u5f71\u8c37\u7684\u540d\u80dc\u4e4b\u4e00\u3002</p><p>\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u8fd9\u5ea7\u795e\u6bbf\u5e94\u5f53\u6709\u795e\u804c\u4eba\u5458\u5927\u7ea6\u516b\u5341\u4f59\u4eba\uff0c\u5176\u4e2d\u5305\u62ec\u4e00\u540d\u6668\u5149\u5723\u4f7f\u3001\u4e8c\u5341\u4f59\u540d\u9ad8\u9636\u7267\u5e08\u3001\u5341\u4f59\u540d\u5723\u6b66\u58eb\u4ee5\u53ca\u5176\u4ed6\u6210\u5458\u3002\u6668\u5149\u5723\u4f7f\u662f\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u4e2d\u6392\u5e8f\u7b2c\u4e8c\u7684\u795e\u804c\u4eba\u5458\uff0c\u4ec5\u6b21\u4e8e\u6559\u7687\uff0c\u901a\u5e38\u90fd\u662f\u67d0\u4e00\u6559\u533a\u7684\u9886\u8896\u4eba\u7269\uff0c\u6216\u8005\u8d44\u5386\u6df1\u539a\u3001\u529f\u52cb\u5353\u8457\u7684\u5143\u8001\uff0c\u4e00\u822c\u4e0d\u4f1a\u8d85\u8fc7\u5341\u540d\uff0c\u76ee\u524d\u6574\u4e2a\u5927\u9646\u7684\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u4e5f\u53ea\u6709\u516d\u4f4d\u3002\u5728\u8bf8\u795e\u4e4b\u4e2d\uff0c\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u548c\u6b7b\u795e\u514b\u5170\u6c83\u5e76\u79f0\u662f\u4ea1\u7075\u7684\u4e24\u5927\u514b\u661f\uff0c\u8428\u9a6c\u65af\u7279\u5f53\u5e74\u4f9d\u4ed7\u9f99\u5deb\u5996\uff0c\u8fd1\u4e4e\u65e0\u654c\u4e8e\u5929\u4e0b\uff0c\u7ed3\u679c\u9047\u5230\u5170\u68ee\u5fb7\u5c14\u5316\u8eab\u4e34\u51e1\uff0c\u987f\u65f6\u88ab\u6253\u5f97\u7070\u98de\u70df\u706d\uff0c\u82e5\u4e0d\u662f\u9884\u5148\u7559\u4e86\u4e00\u624b\uff0c\u65e9\u5c31\u5f7b\u5e95\u5b8c\u86cb\u4e86\u3002\u63d0\u5c14\u7684\u5723\u6b66\u58eb\uff0c\u6ce8\u91cd\u5f8b\u6cd5\uff0c\u8bb2\u6c42\u79e9\u5e8f\uff0c\u4ee5\u6253\u51fb\u90aa\u6076\u4e3a\u5df1\u4efb\uff0c\u4ee5\u5b9e\u9645\u884c\u52a8\u4e3a\u6807\u51c6\uff0c\u5bf9\u4e8e\u4ea1\u7075\u5e76\u65e0\u5148\u5929\u6027\u7684\u6392\u65a5\uff0c\u6700\u591a\u53ea\u662f\u8b66\u60d5\uff1b\u5170\u68ee\u5fb7\u5c14\u7684\u795e\u804c\u4eba\u5458\uff0c\u5219\u666e\u904d\u90fd\u4f1a\u4fee\u4e60\u5404\u79cd\u5bf9\u6297\u4ea1\u7075\u7684\u6cd5\u672f\uff0c\u53ef\u4ee5\u8bf4\u6bcf\u4e2a\u4eba\u90fd\u662f\u517c\u804c\u7684\u4ea1\u7075\u730e\u624b\uff0c\u743c\u6069\u8fd8\u542c\u6885\u83f2\u65af\u8bf4\u8fc7\uff0c\u5170\u68ee\u5fb7\u5c14\u8d50\u4e88\u5176\u7267\u5e08\u7684\u5f88\u591a\u795e\u672f\uff0c\u90fd\u9644\u5e26\u6709\u989d\u5916\u7684\u201c\u538b\u5236\u4ea1\u7075\u6cd5\u672f\u201d\u7684\u6548\u679c\uff0c\u5728\u8bf8\u795e\u4e2d\u4e5f\u7b97\u662f\u72ec\u4e00\u65e0\u4e8c\u3002\u5982\u679c\u73ab\u7470\u5927\u5385\u7684\u795e\u804c\u4eba\u5458\u90fd\u5728\uff0c\u8fd9\u6b21\u5bf9\u4ed8\u8428\u9a6c\u65af\u7279\u5c31\u8f7b\u677e\u591a\u4e86\u3002\u5728\u5bf9\u4ed8\u4ea1\u7075\u8fd9\u4ef6\u4e8b\u4e0a\uff0c\u4e00\u4f4d\u6668\u5149\u5723\u4f7f\u51fa\u9a6c\uff0c\u80dc\u8fc7\u4e24\u4e09\u4e2a\u9009\u6c11\uff0c\u53ef\u60dc\u524d\u6bb5\u65f6\u95f4\uff0c\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u7684\u9ad8\u5c42\u4e0d\u77e5\u505a\u4e86\u4ec0\u4e48\u51b3\u7b56\uff0c\u5c06\u5404\u5730\u795e\u6bbf\u7684\u7cbe\u82f1\u5206\u5b50\u62bd\u8c03\u4e00\u7a7a\uff0c\u4e0d\u77e5\u6240\u5f80\u3002\u6240\u4ee5\u6b64\u65f6\u5728\u73ab\u7470\u5927\u5385\u4e2d\uff0c\u9664\u4e86\u4e00\u4e9b\u89c1\u4e60\u751f\u3001\u4f8d\u4ece\uff0c\u771f\u6b63\u7684\u7267\u5e08\u4ec5\u6709\u56db\u4e2a\uff0c\u4e3a\u9996\u7684\u662f\u4e00\u4f4d\u526f\u4e3b\u6559\uff0c\u540d\u53eb\u827e\u6797\uff0c\u5e74\u9f84\u5927\u7ea6\u4e09\u5341\u4e94\u516d\u5c81\uff0c\u9f3b\u6881\u4e0a\u67b6\u7740\u4e00\u526f\u773c\u955c\uff0c\u76ae\u80a4\u767d\u7699\uff0c\u770b\u8d77\u6765\u6587\u8d28\u5f6c\u5f6c\u3002\u743c\u6069\u542c\u6885\u83f2\u65af\u63d0\u53ca\u8fc7\uff0c\u8bf4\u8fd9\u4f4d\u827e\u6797\u526f\u4e3b\u6559\u64c5\u957f\u5404\u79cd\u9b54\u6cd5\u673a\u5173\u7684\u7f16\u7ec7\u3001\u5efa\u6784\u548c\u89e3\u6790\uff0c\u5728\u8fd9\u4e2a\u9886\u57df\u4e5f\u7b97\u5c0f\u6709\u540d\u6c14\uff0c\u4f46\u813e\u6c14\u6709\u4e9b\u53e4\u602a\uff0c\u4e0d\u592a\u5bb9\u6613\u6253\u4ea4\u9053\u3002</p><p>\u597d\u5728\u743c\u6069\u4e5f\u538b\u6839\u6ca1\u6253\u7b97\u53bb\u6253\u4ea4\u9053\u3002\u6697\u591c\u5973\u795e\u838e\u5c14\u4e0e\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\uff0c\u8fd9\u4e24\u4f4d\u4e00\u6697\u4e00\u5149\uff0c\u5c5e\u4e8e\u6b7b\u654c\uff0c\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u743c\u6069\u8fd9\u79cd\u9634\u9b42\u57ce\u5deb\u5e08\u3001\u6697\u591c\u9009\u6c11\uff0c\u538b\u6839\u4e0d\u53ef\u80fd\u4f1a\u8fdb\u5165\u6668\u66e6\u795e\u6bbf\u8fd9\u79cd\u654c\u65b9\u5927\u672c\u8425\uff0c\u90a3\u7eaf\u7cb9\u662f\u627e\u6b7b\u3002\u5176\u5b9e\u743c\u6069\u5e76\u4e0d\u662f\u838e\u5c14\u9009\u6c11\uff0c\u4ed6\u4e5f\u6ca1\u8fd9\u4e2a\u81ea\u89c9\uff0c\u95ee\u9898\u662f\u522b\u4eba\u90fd\u8fd9\u4e48\u8ba4\u4e3a\uff0c\u4ed6\u4e5f\u6ca1\u529e\u6cd5\u3002</p><p>\u827e\u6797\u662f\u4e2a\u6587\u804c\u4eba\u5458\uff0c\u641e\u7814\u7a76\u5f88\u64c5\u957f\uff0c\u4e0a\u6218\u573a\u662f\u4e0d\u884c\u7684\uff0c\u6240\u4ee5\u4ed6\u4e5f\u6ca1\u6253\u7b97\u4e0a\u6218\u573a\uff0c\u53ea\u662f\u5e94\u51ef\u5c14\u672c\u6240\u8bf7\uff0c\u63d0\u4f9b\u4e00\u4e9b\u540e\u52e4\u8f85\u52a9\u5de5\u4f5c\u3002\u743c\u6069\u62b5\u8fbe\u795e\u6bbf\u7684\u65f6\u5019\uff0c\u6668\u66e6\u7267\u5e08\u4eec\u6b63\u5728\u4e3e\u884c\u4eea\u5f0f\uff0c\u4ed6\u4eec\u62ff\u51fa\u4e00\u4e2a\u534a\u4eba\u9ad8\u7684\u5de8\u5927\u91d1\u676f\uff0c\u6ce8\u6ee1\u6c34\uff0c\u7136\u540e\u96c6\u4f53\u7948\u7977\u3002\u5177\u4f53\u8be6\u60c5\uff0c\u743c\u6069\u9650\u4e8e\u8eab\u4efd\uff0c\u4e0d\u65b9\u4fbf\u9760\u8fd1\u56f4\u89c2\uff0c\u6240\u4ee5\u4e5f\u672a\u770b\u5f97\u4ed4\u7ec6\u3002\u7948\u7977\u6574\u6574\u8fdb\u884c\u4e86\u4e24\u4e2a\u5c0f\u65f6\uff0c\u5728\u4e0b\u5348\u4e00\u70b9\u534a\u7684\u65f6\u5019\uff0c\u4eea\u5f0f\u7ed3\u675f\u4e86\u3002\u7267\u5e08\u4eec\u62ff\u51fa\u5f88\u591a\u5c0f\u7684\u73ab\u7470\u8272\u73bb\u7483\u676f\uff0c\u5c06\u5927\u91d1\u676f\u4e2d\u7684\u6c34\u5206\u88c5\u5176\u4e2d\uff0c\u7136\u540e\u53c8\u62ff\u51fa\u5f88\u591a\u6709\u7ea2\u3001\u9ec4\u3001\u7eff\u5b9d\u77f3\u9576\u5d4c\u7684\u62a4\u8eab\u7b26\uff0c\u4e00\u8d77\u9012\u7ed9\u6bcf\u4e00\u4f4d\u51fa\u5f81\u8005\u3002</p><p>\u6309\u7167\u795e\u6bbf\u7684\u8bf4\u6cd5\uff0c\u8fd9\u6c34\u548c\u62a4\u8eab\u7b26\u90fd\u7ecf\u8fc7\u795d\u798f\uff0c\u8574\u542b\u4e86\u6668\u66e6\u4e4b\u795e\u8d50\u4e88\u7684\u795e\u5723\u529b\u91cf\uff0c\u80fd\u591f\u6709\u6548\u5730\u514b\u5236\u4ea1\u7075\uff0c\u5bf9\u4ed8\u8428\u9a6c\u65af\u7279\u548c\u9f99\u5deb\u6559\u786e\u5b9e\u5f88\u5408\u9002\u3002\u51db\u597d\u5947\u5730\u5c1d\u4e86\u4e00\u53e3\uff0c\u8868\u793a\u8fd8\u5e26\u70b9\u751c\u5473\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u743c\u6069\u8fd8\u662f\u627e\u501f\u53e3\u62d2\u7edd\u4e86\uff0c\u4ed6\u8eab\u4e0a\u7684\u90aa\u795e\u795e\u529b\u592a\u591a\uff0c\u4e07\u4e00\u548c\u5170\u68ee\u5fb7\u5c14\u795e\u529b\u51b2\u7a81\u8d77\u6765\uff0c\u90a3\u5c31\u9ebb\u70e6\u5927\u4e86\uff0c\u8fd9\u4e2a\u98ce\u9669\u6ca1\u5fc5\u8981\u5192\u3002</p><p>\u5f85\u6240\u6709\u4eba\u90fd\u996e\u4e0b\u5723\u6c34\uff0c\u4f69\u597d\u62a4\u8eab\u7b26\u540e\uff0c\u6668\u66e6\u7267\u5e08\u4eec\u518d\u5ea6\u5f00\u59cb\u7948\u7977\u3002\u91d1\u8272\u7684\u5149\u7fbd\u4eff\u4f5b\u96ea\u7247\uff0c\u4ece\u5927\u6bbf\u7684\u7a79\u9876\u7eb7\u7eb7\u6d12\u6d12\u5730\u98d8\u843d\u4e0b\u6765\uff0c\u5c06\u5728\u573a\u6240\u6709\u4eba\u7684\u76ae\u80a4\u90fd\u9540\u4e0a\u4e86\u4e00\u5c42\u91d1\u8272\u8584\u819c\uff0c\u4ee4\u4eba\u611f\u5230\u6e29\u6696\u65e0\u6bd4\uff0c\u65e0\u7a77\u65e0\u5c3d\u7684\u52c7\u6c14\u3001\u4fe1\u5fc3\u4ece\u5fc3\u5e95\u6d8c\u51fa\u2014\u2014\u9664\u4e86\u743c\u6069\uff0c\u4ed6\u65e9\u65e9\u5730\u5c31\u907f\u8ba9\u5f00\u6765\uff0c\u9000\u51fa\u6bbf\u5916\u3002</p><p>\u5730\u9762\u5728\u8f7b\u5fae\u5730\u6447\u6643\uff0c\u4eff\u4f5b\u5730\u9707\u3002\u8fd9\u662f\u4e00\u76f4\u9690\u8eab\u5e55\u540e\u7684\u9634\u5f71\u8c37\u5927\u8d24\u8005\u4f0a\u5c14\u660e\u65af\u7279\u5f00\u59cb\u51fa\u624b\uff0c\u4ed6\u6fc0\u53d1\u4e86\u5b88\u95e8\u6c34\u6676\u7684\u6700\u5927\u529b\u91cf\uff0c\u751a\u81f3\u63d0\u524d\u900f\u652f\uff0c\u7136\u540e\u4e00\u6b21\u6027\u6325\u970d\u51fa\u53bb\u3002\u51b0\u84dd\u8272\u7684\u5149\u8292\u4ece\u9547\u4e2d\u87ba\u65cb\u5f62\u7684\u7070\u9eef\u9ad8\u5854\u9876\u4e0a\u8ff8\u53d1\u3001\u6269\u6563\uff0c\u8f6c\u773c\u95f4\u5f62\u6210\u6c79\u6d8c\u6f8e\u6e43\u7684\u5149\u4e4b\u6f6e\u6c34\uff0c\u6d69\u6d69\u8361\u8361\u5730\u5411\u9547\u5916\u56db\u9762\u516b\u65b9\u6d8c\u53bb\u3002</p><p>\u201c\u79d8\u5668\u7684\u2018\u9886\u57df\u2019\u88ab\u51bb\u7ed3\u4e86\u3002\u201d</p><p>\u624e\u745e\u5c14\u7684\u58f0\u97f3\u7a81\u7136\u5728\u8033\u8fb9\u54cd\u8d77\uff0c\u743c\u6069\u8f6c\u8fc7\u5934\u4e00\u770b\uff0c\u5c31\u89c1\u9b54\u59ec\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u7ad9\u5728\u4e86\u8eab\u65c1\u3002\u5979\u4ef0\u7740\u5934\uff0c\u770b\u7740\u5929\u7a7a\u4e2d\u7684\u5149\u6f6e\uff0c\u5634\u89d2\u9732\u51fa\u5fae\u5fae\u7684\u7b11\u610f\uff0c\u50cf\u662f\u5728\u8ba5\u8bbd\u3002\u201c\u600e\u4e48\u4e86\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u6709\u4ec0\u4e48\u4e0d\u5bf9\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u554a\uff0c\u4e00\u5207\u6b63\u5e38\u3002\u201d\u624e\u745e\u5c14\u8bf4\u3002</p><p>\u201c\u771f\u7684\uff1f\u201d\u743c\u6069\u6000\u7591\u5730\u770b\u4e86\u5979\u4e00\u773c\uff0c\u603b\u89c9\u5f97\u9b54\u59ec\u5728\u9690\u7792\u4ec0\u4e48\uff0c\u4f46\u77e5\u9053\u8ffd\u95ee\u4e5f\u6ca1\u6709\u610f\u4e49\u3002\u201c\u6628\u665a\u7684\u4e8b\u60c5\u662f\u4f60\u5e72\u7684\u5427\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u6628\u591c\u4ed6\u660e\u660e\u548c\u51db\u7ffb\u4e91\u8986\u96e8\uff0c\u641e\u5b8c\u524d\u9762\u53c8\u641e\u540e\u9762\uff0c\u9192\u6765\u5374\u53d1\u73b0\u6795\u8fb9\u4eba\u662f\u838e\u73de\u514b\u3002\u5982\u679c\u5355\u7eaf\u53ea\u662f\u8ba4\u9519\u4e86\u4eba\uff0c\u90a3\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u53cd\u6b63\u90fd\u662f\u81ea\u5df1\u5973\u4eba\uff0c\u5076\u5c14\u641e\u9519\u4e5f\u65e0\u6240\u8c13\uff0c\u81f3\u5c11\u8bf4\u660e\u81ea\u5df1\u6027\u529f\u80fd\u6b63\u5e38\u3002\u95ee\u9898\u662f\u743c\u6069\u770b\u5f97\u5f88\u6e05\u695a\uff0c\u9b45\u9b54\u867d\u7136\u662f\u8d64\u88f8\u7684\uff0c\u5374\u660e\u663e\u6ca1\u6709\u6b22\u597d\u8fc7\u7684\u75d5\u8ff9\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ece\u5934\u5230\u5c3e\u538b\u6839\u5c31\u662f\u4e00\u573a\u6625\u68a6\u800c\u5df2\u3002</p><p>\u743c\u6069\u89c9\u5f97\u81ea\u5df1\u4e0d\u4f1a\u65e0\u7f18\u65e0\u6545\u505a\u8fd9\u79cd\u68a6\uff0c\u88ab\u4eba\u5f71\u54cd\u7684\u53ef\u80fd\u6027\u5f88\u5927\u3002\u4ed6\u867d\u7136\u4e0d\u662f\u838e\u5c14\u9009\u6c11\uff0c\u4f46\u786e\u5b9e\u6709\u5f71\u706b\u5728\u8eab\uff0c\u5f71\u706b\u9644\u5e26\u610f\u5fd7\u5c4f\u969c\u7684\u6548\u679c\uff0c\u8ba9\u4ed6\u514d\u75ab\u7cbe\u795e\u6cd5\u672f\u7684\u88ad\u51fb\uff0c\u6309\u9053\u7406\u8bf4\uff0c\u4ed6\u4e0d\u5e94\u8be5\u5728\u65e0\u58f0\u65e0\u606f\u4e4b\u95f4\u5c31\u5760\u5165\u68a6\u5883\u800c\u4e0d\u81ea\u77e5\u3002\u5f53\u7136\uff0c\u4e16\u754c\u4e0a\u6ca1\u6709\u7edd\u5bf9\u7684\u9632\u5fa1\uff0c\u591c\u5973\u58eb\u4e5f\u4e0d\u662f\u81f3\u9ad8\u65e0\u4e0a\u4e4b\u795e\uff0c\u4f46\u5373\u4fbf\u53ea\u662f\u838e\u5c14\u7684\u4e00\u70b9\u5f71\u706b\uff0c\u80fd\u591f\u6253\u7834\u5b83\u7684\u4eba\u5e94\u8be5\u4e5f\u4e0d\u4f1a\u5f88\u591a\uff0c\u800c\u5176\u4e2d\u6700\u6709\u5acc\u7591\u7684\u663e\u7136\u662f\u624e\u745e\u5c14\u3002</p><p>\u201c\u55ef\uff0c\u600e\u4e48\u6837\uff0c\u6839\u636e\u6211\u7684\u7ecf\u9a8c\uff0c\u505a\u4e2a\u7f8e\u68a6\uff0c\u9192\u6765\u4f1a\u683c\u5916\u795e\u6e05\u6c14\u723d\u3002\u201d</p><p>\u201c\u8c22\u8c22\uff0c\u4f46\u4f60\u5230\u5e95\u662f\u4ec0\u4e48\u610f\u601d\u5462\uff1f\u201d</p><p>\u201c\u5176\u5b9e\u4e5f\u6ca1\u4ec0\u4e48\u5566\uff0c\u5c31\u662f\u505a\u4e2a\u8bd5\u9a8c\u800c\u5df2\uff0c\u770b\u770b\u90a3\u4e2a\u8bc5\u5492\u7a76\u7adf\u80fd\u4e0d\u80fd\u89e3\u3002\u201d</p><p>\u63d0\u5230\u201c\u90a3\u4e2a\u8bc5\u5492\u201d\uff0c\u743c\u6069\u7684\u8138\u8272\u987f\u65f6\u9ed1\u4e86\u4e0b\u6765\u3002\u4ed6\u4e5f\u662f\u65e9\u4e0a\u9192\u6765\u540e\u624d\u60f3\u8d77\uff0c\u81ea\u5df1\u4e2d\u4e86\u7ef4\u82e5\u62c9\u7684\u8bc5\u5492\uff0c\u53ea\u80fd\u548c\u5979\u4e00\u4e2a\u4eba\u505a\u7231\uff0c\u78b0\u5230\u5176\u4ed6\u5973\u4eba\u5c31\u9633\u75ff\uff0c\u8fd9\u4ef6\u4e8b\u5df2\u7ecf\u7ecf\u624e\u745e\u5c14\u548c\u838e\u7433\u5a1c\u53cd\u590d\u9a8c\u8bc1\u8fc7\u5c5e\u5b9e\u3002\u6240\u4ee5\u5982\u679c\u6628\u591c\u53ea\u662f\u641e\u9519\u4e86\u4eba\uff0c\u743c\u6069\u4f1a\u5f88\u9ad8\u5174\uff0c\u4f46\u5f04\u5230\u6700\u540e\u8fd8\u662f\u4e00\u573a\u5e7b\u68a6\uff0c\u8fd9\u5c31\u6bd4\u8f83\u4ee4\u4eba\u5931\u671b\u4e86\u3002</p><p>\u201c\u6211\u731c\u6d4b\u90a3\u4e2a\u8bc5\u5492\u7684\u4f5c\u7528\u539f\u7406\uff0c\u662f\u4ece\u7cbe\u795e\u4e0a\u538b\u8feb\uff0c\u800c\u975e\u751f\u7406\u4e0a\u9650\u5236\uff0c\u201d\u624e\u745e\u5c14\u89e3\u91ca\uff0c\u201c\u6240\u4ee5\u6211\u60f3\u8bd5\u8bd5\u770b\uff0c\u5982\u679c\u4f60\u5fd8\u8bb0\u4e86\u2018\u81ea\u5df1\u88ab\u8bc5\u5492\u2019\u8fd9\u4e2a\u4e8b\u5b9e\uff0c\u4f1a\u4e0d\u4f1a\u5c31\u80fd\u6446\u8131\u5176\u63a7\u5236\u3002\u201d</p><p>\u201c\u4f60\u7684\u610f\u601d\u662f\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u81ea\u5df1\u88ab\u8bc5\u5492\u4e86\uff0c\u6240\u4ee5\u90a3\u4e2a\u8bc5\u5492\u5c31\u771f\u7684\u751f\u6548\u4e86\uff1b\u5982\u679c\u6211\u4e0d\u77e5\u9053\uff0c\u6216\u8005\u9057\u5fd8\u4e86\uff0c\u90a3\u4e48\u5b83\u5c31\u6709\u53ef\u80fd\u4e0d\u751f\u6548\uff1f\u201d</p><p>\u624e\u745e\u5c14\u70b9\u5934\uff0c\u201c\u6211\u662f\u6709\u8fd9\u79cd\u731c\u6d4b\uff0c\u4e0d\u8fc7\u4ece\u6628\u665a\u7684\u8bd5\u9a8c\u7ed3\u679c\u6765\u770b\uff0c\u4f3c\u4e4e\u6ca1\u90a3\u4e48\u7b80\u5355\u3002\u5373\u4fbf\u6211\u501f\u52a9\u68a6\u5883\u8ba9\u4f60\u6682\u65f6\u5fd8\u8bb0\u8bc5\u5492\uff0c\u4f60\u8fd8\u662f\u6ca1\u529e\u6cd5\u52c3\u8d77\u3002\u201d</p><p>\u8fd9\u542c\u8d77\u6765\u771f\u662f\u8ba9\u4eba\u60b2\u4f24\u3002</p><p>\u201c\u597d\u5427\uff0c\u6211\u77e5\u9053\u4e86\uff0c\u201d\u743c\u6069\u65e0\u7cbe\u6253\u91c7\u5730\u8bf4\uff0c\u201c\u603b\u4e4b\u5c31\u662f\u5931\u8d25\u4e86\u5bf9\u5427\u3002\u201d</p><p>\u201c\u4e0d\u8981\u8fd9\u4e48\u6c14\u9981\uff0c\u201d\u624e\u745e\u5c14\u5b89\u6170\uff0c\u201c\u4efb\u4f55\u6280\u672f\u7684\u8fdb\u6b65\u90fd\u662f\u8270\u96be\u800c\u66f2\u6298\u7684\uff0c\u9700\u8981\u4e0d\u65ad\u7684\u601d\u7d22\u548c\u53cd\u590d\u7684\u8bd5\u9a8c\uff0c\u6bcf\u4e00\u6b21\u5931\u8d25\u4e2d\u90fd\u8574\u542b\u4e86\u6210\u529f\u7684\u79cd\u5b50\uff0c\u6bcf\u4e00\u6b21\u632b\u6298\u4e2d\u90fd\u5b55\u80b2\u4e86\u524d\u8fdb\u7684\u9636\u68af\uff0c\u6709\u53ef\u80fd\u9700\u8981\u51e0\u4ee3\u4eba\u7684\u52aa\u529b\u624d\u80fd\u8fbe\u6210\u6700\u7ec8\u76ee\u6807\u3002\u5373\u4fbf\u4f60\u5728\u6709\u751f\u4e4b\u5e74\u770b\u4e0d\u5230\u7406\u60f3\u7ed3\u679c\uff0c\u4f46\u4f60\u7684\u8d21\u732e\u4f1a\u88ab\u6240\u6709\u540e\u4eba\u94ed\u8bb0\u7684\u3002\u201d</p><p>\u201c\u7b97\u4e86\uff0c\u6211\u8fd9\u4e2a\u4eba\u76ee\u5149\u77ed\u6d45\uff0c\u8fd8\u662f\u5e0c\u671b\u80fd\u591f\u5728\u6709\u751f\u4e4b\u5e74\u2014\u2014\u4e0d\uff0c\u6700\u591a\u4e00\u4e24\u5e74\u5185\u2014\u2014\u770b\u5230\u7406\u60f3\u7ed3\u679c\u3002\u201d</p><p>\u201c\u4e00\u4e24\u5e74\u554a\uff0c\u8fd9\u65f6\u95f4\u6709\u70b9\u7d27\u5462\uff0c\u90a3\u4e48\u6211\u9700\u8981\u7533\u8bf7\u66f4\u591a\u7684\u7814\u7a76\u7ecf\u8d39\uff0c\u4e00\u95f4\u5de5\u4f5c\u5ba4\uff0c\u8fd8\u8981\u6709\u4e2a\u5f97\u529b\u52a9\u624b\uff0c\u55ef\uff0c\u6bd4\u5982\u51db\u5c31\u633a\u4e0d\u9519\u7684\u3002\u201d</p><p>\u4e0d\u9519\u4e2a\u9b3c\uff0c\u4f60\u8fd9\u5206\u660e\u662f\u5047\u516c\u6d4e\u79c1\u3002\u51db\u662f\u4e2a\u672f\u58eb\uff0c\u65bd\u6cd5\u80fd\u529b\u662f\u5929\u8d4b\u800c\u6765\uff0c\u5979\u81ea\u8eab\u53c8\u4e0d\u7b97\u52aa\u529b\uff0c\u8fd9\u79cd\u4eba\u53bb\u505a\u7814\u7a76\u52a9\u624b\uff0c\u4e0d\u662f\u641e\u7b11\u4e48\u3002</p><p>\u201c\u8fd9\u4f60\u5c31\u4e0d\u61c2\u4e86\uff0c\u7269\u5c3d\u5176\u7528\uff0c\u4eba\u5c3d\u5176\u624d\uff0c\u51db\u4e0d\u9700\u8981\u505a\u7814\u7a76\uff0c\u5979\u53ea\u8981\u8d1f\u8d23\u8ba9\u6211\u6bcf\u5929\u4fdd\u6301\u5fc3\u60c5\u6109\u5feb\uff0c\u5c31\u662f\u6700\u5927\u7684\u8d21\u732e\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u6c89\u95f7\u7684\u8f70\u9e23\u58f0\u81ea\u5934\u9876\u9ad8\u7a7a\u4e2d\u4f20\u6765\uff0c\u72c2\u98ce\u5377\u7740\u4e4c\u4e91\uff0c\u906e\u853d\u4e86\u6574\u4e2a\u5929\u7a79\u3002\u800c\u5728\u6f2b\u5929\u4e4c\u4e91\u4e4b\u4e2d\uff0c\u94f6\u767d\u8272\u7684\u7535\u5149\u6e38\u8d70\u73af\u7ed5\uff0c\u5f62\u6210\u4e09\u4e2a\u5de8\u5927\u7684\u6f29\u6da1\u3002\u8fd9\u662f\u6570\u540d\u9b54\u6cd5\u5973\u795e\u9009\u6c11\u8054\u624b\u6784\u5efa\u7684\u4f20\u9001\u901a\u9053\uff0c\u8ba9\u51fa\u5f81\u8005\u76f4\u63a5\u524d\u5f80\u76ee\u7684\u5730\uff0c\u514d\u53bb\u8def\u4e0a\u7684\u7ea0\u7f20\u3002\u7b2c\u4e94\u79d8\u5668\u7684\u5a01\u80fd\u4e4b\u4e00\uff0c\u662f\u5728\u5176\u9886\u57df\u5185\u4f1a\u4ece\u65e0\u5230\u6709\uff0c\u4e0d\u65ad\u5730\u8bde\u751f\u90aa\u9b54\uff0c\u800c\u4e14\u968f\u7740\u65f6\u95f4\u63a8\u79fb\uff0c\u90aa\u9b54\u4f1a\u4e0d\u65ad\u8fdb\u5316\u3001\u53d8\u5f3a\u3002\u9634\u5f71\u9547\u8fd9\u8fb9\u6218\u529b\u7cbe\u5f3a\uff0c\u6570\u91cf\u5374\u4e0d\u591a\uff0c\u5018\u82e5\u8981\u4e00\u8def\u5e73\u63a8\u8fc7\u53bb\uff0c\u90a3\u5c31\u6d88\u8017\u592a\u5927\u4e86\uff0c\u4e5f\u8d77\u4e0d\u5230\u7a81\u88ad\u7684\u6548\u679c\u3002\u4e3a\u4e86\u6784\u5efa\u8fd9\u4e09\u4e2a\u901a\u9053\uff0c\u636e\u8bf4\u8017\u8d39\u4e0d\u5c11\uff0c\u76f4\u63a5\u5bfc\u81f4\u201c\u4e03\u59d0\u59b9\u201d\u4e2d\u7684\u51e0\u4eba\u65e0\u6cd5\u53c2\u6218\uff0c\u53ea\u80fd\u7559\u5728\u9634\u5f71\u9547\u4e2d\u4f11\u517b\u3002</p><p>\u9b54\u59ec\u8e2e\u8d77\u811a\u5c16\uff0c\u5728\u743c\u6069\u7684\u8138\u988a\u8f7b\u8f7b\u5370\u4e0a\u4e00\u4e2a\u6de1\u7c89\u8272\u7684\u5507\u5370\uff0c\u201c\u597d\u5566\uff0c\u4f60\u8be5\u51fa\u53d1\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4e00\u5207\u5c0f\u5fc3\uff0c\u4e0d\u8981\u968f\u4fbf\u6cbe\u82b1\u60f9\u8349\u54e6\uff0c\u5426\u5219\u6211\u4f1a\u5ac9\u5992\u7684\u3002\u201d</p><p>\u743c\u6069\u4e00\u6014\uff0c\u201c\u4f60\u4e0d\u548c\u6211\u4e00\u8d77\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\uff0c\u6211\u662f\u5973\u4eba\uff0c\u600e\u4e48\u80fd\u4e0a\u6218\u573a\u5462\uff0c\u7559\u5728\u540e\u65b9\u5c06\u5bb6\u52a1\u4e8b\u6253\u7406\u6e05\u695a\uff0c\u7b49\u5f85\u7537\u4eba\u80dc\u5229\u51ef\u65cb\uff0c\u624d\u662f\u6211\u7684\u672c\u5206\u5440\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u5230\u5e95\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d</p><p>\u624e\u745e\u5c14\u7b11\u800c\u4e0d\u7b54\uff0c\u6325\u4e86\u6325\u624b\uff0c\u201c\u518d\u89c1\u3002\u201d</p><p>\u5341\u51e0\u9053\u94f6\u767d\u8272\u5149\u67f1\u81ea\u9ad8\u7a7a\u4e2d\u843d\u4e0b\uff0c\u51fa\u5f81\u8005\u4f9d\u6b21\u8d70\u5165\u5176\u4e2d\uff0c\u968f\u7740\u5149\u67f1\u5189\u5189\u4e0a\u5347\uff0c\u6d88\u5931\u5728\u6f29\u6da1\u4e4b\u4e2d\u3002\u6885\u83f2\u65af\u8d70\u51fa\u795e\u6bbf\u6b63\u95e8\uff0c\u6765\u5230\u743c\u6069\u8eab\u65c1\uff0c\u5979\u671d\u624e\u745e\u5c14\u793c\u8c8c\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u9b54\u59ec\u56de\u62a5\u4ee5\u5fae\u7b11\u3002\u4e00\u9053\u5149\u67f1\u843d\u4e0b\u6765\uff0c\u6b63\u5728\u4ed6\u4eec\u8eab\u65c1\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u8d70\u4e86\u8fdb\u53bb\uff0c\u8f6c\u773c\u95f4\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u203b\u203b\u203b</p><p>\u73ca\u5609\u7ad9\u5728\u7a97\u524d\uff0c\u671b\u7740\u8fdc\u5904\u7684\u9ad8\u7a7a\uff0c\u94f6\u767d\u7535\u5149\u7ec4\u6210\u7684\u6f29\u6da1\u901a\u9053\u6e10\u6e10\u6536\u7f29\uff0c\u6700\u7ec8\u6d88\u5931\u4e0d\u89c1\u3002\u5979\u4e00\u8a00\u4e0d\u53d1\uff0c\u795e\u60c5\u90c1\u90c1\uff0c\u4eff\u4f5b\u5728\u6c89\u601d\u3002</p><p>\u8f7b\u8f7b\u7684\u811a\u6b65\u58f0\u4ece\u80cc\u540e\u54cd\u8d77\uff0c\u201c\u600e\u4e48\u4e86\uff0c\u59d0\u59d0\uff1f\u201d\u838e\u73de\u514b\u95ee\uff0c\u201c\u4f60\u5728\u62c5\u5fc3\u4ed6\uff1f\u6ca1\u4e8b\u5566\uff0c\u90a3\u5bb6\u4f19\u72e1\u733e\u5f97\u5f88\uff0c\u6709\u5371\u9669\u80af\u5b9a\u8eb2\u5f97\u8fdc\u8fdc\u7684\uff0c\u4e0d\u4f1a\u6709\u95ee\u9898\u7684\u3002\u201d</p><p>\u73ca\u5609\u5fae\u5fae\u76b1\u7709\uff0c\u201c\u53ef\u662f\u6211\u603b\u6709\u79cd\u5947\u602a\u7684\u9884\u611f\uff0c\u4f3c\u4e4e\u6709\u4ec0\u4e48\u4e0d\u592a\u597d\u7684\u4e8b\u60c5\u5373\u5c06\u53d1\u751f\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u201d\u73ca\u5609\u82e6\u607c\u5730\u6447\u6447\u5934\uff0c\u201c\u53ea\u662f\u6a21\u7cca\u7684\u611f\u89c9\u3002\u201d</p><p>\u201c\u6709\u6ca1\u6709\u95ee\u8fc7\u8001\u5e08\u5462\uff1f\u201d</p><p>\u201c\u8fd8\u662f\u8054\u7cfb\u4e0d\u4e0a\u3002\u201d</p><p>\u5965\u5609\u83b1\u65af\u5982\u4eca\u662f\u5e7d\u7075\u72b6\u6001\uff0c\u5bc4\u5c45\u5728\u300a\u547d\u8fd0\u957f\u591c\u300b\u4e4b\u4e2d\uff0c\u901a\u5e38\u662f\u7ed9\u73ca\u5609\u4e0a\u8bfe\u7684\u65f6\u5019\u624d\u4f1a\u51fa\u73b0\uff0c\u5176\u4ed6\u65f6\u95f4\u9690\u9041\u65e0\u8e2a\uff0c\u4f46\u73ca\u5609\u8fd8\u662f\u53ef\u4ee5\u968f\u65f6\u8054\u7cfb\u5230\u5979\u7684\u3002\u7136\u800c\u6628\u665a\u4e0e\u743c\u6069\u8c08\u8fc7\u4e4b\u540e\uff0c\u5965\u5609\u83b1\u65af\u7a81\u7136\u5c31\u201c\u6d88\u5931\u201d\u4e86\uff0c\u8fde\u73ca\u5609\u90fd\u611f\u5e94\u4e0d\u5230\uff0c\u53ea\u7559\u4e0b\u4e00\u9053\u8baf\u606f\uff0c\u8bf4\u662f\u6709\u91cd\u8981\u7684\u4e8b\u60c5\u9700\u8981\u51c6\u5907\uff0c\u8ba9\u73ca\u5609\u81ea\u5df1\u591a\u52a0\u5c0f\u5fc3\uff0c\u7b49\u5f85\u5979\u7684\u6307\u4ee4\u3002\u73ca\u5609\u83ab\u540d\u5176\u5999\uff0c\u4f46\u4e5f\u53ea\u80fd\u7167\u529e\u3002</p><p>\u201c\u90a3\u8981\u4e48\u59d0\u59d0\u5360\u535c\u4e00\u4e0b\u8bd5\u8bd5\u770b\uff1f\u201d</p><p>\u73ca\u5609\u8ddf\u968f\u5965\u5609\u83b1\u65af\u5b66\u4e60\uff0c\u8bfe\u7a0b\u4e2d\u5f53\u7136\u5305\u62ec\u5360\u661f\u672f\u3002\u5965\u5609\u83b1\u65af\u662f\u9876\u7ea7\u7684\u5927\u9884\u8a00\u5e08\uff0c\u53c8\u662f\u6089\u5fc3\u6559\u5bfc\uff0c\u73ca\u5609\u5b66\u4e60\u5f97\u4e5f\u5f88\u8ba4\u771f\uff0c\u867d\u7136\u65f6\u65e5\u5c1a\u77ed\uff0c\u4f46\u9020\u8be3\u5df2\u7ecf\u4e0d\u4f4e\uff0c\u53ea\u662f\u7f3a\u4e4f\u7ecf\u9a8c\u800c\u5df2\u3002\u5b9e\u9645\u4e0a\uff0c\u65e9\u5728\u838e\u73de\u514b\u63d0\u8bae\u4e4b\u524d\uff0c\u5979\u5c31\u5df2\u7ecf\u6697\u4e2d\u5360\u7b97\u8fc7\u4e86\u3002</p><p>\u201c\u6211\u7684\u80fd\u529b\u8fd8\u4e0d\u591f\uff0c\u5360\u535c\u7684\u7ed3\u679c\u5f88\u6a21\u7cca\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4f46\u6709\u4e00\u70b9\u5374\u975e\u5e38\u6e05\u6670\uff0c\u5c31\u662f\u90a3\u4e2a\u5730\u65b9\uff0c\u201d\u5979\u6307\u4e86\u6307\u7a97\u5916\u7684\u8fdc\u5904\uff0c\u201c\u90a3\u4e2a\u5730\u65b9\u5728\u6211\u7684\u5360\u535c\u7ed3\u679c\u4e2d\u51fa\u73b0\u8fc7\u5f88\u591a\u6b21\uff0c\u6211\u4e0d\u77e5\u9053\u8fd9\u610f\u5473\u7740\u4ec0\u4e48\uff0c\u662f\u597d\u662f\u574f\uff0c\u4f46\u80af\u5b9a\u662f\u4e00\u4e2a\u5173\u952e\u70b9\u6240\u5728\u3002\u201d</p><p>\u838e\u73de\u514b\u987a\u7740\u73ca\u5609\u7684\u624b\u6307\u671b\u53bb\uff0c\u5979\u770b\u89c1\u4e86\u4e00\u7247\u5efa\u7b51\u7fa4\uff0c\u9ad8\u4f4e\u4e0d\u4e00\uff0c\u4f46\u90fd\u6709\u7740\u73ab\u7470\u8272\u7684\u73bb\u7483\u5916\u5899\u201c\u6668\u66e6\u795e\u6bbf\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u90a3\u91cc\u4f1a\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d\u838e\u73de\u514b\u4e0d\u89e3\uff0c\u4f46\u5979\u60f3\u4e86\u60f3\uff0c\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4eec\u5c31\u53bb\u770b\u770b\u597d\u4e86\uff1f\u201d</p><p>\u201c\u53bb\u770b\u770b\uff1f\u201d\u73ca\u5609\u6709\u4e9b\u72b9\u8c6b\u3002</p><p>\u201c\u55ef\uff0c\u4e0e\u5176\u5728\u8fd9\u91cc\u62c5\u5fc3\uff0c\u4e0d\u5982\u505a\u70b9\u4e8b\u60c5\u3002\u65e2\u7136\u6709\u8fd9\u4e2a\u7ebf\u7d22\uff0c\u90a3\u5c31\u53bb\u8bd5\u8bd5\u770b\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u6709\u4ec0\u4e48\u53d1\u73b0\u5462\u3002\u201d</p><p>\u73ca\u5609\u88ab\u8bf4\u670d\u4e86\uff0c\u201c\u90a3\u597d\uff0c\u6211\u4eec\u73b0\u5728\u5c31\u53bb\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u73ca\u5609\u662f\u838e\u5c14\u4fe1\u5f92\uff0c\u838e\u73de\u514b\u66f4\u662f\u4e2a\u90aa\u9b54\uff0c\u662f\u80af\u5b9a\u4e0d\u53d7\u6668\u66e6\u4e4b\u795e\u7684\u6559\u4f1a\u6240\u6b22\u8fce\u7684\u3002\u60f3\u8981\u50cf\u666e\u901a\u6e38\u5ba2\u4e00\u6837\u53bb\u53c2\u89c2\u795e\u6bbf\u663e\u7136\u4e0d\u73b0\u5b9e\uff0c\u66f4\u4f55\u51b5\u5728\u8fd9\u79cd\u975e\u5e38\u65f6\u671f\uff0c\u6240\u4ee5\u4ece\u4e00\u5f00\u59cb\uff0c\u5979\u4eec\u5c31\u6ca1\u6253\u7b97\u4ece\u6b63\u95e8\u8fdb\u53bb\uff0c\u800c\u662f\u79d8\u5bc6\u6f5c\u5165\u3002\u8fd9\u79cd\u4e8b\u60c5\uff0c\u73ca\u5609\u4e0d\u64c5\u957f\uff0c\u838e\u73de\u514b\u5374\u662f\u7cbe\u901a\u65e0\u6bd4\uff0c\u5728\u5979\u7684\u5f15\u5bfc\u4e4b\u4e0b\uff0c\u4e24\u4eba\u5f88\u5feb\u5c31\u7a7f\u8fc7\u795e\u6bbf\u7684\u5916\u56f4\u5730\u5e26\uff0c\u8fdb\u5165\u6838\u5fc3\u533a\u57df\u3002</p><p>\u6668\u66e6\u795e\u6bbf\u5360\u5730\u9887\u5e7f\uff0c\u5176\u5916\u56f4\u5012\u8fd8\u7f62\u4e86\uff0c\u6838\u5fc3\u533a\u57df\u90fd\u662f\u6709\u4e25\u5bc6\u7684\u9b54\u6cd5\u9632\u5fa1\u91cd\u91cd\u7b3c\u7f69\uff0c\u5c42\u5c42\u5b88\u62a4\uff0c\u914d\u5408\u4e0a\u5b88\u536b\u7684\u795e\u804c\u4eba\u5458\uff0c\u4eff\u4f5b\u4e00\u5ea7\u519b\u4e8b\u5821\u5792\uff0c\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u73ca\u5609\u548c\u838e\u73de\u514b\u6839\u672c\u4e0d\u53ef\u80fd\u6084\u65e0\u58f0\u606f\u5730\u6f5c\u5165\u3002\u4f46\u8be1\u5f02\u7684\u662f\uff0c\u4e24\u4eba\u4e00\u8def\u884c\u6765\uff0c\u867d\u7136\u6709\u4e9b\u66f2\u6298\uff0c\u5374\u59cb\u7ec8\u6ca1\u6709\u9047\u5230\u4ec0\u4e48\u771f\u6b63\u7684\u963b\u788d\uff0c\u8fd9\u4e0d\u7981\u8ba9\u73ca\u5609\u6709\u4e9b\u5fc3\u5934\u4e0d\u5b89\uff0c\u6000\u7591\u662f\u5426\u843d\u5165\u4e86\u4ec0\u4e48\u9677\u9631\u3002\u5979\u4e5f\u77e5\u9053\u6668\u66e6\u795e\u6bbf\u7684\u4e3b\u529b\u90e8\u961f\u524d\u4e0d\u4e45\u88ab\u8c03\u8d70\u7684\u4e8b\u60c5\uff0c\u7559\u5b88\u4eba\u5458\u5e76\u4e0d\u591a\uff0c\u4f46\u8fde\u7267\u5e08\u3001\u4f8d\u4ece\u3001\u89c1\u4e60\u751f\u5728\u5185\uff0c\u4e8c\u5341\u6765\u4e2a\u4eba\u603b\u8fd8\u662f\u6709\u7684\uff0c\u6240\u4ee5\u8fdb\u6765\u534a\u5929\u4e86\uff0c\u4e00\u4e2a\u5b88\u536b\u90fd\u78b0\u4e0d\u5230\uff0c\u8fd9\u5c31\u6709\u4e9b\u8e4a\u8df7\uff1b\u800c\u4e14\u90a3\u4e9b\u9b54\u6cd5\u9677\u9631\u3001\u795e\u672f\u7981\u5236\uff0c\u660e\u660e\u662f\u5b58\u5728\u7684\uff0c\u5374\u57fa\u672c\u90fd\u5904\u4e8e\u201c\u505c\u6ede\u201d\u72b6\u6001\uff0c\u6709\u7684\u751a\u81f3\u5df2\u7ecf\u88ab\u4eba\u63d0\u524d\u7834\u574f\uff0c\u8fd9\u5c31\u66f4\u5947\u602a\u4e86\u3002</p><p>\u201c\u600e\u4e48\u529e\uff0c\u59d0\u59d0\uff1f\u201d\u838e\u73de\u514b\u660e\u663e\u4e5f\u53d1\u73b0\u4e0d\u5bf9\u52b2\uff0c\u201c\u8981\u4e0d\u8981\u5148\u56de\u53bb\uff0c\u8fd9\u6b21\u5c31\u7b97\u4e86\uff1f\u201d</p><p>\u73ca\u5609\u54ac\u4e86\u54ac\u5634\u5507\uff0c\u6447\u6447\u5934\uff0c\u201c\u65e2\u7136\u5df2\u7ecf\u6765\u4e86\uff0c\u4e0d\u80fd\u5c31\u8fd9\u6837\u534a\u9014\u800c\u5e9f\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u603b\u8981\u641e\u6e05\u695a\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u518d\u8bf4\u3002\u201d</p><p>\u5979\u6027\u683c\u5916\u67d4\u5185\u521a\uff0c\u5e73\u7d20\u6e29\u548c\uff0c\u771f\u5230\u4e86\u8981\u51b3\u65ad\u7684\u65f6\u5019\uff0c\u53cd\u800c\u6108\u52a0\u575a\u6301\uff0c\u751a\u81f3\u6709\u70b9\u8ba4\u6b7b\u7406\u3002\u838e\u73de\u514b\u81ea\u7136\u542c\u4ece\u5979\u7684\u610f\u601d\uff0c\u4e24\u4eba\u7ee7\u7eed\u524d\u8fdb\uff0c\u6084\u6084\u7a7f\u8fc7\u4e00\u5904\u56de\u5eca\uff0c\u524d\u9762\u7a81\u7136\u4f20\u6765\u8bf4\u8bdd\u58f0\u3002</p><p>\u90a3\u662f\u4e24\u4e2a\u5e74\u8f7b\u4eba\uff0c\u4ece\u670d\u9970\u4e0a\u53ef\u4ee5\u770b\u51fa\u4ed6\u4eec\u662f\u8fd9\u5ea7\u795e\u6bbf\u7684\u89c1\u4e60\u7267\u5e08\uff0c\u6b63\u5750\u5728\u957f\u6905\u4e0a\u9ad8\u8c08\u9614\u8bba\uff0c\u5174\u81f4\u52c3\u52c3\u3002\u73ca\u5609\u548c\u838e\u73de\u514b\u6084\u6084\u9760\u8fd1\uff0c\u76f4\u5230\u53ef\u4ee5\u542c\u6e05\u695a\u4ed6\u4eec\u7684\u8c08\u8bdd\u5185\u5bb9\u3002\u201c\u827e\u6797\u5148\u751f\u7684\u8fd9\u4e2a\u6784\u601d\u5b9e\u5728\u7cbe\u5de7\uff0c\u201d\u5176\u4e2d\u4e00\u4f4d\u8138\u4e0a\u6709\u96c0\u6591\u7684\u89c1\u4e60\u7267\u5e08\u8d5e\u53f9\u7740\uff0c\u201c\u4f46\u603b\u89c9\u5f97\u5f88\u53ef\u60dc\u554a\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u5723\u7269\u5c31\u5f7b\u5e95\u6bc1\u6389\u4e86\u3002\u201d</p><p>\u201c\u9ece\u660e\u4e4b\u77f3\u672c\u6765\u5c31\u65e0\u6cd5\u53d6\u56de\u4e86\uff0c\u201d\u53e6\u4e00\u4f4d\u68d5\u8272\u5934\u53d1\u7684\u89c1\u4e60\u7267\u5e08\u8bf4\uff0c\u201c\u727a\u7272\u6389\u4e00\u4ef6\u5723\u7269\uff0c\u6362\u53d6\u9f99\u5deb\u6559\u8fd9\u4e2a\u5927\u654c\u7684\u8986\u706d\uff0c\u8fd8\u662f\u5f88\u5212\u7b97\u7684\u2014\u2014\u800c\u4e14\u4f60\u771f\u6b63\u53ef\u60dc\u7684\uff0c\u5176\u5b9e\u4e0d\u662f\u5723\u7269\uff0c\u662f\u90a3\u4f4d\u5723\u6b66\u58eb\u5c0f\u59d0\u5427\u3002\u201d</p><p>\u96c0\u6591\u7267\u5e08\u4e0d\u597d\u610f\u601d\u5730\u7b11\u4e86\u4e24\u58f0\u3002</p><p>\u201c\u6885\u83f2\u65af\u5c0f\u59d0\u786e\u5b9e\u662f\u96be\u5f97\u4e00\u89c1\u7684\u4f73\u4eba\uff0c\u201d\u68d5\u53d1\u7267\u5e08\u8bf4\uff0c\u201c\u7f8e\u4e3d\u3001\u806a\u660e\u3001\u5584\u826f\uff0c\u800c\u4e14\u5b66\u8bc6\u6e0a\u535a\uff0c\u7b80\u76f4\u662f\u5b8c\u7f8e\u65e0\u6687\uff0c\u6211\u76f8\u4fe1\u5373\u4fbf\u5929\u4f7f\u4e5f\u4e0d\u4f1a\u6bd4\u5979\u66f4\u4f18\u79c0\u3002\u4efb\u4f55\u7537\u4eba\u89c1\u5230\u5979\u90fd\u4f1a\u5fc3\u751f\u7231\u6155\uff0c\u8fd9\u662f\u5f88\u6b63\u5e38\u7684\uff0c\u4f46\u5979\u7ec8\u7a76\u662f\u90aa\u795e\u4e4b\u5973\u554a\u3002\u201d</p><p>\u201c\u90a3\u53ea\u662f\u4e2a\u8c23\u4f20\u5427\uff0c\u201d\u96c0\u6591\u7267\u5e08\u8fa9\u89e3\uff0c\u201c\u5982\u679c\u5979\u662f\u90aa\u795e\u4e4b\u5973\uff0c\u600e\u4e48\u4f1a\u6210\u4e3a\u5f8b\u6cd5\u4e4b\u795e\u7684\u5723\u6b66\u58eb\u5462\u3002\u201d</p><p>\u201c\u4e0d\u662f\u8c23\u4f20\uff0c\u201d\u68d5\u53d1\u7267\u5e08\u8bf4\uff0c\u201c\u6211\u542c\u6211\u53d4\u53d4\u8bf4\u8fc7\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u90a3\u53ef\u771f\u662f\u4e0d\u53ef\u601d\u8bae\uff0c\u201d\u96c0\u6591\u7267\u5e08\u4eff\u4f5b\u96be\u4ee5\u7f6e\u4fe1\u5730\u6447\u6447\u5934\uff0c\u201c\u83ab\u975e\u6b63\u56e0\u4e3a\u8fd9\u4e2a\u7f18\u6545\uff0c\u6240\u4ee5\u827e\u6797\u5148\u751f\u624d\u2026\u2026\u201d</p><p>\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u6216\u8005\u8bf4\uff0c\u6ca1\u90a3\u4e48\u7b80\u5355\uff0c\u201d\u68d5\u53d1\u7267\u5e08\u8bf4\uff0c\u201c\u4e4b\u6240\u4ee5\u8fd9\u4e48\u505a\uff0c\u6700\u4e3b\u8981\u7684\u76ee\u7684\u8fd8\u662f\u8981\u89e3\u51b3\u9f99\u5deb\u6559\uff0c\u5176\u4ed6\u90fd\u53ea\u662f\u9644\u5e26\u800c\u5df2\u3002\u201d</p><p>\u201c\u4f46\u8fd9\u6837\u771f\u6ca1\u95ee\u9898\u5417\uff1f\u201d\u96c0\u6591\u7267\u5e08\u72b9\u8c6b\u5730\u8bf4\uff0c\u201c\u5373\u4fbf\u5979\u662f\u90aa\u795e\u4e4b\u5973\uff0c\u5979\u6bd5\u7adf\u4e5f\u662f\u5f8b\u6cd5\u4e4b\u795e\u7684\u5723\u6b66\u58eb\u3002\u6211\u4eec\u8fd9\u4e48\u505a\uff0c\u5c06\u6765\u8981\u5982\u4f55\u89e3\u91ca\uff1f\u201d</p><p>\u201c\u727a\u7272\u5979\u4e00\u4e2a\u4eba\uff0c\u6362\u53d6\u5168\u5c40\u7684\u80dc\u5229\uff0c\u8fd9\u6709\u4ec0\u4e48\u95ee\u9898\uff1f\u5982\u679c\u5979\u771f\u7684\u662f\u4e00\u4f4d\u5723\u6b66\u58eb\uff0c\u90a3\u4e48\u7406\u5f53\u65e9\u6709\u6b64\u89c9\u609f\u5427\uff0c\u201d\u68d5\u53d1\u7267\u5e08\u8bf4\uff0c\u201c\u800c\u4e14\u5982\u679c\u6b64\u6218\u4e0d\u80dc\uff0c\u6211\u4eec\u53ea\u6015\u5168\u90fd\u96be\u9003\u4e00\u6b7b\uff0c\u4e5f\u5c31\u4e0d\u9700\u8981\u5411\u8c01\u89e3\u91ca\u4ec0\u4e48\u4e86\u3002\u53ea\u6709\u5148\u83b7\u5f97\u80dc\u5229\uff0c\u624d\u6709\u8d44\u683c\u53bb\u8003\u8651\u5982\u4f55\u5584\u540e\u7684\u95ee\u9898\u3002\u201d</p><p>\u96c0\u6591\u7267\u5e08\u6c89\u9ed8\u4e86\u7247\u523b\uff0c\u70b9\u70b9\u5934\uff0c\u201c\u4f60\u8bf4\u5f97\u5bf9\u3002\u201d</p><p>\u63a5\u4e0b\u6765\u4e24\u4eba\u53c8\u4ea4\u8c08\u4e86\u4e00\u4f1a\uff0c\u4e3b\u8981\u662f\u5728\u731c\u6d4b\u8fd9\u4e00\u6218\u7684\u7ed3\u679c\uff0c\u610f\u89c1\u4e0d\u5b8c\u5168\u4e00\u81f4\uff0c\u4f46\u6709\u4e00\u70b9\u662f\u5171\u901a\u7684\uff1a\u4ece\u8bed\u6c14\u4e2d\u5224\u65ad\uff0c\u4ed6\u4eec\u5bf9\u526f\u4e3b\u6559\u827e\u6797\u51c6\u5907\u7684\u90a3\u4e2a\u79d8\u5bc6\u6740\u624b\u950f\u90fd\u975e\u5e38\u6709\u4fe1\u5fc3\uff0c\u8ba4\u4e3a\u53ea\u8981\u80fd\u591f\u6210\u529f\u4f7f\u7528\uff0c\u8428\u9a6c\u65af\u7279\u548c\u9f99\u5deb\u6559\u80af\u5b9a\u65e0\u6cd5\u62b5\u6321\u3002\u53c8\u8fc7\u4e86\u4e00\u4f1a\uff0c\u8fdc\u5904\u7684\u949f\u58f0\u54cd\u8d77\uff0c\u5e94\u8be5\u662f\u5230\u4e86\u7948\u7977\u65f6\u95f4\uff0c\u4e24\u4eba\u4fbf\u5306\u5306\u79bb\u53bb\u3002</p><p>\u73ca\u5609\u548c\u838e\u73de\u514b\u4ece\u85cf\u8eab\u5904\u8d70\u51fa\u6765\uff0c\u770b\u7740\u4e24\u4e2a\u89c1\u4e60\u7267\u5e08\u7684\u80cc\u5f71\u3002\u201c\u63a5\u4e0b\u6765\u600e\u4e48\u529e\uff0c\u59d0\u59d0\uff1f\u201d\u838e\u73de\u514b\u8f7b\u58f0\u95ee\u3002</p><p>\u201c\u53bb\u62dc\u8bbf\u4e00\u4e0b\u90a3\u4f4d\u526f\u4e3b\u6559\u827e\u6797\u5148\u751f\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u6211\u5bf9\u4ed6\u90a3\u4ec0\u4e48\u7cbe\u5de7\u6784\u601d\u5f88\u6709\u5174\u8da3\u3002\u201d</p><p>\u7b2c\u4e00\u767e\u4e00\u5341\u4e94\u8282</p><p>\u827e\u6797\u501a\u9760\u5728\u4e00\u628a\u767d\u53f6\u6728\u5236\u6210\u7684\u8eba\u6905\u4e0a\uff0c\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u7ffb\u7740\u4e66\uff0c\u542c\u7740\u7a97\u5916\u98ce\u62c2\u8fc7\u6811\u53f6\u7684\u6c99\u6c99\u58f0\u3002\u8fd9\u628a\u8eba\u6905\u662f\u4ed6\u81ea\u5df1\u6240\u9020\uff0c\u5e76\u975e\u5e02\u573a\u4e0a\u4e70\u6765\uff0c\u4ece\u9009\u6750\u3001\u70ae\u5236\u5230\u7ec4\u88c5\u3001\u4e0a\u6f06\uff0c\u5168\u90fd\u662f\u4ed6\u4eb2\u529b\u4eb2\u4e3a\uff0c\u4e0d\u5047\u4eba\u624b\u3002\u6bcf\u5929\u5348\u540e\u5728\u4e0a\u9762\u8eba\u4e00\u8eba\uff0c\u6216\u8005\u7ffb\u7ffb\u4e66\uff0c\u6216\u8005\u5c0f\u61a9\u7247\u523b\uff0c\u662f\u4ed6\u96be\u5f97\u7684\u4eab\u53d7\u3002</p><p>\u201c\u4eca\u5929\u7684\u5929\u6c14\u771f\u4e0d\u9519\u3002\u201d\u4ed6\u968f\u53e3\u81ea\u8a00\u81ea\u8bed\u3002</p><p>\u827e\u6797\u5e76\u4e0d\u662f\u9634\u5f71\u8c37\u672c\u5730\u4eba\uff0c\u800c\u662f\u6765\u81ea\u5357\u65b9\u7684\u5361\u4e3d\u73ca\uff0c\u90a3\u662f\u4e00\u4e2a\u6d77\u8fb9\u7684\u56fd\u5bb6\uff0c\u9633\u5149\u70bd\u70c8\uff0c\u6c14\u5019\u6f6e\u6e7f\uff0c\u4e0e\u8c37\u5730\u5730\u533a\u7684\u5e72\u71e5\u5bd2\u51b7\u5927\u76f8\u5f84\u5ead\uff0c\u5c3d\u7ba1\u5df2\u7ecf\u5728\u8fd9\u91cc\u5f85\u4e86\u5c06\u8fd1\u516d\u5e74\uff0c\u827e\u6797\u4ecd\u7136\u89c9\u5f97\u4e0d\u9002\u5e94\uff0c\u6bcf\u5230\u79cb\u5b63\u5c31\u4f1a\u6d41\u9f3b\u8840\u3002\u4e0d\u8fc7\u81f3\u5c11\u6709\u4e00\u6837\u597d\u5904\uff0c\u5c31\u662f\u56f0\u6270\u4ed6\u591a\u5e74\u7684\u5173\u8282\u75bc\u75db\u5927\u5927\u51cf\u8f7b\u4e86\uff0c\u6709\u65f6\u5019\u751a\u81f3\u90fd\u611f\u89c9\u4e0d\u5230\u3002\u5c31\u51b2\u8fd9\u4e00\u70b9\uff0c\u4ed6\u8fd8\u662f\u633a\u4e50\u610f\u7ee7\u7eed\u7559\u5728\u6b64\u5730\u4efb\u804c\u7684\uff0c\u53ea\u53ef\u60dc\u5e94\u8be5\u662f\u6ca1\u8fd9\u4e2a\u673a\u4f1a\u4e86\u3002\u8fd9\u4e00\u6b21\u7684\u4e8b\u60c5\uff0c\u867d\u7136\u662f\u5e94\u51ef\u5c14\u672c\u7684\u8981\u6c42\u884c\u4e8b\uff0c\u4f46\u6bd5\u7adf\u51fa\u81ea\u4ed6\u7684\u624b\u7b14\u3002\u90a3\u4f4d\u53eb\u6885\u83f2\u65af\u7684\u5c0f\u59d1\u5a18\u5230\u5e95\u662f\u63d0\u5c14\u5723\u6b66\u58eb\uff0c\u867d\u7136\u7531\u4e8e\u5176\u8840\u7edf\u95ee\u9898\uff0c\u88ab\u6559\u4f1a\u6392\u65a5\u5728\u8fb9\u7f18\uff0c\u4f46\u5979\u5f53\u771f\u51fa\u4e86\u4e8b\uff0c\u6559\u4f1a\u4e0d\u53ef\u80fd\u65e0\u52a8\u4e8e\u8877\uff0c\u5fc5\u7136\u8981\u8ba8\u4e2a\u8bf4\u6cd5\u3002\u5230\u65f6\u5019\uff0c\u81ea\u5df1\u6559\u4f1a\u8fd9\u8fb9\u5c31\u8981\u7ed9\u4e2a\u4ea4\u4ee3\uff0c\u800c\u827e\u6797\u663e\u7136\u662f\u8981\u88ab\u8e22\u51fa\u53bb\u9876\u7f6a\u7684\uff0c\u5f53\u7136\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u5927\u9ebb\u70e6\uff0c\u4f30\u8ba1\u4f1a\u88ab\u6307\u6d3e\u5230\u4ec0\u4e48\u504f\u8fdc\u5730\u533a\uff0c\u8fc7\u4e0a\u5341\u5e74\u516b\u5e74\u7684\u624d\u80fd\u56de\u6765\u4e86\u3002</p><p>\u201c\u90a3\u4e2a\u5c0f\u59d1\u5a18\u2026\u2026\u53ef\u60dc\u4e86\u3002\u201d</p><p>\u827e\u6797\u548c\u6885\u83f2\u65af\u6253\u4ea4\u9053\u4e0d\u591a\uff0c\u751a\u81f3\u90fd\u6ca1\u6709\u5355\u72ec\u4ea4\u8c08\u8fc7\u3002\u4f46\u4f5c\u4e3a\u672c\u5730\u795e\u6bbf\u7684\u526f\u4e3b\u6559\u2014\u2014\u867d\u7136\u53ea\u662f\u4e09\u4f4d\u526f\u4e3b\u6559\u4e4b\u4e00\uff0c\u800c\u4e14\u6392\u540d\u6700\u672b\uff0c\u827e\u6797\u52c9\u5f3a\u4e5f\u53ef\u4ee5\u7b97\u662f\u6559\u4f1a\u7684\u9ad8\u5c42\u4eba\u5458\uff0c\u6709\u8d44\u683c\u63a5\u89e6\u5230\u4e00\u4e9b\u8f83\u4e3a\u673a\u5bc6\u7684\u8d44\u6599\u3002\u4ed6\u77e5\u9053\u6885\u83f2\u65af\u7684\u8eab\u4efd\uff0c\u4f46\u5766\u767d\u5730\u8bf4\uff0c\u4ed6\u5e76\u4e0d\u662f\u5f88\u5728\u610f\u3002\u201c\u90aa\u795e\u4e4b\u5973\u201d\u548c\u201c\u90aa\u795e\u201d\u53c8\u4e0d\u662f\u4e00\u7801\u4e8b\uff0c\u53ea\u6709\u90a3\u4e9b\u6ca1\u89c1\u8bc6\u7684\u5e74\u8f7b\u4eba\u624d\u4f1a\u5bf9\u6b64\u5927\u60ca\u5c0f\u602a\uff0c\u5230\u4e86\u827e\u6797\u8fd9\u79cd\u5e74\u7eaa\u548c\u5730\u4f4d\uff0c\u5bf9\u8840\u7edf\u4ec0\u4e48\u7684\uff0c\u4e0d\u8bf4\u4e0d\u5c51\u4e00\u987e\uff0c\u81f3\u5c11\u4e5f\u4e0d\u4f1a\u592a\u5f53\u56de\u4e8b\u3002\u771f\u8981\u8bf4\u7684\u8bdd\uff0c\u4ed6\u7236\u4eb2\u662f\u4e2a\u8001\u5b9e\u672c\u5206\u7684\u624b\u5de5\u827a\u8005\uff0c\u7237\u7237\u5374\u66fe\u7ecf\u662f\u5361\u4e3d\u73ca\u540d\u566a\u4e00\u65f6\u7684\u5f3a\u76d7\uff0c\u540e\u6765\u88ab\u540c\u4f19\u6240\u6740\uff0c\u6b7b\u65e0\u846c\u8eab\u4e4b\u5730\uff0c\u8bba\u8840\u7edf\u96be\u9053\u5c31\u6bd4\u90aa\u795e\u4e4b\u5973\u8981\u201c\u7eaf\u6d01\u201d\uff1f\u65e2\u7136\u63d0\u5c14\u4f1a\u9009\u62e9\u5979\u4f5c\u4e3a\u5723\u6b66\u58eb\uff0c\u5c31\u8db3\u4ee5\u8bf4\u660e\u95ee\u9898\u4e86\u3002\u6885\u83f2\u65af\u7684\u8fc7\u5f80\u529f\u7ee9\uff0c\u827e\u6797\u4e5f\u662f\u5927\u81f4\u6e05\u695a\u7684\uff0c\u522b\u7684\u4e0d\u8bf4\uff0c\u524d\u4e24\u5e74\u535a\u5f97\u4e4b\u95e8\u5730\u533a\u7206\u53d1\u761f\u75ab\uff0c\u5bc6\u65af\u5353\u8bfa\u7684\u6668\u66e6\u795e\u6bbf\u6d3e\u4eba\u62a4\u9001\u9ece\u660e\u4e4b\u77f3\u524d\u53bb\u6551\u6cbb\uff0c\u6700\u540e\u5374\u843d\u5165\u838e\u5c14\u7684\u9677\u9631\uff0c\u82e5\u975e\u6885\u83f2\u65af\uff0c\u8fd9\u4ef6\u6668\u66e6\u5723\u7269\u5c31\u8981\u843d\u5165\u90aa\u795e\u4e4b\u624b\u4e86\u3002</p><p>\u867d\u7136\u5bf9\u6885\u83f2\u65af\u9887\u6709\u597d\u611f\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u827e\u6797\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u540e\u6094\u4e0d\u5b89\u3002\u51ef\u5c14\u672c\u8bf4\u5f97\u5bf9\uff0c\u5728\u8fd9\u79cd\u751f\u6b7b\u5b58\u4ea1\u7684\u5173\u5934\uff0c\u4e3a\u4e86\u80dc\u5229\u53ea\u80fd\u4e0d\u62e9\u624b\u6bb5\uff0c\u54ea\u6709\u90a3\u4e48\u591a\u53ef\u987e\u5fcc\u7684\u3002\u5170\u68ee\u5fb7\u5c14\u4e0d\u662f\u63d0\u5c14\uff0c\u5e76\u4e0d\u8981\u6c42\u4fe1\u5f92\u7684\u4e00\u4e3e\u4e00\u52a8\u5408\u4e4e\u5f8b\u6cd5\uff0c\u79c9\u6301\u6b63\u4e49\uff0c\u53ea\u8981\u7ed3\u679c\u662f\u597d\u7684\uff0c\u76ee\u6807\u662f\u6b63\u786e\u7684\uff0c\u90a3\u4e48\u5728\u624b\u6bb5\u4e0a\u7075\u6d3b\u53d8\u901a\u4e00\u4e9b\uff0c\u4e5f\u662f\u53ef\u4ee5\u7684\u3002</p><p>\u7b97\u4e86\uff0c\u4e0d\u60f3\u90a3\u4e48\u591a\u4e86\uff0c</p><p>\u53cd\u6b63\u81ea\u5df1\u8be5\u505a\u7684\u3001\u80fd\u505a\u7684\u90fd\u5df2\u7ecf\u505a\u5b8c\uff0c\u63a5\u4e0b\u6765\u5c31\u770b\u51ef\u5c14\u672c\u4ed6\u4eec\u4e86\u3002</p><p>\u5c06\u6240\u6709\u7684\u6742\u5ff5\u90fd\u6448\u53bb\uff0c\u827e\u6797\u5168\u8eab\u653e\u677e\uff0c\u6253\u7b97\u7761\u4e0a\u4e00\u4f1a\uff0c\u4eca\u5929\u7684\u795d\u798f\u4eea\u5f0f\u53ef\u662f\u8017\u8d39\u4e86\u4ed6\u4e0d\u5c11\u7cbe\u529b\uff0c\u9700\u8981\u597d\u597d\u4f11\u606f\u3002\u4f30\u8ba1\u9192\u6765\u7684\u65f6\u5019\uff0c\u90a3\u8fb9\u7684\u6218\u6597\u4e5f\u8be5\u6709\u4e2a\u7ed3\u5c40\u4e86\u5427\u3002</p><p>\u6b63\u534a\u7761\u534a\u9192\u4e2d\uff0c\u4e00\u80a1\u51b0\u51b7\u800c\u9510\u5229\u7684\u523a\u75db\u611f\u81ea\u8116\u9888\u4e0a\u4f20\u6765\uff0c\u8ba9\u4ed6\u5239\u90a3\u95f4\u53d8\u5f97\u6e05\u9192\u65e0\u6bd4\u3002\u7d27\u63a5\u7740\uff0c\u4ed6\u770b\u89c1\u4e00\u4f4d\u7a7f\u7740\u6d45\u7eff\u8272\u76ae\u7532\u7684\u7f8e\u4e3d\u5c11\u5973\u4ece\u9634\u5f71\u4e2d\u8d70\u51fa\u6765\uff0c\u7ad9\u5728\u81ea\u5df1\u9762\u524d\uff0c\u5c45\u9ad8\u4e34\u4e0b\u5730\u4fef\u89c6\u7740\u3002\u201c\u827e\u6797\u5148\u751f\uff1f\u201d\u5979\u6e29\u548c\u5730\u95ee\u3002</p><p>\u201c\u662f\u6211\uff0c\u4f60\u662f\u8c01\uff1f\u201d</p><p>\u65e0\u9700\u4f4e\u5934\u53bb\u770b\uff0c\u827e\u6797\u77e5\u9053\u81ea\u5df1\u7684\u54bd\u5589\u8981\u5bb3\u5df2\u7ecf\u88ab\u4e00\u67c4\u5229\u5203\u62b5\u4f4f\uff0c\u8eab\u540e\u663e\u7136\u8fd8\u6709\u53e6\u5916\u4e00\u4f4d\u88ad\u51fb\u8005\u3002\u4ed6\u8868\u9762\u52c9\u5f3a\u7ef4\u6301\u9547\u5b9a\uff0c\u5fc3\u4e2d\u5374\u662f\u65e2\u60ca\u4e14\u6012\uff0c\u8be7\u5f02\u65e0\u6bd4\u3002\u6668\u66e6\u795e\u6bbf\u867d\u7136\u4e3b\u529b\u88ab\u8c03\u8d70\uff0c\u9632\u5fa1\u786e\u5b9e\u8f83\u5e73\u5e38\u7a7a\u865a\u5f88\u591a\uff0c\u4f46\u4e5f\u7edd\u4e0d\u662f\u53ef\u4ee5\u4efb\u4eba\u968f\u610f\u6765\u5f80\u7684\u5730\u65b9\uff0c\u5b88\u536b\u5c31\u7f62\u4e86\uff0c\u90a3\u4e9b\u9b54\u6cd5\u9632\u5fa1\u53ef\u662f\u4ed6\u4eb2\u81ea\u5e03\u4e0b\u7684\uff0c\u7ecf\u8fc7\u591a\u6b21\u5b8c\u5584\u3001\u91cd\u91cd\u52a0\u56fa\uff0c\u600e\u4e48\u4f1a\u88ab\u4eba\u6084\u65e0\u58f0\u606f\u5730\u6f5c\u8fdb\u6765\uff1f\u827e\u6797\u5f53\u7136\u4e0d\u4f1a\u8ba4\u4e3a\u81ea\u5df1\u7684\u9632\u5fa1\u7edd\u5bf9\u65e0\u61c8\u53ef\u51fb\uff0c\u4f46\u80fd\u505a\u5230\u8fd9\u70b9\u7684\uff0c\u6574\u4e2a\u9634\u5f71\u9547\u4e2d\u5c48\u6307\u53ef\u6570\uff0c\u65e0\u975e\u4e5f\u5c31\u662f\u5927\u8d24\u8005\u3001\u51ef\u5c14\u672c\u7b49\u4eba\u3002\u773c\u524d\u8fd9\u4f4d\u5c11\u5973\u770b\u8d77\u6765\u4e5f\u4e0d\u8fc7\u5341\u4e03\u516b\u5c81\u7684\u5e74\u7eaa\uff0c\u96be\u4e0d\u6210\u5c45\u7136\u662f\u4e00\u4f4d\u9876\u7ea7\u7684\u5927\u5deb\u5e08\u4e0d\u6210\uff0c\u9634\u5f71\u9547\u4e2d\u4ec0\u4e48\u65f6\u5019\u6765\u4e86\u8fd9\u6837\u7684\u4eba\u7269\uff1f</p><p>\u201c\u6211\u662f\u5170\u5c3c\u65af\u7279\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u6211\u542c\u8bf4\u4e86\u4e00\u4e9b\u6709\u8da3\u7684\u4f20\u8a00\uff0c\u6709\u5173\u6885\u83f2\u65af\u5c0f\u59d0\uff0c\u8fd8\u6709\u9ece\u660e\u4e4b\u77f3\uff0c\u4f3c\u4e4e\u4f60\u505a\u4e86\u67d0\u4e9b\u4e8b\u60c5\uff0c\u4f1a\u5bf9\u5979\u5f88\u4e0d\u5229\u2014\u2014\u6211\u5e0c\u671b\u548c\u4f60\u6838\u5b9e\u4e00\u4e0b\u3002\u201d</p><p>\u5170\u5c3c\u65af\u7279\uff1f</p><p>\u827e\u6797\u5feb\u901f\u56de\u5fc6\u7740\u8fd9\u4e2a\u59d3\u6c0f\uff0c\u7136\u540e\u4ed6\u60f3\u4e86\u8d77\u6765\uff0c\u8ba4\u771f\u770b\u7684\u8bdd\uff0c\u773c\u524d\u7684\u5c11\u5973\u548c\u90a3\u4e2a\u838e\u5c14\u9009\u6c11\u8fd8\u9887\u4e3a\u76f8\u4f3c\u3002\u201c\u743c\u6069\xb7\u5170\u5c3c\u65af\u7279\uff1f\u201d\u4ed6\u8bd5\u63a2\u5730\u95ee\uff0c\u201c\u4f60\u548c\u4ed6\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d</p><p>\u201c\u6211\u662f\u4ed6\u59d0\u59d0\uff0c\u201d\u73ca\u5609\u5fae\u5fae\u4e00\u7b11\uff0c\u201c\u81ea\u6211\u4ecb\u7ecd\u5b8c\u6bd5\uff0c\u827e\u6797\u5148\u751f\uff0c\u8bf7\u56de\u7b54\u6211\u7684\u95ee\u9898\u3002\u201d</p><p>\u201c\u4f60\u60f3\u77e5\u9053\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5168\u90e8\u3002\u201d\u73ca\u5609\u8bf4\u3002</p><p>\u201c\u6050\u6015\u6211\u65e0\u53ef\u5949\u544a\uff0c\u201d\u827e\u6797\u8bf4\uff0c\u201c\u6668\u66e6\u7684\u4f8d\u8005\u4e50\u4e8e\u6307\u70b9\u8ff7\u6d25\uff0c\u4f46\u4e0d\u662f\u88ab\u4eba\u62ff\u5200\u67b6\u5728\u8116\u5b50\u4e0a\u7684\u65f6\u5019\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u73ca\u5609\u504f\u4e86\u504f\u5934\uff0c\u827e\u6797\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\uff0c\u53ea\u89c9\u5de6\u624b\u4e00\u9635\u51b0\u51c9\uff0c\u968f\u5373\u662f\u5267\u75db\u88ad\u5fc3\uff0c\u4ed6\u5927\u60ca\u4e4b\u4e0b\uff0c\u4f4e\u5934\u671b\u53bb\uff0c\u6b63\u770b\u89c1\u81ea\u5df1\u7684\u5de6\u624b\u624b\u638c\u4e0e\u624b\u8155\u5206\u79bb\uff0c\u5657\u901a\u4e00\u58f0\u6389\u843d\u5728\u5730\u677f\u4e0a\uff0c\u8840\u6db2\u81ea\u521b\u53e3\u5904\u55b7\u6d8c\u800c\u51fa\uff0c\u6e85\u5f97\u5230\u5904\u90fd\u662f\u3002\u201c\u4f60\u2026\u2026\u4f60\u2026\u2026\u201d</p><p>\u201c\u5f88\u62b1\u6b49\uff0c\u4f46\u6211\u65f6\u95f4\u6709\u9650\uff0c\u53ea\u80fd\u7528\u4e00\u4e9b\u6bd4\u8f83\u76f4\u63a5\u7684\u65b9\u5f0f\u8ba9\u4f60\u660e\u767d\u6211\u7684\u6001\u5ea6\u3002\u5e0c\u671b\u6ca1\u6709\u5f04\u75bc\u4f60\uff0c\u201d\u73ca\u5609\u67d4\u58f0\u8bf4\uff0c\u201c\u73b0\u5728\u53ef\u4ee5\u8bf4\u4e86\u5417\uff1f\u201d</p><p>\u827e\u6797\u5d29\u6e83\u4e86\uff0c\u4ed6\u662f\u4e2a\u7eaf\u7cb9\u7684\u6587\u804c\u4eba\u5458\uff0c\u57fa\u672c\u6ca1\u4e0a\u8fc7\u6218\u573a\u2014\u2014\u5076\u5c14\u53bb\u8fc7\u51e0\u6b21\uff0c\u4e5f\u90fd\u662f\u8fdc\u8fdc\u8eb2\u5728\u5b89\u5168\u7684\u540e\u65b9\uff0c\u4ece\u6ca1\u89c1\u8fc7\u8fd9\u4e48\u8840\u8165\u7684\u573a\u9762\u3002\u5982\u679c\u8fd9\u60c5\u666f\u53d1\u751f\u5728\u522b\u4eba\u8eab\u4e0a\uff0c\u4ed6\u6216\u8bb8\u8fd8\u80fd\u4fdd\u6301\u9547\u5b9a\uff0c\u4f46\u53d1\u751f\u5728\u81ea\u5df1\u8eab\u4e0a\uff0c\u4ed6\u7684\u795e\u7ecf\u5c31\u627f\u53d7\u4e0d\u4f4f\u4e86\u3002\u201c\u6211\u2026\u2026\u6211\u8bf4\uff0c\u201d\u4ed6\u5927\u53e3\u5927\u53e3\u5730\u5598\u7740\u6c14\uff0c\u201c\u4f60\u8d76\u5feb\u2026\u2026\u5e2e\u6211\u6b62\u8840\u3002\u201d</p><p>\u53e6\u4e00\u4f4d\u8910\u53d1\u5c11\u5973\u4ece\u80cc\u540e\u8f6c\u51fa\u6765\uff0c\u5979\u4e5f\u5f88\u6f02\u4eae\uff0c\u800c\u4e14\u8863\u7740\u66b4\u9732\u800c\u6027\u611f\uff0c\u4f46\u827e\u6797\u6839\u672c\u6ca1\u6709\u5fc3\u601d\u6b23\u8d4f\u8fd9\u4e9b\u3002\u5728\u4ed6\u88ab\u795e\u672f\u5f3a\u5316\u8fc7\u7684\u89c6\u89c9\u4e2d\uff0c\u6e05\u695a\u5730\u770b\u89c1\u5c11\u5973\u5934\u4e0a\u7684\u5f2f\u89d2\u548c\u8eab\u540e\u7684\u5c3e\u5df4\uff0c\u8fd9\u662f\u4e2a\u9b45\u9b54\u2014\u2014\u8be5\u6b7b\u7684\uff0c\u90a3\u4e9b\u9b54\u6cd5\u7981\u5236\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u6321\u4e0d\u4f4f\u4eba\u7c7b\u5c31\u7b97\u4e86\uff0c\u600e\u4e48\u4f1a\u8fde\u4e2a\u90aa\u9b54\u95ef\u8fdb\u6765\u90fd\u6ca1\u53cd\u5e94\u3002</p><p>\u9b45\u9b54\u4e00\u53ea\u624b\u4e0a\u63e1\u7740\u77ed\u5251\uff0c\u663e\u7136\u521a\u624d\u5c31\u662f\u5b83\u5207\u4e0b\u4e86\u827e\u6797\u7684\u624b\u638c\uff0c\u5979\u4f38\u51fa\u53e6\u4e00\u53ea\u624b\uff0c\u638c\u5fc3\u5bf9\u7740\u827e\u6797\u624b\u8155\u7684\u65ad\u53e3\u5904\uff0c\u9ed8\u8bf5\u4e86\u4e00\u53e5\u5492\u8bed\u3002\u9ed1\u8272\u7684\u96fe\u6c14\u4ece\u9b45\u9b54\u638c\u5fc3\u6563\u53d1\u51fa\u6765\uff0c\u5c06\u827e\u6797\u7684\u65ad\u8155\u5305\u88f9\u4f4f\uff0c\u6d41\u8840\u505c\u6b62\u4e86\uff0c\u800c\u4e14\u75bc\u75db\u7684\u611f\u89c9\u4e5f\u968f\u4e4b\u6d88\u5931\u4e86\uff0c\u4f46\u827e\u6797\u6e05\u695a\u8fd9\u53ea\u662f\u6682\u65f6\u7684\uff0c\u8fd9\u6839\u672c\u4e0d\u662f\u771f\u6b63\u7684\u6cbb\u7597\u6cd5\u672f\u3002\u201c\u90a3\u4f4d\u6885\u83f2\u65af\u5c0f\u59d0\u878d\u5408\u4e86\u6211\u6559\u5723\u7269\u9ece\u660e\u4e4b\u77f3\uff0c\u6211\u5e03\u7f6e\u4e86\u4e00\u4e2a\u673a\u5173\uff0c\u53ef\u4ee5\u4e00\u6b21\u6027\u6fc0\u53d1\u9ece\u660e\u4e4b\u77f3\u7684\u529b\u91cf\uff0c\u9020\u6210\u795e\u5723\u7206\u70b8\uff0c\u5bf9\u5468\u56f4\u7684\u4ea1\u7075\u9020\u6210\u5de8\u5927\u4f24\u5bb3\uff0c\u201d\u4ed6\u4e00\u53e3\u6c14\u5c06\u8bdd\u8bf4\u5b8c\uff0c\u201c\u8fd9\u662f\u51ef\u5c14\u672c\u5148\u751f\u7684\u4e3b\u610f\uff0c\u6211\u53ea\u662f\u914d\u5408\u3002\u201d</p><p>\u201c\u4e00\u6b21\u6027\u6fc0\u53d1\u9ece\u660e\u4e4b\u77f3\u7684\u529b\u91cf\uff1f\u90a3\u6885\u83f2\u65af\u4f1a\u600e\u4e48\u6837\uff1f\u201d</p><p>\u827e\u6797\u4e0d\u7b54\uff0c\u663e\u7136\u4e5f\u4e0d\u9700\u8981\u56de\u7b54\u3002</p><p>\u73ca\u5609\u7a0d\u7a0d\u6c89\u541f\uff0c\u201c\u5177\u4f53\u600e\u4e48\u505a\uff1f\u201d</p><p>\u9ece\u660e\u4e4b\u77f3\u662f\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u7684\u5723\u7269\uff0c\u4e43\u662f\u4e00\u4ef6\u795e\u5668\uff0c\u8981\u7834\u574f\u5b83\u6216\u8bb8\u4e0d\u96be\uff0c\u4f46\u8981\u5c06\u5b83\u7684\u529b\u91cf\u6fc0\u53d1\u5230\u9876\u5cf0\uff0c\u751a\u81f3\u8d85\u8fc7\u81ea\u8eab\u6781\u9650\uff0c\u5c31\u4e0d\u662f\u90a3\u4e48\u7b80\u5355\u7684\u4e8b\u60c5\u4e86\u3002\u800c\u4e14\u9ece\u660e\u4e4b\u77f3\u5982\u4eca\u5df2\u7ecf\u548c\u6885\u83f2\u65af\u878d\u5408\u4e3a\u4e00\u4f53\uff0c\u827e\u6797\u6839\u672c\u5c31\u63a5\u89e6\u4e0d\u5230\uff0c\u4ed6\u5230\u5e95\u662f\u5982\u4f55\u64cd\u4f5c\u7684\uff1f\u8fd9\u70b9\u5b9e\u5728\u4ee4\u4eba\u8d39\u89e3\u3002</p><p>\u827e\u6797\u7528\u5c3d\u53ef\u80fd\u901a\u4fd7\u800c\u7b80\u6d01\u7684\u8bed\u8a00\u89e3\u91ca\u4e86\u4e00\u756a\uff0c\u603b\u7ed3\u8d77\u6765\u5c31\u662f\uff1a\u4ed6\u53d1\u660e\u4e86\u4e00\u79cd\u540d\u4e3a\u201c\u5171\u632f\u201d\u7684\u7406\u8bba\uff0c\u6839\u636e\u8fd9\u79cd\u7406\u8bba\uff0c\u4efb\u4f55\u4e00\u9053\u9b54\u6cd5\u7269\u54c1\u90fd\u6709\u5176\u56fa\u6709\u7684\u201c\u9891\u7387\u201d\uff0c\u5373\u4fbf\u662f\u795e\u5668\u3001\u5723\u7269\u4e5f\u4e0d\u4f8b\u5916\uff0c\u5176\u529b\u91cf\u53d1\u6325\u7684\u9ad8\u4f4e\uff0c\u6b63\u4e0e\u9891\u7387\u6709\u5173\u3002\u901a\u8fc7\u76f8\u540c\u6216\u8005\u8fd1\u4f3c\u9891\u7387\uff0c\u5c31\u53ef\u4ee5\u5bf9\u9b54\u6cd5\u7269\u54c1\u4ea7\u751f\u5f71\u54cd\u3002\u51fa\u5f81\u4e4b\u524d\uff0c\u795e\u6bbf\u8ba9\u6240\u6709\u4eba\u996e\u4e0b\u5723\u6c34\uff0c\u53c8\u8fdb\u884c\u4e86\u7948\u7977\u795d\u798f\uff0c\u901a\u8fc7\u8fd9\u4e9b\u4eea\u5f0f\u4e0e\u9ece\u660e\u4e4b\u77f3\u5f62\u6210\u4e86\u8054\u7ed3\uff0c\u6784\u6210\u4e00\u4e2a\u5b8c\u6574\u7684\u201c\u5171\u632f\u201d\u7cfb\u7edf\uff0c\u800c\u51ef\u5c14\u672c\u624b\u4e2d\u5219\u638c\u63e1\u7740\u4e00\u67c4\u7279\u5236\u7684\u201c\u94a5\u5319\u201d\u3002\u5728\u5408\u9002\u7684\u65f6\u673a\uff0c\u4ee5\u53ca\u5728\u6070\u5f53\u7684\u8ddd\u79bb\u4e4b\u5185\uff0c\u51ef\u5c14\u672c\u53ef\u4ee5\u6fc0\u53d1\u94a5\u5319\u7684\u529b\u91cf\uff0c\u4ece\u800c\u5f71\u54cd\u5230\u9ece\u660e\u4e4b\u77f3\uff0c\u5c06\u5b83\u7684\u529b\u91cf\u6fc0\u53d1\u5230\u6781\u9650\uff0c\u6700\u7ec8\u7206\u70b8\u5f00\u6765\uff0c\u5b9e\u73b0\u5927\u8303\u56f4\u6e05\u573a\u6548\u679c\u3002</p><p>\u201c\u867d\u7136\u6211\u4e0d\u662f\u5f88\u61c2\uff0c\u4f46\u542c\u8d77\u6765\u786e\u5b9e\u662f\u5f88\u5de7\u5999\u7684\u6784\u601d\uff0c\u201d\u73ca\u5609\u70b9\u5934\u8868\u793a\u8d5e\u8bb8\uff0c\u201c\u90a3\u8981\u5982\u4f55\u7834\u89e3\uff1f\u201d</p><p>\u201c\u6211\u7684\u6784\u601d\u662f\u5b8c\u7f8e\u7684\uff0c\u4e0d\u53ef\u80fd\u7834\u89e3\u2014\u2014\u597d\u5427\uff0c\u94a5\u5319\uff0c\u5b83\u662f\u6fc0\u53d1\u8fd9\u4e2a\u7cfb\u7edf\u7684\u5173\u952e\u3002\u73b0\u5728\u5b83\u5728\u51ef\u5c14\u672c\u5148\u751f\u624b\u4e2d\uff0c\u4f60\u53ef\u4ee5\u53bb\u548c\u4ed6\u5546\u91cf\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u8bf4\uff0c\u201d\u73ca\u5609\u671d\u9b45\u9b54\u793a\u610f\uff0c\u201c\u4f60\u5df2\u7ecf\u6beb\u65e0\u7528\u5904\uff1f\u201d</p><p>\u827e\u6797\u8138\u8272\u82cd\u767d\u5730\u770b\u7740\u9b45\u9b54\u4e3e\u8d77\u77ed\u5251\uff0c\u653e\u5728\u4ed6\u7684\u53f3\u624b\u624b\u8155\u4e0a\u65b9\uff0c\u4f5c\u52bf\u6b32\u65a9\uff0c\u201c\u4e0d\uff0c\u4e0d\u662f\uff0c\u6211\u521a\u521a\u60f3\u8d77\u6765\uff0c\u8fd8\u6709\u4e00\u79cd\u65b9\u6cd5\uff0c\u201d\u4ed6\u614c\u5fd9\u8bf4\uff0c\u201c\u62a4\u7b26\uff0c\u628a\u90a3\u679a\u62a4\u7b26\u6bc1\u6389\u5c31\u884c\u3002\u201d</p><p>\u795e\u6bbf\u7ed9\u6240\u6709\u51fa\u5f81\u8005\u90fd\u914d\u53d1\u4e86\u4e00\u4ef6\u62a4\u8eab\u7b26\uff0c\u5b83\u4eec\u7684\u786e\u6709\u538b\u5236\u4ea1\u7075\u7684\u6548\u679c\uff0c\u4f46\u6885\u83f2\u65af\u7684\u90a3\u679a\u62a4\u7b26\u662f\u7279\u5236\u7684\uff0c\u53ef\u4ee5\u8fdb\u4e00\u6b65\u5f3a\u5316\u94a5\u5319\u4e0e\u9ece\u660e\u4e4b\u77f3\u4e4b\u95f4\u7684\u201c\u8054\u7ed3\u201d\uff0c\u8ba9\u6574\u4e2a\u5171\u632f\u7cfb\u7edf\u66f4\u52a0\u7a33\u56fa\u3002\u53cd\u8fc7\u6765\uff0c\u5982\u679c\u80fd\u591f\u62a2\u5728\u51ef\u5c14\u672c\u6fc0\u53d1\u94a5\u5319\u4e4b\u524d\uff0c\u5c06\u62a4\u7b26\u6467\u6bc1\uff0c\u6574\u4e2a\u7cfb\u7edf\u5fc5\u7136\u4f1a\u56e0\u6b64\u53d7\u5230\u91cd\u521b\uff0c\u751a\u81f3\u76f4\u63a5\u74e6\u89e3\u3002</p><p>\u73ca\u5609\u53c8\u95ee\u4e86\u51e0\u53e5\uff0c\u827e\u6797\u5df2\u7ecf\u51b3\u5b9a\u914d\u5408\uff0c\u81ea\u7136\u662f\u77e5\u65e0\u4e0d\u8a00\uff0c\u8a00\u65e0\u4e0d\u5c3d\u3002\u6700\u540e\u73ca\u5609\u89c1\u5df2\u7ecf\u95ee\u4e0d\u51fa\u4ec0\u4e48\u65b0\u4e1c\u897f\uff0c\u4fbf\u5411\u838e\u73de\u514b\u70b9\u4e86\u70b9\u5934\uff0c\u9b45\u9b54\u4f1a\u610f\uff0c\u62ac\u624b\u4e00\u5251\u5212\u8fc7\uff0c\u7267\u5e08\u53cc\u76ee\u5706\u77aa\uff0c\u54bd\u5589\u4e2d\u53d1\u51fa\u683c\u683c\u7684\u52a8\u9759\uff0c\u968f\u5373\u5657\u901a\u5012\u5730\uff0c\u6c14\u7edd\u8eab\u4ea1\u3002</p><p>\u201c\u73b0\u5728\u600e\u4e48\u529e\uff0c\u59d0\u59d0\uff1f\u201d\u838e\u73de\u514b\u95ee\u3002</p><p>\u201c\u6211\u4eec\u53bb\u627e\u743c\u6069\u548c\u827e\u5f25\u8587\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u5e0c\u671b\u8fd8\u6765\u5f97\u53ca\u3002\u201d</p><p>\u5979\u8f6c\u8eab\u6b32\u8d70\uff0c\u5374\u89c1\u838e\u73de\u514b\u7ad9\u5728\u539f\u5730\u4e00\u52a8\u4e0d\u52a8\uff0c\u53cc\u624b\u4f4e\u5782\u4e0b\u6765\uff0c\u8fde\u773c\u775b\u90fd\u95ed\u4e0a\uff0c\u4eff\u4f5b\u7761\u7740\u3002\u73ca\u5609\u5148\u662f\u8be7\u5f02\uff0c\u968f\u5373\u82e5\u6709\u6240\u609f\uff0c\u4fbf\u5728\u6b64\u65f6\uff0c\u4e00\u4e2a\u58f0\u97f3\u5728\u623f\u95f4\u7684\u89d2\u843d\u91cc\u54cd\u4e86\u8d77\u6765\u3002</p><p>\u201c\u4e0d\u5f97\u4e0d\u8bf4\uff0c\u4f60\u603b\u662f\u80fd\u591f\u8ba9\u6211\u51fa\u4e4e\u610f\u6599\uff0c\u201d\u706b\u53d1\u84dd\u88d9\u7684\u9b54\u59ec\u81ea\u7a7a\u6c14\u4e2d\u6162\u6162\u663e\u73b0\u51fa\u6765\uff0c\u201c\u597d\u4e45\u4e0d\u89c1\u4e86\uff0c\u5c0f\u96c5\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u73ca\u5609\u76b1\u4e86\u76b1\u7709\uff0c\u201c\u6211\u4e0d\u662f\u4ec0\u4e48\u5c0f\u96c5\uff0c\u4f60\u8ba4\u9519\u4eba\u4e86\u3002\u201d</p><p>\u201c\u6216\u8bb8\u5427\uff0c\u201d\u624e\u745e\u5c14\u5e76\u4e0d\u575a\u6301\uff0c\u201c\u53cd\u6b63\u53ea\u662f\u4e2a\u540d\u5b57\u800c\u5df2\u3002\u201d</p><p>\u201c\u662f\u4f60\u5728\u6697\u4e2d\u5e2e\u6211\uff1f\u201d\u73ca\u5609\u95ee\u3002</p><p>\u201c\u7b97\u662f\u5427\uff0c\u867d\u7136\u6211\u53ea\u662f\u5355\u7eaf\u5730\u597d\u5947\uff0c\u60f3\u770b\u770b\u4f60\u4f1a\u5982\u4f55\u9009\u62e9\u800c\u5df2\uff0c\u8c08\u4e0d\u4e0a\u5e2e\u5fd9\uff0c\u201d\u9b54\u59ec\u8bf4\uff0c\u201c\u4f60\u771f\u7684\u6253\u7b97\u53bb\u6551\u827e\u5f25\u8587\uff1f\u201d</p><p>\u201c\u6709\u4ec0\u4e48\u95ee\u9898\u5417\uff1f\u201d\u73ca\u5609\u53cd\u95ee\u3002</p><p>\u201c\u6211\u7684\u610f\u601d\u662f\uff1a\u4f60\u662f\u5426\u5df2\u7ecf\u8ba4\u771f\u8003\u8651\u6e05\u695a\u3002\u201d</p><p>\u201c\u827e\u5f25\u8587\u662f\u6211\u7684\u670b\u53cb\uff0c\u6211\u5f53\u7136\u8981\u53bb\u6551\u5979\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u8fd9\u65e0\u9700\u8003\u8651\u3002\u201d</p><p>\u9b54\u59ec\u8f7b\u7b11\uff0c\u201c\u662f\u5417\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u4eec\u662f\u60c5\u654c\u5462\u3002\u201d</p><p>\u201c\u662f\u670b\u53cb\uff0c\u4e5f\u662f\u60c5\u654c\uff0c\u201d\u73ca\u5609\u5766\u7136\u627f\u8ba4\uff0c\u201c\u4f46\u6211\u4e0d\u4f1a\u89c1\u6b7b\u4e0d\u6551\u3002\u201d</p><p>\u201c\u8fd8\u662f\u90a3\u4e2a\u95ee\u9898\uff0c\u4f60\u771f\u7684\u8003\u8651\u6e05\u695a\u4e86\u5417\uff1f\u201d\u624e\u745e\u5c14\u518d\u6b21\u95ee\uff0c\u201c\u4f60\u82e5\u6551\u4e86\u5979\uff0c\u5c31\u662f\u9677\u743c\u6069\u4e8e\u5371\u9669\u4e4b\u4e2d\u54e6\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u73ca\u5609\u4e0b\u610f\u8bc6\u5730\u95ee\uff0c\u968f\u5373\u53cd\u5e94\u8fc7\u6765\u3002</p><p>\u51ef\u5c14\u672c\u548c\u827e\u6797\u5e76\u4e0d\u662f\u4e0e\u6885\u83f2\u65af\u6709\u4ec0\u4e48\u4ec7\u6028\uff0c\u7279\u610f\u8981\u9488\u5bf9\u5979\u3002\u4ed6\u4eec\u7684\u76ee\u7684\u662f\u8ba9\u9ece\u660e\u4e4b\u77f3\u81ea\u7206\uff0c\u85c9\u6b64\u6253\u5012\u9f99\u5deb\u6559\uff0c\u6487\u5f00\u624b\u6bb5\u662f\u5426\u5149\u660e\u6b63\u5927\u4e0d\u8c08\uff0c\u8fd9\u6218\u672f\u7684\u786e\u662f\u6709\u6548\u7684\u3002\u9ece\u660e\u4e4b\u77f3\u662f\u6668\u66e6\u5723\u7269\uff0c\u662f\u4e00\u5207\u4ea1\u7075\u7684\u514b\u661f\uff0c\u800c\u8428\u9a6c\u65af\u7279\u548c\u4ed6\u7684\u624b\u4e0b\uff0c\u4e5d\u6210\u4e5d\u90fd\u662f\u4ea1\u7075\uff0c\u6b63\u88ab\u5176\u514b\u5236\u3002\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u9f99\u5deb\u6559\u770b\u89c1\u654c\u4eba\u642c\u7740\u9ece\u660e\u4e4b\u77f3\u8fd9\u79cd\u5927\u6740\u5668\u8fc7\u6765\uff0c\u80af\u5b9a\u6709\u591a\u8fdc\u8dd1\u591a\u8fdc\uff0c\u4f46\u5982\u4eca\u5723\u7269\u878d\u5408\u5728\u6885\u83f2\u65af\u4f53\u5185\uff0c\u9690\u853d\u6027\u6781\u4f73\uff0c\u4e00\u65e6\u5728\u5c40\u52bf\u6fc0\u70c8\u65f6\u5f15\u7206\uff0c\u90a3\u6218\u679c\u5c31\u975e\u5e38\u60ca\u4eba\u4e86\uff0c\u8981\u8bf4\u4e00\u4e3e\u6467\u6bc1\u9f99\u5deb\u6559\u4e5f\u4e0d\u662f\u4e0d\u53ef\u80fd\u3002</p><p>\u5f53\u7136\uff0c\u8fd9\u6837\u505a\u662f\u6709\u4ee3\u4ef7\u7684\uff0c\u6700\u5927\u7684\u4ee3\u4ef7\u5c31\u662f\u6885\u83f2\u65af\uff0c\u5979\u5df2\u7ecf\u548c\u9ece\u660e\u4e4b\u77f3\u878d\u5408\u4e3a\u4e00\u3002\u9ece\u660e\u4e4b\u77f3\u81ea\u7206\u4e86\uff0c\u5979\u5fc5\u5b9a\u96be\u4ee5\u5e78\u514d\u3002\u53cd\u8fc7\u6765\u8bf4\uff0c\u5982\u679c\u8981\u6551\u6885\u83f2\u65af\uff0c\u90a3\u5c31\u4e0d\u80fd\u8ba9\u9ece\u660e\u4e4b\u77f3\u81ea\u7206\uff0c\u51ef\u5c14\u672c\u7684\u6218\u672f\u5c31\u6253\u4e86\u6c34\u6f02\u3002\u51ef\u5c14\u672c\u4f1a\u5982\u4f55\uff0c\u73ca\u5609\u5e76\u4e0d\u5173\u5fc3\uff0c\u4f46\u4ed6\u7684\u7b79\u5212\u4e0d\u6210\uff0c\u5f88\u53ef\u80fd\u4f1a\u5bfc\u81f4\u6b64\u6218\u5931\u5229\uff0c\u751a\u81f3\u4e00\u8d25\u6d82\u5730\uff0c\u8428\u9a6c\u65af\u7279\u548c\u9f99\u5deb\u6559\u6210\u4e3a\u8d62\u5bb6\u2014\u2014\u5176\u5b9e\u8c01\u80dc\u8c01\u8d1f\uff0c\u73ca\u5609\u4e5f\u4e0d\u662f\u5f88\u5173\u5fc3\uff0c\u95ee\u9898\u662f\u743c\u6069\u73b0\u5728\u6b63\u5728\u6218\u573a\u4e0a\uff0c\u800c\u4e14\u8fd8\u662f\u5728\u51ef\u5c14\u672c\u8fd9\u8fb9\u3002\u73ca\u5609\u77e5\u9053\u743c\u6069\u6709\u4e9b\u4fdd\u547d\u624b\u6bb5\uff0c\u4f46\u5200\u5251\u65e0\u773c\uff0c\u5175\u51f6\u6218\u5371\uff0c\u8fd9\u79cd\u5927\u89c4\u6a21\u9ad8\u7b49\u7ea7\u7684\u6df7\u6218\uff0c\u8c01\u6562\u4fdd\u8bc1\u81ea\u5df1\u4e00\u5b9a\u80fd\u5168\u8eab\u800c\u9000\uff0c\u81ea\u7136\u80fd\u591a\u4e00\u5206\u80dc\u7b97\u90fd\u662f\u597d\u7684\u3002</p><p>\u624e\u745e\u5c14\u7684\u8a00\u4e0b\u4e4b\u610f\uff0c\u4e0d\u8fc7\u5982\u6b64\u3002</p><p>\u73ca\u5609\u9762\u4e34\u9009\u62e9\u3002\u5982\u679c\u5979\u8896\u624b\u65c1\u89c2\uff0c\u5f53\u505a\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\uff0c\u5219\u6885\u83f2\u65af\u5f88\u53ef\u80fd\u4e0d\u5e78\uff0c\u4f46\u743c\u6069\u5b89\u5168\u7684\u51e0\u7387\u4f1a\u63d0\u5347\uff1b\u53cd\u8fc7\u6765\uff0c\u5982\u679c\u5979\u8981\u53bb\u6551\u6885\u83f2\u65af\uff0c\u4e14\u4e0d\u8bba\u80fd\u5426\u6210\u529f\uff0c\u5982\u679c\u6210\u529f\uff0c\u6885\u83f2\u65af\u5f97\u6551\uff0c\u4f46\u743c\u6069\u906d\u9047\u5371\u9669\u7684\u53ef\u80fd\u6027\u5c31\u4f1a\u5927\u589e\u3002</p><p>\u201c\u6240\u4ee5\u4f60\u4ecd\u7136\u613f\u610f\u53bb\u6551\u4e00\u4e2a\u60c5\u654c\uff0c\u54ea\u6015\u540e\u679c\u662f\u8ba9\u81ea\u5df1\u7231\u4eba\u7684\u6b7b\u4ea1\u6982\u7387\u4e0a\u5347\u90a3\u4e48\u4e00\u70b9\uff1f\u201d</p><p>\u624e\u745e\u5c14\u8bf4\u5f97\u5f88\u5de7\u5999\uff0c\u5979\u5e76\u4e0d\u662f\u8bf4\u201c\u827e\u5f25\u8587\u6b7b\uff0c\u743c\u6069\u5c31\u6d3b\uff1b\u6216\u8005\u827e\u5f25\u8587\u6d3b\uff0c\u743c\u6069\u5c31\u6b7b\u201d\uff0c\u8fd9\u79cd\u8bf4\u6cd5\u6709\u9707\u64bc\u529b\uff0c\u4f46\u592a\u6781\u7aef\uff0c\u53cd\u800c\u4f1a\u9002\u5f97\u5176\u53cd\u3002\u5979\u53ea\u662f\u5f88\u8bda\u5b9e\u5730\u544a\u8bc9\u73ca\u5609\uff0c\u6885\u83f2\u65af\u7684\u751f\u6b7b\uff0c\u4f1a\u5f71\u54cd\u5230\u743c\u6069\u7684\u5b89\u5371\uff0c\u8fd9\u79cd\u5f71\u54cd\u6216\u8bb8\u5e76\u4e0d\u5927\uff0c\u53ea\u662f\u201c\u6b7b\u4ea1\u6982\u7387\u4e0a\u5347\u90a3\u4e48\u4e00\u70b9\u201d\uff0c\u4f46\u8fd9\u5c31\u5df2\u7ecf\u8db3\u591f\u4e86\u3002</p><p>\u4eba\u662f\u611f\u60c5\u751f\u7269\uff0c\u81ea\u5229\u4e43\u662f\u672c\u80fd\uff0c\u4eb2\u8fd1\u8005\u91cd\u6108\u5c71\u5cb3\uff0c\u758f\u8fdc\u8005\u8f7b\u5982\u9e3f\u6bdb\u3002\u8eab\u8fb9\u7684\u4eb2\u670b\u597d\u53cb\u53bb\u4e16\uff0c\u5f80\u5f80\u4f24\u5fc3\u6b32\u7edd\uff0c\u75db\u4e0d\u6b32\u751f\uff1b\u4e07\u91cc\u4e4b\u5916\u7684\u997f\u6b8d\u904d\u91ce\uff0c\u4e0d\u8fc7\u4e00\u58f0\u53f9\u606f\uff0c\u8f6c\u8eab\u5373\u5fd8\u3002\u5982\u679c\u8ba9\u743c\u6069\u6765\u6743\u8861\uff0c\u73ca\u5609\u548c\u6885\u83f2\u65af\u5b70\u91cd\u5b70\u8f7b\uff0c\u4ed6\u6050\u6015\u5f88\u96be\u51b3\u65ad\uff1b\u4f46\u5bf9\u4e8e\u73ca\u5609\u800c\u8a00\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u8c01\u66f4\u91cd\u8981\uff0c\u662f\u5b8c\u5168\u4e0d\u7528\u8003\u8651\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u548c\u827e\u5f25\u8587\u76f8\u6bd4\u8f83\uff0c\u771f\u7684\u5f88\u96be\u8ba9\u4eba\u6709\u81ea\u4fe1\u5462\uff0c\u6709\u65f6\u5019\u5c31\u4f1a\u5fcd\u4e0d\u4f4f\u5ac9\u5992\uff0c\u5ac9\u5992\u5f97\u60f3\u628a\u5979\u6084\u6084\u6740\u6389\uff0c\u201d\u9b54\u59ec\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u662f\u5982\u6b64\uff0c\u60f3\u5fc5\u4f60\u4e5f\u4e0d\u4f8b\u5916\u5427\u3002\u5982\u4eca\u6709\u6b64\u826f\u673a\uff0c\u4f60\u4ec0\u4e48\u90fd\u4e0d\u7528\u505a\uff0c\u5c31\u53ef\u4ee5\u5fc3\u60f3\u4e8b\u6210\u4e86\u54e6\u3002\u201d</p><p>\u201c\u90a3\u4f60\u7684\u76ee\u7684\u4f55\u5728\u5462\uff1f\u201d</p><p>\u201c\u770b\u70ed\u95f9\u554a\uff0c\u6709\u4eba\u66fe\u7ecf\u544a\u8bc9\u6211\u8bf4\uff1a\u591a\u770b\u70ed\u95f9\u6709\u52a9\u4e8e\u4e30\u5bcc\u4eba\u751f\u7ecf\u5386\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u4f60\u4e0d\u7528\u62c5\u5fc3\u6211\u4f1a\u4e8b\u540e\u544a\u5bc6\u2014\u2014\u5982\u679c\u6211\u90a3\u4e48\u505a\uff0c\u5c82\u4e0d\u662f\u628a\u81ea\u5df1\u4e5f\u7275\u8fde\u8fdb\u53bb\u3002\u743c\u6069\u6216\u8bb8\u4f1a\u539f\u8c05\u4f60\uff0c\u4f46\u80af\u5b9a\u4e0d\u4f1a\u539f\u8c05\u6211\u5427\u3002\u201d</p><p>\u201c\u90a3\u4e48\u5047\u5982\u6211\u9009\u62e9\u4ecd\u7136\u8981\u53bb\u6551\u827e\u5f25\u8587\uff0c\u4f60\u4f1a\u963b\u6b62\u6211\u5417\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u89c9\u5f97\u4f60\u4f1a\u8fd9\u4e48\u505a\uff0c\u4f46\u65e0\u8bba\u4f60\u505a\u4ec0\u4e48\uff0c\u6211\u90fd\u4e0d\u4f1a\u963b\u6b62\uff0c\u201d\u624e\u745e\u5c14\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u8bf4\u4e86\uff0c\u6211\u53ea\u662f\u6765\u770b\u70ed\u95f9\u7684\u3002\u201d</p><p>\u201c\u6240\u4ee5\u4f60\u5e76\u4e0d\u62c5\u5fc3\u4ed6\u7684\u5b89\u5371\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u662f\u9b54\u9b3c\u554a\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u6211\u6ca1\u529e\u6cd5\u5e38\u9a7b\u51e1\u95f4\uff0c\u603b\u662f\u4e24\u5730\u5206\u5c45\u5bf9\u611f\u60c5\u4e0d\u597d\u5462\u3002\u4f46\u5982\u679c\u4ed6\u6b7b\u4e86\uff0c\u6211\u5c31\u53ef\u4ee5\u5c06\u4ed6\u63a5\u5230\u5730\u72f1\uff0c\u53d8\u5f97\u548c\u6211\u4e00\u6837\uff0c\u4ece\u6b64\u671d\u5915\u76f8\u5904\uff0c\u53cc\u5bbf\u53cc\u98de\uff0c\u8fd9\u4e0d\u662f\u5f88\u597d\u5417\uff1f\u201d</p><p>\u73ca\u5609\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u7136\u540e\u53f9\u4e86\u53e3\u6c14\u3002\u201c\u786e\u5b9e\u662f\u4e2a\u597d\u673a\u4f1a\uff0c\u9519\u8fc7\u8fd9\u4e00\u6b21\uff0c\u4ee5\u540e\u6211\u4e00\u5b9a\u4f1a\u540e\u6094\u7684\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6240\u4ee5\u53ea\u6b64\u4e00\u6b21\uff0c\u4e0b\u4e0d\u4e3a\u4f8b\u5427\u3002\u201d</p><p>\u201c\u54e6\uff1f\u201d</p><p>\u201c\u4f60\u8bf4\u5f97\u5f88\u6709\u9053\u7406\uff0c\u6211\u51e0\u4e4e\u8981\u88ab\u4f60\u8bf4\u670d\u4e86\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4f46\u6211\u53ea\u662f\u5728\u60f3\uff1a\u5982\u679c\u8ba9\u743c\u6069\u81ea\u5df1\u6765\u9009\u62e9\uff0c\u4ed6\u4f1a\u600e\u4e48\u505a\u5462\uff1f\u201d</p><p>\u624e\u745e\u5c14\u76b1\u7709\u4e0d\u7b54\u3002\u743c\u6069\u7684\u9009\u62e9\u663e\u7136\u662f\u5f88\u6e05\u695a\u7684\u3002</p><p>\u201c\u6211\u4e5f\u66fe\u7ecf\u542c\u8bf4\u8fc7\u4e00\u53e5\u8bdd\uff1a\u5b88\u4f4f\u7537\u4eba\u7684\u5c0a\u4e25\uff0c\u662f\u5973\u4eba\u7684\u77dc\u6301\u3002\u743c\u6069\u5462\uff0c\u5f53\u7136\u4e0d\u7b97\u4e2a\u597d\u4eba\uff0c\u4f46\u6709\u4e9b\u4e8b\u60c5\u8fd8\u662f\u633a\u575a\u6301\u7684\u3002\u5982\u679c\u4ed6\u77e5\u9053\u81ea\u5df1\u8981\u9760\u727a\u7272\u5fc3\u7231\u7684\u5973\u4eba\u6765\u6d3b\u547d\uff0c\u80af\u5b9a\u6b7b\u90fd\u4e0d\u613f\u610f\u3002\u65e2\u7136\u4ed6\u73b0\u5728\u4e0d\u5728\uff0c\u90a3\u4e48\u6211\u4f5c\u4e3a\u59bb\u5b50\uff0c\u81ea\u7136\u8981\u5c0a\u91cd\u4ed6\u7684\u610f\u613f\uff0c\u201d\u73ca\u5609\u7b11\u7740\u8bf4\uff0c\u201c\u81f3\u4e8e\u4ed6\u81ea\u5df1\uff0c\u90a3\u5bb6\u4f19\u4ece\u5c0f\u5c31\u72e1\u733e\uff0c\u6211\u76f8\u4fe1\u6ca1\u90a3\u4e48\u5bb9\u6613\u6b7b\u2014\u2014\u5982\u679c\u771f\u7684\u6b7b\u4e86\uff0c\u90a3\u4e48\u6211\u518d\u53bb\u5730\u72f1\u627e\u4ed6\u5c31\u662f\u4e86\u3002\u201d</p><p>\u624e\u745e\u5c14\u5148\u662f\u6c89\u541f\uff0c\u968f\u5373\u53c8\u5fae\u7b11\u8d77\u6765\uff0c\u201c\u4f60\u8fd8\u771f\u662f\u559c\u6b22\u51fa\u4eba\u610f\u6599\u5462\uff0c\u5c0f\u96c5\uff0c\u201d\u5979\u8bed\u6c14\u8f7b\u677e\u5730\u8bf4\uff0c\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4e48\u6211\u5c31\u987a\u4fbf\u9001\u4f60\u4e00\u7a0b\u5427\u3002\u201d</p><p>\u5979\u6325\u4e86\u6325\u624b\uff0c\u70c8\u7130\u81ea\u865a\u7a7a\u4e2d\u55b7\u6d8c\u51fa\u6765\uff0c\u51dd\u805a\u6210\u5de8\u5927\u7684\u706b\u9e1f\uff0c\u5c06\u73ca\u5609\u548c\u838e\u73de\u514b\u4e00\u53e3\u541e\u4e0b\uff0c\u7136\u540e\u957f\u9e23\u4e00\u58f0\uff0c\u7834\u7a97\u98de\u53bb\u3002\u9b54\u59ec\u4e5f\u968f\u4e4b\u6d88\u5931\uff0c\u8f6c\u773c\u4e4b\u95f4\uff0c\u623f\u95f4\u91cc\u53c8\u6062\u590d\u4e86\u5e73\u9759\uff0c\u53ea\u7559\u4e0b\u4e00\u5177\u5c38\u4f53\u8db4\u5728\u5730\u677f\u4e0a\uff0c\u9c9c\u8840\u6c69\u6c69\u6d41\u6dcc\uff0c\u4eff\u4f5b\u65e0\u4f11\u65e0\u6b62\u3002</p><p>\u203b\u203b\u203b</p><p>\u5728\u706b\u9e1f\u7684\u8179\u4e2d\uff0c\u73ca\u5609\u548c\u838e\u73de\u514b\u5feb\u901f\u7a7f\u8d8a\u5929\u9645\uff0c\u4e00\u8def\u4e0a\u9047\u5230\u4e0d\u5c11\u90aa\u9b54\u963b\u62e6\uff0c\u4f46\u5168\u90fd\u521a\u4e00\u9760\u8fd1\u4fbf\u88ab\u711a\u6210\u7070\u70ec\u3002\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u89c6\u91ce\u4e2d\u51fa\u73b0\u4e00\u7247\u5e7f\u88a4\u65e0\u9645\u7684\u51b0\u539f\uff0c\u7a7a\u65f7\u800c\u8352\u51c9\uff0c\u6ca1\u6709\u4efb\u4f55\u751f\u547d\u7684\u75d5\u8ff9\u3002\u706b\u9e1f\u7ee7\u7eed\u98de\u884c\uff0c\u76f4\u5230\u770b\u89c1\u4e86\u4e00\u5ea7\u7070\u8272\u57ce\u5821\uff0c\u5b83\u5f20\u5f00\u53cc\u7ffc\uff0c\u5982\u5229\u7bad\u822c\u4fef\u51b2\u800c\u4e0b\uff0c\u5728\u5373\u5c06\u649e\u4e0a\u5730\u9762\u7684\u65f6\u5019\u9661\u7136\u6d88\u6563\u65e0\u5f62\uff0c\u73ca\u5609\u548c\u838e\u73de\u514b\u8dcc\u843d\u4e0b\u6765\uff0c\u53d1\u73b0\u81ea\u5df1\u6b63\u7ad9\u5728\u57ce\u5821\u7684\u5927\u95e8\u524d\u3002</p><p>\u201c\u838e\u73de\u514b\uff0c\u4f60\u77e5\u9053\u8fd9\u662f\u54ea\u91cc\u5417\uff1f\u201d\u73ca\u5609\u95ee\u3002</p><p>\u838e\u73de\u514b\u5728\u5979\u8eab\u65c1\u3002\u9b45\u9b54\u5df2\u7ecf\u6062\u590d\u8fc7\u6765\uff0c\u5979\u6b64\u524d\u88ab\u624e\u745e\u5c14\u5236\u4f4f\uff0c\u867d\u7136\u4e0d\u80fd\u8a00\u8bed\u4e5f\u4e0d\u80fd\u884c\u52a8\uff0c\u4f46\u89c6\u89c9\u542c\u89c9\u5e76\u65e0\u95ee\u9898\uff0c\u6e05\u695a\u5730\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002\u201c\u597d\u50cf\u662f\u7b2c\u516b\u72f1\u2018\u5361\u5c3c\u4e9a\u2019\uff0c\u51b0\u96ea\u8352\u539f\u4e4b\u5730\u72f1\uff0c\u201d\u838e\u73de\u514b\u89c2\u5bdf\u4e86\u4e00\u4f1a\uff0c\u201c\u8fd9\u5ea7\u57ce\u5821\u5e94\u8be5\u5c31\u662f\u58a8\u83f2\u65af\u4e4b\u57ce\uff0c\u5361\u5c3c\u4e9a\u7684\u4e2d\u5fc3\u3002\u201d</p><p>\u201c\u7b2c\u516b\u72f1\uff1f\u201d\u73ca\u5609\u60f3\u4e86\u60f3\uff0c\u201c\u6211\u8bb0\u5f97\u743c\u6069\u4ed6\u4eec\u53bb\u7684\u5e94\u8be5\u662f\u7b2c\u4e03\u72f1\u5427\u3002\u201d</p><p>\u201c\u662f\u7684\uff0c\u7b2c\u4e03\u72f1\u3002\u201d\u838e\u73de\u514b\u8bf4\u3002</p><p>\u636e\u51ef\u5c14\u672c\u5f97\u5230\u7684\u60c5\u62a5\uff0c\u9f99\u5deb\u6559\u7684\u636e\u70b9\u6709\u4e09\u5904\uff0c\u5206\u522b\u662f\u5728\u7b2c\u4e8c\u72f1\u8fea\u65af\u3001\u7b2c\u516d\u72f1\u739b\u5c14\u535a\u5409\u4ee5\u53ca\u6700\u5173\u952e\u7684\u7b2c\u4e03\u72f1\u9a6c\u62c9\u591a\u7c73\u5c3c\u3002\u9634\u5f71\u9547\u4e5f\u5175\u5206\u4e09\u8def\uff0c\u540c\u65f6\u8fdb\u653b\u8fd9\u4e09\u4e2a\u5730\u65b9\u3002\u81f3\u4e8e\u7b2c\u516b\u72f1\u5361\u5c3c\u4e9a\uff0c\u5b83\u5e76\u4e0d\u662f\u9884\u8ba1\u4e4b\u4e2d\u7684\u6218\u573a\u3002</p><p>\u624e\u745e\u5c14\u628a\u5979\u4eec\u9001\u5230\u8fd9\u91cc\u6765\u505a\u4ec0\u4e48\uff1f\u96be\u9053\u743c\u6069\u548c\u6885\u83f2\u65af\u5728\u8fd9\u5ea7\u57ce\u5821\u91cc\uff1f</p><p>\u73ca\u5609\u5fc3\u4e2d\u7591\u60d1\uff0c\u62ac\u5934\u6253\u91cf\u7740\u773c\u524d\u7684\u57ce\u5821\u3002\u8fd9\u662f\u4e00\u5ea7\u98ce\u683c\u602a\u5f02\u7684\u5efa\u7b51\uff0c\u900f\u7740\u8ba9\u4eba\u65e0\u6cd5\u5f62\u5bb9\u7684\u5473\u9053\uff0c\u4eff\u4f5b\u662f\u7528\u5404\u79cd\u5404\u6837\u7684\u51e0\u4f55\u4f53\u5806\u780c\u5728\u4e00\u8d77\u800c\u6210\uff0c\u50cf\u662f\u67d0\u4e2a\u65e0\u804a\u827a\u672f\u5bb6\u5f04\u51fa\u6765\u7684\u62d9\u52a3\u4f5c\u54c1\u3002\u4ece\u655e\u5f00\u7684\u5927\u95e8\u671b\u8fdb\u53bb\uff0c\u57ce\u5821\u91cc\u7a7a\u8361\u8361\u7684\uff0c\u9759\u6084\u6084\u7684\uff0c\u6e3a\u65e0\u4eba\u8ff9\uff0c\u6742\u8349\u4e1b\u751f\uff0c\u4eff\u4f5b\u8352\u5e9f\u5df2\u4e45\u3002\u5979\u6c89\u541f\u4e86\u7247\u523b\uff0c\u6700\u540e\u51b3\u5b9a\u8fd8\u662f\u8fdb\u53bb\u770b\u770b\u518d\u8bf4\u3002</p><p>\u201c\u59d0\u59d0\u3002\u201d</p><p>\u838e\u73de\u514b\u53eb\u4e86\u4e00\u58f0\uff0c\u73ca\u5609\u56de\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u9b45\u9b54\u8138\u8272\u82cd\u767d\uff0c\u8eab\u4f53\u6291\u5236\u4e0d\u4f4f\u5730\u5fae\u5fae\u98a4\u6296\uff0c\u50cf\u662f\u627f\u53d7\u7740\u5de8\u5927\u7684\u538b\u529b\uff0c\u8fde\u52c9\u5f3a\u4fdd\u6301\u7ad9\u7acb\u90fd\u56f0\u96be\u3002\u5979\u6014\u4e86\u6014\uff0c\u968f\u5373\u660e\u767d\u8fc7\u6765\u3002\u4e5d\u5c42\u5730\u72f1\uff0c\u5c42\u5c42\u9012\u8fdb\uff0c\u7b2c\u516b\u72f1\u5361\u5c3c\u4e9a\u5df2\u7ecf\u63a5\u8fd1\u201c\u6838\u5fc3\u201d\uff0c\u4f4d\u9762\u6cd5\u5219\u7684\u529b\u91cf\u5df2\u7ecf\u8fbe\u5230\u6781\u81f4\uff0c\u838e\u73de\u514b\u662f\u6076\u9b54\uff0c\u5929\u751f\u9635\u8425\u5bf9\u7acb\uff0c\u5979\u5728\u8fd9\u91cc\u4f1a\u53d7\u5230\u5f3a\u70c8\u7684\u538b\u5236\uff0c\u73b0\u5728\u53ea\u6015\u865a\u5f31\u5f97\u8fde\u4e2a\u666e\u901a\u51e1\u4eba\u90fd\u4e0d\u5982\u3002</p><p>\u201c\u4f60\u5148\u4f11\u606f\u4f1a\u5427\u3002\u201d\u73ca\u5609\u8bf4\u7740\uff0c\u53d6\u51fa\u540a\u5760\u5c06\u838e\u73de\u514b\u6536\u5165\u5176\u4e2d\uff0c\u7136\u540e\u4ece\u300a\u547d\u8fd0\u957f\u591c\u300b\u4e2d\u53d6\u51fa\u201c\u7d2b\u8346\u201d\u7ec6\u5251\uff0c\u72ec\u81ea\u4e00\u4eba\u671d\u57ce\u5821\u4e2d\u8d70\u53bb\u3002</p><p>\u5927\u7ea6\u82b1\u4e86\u5c06\u8fd1\u534a\u4e2a\u5c0f\u65f6\uff0c\u73ca\u5609\u5c06\u57ce\u5821\u51e0\u4e4e\u6574\u4e2a\u63a2\u7d22\u4e86\u4e00\u904d\uff0c\u6ca1\u6709\u9047\u5230\u4efb\u4f55\u4eba\uff0c\u751a\u81f3\u6ca1\u6709\u53d1\u73b0\u6709\u4eba\u751f\u6d3b\u8fc7\u7684\u75d5\u8ff9\uff0c\u4f3c\u4e4e\u5c31\u662f\u4e2a\u8352\u5e9f\u53e4\u5821\u3002\u5979\u7a0d\u4e8b\u4f11\u606f\uff0c\u5c06\u76ee\u5149\u6295\u5411\u6700\u9ad8\u5904\u7684\u4e00\u5ea7\u9ad8\u53f0\uff0c\u90a3\u662f\u552f\u4e00\u6ca1\u6709\u53bb\u8fc7\u7684\u5730\u65b9\uff0c\u5982\u679c\u624e\u745e\u5c14\u5c06\u5979\u9001\u5230\u8fd9\u91cc\uff0c\u4e0d\u662f\u5355\u7eaf\u5730\u6076\u4f5c\u5267\uff0c\u800c\u662f\u771f\u6709\u4ec0\u4e48\u76ee\u7684\u7684\u8bdd\uff0c\u5e94\u8be5\u5c31\u662f\u5728\u90a3\u91cc\u4e86\u3002</p><p>\u5979\u6cbf\u7740\u53f0\u9636\uff0c\u62fe\u7ea7\u800c\u4e0a\uff0c\u8db3\u97f3\u5728\u7a7a\u65f7\u7684\u57ce\u5821\u4e2d\u56de\u54cd\uff0c\u542c\u5f97\u683c\u5916\u6e05\u695a\u3002\u8d70\u4e0a\u6700\u540e\u4e00\u7ea7\u53f0\u9636\uff0c\u773c\u524d\u51fa\u73b0\u4e86\u4e00\u4e2a\u5706\u5f62\u7684\u5e73\u53f0\uff0c\u5de8\u5927\u7684\u9752\u94dc\u9f0e\u5668\u77d7\u7acb\u5728\u4e2d\u592e\uff0c\u65e0\u6570\u7d2b\u8272\u5149\u70b9\u81ea\u9f0e\u4e2d\u5189\u5189\u5347\u8d77\uff0c\u5f25\u6563\u56db\u5468\uff0c\u5e7b\u5316\u6210\u4e5d\u9762\u5de8\u5927\u7684\u955c\u5b50\uff0c\u6574\u9f50\u6392\u5217\u5728\u94dc\u9f0e\u7684\u5468\u56f4\uff0c\u8868\u9762\u6cdb\u7740\u5fae\u5149\u3002\u73ca\u5609\u8d70\u5230\u4e00\u9762\u955c\u5b50\u524d\uff0c\u955c\u4e2d\u5374\u6ca1\u6709\u663e\u793a\u51fa\u81ea\u5df1\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55\u4e1c\u897f\uff0c\u53ea\u662f\u767d\u832b\u832b\u7684\u4e00\u7247\u3002</p><p>\u201c\u51a5\u5e9c\u672a\u5f00\uff0c\u8f6e\u56de\u5c01\u95ed\uff0c\u4f60\u5f53\u7136\u770b\u4e0d\u5230\u81ea\u5df1\u7684\u524d\u4e16\u3002\u201d\u4e00\u4e2a\u6e05\u51b7\u7684\u58f0\u97f3\u81ea\u5934\u9876\u4f20\u6765\u3002</p><p>\u73ca\u5609\u9000\u540e\u4e24\u6b65\uff0c\u62ac\u5934\u671b\u53bb\uff0c\u5979\u770b\u89c1\u4e00\u4e2a\u9ed1\u8272\u7684\u8eab\u5f71\u5750\u5728\u9f0e\u53e3\u5904\uff0c\u90a3\u662f\u4e00\u4f4d\u5a07\u5c0f\u73b2\u73d1\u7684\u5c11\u5973\uff0c\u9762\u5bb9\u7cbe\u81f4\u5f97\u4ee4\u4eba\u5ac9\u5992\uff0c\u91d1\u8272\u7684\u5f02\u86c7\u5728\u5979\u8eab\u4e0a\u76d8\u65cb\u73af\u7ed5\uff0c\u4e0d\u65f6\u541e\u5403\u6e38\u9038\u5728\u7a7a\u6c14\u4e2d\u7684\u7d2b\u8272\u5149\u70b9\u3002\u201c\u4f60\u662f\u8c01\uff1f\u201d\u73ca\u5609\u95ee\u3002</p><p>\u201c\u6211\u662f\u51ef\u745f\u7433\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u5f88\u4e45\u4e0d\u89c1\u4e86\uff0c\u5c0f\u96c5\u3002\u201d</p><p>\u201c\u5bf9\u4e0d\u8d77\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4f46\u6211\u4eec\u8ba4\u8bc6\u5417\uff1f\u6211\u6ca1\u4ec0\u4e48\u5370\u8c61\u3002\u201d</p><p>\u201c\u66fe\u7ecf\u8ba4\u8bc6\uff0c\u201d\u51ef\u745f\u7433\u8bf4\uff0c\u201c\u6211\u4eec\u66fe\u7ecf\u662f\u65e0\u8bdd\u4e0d\u8c08\u7684\u597d\u53cb\uff0c\u4f46\u540e\u6765\u53d1\u751f\u4e86\u4e00\u4e9b\u4e8b\u60c5\uff0c\u4f60\u9009\u62e9\u4e86\u9057\u5fd8\u3002\u201d</p><p>\u73ca\u5609\u6447\u4e86\u6447\u5934\uff0c\u201c\u6211\u4e0d\u77e5\u9053\u4f60\u5728\u8bf4\u4ec0\u4e48\u3002\u201d</p><p>\u201c\u8fd9\u65e0\u5173\u7d27\u8981\uff0c\u201d\u51ef\u745f\u7433\u8bf4\uff0c\u201c\u8fc7\u53bb\u7684\u4e8b\u60c5\u7ec8\u7a76\u8981\u4e86\u7ed3\uff0c\u4f46\u5e76\u4e0d\u4e00\u5b9a\u662f\u73b0\u5728\uff0c\u6211\u4eec\u6709\u7684\u662f\u65f6\u95f4\u3002\u90a3\u4e48\uff0c\u4f60\u6765\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u73ca\u5609\u72b9\u8c6b\u7740\uff0c\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u56de\u7b54\u3002\u4e8b\u5b9e\u4e0a\uff0c\u5979\u5230\u73b0\u5728\u8fd8\u6ca1\u641e\u6e05\u695a\u624e\u745e\u5c14\u628a\u5979\u9001\u5230\u8fd9\u91cc\uff0c\u7a76\u7adf\u662f\u4ec0\u4e48\u7528\u610f\u3002\u6b63\u5728\u6b64\u65f6\uff0c\u4e00\u4e2a\u58f0\u97f3\u5728\u5979\u7684\u8111\u6d77\u4e2d\u7a81\u7136\u54cd\u8d77\uff0c\u90a3\u662f\u6b64\u524d\u4e00\u76f4\u5931\u53bb\u8054\u7cfb\u7684\u5965\u5609\u83b1\u65af\uff0c\u201c\u6740\u6389\u5979\uff0c\u201d\u5927\u5965\u672f\u5e08\u8bf4\uff0c\u201c\u6293\u7d27\u65f6\u95f4\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d\u73ca\u5609\u6115\u7136\u3002\u8fd8\u6ca1\u7b49\u5979\u518d\u95ee\uff0c\u4e00\u56e2\u94f6\u5149\u4ece\u5979\u8eab\u540e\u9634\u5f71\u4e2d\u7535\u5c04\u800c\u51fa\uff0c\u671d\u7740\u9f0e\u4e0a\u7684\u51ef\u745f\u7433\u98de\u6251\u8fc7\u53bb\u3002</p><p>\u94ff\uff01</p><p>\u773c\u770b\u5c31\u8981\u88ab\u51fb\u4e2d\uff0c\u51ef\u745f\u7433\u7684\u8eab\u4f53\u5e45\u5ea6\u5f88\u5c0f\u5730\u6643\u4e86\u4e00\u6643\uff0c\u94f6\u5149\u4fbf\u4ee5\u66f4\u5feb\u7684\u901f\u5ea6\u53cd\u5f39\u56de\u6765\uff0c\u8dcc\u843d\u5728\u5730\u4e0a\uff0c\u53d1\u51fa\u54cd\u4eae\u7684\u649e\u51fb\u58f0\uff0c\u9732\u51fa\u8eab\u5f62\u3002\u90a3\u662f\u4e00\u4e2a\u94f6\u5149\u95ea\u95ea\u7684\u91d1\u5c5e\u4eba\u5076\uff0c\u8f6e\u5ed3\u7cbe\u81f4\uff0c\u795e\u60c5\u751f\u52a8\uff0c\u7b80\u76f4\u4e0e\u771f\u4eba\u65e0\u5f02\uff0c\u770b\u88c5\u626e\u4eff\u4f5b\u4e00\u4f4d\u82e6\u4fee\u8005\uff0c\u8eab\u6750\u7c97\u58ee\uff0c\u808c\u8089\u866c\u7ed3\uff0c\u53cc\u76ee\u5706\u7741\uff0c\u867d\u7136\u8d64\u624b\u7a7a\u62f3\uff0c\u5374\u662f\u900f\u7740\u4e00\u80a1\u51db\u51bd\u6740\u6c14\u3002\u51ef\u745f\u7433\u770b\u7740\u5b83\uff0c\u7709\u5fc3\u5fae\u5fae\u76b1\u8d77\uff0c\u201c\u79d8\u5076\uff1f\u201d</p><p>\u201c\u6b66\u50e7\u9686\u5947\uff01\u201d\u79d8\u5076\u5f00\u53e3\u8bf4\uff0c\u58f0\u97f3\u6709\u4e9b\u6c89\u95f7\uff0c\u5e26\u7740\u91d1\u5c5e\u7684\u5c16\u9510\u3002\u4ed6\u53cc\u819d\u5f2f\u66f2\uff0c\u4e0b\u8e72\uff0c\u7136\u540e\u731b\u7136\u8dc3\u8d77\uff0c\u4eff\u4f5b\u70ae\u5f39\u822c\u5f39\u5c04\u8d77\u6765\uff0c\u518d\u5ea6\u5411\u51ef\u745f\u7433\u51b2\u53bb\u3002</p><p>\u51ef\u745f\u7433\u4f38\u51fa\u5de6\u624b\uff0c\u8f7b\u63cf\u6de1\u5199\u5730\u865a\u63e1\u4e86\u4e00\u4e0b\uff0c\u9ad8\u901f\u51b2\u6765\u7684\u79d8\u5076\u987f\u65f6\u88ab\u65e0\u5f62\u7684\u529b\u91cf\u5b9a\u4f4f\u4e86\uff0c\u60ac\u6d6e\u5728\u534a\u7a7a\u4e2d\uff0c\u4e00\u52a8\u4e5f\u4e0d\u52a8\u3002\u4f46\u4e24\u79d2\u949f\u540e\uff0c\u5b83\u53c8\u5f00\u59cb\u731b\u70c8\u6323\u624e\u8d77\u6765\uff0c\u51ef\u745f\u7433\u968f\u624b\u4e00\u6254\uff0c\u5c06\u5b83\u63b7\u56de\u5730\u4e0a\u3002\u201c\u4ec0\u4e48\u4eba\uff1f\u51fa\u6765\uff01\u201d\u5979\u51b7\u51b7\u5730\u8bf4\u3002</p><p>\u7d2b\u8272\u7684\u900f\u660e\u96fe\u6c14\u4ece\u73ca\u5609\u7684\u624b\u4e2d\u2014\u2014\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u73ca\u5609\u62ff\u5728\u624b\u4e0a\u7684\u90a3\u672c\u300a\u547d\u8fd0\u957f\u591c\u300b\u4e2d\u2014\u2014\u6162\u6162\u6d8c\u51fa\uff0c\u51dd\u805a\u6210\u4e00\u4e2a\u7f8e\u4e3d\u7684\u5973\u5b50\uff0c\u5979\u8eab\u6750\u4e0d\u9ad8\uff0c\u7626\u524a\u800c\u7ea4\u7ec6\uff0c\u4e00\u5934\u94f6\u53d1\u62ab\u80a9\uff0c\u53cc\u773c\u4eff\u4f5b\u7eff\u5b9d\u77f3\u822c\u95ea\u95ea\u53d1\u4eae\u3002\u73ca\u5609\u975e\u5e38\u60ca\u8bb6\uff0c\u5979\u8ba4\u8bc6\u8fd9\u4e2a\u5973\u4eba\uff0c\u662f\u51ef\u5c14\u672c\u7684\u59bb\u5b50\uff0c\u201c\u4e03\u59d0\u59b9\u201d\u4e4b\u4e00\uff0c\u201c\u83b1\u62c9\u5973\u58eb\uff0c\u4f60\u600e\u4e48\u4f1a\u2026\u2026\u201d</p><p>\u201c\u83b1\u62c9\u201d\u77a5\u4e86\u5979\u4e00\u773c\uff0c\u719f\u6089\u7684\u795e\u6001\u8ba9\u73ca\u5609\u7acb\u523b\u660e\u767d\u8fc7\u6765\uff0c\u201c\u8001\u5e08\uff1f\u201d</p><p>\u51ef\u745f\u7433\u5c45\u9ad8\u4e34\u4e0b\u5730\u51dd\u89c6\u7740\u8fd9\u4e2a\u7a81\u7136\u51fa\u73b0\u7684\u5973\u4eba\uff0c\u201c\u4f60\u662f\u8c01\uff1f\u201d</p><p>\u5973\u4eba\u8eac\u8eab\u884c\u793c\uff0c\u201c\u8389\u8389\u4e1d\xb7\u5965\u5609\u83b1\u65af\uff0c\u5411\u2018\u51a5\u51e4\u2019\u516c\u4e3b\u6bbf\u4e0b\u81f4\u610f\u3002\u201d</p><p>\u201c\u4f60\u80fd\u63a7\u5236\u79d8\u5076\uff0c\u201d\u51ef\u745f\u7433\u4f3c\u4e4e\u6709\u4e9b\u56f0\u60d1\uff0c\u201c\u4f46\u6211\u4e0d\u8bb0\u5f97\u4f60\uff0c\u4e5f\u6ca1\u6709\u770b\u89c1\u4efb\u4f55\u5370\u8bb0\u3002\u201d</p><p>\u201c\u5f53\u7136\uff0c\u56e0\u4e3a\u6211\u4eec\u7d20\u672a\u8c0b\u9762\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u5979\u501f\u7528\u83b1\u62c9\u7684\u8eab\u4f53\uff0c\u55d3\u97f3\u4e5f\u968f\u4e4b\u53d1\u751f\u6539\u53d8\uff0c\u53d8\u5f97\u6e05\u8106\u800c\u52a8\u542c\uff0c\u4e0e\u5e73\u5e38\u7684\u4f4e\u6c89\u5927\u4e0d\u76f8\u540c\uff0c\u201c\u800c\u4e14\u8981\u8bf4\u660e\u7684\u662f\uff1a\u6211\u5e76\u975e\u5947\u68b0\u5e08\uff0c\u66f4\u4e0d\u662f\u2018\u7687\u5ba4\u2019\u3002\u6211\u662f\u8010\u745f\u745e\u5c14\u7684\u5965\u672f\u5e08\uff0c\u53ea\u4e0d\u8fc7\u673a\u7f18\u5de7\u5408\uff0c\u5b66\u8fc7\u4e00\u4e9b\u5947\u68b0\u672f\u800c\u5df2\u3002\u4e0d\u8fc7\u5982\u679c\u5957\u7528\u8d35\u56fd\u7684\u6807\u51c6\uff0c\u6211\u52c9\u5f3a\u4e5f\u53ef\u4ee5\u7b97\u662f\u4e00\u540d\u2018\u5b66\u8005\u2019\u5427\u3002\u201d</p><p>\u201c\u5b66\u8005\u53ef\u6ca1\u529e\u6cd5\u63a7\u5236\u79d8\u5076\u3002\u201d</p><p>\u201c\u6bbf\u4e0b\uff0c\u8d35\u56fd\u4e3a\u795e\u738b\u6240\u8986\u706d\uff0c\u8ddd\u4eca\u5df2\u6709\u7ea6\u56db\u5343\u5e74\u4e86\u3002\u65f6\u5149\u6d41\u8f6c\uff0c\u4e16\u4e8b\u53d8\u8fc1\uff0c\u6709\u4e9b\u65e7\u89c2\u5ff5\u3001\u65e7\u601d\u7ef4\uff0c\u5df2\u7ecf\u672a\u5fc5\u9002\u7528\u4e86\uff0c\u201d\u5965\u5609\u83b1\u65af\u5fae\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u8010\u745f\u745e\u5c14\u4e00\u7cfb\uff0c\u867d\u7136\u540e\u8d77\uff0c\u5374\u4e5f\u6709\u4e9b\u72ec\u5f97\u4e4b\u79d8\uff0c\u4e0d\u6562\u5984\u81ea\u83f2\u8584\u3002\u6bbf\u4e0b\u82e5\u6709\u5174\u8da3\uff0c\u4e0d\u59a8\u6307\u70b9\u4e00\u4e8c\u3002\u201d</p><p>\u51ef\u745f\u7433\u51b7\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u4f60\u662f\u8c01\uff1f\u201d\u5979\u53c8\u95ee\u4e86\u4e00\u904d\u3002</p><p>\u5965\u5609\u83b1\u65af\u62cd\u4e86\u62cd\u73ca\u5609\u7684\u5934\uff0c\u201c\u6211\u662f\u5979\u6bcd\u4eb2\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u51ef\u745f\u7433\u8fd8\u6ca1\u53cd\u5e94\uff0c\u73ca\u5609\u5148\u5413\u4e86\u4e00\u8df3\uff0c\u201c\u8001\u5e08\uff0c\u4f60\u521a\u624d\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u662f\u4f60\u6bcd\u4eb2\uff0c\u8fd9\u6709\u4ec0\u4e48\u597d\u60ca\u8bb6\u7684\uff0c\u4f60\u5e94\u8be5\u65e9\u5c31\u731c\u5230\u4e86\u554a\uff0c\u201d\u5965\u5609\u83b1\u65af\u601c\u7231\u5730\u629a\u6478\u7740\u5979\u7684\u5934\u53d1\uff0c\u201c\u6211\u7684\u5973\u513f\u4e0d\u4f1a\u90a3\u4e48\u8fdf\u949d\u5427\u3002\u201d</p><p>\u201c\u53ef\u662f\u2026\u2026\u201d</p><p>\u4ee5\u73ca\u5609\u7684\u806a\u660e\uff0c\u81ea\u7136\u65e9\u5c31\u611f\u89c9\u5230\u5965\u5609\u83b1\u65af\u5bf9\u81ea\u5df1\u7684\u6001\u5ea6\u201c\u4e0d\u6b63\u5e38\u201d\uff0c\u8fdc\u8fdc\u8d85\u51fa\u5bfb\u5e38\u201c\u5e08\u5f92\u201d\u7684\u8303\u7574\u3002\u4e24\u4eba\u8fd9\u6bb5\u65f6\u95f4\u4ee5\u6765\u671d\u5915\u76f8\u5904\uff0c\u73ca\u5609\u53c8\u662f\u5fc3\u601d\u7ec6\u817b\u7684\u4eba\uff0c\u6709\u4e9b\u4e8b\u60c5\uff0c\u867d\u7136\u5634\u4e0a\u4e0d\u8bf4\uff0c\u5fc3\u4e2d\u96be\u514d\u4f1a\u6709\u6240\u731c\u6d4b\u3002\u4f46\u731c\u6d4b\u5f52\u731c\u6d4b\uff0c\u9aa4\u7136\u542c\u5965\u5609\u83b1\u65af\u8bf4\u51fa\u6765\uff0c\u8fd8\u662f\u5413\u4e86\u4e00\u8df3\u3002\u6216\u8005\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u5982\u679c\u81ea\u5df1\u662f\u5965\u5609\u83b1\u65af\u7684\u5973\u513f\uff0c\u90a3\u5c31\u610f\u5473\u7740\u81ea\u5df1\u4e0d\u4ec5\u4ec5\u662f\u73ca\u5609\uff0c\u540c\u65f6\u8fd8\u662f\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u6709\u53e6\u5916\u4e00\u4e2a\u8eab\u4efd\uff0c\u6709\u53e6\u5916\u4e00\u6bb5\u4eba\u751f\u7ecf\u5386\uff0c\u800c\u8fd9\u4e00\u5207\uff0c\u5979\u8fd8\u6ca1\u505a\u597d\u9762\u5bf9\u7684\u5fc3\u7406\u51c6\u5907\u3002</p><p>\u51ef\u745f\u7433\u6447\u4e86\u6447\u5934\uff0c\u201c\u4f60\u4e0d\u662f\u5c0f\u96c5\u7684\u6bcd\u4eb2\u3002\u201d</p><p>\u201c\u6211\u5f53\u7136\u4e0d\u662f\u5c0f\u96c5\u7684\u6bcd\u4eb2\uff0c\u6211\u4e5f\u4e0d\u8ba4\u8bc6\u4f60\u8bf4\u7684\u90a3\u4f4d\u2018\u5c0f\u96c5\u2019\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u6211\u662f\u5979\u7684\u6bcd\u4eb2\u3002\u201d</p><p>\u51ef\u745f\u7433\u601d\u7d22\u4e86\u7247\u523b\uff0c\u201c\u4f60\u610f\u6b32\u4f55\u4e3a\uff1f\u201d</p><p>\u201c\u4e3a\u4eba\u7236\u6bcd\uff0c\u6240\u601d\u6240\u6c42\uff0c\u65e0\u975e\u90fd\u662f\u4e3a\u4e86\u5b50\u5973\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u6211\u8fd9\u4e2a\u5973\u513f\u5462\uff0c\u4ece\u5c0f\u5c31\u4e0d\u8ba9\u6211\u7701\u5fc3\uff0c\u5fc3\u9ad8\u6c14\u50b2\uff0c\u504f\u504f\u773c\u5149\u53c8\u4e0d\u884c\uff0c\u603b\u662f\u9047\u4eba\u4e0d\u6dd1\uff0c\u8fd8\u60f9\u4e86\u4e00\u5806\u9ebb\u70e6\u3002\u6211\u8fd9\u4e2a\u505a\u6bcd\u4eb2\u7684\uff0c\u603b\u4e0d\u80fd\u8896\u624b\u65c1\u89c2\uff0c\u53ea\u597d\u5e2e\u628a\u624b\u4e86\u3002\u201d</p><p>\u5979\u4e00\u8fb9\u8bf4\u8bdd\uff0c\u4e00\u8fb9\u5c55\u5f00\u300a\u547d\u8fd0\u957f\u591c\u300b\uff0c\u65e0\u6570\u9053\u7eda\u4e3d\u7684\u5149\u8292\u4ece\u4e66\u9875\u4e2d\u98de\u51fa\uff0c\u5982\u70df\u82b1\u822c\u5728\u7a7a\u4e2d\u7efd\u653e\u5f00\u6765\uff0c\u53d8\u6210\u4e00\u4e2a\u53c8\u4e00\u4e2a\u7cbe\u5de7\u7e41\u590d\u7684\u5c0f\u578b\u9b54\u6cd5\u9635\uff0c\u6570\u91cf\u5927\u7ea6\u6709\u4e03\u516b\u5341\u4e2a\u3002\u800c\u8fd9\u4e9b\u5c0f\u578b\u6cd5\u9635\u5f7c\u6b64\u52fe\u8fde\u5d4c\u7ed3\uff0c\u4ea4\u9519\u7ec4\u5408\uff0c\u8f6c\u773c\u95f4\u4fbf\u6784\u5efa\u51fa\u4e00\u4e2a\u5e9e\u5927\u65e0\u6bd4\u7684\u7403\u5f62\u9b54\u6cd5\u9635\uff0c\u5c06\u6574\u4e2a\u89c2\u661f\u53f0\u7b3c\u7f69\u5176\u4e2d\u3002\u8fd9\u4e00\u5207\u6240\u82b1\u7684\u65f6\u95f4\u8fd8\u4e0d\u8d85\u8fc7\u5341\u79d2\u949f\uff0c\u5965\u5609\u83b1\u65af\u7684\u8bdd\u97f3\u521a\u843d\uff0c\u9b54\u6cd5\u9635\u4fbf\u5df2\u7ecf\u51dd\u805a\u6210\u578b\u3002\u5018\u82e5\u662f\u743c\u6069\uff0c\u6216\u8005\u4efb\u4f55\u4e00\u4f4d\u5deb\u5e08\u5728\u573a\uff0c\u770b\u5230\u8fd9\u5e45\u573a\u666f\uff0c\u90fd\u4f1a\u60ca\u8bb6\u5f97\u76ee\u77aa\u53e3\u5446\u3002\u56e0\u4e3a\u5965\u5609\u83b1\u65af\u6240\u5c55\u793a\u7684\uff0c\u6b63\u662f\u4f20\u8bf4\u4e2d\u7684\u201c\u7ec4\u88c5\u6cd5\u9635\u201d\u7684\u6280\u5de7\u3002</p><p>\u6240\u8c13\u9b54\u6cd5\u9635\uff0c\u4ece\u672c\u8d28\u4e0a\u8bf4\uff0c\u5176\u5b9e\u5c31\u662f\u5c06\u591a\u4e2a\u5355\u4e00\u9b54\u6cd5\u4ee5\u7279\u5b9a\u5f62\u5f0f\u7ec4\u5408\u8d77\u6765\uff0c\u501f\u6b64\u5b9e\u73b0\u66f4\u5f3a\u5927\u7684\u6548\u679c\u3002\u5f53\u4eca\u4e16\u4e0a\uff0c\u4efb\u4f55\u5deb\u5e08\u8981\u6784\u5efa\u9b54\u6cd5\u9635\uff0c\u90fd\u662f\u4e00\u6b65\u6b65\u5730\u4ece\u5934\u505a\u8d77\uff0c\u9010\u6b21\u5b8c\u6210\u3002\u8d8a\u5e9e\u5927\u590d\u6742\u7684\u9b54\u6cd5\u9635\uff0c\u6b65\u9aa4\u8d8a\u7e41\u591a\uff0c\u6240\u9700\u8981\u7684\u65f6\u95f4\u5c31\u8d8a\u4e45\u3002\u65e9\u5728\u8010\u745f\u65f6\u4ee3\u5c31\u6709\u4eba\u63d0\u51fa\u8bbe\u60f3\uff0c\u5c06\u4e00\u4e2a\u5927\u578b\u9b54\u6cd5\u9635\u62c6\u5206\u6210\u591a\u4e2a\u5c0f\u578b\u201c\u90e8\u4ef6\u201d\uff0c\u5deb\u5e08\u5148\u5c06\u5404\u4e2a\u90e8\u4ef6\u51c6\u5907\u597d\uff0c\u5728\u9700\u8981\u65f6\u518d\u7ec4\u88c5\u8d77\u6765\uff0c\u4ece\u800c\u6781\u5927\u5730\u63d0\u9ad8\u6548\u7387\u3002\u6839\u636e\u8d44\u6599\u8bb0\u8f7d\uff0c\u5f53\u65f6\u6709\u4e03\u516b\u540d\u5927\u5965\u672f\u5e08\u90fd\u6210\u7acb\u4e86\u7814\u53d1\u56e2\u961f\uff0c\u8bd5\u56fe\u653b\u514b\u8fd9\u4e2a\u6280\u672f\u96be\u9898\uff0c\u4e5f\u786e\u5b9e\u53d6\u5f97\u4e86\u7a81\u7834\u6027\u7684\u7684\u8fdb\u5c55\uff0c\u4f46\u5374\u5728\u5373\u5c06\u6210\u529f\u7684\u5173\u5934\uff0c\u6240\u6709\u7814\u7a76\u88ab\u5f3a\u5236\u505c\u6b62\uff0c\u8d44\u6599\u88ab\u9500\u6bc1\uff0c\u81f3\u4e8e\u539f\u56e0\u662f\u4ec0\u4e48\uff0c\u540e\u4eba\u5c31\u4e0d\u5f97\u800c\u77e5\u4e86\u3002</p><p>\u5965\u5609\u83b1\u65af\u663e\u7136\u662f\u7b79\u5212\u5df2\u4e45\uff0c\u51c6\u5907\u5145\u5206\uff0c\u8f6c\u77ac\u4e4b\u95f4\u6784\u5efa\u8d77\u9b54\u6cd5\u9635\uff0c\u540c\u65f6\u53c8\u81ea\u4e66\u4e2d\u53d6\u51fa\u65e0\u6570\u73cd\u7a00\u7f55\u89c1\u7684\u65bd\u6cd5\u6750\u6599\u3001\u88c5\u5907\u9053\u5177\uff0c\u6295\u5165\u6cd5\u9635\u7684\u5404\u4e2a\u5173\u952e\u8282\u70b9\u4e4b\u4e2d\uff0c\u88ab\u5feb\u901f\u878d\u5316\u3001\u5206\u89e3\u3001\u5438\u6536\u3002\u9b54\u6cd5\u9635\u8d8a\u53d1\u5e9e\u5927\uff0c\u5df2\u7ecf\u770b\u4e0d\u5230\u8fb9\u9645\uff0c\u4f46\u4efb\u4f55\u8eab\u5728\u5176\u4e2d\u7684\u4eba\u90fd\u80fd\u6e05\u695a\u5730\u611f\u89c9\u5230\u5b83\u7684\u5b58\u5728\uff0c\u611f\u89c9\u5230\u90a3\u79cd\u78c5\u7934\u7684\u80fd\u91cf\u6d8c\u52a8\u3002</p><p>\u201c\u8001\u5e08\uff0c\u8fd9\u2026\u2026\u662f\u4ec0\u4e48\uff1f\u201d\u73ca\u5609\u95ee\u3002</p><p>\u201c\u5316\u8eab\uff0c\u201d\u5965\u5609\u83b1\u65af\u62ac\u5934\u770b\u7740\u51ef\u745f\u7433\uff0c\u201c\u516c\u4e3b\u6bbf\u4e0b\u5bf9\u5b83\u60f3\u5fc5\u4e0d\u7b97\u964c\u751f\u5427\u3002\u201d</p><p>\u201c\u89c1\u8fc7\uff0c\u4f46\u4f3c\u4e4e\u6709\u6240\u4e0d\u540c\u3002\u201d\u51ef\u745f\u7433\u8bf4\u3002</p><p>\u201c\u867d\u7136\u672c\u51fa\u540c\u6e90\uff0c\u4f46\u5404\u4eba\u6709\u5404\u4eba\u7684\u6539\u9020\uff0c\u7ec6\u8282\u4e0a\u6709\u6240\u6b67\u5f02\u5f88\u6b63\u5e38\uff0c\u4e0d\u8fc7\u57fa\u672c\u529f\u80fd\u5e94\u8be5\u8fd8\u662f\u5dee\u4e0d\u591a\u7684\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u90a3\u4e48\u6bbf\u4e0b\u60f3\u5fc5\u5df2\u7ecf\u6e05\u695a\u6211\u7684\u6765\u610f\u4e86\u3002\u5018\u82e5\u6bbf\u4e0b\u80fd\u591f\u987e\u5ff5\u5f80\u65e5\u60c5\u8c0a\uff0c\u51fa\u624b\u76f8\u52a9\uff0c\u90a3\u4e48\u6211\u5c06\u4e0d\u80dc\u611f\u6fc0\u3002\u201d</p><p>\u51ef\u745f\u7433\u6c89\u9ed8\u7247\u523b\uff0c\u6447\u4e86\u6447\u5934\u3002\u201c\u5c0f\u96c5\u662f\u6211\u7684\u670b\u53cb\uff0c\u539f\u672c\u6211\u7406\u5f53\u76f8\u52a9\u3002\u4f46\u6211\u5df2\u7ecf\u5e94\u5141\u4ed6\u4eba\u5728\u5148\uff0c\u4e0d\u80fd\u80cc\u8bfa\uff0c\u6240\u4ee5\u53ea\u80fd\u8bf4\u62b1\u6b49\u4e86\u3002\u201d</p><p>\u201c\u770b\u6765\u4ea4\u6d89\u5931\u8d25\uff0c\u201d\u5965\u5609\u83b1\u65af\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u90a3\u4e48\u53ea\u597d\u5192\u72af\u4e86\uff0c\u6bbf\u4e0b\u3002\u201d</p><p>\u5979\u7ffb\u5f00\u300a\u547d\u8fd0\u957f\u591c\u300b\uff0c\u6495\u4e0b\u5176\u4e2d\u4e00\u9875\uff0c\u63b7\u5f80\u7a7a\u4e2d\u3002\u4e66\u9875\u81ea\u884c\u71c3\u70e7\u8d77\u6765\uff0c\u53d1\u51fa\u96f7\u9e23\u822c\u7684\u8f70\u9686\u58f0\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u65e0\u6570\u70b9\u661f\u5149\u81ea\u5929\u7a79\u5760\u843d\uff0c\u5316\u4f5c\u5343\u4e07\u652f\u5200\u67aa\u5251\u621f\uff0c\u65e0\u6570\u56e2\u70c8\u7130\u81ea\u5730\u9762\u817e\u8d77\uff0c\u5316\u4f5c\u5343\u4e07\u5934\u72f0\u72de\u731b\u517d\uff0c\u65e0\u6570\u9053\u98d3\u98ce\u81ea\u865a\u7a7a\u4e2d\u6d8c\u51fa\uff0c\u5316\u4f5c\u5343\u4e07\u4e2a\u91d1\u7532\u58eb\u5175\u3002\u968f\u7740\u4e00\u58f0\u957f\u53f7\uff0c\u58eb\u5175\u9f50\u58f0\u6012\u543c\uff0c\u8de8\u4e0a\u731b\u517d\uff0c\u624b\u6301\u5251\u621f\uff0c\u5982\u6f6e\u6c34\u822c\u671d\u7740\u51ef\u745f\u7433\u6d8c\u53bb\u3002</p><p>\u203b\u203b\u203b</p><p>\u8fd9\u662f\u73ca\u5609\u8ddf\u968f\u5965\u5609\u83b1\u65af\u5b66\u827a\u4ee5\u6765\uff0c\u6240\u7ecf\u5386\u7684\u7b2c\u4e00\u573a\u771f\u6b63\u7684\u6218\u6597\uff0c\u5728\u6b64\u4e4b\u524d\u7684\u90a3\u4e9b\u90fd\u53ea\u80fd\u7b97\u662f\u7ec3\u4e60\u3002\u5979\u4e0b\u610f\u8bc6\u5730\u5fc3\u60c5\u7d27\u5f20\uff0c\u624b\u5fc3\u51fa\u6c57\uff0c\u7136\u800c\u66f4\u591a\u7684\u662f\u56f0\u60d1\uff0c\u56e0\u4e3a\u5979\u5b8c\u5168\u4e0d\u77e5\u9053\u5965\u5609\u83b1\u65af\u5230\u5e95\u60f3\u505a\u4ec0\u4e48\uff0c\u4e5f\u5b8c\u5168\u4e0d\u660e\u767d\u8fd9\u573a\u6218\u6597\u7684\u76ee\u7684\u662f\u4ec0\u4e48\u3002</p><p>\u201c\u8001\u5e08\uff0c\u6211\u2026\u2026\u201d</p><p>\u5965\u5609\u83b1\u65af\u6ca1\u6709\u5411\u5979\u89e3\u91ca\uff0c\u56e0\u4e3a\u5979\u5b8c\u5168\u6ca1\u6709\u7a7a\u6687\u3002\u501f\u52a9\u83b1\u62c9\u7684\u8eab\u4f53\uff0c\u5979\u91cd\u65b0\u53c8\u6062\u590d\u4e86\u65bd\u6cd5\u80fd\u529b\uff0c\u4e0d\u518d\u5411\u4ee5\u524d\u90a3\u6837\u5fc5\u987b\u4f9d\u8d56\u300a\u547d\u8fd0\u957f\u591c\u300b\u2014\u2014\u867d\u7136\u8fd9\u53ea\u662f\u6682\u65f6\u7684\u3002\u5404\u79cd\u5f3a\u5927\u5230\u4e0d\u53ef\u601d\u8bae\u7684\u6cd5\u672f\u88ab\u5979\u91ca\u653e\u51fa\u6765\uff0c\u7f16\u7ec7\u6210\u5371\u9669\u7684\u7262\u7b3c\uff0c\u60f3\u8981\u5c06\u51ef\u745f\u7433\u56f4\u56f0\u5176\u4e2d\uff0c\u5c06\u5979\u7ede\u6740\u3002\u4f46\u8fd9\u5e76\u4e0d\u5bb9\u6613\uff0c\u51ef\u745f\u7433\u6ca1\u6709\u4f7f\u7528\u4efb\u4f55\u9b54\u6cd5\uff0c\u5979\u53ea\u662f\u7b80\u5355\u5730\u53ec\u5524\u51fa\u4e00\u67c4\u8840\u8272\u5de8\u9570\uff0c\u5e73\u5e73\u5730\u6a2a\u626b\u51fa\u53bb\uff0c\u4e00\u5207\u6562\u4e8e\u9760\u8fd1\u4e4b\u7269\uff0c\u65e0\u8bba\u662f\u58eb\u5175\u3001\u731b\u517d\u3001\u5200\u5251\u6216\u8005\u9b54\u6cd5\uff0c\u90fd\u5728\u4e00\u51fb\u4e4b\u4e0b\u7070\u98de\u70df\u706d\uff0c\u8fd4\u5f52\u65e0\u5f62\uff0c\u5bf9\u5979\u9020\u4e0d\u6210\u534a\u70b9\u4f24\u5bb3\u3002</p><p>\u4f46\u51ef\u745f\u7433\u5e76\u6ca1\u6709\u53cd\u51fb\u3002</p><p>\u65e0\u8bba\u5965\u5609\u83b1\u65af\u5982\u4f55\u653b\u51fb\uff0c\u51ef\u745f\u7433\u7684\u5e94\u5bf9\u59cb\u7ec8\u90fd\u662f\u6325\u52a8\u5de8\u9570\uff0c\u5c06\u4e00\u5207\u5316\u4f5c\u865a\u65e0\u3002\u5979\u9762\u65e0\u8868\u60c5\uff0c\u4e5f\u4e0d\u8bf4\u8bdd\uff0c\u4eff\u4f5b\u5bf9\u9762\u524d\u7684\u4e00\u5207\u90fd\u6f20\u4e0d\u5173\u5fc3\u3002\u73ca\u5609\u611f\u89c9\u81ea\u5df1\u624b\u8db3\u65e0\u63aa\uff0c\u5979\u5224\u65ad\u4e0d\u51fa\u5f62\u52bf\u4f18\u52a3\uff0c\u4e5f\u4e0d\u77e5\u9053\u81ea\u5df1\u8be5\u505a\u4ec0\u4e48\uff0c\u80fd\u505a\u4ec0\u4e48\u3002\u6b63\u5728\u6b64\u65f6\uff0c\u8033\u8fb9\u5ffd\u7136\u4f20\u6765\u4e00\u58f0\u8f7b\u7b11\uff0c\u201c\u8fd9\u4e48\u5feb\u5c31\u6253\u8d77\u6765\u4e86\u5417\uff1f\u201d</p><p>\u73ca\u5609\u8f6c\u8fc7\u5934\uff0c\u7136\u540e\u5979\u770b\u89c1\u4e86\u624e\u745e\u5c14\u3002</p><p>\u9b54\u59ec\u80cc\u9760\u7740\u4e00\u6839\u77f3\u67f1\uff0c\u53cc\u624b\u73af\u62b1\u5728\u80f8\u524d\uff0c\u770b\u7740\u9762\u524d\u7684\u6fc0\u6218\uff0c\u8138\u4e0a\u7b11\u610f\u76c8\u76c8\u2014\u2014\u4e0d\u77e5\u662f\u5426\u73ca\u5609\u7684\u9519\u89c9\uff0c\u5979\u603b\u89c9\u5f97\u9b54\u59ec\u7684\u7b11\u5bb9\u4e4b\u4e2d\uff0c\u5e26\u7740\u51e0\u5206\u552f\u6050\u5929\u4e0b\u4e0d\u4e71\u7684\u610f\u5473\u3002\u201c\u4e0b\u5348\u597d\uff0c\u5c0f\u96c5\uff0c\u8fd9\u4e48\u5feb\u53c8\u89c1\u9762\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u4f60\u7684\u8001\u5e08\uff0c\u54e6\uff0c\u4e0d\uff0c\u4f60\u7684\u6bcd\u4eb2\u51c6\u5907\u5f97\u5f88\u5145\u5206\u554a\uff0c\u201d\u624e\u745e\u5c14\u8bc4\u4ef7\u7740\uff0c\u201c\u59d0\u59d0\u8fd9\u4e0b\u5b50\u6709\u70b9\u9ebb\u70e6\u4e86\u3002\u201d</p><p>\u73ca\u5609\u731c\u6d4b\u624e\u745e\u5c14\u53e3\u4e2d\u7684\u201c\u59d0\u59d0\u201d\u5e94\u8be5\u5c31\u662f\u90a3\u4f4d\u9ed1\u8863\u5c11\u5973\u51ef\u745f\u7433\u2014\u2014\u663e\u7136\u9664\u6b64\u4e4b\u5916\u4e5f\u6ca1\u6709\u5176\u4ed6\u4eba\u4e86\uff0c\u4f46\u8fd9\u8ba9\u5979\u66f4\u52a0\u8ff7\u60d1\uff0c\u4e0d\u77e5\u9053\u8fd9\u4f4d\u9b54\u59ec\u7a76\u7adf\u662f\u7ad9\u5728\u4ec0\u4e48\u7acb\u573a\u3002\u201c\u4f60\u8ba4\u4e3a\u6211\u8001\u5e08\u4f1a\u83b7\u80dc\uff1f\u201d\u5979\u8bd5\u63a2\u5730\u95ee\u3002</p><p>\u9b54\u59ec\u6447\u6447\u5934\uff0c\u201c\u6700\u7ec8\u7684\u80dc\u8d1f\u4e0d\u597d\u9884\u6599\uff0c\u4f46\u76ee\u524d\u7684\u5f62\u52bf\u7684\u786e\u5bf9\u5979\u6709\u5229\uff0c\u6253\u4ed7\u4e48\uff0c\u539f\u672c\u5c31\u662f\u8fd9\u4e48\u4e00\u56de\u4e8b\uff0c\u6709\u5907\u80dc\u65e0\u5907\uff0c\u591a\u7b97\u80dc\u5c11\u7b97\u3002\u4f60\u6bcd\u4eb2\u4e3a\u4e86\u8fd9\u4e00\u5929\uff0c\u60f3\u5fc5\u5df2\u7ecf\u7b79\u5907\u4e86\u591a\u5e74\uff0c\u5982\u4eca\u5929\u65f6\u5730\u5229\u53c8\u90fd\u5728\u5979\u8fd9\u8fb9\uff0c\u80dc\u7b97\u81ea\u7136\u662f\u6bd4\u8f83\u9ad8\u7684\u3002\u201d</p><p>\u201c\u6211\u4e0d\u592a\u660e\u767d\u4f60\u5728\u8bf4\u4ec0\u4e48\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4f60\u4eec\u90fd\u53eb\u6211\u2018\u5c0f\u96c5\u2019\uff0c\u6211\u4e0d\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u7f18\u6545\u3002\u5982\u679c\u4f60\u613f\u610f\u89e3\u91ca\uff0c\u90a3\u4e48\u6211\u6d17\u8033\u606d\u542c\uff1b\u5982\u679c\u4f60\u559c\u6b22\u6545\u5f04\u7384\u865a\uff0c\u90a3\u4e48\u6211\u53ea\u597d\u6055\u4e0d\u5949\u966a\u4e86\u3002\u201d</p><p>\u9b54\u59ec\u683c\u683c\u7b11\u8d77\u6765\uff0c\u201c\u597d\u5427\uff0c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u90a3\u4e48\u8ba9\u6211\u6765\u7b80\u5355\u89e3\u91ca\u4e00\u4e0b\u3002\u4e0d\u8fc7\u5728\u6b64\u4e4b\u524d\uff0c\u6709\u4e00\u4e2a\u6982\u5ff5\u5148\u8981\u660e\u786e\uff1a\u4f60\u4e0d\u4ec5\u4ec5\u662f\u4f60\uff0c\u4f60\u6709\u53ef\u80fd\u66fe\u7ecf\u662f\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u4e5f\u6709\u53ef\u80fd\u5c06\u4f1a\u662f\u53e6\u5916\u4e00\u4e2a\u4eba\uff0c\u4f46\u65e0\u8bba\u8fc7\u53bb\u3001\u73b0\u5728\u8fd8\u662f\u672a\u6765\uff0c\u4f60\u90fd\u4ecd\u7136\u8fd8\u662f\u4f60\u2014\u2014\u6211\u8fd9\u4e48\u8bf4\uff0c\u4f60\u80fd\u591f\u7406\u89e3\u5427\u3002\u201d</p><p>\u201c\u5c31\u50cf\u838e\u73de\u514b\u90a3\u6837\uff1f\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u5979\u4ee5\u524d\u662f\u51e1\u4eba\uff0c\u540e\u6765\u662f\u90aa\u9b54\uff0c\u4f46\u4ecd\u7136\u662f\u540c\u4e00\u4e2a\u4eba\u3002\u201d</p><p>\u201c\u55ef\uff0c\u4e0d\u592a\u4e00\u6837\uff0c\u4f46\u4e5f\u6709\u7c7b\u4f3c\u4e4b\u5904\u3002\u201d</p><p>\u201c\u90a3\u5c31\u59d1\u4e14\u5148\u8fd9\u6837\u7406\u89e3\u5427\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u6240\u4ee5\u4f60\u4eec\u7684\u610f\u601d\u662f\uff1a\u6211\u66fe\u7ecf\u662f\u4e00\u4e2a\u53eb\u5c0f\u96c5\u7684\u4eba\uff1f\u800c\u4e14\u8fd9\u4f4d\u5c0f\u96c5\u548c\u4f60\u4eec\u5f88\u719f\u6089\uff0c\u4ea4\u60c5\u532a\u6d45\uff1f\u201d</p><p>\u201c\u4f60\u548c\u6211\u5176\u5b9e\u5173\u7cfb\u4e00\u822c\u5566\uff0c\u51c6\u786e\u5730\u8bf4\uff0c\u6211\u4eec\u4e92\u76f8\u770b\u4e0d\u987a\u773c\u624d\u5bf9\uff0c\u201d\u9b54\u59ec\u7b11\u7740\u8bf4\uff0c\u201c\u4f46\u4f60\u548c\u59d0\u59d0\u786e\u5b9e\u662f\u5173\u7cfb\u5f88\u597d\u7684\u95fa\u871c\u5462\u3002\u59d0\u59d0\u8fd8\u628a\u4f60\u4ecb\u7ecd\u7ed9\u5979\u5f1f\u5f1f\uff0c\u60f3\u8ba9\u4f60\u505a\u5979\u7684\u5f1f\u59b9\u3002\u53ef\u60dc\u554a\uff0c\u6700\u540e\u51fa\u4e86\u70b9\u5c0f\u95ee\u9898\uff0c\u5927\u5bb6\u95f9\u7ffb\u4e86\uff0c\u567c\u91cc\u556a\u5566\u4e71\u6597\u4e00\u573a\uff0c\u6211\u662f\u65e9\u65e9\u5c31\u88ab\u9a71\u9010\u51fa\u5c40\uff0c\u59d0\u59d0\u9eef\u7136\u9000\u9690\uff0c\u4f60\u4e5f\u6ca1\u80fd\u5982\u613f\u4ee5\u507f\u2014\u2014\u4e0d\u8fc7\u8fd9\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u73b0\u5728\u770b\u6765\uff0c\u6216\u8bb8\u4f60\u624d\u662f\u6700\u7ec8\u80dc\u5229\u8005\u4e5f\u672a\u53ef\u77e5\u3002\u201d</p><p>\u201c\u542c\u8d77\u6765\u662f\u5f88\u7cbe\u5f69\u7684\u6545\u4e8b\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u53ef\u60dc\u6211\u5b8c\u5168\u6ca1\u6709\u8bb0\u5fc6\uff0c\u5426\u5219\u5fc5\u5b9a\u4f1a\u4e3a\u81ea\u5df1\u611f\u5230\u9a84\u50b2\u3002\u201d</p><p>\u9b54\u59ec\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u4f60\u7684\u786e\u6709\u8d44\u683c\u9a84\u50b2\uff0c\u201d\u5979\u8bf4\uff0c\u8bed\u6c14\u4e2d\u542c\u4e0d\u51fa\u662f\u8912\u662f\u8d2c\uff0c\u201c\u4e00\u4f4d\u5730\u72f1\u9b54\u738b\u56e0\u4f60\u800c\u8bde\u751f\uff0c\u5174\u76db\u5343\u5e74\u7684\u4eba\u7c7b\u5e1d\u56fd\u56e0\u4f60\u800c\u8870\u843d\uff0c\u4f60\u6380\u8d77\u4e86\u6ce2\u6d6a\uff0c\u5f62\u6210\u4e86\u6f29\u6da1\uff0c\u6240\u6709\u4eba\u90fd\u6df1\u9677\u5176\u4e2d\uff0c\u65e0\u6cd5\u81ea\u62d4\uff0c\u552f\u6709\u4f60\u7f6e\u8eab\u4e8b\u5916\u3002\u6211\u4f69\u670d\u7684\u4eba\u4e0d\u591a\uff0c\u51e1\u4eba\u66f4\u662f\u5be5\u5be5\u65e0\u51e0\uff0c\u4f46\u4f60\u5fc5\u7136\u540d\u5217\u5176\u4e2d\u3002\u201d</p><p>\u73ca\u5609\u5fae\u5fae\u8eac\u8eab\uff0c\u201c\u90a3\u771f\u662f\u6211\u7684\u8363\u5e78\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f46\u6211\u4e0d\u660e\u767d\uff0c\u65e2\u7136\u6211\u4eec\u6614\u65e5\u662f\u4ec7\u654c\uff0c\u4e3a\u4ec0\u4e48\u4f60\u8fd8\u4f1a\u5728\u8fd9\u91cc\u548c\u6211\u5fc3\u5e73\u6c14\u548c\u7684\u804a\u5929\uff1f\u201d</p><p>\u201c\u4ec7\u654c\u5176\u5b9e\u4e5f\u8c08\u4e0d\u4e0a\u4e86\uff0c\u771f\u8981\u8bf4\u8d77\u6765\uff0c\u4f60\u8fd8\u4e8e\u6211\u6709\u6069\uff0c\u201d\u624e\u745e\u5c14\u7b11\u76c8\u76c8\u5730\u8bf4\uff0c\u201c\u4f55\u51b5\u6211\u548c\u4f60\u6bcd\u4eb2\u521a\u521a\u8fbe\u6210\u534f\u8bae\uff0c\u5982\u4eca\u4e5f\u7b97\u662f\u76df\u53cb\uff0c\u6b63\u5728\u4e3a\u76f8\u540c\u76ee\u6807\u800c\u52aa\u529b\uff0c\u6211\u53c8\u600e\u4e48\u4f1a\u5bf9\u4f60\u4e0d\u5229\u5462\u3002\u201d</p><p>\u201c\u90a3\u4f60\u4eec\u7684\u76ee\u6807\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u663e\u800c\u6613\u89c1\u554a\uff0c\u8ba9\u4f60\u6210\u4e3a\u795e\u7947\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u8bf4\u4ec0\u4e48\uff01\u201d</p><p>\u201c\u8ba9\u4f60\u6210\u4e3a\u795e\u7947\u3002\u201d\u624e\u745e\u5c14\u53c8\u91cd\u590d\u4e86\u4e00\u904d\u3002</p><p>\u201c\u6211\u4e0d\u660e\u767d\u3002\u201d</p><p>\u201c\u4f60\u8001\u5e08\u521a\u624d\u5e03\u7f6e\u7684\u90a3\u4e2a\u9b54\u6cd5\u9635\uff0c\u53eb\u505a\u2018\u5316\u8eab\u2019\u7684\uff0c\u4f60\u77e5\u9053\u5b83\u7684\u7528\u9014\u5427\u3002\u201d</p><p>\u73ca\u5609\u70b9\u4e86\u70b9\u5934\u3002\u5316\u8eab\u662f\u4e00\u4e2a\u5f11\u795e\u6cd5\u672f\uff0c\u51fa\u53d1\u4e4b\u524d\uff0c\u743c\u6069\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u8428\u9a6c\u65af\u7279\u6b63\u662f\u6253\u7b97\u4f7f\u7528\u8fd9\u4e2a\u6cd5\u672f\u53bb\u5e72\u6389\u5deb\u5e08\u4e4b\u795e\u963f\u7956\u65af\uff0c\u53d6\u800c\u4ee3\u4e4b\uff0c\u4ee5\u4fbf\u7ee7\u7eed\u8ffd\u6c42\u9b54\u6cd5\u5973\u795e\u3002\u4f46\u5979\u4e0d\u77e5\u9053\u5965\u5609\u83b1\u65af\u4e5f\u638c\u63e1\u8fd9\u4e2a\u6cd5\u672f\u2026\u2026\u96be\u4e0d\u6210\u8001\u5e08\u548c\u8428\u9a6c\u65af\u7279\u6697\u4e2d\u6709\u8054\u7cfb\uff1f</p><p>\u201c\u8fd9\u4e2a\u89e3\u91ca\u8d77\u6765\u5c31\u6bd4\u8f83\u9ebb\u70e6\u4e86\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u5361\u5c14\u8428\u65af\u542c\u8bf4\u8fc7\u5427\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u8010\u745f\u7684\u5927\u5965\u672f\u5e08\u5361\u5c14\u8428\u65af\uff0c\u53d1\u660e\u4e86\u4e00\u79cd\u80fd\u591f\u8ba9\u51e1\u4eba\u53d6\u4ee3\u795e\u7947\u7684\u9b54\u6cd5\uff0c\u547d\u540d\u4e3a\u2018\u5316\u8eab\u2019\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u636e\u8bf4\u8fd9\u79cd\u9b54\u6cd5\u5728\u7269\u8d28\u754c\u65e9\u5df2\u5931\u4f20\uff0c\u4f46\u4f60\u6bcd\u4eb2\u6614\u65e5\u662f\u5361\u5c14\u8428\u65af\u7684\u597d\u53cb\uff0c\u4e5f\u66fe\u7ecf\u53c2\u4e0e\u8fc7\u7814\u7a76\uff0c\u6240\u4ee5\u624b\u4e0a\u4e5f\u6709\u4e00\u5957\u8d44\u6599\u3002\u5979\u7b79\u5212\u5df2\u4e45\uff0c\u6253\u7b97\u4e58\u6b64\u826f\u673a\uff0c\u8ba9\u4f60\u53d6\u4e00\u4f4d\u795e\u7947\u800c\u4ee3\u4e4b\u3002\u201d</p><p>\u201c\u54ea\u4f4d\u795e\u7947\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u4f60\u6bcd\u4eb2\u6ca1\u8bf4\uff0c\u4e0d\u8fc7\u6211\u731c\u6d4b\uff0c\u5e94\u8be5\u662f\u5bc6\u65af\u62c9\u5427\uff0c\u5426\u5219\u5979\u6293\u4e2a\u9009\u6c11\u6765\u505a\u4ec0\u4e48\u3002\u201d</p><p>\u624e\u745e\u5c14\u7684\u8a00\u4e0b\u4e4b\u610f\uff0c\u663e\u7136\u8981\u9488\u5bf9\u67d0\u4f4d\u795e\u7947\uff0c\u5fc5\u987b\u5148\u6293\u5230\u5176\u9009\u6c11\u4f5c\u4e3a\u65bd\u6cd5\u9053\u5177\u3002\u8fd9\u70b9\u73ca\u5609\u53ef\u4ee5\u7406\u89e3\uff0c\u4f46\u5979\u8fd8\u662f\u4e0d\u660e\u767d\uff0c\u65e2\u7136\u5965\u5609\u83b1\u65af\u624b\u4e0a\u6709\u9b54\u6cd5\u9635\uff0c\u4e5f\u5df2\u7ecf\u6293\u5230\u4e86\u83b1\u62c9\uff0c\u90a3\u76f4\u63a5\u627e\u4e2a\u5730\u65b9\u65bd\u6cd5\u4e0d\u5c31\u662f\u4e86\uff0c\u8dd1\u6765\u8fd9\u91cc\u548c\u51ef\u745f\u7433\u6253\u4ec0\u4e48\u67b6\uff1f</p><p>\u201c\u56e0\u4e3a\u5979\u8981\u5b8c\u6210\u5316\u8eab\uff0c\u8fd8\u7f3a\u5c11\u6700\u5173\u952e\u7684\u4e00\u4e2a\u8981\u7d20\uff0c\u201d\u4eff\u4f5b\u770b\u51fa\u73ca\u5609\u7684\u7591\u60d1\uff0c\u624e\u745e\u5c14\u89e3\u91ca\u8bf4\uff0c\u201c\u9b54\u6cd5\u9635\u3001\u65bd\u6cd5\u9053\u5177\uff0c\u8fd9\u4e24\u8005\u6613\u5f97\uff0c\u771f\u6b63\u96be\u4ee5\u5177\u5907\u7684\uff0c\u662f\u2018\u9a71\u52a8\u529b\u2019\u3002\u201d</p><p>\u4e00\u4e2a\u5b8c\u6574\u7684\u9b54\u6cd5\u9635\uff0c\u5e94\u5f53\u5305\u62ec\u4e09\u4e2a\u8981\u7d20\uff1a\u5851\u9020\u9b54\u6cd5\u6548\u679c\u7684\u9635\u56fe\u7ed3\u6784\u3001\u534f\u52a9\u80fd\u91cf\u8f6c\u5316\u7684\u9053\u5177\u5a92\u4ecb\uff0c\u4ee5\u53ca\u9a71\u52a8\u529b\u3002\u5982\u679c\u4ec5\u6709\u9635\u56fe\u7ed3\u6784\u548c\u65bd\u6cd5\u9053\u5177\uff0c\u6ca1\u6709\u9a71\u52a8\u529b\uff0c\u5c31\u597d\u50cf\u4e00\u53f0\u69a8\u6c41\u673a\uff0c\u673a\u5668\u6ca1\u6709\u6545\u969c\uff0c\u82f9\u679c\u4e5f\u51c6\u5907\u597d\u4e86\uff0c\u5374\u6ca1\u6709\u901a\u4e0a\u7535\u6e90\uff0c\u4f9d\u7136\u8fd8\u662f\u69a8\u4e0d\u51fa\u679c\u6c41\u3002\u901a\u5e38\u60c5\u51b5\u4e0b\uff0c\u65bd\u6cd5\u8005\u81ea\u5df1\u4ece\u9b54\u7f51\u4e2d\u6c72\u53d6\u529b\u91cf\uff0c\u6ce8\u5165\u9b54\u6cd5\u9635\uff0c\u4e5f\u5373\u662f\u81ea\u5df1\u62c5\u4efb\u9a71\u52a8\u529b\u3002\u4f46\u67d0\u4e9b\u7279\u522b\u5e9e\u5927\u7684\u9b54\u6cd5\u9635\uff0c\u4f8b\u5982\u201c\u5316\u8eab\u201d\u8fd9\u79cd\uff0c\u9700\u8981\u7684\u9a71\u52a8\u529b\u6781\u5f3a\uff0c\u65bd\u6cd5\u8005\u81ea\u8eab\u5b9e\u529b\u4e0d\u591f\uff0c\u5c31\u5fc5\u987b\u53e6\u5bfb\u5916\u63f4\u3002\u5361\u5c14\u8428\u65af\u662f\u8010\u745f\u5386\u53f2\u4e0a\u6700\u5f3a\u529b\u7684\u5927\u5965\u672f\u5e08\uff0c\u5f53\u65f6\u53c8\u80fd\u591f\u76f4\u63a5\u4ece\u201c\u6e90\u201d\u4e2d\u63d0\u53d6\u529b\u91cf\uff0c\u6240\u4ee5\u80fd\u591f\u53d1\u52a8\u201c\u5316\u8eab\u201d\uff1b\u8428\u9a6c\u65af\u7279\u3001\u5965\u5609\u83b1\u65af\u4e24\u4eba\uff0c\u867d\u7136\u90fd\u662f\u7b2c\u4e00\u6d41\u7684\u5927\u5deb\u5e08\uff0c\u4f46\u8f83\u4e4b\u5361\u5c14\u8428\u65af\u8fd8\u662f\u6709\u6240\u4e0d\u5982\uff0c\u800c\u4e14\u81ea\u4ece\u8010\u745f\u4e4b\u540e\uff0c\u9b54\u6cd5\u5973\u795e\u5c06\u201c\u6e90\u201d\u5c01\u95ed\u4e86\uff0c\u6240\u4ee5\u4ed6\u4eec\u53ea\u80fd\u53e6\u5bfb\u4ed6\u6cd5\u3002</p><p>\u8428\u9a6c\u65af\u7279\u5f88\u806a\u660e\uff0c\u4ed6\u627e\u5230\u4e86\u4e00\u79cd\u65b9\u6cd5\uff0c\u4f46\u5fc5\u987b\u501f\u52a9\u51ef\u745f\u7433\u2014\u2014\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u501f\u52a9\u4f0a\u739b\u65af\u5361\u7b2c\u4e94\u79d8\u5668\u2014\u2014\u624d\u80fd\u5b9e\u73b0\uff1b\u800c\u5965\u5609\u83b1\u65af\u66f4\u806a\u660e\uff0c\u5979\u76f4\u63a5\u6765\u62a2\u593a\u80dc\u5229\u679c\u5b9e\u3002</p><p>\u201c\u4f60\u73b0\u5728\u4e5f\u662f\u2018\u51e4\u51f0\u2019\uff0c\u53ea\u8981\u5e72\u6389\u59d0\u59d0\uff0c\u4f60\u5c31\u80fd\u63a5\u638c\u79d8\u5668\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u638c\u63e1\u4e86\u79d8\u5668\uff0c\u4f60\u6bcd\u4eb2\u7684\u8ba1\u5212\u4fbf\u53ef\u4ee5\u5b9e\u73b0\uff0c\u5982\u679c\u4e00\u5207\u987a\u5229\uff0c\u4ee5\u540e\u6211\u4eec\u5c31\u8981\u79f0\u547c\u4f60\u4e3a\u5973\u795e\u965b\u4e0b\u4e86\u3002\u5982\u4f55\uff1f\u73b0\u5728\u662f\u4e0d\u662f\u611f\u89c9\u65e2\u60ca\u53c8\u559c\uff0c\u53c8\u662f\u60f6\u6050\u4e0d\u5b89\uff0c\u5fc3\u60c5\u5f88\u590d\u6742\uff1f\u5f88\u6b63\u5e38\u5566\uff0c\u8c01\u7a81\u7136\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\u90fd\u4f1a\u5413\u4e00\u8df3\uff0c\u201d\u5979\u683c\u683c\u7b11\u7740\uff0c\u201c\u5c31\u50cf\u5f53\u5e74\u6709\u4e2a\u4eba\u5bf9\u6211\u8bf4\uff1a\u55e8\uff0c\u624e\u745e\u5c14\uff0c\u6211\u5e2e\u4f60\u627e\u4e86\u4efd\u65b0\u5de5\u4f5c\uff0c\u53bb\u963f\u5f17\u7eb3\u65af\u5f53\u9b54\u738b\u5427\uff0c\u73b0\u5728\u5c31\u51fa\u53d1\u3002\u7136\u540e\u628a\u6211\u4e00\u811a\u8e39\u8fdb\u5730\u72f1\u2014\u2014\u6211\u5f53\u65f6\u7684\u5fc3\u60c5\uff0c\u4f60\u73b0\u5728\u60f3\u5fc5\u4e5f\u80fd\u4f53\u4f1a\u4e00\u4e8c\u4e86\u5427\u3002\u201d</p><p>\u201c\u6211\u4e0d\u80fd\u4f53\u4f1a\u3002\u201d\u73ca\u5609\u786c\u90a6\u90a6\u5730\u8bf4\u3002</p><p>\u201c\u522b\u7d27\u5f20\uff0c\u4e00\u5f00\u59cb\u90fd\u6709\u70b9\u4e0d\u9002\u5e94\uff0c\u4e60\u60ef\u4ee5\u540e\u5c31\u597d\u4e86\u3002\u8981\u4e0d\u8981\u6211\u4f20\u6388\u70b9\u7ecf\u9a8c\uff1f\u867d\u7136\u6211\u6ca1\u505a\u8fc7\u795e\uff0c\u4f30\u8ba1\u548c\u9b54\u738b\u4e5f\u5dee\u4e0d\u591a\u3002\u5f53\u7136\u505a\u5973\u795e\u53ef\u80fd\u66f4\u5fd9\u70b9\uff0c\u6ca1\u7a7a\u56de\u7269\u8d28\u754c\u548c\u5f1f\u5f1f\u5e7d\u4f1a\uff0c\u4e0d\u8fc7\u4f60\u653e\u5fc3\uff0c\u6211\u4f1a\u7167\u987e\u597d\u4ed6\u7684\u3002\u201d</p><p>\u201c\u4f60\u597d\u50cf\u5f88\u9ad8\u5174\uff1f\u201d</p><p>\u201c\u5982\u679c\u4f60\u662f\u60f3\u8bf4\u6211\u5728\u5e78\u707e\u4e50\u7978\uff0c\u90a3\u4e48\u6211\u627f\u8ba4\uff0c\u662f\u7684\uff0c\u201d\u9b54\u59ec\u7b11\u563b\u563b\u5730\u8bf4\uff0c\u201c\u6211\u662f\u4e2a\u5fc3\u80f8\u72ed\u7a84\u7684\u5973\u4eba\uff0c\u4ece\u6765\u90fd\u662f\uff0c\u4e00\u76f4\u90fd\u662f\uff0c\u6c38\u8fdc\u90fd\u662f\u3002\u201d</p><p>\u73ca\u5609\u6c89\u9ed8\u4e86\u7247\u523b\uff0c\u4e0d\u518d\u7406\u4f1a\u624e\u745e\u5c14\u3002</p><p>\u5965\u5609\u83b1\u65af\u548c\u51ef\u745f\u7433\u7684\u6218\u6597\u4ecd\u7136\u5728\u6301\u7eed\uff0c\u800c\u4e14\u5c40\u52bf\u4f3c\u4e4e\u6ca1\u6709\u4efb\u4f55\u53d8\u5316\u3002\u5965\u5609\u83b1\u65af\u5728\u8fdb\u653b\uff0c\u5979\u7684\u65bd\u6cd5\u901f\u5ea6\u8d8a\u6765\u8d8a\u5feb\uff0c\u4f7f\u7528\u7684\u9b54\u6cd5\u4e5f\u5a01\u529b\u8d8a\u6765\u8d8a\u5927\uff0c\u4eff\u4f5b\u72c2\u98ce\u66b4\u96e8\u4e00\u822c\u78be\u538b\u8fc7\u6765\uff0c\u51ef\u745f\u7433\u5374\u7eb9\u4e1d\u4e0d\u52a8\uff0c\u5de8\u9570\u4e00\u6b21\u53c8\u4e00\u6b21\u5730\u6325\u821e\uff0c\u5c06\u6240\u6709\u653b\u51fb\u5316\u4e3a\u65e0\u5f62\u3002\u73ca\u5609\u89c9\u5f97\u5982\u679c\u8fd9\u6837\u4e0b\u53bb\uff0c\u5979\u4eec\u6709\u53ef\u80fd\u6c38\u8fdc\u4e5f\u5206\u4e0d\u51fa\u80dc\u8d1f\u3002</p><p>\u5ffd\u7136\u95f4\uff0c\u4f4e\u6c89\u800c\u60a0\u957f\u7684\u9f99\u543c\u4ece\u9065\u8fdc\u7684\u5730\u65b9\u4f20\u6765\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u4e00\u9053\u5de8\u5927\u7684\u767d\u8272\u5149\u67f1\u81ea\u9ad8\u7a7a\u4e2d\u5782\u76f4\u8f70\u4e0b\uff0c\u6b63\u6b63\u843d\u5165\u9752\u94dc\u9f0e\u4e2d\u3002\u4e00\u76f4\u7a33\u7a33\u77d7\u7acb\u7740\u7684\u9752\u94dc\u5de8\u9f0e\u9661\u7136\u6d6e\u7a7a\u800c\u8d77\uff0c\u5149\u8292\u56db\u5c04\uff0c\u65e0\u6570\u900f\u660e\u865a\u5f71\u4ece\u9f0e\u4e2d\u98de\u817e\u51fa\u6765\uff0c\u5f62\u6001\u5404\u5f02\uff0c\u6709\u7684\u50cf\u731b\u517d\uff0c\u6709\u7684\u50cf\u4eba\u7c7b\uff0c\u6709\u7684\u50cf\u90aa\u9b54\uff0c\u4f46\u66f4\u591a\u662f\u5de8\u9f99\u4e4b\u5f62\u3002\u76d8\u65cb\u5728\u51ef\u745f\u7433\u8eab\u4e0a\u7684\u91d1\u9cde\u9f99\u86c7\u4e5f\u98de\u4e86\u8d77\u6765\uff0c\u7ed5\u7740\u9752\u94dc\u9f0e\u98de\u7fd4\uff0c\u901f\u5ea6\u8d8a\u6765\u8d8a\u5feb\u3002\u201c\u65f6\u95f4\u5230\u4e86\u3002\u201d\u624e\u745e\u5c14\u8f7b\u58f0\u8bf4\u3002</p><p>\u73ca\u5609\u4e0d\u77e5\u9053\u6240\u8c13\u201c\u65f6\u95f4\u5230\u4e86\u201d\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u4f46\u8fd9\u7a81\u5982\u5176\u6765\u7684\u53d8\u5316\u6240\u9020\u6210\u7684\u5f71\u54cd\u7acb\u523b\u663e\u73b0\u51fa\u6765\uff0c\u5965\u5609\u83b1\u65af\u6ca1\u6709\u53d7\u5230\u4efb\u4f55\u5e72\u6270\uff0c\u51ef\u745f\u7433\u7684\u52a8\u4f5c\u5374\u660e\u663e\u7f13\u6162\u4e86\u8bb8\u591a\uff0c\u5979\u6574\u4e2a\u4eba\u4eff\u4f5b\u80cc\u8d1f\u7740\u67d0\u79cd\u65e0\u5f62\u7684\u538b\u529b\uff0c\u53d8\u5f97\u8fdf\u949d\u8d77\u6765\u3002\u6b64\u6d88\u5f7c\u957f\u4e4b\u4e0b\uff0c\u80dc\u8d1f\u7acb\u523b\u663e\u73b0\uff0c\u5965\u5609\u83b1\u65af\u4ece\u4e66\u4e2d\u53ec\u5524\u51fa\u4e00\u53ea\u767d\u8272\u957f\u6bdb\u3001\u56db\u8e44\u5192\u706b\u7684\u733f\u7334\u72b6\u602a\u517d\uff0c\u51f6\u72e0\u5730\u671d\u51ef\u745f\u7433\u649e\u53bb\uff0c\u51ef\u745f\u7433\u6325\u52a8\u5de8\u9570\uff0c\u5c06\u602a\u517d\u62e6\u8170\u65a9\u6210\u4e24\u6bb5\uff0c\u65ad\u53e3\u5904\u5374\u7a81\u7136\u98de\u51fa\u6570\u5341\u6761\u9752\u8272\u957f\u86c7\uff0c\u75be\u82e5\u95ea\u7535\uff0c\u5c06\u9ed1\u8863\u5c11\u5973\u7d27\u7d27\u7f20\u4f4f\u3002</p><p>\u201c\u518d\u89c1\u4e86\uff0c\u516c\u4e3b\u6bbf\u4e0b\u3002\u201d</p><p>\u5965\u5609\u83b1\u65af\u8bf4\u7740\uff0c\u624b\u4e2d\u4e1d\u6beb\u4e0d\u6162\uff0c\u5149\u7403\u5728\u638c\u5fc3\u805a\u96c6\uff0c\u4e00\u4e2a\u5f3a\u5927\u7684\u7834\u574f\u6cd5\u672f\u6b63\u5728\u51dd\u805a\u6210\u578b\u3002\u51ef\u745f\u7433\u62ac\u5934\u770b\u4e86\u770b\u5979\uff0c\u53c8\u77a5\u4e86\u4e00\u773c\u7ad9\u5728\u540e\u65b9\u7684\u73ca\u5609\u548c\u624e\u745e\u5c14\uff0c\u5ffd\u7136\u53e3\u4e2d\u53d1\u51fa\u4e00\u58f0\u957f\u541f\uff0c\u7d2b\u8272\u7684\u706b\u7130\u4ece\u4f53\u5185\u731b\u70c8\u55b7\u6d8c\u51fa\u6765\uff0c\u5c06\u7f20\u7ed5\u5728\u8eab\u4e0a\u7684\u9752\u86c7\u711a\u70e7\u6b86\u5c3d\u3002\u706b\u7130\u718a\u718a\u71c3\u70e7\uff0c\u51ef\u745f\u7433\u6574\u4e2a\u4eba\u90fd\u88ab\u541e\u6ca1\u5176\u4e2d\uff0c\u8f6c\u773c\u95f4\u5316\u4f5c\u4e00\u53ea\u5de8\u5927\u7684\u706b\u51e4\u51f0\uff0c\u53cc\u7ffc\u6325\u52a8\u5149\u7130\u4e07\u4e08\uff0c\u671d\u7740\u5965\u5609\u83b1\u65af\u51b2\u53bb\u3002\u5965\u5609\u83b1\u65af\u731d\u4e0d\u53ca\u9632\uff0c\u5306\u5fd9\u95f4\u64a4\u53bb\u539f\u672c\u51c6\u5907\u7684\u6cd5\u672f\uff0c\u5728\u9762\u524d\u5316\u51fa\u4e00\u9762\u5143\u7d20\u62a4\u76fe\uff0c\u7136\u800c\u51e4\u51f0\u4e00\u51b2\u4e4b\u4e0b\uff0c\u62a4\u76fe\u77ac\u95f4\u7c89\u788e\uff0c\u706b\u7130\u5982\u6f6e\u6c34\u822c\u6c79\u6d8c\u800c\u6765\uff0c\u773c\u770b\u5c31\u8981\u5c06\u5927\u5965\u672f\u5e08\u6df9\u6ca1\u3002</p><p>\u201c\u8001\u5e08\uff01\u201d</p><p>\u73ca\u5609\u5927\u60ca\u4e4b\u4e0b\uff0c\u4e0b\u610f\u8bc6\u5730\u5f80\u524d\u4f38\u51fa\u624b\u3002\u8bf4\u4e5f\u5947\u602a\uff0c\u5979\u660e\u660e\u6ca1\u6709\u91ca\u653e\u4efb\u4f55\u6cd5\u672f\uff0c\u706b\u7130\u51e4\u51f0\u5374\u50cf\u662f\u88ab\u67d0\u79cd\u65e0\u5f62\u7684\u529b\u91cf\u963b\u6321\uff0c\u660e\u663e\u5730\u987f\u4e86\u4e00\u987f\u3002\u4e58\u6b64\u673a\u4f1a\uff0c\u5965\u5609\u83b1\u65af\u6025\u901f\u540e\u9000\uff0c\u4e8e\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u6210\u529f\u9003\u8131\u70c8\u7130\u711a\u8eab\u7684\u7ed3\u5c40\u3002\u706b\u7130\u51e4\u51f0\u4e00\u51fb\u4e0d\u4e2d\uff0c\u4e5f\u4fbf\u505c\u5728\u7a7a\u4e2d\uff0c\u6ca1\u6709\u7ee7\u7eed\u8ffd\u51fb\uff0c\u201c\u4f60\u8fd0\u6c14\u4e0d\u9519\uff0c\u201d\u51ef\u745f\u7433\u7684\u58f0\u97f3\u4ece\u706b\u7130\u4e2d\u53d1\u51fa\uff0c\u201c\u8fd9\u6b21\u662f\u6211\u8f93\u4e86\uff0c\u5c0f\u96c5\uff0c\u6211\u4eec\u540e\u4f1a\u6709\u671f\u3002\u201d</p><p>\u201c\u59d0\u59d0\u518d\u89c1\uff0c\u201d\u624e\u745e\u5c14\u5728\u8fdc\u5904\u7b11\u7740\u6325\u624b\uff0c\u201c\u6211\u4f1a\u8bb0\u5f97\u8f6c\u544a\u743c\u6069\uff0c\u8ba9\u4ed6\u53bb\u7d2b\u5bb8\u6c99\u6f20\u627e\u4f60\u7684\u3002\u201d</p><p>\u51e4\u51f0\u957f\u9e23\u4e00\u58f0\uff0c\u5468\u8eab\u70c8\u7130\u8f70\u7136\u7efd\u653e\uff0c\u5316\u4f5c\u65e0\u6570\u661f\u661f\u70b9\u70b9\u7684\u5fae\u5149\uff0c\u5c31\u6b64\u6d88\u6563\u3002\u5965\u5609\u83b1\u65af\u671d\u624e\u745e\u5c14\u5fae\u5fae\u70b9\u5934\u81f4\u610f\uff0c\u7136\u540e\u793a\u610f\u73ca\u5609\u4e0a\u524d\uff0c\u73ca\u5609\u5374\u7ad9\u5728\u539f\u5730\u6ca1\u52a8\uff0c\u201c\u5bf9\u4e0d\u8d77\uff0c\u8001\u5e08\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4e8b\u60c5\u53d1\u751f\u5f97\u592a\u7a81\u7136\uff0c\u6211\u8fd8\u6ca1\u6709\u505a\u597d\u51c6\u5907\u3002\u201d</p><p>\u5965\u5609\u83b1\u65af\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u6211\u77e5\u9053\u3002\u201d\u5979\u8bf4\uff0c\u7a81\u7136\u4f38\u624b\u6309\u4f4f\u73ca\u5609\u7684\u80a9\u5934\u3002</p><p>\u73ca\u5609\u53ea\u89c9\u5168\u8eab\u4e00\u9707\uff0c\u7136\u540e\u4fbf\u53d1\u73b0\u81ea\u5df1\u4e0d\u7531\u81ea\u4e3b\u5730\u671d\u524d\u8d70\u53bb\uff0c\u201c\u8001\u5e08\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d\u5979\u6025\u5fd9\u95ee\u3002</p><p>\u201c\u6211\u77e5\u9053\u4f60\u5e76\u6ca1\u6709\u505a\u597d\u51c6\u5907\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u6211\u4e5f\u5f88\u6e05\u695a\uff0c\u4f60\u8ba4\u771f\u8003\u8651\u4e4b\u540e\uff0c\u6700\u7ec8\u4e00\u5b9a\u4f1a\u62d2\u7edd\u3002\u201d</p><p>\u201c\u90a3\u4f60\u2026\u2026\u201d</p><p>\u201c\u4f60\u5c06\u6765\u6216\u8bb8\u4e5f\u4f1a\u4e3a\u4eba\u7236\u6bcd\uff0c\u6240\u4ee5\u6211\u60f3\u544a\u8bc9\u4f60\u6211\u7684\u4e00\u70b9\u5fc3\u5f97\u4f53\u4f1a\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u6211\u771f\u8bda\u5e0c\u671b\u81ea\u5df1\u662f\u4e2a\u5f00\u660e\u7684\u6bcd\u4eb2\uff0c\u80fd\u591f\u5145\u5206\u5c0a\u91cd\u5b50\u5973\u7684\u610f\u613f\uff0c\u8fc7\u53bb\u6211\u4e5f\u4e00\u76f4\u662f\u8fd9\u6837\u505a\u7684\uff0c\u65e0\u8bba\u4f60\u505a\u4ec0\u4e48\u9009\u62e9\uff0c\u54ea\u6015\u6211\u518d\u4e0d\u8d5e\u6210\uff0c\u90fd\u4e0d\u4f1a\u5f3a\u884c\u5e72\u6d89\uff0c\u65e0\u8bba\u6211\u6709\u4ec0\u4e48\u60f3\u6cd5\uff0c\u53ea\u8981\u4f60\u4e0d\u613f\u610f\uff0c\u6211\u90fd\u4e0d\u4f1a\u5f3a\u6c42\u3002\u6211\u4ee5\u4e3a\u6211\u7684\u505a\u6cd5\u662f\u6b63\u786e\u7684\uff0c\u6211\u4ee5\u4e3a\u8fd9\u6837\u4f60\u4f1a\u83b7\u5f97\u5e78\u798f\uff0c\u4f46\u4e8b\u5b9e\u8bc1\u660e\u6211\u9519\u4e86\uff0c\u9519\u5f97\u975e\u5e38\u79bb\u8c31\uff0c\u65e0\u6cd5\u633d\u6551\u3002\u7136\u540e\u6211\u7ec8\u4e8e\u660e\u767d\uff1a\u5c0a\u91cd\u4e0d\u7b49\u4e8e\u653e\u4efb\uff0c\u6211\u662f\u4f60\u7684\u6bcd\u4eb2\uff0c\u6211\u6709\u6743\u529b\u4e5f\u6709\u8d23\u4efb\u4e3a\u4f60\u9009\u62e9\u6b63\u786e\u7684\u4eba\u751f\u9053\u8def\uff0c\u4f60\u73b0\u5728\u4e0d\u4f1a\u7406\u89e3\uff0c\u6216\u8bb8\u5c06\u6765\u4f1a\u7406\u89e3\u2014\u2014\u4e5f\u6216\u8bb8\u6c38\u8fdc\u4e0d\u4f1a\u7406\u89e3\uff0c\u4f46\u6211\u5e76\u4e0d\u5173\u5fc3\u3002\u6211\u4e0d\u9700\u8981\u4f60\u7684\u7406\u89e3\uff0c\u4f46\u6211\u5fc5\u987b\u505a\u6211\u8ba4\u4e3a\u6b63\u786e\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u5979\u6325\u6325\u624b\uff0c\u5c06\u73ca\u5609\u63a8\u5165\u9b54\u6cd5\u9635\u4e2d\uff0c\u7136\u540e\u81ea\u5df1\u4ece\u201c\u83b1\u62c9\u201d\u7684\u8eab\u4f53\u4e2d\u5206\u79bb\u51fa\u6765\uff0c\u6307\u4f7f\u9009\u6c11\u8d70\u5230\u9b54\u6cd5\u9635\u7684\u4e2d\u592e\uff0c\u81ea\u5df1\u5219\u9000\u56de\u5230\u8fb9\u7f18\uff0c\u5c55\u5f00\u300a\u547d\u8fd0\u957f\u591c\u300b\u5f00\u59cb\u65bd\u6cd5\u3002\u5965\u5609\u83b1\u65af\u663e\u7136\u51c6\u5907\u5df2\u4e45\uff0c\u95ea\u95ea\u53d1\u5149\u7684\u5492\u6587\u4e00\u4e2a\u63a5\u4e00\u4e2a\u5730\u81ea\u4e66\u9875\u4e2d\u6d6e\u73b0\u51fa\u6765\uff0c\u6295\u5165\u9752\u94dc\u9f0e\u4e2d\u3002\u73ca\u5609\u8eab\u4e0d\u7531\u5df1\u5730\u4e3e\u8d77\u53cc\u624b\uff0c\u5f00\u59cb\u611f\u5e94\u9b54\u7f51\uff0c\u4ece\u4e2d\u6c72\u53d6\u529b\u91cf\u3002\u9b54\u6cd5\u9635\u5f00\u59cb\u8fd0\u8f6c\u8d77\u6765\uff0c\u901f\u5ea6\u8d8a\u6765\u8d8a\u5feb\uff0c\u4eff\u4f5b\u4e00\u53ea\u5de8\u5927\u7684\u731b\u517d\uff0c\u8feb\u4e0d\u53ca\u5f85\u5730\u60f3\u8981\u541e\u566c\u66f4\u591a\u7684\u9b54\u529b\u3002\u8fd9\u79cd\u9700\u6c42\u8fdc\u8fdc\u8d85\u8d8a\u5979\u7684\u80fd\u529b\uff0c\u8f6c\u77ac\u4e4b\u95f4\u5c31\u5c06\u5979\u4ece\u9b54\u7f51\u4e2d\u6c72\u53d6\u7684\u90a3\u70b9\u80fd\u91cf\u6d88\u5316\u5f97\u4e00\u5e72\u4e8c\u51c0\u3002\u901a\u5e38\u60c5\u51b5\u4e0b\uff0c\u8fd9\u610f\u5473\u7740\u9b54\u6cd5\u9635\u5df2\u7ecf\u5931\u63a7\uff0c\u5982\u679c\u4e0d\u80fd\u53ca\u65f6\u505c\u6b62\uff0c\u5deb\u5e08\u5c06\u4f1a\u906d\u53d7\u81f4\u547d\u7684\u53cd\u566c\u3002\u4f46\u73ca\u5609\u5b89\u7136\u65e0\u6059\uff0c\u56e0\u4e3a\u5c31\u5728\u9b54\u6cd5\u9635\u8fd0\u8f6c\u7684\u540c\u65f6\uff0c\u4e00\u79cd\u5e9e\u5927\u3001\u51f6\u66b4\u800c\u4e14\u96c4\u6d51\u65e0\u6bd4\u7684\u529b\u91cf\u81ea\u9752\u94dc\u9f0e\u4e2d\u900f\u51fa\uff0c\u5728\u5979\u4e0b\u610f\u8bc6\u5730\u6307\u6325\u4e0b\uff0c\u6ce8\u5165\u9b54\u6cd5\u9635\u7684\u6bcf\u4e2a\u5173\u952e\u8282\u70b9\u4e4b\u4e2d\uff0c\u6b63\u597d\u8fbe\u5230\u4e86\u4f9b\u9700\u5e73\u8861\u3002</p><p>\u516d\u9053\u4e0d\u540c\u989c\u8272\u7684\u5149\u67f1\u5728\u9b54\u6cd5\u9635\u7684\u516d\u4e2a\u89d2\u843d\u540c\u65f6\u5347\u8d77\uff0c\u7136\u540e\u5206\u51fa\u65e0\u6570\u6761\u900f\u660e\u7ec6\u7ebf\uff0c\u5411\u56db\u9762\u516b\u65b9\u8513\u5ef6\u3002\u5b83\u4eec\u5206\u522b\u6765\u81ea\u571f\u3001\u6c34\u3001\u706b\u3001\u98ce\u56db\u5143\u7d20\u548c\u6b63\u3001\u8d1f\u80fd\u91cf\u4f4d\u9762\u3002\u8fd9\u516d\u4e2a\u4f4d\u9762\u7edf\u79f0\u4e3a\u201c\u5185\u5c42\u754c\u201d\uff0c\u662f\u76f8\u5bf9\u4e8e\u7269\u8d28\u754c\u548c\u5916\u5c42\u754c\u800c\u8a00\uff0c\u6309\u7167\u901a\u884c\u7684\u7406\u8bba\uff0c\u56db\u5143\u7d20\u662f\u6784\u6210\u4e00\u5207\u7269\u8d28\u7684\u57fa\u7840\uff0c\u800c\u6b63\u8d1f\u80fd\u91cf\u5219\u662f\u7075\u9b42\u7684\u6765\u6e90\uff0c\u7269\u8d28\u754c\u4ee5\u53ca\u8bf8\u5929\u4e07\u754c\uff0c\u4e0a\u81f3\u5929\u5802\u5c71\u3001\u660e\u6c34\u5883\uff0c\u4e0b\u81f3\u4e5d\u5c42\u5730\u72f1\u3001\u65e0\u5c3d\u6df1\u6e0a\uff0c\u4e43\u81f3\u4e00\u5207\u751f\u7075\u6b7b\u7269\uff0c\u90fd\u6e90\u81ea\u8fd9\u516d\u5927\u57fa\u672c\u8981\u7d20\uff0c\u7edd\u65e0\u4f8b\u5916\u3002\u4f4e\u7ea7\u7684\u9b54\u6cd5\u9635\uff0c\u53ef\u80fd\u4ec5\u4ec5\u53ea\u662f\u8054\u7ed3\u9b54\u7f51\uff0c\u800c\u8d8a\u9ad8\u7ea7\u7684\u9b54\u6cd5\u9635\uff0c\u8d8a\u4f1a\u6df1\u5165\u89e6\u53ca\u4e16\u754c\u672c\u6e90\u6240\u5728\uff0c\u4e0e\u56db\u5143\u7d20\u3001\u6b63\u8d1f\u80fd\u91cf\u6253\u4ea4\u9053\u4e5f\u662f\u5bb6\u5e38\u4fbf\u996d\u3002\u4f46\u56db\u5143\u7d20\u5f7c\u6b64\u5faa\u73af\u76f8\u514b\uff0c\u6b63\u8d1f\u80fd\u91cf\u66f4\u662f\u6c34\u706b\u4e0d\u5bb9\uff0c\u5deb\u5e08\u8981\u6c72\u53d6\u5176\u529b\u91cf\uff0c\u5fc5\u7136\u6709\u6240\u4fa7\u91cd\uff0c\u6709\u6240\u53d6\u820d\uff0c\u6216\u8005\u8f6e\u6d41\u6fc0\u53d1\uff0c\u4f9d\u6b21\u53d1\u52a8\u3002\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u50cf\u8fd9\u6837\u56db\u5143\u7d20\u3001\u6b63\u8d1f\u80fd\u91cf\u540c\u65f6\u62db\u51fa\uff0c\u516d\u5927\u5185\u5c42\u4f4d\u9762\u4e4b\u95e8\u4e00\u8d77\u655e\u5f00\uff0c\u5c5e\u4e8e\u4e25\u91cd\u8fdd\u53cd\u57fa\u672c\u7684\u9b54\u6cd5\u5b66\u7406\uff0c\u4e43\u662f\u5178\u578b\u7684\u81ea\u6740\u884c\u4e3a\u3002\u4f46\u5728\u5965\u5609\u83b1\u65af\u7684\u7cbe\u51c6\u63a7\u5236\u4e0b\uff0c\u6240\u6709\u5143\u7d20\u3001\u80fd\u91cf\u5168\u90fd\u6309\u7167\u4e8b\u5148\u89c4\u5212\u597d\u7684\u8def\u7ebf\u6d41\u52a8\u524d\u8fdb\uff0c\u5f7c\u6b64\u4ea4\u9519\u5374\u4e0d\u51b2\u7a81\uff0c\u4e00\u5207\u4e95\u4e95\u6709\u6761\uff0c\u6309\u90e8\u5c31\u73ed\uff0c\u6700\u7ec8\u5728\u9b54\u6cd5\u9635\u7684\u4e2d\u5fc3\u6c47\u96c6\uff0c\u6ce8\u5165\u201c\u83b1\u62c9\u201d\u7684\u4f53\u5185\u3002</p><p>\u201c\u770b\u6765\u4e00\u5207\u987a\u5229\uff0c\u201d\u624e\u745e\u5c14\u60a0\u95f2\u5730\u8bf4\uff0c\u201c\u518d\u8fc7\u4e00\u4f1a\uff0c\u4f60\u5c31\u662f\u6709\u53f2\u4ee5\u6765\u7b2c\u4e00\u4e2a\u4eb2\u773c\u76ee\u7779\u81ea\u5df1\u5973\u513f\u767b\u4e0a\u795e\u4f4d\u7684\u6bcd\u4eb2\u4e86\uff0c\u795d\u8d3a\u4f60\uff0c\u5927\u5965\u672f\u5e08\u3002\u201d</p><p>\u201c\u6211\u8bb0\u5f97\u4f60\u7684\u5e2e\u52a9\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u6211\u4e5f\u4f1a\u9075\u5b88\u8bfa\u8a00\uff0c\u4ed8\u7ed9\u4f60\u5e94\u5f97\u7684\u62a5\u916c\u3002\u201d</p><p>\u201c\u90a3\u597d\u6781\u4e86\uff0c\u201d\u624e\u745e\u5c14\u4f3c\u4e4e\u975e\u5e38\u6b23\u6170\uff0c\u201c\u8bf4\u5b9e\u8bdd\uff0c\u6211\u4e00\u5f00\u59cb\u8fd8\u5f88\u62c5\u5fc3\u5462\uff0c\u751f\u6015\u4f60\u7684\u8ba1\u5212\u5931\u8d25\uff0c\u90a3\u6211\u53ef\u5c31\u613f\u671b\u843d\u7a7a\u4e86\u3002\u201d</p><p>\u201c\u5360\u661f\u5e08\u522b\u65e0\u6240\u957f\uff0c\u552f\u72ec\u64c5\u957f\u5236\u5b9a\u8ba1\u5212\uff0c\u201d\u5965\u5609\u83b1\u65af\u8bf4\uff0c\u201c\u53ea\u8981\u6709\u8db3\u591f\u957f\u7684\u65f6\u95f4\u505a\u51c6\u5907\uff0c\u5c06\u6240\u6709\u53ef\u80fd\u7684\u56e0\u7d20\u90fd\u8003\u8651\u5728\u5185\uff0c\u4e8b\u60c5\u81ea\u7136\u5c31\u4f1a\u53d8\u5f97\u5f88\u5bb9\u6613\u3002\u201d</p><p>\u9b54\u59ec\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u9b54\u9b3c\u5012\u4e5f\u633a\u64c5\u957f\u5236\u5b9a\u8ba1\u5212\uff0c\u53ef\u60dc\u6211\u662f\u4e2a\u7279\u4f8b\u3002\u6211\u4e0d\u559c\u6b22\u8ba1\u5212\uff0c\u89c9\u5f97\u90a3\u592a\u67af\u71e5\u65e0\u5473\uff0c\u800c\u4e14\u4f24\u8111\u7b4b\uff0c\u53ea\u80fd\u5076\u4e00\u4e3a\u4e4b\u3002\u76f8\u6bd4\u8d77\u6765\uff0c\u6211\u66f4\u559c\u6b22\u81c6\u6d4b\u3002\u201d</p><p>\u201c\u81c6\u6d4b\uff1f\u201d</p><p>\u201c\u5c31\u662f\u6ca1\u6709\u6839\u636e\u7684\u4e71\u731c\uff0c\u201d\u9b54\u59ec\u89e3\u91ca\uff0c\u201c\u8fd9\u6837\u6bd4\u8f83\u8f7b\u677e\u6709\u8da3\uff0c\u731c\u9519\u4e86\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u731c\u5bf9\u4e86\u5c31\u4f1a\u5f88\u9ad8\u5174\u2014\u2014\u800c\u4e14\u6211\u5728\u8fd9\u65b9\u9762\u7684\u8fd0\u6c14\u4e00\u5411\u4e0d\u9519\uff0c\u5f80\u5f80\u90fd\u4f1a\u731c\u5bf9\u3002\u201d</p><p>\u201c\u54e6\uff1f\u201d</p><p>\u201c\u73b0\u5728\u6b63\u597d\u6709\u7a7a\uff0c\u5c31\u8ba9\u6211\u6765\u731c\u4e00\u731c\u5427\uff0c\u201d\u9b54\u59ec\u8bf4\uff0c\u6258\u816e\u6c89\u601d\u4e86\u7247\u523b\uff0c\u201c\u55ef\uff0c\u6211\u731c\u4f60\u9a97\u4e86\u6211\uff0c\u8fd9\u4e2a\u9b54\u6cd5\u4e0d\u4f1a\u8ba9\u5979\u6210\u4e3a\u795e\u7947\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u8bf4\u5462\uff1f\u201d</p><p>\u9b54\u59ec\u683c\u683c\u5a07\u7b11\uff0c\u201c\u6211\u8bf4\u8fc7\u4e86\uff0c\u81c6\u6d4b\u5c31\u662f\u4e71\u731c\uff0c\u662f\u6ca1\u6709\u6839\u636e\u7684\u3002\u201d</p><p>\u5965\u5609\u83b1\u65af\u76ef\u7740\u9b54\u59ec\uff0c\u201c\u90a3\u4e48\u4f60\u8fd8\u6709\u5176\u4ed6\u731c\u6d4b\u5417\uff1f\u201d</p><p>\u201c\u6709\u554a\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u6211\u731c\u4f60\u7684\u8ba1\u5212\u4e0d\u4f1a\u6210\u529f\u3002\u201d</p><p>\u201c\u4ecd\u7136\u662f\u6ca1\u6709\u6839\u636e\u7684\u4e71\u731c\uff1f\u201d</p><p>\u201c\u4e0d\uff0c\u8fd9\u6b21\u662f\u6709\u6839\u636e\u7684\uff0c\u201d\u624e\u745e\u5c14\u7b11\u7740\u8bf4\uff0c\u201c\u65e0\u8bba\u4f60\u4f5c\u4f55\u6253\u7b97\uff0c\u81f3\u5c11\u6709\u4e00\u70b9\u662f\u786e\u5b9a\u7684\uff0c\u4f60\u5fc5\u987b\u4f7f\u7528\u2018\u5316\u8eab\u2019\u5f3a\u5236\u5bc6\u65af\u62c9\u5723\u8005\u964d\u4e34\uff0c\u6ca1\u9519\u5427\u3002\u4f46\u8fd9\u4e00\u70b9\u662f\u505a\u4e0d\u5230\u7684\uff0c\u6240\u4ee5\u4f60\u7684\u8ba1\u5212\u5f53\u7136\u6ce8\u5b9a\u5931\u8d25\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u505a\u4e0d\u5230\uff1f\u201d\u5965\u5609\u83b1\u65af\u6c89\u58f0\u95ee\uff0c\u201c\u6211\u7684\u9b54\u6cd5\u9635\u6709\u4ec0\u4e48\u7f3a\u9677\uff1f\u201d</p><p>\u201c\u5e94\u8be5\u6ca1\u6709\u5427\uff0c\u5373\u4fbf\u6709\uff0c\u6211\u4e0d\u662f\u8fd9\u65b9\u9762\u7684\u4e13\u5bb6\uff0c\u4e5f\u770b\u4e0d\u51fa\u6765\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u4f46\u4f60\u5f04\u9519\u4e86\u65b9\u5411\u3002\u95ee\u9898\u4e0d\u5728\u4f60\u8fd9\u8fb9\uff0c\u5728\u4e8e\u5bc6\u65af\u62c9\u90a3\u8fb9\u3002\u5723\u8005\u964d\u4e34\uff0c\u524d\u63d0\u662f\u5979\u8fd8\u6ca1\u6709\u964d\u4e34\u5bf9\u5427\uff0c\u201d\u9b54\u59ec\u7684\u5634\u89d2\u52fe\u8d77\uff0c\u9732\u51fa\u72e1\u733e\u7684\u5fae\u7b11\uff0c\u201c\u5982\u679c\u5979\u65e9\u5df2\u7ecf\u964d\u4e34\u51e1\u95f4\u4e86\u5462\uff1f\u201d</p><p>\u201c\u4f60\u8bf4\u4ec0\u4e48\uff01\u201d</p><p>\u201c\u6211\u662f\u8bf4\uff0c\u5bc6\u65af\u62c9\u65e9\u5df2\u964d\u4e34\u51e1\u95f4\uff0c\u5c31\u5728\u6b64\u5730\uff0c\u201d\u624e\u745e\u5c14\u8bf4\uff0c\u201c\u4e4b\u524d\u6211\u5728\u9634\u5f71\u9547\u89c1\u8fc7\u5979\u3002\u867d\u7136\u6211\u548c\u5979\u4e0d\u719f\uff0c\u4f46\u8fdc\u53e4\u795e\u660e\u7684\u6c14\u606f\uff0c\u6211\u5e94\u8be5\u8fd8\u662f\u4e0d\u4f1a\u8ba4\u9519\u7684\u3002\u201d</p><p>\u5965\u5609\u83b1\u65af\u8fd9\u6b21\u662f\u771f\u6b63\u7684\u5927\u5403\u4e00\u60ca\uff0c\u5979\u5fc3\u5ff5\u7535\u8f6c\uff0c\u610f\u8bc6\u5230\u4e8b\u60c5\u7684\u4e25\u91cd\u6027\uff0c\u6025\u5fd9\u8981\u5c06\u6b63\u5728\u8fd0\u884c\u4e2d\u7684\u9b54\u6cd5\u9635\u505c\u6b62\u4e0b\u6765\u3002\u5979\u4e8b\u5148\u51c6\u5907\u4e86\u591a\u79cd\u9884\u6848\uff0c\u5176\u4e2d\u4e5f\u6709\u201c\u53d1\u751f\u7a81\u7136\u72b6\u51b5\uff0c\u5fc5\u987b\u7acb\u523b\u4e2d\u6b62\u65bd\u6cd5\u201d\u7684\u60c5\u5f62\uff0c\u6240\u4ee5\u8fd9\u539f\u672c\u5e76\u975e\u96be\u4e8b\u2014\u2014\u4f46\u5979\u73b0\u5728\u662f\u5e7d\u7075\u5f62\u6001\uff0c\u65bd\u6cd5\u5fc5\u987b\u501f\u52a9\u547d\u8fd0\u957f\u591c\uff0c\u8fd9\u5bfc\u81f4\u5979\u7684\u52a8\u4f5c\u6162\u4e86\u4e00\u62cd\u3002\u800c\u5c31\u662f\u8fd9\u4e00\u7f13\uff0c\u9b54\u6cd5\u9635\u9661\u5730\u5267\u70c8\u9707\u52a8\uff0c\u8054\u7ed3\u5185\u5c42\u754c\u7684\u516d\u4e2a\u4f4d\u9762\u901a\u9053\u65e0\u6cd5\u63a7\u5236\u5730\u81ea\u884c\u6269\u5927\uff0c\u539f\u672c\u89c4\u5f8b\u65e0\u6bd4\u7684\u80fd\u91cf\u5239\u90a3\u95f4\u53d8\u5f97\u72c2\u66b4\u8d77\u6765\uff0c\u4eff\u4f5b\u5e73\u9759\u7684\u6d77\u9762\u7a81\u7136\u5377\u8d77\u6ed4\u5929\u5de8\u6d6a\uff0c\u51b2\u7834\u9650\u5236\uff0c\u78be\u788e\u675f\u7f1a\uff0c\u5982\u6d2a\u6c34\u51b3\u5824\u822c\u6c6a\u6d0b\u6063\u8086\uff0c\u5c06\u9b54\u6cd5\u9635\u4e2d\u7684\u4e00\u5207\uff0c\u5305\u62ec\u73ca\u5609\uff0c\u5305\u62ec\u83b1\u62c9\uff0c\u5305\u62ec\u5965\u5609\u83b1\u65af\u5728\u5185\uff0c\u5c3d\u6570\u6df9\u6ca1\u3002</p></article>"}}}]);
