(window.webpackJsonp=window.webpackJsonp||[]).push([[536],{558:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e00\u767e\u4e00\u5341\u516b\u8282 \u65f6\u95f4\u505c\u6b62",content:"<article><h2>\u7b2c\u4e00\u767e\u4e00\u5341\u516b\u8282 \u65f6\u95f4\u505c\u6b62</h2><p>\u743c\u6069\u89c9\u5f97\u773c\u524d\u7684\u5c40\u9762\u4e00\u7247\u6df7\u4e71\uff0c\u8ba9\u4ed6\u7406\u4e0d\u6e05\u5934\u7eea\u3002\u8428\u9a6c\u65af\u7279\u5174\u81f4\u52c3\u52c3\u5730\u51c6\u5907\u5a5a\u793c\uff0c\u743c\u6069\u7531\u6b64\u63a8\u8bba\u8001\u5deb\u5996\u4e0e\u9b54\u6cd5\u5973\u795e\u6697\u4e2d\u52fe\u7ed3\uff0c\u751a\u81f3\u5973\u795e\u5df2\u7ecf\u5723\u8005\u964d\u4e34\uff1b\u8fd9\u4e2a\u63a8\u6d4b\u4f3c\u4e4e\u662f\u88ab\u8bc1\u5b9e\u4e86\uff0c\u8428\u9a6c\u65af\u7279\u627f\u8ba4\u4ed6\u83b7\u5f97\u5973\u795e\u7684\u795e\u8c15\uff08\u867d\u7136\u662f\u68a6\u4e2d\uff0c\u800c\u4e14\u795e\u8c15\u7684\u5185\u5bb9\u660e\u663e\u6709\u95ee\u9898\uff09\uff0c\u5973\u795e\u73b0\u5728\u4e5f\u5df2\u7ecf\u73b0\u8eab\uff1b\u4f46\u8428\u9a6c\u65af\u7279\u5374\u53c8\u662f\u4e00\u526f\u5b8c\u5168\u4e0d\u8ba4\u8bc6\u5973\u795e\u7684\u6a21\u6837\uff0c\u751a\u81f3\u8d28\u7591\u5176\u8eab\u4efd\u7684\u771f\u5b9e\u6027\u2014\u2014\u8fd9\u4e00\u5207\u4f3c\u4e4e\u76f8\u4e92\u51b2\u7a81\uff0c\u65e0\u6cd5\u89e3\u91ca\uff0c\u5b8c\u5168\u5c31\u662f\u4e00\u56e2\u6df7\u4e71\uff0c\u4f46\u743c\u6069\u5374\u53c8\u4e0b\u610f\u8bc6\u5730\u89c9\u5f97\u7b54\u6848\u5c31\u5728\u5176\u4e2d\uff0c\u8c1c\u5e95\u663e\u800c\u6613\u89c1\uff0c\u53ea\u662f\u81ea\u5df1\u6ca1\u6709\u4ed4\u7ec6\u53bb\u601d\u8003\u7f62\u4e86\u3002</p><p>\u7136\u800c\u4ed6\u5df2\u7ecf\u6ca1\u6709\u65f6\u95f4\u53bb\u4ed4\u7ec6\u601d\u8003\u3002</p><p>\u201c\u5361\u8389\u5e15\u4e1d\u201d\uff0c\u6216\u8005\u8bf4\u5e73\u80f8\u7684\u9b54\u6cd5\u5973\u795e\uff0c\u6ca1\u6709\u7b2c\u4e00\u65f6\u95f4\u53bb\u5904\u7406\u8428\u9a6c\u65af\u7279\uff0c\u5374\u62ac\u624b\u671d\u743c\u6069\u53d1\u51fa\u4e86\u4e00\u9053\u5149\u7bad\u3002\u743c\u6069\u6025\u5fd9\u95ea\u907f\uff0c\u4f46\u5149\u7bad\u5982\u5f71\u968f\u5f62\uff0c\u65e0\u6cd5\u6446\u8131\u3002\u4e00\u76f4\u8ddf\u968f\u7740\u743c\u6069\u7684\u84dd\u8774\u8776\u98de\u4e86\u8d77\u6765\uff0c\u5f20\u5f00\u53cc\u7ffc\uff0c\u6321\u4f4f\u5149\u7bad\u3002\u9b54\u6cd5\u5973\u795e\u51b7\u7b11\u4e86\u4e00\u58f0\uff0c\u201c\u9b54\u8776\u4e4b\u543b\uff0c\u5979\u8fd8\u771f\u5728\u610f\u4f60\uff0c\u771f\u6709\u8da3\u3002\u201d</p><p>\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u77e5\u9053\u5f88\u591a\u9b54\u9b3c\u53ef\u4ee5\u60df\u5999\u60df\u8096\u5730\u6a21\u62df\u51e1\u4eba\u7684\u60c5\u611f\uff0c\u4f46\u90a3\u7ec8\u7a76\u53ea\u662f\u6a21\u62df\u800c\u5df2\uff0c\u624e\u745e\u5c14\u5374\u662f\u771f\u7684\u628a\u81ea\u5df1\u50ac\u7720\u6210\u4e86\u4e00\u4e2a\u51e1\u4eba\u5973\u5b50\uff0c\u6211\u662f\u5e94\u8be5\u88ab\u5979\u611f\u52a8\u5462\uff0c\u8fd8\u662f\u5e94\u8be5\u8bf4\u5979\u611a\u8822\uff1f\u201d\u5973\u795e\u8bf4\uff0c\u201c\u4e0d\u8fc7\u8fd9\u4e9b\u90fd\u65e0\u5173\u7d27\u8981\u4e86\u3002\u5947\u68b0\u5e08\uff0c\u4f60\u65e9\u8be5\u6b7b\u53bb\uff0c\u504f\u504f\u53c8\u4ece\u51a5\u754c\u722c\u56de\u6765\uff0c\u8fd9\u6837\u4f1a\u7ed9\u5f88\u591a\u4eba\u9020\u6210\u56f0\u6270\u7684\u3002\u4e3a\u4e86\u8fd9\u4e2a\u4e16\u754c\u7684\u548c\u5e73\uff0c\u4f60\u8fd8\u662f\u518d\u6b7b\u4e00\u6b21\u5427\u3002\u201d</p><p>\u5979\u6325\u4e86\u6325\u624b\uff0c\u518d\u6b21\u53d1\u51fa\u5149\u7bad\uff0c\u5728\u4e2d\u9014\u4e00\u5206\u4e3a\u4e8c\uff0c\u5176\u4e2d\u4e00\u652f\u7f20\u4f4f\u4e86\u8774\u8776\uff0c\u53e6\u4e00\u652f\u4e58\u673a\u5c04\u5411\u743c\u6069\u3002\u743c\u6069\u8fd9\u6b21\u6709\u4e86\u51c6\u5907\uff0c\u6b63\u8981\u53cd\u51fb\uff0c\u6885\u83f2\u65af\u62a2\u5148\u4e00\u6b65\uff0c\u6321\u5728\u4ed6\u7684\u8eab\u524d\uff0c\u4e00\u5251\u65a9\u51fa\u3002</p><p>\u91d1\u8272\u7684\u5149\u8292\u4ece\u6885\u83f2\u65af\u4f53\u5185\u8ff8\u53d1\u51fa\u6765\uff0c\u51dd\u805a\u6210\u5de8\u5927\u7684\u4eba\u5f62\u865a\u5f71\uff0c\u90a3\u662f\u4e00\u4f4d\u7f8e\u4e3d\u7684\u91d1\u53d1\u5973\u5b50\uff0c\u624b\u6301\u957f\u5251\uff0c\u82f1\u6c14\u51db\u51db\uff0c\u516d\u53ea\u5149\u7ffc\u663e\u793a\u51fa\u5979\u662f\u4e00\u4f4d\u5929\u4f7f\u3002\u4eff\u4f5b\u662f\u5f97\u5230\u52a0\u6301\uff0c\u6885\u83f2\u65af\u7684\u529b\u91cf\u9661\u7136\u589e\u5f3a\uff0c\u4e00\u5251\u65a9\u5728\u9b54\u6cd5\u5973\u795e\u53d1\u51fa\u7684\u5149\u7bad\u4e0a\uff0c\u5c06\u5b83\u9707\u5f97\u7c89\u788e\u3002</p><p>\u5973\u795e\u5fae\u5fae\u70b9\u5934\uff0c\u4f3c\u4e4e\u5728\u8868\u793a\u8d5e\u8bb8\uff0c\u201c\u4e0d\u9519\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u539f\u6765\u4f60\u5df2\u7ecf\u53ef\u4ee5\u5b8c\u6210\u2018\u76df\u7ea6\u2019\u4e86\u3002\u201d</p><p>\u201c\u8fd8\u4e0d\u884c\uff0c\u201d\u6885\u83f2\u65af\u7a33\u7a33\u63e1\u7740\u94f6\u5251\uff0c\u5de8\u5927\u7684\u5929\u4f7f\u865a\u5f71\u7b3c\u7f69\u7740\u5979\uff0c\u201c\u53ea\u662f\u80fd\u591f\u501f\u7528\u90e8\u5206\u529b\u91cf\u800c\u5df2\u3002\u201d</p><p>\u201c\u5feb\u4e86\u3002\u201d\u5973\u795e\u8bf4\u3002</p><p>\u743c\u6069\u542c\u5f97\u4e5f\u5f88\u60ca\u8bb6\uff0c\u4ed6\u90fd\u4e0d\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u96be\u602a\u6885\u83f2\u65af\u795e\u795e\u79d8\u79d8\u8bf4\u81ea\u5df1\u6709\u4e00\u5f20\u5e95\u724c\uff0c\u539f\u6765\u5c31\u662f\u6307\u8fd9\u4e2a\u3002</p><p>\u63d0\u5c14\u6559\u4f1a\u7684\u516d\u5927\u6559\u533a\uff0c\u5411\u6765\u5404\u884c\u5176\u662f\uff0c\u5404\u81ea\u4e3a\u653f\uff0c\u5e76\u65e0\u4ec0\u4e48\u7edf\u4e00\u9886\u5bfc\uff0c\u5f7c\u6b64\u4e4b\u95f4\u7684\u5dee\u5f02\u5176\u5b9e\u633a\u5927\u3002\u5c31\u6bd4\u5982\u540c\u6837\u662f\u63d0\u5c14\u5723\u6b66\u58eb\uff0c\u6df1\u6c34\u57ce\u6559\u533a\u51fa\u6765\u7684\uff0c\u548c\u8c37\u5730\u6559\u533a\u51fa\u6765\u7684\uff0c\u90a3\u5c31\u5b8c\u5168\u662f\u4e24\u4e2a\u7248\u672c\u3002\u636e\u8bf4\u662f\u7531\u4e8e\u5723\u5361\u7f2a\u5c14\u5973\u58eb\u7684\u7f18\u6545\uff0c\u8c37\u5730\u6559\u533a\u7684\u5723\u6b66\u58eb\u6709\u4e00\u9879\u7279\u522b\u7684\u80fd\u529b\uff0c\u662f\u5176\u4ed6\u6559\u533a\u5723\u6b66\u58eb\u90fd\u4e0d\u5177\u5907\u7684\uff0c\u5373\u5f53\u4ed6\u4eec\u8d44\u5386\u8db3\u591f\u6df1\u5e76\u4e14\u5bf9\u5723\u529b\u7684\u638c\u63e1\u8fbe\u5230\u4e00\u5b9a\u7a0b\u5ea6\u65f6\uff0c\u4fbf\u80fd\u591f\u4e0e\u5929\u5802\u5c71\u7684\u67d0\u4e00\u4f4d\u5929\u4f7f\u8ba2\u7acb\u76df\u7ea6\uff0c\u5728\u6709\u5fc5\u8981\u65f6\u53ec\u5524\u5176\u4e0b\u51e1\u76f8\u52a9\u3002\u90a3\u4f4d\u59b9\u63a7\u5723\u6b66\u58eb\u5e0c\u6b27\u5c31\u6709\u4e00\u4f4d\u5fa1\u5929\u4f7f\u76df\u53cb\uff0c\u6885\u83f2\u65af\u4e4b\u524d\u6ca1\u6709\uff0c\u4e00\u65b9\u9762\u662f\u56e0\u4e3a\u5979\u8d44\u5386\u4e0d\u591f\u6df1\uff0c\u53e6\u4e00\u65b9\u9762\u662f\u7531\u4e8e\u5df4\u5c14\u8fd9\u4e2a\u9690\u60a3\u5b58\u5728\uff0c\u5bfc\u81f4\u5979\u65e0\u6cd5\u52a8\u7528\u63d0\u5c14\u8d50\u4e88\u7684\u5723\u529b\uff0c\u66f4\u8c08\u4e0d\u4e0a\u638c\u63e1\u3002\u5982\u4eca\u968f\u7740\u65f6\u95f4\u63a8\u79fb\uff0c\u6885\u83f2\u65af\u9010\u6b65\u6d88\u5316\u6740\u622e\u795e\u529b\uff0c\u4f5c\u4e3a\u201c\u5723\u6b66\u58eb\u201d\u7684\u80fd\u529b\u6e10\u6e10\u6062\u590d\uff0c\u5b8c\u6210\u201c\u76df\u7ea6\u201d\u662f\u8fdf\u65e9\u7684\u4e8b\u60c5\uff0c\u4f46\u743c\u6069\u4e5f\u6ca1\u60f3\u5230\u4f1a\u8fd9\u4e48\u5feb\uff0c\u5979\u8fd8\u771f\u662f\u8fdb\u5c55\u795e\u901f\u3002</p><p>\u4e0d\u8fc7\u6885\u83f2\u65af\u8fdb\u6b65\u867d\u5feb\uff0c\u4f46\u4e0d\u5b8c\u5168\u7684\u5929\u4f7f\u53ec\u5524\uff0c\u663e\u7136\u8fd8\u662f\u8fdc\u8fdc\u4e0d\u8db3\u4ee5\u5bf9\u6297\u4e00\u4f4d\u6700\u53e4\u8001\u7684\u9ad8\u7b49\u795e\u3002\u9b54\u6cd5\u5973\u795e\u7b2c\u4e09\u6b21\u62ac\u8d77\u624b\uff0c\u84dd\u767d\u8272\u7684\u95ea\u7535\u4ece\u5979\u7684\u6307\u5c16\u53d1\u51fa\uff0c\u5148\u5c04\u7a7f\u4e86\u8774\u8776\uff0c\u63a5\u7740\u51fb\u4e2d\u6885\u83f2\u65af\u7684\u94f6\u5251\uff0c\u5c06\u5979\u9707\u98de\u51fa\u53bb\uff0c\u6700\u540e\u518d\u6b21\u5c04\u5411\u743c\u6069\u3002\u743c\u6069\u907f\u65e0\u53ef\u907f\uff0c\u5728\u95ea\u7535\u5373\u5c06\u51fb\u4e2d\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u7528\u529b\u634f\u788e\u4e86\u5965\u6c83\u7ed9\u4ed6\u7684\u9ab7\u9ac5\u5934\u9aa8\uff0c\u540c\u65f6\u4e0d\u987e\u4e00\u5207\u5730\u6fc0\u53d1\u51fa\u4e86\u4ed6\u6240\u62e5\u6709\u7684\u5168\u90e8\u795e\u529b\u3002</p><p>\u5deb\u5e08\u7684\u8eab\u5f71\u9661\u7136\u95f4\u4ece\u539f\u5730\u6d88\u5931\uff0c\u4e0b\u4e00\u77ac\u95f4\uff0c\u4ed6\u51fa\u73b0\u5728\u5973\u795e\u7684\u80cc\u540e\u3002\u84dd\u767d\u8272\u95ea\u7535\u8ddf\u968f\u743c\u6069\u7a7f\u8d8a\u7a7a\u95f4\uff0c\u51c6\u786e\u5730\u6253\u5728\u4ed6\u8eab\u4e0a\uff0c\u53d1\u51fa\u9707\u8033\u6b32\u804b\u7684\u5de8\u54cd\u3002\u5965\u6c83\u663e\u7136\u4e0d\u4f1a\u6599\u5230\u743c\u6069\u4f1a\u62db\u60f9\u4e0a\u4e00\u4f4d\u9ad8\u7b49\u795e\uff0c\u4f46\u4ed6\u7ed9\u5b66\u751f\u7684\u4fdd\u547d\u5b9d\u7269\u7684\u786e\u8d27\u771f\u4ef7\u5b9e\uff0c\u9b54\u6cd5\u5973\u795e\u7684\u8fd9\u4e00\u51fb\u7adf\u7136\u65e0\u529f\u800c\u8fd4\u3002\u743c\u6069\u4e58\u673a\u53d1\u52a8\u53cd\u51fb\uff0c\u5f71\u706b\u4ece\u4ed6\u7684\u638c\u5fc3\u5feb\u901f\u6d8c\u51fa\uff0c\u51dd\u805a\u6210\u4e00\u6761\u7070\u8272\u957f\u97ad\uff0c\u95ea\u70c1\u7684\u96f7\u9706\u6e38\u8d70\u5176\u4e0a\uff0c\u53d1\u51fa\u523a\u8033\u7684\u7206\u88c2\u58f0\uff0c\u5982\u6bd2\u86c7\u822c\u671d\u5973\u795e\u5c04\u53bb\u3002</p><p>\u9b54\u6cd5\u5973\u795e\u662f\u9b54\u7f51\u7684\u638c\u63a7\u8005\uff0c\u5f53\u5979\u9ad8\u5c45\u795e\u5ea7\u65f6\uff0c\u53ef\u4ee5\u968f\u5fc3\u6240\u6b32\u5c01\u5370\u4efb\u4f55\u4e00\u540d\u5deb\u5e08\u7684\u5965\u672f\u80fd\u529b\uff0c\u5c3d\u7ba1\u5979\u73b0\u5728\u662f\u5723\u8005\u5f62\u6001\uff0c\u7406\u8bba\u4e0a\u8bf4\u65e0\u6cd5\u8fd0\u7528\u6cd5\u5219\u7684\u529b\u91cf\uff0c\u4f46\u743c\u6069\u8fd8\u662f\u4e0d\u6562\u5192\u9669\u4f7f\u7528\u9b54\u6cd5\uff0c\u53ea\u6562\u4ee5\u795e\u529b\u8fdb\u653b\u3002\u5973\u795e\u663e\u7136\u6709\u4e9b\u51fa\u4e4e\u610f\u6599\uff0c\u4f46\u4e5f\u6ca1\u6709\u592a\u8fc7\u5728\u610f\uff0c\u5979\u53cd\u624b\u4e00\u63e1\uff0c\u4fbf\u5c06\u5f71\u706b\u957f\u97ad\u5316\u4f5c\u65e0\u5f62\u3002\u5723\u8005\u5f62\u6001\u7684\u795e\u7947\uff0c\u8f83\u4e4b\u4e8e\u5316\u8eab\uff0c\u867d\u7136\u4f1a\u6682\u65f6\u5931\u53bb\u201c\u795e\u201d\u7684\u8eab\u4efd\uff0c\u4f1a\u88ab\u51e1\u95f4\u6b66\u5668\u548c\u6cd5\u672f\u6240\u4f24\u5bb3\uff0c\u4e5f\u65e0\u6cd5\u4efb\u610f\u4f7f\u7528\u795e\u804c\u6cd5\u5219\uff0c\u4f46\u529b\u91cf\u5e76\u6ca1\u6709\u524a\u5f31\u592a\u591a\u3002\u743c\u6069\u4e1c\u62fc\u897f\u51d1\u7a83\u53d6\u6765\u7684\u90a3\u4e9b\u795e\u529b\uff0c\u5b8c\u5168\u65e0\u6cd5\u4e0e\u4e4b\u76f8\u6bd4\u62df\uff0c\u8f7b\u6613\u5c31\u88ab\u51fb\u6e83\u3002\u5973\u795e\u8f7b\u5f39\u624b\u6307\uff0c\u4e00\u70b9\u94f6\u706b\u98de\u5c04\u51fa\u6765\uff0c\u773c\u770b\u5c31\u8981\u51fb\u4e2d\u743c\u6069\u7684\u7709\u5fc3\uff0c\u5374\u88ab\u4e00\u53ea\u624b\u4ece\u65c1\u8fb9\u4f38\u51fa\u6765\uff0c\u4eff\u4f5b\u6293\u4e00\u53ea\u8424\u706b\u866b\u822c\u5c06\u94f6\u706b\u63e1\u4f4f\u4e86\u3002</p><p>\u201c\u4f60\u5e72\u4ec0\u4e48\uff0c\u6b23\u5e03\uff1f\u201d</p><p>\u9b54\u6cd5\u5973\u795e\u7684\u8bed\u6c14\u9887\u4e3a\u4e0d\u60a6\uff0c\u8fd9\u5f88\u6b63\u5e38\uff0c\u4e00\u4ef6\u4e8b\u60c5\u88ab\u4e00\u800c\u518d\u3001\u518d\u800c\u4e09\u5730\u6253\u65ad\uff0c\u6362\u4e86\u8c01\u90fd\u4f1a\u706b\u5192\u4e09\u4e08\u3002\u6b23\u5e03\u5374\u5b8c\u5168\u6ca1\u6709\u4f53\u8c05\u6bcd\u4eb2\u7684\u610f\u601d\uff0c\u201c\u4f60\u641e\u4ec0\u4e48\u540d\u5802\uff1f\u201d\u98ce\u66b4\u5973\u738b\u8d28\u95ee\uff0c\u201c\u653e\u7740\u8428\u9a6c\u65af\u7279\u5728\u8fd9\u91cc\u4e0d\u7ba1\uff0c\u8dd1\u6765\u627e\u4ed6\u9ebb\u70e6\u505a\u4ec0\u4e48\uff0c\u4ed6\u53c8\u6ca1\u5f97\u7f6a\u4f60\u5427\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u77e5\u9053\u8fd9\u4e2a\u4eba\u7684\u6765\u5386\uff0c\u201d\u5973\u795e\u8bf4\uff0c\u201c\u4ed6\u662f\u4e2a\u5371\u9669\u5206\u5b50\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\u4ed6\u662f\u6211\u5b66\u751f\u7684\u7537\u53cb\uff0c\u201d\u6b23\u5e03\u56de\u7b54\uff0c\u201c\u6211\u90a3\u4e2a\u5b66\u751f\u73b0\u5728\u4e0d\u5728\uff0c\u6211\u4f5c\u4e3a\u5979\u7684\u8001\u5e08\uff0c\u5982\u679c\u773c\u7741\u7741\u770b\u7740\u5979\u7684\u7537\u53cb\u88ab\u4f60\u6740\u6389\uff0c\u5230\u65f6\u5019\u6211\u600e\u4e48\u5411\u5979\u4ea4\u4ee3\u3002\u201d</p><p>\u5973\u795e\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u4f60\u8fd8\u662f\u548c\u4ee5\u524d\u4e00\u6837\uff0c\u603b\u662f\u6709\u4e9b\u83ab\u540d\u5176\u5999\u7684\u575a\u6301\u3002\u201d</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u4e00\u53ea\u900f\u660e\u5de8\u638c\u7a81\u7136\u51fa\u73b0\uff0c\u91cd\u91cd\u5c06\u6b23\u5e03\u62cd\u98de\u51fa\u53bb\u3002\u201c\u5b69\u5b50\u4e0d\u542c\u8bdd\uff0c\u7236\u6bcd\u5e94\u8be5\u53ca\u65f6\u7ba1\u6559\uff0c\u201d\u5973\u795e\u6de1\u6de1\u5730\u8bf4\uff0c\u201c\u5426\u5219\u53ea\u4f1a\u5bb3\u4eba\u5bb3\u5df1\u3002\u201d</p><p>\u5979\u518d\u6b21\u5c06\u76ee\u5149\u8f6c\u5230\u743c\u6069\u8eab\u4e0a\uff0c\u201c\u4f60\u8fd8\u6709\u5176\u4ed6\u5e2e\u624b\u5417\uff1f\u201d</p><p>\u743c\u6069\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u6211\u5e94\u8be5\u4e0d\u662f\u4f60\u7684\u9996\u8981\u654c\u4eba\u5427\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u6211\u7684\u654c\u4eba\uff0c\u4f60\u662f\u8fd9\u4e2a\u4e16\u754c\u7684\u654c\u4eba\uff0c\u201d\u5973\u795e\u8bf4\uff0c\u201c\u6211\u53ea\u662f\u66ff\u5929\u884c\u9053\u3002\u201d</p><p>\u94f6\u8272\u7684\u5149\u5728\u5979\u7684\u638c\u5fc3\u805a\u96c6\uff0c\u84c4\u52bf\u5f85\u53d1\uff0c\u9b54\u6cd5\u5973\u795e\u663e\u7136\u7ec8\u4e8e\u51b3\u5b9a\u5168\u529b\u51fa\u624b\uff0c\u5373\u4fbf\u6885\u83f2\u65af\u3001\u6b23\u5e03\u518d\u6765\u963b\u62e6\uff0c\u4e5f\u53ea\u4f1a\u88ab\u4e00\u5e76\u541e\u566c\u3002\u743c\u6069\u7684\u8111\u4e2d\u8f6c\u77ac\u95f4\u95ea\u8fc7\u767e\u5341\u4e2a\u5ff5\u5934\uff0c\u5374\u6ca1\u6709\u4e00\u6837\u80fd\u591f\u5e94\u4ed8\u773c\u524d\u7684\u5c40\u9762\uff0c\u552f\u4e00\u7684\u529e\u6cd5\u4f3c\u4e4e\u5c31\u662f\u9003\u3002\u7b2c\u4e94\u79d8\u5668\u7684\u7981\u5236\u5bf9\u201c\u7fd4\u9f99\u201d\u800c\u8a00\u5f62\u540c\u865a\u8bbe\uff0c\u4ed6\u53ef\u4ee5\u501f\u6b64\u8131\u8eab\uff0c\u4f46\u8fd9\u6837\u4e00\u6765\uff0c\u5c31\u662f\u5c06\u6240\u6709\u4eba\u90fd\u629b\u4e0b\u4e86\u3002\u4ee5\u9b54\u6cd5\u5973\u795e\u73b0\u5728\u8868\u73b0\u51fa\u7684\u5bf9\u743c\u6069\u7684\u654c\u610f\uff0c\u5979\u5bf9\u6885\u83f2\u65af\uff0c\u5bf9\u73ca\u5609\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4ec0\u4e48\u6b63\u9762\u770b\u6cd5\u5427\u3002</p><p>\u8fd9\u662f\u4e2a\u4ee4\u4eba\u96be\u4ee5\u6289\u62e9\u7684\u95ee\u9898\uff1b\u5e78\u8fd0\u7684\u662f\uff0c\u4ed6\u5e76\u4e0d\u9700\u8981\u505a\u9009\u62e9\u3002</p><p>\u5973\u795e\u5c06\u94f6\u8272\u5149\u7403\u63e1\u5728\u624b\u4e2d\uff0c\u5374\u5e76\u672a\u7acb\u523b\u53d1\u51fa\u3002\u201c\u8fd8\u4e0d\u51fa\u6765\u5417\uff1f\u201d\u5979\u7a81\u7136\u9ad8\u58f0\u8bf4\uff0c\u201c\u8fd9\u662f\u4f60\u7684\u5730\u76d8\uff0c\u6211\u4e5f\u7b97\u662f\u5ba2\u4eba\uff0c\u8eb2\u8eb2\u85cf\u85cf\uff0c\u4e0d\u662f\u5f85\u5ba2\u4e4b\u9053\u5427\u3002\u201d</p><p>\u77d7\u7acb\u5728\u5730\u4e0a\u7684\u83f1\u5f62\u6c34\u6676\u7a81\u7136\u8ff8\u53d1\u51fa\u523a\u773c\u7684\u91d1\u5149\uff0c\u4e00\u4e2a\u5e9e\u5927\u7684\u8eab\u5f71\u4ece\u91cc\u9762\u6d6e\u73b0\u51fa\u6765\uff0c\u548c\u534a\u7a7a\u4e2d\u7684\u9b54\u6cd5\u5973\u795e\u9065\u9065\u76f8\u5bf9\u3002\u90a3\u662f\u8428\u9a6c\u65af\u7279\uff0c\u4f46\u4f3c\u4e4e\u54ea\u91cc\u53c8\u4e0d\u592a\u5bf9\u52b2\uff0c\u4ed6\u7684\u5934\u53d1\u989c\u8272\u53d8\u6df1\u4e86\u4e00\u70b9\uff0c\u80e1\u987b\u597d\u50cf\u4e5f\u53d8\u957f\u4e86\u4e00\u70b9\uff0c\u66f4\u5173\u952e\u7684\u662f\uff0c\u4ed6\u7684\u201c\u6c14\u8d28\u201d\u4e0d\u4e00\u6837\u4e86\uff0c\u4e4b\u524d\u90a3\u79cd\u9690\u9690\u7684\u75af\u72c2\u6d88\u5931\u4e0d\u89c1\uff0c\u8138\u4e0a\u7b11\u772f\u772f\u7684\uff0c\u6574\u4e2a\u4eba\u770b\u8d77\u6765\u5c31\u50cf\u662f\u4e2a\u548c\u853c\u53ef\u4eb2\u7684\u8001\u4eba\u3002\u201c\u522b\u6fc0\u52a8\uff0c\u5973\u795e\u965b\u4e0b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6709\u4ec0\u4e48\u4e8b\u60c5\u53ef\u4ee5\u5750\u4e0b\u6765\u5546\u91cf\u561b\uff0c\u4e0d\u7528\u4e00\u4e0a\u6765\u5c31\u6253\u6253\u6740\u6740\u7684\uff0c\u591a\u4e0d\u548c\u8c10\u3002\u201d</p><p>\u90a3\u4e0d\u662f\u8428\u9a6c\u65af\u7279\u7684\u58f0\u97f3\u3002</p><p>\u9b54\u6cd5\u5973\u795e\u51b7\u7b11\uff0c\u201c\u4f60\u8bbe\u5c40\u5c06\u6211\u56f0\u5728\u6b64\u5904\uff0c\u5df2\u7ecf\u662f\u5c45\u5fc3\u4e0d\u826f\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u5546\u91cf\u7684\uff1f\u201d</p><p>\u201c\u8bef\u4f1a\uff0c\u8bef\u4f1a\uff0c\u201d\u8001\u4eba\u8fde\u5fd9\u8fa9\u89e3\uff0c\u201c\u6211\u53ef\u4ece\u6765\u6ca1\u6709\u9488\u5bf9\u60a8\u7684\u610f\u601d\uff0c\u5f04\u6210\u73b0\u5728\u8fd9\u79cd\u5c40\u9762\uff0c\u53ea\u80fd\u8bf4\u662f\u9634\u5dee\u9633\u9519\uff0c\u6211\u4e5f\u662f\u4e0d\u4e45\u524d\u624d\u77e5\u9053\u539f\u6765\u60a8\u5df2\u7ecf\u5927\u9a7e\u5149\u4e34\u6b64\u5730\u2014\u2014\u4e0d\u8fc7\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u201d\u4ed6\u4e0d\u77e5\u9053\u4ece\u54ea\u91cc\u62ff\u51fa\u4e00\u672c\u539a\u539a\u7684\u4e66\uff0c\u7ffb\u5f00\u5230\u5176\u4e2d\u4e00\u9875\uff0c\u6307\u7740\u4e0a\u9762\u7684\u6587\u5b57\uff0c\u201c\u6839\u636e\u300a\u5929\u754c\u4e0e\u5730\u72f1\u5173\u4e8e\u4e92\u76f8\u5c0a\u91cd\u4e3b\u6743\u4ee5\u53ca\u4e92\u4e0d\u4fb5\u72af\u7684\u8c05\u89e3\u5907\u5fd8\u5f55\u300b\u7b2c\u5341\u4e09\u6761\u7b2c\u4e8c\u6b3e\u7684\u7ea6\u5b9a\uff0c\u5305\u62ec\u60a8\u5728\u5185\u7684\u4e8c\u5341\u4e03\u4f4d\u795e\u7947\uff0c\u5728\u6ca1\u6709\u53d1\u52a8\u6218\u4e89\u5e76\u4e14\u6b63\u5f0f\u5ba3\u6218\u7684\u60c5\u51b5\u4e0b\uff0c\u672a\u7ecf\u4e5d\u72f1\u4e4b\u4e3b\u2014\u2014\u4e5f\u5c31\u662f\u672c\u4eba\u2014\u2014\u7684\u51c6\u8bb8\uff0c\u662f\u4e0d\u5e94\u8be5\u8fdb\u5165\u5730\u72f1\u7684\u3002\u201d</p><p>\u201c\u7ea6\u5b9a\u7684\u5185\u5bb9\u6211\u8bb0\u5f97\u5f88\u6e05\u695a\uff0c\u201d\u5973\u795e\u56de\u7b54\uff0c\u201c\u5b83\u7981\u6b62\u7684\u662f\u4f5c\u4e3a\u2018\u795e\u2019\u7684\u6211\uff0c\u800c\u6211\u73b0\u5728\u5e76\u975e\u795e\u7947\u3002\u201d</p><p>\u201c\u90a3\u662f\u56e0\u4e3a\u5728\u7b7e\u7f72\u8fd9\u4efd\u5907\u5fd8\u5f55\u7684\u65f6\u5019\uff0c\u538b\u6839\u5c31\u6ca1\u6709\u2018\u5723\u8005\u964d\u4e34\u2019\u8fd9\u56de\u4e8b\uff0c\u201d\u8001\u4eba\u6297\u8bae\uff0c\u201c\u4f60\u8fd9\u662f\u5728\u73a9\u5f04\u6587\u5b57\u6e38\u620f\uff0c\u4e25\u91cd\u7f3a\u4e4f\u5951\u7ea6\u7cbe\u795e\u3002\u201d</p><p>\u201c\u4f60\u4f5c\u4e3a\u9b54\u9b3c\u9886\u8896\uff0c\u8bf4\u8fd9\u79cd\u8bdd\u771f\u7684\u4e0d\u89c9\u5f97\u4e22\u8138\u5417\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u89c9\u5f97\u554a\uff0c\u201d\u8001\u4eba\u7684\u795e\u60c5\u5f02\u5e38\u65e0\u8f9c\uff0c\u201c\u6211\u4eec\u505a\u9b54\u9b3c\u7684\uff0c\u5c31\u662f\u8981\u8bb2\u89c4\u77e9\uff0c\u8bb2\u5951\u7ea6\uff0c\u8bb2\u4fe1\u7528\uff0c\u5426\u5219\u8ddf\u90a3\u4e9b\u795e\u7947\u8fd8\u6709\u4ec0\u4e48\u533a\u522b\u2014\u2014\u6211\u7ecf\u5e38\u8fd9\u4e48\u6559\u5bfc\u624b\u4e0b\u7684\u3002\u6709\u9519\u5417\uff1f\u201d</p><p>\u201c\u5e9f\u8bdd\u5c11\u8bf4\uff0c\u201d\u5973\u795e\u51b7\u51b7\u5730\u8bf4\uff0c\u201c\u4f60\u7a76\u7adf\u610f\u6b32\u4f55\u4e3a\uff1f\u201d</p><p>\u8001\u4eba\u644a\u5f00\u624b\uff0c\u201c\u4e00\u5207\u7686\u5728\u60a8\u7684\u773c\u524d\uff0c\u6211\u6ca1\u6709\u4e1d\u6beb\u9690\u7792\u3002\u201d</p><p>\u201c\u51e1\u4eba\u6709\u53e5\u8bdd\uff0c\u8bf4\u4e16\u6613\u65f6\u79fb\uff0c\u4eca\u975e\u6614\u6bd4\u3002\u867d\u7136\u5bf9\u4e8e\u4f60\u6211\u800c\u8a00\uff0c\u6570\u5343\u5e74\u7684\u65f6\u5149\u4e5f\u4e0d\u7b97\u6f2b\u957f\uff0c\u4f46\u6709\u4e9b\u4e8b\u60c5\uff0c\u7684\u786e\u8fd8\u662f\u6539\u53d8\u4e86\uff0c\u201d\u5973\u795e\u8bf4\uff0c\u201c\u4f60\u82e5\u8fd8\u60f3\u91cd\u5efa\u5e7d\u51a5\uff0c\u518d\u5f00\u5730\u5e9c\uff0c\u90a3\u5c31\u662f\u8bf8\u795e\u4e4b\u516c\u654c\uff0c\u5929\u754c\u5c06\u4f1a\u4e0d\u60dc\u4e00\u5207\u53d1\u52a8\u6218\u4e89\uff0c\u4e5d\u72f1\u4ece\u6b64\u518d\u65e0\u5b81\u65e5\u2014\u2014\u8fd9\u4e9b\u4f60\u90fd\u5df2\u7ecf\u8003\u8651\u6e05\u695a\u4e86\u5417\uff1f\u201d</p><p>\u201c\u6253\u5c31\u6253\u5457\uff0c\u201d\u8001\u4eba\u4e0d\u4ee5\u4e3a\u7136\uff0c\u201c\u53cd\u6b63\u53c8\u4e0d\u662f\u7b2c\u4e00\u6b21\u4e86\u3002\u6211\u90a3\u5e2e\u624b\u4e0b\u4e5f\u8d8a\u6765\u8d8a\u4e0d\u542c\u8bdd\uff0c\u4e2a\u4e2a\u81ea\u884c\u5176\u662f\uff0c\u628a\u5730\u72f1\u5f04\u5f97\u4e4c\u70df\u7634\u6c14\uff0c\u5230\u5904\u90fd\u662f\u8fdd\u7ae0\u5efa\u7b51\u3002\u6253\u4e00\u4ed7\u6b63\u597d\uff0c\u4e58\u673a\u6e05\u7406\u6e05\u7406\uff0c\u514d\u5f97\u6211\u770b\u7740\u5fc3\u70e6\u3002\u201d</p><p>\u5973\u795e\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u201c\u65e2\u7136\u8fd9\u6837\uff0c\u90a3\u6211\u5c31\u6ca1\u4ec0\u4e48\u597d\u8bf4\u4e86\u3002\u201d</p><p>\u5979\u62ac\u8d77\u624b\uff0c\u94f6\u8272\u7684\u5149\u7403\u98de\u5c04\u800c\u51fa\uff0c\u7b14\u76f4\u5730\u51fb\u5411\u8001\u4eba\u3002\u8001\u4eba\u54c8\u54c8\u5927\u7b11\uff0c\u6beb\u4e0d\u8eb2\u95ea\uff0c\u94f6\u7403\u51c6\u786e\u5730\u7838\u4e2d\u4e86\uff0c\u5c06\u4ed6\u70b8\u6210\u65e0\u6570\u900f\u660e\u788e\u7247\u3002\u6bcf\u4e00\u5757\u788e\u7247\u4e2d\u90fd\u817e\u8d77\u5149\uff0c\u6c47\u805a\u6210\u4e00\u53ea\u91d1\u707f\u707f\u7684\u5de8\u86c7\uff0c\u5ffd\u800c\u94bb\u8fdb\u4e86\u83f1\u5f62\u6c34\u6676\u4e4b\u4e2d\uff0c\u6d88\u5931\u4e0d\u89c1\uff0c\u53ea\u7559\u4e0b\u7b11\u58f0\u4ecd\u7136\u5728\u5929\u5730\u4e4b\u95f4\u56de\u8361\uff0c\u6e10\u6e10\u8fdc\u53bb\u3002\u968f\u7740\u8001\u4eba\u7684\u6d88\u5931\uff0c\u516b\u4e2a\u5de8\u5927\u738b\u5ea7\u8fde\u540c\u9ed1\u5f71\u4e5f\u968f\u4e4b\u6d88\u5931\u4e86\uff0c\u4f46\u5374\u6709\u66f4\u591a\u7684\u9b54\u9b3c\u4ece\u865a\u7a7a\u4e2d\u6d8c\u73b0\uff0c\u524d\u8d74\u540e\u7ee7\u5730\u56f4\u653b\u8fc7\u6765\u3002</p><p>\u5973\u795e\u6447\u6447\u5934\uff0c\u4ece\u7a7a\u4e2d\u964d\u843d\u4e0b\u6765\uff0c\u5979\u7684\u8db3\u5c16\u521a\u521a\u63a5\u89e6\u5230\u5730\u9762\uff0c\u72c2\u98ce\u4fbf\u968f\u4e4b\u6d8c\u8d77\uff0c\u5411\u56db\u9762\u5439\u53bb\uff0c\u5c06\u4ee5\u5979\u4e3a\u4e2d\u5fc3\uff0c\u534a\u5f84\u5341\u91cc\u4e4b\u5185\u7684\u9b54\u9b3c\u5168\u90fd\u5439\u6210\u4e86\u7c89\u672b\uff0c\u6e05\u51fa\u4e86\u4e00\u7247\u5706\u5f62\u7a7a\u5730\u3002</p><p>\u51ef\u5c14\u672c\u7b49\u9009\u6c11\u8d76\u8fc7\u6765\uff0c\u89d0\u89c1\u5973\u795e\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u4ecd\u7136\u8fdc\u8fdc\u5730\u4fdd\u6301\u8ddd\u79bb\uff0c\u6212\u5907\u7740\uff0c\u6709\u8da3\u7684\u662f\uff0c\u548c\u4ed6\u4eec\u4e00\u6837\u505a\u6cd5\u7684\u8fd8\u6709\u4e00\u4e2a\u4eba\uff1a\u8ff7\u96fe\u5927\u5e08\u3002\u5973\u795e\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u4ec0\u4e48\u8bdd\u4e5f\u6ca1\u8bf4\u3002</p><p>\u201c\u5230\u5e95\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u6b23\u5e03\u7b2c\u4e00\u4e2a\u53d1\u95ee\uff0c\u201c\u8428\u9a6c\u65af\u7279\u600e\u4e48\u4f1a\u53d8\u6210\u4e86\u4e5d\u72f1\u4e4b\u4e3b\uff1f\u201d</p><p>\u201c\u53ea\u662f\u4e00\u4e2a\u6295\u5f71\uff0c\u201d\u5973\u795e\u56de\u7b54\uff0c\u201c\u90aa\u86c7\u662f\u8fd9\u4ef6\u4f0a\u739b\u65af\u5361\u79d8\u5668\u7684\u521b\u9020\u8005\u4e4b\u4e00\uff0c\u73b0\u5728\u770b\u6765\uff0c\u4ed6\u65e9\u6709\u51c6\u5907\uff0c\u5728\u79d8\u5668\u91cc\u7559\u4e0b\u4e86\u4e00\u4e2a\u6295\u5f71\u3002\u8428\u9a6c\u65af\u7279\u90a3\u4e2a\u7b28\u86cb\u5728\u8fd9\u91cc\u5f85\u5f97\u592a\u4e45\uff0c\u88ab\u4ed6\u76ef\u4e0a\uff0c\u88ab\u6084\u6084\u9644\u4f53\u4e86\u3002\u201d</p><p>\u201c\u4ea1\u7075\u4e5f\u4f1a\u88ab\u90aa\u9b54\u9644\u4f53\u5417\uff1f\u201d\u54c8\u8d1d\u5c14\u60ca\u8bb6\u5730\u95ee\u3002</p><p>\u90aa\u9b54\u53ef\u4ee5\u9644\u4f53\u51e1\u4eba\uff0c\u4ea1\u7075\u4ece\u4e25\u683c\u610f\u4e49\u4e0a\u8bf4\u4e5f\u8fd8\u662f\u51e1\u4eba\uff0c\u81ea\u7136\u53ef\u4ee5\u88ab\u9644\u4f53\u3002\u4f46\u8fd9\u53ea\u662f\u7406\u8bba\u4e0a\u6210\u7acb\uff0c\u5728\u5b9e\u8df5\u4e2d\uff0c\u4e00\u822c\u90fd\u662f\u8ba4\u4e3a\u53ea\u6709\u751f\u8005\u4f1a\u88ab\u90aa\u9b54\u9644\u4f53\uff0c\u4ea1\u7075\u662f\u514d\u75ab\u7684\uff0c\u81f3\u5c11\u6ca1\u6709\u542c\u8fc7\u8fd9\u79cd\u5148\u4f8b\u3002</p><p>\u201c\u4e00\u822c\u7684\u90aa\u9b54\u6216\u8bb8\u505a\u4e0d\u5230\uff0c\u4f46\u5bf9\u4e8e\u90aa\u86c7\u800c\u8a00\u4e0d\u662f\u969c\u788d\u3002\u4ed6\u662f\u6700\u4f18\u79c0\u7684\u5deb\u5e08\uff0c\u53ea\u8981\u7406\u8bba\u4e0a\u80fd\u591f\u6210\u7acb\uff0c\u4ed6\u5c31\u53ef\u4ee5\u8ba9\u5b83\u53d8\u6210\u73b0\u5b9e\u3002\u201d</p><p>\u54c8\u8d1d\u5c14\u4e0d\u8bf4\u8bdd\u4e86\u3002</p><p>\u201c\u90a3\u4ed6\u60f3\u5e72\u4ec0\u4e48\uff1f\u201d\u6b23\u5e03\u53c8\u95ee\uff0c\u201c\u4f60\u521a\u624d\u8bf4\u4ec0\u4e48\u91cd\u5efa\u5e7d\u51a5\u3001\u518d\u5f00\u5730\u5e9c\uff0c\u90a3\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u90aa\u86c7\u7684\u4e00\u4e2a\u8ba1\u5212\uff0c\u4f1a\u6bc1\u706d\u8fd9\u4e2a\u7269\u8d28\u754c\uff0c\u8ba9\u6240\u6709\u51e1\u4eba\u7684\u7075\u9b42\u5760\u5165\u5730\u72f1\uff0c\u201d\u5973\u795e\u7b80\u5355\u5730\u89e3\u91ca\uff0c\u201c\u4e0a\u6b21\u4ed6\u5931\u8d25\u4e86\uff0c\u6211\u4eec\u4ee5\u4e3a\u4e00\u5207\u5230\u6b64\u4e3a\u6b62\uff0c\u6ca1\u60f3\u5230\u4ed6\u4ecd\u672a\u653e\u5f03\uff0c\u800c\u4e14\u5c45\u7136\u771f\u7684\u88ab\u4ed6\u7b49\u5230\u4e86\u673a\u4f1a\u3002\u201d</p><p>\u5979\u5bf9\u743c\u6069\u8fdc\u8fdc\u5730\u62db\u4e86\u62db\u624b\uff0c\u201c\u5947\u68b0\u5e08\uff0c\u6211\u60f3\u4f60\u5e94\u8be5\u80fd\u591f\u542c\u61c2\u6211\u7684\u8bdd\u5427\u3002\u201d</p><p>\u6240\u6709\u4eba\u90fd\u770b\u7740\u743c\u6069\uff0c\u743c\u6069\u4fdd\u6301\u6c89\u9ed8\u3002</p><p>\u201c\u90aa\u86c7\u82e5\u662f\u6210\u529f\uff0c\u8fd9\u4e2a\u4e16\u754c\u5fc5\u5c06\u6bc1\u706d\uff0c\u6211\u76f8\u4fe1\u8fd9\u975e\u4f60\u6240\u613f\uff0c\u201d\u5973\u795e\u8bf4\uff0c\u201c\u5982\u679c\u4f60\u4e0d\u60f3\u51fa\u73b0\u8fd9\u79cd\u7ed3\u679c\uff0c\u552f\u4e00\u7684\u9014\u5f84\u5c31\u662f\u4e0e\u6211\u5408\u4f5c\u3002\u201d</p><p>\u201c\u600e\u4e48\u5408\u4f5c\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u90aa\u86c7\u9700\u8981\u4e00\u4e2a\u4eba\u6765\u5e2e\u4ed6\u5b8c\u6210\u4e4b\u524d\u90a3\u4efd\u4e2d\u65ad\u7684\u5951\u7ea6\uff0c\u201d\u5973\u795e\u8bf4\uff0c\u201c\u5f53\u4eca\u4e16\u4e0a\uff0c\u4f60\u662f\u552f\u4e00\u7684\u4eba\u9009\u3002\u6240\u4ee5\u6211\u8981\u963b\u6b62\u90aa\u86c7\uff0c\u53ea\u6709\u4e24\u79cd\u65b9\u6cd5\uff0c\u6216\u8005\u5e72\u6389\u4f60\uff0c\u6216\u8005\u5e72\u6389\u4ed6\u2014\u2014\u5f53\u7136\u4e0d\u662f\u4ed6\u672c\u4eba\uff0c\u800c\u662f\u8fd9\u4e2a\u5f71\u5b50\u3002\u524d\u8005\u4e00\u52b3\u6c38\u9038\uff0c\u540e\u8005\u81f3\u5c11\u4e5f\u53ef\u4ee5\u6682\u65f6\u89e3\u51b3\u95ee\u9898\u3002\u201d</p><p>\u201c\u542c\u8d77\u6765\u4f60\u5e94\u8be5\u9009\u62e9\u524d\u8005\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u5f88\u62b1\u6b49\uff0c\u6211\u8fd8\u4e0d\u6253\u7b97\u6b7b\uff0c\u5373\u4fbf\u4f60\u544a\u8bc9\u6211\u8fd9\u662f\u4e3a\u4e86\u62ef\u6551\u4e16\u754c\u3002\u6211\u7684\u786e\u559c\u6b22\u8fd9\u4e2a\u4e16\u754c\uff0c\u4e0d\u5e0c\u671b\u770b\u5230\u5b83\u6bc1\u706d\uff0c\u4f46\u6211\u66f4\u7231\u81ea\u5df1\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\u4f60\u80fd\u591f\u968f\u65f6\u79bb\u5f00\u8fd9\u91cc\uff0c\u201d\u5973\u795e\u8bf4\uff0c\u201c\u6ca1\u6709\u4ec0\u4e48\u7a7a\u95f4\u7981\u5236\u80fd\u591f\u771f\u6b63\u963b\u6321\u4e00\u4f4d\u2018\u7fd4\u9f99\u2019\uff0c\u8fd9\u70b9\u6211\u5f88\u6e05\u695a\u3002\u4f46\u4f60\u7684\u5973\u53cb\u8fd8\u5728\u8fd9\u91cc\uff0c\u6211\u60f3\u4f60\u4e0d\u4f1a\u4e22\u4e0b\u5979\u4e0d\u7ba1\u3002\u4e3a\u514d\u8bef\u89e3\uff0c\u6211\u5148\u8bf4\u660e\uff0c\u8fd9\u5e76\u975e\u5a01\u80c1\uff0c\u4ec5\u4ec5\u662f\u5728\u9648\u8ff0\u4e8b\u5b9e\u3002\u5982\u679c\u4f60\u4e0d\u60f3\u5931\u53bb\u8fd9\u4f4d\u7f8e\u4e3d\u7684\u5c0f\u59d0\uff0c\u6211\u5efa\u8bae\u4f60\u8ba4\u771f\u8003\u8651\u6211\u7684\u63d0\u8bae\u3002\u201d</p><p>\u201c\u4f60\u7684\u63d0\u8bae\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5e2e\u6211\u5e72\u6389\u90aa\u86c7\u7684\u5f71\u5b50\uff0c\u201d\u5973\u795e\u8bf4\uff0c\u201c\u4f5c\u4e3a\u56de\u62a5\uff0c\u5728\u4e8b\u6210\u4e4b\u540e\uff0c\u6211\u53ef\u4ee5\u5141\u8bb8\u4f60\u548c\u4f60\u7684\u5973\u53cb\u5b89\u5168\u79bb\u5f00\u3002\u6211\u77e5\u9053\u4f60\u7684\u5973\u53cb\u4e0d\u6b62\u4e00\u4e2a\uff0c\u5979\u4eec\u90fd\u5728\u5b89\u5168\u540d\u5355\u4e0a\uff0c\u5305\u62ec\u624e\u745e\u5c14\u3002\u201d</p><p>\u201c\u4f60\u89c9\u5f97\u6211\u4e3a\u4ec0\u4e48\u8981\u76f8\u4fe1\u4f60\u5462\uff1f\u201d</p><p>\u5973\u795e\u644a\u5f00\u624b\uff0c\u7528\u94f6\u706b\u51dd\u6210\u4e00\u9875\u7eb8\uff0c\u7136\u540e\u8f7b\u98d8\u98d8\u5730\u98de\u5411\u743c\u6069\uff0c\u201c\u8fd9\u662f\u6211\u4ee5\u795e\u540d\u4f5c\u51fa\u7684\u627f\u8bfa\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8fdd\u53cd\u627f\u8bfa\u4f1a\u635f\u5bb3\u6211\u7684\u795e\u6027\uff0c\u6240\u4ee5\u6211\u4e0d\u4f1a\u90a3\u4e48\u505a\u2014\u2014\u5173\u4e8e\u8fd9\u4e00\u70b9\uff0c\u4f60\u53ef\u4ee5\u5411\u6b23\u5e03\u6c42\u8bc1\u3002\u201d</p><p>\u6b23\u5e03\u70b9\u4e86\u70b9\u5934\uff0c\u793a\u610f\u5973\u795e\u6240\u8a00\u5c5e\u5b9e\u3002</p><p>\u743c\u6069\u8003\u8651\u4e86\u51e0\u79d2\u949f\uff0c\u201c\u600e\u4e48\u505a\uff1f\u201d</p><p>\u8bf4\u8bdd\u4e4b\u95f4\uff0c\u9b54\u9b3c\u5927\u519b\u5df2\u7ecf\u53c8\u51b2\u5230\u8fd1\u524d\u3002\u5973\u795e\u62ff\u51fa\u4e00\u67c4\u7fe0\u7eff\u8272\u7684\u77ed\u6756\uff0c\u4ea4\u7ed9\u54c8\u8d1d\u5c14\uff0c\u540c\u65f6\u544a\u8bc9\u4ed6\u5492\u8bed\u3002\u54c8\u8d1d\u5c14\u6301\u6756\u8bf5\u5492\uff0c\u65e0\u6570\u5149\u8292\u95ea\u95ea\u7684\u865a\u5f71\u4ece\u77ed\u6756\u4e2d\u98de\u51fa\u6765\uff0c\u5206\u8d74\u56db\u5468\uff0c\u5c06\u9b54\u9b3c\u4eec\u963b\u6321\u4f4f\u4e86\u3002\u201c\u575a\u6301\u4e00\u4f1a\uff0c\u201d\u5973\u795e\u8bf4\uff0c\u201c\u4e0d\u4f1a\u592a\u4e45\u3002\u201d</p><p>\u201c\u662f\u3002\u201d\u54c8\u8d1d\u5c14\u9886\u547d\u3002</p><p>\u5973\u795e\u5c06\u76ee\u5149\u8f6c\u56de\u5230\u5979\u7684\u9009\u6c11\u4eec\u8eab\u4e0a\uff0c\u201c\u90aa\u86c7\u975e\u5e38\u5f3a\u5927\uff0c\u5018\u82e5\u662f\u5728\u5730\u72f1\u4e4b\u4e2d\u4e0e\u5176\u53d1\u751f\u51b2\u7a81\uff0c\u6211\u4e00\u5b9a\u4f1a\u907f\u800c\u8fdc\u4e4b\u3002\u5e78\u597d\uff0c\u8fd9\u91cc\u7ec8\u7a76\u4e0d\u662f\u771f\u6b63\u7684\u5730\u72f1\uff0c\u53ea\u662f\u4e00\u4e2a\u4eff\u5236\u54c1\uff0c\u800c\u90aa\u86c7\u4e5f\u5e76\u975e\u672c\u4f53\uff0c\u53ea\u662f\u4e00\u4e2a\u6295\u5f71\uff0c\u4ed6\u5fc5\u987b\u501f\u52a9\u8428\u9a6c\u65af\u7279\u624d\u80fd\u6682\u65f6\u63a7\u5236\u79d8\u5668\u3002\u6240\u4ee5\u65b9\u6cd5\u5f88\u7b80\u5355\uff1a\u53ea\u8981\u5c06\u90a3\u4e2a\u7b28\u86cb\u5e72\u6389\u5c31\u884c\uff0c\u79d8\u5668\u5931\u53bb\u64cd\u63a7\u8005\uff0c\u9886\u57df\u5c31\u4f1a\u81ea\u7136\u5d29\u6e83\uff0c\u90aa\u86c7\u4e5f\u53ea\u80fd\u8001\u8001\u5b9e\u5b9e\u9000\u56de\u79d8\u5668\u4e4b\u4e2d\u3002\u201d</p><p>\u201c\u4f60\u662f\u4e0d\u662f\u5f04\u9519\u4e86\u4e00\u4ef6\u4e8b\uff0c\u201d\u743c\u6069\u5fcd\u4e0d\u4f4f\u8bf4\uff0c\u201c\u63a7\u5236\u79d8\u5668\u7684\u4e0d\u662f\u90aa\u86c7\u6216\u8005\u8428\u9a6c\u65af\u7279\uff0c\u662f\u4e00\u4f4d\u51ef\u745f\u7433\u5c0f\u59d0\u3002\u201d</p><p>\u201c\u6211\u6ca1\u5f04\u9519\uff0c\u662f\u4f60\u7684\u6d88\u606f\u592a\u4e0d\u7075\u901a\uff0c\u201d\u5973\u795e\u8bf4\uff0c\u201c\u4e94\u5206\u949f\u4e4b\u524d\uff0c\u90a3\u4f4d\u51ef\u745f\u7433\u5c0f\u59d0\u5df2\u7ecf\u6d88\u5931\u4e86\uff0c\u73b0\u5728\u6b63\u5728\u63a7\u5236\u79d8\u5668\u7684\uff0c\u662f\u9644\u4f53\u5728\u8428\u9a6c\u65af\u7279\u8eab\u4e0a\u7684\u90aa\u86c7\u3002\u201d</p><p>\u201c\u51ef\u745f\u7433\u6d88\u5931\u4e86\uff1f\u201d\u743c\u6069\u5927\u5403\u4e00\u60ca\uff0c\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u5973\u795e\u6ca1\u6709\u56de\u7b54\u8fd9\u4e2a\u95ee\u9898\uff0c\u201c\u90aa\u86c7\u73b0\u5728\u5c31\u5728\u8fd9\u4e2a\u6c34\u6676\u91cc\uff0c\u5f85\u4f1a\u6211\u4f1a\u653b\u51fb\u5b83\uff0c\u5c06\u90aa\u86c7\u903c\u51fa\u6765\uff0c\u5e76\u4e14\u5c06\u4ed6\u4eec\u6682\u65f6\u5206\u79bb\u5f00\u6765\uff0c\u201d\u5979\u5bf9\u9009\u6c11\u4eec\u8bf4\uff0c\u201c\u8981\u505a\u5230\u8fd9\u70b9\uff0c\u6211\u9700\u8981\u534f\u52a9\u3002\u201d</p><p>\u201c\u6211\u4eec\u4f1a\u5168\u529b\u4ee5\u8d74\u3002\u201d\u51ef\u5c14\u672c\u8bf4\u3002</p><p>\u201c\u4e0d\uff0c\u4e0d\u662f\u4f60\uff0c\u7531\u98ce\u66b4\u548c\u6b23\u5e03\u6765\u534f\u52a9\u6211\uff0c\u5979\u4eec\u662f\u6211\u7684\u5973\u513f\uff0c\u529b\u91cf\u66f4\u5bb9\u6613\u5951\u5408\uff0c\u201d\u5973\u795e\u8bf4\uff0c\u201c\u81f3\u4e8e\u4f60\u53e6\u6709\u4efb\u52a1\u3002\u6211\u6709\u628a\u63e1\u5c06\u90aa\u86c7\u548c\u8428\u9a6c\u65af\u7279\u5206\u79bb\uff0c\u4f46\u4f1a\u5f88\u77ed\u6682\uff0c\u53ef\u80fd\u53ea\u6709\u4e94\u516d\u79d2\u949f\uff0c\u751a\u81f3\u66f4\u5c11\uff0c\u5fc5\u987b\u5728\u8fd9\u4e2a\u65f6\u95f4\u4e4b\u5185\u5c06\u8428\u9a6c\u65af\u7279\u5e72\u6389\uff0c\u201d\u5973\u795e\u671d\u743c\u6069\u770b\u8fc7\u6765\uff0c\u201c\u8fd9\u5c31\u8981\u770b\u4f60\u4e86\uff0c\u5947\u68b0\u5e08\u3002\u201d</p><p>\u8fd9\u662f\u67d0\u79cd\u65b0\u5f0f\u73a9\u7b11\u5417\uff1f\u8ba9\u743c\u6069\u5728\u51e0\u79d2\u4e2d\u4e4b\u5185\u5e72\u6389\u8428\u9a6c\u65af\u7279\uff1f\u5e94\u8be5\u662f\u53cd\u8fc7\u6765\uff0c\u8981\u743c\u6069\u5728\u8428\u9a6c\u65af\u7279\u624b\u4e0b\u575a\u6301\u51e0\u79d2\u624d\u5bf9\u5427\u3002</p><p>\u201c\u60a8\u592a\u9ad8\u770b\u6211\u4e86\uff0c\u201d\u743c\u6069\u63a8\u8131\uff0c\u4ed6\u65e0\u610f\u5145\u5f53\u70ae\u7070\uff0c\u201c\u6211\u53ea\u662f\u4e2a\u5c0f\u5deb\u5e08\u800c\u5df2\uff0c\u60a8\u7684\u90e8\u4e0b\u65e0\u8bba\u54ea\u4e00\u4e2a\u90fd\u6bd4\u6211\u5f3a\u5f97\u591a\u3002\u201d</p><p>\u201c\u6211\u542c\u8bf4\u4f60\u4e4b\u524d\u6253\u8d25\u8fc7\u8428\u9a6c\u65af\u7279\u3002\u201d</p><p>\u201c\u5e76\u4e0d\u662f\u53ea\u6709\u6211\uff0c\u6b23\u5e03\u5973\u58eb\u4e5f\u5728\u573a\uff0c\u800c\u4e14\u90a3\u6b21\u4e5f\u53ea\u662f\u8fd0\u6c14\u597d\u800c\u5df2\u3002\u201d</p><p>\u201c\u5e78\u8fd0\u539f\u672c\u5c31\u662f\u6700\u5f3a\u5927\u7684\u9b54\u6cd5\uff0c\u201d\u5973\u795e\u8bf4\uff0c\u201c\u8fd9\u4e00\u6b21\u4e5f\u540c\u6837\u5982\u6b64\u3002\u201d</p><p>\u743c\u6069\u51b7\u7b11\u4e00\u58f0\uff0c\u201c\u6211\u662f\u4e2a\u51e1\u4eba\uff0c\u6bd4\u4e0d\u4e0a\u795e\u660e\u7684\u9ad8\u77bb\u8fdc\u77a9\uff0c\u66f4\u4e0d\u559c\u6b22\u6545\u5f04\u7384\u865a\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5982\u679c\u4f60\u613f\u610f\u89e3\u91ca\uff0c\u90a3\u4e48\u6211\u6d17\u8033\u606d\u542c\uff0c\u5426\u5219\u6055\u4e0d\u5949\u966a\u4e86\u3002\u201d</p><p>\u5973\u795e\u6beb\u4e0d\u52a8\u6012\uff0c\u201c\u6211\u7684\u786e\u5e94\u8be5\u8bf4\u5f97\u66f4\u6e05\u695a\u4e00\u70b9\u3002\u90aa\u86c7\u7684\u5f71\u5b50\u9644\u4f53\u8428\u9a6c\u65af\u7279\uff0c\u6b63\u5728\u63a7\u5236\u79d8\u5668\uff0c\u8fd9\u8ba9\u8428\u9a6c\u65af\u7279\u6682\u65f6\u83b7\u5f97\u4e86\u4e00\u79cd\u795e\u79d8\u7684\u4fdd\u62a4\u3002\u8fd9\u79cd\u4fdd\u62a4\u5f88\u575a\u56fa\uff0c\u800c\u4e14\u975e\u5e38\u7279\u6b8a\uff0c\u8d85\u51fa\u8fd9\u4e2a\u4e16\u754c\u7684\u89c4\u5219\u4e4b\u5916\uff0c\u5373\u4fbf\u662f\u6211\u4e5f\u5f88\u96be\u795b\u9664\uff0c\u552f\u6709\u4f60\u53ef\u4ee5\u8f7b\u6613\u6253\u7834\u3002\u8fd9\u5c31\u662f\u6211\u7684\u610f\u601d\u3002\u201d</p><p>\u201c\u6211\u5982\u4f55\u6253\u7834\u4f60\u6240\u8bf4\u7684\u795e\u79d8\u4fdd\u62a4\uff1f\u201d</p><p>\u201c\u53ea\u9700\u8981\u653b\u51fb\u4ed6\u5c31\u53ef\u4ee5\u4e86\uff0c\u54ea\u6015\u7528\u6700\u4f4e\u9636\u7684\u6cd5\u672f\uff0c\u5982\u679c\u4f60\u613f\u610f\u7684\u8bdd\uff0c\u4e5f\u53ef\u4ee5\u7528\u5315\u9996\uff0c\u6211\u770b\u5230\u4f60\u6b63\u597d\u6709\u4e00\u67c4\uff0c\u201d\u5973\u795e\u987f\u4e86\u987f\uff0c\u76ee\u5149\u5728\u743c\u6069\u6302\u5728\u8170\u95f4\u7684\u201c\u51b0\u8679\u5251\u201d\u4e0a\u626b\u8fc7\uff0c\u743c\u6069\u6e05\u695a\u5730\u611f\u89c9\u5230\u5315\u9996\u98a4\u4e86\u98a4\uff0c\u201c\u79d8\u5668\u4f1a\u81ea\u52a8\u5224\u5b9a\u6743\u9650\u9ad8\u4f4e\uff0c\u4ece\u800c\u505a\u51fa\u9009\u62e9\u3002\u90aa\u86c7\u867d\u7136\u4e5f\u7b97\u662f\u79d8\u5668\u7684\u94f8\u9020\u8005\u4e4b\u4e00\uff0c\u56e0\u6b64\u62e5\u6709\u5c11\u91cf\u6743\u9650\uff0c\u4f46\u5fc5\u5b9a\u4e0d\u4f1a\u8d85\u8fc7\u4f60\u8fd9\u4f4d\u2018\u7fd4\u9f99\u2019\u5947\u68b0\u5e08\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u8868\u793a\u8ba4\u53ef\u5973\u795e\u7684\u89e3\u91ca\u3002\u201c\u4f46\u5373\u4fbf\u6ca1\u6709\u4fdd\u62a4\uff0c\u6211\u4e5f\u4e0d\u53ef\u80fd\u662f\u8428\u9a6c\u65af\u7279\u7684\u5bf9\u624b\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u201c\u662f\u7684\uff0c\u6240\u4ee5\u4f60\u53ea\u9700\u8981\u8d1f\u8d23\u6253\u7834\u4fdd\u62a4\u5c31\u53ef\u4ee5\u4e86\uff0c\u201d\u5973\u795e\u8bf4\uff0c\u201c\u51ef\u5c14\u672c\u548c\u7ef4\u82e5\u62c9\u4f1a\u8d1f\u8d23\u6740\u6b7b\u4ed6\uff0c\u6885\u83f2\u65af\u5c0f\u59d0\u4e5f\u4f1a\u5e2e\u4e0a\u5fd9\uff0c\u6211\u6ce8\u610f\u5230\u5979\u6709\u4e00\u4ef6\u514b\u5236\u4ea1\u7075\u7684\u795e\u5668\u3002\u201d</p><p>\u8f89\u9633\u62a4\u7b26\u7684\u786e\u662f\u514b\u5236\u4ea1\u7075\u7684\u795e\u5668\uff0c\u4f46\u4ec5\u51ed\u5b83\u5c31\u60f3\u79d2\u6740\u8428\u9a6c\u65af\u7279\uff0c\u743c\u6069\u89c9\u5f97\u5e0c\u671b\u4e0d\u5927\uff0c\u5373\u4fbf\u6709\u51ef\u5c14\u672c\u548c\u7ef4\u82e5\u62c9\u7684\u5e2e\u52a9\u4e5f\u662f\u5982\u6b64\u3002\u4e0d\u8fc7\u4e8b\u5df2\u81f3\u6b64\uff0c\u4e5f\u6ca1\u522b\u7684\u529e\u6cd5\uff0c\u53ea\u597d\u8bd5\u8bd5\u770b\u4e86\u3002\u5f62\u52bf\u53d8\u5e7b\u83ab\u6d4b\uff0c\u4ed6\u5b8c\u5168\u88ab\u5f04\u7cca\u6d82\u4e86\uff0c\u641e\u4e0d\u6e05\u695a\u600e\u4e48\u4f1a\u6f14\u53d8\u6210\u8fd9\u79cd\u6837\u5b50\uff0c\u73b0\u5728\u80fd\u505a\u7684\u4e5f\u53ea\u80fd\u662f\u5148\u5b89\u5168\u8131\u8eab\uff0c\u518d\u6162\u6162\u5f04\u6e05\u695a\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u3002</p><p>\u201c\u6211\u53ef\u4ee5\u6682\u65f6\u524a\u5f31\u8428\u9a6c\u65af\u7279\u7684\u529b\u91cf\uff0c\u201d\u8ff7\u96fe\u5927\u5e08\u7a81\u7136\u8bf4\uff0c\u4ed6\u8d70\u8fd1\u4e86\u4e00\u4e9b\uff0c\u4f46\u548c\u5176\u4ed6\u4eba\u8fd8\u662f\u4fdd\u6301\u7740\u4e00\u5b9a\u8ddd\u79bb\uff0c\u201c\u8ba9\u4ed6\u7684\u53cd\u5e94\u548c\u52a8\u4f5c\u90fd\u53d8\u5f97\u7a0d\u7a0d\u8fdf\u949d\u4e00\u70b9\uff0c\u4f46\u6301\u7eed\u65f6\u95f4\u4e0d\u4f1a\u592a\u4e45\uff0c\u5927\u6982\u53ea\u6709\u4e24\u4e09\u79d2\u949f\u3002\u201d</p><p>\u201c\u90a3\u5f88\u597d\uff0c\u201d\u5973\u795e\u8bf4\uff0c\u201c\u4e24\u4e09\u79d2\u949f\u8db3\u591f\u4e86\u3002\u201d</p><p>\u5deb\u5e08\u6597\u6cd5\uff0c\u751f\u6b7b\u4e00\u77ac\uff0c\u6253\u6210\u6301\u4e45\u6218\u7684\u60c5\u5f62\u672c\u5c31\u4e0d\u591a\uff0c\u4e24\u4e09\u79d2\u949f\u7684\u786e\u8db3\u591f\u4e86\u3002</p><p>\u201c\u6240\u4ee5\u6211\u4eec\u7b97\u662f\u8fbe\u6210\u4e00\u81f4\u610f\u89c1\u4e86\uff1f\u201d\u5973\u795e\u95ee\u3002</p><p>\u201c\u6211\u6709\u4e2a\u95ee\u9898\uff0c\u201d\u6885\u83f2\u65af\u7a81\u7136\u8bf4\uff0c\u201c\u8428\u9a6c\u65af\u7279\u4e0d\u662f\u4f60\u7684\u5fe0\u5b9e\u7231\u6155\u8005\u5417\uff1f\u65e2\u7136\u4f60\u80fd\u591f\u5c06\u4ed6\u548c\u90aa\u86c7\u5206\u79bb\u5f00\u6765\uff0c\u4ed6\u6062\u590d\u6e05\u9192\u4e4b\u540e\uff0c\u81ea\u7136\u4f1a\u5bf9\u4f60\u552f\u547d\u662f\u4ece\uff0c\u5c31\u4e0d\u9700\u8981\u518d\u53d1\u751f\u6218\u6597\u4e86\u3002\u201d</p><p>\u201c\u4f60\u6ca1\u770b\u89c1\u5417\uff0c\u4ed6\u5df2\u7ecf\u4e0d\u8ba4\u8bc6\u6211\u4e86\u3002\u201d</p><p>\u201c\u90a3\u96be\u9053\u4e0d\u662f\u56e0\u4e3a\u4ed6\u88ab\u9644\u4f53\u7684\u7f18\u6545\u5417\uff1f\u201d</p><p>\u5973\u795e\u6447\u6447\u5934\uff0c\u201c\u4e0d\u662f\uff0c\u90aa\u86c7\u662f\u521a\u521a\u624d\u771f\u6b63\u9644\u4f53\u6210\u529f\uff0c\u4e4b\u524d\u4e00\u76f4\u53ea\u662f\u6f5c\u4f0f\uff0c\u6240\u4ee5\u8fde\u6211\u4e5f\u6ca1\u6709\u53d1\u89c9\u3002\u8428\u9a6c\u65af\u7279\u662f\u4e2a\u7b28\u86cb\uff0c\u4f46\u4ed6\u662f\u4e2a\u529b\u91cf\u5f3a\u5927\u7684\u7b28\u86cb\uff0c\u82e5\u975e\u4ed6\u81ea\u5df1\u5148\u5931\u53bb\u7406\u667a\uff0c\u9732\u51fa\u7834\u7efd\uff0c\u5373\u4fbf\u662f\u90aa\u86c7\u7684\u6295\u5f71\uff0c\u4e5f\u5f88\u96be\u4e58\u9699\u800c\u5165\u3002\u201d</p><p>\u6885\u83f2\u65af\u70b9\u4e86\u70b9\u5934\uff0c\u8868\u793a\u660e\u767d\u3002</p><p>\u201c\u8fd8\u6709\u4ec0\u4e48\u95ee\u9898\u5417\uff0c\u5947\u68b0\u5e08\uff1f\u201d\u5973\u795e\u95ee\u3002</p><p>\u201c\u6ca1\u6709\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5c31\u6309\u4f60\u6240\u8bf4\u7684\u505a\u5427\u3002\u201d</p><p>\u201c\u5f88\u597d\u3002\u201d\u5973\u795e\u8bf4\uff0c\u7136\u540e\u5411\u6c34\u6676\u53d1\u51fa\u4e00\u9053\u5149\u3002</p><p>\u90a3\u662f\u4e00\u9053\u770b\u8d77\u6765\u5f88\u666e\u901a\u7684\u767d\u5149\uff0c\u4f46\u975e\u5e38\u4eae\uff0c\u8000\u773c\u523a\u76ee\u3002\u6c34\u6676\u5728\u767d\u5149\u7684\u7167\u5c04\u4e0b\u5f00\u59cb\u6447\u6643\u8d77\u6765\uff0c\u53d1\u51fa\u55e1\u55e1\u55e1\u7684\u9707\u98a4\u58f0\uff0c\u8868\u9762\u51fa\u73b0\u4e00\u6761\u6761\u7ec6\u5c0f\u7684\u88c2\u7f1d\u3002\u98ce\u66b4\u548c\u6b23\u5e03\u540c\u65f6\u53d1\u51fa\u94f6\u706b\uff0c\u6ce8\u5165\u90a3\u9053\u767d\u5149\u4e4b\u4e2d\uff0c\u8ba9\u5b83\u53d8\u5f97\u66f4\u52a0\u660e\u4eae\u3002\u91d1\u8272\u7684\u96fe\u6c14\u4ece\u88c2\u7eb9\u4e2d\u9038\u51fa\uff0c\u51dd\u805a\u5728\u4e00\u8d77\uff0c\u6e10\u6e10\u5f62\u6210\u5de8\u86c7\u7684\u8f6e\u5ed3\u3002\u9b54\u6cd5\u5973\u795e\u5439\u4e86\u53e3\u6c14\uff0c\u5f62\u6210\u731b\u70c8\u7684\u72c2\u98ce\uff0c\u5c06\u91d1\u8272\u96fe\u6c14\u5439\u6563\uff0c\u9690\u9690\u7ea6\u7ea6\u663e\u9732\u51fa\u8428\u9a6c\u65af\u7279\u7684\u8eab\u5f71\u3002\u201c\u5c31\u662f\u73b0\u5728\uff01\u201d\u5979\u5bf9\u743c\u6069\u8bf4\u3002</p><p>\u91d1\u8272\u96fe\u6c14\u88ab\u72c2\u98ce\u5439\u6563\uff0c\u53ea\u4e00\u8f6c\u77ac\u95f4\u53c8\u91cd\u65b0\u805a\u96c6\u8d77\u6765\uff0c\u5316\u4f5c\u5de8\u86c7\u3002\u5973\u795e\u5f20\u5f00\u53cc\u81c2\uff0c\u6293\u4f4f\u6b23\u5e03\u548c\u98ce\u66b4\uff0c\u4e09\u4e2a\u4eba\u7684\u94f6\u706b\u6c47\u805a\u5728\u4e00\u8d77\uff0c\u5851\u6210\u4e00\u53ea\u5de8\u5927\u7684\u94f6\u9e70\uff0c\u4e00\u722a\u6293\u4f4f\u91d1\u86c7\uff0c\u91d1\u86c7\u53cd\u8fc7\u6765\u7f20\u4f4f\u9e70\u722a\uff0c\u6602\u9996\u53cd\u51fb\u3002\u5b83\u4eec\u7ffb\u6eda\u7740\uff0c\u640f\u6597\u7740\uff0c\u5728\u9ad8\u7a7a\u4e2d\u6fc0\u70c8\u53ae\u6740\uff0c\u91d1\u8272\u548c\u94f6\u8272\u7684\u8840\u6db2\u98de\u6e85\u6d12\u843d\uff0c\u4eff\u4f5b\u9aa4\u96e8\u3002</p><p>\u8428\u9a6c\u65af\u7279\u88ab\u201c\u7559\u201d\u4e86\u4e0b\u6765\uff0c\u8001\u5deb\u5996\u7ad9\u5728\u534a\u7a7a\u4e2d\uff0c\u8138\u4e0a\u7684\u795e\u60c5\u5f88\u5947\u602a\uff0c\u4f3c\u60b2\u4f3c\u559c\uff0c\u53d8\u5e7b\u4e0d\u5b9a\uff0c\u773c\u795e\u8ff7\u832b\u800c\u7a7a\u6d1e\uff0c\u4eff\u4f5b\u9677\u5165\u6c89\u601d\u4e4b\u4e2d\u3002</p><p>\u201c\u4e0d\u53ef\u80fd\uff0c\u6211\u600e\u4e48\u4f1a\u8ba4\u9519\uff0c\u201d\u8428\u9a6c\u65af\u7279\u5583\u5583\u81ea\u8bed\uff0c\u201c\u8fd9\u7edd\u5bf9\u4e0d\u53ef\u80fd\u3002\u5bf9\uff0c\u8fd9\u662f\u5e7b\u89c9\uff0c\u8fd9\u4e00\u5b9a\u662f\u5e7b\u89c9\uff0c\u201d\u4ed6\u731b\u7136\u62ac\u8d77\u5934\uff0c\u6012\u89c6\u7740\u6240\u6709\u4eba\uff0c\u201c\u8fd9\u4e00\u5b9a\u662f\u5e7b\u89c9\uff0c\u5bf9\u4e0d\u5bf9\uff0c\u4f60\u4eec\u5413\u4e0d\u5012\u6211\u7684\uff01\u201d</p><p>\u51ef\u5c14\u672c\u538b\u6839\u6ca1\u6709\u529f\u592b\u53bb\u7406\u776c\u4e00\u4e2a\u75af\u5b50\u7684\u80e1\u8a00\u4e71\u8bed\uff0c\u4ed6\u4e3e\u8d77\u9ed1\u6756\uff0c\u5728\u4e24\u79d2\u949f\u4e4b\u5185\u53d1\u51fa\u4e86\u4e03\u4e2a\u6cd5\u672f\uff0c\u65e0\u8bba\u54ea\u4e00\u4e2a\u90fd\u8db3\u4ee5\u8ba9\u6700\u5f3a\u58ee\u7684\u5de8\u4eba\u5728\u77ac\u95f4\u4e27\u5931\u6218\u6597\u529b\u3002\u743c\u6069\u548c\u7ef4\u82e5\u62c9\u4e5f\u968f\u4e4b\u53d1\u51fa\u4e86\u653b\u51fb\uff0c\u4f46\u8fd9\u4e00\u5207\u90fd\u6ca1\u6709\u53d6\u5f97\u4ec0\u4e48\u6548\u679c\u3002\u8001\u5deb\u5996\u867d\u7136\u9677\u5165\u75af\u72c2\uff0c\u5b9e\u529b\u5374\u534a\u70b9\u6ca1\u6709\u6253\u6298\uff0c\u751a\u81f3\u6bd4\u6e05\u9192\u65f6\u72b9\u6709\u8fc7\u4e4b\uff0c\u4ed6\u53ea\u7528\u4e86\u4e00\u4e2a\u5492\u8bed\uff0c\u5c31\u5c06\u6240\u6709\u7684\u653b\u51fb\u90fd\u6321\u4e86\u4e0b\u6765\u3002</p><p>\u8428\u9a6c\u65af\u7279\u5f00\u59cb\u53cd\u51fb\uff0c\u4ed6\u53e3\u4e2d\u8bf5\u5492\uff0c\u540c\u65f6\u4e00\u6839\u4e00\u6839\u5730\u5c06\u81ea\u5df1\u7684\u5de6\u624b\u6307\u9aa8\u5378\u4e0b\u3001\u63b0\u65ad\u3001\u634f\u788e\uff0c\u6bcf\u4e00\u5757\u788e\u7247\u90fd\u5316\u4f5c\u4e00\u5934\u602a\u517d\uff0c\u671d\u7740\u654c\u4eba\u51b2\u6765\u3002\u5b83\u4eec\u663e\u7136\u662f\u67d0\u79cd\u4ea1\u7075\u6cd5\u672f\u7684\u9020\u7269\uff0c\u5f62\u6001\u626d\u66f2\uff0c\u5343\u5947\u767e\u602a\uff0c\u4f46\u65e0\u4e00\u4f8b\u5916\u5168\u90fd\u662f\u4ee5\u9aa8\u9ab8\u5236\u6210\uff0c\u6bcf\u4e00\u5904\u9aa8\u67b6\u7684\u7f1d\u9699\u4e2d\u90fd\u900f\u7740\u523a\u773c\u7684\u7ea2\u5149\u3002\u51ef\u5c14\u672c\u521b\u9020\u51fa\u4e00\u9762\u900f\u660e\u7684\u7f51\uff0c\u94fa\u5929\u76d6\u5730\u5730\u843d\u4e0b\uff0c\u5c06\u6240\u6709\u602a\u517d\u7b3c\u7f69\u5176\u4e2d\uff0c\u4ea1\u7075\u602a\u517d\u5728\u7f51\u4e2d\u5de6\u51b2\u53f3\u7a81\uff0c\u594b\u529b\u6323\u624e\uff0c\u4f46\u4e00\u65f6\u95f4\u65e0\u6cd5\u51b2\u7834\u3002</p><p>\u8428\u9a6c\u65af\u7279\u51b7\u54fc\u4e00\u58f0\uff0c\u518d\u6b21\u8bf5\u5492\uff0c\u6240\u6709\u602a\u517d\u7684\u4f53\u578b\u90fd\u731b\u7136\u95f4\u81a8\u80c0\u8d77\u6765\uff0c\u5728\u4e0d\u5230\u4e00\u79d2\u949f\u7684\u65f6\u95f4\u91cc\u53d8\u5927\u4e86\u63a5\u8fd1\u56db\u4e94\u500d\uff0c\u529b\u91cf\u4e5f\u660e\u663e\u5f97\u5230\u63d0\u5347\u3002\u51ef\u5c14\u672c\u7684\u6cd5\u672f\u4e4b\u7f51\u987f\u65f6\u62b5\u5fa1\u4e0d\u4f4f\uff0c\u77ac\u95f4\u5c31\u88ab\u6495\u5f00\u4e86\u51e0\u4e2a\u53e3\u5b50\u3002\u4e03\u516b\u5934\u602a\u7269\u4ece\u7f1d\u9699\u4e2d\u94bb\u51fa\u6765\uff0c\u6251\u5411\u51ef\u5c14\u672c\u3002</p><p>\u5c31\u5728\u6b64\u65f6\uff0c\u4e00\u9053\u94f6\u8272\u5029\u5f71\u6beb\u65e0\u9884\u5146\u5730\u51fa\u73b0\u5728\u8428\u9a6c\u65af\u7279\u7684\u80cc\u540e\u3002</p><p>\u6885\u83f2\u65af\u53d1\u52a8\u201c\u51a5\u6b65\u201d\uff0c\u77ac\u79fb\u5230\u8428\u9a6c\u65af\u7279\u7684\u80cc\u540e\uff0c\u4e00\u5251\u7b14\u76f4\u523a\u51fa\u3002\u51a5\u6b65\u662f\u4e00\u79cd\u5a01\u529b\u6781\u5927\u7684\u523a\u6740\u6280\u5de7\uff0c\u662f\u6740\u622e\u795e\u529b\u8d4b\u4e88\u7684\u529b\u91cf\u4e4b\u4e00\uff0c\u4e4b\u524d\u5728\u5854\u745f\u8c37\uff0c\u5979\u66fe\u7ecf\u7528\u76f8\u540c\u7684\u624b\u6cd5\u51fb\u6740\u4e86\u4e00\u4f4d\u540d\u4e3a\u8010\u8bfa\u7684\u82cd\u767d\u9886\u4e3b\u3002\u8428\u9a6c\u65af\u7279\u540c\u6837\u6ca1\u80fd\u53ca\u65f6\u5bdf\u89c9\u6885\u83f2\u65af\u7684\u903c\u8fd1\uff0c\u76f4\u5230\u94f6\u5251\u523a\u5165\u4f53\u5185\u65b9\u624d\u53cd\u5e94\u8fc7\u6765\u3002\u5c11\u5973\u4f4e\u559d\u4e00\u58f0\uff0c\u8f89\u9633\u62a4\u7b26\u518d\u5ea6\u53d1\u52a8\uff0c\u63d0\u5c14\u548c\u8fdc\u53e4\u592a\u9633\u795e\u7684\u5de8\u5927\u795e\u76f8\u865a\u5f71\u540c\u65f6\u51fa\u73b0\uff0c\u8981\u5c06\u8428\u9a6c\u65af\u7279\u4e00\u51fb\u65a9\u6740\u3002</p><p>\u545c\uff01</p><p>\u5c16\u9510\u7684\u5578\u53eb\u58f0\u9661\u7136\u54cd\u8d77\uff0c\u8428\u9a6c\u65af\u7279\u88ab\u94f6\u5251\u523a\u4e2d\u7684\u90e8\u4f4d\u51fa\u73b0\u4e86\u4e00\u4e2a\u5706\u5f62\u9ed1\u6d1e\uff0c\u6df1\u4e0d\u89c1\u5e95\uff0c\u5b9b\u5982\u865a\u7a7a\u3002\u9f99\u5deb\u6559\u4e3b\u7ec8\u7a76\u4e0d\u662f\u5176\u624b\u4e0b\u53ef\u6bd4\uff0c\u95f4\u4e0d\u5bb9\u53d1\u4e4b\u9645\u4ecd\u7136\u505a\u51fa\u4e86\u6b63\u786e\u5e94\u5bf9\uff0c\u4ed6\u5c06\u81ea\u5df1\u8eab\u4f53\u7684\u4e00\u90e8\u5206\u53d8\u6210\u4e86\u201c\u865a\u65e0\u201d\uff0c\u4e0e\u8d1f\u80fd\u91cf\u4f4d\u9762\u8fde\u63a5\uff0c\u8fd9\u5341\u5206\u5192\u9669\uff0c\u4f46\u7684\u786e\u6709\u6548\u3002\u6885\u83f2\u65af\u50ac\u52a8\u5723\u529b\uff0c\u94f6\u5251\u4e0a\u5149\u534e\u6e90\u6e90\u4e0d\u65ad\u5730\u6d8c\u51fa\uff0c\u5374\u5c3d\u6570\u90fd\u704c\u6ce8\u5230\u4e86\u8fd9\u865a\u7a7a\u9ed1\u6d1e\u4e4b\u4e2d\uff0c\u672a\u80fd\u5bf9\u8428\u9a6c\u65af\u7279\u9020\u6210\u4efb\u4f55\u4f24\u5bb3\u3002\u4e0d\u4ec5\u5982\u6b64\uff0c\u6570\u4e0d\u6e05\u7684\u7ec6\u5c0f\u9ed1\u8272\u89e6\u624b\u4ece\u865a\u7a7a\u4e2d\u6e38\u51fa\u6765\uff0c\u53cd\u8fc7\u6765\u541e\u566c\u5723\u5149\uff0c\u4fb5\u8680\u94f6\u5251\uff0c\u671d\u6885\u83f2\u65af\u88ad\u53bb\u3002\u6885\u83f2\u65af\u60f3\u8981\u64a4\u5251\u540e\u9000\uff0c\u5374\u88ab\u65e0\u5f62\u7684\u529b\u91cf\u5438\u4f4f\u4e86\uff0c\u65e0\u6cd5\u8131\u624b\uff0c\u773c\u770b\u9ed1\u8272\u89e6\u624b\u8d8a\u6765\u8d8a\u8fd1\uff0c\u5979\u6df1\u5438\u4e00\u53e3\u6c14\uff0c\u731b\u7136\u9ad8\u559d\u51fa\u58f0\u3002</p><p>\u201c\u5f25\u8d5b\u4e9a\uff01\u201d</p><p>\u5723\u6b66\u58eb\u53eb\u51fa\u76df\u7ea6\u5929\u4f7f\u7684\u540d\u5b57\uff0c\u4e00\u5bf9\u5bf9\u900f\u660e\u5149\u7ffc\u81ea\u5c11\u5973\u7684\u80cc\u540e\u4f9d\u6b21\u8212\u5c55\u5f00\u6765\uff0c\u6bcf\u5f20\u5f00\u4e00\u5bf9\u5149\u7ffc\uff0c\u94f6\u5251\u4e0a\u7684\u5723\u5149\u5c31\u66f4\u52a0\u660e\u4eae\u4e00\u5206\u3002\u5f53\u5149\u7ffc\u5f20\u5f00\u5230\u7b2c\u516d\u5bf9\u65f6\uff0c\u6885\u83f2\u65af\u7684\u501f\u529b\u8fbe\u5230\u4e86\u6781\u81f4\uff0c\u94f6\u5251\u4e0a\u5723\u5149\u8f70\u7136\u7efd\u653e\uff0c\u51dd\u805a\u6210\u4e00\u4e2a\u70bd\u70c8\u7684\u5149\u7403\uff0c\u5bf9\u6297\u7740\u4e0d\u65ad\u538b\u8feb\u8fc7\u6765\u7684\u9ed1\u6697\u89e6\u624b\u3002</p><p>\u6885\u83f2\u65af\u8fd8\u6ca1\u6709\u771f\u6b63\u5b8c\u6210\u76df\u7ea6\uff0c\u8fd9\u79cd\u5411\u5929\u4f7f\u501f\u529b\u7684\u505a\u6cd5\u4e0d\u80fd\u6301\u4e45\uff0c\u5c5e\u4e8e\u77ed\u6682\u7206\u53d1\uff0c\u518d\u8fc7\u7247\u523b\u5c31\u4f1a\u6025\u5267\u8870\u9000\u3002\u8428\u9a6c\u65af\u7279\u7684\u5904\u5883\u4e5f\u4e0d\u600e\u4e48\u597d\uff0c\u5728\u81ea\u5df1\u4f53\u5185\u6253\u5f00\u8fde\u63a5\u8d1f\u80fd\u91cf\u7684\u4f4d\u9762\u4e4b\u95e8\uff0c\u8fd9\u662f\u5178\u578b\u7684\u4f5c\u6b7b\u884c\u4e3a\uff0c\u5373\u4fbf\u4ed6\u662f\u4ea1\u7075\u4e5f\u627f\u53d7\u4e0d\u8d77\uff0c\u5c24\u5176\u662f\u5728\u6709\u5916\u6765\u529b\u91cf\u4e0d\u65ad\u4fb5\u88ad\u7684\u60c5\u51b5\u4e0b\uff0c\u53ea\u8981\u4ed6\u5bf9\u80fd\u91cf\u5e73\u8861\u7684\u628a\u63e1\u7a0d\u6709\u5931\u8bef\uff0c\u8001\u5deb\u5996\u5c31\u4f1a\u843d\u4e2a\u7070\u98de\u70df\u706d\u7684\u4e0b\u573a\u3002\u8428\u9a6c\u65af\u7279\u4e5f\u662f\u60c5\u6025\u4e4b\u4e0b\u7684\u65e0\u5948\u4e4b\u4e3e\uff0c\u51ed\u7740\u9ad8\u8d85\u7684\u9b54\u6cd5\u6280\u5de7\uff0c\u4ed6\u7a33\u4f4f\u4e86\u5c40\u9762\uff0c\u4f46\u4ed6\u540c\u65f6\u4e5f\u88ab\u7275\u5236\u4f4f\u4e86\uff0c\u4e00\u65f6\u95f4\u65e0\u6cd5\u52a8\u5f39\u3002</p><p>\u94f6\u5251\u4e0e\u9ed1\u6d1e\u3001\u6885\u83f2\u65af\u548c\u8428\u9a6c\u65af\u7279\uff0c\u53cc\u65b9\u5f62\u6210\u4e86\u5fae\u5999\u7684\u5e73\u8861\uff0c\u4f46\u8fd9\u79cd\u5e73\u8861\u663e\u7136\u662f\u6682\u65f6\u7684\uff0c\u968f\u65f6\u53ef\u80fd\u88ab\u6253\u7834\u3002\u743c\u6069\u867d\u7136\u53e6\u6709\u6253\u7b97\uff0c\u4f46\u773c\u4e0b\u8fd9\u79cd\u5c40\u9762\u5374\u5b8c\u5168\u4e0d\u5728\u4ed6\u7684\u9884\u8ba1\u4e4b\u4e2d\uff0c\u4e00\u65f6\u95f4\u4e5f\u5446\u4f4f\u4e86\u3002</p><p>\u9ad8\u7a7a\u4e4b\u4e2d\u4f20\u6765\u9707\u8033\u6b32\u804b\u7684\u5636\u543c\uff0c\u91d1\u86c7\u6781\u529b\u60f3\u8981\u671d\u8428\u9a6c\u65af\u7279\u51b2\u8fc7\u6765\uff0c\u5374\u88ab\u94f6\u9e70\u6b7b\u6b7b\u7f20\u4f4f\uff0c\u4f46\u4ece\u5f62\u52bf\u4e0a\u770b\uff0c\u94f6\u9e70\u5df2\u7ecf\u5feb\u8981\u963b\u62e6\u4e0d\u4f4f\u3002</p><p>\u4e00\u9053\u9ed1\u5f71\u7a81\u7136\u51fa\u73b0\u5728\u6885\u83f2\u65af\u8eab\u65c1\u3002</p><p>\u201c\u62b1\u6b49\u4e86\uff0c\u5723\u6b66\u58eb\u3002\u201d\u51ef\u5c14\u672c\u8bf4\uff0c\u7528\u529b\u63e1\u7d27\u624b\u4e2d\u7684\u4e00\u679a\u6668\u66e6\u62a4\u7b26\u3002</p><p>\u6885\u83f2\u65af\u4e0d\u660e\u5c31\u91cc\uff0c\u4f46\u968f\u5373\u5168\u8eab\u731b\u7136\u4e00\u9707\uff0c\u5979\u611f\u89c9\u5230\u4e00\u80a1\u5f3a\u70c8\u65e0\u6bd4\u7684\u80fd\u91cf\u81ea\u4f53\u5185\u5feb\u901f\u5347\u8d77\uff0c\u5b8c\u5168\u4e0d\u53d7\u81ea\u5df1\u63a7\u5236\u3002\u90a3\u662f\u9ece\u660e\u4e4b\u77f3\u7684\u529b\u91cf\uff0c\u81ea\u4ece\u88ab\u6885\u83f2\u65af\u878d\u5408\u4e4b\u540e\uff0c\u5b83\u5c31\u4e00\u76f4\u5b89\u9759\u5730\u5f85\u7740\uff0c\u9547\u538b\u5df4\u5c14\u7684\u6740\u622e\u795e\u6027\u3002\u773c\u4e0b\u5b83\u5374\u4eff\u4f5b\u53d7\u5230\u53ec\u5524\u4e00\u822c\uff0c\u4ece\u6c89\u7761\u4e2d\u60ca\u9192\uff0c\u8dc3\u8dc3\u6b32\u8bd5\u3002\u201c\u4f60\u5728\u5e72\u4ec0\u4e48\uff01\u201d\u6885\u83f2\u65af\u4e00\u60ca\u4e4b\u4e0b\uff0c\u671d\u51ef\u5c14\u672c\u5389\u58f0\u8d28\u95ee\u3002</p><p>\u51ef\u5c14\u672c\u795e\u8272\u4e0d\u53d8\uff0c\u4e5f\u4e0d\u4f5c\u7b54\uff0c\u7ee7\u7eed\u671d\u62a4\u7b26\u4e4b\u4e2d\u6ce8\u5165\u9b54\u529b\uff0c\u6fc0\u53d1\u5b83\u4e0e\u6885\u83f2\u65af\u4f53\u5185\u9ece\u660e\u4e4b\u77f3\u7684\u201c\u5171\u632f\u201d\u3002\u4ed6\u5e76\u4e0d\u662f\u5f88\u559c\u6b22\u8fd9\u4e48\u505a\uff0c\u539f\u672c\u4e5f\u5c31\u662f\u4e00\u4e2a\u5907\u7528\u65b9\u6848\uff0c\u5018\u82e5\u6885\u83f2\u65af\u523a\u6740\u5f97\u624b\uff0c\u4e00\u5251\u65a9\u4e86\u8428\u9a6c\u65af\u7279\uff0c\u81ea\u7136\u662f\u6700\u597d\u4e0d\u8fc7\uff0c\u4f46\u773c\u4e0b\u9677\u5165\u50f5\u6301\uff0c\u90aa\u86c7\u53c8\u968f\u65f6\u53ef\u80fd\u56de\u5f52\uff0c\u90a3\u5c31\u53ea\u80fd\u5982\u6b64\u4e86\u3002\u81f3\u4e8e\u5df4\u5c14\u6709\u53ef\u80fd\u4f1a\u501f\u673a\u8131\u56f0\uff0c\u51ef\u5c14\u672c\u5012\u4e0d\u662f\u7279\u522b\u5728\u610f\uff0c\u4e00\u4e2a\u5df2\u7ecf\u9668\u843d\u7684\u90aa\u795e\u6b8b\u9b42\uff0c\u603b\u6709\u529e\u6cd5\u5bf9\u4ed8\u7684\u3002</p><p>\u518d\u4e00\u6b21\u7684\u9707\u52a8\uff0c\u9ece\u660e\u4e4b\u77f3\u7684\u865a\u5f71\u5df2\u7ecf\u81ea\u6885\u83f2\u65af\u4f53\u5185\u82e5\u9690\u82e5\u73b0\u5730\u900f\u51fa\u3002\u968f\u7740\u7981\u9522\u7684\u677e\u52a8\uff0c\u6740\u622e\u795e\u529b\u6240\u5316\u6210\u7684\u602a\u517d\u4e5f\u5728\u8822\u8822\u6b32\u52a8\uff0c\u968f\u65f6\u51c6\u5907\u51b2\u51fa\u7262\u7b3c\u3002\u5c11\u5973\u5fc3\u4e2d\u60ca\u60f6\uff0c\u5374\u65e0\u8ba1\u53ef\u65bd\uff0c\u5979\u672c\u80fd\u5730\u671d\u743c\u6069\u770b\u53bb\uff0c\u5374\u53d1\u73b0\u7537\u53cb\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u6d88\u5931\u4e86\u3002\u53cd\u800c\u662f\u90a3\u4f4d\u4e00\u76f4\u8896\u624b\u65c1\u89c2\u7684\u8ff7\u96fe\u5927\u5e08\uff0c\u6084\u65e0\u58f0\u606f\u5730\u77ac\u79fb\u5230\u4e86\u51ef\u5c14\u672c\u8eab\u540e\uff0c\u4ed6\u671d\u6885\u83f2\u65af\u505a\u4e86\u4e2a\u201c\u5618\u201d\u7684\u624b\u52bf\uff0c\u4ece\u8896\u4e2d\u53d6\u51fa\u4e00\u53ea\u9ed1\u8272\u7684\u8718\u86db\uff0c\u5c06\u5b83\u8f7b\u8f7b\u653e\u5728\u51ef\u5c14\u672c\u7684\u80a9\u5934\u3002</p><p>\u8fd1\u5728\u54ab\u5c3a\u7684\u5371\u9669\uff0c\u51ef\u5c14\u672c\u5374\u5b8c\u5168\u6ca1\u6709\u5bdf\u89c9\uff0c\u4ed6\u5168\u795e\u8d2f\u6ce8\u5730\u63a7\u5236\u7740\u6668\u66e6\u62a4\u7b26\u3002\u6839\u636e\u827e\u6797\u544a\u8bc9\u4ed6\u7684\u8bf4\u6cd5\uff0c\u201c\u5171\u632f\u201d\u4f1a\u4e00\u6ce2\u6bd4\u4e00\u6ce2\u5f3a\uff0c\u5f53\u7b2c\u4e09\u6b21\u5171\u632f\u4e4b\u540e\uff0c\u9ece\u660e\u4e4b\u77f3\u7684\u529b\u91cf\u5c31\u4f1a\u88ab\u6fc0\u53d1\u5230\u6700\u5927\u5316\uff0c\u7136\u540e\u7206\u70b8\u3002\u8428\u9a6c\u65af\u7279\u8fd9\u4e2a\u5deb\u5996\u81ea\u7136\u9996\u5f53\u5176\u51b2\uff0c\u4f46\u51ef\u5c14\u672c\u4e5f\u5fc5\u987b\u53ca\u65f6\u64a4\u9000\uff0c\u5426\u5219\u96be\u514d\u88ab\u6ce2\u53ca\u3002</p><p>\u7b2c\u4e8c\u6b21\u5171\u632f\u5df2\u7ecf\u5b8c\u6210\uff0c\u9ece\u660e\u4e4b\u77f3\u7684\u865a\u5f71\u5f00\u59cb\u6d6e\u73b0\uff0c\u4e00\u5207\u90fd\u5982\u827e\u6797\u6240\u9884\u8ba1\u7684\u90a3\u6837\uff0c\u63a5\u4e0b\u6765\uff0c\u53ea\u8981\u518d\u53d1\u52a8\u4e00\u6b21\u5c31\u5927\u529f\u544a\u6210\u4e86\u3002</p><p>\u51ef\u5c14\u672c\u5982\u6b64\u60f3\u7740\uff0c\u7a81\u7136\u611f\u89c9\u4e0d\u5bf9\u52b2\uff0c\u6ca1\u6709\u4efb\u4f55\u5f81\u5146\uff0c\u5b8c\u5168\u662f\u4e0b\u610f\u8bc6\u7684\uff0c\u4ed6\u53d1\u73b0\u6709\u4eba\u5728\u80cc\u540e\u5077\u88ad\u3002\u987e\u4e0d\u5f97\u53d1\u52a8\u5171\u632f\uff0c\u51ef\u5c14\u672c\u731b\u7136\u8f6c\u8eab\uff0c\u6b63\u770b\u89c1\u8ff7\u96fe\u5927\u5e08\u4ece\u4ed6\u80a9\u4e0a\u5c06\u624b\u6536\u56de\u6765\u3002</p><p>\u201c\u4f60\u5728\u5e72\u4ec0\u4e48\uff1f\u201d\u51ef\u5c14\u672c\u6012\u559d\uff0c\u540c\u65f6\u53d1\u51fa\u4e00\u9053\u653b\u51fb\u6cd5\u672f\u3002</p><p>\u8ff7\u96fe\u5927\u5e08\u88ab\u6cd5\u672f\u51fb\u4e2d\u4e86\uff0c\u4f46\u663e\u7136\u6ca1\u6709\u53d7\u4f24\uff0c\u4ed6\u53ea\u662f\u5f80\u540e\u9000\u4e86\u4e00\u6bb5\u8ddd\u79bb\uff0c\u7136\u540e\u62ac\u8d77\u624b\uff0c\u5c06\u94c1\u9762\u5177\u6458\u4e86\u4e0b\u6765\u3002\u5728\u9762\u5177\u6458\u4e0b\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u201c\u4ed6\u201d\u7684\u6574\u4e2a\u5f62\u8c8c\u90fd\u6539\u53d8\u4e86\uff0c\u53d8\u6210\u4e00\u4f4d\u8eab\u6750\u9ad8\u6311\u7684\u7f8e\u4e3d\u5973\u5b50\uff0c\u84dd\u8272\u7684\u957f\u88d9\u7aef\u5e84\u5178\u96c5\uff0c\u4e00\u5934\u706b\u7ea2\u957f\u53d1\u5982\u7011\u5782\u843d\u3002\u5973\u5b50\u5fae\u7b11\u7740\uff0c\u655b\u88d9\u65bd\u793c\uff0c\u201c\u4e0b\u5348\u597d\uff0c\u57ce\u4e3b\u5927\u4eba\u3002\u201d</p><p>\u5f3a\u70c8\u7684\u80fd\u91cf\u6ce2\u52a8\u4ece\u65c1\u8fb9\u4f20\u6765\uff0c\u4e00\u6ce2\u6bd4\u4e00\u6ce2\u6025\u5207\u3002\u867d\u7136\u5931\u53bb\u51ef\u5c14\u672c\u7684\u5f15\u5bfc\uff0c\u4f46\u9ece\u660e\u4e4b\u77f3\u7684\u529b\u91cf\u5df2\u7ecf\u88ab\u6fc0\u53d1\uff0c\u65e0\u6cd5\u6536\u56de\uff0c\u5b83\u7684\u9707\u52a8\u8d8a\u6765\u8d8a\u5feb\uff0c\u900f\u660e\u865a\u5f71\u7684\u8868\u9762\u5df2\u7ecf\u5f00\u59cb\u51fa\u73b0\u65e0\u6570\u9053\u88c2\u9699\uff0c\u800c\u4e14\u5728\u6025\u901f\u6269\u5927\uff0c\u773c\u770b\u5c31\u8981\u5206\u5d29\u79bb\u6790\u3002\u51ef\u5c14\u672c\u5fc3\u5ff5\u7535\u8f6c\uff0c\u52a8\u4f5c\u5374\u4e1d\u6beb\u4e0d\u6162\uff0c\u4ed6\u53d1\u52a8\u4e86\u4e00\u4e2a\u4f20\u9001\u6cd5\u672f\uff0c\u77ac\u95f4\u5c06\u81ea\u5df1\u9001\u5230\u51e0\u767e\u4e08\u4e4b\u5916\u3002</p><p>\u201c\u5feb\u8d70\uff01\u201d\u6885\u83f2\u65af\u5bf9\u624e\u745e\u5c14\u8bf4\uff0c\u5979\u5df2\u7ecf\u7aed\u5c3d\u5168\u529b\u5728\u538b\u5236\u9ece\u660e\u4e4b\u77f3\uff0c\u4f46\u575a\u6301\u4e0d\u4e86\u591a\u4e45\uff0c\u201c\u5b83\u9a6c\u4e0a\u8981\u7206\u70b8\u4e86\u3002\u201d</p><p>\u9ece\u660e\u4e4b\u77f3\u662f\u6668\u66e6\u6559\u4f1a\u7684\u5723\u7269\uff0c\u8574\u542b\u5170\u68ee\u5fb7\u5c14\u7684\u795e\u529b\uff0c\u5170\u68ee\u5fb7\u5c14\u662f\u6668\u66e6\u4e4b\u795e\u3001\u5149\u8f89\u4e4b\u795e\uff0c\u662f\u4e00\u5207\u9ed1\u6697\u9634\u90aa\u7684\u514b\u661f\u3002\u4ea1\u7075\u4e5f\u7f62\u3001\u90aa\u9b54\u4e5f\u597d\uff0c\u90fd\u5c5e\u4e8e\u4ed6\u4e25\u5389\u6253\u51fb\u7684\u5bf9\u8c61\u3002\u9ece\u660e\u4e4b\u77f3\u4e00\u65e6\u7206\u70b8\uff0c\u624e\u745e\u5c14\u8fd9\u4e2a\u5927\u9b54\u9b3c\u5fc5\u7136\u9996\u5f53\u5176\u51b2\uff0c\u6885\u83f2\u65af\u5bf9\u5979\u6ca1\u6709\u591a\u5c11\u597d\u5370\u8c61\uff0c\u4f46\u770b\u5728\u743c\u6069\u7684\u4efd\u4e0a\uff0c\u8fd8\u662f\u51fa\u8a00\u63d0\u9192\u3002</p><p>\u4f3c\u4e4e\u662f\u53d1\u73b0\u8fd9\u91cc\u6240\u53d1\u751f\u7684\u53d8\u5316\uff0c\u91d1\u86c7\u548c\u94f6\u9e70\u653e\u5f03\u4e86\u4e89\u6597\uff0c\u540c\u65f6\u4ece\u9ad8\u7a7a\u4e2d\u6251\u4e86\u4e0b\u6765\u3002\u624e\u745e\u5c14\u77a5\u4e86\u4e00\u773c\uff0c\u4e0d\u4f46\u6ca1\u6709\u79bb\u5f00\uff0c\u53cd\u800c\u8d70\u8fd1\u51e0\u6b65\uff0c\u201c\u6ca1\u4e8b\uff0c\u522b\u5bb3\u6015\uff0c\u827e\u5f25\u8587\uff0c\u201d\u5979\u7b11\u76c8\u76c8\u5730\u629a\u6478\u7740\u5723\u6b66\u58eb\u7684\u5934\u53d1\uff0c\u201c\u653e\u677e\uff0c\u8ba9\u5b83\u7206\u70b8\u597d\u4e86\u3002\u201d</p><p>\u4ec0\u4e48\uff1f</p><p>\u6885\u83f2\u65af\u4e0d\u89e3\u5176\u610f\uff0c\u4f46\u5979\u4e5f\u7684\u786e\u5df2\u7ecf\u652f\u6301\u4e0d\u4f4f\u3002\u9ece\u660e\u4e4b\u77f3\u5728\u4e00\u8f6e\u5267\u70c8\u9707\u52a8\u4e4b\u540e\uff0c\u7136\u540e\u7a81\u7136\u9759\u6b62\u4e86\u5927\u7ea6\u4e24\u4e09\u79d2\u949f\uff0c\u7d27\u63a5\u7740\u4fbf\u7206\u70b8\u5f00\u6765\u3002\u767d\u8272\u7684\u5149\u4ece\u4e2d\u8ff8\u53d1\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u4fbf\u4f1a\u5982\u6d77\u6d6a\u822c\u547c\u5578\u5e2d\u5377\uff0c\u8361\u5e73\u5468\u56f4\u7684\u4e00\u5207\u9ed1\u6697\u4e0e\u90aa\u6076\u3002</p><p>\u5c31\u5728\u8fd9\u4e00\u77ac\uff0c\u65f6\u95f4\u505c\u6b62\u4e86\u3002</p></article>"}}}]);
