(window.webpackJsonp=window.webpackJsonp||[]).push([[537],{560:function(p){p.exports={chapterTitle:null,title:"\u7b2c537\u7ae0 \u5149\u5f69\u593a\u76ee",content:"<article><h2>\u7b2c537\u7ae0 \u5149\u5f69\u593a\u76ee</h2><p>\u3000\u3000\u201c\u8fd9\u5ea7\u7eaf\u6728\u7ed3\u6784\u7684\u65b9\u697c\u5efa\u4e8e\u6e05\u4e2d\u53f6\uff0c\u5efa\u6210\u4e4b\u521d\u5c31\u662f\u7701\u5185\u6709\u540d\u7684\u68cb\u9986\uff0c\u6e05\u672b\u534e\u4e1c\u6709\u4e24\u4e2a\u56f4\u68cb\u540d\u4eba\uff0c\u5e76\u79f0\u53f6\u738b\uff0c\u738b\u7ecd\u6587\u665a\u5e74\u5c31\u5728\u8fd9\u91cc\u5f00\u9986\u6388\u68cb\uff0c\u90a3\u65f6\u662f\u6d77\u5dde\u56f4\u68cb\u754c\u6700\u5177\u76db\u540d\u7684\u65f6\u671f\uff0c\u4e0e\u5f53\u65f6\u7684\u6d77\u5dde\u74f7\u5668\u3001\u5e03\u67d3\u5e76\u79f0\u6d77\u5dde\u4e09\u7edd\uff1b\u6211\u4eec\u4e16\u7eaa\u9526\u6e56\u6295\u5165\u516b\u767e\u4e07\u7684\u5de8\u8d44\u4fee\u7f2e\u6728\u697c\u4e3a\u4e1c\u6d77\u7701\u6350\u5efa\u7b2c\u4e00\u5ea7\u68cb\u827a\u535a\u7269\u9986\uff0c\u201d\u4e0d\u6653\u5f97\u90b5\u81f3\u521a\u4f55\u65f6\u5c06\u4e39\u4e95\u5df7\u89c4\u5212\u8be6\u56fe\u6302\u5728\u68cb\u9986\u7684\u4e09\u697c\uff0c\u8fd9\u4eba\u592a\u9b3c\uff0c\u5f20\u606a\u3001\u674e\u660e\u5b66\u3001\u674e\u6bc5\u534e\u968f\u8bb8\u745e\u5e73\u4e0a\u697c\u65f6\uff0c\u90b5\u81f3\u521a\u6b63\u62ff\u7740\u805a\u5149\u7b14\u5728\u89c4\u5212\u8be6\u56fe\u4e0a\u4e3a\u674e\u8fdc\u6e56\u4e0e\u7701\u653f\u5e9c\u966a\u540c\u5b98\u5458\u8bb2\u89e3\u4e39\u4e95\u5df7\u7684\u6539\u9020\u65b9\u6848\uff0c\u770b\u5f97\u51fa\u4ed6\u8fd8\u771f\u662f\u82b1\u4e86\u4e00\u756a\u5fc3\u601d\u3002\u5f20\u606a\u4e0a\u697c\u6765\uff0c\u5b89\u9759\u7684\u7ad9\u5728\u697c\u68af\u65c1\uff0c\u542c\u7740\u90b5\u81f3\u521a\u7ee7\u7eed\u5728\u90a3\u91cc\u5ffd\u60a0\uff0c\u201c\u9664\u4e86\u68cb\u827a\u535a\u7269\u9986\u4e4b\u5916\uff0c\u5728\u4e39\u4e95\u5df7\u7684\u5317\u6bb5\uff0c\u6211\u4eec\u4e16\u7eaa\u9526\u6e56\u5c06\u4e3a\u6d77\u5dde\u5e02\u6c11\u518d\u65e0\u507f\u6350\u5efa\u4e00\u5ea7\u84dd\u5370\u5e03\u67d3\u535a\u7269\u9986\u4e0e\u6d77\u5dde\u74f7\u535a\u7269\u9986\uff0c\u4ee5\u6b64\u6765\u5ba3\u626c\u6d77\u5dde\u5e02\u7684\u6587\u5316\u4f20\u7edf\u3002\u5728\u5e03\u67d3\u4e0e\u6d77\u5dde\u74f7\u535a\u7269\u9986\u4ee5\u5317\u533a\u57df\u7684\u5efa\u7b51\uff0c\u6211\u4eec\u5c06\u6539\u9020\u6210\u53e4\u5178\u98ce\u683c\u4e0e\u73b0\u4ee3\u5316\u4eba\u5c45\u5b8c\u7f8e\u7ed3\u5408\u7684\u4f4f\u5b85\u793a\u8303\u533a\uff0c\u4e39\u4e95\u5df7\u6539\u9020\u5b8c\u6210\u4e4b\u540e\uff0c\u5c06\u5f62\u6210\u4e00\u5ea7\u878d\u53e4\u5178\u98ce\u683c\u6c11\u5c45\u3001\u535a\u7269\u5c55\u89c8\u3001\u9910\u996e\u9152\u5427\u3001\u4f1a\u8bae\u4e2d\u5fc3\u3001\u8d2d\u7269\u65c5\u6e38\u7684\u4f11\u95f2\u6587\u5316\u7efc\u5408\u4f53\uff1b\u4e3a\u4e86\u8ba9\u5e02\u91cc\u5bf9\u6211\u4eec\u4e16\u7eaa\u9526\u6e56\u6709\u4fe1\u5fc3\uff0c\u5c06\u6574\u6761\u4e39\u4e95\u5df7\u653e\u624b\u4ea4\u7ed9\u6211\u4eec\u4e16\u7eaa\u9526\u6e56\u6765\u6539\u9020\u3001\u8fd0\u8425\uff0c\u6211\u4eec\u7279\u5730\u5148\u671f\u6295\u5165\u8d44\u91d1\u6539\u9020\u4e86\u51e0\u680b\u529f\u80fd\u5404\u5f02\u7684\u5b85\u5b50\uff0c\u6709\u8fd9\u5ea7\u535a\u7269\u9662\uff0c\u6709\u9910\u9986\uff0c\u6709\u9152\u5427\uff0c\u4e5f\u6709\u7b26\u5408\u73b0\u4ee3\u4eba\u5c45\u8981\u6c42\u7684\u7cbe\u54c1\u6c11\u5c45\uff0c\u8fd9\u6b21\u6b63\u597d\u6709\u673a\u5728\u674e\u7701\u957f\u9762\u524d\u5356\u5f04\u4e00\u4e0b\uff0c\u4e5f\u60f3\u8bf7\u674e\u7701\u957f\u5e2e\u6211\u4eec\u68c0\u67e5\u4e00\u4e0b\uff0c\u770b\u770b\u6211\u4eec\u7684\u5de5\u4f5c\u662f\u5426\u6709\u4ec0\u4e48\u9057\u6f0f\u7684\u5730\u65b9\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u884c\u7a0b\u662f\u4e0d\u662f\u6709\u4e9b\u7d27\uff1f\u201d\u82cf\u9038\u7fa4\u4f4e\u5934\u4e0e\u5510\u5b66\u8c26\u5546\u91cf\u3002\u5b89\u6392\u884c\u7a0b\u65f6\uff0c\u82cf\u9038\u7fa4\u4e0e\u5468\u5bcc\u660e\u64cd\u4f5c\u7740\u7279\u610f\u591a\u5b89\u6392\u4e86\u80fd\u4f53\u73b0\u5468\u5bcc\u660e\u653f\u7ee9\u7684\u5730\u65b9\uff0c\u5510\u5b66\u8c26\u4e5f\u6ca1\u6709\u8ddf\u4ed6\u4eec\u4e89\u4ec0\u4e48\uff0c\u6bd5\u7adf\u82cf\u9038\u7fa4\u4e0e\u674e\u8fdc\u6e56\u4eb2\u8fd1\uff0c\u5728\u8fd9\u4e0a\u9762\u4e89\u6267\u662f\u6ca1\u6709\u610f\u4e49\u7684\u3002\u90b5\u81f3\u521a\u6a2a\u63d2\u4e00\u817f\u662f\u8c01\u4e5f\u60f3\u4e0d\u5230\u7684\uff0c\u5c06\u674e\u8fdc\u6e56\u4e0e\u7701\u91cc\u966a\u540c\u5b98\u5458\u7684\u6ce8\u610f\u529b\u5438\u5f15\u5230\u8fd9\u91cc\u6765\uff0c\u8fd8\u60f3\u7ee7\u7eed\u9886\u7740\u674e\u8fdc\u6e56\u6309\u7167\u4ed6\u4eec\u7684\u8def\u7ebf\u8d70\u4e0b\u53bb\uff1b\u82cf\u9038\u7fa4\u5f53\u7136\u4e0d\u613f\u610f\uff0c\u60f3\u575a\u6301\u56de\u5230\u65e2\u5b9a\u7684\u884c\u7a0b\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u201c\u6ca1\u5173\u7cfb\uff0c\u53ea\u9700\u8981\u660e\u5929\u4e5d\u70b9\u949f\u4e4b\u524d\u8ba9\u6211\u8d76\u56de\u7701\u91cc\u53c2\u52a0\u4e00\u4e2a\u4f1a\u8bae\u5c31\u884c\uff0c\u201d\u674e\u8fdc\u6e56\u542c\u5230\u82cf\u9038\u7fa4\u4f4e\u58f0\u8ddf\u5510\u5b66\u8c26\u8bf4\u7684\u8bdd\uff0c\u8bf4\u4e86\u4e00\u53e5\uff0c\u4ed6\u7684\u786e\u7ed9\u8fd9\u5ea7\u582a\u79f0\u5de8\u578b\u6728\u96d5\u7684\u65b9\u697c\u6240\u4f53\u73b0\u51fa\u6765\u7684\u53e4\u5178\u97f5\u7f8e\u5438\u5f15\u4f4f\uff0c\u5018\u82e5\u6beb\u4e0d\u6253\u6298\u6263\u7684\u5c06\u90b5\u81f3\u521a\u6240\u63cf\u8ff0\u7684\u89c4\u5212\u90fd\u4f53\u73b0\u51fa\u6765\uff0c\u4e39\u4e95\u5df7\u5c06\u6210\u4e3a\u53e4\u5efa\u7b51\u4fdd\u62a4\u4e0e\u5546\u4e1a\u5f00\u53d1\u7684\u5178\u8303\uff0c\u4e1c\u6d77\u7701\u5883\u5185\u4e5f\u4e0d\u6b62\u8fd9\u4e00\u5904\u53e4\u5efa\u7b51\u7fa4\u5b58\u5728\u6587\u5316\u9057\u8ff9\u4fdd\u5b58\u4e0e\u57ce\u5e02\u73b0\u4ee3\u5316\u5efa\u8bbe\u7684\u77db\u76fe\uff0c\u4e0d\u8fc7\u4ed6\u53c8\u5173\u5fc3\u8d77\u4e00\u4e2a\u95ee\u9898\uff0c\u4fa7\u5934\u95ee\u5510\u5b66\u8c26\uff0c\u201c\u4e16\u7eaa\u9526\u6e56\u4e0e\u9526\u6e56\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u770b\u89c1\u5f20\u606a\u4ed6\u4eec\u4e0a\u697c\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u8fd8\u8981\u95ee\u5f53\u4e8b\u4eba\uff1f\u201d\u56de\u5934\u770b\u5411\u5f20\u606a\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u8f6c\u8fc7\u6765\u5934\u6765\uff0c\u5f20\u606a\u8eab\u5b50\u534a\u4f9d\u7740\u697c\u68af\u6276\u624b\uff0c\u6446\u624b\u8bf4\u9053\uff1a\u201c\u8ddf\u6211\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u6211\u5c0f\u53d4\u4ed6\u4eec\u9f13\u6363\u51fa\u6765\u7684\u73a9\u827a\uff0c\u6211\u90fd\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u6211\u5e73\u65f6\u8fd8\u8981\u4e0a\u5b66\u7684\uff0c\u54ea\u6709\u95f2\u5de5\u592b\u53c2\u4e0e\u4ed6\u4eec\u7684\u4e8b\u2026\u2026\u201d\u5f20\u606a\u5fd9\u7740\u5c06\u81ea\u5df1\u6487\u6e05\uff0c\u4e5f\u4e0d\u8d70\u5230\u524d\u9762\u53bb\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u4fbf\u4ecb\u7ecd\u65e9\u7ad9\u5728\u4e00\u8d77\u3001\u8fd8\u6ca1\u6709\u673a\u4f1a\u63d2\u4e0a\u8bdd\u7684\u5f20\u77e5\u975e\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u77e5\u975e\u662f\u65b0\u829c\u5e02\u526f\u5e02\u957f\u5f20\u77e5\u884c\u7684\u5802\u5144\u5f1f\uff0c\u4ed6\u540d\u4e0b\u7684\u5b8f\u8fdc\u5b9e\u4e1a\u516c\u53f8\u8fd9\u4e24\u5e74\u5f00\u53d1\u7684\u5b8f\u8fdc\u5c0f\u533a\uff0c\u4e3a\u5e02\u91cc\u89e3\u51b3\u4e86\u4e09\u5343\u4e2a\u62c6\u8fc1\u5bb6\u5ead\u7684\u4f4f\u623f\u95ee\u9898\uff0c\u6c99\u7530\u897f\u7247\u6240\u6709\u7684\u62c6\u8fc1\u5bb6\u5ead\u90fd\u5c06\u5728\u5b8f\u8fdc\u5c0f\u533a\u5f97\u5230\u59a5\u5584\u7684\u5b89\u7f6e\uff0c\u4e3a\u5e02\u533a\u505a\u51fa\u5f88\u5927\u7684\u8d21\u732e\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u7684\u8fd9\u756a\u8bdd\uff0c\u5468\u5bcc\u660e\u3001\u82cf\u9038\u7fa4\u90fd\u6311\u4e0d\u534a\u70b9\u7684\u6bdb\u75c5\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u8c26\u7136\u4e00\u7b11\uff0c\u4ecb\u7ecd\u4e16\u7eaa\u9526\u6e56\u7684\u4e00\u4e9b\u60c5\u51b5\uff0c\u81f3\u5c11\u4ece\u76ee\u524d\u7684\u80a1\u6743\u7ed3\u6784\u4e0a\uff0c\u4e16\u7eaa\u9526\u6e56\u4e0e\u9526\u6e56\u662f\u622a\u7136\u65e0\u5173\u7684\u3002</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u4e4b\u524d\u6ca1\u6709\u542c\u8bf4\u8fc7\u4e16\u7eaa\u9526\u6e56\u4e0e\u4e39\u4e95\u5df7\u6539\u9020\u9879\u76ee\uff0c\u4f46\u662f\u5bf9\u5b8f\u8fdc\u5b9e\u4e1a\u7edd\u4e0d\u4f1a\u964c\u751f\u3002</p><p>\u3000\u3000\u201c\u6211\u5728\u7701\u91cc\u65e9\u5c31\u542c\u8bf4\u8fc7\u5b8f\u8fdc\u5b9e\u4e1a\u7684\u4e8b\u3001\u7ee9\uff0c\u4e1c\u6d77\u7701\u9700\u8981\u4f60\u8fd9\u6837\u6709\u793e\u4f1a\u8d23\u4efb\u611f\u7684\u4f01\u4e1a\u5bb6\u53c2\u4e0e\u5730\u65b9\u7684\u5efa\u8bbe\u2026\u2026\u201d\u674e\u8fdc\u6e56\u4f38\u624b\u4e0e\u5f20\u77e5\u975e\u63e1\u624b\uff0c\u505c\u987f\u4e86\u4e00\u4f1a\u513f\uff0c\u4ee5\u4fbf\u968f\u884c\u7684\u8bb0\u8005\u62cd\u7167\u3002</p><p>\u3000\u3000\u4e0d\u8981\u8bf4\u5f20\u77e5\u975e\u4e0e\u5f20\u77e5\u884c\u4e4b\u95f4\u8fdc\u4e86\u4e00\u5c42\u7684\u5173\u7cfb\u5e76\u4e0d\u76f4\u63a5\u62b5\u89e6\u4e2d\u592e\u7684\u6709\u5173\u89c4\u5b9a\uff0c\u5373\u4f7f\u62ff\u76f8\u5bf9\u4e25\u683c\u7684\u76ee\u5149\u53bb\u5ba1\u89c6\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u5b8f\u8fdc\u5b9e\u4e1a\u7684\u4e0d\u662f\u3002</p><p>\u3000\u3000\u5546\u4eba\u725f\u5229\uff0c\u7406\u6240\u5f53\u7136\uff0c\u725f\u5229\u4e4b\u4f59\u4e0d\u5fd8\u4e3a\u5730\u65b9\u505a\u51fa\u8d21\u732e\uff0c\u5c31\u7279\u4e3a\u4e0d\u6613\u3002\u674e\u8fdc\u6e56\u9700\u8981\u8fd9\u6837\u7684\u653f\u6cbb\u8868\u6001\u6765\u5ba3\u626c\u4ed6\u7684\u6cbb\u7701\u7406\u5ff5\uff0c\u5728\u884c\u7a0b\u4e0a\u4e5f\u6ca1\u6709\u5b89\u6392\u53c2\u89c2\u5b8f\u8fdc\u5c0f\u533a\uff0c\u4ed6\u5728\u8003\u8651\u8981\u4e0d\u8981\u5f7b\u5e95\u7684\u6539\u53d8\u884c\u7a0b\uff0c\u53c2\u89c2\u5b8f\u8fdc\u5c0f\u533a\uff0c\u6bd5\u7adf\u6709\u5229\u6539\u5584\u4ed6\u7684\u4eb2\u6c11\u5f62\u8c61\uff0c\u4e0d\u8fc7\u4e5f\u6df1\u523b\u4f53\u4f1a\u5230\u5510\u5b66\u8c26\u5728\u6d77\u5dde\u7684\u6839\u57fa\u4e4b\u7a33\u5065\u3002</p><p>\u3000\u3000\u7ec4\u5efa\u5e02\u5c5e\u63a7\u80a1\u516c\u53f8\u76d8\u6d3b\u56fd\u6709\u8d44\u4ea7\u5f7b\u5e95\u6539\u5584\u6570\u5341\u5bb6\u56fd\u6709\u4f01\u4e1a\u7684\u7ecf\u8425\u9762\u8c8c\u3001\u533a\u57df\u7ecf\u6d4e\u7684\u5d1b\u8d77\u3001\u57ce\u5e02\u6cbb\u5b89\u4e0e\u4eba\u5c45\u73af\u5883\u7684\u6539\u5584\u3001\u5bbd\u677e\u7684\u5e72\u7fa4\u5173\u7cfb\uff0c\u57ce\u5e02\u5728\u56fd\u5185\u7684\u7206\u5149\u7387\u3001\u77e5\u540d\u5ea6\u4e5f\u9010\u5e74\u63d0\u9ad8\uff0c\u8fd9\u4e9b\u90fd\u662f\u5510\u5b66\u8c26\u8eab\u4e0a\u6240\u627f\u8f7d\u7684\u653f\u7ee9\u95ea\u5149\u70b9\uff0c\u8981\u662f\u4ed6\u5728\u4e0a\u5c42\u518d\u6709\u8fc7\u786c\u7684\u5173\u7cfb\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u8d70\u4e0a\u706b\u7bad\u5f0f\u7684\u5347\u8fc1\u4e4b\u8def\u3002\u5373\u4f7f\u5468\u5bcc\u660e\u4e0d\u4e3b\u52a8\u63d0\u51fa\u9000\u5c45\u4e8c\u7ebf\uff0c\u7701\u91cc\u4e5f\u8981\u8003\u8651\u5c06\u5468\u5bcc\u660e\u632a\u5f00\u7ed9\u5510\u5b66\u8c26\u8ba9\u4f4d\u5b50\u3002</p><p>\u3000\u3000\u5b98\u573a\u6709\u6f5c\u89c4\u5219\u4e0d\u5047\uff0c\u4f46\u662f\u660e\u9762\u4e0a\u7684\u89c4\u5219\uff0c\u4e5f\u662f\u90fd\u8981\u9075\u5b88\u7684\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u95ea\u4eae\u7684\u653f\u7ee9\u4e4b\u540e\uff0c\u90fd\u7ad9\u7740\u8c01\uff1f</p><p>\u3000\u3000\u6d77\u5dde\u63a7\u80a1\u662f\u5f20\u77e5\u884c\u5728\u79bb\u5f00\u6d77\u5dde\u4e4b\u524d\u7ec4\u5efa\u5c31\u53d6\u5f97\u5f88\u5927\u7684\u6210\u5c31\uff0c\u57ce\u5357\u533a\u57df\u7ecf\u6d4e\u7684\u5d1b\u8d77\u4e0e\u9526\u6e56\u6709\u7740\u6781\u5927\u7684\u5173\u7cfb\uff0c\u6c99\u7530\u897f\u7247\u62c6\u8fc1\u65f6\uff0c\u5e72\u7fa4\u5173\u7cfb\u7d27\u5f20\uff0c\u662f\u5f20\u77e5\u975e\u7684\u5b8f\u8fdc\u5b9e\u4e1a\u4e3b\u52a8\u627f\u62c5\u5b89\u7f6e\u623f\u7684\u5efa\u8bbe\u91cd\u4efb\u3002</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u4e0e\u5f20\u77e5\u884c\u6709\u8fc7\u51e0\u9762\u4e4b\u7f18\uff0c\u6ca1\u6709\u600e\u4e48\u4ea4\u8c08\u8fc7\uff0c\u662f\u4e00\u4e2a\u9ad8\u7626\u3001\u5112\u96c5\u3001\u4e66\u751f\u6c14\u91cd\u7684\u4eba\uff0c\u5f20\u77e5\u975e\u8fd9\u624d\u521d\u6b21\u89c1\u9762\uff0c\u8eab\u9ad8\u4f53\u5065\uff0c\u7cbe\u529b\u5145\u6c9b\uff0c\u76ee\u5149\u70af\u70af\u6709\u795e\uff0c\u900f\u51fa\u7ecf\u5546\u4eba\u7684\u7cbe\u660e\u4e0e\u5706\u6ed1\uff0c\u90fd\u662f\u6770\u51fa\u4e0d\u51e1\u7684\u4eba\u7269\uff0c\u4f46\u662f\u5728\u5f20\u606a\u9762\u524d\uff0c\u90fd\u8981\u663e\u5f97\u9eef\u6de1\u65e0\u5149\u5427\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u5f20\u606a\u5927\u9020\u58f0\u52bf\u8feb\u4f7f\u81ea\u5df1\u8ba9\u6b65\u7684\u884c\u4e3a\uff0c\u674e\u8fdc\u6e56\u5fc3\u91cc\u9887\u4e3a\u4e0d\u6ee1\uff0c\u4e0e\u5f20\u77e5\u975e\u63e1\u624b\uff0c\u6446\u51fa\u59ff\u52bf\u7ed9\u8bb0\u8005\u62cd\u7167\u65f6\uff0c\u773c\u89d2\u4f59\u5149\u5374\u5728\u6253\u91cf\u5728\u4f9d\u7ad9\u5728\u697c\u68af\u53e3\u7684\u5f20\u606a\uff1a\u4e00\u4e2a\u8f7b\u677e\u81ea\u7136\u3001\u4eff\u4f5b\u5230\u535a\u7269\u9986\u6765\u53c2\u89c2\u7684\u5b66\u751f\u6a21\u6837\uff1b\u6362\u4e00\u4e2a\u89d2\u5ea6\uff0c\u8fd9\u4e2a\u672a\u6ee1\u4e8c\u5341\u5e74\u7684\u9752\u5e74\uff0c\u80fd\u5728\u56fd\u5185\u5f53\u524d\u7684\u65b0\u95fb\u4f53\u5236\u4e0b\uff0c\u5c06\u58f0\u52bf\u9020\u5f97\u5982\u6b64\u7684\u5982\u706b\u5982\u837c\uff0c\u5c31\u7edd\u975e\u5bfb\u5e38\u7684\u624b\u8155\u3002</p><p>\u3000\u3000\u5c11\u5e74\u53ef\u754f\uff01</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u8fd9\u65f6\u5f53\u7136\u4e0d\u80fd\u5bf9\u5f20\u606a\u7684\u5230\u6765\u89c6\u800c\u4e0d\u89c1\uff0c\u7b49\u8bb0\u8005\u62cd\u5b8c\u7167\uff0c\u9694\u7740\u4eba\u7fa4\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u600e\u4e48\u4e0d\u7ad9\u5230\u524d\u9762\u6765\uff1f\u201d\u597d\u50cf\u4e4b\u524d\u4e24\u4eba\u6beb\u65e0\u82a5\u8482\u3002</p><p>\u3000\u3000\u5f20\u606a\u54c2\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6015\u95ea\u5149\u706f\u4f1a\u8000\u7740\u773c\u775b\u2026\u2026\u201d\u8bf4\u7740\u8bdd\uff0c\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u201c\u89c1\u98ce\u4e5f\u4e0d\u884c\uff0c\u53ef\u80fd\u662f\u773c\u75c5\u3002\u201d</p><p>\u3000\u3000\u8fd9\u6837\u7684\u9b3c\u8bdd\u504f\u8ba9\u4ed6\u8bf4\u5f97\u7406\u6240\u5f53\u7136\uff0c\u8fd8\u6709\u4e9b\u5c11\u5e74\u7eaf\u771f\u7684\u6c14\u606f\uff1b\u82cf\u9038\u7fa4\u5fc3\u60f3\uff1a\u771f\u662f\u65e0\u8d56\uff0c\u504f\u504f\u53c8\u4e0d\u80fd\u5f00\u53e3\u7533\u65a5\u4ed6\u4e0d\u8bc6\u62ac\u4e3e\u3002\u4ee5\u7231\u8fbe\u96c6\u56e2\u4eca\u65e5\u7684\u58f0\u52bf\uff0c\u4ed6\u4e5f\u5f97\u8bc6\u65f6\u52a1\u907f\u514d\u4e0e\u4e4b\u76f4\u63a5\u51b2\u7a81\u3002</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u7b11\u4e86\u7b11\uff0c\u4e0d\u4ee5\u4e3a\u610f\uff0c\u8fd9\u6837\u7684\u5927\u5ea6\u4ed6\u8fd8\u662f\u8981\u8868\u73b0\u51fa\u6765\u7684\uff0c\u4f4e\u5934\u5429\u5490\u4e86\u9646\u6587\u592b\u51e0\u53e5\uff1b\u9646\u6587\u592b\u8d70\u5230\u4e00\u65c1\u8ba9\u8bb0\u8005\u4eec\u90fd\u5c06\u7167\u76f8\u673a\u7b49\u90fd\u6536\u8d77\u6765\uff0c\u64b0\u5199\u62a5\u9053\u65f6\uff0c\u4e5f\u8981\u5c06\u8fd9\u4e00\u6bb5\u7ed9\u5ffd\u7565\u6389\uff1a\u8fd9\u6837\u4e00\u4e2a\u4eba\u7269\u66b4\u9732\u5728\u516c\u4f17\u9762\u524d\u5c31\u6709\u4e9b\u60ca\u4e16\u9a87\u4fd7\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u5230\u524d\u9762\uff0c\u5411\u674e\u8fdc\u6e56\u4ecb\u7ecd\u674e\u660e\u5b66\u3001\u674e\u6bc5\u534e\u3002\u674e\u660e\u5b66\uff0c\u674e\u8fdc\u6e56\u4e5f\u4e0d\u964c\u751f\uff0c\u5bf9\u4ed6\u7684\u5173\u6ce8\uff0c\u81f3\u5c11\u8981\u8d85\u8fc7\u5bf9\u6d77\u5dde\u5e02\u59d4\u4e00\u822c\u5e38\u59d4\u6210\u5458\u7684\u5173\u6ce8\uff0c\u6d77\u5dde\u63a7\u80a1\u7684\u7ecf\u9a8c\u662f\u76f8\u5f53\u6709\u76ca\u5e76\u4e14\u6709\u5f71\u54cd\u529b\u7684\u7ecf\u9a8c\uff0c\u8981\u662f\u6d77\u5dde\u5e02\u59d4\u8003\u8651\u7ed9\u674e\u660e\u5b66\u6302\u526f\u5e02\u957f\u7684\u8854\uff0c\u7701\u91cc\u4e5f\u4f1a\u540c\u610f\uff1b\u6bd5\u7adf\u4ed6\u662f\u6d77\u5dde\u63a7\u80a1\u7684\u5b9e\u9645\u7ba1\u7406\u8005\u3002\u674e\u6bc5\u534e\uff0c\u674e\u8fdc\u6e56\u5c31\u5f88\u964c\u751f\u4e86\uff0c\u6d77\u5dde\u5e02\u5546\u4e1a\u94f6\u884c\u7ec4\u5efa\u8fd8\u6ca1\u6709\u51e0\u4e2a\u6708\uff0c\u8fd8\u6ca1\u6709\u592a\u5927\u7684\u5f71\u54cd\u529b\uff0c\u5373\u4f7f\u5728\u6d77\u5dde\u5e02\u9012\u4e0a\u6765\u7684\u6587\u4ef6\u4e2d\u770b\u5230\u674e\u6bc5\u534e\u7684\u540d\u5b57\uff0c\u90fd\u6ca1\u6709\u7559\u4e0b\u592a\u6df1\u7684\u5370\u8c61\u3002</p><p>\u3000\u3000\u5f20\u606a\u8981\u5c06\u674e\u6bc5\u534e\u63a8\u5230\u524d\u53f0\uff0c\u662f\u8003\u8651\u5230\u4eca\u5929\u4e0e\u674e\u8fdc\u6e56\u7684\u8bdd\u9898\u4e2d\u4f1a\u63d0\u5230\u6c11\u8425\u4f01\u4e1a\u5728\u56fd\u6c11\u7ecf\u6d4e\u4e2d\u7684\u5730\u4f4d\u95ee\u9898\u3002\u652f\u6301\u5730\u65b9\u6c11\u8425\u4f01\u4e1a\u53d1\u5c55\u7684\u63a8\u52a8\u529b\u6765\u81ea\u54ea\u91cc\uff1f\u5730\u65b9\u7ed9\u51fa\u5e73\u7b49\u7ade\u4e89\u7684\u653f\u7b56\u90a3\u662f\u5fc5\u987b\u7684\uff0c\u4f46\u662f\u91d1\u878d\u8d44\u672c\u7684\u652f\u6301\uff0c\u5bf9\u773c\u4e0b\u8fd8\u5f88\u8106\u5f31\u3001\u5b9e\u529b\u8fd8\u5f88\u5f31\u5c0f\u7684\u6c11\u8425\u7ecf\u6d4e\u4f53\u5c31\u6781\u4e3a\u91cd\u8981\u3002\u56db\u5927\u56fd\u6709\u94f6\u884c\u53d7\u5730\u65b9\u653f\u7b56\u7684\u5f71\u54cd\u5f88\u5c0f\uff0c\u6c11\u8425\u4f01\u4e1a\u60f3\u4ece\u56db\u5927\u56fd\u6709\u94f6\u884c\u83b7\u5f97\u91d1\u878d\u652f\u6301\u7684\u96be\u5ea6\u4e0e\u6210\u672c\u90fd\u5f88\u9ad8\uff0c\u90a3\u5982\u4f55\u53d1\u6325\u597d\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\u7684\u4f5c\u7528\u5c31\u5f88\u5173\u952e\u3002</p><p>\u3000\u3000\u5728\u68cb\u9986\u7a0d\u4f5c\u505c\u7559\uff0c\u4fbf\u4e0b\u697c\u53bb\u53c2\u89c2\u4e39\u4e95\u5df7\u5df2\u7ecf\u6539\u9020\u597d\u7684\u5efa\u7b51\uff0c\u8fd9\u4e9b\u5efa\u7b51\u5176\u5b9e\u90fd\u662f\u5728\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u5f00\u53d1\u96c6\u56e2\u7ec4\u5efa\u4e4b\u524d\u5c31\u7531\u5f20\u606a\u51fa\u8d44\u6539\u9020\u5b8c\u6210\uff0c\u9664\u4e86\u7eaf\u6728\u7ed3\u6784\u7684\u53e4\u65b9\u697c\u68cb\u9986\uff0c\u8fd8\u6709\u9648\u5947\u7684\u516b\u9526\u73cd\u79c1\u623f\u83dc\u9986\u3001\u78a7\u6d77\u4e91\u5929\u9152\u5427\u7b49\u5df2\u7ecf\u6b63\u5f0f\u8fdb\u884c\u5546\u4e1a\u8fd0\u8425\u7684\u5efa\u7b51\uff0c\u8fd8\u6709\u78a7\u6d77\u4e91\u5929\u9152\u5427\u80cc\u540e\u3001\u9526\u6e56\u62ff\u6765\u62db\u5f85\u8d35\u5bbe\u6240\u7528\u7684\u4e00\u5904\u5b85\u5b50\u3002</p></article>"}}}]);
