(window.webpackJsonp=window.webpackJsonp||[]).push([[538],{560:function(p){p.exports={chapterTitle:"\u7b2c\u5341\u4e00\u5377 \u4e1c\u57df\u7bc7\uff08\u4e0a\uff09\u5f7b\u68ee\u5854",title:"\u5e8f\u7ae0",content:"<article><h1>\u7b2c\u5341\u4e00\u5377 \u4e1c\u57df\u7bc7\uff08\u4e0a\uff09\u5f7b\u68ee\u5854</h1><h2>\u5e8f\u7ae0</h2><p>\u4f3d\u6d85\u62c4\u7740\u957f\u621f\uff0c\u7ad9\u5728\u5251\u6cb3\u7684\u897f\u8fb9\uff0c\u52aa\u529b\u671d\u7740\u4e1c\u5cb8\u773a\u671b\u3002\u4ed6\u7684\u6218\u9a7c\u534a\u8dea\u5728\u5cb8\u8fb9\uff0c\u4f38\u957f\u8116\u9888\uff0c\u5495\u6ecb\u5495\u6ecb\u5730\u7545\u996e\u6cb3\u6c34\uff0c\u4e0d\u65f6\u53d1\u51fa\u54cd\u4eae\u7684\u5636\u9e23\u58f0\u3002\u5251\u6cb3\u7684\u6c34\u9762\u5f88\u5bbd\uff0c\u4f3d\u6d85\u7684\u89c6\u529b\u4e0d\u9519\uff0c\u4ed6\u80fd\u591f\u770b\u89c1\u5bf9\u5cb8\u91cd\u91cd\u53e0\u53e0\u7684\u8425\u5792\uff0c\u91cc\u9762\u4eba\u6765\u4eba\u5f80\uff0c\u8425\u5792\u4e2d\u9ad8\u9ad8\u7ad6\u7740\u51e0\u5341\u9762\u4e94\u989c\u516d\u8272\u7684\u65d7\u5e1c\uff0c\u8fce\u98ce\u62db\u5c55\uff0c\u4f46\u4ed6\u770b\u4e0d\u6e05\u695a\u65d7\u5e1c\u4e0a\u753b\u7740\u4ec0\u4e48\u3002</p><p>\u201c\u7ed9\uff0c\u5934\u513f\u3002\u201d\u4e00\u4e2a\u58eb\u5175\u8d70\u8fc7\u6765\uff0c\u9012\u7ed9\u4f3d\u6d85\u4e00\u4e2a\u4e1c\u897f\u3002</p><p>\u90a3\u662f\u4e00\u4e2a\u957f\u957f\u7684\u5706\u67f1\u4f53\uff0c\u770b\u8d77\u6765\u50cf\u662f\u4e00\u6839\u9ec4\u94dc\u77ed\u68cd\uff0c\u5728\u5915\u9633\u4e0b\u53d1\u51fa\u4e00\u95ea\u4e00\u95ea\u7684\u51b7\u5149\u3002\u90a3\u4e9b\u585e\u5c14\u7ea2\u888d\u5deb\u5e08\u79f0\u4e4b\u4e3a\u201c\u671b\u8fdc\u955c\u201d\uff0c\u662f\u4e00\u79cd\u70bc\u91d1\u672f\u4ea7\u7269\uff0c\u4ef7\u683c\u975e\u5e38\u6602\u8d35\uff0c\u4f46\u7684\u786e\u7269\u6709\u6240\u503c\u3002</p><p>\u4f3d\u6d85\u5c06\u5b83\u51d1\u5728\u53f3\u773c\u524d\uff0c\u772f\u4e0a\u5de6\u773c\uff0c\u7136\u540e\u8fdc\u5904\u539f\u672c\u6a21\u7cca\u7684\u666f\u8272\u53d8\u5f97\u6e05\u6670\u8d77\u6765\uff0c\u4eff\u4f5b\u7a7a\u95f4\u7684\u8ddd\u79bb\u88ab\u4e00\u4e0b\u5b50\u7f29\u77ed\u4e86\u3002\u4ed6\u73b0\u5728\u53ef\u4ee5\u6e05\u695a\u770b\u89c1\u5bf9\u5cb8\u8425\u5792\u4e2d\u6bcf\u4e00\u4e2a\u58eb\u5175\u7684\u8138\uff0c\u770b\u89c1\u4ed6\u4eec\u7684\u9762\u90e8\u8868\u60c5\uff0c\u770b\u89c1\u4ed6\u4eec\u76d4\u7532\u4e0a\u7684\u82b1\u7eb9\u548c\u4f24\u75d5\u3002\u4f3d\u6d85\u5c06\u89c6\u7ebf\u4e0a\u79fb\uff0c\u52aa\u529b\u8fa8\u8ba4\u7740\u90a3\u4e9b\u65d7\u5e1c\u4e0a\u7684\u56fe\u6848\uff0c\u98ce\u5f88\u5927\uff0c\u65d7\u5e1c\u968f\u98ce\u7ffb\u8f6c\u4e0d\u606f\uff0c\u4f46\u671b\u8fdc\u955c\u4e0a\u9644\u7740\u7684\u9b54\u6cd5\u53ef\u4ee5\u7cbe\u786e\u6355\u6349\u52a8\u6001\u753b\u9762\u3002\u4ed6\u770b\u89c1\u4e86\u5404\u79cd\u5404\u6837\u7684\u52a8\u7269\uff1a\u632f\u7fc5\u9ad8\u98de\u7684\u96c4\u9e70\u3001\u6602\u9996\u5410\u4fe1\u7684\u6bd2\u86c7\u3001\u6234\u7740\u7d2b\u8272\u5341\u5b57\u73af\u7684\u6731\u9e6d\u3001\u989d\u5934\u6709\u7740\u6708\u4eae\u5fbd\u8bb0\u7684\u6bcd\u725b\uff0c\u4ee5\u53ca\u4e00\u53ea\u5f20\u53e3\u66f3\u5c3e\u3001\u5229\u9f7f\u68ee\u68ee\uff0c\u5728\u6d51\u6d4a\u6cb3\u6c34\u4e2d\u7ffb\u6eda\u7684\u9cc4\u9c7c\u3002</p><p>\u201c\u4e94\u4e2a\u795e\u738b\u90fd\u5230\u9f50\u4e86\uff0c\u201d\u4f3d\u6d85\u9ed8\u9ed8\u5730\u60f3\uff0c\u201c\u770b\u6765\u7684\u786e\u662f\u6253\u7b97\u51b3\u4e00\u80dc\u8d1f\u4e86\u3002\u201d</p><p>\u7a46\u7f55\u5e1d\u56fd\u7531\u4e94\u4f4d\u795e\u738b\u5171\u540c\u7edf\u6cbb\uff0c\u9e70\u3001\u86c7\u3001\u9e6d\u3001\u725b\u548c\u9cc4\u5206\u522b\u662f\u4ed6\u4eec\u7684\u8c61\u5f81\u3002\u5728\u4ee5\u5f80\u7684\u5386\u6b21\u6218\u4e89\u4e2d\uff0c\u4ece\u6765\u6ca1\u6709\u4e94\u4f4d\u795e\u738b\u540c\u65f6\u51fa\u73b0\u7684\u60c5\u5f62\uff0c\u81f3\u5c11\u636e\u4f3d\u6d85\u6240\u77e5\u8fd9\u662f\u7b2c\u4e00\u6b21\u3002\u4f5c\u4e3a\u9ad8\u7ea7\u5c06\u9886\uff0c\u4f3d\u6d85\u6e05\u695a\u5730\u77e5\u9053\u6bcf\u4e00\u79cd\u52a8\u7269\u4ee3\u8868\u7740\u8c01\uff0c\u4e5f\u5bf9\u6bcf\u4e00\u4f4d\u795e\u738b\u7684\u8d44\u6599\u6709\u6240\u4e86\u89e3\uff0c\u5c3d\u7ba1\u5982\u6b64\uff0c\u6bcf\u6b21\u770b\u5230\u8fd9\u4e9b\u65d7\u5e1c\uff0c\u4ed6\u8fd8\u662f\u5f88\u4e0d\u9002\u5e94\uff0c\u89c9\u5f97\u5f88\u523a\u773c\uff0c\u5b8c\u5168\u65e0\u6cd5\u7406\u89e3\u8fd9\u4e9b\u7a46\u7f55\u4eba\u600e\u4e48\u4f1a\u6709\u5982\u6b64\u5947\u7279\u7684\u98ce\u4fd7\u3002\u5728\u6069\u745f\uff0c\u795e\u738b\u88ab\u8ba4\u4e3a\u662f\u591c\u7a7a\u4e2d\u7684\u661f\u8fb0\uff0c\u9ad8\u9ad8\u5728\u4e0a\uff0c\u4fef\u89c6\u4f17\u751f\uff0c\u7a46\u7f55\u4eba\u5374\u5c06\u4ed6\u4eec\u7684\u795e\u738b\u4e0e\u8fd9\u4e9b\u5e73\u51e1\u4f4e\u8d31\u7684\u91ce\u517d\u5f7c\u6b64\u5bf9\u5e94\uff0c\u751a\u81f3\u6df7\u4e3a\u4e00\u8c08\uff0c\u5728\u4f3d\u6d85\u770b\u6765\uff0c\u65e0\u8bba\u600e\u4e48\u7528\u201c\u6587\u5316\u5dee\u5f02\u201d\u6765\u89e3\u91ca\uff0c\u8fd9\u90fd\u662f\u4e0d\u6298\u4e0d\u6263\u7684\u5192\u72af\u4e0e\u4eb5\u6e0e\uff0c\u662f\u5bf9\u795e\u738b\u7684\u83ab\u5927\u4e0d\u656c\u3002</p><p>\u6216\u8005\u8bf4\uff0c\u8fd9\u6070\u6070\u4ece\u53e6\u4e00\u65b9\u9762\u8bc1\u660e\uff0c\u90a3\u4e9b\u7a46\u7f55\u4eba\u7684\u6240\u8c13\u795e\u738b\uff0c\u5b9e\u9645\u4e0a\u90fd\u4e0d\u8fc7\u662f\u4e00\u7fa4\u4f2a\u795e\uff0c\u6ca1\u6709\u8d44\u683c\u4e0e\u6069\u745f\u7684\u771f\u795e\u76f8\u63d0\u5e76\u8bba\u3002</p><p>\u4f46\u6069\u745f\u7684\u771f\u795e\u5df2\u7ecf\u9668\u843d\u4e86\u3002</p><p>\u5c3d\u7ba1\u4e0d\u613f\u610f\u627f\u8ba4\uff0c\u4f46\u4ece\u73b0\u5b9e\u7684\u529b\u91cf\u5bf9\u6bd4\u4e0a\u770b\uff0c\u6069\u745f\u5df2\u7ecf\u843d\u5165\u4e86\u4e0b\u98ce\u3002\u7a46\u7f55\u4e0e\u6069\u745f\u662f\u6b7b\u654c\u4e0e\u5bbf\u4ec7\uff0c\u4e24\u56fd\u4e4b\u95f4\u7684\u7b2c\u4e00\u6b21\u6218\u4e89\u53ef\u4ee5\u4e0a\u6eaf\u5230\u4e24\u5343\u591a\u5e74\u524d\uff0c\u4e4b\u540e\u51b2\u7a81\u5c31\u518d\u4e5f\u6ca1\u6709\u771f\u6b63\u505c\u6b62\u8fc7\uff0c\u5e73\u5747\u6bcf\u4e03\u516b\u5341\u5e74\u5c31\u4f1a\u6709\u4e00\u6b21\u5927\u89c4\u6a21\u56fd\u6218\uff0c\u5c0f\u89c4\u6a21\u7684\u8fb9\u5883\u6469\u64e6\u66f4\u662f\u4e0d\u8ba1\u5176\u6570\u3002\u6069\u745f\u80dc\u5229\u8fc7\u5f88\u591a\u6b21\uff0c\u4e5f\u6218\u8d25\u8fc7\u5f88\u591a\u6b21\uff0c\u4f46\u7edd\u6ca1\u6709\u54ea\u4e00\u6b21\u4f1a\u8ba9\u7a46\u7f55\u4eba\u7684\u519b\u961f\u6253\u5230\u5251\u6cb3\u8fb9\u2014\u2014\u5251\u6cb3\u897f\u5cb8\u5c31\u662f\u6069\u745f\u7684\u738b\u57ce\u201c\u6602\u745f\u65af\u201d\u3002\u5b9e\u9645\u4e0a\uff0c\u5018\u82e5\u4e0d\u662f\u795e\u59ec\u6bbf\u4e0b\u7a81\u7136\u5c55\u73b0\u51fa\u60ca\u4eba\u7684\u519b\u4e8b\u624d\u534e\uff0c\u7387\u9886\u6069\u745f\u519b\u6253\u8d62\u4e86\u4e09\u4e2a\u6708\u524d\u7684\u90a3\u573a\u9f99\u5251\u5ce1\u8c37\u4e4b\u6218\uff0c\u5982\u4eca\u8fde\u9996\u90fd\u53ea\u6015\u5df2\u7ecf\u6ca6\u9677\u654c\u624b\u4e86\u3002</p><p>\u5c3d\u7ba1\u5982\u6b64\uff0c\u5f62\u52bf\u4ecd\u7136\u4e0d\u5bb9\u4e50\u89c2\u3002</p><p>\u4e0e\u7a46\u7f55\u5e1d\u56fd\u7684\u591a\u540d\u795e\u738b\u5171\u540c\u7edf\u6cbb\u4e0d\u540c\uff0c\u6069\u745f\u5e1d\u56fd\u7684\u541b\u4e3b\u662f\u552f\u4e00\u7684\uff0c\u81f3\u5c11\u4ece\u4e24\u5343\u516b\u767e\u5e74\u524d\u5f00\u59cb\uff0c\u201c\u96f7\u9706\u7684\u6267\u638c\u8005\u201d\u5409\u52d2\u4eca\u5c31\u662f\u6069\u745f\u7684\u552f\u4e00\u4e3b\u5bb0\uff0c\u81f3\u9ad8\u65e0\u4e0a\u7684\u795e\u738b\uff0c\u4ed6\u4ee5\u51e1\u4eba\u7684\u5f62\u6001\u9ad8\u8e1e\u738b\u5ea7\uff0c\u6bcf\u4e09\u767e\u5e74\u8fdb\u884c\u4e00\u6b21\u8f6c\u4e16\u91cd\u751f\uff0c\u7edf\u6cbb\u7740\u8fd9\u4e2a\u56fd\u5bb6\u7684\u5929\u7a7a\u3001\u5927\u5730\u4e0e\u6d77\u6d0b\uff0c\u638c\u63e1\u7740\u5b50\u6c11\u7684\u751f\u547d\u4e0e\u6b7b\u4ea1\u3002\u4ece\u53e4\u5230\u4eca\uff0c\u4ece\u6765\u6ca1\u6709\u4efb\u4f55\u4e00\u4f4d\u5e1d\u738b\u80fd\u591f\u7ef4\u6301\u8fd9\u6837\u957f\u4e45\u7684\u7edf\u6cbb\uff0c\u4e5f\u6ca1\u6709\u4efb\u4f55\u4e00\u4f4d\u738b\u8005\u80fd\u591f\u62e5\u6709\u8fd9\u6837\u663e\u8d6b\u7684\u6743\u5a01\uff0c\u7edd\u5bf9\u7684\u4e2d\u592e\u96c6\u6743\u5e26\u6765\u5f3a\u5927\u7684\u529b\u91cf\uff0c\u4f46\u5b83\u7684\u526f\u4f5c\u7528\u4e5f\u662f\u663e\u800c\u6613\u89c1\u7684\uff0c\u8fd9\u4e00\u70b9\uff0c\u5728\u5341\u516d\u5e74\u524d\u6e05\u695a\u5730\u663e\u73b0\u51fa\u6765\u3002</p><p>\u5341\u516d\u5e74\u524d\uff0c\u795e\u738b\u5409\u52d2\u4eca\u5728\u4e00\u6b21\u5de1\u6e38\u9014\u4e2d\u7a81\u7136\u53bb\u4e16\uff0c\u800c\u4e14\u6ca1\u6709\u50cf\u4ee5\u524d\u4e00\u6837\u8f6c\u751f\u3002\u796d\u53f8\u4eec\u5ba3\u79f0\u4ed6\u5728\u5929\u754c\u4f11\u606f\uff0c\u5728\u5c06\u6765\u7684\u67d0\u4e2a\u65f6\u95f4\u4f1a\u518d\u6b21\u964d\u4e34\u51e1\u95f4\uff0c\u4eba\u4eec\u5bf9\u8fd9\u79cd\u8bf4\u6cd5\u5c06\u4fe1\u5c06\u7591\uff0c\u5728\u5fd0\u5fd1\u4e0d\u5b89\u5730\u7b49\u5f85\u4e86\u516d\u5e74\u4e4b\u540e\uff0c\u5409\u52d2\u4eca\u5e76\u672a\u56de\u5f52\uff0c\u53cd\u800c\u662f\u795e\u738b\u7684\u201c\u4ee3\u8a00\u4eba\u201d\u3001\u9996\u5e2d\u5927\u796d\u53f8\u590f\u9c81\u5e15\u514b\u4e5f\u795e\u79d8\u5931\u8e2a\u3002\u6050\u614c\u7684\u60c5\u7eea\u5728\u6240\u6709\u4eba\u5fc3\u4e2d\u5f25\u6f2b\uff0c\u800c\u5206\u88c2\u7684\u840c\u82bd\u5f00\u59cb\u6084\u6084\u751f\u957f\uff0c\u81ea\u8ba4\u4e3a\u6709\u8d44\u683c\u89ca\u89ce\u738b\u4f4d\u7684\u5f3a\u8005\u4eec\u8822\u8822\u6b32\u52a8\uff0c\u5f7c\u6b64\u4e89\u6597\uff0c\u5931\u53bb\u4e86\u552f\u4e00\u541b\u738b\u7684\u6069\u745f\u5e1d\u56fd\u5728\u6781\u77ed\u65f6\u95f4\u5185\u5760\u5165\u6fd2\u4e34\u5d29\u6e83\u7684\u5883\u5730\u3002\u5317\u65b9\u7684\u5bbf\u654c\u81ea\u7136\u4e0d\u4f1a\u653e\u5f03\u8fd9\u79cd\u5929\u8d50\u826f\u673a\uff0c\u7a46\u7f55\u5e1d\u56fd\u7684\u519b\u961f\u8d8a\u8fc7\u8fb9\u5883\uff0c\u5728\u4e0d\u5230\u4e24\u5e74\u7684\u65f6\u95f4\u91cc\u5360\u9886\u4e86\u6069\u745f\u4e09\u5206\u4e4b\u4e00\u7684\u56fd\u571f\uff0c\u5982\u4eca\u66f4\u662f\u5df2\u7ecf\u62b5\u8fbe\u5251\u6cb3\uff0c\u5175\u4e34\u738b\u57ce\u4e4b\u4e0b\u3002\u8fd9\u79cd\u524d\u6240\u672a\u6709\u7684\u9669\u6076\u5c40\u9762\u8ba9\u5f88\u591a\u6069\u745f\u4eba\u5931\u53bb\u4e86\u4fe1\u5fc3\uff0c\u5373\u4fbf\u5728\u519b\u961f\u5185\u90e8\uff0c\u636e\u4f3d\u6d85\u6240\u77e5\uff0c\u4e5f\u6709\u4e0d\u5c11\u9ad8\u7ea7\u5c06\u9886\u5bf9\u6218\u5c40\u7684\u524d\u666f\u62b1\u6709\u60b2\u89c2\u7acb\u573a\uff0c\u751a\u81f3\u4e0e\u7a46\u7f55\u4eba\u6697\u901a\u6b3e\u66f2\uff0c\u5f7c\u6b64\u52fe\u7ed3\u3002</p><p>\u4f3d\u6d85\u662f\u4e00\u4f4d\u5fe0\u8bda\u7684\u52c7\u58eb\uff0c\u80c6\u602f\u6216\u8005\u80cc\u53db\uff0c\u4efb\u4f55\u65f6\u5019\u90fd\u4e0d\u5728\u4ed6\u7684\u9009\u62e9\u8303\u56f4\u4e4b\u5185\u3002\u4f46\u4ed6\u5e76\u4e0d\u806a\u660e\uff0c\u627e\u4e0d\u51fa\u80fd\u591f\u53d6\u5f97\u80dc\u5229\u7684\u65b9\u6cd5\uff0c\u8fd9\u4ee4\u4ed6\u7126\u8651\u4e0d\u5b89\uff0c\u5fe7\u5fc3\u5fe1\u5fe1\u3002</p><p>\u201c\u7d22\u65af\u4e5f\u6765\u4e86\u5417\uff1f\u201d\u4e00\u4e2a\u597d\u542c\u7684\u58f0\u97f3\u5728\u8033\u8fb9\u54cd\u8d77\u3002</p><p>\u201c\u6765\u4e86\uff0c\u201d\u4f3d\u6d85\u968f\u53e3\u56de\u7b54\uff0c\u201c\u6211\u770b\u89c1\u4e86\u4ed6\u7684\u6731\u9e6d\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u4ed6\u4e5f\u5728\u7684\u8bdd\uff0c\u90a3\u53ef\u5c31\u6709\u70b9\u9ebb\u70e6\u4e86\u3002\u201d</p><p>\u201c\u662f\u554a\u3002\u201d\u4f3d\u6d85\u8bf4\uff0c\u968f\u540e\u7a81\u7136\u53cd\u5e94\u8fc7\u6765\uff0c\u8d76\u5feb\u653e\u4e0b\u671b\u773c\u955c\uff0c\u4ed6\u770b\u89c1\u4e00\u4f4d\u9ed1\u8272\u957f\u53d1\uff0c\u6d45\u7d2b\u8272\u773c\u775b\u7684\u5c11\u5973\uff0c\u7a7f\u7740\u534e\u7f8e\u7684\u957f\u88d9\uff0c\u5934\u4e0a\u6234\u7740\u4e00\u9876\u767d\u8272\u738b\u51a0\uff0c\u5927\u7ea6\u5341\u516d\u4e03\u5c81\u7684\u5e74\u7eaa\uff0c\u8138\u4e0a\u8fd8\u7565\u5e26\u7740\u51e0\u5206\u7a1a\u6c14\uff0c\u5374\u5df2\u7ecf\u662f\u660e\u8273\u65e0\u4fe6\uff0c\u8ba9\u4eba\u4e0d\u6562\u76f4\u89c6\uff0c\u4eff\u4f5b\u591a\u770b\u4e00\u773c\u90fd\u662f\u4eb5\u6e0e\u3002\u201c\u795e\u59ec\u6bbf\u4e0b\uff0c\u4f60\u600e\u4e48\u7a81\u7136\u8dd1\u6765\u8fd9\u91cc\uff1f\u201d\u4ed6\u7d27\u5f20\u5730\u6253\u91cf\u7740\u56db\u5468\uff0c\u9632\u5907\u968f\u65f6\u6709\u53ef\u80fd\u51fa\u73b0\u7684\u654c\u4eba\uff0c\u201c\u592a\u5371\u9669\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u662f\u8fd8\u6ca1\u5230\u7ea6\u597d\u7684\u5f00\u6218\u65f6\u95f4\u561b\uff0c\u201d\u88ab\u79f0\u4e3a\u201c\u795e\u59ec\u201d\u7684\u5c11\u5973\u7b11\u7740\u8bf4\uff0c\u201c\u5341\u65e5\u4e4b\u540e\uff0c\u51b3\u6218\u4e8e\u5251\u6cb3\u4e4b\u4e1c\u2014\u2014\u6218\u4e66\u4e0a\u662f\u8fd9\u4e48\u8bf4\u7684\uff0c\u8fd8\u6709\u8377\u9c81\u65af\u795e\u738b\u7684\u5370\u73ba\uff0c\u73b0\u5728\u624d\u662f\u7b2c\u56db\u5929\u5462\u3002\u8377\u9c81\u65af\u795e\u738b\u662f\u7a46\u7f55\u8bf8\u795e\u4e4b\u9996\uff0c\u6211\u60f3\u603b\u4e0d\u81f3\u4e8e\u4f1a\u5931\u4fe1\u5427\u3002\u201d</p><p>\u201c\u8fd8\u662f\u592a\u5371\u9669\u4e86\u3002\u201d</p><p>\u4f3d\u6d85\u4f9d\u65e7\u5f88\u62c5\u5fc3\uff0c\u4ed6\u53ef\u4fe1\u4e0d\u8fc7\u4ec0\u4e48\u8377\u9c81\u65af\u795e\u738b\uff0c\u9e70\u4ece\u6765\u90fd\u662f\u51f6\u6b8b\u72e1\u8bc8\u7684\u751f\u7269\u3002\u6b64\u5730\u4e0e\u654c\u65b9\u9760\u5f97\u592a\u8fd1\uff0c\u548c\u5df1\u65b9\u7684\u5927\u8425\u5c1a\u6709\u4e00\u6bb5\u8ddd\u79bb\uff0c\u795e\u59ec\u662f\u5982\u4eca\u6069\u745f\u7684\u6700\u9ad8\u9886\u8896\uff0c\u4e5f\u662f\u8fd9\u652f\u519b\u961f\u7684\u7edf\u5e05\uff0c\u5018\u82e5\u5bf9\u65b9\u77e5\u9053\u795e\u59ec\u5728\u8fd9\u91cc\uff0c\u4ee5\u7cbe\u9510\u6218\u529b\u7a81\u7136\u88ad\u51fb\uff0c\u4e07\u4e00\u795e\u59ec\u6709\u4e2a\u95ea\u5931\uff0c\u90a3\u8fd9\u4e00\u6218\u5c31\u4e0d\u7528\u6253\u4e86\u3002</p><p>\u201c\u6211\u77e5\u9053\u5566\uff0c\u4f3d\u6d85\uff0c\u4f60\u522b\u603b\u8fd9\u4e48\u4e25\u8083\u561b\uff0c\u201d\u795e\u59ec\u4eff\u4f5b\u5f88\u65e0\u5948\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u6211\u5728\u540e\u9762\u5f85\u7740\u65e0\u804a\uff0c\u6240\u4ee5\u51fa\u6765\u6563\u6563\u6b65\u3001\u900f\u900f\u6c14\u800c\u5df2\u3002\u65e2\u7136\u4f60\u8bf4\u6709\u5371\u9669\uff0c\u90a3\u6211\u4eec\u73b0\u5728\u5c31\u56de\u53bb\u597d\u4e86\u3002\u201d</p><p>\u4f3d\u6d85\u5439\u4e86\u4e2a\u53e3\u54e8\uff0c\u5c06\u9644\u8fd1\u81ea\u5df1\u7684\u5c5e\u4e0b\u90fd\u805a\u96c6\u8d77\u6765\uff0c\u62a4\u9001\u795e\u59ec\u8fd4\u56de\u3002\u201c\u6bbf\u4e0b\uff0c\u201d\u4ed6\u72b9\u8c6b\u4e86\u4e00\u4f1a\uff0c\u6700\u7ec8\u8fd8\u662f\u8bf4\uff0c\u201c\u6218\u573a\u4e0a\u592a\u5371\u9669\uff0c\u6211\u5b89\u6392\u4e00\u961f\u4eba\uff0c\u660e\u5929\u62a4\u9001\u4f60\u53bb\u8ff7\u5931\u68ee\u6797\u5427\u3002\u201d</p><p>\u201c\u90a3\u600e\u4e48\u884c\uff0c\u201d\u795e\u59ec\u8be7\u5f02\u5730\u53cd\u95ee\uff0c\u201c\u6211\u662f\u7edf\u5e05\uff0c\u600e\u4e48\u80fd\u4e34\u9635\u8131\u9003\u5462\u3002\u201d</p><p>\u201c\u4f46\u4f60\u5728\u8fd9\u91cc\u5f88\u5371\u9669\u3002\u201d\u4f3d\u6d85\u575a\u6301\u529d\u8bf4\u3002</p><p>\u201c\u6b64\u6218\u82e5\u8d25\uff0c\u6211\u4eec\u6050\u6015\u5c31\u518d\u4e5f\u6ca1\u6709\u53cd\u6297\u529b\u91cf\u4e86\uff0c\u5230\u65f6\u5019\u7a46\u7f55\u4eba\u5168\u519b\u5357\u4e0b\uff0c\u6069\u745f\u53c8\u6709\u4ec0\u4e48\u5730\u65b9\u4e0d\u5371\u9669\u5462\u3002\u201d</p><p>\u201c\u6216\u8bb8\u2026\u2026\u201d\u4f3d\u6d85\u6b32\u8a00\u53c8\u6b62\u3002</p><p>\u5c11\u5973\u7b11\u7740\u6447\u6447\u5934\uff0c\u201c\u6211\u77e5\u9053\u4f60\u7684\u610f\u601d\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f46\u6211\u662f\u6069\u745f\u7684\u795e\u59ec\uff0c\u8fd9\u91cc\u662f\u6211\u7684\u56fd\u5bb6\uff0c\u8fd9\u91cc\u662f\u6211\u7684\u9886\u571f\uff0c\u8fd9\u91cc\u662f\u6211\u7684\u5b50\u6c11\uff0c\u6211\u7edd\u5bf9\u4e0d\u4f1a\u79bb\u5f00\u6069\u745f\u534a\u6b65\uff0c\u8fd9\u662f\u6211\u7684\u8a93\u8a00\uff0c\u662f\u6211\u5411\u5b50\u6c11\u7684\u627f\u8bfa\uff0c\u65e0\u8bba\u53d1\u751f\u4ec0\u4e48\uff0c\u6211\u90fd\u4f1a\u575a\u6301\u5230\u5e95\uff0c\u76f4\u5230\u6b7b\u4ea1\u3002\u201d</p><p>\u6709\u7740\u957f\u957f\u7684\u767d\u91d1\u8272\u7ed2\u6bdb\u7684\u6218\u9a7c\u88ab\u7275\u8fc7\u6765\uff0c\u4e00\u540d\u5e74\u8f7b\u7684\u58eb\u5175\u534a\u8dea\u7740\uff0c\u4fef\u4e0b\u8eab\uff0c\u8ba9\u795e\u59ec\u8e0f\u7740\u4ed6\u7684\u810a\u80cc\u9a91\u4e0a\u6218\u9a7c\uff0c\u201c\u8c22\u8c22\u4f60\uff0c\u201d\u795e\u59ec\u5ae3\u7136\u5fae\u7b11\u7740\uff0c\u8f7b\u58f0\u5bf9\u4ed6\u8bf4\uff0c\u201c\u4f60\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d</p><p>\u58eb\u5175\u5927\u5403\u4e00\u60ca\uff0c\u4ed6\u6ca1\u6709\u60f3\u5230\u5c0a\u8d35\u7684\u795e\u59ec\u4f1a\u5411\u4ed6\u9053\u8c22\uff0c\u800c\u4e14\u613f\u610f\u5c48\u5c0a\u5782\u8be2\u4ed6\u7684\u540d\u5b57\uff0c\u201c\u963f\u8428\xb7\u963f\u8428\u5170\uff0c\u201d\u4ed6\u7ed3\u7ed3\u5df4\u5df4\u5730\u8bf4\uff0c\u201c\u6211\u53eb\u963f\u8428\u5170\uff0c\u6bbf\u4e0b\u3002\u201d</p><p>\u201c\u963f\u8428\u5170\uff0c\u201d\u795e\u59ec\u91cd\u590d\u4e86\u4e00\u904d\uff0c\u201c\u8fd9\u4e2a\u540d\u5b57\u5f88\u597d\u3002\u6211\u6709\u4e00\u4f4d\u670b\u53cb\uff0c\u4e5f\u53eb\u8fd9\u4e2a\u540d\u5b57\uff0c\u4ed6\u662f\u4e00\u4f4d\u52c7\u6562\u7684\u6218\u58eb\uff0c\u548c\u4f60\u4e00\u6837\u3002\u201d</p><p>\u58eb\u5175\u6fc0\u52a8\u5f97\u6ee1\u8138\u901a\u7ea2\uff0c\u624b\u8db3\u65e0\u63aa\uff0c\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\u3002\u795e\u59ec\u5fae\u5fae\u4e00\u7b11\uff0c\u201c\u963f\u8428\u5170\uff0c\u4f60\u89c9\u5f97\u8fd9\u573a\u6218\u4e89\uff0c\u6211\u4eec\u80fd\u8d62\u5417\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff01\u201d\u58eb\u5175\u4e0d\u5047\u601d\u7d22\u5730\u56de\u7b54\uff0c\u201c\u661f\u8fb0\u7167\u8000\u6069\u745f\uff0c\u6211\u4eec\u5fc5\u5c06\u80dc\u5229\uff01\u201d</p><p>\u201c\u4f60\u89c9\u5f97\u5462\uff0c\u4f3d\u6d85\u3002\u201d</p><p>\u4f3d\u6d85\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u201c\u6211\u4eec\u80fd\u8d62\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u201d\u5c11\u5973\u7b11\u8d77\u6765\uff0c\u201c\u53ef\u662f\u770b\u8d77\u6765\u4f60\u4e0d\u592a\u6709\u4fe1\u5fc3\u554a\u3002\u201d</p><p>\u201c\u6211\u4eec\u7684\u786e\u5f62\u52bf\u4e0d\u5229\uff0c\u201d\u4f3d\u6d85\u8bf4\uff0c\u201c\u4f46\u5e76\u975e\u6ca1\u6709\u53d6\u80dc\u7684\u673a\u4f1a\u3002\u201d</p><p>\u795e\u59ec\u70b9\u5934\u8868\u793a\u8d5e\u540c\uff0c\u201c\u6211\u4eec\u7684\u786e\u8fd8\u6709\u673a\u4f1a\uff0c\u201d\u5979\u63d0\u9ad8\u58f0\u97f3\uff0c\u770b\u7740\u5468\u56f4\u7684\u58eb\u5175\u4eec\uff0c\u201c\u53ea\u8981\u80fd\u518d\u575a\u6301\u4e00\u4e2a\u6708\uff0c\u6211\u4eec\u5c31\u80fd\u8d62\u4e0b\u8fd9\u573a\u6218\u4e89\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u4f3d\u6d85\u8131\u53e3\u95ee\u3002</p><p>\u201c\u56e0\u4e3a\u96e8\u5b63\u8981\u6765\u4e86\u3002\u201d\u795e\u59ec\u8bf4\u3002</p><p>\u4f3d\u6d85\u76b1\u7709\uff0c\u201c\u4f46\u96e8\u5b63\u8981\u5230\u4e03\u6708\u4e2d\u65ec\uff0c\u73b0\u5728\u624d\u662f\u56db\u6708\u521d\u3002\u201d</p><p>\u201c\u4eca\u5e74\u7684\u96e8\u5b63\u4f1a\u6765\u5f97\u6bd4\u8f83\u65e9\u3002\u201d\u795e\u59ec\u8bf4\u3002</p><p>\u4e1c\u57df\u7684\u96e8\u5b63\u5411\u6765\u5f88\u51c6\u65f6\uff0c\u6bcf\u5e74\u7684\u4e03\u6708\u4e2d\u4e0b\u65ec\u5f00\u59cb\uff0c\u6301\u7eed\u4e09\u4e2a\u6708\uff0c\u76f4\u5230\u5341\u6708\u5e95\u7ed3\u675f\uff0c\u4ece\u65e0\u4f8b\u5916\uff0c\u81f3\u5c11\u5728\u4f3d\u6d85\u51e0\u5341\u5e74\u7684\u8bb0\u5fc6\u4e2d\u662f\u5982\u6b64\uff0c\u6240\u4ee5\u795e\u59ec\u7684\u8bf4\u6cd5\u542c\u8d77\u6765\u672a\u514d\u6709\u4e9b\u4e0d\u9760\u8c31\u3002\u800c\u4e14\u5373\u4fbf\u96e8\u5b63\u63d0\u524d\u5230\u6765\uff0c\u4e5f\u5e76\u4e0d\u610f\u5473\u7740\u5c40\u52bf\u5c31\u4f1a\u5411\u6069\u745f\u4e00\u65b9\u503e\u659c\uff0c\u65e0\u975e\u662f\u53cc\u65b9\u90fd\u4e0d\u5229\u4e8e\u884c\u519b\u4f5c\u6218\uff0c\u5c40\u9762\u50f5\u6301\u800c\u5df2\u2014\u2014\u751a\u81f3\u6709\u53ef\u80fd\u5bf9\u6069\u745f\u66f4\u52a0\u4e0d\u5229\uff0c\u56e0\u4e3a\u7a46\u7f55\u4e94\u4f4d\u795e\u738b\u4e4b\u4e00\u7684\u201c\u9cc4\u9c7c\u201d\u7d22\u8d1d\u514b\uff0c\u5c31\u662f\u6700\u64c5\u957f\u6c34\u4e2d\u4f5c\u6218\u7684\u3002</p><p>\u201c\u5230\u65f6\u5019\u6211\u4eec\u4f1a\u6709\u63f4\u519b\u3002\u201d\u795e\u59ec\u53c8\u8bf4\u3002</p><p>\u201c\u63f4\u519b\uff1f\u201d\u4f3d\u6d85\u4e00\u6014\uff0c\u968f\u5373\u5927\u559c\u8fc7\u671b\uff0c\u201c\u585e\u5c14\u4eba\uff1f\u90a3\u4e9b\u7ea2\u888d\u5149\u5934\u4eec\u7ec8\u4e8e\u5f00\u7a8d\u4e86\u5417\uff1f\u201d</p><p>\u795e\u59ec\u7b11\u800c\u4e0d\u7b54\u3002\u4f3d\u6d85\u5c06\u6b64\u89c6\u4e3a\u9ed8\u8ba4\uff0c\u4ed6\u5174\u594b\u5730\u6413\u7740\u624b\uff0c\u201c\u6211\u5c31\u77e5\u9053\uff0c\u585e\u5c14\u4eba\u548c\u7a46\u7f55\u4eba\u53ef\u662f\u6b7b\u654c\uff0c\u600e\u4e48\u53ef\u80fd\u5750\u5b9e\u8fd9\u79cd\u826f\u673a\u4e0d\u7406\u5462\u3002\u53ea\u8981\u6211\u4eec\u5728\u8fd9\u91cc\u62d6\u4f4f\uff0c\u585e\u5c14\u4ece\u80cc\u540e\u88ad\u51fb\uff0c\u7a46\u7f55\u4eba\u5c31\u5b8c\u86cb\u4e86\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u5468\u56f4\u7684\u58eb\u5175\u4e5f\u7eb7\u7eb7\u70b9\u5934\uff0c\u89c9\u5f97\u4f3d\u6d85\u6240\u8a00\u6709\u7406\uff0c\u539f\u672c\u7565\u663e\u4f4e\u8ff7\u7684\u58eb\u6c14\u4e3a\u4e4b\u4e00\u632f\u3002\u795e\u59ec\u5374\u8f7b\u8f7b\u4e00\u7b11\uff0c\u6ca1\u6709\u518d\u591a\u8bf4\u4ec0\u4e48\u3002</p><p>\u56de\u5230\u9a7b\u5730\uff0c\u4f3d\u6d85\u5148\u884c\u544a\u8f9e\uff0c\u795e\u59ec\u4e5f\u56de\u5230\u81ea\u5df1\u7684\u5e10\u7bf7\uff0c\u5979\u5c4f\u9000\u5de6\u53f3\uff0c\u60f3\u8981\u4e00\u4e2a\u4eba\u72ec\u81ea\u6c89\u601d\u4e00\u4f1a\uff0c\u5076\u7136\u62ac\u5934\uff0c\u53d1\u73b0\u4e00\u4e2a\u4eba\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u51fa\u73b0\u5728\u5979\u7684\u9762\u524d\uff0c\u8ddd\u79bb\u5f88\u8fd1\uff0c\u4f46\u5979\u5b8c\u5168\u6ca1\u6709\u5bdf\u89c9\u5230\u3002</p><p>\u90a3\u662f\u4e00\u4f4d\u7cbe\u81f4\u7f8e\u4e3d\u7684\u4eba\u7c7b\u5c11\u5973\uff0c\u7565\u663e\u5c16\u957f\u7684\u8033\u6735\u4f3c\u4e4e\u663e\u793a\u5979\u6709\u4e00\u4e9b\u7cbe\u7075\u7684\u8840\u7edf\uff0c\u9752\u94dc\u8272\u7684\u706b\u7130\u51dd\u805a\u6210\u738b\u5ea7\uff0c\u6258\u7740\u5979\u5a07\u5c0f\u73b2\u73d1\u7684\u8eab\u8eaf\u3002\u4e00\u5934\u53c8\u957f\u53c8\u76f4\u7684\u9ed1\u53d1\u76f4\u5782\u5230\u811a\u8e1d\uff0c\u76ae\u80a4\u50cf\u725b\u5976\u822c\u4e73\u767d\u3002\u5979\u659c\u659c\u5730\u501a\u9760\u5728\u738b\u5ea7\u4e2d\uff0c\u53f3\u624b\u6258\u816e\uff0c\u4eff\u4f5b\u5728\u6c89\u601d\uff0c\u968f\u610f\u4e0b\u5782\u7684\u5de6\u624b\u63e1\u7740\u4e00\u53ea\u5e03\u5a03\u5a03\u3002\u795e\u59ec\u770b\u89c1\u5979\uff0c\u5148\u662f\u4e00\u60ca\uff0c\u968f\u5373\u5927\u559c\u8fc7\u671b\uff0c\u201c\u683c\u83b1\u59d0\u59d0\uff0c\u4f60\u7ec8\u4e8e\u6765\u4e86\u3002\u201d</p><p>\u201c\u4f60\u547c\u5524\u4e86\u6211\u5f88\u591a\u6b21\uff0c\u201d\u5c11\u5973\u7684\u76ee\u5149\u4f4e\u5782\uff0c\u770b\u4e5f\u4e0d\u770b\u795e\u59ec\uff0c\u8bed\u6c14\u975e\u5e38\u51b7\u6f20\uff0c\u201c\u7a76\u7adf\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u201c\u7a46\u7f55\u4eba\u6253\u8fc7\u6765\u4e86\uff0c\u6211\u2014\u2014\u201d</p><p>\u201c\u6211\u8bf4\u8fc7\u5f88\u591a\u6b21\uff0c\u65e0\u5173\u7d27\u8981\u7684\u5c0f\u4e8b\u4e0d\u8981\u6253\u6270\u6211\uff0c\u201d\u5c11\u5973\u51b7\u51b0\u51b0\u5730\u6253\u65ad\u4e86\u795e\u59ec\uff0c\u201c\u6709\u90a3\u4ef6\u4e1c\u897f\u7684\u4e0b\u843d\u5417\uff1f\u201d</p><p>\u201c\u6682\u65f6\u8fd8\u6ca1\u6709\uff0c\u201d\u795e\u59ec\u4f4e\u4e0b\u5934\uff0c\u201c\u6309\u7167\u60a8\u7684\u5429\u5490\uff0c\u6bcf\u4e00\u4e2a\u6714\u6708\uff0c\u6211\u90fd\u4f1a\u641c\u5bfb\u4ed6\u7684\u8bb0\u5fc6\uff0c\u8fc4\u4eca\u4e3a\u6b62\u8fd8\u662f\u4e00\u65e0\u6240\u83b7\u3002\u201d</p><p>\u201c\u662f\u5417\u3002\u201d</p><p>\u5c11\u5973\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4eff\u4f5b\u9677\u5165\u6c89\u601d\u3002\u795e\u59ec\u4e0d\u6562\u6253\u6270\uff0c\u9759\u9759\u5730\u7b49\u5f85\u7740\u3002</p><p>\u5341\u516d\u5e74\u524d\uff0c\u4e00\u4e2a\u5076\u7136\u7684\u673a\u4f1a\uff0c\u5979\u8ba4\u8bc6\u4e86\u773c\u524d\u8fd9\u4f4d\u81ea\u79f0\u201c\u683c\u83b1\u201d\u7684\u5c11\u5973\uff0c\u4e0d\u77e5\u9053\u5176\u6765\u5386\uff0c\u552f\u4e00\u53ef\u4ee5\u80af\u5b9a\u7684\u662f\uff0c\u683c\u83b1\u7edd\u975e\u51e1\u4eba\uff0c\u5979\u5a07\u5c0f\u7684\u8eab\u8eaf\u4e2d\u8574\u542b\u7740\u96be\u4ee5\u60f3\u8c61\u7684\u529b\u91cf\uff0c\u751a\u81f3\u53ef\u4ee5\u8f7b\u800c\u6613\u4e3e\u5730\u538b\u4e0b\u4e00\u4f4d\u795e\u738b\u6fd2\u6b7b\u524d\u7684\u75af\u72c2\u81ea\u7206\u3002\u795e\u59ec\u4e0d\u6b62\u4e00\u6b21\u5730\u6000\u7591\u8fc7\u683c\u83b1\u662f\u5426\u5176\u5b9e\u662f\u67d0\u4f4d\u795e\u660e\uff0c\u4f46\u5979\u53ef\u4ee5\u80af\u5b9a\u4e1c\u57df\u8bf8\u795e\u738b\u4e2d\u7edd\u65e0\u6b64\u4eba\u3002\u795e\u59ec\u5bf9\u4e2d\u571f\u5927\u9646\u7684\u8bf8\u795e\u4e5f\u6709\u6240\u4e86\u89e3\uff0c\u666e\u901a\u6069\u745f\u4eba\u867d\u7136\u4fe1\u606f\u853d\u585e\uff0c\u4f46\u5730\u4f4d\u5230\u4e86\u5979\u8fd9\u79cd\u7a0b\u5ea6\uff0c\u53ea\u8981\u6709\u610f\uff0c\u4f9d\u7136\u53ef\u4ee5\u83b7\u5f97\u8db3\u591f\u7684\u8d44\u6599\uff0c\u4f46\u5728\u4ed4\u7ec6\u5bf9\u6bd4\u4e4b\u540e\uff0c\u795e\u59ec\u8fd8\u662f\u6ca1\u6709\u627e\u5230\u7b54\u6848\u3002\u6ca1\u6709\u4efb\u4f55\u4e00\u4f4d\u5df2\u77e5\u7684\u795e\u660e\u4e0e\u5979\u76f8\u4f3c\uff0c\u4f46\u5979\u5728\u4e0d\u7ecf\u610f\u95f4\u6240\u5c55\u73b0\u51fa\u6765\u7684\u529b\u91cf\uff0c\u7edd\u4e0d\u4e9a\u4e8e\u4efb\u4f55\u4e00\u4f4d\u4e1c\u57df\u795e\u738b\uff0c\u751a\u81f3\u72b9\u6709\u8fc7\u4e4b\u3002</p><p>\u683c\u83b1\u6765\u5230\u4e1c\u57df\uff0c\u662f\u4e3a\u4e86\u5bfb\u627e\u67d0\u4ef6\u4e1c\u897f\uff0c\u636e\u5979\u6240\u8a00\uff0c\u6069\u745f\u795e\u738b\u5409\u52d2\u4eca\u2014\u2014\u4e5f\u5373\u662f\u795e\u59ec\u7684\u4e08\u592b\u2014\u2014\u662f\u6700\u540e\u4e00\u4e2a\u63a5\u89e6\u8fc7\u8fd9\u4ef6\u4e1c\u897f\u7684\u4eba\u3002\u5409\u52d2\u4eca\u6b7b\u540e\uff0c\u683c\u83b1\u4ee5\u79d8\u6cd5\u5c06\u5176\u8bb0\u5fc6\u7559\u5b58\u4e0b\u6765\uff0c\u5728\u6bcf\u4e00\u4e2a\u6714\u6708\u4e4b\u591c\uff0c\u7531\u795e\u59ec\u5728\u5176\u4e2d\u5bfb\u627e\u4e0b\u843d\u3002\u76f4\u5230\u5982\u4eca\uff0c\u9664\u4e86\u4e00\u4e9b\u82e5\u6709\u82e5\u65e0\u7684\u7ebf\u7d22\uff0c\u683c\u83b1\u4ecd\u7136\u6ca1\u6709\u5f97\u5230\u81ea\u5df1\u60f3\u8981\u7684\u7b54\u6848\u3002</p><p>\u9ad8\u9ad8\u5728\u4e0a\u7684\u4eba\u7c7b\uff0c\u4e0d\u4f1a\u5173\u5fc3\u8682\u8681\u7684\u5fc3\u60c5\uff0c\u66f4\u4e0d\u4f1a\u5728\u610f\u5b83\u4eec\u7684\u751f\u6b7b\u3002\u795e\u59ec\u5f88\u6e05\u695a\u5730\u7406\u89e3\u8fd9\u4e00\u70b9\uff0c\u4e5f\u77e5\u9053\u81ea\u5df1\u5bf9\u4e8e\u683c\u83b1\u800c\u8a00\uff0c\u4e0d\u4f1a\u6bd4\u4e00\u53ea\u8682\u8681\u6216\u8005\u4e00\u53ea\u5fae\u5c18\u66f4\u52a0\u91cd\u8981\u3002\u5979\u552f\u4e00\u7684\u4ef7\u503c\uff0c\u5c31\u662f\u5e2e\u52a9\u683c\u83b1\u627e\u5230\u90a3\u4e2a\u4e1c\u897f\uff0c\u4e00\u65e6\u627e\u5230\uff0c\u5979\u5bf9\u4e8e\u683c\u83b1\u5c31\u5931\u53bb\u4e86\u5168\u90e8\u7684\u5b58\u5728\u610f\u4e49\uff0c\u4e5f\u4e0d\u4f1a\u518d\u5f97\u5230\u4efb\u4f55\u5e2e\u52a9\uff0c\u6216\u8005\u8bf4\u6069\u8d50\u3002</p><p>\u90a3\u5e76\u975e\u795e\u59ec\u7684\u671f\u671b\uff0c\u81f3\u5c11\u6682\u65f6\u4e0d\u662f\u3002</p><p>\u8fc7\u4e86\u534a\u54cd\uff0c\u5c11\u5973\u5fae\u5fae\u62ac\u8d77\u5934\uff0c\u4f46\u6ca1\u6709\u8bf4\u8bdd\u3002\u795e\u59ec\u5bdf\u8a00\u89c2\u8272\uff0c\u201c\u6211\u89c9\u5f97\uff0c\u6700\u5927\u7684\u53ef\u80fd\u6027\u8fd8\u662f\u5728\u4ed6\u7684\u5b9d\u5e93\u91cc\uff0c\u201d\u5979\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u8bf4\uff0c\u201c\u4ed6\u5c31\u50cf\u4e00\u53ea\u677e\u9f20\uff0c\u559c\u6b22\u628a\u6240\u6709\u641c\u96c6\u5230\u7684\u4e1c\u897f\u90fd\u6254\u8fdb\u5b9d\u5e93\uff0c\u7edd\u5927\u90e8\u5206\u8fde\u770b\u90fd\u4e0d\u770b\uff0c\u6240\u4ee5\u4e5f\u4e0d\u4f1a\u6709\u76f8\u5173\u7684\u8bb0\u5fc6\u3002\u201d</p><p>\u201c\u662f\u6709\u53ef\u80fd\uff0c\u201d\u5c11\u5973\u7ec8\u4e8e\u5f00\u53e3\uff0c\u201c\u4f60\u4e0a\u6b21\u6d3e\u53bb\u7684\u4eba\u592a\u5dee\u52b2\uff0c\u81ea\u5df1\u6b7b\u4e86\uff0c\u94a5\u5319\u4e5f\u88ab\u4ed6\u4eba\u6240\u5f97\u3002\u4e0d\u8fc7\u5f88\u6070\u5de7\uff0c\u62ff\u5230\u94a5\u5319\u7684\u8fd9\u4e2a\u4eba\uff0c\u662f\u6211\u4e00\u4f4d\u6545\u4eba\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d\u795e\u59ec\u5927\u559c\u8fc7\u671b\uff0c\u201c\u90a3\u4e48\u60a8\u80fd\u5426\u8bf7\u4ed6\u5c06\u94a5\u5319\u5f52\u8fd8\uff1f\u201d</p><p>\u201c\u5f88\u96be\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u5982\u679c\u4ed6\u5df2\u7ecf\u4e0d\u8bb0\u5f97\u6211\uff0c\u90a3\u4e48\u5f53\u7136\u4e0d\u4f1a\u5f52\u8fd8\uff1b\u5982\u679c\u4ed6\u8fd8\u8bb0\u5f97\u6211\uff0c\u90a3\u5c31\u66f4\u4e0d\u4f1a\u3002\u770b\u5728\u67d0\u4e2a\u4eba\u7684\u4efd\u4e0a\uff0c\u6211\u4e5f\u4e0d\u65b9\u4fbf\u51fa\u624b\u5f3a\u593a\uff0c\u6240\u4ee5\u53ea\u80fd\u9760\u4f60\u81ea\u5df1\u4e86\uff0c\u201d\u5979\u968f\u624b\u5728\u7a7a\u6c14\u4e2d\u5212\u4e86\u51e0\u7b14\uff0c\u52fe\u52d2\u51fa\u4e00\u4e2a\u4eba\u7c7b\u5c11\u5e74\u7684\u9762\u5bb9\uff0c\u201c\u4f60\u7684\u8fd0\u6c14\u4e0d\u9519\uff0c\u4ed6\u6b64\u523b\u6b63\u5728\u6765\u4e1c\u57df\u7684\u8def\u4e0a\u3002\u201d</p><p>\u201c\u90a3\u6211\u5982\u4f55\u624d\u80fd\u627e\u5230\u4ed6\uff1f\u201d\u795e\u59ec\u8fde\u5fd9\u95ee\u3002</p><p>\u201c\u4f60\u4f1a\u89c1\u5230\u4ed6\u7684\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u7b49\u5f85\u5373\u53ef\u3002\u201d</p><p>\u795e\u59ec\u6014\u4e86\u4e00\u4e0b\uff0c\u968f\u5373\u4f4e\u4e0b\u5934\uff0c\u201c\u662f\u3002\u201d</p><p>\u201c\u63a5\u4e0b\u6765\u6211\u4f1a\u5728\u51e1\u95f4\u5f85\u4e00\u6bb5\u65f6\u95f4\uff0c\u201d\u683c\u83b1\u8bf4\uff0c\u201c\u4f60\u4e0d\u7528\u518d\u547c\u5524\u6211\uff0c\u6211\u5982\u679c\u60f3\u51fa\u73b0\uff0c\u81ea\u7136\u5c31\u4f1a\u51fa\u73b0\u3002\u5e0c\u671b\u6211\u4e0b\u6b21\u51fa\u73b0\u7684\u65f6\u5019\uff0c\u4f60\u80fd\u591f\u7ed9\u6211\u4e00\u4e9b\u597d\u6d88\u606f\u3002\u6211\u63d0\u9192\u4f60\uff0c\u96c5\u5c14\u8d1d\u7433\u5a1c\u795e\u59ec\u6bbf\u4e0b\uff0c\u4f60\u5df2\u7ecf\u6d6a\u8d39\u4e86\u592a\u591a\u65f6\u95f4\uff0c\u8fd9\u662f\u6700\u540e\u4e00\u6b21\u673a\u4f1a\u3002\u201d</p><p>\u5979\u62ac\u4e86\u62ac\u624b\uff0c\u5c06\u4e00\u76f4\u63e1\u5728\u624b\u4e2d\u7684\u90a3\u53ea\u5e03\u5a03\u5a03\u4e22\u8fc7\u6765\uff0c\u795e\u59ec\u8fde\u5fd9\u63a5\u4f4f\u3002\u201c\u5e26\u7740\u5979\uff0c\u5979\u4f1a\u4fdd\u62a4\u4f60\u7684\u5b89\u5168\u3002\u201d\u5c11\u5973\u8bf4\u3002</p><p>\u201c\u662f\u3002\u201d</p><p>\u5f53\u5979\u518d\u6b21\u62ac\u8d77\u5934\u65f6\uff0c\u5c11\u5973\u5df2\u7ecf\u6d88\u5931\u4e86\uff0c\u7a7a\u6c14\u4e2d\u6ca1\u6709\u7559\u4e0b\u4e00\u4e1d\u75d5\u8ff9\uff0c\u4eff\u4f5b\u5979\u4ece\u672a\u6765\u8fc7\u4e00\u822c\u3002\u4f46\u90a3\u53ea\u5e03\u5a03\u5a03\u4ecd\u7136\u5728\u795e\u59ec\u7684\u624b\u4e2d\uff0c\u8bc1\u660e\u521a\u624d\u53d1\u751f\u7684\u4e00\u5207\u5e76\u975e\u5e7b\u89c9\u3002</p><p>\u795e\u59ec\u4e3e\u8d77\u5e03\u5a03\u5a03\uff0c\u4ed4\u7ec6\u7aef\u8be6\uff0c\u90a3\u662f\u4e00\u4e2a\u505a\u5de5\u5f88\u7c97\u7cd9\u7684\u73a9\u5076\uff0c\u6a21\u6837\u4e5f\u5f88\u666e\u901a\uff0c\u91d1\u8272\u5934\u53d1\u7684\u5c0f\u5973\u5b69\uff0c\u80d6\u4e4e\u4e4e\u7684\u8138\u86cb\uff0c\u9c9c\u7ea2\u7684\u5634\u5507\uff0c\u9ed1\u8272\u7684\u9ebb\u7ebf\u7f1d\u5236\u51fa\u7709\u773c\uff0c\u7b11\u563b\u563b\u7684\u3002\u4e0d\u77e5\u662f\u5426\u9519\u89c9\uff0c\u795e\u59ec\u603b\u89c9\u5f97\u5b83\u7684\u7b11\u5bb9\u4e2d\u900f\u7740\u4e00\u79cd\u9634\u68ee\u68ee\u7684\u90aa\u6c14\uff0c\u8ba9\u5979\u4e0d\u5bd2\u800c\u6817\u3002\u4e0b\u610f\u8bc6\u5730\u60f3\u5c06\u5b83\u6254\u6389\uff0c\u72b9\u8c6b\u4e86\u7247\u523b\uff0c\u795e\u59ec\u627e\u51fa\u4e00\u4e2a\u888b\u5b50\uff0c\u5c06\u5e03\u5a03\u5a03\u88c5\u8fdb\u5176\u4e2d\uff0c\u7136\u540e\u6302\u5728\u8170\u95f4\u3002</p><p>\u521a\u521a\u505a\u5b8c\u8fd9\u4e00\u5207\uff0c\u4e00\u53ea\u9e1f\u6251\u68f1\u68f1\u5730\u98de\u4e86\u8fdb\u6765\uff0c\u5b83\u770b\u4e0a\u53bb\u662f\u4e00\u53ea\u4e4c\u9e26\uff0c\u4f46\u7fbd\u6bdb\u662f\u9752\u84dd\u8272\u7684\uff0c\u4f53\u578b\u6bd4\u4e00\u822c\u7684\u4e4c\u9e26\u8981\u5c0f\u5f88\u591a\uff0c\u800c\u4e14\u5728\u5c3e\u90e8\u6709\u4e00\u6761\u95ea\u7535\u5f62\u72b6\u7684\u767d\u8272\u82b1\u7eb9\u3002\u795e\u59ec\u62ac\u8d77\u624b\uff0c\u8ba9\u4e4c\u9e26\u843d\u5728\u5979\u7684\u5de6\u81c2\u4e0a\uff0c\u201c\u6211\u7761\u9192\u4e86\uff0c\u201d\u4e4c\u9e26\u53e3\u5410\u4eba\u8a00\uff0c\u201c\u6700\u8fd1\u6709\u4ec0\u4e48\u6709\u8da3\u7684\u4e8b\u60c5\u5417\uff1f\u201d</p><p>\u201c\u6709\uff0c\u201d\u795e\u59ec\u8bf4\uff0c\u5979\u7684\u795e\u6001\u8f7b\u677e\uff0c\u5168\u65e0\u534a\u70b9\u521a\u624d\u9762\u5bf9\u201c\u683c\u83b1\u201d\u65f6\u7684\u62d8\u8c28\uff0c\u201c\u4f60\u7b49\u5f85\u7684\u4eba\u7ec8\u4e8e\u6765\u4e86\u3002\u201d</p><p>\u201c\u5979\u6765\u4e86\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u201d\u795e\u59ec\u8bf4\uff0c\u201c\u53e6\u5916\u6211\u53ef\u80fd\u8981\u53bb\u4e00\u8d9f\u5f7b\u68ee\u5854\uff0c\u4f60\u8981\u4e0d\u8981\u966a\u6211\u4e00\u8d77\uff1f\u201d</p><p>\u201c\u6ca1\u7a7a\uff0c\u201d\u4e4c\u9e26\u51b7\u51b0\u51b0\u5730\u8bf4\uff0c\u201c\u4f60\u53bb\u5f7b\u68ee\u5854\u5e72\u561b\uff1f\u201d</p><p>\u201c\u4e0d\u53bb\u5c31\u7b97\u4e86\uff0c\u201d\u795e\u59ec\u8bf4\uff0c\u201c\u6211\u53bb\u62ff\u56de\u4e00\u4ef6\u4e1c\u897f\uff0c\u987a\u4fbf\u4e5f\u89c1\u89c1\u6545\u4eba\u3002\u201d</p><p>\u7b2c\u4e00\u8282</p><p>\u968f\u7740\u624e\u745e\u5c14\u7684\u79bb\u5f00\uff0c\u5979\u521b\u9020\u51fa\u6765\u7684\u201c\u68a6\u201d\u4e5f\u968f\u4e4b\u7834\u788e\uff0c\u743c\u6069\u731b\u7136\u60ca\u9192\u8fc7\u6765\u3002\u7b2c\u4e94\u79d8\u5668\u5f20\u5f00\u7684\u9886\u57df\u5d29\u6e83\u6b86\u5c3d\uff0c\u4ed6\u53d1\u73b0\u81ea\u5df1\u5df2\u7ecf\u53c8\u56de\u5230\u4e86\u7269\u8d28\u754c\uff0c\u6b63\u5728\u4e00\u5904\u6cb3\u6d41\u8fb9\uff0c\u8fdc\u8fdc\u53ef\u4ee5\u770b\u89c1\u9634\u5f71\u9547\u7684\u7070\u8272\u87ba\u65cb\u9ad8\u5854\u3002\u4e00\u53ea\u5927\u7ea6\u6709\u4e24\u4eba\u9ad8\u7684\u9752\u94dc\u9f0e\u5b89\u9759\u5730\u77d7\u7acb\u5728\u4ed6\u8eab\u8fb9\uff0c\u9664\u6b64\u4e4b\u5916\u522b\u65e0\u5176\u4ed6\u3002</p><p>\u9b54\u7f51\u4e0d\u7a33\u5b9a\u3002</p><p>\u4f5c\u4e3a\u5deb\u5e08\uff0c\u8fd9\u662f\u4ed6\u7684\u672c\u80fd\u53cd\u5e94\uff0c\u65e0\u9700\u523b\u610f\u8bd5\u63a2\uff0c\u610f\u8bc6\u81ea\u7136\u800c\u7136\u5730\u5c31\u5bdf\u89c9\u5230\u4e86\u8fd9\u4e00\u70b9\u3002\u5728\u4ed6\u5341\u591a\u5e74\u7684\u5deb\u5e08\uff08\u5305\u62ec\u5b66\u5f92\uff09\u751f\u6daf\u4e2d\uff0c\u9b54\u7f51\u4e00\u76f4\u662f\u5b89\u9759\u7684\uff0c\u7a33\u56fa\u7684\uff0c\u5b9b\u5982\u6e56\u9762\uff0c\u5e73\u6ed1\u5982\u955c\uff0c\u53ea\u5728\u5deb\u5e08\u4ece\u4e2d\u6c72\u53d6\u529b\u91cf\u65f6\u4f1a\u7565\u8d77\u6d9f\u6f2a\uff0c\u968f\u5373\u6d88\u706d\u3002\u7136\u800c\u5728\u6b64\u65f6\uff0c\u539f\u672c\u5e73\u9759\u7684\u6e56\u9762\u4eff\u4f5b\u522e\u8d77\u4e86\u5927\u98ce\uff0c\u5b83\u5728\u52a8\u8361\uff0c\u5728\u6447\u6643\uff0c\u5728\u6697\u6d41\u6c79\u6d8c\uff0c\u4eff\u4f5b\u968f\u65f6\u53ef\u80fd\u6380\u8d77\u60ca\u6d9b\u9a87\u6d6a\u3002</p><p>\u743c\u6069\u6c89\u601d\u4e86\u4e00\u4f1a\uff0c\u62ff\u8d77\u624e\u745e\u5c14\u7ed9\u4ed6\u7684\u300a\u547d\u8fd0\u957f\u591c\u300b\uff0c\u8fd9\u539f\u672c\u662f\u5965\u52a0\u83b1\u65af\u7684\u4e1c\u897f\uff0c\u636e\u8bf4\u662f\u4f0a\u739b\u65af\u5361\u7b2c\u56db\u79d8\u5668\u201c\u65e5\u6708\u4e4b\u4e66\u201d\u7684\u5f71\u5668\uff0c\u4ed6\u89c1\u8fc7\u5f88\u591a\u6b21\uff0c\u4f46\u4ece\u6ca1\u7528\u8fc7\uff0c\u4e5f\u4e0d\u77e5\u9053\u53e3\u4ee4\uff0c\u5c3d\u7ba1\u5982\u6b64\uff0c\u4ed6\u8fd8\u662f\u6beb\u65e0\u969c\u788d\u5730\u6fc0\u53d1\u4e86\u5b83\u7684\u529b\u91cf\u3002\u6839\u636e\u6b27\u51ef\u7684\u8bf4\u6cd5\uff0c\u4f0a\u739b\u65af\u5361\u65f6\u4ee3\u7684\u5947\u68b0\u672f\u6ce8\u91cd\u8840\u8109\uff0c\u201c\u7687\u5ba4\u201d\u53ef\u4ee5\u51ed\u501f\u7075\u9b42\u5370\u8bb0\uff0c\u76f4\u63a5\u8d8a\u8fc7\u201c\u5b66\u8005\u201d\u8bbe\u4e0b\u7684\u4efb\u4f55\u53e3\u4ee4\u3001\u7981\u5236\uff0c\u4e4b\u524d\u90a3\u526f\u8428\u74e6\u68cb\u5c31\u662f\u5982\u6b64\u3002</p><p>\u7ffb\u5f00\u4e66\u9875\uff0c\u5deb\u5e08\u5c06\u610f\u8bc6\u6c89\u5165\u5176\u4e2d\uff0c\u7136\u540e\u4ed6\u770b\u89c1\u4e86\u5f88\u591a\u4eba\u3002</p><p>\u4ed6\u9996\u5148\u770b\u5230\u7684\u662f\u51db\uff0c\u5c0f\u5973\u5deb\u6b63\u5728\u6c89\u7761\uff0c\u5979\u88ab\u4e00\u4e2a\u4e03\u5f69\u7f24\u7eb7\u7684\u5149\u7403\u5305\u88f9\u7740\uff0c\u5149\u7403\u4eff\u4f5b\u6709\u751f\u547d\u4e00\u822c\uff0c\u6709\u8282\u594f\u5730\u81a8\u80c0\u3001\u6536\u7f29\uff0c\u50cf\u662f\u5728\u547c\u5438\u3002\u5343\u4e07\u9053\u7ec6\u7ec6\u7684\u900f\u660e\u7075\u7ebf\u4ece\u5149\u7403\u5185\u58c1\u4e0a\u6563\u53d1\u51fa\u6765\uff0c\u8fde\u63a5\u7740\u51db\u7684\u8eab\u4f53\u6bcf\u4e00\u5904\u3002\u743c\u6069\u770b\u4e86\u4e00\u4f1a\uff0c\u89c1\u5979\u795e\u60c5\u8212\u7f13\uff0c\u9762\u5bb9\u5b89\u9759\uff0c\u663e\u7136\u6ca1\u4ec0\u4e48\u5371\u9669\uff0c\u4fbf\u8d8a\u8fc7\u5979\uff0c\u7ee7\u7eed\u5f80\u524d\u8d70\u3002</p><p>\u7b2c\u4e8c\u4e2a\u770b\u89c1\u7684\u662f\u6885\u83f2\u65af\uff0c\u5723\u6b66\u58eb\u5c11\u5973\u4e5f\u5728\u6c89\u7761\uff0c\u4f46\u5979\u8eab\u65c1\u6709\u4e00\u4e2a\u4eba\u5728\u5b88\u62a4\u3002\u90a3\u662f\u4e00\u4f4d\u8eab\u6750\u9ad8\u6311\u7684\u91d1\u53d1\u7f8e\u5973\uff0c\u7f8e\u4e3d\u7684\u8138\u5e9e\u4e0a\u795e\u60c5\u51b7\u6f20\uff0c\u900f\u7740\u9690\u9690\u7684\u5a01\u4e25\u611f\uff0c\u6c14\u8d28\u4e0e\u6885\u83f2\u65af\u6709\u51e0\u5206\u76f8\u8fd1\u3002\u743c\u6069\u8ba4\u8bc6\u5979\uff0c\u5728\u5730\u72f1\u7684\u65f6\u5019\u89c1\u8fc7\u9762\uff0c\u82b1\u4e86\u70b9\u529f\u592b\u4ece\u62dc\u5c14\u624b\u91cc\u5c06\u5979\u6551\u51fa\u6765\uff0c\u8ba9\u5979\u5f97\u4ee5\u91cd\u8fd4\u5929\u754c\u3002\u201c\u4f60\u597d\uff0c\u5f25\u8d5b\u4e9a\u3002\u201d\u4ed6\u6253\u62db\u547c\u3002</p><p>\u201c\u4f60\u597d\u3002\u201d\u66e6\u5929\u4f7f\u5fae\u5fae\u70b9\u5934\u3002</p><p>\u201c\u5979\u600e\u4e48\u6837\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u6ca1\u4ec0\u4e48\u5927\u4e8b\uff0c\u4f11\u606f\u4e00\u9635\u5c31\u53ef\u4ee5\u4e86\uff0c\u201d\u66e6\u5929\u4f7f\u8bf4\uff0c\u201c\u9ece\u660e\u4e4b\u77f3\u867d\u7136\u6bc1\u6389\uff0c\u4f46\u5df4\u5c14\u4e5f\u88ab\u91cd\u521b\uff0c\u77ed\u65f6\u95f4\u5185\u662f\u5b89\u5168\u7684\u3002\u201d</p><p>\u201c\u90a3\u5c31\u597d\u3002\u201d</p><p>\u7b2c\u4e09\u4e2a\u770b\u89c1\u7684\u662f\u73ca\u5609\uff0c\u5979\u5012\u662f\u5904\u4e8e\u6e05\u9192\u72b6\u6001\uff0c\u6b63\u62c5\u5fe7\u5730\u770b\u7740\u5965\u52a0\u83b1\u65af\u3002\u5927\u5965\u672f\u5e08\u7684\u5f62\u4f53\u5df2\u7ecf\u8fd1\u4e4e\u5b8c\u5168\u900f\u660e\uff0c\u611f\u89c9\u4e00\u9635\u98ce\u5439\u8fc7\u5c31\u4f1a\u6d88\u6563\u3002\u770b\u89c1\u743c\u6069\uff0c\u5965\u52a0\u83b1\u65af\u7684\u8138\u4e0a\u9732\u51fa\u4e00\u4e1d\u7b11\u610f\uff0c\u50cf\u662f\u8ba5\u8bbd\uff0c\u53c8\u4f3c\u662f\u65e0\u5948\u7684\u81ea\u5632\uff0c\u201c\u6ca1\u60f3\u5230\u5c45\u7136\u4f1a\u662f\u8fd9\u79cd\u7ed3\u5c40\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u8fd8\u771f\u662f\u6709\u4e2a\u597d\u60c5\u4eba\u3002\u201d</p><p>\u201c\u6211\u4e5f\u6ca1\u60f3\u5230\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e16\u4e8b\u96be\u6599\u3002\u201d</p><p>\u201c\u4e16\u4e8b\u96be\u6599\uff0c\u201d\u5965\u52a0\u83b1\u65af\u91cd\u590d\u8fd9\u53e5\u8bdd\uff0c\u201c\u6211\u662f\u9884\u8a00\u5e08\uff0c\u4f46\u5374\u65e0\u6cd5\u9884\u8a00\u81ea\u5df1\u7684\u7ed3\u5c40\uff0c\u8fd9\u771f\u662f\u8bbd\u523a\u3002\u201d</p><p>\u5979\u770b\u7740\u73ca\u5609\uff0c\u4f38\u51fa\u624b\uff0c\u629a\u6478\u5979\u7684\u5934\u53d1\uff0c\u201c\u6211\u8981\u8d70\u4e86\u3002\u201d\u5965\u52a0\u83b1\u65af\u8bf4\u3002</p><p>\u73ca\u5609\u6ce3\u4e0d\u6210\u58f0\u3002</p><p>\u201c\u522b\u54ed\uff0c\u50bb\u5b69\u5b50\uff0c\u522b\u54ed\uff0c\u201d\u5965\u52a0\u83b1\u65af\u8bf4\uff0c\u201c\u8fd9\u6bb5\u65f6\u95f4\u80fd\u5929\u5929\u966a\u7740\u4f60\uff0c\u6211\u5f88\u5f00\u5fc3\u3002\u6211\u539f\u672c\u5c31\u8981\u79bb\u5f00\uff0c\u53ea\u662f\u60f3\u5728\u79bb\u5f00\u524d\u7ed9\u4f60\u591a\u7559\u4e0b\u70b9\u4e1c\u897f\uff0c\u53ef\u60dc\u6ca1\u6210\u529f\u3002\u7b97\u5566\uff0c\u201d\u5979\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u770b\u6765\u53ea\u80fd\u628a\u4f60\u6258\u4ed8\u7ed9\u8fd9\u5bb6\u4f19\u4e86\u3002\u743c\u6069\uff01\u201d\u5979\u7a81\u7136\u53eb\u743c\u6069\u7684\u540d\u5b57\u3002</p><p>\u201c\u6211\u5728\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u4f60\u548c\u90a3\u4e2a\u9b45\u9b54\u4e4b\u95f4\u6709\u4e00\u4efd\u771f\u540d\u5951\u7ea6\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u767d\u75f4\uff01\u201d\u5927\u5965\u672f\u5e08\u51b7\u51b7\u5730\u8bc4\u4ef7\uff0c\u201c\u4efb\u4f55\u667a\u5546\u6b63\u5e38\u7684\u5deb\u5e08\u90fd\u4e0d\u4f1a\u8fd9\u4e48\u505a\u3002\u201d</p><p>\u743c\u6069\u4e0d\u60f3\u548c\u5979\u4e89\u8fa9\uff0c\u7d22\u6027\u4e0d\u8bf4\u8bdd\u3002</p><p>\u5965\u52a0\u83b1\u65af\u62ac\u8d77\u53f3\u624b\uff0c\u7528\u638c\u5fc3\u538b\u5728\u743c\u6069\u7684\u524d\u989d\u4e0a\uff0c\u5979\u662f\u5e7d\u7075\uff0c\u6ca1\u6709\u5b9e\u4f53\uff0c\u743c\u6069\u53ea\u89c9\u53cc\u773c\u50cf\u662f\u88ab\u4e00\u5c42\u6726\u80e7\u96fe\u6c14\u6240\u906e\u4f4f\uff0c\u8fc7\u4e86\u7247\u523b\u53c8\u6563\u5f00\u3002\u4ed6\u5b8c\u5168\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\uff0c\u5965\u52a0\u83b1\u65af\u5374\u9677\u5165\u6c89\u601d\uff0c\u201c\u53bb\u4e1c\u57df\u3002\u201d\u8fc7\u4e86\u534a\u54cd\uff0c\u5927\u5965\u672f\u5e08\u7a81\u7136\u8bf4\u3002</p><p>\u201c\u55ef\uff1f\u201d\u743c\u6069\u83ab\u540d\u5176\u5999\u3002</p><p>\u201c\u5728\u4e1c\u57df\uff0c\u6709\u53ef\u80fd\u4f60\u4f1a\u9047\u5230\u89e3\u9664\u771f\u540d\u5951\u7ea6\u7684\u673a\u4f1a\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d\u743c\u6069\u5927\u559c\u8fc7\u671b\uff0c\u201c\u4e1c\u57df\u5177\u4f53\u4ec0\u4e48\u5730\u65b9\uff1f\u8981\u53bb\u627e\u8c01\uff1f\u201d</p><p>\u201c\u4e0d\u77e5\u9053\uff0c\u201d\u5965\u52a0\u83b1\u65af\u8bf4\uff0c\u201c\u9884\u8a00\u53ea\u80fd\u7ba1\u7aa5\u672a\u6765\u7684\u4e9b\u8bb8\u788e\u7247\uff0c\u4e0d\u53ef\u80fd\u770b\u89c1\u5168\u8c8c\u3002\u201d</p><p>\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u771f\u540d\u5951\u7ea6\u7684\u5b58\u5728\uff0c\u59cb\u7ec8\u662f\u4e00\u4e2a\u9690\u60a3\uff0c\u53ea\u662f\u82e6\u4e8e\u65e0\u6cd5\u89e3\u9664\uff0c\u53ea\u80fd\u6682\u65f6\u653e\u7740\u3002\u5982\u4eca\u5965\u52a0\u83b1\u65af\u7ed9\u51fa\u6307\u70b9\uff0c\u867d\u8bf4\u6bd4\u8f83\u6a21\u7cca\uff0c\u4f46\u6bd5\u7adf\u662f\u6709\u4e86\u65b9\u5411\u3002\u201c\u8c22\u8c22\u3002\u201d\u4ed6\u771f\u5fc3\u5b9e\u610f\u5730\u8bf4\u3002</p><p>\u201c\u6211\u53ea\u662f\u4e3a\u4e86\u73ca\u5609\uff0c\u201d\u5965\u52a0\u83b1\u65af\u8bf4\uff0c\u201c\u5176\u4ed6\u7684\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u597d\u8bf4\u4e86\uff0c\u201d\u5979\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u201c\u5bf9\u5979\u597d\u70b9\uff0c\u53ef\u4ee5\u5417\uff1f\u201d</p><p>\u201c\u6211\u4f1a\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u653e\u5fc3\u5427\u3002\u201d</p><p>\u5965\u52a0\u83b1\u65af\u70b9\u4e86\u70b9\u5934\uff0c\u6700\u540e\u770b\u4e86\u73ca\u5609\u4e00\u773c\uff0c\u8eab\u4f53\u6162\u6162\u53d8\u5f97\u5b8c\u5168\u900f\u660e\uff0c\u6d88\u5931\u4e0d\u89c1\u3002\u73ca\u5609\u518d\u4e5f\u5fcd\u4e0d\u4f4f\uff0c\u5931\u58f0\u75db\u54ed\u51fa\u6765\uff0c\u743c\u6069\u60f3\u5b89\u6170\u5979\uff0c\u73ca\u5609\u6447\u4e86\u6447\u5934\uff0c\u201c\u4f60\u5148\u53bb\u505a\u522b\u7684\u4e8b\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8ba9\u6211\u4e00\u4e2a\u4eba\u5b89\u9759\u5f85\u4f1a\u3002\u201d</p><p>\u201c\u597d\u5427\u3002\u201d</p><p>\u63a5\u4e0b\u6765\uff0c\u743c\u6069\u770b\u89c1\u4e86\u4e24\u4e2a\u8ba9\u4ed6\u610f\u60f3\u4e0d\u5230\u7684\u4eba\uff1a\u98ce\u66b4\u5973\u738b\u6b23\u5e03\uff0c\u4ee5\u53ca\u8428\u9a6c\u65af\u7279\u3002\u6b23\u5e03\u770b\u8d77\u6765\u72b6\u51b5\u5f88\u4e0d\u5999\uff0c\u5979\u5e73\u8eba\u7740\uff0c\u60ac\u6d6e\u5728\u865a\u7a7a\u4e2d\uff0c\u94f6\u767d\u8272\u7684\u706b\u7130\u4ece\u5979\u7684\u4f53\u5185\u4e0d\u65ad\u6d8c\u51fa\uff0c\u53c8\u6ce8\u5165\uff0c\u5faa\u73af\u5f80\u590d\uff0c\u541e\u5410\u4e0d\u5b9a\uff0c\u8ba9\u5979\u6574\u4e2a\u4eba\u770b\u8d77\u6765\u50cf\u662f\u4e00\u6839\u71c3\u70e7\u7684\u5de8\u5927\u8721\u70db\u3002</p><p>\u201c\u5979\u8fd9\u662f\u600e\u4e48\u4e86\uff1f\u201d\u743c\u6069\u95ee\u5750\u5728\u65c1\u8fb9\u7684\u8428\u9a6c\u65af\u7279\u3002</p><p>\u201c\u94f6\u706b\u5931\u63a7\u4e86\uff0c\u201d\u8001\u5deb\u5996\u56de\u7b54\uff0c\u201c\u662f\u4f60\u90a3\u4e00\u5251\u7684\u529f\u52b3\u3002\u201d</p><p>\u743c\u6069\u4ee5\u661f\u8fb0\u5251\uff08\u5f71\uff09\u653b\u51fb\u9b54\u6cd5\u5973\u795e\uff0c\u5c3d\u7ba1\u5973\u795e\u672a\u96e8\u7ef8\u7f2a\uff0c\u4f7f\u7528\u9884\u5148\u5907\u4efd\u7684\u5723\u8005\u8eb2\u8fc7\u4e00\u52ab\uff0c\u4f46\u4ecd\u88ab\u91cd\u521b\u3002\u5723\u8005\u4e0e\u5316\u8eab\u4e0d\u540c\uff0c\u5723\u8005\u6b7b\uff0c\u5219\u795e\u7957\u9668\uff0c\u743c\u6069\u8fd9\u4e00\u51fb\u867d\u7136\u672a\u80fd\u5b8c\u6210\u5f11\u795e\uff0c\u5374\u8fd8\u662f\u9020\u6210\u4e86\u975e\u5e38\u4e25\u91cd\u7684\u540e\u679c\u3002\u5728\u743c\u6069\u770b\u4e0d\u89c1\u7684\u5730\u65b9\uff0c\u6574\u4e2a\u4e16\u754c\u7684\u9b54\u7f51\u90fd\u5728\u90a3\u77ed\u77ed\u7684\u51e0\u5206\u949f\u5185\u53d8\u5f97\u7d0a\u4e71\u4e0d\u582a\uff0c\u4f7f\u7528\u9b54\u6cd5\u53d8\u6210\u4e00\u4ef6\u975e\u5e38\u5371\u9669\u7684\u4e8b\u60c5\uff0c\u65e0\u6570\u5deb\u5e08\u56e0\u6b64\u53d1\u751f\u5404\u79cd\u610f\u5916\uff0c\u751a\u81f3\u8eab\u4ea1\u3002\u800c\u5973\u795e\u7684\u5723\u8005\u88ab\u6467\u6bc1\u65f6\uff0c\u6240\u6709\u5f53\u65f6\u5728\u9644\u8fd1\u533a\u57df\u7684\u9009\u6c11\u90fd\u53d7\u5230\u5f71\u54cd\uff0c\u4ed6\u4eec\u7684\u94f6\u706b\u5f00\u59cb\u5931\u63a7\u3001\u66b4\u8d70\uff0c\u5982\u679c\u4e0d\u80fd\u53ca\u65f6\u63a7\u5236\u7684\u8bdd\uff0c\u5fc5\u7136\u4f1a\u6709\u751f\u547d\u5371\u9669\u3002</p><p>\u201c\u6709\u6ca1\u6709\u4ec0\u4e48\u529e\u6cd5\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u4f60\u4e0d\u662f\u53f7\u79f0\u5bf9\u795e\u529b\u6700\u6709\u7814\u7a76\u7684\u51e1\u4eba\u5417\uff0c\u53d1\u8868\u70b9\u4e13\u4e1a\u610f\u89c1\u5427\u3002\u201d</p><p>\u201c\u552f\u4e00\u7684\u529e\u6cd5\u5c31\u662f\u5c06\u94f6\u706b\u6536\u56de\uff0c\u6216\u8005\u79fb\u8d70\uff0c\u201d\u8428\u9a6c\u65af\u7279\u8bf4\uff0c\u201c\u8fd9\u4e2a\u6211\u5c31\u65e0\u80fd\u4e3a\u529b\u4e86\u3002\u8bf4\u5b9e\u8bdd\uff0c\u6b23\u5e03\u7b97\u662f\u90a3\u51e0\u4e2a\u5973\u4eba\u4e2d\u6211\u96be\u5f97\u770b\u5f97\u987a\u773c\u7684\uff0c\u5982\u679c\u80fd\u5e2e\u5fd9\uff0c\u6211\u4e5f\u4e0d\u4ecb\u610f\u5e2e\u4e00\u628a\uff0c\u201d\u4ed6\u8038\u8038\u80a9\uff0c\u201c\u53ef\u60dc\u4e0d\u884c\u3002\u201d</p><p>\u201c\u4f60\u795e\u7ecf\u6b63\u5e38\u4e86\uff1f\u201d\u743c\u6069\u8be7\u5f02\u5730\u95ee\u3002</p><p>\u201c\u55ef\uff0c\u5e94\u8be5\u7b97\u662f\u6b63\u5e38\u4e86\u5427\uff0c\u201d\u8428\u9a6c\u65af\u7279\u8bf4\uff0c\u201c\u5c31\u7b97\u662f\u68a6\uff0c\u505a\u4e86\u8fd9\u4e48\u4e45\uff0c\u4e5f\u8be5\u9192\u4e86\u3002\u201d</p><p>\u743c\u6069\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\uff0c\u201c\u90a3\u5c31\u597d\uff0c\u201d\u4ed6\u6700\u540e\u53ea\u80fd\u6577\u884d\u5730\u8bf4\u4e86\u4e00\u53e5\uff0c\u201c\u6240\u4ee5\u4f60\u786e\u5b9e\u6ca1\u529e\u6cd5\uff1f\u201d</p><p>\u201c\u6211\u6ca1\u529e\u6cd5\uff0c\u4f46\u4f60\u6709\u3002\u201d</p><p>\u201c\u6211\uff1f\u201d\u743c\u6069\u4e00\u6014\u3002</p><p>\u4ed6\u548c\u6b23\u5e03\u6253\u4ea4\u9053\u5e76\u4e0d\u591a\uff0c\u5173\u7cfb\u4e00\u76f4\u5e73\u5e73\uff0c\u751a\u81f3\u6709\u8fc7\u51b2\u7a81\u3002\u4f46\u4e4b\u524d\u9b54\u6cd5\u5973\u795e\u8981\u6740\u4ed6\uff0c\u6b23\u5e03\u66fe\u7ecf\u51fa\u624b\u963b\u6b62\uff0c\u4e8e\u4ed6\u6709\u6069\u3002\u5373\u4fbf\u4e0d\u8bba\u8fd9\u4e2a\uff0c\u5979\u8fd8\u662f\u51db\u7684\u8001\u5e08\uff0c\u4ec5\u5c31\u8fd9\u70b9\uff0c\u743c\u6069\u4fbf\u65e0\u6cd5\u8896\u624b\u65c1\u89c2\u3002\u624e\u745e\u5c14\u7279\u5730\u5c06\u5979\u6536\u5230\u4e66\u4e2d\uff0c\u53ef\u80fd\u4e5f\u6709\u8fd9\u4e2a\u7528\u610f\u5728\u3002\u95ee\u9898\u662f\u4ed6\u5bf9\u94f6\u706b\u8fd1\u4e4e\u4e00\u65e0\u6240\u77e5\uff0c\u54ea\u6709\u529e\u6cd5\u89e3\u51b3\uff1f</p><p>\u4e0d\uff0c\u771f\u8981\u8bf4\u8d77\u6765\uff0c\u529e\u6cd5\u4f3c\u4e4e\u8fd8\u771f\u662f\u6709\u4e00\u4e2a\u2026\u2026</p><p>\u743c\u6069\u6c89\u541f\u534a\u54cd\uff0c\u53c8\u6447\u4e86\u6447\u5934\u3002\u9996\u5148\uff0c\u8fd9\u65b9\u6cd5\u76ee\u524d\u6ca1\u6cd5\u7528\uff0c\u5b58\u5728\u5173\u952e\u7684\u6280\u672f\u969c\u788d\uff1b\u5176\u6b21\uff0c\u5c31\u7b97\u771f\u8981\u7528\u8fd9\u65b9\u6cd5\uff0c\u4e5f\u5f97\u5148\u5f81\u6c42\u76f8\u5173\u5f53\u4e8b\u4eba\u540c\u610f\u624d\u884c\uff0c\u5c31\u50cf\u533b\u751f\u8981\u7ed9\u75c5\u4eba\u52a8\u624b\u672f\uff0c\u4e00\u5b9a\u8981\u5148\u8ba9\u5bf9\u65b9\u7b7e\u98ce\u9669\u544a\u77e5\u4e66\uff0c\u5426\u5219\u4f1a\u5f88\u9ebb\u70e6\u3002\u4f46\u73b0\u5728\u6b23\u5e03\u8fd9\u6837\u5b50\uff0c\u81ea\u5df1\u663e\u7136\u662f\u6ca1\u6cd5\u7b7e\u5b57\u4e86\uff0c\u5bb6\u5c5e\u5012\u662f\u4e0d\u5c11\uff0c\u4f46\u743c\u6069\u603b\u4e0d\u53ef\u80fd\u53bb\u627e\u5979\u8001\u5988\u6216\u8005\u59d0\u59b9\u4eec\uff0c\u5979\u53c8\u6ca1\u7ed3\u5a5a\uff0c\u6ca1\u6709\u4e08\u592b\u548c\u5b50\u5973\uff0c\u518d\u5f80\u540e\u7b97\u5c31\u662f\u51db\u4e86\u3002\u51db\u662f\u5979\u7684\u5b66\u751f\uff0c\u5173\u7cfb\u662f\u5f88\u4eb2\u8fd1\u7684\uff0c\u4ee3\u7406\u4e00\u4e0b\u4e5f\u8bf4\u5f97\u901a\uff0c\u4f46\u5c0f\u5973\u5deb\u521a\u521a\u5929\u964d\u6a2a\u8d22\uff0c\u51ed\u7a7a\u83b7\u5f97\u5de8\u9f99\u4e4b\u529b\u7684\u6d17\u793c\uff0c\u73b0\u5728\u6b63\u5fd9\u7740\u6d88\u5316\u5438\u6536\uff0c\u8c01\u77e5\u9053\u4ec0\u4e48\u65f6\u5019\u80fd\u9192\u8fc7\u6765\u3002</p><p>\u201c\u4f60\u53ef\u4ee5\u6162\u6162\u8003\u8651\uff0c\u201d\u8001\u5deb\u5996\u8bf4\uff0c\u4ed6\u5e94\u8be5\u4e0d\u77e5\u9053\u743c\u6069\u88ab\u7ef4\u82e5\u62c9\u8bc5\u5492\u7684\u4e8b\u60c5\uff0c\u53ea\u4ee5\u4e3a\u4ed6\u8fd8\u662f\u5fc3\u6709\u987e\u5fcc\uff0c\u4f46\u4e5f\u61d2\u5f97\u591a\u7ba1\uff0c\u201c\u8fd9\u672c\u4e66\u7684\u529b\u91cf\u5f88\u53e4\u602a\uff0c\u538b\u5236\u4f4f\u4e86\u94f6\u706b\u2014\u2014\u4e0d\uff0c\u51c6\u786e\u5730\u8bf4\uff0c\u4e0d\u662f\u538b\u5236\uff0c\u662f\u8ba9\u94f6\u706b\u6682\u65f6\u51bb\u7ed3\u4f4f\u4e86\u3002\u201d</p><p>\u743c\u6069\u6307\u4e86\u6307\u6b23\u5e03\uff0c\u94f6\u706b\u5728\u5979\u8eab\u4e0a\u718a\u718a\u71c3\u70e7\uff0c\u541e\u5410\u4e0d\u5b9a\uff0c\u8fd9\u53ef\u534a\u70b9\u4e0d\u50cf\u201c\u51bb\u7ed3\u201d\u7684\u6837\u5b50\u3002</p><p>\u201c\u90a3\u53ea\u662f\u8868\u8c61\uff0c\u706b\u7130\u7684\u5f62\u6001\u4ecd\u7136\u5b58\u5728\uff0c\u5728\u52a8\uff0c\u4f46\u5176\u529b\u91cf\u88ab\u51bb\u7ed3\u4e86\uff0c\u201d\u8428\u9a6c\u65af\u7279\u8bf4\uff0c\u201c\u53ea\u8981\u5979\u4e0d\u79bb\u5f00\u8fd9\u672c\u4e66\uff0c\u60c5\u51b5\u5c31\u4e0d\u4f1a\u8fdb\u4e00\u6b65\u6076\u5316\u3002\u201d</p><p>\u201c\u6709\u6ca1\u6709\u53ef\u80fd\u81ea\u5df1\u597d\u8f6c\uff1f\u201d</p><p>\u201c\u6ca1\u6709\u3002\u201d</p><p>\u90a3\u5c31\u662f\u53ea\u80fd\u6c38\u8fdc\u505a\u7761\u7f8e\u4eba\u4e86\u3002</p><p>\u65e2\u7136\u6682\u65f6\u6ca1\u6709\u5371\u9669\uff0c\u53c8\u6ca1\u529e\u6cd5\u89e3\u51b3\uff0c\u6b23\u5e03\u7684\u95ee\u9898\u5c31\u5148\u629b\u5f00\uff0c\u65e5\u540e\u518d\u8bf4\u3002\u201c\u4f60\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d\u743c\u6069\u95ee\u8428\u9a6c\u65af\u7279\u3002</p><p>\u8001\u5deb\u5996\u65e2\u7136\u6e05\u9192\uff0c\u4ee5\u4ed6\u7684\u667a\u5546\uff0c\u4e4b\u524d\u7684\u4e8b\u60c5\u5c31\u7b97\u6ca1\u6709\u5b8c\u5168\u60f3\u660e\u767d\uff0c\u57fa\u672c\u4e5f\u80fd\u63a8\u6d4b\u4e2a\u516b\u4e5d\u4e0d\u79bb\u5341\u3002\u8ffd\u7a76\u8d77\u6765\uff0c\u4ed6\u7b97\u662f\u88ab\u624e\u745e\u5c14\u72e0\u72e0\u5751\u4e86\u4e00\u628a\uff0c\u800c\u624e\u745e\u5c14\u662f\u743c\u6069\u7684\u60c5\u4eba\uff0c\u73b0\u5728\u5979\u56de\u5730\u72f1\u4e86\uff0c\u4e00\u5207\u540e\u7eed\u95ee\u9898\u81ea\u7136\u5c31\u5f97\u743c\u6069\u6765\u627f\u62c5\u3002\u743c\u6069\u5bf9\u6b64\u4e5f\u6ca1\u4ec0\u4e48\u610f\u89c1\uff0c\u4ed6\u770b\u5f97\u51fa\u6765\uff0c\u8428\u9a6c\u65af\u7279\u73b0\u5728\u7684\u72b6\u6001\u6781\u5ea6\u8870\u5f31\uff0c\u771f\u8981\u73b0\u5728\u5c31\u53d1\u751f\u51b2\u7a81\uff0c\u80af\u5b9a\u4e0d\u662f\u81ea\u5df1\u7684\u5bf9\u624b\uff0c\u6709\u4ec0\u4e48\u597d\u6015\u7684\u3002</p><p>\u201c\u6211\u7684\u65f6\u95f4\u4e0d\u591a\u4e86\u3002\u201d\u8428\u9a6c\u65af\u7279\u8bf4\uff0c\u201c\u80fd\u542c\u6211\u8bf4\u51e0\u53e5\u8bdd\u5417\uff1f\u201d</p><p>\u743c\u6069\u4e0d\u77e5\u9053\u4eca\u5929\u662f\u4e0d\u662f\u4ec0\u4e48\u7279\u6b8a\u65e5\u5b50\uff0c\u4e0d\u662f\u544a\u522b\u5c31\u662f\u6c38\u522b\uff0c\u5148\u662f\u9001\u8d70\u5973\u53cb\u56de\u5730\u72f1\uff0c\u53c8\u770b\u7740\u5973\u53cb\u7684\u8001\u5988\u9b42\u98de\u9b44\u6563\uff0c\u73b0\u5728\u53c8\u8981\u542c\u4e00\u4f4d\u8001\u5deb\u5996\u4ea4\u4ee3\u540e\u4e8b\u3002\u201c\u8bf7\u8bb2\uff0c\u201d\u4ed6\u5728\u5bf9\u9762\u5750\u4e86\u4e0b\u6765\uff0c\u201c\u5982\u679c\u4f60\u6709\u4ec0\u4e48\u5fc3\u613f\uff0c\u6211\u80fd\u5e2e\u5fd9\u7684\uff0c\u4f1a\u5c3d\u529b\u800c\u4e3a\u2014\u2014\u4f46\u592a\u96be\u7684\u5c31\u7b97\u4e86\u3002\u201d</p><p>\u8428\u9a6c\u65af\u7279\u7b11\u4e86\u7b11\uff0c\u201c\u6211\u8fd9\u8f88\u5b50\uff0c\u4eff\u4f5b\u5927\u68a6\u4e00\u573a\uff0c\u604d\u604d\u60da\u60da\uff0c\u81f3\u4eca\u65b9\u9192\u3002\u65e2\u7136\u5168\u662f\u865a\u5e7b\uff0c\u4ece\u672a\u771f\u6b63\u7ecf\u5386\uff0c\u4e5f\u5c31\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u5fc3\u613f\u672a\u4e86\u3002\u6211\u6ca1\u6709\u4eb2\u4eba\uff0c\u4e5f\u6ca1\u6709\u670b\u53cb\uff0c\u5012\u662f\u548c\u4f60\u51e0\u6b21\u76f8\u9047\uff0c\u66f4\u6ca1\u60f3\u5230\u662f\u4f60\u966a\u6211\u8d70\u8fd9\u6700\u540e\u4e00\u7a0b\u2014\u2014\u771f\u662f\u4e16\u4e8b\u96be\u6599\u554a\u3002\u201d</p><p>\u201c\u4e16\u4e8b\u96be\u6599\u3002\u201d\u743c\u6069\u91cd\u590d\u4e86\u4e00\u6b21\uff0c\u7136\u540e\u4e5f\u5fcd\u4e0d\u4f4f\u53f9\u4e86\u53e3\u6c14\u3002</p><p>\u201c\u4f60\u8001\u5e08\u6559\u4e86\u6211\u5206\u88c2\u547d\u5323\u7684\u65b9\u6cd5\uff0c\u4f46\u4ed6\u663e\u7136\u85cf\u4e86\u4e00\u624b\uff0c\u6211\u53ea\u80fd\u5c06\u547d\u5323\u4e00\u5206\u4e3a\u4e8c\u3002\u5176\u4e2d\u4e00\u4e2a\u5728\u9f99\u72c2\u8ff7\u9501\u7684\u529b\u91cf\u6fc0\u53d1\u5230\u9876\u5cf0\u65f6\u6bc1\u4e86\uff0c\u8fd9\u8ba9\u6211\u795e\u667a\u53d7\u635f\uff0c\u82e5\u975e\u5982\u6b64\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u90a3\u4e48\u8f7b\u6613\u88ab\u90aa\u86c7\u965b\u4e0b\u548c\u4f60\u90a3\u4f4d\u60c5\u4eba\u9a97\u8fc7\uff0c\u53e6\u5916\u4e00\u4e2a\u6211\u968f\u8eab\u643a\u5e26\uff0c\u7528\u9014\u4f30\u8ba1\u4f60\u4e5f\u5df2\u7ecf\u77e5\u9053\u3002\u51ef\u5c14\u672c\u5c31\u662f\u56e0\u4e3a\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u6240\u4ee5\u6545\u610f\u653e\u4efb\u6211\u53d1\u52a8\u8ff7\u9501\uff0c\u4ed6\u662f\u60f3\u5982\u679c\u8fd9\u6b21\u80fd\u6740\u6389\u6211\uff0c\u5c31\u53ef\u4ee5\u4e00\u52b3\u6c38\u9038\u4e86\u3002\u201d</p><p>\u6700\u540e\u51ef\u5c14\u672c\u5012\u6ca1\u80fd\u5f97\u624b\uff0c\u4f46\u8428\u9a6c\u65af\u7279\u5148\u662f\u88ab\u90aa\u86c7\u9644\u4f53\uff0c\u53c8\u88ab\u6885\u83f2\u65af\u51a5\u6b65\u80cc\u523a\uff0c\u66f4\u88ab\u9ece\u660e\u4e4b\u77f3\u8fd1\u8ddd\u79bb\u7206\u53d1\u2014\u2014\u867d\u7136\u5728\u7eb3\u745f\u7684\u5f15\u5bfc\u4e0b\uff0c\u4e00\u90e8\u5206\u529b\u91cf\u76f4\u63a5\u653b\u51fb\u5df4\u5c14\uff0c\u4f46\u5927\u90e8\u5206\u7684\u7206\u70b8\u5a01\u529b\u8fd8\u662f\u8303\u56f4\u65e0\u5dee\u522b\u653b\u51fb\uff0c\u8428\u9a6c\u65af\u7279\u8fd9\u4e2a\u4ea1\u7075\u81ea\u7136\u9996\u5f53\u5176\u51b2\u3002\u8f6e\u756a\u8f70\u70b8\u4e4b\u4e0b\uff0c\u8001\u5deb\u5996\u518d\u5f3a\u4e5f\u652f\u6491\u4e0d\u4f4f\uff0c\u968f\u8eab\u643a\u5e26\u7684\u547d\u5323\u5df2\u7ecf\u88ab\u6fd2\u4e34\u7c89\u788e\uff0c\u4ed6\u81ea\u5df1\u4e5f\u547d\u4e0d\u4e45\u77e3\u3002\u82e5\u4e0d\u662f\u624e\u745e\u5c14\u5c06\u4ed6\u6536\u8fdb\u4e66\u4e2d\uff0c\u73b0\u5728\u53ea\u6015\u5df2\u7ecf\u5f7b\u5e95\u5b8c\u86cb\u4e86\u3002</p><p>\u201c\u5979\u59cb\u7ec8\u6ca1\u6709\u6b63\u773c\u770b\u8fc7\u6211\uff0c\u660e\u660e\u5df2\u7ecf\u6765\u4e86\u8fd9\u4e48\u4e45\uff0c\u90fd\u6ca1\u548c\u6211\u8bf4\u8fc7\u4e00\u53e5\u8bdd\uff0c\u201d\u8428\u9a6c\u65af\u7279\u4f4e\u58f0\u8bf4\uff0c\u201c\u5176\u5b9e\u6211\u4e5f\u6ca1\u4ec0\u4e48\u7279\u522b\u5962\u671b\uff0c\u53ea\u662f\u60f3\u518d\u89c1\u4e00\u6b21\uff0c\u60f3\u95ee\u4e00\u4e2a\u95ee\u9898\u800c\u5df2\u3002\u65e0\u8bba\u7b54\u6848\u662f\u4ec0\u4e48\uff0c\u6211\u90fd\u6ca1\u6709\u9057\u61be\u4e86\uff0c\u53ef\u60dc\u8fde\u8fd9\u4e2a\u673a\u4f1a\u90fd\u6ca1\u6709\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u4e0d\u662f\u8bf4\uff0c\u8fd9\u4e9b\u90fd\u662f\u865a\u5e7b\u4e00\u68a6\uff0c\u73b0\u5728\u5df2\u7ecf\u9192\u4e86\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u505a\u4e86\u4e00\u4e2a\u7f8e\u68a6\uff0c\u96be\u9053\u4e0d\u4f1a\u65f6\u5e38\u56de\u5473\u5417\uff1f\u68a6\u4e2d\u7a81\u7136\u60ca\u9192\uff0c\u96be\u9053\u4e0d\u4f1a\u6005\u7136\u82e5\u5931\u5417\uff1f\u201d\u8001\u5deb\u5996\u5475\u5475\u5730\u7b11\u8d77\u6765\uff0c\u201c\u4f60\u662f\u4e2a\u5e74\u8f7b\u4eba\uff0c\u88c5\u4ec0\u4e48\u6210\u719f\u5462\uff0c\u770b\u7834\u4eba\u751f\u4ec0\u4e48\u7684\uff0c\u662f\u6211\u8fd9\u79cd\u8001\u4eba\u7684\u4e13\u5229\uff0c\u4f60\u8fd8\u662f\u53bb\u7ee7\u7eed\u8ffd\u4f60\u7684\u68a6\u5427\u3002\u201d</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u201c\u4e5f\u662f\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u201c\u6309\u7167\u4e1c\u65b9\u5927\u9646\u7684\u4e60\u4fd7\uff0c\u6211\u4eec\u76f8\u8bc6\u4e00\u573a\uff0c\u4f60\u662f\u6211\u6700\u540e\u65f6\u523b\u966a\u5728\u8eab\u8fb9\u7684\u4eba\uff0c\u6709\u8d44\u683c\u7ee7\u627f\u6211\u7684\u9057\u4ea7\uff0c\u201d\u8428\u9a6c\u65af\u7279\u8bf4\uff0c\u201c\u53ef\u60dc\u6211\u8fd9\u8f88\u5b50\u6ca1\u7ed3\u8fc7\u5a5a\uff0c\u6240\u4ee5\u4e5f\u6ca1\u6709\u6f02\u4eae\u5973\u513f\u6258\u4ed8\u7ed9\u4f60\uff0c\u53ea\u6709\u4e00\u4e9b\u8d22\u4ea7\u3002\u6211\u6bd4\u4e0d\u4e0a\u4f60\u8001\u5e08\u73cd\u85cf\u4e30\u5bcc\uff0c\u4f30\u8ba1\u4f60\u4e5f\u770b\u4e0d\u4e0a\u773c\u4e86\u3002\u201d</p><p>\u2026\u2026\u4ec0\u4e48\u4e1c\u65b9\u5927\u9646\u7684\u4e60\u4fd7\u4f1a\u6709\u201c\u628a\u6f02\u4eae\u5973\u513f\u6258\u4ed8\u7ed9\u4eba\u201d\u8fd9\u79cd\u5947\u602a\u7684\u8bbe\u5b9a\u554a\uff0c\u4f60\u83ab\u975e\u662f\u770b\u5c0f\u8bf4\u8d70\u706b\u5165\u9b54\u4e86\u5417\u3002\u53e6\u5916\uff0c\u8d22\u4ea7\u4ec0\u4e48\u7684\uff0c\u591a\u591a\u76ca\u5584\uff0c\u6211\u7edf\u7edf\u90fd\u770b\u5f97\u4e0a\uff0c\u4e00\u70b9\u4e0d\u4ecb\u610f\u7684\u3002</p><p>\u8428\u9a6c\u65af\u7279\u4ea4\u7ed9\u743c\u6069\u7b2c\u4e00\u4efd\uff0c\u4e5f\u662f\u4ed6\u8bf4\u6700\u6709\u4ef7\u503c\u7684\u8d22\u4ea7\uff0c\u662f\u4e00\u672c\u4e66\uff0c\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u4e00\u5377\u7af9\u518c\u3002</p><p>\u201c\u8fd9\u662f\u6211\u6e38\u5386\u4e1c\u65b9\u5927\u9646\u65f6\uff0c\u4e00\u4f4d\u5deb\u5e08\u9001\u7ed9\u6211\u7684\u793c\u7269\uff0c\u56e0\u4e3a\u77e5\u9053\u6211\u770b\u4e0d\u61c2\u7fd4\u9f99\u6587\u5b57\uff0c\u5df2\u7ecf\u7ffb\u8bd1\u6210\u901a\u7528\u8bed\u4e86\uff0c\u4f60\u5e94\u8be5\u4e5f\u80fd\u770b\u61c2\u3002\u5b83\u91cc\u9762\u8bb0\u5f55\u4e86\u7fd4\u9f99\u5e1d\u56fd\u5386\u53f2\u4e0a\u4e00\u4f4d\u5927\u8d24\u8005\u4e0e\u5176\u5b66\u751f\u7684\u8c08\u8bdd\uff0c\u8574\u542b\u4eba\u751f\u81f3\u7406\uff0c\u56ca\u62ec\u5929\u5730\u5927\u9053\uff0c\u636e\u8bf4\u66fe\u7ecf\u6709\u4e00\u4f4d\u5bb0\u76f8\u53ea\u901a\u8bfb\u4e00\u534a\uff0c\u5c31\u80fd\u6210\u529f\u5730\u6cbb\u7406\u56fd\u5bb6\u3002\u4f60\u77e5\u9053\uff0c\u6211\u7684\u9f99\u5deb\u6559\u603b\u662f\u4e71\u7cdf\u7cdf\u7684\uff0c\u7ec4\u7ec7\u6da3\u6563\uff0c\u4ee4\u6211\u5934\u75bc\uff0c\u6211\u539f\u672c\u662f\u60f3\u4ece\u4e2d\u5b66\u4e60\u4e00\u4e9b\u7ba1\u7406\u65b9\u6cd5\uff0c\u4f46\u5927\u7ea6\u662f\u6211\u7684\u5929\u8d4b\u4e0d\u591f\uff0c\u9886\u609f\u4e0d\u51fa\u4ec0\u4e48\u3002\u73b0\u5728\u53ea\u80fd\u9001\u7ed9\u4f60\u4e86\uff0c\u4f60\u73b0\u5728\u867d\u7136\u8fd8\u6bd4\u8f83\u5f31\u5c0f\uff0c\u4f46\u6765\u5386\u975e\u51e1\uff0c\u80cc\u666f\u6df1\u539a\uff0c\u5047\u4ee5\u65f6\u65e5\u5fc5\u7136\u4f1a\u6210\u4e3a\u96c4\u636e\u5927\u9646\u7684\u4e00\u65b9\u52bf\u529b\uff0c\u8fd9\u672c\u4e66\u4f1a\u5f88\u6709\u7528\u5904\u3002\u201d</p><p>\u743c\u6069\u770b\u4e0d\u89c1\u81ea\u5df1\u7684\u8868\u60c5\uff0c\u4f46\u4ed6\u80af\u5b9a\u662f\u4e00\u5934\u9ed1\u7ebf\u3002\u62b1\u7740\u6700\u540e\u4e00\u70b9\u4fa5\u5e78\uff0c\u4ed6\u6253\u5f00\u7af9\u518c\u770b\u4e86\u770b\uff0c\u6620\u5165\u773c\u5e18\u7684\u7b2c\u4e00\u884c\u5b57\u662f\u201c\u6709\u670b\u53cb\u4ece\u8fdc\u5904\u6765\u62dc\u8bbf\uff0c\u771f\u662f\u4ee4\u4eba\u5fc3\u60c5\u6109\u5feb\u7684\u4e8b\u60c5\u554a\u201d\uff0c\u65c1\u8fb9\u7a7a\u767d\u5904\u8fd8\u6709\u8428\u9a6c\u65af\u7279\u5199\u7684\u5fc3\u5f97\u4f53\u4f1a\uff1a\u201c\u9996\u5148\uff0c\u4f60\u8981\u6709\u4e00\u4e2a\u670b\u53cb\u3002\u201d</p><p>\u201c\u2026\u2026\u201d\u743c\u6069\u5df2\u7ecf\u5b8c\u5168\u65e0\u8a00\u4ee5\u5bf9\u3002</p><p>\u6000\u7740\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u5f62\u5bb9\u7684\u5fc3\u60c5\uff0c\u743c\u6069\u6bd5\u606d\u6bd5\u656c\u5730\u5c06\u8fd9\u4efd\u73cd\u8d35\u793c\u7269\u6536\u597d\uff0c\u5e76\u4e14\u5411\u8428\u9a6c\u65af\u7279\u518d\u4e09\u4fdd\u8bc1\u81ea\u5df1\u4f1a\u52aa\u529b\u7814\u8bfb\u3002</p><p>\u5e78\u597d\uff0c\u63a5\u4e0b\u6765\u7684\u4e1c\u897f\u6ca1\u90a3\u4e48\u8be1\u5f02\u3002\u4e00\u4efd\u5de8\u9f99\u5951\u7ea6\uff0c\u8fd9\u662f\u9f99\u5deb\u6559\u7684\u81f3\u5b9d\uff0c\u6301\u6709\u5b83\u53ef\u4ee5\u9a71\u9063\u4efb\u4f55\u4e00\u4e2a\u9f99\u5deb\u5996\uff0c\u4e0d\u8fc7\u8428\u9a6c\u65af\u7279\u6709\u8a00\u5728\u5148\uff0c\u8fd9\u4e1c\u897f\u4e0a\u9644\u6709\u65e0\u6cd5\u89e3\u9664\u7684\u8bc5\u5492\uff0c\u743c\u6069\u76ee\u524d\u9020\u8be3\u5c1a\u6d45\uff0c\u6700\u597d\u4e0d\u8981\u52a8\u7528\uff1b\u4e00\u4e9b\u96f6\u96f6\u6563\u6563\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u4ef7\u503c\u90fd\u4e0d\u4f4e\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u4ec0\u4e48\u7279\u522b\u503c\u5f97\u63d0\u53ca\u7684\uff1b\u6700\u540e\u8fd8\u6709\u4e00\u5f20\u5730\u56fe\u3002</p><p>\u201c\u6211\u4e4b\u524d\u8bf4\u8fc7\uff0c\u6211\u5728\u4e1c\u57df\u7684\u6069\u745f\u5e1d\u56fd\u5883\u5185\u53d1\u73b0\u4e86\u4e00\u5ea7\u8010\u745f\u6d6e\u7a7a\u57ce\uff0c\u867d\u7136\u8352\u5e9f\uff0c\u4f46\u6838\u5fc3\u533a\u57df\u4ecd\u7136\u5b8c\u597d\u3002\u5b83\u5760\u843d\u5728\u4e00\u5904\u8fdc\u53e4\u68ee\u6797\u4e4b\u4e2d\uff0c\u8fd9\u662f\u6211\u7ed8\u5236\u7684\u5730\u56fe\u3002\u201d</p><p>\u743c\u6069\u5bf9\u8fd9\u4e2a\u6709\u5370\u8c61\uff0c\u8fd8\u662f\u8428\u9a6c\u65af\u7279\u8bf7\u4ed6\u5e2e\u5fd9\u53bb\u5bf9\u4ed8\u6cf0\u62c9\u65af\u594e\u5de8\u517d\u7684\u65f6\u5019\uff0c\u629b\u51fa\u8fd9\u4e2a\u6761\u4ef6\u4f5c\u4e3a\u7b79\u7801\uff0c\u4f46\u88ab\u743c\u6069\u62d2\u7edd\u4e86\u3002\u73b0\u5728\u4e0d\u662f\u505a\u4ea4\u6613\uff0c\u800c\u662f\u76f4\u63a5\u8d60\u9001\uff0c\u743c\u6069\u5f53\u7136\u5c31\u6ca1\u610f\u89c1\uff0c\u5f88\u5f00\u5fc3\u5730\u63a5\u53d7\u4e0b\u6765\u3002</p><p>\u201c\u5443\uff0c\u5bf9\u4e86\uff0c\u8fd9\u5ea7\u6d6e\u7a7a\u57ce\u4f60\u662f\u4ec0\u4e48\u65f6\u5019\u53d1\u73b0\u7684\uff1f\u201d\u743c\u6069\u7a81\u7136\u60f3\u8d77\u6765\uff0c\u201c\u4e0d\u4f1a\u662f\u767e\u516b\u5341\u5e74\u524d\u5427\uff0c\u90a3\u4f30\u8ba1\u65e9\u5c31\u88ab\u4eba\u6377\u8db3\u5148\u767b\u4e86\u3002\u201d</p><p>\u8428\u9a6c\u65af\u7279\u60f3\u4e86\u60f3\uff0c\u201c\u5927\u6982\uff0c\u4e09\u767e\u591a\u5e74\u524d\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u653e\u5fc3\u597d\u4e86\uff0c\u201d\u8428\u9a6c\u65af\u7279\u5b89\u6170\u4ed6\uff0c\u201c\u6211\u8bf4\u4e86\uff0c\u5b83\u7684\u6838\u5fc3\u533a\u57df\u5b8c\u597d\uff0c\u8ff7\u9501\u4ecd\u7136\u5728\u8fd0\u4f5c\uff0c\u8fde\u6211\u90fd\u786c\u95ef\u4e0d\u8fdb\u53bb\u3002\u4e1c\u57df\u90a3\u8fb9\u9b54\u6cd5\u6c34\u5e73\u5f88\u4f4e\uff0c\u4e0d\u53ef\u80fd\u6709\u4eba\u80fd\u62a2\u5148\u4e0b\u624b\u7684\u3002\u201d</p><p>\u201c\u5e0c\u671b\u5982\u6b64\u5427\u3002\u201d</p><p>\u201c\u597d\u4e86\uff0c\u5c31\u8fd9\u4e9b\u4e1c\u897f\uff0c\u201d\u8428\u9a6c\u65af\u7279\u62cd\u62cd\u624b\uff0c\u201c\u94b1\u8d22\u4ec0\u4e48\u7684\uff0c\u6211\u5411\u6765\u4e0d\u5728\u610f\uff0c\u6599\u60f3\u4f60\u4e5f\u6ca1\u5174\u8da3\uff0c\u5c31\u7701\u7565\u5427\u3002\u6309\u7167\u4e1c\u65b9\u7684\u4f20\u7edf\uff0c\u5f80\u5f80\u8fd8\u6709\u4e2a\u4ec0\u4e48\u4f20\u529f\u704c\u9876\u7684\u4eea\u5f0f\uff0c\u8bf4\u662f\u8981\u4e24\u4e2a\u4eba\u8111\u888b\u9876\u7740\u8111\u888b\uff0c\u4e92\u76f8\u9876\u725b\uff0c\u8fd9\u6837\u53ef\u4ee5\u5206\u4eab\u5f7c\u6b64\u7684\u667a\u6167\u2014\u2014\u54c8\u54c8\uff0c\u522b\u90a3\u79cd\u8138\u8272\uff0c\u6211\u77e5\u9053\u8fd9\u542c\u8d77\u6765\u6709\u70b9\u50bb\uff0c\u6211\u4eec\u6bd5\u7adf\u4e0d\u662f\u4e1c\u65b9\u4eba\uff0c\u5c31\u4e0d\u7528\u6837\u6837\u9075\u7167\u4e86\u3002\u90a3\u4e48\u6700\u540e\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u6709\u4e00\u70b9\u5fc3\u613f\uff0c\u5e0c\u671b\u4f60\u80fd\u5e2e\u6211\u5b8c\u6210\u3002\u201d</p><p>\u743c\u6069\u7aef\u6b63\u8eab\u59ff\uff0c\u795e\u60c5\u8083\u7a46\uff0c\u201c\u8bf7\u8bb2\uff0c\u6211\u5fc5\u5b9a\u5c3d\u529b\u3002\u201d</p><p>\u8001\u5deb\u5996\u53cd\u800c\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u6700\u540e\u624d\u6162\u6162\u5f00\u53e3\uff0c\u201c\u5982\u679c\u4f60\u6709\u673a\u4f1a\uff0c\u8fd8\u80fd\u518d\u9047\u5230\u5979\uff0c\u8bf7\u5e2e\u6211\u95ee\u4e00\u53e5\u8bdd\u3002\u201d</p><p>\u201c\u662f\u95ee\uff1a\u4f60\u5230\u5e95\u6709\u6ca1\u6709\u7231\u8fc7\u6211\uff1f\u201d</p><p>\u8428\u9a6c\u65af\u7279\u5927\u5403\u4e00\u60ca\uff0c\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d</p><p>\u62dc\u6258\uff0c\u4f60\u8fd9\u79cd\u5e9f\u5b85\uff0c\u8fd8\u80fd\u95ee\u51fa\u4ec0\u4e48\u6709\u521b\u610f\u7684\u95ee\u9898\uff0c\u96be\u4e0d\u6210\u4f60\u8fd8\u80fd\u95ee\u6316\u6398\u673a\u6280\u672f\u54ea\u5bb6\u5f3a\u4e48\u3002</p><p>\u867d\u7136\u6781\u5ea6\u65e0\u8bed\uff0c\u4f46\u743c\u6069\u8fd8\u662f\u8ba4\u771f\u5730\u7b54\u5e94\u4e0b\u6765\u3002</p><p>\u8428\u739b\u65af\u7279\u5fc3\u613f\u5df2\u4e86\uff0c\u4e5f\u4e0d\u518d\u591a\u8bf4\uff0c\u4ed6\u7684\u8eaf\u4f53\u6e10\u6e10\u5d29\u6e83\uff0c\u5316\u4f5c\u65e0\u6570\u7070\u70ec\uff0c\u743c\u6069\u6df1\u6df1\u8eac\u8eab\u884c\u793c\uff0c\u7136\u540e\u6325\u4e86\u6325\u624b\uff0c\u4e00\u9635\u98ce\u5e73\u5730\u5377\u8d77\uff0c\u5c06\u7070\u70ec\u5439\u6563\uff0c\u6d88\u5931\u5728\u865a\u7a7a\u4e4b\u4e2d\u3002</p><p>\u203b\u203b\u203b</p><p>\u7a81\u7136\u4e4b\u95f4\uff0c\u5965\u52a0\u83b1\u65af\u548c\u8428\u9a6c\u65af\u7279\u5148\u540e\u6d88\u901d\uff0c\u8fd9\u4e24\u4f4d\u90fd\u662f\u5927\u4eba\u7269\uff0c\u5965\u52a0\u83b1\u65af\u5012\u8fd8\u7f62\u4e86\uff0c\u8428\u9a6c\u65af\u7279\u5374\u662f\u9f99\u5deb\u6559\u4e4b\u4e3b\uff0c\u540d\u9707\u5927\u9646\u7684\u5927\u53cd\u6d3e\uff0c\u5176\u5b58\u4ea1\u5173\u7cfb\u532a\u6d45\uff0c\u7275\u6d89\u5927\u9646\u683c\u5c40\u53d8\u5316\uff0c\u5374\u6b7b\u5f97\u8fd9\u6837\u65e0\u58f0\u65e0\u606f\uff0c\u5b9e\u5728\u8ba9\u4eba\u559f\u53f9\u3002\u5e78\u597d\uff0c\u4ed6\u4eec\u548c\u743c\u6069\u90fd\u6ca1\u6709\u592a\u5bc6\u5207\u7684\u5173\u7cfb\uff0c\u4e5f\u8c08\u4e0d\u4e0a\u5982\u4f55\u60b2\u4f24\uff0c\u5f88\u5feb\u5deb\u5e08\u5c31\u6536\u62fe\u5fc3\u60c5\uff0c\u51c6\u5907\u7ee7\u7eed\u65b0\u7684\u5f81\u9014\u3002</p><p>\u6885\u83f2\u65af\u548c\u51db\u90fd\u8fd8\u5728\u6c89\u7761\uff0c\u73ca\u5609\u867d\u7136\u6e05\u9192\uff0c\u4f46\u5979\u521a\u521a\u5931\u53bb\u6bcd\u4eb2\uff0c\u60c5\u7eea\u6781\u5ea6\u4f4e\u843d\uff0c\u743c\u6069\u4fbf\u8ba9\u5979\u4eec\u90fd\u7ee7\u7eed\u5728\u4e66\u4e2d\u4f11\u606f\uff0c\u540c\u65f6\u5c06\u4e5d\u5e7d\u9f0e\u4e5f\u6536\u5165\u4e66\u4e2d\u3002\u8fd9\u672c\u4e66\u662f\u4f0a\u739b\u65af\u5361\u7b2c\u56db\u79d8\u5668\u7684\u5f71\u5668\uff0c\u529f\u80fd\u7c7b\u4f3c\uff0c\u91cc\u9762\u6709\u975e\u5e38\u5927\u7684\u5f02\u6b21\u5143\u7a7a\u95f4\u3002\u4e5d\u5e7d\u9f0e\u4e5f\u662f\u7a7a\u95f4\u79d8\u5668\uff0c\u4f46\u5fc5\u987b\u6fc0\u6d3b\u624d\u80fd\u5f20\u5f00\u9886\u57df\uff0c\u672a\u6fc0\u6d3b\u65f6\u6ca1\u6709\u4efb\u4f55\u50a8\u7269\u529f\u80fd\uff1b\u7b2c\u56db\u79d8\u5668\u5374\u4e0d\u540c\uff0c\u5b83\u6ca1\u6709\u4ec0\u4e48\u201c\u6fc0\u6d3b\u201d\u72b6\u6001\uff0c\u6216\u8005\u8bf4\u5b83\u4e00\u76f4\u5c31\u662f\u4fdd\u6301\u8fd0\u8f6c\u72b6\u6001\uff0c\u9886\u57df\u81ea\u5e26\uff0c\u5305\u5bb9\u4e07\u5343\u3002</p><p>\u9b54\u7f51\u4ecd\u7136\u6709\u4e9b\u52a8\u8361\uff0c\u4f46\u5b83\u6b63\u5728\u8d8b\u5411\u6062\u590d\u7a33\u5b9a\uff0c\u743c\u6069\u6e05\u695a\u5730\u611f\u89c9\u5230\u4e86\u8fd9\u4e00\u70b9\u3002\u4ed6\u731c\u6d4b\u662f\u9b54\u6cd5\u5973\u795e\u56de\u5f52\u795e\u5ea7\u4e4b\u540e\uff0c\u7acb\u523b\u505a\u51fa\u4e86\u8865\u6551\uff0c\u8fd9\u662f\u5979\u7684\u5929\u804c\u6240\u5728\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u743c\u6069\u72b9\u8c6b\u4e86\u4e00\u4f1a\uff0c\u8fd8\u662f\u5207\u65ad\u4e86\u81ea\u5df1\u4e0e\u9b54\u7f51\u7684\u8054\u63a5\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u4ed6\u76f8\u5f53\u4e8e\u6682\u65f6\u4e27\u5931\u4e86\u6240\u6709\u7684\u65bd\u6cd5\u80fd\u529b\uff0c\u9664\u4e86\u90a3\u4e9b\u4ece\u5404\u79cd\u6e20\u9053\u83b7\u5f97\u7684\u795e\u529b\uff0c\u4ee5\u53ca\u624b\u4e2d\u7684\u300a\u547d\u8fd0\u957f\u591c\u300b\uff0c\u518d\u65e0\u5176\u4ed6\u529b\u91cf\u53ef\u7528\u3002</p><p>\u8db3\u591f\u4e86\uff0c\u4ed6\u60f3\u3002</p><p>\u5deb\u5e08\u6bcf\u5929\u80fd\u591f\u4f7f\u7528\u7684\u6cd5\u672f\u662f\u6709\u9650\u7684\uff0c\u800c\u4e14\u5fc5\u987b\u63d0\u524d\u51c6\u5907\u597d\u3002\u4e00\u573a\u5927\u6218\uff0c\u743c\u6069\u51c6\u5907\u7684\u6cd5\u672f\u5df2\u7ecf\u6d88\u8017\u5f97\u4e03\u4e03\u516b\u516b\uff0c\u6240\u5269\u65e0\u51e0\u4e86\uff0c\u6240\u4ee5\u5bf9\u4e8e\u6b64\u523b\u7684\u4ed6\u800c\u8a00\uff0c\u6682\u65f6\u653e\u5f03\u65bd\u6cd5\u80fd\u529b\uff0c\u5f71\u54cd\u5176\u5b9e\u5e76\u6ca1\u6709\u60f3\u8c61\u5f97\u90a3\u4e48\u5927\uff0c\u4f46\u8fd9\u6837\u4f1a\u5b89\u5168\u5f88\u591a\u3002</p><p>\u838e\u73de\u514b\u88ab\u743c\u6069\u53ec\u5524\u51fa\u6765\uff0c\u9b45\u9b54\u5728\u5927\u6218\u4e4b\u524d\u5c31\u56de\u5230\u5b9d\u77f3\u4e2d\uff0c\u5bf9\u540e\u9762\u53d1\u751f\u7684\u4e8b\u60c5\u4e00\u65e0\u6240\u77e5\u3002\u743c\u6069\u7b80\u7565\u5730\u8bb2\u4e86\u4e00\u904d\uff0c\u838e\u73de\u514b\u542c\u5f97\u60ca\u53f9\u8fde\u8fde\uff0c\u6ca1\u60f3\u5230\u5267\u60c5\u5c45\u7136\u5982\u6b64\u5cf0\u56de\u8def\u8f6c\uff0c\u5404\u65b9\u52bf\u529b\u7eb7\u7eb7\u843d\u5b50\u535a\u5f08\uff0c\u5374\u88ab\u624e\u745e\u5c14\u4ee5\u4e00\u5df1\u4e4b\u529b\uff0c\u7528\u7eb5\u6a2a\u624b\u6bb5\uff0c\u56e0\u52bf\u5229\u5bfc\uff0c\u5c3d\u6570\u7834\u53bb\uff0c\u5230\u5934\u6765\u5404\u65b9\u6b7b\u4f24\u6b86\u5c3d\uff0c\u53cd\u5012\u662f\u6700\u4e0d\u8d77\u773c\u7684\u743c\u6069\u4e00\u8dc3\u800c\u8d77\uff0c\u6536\u83b7\u6700\u591a\u3002\u8fd9\u79cd\u626d\u8f6c\u4e7e\u5764\u7684\u624b\u6bb5\uff0c\u5b9e\u5728\u662f\u8ba9\u4eba\u53f9\u4e3a\u89c2\u6b62\uff0c\u7531\u4e0d\u5f97\u838e\u73de\u514b\u4e0d\u4f69\u670d\u3002</p><p>\u201c\u592a\u5389\u5bb3\u4e86\uff0c\u201d\u5979\u6ee1\u8138\u90fd\u662f\u5d07\u62dc\u7684\u8868\u60c5\uff0c\u201c\u4e3b\u4eba\u4f60\u80fd\u4e0d\u80fd\u5e2e\u6211\u8bf4\u8bf4\uff0c\u6211\u60f3\u62dc\u5979\u4e3a\u5e08\u3002\u201d</p><p>\u201c\u4f60\u4e00\u4e2a\u6076\u9b54\u62dc\u5927\u9b54\u9b3c\u505a\u8001\u5e08\uff0c\u4e0d\u89c9\u5f97\u5f88\u5947\u602a\u4e48\u3002\u201d</p><p>\u201c\u6ca1\u6709\u554a\uff0c\u5b66\u65e0\u5148\u540e\uff0c\u8fbe\u8005\u4e3a\u5e08\u561b\u3002\u201d</p><p>\u201c\u2026\u2026\u8428\u9a6c\u65af\u7279\u7ed9\u4e86\u6211\u4e00\u672c\u4e66\uff0c\u4f60\u53ef\u4ee5\u770b\u770b\uff0c\u91cc\u9762\u6709\u53e5\u8bdd\u4f60\u4e00\u5b9a\u5f88\u559c\u6b22\uff0c\u201d\u743c\u6069\u628a\u7af9\u518c\u62ff\u51fa\u6765\uff0c\u7ffb\u5230\u90a3\u4e00\u5757\uff0c\u201c\u4f60\u770b\uff0c\u5c31\u662f\u8fd9\u53e5\uff0c\u4e09\u4eba\u884c\u2014\u2014\u6211\u9760\uff01\u201d\u4ed6\u5fcd\u4e0d\u4f4f\u7206\u4e86\u4e00\u53e5\u7c97\u53e3\uff0c\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u838e\u73de\u514b\u51d1\u8fc7\u6765\uff0c\u53ea\u89c1\u90a3\u7af9\u518c\u4e0a\u7528\u901a\u7528\u8bed\u6e05\u6e05\u695a\u695a\u5730\u5199\u7740\uff1a\u201c\u5982\u679c\u4f60\u540c\u65f6\u548c\u4e24\u4e2a\u4eba\u505a\u7231\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u4e00\u5b9a\u6280\u672f\u9ad8\u8d85\uff0c\u53ef\u4ee5\u505a\u4f60\u7684\u8001\u5e08\u3002\u201d</p><p>\u201c\u8fd9\u53e5\u8bdd\u597d\u50cf\u633a\u6709\u8da3\u554a\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\u4e66\uff1f\u6027\u7231\u6307\u5357\u5417\uff1f\u201d</p><p>\u201c\u4e0d\uff0c\u4e0d\u662f\uff0c\u201d\u743c\u6069\u624b\u5fd9\u811a\u4e71\u5730\u628a\u4e66\u6536\u8d77\u6765\uff0c\u201c\u8fd9\u662f\u4e00\u672c\u8574\u542b\u4eba\u751f\u81f3\u7406\u7684\u5723\u8d24\u8bed\u5f55\u3002\u201d</p><p>\u2026\u2026\u600e\u4e48\u8fd9\u8bdd\u8bf4\u8d77\u6765\u8fd9\u4e48\u4e0d\u5bf9\u52b2\u3002</p><p>\u838e\u73de\u514b\u5374\u5f88\u81ea\u7136\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u7537\u5973\u6027\u7231\uff0c\u7684\u786e\u662f\u4eba\u751f\u81f3\u7406\uff0c\u8fd9\u672c\u4e66\u80fd\u4e0d\u80fd\u501f\u6211\u8bfb\u4e00\u8bfb\uff0c\u6211\u89c9\u5f97\u81ea\u5df1\u5e94\u8be5\u5b66\u4e60\u63d0\u9ad8\u4e00\u4e0b\u3002\u79bb\u5f00\u6df1\u6e0a\u592a\u4e45\u4e86\uff0c\u4f60\u53c8\u4e0d\u8bb8\u6211\u52fe\u642d\u5176\u4ed6\u7537\u4eba\uff0c\u6709\u65f6\u5019\u90fd\u5feb\u8981\u5fd8\u8bb0\u81ea\u5df1\u662f\u4e2a\u9b45\u9b54\u4e86\uff0c\u4e13\u4e1a\u7d20\u517b\u4e0d\u80fd\u4e22\u4e0b\u554a\u3002\u201d</p><p>\u4e0d\u7528\u4e0d\u7528\uff0c\u4f60\u5df2\u7ecf\u662f\u6b64\u9053\u9ad8\u624b\uff0c\u5c31\u4e0d\u9700\u8981\u5b66\u4e60\u63d0\u9ad8\u4e86\u3002\u518d\u5b66\u4e60\u63d0\u9ad8\uff0c\u743c\u6069\u54ea\u91cc\u8fd8\u5e94\u4ed8\u5f97\u6765\uff0c\u6ca1\u770b\u5230\u540e\u5bab\u91cc\u8fd8\u6709\u90a3\u4e48\u591a\u6f02\u4eae\u5973\u5b69\u5b50\u90fd\u5728\u7b49\u7740\u5462\u3002</p><p>\u8d39\u4e86\u534a\u5929\u53e3\u820c\uff0c\u743c\u6069\u7ec8\u4e8e\u5411\u838e\u73de\u514b\u89e3\u91ca\u6e05\u695a\uff0c\u8fd9\u4e0d\u662f\u4ec0\u4e48\u6027\u7231\u6307\u5357\uff0c\u81f3\u4e8e\u90a3\u53e5\u8bdd\u7eaf\u7cb9\u662f\u7ffb\u8bd1\u95ee\u9898\uff0c\u4e07\u4e07\u4e0d\u53ef\u5f53\u771f\u3002\u770b\u9b45\u9b54\u7684\u795e\u60c5\uff0c\u660e\u663e\u8fd8\u662f\u5c06\u4fe1\u5c06\u7591\uff0c\u743c\u6069\u4e5f\u5b9e\u5728\u6ca1\u529b\u6c14\u5206\u8fa8\u4e86\uff0c\u201c\u5148\u4e0d\u7ba1\u8fd9\u4e2a\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8ddf\u6211\u53bb\u9634\u5f71\u9547\u3002\u201d</p><p>\u201c\u53bb\u5e72\u561b\uff1f\u201d</p><p>\u201c\u627e\u4eba\u3002\u201d</p><p>\u743c\u6069\u8981\u627e\u7684\u81ea\u7136\u662f\u7ef4\u82e5\u62c9\uff0c\u4f20\u9053\u5deb\u5e08\u3002</p><p>\u5728\u6700\u540e\u7684\u5927\u6218\u4e2d\uff0c\u7ef4\u82e5\u62c9\u5e76\u672a\u53d7\u5230\u592a\u5927\u6ce2\u53ca\uff0c\u5979\u4e0d\u662f\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\uff0c\u4e5f\u4e0d\u4f1a\u6709\u94f6\u706b\u5931\u63a7\u7684\u5371\u9669\u3002\u624e\u745e\u5c14\u6ca1\u6709\u5c06\u5979\u6536\u5230\u4e66\u4e2d\uff0c\u90a3\u4e48\u5979\u6700\u53ef\u80fd\u7684\u53bb\u5411\uff0c\u5c31\u662f\u9634\u5f71\u9547\u3002\u867d\u7136\u6ca1\u6709\u4ec0\u4e48\u8bc1\u636e\uff0c\u4f46\u743c\u6069\u662f\u5982\u6b64\u5224\u65ad\u7684\u3002</p><p>\u5bf9\u4e8e\u8fd9\u573a\u5927\u6218\u4e2d\u7684\u5404\u65b9\u52bf\u529b\u6765\u8bf4\uff0c\u7ef4\u82e5\u62c9\u5e76\u4e0d\u7b97\u4ec0\u4e48\u91cd\u8981\u89d2\u8272\u3002\u5979\u6700\u5f00\u59cb\u662f\u88ab\u8428\u9a6c\u65af\u7279\u5f53\u505a\u65bd\u6cd5\u9053\u5177\uff0c\u540e\u6765\u8001\u5deb\u5996\u88ab\u624e\u745e\u5c14\u8bf1\u9a97\uff0c\u653e\u5f03\u5316\u8eab\u8ba1\u5212\uff0c\u5979\u5c31\u66f4\u6ca1\u6709\u5229\u7528\u4ef7\u503c\u4e86\u3002\u4f46\u5bf9\u743c\u6069\u800c\u8a00\uff0c\u5979\u5374\u5f88\u91cd\u8981\uff0c\u5341\u5206\u91cd\u8981\uff0c\u56e0\u4e3a\u76f4\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u5979\u4e0b\u5728\u743c\u6069\u8eab\u4e0a\u7684\u4e13\u60c5\u8bc5\u5492\u8fd8\u6ca1\u89e3\u5462\u3002</p><p>\u4f5c\u4e3a\u4e00\u4e2a\u4e0d\u6c42\u5f81\u670d\u4e16\u754c\uff0c\u4e0d\u6c42\u91d1\u5c71\u94f6\u6d77\uff0c\u4e0d\u6c42\u6539\u53d8\u5386\u53f2\uff0c\u4e0d\u6c42\u5c60\u7f8e\u706d\u65e5\uff0c\u53ea\u60f3\u7740\u5efa\u4e00\u5ea7\u6d6e\u7a7a\u57ce\uff0c\u5f00\u4e00\u5ea7\u5927\u5927\u7684\u540e\u5bab\u7684\u743c\u6069\u800c\u8a00\uff0c\u7ef4\u82e5\u62c9\u7684\u8fd9\u4e2a\u8bc5\u5492\u7b80\u76f4\u5c31\u662f\u68a6\u9b47\uff0c\u8db3\u4ee5\u4ee4\u4ed6\u7684\u4eba\u751f\u4ece\u6b64\u7070\u9eef\u65e0\u5149\uff0c\u751f\u6d3b\u5931\u53bb\u610f\u4e49\u3002\u867d\u8bf4\u624e\u745e\u5c14\u80fd\u7528\u5851\u68a6\u4e4b\u6cd5\u89c4\u907f\uff0c\u4f46\u90a3\u6bd5\u7adf\u53ea\u662f\u6cbb\u6807\u4e0d\u6cbb\u672c\uff0c\u800c\u4e14\u9b54\u59ec\u53c8\u56de\u5730\u72f1\u53bb\u4e86\uff0c\u4ee5\u540e\u7684\u6027\u798f\u751f\u6d3b\u8981\u600e\u4e48\u529e\u5462\uff1f\u6709\u9274\u4e8e\u6b64\uff0c\u522b\u7684\u4e8b\u60c5\u90fd\u53ef\u4ee5\u6682\u65f6\u653e\u5230\u4e00\u8fb9\uff0c\u7ef4\u82e5\u62c9\u662f\u4e00\u5b9a\u8981\u5148\u627e\u5230\u7684\u3002</p><p>\u201c\u53ef\u662f\uff0c\u4e3b\u4eba\u4f60\u548c\u9b54\u6cd5\u5973\u795e\u5df2\u7ecf\u5f7b\u5e95\u6210\u4e86\u6b7b\u654c\uff0c\u73b0\u5728\u53bb\u9634\u5f71\u9547\uff0c\u662f\u4e0d\u662f\u592a\u5371\u9669\u4e86\u70b9\uff1f\u201d\u838e\u73de\u514b\u63d0\u51fa\u7591\u95ee\u3002</p><p>\u743c\u6069\u5f53\u7136\u8003\u8651\u8fc7\u8fd9\u4e2a\u95ee\u9898\uff0c\u201c\u73b0\u5728\u5c31\u662f\u6700\u597d\u7684\u673a\u4f1a\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u4f11\u606f\u4e86\u534a\u5929\u65f6\u95f4\uff0c\u53c8\u505a\u4e86\u4e00\u4e9b\u51c6\u5907\u5de5\u4f5c\uff0c\u770b\u770b\u591c\u5e55\u964d\u4e34\uff0c\u4ed6\u548c\u838e\u73de\u514b\u5f00\u59cb\u52a8\u8eab\u3002\u9634\u5f71\u9547\u7684\u5b88\u536b\u660e\u663e\u4e0d\u5982\u4e4b\u524d\u68ee\u4e25\uff0c\u8fd9\u5728\u743c\u6069\u7684\u610f\u6599\u4e4b\u4e2d\u3002\u4e00\u573a\u5927\u6218\uff0c\u9b54\u6cd5\u5973\u795e\u6559\u4f1a\u635f\u5931\u60e8\u91cd\uff0c\u4e0a\u81f3\u5973\u795e\u3001\u9009\u6c11\uff0c\u4e0b\u81f3\u666e\u901a\u6210\u5458\uff0c\u90fd\u6709\u5f88\u5927\u7684\u4f24\u4ea1\u3002\u6b23\u5e03\u7684\u60c5\u5f62\u5e94\u8be5\u4e0d\u662f\u7279\u4f8b\uff0c\u4e5f\u5373\u662f\u8bf4\uff0c\u5f53\u65f6\u5728\u573a\u7684\u51ef\u5c14\u672c\u3001\u98ce\u66b4\uff0c\u80af\u5b9a\u4e5f\u9677\u5165\u540c\u6837\u7684\u94f6\u706b\u5931\u63a7\u7684\u5c40\u9762\uff0c\u751a\u81f3\u7559\u5728\u9634\u5f71\u9547\u4e2d\u7684\u90a3\u4e9b\u9009\u6c11\u4e5f\u6709\u53ef\u80fd\u88ab\u6ce2\u53ca\u3002\u5931\u53bb\u4e86\u8fd9\u4e9b\u9886\u5bfc\u8005\uff0c\u9634\u5f71\u9547\u7684\u9632\u5fa1\u5728\u743c\u6069\u773c\u4e2d\u770b\u6765\u4e5f\u5c31\u4e0d\u7b97\u4ec0\u4e48\u4e86\uff0c\u82b1\u4e86\u534a\u4e2a\u591a\u5c0f\u65f6\uff0c\u4ed6\u627e\u5230\u4e00\u4e2a\u6f0f\u6d1e\uff0c\u5e26\u7740\u838e\u73de\u514b\u6f5c\u5165\u8fdb\u6765\u3002</p><p>\u63a5\u4e0b\u6765\u8981\u627e\u5230\u7ef4\u82e5\u62c9\u5728\u54ea\u91cc\u3002</p><p>\u8fd9\u4e2a\u5c31\u6bd4\u8f83\u96be\u4e86\uff0c\u8c01\u77e5\u9053\u5979\u5728\u54ea\u91cc\u3002\u9634\u5f71\u9547\u867d\u7136\u4e0d\u7b97\u5e7f\u5927\uff0c\u5374\u4e5f\u4e0d\u7b97\u5c0f\uff0c\u5efa\u7b51\u6797\u7acb\uff0c\u8981\u627e\u4e00\u4e2a\u4eba\u8c08\u4f55\u5bb9\u6613\u3002\u743c\u6069\u4f2a\u88c5\u6210\u4e00\u4e2a\u666e\u901a\u7684\u9634\u5f71\u9547\u5c45\u6c11\uff0c\u8f6c\u4e86\u4e24\u5708\uff0c\u4ecd\u7136\u662f\u4e00\u65e0\u6240\u83b7\u3002\u4ed6\u4e0d\u6562\u8fc7\u5206\u6d3b\u8dc3\uff0c\u6015\u60f9\u4eba\u7591\u8651\uff0c\u6bd5\u7adf\u8fd9\u91cc\u76ee\u524d\u4ecd\u662f\u654c\u65b9\u5927\u672c\u8425\uff0c\u867d\u7136\u9b54\u6cd5\u5973\u795e\u5df2\u7ecf\u56de\u5f52\u5929\u754c\uff0c\u9009\u6c11\u4eec\u6216\u4f24\u6216\u4ea1\uff0c\u4f46\u743c\u6069\u81ea\u5df1\u4e5f\u4e0d\u662f\u4ec0\u4e48\u7edd\u9876\u4eba\u7269\uff0c\u6ca1\u8d44\u683c\u5927\u6447\u5927\u6446\u5730\u56a3\u5f20\u3002</p><p>\u201c\u6211\u53bb\u8bd5\u8bd5\u5427\u3002\u201d\u838e\u73de\u514b\u8bf4\u3002</p><p>\u743c\u6069\u70b9\u5934\uff0c\u4ed6\u627e\u4e86\u4e2a\u7a7a\u623f\u5b50\u7b49\u5f85\u7740\uff0c\u5927\u7ea6\u8fc7\u4e86\u534a\u4e2a\u5c0f\u65f6\uff0c\u838e\u73de\u514b\u56de\u6765\u4e86\uff0c\u540e\u9762\u8fd8\u8ddf\u7740\u4e00\u4e2a\u4e2d\u5e74\u7537\u4eba\uff0c\u7a7f\u7740\u9634\u5f71\u9547\u5b88\u536b\u7684\u5236\u670d\uff0c\u773c\u795e\u53d1\u76f4\uff0c\u663e\u7136\u5df2\u7ecf\u88ab\u63a7\u5236\u3002\u201c\u4ed6\u8bf4\u5927\u6982\u4e24\u5c0f\u65f6\u524d\uff0c\u89c1\u8fc7\u4e00\u4f4d\u91d1\u8272\u957f\u53d1\u7684\u5973\u5deb\u5e08\uff0c\u4ece\u76f8\u8c8c\u548c\u4f53\u6001\u63cf\u8ff0\u4e0a\u770b\uff0c\u5e94\u8be5\u5c31\u662f\u4f60\u8bf4\u7684\u90a3\u4f4d\u7ef4\u82e5\u62c9\u5c0f\u59d0\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u4ed4\u7ec6\u76d8\u95ee\uff0c\u8fd9\u4e2a\u5b88\u536b\u663e\u7136\u77e5\u9053\u7684\u4e5f\u4e0d\u662f\u5f88\u591a\uff0c\u4ed6\u751a\u81f3\u90fd\u4e0d\u8ba4\u8bc6\u7ef4\u82e5\u62c9\uff0c\u53ea\u662f\u5728\u6267\u52e4\u65f6\u5076\u7136\u9047\u5230\u4e00\u4f4d\u964c\u751f\u7684\u91d1\u53d1\u7f8e\u4eba\uff0c\u884c\u8272\u5306\u5306\uff0c\u795e\u60c5\u843d\u5bde\uff0c\u4e00\u65f6\u7ec5\u58eb\u7cbe\u795e\u53d1\u4f5c\uff0c\u4e0a\u53bb\u5618\u5bd2\u95ee\u6696\u4e86\u51e0\u53e5\uff0c\u7ed3\u679c\u88ab\u5b8c\u5168\u65e0\u89c6\u3002\u4e0b\u73ed\u540e\u4ed6\u4e0e\u540c\u4e8b\u5728\u9152\u9986\u91cc\u5439\u5618\u201c\u8273\u9047\u201d\uff0c\u6070\u597d\u88ab\u838e\u73de\u514b\u542c\u89c1\uff0c\u4fbf\u88ab\u6293\u4e86\u56de\u6765\u3002\u4f46\u4ece\u4ed6\u7684\u63cf\u8ff0\u6765\u770b\uff0c\u90a3\u4f4d\u91d1\u53d1\u7f8e\u4eba\u5e94\u8be5\u5c31\u662f\u7ef4\u82e5\u62c9\u6ca1\u9519\uff0c\u81f3\u4e8e\u53bb\u5411\uff0c\u5b88\u536b\u53ea\u80fd\u8bf4\u51fa\u4e24\u4e2a\u5c0f\u65f6\u4e4b\u524d\uff0c\u770b\u89c1\u5979\u53bb\u4e86\u9547\u5b50\u5357\u8fb9\u7684\u4e00\u5904\u5c45\u6c11\u533a\uff0c\u5177\u4f53\u54ea\u680b\u623f\u5b50\u5c31\u5b9e\u5728\u4e0d\u6e05\u695a\u4e86\u3002</p><p>\u53ea\u80fd\u6328\u4e2a\u53bb\u627e\u4e86\u3002</p><p>\u603b\u7b97\u662f\u6709\u4e86\u5927\u81f4\u8303\u56f4\uff0c\u6bd4\u6beb\u65e0\u76ee\u7684\u7684\u4e71\u649e\u78b0\u8fd0\u6c14\u5f3a\u3002\u743c\u6069\u8ba9\u838e\u73de\u514b\u5c06\u5b88\u536b\u50ac\u7720\uff0c\u4e22\u5728\u623f\u5b50\u7684\u9601\u697c\u4e0a\u3002\u838e\u73de\u514b\u5efa\u8bae\u76f4\u63a5\u6740\u6389\u6bd4\u8f83\u7701\u4e8b\uff0c\u743c\u6069\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u8fd8\u662f\u6447\u4e86\u6447\u5934\u3002\u201c\u6ca1\u5fc5\u8981\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6740\u4e86\u6709\u8840\u8165\u6c14\uff0c\u53cd\u800c\u5bb9\u6613\u62db\u4eba\u6ce8\u610f\u3002\u201d</p><p>\u201c\u8840\u8165\u6c14\u5f88\u5bb9\u6613\u6d88\u9664\u7684\u3002\u201d</p><p>\u201c\u7b97\u4e86\uff0c\u522b\u9ebb\u70e6\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8d76\u5feb\u8d70\uff0c\u8fd8\u6709\u6b63\u4e8b\u8981\u505a\u5462\u3002\u627e\u4e0d\u5230\u5979\uff0c\u4f60\u4ee5\u540e\u7684\u6027\u798f\u751f\u6d3b\u5c31\u6ca1\u6709\u4e86\u3002\u201d</p><p>\u201c\u6211\u624d\u4e0d\u62c5\u5fc3\u5462\uff0c\u6709\u4eba\u6bd4\u6211\u66f4\u7740\u6025\uff0c\u201d\u9b45\u9b54\u5403\u5403\u5730\u7b11\uff0c\u201c\u5176\u5b9e\u4e3b\u4eba\u4f60\u5c31\u662f\u865a\u4f2a\u5bf9\u5427\uff0c\u660e\u660e\u90fd\u5df2\u7ecf\u6740\u4e86\u90a3\u4e48\u591a\uff0c\u504f\u504f\u8fd8\u8981\u5728\u610f\u8fd9\u79cd\u5c0f\u89d2\u8272\u3002\u201d</p><p>\u201c\u6211\u5c31\u662f\u865a\u4f2a\uff0c\u6211\u559c\u6b22\uff01\u4f60\u7ba1\u5f97\u7740\uff01\u201d</p><p>\u743c\u6069\u76f4\u622a\u4e86\u5f53\u5730\u7ed3\u675f\u4e89\u8bba\u3002\u4e24\u4eba\u8d81\u7740\u591c\u8272\u63a9\u62a4\uff0c\u94bb\u5230\u5b88\u536b\u6240\u8bf4\u7684\u90a3\u7247\u5c45\u6c11\u533a\u4e2d\uff0c\u6328\u5bb6\u6328\u6237\u5730\u5bfb\u627e\u3002\u603b\u7b97\u8fd0\u6c14\u4e0d\u592a\u574f\uff0c\u5728\u7b2c\u4e94\u5bb6\u7684\u65f6\u5019\uff0c\u743c\u6069\u7ec8\u4e8e\u770b\u89c1\u4e86\u4f20\u9053\u5deb\u5e08\u3002</p><p>\u201c\u8fd9\u662f\u4e2a\u795e\u6bbf\u554a\u3002\u201d\u838e\u73de\u514b\u6084\u58f0\u8bf4\u3002</p><p>\u4ece\u5916\u9762\u5b8c\u5168\u6ca1\u770b\u51fa\u6765\uff0c\u8fdb\u5165\u5185\u90e8\u624d\u53d1\u73b0\uff0c\u8fd9\u4e2a\u6c11\u5b85\u822c\u7684\u5efa\u7b51\u5c45\u7136\u662f\u4e2a\u795e\u6bbf\uff0c\u6709\u795e\u50cf\uff0c\u6709\u796d\u575b\uff0c\u8fd8\u6709\u7948\u7977\u5ba4\u4e4b\u7c7b\uff0c\u4f46\u683c\u5c40\u90fd\u5f88\u5c0f\uff0c\u4e5f\u5f88\u7b80\u964b\uff0c\u5bb6\u5177\u4e5f\u90fd\u9887\u6709\u4e9b\u9648\u65e7\uff0c\u51b7\u51b7\u6e05\u6e05\u7684\u3002\u795e\u50cf\u662f\u4e00\u4e2a\u6234\u7740\u5c16\u9876\u515c\u5e3d\u7684\u767d\u80e1\u5b50\u8001\u5deb\u5e08\uff0c\u8fd9\u79cd\u5e3d\u5b50\u662f\u51e0\u767e\u5e74\u524d\u6bd4\u8f83\u6d41\u884c\u7684\u5deb\u5e08\u5e3d\u7684\u6b3e\u5f0f\uff0c\u636e\u8bf4\u5f53\u65f6\u5f88\u98ce\u9761\uff0c\u73b0\u5728\u65e9\u5c31\u6ca1\u4eba\u6234\u4e86\u3002\u201c\u5deb\u5e08\u4e4b\u795e\u3002\u201d\u743c\u6069\u8f7b\u58f0\u5bf9\u838e\u73de\u514b\u8bf4\u3002</p><p>\u7ef4\u82e5\u62c9\u662f\u5deb\u5e08\u4e4b\u795e\u963f\u7956\u65af\u7684\u9009\u6c11\uff0c\u8fd9\u5ea7\u5c0f\u5c0f\u7684\u795e\u6bbf\u663e\u7136\u7b97\u662f\u6559\u4f1a\u5728\u9634\u5f71\u9547\u7684\u4e00\u4e2a\u636e\u70b9\uff0c\u4f46\u9664\u4e86\u7ef4\u82e5\u62c9\u4e4b\u5916\uff0c\u518d\u6ca1\u770b\u5230\u5176\u4ed6\u4eba\uff0c\u4e0d\u77e5\u9053\u662f\u6218\u6b7b\u4e86\uff0c\u8fd8\u662f\u539f\u672c\u5c31\u6ca1\u6709\u3002\u743c\u6069\u548c\u838e\u73de\u514b\u6f5c\u5165\u7684\u65f6\u5019\uff0c\u7ef4\u82e5\u62c9\u6b63\u7ad9\u5728\u795e\u50cf\u524d\u9ed8\u9ed8\u7977\u544a\uff0c\u5634\u5507\u65e0\u58f0\u5730\u9ed8\u5ff5\u7740\uff0c\u4e0d\u77e5\u9053\u5728\u8bf4\u4ec0\u4e48\u3002\u743c\u6069\u4e0d\u6562\u6253\u6270\uff0c\u795e\u7957\u5e76\u975e\u65f6\u5e38\u5173\u6ce8\u51e1\u95f4\uff0c\u4f46\u8fd9\u79cd\u9ad8\u7ea7\u795e\u804c\u4eba\u5458\u7948\u7977\u7684\u65f6\u5019\uff0c\u5f80\u5f80\u80fd\u591f\u5f53\u771f\u4e0e\u795e\u7957\u6c9f\u901a\uff0c\u5efa\u7acb\u7cbe\u795e\u8054\u7ed3\u3002\u5018\u82e5\u8d38\u7136\u9732\u9762\uff0c\u88ab\u5deb\u5e08\u795e\u5173\u6ce8\u4e00\u4e0b\uff0c\u90a3\u5c31\u9ebb\u70e6\u5927\u4e86\u3002\u4ed6\u8010\u5fc3\u5730\u7b49\u5f85\u7740\uff0c\u8db3\u8db3\u7b49\u4e86\u5927\u7ea6\u4e00\u4e2a\u534a\u5c0f\u65f6\uff0c\u7ec8\u4e8e\u770b\u89c1\u7ef4\u82e5\u62c9\u7ed3\u675f\u4e86\u7948\u7977\u3002\u4f20\u9053\u5deb\u5e08\u5148\u662f\u53bb\u6d74\u5ba4\u6d17\u6fa1\uff0c\u7136\u540e\u88f9\u7740\u7761\u888d\u56de\u5230\u5367\u5ba4\uff0c\u743c\u6069\u53ec\u5524\u4e86\u4e00\u4e2a\u79d8\u6cd5\u773c\u6f5c\u8fdb\u53bb\uff0c\u53d1\u73b0\u5979\u5e76\u672a\u4f11\u606f\uff0c\u800c\u662f\u5750\u5728\u5e8a\u5934\uff0c\u4e00\u8fb9\u7528\u6bdb\u5dfe\u64e6\u5e72\u5934\u53d1\uff0c\u4e00\u8fb9\u6c89\u601d\u7740\uff0c\u7709\u5934\u7d27\u9501\uff0c\u4eff\u4f5b\u6709\u4ec0\u4e48\u95ee\u9898\u96be\u4ee5\u7d22\u89e3\uff0c\u4ee5\u81f3\u4e8e\u8fde\u88ab\u4eba\u8fd1\u8ddd\u79bb\u5077\u7aa5\u90fd\u6ca1\u53d1\u89c9\u3002</p><p>\u743c\u6069\u8ba9\u838e\u73de\u514b\u4fa6\u67e5\u56db\u5468\uff0c\u786e\u8ba4\u6ca1\u6709\u4efb\u4f55\u9677\u9631\u57cb\u4f0f\uff0c\u7136\u540e\u624d\u63a8\u5f00\u95e8\u8d70\u8fdb\u6765\u3002\u7ef4\u82e5\u62c9\u5413\u4e86\u4e00\u8df3\uff0c\u5b9a\u775b\u770b\u89c1\u662f\u743c\u6069\uff0c\u987f\u65f6\u8138\u8272\u5927\u53d8\uff0c\u62ac\u624b\u84c4\u8d77\u4e00\u4e2a\u5149\u7403\u5c31\u8981\u6254\u8fc7\u6765\uff0c\u743c\u6069\u4e0d\u95ea\u4e0d\u907f\uff0c\u201c\u505c\u4e0b\uff01\u201d\u4ed6\u8bf4\u3002</p><p>\u7ef4\u82e5\u62c9\u4e0d\u7531\u81ea\u4e3b\u5730\u8eab\u4f53\u50f5\u76f4\uff0c\u6cd5\u672f\u5931\u53bb\u63a7\u5236\uff0c\u4e5f\u968f\u4e4b\u6d88\u6563\u65e0\u5f62\u3002\u201c\u4f60\u5fd8\u4e86\uff0c\u6211\u4eec\u4e4b\u95f4\u8fd8\u6709\u4e00\u4efd\u5951\u7ea6\uff0c\u7ecf\u8fc7\u5730\u72f1\u6cd5\u5219\u516c\u8bc1\u8fc7\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5728\u5b83\u6ca1\u6709\u5931\u6548\u4e4b\u524d\uff0c\u6211\u5bf9\u4f60\u90fd\u6709\u7edd\u5bf9\u7684\u63a7\u5236\u6743\uff0c\u53ea\u8981\u4e0d\u6d89\u53ca\u90a3\u4e2a\u8bc5\u5492\uff0c\u6211\u7684\u4efb\u4f55\u547d\u4ee4\u4f60\u90fd\u5fc5\u987b\u9075\u4ece\u3002\u201d</p><p>\u201c\u4f60\u6765\u5e72\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u5e94\u8be5\u6e05\u695a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5f53\u7136\u662f\u4e3a\u4e86\u89e3\u9664\u8bc5\u5492\u3002\u201d</p><p>\u201c\u4f11\u60f3\uff01\u201d</p><p>\u201c\u6211\u5df2\u7ecf\u5b8c\u6210\u4e86\u5951\u7ea6\u7684\u6761\u4ef6\uff0c\u201d\u743c\u6069\u63d0\u9192\uff0c\u201c\u8131\u79bb\u8428\u9a6c\u65af\u7279\u7684\u63a7\u5236\u3001\u5728\u672a\u53d7\u5230\u81f4\u547d\u4f24\u5bb3\u7684\u60c5\u51b5\u4e0b\u56de\u5230\u9634\u5f71\u9547\u3001\u9634\u5f71\u9547\u6ca1\u6709\u88ab\u8428\u9a6c\u65af\u7279\u6240\u5360\u636e\u2014\u2014\u8fd9\u4e09\u4e2a\u6761\u4ef6\u73b0\u5728\u90fd\u6ee1\u8db3\u4e86\u3002\u800c\u4e14\u6211\u5728\u5176\u4e2d\u4e5f\u7b97\u51fa\u4e86\u529b\uff0c\u8fd9\u4e2a\u4f60\u4e0d\u4f1a\u5426\u8ba4\u5427\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u7684\u786e\u6ca1\u6cd5\u5426\u8ba4\u8fd9\u70b9\uff0c\u8428\u9a6c\u65af\u7279\u5c31\u662f\u88ab\u624e\u745e\u5c14\u548c\u6885\u83f2\u65af\u5e72\u6389\u7684\uff0c\u624e\u745e\u5c14\u662f\u5e55\u540e\u9ed1\u624b\uff0c\u6885\u83f2\u65af\u662f\u76f4\u63a5\u52a8\u624b\uff0c\u8fd9\u4e24\u4eba\u90fd\u662f\u743c\u6069\u7684\u60c5\u4eba\uff0c\u81ea\u7136\u7b97\u662f\u743c\u6069\u6709\u51fa\u529b\u3002\u201c\u65e2\u7136\u4f60\u8bf4\u6761\u4ef6\u5df2\u7ecf\u5b8c\u6210\uff0c\u90a3\u4f60\u51ed\u501f\u5951\u7ea6\u5f3a\u5236\u8981\u6211\u89e3\u5492\u4e0d\u5c31\u53ef\u4ee5\u4e86\uff0c\u201d\u4f20\u9053\u5deb\u5e08\u51b7\u7b11\uff0c\u201c\u4f55\u5fc5\u8fd8\u8981\u548c\u6211\u591a\u8d39\u53e3\u820c\u3002\u201d</p><p>\u5951\u7ea6\u7684\u786e\u5c1a\u672a\u5b8c\u6210\u3002</p><p>\u5f53\u65f6\u7ef4\u82e5\u62c9\u5f00\u51fa\u7684\u6761\u4ef6\uff0c\u9664\u4e86\u743c\u6069\u4e0a\u9762\u5217\u4e3e\u7684\u8fd9\u51e0\u9879\uff0c\u8fd8\u6709\u6700\u540e\u4e00\u6761\uff0c\u662f\u201c\u89c1\u5230\u4f0a\u5c14\u660e\u65af\u7279\u6216\u8005\u6b23\u5e03\u4e4b\u4e2d\u7684\u4efb\u610f\u4e00\u4eba\u201d\u3002\u6761\u4ef6\u6ca1\u6709\u5168\u90e8\u8fbe\u6210\uff0c\u743c\u6069\u5f53\u7136\u6ca1\u529e\u6cd5\u5f3a\u5236\u7ef4\u82e5\u62c9\u89e3\u5492\uff0c\u4e0d\u8fc7\u8fd9\u4e5f\u5e76\u975e\u96be\u4e8b\uff0c\u6216\u8005\u8bf4\uff0c\u6b63\u5728\u4ed6\u7684\u610f\u6599\u4e4b\u4e2d\uff0c\u201c\u4f60\u60f3\u89c1\u6b23\u5e03\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u4f60\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u7ef4\u82e5\u62c9\u8b66\u60d5\u5730\u770b\u7740\u4ed6\u3002</p><p>\u201c\u6ca1\u4ec0\u4e48\u610f\u601d\uff0c\u5c31\u662f\u95ee\u95ee\u800c\u5df2\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5982\u679c\u4f60\u60f3\u89c1\u5979\uff0c\u90a3\u4e48\u5c31\u95ed\u4e0a\u773c\u775b\uff0c\u4e0d\u8981\u62b5\u6297\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u8fd8\u662f\u4f9d\u8a00\u95ed\u4e0a\u773c\u775b\u3002\u743c\u6069\u53d6\u51fa\u300a\u547d\u8fd0\u957f\u591c\u300b\uff0c\u5c06\u5979\u6536\u5165\u4e66\u9875\u4e4b\u4e2d\u3002\u4ed6\u539f\u672c\u4e5f\u53ef\u4ee5\u628a\u6b23\u5e03\u91ca\u653e\u51fa\u6765\uff0c\u90a3\u6837\u4e0d\u4f1a\u66b4\u9732\u8fd9\u672c\u4e66\uff0c\u4f46\u6b23\u5e03\u5982\u4eca\u72b6\u51b5\u4e0d\u7a33\u5b9a\uff0c\u5728\u4e66\u4e2d\u7684\u5f02\u6b21\u5143\u8fd8\u597d\u4e9b\uff0c\u4e00\u65e6\u56de\u5230\u7269\u8d28\u754c\uff0c\u53ea\u6015\u4f1a\u6709\u4ec0\u4e48\u4e0d\u53ef\u6d4b\u7684\u53d8\u6545\uff0c\u90a3\u5c31\u592a\u5371\u9669\u4e86\u3002</p><p>\u4e24\u79d2\u949f\u540e\uff0c\u743c\u6069\u7684\u58f0\u97f3\u5728\u8033\u8fb9\u54cd\u8d77\uff0c\u7ef4\u82e5\u62c9\u7741\u5f00\u773c\uff0c\u7136\u540e\u5979\u770b\u89c1\u4e86\u6b23\u5e03\u3002\u98ce\u66b4\u5973\u738b\u7684\u72b6\u51b5\u770b\u8d77\u6765\u6ca1\u6709\u4ec0\u4e48\u597d\u8f6c\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u660e\u663e\u6076\u5316\uff0c\u4ecd\u7136\u8fd8\u662f\u548c\u4e0a\u6b21\u743c\u6069\u770b\u5230\u7684\u4e00\u6837\uff0c\u660f\u8ff7\u4e0d\u9192\u3002\u201c\u4f60\u770b\u5230\u4e86\uff0c\u8fd9\u662f\u6b23\u5e03\uff0c\u6ca1\u9519\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u73b0\u5728\u662f\u4e0d\u662f\u6240\u6709\u7684\u6761\u4ef6\u90fd\u5df2\u7ecf\u8fbe\u6210\uff1f\u201d</p><p>\u201c\u4f60\u80fd\u6551\u5979\uff0c\u5bf9\u5427\u3002\u201d\u7ef4\u82e5\u62c9\u7a81\u7136\u8bf4\u3002</p><p>\u201c\u5979\u73b0\u5728\u662f\u94f6\u706b\u5931\u63a7\u66b4\u8d70\uff0c\u8981\u6551\u5979\u53ea\u6709\u5c06\u94f6\u706b\u79fb\u8d70\uff0c\u6211\u7684\u786e\u6709\u8fd9\u4e2a\u80fd\u529b\u2014\u2014\u8fd9\u4e2a\u4f60\u662f\u6709\u4eb2\u8eab\u4f53\u9a8c\u7684\uff0c\u4e0d\u662f\u5417\uff1f\u201d</p><p>\u7ef4\u82e5\u62c9\u6c89\u9ed8\u7247\u523b\uff0c\u70b9\u4e86\u70b9\u5934\u3002</p><p>\u201c\u90a3\u8d76\u5feb\u89e3\u5492\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u7684\u957f\u67aa\u5df2\u7ecf\u9965\u6e34\u96be\u8010\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u663e\u7136\u6ca1\u542c\u61c2\u8fd9\u79cd\u51b7\u7b11\u8bdd\uff0c\u5979\u6447\u4e86\u6447\u5934\uff0c\u201c\u73b0\u5728\u8fd8\u4e0d\u884c\u3002\u201d</p><p>\u743c\u6069\u76b1\u7709\uff0c\u201c\u7ef4\u82e5\u62c9\u5c0f\u59d0\uff0c\u6211\u662f\u4e2a\u5f88\u6709\u5951\u7ea6\u7cbe\u795e\u7684\u4eba\u3002\u4e4b\u6240\u4ee5\u6ca1\u6709\u76f4\u63a5\u7528\u5951\u7ea6\u5f3a\u5236\uff0c\u662f\u56e0\u4e3a\u90a3\u592a\u96be\u770b\u4e86\u70b9\u3002\u4f46\u5982\u679c\u4f60\u4e0d\u5b88\u4fe1\u7528\uff0c\u90a3\u6211\u5c31\u53ea\u80fd\u5192\u72af\u4e86\u3002\u201d</p><p>\u201c\u5e76\u4e0d\u662f\u6211\u4e0d\u5b88\u4fe1\u7528\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u6211\u4e5f\u613f\u610f\u4e3a\u4f60\u89e3\u5492\u3002\u6b23\u5e03\u662f\u6211\u7684\u8001\u5e08\uff0c\u6211\u96be\u9053\u4f1a\u770b\u7740\u5979\u9047\u9669\uff1f\u95ee\u9898\u5728\u4e8e\u73b0\u5728\u6ca1\u529e\u6cd5\u89e3\u5492\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u4f60\u7684\u5c01\u9b54\u624b\u73af\u4e0d\u662f\u5df2\u7ecf\u53d6\u6389\u4e86\u5417\uff0c\u4f60\u73b0\u5728\u53ef\u4ee5\u65bd\u6cd5\u4e86\u3002\u201d</p><p>\u201c\u662f\u53ef\u4ee5\uff0c\u4f46\u89e3\u9664\u8fd9\u4e2a\u8bc5\u5492\uff0c\u9700\u8981\u7684\u4e0d\u4ec5\u4ec5\u662f\u5492\u8bed\uff0c\u8fd8\u8981\u6709\u4e00\u4ef6\u65bd\u6cd5\u6750\u6599\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u6211\u6ca1\u6709\u6750\u6599\uff0c\u6240\u4ee5\u6ca1\u529e\u6cd5\u73b0\u5728\u4e3a\u4f60\u89e3\u5492\u3002\u201d</p><p>\u201c\u2026\u2026\u4ec0\u4e48\u6750\u6599\uff1f\u201d</p><p>\u201c\u4e00\u79cd\u7a00\u6709\u91d1\u5c5e\uff0c\u6ca1\u6709\u540d\u5b57\uff0c\u53ea\u6709\u5728\u4e1c\u57df\u624d\u80fd\u627e\u5230\uff0c\u4e2d\u571f\u6839\u672c\u5c31\u6ca1\u6709\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u5fc5\u987b\u8981\u6709\u8fd9\u79cd\u91d1\u5c5e\uff0c\u6211\u624d\u80fd\u4e3a\u4f60\u89e3\u9664\u8bc5\u5492\u3002\u4f60\u53ef\u4ee5\u4e0d\u76f8\u4fe1\u6211\u7684\u8bdd\uff0c\u4f46\u8fd9\u662f\u4e8b\u5b9e\uff0c\u7528\u5951\u7ea6\u5f3a\u5236\u6211\u8bd5\u8bd5\u5c31\u77e5\u9053\u3002\u201d</p><p>\u743c\u6069\u987f\u65f6\u65e0\u8bed\uff0c\u4ed6\u5411\u6765\u81ea\u8be9\u806a\u660e\uff0c\u81f3\u5c11\u5728\u73a9\u5f04\u6587\u5b57\u6e38\u620f\u4e0a\u9887\u6709\u81ea\u4fe1\uff0c\u8c01\u6599\u5230\u5c45\u7136\u4f1a\u88ab\u7ef4\u82e5\u62c9\u8fd9\u79cd\u5916\u884c\u6446\u4e86\u4e00\u9053\u3002\u6ca1\u6709\u6750\u6599\u5c31\u6ca1\u6cd5\u89e3\u5492\uff0c\u8fd9\u5e76\u4e0d\u662f\u7ef4\u82e5\u62c9\u4e0d\u613f\u610f\uff0c\u4e5f\u4e0d\u662f\u5979\u529e\u4e0d\u5230\uff0c\u53ea\u662f\u6ca1\u6709\u6ee1\u8db3\u65bd\u6cd5\u6761\u4ef6\uff0c\u6240\u4ee5\u4e0d\u80fd\u7b97\u5979\u8fdd\u53cd\u5951\u7ea6\u3002\u4e0d\u8fc7\u8f6c\u5ff5\u4e00\u60f3\uff0c\u4ed6\u53c8\u89c9\u5f97\u8fd9\u4ef6\u4e8b\u6709\u4e9b\u8e4a\u8df7\uff0c\u201c\u4e1c\u57df\uff1f\u201d</p><p>\u4ed6\u6b63\u6253\u7b97\u8981\u53bb\u4e1c\u57df\uff0c\u7ef4\u82e5\u62c9\u5c31\u8bf4\u90a3\u79cd\u89e3\u5492\u91d1\u5c5e\u4ea7\u5730\u5728\u4e1c\u57df\uff0c\u8fd9\u4e5f\u672a\u514d\u592a\u5de7\u5408\u4e86\u70b9\u5427\u3002\u8fd9\u4e2a\u201c\u4e13\u60c5\u201d\u8bc5\u5492\uff0c\u96be\u9053\u548c\u4e1c\u57df\u8fd8\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f</p><p>\u201c\u7684\u786e\u662f\u6709\u5173\u7cfb\u7684\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u9a6c\u4f26\u4e2d\u8fd9\u4e2a\u8bc5\u5492\u7684\u65f6\u5019\uff0c\u5c31\u662f\u5728\u4e1c\u57df\u3002\u201d</p><p>\u539f\u6765\u5f53\u5e74\u9a6c\u4f26\u4e3a\u4e86\u8eb2\u907f\u5a1c\u5854\u8389\u838e\uff0c\u7d22\u6027\u8fdc\u79bb\u4e2d\u571f\uff0c\u8dd1\u5230\u6069\u745f\u5f85\u4e86\u5f88\u591a\u5e74\uff0c\u7ed3\u679c\u6700\u7ec8\u8fd8\u662f\u6ca1\u8eb2\u8fc7\uff0c\u88ab\u5a1c\u5854\u8389\u838e\u627e\u5230\uff0c\u4e2d\u4e86\u8fd9\u4e2a\u8bc5\u5492\u3002\u9a6c\u4f26\u540e\u6765\u4ed4\u7ec6\u7814\u7a76\uff0c\u53d1\u73b0\u8fd9\u4e2a\u8bc5\u5492\u5e76\u4e0d\u7eaf\u7cb9\u662f\u4e2d\u571f\u6cd5\u672f\uff0c\u800c\u662f\u6742\u7cc5\u4e86\u67d0\u4e9b\u4e1c\u57df\u7684\u53e4\u8001\u5deb\u672f\u5728\u5176\u4e2d\uff0c\u6240\u4ee5\u5f88\u96be\u7834\u89e3\u3002\u5a1c\u5854\u8389\u838e\u5e76\u4e0d\u662f\u4e1c\u57df\u4eba\uff0c\u5979\u51fa\u751f\u4e8e\u6df1\u6c34\u57ce\uff0c\u5728\u94f6\u6708\u57ce\u4e0a\u5b66\uff0c\u63a5\u53d7\u7684\u662f\u6700\u6b63\u7edf\u7684\u4e2d\u571f\u9b54\u6cd5\u6559\u80b2\uff0c\u4ec0\u4e48\u65f6\u5019\u5b66\u4f1a\u4e86\u4e1c\u57df\u7684\u5deb\u672f\uff0c\u9a6c\u4f26\u4e5f\u4e0d\u5f97\u800c\u77e5\u3002</p><p>\u4e3a\u4e86\u89e3\u9664\u8fd9\u4e2a\u8981\u547d\u7684\u8bc5\u5492\uff0c\u9a6c\u4f26\u82b1\u4e86\u5f88\u5927\u7cbe\u529b\uff0c\u6700\u7ec8\u7814\u7a76\u51fa\u4e86\u7406\u8bba\u4e0a\u7684\u89e3\u5492\u65b9\u6cd5\u2014\u2014\u4ec5\u4ec5\u662f\u7406\u8bba\u4e0a\u7684\uff0c\u56e0\u4e3a\u4ed6\u6839\u672c\u6ca1\u673a\u4f1a\u5b9e\u8df5\uff0c\u5a1c\u5854\u8389\u838e\u5f53\u65f6\u5df2\u7ecf\u53bb\u4e16\uff0c\u8ba9\u8fd9\u4e2a\u8bc5\u5492\u53d8\u6210\u4e86\u89e3\u4e0d\u5f00\u7684\u6b7b\u5492\u3002\u6839\u636e\u9a6c\u4f26\u7684\u5206\u6790\uff0c\u8fd9\u4e2a\u8bc5\u5492\u5728\u53d1\u660e\u51fa\u6765\u7684\u65f6\u5019\uff0c\u501f\u52a9\u4e86\u90a3\u79cd\u7a00\u6709\u91d1\u5c5e\u7684\u529b\u91cf\uff0c\u56e0\u6b64\u82e5\u8981\u89e3\u5f00\u5b83\uff0c\u540c\u6837\u4e5f\u5fc5\u987b\u8981\u6709\u8fd9\u79cd\u91d1\u5c5e\u624d\u80fd\u529e\u5230\u3002</p><p>\u201c\u90a3\u8fd9\u79cd\u91d1\u5c5e\u2014\u2014\u89c1\u9b3c\uff0c\u5b83\u603b\u8be5\u6709\u4e2a\u540d\u5b57\u5427\u2014\u2014\u6709\u4ec0\u4e48\u7279\u70b9\uff1f\u5728\u4e1c\u57df\u7684\u4ec0\u4e48\u5730\u65b9\u80fd\u591f\u627e\u5230\uff1f\u201d</p><p>\u201c\u4e1c\u57df\u4eba\u8ba4\u4e3a\u8fd9\u79cd\u91d1\u5c5e\u4e0a\u9644\u6709\u90aa\u6076\u7684\u8bc5\u5492\uff0c\u4efb\u4f55\u78b0\u89e6\u5b83\u7684\u4eba\u90fd\u4f1a\u906d\u9047\u6a2a\u6b7b\uff0c\u751a\u81f3\u63d0\u53ca\u5b83\u7684\u4eba\u90fd\u4f1a\u906d\u9047\u4e0d\u5e78\uff0c\u6240\u4ee5\u4e5f\u5c31\u6ca1\u6709\u540d\u5b57\u4e86\u3002\u9a6c\u4f26\u7ed9\u5b83\u53d6\u4e86\u4e2a\u4e34\u65f6\u7684\u4ee3\u79f0\uff0c\u662f\u2018\u5deb\u94c1\u2019\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u5deb\u94c1\u5448\u94f6\u767d\u8272\uff0c\u7279\u70b9\u662f\u6781\u8f7b\uff0c\u6bd4\u79d8\u94f6\u8fd8\u8f7b\u5f97\u591a\uff0c\u800c\u4e14\u67d4\u8f6f\u6613\u53d8\u5f62\u3002\u81f3\u4e8e\u5728\u4ec0\u4e48\u5730\u65b9\u80fd\u591f\u627e\u5230\uff0c\u9a6c\u4f26\u4e5f\u4e0d\u6e05\u695a\uff0c\u81f3\u5c11\u4ed6\u7559\u4e0b\u7684\u8bb0\u5fc6\u91cc\u6ca1\u6709\u8fd9\u4e00\u6bb5\uff0c\u53ea\u8bf4\u5728\u4e1c\u57df\u3002\u201d</p><p>\u201c\u90a3\u597d\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u65e2\u7136\u5982\u6b64\uff0c\u5c31\u53ea\u80fd\u8bf7\u4f60\u8ddf\u6211\u53bb\u4e00\u8d9f\u4e1c\u57df\u4e86\u3002\u201d</p><p>\u201c\u597d\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u7b54\u5e94\u5f97\u5e72\u8106\u5229\u843d\uff0c\u5012\u662f\u8ba9\u743c\u6069\u6709\u4e9b\u6000\u7591\u8d77\u6765\uff0c\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u53ea\u8981\u8bc5\u5492\u4e00\u5929\u672a\u89e3\uff0c\u81ea\u5df1\u624b\u4e2d\u638c\u63e1\u5951\u7ea6\uff0c\u5c31\u53ef\u4ee5\u5f3a\u5236\u547d\u4ee4\u5979\uff0c\u4e5f\u4e0d\u6015\u5979\u641e\u4ec0\u4e48\u82b1\u6837\u3002\u53cd\u6b63\u8fd9\u4e00\u8d9f\u975e\u53bb\u4e0d\u53ef\uff0c\u771f\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u4e5f\u5f97\u5148\u505a\u4e86\u624d\u77e5\u9053\u3002</p><p>\u201c\u4f60\u786e\u5b9a\uff0c\u90a3\u79cd\u91d1\u5c5e\u2014\u2014\u5deb\u94c1\u2014\u2014\u53ef\u4ee5\u5728\u4e1c\u57df\u627e\u5230\uff1b\u800c\u53ea\u8981\u627e\u5230\u5deb\u94c1\uff0c\u4f60\u5c31\u53ef\u4ee5\u89e3\u9664\u8bc5\u5492\uff0c\u4e0d\u518d\u9700\u8981\u5176\u4ed6\u6761\u4ef6\u4e86\u5427\uff1f\u201d</p><p>\u201c\u662f\u7684\uff0c\u6211\u786e\u5b9a\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u5deb\u94c1\u53ea\u6709\u5728\u4e1c\u57df\u624d\u80fd\u627e\u5230\uff0c\u627e\u5230\u6211\u5c31\u53ef\u4ee5\u4e3a\u4f60\u89e3\u5492\u3002\u6211\u518d\u8bf4\u4e00\u6b21\uff0c\u6211\u4e0d\u4f1a\u62ff\u6b23\u5e03\u7684\u5b89\u5371\u6765\u5f00\u73a9\u7b11\u3002\u201d</p><p>\u201c\u90a3\u4e48\u6211\u4eec\u8d70\u5427\u3002\u201d</p><p>\u5929\u5927\u5730\u5927\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e8b\u60c5\u6bd4\u81ea\u5df1\u4e00\u8f88\u5b50\u7684\u6027\u798f\u751f\u6d3b\u66f4\u91cd\u8981\u5462\uff0c\u53cd\u6b63\u743c\u6069\u8fd8\u5e74\u8f7b\uff0c\u6b63\u9002\u5408\u4e00\u573a\u8bf4\u8d70\u5c31\u8d70\u7684\u65c5\u884c\u3002</p><p>\u7ef4\u82e5\u62c9\u8131\u6389\u7761\u888d\u6362\u4e0a\u51fa\u95e8\u7684\u8863\u670d\uff0c\u743c\u6069\u6709\u70b9\u4e0d\u653e\u5fc3\uff0c\u6015\u5979\u73a9\u4ec0\u4e48\u82b1\u6837\uff0c\u7559\u5728\u623f\u95f4\u91cc\u76d1\u89c6\uff0c\u5979\u4e5f\u6ca1\u6709\u6297\u62d2\uff0c\u53cd\u6b63\u4e24\u4eba\u867d\u7136\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u611f\u60c5\uff0c\u4f46\u8981\u8bba\u8089\u4f53\u5173\u7cfb\uff0c\u65e9\u5c31\u4eb2\u5bc6\u5f97\u4e0d\u80fd\u518d\u4eb2\u5bc6\u4e86\uff0c\u7ef4\u82e5\u62c9\u7684\u88f8\u4f53\u5df2\u7ecf\u88ab\u743c\u6069\u770b\u8fc7\u65e0\u6570\u6b21\uff0c\u4e5f\u6ca1\u5fc5\u8981\u518d\u5bb3\u7f9e\u3002\u7b49\u5979\u7a7f\u597d\u8863\u670d\uff0c\u743c\u6069\u6b63\u8981\u51fa\u95e8\uff0c\u60f3\u4e86\u60f3\uff0c\u5c06\u838e\u73de\u514b\u6536\u56de\u5b9d\u77f3\u4e4b\u4e2d\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u7ef4\u82e5\u62c9\u5947\u602a\u3002</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u4e00\u7537\u4e00\u5973\u540c\u884c\u5f88\u6b63\u5e38\uff0c\u4e00\u7537\u4e24\u5973\u5c31\u662f\u516c\u5f00\u5438\u5f15\u4ec7\u6068\uff0c\u6211\u8fd9\u4e2a\u4eba\u6bd4\u8f83\u559c\u6b22\u4f4e\u8c03\u4e00\u70b9\u3002\u201d</p><p>\u4e24\u4eba\u540c\u884c\u51fa\u95e8\uff0c\u671d\u9547\u5916\u8d70\u53bb\u3002\u8d70\u51fa\u57ce\u95e8\u4e0d\u8fdc\uff0c\u743c\u6069\u7a81\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u4ed6\u770b\u89c1\u524d\u65b9\u8d70\u6765\u4e00\u4e2a\u8001\u4eba\uff0c\u7a7f\u7740\u7ea2\u8272\u7684\u957f\u888d\uff0c\u6234\u7740\u4e00\u9876\u5c16\u5c16\u7684\u5deb\u5e08\u5e3d\uff0c\u8eab\u6750\u9ad8\u5927\uff0c\u80e1\u987b\u82b1\u767d\uff0c\u5634\u91cc\u53fc\u7740\u4e2a\u70df\u6597\uff0c\u8170\u95f4\u8fd8\u4f69\u7740\u4e00\u67c4\u77ed\u5251\u3002\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u5dee\u70b9\u4ee5\u4e3a\u662f\u5deb\u5e08\u4e4b\u795e\u964d\u4e34\u51e1\u95f4\uff0c\u4ed4\u7ec6\u518d\u770b\uff0c\u89c9\u5f97\u548c\u795e\u50cf\u8fd8\u662f\u9887\u6709\u533a\u522b\u3002\u8fd9\u65f6\u5019\u8eab\u65c1\u7684\u7ef4\u82e5\u62c9\u5df2\u7ecf\u8eac\u8eab\u884c\u793c\uff0c\u201c\u5927\u8d24\u8005\u3002\u201d\u5979\u8bf4\u3002</p><p>\u5927\u9646\u4e0a\u6709\u8d44\u683c\u88ab\u51a0\u4ee5\u201c\u5927\u8d24\u8005\u201d\u5934\u8854\u7684\u5e76\u4e0d\u591a\uff0c\u9634\u5f71\u8c37\u4e2d\u66f4\u662f\u53ea\u6709\u4e00\u4e2a\uff0c\u5c31\u662f\u4f0a\u5c14\u660e\u65af\u7279\uff0c\u9b54\u6cd5\u5973\u795e\u73b0\u5b58\u7684\u9009\u6c11\u4e2d\u8d44\u683c\u6700\u6df1\u3001\u5e74\u9f84\u6700\u957f\u3001\u540d\u58f0\u6700\u54cd\u4eae\u7684\u4eba\u7269\uff0c\u6b23\u5e03\u59d0\u59b9\u7b49\u4eba\u5c0f\u65f6\u5019\u90fd\u662f\u7531\u4ed6\u629a\u517b\u7684\u3002\u743c\u6069\u6ca1\u89c1\u8fc7\u4ed6\uff0c\u4f46\u540d\u5b57\u5982\u96f7\u8d2f\u8033\uff0c\u65e9\u5c31\u542c\u8fc7\u65e0\u6570\u6b21\u4e86\uff0c\u5374\u6ca1\u60f3\u5230\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\u89c1\u9762\u3002</p><p>\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff1f\u201d\u4f0a\u5c14\u660e\u65af\u7279\u53d6\u4e0b\u70df\u6597\uff0c\u8bed\u6c14\u5e73\u6de1\u5730\u95ee\u3002</p><p>\u201c\u662f\u6211\u3002\u201d</p><p>\u743c\u6069\u51dd\u795e\u6212\u5907\u7740\uff0c\u4ed6\u542c\u6885\u83f2\u65af\u8bf4\u8fc7\uff0c\u5728\u6b64\u524d\u90a3\u4e00\u6218\u5f00\u59cb\u65f6\uff0c\u4f0a\u5c14\u660e\u65af\u7279\u6fc0\u53d1\u5b88\u95e8\u4eba\u6c34\u6676\u7684\u529b\u91cf\u51bb\u7ed3\u9886\u57df\uff0c\u8fd9\u5e76\u4e0d\u662f\u8f7b\u677e\u7684\u5de5\u4f5c\uff0c\u5927\u8d24\u8005\u5e74\u4e8b\u5df2\u9ad8\uff0c\u5f88\u53ef\u80fd\u4f1a\u627f\u53d7\u4e0d\u4f4f\u3002\u5728\u540e\u6765\u7684\u6218\u6597\u4e2d\uff0c\u4ece\u59cb\u81f3\u7ec8\uff0c\u5927\u8d24\u8005\u59cb\u7ec8\u6ca1\u6709\u9732\u8fc7\u4e00\u6b21\u9762\uff0c\u8fd9\u4e5f\u5370\u8bc1\u4e86\u6885\u83f2\u65af\u7684\u8bf4\u6cd5\u3002\u743c\u6069\u4e4b\u6240\u4ee5\u6562\u5b64\u8eab\u8fdb\u5165\u9634\u5f71\u9547\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u4e5f\u662f\u4ee5\u4e3a\u4f0a\u5c14\u660e\u65af\u7279\u5df2\u7ecf\u4e27\u5931\u6218\u6597\u529b\uff0c\u4e0d\u8db3\u4e3a\u60e7\u3002\u4f46\u73b0\u5728\u770b\u6765\uff0c\u8fd9\u4f4d\u8001\u4eba\u6b65\u5c65\u7a33\u5b9a\uff0c\u795e\u6001\u4ece\u5bb9\uff0c\u5168\u7136\u4e0d\u50cf\u6709\u4f24\u5728\u8eab\u7684\u6a21\u6837\uff0c\u5b8c\u5168\u51fa\u4e4e\u81ea\u5df1\u7684\u9884\u6599\u3002</p><p>\u8fd9\u6b21\u9ebb\u70e6\u6709\u70b9\u5927\u4e86\u3002</p><p>\u4f0a\u5c14\u660e\u65af\u7279\u70b9\u4e86\u70b9\u5934\uff0c\u5c06\u70df\u6597\u653e\u56de\u53e3\u4e2d\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u5341\u51e0\u9897\u900f\u660e\u7684\u529b\u573a\u98de\u5f39\u4ece\u70df\u6597\u4e2d\u5c04\u51fa\u6765\uff0c\u88ad\u5411\u743c\u6069\u3002\u743c\u6069\u65e9\u6709\u9632\u5907\uff0c\u9f99\u9cde\u76fe\u65e9\u5c31\u98de\u51fa\uff0c\u6321\u5728\u8eab\u524d\uff0c\u540c\u65f6\u53d1\u51fa\u4e86\u4e00\u9053\u95ea\u7535\u3002</p><p>\u90a3\u5e76\u975e\u6cd5\u672f\uff0c\u800c\u662f\u5409\u52d2\u4eca\u7684\u795e\u529b\u51dd\u805a\uff0c\u671d\u7740\u5927\u8d24\u8005\u6fc0\u5c04\u8fc7\u53bb\uff0c\u5728\u8000\u773c\u7684\u95ea\u7535\u4e4b\u4e2d\uff0c\u5374\u6f5c\u4f0f\u7740\u4e00\u9053\u6de1\u6de1\u7684\u9634\u5f71\u3002\u838e\u5c14\u662f\u9690\u79d8\u4e4b\u795e\uff0c\u5f71\u706b\u6700\u64c5\u9690\u85cf\u4f2a\u88c5\uff0c\u743c\u6069\u8fd9\u662f\u5728\u8d4c\u4e00\u628a\uff0c\u4ed6\u81ea\u77e5\u7edd\u975e\u5927\u8d24\u8005\u7684\u5bf9\u624b\uff0c\u53ea\u80fd\u51fa\u5176\u4e0d\u610f\u3002\u94f6\u706b\u4e0e\u5f71\u706b\u4e92\u76f8\u514b\u5236\uff0c\u4f46\u5018\u82e5\u76f4\u63a5\u786c\u78b0\u786c\uff0c\u4f1a\u5f15\u53d1\u5267\u70c8\u7206\u70b8\uff0c\u5018\u82e5\u4f0a\u5c14\u660e\u65af\u7279\u6ca1\u6709\u6ce8\u610f\u5230\u95ea\u7535\u4e2d\u9690\u85cf\u7684\u5f71\u706b\uff0c\u4ee5\u94f6\u706b\u6765\u8fce\u654c\u7684\u8bdd\uff0c\u743c\u6069\u4fbf\u6709\u4e86\u4e58\u673a\u8131\u8eab\u7684\u673a\u4f1a\u3002</p><p>\u7136\u800c\u4ed6\u5931\u671b\u4e86\u3002\u4f0a\u5c14\u660e\u65af\u7279\u53d1\u51fa\u4e86\u4e00\u56e2\u94f6\u706b\uff0c\u5feb\u901f\u5206\u88c2\u5f00\u6765\uff0c\u4eff\u4f5b\u5f20\u5f00\u7684\u7f51\uff0c\u5c06\u95ea\u7535\u4ee5\u53ca\u6f5c\u4f0f\u5728\u5176\u4e2d\u7684\u5f71\u706b\u5168\u90fd\u5305\u88f9\u4f4f\uff0c\u4e09\u79cd\u529b\u91cf\u5728\u5c0f\u8303\u56f4\u5185\u53d1\u751f\u4e86\u51b2\u7a81\uff0c\u4f46\u5f88\u5feb\u5c31\u4e00\u8d77\u6d88\u5931\u4e86\uff0c\u4eff\u4f5b\u88ab\u9001\u5230\u4e86\u53e6\u4e00\u4e2a\u7a7a\u95f4\u3002\u743c\u6069\u8d81\u8fd9\u4e2a\u673a\u4f1a\u4ece\u300a\u547d\u8fd0\u957f\u591c\u300b\u4e2d\u53d6\u51fa\u4e86\u4e00\u53ea\u9ed1\u8272\u6728\u9e1f\uff0c\u629b\u5728\u7a7a\u4e2d\u3002\u6728\u9e1f\u6025\u901f\u53d8\u5927\uff0c\u6293\u8d77\u743c\u6069\uff0c\u773c\u770b\u5c31\u8981\u632f\u7fc5\u98de\u53bb\u3002</p><p>\u4e00\u9053\u94f6\u8272\u5f27\u5149\u5982\u5229\u5203\u822c\u7834\u7a7a\u63a0\u6765\uff0c\u65a9\u5411\u743c\u6069\u548c\u6728\u9e1f\uff0c\u5b83\u6765\u52bf\u6781\u5feb\uff0c\u743c\u6069\u5df2\u7ecf\u6765\u4e0d\u53ca\u53d1\u51fa\u5f71\u706b\u963b\u62e6\u2014\u2014\u743c\u6069\u5e76\u4e0d\u662f\u771f\u6b63\u7684\u838e\u5c14\u9009\u6c11\uff0c\u4ed6\u4e4b\u6240\u4ee5\u80fd\u4f7f\u7528\u5f71\u706b\uff0c\u662f\u501f\u52a9\u838e\u5c14\u7ed9\u4ed6\u7684\u62a4\u7b26\uff0c\u4e2d\u95f4\u9694\u4e86\u8fd9\u4e00\u5c42\uff0c\u672a\u514d\u5c31\u4e0d\u591f\u5f97\u5fc3\u5e94\u624b\uff0c\u5e73\u5e38\u65e0\u6240\u8c13\uff0c\u771f\u6b63\u5230\u4e86\u751f\u6b7b\u6538\u5173\u7684\u65f6\u523b\uff0c\u5f80\u5f80\u4e5f\u5c31\u5dee\u4e86\u8fd9\u4e00\u77ac\u3002\u743c\u6069\u4e5f\u7b97\u8eab\u7ecf\u767e\u6218\uff0c\u51b3\u65ad\u6781\u5feb\uff0c\u547d\u4ee4\u6728\u9e1f\u5c06\u4ed6\u8fdc\u8fdc\u63b7\u51fa\u53bb\uff0c\u582a\u582a\u907f\u8fc7\u65a9\u51fb\u3002\u4ed6\u72fc\u72c8\u5730\u5728\u5730\u9762\u4e0a\u6eda\u4e86\u51e0\u5708\uff0c\u7ffb\u8eab\u722c\u8d77\uff0c\u5c31\u89c1\u6728\u9e1f\u5df2\u7ecf\u88ab\u5f27\u5149\u780d\u6210\u4e86\u4e24\u622a\u3002</p><p>\u4f0a\u5c14\u660e\u65af\u7279\u62d4\u51fa\u8170\u95f4\u7684\u77ed\u5251\uff0c\u4e0d\u7d27\u4e0d\u6162\u5730\u671d\u7740\u743c\u6069\u8d70\u8fc7\u6765\uff0c\u4ed6\u4e00\u8fb9\u9760\u8fd1\uff0c\u4e00\u8fb9\u6325\u52a8\u77ed\u5251\uff0c\u4e00\u9053\u53c8\u4e00\u9053\u8d64\u7ea2\u8272\u7684\u706b\u7130\u4ece\u5251\u5203\u4e2d\u63a5\u8fde\u53d1\u51fa\uff0c\u671d\u7740\u743c\u6069\u5c04\u53bb\u3002\u743c\u6069\u6b63\u5f85\u62b5\u6321\uff0c\u4e00\u76f4\u9759\u9759\u5f85\u5728\u65c1\u8fb9\u7684\u7ef4\u82e5\u62c9\u7a81\u7136\u52a8\u4e86\u8d77\u6765\uff0c\u5979\u53cc\u624b\u4ea4\u9519\uff0c\u5341\u6307\u98de\u821e\u53d8\u5e7b\uff0c\u540c\u65f6\u52fe\u52d2\u51fa\u4e94\u4e2a\u7b26\u6587\uff0c\u7ec4\u5408\u6210\u4e00\u9762\u5de8\u5927\u7684\u5149\u76fe\u8986\u76d6\u5728\u743c\u6069\u8eab\u4e0a\uff0c\u582a\u582a\u6321\u4f4f\u4e86\u706b\u7130\u5200\u7684\u65a9\u51fb\u3002\u743c\u6069\u4e00\u6014\u4e4b\u4e0b\uff0c\u7ef4\u82e5\u62c9\u5df2\u7ecf\u77ac\u79fb\u5230\u4ed6\u8eab\u65c1\uff0c\u4f38\u624b\u6293\u4f4f\u4ed6\u7684\u80f3\u818a\uff0c\u540e\u9000\u534a\u6b65\uff0c\u94f6\u8272\u7684\u4f20\u9001\u95e8\u5728\u80cc\u540e\u77ac\u95f4\u6210\u578b\u3002\u4f0a\u5c14\u660e\u65af\u7279\u53d1\u51fa\u4e00\u9053\u6b21\u5143\u951a\uff0c\u60f3\u8981\u5b9a\u4f4f\u4f20\u9001\u95e8\uff0c\u4f46\u5df2\u7ecf\u665a\u4e86\u4e00\u6b65\uff0c\u743c\u6069\u548c\u7ef4\u82e5\u62c9\u5df2\u7ecf\u6d88\u5931\u4e0d\u89c1\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u5530\uff01</p><p>\u539f\u672c\u5e73\u9759\u7684\u7a7a\u95f4\u88ab\u731b\u7136\u6495\u5f00\uff0c\u9732\u51fa\u9ed1\u8272\u7684\u692d\u5706\u5f62\u88c2\u9699\uff0c\u7d27\u63a5\u7740\u4e24\u4e2a\u4eba\u4ece\u4e2d\u6389\u51fa\u6765\u3002\u743c\u6069\u534a\u8e72\u7740\uff0c\u624b\u638c\u6309\u5730\uff0c\u505a\u4e86\u4e24\u4e2a\u6df1\u547c\u5438\uff0c\u65b9\u624d\u5c06\u8111\u4e2d\u7684\u6655\u7729\u611f\u9a71\u9664\uff0c\u7136\u540e\u6162\u6162\u7ad9\u8d77\u8eab\u6765\uff0c\u6253\u91cf\u56db\u5468\u3002</p><p>\u7ef4\u82e5\u62c9\u7684\u9b54\u6cd5\u9020\u8be3\u6bd4\u743c\u6069\u786e\u5b9e\u8981\u9ad8\u660e\u4e00\u4e9b\uff0c\u540c\u6837\u662f\u4f20\u9001\u672f\uff0c\u5728\u5979\u624b\u4e2d\u65bd\u5c55\u51fa\u6765\uff0c\u53d1\u52a8\u901f\u5ea6\u660e\u663e\u66f4\u5feb\uff0c\u800c\u4e14\u4f3c\u4e4e\u8fd8\u9644\u5e26\u4e86\u4e00\u4e9b\u5e72\u6270\u9b54\u7f51\u7684\u6548\u679c\uff0c\u9632\u6b62\u654c\u4eba\u5faa\u8ff9\u8ffd\u8e2a\u3002\u4f46\u4f20\u9001\u7684\u8ddd\u79bb\u5e94\u8be5\u5e76\u4e0d\u7b97\u592a\u957f\uff0c\u743c\u6069\u867d\u7136\u8fa8\u8ba4\u4e0d\u51fa\u81ea\u5df1\u6240\u5728\u7684\u65b9\u4f4d\uff0c\u4f46\u4ece\u5730\u5f62\u548c\u690d\u88ab\u5224\u65ad\uff0c\u5e94\u8be5\u8fd8\u662f\u9634\u5f71\u8c37\u9644\u8fd1\uff0c\u5e76\u672a\u8fdc\u79bb\u3002</p><p>\u201c\u4f60\u4e3a\u4ec0\u4e48\u8981\u6551\u6211\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u4ed6\u6ca1\u60f3\u5230\u6700\u540e\u5173\u5934\uff0c\u5c45\u7136\u662f\u7ef4\u82e5\u62c9\u4e3b\u52a8\u51fa\u624b\u63f4\u6551\uff0c\u8fd9\u5e76\u975e\u4ed6\u7684\u547d\u4ee4\uff0c\u5f53\u65f6\u60c5\u51b5\u7d27\u6025\uff0c\u4ed6\u5168\u795e\u8d2f\u6ce8\u5bf9\u6297\u5f3a\u654c\uff0c\u538b\u6839\u5c31\u6ca1\u6709\u7a7a\u6687\u5bf9\u7ef4\u82e5\u62c9\u4e0b\u8fbe\u6307\u793a\u3002\u6240\u4ee5\u743c\u6069\u5f88\u5947\u602a\uff0c\u4ed6\u548c\u7ef4\u82e5\u62c9\u7684\u5173\u7cfb\u6ca1\u8fd9\u4e48\u597d\u5427\u3002</p><p>\u201c\u6211\u8bf4\u4e86\uff0c\u6211\u8981\u4f60\u5e2e\u5fd9\u6551\u6b23\u5e03\u3002\u201d\u7ef4\u82e5\u62c9\u56de\u7b54\u3002</p><p>\u201c\u4ec5\u4ec5\u53ea\u662f\u8fd9\u4e2a\u539f\u56e0\u5417\uff1f\u201d</p><p>\u743c\u6069\u9887\u4e3a\u6000\u7591\u3002\u5766\u767d\u5730\u8bf4\uff0c\u94f6\u706b\u5931\u63a7\u7684\u95ee\u9898\uff0c\u672a\u5fc5\u771f\u7684\u53ea\u6709\u4ed6\u4e00\u4eba\u53ef\u4ee5\u89e3\u51b3\uff0c\u6bd5\u7adf\u9b54\u6cd5\u5973\u795e\u53c8\u6ca1\u6b7b\uff0c\u53ef\u80fd\u5979\u6682\u65f6\u53d7\u521b\uff0c\u65e0\u6687\u987e\u53ca\uff0c\u4f46\u5047\u4ee5\u65f6\u65e5\u603b\u80fd\u6062\u590d\uff0c\u5230\u65f6\u5019\u5c06\u9009\u6c11\u4eec\u7684\u94f6\u706b\u6536\u56de\u6765\u5c31\u662f\u3002\u76f8\u6bd4\u8d77\u6765\uff0c\u743c\u6069\u7684\u201c\u6cbb\u7597\u201d\u65b9\u6cd5\u5374\u6709\u5f88\u5927\u7684\u201c\u526f\u4f5c\u7528\u201d\u3002\u7ef4\u82e5\u62c9\u53ea\u662f\u62ff\u8fd9\u4e2a\u505a\u7406\u7531\uff0c\u5b9e\u5728\u6709\u70b9\u7275\u5f3a\u3002</p><p>\u5728\u743c\u6069\u770b\u6765\uff0c\u7ef4\u82e5\u62c9\u5f53\u65f6\u5b8c\u5168\u53ef\u4ee5\u6709\u53e6\u4e00\u79cd\u9009\u62e9\uff1a\u534f\u52a9\u5927\u8d24\u8005\u51fb\u6740\u743c\u6069\uff0c\u6551\u51fa\u6b23\u5e03\uff0c\u6216\u8005\u4f3a\u673a\u62a2\u4e0b\u300a\u547d\u8fd0\u957f\u591c\u300b\u3002\u5f53\u7136\u4e86\uff0c\u8fd9\u4e48\u505a\u80fd\u5426\u6210\u529f\u4e5f\u4e0d\u597d\u8bf4\uff0c\u6709\u53ef\u80fd\u6740\u4e0d\u6b7b\u743c\u6069\uff0c\u6216\u8005\u62a2\u4e0d\u5230\u4e66\uff0c\u8fd8\u662f\u8ba9\u4ed6\u9003\u6389\uff0c\u6709\u53ef\u80fd\u62ff\u5230\u4e66\u540e\u6ca1\u529e\u6cd5\u628a\u6b23\u5e03\u4ece\u4e2d\u6551\u51fa\uff0c\u6bd5\u7adf\u90a3\u662f\u4f0a\u739b\u65af\u5361\u7684\u9020\u7269\uff0c\u5c3d\u7ba1\u4e0d\u662f\u8840\u8109\u9650\u5b9a\u7684\u6b63\u724c\u79d8\u5668\uff0c\u4f46\u4e0e\u73b0\u4eca\u7684\u9b54\u6cd5\u5b66\u4f53\u7cfb\u4e5f\u662f\u5927\u76f8\u8fe5\u5f02\u7684\u3002\u4f46\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u603b\u6bd4\u53cd\u8fc7\u6765\u5e2e\u52a9\u743c\u6069\uff0c\u5c06\u6765\u773c\u7741\u7741\u5730\u770b\u7740\u6b23\u5e03\u88ab\u4ed6\u6b3a\u8d1f\uff0c\u8981\u5408\u60c5\u5408\u7406\u4e00\u4e9b\u5427\u3002</p><p>\u7ef4\u82e5\u62c9\u4e0d\u8bf4\u8bdd\u3002</p><p>\u201c\u8fd9\u8fd8\u4e0d\u7b80\u5355\uff0c\u201d\u8bf4\u8bdd\u7684\u662f\u838e\u73de\u514b\uff0c\u5979\u4ece\u5b9d\u77f3\u4e2d\u653e\u51fa\u6765\uff0c\u5f04\u6e05\u695a\u4e8b\u60c5\u7ecf\u8fc7\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u5f53\u7136\u662f\u4e3b\u4eba\u4f60\u7684\u5927\u5bb6\u4f19\u6bd4\u8f83\u5389\u5bb3\uff0c\u5979\u88ab\u4f60\u5b8c\u5168\u5f81\u670d\uff0c\u8eab\u5fc3\u6ca6\u9677\uff0c\u65e5\u4e45\u751f\u60c5\uff0c\u56e0\u5978\u6210\u7231\uff0c\u4ece\u6b64\u82b3\u5fc3\u6697\u8bb8\uff0c\u975e\u4f60\u4e0d\u5ac1\u3002\u4f60\u8fde\u8fd9\u4e2a\u90fd\u770b\u4e0d\u51fa\u6765\uff0c\u8fd8\u975e\u8981\u95ee\uff0c\u771f\u662f\u4e0d\u89e3\u98ce\u60c5\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u771f\u7684\u6ca1\u6709\u4f60\u8bf4\u7684\u90a3\u4e48\u5389\u5bb3\u3002\u201d</p><p>\u743c\u6069\u5c31\u7b97\u518d\u81ea\u604b\uff0c\u8fd9\u70b9\u57fa\u672c\u7684\u81ea\u77e5\u4e4b\u660e\u8fd8\u662f\u6709\u7684\uff0c\u4f46\u7ef4\u82e5\u62c9\u7684\u60c5\u51b5\u4e5f\u7684\u786e\u6bd4\u8f83\u5947\u602a\uff0c\u4e0d\u641e\u6e05\u695a\u7684\u8bdd\uff0c\u603b\u662f\u5fc3\u4e2d\u96be\u5b89\u3002\u201c\u5230\u5e95\u600e\u4e48\u56de\u4e8b\uff0c\u7ef4\u82e5\u62c9\u5c0f\u59d0\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u4f60\u77e5\u9053\uff0c\u5373\u4fbf\u4f60\u62d2\u7edd\u56de\u7b54\uff0c\u6211\u4e5f\u4ecd\u7136\u53ef\u4ee5\u5f3a\u5236\u4f60\u8bf4\u51fa\u771f\u76f8\u3002\u6240\u4ee5\u6211\u4eec\u5c31\u6ca1\u5fc5\u8981\u6d6a\u8d39\u65f6\u95f4\u4e86\u5427\u3002\u201d</p><p>\u201c\u6211\u8bf4\u7684\u662f\u4e8b\u5b9e\uff0c\u786e\u5b9e\u662f\u4e3a\u4e86\u6b23\u5e03\uff0c\u201d\u7ef4\u82e5\u62c9\u4e0d\u5f97\u4e0d\u8fa9\u89e3\uff0c\u201c\u5979\u4f53\u5185\u94f6\u706b\u5931\u63a7\uff0c\u53c8\u6ca1\u6709\u53ca\u65f6\u5904\u7406\uff0c\u5df2\u7ecf\u803d\u8bef\u4e86\u592a\u4e45\u3002\u73b0\u5728\u5168\u9760\u4f60\u8fd9\u672c\u4e66\u9694\u7edd\u7a7a\u95f4\uff0c\u963b\u65ad\u5979\u548c\u5916\u754c\u7684\u8054\u7cfb\uff0c\u6240\u4ee5\u6682\u65f6\u65e0\u4e8b\u3002\u53ea\u8981\u5979\u4e00\u79bb\u5f00\u8fd9\u672c\u4e66\uff0c\u7acb\u523b\u5c31\u4f1a\u6709\u5371\u9669\u3002\u201d</p><p>\u8fd9\u4e00\u70b9\u743c\u6069\u5012\u8fd8\u771f\u4e0d\u77e5\u9053\uff0c\u4f30\u8ba1\u7ef4\u82e5\u62c9\u4e5f\u6ca1\u5fc5\u8981\u6492\u8fd9\u79cd\u8c0e\u3002\u4ed6\u521a\u624d\u8fd8\u60f3\u8981\u4e0d\u8981\u627e\u4e2a\u4ec0\u4e48\u673a\u4f1a\uff0c\u6084\u6084\u628a\u6b23\u5e03\u4e22\u7ed9\u9b54\u6cd5\u5973\u795e\u7684\u6559\u4f1a\u7b97\u4e86\uff0c\u65e2\u7136\u81ea\u5df1\u7684\u8bc5\u5492\u77ed\u65f6\u95f4\u5185\u89e3\u4e0d\u5f00\uff0c\u8fd8\u5f97\u53bb\u4e1c\u57df\u624d\u6709\u5e0c\u671b\uff0c\u90a3\u5c31\u6ca1\u5fc5\u8981\u5343\u91cc\u8fe2\u8fe2\u5e26\u7740\u5979\uff0c\u53cd\u800c\u5bb3\u4e86\u4eba\u5bb6\u3002\u73b0\u5728\u770b\u6765\uff0c\u4e0d\u5e26\u7740\u5979\u8fd8\u4e0d\u884c\u4e86\u3002</p><p>\u7b97\u4e86\uff0c\u5e26\u7740\u5c31\u5e26\u7740\u5427\uff0c\u53cd\u6b63\u53c8\u4e0d\u5360\u5730\u65b9\uff0c\u4e22\u8fdb\u4e66\u91cc\u4ec0\u4e48\u90fd\u4e0d\u7528\u7ba1\uff0c\u7701\u5fc3\u7701\u529b\u3002</p><p>\u201c\u8fd9\u4e2a\u89e3\u91ca\u662f\u8bf4\u5f97\u8fc7\u53bb\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u6211\u89c9\u5f97\u4f60\u4e00\u5b9a\u8fd8\u6709\u522b\u7684\u7406\u7531\u5427\uff1f\u5426\u5219\u7684\u8bdd\uff0c\u4f60\u81f3\u5c11\u53ef\u4ee5\u4e58\u673a\u9003\u8dd1\u3002\u201d</p><p>\u201c\u795e\u7684\u8c15\u793a\u3002\u201d\u7ef4\u82e5\u62c9\u72b9\u8c6b\u4e86\u7247\u523b\uff0c\u5f88\u4e0d\u60c5\u613f\u5730\u8bf4\u3002</p><p>\u201c\u795e\u8c15\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u6b64\u524d\u7b2c\u4e94\u79d8\u5668\u9886\u57df\u5d29\u6e83\uff0c\u6240\u6709\u4eba\u91cd\u8fd4\u7269\u8d28\u754c\u3002\u4ea4\u6218\u53cc\u65b9\u4e4b\u4e2d\uff0c\u9f99\u5deb\u6559\u4f24\u4ea1\u5f88\u91cd\uff0c\u4f46\u8fd8\u6709\u4e00\u4e9b\u6b8b\u4f59\uff0c\u4ed6\u4eec\u5206\u6563\u9041\u9003\uff0c\u4e0d\u77e5\u6240\u8e2a\uff1b\u9b54\u6cd5\u5973\u795e\u6559\u4f1a\u8fd9\u8fb9\u635f\u5931\u66f4\u91cd\uff0c\u9886\u8896\u4eba\u7269\u6216\u6b7b\u6216\u5e9f\uff0c\u7fa4\u9f99\u65e0\u9996\uff0c\u53cd\u5012\u662f\u7ef4\u82e5\u62c9\u72b6\u51b5\u6700\u597d\uff0c\u5979\u548c\u54c8\u8d1d\u5c14\u4e00\u8d77\u6536\u62e2\u90e8\u4e0b\uff0c\u96c6\u4f53\u9000\u56de\u9634\u5f71\u9547\u4e2d\u4f11\u6574\u3002\u5728\u56de\u7a0b\u9014\u4e2d\uff0c\u5979\u7a81\u7136\u5fc3\u6709\u6240\u611f\uff0c\u56de\u5230\u9547\u4e2d\u540e\u4fbf\u7b2c\u4e00\u65f6\u95f4\u53bb\u4e86\u795e\u6bbf\uff0c\u8bf7\u6c42\u795e\u7957\u6307\u70b9\u8ff7\u6d25\uff0c\u662d\u793a\u672a\u6765\u3002</p><p>\u5deb\u5e08\u4e4b\u795e\u56de\u5e94\u4e86\u5979\u7684\u7948\u7977\uff0c\u5374\u7ed9\u4e86\u4e00\u4e2a\u5947\u602a\u7684\u795e\u8c15\uff0c\u8981\u5979\u201c\u53bb\u4e1c\u65b9\u201d\u3002\u7ef4\u82e5\u62c9\u83ab\u540d\u5176\u5999\uff0c\u665a\u4e0a\u518d\u6b21\u7948\u7977\u2014\u2014\u5c31\u662f\u743c\u6069\u770b\u89c1\u7684\u90a3\u6b21\u2014\u2014\u7136\u540e\u83b7\u5f97\u4e86\u66f4\u6e05\u6670\u7684\u6307\u793a\u3002</p><p>\u201c\u795e\u8bf4\uff0c\u6211\u5e94\u8be5\u8ddf\u968f\u4f60\u53bb\u4e1c\u65b9\uff0c\u6211\u7684\u68a6\u60f3\u5c06\u4f1a\u5728\u90a3\u91cc\u8d77\u6b65\uff0c\u5e76\u4e14\u6700\u7ec8\u5b8c\u6210\uff0c\u201d\u7ef4\u82e5\u62c9\u8f6c\u8ff0\u795e\u8c15\uff0c\u201c\u6240\u4ee5\u6211\u4e0d\u80fd\u770b\u7740\u4f60\u6b7b\u2014\u2014\u8981\u6b7b\u4e5f\u7b49\u5230\u4e86\u4e1c\u65b9\u518d\u6b7b\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4f60\u653e\u5fc3\uff0c\u4eba\u751f\u5982\u6b64\u7f8e\u597d\uff0c\u6211\u8fd8\u6ca1\u4eab\u53d7\u591f\uff0c\u4e0d\u4f1a\u968f\u4fbf\u6b7b\u7684\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u7684\u68a6\u60f3\u662f\u4ec0\u4e48\uff0c\u743c\u6069\u5012\u4e0d\u662f\u5f88\u6e05\u695a\uff0c\u4ed6\u6b63\u8981\u7ee7\u7eed\u8be2\u95ee\uff0c\u5ffd\u7136\u4e66\u9875\u4e2d\u6cdb\u51fa\u5fae\u5149\uff0c\u7d27\u63a5\u7740\u4e00\u4e2a\u6d3b\u529b\u56db\u5c04\u7684\u8eab\u5f71\u8df3\u51fa\u6765\u3002\u201c\u665a\u4e0a\u597d\uff0c\u743c\u6069\uff0c\u201d\u51db\u6253\u62db\u547c\uff0c\u201c\u54a6\uff0c\u8fd9\u4f4d\u662f\u2026\u2026\u201d</p><p>\u201c\u54e6\uff0c\u6211\u6765\u4ecb\u7ecd\u4e00\u4e0b\uff0c\u51db\uff0c\u8fd9\u4f4d\u662f\u4f60\u7684\u5e08\u59d0\uff0c\u5854\u62c9\u590f\xb7\u7ef4\u82e5\u62c9\u3002\u201d</p><p>\u201c\u4f60\u597d\uff0c\u201d\u51db\u9ad8\u5174\u5730\u8bf4\uff0c\u201c\u539f\u6765\u4f60\u5c31\u662f\u5854\u62c9\u590f\u59d0\u59d0\u554a\uff0c\u6211\u542c\u8001\u5e08\u63d0\u8fc7\u4f60\u5f88\u591a\u6b21\u5462\uff0c\u8bf4\u4f60\u6700\u806a\u660e\u4e86\uff0c\u51fa\u95e8\u4ece\u6765\u4e0d\u4f1a\u8ff7\u8def\uff0c\u4e70\u4e1c\u897f\u8fd8\u4f1a\u780d\u4ef7\uff0c\u5389\u5bb3\u6781\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u4eec\u5e08\u5f92\u4fe9\u5c31\u662f\u8fd9\u4e48\u5b9a\u4e49\u2018\u806a\u660e\u2019\u7684\u5417\uff1f\u201d</p><p>\u201c\u96be\u9053\u4f60\u4f1a\u780d\u4ef7\uff1f\u201d\u51db\u53cd\u95ee\u3002</p><p>\u201c\u4e0d\u4f1a\u3002\u201d\u743c\u6069\u8001\u8001\u5b9e\u5b9e\u5730\u627f\u8ba4\u3002</p><p>\u201c\u90a3\u4e0d\u5c31\u662f\u4e86\uff0c\u81ea\u5df1\u505a\u4e0d\u5230\u8fd8\u5ac9\u5992\u522b\u4eba\uff0c\u771f\u5c0f\u6c14\uff0c\u201d\u51db\u4e0d\u5c51\u5730\u6241\u6241\u5634\uff0c\u201c\u522b\u7406\u4ed6\uff0c\u4ed6\u4e00\u5411\u8fd9\u4e48\u6ca1\u773c\u5149\u3002\u5854\u59d0\u59d0\uff0c\u4f60\u600e\u4e48\u5728\u8fd9\u91cc\u554a\uff0c\u662f\u4e0d\u662f\u6765\u89c1\u8001\u5e08\u3002\u201d</p><p>\u201c\u5443\uff0c\u5176\u5b9e\u6211\u2026\u2026\u201d\u7ef4\u82e5\u62c9\u6b32\u5f85\u89e3\u91ca\uff0c\u5374\u53c8\u4e0d\u77e5\u9053\u4ece\u4f55\u8bf4\u8d77\uff0c\u5f88\u591a\u4e1c\u897f\u66f4\u662f\u7f9e\u4e8e\u542f\u9f7f\uff0c\u6ca1\u529e\u6cd5\u8bf4\u51fa\u53e3\u3002\u89c1\u5979\u72af\u96be\uff0c\u743c\u6069\u4f5c\u4e3a\u4e00\u4e2a\u6709\u7ec5\u58eb\u98ce\u5ea6\u7684\u7537\u6027\uff0c\u81ea\u7136\u8981\u4e49\u4e0d\u5bb9\u8f9e\u5730\u66ff\u5979\u89e3\u56f4\uff0c\u201c\u5176\u5b9e\u662f\u8fd9\u6837\uff0c\u201d\u4ed6\u4e00\u672c\u6b63\u7ecf\u5730\u5bf9\u51db\u8bf4\uff0c\u201c\u5854\u62c9\u590f\u8981\u548c\u6211\u4eec\u4e00\u8d77\u53bb\u4e1c\u57df\u65c5\u884c\u3002\u6b23\u5e03\u5973\u58eb\u5979\u521a\u624d\u5df2\u7ecf\u89c1\u8fc7\u4e86\u3002\u201d</p><p>\u201c\u53bb\u4e1c\u57df\u5e72\u561b\uff1f\u201d\u51db\u5947\u602a\u5730\u95ee\uff0c\u201c\u55ef\uff1f\u201d\u5979\u7a81\u7136\u53d1\u89c9\u4e86\u4ec0\u4e48\uff0c\u201c\u4f60\u600e\u4e48\u76f4\u63a5\u53eb\u5979\u7684\u540d\u5b57\uff1f\u201d</p><p>\u201c\u521a\u624d\u8fd8\u6ca1\u4ecb\u7ecd\u5b8c\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5854\u62c9\u590f\u4e0d\u4ec5\u662f\u4f60\u7684\u5e08\u59d0\uff0c\u8fd8\u662f\u4f60\u7684\u59b9\u59b9\uff0c\u5979\u5df2\u7ecf\u7b54\u5e94\u505a\u6211\u7684\u5973\u53cb\u4e86\u3002\u201d</p><p>\u51db\u7684\u773c\u775b\u90fd\u77aa\u5706\u4e86\uff0c\u201c\u4f60\u8bf4\u4ec0\u4e48\uff1f\u4f60\u8111\u888b\u6ca1\u574f\u5427\uff1f\u201d\u5979\u8f6c\u8fc7\u5934\u770b\u7740\u7ef4\u82e5\u62c9\uff0c\u201c\u5854\u59d0\u59d0\uff0c\u4ed6\u8bf4\u7684\u662f\u771f\u7684\uff1f\u201d</p><p>\u201c\u6839\u672c\u6ca1\u8fd9\u56de\u4e8b\uff0c\u201d\u7ef4\u82e5\u62c9\u7684\u8138\u988a\u901a\u7ea2\uff0c\u201c\u4ed6\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u624d\u6ca1\u6709\u7b54\u5e94\u505a\u4ed6\u5973\u53cb\u4ec0\u4e48\u7684\u3002\u201d</p><p>\u201c\u6ca1\u6709\u5417\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90a3\u6211\u4eec\u671d\u5915\u76f8\u5904\u7684\u90a3\u4e5d\u5929\u4e5d\u591c\u7b97\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u2026\u2026\u671d\u5915\u76f8\u5904\u2026\u2026\u4e5d\u5929\u4e5d\u591c\uff1f\u201d\u51db\u5403\u60ca\u5730\u6342\u4f4f\u5634\uff0c\u201c\u4f60\u4eec\u5c45\u7136\u5df2\u7ecf\u2026\u2026\u592a\u8fc7\u5206\u4e86\uff0c\u6211\u4e00\u5b9a\u8981\u544a\u8bc9\u827e\u5f25\u8587\uff0c\u8ba9\u5979\u597d\u597d\u6559\u8bad\u4f60\uff01\u201d</p><p>\u201c\u6ca1\u4e8b\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53cd\u6b63\u5979\u4e5f\u5dee\u4e0d\u591a\u5e94\u8be5\u4e60\u60ef\u4e86\u3002\u201d</p><p>\u201c\u6211\u53ef\u8fd8\u6ca1\u4e60\u60ef\uff01\u201d\u51db\u5927\u58f0\u6297\u8bae\uff0c\u201c\u4f60\u90fd\u6ca1\u6709\u5148\u5f81\u6c42\u6211\u7684\u610f\u89c1\uff01\u201d</p><p>\u201c\u4f60\u53c8\u4e0d\u662f\u6211\u5973\u53cb\uff0c\u6211\u4e3a\u4ec0\u4e48\u8981\u5148\u5f81\u6c42\u4f60\u7684\u610f\u89c1\uff1f\u201d</p><p>\u201c\u8c01\u8bf4\u6211\u4e0d\u662f\u7684\uff1f\u4eba\u5bb6\u628a\u5c41\u80a1\u90fd\u8ba9\u4f60\u73a9\u4e86\uff0c\u4f60\u5c45\u7136\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u201d\u51db\u6ceb\u7136\u6b32\u6ce3\uff0c\u201c\u771f\u662f\u592a\u7edd\u60c5\u4e86\u3002\u5854\u59d0\u59d0\uff0c\u4f60\u770b\uff0c\u8fd9\u5bb6\u4f19\u5c31\u662f\u8fd9\u4e48\u8d1f\u5fc3\u8584\u60c5\uff0c\u4f60\u5343\u4e07\u4e0d\u8981\u88ab\u4ed6\u7684\u751c\u8a00\u871c\u8bed\u6b3a\u9a97\uff0c\u8d76\u5feb\u60ac\u5d16\u52d2\u9a6c\u8fd8\u6765\u5f97\u53ca\u3002\u201d</p><p>\u2026\u2026\u751c\u8a00\u871c\u8bed\u4ec0\u4e48\u7684\uff0c\u6211\u8fd8\u771f\u6ca1\u6709\uff0c\u76f4\u63a5\u5c31\u4e0a\u4e86\u3002</p><p>\u95f9\u817e\u4e00\u901a\uff0c\u51db\u603b\u7b97\u662f\u52c9\u52c9\u5f3a\u5f3a\u63a5\u53d7\u53c8\u591a\u4e86\u4e00\u4e2a\u201c\u59d0\u59b9\u201d\u7684\u4e8b\u5b9e\uff0c\u800c\u7ef4\u82e5\u62c9\u5728\u72b9\u8c6b\u4e00\u756a\u4e4b\u540e\u4e5f\u4e0d\u518d\u53cd\u5bf9\uff0c\u7b97\u662f\u9ed8\u8ba4\u743c\u6069\u7684\u8bf4\u6cd5\u3002\u6bd5\u7adf\u8fd9\u6837\u89e3\u51b3\u95ee\u9898\uff0c\u4e5f\u7b97\u662f\u5f7c\u6b64\u90fd\u80fd\u4e0b\u5f97\u4e86\u53f0\u9636\uff0c\u603b\u6bd4\u8bf4\u51fa\u5b9e\u60c5\u8981\u597d\uff0c\u6709\u4e9b\u4e8b\u60c5\uff0c\u743c\u6069\u56fa\u7136\u4e0d\u5e0c\u671b\u88ab\u522b\u4eba\u77e5\u9053\uff0c\u7ef4\u82e5\u62c9\u53c8\u4f55\u5c1d\u559c\u6b22\u516c\u8bf8\u4e8e\u4f17\u3002\u5173\u952e\u662f\u6ca1\u6709\u4ec0\u4e48\u5b9e\u9645\u5f71\u54cd\uff0c\u65e0\u8bba\u662f\u5426\u5bf9\u5916\u5ba3\u79f0\u5979\u662f\u743c\u6069\u7684\u5973\u53cb\uff0c\u8be5\u505a\u7684\u8fd8\u662f\u4f1a\u505a\uff0c\u8be5\u53d1\u751f\u7684\u8fd8\u662f\u4f1a\u53d1\u751f\uff0c\u743c\u6069\u73b0\u5728\u8bc5\u5492\u672a\u89e3\uff0c\u504f\u504f\u8eab\u8fb9\u7f8e\u4eba\u73af\u7ed5\uff0c\u706b\u6c14\u65fa\u76db\uff0c\u552f\u4e00\u80fd\u4e0b\u624b\u7684\u5bf9\u8c61\u5c31\u662f\u7ef4\u82e5\u62c9\uff0c\u4ed6\u5982\u679c\u4f1a\u653e\u8fc7\u624d\u89c1\u9b3c\u3002\u7ef4\u82e5\u62c9\u5df2\u7ecf\u53ef\u4ee5\u9884\u6599\u5728\u63a5\u4e0b\u6765\u4e00\u76f4\u5230\u8bc5\u5492\u89e3\u9664\u4e4b\u524d\uff0c\u81ea\u5df1\u7684\u201c\u8d1f\u62c5\u201d\u90fd\u4f1a\u975e\u5e38\u91cd\u4e86\u3002</p><p>\u201c\u5728\u51db\u5979\u4eec\u770b\u6765\uff0c\u5c31\u662f\u6211\u8fd9\u4e2a\u65b0\u6765\u7684\u5973\u53cb\u72ec\u5360\u4e07\u5343\u5ba0\u7231\u4e8e\u4e00\u8eab\uff0c\u4f30\u8ba1\u4f1a\u5f88\u5ac9\u5992\u5427\u2014\u2014\u5478\uff0c\u6211\u600e\u4e48\u4f1a\u60f3\u8fd9\u79cd\u5947\u602a\u7684\u4e8b\u60c5\uff0c\u771f\u662f\u83ab\u540d\u5176\u5999\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u6536\u655b\u5fc3\u795e\uff0c\u6b63\u8981\u8bf4\u8bdd\uff0c\u5374\u611f\u89c9\u6709\u4eba\u5728\u8f7b\u8f7b\u626f\u5979\u7684\u8863\u8896\uff0c\u4f4e\u5934\u4e00\u770b\uff0c\u53d1\u73b0\u662f\u51db\u3002\u201c\u5854\u59d0\u59d0\uff0c\u4f60\u8fd9\u4e48\u6f02\u4eae\uff0c\u4ed6\u80af\u5b9a\u5f88\u559c\u6b22\uff0c\u4ee5\u540e\u8981\u8ba9\u7740\u6211\u4e00\u70b9\u597d\u4e0d\u597d\uff0c\u201d\u5979\u561f\u7740\u5634\uff0c\u201c\u8fd9\u5bb6\u4f19\u8eab\u8fb9\u5973\u4eba\u592a\u591a\uff0c\u6211\u90fd\u96be\u5f97\u8f6e\u5230\u4e00\u6b21\uff0c\u4f60\u6765\u4e86\u6211\u5c31\u66f4\u6ca1\u673a\u4f1a\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u743c\u6069\u89c9\u5f97\u5982\u679c\u518d\u4e0d\u5236\u6b62\uff0c\u51db\u5c31\u8981\u628a\u81ea\u5df1\u5f62\u5bb9\u6210\u4e16\u754c\u4e0a\u6700\u53ef\u601c\u7684\u6df1\u95fa\u6028\u5987\u4e86\uff0c\u4ed6\u5f53\u673a\u7acb\u65ad\uff0c\u628a\u51db\u4ece\u7ef4\u82e5\u62c9\u7684\u8eab\u8fb9\u786c\u626f\u5f00\u6765\u3002\u201c\u597d\u4e86\u597d\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u4eec\u5148\u8d76\u5feb\u79bb\u5f00\u8fd9\u91cc\uff0c\u4f60\u4eec\u6709\u4ec0\u4e48\u8bdd\u8def\u4e0a\u518d\u8bf4\uff0c\u53cd\u6b63\u65f6\u95f4\u591a\u5f97\u662f\u3002\u201d</p><p>\u8d76\u5feb\u79bb\u5f00\u662f\u5fc5\u987b\u7684\uff0c\u4e07\u4e00\u5927\u8d24\u8005\u8ffd\u4e0a\u6765\u90a3\u53ef\u5c31\u9ebb\u70e6\u4e86\uff0c\u521a\u624d\u4ea4\u624b\u867d\u7136\u77ed\u6682\uff0c\u4f46\u743c\u6069\u5df2\u7ecf\u5224\u65ad\u51fa\u5c31\u7b97\u81ea\u5df1\u548c\u51db\u8054\u624b\uff0c\u518d\u52a0\u4e0a\u7ef4\u82e5\u62c9\u5e2e\u5fd9\uff0c\u4f30\u8ba1\u4e5f\u6253\u4e0d\u8fc7\u90a3\u4f4d\u8001\u5deb\u5e08\u3002\u65e2\u7136\u660e\u77e5\u6253\u4e0d\u8fc7\uff0c\u5f53\u7136\u9003\u5f97\u8d8a\u8fdc\u8d8a\u597d\u3002</p><p>\u201c\u6211\u4eec\u8981\u53bb\u54ea\u513f\u5462\uff1f\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u56de\u5854\u745f\u8c37\uff1f\u201d</p><p>\u201c\u7b97\u4e86\u5427\uff0c\u6211\u53ef\u4e0d\u60f3\u5f15\u53d1\u5b97\u6559\u6218\u4e89\u3002\u201d</p><p>\u201c\u90a3\u4e48\u662f\u56de\u9634\u9b42\u57ce\uff1f\u201d</p><p>\u743c\u6069\u4e5f\u4e0d\u60f3\u56de\u9634\u9b42\u57ce\uff0c\u4ed6\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u201c\u4f60\u4eec\u8c01\u77e5\u9053\u2018\u7d2b\u5bb8\u6c99\u6f20\u2019\u8fd9\u5730\u65b9\u5728\u54ea\uff1f\u201d</p><p>\u838e\u73de\u514b\u6447\u5934\uff0c\u7ef4\u82e5\u62c9\u4e5f\u8868\u793a\u4e0d\u77e5\u9053\uff0c\u5012\u662f\u51db\u76b1\u7740\u7709\uff0c\u201c\u8fd9\u540d\u5b57\u6211\u597d\u50cf\u6709\u70b9\u5370\u8c61\u2026\u2026\u201d\u5979\u60f3\u4e86\u534a\u54cd\uff0c\u6700\u7ec8\u6446\u4e86\u6446\u624b\uff0c\u201c\u7b97\u4e86\uff0c\u60f3\u4e0d\u8d77\u6765\u3002\u201d</p><p>\u201c\u2026\u2026\u90a3\u597d\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u505a\u4e86\u51b3\u5b9a\uff0c\u201c\u6211\u4eec\u53bb\u4e1c\u57df\u3002\u201d</p><p>\u201c\u53bb\u4e1c\u57df\u5417\uff1f\u6211\u6765\u5e26\u8def\u597d\u4e86\uff0c\u201d\u51db\u81ea\u544a\u594b\u52c7\uff0c\u201c\u90a3\u5730\u65b9\u6211\u5f88\u719f\u7684\uff0c\u201d\u5979\u62ac\u5934\u56db\u5904\u5f20\u671b\u7247\u523b\uff0c\u4f38\u624b\u4e00\u6307\uff0c\u201c\u5f80\u8fd9\u8fb9\u8d70\u3002\u201d</p><p>\u201c\u4f60\u786e\u5b9a\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u4e1c\u57df\u561b\uff0c\u4e00\u76f4\u5f80\u4e1c\u8d70\u5c31\u5bf9\u4e86\u3002\u201d</p><p>\u201c\u4f46\u4f60\u6307\u7684\u662f\u897f\u65b9\u554a\u3002\u201d</p><p>\u201c\u54e6\uff0c\u201d\u51db\u5403\u4e86\u4e00\u60ca\uff0c\u201c\u90a3\u8fb9\u662f\u897f\u65b9\u5417\u2014\u2014\u4e0d\u8fc7\u65e0\u6240\u8c13\u5566\uff0c\u53cd\u6b63\u5927\u5730\u662f\u7403\u5f62\uff0c\u5f80\u897f\u8d70\u4e5f\u80fd\u8d70\u5230\u4e1c\u65b9\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u8fd9\u79cd\u65b9\u5411\u611f\u4e5f\u597d\u610f\u601d\u5e26\u8def\uff1f\u201d</p><p>\u201c\u665a\u4e0a\u5929\u9ed1\u770b\u4e0d\u6e05\uff01\u201d</p><p>\u201c\u522b\u626f\u4e86\uff0c\u6307\u5317\u661f\u5c31\u5728\u4f60\u5934\u4e0a\u5462\u3002\u201d</p><p>\u201c\u4eba\u5bb6\u591c\u91cc\u89c6\u529b\u4e0d\u597d\u561b\u3002\u201d</p><p>\u201c\u4f60\u7684\u591c\u89c6\u80fd\u529b\u4e0d\u597d\u5417\uff0c\u600e\u4e48\u4ece\u6ca1\u542c\u4f60\u8bf4\u8fc7\uff1f\u4e0d\u5bf9\uff0c\u8fd9\u5173\u89c6\u529b\u4ec0\u4e48\u4e8b\uff0c\u90a3\u4e48\u4eae\u7684\u661f\u661f\uff0c\u662f\u4e2a\u4eba\u90fd\u80fd\u770b\u89c1\u5427\u3002\u201d</p><p>\u201c\u4eba\u5bb6\u53ef\u4e0d\u662f\u4eba\uff0c\u201d\u51db\u633a\u4e86\u633a\u80f8\u812f\uff0c\u201c\u4eba\u5bb6\u662f\u9f99\u5462\u3002\u201d</p><p>\u201c\u4f60\u771f\u662f\u9f99\u7c7b\u4e4b\u803b\u3002\u201d</p></article>"}}}]);
