(window.webpackJsonp=window.webpackJsonp||[]).push([[540],{562:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u8282 \u53bb\u4e1c\u57df",content:"<article><h2>\u7b2c\u4e09\u8282 \u53bb\u4e1c\u57df</h2><p>\u5f53\u4e00\u4ef6\u4e8b\u60c5\u65e0\u6cd5\u9690\u7792\u7684\u65f6\u5019\uff0c\u4e3b\u52a8\u5766\u767d\u4e0d\u5931\u4e3a\u4e0a\u7b56\uff0c\u81f3\u5c11\u53ef\u4ee5\u4e89\u53d6\u4e00\u70b9\u5370\u8c61\u5206\u3002\u743c\u6069\u4e0d\u7b49\u6885\u83f2\u65af\u5f00\u53e3\u8be2\u95ee\uff0c\u5c31\u4e00\u4e94\u4e00\u5341\u5730\u5c06\u6240\u6709\u7ecf\u8fc7\u5bf9\u5979\u8bf4\u4e86\u4e00\u904d\u3002\u201c\u6574\u4e2a\u4e8b\u60c5\u7ecf\u8fc7\u5c31\u662f\u8fd9\u6837\u3002\u201d\u4ed6\u8bf4\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u5077\u770b\u4e86\u4e00\u773c\u5c11\u5973\u3002</p><p>\u201c\u539f\u6765\u5982\u6b64\u3002\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u8bed\u6c14\u6ce2\u6f9c\u4e0d\u60ca\u3002</p><p>\u5c11\u5973\u7684\u53cd\u5e94\u5b8c\u5168\u51fa\u4e4e\u743c\u6069\u7684\u610f\u6599\uff0c\u5728\u4ed6\u7684\u9884\u8ba1\u4e4b\u4e2d\uff0c\u6885\u83f2\u65af\u4f1a\u53d1\u706b\uff0c\u4f1a\u751f\u6c14\uff0c\u6240\u4ee5\u4ed6\u4e5f\u505a\u597d\u4e86\u9053\u6b49\u8ba4\u9519\u7684\u51c6\u5907\uff0c\u751a\u81f3\u8fde\u75db\u54ed\u6d41\u6d95\u62b1\u5927\u817f\u7684\u9884\u6848\u90fd\u5728\u5fc3\u91cc\u6f14\u7ec3\u8fc7\u3002\u7136\u800c\u5c11\u5973\u5982\u6b64\u5e73\u9759\uff0c\u53cd\u800c\u8ba9\u4ed6\u4e00\u65f6\u95f4\u4e0d\u77e5\u6240\u63aa\uff0c\u4e0d\u77e5\u9053\u63a5\u4e0b\u6765\u8be5\u8bf4\u4ec0\u4e48\u597d\u4e86\u3002</p><p>\u201c\u6240\u4ee5\u4f60\u73b0\u5728\u662f\u51c6\u5907\u53bb\u4e1c\u57df\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u201c\u55ef\uff0c\u201d\u743c\u6069\u4e0d\u77e5\u9053\u5979\u4e3a\u4ec0\u4e48\u7a81\u7136\u6362\u4e86\u8bdd\u9898\uff0c\u4f46\u8fd8\u662f\u8001\u8001\u5b9e\u5b9e\u56de\u7b54\uff0c\u201c\u6211\u76ee\u524d\u662f\u8fd9\u4e2a\u6253\u7b97\uff0c\u4f60\u89c9\u5f97\u5462\u3002\u201d</p><p>\u201c\u4f60\u53bb\u4e1c\u57df\u7684\u76ee\u7684\u662f\u4ec0\u4e48\uff1f\u201d\u6885\u83f2\u65af\u95ee\uff0c\u201c\u4e3a\u4e86\u89e3\u9664\u8bc5\u5492\uff1f\u201d</p><p>\u201c\u8fd9\u662f\u4e00\u4e2a\u539f\u56e0\u3002\u201d</p><p>\u743c\u6069\u51b3\u5b9a\u53bb\u4e1c\u57df\uff0c\u662f\u57fa\u4e8e\u591a\u79cd\u56e0\u7d20\u7efc\u5408\u8003\u8651\uff0c\u5bfb\u627e\u89e3\u9664\u4e13\u60c5\u8bc5\u5492\u7684\u201c\u5deb\u94c1\u201d\u81ea\u7136\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u4f46\u5e76\u975e\u5168\u90e8\uff0c\u751a\u81f3\u4e0d\u662f\u6700\u91cd\u8981\u7684\u539f\u56e0\u3002\u5b9e\u9645\u4e0a\uff0c\u5728\u743c\u6069\u53bb\u9634\u5f71\u9547\u627e\u7ef4\u82e5\u62c9\u4e4b\u524d\uff0c\u4ed6\u5c31\u5df2\u7ecf\u6709\u4e86\u8fd9\u4e2a\u6253\u7b97\uff0c\u53ea\u662f\u6ca1\u6709\u8bf4\u51fa\u53e3\u800c\u5df2\u3002</p><p>\u201c\u8fd8\u6709\u522b\u7684\u539f\u56e0\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u201c\u771f\u540d\u5951\u7ea6\u7684\u4e8b\u60c5\u4e5f\u662f\u4e00\u4e2a\u539f\u56e0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u5965\u52a0\u83b1\u65af\u5728\u6d88\u901d\u4e4b\u524d\u66fe\u7ecf\u505a\u51fa\u9884\u8a00\uff0c\u8bf4\u4e1c\u57df\u6709\u89e3\u9664\u771f\u540d\u5951\u7ea6\u7684\u673a\u7f18\uff0c\u867d\u7136\u8fd9\u9884\u8a00\u5341\u5206\u6a21\u7cca\uff0c\u4f46\u53cd\u6b63\u522b\u65e0\u4ed6\u6cd5\uff0c\u4e5f\u4e0d\u59a8\u5c1d\u8bd5\uff0c\u201c\u53e6\u5916\uff0c\u8428\u739b\u65af\u7279\u8bf4\u7684\u90a3\u4e2a\u6d6e\u7a7a\u57ce\uff0c\u6211\u4e5f\u633a\u6709\u5174\u8da3\u3002\u201d</p><p>\u6885\u83f2\u65af\u6447\u6447\u5934\u3002</p><p>\u201c\u597d\u5427\uff0c\u6211\u627f\u8ba4\uff0c\u8fd9\u4e9b\u90fd\u4e0d\u662f\u4e3b\u8981\u539f\u56e0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u60f3\u53bb\u78b0\u78b0\u8fd0\u6c14\uff0c\u770b\u80fd\u4e0d\u80fd\u627e\u5230\u5176\u4ed6\u51e0\u4ef6\u79d8\u5668\u3002\u201d</p><p>\u201c\u7b2c\u4e94\u79d8\u5668\u4e0d\u662f\u5df2\u7ecf\u5728\u4f60\u624b\u4e0a\u4e86\u5417\uff1f\u201d\u6885\u83f2\u65af\u4e0d\u4ee5\u4e3a\u7136\uff0c\u201c\u8fd9\u79cd\u4e1c\u897f\u53c8\u4e0d\u662f\u8d8a\u591a\u8d8a\u597d\u3002\u5c31\u7b97\u96c6\u9f50\u4e03\u79d8\u5668\uff0c\u4f60\u4e5f\u53ea\u662f\u4e00\u4e2a\u4eba\uff0c\u53c8\u80fd\u540c\u65f6\u9a7e\u9a6d\u51e0\u4ef6\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u4e00\u4e2a\u4eba\uff0c\u201d\u743c\u6069\u7ea0\u6b63\uff0c\u201c\u4f60\u5fd8\u4e86\uff0c\u8fd8\u6709\u73ca\u5609\u3002\u201d</p><p>\u5c11\u5973\u51dd\u89c6\u7740\u4ed6\uff0c\u8fc7\u4e86\u534a\u54cd\uff0c\u5ffd\u7136\u5fae\u5fae\u4e00\u7b11\uff0c\u5c06\u624b\u4e2d\u7684\u9b54\u6cd5\u7b14\u8bb0\u653e\u5230\u684c\u4e0a\uff0c\u201c\u4f60\u6700\u8fd1\u8fd9\u6bb5\u65f6\u95f4\u5f88\u52aa\u529b\u554a\u3002\u201d\u5979\u8bf4\u3002</p><p>\u201c\u4e0d\u52aa\u529b\u4e0d\u884c\uff0c\u201d\u743c\u6069\u8038\u8038\u80a9\uff0c\u201c\u9634\u5f71\u8c37\u8fd9\u4e00\u6218\uff0c\u8ba9\u6211\u660e\u767d\u4e86\u4e00\u4e9b\u4e1c\u897f\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d\u6885\u83f2\u65af\u9976\u6709\u5174\u81f4\uff0c\u201c\u4f60\u660e\u767d\u4e86\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u5fc5\u987b\u5c3d\u5feb\u53d8\u5f3a\uff0c\u8fd9\u6837\u624d\u6709\u80fd\u529b\u4fdd\u62a4\u8eab\u8fb9\u7684\u4eba\u3002\u201d</p><p>\u201c\u5c31\u8fd9\u4e2a\uff1f\u201d\u5c11\u5973\u4f3c\u4e4e\u6709\u4e9b\u5931\u671b\uff0c\u201c\u8fd9\u4e5f\u4e0d\u7b97\u4ec0\u4e48\u65b0\u7684\u89c9\u609f\u5427\uff0c\u4f60\u4e0d\u662f\u65e9\u5c31\u77e5\u9053\u4e86\u5417\uff1f\u201d</p><p>\u201c\u4e0d\uff0c\u4e0d\u4e00\u6837\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u4e4b\u524d\u662f\u8ba4\u4e3a\u6211\u2018\u5e94\u8be5\u2019\u53d8\u5f3a\uff0c\u800c\u73b0\u5728\u5219\u662f\u2018\u5fc5\u987b\u2019\u3002\u201d</p><p>\u201c\u533a\u522b\u4f55\u5728\u5462\uff1f\u201d</p><p>\u743c\u6069\u8ba4\u771f\u60f3\u4e86\u4e00\u4f1a\uff0c\u8003\u8651\u5982\u4f55\u63aa\u8f9e\uff0c\u201c\u8fd9\u4e48\u8bf4\u5427\uff0c\u6211\u4e00\u76f4\u4ee5\u6765\u7684\u7406\u89e3\u662f\uff1a\u8fd9\u4e2a\u4e16\u754c\uff0c\u5bf9\u4e8e\u6211\uff0c\u4ee5\u53ca\u6211\u8eab\u8fb9\u7684\u4eba\u6765\u8bf4\uff0c\u6574\u4f53\u800c\u8a00\u8fd8\u662f\u5b89\u5168\u7684\u3002\u6211\u4e0d\u662f\u88ab\u901a\u7f09\u7684\u4ea1\u547d\u6740\u624b\uff0c\u4e5f\u4e0d\u662f\u4ec7\u5bb6\u65e0\u6570\u7684\u9ed1\u9053\u9996\u9886\uff0c\u6211\u8d70\u5728\u8857\u9053\u4e0a\uff0c\u4e0d\u7528\u62c5\u5fc3\u8def\u4e0a\u7684\u884c\u4eba\u968f\u65f6\u62d4\u51fa\u4e00\u628a\u5200\u6765\u780d\u6211\uff0c\u6211\u53bb\u9910\u9986\u5403\u996d\uff0c\u4e0d\u7528\u62c5\u5fc3\u6709\u4eba\u7ed9\u6211\u4e0b\u6bd2\u3002\u5f53\u7136\uff0c\u6211\u4f1a\u53c2\u4e0e\u4e00\u4e9b\u4e8b\u60c5\uff0c\u4f1a\u5377\u5165\u4e00\u4e9b\u7ea0\u7eb7\uff0c\u4e0d\u53ef\u907f\u514d\u5730\u4f1a\u906d\u9047\u4e00\u4e9b\u5371\u9669\uff0c\u4f46\u8fd9\u6bd5\u7adf\u4e0d\u662f\u5e38\u6001\u3002\u6211\u5e94\u8be5\u8ba9\u81ea\u5df1\u53d8\u5f97\u66f4\u5f3a\uff0c\u4ece\u800c\u5e94\u4ed8\u8fd9\u4e9b\u5076\u53d1\u6027\u7684\uff0c\u4e0d\u5e38\u89c1\u7684\u98ce\u9669\u2014\u2014\u6211\u539f\u672c\u662f\u8fd9\u4e48\u60f3\u7684\u3002\u201d</p><p>\u201c\u542c\u8d77\u6765\u4e5f\u6ca1\u9519\u3002\u201d</p><p>\u201c\u4e8b\u5b9e\u662f\u5927\u9519\u7279\u9519\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u521a\u521a\u53d1\u73b0\uff0c\u6211\u81ea\u5df1\u5c31\u662f\u5371\u9669\u7684\u6765\u6e90\u3002\u201d</p><p>\u201c\u54e6\uff1f\u201d</p><p>\u201c\u65e0\u8bba\u662f\u5426\u627f\u8ba4\uff0c\u662f\u5426\u5b58\u5728\u8bb0\u5fc6\uff0c\u6216\u8005\u662f\u5426\u81ea\u613f\uff0c\u4e8b\u5b9e\u662f\uff0c\u6211\u548c\u67d0\u4f4d\u4e0a\u53e4\u65f6\u4ee3\u7684\u5947\u68b0\u5e08\u6709\u8054\u7cfb\uff0c\u5f88\u591a\u4eba\u90fd\u8ba4\u4e3a\u6211\u548c\u4ed6\u662f\u540c\u4e00\u4e2a\u4eba\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u5bfc\u81f4\u7684\u7ed3\u679c\uff0c\u5c31\u662f\u6211\u5fc5\u987b\u8981\u627f\u62c5\u56e0\u4e3a\u4ed6\u6240\u5bfc\u81f4\u7684\u4e00\u5207\u5371\u9669\u2014\u2014\u5c31\u76ee\u524d\u6240\u77e5\uff0c\u90a3\u5bb6\u4f19\u7684\u4ec7\u5bb6\u904d\u5730\u90fd\u662f\uff0c\u800c\u4e14\u5168\u90fd\u5f88\u96be\u7f20\u3002\u201d</p><p>\u201c\u6240\u4ee5\u4f60\u8981\u53bb\u4e1c\u57df\uff1f\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u8fd9\u624d\u662f\u6700\u4e3b\u8981\u7684\u539f\u56e0\u5427\u3002\u201d</p><p>\u201c\u5bf9\uff0c\u201d\u743c\u6069\u627f\u8ba4\uff0c\u201c\u90a3\u5bb6\u4f19\u7ed9\u6211\u62db\u6765\u8fd9\u4e48\u591a\u9ebb\u70e6\uff0c\u6211\u603b\u8981\u641e\u6e05\u695a\u5230\u5e95\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u5426\u5219\u5c82\u4e0d\u662f\u592a\u51a4\u6789\u4e86\u3002\u201d</p><p>\u201c\u597d\u50cf\u4e5f\u4e0d\u5168\u662f\u9ebb\u70e6\u554a\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u4e5f\u7ed9\u4f60\u7559\u4e86\u51e0\u4e2a\u7f8e\u4eba\u513f\u5462\uff0c\u6bd4\u5982\u8bf4\u624e\u745e\u5c14\u3001\u51ef\u745f\u7433\uff0c\u597d\u50cf\u8fd8\u6709\u90a3\u4f4d\u8ff7\u96fe\u5927\u5e08\u7c73\u65af\u5170\u8fbe\u5c14\uff1f\u867d\u7136\u6ca1\u89c1\u8fc7\u771f\u5bb9\uff0c\u4f46\u7cbe\u7075\u516c\u4e3b\u4e0d\u4f1a\u5dee\u5230\u54ea\u91cc\u53bb\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u5979\u4eec\u5c31\u662f\u6700\u5927\u7684\u9ebb\u70e6\u3002\u201d</p><p>\u743c\u6069\u559c\u6b22\u7f8e\u5973\uff0c\u4f46\u4ed6\u6bd4\u8f83\u559c\u6b22\u81ea\u5df1\u5f04\u5230\u624b\u7684\uff0c\u800c\u4e0d\u662f\u83ab\u540d\u5176\u5999\u4ece\u5929\u4e0a\u7a81\u7136\u6389\u4e0b\u6765\uff0c\u8fd9\u6837\u4f1a\u8ba9\u4ed6\u4e25\u91cd\u7f3a\u4e4f\u5fc3\u7406\u51c6\u5907\uff0c\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u53bb\u76f8\u5904\uff0c\u800c\u4e14\u8fd8\u4f1a\u5f15\u53d1\u5bb6\u5ead\u77db\u76fe\u2014\u2014\u6216\u8005\u8bf4\uff0c\u66f4\u5bb9\u6613\u5f15\u53d1\u77db\u76fe\u3002\u6885\u83f2\u65af\u53ef\u4ee5\u4e0d\u4ecb\u610f\u51db\uff0c\u56e0\u4e3a\u51db\u662f\u5979\u7684\u597d\u53cb\uff1b\u53ef\u4ee5\u52c9\u5f3a\u63a5\u53d7\u73ca\u5609\u548c\u838e\u73de\u514b\uff0c\u56e0\u4e3a\u5979\u4eec\u81f3\u5c11\u4e0d\u662f\u201c\u964c\u751f\u4eba\u201d\uff0c\u6253\u8fc7\u4ea4\u9053\uff0c\u6709\u8fc7\u63a5\u89e6\uff1b\u4f46\u624e\u745e\u5c14\u548c\u51ef\u745f\u7433\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u81f3\u4e8e\u7c73\u65af\u5170\u8fbe\u5c14\uff0c\u90a3\u5c31\u66f4\u522b\u63d0\u4e86\uff0c\u6309\u7167\u624e\u745e\u5c14\u7684\u8bf4\u6cd5\uff0c\u743c\u6069\u4e0a\u8f88\u5b50\u59cb\u4e71\u7ec8\u5f03\uff0c\u5bb3\u6b7b\u4e86\u5979\u59d0\u59d0\uff0c\u53cc\u65b9\u8bf4\u662f\u6b7b\u654c\u4e5f\u4e0d\u8fc7\u5206\u3002</p><p>\u9634\u5f71\u8c37\u5927\u6218\u4e4b\u524d\uff0c\u743c\u6069\u5bf9\u4e8e\u81ea\u5df1\u7684\u8fd9\u4e2a\u201c\u524d\u4e16\u201d\u95ee\u9898\u5e76\u4e0d\u662f\u5341\u5206\u5728\u610f\uff0c\u4ed6\u603b\u89c9\u5f97\u4e0d\u8bba\u771f\u5047\uff0c\u603b\u4e4b\u90fd\u5df2\u7ecf\u8fc7\u53bb\u51e0\u5343\u5e74\u4e86\uff0c\u6614\u65e5\u5373\u4fbf\u6709\u4ec0\u4e48\u6069\u6028\u662f\u975e\uff0c\u4e5f\u65e9\u5c31\u968f\u7740\u65f6\u95f4\u800c\u6d88\u901d\u6e6e\u706d\uff0c\u5316\u4f5c\u7070\u70ec\uff0c\u4e0d\u5fc5\u518d\u63d0\u3002\u53cd\u6b63\u81ea\u5df1\u53c8\u6ca1\u6253\u7b97\u7ee7\u627f\u9057\u4ea7\uff0c\u4f55\u5fc5\u7a77\u6839\u7a76\u5e95\uff0c\u542b\u7cca\u8fc7\u53bb\u4e5f\u5c31\u7f62\u4e86\u3002\u4f46\u4e8b\u5b9e\u544a\u8bc9\u4ed6\uff1a\u6709\u4e9b\u4e8b\u60c5\uff0c\u60f3\u8eb2\u662f\u8eb2\u4e0d\u6389\u7684\uff0c\u6709\u4e9b\u6069\u6028\uff0c\u662f\u4e0d\u505a\u4e86\u7ed3\u5c31\u65e0\u6cd5\u6d88\u6cef\u7684\u2014\u2014\u6700\u5173\u952e\u7684\u662f\uff0c\u6709\u4e9b\u4eba\uff0c\u662f\u5386\u7ecf\u5343\u5e74\u4ecd\u7136\u8fd8\u5b58\u5728\u7684\u3002</p><p>\u201c\u6211\u8ba8\u538c\u9b54\u6cd5\u4e16\u754c\uff01\u201d\u4ed6\u8bf4\u3002</p><p>\u559c\u6b22\u4e5f\u7f62\uff0c\u8ba8\u538c\u4e5f\u7f62\uff0c\u4e8b\u5b9e\u5df2\u7ecf\u6446\u5728\u9762\u524d\uff0c\u5bb9\u4e0d\u5f97\u743c\u6069\u7ee7\u7eed\u88c5\u9e35\u9e1f\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u4ed6\u4e5f\u53ea\u80fd\u6b63\u9762\u8fce\u4e0a\u4e86\u3002</p><p>\u201c\u4f60\u53ef\u4ee5\u76f4\u63a5\u95ee\u624e\u745e\u5c14\u561b\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5979\u80af\u5b9a\u6e05\u695a\u3002\u201d</p><p>\u201c\u5979\u56de\u5730\u72f1\u4e4b\u540e\uff0c\u6211\u5c31\u8054\u7cfb\u4e0d\u4e0a\u4e86\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u6309\u7167\u901a\u884c\u7684\u9b54\u6cd5\u7406\u8bba\uff0c\u5deb\u5e08\u53ea\u8981\u638c\u63e1\u90aa\u9b54\u7684\u771f\u540d\uff0c\u65e0\u8bba\u76f8\u9694\u591a\u8fdc\uff0c\u662f\u5426\u8de8\u8d8a\u4f4d\u9762\uff0c\u90fd\u53ef\u4ee5\u8054\u7cfb\u3002\u4f46\u624e\u745e\u5c14\u662f\u9b54\u59ec\uff0c\u6216\u8bb8\u4e0d\u9002\u7528\u4e8e\u8fd9\u4e2a\u901a\u5e38\u7684\u89c4\u5219\uff0c\u53cd\u6b63\u743c\u6069\u6ca1\u529e\u6cd5\u8054\u7cfb\u5979\uff0c\u53ea\u6709\u9690\u7ea6\u7684\u611f\u5e94\uff0c\u80fd\u786e\u5b9a\u5979\u4f9d\u7136\u201c\u5b58\u5728\u201d\uff0c\u6ca1\u6709\u88ab\u62dc\u5c14\u5e72\u6389\uff0c\u81f3\u4e8e\u5177\u4f53\u60c5\u51b5\uff0c\u5c31\u4e00\u65e0\u6240\u77e5\u4e86\u3002\u800c\u4e14\u5766\u767d\u5730\u8bf4\uff0c\u624e\u745e\u5c14\u7684\u8bf4\u6cd5\u4e5f\u4ec5\u4ec5\u53ea\u662f\u5979\u7684\u8bf4\u6cd5\uff0c\u662f\u4e0d\u662f\u5b8c\u5168\u7b26\u5408\u4e8b\u5b9e\uff0c\u4e5f\u8fd8\u8981\u53e6\u5f53\u522b\u8bba\u3002</p><p>\u201c\u4eba\u5bb6\u628a\u771f\u540d\u90fd\u4ea4\u7ed9\u4f60\u4e86\uff0c\u4f60\u8fd8\u4e0d\u4fe1\u4efb\u5979\uff1f\u201d\u5c11\u5973\u7b11\u7740\u8bf4\uff0c\u201c\u592a\u8584\u60c5\u4e86\u5427\u3002\u201d</p><p>\u201c\u8fd9\u4e0d\u662f\u4fe1\u4efb\u4e0d\u4fe1\u4efb\u7684\u95ee\u9898\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5979\u6709\u5979\u7684\u7acb\u573a\u3002\u4efb\u4f55\u4eba\u5728\u6709\u7acb\u573a\u7684\u60c5\u51b5\u4e0b\uff0c\u90fd\u4e0d\u4f1a\u771f\u6b63\u5ba2\u89c2\u5730\u9648\u8ff0\u4e8b\u5b9e\u3002\u201d</p><p>\u6885\u83f2\u65af\u8138\u4e0a\u7684\u7b11\u610f\u655b\u53bb\uff0c\u5979\u6c89\u9759\u5730\u770b\u7740\u743c\u6069\uff0c\u4eff\u4f5b\u5728\u601d\u8003\u4ec0\u4e48\uff0c\u6700\u7ec8\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u8bf4\u5f97\u6ca1\u9519\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u8981\u53bb\u7d2b\u5bb8\u6c99\u6f20\u627e\u51ef\u745f\u7433\u5bf9\u5427\u3002\u201d</p><p>\u201c\u55ef\uff0c\u624e\u745e\u5c14\u662f\u8fd9\u4e48\u8bf4\u7684\uff0c\u201d\u743c\u6069\u76b1\u7740\u7709\uff0c\u201c\u4f46\u6211\u5728\u5730\u56fe\u4e0a\u627e\u4e86\u534a\u5929\uff0c\u4e5f\u6ca1\u770b\u89c1\u7d2b\u5bb8\u6c99\u6f20\u5728\u54ea\u91cc\u3002\u201d</p><p>\u201c\u5730\u56fe\u4e0a\u5f53\u7136\u6ca1\u6709\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u7d2b\u5bb8\u6c99\u6f20\u662f\u51e0\u5343\u5e74\u524d\u7684\u53e4\u540d\uff0c\u795e\u738b\u8986\u706d\u4f0a\u739b\u65af\u5361\u4e4b\u540e\uff0c\u5c31\u628a\u5b83\u6539\u4e86\u540d\u5b57\uff0c\u201d\u5c11\u5973\u62ff\u51fa\u5730\u56fe\uff0c\u626b\u4e86\u4e00\u773c\uff0c\u5728\u4e0a\u9762\u6307\u4e86\u4e00\u6307\uff0c\u201c\u73b0\u5728\u5b83\u53eb\u795e\u4e34\u6c99\u6f20\u3002\u201d</p><p>\u743c\u6069\u987a\u7740\u5979\u7684\u624b\u6307\u770b\u53bb\uff0c\u53d1\u73b0\u90a3\u662f\u5728\u6574\u4e2a\u5927\u9646\u5730\u56fe\u7684\u6700\u4e1c\u8fb9\uff0c\u4e00\u7247\u5f2f\u6708\u5f62\u7684\u5927\u6c99\u6f20\u3002\u201c\u8fd9\u5730\u65b9\u2026\u2026\u73b0\u5728\u5e94\u8be5\u5c5e\u4e8e\u7a46\u7f55\u5e1d\u56fd\u5883\u5185\u5427\uff1f\u201d</p><p>\u201c\u540d\u4e49\u4e0a\u662f\uff0c\u5b9e\u9645\u4e0a\u662f\u5757\u65e0\u4e3b\u4e4b\u5730\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f20\u8bf4\u5b83\u662f\u4e1c\u57df\u8bf8\u795e\u738b\u964d\u4e34\u6b64\u4e16\u754c\uff0c\u6700\u521d\u62b5\u8fbe\u7684\u5730\u65b9\uff0c\u201d\u5979\u987f\u4e86\u987f\uff0c\u201c\u4e5f\u662f\u6614\u65e5\u4f0a\u739b\u65af\u5361\u7684\u9996\u90fd\u6240\u5728\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\u3002\u201d</p><p>\u743c\u6069\u5bf9\u4e8e\u4e1c\u57df\u7684\u4e86\u89e3\u5f88\u5c11\uff0c\u4e00\u4e9b\u6700\u57fa\u672c\u7684\u4e1c\u897f\u8fd8\u77e5\u9053\u70b9\uff0c\u50cf\u7d2b\u5bb8\u6c99\u6f20\u8fd9\u79cd\u6bd4\u8f83\u751f\u50fb\u7684\uff0c\u5c31\u5b8c\u5168\u6ca1\u6982\u5ff5\u4e86\u3002\u8fd9\u51e0\u5929\u4ed6\u4e5f\u8ba9\u838e\u73de\u514b\u53bb\u641c\u96c6\u76f8\u5173\u8d44\u6599\uff0c\u4f46\u6210\u6548\u4e0d\u5927\uff0c\u4e1c\u57df\u90a3\u5730\u65b9\u5b9e\u5728\u592a\u504f\u8fdc\u4e86\uff0c\u53bb\u8fc7\u7684\u4eba\u5f88\u5c11\uff0c\u8bf4\u4e0d\u51fa\u592a\u591a\u6709\u4ef7\u503c\u7684\u4e1c\u897f\u3002</p><p>\u201c\u90a3\u662f\u4f60\u6ca1\u6709\u627e\u5bf9\u5730\u65b9\uff0c\u201d\u6885\u83f2\u65af\u8bf4\u7740\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u201c\u966a\u6211\u51fa\u53bb\u8d70\u8d70\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u53f6\u5f18\u57ce\u7684\u6574\u4e2a\u57ce\u533a\u5efa\u5728\u4e00\u4e2a\u5de8\u5927\u7684\u3001\u4ee5\u5e73\u7f13\u89d2\u5ea6\u5ef6\u4f38\u5165\u6d77\u7684\u659c\u5761\u4e0a\uff0c\u7531\u4e8e\u7f3a\u4e4f\u89c4\u5212\uff0c\u8857\u9053\u539f\u672c\u5c31\u4e0d\u5bbd\u9614\uff0c\u4e24\u65c1\u53c8\u6446\u6ee1\u4e86\u9cde\u6b21\u6809\u6bd4\u7684\u5546\u94fa\uff0c\u663e\u5f97\u683c\u5916\u62e5\u5835\u3002\u743c\u6069\u62a4\u7740\u6885\u83f2\u65af\uff0c\u52aa\u529b\u4ece\u4eba\u7fa4\u4e2d\u6324\u51fa\u6765\uff0c\u611f\u89c9\u81ea\u5df1\u90fd\u8981\u88ab\u538b\u6241\u4e86\u3002\u201c\u600e\u4e48\u8fd9\u4e48\u70ed\u95f9\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u53f6\u5f18\u6709\u8fd9\u4e48\u591a\u4eba\u5417\uff1f\u201d</p><p>\u201c\u4eca\u5929\u662f\u5723\u82bd\u8282\uff0c\u6851\u6bd4\u4e9a\u7684\u4f20\u7edf\u8282\u65e5\uff0c\u201d\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u201c\u4ece\u4e2d\u5348\u5341\u4e8c\u70b9\u5230\u665a\u4e0a\u516b\u70b9\uff0c\u90fd\u662f\u5e86\u795d\u65f6\u95f4\u3002\u201d</p><p>\u7136\u800c\u6885\u83f2\u65af\u663e\u7136\u6ca1\u6709\u53c2\u52a0\u8282\u65e5\u72c2\u6b22\u7684\u6253\u7b97\uff0c\u201c\u90a3\u6211\u4eec\u63a5\u4e0b\u6765\u8981\u53bb\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u9152\u9986\u3002\u201d</p><p>\u53f6\u5f18\u57ce\u522b\u7684\u4e0d\u591a\uff0c\u9152\u9986\u8fd8\u771f\u4e0d\u5c11\uff0c\u5927\u5927\u5c0f\u5c0f\u52a0\u8d77\u6765\u5dee\u4e0d\u591a\u6709\u5341\u51e0\u5bb6\uff0c\u5176\u4e2d\u6700\u6709\u540d\u7684\u53eb\u505a\u201c\u6ce2\u6d1b\u6d1b\u201d\uff0c\u5c31\u5efa\u5728\u7801\u5934\u8fb9\u3002\u6885\u83f2\u65af\u544a\u8bc9\u743c\u6069\uff0c\u6ce2\u6d1b\u6d1b\u662f\u6851\u6bd4\u4e9a\u738b\u56fd\u6c11\u95f4\u4f20\u8bf4\u4e2d\u7684\u4e00\u4f4d\u4e0a\u53e4\u795e\u7957\uff0c\u4eba\u4eec\u76f8\u4fe1\u4ed6\u638c\u63e1\u5927\u6d77\u7684\u529b\u91cf\uff0c\u80fd\u591f\u6380\u8d77\u60ca\u6d9b\u9a87\u6d6a\uff0c\u4e5f\u53ef\u4ee5\u4fdd\u4f51\u51fa\u6d77\u8005\u4e00\u5e06\u98ce\u987a\u3002\u201c\u5176\u5b9e\u5c31\u662f\u5b89\u535a\u62c9\u7684\u4e00\u4e2a\u5316\u540d\u3002\u201d\u5c11\u5973\u8bf4\u3002</p><p>\u201c\u8fd9\u4e9b\u795e\u4e3a\u4ec0\u4e48\u90fd\u559c\u6b22\u7528\u9a6c\u7532\u5462\uff1f\u201d</p><p>\u201c\u5de5\u4f5c\u538b\u529b\u592a\u5927\uff0c\u5076\u5c14\u6362\u6362\u8eab\u4efd\uff0c\u6709\u52a9\u4e8e\u7f13\u89e3\u5fc3\u60c5\u561b\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u5c11\u5973\u50cf\u662f\u5728\u8bf4\u51b7\u7b11\u8bdd\uff0c\u743c\u6069\u4e00\u65f6\u4e5f\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u63a5\u8bdd\u3002\u597d\u5728\u8fd9\u65f6\u5019\uff0c\u6ce2\u6d1b\u6d1b\u9152\u9986\u5230\u4e86\u3002</p><p>\u4e16\u754c\u4e0a\u7684\u9152\u9986\u90fd\u5dee\u4e0d\u591a\uff0c\u55a7\u56a3\u5435\u95f9\uff0c\u4e71\u7cdf\u7cdf\u7684\uff0c\u5145\u65a5\u7740\u5404\u79cd\u53d1\u9152\u75af\u7684\u9189\u9b3c\uff0c\u5927\u543c\u5927\u53eb\uff0c\u8ba9\u743c\u6069\u4e00\u8fdb\u6765\u5c31\u611f\u89c9\u8111\u888b\u5728\u75bc\u3002\u6885\u83f2\u65af\u5374\u597d\u6574\u4ee5\u6687\u5730\u62c9\u7740\u4ed6\u5230\u4e00\u4e2a\u89d2\u843d\u5750\u4e0b\uff0c\u968f\u4fbf\u70b9\u4e86\u4e24\u676f\u996e\u6599\u3002</p><p>\u201c\u6211\u4eec\u6765\u8fd9\u91cc\u5e72\u561b\uff1f\u201d\u743c\u6069\u5fcd\u4e0d\u4f4f\u95ee\u3002</p><p>\u5c11\u5973\u6162\u6162\u5730\u555c\u7740\u5979\u8981\u7684\u8461\u8404\u6c41\uff0c\u6ca1\u6709\u56de\u7b54\uff0c\u800c\u662f\u7528\u624b\u52bf\u793a\u610f\u4ed6\u201c\u503e\u542c\u201d\u3002</p><p>\u743c\u6069\u6ee1\u8179\u7591\u60d1\u5730\u4fa7\u8033\u503e\u542c\uff0c\u7136\u800c\u5468\u56f4\u5b9e\u5728\u592a\u5435\u4e86\uff0c\u6240\u6709\u4eba\u90fd\u5728\u8bf4\u8bdd\uff0c\u6240\u6709\u4eba\u90fd\u5728\u63d0\u9ad8\u97f3\u91cf\uff0c\u5404\u79cd\u65b9\u8a00\u6df7\u6742\u5728\u4e00\u8d77\uff0c\u5bfc\u81f4\u4ed6\u5b8c\u5168\u4e0d\u77e5\u9053\u6885\u83f2\u65af\u5230\u5e95\u8981\u4ed6\u542c\u4ec0\u4e48\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u4ed6\u7684\u8033\u6735\u603b\u7b97\u9002\u5e94\u4e86\u4e00\u70b9\uff0c\u52c9\u5f3a\u80fd\u5206\u8fa8\u51fa\u65c1\u8fb9\u51e0\u5f20\u684c\u5b50\u7684\u5ba2\u4eba\u4ea4\u8c08\u7684\u5185\u5bb9\u3002</p><p>\u4ed6\u4eec\u5728\u8bf4\u524d\u4e0d\u4e45\u53d1\u751f\u7684\u9634\u5f71\u8c37\u4e4b\u6218\u3002</p><p>\u53f6\u5f18\u57ce\u8ddd\u79bb\u9634\u5f71\u8c37\u4e0d\u7b97\u592a\u8fdc\uff0c\u4e5f\u5c31\u4e24\u4e09\u65e5\u7684\u8def\u7a0b\uff0c\u53d1\u751f\u8fd9\u4e48\u5927\u7684\u4e8b\u60c5\uff0c\u4eba\u4eec\u8c08\u8bba\u662f\u5f88\u6b63\u5e38\u7684\u3002\u4f46\u8fd9\u4e9b\u9152\u5ba2\u4eec\u663e\u7136\u6ca1\u4ec0\u4e48\u9760\u8c31\u7684\u6d88\u606f\u6e20\u9053\uff0c\u6240\u8bf4\u90fd\u662f\u4e00\u4e9b\u7247\u9cde\u53ea\u722a\uff0c\u5176\u4e2d\u8fd8\u6709\u4e0d\u5c11\u8352\u8bde\u4e0d\u7ecf\u7684\u731c\u6d4b\uff0c\u8ba9\u743c\u6069\u542c\u7740\u90fd\u60f3\u53d1\u7b11\uff0c\u540c\u65f6\u4e5f\u4e0d\u514d\u7591\u60d1\uff0c\u6885\u83f2\u65af\u7279\u5730\u6765\u9152\u9986\uff0c\u5c31\u662f\u4e3a\u4e86\u542c\u8fd9\u4e9b\u5b8c\u5168\u4e0d\u7740\u8fb9\u9645\u7684\u4f20\u8a00\uff1f</p><p>\u6070\u5728\u6b64\u65f6\uff0c\u4e00\u58f0\u5927\u559d\u54cd\u8d77\u3002</p><p>\u201c\u4f60\u4eec\u4ed6\u5988\u7684\u7edf\u7edf\u90fd\u5728\u653e\u5c41\uff01\u201d</p><p>\u743c\u6069\u626d\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u4e00\u4e2a\u4e09\u5341\u6765\u5c81\u7684\u7537\u5b50\u8d70\u8fc7\u6765\uff0c\u4ed6\u7684\u5934\u53d1\u7ea2\u5f97\u50cf\u4e00\u56e2\u70c8\u706b\uff0c\u7626\u524a\u7684\u8138\u988a\u4e0a\u6709\u9053\u957f\u957f\u7684\u5200\u75a4\uff0c\u4e00\u624b\u62ce\u7740\u9152\u74f6\uff0c\u53e6\u4e00\u624b\u6402\u7740\u4e00\u4f4d\u6d53\u5986\u8273\u62b9\u7684\u5973\u5b50\uff0c\u811a\u6b65\u8e09\u8dc4\uff0c\u6ee1\u8138\u901a\u7ea2\uff0c\u663e\u7136\u5df2\u7ecf\u559d\u5f97\u4e0d\u5c11\uff0c\u6709\u4e86\u4e03\u516b\u5206\u9189\u610f\u7684\u6a21\u6837\u3002\u89c1\u8fd9\u4eba\u8fc7\u6765\uff0c\u539f\u672c\u6b63\u5728\u70ed\u70c8\u4ea4\u8c08\u7684\u9152\u5ba2\u4eec\u7eb7\u7eb7\u8d77\u8eab\uff0c\u7ed9\u4ed6\u8ba9\u51fa\u4f4d\u7f6e\uff0c\u8fd9\u540d\u7537\u5b50\u4e5f\u6beb\u4e0d\u5ba2\u6c14\uff0c\u5927\u6447\u5927\u6446\u5730\u4e00\u5c41\u80a1\u5750\u4e0b\u6765\uff0c\u987a\u624b\u6293\u8d77\u4e00\u5757\u725b\u6392\u4e22\u8fdb\u5634\u91cc\uff0c\u56bc\u4e86\u51e0\u53e3\u53c8\u201c\u5478\u201d\u5730\u5410\u4e86\u51fa\u6765\u3002</p><p>\u201c\u96be\u5403\uff01\u201d\u4ed6\u7c97\u58f0\u7c97\u6c14\u5730\u8bf4\u3002</p><p>\u65c1\u8fb9\u7684\u9152\u5ba2\u4eec\u6beb\u4e0d\u5728\u610f\uff0c\u4eff\u4f5b\u4e60\u4ee5\u4e3a\u5e38\uff0c\u201c\u7f57\u6069\uff0c\u4f60\u6709\u4ec0\u4e48\u72ec\u5bb6\u65b0\u95fb\uff1f\u201d\u4e00\u4e2a\u5149\u5934\u7684\u6c34\u624b\u95ee\uff0c\u201c\u8bf4\u6765\u542c\u542c\uff0c\u4e0d\u4f1a\u662f\u5439\u725b\u5427\u3002\u201d</p><p>\u7f57\u6069\u4ece\u9f3b\u5b50\u91cc\u54fc\u4e86\u4e00\u58f0\u3002</p><p>\u201c\u524d\u4e9b\u65e5\u5b50\uff0c\u7ad6\u7434\u624b\u8054\u76df\u4e0e\u9f99\u5deb\u6559\u5728\u9634\u5f71\u8c37\u5927\u6218\u3002\u9f99\u5deb\u6559\u4e3b\u9500\u58f0\u533f\u8ff9\u591a\u5e74\uff0c\u8fd9\u6b21\u662f\u6709\u5907\u800c\u6765\uff0c\u6765\u8005\u4e0d\u5584\uff0c\u7ad6\u7434\u624b\u672c\u6765\u5c31\u662f\u4ed6\u7684\u624b\u4e0b\u8d25\u5c06\uff0c\u54ea\u91cc\u6253\u5f97\u8fc7\uff0c\u6700\u540e\u53ea\u597d\u53c8\u6545\u6280\u91cd\u65bd\uff0c\u8bf7\u795e\u7957\u4e0b\u51e1\u52a9\u9635\u3002\u9b54\u6cd5\u5973\u795e\u4eb2\u81ea\u51fa\u624b\uff0c\u7ed3\u679c\u5403\u4e86\u5927\u4e8f\u2014\u2014\u8fd9\u4e2a\u4f60\u4eec\u90fd\u77e5\u9053\u4e86\u5427\u3002\u201d</p><p>\u201c\u77e5\u9053\u77e5\u9053\uff0c\u201d\u5149\u5934\u6c34\u624b\u4e0d\u8010\u70e6\u5730\u8bf4\uff0c\u201c\u6709\u5c41\u5feb\u653e\u3002\u201d</p><p>\u201c\u90a3\u4f60\u4eec\u77e5\u9053\u9b54\u6cd5\u5973\u795e\u662f\u600e\u4e48\u53d7\u4f24\u7684\u5417\uff1f\u201d</p><p>\u6240\u6709\u4eba\u90fd\u4e00\u9f50\u6447\u5934\u3002</p><p>\u7f57\u6069\u5f97\u610f\u6d0b\u6d0b\u5730\u6643\u4e86\u6643\u8111\u888b\uff0c\u201c\u8428\u739b\u65af\u7279\u518d\u5389\u5bb3\uff0c\u4ed6\u6bd5\u7adf\u662f\u4e2a\u5deb\u5e08\uff0c\u9762\u5bf9\u6267\u638c\u9b54\u6cd5\u7684\u5973\u795e\uff0c\u4ed6\u5b8c\u5168\u4e0d\u662f\u5bf9\u624b\u3002\u773c\u770b\u5c31\u8981\u4e00\u8d25\u6d82\u5730\uff0c\u4ed6\u5b64\u6ce8\u4e00\u63b7\uff0c\u62ff\u81ea\u5df1\u7684\u7075\u9b42\u5b9d\u77f3\u4f5c\u4e3a\u796d\u54c1\uff0c\u4ece\u5730\u72f1\u4e2d\u53ec\u5524\u6765\u90aa\u9b54\u4e4b\u738b\u3002\u90a3\u4f4d\u9b54\u738b\u9a91\u7740\u9ed1\u8272\u7684\u68a6\u9b47\uff0c\u624b\u6301\u5bd2\u971c\u7b3c\u7f69\u7684\u957f\u5251\uff0c\u4e0e\u9b54\u6cd5\u5973\u795e\u5728\u9634\u5f71\u9547\u7684\u87ba\u65cb\u9ad8\u5854\u4e0a\u6fc0\u6218\u4e09\u767e\u56de\u5408\uff0c\u6700\u7ec8\u5973\u795e\u4ee5\u81ea\u8eab\u8d1f\u4f24\u4f5c\u4e3a\u4ee3\u4ef7\uff0c\u53d1\u52a8\u4e86\u4e00\u4e2a\u7981\u5492\uff0c\u5c06\u9b54\u738b\u6253\u56de\u5730\u72f1\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u743c\u6069\u53ea\u89c9\u65e0\u8bed\uff0c\u53ea\u89c1\u5176\u4ed6\u9152\u5ba2\u5374\u90fd\u542c\u5f97\u9891\u9891\u70b9\u5934\uff0c\u5341\u5206\u5165\u795e\u3002\u4ed6\u5fc3\u4e2d\u4e00\u52a8\uff0c\u770b\u4e86\u770b\u6885\u83f2\u65af\uff0c\u53d1\u73b0\u5c11\u5973\u867d\u7136\u53ea\u987e\u4f4e\u5934\u559d\u8461\u8404\u6c41\uff0c\u773c\u89d2\u4f59\u5149\u5374\u5206\u660e\u4e5f\u662f\u77a5\u5728\u90a3\u4e2a\u7f57\u6069\u8eab\u4e0a\u3002</p><p>\u7f57\u6069\u663e\u7136\u6ca1\u6709\u5bdf\u89c9\u81ea\u5df1\u5df2\u7ecf\u88ab\u76ef\u4e0a\uff0c\u81ea\u987e\u81ea\u5730\u5927\u8c08\u6709\u5173\u9634\u5f71\u8c37\u4e4b\u6218\u7684\u5404\u79cd\u7ec6\u8282\u3002\u743c\u6069\u542c\u4e86\u4e00\u4f1a\uff0c\u4e0d\u7531\u5f97\u54d1\u7136\uff0c\u8fd9\u5bb6\u4f19\u4e0d\u77e5\u9053\u4ece\u54ea\u91cc\u5f97\u6765\u7684\u6d88\u606f\uff0c\u8bf4\u5f97\u534a\u771f\u534a\u5047\uff0c\u4f3c\u662f\u800c\u975e\uff0c\u660e\u660e\u548c\u5b9e\u9645\u60c5\u51b5\u538b\u6839\u4e0d\u662f\u4e00\u56de\u4e8b\uff0c\u4f46\u504f\u504f\u8fd8\u53c8\u8a00\u4e4b\u51ff\u51ff\uff0c\u903b\u8f91\u4e25\u5bc6\uff0c\u63a8\u7406\u6e05\u6670\uff0c\u5934\u5934\u662f\u9053\uff0c\u743c\u6069\u89c9\u5f97\u81ea\u5df1\u5982\u679c\u7ee7\u7eed\u542c\u4e0b\u53bb\uff0c\u53ea\u6015\u90fd\u8981\u6000\u7591\u81ea\u5df1\u7684\u8bb0\u5fc6\u662f\u4e0d\u662f\u51fa\u9519\u4e86\u3002</p><p>\u201c\u8fd9\u5bb6\u4f19\u8bb2\u7684\u6545\u4e8b\uff0c\u7b80\u76f4\u6bd4\u73b0\u5b9e\u8fd8\u66f4\u50cf\u771f\u76f8\u3002\u201d\u4ed6\u5fcd\u4e0d\u4f4f\u4f4e\u58f0\u611f\u53f9\u3002</p><p>\u201c\u4e0d\u662f\u6545\u4e8b\u6bd4\u73b0\u5b9e\u66f4\u50cf\u771f\u76f8\uff0c\u662f\u73b0\u5b9e\u539f\u672c\u5c31\u6bd4\u6545\u4e8b\u66f4\u79bb\u5947\uff0c\u201d\u6885\u83f2\u65af\uff0c\u201c\u4f60\u73b0\u5728\u7ad9\u8d77\u6765\u5bf9\u522b\u4eba\u8bf4\uff0c\u9634\u5f71\u8c37\u4e4b\u6218\uff0c\u4f60\u624d\u662f\u4e00\u5207\u7684\u5173\u952e\u3002\u4e00\u4f4d\u5730\u72f1\u9b54\u59ec\u5bf9\u4f60\u6b7b\u5fc3\u584c\u5730\uff0c\u4e3a\u4e86\u4f60\u628a\u6240\u6709\u4eba\u90fd\u73a9\u5f04\u4e8e\u80a1\u638c\u4e4b\u4e0a\uff0c\u6700\u540e\u8fd8\u5e2e\u4f60\u4eb2\u624b\u91cd\u521b\u795e\u7957\uff0c\u5a01\u903c\u90aa\u86c7\u2014\u2014\u4f60\u89c9\u5f97\u8fd9\u4e2a\u7248\u672c\u6709\u6ca1\u6709\u4eba\u4f1a\u76f8\u4fe1\uff1f\u201d</p><p>\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u7acb\u523b\u6447\u5934\u3002\u5f00\u73a9\u7b11\uff0c\u8fd9\u6545\u4e8b\u534a\u70b9\u5408\u7406\u6027\u90fd\u6ca1\u6709\uff0c\u4e00\u542c\u5c31\u662f\u8352\u8bde\u65e0\u7a3d\uff0c\u53ef\u7b11\u4e4b\u6781\uff0c\u54ea\u91cc\u4f1a\u6709\u4eba\u76f8\u4fe1\u3002\u522b\u8bf4\u522b\u4eba\u4e86\uff0c\u8fde\u743c\u6069\u81ea\u5df1\u90fd\u4e0d\u4fe1\u3002</p><p>\u8bdd\u8bf4\u56de\u6765\uff0c\u771f\u76f8\u56fa\u7136\u4ee4\u4eba\u96be\u4ee5\u7f6e\u4fe1\uff0c\u4f46\u8fd9\u5bb6\u4f19\u80fd\u628a\u8c0e\u8a00\u8bf4\u5f97\u5982\u6b64\u5929\u8863\u65e0\u7f1d\uff0c\u4e5f\u7740\u5b9e\u662f\u4e0d\u7b80\u5355\uff0c\u8fd9\u53ef\u4e0d\u662f\u4e00\u822c\u4eba\u80fd\u505a\u5230\u7684\u4e8b\u60c5\u3002\u6216\u8005\u8bf4\uff0c\u4e00\u822c\u4eba\u4e5f\u4e0d\u4f1a\u95f2\u7740\u65e0\u804a\u8fd9\u4e48\u505a\u5427\u3002</p><p>\u6885\u83f2\u65af\u7ec8\u4e8e\u559d\u5b8c\u4e86\u5979\u90a3\u676f\u8461\u8404\u6c41\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u8d70\u5230\u90a3\u4e2a\u7f57\u6069\u80cc\u540e\uff0c\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u8180\u3002</p><p>\u201c\u563f\uff01\u8fd9\u5c0f\u599e\u597d\u6f02\u4eae\u554a\u3002\u201d</p><p>\u65c1\u8fb9\u7684\u9152\u5ba2\u4eec\u7eb7\u7eb7\u53d1\u51fa\u54c4\u7b11\uff0c\u8fd8\u6709\u4eba\u5bf9\u7740\u6885\u83f2\u65af\u5439\u8d77\u6765\u53e3\u54e8\u2014\u2014\u7136\u540e\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u4ed6\u53d1\u73b0\u81ea\u5df1\u7684\u5589\u5499\u50cf\u662f\u88ab\u67d0\u79cd\u4e1c\u897f\u5835\u4f4f\u4e86\uff0c\u522b\u8bf4\u53d1\u51fa\u58f0\u97f3\uff0c\u8fde\u547c\u5438\u90fd\u900f\u4e0d\u8fc7\u6765\u3002\u773c\u770b\u521a\u521a\u8fd8\u597d\u7aef\u7aef\u7684\u540c\u4f34\u7a81\u7136\u53d8\u5f97\u9762\u8272\u9752\u7d2b\uff0c\u53cc\u624b\u627c\u4f4f\u81ea\u5df1\u7684\u54bd\u5589\uff0c\u5012\u5728\u5730\u4e0a\u7ffb\u6eda\uff0c\u65c1\u8fb9\u7684\u4eba\u90fd\u5413\u4e86\u4e00\u5927\u8df3\u3002\u63a5\u7740\u5c31\u770b\u89c1\u4e00\u4e2a\u5e74\u8f7b\u4eba\u8d70\u8fc7\u6765\uff0c\u4fef\u8eab\u67e5\u770b\u4e86\u7247\u523b\uff0c\u201c\u55ef\uff0c\u8fd9\u597d\u50cf\u662f\u98df\u7269\u4e2d\u6bd2\uff0c\u201d\u5e74\u8f7b\u4eba\u8bf4\uff0c\u201c\u8d76\u5feb\u9001\u53bb\u6d17\u80c3\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u6551\u3002\u201d</p><p>\u9152\u5ba2\u4eec\u614c\u614c\u5f20\u5f20\u5730\u628a\u90a3\u4e2a\u5012\u9709\u86cb\u62ac\u8d70\u4e86\u3002\u743c\u6069\u76f4\u8d77\u8170\uff0c\u6b63\u597d\u8fce\u4e0a\u5c11\u5973\u7684\u76ee\u5149\uff0c\u201c\u90a3\u5bb6\u4f19\u6ca1\u4e8b\uff0c\u201d\u743c\u6069\u8d76\u5feb\u5206\u8fa8\uff0c\u201c\u6211\u6ca1\u6709\u771f\u7684\u4e0b\u6bd2\uff0c\u53ea\u662f\u8b66\u544a\u4ed6\u4e00\u4e0b\u800c\u5df2\uff0c\u8fc7\u51e0\u5206\u949f\u5c31\u597d\u3002\u201d</p><p>\u6885\u83f2\u65af\u55ef\u4e86\u4e00\u58f0\uff0c\u76ee\u5149\u8f6c\u5411\u7f57\u6069\uff0c\u540e\u8005\u4e00\u5f00\u59cb\u6709\u4e9b\u60ca\u8bb6\uff0c\u4f46\u540e\u6765\u4f3c\u4e4e\u662f\u8ba4\u51fa\u4e86\u6885\u83f2\u65af\uff0c\u8038\u4e86\u8038\u80a9\uff0c\u628a\u6000\u91cc\u7684\u5973\u5b50\u63a8\u5230\u4e00\u65c1\u3002\u201c\u6709\u4ec0\u4e48\u6307\u6559\uff0c\u5723\u6b66\u58eb\uff1f\u201d\u4ed6\u95ee\uff0c\u8bed\u6c14\u4e2d\u900f\u7740\u4e0d\u60c5\u4e0d\u613f\u3002</p><p>\u201c\u6211\u9700\u8981\u4e00\u4e9b\u8d44\u6599\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u5173\u4e8e\u4e1c\u57df\u7684\u3002\u6211\u8981\u53bb\u90a3\u8fb9\u4e00\u8d9f\uff0c\u529e\u70b9\u4e8b\u60c5\uff0c\u6216\u8bb8\u8fd8\u4f1a\u8003\u8003\u53e4\u4ec0\u4e48\u7684\u3002\u201d</p><p>\u7f57\u6069\u626d\u4e86\u626d\u8116\u5b50\uff0c\u7ad9\u8d77\u8eab\u6765\uff0c\u201c\u8d70\u5427\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u7f57\u6069\u5728\u524d\u9762\u5e26\u8def\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u4e0d\u8fdc\u4e0d\u8fd1\u5730\u8ddf\u5728\u540e\u9762\uff0c\u4fdd\u6301\u7740\u4e00\u5b9a\u8ddd\u79bb\u3002\u4ed6\u4eec\u7a7f\u8fc7\u57ce\u533a\u4e2d\u5fc3\uff0c\u671d\u897f\u5317\u90e8\u8d70\u53bb\uff0c\u8d8a\u8d70\u8d8a\u662f\u8352\u51c9\u51b7\u6e05\uff0c\u5468\u56f4\u7684\u884c\u4eba\u8d8a\u6765\u8d8a\u5c11\u3002\u4f46\u4e5f\u6ca1\u8d70\u591a\u4e45\uff0c\u5927\u7ea6\u5341\u6765\u5206\u949f\uff0c\u6885\u83f2\u65af\u505c\u4f4f\u811a\u6b65\uff0c\u201c\u5230\u4e86\u3002\u201d</p><p>\u8fd9\u662f\u4e00\u4e2a\u5c0f\u5df7\uff0c\u4e24\u65c1\u90fd\u662f\u5c45\u6c11\u4eba\u5bb6\uff0c\u4f46\u4f3c\u4e4e\u5df2\u7ecf\u957f\u671f\u65e0\u4eba\u5c45\u4f4f\uff0c\u843d\u6ee1\u7070\u5c18\u7684\u95e8\u7a97\u6837\u5f0f\u53e4\u65e7\uff0c\u904d\u5e03\u8513\u85e4\uff0c\u77f3\u9636\u4e0a\u722c\u6ee1\u4e86\u9752\u82d4\u3002\u7f57\u6069\u5f84\u76f4\u8d70\u5230\u5df7\u5c3e\uff0c\u5728\u5899\u58c1\u4e0a\u6709\u8282\u594f\u5730\u6572\u4e86\u5341\u51e0\u4e0b\uff0c\u7136\u540e\u4e00\u6247\u975e\u5e38\u9690\u853d\u7684\u95e8\u6162\u6162\u5730\u6253\u5f00\u4e86\u4e00\u6761\u7f1d\u3002\u7f57\u6069\u5148\u4fa7\u8eab\u6324\u8fdb\u53bb\uff0c\u8fc7\u4e86\u4e03\u516b\u79d2\u949f\uff0c\u4f38\u51fa\u534a\u4e2a\u80f3\u818a\u671d\u5916\u9762\u62db\u4e86\u62db\u624b\uff0c\u6885\u83f2\u65af\u4e8e\u662f\u4e5f\u968f\u4e4b\u8fdb\u53bb\uff0c\u540c\u65f6\u793a\u610f\u743c\u6069\u5728\u539f\u5730\u7b49\u5f85\u3002</p><p>\u5927\u7ea6\u8fc7\u4e86\u5c06\u8fd1\u4e00\u4e2a\u5c0f\u65f6\uff0c\u743c\u6069\u5df2\u7ecf\u65e0\u804a\u5230\u5c06\u5730\u9762\u4e0a\u77f3\u677f\u7684\u7eb9\u8def\u548c\u88c2\u7f1d\u90fd\u7814\u7a76\u4e86\u4e00\u904d\uff0c\u6885\u83f2\u65af\u7ec8\u4e8e\u8fd4\u56de\uff0c\u540c\u65f6\u8fd8\u62b1\u7740\u4e00\u5927\u5806\u7eb8\u5f20\uff0c\u5dee\u4e0d\u591a\u6709\u534a\u4eba\u9ad8\u3002</p><p>\u743c\u6069\u8d76\u5feb\u63a5\u8fc7\u6765\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u6709\u5173\u4e1c\u57df\u7684\u8d44\u6599\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u590d\u5236\u8d44\u6599\u7684\u4eba\u5de5\u8d39\uff0c\u7eb8\u5f20\u6cb9\u58a8\u6210\u672c\uff0c\u4ee5\u53ca\u7ed9\u795e\u6bbf\u7684\u6350\u732e\uff0c\u7b49\u7b49\u7b49\u7b49\uff0c\u4e00\u5171\u82b1\u8d39\u4e00\u5343\u516b\u767e\u4e03\u5341\u4e09\u679a\u91d1\u5e01\u3002\u6211\u62ff\u4f60\u7684\u94b1\u888b\u4ed8\u7684\uff0c\u56e0\u4e3a\u91d1\u5e01\u4e0d\u591f\uff0c\u6211\u7528\u5b9d\u77f3\u6298\u7b97\uff0c\u53e6\u52a0\u4e86\u767e\u5206\u4e4b\u5341\u7684\u624b\u7eed\u8d39\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u8d35\uff1f\u201d\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u4ed6\u6000\u91cc\u7684\u8fd9\u4e9b\u8d44\u6599\u5927\u6982\u4e5f\u5c31\u662f\u4e24\u5343\u9875\u5de6\u53f3\uff0c\u7b97\u8d77\u6765\u5e73\u5747\u6bcf\u9875\u4e00\u679a\u91d1\u5e01\uff0c\u8fd9\u6210\u672c\u5b9e\u5728\u662f\u6709\u4e9b\u9ad8\u5f97\u60ca\u4eba\u3002</p><p>\u201c\u77e5\u8bc6\u662f\u65e0\u4ef7\u7684\u561b\u3002\u201d\u5c11\u5973\u8bf4\u3002</p><p>\u201c\u2026\u2026\u201d</p><p>\u8fd4\u56de\u7684\u8def\u4e0a\uff0c\u743c\u6069\u7ec8\u4e8e\u641e\u6e05\u695a\u6885\u83f2\u65af\u521a\u624d\u53bb\u7684\u662f\u4ec0\u4e48\u5730\u65b9\u3002\u201c\u77e5\u8bc6\u795e\u6bbf\uff1f\u201d\u4ed6\u5947\u602a\uff0c\u201c\u600e\u4e48\u641e\u5f97\u8fd9\u4e48\u9690\u853d\uff0c\u6211\u8bb0\u5f97\u6b27\u683c\u739b\u4e0d\u662f\u90aa\u795e\u5427\u3002\u201d</p><p>\u6b27\u683c\u739b\u5f53\u7136\u4e0d\u662f\u90aa\u795e\uff0c\u4ed6\u662f\u77e5\u8bc6\u4e4b\u795e\uff0c\u5728\u8bf8\u795e\u4e4b\u4e2d\u5411\u6765\u5730\u4f4d\u8d85\u7136\uff0c\u7acb\u573a\u4e2d\u7acb\uff0c\u5176\u4fe1\u5f92\u591a\u4ee5\u201c\u8bb0\u5f55\u8005\u201d\u81ea\u5c45\u3002\u5343\u4e07\u5e74\u7684\u79ef\u7d2f\u548c\u4f20\u627f\uff0c\u8ba9\u6b27\u683c\u739b\u6559\u4f1a\u6210\u4e3a\u5927\u9646\u4e0a\u6700\u5927\u7684\u56fe\u4e66\u9986\u3001\u8d44\u6599\u5e93\u4ee5\u53ca\u516b\u5366\u8c23\u8a00\u53d1\u6e90\u5730\u2014\u2014\u6bd4\u5982\u743c\u6069\u4e4b\u524d\u53bb\u8fc7\u7684\u70db\u5821\u3002\u6885\u83f2\u65af\u53bb\u6b27\u683c\u739b\u795e\u6bbf\u8d2d\u4e70\u6709\u5173\u4e1c\u57df\u7684\u8d44\u6599\uff0c\u8fd9\u4e2a\u5f88\u6b63\u5e38\uff0c\u4f46\u8fd9\u5ea7\u795e\u6bbf\u5982\u6b64\u4f4e\u8c03\u795e\u79d8\uff0c\u641e\u5f97\u4eff\u4f5b\u5730\u4e0b\u7ec4\u7ec7\uff0c\u5c31\u8ba9\u4eba\u8ff7\u60d1\u4e0d\u89e3\u4e86\u3002</p><p>\u201c\u4ed6\u4eec\u662f\u2018\u9690\u79d8\u8005\u2019\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u77e5\u8bc6\u4e4b\u795e\u6559\u4f1a\u4e2d\u7684\u4e00\u4e2a\u6d3e\u522b\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u5c5e\u4e8e\u5c11\u6570\u6d3e\u3002\u201d</p><p>\u6885\u83f2\u65af\u5927\u81f4\u89e3\u91ca\u4e86\u4e00\u4e0b\uff0c\u743c\u6069\u65b9\u624d\u660e\u767d\u3002\u77e5\u8bc6\u4e4b\u795e\u6b27\u683c\u739b\u662f\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6700\u53e4\u8001\u7684\u9ad8\u7b49\u795e\u4e4b\u4e00\uff0c\u5386\u53f2\u60a0\u4e45\uff0c\u4fe1\u5f92\u4f17\u591a\uff0c\u6587\u827a\u4e4b\u795e\u8fea\u5948\u5c14\u3001\u5de5\u5320\u4e4b\u795e\u8d21\u5fb7\u3001\u97f3\u4e50\u4e4b\u795e\u5bc6\u9ece\u5c14\u90fd\u662f\u4ed6\u7684\u4ece\u795e\u3002\u6b27\u683c\u739b\u6559\u4f1a\u7684\u7ed3\u6784\u975e\u5e38\u677e\u6563\uff0c\u98ce\u6c14\u81ea\u7531\u6563\u6f2b\uff0c\u6559\u5185\u7684\u6d3e\u7cfb\u683c\u5916\u591a\uff0c\u4e0d\u540c\u6d3e\u7cfb\u7684\u5b66\u672f\u89c2\u70b9\u6709\u53ef\u80fd\u5357\u8f95\u5317\u8f99\uff0c\u5b8c\u5168\u5bf9\u7acb\u3002\u5176\u4e2d\u6709\u4e00\u6d3e\u81ea\u79f0\u201c\u9690\u79d8\u8005\u201d\uff0c\u4ed6\u4eec\u8ba4\u4e3a\u77e5\u8bc6\u4e43\u662f\u4e16\u95f4\u6700\u5371\u9669\u4e4b\u7269\uff0c\u7528\u4e4b\u4e0d\u5f53\u5219\u4f1a\u9020\u6210\u5de8\u5927\u7834\u574f\uff0c\u751a\u81f3\u6bc1\u706d\u4e16\u754c\uff0c\u56e0\u6b64\u9664\u4e86\u4e00\u4e9b\u6700\u57fa\u7840\u548c\u5fc5\u8981\u7684\u5e38\u8bc6\u4e4b\u5916\uff0c\u51e1\u4eba\u4e0d\u5e94\u5f53\u4e86\u89e3\u66f4\u591a\u3002</p><p>\u4e0e\u4e00\u822c\u7684\u6b27\u683c\u739b\u4fe1\u5f92\u4e0d\u540c\uff0c\u9690\u79d8\u8005\u4e0d\u4ec5\u4e0d\u81f4\u529b\u4e8e\u4f20\u64ad\u77e5\u8bc6\uff0c\u53cd\u800c\u66f4\u503e\u5411\u4e8e\u4fdd\u5b88\u79d8\u5bc6\uff0c\u4ed6\u4eec\u6709\u4e00\u5957\u8be6\u7ec6\u7684\u5206\u7ea7\u6807\u51c6\uff0c\u5c06\u4e16\u754c\u4e0a\u7684\u77e5\u8bc6\u6309\u7167\u5371\u9669\u7a0b\u5ea6\u5212\u5206\u4e3a\u4e0d\u540c\u7b49\u7ea7\uff0c\u7136\u540e\u4ed6\u4eec\u7684\u65e5\u5e38\u5de5\u4f5c\uff0c\u5c31\u662f\u60f3\u65b9\u8bbe\u6cd5\u5c06\u90a3\u4e9b\u6bd4\u8f83\u5371\u9669\u7684\u77e5\u8bc6\u5c01\u9501\u3001\u9690\u85cf\u8d77\u6765\uff0c\u4e0d\u8ba9\u66f4\u591a\u7684\u4eba\u77e5\u6653\u3002\u4e3a\u4e86\u8fbe\u5230\u8fd9\u4e2a\u76ee\u6807\uff0c\u201c\u9690\u79d8\u8005\u201d\u884c\u4e8b\u5f80\u5f80\u4e0d\u62e9\u624b\u6bb5\uff0c\u540d\u58f0\u81ea\u7136\u4e0d\u4f73\uff0c\u4e5f\u4e0d\u53d7\u6b22\u8fce\uff0c\u636e\u70b9\u4f4e\u8c03\u70b9\u662f\u5fc5\u987b\u7684\u3002\u53f6\u5f18\u57ce\u7684\u8fd9\u5ea7\u77e5\u8bc6\u795e\u6bbf\uff0c\u7b97\u8d77\u6765\u4e5f\u5df2\u7ecf\u6709\u4e03\u5341\u591a\u5e74\u7684\u5386\u53f2\u4e86\uff0c\u4f46\u77e5\u9053\u7684\u4eba\u5be5\u5be5\u65e0\u51e0\u3002</p><p>\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u4ed6\u4eec\u4e3a\u4ec0\u4e48\u4f1a\u7ed9\u4f60\u8fd9\u4e9b\u8d44\u6599\u5462\uff1f\u201d</p><p>\u201c\u4ed6\u4eec\u6b20\u6211\u4e00\u4e2a\u4eba\u60c5\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u53e6\u5916\uff0c\u8fd9\u4e9b\u6709\u5173\u4e1c\u57df\u7684\u8d44\u6599\uff0c\u5728\u4ed6\u4eec\u7684\u5206\u7ea7\u6807\u51c6\u4e2d\u5371\u9669\u7a0b\u5ea6\u5f88\u4f4e\uff0c\u5e76\u4e0d\u4e25\u7981\u6d41\u4f20\u3002\u201d</p><p>\u201c\u90a3\u4ec0\u4e48\u4e1c\u897f\u662f\u4ed6\u4eec\u8ba4\u4e3a\u975e\u5e38\u5371\u9669\uff0c\u4e25\u7981\u6d41\u4f20\u7684\u5462\uff1f\u201d\u743c\u6069\u9887\u4e3a\u597d\u5947\u3002</p><p>\u201c\u5f88\u591a\u554a\uff0c\u6bd4\u5982\u8bf4\u8fd9\u6b21\u9634\u5f71\u8c37\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u6309\u7167\u4ed6\u4eec\u7684\u6807\u51c6\uff0c\u5c31\u5c5e\u4e8e\u4e0d\u5e94\u5f53\u516c\u5f00\u7684\u90a3\u4e00\u7c7b\u3002\u201d</p><p>\u201c\u90a3\u4ed6\u4eec\u8fd8\u8dd1\u5230\u9152\u9986\u91cc\u80e1\u8bf4\u516b\u9053\u2014\u2014\u6211\u660e\u767d\u4e86\uff0c\u201d\u743c\u6069\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u8fd9\u5bb6\u4f19\u662f\u6545\u610f\u7684\u3002\u201d</p><p>\u201c\u63a9\u76d6\u771f\u76f8\u7684\u6700\u597d\u529e\u6cd5\uff0c\u4e0d\u662f\u6c89\u9ed8\uff0c\u800c\u662f\u8c23\u8a00\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u62b1\u7740\u4e00\u5927\u5806\u7eb8\u8d70\u8def\u5b9e\u5728\u592a\u9ebb\u70e6\uff0c\u5e78\u597d\u743c\u6069\u6709\u88c5\u5907\uff0c\u968f\u624b\u628a\u5b83\u4eec\u7edf\u7edf\u4e22\u8fdb\u6b21\u5143\u888b\u91cc\uff0c\u4e24\u624b\u7a7a\u7a7a\u987f\u65f6\u8f7b\u677e\u591a\u4e86\u3002\u53ea\u662f\u53f6\u5f18\u57ce\u7684\u5e02\u533a\u4ecd\u7136\u62e5\u6324\uff0c\u743c\u6069\u60f3\u7740\u5c3d\u91cf\u907f\u5f00\u4eba\u591a\u7684\u5730\u65b9\uff0c\u7ed3\u679c\u4e03\u8f6c\u516b\u7ed5\u4e4b\u4e0b\uff0c\u5dee\u70b9\u8ff7\u4e86\u8def\u3002\u201c\u8fd9\u57ce\u5e02\u5efa\u8bbe\u592a\u5dee\u4e86\u3002\u201d\u4ed6\u4e0d\u9ad8\u5174\u5730\u8bf4\u3002</p><p>\u201c\u5386\u53f2\u539f\u56e0\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u53f6\u5f18\u4ee5\u524d\u662f\u4e2a\u5c0f\u9547\uff0c\u4ee5\u5f00\u91c7\u77f3\u77ff\u4e3a\u4e3b\u8981\u7ecf\u6d4e\u6536\u5165\u6765\u6e90\u3002\u4e5f\u5c31\u662f\u8fd1\u4e09\u5341\u5e74\u822a\u6d77\u6280\u672f\u8fdb\u6b65\uff0c\u6d77\u4e0a\u8d38\u6613\u53d1\u8fbe\uff0c\u624d\u968f\u4e4b\u5174\u76db\u8d77\u6765\uff0c\u4eba\u53e3\u9aa4\u7136\u589e\u591a\uff0c\u4f46\u9002\u5408\u5c45\u4f4f\u7684\u5730\u65b9\u5c31\u8fd9\u4e48\u5927\uff0c\u57ce\u5e02\u627f\u53d7\u4e0d\u4f4f\uff0c\u5c31\u53d8\u6210\u73b0\u5728\u8fd9\u6837\u4e86\u3002\u201d</p><p>\u201c\u8fd8\u662f\u6ca1\u6709\u7ba1\u7406\u597d\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5982\u679c\u6211\u6765\u5f53\u57ce\u4e3b\uff0c\u80af\u5b9a\u4e0d\u4f1a\u5f04\u6210\u73b0\u5728\u8fd9\u79cd\u6837\u5b50\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d\u5c11\u5973\u7b11\u76c8\u76c8\u5730\u8bf4\u3002</p><p>\u201c\u90a3\u5f53\u7136\uff0c\u201d\u743c\u6069\u9887\u6709\u81ea\u4fe1\uff0c\u201c\u6211\u53ef\u662f\u66fe\u7ecf\u5728\u6709\u4e0a\u5343\u4e07\u4eba\u53e3\u7684\u57ce\u5e02\u91cc\u5c45\u4f4f\u751f\u6d3b\u8fc7\u5462\u3002\u201d</p><p>\u53f6\u5f18\u57ce\u7684\u672c\u5730\u5c45\u6c11\u52a0\u4e0a\u5916\u6765\u4eba\u53e3\uff0c\u6700\u591a\u4e5f\u4e0d\u8d85\u8fc7\u4e94\u4e07\uff0c\u6bd4\u8d77\u743c\u6069\u8bb0\u5fc6\u4e2d\u90a3\u4e9b\u56fd\u9645\u5927\u90fd\u5e02\uff0c\u5145\u5176\u91cf\u53ea\u80fd\u7b97\u4e2a\u4e61\u9547\u3002\u201c\u5728\u57ce\u5e02\u91cc\u751f\u6d3b\u8fc7\uff0c\u548c\u7ba1\u7406\u5b83\u662f\u4e24\u7801\u4e8b\u5427\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u201c\u54e6\uff0c\u6211\u8fd8\u73a9\u8fc7\u6a21\u62df\u57ce\u5e02\uff0c\u56db\u4ee3\u548c\u4e94\u4ee3\u90fd\u73a9\u8fc7\u3002\u201d</p><p>\u201c\u2026\u2026\u90a3\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d</p><p>\u201c\u4e00\u79cd\u8bad\u7ec3\u4eba\u5982\u4f55\u5f53\u57ce\u4e3b\u7684\u6e38\u620f\uff0c\u6211\u73a9\u5f97\u5f88\u597d\u7684\uff0c\u201d\u743c\u6069\u611f\u53f9\uff0c\u201c\u53ef\u60dc\u82f1\u96c4\u65e0\u7528\u6b66\u4e4b\u5730\uff0c\u6ca1\u6709\u8c01\u9001\u4e00\u5ea7\u57ce\u5e02\u6765\u8ba9\u6211\u65bd\u5c55\u624d\u534e\u3002\u201d</p><p>\u201c\u8428\u739b\u65af\u7279\u4e0d\u662f\u8bf4\u6709\u4e00\u5ea7\u6d6e\u7a7a\u57ce\u5728\u6069\u745f\u5417\uff0c\u201d\u6885\u83f2\u65af\u63d0\u9192\uff0c\u201c\u5730\u56fe\u90fd\u7559\u7ed9\u4f60\u4e86\u3002\u201d</p><p>\u201c\u8c01\u77e5\u9053\u90a3\u5bb6\u4f19\u8bf4\u7684\u662f\u771f\u662f\u5047\u3002\u5c31\u7b97\u662f\u771f\u7684\uff0c\u8fd9\u90fd\u51e0\u767e\u5e74\u8fc7\u53bb\u4e86\uff0c\u5f88\u53ef\u80fd\u5df2\u7ecf\u88ab\u4eba\u6377\u8db3\u5148\u767b\uff0c\u201d\u743c\u6069\u5174\u81f4\u4e0d\u662f\u5f88\u9ad8\uff0c\u201c\u53cd\u6b63\u6709\u65f6\u95f4\u7684\u8bdd\uff0c\u5c31\u987a\u4fbf\u53bb\u627e\u627e\u770b\u5427\u3002\u201d</p><p>\u6885\u83f2\u65af\u6447\u4e86\u6447\u5934\uff0c\u201c\u4e0d\u5bf9\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u8428\u739b\u65af\u7279\u6ca1\u5fc5\u8981\u9a97\u4f60\uff0c\u90a3\u5ea7\u6d6e\u7a7a\u57ce\u5e94\u8be5\u786e\u5b9e\u5b58\u5728\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u800c\u4e14\u5b83\u5e94\u8be5\u662f\u4f60\u6b64\u6b21\u4e1c\u57df\u4e4b\u884c\u6700\u91cd\u8981\u7684\u76ee\u6807\u4e4b\u4e00\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u6211\u53c8\u4e0d\u662f\u771f\u7684\u5f88\u60f3\u5f53\u4ec0\u4e48\u57ce\u4e3b\uff0c\u524d\u9762\u662f\u5f00\u73a9\u7b11\u800c\u5df2\u5566\u2014\u2014\u96be\u9053\u4f60\u60f3\u5f53\uff1f\u201d</p><p>\u201c\u53ef\u4ee5\u554a\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u6211\u5f53\u4e0a\u57ce\u4e3b\u9881\u53d1\u7684\u7b2c\u4e00\u9053\u547d\u4ee4\uff0c\u5c31\u662f\u628a\u50cf\u4f60\u8fd9\u79cd\u5927\u96be\u4e34\u5934\u8fd8\u61f5\u61c2\u4e0d\u89c9\u7684\u7b28\u86cb\u7edf\u7edf\u5173\u7981\u95ed\u3002\u201d</p><p>\u201c\u6b64\u8bdd\u600e\u8bb2\uff1f\u201d</p><p>\u51e0\u4e2a\u5b69\u5b50\u5b09\u7b11\u6253\u95f9\u7740\uff0c\u4ece\u8fdc\u5904\u8dd1\u8fc7\u6765\uff0c\u7528\u597d\u5947\u7684\u773c\u795e\u770b\u7740\u8fd9\u5bf9\u9752\u5e74\u7537\u5973\u3002\u5df7\u5b50\u5f88\u7a84\uff0c\u6885\u83f2\u65af\u5fae\u7b11\u7740\uff0c\u4fa7\u8eab\u8ba9\u5f00\u8def\uff0c\u7b49\u4ed6\u4eec\u8dd1\u8fdc\u4e4b\u540e\uff0c\u5c11\u5973\u7f13\u7f13\u5f00\u53e3\u3002\u201c\u743c\u6069\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8fd9\u6b21\u5927\u6218\uff0c\u4f60\u6ca1\u53d1\u73b0\u6709\u4e00\u4ef6\u4e8b\u60c5\u5f88\u5947\u602a\u5417\uff1f\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u9634\u9b42\u57ce\u5728\u54ea\u91cc\uff1f\u201d</p><p>\u9634\u5f71\u8c37\u4e4b\u6218\uff0c\u654c\u5bf9\u53cc\u65b9\u662f\u4ee5\u9009\u6c11\u4e3a\u4e3b\u529b\u7684\u9b54\u6cd5\u5973\u795e\u6559\u4f1a\uff0c\u4ee5\u53ca\u8428\u9a6c\u65af\u7279\u5377\u571f\u91cd\u6765\u53ec\u96c6\u7684\u9f99\u5deb\u6559\uff0c\u8fd9\u4e24\u65b9\u90fd\u662f\u5927\u9646\u4e0a\u4e3e\u8db3\u8f7b\u91cd\u7684\u52bf\u529b\uff0c\u5386\u53f2\u60a0\u4e45\uff0c\u6069\u6028\u7ea0\u845b\u4e5f\u540c\u6837\u60a0\u4e45\uff0c\u8fd9\u4e00\u6218\u7684\u80dc\u8d1f\u4e0d\u4ec5\u4ec5\u5173\u7cfb\u81ea\u8eab\uff0c\u540c\u65f6\u4e5f\u5bf9\u5927\u9646\u7684\u5c40\u52bf\u6709\u7740\u6df1\u8fdc\u5f71\u54cd\u3002\u56e0\u6b64\u5728\u6574\u4e2a\u8fc7\u7a0b\u4e2d\uff0c\u5176\u4ed6\u6216\u660e\u6216\u6697\u53c2\u4e0e\u5176\u4e2d\u7684\u52bf\u529b\u5176\u5b9e\u4e0d\u5c11\uff0c\u9b54\u6cd5\u5973\u795e\u6559\u4f1a\u8fd9\u8fb9\uff0c\u9664\u4e86\u5173\u7cfb\u6bd4\u8f83\u5bc6\u5207\u7684\u7ad6\u7434\u624b\u540c\u76df\u4e4b\u5916\uff0c\u6bd4\u5982\u63d0\u5c14\u6559\u4f1a\u6d3e\u51fa\u4e86\u6885\u83f2\u65af\u53bb\u5e2e\u5fd9\u641e\u5b9a\u9f99\u5deb\u5996\uff0c\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u63d0\u4f9b\u4e86\u5f15\u7206\u9ece\u660e\u4e4b\u77f3\u7684\u65b9\u6848\uff0c\u7b49\u7b49\uff1b\u800c\u5728\u9f99\u5deb\u6559\u90a3\u8fb9\uff0c\u4e5f\u6709\u50cf\u5e0c\u745e\u514b\u9009\u6c11\u3001\u6697\u591c\u9762\u5177\u8fd9\u4e9b\u6210\u540d\u5df2\u4e45\u7684\u53cd\u6d3e\u7ec4\u7ec7\u9732\u9762\u3002\u8fd9\u4e9b\u8fd8\u4ec5\u4ec5\u53ea\u662f\u743c\u6069\u77e5\u9053\u7684\uff0c\u6f5c\u4f0f\u5728\u6697\u5904\u9ed8\u9ed8\u5173\u6ce8\u7684\u6216\u8bb8\u66f4\u591a\u3002\u90a3\u4e48\u95ee\u9898\u51fa\u73b0\u4e86\uff1a\u9634\u9b42\u57ce\u5728\u5e72\u561b\u5462\uff1f</p><p>\u9634\u9b42\u57ce\u548c\u9f99\u5deb\u6559\u5173\u7cfb\u5982\u4f55\uff0c\u743c\u6069\u4e0d\u662f\u9ad8\u5c42\uff0c\u4e0d\u6562\u5984\u8a00\uff0c\u4f46\u548c\u9b54\u6cd5\u5973\u795e\u6559\u4f1a\u662f\u6b7b\u654c\uff0c\u8fd9\u70b9\u6bcb\u5eb8\u7f6e\u7591\u3002\u6309\u7167\u5e38\u7406\uff0c\u8fd9\u4e24\u8fb9\u706b\u62fc\u8d77\u6765\uff0c\u8981\u6253\u4e2a\u4f60\u6b7b\u6211\u6d3b\u7684\u67b6\u52bf\uff0c\u9634\u9b42\u57ce\u5c31\u7b97\u4e0d\u76f4\u63a5\u4e0b\u573a\u5e2e\u5fd9\uff0c\u4e5f\u6ca1\u9053\u7406\u4e00\u526f\u5b8c\u5168\u4e8b\u4e0d\u5173\u5df1\u7684\u6a21\u6837\u3002\u4f46\u4e8b\u5b9e\u662f\uff0c\u6574\u4e2a\u9634\u5f71\u8c37\u4e4b\u6218\u4e2d\uff0c\u9634\u9b42\u57ce\u4f3c\u4e4e\u5b8c\u5168\u7f6e\u8eab\u4e8b\u5916\uff0c\u8fde\u6d3e\u4e2a\u89c2\u5bdf\u5458\u53bb\u505a\u8bb0\u5f55\u7684\u5174\u81f4\u90fd\u6ca1\u6709\uff0c\u8fd9\u5c31\u592a\u5947\u602a\u4e86\u3002</p><p>\u5514\uff0c\u4e0d\u5bf9\uff0c\u8bf4\u9634\u9b42\u57ce\u5b8c\u5168\u6ca1\u6709\u53c2\u4e0e\u4e5f\u4e0d\u51c6\u786e\u2014\u2014\u4e0d\u662f\u8fd8\u6709\u743c\u6069\u4e48\u3002\u5982\u679c\u628a\u743c\u6069\u89c6\u4e3a\u9634\u9b42\u57ce\u7684\u4ee3\u8868\u7684\u8bdd\uff0c\u90a3\u7b80\u76f4\u53ef\u4ee5\u8bf4\u662f\u9634\u9b42\u57ce\u4e00\u624b\u5de6\u53f3\u4e86\u6218\u5c40\u7684\u6700\u7ec8\u8d70\u5411\u3002</p><p>\u201c\u5982\u679c\u6211\u6ca1\u8bb0\u9519\uff0c\u4f60\u4e4b\u524d\u7684\u6bcf\u4e00\u6b21\u65c5\u884c\uff0c\u90fd\u662f\u5949\u547d\u800c\u4e3a\uff0c\u6709\u660e\u786e\u7684\u4efb\u52a1\u5728\u8eab\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u6211\u662f\u9634\u9b42\u57ce\u7684\u516c\u52a1\u5458\u561b\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6bcf\u4e2a\u6708\u9886\u4e86\u85aa\u6c34\uff0c\u603b\u8981\u5e72\u70b9\u6d3b\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u6b21\u5462\uff1f\u201d\u6885\u83f2\u65af\u53cd\u95ee\uff0c\u201c\u4f60\u53bb\u9634\u5f71\u9547\uff0c\u662f\u9634\u9b42\u57ce\u7684\u6307\u4ee4\uff1f\u201d</p><p>\u743c\u6069\u6447\u6447\u5934\uff0c\u201c\u4e0d\u662f\u3002\u201d</p><p>\u6885\u83f2\u65af\u8bf4\u5f97\u6ca1\u9519\uff0c\u4ed6\u4e4b\u524d\u6bcf\u6b21\u79bb\u5f00\u9634\u9b42\u57ce\uff0c\u65e0\u4e00\u4f8b\u5916\u662f\u6267\u884c\u4efb\u52a1\u3002\u53bb\u535a\u5f97\u4e4b\u95e8\uff0c\u662f\u5949\u547d\u7ed9\u62c9\u6c83\u514b\u9001\u4fe1\uff1b\u53bb\u585e\u5c14\uff0c\u662f\u53d7\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u53ec\u5524\uff1b\u53bb\u5e7d\u6697\u5730\u57df\uff0c\u662f\u519b\u4e8b\u884c\u52a8\u3002\u6bcf\u6b21\u90fd\u662f\u4e0a\u7ea7\u660e\u786e\u6307\u4ee4\uff0c\u6709\u6e05\u695a\u7684\u4efb\u52a1\u76ee\u6807\uff0c\u5b8c\u6210\u4e4b\u540e\u8981\u56de\u57ce\u590d\u547d\uff0c\u81f3\u4e8e\u6709\u65f6\u5019\u9014\u4e2d\u51fa\u73b0\u4e00\u4e9b\u610f\u5916\uff0c\u6bd4\u5982\u6389\u8fdb\u6df1\u6e0a\uff0c\u5377\u5165\u8840\u6218\u4e4b\u7c7b\uff0c\u90a3\u662f\u53e6\u4e00\u7801\u4e8b\u3002\u800c\u8fd9\u4e00\u6b21\u5219\u4e0d\u540c\uff0c\u6ca1\u6709\u660e\u786e\u7684\u6307\u4ee4\uff0c\u6ca1\u6709\u5177\u4f53\u7684\u4efb\u52a1\uff0c\u9664\u4e86\u6700\u5f00\u59cb\u53bb\u534f\u52a9\u6253\u635e\u661f\u9668\u57ce\uff0c\u8fd8\u7b97\u662f\u5e26\u70b9\u5de5\u4f5c\u6027\u8d28\uff0c\u4e4b\u540e\u7684\u4e00\u5207\u5c31\u5b8c\u5168\u662f\u743c\u6069\u7684\u4e2a\u4eba\u884c\u4e3a\u4e86\uff0c\u5305\u62ec\u53bb\u5854\u745f\u8c37\uff0c\u53bb\u9634\u5f71\u9547\uff0c\u90fd\u662f\u4ed6\u81ea\u884c\u51b3\u5b9a\uff0c\u4e0e\u9634\u9b42\u57ce\u6ca1\u6709\u4efb\u4f55\u5173\u7cfb\u2014\u2014\u81f3\u5c11\u8868\u9762\u4e0a\u770b\u8d77\u6765\u662f\u8fd9\u6837\u3002</p><p>\u5f53\u7136\uff0c\u4e16\u4e8b\u96be\u6599\uff0c\u53d8\u5e7b\u83ab\u6d4b\uff0c\u9634\u9b42\u57ce\u4e5f\u4e0d\u53ef\u80fd\u603b\u662f\u80fd\u591f\u6d1e\u6089\u5148\u673a\u3002\u5982\u679c\u4e8b\u524d\u6ca1\u6709\u60f3\u5230\u4f1a\u6f14\u53d8\u6210\u8fd9\u79cd\u5c40\u9762\uff0c\u6240\u4ee5\u53cd\u5e94\u6162\u4e86\u4e00\u6b65\uff0c\u5012\u4e5f\u8bf4\u5f97\u8fc7\u53bb\u3002\u4f46\u4e8b\u60c5\u5df2\u7ecf\u8fc7\u53bb\u4e94\u516d\u5929\uff0c\u8fde\u9152\u9986\u91cc\u7684\u9189\u9b3c\u90fd\u5728\u6d25\u6d25\u4e50\u9053\uff0c\u8c23\u8a00\u6ee1\u5929\u98de\uff0c\u9634\u9b42\u57ce\u5374\u4ecd\u7136\u6ca1\u6709\u534a\u70b9\u53cd\u5e94\uff0c\u8fd9\u5c31\u5b9e\u5728\u5f88\u4e0d\u6b63\u5e38\u3002</p><p>\u201c\u4e4b\u524d\u7684\u4e8b\u60c5\u4e14\u4e0d\u8bba\uff0c\u4f60\u73b0\u5728\u6253\u7b97\u53bb\u4e1c\u57df\uff0c\u77ed\u5219\u4e09\u4e94\u4e2a\u6708\uff0c\u957f\u5219\u4e00\u4e24\u5e74\uff0c\u662f\u80af\u5b9a\u6ca1\u6cd5\u8fd4\u56de\u4e86\u3002\u4f60\u6709\u8fd9\u4e48\u957f\u7684\u5047\u671f\u5417\uff1f\u9634\u9b42\u57ce\u7684\u516c\u52a1\u5458\u798f\u5229\u5f85\u9047\u6709\u8fd9\u4e48\u597d\u5417\uff1f\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f60\u505c\u5728\u8fd9\u91cc\uff0c\u662f\u60f3\u8bd5\u63a2\u9634\u9b42\u57ce\u7684\u53cd\u5e94\u5427\uff0c\u7ed3\u679c\u5982\u4f55\uff1f\u201d</p><p>\u743c\u6069\u4e4b\u6240\u4ee5\u5728\u53f6\u5f18\u57ce\u6682\u65f6\u505c\u7559\uff0c\u4e00\u65b9\u9762\u662f\u7b49\u6885\u83f2\u65af\u82cf\u9192\uff0c\u53e6\u4e00\u65b9\u9762\u8ba9\u838e\u73de\u514b\u653e\u51fa\u4ed6\u8981\u53bb\u4e1c\u57df\u7684\u6d88\u606f\uff0c\u8bd5\u63a2\u53cd\u5e94\uff0c\u7ed3\u679c\u5374\u662f\u7b49\u4e86\u56db\u4e94\u5929\uff0c\u4ec0\u4e48\u90fd\u6ca1\u7b49\u5230\u3002\u603b\u4e0d\u81f3\u4e8e\u8bf4\u9634\u9b42\u57ce\u8fde\u743c\u6069\u90fd\u627e\u4e0d\u5230\u5427\uff0c\u90a3\u4e5f\u672a\u514d\u592a\u5c0f\u770b\u90a3\u7fa4\u9634\u9b42\u738b\u5b50\u4eec\u4e86\u3002</p><p>\u201c\u770b\u8d77\u6765\uff0c\u597d\u50cf\u6ca1\u4eba\u7ba1\u6211\u4e86\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u6211\u4eec\u4e4b\u524d\u66fe\u7ecf\u731c\u6d4b\u8fc7\uff0c\u5bf9\u4e8e\u9634\u9b42\u57ce\u800c\u8a00\uff0c\u4f60\u662f\u4e00\u4e2a\u975e\u5e38\u2018\u91cd\u8981\u2019\u7684\u4eba\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201d\u55ef\u3002\u201c</p><p>\u5728\u6df1\u6e0a\u65ad\u57df\u9547\u4e2d\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u66fe\u7ecf\u6709\u8fc7\u4e00\u6b21\u957f\u8c08\uff0c\u636e\u4ed6\u4eec\u5f53\u65f6\u7684\u5206\u6790\uff0c\u743c\u6069\u5e76\u4e0d\u50cf\u4ed6\u8868\u9762\u4e0a\u770b\u8d77\u6765\u90a3\u6837\u81ea\u7531\u81ea\u5728\uff0c\u800c\u662f\u4e00\u679a\u68cb\u5b50\uff0c\u9634\u9b42\u57ce\u5bf9\u4ed6\u5bc4\u4e88\u539a\u671b\uff08\u65e0\u8bba\u8fd9\u79cd\u539a\u671b\u662f\u4ec0\u4e48\uff0c\u662f\u597d\u662f\u574f\uff09\uff0c\u671f\u5f85\u4ed6\u5728\u672a\u6765\u7684\u67d0\u4e2a\u65f6\u523b\uff0c\u80fd\u591f\u53d1\u6325\u67d0\u79cd\u91cd\u8981\u7684\u4f5c\u7528\u3002</p><p>\u6216\u8bb8\u8fd9\u79cd\u60f3\u6cd5\u6709\u70b9\u81ea\u604b\uff0c\u4f46\u4e8b\u5b9e\u7684\u786e\u5c31\u662f\u5982\u6b64\uff0c\u800c\u4e14\u8d8a\u6765\u8d8a\u591a\u7684\u8ff9\u8c61\u4e5f\u5728\u8bc1\u660e\u8fd9\u4e00\u70b9\u3002\u6bd4\u5982\u8bf4\uff0c\u743c\u6069\u6781\u6709\u53ef\u80fd\u662f\u67d0\u4f4d\u4f0a\u739b\u65af\u5361\u7687\u5ba4\u5947\u68b0\u5e08\u8f6c\u4e16\uff0c\u4ed6\u53ef\u4ee5\u4f7f\u7528\u5a01\u529b\u5de8\u5927\u7684\u79d8\u5668\uff0c\u751a\u81f3\u80fd\u591f\u5bf9\u6297\u795e\u660e\uff0c\u4ed6\u6709\u6df1\u4e0d\u53ef\u6d4b\u7684\u80cc\u666f\uff0c\u5730\u72f1\u5927\u9b54\u9b3c\u90fd\u662f\u4ed6\u7684\u5ba0\u59ec\u2014\u2014\u5982\u679c\u9634\u9b42\u57ce\u77e5\u9053\u8fd9\u4e00\u70b9\uff0c\u5e76\u4e14\u662f\u4ece\u4e00\u5f00\u59cb\u5c31\u77e5\u9053\u8fd9\u4e00\u70b9\uff0c\u90a3\u4e48\u65e0\u8bba\u600e\u4e48\u91cd\u89c6\u743c\u6069\u90fd\u4e0d\u4e3a\u8fc7\u3002</p><p>\u201c\u5982\u679c\u8fd9\u79cd\u731c\u6d4b\u6210\u7acb\uff0c\u90a3\u4e48\u9634\u9b42\u57ce\u6709\u4ec0\u4e48\u7406\u7531\u4f1a\u8ba9\u4f60\u7a81\u7136\u8131\u79bb\u63a7\u5236\uff1f\u201d</p><p>\u201c\u4e5f\u4e0d\u7b97\u8131\u79bb\u63a7\u5236\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u591c\u5973\u58eb\u5728\u6211\u7684\u68a6\u4e2d\u51fa\u73b0\u8fc7\uff0c\u4e24\u6b21\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u743c\u6069\u5bf9\u5979\u6ca1\u6709\u9690\u7792\u4efb\u4f55\u7ec6\u8282\uff0c\u201c\u4f46\u7b2c\u4e00\u6b21\u662f\u5728\u4f60\u5df2\u7ecf\u8fdb\u5165\u9634\u5f71\u9547\u4e4b\u540e\uff0c\u7b2c\u4e8c\u6b21\u662f\u4f60\u5df2\u7ecf\u51b3\u5b9a\u53bb\u4e1c\u57df\u4e4b\u540e\uff0c\u5bf9\u5427\u3002\u5979\u662f\u7ed9\u4e86\u4f60\u4e00\u4e9b\u6307\u793a\uff0c\u4f46\u4e25\u683c\u6765\u8bf4\uff0c\u90a3\u4e5f\u5e76\u4e0d\u662f\u771f\u6b63\u7684\u4efb\u52a1\u3002\u4e5f\u5373\u662f\u8bf4\uff0c\u4ecd\u7136\u662f\u4f60\u81ea\u5df1\u5728\u505a\u51b3\u5b9a\uff0c\u5979\u6700\u591a\u53ea\u7b97\u662f\u987a\u52bf\u800c\u4e3a\u3002\u800c\u4e14\uff0c\u5982\u679c\u6211\u4eec\u4e4b\u524d\u7684\u731c\u6d4b\u6ca1\u9519\uff0c\u9634\u9b42\u57ce\u548c\u838e\u5c14\uff0c\u5728\u5bf9\u4e8e\u4f60\u7684\u95ee\u9898\u4e0a\u5e76\u4e0d\u4e00\u5b9a\u7acb\u573a\u4e00\u81f4\uff0c\u5bf9\u5427\uff1f\u201d</p><p>\u743c\u6069\u65e0\u6cd5\u56de\u7b54\u8fd9\u4e2a\u95ee\u9898\uff0c\u4e00\u76f4\u4ee5\u6765\uff0c\u81ea\u5df1\u611f\u89c9\u5c31\u50cf\u662f\u4e00\u679a\u68cb\u5b50\uff0c\u603b\u662f\u5728\u4ed6\u4eba\u7684\u64cd\u63a7\u4e4b\u4e2d\uff0c\u6240\u6709\u8def\u7ebf\u90fd\u662f\u88ab\u9884\u5148\u5b89\u6392\u597d\u7684\uff0c\u800c\u68cb\u624b\u65e0\u7591\u5c31\u662f\u9634\u9b42\u57ce\uff1b\u8fd9\u4e2a\u611f\u89c9\u6216\u8bb8\u6ca1\u6709\u9519\uff0c\u90a3\u4e48\u95ee\u9898\u6765\u4e86\uff0c\u65e2\u7136\u4ed6\u662f\u4e00\u4e2a\u5982\u6b64\u91cd\u8981\u7684\u68cb\u5b50\uff0c\u4e4b\u524d\u4e5f\u4e00\u76f4\u88ab\u5173\u6ce8\uff0c\u88ab\u63a7\u5236\uff0c\u90a3\u4e3a\u4ec0\u4e48\u7a81\u7136\u5c31\u53d8\u5f97\u5982\u6b64\u81ea\u7531\u4e86\u5462\uff1f\u60f3\u53bb\u54ea\u5c31\u53bb\u54ea\uff0c\u60f3\u5e72\u561b\u5c31\u5e72\u561b\uff0c\u522b\u8bf4\u4e8b\u524d\u5e72\u6d89\uff0c\u8fde\u4e8b\u540e\u6765\u95ee\u4e00\u53e5\u7684\u4eba\u90fd\u6ca1\u6709\uff0c\u8fd9\u4e5f\u592a\u8be1\u5f02\u4e86\u5427\u3002</p><p>\u201c\u6240\u4ee5\u4f60\u7684\u5224\u65ad\u662f\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u6211\u89c9\u5f97\uff0c\u65e0\u975e\u4e24\u79cd\u53ef\u80fd\uff0c\u4e00\u79cd\u5c31\u662f\u4ed6\u4eec\u4ecd\u7136\u5728\u5e55\u540e\u63a7\u5236\uff0c\u4f60\u6240\u505a\u7684\u4e00\u5207\uff0c\u770b\u4f3c\u5b8c\u5168\u81ea\u7531\uff0c\u5b9e\u9645\u4e0a\u90fd\u662f\u5728\u63a7\u5236\u4e4b\u4e2d\uff0c\u81f3\u5c11\u662f\u5728\u522b\u4eba\u7684\u9884\u6599\u4e4b\u5185\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u8fd9\u4e5f\u4e0d\u662f\u6ca1\u6709\u53ef\u80fd\u3002\u4f60\u6765\u5854\u745f\u8c37\uff0c\u8fd9\u5f88\u597d\u9884\u6d4b\uff0c\u56e0\u4e3a\u6211\u5728\uff1b\u4f60\u53bb\u9634\u5f71\u8c37\uff0c\u8fd9\u4e5f\u4e0d\u96be\u9884\u6d4b\uff0c\u56e0\u4e3a\u6211\u5148\u53bb\u4e86\u9634\u5f71\u8c37\u3002\u800c\u6211\u53bb\u9634\u5f71\u8c37\uff0c\u867d\u7136\u662f\u6559\u4f1a\u6307\u4ee4\uff0c\u4f46\u4e5f\u662f\u60c5\u7406\u4e4b\u4e2d\u7684\u4e8b\u60c5\u3002\u5854\u745f\u8c37\u548c\u9634\u5f71\u8c37\u4e0d\u8fdc\uff0c\u8428\u9a6c\u65af\u7279\u5927\u4e3e\u8fdb\u653b\uff0c\u6211\u4eec\u6559\u4f1a\u4e0d\u53ef\u80fd\u5f53\u771f\u8896\u624b\u65c1\u89c2\uff0c\u603b\u8981\u6709\u6240\u8868\u793a\uff0c\u6211\u88ab\u6d3e\u53bb\u7684\u53ef\u80fd\u6027\u5f88\u9ad8\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5982\u679c\u8fd9\u6837\u5206\u6790\uff0c\u5176\u5b9e\u4f60\u7684\u884c\u52a8\u8def\u7ebf\u90fd\u662f\u5b8c\u5168\u53ef\u4ee5\u9884\u6d4b\u7684\u3002\u751a\u81f3\u6709\u53ef\u80fd\u4e0d\u662f\u5355\u7eaf\u7684\u9884\u6d4b\uff0c\u8fd9\u6837\u4e00\u573a\u5927\u6218\uff0c\u53c2\u4e0e\u52bf\u529b\u4f17\u591a\uff0c\u96be\u4fdd\u5176\u4e2d\u6ca1\u6709\u9634\u9b42\u57ce\u7684\u4eba\u6df7\u8ff9\u5176\u4e2d\u3002\u201d</p><p>\u201c\u4f46\u6211\u6ca1\u6709\u6536\u5230\u4efb\u4f55\u9634\u9b42\u57ce\u65b9\u9762\u53d1\u51fa\u7684\u6307\u4ee4\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u6216\u8bb8\u662f\u56e0\u4e3a\u4f60\u7684\u884c\u52a8\u6070\u597d\u7b26\u5408\u9634\u9b42\u57ce\u7684\u9884\u671f\uff0c\u6240\u4ee5\u5c31\u65e0\u9700\u518d\u7279\u522b\u6307\u4ee4\u4e86\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u4f60\u770b\uff0c\u8fd9\u4e00\u6218\u4e0b\u6765\uff0c\u8c01\u83b7\u76ca\u6700\u5927\uff1f\u9f99\u5deb\u6559\u91cd\u521b\uff0c\u8fde\u8428\u9a6c\u65af\u7279\u90fd\u5f7b\u5e95\u6b7b\u4e86\uff1b\u9b54\u6cd5\u5973\u795e\u6559\u4f1a\u4e5f\u88ab\u91cd\u521b\uff0c\u9ad8\u5c42\u6b7b\u4f24\u65e0\u6570\uff0c\u8fde\u5973\u795e\u90fd\u88ab\u4f60\u780d\u4e86\u4e00\u5251\uff1b\u4f60\u7b97\u662f\u6709\u6240\u6536\u83b7\uff0c\u4f46\u9664\u4e86\u4f60\u4e4b\u5916\u5462\uff1f\u201d</p><p>\u9664\u4e86\u743c\u6069\u4e4b\u5916\uff0c\u5e94\u8be5\u5c31\u662f\u9634\u9b42\u57ce\u548c\u838e\u5c14\u6559\u4f1a\u4e86\u3002\u5e38\u8a00\u8bf4\uff0c\u654c\u4eba\u7684\u5feb\u4e50\u5c31\u662f\u6211\u7684\u90c1\u95f7\uff0c\u654c\u4eba\u90c1\u95f7\u5c31\u662f\u6211\u7684\u5feb\u4e50\uff0c\u9b54\u6cd5\u5973\u795e\u90c1\u95f7\u4e86\uff0c\u6700\u5f00\u5fc3\u7684\u7b2c\u4e00\u4e2a\u5e94\u8be5\u662f\u591c\u5973\u58eb\u838e\u5c14\uff0c\u7b2c\u4e8c\u4e2a\u5c31\u662f\u9634\u9b42\u57ce\u3002</p><p>\u201c\u8fd8\u6709\u4e00\u79cd\u53ef\u80fd\u5462\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u8fd8\u6709\u4e00\u79cd\u53ef\u80fd\uff0c\u5c31\u662f\u4f60\u786e\u5b9e\u5df2\u7ecf\u8131\u79bb\u63a7\u5236\u4e86\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u6216\u8bb8\u662f\u9634\u9b42\u57ce\u7684\u5185\u90e8\u51fa\u73b0\u95ee\u9898\uff0c\u6216\u8bb8\u662f\u53e6\u6709\u5f3a\u5927\u7684\u529b\u91cf\u5e72\u6d89\u2014\u2014\u4e0d\u662f\u8fde\u5730\u72f1\u9b54\u59ec\u90fd\u5bf9\u4f60\u503e\u5fc3\u81e3\u670d\u5417\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u6709\u54ea\u4f4d\u5973\u795e\u662f\u4f60\u7684\u60c5\u4eba\u5462\uff0c\u201d\u5979\u6700\u7ec8\u8fd8\u662f\u5fcd\u4e0d\u4f4f\uff0c\u7a0d\u7a0d\u8bbd\u523a\u4e86\u4e00\u53e5\uff0c\u201c\u6216\u8bb8\u4f60\u7684\u65e7\u60c5\u4eba\u4eec\u51fa\u624b\u5e72\u6d89\uff0c\u903c\u8feb\u9634\u9b42\u57ce\u653e\u5f03\u4e86\u4f60\u8fd9\u679a\u68cb\u5b50\uff0c\u4e5f\u4e0d\u65e0\u53ef\u80fd\u3002\u201d</p><p>\u8fd9\u4e2a\u53ef\u80fd\u6027\u4f3c\u4e4e\u4e0d\u5927\uff0c\u80fd\u591f\u903c\u8feb\u9634\u9b42\u57ce\u653e\u624b\u7684\uff0c\u6050\u6015\u53ea\u6709\u838e\u5c14\uff0c\u751a\u81f3\u8fde\u838e\u5c14\u4e5f\u672a\u5fc5\u80fd\u505a\u5230\u2014\u2014\u5b9e\u9645\u4e0a\uff0c\u743c\u6069\u4e00\u76f4\u9690\u9690\u6000\u7591\u9634\u9b42\u57ce\u57f9\u517b\u81ea\u5df1\u7684\u76ee\u7684\u4e4b\u4e00\uff0c\u5c31\u662f\u4e3a\u4e86\u5c06\u6765\u5bf9\u6297\u838e\u5c14\uff1b\u800c\u838e\u5c14\u5219\u662f\u5728\u53d1\u73b0\u9634\u9b42\u57ce\u7684\u8ba1\u5212\u4e4b\u540e\uff0c\u51fa\u624b\u5e72\u6d89\uff0c\u8bd5\u56fe\u5c06\u743c\u6069\u62c9\u5230\u5979\u90a3\u4e00\u8fb9\u3002\u8fd9\u5f53\u7136\u53ea\u662f\u731c\u6d4b\uff0c\u6ca1\u4ec0\u4e48\u8bc1\u636e\uff0c\u4f46\u8ff9\u8c61\u8fd8\u662f\u6709\u7684\uff0c\u5c31\u662f\u4ed6\u7684\u201c\u8bb0\u5fc6\u7f16\u7ec7\u201d\u3002</p><p>\u201c\u7a76\u7adf\u662f\u54ea\u4e00\u79cd\u53ef\u80fd\u6027\uff0c\u73b0\u5728\u6211\u4eec\u65e0\u6cd5\u5f97\u77e5\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u4f46\u65e0\u8bba\u54ea\u4e00\u79cd\uff0c\u5bf9\u4f60\u800c\u8a00\u90fd\u672a\u5fc5\u662f\u597d\u4e8b\u3002\u201d</p><p>\u5982\u679c\u662f\u524d\u4e00\u79cd\u53ef\u80fd\uff0c\u90a3\u4e48\u610f\u5473\u7740\u9634\u9b42\u57ce\u5bf9\u743c\u6069\u7684\u63a7\u5236\u8fdb\u4e00\u6b65\u52a0\u5f3a\uff0c\u53d8\u5f97\u66f4\u52a0\u9690\u853d\uff0c\u8fd9\u5f53\u7136\u4e0d\u662f\u4ec0\u4e48\u597d\u6d88\u606f\u3002\u4f46\u540e\u4e00\u79cd\u53ef\u80fd\u6027\u4e5f\u5f88\u9ebb\u70e6\uff0c\u5f88\u591a\u65f6\u5019\uff0c\u8131\u79bb\u63a7\u5236\u5e76\u4e0d\u4ee3\u8868\u81ea\u7531\uff0c\u53cd\u800c\u662f\u5371\u9669\u7684\u5f81\u5146\u3002\u5c31\u5982\u4e00\u679a\u68cb\u5b50\uff0c\u53ea\u8981\u6309\u7167\u5212\u597d\u7684\u8def\u7ebf\uff0c\u5728\u68cb\u76d8\u4e0a\u5c31\u53ef\u4ee5\u7545\u901a\u65e0\u963b\uff0c\u867d\u7136\u4f1a\u906d\u5230\u654c\u519b\u653b\u51fb\uff0c\u4f46\u540c\u65f6\u4e5f\u6709\u53cb\u65b9\u63f4\u52a9\uff0c\u4f46\u5982\u679c\u8fd9\u679a\u68cb\u5b50\u8131\u79bb\u68cb\u624b\u7684\u63a7\u5236\uff0c\u5728\u68cb\u76d8\u4e0a\u968f\u610f\u8d70\u52a8\uff0c\u8086\u610f\u800c\u884c\uff0c\u7ed3\u679c\u5fc5\u7136\u662f\u88ab\u4e24\u8fb9\u5939\u51fb\uff0c\u7fa4\u8d77\u653b\u4e4b\u3002</p><p>\u82e5\u662f\u5728\u4ee5\u524d\uff0c\u6539\u6362\u9635\u8425\uff0c\u6295\u5954\u539f\u672c\u7684\u5bf9\u624b\u65d7\u4e0b\uff0c\u4e5f\u672a\u5c1d\u4e0d\u662f\u4e2a\u529e\u6cd5\u3002\u867d\u7136\u743c\u6069\u4ece\u672a\u8fd9\u4e48\u6253\u7b97\u8fc7\uff0c\u4f46\u6bd5\u7adf\u662f\u4e00\u6761\u9000\u8def\uff0c\u4e07\u4e0d\u5f97\u5df2\u7684\u60c5\u51b5\u4e0b\uff0c\u4e5f\u4e0d\u662f\u4e0d\u80fd\u8003\u8651\u3002\u4f46\u9634\u5f71\u8c37\u4e00\u6218\u4e4b\u540e\uff0c\u8fd9\u79cd\u53ef\u80fd\u6027\u5df2\u7ecf\u538b\u6839\u4e0d\u5b58\u5728\u4e86\u3002</p><p>\u66f4\u9ebb\u70e6\u7684\u662f\uff0c\u9634\u5f71\u8c37\u4e00\u6218\u4e4b\u540e\uff0c\u743c\u6069\u7684\u201c\u4ef7\u503c\u201d\u5df2\u7ecf\u663e\u73b0\u51fa\u6765\u3002\u5728\u6b64\u4e4b\u524d\uff0c\u4ed6\u53ea\u662f\u4e00\u4e2a\u6765\u81ea\u9634\u9b42\u57ce\u7684\u5deb\u5e08\uff0c\u867d\u7136\u5929\u8d4b\u4e0d\u51e1\uff0c\u5e74\u7eaa\u8f7b\u8f7b\u5c31\u51dd\u6210\u771f\u540d\uff0c\u800c\u4e14\u4e0d\u77e5\u4f55\u6545\u53d7\u5230\u5973\u795e\u7684\u9752\u7750\uff0c\u6210\u4e3a\u838e\u5c14\u9009\u6c11\u2014\u2014\u4f46\u4e5f\u5c31\u4ec5\u6b64\u800c\u5df2\uff0c\u8fd9\u4e16\u754c\u4e0a\u4ece\u6765\u4e0d\u7f3a\u4e4f\u5929\u624d\uff0c\u795e\u7957\u7684\u9009\u6c11\u4e5f\u4e0d\u7b97\u5982\u4f55\u7f55\u89c1\u3002\u4f46\u73b0\u5728\u60c5\u51b5\u4e0d\u4e00\u6837\u4e86\uff0c\u63a7\u5236\u4e86\u743c\u6069\uff0c\u5c31\u6709\u53ef\u80fd\u638c\u63e1\u4e0a\u53e4\u9b54\u6cd5\u5e1d\u56fd\u7684\u79d8\u5668\uff0c\u751a\u81f3\u6709\u53ef\u80fd\u95f4\u63a5\u63a7\u5236\u4e00\u4f4d\u5730\u72f1\u9b54\u59ec\uff0c\u8fd9\u8bf1\u60d1\u529b\u5b9e\u5728\u592a\u5f3a\u3002\u6240\u8c13\u541b\u5b50\u65e0\u7f6a\uff0c\u6000\u74a7\u5176\u7f6a\uff0c\u743c\u6069\u5de6\u770b\u53f3\u770b\uff0c\u81ea\u5df1\u90fd\u89c9\u5f97\u73b0\u5728\u8fd8\u6ca1\u6709\u88ab\u4eba\u8ffd\u6740\u7ed1\u67b6\uff0c\u5b9e\u5728\u662f\u8fd0\u6c14\u5f88\u597d\u3002</p><p>\u201c\u5927\u6982\u662f\u5fc3\u5b58\u5fcc\u60ee\u5427\uff0c\u6bd5\u7adf\u4f60\u521a\u521a\u91cd\u521b\u4e86\u4e00\u4f4d\u9ad8\u7b49\u795e\u3002\u201d</p><p>\u743c\u6069\u82e6\u7b11\uff0c\u4ed6\u80fd\u51fb\u4f24\u9b54\u6cd5\u5973\u795e\uff0c\u538b\u6839\u4e0d\u662f\u81ea\u5df1\u7684\u672c\u4e8b\uff0c\u9996\u5148\u662f\u7b2c\u4e94\u79d8\u5668\u201c\u4e5d\u5e7d\u9f0e\u201d\u7684\u9886\u57df\u538b\u5236\uff0c\u8ba9\u5973\u795e\u4e0d\u5f97\u4e0d\u4ee5\u5723\u8005\u5f62\u6001\u964d\u4e34\uff0c\u53d8\u5f97\u5341\u5206\u8106\u5f31\uff1b\u5176\u6b21\u662f\u624e\u745e\u5c14\u7684\u5e2e\u52a9\uff0c\u8fd8\u7ed9\u4e86\u4ed6\u4e00\u628a\u7b2c\u4e09\u79d8\u5668\u201c\u661f\u8fb0\u5251\u201d\u7684\u5f71\u5668\uff1b\u518d\u52a0\u4e0a\u51db\u63d0\u4f9b\u5de8\u9f99\u4e4b\u529b\u4f5c\u4e3a\u652f\u6301\uff0c\u73ca\u5609\u4ee5\u201c\u51e4\u51f0\u201d\u4e4b\u80fd\u51bb\u7ed3\u65f6\u95f4\uff0c\u7ed9\u743c\u6069\u521b\u9020\u4e86\u51fa\u624b\u7684\u673a\u4f1a\u3002\u4f46\u661f\u8fb0\u5251\u7684\u5f71\u5668\u5f53\u573a\u5df2\u7ecf\u6bc1\u635f\uff0c\u624e\u745e\u5c14\u4e5f\u8fd4\u56de\u5730\u72f1\uff0c\u65e0\u6cd5\u8054\u7cfb\uff0c\u4e5d\u5e7d\u9f0e\u5012\u662f\u5728\u624b\u4e2d\uff0c\u4f46\u5b83\u77ed\u65f6\u95f4\u5185\u662f\u4e0d\u53ef\u80fd\u518d\u5ea6\u542f\u52a8\u4e86\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ed6\u6240\u6709\u7684\u4f9d\u4ed7\u90fd\u6d88\u5931\u6b86\u5c3d\uff0c\u53c8\u88ab\u6253\u56de\u539f\u5f62\u3002</p><p>\u743c\u6069\u81ea\u5df1\u5f88\u6e05\u695a\uff0c\u4ed6\u73b0\u5728\u770b\u4f3c\u98ce\u5e73\u6d6a\u9759\uff0c\u5b9e\u9645\u4e0a\u5904\u5883\u975e\u5e38\u5371\u9669\u3002\u9634\u5f71\u8c37\u4e00\u6218\uff0c\u4e0e\u9b54\u6cd5\u5973\u795e\u6559\u4f1a\u6210\u4e86\u6b7b\u654c\uff0c\u539f\u672c\u7684\u4e00\u70b9\u5584\u7f18\u6d88\u6563\u6b86\u5c3d\uff1b\u8eab\u6000\u91cd\u5b9d\uff0c\u5f15\u4eba\u89ca\u89ce\uff0c\u53c8\u6ca1\u6709\u8db3\u591f\u7684\u5b9e\u529b\u81ea\u4fdd\uff1b\u504f\u504f\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u4e0e\u9634\u9b42\u57ce\u7684\u5173\u7cfb\u53d8\u5f97\u5fae\u5999\uff0c\u672a\u5fc5\u8fd8\u80fd\u50cf\u4ee5\u524d\u4e00\u6837\u4f5c\u4e3a\u501a\u9760\u548c\u540e\u76fe\u3002\u4ed6\u53c8\u4e0d\u613f\u610f\u53e6\u6295\u7b2c\u4e09\u65b9\u5bfb\u6c42\u5e87\u62a4\uff0c\u4e4b\u524d\u5df2\u7ecf\u62d2\u7edd\u4e86\u63d0\u5c14\u5927\u4e3b\u6559\u7684\u62db\u63fd\u3002\u5982\u6b64\u4e00\u6765\uff0c\u53ef\u4ee5\u9009\u62e9\u7684\u4f59\u5730\u5c31\u4e0d\u591a\u4e86\u3002</p><p>\u6240\u4ee5\u4ed6\u51b3\u5b9a\u53bb\u4e1c\u57df\u3002</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u73b0\u5728\u9700\u8981\u4e00\u4e2a\u5730\u65b9\uff0c\u9996\u5148\u80fd\u591f\u8ba9\u4ed6\u8eb2\u907f\u9b54\u6cd5\u5973\u795e\u63a5\u4e0b\u6765\u5fc5\u7136\u5c55\u5f00\u7684\u62a5\u590d\uff0c\u5176\u6b21\u80fd\u591f\u8ba9\u4ed6\u8fdc\u79bb\u5404\u79cd\u6f5c\u5728\u7684\u654c\u4eba\u548c\u89ca\u89ce\u3002\u6700\u540e\uff0c\u5982\u679c\u771f\u7684\u662f\u7531\u4e8e\u67d0\u79cd\u7f18\u6545\uff0c\u9634\u9b42\u57ce\u5931\u53bb\u4e86\u5bf9\u81ea\u5df1\u7684\u63a7\u5236\uff0c\u90a3\u4e48\u743c\u6069\u663e\u7136\u4e5f\u4e0d\u6253\u7b97\u518d\u8dd1\u56de\u53bb\uff0c\u800c\u662f\u8d81\u6b64\u673a\u4f1a\u6de1\u5316\u8054\u7cfb\uff0c\u79bb\u5f97\u8fdc\u8fdc\u7684\u4e3a\u597d\u3002</p><p>\u7b26\u5408\u8fd9\u4e9b\u6761\u4ef6\u7684\uff0c\u53ea\u6709\u4e1c\u57df\u3002</p><p>\u743c\u6069\u5bf9\u4e1c\u57df\u7684\u4e86\u89e3\u5e76\u4e0d\u591a\uff0c\u4f46\u57fa\u672c\u6982\u5ff5\u8fd8\u662f\u6709\u7684\u3002\u6885\u83f2\u65af\u66fe\u7ecf\u8ddf\u4ed6\u8bf4\u8fc7\uff0c\u4e1c\u57df\u662f\u795e\u738b\u7684\u7edf\u6cbb\u8303\u56f4\uff0c\u5c01\u95ed\u9694\u7edd\uff0c\u81ea\u6210\u4f53\u7cfb\u3002\u50cf\u9b54\u6cd5\u5973\u795e\u8fd9\u79cd\u9ad8\u7b49\u795e\uff0c\u6559\u4f1a\u89c4\u6a21\u5e9e\u5927\uff0c\u4fe1\u5f92\u904d\u5e03\u5927\u9646\uff0c\u795e\u6bbf\u5904\u5904\u7686\u662f\uff0c\u552f\u72ec\u4e1c\u57df\u5374\u662f\u4f8b\u5916\uff0c\u56e0\u4e3a\u795e\u738b\u4eec\u6839\u672c\u4e0d\u5141\u8bb8\u4efb\u4f55\u5916\u6765\u795e\u660e\u5728\u4e1c\u57df\u5883\u5185\u4f20\u6559\u3002\u5b97\u6559\u5982\u6b64\uff0c\u5176\u4ed6\u4e5f\u7c7b\u4f3c\uff0c\u50cf\u7ad6\u7434\u624b\u540c\u76df\u3001\u9f99\u5deb\u6559\u3001\u6563\u5854\u6797\u4f1a\u8fd9\u4e9b\u7ec4\u7ec7\uff0c\u5728\u5927\u9646\u4e0a\u540d\u58f0\u8d6b\u8d6b\uff0c\u5728\u4e1c\u57df\u5374\u6ca1\u4ec0\u4e48\u5f71\u54cd\u529b\u3002\u743c\u6069\u8dd1\u5230\u4e1c\u57df\uff0c\u867d\u7136\u4e0d\u80fd\u8bf4\u5c31\u53ef\u4ee5\u5b8c\u5168\u907f\u514d\u8ffd\u6740\uff0c\u4f46\u60c5\u5f62\u786e\u5b9e\u4f1a\u597d\u5f88\u591a\u3002</p><p>\u9664\u4e86\u907f\u96be\uff0c\u4e1c\u57df\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\u53e6\u6709\u4e00\u5c42\u542b\u4e49\uff0c\u5b83\u662f\u6614\u65e5\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u9886\u5730\u6240\u5728\u3002</p><p>\u8eb2\u907f\u7ec8\u7a76\u4e0d\u662f\u957f\u4e45\u4e4b\u8ba1\uff0c\u589e\u5f3a\u81ea\u5df1\u7684\u529b\u91cf\u624d\u662f\u6839\u672c\u89e3\u51b3\u4e4b\u9053\uff0c\u6240\u4ee5\u8fd9\u4e2a\u5730\u65b9\u6700\u597d\u8fd8\u80fd\u63d0\u4f9b\u4e00\u4e9b\u52a9\u529b\uff0c\u8ba9\u4ed6\u80fd\u591f\u5feb\u901f\u5730\u53d8\u5f3a\u3002\u5355\u7eaf\u7684\u53d8\u5f3a\u4e0d\u96be\uff0c\u52aa\u529b\u953b\u70bc\u5c31\u662f\uff1b\u4f46\u8fd8\u8981\u201c\u5feb\u901f\u201d\u7684\u8bdd\uff0c\u6700\u597d\u529e\u6cd5\u5c31\u662f\u501f\u52a9\u5916\u7269\u3002\u4f0a\u739b\u65af\u5361\u5947\u68b0\u5e08\u4eec\u5236\u4f5c\u5668\u7269\u7684\u80fd\u529b\uff0c\u5728\u9634\u5f71\u8c37\u4e00\u6218\u4e2d\u5df2\u7ecf\u5f97\u5230\u5145\u5206\u7684\u8bc1\u660e\uff0c\u743c\u6069\u7684\u60f3\u6cd5\u5f88\u7b80\u5355\uff0c\u5c31\u662f\u6307\u671b\u80fd\u518d\u627e\u5230\u51e0\u4e2a\u7c7b\u4f3c\u201c\u4e5d\u5e7d\u9f0e\u201d\u8fd9\u79cd\u79d8\u5668\uff0c\u90a3\u5c31\u53ef\u4ee5\u6a2a\u7740\u8d70\u4e86\u3002</p><p>\u4f46\u6885\u83f2\u65af\u663e\u7136\u6709\u4e0d\u540c\u610f\u89c1\u3002\u201c\u8fd9\u5b8c\u5168\u662f\u5728\u78b0\u8fd0\u6c14\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u4e3a\u4ec0\u4e48\u4e0d\u628a\u91cd\u70b9\u653e\u5728\u90a3\u5ea7\u6d6e\u7a7a\u57ce\u4e0a\u5462\uff1f\u201d</p><p>\u743c\u6069\u5012\u4e5f\u8003\u8651\u8fc7\u8fd9\u4e2a\u95ee\u9898\u3002\u4f0a\u739b\u65af\u5361\u7684\u79d8\u5668\u867d\u7136\u5a01\u529b\u5de8\u5927\uff0c\u4f46\u4e0b\u843d\u4e0d\u660e\uff0c\u627e\u8d77\u6765\u5b8c\u5168\u6ca1\u6709\u4efb\u4f55\u7ebf\u7d22\uff0c\u552f\u4e00\u7684\u6307\u671b\u662f\u51ef\u745f\u7433\u3002\u4f46\u51ef\u745f\u7433\u663e\u7136\u624b\u91cc\u4e5f\u6ca1\u6709\u5176\u4ed6\u79d8\u5668\uff0c\u5426\u5219\u65e9\u62ff\u51fa\u6765\u4e86\uff0c\u81f3\u4e8e\u5979\u662f\u5426\u77e5\u9053\u5176\u4ed6\u79d8\u5668\u7684\u4e0b\u843d\uff0c\u53ea\u6015\u4e5f\u662f\u4e2a\u672a\u77e5\u6570\u3002\u603b\u800c\u8a00\u4e4b\uff0c\u786e\u5b9e\u662f\u8981\u78b0\u8fd0\u6c14\u3002\u6d6e\u7a7a\u57ce\u5219\u4e0d\u540c\uff0c\u8428\u9a6c\u65af\u7279\u5df2\u7ecf\u7ed9\u4e86\u6bd4\u8f83\u8be6\u7ec6\u7684\u8d44\u6599\uff0c\u8fde\u5730\u56fe\u90fd\u753b\u597d\u4e86\uff0c\u627e\u8d77\u6765\u96be\u5ea6\u4f4e\u5f88\u591a\u3002\u4ece\u5b9e\u7528\u6027\u4e0a\u8bf4\uff0c\u6d6e\u7a7a\u57ce\u7684\u4ef7\u503c\u4f3c\u4e4e\u4e5f\u8981\u5927\u4e00\u4e9b\u3002\u79d8\u5668\u518d\u5f3a\uff0c\u7ec8\u7a76\u53ea\u662f\u51e0\u4ef6\u4e1c\u897f\uff0c\u4e00\u5ea7\u5b8c\u6574\u672a\u906d\u635f\u574f\u7684\u6d6e\u7a7a\u57ce\uff0c\u5219\u53ef\u4ee5\u770b\u505a\u4e00\u5ea7\u65e0\u6bd4\u4e30\u5bcc\u7684\u9b54\u6cd5\u5b9d\u85cf\uff0c\u5bf9\u4e8e\u5deb\u5e08\u6765\u8bf4\u5176\u610f\u4e49\u65e0\u6cd5\u4f30\u91cf\u3002</p><p>\u5173\u952e\u5728\u4e8e\uff1a\u8fd9\u5ea7\u6d6e\u7a7a\u57ce\u662f\u5426\u771f\u7684\u50cf\u8428\u9a6c\u65af\u7279\u6240\u8bf4\u90a3\u6837\uff0c\u6838\u5fc3\u533a\u57df\u4fdd\u6301\u5b8c\u6574\uff1b\u5982\u679c\u662f\uff0c\u90a3\u4e48\u743c\u6069\u80fd\u5426\u5b8c\u5168\u638c\u63e1\u3002</p><p>\u5982\u679c\u6d6e\u7a7a\u57ce\u5df2\u7ecf\u635f\u6bc1\uff0c\u90a3\u5b83\u7684\u4ef7\u503c\u5c31\u5927\u6253\u6298\u6263\uff1b\u4f46\u5982\u679c\u771f\u7684\u4fdd\u6301\u5b8c\u597d\uff0c\u540c\u6837\u4e5f\u662f\u9ebb\u70e6\u3002\u8010\u745f\u65f6\u4ee3\u7684\u6d6e\u7a7a\u57ce\uff0c\u6bcf\u4e00\u5ea7\u90fd\u662f\u5927\u5965\u672f\u5e08\u6240\u5efa\uff0c\u6027\u8d28\u5c31\u76f8\u5f53\u4e8e\u73b0\u5728\u67d0\u4e9b\u5deb\u5e08\u4eec\u7684\u9b54\u6cd5\u5854\uff0c\u89c4\u6a21\u66f4\u5927\uff0c\u529f\u80fd\u66f4\u591a\uff0c\u800c\u4e14\u6709\u4ee5\u5bc6\u745f\u80fd\u6838\u4e3a\u6838\u5fc3\u7684\u8ff7\u9501\u7b3c\u7f69\u9632\u5fa1\uff0c\u5373\u4fbf\u539f\u4e3b\u4eba\u4e0d\u5728\u4e86\uff0c\u4e5f\u4e0d\u662f\u5916\u4eba\u53ef\u4ee5\u8f7b\u6613\u63a5\u624b\u7684\uff0c\u81f3\u5c11\u743c\u6069\u6ca1\u8fd9\u4e2a\u81ea\u4fe1\u3002</p><p>\u76f8\u6bd4\u8d77\u6765\uff0c\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u9057\u4ea7\u2014\u2014\u5982\u679c\u6709\u5e76\u4e14\u80fd\u627e\u5230\u7684\u8bdd\u2014\u2014\u5c31\u6ca1\u8fd9\u79cd\u9ebb\u70e6\u3002\u4e00\u65b9\u9762\uff0c\u743c\u6069\u6709\u201c\u7687\u5ba4\u201d\u7684\u8840\u7edf\uff0c\u5c31\u50cf****\uff0c\u53ef\u4ee5\u6253\u7834\u5927\u591a\u6570\u7981\u5236\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u8fd8\u6709\u51ef\u745f\u7433\u5e2e\u5fd9\u2014\u2014\u524d\u63d0\u662f\u80fd\u591f\u627e\u5230\u5979\u3002</p><p>\u201c\u9ebb\u70e6\u5c31\u5728\u8fd9\u91cc\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f60\u5ffd\u7565\u4e86\u4e00\u4ef6\u4e8b\uff1a\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u662f\u600e\u4e48\u706d\u4ea1\u7684\uff1f\u201d</p><p>\u201c\u662f\u88ab\u4e1c\u57df\u7684\u795e\u738b\u4eec\u51fb\u8d25\u2014\u2014\u201d\u743c\u6069\u6014\u4e86\u4e00\u4e0b\uff0c\u604d\u7136\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u795e\u738b\uff1f\u201d</p><p>\u5c11\u5973\u5728\u638c\u5fc3\u5199\u4e86\u4e2a\u5b57\uff0c\u90a3\u662f\u4e00\u4e2a\u8c61\u5f62\u6587\u5b57\uff0c\u4f46\u743c\u6069\u770b\u4e0d\u61c2\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8fd9\u662f\u4e00\u4e2a\u53e4\u6069\u745f\u6587\u5b57\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u600e\u4e48\u8bfb\uff0c\u4f46\u610f\u601d\u662f\u2018\u4f7f\u7528\u90aa\u672f\u3001\u5236\u9020\u90aa\u6076\u5668\u5177\u7684\u4eba\u2019\uff0c\u4e5f\u5c31\u662f\u5947\u68b0\u5e08\uff0c\u6216\u8005\u8bf4\u5deb\u5e08\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5b83\u662f\u4e2a\u591a\u4e49\u8bcd\uff0c\u8fd8\u6709\u4e00\u79cd\u542b\u4e49\u662f\uff1a\u90aa\u9b54\u3002\u6069\u745f\u548c\u7a46\u7f55\u4e24\u56fd\u795e\u738b\u66fe\u7ecf\u5171\u540c\u9881\u4ee4\uff1a\u51e1\u81ea\u79f0\u90aa\u9b54\u8005\uff0c\u5e94\u5904\u4ee5\u711a\u5211\u2014\u2014\u5c31\u662f\u540a\u5728\u7ede\u67b6\u4e0a\u70e7\u6b7b\u3002\u201d</p><p>\u743c\u6069\u660e\u767d\u5979\u7684\u610f\u601d\u3002</p><p>\u4f0a\u739b\u65af\u5361\u662f\u88ab\u795e\u738b\u5171\u540c\u8986\u706d\uff0c\u5728\u6b64\u57fa\u7840\u4e0a\u5efa\u7acb\u8d77\u7a46\u7f55\u3001\u6069\u745f\u4e24\u5927\u4e1c\u57df\u5e1d\u56fd\u3002\u795e\u738b\u6218\u80dc\u4e4b\u540e\uff0c\u4e0d\u4ec5\u706d\u56fd\uff0c\u540c\u65f6\u8fd8\u5c06\u4f0a\u739b\u65af\u5361\u7684\u6240\u6709\u6587\u660e\u75d5\u8ff9\u5c3d\u6570\u6467\u6bc1\uff0c\u8fde\u6587\u5b57\u8bb0\u8f7d\u90fd\u7981\u7edd\u6d41\u4f20\uff0c\u53ef\u89c1\u5fcc\u60ee\u6781\u6df1\u3002\u743c\u6069\u60f3\u8981\u53bb\u53d1\u6398\u4f0a\u739b\u65af\u5361\u9057\u8ff9\uff0c\u5bfb\u627e\u5931\u843d\u79d8\u5668\uff0c\u8fd9\u5728\u522b\u7684\u5730\u65b9\u5012\u65e0\u6240\u8c13\uff0c\u5728\u4e1c\u57df\uff0c\u5c31\u662f\u4e0d\u8d66\u4e4b\u7f6a\uff0c\u53ea\u6015\u4ed6\u521a\u521a\u9732\u51fa\u4e00\u70b9\u610f\u601d\uff0c\u6216\u8005\u662f\u7a0d\u7a0d\u66b4\u9732\u51fa\u5176\u201c\u7fd4\u9f99\u7687\u5ba4\u201d\u7684\u8eab\u4efd\uff0c\u5c31\u4f1a\u5f15\u6765\u795e\u738b\u4eec\u7684\u96f7\u9706\u4e4b\u6012\u3002\u795e\u738b\u7edf\u6cbb\u4e1c\u57df\u56db\u5343\u5e74\uff0c\u5c01\u95ed\u81ea\u5b88\uff0c\u6839\u57fa\u6df1\u539a\uff0c\u743c\u6069\u62ff\u4ec0\u4e48\u53bb\u548c\u4eba\u5bb6\u5bf9\u6297\u3002</p><p>\u201c\u77e5\u9053\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u548c\u5947\u68b0\u5e08\u7684\u4eba\u7684\u786e\u5f88\u5c11\uff0c\u4f46\u4e5f\u4e0d\u662f\u7edd\u65e0\u4ec5\u6709\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u4f60\u4ee5\u4e3a\u8fd9\u4e48\u591a\u5e74\uff0c\u5c31\u6ca1\u6709\u4eba\u62b1\u7740\u548c\u4f60\u4e00\u6837\u7684\u60f3\u6cd5\u53bb\u5bfb\u5b9d\u5417\uff1f\u6216\u8bb8\u6709\u6781\u5c11\u6570\u5e78\u8fd0\u8005\u53ef\u4ee5\u5168\u8eab\u800c\u9000\uff0c\u4f46\u5bf9\u4e8e\u7edd\u5927\u591a\u6570\u4eba\uff0c\u4ed6\u4eec\u7684\u4e0b\u573a\u5c31\u662f\u88ab\u540a\u6b7b\u5728\u795e\u738b\u5bab\u6bbf\u524d\u7684\u5e7f\u573a\u8def\u706f\u4e0a\u3002\u4f60\u89c9\u5f97\u4f60\u7684\u8fd0\u6c14\u5982\u4f55\uff1f\u201d</p><p>\u743c\u6069\u7684\u8fd0\u6c14\u5176\u5b9e\u4e5f\u8fd8\u4e0d\u7b97\u5dee\uff0c\u81f3\u5c11\u8fd9\u4e00\u8def\u8d70\u6765\uff0c\u4e0d\u8bf4\u4e00\u5e06\u98ce\u987a\uff0c\u4e5f\u603b\u80fd\u9022\u51f6\u5316\u5409\u3002\u4e0d\u8fc7\u4e16\u4e8b\u96be\u6599\uff0c\u80fd\u4e0d\u5192\u9669\u5f53\u7136\u8fd8\u662f\u5c3d\u91cf\u4e0d\u5192\u9669\u7684\u597d\u3002</p><p>\u201c\u6211\u8bb0\u5f97\u4f60\u4ee5\u524d\u4e0d\u662f\u8bf4\u8fc7\uff0c\u4e1c\u57df\u7684\u795e\u738b\u4eec\u90fd\u6b7b\u5f97\u5dee\u4e0d\u591a\u4e86\u4e48\uff1f\u201d</p><p>\u201c\u6069\u745f\u7684\u6700\u540e\u4e00\u4e2a\u795e\u738b\u5728\u5341\u516d\u5e74\u524d\u5df2\u7ecf\u9668\u843d\uff0c\u4f46\u7a46\u7f55\u76ee\u524d\u4ecd\u7136\u662f\u7531\u4e94\u4f4d\u795e\u738b\u5171\u540c\u7edf\u6cbb\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u90a3\u5ea7\u7d2b\u5bb8\u6c99\u6f20\uff0c\u4f0a\u739b\u65af\u5361\u7684\u9057\u5740\uff0c\u5c31\u5728\u7a46\u7f55\u5883\u5185\u3002\u201d</p><p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u60f3\u53bb\u7d2b\u5bb8\u6c99\u6f20\uff0c\u5c31\u5f88\u6709\u53ef\u80fd\u4f1a\u548c\u795e\u738b\u53d1\u751f\u6b63\u9762\u51b2\u7a81\u3002</p><p>\u201c\u5728\u8fd9\u65b9\u9762\uff0c\u6d6e\u7a7a\u57ce\u8fd9\u4e2a\u9009\u9879\u5c31\u6709\u5b83\u7684\u4f18\u52bf\u4e86\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6309\u7167\u8428\u9a6c\u65af\u7279\u6240\u8bf4\uff0c\u5b83\u4f4d\u4e8e\u8ff7\u5931\u68ee\u6797\u3002\u8ff7\u5931\u68ee\u6797\u5728\u5f7b\u68ee\u5854\u548c\u6069\u745f\u7684\u8fb9\u5883\u4ea4\u754c\u4e4b\u5904\u3002\u201d</p><p>\u65e0\u8bba\u662f\u5f7b\u68ee\u5854\u8fd8\u662f\u6069\u745f\uff0c\u73b0\u5728\u90fd\u5df2\u7ecf\u6ca1\u6709\u795e\u738b\u4e86\u3002</p><p>\u201c\u8fd9\u6837\u554a\uff0c\u201d\u743c\u6069\u5934\u75bc\u8d77\u6765\uff0c\u4ed6\u7684\u786e\u5fd8\u4e86\u8fd9\u4e00\u70b9\uff0c\u601d\u7ef4\u51fa\u73b0\u76f2\u533a\uff0c\u201c\u4f46\u6d6e\u7a7a\u57ce\u7684\u7981\u5236\u95ee\u9898\u600e\u4e48\u89e3\u51b3\uff1f\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u53ef\u6ca1\u8fd9\u4e2a\u672c\u4e8b\uff0c\u96be\u9053\u771f\u8981\u53bb\u8bf7\u6211\u8001\u5e08\u5e2e\u5fd9\uff1f\u201d</p><p>\u8428\u9a6c\u65af\u7279\u66fe\u7ecf\u5efa\u8bae\uff0c\u743c\u6069\u53ef\u4ee5\u627e\u5965\u6c83\u6765\u5e2e\u5fd9\u3002\u8fd9\u4e2a\u786e\u5b9e\u53ef\u884c\uff0c\u4f5c\u4e3a\u5f53\u4eca\u4e16\u4e0a\u5be5\u5be5\u53ef\u6570\u7684\u8010\u745f\u5927\u5965\u672f\u5e08\u4e4b\u4e00\uff0c\u5965\u6c83\u5e94\u8be5\u6709\u8fd9\u4e2a\u672c\u4e8b\uff0c\u4f46\u743c\u6069\u4e0b\u610f\u8bc6\u5730\u4e0d\u60f3\u8fd9\u4e48\u505a\u3002\u4ed6\u6b64\u6b21\u8fdc\u8d70\u4e1c\u57df\uff0c\u5176\u4e2d\u4e00\u4e2a\u76ee\u7684\u5c31\u662f\u8bd5\u56fe\u6de1\u5316\u548c\u9634\u9b42\u57ce\u4e4b\u95f4\u7684\u8054\u7cfb\uff0c\u66f4\u4e0d\u60f3\u81ea\u5df1\u8f9b\u8f9b\u82e6\u82e6\u627e\u5230\u4e86\u6d6e\u7a7a\u57ce\uff0c\u5374\u62f1\u624b\u4e0a\u4ea4\u3002\u5965\u6c83\u867d\u7136\u4e0d\u662f\u9634\u9b42\u57ce\u4eba\uff0c\u4f46\u4ed6\u548c\u9634\u9b42\u57ce\u4e3b\u90fd\u662f\u540c\u4e00\u4e2a\u65f6\u4ee3\u8d70\u8fc7\u6765\u7684\u4eba\uff0c\u4ea4\u60c5\u53ea\u6015\u6bd4\u743c\u6069\u60f3\u8c61\u7684\u66f4\u7d27\u5bc6\uff0c\u81f3\u5c11\u6709\u4e00\u4ef6\u4e8b\u743c\u6069\u8bb0\u5f97\u5f88\u6e05\u695a\uff0c\u4ed6\u5f53\u65e5\u5728\u74dc\u91cc\u5fb7\u65af\u57ce\u5076\u7136\u5f97\u5230\u4e86\u4e00\u5f20\u8010\u745f\u5377\u8f74\uff0c\u60f3\u8d77\u5965\u6c83\u7684\u85cf\u5b9d\u5e93\u91cc\u4e5f\u6709\u8fd9\u4e1c\u897f\uff0c\u4e8e\u662f\u9001\u7ed9\u8001\u5e08\u4f5c\u4e3a\u793c\u7269\uff0c\u7ed3\u679c\u5965\u6c83\u8f6c\u624b\u5c31\u9001\u7ed9\u4e86\u9634\u9b42\u57ce\u3002</p><p>\u201c\u6216\u8bb8\u5e76\u4e0d\u9700\u8981\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u627e\u4f60\u59d0\u59d0\u5e2e\u5fd9\u5c31\u53ef\u4ee5\u4e86\u3002\u201d</p><p>\u201c\u6211\u59d0\u59d0\uff1f\u201d\u743c\u6069\u6014\u4e86\u4e00\u4e0b\uff0c\u201c\u4f60\u662f\u8bf4\u73ca\u5609\uff1f\u201d</p><p>\u5c11\u5973\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u96be\u9053\u4f60\u8fd8\u6709\u5176\u4ed6\u59d0\u59d0\uff1f\u201d</p><p>\u4e8b\u5b9e\u4e0a\u8fd8\u771f\u6709\uff0c\u81f3\u5c11\u5728\u624e\u745e\u5c14\u53e3\u4e2d\uff0c\u743c\u6069\u7684\u201c\u59d0\u59d0\u201d\u662f\u6307\u51ef\u745f\u7433\uff1b\u838e\u5c14\u4e5f\u559c\u6b22\u743c\u6069\u53eb\u5979\u59d0\u59d0\uff0c\u4e0d\u77e5\u9053\u662f\u4f55\u7f18\u6545\u3002\u4e0d\u8fc7\u8fd9\u4e9b\u8fd8\u662f\u4e0d\u8981\u63d0\u8d77\u4e86\uff0c\u514d\u5f97\u627e\u6253\u3002\u201c\u5f53\u7136\u6ca1\u6709\uff0c\u201d\u4ed6\u8d76\u5feb\u8bf4\uff0c\u201c\u53ea\u662f\u89c9\u5f97\u5947\u602a\uff0c\u6240\u4ee5\u786e\u8ba4\u4e00\u4e0b\u3002\u201d</p><p>\u73ca\u5609\u5929\u8d4b\u4e0d\u5dee\uff0c\u53c8\u6709\u5965\u5609\u83b1\u65af\u8fd9\u79cd\u540d\u5e08\u6089\u5fc3\u6307\u70b9\uff0c\u8fdb\u6b65\u786e\u5b9e\u662f\u975e\u5e38\u5feb\u7684\uff0c\u4f46\u6bd5\u7adf\u65f6\u95f4\u592a\u77ed\uff0c\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u4e5f\u53ea\u80fd\u611f\u5e94\u5230\u9b54\u7f51\u7684\u7b2c\u4e8c\u5c42\u3002\u5728\u7edd\u5927\u591a\u6570\u5730\u65b9\uff0c\u8fd9\u5df2\u7ecf\u79f0\u5f97\u4e0a\u662f\u4e00\u4f4d\u5408\u683c\u7684\u5deb\u5e08\uff0c\u4f46\u6309\u7167\u9634\u9b42\u57ce\u7684\u6807\u51c6\uff0c\u53ea\u80fd\u7b97\u662f\u672a\u6bd5\u4e1a\u7684\u5b66\u5f92\u3002\u8981\u8bf4\u5979\u80fd\u591f\u5e2e\u5fd9\u89e3\u5f00\u6d6e\u7a7a\u57ce\u7684\u7981\u5236\uff0c\u8fd9\u5b9e\u5728\u4ee4\u4eba\u96be\u4ee5\u7406\u89e3\uff0c\u5982\u679c\u4e0d\u662f\u51fa\u81ea\u6885\u83f2\u65af\u4e4b\u53e3\uff0c\u743c\u6069\u80af\u5b9a\u53ea\u4f1a\u5f53\u9b3c\u626f\u3002</p><p>\u201c\u4e1c\u57df\u7684\u90a3\u5ea7\u6d6e\u7a7a\u57ce\uff0c\u662f\u4e0d\u662f\u548c\u5979\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u8bd5\u63a2\u5730\u95ee\u3002</p><p>\u201c\u6211\u4e0d\u662f\u767e\u5206\u4e4b\u767e\u5730\u786e\u5b9a\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f46\u6211\u521a\u521a\u770b\u5230\u4e00\u4efd\u8d44\u6599\uff0c\u663e\u793a\u5b83\u539f\u672c\u7684\u4e3b\u4eba\uff0c\u6781\u6709\u53ef\u80fd\u662f\u2018\u65c5\u8005\u2019\u6b27\u8d1d\u4f26\u3002\u201d</p></article>"}}}]);
