(window.webpackJsonp=window.webpackJsonp||[]).push([[543],{566:function(p){p.exports={chapterTitle:null,title:"\u7b2c40\u7ae0 \u6240\u6709\u7684\u95ee\u9898\u90fd\u80fd\u89e3\u51b3",content:"<article><h2>\u7b2c40\u7ae0 \u6240\u6709\u7684\u95ee\u9898\u90fd\u80fd\u89e3\u51b3</h2><p>\u3000\u3000\u8c22\u77bb\u4ee5\u865a\u589e\u8d44\u4ea7\u7684\u65b9\u5f0f\uff0c\u4ece\u4e2d\u884c\u8d37\u51fa\u4e00\u5343\u4e07\uff0c\u8fd9\u4e5f\u662f\u4ed6\u88ab\u8d76\u51fa\u6d77\u88d5\u516c\u53f8\u7684\u5173\u952e\u3002\u5f53\u7136\uff0c\u4ee5\u8c22\u665a\u6674\u80cc\u540e\u7684\u5173\u7cfb\uff0c\u5b8c\u5168\u80fd\u591f\u5c06\u8fd9\u7b14\u5de8\u6b3e\u7559\u5728\u516c\u53f8\uff0c\u8c22\u665a\u6674\u8fd8\u662f\u5c06\u8fd9\u7b14\u6b3e\u9879\u63d0\u524d\u8fd8\u7ed9\u4e2d\u884c\uff0c\u4ee4\u6d77\u88d5\u516c\u53f8\u7684\u6d41\u52a8\u8d44\u91d1\u6349\u895f\u89c1\u8098\uff1b\u6d77\u88d5\u516c\u53f8\u8fd9\u51e0\u5e74\u7684\u6df7\u4e71\u7ba1\u7406\u5df2\u7ecf\u8ba9\u5f90\u5fd7\u660e\u4e00\u624b\u5efa\u7acb\u8d77\u6765\u7684\u5206\u9500\u6e20\u9053\u9677\u5165\u762b\u75ea\u3002</p><p>\u3000\u3000\u6240\u8c13\u7684\u6d77\u5dde\u529e\u516c\u4e8b\uff0c\u9664\u4e86\u8fd8\u6709\u4e24\u4e2a\u6708\u79df\u671f\u7684\u529e\u516c\u5ba4\u4e4b\u5916\uff0c\u518d\u6ca1\u6709\u522b\u7684\u4ec0\u4e48\u8d44\u6e90\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u8bb8\u601d\u5750\u5728\u5bf9\u9762\u6d41\u9732\u51fa\u62c5\u5fe7\u7684\u773c\u795e\uff0c\u671d\u5979\u7b11\u4e86\u7b11\uff0c\u5bf9\u7740\u7535\u8bdd\u91cc\u8fd8\u672a\u8c0b\u8fc7\u9762\u7684\u53f6\u5c0f\u6850\u8bf4\uff1a\u201c\u73b0\u5728\u6709\u5f88\u591a\u4e0d\u5229\u6d77\u88d5\u516c\u53f8\u7684\u6d41\u8a00\uff0c\u6d77\u88d5\u516c\u53f8\u6700\u8fd1\u53d1\u751f\u7684\u4e00\u4e9b\u4e8b\u60c5\uff0c\u5728\u4e1a\u5185\u4e5f\u4e0d\u662f\u79d8\u95fb\uff0c\u6211\u4e0d\u5426\u8ba4\u6d77\u88d5\u516c\u53f8\u76ee\u524d\u5728\u8d44\u91d1\u4e0e\u5206\u9500\u6e20\u9053\u5b58\u5728\u9047\u5230\u4e00\u4e9b\u95ee\u9898\uff0c\u4f46\u662f\u4f60\u4e5f\u4e0d\u80fd\u5426\u8ba4\uff0c\u6d77\u88d5\u516c\u53f8\u6709\u80fd\u529b\u5728\u5f88\u77ed\u7684\u65f6\u95f4\u5185\u89e3\u51b3\u8fd9\u4e9b\u95ee\u9898\uff0c\u81f3\u5c11\u6d77\u5dde\u529e\u4e8b\u5904\u5728\u77ed\u65f6\u95f4\u5185\u80fd\u89e3\u51b3\u8d44\u91d1\u4e0e\u5206\u9500\u6e20\u9053\u7684\u95ee\u9898\u3002\u201d</p><p>\u3000\u3000\u542c\u5230\u5f20\u606a\u76f4\u63a5\u6697\u793a\u7701\u59d4\u526f\u4e66\u8bb0\u5f90\u5b66\u5e73\u7ad9\u5728\u6d77\u88d5\u516c\u53f8\u7684\u80cc\u540e\uff0c\u7535\u8bdd\u90a3\u5934\u7684\u53f6\u5c0f\u6850\u6c89\u9ed8\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u9664\u4e86\u7231\u7acb\u4fe1\u4e4b\u5916\uff0c\u8fd8\u6709\u597d\u51e0\u5bb6\u56fd\u9645\u901a\u8baf\u5236\u9020\u5546\u4f1a\u5728\u4eca\u660e\u4e24\u5e74\u8fdb\u5165\u56fd\u5185\uff0c\u6d77\u88d5\u516c\u53f8\u4e3a\u4ec0\u4e48\u4e0d\u505a\u8fd9\u4e9b\u54c1\u724c\u7684\u4ee3\u7406\uff1f\u201d</p><p>\u3000\u3000\u201c\u660e\u5e74\u53ea\u6709\u7231\u7acb\u4fe1\u6709\u63a8\u51fa\u4e2d\u6587\u6570\u5b57\u624b\u673a\u7684\u8ba1\u5212\uff0c\u98de\u5229\u6d66\u3001\u897f\u95e8\u5b50\u3001\u963f\u5c14\u5361\u7279\u3001\u7d22\u5c3c\u3001\u591a\u666e\u8fbe\u7b49\u56fd\u9645\u901a\u8baf\u5236\u9020\u5546\u867d\u7136\u4e5f\u4f1a\u9646\u7eed\u8fdb\u5165\u56fd\u5185\u5e02\u573a\uff0c\u4f46\u662f\u4ed6\u4eec\u5bf9\u56fd\u5185\u5e02\u573a\u7684\u524d\u666f\u4fe1\u5fc3\u4e0d\u8db3\uff0c\u5236\u7ea6\u8fd9\u4e9b\u54c1\u724c\u660e\u5e74\u5728\u56fd\u5185\u7684\u6269\u5f20\uff0c\u8fd9\u5bf9\u4ee3\u7406\u5546\u662f\u76f8\u5f53\u81f4\u547d\u7684\uff0c\u201d\u5f20\u606a\u89c1\u53f6\u5c0f\u6850\u4e3b\u52a8\u5c06\u8bdd\u9898\u8f6c\u5230\u6570\u5b57\u624b\u673a\u4e0a\u9762\uff0c\u6b63\u5408\u81ea\u5df1\u7684\u5fc3\u610f\uff0c\u8f7b\u8f66\u719f\u8def\u7684\u8bf4\u9053\uff0c\u201c\u5c31\u76ee\u524d\u56fd\u5185\u80fd\u5bfb\u89c1\u51e0\u6b3e\u82f1\u6587\u754c\u9762\u7684\u6570\u5b57\u624b\u673a\uff0c\u6211\u4ee5\u4e3a\u7231\u7acb\u4fe1\u662f\u60df\u4e00\u80fd\u5728\u77ed\u671f\u5185\u4e0e\u6469\u6258\u7f57\u62c9\u7ade\u4e89\u7684\u54c1\u724c\uff0cGH388\u5c06\u5f00\u521b\u76f4\u677f\u624b\u673a\u7684\u5148\u6cb3\uff0c\u5b83\u6240\u4f7f\u7528\u7684\u4fe1\u53f7\u6307\u793a\u706f\uff0c\u4e5f\u6709\u53ef\u80fd\u6210\u4e3a\u6570\u5b57\u624b\u673a\u7684\u6807\u51c6\u914d\u7f6e\uff0c\u7231\u7acb\u4fe1\u660e\u5e74\u63a8\u51fa\u7684\u4e2d\u6587\u624b\u673a\uff0c\u5e94\u8be5\u662fGH388\u7684\u5347\u7ea7\u7248\uff0c\u8fd9\u6b3e\u673a\u578b\uff0c\u5bf9\u4e8e\u89c1\u60ef\u2018\u5927\u54e5\u5927\u2019\u8fd9\u4e2a\u5927\u6c34\u58f6\u8eab\u6750\u7684\u624b\u673a\u7528\u6237\u6765\u8bf4\uff0c\u4e00\u76f4\u4f1a\u6709\u60ca\u8273\u7684\u611f\u89c9\uff0c\u5c31\u50cf\u6211\u73b0\u5728\u975e\u5e38\u671f\u5f85\u8ddf\u53f6\u5c0f\u59d0\u89c1\u9762\u4e00\u6837\uff0c\u6211\u4e5f\u8feb\u5207\u5e0c\u671b\u770b\u5230\u7231\u7acb\u4fe1\u4e2d\u6587\u6570\u5b57\u624b\u673a\u7684\u95ee\u4e16\uff0c\u7b49\u8fc7\u51e0\u5e74\u518d\u56de\u5934\u770b\uff0c\u4e0d\u80fd\u6210\u4e3a\u7231\u7acb\u4fe1\u7684\u4ee3\u7406\u5546\uff0c\u4f1a\u662f\u4e00\u4ef6\u4ee4\u4eba\u76f8\u5f53\u9057\u61be\u7684\u4e8b\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u601d\u89c1\u5f20\u606a\u64d2\u7740\u7535\u8bdd\u4f83\u4f83\u800c\u8c08\uff0c\u5982\u679c\u4e0d\u770b\u4ed6\u7a1a\u6c14\u7684\u8138\uff0c\u5b8c\u5168\u60f3\u4e0d\u5230\u4f1a\u662f\u4e00\u4e2a\u5341\u516d\u5c81\u7684\u5c11\u5e74\uff0c\u96be\u602a\u8c22\u665a\u6674\u4f1a\u5c06\u6d77\u5dde\u529e\u4e8b\u5904\u4ea4\u7ed9\u4ed6\u80e1\u641e\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u7535\u8bdd\u91cc\u8868\u73b0\u51fa\u5bf9\u56fd\u5185\u624b\u673a\u5e02\u573a\u7684\u719f\u6089\u4ee5\u53ca\u5bf9\u5e02\u573a\u524d\u666f\u7684\u4fe1\u5fc3\uff0c\u8ba9\u53f6\u5c0f\u6850\u9887\u4e3a\u610f\u52a8\uff0c\u4f55\u51b5\u6d77\u88d5\u516c\u53f8\u4e5f\u4e0d\u662f\u968f\u968f\u4fbf\u4fbf\u5c31\u80fd\u5e94\u4ed8\u8fc7\u53bb\u7684\u516c\u53f8\uff0c\u8868\u793a\u4f1a\u62bd\u65f6\u95f4\u5230\u6d77\u5dde\u4e0e\u4ed6\u4eec\u63a5\u89e6\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u56fd\u5185\u624b\u673a\u5e02\u573a\u521a\u521a\u840c\u751f\uff0c\u6ca1\u6709\u592a\u590d\u6742\u7684\u4e1c\u897f\uff0c\u52a0\u4e0a\u5176\u4ed6\u6d89\u8db3\u624b\u673a\u9500\u552e\u884c\u4e1a\u7684\u4ee3\u7406\u5546\u591a\u4e3a\u5bfb\u547c\u673a\u7684\u7ecf\u8425\u9500\u552e\u5546\u8f6c\u578b\u800c\u6765\uff0c\u5bf9\u4e5d\u56db\u5e74\u521a\u521a\u840c\u751f\u7684\u624b\u673a\u5e02\u573a\uff0c\u90fd\u6ca1\u6709\u5b8c\u6574\u7684\u6982\u5ff5\u3002\u5c31\u7b97\u5f20\u606a\u5728\u77ed\u77ed\u534a\u5929\u65f6\u95f4\u91cc\u7ed9\u8bb8\u601d\u704c\u8f93\u7684\u624b\u673a\u77e5\u8bc6\uff0c\u4e5f\u8db3\u591f\u8ba9\u8bb8\u601d\u5728\u4e5d\u56db\u5e74\u5192\u5145\u534a\u4e2a\u884c\u5bb6\uff0c\u4f55\u51b5\u81ea\u5df1\u4eb2\u81ea\u4e0a\u9635\u3002</p><p>\u3000\u3000\u5f20\u606a\u653e\u4e0b\u7535\u8bdd\uff0c\u671d\u8bb8\u601d\u63e1\u4e86\u63e1\u62f3\u5934\uff0c\u8868\u793a\u5927\u529f\u544a\u6210\u3002</p><p>\u3000\u3000\u529e\u516c\u5ba4\u5f88\u5b89\u9759\uff0c\u8bb8\u601d\u9690\u9690\u7ea6\u7ea6\u80fd\u542c\u89c1\u7535\u8bdd\u90a3\u5934\u7684\u58f0\u97f3\uff0c\u89c1\u5f20\u606a\u4e00\u8138\u5174\u594b\u7684\u6837\u5b50\uff0c\u4e0d\u5fcd\u5fc3\u6253\u51fb\u4ed6\uff1a\u201c\u60f3\u62ff\u4e0b\u4ee3\u7406\u6743\uff0c\u5206\u9500\u6e20\u9053\u5728\u54ea\u91cc\uff0c\u94fa\u8d27\u7684\u8d44\u91d1\u5728\u54ea\u91cc\uff0c\u53ef\u4e0d\u662f\u628a\u4eba\u5bb6\u9a97\u8fc7\u6765\u5c31\u7b97\u6210\u529f\u7684\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8f7b\u677e\u7684\u8bf4\u9053\uff1a\u201c\u8d70\u51fa\u6210\u529f\u7684\u7b2c\u4e00\u6b65\uff0c\u4e5f\u662f\u503c\u5f97\u9ad8\u5174\u7684\uff0c\u6d77\u5dde\u597d\u6b79\u4e5f\u662f\u6211\u7684\u5730\u76d8\uff0c\u4f60\u4ee5\u4e3a\u5979\u8d70\u4e0a\u6d77\u5dde\u7684\u571f\u5730\uff0c\u8fd8\u80fd\u8131\u5f97\u6389\u6211\u7684\u624b\u638c\u5fc3\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u53bb\uff0c\u522b\u81ed\u7f8e\u4e86\uff0c\u201d\u8bb8\u601d\u4ece\u62bd\u5c49\u91cc\u62ff\u51fa\u4e00\u4efd\u62a5\u7eb8\uff0c\u201c\u8fd9\u5219\u62db\u8058\u5e7f\u544a\u4e0d\u6653\u5f97\u8fd8\u4f1a\u9a97\u591a\u5c11\u4eba\u8fc7\u6765\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u6ca1\u6709\u4e24\u4e2a\u4eba\u5c31\u80fd\u5c06\u529e\u4e8b\u5904\u6491\u8d77\u6765\u7684\u4fe1\u5fc3\uff0c\u5979\u4e4b\u6240\u4ee5\u7559\u4e0b\u6765\uff0c\u66f4\u591a\u7684\u89c9\u5f97\u4e8f\u6b20\u81ea\u5df1\u4e00\u4efd\u5f88\u5927\u7684\u4eba\u60c5\uff0c\u5f20\u606a\u563f\u563f\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0b\u5468\u5e94\u8058\u7684\u4eba\u5f88\u591a\uff0c\u4f60\u4ece\u7b5b\u9009\u4e00\u4e9b\u4eba\u51fa\u6765\uff0c\u5c31\u6311\u90a3\u4e9b\u770b\u8d77\u6765\u6c34\u51c6\u5f88\u9ad8\u3001\u4e0d\u5927\u597d\u9a97\u7684\u4eba\u53c2\u52a0\u9762\u8bd5\uff0c\u9762\u8bd5\u5c31\u5b89\u6392\u5728\u4e0b\u5468\u516d\uff0c\u6211\u624d\u6709\u65f6\u95f4\u53c2\u52a0\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u4e5f\u662f\u6b63\u5f0f\u5f00\u5b66\u7684\u524d\u4e00\u5929\uff0c\u5f20\u606a\u4e5f\u8017\u5728\u529e\u4e8b\u5904\uff0c\u8ddf\u8bb8\u601d\u5728\u4e00\u8d77\u3002\u56fd\u5185\u5173\u4e8e\u6570\u5b57\u901a\u8baf\u7684\u8d44\u6599\u5f88\u5c11\uff0c\u5f20\u606a\u66f4\u591a\u6536\u96c6\u7684\u662f\u82f1\u6587\u8d44\u6599\uff0c\u8d44\u6599\u91cc\u6709\u5f88\u591a\u4e13\u4e1a\u8bcd\u6c47\uff0c\u8bb8\u601d\u8bfb\u8d77\u6765\u90fd\u5f88\u5403\u529b\uff0c\u4e00\u9047\u5230\u4e13\u4e1a\u8bcd\u6c47\u5c31\u8981\u7ffb\u725b\u6d25\u8bcd\u5178\uff0c\u89c1\u5f20\u606a\u968f\u624b\u7ffb\u9875\uff0c\u8ddf\u8bfb\u5c0f\u8bf4\u4e66\u4e00\u6837\u8fc5\u901f\uff0c\u7b11\u7740\u5bf9\u4ed6\u8bf4\uff1a\u201c\u4e0d\u8981\u5728\u8fd9\u91cc\u88c5\u6a21\u4f5c\u6837\uff0c\u4f60\u8981\u89c9\u5f97\u95f7\uff0c\u5148\u51fa\u53bb\u73a9\u4e00\u4f1a\u513f\uff0c\u7b49\u6211\u7ffb\u8bd1\u51fa\u6765\uff0c\u4f60\u518d\u770b\u4e5f\u884c\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u6211\u88c5\u6a21\u4f5c\u6837\uff1f\u201d\u5f20\u606a\u6307\u7740\u81ea\u5df1\u7684\u9f3b\u5b50\uff0c\u62ac\u5934\u89c1\u8bb8\u601d\u8f7b\u8511\u7684\u773c\u775b\u6311\u89c6\u7740\u81ea\u5df1\uff0c\u54d1\u7136\u5931\u7b11\uff0c\u201ctoattitudinize;toputonairs;toassumeairs;toputonashow;tostrikeatheatricalattitude;affected\uff0c\u2018\u88c5\u6a21\u4f5c\u6837\u2019\u7684\u82f1\u8bed\uff0c\u6211\u53ea\u8bb0\u5f97\u8fd9\u51e0\u79cd\u8bf4\u6cd5\uff0c\u4f60\u67e5\u4e00\u67e5\u8fd8\u6709\u522b\u7684\u7ffb\u8bd1\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u6123\u6123\u7684\u770b\u7740\u5f20\u606a\uff1a\u201c\u771f\u7684\u5047\u7684\uff1f\u201d</p><p>\u3000\u3000\u4eba\u751f\u8bfb\u6863\u524d\uff0c\u4f5c\u4e3a\u9686\u88d5\u96c6\u56e2\u8d1f\u8d23\u5bf9\u5916\u8d38\u6613\u7684\u804c\u4e1a\u7ecf\u7406\u4eba\uff0c\u82f1\u8bed\u6c34\u5e73\u600e\u4e48\u53ef\u80fd\u4f1a\u5dee\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5c06\u8bb8\u601d\u9f3b\u6881\u4e0a\u9ed1\u73b3\u7441\u6846\u773c\u955c\u6458\u4e0b\uff0c\u76ef\u7740\u5979\u9ed1\u767d\u5206\u660e\u7684\u773c\u73e0\u5b50\u5b9a\u5728\u90a3\u91cc\uff0c\u5979\u8fd9\u5403\u60ca\u7684\u6837\u5b50\u4e5f\u76f8\u5f53\u7684\u7f8e\uff0c\u633a\u76f4\u7684\u9f3b\u6881\u4e0b\u662f\u5a07\u6da6\u7684\u7ea2\u5507\uff0c\u5fae\u5fae\u5f20\u7740\uff0c\u8d1d\u9f7f\u95ea\u7740\u6d01\u83b9\u7684\u5fae\u5149\uff0c\u8ba9\u4eba\u60f3\u51d1\u4e0a\u53bb\u8214\u4e00\u53e3\u3002</p><p>\u3000\u3000\u201c\u4f60\u5728\u7eb8\u4e0a\u5199\u5199\u753b\u753b\uff0c\u539f\u6765\u662f\u60f3\u5c06\u8fd9\u4e9b\u8d44\u6599\u7ffb\u8bd1\u51fa\u6765\uff0c\u8ba9\u6211\u770b\u770b\u4f60\u7684\u82f1\u6587\u6c34\u5e73\u5408\u4e0d\u5408\u683c\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u8bb8\u601d\u9762\u524d\u7684\u4fe1\u7eb8\u4e0e\u82f1\u6587\u8d44\u6599\u62ff\u8fc7\u6765\uff0c\u4fe1\u7eb8\u62ac\u5934\u4e0a\u5370\u7740\u201c\u6d77\u5dde\u5e02\u519c\u673a\u603b\u5382\u201d\u7684\u7ea2\u5b57\uff0c\u662f\u8bb8\u601d\u7236\u4eb2\u8bb8\u6d77\u5c71\u4ece\u5382\u91cc\u505a\u56de\u6765\u7684\u4fe1\u7eb8\uff0c\u8bb8\u601d\u62ff\u4e86\u4e00\u4e9b\u5230\u529e\u4e8b\u5904\u7528\u3002\u5f20\u606a\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5fd8\u4e86\u8ddf\u6211\u5988\u8981\u96f6\u82b1\u94b1\u4e86\uff0c\u529e\u4e8b\u5904\u8fde\u4e70\u4fe1\u7eb8\u7684\u94b1\u90fd\u6ca1\u6709\uff0c\u8bf4\u51fa\u53bb\u4e22\u4eba\u554a\u3002\u201d\u62ff\u8d77\u7b14\u5c31\u5728\u8bb8\u601d\u7ffb\u8bd1\u7684\u5b57\u53e5\u4e0a\u4fee\u6539\u3002</p><p>\u3000\u3000\u8bb8\u601d\u60f3\u8d77\u7b2c\u4e00\u6b21\u5728\u5f20\u606a\u623f\u95f4\u91cc\u770b\u5230\u90a3\u672c\u83f2\u5217\u6d66\xb7\u79d1\u7279\u52d2\u7684\u82f1\u6587\u539f\u4f5c\u300a\u8425\u9500\u7ba1\u7406\u300b\uff0c\u5939\u5728\u4e66\u9875\u91cc\u7684\u7eb8\u6761\u4e0a\u7528\u82f1\u6587\u5199\u4e0b\u5bc6\u5bc6\u9ebb\u9ebb\u7684\u6279\u6ce8\uff0c\u6562\u60c5\u771f\u662f\u5f20\u606a\u5728\u770b\uff0c\u89c1\u5f20\u606a\u5728\u81ea\u5df1\u7ffb\u8bd1\u7684\u7eb8\u5212\u4e86\u597d\u51e0\u9053\u6a2a\u6760\uff0c\u8138\u4e00\u7ea2\uff0c\u8d76\u5fd9\u5c06\u4fe1\u7eb8\u62a2\u4e86\u56de\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u770b\u5f97\u61c2\u5c31\u884c\uff0c\u7701\u5f97\u6211\u7ffb\u8bd1\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u4e0d\u884c\uff0c\u6211\u770b\u4f60\u4e0a\u9762\u6709\u5f88\u591a\u9519\u8bef\uff0c\u6211\u90fd\u5e2e\u4f60\u6539\u8fc7\u6765\u4e86\uff0c\u4f60\u7684\u82f1\u8bed\u9700\u8981\u52a0\u5f3a\uff0c\u8981\u4e0d\u6211\u5e2e\u4f60\u8f85\u5bfc\u8f85\u5bfc\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u601d\u4fcf\u8138\u6da8\u5f97\u901a\u7ea2\uff0c\u89c1\u5f20\u606a\u620f\u8c11\u7684\u76ef\u7740\u81ea\u5df1\uff0c\u7f9e\u6124\u6b32\u6b7b\uff0c\u4f38\u624b\u8981\u906e\u4f4f\u4ed6\u7684\u773c\u775b\u3002\u5f20\u606a\u6293\u4f4f\u5979\u7684\u624b\u6309\u4e0b\u6765\uff0c\u76ef\u7740\u5979\u5feb\u8981\u6e17\u51fa\u6c34\u8ff9\u7684\u7f8e\u7738\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u53cd\u6b63\u6211\u8ddf\u4f60\u7238\u7238\u8bf4\u8981\u4f60\u8f85\u5bfc\u6211\u529f\u8bfe\u7684\uff0c\u6211\u5e73\u65f6\u4e5f\u4e0d\u80fd\u5230\u529e\u4e8b\u5904\uff0c\u4f60\u665a\u4e0a\u53bb\u6211\u90a3\u91cc\u5e2e\u6211\u8f85\u5bfc\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u518d\u8bf4\uff0c\u201d\u8bb8\u601d\u5c06\u624b\u62bd\u56de\u6765\uff0c\u5fc3\u60f3\u81ea\u5df1\u4ee5\u524d\u600e\u4e48\u4f1a\u89c9\u5f97\u4ed6\u5355\u7eaf\uff1f\u771f\u662f\u778e\u4e86\u773c\uff0c\u8ba9\u4ed6\u76ef\u5f97\u6709\u4e9b\u5fc3\u614c\uff0c\u5634\u4e0a\u5374\u4e0d\u670d\u8f93\uff0c\u201c\u5c31\u7b97\u82f1\u8bed\u4e0d\u80fd\u8f85\u5bfc\u4f60\uff0c\u5176\u4ed6\u529f\u8bfe\u8fd8\u662f\u884c\u7684\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u5f88\u671f\u5f85\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u770b\uff1f\u201d\u8bb8\u601d\u55d4\u6012\u9053\uff0c\u4f46\u5bf9\u5f20\u606a\u6beb\u65e0\u6548\u679c\u3002\u8bb8\u601d\u5fc3\u865a\u7684\u4fa7\u8fc7\u8eab\u5b50\u53bb\uff0c\u5fc3\u60f3\u5f20\u606a\u8981\u771f\u662f\u90a3\u79cd\u5c11\u5e74\u5929\u624d\uff0c\u4e0d\u662f\u5c31\u7cdf\u7cd5\u4e86\uff1f\u60f3\u8d77\u83f2\u5217\u6d66\xb7\u79d1\u7279\u52d2\u7684\u82f1\u6587\u539f\u4f5c\u300a\u8425\u9500\u7ba1\u7406\u300b\u53ef\u4e0d\u53ea\u662f\u82f1\u6587\u597d\uff0c\u5c31\u80fd\u770b\u61c2\u7684\u3002</p></article>"}}}]);
