(window.webpackJsonp=window.webpackJsonp||[]).push([[544],{567:function(p){p.exports={chapterTitle:null,title:"\u7b2c544\u7ae0 \u9999\u6e2f\u8857\u89d2\u7684\u98ce\u60c5",content:"<article><h2>\u7b2c544\u7ae0 \u9999\u6e2f\u8857\u89d2\u7684\u98ce\u60c5</h2><p>\u3000\u3000\u4f17\u4eba\u805a\u5728\u5609\u4fe1\u5b9e\u4e1a\u4e13\u95e8\u7ed9\u5f20\u606a\u51c6\u5907\u7684\u529e\u516c\u5ba4\u91cc\uff0c\u4e3b\u8981\u662f\u5173\u6ce8\u5609\u4fe1\u5b9e\u4e1a\u5728\u53d1\u5e03\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u4e4b\u540e\u7684\u80a1\u4ef7\u8868\u73b0\uff0c\u8fd9\u76f4\u63a5\u53cd\u5e94\u6295\u8d44\u4eba\u5bf9\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u7684\u4fe1\u5fc3\u3002</p><p>\u3000\u3000\u4e0b\u5348\u56db\u70b9\u949f\uff0c\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u6240\u7ed3\u675f\u4e00\u5929\u7684\u4ea4\u6613\uff0c\u5609\u4fe1\u5b9e\u4e1a\u80a1\u4ef7\u6700\u7ec8\u4ee512\uff05\u7684\u6da8\u5e45\u6536\u76d8\uff0c\u76d8\u4e2d\u6700\u591a\u51b2\u5230\u6bcf\u80a16\u6e2f\u5143\u7684\u9ad8\u4ef7\u3002</p><p>\u3000\u3000\u5173\u952e\u662f\u63a5\u4e0b\u6765\u4e00\u4e2a\u6708\u7684\u80a1\u4ef7\u8868\u73b0\uff0c\u9648\u4fe1\u751f\u3001\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u7b49\u4eba\u591a\u5c11\u6709\u4e9b\u4fe1\u5fc3\u4e0d\u8db3\uff0c\u5f20\u606a\u7ad9\u8d77\u6765\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u53c8\u4e0d\u80fd\u76f4\u63a5\u544a\u8bc9\u4ed6\u4eec\u63a5\u4e0b\u6765\u8fd9\u4e09\u4e2a\u6708\u5c06\u662f\u9999\u6e2f\u80a1\u5e02\u5728\u91d1\u878d\u98ce\u66b4\u4e4b\u524d\u6700\u75af\u72c2\u7684\u4e09\u4e2a\u6708\uff0c\u6709\u4e2d\u8d44\u53c2\u4e0e\u6216\u76f4\u63a5\u63a7\u80a1\u7684\u7ea2\u7b79\u80a1\u6da8\u5e45\u63a5\u8fd180\uff05\u3002\u4e4b\u540e\u5e02\u573a\u5c31\u4f1a\u53d8\u5f97\u8fdf\u7591\u800c\u60ca\u60e7\uff0c\u90a3\u4e9b\u6781\u5c11\u6570\u55c5\u5230\u5371\u9669\u7684\u6295\u8d44\u4eba\u63d0\u524d\u51fa\u573a\uff0c\u7edd\u5927\u591a\u6570\u6295\u8d44\u8005\u5374\u8981\u9971\u53d7\u91d1\u878d\u98ce\u66b4\u7684\u6467\u6b8b\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u3001\u5b59\u5c1a\u4e49\u7b49\u4eba\u79bb\u5f00\u529e\u516c\u5ba4\uff0c\u5f20\u606a\u7ad9\u8d77\u6765\uff0c\u6ce8\u89c6\u7740\u7a97\u5916\u7684\u98ce\u666f\uff0c\u73bb\u7483\u7a97\u4e0a\u51dd\u7740\u6c34\u96fe\uff0c\u7a97\u5916\u7684\u98ce\u666f\u6709\u4e9b\u6a21\u7cca\uff0c\u770b\u4e0d\u6e05\u96e8\u4e1d\uff0c\u4f46\u662f\u80fd\u542c\u5230\u7a97\u5916\u7684\u5fae\u96e8\u4e00\u76f4\u672a\u505c\uff0c\u5fc3\u60f3\u5728\u5373\u5c06\u6765\u88ad\u7684\u91d1\u878d\u98ce\u66b4\u4e2d\uff0c\u4f1a\u6709\u591a\u5c11\u4eba\u503e\u5bb6\u8361\u4ea7\u3001\u6d41\u6d6a\u8857\u5934\u6216\u8005\u4ece\u90fd\u5e02\u5f15\u4ee5\u4e3a\u50b2\u7684\u6469\u5929\u9ad8\u697c\u4e00\u7eb5\u800c\u4e0b\u4e86\u5374\u4e0d\u503c\u5f97\u671f\u5f85\u7684\u4eba\u751f\uff1f\u81ea\u5df1\u867d\u7136\u60f3\u6781\u529b\u5f25\u8865\u4ec0\u4e48\uff0c\u4f46\u662f\u4e2a\u4eba\u7684\u529b\u91cf\u592a\u8fc7\u8106\u5f31\u4e86\uff0c\u5c31\u8fde\u53f6\u81fb\u6c11\u4e5f\u7ed9\u56fd\u5185\u5916\u7684\u4e3b\u6d41\u7ecf\u6d4e\u5b66\u5bb6\u653b\u51fb\u5f97\u4f53\u65e0\u5b8c\u80a4\uff0c\u53ea\u80fd\u5728\u81ea\u5df1\u7684\u804c\u8f96\u8303\u56f4\u4e4b\u5185\u52aa\u529b\u7684\u505a\u4e9b\u8865\u6551\u3002</p><p>\u3000\u3000\u8bb8\u601d\u51dd\u7738\u671b\u7740\u8fd9\u4e2a\u8ba9\u81ea\u5df1\u65e5\u591c\u7275\u6302\u7684\u524a\u7626\u80cc\u5f71\uff0c\u6b64\u65f6\u770b\u8d77\u6765\u6709\u4e9b\u6c89\u91cd\uff0c\u8d70\u8fc7\u6765\uff0c\u8f7b\u8f7b\u7275\u4f4f\u4ed6\u7684\u624b\uff1a\u201c\u600e\u4e48\u4e86\uff0c\u5728\u60f3\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u662f\u4e00\u6761\u6cb3\u2026\u2026\u201d\u5f20\u606a\u4f38\u51fa\u624b\u6307\u5728\u8499\u7740\u6c34\u6c7d\u4e0a\u7684\u73bb\u7483\u4e0a\u753b\u51fa\u4e00\u9053\u660e\u4eae\u7684\u7ebf\uff0c\u6620\u51fa\u7a97\u5916\u7684\u9ad8\u697c\u5927\u53a6\u4e0e\u4e0b\u96e8\u5929\u7a7a\u7684\u6b8b\u7f3a\u7684\u753b\u9762\uff0c\u201c\u6bcf\u4e2a\u4eba\u90fd\u5728\u91cc\u9762\u6251\u817e\uff0c\u603b\u662f\u8981\u7ed9\u6cb3\u6c34\u8d76\u7740\u5f80\u4e0b\u6e38\u8d70\u2026\u2026\u201d\u7a81\u7136\u626f\u5230\u8fd9\u6837\u7684\u8bdd\u9898\u4e00\u5b9a\u8ba9\u8bb8\u601d\u542c\u4e86\u4e00\u5934\u96fe\u6c34\uff0c\u5f20\u606a\u8f7b\u7b11\u8d77\u6765\uff0c\u201c\u8fd9\u5ea7\u57ce\u5e02\u7684\u6b23\u6b23\u5411\u8363\u8ba9\u8bb8\u591a\u4eba\u6d41\u79bb\u5fd8\u8fd4\uff0c\u5373\u4f7f\u9634\u973e\u7684\u96e8\u5929\u9ec4\u660f\uff0c\u8fd9\u5ea7\u90fd\u5e02\u90fd\u5145\u6ee1\u7740\u5f02\u6837\u7684\u60c5\u8c03\uff0c\u6211\u5728\u601d\u8003\u8fd9\u5ea7\u90fd\u5e02\u7e41\u8363\u80cc\u540e\u7684\u57fa\u7840\u662f\u4ec0\u4e48\uff0c\u662f\u575a\u56fa\u7684\u5ca9\u77f3\u8fd8\u662f\u8f7b\u6d6e\u7684\u6d41\u6c99\uff0c\u5f53\u7e41\u8363\u5c06\u9893\uff0c\u8fd9\u7eb7\u6270\u7684\u82b8\u82b8\u4f17\u751f\u76f8\u53c8\u662f\u600e\u6837\u7684\u6a21\u6837\uff1f\u201d\u6402\u8fc7\u8bb8\u601d\u8f7b\u677e\u7684\u7ea4\u8170\uff0c\u8ba9\u5979\u5a07\u8f6f\u7684\u8eab\u5b50\u4f9d\u5728\u81ea\u5df1\u7684\u6000\u91cc\uff0c\u5728\u5979\u7684\u7ea2\u6da6\u7684\u5634\u5507\u4e0a\u8f7b\u8f7b\u7684\u543b\u7740\uff0c\u8bb8\u601d\u7684\u5a07\u5a9a\u795e\u6001\u8ba9\u4eba\u5fc3\u8361\u9b42\u79fb\uff0c\u201c\u966a\u6211\u51fa\u53bb\u8d70\u8d70\uff0c\u6211\u559c\u6b22\u8fd9\u5ea7\u57ce\u5e02\u7684\u96e8\u5929\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u4e00\u95f4\u53eb\u4e0d\u51fa\u540d\u5b57\u7684\u5c0f\u5df7\u5b50\u91cc\uff0c\u5df7\u5b50\u62d0\u89d2\u4e34\u8857\u6311\u8d77\u4e00\u5757\u96e8\u5e18\uff0c\u8fd9\u5927\u6982\u662f\u5f20\u606a\u770b\u5230\u6700\u5c0f\u7684\u9152\u5427\uff0c\u96e8\u5e18\u4e0b\u6446\u7740\u4e24\u5f20\u9ad8\u811a\u5706\u51f3\uff0c\u539f\u6728\u7684\u5427\u53f0\u5c31\u8d34\u7740\u95e8\u6963\uff0c\u5750\u5728\u9ad8\u811a\u5706\u51f3\u4e0a\uff0c\u811a\u4f38\u51fa\u53bb\u80fd\u63a5\u5230\u96e8\u6ef4\uff0c\u98ce\u5411\u6b63\u597d\uff0c\u5f20\u606a\u4e0e\u8bb8\u601d\u5750\u5728\u9ad8\u811a\u5706\u51f3\u4e0a\uff0c\u55c5\u7740\u9999\u6e2f\u6625\u96e8\u7684\u6c14\u606f\u3001\u54c1\u5c1d\u7740\u6fb3\u5927\u5229\u4e9a\u7684\u7ea2\u9152\uff0c\u8fd9\u662f\u4e00\u6761\u5e72\u51c0\u7684\u6ca1\u6709\u591a\u5c11\u884c\u4eba\u7684\u8857\u5df7\uff0c\u53ef\u4ee5\u5c3d\u60c5\u611f\u53d7\u9999\u6e2f\u96e8\u540e\u9ec4\u660f\u7684\u60c5\u8c03\u3002</p><p>\u3000\u3000\u201c\u8981\u662f\u6d77\u5dde\u4e5f\u6709\u8fd9\u4e48\u4e00\u95f4\u9152\u5427\uff0c\u5c31\u592a\u597d\u4e86\u2026\u2026\u201d\u8bb8\u601d\u611f\u6168\u7684\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4f60\u5012\u662f\u60f3\u628a\u4e00\u5207\u597d\u7684\u4e1c\u897f\u90fd\u642c\u56de\u6d77\u5dde\u53bb\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u56fd\u4eba\u4f3c\u4e4e\u751f\u6d3b\u90fd\u5f88\u5306\u5fd9\uff0c\u5f88\u5c11\u6709\u4eba\u80fd\u60a0\u95f2\u4eab\u53d7\u4e0b\u5348\u65f6\u5149\u7684\u5962\u4f88\uff0c\u6240\u4ee5\uff0c\u5728\u56fd\u5185\u5496\u5561\u5385\u3001\u9152\u5427\u5c31\u90fd\u4e0e\u5962\u4f88\u76f8\u6302\u94a9\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5728\u8fd9\u4e34\u8857\u7684\u9152\u5427\uff0c\u54c1\u5c1d\u4e00\u676f\u4ea7\u81ea\u6fb3\u5927\u5229\u4e9a\u7684\u4f18\u8d28\u7ea2\u9152\uff0c\u53ea\u9700\u8981\u4e8c\u5341\u6e2f\u5e01\uff0c\u6bd4\u8d77\u9999\u6e2f\u7684\u4eba\u5747\u6536\u5165\uff0c\u90a3\u7edd\u5bf9\u662f\u4e0e\u5962\u4f88\u6302\u4e0d\u4e0a\u94a9\u7684\u3002</p><p>\u3000\u3000\u9648\u9759\u6491\u7740\u96e8\u4f1e\u8d70\u8fc7\u6765\uff0c\u8d70\u5230\u5df7\u5b50\u62d0\u89d2\uff0c\u96e8\u4f1e\u62ac\u4e86\u62ac\uff0c\u624d\u770b\u89c1\u5f20\u606a\u4e0e\u8bb8\u601d\u5750\u5728\u5979\u8fd9\u4e9b\u5929\u6765\u4e60\u60ef\u5750\u7684\u9ad8\u811a\u5706\u51f3\u4e0a\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u6211\u4e5f\u5728\u60f3\u96e8\u540e\u7684\u9ec4\u660f\u5728\u8fd9\u91cc\u996e\u4e00\u676f\u7ea2\u9152\u770b\u7740\u96e8\u4e1d\u4ece\u7ea2\u7816\u697c\u7684\u5c4b\u9876\u6ed1\u8fc7\u4e00\u5b9a\u4f1a\u5341\u5206\u7684\u6d6a\u6f2b\uff0c\u201d\u9648\u9759\u6491\u7740\u96e8\u4f1e\uff0c\u5a77\u5a77\u7389\u7acb\u7684\u51dd\u7738\u770b\u7740\u5f20\u606a\u3001\u8bb8\u601d\uff0c\u7cbe\u81f4\u7684\u8138\u5ed3\u4e0a\u52fe\u52d2\u51fa\u76c8\u76c8\u7684\u7b11\u610f\uff0c\u201c\u6ca1\u60f3\u5230\u8ba9\u4f60\u4eec\u4fe9\u4eba\u6377\u8db3\u5148\u767b\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e5f\u5728\u9999\u6e2f\uff1f\u201d\u8fd8\u662f\u4e00\u5468\u524d\u674e\u8fdc\u6e56\u5230\u6d77\u5dde\u89c6\u5bdf\u65f6\uff0c\u4e0e\u9648\u9759\u89c1\u8fc7\u9762\uff0c\u6ca1\u60f3\u5230\u5728\u9999\u6e2f\u53c8\u9047\u4e0a\uff0c\u4e34\u8857\u53ea\u6709\u4e24\u5f20\u9ad8\u811a\u5706\u51f3\uff0c\u5f20\u606a\u7ad9\u8d77\u6765\uff0c\u8ba9\u7ed9\u9648\u9759\u5750\u3002</p><p>\u3000\u3000\u201c\u5230\u9999\u6e2f\u6709\u4e94\u5929\u4e86\uff0c\u524d\u5929\u5750\u8f66\u7ecf\u8fc7\u8fd9\u91cc\uff0c\u770b\u5230\u8fd9\u5bb6\u4e34\u8857\u7684\u8ff7\u4f60\u9152\u5427\uff0c\u8fd9\u65f6\u5019\u6b63\u597d\u6ca1\u4e8b\uff0c\u5c31\u60f3\u7740\u8fc7\u6765\u5750\u4e00\u5750\uff0c\u6ca1\u60f3\u5230\u4f1a\u9047\u4e0a\u4f60\u4eec\u3002\u201d\u9648\u9759\u5c06\u6587\u4ef6\u5939\u653e\u5230\u4e34\u8857\u7684\u5427\u53f0\u4e0a\uff0c\u5c06\u7ea2\u8272\u7684\u6d0b\u4f1e\u606f\u4e86\u63d2\u5230\u4e00\u65c1\u7684\u91d1\u5c5e\u4e1d\u7f16\u7ec7\u7684\u9542\u7a7a\u4f1e\u7b52\u91cc\uff0c\u5c06\u843d\u5230\u773c\u524d\u7684\u4e00\u7f15\u5377\u66f2\u7684\u8910\u8272\u957f\u53d1\u634b\u81f3\u8033\u540e\uff0c\u7cbe\u81f4\u7684\u5986\u5bb9\uff0c\u7738\u773c\u542b\u6625\uff0c\u5979\u7a7f\u7740\u804c\u4e1a\u5957\u88d9\uff0c\u76f4\u8170\u5750\u7740\uff0c\u80f8\u90e8\u9ad8\u8038\u3001\u86ee\u8170\u7ea4\u76c8\uff0c\u65e0\u7aef\u7684\u6027\u611f\u8ff7\u4eba\u3002</p><p>\u3000\u3000\u96e8\u5e18\u4e0d\u5927\uff0c\u5f20\u606a\u53ea\u6709\u7aef\u7740\u9152\u676f\u4f9d\u5427\u53f0\u7ad9\u5728\u8bb8\u601d\u4e0e\u9648\u9759\u4e2d\u95f4\uff0c\u9f3b\u7aef\u55c5\u7740\u4e24\u4e2a\u7edd\u7f8e\u4f73\u4eba\u8eab\u4e0a\u4f20\u6765\u4e0d\u540c\u7684\u5e7d\u9999\uff0c\u4ed6\u5012\u662f\u6beb\u65e0\u987e\u8651\u7684\u4e0e\u8bb8\u601d\u6328\u8fd1\u4e00\u4e9b\uff0c\u626d\u8fc7\u5934\uff0c\u7b11\u7740\u5bf9\u9648\u9759\u8bf4\uff1a\u201c\u4f60\u4eec\u4fe9\u4eba\u5f80\u8fd9\u91cc\u4e00\u5750\uff0c\u8fd9\u96e8\u540e\u7684\u9ec4\u660f\u7684\u786e\u662f\u989d\u5916\u7684\u6709\u60c5\u8c03\u2026\u2026\u201d</p><p>\u3000\u3000\u867d\u7136\u662f\u4e34\u8857\u53ea\u6709\u4e24\u5f20\u9ad8\u811a\u5706\u51f3\u7684\u8ff7\u4f60\u9152\u5427\uff0c\u5427\u53f0\u91cc\u82b1\u767d\u5934\u53d1\u7684\u5e97\u4e3b\u4f9d\u7136\u4e00\u4e1d\u4e0d\u82df\u7a7f\u7740\u9152\u4fdd\u5236\u670d\uff0c\u4ed6\u62ff\u51fa\u4e00\u676f\u6676\u83b9\u5254\u900f\u7684\u73bb\u7483\u676f\u6446\u5230\u9648\u9759\u9762\u524d\uff0c\u5374\u5bf9\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u5c0f\u4f19\u5b50\uff0c\u771f\u7fa1\u6155\u4f60\uff0c\u6709\u4e24\u4eba\u5982\u82b1\u4f3c\u7389\u7684\u5973\u4f34\uff0c\u4e0d\u6653\u5f97\u4f1a\u7fa1\u715e\u591a\u5c11\u8def\u4eba\u7684\u76ee\u5149\u3002\u60f3\u6211\u5f53\u5e74\u4e5f\u66fe\u540c\u65f6\u4ea4\u5f80\u51e0\u4e2a\u7f8e\u4eba\uff0c\u8fd9\u7684\u786e\u662f\u65c1\u4eba\u7fa1\u6155\u4e0d\u4e86\u7684\u798f\u4efd\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u54c8\u54c8\u4e00\u7b11\uff0c\u201c\u6211\u6709\u8fd9\u798f\u4efd\uff1f\u201d</p><p>\u3000\u3000\u9648\u9759\u76c8\u76c8\u4e00\u7b11\uff0c\u4e5f\u4e0d\u5fd9\u7740\u5426\u8ba4\uff0c\u770b\u7740\u8001\u4eba\u59ff\u52bf\u4f18\u96c5\u7684\u5012\u9152\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8001\u4f2f\u60a8\u73b0\u5728\u4e5f\u80fd\u8ba8\u5e74\u8f7b\u5973\u5b69\u5b50\u559c\u6b22\u554a\uff01\u201d</p><p>\u3000\u3000\u8001\u4eba\u7709\u5b87\u660e\u4fca\uff0c\u5373\u4f7f\u6ee1\u5934\u82b1\u767d\u77ed\u53d1\uff0c\u4e5f\u7cbe\u795e\u6296\u64de\uff0c\u80fd\u5728\u4e0d\u5230\u4e09\u56db\u5e73\u65b9\u7c73\u7684\u4e34\u8857\u5e97\u94fa\u5f00\u4e00\u95f4\u8ff7\u4f60\u9152\u5427\u7684\u4eba\uff0c\u5927\u6982\u4e5f\u771f\u662f\u90a3\u79cd\u6027\u60c5\u4e0d\u7f81\u7684\u4eba\u624d\u80fd\u505a\u51fa\u6765\uff0c\u8fd9\u79cd\u7537\u4eba\u5e74\u8f7b\u65f6\u603b\u662f\u8ba8\u5973\u5b69\u5b50\u559c\u6b22\u3002</p><p>\u3000\u3000\u8001\u4eba\u6447\u5934\u7b11\u7740\u8bf4\uff1a\u201c\u4e0d\u884c\u4e86\uff0c\u60c5\u4e4b\u4e00\u5b57\u592a\u8fc7\u4f24\u4eba\uff0c\u6211\u8fd9\u8f88\u5b50\u4f24\u8fc7\u522b\u4eba\uff0c\u4e5f\u4f24\u8fc7\u81ea\u5df1\u3002\u611f\u60c5\u7684\u4e8b\u6709\u6b22\u4e50\u3001\u6709\u60b2\u4f24\uff0c\u60c5\u4eba\u603b\u662f\u611f\u6168\u6b22\u4e50\u7684\u65f6\u95f4\u592a\u8fc7\u77ed\u6682\u3001\u603b\u662f\u7559\u4e0b\u592a\u591a\u60b2\u4f24\u7684\u7a7a\u95f4\u3002\u6211\u4e00\u76f4\u5728\u60f3\u4e3a\u4ec0\u4e48\u60c5\u4eba\u4e4b\u95f4\u4e0d\u80fd\u4e13\u6ce8\u7684\u53bb\u4eab\u53d7\u8fd9\u4efd\u60c5\u611f\u4e2d\u7684\u6b22\u4e50\u4e0e\u751c\u7f8e\uff0c\u8981\u6653\u5f97\uff0c\u4e00\u4efd\u5b8c\u7f8e\u7684\u60c5\u611f\u53ea\u662f\u81ea\u5df1\u6240\u611f\u53d7\u5230\u7684\u90a3\u751c\u871c\u3001\u6b22\u4e50\u7684\u4e00\u90e8\u5206\u800c\u5df2\uff0c\u81f3\u4e8e\u60b2\u4f24\u4ec0\u4e48\u7684\uff0c\u5b8c\u5168\u5ffd\u89c6\u6389\u5c31\u53ef\u4ee5\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u662f\u4ec0\u4e48\u7231\u60c5\u7406\u8bba\uff1f\u201d\u9648\u9759\u63a5\u8fc7\u7ea2\u9152\u676f\uff0c\u4e0b\u5df4\u78d5\u5728\u5427\u53f0\u4e0a\uff0c\u7591\u60d1\u7684\u95ee\uff0c\u201c\u5f88\u96be\u7406\u89e3\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4f4d\u5c0f\u54e5\uff0c\u5728\u4f60\u773c\u91cc\uff0c\u662f\u4f4d\u5b8c\u7f8e\u7684\u60c5\u4eba\uff0c\u201d\u8001\u4eba\u8d77\u4e86\u8c08\u5174\uff0c\u5174\u81f4\u52c3\u52c3\u7684\u804a\u4e86\u8d77\u6765\uff0c\u624b\u6307\u7740\u8bb8\u601d\uff0c\u5bf9\u9648\u9759\u8bf4\uff0c\u201c\u5728\u8fd9\u4f4d\u7f8e\u4e3d\u7684\u5c0f\u59d0\u773c\u91cc\uff0c\u4e5f\u662f\u5b8c\u7f8e\u7684\u60c5\u4eba\uff0c\u8fd9\u4f4d\u5c0f\u59d0\u96be\u9053\u5c31\u53ef\u4ee5\u56e0\u4e3a\u4ed6\u8eab\u8fb9\u5df2\u7ecf\u6709\u4f60\u76f8\u4f34\u5c31\u8981\u53bb\u8fc1\u5c31\u53e6\u4e00\u4efd\u52c9\u5f3a\u7684\u60c5\u611f\u5417\uff1f\u5b8c\u7f8e\u7684\u60c5\u611f\u603b\u662f\u72ec\u4e00\u65e0\u4e8c\u7684\uff0c\u4f46\u662f\u53c8\u65f6\u4e0d\u65f6\u7684\u4f1a\u649e\u8f66\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u8001\u4f2f\u60a8\u5e74\u8f7b\u65f6\u4e00\u5b9a\u662f\u8bb8\u591a\u5973\u5b69\u5b50\u5fc3\u76ee\u4e2d\u7684\u5b8c\u7f8e\u60c5\u4eba\u5427\uff1f\u201d\u8bb8\u601d\u4fcf\u76ae\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u5ae3\u7136\u4e00\u7b11\u7684\u98ce\u60c5\u7aef\u662f\u8ff7\u4eba\u3002</p><p>\u3000\u3000\u201c\u6211\u5728\u522b\u4eba\u5fc3\u76ee\u662f\u5426\u591f\u5b8c\u7f8e\uff0c\u6211\u4e0d\u77e5\u9053\uff0c\u4f46\u662f\u6211\u5fc3\u76ee\u4e2d\u7684\u5b8c\u7f8e\u60c5\u4eba\u5374\u9009\u62e9\u5ac1\u7ed9\u4e86\u522b\u4eba\uff0c\u6211\u5728\u8fd9\u91cc\u5f00\u8fd9\u4e48\u4e00\u5bb6\u8ff7\u4f60\u9152\u5427\uff0c\u53ea\u662f\u5e0c\u671b\u5979\u8def\u8fc7\u65f6\u5076\u5c14\u80fd\u505c\u4e0b\u5750\u4e00\u5750\u559d\u4e00\u676f\u9152\u2026\u2026\u6709\u65f6\u5019\u662f\u5979\u4f1a\u4e0e\u5979\u5148\u751f\u4e00\u8d77\u5728\u8fd9\u4e24\u5f20\u9ad8\u5f20\u5706\u51f3\u4e0a\u5750\u4e0b\u6765\uff0c\u770b\u4e00\u770b\u7ea2\u7816\u697c\u9876\u4e4b\u4e0a\u7684\u5915\u9633\uff0c\u6211\u603b\u4f1a\u4e13\u6ce8\u7684\u4eab\u53d7\u6b22\u4e50\u7684\u611f\u89c9\u2026\u2026\u201d\u8001\u4eba\u671b\u7740\u8857\u5df7\u7684\u96e8\u5e18\uff0c\u5fae\u5e26\u7740\u611f\u6168\u7684\u8bed\u6c14\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u4ece\u5427\u53f0\u7684\u4e00\u89d2\u5e2e\u8001\u4eba\u62ff\u8fc7\u4e00\u53ea\u73bb\u7483\u676f\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u8bf7\u8001\u4f2f\u4f60\u559d\u676f\u9152\uff0c\u4e3a\u4e86\u8fd9\u95f4\u5728\u96e8\u540e\u9ec4\u660f\u7684\u5b8c\u7f8e\u9152\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u53c8\u6dfb\u4e86\u4e00\u6b21\u9152\uff0c\u7b49\u5df7\u5b50\u91cc\u884c\u4eba\u6e10\u591a\u8d77\u6765\uff0c\u5f20\u606a\u4ed8\u4e86\u516b\u676f\u7ea2\u9152\u7684\u9152\u94b1\u79bb\u5f00\u3002</p><p>\u3000\u3000\u79d1\u738b\u5728\u9999\u6e2f\u7684\u5206\u516c\u53f8\u8981\u8d70\u8fc7\u4e24\u6761\u8857\u9053\uff0c\u5f20\u606a\u4e0e\u8bb8\u601d\u6f2b\u65e0\u76ee\u7684\u7684\u5728\u9999\u6e2f\u7684\u8857\u5934\u4e71\u901b\uff0c\u4fbf\u5148\u6563\u6b65\u9001\u9648\u9759\u56de\u5979\u4eec\u516c\u53f8\u697c\u4e0b\u3002</p><p>\u3000\u3000\u9648\u9759\u6491\u7740\u4f1e\uff0c\u60f3\u8d77\u9152\u5427\u7684\u8001\u4eba\uff0c\u5634\u7b11\u6396\u7740\u7b11\u610f\uff0c\u7709\u773c\u5982\u6708\u7684\u8bf4\u9053\uff1a\u201c\u771f\u662f\u4e00\u4e2a\u6709\u8da3\u7684\u8001\u4eba\u2026\u2026\u6539\u5929\u6709\u7a7a\uff0c\u6211\u518d\u8bf7\u4f60\u4eec\u5230\u90a3\u95f4\u9152\u5427\u5750\u4e00\u5750\u3002\u201d\u5a77\u5a77\u7389\u7acb\u7684\u5979\u6491\u7740\u96e8\u4f1e\u7ad9\u5728\u96e8\u4e2d\uff0c\u683c\u5916\u7684\u5a07\u7f8e\u3002</p><p>\u3000\u3000\u201c\u597d\u554a\u2026\u2026\u201d\u5f20\u606a\u6491\u7740\u4f1e\uff0c\u5e2e\u8bb8\u601d\u6321\u7740\u96e8\uff0c\u770b\u7740\u9648\u9759\u8f6c\u8eab\u5f80\u5199\u5b57\u697c\u8d70\u53bb\uff0c\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u53c8\u558a\u4f4f\u5979\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u4e0d\u4f1a\u73b0\u5728\u8ba9\u6211\u8bf7\u4f60\u4eec\u4fe9\u5427\uff1f\u201d\u9648\u9759\u7591\u60d1\u7684\u8f6c\u8fc7\u8eab\u6765\uff0c\u201c\u6709\u4ec0\u4e48\u4e8b\u60c5\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u8be5\u6211\u591a\u5634\u591a\u820c\u7684\uff0c\u201d\u5f20\u606a\u811a\u637b\u7740\u5730\uff0c\u201c\u79d1\u738b\u5728\u4e1c\u5357\u4e9a\u7684\u7280\u5229\u6269\u5f20\u5e76\u975e\u6ca1\u6709\u57cb\u4e0b\u5371\u673a\uff0c\u6709\u4e9b\u89c2\u70b9\uff0c\u76f8\u4fe1\u4f60\u4eec\u4e5f\u5f88\u96be\u63a5\u53d7\uff0c\u4e0d\u8fc7\u6211\u76f8\u4fe1\u63d0\u9192\u79d1\u738b\u6ce8\u610f\u4e00\u4e0b\u73b0\u91d1\u6d41\u4e0e\u5e94\u6536\u6b3e\u9879\u7684\u95ee\u9898\u5e94\u8be5\u4e0d\u4f1a\u88ab\u89c6\u4e3a\u662f\u6076\u610f\u3002\u201d</p></article>"}}}]);
