(window.webpackJsonp=window.webpackJsonp||[]).push([[546],{568:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e5d\u8282 \u4eba\u751f\u610f\u4e49",content:"<article><h2>\u7b2c\u4e5d\u8282 \u4eba\u751f\u610f\u4e49</h2><p>\u51db\u4e0d\u80af\u518d\u591a\u8bf4\uff0c\u743c\u6069\u4e5f\u4fbf\u6ca1\u6709\u8ffd\u95ee\uff0c\u201c\u6211\u77e5\u9053\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u603b\u4e4b\u4e0d\u8981\u8ba9\u827e\u5f25\u8587\u56de\u5fa1\u5b87\u5c71\u5c31\u5bf9\u4e86\uff0c\u662f\u5427\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u800c\u4e14\u4e0d\u8981\u8ba9\u5979\u53d1\u73b0\uff1f\u201d</p><p>\u201c\u5bf9\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\u4e86\u3002\u201d\u743c\u6069\u518d\u6b21\u8bf4\uff0c\u505a\u51fa\u4fdd\u8bc1\u3002</p><p>\u51db\u70b9\u70b9\u5934\uff0c\u62c9\u7740\u743c\u6069\u7684\u624b\u5f80\u56de\u8d70\uff0c\u4ed6\u4eec\u8d8a\u8fc7\u77f3\u6865\uff0c\u5230\u4e86\u8f9b\u5df4\u6cb3\u5317\u3002\u65b0\u57ce\u533a\u89c4\u5212\u5f97\u5f88\u4e0d\u9519\uff0c\u533a\u57df\u5206\u660e\uff0c\u9053\u8def\u6574\u9f50\uff0c\u800c\u4e14\u8bbe\u6709\u4e13\u95e8\u7684\u884c\u4eba\u901a\u9053\u2014\u2014\u6700\u540e\u8fd9\u4e00\u70b9\u5c24\u5176\u96be\u5f97\uff0c\u81f3\u5c11\u743c\u6069\u5728\u5176\u4ed6\u6240\u6709\u57ce\u5e02\u90fd\u6ca1\u89c1\u5230\u8fc7\u3002\u743c\u6069\u548c\u51db\u6b63\u8d70\u7740\uff0c\u4e00\u67b6\u9a6c\u8f66\u4ece\u540e\u9762\u5feb\u901f\u9a76\u8fc7\u6765\uff0c\u7a81\u7136\u505c\u5728\u65c1\u8fb9\uff0c\u5e03\u5e18\u6380\u5f00\u4e86\uff0c\u4e00\u4e2a\u4eba\u4ece\u8f66\u4e2d\u8d70\u4e0b\u6765\uff0c\u201c\u665a\u4e0a\u597d\uff0c\u51db\u3002\u201d</p><p>\u743c\u6069\u8f6c\u8fc7\u5934\uff0c\u770b\u89c1\u4e00\u4f4d\u5927\u7ea6\u4e09\u5341\u4f59\u5c81\u7684\u4e2d\u5e74\u7537\u5b50\uff0c\u4ed6\u8138\u8272\u82cd\u767d\uff0c\u8eab\u6750\u5f02\u5e38\u7626\u524a\uff0c\u770b\u8d77\u6765\u4eff\u4f5b\u4e00\u9635\u98ce\u5c31\u80fd\u5439\u5012\u4f3c\u7684\u3002\u51db\u4f3c\u4e4e\u6014\u4e86\u6014\uff0c\u505c\u4f4f\u811a\u6b65\u671d\u4ed6\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u4f60\u597d\uff0c\u57c3\u535c\u62c9\u3002\u201d</p><p>\u201c\u542c\u4f0a\u68ee\u8bf4\u4f60\u53bb\u4e86\u4e2d\u571f\uff0c\u201d\u7537\u5b50\u8bf4\uff0c\u201c\u4ec0\u4e48\u65f6\u5019\u56de\u6765\u7684\uff1f\u8fd9\u4f4d\u662f\u4f60\u670b\u53cb\uff1f\u201d</p><p>\u201c\u672a\u5a5a\u592b\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u6211\u6709\u70b9\u4e8b\u60c5\u8981\u53bb\u6069\u745f\uff0c\u8def\u8fc7\u8fd9\u91cc\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u7537\u5b50\u5e94\u4e86\u4e00\u58f0\uff0c\u76ee\u5149\u5728\u743c\u6069\u8138\u4e0a\u6765\u56de\u626b\u4e86\u51e0\u904d\uff0c\u4eff\u4f5b\u4e5f\u6ca1\u4ec0\u4e48\u8bdd\u597d\u8bf4\uff0c\u4fbf\u62ac\u8d77\u5de6\u624b\uff0c\u5728\u80f8\u524d\u505a\u4e86\u4e2a\u5947\u602a\u7684\u624b\u52bf\uff0c\u50cf\u662f\u6253\u62db\u547c\uff0c\u7136\u540e\u5f84\u81ea\u8f6c\u8eab\u94bb\u8fdb\u9a6c\u8f66\u4e2d\uff0c\u8f66\u592b\u7529\u4e86\u58f0\u54cd\u97ad\uff0c\u8f66\u8f6e\u7cbc\u7cbc\u7ee7\u7eed\u524d\u884c\u3002</p><p>\u201c\u4ed6\u662f\u8c01\u554a\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u57ce\u4e3b\u6ce2\u65af\u62c9\u8428\u5c14\u7684\u513f\u5b50\uff0c\u540d\u5b57\u662f\u57c3\u535c\u62c9\u2014\u2014\u5728\u6069\u745f\u8bed\u91cc\u662f\u2018\u806a\u660e\u4eba\u2019\u7684\u610f\u601d\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u6211\u548c\u4ed6\u5176\u5b9e\u4e0d\u600e\u4e48\u719f\u6089\uff0c\u4e5f\u5c31\u89c1\u8fc7\u4e24\u6b21\u9762\uff0c\u8fd8\u662f\u4e09\u6b21\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u77e5\u9053\u4f60\u8fd8\u8ba4\u8bc6\u8fd9\u4e48\u591a\u4eba\u3002\u201d</p><p>\u201c\u6211\u66fe\u7ecf\u5728\u8fd9\u57ce\u91cc\u5f85\u4e86\u4e00\u5e74\u591a\u561b\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u827e\u5f25\u8587\u79bb\u5f00\u4e4b\u540e\uff0c\u6211\u8fd8\u5f85\u4e86\u534a\u5e74\u5462\u3002\u90a3\u65f6\u5019\u65b0\u57ce\u533a\u8fd8\u6ca1\u5efa\u6210\uff0c\u4e5f\u6ca1\u8fd9\u4e48\u591a\u7684\u5916\u6765\u4eba\uff0c\u672c\u5730\u5c45\u6c11\u5c31\u90a3\u4e48\u591a\uff0c\u65f6\u95f4\u957f\u4e86\u81ea\u7136\u4f1a\u8ba4\u8bc6\u4e00\u4e9b\u3002\u201d</p><p>\u8fd9\u5012\u4e5f\u662f\uff0c\u4f46\u743c\u6069\u597d\u5947\u7684\u5730\u65b9\u5176\u5b9e\u4e0d\u5728\u4e8e\u6b64\uff0c\u201c\u4f60\u597d\u50cf\u2026\u2026\u4e0d\u600e\u4e48\u559c\u6b22\u770b\u89c1\u4ed6\uff1f\u201d</p><p>\u201c\u4e5f\u4e0d\u662f\uff0c\u201d\u51db\u76b1\u4e86\u76b1\u7709\uff0c\u201c\u8fd9\u4e2a\u4eba\uff0c\u600e\u4e48\u8bf4\u5462\uff0c\u603b\u89c9\u5f97\u9634\u68ee\u68ee\u7684\uff0c\u6211\u4e00\u770b\u5230\u4ed6\u90a3\u5f20\u8138\u5c31\u89c9\u5f97\u4e0d\u8212\u670d\uff0c\u201d\u5979\u6487\u4e86\u6487\u5634\uff0c\u201c\u800c\u4e14\u592a\u7626\u4e86\uff0c\u7626\u5f97\u8ddf\u4e00\u6839\u8349\u4f3c\u7684\u3002\u201d</p><p>\u6211\u53ea\u77e5\u9053\u5f88\u591a\u4eba\u6b67\u89c6\u80d6\u5b50\uff0c\u5374\u4e0d\u77e5\u9053\u539f\u6765\u4f60\u8fd8\u6b67\u89c6\u7626\u5b50\u2026\u2026</p><p>\u201c\u4f60\u4e0d\u559c\u6b22\u4ed6\uff0c\u4ed6\u5012\u4f3c\u4e4e\u633a\u559c\u6b22\u4f60\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90fd\u6ca1\u89c1\u8fc7\u51e0\u6b21\uff0c\u8fd8\u7279\u5730\u505c\u4e0b\u6765\u8ddf\u4f60\u6253\u62db\u547c\u3002\u201d</p><p>\u201c\u8bf4\u5230\u8fd9\u4e2a\uff0c\u6211\u4e5f\u633a\u5947\u602a\u7684\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u4ee5\u524d\u4ed6\u53ef\u662f\u4ece\u6765\u4e0d\u4f1a\u4e3b\u52a8\u642d\u7406\u4eba\u7684\uff0c\u4e0d\u7ba1\u662f\u8c01\u3002\u4ed6\u5f1f\u5f1f\u8bf4\u4ed6\u5c0f\u65f6\u5019\u5927\u8111\u53d7\u8fc7\u4f24\uff0c\u4ece\u6b64\u5c31\u6027\u683c\u7279\u522b\u5b64\u50fb\u3002\u201d</p><p>\u201c\u8bf4\u4e0d\u5b9a\u4ed6\u4ee5\u524d\u6697\u604b\u4f60\uff0c\u53ea\u662f\u4e0d\u597d\u610f\u601d\u5f00\u53e3\uff0c\u201d\u743c\u6069\u5f00\u73a9\u7b11\uff0c\u201c\u73b0\u5728\u7ec8\u4e8e\u9f13\u8db3\u52c7\u6c14\u8981\u6765\u8868\u767d\uff0c\u7ed3\u679c\u4f60\u53c8\u4e00\u526f\u7231\u7406\u4e0d\u7406\u7684\u6837\u5b50\uff0c\u628a\u4ed6\u5413\u8dd1\u4e86\u3002\u201d</p><p>\u201c\u6211\u624d\u4e0d\u8981\u88ab\u8fd9\u79cd\u5bb6\u4f19\u559c\u6b22\u5462\uff0c\u201d\u5c0f\u5973\u5deb\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u5c31\u7b97\u6bd4\u4e0d\u4e0a\u827e\u5f25\u8587\uff0c\u8d77\u7801\u4e5f\u8981\u6709\u4f60\u7684\u6c34\u51c6\u624d\u53ef\u4ee5\u5427\uff0c\u5426\u5219\u8fde\u88ab\u8003\u8651\u7684\u8d44\u683c\u90fd\u6ca1\u6709\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u8fd9\u5230\u5e95\u7b97\u662f\u5938\u5956\u8fd8\u662f\u8d2c\u4f4e\u5462\uff1f\u201d</p><p>\u201c\u53ea\u662f\u9648\u8ff0\u4e8b\u5b9e\u5566\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u7b11\uff0c\u4e0d\u77e5\u600e\u7684\uff0c\u4ed6\u5fc3\u4e2d\u6709\u4e9b\u9690\u9690\u7684\u7591\u60d1\u3002\u57c3\u535c\u62c9\u4e34\u8d70\u65f6\u505a\u7684\u90a3\u4e2a\u5947\u602a\u7684\u624b\u52bf\uff0c\u603b\u662f\u5728\u773c\u524d\u53cd\u590d\u95ea\u73b0\uff0c\u4eff\u4f5b\u5728\u63d0\u9192\u7740\u4ed6\u67d0\u4e9b\u57cb\u5728\u8bb0\u5fc6\u6df1\u5904\u7684\u4e1c\u897f\u3002\u201c\u51db\uff0c\u201d\u8d70\u4e86\u4e00\u6bb5\u8def\uff0c\u743c\u6069\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u95ee\uff0c\u201c\u8fd9\u4e2a\uff0c\u201d\u4ed6\u6a21\u4eff\u4e86\u4e00\u4e0b\u57c3\u535c\u62c9\u7684\u52a8\u4f5c\uff0c\u5de6\u624b\u638c\u5fc3\u865a\u63e1\uff0c\u62c7\u6307\u5c48\u8d77\uff0c\u8d34\u5728\u4e2d\u6307\u548c\u65e0\u540d\u6307\u7684\u6307\u8179\u4e0a\uff0c\u201c\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u51db\u504f\u7740\u5934\u60f3\u4e86\u4f1a\uff0c\u201c\u4e0d\u77e5\u9053\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u53ef\u80fd\u662f\u67d0\u79cd\u6253\u62db\u547c\u7684\u65b9\u5f0f\u5427\u3002\u201d</p><p>\u201c\u5f7b\u68ee\u5854\u4eba\u662f\u8fd9\u6837\u6253\u62db\u547c\u7684\u5417\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u662f\uff0c\u201d\u51db\u8bf4\uff0c\u7136\u540e\u53c8\u72b9\u8c6b\u8d77\u6765\uff0c\u201c\u53cd\u6b63\u8f9b\u5df4\u57ce\u4eba\u80af\u5b9a\u4e0d\u4f1a\u8fd9\u6837\uff0c\u6216\u8bb8\u662f\u522b\u7684\u5730\u65b9\u7684\u4e60\u4fd7\uff1f\u4e5f\u53ef\u80fd\u662f\u67d0\u79cd\u53e4\u4ee3\u7684\u793c\u4eea\u5427\u3002\u57c3\u535c\u62c9\u559c\u6b22\u94bb\u7814\u4e00\u4e9b\u5f88\u751f\u50fb\u7684\u4e1c\u897f\uff0c\u8c01\u77e5\u9053\u4ed6\u7a76\u7adf\u662f\u4ec0\u4e48\u610f\u601d\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u51db\u662f\u4e2a\u5f88\u5355\u7eaf\u7684\u4eba\uff0c\u5bf9\u4e8e\u81ea\u5df1\u4e0d\u611f\u5174\u8da3\u7684\u4e1c\u897f\u5f80\u5f80\u5c31\u76f4\u63a5\u5ffd\u7565\uff0c\u7136\u800c\u743c\u6069\u603b\u89c9\u5f97\u4e8b\u60c5\u6ca1\u8fd9\u4e48\u7b80\u5355\uff0c\u67d0\u79cd\u76f4\u89c9\u544a\u8bc9\u4ed6\u5e94\u8be5\u63a2\u5bfb\u4e0b\u53bb\u3002\u5728\u8fd9\u65b9\u9762\uff0c\u51db\u5c31\u5e2e\u4e0d\u4e0a\u4ec0\u4e48\u5fd9\u4e86\uff0c\u597d\u5728\u743c\u6069\u4e5f\u538b\u6839\u5c31\u6ca1\u6307\u671b\u5979\u3002</p><p>\u56de\u5230\u65c5\u5e97\uff0c\u743c\u6069\u76f4\u63a5\u53bb\u627e\u6885\u83f2\u65af\u3002</p><p>\u201c\u8fd9\u4e2a\u624b\u52bf\u6211\u4e5f\u6ca1\u89c1\u8fc7\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u611f\u89c9\u4e0a\u4e0d\u50cf\u662f\u6253\u62db\u547c\uff0c\u592a\u9690\u853d\u4e86\uff0c\u66f4\u50cf\u662f\u4f60\u4eec\u5deb\u5e08\u5728\u65bd\u6cd5\u65f6\u7684\u52a8\u4f5c\u3002\u201d</p><p>\u201c\u4ed6\u5f53\u65f6\u6ca1\u6709\u65bd\u6cd5\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u8eab\u4e3a\u9ad8\u9636\u5deb\u5e08\uff0c\u8fd9\u4e00\u70b9\u4ed6\u53ef\u4ee5\u80af\u5b9a\uff0c\u201c\u800c\u4e14\u5982\u679c\u662f\u65bd\u6cd5\u52a8\u4f5c\uff0c\u4ed6\u662f\u4ec0\u4e48\u610f\u601d\u5462\uff1f\u5728\u5411\u6211\u8868\u660e\u540c\u884c\u8eab\u4efd\u5417\uff1f\u201d</p><p>\u201c\u4e0d\u77e5\u9053\uff0c\u6211\u6ca1\u542c\u8bf4\u4ed6\u5b66\u8fc7\u9b54\u6cd5\uff0c\u201d\u5979\u60f3\u4e86\u4e00\u4f1a\uff0c\u201c\u6211\u548c\u57c3\u535c\u62c9\u4e5f\u4e0d\u592a\u719f\uff0c\u4e0d\u8fc7\u6211\u77e5\u9053\u4ed6\u7684\u786e\u4e00\u76f4\u5728\u7814\u7a76\u67d0\u4e9b\u4e1c\u897f\u2014\u2014\u67d0\u4e9b\u4e0d\u592a\u597d\u7684\u4e1c\u897f\u2014\u2014\u6216\u8bb8\u4e0e\u6b64\u6709\u5173\u5427\u3002\u201d</p><p>\u201c\u4f60\u6240\u8c13\u4e0d\u592a\u597d\u7684\u4e1c\u897f\u662f\u6307\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u8fd9\u4e2a\u6bd4\u8f83\u96be\u5f62\u5bb9\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5f7b\u68ee\u5854\u4eba\u79f0\u4e4b\u4e3a\u2018\u89a1\u672f\u2019\uff0c\u5b83\u7684\u542b\u4e49\u5f88\u5e7f\uff0c\u6307\u4e0e\u90aa\u9b54\u3001\u8bc5\u5492\u3001\u4ea1\u7075\u3001\u6bd2\u836f\u6709\u5173\u7684\u77e5\u8bc6\u4e0e\u6280\u827a\u2026\u2026\u5927\u81f4\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u6885\u83f2\u65af\u89c9\u5f97\u8fd9\u4e2a\u8bcd\u4e0d\u592a\u5bb9\u6613\u89e3\u91ca\uff0c\u8fc7\u5206\u542b\u6df7\uff0c\u5728\u4e2d\u571f\u8bed\u8a00\u4e2d\u5e76\u6ca1\u6709\u4e00\u4e2a\u5bf9\u5e94\u7684\u4e13\u95e8\u8bcd\u6c47\uff0c\u4f46\u4ed6\u7406\u89e3\u8d77\u6765\u6beb\u65e0\u969c\u788d\u3002\u201c\u6240\u4ee5\u4ed6\u662f\u5728\u5411\u6211\u53d1\u51fa\u8bc5\u5492\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u6211\u89c9\u5f97\u4e0d\u50cf\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u201c\u4e5f\u53ef\u80fd\u6839\u672c\u5c31\u6ca1\u6709\u4ec0\u4e48\u610f\u601d\uff0c\u201d\u51db\u4ece\u6d74\u5ba4\u91cc\u8d70\u51fa\u6765\uff0c\u88f9\u7740\u6bdb\u5dfe\uff0c\u201c\u4f60\u4eec\u592a\u795e\u7ecf\u7d27\u5f20\u4e86\u3002\u8bf4\u4e0d\u5b9a\u4eba\u5bb6\u53ea\u662f\u5f53\u65f6\u624b\u6307\u62bd\u7b4b\u4e86\uff0c\u968f\u610f\u6d3b\u52a8\u4e00\u4e0b\u800c\u5df2\u3002\u201d</p><p>\u201c\u2026\u2026\u4e5f\u6709\u8fd9\u79cd\u53ef\u80fd\u3002\u201d</p><p>\u743c\u6069\u5f88\u5e0c\u671b\u81ea\u5df1\u4e5f\u80fd\u62e5\u6709\u548c\u51db\u4e00\u6837\u826f\u597d\u7684\u5fc3\u6001\uff0c\u53ef\u60dc\u4ed6\u505a\u4e0d\u5230\u3002\u5c3d\u7ba1\u6ca1\u6709\u4ec0\u4e48\u51ed\u636e\uff0c\u4f46\u76f4\u89c9\u5f88\u6e05\u695a\u5730\u544a\u8bc9\u4ed6\uff0c\u57c3\u535c\u62c9\u5f53\u65f6\u505a\u7684\u90a3\u4e2a\u624b\u52bf\u662f\u6709\u7279\u522b\u542b\u4e49\u7684\uff0c\u5e94\u8be5\u662f\u67d0\u79cd\u6697\u53f7\u3002</p><p>\u201c\u57c3\u535c\u62c9\u662f\u4e2a\u4ec0\u4e48\u6837\u7684\u4eba\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u4e00\u4e2a\u83ab\u540d\u5176\u5999\u7684\u4eba\u3002\u201d\u8fd9\u662f\u51db\u7684\u7b54\u6848\u3002</p><p>\u6885\u83f2\u65af\u60f3\u4e86\u60f3\uff0c\u201c\u966a\u6211\u51fa\u53bb\u8d70\u8d70\u3002\u201d\u5979\u5bf9\u743c\u6069\u8bf4\u3002</p><p>\u201c\u55ef\u3002\u201d</p><p>\u51db\u4e5f\u8981\u8ddf\u8fc7\u6765\uff0c\u5374\u88ab\u6885\u83f2\u65af\u5236\u6b62\u4e86\uff0c\u547d\u4ee4\u5979\u4e56\u4e56\u5f85\u5728\u623f\u95f4\u91cc\u4f11\u606f\u3002\u4e24\u4eba\u8d70\u51fa\u65c5\u5e97\uff0c\u8d70\u4e86\u4e00\u6bb5\u8def\uff0c\u5728\u4e00\u4e2a\u504f\u50fb\u7684\u8857\u9053\u62d0\u89d2\u505c\u4f4f\u4e86\u3002\u6885\u83f2\u65af\u793a\u610f\u743c\u6069\u63fd\u7740\u5979\u7684\u8170\uff0c\u4e00\u56e2\u6de1\u6de1\u7684\u96fe\u6c14\u5c06\u4ed6\u4eec\u7684\u8eab\u4f53\u5305\u88f9\uff0c\u7136\u540e\u6f02\u6d6e\u8d77\u6765\uff0c\u79bb\u5f00\u5730\u9762\uff0c\u5347\u4e0a\u9ad8\u7a7a\uff0c\u4fef\u77b0\u591c\u8272\u4e2d\u7684\u8f9b\u5df4\u57ce\u3002</p><p>\u767d\u5929\u8d70\u5728\u8857\u9053\u4e0a\u8fd8\u4e0d\u89c9\u5f97\uff0c\u73b0\u5728\u5728\u7a7a\u4e2d\u4fef\u77b0\uff0c\u743c\u6069\u53d1\u73b0\u8f9b\u5df4\u57ce\u7684\u9762\u79ef\u8fd8\u4e0d\u5c0f\u3002\u6cb3\u5357\u5cb8\u7684\u8001\u57ce\u533a\u770b\u8d77\u6765\u6bd4\u8f83\u6742\u4e71\u7834\u8d25\uff0c\u4f46\u6cb3\u5317\u5cb8\u7684\u65b0\u57ce\u533a\u5219\u5efa\u7b51\u6574\u9f50\uff0c\u8857\u9053\u7b14\u76f4\uff0c\u5de6\u53f3\u5bf9\u79f0\uff0c\u5b9b\u5982\u68cb\u5c40\uff0c\u5012\u662f\u633a\u7b26\u5408\u743c\u6069\u8bb0\u5fc6\u4e2d\u73b0\u4ee3\u57ce\u5e02\u7684\u6a21\u6837\u3002</p><p>\u201c\u57c3\u535c\u62c9\u6027\u683c\u5b64\u50fb\uff0c\u6df1\u5c45\u7b80\u51fa\uff0c\u5f88\u5c11\u548c\u4eba\u63a5\u89e6\uff0c\u6211\u548c\u51db\u5bf9\u4ed6\u4e5f\u6ca1\u4ec0\u4e48\u4e86\u89e3\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u4e0d\u8fc7\u6709\u4eba\u66fe\u7ecf\u544a\u8bc9\u6211\u4eec\u8bf4\uff0c\u8f9b\u5df4\u57ce\u8fd9\u5341\u516d\u5e74\u6765\u7684\u79cd\u79cd\u53d8\u9769\u653f\u4ee4\uff0c\u8868\u9762\u4e0a\u662f\u57ce\u4e3b\u6ce2\u65af\u62c9\u8428\u5c14\u9881\u5e03\uff0c\u4f46\u5176\u5b9e\u5927\u534a\u90fd\u51fa\u81ea\u57c3\u535c\u62c9\u4e4b\u624b\u3002\u201d</p><p>\u201c\u54e6\uff1f\u201d</p><p>\u743c\u6069\u9887\u4e3a\u51fa\u4e4e\u610f\u6599\uff0c\u56e0\u4e3a\u5916\u8c8c\u7ed9\u4eba\u7684\u5148\u5165\u4e3a\u4e3b\u5370\u8c61\uff0c\u52a0\u4e0a\u6885\u83f2\u65af\u548c\u51db\u4e4b\u524d\u7684\u63cf\u8ff0\uff0c\u8ba9\u4ed6\u4ee5\u4e3a\u8fd9\u4e2a\u57c3\u535c\u62c9\u662f\u4e2a\u5b66\u8005\u4e4b\u6d41\u7684\u4eba\u7269\uff0c\u5374\u6ca1\u60f3\u5230\u8fd8\u6709\u53ef\u80fd\u662f\u9690\u8eab\u5e55\u540e\u7684\u653f\u6cbb\u5bb6\u3002\u6cbb\u6c11\u7406\u653f\u662f\u975e\u5e38\u7cbe\u7ec6\u800c\u590d\u6742\u7684\u5b9e\u8df5\u5de5\u4f5c\uff0c\u53ef\u4e0d\u4ec5\u4ec5\u53ea\u662f\u9760\u5929\u751f\u806a\u660e\uff0c\u95ed\u95e8\u9020\u8f66\u5c31\u80fd\u505a\u5230\u7684\uff0c\u5018\u82e5\u6885\u83f2\u65af\u6240\u8a00\u5c5e\u5b9e\uff0c\u90a3\u8fd9\u4f4d\u57c3\u535c\u62c9\u53ef\u771f\u662f\u4e0d\u7b80\u5355\u3002</p><p>\u201c\u4f60\u8fd9\u4e48\u8bf4\uff0c\u6211\u5012\u662f\u60f3\u8d77\u6765\u4e86\uff0c\u201d\u743c\u6069\u56de\u5fc6\u4e86\u4e00\u4e0b\uff0c\u201c\u77e5\u8bc6\u795e\u6bbf\u7684\u8d44\u6599\u91cc\u4e5f\u6709\u63d0\u53ca\uff0c\u6700\u8fd1\u51e0\u5e74\u6765\uff0c\u8f9b\u5df4\u57ce\u4e3b\u7684\u8eab\u4f53\u4e00\u76f4\u4e0d\u597d\uff0c\u9664\u4e86\u4e00\u4e9b\u5fc5\u8981\u573a\u5408\uff0c\u5f88\u5c11\u51fa\u5934\u9732\u9762\uff0c\u6743\u529b\u5b9e\u9645\u4e0a\u5df2\u7ecf\u79fb\u4ea4\u5230\u5176\u957f\u5b50\u624b\u4e2d\u2014\u2014\u4e0d\u8fc7\u6211\u5f53\u65f6\u6ca1\u6ce8\u610f\u8fd9\u4e2a\u2018\u957f\u5b50\u2019\u7684\u540d\u5b57\u3002\u201d</p><p>\u201c\u5c31\u662f\u57c3\u535c\u62c9\u3002\u6ce2\u65af\u62c9\u8428\u5c14\u57ce\u4e3b\u6709\u4e09\u5b50\u4e24\u5973\uff0c\u57c3\u535c\u62c9\u662f\u5176\u957f\u5b50\uff0c\u4e5f\u6700\u5f97\u4fe1\u4efb\u548c\u501a\u91cd\u3002\u8001\u57ce\u4e3b\u7684\u8eab\u4f53\u72b6\u51b5\u5df2\u7ecf\u5f88\u4e0d\u4e50\u89c2\uff0c\u53ea\u6015\u65f6\u65e5\u65e0\u591a\uff0c\u4e00\u65e6\u53bb\u4e16\uff0c\u57c3\u535c\u62c9\u5c31\u662f\u65b0\u7684\u8f9b\u5df4\u57ce\u4e3b\uff0c\u201d\u6885\u83f2\u65af\u8bf4\u7740\uff0c\u5f80\u4e0b\u65b9\u6307\u4e86\u6307\uff0c\u201c\u4f60\u89c9\u5f97\u65b0\u57ce\u533a\u5efa\u9020\u5f97\u5982\u4f55\uff1f\u201d</p><p>\u201c\u5f88\u4e0d\u9519\u3002\u201d</p><p>\u201c\u6574\u4e2a\u65b0\u57ce\u533a\u7684\u9009\u5740\u89c4\u5212\u3001\u5efa\u7b51\u5e03\u5c40\uff0c\u90fd\u662f\u57c3\u535c\u62c9\u4e00\u4eba\u5b8c\u6210\u7684\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5f53\u7136\u8fd9\u4e0d\u662f\u5b98\u65b9\u8bf4\u6cd5\uff0c\u4f46\u5e94\u5f53\u5c5e\u5b9e\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u5bb6\u4f19\u8fd8\u662f\u4eba\u7c7b\u5417\uff1f\u201d</p><p>\u201c\u4ece\u8eab\u6750\u6765\u770b\u4e0d\u50cf\u662f\u4e2a\u77ee\u4eba\uff0c\u201d\u5c11\u5973\u5f00\u4e86\u53e5\u73a9\u7b11\uff0c\u7136\u540e\u6536\u655b\u7b11\u5bb9\uff0c\u201c\u4f60\u5728\u731c\u6d4b\u4ec0\u4e48\uff0c\u662f\u5427\uff1f\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u5bf9\u6885\u83f2\u65af\u4ed6\u6ca1\u4ec0\u4e48\u597d\u9690\u7792\u7684\uff0c\u201c\u6211\u4eca\u5929\u542c\u5230\u4e00\u4e2a\u4f20\u8bf4\uff0c\u6709\u5173\u8f9b\u5df4\u57ce\u7684\u8d77\u6e90\u7684\u3002\u201d</p><p>\u201c\u88ab\u795e\u738b\u6740\u6b7b\u7684\u5deb\u5e08\uff0c\u57cb\u846c\u5728\u8f9b\u5df4\u6cb3\u7684\u5317\u5cb8\uff0c\u201d\u6885\u83f2\u65af\u77e5\u9053\u4ed6\u8981\u8bf4\u4ec0\u4e48\uff0c\u201c\u4f60\u8ba4\u4e3a\u4ed6\u662f\u4e00\u4f4d\u5947\u68b0\u5e08\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u6240\u4ee5\u57c3\u535c\u62c9\u662f\u88ab\u5947\u68b0\u5e08\u7684\u9b3c\u9b42\u63a7\u5236\u4e86\uff1f\u8fd9\u5012\u662f\u80fd\u89e3\u91ca\u5f88\u591a\u95ee\u9898\u3002\u201d</p><p>\u201c\u5bf9\u554a\uff0c\u5047\u8bbe\u4ed6\u7684\u8eaf\u58f3\u91cc\u662f\u4e00\u4e2a\u5947\u68b0\u5e08\u7684\u53e4\u8001\u4ea1\u7075\uff0c\u90a3\u4e48\u4ed6\u5b8c\u5168\u6709\u53ef\u80fd\u770b\u51fa\u6211\u7684\u6765\u5386\uff0c\u6240\u4ee5\u4ed6\u505a\u7684\u90a3\u4e2a\u5947\u602a\u624b\u52bf\u5176\u5b9e\u662f\u4e2a\u8868\u660e\u8eab\u4efd\u7684\u6697\u53f7\uff1f\u201d</p><p>\u4e00\u4e2a\u58f0\u97f3\u7a81\u7136\u5728\u65c1\u8fb9\u54cd\u8d77\uff0c\u201c\u4e0d\u662f\u3002\u201d</p><p>\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u7136\u540e\u624d\u53d1\u73b0\u58f0\u97f3\u662f\u4ece\u81ea\u5df1\u7684\u8170\u5e26\u4e0a\u53d1\u51fa\u3002\u4ed6\u6014\u4e86\u6014\uff0c\u5c06\u5927\u4e3b\u6559\u8d60\u9001\u7684\u90a3\u67c4\u5315\u9996\u62d4\u51fa\u9798\uff0c\u4e3e\u5728\u9762\u524d\uff0c\u201c\u51b0\u8679\uff1f\u201d</p><p>\u201c\u5f53\u7136\u662f\u6211\u3002\u201d\u5b9d\u77f3\u9f99\u7684\u58f0\u97f3\u4ece\u5315\u9996\u4e2d\u4f20\u51fa\u3002</p><p>\u201c\u4f60\u6700\u8fd1\u5e72\u5417\u53bb\u4e86\uff0c\u4e00\u76f4\u90fd\u6ca1\u52a8\u9759\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u5931\u8e2a\u4e86\u5462\u3002\u201d</p><p>\u201c\u6211\u6253\u4e86\u4e2a\u76f9\u800c\u5df2\uff0c\u201d\u9f99\u8bf4\uff0c\u201c\u53cd\u6b63\u4f60\u53c8\u6ca1\u4ec0\u4e48\u4e8b\u627e\u6211\u3002\u201d</p><p>\u201c\u4f60\u521a\u624d\u8bf4\u2018\u4e0d\u662f\u2019\uff0c\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u201c\u90a3\u4e2a\u4eba\u8eab\u4e0a\u6ca1\u6709\u4ea1\u7075\u7684\u5473\u9053\uff0c\u4e00\u70b9\u90fd\u6ca1\u6709\uff0c\u201d\u9f99\u6253\u4e86\u4e2a\u54c8\u6b20\uff0c\u4eff\u4f5b\u5f88\u65e0\u804a\u7684\u6837\u5b50\uff0c\u201c\u6240\u4ee5\u4f60\u4eec\u7684\u731c\u6d4b\u6beb\u65e0\u610f\u4e49\uff0c\u56e0\u4e3a\u4ece\u4e00\u5f00\u59cb\u5c31\u4e0d\u6210\u7acb\u3002\u201d</p><p>\u201c\u54e6\uff1f\u201d</p><p>\u201c\u4ed6\u505a\u7684\u90a3\u4e2a\u624b\u52bf\u5462\uff1f\u201d\u6885\u83f2\u65af\u95ee\uff0c\u201c\u4f60\u8ba4\u8bc6\u5417\uff1f\u201d</p><p>\u9f99\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u50cf\u662f\u5728\u52aa\u529b\u56de\u5fc6\uff0c\u201c\u4e0d\u8ba4\u8bc6\uff0c\u201d\u5b83\u6700\u540e\u8bf4\uff0c\u201c\u5e94\u8be5\u53ea\u662f\u5355\u7eaf\u7684\u624b\u6307\u62bd\u7b4b\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u8fd9\u4e9b\u65e0\u5173\u7d27\u8981\u7684\u5c0f\u4e8b\u5148\u4e0d\u63d0\uff0c\u201d\u9f99\u8bf4\uff0c\u4ed6\u7684\u58f0\u97f3\u53d8\u5f97\u4e25\u8083\uff0c\u201c\u6211\u6709\u4e00\u4e2a\u5f88\u91cd\u8981\u7684\u8981\u6c42\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u6211\u5df2\u7ecf\u5f88\u4e45\u6ca1\u6709\u771f\u6b63\u5165\u7761\u4e86\u2014\u2014\u4f60\u77e5\u9053\uff0c\u8001\u4eba\u7761\u7720\u8d28\u91cf\u90fd\u4e0d\u597d\u3002\u53d8\u6210\u4ea1\u7075\u90a3\u6bb5\u65f6\u95f4\uff0c\u6211\u8eab\u5fc3\u75db\u82e6\uff0c\u7cbe\u795e\u7126\u8651\uff0c\u76f4\u5230\u6210\u4e3a\u5668\u7075\u624d\u653e\u677e\u4e0b\u6765\uff0c\u201d\u9f99\u8bf4\uff0c\u201c\u6211\u521a\u521a\u7761\u4e86\u4e00\u89c9\uff0c\u505a\u4e86\u4e2a\u7f8e\u68a6\uff0c\u73b0\u5728\u7cbe\u795e\u9971\u6ee1\uff0c\u611f\u89c9\u6574\u6761\u9f99\u90fd\u7115\u7136\u4e00\u65b0\uff0c\u521b\u4f5c\u7075\u611f\u6c79\u6d8c\u800c\u6765\u3002\u201d</p><p>\u201c\u6240\u4ee5\uff1f\u201d</p><p>\u201c\u6240\u4ee5\u6211\u60f3\u753b\u753b\uff01\u201d</p><p>\u6211\u8fd8\u60f3\u6253\u7bee\u7403\u5462\uff0c\u743c\u6069\u8038\u4e86\u8038\u80a9\uff0c\u201c\u4f60\u90fd\u6ca1\u6709\u53f3\u524d\u722a\u4e86\uff0c\u600e\u4e48\u753b\u753b\uff1f\u201d</p><p>\u201c\u8fd9\u4e0d\u662f\u91cd\u70b9\uff0c\u6211\u53ef\u4ee5\u7528\u60f3\u8c61\u4f5c\u4e3a\u753b\u7b14\uff0c\u5728\u610f\u8bc6\u7684\u7a7a\u767d\u753b\u5e03\u4e0a\u6cfc\u58a8\u6325\u6beb\uff0c\u7eb5\u6a2a\u9a70\u9a8b\uff0c\u201d\u9f99\u8bf4\uff0c\u201c\u91cd\u70b9\u5728\u4e8e\uff0c\u6211\u9700\u8981\u4e00\u4e2a\u6a21\u7279\u3002\u201d</p><p>\u201c\u2026\u2026\u6240\u4ee5\u4f60\u7684\u610f\u601d\u662f\u8981\u6211\u5e2e\u4f60\u53bb\u627e\u4e2a\u6a21\u7279\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u201d\u9f99\u8bf4\uff0c\u201c\u6211\u7684\u8981\u6c42\u53c8\u4e0d\u9ad8\uff0c\u53ea\u8981\u8138\u86cb\u6f02\u4eae\u8eab\u6750\u7a88\u7a95\u6c14\u8d28\u51fa\u4f17\u5c31\u53ef\u4ee5\u4e86\uff0c\u4f60\u8eab\u8fb9\u4e00\u5927\u628a\uff0c\u968f\u4fbf\u5b89\u6392\u4e00\u4e2a\u5c31\u884c\uff0c\u6211\u4e0d\u6311\u5254\u7684\u3002\u201d</p><p>\u201c\u4f60\u8fd9\u8fd8\u53eb\u8981\u6c42\u4e0d\u9ad8\uff1f\u201d\u743c\u6069\u65e0\u529b\u5410\u69fd\uff0c\u201c\u7b97\u4e86\uff0c\u6211\u5e2e\u4f60\u95ee\u95ee\u8c01\u6709\u5174\u8da3\u2014\u2014\u7b49\u4e00\u4e0b\uff0c\u201d\u4ed6\u7a81\u7136\u60f3\u8d77\u6765\uff0c\u201c\u4f60\u6240\u8c13\u7684\u6a21\u7279\u662f\u8981\u88f8\u4f53\u5417\uff1f\u201d</p><p>\u201c\u7a7f\u4e86\u8863\u670d\u8fd8\u53eb\u4ec0\u4e48\u6a21\u7279\uff1f\u201d\u9f99\u53cd\u95ee\u3002</p><p>\u201c\u90a3\u4e0d\u53ef\u80fd\uff0c\u201d\u743c\u6069\u65ad\u7136\u62d2\u7edd\uff0c\u201c\u6211\u624d\u4e0d\u4f1a\u8ba9\u6211\u5973\u4eba\u4e0d\u7a7f\u8863\u670d\u7ed9\u522b\u7684\u7537\u4eba\u770b\u2014\u2014\u96c4\u9f99\u4e5f\u4e0d\u884c\u3002\u201d</p><p>\u201c\u522b\u90a3\u4e48\u5c0f\u6c14\u561b\uff0c\u201d\u9f99\u8bf4\uff0c\u201c\u6709\u597d\u4e1c\u897f\u8981\u61c2\u5f97\u548c\u522b\u4eba\u4e00\u8d77\u5206\u4eab\u3002\u800c\u4e14\u6211\u8fd9\u662f\u827a\u672f\u554a\uff0c\u827a\u672f\u4f60\u61c2\u4e0d\u61c2\uff1f\u4e0d\u8981\u7528\u4f60\u90a3\u79cd\u4f4e\u4fd7\u7684\u601d\u7ef4\u6765\u8861\u91cf\u6211\uff0c\u6211\u662f\u5b8c\u5168\u7eaf\u6d01\u7684\u76ee\u7684\uff0c\u62b1\u7740\u5bf9\u7f8e\u597d\u4e8b\u7269\u7684\u65e0\u6bd4\u5411\u5f80\u548c\u94a6\u6155\u6765\u6b23\u8d4f\u7684\u3002\u201d</p><p>\u201c\u603b\u4e4b\u5c31\u662f\u4e0d\u884c\u3002\u201d</p><p>\u201c\u4e0d\u80fd\u5546\u91cf\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u597d\u5546\u91cf\u7684\u3002\u201d</p><p>\u201c\u8bdd\u4e0d\u8981\u8bf4\u8fd9\u4e48\u7edd\u5bf9\uff0c\u201d\u9f99\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u53ef\u662f\u6709\u4f60\u7684\u628a\u67c4\u3002\u201d</p><p>\u5207\uff0c\u743c\u6069\u538b\u6839\u4e0d\u4fe1\uff0c\u51b0\u8679\u8ba4\u8bc6\u4ed6\u53c8\u6ca1\u591a\u4e45\uff0c\u600e\u4e48\u53ef\u80fd\u638c\u63e1\u4ed6\u4ec0\u4e48\u9ed1\u6750\u6599\u3002\u81ea\u5df1\u884c\u5f97\u6b63\uff0c\u5750\u5f97\u76f4\uff0c\u5149\u660e\u78ca\u843d\uff0c\u54ea\u6709\u4ec0\u4e48\u628a\u67c4\u3002\u201c\u6211\u4e0d\u540c\u610f\uff0c\u4f60\u51c6\u5907\u600e\u4e48\u505a\uff1f\u201d</p><p>\u201c\u6211\u5c31\u6ee1\u5927\u8857\u5ba3\u4f20\uff0c\u89c1\u4eba\u5c31\u8bf4\u4f60\u4e0d\u4e3e\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u9760\uff01\u201d</p><p>\u51b0\u8679\u7684\u8fd9\u4e2a\u5a01\u80c1\u7684\u786e\u6709\u6740\u4f24\u529b\uff0c\u743c\u6069\u4e0d\u7981\u8003\u8651\u8981\u4e0d\u8981\u628a\u8fd9\u8001\u5bb6\u4f19\u4e22\u5230\u6b21\u5143\u888b\u91cc\uff0c\u4f46\u6700\u7ec8\u8fd8\u662f\u89c9\u5f97\u5bf9\u957f\u8005\u5e94\u8be5\u4fdd\u6301\u4e00\u5b9a\u7a0b\u5ea6\u7684\u5c0a\u91cd\u3002\u4ed6\u601d\u8003\u7247\u523b\uff0c\u51b3\u5b9a\u4f5c\u51fa\u4e00\u4e9b\u8ba9\u6b65\u3002\u201c\u597d\u5427\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u53ef\u4ee5\u7ed9\u4f60\u627e\u4e2a\u6a21\u7279\uff0c\u4f46\u5979\u4e0d\u53ef\u80fd\u88f8\u4f53\uff0c\u8fd9\u662f\u539f\u5219\u95ee\u9898\uff0c\u6ca1\u6709\u8ba8\u8bba\u7684\u4f59\u5730\u3002\u201d</p><p>\u201c\u53ef\u662f\u2014\u2014\u201d</p><p>\u201c\u4e0d\u8fc7\u6211\u4f1a\u5efa\u8bae\u5979\u7a7f\u5f97\u5c11\u4e00\u70b9\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u7b97\u662f\u534a\u88f8\u3002\u201d</p><p>\u9f99\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u50cf\u662f\u5728\u6743\u8861\uff0c\u201c\u90a3\u597d\u5427\uff0c\u201d\u5b83\u6700\u540e\u561f\u56d4\u7740\u8bf4\uff0c\u201c\u534a\u88f8\u5c31\u534a\u88f8\uff0c\u9a6c\u9a6c\u864e\u864e\u5148\u5c06\u5c31\u5427\u3002\u201d</p><p>\u201c\u90a3\u4f60\u80fd\u7ed9\u6211\u4ec0\u4e48\u56de\u62a5\u5462\uff1f\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u56de\u62a5\uff1f\u201d\u9f99\u7591\u60d1\u3002</p><p>\u201c\u5f53\u7136\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u4e3a\u4f60\u63d0\u4f9b\u7ed8\u753b\u6a21\u7279\uff0c\u96be\u9053\u4f60\u4e0d\u5e94\u8be5\u7ed9\u4e88\u4e00\u4e9b\u56de\u62a5\u5417\uff1f\u201d</p><p>\u201c\u8fd9\u4e48\u65a4\u65a4\u8ba1\u8f83\u592a\u4f24\u611f\u60c5\u4e86\u5427\uff0c\u201d\u9f99\u8bf4\uff0c\u201c\u4f60\u770b\u5f88\u591a\u4eba\u517b\u5ba0\u7269\uff0c\u4e5f\u4ece\u6765\u6ca1\u6709\u8981\u6c42\u56de\u62a5\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u597d\u6b79\u4e5f\u662f\u4e00\u6761\u9f99\uff0c\u5c45\u7136\u62ff\u5ba0\u7269\u81ea\u6bd4\uff0c\u4f60\u8fd8\u6709\u6ca1\u6709\u4e00\u70b9\u57fa\u672c\u7684\u8282\u64cd\uff1f\u201d</p><p>\u201c\u8282\u64cd\u6709\u4ec0\u4e48\u7528\uff0c\u201d\u9f99\u4e0d\u4ee5\u4e3a\u7136\uff0c\u201c\u80fd\u5f53\u5b9d\u77f3\u5403\u5417\uff1f\u201d</p><p>\u743c\u6069\u6ca1\u6709\u9884\u6599\u5230\u5bf9\u65b9\u7684\u4e0b\u9650\u80fd\u4f4e\u5230\u8fd9\u79cd\u7a0b\u5ea6\uff0c\u4e0d\u8fc7\u8fd9\u4e5f\u53ea\u662f\u8ba9\u4ed6\u6014\u4e86\u4e00\u4e0b\u800c\u5df2\uff0c\u201c\u5ba0\u7269\u4e5f\u662f\u6709\u56de\u62a5\u7684\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6492\u5a07\u6253\u6eda\u7ffb\u8ddf\u5934\u6447\u5c3e\u5df4\uff0c\u7ed9\u4e3b\u4eba\u63d0\u4f9b\u7cbe\u795e\u6109\u60a6\uff0c\u6709\u7684\u8fd8\u4f1a\u53fc\u62d6\u978b\u5462\u3002\u201d</p><p>\u201c\u6211\u624d\u4e0d\u4f1a\u53fc\u62d6\u978b\uff01\u201d\u9f99\u6c14\u6025\u8d25\u574f\u3002</p><p>\u201c\u6211\u4e5f\u6ca1\u6709\u62d6\u978b\u8ba9\u4f60\u53fc\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u5e76\u65e0\u4ed6\u610f\uff0c\u53ea\u662f\u5173\u4e8e\u57c3\u535c\u62c9\u8fd9\u4e2a\u4eba\uff0c\u8fd8\u60f3\u542c\u542c\u4f60\u7684\u66f4\u591a\u770b\u6cd5\u3002\u4f60\u662f\u4f4d\u957f\u8005\uff0c\u5386\u7ecf\u65f6\u5149\u6c89\u6dc0\u7684\u7ecf\u9a8c\u5c31\u662f\u73cd\u8d35\u7684\u8d22\u5bcc\uff0c\u6bd4\u4efb\u4f55\u5b9d\u77f3\u90fd\u6709\u4ef7\u503c\u3002\u201d</p><p>\u9f99\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u8fd9\u8fd8\u5dee\u4e0d\u591a\uff0c\u201d\u5b83\u60f3\u4e86\u60f3\uff0c\u201c\u90a3\u4e2a\u53eb\u57c3\u535c\u62c9\u7684\u4eba\u7c7b\u2026\u2026\u6211\u4e5f\u770b\u4e0d\u51fa\u4ec0\u4e48\uff0c\u53ea\u80fd\u8bf4\uff0c\u4ed6\u8eab\u4e0a\u6709\u79cd\u5947\u602a\u7684\u5473\u9053\uff0c\u8ba9\u6211\u611f\u89c9\u5f88\u5371\u9669\uff0c\u5f88\u4e0d\u8212\u670d\u2014\u2014\u800c\u4e14\u597d\u50cf\u6709\u70b9\u719f\u6089\uff0c\u603b\u89c9\u5f97\u66fe\u7ecf\u5728\u4ec0\u4e48\u5730\u65b9\u95fb\u5230\u8fc7\u4f3c\u7684\u3002\u201d</p><p>\u743c\u6069\u7cbe\u795e\u4e00\u632f\uff0c\u8fd9\u6216\u8bb8\u662f\u4e2a\u7ebf\u7d22\uff0c\u201c\u4ec0\u4e48\u5730\u65b9\uff1f\u201d</p><p>\u201c\u4e0d\u8bb0\u5f97\u4e86\u3002\u201d</p><p>\u201c\u4f60\u8fd9\u8bb0\u6027\u4e5f\u592a\u5dee\u4e86\u5427\u3002\u201d</p><p>\u201c\u5b69\u5b50\uff0c\u5982\u679c\u4f60\u50cf\u6211\u4e00\u6837\u6d3b\u4e86\u8fd9\u4e48\u4e45\uff0c\u8fd8\u80fd\u6e05\u695a\u5730\u62fc\u5f97\u51fa\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u90a3\u5c31\u5df2\u7ecf\u5f88\u4e86\u4e0d\u8d77\u4e86\uff0c\u201d\u9f99\u8001\u6c14\u6a2a\u79cb\u5730\u8bf4\uff0c\u201c\u6211\u89c1\u8fc7\u7684\u4eba\uff0c\u6bd4\u4f60\u6ca1\u89c1\u8fc7\u7684\u4eba\u8fd8\u591a\uff1b\u6211\u8d70\u8fc7\u7684\u5730\u65b9\uff0c\u6bd4\u4f60\u6ca1\u53bb\u8fc7\u7684\u5730\u65b9\u8fd8\u591a\uff0c\u6211\u54ea\u80fd\u4e00\u4e2a\u4e2a\u5168\u90fd\u8bb0\u4f4f\u2014\u2014\u7b49\u6709\u7a7a\u6211\u4f1a\u60f3\u60f3\u7684\u3002\u201d</p><p>\u201c\u4e0d\u5bf9\u5427\uff0c\u6211\u542c\u8bf4\u4f60\u4ee5\u524d\u5c31\u662f\u4e2a\u8d85\u7ea7\u5b85\uff0c\u4e00\u8f88\u5b50\u90fd\u8eb2\u5728\u5bb6\u91cc\u4e0d\u51fa\u95e8\u7684\u554a\u3002\u201d</p><p>\u201c\u8c01\u8bf4\u7684\uff01\u201d\u9f99\u4e0d\u9ad8\u5174\uff0c\u201c\u8c01\u5b85\u4e86\uff1f\u6211\u53ea\u662f\u4e0d\u600e\u4e48\u559c\u6b22\u5728\u516c\u4f17\u573a\u5408\u629b\u5934\u9732\u9762\u800c\u5df2\u3002\u8c01\u5c45\u7136\u5728\u80cc\u540e\u8fd9\u4e48\u8bfd\u8c24\u6211\uff0c\u6211\u4e00\u5b9a\u8981\u53bb\u6cd5\u5ead\u8d77\u8bc9\u4ed6\u3002\u201d</p><p>\u743c\u6069\u6307\u4e86\u6307\u65c1\u8fb9\u7684\u5c11\u5973\u3002\u201c\u5979\u8bf4\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u827e\u5f25\u8587\u8bf4\u7684\u5c31\u7b97\u4e86\uff0c\u201d\u9f99\u8baa\u8baa\u5730\u8bf4\uff0c\u201c\u603b\u4e4b\u6211\u5176\u5b9e\u4e5f\u4e0d\u600e\u4e48\u5b85\u7684\uff0c\u5076\u5c14\uff0c\u6211\u662f\u8bf4\u6709\u65f6\u5019\uff0c\u6211\u4e5f\u4f1a\u4e0b\u5c71\u5230\u4eba\u7c7b\u96c6\u5e02\u91cc\u6e9c\u8fbe\u6e9c\u8fbe\u7684\u3002\u55ef\u54fc\uff0c\u8fd9\u4e2a\u8bdd\u9898\u5c31\u5230\u6b64\u4e3a\u6b62\u5427\uff0c\u201d\u5b83\u6e05\u4e86\u6e05\u55d3\u5b50\uff0c\u201c\u53cd\u6b63\u5982\u679c\u53ef\u4ee5\u7684\u8bdd\uff0c\u6211\u5efa\u8bae\u4f60\u4eec\u4e0d\u8981\u548c\u90a3\u4e2a\u57c3\u535c\u62c9\u6253\u4ea4\u9053\uff0c\u90a3\u5bb6\u4f19\u6709\u70b9\u53e4\u602a\u3002\u4f46\u4ed6\u4e0d\u662f\u4ea1\u7075\uff0c\u4e5f\u6ca1\u6709\u88ab\u4ec0\u4e48\u4ea1\u7075\u63a7\u5236\uff0c\u4f60\u4eec\u4e4b\u524d\u7684\u731c\u6d4b\u65b9\u5411\u662f\u9519\u7684\uff0c\u8fd9\u70b9\u6211\u53ef\u4ee5\u786e\u5b9a\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d</p><p>\u743c\u6069\u6709\u4e9b\u5931\u671b\uff0c\u4ed6\u539f\u672c\u81ea\u89c9\u5df2\u7ecf\u5c06\u6240\u6709\u7ebf\u7d22\u5b8c\u7f8e\u5730\u8054\u7cfb\u8d77\u6765\uff0c\u770b\u7a7f\u4e86\u9690\u85cf\u5728\u9ed1\u6697\u4e2d\u7684\u771f\u76f8\uff0c\u6ca1\u60f3\u5230\u88ab\u51b0\u8679\u76f4\u63a5\u5426\u51b3\u4e86\u3002\u5c3d\u7ba1\u5f88\u5b85\uff0c\u4f46\u6bd5\u7adf\u662f\u6d3b\u4e86\u5343\u5e74\u7684\u592a\u53e4\u5de8\u9f99\uff0c\u5728\u7ed9\u63d0\u5c14\u6559\u4f1a\u505a\u5b88\u62a4\u795e\u517d\u671f\u95f4\u4e5f\u53c2\u4e0e\u8fc7\u4e0d\u5c11\u6076\u6218\uff0c\u773c\u5149\u89c1\u8bc6\u90fd\u4e0d\u7f3a\uff0c\u5b83\u65e2\u7136\u80fd\u8bf4\u5f97\u5982\u6b64\u80af\u5b9a\uff0c\u90a3\u5e94\u8be5\u5c31\u4e0d\u4f1a\u9519\u4e86\u3002</p><p>\u7b97\u4e86\uff0c\u4e0d\u60f3\u90a3\u4e48\u591a\uff0c\u53cd\u6b63\u6b64\u5730\u53ea\u662f\u8def\u8fc7\uff0c\u81ea\u5df1\u8fd8\u6709\u6b63\u4e8b\u8981\u505a\u5462\u3002</p><p>\u56de\u5230\u65c5\u5e97\uff0c\u743c\u6069\u5c06\u5315\u9996\u4ea4\u7ed9\u4e86\u838e\u73de\u514b\uff0c\u5631\u5490\u4e86\u9b45\u9b54\u51e0\u53e5\uff0c\u7136\u540e\u53bb\u627e\u73ca\u5609\u3002</p><p>\u73ca\u5609\u6b63\u5728\u623f\u95f4\u91cc\uff0c\u5bf9\u7740\u9762\u524d\u7684\u4e00\u7247\u900f\u660e\u6c34\u955c\u6c89\u601d\uff0c\u955c\u4e2d\u661f\u5149\u7480\u74a8\u95ea\u70c1\uff0c\u4ea4\u76f8\u8f89\u6620\uff0c\u4eff\u4f5b\u6674\u6717\u591c\u7a7a\uff0c\u5176\u4e2d\u6709\u51e0\u679a\u661f\u8fb0\u683c\u5916\u660e\u4eae\uff0c\u5149\u8292\u7efd\u653e\uff0c\u5c06\u5468\u56f4\u7684\u5176\u4ed6\u661f\u8fb0\u90fd\u63a9\u76d6\u4e86\u4e0b\u53bb\u3002\u5c11\u5973\u51dd\u795e\u5b9a\u5fc3\uff0c\u6b63\u8981\u7ec6\u770b\uff0c\u5ffd\u7136\u95e8\u88ab\u76f4\u63a5\u63a8\u5f00\u4e86\uff0c\u4e00\u4e2a\u4eba\u8d70\u8fdb\u6765\uff0c\u53d7\u6b64\u6253\u6270\uff0c\u6c34\u955c\u5267\u70c8\u5730\u6643\u4e86\u4e00\u6643\uff0c\u8fde\u540c\u955c\u4e2d\u7684\u661f\u5149\u77ac\u95f4\u6d88\u6563\u6210\u96fe\uff0c\u56db\u5904\u98d8\u6563\u3002</p><p>\u201c\u554a\uff0c\u59d0\u59d0\uff0c\u5bf9\u4e0d\u8d77\uff0c\u201d\u743c\u6069\u6ca1\u6599\u5230\u73ca\u5609\u6b63\u5728\u89c2\u661f\uff0c\u4ed6\u548c\u81ea\u5df1\u59d0\u59d0\u968f\u4fbf\u60ef\u4e86\uff0c\u4e00\u65f6\u5fd8\u4e86\u6572\u95e8\uff0c\u201c\u6ca1\u4e8b\u5427\uff1f\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u201d\u73ca\u5609\u6e29\u67d4\u5730\u7b11\u4e86\u7b11\uff0c\u7528\u624b\u6307\u63c9\u4e86\u63c9\u989d\u5934\uff0c\u201c\u6709\u70b9\u7d2f\u4e86\uff0c\u6b63\u51c6\u5907\u4f11\u606f\u5462\u3002\u201d</p><p>\u5deb\u5e08\u65bd\u6cd5\u65f6\u6700\u5fcc\u6253\u6270\uff0c\u5bb9\u6613\u906d\u53d7\u53cd\u566c\uff0c\u743c\u6069\u653e\u5fc3\u4e0d\u4e0b\uff0c\u8fc7\u6765\u66ff\u73ca\u5609\u68c0\u67e5\u4e86\u4e00\u904d\uff0c\u53d1\u73b0\u786e\u5b9e\u6ca1\u4ec0\u4e48\u95ee\u9898\uff0c\u65b9\u624d\u677e\u4e86\u53e3\u6c14\u3002\u201c\u59d0\u59d0\u521a\u624d\u5728\u770b\u4ec0\u4e48\u5462\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u6211\u60f3\u770b\u770b\u4e1c\u57df\u8fd9\u4e9b\u795e\u738b\u7684\u2018\u795e\u5ea7\u2019\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u5f88\u5947\u602a\uff0c\u661f\u56fe\u4e0a\u663e\u793a\u4e0d\u51fa\u6765\u3002\u201d</p><p>\u201c\u795e\u738b\u662f\u4e0d\u662f\u771f\u6b63\u7684\u795e\u7957\u90fd\u4e0d\u597d\u8bf4\uff0c\u4f60\u770b\u4e0d\u89c1\u4e5f\u6b63\u5e38\u5427\u3002\u201d\u743c\u6069\u5012\u4e0d\u89c9\u5f97\u6709\u4ec0\u4e48\u3002</p><p>\u201c\u4e5f\u4e0d\u662f\u5b8c\u5168\u770b\u4e0d\u89c1\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u5728\u5e94\u8be5\u5c5e\u4e8e\u4e1c\u57df\u795e\u738b\u7684\u4f4d\u7f6e\uff0c\u6211\u80fd\u591f\u770b\u89c1\u4e00\u4e2a\u795e\u5ea7\u3002\u201d</p><p>\u201c\u54e6\uff0c\u90a3\u662f\u5bf9\u5e94\u54ea\u4e2a\u795e\u738b\u5462\uff1f\u201d</p><p>\u201c\u95ee\u9898\u5c31\u5728\u4e8e\u6b64\uff0c\u201d\u73ca\u5609\u76b1\u7709\uff0c\u201c\u5b83\u4f3c\u4e4e\u5bf9\u5e94\u4e1c\u57df\u7684\u6240\u6709\u795e\u738b\u3002\u201d</p><p>\u743c\u6069\u6014\u4e86\u4e00\u4e0b\uff0c\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u5c31\u662f\u8bf4\uff0c\u65e0\u8bba\u6211\u60f3\u89c2\u6d4b\u7684\u662f\u54ea\u4e00\u4f4d\u795e\u738b\uff0c\u661f\u56fe\u4e0a\u663e\u793a\u7684\u90fd\u662f\u90a3\u4e2a\u795e\u5ea7\uff0c\u201d\u73ca\u5609\u89e3\u91ca\uff0c\u201c\u5982\u679c\u6309\u7167\u8001\u5e08\u6559\u6211\u7684\u77e5\u8bc6\u6765\u5206\u6790\uff0c\u8fd9\u610f\u5473\u7740\uff1a\u4e1c\u57df\u7684\u6240\u6709\u795e\u738b\u2014\u2014\u81f3\u5c11\u662f\u76ee\u524d\u73b0\u5b58\u7684\u4e94\u4f4d\u795e\u738b\uff0c\u5176\u5b9e\u662f\u4e00\u4f4d\u795e\u7957\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u600e\u4e48\u53ef\u80fd\uff1f\u201d</p><p>\u795e\u7957\u6709\u53ef\u80fd\u5b58\u5728\u591a\u79cd\u795e\u76f8\uff0c\u4e5f\u6709\u53ef\u80fd\u5728\u4e0d\u540c\u7684\u65f6\u671f\u3001\u4e0d\u540c\u7684\u5730\u533a\u4f7f\u7528\u4e0d\u540c\u7684\u540d\u53f7\uff0c\u8fd9\u4e9b\u90fd\u662f\u6709\u5b9e\u4f8b\u7684\u3002\u6bd4\u5982\u8bf4\u98ce\u66b4\u4e4b\u795e\u5854\u6d1b\u65af\uff0c\u4ed6\u5728\u8010\u745f\u65f6\u4ee3\u540d\u4e3a\u201c\u67ef\u8428\u201d\uff0c\u5728\u5361\u8389\u73ca\u88ab\u79f0\u4e3a\u201c\u6bd5\u5c14\u745e\u65af\u201d\uff0c\u5728\u897f\u571f\u67d0\u4e9b\u5730\u533a\u88ab\u79f0\u4e3a\u201c\u5bc7\u624e\u201d\u3002\u4f46\u4e1c\u57df\u8bf8\u795e\u738b\u7684\u60c5\u5f62\u4e0e\u6b64\u5b8c\u5168\u4e0d\u540c\uff0c\u4ed6\u4eec\u5386\u53f2\u60a0\u4e45\uff0c\u540c\u65f6\u5b58\u5728\uff0c\u5f7c\u6b64\u6709\u7740\u660e\u663e\u7684\u533a\u522b\uff0c\u800c\u4e14\u4e92\u76f8\u4e89\u6597\u4e0d\u606f\u3002\u5982\u679c\u8bf4\u4ed6\u4eec\u5176\u5b9e\u662f\u4e00\u4f4d\u795e\u7957\uff0c\u8fd9\u5b9e\u5728\u6709\u70b9\u8d85\u51fa\u743c\u6069\u7684\u7406\u89e3\u80fd\u529b\u3002</p><p>\u201c\u7684\u786e\u5f88\u4e0d\u53ef\u601d\u8bae\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u6211\u4e5f\u65e0\u6cd5\u89e3\u91ca\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u3002\u4e5f\u53ef\u80fd\u662f\u6211\u770b\u9519\u4e86\u5427\uff0c\u5360\u661f\u672f\u6211\u6bd5\u7adf\u63a5\u89e6\u65f6\u95f4\u8fd8\u592a\u77ed\uff0c\u7406\u89e3\u4e0d\u6df1\uff0c\u5982\u679c\u8001\u5e08\u8fd8\u5728\u5c31\u597d\u4e86\uff0c\u53ef\u4ee5\u5411\u5979\u8bf7\u6559\u3002\u201d</p><p>\u63d0\u5230\u5965\u5609\u83b1\u65af\uff0c\u4e24\u4eba\u90fd\u6c89\u9ed8\u4e86\u7247\u523b\uff0c\u201c\u4e0d\u8bf4\u8fd9\u4e2a\u4e86\uff0c\u201d\u73ca\u5609\u4e3b\u52a8\u6362\u4e86\u8bdd\u9898\uff0c\u201c\u4f60\u4eca\u5929\u548c\u51db\u901b\u8857\u53bb\u4e86\uff1f\u201d</p><p>\u201c\u966a\u5979\u53bb\u65e7\u57ce\u533a\u8f6c\u4e86\u8f6c\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u770b\u671b\u4e00\u4f4d\u6545\u4eba\u3002\u201d</p><p>\u201c\u662f\u67d0\u4f4d\u4ef0\u6155\u5979\u7684\u7537\u6027\u5417\uff1f\u201d\u73ca\u5609\u5f00\u73a9\u7b11\u3002</p><p>\u201c\u662f\u4e00\u4f4d\u8001\u5976\u5976\uff0c\u505a\u7684\u997c\u5f88\u597d\u5403\uff0c\u6211\u8fd8\u5e26\u4e86\u51e0\u5757\u56de\u6765\u5462\uff0c\u59d0\u59d0\u8981\u4e0d\u8981\u5c1d\u5c1d\uff1f\u201d</p><p>\u73ca\u5609\u6447\u6447\u5934\uff0c\u201c\u592a\u665a\u4e86\uff0c\u660e\u5929\u5427\u3002\u201d</p><p>\u201c\u7684\u786e\u662f\u5f88\u665a\u4e86\u5462\uff0c\u201d\u743c\u6069\u987a\u52bf\u8bf4\uff0c\u62b1\u4f4f\u5c11\u5973\uff0c\u201c\u6240\u4ee5\u59d0\u59d0\u6211\u4eec\u65e9\u70b9\u4f11\u606f\u5427\uff0c\u660e\u5929\u8fd8\u8981\u51fa\u53d1\u3002\u201d</p><p>\u201c\u54a6\uff0c\u4f60\u4e0d\u53bb\u627e\u5854\u62c9\u590f\u5417\uff1f\u201d</p><p>\u201c\u59d0\u59d0\u4e0d\u559c\u6b22\u6211\u966a\u7740\uff1f\u201d\u743c\u6069\u53cd\u95ee\u3002</p><p>\u201c\u559c\u6b22\u554a\uff0c\u4f46\u4f60\u4e0d\u4f1a\u618b\u7740\u5f88\u96be\u53d7\uff1f\u201d\u73ca\u5609\u7b11\u76c8\u76c8\u5730\u8bf4\uff0c\u201c\u5230\u65f6\u5019\u59d0\u59d0\u53ef\u6ca1\u529e\u6cd5\u5e2e\u4f60\u89e3\u51b3\u54e6\u3002\u201d</p><p>\u201c\u6211\u53c8\u4e0d\u662f\u6bcf\u65f6\u6bcf\u523b\u90fd\u60f3\u8981\uff0c\u201d\u743c\u6069\u6297\u8bae\uff0c\u201c\u4e0d\u8981\u8bf4\u5f97\u597d\u50cf\u6211\u6574\u5929\u6b32\u6c42\u4e0d\u6ee1\u4f3c\u7684\u3002\u201d</p><p>\u201c\u5e74\u8f7b\u7537\u5b69\u5b50\u7cbe\u529b\u65fa\u76db\uff0c\u6bcf\u65f6\u6bcf\u523b\u90fd\u60f3\u8981\u5f88\u6b63\u5e38\u554a\u3002\u59d0\u59d0\u53ef\u4ee5\u7406\u89e3\u7684\uff0c\u4e0d\u7528\u4e0d\u597d\u610f\u601d\u3002\u201d</p><p>\u201c\u6211\u624d\u6ca1\u6709\u4ec0\u4e48\u4e0d\u597d\u610f\u601d\uff01\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u53ea\u662f\u5355\u7eaf\u5730\u60f3\u4eca\u665a\u966a\u59d0\u59d0\u800c\u5df2\u3002\u201d</p><p>\u73ca\u5609\u5ae3\u7136\u5fae\u7b11\uff0c\u201c\u90a3\u597d\u5427\uff0c\u4e0d\u8fc7\u5148\u8bf4\u597d\uff0c\u4e0d\u51c6\u534a\u591c\u5077\u5077\u6e9c\u8d70\u54e6\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u62b1\u7740\u6f02\u4eae\u59d0\u59d0\u5165\u7761\u5f53\u7136\u662f\u4ef6\u5f88\u7f8e\u597d\u7684\u4e8b\u60c5\uff0c\u4f46\u80fd\u770b\u80fd\u6478\u4e0d\u80fd\u5403\uff0c\u90a3\u5c31\u5f88\u6298\u78e8\u4eba\u4e86\u3002\u800c\u4e14\u8fd8\u4e0d\u662f\u59d0\u59d0\u4e0d\u613f\u610f\uff0c\u662f\u81ea\u5df1\u4e0d\u884c\uff0c\u8fd9\u5c31\u66f4\u52a0\u4ee4\u4eba\u90c1\u95f7\u4e86\u3002</p><p>\u4e00\u591c\u8f97\u8f6c\u53cd\u4fa7\uff0c\u7b2c\u4e8c\u5929\u65e9\u4e0a\u8d77\u5e8a\u65f6\uff0c\u743c\u6069\u89c9\u5f97\u81ea\u5df1\u7cbe\u795e\u5f88\u4e0d\u597d\uff0c\u773c\u775b\u751f\u6da9\uff0c\u9ed1\u773c\u5708\u80af\u5b9a\u90fd\u52a0\u6df1\u4e86\u51e0\u5206\u3002\u4e0b\u4f53\u5374\u662f\u4e00\u67f1\u64ce\u5929\uff0c\u6bd4\u8c01\u90fd\u7cbe\u795e\uff0c\u53ef\u60dc\u5c31\u662f\u65e0\u5904\u53d1\u6cc4\u3002\u7ef4\u82e5\u62c9\u7684\u8fd9\u4e2a\u4e13\u60c5\u8bc5\u5492\u5c31\u662f\u8fd9\u4e48\u9ebb\u70e6\uff0c\u5b83\u53ea\u662f\u963b\u6b62\u53d7\u8bc5\u5492\u8005\u4e0e\u5176\u4ed6\u5f02\u6027\u4ea4\u5408\uff0c\u5374\u5e76\u6ca1\u6709\u5f71\u54cd\u5176\u529f\u80fd\uff0c\u8be5\u6709\u6b32\u671b\u7684\u65f6\u5019\u7167\u6837\u6709\u6b32\u671b\uff0c\u8be5\u52c3\u8d77\u7684\u65f6\u5019\u7167\u6837\u4f1a\u52c3\u8d77\uff0c\u5c31\u662f\u6ca1\u6cd5\u6ee1\u8db3\u3002</p><p>\u201c\u4eca\u665a\u8fd8\u662f\u53bb\u627e\u5854\u62c9\u590f\u5427\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4e0d\u7136\u5bf9\u8eab\u4f53\u4e0d\u597d\u3002\u201d</p><p>\u5c11\u5973\u7684\u8bed\u6c14\u548c\u795e\u60c5\u4e4b\u4e2d\uff0c\u867d\u7136\u5fae\u6709\u918b\u610f\uff0c\u5374\u5e76\u4e0d\u662f\u5728\u8bf4\u53cd\u8bdd\uff0c\u800c\u662f\u771f\u5fc3\u5982\u6b64\u5efa\u8bae\u3002\u4ece\u5c0f\u4e00\u8d77\u957f\u5927\uff0c\u743c\u6069\u8fd9\u70b9\u8fa8\u522b\u80fd\u529b\u8fd8\u662f\u6709\u7684\u3002\u8fd9\u8ba9\u4ed6\u4e0d\u7981\u6709\u4e9b\u5947\u602a\uff0c\u5b9e\u4e8b\u6c42\u662f\u5730\u8bf4\uff0c\u73ca\u5609\u5728\u8fd9\u65b9\u9762\u53ef\u5e76\u4e0d\u7b97\u7279\u522b\u201c\u5bbd\u5bb9\u201d\u3002\u5979\u5f53\u7136\u4f1a\u5728\u610f\u743c\u6069\u7684\u8eab\u5fc3\u5065\u5eb7\u95ee\u9898\uff0c\u4f46\u4ec5\u4ec5\u56e0\u4e3a\u8fd9\u4e00\u70b9\uff0c\u5c31\u529d\u4ed6\u53bb\u627e\u522b\u7684\u5973\u4eba\uff0c\u8fd9\u53ef\u4e0d\u592a\u7b26\u5408\u5979\u7684\u6027\u683c\u3002</p><p>\u201c\u59d0\u59d0\u4f60\u6700\u8fd1\u4f3c\u4e4e\u548c\u5979\u6765\u5f80\u5f88\u591a\u554a\u3002\u201d\u743c\u6069\u8bd5\u63a2\u5730\u95ee\u3002</p><p>\u201c\u55ef\uff0c\u6211\u7ecf\u5e38\u6709\u4e9b\u95ee\u9898\u8bf7\u6559\u5979\u3002\u201d</p><p>\u201c\u5979\u4f1a\u6559\u4f60\uff1f\u201d</p><p>\u743c\u6069\u6709\u4e9b\u8be7\u5f02\uff0c\u73ca\u5609\u4f1a\u8bf7\u6559\u7ef4\u82e5\u62c9\u7684\uff0c\u53ea\u53ef\u80fd\u662f\u9b54\u6cd5\u5b66\u4e60\u4e0a\u7684\u95ee\u9898\uff0c\u4f46\u8fd9\u79cd\u73cd\u8d35\u7684\u77e5\u8bc6\uff0c\u600e\u4e48\u53ef\u80fd\u968f\u968f\u4fbf\u4fbf\u6559\u4eba\uff0c\u7ef4\u82e5\u62c9\u548c\u73ca\u5609\u53c8\u6ca1\u4ec0\u4e48\u4ea4\u60c5\u3002\u6ca1\u6709\u54ea\u4e2a\u5deb\u5e08\u4f1a\u628a\u81ea\u5df1\u8f9b\u8f9b\u82e6\u82e6\uff0c\u82b1\u8d39\u65e0\u6570\u6210\u672c\u548c\u4ee3\u4ef7\u624d\u83b7\u5f97\u7684\u77e5\u8bc6\u3001\u6280\u827a\uff0c\u8f7b\u6613\u5730\u4f20\u6388\u7ed9\u522b\u4eba\u3002\u743c\u6069\u5f53\u5e74\u8fdb\u5deb\u5e08\u5b66\u6821\uff0c\u53ef\u662f\u82b1\u5149\u4e86\u5bb6\u91cc\u7684\u5168\u90e8\u79ef\u84c4\uff0c\u800c\u4e14\u771f\u8981\u8ba1\u7b97\u8d77\u6765\uff0c\u90a3\u70b9\u94b1\u53ea\u80fd\u7b97\u662f\u8c61\u5f81\u6027\u6536\u8d39\uff0c\u743c\u6069\u5728\u6821\u671f\u95f4\u6240\u9886\u53d6\u3001\u6d88\u8017\u7684\u90a3\u4e9b\u9b54\u6cd5\u6750\u6599\uff0c\u6210\u672c\u90fd\u8fdc\u8fdc\u4e0d\u6b62\u8fd9\u4e2a\u4ef7\u4e86\u3002</p><p>\u5f53\u7136\u4e86\uff0c\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u5b66\u6821\uff0c\u672c\u6765\u5c31\u662f\u5185\u90e8\u62db\u751f\uff0c\u57f9\u517b\u7684\u90fd\u662f\u81ea\u5df1\u672a\u6765\u7684\u653f\u5e9c\u5b98\u50da\u6216\u8005\u519b\u961f\u58eb\u5b98\uff0c\u5b66\u8d39\u4f4e\u70b9\u5f88\u6b63\u5e38\u2014\u2014\u56e0\u4e3a\u6240\u6709\u7684\u5b66\u751f\u6bd5\u4e1a\u4e4b\u540e\uff0c\u90fd\u8981\u4e3a\u9634\u9b42\u57ce\u670d\u52a1\uff0c\u800c\u4e14\u8fd9\u79cd\u670d\u52a1\u8fd8\u662f\u7ec8\u751f\u7684\uff0c\u60f3\u8f9e\u804c\u90fd\u4e0d\u53ef\u80fd\u3002\u6240\u4ee5\u743c\u6069\u4ee5\u53ca\u4ed6\u7684\u540c\u5b66\u4eec\uff0c\u4e3a\u4e86\u6210\u4e3a\u5deb\u5e08\uff0c\u6240\u4ed8\u51fa\u7684\u53ef\u4e0d\u4ec5\u4ec5\u662f\u90a3\u70b9\u5b66\u8d39\uff0c\u8fd8\u9644\u52a0\u4e86\u4e00\u4efd\u6c38\u4e45\u6709\u6548\u7684\u5356\u8eab\u5951\u3002</p><p>\u5355\u770b\u201c\u4f20\u9053\u5deb\u5e08\u201d\u8fd9\u4e2a\u5934\u8854\uff0c\u6216\u8bb8\u4f1a\u8ba9\u4eba\u4ea7\u751f\u8bef\u89e3\uff0c\u89c9\u5f97\u8fd9\u662f\u4e00\u4e2a\u597d\u4e3a\u4eba\u5e08\uff0c\u559c\u6b22\u5230\u5904\u6307\u70b9\u7684\u804c\u4e1a\u2014\u2014\u4f46\u5176\u5b9e\u5b8c\u5168\u4e0d\u662f\u3002\u963f\u7956\u65af\u7684\u6559\u4e49\u5b97\u65e8\u662f\u8ffd\u6c42\u81f3\u9ad8\u5965\u672f\u4e4b\u9053\uff0c\u53ef\u4e0d\u662f\u201c\u6709\u6559\u65e0\u7c7b\u201d\uff0c\u5c31\u7b97\u8981\u4f20\u9053\uff0c\u4e5f\u4e0d\u53ef\u80fd\u4f20\u7ed9\u654c\u4eba\u2014\u2014\u73ca\u5609\u51fa\u8eab\u9634\u9b42\u57ce\uff0c\u662f\u838e\u5c14\u7684\u4fe1\u5f92\uff0c\u4e0e\u7ef4\u82e5\u62c9\u5929\u7136\u7acb\u573a\u654c\u5bf9\u3002\u743c\u6069\u5012\u662f\u4ece\u7ef4\u82e5\u62c9\u90a3\u91cc\u5b66\u5230\u4e86\u4e00\u4e9b\u7c89\u7ea2\u6cd5\u672f\uff0c\u90a3\u662f\u7279\u6b8a\u60c5\u51b5\uff0c\u4e0d\u80fd\u62ff\u6765\u4f5c\u4e3a\u4f8b\u5b50\u3002</p><p>\u201c\u6b63\u5e38\u60c5\u51b5\u4e0b\u6216\u8bb8\u662f\u4e0d\u4f1a\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4f46\u5979\u65e2\u7136\u8981\u505a\u6211\u7684\u5f1f\u5ab3\uff0c\u53c8\u600e\u4e48\u80fd\u4e0d\u8ba8\u597d\u6211\u8fd9\u4e2a\u59d0\u59d0\u5462\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u73ca\u5609\u5728\u5f00\u73a9\u7b11\uff0c\u4f46\u743c\u6069\u5e76\u6ca1\u6709\u7b11\u3002</p><p>\u7ef4\u82e5\u62c9\u548c\u743c\u6069\u5982\u4eca\u7684\u60c5\u5f62\uff0c\u53ea\u80fd\u8bf4\u662f\u9634\u5dee\u9633\u9519\uff0c\u673a\u7f18\u5de7\u5408\uff0c\u5f7c\u6b64\u5e76\u65e0\u4ec0\u4e48\u611f\u60c5\u57fa\u7840\u53ef\u8a00\uff0c\u4e5f\u770b\u4e0d\u5230\u672a\u6765\u7684\u53d1\u5c55\u53ef\u80fd\u3002\u743c\u6069\u4ece\u6ca1\u8003\u8651\u8fc7\u8981\u5a36\u5979\u4e3a\u59bb\uff0c\u76f8\u4fe1\u7ef4\u82e5\u62c9\u4e5f\u6ca1\u6709\u8981\u5c06\u7ec8\u751f\u6258\u4ed8\u7ed9\u743c\u6069\u7684\u6253\u7b97\uff0c\u6240\u8c13\u201c\u5f1f\u5ab3\u8ba8\u597d\u59d0\u59d0\u201d\u4e91\u4e91\uff0c\u5f53\u7136\u53ea\u7b97\u662f\u7b11\u8bdd\u3002</p><p>\u771f\u5b9e\u7684\u60c5\u51b5\uff0c\u53ea\u4f1a\u662f\u53cd\u8fc7\u6765\u3002\u4e0d\u662f\u7ef4\u82e5\u62c9\u523b\u610f\u8ba8\u597d\uff0c\u800c\u662f\u73ca\u5609\u4e3b\u52a8\u7ed3\u4ea4\u3002</p><p>\u5728\u8fd9\u4e2a\u5c0f\u56e2\u4f53\u4e2d\uff0c\u5176\u4ed6\u4eba\u90fd\u662f\u743c\u6069\u7684\u5973\u53cb\uff0c\u800c\u4e14\u4e4b\u524d\u90fd\u8ba4\u8bc6\uff0c\u6709\u8fc7\u4e00\u6bb5\u65f6\u95f4\u7684\u4ea4\u5f80\uff0c\u5728\u9634\u5f71\u8c37\u4e00\u6218\u4e2d\u4e5f\u66fe\u5171\u7ecf\u60a3\u96be\uff0c\u5e76\u80a9\u4f5c\u6218\uff0c\u67d0\u79cd\u7a0b\u5ea6\u4e0a\u7b97\u662f\u201c\u81ea\u5df1\u4eba\u201d\uff0c\u7ef4\u82e5\u62c9\u5374\u662f\u4f8b\u5916\uff0c\u5979\u662f\u521a\u52a0\u5165\u7684\u201c\u65b0\u4eba\u201d\uff0c\u548c\u5176\u4ed6\u4eba\u4e4b\u524d\u7d20\u4e0d\u76f8\u8bc6\uff0c\u5374\u56e0\u4e3a\u201c\u4e13\u60c5\u8bc5\u5492\u201d\u7684\u5173\u7cfb\uff0c\u53cd\u800c\u6210\u4e86\u552f\u4e00\u548c\u743c\u6069\u4fdd\u6301\u4eb2\u5bc6\u8089\u4f53\u5173\u7cfb\u7684\u4eba\uff0c\u5176\u4ed6\u5c11\u5973\u90fd\u88ab\u5f3a\u5236\u963b\u6321\u5728\u5916\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u96be\u514d\u5c31\u663e\u5f97\u6709\u4e9b\u683c\u683c\u4e0d\u5165\uff0c\u8eab\u4efd\u5c34\u5c2c\uff0c\u751a\u81f3\u9690\u9690\u88ab\u6392\u65a5\u3002</p><p>\u4f5c\u4e3a\u4e00\u4e2a\u4ee5\u201c\u5efa\u7acb\u4e00\u4e2a\u5e9e\u5927\u540e\u5bab\u201d\u4e3a\u76ee\u6807\u7684\u7537\u6027\u6765\u8bf4\uff0c\u743c\u6069\u5f53\u7136\u662f\u5e0c\u671b\u81ea\u5df1\u7684\u5973\u4eba\u90fd\u80fd\u548c\u7766\u76f8\u5904\u2014\u2014\u81f3\u5c11\u5728\u8868\u9762\u4e0a\u662f\u5982\u6b64\uff0c\u5426\u5219\u5c82\u4e0d\u662f\u8981\u5934\u75db\u6b7b\u3002\u4f46\u5973\u5b69\u5b50\u4eec\u53ef\u6ca1\u4e49\u52a1\u6ee1\u8db3\u743c\u6069\u7684\u671f\u5f85\uff0c\u6885\u83f2\u65af\u81ea\u4e0d\u7528\u8bf4\uff0c\u838e\u73de\u514b\u5bf9\u7ef4\u82e5\u62c9\u4e5f\u5f88\u4e0d\u6ee1\uff0c\u56e0\u4e3a\u4e13\u60c5\u8bc5\u5492\u7684\u5173\u7cfb\uff0c\u5979\u5df2\u7ecf\u591a\u65e5\u6ca1\u6709\u81ea\u743c\u6069\u5904\u83b7\u5f97\u7cbe\u6db2\u4e86\uff0c\u8fd9\u5bf9\u4e00\u53ea\u9b45\u9b54\u800c\u8a00\u53ef\u662f\u5f88\u4e25\u91cd\u7684\u4e8b\u60c5\uff0c\u4e5f\u5c31\u53ea\u6709\u51db\u5fc3\u601d\u5355\u7eaf\uff0c\u53c8\u548c\u7ef4\u82e5\u62c9\u6709\u4e00\u5c42\u5e08\u59d0\u59b9\u7684\u540d\u5206\uff08\u867d\u7136\u4e4b\u524d\u5176\u5b9e\u4e5f\u4ece\u672a\u89c1\u8fc7\uff09\uff0c\u5173\u7cfb\u5012\u8fd8\u4e0d\u9519\uff0c\u4f46\u5979\u6ca1\u4ec0\u4e48\u81ea\u5df1\u7684\u7acb\u573a\uff0c\u5982\u679c\u8981\u5728\u6885\u83f2\u65af\u548c\u7ef4\u82e5\u62c9\u4e4b\u95f4\u9009\u62e9\u4e00\u4e2a\u7684\u8bdd\uff0c\u7b54\u6848\u662f\u6beb\u65e0\u60ac\u5ff5\u7684\u3002</p><p>\u5e78\u597d\u8fd8\u6709\u73ca\u5609\u3002</p><p>\u6ca1\u6709\u8c01\u4f1a\u559c\u6b22\u591a\u4e00\u4e2a\u60c5\u654c\uff0c\u73ca\u5609\u4e5f\u4e0d\u4f8b\u5916\uff0c\u4f46\u5982\u679c\u5df2\u7ecf\u662f\u65e2\u6210\u4e8b\u5b9e\uff0c\u5979\u4e5f\u4f1a\u5c3d\u53ef\u80fd\u53bb\u63a5\u53d7\uff0c\u53bb\u8bd5\u7740\u76f8\u5904\u4ea4\u5f80\uff0c\u7ef4\u6301\u4e00\u4e2a\u8fc7\u5f97\u53bb\u7684\u5173\u7cfb\uff0c\u4e0d\u4f1a\u9488\u950b\u76f8\u5bf9\uff0c\u4e5f\u4e0d\u4f1a\u523b\u610f\u5b64\u7acb\u3001\u51b7\u843d\u5bf9\u65b9\uff0c\u65e0\u8bba\u5bf9\u6885\u83f2\u65af\uff0c\u5bf9\u8299\u857e\u72c4\u59d0\u59b9\uff0c\u5bf9\u51db\uff0c\u5bf9\u838e\u73de\u514b\u90fd\u662f\u5982\u6b64\u3002\u5979\u4f1a\u5ac9\u5992\uff0c\u4f1a\u5403\u918b\uff0c\u4f1a\u5fc3\u91cc\u4e0d\u9ad8\u5174\uff0c\u4f46\u5979\u4ecd\u7136\u4f1a\u5e2e\u52a9\u5f1f\u5f1f\u53bb\u5e94\u4ed8\u3001\u89e3\u51b3\u8fd9\u4e00\u5207\u9ebb\u70e6\uff0c\u5c31\u50cf\u662f\u4f4d\u5bbd\u5bb9\u7684\u5973\u4e3b\u4eba\u3002</p><p>\u6709\u8fd9\u4e48\u7f8e\u4e3d\u6e29\u67d4\u4f53\u8d34\u5927\u65b9\u7684\u59d0\u59d0\uff0c\u743c\u6069\u771f\u662f\u611f\u52a8\u5f97\u4e00\u584c\u7cca\u6d82\uff0c\u6b63\u8981\u8d76\u5feb\u732e\u6bb7\u52e4\u8868\u5fe0\u5fc3\uff0c\u73ca\u5609\u5374\u6325\u4e86\u6325\u624b\uff0c\u8868\u793a\u5230\u4e86\u5b66\u4e60\u65f6\u95f4\uff0c\u76f4\u63a5\u5c06\u4ed6\u4ece\u95fa\u623f\u91cc\u8d76\u4e86\u51fa\u6765\u3002\u4e34\u8d70\u4e4b\u524d\uff0c\u770b\u89c1\u73ca\u5609\u6b63\u5c06\u51e0\u5757\u7af9\u7b80\u4ece\u53e3\u888b\u91cc\u638f\u51fa\u6765\uff0c\u6309\u987a\u5e8f\u6446\u5728\u684c\u4e0a\uff0c\u9010\u4e2a\u6284\u5f55\u4e0a\u9762\u7684\u6587\u5b57\u3002</p><p>\u59d0\u59d0\u5f88\u52aa\u529b\u5462\uff0c\u743c\u6069\u60f3\u3002</p><p>\u5251\u672f\u3001\u5360\u661f\u3001\u9b54\u6cd5\u3001\u70bc\u91d1\uff0c\u8fd8\u6709\u97f3\u4e50\uff0c\u73ca\u5609\u6bcf\u5929\u7684\u8bfe\u7a0b\u90fd\u662f\u6392\u5f97\u6ee1\u6ee1\u7684\uff0c\u4e00\u65e5\u81f3\u5c11\u8981\u82b1\u5341\u4e2a\u5c0f\u65f6\u5728\u5b66\u4e60\u4e0a\uff0c\u800c\u4e14\u4ece\u65e0\u95f4\u65ad\uff0c\u4ece\u65e0\u61c8\u6020\u3002\u5979\u7684\u5929\u8d4b\u672c\u5c31\u4f18\u79c0\uff0c\u53c8\u66fe\u7ecf\u6709\u5965\u52a0\u83b1\u65af\u8fd9\u79cd\u660e\u5e08\u6307\u70b9\uff0c\u518d\u80fd\u5982\u6b64\u523b\u82e6\uff0c\u5176\u8fdb\u6b65\u4e4b\u660e\u663e\uff0c\u662f\u6709\u76ee\u5171\u7779\u7684\u3002\u522b\u7684\u4e0d\u8bf4\uff0c\u5c31\u4ee5\u5251\u672f\u800c\u8bba\uff0c\u524d\u65e5\u8fd8\u5728\u8239\u4e0a\u7684\u65f6\u5019\uff0c\u743c\u6069\u5076\u7136\u770b\u5230\u73ca\u5609\u548c\u838e\u73de\u514b\u5bf9\u7ec3\uff0c\u5728\u9b45\u9b54\u6ca1\u6709\u660e\u663e\u653e\u6c34\u7684\u60c5\u51b5\u4e0b\uff0c\u5df2\u7ecf\u80fd\u591f\u6253\u5f97\u6709\u6765\u6709\u5f80\u3002</p><p>\u743c\u6069\u8fd9\u6bb5\u65f6\u95f4\u4e5f\u7b97\u8ba4\u771f\uff0c\u4f46\u548c\u73ca\u5609\u6bd4\u8d77\u6765\u8fd8\u662f\u6709\u6240\u4e0d\u5982\uff0c\u5176\u4e2d\u4e00\u4e2a\u5f88\u91cd\u8981\u7684\u539f\u56e0\u662f\u4ed6\u603b\u4f1a\u6709\u5404\u79cd\u5404\u6837\u7684\u5e72\u6270\u2014\u2014\u5176\u4e2d\u6700\u5927\u7684\u4e00\u4e2a\u5e72\u6270\u6e90\u540d\u4e3a\u201c\u51db\u201d\u3002</p><p>\u4e0b\u5348\u4e24\u70b9\u591a\u949f\uff0c\u743c\u6069\u5728\u81ea\u5df1\u7684\u623f\u95f4\u91cc\u8ba1\u7b97\u4e00\u4e2a\u9b54\u6cd5\u516c\u5f0f\uff0c\u6b63\u805a\u7cbe\u4f1a\u795e\u4e2d\uff0c\u623f\u95e8\u7a81\u7136\u88ab\u63a8\u5f00\uff0c\u51db\u4e00\u9635\u98ce\u5730\u51b2\u8fdb\u6765\uff0c\u201c\u4f60\u5728\u8fd9\u91cc\u554a\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u966a\u6211\u51fa\u53bb\u4e70\u4e1c\u897f\u3002\u201d</p><p>\u743c\u6069\u4e0b\u610f\u8bc6\u5730\u60f3\u8bf4\u201c\u4f60\u4e0d\u662f\u6628\u5929\u624d\u521a\u521a\u901b\u8fc7\u8857\u4e48\uff1f\u201d\u4f46\u8f6c\u5ff5\u4e00\u60f3\uff0c\u5bf9\u4e8e\u5973\u5b69\u5b50\u6765\u8bf4\u8fd9\u4e2a\u95ee\u9898\u6beb\u65e0\u610f\u4e49\uff0c\u901b\u8857\u662f\u521a\u9700\uff0c\u6bd4\u5403\u996d\u66f4\u91cd\u8981\u3002\u201c\u6211\u6b63\u6709\u4e8b\u5462\uff0c\u4f60\u8ba9\u827e\u5f25\u8587\u966a\u4f60\u53bb\u597d\u4e86\u3002\u201d</p><p>\u201c\u5c31\u662f\u827e\u5f25\u8587\u8ba9\u6211\u53bb\u4e70\u4e1c\u897f\uff0c\u5979\u5728\u5fd9\u7740\u6574\u7406\u8d44\u6599\uff0c\u6ca1\u7a7a\u3002\u201d</p><p>\u201c\u6211\u4e5f\u6ca1\u7a7a\u2014\u2014\u4f60\u8bf4\u5979\u8ba9\u4f60\u53bb\u4e70\u4e1c\u897f\uff1f\u4e70\u4ec0\u4e48\uff1f\u201d</p><p>\u51db\u6478\u51fa\u4e00\u5f20\u7eb8\uff0c\u201c\u90fd\u5728\u8fd9\u4e0a\u9762\uff0c\u5979\u6015\u6211\u8bb0\u4e0d\u4f4f\uff0c\u90fd\u5199\u4e0b\u6765\u4e86\u3002\u201d</p><p>\u743c\u6069\u63a5\u8fc7\u6765\u4e00\u770b\uff0c\u4e0a\u9762\u7528\u901a\u7528\u8bed\u5199\u4e86\u5341\u51e0\u884c\uff0c\u4e3b\u8981\u90fd\u662f\u4e00\u4e9b\u65c5\u884c\u7528\u54c1\uff0c\u6bd4\u5982\u5e10\u7bf7\u3001\u7761\u888b\u4e4b\u7c7b\uff0c\u8fd8\u6709\u4e00\u4e9b\u533b\u836f\u7528\u54c1\u3002\u201c\u4e70\u8fd9\u4e9b\u505a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u5947\u602a\uff0c\u201c\u6211\u4eec\u6709\u9752\u94dc\u8c6a\u5b85\u554a\u3002\u201d</p><p>\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u51fa\u95e8\u957f\u9014\u65c5\u884c\u5f53\u7136\u8981\u51c6\u5907\u8fd9\u4e9b\u4e1c\u897f\uff0c\u4f46\u5965\u6c83\u9001\u7ed9\u743c\u6069\u7684\u201c\u9752\u94dc\u8c6a\u5b85\u201d\uff0c\u968f\u65f6\u968f\u5730\u90fd\u53ef\u4ee5\u5c55\u5f00\uff0c\u76f8\u5f53\u4e8e\u4e00\u5ea7\u79fb\u52a8\u57ce\u5821\uff0c\u8fd8\u9700\u8981\u8fd9\u4e48\u9ebb\u70e6\u505a\u4ec0\u4e48\u3002</p><p>\u201c\u8c01\u77e5\u9053\uff0c\u53cd\u6b63\u827e\u5f25\u8587\u5c31\u662f\u8fd9\u4e48\u4ea4\u4ee3\u7684\uff0c\u8981\u4e48\u4f60\u95ee\u95ee\u5979\u53bb\u597d\u4e86\u3002\u201d</p><p>\u743c\u6069\u4e0d\u660e\u6240\u4ee5\uff0c\u4f46\u4e5f\u61d2\u5f97\u53bb\u591a\u95ee\uff0c\u6885\u83f2\u65af\u65e2\u7136\u8fd9\u4e48\u51b3\u5b9a\uff0c\u81ea\u7136\u6709\u5176\u7406\u7531\uff0c\u548c\u51db\u4e00\u6837\uff0c\u4ed6\u5bf9\u6885\u83f2\u65af\u6709\u7740\u65e0\u6761\u4ef6\u7684\u4fe1\u4efb\u3002\u201c\u90a3\u4f60\u8ba9\u5854\u62c9\u590f\u966a\u4f60\u53bb\u597d\u4e86\uff0c\u201d\u4ed6\u63a8\u8131\uff0c\u201c\u5979\u5e94\u8be5\u6ca1\u4e8b\u5427\u3002\u201d</p><p>\u201c\u5854\u59d0\u59d0\u548c\u73ca\u5609\u5728\u4e00\u8d77\u5462\uff0c\u6ca1\u7a7a\u7406\u6211\u3002\u201d</p><p>\u201c\u53ef\u662f\u6211\u771f\u7684\u6709\u4e8b\u3002\u201d</p><p>\u201c\u522b\u62d6\u62d6\u62c9\u62c9\u4e86\uff0c\u201d\u51db\u76f4\u63a5\u628a\u743c\u6069\u4ece\u6905\u5b50\u4e0a\u62c9\u8d77\u6765\uff0c\u201c\u4f60\u770b\u4f60\u7684\u76ae\u80a4\u8fd9\u4e48\u82cd\u767d\uff0c\u663e\u7136\u662f\u592a\u9633\u6652\u5f97\u592a\u5c11\uff0c\u8d76\u5feb\u8ddf\u6211\u51fa\u95e8\u900f\u900f\u6c14\uff0c\u6574\u5929\u5f85\u5728\u623f\u95f4\u91cc\u4f1a\u53d1\u9709\u7684\u3002\u201d</p><p>\u201c\u6211\u54ea\u91cc\u76ae\u80a4\u82cd\u767d\u4e86\uff0c\u660e\u660e\u5f88\u5065\u5eb7\u597d\u4e0d\u597d\uff0c\u4f60\u770b\u6211\u2014\u2014\u201d</p><p>\u201c\u8d70\u5566\u8d70\u5566\u3002\u201d</p><p>\u51db\u7684\u529b\u6c14\u5f88\u5927\uff0c\u743c\u6069\u5b8c\u5168\u6ca1\u6709\u4efb\u4f55\u53cd\u6297\u4f59\u5730\u5c31\u88ab\u62fd\u8d70\u4e86\uff0c\u7ea2\u9f99\u8840\u8109\u5728\u8fd9\u4e00\u523b\u4f53\u73b0\u5f97\u6dcb\u6f13\u5c3d\u81f4\u3002</p><p>\u6885\u83f2\u65af\u5217\u7684\u6e05\u5355\u4e0a\u4e1c\u897f\u4e0d\u5c11\uff0c\u79cd\u7c7b\u4e5f\u90fd\u4e0d\u540c\uff0c\u743c\u6069\u548c\u51db\u8dd1\u4e86\u4e03\u516b\u4e2a\u5546\u5e97\uff0c\u603b\u7b97\u662f\u628a\u4e1c\u897f\u57fa\u672c\u4e70\u9f50\u4e86\uff0c\u53ea\u5dee\u6700\u540e\u4e00\u6837\uff0c\u662f\u4e00\u79cd\u9a71\u8d76\u86c7\u866b\u7684\u836f\u6c34\uff0c\u5230\u5904\u90fd\u627e\u4e0d\u5230\u3002\u743c\u6069\u90fd\u51c6\u5907\u7b97\u4e86\uff0c\u5012\u662f\u51db\u7a81\u7136\u60f3\u8d77\u6765\uff0c\u5e26\u7740\u743c\u6069\u5230\u4e86\u65e7\u57ce\u533a\uff0c\u5728\u4e00\u4e2a\u770b\u8d77\u6765\u6fd2\u4e34\u7834\u4ea7\u7684\u5e97\u94fa\u91cc\u4e70\u5230\u4e86\u836f\u6c34\u3002\u201c\u8fd9\u624d\u51e0\u5e74\u554a\uff0c\u4ee5\u524d\u7684\u597d\u4e1c\u897f\u90fd\u627e\u4e0d\u5230\u4e86\uff0c\u201d\u51db\u611f\u6168\uff0c\u201c\u4e16\u754c\u7684\u53d8\u5316\u771f\u662f\u5feb\u3002\u201d</p><p>\u9053\u7406\u867d\u7136\u6ca1\u9519\uff0c\u4f46\u4f60\u4e00\u4e2a\u5341\u4e03\u516b\u5c81\u7684\u5c11\u5973\uff0c\u8bf4\u8fd9\u79cd\u8bdd\u4e0d\u89c9\u5f97\u5f88\u5947\u602a\u4e48\u3002</p><p>\u201c\u65e2\u7136\u90fd\u6765\u65e7\u57ce\u533a\u4e86\uff0c\u8981\u4e0d\u8981\u518d\u53bb\u770b\u770b\u5580\u6d41\u5976\u5976\uff1f\u201d\u743c\u6069\u63d0\u8bae\u3002</p><p>\u201c\u597d\u554a\u3002\u201d\u51db\u70b9\u5934\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u827e\u5f25\u8587\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u743c\u6069\u5ffd\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u201c\u5979\u548c\u5580\u6d41\u5976\u5976\u2026\u2026\u6709\u4ec0\u4e48\u95ee\u9898\u5417\uff1f\u201d</p><p>\u6628\u5929\u51db\u5e26\u7740\u743c\u6069\u53bb\u65e7\u57ce\u533a\u89c1\u5580\u6d41\u5976\u5976\uff0c\u4e8b\u5148\u5374\u6ca1\u544a\u8bc9\u6885\u83f2\u65af\uff0c\u8fd9\u4e00\u70b9\u5f88\u5947\u602a\u3002\u5982\u679c\u6885\u83f2\u65af\u548c\u5580\u6d41\u5976\u5976\u4e0d\u719f\uff0c\u6240\u4ee5\u51db\u7565\u8fc7\u4e0d\u63d0\uff0c\u90a3\u5012\u8fd8\u7b97\u6b63\u5e38\uff0c\u4f46\u660e\u663e\u53c8\u4e0d\u662f\u8fd9\u4e48\u4e00\u56de\u4e8b\u3002\u53c8\u6216\u8005\u662f\u4e24\u4eba\u6709\u4ec0\u4e48\u77db\u76fe\u2014\u2014\u4f46\u4e14\u4e0d\u8bf4\u8fd9\u79cd\u53ef\u80fd\u6027\u5fae\u4e4e\u5176\u5fae\uff0c\u4ece\u5580\u6d41\u5976\u5976\u7684\u8bf4\u8bdd\u6765\u770b\uff0c\u4e5f\u5b8c\u5168\u770b\u4e0d\u51fa\u6709\u7ed3\u6028\u7684\u8ff9\u8c61\u3002\u743c\u6069\u5de6\u601d\u53f3\u60f3\uff0c\u59cb\u7ec8\u6ca1\u660e\u767d\u662f\u600e\u4e48\u56de\u4e8b\u3002</p><p>\u201c\u8fd9\u4e2a\uff0c\u600e\u4e48\u8bf4\u5462\uff0c\u6709\u70b9\u7ed5\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u6211\u4e0d\u662f\u8ddf\u4f60\u8bf4\u8fc7\uff0c\u5580\u6d41\u5976\u5976\u6709\u4e24\u4e2a\u513f\u5b50\u4e48\u3002\u201d</p><p>\u201c\u55ef\uff0c\u4f60\u8bf4\u4ed6\u4eec\u90fd\u6218\u6b7b\u4e86\u3002\u201d</p><p>\u201c\u5bf9\uff0c\u4f46\u5979\u5176\u5b9e\u8fd8\u6709\u4e2a\u5b59\u5b50\uff0c\u662f\u957f\u5b50\u7559\u4e0b\u6765\u7684\u3002\u957f\u5b50\u6218\u6b7b\u540e\uff0c\u513f\u5ab3\u6539\u5ac1\uff0c\u53bb\u4e86\u5916\u5730\uff0c\u4ece\u6b64\u65ad\u4e86\u97f3\u8baf\uff0c\u5580\u6d41\u5976\u5976\u5c31\u548c\u5b59\u5b50\u76f8\u4f9d\u4e3a\u547d\u3002\u5979\u5b59\u5b50\u53eb\u4ec0\u4e48\u6765\u7740\uff0c\u201d\u51db\u52aa\u529b\u5730\u60f3\u4e86\u4e00\u4f1a\uff0c\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u62c9\u83ab\u745f\u65af\u3002\u201d</p><p>\u201c\u7136\u540e\u5462\uff1f\u201d</p><p>\u201c\u7136\u540e\u6709\u6bb5\u65f6\u95f4\uff0c\u6211\u548c\u827e\u5f25\u8587\u5c31\u4f4f\u5728\u5580\u6d41\u5976\u5976\u5bb6\u91cc\u3002\u62c9\u83ab\u745f\u65af\u9047\u5230\u4e86\u827e\u5f25\u8587\uff0c\u4e00\u89c1\u949f\u60c5\uff0c\u7231\u6155\u4e0d\u5df2\uff0c\u4f46\u4f60\u77e5\u9053\u7684\uff0c\u827e\u5f25\u8587\u5bf9\u7537\u4eba\u4ece\u6765\u90fd\u662f\u4e0d\u5047\u8f9e\u8272\uff0c\u51b7\u82e5\u51b0\u971c\u2014\u2014\u597d\u5427\uff0c\u5bf9\u4f60\u6216\u8bb8\u662f\u4f8b\u5916\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u6240\u4ee5\u62c9\u83ab\u745f\u65af\u5c31\u5f88\u4f24\u5fc3\uff0c\u4e8e\u662f\u5c31\u8dd1\u53bb\u559d\u9152\uff0c\u7136\u540e\u501f\u7740\u9152\u610f\u5c31\u60f3\u5bf9\u827e\u5f25\u8587\u56fe\u8c0b\u4e0d\u8f68\u3002\u201d</p><p>\u201c\u2026\u2026\u4ed6\u662f\u4e0d\u60f3\u6d3b\u4e86\u4e48\uff1f\u201d</p><p>\u6885\u83f2\u65af\u90a3\u65f6\u5019\u5e94\u8be5\u624d\u4e5d\u5230\u5341\u5c81\u5de6\u53f3\uff0c\u5e74\u7eaa\u5e7c\u5c0f\uff0c\u8fd8\u6ca1\u9047\u5230\u63d0\u5c14\u5927\u4e3b\u6559\uff0c\u4e5f\u6ca1\u6709\u6210\u4e3a\u5723\u6b66\u58eb\uff0c\u4f46\u5979\u5bb6\u5b66\u6e0a\u6e90\uff0c\u6bcd\u4eb2\u662f\u8d27\u771f\u4ef7\u5b9e\u7684\u6740\u622e\u4e4b\u795e\u9009\u6c11\uff0c\u4f53\u5185\u50a8\u5b58\u4e86\u5927\u90e8\u5206\u90aa\u795e\u795e\u529b\uff0c\u54ea\u91cc\u662f\u4e00\u4e2a\u666e\u901a\u4eba\u53ef\u4ee5\u6b3a\u8d1f\u7684\u3002\u62c9\u83ab\u745f\u65af\u8fd9\u4e48\u505a\uff0c\u9664\u4e86\u627e\u6b7b\uff0c\u522b\u65e0\u89e3\u91ca\u3002</p><p>\u7ed3\u679c\u662f\u4ed6\u771f\u7684\u6b7b\u4e86\u3002</p><p>\u6bd4\u8d77\u73b0\u5728\uff0c\u5f53\u65f6\u7684\u6885\u83f2\u65af\u663e\u7136\u8fd8\u6709\u4e9b\u7a1a\u5ae9\uff0c\u5bf9\u81ea\u5df1\u7684\u529b\u91cf\u7684\u638c\u63e1\u4e5f\u4e0d\u662f\u90a3\u4e48\u5b8c\u7f8e\u3002\u7f9e\u6012\u4e4b\u4e0b\uff0c\u5979\u7684\u51fa\u624b\u91cd\u4e86\u4e00\u4e9b\uff0c\u800c\u9189\u9152\u72b6\u6001\u7684\u62c9\u83ab\u745f\u65af\u66f4\u662f\u63a7\u5236\u4e0d\u4f4f\u8eab\u4f53\uff0c\u683d\u5012\u7684\u65f6\u5019\u5934\u90e8\u5148\u7740\u5730\uff0c\u8116\u9888\u62d7\u65ad\uff0c\u767b\u65f6\u4e0d\u6cbb\u8eab\u4ea1\u3002</p><p>\u201c\u51fa\u4e86\u8fd9\u4e2a\u4e8b\u60c5\uff0c\u5927\u5bb6\u90fd\u5f88\u96be\u53d7\uff0c\u5580\u6d41\u5976\u5976\u8fd8\u662f\u6bd4\u8f83\u901a\u60c5\u8fbe\u7406\u7684\uff0c\u6ca1\u6709\u591a\u8d23\u602a\u827e\u5f25\u8587\uff0c\u4f46\u827e\u5f25\u8587\u89c9\u5f97\u6ca1\u529e\u6cd5\u9762\u5bf9\u5979\uff0c\u4e8e\u662f\u642c\u4e86\u51fa\u53bb\uff0c\u4ece\u6b64\u5c31\u518d\u4e5f\u6ca1\u6709\u56de\u6765\u8fc7\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\u3002\u201d</p><p>\u4e86\u89e3\u5230\u4e8b\u60c5\u7ecf\u8fc7\uff0c\u743c\u6069\u4e5f\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\uff0c\u53ea\u80fd\u542b\u7cca\u8fc7\u53bb\u3002</p><p>\u201c\u5176\u5b9e\u62c9\u83ab\u745f\u65af\u4eba\u633a\u597d\u7684\uff0c\u4ed6\u8fd8\u7ecf\u5e38\u7ed9\u6211\u4e70\u86cb\u7cd5\u5462\uff0c\u201d\u51db\u60cb\u60dc\u5730\u8bf4\uff0c\u201c\u6211\u8fd8\u5e2e\u4ed6\u5728\u827e\u5f25\u8587\u9762\u524d\u8bf4\u8fc7\u51e0\u53e5\u597d\u8bdd\uff0c\u53ef\u60dc\u4e86\u3002\u4ed6\u5982\u679c\u4e0d\u662f\u559d\u591a\u4e86\u72af\u50bb\uff0c\u6162\u6162\u6765\uff0c\u672a\u5fc5\u6ca1\u6709\u673a\u4f1a\u3002\u201d</p><p>\u743c\u6069\u5012\u662f\u4e00\u70b9\u90fd\u4e0d\u89c9\u5f97\u53ef\u60dc\uff0c\u771f\u8ba9\u8fd9\u5bb6\u4f19\u6377\u8db3\u5148\u5f97\uff0c\u90a3\u8fd8\u6709\u81ea\u5df1\u4ec0\u4e48\u4e8b\u3002\u201c\u5f53\u65f6\u8ffd\u6c42\u827e\u5f25\u8587\u7684\u4eba\u5e94\u8be5\u633a\u591a\u7684\u5427\uff0c\u201d\u4ed6\u5c94\u5f00\u8bdd\u9898\uff0c\u201c\u603b\u4e0d\u81f3\u4e8e\u5c31\u4ed6\u4e00\u4e2a\uff1f\u201d</p><p>\u201c\u5f88\u591a\u554a\uff0c\u5341\u51e0\u4e2a\u4e8c\u5341\u4e2a\u603b\u662f\u6709\u7684\uff0c\u53ef\u662f\u827e\u5f25\u8587\u4e00\u4e2a\u90fd\u770b\u4e0d\u4e0a\uff0c\u5979\u5fc3\u9ad8\u6c14\u50b2\uff0c\u603b\u60f3\u627e\u4e00\u4e2a\u6bd4\u81ea\u5df1\u806a\u660e\uff0c\u6bd4\u81ea\u5df1\u5f3a\u7684\u7537\u4eba\uff0c\u201d\u51db\u6241\u6241\u5634\uff0c\u201c\u6ca1\u60f3\u5230\u6700\u540e\u5c45\u7136\u88ab\u4f60\u9a97\u5230\u624b\uff0c\u771f\u662f\u610f\u60f3\u4e0d\u5230\u3002\u201d</p><p>\u201c\u6211\u600e\u4e48\u4e86\uff1f\u201d\u743c\u6069\u4e0d\u670d\u6c14\uff0c\u201c\u597d\u5427\uff0c\u6211\u662f\u4e0d\u4e00\u5b9a\u6bd4\u5979\u66f4\u806a\u660e\uff0c\u4e5f\u4e0d\u6bd4\u5979\u5f3a\u2014\u2014\u4f46\u4e24\u4e2a\u4eba\u5728\u4e00\u8d77\uff0c\u91cd\u8981\u7684\u4e0d\u662f\u8fd9\u4e9b\u554a\uff0c\u91cd\u8981\u7684\u662f\u4e24\u60c5\u76f8\u60a6\uff0c\u5927\u5bb6\u90fd\u5f88\u5f00\u5fc3\u5c31\u597d\u4e86\u561b\u3002\u201d</p><p>\u201c\u5e9f\u8bdd\uff0c\u4f60\u5f53\u7136\u662f\u5f00\u5fc3\u5566\uff0c\u827e\u5f25\u8587\u5c31\u672a\u5fc5\u4e86\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u5929\u5929\u88ab\u7537\u4eba\u73a9\u5c41\u80a1\uff0c\u6709\u4ec0\u4e48\u597d\u5f00\u5fc3\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u743c\u6069\u4e00\u65f6\u95f4\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u56de\u7b54\uff0c\u53ea\u80fd\u5f3a\u8fa9\uff1a\u201c\u4f60\u53c8\u4e0d\u662f\u5979\uff0c\u4f60\u600e\u4e48\u77e5\u9053\u5979\u4e0d\u5f00\u5fc3\u5462\uff1f\u201d</p><p>\u201c\u6211\u5f53\u7136\u77e5\u9053\uff0c\u53c8\u4e0d\u662f\u6ca1\u88ab\u4f60\u73a9\u8fc7\u3002\u201d</p><p>\u201c\u2026\u2026\u4f46\u6211\u73a9\u4f60\u5c41\u80a1\u7684\u65f6\u5019\uff0c\u4f60\u4e5f\u5f88\u5f00\u5fc3\u554a\u3002\u201d</p><p>\u201c\u624d\u6ca1\u6709\u5462\uff01\u201d\u51db\u8138\u4e00\u7ea2\uff0c\u201c\u4eba\u5bb6\u54ea\u6709\u5f88\u5f00\u5fc3\uff01\u201d</p><p>\u201c\u660e\u660e\u5c31\u662f\u3002\u201d</p><p>\u201c\u624d\u4e0d\u662f\uff01\u201d</p><p>\u201c\u771f\u7684\u4e0d\u662f\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u662f\uff01\u201d\u51db\u8bf4\uff0c\u201c\u5f88\u75db\u7684\uff0c\u4e0a\u6b21\u505a\u8fc7\u4e4b\u540e\u6211\u75db\u4e86\u597d\u51e0\u5929\u3002\u827e\u5f25\u8587\u5c31\u662f\u5bf9\u4f60\u592a\u597d\u4e86\uff0c\u6362\u505a\u662f\u6211\uff0c\u65e9\u5c31\u628a\u4f60\u8e39\u5230\u5e8a\u5e95\u4e0b\u53bb\u4e86\u3002\u201d</p><p>\u743c\u6069\u6b63\u60f3\u8c03\u7b11\u51e0\u53e5\uff0c\u5ffd\u7136\u60f3\u8d77\u81ea\u5df1\u73b0\u5728\u7684\u72b6\u6001\uff0c\u4e0d\u7531\u5f97\u53f9\u4e86\u53e3\u6c14\uff0c\u7a81\u7136\u95f4\u4fbf\u610f\u5174\u7d22\u7136\u3002\u5c31\u5728\u4e24\u4e2a\u6708\u4e4b\u524d\uff0c\u4ed6\u8fd8\u662f\u5de6\u62e5\u53f3\u62b1\uff0c\u591c\u591c\u7b19\u6b4c\uff0c\u5982\u4eca\u7f8e\u4eba\u4ecd\u5728\uff0c\u7b11\u8bed\u5ae3\u7136\uff0c\u81ea\u5df1\u5374\u6709\u5fc3\u65e0\u529b\uff0c\u8fd9\u843d\u5dee\u5b9e\u5728\u592a\u5927\u4e86\u3002</p><p>\u201c\u597d\u5566\u597d\u5566\uff0c\u522b\u8fd9\u4e48\u5782\u5934\u4e27\u6c14\u7684\uff0c\u201d\u51db\u8d76\u5feb\u5b89\u6170\uff0c\u201c\u4f60\u53c8\u4e0d\u662f\u771f\u7684\u4e0d\u884c\u4e86\uff0c\u53ea\u662f\u8bc5\u5492\u800c\u5df2\uff0c\u89e3\u5f00\u5c31\u597d\u4e86\u2014\u2014\u800c\u4e14\u4e0d\u662f\u8fd8\u6709\u5854\u59d0\u59d0\u4e48\u3002\u5979\u53ef\u662f\u4e2a\u53d1\u80b2\u6210\u719f\u7684\u5927\u7f8e\u4eba\uff0c\u4f60\u9752\u82f9\u679c\u5403\u591a\u4e86\uff0c\u6b63\u597d\u6362\u6362\u53e3\u5473\uff0c\u514d\u5f97\u771f\u7684\u53d8\u6210\u841d\u8389\u63a7\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u8bf4\u8fd9\u4e9b\u8bdd\uff0c\u81ea\u5df1\u4e0d\u89c9\u5f97\u5f88\u5947\u602a\u5417\uff1f\u201d</p><p>\u201c\u5947\u602a\uff1f\u201d\u51db\u76b1\u7709\u60f3\u4e86\u60f3\uff0c\u4f3c\u4e4e\u6ca1\u89c9\u5f97\u54ea\u91cc\u6709\u95ee\u9898\uff0c\u201c\u6211\u8bf4\u7684\u6709\u4ec0\u4e48\u4e0d\u5bf9\u5417\uff1f\u201d</p><p>\u4e0d\uff0c\u6ca1\u4ec0\u4e48\u4e0d\u5bf9\uff0c\u5c31\u662f\u592a\u5bf9\u4e86\u3002\u743c\u6069\u4e00\u8fb9\u9ed8\u9ed8\u5730\u60f3\u7740\uff0c\u4e00\u8fb9\u51b3\u5b9a\u627e\u4e2a\u673a\u4f1a\u8d81\u51db\u4e0d\u5728\uff0c\u5077\u5077\u6e9c\u8fdb\u5979\u7684\u95fa\u623f\uff0c\u628a\u90a3\u672c\u300a\u540e\u5bab\u6218\u7565\u300b\u62ff\u56de\u6765\u3002\u8fd9\u79cd\u5927\u6076\u9b54\u7684\u8457\u4f5c\u679c\u7136\u4e0d\u80fd\u968f\u4fbf\u4e71\u770b\uff0c\u73b0\u5728\u624d\u521a\u521a\u4e24\u4e2a\u6708\u5c31\u5df2\u7ecf\u8fd9\u6837\uff0c\u518d\u8ba9\u5979\u7ee7\u7eed\u8bfb\u4e0b\u53bb\uff0c\u8fd8\u4e0d\u77e5\u9053\u5fc3\u7075\u4f1a\u88ab\u6bd2\u5bb3\u5230\u4ec0\u4e48\u5730\u6b65\u5462\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u201d\u51db\u7528\u624b\u8098\u6345\u4e86\u6345\u743c\u6069\uff0c\u201c\u5854\u59d0\u59d0\u597d\u5403\u4e48\uff1f\u201d</p><p>\u201c\u2026\u2026\u633a\u597d\u5403\u7684\uff0c\u201d\u743c\u6069\u65e0\u8bed\uff0c\u201c\u4f60\u95ee\u8fd9\u4e2a\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u597d\u5947\u95ee\u95ee\u800c\u5df2\u561b\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u548c\u5854\u59d0\u59d0\u505a\uff0c\u4e0e\u548c\u6211\u4eec\u505a\uff0c\u611f\u89c9\u6709\u4ec0\u4e48\u4e0d\u540c\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u5f88\u96be\u63cf\u8ff0\u554a\u2026\u2026\u5176\u5b9e\u4e5f\u6ca1\u4ec0\u4e48\u592a\u5927\u533a\u522b\u5427\u3002\u201d</p><p>\u201c\u9a97\u4eba\uff0c\u5982\u679c\u6ca1\u6709\u4ec0\u4e48\u533a\u522b\uff0c\u90a3\u4f60\u5e72\u561b\u60f3\u8981\u5f88\u591a\u5973\u4eba\uff0c\u8981\u548c\u4e0d\u540c\u7684\u5973\u4eba\u505a\u5462\uff1f\u6709\u4e00\u4e2a\u4e0d\u5c31\u591f\u4e86\u5417\uff1f\u201d</p><p>\u8fd9\u4e2a\u8bf4\u6cd5\u5f88\u6709\u9053\u7406\uff0c\u743c\u6069\u65e0\u8a00\u4ee5\u5bf9\u3002</p><p>\u201c\u5f53\u7136\u8fd8\u662f\u6709\u4e00\u4e9b\u4e0d\u4e00\u6837\u7684\uff0c\u201d\u4ed6\u52aa\u529b\u89e3\u91ca\uff0c\u201c\u6bd4\u5982\u8bf4\uff0c\u5979\u66f4\u654f\u611f\u4e00\u70b9\uff0c\u6e7f\u5f97\u5feb\u4e00\u4e9b\uff0c\u8fdb\u5165\u7684\u65f6\u5019\u4e5f\u4f1a\u66f4\u5bb9\u6613\uff0c\u4f46\u6ca1\u6709\u4f60\u91cc\u9762\u90a3\u4e48\u70ed\u3002\u5979\u6bd4\u4f60\u5bb9\u6613\u6cc4\u51fa\u6765\uff0c\u5f53\u7136\u8fd9\u662f\u8bf4\u524d\u9762\uff0c\u73a9\u5c41\u80a1\u7684\u8bdd\u5979\u4f1a\u9ad8\u6f6e\u5f97\u6bd4\u8f83\u6162\u2014\u2014\u201d</p><p>\u201c\u7b49\u7b49\uff0c\u201d\u51db\u6253\u65ad\uff0c\u201c\u4f60\u628a\u5854\u59d0\u59d0\u7684\u5c41\u80a1\u4e5f\u5f00\u4e86\uff1f\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u771f\u8fc7\u5206\uff0c\u201d\u51db\u8bc4\u4ef7\uff0c\u201c\u5854\u59d0\u59d0\u4e00\u5b9a\u5f88\u75bc\u3002\u96be\u602a\u6bcf\u6b21\u5979\u548c\u6211\u804a\u5929\u65f6\u63d0\u5230\u4f60\uff0c\u90fd\u8bf4\u4f60\u662f\u4e2a\u8d85\u7ea7\u5927\u574f\u86cb\u3002\u201d</p><p>\u4f60\u7684\u610f\u601d\u662f\u5982\u679c\u6211\u6ca1\u6709\u7ed9\u5979\u5c41\u80a1\u5f00\u82de\uff0c\u6211\u5c31\u662f\u4e2a\u597d\u4eba\u4e86\uff1f\u90a3\u8fd9\u597d\u4eba\u4e5f\u672a\u514d\u592a\u5bb9\u6613\u5f53\u4e86\u5427\u3002</p><p>\u201c\u5f53\u7136\u4e0d\u662f\uff0c\u53ea\u662f\u6ca1\u6709\u90a3\u4e48\u7279\u522b\u574f\u800c\u5df2\uff0c\u201d\u51db\u504f\u7740\u8111\u888b\u60f3\u4e86\u60f3\uff0c\u201c\u5c31\u7b97\u4e2a\u666e\u901a\u5927\u574f\u86cb\u5427\u3002\u201d</p><p>\u90a3\u6211\u8fd8\u662f\u5b81\u613f\u5f53\u8d85\u7ea7\u5927\u574f\u86cb\u3002</p><p>\u5982\u679c\u80fd\u505a\u4e2a\u597d\u4eba\uff0c\u743c\u6069\u5176\u5b9e\u8fd8\u662f\u613f\u610f\u7684\uff0c\u4f46\u8981\u4ed6\u4e3a\u6b64\u653e\u5f03\u67d0\u4e9b\u91cd\u8981\u7684\u4e1c\u897f\uff0c\u90a3\u5c31\u4e0d\u53ef\u80fd\u4e86\u3002\u743c\u6069\u7684\u6b32\u671b\u4e0d\u591a\uff0c\u91d1\u94b1\u3001\u6743\u52bf\u3001\u540d\u671b\uff0c\u8fd9\u4e9b\u5bf9\u4ed6\u90fd\u6ca1\u591a\u5927\u5438\u5f15\u529b\uff0c\u552f\u6709\u7f8e\u8272\u662f\u4ed6\u6240\u597d\u3002\u5bf9\u4ed6\u800c\u8a00\uff0c\u4ec0\u4e48\u79f0\u738b\u79f0\u5e1d\u3001\u7edf\u4e00\u4e16\u754c\u3001\u5bcc\u7532\u5929\u4e0b\u3001\u540d\u5782\u5343\u53e4\u8fd9\u4e9b\u4e1c\u897f\uff0c\u7edf\u7edf\u6ca1\u4ec0\u4e48\u610f\u4e49\uff0c\u552f\u6709\u6253\u9020\u4e00\u4e2a\u5927\u5927\u7684\u540e\u5bab\uff0c\u624d\u662f\u4ed6\u7684\u4eba\u751f\u76ee\u6807\u3002\u800c\u201c\u505a\u4e00\u4e2a\u597d\u4eba\u201d\uff0c\u4e0e\u8fd9\u4e2a\u4eba\u751f\u76ee\u6807\u80af\u5b9a\u662f\u76f8\u51b2\u7a81\u7684\u3002</p><p>\u743c\u6069\u80fd\u591f\u5f97\u5230\u51db\uff0c\u56fa\u7136\u6709\u673a\u7f18\u5de7\u5408\u7684\u6210\u5206\uff0c\u4f46\u5176\u4e2d\u5f88\u91cd\u8981\u7684\u4e00\u4e2a\u56e0\u7d20\u662f\u4ed6\u662f\u6885\u83f2\u65af\u7684\u7537\u53cb\uff0c\u82e5\u975e\u8fd9\u5c42\u8eab\u4efd\uff0c\u53ea\u6015\u8fde\u9760\u8fd1\u5c0f\u5973\u5deb\u90fd\u6ca1\u673a\u4f1a\uff0c\u66f4\u522b\u8bf4\u63a8\u5012\u4e86\u3002\u800c\u743c\u6069\u80fd\u5f97\u5230\u6885\u83f2\u65af\uff0c\u5728\u4e00\u5f00\u59cb\u65f6\u5019\u6240\u7528\u7684\u624b\u6bb5\uff0c\u53ef\u534a\u70b9\u8c08\u4e0d\u4e0a\u5149\u660e\u6b63\u5927\uff0c\u5b8c\u5168\u5c31\u662f\u4e58\u4eba\u4e4b\u5371\u3002\u4e00\u6bb5\u7f8e\u597d\u7684\u604b\u60c5\uff0c\u5374\u6ca1\u6709\u4e00\u4e2a\u5b8c\u7f8e\u7684\u5f00\u7aef\uff0c\u743c\u6069\u6709\u65f6\u5019\u60f3\u60f3\uff0c\u4e5f\u4e0d\u514d\u6709\u4e9b\u8bb8\u9057\u61be\u2014\u2014\u4f46\u4e5f\u4ec5\u4ec5\u53ea\u662f\u9057\u61be\u800c\u5df2\uff0c\u5982\u679c\u65f6\u5149\u5012\u6d41\uff0c\u91cd\u56de\u8fc7\u53bb\uff0c\u4ed6\u4f9d\u7136\u8fd8\u662f\u4f1a\u4f5c\u51fa\u540c\u6837\u7684\u9009\u62e9\u3002</p><p>\u80fd\u5f97\u5230\u7684\uff0c\u624d\u662f\u771f\u7684\uff0c\u81f3\u4e8e\u201c\u505a\u4e2a\u597d\u4eba\u201d\u4e4b\u7c7b\u7684\uff0c\u90a3\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u80fd\u5403\u5417\uff1f</p><p>\u201c\u90a3\u4f60\u662f\u559c\u6b22\u73a9\u5854\u59d0\u59d0\u7684\u5c41\u80a1\u5462\uff0c\u8fd8\u662f\u827e\u5f25\u8587\u7684\u5462\uff1f\u201d</p><p>\u201c\u2026\u2026\u90fd\u559c\u6b22\u3002\u201d</p><p>\u201c\u54ea\u4e2a\u66f4\u559c\u6b22\u5462\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u6ca1\u6cd5\u6bd4\u8f83\u554a\uff0c\u5c31\u50cf\u4f60\u65e2\u559c\u6b22\u9c7c\u53c8\u559c\u6b22\u718a\u638c\uff0c\u4e0d\u540c\u7684\u53e3\u5473\uff0c\u5404\u6709\u5404\u7684\u597d\u5904\uff0c\u6ca1\u6709\u9ad8\u4f4e\u4e4b\u5206\u3002\u201d</p><p>\u201c\u53ef\u662f\u6211\u4e0d\u559c\u6b22\u5403\u9c7c\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u6211\u5c0f\u65f6\u5019\u5403\u9c7c\u7ecf\u5e38\u88ab\u523a\u5361\u4f4f\u5589\u5499\uff0c\u6240\u4ee5\u5c31\u4e0d\u7231\u5403\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u53ea\u662f\u6253\u4e2a\u6bd4\u65b9\u800c\u5df2\u3002\u201d</p><p>\u201c\u603b\u4e4b\u5c31\u662f\u4e0d\u540c\u7684\u5973\u4eba\uff0c\u6709\u4e0d\u4e00\u6837\u7684\u4f53\u9a8c\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u5bf9\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e16\u754c\u4f55\u7b49\u5e7f\u9614\uff0c\u4eba\u751f\u53c8\u4f55\u5176\u77ed\u6682\uff0c\u5c31\u5e94\u8be5\u591a\u7ecf\u5386\uff0c\u591a\u4f53\u9a8c\uff0c\u591a\u54c1\u5c1d\uff0c\u8fd9\u6837\u6700\u540e\u624d\u4e0d\u4f1a\u7559\u4e0b\u9057\u61be\u2014\u2014\u6240\u4ee5\u6211\u7edd\u5bf9\u4e0d\u662f\u56e0\u4e3a\u8d2a\u604b\u7f8e\u8272\u624d\u8fd9\u6837\uff0c\u800c\u662f\u4e3a\u4e86\u8ffd\u6c42\u4eba\u751f\u7684\u610f\u4e49\u5b8c\u6574\uff0c\u8fd9\u662f\u4e00\u79cd\u7eaf\u7cb9\u7684\u5bf9\u7f8e\u7684\u8ffd\u6c42\uff0c\u4f60\u4e00\u5b9a\u80fd\u591f\u660e\u767d\u7684\u5427\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u51db\u82e5\u6709\u6240\u601d\uff0c\u201c\u6240\u4ee5\u8bf4\uff0c\u6211\u53ea\u6709\u4f60\u4e00\u4e2a\u7537\u53cb\uff0c\u8fd9\u662f\u4e0d\u6b63\u786e\u7684\uff0c\u5e94\u8be5\u591a\u4ea4\u51e0\u4e2a\u624d\u5bf9\u3002\u201d</p><p>\u201c\u4e0d\u884c\uff01\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u51db\u53cd\u95ee\uff0c\u201c\u6211\u4e5f\u8981\u8ffd\u6c42\u4eba\u751f\u7684\u610f\u4e49\u554a\u3002\u201d</p><p>\u201c\u4e0d\u884c\u5c31\u662f\u4e0d\u884c\uff01\u201d</p><p>\u201c\u5c0f\u6c14\u9b3c\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u6837\u8bf4\u5427\uff0c\u51db\uff0c\u201d\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u201c\u7537\u6027\u548c\u5973\u6027\u662f\u4e0d\u4e00\u6837\u7684\u3002\u4e00\u4e2a\u7537\u6027\u540c\u65f6\u4ea4\u5f80\u5f88\u591a\u5973\u6027\u662f\u2018\u6b63\u786e\u2019\u7684\uff0c\u800c\u4e00\u4e2a\u5973\u6027\u540c\u65f6\u4ea4\u5f80\u5f88\u591a\u7537\u6027\u5219\u662f\u2018\u4e0d\u6b63\u786e\u2019\u7684\uff0c\u524d\u4e00\u79cd\u6a21\u5f0f\u4e0d\u4f1a\u5bfc\u81f4\u8840\u7edf\u7684\u6df7\u6dc6\uff0c\u6bcf\u4e00\u4e2a\u540e\u4ee3\u7684\u8840\u8109\u6765\u6e90\u90fd\u662f\u6e05\u695a\u800c\u786e\u5b9a\u7684\uff0c\u800c\u540e\u4e00\u79cd\u6a21\u5f0f\uff0c\u4f1a\u5bfc\u81f4\u7236\u4eb2\u65e0\u6cd5\u5224\u65ad\u8c01\u662f\u4ed6\u7684\u5b50\u5973\uff0c\u6216\u8005\u662f\u5176\u4ed6\u4eba\u7684\u5b69\u5b50\uff0c\u8fd9\u6837\u4f1a\u4e25\u91cd\u635f\u5bb3\u7236\u4eb2\u5bf9\u540e\u4ee3\u7684\u8d23\u4efb\u611f\uff0c\u4ece\u800c\u8ba9\u4e0b\u4e00\u4ee3\u65e0\u6cd5\u5f97\u5230\u5145\u8db3\u7684\u8d44\u6e90\u6210\u957f\uff0c\u957f\u6b64\u4ee5\u5f80\uff0c\u751f\u547d\u5c31\u4f1a\u840e\u7f29\uff0c\u8840\u8109\u5c31\u4f1a\u65ad\u7edd\uff0c\u65cf\u7fa4\u5c31\u4f1a\u6d88\u4ea1\uff0c\u4e16\u754c\u5c31\u4f1a\u6bc1\u706d\u2014\u2014\u6240\u4ee5\u4f60\u73b0\u5728\u77e5\u9053\u540e\u679c\u7684\u4e25\u91cd\u6027\u4e86\u5427\u3002\u201d</p><p>\u51db\u770b\u4e86\u743c\u6069\u4e00\u4f1a\uff0c\u683c\u683c\u5730\u7b11\u8d77\u6765\uff0c\u201c\u4f60\u771f\u5389\u5bb3\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u660e\u660e\u5c31\u662f\u81ea\u5df1\u5c0f\u6c14\uff0c\u5374\u80fd\u8f6c\u77ac\u4e4b\u95f4\u5c31\u7f16\u51fa\u4e00\u901a\u51a0\u5195\u5802\u7687\u7684\u5927\u9053\u7406\uff0c\u800c\u4e14\u542c\u8d77\u6765\u597d\u50cf\u8fd8\u6709\u51ed\u6709\u636e\u7684\uff0c\u96be\u602a\u4f60\u80fd\u628a\u827e\u5f25\u8587\u9a97\u5230\u624b\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u53ef\u60dc\u6211\u624d\u4e0d\u5403\u8fd9\u4e00\u5957\u5462\u3002\u5c31\u7b97\u8fd9\u4e2a\u9053\u7406\u518d\u5bf9\uff0c\u4f46\u5bf9\u4e8e\u4f60\u800c\u8a00\u53c8\u4e0d\u9002\u7528\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u4e0d\u9002\u7528\uff1f\u201d\u743c\u6069\u5947\u602a\u3002</p><p>\u201c\u4f60\u53c8\u4e0d\u7528\u62c5\u5fc3\u4ec0\u4e48\u540e\u4ee3\u8840\u7edf\u6df7\u6dc6\u7684\u95ee\u9898\u2014\u2014\u4f60\u4e0d\u662f\u6709\u4e0d\u5b55\u75c7\u561b\uff1f\u201d</p><p>\u201c\u2026\u2026\u8c01\u8bf4\u6211\u6709\u4e0d\u5b55\u75c7\u4e86\uff1f\u201d</p><p>\u201c\u90a3\u600e\u4e48\u6211\u8fd8\u6ca1\u6000\u5b55\u5462\uff1f\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u827e\u5f25\u8587\u4e5f\u6ca1\u6709\u3002\u201d</p><p>\u201c\u5979\u6ca1\u6709\u662f\u6b63\u5e38\u7684\u597d\u5427\uff01\u201d</p><p>\u201c\u5176\u4ed6\u4eba\u4e5f\u6ca1\u6709\u554a\uff0c\u201d\u51db\u6273\u7740\u624b\u6307\u4e00\u4e2a\u4e2a\u5730\u6570\u8fc7\u6765\uff0c\u201c\u73ca\u5609\u3001\u5854\u59d0\u59d0\u3001\u838e\u73de\u514b\uff0c\u90fd\u6ca1\u6709\u3002\u201d</p><p>\u201c\u4f20\u5947\u6545\u4e8b\u7684\u4e3b\u89d2\u901a\u5e38\u90fd\u6ca1\u6709\u5b50\u55e3\uff0c\u201d\u743c\u6069\u786c\u7740\u5934\u76ae\u8bf4\uff0c\u201c\u8fd9\u662f\u5f88\u6b63\u5e38\u7684\u4e8b\u60c5\uff0c\u548c\u4e0d\u5b55\u75c7\u4ec0\u4e48\u7684\u6beb\u65e0\u5173\u7cfb\u3002\u4f60\u770b\u683c\u62c9\u5179\u7279\u90a3\u672c\u4e66\u91cc\u5e94\u8be5\u90fd\u662f\u8fd9\u6837\u5427\u3002\u201d</p><p>\u201c\u6ca1\u6709\u554a\uff0c\u6211\u521a\u770b\u5b8c\u7684\u4e00\u7bc7\uff0c\u91cc\u9762\u7684\u4e3b\u89d2\u4e00\u53e3\u6c14\u751f\u4e86\u4e00\u767e\u4e2a\u513f\u5b50\uff0c\u7136\u540e\u8ba9\u4ed6\u4eec\u4e92\u76f8\u4e89\u6597\uff0c\u4ece\u4e2d\u6311\u9009\u51fa\u4e86\u4e94\u4e2a\u6700\u5f3a\u8005\uff0c\u5206\u522b\u6388\u4e88\u4ed6\u4eec\u4e00\u526f\u9752\u94dc\u6750\u8d28\u7684\u9b54\u6cd5\u52a8\u529b\u94e0\u7532\uff0c\u8ba9\u4ed6\u4eec\u6210\u4e3a\u5973\u795e\u7684\u9009\u6c11\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u770b\u9519\u4e86\uff0c\u90a3\u4e94\u4e2a\u9752\u94dc\u9009\u6c11\u624d\u662f\u4e3b\u89d2\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d\u51db\u5c06\u4fe1\u5c06\u7591\uff0c\u201c\u53ef\u662f\u90a3\u4e94\u4e2a\u5bb6\u4f19\u770b\u8d77\u6765\u90fd\u6ca1\u6709\u4e3b\u89d2\u76f8\u554a\uff0c\u5c24\u5176\u662f\u5176\u4e2d\u6709\u4e00\u4e2a\uff0c\u6bcf\u6b21\u90fd\u88ab\u4eba\u4e00\u62f3\u6253\u8db4\u4e0b\uff0c\u7136\u540e\u7528\u811a\u5728\u8138\u4e0a\u8e29\u554a\u8e29\uff0c\u770b\u8d77\u6765\u597d\u5f31\u7684\u3002\u201d</p><p>\u201c\u90a3\u5c31\u66f4\u6ca1\u9519\u4e86\uff0c\u4ed6\u6b63\u662f\u4e3b\u89d2\u4e2d\u7684\u4e3b\u89d2\u3002\u201d</p><p>\u6bcf\u6b21\u548c\u51db\u804a\u5929\uff0c\u743c\u6069\u7684\u5fc3\u60c5\u603b\u662f\u4f1a\u53d8\u5f97\u5f88\u6109\u5feb\uff0c\u5979\u5929\u771f\u3001\u5355\u7eaf\uff0c\u5374\u53c8\u5e76\u975e\u6d45\u8584\uff0c\u800c\u662f\u53d1\u81ea\u5185\u5fc3\u7684\u5766\u8bda\uff0c\u4eff\u4f5b\u6676\u83b9\u5254\u900f\u7684\u5b9d\u77f3\uff0c\u7ea4\u5c18\u4e0d\u67d3\uff0c\u6bcf\u4e2a\u5207\u9762\u90fd\u95ea\u95ea\u53d1\u5149\uff0c\u6709\u79cd\u96be\u4ee5\u5f62\u5bb9\u7684\u9b45\u529b\uff0c\u8ba9\u743c\u6069\u4e0d\u7531\u81ea\u4e3b\u5730\u653e\u677e\u4e0b\u6765\uff0c\u5c06\u6240\u6709\u7684\u70e6\u607c\u548c\u5fe7\u8651\u90fd\u6682\u65f6\u5fd8\u5374\u3002\u6709\u90a3\u4e48\u4e00\u4f1a\uff0c\u4ed6\u751a\u81f3\u90fd\u5728\u8ba4\u771f\u601d\u8003\uff0c\u5982\u679c\u5c06\u6765\u5f53\u771f\u80fd\u5f97\u507f\u6240\u613f\uff0c\u7f8e\u68a6\u6210\u771f\uff0c\u5efa\u7acb\u4e00\u4e2a\u5927\u5927\u7684\u540e\u5bab\uff0c\u5c06\u6240\u6709\u559c\u6b22\u7684\u5973\u5b69\u5b50\u90fd\u7eb3\u5165\u5176\u4e2d\u7684\u8bdd\uff0c\u8981\u4e0d\u8981\u5c31\u7acb\u51db\u4e3a\u6b63\u59bb\uff0c\u5979\u770b\u8d77\u6765\u786e\u5b9e\u5f88\u9002\u5408\u3002</p><p>\u5f53\u7136\uff0c\u4e5f\u53ea\u662f\u60f3\u60f3\u800c\u5df2\u3002</p><p>\u65e7\u57ce\u533a\u5f88\u5927\uff0c\u5355\u8bba\u9762\u79ef\u7684\u8bdd\uff0c\u6bd4\u65b0\u57ce\u533a\u8fd8\u80dc\u8fc7\u51e0\u5206\uff0c\u4f46\u8def\u4e0a\u51e0\u4e4e\u770b\u4e0d\u5230\u884c\u4eba\uff0c\u5468\u56f4\u9759\u6084\u6084\u7684\uff0c\u5f88\u591a\u623f\u5c4b\u90fd\u5df2\u7ecf\u660e\u663e\u6ca1\u6709\u4eba\u5c45\u4f4f\u3002\u743c\u6069\u4e0d\u8ba4\u8bc6\u8def\uff0c\u53ea\u80fd\u8ddf\u7740\u51db\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u51db\u7a81\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u540c\u65f6\u5c06\u4ed6\u4e00\u628a\u62c9\u4f4f\u3002</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u57c3\u535c\u62c9\u3002\u201d\u51db\u8bf4\u3002</p><p>\u743c\u6069\u987a\u7740\u51db\u7684\u89c6\u7ebf\u770b\u53bb\uff0c\u770b\u89c1\u8fdc\u5904\u4e00\u8f86\u9a6c\u8f66\u4ece\u8857\u9053\u8f6c\u89d2\u5904\u9a76\u8fc7\uff0c\u5f62\u5236\u6709\u4e9b\u719f\u6089\u3002\u900f\u8fc7\u9a6c\u8f66\u7684\u8f66\u7a97\uff0c\u4ed6\u77a5\u89c1\u4e86\u4e00\u4e2a\u4eba\u7684\u4fa7\u8138\uff0c\u6b63\u662f\u6628\u665a\u89c1\u8fc7\u7684\u90a3\u4f4d\u201c\u57c3\u535c\u62c9\u201d\uff0c\u8f9b\u5df4\u57ce\u57ce\u4e3b\u4e4b\u5b50\u3002</p></article>"}}}]);
