(window.webpackJsonp=window.webpackJsonp||[]).push([[547],{569:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u8282 \u706b\u4e0e\u7ea2\u9f99",content:"<article><h2>\u7b2c\u5341\u8282 \u706b\u4e0e\u7ea2\u9f99</h2><p>\u201c\u4ed6\u600e\u4e48\u5728\u8fd9\u91cc\uff1f\u201d\u51db\u76b1\u7709\u3002</p><p>\u201c\u7ba1\u4ed6\u5462\uff0c\u201d\u743c\u6069\u89c9\u5f97\u83ab\u540d\u5176\u5999\uff0c\u201c\u8fd9\u91cc\u662f\u4ed6\u7684\u5730\u76d8\uff0c\u4ed6\u60f3\u53bb\u54ea\u5c31\u53bb\u54ea\u5427\uff0c\u53cd\u6b63\u548c\u6211\u4eec\u53c8\u6ca1\u5173\u7cfb\u3002\u201d</p><p>\u201c\u8fd9\u91cc\u662f\u65e7\u57ce\u533a\u3002\u201d</p><p>\u201c\u90a3\u53c8\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u57c3\u535c\u62c9\u4ece\u6765\u4e0d\u6765\u65e7\u57ce\u533a\uff0c\u4ed6\u4e0d\u559c\u6b22\u8fd9\u91cc\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u8fd9\u4e0d\u5bf9\u52b2\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u5728\u7b56\u5212\u4ec0\u4e48\u9634\u8c0b\u8be1\u8ba1\u3002\u201d</p><p>\u743c\u6069\u89c9\u5f97\u51db\u672a\u514d\u6709\u4e9b\u8fc7\u5206\u654f\u611f\uff0c\u57c3\u535c\u62c9\u662f\u8f9b\u5df4\u57ce\u4eba\uff0c\u5728\u65b0\u57ce\u533a\u5efa\u6210\u4e4b\u524d\uff0c\u4ed6\u4e5f\u66fe\u7ecf\u5728\u65e7\u57ce\u533a\u751f\u6d3b\u4e86\u591a\u5e74\uff0c\u5076\u5c14\u6545\u5730\u91cd\u6e38\uff0c\u4e5f\u662f\u4eba\u4e4b\u5e38\u60c5\uff0c\u6709\u4ec0\u4e48\u4e0d\u5bf9\u52b2\u7684\u3002\u4f46\u51db\u5411\u6765\u662f\u51ed\u76f4\u89c9\u505a\u5224\u65ad\u7684\u4eba\uff0c\u624d\u4e0d\u4f1a\u8003\u8651\u8fd9\u4e9b\uff0c\u5979\u89c9\u5f97\u6709\u95ee\u9898\uff0c\u90a3\u5c31\u4e00\u5b9a\u662f\u6709\u95ee\u9898\uff0c\u6ca1\u4ec0\u4e48\u597d\u4e89\u8bba\u7684\u3002</p><p>\u201c\u90a3\u6211\u4eec\u600e\u4e48\u529e\uff0c\u8ddf\u4e0a\u53bb\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u8ddf\u4e0a\u53bb\u770b\u770b\u3002\u201d</p><p>\u57c3\u535c\u62c9\u662f\u4e58\u7740\u9a6c\u8f66\uff0c\u743c\u6069\u548c\u51db\u5219\u662f\u6b65\u884c\uff0c\u4f46\u65e7\u57ce\u533a\u7684\u9053\u8def\u539f\u672c\u5c31\u4e0d\u662f\u5f88\u5bbd\u655e\u5e73\u5766\uff0c\u53c8\u5e74\u4e45\u5931\u4fee\uff0c\u9a6c\u8f66\u6839\u672c\u8dd1\u4e0d\u8d77\u6765\uff0c\u6240\u4ee5\u60f3\u8ddf\u4e0a\u5e94\u8be5\u4e5f\u4e0d\u96be\u3002\u4e0d\u6599\u8fd8\u6ca1\u8d70\u591a\u8fdc\uff0c\u521a\u521a\u8f6c\u8fc7\u4e00\u4e2a\u8def\u53e3\uff0c\u9a6c\u8f66\u4fbf\u7a81\u7136\u5931\u53bb\u4e86\u8e2a\u8ff9\uff0c\u4eff\u4f5b\u4ece\u7a7a\u6c14\u4e2d\u84b8\u53d1\u4e86\u4e00\u822c\uff0c\u743c\u6069\u5de6\u770b\u53f3\u770b\uff0c\u4e5f\u6ca1\u53d1\u73b0\u4ec0\u4e48\u5c94\u8def\u53e3\uff0c\u4e0d\u7531\u5f97\u8be7\u5f02\u8d77\u6765\u3002</p><p>\u201c\u4ed6\u53bb\u54ea\u4e86\uff1f\u201d\u51db\u5f88\u5947\u602a\u3002</p><p>\u201c\u5927\u6982\u662f\u53d1\u73b0\u4e86\u6211\u4eec\u5728\u8ddf\u8e2a\u5427\uff1f\u201d\u743c\u6069\u731c\u6d4b\uff0c\u201c\u4f46\u6211\u6ca1\u611f\u89c9\u5230\u6cd5\u672f\u7684\u75d5\u8ff9\u554a\uff0c\u4ed6\u662f\u600e\u4e48\u7a81\u7136\u6d88\u5931\u7684\uff1f\u201d</p><p>\u51db\u4e5f\u5f04\u4e0d\u660e\u767d\uff0c\u4f46\u5979\u7684\u4f18\u70b9\u5c31\u662f\u4ece\u6765\u4e0d\u7ea0\u7ed3\u3002\u201c\u8dd1\u4e86\u5c31\u7b97\u4e86\uff0c\u672c\u6765\u8fd8\u60f3\u770b\u770b\u4ed6\u9b3c\u9b3c\u795f\u795f\u7684\u60f3\u5e72\u561b\u5462\uff0c\u201d\u5979\u770b\u770b\u5929\u8272\uff0c\u201c\u6211\u4eec\u53bb\u770b\u5580\u6d41\u5976\u5976\u5427\u3002\u201d</p><p>\u5230\u4e86\u5580\u6d41\u5976\u5976\u7684\u5bb6\u4e2d\uff0c\u5374\u53d1\u73b0\u5979\u4e0d\u5728\uff0c\u95e8\u662f\u655e\u5f00\u7684\u3002\u743c\u6069\u548c\u51db\u4ee5\u4e3a\u8001\u4eba\u4e34\u65f6\u5916\u51fa\uff0c\u4e5f\u6ca1\u6709\u591a\u60f3\uff0c\u7136\u800c\u4e00\u76f4\u7b49\u5f85\u4e86\u534a\u4e2a\u591a\u5c0f\u65f6\uff0c\u4ecd\u7136\u4e0d\u89c1\u8001\u4eba\u8fd4\u56de\uff0c\u51db\u4e0d\u7531\u5f97\u5c31\u6709\u4e9b\u5750\u4e0d\u4f4f\u4e86\uff0c\u201c\u8981\u4e48\u53bb\u95ee\u95ee\u90bb\u5c45\u5427\u3002\u201d\u743c\u6069\u63d0\u8bae\u3002</p><p>\u201c\u55ef\u3002\u201d</p><p>\u4e24\u4eba\u6b63\u8981\u51fa\u95e8\uff0c\u4e00\u4f4d\u4e2d\u5e74\u7537\u5b50\u8d70\u8fdb\u6765\uff0c\u4ed6\u663e\u7136\u6ca1\u6599\u5230\u5bb6\u91cc\u8fd8\u6709\u4eba\uff0c\u6123\u4e86\u4e00\u4e0b\uff0c\u7136\u540e\u4f3c\u4e4e\u662f\u8ba4\u51fa\u4e86\u5ba2\u4eba\u7684\u8eab\u4efd\uff0c\u201c\u662f\u5c0f\u51db\u5427\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u662f\u6211\uff0c\u591a\u5c3c\u5927\u53d4\uff0c\u597d\u4e45\u4e0d\u89c1\u4e86\uff0c\u201d\u51db\u4e56\u5de7\u5730\u6253\u62db\u547c\uff0c\u201c\u6211\u662f\u6765\u770b\u5580\u6d41\u5976\u5976\u7684\uff0c\u5979\u51fa\u95e8\u4e86\u5417\uff1f\u201d</p><p>\u201c\u5979\u51fa\u4e8b\u4e86\u3002\u201d</p><p>\u201c\u554a\uff1f\u201d\u51db\u5413\u4e86\u4e00\u8df3\uff0c\u201c\u5979\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u4e0b\u5348\u53bb\u6311\u6c34\u7684\u65f6\u5019\u6454\u4e86\u4e00\u8de4\uff0c\u633a\u4e25\u91cd\u7684\uff0c\u73b0\u5728\u5728\u9f99\u5bab\u3002\u201d</p><p>\u8fd9\u4f4d\u591a\u5c3c\u5927\u53d4\u4f4f\u5728\u9644\u8fd1\uff0c\u5580\u6d41\u5976\u5976\u51fa\u4e8b\u7684\u65f6\u5019\u4ed6\u6b63\u597d\u5728\u573a\uff0c\u8d76\u7d27\u548c\u51e0\u4e2a\u90bb\u5c45\u4e00\u8d77\u5c06\u8001\u4eba\u9001\u5230\u201c\u9f99\u5bab\u201d\u91cc\uff0c\u627e\u796d\u53f8\u6cbb\u7597\u3002\u4ed6\u662f\u56de\u6765\u66ff\u5580\u6d41\u5976\u5976\u5173\u95e8\u7684\uff0c\u4e0d\u6599\u6070\u597d\u78b0\u5230\u51db\u3002</p><p>\u201c\u90a3\u6211\u4eec\u73b0\u5728\u5c31\u53bb\u3002\u201d</p><p>\u51db\u4e8c\u8bdd\u4e0d\u8bf4\uff0c\u62c9\u7740\u743c\u6069\u51fa\u95e8\u3002\u201c\u9f99\u5bab\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u743c\u6069\u4e00\u8fb9\u88ab\u51db\u62c9\u7740\u98de\u5954\u4e00\u8fb9\u95ee\uff0c\u4ed6\u5f00\u59cb\u62c5\u5fc3\u5f85\u4f1a\u8981\u4e0d\u8981\u6f5c\u6c34\uff0c\u65e9\u4e0a\u53ef\u6ca1\u6709\u51c6\u5907\u8fd9\u79cd\u7c7b\u578b\u7684\u6cd5\u672f\u3002</p><p>\u201c\u9f99\u5bab\u662f\u2026\u2026\u5f85\u4f1a\u4f60\u5c31\u770b\u5230\u4e86\u3002\u201d</p><p>\u5927\u6982\u662f\u89c9\u5f97\u89e3\u91ca\u8d77\u6765\u592a\u9ebb\u70e6\uff0c\u4e8e\u662f\u7d22\u6027\u5c31\u4e0d\u89e3\u91ca\uff0c\u743c\u6069\u4e5f\u6ca1\u529e\u6cd5\uff0c\u53ea\u597d\u8ddf\u7740\u51db\u3002\u5927\u7ea6\u5341\u51e0\u5206\u949f\u540e\uff0c\u4ed6\u4eec\u6765\u5230\u4e86\u4e00\u4e2a\u516c\u56ed\u6a21\u6837\u7684\u5730\u65b9\uff0c\u679d\u53f6\u8302\u76db\u7684\u6811\u4e1b\u4e4b\u4e2d\uff0c\u9690\u9690\u7ea6\u7ea6\u9732\u51fa\u4e00\u5904\u6697\u9752\u8272\u7684\u5efa\u7b51\uff0c\u518d\u8d70\u8fdb\u4e9b\uff0c\u53d1\u73b0\u90a3\u662f\u4e00\u5ea7\u57ce\u5821\uff0c\u663e\u7136\u4e5f\u9887\u6709\u4e9b\u5e74\u5934\u4e86\u3002\u57ce\u5821\u7684\u5927\u95e8\u655e\u5f00\u7740\uff0c\u6709\u4e24\u540d\u536b\u5175\u5728\u628a\u5b88\uff0c\u770b\u8d77\u6765\u4e5f\u90fd\u61d2\u6d0b\u6d0b\u7684\uff0c\u6ca1\u4ec0\u4e48\u7cbe\u795e\u3002\u201c\u8fd9\u5c31\u662f\u9f99\u5bab\u3002\u201d\u51db\u8bf4\u3002</p><p>\u201c\u9f99\u5728\u54ea\u91cc\uff1f\u201d</p><p>\u201c\u65e9\u6b7b\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u51db\u89e3\u91ca\u4e86\u51e0\u53e5\uff0c\u743c\u6069\u8fd9\u624d\u660e\u767d\u8fc7\u6765\uff0c\u539f\u6765\u8fd9\u662f\u6614\u65e5\u5bdf\u65af\u8428\u7684\u5bab\u6bbf\u6240\u5728\u3002\u5bdf\u65af\u8428\u7684\u4e3a\u4eba\uff0c\u9887\u4e3a\u597d\u5927\u559c\u529f\uff0c\u770b\u770b\u6e2f\u53e3\u5916\u7684\u90a3\u5ea7\u9752\u94dc\u5927\u706f\u5854\u4fbf\u77e5\u9053\uff0c\u4ed6\u7edf\u4e00\u5f7b\u68ee\u5854\uff0c\u767b\u4e0a\u738b\u4f4d\u540e\uff0c\u4fbf\u5f81\u53d1\u6c11\u592b\uff0c\u4e3a\u81ea\u5df1\u4fee\u5efa\u4e86\u4e00\u5ea7\u58ee\u89c2\u7684\u5bab\u6bbf\u3002\u56e0\u4e3a\u5bdf\u65af\u8428\u662f\u201c\u7ea2\u9f99\u738b\u201d\uff0c\u6240\u4ee5\u4ed6\u7684\u5bab\u6bbf\u5f53\u7136\u88ab\u79f0\u4e3a\u9f99\u5bab\u3002\u5bdf\u65af\u8428\u5931\u8e2a\u4e4b\u540e\uff0c\u5f7b\u68ee\u5854\u518d\u5ea6\u9677\u5165\u6218\u4e71\uff0c\u4ed6\u7684\u4e00\u540d\u90e8\u4e0b\uff08\u73b0\u4efb\u8f9b\u5df4\u57ce\u4e3b\u6ce2\u65af\u7eb3\u8428\u5c14\u7684\u5148\u7956\uff09\u5360\u636e\u4e86\u8f9b\u5df4\u57ce\uff0c\u81ea\u5c01\u57ce\u4e3b\uff0c\u5374\u6ca1\u6709\u5165\u4f4f\u9f99\u5bab\uff0c\u800c\u662f\u53e6\u5916\u5efa\u4e86\u4e00\u5ea7\u5e9c\u90b8\uff0c\u9f99\u5bab\u5c31\u6b64\u7a7a\u7f6e\u4e0b\u6765\uff0c\u4e4b\u540e\u4e5f\u518d\u4e5f\u6ca1\u6709\u4eba\u5165\u4f4f\u3002</p><p>\u5927\u6982\u662f\u5f53\u65f6\u5efa\u9020\u7684\u65f6\u5019\u7684\u786e\u5f88\u7528\u5fc3\uff0c\u8fd9\u5ea7\u9f99\u5bab\u7684\u5efa\u7b51\u8d28\u91cf\u597d\u5f97\u51fa\u5947\uff0c\u5728\u6ca1\u6709\u4efb\u4f55\u7ef4\u62a4\u7684\u60c5\u51b5\u4e0b\uff0c\u5386\u7ecf\u51e0\u767e\u5e74\u7684\u98ce\u98ce\u96e8\u96e8\uff0c\u4ecd\u7136\u5c79\u7acb\u4e0d\u5012\u3002\u5341\u51e0\u5e74\u524d\uff0c\u4e00\u76f4\u5904\u4e8e\u5730\u4e0b\u72b6\u6001\u7684\u7ea2\u9f99\u6559\u4f1a\u83b7\u51c6\u5728\u8f9b\u5df4\u57ce\u516c\u5f00\u4f20\u6559\uff0c\u53c8\u5bf9\u5b83\u8fdb\u884c\u4e86\u4e00\u5b9a\u7684\u4fee\u7f2e\uff0c\u4e5f\u66fe\u7ecf\u8003\u8651\u8fc7\u5c31\u4ee5\u6b64\u4f5c\u4e3a\u795e\u6bbf\uff0c\u4f46\u6709\u4eba\u63d0\u51fa\u8fd9\u91cc\u66fe\u7ecf\u662f\u963f\u666e\u82cf\u795e\u6bbf\u7684\u9057\u5740\u6240\u5728\uff0c\u4e8e\u662f\u4fbf\u653e\u5f03\u4e86\uff0c\u6539\u5728\u5317\u5cb8\u5efa\u4e86\u4e00\u5ea7\u65b0\u7684\u795e\u6bbf\u3002</p><p>\u201c\u8fd9\u91cc\u66fe\u7ecf\u662f\u963f\u666e\u82cf\u7684\u795e\u6bbf\uff1f\u201d</p><p>\u201c\u4f20\u8bf4\u662f\uff0c\u8c01\u77e5\u9053\u5462\uff0c\u90fd\u662f\u51e0\u5343\u5e74\u524d\u7684\u4e8b\u60c5\u4e86\u3002\u201d</p><p>\u636e\u8bf4\u6614\u65e5\u4e24\u4f4d\u795e\u738b\u5728\u6b64\u5730\u5efa\u57ce\uff0c\u5efa\u4e86\u4e24\u5ea7\u795e\u6bbf\uff0c\u963f\u666e\u82cf\u7684\u795e\u6bbf\u4f4d\u4e8e\u8f9b\u5df4\u57ce\u7684\u4e2d\u5fc3\uff0c\u5e87\u62a4\u5e02\u6c11\uff0c\u63d0\u4e9a\u739b\u7279\u7684\u795e\u6bbf\u4f4d\u4e8e\u57ce\u5916\u7684\u6cb3\u5317\u5cb8\uff0c\u9547\u538b\u5deb\u5e08\u7684\u90aa\u7075\u3002\u4e0d\u8fc7\u968f\u7740\u795e\u738b\u7684\u9668\u843d\uff0c\u795e\u6bbf\u4e5f\u5728\u6f2b\u957f\u7684\u5386\u53f2\u4e2d\u53d8\u6210\u5e9f\u589f\uff0c\u9664\u4e86\u4e00\u4e9b\u865a\u65e0\u7f25\u7f08\u7684\u4f20\u8bf4\uff0c\u8fde\u4f9b\u540e\u4eba\u51ed\u540a\u7684\u9057\u8ff9\u90fd\u770b\u4e0d\u5230\u4e86\u3002</p><p>\u7ea2\u9f99\u6559\u4f1a\u4ee5\u5bdf\u65af\u8428\u4e3a\u795e\uff0c\u795e\u66fe\u7ecf\u7684\u5c45\u6240\uff0c\u90a3\u5f53\u7136\u5c31\u662f\u201c\u5723\u5c45\u201d\uff0c\u867d\u7136\u5728\u6cb3\u5317\u5cb8\u53e6\u5efa\u795e\u6bbf\uff0c\u4f46\u4ecd\u7136\u6d3e\u4eba\u7167\u770b\u6b64\u5904\uff0c\u4e5f\u4f1a\u6d3e\u9063\u796d\u53f8\u957f\u671f\u8f6e\u503c\u9a7b\u5b88\u3002\u800c\u4e14\u9887\u6709\u4e00\u4e9b\u4e0a\u4e86\u5e74\u7eaa\uff0c\u60f3\u6cd5\u6bd4\u8f83\u5b88\u65e7\u7684\u5c45\u6c11\uff0c\u4f8b\u5982\u5580\u6d41\u5976\u5976\u8fd9\u79cd\uff0c\u4fe1\u4ef0\u8654\u8bda\uff0c\u5374\u656c\u754f\u4f20\u8bf4\uff0c\u4e0d\u613f\u53bb\u8f9b\u5df4\u6cb3\u5317\u5cb8\uff0c\u4fbf\u9009\u62e9\u6b64\u5904\u4f5c\u4e3a\u5b97\u6559\u6d3b\u52a8\u7684\u636e\u70b9\u3002\u65f6\u95f4\u4e00\u957f\uff0c\u8fd9\u91cc\u5c31\u53d8\u6210\u4e86\u4e00\u4e2a\u7c7b\u4f3c\u4e8e\u795e\u6bbf\u5206\u90e8\u7684\u5b58\u5728\u3002\u4e1c\u57df\u7684\u533b\u5b66\u4e0d\u600e\u4e48\u53d1\u8fbe\uff0c\u65e7\u57ce\u533a\u4e2d\u751a\u81f3\u90fd\u6ca1\u6709\u4e00\u5bb6\u533b\u9986\uff08\u539f\u672c\u6709\u4e00\u5bb6\uff0c\u5df2\u7ecf\u642c\u8fc1\u5230\u6cb3\u5317\u5cb8\u65b0\u57ce\u533a\u4e86\uff09\uff0c\u5c45\u6c11\u4eec\u5e73\u5e38\u5c0f\u4f24\u5c0f\u75c5\u5c31\u81ea\u5df1\u89e3\u51b3\uff0c\u82e5\u662f\u60c5\u5f62\u4e25\u91cd\uff0c\u4f8b\u5982\u5580\u6d41\u5976\u5976\u8fd9\u6b21\uff0c\u5c31\u4f1a\u9001\u5230\u9f99\u5bab\u4e2d\uff0c\u8bf7\u6c42\u796d\u53f8\u5e2e\u5fd9\u6cbb\u7597\u3002</p><p>\u9f99\u5bab\u5e76\u975e\u65c5\u6e38\u666f\u70b9\uff0c\u5b83\u53ea\u5bf9\u7ea2\u9f99\u6559\u4f1a\u5185\u90e8\u6210\u5458\u548c\u4fe1\u5f92\u5f00\u653e\uff0c\u743c\u6069\u548c\u51db\u90fd\u4e0d\u7b26\u5408\u6761\u4ef6\uff0c\u76f4\u63a5\u8fdb\u662f\u8fdb\u4e0d\u53bb\u7684\uff0c\u80af\u5b9a\u4f1a\u88ab\u536b\u5175\u62e6\u4e0b\u3002\u4f46\u8fd9\u4e5f\u96be\u4e0d\u5012\u4ed6\u4eec\uff0c\u743c\u6069\u4ece\u6597\u7bf7\u91cc\u53d6\u51fa\u7d2b\u6c34\u6676\u6cd5\u6756\uff0c\u5728\u81ea\u5df1\u548c\u51db\u8eab\u4e0a\u90fd\u8f7b\u89e6\u4e86\u4e0b\uff0c\u5c06\u4e24\u4eba\u9690\u5f62\uff0c\u7136\u540e\u671d\u7740\u9f99\u5bab\u5185\u8d70\u8fdb\u53bb\u3002</p><p>\u5b88\u95e8\u7684\u58eb\u5175\u5b8c\u5168\u6ca1\u6709\u5bdf\u89c9\uff0c\u8ba9\u743c\u6069\u548c\u51db\u987a\u5229\u901a\u8fc7\u3002\u8f6c\u60a0\u4e86\u7247\u523b\uff0c\u4ed6\u4eec\u5728\u4e00\u4e2a\u623f\u95f4\u91cc\u627e\u5230\u4e86\u5580\u6d41\u5976\u5976\uff0c\u8001\u4eba\u6b63\u5e73\u8eba\u5728\u5e8a\u4e0a\uff0c\u53f3\u817f\u3001\u53f3\u81c2\u4e0a\u90fd\u7f20\u7740\u767d\u8272\u7684\u5e03\u6761\u7ed1\u5e26\uff0c\u661f\u661f\u70b9\u70b9\u7684\u8840\u8ff9\u4ece\u4e2d\u900f\u51fa\u6765\uff0c\u770b\u8d77\u6765\u4f24\u5f97\u4e0d\u8f7b\uff0c\u4f46\u4eba\u7684\u795e\u667a\u8fd8\u6e05\u9192\uff0c\u6b63\u5728\u548c\u65c1\u8fb9\u7684\u51e0\u4e2a\u4eba\u8bf4\u8bdd\u3002\u743c\u6069\u770b\u4e86\u4e00\u5708\uff0c\u5374\u5e76\u672a\u53d1\u73b0\u6709\u796d\u53f8\u6a21\u6837\u7684\u4eba\u5728\u573a\uff0c\u770b\u8863\u7740\u90fd\u53ea\u662f\u666e\u901a\u7684\u57ce\u4e2d\u5c45\u6c11\u3002</p><p>\u743c\u6069\u5f85\u8981\u8fdb\u53bb\uff0c\u5374\u88ab\u51db\u62c9\u4f4f\u4e86\uff0c\u201c\u7b49\u4f1a\u3002\u201d\u5979\u8bf4\u3002</p><p>\u201c\u4f60\u8fd9\u4e0b\u53ef\u771f\u662f\u6454\u5f97\u591f\u91cd\uff0c\u201d\u4e00\u4f4d\u548c\u5580\u6d41\u5976\u5976\u5e74\u9f84\u76f8\u4eff\uff0c\u56f4\u7740\u6697\u9ec4\u8272\u56f4\u5dfe\u7684\u8001\u5987\u4eba\u8bf4\uff0c\u201c\u5e78\u597d\u65c1\u8fb9\u6709\u4eba\uff0c\u5426\u5219\u53ef\u5c31\u5371\u9669\u4e86\u3002\u201d</p><p>\u201c\u4eba\u8001\u4e86\uff0c\u817f\u811a\u90fd\u50f5\u4e86\uff0c\u201d\u5580\u6d41\u5976\u5976\u53f9\u606f\u7740\u8bf4\uff0c\u201c\u8fd9\u51e0\u5e74\u7ecf\u5e38\u751f\u75c5\uff0c\u8eab\u4e0a\u5230\u5904\u75db\uff0c\u770b\u6765\u4e5f\u662f\u5feb\u4e0d\u884c\u4e86\u3002\u5c31\u7b97\u6ca1\u6709\u8fd9\u6b21\uff0c\u4e5f\u6d3b\u4e0d\u4e86\u591a\u4e45\u55bd\u3002\u201d</p><p>\u201c\u522b\u4e71\u8bf4\uff0c\u53ea\u662f\u4e00\u70b9\u5c0f\u4f24\u800c\u5df2\uff0c\u796d\u53f8\u52a8\u52a8\u624b\u6307\u5c31\u80fd\u6062\u590d\uff0c\u6211\u4e0a\u6b21\u4e5f\u662f\u628a\u80f3\u818a\u6454\u6298\u4e86\uff0c\u8bf7\u5361\u5170\u5927\u4eba\u5ff5\u4e86\u4e00\u904d\u5492\u8bed\uff0c\u4e0d\u5230\u56db\u4e94\u5929\u5c31\u5b8c\u5168\u957f\u597d\u4e86\uff0c\u201d\u53e6\u4e00\u4f4d\u4e2d\u5e74\u7537\u5b50\u8bf4\uff0c\u4ed6\u957f\u7740\u4e00\u4e2a\u7ea2\u5f64\u5f64\u7684\u9152\u7cdf\u9f3b\u5b50\uff0c\u770b\u8d77\u6765\u9887\u4e3a\u6ed1\u7a3d\uff0c\u4f46\u8bf4\u8bdd\u58f0\u97f3\u5012\u662f\u610f\u5916\u7684\u6e29\u548c\uff0c\u201c\u963f\u59e8\u8fd8\u662f\u5b89\u5fc3\u4f11\u606f\uff0c\u5361\u5170\u5927\u4eba\u5e94\u8be5\u4e5f\u5feb\u56de\u6765\u4e86\u3002\u201d</p><p>\u201c\u53ea\u6015\u5979\u56de\u6765\u4e5f\u6ca1\u7528\uff0c\u201d\u4e00\u4e2a\u7a7f\u7740\u7070\u6251\u6251\u7684\u5916\u5957\u7684\u9752\u5e74\u8bf4\uff0c\u8138\u548c\u4e4b\u524d\u8bf4\u8bdd\u7684\u4e2d\u5e74\u4eba\u957f\u5f97\u5341\u5206\u4e4b\u50cf\uff0c\u663e\u7136\u662f\u7236\u5b50\uff0c\u8fde\u9152\u7cdf\u9f3b\u5b50\u90fd\u5b8c\u7f8e\u9057\u4f20\u4e86\u4e0b\u6765\uff0c\u201c\u9f99\u738b\u5df2\u7ecf\u79bb\u53bb\uff0c\u5723\u5973\u5373\u5c06\u964d\u4e34\uff0c\u90a3\u4e9b\u796d\u53f8\u4eec\u5df2\u7ecf\u6210\u4e86\u4e27\u5bb6\u4e4b\u72ac\uff0c\u6ca1\u4e86\u9760\u5c71\uff0c\u8fd8\u80fd\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u653e\u5c41\uff01\u201d\u4e2d\u5e74\u7537\u5b50\u5413\u4e86\u4e00\u8df3\uff0c\u8d76\u7d27\u5475\u65a5\uff0c\u201c\u90a3\u4e9b\u90fd\u662f\u8c23\u8a00\uff0c\u600e\u4e48\u80fd\u4fe1\uff0c\u4f60\u4e0d\u8981\u547d\u4e86\uff1f\u201d</p><p>\u201c\u6211\u8bf4\u7684\u660e\u660e\u662f\u4e8b\u5b9e\uff01\u201d\u9752\u5e74\u4e0d\u670d\u6c14\u5730\u8bf4\uff0c\u201c\u5927\u5bb6\u90fd\u68a6\u89c1\u4e86\uff0c\u53c8\u4e0d\u662f\u6211\u4e00\u4e2a\u4eba\uff0c\u8001\u7238\u4f60\u4e0d\u662f\u4e5f\u68a6\u89c1\u4e86\u5417\uff0c\u8fd8\u6709\u5580\u6d41\u5976\u5976\u4e5f\u662f\uff0c\u8fd8\u6709\u2014\u2014\u201d</p><p>\u201c\u68a6\u91cc\u7684\u4e1c\u897f\u600e\u4e48\u80fd\u5f53\u771f\uff01\u800c\u4e14\u524d\u51e0\u5929\u5927\u796d\u53f8\u4e0d\u662f\u5df2\u7ecf\u8bf4\u4e86\u5417\uff0c\u90a3\u662f\u90aa\u9b54\u4f5c\u795f\uff0c\u4e0d\u80fd\u4fe1\u7684\u3002\u201d</p><p>\u9752\u5e74\u8fd8\u8981\u4e89\u8fa9\uff0c\u5374\u88ab\u7236\u4eb2\u77aa\u4e86\u56de\u53bb\uff0c\u4ed6\u4e0d\u60c5\u4e0d\u613f\u5730\u561f\u56d4\u4e86\u51e0\u53e5\uff0c\u6ca1\u6709\u518d\u8bf4\u8bdd\u3002\u201c\u56de\u53bb\u8ba9\u4f60\u5988\u5988\u505a\u665a\u996d\u9001\u8fc7\u6765\uff0c\u201d\u4e2d\u5e74\u7537\u5b50\u8bf4\uff0c\u201c\u53e6\u5916\u518d\u5e26\u4e24\u5e8a\u88ab\u5b50\u6765\uff0c\u591c\u91cc\u5f88\u51b7\u7684\u3002\u201d</p><p>\u9752\u5e74\u70b9\u70b9\u5934\uff0c\u51fa\u95e8\u53bb\u4e86\u3002\u623f\u95f4\u91cc\u4e00\u65f6\u95f4\u7a81\u7136\u5b89\u9759\u4e0b\u6765\uff0c\u8fc7\u4e86\u56db\u4e94\u5206\u949f\uff0c\u4e00\u4e2a\u80e1\u987b\u82b1\u767d\u7684\u8001\u5934\u5b50\u54b3\u55fd\u4e00\u58f0\uff0c\u6253\u7834\u4e86\u6c89\u5bc2\u3002\u201c\u5176\u5b9e\u5c0f\u7a46\u6cd5\u8bf4\u7684\u4e5f\u4e0d\u4e00\u5b9a\u9519\u3002\u201d</p><p>\u201c\u63d0\u5927\u53d4\uff0c\u4f60\u4e0d\u4f1a\u4e5f\u76f8\u4fe1\u90a3\u79cd\u4e1c\u897f\u5427\uff0c\u201d\u4e2d\u5e74\u7537\u5b50\u4f3c\u4e4e\u5f88\u7d27\u5f20\u7684\u6837\u5b50\uff0c\u201c\u795e\u6bbf\u5df2\u7ecf\u8bf4\u4e86\u90a3\u90fd\u662f\u5047\u7684\u3002\u201d</p><p>\u201c\u795e\u6bbf\u8bf4\u7684\u5c31\u4e00\u5b9a\u5bf9\u5417\uff1f\u201d</p><p>\u201c\u53ef\u662f\u2026\u2026\u201d</p><p>\u201c\u6211\u4eca\u5e74\u4e94\u5341\u4e5d\u5c81\uff0c\u4e5d\u5c81\u7684\u65f6\u5019\u5c31\u5165\u4e86\u6559\uff0c\u5df2\u7ecf\u6574\u6574\u4e94\u5341\u5e74\u5566\uff0c\u6559\u5b97\u90fd\u6362\u4e86\u4e09\u4ee3\u3002\u7167\u6211\u8bf4\uff0c\u73b0\u5728\u8fd9\u4e9b\u796d\u53f8\u4eec\uff0c\u53ef\u662f\u8fdc\u8fdc\u6bd4\u4e0d\u5f97\u4ee5\u524d\uff0c\u81ea\u4ece\u8fdb\u4e86\u57ce\uff0c\u4e00\u4e2a\u4e2a\u90fd\u2026\u2026\u201d\u4ed6\u62bf\u4e86\u62bf\u5634\uff0c\u201c\u53cd\u6b63\u5462\uff0c\u5982\u679c\u6211\u662f\u9f99\u738b\uff0c\u4e5f\u662f\u4e0d\u4f1a\u518d\u4fe1\u4efb\u4ed6\u4eec\u4e86\u3002\u201d</p><p>\u201c\u4f60\u662f\u89c9\u5f97\u90a3\u4e2a\u68a6\u662f\u771f\u7684\uff1f\u201d\u5580\u6d41\u5976\u5976\u95ee\u3002</p><p>\u201c\u6211\u53ef\u6ca1\u8fd9\u4e48\u8bf4\uff0c\u201d\u8001\u5934\u56de\u7b54\uff0c\u201c\u6211\u53ea\u662f\u8bf4\uff0c\u4e0d\u4e00\u5b9a\uff0c\u6709\u53ef\u80fd\u662f\u5047\u7684\uff0c\u4e5f\u6709\u53ef\u80fd\u662f\u771f\u7684\u3002\u201d</p><p>\u201c\u5982\u679c\u662f\u771f\u7684\uff0c\u90a3\u53ef\u5c31\u662f\u2026\u2026\u201d\u5580\u6d41\u5976\u5976\u987f\u4e86\u987f\uff0c\u201c\u8981\u51fa\u4e8b\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u7ba1\u662f\u771f\u662f\u5047\uff0c\u90fd\u80af\u5b9a\u8981\u51fa\u4e8b\uff0c\u201d\u8001\u5934\u70b9\u70b9\u5934\u8868\u793a\u8d5e\u540c\uff0c\u201c\u5149\u4e00\u4e2a\u2018\u5723\u5973\u2019\uff0c\u5c31\u5df2\u7ecf\u662f\u4f17\u77e2\u4e4b\u7684\u3002\u201d</p><p>\u201c\u7ba1\u4ed6\u5462\uff0c\u201d\u56f4\u7740\u6697\u9ec4\u8272\u56f4\u5dfe\u7684\u8001\u5987\u4eba\u8bf4\uff0c\u201c\u771f\u4e5f\u7f62\uff0c\u5047\u4e5f\u597d\uff0c\u9f99\u738b\u4e5f\u7f62\uff0c\u5723\u5973\u4e5f\u597d\uff0c\u53cd\u6b63\u90fd\u662f\u5317\u8fb9\u7684\u4e8b\u60c5\uff0c\u548c\u6211\u4eec\u8fd9\u4e9b\u8001\u5bb6\u4f19\u53c8\u6ca1\u4ec0\u4e48\u5173\u7cfb\u3002\u5580\u6d41\u4f60\u4e13\u5fc3\u517b\u4f24\u5c31\u597d\u4e86\uff0c\u53cd\u6b63\u5bb6\u91cc\u53c8\u6ca1\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6b63\u597d\u5728\u8fd9\u91cc\u591a\u6b47\u606f\u51e0\u5929\uff0c\u7b49\u8fc7\u4e86\u85cf\u98ce\u8282\u518d\u56de\u53bb\u3002\u201d</p><p>\u201c\u90a3\u53ef\u4e0d\u884c\uff0c\u5c0f\u51db\u56de\u6765\u4e86\uff0c\u8bf4\u8fd9\u51e0\u5929\u8981\u6765\u770b\u6211\uff0c\u8bf4\u4e0d\u5b9a\u5c0f\u6885\u4e5f\u8981\u6765\uff0c\u6211\u5f97\u56de\u5bb6\u53bb\u3002\u201d</p><p>\u201c\u4f60\u73b0\u5728\u56de\u5bb6\u4e5f\u53ea\u80fd\u8eba\u5728\u5e8a\u4e0a\u554a\u3002\u201d</p><p>\u201c\u5979\u4eec\u4e24\u4e2a\u56de\u6765\u4e86\uff1f\u4ec0\u4e48\u65f6\u5019\uff1f\u201d\u9152\u7cdf\u9f3b\u5b50\u7684\u4e2d\u5e74\u4eba\u95ee\uff0c\u201c\u8fd9\u90fd\u6709\u516d\u4e03\u5e74\u6ca1\u89c1\u4e86\u5427\u3002\u201d</p><p>\u201c\u5dee\u4e0d\u591a\u5feb\u4e03\u5e74\u4e86\uff0c\u5c0f\u51db\u73b0\u5728\u90fd\u53d8\u6210\u5927\u59d1\u5a18\u4e86\uff0c\u8fd8\u5e26\u7740\u7537\u53cb\uff0c\u770b\u6837\u5b50\u611f\u60c5\u5f88\u597d\uff0c\u201d\u5580\u6d41\u5976\u5976\u8bf4\uff0c\u201c\u542c\u8bf4\u5c0f\u6885\u4e5f\u6709\u7537\u53cb\u4e86\uff0c\u4f60\u5bb6\u7684\u5c0f\u7a46\u6cd5\u662f\u6ca1\u673a\u4f1a\u4e86\u3002\u201d</p><p>\u4e2d\u5e74\u7537\u5b50\u5c34\u5c2c\u5730\u7b11\u4e86\u7b11\uff1a\u201c\u4ee5\u524d\u662f\u5f00\u73a9\u7b11\u8bf4\u8bf4\u800c\u5df2\uff0c\u90a3\u5bb6\u4f19\u600e\u4e48\u914d\u5f97\u4e0a\u5c0f\u51db\uff0c\u672c\u6765\u5c31\u6ca1\u6307\u671b\u7684\u3002\u201d</p><p>\u201c\u5c0f\u51db\u548c\u5c0f\u6885\u90fd\u8981\u5ac1\u4eba\u4e86\uff1f\u65f6\u95f4\u8fc7\u5f97\u8fd8\u771f\u662f\u5feb\u554a\uff0c\u201d\u8001\u5934\u611f\u53f9\uff0c\u201c\u6211\u8fd8\u8bb0\u5f97\u7b2c\u4e00\u6b21\u89c1\u5230\u5979\u4eec\u7684\u65f6\u5019\uff0c\u662f\u5728\u4f60\u5bb6\u9662\u5b50\u91cc\uff0c\u5f53\u65f6\u5979\u4eec\u548c\u4f60\u5b59\u5b50\u4e00\u8d77\u73a9\u2014\u2014\u201d</p><p>\u4ed6\u5ffd\u7136\u987f\u4f4f\u4e86\uff0c\u610f\u8bc6\u5230\u8bf4\u9519\u4e86\u8bdd\uff0c\u5580\u6d41\u5976\u5976\u5012\u662f\u4e0d\u592a\u5728\u610f\uff0c\u201c\u6211\u5b59\u5b50\u7684\u4e8b\u662f\u4ed6\u81ea\u5df1\u72af\u7cca\u6d82\uff0c\u53c8\u4e0d\u80fd\u602a\u5c0f\u6885\uff0c\u5c31\u4ed6\u90a3\u4e2a\u813e\u6027\uff0c\u65e9\u665a\u8ddf\u4ed6\u7236\u4eb2\u4e00\u6837\u6b7b\u5728\u6218\u573a\u4e0a\u3002\u201d</p><p>\u201c\u597d\u4e86\u597d\u4e86\uff0c\u6211\u4eec\u5148\u56de\u53bb\u5427\uff0c\u8ba9\u5979\u4e00\u4e2a\u4eba\u5b89\u9759\u4f11\u606f\uff0c\u201d\u8001\u5934\u8bf4\uff0c\u201c\u5c31\u7b97\u4f60\u8981\u56de\u5bb6\uff0c\u90a3\u4e5f\u662f\u660e\u5929\u518d\u8bf4\uff0c\u4eca\u5929\u80af\u5b9a\u662f\u4e0d\u65b9\u4fbf\u52a8\u4e86\u3002\u6211\u53eb\u591a\u5c3c\u5e2e\u4f60\u770b\u7740\u5bb6\uff0c\u5982\u679c\u5c0f\u51db\u6765\u4e86\uff0c\u5c31\u8ddf\u5979\u8bf4\u4f60\u5728\u8fd9\u91cc\u3002\u201d</p><p>\u5f53\u6240\u6709\u4eba\u90fd\u79bb\u5f00\u540e\uff0c\u51db\u4f4e\u58f0\u8ddf\u743c\u6069\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u89e3\u9664\u9690\u5f62\u9b54\u6cd5\uff0c\u6572\u4e86\u6572\u95e8\uff0c\u7136\u540e\u8d70\u8fdb\u53bb\u3002\u5580\u6d41\u5976\u5976\u6b63\u6253\u7b97\u4f11\u606f\uff0c\u770b\u89c1\u51db\u548c\u743c\u6069\uff0c\u5012\u662f\u51fa\u4e4e\u610f\u6599\uff0c\u201c\u4f60\u4eec\u600e\u4e48\u6765\u4e86\u3002\u201d</p><p>\u201c\u9047\u5230\u4e86\u591a\u5c3c\u5927\u53d4\uff0c\u542c\u4ed6\u8bf4\u4f60\u5728\u8fd9\u91cc\uff0c\u201d\u51db\u5728\u5e8a\u8fb9\u5750\u4e0b\u6765\uff0c\u201c\u6454\u5f97\u5f88\u4e25\u91cd\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u5927\u788d\uff0c\u4e00\u70b9\u9aa8\u6298\u800c\u5df2\uff0c\u8eba\u51e0\u5929\u5c31\u597d\u4e86\u3002\u201d\u5580\u6d41\u5976\u5976\u8bf4\uff0c\u5979\u62ac\u4e86\u62ac\u53f3\u81c2\uff0c\u4f3c\u4e4e\u60f3\u5411\u51db\u8bc1\u660e\u201c\u6ca1\u4ec0\u4e48\u5927\u788d\u201d\uff0c\u5374\u662f\u4e0d\u5c0f\u5fc3\u89e6\u52a8\u4e86\u4ec0\u4e48\uff0c\u8138\u8272\u987f\u65f6\u4e00\u53d8\uff0c\u989d\u5934\u4e0a\u8fde\u6c57\u73e0\u90fd\u6c81\u51fa\u6765\u3002\u51db\u8d76\u5fd9\u5c06\u5979\u7684\u624b\u81c2\u6258\u4f4f\uff0c\u6162\u6162\u653e\u56de\u5e8a\u4e0a\uff0c\u201c\u522b\u4e71\u52a8\uff0c\u201d\u5979\u8d23\u5907\uff0c\u201c\u600e\u4e48\u796d\u53f8\u8fd8\u4e0d\u6765\u7ed9\u4f60\u6cbb\u7597\u3002\u201d</p><p>\u201c\u796d\u53f8\u90fd\u56de\u5317\u8fb9\u4e86\uff0c\u201d\u5580\u6d41\u5976\u5976\u8bf4\uff0c\u201c\u6211\u51fa\u4e8b\u5f97\u4e0d\u5de7\uff0c\u4ed6\u4eec\u4eca\u5929\u4e0a\u5348\u8d70\u7684\uff0c\u8fd8\u4e0d\u77e5\u9053\u4ec0\u4e48\u65f6\u5019\u80fd\u56de\u6765\u5462\u3002\u201d</p><p>\u201c\u771f\u662f\u4e0d\u8d1f\u8d23\u4efb\uff0c\u201d\u51db\u62b1\u6028\uff0c\u201c\u4e0d\u8fc7\u6ca1\u5173\u7cfb\uff0c\u796d\u53f8\u800c\u5df2\uff0c\u6211\u4e5f\u5e26\u4e86\u4e00\u4e2a\u6765\uff0c\u800c\u4e14\u6bd4\u4ed6\u4eec\u9ad8\u660e\u591a\u4e86\u3002\u201d</p><p>\u201c\u4f60\u7537\u53cb\u662f\u796d\u53f8\uff1f\u201d\u5580\u6d41\u5976\u5976\u7741\u5927\u4e86\u773c\u775b\uff0c\u201c\u6628\u5929\u6ca1\u542c\u4f60\u8bf4\u2014\u2014\u800c\u4e14\u4ed6\u600e\u4e48\u4e0d\u7a7f\u7ea2\u888d\uff1f\u201d</p><p>\u201c\u4ed6\u4e0d\u662f\uff0c\u4e0d\u8fc7\u4ed6\u6709\u4e2a\u4eb2\u621a\u662f\u796d\u53f8\uff0c\u5f88\u5389\u5bb3\u7684\u3002\u201d</p><p>\u743c\u6069\u542c\u4e0d\u61c2\u51db\u5728\u8bf4\u4ec0\u4e48\uff0c\u4e0d\u8fc7\u4ed6\u77e5\u9053\u8be5\u81ea\u5df1\u4e0a\u573a\u4e86\u3002</p><p>\u4ed6\u5f80\u540e\u62db\u4e86\u62db\u624b\uff0c\u4e00\u4f4d\u94f6\u53d1\u5973\u5b50\u4ece\u95e8\u5916\u8d70\u8fc7\u6765\uff0c\u5979\u7a7f\u7740\u94f6\u7070\u8272\u3001\u7ee3\u7740\u86db\u7f51\u82b1\u7eb9\u7684\u957f\u888d\uff0c\u808c\u80a4\u5b9b\u5982\u58a8\u7389\uff0c\u73b2\u73d1\u6d6e\u51f8\uff0c\u8eab\u6750\u706b\u8fa3\u5f02\u5e38\u3002\u5580\u6d41\u5976\u5976\u793c\u8c8c\u6027\u5730\u671d\u5979\u7b11\u4e86\u7b11\uff0c\u94f6\u53d1\u5973\u5b50\u5374\u9762\u65e0\u8868\u60c5\uff0c\u4e5f\u4e0d\u8bf4\u8bdd\uff0c\u5f84\u76f4\u8d70\u5230\u5e8a\u8fb9\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u679a\u8718\u86db\u5f62\u72b6\u7684\u5fbd\u7ae0\uff0c\u653e\u5728\u5580\u6d41\u5976\u5976\u7684\u80f8\u53e3\uff0c\u8f7b\u58f0\u5ff5\u8bf5\u5492\u8bed\u3002</p><p>\u67d4\u548c\u7684\u767d\u5149\u4ece\u8718\u86db\u5fbd\u7ae0\u4e0a\u6563\u53d1\u51fa\u6765\uff0c\u8986\u76d6\u7740\u53d7\u4f24\u7684\u90e8\u4f4d\uff0c\u50cf\u6c34\u4e00\u6837\u6162\u6162\u6e17\u900f\u8fdb\u53bb\u3002\u51e0\u5206\u949f\u4e4b\u540e\uff0c\u5979\u6536\u56de\u5fbd\u7ae0\uff0c\u9000\u5230\u4e00\u65c1\u3002\u201c\u771f\u7684\u4e0d\u75bc\u4e86\uff0c\u5c0f\u51db\u3002\u201d\u5580\u6d41\u5976\u5976\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u52a8\u4e86\u52a8\u624b\u81c2\uff0c\u60ca\u559c\u5730\u8bf4\u3002</p><p>\u201c\u90a3\u5f53\u7136\uff0c\u201d\u51db\u5f97\u610f\u6d0b\u6d0b\u5730\u8bf4\uff0c\u201c\u6211\u8bf4\u4e86\u5979\u5f88\u5389\u5bb3\u7684\u3002\u201d</p><p>\u9aa8\u6298\u8fd9\u79cd\u53d7\u4f24\uff0c\u5373\u4fbf\u6709\u795e\u672f\u6cbb\u7597\uff0c\u4e5f\u5f88\u96be\u7acb\u523b\u75ca\u6108\uff0c\u4ecd\u7136\u9700\u8981\u4f11\u517b\u4e00\u6bb5\u65f6\u95f4\u3002\u51db\u966a\u7740\u5580\u6d41\u5976\u5976\u804a\u5929\uff0c\u743c\u6069\u538b\u6839\u542c\u4e0d\u61c2\uff0c\u4e5f\u6ca1\u529e\u6cd5\u53c2\u4e0e\uff0c\u5728\u65c1\u8fb9\u5f85\u7740\u65e0\u804a\uff0c\u7d22\u6027\u81ea\u5df1\u51fa\u53bb\u6e9c\u8fbe\u6e9c\u8fbe\u3002</p><p>\u5e26\u7740\u94f6\u53d1\u5973\u5b50\u8d70\u5230\u5916\u9762\uff0c\u7136\u540e\u5c06\u5b83\u53d8\u56de\u68cb\u5b50\u6536\u8d77\u3002\u4ed6\u5f53\u7136\u6ca1\u6709\u4ec0\u4e48\u796d\u53f8\u4eb2\u621a\uff0c\u53ea\u662f\u6b27\u51ef\u9001\u7ed9\u4ed6\u7684\u90a3\u526f\u8428\u74e6\u68cb\u9b54\u50cf\u800c\u5df2\uff0c\u91cc\u9762\u6709\u56db\u679a\u201c\u86db\u540e\u7267\u5e08\u201d\u3002\u6885\u83f2\u65af\u8bf4\u8fc7\uff0c\u4e1c\u57df\u7981\u6b62\u8bf8\u795e\u7684\u795e\u529b\u4ecb\u5165\uff0c\u743c\u6069\u4e0d\u77e5\u9053\u8fd9\u4e2a\u9650\u5236\u5bf9\u7f57\u4e1d\u662f\u5426\u751f\u6548\uff0c\u4f46\u65e0\u8bba\u662f\u4e0e\u4e0d\u662f\u90fd\u6ca1\u4ec0\u4e48\u5f71\u54cd\uff0c\u56e0\u4e3a\u7267\u5e08\u9b54\u50cf\u6240\u65bd\u5c55\u7684\u201c\u795e\u672f\u201d\uff0c\u6839\u672c\u5c31\u4e0d\u662f\u771f\u7684\u6765\u81ea\u86db\u540e\uff0c\u6216\u8005\u5176\u4ed6\u795e\u660e\uff0c\u800c\u662f\u6df1\u6e0a\u5927\u6076\u9b54\u8fea\u9b54\u9ad8\u6839\u3002</p><p>\u81f3\u4e8e\u4e3a\u4ec0\u4e48\u8981\u4f2a\u88c5\u4e00\u4e0b\uff0c\u5219\u662f\u51db\u7684\u8981\u6c42\uff0c\u6309\u7167\u5979\u7684\u8bf4\u6cd5\uff0c\u5728\u8f9b\u5df4\u57ce\u91cc\uff0c\u7edd\u5bf9\u4e0d\u8981\u516c\u5f00\u5e26\u7740\u9b54\u50cf\u3001\u5080\u5121\u4e4b\u7c7b\u7684\u4e1c\u897f\u5230\u5904\u8dd1\uff0c\u56e0\u4e3a\u8fd9\u88ab\u89c6\u4e3a\u201c\u90aa\u9b54\u201d\u7684\u6807\u51c6\u7279\u5f81\u4e4b\u4e00\uff0c\u4f1a\u88ab\u7fa4\u8d77\u800c\u653b\u4e4b\u7684\u3002</p><p>\u5c06\u5973\u796d\u53f8\u9b54\u50cf\u53d8\u56de\u68cb\u5b50\u6536\u8d77\uff0c\u743c\u6069\u5728\u201c\u9f99\u5bab\u201d\u91cc\u4fe1\u6b65\u6e38\u8d70\uff0c\u504c\u5927\u7684\u4e00\u5ea7\u5efa\u7b51\uff0c\u91cc\u9762\u5c45\u7136\u4e00\u4e2a\u4eba\u90fd\u6ca1\u6709\uff0c\u5b89\u9759\u5f97\u6709\u4e9b\u6e17\u4eba\u3002\u4ed6\u5012\u662f\u4e0d\u5728\u610f\uff0c\u8d70\u4e86\u4e00\u4f1a\uff0c\u7a7f\u8fc7\u4e00\u4e2a\u957f\u957f\u7684\u8d70\u5eca\uff0c\u8fdb\u5165\u4e00\u4e2a\u5706\u5f62\u5927\u5385\uff0c\u770b\u8d77\u6765\u50cf\u662f\u4f1a\u8bae\u5ba4\uff0c\u6b63\u4e2d\u95f4\u6446\u653e\u4e86\u4e00\u5f20\u65b9\u5f62\u957f\u684c\uff0c\u5468\u56f4\u662f\u4e00\u5708\u6905\u5b50\uff0c\u9664\u6b64\u4e4b\u5916\u4e5f\u6ca1\u4ec0\u4e48\u5176\u4ed6\u4e1c\u897f\u3002\u743c\u6069\u626b\u89c6\u4e00\u5708\uff0c\u6b63\u8981\u79bb\u5f00\uff0c\u5ffd\u7136\u773c\u89d2\u4f59\u5149\u77a5\u89c1\u5899\u58c1\u4e0a\u4f3c\u4e4e\u6709\u4ec0\u4e48\u4e1c\u897f\u3002</p><p>\u4ed6\u597d\u5947\u5fc3\u8d77\uff0c\u8d70\u4e0a\u524d\u7ec6\u770b\uff0c\u53d1\u73b0\u662f\u4e00\u6392\u58c1\u753b\uff0c\u6ca1\u6709\u7528\u989c\u6599\uff0c\u53ea\u662f\u7ebf\u6761\u52fe\u52d2\u7d20\u63cf\uff0c\u4f46\u7b14\u6cd5\u5341\u5206\u9ad8\u660e\uff0c\u6829\u6829\u5982\u771f\u3002\u743c\u6069\u9762\u524d\u7684\u8fd9\u4e00\u5e45\uff0c\u753b\u7684\u662f\u4e00\u6761\u6cb3\uff0c\u6cb3\u4e0a\u6709\u4e00\u6761\u8239\uff0c\u4e24\u4e2a\u5934\u6234\u738b\u51a0\u7684\u7537\u5b50\u76f8\u5bf9\u5750\u5728\u8239\u4e0a\uff0c\u50cf\u662f\u5728\u4e3e\u676f\u5171\u996e\uff0c\u800c\u5728\u6cb3\u7684\u4e24\u5cb8\uff0c\u5206\u522b\u9648\u5217\u7740\u4e00\u652f\u519b\u961f\uff0c\u8863\u7532\u9c9c\u660e\uff0c\u5200\u67aa\u5982\u6797\uff0c\u6740\u6c14\u817e\u817e\u6251\u9762\u800c\u6765\u3002</p><p>\u743c\u6069\u770b\u4e0d\u660e\u767d\uff0c\u4e8e\u662f\u518d\u53bb\u770b\u5176\u4ed6\u7684\uff0c\u53c8\u770b\u4e86\u51e0\u5e45\uff0c\u5012\u662f\u6e10\u6e10\u660e\u767d\u8fc7\u6765\uff0c\u8fd9\u5e94\u8be5\u5c31\u662f\u201c\u7ea2\u9f99\u738b\u201d\u5bdf\u65af\u8428\u7edf\u4e00\u5f7b\u68ee\u5854\u7684\u7ecf\u5386\uff0c\u753b\u7684\u5185\u5bb9\u8bb0\u5f55\u4e86\u4ed6\u5728\u8f9b\u5df4\u57ce\u8d77\u5bb6\uff0c\u805a\u96c6\u519b\u961f\uff0c\u4e00\u8def\u5f81\u6218\uff0c\u6700\u540e\u52a0\u5195\u4e3a\u738b\uff0c\u5012\u6570\u7b2c\u4e09\u5e45\u5c31\u662f\u4ed6\u9a71\u4f7f\u6c11\u592b\u5efa\u7b51\u9752\u94dc\u5927\u706f\u5854\u7684\u573a\u666f\uff0c\u6700\u540e\u4e00\u5e45\u5219\u662f\u4ed6\u5728\u6218\u573a\u4e0a\u5316\u8eab\u4e3a\u9f99\uff0c\u817e\u7a7a\u98de\u5929\uff0c\u4ece\u6b64\u6d88\u5931\u3002\u743c\u6069\u53c8\u4ece\u5934\u770b\u4e86\u4e00\u904d\uff0c\u8d8a\u770b\u8d8a\u89c9\u5f97\u6709\u610f\u601d\uff0c\u7d22\u6027\u7528\u6cd5\u672f\u5c06\u6240\u6709\u56fe\u753b\u90fd\u590d\u5236\u5230\u4e00\u5f20\u7a7a\u767d\u7684\u7eb8\u4e0a\uff0c\u4e22\u8fdb\u6b21\u5143\u888b\u91cc\uff0c\u6253\u7b97\u56de\u53bb\u4ed4\u7ec6\u7814\u7a76\u3002</p><p>\u53c8\u8d70\u4e86\u4e00\u4f1a\uff0c\u6ca1\u6709\u770b\u5230\u4ec0\u4e48\u503c\u5f97\u6ce8\u610f\u7684\u4e1c\u897f\uff0c\u4ed6\u4fbf\u539f\u8def\u8fd4\u56de\u3002</p><p>\u7a7f\u8fc7\u6765\u65f6\u7684\u90a3\u6761\u957f\u5eca\uff0c\u743c\u6069\u5ffd\u7136\u5fc3\u751f\u8b66\u5146\uff0c\u4ed6\u8138\u4e0a\u4e0d\u52a8\u58f0\u8272\uff0c\u53f3\u624b\u85cf\u5728\u8896\u4e2d\u6084\u6084\u5212\u4e86\u4e2a\u7b26\u6587\uff0c\u731b\u7136\u4e94\u6307\u4e00\u63e1\uff0c\u731b\u5730\u671d\u4fa7\u540e\u65b9\u7838\u4e86\u8fc7\u53bb\u3002</p><p>\u6ca1\u6709\u52a8\u9759\u3002</p><p>\u4ed6\u7684\u6cd5\u672f\u6ca1\u6709\u78b0\u5230\u4efb\u4f55\u654c\u4eba\uff0c\u4e5f\u6ca1\u6709\u906d\u9047\u4efb\u4f55\u53cd\u6297\u2014\u2014\u4f46\u540c\u65f6\u4e5f\u6ca1\u6709\u4ea7\u751f\u4efb\u4f55\u6548\u679c\uff0c\u5b83\u5c31\u50cf\u662f\u4e00\u5757\u77f3\u5b50\u6254\u8fdb\u4e86\u65e0\u5e95\u7684\u6df1\u6e0a\uff0c\u8fde\u56de\u58f0\u90fd\u6ca1\u6709\u4f20\u51fa\u6765\u3002\u743c\u6069\u9759\u9759\u5730\u7ad9\u5728\u539f\u5730\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u4e00\u4e2a\u7070\u767d\u8272\u7684\u5f71\u5b50\u81ea\u9ed1\u6697\u4e2d\u7f13\u7f13\u6d6e\u73b0\u51fa\u6765\u3002</p><p>\u201c\u79bb\u5f00\u8fd9\u91cc\uff0c\u201d\u5f71\u5b50\u8bf4\uff0c\u58f0\u97f3\u7a7a\u6d1e\uff0c\u6beb\u65e0\u611f\u60c5\uff0c\u201c\u9a6c\u4e0a\u3002\u201d</p><p>\u201c\u4f60\u662f\u8c01\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u79bb\u5f00\u8fd9\u91cc\u3002\u201d\u5f71\u5b50\u91cd\u590d\u3002</p><p>\u743c\u6069\u76b1\u7709\uff0c\u6ca1\u6709\u56de\u7b54\uff0c\u4e5f\u6ca1\u6709\u79fb\u52a8\u3002\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5f71\u5b50\u5ffd\u7136\u81ea\u5df1\u6d88\u5931\u4e86\u3002</p><p>\u743c\u6069\u601d\u7d22\u4e86\u4e00\u4f1a\uff0c\u56de\u5230\u5580\u6d41\u5976\u5976\u7684\u623f\u95f4\uff0c\u53d1\u73b0\u591a\u4e86\u4e00\u4e2a\u4eba\uff0c\u5c31\u662f\u4e4b\u524d\u88ab\u7236\u4eb2\u8d76\u56de\u53bb\u62ff\u665a\u9910\u548c\u88ab\u5b50\u7684\u90a3\u4e2a\u9752\u5e74\uff0c\u6b63\u5728\u548c\u51db\u8bf4\u8bdd\uff0c\u5580\u6d41\u5976\u5976\u5728\u5403\u665a\u996d\u3002</p><p>\u743c\u6069\u7528\u534a\u751f\u4e0d\u719f\u7684\u6069\u745f\u8bed\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u9752\u5e74\u70b9\u5934\u81f4\u610f\uff0c\u4f46\u8868\u60c5\u51b7\u51b7\u6de1\u6de1\u7684\uff0c\u663e\u7136\u4e5f\u65e0\u610f\u4ea4\u8c08\u3002\u201c\u90a3\u6211\u5148\u8d70\u4e86\u3002\u201d\u4ed6\u5bf9\u51db\u8bf4\uff0c\u8bf4\u7684\u5012\u662f\u4e2d\u571f\u901a\u7528\u8bed\uff0c\u53d1\u97f3\u8fd8\u9887\u4e3a\u6807\u51c6\u3002</p><p>\u201c\u633a\u665a\u4e86\uff0c\u4f60\u4eec\u4fe9\u4e5f\u65e9\u70b9\u56de\u53bb\u5427\u3002\u201d\u7b49\u9752\u5e74\u8d70\u540e\uff0c\u5580\u6d41\u5976\u5976\u5403\u5b8c\u996d\uff0c\u5bf9\u743c\u6069\u548c\u51db\u8bf4\u3002</p><p>\u201c\u4f60\u73b0\u5728\u884c\u52a8\u4e0d\u4fbf\uff0c\u6ca1\u4eba\u7167\u987e\u53ef\u4e0d\u884c\u3002\u201d</p><p>\u201c\u8fd9\u91cc\u665a\u4e0a\u90fd\u6709\u4eba\u503c\u73ed\u7684\uff0c\u4e0d\u7528\u62c5\u5fc3\uff0c\u201d\u5580\u6d41\u5976\u5976\u50ac\u4fc3\uff0c\u201c\u4f60\u4eec\u8d76\u5feb\u56de\u53bb\u5427\u3002\u201d</p><p>\u51db\u60f3\u4e86\u4f1a\uff0c\u201c\u90a3\u597d\u5427\uff0c\u660e\u5929\u6211\u4eec\u518d\u6765\u770b\u4f60\u3002\u201d</p><p>\u4f9d\u65e7\u662f\u7528\u9690\u5f62\u6cd5\u672f\u79bb\u5f00\u9f99\u5bab\uff0c\u8d70\u5728\u56de\u53bb\u7684\u8def\u4e0a\uff0c\u51db\u5927\u5927\u5730\u677e\u4e86\u53e3\u6c14\uff0c\u201c\u4f60\u8dd1\u54ea\u53bb\u4e86\uff0c\u201d\u5979\u62b1\u6028\uff0c\u201c\u534a\u5929\u90fd\u4e0d\u56de\u6765\uff0c\u6211\u90fd\u5feb\u8981\u88ab\u90a3\u5bb6\u4f19\u70e6\u6b7b\u4e86\u3002\u201d</p><p>\u51db\u53e3\u4e2d\u7684\u201c\u90a3\u5bb6\u4f19\u201d\uff0c\u663e\u7136\u662f\u6307\u521a\u624d\u90a3\u4f4d\u9752\u5e74\uff0c\u4ed6\u5bf9\u51db\u6709\u610f\u601d\uff0c\u8fd9\u70b9\u786e\u5b9a\u65e0\u7591\u2014\u2014\u5728\u79bb\u5f00\u7684\u65f6\u5019\uff0c\u4ed6\u8fd8\u72e0\u72e0\u77aa\u4e86\u743c\u6069\u51e0\u773c\u3002\u53ef\u60dc\u51db\u770b\u8d77\u6765\u5bf9\u4ed6\u6ca1\u4ec0\u4e48\u597d\u611f\u3002</p><p>\u201c\u6211\u53bb\u540e\u9762\u901b\u4e86\u901b\u3002\u201d\u743c\u6069\u968f\u53e3\u8bf4\u3002</p><p>\u201c\u540e\u9762\u90fd\u662f\u4e9b\u7a7a\u623f\u5b50\uff0c\u6709\u4ec0\u4e48\u597d\u901b\u7684\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u88ab\u5973\u9b3c\u6293\u8d70\u4e86\u5462\u3002\u201d</p><p>\u201c\u5973\u9b3c\uff1f\u201d</p><p>\u201c\u9f99\u5bab\u91cc\u7ecf\u5e38\u95f9\u9b3c\uff0c\u54e6\uff0c\u4e5f\u4e0d\u662f\u7ecf\u5e38\uff0c\u5076\u5c14\u95f9\u9b3c\uff0c\u8981\u4e0d\u7136\u600e\u4e48\u51e0\u767e\u5e74\u4e00\u76f4\u7a7a\u7740\u6ca1\u4eba\u4f4f\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u636e\u8bf4\u662f\u5f53\u5e74\u7ea2\u9f99\u738b\u6536\u96c6\u4e86\u5f88\u591a\u7f8e\u5973\u5145\u5b9e\u540e\u5bab\uff0c\u7ed3\u679c\u4ed6\u7a81\u7136\u5931\u8e2a\u4e86\uff0c\u90a3\u4e9b\u7f8e\u5973\u5168\u90fd\u6b89\u60c5\u81ea\u6740\u3002\u6b7b\u540e\u9634\u9b42\u4e0d\u6563\uff0c\u4f9d\u7136\u7559\u5728\u90a3\u4e9b\u623f\u5b50\u91cc\uff0c\u5982\u679c\u53d1\u73b0\u6709\u5b64\u8eab\u4e00\u4eba\u7684\u7537\u6027\uff0c\u5979\u4eec\u5c31\u4f1a\u5192\u51fa\u6765\uff0c\u628a\u4eba\u5438\u5f97\u7cbe\u5c3d\u4eba\u4ea1\u3002\u66fe\u7ecf\u6709\u5c0f\u5077\u6e9c\u8fdb\u53bb\u60f3\u627e\u70b9\u5b9d\u85cf\uff0c\u7ed3\u679c\u5c31\u88ab\u5973\u9b3c\u7f20\u4f4f\uff0c\u7b49\u88ab\u53d1\u73b0\u7684\u65f6\u5019\u5df2\u7ecf\u53d8\u6210\u4e86\u5e72\u5c38\u3002\u201d</p><p>\u4f60\u8fd9\u8bb2\u6545\u4e8b\u7684\u6c34\u5e73\u4e0d\u884c\u554a\uff0c\u660e\u660e\u662f\u633a\u597d\u7684\u6050\u6016\u5c0f\u8bf4\u8bbe\u5b9a\uff0c\u88ab\u4f60\u8fd9\u79cd\u8f7b\u677e\u7684\u8bed\u6c14\u8bf4\u51fa\u6765\uff0c\u4e00\u70b9\u611f\u89c9\u90fd\u6ca1\u6709\u4e86\u3002</p><p>\u4f1a\u628a\u4eba\u5438\u5f97\u7cbe\u5c3d\u4eba\u4ea1\u7684\u6f02\u4eae\u5973\u9b3c\u4ec0\u4e48\u7684\uff0c\u743c\u6069\u662f\u4e00\u4e2a\u90fd\u6ca1\u78b0\u89c1\uff0c\u5012\u662f\u6709\u4e2a\u9ed1\u5f71\uff0c\u4f3c\u4e4e\u6709\u70b9\u6cbe\u8fb9\uff0c\u7136\u800c\u90a3\u4e1c\u897f\u770b\u8d77\u6765\u5e76\u4e0d\u50cf\u5973\u9b3c\uff0c\u81f3\u5c11\u770b\u4e0d\u51fa\u660e\u663e\u7684\u6027\u522b\uff0c\u800c\u4e14\u5b83\u53ea\u662f\u8ba9\u743c\u6069\u6eda\u86cb\uff0c\u4e5f\u6ca1\u6709\u6251\u4e0a\u6765\u6c42\u6b22\uff0c\u6240\u4ee5\u5e94\u8be5\u4e0d\u662f\u3002</p><p>\u201c\u90a3\u4f60\u600e\u4e48\u4e0d\u65e9\u63d0\u9192\u6211\uff0c\u201d\u743c\u6069\u5f00\u73a9\u7b11\uff0c\u201c\u4e07\u4e00\u6211\u771f\u7684\u9047\u4e0a\u5973\u9b3c\u4e86\u600e\u4e48\u529e\uff1f\u201d</p><p>\u201c\u771f\u9047\u4e0a\u4e86\u4e5f\u6ca1\u4e8b\u554a\uff0c\u4f60\u8fd9\u4e48\u597d\u8272\uff0c\u89c1\u4e00\u4e2a\u4e0a\u4e00\u4e2a\uff0c\u795e\u5b50\u9f99\u5973\u90aa\u9b54\u9009\u6c11\u90fd\u641e\u5b9a\u4e86\uff0c\u533a\u533a\u5973\u9b3c\u4ec0\u4e48\u7684\uff0c\u5b8c\u5168\u4e0d\u5728\u8bdd\u4e0b\u561b\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u8fd9\u4e48\u5938\u5956\u6211\u4f1a\u4e0d\u597d\u610f\u601d\u7684\u3002\u201d</p><p>\u201c\u4f60\u54ea\u6709\u4e0d\u597d\u610f\u601d\uff0c\u6211\u600e\u4e48\u6ca1\u770b\u51fa\u6765\u3002\u201d</p><p>\u201c\u6709\u7684\uff0c\u56e0\u4e3a\u6211\u5176\u5b9e\u5185\u5fc3\u662f\u4e2a\u5f88\u4e13\u4e00\u7684\u4eba\u3002\u201d</p><p>\u201c\u4f60\u4e13\u4e00\u4e2a\u9b3c\uff0c\u201d\u51db\u6487\u6487\u5634\uff0c\u201c\u8fde\u5580\u6d41\u5976\u5976\u90fd\u770b\u51fa\u4f60\u5c31\u662f\u4e2a\u82b1\u5fc3\u6ee5\u60c5\u7684\u5bb6\u4f19\uff0c\u8fd8\u7279\u522b\u63d0\u9192\u6211\u8981\u5c0f\u5fc3\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u5927\u60ca\uff0c\u201c\u8fd9\u4e2a\u4ece\u4f55\u8bf4\u8d77\uff1f\u201d</p><p>\u201c\u4f60\u8d70\u4e86\u4ee5\u540e\uff0c\u5979\u5c31\u95ee\u6211\uff0c\u521a\u624d\u90a3\u4e2a\u9ed1\u76ae\u80a4\u7684\u5973\u4eba\u662f\u4f60\u4ec0\u4e48\u4eba\uff0c\u6211\u8bf4\u662f\u4f60\u8868\u59b9\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u8868\u59b9\u600e\u4e48\u53ef\u80fd\u662f\u4e2a\u9ed1\u6697\u7cbe\u7075\uff1f\u201d</p><p>\u201c\u5580\u6d41\u5976\u5976\u53c8\u4e0d\u8ba4\u8bc6\u9ed1\u6697\u7cbe\u7075\uff0c\u5979\u53ea\u4ee5\u4e3a\u662f\u76ae\u80a4\u7a0d\u5fae\u504f\u9ed1\u4e86\u4e00\u70b9\u800c\u5df2\u3002\u201d</p><p>\u90a3\u4e0d\u662f\u201c\u7a0d\u5fae\u504f\u9ed1\u4e86\u4e00\u70b9\u201d\u5427\u2026\u2026</p><p>\u201c\u4eba\u7c7b\u7684\u8033\u6735\u4e5f\u6ca1\u90a3\u4e48\u5c16\u957f\u554a\u3002\u201d</p><p>\u201c\u5149\u7ebf\u53c8\u4e0d\u597d\uff0c\u8c01\u5728\u610f\u8fd9\u79cd\u7ec6\u8282\u3002\u201d</p><p>\u201c\u5979\u770b\u8d77\u6765\u660e\u663e\u6bd4\u6211\u5e74\u9f84\u5927\uff0c\u600e\u4e48\u4e5f\u4e0d\u53ef\u80fd\u662f\u6211\u8868\u59b9\u5427\uff1f\u201d</p><p>\u201c\u4f60\u8f88\u5206\u9ad8\u561b\u3002\u201d</p><p>\u2026\u2026\u8868\u5144\u59b9\u4e5f\u6709\u8f88\u5206\u9ad8\u4f4e\u5417\uff1f</p><p>\u201c\u5979\u5c31\u53ee\u5631\u6211\u5e73\u65f6\u8981\u628a\u4f60\u770b\u7d27\u4e00\u70b9\uff0c\u7537\u4eba\u90fd\u6700\u559c\u6b22\u8fd9\u79cd\u98ce\u9a9a\u6210\u719f\u7684\u7c7b\u578b\uff0c\u4e00\u4e0d\u6ce8\u610f\u5c31\u4f1a\u6e9c\u51fa\u53bb\u5077\u5403\u3002\u8fd9\u4e2a\u662f\u4f60\u8868\u59b9\u5012\u8fd8\u7f62\u4e86\uff0c\u6ca1\u4ec0\u4e48\u5371\u9669\uff0c\u5426\u5219\u7edd\u5bf9\u4e0d\u80fd\u8ba9\u4f60\u548c\u5979\u6709\u673a\u4f1a\u5355\u72ec\u76f8\u5904\u3002\u201d</p><p>\u201c\u4f60\u600e\u4e48\u56de\u7b54\u7684\uff1f\u201d\u743c\u6069\u5ffd\u7136\u6709\u79cd\u4e0d\u7965\u7684\u9884\u611f\u3002</p><p>\u201c\u6211\u8bf4\u8fd9\u4e2a\u5c3d\u7ba1\u653e\u5fc3\u597d\u4e86\uff0c\u4f60\u662f\u4e2a\u65e0\u53ef\u6551\u836f\u7684\u59d0\u63a7\uff0c\u5bf9\u59b9\u59b9\u8fd9\u79cd\u751f\u7269\u538b\u6839\u6ca1\u5174\u8da3\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u743c\u6069\u65e0\u8a00\u4ee5\u5bf9\uff0c\u53ea\u80fd\u6c89\u9ed8\u3002</p><p>\u5929\u8272\u5df2\u7ecf\u5f88\u6697\uff0c\u65e7\u57ce\u533a\u7684\u8def\u4e0a\u4e5f\u6ca1\u6709\u8def\u706f\uff0c\u6708\u4eae\u4e5f\u534a\u906e\u534a\u63a9\u5730\u8eb2\u5728\u4e91\u5c42\u91cc\u3002\u5927\u6982\u662f\u56e0\u4e3a\u5728\u6d77\u8fb9\u7684\u7f18\u6545\uff0c\u8f9b\u5df4\u57ce\u663c\u591c\u6e29\u5dee\u975e\u5e38\u5927\uff0c\u767d\u5929\u53ef\u4ee5\u7a7f\u5355\u8863\uff0c\u592a\u9633\u4e00\u843d\u5c71\u5c31\u6c14\u6e29\u5267\u964d\uff0c\u4e00\u9635\u98ce\u4fa7\u9762\u5439\u6765\uff0c\u51db\u6253\u4e86\u4e2a\u98a4\uff0c\u201c\u597d\u51b7\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u51b7\u8840\u2026\u2026\u6211\u662f\u8bf4\uff0c\u4f60\u4e0d\u662f\u4e0d\u6015\u51b7\u5417\uff1f\u201d</p><p>\u201c\u628a\u5916\u5957\u7ed9\u6211\uff01\u201d\u51db\u6ca1\u597d\u6c14\u5730\u8bf4\u3002</p><p>\u743c\u6069\u8d76\u5feb\u8131\u4e0b\u6597\u7bf7\u5e2e\u5979\u88f9\u4e0a\uff0c\u542c\u89c1\u5979\u5728\u4f4e\u58f0\u561f\u56d4\u3002\u201c\u771f\u662f\u7684\uff0c\u201d\u5c0f\u5973\u5deb\u62b1\u6028\uff0c\u201c\u4e00\u70b9\u90fd\u4e0d\u77e5\u9053\u4f53\u8d34\u5973\u5b69\u5b50\uff0c\u8fd9\u4e48\u7b28\u7684\u7537\u4eba\uff0c\u827e\u5f25\u8587\u7a76\u7adf\u662f\u600e\u4e48\u770b\u4e0a\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u9519\u4e86\uff0c\u5bf9\u4e0d\u8d77\u3002\u201d</p><p>\u743c\u6069\u8bdd\u97f3\u672a\u843d\uff0c\u8eab\u540e\u5ffd\u7136\u4f20\u6765\u4e00\u9635\u55a7\u54d7\uff0c\u5f88\u591a\u4eba\u558a\u53eb\u8d77\u6765\uff0c\u9690\u9690\u8fd8\u6709\u706b\u5149\u95ea\u52a8\u3002\u4e24\u4eba\u9f50\u9f50\u56de\u5934\u671b\u53bb\uff0c\u53ea\u89c1\u65b9\u624d\u8d70\u8fc7\u6765\u7684\u65b9\u5411\uff0c\u4e00\u56e2\u70c8\u7130\u817e\u7a7a\uff0c\u6b63\u718a\u718a\u71c3\u70e7\u3002\u201c\u90a3\u662f\u2026\u2026\u201d\u51db\u7684\u8138\u8272\u5927\u53d8\uff0c\u201c\u9f99\u5bab\uff1f\u201d</p><p>\u743c\u6069\u4e00\u628a\u63e1\u4f4f\u5979\u7684\u624b\u8155\uff0c\u53d1\u52a8\u4e86\u4f20\u9001\u6cd5\u672f\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u4ed6\u4eec\u56de\u5230\u4e86\u9f99\u5bab\u524d\u3002</p><p>\u706b\u7130\u662f\u4ece\u4e1c\u5357\u89d2\u71c3\u8d77\uff0c\u4eff\u4f5b\u88ab\u56da\u7981\u5df2\u4e45\u7684\u731b\u517d\u7a81\u83b7\u91ca\u653e\uff0c\u5c31\u5728\u8fd9\u77ed\u77ed\u6570\u606f\u4e4b\u95f4\uff0c\u5df2\u7ecf\u8ba9\u9f99\u5bab\u53d8\u6210\u4e86\u4e00\u7247\u706b\u6d77\u3002\u5c4b\u9876\u3001\u6881\u67f1\u3001\u5730\u9762\uff0c\u6240\u6709\u7684\u4e00\u5207\u90fd\u88ab\u70c8\u7130\u6240\u541e\u566c\u3002\u51db\u5927\u53eb\u4e00\u58f0\uff0c\u4e0d\u5047\u601d\u7d22\u5730\u51b2\u4e86\u8fdb\u53bb\u3002</p><p>\u5979\u65e0\u60e7\u706b\u7130\uff0c\u743c\u6069\u53ef\u4e0d\u6562\u76f4\u63a5\u5f80\u91cc\u51b2\uff0c\u5fc3\u5ff5\u8f6c\u52a8\u4e4b\u4e0b\uff0c\u53cc\u81c2\u4e0a\u7684\u9b54\u6cd5\u523a\u9752\u540c\u65f6\u95ea\u70c1\uff0c\u5728\u8eab\u4e0a\u53e0\u52a0\u4e86\u4e24\u9053\u5143\u7d20\u62a4\u76fe\uff0c\u63a5\u7740\u53c8\u91ca\u653e\u4e86\u4e00\u4e2a\u62b5\u6297\u9ad8\u6e29\u7684\u6cd5\u672f\u3002\u800c\u5c31\u8fd9\u4e00\u803d\u8bef\uff0c\u5f53\u4ed6\u8ddf\u7740\u8e0f\u5165\u706b\u6d77\u65f6\uff0c\u5df2\u7ecf\u770b\u4e0d\u5230\u51db\u7684\u8eab\u5f71\u4e86\u3002</p><p>\u201c\u51db\uff01\u201d</p><p>\u743c\u6069\u558a\u4e86\u51e0\u58f0\uff0c\u6ca1\u6709\u4efb\u4f55\u56de\u5e94\uff0c\u53ea\u542c\u89c1\u706b\u7130\u5728\u8eab\u65c1\u201c\u6bd5\u9646\u6bd5\u9646\u201d\u7684\u71c3\u70e7\u58f0\uff0c\u867d\u7136\u65e0\u6cd5\u7a81\u7834\u5143\u7d20\u62a4\u76fe\u4f24\u5bb3\u5230\u4ed6\uff0c\u4f46\u9ad8\u6e29\u5374\u8ba9\u7a7a\u6c14\u53d8\u5f97\u626d\u66f2\uff0c\u89c6\u91ce\u4e2d\u4e00\u5207\u666f\u8c61\u90fd\u5728\u6a21\u7cca\u3001\u53d8\u5f62\uff0c\u8ba9\u4ed6\u65e0\u6cd5\u6e05\u695a\u5730\u5224\u65ad\u81ea\u5df1\u548c\u5468\u56f4\u7269\u4f53\u7684\u8ddd\u79bb\u8fdc\u8fd1\u3002\u8fd9\u4ee4\u4ed6\u96be\u53d7\uff0c\u6709\u79cd\u9690\u9690\u7684\u6655\u7729\u611f\uff0c\u751a\u81f3\u547c\u5438\u90fd\u53d8\u5f97\u56f0\u96be\uff0c\u8fd9\u5927\u6982\u662f\u7f3a\u6c27\u7684\u7f18\u6545\u2026\u2026\u4e0d\u5bf9\uff01</p><p>\u5c16\u9510\u7684\u523a\u75db\u81ea\u610f\u8bc6\u6df1\u5904\u88ad\u6765\uff0c\u8ba9\u4ed6\u9661\u7136\u60ca\u9192\uff0c\u8fd9\u662f\u9884\u5148\u9690\u85cf\u5728\u8eab\u4e0a\u7684\u4fdd\u62a4\u6cd5\u672f\u5728\u53d1\u51fa\u731b\u70c8\u8b66\u544a\u3002\u7d2b\u6c34\u6676\u6cd5\u6756\u81ea\u8896\u4e2d\u6ed1\u51fa\uff0c\u88ab\u5deb\u5e08\u63e1\u5728\u53f3\u624b\u4e2d\uff0c\u516d\u679a\u7eef\u7ea2\u4e4b\u6cea\u5b9d\u77f3\u540c\u65f6\u53d1\u5149\uff0c\u51dd\u6210\u4e00\u4f53\uff0c\u7d27\u63a5\u7740\u5411\u56db\u9762\u516b\u65b9\u6025\u901f\u6269\u6563\u5f00\u53bb\u3002</p><p>\u7136\u540e\u743c\u6069\u770b\u89c1\u4e86\u654c\u4eba\u3002</p><p>\u65e0\u6570\u9ed1\u8272\u7684\u5f71\u5b50\u4ece\u706b\u7130\u91cc\u6d41\u51fa\uff0c\u873f\u8712\u626d\u66f2\u7740\uff0c\u4eff\u4f5b\u86c7\u866b\u822c\u5728\u5730\u9762\u4e0a\u7ffb\u6eda\u8815\u52a8\u3002\u5b83\u4eec\u7684\u901f\u5ea6\u90fd\u5f88\u7f13\u6162\uff0c\u534a\u900f\u660e\u7684\u8eaf\u4f53\u53d8\u5e7b\u4e0d\u5b9a\uff0c\u4f46\u5176\u4e2d\u8574\u542b\u7684\u6076\u610f\u662f\u5982\u6b64\u663e\u800c\u6613\u89c1\uff0c\u51e0\u4e4e\u90fd\u8981\u6ee1\u6ea2\u800c\u51fa\u3002\u743c\u6069\u7b49\u5f85\u4e86\u51e0\u79d2\u949f\uff0c\u7136\u540e\u4ed6\u770b\u89c1\u4e00\u53ea\u53c8\u4e00\u53ea\u5934\u751f\u5f2f\u89d2\u3001\u6697\u7ea2\u8272\u76ae\u80a4\u7684\u602a\u7269\u4ece\u9ed1\u5f71\u4e2d\u8e66\u8df3\u51fa\u6765\u3002</p><p>\u201c\u602f\u9b54\uff1f\u201d</p><p>\u5728\u4e0b\u5c42\u754c\u5f85\u8fc7\u534a\u5e74\uff0c\u522b\u7684\u6536\u83b7\u6ca1\u6709\uff0c\u5bf9\u90aa\u9b54\u7684\u5e38\u89c1\u201c\u54c1\u79cd\u201d\u5012\u7b97\u662f\u4e86\u5982\u6307\u638c\uff0c\u743c\u6069\u4e00\u773c\u5c31\u8fa8\u8ba4\u51fa\u4e86\u8fd9\u4e9b\u602a\u7269\u7684\u8eab\u4efd\uff0c\u7136\u800c\u8fd9\u4ee4\u4ed6\u66f4\u52a0\u8ff7\u60d1\u3002\u602f\u9b54\u662f\u6076\u9b54\u4e2d\u6700\u4f4e\u7ea7\u7684\u4e00\u7c7b\uff0c\u6570\u91cf\u6781\u591a\uff0c\u5728\u6df1\u6e0a\u91cc\u904d\u5730\u90fd\u662f\uff0c\u800c\u4e14\u662f\u4ece\u4f4d\u9762\u865a\u7a7a\u4e2d\u4e0d\u65ad\u81ea\u7136\u8bde\u751f\uff0c\u5c31\u50cf\u87d1\u8782\u4e00\u6837\u8e29\u90fd\u8e29\u4e0d\u5b8c\u2014\u2014\u4f46\u4e3a\u4ec0\u4e48\u4f1a\u51fa\u73b0\u5728\u8fd9\u4e2a\u5730\u65b9\uff1f</p><p>\u602f\u9b54\u4eec\u4e5f\u53d1\u73b0\u4e86\u743c\u6069\uff0c\u5b83\u4eec\u62e5\u6324\u5728\u4e00\u8d77\uff0c\u5374\u4e0d\u6562\u9760\u8fd1\uff0c\u53ea\u662f\u6076\u72e0\u72e0\u5730\u76ef\u7740\u8fd9\u4e2a\u4eba\u7c7b\uff0c\u53d1\u51fa\u53fd\u91cc\u5471\u5566\u7684\u543c\u53eb\u58f0\u3002\u743c\u6069\u4e5f\u6ca1\u7a7a\u53bb\u5728\u610f\u8fd9\u4e9b\u602f\u9b54\uff0c\u4ed6\u76b1\u7740\u7709\uff0c\u89c2\u5bdf\u7740\u56db\u5468\uff0c\u60f3\u8981\u641e\u6e05\u695a\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u3002</p><p>\u6709\u4eba\u6b63\u5728\u8fd9\u91cc\u53ec\u5524\u90aa\u9b54\uff1f</p><p>\u8fd9\u662f\u6700\u5408\u7406\u7684\u731c\u6d4b\uff0c\u4f46\u968f\u5373\u5c31\u88ab\u743c\u6069\u81ea\u5df1\u5426\u51b3\u4e86\u3002\u4efb\u4f55\u6cd5\u672f\u53ec\u5524\u90aa\u9b54\uff0c\u57fa\u672c\u539f\u7406\u90fd\u662f\u8981\u6253\u5f00\u4f4d\u9762\u901a\u9053\uff0c\u5c06\u53ec\u5524\u5bf9\u8c61\u4ece\u4e0b\u5c42\u754c\u62c9\u8fc7\u6765\u3002\u6253\u5f00\u4e24\u4e2a\u4e3b\u4f4d\u9762\u4e4b\u95f4\u7684\u901a\u9053\u53ef\u4e0d\u662f\u4e00\u4ef6\u7b80\u5355\u7684\u4e8b\u60c5\uff0c\u52a8\u9759\u7edd\u4e0d\u4f1a\u5c0f\uff0c\u4e0d\u53ef\u80fd\u5b8c\u5168\u7792\u5f97\u8fc7\u743c\u6069\u8fd9\u79cd\u8eab\u5904\u5176\u95f4\u7684\u9ad8\u9636\u5deb\u5e08\u3002\u5728\u770b\u5230\u602f\u9b54\u7684\u65f6\u5019\uff0c\u4ed6\u5df2\u7ecf\u6084\u6084\u91ca\u653e\u4e86\u63a2\u6d4b\u6cd5\u672f\uff0c\u6ca1\u6709\u611f\u5e94\u5230\u4efb\u4f55\u7a7a\u95f4\u9707\u52a8\u7684\u8ff9\u8c61\u3002</p><p>\u65e2\u7136\u6ca1\u6709\u5deb\u5e08\u6253\u5f00\u4f4d\u9762\u901a\u9053\uff0c\u90a3\u4e48\u8fd9\u4e9b\u602f\u9b54\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff1f\u96be\u4e0d\u6210\u662f\u5730\u5e95\u4e0b\u94bb\u51fa\u6765\u7684\u4e0d\u6210\u3002</p><p>\u66f4\u591a\u7684\u90aa\u9b54\u5192\u51fa\u6765\uff0c\u5927\u90e8\u5206\u4ecd\u7136\u90fd\u662f\u602f\u9b54\uff0c\u4f46\u968f\u7740\u65f6\u95f4\u7684\u63a8\u79fb\uff0c\u5df2\u7ecf\u5f00\u59cb\u51fa\u73b0\u66f4\u9ad8\u9636\u7684\u5938\u585e\u9b54\u3001\u5df4\u5e03\u9b54\u751a\u81f3\u50cf\u79c3\u9e6b\u822c\u7684\u5f17\u6d1b\u9b54\u2014\u2014\u5168\u90fd\u662f\u65e0\u5c3d\u6df1\u6e0a\u7684\u6076\u9b54\u4e00\u7cfb\uff0c\u6ca1\u6709\u5730\u72f1\u9b54\u9b3c\u3002</p><p>\u201c\u706b\uff01\u201d</p><p>\u743c\u6069\u7ec8\u4e8e\u770b\u51fa\u4e86\u7aef\u502a\uff0c\u7684\u786e\u4e0d\u662f\u6709\u4eba\u6253\u5f00\u4e86\u901a\u5f80\u6df1\u6e0a\u7684\u4f4d\u9762\u901a\u9053\uff0c\u95ee\u9898\u7684\u5173\u952e\u5c31\u5728\u8fd9\u7a81\u5982\u5176\u6765\u71c3\u8d77\u7684\u5927\u706b\u4e0a\u3002\u8fd9\u4e9b\u706b\u7130\u770b\u4f3c\u5e73\u5e38\uff0c\u65e2\u975e\u6cd5\u672f\u4e4b\u706b\uff0c\u4e5f\u975e\u90aa\u708e\uff0c\u4f46\u5b83\u7684\u71c3\u70e7\u5374\u4eff\u4f5b\u7834\u574f\u4e86\u67d0\u79cd\u5c01\u5370\u2014\u2014\u6216\u8005\u53cd\u8fc7\u6765\u8bf4\uff0c\u706b\u7130\u662f\u5c01\u5370\u88ab\u6253\u7834\u7684\u8868\u73b0\u2014\u2014\u8ba9\u539f\u672c\u88ab\u9547\u538b\u5728\u6b64\u5904\u7684\u90aa\u9b54\u7eb7\u7eb7\u82cf\u9192\u8fc7\u6765\u3002</p><p>\u8fd9\u5ea7\u9f99\u5bab\u4e4b\u4e2d\uff0c\u539f\u672c\u5c45\u7136\u6f5c\u4f0f\u7740\u5982\u6b64\u591a\u7684\u6df1\u6e0a\u90aa\u9b54\uff1f</p><p>\u4e00\u53ea\u8ff7\u8bf1\u9b54\u4ece\u9ed1\u8272\u7684\u5f71\u5b50\u91cc\u722c\u51fa\u6765\uff0c\u968f\u624b\u5c06\u6321\u5728\u5b83\u8eab\u524d\u7684\u4e00\u53ea\u5938\u585e\u9b54\u6495\u6210\u4e24\u534a\uff0c\u7136\u540e\u76f4\u7acb\u8d77\u4e0a\u8eab\uff0c\u4fef\u89c6\u7740\u743c\u6069\u3002\u5728\u6df1\u6e0a\u6076\u9b54\u4e4b\u4e2d\uff0c\u8ff7\u8bf1\u9b54\u5c5e\u4e8e\u4f53\u578b\u8f83\u5c0f\u7684\u4e00\u7c7b\uff0c\u4f46\u8fd9\u53ea\u5374\u683c\u5916\u7684\u5927\uff0c\u800c\u4e14\u76ae\u80a4\u4e0a\u8fd8\u957f\u6ee1\u4e86\u5404\u79cd\u5c16\u523a\uff0c\u770b\u8d77\u6765\u9887\u4e0d\u5bfb\u5e38\u3002\u201c\u662f\u4f60\u6253\u7834\u76d1\u7262\uff0c\u5c06\u6211\u91ca\u653e\u51fa\u6765\u7684\uff0c\u51e1\u4eba\uff1f\u201d\u5b83\u95ee\u3002</p><p>\u743c\u6069\u6447\u6447\u5934\uff1a\u201c\u4e0d\u662f\u3002\u201d</p><p>\u8ff7\u8bf1\u9b54\u663e\u7136\u6123\u4e86\u4e00\u4e0b\uff1a\u201c\u4f60\u5e94\u8be5\u56de\u7b54\u2018\u662f\u2019\u624d\u5bf9\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u7136\u540e\u6211\u5c31\u53ef\u4ee5\u8bf4\u8981\u7ed9\u4e88\u4f60\u62a5\u916c\uff0c\u7136\u540e\u4f60\u5c31\u4f1a\u6ee1\u6000\u671f\u671b\u5730\u7b49\u5f85\u7740\uff0c\u7136\u540e\u6211\u5c31\u4f1a\u628a\u4f60\u7684\u56db\u80a2\u54ac\u4e0b\u6765\uff0c\u67b6\u5728\u706b\u4e0a\u70e4\u719f\uff0c\u4f5c\u4e3a\u5956\u52b1\uff0c\u6211\u4f1a\u6177\u6168\u5730\u8ba9\u4f60\u81ea\u5df1\u5403\u7b2c\u4e00\u53e3\u2014\u2014\u6211\u662f\u4e0d\u662f\u5f88\u6709\u60f3\u6cd5\uff1f\u201d</p><p>\u743c\u6069\u53f9\u4e86\u53e3\u6c14\uff1a\u201c\u6709\u60f3\u6cd5\u7684\u4eba\u90fd\u6b7b\u5f97\u5feb\u2014\u2014\u6709\u60f3\u6cd5\u7684\u90aa\u9b54\u4e5f\u4e00\u6837\u3002\u201d</p><p>\u201c\u4f60\u7684\u80c6\u91cf\u5012\u8fd8\u771f\u4e0d\u5c0f\uff0c\u201d\u8ff7\u8bf1\u9b54\u96be\u4ee5\u7f6e\u4fe1\u5730\u770b\u7740\u743c\u6069\uff0c\u201c\u51e1\u4eba\u73b0\u5728\u90fd\u53d8\u5f97\u8fd9\u4e48\u72c2\u5984\u81ea\u5927\u4e86\u5417\uff1f\u201d</p><p>\u743c\u6069\u6ca1\u6709\u56de\u7b54\uff0c\u800c\u662f\u6307\u4e86\u6307\u5b83\u80cc\u540e\u3002</p><p>\u201c\u6211\u80cc\u540e\u6709\u4ec0\u4e48\uff1f\u54fc\u54fc\uff0c\u6211\u624d\u4e0d\u4f1a\u4e0a\u4f60\u7684\u5f53\uff0c\u8fd9\u79cd\u7b80\u5355\u7684\u628a\u620f\uff0c\u600e\u4e48\u80fd\u9a97\u5f97\u8fc7\u6211\u8fd9\u6837\u806a\u660e\u7684\u6076\u9b54\uff0c\u201d\u8ff7\u8bf1\u9b54\u5634\u91cc\u8bf4\u7740\uff0c\u7a81\u7136\u731b\u5730\u8f6c\u8eab\uff0c\u201c\u54c8\uff0c\u679c\u7136\u4ec0\u4e48\u90fd\u6ca1\u6709\uff0c\u6211\u65e9\u5c31\u77e5\u9053\u2014\u2014\u201d</p><p>\u4e00\u9897\u6a58\u7ea2\u8272\u7684\u706b\u7403\u4ece\u8fdc\u5904\u75be\u5c04\u800c\u81f3\uff0c\u6b63\u6b63\u5730\u51fb\u4e2d\u4e86\u5b83\u7684\u80f8\u53e3\u3002\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u8ff7\u8bf1\u9b54\u53d8\u6210\u4e86\u4e00\u644a\u7070\u70ec\u3002</p><p>\u51db\u4ece\u706b\u7130\u4e2d\u8d70\u51fa\u6765\uff0c\u6276\u7740\u5580\u6d41\u5976\u5976\uff0c\u8001\u5987\u4eba\u5782\u7740\u5934\uff0c\u663e\u7136\u5df2\u7ecf\u660f\u8ff7\u8fc7\u53bb\u3002\u731b\u70c8\u7684\u98ce\u73af\u7ed5\u5728\u5979\u7684\u8eab\u4f53\u5468\u56f4\uff0c\u5c06\u6240\u6709\u7684\u706b\u7130\u90fd\u8fdc\u8fdc\u63a8\u5f00\uff0c\u5f62\u6210\u4e00\u4e2a\u5b89\u5168\u533a\u57df\u3002\u51db\u662f\u7ea2\u9f99\u4e4b\u5973\uff0c\u5979\u81ea\u5df1\u5f53\u7136\u4e0d\u6015\u706b\u7130\uff0c\u968f\u4fbf\u70e7\u90fd\u6ca1\u4e8b\uff0c\u4f46\u5580\u6d41\u5976\u5976\u5374\u662f\u666e\u901a\u4eba\uff0c\u5b8c\u5168\u7ecf\u53d7\u4e0d\u4f4f\u7684\u3002\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5979\u95ee\u743c\u6069\uff0c\u201c\u8fd9\u4e9b\u5bb6\u4f19\u4ece\u54ea\u91cc\u5192\u51fa\u6765\u7684\uff1f\u201d</p><p>\u201c\u8c01\u77e5\u9053\u3002\u201d</p><p>\u6700\u5f3a\u7684\u8ff7\u8bf1\u9b54\u88ab\u4e00\u51fb\u8f70\u6740\uff0c\u5176\u4ed6\u6076\u9b54\u5b8c\u5168\u4e0d\u6562\u518d\u9760\u8fd1\uff0c\u7eb7\u7eb7\u56db\u6563\u9003\u7a9c\u3002\u51db\u4e5f\u61d2\u5f97\u53bb\u7ba1\uff0c\u6b63\u51c6\u5907\u51b2\u51fa\u706b\u6d77\uff0c\u5374\u88ab\u743c\u6069\u62c9\u4f4f\u4e86\uff0c\u201c\u7b49\u7b49\u3002\u201d</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u51db\u5947\u602a\u3002</p><p>\u201c\u51fa\u4e0d\u53bb\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u7a7a\u95f4\u88ab\u5c01\u9501\u4e86\u3002\u201d</p><p>\u5c31\u5728\u8ff7\u8bf1\u9b54\u51fa\u73b0\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u67d0\u79cd\u201c\u53d8\u5316\u201d\u6084\u65e0\u58f0\u606f\u5730\u53d1\u751f\u4e86\uff0c\u718a\u718a\u71c3\u70e7\u7684\u706b\u7130\u53d8\u5f97\u900f\u660e\u3001\u865a\u5316\uff0c\u8be1\u5f02\u7684\u529b\u91cf\u5728\u5176\u4e2d\u5954\u6d8c\u6d41\u52a8\uff0c\u8ba9\u6bcf\u4e00\u56e2\u706b\u90fd\u4eff\u4f5b\u53d8\u6210\u4e86\u4e00\u679a\u9b54\u6cd5\u7b26\u6587\u3002\u800c\u968f\u7740\u706b\u52bf\u8513\u5ef6\uff0c\u6240\u6709\u8fd9\u4e9b\u7b26\u6587\u5f7c\u6b64\u52fe\u8fde\uff0c\u7ec4\u6210\u4e86\u4e00\u9053\u65e0\u5f62\u7684\u201c\u5899\u201d\uff0c\u5c06\u539f\u672c\u5c5e\u4e8e\u9f99\u5bab\u7684\u8fd9\u5757\u7a7a\u95f4\u4e0e\u7269\u8d28\u754c\u5206\u9694\u5f00\u6765\u3002\u8fd9\u662f\u4e00\u79cd\u6781\u5176\u5f3a\u5927\u7684\u6cd5\u672f\uff08\u5982\u679c\u5b83\u662f\u6cd5\u672f\u7684\u8bdd\uff09\uff0c\u743c\u6069\u627e\u4e0d\u51fa\u65bd\u6cd5\u8005\u662f\u8c01\uff0c\u4e5f\u8fa8\u8bc6\u4e0d\u51fa\u5177\u4f53\u95e8\u9053\uff0c\u4f46\u4ed6\u53ef\u4ee5\u6e05\u695a\u5730\u611f\u89c9\u5230\u5b83\u7684\u5b58\u5728\u2014\u2014\u6216\u8005\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u4ed6\u80fd\u591f\u76f4\u63a5\u201c\u770b\u89c1\u201d\u90a3\u9053\u7a7a\u95f4\u4e4b\u5899\u7684\u5b58\u5728\u3002</p><p>\u51db\u53d1\u51fa\u4e86\u4e00\u675f\u706b\u7bad\uff0c\u7136\u540e\u770b\u89c1\u5b83\u5728\u5c04\u51fa\u4e00\u6bb5\u8ddd\u79bb\u4e4b\u540e\uff0c\u7a81\u5140\u5730\u6d88\u5931\u5728\u7a7a\u6c14\u4e2d\uff0c\u8fd9\u9a8c\u8bc1\u4e86\u743c\u6069\u7684\u5224\u65ad\u3002\u5979\u671d\u8fdc\u5904\u671b\u4e86\u671b\uff0c\u53ea\u89c1\u4e00\u7247\u6a21\u7cca\uff0c\u6839\u672c\u770b\u4e0d\u6e05\u5916\u9762\u7684\u666f\u8c61\uff0c\u4e5f\u542c\u4e0d\u5230\u4efb\u4f55\u5916\u9762\u7684\u52a8\u9759\uff0c\u6574\u4e2a\u9f99\u5bab\u4eff\u4f5b\u88ab\u7b3c\u7f69\u5728\u4e00\u4e2a\u534a\u900f\u660e\u7684\u73bb\u7483\u7f69\u91cc\u3002\u201c\u80fd\u6253\u7834\u5417\uff1f\u201d\u51db\u95ee\u3002</p><p>\u201c\u5e94\u8be5\u53ef\u4ee5\uff0c\u6211\u6b63\u5728\u627e\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u6cd5\u6756\u4e0a\u9576\u5d4c\u7684\u516d\u679a\u7eef\u7ea2\u4e4b\u6cea\u5149\u8292\u95ea\u70c1\u4e0d\u5b9a\uff0c\u5ffd\u660e\u5ffd\u6697\uff0c\u201c\u7a7a\u95f4\u5c01\u9501\u4e0e\u8fd9\u4e9b\u706b\u7130\u6709\u5173\uff0c\u706b\u52bf\u6700\u5f3a\u7684\u5730\u65b9\u5c31\u662f\u7ed3\u70b9\u6240\u5728\uff0c\u53ea\u8981\u627e\u5230\u4e86\u7ed3\u70b9\uff0c\u6253\u7834\u5b83\u5c31\u4e0d\u96be\u4e86\u3002\u6211\u521a\u624d\u521d\u6b65\u626b\u63cf\uff0c\u9501\u5b9a\u4e86\u5341\u4e03\u4e2a\u53ef\u80fd\u76ee\u6807\uff0c\u73b0\u5728\u5df2\u7ecf\u6392\u9664\u4e86\u56db\u4e2a\uff0c\u8fd8\u6709\u5341\u4e09\u4e2a\uff0c\u6b63\u5728\u9010\u4e2a\u68c0\u6d4b\u2014\u2014\u201d</p><p>\u201c\u8fd8\u8981\u591a\u4e45\uff1f\u201d</p><p>\u201c\u5927\u6982\u56db\u5341\u5206\u949f\u5427\u2026\u2026\u8fd0\u6c14\u597d\u7684\u8bdd\u8bf4\u4e0d\u5b9a\u534a\u4e2a\u5c0f\u65f6\u3002\u201d</p><p>\u201c\u4e0d\u884c\uff0c\u201d\u51db\u6447\u6447\u5934\uff0c\u201c\u5580\u6d41\u5976\u5976\u7b49\u4e0d\u4e86\u8fd9\u4e48\u4e45\u3002\u201d</p><p>\u4ed6\u4eec\u8eab\u5904\u4e00\u7247\u706b\u6d77\u4e4b\u4e2d\uff0c\u98ce\u53ef\u4ee5\u63a8\u5f00\u70c8\u7130\uff0c\u6cd5\u672f\u53ef\u4ee5\u963b\u9694\u9ad8\u6e29\uff0c\u4f46\u4ecd\u7136\u6709\u4e9b\u95ee\u9898\u662f\u65e0\u6cd5\u89e3\u51b3\u7684\uff0c\u6bd4\u5982\u8bf4\u7a7a\u6c14\u3002\u743c\u6069\u548c\u51db\u90fd\u662f\u9ad8\u9636\u5deb\u5e08\uff0c\u81ea\u4fdd\u4e0d\u96be\uff0c\u5580\u6d41\u5976\u5976\u5374\u662f\u4e00\u4e2a\u521a\u521a\u53d7\u4e86\u91cd\u4f24\u7684\u8001\u4eba\uff0c\u8eab\u4f53\u865a\u5f31\uff0c\u4e0d\u53ef\u80fd\u5728\u8fd9\u79cd\u73af\u5883\u91cc\u5f85\u592a\u4e45\u3002</p><p>\u743c\u6069\u76b1\u7709\uff0c\u4ed6\u5f53\u7136\u660e\u767d\u8fd9\u70b9\uff0c\u4f46\u4e5f\u6ca1\u4ec0\u4e48\u597d\u529e\u6cd5\uff0c\u7a7a\u95f4\u6cd5\u672f\u5e76\u4e0d\u662f\u4ed6\u7684\u64c5\u957f\uff0c\u7834\u89e3\u8d77\u6765\u9700\u8981\u82b1\u5f88\u957f\u65f6\u95f4\u3002\u6309\u7167\u624e\u745e\u5c14\u7684\u8bf4\u6cd5\uff0c\u53ea\u8981\u80fd\u591f\u6062\u590d\u201c\u7fd4\u9f99\u201d\u7684\u8bb0\u5fc6\u548c\u529b\u91cf\uff0c\u4efb\u4f55\u7a7a\u95f4\u7981\u5236\u90fd\u56f0\u4e0d\u4f4f\u4ed6\uff0c\u4f46\u4ed6\u6682\u65f6\u8fd8\u505a\u4e0d\u5230\u3002</p><p>\u201c\u662f\u8fd9\u4e9b\u706b\u7130\u5c01\u9501\u4e86\u7a7a\u95f4\uff1f\u201d\u51db\u95ee\u3002</p><p>\u201c\u4e0d\u51c6\u786e\uff0c\u4f46\u4e5f\u53ef\u4ee5\u8fd9\u4e48\u8bf4\u5427\u3002\u636e\u6211\u7684\u63a8\u6d4b\uff0c\u8fd9\u662f\u4e00\u79cd\u5199\u5728\u706b\u7130\u4e0a\u7684\u6cd5\u672f\uff0c\u706b\u7130\u662f\u5b83\u7684\u8f7d\u4f53\u2014\u2014\u201d</p><p>\u201c\u6276\u7740\u5979\u3002\u201d\u51db\u6253\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u5c06\u5580\u6d41\u5976\u5976\u63a8\u8fc7\u6765\u3002</p><p>\u743c\u6069\u8fde\u5fd9\u6276\u4f4f\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u8bf4\u8bdd\uff0c\u5c31\u89c1\u51db\u4e00\u8dc3\u800c\u8d77\uff0c\u60ac\u6d6e\u5728\u534a\u7a7a\u4e2d\uff0c\u706b\u7ea2\u8272\u7684\u5149\u534e\u6d41\u6ea2\u5168\u8eab\uff0c\u5728\u4e0b\u4e00\u523b\uff0c\u5979\u5316\u4f5c\u5de8\u5927\u7684\u7ea2\u9f99\uff0c\u5f20\u5f00\u5634\uff0c\u6df1\u6df1\u5730\u5438\u4e86\u53e3\u6c14\u3002</p><p>\u201c\u547c\uff01\u201d</p><p>\u98ce\u4ece\u5730\u9762\u6d8c\u8d77\uff0c\u65e0\u6570\u56e2\u6c14\u6d41\u4eff\u4f5b\u4e00\u961f\u8bad\u7ec3\u6709\u7d20\u7eaa\u5f8b\u4e25\u660e\u7684\u58eb\u5175\uff0c\u5728\u6574\u4e2a\u9f99\u5bab\u4e2d\u6e05\u7406\u626b\u8361\u7740\uff0c\u5c06\u6bcf\u4e00\u7247\u706b\u6d77\u3001\u6bcf\u4e00\u6735\u706b\u82d7\u3001\u6bcf\u4e00\u7c92\u706b\u661f\u90fd\u5265\u79bb\u51fa\u6765\uff0c\u5377\u631f\u800c\u8d77\uff0c\u81ea\u56db\u9762\u516b\u65b9\u671d\u7740\u7a7a\u4e2d\u6c47\u805a\u3002\u743c\u6069\u538b\u6839\u6ca1\u60f3\u5230\u51db\u4f1a\u6765\u8fd9\u4e00\u624b\uff0c\u76ee\u77aa\u53e3\u5446\u4e4b\u95f4\uff0c\u6240\u6709\u7684\u706b\u7130\u90fd\u5df2\u7ecf\u88ab\u7ea2\u9f99\u5438\u5165\u53e3\u4e2d\uff0c\u70b9\u6ef4\u4e0d\u5269\u3002</p><p>\u5931\u53bb\u4e86\u8f7d\u4f53\uff0c\u5c01\u9501\u7a7a\u95f4\u7684\u529b\u91cf\u77ac\u95f4\u6e83\u6563\uff0c\u518d\u4e5f\u65e0\u6cd5\u7ef4\u6301\u5c4f\u969c\u3002\u4f46\u5927\u706b\u9020\u6210\u7684\u7834\u574f\u5df2\u7ecf\u65e0\u6cd5\u56de\u590d\uff0c\u539f\u672c\u5b8f\u4f1f\u58ee\u4e3d\u7684\u9f99\u5bab\u5df2\u7ecf\u53ea\u5269\u4e0b\u4e00\u7247\u65ad\u58c1\u6b8b\u57a3\u3002\u4e00\u7fa4\u4eba\u51b2\u8fdb\u6765\uff0c\u4ed6\u4eec\u90fd\u662f\u4f4f\u5728\u9644\u8fd1\u7684\u5c45\u6c11\uff0c\u53d1\u73b0\u8d77\u706b\u540e\u5c31\u7acb\u523b\u8d76\u4e86\u8fc7\u6765\uff0c\u4f46\u4e4b\u524d\u4e00\u76f4\u88ab\u963b\u6321\u5728\u5916\u9762\uff0c\u65e0\u6cd5\u9760\u8fd1\uff0c\u76f4\u5230\u770b\u5230\u706b\u7130\u7184\u706d\u624d\u6562\u8fdb\u5165\u3002\u800c\u4ed6\u4eec\u6240\u770b\u5230\u7684\uff0c\u662f\u4e00\u5934\u5de8\u5927\u7684\uff0c\u5168\u8eab\u95ea\u70c1\u7740\u7ea2\u5149\u7684\u9f99\uff0c\u6b63\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u5c06\u524d\u722a\u4e2d\u6258\u7740\u7684\u8001\u5987\u4eba\u653e\u5728\u4e00\u5757\u5e73\u5766\u5e72\u51c0\u7684\u5730\u9762\u4e0a\uff0c\u7136\u540e\u957f\u541f\u4e00\u58f0\uff0c\u632f\u7ffc\u817e\u7a7a\u800c\u8d77\uff0c\u5728\u591c\u8272\u4e2d\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u203b\u203b\u203b</p><p>\u201c\u4f60\u4eec\u53bb\u9f99\u5bab\u4e86\uff1f\u201d\u6885\u83f2\u65af\u653e\u4e0b\u7b14\uff0c\u95ee\u3002</p><p>\u201c\u55ef\u3002\u201d\u743c\u6069\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u56de\u7b54\u7740\uff0c\u773c\u5149\u8ddf\u7740\u51db\u5728\u623f\u95f4\u91cc\u6253\u8f6c\u3002</p><p>\u51db\u7684\u7ea2\u9f99\u53d8\u8eab\u6709\u4e2a\u526f\u4f5c\u7528\uff0c\u5c31\u662f\u8863\u670d\u4f1a\u88ab\u6491\u7206\u6389\uff0c\u9f99\u5f62\u6001\u65f6\u5168\u8eab\u9cde\u7532\u5012\u8fd8\u7f62\u4e86\uff0c\u6062\u590d\u6210\u4eba\u5f62\u540e\u5c31\u6210\u4e86\u5168\u8eab\u8d64\u88f8\uff0c\u53ea\u80fd\u6682\u65f6\u7528\u5e7b\u672f\u6216\u8005\u706b\u7130\u906e\u63a9\u3002\u6240\u4ee5\u5979\u56de\u5230\u4f4f\u5904\u540e\u7684\u7b2c\u4e00\u4ef6\u4e8b\uff0c\u5c31\u662f\u8dd1\u5230\u6885\u83f2\u65af\u7684\u5367\u5ba4\u91cc\u627e\u8863\u670d\u3002\u56e0\u4e3a\u6ca1\u6709\u5916\u4eba\uff0c\u5c0f\u5973\u5deb\u4e5f\u4e0d\u5bb3\u7f9e\uff0c\u5927\u5927\u65b9\u65b9\u5730\u8d64\u88f8\u7740\u8eab\u4f53\uff0c\u5c06\u6885\u83f2\u65af\u7684\u51e0\u5957\u8863\u670d\u94fa\u5728\u5e8a\u4e0a\uff0c\u4e00\u8fb9\u8bd5\u7a7f\u8fd8\u4e00\u8fb9\u54c1\u5934\u8bba\u8db3\uff0c\u5012\u662f\u8ba9\u743c\u6069\u8d81\u673a\u5927\u9971\u773c\u798f\u3002</p><p>\u201c\u600e\u4e48\u5168\u662f\u886c\u886b\u548c\u957f\u88e4\uff0c\u592a\u5355\u8c03\u4e86\uff0c\u827e\u5f25\u8587\u4f60\u5076\u5c14\u4e5f\u5e94\u8be5\u6362\u6362\u53e3\u5473\uff0c\u4e70\u51e0\u4ef6\u88d9\u5b50\u561b\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\u4f60\u4e0d\u559c\u6b22\u7a7f\u88d9\u5b50\uff0c\u4f46\u6211\u559c\u6b22\u770b\u4f60\u7a7f\u554a\uff0c\u55ef\uff0c\u743c\u6069\u4e5f\u559c\u6b22\uff0c\u5bf9\u4e0d\u5bf9\uff1f\u201d</p><p>\u201c\u88e4\u5b50\u592a\u957f\u4e86\uff0c\u771f\u662f\u7684\uff0c\u817f\u751f\u90a3\u4e48\u957f\u505a\u4ec0\u4e48\uff0c\u8ba9\u4eba\u5ac9\u5992\u6b7b\u4e86\u3002\u201d</p><p>\u201c\u827e\u5f25\u8587\u4f60\u7684\u6587\u80f8\u5168\u90fd\u592a\u5c0f\u4e86\u5566\uff0c\u52d2\u5f97\u597d\u4e0d\u8212\u670d\uff0c\u5c31\u6ca1\u6709\u4e00\u4e2a\u7a0d\u5fae\u5927\u70b9\u7684\u5417\uff1f\u201d</p><p>\u6885\u83f2\u65af\u7ec8\u4e8e\u5fcd\u65e0\u53ef\u5fcd\uff1a\u201c\u95ed\u5634\uff01\u4f60\u80f8\u5927\u5c31\u4e86\u4e0d\u8d77\u554a\uff1f\u201d</p><p>\u51db\u9a84\u50b2\u5730\u633a\u4e86\u633a\u80f8\uff0c\u4e24\u5927\u56e2\u96ea\u767d\u5ae9\u8089\u9669\u4e9b\u4ece\u660e\u663e\u5c0f\u4e86\u4e00\u53f7\u7684\u6587\u80f8\u4e2d\u5f39\u8df3\u51fa\u6765\uff0c\u6643\u5f97\u743c\u6069\u773c\u775b\u90fd\u82b1\u4e86\u3002\u201c\u6211\u80f8\u5927\u5f53\u7136\u4e86\u4e0d\u8d77\u5566\uff0c\u201d\u5979\u5f97\u610f\u6d0b\u6d0b\u5730\u8bf4\uff0c\u201c\u8c01\u8ba9\u827e\u5f25\u8587\u4f60\u53d1\u80b2\u592a\u6162\u5462\u3002\u201d</p><p>\u201c\u6211\u53ea\u662f\u4f18\u5148\u53d1\u80b2\u667a\u529b\u800c\u5df2\u3002\u201d</p><p>\u201c\u5973\u4eba\u8981\u667a\u529b\u6709\u4ec0\u4e48\u7528\uff1f\u201d\u51db\u4e0d\u4ee5\u4e3a\u7136\u3002</p><p>\u201c\u667a\u529b\u9ad8\u4e0d\u5bb9\u6613\u88ab\u7537\u4eba\u9a97\u3002\u201d</p><p>\u201c\u7537\u4eba\u770b\u5230\u5927\u80f8\u59b9\u5b50\uff0c\u667a\u529b\u81ea\u7136\u6025\u5267\u964d\u4f4e\uff0c\u624d\u4e0d\u8db3\u4e3a\u60e7\u5462\uff0c\u201d\u51db\u4e00\u8fb9\u8bf4\u4e00\u8fb9\u6263\u886c\u886b\u7684\u7ebd\u6263\uff0c\u7136\u540e\u53d1\u73b0\u80f8\u53e3\u7684\u4e24\u7c92\u65e0\u8bba\u5982\u4f55\u4e5f\u6263\u4e0d\u4e0a\uff0c\u5c31\u7b97\u52c9\u5f3a\u6263\u4e0a\u4e86\uff0c\u4e00\u5438\u6c14\u4e5f\u80af\u5b9a\u4f1a\u5d29\u5f00\uff0c\u53ea\u5f97\u653e\u5f03\uff0c\u201c\u4f60\u770b\u770b\u4f60\uff0c\u5c31\u662f\u5178\u578b\u7684\u53cd\u9762\u6559\u6750\u3002\u201d</p><p>\u201c\u6211\u600e\u4e48\u6210\u4e86\u53cd\u9762\u6559\u6750\uff1f\u201d</p><p>\u201c\u4f60\u88ab\u743c\u6069\u9a97\u5230\u624b\u4e86\u554a\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u8fd9\u5c31\u662f\u80f8\u4e0d\u591f\u5927\u7684\u6076\u679c\u3002\u201d</p><p>\u6885\u83f2\u65af\u6c89\u9ed8\u4e86\u51e0\u79d2\u949f\uff0c\u51b3\u5b9a\u4e0d\u518d\u7406\u776c\u51db\u3002\u201c\u4f60\u4eec\u53bb\u9f99\u5bab\u5e72\u561b\u5462\uff1f\u201d\u5979\u95ee\u743c\u6069\u3002</p><p>\u743c\u6069\u604b\u604b\u4e0d\u820d\u5730\u5c06\u76ee\u5149\u4ece\u51db\u8eab\u4e0a\u79fb\u5f00\uff0c\u7b80\u660e\u627c\u8981\u5730\u628a\u6574\u4e2a\u4e8b\u60c5\u7ecf\u8fc7\u8bf4\u4e86\u4e00\u904d\u3002\u201c\u9f99\u5bab\u91cc\u5c01\u5370\u4e86\u90aa\u9b54\uff1f\u201d\u6885\u83f2\u65af\u6c89\u541f\u4e86\u534a\u54cd\uff0c\u6700\u7ec8\u6447\u6447\u5934\uff0c\u201c\u6240\u6709\u8d44\u6599\u91cc\u90fd\u6ca1\u6709\u4efb\u4f55\u8fd9\u65b9\u9762\u7684\u8bb0\u8f7d\u3002\u201d</p><p>\u201c\u5bf9\u4e86\uff0c\u201d\u51db\u7ec8\u4e8e\u6362\u597d\u4e86\u8863\u670d\uff0c\u201c\u6211\u53d1\u73b0\u4e86\u8fd9\u4e2a\u3002\u201d</p><p>\u5979\u62ff\u51fa\u4e00\u4e2a\u900f\u660e\u7684\u73e0\u5b50\uff0c\u9012\u7ed9\u6885\u83f2\u65af\u3002\u201c\u6211\u628a\u6240\u6709\u706b\u7130\u5438\u8fdb\u6765\uff0c\u5b83\u4eec\u6700\u540e\u5c31\u51dd\u7ed3\u6210\u4e86\u8fd9\u4e1c\u897f\uff0c\u201d\u51db\u89e3\u91ca\uff0c\u201c\u6211\u89c9\u5f97\u662f\u4e0d\u662f\u5c31\u662f\u5b83\u5728\u6363\u9b3c\u3002\u201d</p><p>\u743c\u6069\u4e5f\u51d1\u8fc7\u6765\u770b\uff0c\u90a3\u662f\u4e00\u4e2a\u62c7\u6307\u5927\u5c0f\u7684\u5706\u73e0\uff0c\u65e0\u8272\u900f\u660e\uff0c\u770b\u8d77\u6765\u6beb\u4e0d\u8d77\u773c\u3002\u4ed6\u6478\u4e86\u4e00\u4e0b\uff0c\u53d1\u73b0\u8868\u9762\u5f88\u5149\u6ed1\uff0c\u4f46\u4e0d\u51b7\uff0c\u53cd\u800c\u6709\u79cd\u6e29\u6da6\u7684\u611f\u89c9\uff0c\u800c\u4e14\u5f88\u6c89\uff0c\u5206\u91cf\u4e0d\u8f7b\u3002\u4e09\u4eba\u5bf9\u7740\u73e0\u5b50\u7814\u7a76\u4e86\u534a\u54cd\uff0c\u4e5f\u90fd\u6ca1\u770b\u51fa\u4ec0\u4e48\u95e8\u9053\uff0c\u65e2\u770b\u4e0d\u5230\u534a\u70b9\u9b54\u6cd5\u7075\u5149\uff0c\u4e5f\u611f\u89c9\u4e0d\u5230\u91cc\u9762\u8574\u542b\u4e86\u4efb\u4f55\u529b\u91cf\uff0c\u552f\u4e00\u7b97\u662f\u6709\u4e9b\u7279\u6b8a\u4e4b\u5904\uff0c\u662f\u5b83\u7684\u6750\u8d28\u3002\u201c\u8fd9\u662f\u4ec0\u4e48\u505a\u7684\uff1f\u201d\u51db\u597d\u5947\u5730\u8ba9\u73e0\u5b50\u5728\u638c\u5fc3\u6eda\u6765\u6eda\u53bb\uff0c\u201c\u770b\u7740\u6709\u70b9\u50cf\u6c34\u6676\u3002\u201d</p><p>\u201c\u4e0d\u662f\u6c34\u6676\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6c34\u6676\u6ca1\u8fd9\u4e48\u91cd\uff0c\u800c\u4e14\uff0c\u201d\u5979\u7528\u624b\u6307\u634f\u4e86\u634f\u73e0\u5b50\uff0c\u53ef\u4ee5\u770b\u5230\u8f7b\u5fae\u7684\u53d8\u5f62\uff0c\u201c\u4e5f\u4e0d\u53ef\u80fd\u8fd9\u4e48\u8f6f\u3002\u201d</p><p>\u201c\u8fd8\u662f\u6709\u5f39\u6027\u7684\uff1f\u201d\u743c\u6069\u90fd\u6ca1\u53d1\u73b0\u8fd9\u70b9\u3002</p><p>\u65e0\u8272\u900f\u660e\uff0c\u5bc6\u5ea6\u5927\uff0c\u6709\u4e00\u5b9a\u7684\u5f39\u6027\uff0c\u8fd9\u7a76\u7adf\u662f\u4ec0\u4e48\u6750\u8d28\uff0c\u4e00\u65f6\u95f4\u8fd8\u771f\u60f3\u4e0d\u51fa\u6765\u3002\u5982\u679c\u4ec5\u4ec5\u5982\u6b64\uff0c\u5012\u4e5f\u8fd8\u7f62\u4e86\uff0c\u65e0\u975e\u662f\u67d0\u79cd\u4e0d\u8ba4\u8bc6\u7684\u65b0\u7269\u8d28\u800c\u5df2\u3002\u4e16\u754c\u4e4b\u5927\uff0c\u8c01\u4e5f\u4e0d\u6562\u8bf4\u81ea\u5df1\u80fd\u591f\u5c3d\u77e5\u5c3d\u8bc6\uff0c\u6ca1\u4ec0\u4e48\u597d\u5947\u602a\u7684\u3002\u4f46\u51db\u8bf4\u5f97\u5f88\u6e05\u695a\uff0c\u8fd9\u4e1c\u897f\u662f\u5979\u5f53\u65f6\u541e\u5438\u4e86\u9f99\u5bab\u4e2d\u7684\u6240\u6709\u706b\u7130\u4e4b\u540e\u51dd\u7ed3\u800c\u6210\u7684\uff0c\u8fd9\u5c31\u4e0d\u80fd\u7b49\u95f2\u89c6\u4e4b\u4e86\u3002</p><p>\u201c\u53bb\u95ee\u95ee\u5854\u59d0\u59d0\u5427\uff0c\u201d\u51db\u63d0\u8bae\uff0c\u201c\u5979\u53ef\u80fd\u4f1a\u77e5\u9053\u3002\u201d</p><p>\u6885\u83f2\u65af\u6ca1\u4ec0\u4e48\u5f02\u8bae\uff0c\u76f4\u89c9\u544a\u8bc9\u5979\u8fd9\u4e1c\u897f\u5f88\u5371\u9669\uff0c\u6700\u597d\u5c3d\u5feb\u641e\u6e05\u695a\u6765\u5386\u3002</p><p>\u51e0\u5206\u949f\u540e\uff0c\u7ef4\u82e5\u62c9\u88ab\u51db\u62c9\u8fdb\u6765\uff0c\u201c\u5c31\u662f\u8fd9\u4e1c\u897f\uff0c\u201d\u51db\u628a\u73e0\u5b50\u9012\u7ed9\u5979\uff0c\u201c\u4f60\u770b\u8ba4\u4e0d\u8ba4\u8bc6\u3002\u201d</p><p>\u5728\u8fc7\u6765\u7684\u65f6\u5019\uff0c\u7ef4\u82e5\u62c9\u5df2\u7ecf\u542c\u51db\u7b80\u7565\u8bf4\u4e86\u4e8b\u60c5\u7ecf\u8fc7\uff0c\u5979\u62ff\u8d77\u73e0\u5b50\u770b\u4e86\u4e00\u4f1a\uff0c\u53c8\u91ca\u653e\u4e86\u51e0\u4e2a\u63a2\u6d4b\u6cd5\u672f\uff0c\u6c89\u541f\u4e86\u534a\u54cd\uff0c\u7136\u540e\u70b9\u70b9\u5934\uff1a\u201c\u6211\u5927\u6982\u77e5\u9053\u5b83\u662f\u4ec0\u4e48\u4e86\uff0c\u4e0d\u8fc7\u8fd8\u8981\u8fdb\u4e00\u6b65\u786e\u8ba4\u3002\u5982\u679c\u65b9\u4fbf\u7684\u8bdd\uff0c\u53ef\u4ee5\u8ba9\u6211\u7814\u7a76\u4e00\u6bb5\u65f6\u95f4\u5417\uff1f\u201d</p><p>\u201c\u5b83\u7a76\u7adf\u662f\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u5982\u679c\u6211\u7684\u521d\u6b65\u5224\u65ad\u6ca1\u9519\u7684\u8bdd\uff0c\u5b83\u6709\u53ef\u80fd\u662f\u4e00\u679a\u2018\u70bc\u9b54\u77f3\u2019\u3002\u201d</p><p>\u201c\u70bc\u9b54\u77f3\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u636e\u8bf4\u662f\u4e00\u4f4d\u53e4\u795e\u8d50\u4e88\u51e1\u4eba\u7684\u5b9d\u7269\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u662f\u501f\u52a9\u8679\u96fe\uff0c\u4ece\u4e00\u4f4d\u524d\u4efb\u4f20\u9053\u5deb\u5e08\u7684\u8bb0\u5fc6\u4e2d\u627e\u5230\u4e86\u7ebf\u7d22\u3002\u8fd9\u4f4d\u4f20\u9053\u5deb\u5e08\u662f\u4e2a\u85cf\u4e66\u5bb6\uff0c\u70ed\u8877\u4e8e\u6536\u8d2d\u5404\u79cd\u53e4\u7c4d\uff0c\u66fe\u7ecf\u4ece\u8def\u8fb9\u5730\u644a\u4e0a\u4e70\u5230\u8fc7\u4e00\u672c\u4e66\uff0c\u91cc\u9762\u8bb0\u5f55\u4e86\u5f88\u591a\u5931\u4f20\u7684\u5b9d\u7269\uff0c\u5176\u4e2d\u5c31\u6709\u201c\u70bc\u9b54\u77f3\u201d\u3002\u636e\u8bf4\u662f\u5728\u4e0a\u53e4\u65f6\u4ee3\uff0c\u90aa\u9b54\u8086\u8650\u51e1\u95f4\uff0c\u4eba\u4eec\u5411\u6240\u5d07\u62dc\u7684\u795e\u660e\u7948\u6c42\u5e2e\u52a9\uff0c\u4e00\u4f4d\u53e4\u795e\u5f97\u77e5\u6b64\u4e8b\uff0c\u4fbf\u8d50\u4e0b\u4e00\u679a\u95ea\u95ea\u53d1\u5149\u7684\u73e0\u5b50\uff0c\u8fd9\u4fbf\u662f\u201c\u70bc\u9b54\u77f3\u201d\u3002\u5b83\u5176\u5b9e\u5c31\u662f\u4e00\u4e2a\u6b21\u5143\u76d1\u72f1\uff0c\u53ef\u4ee5\u5c06\u90aa\u9b54\u56da\u7981\u5176\u4e2d\uff0c\u9664\u975e\u6301\u6709\u8005\u540c\u610f\u6216\u8005\u70bc\u9b54\u77f3\u6bc1\u574f\uff0c\u5426\u5219\u6c38\u8fdc\u4e5f\u65e0\u6cd5\u9003\u8131\u3002</p><p>\u201c\u4e5f\u5c31\u662f\u4e00\u4e2a\u5bf9\u4ed8\u90aa\u9b54\u7684\u6cd5\u5668\u5bf9\u5427\uff0c\u201d\u51db\u62ff\u5728\u624b\u91cc\u5de6\u770b\u53f3\u770b\uff0c\u201c\u90a3\u8981\u600e\u4e48\u7528\u5462\uff1f\u201d</p><p>\u201c\u4e0d\u77e5\u9053\uff0c\u201d\u7ef4\u82e5\u62c9\u6447\u5934\uff0c\u201c\u8bb0\u8f7d\u91cc\u6ca1\u63d0\u5230\u4f7f\u7528\u65b9\u6cd5\u3002\u201d</p><p>\u201c\u80fd\u56da\u7981\u591a\u5f3a\u7684\u6076\u9b54\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u8ff7\u8bf1\u9b54\uff1f\u86c7\u9b54\uff1f\u603b\u4e0d\u81f3\u4e8e\u80fd\u5bf9\u4ed8\u708e\u9b54\u5427\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u4e5f\u4e0d\u6e05\u695a\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u5176\u5b9e\uff0c\u70bc\u9b54\u77f3\u8fd9\u4e1c\u897f\u662f\u5426\u5f53\u771f\u5b58\u5728\uff0c\u90fd\u662f\u4e2a\u95ee\u9898\uff0c\u6bd5\u7adf\u662f\u4e0a\u53e4\u65f6\u4ee3\u7684\u4e1c\u897f\uff0c\u65e9\u5c31\u9057\u5931\u4e86\uff0c\u8c01\u90fd\u6ca1\u89c1\u5230\u8fc7\u5b9e\u7269\u3002\u6211\u53ea\u662f\u8bf4\u4f60\u4eec\u624b\u91cc\u8fd9\u9897\u73e0\u5b50\uff0c\u770b\u8d77\u6765\u548c\u8bb0\u8f7d\u91cc\u7684\u70bc\u9b54\u77f3\u5f88\u50cf\uff0c\u7a76\u7adf\u662f\u4e0e\u4e0d\u662f\uff0c\u8fd8\u4e0d\u4e00\u5b9a\u5462\u3002\u201d</p><p>\u90a3\u5c31\u5148\u7814\u7a76\u7814\u7a76\u518d\u8bf4\u5427\u3002</p><p>\u628a\u70bc\u9b54\u77f3\u4e22\u7ed9\u7ef4\u82e5\u62c9\u53bb\u7814\u7a76\uff0c\u8fd9\u4ef6\u4e8b\u5c31\u6682\u65f6\u544a\u4e00\u6bb5\u843d\u3002\u5982\u6b64\u4e00\u6765\uff0c\u743c\u6069\u548c\u51db\u4e4b\u524d\u770b\u5230\u7684\u6076\u9b54\uff0c\u4e5f\u7b97\u662f\u6709\u4e86\u521d\u6b65\u89e3\u91ca\u3002\u867d\u7136\u5177\u4f53\u7684\u7ec6\u8282\u4ecd\u7136\u4e0d\u6e05\u695a\uff0c\u6bd4\u5982\u8bf4\u9f99\u5bab\u91cc\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u679a\u70bc\u9b54\u77f3\uff0c\u53c8\u4e3a\u4ec0\u4e48\u4f1a\u5e73\u9759\u4e86\u51e0\u767e\u5e74\uff0c\u5728\u4eca\u665a\u7a81\u7136\u663e\u73b0\u2014\u2014\u4f46\u8fd9\u4e9b\u90fd\u4e0d\u662f\u592a\u91cd\u8981\uff0c\u53ef\u4ee5\u4ee5\u540e\u518d\u6162\u6162\u7422\u78e8\u3002</p><p>\u65f6\u5019\u5df2\u7ecf\u4e0d\u65e9\uff0c\u8be5\u4f11\u606f\u4e86\u3002\u743c\u6069\u9001\u7ef4\u82e5\u62c9\u56de\u5979\u7684\u623f\u95f4\uff0c\u987a\u4fbf\u7559\u5bbf\u3002\u4e24\u4eba\u4e0d\u662f\u60c5\u4fa3\uff0c\u4f46\u90fd\u5df2\u7ecf\u505a\u8fc7\u65e0\u6570\u6b21\uff0c\u5f88\u81ea\u7136\u5730\u6d17\u6fa1\u3001\u4e0a\u5e8a\u3001\u505a\u7231\u3002</p><p>\u6cd5\u672f\u521b\u9020\u7684\u9b54\u5634\u4e2d\uff0c\u8f7b\u67d4\u60a0\u626c\u7684\u97f3\u4e50\u58f0\u82e5\u6709\u82e5\u65e0\u5730\u4f20\u51fa\uff0c\u8426\u7ed5\u5ba4\u4e2d\u3002\u743c\u6069\u4ece\u5e8a\u8fb9\u67dc\u4e2d\u62ff\u51fa\u4e00\u74f6\u8461\u8404\u9152\u548c\u4e24\u53ea\u9152\u676f\uff0c\u5206\u522b\u659f\u6ee1\uff0c\u9012\u4e86\u4e00\u53ea\u7ed9\u5973\u5deb\u5e08\u3002\u8461\u8404\u9152\u662f\u65c5\u5e97\u63d0\u4f9b\u7684\uff0c\u53e3\u611f\u8fd8\u4e0d\u9519\uff0c\u5f53\u7136\u4ef7\u683c\u4e5f\u4e0d\u83f2\uff0c\u4ed6\u5176\u5b9e\u4e0d\u600e\u4e48\u559c\u6b22\u559d\u9152\uff0c\u4f46\u5076\u5c14\u5c1d\u5c1d\uff0c\u4f5c\u4e3a\u52a9\u5174\uff0c\u4e5f\u65e0\u4e0d\u53ef\u3002</p><p>\u3010\u548c\u8c10\u3011</p><p>\u8db3\u8db3\u8fc7\u4e86\u5341\u591a\u5206\u949f\uff0c\u7ef4\u82e5\u62c9\u624d\u4ece\u5931\u795e\u4e2d\u6e10\u6e10\u6062\u590d\uff0c\u5979\u5168\u8eab\u8f6f\u7ef5\u7ef5\u7684\uff0c\u534a\u70b9\u529b\u6c14\u90fd\u6ca1\u6709\uff0c\u8fde\u4e00\u6839\u624b\u6307\u90fd\u4e0d\u60f3\u52a8\u5f39\uff0c\u53ea\u611f\u89c9\u81ea\u5df1\u88ab\u7537\u4eba\u62b1\u5728\u6000\u91cc\uff0c\u8138\u8d34\u7740\u4ed6\u7684\u80f8\u819b\uff0c\u542c\u89c1\u549a\u549a\u7684\u5fc3\u8df3\uff0c\u5728\u5bc2\u9759\u7684\u591c\u91cc\u683c\u5916\u6e05\u695a\u3002\u6709\u90a3\u4e48\u4e00\u4f1a\uff0c\u5979\u751a\u81f3\u4ea7\u751f\u9519\u89c9\uff0c\u4eff\u4f5b\u4e24\u4eba\u662f\u6700\u4eb2\u5bc6\u7684\u7231\u4fa3\uff0c\u5c71\u76df\u6d77\u8a93\uff0c\u76f8\u7ea6\u5f7c\u6b64\u7ec8\u751f\u501a\u9760\uff0c\u6c38\u8fdc\u4e0d\u79bb\u4e0d\u5f03\u2014\u2014\u76f4\u5230\u7537\u4eba\u7684\u4e00\u53e5\u8bdd\u6253\u7834\u4e86\u5979\u7684\u5e7b\u60f3\u3002</p><p>\u201c\u4f60\u7684\u5c0f\u5e08\u59b9\u8bf4\u5f97\u771f\u6ca1\u9519\uff0c\u6bd4\u8d77\u9752\u82f9\u679c\uff0c\u8fd8\u662f\u6210\u719f\u5973\u4eba\u5403\u8d77\u6765\u66f4\u53ef\u53e3\uff0c\u201d\u4ed6\u610f\u72b9\u672a\u5c3d\u5730\u8bf4\uff0c\u201c\u4e0d\u77e5\u9053\u4f60\u8001\u5e08\u7684\u5473\u9053\u5982\u4f55\uff0c\u8fd8\u771f\u662f\u8ba9\u4eba\u671f\u5f85\u5462\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u5c31\u662f\u4e2a\u6df7\u86cb\uff01\u201d</p></article>"}}}]);
