(window.webpackJsonp=window.webpackJsonp||[]).push([[548],{571:function(p){p.exports={chapterTitle:null,title:"\u7b2c404\u7ae0 \u5185\u90e8\u7684\u77db\u76fe",content:"<article><h2>\u7b2c404\u7ae0 \u5185\u90e8\u7684\u77db\u76fe</h2><p>\u3000\u3000\u5f20\u606a\u773c\u775b\u91cc\u6ca1\u6709\u592a\u591a\u7684\u8868\u60c5\uff0c\u53ea\u662f\u51b7\u6de1\u7684\u6ce8\u89c6\u7740\u5e02\u573a\u90e8\u534e\u5357\u529e\u4e8b\u5904\u4e0b\u5c5e\u7684\u5458\u5de5\u8096\u9038\u7fa4\uff0c\u8ba9\u4ed6\u89e3\u91ca\u4f55\u4ee5\u4f1a\u7406\u76f4\u6c14\u58ee\u7684\u5728\u529e\u516c\u5ba4\u91cc\u5bf9\u7740\u8d22\u52a1\u90e8\u526f\u603b\u76d1\u5b59\u7965\u4e1c\u5927\u56b7\u5927\u53eb\u3002</p><p>\u3000\u3000\u8096\u9038\u7fa4\u817f\u811a\u6709\u4e9b\u53d1\u8f6f\uff0c\u4ed6\u8fdb\u516c\u53f8\u4e00\u76f4\u5728\u8bbe\u4e8e\u5e7f\u5dde\u7684\u534e\u5357\u529e\u4e8b\u5904\u5de5\u4f5c\uff0c\u867d\u7136\u56de\u603b\u90e8\u7684\u673a\u4f1a\u4e5f\u4e0d\u5c11\uff0c\u4f46\u6ca1\u6709\u4e0e\u5f20\u606a\u78b0\u8fc7\u9762\uff0c\u76f4\u5230\u5218\u660e\u8f89\u627e\u5230\u4ed6\u8bad\u4e86\u4ed6\u4e00\u987f\uff0c\u624d\u6653\u5f97\u521a\u624d\u51b2\u649e\u7684\u5e74\u8f7b\u4eba\u7adf\u7136\u662f\u516c\u53f8\u7684\u5927\u8001\u677f\u3002</p><p>\u3000\u3000\u201c\u6211\u2026\u2026\u201d\u8096\u9038\u7fa4\u55eb\u5685\u4e86\u534a\u5929\uff0c\u6ca1\u6709\u5410\u51fa\u4e00\u53e5\u8bdd\u6765\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u521a\u624d\u7ed9\u5f20\u606a\u9876\u4e86\u4e24\u56de\uff0c\u8fd9\u65f6\u4e5f\u4e0d\u6562\u80e1\u4e71\u5f00\u53e3\u5e2e\u4ed6\u89e3\u91ca\u4ec0\u4e48\uff0c\u53ea\u662f\u4f4e\u5934\u770b\u7740\u5730\u9762\uff0c\u4e0b\u9762\u7684\u9500\u552e\u4e3b\u7ba1\u66f4\u4e0d\u6562\u542d\u58f0\uff0c\u76f4\u62ff\u773c\u775b\u770b\u7740\u4ed6\u4eec\u7684\u8001\u5927\u6768\u4e91\u3002\u6768\u4e91\u89c1\u5218\u660e\u8f89\u4f4e\u5934\u770b\u7740\u5730\u9762\uff0c\u4fbf\u5b66\u4ed6\u6a21\u6837\uff0c\u4f4e\u5934\u770b\u7740\u5730\u9762\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u6768\u4e91\u7684\u53cd\u5e94\u770b\u5728\u773c\u91cc\uff0c\u53ef\u4ee5\u770b\u51fa\u4ed6\u5bf9\u5218\u660e\u8f89\u662f\u6709\u4e0d\u6ee1\u7684\uff0c\u62ac\u624b\u6307\u4e86\u6307\u5b59\u7965\u4e1c\uff0c\u8bf4\u9053\uff1a\u201c\u5b59\u7ecf\u7406\uff0c\u4f60\u5c06\u5f15\u8d77\u8fd9\u6b21\u4e8b\u60c5\u7684\u7968\u636e\u62ff\u7ed9\u6211\u770b\u4e00\u770b\uff1f\u201d</p><p>\u3000\u3000\u5b59\u7965\u4e1c\u9a6c\u4e0a\u4ece\u5ea7\u4f4d\u4e0a\u7ad9\u8d77\u6765\uff0c\u8dd1\u5230\u5f20\u606a\u8eab\u4fa7\uff0c\u5c06\u6587\u4ef6\u5939\u9012\u7ed9\u4ed6\u9762\u524d\uff0c\u53d7\u4e86\u4e9b\u7a9d\u56ca\u6c14\uff0c\u8be5\u7528\u5230\u7684\u6750\u6599\u90fd\u51c6\u5907\u597d\u5728\u8fd9\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u62ff\u624b\u6307\u62e8\u7740\u7c98\u8d34\u5728\u4e00\u5f20\u7eb8\u4e0a\u7684\u5341\u591a\u5f20\u7968\u636e\uff0c\u8bf4\u9053\uff1a\u201c\u66f9\u65b0\u9633\uff0c\u8fd9\u5806\u53d1\u7968\u662f\u4f60\u7b7e\u5b57\u540c\u610f\u62a5\u9500\u7684\uff0c\u4f60\u6709\u4ec0\u4e48\u8bdd\u8981\u8bf4\uff1f\u201d\u5f20\u606a\u62ac\u5934\u770b\u4e86\u4e00\u773c\u4e00\u76f4\u57cb\u5934\u770b\u684c\u4e0a\u7b14\u8bb0\u672c\u7684\u534e\u5357\u529e\u4e8b\u5904\u4e3b\u4efb\u66f9\u65b0\u9633\u3002</p><p>\u3000\u3000\u201c\u54b3\uff0c\u201d\u66f9\u65b0\u9633\u8f7b\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u72b9\u8c6b\u7740\u7ad9\u4e86\u8d77\u6765\uff0c\u51b3\u5b9a\u5148\u5c06\u8d23\u4efb\u63fd\u4e0b\u6765\u518d\u8bf4\uff0c\u4e0d\u7136\u5f04\u50f5\u4e86\uff0c\u5927\u8001\u677f\u4e0b\u4e0d\u4e86\u53f0\uff0c\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u66f4\u6ca1\u6709\u53f0\u9636\u597d\u4e0b\uff0c\u8bf4\u9053\uff0c\u201c\u6709\u4e9b\u4e8b\u60c5\uff0c\u4e3b\u8981\u602a\u6211\u6ca1\u6709\u8ddf\u5c0f\u8096\u8bb2\u660e\u767d\uff0c\u5c0f\u8096\u813e\u6c14\u6709\u4e9b\u6025\u8e81\uff0c\u5de5\u4f5c\u5f53\u4e2d\u5bb9\u6613\u8ddf\u4eba\u8d77\u4e89\u6267\uff0c\u6211\u5c31\u7ed9\u8fd9\u5c0f\u5b50\u9876\u4e86\u5f88\u51e0\u56de\uff0c\u4ed6\u5bf9\u5b59\u7ecf\u7406\u6709\u4ec0\u4e48\u5bf9\u4e0d\u4f4f\u7684\u5730\u65b9\uff0c\u6211\u4ee3\u4ed6\u5411\u5b59\u7ecf\u7406\u9053\u6b49\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7a00\u6ce5\u5c31\u8fd9\u4e48\u548c\u4e0b\u53bb\uff1f\u201d\u5f20\u606a\u5c06\u5b59\u7965\u4e1c\u7684\u6587\u4ef6\u5939\u731b\u7136\u4e00\u5408\uff0c\u4e22\u5230\u66f9\u65b0\u9633\u9762\u524d\uff0c\u201c\u4f60\u81ea\u5df1\u770b\u770b\uff0c\u5218\u7ecf\u7406\u5e73\u65e5\u5de5\u4f5c\u5fd9\uff0c\u4f60\u7b7e\u5b57\u4e4b\u524d\u4e5f\u62bd\u4e0d\u51fa\u65f6\u95f4\u6765\u770b\u4e00\u773c\uff1f\u8fd8\u662f\u8bf4\u4f60\u6839\u672c\u5c31\u4e0d\u77e5\u9053\u516c\u53f8\u7684\u8d22\u52a1\u89c4\u7ae0\u5236\u5ea6\uff1f\u201d\u5f20\u606a\u624b\u538b\u7740\u684c\u9762\uff0c\u970d\u7136\u7ad9\u4e86\u8d77\u6765\uff0c\u770b\u7740\u534e\u5357\u529e\u4e8b\u5904\u4e0b\u5c5e\u7684\u9500\u552e\u4eba\u5458\u8096\u9038\u7fa4\uff0c\u201c\u4f60\u4f55\u4ee5\u7406\u76f4\u6c14\u58ee\u7684\u51b2\u7740\u5b59\u7ecf\u7406\u5927\u56b7\u5927\u53eb\uff1f\u201d\u773c\u795e\u5c16\u9510\u7684\u76ef\u7740\u8096\u9038\u7fa4\uff0c\u201c\u5e02\u573a\u90e8\u7684\u5458\u5de5\uff0c\u4ec0\u4e48\u65f6\u5019\u7ad9\u5728\u4eba\u9762\u524d\uff0c\u8fde\u4e00\u53e5\u8fa9\u9a73\u7684\u8bdd\u90fd\u4e0d\u6562\u8bf4\uff1f\u201d</p><p>\u3000\u3000\u8096\u9038\u7fa4\u7ed9\u4ed6\u8fd9\u4e48\u4e00\u76ef\uff0c\u5fc3\u91cc\u53d1\u865a\uff0c\u867d\u7136\u77e5\u9053\u773c\u524d\u8fd9\u4eba\u662f\u5927\u8001\u677f\uff0c\u800c\u4e14\u90a3\u4e9b\u4f20\u95fb\u4e5f\u795e\u4e4e\u5176\u795e\uff0c\u4f46\u6bd5\u7adf\u662f\u6bd4\u81ea\u5df1\u5e74\u8f7b\u5f97\u591a\u7684\u9752\u5e74\uff0c\u591a\u5c11\u6709\u4e9b\u4e0d\u7518\u5fc3\u5c31\u7ed9\u4ed6\u8fd9\u4e48\u4e00\u770b\u5c31\u53d1\u865a\u7834\u80c6\uff0c\u786c\u7740\u5934\u76ae\u8bf4\uff1a\u201c\u5176\u4ed6\u533a\u7684\u9500\u552e\u4eba\u5458\u4e5f\u8fd9\u4e48\u505a\uff0c\u8d22\u52a1\u90e8\u504f\u504f\u8ddf\u6211\u4eec\u5357\u7247\u7684\u4eba\u8fc7\u4e0d\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8c01\uff1f\u201d\u5f20\u606a\u624b\u6491\u7740\u684c\u9762\u7ad9\u8d77\u6765\uff0c\u76ef\u7740\u8096\u9038\u7fa4\uff0c\u201c\u4f60\u8bf4\u51fa\u6765\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u8096\u9038\u7fa4\u521a\u60f3\u5f20\u53e3\u8bf4\u4ec0\u4e48\uff0c\u5374\u7ed9\u5218\u660e\u8f89\u62ac\u5934\u4e25\u5389\u7684\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u624d\u60f3\u5230\u5e73\u65f6\u4e3b\u7ba1\u4e4b\u95f4\u7684\u7262\u9a9a\u8bdd\u600e\u4e48\u53ef\u80fd\u62ff\u5230\u53f0\u9762\u4e0a\u6765\u8bf4\uff1f\u53ea\u662f\u60f3\u6536\u56de\u90a3\u53e5\u8bdd\u5df2\u7ecf\u6765\u4e0d\u53ca\u3002</p><p>\u3000\u3000\u201c\u8bf4\uff0c\u201d\u5f20\u606a\u770b\u5230\u5218\u660e\u8f89\u77aa\u8096\u9038\u7fa4\u51cc\u5389\u7684\u773c\u795e\uff0c\u6307\u7740\u8096\u9038\u7fa4\uff0c\u201c\u4ec0\u4e48\u5357\u7247\uff0c\u5317\u7247\uff0c\u6211\u8fd8\u7b2c\u4e00\u6b21\u542c\u5230\u6709\u5357\u7247\u3001\u5317\u7247\u4e4b\u8bf4\uff0c\u4f60\u8bf4\uff0c\u4ec0\u4e48\u8bc1\u636e\u8bf4\u660e\u8d22\u52a1\u90e8\u53ea\u8ddf\u4f60\u4eec\u5357\u7247\u7684\u9500\u552e\u4eba\u5458\u8fc7\u4e0d\u53bb\uff1f\u4f60\u77e5\u9053\u5317\u7247\u8fd8\u6709\u8c01\u8fdd\u53cd\u516c\u53f8\u7684\u8d22\u52a1\u89c4\u7ae0\u5236\u5ea6\u62a5\u9500\u7968\u636e\uff0c\u201d\u5f20\u606a\u731b\u7136\u4e00\u62cd\u684c\u5b50\uff0c\u201c\u4f60\u8bf4\u51fa\u6765\uff0c\u6211\u53eb\u4ed6\u5148\u6eda\u51fa\u516c\u53f8\uff01\u201d</p><p>\u3000\u3000\u5468\u6e38\u62c5\u4efb\u65b0\u5149\u7eb8\u4e1a\u603b\u88c1\u4e4b\u524d\u63a8\u8350\u6768\u4e91\u63a5\u66ff\u4ed6\u7684\u4f4d\u5b50\uff0c\u8003\u8651\u5230\u5218\u660e\u8f89\u53ef\u80fd\u6709\u610f\u89c1\uff0c\u53ea\u8ba9\u6768\u4e91\u62c5\u4efb\u5e02\u573a\u90e8\u603b\u76d1\uff0c\u6682\u65f6\u4e0d\u5347\u526f\u603b\uff0c\u8fd8\u7279\u610f\u627e\u5218\u660e\u8f89\u3001\u6768\u4e91\u8c08\u8fc7\u8bdd\uff0c\u8ba9\u5218\u660e\u8f89\u8d1f\u8d23\u534e\u5357\u3001\u897f\u5357\u3001\u534e\u4e2d\u4e09\u4e2a\u5927\u533a\u7684\u5e02\u573a\uff0c\u8fd9\u4e48\u505a\u81ea\u7136\u6709\u5b89\u629a\u5218\u660e\u8f89\u4e4b\u610f\uff0c\u6ca1\u60f3\u5230\u5e02\u573a\u90e8\u5185\u90e8\u7adf\u516c\u7136\u6709\u4e86\u88c2\u75d5\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u8096\u9038\u7fa4\u6ca1\u6709\u80c6\u5b50\u518d\u5f00\u53e3\u8bf4\u8bdd\uff0c\u9759\u9ed8\u4e86\u4e00\u4f1a\u513f\uff0c\u770b\u7740\u53f0\u4e0b\u5750\u7740\u7684\u4f17\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u53f8\u4ec0\u4e48\u4e8b\u60c5\u5212\u5206\u5c71\u5934\u4e86\uff1f\u4ec0\u4e48\u65f6\u5019\u5206\u5357\u7247\u3001\u5317\u7247\u4e86\uff0c\u516c\u53f8\u8fd9\u4e48\u5927\u7684\u4e3e\u52a8\uff0c\u4e3a\u4ec0\u4e48\u6211\u4e0d\u77e5\u9053\uff1f\u6709\u8c01\u80fd\u544a\u8bc9\u6211\u4ec0\u4e48\u539f\u56e0\u5417\uff1f\u201d\u6bd4\u8d77\u5f20\u606a\u76db\u6012\u4e4b\u4e0b\u731b\u62cd\u684c\u5b50\uff0c\u4ed6\u6b64\u65f6\u7684\u5e73\u7f13\u51b7\u9759\u7684\u8bed\u8c03\u66f4\u8ba9\u4ed6\u4eba\u4e0d\u6562\u63a5\u4ed6\u7684\u8bdd\uff0c\u201c\u5728\u5ea7\u5f88\u591a\u4eba\u90fd\u662f\u641e\u5e02\u573a\u8425\u9500\u7684\u7cbe\u82f1\uff0c\u90fd\u8ba4\u4e3a\u73b0\u5728\u662f\u6e20\u9053\u4e3a\u738b\u7684\u65f6\u4ee3\uff0c\u6709\u4e86\u6e20\u9053\uff0c\u5c31\u6709\u4e86\u5e02\u573a\uff0c\u54c1\u724c\u53ef\u4ee5\u7092\u51fa\u6765\u3001\u53ef\u4ee5\u62ff\u94b1\u7838\u51fa\u6765\uff0c\u518d\u968f\u4fbf\u627e\u5bb6\u52a0\u5de5\u5382\u8d34\u724c\u5c31\u80fd\u751f\u4ea7\u51fa\u4ea7\u54c1\uff0c\u5c31\u80fd\u8d5a\u5230\u5927\u628a\u7684\u94b1\u3002\u9664\u4e86\u4f60\u4eec\uff0c\u5176\u4ed6\u4eba\u5bf9\u516c\u53f8\u505a\u7684\u8d21\u732e\u8fde\u8349\u82a5\u90fd\u4e0d\u5982\u3002\u5c31\u50cf\u5b59\u7ecf\u7406\uff0c\u8fd8\u4e0d\u90fd\u662f\u56e0\u4e3a\u4f60\u4eec\u505a\u51fa\u6765\u7684\u6210\u7ee9\uff0c\u4ed6\u4eec\u624d\u80fd\u62ff\u8fd9\u4e48\u9ad8\u7684\u85aa\u6c34\uff1f\u5b59\u7ecf\u7406\u5c1a\u4e14\u5982\u6b64\uff0c\u4f60\u4eec\u5bf9\u516c\u53f8\u5176\u4ed6\u540c\u4ec1\u5927\u56b7\u5927\u53eb\u4e0d\u662f\u66f4\u60c5\u6709\u53ef\u539f\uff1f\u201d\u5f20\u606a\u505c\u987f\u4e86\u4e00\u4f1a\u513f\uff0c\u770b\u7740\u53f0\u4e0b\u7684\u9500\u552e\u4e3b\u7ba1\u4eec\uff0c\u4ed6\u4eec\u8fd9\u65f6\u662f\u6ca1\u6709\u80c6\u5b50\u62ac\u8d77\u5934\u6765\u770b\u4ed6\uff0c\u4f46\u662f\u4ed6\u4eec\u5fc3\u91cc\u6709\u591a\u5c11\u4eba\u6709\u7740\u4e0d\u4ee5\u4e3a\u7136\u7684\u60f3\u6cd5\u5c31\u4e0d\u5f97\u800c\u77e5\uff0c\u51b7\u51bd\u7684\u7b11\u4e86\u7b11\uff0c\u201c\u5404\u4f4d\u90fd\u6709\u5f88\u806a\u660e\u7684\u5934\u8111\uff0c\u4ec0\u4e48\u5927\u9053\u7406\u4e0d\u7528\u6211\u6765\u8bf4\uff0c\u6211\u53ef\u4ee5\u5f88\u660e\u786e\u7684\u6b63\u544a\u5728\u5ea7\u7684\u5404\u4f4d\uff0c\u5e02\u573a\u90e8\u662f\u5f88\u91cd\u8981\uff0c\u4f46\u5728\u6211\u7684\u773c\u91cc\uff0c\u5374\u4e0d\u662f\u5176\u4ed6\u90e8\u95e8\u91cd\u8981\u4e00\u5206\uff0c\u8c01\u81ea\u4ee5\u4e3a\u638c\u63e1\u4e86\u6e20\u9053\u5c31\u638c\u63e1\u4e86\u516c\u53f8\u7684\u547d\u8109\uff0c\u6211\u7edd\u4e0d\u633d\u7559\u4ed6\u3002\u201d\u5f20\u606a\u5c06\u8bb0\u5f55\u672c\u4e00\u5408\uff0c\u5bf9\u4e01\u69d0\u8bf4\uff0c\u201c\u6211\u4eec\u8d70\uff0c\u8ba9\u4ed6\u4eec\u5185\u90e8\u81ea\u5df1\u597d\u597d\u53cd\u601d\u4e00\u4e0b\u3002\u201d\u62ff\u8d77\u8bb0\u5f55\u672c\u8d70\u51fa\u4f1a\u8bae\u5ba4\uff0c\u4e01\u69d0\u8ddf\u5728\u540e\u9762\uff0c\u82cf\u6d25\u4e1c\u4e0e\u848b\u8587\u3001\u5468\u4e00\u5e73\u9762\u9762\u76f8\u89d1\uff0c\u5f20\u606a\u4e34\u8d70\u65f6\u8bf4\u8981\u4ed6\u4eec\u5185\u90e8\u5148\u53cd\u601d\uff0c\u4e5f\u4e0d\u4fbf\u53bb\u62c9\u4f4f\uff0c\u53ea\u5f97\u6b20\u7740\u8eab\u5b50\u9001\u5f20\u606a\u4ed6\u4eec\u79bb\u5f00\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u53bb\u54ea\u91cc\uff1f\u201d\u4e01\u69d0\u8ddf\u7740\u5f20\u606a\u5750\u4e0a\u8f66\uff0c\u73b0\u5728\u7814\u53d1\u4e2d\u5fc3\u5206\u62c6\u51fa\u53bb\uff0c\u4e0d\u7136\u4ed6\u4e5f\u5f97\u7559\u5728\u4f1a\u8bae\u5ba4\u8ddf\u7740\u53cd\u601d\uff0c\u4ed6\u4ece\u6ca1\u6709\u89c1\u5f20\u606a\u53d1\u8fd9\u4e48\u5927\u706b\uff0c\u8ddf\u7740\u51fa\u6765\uff0c\u4e00\u76f4\u5230\u4e0a\u8f66\u624d\u95ee\u5f20\u606a\u4e00\u53e5\u8bdd\u3002</p><p>\u3000\u3000\u201c\u5148\u4e0a\u6765\u518d\u8bf4\uff0c\u201d\u5f20\u606a\u62db\u624b\u8ba9\u4e01\u69d0\u4e0a\u6765\uff0c\u624d\u8ddf\u5085\u4fca\u8bf4\uff0c\u201c\u53bb\u5c0f\u6c5f\u9c7c\u9986\u5403\u5200\u9c7c\uff0c\u6211\u4eca\u5929\u5f97\u597d\u597d\u7684\u8010\u7740\u6027\u5b50\u5403\u5200\u9c7c\u624d\u4e0d\u80fd\u7ed9\u9c7c\u523a\u5361\u7740\u5589\u5499\u3002\u201d</p><p>\u3000\u3000\u201c\u6709\u4e9b\u4e8b\uff0c\u8001\u82cf\u4e5f\u6709\u96be\u5904\uff0c\u201d\u4e01\u69d0\u89c1\u5f20\u606a\u8fd8\u6709\u5fc3\u60c5\u8bf4\u51b7\u7b11\u8bdd\uff0c\u7f13\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff0c\u201c\u95ee\u9898\u4e0d\u5927\uff0c\u8001\u82cf\u5c31\u51b3\u5b9a\u5148\u538b\u7740\u4e0d\u544a\u8bc9\u4f60\uff0c\u6211\u4e5f\u662f\u8fd9\u4e48\u60f3\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u6709\u4eba\u7684\u5730\u65b9\u5c31\u6709\u6c5f\u6e56\uff0c\u201d\u5f20\u606a\u8f7b\u8f7b\u5482\u4e86\u5482\u5634\uff0c\u201c\u516c\u53f8\u81ea\u7136\u4e5f\u4f1a\u6709\u516c\u53f8\u653f\u6cbb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u90fd\u6e05\u695a\uff1f\u201d\u4e01\u69d0\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u90fd\u95f9\u6210\u8fd9\u6837\u5b50\u4e86\uff0c\u6211\u8fd8\u80fd\u4e0d\u660e\u767d\uff1f\u201d\u5f20\u606a\u6487\u4e86\u6487\u5634\uff0c\u5f53\u521d\u521b\u529e\u7231\u8fbe\u7535\u5b50\u7684\u65f6\u5019\uff0c\u5c06\u5218\u660e\u8f89\u4e0e\u848b\u8587\u4ece\u9526\u6e56\u501f\u51fa\u6765\u5e2e\u81ea\u5df1\uff0c\u8fd8\u662f\u5218\u660e\u8f89\u5728\u5408\u80a5\u627e\u5230\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4e24\u4eba\u3002\u968f\u7740\u7231\u8fbe\u7535\u5b50\u7684\u53d1\u5c55\uff0c\u6bcf\u4e2a\u4eba\u5728\u516c\u53f8\u7684\u5b9a\u4f4d\u5c31\u4f1a\u51fa\u73b0\u843d\u5dee\uff0c\u5218\u660e\u8f89\u7684\u80fd\u529b\u8fd8\u4e0d\u80fd\u72ec\u6321\u4e00\u9762\uff0c\u53bb\u5e74\u4ed6\u5728\u5e7f\u5dde\u5c31\u95f9\u8fc7\u4e00\u6b21\u522b\u626d\uff0c\u60f3\u5fc5\u6768\u4e91\u9876\u66ff\u5468\u6e38\u62c5\u4efb\u5e02\u573a\u90e8\u603b\u76d1\u4e4b\u540e\uff0c\u5fc3\u91cc\u7684\u610f\u89c1\u66f4\u5927\u3002\u82cf\u6d25\u4e1c\u867d\u7136\u662f\u603b\u7ecf\u7406\uff0c\u7ba1\u675f\u5176\u4ed6\u4eba\u8fd8\u5bb9\u6613\u4e00\u4e9b\uff0c\u4f46\u662f\u5bf9\u5218\u660e\u8f89\u3001\u848b\u8587\u3001\u5468\u4e00\u5e73\u3001\u6768\u4e91\u8fd9\u4e9b\u4eba\uff0c\u90fd\u65e0\u6cd5\u843d\u4e0b\u8138\u6765\u53bb\u4e25\u683c\u7ba1\u675f\u7684\u3002\u5f20\u606a\u4eca\u5929\u4e5f\u662f\u72e0\u4e0b\u5fc3\u501f\u9898\u6253\u538b\u4e00\u4e0b\u5e02\u573a\u90e8\u7684\u6c14\u7130\uff0c\u8ba9\u4ed6\u4eec\u5185\u90e8\u5148\u53cd\u601d\u53cd\u601d\uff0c\u7136\u540e\u518d\u627e\u673a\u4f1a\u51fa\u9762\u5b89\u629a\u4e00\u4e0b\u5218\u660e\u8f89\uff0c\u8981\u662f\u4ed6\u4e0d\u7518\u5fc3\u5c48\u5c45\u6768\u4e91\u4e4b\u4e0b\uff0c\u8c03\u4ed6\u53bb\u65b0\u5149\u7eb8\u4e1a\u7ed9\u5468\u6e38\u5f53\u526f\u624b\u4e5f\u884c\uff0c\u603b\u4e0d\u80fd\u5728\u540c\u4e00\u90e8\u95e8\u5185\u90e8\u641e\u4e24\u4e2a\u5c71\u5934\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u4e01\u69d0\u8fd8\u6709\u5085\u4fca\u53bb\u642c\u5230\u65b0\u5740\u7684\u5c0f\u6c5f\u9c7c\u9986\u5403\u9c7c\u5bb4\uff0c\u6070\u5de7\u9c7c\u9986\u91cc\u4eca\u5929\u8fd8\u771f\u6709\u51e0\u5c3e\u5200\u9c7c\uff0c\u4e5d\u516d\u5e74\uff0c\u5c0f\u6c5f\u91cc\u7684\u91ce\u751f\u5200\u9c7c\u5df2\u7ecf\u5f88\u7f55\u89c1\u4e86\uff0c\u60f3\u8981\u5403\u5230\u4e5f\u662f\u53ef\u9047\u4e0d\u53ef\u6c42\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u516b\u70b9\u949f\u5de6\u53f3\uff0c\u4e01\u69d0\u63a5\u5230\u82cf\u6d25\u4e1c\u7684\u7535\u8bdd\uff0c\u4e01\u69d0\u8ddf\u5f20\u606a\u8bf4\uff1a\u201c\u4ed6\u4eec\u4f1a\u5f00\u5b8c\u4e86\uff0c\u8001\u82cf\u95ee\u6211\u8ddf\u4e0d\u8ddf\u4f60\u5728\u4e00\u8d77\u3002\u201d</p><p>\u3000\u3000\u201c\u8ba9\u4ed6\u4eec\u8fc7\u6765\u5427\uff0c\u5200\u9c7c\u6211\u4eec\u5403\u5b8c\u4e86\uff0c\u7ed9\u4ed6\u4eec\u968f\u4fbf\u6dfb\u4e9b\u4ec0\u4e48\u83dc\u3002\u201d\u5f20\u606a\u5c31\u5750\u5728\u5305\u53a2\u95e8\u8fb9\uff0c\u6253\u5f00\u95e8\u62db\u547c\u5e97\u8001\u677f\u9001\u83dc\u5355\u8fdb\u6765\u3002</p><p>\u3000\u3000\u7b49\u4e86\u7247\u523b\uff0c\u82cf\u6d25\u4e1c\u4e0e\u6768\u4e91\u4fe9\u4eba\u8d76\u4e86\u8fc7\u6765\uff0c\u82cf\u6d25\u4e1c\u8bf4\u9053\uff1a\u201c\u516c\u53f8\u9910\u5385\u672c\u6765\u6709\u8ba1\u5212\u5b89\u6392\u805a\u9910\uff0c\u4f1a\u540e\u770b\u5927\u5bb6\u90fd\u6ca1\u6709\u5fc3\u60c5\uff0c\u5c31\u8ba9\u4ed6\u4eec\u90fd\u6563\u4e86\uff0c\u6211\u8ddf\u6768\u4e91\u8fc7\u6765\u8ddf\u4f60\u68c0\u8ba8\u6765\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u6ca1\u7ed9\u4f60\u4eec\u558a\u9152\uff0c\u5148\u6252\u4e24\u53e3\u996d\u586b\u586b\u809a\u5b50\u5427\uff0c\u201d\u5f20\u606a\u89c1\u5218\u660e\u8f89\u6ca1\u6709\u8fc7\u6765\uff0c\u82cf\u6d25\u4e1c\u4e0e\u6768\u4e91\u4e5f\u6ca1\u6709\u8bf4\u4ed6\u7684\u4e0d\u662f\uff0c\u8ba9\u4ed6\u4eec\u5148\u5750\u4e0b\u6765\u5403\u996d\uff0c\u201c\u5e02\u573a\u90e8\u73b0\u5728\u8fd9\u6837\u5b50\uff0c\u4f60\u4eec\u4fe9\u5f53\u7136\u662f\u6709\u8d23\u4efb\u7684\uff0c\u4f46\u662f\u4e5f\u4e0d\u8981\u66ff\u8c01\u63fd\u8d23\u4efb\uff0c\u6211\u4e5f\u6709\u8003\u8651\u4e0d\u5468\u3002\u73b0\u5728\u80fd\u770b\u51fa\u82d7\u5934\u4e5f\u4e3a\u65f6\u4e0d\u665a\uff0c\u660e\u5e74\u7684\u5e02\u573a\u60c5\u51b5\u4f1a\u5f88\u590d\u6742\uff0c\u6709\u4e9b\u95ee\u9898\u603b\u4e0d\u80fd\u62d6\u5230\u660e\u5e74\u518d\u5904\u7406\u3002\u201d</p></article>"}}}]);
