(window.webpackJsonp=window.webpackJsonp||[]).push([[549],{572:function(p){p.exports={chapterTitle:null,title:"\u7b2c549\u7ae0 \u5750\u4e0d\u4f4f",content:"<article><h2>\u7b2c549\u7ae0 \u5750\u4e0d\u4f4f</h2><p>\u3000\u3000\u98de\u673a\u8d8a\u8fc7\u575a\u5b9e\u539a\u91cd\u611f\u7684\u4e91\u5c42\uff0c\u4ece\u8237\u7a97\u671b\u4e0b\u53bb\uff0c\u5915\u9633\u6295\u5c04\u800c\u6210\u7684\u98de\u673a\u5f71\u5b50\u5728\u6e5b\u84dd\u7684\u6d77\u6c34\u4e0a\u6d6e\u63a0\uff0c\u9ec4\u660f\u65f6\u5206\u7684\u6d77\u6d0b\u6709\u7740\u96be\u4ee5\u8a00\u55bb\u7684\u7f8e\u3002</p><p>\u3000\u3000\u5f20\u606a\u6536\u56de\u89c6\u7ebf\uff1b\u8bb8\u601d\u5750\u5728\u9760\u8fc7\u9053\u7684\u5ea7\u4f4d\u4e0a\uff0c\u80f3\u818a\u8098\u513f\u652f\u5728\u6276\u624b\u4e0a\uff0c\u624b\u6258\u7740\u4e0b\u5df4\uff0c\u51dd\u7738\u4e13\u6ce8\u7684\u770b\u7740\u8237\u7a97\u5916\u7684\u6e5b\u84dd\u6d77\u6d0b\uff0c\u633d\u7740\u53d1\u9afb\u7684\u7cbe\u81f4\u5934\u9885\u6a2a\u5728\u5f20\u606a\u80f8\u524d\uff0c\u6234\u7740\u9ed1\u8272\u7684\u73b3\u7441\u6846\u5e73\u5149\u955c\uff0c\u9ad8\u9ad8\u6311\u8d77\u7684\u773c\u776b\u6bdb\u51e0\u4e4e\u62b5\u5230\u900f\u660e\u7684\u955c\u7247\uff0c\u5f20\u606a\u4f38\u624b\u5c06\u5979\u7684\u73b3\u7441\u6846\u773c\u955c\u6458\u4e0b\u6765\uff0c\u624b\u6795\u7740\u8111\u888b\uff0c\u4ed4\u7ec6\u6b23\u8d4f\u8fd9\u4e00\u5e45\u8ba9\u4eba\u5fc3\u60b8\u7684\u9500\u9b42\u5bb9\u989c\u3002</p><p>\u3000\u3000\u201c\u770b\u4ec0\u4e48\uff1f\u201d\u65e0\u8bba\u591a\u719f\u6089\uff0c\u5728\u5f20\u606a\u7684\u51dd\u89c6\u4e0b\uff0c\u8bb8\u601d\u79cb\u6c34\u6e05\u6f88\u7684\u773c\u7738\u91cc\u51dd\u7740\u4e9b\u8bb8\u7f9e\u6da9\uff0c\u8981\u907f\u5f00\u4ed6\u707c\u4eba\u7684\u773c\u795e\uff0c\u4fbf\u6795\u7740\u4ed6\u7684\u80a9\u5934\u5c0f\u58f0\u8bf4\u8bdd\uff0c\u201c\u4f60\u8ddf\u9648\u9759\u901a\u7535\u8bdd\u65f6\uff0c\u8bf4\u8c22\u5251\u5357\u8fd9\u6b21\u592a\u806a\u660e\u4e86\uff0c\u6211\u6ca1\u6709\u60f3\u660e\u767d\u5462\uff0c\u9648\u9759\u5728\u7535\u8bdd\u91cc\u8ddf\u4f60\u8bf4\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u5979\u5012\u6ca1\u6709\u8bf4\u592a\u591a\uff0c\u201d\u5f20\u606a\u55c5\u7740\u6de1\u6de1\u7684\u53d1\u9999\uff0c\u5fae\u4fa7\u8fc7\u5934\u6765\uff0c\u4e0b\u5df4\u8f7b\u8f7b\u538b\u5728\u8bb8\u601d\u7684\u79c0\u53d1\u4e0a\uff0c\u8bf4\u9053\uff0c\u201c\u79d1\u738b\u7684\u6d77\u5916\u516c\u53f8\u662f\u79d1\u738b\u5404\u5bb6\u80a1\u4e1c\u6309\u6bd4\u4f8b\u6ce8\u8d44\u4e00\u4ebf\u6e2f\u5143\u6ce8\u518c\u6210\u7acb\uff0c\u8d1f\u8d23\u66ff\u79d1\u738b\u5f00\u62d3\u6d77\u5916\u5e02\u573a\u3002\u4e00\u822c\u8bf4\u6765\uff0c\u79d1\u738b\u7684\u6d77\u5916\u516c\u53f8\u4e0e\u79d1\u738b\u4e4b\u95f4\u4e0d\u5e94\u8be5\u6709\u5b9e\u9645\u6027\u7684\u5229\u76ca\u51b2\u7a81\uff0c\u4f46\u662f\u4e8b\u60c5\u5e76\u975e\u7edd\u5bf9\uff0c\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u5bf9\u79d1\u738b\u7684\u6301\u80a1\u662f\u901a\u8fc7\u5609\u4fe1\u7535\u5b50\u8fdb\u884c\u7684\uff0c\u7136\u800c\u5bf9\u79d1\u738b\u6d77\u5916\u516c\u53f8\u7684\u6301\u80a1\uff0c\u5374\u662f\u845b\u836b\u5747\u4ee5\u79c1\u4eba\u540d\u4e49\u76f4\u63a5\u6ce8\u8d44\uff0c\u8fd9\u5f53\u4e2d\u6216\u8bb8\u6709\u845b\u660e\u4fe1\u60f3\u501f\u673a\u5c06\u845b\u836b\u5747\u57f9\u517b\u6210\u63a5\u73ed\u4eba\u7684\u610f\u56fe\uff0c\u5374\u4e5f\u9020\u6210\u67d0\u79cd\u5229\u76ca\u4e0a\u7684\u5931\u8861\uff0c\u8fd8\u6709\u4e00\u70b9\u53ef\u80fd\u4e5f\u662f\u76f8\u5f53\u81f4\u547d\u7684\uff0c\u90a3\u5c31\u662f\u845b\u836b\u5747\u4e2a\u4eba\u5bf9\u79d1\u738b\u7684\u5f71\u54cd\u529b\u975e\u5e38\u5f31\uff0c\u4f46\u662f\u5bf9\u6d77\u5916\u516c\u53f8\u7684\u63a7\u5236\u529b\u5374\u7279\u522b\u7684\u5f3a\uff0c\u8fd9\u672c\u8eab\u5c31\u662f\u4e00\u79cd\u5931\u8861\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u2026\u2026\u4f60\u90a3\u5929\u63d0\u9192\u9648\u9759\u4e5f\u4e0d\u662f\u597d\u610f\u55bd\uff1f\u201d\u8bb8\u601d\u7cbe\u81f4\u7684\u5934\u9885\u6ca1\u6709\u79bb\u5f00\u5f20\u606a\u7684\u80a9\u8180\uff0c\u800c\u662f\u4e0b\u5df4\u9ad8\u9ad8\u626c\u8d77\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u5c06\u9648\u9759\u5f53\u6210\u670b\u53cb\u5bf9\u5f85\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u7684\u63d0\u9192\u672a\u5fc5\u6ca1\u6709\u6076\u610f\uff0c\u4f46\u6076\u610f\u5374\u4e0d\u662f\u9488\u5bf9\u5927\u96c5\u96c6\u56e2\uff0c\u6240\u4ee5\u4e0d\u80fd\u7b97\u5bf9\u4e0d\u8d77\u670b\u53cb\u5427\uff1f\u201d\u5f20\u606a\u6d45\u7b11\u7740\uff0c\u201c\u9648\u9759\u5728\u7535\u8bdd\u91cc\u544a\u8bc9\u6211\uff0c\u5927\u96c5\u96c6\u56e2\u5c06\u8003\u8651\u6536\u7f29\u5bf9\u79d1\u738b\u6d77\u5916\u516c\u53f8\u7684\u6301\u80a1\uff0c\u64a4\u80a1\u90fd\u6709\u53ef\u80fd\u3002\u6211\u60f3\u4ed6\u4eec\u5e94\u8be5\u5bf9\u4e1c\u5357\u4e9a\u7684\u5e02\u573a\u7b56\u7565\u6709\u8fc7\u4e00\u756a\u4e89\u6267\u3001\u610f\u89c1\u5f97\u4e0d\u5230\u7edf\u4e00\u4e4b\u540e\uff0c\u624d\u4f1a\u6709\u8fd9\u6837\u7684\u8003\u8651\u5427\uff1f\u5e02\u573a\u9ad8\u901f\u6269\u5f20\u671f\u60f3\u8981\u4fdd\u6301\u7a33\u5065\u7684\u8d22\u52a1\u662f\u5f88\u56f0\u96be\u7684\u4e8b\u60c5\u3002\u5927\u96c5\u96c6\u56e2\u7684\u7ecf\u8425\u4e0e\u6295\u8d44\u601d\u8def\u76f8\u5bf9\u4fdd\u5b88\uff0c\u518d\u6709\u6211\u2018\u989d\u5916\u2019\u7684\u63d0\u9192\uff0c\u8981\u662f\u79d1\u738b\u5404\u5927\u80a1\u4e1c\u5bf9\u4e1c\u5357\u4e9a\u5e02\u573a\u7b56\u7565\u6709\u5206\u6b67\uff0c\u9648\u9759\u6240\u4ee3\u8868\u7684\u5927\u96c5\u96c6\u56e2\u9009\u62e9\u7ad9\u5728\u54ea\u4e00\u8fb9\uff0c\u51e0\u4e4e\u662f\u53ef\u4ee5\u80af\u5b9a\u7684\u3002\u8003\u8651\u5230\u79d1\u738b\u6d77\u5916\u516c\u53f8\u7684\u6027\u8d28\uff0c\u6709\u6211\u8fd9\u4e48\u4e00\u6311\u62d4\u79bb\u95f4\uff0c\u5927\u96c5\u96c6\u56e2\u64a4\u51fa\u6765\u7684\u53ef\u80fd\u6027\u5c31\u76f8\u5f53\u5927\u4e86\uff0c\u4f46\u662f\u54ea\u4e00\u65b9\u4f1a\u575a\u6301\u6fc0\u8fdb\u7684\u5e02\u573a\u6269\u5f20\u8def\u7ebf\uff1f\u76ee\u524d\u7684\u9636\u6bb5\uff0c\u79d1\u738b\u653e\u5f03\u4e1c\u5357\u4e9a\u5e02\u573a\u662f\u4e0d\u53ef\u80fd\u7684\uff0c\u5728\u8fd9\u4e4b\u524d\uff0c\u6211\u8fd8\u6ca1\u6709\u60f3\u5230\u6b63\u6cf0\u96c6\u56e2\u8fd9\u6b21\u4e5f\u4f1a\u8003\u8651\u6536\u7f29\u5bf9\u79d1\u738b\u7684\u6301\u80a1\uff0c\u6211\u542c\u9648\u9759\u8bf4\u6b63\u6cf0\u96c6\u56e2\u4e5f\u4f1a\u4e00\u5e76\u8003\u8651\u7f29\u80a1\uff0c\u6240\u4ee5\u8bf4\u8c22\u5251\u5357\u8fd9\u4eba\u592a\u806a\u660e\u4e86\uff0c\u5f53\u7136\u4e86\uff0c\u4e5f\u4e0d\u4e00\u5b9a\u5c31\u662f\u8c22\u5251\u5357\u4e00\u4eba\u7684\u7cbe\u660e\uff0c\u4ed6\u80cc\u540e\u7684\u8c22\u6c49\u9756\u3001\u8c22\u6c49\u9756\u3001\u5468\u747e\u73ba\u54ea\u4e2a\u662f\u80af\u5403\u4e00\u70b9\u4e8f\u7684\u4eba\u7269\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u201d\u8bb8\u601d\u500f\u7684\u5750\u76f4\u8eab\u5b50\uff0c\u201c\u4f60\u662f\u8bf4\u4ed6\u4eec\u4e5f\u55c5\u5230\u5371\u673a\u7684\u6c14\u606f\uff0c\u8981\u8ba9\u845b\u836b\u5747\u9876\u5728\u524d\u9762\u627f\u62c5\u5feb\u901f\u5f00\u62d3\u6d77\u5916\u5e02\u573a\u7684\u98ce\u9669\uff1f\u201d</p><p>\u3000\u3000\u201c\u5e94\u8be5\u591a\u5c11\u6709\u4e00\u4e9b\u8fd9\u4e2a\u56e0\u7d20\uff0c\u8fd9\u4e5f\u7b26\u5408\u8c22\u5bb6\u7684\u4e00\u8d2f\u4f5c\u98ce\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u5f53\u7136\uff0c\u8fd8\u53ef\u80fd\u6709\u53e6\u5916\u7684\u56e0\u7d20\u3002\u96be\u9053\u8c22\u5251\u5357\u5c31\u4e0d\u60f3\u5c06\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u66f4\u6df1\u5165\u7684\u8bf1\u8fdb\u9526\u6e56\u4e0e\u4ed6\u4eec\u6b63\u6cf0\u96c6\u56e2\u4e4b\u95f4\u7684\u6069\u6028\u91cc\u6765\uff1f\u4f46\u662f\uff0c\u4ed6\u4eec\u4e0d\u53ef\u80fd\u8ba9\u5609\u4fe1\u7535\u5b50\u518d\u589e\u52a0\u5bf9\u79d1\u738b\u7684\u6295\u8d44\uff0c\u90a3\u6837\u4f1a\u8ba9\u4ed6\u4eec\u5931\u53bb\u5bf9\u79d1\u738b\u7684\u63a7\u5236\u6743\uff0c\u6d77\u5916\u516c\u53f8\u662f\u9644\u5eb8\u4e8e\u79d1\u738b\u7684\uff0c\u662f\u8d1f\u8d23\u5f00\u62d3\u79d1\u738b\u6d77\u5916\u5e02\u573a\u7684\uff0c\u5373\u4f7f\u6d77\u5916\u516c\u53f8\u8ba9\u845b\u836b\u5747\u7edd\u5bf9\u63a7\u80a1\uff0c\u4e5f\u6539\u53d8\u4e0d\u4e86\u6d77\u5916\u516c\u53f8\u9644\u5eb8\u4e8e\u79d1\u738b\u7684\u4e8b\u5b9e\uff0c\u4f46\u662f\u5374\u53ef\u4ee5\u8ba9\u6d77\u5916\u516c\u53f8\u501f\u52a9\u845b\u6c0f\u7236\u5b50\u96c4\u539a\u7684\u8d44\u91d1\u5b9e\u529b\u6765\u8fc5\u901f\u5f00\u62d3\u6d77\u5916\u5e02\u573a\uff1b\u5f53\u7136\u4e86\uff0c\u5c31\u7b97\u6709\u4ec0\u4e48\u635f\u5931\uff0c\u4e5f\u662f\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u627f\u62c5\u5f97\u591a\u2026\u2026\u201d</p><p>\u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5f20\u606a\u7b11\u4e86\u7b11\uff1a\u201c\u4f60\u8bf4\u8c22\u5251\u5357\u4ed6\u4eec\u806a\u660e\u4e0d\u806a\u660e\uff1f\u4ed6\u4eec\u7b80\u76f4\u662f\u592a\u806a\u660e\u4e86\u2026\u2026\u8c01\u8981\u662f\u8ddf\u4ed6\u4eec\u5408\u4f5c\uff0c\u591c\u91cc\u90fd\u7761\u4e0d\u5b89\u7a33\u7684\uff0c\u6211\u4eec\u53ef\u662f\u65e9\u5c31\u5f97\u5230\u6559\u8bad\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u5f97\u4e86\u4fbf\u5b9c\u8fd8\u5356\u4e8f\u2026\u2026\u201d\u8bb8\u601d\u6a2a\u76ee\u987e\u76fc\u751f\u59ff\uff0c\u201c\u662f\u4ed6\u4eec\u5728\u4f60\u8fd9\u91cc\u5f97\u5230\u6559\u8bad\u5427\uff0c\u4e0d\u7136\u4ed6\u4eec\u672a\u5fc5\u80af\u51cf\u5c11\u5bf9\u6d77\u5916\u516c\u53f8\u7684\u6301\u80a1\uff0c\u6bd5\u7adf\u6d77\u5916\u516c\u53f8\u8981\u5206\u6389\u4ed6\u4eec\u4e00\u90e8\u5206\u5229\u6da6\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e5f\u5bf9\uff0c\u201d\u5f20\u606a\u9f3b\u5934\u8d34\u7740\u8bb8\u601d\u7684\u79c0\u53d1\u6709\u4e9b\u75d2\uff0c\u4f38\u624b\u6478\u4e86\u6478\uff0c\u201c\u4ed6\u4eec\u8fd9\u4e9b\u4eba\uff0c\u6bd5\u7adf\u662f\u4e0d\u5bb9\u5ffd\u89c6\u7684\u89d2\u8272\u2026\u2026\u53ef\u60dc\u554a\uff0c\u6211\u5c06\u4ed6\u4eec\u770b\u900f\u4e86\u6ca1\u6709\u7528\uff0c\u8981\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u5c06\u4ed6\u4eec\u770b\u900f\uff0c\u72d7\u54ac\u72d7\u54ac\u8d77\u6765\u624d\u597d\u73a9\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u5c31\u662f\u597d\u6597\uff0c\u201d\u8bb8\u601d\u7ec6\u58f0\u5a07\u55d4\u9053\uff0c\u201c\u4f60\u5c31\u6ca1\u60f3\u7740\u8981\u5c06\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u5b8c\u5168\u626f\u8fdb\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u597d\u6597\u597d\u554a\uff0c\u4e00\u6f6d\u6b7b\u6c34\uff0c\u6162\u6162\u53d1\u5c55\uff0c\u6d77\u5dde\u7684\u7535\u5b50\u5de5\u4e1a\u5230\u4ec0\u4e48\u5e74\u4ee3\u624d\u4f1a\u80fd\u6210\u89c4\u6a21\uff1f\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u73b0\u5728\u4e0d\u80fd\u7b97\u89c4\u6a21\uff1f\u201d\u8bb8\u601d\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4e5f\u7b97\uff0c\u4e0d\u8fc7\u4ea7\u4e1a\u592a\u5355\u4e00\u4e86\uff0c\u8981\u5f62\u6210\u7e41\u76db\u7684\u4ea7\u4e1a\u68ee\u6797\uff0c\u5dee\u8ddd\u592a\u8fdc\u4e86\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u98de\u673a\u98de\u4e34\u5efa\u90ba\u4e0a\u7a7a\uff0c\u673a\u7ffc\u4e0b\u662f\u6ee1\u57ce\u7e41\u661f\u4f3c\u7684\u706f\u706b\uff0c\u4e0b\u98de\u673a\u5230\u884c\u5b50\u8f93\u9001\u67b6\u524d\u7b49\u7740\u62ff\u884c\u674e\uff0c\u900f\u8fc7\u73bb\u7483\uff0c\u5f20\u606a\u770b\u89c1\u67f3\u5fd7\u519b\u7ad9\u5728\u5916\u9762\u7684\u5927\u5385\u91cc\u671d\u8fd9\u8fb9\u6325\u624b\uff1b\u516c\u53f8\u90a3\u91cc\u9a6c\u6d77\u9f99\u4eb2\u81ea\u5f00\u8f66\u6765\u63a5\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u519b\u65c1\u8fb9\u8fd8\u7ad9\u7740\u4e00\u4e2a\u4eba\uff0c\u5f20\u606a\u4e5f\u8ba4\u8bc6\uff0c\u662f\u53bb\u5e74\u5165\u79cb\u966a\u5510\u5a67\u5230\u4e1c\u6d77\u5927\u5b66\u65b0\u6d66\u6821\u533a\u770b\u671b\u9648\u5983\u84c9\u65e0\u610f\u95f4\u5728\u6c5f\u8fb9\u9047\u5230\u7684\u65b0\u6d66\u533a\u59d4\u4e66\u8bb0\u3001\u65b0\u6d66\u9ad8\u65b0\u79d1\u6280\u5de5\u4e1a\u56ed\u533a\u4e3b\u4efb\u9ec4\u514b\u7fa4\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u60f3\u5230\u67f3\u5fd7\u519b\u8981\u89c1\u81ea\u5df1\u662f\u6253\u7b97\u5f15\u8350\u9ec4\u514b\u7fa4\uff0c\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u4ed6\u80fd\u731c\u51fa\u5230\u9ec4\u514b\u7fa4\u51fa\u73b0\u5728\u8fd9\u91cc\u7684\u610f\u56fe\uff0c\u8fd9\u5f88\u597d\u731c\uff0c\u9ec4\u514b\u7fa4\u662f\u65b0\u6d66\u9ad8\u65b0\u56ed\u533a\u4e3b\u4efb\u3001\u515a\u7ec4\u4e66\u8bb0\u7684\u8eab\u4efd\uff0c\u5c31\u8868\u660e\u4ed6\u51fa\u73b0\u5728\u8fd9\u91cc\u7684\u610f\u56fe\u5c31\u662f\u5954\u9526\u6e56\u7684\u79d1\u6280\u56ed\u9879\u76ee\u6765\u7684\u3002</p><p>\u3000\u3000\u4f46\u662f\u9ec4\u514b\u7fa4\u4e3a\u4ec0\u4e48\u80fd\u627e\u5230\u67f3\u5fd7\u519b\u5f53\u5f15\u8350\u4eba\uff0c\u800c\u8fd9\u4ef6\u4e8b\u662f\u4e0d\u662f\u5f97\u5230\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u7684\u9996\u80af\uff0c\u5f20\u606a\u4e00\u65f6\u95f4\u5c31\u731c\u4e0d\u900f\u4e86\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5f20\u606a\u4e5f\u6709\u4e00\u70b9\u5acc\u9ec4\u514b\u7fa4\u7ea7\u522b\u5dee\u4e86\u4e9b\uff0c\u5f20\u606a\u9700\u8981\u501f\u7528\u5efa\u90ba\u5e02\u91cc\u7684\u529b\u91cf\u6d88\u6389\u4e00\u4e9b\u674e\u8fdc\u6e56\u7684\u5f3a\u52bf\uff0c\u9ec4\u514b\u7fa4\u53ea\u662f\u65b0\u6d66\u533a\u59d4\u4e66\u8bb0\u800c\u5df2\uff0c\u4ed6\u53ef\u662f\u6307\u671b\u5efa\u90ba\u5e02\u91cc\u51fa\u9762\u7684\u3002</p><p>\u3000\u3000\u731c\u4e0d\u900f\u5c31\u6709\u4e9b\u68d8\u624b\uff0c\u5f20\u606a\u8fd8\u5f97\u8981\u5728\u9676\u664b\u4e0e\u674e\u8fdc\u6e56\u7684\u5939\u7f1d\u91cc\u751f\u5b58\u5462\u3002</p><p>\u3000\u3000\u8868\u73b0\u51fa\u6765\uff0c\u5374\u6ca1\u6709\u4ec0\u4e48\u72b9\u8c6b\uff0c\u5f20\u606a\u795e\u8272\u632f\u594b\u7684\u8d70\u5230\u5916\u9762\u7684\u63a5\u673a\u5927\u5385\uff0c\u4e0e\u674e\u4e49\u6c5f\u63e1\u624b\u9053\uff1a\u201c\u4f60\u4e0e\u9ec4\u4e66\u8bb0\u5728\u5e02\u533a\u7b49\u6211\u5c31\u884c\u4e86\uff0c\u8fd9\u4e2a\u600e\u4e48\u5f53\u5f97\u8d77\uff1f\u201d</p><p>\u3000\u3000\u201c\u514b\u7fa4\u73b0\u5728\u662f\u5efa\u90ba\u7684\u526f\u5e02\u957f\uff0c\u201d\u67f3\u5fd7\u519b\u7591\u60d1\u7684\u95ee\uff0c\u201c\u4f60\u8ba4\u8bc6\u9ec4\u5e02\u957f\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0a\u56de\u4e1c\u6d77\u5927\u5b66\u641e\u65b0\u751f\u519b\u8bad\u9605\u5175\uff0c\u4f60\u4e0d\u662f\u8ddf\u9676\u664b\u4e66\u8bb0\u90fd\u5230\u73b0\u573a\uff0c\u9ec4\u5e02\u957f\u5f53\u65f6\u4f5c\u4e3a\u65b0\u6d66\u533a\u7684\u533a\u59d4\u4e66\u8bb0\uff0c\u4e5f\u6709\u88ab\u9080\u8bf7\u53c2\u52a0\u2026\u2026\u201d\u5f20\u606a\u63d0\u9192\u67f3\u5fd7\u519b\u4ec0\u4e48\u65f6\u5019\u4ed6\u4eec\u4e09\u4eba\u5728\u4e00\u8d77\u89c1\u8fc7\u9762\uff0c\u6ca1\u60f3\u5230\u9ec4\u514b\u7fa4\u5f80\u524d\u5c0f\u5c0f\u8fc8\u4e86\u4e00\u6b65\u2014\u2014\u4e5f\u53ea\u662f\u5c0f\u5c0f\u4e00\u6b65\u800c\u5df2\u2014\u2014\u5e94\u8be5\u662f\u8fd9\u51e0\u5929\u91cc\u7684\u4e8b\u60c5\uff0c\u4efb\u547d\u6709\u6ca1\u6709\u4e0b\u6765\u90fd\u4e0d\u4e00\u5b9a\uff1b\u4ed6\u5bf9\u5efa\u90ba\u5e02\u91cc\u7684\u4eba\u4e8b\u8c03\u52a8\u4e00\u76f4\u5f88\u5173\u6ce8\u3002</p><p>\u3000\u3000\u201c\u54e6\u3002\u201d\u67f3\u5fd7\u519b\u8bb0\u8d77\u8fd9\u4e2a\u7ec6\u8282\u6765\uff0c\u4e5f\u4e0d\u602a\u4ed6\uff0c\u5f53\u521d\u4e1c\u5927\u65b0\u751f\u519b\u8bad\u9605\u5175\u65f6\uff0c\u9ec4\u514b\u7fa4\u5728\u7701\u59d4\u4e66\u8bb0\u9762\u524d\u53ea\u662f\u5c0f\u89d2\u8272\uff0c\u5f88\u96be\u5165\u4eba\u773c\u7684\uff0c\u5f20\u606a\u53c8\u6ca1\u6709\u5f53\u7740\u67f3\u5fd7\u519b\u7684\u9762\u8ddf\u9ec4\u514b\u7fa4\u6253\u62db\u547c\uff0c\u5ffd\u89c6\u8fc7\u53bb\u4e5f\u6b63\u5e38\u3002</p><p>\u3000\u3000\u9ec4\u514b\u7fa4\u53bb\u5e74\u5165\u79cb\u65f6\u5728\u5c0f\u6c5f\u7684\u4e71\u77f3\u6ee9\u9047\u5230\u5f20\u606a\u5c31\u8ba4\u51fa\u4ed6\u6765\uff0c\u90a3\u65f6\u4e0d\u662f\u77e5\u9053\u4ed6\u7684\u5e95\u7ec6\uff0c\u800c\u662f\u5f20\u606a\u53bb\u5e74\u590f\u5929\u5728\u65b0\u829c\u8868\u73b0\u592a\u5a01\u98ce\u4e86\uff0c\u60f9\u5f97\u4e1c\u6d77\u7701\u5185\u7684\u9ad8\u7ea7\u5b98\u5458\u51e0\u4e4e\u90fd\u6653\u5f97\u4ed6\u7684\u201c\u6df7\u5e10\u201d\u4e8b\u8ff9\uff1b\u9ec4\u514b\u7fa4\u8fd9\u65f6\u5019\u624d\u7b97\u771f\u6b63\u7684\u201c\u8ba4\u8bc6\u201d\u5f20\u606a\uff0c\u8fd9\u5c0f\u5b50\u6562\u8ddf\u7701\u957f\u674e\u8fdc\u6e56\u6446\u8c31\uff0c\u9ec4\u514b\u7fa4\u5c31\u6ca1\u6709\u6307\u671b\u81ea\u5df1\u526f\u5e02\u957f\u7684\u5934\u8854\u80fd\u8ba9\u4ed6\u6298\u670d\uff0c\u4e3b\u52a8\u4f38\u51fa\u624b\u4e0e\u5f20\u606a\u63e1\u624b\u5bd2\u6684\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4f4d\u662f\u2026\u2026\u201d\u9ec4\u514b\u7fa4\u3001\u67f3\u5fd7\u519b\u90fd\u4e0d\u786e\u8ba4\u8bb8\u601d\u7684\u8eab\u4efd\uff0c\u7591\u60d1\u7684\u7b49\u7740\u5f20\u606a\u5e2e\u4ed6\u4eec\u4ecb\u7ecd\uff1a\u8c01\u6653\u5f97\u5f20\u606a\u5e74\u5c11\u98ce\u6d41\u3001\u8fd9\u4f4d\u662f\u4ed6\u7684\u5f02\u6027\u670b\u53cb\uff0c\u8fd8\u662f\u53e6\u6709\u8eab\u4efd\uff1f</p><p>\u3000\u3000\u5efa\u90ba\u8981\u6bd4\u9999\u6e2f\u51b7\u4e86\u8bb8\u591a\uff0c\u4e0b\u98de\u673a\u4e4b\u540e\uff0c\u8bb8\u601d\u7a7f\u7740\u6a59\u9ec4\u7684\u77ed\u98ce\u8863\uff0c\u6234\u7740\u9ed1\u8272\u73b3\u7441\u6846\u7684\u773c\u955c\uff0c\u633d\u7740\u53d1\u9afb\uff0c\u5de5\u4f5c\u88c5\u675f\u6253\u626e\u7684\u5979\u63a9\u53bb\u5a07\u67d4\u9500\u9b42\u7684\u5a9a\u8273\uff0c\u7136\u800c\u7709\u773c\u95f4\u7684\u79c0\u7f8e\u4e0d\u51cf\uff0c\u98ce\u5ea6\u7ef0\u7ea6\uff0c\u6709\u7740\u5178\u96c5\u7aef\u5e84\u7684\u77e5\u6027\u7f8e\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u9999\u6e2f\u8d8a\u79c0\u63a7\u80a1\u7684\u603b\u88c1\u2026\u2026\u5979\u4e2a\u4eba\u8fd8\u5728\u5efa\u90ba\u6295\u8d44\u4e86\u4e00\u5bb6\u7ef4\u57df\u516c\u53f8\u2026\u2026\u201d\u5f20\u606a\u5728\u8bb8\u601d\u53d6\u540d\u7247\u4e4b\u65f6\u4ee3\u5979\u4ecb\u7ecd\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u519b\u3001\u9ec4\u514b\u7fa4\u63a5\u8fc7\u8bb8\u601d\u9012\u8fc7\u6765\u7684\u6a80\u7ea2\u63cf\u6d45\u4e91\u7eb9\u8bbe\u8ba1\u53e4\u96c5\u7684\u9576\u91d1\u7ebf\u540d\u7247\uff0c\u770b\u7740\u4e0a\u9762\u7684\u540d\u5b57\uff0c\u5fc3\u795e\u90fd\u4e00\u51db\uff1a\u8fd9\u4e2a\u5973\u4eba\uff01</p><p>\u3000\u3000\u5f20\u606a\u773c\u795e\u655b\u7740\uff0c\u671b\u7740\u67f3\u5fd7\u519b\u3001\u9ec4\u514b\u7fa4\u7684\u53cd\u5e94\uff0c\u5634\u5507\u5374\u54a7\u7740\u7b11\uff0c\u4e1d\u6beb\u6ca1\u6709\u51b7\u6de1\u4e0b\u6765\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u6b64\u65f6\u7684\u5f20\u606a\uff0c\u4ed6\u4e2a\u4eba\u662f\u4e0e\u8d8a\u79c0\u6ca1\u6709\u4e00\u70b9\u5173\u7cfb\u4e86\uff0c\u8d8a\u79c0\u63a7\u80a1\u53ea\u4ee3\u8868\u6d77\u88d5\u516c\u53f8\u3001\u82cf\u6d25\u4e1c\u7b49\u9526\u6e56\u9ad8\u5c42\u7ba1\u7406\u4eba\u5458\u8fd8\u6709\u8bb8\u601d\u4e2a\u4eba\u7684\u6743\u76ca\uff0c\u5176\u4e2d\u6d77\u88d5\u516c\u53f8\u7edd\u5bf9\u63a7\u80a1\uff0c\u65e0\u8bba\u8bf4\u662f\u660e\u9762\u4e0a\uff0c\u8fd8\u662f\u5b98\u9762\u4e0a\uff0c\u8bb8\u601d\u90fd\u53ea\u80fd\u7b97\u662f\u8c22\u665a\u6674\u5728\u9999\u6e2f\u7684\u4ee3\u7406\u4eba\u3002</p><p>\u3000\u3000\u8fd9\u5e74\u5934\u4e0d\u5174\u8ffd\u7a76\u5386\u53f2\u9648\u8d26\u4e86\uff0c\u5f53\u5e74\u7684\u56fd\u519b\u65e5\u5bc7\uff0c\u6570\u5341\u5e74\u8f6c\u8eab\u6210\u4e86\u624b\u91cc\u6258\u7740\u5927\u628a\u73b0\u949e\u7684\u6295\u8d44\u4eba\uff0c\u8fd8\u4e0d\u662f\u60f9\u5f97\u56fd\u5185\u4e00\u7fa4\u5b98\u5458\u4eec\u56f4\u7740\u6367\u7740\uff1f\u9ec4\u514b\u7fa4\u54c8\u54c8\u4e00\u7b11\uff0c\u5fae\u8eac\u7740\u8eab\u5b50\u81f4\u610f\uff0c\u5f00\u73a9\u7b11\u8bf4\uff1a\u201c\u6211\u90fd\u8981\u628a\u6295\u8d44\u4eba\u5f53\u8863\u98df\u7236\u6bcd\u7684\uff0c\u4ee5\u540e\u8fd8\u8981\u6307\u671b\u8bb8\u601d\u5c0f\u59d0\u4e3a\u5efa\u90ba\u7684\u53d1\u5c55\u591a\u51fa\u4e00\u5206\u529b\u5462\u3002\u201d</p><p>\u3000\u3000\u5bd2\u6684\u4e4b\u540e\uff0c\u4fbf\u8981\u4e58\u8f66\u53bb\u5e02\u533a\u5403\u591c\u5bb5\uff0c\u516c\u53f8\u6709\u8f66\u6765\u63a5\uff0c\u5f20\u606a\u4fbf\u4e0e\u67f3\u5fd7\u519b\u3001\u9ec4\u514b\u7fa4\u4ed6\u4eec\u5206\u5f00\u6765\u5750\u8f66\u524d\u5f80\u5e02\u533a\u3002\u5750\u5230\u8f66\u91cc\uff0c\u8bb8\u601d\u8f7b\u8f7b\u62cd\u4e86\u62cd\u81ea\u5df1\u7684\u80f8\u53e3\uff0c\u5fc3\u865a\u7684\u6837\u5b50\uff0c\u5085\u4fca\u3001\u9a6c\u6d77\u9f99\u5750\u5728\u524d\u6392\uff0c\u5012\u4e0d\u4fbf\u8ddf\u5f20\u606a\u8bf4\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f00\u73a9\u7b11\u8bf4\uff1a\u201c\u6709\u94b1\u5c31\u662f\u5927\u5a18\uff0c\u4f60\u5fc3\u865a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u5a07\u55d4\u7684\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u624b\u91cc\u4f38\u5230\u5f20\u606a\u5927\u817f\u4e0a\u6390\u4e86\u4e00\u8bb0\uff0c\u4e0d\u8ba9\u4ed6\u80e1\u8bf4\u516b\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u4e0d\u518d\u9017\u8bb8\u601d\uff0c\u62ff\u8d77\u7535\u8bdd\u7ed9\u8bb8\u9e3f\u4f2f\u6253\u7535\u8bdd\uff0c\u8bb8\u9e3f\u4f2f\u4eba\u5728\u6f84\u9633\u3002\u8fd9\u65f6\u5019\u6b63\u662f\u5927\u9762\u79ef\u8425\u6797\u7684\u6700\u597d\u65f6\u673a\uff0c\u6797\u4e1a\u516c\u53f8\u5728\u5efa\u90ba\u8fd9\u8fb9\u7559\u4e0b\u6765\u7684\u4eba\u5f88\u5c11\u3002\u5728\u7535\u8bdd\u91cc\uff0c\u5f20\u606a\u5c06\u9ec4\u514b\u7fa4\u7a81\u7136\u51fa\u73b0\u7684\u4e8b\u60c5\u544a\u8bc9\u8bb8\u9e3f\u4f2f\uff0c\u6709\u4e9b\u5730\u65b9\u4ed6\u8fd8\u770b\u4e0d\u5927\u900f\uff0c\u9700\u8981\u8bb8\u9e3f\u4f2f\u63d0\u51fa\u610f\u89c1\u4f9b\u4ed6\u53c2\u8003\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u7684\u786e\uff0c\u76ee\u524d\u80fd\u77e5\u9053\u4fe1\u606f\u592a\u5c11\uff0c\u5f88\u96be\u5224\u65ad\u9ec4\u514b\u7fa4\u7684\u4f53\u73b0\u54ea\u65b9\u9762\u7684\u610f\u56fe\uff0c\u8bb8\u9e3f\u4f2f\u5728\u7535\u8bdd\u91cc\u95ee\uff1a\u201c\u9ec4\u514b\u7fa4\u5728\u7701\u91cc\u7684\u5173\u7cfb\u7ebf\u4e0d\u660e\u6717\uff0c\u5728\u5efa\u90ba\u5e02\u91cc\uff0c\u4ed6\u4e0e\u5e02\u59d4\u526f\u4e66\u8bb0\u3001\u5e02\u957f\u9ad8\u5fd7\u534e\u8d70\u5f97\u4eb2\u8fd1\uff0c\u4f46\u662f\u4e0d\u6392\u9664\u9ec4\u514b\u7fa4\u6709\u81ea\u5df1\u7acb\u529f\u7684\u610f\u613f\u2026\u2026\u4e5f\u53ef\u80fd\u662f\u5236\u6d46\u9879\u76ee\u7684\u4e8b\u60c5\u8ba9\u4e00\u4e9b\u4eba\u5fc3\u6709\u4f59\u60b8\uff0c\u5c06\u9ec4\u514b\u7fa4\u6253\u53d1\u51fa\u6765\u8bd5\u4e00\u8bd5\u9526\u6e56\u7684\u6c34\u5e95\uff0c\u9ec4\u514b\u7fa4\u7684\u526f\u5e02\u957f\u4efb\u547d\u662f\u8fd9\u4e24\u5929\u521a\u4e0b\u6765\u7684\uff0c\u5177\u4f53\u7684\u5de5\u4f5c\u5206\u7ba1\u8303\u56f4\u8fd8\u6ca1\u6709\u786e\u5b9a\uff0c\u4e0d\u8fc7\u9ec4\u514b\u7fa4\u8fd8\u9876\u7740\u9ad8\u65b0\u56ed\u533a\u4e3b\u4efb\u7684\u5934\u8854\uff0c\u4ed6\u51fa\u9762\uff0c\u65b9\u65b9\u9762\u9762\u90fd\u4ea4\u5f85\u5f97\u8fc7\u53bb\u3002\u4f60\u662f\u62c5\u5fc3\u67f3\u5fd7\u519b\u63ba\u548c\u8fdb\u6765\u53ef\u80fd\u6709\u9676\u664b\u4e66\u8bb0\u7684\u610f\u56fe\u5728\u5185\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u6709\u8fd9\u5c42\u62c5\u5fe7\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6211\u4e4b\u524d\u7b97\u662f\u9a73\u4e86\u674e\u8fdc\u6e56\u7684\u9762\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8981\u4fdd\u6301\u5e73\u8861\uff0c\u4e0d\u80fd\u539a\u6b64\u8584\u5f7c\uff0c\u6028\u6068\u624d\u4e0d\u4f1a\u6ecb\u751f\uff0c\u201d\u8bb8\u9e3f\u4f2f\u90a3\u8fb9\u6c89\u541f\u4e86\u7247\u523b\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u5012\u89c9\u5f97\u9676\u664b\u4e66\u8bb0\u4e0d\u5927\u4f1a\u76f4\u63a5\u5173\u6ce8\u8fd9\u4ef6\u4e8b\uff0c\u5927\u6982\u662f\u67f3\u5fd7\u519b\u79c1\u4e0b\u91cc\u4e3a\u8fd9\u4ef6\u4e8b\u5728\u51fa\u529b\u2026\u2026\u9676\u664b\u79bb\u5f00\u540e\uff0c\u67f3\u5fd7\u519b\u4e5f\u8981\u4e3a\u81ea\u5df1\u8c0b\u4e2a\u597d\u51fa\u8eab\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u89c1\u673a\u884c\u4e8b\u5427\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u592a\u591a\u7684\u65f6\u95f4\u8ddf\u4ed6\u4eec\u73a9\u592a\u6781\u62f3\u4e86\uff0c\u4e0e\u5fb7\u5dde\u4eea\u5668\u7684\u6280\u672f\u8f6c\u8ba9\u534f\u8bae\u57fa\u672c\u4e0a\u6572\u5b9a\u4e86\uff0c\u9526\u6e56\u8981\u4e0a\u624b\u673a\u9879\u76ee\uff0c\u5fc5\u987b\u8981\u628a\u5730\u65b9\u653f\u5e9c\u7ed1\u4e0a\u8f66\u6765\uff0c\u624d\u80fd\u628a\u63e1\u62ff\u5230\u7b2c\u4e00\u6279\u7684\u624b\u673a\u724c\u7167\u2026\u2026\u201d</p><p>\u3000\u3000\u4e24\u8f86\u8f66\u884c\u9a76\u5728\u5feb\u901f\u901a\u9053\u4e0a\uff0c\u8def\u706f\u98de\u901f\u540e\u9000\u7559\u4e0b\u62d6\u5c3e\u5df4\u7684\u5149\u6655\uff0c\u9664\u4e86\u5076\u5c14\u8d85\u8fc7\u7684\u8f66\u8f86\uff0c\u5efa\u90ba\u7684\u591c\u8272\u9759\u8c27\uff0c\u5f20\u606a\u5c06\u8f66\u7a97\u6253\u5f00\u4e00\u6761\u7f1d\uff0c\u8ba9\u6625\u5929\u5939\u7740\u7530\u91ce\u82ac\u82b3\u7684\u98ce\u5439\u8fdb\u6765\u3002</p><p>\u3000\u3000\u5230\u5e02\u533a\u5df2\u7ecf\u662f\u591c\u91cc\u4e5d\u70b9\u949f\uff0c\u9ec4\u514b\u7fa4\u3001\u67f3\u5fd7\u519b\u5728\u5efa\u90ba\u996d\u5e97\u4e3a\u5f20\u606a\u63a5\u98ce\u6d17\u5c18\uff0c\u5f20\u606a\u4ed6\u4eec\u5728\u98de\u673a\u4e0a\u7528\u8fc7\u665a\u9910\uff0c\u67f3\u5fd7\u519b\u3001\u9ec4\u514b\u7fa4\u4e5f\u4e0d\u53ef\u80fd\u5e72\u7b49\u5230\u73b0\u5728\u4e0d\u5403\u996d\uff0c\u5728\u5efa\u90ba\u996d\u5e97\u5f53\u771f\u662f\u591c\u5bb5\u4e86\u3002</p><p>\u3000\u3000\u4ece\u4e09\u6708\u521d\uff0c\u7231\u8fbe\u96c6\u56e2\u4e0e\u5fb7\u5dde\u4eea\u5668\u53d1\u5e03\u8054\u5408\u58f0\u660e\uff0c\u9648\u4fe1\u751f\u63d0\u524d\u7ed3\u675f\u5728\u5fb7\u4eea\u7684\u4efb\u671f\u51fa\u4efb\u7231\u8fbe\u96c6\u56e2\u7684\u603b\u88c1\uff0c\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u91cc\uff0c\u9648\u4fe1\u751f\u7ad9\u5728\u5730\u9762\u4e0a\u7684\u65f6\u95f4\u4e0d\u89c1\u5f97\u6bd4\u98de\u673a\u4e0a\u591a\u591a\u5c11\uff0c\u8fd9\u6bb5\u65f6\u95f4\u91cc\uff0c\u7231\u8fbe\u96c6\u56e2\u4e5f\u79c0\u8db3\u4e86\u98ce\u5934\u3002</p><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u5ba3\u5e03\u5c06\u5168\u9762\u5411\u6d88\u8d39\u7535\u5b50\u5e02\u573a\u8fdb\u519b\uff0c\u5ba3\u5e03\u5c06\u62e9\u5730\u6253\u9020\u5927\u578b\u7684\u79d1\u6280\u56ed\u9879\u76ee\u4f5c\u4e3a\u7231\u8fbe\u96c6\u56e2\u5411\u6d88\u8d39\u7535\u5b50\u5e02\u573a\u8fdb\u519b\u7684\u6280\u672f\u652f\u6301\u540e\u76fe\uff0c\u4e3a\u6b64\u5927\u9020\u58f0\u52bf\uff0c\u5efa\u90ba\u8fd9\u8fb9\u53cd\u5e94\u8fdf\u949d\uff0c\u5e76\u4e0d\u4ee3\u8868\u676d\u5dde\u3001\u6b66\u6c49\u7b49\u57ce\u5e02\u7684\u53cd\u5e94\u4e5f\u8fdf\u949d\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4e09\u6708\u4e2d\u65ec\u98de\u5f80\u6b66\u6c49\uff0c\u8bbf\u95ee\u6b66\u6c49\u5927\u5b66\u3001\u534e\u4e2d\u79d1\u6280\u5927\u5b66\u7b49\u9662\uff0c\u4e3a\u7ec4\u5efa\u7231\u8fbe\u96c6\u56e2\u7684\u73ed\u5e95\u62db\u5175\u4e70\u9a6c\u2014\u2014\u5f20\u606a\u7ed9\u9648\u4fe1\u751f\u7684\u4eba\u4e8b\u6743\u9650\u653e\u5f97\u5f88\u5bbd\uff0c\u7231\u8fbe\u7535\u5b50\u90a3\u91cc\u9ad8\u5c42\u4e2d\u6682\u65f6\u53ea\u6709\u82cf\u6d25\u4e1c\u3001\u5468\u4e00\u5e73\u7b49\u5c11\u6570\u4f1a\u8c03\u5230\u7231\u8fbe\u96c6\u56e2\uff0c\u4e3a\u4e86\u7ef4\u6301\u4e0e\u5fb7\u4eea\u7684\u826f\u597d\u5173\u7cfb\uff0c\u9648\u4fe1\u751f\u53c8\u4e0d\u4fbf\u76f4\u63a5\u4ece\u5fb7\u4eea\u62c9\u592a\u591a\u7684\u4eba\u52a0\u5165\u9526\u6e56\uff0c\u4ed6\u53ea\u6709\u5229\u7528\u81ea\u5df1\u5728\u56fd\u5185\u7535\u5b50\u4ea7\u4e1a\u7684\u5f71\u54cd\u529b\u4e3a\u7231\u8fbe\u96c6\u56e2\u62db\u63fd\u4e1a\u5185\u7cbe\u82f1\uff0c\u5f53\u7136\uff0c\u5916\u8058\u662f\u4e00\u56de\u4e8b\uff0c\u516c\u53f8\u5efa\u7acb\u4eba\u624d\u5e93\u81ea\u5df1\u57f9\u517b\u4e5f\u662f\u6781\u5176\u91cd\u8981\u7684\uff0c\u603b\u4e4b\u76f8\u5f53\u957f\u7684\u65f6\u95f4\u91cc\uff0c\u9648\u4fe1\u751f\u4f1a\u5728\u5929\u7a7a\u4e0a\u5230\u5904\u98de\uff0c\u867d\u7136\u8bf4\u4ed6\u5df2\u7ecf\u51fa\u4efb\u7231\u8fbe\u96c6\u56e2\u603b\u88c1\uff0c\u4eba\u5374\u8fd8\u6ca1\u6709\u5230\u6d77\u5dde\u9732\u4e2a\u9762\u5462\u2014\u2014\u9648\u4fe1\u751f\u5230\u6b66\u6c49\u4e4b\u540e\uff0c\u6b66\u6c49\u5e02\u59d4\u5b89\u6392\u5e38\u52a1\u526f\u5e02\u957f\u8c22\u91d1\u53d1\u63a5\u5f85\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4e5f\u662f\u5728\u6b66\u6c49\u8fdb\u4e00\u6b65\u660e\u786e\u7231\u8fbe\u96c6\u56e2\u7684\u79d1\u6280\u56ed\u9879\u76ee\u7ec6\u8282\uff0c\u4e5f\u662f\u5728\u6b66\u6c49\u900f\u9732\u7231\u8fbe\u96c6\u56e2\u5c06\u5728\u9999\u6e2f\u3001\u65e5\u672c\u4e1c\u4eac\u3001\u7b51\u6ce2\u5e02\u3001\u97e9\u56fd\u6c49\u57ce\u6295\u8d44\u6210\u7acb\u4ea7\u54c1\u7814\u53d1\u4e2d\u5fc3\u7684\u5177\u4f53\u8ba1\u5212\uff0c\u7231\u8fbe\u96c6\u56e2\u7684\u6280\u672f\u6269\u5f20\u8def\u7ebf\u5728\u6b66\u6c49\u9996\u6b21\u9732\u51fa\u5168\u8c8c\uff0c\u4ece\u4eba\u529b\u8d44\u6e90\u6210\u672c\u4e0e\u4ea7\u4e1a\u6295\u8d44\u7684\u5e03\u5c40\u4e0a\u6765\u8003\u8651\uff0c\u6280\u672f\u6269\u5f20\u7684\u91cd\u5934\u620f\u8fd8\u662f\u5728\u56fd\u5185\u3002</p><p>\u3000\u3000\u4e5f\u662f\u90a3\u65f6\uff0c\u674e\u8fdc\u6e56\u505a\u51fa\u5230\u6d77\u5dde\u8003\u5bdf\u7684\u51b3\u5b9a\uff0c\u4f46\u662f\u674e\u8fdc\u6e56\u8003\u5bdf\u6d77\u5dde\u65f6\u788d\u4e8e\u524d\u8f66\u4e4b\u9274\u7edd\u53e3\u4e0d\u95ee\u53ca\u79d1\u6280\u56ed\u9879\u76ee\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4e8c\u5341\u4e8c\u65e5\u98de\u5f80\u4ed6\u8bfb\u672c\u79d1\u65f6\u7684\u6bcd\u6821\u6d59\u6c5f\u5927\u5b66\u505a\u6f14\u8bb2\uff0c\u676d\u5dde\u5e02\u957f\u5b59\u6653\u6613\u51fa\u9762\u63a5\u89c1\uff1b\u9648\u4fe1\u751f\u4e8c\u5341\u4e03\u65e5\u98de\u5f80\u9999\u6e2f\uff0c\u4e8c\u5341\u516b\u65e5\u5728\u9999\u6e2f\u5ba3\u5e03\u4e0e\u9999\u6e2f\u5927\u5b66\u3001\u9999\u6e2f\u7406\u5de5\u5927\u5b66\u5408\u4f5c\u6295\u8d44\u6210\u7acb\u9999\u6e2f\u4ea7\u54c1\u7814\u53d1\u4e2d\u5fc3\uff0c\u5efa\u90ba\u8fd9\u8fb9\u591a\u5c11\u6709\u4e9b\u5750\u4e0d\u4f4f\u4e86\u3002</p></article>"}}}]);
