(window.webpackJsonp=window.webpackJsonp||[]).push([[550],{573:function(p){p.exports={chapterTitle:null,title:"\u7b2c406\u7ae0 \u9759\u89c2\u5176\u53d8",content:"<article><h2>\u7b2c406\u7ae0 \u9759\u89c2\u5176\u53d8</h2><p>\u3000\u3000\u5bf9\u5916\u754c\u6765\u8bf4\uff0c\u592e\u89c6\u6807\u738b\u53ea\u610f\u5473\u7740\u65b0\u95fb\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u4e24\u4e2a\u8282\u76ee\u4e4b\u95f4\u7684\u7b2c\u4e00\u67615\u79d2\u949f\u5e7f\u544a\uff0c\u4f46\u662f\u5218\u660e\u8f89\u3001\u6768\u4e91\u4ed6\u4eec\u90fd\u77e5\u9053\u201c\u592e\u89c6\u6807\u738b\u201d\u5176\u5b9e\u662f\u4e00\u6574\u5957\u5e02\u573a\u8fd0\u4f5c\u7b56\u7565\u7684\u4ee3\u540d\u8bcd\uff0c\u8fd9\u662f\u5f53\u524d\u56fd\u5185\u6700\u6709\u4ef7\u503c\u7684\u4e00\u5957\u5e02\u573a\u8fd0\u4f5c\u7b56\u7565\uff0c\u5176\u4ef7\u503c\u8981\u8fdc\u8fdc\u8d85\u8fc7\u4e2d\u592e\u7535\u89c6\u53f0\u4e03\u70b9\u65b0\u95fb\u8054\u64ad\u540e\u7684\u90a35\u79d2\u949f\uff0c\u4e0e\u4e2d\u592e\u7535\u89c6\u53f0\u4e5f\u4e0d\u4ec5\u4ec5\u53ea\u6709\u6bcf\u59295\u79d2\u949f\u7684\u5408\u4f5c\uff0c\u6240\u4ee5\u9700\u8981\u4e00\u5957\u5207\u5b9e\u53ef\u884c\u800c\u5468\u5bc6\u8be6\u7ec6\u7684\u65b9\u6848\u6765\u8ba9\u592e\u89c6\u6807\u738b\u53d1\u6325\u51fa\u66f4\u5927\u7684\u4ef7\u503c\u6765\uff0c\u800c\u4e0d\u662f\u5750\u7b49\u5230\u5341\u4e00\u6708\u516b\u65e5\u5728\u592e\u89c6\u6885\u5730\u4e9a\u4e2d\u5fc3\u4e3e\u884c\u7684\u5e7f\u544a\u62db\u6807\u4f1a\u4e3e\u724c\u62a5\u4ef7\u90a3\u4e48\u7b80\u5355\u3002</p><p>\u3000\u3000\u8fde\u7eed\u5f00\u4e86\u4e24\u5929\u7684\u4f1a\u6765\u8ba8\u8bba\u660e\u5e74\u7684\u5e02\u573a\u8ba1\u5212\uff0c\u5176\u4e2d\u5012\u662f\u6709\u4e00\u5929\u4e13\u95e8\u6765\u8ba8\u8bba\u592e\u89c6\u6807\u738b\u7684\u95ee\u9898\uff0c\u63a5\u4e0b\u6765\u5c31\u662f\u56fd\u5e86\u8282\u52a0\u53cc\u4f11\u5468\u672b\u7684\u4e09\u5929\u5047\u671f\uff0c\u5404\u9500\u552e\u533a\u4e3b\u7ba1\u5927\u591a\u6570\u4e0d\u662f\u6d77\u5dde\u4eba\uff0c\u5927\u591a\u5728\u4e5d\u6708\u4e09\u5341\u65e5\u4e0b\u5348\u79bb\u5f00\u6d77\u5dde\u56de\u5bb6\u4f11\u5047\u3002</p><p>\u3000\u3000\u4e5d\u6708\u4e09\u5341\u65e5\u4e0b\u5348\u7684\u4f1a\u8bae\u7ed3\u675f\u5f88\u65e9\uff0c\u4f1a\u8bae\u4e00\u7ed3\u675f\uff0c\u5e02\u573a\u90e8\u7684\u4eba\u90fd\u5df2\u7ecf\u79bb\u5f00\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u7ea6\u597d\u665a\u4e0a\u4e94\u70b9\u53bb\u5b66\u6821\u63a5\u5979\uff0c\u65f6\u95f4\u8fd8\u65e9\uff0c\u5750\u5728\u4f1a\u8bae\u5ba4\u4e0d\u6025\u7740\u79bb\u5f00\uff0c\u4e0e\u82cf\u6d25\u4e1c\u4ed6\u4eec\u804a\u4e86\u4e00\u4f1a\u513f\u5929\uff0c\u89c1\u5218\u660e\u8f89\u62ff\u8d77\u4f1a\u8bae\u684c\u4e0a\u7684\u9999\u70df\u8981\u51fa\u53bb\uff0c\u5bf9\u4ed6\u8bf4\uff1a\u201c\u53bb\u54ea\u91cc\u62bd\u70df\uff1f\u8001\u82cf\u3001\u8001\u4e01\u70df\u763e\u4e5f\u72af\u4e86\uff0c\u4e00\u8d77\u53bb\u62bd\u4e24\u6839\u3002\u201d\u62cd\u4e86\u62cd\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u7684\u80a9\u8180\uff0c\u9080\u4ed6\u4eec\u4e00\u8d77\u51fa\u53bb\u62bd\u70df\u3002</p><p>\u3000\u3000\u4e01\u69d0\u62ac\u5934\u8fd8\u60f3\u8bf4\u4ed6\u6700\u8fd1\u7ed9\u8001\u5a46\u903c\u7740\u6212\u70df\uff0c\u8bdd\u6ca1\u6709\u8bf4\u51fa\u53e3\uff0c\u7ed9\u82cf\u6d25\u4e1c\u62fd\u7740\u80f3\u81c2\u5f80\u5916\u62d6\u3002</p><p>\u3000\u3000\u884c\u653f\u7efc\u5408\u697c\u5929\u53f0\u4e0a\uff0c\u6709\u4e24\u540d\u7684\u5973\u5458\u5de5\u4f9d\u7740\u680f\u6746\u804a\u5929\uff0c\u770b\u7740\u82cf\u6d25\u4e1c\u4ed6\u4eec\u8d70\u4e0a\u6765\uff0c\u5fc3\u865a\u7684\u4f4e\u5934\u8d70\u5f00\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u5c06\u9999\u70df\u76d2\u9012\u8fc7\u6765\uff0c\u5f20\u606a\u63a5\u4e86\u4e00\u652f\u70df\uff0c\u88c5\u6a21\u4f5c\u6837\u7684\u5728\u8eab\u4e0a\u6478\u6253\u706b\u673a\uff0c\u4ed6\u8eab\u4e0a\u81ea\u7136\u6ca1\u6709\u6253\u706b\u673a\uff0c\u5c31\u7b49\u7740\u82cf\u6d25\u4e1c\u8fc7\u6765\u5e2e\u4ed6\u70b9\u70df\u3002</p><p>\u3000\u3000\u201c\u6d77\u5dde\u63a7\u80a1\u90a3\u8fb9\u4e5f\u6709\u4e09\u5929\u5047\uff1f\u201d\u5f20\u606a\u95ee\u5218\u660e\u8f89\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6709\u4e09\u5929\u5462\uff0c\u4e24\u4e2a\u4eba\u6b63\u53d1\u6101\u505a\u4ec0\u4e48\u597d\u5462\uff0c\u201d\u5218\u660e\u8f89\u7b11\u7740\u8bf4\uff0c\u4ed6\u4e60\u60ef\u62ff\u624b\u6307\u634f\u7740\u9999\u70df\u62bd\uff0c\u201c\u8001\u592b\u8001\u59bb\u5c31\u8fd9\u70b9\u4e0d\u597d\u3002\u201d</p><p>\u3000\u3000\u5218\u660e\u8f89\u7684\u5973\u670b\u53cb\u8096\u739f\u8bfb\u7814\u65f6\uff0c\u5bfc\u5e08\u662f\u674e\u660e\u5b66\uff0c\u5f20\u77e5\u884c\u5c06\u674e\u660e\u5b66\u8bf7\u8fdb\u6d77\u5dde\u63a7\u80a1\u62c5\u4efb\u603b\u7ecf\u7406\u4e00\u804c\uff0c\u8096\u739f\u6bd5\u4e1a\u540e\u4e5f\u7ecf\u674e\u660e\u5b66\u63a8\u8350\u8fdb\u5165\u6d77\u5dde\u63a7\u80a1\u5de5\u4f5c\u3002\u5f20\u606a\u5bf9\u8096\u739f\u7684\u5370\u8c61\u4e0d\u592a\u6df1\u523b\uff0c\u4ec5\u6709\u51e0\u6b21\u63a5\u89e6\uff0c\u53ea\u89c9\u5f97\u5979\u662f\u4e00\u4e2a\u9887\u6709\u5fc3\u601d\u7684\u5973\u4eba\uff0c\u80fd\u529b\u4e0d\u9519\uff0c\u73b0\u5728\u662f\u6d77\u5dde\u63a7\u80a1\u603b\u7ecf\u7406\u5ba4\u52a9\u7406\u3002</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u82cf\u6d25\u4e1c\u563f\u563f\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u70b9\u4f60\u8981\u8ddf\u4e01\u69d0\u5b66\u4e60\uff0c\u4e01\u69d0\u8ddf\u4ed6\u8001\u5a46\u4ece\u5c0f\u4f4f\u4e00\u4e2a\u6751\u5b50\u91cc\uff0c\u62ff\u4e01\u69d0\u7684\u8bdd\u6765\u8bf4\uff0c\u4ed6\u4eec\u4fe9\u4eba\u6253\u7a7f\u5f00\u6863\u88e4\u5c31\u76f8\u4e92\u7785\u7740\u987a\u773c\uff0c\u4e0a\u56de\u770b\u5230\u4ed6\u4eec\u4fe9\u53e3\u5b50\u901b\u8857\u8fd8\u624b\u7275\u7740\u624b\uff0c\u5dee\u70b9\u6ca1\u628a\u6211\u7259\u7ed9\u9178\u6389\uff0c\u4eba\u5bb6\u5c31\u4e00\u70b9\u90fd\u6ca1\u6709\u8001\u592b\u8001\u59bb\u7684\u81ea\u89c9\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u8001\u592b\u8001\u59bb\u8be5\u4ec0\u4e48\u6837\u5b50\uff1f\u201d\u4e01\u69d0\u8e72\u5728\u5730\u4e0a\u62bd\u70df\uff0c\u4ef0\u7740\u5934\u95ee\u82cf\u6d25\u4e1c\u3002</p><p>\u3000\u3000\u201c\u7537\u4eba\u5728\u524d\u9762\u8d76\uff0c\u5973\u4eba\u5728\u540e\u9762\u8ffd\uff1b\u6216\u8005\u5973\u4eba\u5728\u540e\u9762\u62d6\uff0c\u7537\u4eba\u5728\u524d\u9762\u50ac\uff0c\u8fd9\u66f4\u7b26\u5408\u8001\u592b\u8001\u59bb\u901b\u8857\u7684\u5f62\u8c61\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u5e2e\u82cf\u6d25\u4e1c\u56de\u7b54\uff0c\u53c8\u5bf9\u5218\u660e\u8f89\u8bf4\uff0c\u201c\u7a9d\u5728\u5bb6\u91cc\u662f\u633a\u65e0\u804a\u7684\uff0c\u53bb\u54ea\u91cc\u627e\u5bb6\u6e21\u5047\u6751\u4f4f\u51e0\u5929\uff0c\u5012\u662f\u80fd\u8f7b\u677e\u4e00\u4e0b\u3002\u6211\u8fd9\u51e0\u5929\u8fd8\u662f\u7a7a\u4e0d\u4e0b\u6765\uff0c\u4e0d\u7136\u5c31\u5728\u6d77\u8fb9\u5305\u4e00\u5bb6\u6e21\u5047\u5bbe\u9986\u8bf7\u516c\u53f8\u6240\u6709\u7684\u7ba1\u7406\u4eba\u5458\u5e26\u4e0a\u5bb6\u4eba\u4e00\u8d77\u53bb\u4f4f\u51e0\u5929\u3002\u201d</p><p>\u3000\u3000\u4e01\u69d0\u8bf4\u9053\uff1a\u201c\u4f60\u65e2\u7136\u5f00\u4e86\u53e3\uff0c\u90a3\u6211\u4eec\u5c31\u8bb0\u4e0a\u5fc3\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u5c31\u8bb0\u7740\u5427\uff0c\u6211\u662f\u4e0d\u4f1a\u5f53\u771f\u7684\uff0c\u201d\u82cf\u6d25\u4e1c\u7b11\u7740\u8bf4\uff0c\u201c\u9664\u975e\u7b49\u5230\u9000\u4f11\uff0c\u4e0d\u7136\u770b\u4e0d\u5230\u5927\u5bb6\u6709\u540c\u65f6\u4f11\u5047\u7684\u53ef\u80fd\u6027\u554a\u3002\u201d</p><p>\u3000\u3000\u8282\u5047\u65e5\uff0c\u516c\u53f8\u81f3\u5c11\u8981\u7559\u4e00\u540d\u7ecf\u7406\u7ea7\u7684\u4eba\u5458\u503c\u73ed\u7684\uff0c\u53e6\u627e\u4e2a\u5730\u65b9\u4f11\u5047\u4e4b\u7c7b\u7684\u6d3b\u52a8\uff0c\u516c\u53f8\u7684\u7ba1\u7406\u4eba\u5458\u662f\u805a\u4e0d\u9f50\u7684\uff0c\u4e0d\u4ec5\u805a\u4e0d\u8d77\uff0c\u5927\u90e8\u5206\u7684\u7ba1\u7406\u4eba\u5458\u90fd\u8981\u7f3a\u5e2d\uff0c\u5f20\u606a\u7684\u8bdd\u4e5f\u771f\u53ea\u80fd\u542c\u542c\u800c\u5df2\u3002</p><p>\u3000\u3000\u5f20\u606a\u62cd\u62cd\u8111\u888b\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u5f53\u6211\u5728\u800d\u6ed1\u5934\uff1f\u8981\u4e0d\u8fd9\u6837\u597d\u4e86\uff0c\u4f60\u4eec\u6bcf\u5e74\u5916\u51fa\u65c5\u6e38\u4e00\u6b21\uff0c\u8d39\u7528\u90fd\u7b97\u5728\u6211\u5934\u4e0a\uff0c\u8fd9\u6837\u603b\u5f52\u597d\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u5012\u4e0d\u9519\uff0c\u201d\u82cf\u6d25\u4e1c\u70b9\u70b9\u5934\uff0c\u5374\u53c8\u53f9\u6c14\u8bf4\uff0c\u201c\u53ef\u60dc\u6211\u8fd9\u4e09\u5929\u62bd\u4e0d\u51fa\u7a7a\u6765\uff0c\u6211\u770b\u5c0f\u5218\u8ddf\u5973\u670b\u53cb\u5c31\u6311\u4e2a\u597d\u4e00\u4e9b\u5730\u65b9\u73a9\u4e00\u4e0b\uff0c\u770b\u606a\u5c11\u4f1a\u4e0d\u4f1a\u771f\u5151\u73b0\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u8096\u739f\u6709\u62a4\u7167\u5427\uff1f\u8ba9\u5979\u591a\u8bf7\u51e0\u5929\u5047\uff0c\u4f60\u4eec\u4e00\u8d77\u53bb\u56fd\u5916\u8f6c\u4e00\u5708\u56de\u6765\uff0c\u5979\u4e0d\u597d\u5f00\u53e3\uff0c\u6211\u8ddf\u5b8b\u57f9\u660e\u3001\u674e\u660e\u5b66\u53bb\u8bf4\u3002\u660e\u5e74\u8981\u9010\u6e10\u62d3\u5c55\u6d77\u5916\u5e02\u573a\uff0c\u6211\u60f3\u4ee5\u4f60\u4e3a\u4e3b\u5728\u5e02\u573a\u90e8\u4e0b\u9762\u8bbe\u7acb\u6d77\u5916\u5206\u90e8\uff0c\u6210\u7acb\u540e\u5c31\u7531\u4f60\u5206\u7ba1\uff0c\u8fc7\u4e00\u4e24\u5e74\u65f6\u673a\u6210\u719f\u4e4b\u540e\uff0c\u516c\u53f8\u4f1a\u5927\u529b\u5f00\u53d1\u6d77\u5916\u5e02\u573a\u2026\u2026\u201d\u5b8b\u57f9\u660e\u73b0\u5728\u517c\u4efb\u6d77\u5dde\u63a7\u80a1\u7684\u8463\u4e8b\u957f\u3002</p><p>\u3000\u3000\u201c\u8096\u739f\u62a4\u7167\u5012\u662f\u6709\u7684\uff0c\u201d\u5218\u660e\u8f89\u7b11\u7740\u8bf4\uff1a\u201c\u8096\u739f\u5973\u6743\u601d\u60f3\u633a\u91cd\uff0c\u603b\u8bf4\u4e24\u4eba\u7684\u4e8b\u60c5\u8981\u4e24\u4eba\u4e00\u8d77\u62ff\u4e3b\u610f\uff0c\u6211\u53ef\u4e0d\u6562\u5728\u8fd9\u91cc\u64c5\u4f5c\u4e3b\u5f20\u2026\u2026\u518d\u8bf4\uff0c\u5979\u5bf9\u51fa\u56fd\u65c5\u6e38\u5174\u8da3\u4f3c\u4e4e\u4e5f\u4e0d\u592a\u5927\u3002\u201d</p><p>\u3000\u3000\u201c\u662f\u561b\uff1f\u201d\u5f20\u606a\u5634\u89d2\u7275\u4e86\u7275\uff0c\u7b97\u662f\u7b11\u4e86\u4e00\u4e0b\uff0c\u624b\u91cc\u7684\u9999\u70df\u8fd8\u5269\u4e0b\u5c0f\u534a\u622a\uff0c\u53c8\u4ece\u5218\u660e\u8f89\u624b\u91cc\u7684\u70df\u76d2\u91cc\u62bd\u51fa\u4e00\u652f\u9999\u70df\uff0c\u63a5\u7740\u70b9\u4e0a\uff0c\u201c\u90a3\u5c31\u7b97\u4e86\uff0c\u7b49\u4f60\u4eec\u7ed3\u5a5a\u7684\u65f6\u5019\uff0c\u8bf7\u4f60\u4eec\u53bb\u56fd\u5916\u6e21\u871c\u6708\u8bf4\u4e0d\u5b9a\u80fd\u884c\u3002\u201d</p><p>\u3000\u3000\u4e01\u69d0\u624b\u91cc\u7684\u9999\u70df\u4e5f\u62bd\u5b8c\u4e86\uff0c\u63a5\u8fc7\u5218\u660e\u8f89\u7684\u70df\u76d2\uff0c\u89c1\u91cc\u9762\u53ea\u6709\u4e24\u6839\u70df\uff0c\u5206\u7ed9\u82cf\u6d25\u4e1c\u3001\u5218\u660e\u8f89\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5dee\u70b9\u90fd\u5fd8\u4e86\u6211\u5728\u6212\u70df\u3002\u201d</p><p>\u3000\u3000\u7b49\u9999\u70df\u62bd\u5b8c\uff0c\u5f20\u606a\u7ad9\u4e86\u8d77\u6765\uff0c\u770b\u7740\u8fdc\u65b9\u6797\u68a2\u4e4b\u5916\u7684\u659c\u9633\uff0c\u6709\u4e9b\u83ab\u540d\u7684\u4f24\u611f\uff0c\u9759\u9ed8\u4e86\u7247\u523b\uff0c\u8bf4\u4e86\u53e5\uff1a\u201c\u6211\u5148\u56de\u5e02\u533a\u4e86\uff0c\u4f60\u4eec\u662f\u4e0d\u662f\u8fd8\u8981\u7b49\u5230\u70b9\u518d\u8d70\uff1f\u201d</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5e73\u65f6\u5012\u65e0\u6240\u8c13\uff0c\u653e\u5047\u524d\u4e00\u5929\u5c31\u8981\u5b88\u7740\u70b9\u8d70\u3002\u201d\u62c9\u4e86\u62c9\u4e01\u69d0\uff0c\u201c\u4f60\u7559\u4e0b\u6765\u7b49\u6211\u4e00\u4f1a\u513f\u8fd8\u662f\u56de\u4f60\u7684\u4e00\u4ea9\u4e09\u5206\u5730\uff1f\u201d</p><p>\u3000\u3000\u201c\u4eca\u5929\u7684\u5de5\u4f5c\u8ba1\u5212\u5c31\u662f\u5230\u8fd9\u91cc\u6765\u5f00\u4f1a\uff0c\u6211\u90a3\u91cc\u65e9\u5c31\u5b89\u6392\u597d\u4e86\uff0c\u6211\u7559\u4e0b\u6765\u7b49\u4f60\u4e00\u4f1a\u513f\u4e5f\u6210\u3002\u201d\u4e01\u69d0\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u5148\u4e0b\u4e86\u697c\uff0c\u5218\u660e\u8f89\u8fd8\u8981\u5728\u5929\u53f0\u4e0a\u7ad9\u4e00\u4f1a\u513f\uff0c\u82cf\u6d25\u4e1c\u62c9\u7740\u4e01\u69d0\u8fdb\u529e\u516c\u5ba4\u3002</p><p>\u3000\u3000\u201c\u8fd9\u6bb5\u65f6\u95f4\uff0c\u4f60\u4e0d\u8981\u8ba9\u5e02\u573a\u90e8\u7684\u4eba\u968f\u4fbf\u53bb\u4f60\u90a3\u91cc\u3002\u201d\u82cf\u6d25\u4e1c\u8ba9\u4e01\u69d0\u5750\u5230\u529e\u516c\u5ba4\u4e00\u89d2\u7684\u6c99\u53d1\u4e0a\uff0c\u8fd9\u4e2a\u89d2\u843d\u79bb\u95e8\u6700\u8fdc\uff0c\u5c31\u7b97\u4e0d\u63a9\u95e8\uff0c\u8c08\u8bdd\u4e5f\u4e0d\u865e\u522b\u4eba\u4f1a\u542c\u89c1\u3002</p><p>\u3000\u3000\u201c\u4f60\u662f\u8bf4\u534e\u5357\u529e\u7684\u66f9\u65b0\u9633\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u597d\u8bf4\u554a\uff0c\u201d\u82cf\u6d25\u4e1c\u6447\u4e86\u6447\u5934\uff0c\u201c\u516c\u53f8\u4e94\u5e74\u89c4\u5212\u4e2d\uff0c\u6d77\u5916\u5e02\u573a\u662f\u660e\u540e\u5e74\u7684\u91cd\u5fc3\uff0c\u8fd9\u65f6\u5019\u5148\u5728\u5e02\u573a\u90e8\u4e0b\u9762\u8bbe\u7acb\u6d77\u5916\u5206\u90e8\uff0c\u5de5\u4f5c\u660e\u5e74\u5148\u542f\u52a8\u8d77\u6765\u3002\u4f60\u6211\u5fc3\u91cc\u90fd\u6e05\u695a\uff0c\u6d77\u5916\u5e02\u573a\u4e0d\u4ec5\u4ec5\u662f\u4ee3\u7406\u9500\u552e\u95ee\u9898\uff0c\u8981\u7ed5\u5f00\u5173\u7a0e\u58c1\u5792\uff0c\u4e0e\u6d77\u5916\u4f01\u4e1a\u8fdb\u884c\u5e7f\u6cdb\u7684\u5408\u4f5c\u751a\u81f3\u5408\u8d44\u3001\u72ec\u8d44\u8bbe\u7acb\u5de5\u5382\uff0c\u90fd\u662f\u5fc5\u8981\u7684\u624b\u6bb5\uff0c\u6d77\u5916\u5206\u90e8\u7684\u91cd\u8981\u6027\u4e0d\u8a00\u800c\u55bb\u3002\u606a\u5c11\u521a\u624d\u7684\u8bdd\u91cc\u63d0\u5230\u6d77\u5916\u5206\u90e8\u662f\u5728\u5b89\u629a\u5218\u660e\u8f89\uff0c\u4ed6\u4f3c\u4e4e\u4e0d\u4e3a\u6240\u52a8\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u4e01\u69d0\u5bf9\u590d\u6742\u7684\u4eba\u4e8b\u603b\u8981\u7a0d\u7a0d\u8fdf\u949d\u4e00\u4e9b\uff0c\u201c\u4f60\u662f\u8bf4\u6211\u90a3\u91cc\u8981\u9632\u7740\u5218\u660e\u8f89\uff1f\u201d</p><p>\u3000\u3000\u201c\u5e0c\u671b\u4e0d\u662f\u4ed6\u3002\u201d\u82cf\u6d25\u4e1c\u4e5f\u5ff5\u5f53\u521d\u7684\u60c5\u8c0a\uff0c\u4e01\u69d0\u5728\u590d\u6742\u4e0a\u7684\u4eba\u4e8b\u4e0a\u7a0d\u8fdf\u949d\u4e00\u4e9b\uff0c\u201c\u4e5f\u53ea\u662f\u5e0c\u671b\u5982\u6b64\u554a\u3002\u201d\u8fd8\u60f3\u8ddf\u4e01\u69d0\u518d\u8bf4\u4ec0\u4e48\uff0c\u88e4\u515c\u91cc\u7684\u624b\u673a\u54cd\u8d77\u6765\uff0c\u662f\u5f20\u606a\u6253\u6765\u7684\u3002</p><p>\u3000\u3000\u201c\u6211\u5728\u529e\u516c\u5ba4\u91cc\uff0c\u5c31\u8001\u4e01\u8ddf\u6211\u5728\u4e00\u8d77\u3002\u201d\u82cf\u6d25\u4e1c\u5bf9\u7740\u7535\u8bdd\u8bf4\u3002</p><p>\u3000\u3000\u201c\u8ba9\u8001\u4e01\u4e5f\u9759\u89c2\u5176\u53d8\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u597d\u7684\uff0c\u6211\u8ddf\u8001\u4e01\u8bf4\u3002\u201d\u82cf\u6d25\u4e1c\u70b9\u70b9\u5934\uff0c\u7b49\u7740\u5f20\u606a\u5148\u6302\u7535\u8bdd\uff0c\u624d\u5c06\u624b\u673a\u5408\u4e0a\uff0c\u5bf9\u4e01\u69d0\u8bf4\uff0c\u201c\u606a\u5c11\u8ba9\u6211\u4eec\u9759\u89c2\u5176\u53d8\uff0c\u4e00\u5207\u5c31\u770b\u4ed6\u81ea\u5df1\u600e\u4e48\u9009\u62e9\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u6211\u660e\u767d\u4e86\uff0c\u201d\u4e01\u69d0\u62cd\u62cd\u8111\u95e8\uff0c\u201c\u6211\u8fd8\u4ee5\u4e3a\u606a\u5c11\u5c06\u8d85\u7ea7VCD\u7684\u4e8b\u60c5\u7ed9\u5fd8\u4e86\uff0c\u90fd\u51b3\u5b9a\u597d\u662f\u660e\u5e74\u4e3b\u63a8\u7684\u4ea7\u54c1\uff0c\u600e\u4e48\u4f1a\u4e0d\u653e\u5165\u660e\u5e74\u7684\u5e02\u573a\u8ba1\u5212\u4e00\u8d77\u8fdb\u884c\u8ba8\u8bba\u5462\uff1f\u539f\u6765\u662f\u606a\u5c11\u538b\u6839\u5c31\u6ca1\u6709\u60f3\u7740\u8981\u63d0\u2026\u2026\u4f60\u5012\u662f\u65e9\u660e\u767d\u4e86\uff0c\u600e\u4e48\u5f00\u4f1a\u7684\u65f6\u5019\u8fd8\u715e\u6709\u4ecb\u4e8b\u7684\u8ba8\u8bba\u592e\u89c6\u6807\u738b\u7684\u95ee\u9898\uff1f\u201d</p><p>\u3000\u3000\u201c\u5373\u4f7f\u660e\u5e74\u592e\u89c6\u6807\u738b\u7ed9\u54c4\u62ac\u5230\u4e09\u4e2a\u4ebf\uff0c\u6211\u4eec\u4e5f\u4e0d\u662f\u975e\u8981\u820d\u5f03\u4e0d\u53ef\uff0c\u201d\u82cf\u6d25\u4e1c\u6ca1\u5fc3\u6ca1\u80ba\u7684\u7b11\u4e86\u7b11\uff0c\u201c\u62ff\u4e0b\u660e\u5e74\u7684\u592e\u89c6\u6807\u738b\u65e0\u53ef\u907f\u514d\u7684\u4f1a\u63d0\u9ad8\u6211\u4eec\u7684\u8fd0\u8425\u6210\u672c\uff0c\u4f46\u662f\u5728\u5e02\u573a\u6301\u7eed\u589e\u957f\u7684\u60c5\u51b5\u4e0b\uff0c\u4fdd\u6301\u539f\u6709\u7684\u5e02\u573a\u4efd\u989d\uff0c\u4f1a\u4fc3\u4f7f\u6211\u4eec\u91c7\u7528\u66f4\u591a\u7684TI\u89e3\u7801\u677f\u3002\u8fd9\u8fb9\u7ecf\u8425\u6210\u672c\u63d0\u9ad8\uff0c\u5229\u6da6\u964d\u4f4e\uff0c\u4f46\u662f\u9999\u6e2f\u516c\u53f8\u90a3\u91cc\u5374\u53ef\u4ee5\u622a\u7559\u66f4\u591a\u7684\u5229\u6da6\uff1b\u66f4\u4e3b\u8981\u7684\uff0c\u5373\u4f7f\u660e\u5e74\u666e\u901a\u5f71\u789f\u673a\u5e02\u573a\u5982\u606a\u5c11\u6240\u9884\u6d4b\u7684\u90a3\u6837\u9677\u5165\u4f17\u96c4\u6df7\u6218\u7684\u72b6\u51b5\uff0c\u6211\u4eec\u4e5f\u6709\u8d85\u7ea7VCD\u8fd9\u4e00\u5347\u7ea7\u4ea7\u54c1\u6765\u4fdd\u8bc1\u6211\u4eec\u7684\u5229\u6da6\u70b9\u4e0d\u53d7\u666e\u901a\u5f71\u789f\u673a\u5e02\u573a\u6df7\u4e71\u7ade\u4e89\u7684\u5f71\u54cd\uff0c\u5c31\u51ed\u501f\u8fd9\u4e24\u70b9\uff0c\u5373\u4f7f\u771f\u7684\u51c6\u5907\u5341\u4e00\u6708\u62ff\u4e09\u4e2a\u4ebf\u53bb\u4e89\u660e\u5e74\u7684\u592e\u89c6\u6807\u738b\uff0c\u4e5f\u4e0d\u662f\u4e0d\u53ef\u4ee5\u8ba4\u771f\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u3000\u3000\u201c\u606a\u5c11\u8fd9\u6837\u5b50\uff0c\u5012\u662f\u5e0c\u671b\u522b\u4eba\u53bb\u4e89\u8fd9\u4e2a\u6807\u738b\u2026\u2026\u201d\u4e01\u69d0\u73b0\u5728\u603b\u7b97\u662f\u60f3\u660e\u767d\u8fc7\u6765\u4e86\u3002</p><p>\u3000\u3000\u201c\u987a\u6c34\u63a8\u821f\u7684\u4e8b\u60c5\u7f62\u4e86\u3002\u201d\u82cf\u6d25\u4e1c\u7b11\u4e86\u7b11\uff0c\u4e0e\u6b63\u6cf0\u3001\u9526\u57ce\u7684\u6069\u6028\u662f\u8d8a\u6765\u8d8a\u6df1\u4e86\uff0c\u5373\u4f7f\u6362\u6210\u81ea\u5df1\uff0c\u6709\u673a\u4f1a\u4e5f\u662f\u8981\u6446\u4ed6\u4eec\u4e00\u9053\u7684\u3002</p></article>"}}}]);
