(window.webpackJsonp=window.webpackJsonp||[]).push([[553],{576:function(p){p.exports={chapterTitle:null,title:"\u7b2c553\u7ae0 \u59da\u6587\u76db\u7684\u4e00\u4e2a\u6708",content:"<article><h2>\u7b2c553\u7ae0 \u59da\u6587\u76db\u7684\u4e00\u4e2a\u6708</h2><p>\u3000\u3000\u53bb\u6c5f\u5357\u7701\u6709\u4e2a\u7701\u59d4\u526f\u4e66\u8bb0\u7f69\u7740\uff0c\u7684\u786e\u4f1a\u6d3b\u5f97\u5f88\u6ecb\u6da6\u3002\u4e2d\u592e\u5173\u7cfb\u8c31\u7cfb\u592a\u590d\u6742\uff0c\u8981\u4e0d\u662f\u59da\u6587\u76db\u81ea\u5df1\u8bf4\uff0c\u5f20\u606a\u4e00\u65f6\u8fd8\u60f3\u4e0d\u8d77\u9648\u65b0\u6c11\u662f\u987e\u957f\u5e9a\u7684\u5c0f\u5973\u5a7f\uff0c\u53f6\u5bb6\u4eba\u8109\u751a\u5e7f\uff0c\u987e\u5bb6\u60f3\u5fc5\u4e5f\u4e0d\u5dee\uff1b\u5374\u662f\u542c\u5230\u59da\u6587\u76db\u8bf4\u4ed6\u662f\u81ea\u5df1\u9009\u62e9\u5230\u5efa\u90ba\u6765\u7684\uff0c\u5f20\u606a\u5012\u6709\u5174\u8da3\uff0c\u7b11\u7740\u95ee\uff1a\u201c\u5efa\u90ba\u6709\u4ec0\u4e48\u5438\u5f15\u4f60\u7684\u5730\u65b9\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u662f\u5efa\u90ba\u6709\u4ec0\u4e48\u5730\u65b9\u80fd\u5438\u5f15\u6211\uff0c\u6211\u8981\u8bf4\u662f\u56e0\u4e3a\u4e1c\u6d77\u6709\u9526\u6e56\uff0c\u4f1a\u4e0d\u4f1a\u592a\u5510\u7a81\uff1f\u201d\u59da\u6587\u76db\u773c\u775b\u772f\u8d77\u6765\u7b11\u7740\uff0c\u5e73\u9759\u7684\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u542c\u59da\u6587\u76db\u8fd9\u4e48\u8bf4\uff0c\u5f20\u606a\u5fc3\u60f3\u59da\u6587\u76db\u8fd9\u6b21\u5927\u6982\u662f\u5176\u5fd7\u4e0d\u5c0f\uff0c\u65e2\u7136\u53f6\u7956\u8303\u4ea4\u5f85\u4e0b\u6765\u7684\u4e8b\uff0c\u53f6\u5efa\u658c\u9886\u8fdb\u95e8\u6765\u2014\u2014\u603b\u4e0d\u80fd\u6307\u671b\u53f6\u8001\u7237\u4eb2\u81ea\u9886\u4e0a\u95e8\u2014\u2014\u6de1\u6de1\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u6211\u8981\u597d\u597d\u542c\u4f60\u8bf4\u8bf4\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u5e74\u540e\u5317\u4eac\u98de\u5f80\u7f8e\u56fd\u8fbe\u62c9\u65af\uff0c\u4ece\u7f8e\u56fd\u8fbe\u62c9\u65af\u98de\u56de\u56fd\u5185\uff0c\u4e2d\u95f4\u5728\u9999\u6e2f\u505c\u7559\u4e86\u4e00\u4e0b\u3002\u5728\u5efa\u90ba\u5f97\u77e5\u674e\u8fdc\u6e56\u60f3\u8981\u5728\u4ed6\u4e0a\u4efb\u7701\u957f\u540e\u6293\u51e0\u4e2a\u5927\u9879\u76ee\u9526\u6e56\u53bb\u5e74\u629b\u51fa\u6765\u7684\u5236\u6d46\u9879\u76ee\u8ba1\u5212\u4e5f\u5728\u674e\u8fdc\u6e56\u7684\u9009\u62e9\u4e4b\u5217\uff0c\u5f20\u606a\u88ab\u8feb\u4e0e\u82cf\u6d25\u4e1c\u7b49\u4eba\u98de\u5f80\u5317\u4eac\u60f3\u7740\u5728\u5317\u4eac\u5927\u9020\u58f0\u52bf\u8f6c\u79fb\u674e\u8fdc\u6e56\u7684\u6ce8\u610f\u529b\u3002\u6b64\u6b21\u5317\u4eac\u4e4b\u884c\u5076\u7136\u4e0e\u6613\u99a8\u3001\u59da\u6587\u76db\u7b49\u4eba\u76f8\u9047\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u5728\u591c\u5e97\u521d\u4e0e\u5f20\u606a\u89c1\u9762\u65f6\u5e76\u4e0d\u6653\u5f97\u5f20\u606a\u7684\u8eab\u4efd\u2014\u2014\u5374\u6653\u5f97\u59da\u575a\u3001\u8d75\u9e4f\u662f\u7231\u8fbe\u7535\u5b50\u7684\u4e2d\u5c42\u7ba1\u7406\u4eba\u5458\u2014\u2014\u5f53\u65f6\u4ed6\u6025\u4e8e\u6446\u8131\u90e8\u59d4\u6c89\u66ae\u7684\u6c1b\u56f4\u788d\u4e8e\u5bb6\u5ead\u7684\u963b\u529b\u65e0\u6cd5\u4efb\u610f\u5984\u4e3a\u5374\u53c8\u5728\u4e0e\u5468\u5174\u4e1c\u4e89\u8054\u4fe1\u96c6\u56e2\u603b\u7ecf\u7406\u52a9\u7406\u7684\u4f4d\u5b50\u65f6\u53d7\u632b\uff0c\u610f\u5fd7\u4e5f\u6709\u4e9b\u6d88\u6c89\u3002</p><p>\u3000\u3000\u9526\u6e56\u6700\u7ec8\u9009\u62e9\u662f\u7231\u8fbe\u96c6\u56e2\u7684\u540d\u4e49\u5728\u5317\u4eac\u5927\u9020\u58f0\u52bf\uff0c\u4e0d\u4ec5\u4ec5\u5438\u5f15\u4e86\u5a92\u4f53\u4e0e\u516c\u4f17\u7684\u76ee\u5149\uff0c\u4e5f\u5438\u5f15\u4e86\u5317\u4eac\u5b98\u50da\u9636\u5c42\u7684\u6ce8\u610f\uff0c\u7279\u522b\u662f\u7231\u8fbe\u96c6\u56e2\u5f15\u8d77\u5a92\u4f53\u5bf9\u6c11\u8425\u7ecf\u6d4e\u5730\u4f4d\u7684\u5927\u8ba8\u8bba\uff0c\u66f4\u662f\u5438\u5f15\u4e86\u9ad8\u5c42\u4eba\u7269\u7684\u6ce8\u610f\uff0c\u4e5f\u5305\u62ec\u9000\u5c45\u5230\u5e55\u540e\u5374\u65f6\u523b\u5173\u6ce8\u56fd\u5185\u653f\u6cbb\u3001\u7ecf\u6d4e\u5c40\u52bf\u7684\u524d\u9ad8\u5c42\u4eba\u7269\uff0c\u59da\u6587\u76db\u4e5f\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u987e\u5bb6\u8001\u7237\u5b50\u987e\u957f\u5e9a\u4e5f\u662f\u5176\u4e2d\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u7d27\u63a5\u4e0b\u6765\uff0c\u9648\u4fe1\u751f\u4ee5\u7231\u8fbe\u96c6\u56e2\u7684\u540d\u4e49\u5ba3\u5e03\u6280\u672f\u6269\u5f20\u5411\u6d88\u8d39\u7535\u5b50\u5e02\u573a\u5168\u9762\u8fdb\u519b\u7684\u8ba1\u5212\u5e76\u6b63\u5f0f\u542f\u52a8\u79d1\u6280\u56ed\u9879\u76ee\uff0c\u4e0e\u4e1c\u6d77\u7701\u5185\u7684\u8b66\u60d5\u3001\u8fdf\u949d\u4e0d\u540c\uff0c\u5176\u4ed6\u4eba\u4e00\u5f00\u59cb\u5c31\u5bf9\u7231\u8fbe\u96c6\u56e2\u7684\u79d1\u6280\u56ed\u9879\u76ee\u8d77\u4e86\u5f88\u5927\u7684\u5174\u8da3\u2014\u2014\u6bd5\u7adf\u6ca1\u6709\u5f20\u606a\u624b\u91cc\u5403\u8fc7\u618b\uff0c\u800c\u7231\u8fbe\u96c6\u56e2\u8fd9\u6bb5\u65f6\u95f4\u5927\u9020\u58f0\u52bf\uff0c\u4f7f\u79d1\u6280\u56ed\u9879\u76ee\u770b\u8d77\u6765\u5341\u5206\u7684\u771f\u5b9e\u53ef\u4fe1\uff0c\u5927\u5bb6\u90fd\u76f8\u4fe1\u7231\u8fbe\u96c6\u56e2\u5728\u789f\u673a\u4e1a\u52a1\u4e5d\u516d\u5e74\u4e00\u5e74\u8d5a\u4e86\u5341\u4e2a\u4ebf\uff0c\u53ea\u8981\u8d44\u91d1\u4e0d\u5206\u6563\uff0c\u4e5d\u4e03\u5e74\u7684\u8425\u5229\u4e5f\u8db3\u4ee5\u652f\u6491\u79d1\u6280\u56ed\u9879\u76ee\uff0c\u6ca1\u6709\u4ec0\u4e48\u7406\u7531\u4e0d\u76f8\u4fe1\u2014\u2014\u5bf9\u66ae\u6c14\u6c89\u6c89\u7684\u90ae\u7535\u90e8\u4ee5\u53ca\u56fd\u5185\u4ee5\u56fd\u6709\u4f01\u4e1a\u4e3b\u5bfc\u7684\u7535\u5b50\u4ea7\u4e1a\u6709\u7740\u6df1\u6df1\u5931\u671b\u7684\u59da\u6587\u76db\u5374\u4f3c\u7ed9\u6ce8\u5165\u4e00\u9488\u5174\u594b\u5242\uff0c\u8ba9\u4ed6\u610f\u8bc6\u6216\u8bb8\u6c11\u8425\u7ecf\u6d4e\u624d\u662f\u5f15\u5bfc\u56fd\u5185\u901a\u4fe1\u7535\u5b50\u4ea7\u4e1a\u8d70\u5411\u817e\u98de\u7684\u5173\u952e\uff1b\u4ed6\u6700\u521d\u7684\u610f\u613f\u751a\u81f3\u60f3\u76f4\u63a5\u52a0\u5165\u7231\u8fbe\u96c6\u56e2\u3002</p><p>\u3000\u3000\u4ed6\u7684\u8fd9\u4e2a\u5ff5\u5934\u5374\u5728\u4ed6\u8001\u5b50\u987e\u5174\u79b9\u9762\u524d\u7ed9\u6cfc\u4e86\u4e00\u76c6\u51b7\u6c34\uff0c\u867d\u7136\u56fd\u5185\u5b98\u573a\u5145\u585e\u7740\u5e73\u5eb8\u7684\u4eba\u7269\uff0c\u4f46\u662f\u80fd\u5728\u5b98\u573a\u7efd\u653e\u98ce\u5f69\u4e5f\u65e0\u4e0d\u662f\u7cbe\u82f1\uff0c\u987e\u5bb6\u5e76\u4e0d\u5e0c\u671b\u4ed6\u56e0\u4e3a\u4e00\u65f6\u7684\u53d7\u632b\u5c31\u6b64\u6390\u65ad\u81ea\u5df1\u5728\u653f\u6cbb\u4e0a\u7684\u524d\u9014\uff0c\u800c\u4e14\u987e\u5bb6\u9700\u8981\u5728\u653f\u575b\u4e0a\u6709\u540e\u7ee7\u4e4b\u4eba\u3002</p><p>\u3000\u3000\u56fd\u5185\u7684\u653f\u6cbb\u4f53\u5236\u6bd5\u7adf\u4e0e\u56fd\u5916\u4e0d\u540c\uff0c\u4f60\u8981\u5728\u4ed5\u9014\u4e0a\u53d1\u5c55\uff0c\u5c31\u5fc5\u987b\u5728\u8fd9\u4e2a\u4f53\u5236\u5185\u6210\u957f\uff0c\u54ea\u6015\u8fdb\u5165\u56fd\u6709\u4f01\u4e1a\u91cc\u6298\u817e\u4e5f\u884c\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u4e0e\u5bb6\u5ead\u4e89\u6267\u4e4b\u4e0b\uff0c\u6700\u7ec8\u7684\u59a5\u534f\u65b9\u6848\u662f\u6ee1\u8db3\u4ed6\u4e4b\u524d\u7684\u610f\u613f\u540c\u610f\u4ed6\u8c03\u53bb\u5730\u65b9\u4efb\u804c\u3002\u987e\u5bb6\u4e5f\u4e0d\u4f1a\u5c06\u59da\u6587\u76db\u4e22\u5230\u67d0\u4e2a\u5c71\u6c9f\u6c9f\u91cc\u81ea\u751f\u81ea\u706d\uff0c\u518d\u8bf4\u59da\u6587\u76db\u4e00\u4e2a\u526f\u5904\u7ea7\u7684\u5b98\u5458\uff0c\u5e76\u4e0d\u9700\u8981\u52a8\u7528\u591a\u5f3a\u7684\u529b\u91cf\u5c31\u80fd\u968f\u4fbf\u5728\u67d0\u4e2a\u57ce\u5e02\u5b89\u63d2\u4e0b\u53bb\uff0c\u6700\u597d\u7684\u65b9\u6848\u5f53\u7136\u662f\u53bb\u6709\u4eba\u80fd\u7167\u987e\u5230\u7684\u57ce\u5e02\u4efb\u804c\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u59da\u6587\u76db\u4f9d\u7136\u5bf9\u7231\u8fbe\u96c6\u56e2\u4e0d\u6b7b\u5fc3\uff0c\u7231\u8fbe\u96c6\u56e2\u7684\u8fd9\u6b21\u5ffd\u60a0\u8ba9\u4ed6\u770b\u5230\u56fd\u5185\u7535\u5b50\u4ea7\u4e1a\u5d1b\u8d77\u7684\u5e0c\u671b\u2014\u2014\u7231\u8fbe\u96c6\u56e2\u7684\u5ffd\u60a0\u4e0e\u4ed6\u957f\u4e45\u4ee5\u6765\u840c\u751f\u7684\u60f3\u6cd5\u6697\u5408\uff0c\u4ed6\u5e76\u6ca1\u6709\u610f\u8bc6\u5230\u7231\u8fbe\u96c6\u56e2\u65e9\u5c31\u5bf9\u79fb\u52a8\u901a\u8baf\u4ea7\u54c1\u52a8\u4e86\u5fc3\u601d\uff0c\u4e5f\u6ca1\u6709\u610f\u8bc6\u5230\u8d75\u6d4e\u4e1c\u526f\u603b\u7406\u4f1a\u94c1\u4e86\u5fc3\u8981\u5c06\u90ae\u7535\u90e8\u5206\u62c6\u3001\u5c06\u7535\u4fe1\u90e8\u95e8\u4e0e\u7535\u5b50\u5de5\u4e1a\u90e8\u7b49\u91cd\u7ec4\u6210\u4fe1\u606f\u4ea7\u4e1a\u90e8\u5e76\u5728\u4e00\u5e74\u4e4b\u540e\u6210\u4e3a\u73b0\u5b9e\uff0c\u4ed6\u4e0d\u79bb\u5f00\u90ae\u7535\u90e8\u4e5f\u4f1a\u4e0e\u7231\u8fbe\u96c6\u56e2\u53d1\u751f\u4ea4\u96c6\u2014\u2014\u59da\u6587\u76db\u5fc3\u91cc\u60f3\uff1a\u56fd\u5185\u4e3b\u8981\u57ce\u5e02\u90fd\u5728\u641e\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u56ed\uff0c\u7231\u8fbe\u96c6\u56e2\u5982\u6b64\u5927\u89c4\u6a21\u7684\u79d1\u6280\u56ed\u9879\u76ee\u5fc5\u987b\u4f9d\u6258\u5927\u57ce\u5e02\u7684\u6280\u672f\u4eba\u624d\u8d44\u6e90\uff0c\u6700\u7ec8\u843d\u6237\u6b66\u6c49\u3001\u676d\u5dde\u3001\u6210\u90fd\u6216\u8005\u5efa\u90ba\u8fd9\u4e9b\u57ce\u5e02\u4e2d\u7684\u67d0\u4e2a\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u56ed\u533a\u5185\uff0c\u90a3\u51e0\u4e4e\u662f\u80fd\u80af\u5b9a\u7684\u4e8b\u60c5\uff0c\u4ed6\u539f\u6253\u7b97\u7b49\u7231\u8fbe\u96c6\u56e2\u7684\u79d1\u6280\u56ed\u9879\u76ee\u786e\u5b9a\u57ce\u5e02\u4e4b\u540e\uff0c\u4ed6\u518d\u6709\u9488\u5bf9\u6027\u7684\u9009\u62e9\u53bb\u5730\u65b9\u4efb\u804c\u3002\u5f53\u4ed6\u5c06\u81ea\u5df1\u7684\u6253\u7b97\u8ddf\u987e\u5bb6\u8001\u7237\u5b50\u987e\u957f\u5e9a\u5546\u91cf\u65f6\uff0c\u987e\u957f\u5e9a\u76f4\u63a5\u8bf4\u9053\uff1a\u201c\u53bb\u5efa\u90ba\uff0c\u5e4c\u5b50\u626f\u5f97\u518d\u591a\uff0c\u5ffd\u60a0\u518d\u5389\u5bb3\uff0c\u6700\u540e\u8fd8\u662f\u5728\u5efa\u90ba\u2026\u2026\u201d</p><p>\u3000\u3000\u90a3\u65f6\uff0c\u5f20\u606a\u8fd8\u6b63\u4e0e\u674e\u8fdc\u6e56\u5904\u4e8e\u50f5\u6301\u671f\u3001\u674e\u8fdc\u6e56\u8fd8\u6ca1\u6709\u5230\u6d77\u5dde\u8003\u5bdf\u4f5c\u51fa\u8ba9\u6b65\u3002</p><p>\u3000\u3000\u5728\u8001\u4e00\u8f88\u4eba\u5f53\u4e2d\uff0c\u8bb8\u591a\u4eba\u5bf9\u56fd\u5185\u975e\u516c\u6709\u5236\u7ecf\u6d4e\u4f53\u7684\u5d1b\u8d77\u9887\u6709\u5fae\u8f9e\uff0c\u751f\u6015\u975e\u516c\u6709\u5236\u7ecf\u6d4e\u4e0e\u516c\u6709\u5236\u7ecf\u6d4e\u7684\u529b\u91cf\u5bf9\u6bd4\u53d1\u751f\u9006\u8f6c\u4f1a\u5bf9\u56fd\u5185\u4ee5\u516c\u6709\u5236\u7ecf\u6d4e\u4e3a\u57fa\u7840\u7684\u56fd\u5bb6\u4f53\u5236\u5f62\u6210\u5a01\u80c1\uff0c\u4f46\u662f\u987e\u957f\u5e9a\u5374\u975e\u4fdd\u5b88\u7684\u4e00\u6d3e\uff0c\u81ea\u4ece\u7231\u8fbe\u96c6\u56e2\u5728\u5317\u4eac\u5927\u9020\u58f0\u52bf\u3001\u7279\u522b\u662f\u4ee5\u6b64\u4e3a\u6e90\u5934\u5728\u5a92\u4f53\u4e0a\u5f15\u8d77\u6c11\u8425\u7ecf\u6d4e\u7b49\u975e\u516c\u6709\u5236\u7ecf\u6d4e\u5730\u4f4d\u7684\u5927\u8ba8\u8bba\u65f6\uff0c\u4ed6\u5c31\u5f00\u59cb\u5173\u6ce8\u7231\u8fbe\u96c6\u56e2\u3001\u5173\u6ce8\u9526\u6e56\uff0c\u4e5f\u5173\u6ce8\u5230\u5f20\u606a\u8fd9\u4e2a\u582a\u79f0\u62e5\u6709\u5947\u8ff9\u4e4b\u624b\u7684\u5e74\u8f7b\u5f97\u8fc7\u5206\u7684\u9752\u5e74\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u8fd8\u662f\u4ece\u4ed6\u5bb6\u8001\u7237\u5b50\u90a3\u91cc\u624d\u77e5\u9053\u5f20\u606a\u7684\u771f\u5b9e\u8eab\u4efd\uff0c\u5012\u4e5f\u6108\u53d1\u575a\u5b9a\u4e0e\u4e4b\u7ed3\u4ea4\u3001\u4e0e\u7231\u8fbe\u96c6\u56e2\u642d\u4e0a\u7ebf\u7684\u5fc3\u601d\uff0c\u4ed6\u8fd8\u4e0d\u6653\u5f97\u5f20\u606a\u65e9\u5c31\u5bf9\u90ae\u7535\u90e8\u7684\u5173\u7cfb\u7f51\u52a8\u4e86\u5fc3\u601d\uff0c\u773c\u4e0b\u53ea\u662f\u6309\u5175\u4e0d\u52a8\u800c\u5df2\u2014\u2014\u867d\u7136\u66fe\u5728\u591c\u5e97\u76f8\u9047\uff0c\u4f46\u662f\u53cc\u65b9\u90fd\u7792\u4f4f\u771f\u6b63\u7684\u8eab\u4efd\u4e0d\u63d0\uff0c\u77ed\u671f\u5185\u671f\u5f85\u4e0b\u6b21\u7684\u9082\u9005\u662f\u4e0d\u73b0\u5b9e\u7684\uff0c\u90fd\u4e0d\u6653\u5f97\u5f20\u606a\u8fc7\u591a\u4e45\u4f1a\u518d\u6b21\u5230\u5317\u4eac\u2014\u2014\u4ed6\u8fd8\u9700\u8981\u4e00\u4e2a\u771f\u6b63\u610f\u4e49\u4e0a\u7684\u5f15\u8350\u4eba\u3002</p><p>\u3000\u3000\u987e\u957f\u5e9a\u4e86\u89e3\u5230\u5f90\u5b66\u5e73\u8c03\u79bb\u4e1c\u6d77\u7701\u7684\u59cb\u672b\u4e0e\u4e1c\u6d77\u7701\u5b98\u573a\u52bf\u529b\u5206\u5e03\u4e4b\u540e\uff0c\u4fbf\u80fd\u731c\u5230\u7231\u8fbe\u96c6\u56e2\u5728\u5317\u4eac\u7684\u8fd9\u756a\u4f5c\u4e3a\u5176\u5b9e\u6709\u7ed9\u674e\u8fdc\u6e56\u65bd\u52a0\u538b\u529b\u7684\u610f\u601d\uff1b\u5730\u65b9\u4e0a\u7684\u4f01\u4e1a\u8fd8\u662f\u4e60\u60ef\u501f\u52a9\u5730\u65b9\u653f\u5e9c\u7684\u529b\u91cf\u6210\u4e8b\uff0c\u7136\u540e\u5728\u7231\u8fbe\u96c6\u56e2\u6b64\u6b21\u6405\u51fa\u6765\u7684\u52a8\u9759\u5b8c\u5168\u770b\u4e0d\u5230\u5730\u65b9\u653f\u5e9c\u7684\u4f5c\u4e3a\u3002</p><p>\u3000\u3000\u987e\u957f\u5e9a\u770b\u5230\u674e\u8fdc\u6e56\u4e0e\u9526\u6e56\u4e4b\u95f4\u7684\u50f5\u6301\uff0c\u6709\u4eba\u80fd\u66ff\u4ed6\u4eec\u65a1\u65cb\u7f13\u548c\u53cc\u65b9\u7684\u5173\u7cfb\u90a3\u81ea\u7136\u4f1a\u53d7\u5230\u65b9\u9762\u7684\u91cd\u89c6\uff0c\u4fbf\u5bf9\u59da\u6587\u76db\u8bf4\uff1a\u201c\u4e0e\u5176\u65e5\u540e\u9526\u4e0a\u6dfb\u82b1\uff0c\u8fd8\u4e0d\u5982\u6b64\u65f6\u53bb\u96ea\u4e2d\u9001\u78b3\uff01\u201d</p><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u5728\u5317\u4eac\u5927\u9020\u58f0\u52bf\u65f6\uff0c\u53f6\u81fb\u6c11\u51fa\u9762\u6447\u65d7\u5450\u558a\uff0c\u53f6\u5bb6\u4e0e\u9526\u6e56\u7684\u5173\u7cfb\u4e00\u76ee\u4e86\u7136\uff0c\u987e\u957f\u5e9a\u6ca1\u6709\u627e\u53f6\u81fb\u6c11\u6253\u4ea4\u9053\uff0c\u800c\u662f\u76f4\u63a5\u5c06\u591a\u5e74\u670b\u53cb\u53f6\u7956\u8303\u8bf7\u5230\u5317\u4eac\u3002</p><p>\u3000\u3000\u6709\u4e9b\u4e8b\uff0c\u5f20\u606a\u6ca1\u6709\u8ddf\u53f6\u5bb6\u8001\u7237\u5b50\u660e\u8bf4\uff0c\u4e0d\u8fc7\u7231\u8fbe\u96c6\u56e2\u5728\u5916\u9762\u5927\u5ffd\u60a0\uff0c\u540c\u6837\u7792\u4e0d\u8fc7\u53f6\u7956\u8303\u7684\u773c\u775b\uff0c\u53f6\u7956\u8303\u5f53\u7136\u80fd\u66f4\u786e\u8ba4\u5f20\u606a\u6b64\u4e3e\u5176\u5b9e\u662f\u4e0e\u674e\u8fdc\u6e56\u5728\u6697\u4e2d\u8f83\u91cf\u3002</p><p>\u3000\u3000\u653f\u6cbb\u773c\u5149\u5927\u6982\u5c31\u662f\u6307\u9009\u4e2d\u4e00\u6761\u7b26\u5408\u53d1\u5c55\u8d8b\u52bf\u7684\u9053\u8def\uff0c\u5e76\u7d27\u968f\u4e0d\u820d\u3002</p><p>\u3000\u3000\u65e2\u7136\u987e\u957f\u5e9a\u4e0e\u59da\u5174\u76db\u6709\u8fd9\u4e2a\u773c\u5149\uff0c\u53f6\u7956\u8303\u5f53\u7136\u613f\u610f\u9526\u4e0a\u6dfb\u82b1\uff0c\u4f55\u51b5\u4e24\u5bb6\u5173\u7cfb\u6570\u5341\u5e74\u6765\u90fd\u662f\u63f4\u4e3a\u52a9\u529b\u7684\u90a3\u79cd\u5bc6\u5207\u3002</p><p>\u3000\u3000\u5012\u662f\u6ca1\u6709\u60f3\u5230\u674e\u8fdc\u6e56\u968f\u540e\u5c31\u5230\u6d77\u5dde\u8003\u5bdf\u4e3b\u52a8\u505a\u51fa\u8ba9\u6b65\uff0c\u4f46\u662f\u79d1\u6280\u56ed\u9879\u76ee\u5df2\u7ecf\u60ac\u800c\u672a\u51b3\uff0c\u987e\u957f\u5e9a\u5c31\u60f3\u5230\u674e\u8fdc\u6e56\u4e0e\u9526\u6e56\u4e4b\u95f4\u7684\u50f5\u6301\u5173\u7cfb\u5e76\u6ca1\u6709\u771f\u6b63\u7684\u7834\u51b0\uff0c\u81ea\u7136\u4e5f\u5c31\u6709\u4e86\u987e\u5bb6\u8868\u73b0\u7684\u4f59\u5730\u3002</p><p>\u3000\u3000\u4e4b\u540e\u5c31\u8fd0\u4f5c\u59da\u6587\u76db\u8c03\u5f80\u5efa\u90ba\u7684\u4e8b\u60c5\uff0c3\u670828\u65e5\uff0c\u5609\u4fe1\u5b9e\u4e1a\u5728\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u6240\u516c\u5e03\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\uff0c\u59da\u6587\u76db30\u65e5\u624d\u529e\u7406\u5b8c\u501f\u8c03\u624b\u7eed\u8d76\u5230\u5efa\u90ba\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u4eba\u5230\u5efa\u90ba\u4e4b\u540e\uff0c\u5f20\u606a\u4eba\u4e00\u76f4\u6ede\u7559\u5728\u9999\u6e2f\uff0c\u59da\u6587\u76db\u4fbf\u7c98\u4e0a\u53f6\u5efa\u658c\uff1b\u53f6\u7956\u8303\u5728\u4e2d\u592e\u4efb\u804c\u65f6\uff0c\u53f6\u5efa\u658c\u6709\u76f8\u5f53\u957f\u7684\u4e00\u6bb5\u65f6\u95f4\u5728\u5317\u4eac\u5c45\u4f4f\uff0c\u867d\u7136\u6bd4\u59da\u6587\u76db\u5e74\u957f\u4e09\u5c81\uff0c\u957f\u5927\u540e\u867d\u7136\u6765\u5f80\u4e0d\u5bc6\u5207\uff0c\u4f46\u4e5f\u7b97\u6709\u53d1\u5c0f\u60c5\u8c0a\uff0c\u52a0\u4e0a\u662f\u8001\u7237\u5b50\u4ea4\u5f85\u4e0b\u6765\u7684\u4e8b\u60c5\uff0c\u8fd9\u4e24\u5929\u7a7a\u95f2\u65f6\u95f4\u4fbf\u4e0e\u59da\u6587\u76db\u7c98\u5728\u4e00\u8d77\uff0c\u5173\u7cfb\u4e5f\u5c31\u91cd\u65b0\u70ed\u7edc\u8d77\u6765\uff1b\u7b49\u7740\u5f20\u606a\u5230\u5efa\u90ba\u518d\u5f15\u8350\u4e0d\u8fdf\u3002\u8fd9\u6b21\u7684\u786e\u4e5f\u662f\u59da\u6587\u76db\u4ece\u6613\u99a8\u540c\u4e8b\u90a3\u91cc\u77e5\u9053\u5f20\u606a\u4e0e\u8bb8\u601d\u4e58\u4e1c\u822a\u7684\u98de\u673a\u62b5\u8fbe\u5efa\u90ba\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u59da\u6587\u76db\u6709\u9009\u62e9\u7684\u503e\u8ff0\u4e86\u4ed6\u8fd9\u4e00\u4e2a\u591a\u6708\u6765\u7684\u5fc3\u8def\u5386\u7a0b\uff0c\u5f20\u606a\u542c\u4e86\u5fc3\u91cc\u5fae\u5fae\u4e00\u53f9\uff1a\u5f53\u4f60\u8ffd\u9010\u673a\u4f1a\u65f6\uff0c\u522b\u4eba\u4e5f\u5e2e\u4f60\u5f53\u6210\u673a\u4f1a\u5728\u8ffd\u9010\u3002\u5f20\u606a\u7684\u786e\u9700\u8981\u4e00\u4e2a\u4eba\u6765\u7f13\u548c\u4e0e\u674e\u8fdc\u6e56\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u8ba9\u79d1\u6280\u56ed\u9879\u76ee\u6b63\u5f0f\u6d6e\u51fa\u6e56\u9762\uff0c\u59da\u6587\u76db\u53ea\u662f\u526f\u5904\u7ea7\u7684\u501f\u8c03\u4eba\u5458\uff0c\u4f46\u662f\u4ed6\u80cc\u540e\u7684\u987e\u5bb6\u5374\u662f\u6709\u8fd9\u4e2a\u8d44\u683c\u7684\u3002</p></article>"}}}]);
