(window.webpackJsonp=window.webpackJsonp||[]).push([[554],{577:function(p){p.exports={chapterTitle:null,title:"\u7b2c554\u7ae0 \u79d1\u6280\u56ed\u8ba1\u5212",content:"<article><h2>\u7b2c554\u7ae0 \u79d1\u6280\u56ed\u8ba1\u5212</h2><p>\u3000\u3000\u72ee\u5b50\u56ed\u522b\u5885\u533a\uff0c\u51cc\u6668\u53d1\u767d\u7684\u6708\u7259\u60ac\u5728\u94c5\u84dd\u8272\u7684\u591c\u7a7a\u4e2d\uff0c\u4eff\u4f5b\u6d45\u91d1\u8272\u7684\u6302\u4ef6\uff0c\u72ee\u5b50\u5c71\u9732\u51fa\u72f0\u72de\u7684\u9ed1\u5f71\uff0c\u6c5f\u6c34\u6d9b\u58f0\u7a78\u7c0c\u4f20\u6765\uff0c\u5f20\u606a\u3001\u53f6\u5efa\u658c\u3001\u59da\u6587\u76db\u5750\u5728\u5ba2\u5385\u7684\u6c99\u53d1\u4e0a\uff0c\u5c06\u4e24\u74f6\u7ea2\u74f6\u559d\u5b8c\uff0c\u5496\u5561\u4e5f\u996e\u4e86\u4e24\u4e09\u58f6\uff0c\u53f6\u5efa\u658c\u8dd1\u5230\u53a8\u623f\u53c8\u716e\u4e86\u4e00\u58f6\u5496\u5561\u8fc7\u6765\u7ed9\u5f20\u606a\u3001\u59da\u6587\u76db\u8fd8\u6709\u81ea\u5df1\u5404\u6dfb\u4e86\u4e00\u676f\u3002\u770b\u7740\u5916\u9762\u7684\u5929\u8272\uff0c\u4f3c\u4e4e\u662f\u6708\u8272\u91cc\u6e17\u7740\u6668\u5149\u4e86\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5728\u5ba2\u5385\u91cc\u5750\u4e86\u6709\u4e94\u4e2a\u5c0f\u65f6\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u4e2d\u592e\u53d1\u5e03\u53d1\u5c55\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u7684\u65b9\u9488\uff0c\u5404\u5730\u54cd\u5e94\u7eb7\u7eb7\u6210\u7acb\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u56ed\u4e0e\u9ad8\u65b0\u6280\u672f\u521b\u4e1a\u670d\u52a1\u4e2d\u5fc3\uff0c\u201d\u59da\u6587\u76db\u4f83\u4f83\u800c\u8c08\uff0c\u4ed6\u8981\u5c06\u81ea\u5df1\u7684\u89c1\u8bc6\u4e0e\u60f3\u6cd5\u5c55\u73b0\u51fa\u6765\uff0c\u201c\u9ad8\u65b0\u6280\u672f\u521b\u4e1a\u670d\u52a1\u4e2d\u5fc3\u8131\u80ce\u4e8e\u4f01\u4e1a\u5b75\u5316\u5668\u8fd9\u4e00\u6982\u5ff5\uff0c87\u5e74\u5f15\u5165\u56fd\u5185\uff0c\u6b21\u5e74\u7eb3\u5165\u56fd\u5bb6\u706b\u70ac\u8ba1\u5212\uff0c\u4e3a\u521d\u521b\u671f\u7684\u4f01\u4e1a\u63d0\u4f9b\u4f4e\u79df\u91d1\u529e\u516c\u573a\u5730\u3001\u57fa\u7840\u529e\u516c\u8bbe\u65bd\u4e0e\u57fa\u672c\u529e\u516c\u670d\u52a1\uff0c\u5728\u878d\u8d44\u4e0e\u4f01\u4e1a\u54a8\u8be2\u65b9\u9762\u63d0\u4f9b\u5e2e\u52a9\u6765\u57f9\u80b2\u8fd9\u4e9b\u521d\u521b\u671f\u3001\u8fd8\u5904\u4e8e\u2018\u5375\u2019\u9636\u6bb5\u7684\u4f01\u4e1a\u5feb\u901f\u6210\u957f\uff1b\u5f53\u8fd9\u4e9b\u4f01\u4e1a\u6210\u719f\u4e4b\u540e\uff0c\u5c31\u5c06\u5176\u4e22\u5230\u9ad8\u65b0\u4ea7\u4e1a\u56ed\u533a\u6216\u5176\u4ed6\u573a\u6240\u6269\u5927\u53d1\u5c55\u89c4\u6a21\u2026\u2026\u5bfb\u5e38\u610f\u4e49\u4e0a\uff0c\u521b\u4e1a\u4e2d\u5fc3\u662f\u4ee5\u4f01\u4e1a\u5b75\u5316\u4e3a\u76ee\u7684\uff0c\u4f46\u662f\u4e5f\u53ef\u4ee5\u4ee5\u57f9\u80b2\u6280\u672f\u9879\u76ee\u4e3a\u76ee\u7684\uff1b\u5bfb\u5e38\u610f\u4e49\u4e0a\uff0c\u4f01\u4e1a\u5b75\u5316\u5668\u662f\u901a\u8fc7\u4e0a\u5e02\u3001\u63a7\u80a1\u3001\u8f6c\u8ba9\u6280\u672f\u3001\u51fa\u552e\u4f01\u4e1a\u7684\u5f62\u5f0f\u6765\u83b7\u5f97\u5229\u76ca\u7684\uff0c\u4f46\u662f\u4e5f\u53ef\u4ee5\u4e13\u95e8\u5b75\u5316\u81ea\u8eab\u80fd\u5438\u5f15\u7684\u6280\u672f\u9879\u76ee\u3002\u7231\u8fbe\u96c6\u56e2\u4e3a\u5168\u9762\u8fdb\u519b\u6d88\u8d39\u7535\u5b50\u5e02\u573a\uff0c\u8981\u8054\u5408\u6210\u7acb\u7684\u79d1\u7814\u5ba4\u592a\u591a\uff0c\u8fd8\u4e0d\u5982\u90fd\u6574\u5408\u5230\u4e00\u5bb6\u79d1\u6280\u56ed\u91cc\u53bb\u3002\u7231\u8fbe\u96c6\u56e2\u8bbe\u7acb\u7684\u79d1\u6280\u56ed\u5e94\u8be5\u4e0d\u540c\u4e8e\u4e00\u822c\u610f\u4e49\u4e0a\u7684\u4f01\u4e1a\u5b75\u5316\u5668\uff0c\u51c6\u786e\u7684\u8bf4\u5e94\u8be5\u662f\u6280\u672f\u9879\u76ee\u5b75\u5316\u5668\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u653e\u4e0b\u624b\u91cc\u7684\u5496\u5561\u676f\uff0c\u59da\u6587\u76db\u4eca\u591c\u6240\u8a00\u867d\u7136\u4e0e\u81ea\u5df1\u5fc3\u91cc\u6240\u60f3\u6709\u4e00\u4e9b\u5dee\u8ddd\uff0c\u4f46\u662f\u5728\u4e5d\u4e03\u5e74\uff0c\u4ed6\u7684\u89c1\u8bc6\u7b97\u5f97\u4e0a\u5353\u5c14\u4e0d\u51e1\uff0c\u4e00\u4e2a\u591a\u6708\u524d\u5728\u9152\u5427\u9493\u5230\u5317\u5916\u7684\u5b66\u751f\u59b9\u5c31\u53bb\u591c\u5e97\u5531\u6b4c\u7684\u59da\u6587\u76db\u8eab\u4e0a\u6d41\u9732\u51fa\u9ad8\u5e72\u5b50\u5f1f\u4e0d\u7f81\u653e\u7eb5\u7684\u7ea8\u7ed4\u6c14\u606f\uff0c\u6709\u7740\u4e9b\u8bb8\u7684\u9893\u5e9f\uff0c\u6b64\u65f6\u7684\u59da\u6587\u76db\u5374\u9510\u610f\u8fdb\u53d6\u3001\u950b\u8292\u6bd5\u9732\uff0c\u4ece\u5317\u4eac\u90ae\u7535\u5b66\u9662\u7814\u7a76\u751f\u6bd5\u4e1a\u7684\u4ed6\uff0c\u5728\u4ed6\u8fc8\u5165\u4e09\u5341\u5c81\u4e4b\u524d\u7684\u4e00\u4e2a\u5e74\u5934\uff0c\u8eab\u4f53\u91cc\u4f3c\u4e4e\u6709\u4ec0\u4e48\u4e1c\u897f\u5728\u89c9\u9192\u3002</p><p>\u3000\u3000\u5f20\u606a\u4eff\u4f5b\u770b\u5230\u8fd9\u4e2a\u5e74\u9f84\u7684\u81ea\u5df1\uff0c\u5f53\u7136\uff0c\u81ea\u5df1\u53ef\u6ca1\u6709\u80cc\u666f\u8fd9\u4e48\u6df1\u8fdc\u7684\u5bb6\u5ead\u652f\u6491\uff0c\u5fc3\u5883\u7684\u76f8\u4eff\u5427\uff0c\u96be\u602a\u53f6\u5efa\u658c\u4e0e\u59da\u6587\u76db\u8fd1\u5e74\u6765\u6ca1\u6709\u4ec0\u4e48\u8054\u7cfb\uff0c\u752b\u89c1\u9762\u5c31\u628a\u4ed6\u5f80\u72ee\u5b50\u56ed\u9886\uff0c\u4e5f\u662f\u6c14\u5473\u76f8\u6295\u7684\u7f18\u6545\u5427\uff1f</p><p>\u3000\u3000\u201c\u9526\u6e56\u8bbe\u7acb\u79d1\u6280\u56ed\u7684\u521d\u8877\u7684\u786e\u8981\u5b75\u5316\u6211\u4eec\u8fdb\u519b\u6d88\u8d39\u7535\u5b50\u5e02\u573a\u6240\u9700\u8981\u7684\u4ea7\u54c1\u6280\u672f\uff0c\u8fd9\u7b97\u662f\u2018\u81ea\u6211\u2019\u5b75\u5316\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u662f\u6280\u672f\u5b75\u5316\u3002\u4e0d\u9700\u8981\u7b49\u5230\u5341\u5e74\u3001\u4e8c\u5341\u5e74\u540e\u518d\u6765\u540e\u6094\uff0c\u6211\u4eec\u73b0\u5728\u5df2\u7ecf\u770b\u5230\uff0c\u5728\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u7684\u98df\u7269\u88e2\u4e2d\uff0c\u56fd\u5185\u7535\u5b50\u4f01\u4e1a\u82e5\u662f\u4e0d\u53d1\u5c55\u5177\u6709\u77e5\u8bc6\u4ea7\u6743\u7684\u4ea7\u54c1\u4e0e\u6838\u5fc3\u5143\u4ef6\u6280\u672f\uff0c\u6700\u7ec8\u53ea\u80fd\u5904\u5728\u8fd9\u6761\u98df\u7269\u88e2\u7684\u672b\u7aef\uff0c\u6210\u4e3a\u4e0a\u6e38\u5382\u5546\u7684\u98df\u7269\u6e90\u3002\u79d1\u6280\u56ed\u7684\u6280\u672f\u5b75\u5316\u5c31\u662f\u8981\u6df1\u5ea6\u6316\u6398\u5efa\u90ba\u5e02\u7684\u6280\u672f\u8d44\u6e90\u5e76\u83b7\u5f97\u5168\u7403\u8303\u56f4\u7684\u6280\u672f\u4e13\u5229\u4ea7\u6743\u4e3a\u76ee\u7684\uff0c\u4e5f\u5c06\u4f9d\u6258\u9526\u6e56\u8bbe\u4e8e\u7f8e\u56fd\u7845\u8c37\u3001\u65e5\u672c\u7b51\u6ce2\u5e02\u7684\u524d\u6cbf\u6280\u672f\u5f00\u53d1\u5b9e\u9a8c\u57fa\u5730\uff0c\u5b9e\u73b0\u9ad8\u7ea7\u4eba\u624d\u4e0e\u524d\u6cbf\u6280\u672f\u7684\u53cc\u5411\u4ea4\u6d41\uff0c\u6700\u7ec8\u4f7f\u5f97\u79d1\u6280\u56ed\u6210\u4e3a\u9526\u6e56\u8fdb\u884c\u6d88\u8d39\u7535\u5b50\u5e02\u573a\u7684\u6280\u672f\u540e\u76fe\u2026\u2026\u201d\u6709\u53f6\u5bb6\u8001\u7237\u5b50\u4e3a\u5f15\u8350\u4eba\uff0c\u5f20\u606a\u4e5f\u4e0d\u518d\u85cf\u7740\u6396\u7740\uff0c\u59da\u6587\u76db\u7684\u51fa\u73b0\uff0c\u5f20\u606a\u662f\u6709\u610f\u5916\u4e4b\u559c\u7684\uff0c\u4ed6\u7684\u8eab\u4efd\u3001\u4ed6\u7684\u9605\u5386\u4e0e\u4e13\u4e1a\u7d20\u517b\u7b80\u76f4\u5c31\u662f\u4e3a\u9526\u6e56\u6b64\u65f6\u7684\u5b8f\u5927\u8ba1\u5212\u6240\u91cf\u8eab\u5b9a\u5236\uff0c\u66f4\u4e3a\u96be\u5f97\u7684\u662f\u5927\u5bb6\u6027\u60c5\u8fd8\u76f8\u6295\uff0c\u4ed6\u4e5f\u6beb\u4e0d\u63a9\u9970\u79d1\u6280\u56ed\u7684\u76ee\u6807\u5730\u5c31\u662f\u5efa\u90ba\u5e02\uff0c\u201c\u518d\u884d\u751f\u4e00\u5c42\uff0c\u79d1\u6280\u56ed\u53ef\u4ee5\u5b75\u5316\u9526\u6e56\u6240\u9700\u8981\u7684\u914d\u5957\u4f01\u4e1a\uff0c\u9526\u6e56\u65d7\u4e0b\u76ee\u524d\u4ee5\u65b0\u5149\u7eb8\u4e1a\u4e0e\u7231\u8fbe\u7535\u5b50\u4e3a\u4e3b\u4f53\uff0c\u5173\u8054\u7684\u4f01\u4e1a\u8fd8\u5305\u62ec\u9999\u96ea\u6d77\u7535\u5668\u2026\u2026\u201d\u5f20\u606a\u770b\u4e86\u53f6\u5efa\u658c\u4e00\u773c\uff0c\u201c\u8981\u662f\u53f6\u54e5\u613f\u610f\uff0c\u6211\u4e5f\u53ef\u4ee5\u5c06\u76db\u946b\u7535\u5668\u8003\u8651\u8fdb\u53bb\u2026\u2026\u914d\u5957\u4f01\u4e1a\u5c06\u6570\u4ee5\u767e\u8ba1\uff0c\u6709\u4e9b\u914d\u5957\u4f01\u4e1a\u505a\u5f97\u76f8\u5f53\u597d\u5e76\u9010\u6b65\u6210\u957f\uff0c\u4f46\u662f\u6709\u4e9b\u914d\u5957\u670d\u52a1\uff0c\u6211\u4eec\u8fd8\u627e\u4e0d\u5230\u6ee1\u610f\u7684\uff0c\u90fd\u53ef\u4ee5\u6709\u8ba1\u5212\u7684\u57f9\u80b2\u51fa\u6765\uff1b\u9664\u6b64\u4e4b\u5916\uff0c\u5f53\u7136\uff0c\u9526\u6e56\u5e76\u4e0d\u6392\u65a5\u5e2e\u52a9\u6709\u8be6\u7ec6\u5546\u4e1a\u8ba1\u5212\u7684\u56e2\u961f\u6216\u4e2a\u4eba\u5728\u79d1\u6280\u56ed\u91cc\u5b75\u5316\u81ea\u5df1\u7684\u7535\u5b50\u4fe1\u606f\u4f01\u4e1a\u2026\u2026\u56fd\u5185\u7684\u4f01\u4e1a\u5b75\u5316\u5668\u8fd8\u53ea\u662f\u63d0\u4f9b\u521d\u9636\u6bb5\u7684\u521b\u4e1a\u670d\u52a1\uff0c\u7136\u800c\u56fd\u5185\u4e2d\u5c0f\u9ad8\u65b0\u4f01\u4e1a\u521b\u4e1a\u9636\u6bb5\u6700\u7f3a\u4e4f\u7684\u5374\u662f\u8d44\u91d1\uff0c\u79d1\u6280\u56ed\u8fd8\u5c06\u8bbe\u7acb\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\uff0c\u4e3a\u4e86\u8d44\u683c\u8fdb\u5165\u79d1\u6280\u56ed\u7684\u9879\u76ee\u63d0\u4f9b\u4e00\u5b9a\u7684\u98ce\u9669\u6295\u8d44\u2026\u2026\u201d</p><p>\u3000\u3000\u56fd\u5916\u7684\u521b\u4e1a\u57f9\u80b2\u65e9\u5df2\u7ecf\u5f62\u6210\u4f01\u4e1a\u5b75\u5316\u4e0e\u98ce\u9669\u6295\u8d44\u878d\u4e3a\u4e00\u4f53\u7684\u6a21\u5f0f\uff0c\u56fd\u5185\u5404\u4e2a\u57ce\u5e02\u7684\u9ad8\u65b0\u6280\u672f\u521b\u4e1a\u670d\u52a1\u4e2d\u5fc3\u4e5f\u52aa\u529b\u8981\u4e3a\u4f01\u4e1a\u63d0\u4f9b\u878d\u8d44\u670d\u52a1\uff0c\u4f46\u662f\u5b9e\u9645\u60c5\u51b5\u5374\u53c8\u662f\u5982\u4f55\uff1f\u636e\u59da\u6587\u76db\u4e86\u89e3\u5efa\u90ba\u5e02\u653f\u5e9c\u524d\u540e\u4e09\u6b21\u4e3a\u521b\u4e1a\u4e2d\u5fc3\u5212\u51fa\u7684\u521b\u4e1a\u57fa\u91d1\u603b\u5171\u624d\u516d\u767e\u4e07\u5143\uff0c\u8981\u662f\u9526\u6e56\u7684\u79d1\u6280\u56ed\u9879\u76ee\u63d0\u4f9b\u4f01\u4e1a\u5b75\u5316\u670d\u52a1\u5e76\u8bbe\u7acb\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u5c06\u662f\u56fd\u5185\u9996\u5bb6\u79c1\u8425\u4f01\u4e1a\u5b75\u5316\u5668\u4e0e\u9996\u5bb6\u98ce\u9669\u6295\u8d44\u57fa\u91d1\uff0c\u610f\u4e49\u975e\u540c\u51e1\u54cd\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e4b\u524d\u4ee5\u7231\u8fbe\u96c6\u56e2\u5411\u6606\u817e\u5728\u7ebf\u6ce8\u8d44\uff0c\u4e5f\u5e94\u8be5\u5c5e\u4e8e\u98ce\u9669\u6295\u8d44\u7684\u8303\u7574\uff0c\u53ea\u662f\u90a3\u5c5e\u4e8e\u4e2a\u4f8b\uff0c\u8fd8\u6ca1\u6709\u5f62\u6210\u7edf\u4e00\u7684\u98ce\u6295\u57fa\u91d1\u7ba1\u7406\u6a21\u5f0f\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u95ee\u9053\uff1a\u201c\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u7684\u89c4\u6a21\u5927\u7ea6\u6709\u591a\u5927\uff1f\u201d</p><p>\u3000\u3000\u201c\u516d\u4e09\u4e00\u5206\u6210\uff0c\u79d1\u6280\u56ed\u524d\u671f\u8ba1\u5212\u6295\u5165\u5341\u4e2a\u4ebf\uff0c\u7528\u4e8e\u9526\u6e56\u81ea\u8eab\u6280\u672f\u5b75\u5316\u7684\u8d44\u91d1\u5360\u516d\u6210\uff0c\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u5360\u4e09\u6210\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e09\u4e2a\u4ebf\uff01\u201d\u5f20\u606a\u8bdd\u8fd8\u6ca1\u6709\u8bf4\u5b8c\uff0c\u53f6\u5efa\u658c\u7d27\u5f20\u7684\u6293\u4f4f\u5f20\u606a\u7684\u80f3\u818a\uff0c\u201c\u8981\u4e0d\u662f\u8ba9\u76db\u946b\u4e5f\u8fdb\u5165\u79d1\u6280\u56ed\u5b75\u5316\u5b75\u5316\uff1f\u201d</p><p>\u3000\u3000\u201c\u9526\u6e56\u73b0\u5728\u4e0d\u80fd\u5206\u5fc3\u505a\u522b\u7684\uff0c\u201d\u5f20\u606a\u77e5\u9053\u76db\u946b\u7f3a\u4e4f\u8d44\u91d1\u6269\u5f20\uff0c\u4ed6\u53ea\u80fd\u62b1\u6b49\u7684\u518d\u5ea6\u62d2\u7edd\u53f6\u5efa\u658c\uff0c\u201c\u79d1\u6280\u56ed\u8fd8\u8ba1\u5212\u4e0e\u56fd\u5bb6\u6709\u5173\u90e8\u59d4\u8054\u5408\u7ec4\u7ec7\u4e00\u4e2a\u9f13\u52b1\u9ad8\u6821\u5b66\u751f\u8bfe\u5916\u5b66\u672f\u53d1\u660e\u521b\u9020\u4e0e\u521b\u4e1a\u8ba1\u5212\u7684\u8d5b\u4e8b\uff0c\u5269\u4e0b\u7684\u4e00\u4e2a\u4ebf\u8d44\u91d1\uff0c\u5c06\u7528\u4e8e\u8d5b\u4e8b\u7684\u7ec4\u7ec7\u3001\u5ba3\u4f20\u3001\u5956\u52b1\u751a\u81f3\u53ef\u4ee5\u5411\u83b7\u5956\u7684\u521b\u4e1a\u8ba1\u5212\u4e0e\u53d1\u660e\u63d0\u4f9b\u4e00\u5b9a\u7684\u521b\u4e1a\u6295\u8d44\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7b49\u7b49\uff0c\u201d\u53f6\u5efa\u658c\u542c\u51fa\u4e9b\u4e0d\u5bf9\u52b2\u6765\uff0c\u201c\u5341\u4e2a\u4ebf\uff0c\u4f60\u90fd\u4e00\u5206\u4e0d\u5dee\u7684\u90fd\u6709\u4e86\u5b89\u6392\uff0c\u79d1\u6280\u56ed\u7684\u57fa\u5efa\u6295\u8d44\u5462\uff0c\u662f\u4ece\u516d\u4e2a\u4ebf\u91cc\u5212\u51fa\u6765\uff0c\u8fd8\u662f\u4ece\u4e09\u4e2a\u4ebf\u91cc\u5212\u51fa\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e16\u7eaa\u9526\u6e56\u6210\u7acb\u7684\u610f\u4e49\u662f\u4ec0\u4e48\uff0c\u4ec5\u4ec5\u662f\u4e3a\u9910\u996e\u5a31\u4e50\u516c\u53f8\u5f00\u53d1\u5546\u4e1a\u5730\u4ea7\uff1f\u201d\u5f20\u606a\u95ee\u53f6\u5efa\u658c\uff0c\u90b5\u81f3\u521a\u7ec4\u5efa\u4e16\u7eaa\u9526\u6e56\u65f6\uff0c\u7ecf\u5f20\u606a\u7684\u4ecb\u7ecd\uff0c\u5c06\u53f6\u5bb6\u4e5f\u62c9\u4e86\u8fdb\u53bb\u2014\u2014\u53f6\u5efa\u658c\u672c\u8eab\u5bf9\u5730\u4ea7\u5f00\u53d1\u5c31\u5f88\u96be\u5174\u8da3\uff0c\u201c\u79d1\u6280\u56ed\u6240\u9700\u8981\u7684\u57fa\u5730\uff0c\u96be\u9053\u4e0d\u662f\u4e16\u7eaa\u9526\u6e56\u5e94\u8be5\u7eb3\u5165\u7684\u4e1a\u52a1\u8303\u7574\u5417\uff1f\u4e16\u7eaa\u9526\u6e56\u5f00\u53d1\u5b8c\u6210\uff0c\u9526\u6e56\u627f\u79df\u5c31\u53ef\u4ee5\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u79d1\u6280\u56ed\u5e94\u8be5\u8981\u7b97\u5de5\u4e1a\u5efa\u7b51\u5427\uff1f\u201d\u53f6\u5efa\u658c\u8fdf\u7591\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u8981\u6700\u5927\u9650\u5ea6\u7684\u8d34\u8fd1\u5efa\u90ba\u5e02\u7684\u9ad8\u6821\u4e0e\u79d1\u7814\u673a\u6784\uff0c\u6211\u4f1a\u5c06\u79d1\u6280\u56ed\u6446\u5230\u6c5f\u5317\u7684\u65b0\u6d66\u9ad8\u65b0\u4ea7\u4e1a\u56ed\u533a\u53bb\u5417\uff1f\u6211\u4e5f\u6ca1\u6709\u8bf4\u79d1\u6280\u56ed\u5c31\u4e00\u5b9a\u662f\u6b7b\u677f\u7684\u5de5\u4e1a\u5efa\u7b51\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6211\u5e0c\u671b\u5728\u71d5\u5f52\u6e56\u7554\u51fa\u73b0\u4e00\u5ea7\u5927\u5b66\u6821\u56ed\u5f0f\u7684\u79d1\u6280\u56ed\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u624d\u662f\u9526\u6e56\u79d1\u6280\u56ed\u9879\u76ee\u8ba1\u5212\u7684\u5168\u8c8c\u5427\uff1f\u59da\u6587\u76db\u8fd8\u6ca1\u6709\u6df1\u5165\u7684\u63a5\u89e6\u5230\u9526\u6e56\u4f53\u7cfb\u7684\u6df1\u5904\u53bb\uff0c\u5373\u4f7f\u4e0d\u6653\u5f97\u7231\u8fbe\u7535\u5b50\u7684\u8f89\u714c\u6210\u5c31\uff0c\u79d1\u6280\u56ed\u9879\u76ee\u8ba1\u5212\u4e5f\u662f\u4e00\u9879\u8ba9\u4eba\u53f9\u4e3a\u89c2\u6b62\u7684\u5b8f\u5927\u8ba1\u5212\uff0c\u59da\u6587\u76db\u770b\u7740\u773c\u524d\u7684\u5f20\u606a\uff0c\u4eff\u4f5b\u662f\u4e2a\u68a6\u60f3\u5bb6\uff0c\u800c\u8fd9\u4e2a\u770b\u8d77\u6765\u9065\u8fdc\u7684\u68a6\u8fdc\uff0c\u5374\u53c8\u90a3\u6837\u7684\u89e6\u624b\u53ef\u5f97\u3002</p><p>\u3000\u3000\u9526\u6e56\u7684\u79d1\u6280\u56ed\u9879\u76ee\u5206\u6210\u4e09\u5927\u90e8\u5206\uff0c\u8017\u8d44\u6700\u591a\u3001\u5229\u7528\u5efa\u90ba\u6280\u672f\u8d44\u6e90\u8fdb\u884c\u81ea\u8eab\u6280\u672f\u5b75\u5316\u770b\u8d77\u6765\u5f71\u54cd\u6700\u5c0f\u3002\u4e09\u4e2a\u4ebf\u7684\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u6216\u8bb8\u4f1a\u6311\u9009\u6d88\u8d39\u7535\u5b50\u76f8\u5173\u9886\u57df\u7684\u521b\u4e1a\u8005\uff0c\u4f46\u662f\u5982\u6b64\u89c4\u6a21\u7684\u98ce\u9669\u57fa\u91d1\uff0c\u5bf9\u4e8e\u7f3a\u4e4f\u8d44\u91d1\u5c31\u5982\u540c\u597d\u51e0\u5e74\u6ca1\u6709\u5438\u5230\u4eba\u8840\u7684\u5438\u8840\u9b3c\u4e00\u6837\u7684\u521b\u4e1a\u8005\u6216\u521b\u4e1a\u56e2\u961f\uff0c\u5927\u6982\u4f1a\u6709\u5f88\u591a\u4eba\u8702\u62e5\u5230\u5efa\u90ba\u6765\u5bfb\u627e\u673a\u4f1a\u3002\u8fd8\u6709\u90a3\u4e2a\u5927\u5b66\u751f\u521b\u4e1a\u4e0e\u5b66\u672f\u53d1\u660e\u8d5b\u4e8b\u8ba1\u5212\uff0c\u542c\u8d77\u6765\u4e5f\u662f\u90a3\u4e48\u7684\u8bf1\u4eba\u5fc3\u52a8\uff0c\u59da\u6587\u76db\u65e9\u5c31\u542c\u8bf4\u79d1\u534f\u3001\u6559\u80b2\u9ad8\u6559\u53f8\u6709\u610f\u4e3e\u529e\u8fd9\u6837\u7684\u6d3b\u52a8\uff0c\u5374\u4e00\u76f4\u56e0\u4e3a\u7ecf\u8d39\u7684\u95ee\u9898\u6ca1\u80fd\u6b63\u5f0f\u63d0\u5230\u65e5\u7a0b\u4e0a\u6765\uff0c\u8981\u662f\u7231\u8fbe\u96c6\u56e2\u80fd\u63d0\u4f9b\u4e00\u4e2a\u4ebf\u7684\u8d44\u91d1\u534f\u529e\uff0c\u90a3\u5b9e\u73b0\u7684\u53ef\u80fd\u6027\u5c31\u6781\u5ea6\u4e0a\u5347\uff0c\u8981\u662f\u80fd\u6210\u529f\u80fd\u5b9e\u65bd\u7684\u8bdd\uff0c\u5bf9\u7231\u8fbe\u96c6\u56e2\u3001\u5bf9\u79d1\u6280\u56ed\u4e5f\u6709\u7740\u6781\u5927\u7684\u4fc3\u8fdb\u4f5c\u7528\u3002</p><p>\u3000\u3000\u79d1\u6280\u672f\u56ed\u9879\u76ee\u7684\u4e09\u4e2a\u90e8\u5206\uff0c\u7b2c\u4e00\u90e8\u5206\uff0c\u4e0d\u9700\u8981\u653f\u5e9c\u4ecb\u5165\uff0c\u7b2c\u4e8c\u90e8\u5206\uff0c\u653f\u5e9c\u8981\u8d77\u5f15\u5bfc\u7684\u4f5c\u7528\uff0c\u7b2c\u4e09\u90e8\u5206\uff0c\u653f\u5e9c\u5c31\u4f1a\u5360\u636e\u4e3b\u52a8\uff0c\u5bf9\u59da\u6587\u76db\u6765\u8bf4\uff0c\u8981\u662f\u81ea\u59cb\u81ea\u7ec8\u7684\u53c2\u4e0e\u6574\u4e2a\u7684\u79d1\u6280\u56ed\u9879\u76ee\uff0c\u4e5f\u5c31\u4f1a\u6709\u66f4\u591a\u7684\u3001\u66f4\u51fa\u4f17\u7684\u4e2a\u4eba\u8868\u73b0\uff1a\u6b64\u65f6\uff0c\u59da\u6587\u76db\u624d\u5f7b\u5e95\u4ece\u4e0e\u5468\u5174\u4e1c\u4e89\u8054\u4fe1\u603b\u7ecf\u7406\u52a9\u7406\u4f4d\u5b50\u5931\u5229\u7684\u9634\u5f71\u91cc\u8d70\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u5728\u8fd9\u91cc\u7eb8\u4e0a\u8c08\u5175\uff0c\u8fd8\u4e0d\u5982\u5230\u73b0\u573a\u8d70\u4e00\u8d70\uff0c\u201d\u53f6\u5efa\u658c\u8bf4\u9053\uff0c\u201c\u6211\u8fd8\u60f3\u770b\u770b\u5728\u71d5\u5f52\u6e56\u5230\u5e95\u8be5\u600e\u4e48\u6837\u624d\u80fd\u5efa\u7acb\u4e00\u5ea7\u5927\u5b66\u6821\u56ed\u5f0f\u7684\u79d1\u6280\u56ed\u6765\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6253\u4e86\u4e2a\u54c8\u6b20\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u73b0\u5728\u6ca1\u8fd9\u4e2a\u597d\u7cbe\u795e\uff0c\u6307\u4e0d\u5b9a\u5750\u5230\u8f66\u91cc\u5c31\u7761\u7740\u4e86\uff0c\u6211\u8fd9\u51e0\u5929\u90fd\u7559\u5728\u5efa\u90ba\uff0c\u4e0b\u5348\u518d\u8bf4\u5427\u3002\u201d\u59da\u6587\u76db\u7684\u51fa\u73b0\u89e3\u51b3\u5f20\u606a\u6700\u5934\u75bc\u7684\u95ee\u9898\uff0c\u4ed6\u5c31\u6ca1\u6709\u5fc5\u8981\u4e1c\u5954\u897f\u8d70\u4e86\u3002</p></article>"}}}]);
