(window.webpackJsonp=window.webpackJsonp||[]).push([[556],{579:function(p){p.exports={chapterTitle:null,title:"\u7b2c556\u7ae0 \u8c01\u6765\u51fa\u9762",content:"<article><h2>\u7b2c556\u7ae0 \u8c01\u6765\u51fa\u9762</h2><p>\u3000\u3000\u5f20\u77e5\u884c\u4e0d\u662f\u4e00\u4e2a\u4eba\u8d76\u56de\u7701\u57ce\uff0c\u800c\u662f\u4e0e\u5b54\u957f\u6cb3\u8fd8\u6709\u4e91\u6c60\u9152\u4e1a\u5e02\u573a\u90e8\u53e6\u4e24\u540d\u5458\u5de5\u4e00\u8d77\u4e58\u8f66\u4ece\u65b0\u829c\u8d76\u8fc7\u6765\u3002\u52a0\u4e0a\u53f8\u673a\u4e00\u4e2a\u4e94\u4eba\uff0c\u5c31\u6324\u4e00\u8f86\u6851\u5854\u7eb3\uff0c\u8d70\u56fd\u9053\u8fc7\u6765\u8981\u56db\u4e2a\u591a\u5c0f\u65f6\uff0c\u90fd\u4e0d\u6653\u5f97\u8f66\u91cc\u6324\u5f97\u96be\u53d7\u4e0d\u96be\u7231\uff1b\u6362\u5f20\u606a\uff0c\u77ed\u9014\u6324\u6324\u8fd8\u884c\uff0c\u957f\u9014\u65e9\u5c31\u5206\u4e24\u90e8\u8f66\u8d70\u4e86\u3002</p><p>\u3000\u3000\u5728\u6210\u90fd\u4e3e\u529e\u7684\u7cd6\u9152\u4ea4\u6613\u4f1a\u4e0a\uff0c\u4e91\u6c60\u9152\u4e1a\u4e00\u63b7\u5343\u91d1\uff0c\u4e00\u4e2a\u661f\u671f\u7684\u4f1a\u5c55\u65f6\u95f4\uff0c\u5728\u6210\u90fd\u7528\u6389\u7684\u5ba3\u4f20\u8d39\u7528\u8d85\u8fc7\u4e24\u767e\u4e07\uff0c\u5b54\u957f\u6cb3\u5c06\u6210\u90fd\u60df\u6709\u7684\u4e09\u53ea\u98de\u8247\u90fd\u79df\u4e86\u8fc7\u6765\u5ba3\u53d1\u4f20\u5355\uff0c\u5438\u5f15\u4ece\u5168\u56fd\u5404\u5730\u524d\u6765\u53c2\u52a0\u7cd6\u9152\u4ea4\u6613\u4f1a\u7684\u7ecf\u9500\u5546\u7684\u6ce8\u610f\uff0c\u4ec5\u5728\u7cd6\u9152\u4ea4\u6613\u4f1a\u4e0a\u6240\u83b7\u5f97\u7684\u8ba2\u5355\u5c31\u9ad8\u8fbe\u4e24\u4e2a\u4ebf\u3002</p><p>\u3000\u3000\u8981\u6653\u5f97\u4e91\u6c60\u9152\u4e1a\u91cd\u7ec4\u4e4b\u524d\u7684\u51e0\u5bb6\u9152\u5382\u4e00\u5e74\u7684\u8425\u9500\u989d\u52a0\u8d77\u6765\u90fd\u4e0d\u5230\u4e24\u4e2a\u4ebf\u3002</p><p>\u3000\u3000\u53d6\u5f97\u8fd9\u6837\u7684\u6210\u7ee9\u5f88\u5bb9\u6613\u8ba9\u4eba\u5934\u8111\u53d1\u660f\u7684\uff0c\u8fd8\u597d\u5f20\u77e5\u884c\u7684\u5934\u8111\u662f\u6e05\u9192\u7684\uff0c\u4e4b\u524d\u5bf9\u4e91\u6c60\u9152\u4e1a\u8fd8\u6bd4\u8f83\u653e\u6743\uff0c\u6b64\u65f6\u5c31\u4eb2\u81ea\u5e72\u9884\u5230\u6e20\u9053\u5efa\u8bbe\u3001\u917f\u9152\u5de5\u827a\u6539\u826f\u3001\u54c1\u8d28\u63a7\u5236\u3001\u8d22\u52a1\u7ba1\u7406\u7b49\u5404\u4e2a\u73af\u8282\u4e2d\u53bb\uff1b\u53ea\u8981\u5728\u9ad8\u901f\u6269\u5f20\u671f\u80fd\u63a7\u5236\u4f4f\u6240\u6709\u7684\u73af\u8282\u4e0d\u4f1a\u56e0\u4e3a\u54c1\u724c\u7684\u6025\u901f\u6269\u5f20\u800c\u5d29\u6e83\uff0c\u4f01\u4e1a\u624d\u7b97\u8fc8\u51fa\u8d70\u5411\u8f89\u714c\u7684\u7b2c\u4e00\u6b65\uff0c\u4e0d\u7136\u53ea\u662f\u6619\u82b1\u4e00\u73b0\u3002</p><p>\u3000\u3000\u8fd1\u671f\u4e91\u6c60\u9152\u4e1a\u5728\u5efa\u90ba\u6709\u4e00\u4e2a\u5927\u578b\u7684\u4ea7\u54c1\u63a8\u4ecb\u4f1a\uff0c\u5f20\u77e5\u884c\u4eb2\u81ea\u5230\u5efa\u90ba\u6765\u7763\u9635\u3002</p><p>\u3000\u3000\u5b54\u957f\u6cb3\u5c06\u5e02\u573a\u90e8\u4e24\u540d\u5458\u5de5\u5148\u6253\u53d1\u53bb\u5206\u516c\u53f8\u7ec4\u7ec7\u7684\u63a8\u4ecb\u4f1a\u73b0\u573a\uff0c\u8ba9\u53f8\u673a\u5f00\u8f66\u5c06\u4ed6\u4e0e\u5f20\u77e5\u884c\u9001\u5230\u8fd9\u8fb9\u7684\u522b\u5885\u533a\uff0c\u53f8\u673a\u6ca1\u6709\u505c\u987f\u4e5f\u53bb\u4e86\u63a8\u4ecb\u4f1a\u73b0\u573a\uff0c\u6bd5\u7adf\u5f20\u77e5\u884c\u4e0e\u9526\u6e56\u7684\u5173\u7cfb\u5e76\u4e0d\u9002\u5408\u516c\u5f00\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u77e5\u884c\u4e0e\u5b54\u957f\u6cb3\u521a\u8d70\u8fdb\u522b\u5885\u5ba2\u5385\uff0c\u53f6\u5efa\u658c\u4e0e\u59da\u6587\u76db\u5c31\u52fe\u80a9\u642d\u80cc\u7684\u8d70\u8fdb\u6765\uff0c\u6ca1\u5bd2\u6684\u4e24\u53e5\uff0c\u5f20\u606a\u4e0b\u697c\u6765\u8bf4\u4e86\u90a3\u4e00\u901a\u8bdd\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u63a5\u8fc7\u5f20\u606a\u9012\u7ed9\u4ed6\u7684\u51e0\u5f20\u7d20\u63cf\uff0c\u542c\u4ed6\u8bf4\u8981\u5c06\u71d5\u5f52\u6e56\u4e1c\u5230\u5e08\u5927\u5357\u95e8\u7684\u571f\u5730\u90fd\u62ff\u4e0b\u6765\uff0c\u8bb6\u5f02\u7684\u770b\u4e86\u53f6\u5efa\u658c\u4e00\u773c\u3002\u7ecf\u8fc7\u6628\u5929\u7684\u957f\u8c08\uff0c\u4ed6\u6574\u4e2a\u4eba\u90fd\u5904\u4e8e\u4ea2\u594b\u7684\u72b6\u6001\uff0c\u4ed7\u7740\u4e0e\u53f6\u5efa\u658c\u53d1\u5c0f\u7684\u4ea4\u60c5\uff0c\u5f3a\u62c9\u7740\u4ed6\u53bb\u71d5\u5f52\u6e56\u8d70\u4e86\u4e00\u5708\uff0c\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u7761\u89c9\u3002\u71d5\u5f52\u6e56\u4e0e\u5e08\u5927\u5357\u95e8\u4e4b\u524d\u6709\u4e24\u5bb6\u5370\u67d3\u5382\uff0c\u5c06\u4e24\u5bb6\u5370\u67d3\u5382\u5305\u62ec\u5728\u5185\uff0c\u90a3\u5757\u533a\u57df\u5dee\u4e0d\u591a\u6709\u516b\u767e\u4ea9\u5730\uff0c\u4f5c\u4e3a\u6280\u672f\u9879\u76ee\u5b75\u5316\u5668\u7684\u79d1\u6280\u56ed\u9700\u8981\u8fd9\u4e48\u5927\u7684\u5730\u76d8\uff1f\u8fd8\u6709\u4e3a\u4ec0\u4e48\u8981\u90fd\u79cd\u4e0a\u7ea2\u6a61\u6811\uff0c\u8fd8\u8d77\u6a61\u6811\u56ed\u8fd9\u6837\u7684\u540d\u5b57\uff1f</p><p>\u3000\u3000\u59da\u6587\u76db\u6bd5\u7adf\u5bf9\u5f20\u606a\u7684\u4e86\u89e3\u4e0d\u6df1\uff0c\u53f6\u5efa\u658c\u63a5\u8fc7\u4ed6\u624b\u91cc\u7684\u7d20\u63cf\u7ffb\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u77e5\u9053\u8fd9\u4e9b\u90fd\u51fa\u81ea\u8bb8\u601d\u7684\u624b\u7b14\uff0c\u4e00\u523b\u949f\u524d\u5f20\u606a\u6253\u7535\u8bdd\u8ba9\u4ed6\u8d76\u8fc7\u6765\u65f6\uff0c\u8fd8\u542c\u5230\u8bb8\u601d\u5728\u4ed6\u8eab\u8fb9\u7684\u58f0\u97f3\uff0c\u8fd9\u65f6\u5019\u5927\u6982\u72fc\u72c8\u7684\u79bb\u5f00\u522b\u5885\u4e86\u5427\uff0c\u5c31\u50cf\u4ed6\u601c\u60dc\u5b59\u9759\u9999\u4e00\u6837\uff0c\u4e5f\u80fd\u4f53\u4f1a\u5230\u5f20\u606a\u5bf9\u8bb8\u601d\u7684\u611f\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u5f88\u597d\u7684\u6784\u601d\u554a\uff0c\u6211\u4e5f\u5f88\u8ba8\u538c\u51b7\u51b0\u51b0\u7684\u5de5\u4e1a\u5efa\u7b51\u3001\u51b7\u51b0\u51b0\u7684\u540d\u79f0\uff0c\u5c31\u53eb\u6a61\u6811\u56ed\u597d\u4e86\u3002\u201d\u5c48\u6307\u5f39\u4e86\u5f39\u7d20\u63cf\u753b\u7eb8\uff0c\u53d1\u51fa\u6e05\u8106\u7684\u54cd\u58f0\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u80fd\u4e0d\u80fd\u5c06\u5b66\u5e9c\u5df7\u4e00\u5e76\u5212\u5165\u6a61\u6811\u56ed\u7684\u8303\u7574\u4e4b\u5185\uff1f\u201d\u4ed6\u8fd9\u662f\u5047\u516c\u6d4e\u79c1\uff0c\u5b66\u5e9c\u5df7\u65e5\u76ca\u7834\u8d25\uff0c\u5efa\u90ba\u5e02\u91cc\u867d\u7136\u5c06\u5176\u7eb3\u5165\u65e7\u57ce\u6539\u9020\u4e4b\u5217\uff0c\u4f46\u662f\u5404\u65b9\u52bf\u529b\u4e89\u62a2\u8fd9\u5757\u80a5\u8089\uff0c\u4f7f\u5f97\u5b66\u5e9c\u5df7\u6539\u9020\u9879\u76ee\u60ac\u800c\u672a\u51b3\u3002\u4ed6\u5012\u4e0d\u662f\u89ca\u89ce\u5b66\u5e9c\u5df7\u8fd9\u5757\u80a5\u8089\uff0c\u800c\u662f\u4e0d\u60f31978\u6240\u5728\u5b66\u5e9c\u5df7\u7ed9\u641e\u5f97\u4e4c\u70df\u7634\u6c14\u3002\u4ed6\u4e0e\u5efa\u90ba\u5e02\u91cc\u6ca1\u6709\u7279\u522b\u5bc6\u5207\u7684\u8054\u7cfb\uff0c\u4f46\u662f\u5c06\u5b66\u5e9c\u5df7\u6539\u9020\u4e0e\u9526\u6e56\u7684\u79d1\u6280\u56ed\u9879\u76ee\u7ed1\u5728\u4e00\u8d77\uff0c\u5c31\u5bb9\u6613\u64cd\u4f5c\u591a\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u80fd\u660e\u767d\u53f6\u5efa\u658c\u7684\u5fc3\u601d\uff0c\u53f6\u5efa\u658c\u4e4b\u524d\u4e00\u76f4\u6002\u607f\u81ea\u5df1\u8ddf\u7740\u4ed6\u4e00\u8d77\u641e\u5546\u4e1a\u5730\u4ea7\uff0c\u4e3b\u8981\u8fd8\u662f\u60f3\u5c06\u5b66\u5e9c\u5df7\u7684\u6539\u9020\u5de5\u7a0b\u63fd\u4e0b\u6765\uff1b\u5f20\u606a\u4e4b\u524d\u662f\u4e0d\u60f3\u6d89\u8db3\u5206\u5fc3\u6d89\u8db3\u5730\u4ea7\u4e1a\uff0c\u4e00\u76f4\u56de\u907f\u53f6\u5efa\u658c\u7684\u5efa\u8bae\uff0c\u79d1\u6280\u56ed\u4e5f\u8981\u59d4\u6258\u4e16\u7eaa\u9526\u6e56\u4e00\u8d77\u5f00\u53d1\u5efa\u8bbe\uff0c\u5c06\u5b66\u5e9c\u5df7\u6539\u9020\u5de5\u7a0b\u6346\u7ed1\u4e0a\u4e0d\u8fc7\u662f\u987a\u6c34\u4eba\u60c5\uff0c\u4ed6\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u6346\u7ed1\u5728\u4e00\u8d77\uff0c\u5c31\u4e00\u5b9a\u8981\u6709\u4e00\u4e2a\u6b63\u5927\u5149\u660e\u7684\u540d\u4e49\uff0c\u6211\u770b\u8fd9\u6837\u597d\u4e86\uff0c\u5b66\u5e9c\u5df7\u6574\u4e2a\u62ff\u4e0b\u6765\uff0c\u9664\u4e86\u6cbf\u8857\u5546\u94fa\u4e4b\u5916\uff0c\u5b66\u5e9c\u5df7\u4e0e\u4e1c\u6d77\u5927\u5b66\u4e1c\u56f4\u5899\u4e4b\u95f4\u4e0d\u591a\u7684\u5730\u65b9\u90fd\u62ff\u51fa\u6765\u5f00\u53d1\u9ad8\u7ea7\u4eba\u624d\u516c\u5bd3\uff0c\u7531\u79d1\u6280\u56ed\u56de\u8d2d\uff0c\u7528\u4e8e\u5ec9\u4ef7\u51fa\u79df\u7ed9\u5728\u79d1\u6280\u56ed\u5de5\u4f5c\u7684\u6280\u672f\u4eba\u5458\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u79d1\u6280\u56ed\u5efa\u6210\u4e4b\u540e\u5360\u5730\u6015\u8981\u6709\u5343\u4ea9\u4e86\u5427\uff1f\u201d\u5f20\u77e5\u884c\u65e9\u5e74\u5728\u5efa\u90ba\u4e0a\u4e86\u4e03\u5e74\u7684\u5b66\uff0c\u7531\u5de5\u4f5c\u5173\u7cfb\uff0c\u6bcf\u5e74\u5f80\u8fd4\u5efa\u90ba\u7684\u673a\u4f1a\u4e5f\u591a\uff0c\u5bf9\u71d5\u5f52\u6e56\u4e00\u5e26\u7684\u60c5\u51b5\u5f88\u4e86\u89e3\uff0c\u4ed6\u5f88\u5173\u5fc3\u9526\u6e56\u7684\u52a8\u5411\uff0c\u4f46\u662f\u901a\u5e38\u80fd\u90fd\u514b\u5236\u7740\u4e0d\u5e72\u6d89\u9526\u6e56\u7684\u4e8b\u52a1\uff0c\u65e2\u7136\u5728\u573a\uff0c\u603b\u662f\u8981\u63d2\u4e00\u4e24\u53e5\u8bdd\u7684\uff0c\u201c\u79d1\u6280\u56ed\u7684\u65b9\u6848\u90fd\u505a\u51fa\u6765\u4e86\uff1f\u5efa\u90ba\u5e02\u91cc\u73b0\u5728\u6709\u4ec0\u4e48\u52a8\u9759\u6ca1\u6709\uff1f\u6211\u770b\u79d1\u6280\u56ed\u7684\u89c4\u5212\u8981\u7b49\u5efa\u90ba\u5e02\u91cc\u5148\u7ed9\u53cd\u5e94\uff0c\u4f60\u4eec\u518d\u62ff\u51fa\u6765\u3002\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8fd8\u521a\u6765\u5f97\u53ca\u5411\u5f20\u77e5\u884c\u4ecb\u7ecd\u59da\u6587\u76db\u7684\u540d\u5b57\uff0c\u8fd8\u6ca1\u6709\u8be6\u7ec6\u4ecb\u7ecd\u59da\u6587\u76db\u7684\u8eab\u4efd\u4e0e\u80cc\u666f\uff0c\u5f20\u77e5\u884c\u770b\u5230\u53f6\u5efa\u658c\u63fd\u7740\u59da\u6587\u76db\u7684\u80a9\u8180\u8fdb\u6765\u7684\uff0c\u4e5f\u6ca1\u6709\u7ec6\u60f3\u3002</p><p>\u3000\u3000\u201c\u79d1\u6280\u56ed\u7684\u89c4\u5212\uff0c\u6211\u73b0\u5728\u624d\u6709\u4e2a\u6982\u5ff5\uff0c\u201d\u5f20\u606a\u7ed9\u4ed6\u7238\u8be6\u7ec6\u4ecb\u7ecd\u4e86\u59da\u6587\u76db\u7684\u8eab\u4efd\uff0c\u201c\u4e00\u5207\u8fd8\u6709\u6587\u76db\u5e2e\u7740\u8fd0\u7b79\u5e37\u5e44\u2026\u2026\u201d</p><p>\u3000\u3000\u521a\u624d\u9488\u5bf9\u5efa\u90ba\u5e02\u91cc\u7684\u8bdd\u6709\u4e9b\u4e0d\u59a5\uff0c\u4e0d\u8fc7\u5f20\u77e5\u884c\u4e5f\u6ca1\u6709\u89c9\u5f97\u6709\u4ec0\u4e48\u5927\u788d\uff0c\u4e5f\u77e5\u9053\u5bf9\u4e8e\u59da\u6587\u76db\u6765\u8bf4\uff0c\u5efa\u90ba\u5e02\u3001\u4e1c\u6d77\u7701\u6216\u8bb8\u662f\u4ed6\u4ed5\u9014\u4e0a\u7684\u9636\u68af\uff0c\u8fd9\u91cc\u7ad9\u7740\u7684\u8fd9\u4e9b\u4eba\u624d\u662f\u4ed6\u4ed5\u9014\u4e0a\u7684\u4f19\u4f34\uff1b\u4e0d\u8fc7\u59da\u6587\u76db\u7684\u51fa\u73b0\u4e5f\u771f\u662f\u7ed9\u9526\u6e56\u96ea\u4e2d\u9001\u70ad\u3002</p><p>\u3000\u3000\u201c\u5efa\u90ba\u5e02\u91cc\u8c01\u4f1a\u51fa\u9762\u63a8\u52a8\u8fd9\u4ef6\u4e8b\uff1f\u201d\u5f20\u77e5\u884c\u95ee\u9053\u3002</p><p>\u3000\u3000\u4e4b\u524d\uff0c\u5efa\u90ba\u5e02\u91cc\u4fdd\u6301\u6c89\u9ed8\u3001\u53cd\u5e94\u8fdf\u949d\u4e5f\u662f\u4e3b\u8981\u56e0\u4e3a\u7701\u957f\u674e\u8fdc\u6e56\u4e0e\u9526\u6e56\u7684\u50f5\u6301\u5173\u7cfb\uff0c\u6b64\u65f6\u6709\u987e\u5bb6\u5e2e\u7740\u7f13\u548c\u53cc\u65b9\u7684\u5173\u7cfb\uff0c\u79d1\u6280\u56ed\u9879\u76ee\u843d\u6237\u5efa\u90ba\u5c31\u6beb\u65e0\u7591\u95ee\u662f\u4e00\u9879\u5927\u653f\u7ee9\uff0c\u59da\u6587\u76db\u7ea7\u522b\u504f\u4f4e\uff0c\u53c8\u521d\u6765\u4e4d\u5230\uff0c\u867d\u7136\u6709\u987e\u5bb6\u5728\u80cc\u540e\u8fd0\u4f5c\uff0c\u5c45\u529f\u6700\u5927\uff0c\u4f46\u662f\u8fd9\u6b21\u8868\u9762\u7684\u529f\u52b3\u5374\u662f\u8981\u8ba9\u7ed9\u522b\u4eba\uff0c\u66f4\u4f55\u51b5\u5efa\u90ba\u5e02\u91cc\u4e5f\u7684\u786e\u9700\u8981\u7ad9\u51fa\u4e00\u4e2a\u591f\u5206\u91cf\u7684\u4eba\u6311\u5927\u6881\u3001\u63a8\u52a8\u79d1\u6280\u56ed\u9879\u76ee\u7684\u843d\u5b9e\u3002</p><p>\u3000\u3000\u201c\u6211\u80fd\u987a\u5229\u8c03\u5230\u5efa\u90ba\uff0c\u8fd8\u591a\u4e8f\u738b\u7ef4\u5747\u5e02\u957f\u5e2e\u5fd9\u2026\u2026\u201d\u59da\u6587\u76db\u6ca1\u6709\u5c06\u8bdd\u8bf4\u6b7b\uff0c\u8981\u4e0d\u662f\u5f20\u77e5\u884c\u4e3b\u52a8\u95ee\u8fd9\u4e00\u53e5\uff0c\u4ed6\u8fd8\u6ca1\u6709\u673a\u4f1a\u5c06\u738b\u7ef4\u5747\u7684\u540d\u5b57\u62ac\u51fa\u6765\uff0c\u201c\u738b\u7ef4\u5747\u5e02\u957f\u66fe\u662f\u6211\u7237\u7237\u7684\u79d8\u4e66\u2026\u2026\u201d</p><p>\u3000\u3000\u738b\u7ef4\u5747\u662f\u526f\u5e02\u957f\uff0c\u4f46\u540c\u65f6\u4e5f\u662f\u5e02\u59d4\u5e38\u59d4\uff0c\u5728\u5efa\u90ba\u5e02\u653f\u5e9c\u91cc\uff0c\u6bd4\u5e02\u59d4\u5e38\u59d4\u3001\u5e38\u52a1\u526f\u5e02\u957f\u80e1\u5b97\u5e86\u7684\u5730\u4f4d\u7565\u4f4e\uff0c\u4f46\u662f\u6bd4\u4e00\u822c\u7684\u526f\u5e02\u957f\u5730\u4f4d\u8981\u9ad8\u3002</p><p>\u3000\u3000\u6628\u591c\u624d\u77e5\u9053\u59da\u6587\u76db\u7684\u771f\u5b9e\u8eab\u4efd\uff0c\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u6536\u96c6\u4e0e\u524d\u987e\u95ee\u59d4\u5458\u987e\u957f\u5e9a\u76f8\u5173\u7684\u8d44\u6599\uff0c\u60f3\u5230\u5efa\u90ba\u5e02\u51e0\u540d\u5e38\u59d4\u6765\u5386\u90fd\u4e0d\u4f1a\u5c11\uff0c\u6ca1\u6709\u60f3\u5230\u5e38\u59d4\u526f\u5e02\u957f\u738b\u7ef4\u5747\u4f1a\u7adf\u66fe\u7ecf\u62c5\u4efb\u8fc7\u987e\u957f\u5e9a\u7684\u79d8\u4e66\uff0c\u60f3\u6765\u987e\u957f\u5e9a\u5c06\u59da\u6587\u76db\u653e\u5230\u5efa\u90ba\u6765\uff0c\u5efa\u90ba\u9664\u4e86\u53f6\u5bb6\u4e4b\u5916\uff0c\u4e5f\u4e0d\u53ef\u80fd\u6ca1\u6709\u522b\u7684\u5b89\u6392\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u738b\u7ef4\u5747\u53ea\u6709\u4ece\u62a5\u7aef\u3001\u7535\u89c6\u4e0e\u8d44\u6599\u91cc\u63d0\u53d6\u7684\u7247\u9762\u5370\u8c61\uff0c\u4e0d\u8fc7\u8fd9\u4e0d\u6253\u7d27\uff0c\u81ea\u5df1\u4ee5\u540e\u8ba4\u51c6\u59da\u6587\u76db\u5c31\u884c\uff0c\u95ee\u4ed6\u7238\uff1a\u201c\u7238\uff0c\u4e2d\u5348\u9002\u4e0d\u9002\u5408\u9080\u8bf7\u738b\u7ef4\u5747\u5e02\u957f\u4e00\u8d77\u5403\u996d\uff0c\u628a\u91d1\u4f2f\u4f2f\u4e5f\u7ea6\u4e0a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u770b\u4e86\u770b\u624b\u8868\uff0c\u8fd9\u65f6\u95f4\u996d\u5e97\u8d76\u5f97\u7d27\u7684\u5df2\u7ecf\u559d\u4e86\u4e00\u8f6e\u9152\u2026\u2026\u6015\u738b\u7ef4\u5747\u65e9\u6709\u5b89\u6392\uff0c\u8fd9\u65f6\u5019\u624d\u6253\u7535\u8bdd\u7ea6\u6709\u4e9b\u5931\u793c\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u6211\u6253\u7535\u8bdd\u8054\u7cfb\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u4e0d\u7ba1\u738b\u7ef4\u5747\u80fd\u4e0d\u80fd\u62bd\u8eab\u524d\u6765\uff0c\u5f20\u606a\u90fd\u8981\u6253\u7535\u8bdd\u8bf7\u91d1\u56fd\u6d77\u8fc7\u6765\u7528\u9910\u7684\uff1b\u91d1\u56fd\u6d77\u773c\u4e0b\u662f\u7701\u653f\u6cd5\u59d4\u526f\u4e66\u8bb0\u3001\u516c\u5b89\u5385\u5385\u957f\uff0c\u5f20\u606a\u5e73\u65f6\u5230\u5efa\u90ba\u91cc\uff0c\u4e5f\u6ca1\u6709\u673a\u4f1a\u53bb\u627e\u4ed6\uff0c\u4eca\u5929\u6b63\u597d\u4ed6\u7238\u5728\u8d76\u5230\u7701\u57ce\uff0c\u54ea\u6015\u4e0b\u5348\u4e00\u70b9\u949f\u8bf7\u91d1\u56fd\u6d77\u8fc7\u6765\u805a\u4e00\u805a\u90fd\u4e0d\u4f1a\u5510\u7a81\u3002</p><p>\u3000\u3000\u59da\u6587\u76db\u4e0a\u5348\u5df2\u7ecf\u62bd\u7a7a\u5c06\u6628\u591c\u7684\u957f\u8c08\u8ddf\u738b\u7ef4\u5747\u505a\u4e86\u6c47\u62a5\uff0c\u4e2d\u5348\u672c\u8981\u62c9\u53f6\u5efa\u658c\u4e0e\u738b\u7ef4\u5747\u4e00\u8d77\u5403\u996d\u7684\u2014\u2014\u4ee5\u4e3a\u5f20\u606a\u4e2d\u5348\u9192\u4e0d\u8fc7\u6765\uff0c\u5c31\u6ca1\u6709\u7ea6\u5f20\u606a\u2014\u2014\u5374\u7ed9\u5f20\u606a\u7684\u7535\u8bdd\u7a81\u7136\u53eb\u4e86\u8fc7\u6765\uff1b\u738b\u7ef4\u5747\u4e2d\u5348\u6ca1\u6709\u522b\u7684\u5b89\u6392\uff0c\u63a5\u5230\u59da\u6587\u76db\u7684\u7535\u8bdd\uff0c\u5c31\u5750\u8f66\u8d76\u4e86\u8fc7\u6765\uff0c\u6070\u4e0e\u91d1\u56fd\u6d77\u7684\u8f66\u5b50\u524d\u540e\u811a\u8fdb\u4e86\u72ee\u5b50\u56ed\u522b\u5885\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u738b\u7ef4\u5747\u4e0e\u987e\u5bb6\u5173\u7cfb\u6e0a\u6e90\uff0c\u4e0d\u4ec5\u66fe\u62c5\u4efb\u4e0e\u987e\u957f\u5e9a\u7684\u79d8\u4e66\uff0c\u800c\u4e14\u4e0e\u987e\u957f\u5e9a\u7684\u5c0f\u5973\u5a7f\u3001\u59da\u6587\u76db\u7684\u5c0f\u59d1\u592b\u9648\u65b0\u6c11\u662f\u540c\u5b66\u3002</p><p>\u3000\u3000\u987e\u957f\u5e9a\u6253\u7535\u8bdd\u627e\u5230\u4ed6\u8bf4\u8981\u5c06\u59da\u6587\u76db\u4e22\u5230\u5efa\u90ba\u6765\u6258\u4ed6\u7167\u987e\uff0c\u4ed6\u54ea\u6709\u4e0d\u5e94\u4e4b\u7406\uff1f\u4e0d\u5ff5\u4ee5\u5f80\u7684\u63d0\u643a\u4e4b\u60c5\uff0c\u54ea\u6015\u8003\u8651\u5230\u4ee5\u540e\u7684\u4ed5\u9014\uff0c\u4ed6\u4e5f\u8981\u4e0e\u987e\u5bb6\u641e\u597d\u5173\u7cfb\u3002</p><p>\u3000\u3000\u8fd8\u662f\u59da\u6587\u76db30\u65e5\u4eba\u5230\u5efa\u90ba\u540e\uff0c\u624d\u5411\u738b\u7ef4\u5747\u900f\u9732\u4ed6\u7684\u610f\u56fe\u3002</p><p>\u3000\u3000\u65e9\u572828\u65e5\uff0c\u5609\u4fe1\u5b9e\u4e1a\u516c\u5e03\u589e\u52a0\u5e76\u8d2d\u65b9\u6848\u5728\u4e1c\u6d77\u6380\u8d77\u8f69\u7136\u5927\u6ce2\uff0c\u5efa\u90ba\u5e02\u91cc\u624d\u5f00\u59cb\u5750\u7acb\u4e0d\u5b89\uff0c\u6015\u9526\u6e56\u7684\u79d1\u6280\u56ed\u9879\u76ee\u7ed9\u5176\u4ed6\u57ce\u5e02\u62a2\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u738b\u7ef4\u5747\u4eca\u5e74\u4e94\u5341\u5c81\uff0c\u4e2a\u5b50\u4e0d\u9ad8\uff0c\u4eba\u5f88\u7cbe\u7626\uff0c\u989d\u524d\u7684\u5934\u53d1\u90fd\u5df2\u7ecf\u6389\u5149\uff0c\u9732\u51fa\u6cb9\u4eae\u7684\u8111\u95e8\uff0c\u6c14\u8d28\u5012\u662f\u6587\u8d28\u5f6c\u5f6c\uff0c\u4e0e\u91d1\u56fd\u6d77\u5728\u5c0f\u533a\u95e8\u53e3\u9047\u5230\uff0c\u867d\u7136\u4e0d\u5927\u719f\u6089\uff0c\u4f46\u662f\u77e5\u9053\u91d1\u56fd\u6d77\u662f\u5f90\u5b66\u5e73\u4e00\u624b\u63d0\u62d4\u4e0a\u6765\u3001\u5728\u6d77\u5dde\u6302\u804c\u4e0e\u5f20\u77e5\u884c\u3001\u5f20\u606a\u7236\u5b50\u5173\u7cfb\u5bc6\u5207\uff0c\u770b\u5230\u4ed6\u8ddf\u81ea\u5df1\u4e00\u8d77\u8fdb\u5c0f\u533a\uff0c\u642d\u4e86\u4e24\u53e5\u8bdd\u63e1\u7740\u624b\u5c31\u6ca1\u6709\u5206\u5f00\u8fc7\u4e00\u8d77\u8d70\u4e86\u8fc7\u6765\uff0c\u4eb2\u5bc6\u5f97\u5c31\u50cf\u591a\u5e74\u7684\u8001\u670b\u53cb\u3002</p><p>\u3000\u3000\u738b\u7ef4\u5747\u5bf9\u5f20\u77e5\u884c\u4e5f\u975e\u5e38\u7684\u4eb2\u70ed\uff0c\u867d\u7136\u4ed6\u662f\u6b63\u5385\u7ea7\uff0c\u5f20\u77e5\u884c\u662f\u526f\u5385\u7ea7\uff0c\u4f46\u662f\u5f20\u77e5\u884c\u6709\u4e00\u4e2a\u6562\u8ddf\u7701\u957f\u674e\u8fdc\u6e56\u53eb\u677f\u7684\u513f\u5b50\uff0c\u738b\u7ef4\u5747\u4e0b\u610f\u8bc6\u7684\u89c9\u5f97\u5f20\u77e5\u884c\u7684\u8eab\u4efd\u4e0d\u4f4e\u4e8e\u81ea\u5df1\uff0c\u89c1\u5f20\u77e5\u884c\u8981\u4e0b\u53f0\u9636\u6765\u8fce\uff0c\u5fd9\u62a2\u5148\u4e00\u6b65\u63e1\u7740\u4ed6\u7684\u624b\u4e0d\u8ba9\u4ed6\u8d70\u4e0b\u53f0\u9636\u3002</p><p>\u3000\u3000\u7136\u800c\uff0c\u738b\u7ef4\u5747\u66f4\u591a\u7684\u65f6\u95f4\u662f\u5728\u6253\u91cf\u5f20\u606a\uff0c\u8fd9\u6bb5\u65f6\u95f4\uff0c\u5f20\u606a\u5728\u7701\u5185\u53d7\u5230\u5173\u6ce8\u8981\u8fdc\u6bd4\u4ed6\u8001\u5b50\u5f20\u77e5\u884c\u591a\u3002\u4ed6\u5df2\u77e5\u6628\u591c\u5f20\u606a\u3001\u53f6\u5efa\u658c\u3001\u59da\u6587\u76db\u957f\u8c08\u7684\u5185\u5bb9\u2014\u2014\u8fd8\u4e0d\u6653\u5f97\u5f20\u606a\u5df2\u7ecf\u8ddf\u59da\u6587\u76db\u8bf4\u4e86\u66f4\u5177\u4f53\u7684\u89c4\u5212\u4e0e\u6b63\u5f0f\u7684\u540d\u79f0\u2014\u2014\u4e4b\u524d\u4e5f\u6ca1\u6709\u60f3\u5230\u9526\u6e56\u7684\u79d1\u6280\u56ed\u9879\u76ee\u4f1a\u662f\u5982\u6b64\u6c14\u52bf\u6062\u5f18\u7684\u8ba1\u5212\uff0c\u4e0d\u4ec5\u4ec5\u4e3a\u4e86\u6ee1\u8db3\u7231\u8fbe\u96c6\u56e2\u81ea\u8eab\u6280\u672f\u5b75\u5316\u7684\u9700\u8981\uff0c\u4e09\u4e2a\u4ebf\u7684\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u5728\u56fd\u5185\u53ef\u8c13\u89c4\u6a21\u7a7a\u524d\uff0c\u52bf\u5fc5\u80fd\u4ece\u5168\u56fd\u5404\u5730\u5438\u5f15\u6d88\u8d39\u7535\u5b50\u76f8\u5173\u9886\u57df\u7684\u521b\u4e1a\u8005\u4e91\u96c6\u5efa\u90ba\uff0c\u5c06\u76f4\u63a5\u50ac\u5316\u5efa\u90ba\u5e02\u7684\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\uff1b\u800c\u51fa\u8d44\u8d5e\u52a9\u5168\u56fd\u9ad8\u6821\u5b66\u751f\u53c2\u4e0e\u7684\u521b\u4e1a\u4e0e\u5b66\u672f\u53d1\u660e\u8d5b\u4e8b\uff0c\u66f4\u80fd\u9020\u6210\u5168\u56fd\u6027\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u5934\u8111\uff0c\u8fd9\u9752\u5e74\u6709\u7740\u4e00\u4e2a\u8d85\u8d8a\u5e38\u4eba\u592a\u591a\u7684\u5934\u8111\uff0c\u770b\u7740\u5f20\u606a\u6c14\u5ea6\u4ece\u5bb9\u7684\u7ad9\u5728\u4ed6\u8001\u5b50\u5f20\u77e5\u884c\u7684\u8eab\u540e\u8c28\u5b88\u665a\u8f88\u7684\u8eab\u4efd\u4e0e\u793c\u8282\uff0c\u738b\u7ef4\u5747\u5fc3\u91cc\u5fae\u5fae\u7684\u611f\u6168\u7740\uff0c\u9526\u6e56\u7684\u5d1b\u8d77\u5947\u8ff9\u771f\u662f\u8ba9\u4eba\u5f88\u96be\u60f3\u8c61\u7684\u3002</p><p>\u3000\u3000\u91d1\u56fd\u6d77\u5012\u662f\u968f\u4fbf\uff0c\u5f88\u9ad8\u5174\u770b\u5230\u5f20\u606a\u5728\u77ed\u77ed\u4e24\u4e09\u5e74\u7684\u65f6\u95f4\u91cc\u7efd\u653e\u51fa\u5982\u6b64\u8000\u773c\u7684\u5149\u8292\u3002\u867d\u8bf4\u674e\u8fdc\u6e56\u5fc3\u91cc\u5bf9\u9526\u6e56\u6709\u6897\uff0c\u4f46\u662f\u91d1\u56fd\u6d77\u4e5f\u76f8\u4fe1\u674e\u8fdc\u6e56\u662f\u538b\u5236\u4e0d\u4f4f\u9526\u6e56\u7684\u5149\u8292\u7684\uff0c\u4ed6\u53ea\u662f\u5b88\u597d\u81ea\u5df1\u7684\u672c\u5206\uff0c\u4e5f\u4e0d\u4f1a\u53bb\u523b\u610f\u5df4\u7ed3\u674e\u8fdc\u6e56\uff0c\u4e5f\u4e0d\u60e7\u674e\u8fdc\u6e56\u4f1a\u65e0\u7aef\u8fc1\u6012\u4e8e\u81ea\u5df1\uff0c\u63a5\u5230\u5f20\u606a\u7684\u7535\u8bdd\uff0c\u8bf4\u662f\u4ed6\u7238\u5f20\u77e5\u884c\u521a\u5de7\u4e5f\u5728\u5efa\u90ba\uff0c\u4fbf\u6b23\u7136\u6765\u4f1a\uff0c\u4e5f\u6ca1\u60f3\u5230\u4e8b\u60c5\u5df2\u7ecf\u6709\u4e86\u67f3\u6697\u82b1\u660e\u7684\u8f6c\u53d8\u3002</p><p>\u3000\u3000\u4e2d\u5348\u5206\u4e58\u4e09\u90e8\u8f66\u5230\u72ee\u5b50\u5c71\u534a\u5c71\u8170\u7684\u671b\u6c5f\u9601\u7528\u9910\uff0c\u5f53\u7740\u738b\u7ef4\u5747\u7684\u9762\u4e5f\u4e0d\u4fbf\u8c08\u79d1\u6280\u56ed\u9879\u76ee\u7684\u7ec6\u8282\uff0c\u5c31\u7b49\u7740\u738b\u7ef4\u5747\u63a8\u52a8\u5efa\u90ba\u5e02\u91cc\u5c31\u79d1\u6280\u56ed\u9879\u76ee\u5f62\u6210\u7edf\u4e00\u610f\u89c1\uff0c\u5f85\u5efa\u90ba\u5e02\u91cc\u5f62\u6210\u79ef\u7ea7\u7684\u7edf\u4e00\u610f\u89c1\u4e4b\u540e\uff0c\u9526\u6e56\u8fd9\u8fb9\u5c31\u4f1a\u8ba9\u9648\u4fe1\u751f\u6216\u8bb8\u82cf\u6d25\u4e1c\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u6b63\u5f0f\u4e0e\u5efa\u90ba\u5e02\u653f\u5e9c\u8c08\u8be6\u7ec6\u7684\u79d1\u6280\u56ed\u9879\u76ee\u5408\u4f5c\u7ec6\u8282\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u5c06\u6628\u665a\u9ec4\u514b\u7fa4\u4e0e\u67f3\u5fd7\u519b\u622a\u4f4f\u4ed6\u4e00\u8d77\u5403\u996d\u7684\u4e8b\u60c5\u8bf4\u7ed9\u738b\u7ef4\u5747\u3001\u59da\u6587\u76db\u542c\uff0c\u770b\u4ed6\u4eec\u8981\u4e0d\u8981\u5c06\u9ec4\u514b\u7fa4\u4e00\u8d77\u5e26\u4e0a\uff1f\u8fd9\u91cc\u9762\u6216\u8bb8\u4f1a\u6709\u9ec4\u514b\u7fa4\u8981\u4e0e\u738b\u7ef4\u5747\u5206\u529f\u7684\u73b0\u5b9e\u8003\u8651\uff0c\u4f46\u662f\u9ec4\u514b\u7fa4\u521a\u8c03\u5230\u5efa\u90ba\u5e02\u653f\u5e9c\u62c5\u4efb\u526f\u5e02\u957f\uff0c\u5206\u7ba1\u804c\u52a1\u8fd8\u6ca1\u6709\u786e\u8ba4\uff0c\u4f46\u662f\u6bd5\u7adf\u5934\u4e0a\u8fd8\u9876\u7740\u9ad8\u65b0\u6280\u672f\u56ed\u533a\u4e3b\u4efb\u7684\u5934\u8854\uff0c\u8981\u662f\u5c06\u4ed6\u4e00\u8d77\u62c9\u8fdb\u6765\uff0c\u65e5\u540e\u5f88\u591a\u4e8b\u60c5\u90fd\u4f1a\u76f8\u5bf9\u987a\u5229\u4e00\u4e9b\u3002</p></article>"}}}]);
