(window.webpackJsonp=window.webpackJsonp||[]).push([[558],{581:function(p){p.exports={chapterTitle:null,title:"\u7b2c413\u7ae0 \u5b8c\u6574\u8ba1\u5212\u7684\u4e00\u73af",content:"<article><h2>\u7b2c413\u7ae0 \u5b8c\u6574\u8ba1\u5212\u7684\u4e00\u73af</h2><p>\u3000\u3000\u201c\u6bd4\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\u66f4\u53d7\u4e9a\u6d32\u5404\u56fd\u6b22\u8fce\u7684\u7ecf\u6d4e\u5b66\u5bb6\u597d\u50cf\u662f\u7ea6\u7ff0\xb7\u5948\u65af\u6bd4\u7279\uff1f\u201d\u53f6\u5efa\u658c\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u662f\u8bf4\u5948\u65af\u6bd4\u7279\u6240\u8457\u7684\u300a\u4e9a\u6d32\u5927\u8d8b\u52bf\u300b\uff1f\u201d\u5f20\u606a\u95ee\u53f6\u5efa\u658c\uff0c\u201c\u56fd\u5185\u6709\u51fa\u7248\u793e\u7ffb\u8bd1\u4ed6\u7684\u8fd9\u672c\u4e13\u8457\uff1f\u6211\u624b\u91cc\u53ea\u6709\u4e00\u672c\u82f1\u6587\u7248\u3002\u4ed6\u7684\u89c2\u70b9\u7684\u786e\u5728\u4e9a\u6d32\u5f88\u53d7\u6b22\u8fce\uff0c\u56fd\u5185\u5e94\u8be5\u6709\u7ffb\u8bd1\u6587\u672c\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u5916\u6587\u51fa\u7248\u793e\u4e03\u6708\u4efd\u5c31\u51fa\u7248\u4e86\u4e2d\u6587\u7248\uff0c\u8fd8\u662f\u6211\u4e09\u53d4\u63a8\u8350\u6211\u8bfb\u7684\uff0c\u201d\u53f6\u5efa\u658c\u7b11\u7740\u8bf4\uff0c\u201c\u597d\u8bdd\u6bd5\u7adf\u662f\u8c01\u90fd\u613f\u610f\u542c\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u539f\u6765\u662f\u4e09\u53d4\u8001\u4eba\u5bb6\u63a8\u8350\u7684\uff0c\u770b\u6765\u7ea6\u7ff0\xb7\u5948\u65af\u6bd4\u7279\u7684\u89c2\u70b9\u8fd8\u771f\u662f\u5927\u884c\u5176\u9053\u554a\u3002\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u5948\u65af\u6bd4\u7279\u4e50\u89c2\u7684\u4f30\u8ba1\u5d1b\u8d77\u7684\u4e9a\u6d32\u5c06\u6210\u4e3a\u672a\u6765\u4e16\u754c\u7684\u4e2d\u5fc3\uff0c\u4e9a\u6d32\u5c06\u6301\u7eed\u7e41\u8363\u4e0b\u53bb\uff0c\u8fd9\u8bdd\u6211\u4e5f\u7231\u542c\uff0c\u4e0d\u8fc7\u5948\u65af\u6bd4\u7279\u5ffd\u89c6\u4e9a\u6d32\u7279\u522b\u662f\u4e1c\u4e9a\u5404\u56fd\u7ecf\u6d4e\u53d1\u5c55\u6a21\u5f0f\u4e2d\u5b58\u5728\u7684\u4e00\u4e9b\u5f0a\u7aef\uff0c\u8fd9\u4e9b\u5f0a\u7aef\u8fd8\u5341\u5206\u7684\u4e25\u91cd\uff0c\u5374\u662f\u6211\u4eec\u8981\u6ce8\u610f\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u4e09\u53d4\u3001\u53f6\u7956\u8303\u7b2c\u4e09\u4e2a\u513f\u5b50\u53f6\u81fb\u6c11\u662f\u5916\u7ecf\u8d38\u90e8\u515a\u7ec4\u6210\u5458\uff0c\u5206\u7ba1\u4e9a\u6d32\u53f8\uff0c\u5728\u5916\u7ecf\u8d38\u90e8\u8fd1\u4e8c\u5341\u4e2a\u526f\u90e8\u7ea7\u4e2d\u6392\u540d\u867d\u7136\u4e0d\u7b97\u9760\u524d\uff0c\u4f46\u4e9a\u6d32\u53f8\u5728\u5916\u7ecf\u8d38\u90e8\u662f\u4ec5\u6b21\u4e8e\u7f8e\u5927\u53f8\u7684\u5b9e\u6743\u90e8\u95e8\uff0c\u7531\u4e8e\u8fd1\u5e74\u6765\u4e9a\u6d32\u5404\u56fd\u7ecf\u6d4e\u7684\u6d3b\u8dc3\u3001\u4e1c\u4e9a\u5404\u56fd\u7684\u6d77\u5916\u534e\u4eba\u5728\u56fd\u5185\u5f15\u8fdb\u5916\u8d44\u4efd\u989d\u4e0a\u5360\u4e86\u6700\u5927\u7684\u4e00\u5757\u6bd4\u4f8b\uff0c\u518d\u52a0\u4e0a\u7f8e\u56fd\u5bf9\u56fd\u5185\u7684\u7ecf\u6d4e\u6709\u610f\u65e0\u610f\u7684\u538b\u5236\uff0c\u4f7f\u5f97\u4e9a\u6d32\u53f8\u7684\u5730\u4f4d\u5c24\u5176\u7684\u91cd\u8981\u3002</p><p>\u3000\u3000\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u53f6\u5efa\u658c\u5c31\u6765\u52b2\u4e86\uff0c\u4e3e\u7740\u9152\u676f\uff0c\u8eab\u5b50\u503e\u4fa7\u8fc7\u6765\uff1a\u201c\u4f60\u4e3b\u5f20\u5609\u4fe1\u5b9e\u4e1a\u6682\u65f6\u653e\u7f13\u62d3\u5c55\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u4e1a\u52a1\uff0c\u662f\u4e0d\u662f\u65e9\u5c31\u6709\u8fd9\u6837\u7684\u5224\u65ad\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5609\u4fe1\u5b9e\u4e1a\u6700\u65e9\u7684\u5408\u4f5c\u5c31\u662f\u5c06\u7231\u8fbe\u5f71\u789f\u673a\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u54c1\u724c\u4ee3\u7406\u6743\u4e0e\u4e00\u90e8\u5206\u73b0\u91d1\u6089\u6570\u7f6e\u5165\u5609\u4fe1\u5b9e\u4e1a\uff0c\u4ee5\u6362\u53d6\u5609\u4fe1\u5b9e\u4e1a\u7b2c\u4e8c\u80a1\u4e1c\u7684\u5730\u4f4d\uff0c\u867d\u7136\u5c06\u7231\u8fbe\u5f71\u789f\u673a\u5728\u4e1c\u5357\u4e9a\u7684\u4ee3\u7406\u6743\u7f6e\u5165\u5609\u4fe1\u5b9e\u4e1a\uff0c\u5f20\u606a\u5374\u8bf4\u670d\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u7b49\u4eba\u6682\u65f6\u653e\u7f13\u5728\u4e1c\u5357\u5730\u533a\u5927\u4e3e\u62d3\u5c55\u7684\u5f71\u789f\u673a\u4e1a\u52a1\uff0c\u53ea\u662f\u5229\u7528\u73b0\u5728\u7684\u8d44\u6e90\u7ec4\u7ec7\u751f\u4ea7\u3001\u9500\u552e\uff0c\u6700\u91cd\u8981\u7684\u4e00\u6b65\u68cb\u5374\u662f\u8054\u5408\u53f6\u5bb6\u5165\u80a1\u60e0\u5c71\u9999\u96ea\u6d77\uff0c\u5168\u529b\u53d1\u5c55\u9999\u96ea\u6d77\u5728\u56fd\u5185\u7684\u7535\u51b0\u7bb1\u4e1a\u52a1\uff0c\u53f6\u5c0f\u6850\u4e5f\u8ba9\u5f20\u606a\u5506\u4f7f\u7740\u8fdb\u5165\u9999\u96ea\u6d77\u8d1f\u8d23\u5e02\u573a\u8fd0\u8425\u90a3\u4e00\u5757\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u7b97\u662f\u5427\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u53ef\u80fd\u4f1a\u9519\u8fc7\u4e00\u4e9b\u65f6\u673a\uff0c\u4f46\u662f\u7ecf\u8425\u6700\u91cd\u8981\u7684\u6cd5\u5219\u662f\u751f\u5b58\u4e0b\u6765\uff0c\u5609\u4fe1\u5b9e\u4e1a\u90a3\u65f6\u7684\u60c5\u51b5\u4e0d\u7b97\u5f88\u597d\uff0c\u9700\u8981\u66f4\u7a33\u59a5\u7684\u7ecf\u8425\u7b56\u7565\uff0c\u56fd\u5185\u8fd9\u4e48\u5927\u7684\u5e02\u573a\u5f85\u5f00\u53d1\uff0c\u5f53\u7136\u662f\u9996\u5148\u53d1\u5c55\u56fd\u5185\u7684\u4e1a\u52a1\u3002\u201d</p><p>\u3000\u3000\u5982\u4eca\u5609\u4fe1\u5b9e\u4e1a\u56db\u5927\u80a1\u4e1c\u4e2d\uff0c\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u5c45\u9996\u4f4d\uff0c\u76db\u946b\u96c6\u56e2\u5c45\u7b2c\u56db\uff0c\u5c45\u7b2c\u4e8c\u3001\u7b2c\u4e09\u5927\u80a1\u4e1c\u7684\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u6240\u6301\u7684\u80a1\u4efd\u540d\u4e49\u90fd\u5f52\u845b\u5bb6\u6240\u6709\uff0c\u845b\u5bb6\u662f\u5609\u4fe1\u5b9e\u4e1a\u540d\u4e49\u4e0a\u7684\u7b2c\u4e00\u5927\u80a1\u4e1c\u3002</p><p>\u3000\u3000\u4ece\u516d\u6708\u521d\u6b63\u5f0f\u63a7\u80a1\u9999\u96ea\u6d77\u6210\u7acb\u9999\u96ea\u6d77\u7535\u5668\u96c6\u56e2\u6709\u9650\u516c\u53f8\u4ee5\u6765\uff0c\u9999\u96ea\u6d77\u7684\u7535\u51b0\u7bb1\u4e1a\u52a1\u589e\u957f\u901f\u5ea6\u8fc5\u901f\u56de\u5347\u5230\u4e5d\u56db\u3001\u4e5d\u4e94\u5e74\u7684\u6700\u9ad8\u6c34\u5e73\uff0c\u7531\u4e8e\u9999\u96ea\u6d77\u4e0e\u97e9\u56fd\u4e09\u661f\u96c6\u56e2\u8c08\u5224\u7684\u4e00\u5e74\u591a\u65f6\u95f4\u6709\u8352\u5e9f\u4e1a\u52a1\u4e4b\u5acc\uff0c\u8981\u593a\u56de\u201c\u5357\u9999\u96ea\u6d77\u3001\u5317\u6d77\u5c14\u201d\u7684\u767d\u7535\u5e02\u573a\u5730\u4f4d\uff0c\u8fd8\u8981\u7b49\u9999\u96ea\u6d77\u7535\u5668\u96c6\u56e2\u65b0\u7684\u751f\u4ea7\u7ebf\u6295\u4ea7\u4e4b\u540e\u624d\u6709\u53ef\u80fd\u3002\u9999\u96ea\u6d77\u7684\u7535\u51b0\u7bb1\u4e1a\u52a1\u5728\u7b2c\u4e09\u5b63\u5ea6\u5f00\u59cb\u6062\u590d\u6027\u7684\u589e\u6da8\uff0c\u7b2c\u4e09\u5b63\u5ea6\u5f52\u5165\u63a7\u80a1\u516c\u53f8\u5609\u4fe1\u5b9e\u4e1a\u540d\u4e49\u7684\u51c0\u5229\u6da6\u5df2\u7ecf\u9ad8\u8fbe\u4e00\u5343\u4e07\uff0c\u5609\u4fe1\u5b9e\u4e1a\u5728\u6263\u9664\u8d22\u52a1\u6210\u672c\u4e4b\u540e\uff0c\u7b2c\u4e09\u5b63\u5ea6\u7ec8\u4e8e\u6709\u4e00\u4efd\u597d\u770b\u7684\u5b63\u62a5\uff0c\u652f\u6491\u524d\u671f\u6295\u8d44\u4eba\u5bf9\u5609\u4fe1\u5b9e\u4e1a\u7684\u4ef7\u503c\u5224\u65ad\uff0c\u5609\u4fe1\u5b9e\u4e1a\u7684\u80a1\u4ef7\u4e00\u76f4\u5904\u4e8e\u4e0a\u5347\u671f\uff1b\u8fd9\u4e9b\u65e0\u7591\u90fd\u8bc1\u660e\u5f20\u606a\u5224\u65ad\u7684\u6b63\u786e\u6027\uff0c\u5f53\u7136\uff0c\u4e5f\u662f\u4e8b\u5148\u53ef\u4ee5\u9884\u6599\u7684\uff0c\u4e0d\u7136\u5f20\u606a\u4e5f\u65e0\u6cd5\u8bf4\u670d\u5b59\u5c1a\u8863\u3001\u845b\u660e\u5fb7\u4e24\u4eba\u91c7\u7eb3\u4ed6\u7684\u5efa\u8bae\u3002</p><p>\u3000\u3000\u201c\u4e1c\u4e9a\u5730\u533a\u7684\u7ecf\u6d4e\u73af\u5883\u5c31\u8981\u590d\u6742\u592a\u591a\u2026\u2026\u201d\u5f20\u606a\u7ee7\u7eed\u8bf4\u9053\uff0c\u201c\u6700\u8fd1\u56fd\u9645\u70ed\u94b1\u52a0\u901f\u5411\u8fd9\u4e9b\u5730\u533a\u6d8c\u5165\uff0c\u4e4b\u524d\u5b58\u5728\u7684\u5f0a\u7aef\u4f1a\u53d7\u5230\u50ac\u5316\u800c\u8fdb\u4e00\u6b65\u7684\u52a0\u5267\uff1b\u6b64\u5916\uff0c\u8fd9\u51e0\u5e74\u56fd\u9645\u4e0a\u6295\u673a\u8d44\u672c\u7684\u6d41\u52a8\u6027\u65e5\u76ca\u653e\u5bbd\uff0c\u7ecf\u7814\u6240\u521a\u51fa\u7089\u4e00\u4e2a\u6570\u636e\uff0c\u6709\u7740\u5145\u5206\u6d41\u52a8\u6027\u7684\u70ed\u94b1\u89c4\u6a21\u5df2\u7ecf\u8fbe\u5230\u4e00\u4e07\u4ebf\u7f8e\u91d1\uff0c\u4e14\u4e0d\u7ba1\u7ecf\u7814\u6240\u7684\u6570\u636e\u6709\u591a\u5927\u7684\u53ef\u4fe1\u5ea6\uff0c\u4f46\u662f\u56fd\u9645\u70ed\u94b1\u7684\u89c4\u6a21\u5c0f\u4e0d\u4e86\u3002\u6709\u8fd9\u6837\u7684\u5229\u5668\uff0c\u4f60\u6562\u8bf4\u80cc\u540e\u5c31\u6ca1\u6709\u51f6\u731b\u7684\u730e\u624b\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u4eca\u5e74\u4ee5\u6765\u624d\u6709\u65f6\u95f4\u7814\u7a76\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\u7684\u7ecf\u6d4e\u7406\u8bba\u4e0e\u4e1c\u4e9a\u5730\u533a\u7684\u5b8f\u89c2\u7ecf\u6d4e\u5f62\u52bf\uff0c\u4e4b\u524d\u7684\u5224\u65ad\uff0c\u81ea\u7136\u662f\u5360\u4e86\u91cd\u751f\u7684\u4fbf\u5b9c\uff0c\u62ff\u53d1\u751f\u8fc7\u7684\u5386\u53f2\u4e8b\u4ef6\u4e0e\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\u7684\u7ecf\u6d4e\u7406\u8bba\u76f8\u4e92\u5370\u8bc1\uff0c\u518d\u5145\u5206\u6536\u96c6\u4e9a\u6d32\u5404\u56fd\u7684\u7ecf\u6d4e\u6570\u636e\u8fdb\u884c\u5206\u6790\uff0c\u5f20\u606a\u81ea\u7136\u6709\u66f4\u5145\u5206\u7684\u81ea\u4fe1\u6307\u51fa\u4e9a\u6d32\u5404\u56fd\u7ecf\u6d4e\u4e2d\u5b58\u5728\u7684\u4e25\u91cd\u5f0a\u7aef\uff0c\u4f46\u662f\u4ed6\u7684\u5f71\u54cd\u529b\u5b9e\u5728\u6709\u9650\uff0c\u9664\u4e86\u53f6\u5efa\u658c\uff0c\u8fd8\u6709\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u7b49\u4eba\u4e4b\u5916\uff0c\u8fd8\u6709\u591a\u5c11\u4eba\u4f1a\u8ba4\u540c\u4ed6\u7684\u89c2\u70b9\u5b9e\u5728\u503c\u5f97\u6000\u7591\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u6709\u4e9b\u95ee\u9898\u53ef\u4ee5\u8ddf\u5f90\u5b66\u5e73\u4ee5\u53ca\u53f6\u81fb\u6c11\u7b49\u4eba\u4ea4\u6d41\uff0c\u4f46\u662f\u5f20\u606a\u4e5f\u662f\u6700\u8fd1\u624d\u5c06\u4e00\u4e9b\u95ee\u9898\u7814\u7a76\u900f\u5f7b\uff0c\u4e4b\u524d\u603b\u4e0d\u80fd\u7a7a\u53e3\u65e0\u51ed\u7684\u76f4\u63a5\u6307\u51fa\u660e\u5e74\u4e9a\u6d32\u5730\u533a\u4f1a\u7206\u53d1\u6781\u5176\u4e25\u91cd\u7684\u7ecf\u6d4e\u5371\u673a\u3002</p><p>\u3000\u3000\u201c\u5509\uff0c\u4f60\u4eec\u4fe9\u4e2a\u5927\u7537\u4eba\u5934\u51d1\u5230\u4e00\u8d77\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u5b59\u9759\u6aac\u624b\u53c9\u7740\u8170\u7ad9\u5728\u4ed6\u7684\u8eab\u540e\uff0c\u6328\u7740\u8fd8\u8fd1\uff0c\u9f3b\u5c16\u5dee\u70b9\u64e6\u4e0a\u5979\u9ad8\u633a\u7684\u80f8\u90e8\u3002\u5f20\u606a\u4e5f\u4e0d\u662f\u90a3\u79cd\u6709\u4fbf\u5b9c\u4e0d\u5360\u7684\u4eba\uff0c\u53ef\u662f\u5b59\u9759\u6aac\u7684\u4fbf\u5b9c\uff0c\u4ed6\u53ef\u4e0d\u6562\u968f\u4fbf\u5360\uff0c\u8eab\u5b50\u5f80\u5427\u53f0\u90a3\u8fb9\u9760\u4e86\u9760\uff0c\u55c5\u7740\u4ece\u5b59\u9759\u6aac\u8eab\u4e0a\u4f20\u6765\u6de1\u6de1\u7684\u9999\u6c14\uff0c\u626d\u5934\u53bb\u770b\u5510\u5a67\u5979\u4eec\u3002\u9152\u5427\u7684\u5176\u4ed6\u5de5\u4f5c\u4eba\u5458\u90fd\u5df2\u7ecf\u9646\u7eed\u6765\u4e86\uff0c\u5510\u5a67\u3001\u9648\u5983\u84c9\u3001\u82cf\u4e00\u5a77\u5979\u4eec\u4e09\u4e2a\u5973\u5b69\u5b50\u5c31\u63d2\u4e0d\u4e0a\u624b\uff0c\u6b63\u5750\u5728\u91cc\u9762\u559d\u7740\u996e\u6599\u804a\u5929\u5462\u3002</p><p>\u3000\u3000\u5f20\u606a\u3001\u53f6\u5efa\u658c\u8ba8\u8bba\u4e9a\u6d32\u7ecf\u6d4e\u8c08\u5f97\u6295\u673a\uff0c\u5934\u5dee\u4e0d\u591a\u6328\u5230\u4e00\u5757\uff0c\u5f20\u606a\u8fd8\u638f\u51fa\u6c34\u7b14\u3001\u62ff\u6765\u9910\u5dfe\u7eb8\u5c06\u4e00\u4e9b\u5173\u952e\u7684\u7ecf\u6d4e\u6570\u636e\u5199\u51fa\u6765\u3001\u5e76\u753b\u51fa\u56fe\u6765\u7ed9\u53f6\u5efa\u658c\u770b\u3002</p><p>\u3000\u3000\u201c\u591a\u5927\u4eba\u4e86\uff0c\u8fd8\u559c\u6b22\u5728\u9910\u5dfe\u7eb8\u4e0a\u4e71\u6d82\u4e71\u753b\uff1f\u201d\u5b59\u9759\u6aac\u4f38\u624b\u8fc7\u6765\uff0c\u5c06\u5bc6\u5bc6\u9ebb\u9ebb\u5199\u6ee1\u6570\u636e\u8fd8\u6709\u62ff\u7ea2\u8272\u7b14\u753b\u4e0a\u7ebf\u6027\u56fe\u7684\u4e00\u53e0\u9910\u5dfe\u7eb8\u62ff\u8fc7\u6765\uff0c\u77aa\u773c\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u6ca1\u770b\u660e\u767d\uff0c\u201c\u90fd\u662f\u4e9b\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u770b\u4e0d\u61c2\u7684\uff0c\u201d\u53f6\u5efa\u658c\u5c06\u9910\u5dfe\u7eb8\u4ece\u5b59\u9759\u6aac\u624b\u91cc\u62ff\u8fc7\u53bb\uff0c\u201c\u4f60\u4e5f\u4e0d\u4f1a\u5bf9\u8fd9\u4e9b\u4e1c\u897f\u611f\u5174\u8da3\u7684\u3002\u201d\u5c06\u9910\u5dfe\u7eb8\u94fa\u5c55\u5728\u5427\u53f0\u4e0a\uff0c\u624b\u6307\u6572\u7740\u5427\u53f0\uff0c\u4e00\u624b\u8fd8\u6258\u7740\u4e0b\u5df4\uff0c\u5f20\u606a\u4e5f\u8f6c\u8fc7\u8eab\u6765\u5750\u6b63\u4e86\uff0c\u6ca1\u6709\u53bb\u7406\u4f1a\u5b59\u9759\u6aac\u3002</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u5bf9\u8fd9\u4e9b\u4e1c\u897f\u5c31\u4e0d\u611f\u5174\u8da3\u4e86\uff1f\u201d\u5b59\u9759\u6aac\u9f13\u7740\u816e\u7ed1\u5b50\uff0c\u8fd9\u6bb5\u65f6\u95f4\u5979\u4e5f\u662f\u5f88\u8ba4\u771f\u7684\u5728\u6253\u7406\u9152\u5427\uff0c\u53f6\u5efa\u658c\u4e0e\u5f20\u606a\u7684\u8f7b\u89c6\u8ba9\u5979\u5fc3\u91cc\u8fd8\u4e0d\u597d\u53d7\u3002</p><p>\u3000\u3000\u201c\u90a3\u4f60\u6765\u770b\uff0c\u201d\u53f6\u5efa\u658c\u5f80\u65c1\u8fb9\u632a\u4e86\u4e2a\u4f4d\u7f6e\uff0c\u8ba9\u5b59\u9759\u6aac\u5750\u4ed6\u4e0e\u5f20\u606a\u4e2d\u95f4\uff0c\u201c\u770b\u8fd9\u4e1c\u897f\u770b\u5f97\u5934\u660f\u8111\u80c0\u5f88\u5f71\u54cd\u98df\u6b32\u7684\u2026\u2026\u554a\uff0c\u6211\u4eec\u53eb\u7684\u5916\u5356\u8be5\u9001\u8fc7\u6765\u4e86\u5427\uff1f\u201d\u62ac\u624b\u770b\u4e86\u770b\u8155\u8868\uff0c\u53c8\u56de\u5934\u770b\u4e86\u770b\u9152\u5427\u7684\u7384\u5173\u3002</p><p>\u3000\u3000\u5f20\u606a\u77a5\u773c\u770b\u7740\u5b59\u9759\u6aac\u7a7f\u7740\u70df\u7070\u8272\u7684\u77ed\u8896\u7f8a\u7ed2\u886b\uff0c\u9664\u4e86\u767d\u5ae9\u7684\u80f3\u818a\u9732\u5728\u5916\u9762\uff0c\u8fd8\u9732\u51fa\u597d\u5927\u4e00\u5757\u7ec6\u6ed1\u7684\u80cc\u808c\uff0c\u5979\u4eba\u8db4\u5728\u5427\u53f0\u4e0a\u770b\u9910\u5dfe\u7eb8\u4e0a\u5199\u7740\u7684\u6570\u636e\u4e0e\u4e00\u4e9b\u7ecf\u6d4e\u5b66\u672f\u8bed\uff0c\u5305\u88f9\u7740\u5c0f\u4e30\u81c0\u7684\u76ae\u88d9\u538b\u4f4e\u4e86\u4e00\u4e9b\uff0c\u9732\u51fa\u91cc\u9762\u7ea2\u8272\u5185\u88e4\u7684\u857e\u4e1d\u8fb9\uff0c\u9732\u51fa\u7684\u8170\u808c\u7ec6\u817b\u96ea\u767d\uff0c\u5f20\u606a\u4e0d\u6562\u591a\u770b\uff0c\u8db4\u5230\u5427\u53f0\u4e0a\uff0c\u5374\u53c8\u4e0d\u53ef\u907f\u514d\u7684\u770b\u89c1\u5b59\u9759\u6aac\u80f8\u524d\u9732\u51fa\u4e00\u89d2\u767d\u5ae9\u7684\u4e73\u808c\uff0c\u53ea\u5f97\u5750\u76f4\u8eab\u4f53\u89c6\u91ce\u8d8a\u8fc7\u5b59\u9759\u6aac\u7684\u5934\u9876\u4e0e\u53f6\u5efa\u658c\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u201c\u8981\u770b\u5c31\u770b\u5427\uff0c\u6211\u53c8\u4e0d\u4ecb\u610f\uff0c\u4e5f\u4e0d\u4f1a\u8ddf\u4f60\u5c0f\u5973\u670b\u53cb\u53bb\u8bf4\u4ec0\u4e48\u3002\u201d\u5b59\u9759\u6aac\u64a9\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5634\u91cc\u8fd9\u4e48\u8bf4\u7740\uff0c\u8fd8\u662f\u62c9\u4e86\u62c9\u8863\u886b\uff0c\u5c06\u80cc\u81c0\u95f4\u9732\u51fa\u6765\u7684\u90a3\u622a\u767d\u8089\u906e\u4f4f\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8fd9\u4e00\u773c\u8fd8\u662f\u76f8\u5f53\u7684\u64a9\u4eba\uff0c\u5f20\u606a\u6320\u6320\u9f3b\u5934\uff0c\u4e0d\u6562\u60f9\u8fd9\u5996\u5973\uff0c\u8d70\u5230\u53f6\u5efa\u658c\u53e6\u4e00\u4fa7\u5750\u4e0b\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u7b11\u4e86\u7b11\uff0c\u53bb\u5e74\u5f20\u606a\u4e0e\u8bb8\u601d\u4e00\u8d77\u5728\u5b59\u5bb6\u505a\u5ba2\uff0c\u4e0e\u5b59\u9759\u6aac\u63a5\u89e6\u7684\u65f6\u95f4\u4e5f\u591a\uff0c\u5b59\u9759\u6aac\u591a\u5c11\u4f1a\u6709\u4e9b\u7591\u5fc3\uff0c\u6709\u4e9b\u4e8b\u60c5\uff0c\u5f53\u4e8b\u4eba\u5fc3\u77e5\u809a\u660e\u4e5f\u5c31\u7f62\u4e86\uff0c\u8981\u662f\u7ed9\u522b\u4eba\u65e0\u610f\u95f4\u6345\u7a7f\uff0c\u90a3\u624d\u53eb\u8981\u8001\u547d\u5462\uff0c\u96be\u602a\u5f20\u606a\u8fd9\u65f6\u5019\u8fde\u4e00\u53e5\u53cd\u5507\u76f8\u8ba5\u7684\u8bdd\u90fd\u6ca1\u6709\uff0c\u5927\u6982\u6068\u4e0d\u5f97\u5b59\u9759\u6aac\u5ffd\u89c6\u4ed6\u7684\u5b58\u5728\u3002\u53f6\u5efa\u658c\u4e5f\u4fa7\u8fc7\u8eab\u4e0d\u7406\u5b59\u9759\u6aac\uff0c\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u4f1a\u53bb\u5317\u4eac\uff0c\u8fd8\u662f\u8bf4\u6211\u8bf7\u4e09\u53d4\u5230\u8fd9\u91cc\u6765\u8ddf\u4f60\u804a\u4e00\u804a\uff1f\u201d</p><p>\u3000\u3000\u804a\uff0c\u81ea\u7136\u662f\u804a\u4e9a\u6d32\u5730\u533a\u7684\u5b8f\u89c2\u7ecf\u6d4e\u5f62\u52bf\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u4e9a\u6d32\u7279\u522b\u662f\u4e1c\u4e9a\u5730\u533a\u7684\u7ecf\u6d4e\u5f62\u52bf\uff0c\u4e3b\u6d41\u89c2\u70b9\u4e00\u5411\u90fd\u76f8\u5f53\u4e50\u89c2\uff0c\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\u7684\u89c2\u70b9\u662f\u975e\u4e3b\u6d41\u7684\uff0c\u5e76\u4e0d\u53d7\u4e3b\u6d41\u7fa4\u4f53\u7684\u91cd\u89c6\uff0c\u800c\u5f20\u606a\u4e00\u756a\u8a00\u8bba\uff0c\u5219\u662f\u5728\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\u7684\u7ecf\u6d4e\u7406\u8bba\u57fa\u7840\u4e0a\u63d0\u4f9b\u66f4\u8be6\u7ec6\u7684\u8bba\u636e\uff0c\u81ea\u7136\u66f4\u6709\u8bf4\u670d\u529b\u3002</p><p>\u3000\u3000\u62ff\u8001\u7237\u5b50\u8bdd\u6765\u8bf4\uff0c\u201c\u6539\u9769\u5f00\u653e\u8fd1\u4e8c\u5341\u5e74\uff0c\u672a\u66fe\u9047\u6709\u4e00\u4eba\u6bd4\u6b64\u5c11\u5e74\u66f4\u80fd\u609f\u900f\u4e00\u4e2a\u884c\u4e1a\u201d\uff0c\u5f20\u606a\u7684\u610f\u89c1\uff0c\u4e0d\u8981\u8bf4\u53f6\u5efa\u658c\u4e86\uff0c\u53f6\u5bb6\u5176\u4ed6\u4eba\u90fd\u751a\u4e3a\u91cd\u89c6\u3002\u76db\u946b\u8f6c\u578b\u4e3b\u653b\u5bb6\u7535\u8fde\u9501\u4e5f\u662f\u5728\u542c\u53d6\u5f20\u606a\u610f\u89c1\u4e4b\u540e\u614e\u91cd\u8003\u5bdf\u5e02\u573a\u4e4b\u540e\u7684\u51b3\u5b9a\uff0c\u624d\u4e00\u5e74\u591a\u4e9b\u7684\u65f6\u95f4\u8fc7\u53bb\uff0c\u770b\u6765\u8fd9\u6b65\u68cb\u662f\u53f6\u5bb6\u8fd1\u5e74\u6765\u5728\u5546\u4e1a\u4e0a\u8d70\u5f97\u6700\u6210\u529f\u7684\u4e00\u6b65\u68cb\u3002\u5229\u7528\u53f6\u81fb\u6c11\u7b49\u53f6\u5bb6\u4eba\u8109\u5728\u6caa\u6d59\u4e00\u5e26\u7684\u5f71\u54cd\u529b\uff0c\u76db\u946b\u73af\u7403\u7535\u5668\u5df2\u7ecf\u5728\u6caa\u6d59\u4e24\u5730\u5b8c\u6210\u5bb6\u7535\u8fde\u9501\u4e1a\u7684\u5e03\u5c40\uff0c\u4ec5\u8fd9\u4e00\u5757\u7684\u8d44\u4ea7\u5c31\u8fc5\u901f\u6269\u5145\u5230\u4e94\u4e2a\u4ebf\u4e4b\u591a\uff1b\u7231\u7acb\u4fe1\u5728\u534e\u4e1c\u5730\u533a\u7684\u624b\u673a\u4ee3\u7406\u4e1a\u52a1\u7ee7\u7eed\u7ed9\u76db\u946b\u7684\u53d1\u5c55\u63d0\u4f9b\u53ef\u89c2\u7684\u8d44\u91d1\uff0c\u4f46\u5df2\u7ecf\u4e0d\u662f\u53f6\u5bb6\u6700\u91cd\u8981\u7684\u4e1a\u52a1\u4e86\u3002\u4e5f\u6b63\u56e0\u4e3a\u76db\u946b\u73af\u7403\u7535\u5668\u96c6\u56e2\u6781\u6709\u5206\u5bf8\u628a\u63e1\u65f6\u673a\u7684\u98de\u901f\u53d1\u5c55\uff0c\u4f7f\u5f97\u53f6\u5efa\u658c\u5728\u53f6\u5bb6\u7684\u5730\u4f4d\u8fc5\u901f\u7a9c\u9ad8\uff0c\u5df2\u7ecf\u6210\u4e3a\u53f6\u5bb6\u5728\u5546\u754c\u7684\u4ee3\u8868\uff0c\u4ee5\u5f80\u53f6\u5bb6\u5206\u6563\u7684\u4ea7\u4e1a\u90fd\u9646\u7eed\u6574\u5408\u5e76\u5165\u76db\u946b\u540d\u4e0b\uff0c\u76db\u946b\u5df2\u7ecf\u4ece\u4e24\u5e74\u524d\u7684\u8d38\u6613\u516c\u53f8\u521d\u5177\u5927\u578b\u96c6\u56e2\u7684\u89c4\u6a21\u3002</p><p>\u3000\u3000\u867d\u7136\u5f20\u606a\u660e\u786e\u6307\u51fa\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5bf9\u56fd\u5185\u7684\u5f71\u54cd\u662f\u5229\u5927\u4e8e\u5f0a\uff0c\u4e3b\u8981\u662f\u524d\u51e0\u5e74\u56fd\u52a1\u9662\u63a8\u884c\u7684\u91d1\u878d\u4ea7\u4e1a\u653f\u7b56\uff0c\u5df2\u7ecf\u5f88\u5927\u7a0b\u5ea6\u4e0a\u5316\u89e3\u4e86\u56fd\u5185\u7ecf\u6d4e\u5b58\u5728\u7684\u4e00\u4e9b\u5f0a\u7aef\uff0c\u4e5f\u6b63\u662f\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u4e4b\u540e\uff0c\u56fd\u5185\u8fce\u6765\u7ecf\u6d4e\u53d1\u5c55\u7684\u9ec4\u91d1\u5341\u5e74\uff0c\u4f46\u662f\u5bf9\u4e9a\u6d32\u7ecf\u6d4e\u5f62\u52bf\u6709\u66f4\u51c6\u786e\u7684\u5224\u65ad\uff0c\u65e0\u7591\u80fd\u8ba9\u56fd\u5185\u7ecf\u6d4e\u53d7\u76ca\u66f4\u591a\u2026\u2026</p><p>\u3000\u3000\u53f6\u81fb\u6c11\u5728\u5916\u7ecf\u8d38\u90e8\u5206\u7ba1\u4e9a\u6d32\u53f8\uff0c\u76f4\u63a5\u5904\u7406\u4e0e\u4e9a\u6d32\u5404\u56fd\u4e4b\u95f4\u7684\u7ecf\u8d38\u4e8b\u52a1\uff0c\u662f\u5236\u5b9a\u5bf9\u4e9a\u6d32\u5404\u56fd\u7ecf\u8d38\u653f\u7b56\u7684\u91cd\u8981\u4eba\u7269\u4e4b\u4e00\u3002\u867d\u7136\u968f\u5927\u6d41\u9519\u8bef\u4f30\u8ba1\u4e9a\u6d32\u5730\u533a\u7684\u7ecf\u6d4e\u5f62\u52bf\u4e0d\u662f\u4ec0\u4e48\u5927\u4e0d\u4e86\u7684\u4e8b\u60c5\uff0c\u8981\u662f\u80fd\u51c6\u786e\u65e0\u8bef\u7684\u9884\u6d4b\u51fa\u4e9a\u6d32\u7ecf\u6d4e\u5f62\u52bf\uff0c\u5e76\u4f7f\u56fd\u5185\u5728\u4e0e\u4e1c\u4e9a\u5404\u56fd\u7ecf\u8d38\u6d3b\u52a8\u4e2d\u53d7\u76ca\uff0c\u90a3\u7edd\u5bf9\u662f\u653f\u6cbb\u751f\u6daf\u4e2d\u5149\u5f69\u593a\u76ee\u7684\u4e00\u7b14\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u6240\u4ee5\u5e0c\u671b\u5f20\u606a\u4e0e\u4ed6\u4e09\u53d4\u53f6\u81fb\u6c11\u804a\u4e00\u804a\u3002</p><p>\u3000\u3000\u201c\u4f60\u8f6c\u544a\u4e0d\u5c31\u884c\u4e86\uff1f\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u8fd9\u4e24\u5929\u6211\u4e5f\u6ca1\u4e8b\u53ef\u505a\uff0c\u4f60\u8981\u6709\u7a7a\uff0c\u6211\u5c31\u8ddf\u4f60\u591a\u5439\u5439\u725b\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u6765\u8f6c\u8ff0\uff0c\u53ef\u80fd\u65e0\u6cd5\u5c06\u95ee\u9898\u8bf4\u900f\uff0c\u8fd8\u662f\u8ba9\u4e09\u53d4\u56de\u6765\u4e00\u8d9f\uff0c\u201d\u53f6\u5efa\u658c\u6bd4\u8f83\u80fd\u63a5\u53d7\u5f20\u606a\u7684\u5224\u65ad\uff0c\u81f3\u4e8e\u4ed6\u4e09\u53d4\u53f6\u81fb\u6c11\u80fd\u4e0d\u80fd\u63a5\u53d7\uff0c\u5c31\u4e0d\u5f97\u800c\u77e5\uff0c\u8fd8\u8ba9\u4ed6\u4eec\u5f53\u9762\u804a\u4e00\u804a\u624d\u80fd\u5c06\u95ee\u9898\u8bf4\u900f\uff0c\u53c8\u60f3\u8d77\u4e00\u4e8b\uff0c\u201c\u67f3\u5fd7\u519b\u7ea6\u8bf7\u4f60\u89c1\u9762\uff0c\u4f60\u4f1a\u8bf4\u8d77\u8fd9\u4e2a\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u662f\u4ed6\u4eec\u613f\u610f\u542c\uff0c\u6211\u5f53\u7136\u4f1a\u8bf4\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6bd5\u7adf\u7701\u91cc\u51e0\u4e2a\u5927\u9879\u76ee\uff0c\u8fd8\u8981\u9676\u664b\u4eb2\u81ea\u70b9\u5934\u624d\u884c\u3002\u201d</p><p>\u3000\u3000\u201c\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u4e0e\u4e1c\u65e5\u94c1\u7684\u90a3\u4e2a\u9879\u76ee\uff1f\u201d\u53f6\u5efa\u658c\u95ee\u9053\u3002</p><p>\u3000\u3000\u7701\u91cc\u4e3a\u4f7f\u5efa\u90ba\u5e02\u4e1c\u5357\u7684\u5c0f\u5c97\u53bf\u6210\u4e3a\u4e1c\u90e8\u91cd\u8981\u7684\u94a2\u94c1\u751f\u4ea7\u57fa\u5730\uff0c\u53bb\u5e74\u542f\u52a8\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u9879\u76ee\uff0c\u9879\u76ee\u6240\u9700\u8bbe\u5907\u4e0e\u6280\u672f\uff0c\u90fd\u8ba1\u5212\u4ece\u65e5\u672c\u5f15\u8fdb\uff0c\u6700\u7ec8\u9009\u62e9\u4e09\u4e95\u8d22\u56e2\u65d7\u4e0b\u7684\u4e1c\u65e5\u94c1\u96c6\u56e2\u8fdb\u884c\u5408\u4f5c\u8c08\u5224\u3002</p><p>\u3000\u3000\u56db\u6708\u521d\uff0c\u6c14\u6124\u4e8e\u5c0f\u65e5\u672c\u7684\u50b2\u6162\u3001\u65e0\u793c\uff0c\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u3001\u674e\u4e49\u6c5f\u7b49\u4eba\u4e00\u624b\u6ce1\u5236\u5bcc\u6625\u9601\u4e70\u6625\u4e8b\u4ef6\uff0c\u4f7f\u5f97\u65e5\u65b9\u8c08\u5224\u4eba\u5458\u5728\u5bcc\u6625\u9601\u9152\u5e97\u4e70\u6625\u3001\u8fb1\u534e\u7684\u884c\u6b62\u66b4\u9732\u4e8e\u516c\u4f17\u4e4b\u524d\u3002</p><p>\u3000\u3000\u8be5\u4e8b\u4ef6\uff0c\u5728\u7701\u5185\u6380\u8d77\u7684\u4e00\u80a1\u53cd\u65e5\u3001\u53cd\u97e9\u7684\u601d\u6f6e\uff0c\u4e5f\u4f7f\u5f97\u9879\u76ee\u8c08\u5224\u4e00\u5ea6\u4e2d\u6b62\uff0c\u7701\u91cc\u4e5f\u8868\u8fbe\u4e2d\u6b62\u8c08\u5224\u4e5f\u65e0\u6240\u8c13\u7684\u5f3a\u786c\u6001\u5ea6\uff0c\u4e1c\u65e5\u94c1\u81ea\u77e5\u7406\u4e8f\uff0c\u5c06\u6d89\u53ca\u5bcc\u6625\u9601\u4e70\u6625\u4e8b\u4ef6\u7684\u8c08\u5224\u7ec4\u4eba\u5458\u6089\u6570\u53ec\u56de\u5904\u7f6e\uff0c\u53e6\u6d3e\u4eba\u5458\u91cd\u542f\u8c08\u5224\u3002</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u7684\uff0c\u4f46\u4e5f\u4e0d\u6b62\u8fd9\u9879\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u672c\u4eba\u5341\u5206\u8ba4\u540c\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\u5bf9\u4e9a\u6d32\u7ecf\u6d4e\u5f62\u52bf\u7684\u8ba4\u8bc6\uff0c\u201d\u5f20\u606a\u6de1\u6de1\u7684\u8bf4\u9053\uff0c\u201c\u867d\u7136\u6b64\u65f6\u5948\u65af\u6bd4\u7279\u5173\u4e8e\u4e9a\u6d32\u5c06\u6301\u7eed\u7e41\u8363\u7684\u89c2\u70b9\u66f4\u53d7\u6b22\u8fce\u3002\u5948\u65af\u6bd4\u7279\u89c2\u70b9\u7684\u76db\u884c\uff0c\u53ef\u4ee5\u8bf4\u662f\u8fd1\u51e0\u5e74\u6765\u5927\u91cf\u70ed\u94b1\u6d8c\u5165\u4e1c\u4e9a\u5404\u56fd\u7684\u50ac\u5316\u5242\u4e4b\u4e00\uff0c\u5927\u91cf\u6295\u673a\u6027\u70ed\u94b1\u4e0e\u4e1c\u4e9a\u5404\u56fd\u672c\u8eab\u5b58\u5728\u7684\u7ecf\u6d4e\u7f3a\u9677\uff0c\u6709\u5f88\u5927\u7684\u53ef\u80fd\u6027\u8bf1\u4f7f\u8fd9\u4e9b\u5730\u533a\u7206\u53d1\u7ecf\u6d4e\u5371\u673a\uff0c\u4f60\u8bf4\u90a3\u4e9b\u56fd\u9645\u91d1\u878d\u6295\u8d44\u5de8\u9cc4\u6709\u6ca1\u6709\u4ece\u4e2d\u55c5\u5230\u9c9c\u8840\u7684\u6c14\u5473\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff01\u201d\u4ece\u5f20\u606a\u6536\u96c6\u6765\u7684\u90a3\u4e9b\u8be6\u7ec6\u7684\u6570\u636e\u91cc\uff0c\u53f6\u5efa\u658c\u4e5f\u503e\u5411\u4e8e\u4ee5\u4e3a\u4e1c\u4e9a\u5730\u533a\u6709\u5f88\u5927\u7684\u7206\u53d1\u5c40\u90e8\u7ecf\u6d4e\u5371\u673a\u7684\u53ef\u80fd\u6027\u3002</p><p>\u3000\u3000\u201c\u4e9a\u6d32\u7ecf\u6d4e\u8981\u53d1\u751f\u52a8\u8361\uff0c\u6700\u5148\u5c06\u4ece\u4e1c\u5357\u4e9a\u5730\u533a\u5f00\u59cb\uff0c\u7279\u522b\u662f\u6cf0\u56fd\u3001\u5370\u5c3c\u7b49\u56fd\u7684\u7ecf\u6d4e\u5f0a\u7aef\u7279\u522b\u7684\u4e25\u91cd\uff0c\u9632\u8303\u5371\u673a\u7684\u80fd\u529b\u53c8\u5dee\uff1b\u8fc7\u53bb\u4e8c\u5341\u5e74\uff0c\u4e1c\u5357\u4e9a\u5730\u533a\u662f\u65e5\u672c\u6700\u5927\u7684\u8d44\u672c\u8f93\u51fa\u5730\uff0c\u4e1c\u5357\u4e9a\u7684\u7ecf\u6d4e\u4e0d\u7a33\uff0c\u65e5\u672c\u5fc5\u53d7\u6ce2\u53ca\uff0c\u9996\u5148\u4f1a\u8868\u73b0\u5728\u6c47\u7387\u4e0a\u3002\u65e5\u5143\u5bf9\u7f8e\u5143\u6c47\u7387\u4e0b\u8dcc\uff0c\u4eba\u6c11\u5e01\u76f8\u5bf9\u575a\u633a\uff0c\u94a2\u94c1\u9879\u76ee\u5f15\u8fdb\u8bbe\u5907\u4e0e\u6280\u672f\u6240\u9700\u7684\u8d44\u91d1\u82e5\u80fd\u5728\u65e5\u672c\u4e89\u53d6\u65e5\u5143\u8d37\u6b3e\uff0c\u81f3\u5c11\u53ef\u4ee5\u5728\u6c47\u7387\u4e0a\u83b7\u5229\u3002\u201d</p><p>\u3000\u3000\u8fd9\u4f1a\u513f\uff0c\u5916\u5356\u7ed9\u9001\u5230\u5e97\u91cc\u6765\uff0c\u8981\u8d76\u7d27\u5403\u5b8c\u665a\u996d\u597d\u5f00\u59cb\u591c\u95f4\u7684\u8425\u4e1a\uff0c\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u6682\u65f6\u505c\u6b62\u8ba8\u8bba\u8fd9\u4e9b\u95ee\u9898\uff0c\u62db\u547c\u5510\u5a67\u5979\u4eec\u4e00\u8d77\u8fc7\u6765\u51c6\u5907\u3002\u5b59\u9759\u6aac\u62ff\u7740\u5f20\u606a\u521a\u624d\u5199\u4e0b\u4e00\u4e32\u7ecf\u6d4e\u6570\u636e\u7684\u9910\u5dfe\u7eb8\uff0c\u770b\u4e86\u534a\u5929\u6123\u662f\u770b\u4e0d\u51fa\u4e2a\u6240\u4ee5\u7136\u6765\uff0c\u6c14\u607c\u7684\u5c06\u9910\u5dfe\u7eb8\u4e22\u7ed9\u53f6\u5efa\u658c\uff0c\u770b\u89c1\u53f6\u5efa\u658c\u5f53\u5b9d\u4f3c\u7684\u5c06\u8fd9\u4e9b\u9910\u5dfe\u7eb8\u6536\u4e86\u8d77\u6765\uff0c\u9ed1\u767d\u5206\u660e\u7684\u773c\u73e0\u5b50\u7ffb\u8f6c\u4e86\u51e0\u4e0b\uff0c\u6c14\u52bf\u88ab\u593a\uff0c\u5012\u4e5f\u8bf4\u4e0d\u51fa\u4ec0\u4e48\u7259\u5c16\u5634\u5229\u7684\u8bdd\u3002\u5979\u4e0d\u660e\u767d\u5f20\u606a\u8fd8\u5728\u8bfb\u9ad8\u4e2d\uff0c\u4e3a\u4ec0\u4e48\u80fd\u77e5\u9053\u8fd9\u4e9b\uff1f</p><p>\u3000\u3000\u5403\u8fc7\u665a\u996d\uff0c\u6536\u62fe\u4e86\u4e00\u4e0b\uff0c\u9152\u5427\u5219\u6b63\u5f0f\u5f00\u59cb\u4e00\u5929\u7684\u8425\u4e1a\uff0c\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u3001\u82cf\u4e00\u5a77\u5979\u4eec\u7b2c\u4e00\u6b21\u51fa\u5165\u9152\u5427\u8fd9\u6837\u7684\u573a\u6240\uff0c\u770b\u7740\u4eba\u6d41\u6d8c\u8fdb\u6765\uff0c\u5341\u5206\u7684\u5174\u594b\uff1b\u5f20\u606a\u5219\u4e0e\u53f6\u5efa\u658c\u5bfb\u4e86\u4e00\u5904\u89d2\u843d\u7ee7\u7eed\u4ed6\u4eec\u665a\u996d\u524d\u672a\u5b8c\u7684\u8bdd\u9898\u3002</p><p>\u3000\u3000\u8bf4\u53ca\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u9879\u76ee\uff0c\u8fd9\u662f\u4e1c\u6d77\u7701\u6709\u53f2\u4ee5\u6765\u6700\u5927\u7684\u4e00\u9879\u6295\u8d44\u9879\u76ee\uff0c\u6d89\u53ca\u91d1\u989d\u9ad8\u8fbe\u767e\u4ebf\uff0c\u5176\u4e2d\u8fd1\u767e\u5206\u4e4b\u4e03\u516b\u5341\u7684\u8d44\u91d1\u90fd\u8981\u7528\u5728\u4ece\u4e1c\u65e5\u94c1\u5f15\u8d77\u8bbe\u5907\u4e0e\u6280\u672f\u4e0a\uff0c\u8d44\u91d1\u7684\u6765\u6e90\u662f\u5f88\u91cd\u8981\u7684\u8c08\u5224\u9879\u76ee\u3002</p><p>\u3000\u3000\u82e5\u80fd\u5168\u90e8\u4ece\u65e5\u672c\u56fd\u5185\u83b7\u5f97\u65e5\u5143\u8d37\u6b3e\u5f15\u8fdb\u8bbe\u5907\u4e0e\u6280\u672f\uff0c\u5e76\u4e14\u5c06\u8d37\u6b3e\u5f52\u8fd8\u671f\u63a8\u5ef6\u5230\u4e5d\u516b\u5e74\u4e4b\u540e\uff0c\u4e5d\u516b\u5e74\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u8fc7\u540e\u65e5\u5143\u76f8\u5bf9\u7f8e\u5143\u6c47\u7387\u5927\u8dcc\uff0c\u800c\u4eba\u6c11\u5e01\u6c47\u7387\u575a\u633a\uff0c\u4ec5\u8fd9\u4e00\u90e8\u5206\u5c31\u53ef\u4ee5\u5b9e\u9645\u5c11\u5f52\u8fd8\u8d85\u8fc730\uff05\u7684\u8d37\u6b3e\u3002</p><p>\u3000\u3000\u65e5\u672c\u662f\u6700\u65e9\u5bf9\u534e\u63d0\u4f9b\u653f\u5e9c\u8d37\u6b3e\u7684\u56fd\u5bb6\u4e4b\u4e00\uff0c\u4ece79\u5e74\u5f00\u59cb\u52302008\u5e74\u7684\u4e09\u5341\u5e74\u95f4\uff0c\u65e5\u672c\u653f\u5e9c\u5148\u540e\u5411\u534e\u63d0\u4f9b\u603b\u989d\u7ea62.9\u4e07\u4ebf\u65e5\u5143\u7684\u63f4\u52a9\u6027\u8d37\u6b3e\uff0c\u5360\u56fd\u5185\u63a5\u53d7\u5916\u56fd\u5b98\u65b9\u63f4\u52a9\u6027\u8d37\u6b3e\u768460\uff05\u3002\u65e5\u672c\u653f\u5e9c\u5411\u534e\u63d0\u4f9b\u63f4\u52a9\u6027\u8d37\u6b3e\u6709\u7740\u6df1\u523b\u7684\u5386\u53f2\u4e0e\u653f\u6cbb\u56e0\u7d20\uff08\u4e2d\u56fd\u653e\u5f03\u5bf9\u65e5\u7684\u6218\u4e89\u8d54\u6b3e\u8981\u6c42\uff0c\u65e5\u672c\u9700\u8981\u6539\u5584\u4e2d\u65e5\u5173\u7cfb\uff09\uff0c\u4f46\u662f\u6700\u4e3b\u8981\u7684\u7ecf\u6d4e\u539f\u56e0\u4fbf\u662f\u4f7f\u5f97\u65e5\u672c\u4f01\u4e1a\u5728\u5bf9\u534e\u8d37\u6b3e\u4e2d\u83b7\u5f97\u5927\u91cf\u7684\u5408\u540c\uff0c\u7c4d\u6b64\u5927\u4e3e\u5f00\u8fdb\u4e2d\u56fd\u5e02\u573a\uff0c\u5e26\u52a8\u65e5\u672c\u7ecf\u6d4e\u7684\u53d1\u5c55\u3002</p><p>\u3000\u3000\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u4e0e\u4e1c\u65e5\u94c1\u7684\u5408\u4f5c\uff0c\u5b8c\u5168\u53ef\u4ee5\u901a\u8fc7\u5b98\u65b9\u8c08\u5224\u4ece\u65e5\u672c\u83b7\u53d6\u4f4e\u606f\u8d37\u6b3e\u3002</p><p>\u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u56fd\u5185\u867d\u7136\u62b5\u5fa1\u4f4f\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u4fb5\u88ad\uff0c\u4f46\u662f\u4e5d\u516b\u3001\u4e5d\u4e5d\u5e74\u7684\u7ecf\u6d4e\u8fd8\u662f\u53d7\u5230\u4e00\u5b9a\u7684\u5f71\u54cd\uff0c\u56fd\u52a1\u9662\u6839\u636e\u5f53\u65f6\u7684\u5f62\u52bf\u63d0\u51fa\u6070\u5f53\u7684\u63aa\u65bd\uff0c\u5316\u89e3\u4e86\u7ecf\u6d4e\u653e\u7f13\u7684\u538b\u529b\uff0c\u4f46\u662f\u5927\u529b\u53d1\u5c55\u623f\u5730\u4ea7\u4e0e\u6559\u80b2\u7b49\u4ea7\u4e1a\u6765\u62c9\u52a8\u5185\u9700\uff0c\u4e5f\u7ed9\u65e5\u540e\u6c11\u751f\u9057\u7559\u4e0b\u5f88\u591a\u95ee\u9898\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e4b\u524d\u5c31\u8fd9\u4e9b\u95ee\u9898\u4e0e\u5f90\u5b66\u5e73\u5c31\u9646\u7eed\u6709\u4e9b\u4ea4\u6d41\uff0c\u8981\u662f\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u4e5f\u80fd\u63a5\u53d7\u4fdd\u7f57\xb7\u514b\u9c81\u683c\u66fc\u5bf9\u4e9a\u6d32\u7ecf\u6d4e\u5f62\u52bf\u7684\u5224\u65ad\uff0c\u4e1c\u6d77\u7701\u5b8c\u5168\u53ef\u4ee5\u5728\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u7206\u53d1\u4e4b\u524d\u505a\u4e9b\u51c6\u5907\uff0c\u52a0\u5927\u5bf9\u57fa\u7840\u8bbe\u65bd\u7684\u5efa\u8bbe\u6295\u8d44\u2014\u2014\u65e5\u672c\u653f\u5e9c\u5bf9\u534e\u7684\u63f4\u52a9\u6027\u8d37\u6b3e\uff0c\u5e94\u670940\uff05\u7684\u6bd4\u4f8b\u7528\u4e8e\u57fa\u7840\u8bbe\u65bd\u5efa\u8bbe\u4e0a\u2014\u2014\u52a0\u5f3a\u4e0e\u65e5\u672c\u653f\u5e9c\u7684\u8c08\u5224\uff0c\u5c3d\u53ef\u80fd\u589e\u52a0\u4eca\u660e\u4e24\u5e74\u65e5\u672c\u5bf9\u534e\u63f4\u52a9\u6027\u8d37\u6b3e\u7684\u989d\u5ea6\uff0c\u7528\u4e8e\u7701\u9ad8\u901f\u516c\u8def\u7f51\u3001\u94c1\u8def\u7f51\u3001\u5c0f\u6c5f\u5824\u9632\u7b49\u57fa\u7840\u8bbe\u65bd\u5efa\u8bbe\u3002</p><p>\u3000\u3000\u8fd9\u5176\u4e2d\u7684\u79cd\u79cd\u597d\u5904\uff0c\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u4e4b\u540e\u81ea\u7136\u4e00\u4e00\u5448\u73b0\u3002</p><p>\u3000\u3000\u4f46\u662f\u65e5\u672c\u653f\u5e9c\u5bf9\u534e\u63f4\u52a9\u6027\u8d37\u6b3e\u6d89\u53ca\u5230\u7684\u653f\u6cbb\u610f\u4e49\u6bd4\u8f83\u91cd\u5927\uff0c\u516b\u5341\u5e74\u4ee3\u901a\u5e38\u662f\u65e5\u672c\u9996\u76f8\u8bbf\u534e\u65f6\u7528\u6765\u6539\u5584\u4e2d\u65e5\u5173\u7cfb\u7684\u91cd\u8981\u7b79\u7801\u3002\u4e5d\u5341\u5e74\u4ee3\u4ee5\u6765\uff0c\u4e3a\u4e86\u914d\u5408\u65e5\u672c\u4f01\u4e1a\u5927\u4e3e\u8fdb\u5165\u4e2d\u56fd\u5e02\u573a\u7684\u9700\u8981\uff0c\u76f8\u5173\u8d37\u6b3e\u7684\u6761\u4ef6\u624d\u9010\u6e10\u653e\u677e\uff0c\u4e0d\u8fc7\u8981\u6ca1\u6709\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u751a\u81f3\u4e2d\u592e\u7684\u652f\u6301\uff0c\u4e00\u7cfb\u5217\u7684\u8ba1\u5212\uff0c\u7279\u522b\u662f\u4e89\u53d6\u65e5\u5143\u8d37\u6b3e\u589e\u52a0\u5bf9\u7701\u5185\u57fa\u7840\u8bbe\u65bd\u7684\u6295\u8d44\uff0c\u5f90\u5b66\u5e73\u4e5f\u5f88\u96be\u53bb\u63a8\u52a8\u83b7\u5f97\u6210\u529f\u3002</p><p>\u3000\u3000\u5373\u4f7f\u4e0d\u80fd\u83b7\u5f97\u4f4e\u606f\u7684\u63f4\u52a9\u6027\u8d37\u6b3e\uff0c\u4ece\u65e5\u672c\u83b7\u53d6\u5546\u4e1a\u8d37\u6b3e\uff0c\u53ea\u8981\u5728\u4e5d\u516b\u5e74\u65e5\u672c\u7ecf\u6d4e\u53d7\u6ce2\u53ca\u4e4b\u524d\u8c08\u6210\u9879\u76ee\uff0c\u4e5f\u80fd\u76f4\u63a5\u4ece\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u4e2d\u76f4\u63a5\u83b7\u76ca\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5f20\u606a\u4e5f\u4e0d\u662f\u6ca1\u6709\u81ea\u5df1\u7684\u5229\u76ca\u6743\u8861\u3002</p><p>\u3000\u3000\u9999\u96ea\u6d77\u4eca\u660e\u4e24\u5e74\u8981\u5f15\u8fdb\u65b0\u7684\u751f\u4ea7\u7ebf\u4e0e\u6280\u672f\uff0c\u65b0\u5149\u7eb8\u4e1a\u4eca\u660e\u4e24\u5e74\u4e5f\u8981\u5927\u4e3e\u6269\u5f20\u9020\u7eb8\u4ea7\u80fd\uff0c\u4e24\u8fb9\u9879\u76ee\u91d1\u878d\u52a0\u8d77\u6765\u5c06\u8fd1\u5341\u4e2a\u4ebf\u3002\u5f20\u606a\u503e\u5411\u4e0e\u65e5\u97e9\u4f01\u4e1a\u5408\u4f5c\uff0c\u65e5\u97e9\u4f01\u4e1a\u5728\u8fd9\u4e24\u65b9\u9762\u7684\u6280\u672f\u4e0e\u673a\u68b0\u5236\u9020\u6c34\u6765\u8fd8\u7b97\u503c\u5f97\u4fe1\u4efb\uff0c\u4f46\u662f\u4ec5\u51ed\u9999\u96ea\u6d77\u4e0e\u65b0\u5149\u7eb8\u4e1a\u7684\u6c11\u95f4\u5f71\u54cd\u529b\uff0c\u60f3\u4ece\u65e5\u672c\u83b7\u5f97\u5546\u4e1a\u8d37\u6b3e\u7684\u53ef\u80fd\u6027\u6781\u5fae\uff08\u83b7\u5f97\u63f4\u52a9\u6027\u65e5\u5143\u8d37\u6b3e\u66f4\u4e0d\u53ef\u80fd\uff09\uff0c\u5f20\u606a\u662f\u60f3\u9999\u96ea\u6d77\u3001\u65b0\u5149\u7eb8\u4e1a\u7684\u9879\u76ee\u80fd\u642d\u4e0a\u7701\u91cc\u7684\u4fbf\u8f66\uff0c\u6210\u4e3a\u7701\u91cc\u4e00\u63fd\u5b50\u9879\u76ee\u4e2d\u7684\u5b50\u9879\u76ee\u3002\u9999\u96ea\u6d77\u672c\u8eab\u5c31\u6709\u8fd1\u534a\u6570\u7684\u80a1\u4efd\u662f\u56fd\u6709\u6027\u8d28\u7684\uff0c\u65b0\u5149\u7eb8\u4e1a\u5728\u5c0f\u6c5f\u4e2d\u4e0a\u6e38\u5927\u89c4\u6a21\u7684\u690d\u6797\u3001\u5e76\u8ba1\u5212\u7b79\u5efa\u5927\u578b\u7684\u5236\u6d46\u751f\u4ea7\u57fa\u5730\uff0c\u5f53\u7136\u6709\u7406\u7531\u4eab\u53d7\u4e00\u4e9b\u4fbf\u6377\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u53ea\u662f\u5f20\u606a\u6574\u4e2a\u5b8c\u6574\u8ba1\u5212\u4e2d\u7684\u4e00\u73af\u3002</p></article>"}}}]);
