(window.webpackJsonp=window.webpackJsonp||[]).push([[558],{581:function(p){p.exports={chapterTitle:null,title:"\u7b2c558\u7ae0 \u964d\u9636\u76f8\u8fce",content:"<article><h2>\u7b2c558\u7ae0 \u964d\u9636\u76f8\u8fce</h2><p>\u3000\u3000\u53ea\u5269\u4e0b\u8584\u8584\u4e00\u5c42\u7684\u575a\u51b0\u4e00\u6345\u5373\u7834\uff0c\u4e4b\u524d\u53ea\u662f\u6ca1\u6709\u6345\u7834\u8fd9\u5c42\u575a\u51b0\u7684\u4eba\uff1b\u59da\u6587\u76db\u80cc\u540e\u7684\u987e\u5bb6\u4ee5\u53ca\u53f6\u5bb6\u53ea\u662f\u6f5c\u85cf\u5728\u80cc\u540e\u7684\u529b\u91cf\uff0c\u771f\u6b63\u7ad9\u51fa\u6765\u6345\u7834\u8fd9\u5c42\u575a\u51b0\u7684\u4eba\u662f\u5efa\u90ba\u5e02\u59d4\u5e38\u59d4\u3001\u526f\u5e02\u957f\u738b\u7ef4\u5747\u4e0e\u526f\u5e02\u957f\u3001\u65b0\u6d66\u9ad8\u65b0\u4ea7\u4e1a\u56ed\u533a\u4e3b\u4efb\u9ec4\u514b\u7fa4\u3002</p><p>\u3000\u3000\u738b\u7ef4\u57474\u67084\u65e5\u4e0e\u9646\u6587\u592b\u5728\u5efa\u90ba\u996d\u5e97\u89c1\u9762\u4e4b\u540e\uff0c\u6b21\u65e5\u5206\u522b\u5411\u4e1c\u6d77\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u7701\u957f\u674e\u8fdc\u6e56\u3001\u5efa\u90ba\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u3001\u5e02\u957f\u8096\u660e\u5efa\u6c47\u62a5\u4ed6\u6240\u4e86\u89e3\u5230\u7684\u9526\u6e56\u79d1\u6280\u56ed\u9879\u76ee\u7684\u8fd1\u51b5\uff0c\u5e0c\u671b\u5efa\u90ba\u5e02\u59d4\u5c31\u6b64\u5c3d\u5feb\u5f62\u6210\u7edf\u4e00\u7684\u610f\u89c1\u3002</p><p>\u3000\u30004\u67085\u65e5\uff0c\u9648\u4fe1\u751f\u5728\u79bb\u5f00\u9999\u6e2f\u7684\u524d\u5915\uff0c\u5728\u7231\u8fbe\u96c6\u56e2\u9999\u6e2f\u4ea7\u54c1\u7814\u53d1\u4e2d\u5fc3\u7684\u5960\u57fa\u5178\u793c\u4e0a\u5411\u5916\u754c\u8fdb\u4e00\u6b65\u516c\u5e03\u6b63\u5f0f\u5b9a\u540d\u4e3a\u6a61\u6811\u56ed\u8ba1\u5212\u7684\u79d1\u6280\u56ed\u9879\u76ee\u7ec6\u8282\uff0c\u5305\u62ec\u6295\u8d44\u4e09\u4ebf\u5143\u5efa\u8bbe\u4e00\u4e2a\u56fd\u5bb6\u7ea7\u7684\u5fae\u7535\u5b50\u57fa\u7840\u6280\u672f\u5b9e\u9a8c\u5ba4\u3001\u6295\u8d44\u4e24\u4ebf\u5143\u5efa\u8bbe\u4e00\u4e2a\u5305\u62ec\u6750\u6599\u5206\u6790\u5b9e\u9a8c\u5ba4\u3001\u96c6\u6210\u7535\u8def\u8bbe\u8ba1\u4e0e\u5f00\u53d1\u652f\u63f4\u4e2d\u5fc3\u3001\u77e5\u8bc6\u4ea7\u6743\u670d\u52a1\u4e2d\u5fc3\u5728\u5185\u7684\u7efc\u5408\u6027\u8d28\u7684\u5fae\u7535\u5b50\u6280\u672f\u4e0e\u8f6f\u4ef6\u5f00\u53d1\u670d\u52a1\u4e2d\u5fc3\uff0c\u6295\u8d44\u4e00\u4ebf\u5143\u5efa\u8bbe\u4e00\u4e2a\u4e3b\u8981\u7535\u5b50\u901a\u4fe1\u9886\u57df\u7684\u4e13\u4e1a\u521b\u4e1a\u56ed\u533a\uff0c\u4e00\u4e2a\u603b\u989d\u4e09\u4ebf\u5143\u7684\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u3002</p><p>\u3000\u3000\u9274\u4e8e\u56fd\u5185\u5404\u5927\u57ce\u5e02\u90fd\u5df2\u8bbe\u7acb\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u56ed\u533a\uff0c\u521b\u4e1a\u56ed\u4e0d\u4f1a\u63a5\u53d7\u5236\u9020\u6027\u7684\u4f01\u4e1a\uff0c\u5c06\u4ee5\u6280\u672f\u5f00\u53d1\u7814\u7a76\u7684\u4f01\u4e1a\u4e3a\u4e3b\uff0c\u5165\u56ed\u4f01\u4e1a\u4e0d\u4ec5\u53ef\u4ee5\u83b7\u5f97\u79d1\u6280\u56ed\u63d0\u4f9b\u7684\u878d\u8d44\uff0c\u8fd8\u53ef\u4ee5\u5ec9\u4ef7\u4eab\u53d7\u79d1\u6280\u56ed\u63d0\u4f9b\u7684\u5148\u8fdb\u786c\u4ef6\u8bbe\u5907\u4e0e\u8f6f\u4ef6\u7cfb\u7edf\uff1b\u9664\u4e86\u8bbe\u8ba1\u4e0e\u6d4b\u8bd5\u5e73\u53f0\u4e4b\u5916\uff0c\u79d1\u6280\u56ed\u4e5f\u53ef\u4ee5\u6ee1\u8db3\u5165\u56ed\u4f01\u4e1a\u7684\u6837\u54c1\u751f\u4ea7\u9700\u6c42\u3002</p><p>\u3000\u3000\u8fd9\u4e0e\u5e38\u59d4\u526f\u5e02\u957f\u738b\u7ef4\u5747\u5411\u5efa\u90ba\u5e02\u59d4\u6c47\u62a5\u7684\u60c5\u51b5\u76f8\u5dee\u65e0\u51e0\uff0c\u7f57\u541b\u8c03\u5230\u5efa\u90ba\u62c5\u4efb\u5e02\u59d4\u4e66\u8bb0\u517c\u4efb\u4e1c\u6d77\u7701\u59d4\u5e38\u59d4\u7684\u65f6\u95f4\u4e0d\u957f\uff0c\u5bf9\u5efa\u90ba\u5728\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u4e0a\u7684\u6ede\u540e\u53d1\u5c55\u975e\u5e38\u4e0d\u6ee1\u2014\u2014\u4e5f\u4e0d\u4ec5\u4ec5\u662f\u5efa\u90ba\u5982\u6b64\uff0c\u56fd\u5185\u5404\u5927\u57ce\u5e02\u5728\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u7684\u6295\u5165\u90fd\u975e\u5e38\u7684\u6ede\u540e\uff0c\u4e0d\u4ec5\u9526\u6e56\u624d\u5341\u4e2a\u4ebf\u7684\u6a61\u6811\u56ed\u8ba1\u5212\u4e5f\u4e0d\u53ef\u80fd\u5f15\u8d77\u8fd9\u4e48\u5927\u7684\u8f70\u52a8\u2014\u2014\u5728\u8fde\u591c\u53ec\u5f00\u7684\u5e02\u59d4\u5e38\u59d4\u7d27\u6025\u8054\u7edc\u4f1a\u8bae\u4e0a\uff0c\u7f57\u541b\u8bc4\u4ef7\u6a61\u6811\u56ed\u8ba1\u5212\u8bf4\u9053\uff1a\u201c\u6a61\u6811\u56ed\u8ba1\u5212\u5728\u6280\u672f\u7814\u53d1\u4e0a\u7684\u5de8\u989d\u6295\u8d44\u5c06\u76f4\u63a5\u4fc3\u8fdb\u5730\u65b9\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u7684\u6280\u672f\u5347\u7ea7\uff0c\u4e09\u4e2a\u4ebf\u7684\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\uff0c\u5c06\u5438\u5f15\u4f17\u591a\u7535\u5b50\u4fe1\u606f\u9886\u57df\u7684\u521b\u4e1a\u8005\u8702\u62e5\u800c\u6765\uff0c\u5728\u4e0d\u4e45\u7684\u5c06\u6765\uff0c\u5c06\u4fc3\u8fdb\u5730\u65b9\u5f62\u6210\u4e00\u4e2a\u7535\u5b50\u4fe1\u606f\u6280\u672f\u9886\u57df\u7684\u4ea7\u4e1a\u7fa4\uff0c\u8fd9\u5e94\u8be5\u662f\u5730\u65b9\u653f\u5e9c\u52aa\u529b\u8981\u53bb\u5b9e\u65bd\u7684\u4e00\u9879\u4f1f\u7ee9\uff0c\u201d\u7f57\u541b\u8fd9\u4e48\u8bf4\u65f6\uff0c\u770b\u4e86\u5e02\u59d4\u526f\u4e66\u8bb0\u3001\u5e02\u957f\u8096\u660e\u5efa\u4e00\u773c\uff0c\u65b0\u6d66\u9ad8\u65b0\u4ea7\u4e1a\u56ed\u533a\u5efa\u56ed\u516b\u5e74\uff0c\u5e02\u91cc\u524d\u540e\u4e09\u6b21\u5212\u62e8\u51fa\u6765\u9ad8\u65b0\u6280\u672f\u521b\u4e1a\u7684\u6295\u8d44\u57fa\u91d1\u603b\u989d\u624d\u516d\u767e\u4e07\uff0c\u53ea\u53ca\u9526\u6e56\u4e00\u6b21\u51fa\u624b\u7684\u4e94\u5341\u5206\u4e4b\u4e00\uff0c\u8096\u660e\u5efa\u4e00\u76f4\u662f\u5efa\u90ba\u5e02\u59d4\u7684\u4e3b\u8981\u9886\u5bfc\uff0c\u81ea\u7136\u8981\u5403\u4e9b\u836f\uff1b\u8096\u660e\u5efa\u770b\u7740\u624b\u6307\u538b\u7740\u7684\u6587\u4ef6\u4e0d\u8a00\u8bed\uff0c\u53ea\u6028\u81ea\u5df1\u53d1\u5e94\u6162\u4e86\u534a\u62cd\uff0c\u8010\u7740\u6027\u5b50\u542c\u7f57\u541b\u7ee7\u7eed\u5c06\u8bdd\u8bf4\u5b8c\uff0c\u201c\u8fd9\u9879\u4e8b\u4e1a\u73b0\u5728\u7531\u4e00\u5bb6\u6c11\u8425\u4f01\u4e1a\u6765\u5b8c\u6210\uff0c\u6211\u4eec\u6ca1\u6709\u7406\u7531\u4e0d\u611f\u5230\u60ed\u6127\uff0c\u6211\u770b\u5e02\u91cc\u8981\u521b\u9020\u4e00\u5207\u6709\u5229\u7684\u6761\u4ef6\u5c06\u8fd9\u4e2a\u9879\u76ee\u7559\u5728\u5efa\u90ba\u2026\u2026\u201d</p><p>\u3000\u3000\u7f57\u541b\u5b9a\u4e0b\u8fd9\u6837\u7684\u57fa\u8c03\uff0c\u63a5\u4e0b\u6765\u7684\u8ba8\u8bba\u5c31\u987a\u7545\u591a\u4e86\uff0c\u5373\u4f7f\u9526\u6e56\u4e0d\u613f\u610f\u5c06\u79d1\u6280\u56ed\u653e\u5728\u6c5f\u5317\u7684\u65b0\u6d66\u533a\uff0c\u90a3\u5c31\u5728\u4e1c\u90ca\u71d5\u5f52\u7554\u4e13\u95e8\u5f00\u8f9f\u65b0\u7684\u56ed\u533a\uff0c\u884c\u653f\u4e0a\u5f52\u5c5e\u4e8e\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u56ed\u533a\u7edf\u4e00\u7ba1\u8f96\uff0c\u5c06\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u56ed\u533a\u7684\u4e00\u5207\u4f18\u60e0\u653f\u7b56\u90fd\u590d\u5236\u7ed9\u6a61\u56ed\u6811\u8ba1\u5212\uff0c\u571f\u5730\u5212\u62e8\u4e5f\u5c3d\u53ef\u80fd\u6bd4\u7167\u6700\u4f18\u60e0\u7684\u653f\u7b56\uff0c\u5e38\u59d4\u4f1a\u8bae\u4e0a\u8ba8\u8bba\u4e86\u59da\u6587\u76db\u5728\u5efa\u90ba\u7684\u804c\u52a1\u95ee\u9898\uff0c\u4efb\u547d\u59da\u6587\u76db\u4e3a\u65b0\u6d66\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u56ed\u533a\u4e3b\u4efb\u52a9\u7406\u3001\u56ed\u533a\u6295\u8d44\u4fc3\u8fdb\u5c40\u526f\u5c40\u957f\uff08\u6b63\u5904\u7ea7\uff09\u3002</p><p>\u3000\u3000\u6682\u65f6\u4ee5\u5e38\u59d4\u526f\u5e02\u957f\u738b\u7ef4\u5747\u3001\u526f\u5e02\u957f\u3001\u9ad8\u65b0\u4ea7\u4e1a\u56ed\u533a\u4e3b\u4efb\u9ec4\u514b\u7fa4\u3001\u79d1\u6280\u5c40\u5c40\u957f\u5218\u6167\u3001\u9ad8\u65b0\u4ea7\u4e1a\u56ed\u533a\u4e3b\u4efb\u52a9\u7406\u59da\u6587\u76db\u56db\u4eba\u4e3a\u4e3b\u6210\u7acb\u6a61\u6811\u56ed\u8ba1\u5212\u8054\u7edc\u5c0f\u7ec4\uff0c\u8d1f\u8d23\u5c06\u9526\u6e56\u7684\u79d1\u6280\u56ed\u9879\u76ee\u633d\u7559\u5728\u5efa\u90ba\u3002</p><p>\u3000\u3000\u7684\u786e\uff0c\u4e5f\u53ea\u6709\u9526\u6e56\u516c\u5e03\u6a61\u6811\u56ed\u8ba1\u5212\u7684\u7ec6\u8282\uff0c\u5730\u65b9\u653f\u5e9c\u624d\u80fd\u770b\u5230\u66f4\u5177\u4f53\u7684\u610f\u4e49\uff0c\u7f57\u541b\u751a\u81f3\u4e0d\u8bb3\u8a00\u7684\u8ddf\u738b\u7ef4\u5747\u8bf4\uff1a\u201c\u6a61\u6811\u56ed\u8ba1\u5212\u5b9e\u65bd\u540e\u7684\u610f\u4e49\u4e0e\u4ef7\u503c\uff0c\u6211\u770b\u6bd4\u9526\u6e56\u7684\u5236\u6d46\u9879\u76ee\u8fd8\u8981\u91cd\u8981\uff01\u201d</p><p>\u3000\u3000\u8fd9\u53e5\u8bdd\u91cc\uff0c\u7f57\u541b\u5bf9\u674e\u8fdc\u6e56\u4e5f\u662f\u9887\u6709\u5fae\u8f9e\uff0c\u4e0d\u8fc7\u4ed6\u8eab\u4e3a\u7701\u59d4\u5e38\u59d4\u3001\u5efa\u90ba\u5e02\u59d4\u4e66\u8bb0\uff0c\u653f\u6cbb\u5730\u4f4d\u5f88\u9ad8\uff0c\u5c31\u7b97\u79c1\u4e0b\u5e95\u5bf9\u674e\u8fdc\u6e56\u6709\u4e9b\u610f\u89c1\uff0c\u4f20\u5230\u674e\u8fdc\u6e56\u7684\u8033\u6735\u91cc\u4e5f\u53ea\u4f1a\u5f53\u542c\u4e0d\u89c1\u5904\u7406\uff1b\u738b\u7ef4\u5747\u5374\u53ea\u6709\u7b11\u4e00\u7b11\uff0c\u4e0d\u7f6e\u53ef\u5426\uff1a\u7f57\u541b\u80fd\u575a\u5b9a\u7684\u652f\u6301\u9526\u6e56\u7684\u6a61\u6811\u56ed\u8ba1\u5212\u5c31\u597d\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u30004\u67088\u65e5\uff0c\u7231\u8fbe\u96c6\u56e2\u9648\u4fe1\u751f\u3001\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u603b\u88c1\u4e01\u69d0\u3001\u7231\u8fbe\u7535\u5b50\u603b\u88c1\u82cf\u6d25\u4e1c\u3001\u5609\u4fe1\u5b9e\u4e1a\u8463\u4e8b\u957f\u5b59\u5c1a\u4e49\u3001\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u5f00\u53d1\u96c6\u56e2\u603b\u88c1\u90b5\u81f3\u521a\u7b49\u4eba\u5728\u5efa\u90ba\u5e02\u5e38\u59d4\u526f\u5e02\u957f\u738b\u7ef4\u5747\u3001\u526f\u5e02\u957f\u9ec4\u514b\u7fa4\u7b49\u5efa\u90ba\u5e02\u653f\u5e9c\u5b98\u5458\u53c2\u89c2\u4e1c\u6d77\u5927\u5b66\u6280\u672f\u5b66\u9662\u7684\u6559\u5b66\u7814\u7a76\u6d3b\u52a8\u3002</p><p>\u3000\u30004\u67088\u65e5\u4e0b\u5348\uff0c\u53ec\u5f00\u7684\u8bb0\u8005\u62db\u5f85\u4f1a\u73b0\u573a\uff0c\u9648\u4fe1\u751f\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u5c06\u4e24\u5343\u4e07\u5143\u7684\u73b0\u91d1\u652f\u7968\u6350\u8d60\u7ed9\u4e1c\u6d77\u5927\u5b66\uff0c\u7528\u4e8e\u4e1c\u6d77\u5927\u5b66\u6280\u672f\u5b66\u9662\u5fae\u7535\u5b50\u5de5\u7a0b\u7b49\u76f8\u5173\u4e13\u4e1a\u7684\u5b66\u79d1\u5efa\u8bbe\u4ee5\u53ca\u8868\u5f70\u4e1c\u6d77\u5927\u5b66\u5728\u5fae\u7535\u5b50\u9886\u57df\u505a\u51fa\u5353\u8d8a\u8d21\u732e\u7684\u5b66\u8005\uff0c\u5e76\u5411\u4e1c\u6d77\u5927\u5b66\u53d1\u51fa\u5171\u540c\u5efa\u8bbe\u56fd\u5bb6\u7ea7\u5fae\u7535\u5b50\u57fa\u7840\u6280\u672f\u5b9e\u9a8c\u5ba4\u7684\u9080\u8bf7\u3002</p><p>\u3000\u3000\u4e1c\u6d77\u5927\u5b66\u6821\u957f\u4e0e\u4e34\u65f6\u8d76\u5230\u5efa\u90ba\u7684\u6559\u80b2\u90e8\u9ad8\u6559\u53f8\u53f8\u957f\u3001\u5efa\u90ba\u5e02\u5e38\u59d4\u526f\u5e02\u957f\u738b\u7ef4\u6301\u7b49\u4eba\u5171\u540c\u4e3b\u6301\u4e86\u6350\u8d60\u4eea\u5f0f\uff1b\u8fd9\u662f\u4e1c\u6d77\u5927\u5b66\u5efa\u6821\u6709\u53f2\u4ee5\u6765\u6536\u5230\u7684\u6700\u5927\u5355\u7b14\u6350\u8d60\uff0c\u53c8\u662f\u9526\u6e56\u6a61\u6811\u56ed\u8ba1\u5212\u7684\u524d\u594f\uff0c\u5438\u5f15\u4e86\u7701\u5185\u5916\u5927\u6279\u5a92\u4f53\u8bb0\u8005\uff0c\u7701\u7535\u89c6\u53f0\u76f4\u64ad\u73b0\u573a\u6350\u8d60\u4eea\u5f0f\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c31\u4e0e\u53f6\u5efa\u658c\u5c31\u7a9d\u5728\u5b66\u5e9c\u5df71978\u7684\u684c\u7403\u5ba4\u91cc\u770b\u7740\u7535\u89c6\u91cc\u7684\u73b0\u573a\u76f4\u64ad\u3002</p><p>\u3000\u3000\u5f20\u606a\u59ff\u6001\u8212\u670d\u7684\u9760\u7740\u78e8\u7802\u73bb\u7483\u4e0e\u94dd\u5408\u91d1\u7ec4\u6210\u7684\u9694\u677f\uff0c\u811a\u8df7\u5728\u684c\u7403\u684c\u4e0a\uff0c\u4ed6\u8fd9\u51e0\u5929\u4e60\u60ef\u57281978\u91cc\u6ce1\u676f\u8336\uff0c\u6162\u60a0\u60a0\u7684\u54c1\u7740\u8336\u3002</p><p>\u3000\u3000\u201c\u8d64\u88f8\u88f8\u7684\u884c\u8d3f\uff0c\u4e00\u5b9a\u6709\u4e0d\u53ef\u544a\u4eba\u7684\u79d8\u5bc6\u2026\u2026\u201d\u5b59\u9759\u6aac\u7d20\u624b\u53c9\u7740\u8170\uff0c\u4fee\u526a\u7cbe\u81f4\u7684\u6307\u7532\u6d82\u7740\u4eae\u7c89\u8272\u7684\u6307\u7532\u6cb9\u2014\u2014\u6bd4\u8d77\u6628\u5929\u7684\u84dd\u8272\u7f00\u7740\u661f\u5149\u4eae\u70b9\u7684\u6307\u7532\u6cb9\u8ba9\u4eba\u66f4\u80fd\u5bb9\u6613\u63a5\u53d7\u4e9b\u2014\u2014\u770b\u7740\u7535\u89c6\u6beb\u4e0d\u7559\u60c5\u9762\u7684\u8d28\u7591\u9526\u6e56\u7684\u4e0d\u826f\u5c45\u5fc3\u3002</p><p>\u3000\u3000\u7535\u89c6\u91cc\uff0c\u7231\u8fbe\u96c6\u56e2\u6b63\u4e0e\u4e1c\u6d77\u5927\u5b66\u8054\u5408\u5bf9\u4e1c\u6d77\u5927\u5b66\u5728\u5fae\u7535\u5b50\u9886\u57df\u505a\u51fa\u5353\u8d8a\u8d21\u732e\u7684\u5b66\u8005\u8fdb\u884c\u8868\u5f70\uff0c\u7231\u8fbe\u6350\u8d60\u7ed9\u4e1c\u6d77\u5927\u5b66\u7684\u4e24\u5343\u4e07\uff0c\u4e00\u5343\u4e07\u5c06\u62ff\u51fa\u6765\u505a\u5956\u52b1\u57fa\u91d1\uff0c\u5176\u4e2d\u4e24\u767e\u4e07\u8fd9\u6b21\u5c31\u8981\u53d1\u653e\u4e0b\u53bb\uff0c\u53d1\u653e\u5bf9\u8c61\u90fd\u662f\u7cbe\u5fc3\u6311\u9009\u51fa\u6765\u7684\u5728\u5fae\u7535\u5b50\u6280\u672f\u9886\u57df\u62e5\u6709\u76f8\u5f53\u5730\u4f4d\u7684\u5b66\u8005\uff0c\u4e1c\u6d77\u5927\u5b66\u4e24\u540d\u5fae\u7535\u5b50\u9886\u57df\u7684\u5de5\u7a0b\u9662\u9662\u58eb\u6bcf\u4eba\u6700\u591a\u83b7\u5f9740\u4e07\u5956\u91d1\uff0c21\u540d\u5fae\u7535\u5b50\u9886\u57df\u7684\u6559\u6388\u3001\u526f\u6559\u6388\u4ee5\u53ca\u9752\u5e74\u5b66\u8005\u5206\u522b\u83b7\u5f974\u4e07\u30016\u4e07\u300110\u4e07\u4e0d\u7b49\u7684\u5956\u91d1\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u7684\u8868\u5f70\u5956\u52b1\u53ea\u662f\u4ec5\u9650\u4e8e\u4e1c\u6d77\u5927\u5b66\u800c\u5df2\uff0c\u5f71\u54cd\u529b\u5374\u7edd\u4e0d\u4f1a\u4ec5\u9650\u4e8e\u4e1c\u6d77\u5927\u5b66\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u6487\u5634\u7b11\u7740\u8bf4\uff1a\u201c\u820d\u4e0d\u5f97\u5b69\u5b50\u5957\u4e0d\u5230\u72fc\uff0c\u6a61\u6811\u56ed\u8981\u5438\u5f15\u56fd\u5185\u5916\u6700\u9876\u5c16\u7684\u5fae\u7535\u5b50\u6280\u672f\u4e13\u5bb6\uff0c\u8fd9\u4e48\u505a\u7684\u66ff\u53f0\u8bcd\u65e0\u975e\u5c31\u662f\uff1a\u4e0e\u6211\u4eec\u6ca1\u6709\u76f4\u63a5\u5173\u7cfb\u7684\u4e13\u5bb6\u6559\u6388\uff0c\u6a61\u6811\u56ed\u90fd\u820d\u5f97\u53d1\u4e09\u5341\u4e07\uff0c\u4f60\u4eec\u6765\u5427\uff0c\u7f3a\u4e0d\u4e86\u4f60\u4eec\u7684\u2026\u2026\u8fd8\u6709\u554a\uff0c\u4e0d\u8981\u8bf4\u62ff\u5230\u597d\u5904\u7684\u4e13\u5bb6\u8981\u8bf4\u9526\u6e56\u7684\u597d\u8bdd\uff0c\u90a3\u4e9b\u6ca1\u6709\u62ff\u5230\u597d\u5904\u7684\u4e13\u5bb6\u5b66\u8005\u4e5f\u4f1a\u89c9\u5f97\u9526\u6e56\u5c0a\u91cd\u77e5\u8bc6\u3001\u5c0a\u91cd\u5b66\u8005\uff0c\u4e5f\u8981\u62a2\u7740\u8bf4\u9526\u6e56\u7684\u597d\u8bdd\uff0c\u77e5\u8bc6\u5206\u5b50\u672c\u6765\u5c31\u662f\u4e00\u4e2a\u9636\u5c42\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6709\u4f60\u4eec\u8bf4\u7684\u8fd9\u4e48\u5351\u9119\u65e0\u803b\u5417\uff1f\u201d\u5f20\u606a\u65e0\u5948\u7684\u6447\u5934\u82e6\u7b11\u7740\u8bf4\uff0c\u201c\u5efa\u90ba\u5e02\u91cc\u505a\u51fa\u8fd9\u4e48\u5927\u7684\u51b3\u5fc3\uff0c\u9526\u6e56\u603b\u8981\u6295\u6843\u62a5\u674e\uff0c\u62ff\u51fa\u4e9b\u5b9e\u9645\u884c\u4e3a\u7ed9\u4ed6\u4eec\u770b\uff0c\u8fd9\u6837\uff0c\u63a5\u4e0b\u6765\u7684\u5408\u4f5c\u624d\u80fd\u6109\u5feb\u7684\u8fdb\u884c\u4e0b\u53bb\uff0c\u8fd9\u4e9b\u94b1\u603b\u4e0d\u80fd\u76f4\u63a5\u62ff\u53bb\u9001\u7ed9\u5e02\u59d4\u9886\u5bfc\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u4e00\u4e2a\u95ee\u9898\uff0c\u201d\u5b59\u9759\u6aac\u77a5\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u201c\u9526\u6e56\u62ff\u7740\u6a61\u6811\u56ed\u8ba1\u5212\u5230\u5904\u5ffd\u60a0\uff0c\u676d\u5dde\u3001\u6b66\u6c49\u90fd\u6d3e\u51fa\u76f8\u5f53\u7684\u9635\u5bb9\u6765\u4e89\u53d6\uff0c\u4f60\u4eec\u5c31\u4e0d\u6015\u5bd2\u4e86\u4ed6\u4eec\u7684\u5fc3\uff1f\u201d\u4e0e\u5f20\u606a\u5173\u7cfb\u719f\u7edc\u4e4b\u540e\uff0c\u7279\u522b\u662f\u5e74\u524d\u5f20\u606a\u53f3\u811a\u8e1d\u9aa8\u88c2\uff0c\u5728\u9999\u6e2f\u4f11\u517b\u4e86\u5927\u534a\u4e2a\u6708\uff0c\u5b59\u9759\u6aac\u4e5f\u57fa\u672c\u77e5\u9053\u5f20\u606a\u5728\u9526\u6e56\u8fd9\u4e2a\u4f53\u7cfb\u91cc\u7684\u5730\u4f4d\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u81c2\u5c48\u5230\u8111\u540e\u6795\u7740\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6295\u8d44\u5efa\u90ba\u7684\u53ea\u662f\u6a61\u6811\u56ed\u521d\u671f\u8ba1\u5212\uff0c\u7b49\u8fd9\u4e2a\u6a21\u5f0f\u8fd0\u8425\u6210\u529f\uff0c\u5c31\u53ef\u4ee5\u5728\u676d\u5dde\u3001\u6b66\u6c49\u7b49\u5730\u590d\u5236\u2026\u2026\u770b\uff0c\u6211\u4eec\u5e76\u6ca1\u6709\u5728\u5ffd\u60a0\u554a\uff0c\u8fd8\u8981\u4ed6\u4eec\u7a0d\u6709\u4e9b\u8010\u5fc3\u624d\u884c\uff0c\u201d\u62ac\u624b\u770b\u4e86\u770b\u8155\u8868\uff0c\u201c\u54e6\uff0c\u65f6\u95f4\u5230\u4e86\uff0c\u53f6\u54e5\u966a\u6211\u8fc7\u53bb\uff1f\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u6447\u5934\u8bf4\u9053\uff1a\u201c\u674e\u8fdc\u6e56\u3001\u7f57\u541b\u6709\u4ec0\u4e48\u597d\u89c1\u7684\uff0c\u6211\u4e0d\u53bb\u3002\u201d</p><p>\u3000\u3000\u201c\u60f3\u77e5\u9053\u9526\u6e56\u66f4\u591a\u79d8\u5bc6\u7684\u673a\u4f1a\u4e0d\u4f1a\u592a\u591a\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4f60\u4e0d\u53bb\uff1f\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u542c\u5230\u8fd9\u53e5\u8bdd\uff0c\u7ffb\u8eab\u7ad9\u5728\u8d77\u6765\uff1a\u201c\u8d70\u8d70\u8d70\uff0c\u5750\u6211\u7684\u7687\u51a0\u53bb\uff0c\u4f60\u90a3\u8f86Vs40\u592a\u5c0f\u6c14\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u662f\u8981\u53bb\u8ddf\u674e\u8fdc\u6e56\u89c1\u9762\uff0c\u50f5\u6301\u4e86\u8fd9\u4e48\u957f\u65f6\u95f4\uff0c\u4e5f\u5e94\u8be5\u8981\u5750\u4e0b\u6765\u597d\u597d\u7684\u4ea4\u6d41\u4e00\u4e0b\uff0c\u54ea\u6015\u662f\u4e3a\u4e86\u5404\u81ea\u7684\u5229\u76ca\u80fd\u5728\u4e1c\u6d77\u8fd9\u7247\u571f\u5730\u4e0a\u66f4\u597d\u7684\u53d1\u5c55\u4e0b\u53bb\uff0c\u4e5f\u9700\u8981\u597d\u597d\u7684\u5750\u4e0b\u6765\u4ea4\u6d41\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u5750\u5728\u7a97\u524d\uff0c\u4e00\u682a\u94f6\u674f\u7684\u6811\u51a0\u906e\u5728\u7a97\u524d\uff0c\u4ece\u8302\u76db\u6811\u51a0\u91cc\uff0c\u674e\u8fdc\u6e56\u5728\u641c\u89c6\u661f\u661f\u70b9\u70b9\u7684\u84dd\u5929\u3002\u9646\u6587\u592b\u6572\u95e8\u8fdb\u6765\u544a\u8bc9\u4ed6\uff1a\u201c\u7f57\u541b\u4e66\u8bb0\u521a\u4ece\u9676\u664b\u4e66\u8bb0\u90a3\u91cc\u8fc7\u6765\uff0c\u6211\u5b89\u6392\u4ed6\u5728\u5c0f\u4f1a\u8bae\u5ba4\u91cc\u7b49\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u5c31\u8fc7\u53bb\uff0c\u201d\u674e\u8fdc\u6e56\u5c06\u76ee\u5149\u4ece\u7a97\u5916\u6536\u4e86\u56de\u6765\uff0c\u201c\u5f20\u606a\u4ed6\u4eec\u5230\u4e86\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u201c\u521a\u6253\u7535\u8bdd\u8fc7\u6765\uff0c\u897f\u5b89\u8def\u90a3\u8fb9\u6709\u4e9b\u5835\uff0c\u8fd8\u8981\u7b49\u4e9b\u65f6\u95f4\u2026\u2026\u201d</p><p>\u3000\u3000\u5efa\u90ba\u5e02\u7684\u4ea4\u901a\u72b6\u51b5\u6709\u4e9b\u7cdf\u7cd5\uff0c\u8fd9\u65f6\u5019\u5835\u8def\u662f\u8c01\u4e5f\u65e0\u6cd5\u9884\u6599\u7684\u4e8b\u60c5\uff0c\u674e\u8fdc\u6e56\u5e76\u6ca1\u6709\u62b1\u6028\u5f20\u606a\u90a3\u8fb9\u4e3a\u4ec0\u4e48\u4e0d\u63d0\u524d\u51fa\u53d1\uff0c\u5fc3\u6001\u7684\u53d8\u5316\u603b\u662f\u5fae\u5999\u7684\uff0c\u6216\u8bb8\u8fde\u674e\u8fdc\u6e56\u81ea\u5df1\u90fd\u610f\u8bc6\u4e0d\u5230\u3002</p><p>\u3000\u3000\u7f57\u541b\u662f\u7701\u59d4\u5e38\u59d4\u3001\u5efa\u90ba\u5e02\u59d4\u4e66\u8bb0\uff0c\u653f\u6cbb\u5730\u4f4d\u5e76\u4e0d\u6bd4\u4ed6\u8fd9\u4e2a\u7701\u957f\u4f4e\u591a\u5c11\uff0c\u867d\u7136\u79bb\u7ea6\u5b9a\u7684\u65f6\u95f4\u8fd8\u6709\u4e9b\u65f6\u95f4\uff0c\u674e\u8fdc\u6e56\u8fd8\u662f\u8981\u5148\u53bb\u966a\u7f57\u541b\u8bf4\u4e00\u4f1a\u513f\u8bdd\u7684\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u5148\u53bb\u5c0f\u4f1a\u8bae\u5ba4\uff0c\u7b49\u5f20\u606a\u5230\u4e86\uff0c\u4f60\u76f4\u63a5\u9886\u4ed6\u8fc7\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u5370\u8c61\u91cc\uff0c\u674e\u8fdc\u6e56\u7b2c\u4e00\u6b21\u89c1\u5f20\u606a\u8fd8\u662f\u5e74\u540e\u5f20\u606a\u9014\u7ecf\u5efa\u90ba\u4e0e\u53f6\u5efa\u658c\u5728\u5efa\u90ba\u996d\u5e97\u7528\u9910\u90a3\u6b21\uff0c\u90a3\u65f6\u8c01\u80fd\u60f3\u5230\u8fd9\u4e48\u4e2a\u9752\u5e74\u4f1a\u6709\u8d44\u683c\u4e0e\u674e\u8fdc\u6e56\u5206\u5ead\u6297\u793c\u3002\u4ed6\u5148\u966a\u674e\u8fdc\u6e56\u53bb\u5c0f\u4f1a\u8bae\u5ba4\u89c1\u5efa\u90ba\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\uff0c\u518d\u5230\u7701\u653f\u5e9c\u529e\u516c\u5927\u697c\u524d\u7b49\u5f20\u606a\u4ed6\u4eec\u8fc7\u6765\uff0c\u6731\u5c0f\u519b\u5df2\u7ecf\u7ed9\u4ed6\u6253\u53d1\u5728\u8fd9\u91cc\u7b49\u5019\u4e86\u3002</p><p>\u3000\u3000\u867d\u7136\u674e\u8fdc\u6e56\u6ca1\u6709\u4ecb\u610f\uff0c\u8ba9\u674e\u8fdc\u6e56\u4e0e\u7f57\u541b\u5728\u4f1a\u8bae\u5ba4\u7b49\u4e45\u4e86\u4e5f\u4e0d\u597d\uff0c\u9646\u6587\u592b\u95ee\u6731\u5c0f\u519b\uff1a\u201c\u5c0f\u6731\uff0c\u5f20\u606a\u4ed6\u4eec\u8fd8\u8981\u8fc7\u591a\u4e45\u624d\u5230\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9a6c\u4e0a\u5c31\u5230\uff0c\u62d0\u8fc7\u798f\u5c71\u8def\u8def\u53e3\u4e86\u2026\u2026\u201d\u6731\u5c0f\u519b\u8bf4\u9053\u3002</p><p>\u3000\u3000\u90a3\u5c31\u5feb\u4e86\uff0c\u80fd\u8d76\u5728\u7ea6\u5b9a\u7684\u65f6\u95f4\u5185\u5230\u8fbe\uff0c\u9646\u6587\u592b\u653e\u4e86\u5fc3\uff0c\u53c8\u95ee\u6731\u5c0f\u519b\uff1a\u201c\u4f60\u505a\u597d\u51b3\u5b9a\u51c6\u5907\u53bb\u9ad8\u65b0\u4ea7\u4e1a\u56ed\u533a\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u505a\u4e9b\u5b9e\u9645\u7684\u5de5\u4f5c\uff0c\u4e5f\u80fd\u953b\u70bc\u81ea\u5df1\u3002\u201d\u6731\u5c0f\u519b\u8bf4\u9053\uff0c\u4ed6\u8fd9\u6b21\u8c03\u53bb\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u56ed\u533a\u662f\u7ed9\u59da\u6587\u76db\u5f53\u52a9\u624b\uff0c\u62c5\u4efb\u56ed\u533a\u6295\u8d44\u4fc3\u8fdb\u5c40\u5c40\u957f\u52a9\u7406\u3002\u5c31\u50cf\u59da\u6587\u76db\u8c03\u5230\u4e1c\u6d77\u5efa\u90ba\u6765\u7ed9\u4e2a\u6b63\u5904\u7ea7\u8f7b\u800c\u6613\u4e3e\u4e00\u6837\uff0c\u6731\u5c0f\u519b\u8c03\u53bb\u9ad8\u65b0\u4ea7\u4e1a\u56ed\u533a\u7ed9\u4e86\u4e2a\u6b63\u79d1\u7ea7\u4e5f\u51e0\u4e4e\u60ca\u52a8\u4e0d\u4e86\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u5230\u56ed\u533a\u597d\u597d\u953b\u70bc\u4e00\u4e0b\u2026\u2026\u201d\u9646\u6587\u592b\u9f13\u52b1\u7684\u62cd\u4e86\u62cd\u6731\u5c0f\u519b\u7684\u80a9\u8180\uff0c\u770b\u5230\u53f6\u5efa\u658c\u7684\u5927\u7687\u51a0\u9a76\u8fdb\u5927\u697c\u524d\u5ead\u5e7f\u573a\uff0c\u4ed6\u4e0e\u6731\u5c0f\u519b\u8d70\u4e0b\u53f0\u9636\uff0c\u770b\u7740\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u610f\u6c14\u98ce\u53d1\u7684\u8d70\u4e0b\u8f66\u6765\u3002</p><p>\u3000\u3000\u770b\u7740\u9646\u6587\u592b\u964d\u9636\u76f8\u8fce\uff0c\u5f20\u606a\u4e5f\u662f\u865a\u8363\u5fc3\u7206\u6ee1\u554a\uff0c\u54c8\u54c8\u4e00\u7b11\uff0c\u5fd9\u8d70\u8fc7\u6765\u4e0e\u9646\u6587\u592b\u63e1\u624b\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8ba9\u9646\u53d4\u53d4\u4f60\u7b49\u597d\u4e45\u4e86\u3002\u201d\u5012\u662f\u6ca1\u6709\u751f\u786c\u7684\u79f0\u547c\u4ed6\u7684\u804c\u52a1\uff0c\u4e5f\u662f\u5e0c\u671b\u4ed6\u5ff5\u4e4b\u524d\u7684\u60c5\u4efd\uff0c\u6bd5\u7adf\u9526\u6e56\u662f\u4e0d\u4f1a\u9009\u62e9\u4e0e\u674e\u8fdc\u6e56\u5bf9\u6297\u7684\uff0c\u4e5f\u4e0d\u4f1a\u8ba9\u9646\u6587\u592b\u4ed6\u751f\u5b58\u5728\u5bf9\u6297\u7684\u5939\u7f1d\u91cc\u7684\u3002</p></article>"}}}]);
