(window.webpackJsonp=window.webpackJsonp||[]).push([[561],{583:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u8282 \u6e38\u620f",content:"<article><h2>\u7b2c\u4e8c\u8282 \u6e38\u620f</h2><p>\u66b4\u96e8\u7a81\u81f3\u3002</p><p>\u51e0\u4e4e\u662f\u5728\u4e00\u77ac\u95f4\uff0c\u96e8\u4fbf\u5f00\u59cb\u54d7\u5566\u54d7\u5566\u5730\u4e0b\u8d77\u6765\uff0c\u6253\u5728\u6811\u53f6\u4e0a\u53d1\u51fa\u556a\u55d2\u556a\u55d2\u7684\u6e05\u8106\u58f0\uff0c\u5730\u9762\u5df2\u7ecf\u6c47\u805a\u6210\u4e86\u4e00\u9053\u9053\u6d45\u6d45\u7684\u6eaa\u6d41\uff0c\u843d\u53f6\u6f02\u6d6e\u5728\u4e0a\u9762\uff0c\u6253\u7740\u8f6c\u3002\u900f\u8fc7\u6811\u51a0\u95f4\u7684\u7a7a\u9699\u770b\u53bb\uff0c\u524d\u4e00\u523b\u8fd8\u7b97\u662f\u6674\u6717\u7684\u5929\u7a7a\u5df2\u7ecf\u5b8c\u5168\u88ab\u4e4c\u4e91\u7b3c\u7f69\uff0c\u9633\u5149\u534a\u70b9\u90fd\u900f\u4e0d\u51fa\u6765\uff0c\u94f6\u8272\u7684\u8000\u773c\u95ea\u7535\u5b9b\u5982\u5de8\u86c7\uff0c\u4e0d\u65f6\u873f\u8712\u800c\u4e0b\uff0c\u5212\u7834\u5929\u7a7a\uff0c\u968f\u4e4b\u800c\u6765\u7684\u4fbf\u662f\u8f70\u9686\u9686\u7684\u6c89\u95f7\u96f7\u9e23\u3002</p><p>\u5728\u6811\u6797\u4e2d\uff0c\u743c\u6069\u4e00\u884c\u4e03\u4eba\u6b63\u5728\u8dcb\u6d89\u524d\u884c\u3002\u4ed6\u4eec\u6ca1\u6709\u4f1e\uff0c\u4e5f\u6ca1\u6709\u96e8\u8863\uff0c\u4f46\u6bcf\u4e2a\u4eba\u7684\u8eab\u4f53\u8868\u9762\u90fd\u6709\u4e00\u5c42\u900f\u660e\u7684\u3001\u95ea\u95ea\u53d1\u5149\u7684\u201c\u819c\u201d\u5305\u88f9\u7740\uff0c\u963b\u9694\u7740\u96e8\u6c34\u7684\u4fb5\u5165\uff0c\u540c\u65f6\u4e5f\u8d77\u5230\u5728\u9ed1\u6697\u4e2d\u7167\u660e\u7684\u4f5c\u7528\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u96e8\u6c34\u8fd8\u662f\u7ed9\u4ed6\u4eec\u5e26\u6765\u4e86\u5f88\u5927\u7684\u9ebb\u70e6\uff0c\u9053\u8def\u5df2\u7ecf\u6ee1\u662f\u6ce5\u6cde\uff0c\u4e00\u811a\u8e29\u4e0b\u53bb\u5c31\u662f\u4e00\u4e2a\u6d45\u5751\uff0c\u4e0d\u5c0f\u5fc3\u8fd8\u4f1a\u6ed1\u4e00\u8de4\uff0c\u8d70\u8d77\u6765\u975e\u5e38\u8d39\u529b\u3002\u800c\u4e14\u56e0\u4e3a\u96e8\u4e0b\u5f97\u592a\u5927\uff0c\u89c6\u7ebf\u53d7\u5230\u4e25\u91cd\u5f71\u54cd\uff0c\u8fde\u8def\u90fd\u4e0d\u600e\u4e48\u770b\u5f97\u6e05\u3002</p><p>\u201c\u4e0d\u884c\uff0c\u4e0d\u80fd\u8d70\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u96e8\u4e0b\u5f97\u592a\u5927\uff0c\u4f11\u606f\u4f1a\u5427\uff0c\u7b49\u96e8\u505c\u4e86\u518d\u8bf4\u3002\u201d</p><p>\u4ed6\u7ad9\u5b9a\u811a\u6b65\uff0c\u770b\u4e86\u770b\u56db\u5468\uff0c\u671d\u7740\u4e00\u5904\u7a7a\u5730\u9ed8\u8bf5\u5492\u6587\uff0c\u7247\u523b\u4e4b\u540e\uff0c\u4e00\u5ea7\u7070\u8910\u8272\u7684\u5c0f\u6728\u5c4b\u51ed\u7a7a\u51fa\u73b0\u4e86\u3002\u4e00\u884c\u4eba\u8fdb\u5165\uff0c\u5173\u4e0a\u95e8\uff0c\u5c06\u98ce\u96e8\u963b\u9694\u5728\u5916\uff0c\u603b\u7b97\u662f\u53ef\u4ee5\u6b47\u4e0b\u6765\u5598\u53e3\u6c14\u3002</p><p>\u5728\u5ba2\u5385\u91cc\u4f11\u606f\u4e86\u4e00\u4f1a\uff0c\u773c\u770b\u5916\u9762\u7684\u96e8\u8d8a\u4e0b\u8d8a\u5927\uff0c\u54d7\u5566\u54d7\u5566\uff0c\u534a\u70b9\u6ca1\u6709\u51cf\u5f31\u7684\u8d8b\u52bf\uff0c\u743c\u6069\u638f\u51fa\u9b54\u6cd5\u6c99\u6f0f\u770b\u4e86\u770b\u65f6\u95f4\uff0c\u201c\u5dee\u4e0d\u591a\u5feb\u56db\u70b9\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u9b54\u6cd5\u6c99\u6f0f\u7684\u8ba1\u65f6\u4e0d\u662f\u5f88\u51c6\u786e\uff0c\u53ea\u80fd\u4f30\u7b97\u4e00\u4e2a\u5927\u81f4\u65f6\u95f4\uff0c\u201c\u8fd9\u96e8\u770b\u6837\u5b50\u4e00\u65f6\u534a\u4f1a\u662f\u505c\u4e0d\u4e86\uff0c\u4eca\u5929\u5c31\u5728\u8fd9\u91cc\u5bbf\u8425\u5427\u3002\u201d</p><p>\u8fd9\u5ea7\u5c0f\u6728\u5c4b\u5360\u5730\u5f88\u5c0f\uff0c\u5b9e\u9645\u8fdb\u5165\u540e\u5c31\u4f1a\u53d1\u73b0\u5176\u5185\u90e8\u7a7a\u95f4\u8981\u6bd4\u5916\u8868\u770b\u8d77\u6765\u5927\u5f88\u591a\uff0c\u4e0a\u4e0b\u4e24\u5c42\uff0c\u5c3d\u7ba1\u5982\u6b64\uff0c\u5f53\u4e03\u4e2a\u4eba\u90fd\u8fdb\u6765\u540e\uff0c\u8fd8\u662f\u663e\u5f97\u6709\u4e9b\u62e5\u6324\u3002\u201c\u600e\u4e48\u624d\u4e09\u5ba4\u4e00\u5385\uff1f\u201d\u51db\u56db\u5904\u770b\u770b\uff0c\u201c\u592a\u5c0f\u4e86\u5427\u3002\u201d</p><p>\u201c\u522b\u7ad9\u7740\u8bf4\u8bdd\u4e0d\u8170\u75bc\uff0c\u201d\u743c\u6069\u6ca1\u597d\u6c14\u5730\u8bf4\uff0c\u201c\u8981\u4e48\u4f60\u6765\u8bd5\u8bd5\uff1f\u201d</p><p>\u8fd9\u662f\u201c\u8c6a\u5b85\u672f\u201d\uff0c\u4e00\u79cd\u6df1\u53d7\u65c5\u884c\u8005\u559c\u7231\u7684\u6cd5\u672f\uff0c\u80fd\u591f\u51ed\u7a7a\u53ec\u5524\u51fa\u4e00\u5ea7\u623f\u5c4b\u4f9b\u4eba\u5c45\u4f4f\u3001\u4f11\u606f\u3002\u5f53\u7136\u8fd9\u623f\u5c4b\u662f\u4e34\u65f6\u7684\uff0c\u4e00\u6bb5\u65f6\u95f4\u4e4b\u540e\u5c31\u4f1a\u81ea\u52a8\u6d88\u5931\uff0c\u81f3\u4e8e\u80fd\u7ef4\u6301\u591a\u4e45\uff0c\u5c31\u770b\u65bd\u6cd5\u8005\u7684\u9020\u8be3\u9ad8\u4f4e\u4e86\u3002\u665a\u4e0a\u53ec\u5524\u623f\u5c4b\u4f4f\u8fdb\u53bb\uff0c\u7b2c\u4e8c\u5929\u65e9\u4e0a\u8d77\u6765\u53d1\u73b0\u7761\u5728\u8352\u5730\u4e0a\uff0c\u8fd9\u79cd\u4e8b\u60c5\u4e5f\u662f\u7ecf\u5e38\u53d1\u751f\u7684\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u623f\u5c4b\u5927\u5c0f\u4e5f\u662f\u6839\u636e\u65bd\u6cd5\u8005\u7684\u80fd\u529b\u800c\u5b9a\uff0c\u5965\u6c83\u53ec\u51fa\u7684\u623f\u5c4b\u5b9b\u5982\u522b\u5885\uff0c\u6bd4\u62df\u57ce\u5821\uff0c\u800c\u4ee5\u743c\u6069\u76ee\u524d\u7684\u9020\u8be3\uff0c\u53ea\u80fd\u53ec\u51fa\u4e09\u5ba4\u4e00\u5385\uff0c\u9a6c\u9a6c\u864e\u864e\u6b63\u597d\u591f\u7528\u3002</p><p>\u201c\u4ec0\u4e48\u591f\u7528\u554a\uff0c\u201d\u51db\u62b1\u6028\uff0c\u201c\u6211\u4eec\u6709\u4e03\u4e2a\u4eba\u5462\uff0c\u600e\u4e48\u4f4f\uff1f\u201d</p><p>\u201c\u838e\u73de\u514b\u53ef\u4ee5\u56de\u5230\u5b9d\u77f3\u91cc\uff0c\u5269\u4e0b\u516d\u4e2a\u4eba\uff0c\u4e24\u4eba\u4e00\u95f4\uff0c\u6b63\u597d\u3002\u201d</p><p>\u201c\u5f88\u6324\u554a\uff0c\u201d\u51db\u8dd1\u5230\u623f\u95f4\u91cc\u770b\u4e86\u770b\uff0c\u201c\u8fd8\u662f\u5355\u4eba\u5e8a\uff0c\u4e0d\u591f\u7761\u7684\u3002\u201d</p><p>\u201c\u53ef\u4ee5\u53e0\u8d77\u6765\u561b\u3002\u201d</p><p>\u201c\u53e0\u8d77\u6765\u7761\uff1f\u201d\u51db\u9677\u5165\u601d\u7d22\uff0c\u201c\u5730\u65b9\u5012\u662f\u591f\u4e86\uff0c\u4e0d\u8fc7\u8fd9\u6837\u7761\u5f88\u7d2f\u554a\uff0c\u827e\u5f25\u8587\u53c8\u5f88\u91cd\uff0c\u7ecf\u5e38\u538b\u5f97\u6211\u900f\u4e0d\u8fc7\u6c14......\u201d</p><p>\u5514\uff0c\u6211\u597d\u50cf\u542c\u5230\u4e86\u4ec0\u4e48\u4e86\u4e0d\u5f97\u7684\u79d8\u5bc6\u3002</p><p>\u51db\u8bdd\u521a\u51fa\u53e3\u4e5f\u968f\u5373\u53cd\u5e94\u8fc7\u6765\uff0c\u987f\u65f6\u8138\u8272\u7f9e\u7ea2\uff0c\u5e78\u597d\u5176\u4ed6\u4eba\u90fd\u88c5\u4f5c\u6ca1\u542c\u89c1\u3002\u201c\u90a3\u5c31\u8fd9\u6837\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4e24\u4eba\u4e00\u95f4\uff0c\u827e\u5f25\u8587\u548c\u51db\uff0c\u743c\u6069\u548c\u5854\u62c9\u590f\uff0c\u7fe1\u7fe0\u5c31\u548c\u6211\u6324\u4e00\u6324\u5427\u3002\u201d</p><p>\u201c\u6211\u4e0d\u9700\u8981\uff0c\u201d\u7fe1\u7fe0\u5973\u5deb\u8bf4\uff0c\u201c\u6709\u4e2a\u5b89\u9759\u7684\u89d2\u843d\u51a5\u60f3\u5c31\u53ef\u4ee5\u4e86\u3002\u201d</p><p>\u201c\u90a3\u5c31\u8fd8\u662f\u838e\u73de\u514b\u548c\u6211\u4e00\u95f4\u5427\u3002\u201d\u73ca\u5609\u8bf4\u3002</p><p>\u623f\u95f4\u5206\u914d\u5b8c\u6bd5\uff0c\u63a5\u4e0b\u6765\u4fbf\u662f\u51c6\u5907\u665a\u9910\uff0c\u743c\u6069\u53ec\u5524\u51fa\u6765\u7684\u623f\u5c4b\u91cc\u662f\u6ca1\u6709\u53a8\u623f\u7684\u2014\u2014\u5c31\u7b97\u6709\uff0c\u4e5f\u53d8\u4e0d\u51fa\u9505\u7897\u74e2\u76c6\uff0c\u5e78\u597d\u5728\u51fa\u53d1\u4e4b\u524d\u6709\u6240\u51c6\u5907\u3002\u73ca\u5609\u4ece\u6b21\u5143\u888b\u91cc\u53d6\u51fa\u9762\u5305\u3001\u54b8\u8089\u3001\u9c7c\u9171\u548c\u4e00\u4e9b\u852c\u83dc\uff0c\u4e09\u4e0b\u4e94\u9664\u4e8c\u5c31\u5f04\u51fa\u4e86\u4e03\u4e2a\u4e09\u660e\u6cbb\uff0c\u6bcf\u4eba\u4e00\u4e2a\u3002</p><p>\u201c\u597d\u5403\uff0c\u5c31\u662f\u7a0d\u5fae\u6709\u70b9\u54b8\u3002\u201d\u7ef4\u82e5\u62c9\u54ac\u4e86\u4e00\u53e3\uff0c\u8bc4\u4ef7\u8bf4\u3002</p><p>\u201c\u6211\u89c9\u5f97\u5473\u9053\u6b63\u597d\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f60\u90a3\u5757\u9171\u51c3\u5f97\u6709\u70b9\u591a\u5427\u3002\u201d</p><p>\u201c\u59d0\u59d0\u7684\u53a8\u827a\u8d8a\u6765\u8d8a\u597d\u4e86\uff0c\u201d\u743c\u6069\u4e00\u8fb9\u5403\u4e00\u8fb9\u62cd\u9a6c\u5c41\uff0c\u201c\u6211\u89c9\u5f97\u6211\u80fd\u5403\u5341\u4e2a\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u4e2a\u7ed9\u4f60\u5427\uff0c\u201d\u838e\u73de\u514b\u628a\u81ea\u5df1\u7684\u90a3\u4efd\u9012\u8fc7\u6765\uff0c\u201c\u6211\u6ca1\u4ec0\u4e48\u80c3\u53e3\u3002\u201d</p><p>\u5979\u662f\u9b45\u9b54\uff0c\u51e1\u4eba\u7684\u98df\u7269\u5bf9\u5979\u800c\u8a00\u4e0d\u662f\u5fc5\u987b\uff0c\u5403\u4e5f\u53ef\u4ee5\u4e0d\u5403\u4e5f\u65e0\u59a8\u3002\u743c\u6069\u4e5f\u61d2\u5f97\u8ddf\u5979\u5ba2\u6c14\uff0c\u63a5\u4e86\u8fc7\u6765\uff0c\u4ed6\u7684\u786e\u662f\u6709\u70b9\u997f\u4e86\uff0c\u4e09\u53e3\u4e24\u53e3\u628a\u838e\u73de\u514b\u7684\u8fd9\u4efd\u4e5f\u5403\u5b8c\u3002\u201c\u5403\u9971\u4e86\u4e48\uff1f\u201d\u73ca\u5609\u5173\u5fc3\u5730\u95ee\uff0c\u201c\u4e0d\u591f\u6211\u518d\u7ed9\u4f60\u505a\u4e00\u4efd\u3002\u201d</p><p>\u201c\u591f\u4e86\u591f\u4e86\u3002\u201d\u743c\u6069\u8d76\u5feb\u8bf4\uff0c\u73ca\u5609\u624b\u827a\u7684\u786e\u4e0d\u9519\uff0c\u4ece\u5c0f\u64cd\u6301\u5bb6\u52a1\u953b\u70bc\u51fa\u6765\u7684\uff0c\u4f46\u4e09\u660e\u6cbb\u5c31\u662f\u4e09\u660e\u6cbb\uff0c\u505a\u5f97\u518d\u597d\u5b83\u4e5f\u4e0d\u4f1a\u53d8\u6210\u7ea2\u70e7\u8098\u5b50\uff0c\u5403\u4e24\u4e2a\u5df2\u7ecf\u6709\u70b9\u52c9\u5f3a\uff0c\u518d\u591a\u5c31\u4e0d\u884c\u4e86\u3002</p><p>\u201c\u6765\uff0c\u5c1d\u5c1d\u8fd9\u4e2a\uff0c\u524d\u5929\u521a\u521a\u917f\u597d\u7684\u3002\u201d</p><p>\u51db\u4ece\u6b21\u5143\u888b\u91cc\u638f\u51fa\u5de8\u5927\u7684\u6c34\u6676\u74f6\u548c\u4e03\u4e2a\u676f\u5b50\uff0c\u5012\u6ee1\u6df1\u7eff\u8272\u7684\u996e\u6599\uff0c\u4e00\u4eba\u5206\u4e86\u4e00\u676f\u3002</p><p>\u201c\u8fd9\u662f\u4f60\u53d1\u660e\u7684\u90a3\u4ec0\u4e48\uff0c\u201d\u743c\u6069\u4e00\u65f6\u60f3\u4e0d\u8d77\u540d\u5b57\uff0c\u201c\u611f\u89c9\u989c\u8272\u6709\u70b9\u4e0d\u4e00\u6837\u554a\u3002\u201d</p><p>\u201c\u51db\u7684\u5145\u6ee1\u7231\u610f\u7684\u6db2\u4f53\uff0c\u201d\u51db\u5f97\u610f\u5730\u8bf4\uff0c\u201c\u6211\u6700\u65b0\u6539\u8fdb\u4e86\u914d\u65b9\uff0c\u589e\u52a0\u4e86\u4e24\u79cd\u8349\u836f\uff0c\u4e0d\u4f46\u6bd4\u4ee5\u524d\u66f4\u597d\u559d\uff0c\u800c\u4e14\u8fd8\u80fd\u4e30\u80f8\u5462\u3002\u201d</p><p>\u201c......\u201d</p><p>\u743c\u6069\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u628a\u539f\u672c\u5df2\u7ecf\u4e3e\u5230\u5634\u8fb9\u7684\u676f\u5b50\u53c8\u653e\u4e86\u4e0b\u6765\uff0c\u60f3\u4e86\u60f3\uff0c\u9012\u7ed9\u6885\u83f2\u65af\u3002\u5c11\u5973\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u8fd8\u662f\u63a5\u4e86\u8fc7\u6765\uff0c\u4e00\u996e\u800c\u5c3d\u3002</p><p>\u5403\u5b8c\u665a\u9910\uff0c\u5927\u5bb6\u6392\u961f\u53bb\u6d17\u6fa1\u3002\u867d\u8bf4\u7528\u6cd5\u672f\u963b\u6321\u4e86\u96e8\u6c34\uff0c\u4f46\u8d70\u4e86\u4e00\u5929\u7684\u8def\uff0c\u6bcf\u4e2a\u4eba\u90fd\u51fa\u4e86\u6ee1\u8eab\u6c57\uff0c\u8863\u670d\u5168\u6e7f\u900f\u4e86\u3002\u82e5\u662f\u5965\u6c83\u7684\u9752\u94dc\u8c6a\u5b85\u91cc\uff0c\u6d17\u6fa1\u662f\u5f88\u65b9\u4fbf\u7684\uff0c\u90a3\u4f4d\u80d6\u5deb\u5996\u70ed\u7231\u6d17\u6fa1\uff0c\u6d74\u5ba4\u5efa\u5f97\u6bd4\u5367\u5ba4\u90fd\u591a\u3002\u4f46\u743c\u6069\u53ec\u51fa\u7684\u623f\u5c4b\uff0c\u53ea\u6709\u4e00\u95f4\u6d74\u5ba4\uff0c\u4eba\u53c8\u6bd4\u8f83\u591a\uff0c\u4e0d\u6392\u961f\u4e0d\u884c\u3002</p><p>\u201c\u6240\u4ee5\u4f60\u8d76\u5feb\u628a\u5b83\u4fee\u597d\u561b\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u73b0\u5728\u8fd9\u6837\u597d\u4e0d\u65b9\u4fbf\u7684\uff0c\u6211\u8981\u6709\u5927\u5927\u7684\u5e8a\uff0c\u8f6f\u8f6f\u7684\u88ab\u5b50\uff0c\u5149\u7ebf\u67d4\u548c\u7684\u5e8a\u5934\u706f\uff0c\u4eae\u5ea6\u6b63\u9002\u5408\u827e\u5f25\u8587\u7ed9\u6211\u8bfb\u7761\u524d\u6545\u4e8b\u3002\u201d</p><p>\u743c\u6069\u7a81\u7136\u6709\u79cd\u4e0d\u5999\u7684\u611f\u89c9\uff0c\u201c\u4ec0\u4e48\u7761\u524d\u6545\u4e8b\uff1f\u201d</p><p>\u201c\u5c31\u662f\u4f60\u90a3\u672c\u4e66\u561b\uff0c\u5b57\u592a\u5c0f\uff0c\u770b\u8d77\u6765\u597d\u8d39\u52b2\uff0c\u6240\u4ee5\u6211\u8ba9\u827e\u5f25\u8587\u8bfb\u7ed9\u6211\u542c\u3002\u6700\u8fd1\u5728\u8bb2\u4e00\u4e2a\u8003\u8bd5\u843d\u699c\u7684\u5c11\u5e74\u6361\u5230\u4e00\u5f20\u5165\u5b66\u901a\u77e5\u4e66\uff0c\u65e0\u610f\u95f4\u8fdb\u5165\u4e00\u5ea7\u602a\u7269\u5b66\u6821\u7684\u6545\u4e8b\u2014\u2014\u6628\u665a\u8bb2\u5230\u54ea\u4e86\uff1f\u201d</p><p>\u201c\u4e3b\u4eba\u516c\u7b2c\u4e00\u5929\u4e0a\u5b66\u8def\u4e0a\u906d\u9047\u4e86\u4e00\u4f4d\u5c11\u5973\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u8fd9\u4f4d\u5c11\u5973\u6709\u53cc\u91cd\u4eba\u683c\uff0c\u8868\u4eba\u683c\u662f\u6e29\u67d4\u4e56\u5de7\u7684\u5b66\u751f\uff0c\u91cc\u4eba\u683c\u662f\u94f6\u53d1\u7684\u5438\u8840\u5973\u738b\uff0c\u89c9\u9192\u540e\u6253\u5012\u4e86\u524d\u6765\u6ecb\u6270\u7684\u72fc\u4eba\u548c\u68a6\u9b54\uff0c\u7136\u540e\u9b54\u5973\u51fa\u573a\u4e86\u3002\u201d</p><p>\u201c......\u201d</p><p>\u201c\u8bdd\u8bf4\u6211\u4eec\u4ee5\u524d\u6ca1\u6709\u9752\u94dc\u8c6a\u5b85\u7684\u65f6\u5019\uff0c\u4e5f\u6ca1\u89c1\u4f60\u8fd9\u4e48\u5a07\u6c14\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90a3\u65f6\u5019\u968f\u4fbf\u642d\u4e2a\u5e10\u7bf7\u5c31\u4f11\u606f\uff0c\u73b0\u5728\u81f3\u5c11\u6709\u5ea7\u623f\u5c4b\u4e86\uff0c\u4f60\u8fd8\u6311\u4e09\u62e3\u56db\uff0c\u8fd9\u4e48\u591a\u62b1\u6028\u3002\u771f\u662f\u7531\u4fed\u5165\u5962\u6613\uff0c\u7531\u5962\u5165\u4fed\u96be\u3002\u201d</p><p>\u201c\u5582\u5582\uff0c\u6ce8\u610f\u4f60\u7684\u63aa\u8f9e\uff01\u201d\u51db\u5f97\u610f\u5730\u633a\u4e86\u633a\u80f8\uff0c\u201c\u6211\u73b0\u5728\u662f\u7ea2\u9f99\u5973\u738b\uff0c\u8eab\u4efd\u4e0d\u540c\uff0c\u8981\u6c42\u5f53\u7136\u4e0d\u4e00\u6837\u4e86\u3002\u4f60\u89c1\u8fc7\u5973\u738b\u7761\u5e10\u7bf7\u5417\uff1f\u201d</p><p>\u201c\u6211\u4e5f\u6ca1\u89c1\u8fc7\u5973\u738b\u8fd8\u8981\u522b\u4eba\u8bb2\u7761\u524d\u6545\u4e8b\uff01\u201d</p><p>\u201c\u6709\u554a\uff0c\u8001\u5e08\u5c31\u633a\u559c\u6b22\uff0c\u7ecf\u5e38\u8981\u6211\u7ed9\u5979\u8bfb\u5c0f\u8bf4\uff0c\u8bf4\u662f\u6709\u52a9\u4e8e\u5165\u7761\u3002\u201d</p><p>\u201c\u5979\u8fd8\u6709\u8fd9\u4e2a\u7231\u597d\uff1f\u201d\u743c\u6069\u95fb\u6240\u672a\u95fb\uff0c\u8f6c\u773c\u770b\u7ef4\u82e5\u62c9\uff0c\u5c31\u89c1\u5973\u5deb\u5e08\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u8868\u793a\u786e\u8ba4\u3002</p><p>\u201c\u4f60\u8001\u5e08\u90fd\u559c\u6b22\u8bfb\u4ec0\u4e48\u5c0f\u8bf4\u5462\uff1f\u201d\u73ca\u5609\u5728\u65c1\u8fb9\u95ee\u3002</p><p>\u201c\u6700\u559c\u6b22\u7684\u662f\u6050\u6016\u5c0f\u8bf4\uff0c\u9b3c\u6545\u4e8b\u90a3\u79cd\uff0c\u8d8a\u6050\u6016\u8d8a\u597d\uff0c\u7ecf\u5e38\u8bfb\u5f97\u6211\u5fc3\u60ca\u80c6\u6218\uff0c\u665a\u4e0a\u90fd\u4e0d\u6562\u4e00\u4e2a\u4eba\u53bb\u536b\u751f\u95f4\u3002\u201d</p><p>\u201c......\u201d</p><p>\u201c\u518d\u5c31\u662f\u7231\u60c5\u6545\u4e8b\uff0c\u51fa\u8eab\u8d2b\u5bd2\u7684\u7f8e\u4e3d\u5c11\u5973\uff0c\u5728\u5b66\u6821\u91cc\u6bcf\u5929\u88ab\u4e0d\u5b66\u65e0\u672f\u7684\u5bcc\u5bb6\u5b50\u5f1f\u6b3a\u8d1f\uff0c\u4f46\u5929\u957f\u65e5\u4e45\uff0c\u5979\u7ec8\u4e8e\u7528\u5584\u826f\u611f\u5316\u4e86\u5bf9\u65b9\uff1b\u53c8\u6216\u8005\u662f\u5e73\u6c11\u5bb6\u7684\u5c11\u5973\uff0c\u5076\u7136\u83b7\u5f97\u5728\u738b\u5b50\u8eab\u8fb9\u5de5\u4f5c\u7684\u673a\u4f1a\uff0c\u5979\u5929\u771f\u800c\u7b28\u62d9\uff0c\u7ecf\u5e38\u6253\u574f\u4e00\u4e2a\u8336\u676f\u3001\u5f04\u7834\u4e00\u5957\u9910\u5177\uff0c\u6709\u65f6\u5019\u8fd8\u628a\u70ed\u5496\u5561\u6cfc\u5230\u738b\u5b50\u8eab\u4e0a\u4ec0\u4e48\u7684\u2014\u2014\u201d</p><p>\u201c\u7136\u540e\u5979\u88ab\u89e3\u96c7\u4e86\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u662f\uff0c\u738b\u5b50\u7231\u4e0a\u4e86\u5979\uff0c\u4ed6\u4eec\u4e3e\u884c\u4e86\u76db\u5927\u7684\u5a5a\u793c\uff0c\u4ece\u6b64\u8fc7\u4e0a\u5feb\u4e50\u7684\u751f\u6d3b\u3002\u201d</p><p>\u201c\u8fd9\u4f4d\u738b\u5b50\u4e00\u5b9a\u6709\u81ea\u8650\u503e\u5411\u3002\u201d</p><p>\u5410\u69fd\u5f52\u5410\u69fd\uff0c\u743c\u6069\u8fd8\u771f\u6ca1\u60f3\u5230\u6b23\u5e03\u5c45\u7136\u6709\u8fd9\u79cd\u7231\u597d\uff0c\u770b\u6765\u6709\u5fc5\u8981\u56de\u5fc6\u4e00\u4e0b\u4e0a\u8f88\u5b50\u770b\u8fc7\u7684\u90a3\u4e9b\u65e5\u5267\u97e9\u5267\uff0c\u4ee5\u4fbf\u6295\u5176\u6240\u597d\u3002\u81f3\u4e8e\u6050\u6016\u5c0f\u8bf4\u2014\u2014\u8fd9\u4e16\u754c\u4e0a\u771f\u7684\u6709\u80fd\u8ba9\u5979\u611f\u89c9\u6050\u6016\u7684\u9b3c\u6545\u4e8b\u5417\uff1f</p><p>\u51db\u6d17\u6fa1\u53bb\u4e86\uff0c\u5176\u4ed6\u4eba\u5404\u81ea\u56de\u5230\u81ea\u5df1\u623f\u95f4\uff0c\u7fe1\u7fe0\u5973\u5deb\u4e0d\u77e5\u627e\u4e86\u4e2a\u4ec0\u4e48\u89d2\u843d\u5750\u4e0b\uff0c\u81ea\u987e\u81ea\u5730\u5f00\u59cb\u51a5\u60f3\uff0c\u5ba2\u5385\u91cc\u53ea\u7559\u4e0b\u743c\u6069\u548c\u6885\u83f2\u65af\u3002\u4e24\u4eba\u968f\u53e3\u95f2\u804a\uff0c\u770b\u7740\u7a97\u5916\u7684\u96e8\u4f9d\u65e7\u7a00\u91cc\u54d7\u5566\u5730\u4e0b\u4e2a\u4e0d\u505c\uff0c\u6885\u83f2\u65af\u5ffd\u7136\u8bf4\uff1a\u201c\u8fd9\u96e8\u4e0d\u592a\u6b63\u5e38\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u81ea\u4ece\u6211\u4eec\u5230\u4e1c\u57df\uff0c\u5728\u4eca\u5929\u4e4b\u524d\uff0c\u4f60\u8bb0\u5f97\u6709\u4e0b\u8fc7\u96e8\u4e48\uff1f\u201d</p><p>\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u4ece\u62b5\u8fbe\u8f9b\u5df4\u57ce\u5f00\u59cb\u7b97\uff0c\u5c06\u8fd1\u4e24\u4e2a\u6708\uff0c\u5728\u6b64\u671f\u95f4\u7684\u786e\u662f\u4e00\u573a\u96e8\u90fd\u6ca1\u4e0b\u8fc7\uff0c\u5929\u6c14\u6674\u6717\u5f97\u4e00\u584c\u7cca\u6d82\uff0c\u4f46\u8fd9\u4e5f\u8bf4\u660e\u4e0d\u4e86\u4ec0\u4e48\u95ee\u9898\u5427\u3002</p><p>\u201c\u5076\u5c14\u4e0b\u573a\u5c0f\u96e8\u5f88\u6b63\u5e38\uff0c\u8fd9\u6837\u5927\u7684\u96e8\u5c31\u4e0d\u53ef\u80fd\u4e86\uff0c\u8fd9\u662f\u8fdb\u5165\u96e8\u5b63\u624d\u4f1a\u51fa\u73b0\u7684\u60c5\u5f62\u3002\u4f46\u4e1c\u57df\u7684\u96e8\u5b63\u662f\u4ece\u4e03\u6708\u4efd\u4e2d\u65ec\u5f00\u59cb\uff0c\u73b0\u5728\u624d\u521a\u521a\u8fdb\u5165\u516d\u6708\u3002\u201d</p><p>\u743c\u6069\u4f9d\u7136\u4e0d\u89c9\u5f97\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u201c\u5927\u6982\u4eca\u5e74\u96e8\u5b63\u63d0\u524d\u4e86\u5427\u3002\u201d</p><p>\u201c\u4e1c\u57df\u7684\u6c14\u5019\u7a33\u5b9a\uff0c\u5b63\u8282\u5206\u660e\uff0c\u4ece\u6765\u6ca1\u6709\u96e8\u5b63\u63d0\u524d\u6216\u8005\u63a8\u8fdf\u7684\u60c5\u5f62\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u800c\u4e14\uff0c\u6211\u611f\u89c9\u8fd9\u96e8\u91cc\u6709\u4e9b\u4e0d\u4e00\u6837\u7684\u4e1c\u897f\u3002\u201d</p><p>\u4ec0\u4e48\u4e1c\u897f\uff1f\u743c\u6069\u8fd8\u771f\u6ca1\u53d1\u89c9\u3002</p><p>\u201c\u6211\u4e5f\u8bf4\u4e0d\u6e05\u695a\uff0c\u53ea\u662f\u9690\u9690\u89c9\u5f97\u4e0d\u592a\u5bf9\u52b2\u3002\u201d</p><p>\u82e5\u662f\u522b\u4eba\uff0c\u743c\u6069\u8fd8\u53ef\u4ee5\u8ba4\u4e3a\u662f\u795e\u7ecf\u8fc7\u654f\uff0c\u4f46\u65e2\u7136\u662f\u6885\u83f2\u65af\u8fd9\u4e48\u8bf4\uff0c\u90a3\u5c31\u4e0d\u80fd\u4e0d\u8ba4\u771f\u5bf9\u5f85\u4e86\u3002\u8fd9\u573a\u5927\u96e8\u82e5\u975e\u81ea\u7136\u73b0\u8c61\uff0c\u90a3\u5c31\u662f\u4eba\u4e3a\u64cd\u7eb5\uff0c\u7684\u786e\u503c\u5f97\u8b66\u60d5\u3002\u5f3a\u5927\u7684\u5deb\u5e08\u53ef\u4ee5\u5728\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u6539\u53d8\u5929\u6c14\uff0c\u4f46\u80fd\u53ec\u5524\u5982\u6b64\u957f\u65f6\u95f4\u7684\u5927\u66b4\u96e8\uff0c\u8fd9\u9020\u8be3\u53ef\u7740\u5b9e\u60ca\u4eba\uff0c\u522b\u8bf4\u743c\u6069\uff0c\u8fde\u7ef4\u82e5\u62c9\u90fd\u505a\u4e0d\u5230\u3002</p><p>\u96be\u9053\u88ab\u4eba\u7ed9\u76ef\u4e0a\u4e86\uff1f</p><p>\u6709\u624e\u745e\u5c14\u7684\u8b66\u544a\u5728\u5148\uff0c\u743c\u6069\u5df2\u7ecf\u6709\u5145\u5206\u7684\u5fc3\u7406\u51c6\u5907\uff0c\u77e5\u9053\u81ea\u5df1\u4e00\u65e6\u6e21\u6cb3\u8fdb\u5165\u6069\u745f\uff0c\u53ef\u80fd\u5c31\u4f1a\u62db\u60f9\u4e0a\u5404\u79cd\u610f\u60f3\u4e0d\u5230\u7684\u9ebb\u70e6\u2014\u2014\u4f46\u4e5f\u4e0d\u81f3\u4e8e\u8fd9\u4e48\u5feb\u5427\u3002\u800c\u4e14\u5982\u679c\u771f\u6709\u4eba\u5728\u6697\u4e2d\u9488\u5bf9\u81ea\u5df1\uff0c\u5f04\u4e00\u573a\u5927\u96e8\u53c8\u6709\u4ec0\u4e48\u610f\u4e49\uff0c\u8fd9\u70b9\u96e8\u5bf9\u666e\u901a\u4eba\u4f1a\u662f\u5f88\u5927\u7684\u9ebb\u70e6\uff0c\u5bf9\u4ed6\u8fd8\u7b97\u4e0d\u5f97\u4ec0\u4e48\u3002</p><p>\u7b97\u4e86\uff0c\u591a\u60f3\u65e0\u76ca\uff0c\u8d70\u4e00\u6b65\u770b\u4e00\u6b65\u5427\u3002</p><p>\u6309\u7167\u73ca\u5609\u7684\u5206\u914d\uff0c\u743c\u6069\u548c\u7ef4\u82e5\u62c9\u5171\u7528\u540c\u4e00\u4e2a\u623f\u95f4\uff0c\u800c\u4e14\u662f\u6700\u89d2\u843d\u7684\u90a3\u4e00\u95f4\uff0c\u6bd4\u8f83\u9690\u853d\uff0c\u5176\u7528\u610f\u4e0d\u8a00\u800c\u55bb\u3002\u7136\u800c\u5f53\u743c\u6069\u6700\u540e\u4e00\u4e2a\u6d17\u5b8c\u6fa1\uff0c\u63a8\u5f00\u623f\u95f4\u7684\u95e8\uff0c\u7b2c\u4e00\u773c\u770b\u89c1\u7684\u5374\u662f\u838e\u73de\u514b\uff0c\u5979\u548c\u7ef4\u82e5\u62c9\u9762\u5bf9\u9762\u5750\u5728\u5e8a\u4e0a\uff0c\u6b63\u5728\u804a\u5929\uff0c\u4f3c\u4e4e\u5f88\u5f00\u5fc3\u7684\u6a21\u6837\u3002</p><p>\u201c\u4f60\u600e\u4e48\u5728\u8fd9\u91cc\uff1f\u201d\u743c\u6069\u6709\u4e9b\u5947\u602a\u3002</p><p>\u6700\u8fd1\u8fd9\u6bb5\u65f6\u95f4\uff0c\u838e\u73de\u514b\u7684\u786e\u548c\u7ef4\u82e5\u62c9\u8d70\u5f97\u5f88\u8fd1\uff0c\u4e24\u4eba\u7ecf\u5e38\u805a\u5728\u89d2\u843d\u91cc\u7a83\u7a83\u79c1\u8bed\uff0c\u6216\u8005\u8eb2\u5728\u623f\u95f4\u91cc\uff0c\u4e00\u5f85\u5c31\u662f\u5927\u534a\u5929\uff0c\u4e5f\u4e0d\u77e5\u9053\u5728\u5e72\u561b\u3002\u53cd\u6b63\u4e24\u4e2a\u5973\u4eba\u4e5f\u73a9\u4e0d\u51fa\u4ec0\u4e48\u82b1\u6837\uff0c\u5c31\u7b97\u6eda\u5e8a\u5355\u743c\u6069\u4e5f\u65e0\u6240\u8c13\uff0c\u61d2\u5f97\u591a\u8fc7\u95ee\u3002\u4f46\u838e\u73de\u514b\u8fd8\u662f\u5f88\u8bc6\u8da3\u7684\uff0c\u4ece\u6765\u6ca1\u6709\u5230\u4e86\u5c31\u5bdd\u65f6\u95f4\uff0c\u8fd8\u9017\u7559\u5728\u7ef4\u82e5\u62c9\u623f\u95f4\u91cc\u4e0d\u8d70\uff0c\u4eca\u5929\u662f\u53d1\u751f\u4ec0\u4e48\u4e8b\u4e86\uff1f</p><p>\u201c\u6211\u997f\u4e86\u3002\u201d</p><p>\u201c......\u201d</p><p>\u201c\u997f\u5f97\u53d7\u4e0d\u4e86\u4e86\u3002\u201d\u838e\u73de\u514b\u8bf4\uff0c\u4e00\u53cc\u5927\u773c\u775b\u5b9a\u5b9a\u5730\u770b\u7740\u743c\u6069\uff0c\u6ee1\u8138\u65e0\u8f9c\u7684\u795e\u60c5\u3002</p><p>\u201c\u8c01\u8ba9\u4f60\u6311\u98df\u4e0d\u5403\u665a\u9910\uff0c\u201d\u743c\u6069\u968f\u53e3\u8bf4\uff0c\u201c\u6211\u597d\u50cf\u8fd8\u6709\u70b9\u725b\u8089\u5e72\uff0c\u4f60\u8981\u4e0d\u8981\u5148\u57ab\u4e00\u4e0b\uff1f\u5c31\u662f\u6709\u70b9\u786c\uff0c\u56bc\u8d77\u6765\u8d39\u52b2\uff0c\u4e0d\u592a\u597d\u5403\u3002\u201d</p><p>\u201c\u7b28\u86cb\uff0c\u201d\u7ef4\u82e5\u62c9\u770b\u4e0d\u4e0b\u53bb\uff0c\u51fa\u8a00\u63d0\u9192\uff0c\u201c\u5979\u662f\u9b45\u9b54\u3002\u201d</p><p>\u201c\u54e6\uff0c\u5bf9\uff0c\u6211\u5fd8\u4e86\u3002\u201d</p><p>\u9b45\u9b54\uff0c\u53c8\u540d\u5438\u7cbe\u5973\u5996\uff0c\u7537\u4eba\u7684\u7cbe\u6db2\u624d\u662f\u5979\u771f\u6b63\u9700\u8981\u7684\u98df\u7269\u3002\u82e5\u662f\u5728\u4ee5\u524d\u81ea\u7136\u6ca1\u95ee\u9898\uff0c\u743c\u6069\u9694\u4e09\u5dee\u4e94\u5c31\u4f1a\u5c06\u5979\u5582\u5f97\u9971\u9971\u7684\uff0c\u4f46\u5728\u9634\u5f71\u8c37\u65f6\uff0c\u743c\u6069\u4e2d\u4e86\u7ef4\u82e5\u62c9\u7684\u4e13\u60c5\u8bc5\u5492\uff0c\u838e\u73de\u514b\u7684\u98df\u7269\u6765\u6e90\u81ea\u6b64\u65ad\u7edd\u4f9b\u5e94\uff0c\u7b97\u8d77\u6765\u5df2\u7ecf\u6709\u4e24\u4e2a\u591a\u6708\u4e86\uff0c\u4e5f\u96be\u602a\u5979\u8bf4\u997f\u4e86\u3002\u743c\u6069\u4e4b\u524d\u6ca1\u6709\u592a\u6ce8\u610f\uff0c\u5982\u4eca\u4ed4\u7ec6\u770b\uff0c\u53d1\u73b0\u838e\u73de\u514b\u7684\u6c14\u8272\u786e\u5b9e\u4e0d\u592a\u597d\uff0c\u9690\u9690\u6709\u4e9b\u6194\u60b4\u6a21\u6837\uff0c\u4e3e\u6b62\u884c\u52a8\u4e4b\u95f4\u4e5f\u6709\u4e9b\u865a\u5f31\u8fdf\u6ede\uff0c\u4e0d\u4f3c\u4ee5\u524d\u90a3\u6837\u7075\u654f\u4e86\u3002</p><p>\u201c\u9b45\u9b54\u8981\u5b9a\u671f\u5438\u98df\u7537\u6027\u7cbe\u6db2\uff0c\u5426\u5219\u5c31\u4f1a\u53d8\u5f97\u8870\u5f31\uff0c\u5979\u7684\u60c5\u5f62\u53c8\u6bd4\u8f83\u7279\u6b8a\uff0c\u662f\u4ee5\u672c\u4f53\u5728\u7269\u8d28\u754c\uff0c\u9700\u8981\u7684\u2018\u98df\u7269\u2019\u66f4\u591a\uff0c\u73b0\u5728\u5979\u7684\u72b6\u51b5\u5f88\u5dee\uff0c\u5df2\u7ecf\u5feb\u5230\u4e34\u754c\u70b9\u4e86\uff0c\u518d\u8fd9\u6837\u7ee7\u7eed\u4e0b\u53bb\uff0c\u540e\u679c\u4f1a\u5f88\u4e25\u91cd\uff0c\u751a\u81f3\u5bf9\u4f60\u4e5f\u4f1a\u6709\u5f71\u54cd\u2014\u2014\u6bd5\u7adf\u4f60\u4eec\u4e4b\u95f4\u6709\u771f\u540d\u5951\u7ea6\uff0c\u5f7c\u6b64\u662f\u5173\u8054\u7684\u3002\u201d</p><p>\u201c\u90a3\u600e\u4e48\u529e\uff1f\u201d\u743c\u6069\u4e5f\u5f88\u70e6\u607c\uff0c\u4ed6\u4e0d\u662f\u4e0d\u60f3\u5582\u9971\u838e\u73de\u514b\uff0c\u4f46\u6709\u5fc3\u65e0\u529b\u554a\u3002\u9b45\u9b54\u4ee5**\u4e3a\u98df\uff0c\u4f46\u53c8\u4f3c\u4e4e\u4e0d\u662f\u8fd9\u4e48\u7b80\u5355\uff0c\u743c\u6069\u4e5f\u66fe\u7ecf\u8bd5\u8fc7\u81ea\u5df1\u64b8\u51fa\u6765\u5582\u7ed9\u838e\u73de\u514b\uff0c\u5374\u6ca1\u6709\u534a\u70b9\u6548\u679c\u3002\u8fd9\u5176\u4e2d\u4e00\u5b9a\u8fd8\u6709\u4ec0\u4e48\u7ec6\u8282\u95ee\u9898\u6ca1\u641e\u660e\u767d\uff0c\u53ef\u60dc\u838e\u73de\u514b\u5728\u6df1\u6e0a\u91cc\u5f85\u7684\u65f6\u95f4\u592a\u77ed\uff0c\u5f88\u591a\u4e8b\u60c5\u5979\u81ea\u5df1\u90fd\u8bf4\u4e0d\u6e05\u695a\u3002</p><p>\u201c\u5173\u4e8e\u8fd9\u4e2a\u95ee\u9898\uff0c\u7ecf\u8fc7\u6211\u4eec\u8ba4\u771f\u7814\u7a76\uff0c\u6709\u4e86\u521d\u6b65\u7684\u7ed3\u8bba\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u5355\u7eaf\u4f5c\u4e3a\u7269\u8d28\u800c\u5b58\u5728\u7684**\uff0c\u5bf9\u4e8e\u9b45\u9b54\u800c\u8a00\u5e76\u65e0\u4e1d\u6beb\u4ef7\u503c\uff0c\u5979\u6240\u9700\u8981\u7684\uff0c\u662f\u4f5c\u4e3a\u67d0\u79cd\u884c\u4e3a\u7684\u7ed3\u679c\u548c\u8bc1\u660e\uff0c\u56e0\u6b64\u800c\u88ab\u8d4b\u4e88\u7684\u610f\u4e49\uff0c\u624d\u662f\u5979\u771f\u6b63\u7684\u7f8e\u5473\u4f73\u80b4\u3002\u201d</p><p>\u201c......\u6211\u8bfb\u4e66\u5c11\uff0c\u80fd\u4e0d\u80fd\u7528\u66f4\u901a\u4fd7\u6613\u61c2\u4e00\u4e9b\u7684\u8bed\u8a00\u6765\u8bf4\u660e\uff1f\u201d</p><p>\u201c\u5979\u9700\u8981\u7684\u7cbe\u6db2\uff0c\u5fc5\u987b\u662f\u6027\u7231\u7684\u4ea7\u7269\uff0c\u7537\u6027\u5728\u6027\u7231\u4e2d\u8fbe\u5230\u9876\u5cf0\uff0c\u55b7\u51fa\u7684\u751f\u547d\u7cbe\u534e\uff0c\u5bf9\u5979\u800c\u8a00\u624d\u6709\u4ef7\u503c\u3002\u201d</p><p>\u201c\u61c2\u4e86\u3002\u201d</p><p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u9b45\u9b54\u9700\u8981\u7684\u7cbe\u6db2\uff0c\u5fc5\u987b\u662f\u9ad8\u6f6e\u201c\u5236\u9020\u201d\u51fa\u6765\u7684\u3002\u743c\u6069\u81ea\u5df1\u64b8\u51fa\u6765\u7684\uff0c\u5f92\u6709\u5176\u8868\uff0c\u7f3a\u4e4f\u5185\u6db5\uff0c\u6240\u4ee5\u838e\u73de\u514b\u4e0d\u5c51\u4e00\u987e\u3002</p><p>\u8bdd\u8bf4\u5230\u8fd9\u91cc\uff0c\u743c\u6069\u4e5f\u5927\u4f53\u660e\u767d\u4e86\u5979\u4eec\u4e24\u4eba\u7684\u610f\u601d\uff0c\u4e0d\u8fc7\u8fd8\u6709\u6700\u540e\u4e00\u4e2a\u95ee\u9898\u9700\u8981\u786e\u8ba4\u3002\u201c\u53ea\u8981\u662f\u505a\u7231\u800c\u4ea7\u751f\u7684\u7cbe\u6db2\u5c31\u53ef\u4ee5\uff1f\u65e0\u8bba\u5bf9\u8c61\u662f\u4e0d\u662f\u5979\u672c\u4eba\uff1f\u201d</p><p>\u201c\u4e00\u822c\u60c5\u51b5\u4e0b\uff0c\u5f53\u7136\u5bf9\u8c61\u90fd\u662f\u9b45\u9b54\u672c\u4eba\uff0c\u8fd9\u6837\u6548\u679c\u4e5f\u6700\u597d\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u4f46\u6211\u6700\u8fd1\u67e5\u9605\u4e86\u4e00\u4e9b\u8d44\u6599\uff0c\u53d1\u73b0\u6709\u524d\u8f88\u505a\u8fc7\u8fd9\u65b9\u9762\u7684\u7814\u7a76\uff0c\u53d1\u73b0\u4e0e\u9b45\u9b54\u4e4b\u5916\u7684\u4eba<strong>\uff0c</strong>\u5bf9\u9b45\u9b54\u4e5f\u662f\u6709\u6548\u679c\u7684\uff0c\u53ea\u4e0d\u8fc7\u80fd\u91cf\u8f6c\u5316\u6548\u7387\u4f1a\u6bd4\u8f83\u4f4e\uff0c\u6548\u679c\u4f1a\u6253\u6298\u6263\uff0c\u5927\u6982\u662f\u56db\u5206\u4e4b\u4e00\u5230\u4e94\u5206\u4e4b\u4e09\u5de6\u53f3\uff0c\u7531\u4e8e\u6d4b\u8bd5\u5bf9\u8c61\u548c\u8bd5\u9a8c\u6837\u672c\u4e0d\u591f\u591a\uff0c\u6240\u4ee5\u8fd9\u4e2a\u7ed3\u8bba\u4e0d\u662f\u7279\u522b\u7cbe\u786e\uff0c\u4ec5\u4f9b\u53c2\u8003\u3002\u201d</p><p>\u542c\u8d77\u6765\u8fd8\u771f\u662f\u4e00\u672c\u6b63\u7ecf\u7684\u7814\u7a76\u5462\u2014\u2014\u8bdd\u8bf4\u8c01\u5403\u9971\u4e86\u6491\u7740\uff0c\u4f1a\u505a\u8fd9\u79cd\u5947\u8469\u7684\u7814\u7a76\u554a\uff1f\u96be\u9053\u90a3\u4f4d\u524d\u8f88\u4e5f\u662f\u906d\u9047\u4e86\u548c\u743c\u6069\u4e00\u6837\u7684\u95ee\u9898\u5417\uff1f</p><p>\u201c\u90a3\u5012\u4e0d\u662f\uff0c\u4ed6\u5c31\u662f\u51fa\u4e8e\u5355\u7eaf\u7684\u5b66\u672f\u7814\u7a76\u76ee\u7684\u2014\u2014\u5b9e\u9645\u4e0a\uff0c\u6211\u524d\u9762\u8bf4\u7684\u8fd9\u4e9b\u53ea\u662f\u7814\u7a76\u8fc7\u7a0b\u4e2d\u7684\u526f\u4ea7\u54c1\uff0c\u4ed6\u771f\u6b63\u5173\u5fc3\u7684\u5185\u5bb9\u4e0d\u662f\u8fd9\u4e2a\u3002\u201d</p><p>\u201c\u90a3\u4ed6\u771f\u6b63\u60f3\u7814\u7a76\u7684\u662f\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u597d\u5947\u3002</p><p>\u201c\u7537\u4eba\u548c\u7537\u4eba\u505a\u7231\u800c\u4ea7\u751f\u7684\u7cbe\u6db2\uff0c\u5bf9\u9b45\u9b54\u7684\u4f5c\u7528\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u4ed6\u662f\u4e2a\u540c\u6027\u604b\u3002\u201d</p><p>\u201c......\u201d\u743c\u6069\u679c\u65ad\u8df3\u8fc7\u8fd9\u4e2a\u8bdd\u9898\uff0c\u201c\u4f60\u521a\u624d\u8bf4\u4f60\u67e5\u9605\u8d44\u6599\uff0c\u4ec0\u4e48\u8d44\u6599\u4f1a\u8bb0\u8f7d\u8fd9\u4e9b\u4e1c\u897f\uff1f\u201d</p><p>\u201c\u7c89\u7ea2\u4e4b\u4e66\u3002\u201d</p><p>\u201c\u7c89\u7ea2\u4e4b\u4e66\u6211\u4e5f\u5728\u70db\u5821\u770b\u8fc7\u554a\uff0c\u4e0d\u8bb0\u5f97\u6709\u8fd9\u79cd\u5185\u5bb9\u3002\u201d</p><p>\u201c\u4f60\u80fd\u770b\u5230\u7684\u80af\u5b9a\u662f\u7b80\u5316\u7248\u672c\uff0c\u91cc\u9762\u4e00\u5806\u2018\u6b64\u5904\u4e0d\u5b9c\u63cf\u8ff0\u2019\u5bf9\u5427\uff1f\u201d\u7ef4\u82e5\u62c9\u4e0d\u5c51\uff0c\u201c\u6211\u624b\u91cc\u7684\u662f\u65e0\u5220\u8282\u7248\u3002\u201d</p><p>\u6211\u80fd\u4e0d\u80fd\u6c42\u4e00\u4efd\u65e0\u5220\u8282\u7248\uff1f</p><p>\u743c\u6069\u7684\u8fd9\u4e2a\u8bf7\u6c42\u88ab\u6b8b\u5fcd\u62d2\u7edd\u4e86\uff0c\u7ef4\u82e5\u62c9\u8868\u793a\u5979\u624b\u4e0a\u90a3\u4efd\u4e5f\u662f\u4ece\u70db\u5821\u590d\u5236\u8fc7\u6765\u7684\uff0c\u5c5e\u4e8e\u673a\u5bc6\u8d44\u6599\uff0c\u6839\u636e\u5979\u548c\u70db\u5821\u7684\u534f\u8bae\uff0c\u53ea\u9650\u4e8e\u81ea\u5df1\u9605\u8bfb\uff0c\u4e0d\u80fd\u8ba9\u4e0d\u76f8\u5e72\u7684\u4eba\u770b\u3002</p><p>\u201c\u6211\u4e5f\u4e0d\u80fd\u7b97\u4e0d\u76f8\u5e72\u7684\u4eba\u554a\uff0c\u201d\u743c\u6069\u6781\u529b\u4e89\u53d6\uff0c\u201c\u6211\u4eec\u73b0\u5728\u9a6c\u9a6c\u864e\u864e\u4e5f\u7b97\u662f\u60c5\u4fa3\u5427\u3002\u201d</p><p>\u201c\u5373\u4fbf\u5982\u6b64\u4e5f\u4e0d\u884c\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u9664\u4e86\u7981\u6b62\u65e0\u5173\u4eba\u58eb\u63a5\u89e6\u4e4b\u5916\uff0c\u70db\u5821\u7684\u89c4\u5219\u8fd8\u6709\u4e00\u6761\uff1a\u4e0d\u80fd\u8ba9\u65e0\u5220\u8282\u7248\u7684\u7c89\u7ea2\u4e4b\u4e66\u843d\u5165\u90aa\u6076\u4e4b\u5f92\u624b\u4e2d\uff0c\u8fd9\u662f\u4e3a\u4e86\u5e7f\u5927\u5973\u6027\u7684\u5b89\u5168\u548c\u798f\u7949\u8003\u8651\u3002\u201d</p><p>\u201c\u8fd9\u89c4\u5b9a\u5f88\u5408\u7406\u2014\u2014\u7b49\u7b49\uff0c\u4f60\u628a\u8bdd\u8bf4\u6e05\u695a\uff0c\u6211\u54ea\u91cc\u90aa\u6076\u4e86\uff1f\u201d</p><p>\u743c\u6069\u5f88\u4e0d\u6ee1\uff0c\u5c31\u8fd9\u4e2a\u8bdd\u9898\u548c\u7ef4\u82e5\u62c9\u8fdb\u884c\u4e86\u6df1\u5165\u7684\u63a2\u8ba8\uff0c\u76f4\u5230\u5973\u5deb\u5e08\u8fde\u8fde\u6c42\u9976\uff0c\u627f\u8ba4\u9519\u8bef\u3002</p><p>\uff08\u7531\u4e8e\u4f17\u6240\u5468\u77e5\u7684\u539f\u56e0\u4e0d\u53ef\u63cf\u8ff0\uff09</p><p>\u201c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u98df\u7269\u95ee\u9898\u603b\u7b97\u662f\u89e3\u51b3\u4e86\uff0c\u201d\u838e\u73de\u514b\u5927\u5927\u5730\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u770b\u8d77\u6765\u795e\u6e05\u6c14\u723d\uff0c\u5bb9\u5149\u7115\u53d1\uff0c\u548c\u4e4b\u524d\u65e0\u7cbe\u6253\u91c7\u7684\u6a21\u6837\u5b8c\u5168\u5224\u82e5\u4e24\u4eba\uff0c\u5982\u679c\u5ffd\u7565\u5979\u8d64\u88f8\u7684\u8eab\u4f53\u548c\u5634\u89d2\u7684\u767d\u8272\u6db2\u4f53\uff0c\u5c31\u662f\u4e2a\u521a\u521a\u5403\u5b8c\u5927\u9910\uff0c\u5143\u6c14\u5341\u8db3\u7684\u7f8e\u5c11\u5973\uff0c\u201c\u90a3\u4e48\uff0c\u4ee5\u540e\u5c31\u8981\u7ecf\u5e38\u6253\u6270\u4e86\uff0c\u4e3b\u4eba\u4e0d\u4f1a\u4ecb\u610f\u5427\u3002\u201d</p><p>\u743c\u6069\u5012\u662f\u65e0\u6240\u8c13\u4e86\uff0c\u5c31\u4e0d\u77e5\u9053\u7ef4\u82e5\u62c9\u600e\u4e48\u60f3\uff0c\u4e0d\u8fc7\u770b\u5979\u4eca\u5929\u7684\u6837\u5b50\uff0c\u5e94\u8be5\u4e5f\u4e0d\u662f\u5f88\u4ecb\u610f\u3002</p><p>\u5de6\u62e5\u53f3\u62b1\u7740\u4e24\u4f4d\u7f8e\u4eba\u513f\uff0c\u53c8\u5145\u5206\u53d1\u6cc4\u8fc7\uff0c\u743c\u6069\u7761\u5f97\u5f88\u9999\u3002\u53ef\u60dc\u4ed6\u521b\u9020\u51fa\u6765\u7684\u623f\u5b50\u8bbe\u65bd\u6709\u70b9\u5dee\uff0c\u5367\u5ba4\u91cc\u6ca1\u6709\u7a97\u6237\uff0c\u4e5f\u7f3a\u4e4f\u901a\u98ce\u7cfb\u7edf\uff0c\u66f4\u6ca1\u6709\u6e29\u5ea6\u8c03\u63a7\u8bbe\u7f6e\uff0c\u7761\u5230\u65e9\u4e0a\u5c31\u88ab\u70ed\u9192\u4e86\u3002\u623f\u95f4\u91cc\u9ed1\u6c89\u6c89\u7684\uff0c\u4e5f\u4e0d\u77e5\u9053\u5916\u9762\u5929\u6c14\u5982\u4f55\uff0c\u4e0d\u8fc7\u542c\u52a8\u9759\uff0c\u6599\u60f3\u96e8\u5e94\u8be5\u5df2\u7ecf\u505c\u4e86\u3002\u743c\u6069\u722c\u8d77\u6765\uff0c\u4ece\u5e8a\u5934\u62ff\u8fc7\u6c99\u6f0f\u770b\u4e86\u770b\uff0c\u5927\u6982\u662f\u4e94\u70b9\u591a\u949f\uff0c\u8fd8\u4e0d\u5230\u516d\u70b9\u3002</p><p>\u201c\u6709\u70b9\u65e9\u554a\u3002\u201d\u4ed6\u5600\u5495\u4e86\u4e00\u53e5\u3002</p><p>\u6628\u591c\u5dee\u4e0d\u591a\u6298\u817e\u5230\u4e24\u70b9\u534a\uff0c\u7b97\u8d77\u6765\u624d\u7761\u4e86\u4e09\u4e2a\u5c0f\u65f6\uff0c\u773c\u775b\u8fd8\u6709\u4e9b\u9178\u6da9\uff0c\u5934\u4e5f\u6709\u70b9\u6655\u3002\u4ed6\u5b9a\u4e86\u5b9a\u795e\uff0c\u53d1\u73b0\u7ef4\u82e5\u62c9\u8fd8\u5728\u65c1\u8fb9\uff0c\u8737\u7740\u8eab\u5b50\uff0c\u76d6\u7740\u6bdb\u6bef\uff0c\u838e\u73de\u514b\u5374\u6ca1\u4eba\u5f71\u4e86\uff0c\u4e0d\u77e5\u9053\u53bb\u4e86\u54ea\u91cc\u3002</p><p>\u627e\u6761\u6bdb\u5dfe\u64e6\u4e86\u64e6\u6c57\uff0c\u628a\u81ea\u5df1\u5f04\u5e72\u723d\u70b9\uff0c\u743c\u6069\u4e00\u65f6\u95f4\u4e5f\u6ca1\u4e86\u7761\u610f\uff0c\u968f\u624b\u6478\u4e86\u6478\u65c1\u8fb9\u7684\u5973\u5deb\u5e08\uff0c\u89e6\u624b\u5fae\u51c9\u3002\u5979\u6709\u4e2a\u7279\u70b9\uff0c\u4e00\u65e6\u7761\u7740\uff0c\u4f53\u6e29\u5c31\u4f1a\u8d8a\u6765\u8d8a\u4f4e\uff0c\u6bcf\u5929\u65e9\u4e0a\u9192\u6765\u4e4b\u524d\u7684\u4f53\u6e29\u662f\u6700\u4f4e\u7684\uff0c\u743c\u6069\u4e5f\u662f\u548c\u5979\u76f8\u5904\u4e45\u4e86\u624d\u53d1\u73b0\u8fd9\u70b9\uff0c\u5374\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u7f18\u6545\uff0c\u53ef\u80fd\u5404\u4eba\u4f53\u8d28\u4e0d\u540c\u3002</p><p>\uff08\u4f9d\u7136\u662f\u7531\u4e8e\u4f17\u6240\u5468\u77e5\u7684\u539f\u56e0\u4e0d\u53ef\u63cf\u8ff0\uff09</p><p>\u65e9\u4e0a\u8fd0\u52a8\u5b8c\u6bd5\uff0c\u795e\u6e05\u6c14\u723d\uff0c\u743c\u6069\u7a7f\u597d\u8863\u670d\uff0c\u968f\u53e3\u95ee\u9b45\u9b54\uff1a\u201c\u4e00\u5927\u65e9\u4f60\u53bb\u54ea\u4e86\uff1f\u201d</p><p>\u201c\u6211\u53c8\u4e0d\u7528\u7761\u89c9\uff0c\u968f\u4fbf\u51fa\u53bb\u8f6c\u8f6c\uff0c\u547c\u5438\u4e00\u4e0b\u65b0\u9c9c\u7a7a\u6c14\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u4e0d\u662f\u8bf4\u8ff7\u5931\u68ee\u6797\u91cc\u6709\u6811\u7cbe\u5417\uff0c\u6211\u770b\u770b\u80fd\u4e0d\u80fd\u9047\u5230\uff0c\u6293\u51e0\u53ea\u6765\u7ed9\u4f60\u5c1d\u5c1d\u9c9c\u3002\u201d</p><p>\u201c\u5c1d\u9c9c\u4e2a\u9b3c\u554a\uff0c\u201d\u743c\u6069\u6ca1\u597d\u6c14\u5730\u8bf4\uff0c\u201c\u6211\u73b0\u5728\u7684\u72b6\u51b5\u4f60\u53c8\u4e0d\u662f\u4e0d\u77e5\u9053\u3002\u201d</p><p>\u201c\u90a3\u53ef\u4e0d\u4e00\u5b9a\uff0c\u6811\u7cbe\u53c8\u4e0d\u662f\u51e1\u4eba\u3002\u201d</p><p>\u201c\u4f60\u8fd8\u662f\u90aa\u9b54\u5462\uff0c\u6211\u4e0d\u662f\u4e00\u6837\u4e0d\u80fd\u5403\u3002\u201d</p><p>\u201c\u4e0d\u80fd\u5403\uff0c\u770b\u770b\u4e5f\u597d\u561b\uff0c\u4f60\u4e0d\u8981\u90a3\u4e48\u529f\u5229\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u6bd4\u5982\u4e00\u4e2a\u8eab\u6750\u706b\u8fa3\u524d\u51f8\u540e\u7fd8\u957f\u817f\u5927\u80f8\u8fd8\u7a7f\u7740\u7d27\u8eab\u76ae\u8863\u7684\u6f02\u4eae\u6811\u7cbe\uff0c\u5c31\u7b97\u662f\u5355\u7eaf\u770b\u7740\uff0c\u4e5f\u4f1a\u8ba9\u4eba\u5fc3\u60c5\u6109\u5feb\u554a\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u6709\u4eba\u5728\u7aa5\u89c6\u6211\u4eec\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u4e00\u4e2a\u5973\u4eba\uff0c\u80cc\u7740\u5f13\u7bad\uff0c\u8eab\u624b\u975e\u5e38\u7075\u6d3b\u3002\u201d</p><p>\u201c\u4f60\u6ca1\u6293\u4f4f\u5979\uff1f\u201d</p><p>\u9b45\u9b54\u6447\u6447\u5934\uff0c\u201c\u5979\u6bd4\u6211\u719f\u6089\u5730\u5f62\uff0c\u8dd1\u6389\u4e86\u3002\u201d</p><p>\u743c\u6069\u6c89\u541f\u7247\u523b\uff0c\u201c\u51fa\u53bb\u770b\u770b\u3002\u201d</p><p>\u8d70\u51fa\u623f\u5b50\uff0c\u5929\u5df2\u7ecf\u653e\u6674\u4e86\uff0c\u5730\u9762\u8fd8\u6709\u4e9b\u6f6e\u6e7f\uff0c\u743c\u6069\u770b\u5230\u4e86\u4e00\u4e9b\u6d45\u6d45\u7684\u811a\u5370\uff0c\u4f46\u5947\u602a\u7684\u662f\uff0c\u811a\u5370\u4f3c\u4e4e\u662f\u540c\u4e00\u4e2a\u4eba\u7684\uff0c\u800c\u4e14\u662f\u4ece\u623f\u5b50\u91cc\u51fa\u6765\uff0c\u7ed5\u7740\u623f\u5b50\uff0c\u4eff\u4f5b\u6f2b\u65e0\u76ee\u7684\u5730\u95f2\u901b\uff0c\u7136\u540e\u901a\u5411\u524d\u65b9\uff0c\u5728\u4e2d\u9014\u7ecf\u8fc7\u4e00\u9897\u5927\u6811\u65f6\u7a81\u7136\u6d88\u5931\u4e86\u3002\u201c\u8fd9\u662f\u6211\u7684\u811a\u5370\u3002\u201d\u838e\u73de\u514b\u89e3\u91ca\u3002</p><p>\u201c\u5979\u5462\uff1f\u201d</p><p>\u201c\u5979\u5f53\u65f6\u5c31\u7ad9\u5728\u8fd9\u9897\u6811\u4e0a\uff0c\u201d\u838e\u73de\u514b\u6307\u4e86\u6307\uff0c\u201c\u6211\u60f3\u4e58\u5176\u4e0d\u5907\u6293\u4f4f\u5979\uff0c\u4f46\u5979\u975e\u5e38\u8b66\u89c9\uff0c\u6211\u521a\u521a\u4e00\u52a8\uff0c\u5979\u5c31\u9003\u8dd1\u4e86\u3002\u201d</p><p>\u201c\u80fd\u770b\u51fa\u6765\u5386\u5417\uff1f\u201d</p><p>\u201c\u5979\u6234\u7740\u515c\u5e3d\uff0c\u6211\u8fde\u5979\u7684\u8138\u90fd\u6ca1\u770b\u6e05\uff0c\u53ea\u80fd\u80af\u5b9a\u4e0d\u662f\u6211\u4eec\u8ba4\u8bc6\u7684\u4eba\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u743c\u6069\u770b\u4e86\u4e00\u5708\uff0c\u9664\u4e86\u838e\u73de\u514b\u6240\u8bf4\u7684\u4e4b\u5916\uff0c\u4e5f\u6ca1\u6709\u53d1\u73b0\u66f4\u591a\u75d5\u8ff9\u3002\u56de\u5230\u623f\u5b50\u91cc\uff0c\u5176\u4ed6\u4eba\u4e5f\u90fd\u8d77\u5e8a\u4e86\uff0c\u743c\u6069\u628a\u838e\u73de\u514b\u7684\u53d1\u73b0\u8bf4\u4e86\u4e00\u4e0b\uff0c\u5927\u5bb6\u8c08\u4e86\u51e0\u53e5\uff0c\u7531\u4e8e\u7ebf\u7d22\u592a\u5c11\uff0c\u4e5f\u5c31\u65e0\u6cd5\u6df1\u7a76\u3002\u6574\u7406\u884c\u88c5\uff0c\u7ee7\u7eed\u4e0a\u8def\u3002</p><p>\u4ed6\u4eec\u6b64\u65f6\u5df2\u7ecf\u8fdb\u5165\u4e86\u8ff7\u5931\u68ee\u6797\uff0c\u6b63\u4ece\u8fb9\u7f18\u5411\u4e2d\u5fc3\u8fdb\u53d1\u3002\u8d8a\u5f80\u91cc\u8d70\uff0c\u6811\u6728\u8d8a\u6765\u8d8a\u9ad8\u5927\u8302\u5bc6\uff0c\u6811\u51a0\u5c06\u5929\u7a7a\u5b8c\u5168\u906e\u853d\uff0c\u9633\u5149\u6e10\u6e10\u90fd\u900f\u4e0d\u4e0b\u6765\uff0c\u5e78\u597d\u743c\u6069\u65e9\u6709\u51c6\u5907\uff0c\u63d0\u524d\u5236\u4f5c\u4e86\u4e00\u4e9b\u50a8\u5b58\u4e86\u95ea\u5149\u9b54\u6cd5\u7684\u900f\u660e\u7403\u4f53\uff0c\u60ac\u6d6e\u5728\u7a7a\u6c14\u4e2d\uff0c\u7167\u4eae\u5468\u56f4\u3002</p><p>\u8d70\u4e86\u5927\u7ea6\u4e24\u4e2a\u5c0f\u65f6\uff0c\u5ffd\u7136\u542c\u89c1\u6f7a\u6f7a\u6c34\u58f0\uff0c\u663e\u7136\u9644\u8fd1\u6709\u6eaa\u6d41\uff0c\u6240\u6709\u4eba\u90fd\u662f\u7cbe\u795e\u4e00\u632f\u3002\u68ee\u6797\u91cc\u65c5\u884c\uff0c\u6700\u6015\u7684\u662f\u8ff7\u8def\uff0c\u5468\u56f4\u653e\u773c\u6240\u53ca\u90fd\u662f\u6811\u6728\uff0c\u957f\u5f97\u90fd\u5dee\u4e0d\u591a\uff0c\u5f88\u5bb9\u6613\u628a\u4eba\u5f04\u5f97\u6655\u5934\u8f6c\u5411\uff0c\u65e0\u6cd5\u51c6\u786e\u5224\u65ad\u81ea\u5df1\u7684\u65b9\u4f4d\u3002\u4f46\u6765\u4e4b\u524d\u838e\u73de\u514b\u8be2\u95ee\u66fe\u7ecf\u6765\u8fc7\u8ff7\u5931\u68ee\u6797\u7684\u4eba\uff0c\u90fd\u53cd\u590d\u786e\u8ba4\u4e00\u70b9\uff1a\u53ea\u8981\u627e\u5230\u6cb3\uff0c\u6cbf\u7740\u6c34\u6d41\u9006\u884c\u800c\u4e0a\uff0c\u4fbf\u80fd\u627e\u5230\u96c5\u5c14\u8d1d\u7433\u5a1c\u57ce\u3002</p><p>\u68ee\u6797\u91cc\u7684\u9053\u8def\u5e76\u4e0d\u597d\u8d70\uff0c\u4e2d\u9014\u53c8\u906d\u9047\u4e86\u4e00\u573a\u7a81\u5982\u5176\u6765\u7684\u66b4\u96e8\uff0c\u743c\u6069\u4e0d\u5f97\u4e0d\u53ec\u5524\u51fa\u6728\u5c4b\u8ba9\u4f17\u4eba\u8eb2\u907f\u3002\u5e78\u597d\u96e8\u4e0b\u5f97\u5e76\u4e0d\u4e45\uff0c\u5927\u7ea6\u4e24\u4e2a\u5c0f\u65f6\u4fbf\u505c\u6b47\u3002\u5c31\u8fd9\u6837\u8d70\u8d70\u505c\u505c\uff0c\u5f53\u5927\u5bb6\u90fd\u611f\u5230\u975e\u5e38\u75b2\u5026\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u5230\u4e86\u508d\u665a\u3002</p><p>\u201c\u627e\u5230\u4e86\u3002\u201d\u838e\u73de\u514b\u8bf4\u3002</p><p>\u5979\u5728\u4e00\u68f5\u6811\u524d\u89c2\u5bdf\u4e86\u7247\u523b\uff0c\u626f\u5f00\u4e0a\u9762\u7f20\u7ed5\u7684\u8513\u85e4\u679d\u53f6\uff0c\u9732\u51fa\u6811\u5e72\u4e0a\u523b\u7740\u7684\u4e00\u4e2a\u5947\u602a\u7684\u7b26\u53f7\uff0c\u770b\u8d77\u6765\u6709\u4e9b\u50cf\u9e1f\uff0c\u53c8\u50cf\u662f\u62d6\u7740\u957f\u957f\u5c3e\u5df4\u7684\u9c7c\u3002\u636e\u8bf4\u8fd9\u662f\u4e00\u79cd\u53e4\u8001\u7684\u6587\u5b57\uff0c\u743c\u6069\u5f53\u7136\u4e0d\u61c2\uff0c\u4f46\u4ed6\u77e5\u9053\u542b\u4e49\uff0c\u662f\u8868\u793a\u201c\u8fd9\u91cc\u5f88\u5b89\u5168\uff0c\u53ef\u4ee5\u6816\u606f\u201d\u7684\u610f\u601d\u3002\u53ea\u8981\u5728\u6b64\u5bbf\u8425\uff0c\u4fbf\u4e0d\u4f1a\u62c5\u5fc3\u88ab\u91ce\u517d\u88ad\u51fb\u3002</p><p>\u8c6a\u5b85\u672f\u5e76\u4e0d\u662f\u5f88\u5e38\u7528\u7684\u6cd5\u672f\uff0c\u743c\u6069\u4eca\u5929\u53ea\u51c6\u5907\u4e86\u4e00\u4e2a\uff0c\u9014\u4e2d\u5df2\u7ecf\u7528\u8fc7\u3002\u597d\u5728\u5929\u6c14\u5df2\u7ecf\u53d8\u6674\uff0c\u591c\u7a7a\u4e2d\u661f\u5149\u707f\u70c2\uff0c\u91ce\u8425\u4e5f\u4e0d\u662f\u95ee\u9898\u3002\u5927\u5bb6\u4ece\u5305\u88f9\u91cc\u53d6\u51fa\u5e10\u7bf7\uff0c\u642d\u5efa\u8d77\u6765\uff0c\u7136\u540e\u5728\u4e2d\u95f4\u7a7a\u5730\u4e0a\u5347\u8d77\u706b\u5806\uff0c\u665a\u9910\u4e4b\u540e\uff0c\u4e00\u65f6\u65e0\u4e8b\u53ef\u505a\uff0c\u7761\u89c9\u8fd8\u5acc\u592a\u65e9\uff0c\u51db\u4fbf\u8981\u6c42\u743c\u6069\u8bb2\u6545\u4e8b\u3002</p><p>\u201c\u8bb2\u6545\u4e8b\uff1f\u201d</p><p>\u201c\u5bf9\u554a\uff0c\u5c31\u8bb2\u8bb2\u4f60\u548c\u827e\u5f25\u8587\u7684\u604b\u7231\u7ecf\u5386\u597d\u4e86\u3002\u201d</p><p>\u201c......\u8bb2\u6545\u4e8b\u591a\u6ca1\u610f\u601d\uff0c\u4e0d\u5982\u6211\u4eec\u6765\u73a9\u6e38\u620f\u5427\u3002\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u6709\u8da3\u7684\u6e38\u620f\u554a\uff0c\u201d\u51db\u62b1\u6028\uff0c\u201c\u597d\u65e0\u804a\u3002\u201d</p><p>\u743c\u6069\u53ea\u80fd\u611f\u6168\u8fd9\u4e2a\u4e16\u754c\u6ca1\u6709\u667a\u80fd\u624b\u673a\uff0c\u5426\u5219\u4e00\u4eba\u53d1\u4e00\u53ea\uff0c\u5237\u5237\u5fae\u535a\u770b\u770b\u670b\u53cb\u5708\uff0c\u4fdd\u8bc1\u4e00\u70b9\u90fd\u4e0d\u4f1a\u89c9\u5f97\u65e0\u804a\u3002\u4ed6\u52aa\u529b\u56de\u5fc6\u4e86\u4e00\u4e0b\uff0c\u81ea\u5df1\u201c\u524d\u4e16\u201d\u4e5f\u6709\u8fc7\u8fd9\u79cd\u548c\u4e00\u7fa4\u670b\u53cb\u91ce\u8425\u7684\u7ecf\u5386\uff0c\u5f53\u65f6\u5927\u5bb6\u5403\u9971\u4e86\u6491\u7740\u90fd\u5e72\u4ec0\u4e48\u5462\uff1f\u597d\u50cf\u5728\u73a9\u684c\u6e38......\u6709\u4e86\uff01</p><p>\u201c\u6211\u4eec\u6765\u73a9\u72fc\u4eba\u6740\u5427\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u72fc\u4eba\u6740\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5f85\u4f1a\u4f60\u5c31\u77e5\u9053\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u8fc7\u4e03\u4e2a\u4eba\u5c11\u4e86\u70b9\u3002\u201d</p><p>\u201c\u6211\u4eec\u6709\u516b\u4e2a\u4eba\u554a\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u8fd8\u6709\u742a\u96c5\u5462\u3002\u201d</p><p>\u201c\u54e6\uff0c\u5bf9\uff0c\u603b\u662f\u628a\u5979\u5fd8\u4e86\u3002\u201d</p><p>\u9ed1\u732b\u4ece\u73ca\u5609\u8eab\u65c1\u7a9c\u51fa\u6765\uff0c\u5750\u5728\u706b\u5806\u8fb9\uff0c\u53d8\u6210\u732b\u5973\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002</p><p>\u201c\u516b\u4e2a\u4eba\u8fd8\u662f\u5c11\u4e86\u70b9\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e5d\u4e2a\u4eba\u5012\u8fd8\u9a6c\u9a6c\u864e\u864e\u80fd\u73a9\u3002\u201d</p><p>\u201c\u90a3\u5c31\u518d\u52a0\u6211\u4e00\u4e2a\u5427\uff0c\u201d\u5b9d\u77f3\u9f99\u51b0\u8679\u7684\u58f0\u97f3\u5ffd\u7136\u4ece\u743c\u6069\u8170\u95f4\u7684\u77ed\u5251\u4e2d\u4f20\u51fa\uff0c\u201c\u597d\u50cf\u5f88\u6709\u8da3\u7684\u6837\u5b50\u3002\u201d</p><p>\u201c\u4f60\u8001\u4eba\u5bb6\u4e5f\u8981\u53c2\u52a0\uff1f\u201d</p><p>\u201c\u600e\u4e48\uff0c\u77a7\u4e0d\u8d77\u6211\u554a\uff0c\u201d\u9f99\u5f88\u4e0d\u60a6\uff0c\u542c\u8bed\u6c14\u5c31\u80fd\u60f3\u8c61\u5b83\u6b63\u5728\u5439\u80e1\u5b50\u77aa\u773c\uff0c\u201c\u544a\u8bc9\u4f60\uff0c\u5c0f\u5bb6\u4f19\uff0c\u6211\u5e74\u8f7b\u7684\u65f6\u5019\u53ef\u662f\u73a9\u6e38\u620f\u7684\u9ad8\u624b\uff0c\u65e0\u8bba\u662f\u8e22\u6bfd\u5b50\u3001\u6349\u8ff7\u85cf\u8fd8\u662f\u7ffb\u8ddf\u5934\uff0c\u65b9\u5706\u767e\u91cc\u4e4b\u5185\u6ca1\u6709\u6211\u7684\u5bf9\u624b\u3002\u201d</p><p>\u201c......\u5931\u656c\uff0c\u4f46\u6211\u4eec\u63a5\u4e0b\u6765\u8981\u73a9\u7684\u6e38\u620f\u4e0d\u662f\u8e22\u6bfd\u5b50\u6349\u8ff7\u85cf\u6216\u8005\u7ffb\u8ddf\u5934\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e3b\u8981\u662f\u9760\u667a\u529b\uff0c\u4ee5\u53ca\u89c2\u5bdf\u529b\u3002\u201d</p><p>\u201c\u90a3\u5c31\u66f4\u6ca1\u95ee\u9898\u5566\uff0c\u201d\u9f99\u5e95\u6c14\u5341\u8db3\u5730\u8bf4\uff0c\u201c\u5c31\u8ba9\u4f60\u89c1\u8bc6\u89c1\u8bc6\u4ec0\u4e48\u662f\u957f\u8005\u7684\u667a\u6167\u3002\u201d</p><p>\u201c\u597d\u5427\u3002\u201d</p><p>\u743c\u6069\u5fcd\u4f4f\u7b11\uff0c\u8ba9\u838e\u73de\u514b\u4ece\u65c1\u8fb9\u7684\u6811\u4e0a\u5207\u4e86\u4e00\u5927\u5757\u6728\u5934\uff0c\u524a\u6210\u4e5d\u5757\u5df4\u638c\u5927\u5c0f\u7684\u957f\u65b9\u5f62\u6728\u724c\uff0c\u7136\u540e\u95ed\u76ee\u51a5\u60f3\u7247\u523b\uff0c\u6325\u4e86\u6325\u624b\uff0c\u4e5d\u5757\u6728\u724c\u7684\u8868\u9762\u5149\u8292\u95ea\u70c1\uff0c\u6162\u6162\u6d6e\u73b0\u51fa\u4e0d\u540c\u7684\u56fe\u6848\u6765\u3002</p><p>\u201c\u597d\u4e86\uff0c\u6bcf\u4e2a\u4eba\u968f\u673a\u53d6\u4e00\u5757\uff0c\u7b2c\u4e00\u5c40\u6211\u6765\u5f53\u4e3b\u6301\u8005\u3002\u73b0\u5728\u4ecb\u7ecd\u4e00\u4e0b\u6e38\u620f\u89c4\u5219\uff1a\u4ece\u524d\uff0c\u6709\u4e00\u4e2a\u6751\u5e84\uff0c\u6751\u6c11\u91cc\u6f5c\u4f0f\u7740\u72fc\u4eba\uff0c\u767d\u5929\u5b83\u4eec\u4f2a\u88c5\u6210\u6751\u6c11\uff0c\u6bcf\u5230\u591c\u5e55\u964d\u4e34\uff0c\u5b83\u4eec\u5c31\u4f1a\u53d8\u8eab\u6210\u72fc\uff0c\u730e\u6740\u4e00\u4e2a\u6751\u6c11\u2014\u2014\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u53ea\u730e\u6740\u4e00\u4e2a\uff1f\u201d\u51db\u4e3e\u624b\u63d0\u95ee\uff0c\u201c\u4e00\u6b21\u6740\u5149\u4e0d\u597d\u4e48\u3002\u201d</p><p>\u201c\u89c4\u5219\u5982\u6b64\uff0c\u201d\u743c\u6069\u63a5\u7740\u8bf4\uff0c\u201c\u800c\u6bcf\u4e2a\u767d\u5929\uff0c\u6751\u6c11\u4eec\u5c31\u8981\u5728\u4e00\u8d77\u5546\u8bae\uff0c\u5206\u6790\u8c01\u662f\u72fc\u4eba\uff0c\u5e76\u5c06\u90a3\u4e2a\u88ab\u8ba4\u5b9a\u662f\u72fc\u4eba\u7684\u5bb6\u4f19\u8d76\u51fa\u6751\u5e84\uff0c\u653e\u9010\u8352\u91ce\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u6211\u4e0d\u7406\u89e3\uff0c\u201d\u51db\u53c8\u63d0\u95ee\uff0c\u201c\u5982\u679c\u8ba4\u5b9a\u4e86\u4e00\u4e2a\u4eba\u662f\u72fc\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u540a\u6b7b\uff0c\u5374\u8981\u8d76\u51fa\u6751\u5e84\uff1f\u90a3\u591c\u91cc\u72fc\u4eba\u53c8\u8dd1\u56de\u6765\u6740\u4eba\u600e\u4e48\u529e\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u72fc\u4eba\u90fd\u5f88\u6709\u8282\u64cd\uff0c\u4e00\u65e6\u88ab\u8bc6\u7834\u8eab\u4efd\uff0c\u88ab\u653e\u9010\u4e86\uff0c\u5c31\u4e0d\u4f1a\u518d\u56de\u6751\u5e84\u7ee7\u7eed\u6740\u4eba\u3002\u201d</p><p>\u201c\u8fd9\u4e0d\u53ef\u80fd\uff0c\u72fc\u5316\u75c7\u60a3\u8005\u662f\u4e0d\u80fd\u4fdd\u6301\u6e05\u9192\u7406\u667a\u7684\uff0c\u5bf9\u5427\uff0c\u742a\u96c5\u3002\u201d</p><p>\u732b\u5973\u6c89\u9ed8\u5730\u70b9\u4e86\u70b9\u5934\u3002</p><p>\u201c......\u5c11\u5570\u55e6\uff0c\u603b\u4e4b\u89c4\u5219\u5c31\u662f\u8fd9\u6837\uff01\u201d</p><p><strong>*</strong>*</p><p>\u72fc\u4eba\u6740\u7684\u89c4\u5219\u5e76\u4e0d\u590d\u6742\uff0c\u5f88\u5bb9\u6613\u4e0a\u624b\uff0c\u5927\u5bb6\u73a9\u5f97\u5f88\u5f00\u5fc3\uff0c\u552f\u4e00\u7684\u95ee\u9898\u662f\u6885\u83f2\u65af\u603b\u662f\u8d62\u2014\u2014\u4e3b\u8981\u662f\u56e0\u4e3a\u6709\u4e2a\u6c38\u8fdc\u90fd\u65e0\u8111\u7ad9\u5979\u8fb9\u7684\u51db\u3002\u4e8e\u662f\u51e0\u5c40\u4e4b\u540e\uff0c\u4fbf\u6539\u7531\u6885\u83f2\u65af\u6765\u505a\u88c1\u5224\uff0c\u743c\u6069\u81ea\u5df1\u4e0b\u573a\u3002</p><p>\u4ed6\u62bd\u5230\u4e86\u9884\u8a00\u5bb6\uff0c\u5e76\u4e14\u6210\u529f\u62ff\u5230\u4e86\u8b66\u5fbd\uff0c\u6b63\u51c6\u5907\u5927\u5c55\u8eab\u624b\uff0c\u8ba9\u8fd9\u5e2e\u65b0\u624b\u83dc\u9e1f\u770b\u770b\u4ec0\u4e48\u662f\u771f\u6b63\u7684\u6280\u672f\uff0c\u7136\u540e......\u6885\u83f2\u65af\u5ba3\u5e03\u4ed6\u6628\u591c\u88ab\u72fc\u4eba\u6740\u4e86\u3002</p><p>\u201c \u5582\uff0c\u6211\u662f\u65b0\u4eba\uff0c\u65b0\u624b\u5e94\u8be5\u6709\u9996\u8f6e\u4fdd\u62a4\u554a\u3002\u201d</p><p>\u201c\u6709\u8fd9\u89c4\u5219\u5417\uff1f\u201d</p><p>\u201c\u6709\u3002\u201d</p><p>\u201c\u90a3\u597d\u5427\uff0c\u4e0b\u4e00\u5c40\u5f00\u59cb\u751f\u6548\uff0c\u73b0\u5728\u4ea4\u4ee3\u9057\u8a00\u79fb\u4ea4\u8b66\u5fbd\u5427\u3002\u201d</p><p>\u743c\u6069\u8ba4\u771f\u601d\u8003\u7247\u523b\uff0c\u89c9\u5f97\u4f1a\u5728\u7b2c\u4e00\u8f6e\u5c31\u6765\u780d\u4ed6\u7684\uff0c\u80af\u5b9a\u662f\u59b9\u5b50\uff0c\u800c\u4e14\u662f\u4e0a\u8fc7\u5e8a\u7684\u90a3\u79cd\u3002\u59d0\u59d0\u5f53\u7136\u4e0d\u53ef\u80fd\uff0c\u7ef4\u82e5\u62c9\u662f\u4ed6\u7b2c\u4e00\u591c\u67e5\u9a8c\u7684\u6751\u6c11\uff0c\u90a3\u5c31\u5269\u4e0b\u51db\u548c\u838e\u73de\u514b\u4e86\u3002\u4e8e\u662f\u4ed6\u628a\u63e1\u5341\u8db3\u5730\u5728\u9057\u8a00\u4e2d\u628a\u8fd9\u4e24\u4eba\u6807\u8bc6\u4e3a\u201c\u91cd\u5927\u5acc\u7591\u201d\uff0c\u5efa\u8bae\u653e\u9010\u6216\u8005\u6bd2\u6740\uff0c\u7136\u540e\u628a\u8b66\u5fbd\u79fb\u4ea4\u7ed9\u4e86\u7ef4\u82e5\u62c9\uff0c\u8d77\u8eab\u79bb\u573a\u3002</p><p>\u6ca1\u8fc7\u7247\u523b\uff0c\u7b2c\u4e00\u8f6e\u6295\u7968\u7ed3\u675f\uff0c\u5927\u5bb6\u6ca1\u6709\u542c\u4ece\u4ed6\u7684\u9057\u8a00\uff0c\u53cd\u800c\u5728\u65b0\u8b66\u957f\u7ef4\u82e5\u62c9\u7684\u53f7\u53ec\u4e4b\u4e0b\uff0c\u628a\u73ca\u5609\u653e\u9010\u4e86\uff0c\u7406\u7531\u662f\u201c\u8bf4\u8bdd\u592a\u5c11\uff0c\u53d1\u8a00\u6ca1\u6709\u5185\u5bb9\uff0c\u50cf\u662f\u4e00\u53ea\u5728\u52aa\u529b\u9690\u85cf\u8eab\u4efd\u7684\u72fc\u4eba\u3002\u201d</p><p>\u73ca\u5609\u7b11\u7740\u8d77\u8eab\uff0c\u8d70\u5230\u743c\u6069\u8eab\u65c1\u3002\u201c\u4e71\u641e\u4ec0\u4e48\u554a\uff0c\u201d\u743c\u6069\u5f88\u4e0d\u6ee1\uff0c\u201c\u6211\u4e0d\u90fd\u5df2\u7ecf\u8bf4\u4e86\uff0c\u51db\u548c\u838e\u73de\u514b\u5acc\u7591\u6700\u5927\uff0c\u600e\u4e48\u653e\u7740\u8fd9\u4e24\u4e2a\u4e0d\u7ba1\uff0c\u53cd\u800c\u628a\u59d0\u59d0\u653e\u9010\u4e86\u3002\u201d</p><p>\u201c\u79bb\u573a\u73a9\u5bb6\u7981\u6b62\u8bf4\u8bdd\uff0c\u201d\u4e3b\u6301\u4eba\u6885\u83f2\u65af\u53d1\u6325\u4f5c\u7528\uff0c\u201c\u8981\u8ba8\u8bba\u8bf7\u79bb\u8fdc\u70b9\u3002\u201d</p><p>\u201c\u597d\u597d\u3002\u201d</p><p>\u73ca\u5609\u62c9\u7740\u743c\u6069\u5f80\u5916\u8d70\uff0c\u8d70\u51fa\u51e0\u5341\u6b65\u624d\u505c\u4e0b\u6765\u3002\u201c\u8fd9\u5e2e\u83dc\u9e1f\u5c31\u662f\u4e0d\u4f1a\u73a9\uff0c\u8fd9\u4e48\u660e\u663e\u7684\u72fc\u4eba\u90fd\u770b\u4e0d\u51fa\u6765\uff0c\u5854\u62c9\u590f\u4e5f\u662f\uff0c\u4e71\u5e26\u8282\u594f\uff0c\u8fd9\u5c40\u5982\u679c\u8f93\u4e86\u5c31\u662f\u5979\u7684\u8d23\u4efb\uff0c\u201d\u743c\u6069\u66ff\u73ca\u5609\u6253\u62b1\u4e0d\u5e73\uff0c\u201c\u59d0\u59d0\u8bf4\u8bdd\u5c11\u600e\u4e48\u4e86\uff0c\u6709\u4fe1\u606f\u5c31\u591a\u8bf4\u8bdd\uff0c\u6ca1\u4fe1\u606f\u5c31\u5c11\u8bf4\u8bdd\uff0c\u5f88\u6b63\u5e38\u561b\u3002\u5bf9\u4e86\uff0c\u59d0\u59d0\u4f60\u662f\u62bd\u5230\u4ec0\u4e48\u724c\u4e86\uff1f\u201d</p><p>\u201c\u558f\uff0c\u8fd9\u4e2a\u3002\u201d</p><p>\u73ca\u5609\u7b11\u9765\u5982\u82b1\uff0c\u5c06\u6728\u724c\u9012\u8fc7\u6765\uff0c\u743c\u6069\u4e00\u773c\u671b\u53bb\uff0c\u6b63\u770b\u89c1\u724c\u9762\u4e0a\u4e00\u53ea\u9f87\u7259\u54a7\u5634\u7684\u5927\u7070\u72fc\u3002</p><p>\u201c......\u59d0\u59d0\u771f\u5389\u5bb3\uff0c\u201d\u743c\u6069\u987f\u4e86\u4e00\u4e0b\uff0c\u9a6c\u4e0a\u5938\u5956\uff0c\u201c\u73a9\u8fd9\u6e38\u620f\u5c31\u662f\u8981\u51fa\u4eba\u610f\u8868\uff0c\u4e0d\u843d\u7aa0\u81fc\u3002\u50cf\u51db\u90a3\u6837\u65e0\u8111\u7ad9\u8fb9\u827e\u5f25\u8587\uff0c\u6beb\u65e0\u6280\u672f\u542b\u91cf\uff0c\u5c5e\u4e8e\u6807\u51c6\u7684\u53cd\u9762\u6559\u6750\u3002\u59d0\u59d0\u4e0d\u77e5\u9053\u6bd4\u5979\u9ad8\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u800c\u4e14\u6211\u8fd8\u6ca1\u8bf4\u8bdd\uff0c\u59d0\u59d0\u5c31\u77e5\u9053\u6211\u662f\u9884\u8a00\u5bb6\uff0c\u624b\u8d77\u5200\u843d\u628a\u6211\u780d\u4e86\uff0c\u771f\u662f\u6167\u773c\u5982\u70ac\uff0c\u660e\u5bdf\u79cb\u6beb\uff0c\u5b9e\u5728\u662f\u4e86\u4e0d\u8d77\uff0c\u4e86\u4e0d\u8d77\u3002\u201d</p><p>\u201c\u6ca1\u6709\u5566\uff0c\u201d\u73ca\u5609\u7b11\u5f97\u5f88\u5f00\u5fc3\uff0c\u201c\u6211\u5c31\u662f\u770b\u5230\u4f60\u8fd9\u4e48\u591a\u60c5\u4eba\uff0c\u60f3\u60f3\u5fc3\u91cc\u5c31\u4e0d\u75db\u5feb\uff0c\u6240\u4ee5\u5c31\u628a\u4f60\u780d\u4e86\u2014\u2014\u7136\u540e\u6211\u5c31\u5fc3\u60c5\u8212\u7545\u4e86\u3002\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u59d0\u59d0\u5fc3\u60c5\u8212\u7545\u6bd4\u4ec0\u4e48\u90fd\u91cd\u8981\uff0c\u201d\u743c\u6069\u7acb\u523b\u8868\u6001\uff0c\u201c\u53ea\u8981\u59d0\u59d0\u9ad8\u5174\uff0c\u6bcf\u5929\u780d\u4e2a\u5341\u904d\u516b\u904d\u6beb\u65e0\u95ee\u9898\u3002\u201d</p><p>\u201c\u624d\u4e0d\u8981\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u5230\u65f6\u5019\u4f60\u5634\u4e0a\u4e0d\u8bf4\uff0c\u5fc3\u91cc\u80af\u5b9a\u89c9\u5f97\u59d0\u59d0\u5f88\u70e6\u3002\u201d</p><p>\u201c\u4e0d\u4f1a\u554a\uff0c\u5982\u679c\u72fc\u4eba\u90fd\u662f\u50cf\u59d0\u59d0\u8fd9\u6837\u7684\u5927\u7f8e\u4eba\uff0c\u6bcf\u4e2a\u5c11\u5e74\u90fd\u4f1a\u671f\u5f85\u88ab\u6740\u6b7b\u5427\u3002\u201d</p><p>\u6b63\u8bf4\u7b11\u95f4\uff0c\u743c\u6069\u5ffd\u7136\u5fc3\u4e2d\u4e00\u98a4\uff0c\u5f25\u6563\u5728\u5468\u56f4\u7684\u8b66\u6212\u6cd5\u672f\u5411\u4ed6\u53d1\u51fa\u6025\u4fc3\u7684\u8b66\u544a\uff0c\u9884\u793a\u7740\u6709\u67d0\u79cd\u5de8\u5927\u7684\u5371\u9669\u5373\u5c06\u964d\u4e34\u3002\u4ed6\u62ac\u773c\u5411\u56db\u5468\u5306\u5fd9\u671b\u53bb\uff0c\u53ea\u89c1\u9ed1\u6697\u4e4b\u4e2d\uff0c\u4e09\u70b9\u78a7\u83b9\u83b9\u7684\u5149\u7a81\u5140\u4e4d\u73b0\uff0c\u6392\u6210\u4e00\u4e2a\u4e09\u89d2\u5f62\uff0c\u5b9b\u5982\u9b3c\u706b\u822c\u76f4\u671d\u73ca\u5609\u80cc\u540e\u51b2\u6765\uff0c\u901f\u5ea6\u867d\u7136\u4e0d\u5feb\uff0c\u52bf\u5934\u5374\u975e\u5e38\u51f6\u731b\u3002</p><p>\u743c\u6069\u4e0d\u5047\u601d\u7d22\u5730\u5c06\u59d0\u59d0\u7ea4\u8170\u4e00\u63fd\uff0c\u5411\u4fa7\u9762\u8dd1\u53bb\uff0c\u90a3\u4e09\u70b9\u78a7\u5149\u5374\u4eff\u4f5b\u6709\u751f\u547d\u4e00\u822c\uff0c\u786c\u751f\u751f\u5730\u8f6c\u4e86\u4e2a\u5f2f\uff0c\u8ddf\u5728\u540e\u9762\u8ffd\u8fc7\u6765\u3002\u743c\u6069\u770b\u4e0d\u51fa\u8fd9\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u4e0d\u6562\u7ea0\u7f20\uff0c\u6b63\u6253\u7b97\u5148\u64a4\u56de\u5230\u706b\u5806\u8fb9\u548c\u5927\u5bb6\u6c47\u5408\uff0c\u8c01\u6599\u4fa7\u773c\u671b\u53bb\uff0c\u5fc3\u4e2d\u987f\u65f6\u4e00\u60ca\uff0c\u521a\u521a\u8fd8\u5728\u4e0d\u8fdc\u5904\u7684\u6885\u83f2\u65af\u7b49\u4eba\uff0c\u600e\u4e48\u5168\u90fd\u4e0d\u89c1\u4e86\u3002\u800c\u4e14\u4e0d\u4ec5\u4ec5\u662f\u4eba\uff0c\u5c31\u8fde\u706b\u5806\u3001\u5e10\u7bf7\uff0c\u4e5f\u5168\u90fd\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u53ea\u89c1\u4e00\u7247\u9ed1\u6697\u3002</p><p>\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u743c\u6069\u505c\u4f4f\u811a\u6b65\uff0c\u731b\u7136\u8f6c\u8eab\uff0c\u5de6\u624b\u4e94\u6307\u5f20\u5f00\uff0c\u5728\u9762\u524d\u5e03\u7f6e\u51fa\u4e00\u9053\u65e0\u5f62\u5c4f\u969c\u3002\u90a3\u4e09\u70b9\u78a7\u5149\u7b14\u76f4\u649e\u4e86\u4e0a\u6765\uff0c\u53d1\u51fa\u6c89\u95f7\u7684\u649e\u51fb\u58f0\uff0c\u5f80\u540e\u9000\u4e86\u4e00\u6bb5\u8ddd\u79bb\uff0c\u968f\u5373\u5f80\u4e0a\u5347\u8d77\uff0c\u4eff\u4f5b\u6b63\u5728\u84c4\u52bf\u51c6\u5907\u4e0b\u51fb\u3002</p><p>\u201c\u5149\uff01\u201d</p><p>\u73ca\u5609\u4f4e\u4f4e\u5730\u5ff5\u4e86\u4e00\u4e2a\u5b57\uff0c\u4e03\u516b\u56e2\u767d\u8272\u5149\u7403\u4ece\u5979\u7684\u6307\u5c16\u98de\u51fa\uff0c\u60ac\u6d6e\u5728\u534a\u7a7a\u4e2d\uff0c\u7a0d\u7a0d\u9a71\u9000\u5468\u56f4\u7684\u9ed1\u6697\u3002\u501f\u7740\u8fd9\u5149\u4eae\uff0c\u743c\u6069\u7ec8\u4e8e\u770b\u6e05\u4e86\u90a3\u4e09\u70b9\u78a7\u5149\u662f\u4ec0\u4e48\u3002</p><p>\u201c\u86c7\uff1f\u4e0d\uff0c\u662f\u9e70\u86c7\uff1f\u201d</p><p>\u5149\u8292\u7167\u5c04\u4e4b\u4e0b\uff0c\u5077\u88ad\u8005\u7ec8\u4e8e\u663e\u9732\u51fa\u4e86\u8eab\u5f62\uff0c\u90a3\u662f\u4e00\u53ea\u5de8\u5927\u7684\uff0c\u534a\u86c7\u534a\u9e1f\u7684\u602a\u7269\uff0c\u5706\u6eda\u6eda\u7684\u8eab\u8eaf\u6bd4\u6c34\u6876\u8fd8\u8981\u7c97\uff0c\u4e0b\u534a\u622a\u626d\u66f2\u76d8\u65cb\u5728\u5730\u9762\u4e0a\uff0c\u770b\u4e0d\u51fa\u6709\u591a\u957f\uff0c\u4e0a\u534a\u622a\u9ad8\u9ad8\u6602\u8d77\uff0c\u8111\u888b\u5374\u50cf\u662f\u4e00\u53ea\u9e1f\uff0c\u9e1f\u5634\u4e2d\u957f\u6ee1\u4e86\u5c16\u7259\uff0c\u5728\u989d\u5934\u5904\u53c8\u591a\u957f\u4e86\u4e00\u53ea\u773c\u775b\u3002\u743c\u6069\u4e4b\u524d\u770b\u5230\u7684\u90a3\u4e09\u70b9\u78a7\u5149\uff0c\u4fbf\u662f\u5b83\u7684\u4e09\u53ea\u773c\u775b\uff0c\u800c\u5b83\u8eaf\u4f53\u7684\u5176\u4ed6\u90e8\u4f4d\u90fd\u88ab\u9752\u84dd\u8272\u7684\u9cde\u7247\u8986\u76d6\u3002\u8fd9\u79cd\u9cde\u7247\u4e0e\u591c\u8272\u5b8c\u7f8e\u878d\u4e3a\u4e00\u4f53\uff0c\u4f3c\u4e4e\u8fd8\u6709\u5438\u6536\u5149\u7ebf\u7684\u4f5c\u7528\uff0c\u743c\u6069\u5df2\u7ecf\u5f00\u542f\u4e86\u9ed1\u6697\u89c6\u89c9\uff0c\u5c45\u7136\u90fd\u6ca1\u6709\u770b\u89c1\u5b83\u3002</p><p>\u73ca\u5609\u62bd\u51fa\u957f\u7b1b\uff0c\u6253\u7b97\u5439\u594f\u5b89\u7720\u66f2\uff0c\u5374\u88ab\u743c\u6069\u5236\u6b62\u4e86\u3002</p><p>\u201c\u6ca1\u7528\uff0c\u9e70\u86c7\u514d\u75ab\u9b45\u60d1\u3002\u201d</p><p>\u73ca\u5609\u4e0d\u8ba4\u8bc6\u8fd9\u79cd\u602a\u7269\uff0c\u743c\u6069\u5374\u66fe\u7ecf\u5728\u4e66\u4e2d\u89c1\u8fc7\uff0c\u4e43\u662f\u4e0a\u53e4\u65f6\u671f\uff0c\u5927\u9646\u5357\u65b9\u70ed\u5e26\u96e8\u6797\u91cc\uff0c\u67d0\u4e9b\u90aa\u6076\u5deb\u5e08\u7528\u79d8\u6cd5\u70ae\u5236\u51fa\u6765\u7684\u4e00\u79cd\u9b54\u517d\uff0c\u51f6\u6b8b\u55dc\u8840\uff0c\u529b\u5927\u65e0\u7a77\uff0c\u4f46\u56e0\u4e3a\u662f\u9b54\u6cd5\u626d\u66f2\u800c\u6210\u7684\u751f\u7269\uff0c\u65e0\u6cd5\u7e41\u884d\u540e\u4ee3\uff0c\u7406\u5f53\u65e9\u5c31\u7edd\u79cd\u4e86\u624d\u5bf9\uff0c\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\u9047\u5230\u4e00\u53ea\uff1f</p><p>\u4ed6\u4e0d\u6562\u6020\u6162\uff0c\u6025\u5fd9\u6fc0\u53d1\u9632\u62a4\u9b54\u6cd5\uff0c\u77ac\u95f4\u5728\u81ea\u5df1\u548c\u73ca\u5609\u5468\u56f4\u5e03\u4e0b\u4e00\u4e2a\u534a\u7403\u5f62\u5168\u5c01\u95ed\u62a4\u7f69\u3002\u4f20\u8bf4\u9e70\u86c7\u88ab\u5deb\u5e08\u6ce8\u5165\u4e86\u6df1\u6e0a\u6076\u9b54\u7684\u90aa\u529b\uff0c\u80fd\u591f\u55b7\u5c04\u6bd2\u6db2\u3001\u95ea\u7535\u548c\u706b\u7130\uff0c\u5e9e\u5927\u7684\u8eab\u8eaf\u8fd8\u80fd\u77ed\u6682\u98de\u884c\uff0c\u5c24\u5176\u90a3\u53ea\u989d\u4e0a\u7684\u773c\u775b\uff0c\u7741\u5f00\u65f6\u6709\u62b5\u6297\u9b54\u6cd5\u4e4b\u80fd\uff0c\u5e76\u975e\u53ea\u6709\u5355\u7eaf\u7684\u86ee\u529b\uff0c\u4e0d\u662f\u597d\u5bf9\u4ed8\u7684\u3002\u9632\u5fa1\u521a\u521a\u5b8c\u6210\uff0c\u9e70\u86c7\u4fbf\u731b\u7136\u81ea\u7a7a\u4e2d\u7b14\u76f4\u6251\u4e86\u4e0b\u6765\u3002</p><p>\u201c\u7830\uff01\u201d</p><p>\u65e0\u5f62\u7684\u62a4\u7f69\u518d\u6b21\u6321\u4f4f\u4e86\u88ad\u51fb\uff0c\u9e70\u86c7\u88ab\u72e0\u72e0\u5f39\u4e86\u56de\u53bb\uff0c\u8fde\u90a3\u53ea\u9e1f\u5634\u4eff\u4f5b\u90fd\u88ab\u649e\u53d8\u5f62\u4e86\uff0c\u5b83\u6643\u4e86\u6643\u8111\u888b\uff0c\u51f6\u6076\u5730\u77aa\u7740\u9762\u524d\u7684\u4e24\u4e2a\u4eba\u7c7b\uff0c\u8212\u5c55\u8eab\u4f53\uff0c\u5f00\u59cb\u7ed5\u7740\u5708\u5b50\u6e38\u8d70\u3002\u743c\u6069\u7a0d\u7a0d\u677e\u4e86\u53e3\u6c14\uff0c\u611f\u89c9\u8fd9\u53ea\u9e70\u86c7\u548c\u4e66\u7c4d\u4e2d\u7684\u8bb0\u8f7d\u4f3c\u4e4e\u6709\u4e9b\u5dee\u8ddd\uff0c\u6216\u8bb8\u662f\u81ea\u5df1\u8ba4\u9519\u4e86\uff1f\u5176\u5b9e\u53ea\u662f\u4e00\u53ea\u8111\u888b\u53d1\u80b2\u7578\u5f62\u7684\u5927\u87d2\u86c7\uff1f</p><p>\u9e70\u86c7\u53c8\u8bd5\u63a2\u6027\u5730\u51b2\u4e86\u51e0\u6b21\uff0c\u90fd\u88ab\u62a4\u7f69\u6321\u4f4f\uff0c\u773c\u770b\u4f3c\u4e4e\u65e0\u8ba1\u53ef\u65bd\u3002\u743c\u6069\u4e00\u8fb9\u7ee7\u7eed\u65bd\u6cd5\u5f3a\u5316\u62a4\u7f69\uff0c\u4e00\u8fb9\u89c2\u5bdf\u56db\u5468\uff0c\u5bfb\u627e\u6885\u83f2\u65af\u7b49\u4eba\u7684\u8e2a\u8ff9\u3002\u4ed6\u8bb0\u5f97\u5f88\u6e05\u695a\uff0c\u81ea\u5df1\u548c\u73ca\u5609\u660e\u660e\u5c31\u8d70\u51fa\u4e86\u4e0d\u5230\u4e94\u5341\u6b65\u7684\u8ddd\u79bb\uff0c\u5728\u88ab\u88ad\u51fb\u4e4b\u524d\u7684\u4e00\u79d2\u949f\uff0c\u773c\u89d2\u4f59\u5149\u8fd8\u77a5\u89c1\u5230\u706b\u5149\uff0c\u542c\u89c1\u5979\u4eec\u5728\u8bf4\u8bdd\uff0c\u600e\u4e48\u4f1a\u9661\u7136\u95f4\u5c31\u4e0d\u89c1\u4e86\u3002\u8fd9\u4e2a\u95ee\u9898\u641e\u4e0d\u660e\u767d\uff0c\u4ed6\u5fc3\u4e2d\u59cb\u7ec8\u4e0d\u5b89\uff0c\u603b\u89c9\u5f97\u9ed1\u6697\u4e4b\u4e2d\uff0c\u8fd8\u6f5c\u4f0f\u7740\u66f4\u5371\u9669\u7684\u654c\u4eba\u3002</p><p>\u201c\u662f\u4e0d\u662f\u67d0\u79cd\u5e7b\u672f\u7ed3\u754c\uff1f\u201d\u73ca\u5609\u8bf4\u3002</p><p>\u743c\u6069\u6447\u6447\u5934\uff0c\u201c\u6211\u611f\u89c9\u50cf\u662f\u67d0\u79cd\u7a7a\u95f4\u9b54\u6cd5\uff0c\u5468\u56f4\u6709\u7a7a\u95f4\u626d\u66f2\u7684\u8ff9\u8c61\uff0c\u201d\u4ed6\u6c89\u541f\u7740\u8bf4\uff0c\u201c\u5947\u602a\u7684\u662f\uff0c\u6211\u770b\u4e0d\u51fa\u4efb\u4f55\u6cd5\u672f\u7684\u75d5\u8ff9\u3002\u201d</p><p>\u201c\u5148\u628a\u8fd9\u5bb6\u4f19\u89e3\u51b3\u6389\u518d\u8bf4\u3002\u201d\u73ca\u5609\u5efa\u8bae\u3002</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u9ed8\u9ed8\u8bf5\u5492\uff0c\u4e00\u4e2a\u89e3\u79bb\u672f\u5df2\u7ecf\u51c6\u5907\u5b8c\u6bd5\uff0c\u84c4\u52bf\u5f85\u53d1\u3002\u9e70\u86c7\u5982\u6b64\u5de8\u5927\uff0c\u751f\u547d\u529b\u60f3\u5fc5\u4e5f\u5341\u5206\u987d\u5f3a\uff0c\u4f20\u8bf4\u5bf9\u9b54\u6cd5\u4e5f\u6709\u8f83\u5f3a\u7684\u62b5\u6297\u529b\uff0c\u82e5\u662f\u89e3\u79bb\u5c04\u7ebf\u51fb\u4e2d\u5176\u4ed6\u90e8\u4f4d\uff0c\u867d\u7136\u4f1a\u9020\u6210\u4f24\u5bb3\uff0c\u5374\u96be\u4ee5\u81f4\u547d\uff0c\u5fc5\u987b\u8981\u6253\u4e2d\u8981\u5bb3\u624d\u884c\u3002\u636e\u8bb0\u8f7d\uff0c\u9e70\u86c7\u6700\u8584\u5f31\u4e5f\u6700\u5173\u952e\u4e4b\u5904\u5c31\u662f\u989d\u5934\u4e0a\u7684\u7b2c\u4e09\u53ea\u773c\u775b\uff0c\u4f46\u5b83\u4e0d\u65ad\u5728\u873f\u8712\u6e38\u8d70\uff0c\u743c\u6069\u60f3\u7784\u51c6\u4e5f\u4e0d\u5bb9\u6613\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u5ffd\u7136\u95f4\u743c\u6069\u773c\u524d\u4e00\u95ea\uff0c\u5c31\u89c1\u90a3\u53ea\u9e70\u86c7\u7a81\u5140\u6d88\u5931\u5728\u7a7a\u6c14\u4e2d\u3002\u4ed6\u60c5\u77e5\u4e0d\u5999\uff0c\u521a\u8981\u8f6c\u6362\u6cd5\u672f\uff0c\u5c31\u89c9\u811a\u4e0b\u5730\u9762\u731b\u70c8\u9707\u52a8\uff0c\u7d27\u63a5\u7740\uff0c\u4e00\u53ea\u5de8\u5927\u7684\u9e70\u5934\u7834\u571f\u800c\u51fa\uff0c\u4e00\u53e3\u5c06\u73ca\u5609\u541e\u4e86\u4e0b\u53bb\u3002</p><p>\u201c\u59d0\u59d0\uff01\u201d</p><p>\u743c\u6069\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u603b\u7b97\u4ed6\u53cd\u5e94\u8fc5\u901f\uff0c\u5728\u9e70\u86c7\u6d88\u5931\u65f6\u5c31\u610f\u8bc6\u5230\u4e0d\u5bf9\uff0c\u5306\u5fd9\u64a4\u9500\u89e3\u79bb\u672f\uff0c\u53f3\u624b\u865a\u63e1\uff0c\u539f\u672c\u6269\u6563\u5728\u56db\u5468\u7684\u62a4\u7f69\u6025\u901f\u5411\u5185\u538b\u7f29\uff0c\u53d8\u6210\u4e00\u4e2a\u8089\u773c\u53ef\u89c1\u7684\u534a\u900f\u660e\u7403\u4f53\uff0c\u582a\u582a\u5c06\u73ca\u5609\u5305\u88f9\u5728\u5185\u3002</p><p>\u9e70\u86c7\u4e00\u53e3\u54ac\u4e0b\uff0c\u5374\u50cf\u662f\u54ac\u5230\u4e86\u4e00\u9897\u94a2\u7403\uff0c\u575a\u786c\u65e0\u6bd4\uff0c\u54ac\u4e0d\u8fdb\u53bb\uff0c\u53cd\u800c\u5d29\u65ad\u4e86\u4e00\u6392\u5229\u9f7f\u3002\u743c\u6069\u5306\u5fd9\u4e4b\u95f4\u5207\u6362\u6cd5\u672f\uff0c\u80f8\u53e3\u6c14\u8840\u7ffb\u6d8c\uff0c\u773c\u524d\u4e00\u9635\u9635\u5730\u6655\u7729\uff0c\u5374\u4e0d\u6562\u62d6\u5ef6\uff0c\u7d2b\u6c34\u6676\u6cd5\u6756\u81ea\u8896\u4e2d\u6ed1\u51fa\uff0c\u6b63\u8981\u5f3a\u884c\u518d\u91cd\u65b0\u91ca\u653e\u6cd5\u672f\uff0c\u5ffd\u7136\u95f4\u4e00\u4e2a\u9ed1\u8863\u4eba\u4ece\u9ad8\u5904\u6811\u68a2\u4e0a\u8df3\u4e0b\u6765\uff0c\u6b63\u6b63\u5730\u843d\u5230\u9e70\u86c7\u7684\u8111\u888b\u4e0a\uff0c\u4e00\u9053\u8840\u7ea2\u8272\u7684\u5149\u4ece\u9ed1\u8863\u4eba\u7684\u53cc\u624b\u4e2d\u53d1\u51fa\uff0c\u7b14\u76f4\u523a\u5165\u9e70\u86c7\u7684\u989d\u9876\u773c\u775b\u4e2d\u3002</p><p>\u201c\u55e4\uff01\u201d</p><p>\u4f34\u968f\u7740\u4eff\u4f5b\u70e7\u7ea2\u7684\u70d9\u94c1\u6d78\u5165\u51b0\u6c34\u7684\u58f0\u97f3\uff0c\u9e70\u86c7\u9732\u51fa\u5730\u9762\u7684\u534a\u622a\u8eab\u8eaf\u4eff\u4f5b\u6328\u4e86\u5f53\u5934\u4e00\u68d2\uff0c\u731b\u7136\u95f4\u59d4\u987f\u4e0b\u6765\uff0c\u8f6f\u7ef5\u7ef5\u5730\u5012\u5728\u5730\u4e0a\u3002\u73ca\u5609\u4ece\u5b83\u7684\u53e3\u4e2d\u6454\u51fa\uff0c\u6eda\u843d\u5728\u5730\u4e0a\u3002\u743c\u6069\u5306\u5fd9\u8dd1\u8fc7\u53bb\uff0c\u6276\u8d77\u59d0\u59d0\uff0c\u540c\u65f6\u5bf9\u7740\u5730\u9762\u91ca\u653e\u4e86\u4e00\u4e2a\u201c\u5316\u6ce5\u4e3a\u77f3\u201d\uff0c\u5c06\u9e70\u86c7\u56f0\u4f4f\uff0c\u9632\u6b62\u5b83\u518d\u5ea6\u66b4\u8d77\u4f24\u4eba\u3002</p><p>\u201c\u59d0\u59d0\u6ca1\u4e8b\u5427\u3002\u201d\u4ed6\u7d27\u5f20\u5730\u95ee\uff0c\u68c0\u67e5\u73ca\u5609\u8eab\u4e0a\u6709\u65e0\u53d7\u4f24\u3002</p><p>\u201c\u6211\u6ca1\u4e8b\u3002\u201d</p><p>\u73ca\u5609\u8138\u8272\u6709\u4e9b\u82cd\u767d\uff0c\u4f46\u8fd8\u80fd\u7ef4\u6301\u9547\u5b9a\uff0c\u53cd\u5012\u662f\u743c\u6069\u88ab\u5413\u5f97\u591f\u545b\uff0c\u53ea\u602a\u81ea\u5df1\u758f\u5ffd\u5927\u610f\uff0c\u5c45\u7136\u8ba9\u59d0\u59d0\u8eab\u9677\u9669\u5883\u3002\u4ed6\u867d\u7136\u5728\u6700\u540e\u5173\u5934\u5b8c\u6210\u4e86\u62a4\u7f69\uff0c\u4f46\u4e5f\u53ea\u80fd\u6682\u65f6\u62b5\u6321\u4f24\u5bb3\uff0c\u5982\u679c\u9e70\u86c7\u76f4\u63a5\u628a\u73ca\u5609\u541e\u4e86\u4e0b\u53bb\uff0c\u9041\u5165\u5730\u4e0b\uff0c\u6216\u8005\u9003\u5165\u6797\u4e2d\uff0c\u90a3\u53ef\u5982\u4f55\u662f\u597d\u3002\u5e78\u597d\u6709\u4eba\u63f4\u624b\uff0c\u5426\u5219\u540e\u679c\u771f\u662f\u4e0d\u582a\u8bbe\u60f3\u3002</p><p>\u201c\u591a\u8c22\u76f8\u52a9\u3002\u201d\u786e\u5b9a\u73ca\u5609\u6ca1\u6709\u53d7\u4f24\u540e\uff0c\u743c\u6069\u5927\u5927\u677e\u4e86\u53e3\u6c14\uff0c\u8d76\u5fd9\u5411\u9ed1\u8863\u4eba\u9053\u8c22\u3002\u5bf9\u65b9\u5e76\u6ca1\u6709\u7b54\u8bdd\uff0c\u800c\u662f\u9ed8\u9ed8\u5730\u5c06\u4e00\u67c4\u8840\u7ea2\u8272\u7684\u77ed\u77db\u4ece\u9e70\u86c7\u773c\u4e2d\u62d4\u51fa\uff0c\u8f6c\u8eab\u4fbf\u8d70\u3002\u521a\u8d70\u51fa\u51e0\u6b65\uff0c\u5374\u4e0d\u6599\u9e70\u86c7\u5e76\u672a\u6b7b\u900f\uff0c\u9661\u7136\u95f4\u8eab\u8eaf\u626d\u52a8\uff0c\u9e70\u5934\u6602\u8d77\uff0c\u5c31\u8981\u53bb\u54ac\u9ed1\u8863\u4eba\u3002</p><p>\u201c\u6ce8\u610f\u80cc\u540e\uff01\u201d</p><p>\u743c\u6069\u6b63\u770b\u7740\u8fd9\u8fb9\uff0c\u4e00\u89c1\u86c7\u8eab\u52a8\u4f5c\uff0c\u7acb\u523b\u51fa\u58f0\u793a\u8b66\uff0c\u540c\u65f6\u5c31\u8981\u91ca\u653e\u6cd5\u672f\uff0c\u4f46\u5374\u4ecd\u7136\u6162\u4e86\u4e00\u6b65\u3002\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u9ed1\u8863\u4eba\u4eff\u4f5b\u80cc\u540e\u957f\u4e86\u773c\u775b\u4f3c\u7684\uff0c\u731b\u7136\u5c48\u8eab\u5f80\u4fa7\u65b9\u4e00\u4e2a\u7ffb\u6eda\uff0c\u907f\u5f00\u4e86\u88ad\u51fb\u3002</p><p>\u9e70\u86c7\u4e00\u51fb\u4e0d\u4e2d\uff0c\u5c31\u8981\u518d\u54ac\uff0c\u4e00\u9053\u7070\u5149\u4ece\u8fdc\u5904\u5c04\u6765\uff0c\u6b63\u4e2d\u86c7\u8eab\u3002\u5b83\u7684\u989d\u773c\u88ab\u523a\u778e\uff0c\u6297\u9b54\u80fd\u529b\u5df2\u7ecf\u6d88\u5931\uff0c\u575a\u786c\u7684\u9cde\u7532\u80fd\u62b5\u5fa1\u5200\u5251\uff0c\u5374\u65e0\u6cd5\u963b\u6321\u9b54\u6cd5\uff0c\u7070\u5149\u5982\u6c34\u7eb9\u822c\u8fc5\u901f\u8513\u5ef6\uff0c\u5c06\u5b83\u5316\u4f5c\u5ca9\u77f3\uff0c\u4e00\u8282\u4e00\u8282\u5730\u788e\u88c2\u5f00\u6765\u3002</p><p>\u9ed1\u8863\u4eba\u7ad9\u8d77\u6765\uff0c\u743c\u6069\u8fd9\u624d\u770b\u6e05\u5bf9\u65b9\u7684\u6a21\u6837\uff0c\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u90a3\u662f\u4e00\u4f4d\u7a7f\u7740\u7d27\u8eab\u76ae\u8863\u7684\u5973\u5b50\uff0c\u957f\u957f\u7684\u9ed1\u53d1\u62e2\u6210\u9a6c\u5c3e\uff0c\u76f8\u8c8c\u4e0d\u7b97\u51fa\u8272\uff0c\u4f46\u8eab\u4fee\u817f\u957f\uff0c\u6574\u4e2a\u4eba\u900f\u7740\u4e00\u79cd\u6c89\u9759\u7684\u6c14\u8d28\uff0c\u82e5\u975e\u5979\u624b\u4e2d\u8fd8\u62ff\u7740\u90a3\u67c4\u8840\u7ea2\u77ed\u77db\uff0c\u771f\u60f3\u8c61\u4e0d\u51fa\u65b9\u624d\u51fb\u6740\u9e70\u86c7\u7684\u82f1\u59ff\u3002\u5979\u770b\u4e86\u770b\u5df2\u7ecf\u77f3\u5316\u7684\u9e70\u86c7\uff0c\u53c8\u770b\u4e86\u770b\u743c\u6069\uff0c\u53cd\u624b\u5c06\u77ed\u77db\u63d2\u5728\u80cc\u540e\uff0c\u8d70\u4e86\u8fc7\u6765\uff0c\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff0c\u4f46\u743c\u6069\u548c\u73ca\u5609\u90fd\u6ca1\u542c\u61c2\u3002</p><p>\u201c\u62b1\u6b49\uff0c\u6211\u4eec\u4e0d\u592a\u4f1a\u6069\u745f\u8bed\u3002\u201d</p><p>\u201c\u665a\u4e0a\u597d\uff0c\u65c5\u884c\u8005\uff0c\u201d\u5973\u5b50\u8bf4\uff0c\u201c\u6211\u662f\u5854\u7c73\u5a05\u3002\u5f88\u62b1\u6b49\uff0c\u4f60\u4eec\u6050\u6015\u60f9\u4e0a\u9ebb\u70e6\u4e86\u3002\u201d</p></article>"}}}]);
