(window.webpackJsonp=window.webpackJsonp||[]).push([[562],{584:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u8282 \u5384\u8fd0",content:"<article><h2>\u7b2c\u4e09\u8282 \u5384\u8fd0</h2><p>\u201c\u4f60\u4eec\u60f9\u4e0a\u9ebb\u70e6\u4e86\u3002\u201d\u90a3\u4f4d\u81ea\u79f0\u5854\u7c73\u5a05\u7684\u9ed1\u8863\u5973\u5b50\u8bf4\u3002</p><p>\u5979\u73b0\u5728\u8bf4\u7684\u662f\u4e2d\u571f\u5927\u9646\u7684\u901a\u7528\u8bed\uff0c\u800c\u4e14\u8bf4\u5f97\u8fd8\u7b97\u4e0d\u9519\uff0c\u53ea\u662f\u53d1\u97f3\u6bd4\u8f83\u5947\u602a\uff0c\u5e76\u4e0d\u662f\u4e0d\u51c6\u786e\uff0c\u800c\u662f\u6709\u4e9b\u542b\u7cca\uff0c\u5c31\u50cf\u54ac\u7740\u820c\u5934\u5728\u8bf4\u8bdd\uff0c\u5012\u662f\u522b\u6709\u4e00\u79cd\u53ef\u7231\u7684\u5473\u9053\uff0c\u4f46\u8bf4\u51fa\u6765\u7684\u5185\u5bb9\u5c31\u8ba9\u4eba\u5b8c\u5168\u542c\u4e0d\u61c2\u4e86\u3002\u201c\u4ec0\u4e48\u9ebb\u70e6\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u8fd9\u53ea\u9e70\u86c7\u4e0d\u662f\u5df2\u7ecf\u88ab\u5e72\u6389\u4e86\u4e48\uff1f\u96be\u4e0d\u6210\u5b83\u662f\u88ab\u67d0\u4e2a\u5deb\u5e08\u8c62\u517b\u7684\uff0c\u6253\u6b7b\u4e86\u5ba0\u7269\uff0c\u4e3b\u4eba\u8981\u6765\u7b97\u8d26\uff1f\u53c8\u6216\u8005\u5b83\u7684\u540c\u4f34\u3001\u914d\u5076\u8981\u6765\u62a5\u590d\uff1f\u4e00\u5239\u90a3\u95f4\uff0c\u4ed6\u7684\u8111\u4e2d\u5df2\u7ecf\u95ea\u8fc7\u51e0\u79cd\u53ef\u80fd\u6027\uff0c\u968f\u5373\u6697\u81ea\u6212\u5907\uff0c\u5de6\u53f3\u5f20\u671b\uff0c\u552f\u6050\u9ed1\u6697\u5bc6\u6797\u4e4b\u4e2d\uff0c\u518d\u7a9c\u51fa\u53e6\u4e00\u53ea\u602a\u517d\u4ec0\u4e48\u7684\u3002</p><p>\u5854\u7c73\u5a05\u6447\u6447\u5934\uff0c\u201c\u662f\u6211\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u4f60\u521a\u624d\u4e0d\u5e94\u8be5\u5e2e\u52a9\u6211\uff0c\u201d\u5854\u7c73\u5a05\u8bf4\uff0c\u201c\u5384\u8fd0\u73b0\u5728\u7f20\u4e0a\u4f60\u4e86\u3002\u201d</p><p>\u743c\u6069\u548c\u73ca\u5609\u9762\u9762\u76f8\u89d1\uff0c\u66f4\u662f\u83ab\u540d\u5176\u5999\uff0c\u201c\u5854\u7c73\u5a05\u5c0f\u59d0\uff0c\u4e07\u5206\u611f\u8c22\u4f60\u521a\u624d\u76f8\u6551\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4f60\u521a\u624d\u8bf4\u7684\u2018\u5384\u8fd0\u2019\uff0c\u53c8\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u65b9\u4fbf\u7684\u8bdd\uff0c\u80fd\u5426\u76f8\u544a\uff1f\u201d</p><p>\u5854\u7c73\u5a05\u70b9\u4e86\u70b9\u5934\uff0c\u8d70\u5230\u4e00\u68f5\u6811\u5e95\u4e0b\uff0c\u4fef\u8eab\u6361\u8d77\u4e00\u5757\u77f3\u5757\uff0c\u5c06\u5b83\u7ffb\u4e86\u4e2a\u8fb9\u3002\u8fd9\u4e00\u7b80\u7b80\u5355\u5355\u7684\u4e3e\u52a8\u4eff\u4f5b\u89e6\u53d1\u4e86\u67d0\u79cd\u673a\u5173\uff0c\u7a7a\u6c14\u4e00\u9635\u8f7b\u5fae\u6ce2\u6298\uff0c\u5468\u56f4\u7684\u666f\u8c61\u6084\u7136\u53d8\u5316\uff0c\u743c\u6069\u518d\u5ea6\u770b\u89c1\u4e86\u6885\u83f2\u65af\u7b49\u4eba\uff0c\u5979\u4eec\u8fd8\u6b63\u56f4\u5728\u706b\u5806\u8fb9\u73a9\u72fc\u4eba\u6740\uff0c\u73a9\u5f97\u975e\u5e38\u6295\u5165\uff0c\u5b8c\u5168\u6ca1\u610f\u8bc6\u5230\u743c\u6069\u548c\u73ca\u5609\u521a\u521a\u5df2\u7ecf\u7ecf\u5386\u4e86\u4e00\u573a\u5267\u70c8\u640f\u6740\u3002</p><p>\u743c\u6069\u5fc3\u4e2d\u6709\u4e00\u5806\u7591\u95ee\uff0c\u4f46\u4e5f\u53ea\u80fd\u6682\u65f6\u6309\u637a\uff0c\u9080\u8bf7\u5854\u7c73\u5a05\u5728\u706b\u5806\u8fb9\u5750\u4e0b\uff0c\u5e76\u7b80\u7565\u5411\u5176\u4ed6\u4eba\u8bf4\u4e86\u4e00\u4e0b\u521a\u624d\u53d1\u751f\u7684\u4e8b\u60c5\u3002\u201c\u4f60\u8bf4\u521a\u624d\u4f60\u4eec\u5728\u65c1\u8fb9\u9047\u5230\u4e86\u4e00\u53ea\u9e70\u86c7\uff1f\u201d\u6885\u83f2\u65af\u9887\u4e3a\u8be7\u5f02\uff0c\u201c\u600e\u4e48\u6211\u4eec\u5b8c\u5168\u6ca1\u6709\u542c\u89c1\u52a8\u9759\uff1f\u201d</p><p>\u8fd9\u70b9\u743c\u6069\u4e5f\u767e\u601d\u4e0d\u5f97\u5176\u89e3\uff0c\u4ed6\u548c\u73ca\u5609\u660e\u660e\u4e5f\u5c31\u8d70\u51fa\u4e86\u51e0\u5341\u6b65\u8fdc\uff0c\u600e\u4e48\u4f1a\u4eff\u4f5b\u5c31\u8fdb\u5165\u4e86\u53e6\u4e00\u4e2a\u7a7a\u95f4\u3002\u6709\u4e9b\u5f3a\u5927\u7684\u7a7a\u95f4\u6cd5\u672f\u7684\u786e\u80fd\u591f\u505a\u5230\u8fd9\u70b9\uff0c\u6bd4\u5982\u8ff7\u5bab\u672f\uff0c\u4f46\u4e0d\u53ef\u80fd\u8ba9\u4ed6\u5b8c\u5168\u6ca1\u6709\u5bdf\u89c9\uff0c\u65e0\u58f0\u65e0\u606f\u5c31\u4e2d\u4e86\u62db\u3002\u81f3\u4e8e\u6885\u83f2\u65af\u7b49\u4eba\u6ca1\u6709\u53d1\u73b0\u743c\u6069\u548c\u73ca\u5609\u6d88\u5931\uff0c\u8fd9\u5012\u5f88\u597d\u7406\u89e3\uff0c\u5979\u4eec\u73a9\u72fc\u4eba\u6740\u73a9\u5f97\u6c89\u8ff7\uff0c\u8c01\u4f1a\u5728\u4e4e\u4e24\u4e2a\u51fa\u5c40\u8005\u53bb\u54ea\u4e86\u3002</p><p>\u7ef4\u82e5\u62c9\u7ed5\u7740\u706b\u5806\u8d70\u4e86\u4e00\u5708\uff0c\u5012\u662f\u770b\u51fa\u4e86\u4e00\u70b9\u95e8\u9053\uff0c\u201c\u662f\u8fd9\u4e2a\u5bbf\u8425\u5730\u88ab\u67d0\u79cd\u9b54\u6cd5\u5f71\u54cd\uff0c\u5f62\u6210\u4e86\u4e00\u4e2a\u7c7b\u4f3c\u4e8e\u534a\u4f4d\u9762\u7684\u5730\u65b9\uff0c\u7a7a\u95f4\u5e94\u8be5\u4e0d\u5927\uff0c\u4f60\u4eec\u4fe9\u521a\u521a\u662f\u6070\u597d\u8d70\u51fa\u4e86\u8303\u56f4\u3002\u5f88\u6709\u8da3\uff0c\u8fd9\u79cd\u9b54\u6cd5\u6211\u95fb\u6240\u672a\u95fb\uff0c\u4e5f\u5b8c\u5168\u611f\u89c9\u4e0d\u5230\u9b54\u7f51\u6270\u52a8\u7684\u75d5\u8ff9\uff0c\u96be\u9053\u662f\u4e1c\u57df\u7684\u89a1\u672f\uff1f\u201d</p><p>\u201c\u662f\u6211\u5e03\u7f6e\u7684\u3002\u201d\u5854\u7c73\u5a05\u8bf4\u3002</p><p>\u201c\u6240\u4ee5\u90a3\u4e9b\u6069\u745f\u4eba\u90fd\u8bf4\uff0c\u53ea\u8981\u5728\u6807\u8bb0\u597d\u7684\u5bbf\u8425\u5730\u4f11\u606f\uff0c\u5c31\u4e0d\u4f1a\u906d\u9047\u5371\u9669\uff01\u201d\u743c\u6069\u604d\u7136\u660e\u767d\u8fc7\u6765\uff0c\u201c\u662f\u4f60\u4e00\u76f4\u5728\u4fdd\u62a4\u4ed6\u4eec\u3002\u201d</p><p>\u201c\u6211\u662f\u53d7\u795e\u59ec\u7684\u59d4\u6258\uff0c\u62a4\u9001\u90a3\u4e9b\u53bb\u96c5\u5c14\u8d1d\u7433\u5a1c\u57ce\u7684\u65c5\u884c\u8005\u3002\u201d</p><p>\u51db\u9012\u8fc7\u6765\u4e00\u676f\u996e\u6599\uff0c\u5854\u7c73\u5a05\u63a5\u8fc7\u6765\u770b\u4e86\u770b\uff0c\u8f7b\u8f7b\u62bf\u4e86\u4e00\u53e3\uff0c\u7136\u540e\u653e\u5728\u65c1\u8fb9\u3002\u201c\u539f\u6765\u5982\u6b64\u3002\u201d\u743c\u6069\u8bf4\uff0c\u96be\u602a\u5979\u4f1a\u4e3b\u52a8\u5e2e\u5fd9\u5e72\u6389\u90a3\u53ea\u9e70\u86c7\uff0c\u4e0d\u8fc7\u4e4b\u524d\u5854\u7c73\u5a05\u8bf4\u7684\u201c\u5384\u8fd0\u201d\uff0c\u53c8\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f</p><p>\u201c\u6211\u88ab\u4e00\u4e2a\u90aa\u795e\u8bc5\u5492\u4e86\u3002\u201d\u5854\u7c73\u5a05\u89e3\u91ca\u3002</p><p>\u8fd9\u4e2a\u8bc5\u5492\u5f88\u5947\u5f02\uff0c\u5b83\u5e76\u4e0d\u76f4\u63a5\u4f24\u5bb3\u5854\u7c73\u5a05\uff0c\u800c\u662f\u4f5c\u7528\u4e8e\u201c\u4e0e\u5854\u7c73\u5a05\u6709\u5173\u7cfb\u7684\u4eba\u201d\uff0c\u6bd4\u5982\u8bf4\u7236\u6bcd\u3001\u4eb2\u5c5e\u3001\u670b\u53cb\u7b49\u7b49\uff0c\u4e00\u4e2a\u4e2a\u5168\u90fd\u4f1a\u6a2a\u906d\u4e0d\u5e78\uff0c\u9709\u8fd0\u5f53\u5934\uff0c\u51fa\u95e8\u88ab\u8f66\u649e\uff0c\u5e73\u5730\u4f1a\u6454\u8de4\uff0c\u5403\u996d\u90fd\u88ab\u564e\uff0c\u505a\u751f\u610f\u80af\u5b9a\u7834\u4ea7\u3002\u82e5\u53ea\u662f\u666e\u901a\u6765\u5f80\u8fd8\u597d\u8bf4\uff0c\u5927\u4e0d\u4e86\u7834\u70b9\u8d22\uff0c\u9022\u70b9\u707e\uff0c\u4f46\u82e5\u5173\u7cfb\u5bc6\u5207\uff0c\u90a3\u5c31\u5927\u96be\u4e34\u5934\uff0c\u751a\u81f3\u6709\u6027\u547d\u4e4b\u5fe7\u3002</p><p>\u8fd9\u5176\u4e2d\u6700\u5173\u952e\u7684\u4e00\u70b9\uff0c\u5c31\u662f\u4e0d\u80fd\u201c\u53d7\u4eba\u6069\u60e0\u201d\uff0c\u4e5f\u4e0d\u80fd\u201c\u4e88\u4eba\u6069\u60e0\u201d\uff0c\u5854\u7c73\u5a05\u53d7\u795e\u59ec\u59d4\u6258\uff0c\u5de1\u89c6\u8fd9\u6761\u68ee\u6797\u91cc\u7684\u5b89\u5168\u901a\u9053\uff0c\u4fdd\u62a4\u5f80\u6765\u65c5\u4eba\uff0c\u6536\u53d6\u62a5\u916c\uff0c\u8fd9\u662f\u4e00\u4efd\u5de5\u4f5c\uff0c\u4e24\u4e0d\u76f8\u6b20\uff1b\u5f80\u6765\u65c5\u8005\u53d7\u5176\u4fdd\u62a4\uff0c\u4f46\u5df2\u7ecf\u53e6\u5916\u6709\u4eba\u4ed8\u8fc7\u94b1\u4e86\uff0c\u6240\u4ee5\u4e5f\u8c08\u4e0d\u4e0a\u6069\u60e0\u3002\u521a\u624d\u743c\u6069\u548c\u73ca\u5609\u8d70\u51fa\u5b89\u5168\u8303\u56f4\uff0c\u88ab\u9e70\u86c7\u88ad\u51fb\uff0c\u5854\u7c73\u5a05\u51fa\u624b\uff0c\u8fd8\u53ef\u4ee5\u7b97\u662f\u5de5\u4f5c\u5185\u5bb9\u7684\u4e00\u90e8\u5206\uff0c\u6240\u4ee5\u5979\u672c\u6253\u7b97\u8f6c\u8eab\u5c31\u8d70\uff0c\u5c3d\u91cf\u4e0d\u8981\u7275\u626f\uff0c\u8c01\u6599\u9e70\u86c7\u672a\u6b7b\u5077\u88ad\uff0c\u88ab\u743c\u6069\u4e00\u51fb\u77f3\u5316\u4e86\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u5979\u5c31\u7b97\u662f\u53d7\u4e86\u743c\u6069\u7684\u6069\u60e0\uff0c\u5384\u8fd0\u4fbf\u4f1a\u7f20\u7ed5\u5230\u743c\u6069\u2014\u2014\u4ee5\u53ca\u4e0e\u4ed6\u6240\u6709\u5173\u7cfb\u4eb2\u8fd1\u7684\u4eba\u2014\u2014\u8eab\u4e0a\u3002</p><p>\u201c\u8fd8\u6709\u8fd9\u79cd\u8be1\u5f02\u7684\u8bc5\u5492\uff1f\u201d</p><p>\u5728\u8fd9\u4e2a\u9b54\u6cd5\u4e16\u754c\uff0c\u8bc5\u5492\u4e0d\u7a00\u5947\uff0c\u4f46\u5927\u591a\u90fd\u662f\u76f4\u63a5\u4f24\u5bb3\u76ee\u6807\uff0c\u6bd4\u5982\u8ba9\u4eba\u751f\u75c5\u3001\u8870\u8001\u3001\u75bc\u75db\uff0c\u7b49\u7b49\uff0c\u8ba9\u4eba\u5384\u8fd0\u7f20\u8eab\u7684\u6781\u5c11\uff0c\u6bd5\u7adf\u201c\u547d\u8fd0\u201d\u4e00\u9053\uff0c\u5411\u6765\u88ab\u8ba4\u4e3a\u662f\u8fde\u8bf8\u795e\u90fd\u65e0\u6cd5\u638c\u63e1\u7684\u9886\u57df\uff0c\u800c\u4e14\u6309\u7167\u5854\u7c73\u5a05\u7684\u8bf4\u6cd5\uff0c\u8fd9\u79cd\u8bc5\u5492\u8fd8\u6709\u4f20\u67d3\u529f\u80fd\uff0c\u4eff\u4f5b\u761f\u75ab\u4e00\u822c\uff0c\u8fd9\u5c31\u66f4\u4e0d\u53ef\u601d\u8bae\u4e86\u3002</p><p>\u82e5\u662f\u6309\u7167\u5854\u7c73\u5a05\u7684\u8bf4\u6cd5\uff0c\u743c\u6069\u8fd9\u4e00\u884c\u4eba\u73b0\u5728\u5df2\u7ecf\u88ab\u5384\u8fd0\u7f20\u4e0a\uff0c\u63a5\u4e0b\u6765\u5fc5\u5b9a\u591a\u707e\u591a\u96be\uff0c\u8bf4\u4e0d\u5b9a\u597d\u7aef\u7aef\u5730\u8d70\u5728\u8def\u4e0a\u90fd\u4f1a\u88ab\u96f7\u5288\u3002\u8fd9\u662f\u795e\u7684\u8bc5\u5492\uff0c\u5979\u4e5f\u6ca1\u529e\u6cd5\u633d\u56de\uff0c\u53ea\u80fd\u63d0\u9192\uff0c\u8ba9\u743c\u6069\u7b49\u4eba\u5c3d\u91cf\u6ce8\u610f\uff0c\u81ea\u6c42\u591a\u798f\u3002</p><p>\u201c\u60c5\u51b5\u5c31\u662f\u8fd9\u6837\uff0c\u201d\u5854\u7c73\u5a05\u8d77\u8eab\uff0c\u201c\u4ece\u8fd9\u91cc\u5230\u96c5\u5c14\u8d1d\u7433\u5a1c\u57ce\u8fd8\u6709\u516d\u5929\u8def\u7a0b\uff0c\u6211\u4f1a\u6cbf\u9014\u8ddf\u968f\uff0c\u53ea\u8981\u4e0d\u504f\u79bb\u8def\u7ebf\uff0c\u5c31\u4e0d\u4f1a\u51fa\u73b0\u4eca\u665a\u8fd9\u6837\u7684\u5371\u9669\u2014\u2014\u4e0d\u8fc7\u4e5f\u5f88\u96be\u8bf4\uff0c\u88ab\u5384\u8fd0\u7f20\u4f4f\u7684\u4eba\u603b\u662f\u4f1a\u51fa\u73b0\u5404\u79cd\u610f\u5916\uff0c\u4f60\u4eec\u8981\u591a\u52a0\u5c0f\u5fc3\u3002\u201d</p><p>\u4e0d\u7b49\u743c\u6069\u56de\u5e94\uff0c\u5979\u5f84\u76f4\u8d70\u56de\u4e86\u9ed1\u6697\u4e4b\u4e2d\u3002</p><p>\u5854\u7c73\u5a05\u79bb\u5f00\u540e\uff0c\u5927\u5bb6\u4e0d\u7531\u5f97\u8bae\u8bba\u7eb7\u7eb7\uff0c\u4f1a\u4f20\u67d3\u7684\u5384\u8fd0\u8bc5\u5492\u4e00\u8bf4\uff0c\u5b9e\u5728\u8d85\u4e4e\u60f3\u8c61\uff0c\u8ba9\u4eba\u96be\u4ee5\u7f6e\u4fe1\u3002\u800c\u4e14\u8bc5\u5492\u867d\u7136\u65e0\u5f62\u65e0\u5f71\uff0c\u666e\u901a\u4eba\u770b\u4e0d\u89c1\uff0c\u4f46\u5728\u9ad8\u660e\u7684\u5deb\u5e08\u773c\u91cc\uff0c\u5c31\u50cf\u6e38\u620f\u91cc\u4e00\u4e2a\u4eba\u5934\u4e0a\u663e\u793a\u5404\u79cd\u8d1f\u9762\u72b6\u6001\uff0c\u8fd8\u662f\u6709\u8ff9\u53ef\u5faa\u7684\uff0c\u800c\u5854\u7c73\u5a05\u8eab\u4e0a\u5b8c\u5168\u770b\u4e0d\u51fa\u88ab\u8bc5\u5492\u7684\u6837\u5b50\u3002\u4f46\u5979\u8a00\u4e4b\u51ff\u51ff\uff0c\u795e\u60c5\u614e\u91cd\uff0c\u53c8\u4e0d\u4f3c\u5f00\u73a9\u7b11\uff0c\u800c\u4e14\u4e5f\u5b8c\u5168\u6ca1\u8fd9\u4e2a\u5fc5\u8981\u3002</p><p>\u7ef4\u82e5\u62c9\u5012\u4e0d\u662f\u5f88\u5728\u610f\u4ec0\u4e48\u5384\u8fd0\u4e0d\u5384\u8fd0\u7684\u95ee\u9898\uff0c\u5979\u66f4\u5173\u5fc3\u5854\u7c73\u5a05\u7a76\u7adf\u662f\u7528\u4e86\u4ec0\u4e48\u624b\u6cd5\uff0c\u4e0d\u58f0\u4e0d\u54cd\u5730\u5c31\u6784\u5efa\u51fa\u4e00\u4e2a\u534a\u4f4d\u9762\uff0c\u4e0d\u4ec5\u80fd\u6321\u4f4f\u68ee\u6797\u91cc\u7684\u90a3\u4e9b\u91ce\u517d\uff0c\u8fde\u5979\u8fd9\u79cd\u5927\u5deb\u5e08\u90fd\u6ca1\u5bdf\u89c9\u3002\u5c31\u7b97\u662f\u73b0\u5728\u77e5\u9053\u4e86\uff0c\u8ba4\u771f\u68c0\u67e5\uff0c\u4e5f\u53ea\u80fd\u9690\u7ea6\u770b\u51fa\u70b9\u75d5\u8ff9\uff0c\u7aa5\u4e0d\u51fa\u95e8\u9053\u3002\u5bf9\u4e8e\u4e00\u4e2a\u5145\u6ee1\u7814\u7a76\u6b32\u7684\u5deb\u5e08\u6765\u8bf4\uff0c\u4e0d\u641e\u660e\u767d\u8fd9\u5176\u4e2d\u5965\u79d8\uff0c\u53ea\u6015\u4eca\u665a\u90fd\u8981\u7761\u4e0d\u7740\u3002</p><p>\u201c\u90a3\u4e9b\u77f3\u5934\u3002\u201d\u7fe1\u7fe0\u5973\u5deb\u5ffd\u7136\u8bf4\u3002</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u7fe1\u7fe0\u5973\u5deb\u4f38\u624b\u5411\u524d\u6307\uff0c\u5176\u4ed6\u4eba\u987a\u7740\u671b\u53bb\uff0c\u53ea\u770b\u89c1\u51e0\u5757\u77f3\u5934\uff0c\u534a\u6df1\u4e0d\u6d45\u5730\u57cb\u5728\u6ce5\u571f\u91cc\u3002\u68ee\u6797\u4e4b\u4e2d\uff0c\u77f3\u5757\u4e0d\u7b97\u5e38\u89c1\uff0c\u4f46\u4e5f\u5e76\u4e0d\u663e\u773c\uff0c\u8c01\u4e5f\u6ca1\u6709\u6ce8\u610f\u3002\u73b0\u5728\u88ab\u7fe1\u7fe0\u5973\u5deb\u63d0\u9192\uff0c\u4ed4\u7ec6\u518d\u770b......\u4ecd\u7136\u770b\u4e0d\u51fa\u4ec0\u4e48\u95ee\u9898\u3002</p><p>\u201c\u6211\u597d\u50cf\u8bb0\u5f97\u6709\u79cd\u5deb\u672f\uff0c\u5176\u8fd0\u4f5c\u539f\u7406\u4e0d\u660e\uff0c\u901a\u5e38\u662f\u4ee5\u4e00\u4e9b\u5e38\u89c1\u7684\u7269\u4f53\uff0c\u8b6c\u5982\u77f3\u5757\u3001\u6811\u6728\u3001\u82b1\u8349\u7b49\u8fdb\u884c\u5e03\u7f6e\uff0c\u518d\u501f\u52a9\u4e00\u5b9a\u7684\u5730\u7406\u73af\u5883\uff0c\u8b6c\u5982\u6cb3\u6d41\u3001\u5c71\u5cb3\u4e4b\u7c7b\uff0c\u4f1a\u8fbe\u5230\u4e00\u79cd\u7c7b\u4f3c\u4e8e\u8ff7\u5bab\u672f\u7684\u9b54\u6cd5\u6548\u679c\u3002\u201d</p><p>\u201c\u542c\u8d77\u6765\u5f88\u6709\u8da3\uff0c\u4f60\u80fd\u6f14\u793a\u4e00\u4e0b\u5417\uff1f\u201d</p><p>\u201c\u4e0d\u884c\uff0c\u6211\u53ea\u662f\u77e5\u9053\u6709\u8fd9\u6837\u4e00\u79cd\u5deb\u672f\uff0c\u4f46\u662f......\u5b8c\u5168\u6ca1\u6709\u8bb0\u5fc6\uff0c\u6211\u81ea\u5df1\u5e94\u8be5\u662f\u4e0d\u4f1a\u3002\u201d</p><p>\u7fe1\u7fe0\u5973\u5deb\u5f97\u4e86\u5931\u5fc6\u75c7\uff0c\u5979\u7684\u8bb0\u5fc6\u4e00\u76f4\u6ca1\u6709\u771f\u6b63\u6062\u590d\uff0c\u8bf4\u4e0d\u6e05\u81ea\u5df1\u662f\u8c01\uff0c\u4e5f\u4e0d\u77e5\u9053\u6765\u5386\uff0c\u8111\u4e2d\u7ecf\u5e38\u95ea\u8fc7\u4e00\u4e9b\u573a\u666f\u7247\u6bb5\uff0c\u6216\u8005\u7a81\u5140\u5730\u5192\u51fa\u4e00\u4e9b\u4e1c\u897f\uff0c\u8f6c\u77ac\u5373\u901d\u3002\u7ef4\u82e5\u62c9\u77e5\u9053\u5979\u7684\u60c5\u51b5\uff0c\u4e5f\u5c31\u6ca1\u6709\u518d\u8ffd\u95ee\u3002\u5012\u662f\u743c\u6069\u82e5\u6709\u6240\u609f\uff0c\u4ed6\u8bb0\u5f97\u521a\u624d\u5854\u7c73\u5a05\u7684\u786e\u662f\u7ffb\u52a8\u4e86\u4e00\u5757\u77f3\u5934\uff0c\u5c31\u89e6\u53d1\u4e86\u6cd5\u672f\u3002\u800c\u4e14\u7fe1\u7fe0\u5973\u5deb\u8fd9\u79cd\u63cf\u8ff0\uff0c\u5012\u662f\u548c\u4ed6\u8bb0\u5fc6\u4e2d\u7684\u67d0\u4e2a\u4e1c\u897f\u5f88\u6709\u76f8\u4f3c\u4e4b\u5904\u3002</p><p>\u8ba8\u8bba\u4e86\u4e00\u4f1a\uff0c\u770b\u770b\u65f6\u95f4\u4e5f\u4e0d\u65e9\u4e86\uff0c\u5927\u5bb6\u4fbf\u8fdb\u5e10\u7bf7\u4f11\u606f\u3002</p><p>\u7b2c\u4e8c\u5929\u7684\u884c\u7a0b\u6ce2\u6f9c\u4e0d\u60ca\uff0c\u4e5f\u6ca1\u6709\u4e0b\u96e8\uff0c\u66f4\u6ca1\u6709\u8def\u4e0a\u6454\u8de4\uff0c\u5230\u4e86\u508d\u665a\uff0c\u4f9d\u65e7\u662f\u5148\u627e\u5230\u6807\u8bb0\uff0c\u7136\u540e\u5bbf\u8425\uff0c\u5347\u8d77\u7bdd\u706b\u3002\u665a\u9910\u662f\u534a\u8def\u4e0a\u6293\u7684\u4e00\u53ea\u65e0\u89d2\u7f8a\uff0c\u7531\u53d8\u6210\u79d8\u5076\u7684\u6b66\u50e7\u9686\u5947\u8d1f\u8d23\u70f9\u996a\uff0c\u5927\u5bb6\u90fd\u5728\u5fd9\u788c\uff0c\u743c\u6069\u9009\u62e9\u4e00\u4e2a\u7a7a\u65f7\u7684\u5730\u65b9\u5c55\u5f00\u6728\u5c4b\uff0c\u7136\u540e\u5077\u7a7a\u8d70\u5230\u4e00\u65c1\uff0c\u671d\u7740\u6797\u95f4\u65e0\u4eba\u5904\u8bf4\uff1a\u201c\u4e00\u8d77\u665a\u9910\u5427\uff0c\u5854\u7c73\u5a05\u5c0f\u59d0\u3002\u201d</p><p>\u6ca1\u6709\u52a8\u9759\u3002</p><p>\u743c\u6069\u53c8\u91cd\u590d\u4e86\u4e00\u904d\uff0c\u51e0\u79d2\u949f\u540e\u6709\u4e86\u56de\u5e94\uff0c\u58f0\u97f3\u662f\u4ece\u5934\u9876\u4e0a\u4f20\u6765\uff0c\u201c\u8c22\u8c22\uff0c\u4e0d\u5fc5\u4e86\u3002\u201d</p><p>\u4ed6\u4ef0\u8d77\u5934\uff0c\u770b\u89c1\u9ed1\u8863\u7684\u5973\u5b50\u5750\u5728\u4e00\u6839\u957f\u957f\u7684\u679d\u4e2b\u4e0a\uff0c\u80cc\u9760\u7740\u6811\u5e72\uff0c\u53cc\u81c2\u62b1\u819d\uff0c\u770b\u7740\u8fdc\u5904\u3002\u743c\u6069\u770b\u4e86\u770b\u9ad8\u5ea6\uff0c\u89c9\u5f97\u81ea\u5df1\u80af\u5b9a\u722c\u4e0d\u4e0a\u53bb\uff0c\u4e8e\u662f\u6084\u6084\u6fc0\u53d1\u4e86\u53e3\u4ee4\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u4ed6\u9661\u7136\u51fa\u73b0\u5728\u5854\u7c73\u5a05\u7684\u8eab\u65c1\u3002</p><p>\u9ed1\u8863\u7684\u5973\u5b50\u77a5\u4e86\u4ed6\u4e00\u773c\u3002</p><p>\u201c\u6211\u8bb0\u5f97\u6628\u665a\u5df2\u7ecf\u544a\u8bc9\u8fc7\u4f60\u4e86\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u4f1a\u7ed9\u4eba\u5e26\u6765\u5384\u8fd0\uff0c\u5927\u5bb6\u8fd8\u662f\u5c3d\u91cf\u4fdd\u6301\u8ddd\u79bb\u6bd4\u8f83\u597d\u3002\u201d</p><p>\u201c\u6211\u542c\u5230\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u4f60\u4e0d\u662f\u8bf4\uff0c\u5384\u8fd0\u5df2\u7ecf\u7f20\u4e0a\u6211\u4e86\u5417\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u591a\u4e00\u70b9\u5c11\u4e00\u70b9\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u5173\u7cfb\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u76f8\u4fe1\u6211\u8bf4\u7684\u5384\u8fd0\u8bc5\u5492\uff1f\u201d</p><p>\u201c\u76f8\u4fe1\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6b63\u56e0\u4e3a\u76f8\u4fe1\uff0c\u6240\u4ee5\u6211\u4eec\u624d\u66f4\u5e94\u8be5\u540c\u5fc3\u534f\u529b\uff0c\u800c\u4e0d\u662f\u5750\u4ee5\u5f85\u6bd9\u3002\u6211\u548c\u6211\u7684\u540c\u4f34\u5bf9\u9b54\u6cd5\u4e5f\u7b97\u6709\u6240\u4e86\u89e3\uff0c\u8bf4\u4e0d\u5b9a\u53ef\u4ee5\u5e2e\u5fd9\u7834\u89e3\u6389\u8fd9\u4e2a\u8bc5\u5492\u3002\u201d</p><p>\u5854\u7c73\u5a05\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u6211\u77e5\u9053\u4f60\uff0c\u8fd8\u6709\u90a3\u4f4d\u7ef4\u82e5\u62c9\u5c0f\u59d0\uff0c\u90fd\u662f\u5f88\u5389\u5bb3\u7684\u5deb\u5e08\u3002\u4f46\u6211\u8eab\u4e0a\u7684\u8bc5\u5492\u6e90\u81ea\u4e00\u4f4d\u90aa\u795e\uff0c\u5e76\u4e0d\u662f\u51e1\u4eba\u6240\u80fd\u7834\u89e3\u7684\u3002\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4e5f\u66fe\u7ecf\u53bb\u8fc7\u585e\u5c14\uff0c\u8bf7\u7ea2\u888d\u5deb\u5e08\u76f8\u52a9\uff0c\u4f46\u4ed6\u4eec\u4e5f\u675f\u624b\u65e0\u7b56\u3002\u6240\u4ee5\uff0c\u627f\u8499\u76db\u60c5\uff0c\u6211\u975e\u5e38\u611f\u8c22\uff0c\u4f46\u8fd8\u662f\u4e0d\u5fc5\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u53ef\u4e0d\u4e00\u5b9a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u77e5\u9053\u5854\u7c73\u5a05\u5c0f\u59d0\u6709\u6ca1\u6709\u542c\u8bf4\u8fc7\uff0c\u5728\u9065\u8fdc\u4e1c\u65b9\u7684\u7fd4\u9f99\u5e1d\u56fd\uff0c\u6709\u8fd9\u6837\u4e00\u53e5\u8c1a\u8bed\uff1a\u4e09\u4e2a\u76ae\u9769\u5320\u4eba\u7d2f\u79ef\u5728\u4e00\u8d77\uff0c\u5c31\u80fd\u80dc\u8fc7\u6700\u806a\u660e\u7684\u667a\u8005\uff1b\u53c8\u6709\u8c1a\u8bed\u8bf4\uff1a\u4e0a\u5929\u4e0d\u4f1a\u628a\u4eba\u7684\u8def\u5b8c\u5168\u65ad\u7edd\uff0c\u603b\u4f1a\u7559\u4e00\u70b9\u673a\u4f1a\u3002\u5927\u5bb6\u4e00\u8d77\u5546\u91cf\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u6709\u4ec0\u4e48\u529e\u6cd5\u5462\u3002\u201d</p><p>\u5854\u7c73\u5a05\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u201c\u6211\u4e0d\u4e86\u89e3\u7fd4\u9f99\u5e1d\u56fd\uff0c\u4f60\u8bf4\u7684\u90a3\u4e24\u6761\u8c1a\u8bed\uff0c\u6211\u4e5f\u4ece\u6ca1\u542c\u8bf4\u8fc7\u3002\u201d</p><p>\u201c\u54e6\uff0c\u662f\u5417\u3002\u201d</p><p>\u5854\u7c73\u5a05\u8f6c\u8fc7\u8138\uff0c\u770b\u4e86\u743c\u6069\u4e00\u4f1a\uff0c\u5ffd\u7136\u7b11\u8d77\u6765\uff1a\u201c\u6211\u8bf4\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u4f60\u8be5\u4e0d\u4f1a\u662f\u770b\u4e0a\u6211\u4e86\u5427\uff1f\u201d</p><p>\u201c......\u201d</p><p>\u51e0\u4e4e\u662f\u4e0b\u610f\u8bc6\u5730\u60f3\u8981\u5426\u8ba4\uff0c\u4f46\u6700\u540e\u5fcd\u4f4f\u4e86\uff0c\u56e0\u4e3a\u89c9\u5f97\u592a\u4e0d\u793c\u8c8c\u3002\u7136\u800c\u4ed6\u7684\u6c89\u9ed8\u4f3c\u4e4e\u9020\u6210\u4e86\u5854\u7c73\u5a05\u7684\u8bef\u89e3\uff0c\u3000\u3000 \u201c\u6709\u4ef6\u4e8b\u60c5\u6211\u4e0d\u5f97\u4e0d\u8bf4\u660e\u4e00\u4e0b\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u8ffd\u6c42\u8fc7\u6211\u7684\u7537\u4eba\u6709\u5f88\u591a\uff0c\u4f46\u4ed6\u4eec\u7684\u4e0b\u573a\u53ef\u90fd\u4e0d\u592a\u597d\u3002\u201d</p><p>\u201c\u591a\u8c22\u63d0\u9192\uff0c\u4e0d\u8fc7\u6211\u5df2\u7ecf\u6709\u5973\u670b\u53cb\u4e86\u3002\u201d</p><p>\u201c\u6211\u770b\u5230\u4e86\uff0c\u201d\u5854\u7c73\u5a05\u70b9\u5934\uff0c\u201c\u800c\u4e14\u8fd8\u4e0d\u6b62\u4e00\u4f4d\uff0c\u5979\u4eec\u90fd\u5f88\u6f02\u4eae\u3002\u201d</p><p>\u201c\u8c22\u8c22\u3002\u201d</p><p>\u201c\u6240\u4ee5\u4f60\u771f\u7684\u4e0d\u662f\u6253\u7b97\u8ffd\u6c42\u6211\uff1f\u201d</p><p>\u5766\u767d\u5730\u8bf4\uff0c\u743c\u6069\u8fd8\u771f\u7684\u6ca1\u6709\u52a8\u8fc7\u8fd9\u4e2a\u5ff5\u5934\u3002\u8eab\u8fb9\u7cbe\u81f4\u7f8e\u4e3d\u7684\u5973\u5b69\u5b50\u592a\u591a\uff0c\u666e\u901a\u4e00\u70b9\u7684\u5df2\u7ecf\u5f88\u96be\u5438\u5f15\u4ed6\u7684\u6ce8\u610f\uff0c\u5854\u7c73\u5a05\u7684\u8eab\u6750\u5012\u662f\u5f88\u4e0d\u9519\uff0c\u4f46\u76f8\u8c8c\u53ea\u80fd\u8bf4\u662f\u5e73\u51e1\uff0c\u5c31\u7b97\u662f\u606d\u7ef4\uff0c\u4e5f\u5f88\u96be\u8bf4\u5979\u662f\u4f4d\u7f8e\u4eba\u513f\u3002\u81f3\u4e8e\u5979\u8bf4\u6709\u5f88\u591a\u7537\u4eba\u8ffd\u6c42......\u5927\u6982\u662f\u6069\u745f\u4eba\u7684\u5ba1\u7f8e\u89c2\u4e0e\u4e2d\u571f\u5927\u9646\u6709\u522b\u5427\u3002</p><p>\u201c\u6682\u65e0\u6b64\u610f\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u5355\u7eaf\u5730\u662f\u5e0c\u671b\u548c\u5854\u7c73\u5a05\u5c0f\u59d0\u6210\u4e3a\u670b\u53cb\u7f62\u4e86\u3002\u51fa\u95e8\u5728\u5916\uff0c\u5b88\u671b\u76f8\u52a9\uff0c\u4e0d\u662f\u5f88\u6b63\u5e38\u7684\u5417\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u201d\u5854\u7c73\u5a05\u6258\u7740\u816e\u60f3\u4e86\u51e0\u79d2\u949f\uff0c\u201c\u542c\u8d77\u6765\u4e5f\u6709\u9053\u7406\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u6211\u5c31\u4e0d\u63a8\u8f9e\u4e86\u3002\u201d</p><p><strong>*</strong>*</p><p>\u5854\u7c73\u5a05\u7684\u52a0\u5165\u53d7\u5230\u4e00\u81f4\u7684\u6b22\u8fce\uff0c\u5c24\u5176\u662f\u51db\uff0c\u201c\u6211\u4eec\u73b0\u5728\u6709\u5341\u4e2a\u4eba\u4e86\uff0c\u201d\u5979\u5174\u81f4\u52c3\u52c3\u5730\u8bf4\uff0c\u201c\u743c\u6069\uff0c\u4f60\u6628\u5929\u8bf4\u6709\u6269\u5c55\u804c\u4e1a\uff0c\u4ec0\u4e48\u7231\u795e\u5c04\u624b\u3001\u767d\u72fc\u738b\u4e4b\u7c7b\uff0c\u662f\u4e0d\u662f\u53ef\u4ee5\u62ff\u51fa\u6765\u4e86\u3002\u201d</p><p>\u201c\u4f60\u5148\u628a\u57fa\u7840\u804c\u4e1a\u73a9\u597d\u518d\u8bf4\u5427\u3002\u201d</p><p>\u201c\u53ef\u662f\u6211\u60f3\u73a9\u65b0\u804c\u4e1a\u561b\u3002\u201d</p><p>\u201c\u65b0\u804c\u4e1a\u591a\u7684\u662f\uff0c\u4f46\u4eba\u5c11\u6ca1\u6cd5\u73a9\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u6bd4\u5982\u50cf\u5439\u7b1b\u4eba\u3001\u91ce\u5b69\u5b50\u3001\u4e24\u59d0\u59b9\u4e4b\u7c7b\uff0c\u81f3\u5c11\u8981\u51d1\u9f50\u5341\u4e09\u4e2a\u4eba\uff0c\u4e00\u4e2a\u4e3b\u6301\u8005\u52a0\u5341\u4e8c\u4e2a\u73a9\u5bb6\uff0c\u624d\u80fd\u73a9\u5f97\u8d77\u6765\u3002\u201d</p><p>\u201c\u90a3\u4f60\u8d76\u7d27\u518d\u53bb\u591a\u52fe\u642d\u51e0\u4e2a\u5973\u5b69\u5b50\u56de\u6765\u554a\uff0c\u201d\u51db\u62b1\u6028\uff0c\u201c\u771f\u662f\u7684\uff0c\u4e00\u5929\u5230\u665a\u5439\u5618\u8981\u5efa\u7acb\u540e\u5bab\uff0c\u5230\u73b0\u5728\u8fde\u5341\u4e8c\u4e2a\u5973\u5b69\u5b50\u90fd\u6ca1\u51d1\u9f50\uff0c\u4e00\u8d77\u73a9\u6e38\u620f\u90fd\u4e0d\u80fd\u597d\u597d\u73a9\uff0c\u592a\u5dee\u52b2\u4e86\u3002\u201d</p><p>\u201c......\u771f\u62b1\u6b49\u8ba9\u4f60\u5931\u671b\uff0c\u6211\u4e00\u5b9a\u4f1a\u7ee7\u7eed\u52aa\u529b\u7684\u3002\u201d</p><p>\u5230\u5904\u82b1\u5fc3\u4e71\u641e\u4e0d\u4ec5\u6ca1\u6709\u88ab\u8ffd\u780d\uff0c\u53cd\u800c\u88ab\u5973\u5b69\u5b50\u4e3b\u52a8\u8981\u6c42\u5efa\u7acb\u540e\u5bab\uff0c\u8fd9\u7b80\u76f4\u662f\u5947\u5e7b\u5c0f\u8bf4\u4e3b\u89d2\u624d\u6709\u7684\u5f85\u9047\uff0c\u7136\u800c\u4e00\u60f3\u5230\u51fa\u53d1\u70b9\u662f\u4e3a\u4e86\u201c\u73a9\u72fc\u4eba\u6740\u7684\u65f6\u5019\u80fd\u591f\u51d1\u591f\u4eba\u6570\u201d\uff0c\u8fd9\u5c31\u8ba9\u743c\u6069\u6709\u79cd\u8352\u8bde\u611f\uff0c\u603b\u89c9\u5f97......\u597d\u50cf\u81ea\u5df1\u7684\u4ef7\u503c\u88ab\u5b8c\u5168\u5426\u5b9a\u4e86\u4f3c\u7684\u3002</p><p>\u201c\u54ea\u6709\uff0c\u4f60\u4e5f\u662f\u5f88\u6709\u4ef7\u503c\u7684\uff0c\u53ef\u4ee5\u505a\u4e3b\u6301\u4eba\uff0c\u770b\u7740\u6211\u4eec\u73a9\u6e38\u620f\u554a\u3002\u201d</p><p>\u201c\u6df1\u611f\u8363\u5e78\u3002\u201d</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u8fd9\u4e5f\u7b97\u662f\u5f97\u5230\u5149\u660e\u6b63\u5927\u7684\u201c\u8bb8\u53ef\u201d\u4e86\u3002\u5728\u521b\u5efa\u540e\u5bab\u8fd9\u4ef6\u4e8b\u60c5\u4e0a\uff0c\u51db\u5df2\u7ecf\u6295\u4e86\u8d5e\u6210\u7968\u2014\u2014\u867d\u7136\u5979\u672c\u6765\u4e5f\u5c31\u4f3c\u4e4e\u6ca1\u53cd\u5bf9\u8fc7\u3002\u603b\u4e4b\u662f\u4e2a\u597d\u6d88\u606f\u3002</p><p>\u201c\u6240\u4ee5\u540e\u5bab\u7684\u4eba\u6570\u5c31\u662f\u5341\u4e8c\u4e2a\u5bf9\u5427\u3002\u201d\u743c\u6069\u534a\u5f00\u73a9\u7b11\u5730\u786e\u8ba4\u3002</p><p>\u201c\u591a\u51e0\u4e2a\u4e5f\u6ca1\u5173\u7cfb\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u4e07\u4e00\u6709\u4eba\u4e34\u65f6\u6709\u4e8b\u4e0d\u80fd\u53c2\u52a0\u600e\u4e48\u529e\uff0c\u603b\u8981\u6709\u540e\u5907\u8865\u5145\u561b\u3002\u201d</p><p>\u4f60\u8fd8\u771f\u662f\u8003\u8651\u5468\u5168\u3002</p><p>\u5bf9\u4e8e\u743c\u6069\u8fd9\u4e2a\u961f\u4f0d\u6765\u8bf4\uff0c\u5854\u7c73\u5a05\u662f\u4e2a\u5b8c\u5168\u7684\u964c\u751f\u4eba\uff0c\u867d\u7136\u5f7c\u6b64\u90fd\u62b1\u6709\u5584\u610f\uff0c\u4e00\u5f00\u59cb\u7684\u6c14\u6c1b\u8fd8\u662f\u96be\u514d\u6709\u4e9b\u751f\u786c\u3002\u4f46\u73a9\u6e38\u620f\u7684\u786e\u80fd\u591f\u6709\u6548\u5730\u62c9\u8fd1\u8ddd\u79bb\uff0c\u6253\u5b8c\u51e0\u5c40\u72fc\u4eba\u6740\uff0c\u76f8\u5904\u8d77\u6765\u987f\u65f6\u4fbf\u81ea\u5728\u4e86\u8bb8\u591a\u3002</p><p>\u521a\u8ba4\u8bc6\u7684\u65f6\u5019\uff0c\u5854\u7c73\u5a05\u7684\u786e\u7ed9\u4eba\u4e00\u79cd\u201c\u4e0d\u8981\u63a5\u8fd1\u201d\u7684\u51b7\u6f20\u4e4b\u611f\uff0c\u4f46\u7a0d\u5fae\u719f\u6089\u4e4b\u540e\uff0c\u4fbf\u53d1\u73b0\u8fd9\u5e94\u8be5\u662f\u5047\u8c61\uff0c\u6216\u8005\u8bf4\u662f\u4f2a\u88c5\u3002\u53ea\u8981\u5378\u4e0b\u9632\u5907\uff0c\u5979\u5176\u5b9e\u662f\u4e2a\u5f88\u6b63\u5e38\u2014\u2014\u6216\u8005\u8bf4\u5f88\u666e\u901a\u2014\u2014\u7684\u5973\u5b50\uff0c\u4f1a\u804a\u5929\uff0c\u4f1a\u8bf4\u7b11\uff0c\u81f3\u5c11\u8868\u9762\u4e0a\u770b\u8d77\u6765\uff0c\u8fd8\u662f\u5f88\u5f00\u5fc3\u7684\u3002\u89c1\u6b64\u60c5\u5f62\uff0c\u743c\u6069\u4e5f\u4e0d\u7531\u5f97\u6697\u81ea\u5410\u4e86\u53e3\u6c14\uff0c\u89c9\u5f97\u81ea\u5df1\u4f3c\u4e4e\u662f\u505a\u4e86\u4ef6\u6b63\u786e\u7684\u4e8b\u3002</p><p>\u201c\u6765\uff0c\u6211\u5148\u5e2e\u4f60\u68c0\u67e5\u4e00\u4e0b\u3002\u201d</p><p>\u73a9\u4e86\u51e0\u5c40\u4e4b\u540e\uff0c\u5927\u5bb6\u4f11\u606f\u3002\u7ef4\u82e5\u62c9\u4ece\u6b21\u5143\u888b\u91cc\u53d6\u51fa\u4e00\u5806\u4e1c\u897f\uff0c\u5377\u8f74\u3001\u6cd5\u5668\u3001\u5b9d\u77f3\uff0c\u4ee5\u53ca\u4e00\u4e9b\u5947\u602a\u7684\u91d1\u5c5e\uff0c\u7d27\u63a5\u7740\u5728\u865a\u7a7a\u4e2d\u7ed8\u56fe\uff0c\u6784\u5efa\u9b54\u6cd5\u9635\uff0c\u8ba9\u5854\u7c73\u5a05\u5f85\u5728\u4e2d\u5fc3\uff0c\u7136\u800c\u5979\u53cd\u590d\u68c0\u67e5\u4e86\u51e0\u6b21\uff0c\u53c8\u53ec\u51fa\u8679\u96fe\uff0c\u5411\u5386\u4ee3\u4f20\u9053\u5deb\u5e08\u5bfb\u6c42\u6307\u70b9\uff0c\u6700\u540e\u8fd8\u662f\u4e00\u65e0\u6240\u83b7\uff0c\u751a\u81f3\u770b\u4e0d\u51fa\u5854\u7c73\u5a05\u4e2d\u4e86\u8bc5\u5492\uff0c\u66f4\u4e0d\u8981\u8bf4\u5982\u4f55\u7834\u89e3\u4e86\u3002</p><p>\u5bf9\u6b64\u7ed3\u679c\uff0c\u5854\u7c73\u5a05\u5e76\u4e0d\u610f\u5916\uff0c\u5979\u5fae\u7b11\u7740\u6447\u6447\u5934\uff1a\u201c\u6211\u4e2d\u7684\u8bc5\u5492\uff0c\u53ef\u80fd\u548c\u4f60\u4eec\u4e86\u89e3\u7684\u4e0d\u592a\u4e00\u6837\uff0c\u7528\u4f60\u4eec\u7684\u65b9\u6cd5\u662f\u68c0\u6d4b\u4e0d\u51fa\u6765\u7684\u3002\u201d</p><p>\u5750\u5728\u706b\u5806\u8fb9\uff0c\u559d\u7740\u51db\u7684\u996e\u6599\uff0c\u5854\u7c73\u5a05\u8be6\u7ec6\u8bb2\u8ff0\u5979\u7684\u201c\u5384\u8fd0\u8bc5\u5492\u201d\u3002</p><p>\u6709\u5173\u8bc5\u5492\u7684\u6765\u5386\uff0c\u5854\u7c73\u5a05\u8bed\u7109\u4e0d\u8be6\uff0c\u4f3c\u4e4e\u4e0d\u60f3\u591a\u8c08\uff0c\u53ea\u8bf4\u662f\u5728\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u5979\u7684\u4e00\u4f4d\u7956\u5148\u89e6\u6012\u4e86\u67d0\u4e2a\u90aa\u795e\uff0c\u56e0\u6b64\u906d\u7978\u3002\u8fd9\u4e2a\u8bc5\u5492\u6709\u51e0\u4e2a\u5947\u5f02\u4e4b\u5904\uff0c\u7b2c\u4e00\u70b9\u5854\u7c73\u5a05\u6628\u591c\u5df2\u7ecf\u8bf4\u8fc7\uff0c\u5b83\u4e0d\u76f4\u63a5\u4f24\u5bb3\u4e2d\u5492\u8005\uff0c\u800c\u662f\u5f71\u54cd\u5468\u56f4\u7684\u4eba\uff0c\u51e1\u662f\u4eb2\u670b\u597d\u53cb\uff0c\u5fc5\u7136\u906d\u9047\u5404\u79cd\u5384\u8fd0\uff0c\u6216\u8f7b\u6216\u91cd\uff0c\u7edd\u65e0\u5e78\u514d\uff0c\u88ab\u8bc5\u5492\u7684\u90a3\u4e2a\u4eba\u81ea\u5df1\u5374\u6ca1\u4ec0\u4e48\u4e8b\uff0c\u5f80\u5f80\u8fd8\u633a\u957f\u5bff\u3002\u6240\u4ee5\u751a\u81f3\u6709\u63a8\u6d4b\uff0c\u8fd9\u4e2a\u8bc5\u5492\u7684\u4f5c\u7528\uff0c\u5e76\u4e0d\u662f\u201c\u4f20\u67d3\u201d\u5384\u8fd0\uff0c\u800c\u662f\u5c06\u522b\u4eba\u7684\u597d\u8fd0\u6c14\u7ed9\u63a0\u593a\u8fc7\u6765\u3002\u4f46\u4ed4\u7ec6\u63a8\u6572\u4e5f\u4e0d\u592a\u5bf9\uff0c\u88ab\u8bc5\u5492\u8005\u53ea\u662f\u4e0d\u4f1a\u906d\u9047\u98de\u6765\u6a2a\u7978\uff0c\u5176\u4ed6\u4e5f\u5c31\u5e73\u5e38\uff0c\u8be5\u751f\u75c5\u4e00\u6837\u751f\u75c5\uff0c\u8be5\u8d2b\u56f0\u4e00\u6837\u8d2b\u56f0\uff0c\u8be5\u6b7b\u4e00\u6837\u6b7b\uff0c\u770b\u4e0d\u51fa\u6709\u591a\u4e48\u5e78\u8fd0\u3002</p><p>\u7b2c\u4e8c\u70b9\u5947\u5f02\u4e4b\u5904\uff0c\u662f\u8fd9\u4e2a\u8bc5\u5492\u80fd\u591f\u901a\u8fc7\u8840\u8109\u201c\u9057\u4f20\u201d\uff0c\u800c\u4e14\u4e0d\u662f\u7b80\u5355\u7684\u7236\u5b50\u76f8\u627f\u8fd9\u79cd\u6a21\u5f0f\u3002\u88ab\u8bc5\u5492\u8005\u6c38\u8fdc\u662f\u4e00\u4e2a\u4eba\uff0c\u4e0d\u4f1a\u540c\u65f6\u51fa\u73b0\u4e24\u4e2a\u4ee5\u4e0a\uff0c\u6bcf\u4e00\u4f4d\u88ab\u8bc5\u5492\u8005\u53bb\u4e16\u540e\uff0c\u8fd9\u4e2a\u8bc5\u5492\u5c31\u4f1a\u81ea\u52a8\u8f6c\u79fb\u5230\u65b0\u7684\u76ee\u6807\u8eab\u4e0a\u3002\u65b0\u7684\u76ee\u6807\u5fc5\u7136\u662f\u6709\u8840\u7f18\u5173\u7cfb\u7684\u4eba\uff0c\u4f46\u672a\u5fc5\u662f\u5b50\u5973\uff0c\u4e5f\u53ef\u80fd\u662f\u5176\u4ed6\u4eb2\u5c5e\u3002\u636e\u5854\u7c73\u5a05\u6240\u77e5\uff0c\u4e5f\u66fe\u7ecf\u6709\u88ab\u8bc5\u5492\u8005\u672c\u4eba\u662f\u72ec\u5b50\uff0c\u7ec8\u8eab\u4e0d\u5a5a\uff0c\u4e0d\u7559\u5b50\u55e3\uff0c\u670d\u6bd2\u81ea\u6740\uff0c\u60f3\u8981\u4ee5\u6b7b\u7ec8\u7ed3\u8fd9\u4e2a\u8bc5\u5492\uff0c\u7ed3\u679c\u540e\u6765\u53d1\u73b0\uff0c\u8fd9\u4e2a\u8bc5\u5492\u4ecd\u7136\u5b58\u5728\uff0c\u8f6c\u79fb\u5230\u4e86\u4ed6\u7684\u5802\u5f1f\u8eab\u4e0a\u3002\u63a8\u6d4b\u8d77\u6765\uff0c\u9664\u975e\u662f\u6574\u4e2a\u5bb6\u65cf\u5b8c\u5168\u6b7b\u5149\uff0c\u8840\u8109\u5f7b\u5e95\u65ad\u7edd\uff0c\u8bc5\u5492\u624d\u4f1a\u7ed3\u675f\u3002</p><p>\u7b2c\u4e09\u70b9\u5947\u5f02\u4e4b\u5904\uff0c\u5219\u662f\u8bc5\u5492\u6240\u5e26\u6765\u7684\u201c\u5384\u8fd0\u201d\uff0c\u4e5f\u662f\u80fd\u591f\u4f20\u67d3\u7684\u3002\u8b6c\u5982\u8bf4\u743c\u6069\u88ab\u5384\u8fd0\u7f20\u4e0a\u4e86\uff0c\u90a3\u4e48\u4ed6\u8eab\u8fb9\u4eb2\u8fd1\u7684\u4eba\u4e5f\u90fd\u8eb2\u4e0d\u6389\u3002\u7a0d\u7a0d\u8ba9\u4eba\u611f\u5230\u5b89\u6170\u7684\u662f\uff0c\u8fd9\u79cd\u201c\u5384\u8fd0\u201d\u662f\u8bc5\u5492\u7684\u6548\u679c\uff0c\u5e76\u4e0d\u662f\u8bc5\u5492\u672c\u8eab\uff0c\u5b83\u867d\u7136\u4f20\u67d3\uff0c\u5374\u4e0d\u4f1a\u9057\u4f20\uff0c\u800c\u4e14\u6bcf\u201c\u4f20\u67d3\u201d\u4e00\u6b21\uff0c\u6548\u529b\u5c31\u524a\u51cf\u51e0\u5206\uff0c\u800c\u4e14\u968f\u7740\u65f6\u95f4\u63a8\u79fb\uff0c\u6e10\u6e10\u4e5f\u4f1a\u6d88\u6563\u2014\u2014\u53ea\u8981\u522b\u518d\u548c\u88ab\u8bc5\u5492\u8005\u63a5\u89e6\u5c31\u884c\u3002</p><p>\u6240\u6709\u4eba\u90fd\u542c\u5f97\u9762\u9762\u76f8\u89d1\uff0c\u4ed6\u4eec\u53ef\u4e0d\u662f\u521d\u51fa\u8305\u5e90\u7684\u83dc\u9e1f\uff0c\u4e5f\u7b97\u662f\u89c1\u591a\u8bc6\u5e7f\u4e86\uff0c\u5176\u4e2d\u66f4\u6709\u51e0\u540d\u9ad8\u9636\u5deb\u5e08\uff0c\u4f46\u5982\u6b64\u8be1\u5f02\u7684\u8bc5\u5492\uff0c\u5f53\u771f\u662f\u95fb\u6240\u672a\u95fb\uff0c\u5b8c\u5168\u8d85\u51fa\u77e5\u8bc6\u8303\u7574\u3002</p><p>\u201c\u6211\u6709\u4e2a\u95ee\u9898\uff0c\u201d\u743c\u6069\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u8bf4\uff0c\u201c\u4f60\u600e\u4e48\u786e\u5b9a\u81ea\u5df1\u88ab\u8bc5\u5492\u4e86\u5462\u2014\u2014\u6211\u662f\u8bf4\uff0c\u5b83\u6709\u4ec0\u4e48\u6807\u5fd7\uff0c\u5f81\u5146\uff0c\u8868\u73b0\u4e4b\u7c7b\u7684\u5417\uff1f\u201d</p><p>\u5854\u7c73\u5a05\u82e6\u6da9\u5730\u7b11\u4e86\u7b11\uff1a\u201c\u4f60\u8fd8\u662f\u4e0d\u76f8\u4fe1\uff0c\u5bf9\u5427\u3002\u5f88\u591a\u4eba\u50cf\u4f60\u4e00\u6837\uff0c\u89c9\u5f97\u6211\u5728\u5371\u8a00\u8038\u542c\uff0c\u6216\u8005\u8fc7\u5206\u654f\u611f\uff1b\u751a\u81f3\u8fd8\u6709\u4eba\u8ba4\u4e3a\u6211\u6027\u60c5\u51b7\u50fb\uff0c\u4e0d\u613f\u4e0e\u4eba\u63a5\u8fd1\uff0c\u6240\u4ee5\u624d\u7f16\u9020\u51fa\u8fd9\u79cd\u501f\u53e3\u3002\u201d</p><p>\u636e\u5979\u8bf4\uff0c\u8bc5\u5492\u7684\u786e\u662f\u6709\u201c\u6807\u5fd7\u201d\u7684\uff0c\u4f46\u8fd9\u79cd\u6807\u5fd7\u65f6\u9690\u65f6\u73b0\uff0c\u8981\u5728\u7279\u6b8a\u7684\u573a\u5408\u624d\u80fd\u770b\u5230\uff0c\u901a\u5e38\u65e0\u4ece\u5f97\u89c1\u3002\u6240\u4ee5\u6709\u65f6\u5019\uff0c\u524d\u4e00\u4e2a\u88ab\u8bc5\u5492\u8005\u53bb\u4e16\u4e86\uff0c\u5927\u5bb6\u5e76\u4e0d\u77e5\u9053\u8bc5\u5492\u8f6c\u79fb\u5230\u8c01\u7684\u8eab\u4e0a\uff0c\u4f46\u8fd9\u79cd\u60c5\u5f62\u4e0d\u4f1a\u592a\u4e45\uff0c\u56e0\u4e3a\u88ab\u8bc5\u5492\u8005\u5c31\u50cf\u4e00\u4e2a\u4e0d\u65ad\u6563\u53d1\u761f\u75ab\u7684\u9ed1\u6d1e\uff0c\u867d\u7136\u770b\u4e0d\u89c1\uff0c\u4f46\u65f6\u95f4\u4e00\u957f\uff0c\u5927\u5bb6\u81ea\u7136\u5c31\u80fd\u5bdf\u89c9\u3002</p><p>\u4ee5\u5854\u7c73\u5a05\u81ea\u5df1\u4e3a\u4f8b\uff0c\u5979\u5927\u7ea6\u662f\u5728\u516d\u5e74\u524d\uff0c\u5ffd\u7136\u88ab\u8fd9\u4e2a\u8bc5\u5492\u7f20\u4e0a\uff0c\u4e4b\u540e\u7684\u4e00\u5e74\u5185\uff0c\u539f\u672c\u8eab\u4f53\u5eb7\u5065\u7684\u7236\u6bcd\u5ffd\u67d3\u91cd\u75c7\uff0c\u76f8\u7ee7\u75c5\u6b7b\uff0c\u4e00\u76f4\u6625\u98ce\u5f97\u610f\u7684\u672a\u5a5a\u592b\u751f\u610f\u7834\u4ea7\uff0c\u7cbe\u795e\u5931\u5e38\uff0c\u53d8\u6210\u4e86\u75af\u5b50\uff0c\u5c31\u8fde\u5de6\u53f3\u90bb\u5c45\u90fd\u53d7\u7275\u8fde\uff0c\u52a8\u4e0d\u52a8\u5c4b\u5b50\u5931\u706b\uff0c\u5bb6\u91cc\u8fdb\u8d3c\uff0c\u5403\u996d\u54af\u7259\uff0c\u8d70\u8def\u4ec6\u8857\uff0c\u51e1\u6b64\u79cd\u79cd\uff0c\u4e0d\u4e00\u800c\u8db3\u3002\u5854\u7c73\u5a05\u65e0\u5948\u4e4b\u4e0b\uff0c\u79bb\u5f00\u5bb6\u4e61\uff0c\u56db\u5904\u5bfb\u8bbf\uff0c\u60f3\u65b9\u8bbe\u6cd5\u8981\u7834\u89e3\u8fd9\u4e2a\u8bc5\u5492\uff0c\u7136\u800c\u59cb\u7ec8\u5f92\u52b3\u65e0\u83b7\uff0c\u53cd\u800c\u4e00\u8def\u884c\u6765\uff0c\u53c8\u589e\u52a0\u4e86\u66f4\u591a\u7684\u727a\u7272\u8005\u3002</p><p>\u201c\u6240\u4ee5\u4f60\u8eb2\u5728\u8fd9\u8352\u91ce\u68ee\u6797\u91cc\uff0c\u514d\u5f97\u4f24\u5bb3\u5230\u522b\u4eba\uff1f\u201d\u51db\u604d\u7136\u5927\u609f\uff0c\u201c\u5854\u7c73\u5a05\uff0c\u4f60\u771f\u662f\u4e2a\u597d\u4eba\u3002\u201d</p><p>\u5854\u7c73\u5a05\u4e0d\u597d\u610f\u601d\u5730\u6447\u4e86\u6447\u5934\uff1a\u201c\u4e5f\u4e0d\u662f\u5566\uff0c\u6211\u4e5f\u662f\u5728\u8fd9\u91cc\u5bfb\u627e\u4e00\u4ef6\u4e1c\u897f\u3002\u201d</p><p>\u4e3a\u4e86\u89e3\u51b3\u8bc5\u5492\uff0c\u5854\u7c73\u5a05\u591a\u65b9\u63a2\u6c42\uff0c\u904d\u89c8\u53e4\u7c4d\uff0c\u4e0d\u653e\u8fc7\u4efb\u4f55\u4e00\u7ebf\u5e0c\u671b\u3002\u51e0\u5e74\u524d\uff0c\u5979\u4ece\u4e00\u4f4d\u670b\u53cb\u5904\u5f97\u5230\u7ebf\u7d22\uff0c\u8bf4\u5728\u4e0a\u53e4\u65f6\u4ee3\uff0c\u7cbe\u7075\u738b\u56fd\u201c\u83ab\u5c9a\u201d\u6709\u4e00\u4ef6\u540d\u4e3a\u201c\u94f6\u67ab\u201d\u7684\u5b9d\u7269\uff0c\u5f62\u5982\u4e00\u679a\u94f6\u8272\u7684\u67ab\u53f6\uff0c\u4f69\u6234\u8005\u8fdc\u79bb\u4e00\u5207\u82e6\u5384\uff0c\u514d\u906d\u4e00\u5207\u4e0d\u5e78\uff0c\u5018\u82e5\u80fd\u627e\u5230\u8fd9\u4ef6\u5b9d\u7269\uff0c\u6216\u8bb8\u5c31\u53ef\u4ee5\u7834\u89e3\u6216\u8005\u62b5\u6d88\u5854\u7c73\u5a05\u8eab\u4e0a\u7684\u5384\u8fd0\u8bc5\u5492\u3002\u800c\u83ab\u5c9a\u738b\u56fd\u6614\u65e5\u6240\u5728\uff0c\u5c31\u662f\u8fd9\u5ea7\u8ff7\u5931\u68ee\u6797\u3002</p><p>\u201c\u53ef\u662f\uff0c\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u5982\u679c\u94f6\u67ab\u662f\u7cbe\u7075\u6240\u6709\uff0c\u90a3\u5b83\u5e94\u8be5\u968f\u7740\u5927\u64a4\u9000\u88ab\u5e26\u5f80\u6c38\u805a\u5c9b\u4e86\u5427\u3002\u201d</p><p>\u201c\u5982\u679c\u6211\u542c\u5230\u7684\u4f20\u8bf4\u5c5e\u5b9e\uff0c\u90a3\u4e48\u5c31\u6ca1\u6709\u3002\u201d</p><p>\u636e\u8bf4\uff0c\u5728\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u5973\u738b\u751f\u4e86\u4e00\u4e2a\u53ef\u7231\u7684\u5973\u513f\uff0c\u7cbe\u7075\u4eec\u4e3e\u529e\u4e86\u4e00\u573a\u76db\u5927\u7684\u5bb4\u4f1a\uff0c\u5e86\u795d\u516c\u4e3b\u7684\u8bde\u751f\uff0c\u8fde\u6d77\u6d0b\u4e2d\u7684\u6d77\u7cbe\u7075\u90fd\u6d3e\u4eba\u9001\u6765\u4e86\u793c\u7269\u3002\u5341\u4e8c\u4f4d\u7cbe\u7075\u795e\u5e94\u9080\u800c\u6765\uff0c\u8d50\u4e88\u516c\u4e3b\u7f8e\u597d\u7684\u795d\u798f\u3002\u4f46\u86db\u540e\u7f57\u4e1d\u56e0\u4e3a\u6ca1\u6709\u53d7\u5230\u9080\u8bf7\u800c\u9707\u6012\uff0c\u5979\u6d3e\u9063\u4e00\u53ea\u8721\u878d\u5996\uff0c\u5728\u5bb4\u4f1a\u7684\u6700\u540e\u51fa\u73b0\uff0c\u7ed9\u5c0f\u516c\u4e3b\u964d\u4e0b\u90aa\u6076\u7684\u9ed1\u6697\u8bc5\u5492\uff0c\u9884\u8a00\u5979\u5c06\u611a\u8822\u5730\u4f1a\u7231\u4e0a\u4e00\u4e2a\u4eba\u7c7b\uff0c\u5e76\u4e14\u6700\u7ec8\u56e0\u6b64\u4e27\u547d\u3002</p><p>\u5373\u4fbf\u662f\u7cbe\u7075\u8bf8\u795e\u4e5f\u65e0\u6cd5\u89e3\u9664\u7f57\u4e1d\u7684\u8bc5\u5492\uff0c\u4e3a\u4e86\u907f\u514d\u8fd9\u79cd\u53ef\u6015\u7684\u4e0d\u5e78\uff0c\u6708\u5973\u795e\u838e\u7f55\u59ae\u7528\u795e\u529b\u51dd\u6210\u4e86\u4e00\u679a\u94f6\u8272\u7684\u67ab\u53f6\uff0c\u9001\u7ed9\u4e86\u5c0f\u516c\u4e3b\u3002\u5973\u795e\u8bf4\uff0c\u53ea\u8981\u4f69\u6234\u6b64\u7269\uff0c\u4e0d\u79bb\u987b\u81fe\uff0c\u5c31\u4e0d\u4f1a\u88ab\u8bc5\u5492\u6240\u4fd8\u83b7\u3002</p><p>\u7cbe\u7075\u516c\u4e3b\u957f\u5927\u4e86\uff0c\u5979\u806a\u660e\u3001\u5584\u826f\uff0c\u6027\u683c\u6e29\u67d4\uff0c\u6df1\u53d7\u559c\u7231\u548c\u5c0a\u656c\uff0c\u9075\u7167\u5973\u795e\u7684\u610f\u65e8\uff0c\u5979\u59cb\u7ec8\u4f69\u6234\u7740\u90a3\u679a\u94f6\u67ab\uff0c\u7cbe\u7075\u4eec\u4e5f\u56e0\u6b64\u79f0\u5979\u4e3a\u201c\u67ab\u516c\u4e3b\u201d\u3002\u7136\u800c\uff0c\u5728\u4e00\u6b21\u610f\u5916\u4e2d\uff0c\u516c\u4e3b\u4e0d\u614e\u5c06\u5b83\u9057\u5931\u4e86\u3002\u8bc5\u5492\u5982\u5f71\u968f\u5f62\uff0c\u5384\u8fd0\u968f\u4e4b\u6d6e\u73b0\uff0c\u4e0d\u4e45\u4e4b\u540e\uff0c\u516c\u4e3b\u4fbf\u88ab\u4e00\u4e2a\u4eba\u7c7b\u7537\u6027\u6240\u6b3a\u9a97\uff0c\u90c1\u90c1\u800c\u7ec8\u3002</p><p>\u6885\u83f2\u65af\u77a5\u4e86\u743c\u6069\u4e00\u773c\uff0c\u4f3c\u6709\u7b11\u610f\uff0c\u743c\u6069\u63c9\u4e86\u63c9\u989d\u5934\uff0c\u4e0d\u53bb\u7406\u5979\u3002\u201c\u6240\u4ee5\u4f60\u662f\u8981\u5728\u8fd9\u7247\u5927\u68ee\u6797\u91cc\u5bfb\u627e\u4e00\u679a\u51e0\u5343\u5e74\u524d\u9057\u5931\u7684\u67ab\u53f6\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u4e14\u4e0d\u8bf4\u8fd9\u4e2a\u6545\u4e8b\u600e\u4e48\u542c\u600e\u4e48\u4e0d\u5bf9\u52b2\uff0c\u5373\u4fbf\u5c5e\u5b9e\uff0c\u53c8\u8981\u4ece\u54ea\u53bb\u627e\u8fd9\u679a\u94f6\u67ab\uff1f\u8fd9\u548c\u5927\u6d77\u635e\u9488\u4e5f\u5dee\u4e0d\u591a\u4e86\u5427\u3002\u9664\u975e\u53e6\u6709\u5176\u4ed6\u7ebf\u7d22\uff0c\u5426\u5219\u7edd\u65e0\u53ef\u80fd\u529e\u5230\u3002</p><p>\u5854\u7c73\u5a05\u70b9\u70b9\u5934\uff1a\u201c\u7684\u786e\u6709\u4e00\u4e9b\u7ebf\u7d22\u3002\u201d</p><p>\u6839\u636e\u5979\u7684\u670b\u53cb\u67e5\u9605\u8d44\u6599\uff0c\u4ece\u79cd\u79cd\u8bb0\u8f7d\u4e2d\u5206\u6790\uff0c\u4ee5\u53ca\u8bf7\u9ad8\u660e\u7684\u5deb\u5e08\u8fdb\u884c\u5360\u535c\uff0c\u6700\u7ec8\u786e\u5b9a\u4e00\u70b9\uff1a\u516c\u4e3b\u5f53\u5e74\u9057\u5931\u94f6\u67ab\u7684\u5730\u70b9\uff0c\u662f\u5728\u5979\u7684\u5bdd\u5bab\u201c\u6668\u971e\u201d\u4e2d\u3002\u516c\u4e3b\u7684\u5bdd\u5bab\u5f53\u7136\u662f\u5728\u738b\u90fd\uff0c\u6362\u53e5\u8bdd\u8bf4\uff0c\u5982\u679c\u80fd\u627e\u5230\u6614\u65e5\u7cbe\u7075\u738b\u56fd\u7684\u738b\u90fd\u9057\u5740\uff0c\u5c31\u6709\u5e0c\u671b\u80fd\u627e\u5230\u5b9d\u7269\u3002</p><p>\u7cbe\u7075\u66fe\u7ecf\u662f\u5927\u9646\u4e0a\u6700\u53d1\u8fbe\u7684\u6587\u660e\uff0c\u4ed6\u4eec\u867d\u7136\u662f\u81ea\u7136\u4e4b\u7075\uff0c\u68ee\u6797\u4e4b\u5b50\uff0c\u4f46\u5e76\u4e0d\u662f\u8339\u6bdb\u996e\u8840\u7684\u91ce\u517d\uff0c\u4e5f\u662f\u4f1a\u51b6\u70bc\u91d1\u5c5e\u3001\u5efa\u7b51\u623f\u5c4b\u3001\u8425\u9020\u57ce\u6c60\u7684\uff0c\u5fc5\u7136\u4f1a\u7559\u4e0b\u79cd\u79cd\u75d5\u8ff9\u3002\u6309\u7406\u6765\u8bf4\uff0c\u8ff7\u5931\u68ee\u6797\u867d\u5927\uff0c\u4f46\u4e00\u5ea7\u5e9f\u5f03\u7684\u7cbe\u7075\u738b\u90fd\u76ee\u6807\u4e5f\u4e0d\u5c0f\uff0c\u8fd8\u662f\u5e94\u8be5\u5bb9\u6613\u627e\u7684\u3002\u7136\u800c\u8fd1\u51e0\u5e74\u6765\uff0c\u5854\u7c73\u5a05\u8d70\u904d\u4e86\u6574\u4e2a\u8ff7\u5931\u68ee\u6797\uff0c\u4e5f\u53d1\u73b0\u4e86\u4e00\u4e9b\u7cbe\u7075\u738b\u56fd\u7684\u9057\u8ff9\uff0c\u4f46\u4f5c\u4e3a\u76ee\u6807\u7684\u201c\u738b\u90fd\u201d\uff0c\u5374\u59cb\u7ec8\u6ca1\u6709\u534a\u70b9\u8e2a\u5f71\u3002</p><p>\u201c\u4f1a\u4e0d\u4f1a\u5728\u5730\u4e0b\uff1f\u201d\u51db\u731c\u6d4b\u3002</p><p>\u201c\u738b\u90fd\u600e\u4e48\u53ef\u80fd\u4f1a\u5728\u5730\u4e0b\uff0c\u53c8\u4e0d\u662f\u9ed1\u6697\u7cbe\u7075\u3002\u201d</p><p>\u201c\u5e74\u4ee3\u4e45\u8fdc\uff0c\u6c89\u4e0b\u53bb\u4e86\u561b\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u53c8\u6216\u8bb8\u53d1\u5730\u9707\uff0c\u88ab\u63a9\u57cb\u4e86\uff1f\u201d</p><p>\u201c......\u4f60\u8fd8\u4e0d\u5982\u8bf4\u662f\u5929\u964d\u9668\u77f3\uff0c\u7838\u6210\u5927\u5751\u4e86\u3002\u201d</p><p>\u201c\u6709\u4e00\u79cd\u8bf4\u6cd5\uff0c\u83ab\u5c9a\u7684\u7cbe\u7075\u5728\u5927\u64a4\u9000\u65f6\uff0c\u5deb\u5e08\u4eec\u5e03\u4e0b\u4e86\u5f3a\u5927\u7684\u9b54\u6cd5\uff0c\u5c06\u738b\u90fd\u5b8c\u5168\u9690\u853d\u8d77\u6765\uff0c\u867d\u7136\u4ecd\u65e7\u5b58\u5728\u4e8e\u8fd9\u5ea7\u68ee\u6797\u4e4b\u4e2d\uff0c\u5916\u4eba\u5374\u65e0\u4ece\u5f97\u89c1\u3002\u53ea\u6709\u7b49\u6709\u671d\u4e00\u65e5\uff0c\u7cbe\u7075\u81ea\u6c38\u805a\u5c9b\u91cd\u8fd4\u6b64\u5730\uff0c\u738b\u90fd\u624d\u4f1a\u51fa\u73b0\u201d\u5854\u7c73\u5a05\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u5018\u82e5\u5f53\u771f\u5982\u6b64\uff0c\u90a3\u6211\u5927\u6982\u662f\u6c38\u8fdc\u4e5f\u627e\u4e0d\u5230\u4e86\u3002\u201d</p><p>\u89c1\u5979\u795e\u60c5\u9eef\u7136\uff0c\u51db\u8fde\u5fd9\u5b89\u6170\uff0c\u201c\u653e\u5fc3\u5566\uff0c\u7b49\u6211\u4eec\u529e\u5b8c\u4e8b\u56de\u6765\uff0c\u5c31\u5e2e\u4f60\u4e00\u8d77\u627e\u3002\u5176\u5b9e\u6309\u6211\u8bf4\uff0c\u90a3\u4ec0\u4e48\u94f6\u67ab\u4e5f\u4e0d\u89c1\u5f97\u6709\u7528\uff0c\u627e\u4e0d\u627e\u5f97\u5230\u90fd\u6ca1\u5173\u7cfb\uff0c\u4e0d\u7528\u592a\u5728\u610f\u3002\u5982\u679c\u771f\u6709\u7528\u7684\u8bdd\uff0c\u600e\u4e48\u4f1a\u9057\u5931\u5462\u3002\u5982\u679c\u7cbe\u7075\u516c\u4e3b\u5e26\u7740\u5b83\u5c31\u4e0d\u4f1a\u5012\u9709\uff0c\u90a3\u5c31\u6839\u672c\u4e0d\u4f1a\u628a\u5b83\u5f04\u4e22\u561b\uff0c\u5bf9\u4e0d\u5bf9\u3002\u201d</p><p>\u51db\u7684\u903b\u8f91\u96be\u5f97\u5982\u6b64\u6e05\u695a\uff0c\u95ee\u9898\u662f\u5979\u7684\u8bdd\u5e76\u6ca1\u6709\u8d77\u5230\u4efb\u4f55\u5b89\u6170\u4f5c\u7528\uff0c\u5176\u4ed6\u4eba\u8d76\u5feb\u5c94\u5f00\u8bdd\u9898\uff0c\u95ee\u8d77\u5854\u7c73\u5a05\u5728\u8ff7\u5931\u68ee\u6797\u91cc\u7684\u89c1\u95fb\uff0c\u6709\u6ca1\u6709\u4ec0\u4e48\u6709\u8da3\u7684\u5730\u65b9\uff0c\u6216\u8005\u9700\u8981\u6ce8\u610f\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u5012\u4e5f\u6ca1\u4ec0\u4e48\u9700\u8981\u7279\u522b\u6ce8\u610f\u7684\uff0c\u603b\u4f53\u6765\u8bf4\uff0c\u8ff7\u5931\u68ee\u6797\u8fd8\u7b97\u662f\u4e2a\u5b89\u5b81\u7965\u548c\u7684\u5730\u65b9\u5427\u3002\u201d</p><p>\u201c\u5b89\u5b81\u7965\u548c\u5230\u5df2\u7ecf\u53ef\u4ee5\u517b\u90a3\u4e48\u5927\u53ea\u7684\u9e70\u86c7\u4e86\u5417\uff1f\u201d\u743c\u6069\u5fcd\u4e0d\u4f4f\u5410\u69fd\u3002</p><p>\u201c\u8fd9\u4e2a\u786e\u5b9e\u6709\u70b9\u5947\u602a\uff0c\u201d\u5854\u7c73\u5a05\u76b1\u7709\uff0c\u201c\u6211\u5728\u8fd9\u5ea7\u68ee\u6797\u91cc\u5df2\u7ecf\u5f85\u4e86\u51e0\u5e74\uff0c\u4ece\u6765\u6ca1\u89c1\u8fc7\u6216\u8005\u542c\u8bf4\u8fc7\u6709\u9e70\u86c7\uff0c\u5b83\u5c31\u50cf\u662f\u7a81\u7136\u5192\u51fa\u6765\u7684\u3002\u201d</p><p>\u201c\u4f46\u770b\u5b83\u7684\u4e2a\u5934\uff0c\u5c11\u8bf4\u4e5f\u6709\u51e0\u5341\u5c81\u4e86\u5427\u3002\u201d</p><p>\u9e70\u86c7\u5e76\u4e0d\u662f\u81ea\u7136\u751f\u7269\uff0c\u5b83\u662f\u90aa\u6cd5\u6240\u9020\uff0c\u6362\u53e5\u8bdd\u8bf4\uff0c\u9e70\u86c7\u7684\u80cc\u540e\u5e94\u8be5\u6709\u4e2a\u5deb\u5e08\u3002\u53ef\u60dc\u90a3\u53ea\u9e70\u86c7\u88ab\u743c\u6069\u76f4\u63a5\u77f3\u5316\uff0c\u4e5f\u6ca1\u529e\u6cd5\u518d\u62c9\u8fc7\u6765\u4ed4\u7ec6\u68c0\u67e5\uff0c\u53ea\u80fd\u591a\u52a0\u5c0f\u5fc3\u4e86\u3002\u800c\u4e14\u743c\u6069\u611f\u89c9\u81ea\u5df1\u6628\u591c\u9047\u5230\u7684\u90a3\u53ea\u9e70\u86c7\uff0c\u548c\u4e66\u4e0a\u8bb0\u8f7d\u7684\u60c5\u5f62\u4f3c\u4e4e\u53c8\u6709\u6240\u4e0d\u540c\uff0c\u611f\u89c9\u66f4\u50cf\u662f\u53d8\u79cd\uff0c\u81f3\u5c11\u4ed6\u53ef\u4ece\u6765\u6ca1\u542c\u8bf4\u9e70\u86c7\u8fd8\u80fd\u9041\u5730\u7684\u3002</p><p>\u5854\u7c73\u5a05\u8bf4\u4e86\u4e00\u4e9b\u6709\u5173\u8ff7\u5931\u68ee\u6797\u7684\u4f20\u8bf4\uff0c\u5927\u591a\u90fd\u662f\u4e0e\u7cbe\u7075\u6709\u5173\uff0c\u4e5f\u6709\u4e00\u4e9b\u5173\u4e8e\u9b3c\u602a\u3001\u5996\u7075\u548c\u9f99\u7684\u6545\u4e8b\uff0c\u6709\u4e9b\u975e\u5e38\u641e\u7b11\u3002\u6bd4\u5982\u6709\u4e00\u4e2a\u201c\u6076\u9f99\u4e0e\u516c\u4e3b\u201d\u7684\u6545\u4e8b\uff0c\u636e\u8bf4\u5728\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u8ff7\u5931\u68ee\u6797\u4e1c\u8fb9\u7684\u71c3\u70df\u5c71\u8109\u91cc\uff0c\u4f4f\u7740\u4e00\u5934\u51f6\u6076\u7684\u84dd\u9f99\uff0c\u7ecf\u5e38\u5230\u68ee\u6797\u91cc\u6765\u8086\u8650\uff0c\u8ba9\u7cbe\u7075\u4eec\u5934\u75db\u4e0d\u5df2\uff0c\u540e\u6765\u7cbe\u7075\u60f3\u4e86\u4e00\u4e2a\u8ba1\u8c0b\uff0c\u4ed6\u4eec\u7f16\u9020\u6d41\u8a00\uff0c\u8bf4\u5728\u4e1c\u8fb9\u7684\u9065\u8fdc\u4e4b\u5904\uff0c\u5de8\u9f99\u8fde\u7eed\u98de\u884c\u5341\u5929\u5341\u591c\u7684\u5730\u65b9\uff0c\u6709\u4e00\u5ea7\u4eba\u7c7b\u57ce\u5e02\uff0c\u57ce\u91cc\u6709\u4e00\u4f4d\u7f8e\u4e3d\u7684\u516c\u4e3b\uff0c\u6709\u795e\u5947\u7684\u5deb\u672f\uff0c\u80fd\u591f\u70b9\u77f3\u6210\u91d1\uff0c\u516c\u4e3b\u7684\u738b\u5bab\u662f\u7528\u7eaf\u91d1\u6253\u9020\uff0c\u82b1\u56ed\u91cc\u7684\u6bcf\u4e00\u9897\u77f3\u5934\u90fd\u662f\u5b9d\u77f3\uff0c\u8fd8\u6709\u5de8\u5927\u65e0\u6bd4\u7684\u5730\u4e0b\u5b9d\u5e93\uff0c\u91cc\u9762\u662f\u5806\u79ef\u5982\u5c71\u7684\u91d1\u5e01\u3002\u9f99\u76f8\u4fe1\u4e86\u8fd9\u4e2a\u8c0e\u8a00\uff0c\u79bb\u5f00\u5de2\u7a74\uff0c\u5f80\u4e1c\u98de\u4e86\u5341\u5929\u5341\u591c\uff0c\u7ec8\u4e8e\u770b\u5230\u4e86\u4e00\u5ea7\u9ad8\u5927\u5dcd\u5ce8\u7684\u57ce\u6c60\uff0c\u4f46\u57ce\u4e2d\u5e76\u6ca1\u6709\u4ec0\u4e48\u91d1\u5b50\u505a\u7684\u738b\u5bab\uff0c\u53ea\u6709\u4e00\u4f4d\u867d\u7136\u7f8e\u4e3d\uff0c\u5374\u8863\u7740\u6734\u7d20\u7684\u516c\u4e3b\u3002\u9f99\u5f88\u751f\u6c14\uff0c\u964d\u843d\u5728\u516c\u4e3b\u9762\u524d\uff0c\u60f3\u8981\u5403\u6389\u5979\uff0c\u5374\u88ab\u516c\u4e3b\u7528\u5deb\u672f\u6444\u5165\u4e00\u9762\u955c\u5b50\u91cc\uff0c\u4ece\u6b64\u518d\u4e5f\u6ca1\u6709\u56de\u6765\u3002</p><p>\u201c\u84dd\u9f99\u7684\u667a\u5546\u5c31\u662f\u4f4e\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u5982\u679c\u662f\u7ea2\u9f99\u4e00\u5b9a\u4e0d\u4f1a\u4e0a\u8fd9\u79cd\u5f53\u3002\u201d</p><p>\u201c......\u201d</p><p>\u201c\u6211\u542c\u4eba\u8bf4\u68ee\u6797\u91cc\u6709\u6811\u7cbe\uff1f\u201d\u838e\u73de\u514b\u95ee\uff0c\u201c\u6709\u9047\u5230\u5417\uff1f\u201d</p><p>\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\uff0c\u9b45\u9b54\u5bf9\u6811\u7cbe\u4e00\u76f4\u5f88\u611f\u5174\u8da3\uff0c\u5854\u7c73\u5a05\u8868\u793a\u5979\u4e5f\u542c\u8fc7\u6811\u7cbe\u7684\u4f20\u95fb\uff0c\u4f46\u4ece\u6765\u6ca1\u6709\u9047\u5230\u8fc7\u3002\u743c\u6069\u89c9\u5f97\u6811\u7cbe\u5176\u5b9e\u5c31\u662f\u7cbe\u7075\uff0c\u88ab\u65e0\u77e5\u6751\u6c11\u8bef\u8ba4\uff0c\u4f46\u5854\u7c73\u5a05\u8868\u793a\u4e0d\u592a\u53ef\u80fd\u3002\u56e0\u4e3a\u7cbe\u7075\u65e9\u5728\u4e03\u767e\u5e74\u524d\u5c31\u5df2\u7ecf\u5168\u4f53\u79bb\u5f00\u4e86\u8fd9\u5ea7\u68ee\u6797\uff0c\u4e5f\u6ca1\u542c\u8bf4\u6709\u7559\u4e0b\u7684\uff0c\u800c\u6811\u7cbe\u7684\u4f20\u8bf4\uff0c\u6700\u65e9\u8d77\u6e90\u5927\u81f4\u662f\u5728\u4e24\u4e09\u767e\u5e74\u524d\uff0c\u65f6\u95f4\u4e0a\u5c31\u5bf9\u4e0d\u4e0a\u3002</p><p>\u5854\u7c73\u5a05\u63a8\u6d4b\uff0c\u6811\u7cbe\u53ef\u80fd\u4e0e\u201c\u68ee\u6797\u91cc\u7684\u5973\u5deb\u201d\u6709\u5173\u3002</p><p>\u201c\u68ee\u6797\u91cc\u7684\u5973\u5deb\uff1f\u201d</p><p>\u636e\u8bf4\u5728\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u68ee\u6797\u7684\u6df1\u5904\u6709\u4e00\u5ea7\u5c0f\u6728\u5c4b\uff0c\u5b83\u6709\u5c16\u5c16\u7684\u5c4b\u9876\u548c\u5706\u5f62\u7684\u7a97\u6237\uff0c\u693d\u5b50\u4e0a\u957f\u6ee1\u4e86\u8611\u83c7\u3002\u5728\u5c0f\u6728\u5c4b\u91cc\uff0c\u4f4f\u7740\u4e00\u4f4d\u5973\u5deb\uff0c\u5979\u6709\u8be1\u79d8\u83ab\u6d4b\u7684\u5deb\u672f\uff0c\u7528\u8611\u83c7\u917f\u9020\u6210\u795e\u5947\u7684\u836f\u6c34\uff0c\u80fd\u591f\u8ba9\u751f\u8005\u6c38\u8fdc\u6c89\u7761\uff0c\u8ba9\u4ea1\u8005\u81ea\u9ed1\u6697\u4e2d\u590d\u82cf\uff0c\u4efb\u4f55\u4eba\u90fd\u53ef\u4ee5\u5411\u5979\u6c42\u52a9\uff0c\u4f46\u524d\u63d0\u662f\u5fc5\u987b\u56de\u7b54\u51fa\u5979\u7684\u4e00\u4e2a\u95ee\u9898\u3002\u5982\u679c\u56de\u7b54\u4e0d\u51fa\uff0c\u4e0d\u4ec5\u5f97\u4e0d\u5230\u5973\u5deb\u7684\u5e2e\u52a9\uff0c\u8fd8\u4f1a\u53d7\u5230\u4e25\u5389\u7684\u60e9\u7f5a\uff0c\u7537\u6027\u4f1a\u88ab\u53d8\u6210\u77f3\u50cf\u4e22\u5f03\u5728\u6811\u4e1b\u4e2d\uff0c\u5973\u6027\u7684\u7075\u9b42\u4f1a\u88ab\u6c38\u8fdc\u7981\u9522\u5728\u5c0f\u6728\u5c4b\u7684\u9644\u8fd1\uff0c\u5316\u4f5c\u6e38\u8361\u5728\u68ee\u6797\u91cc\u7684\u5973\u5996\u3002\u5854\u7c73\u5a05\u89c9\u5f97\uff0c\u4f20\u8bf4\u4e2d\u7684\u201c\u6811\u7cbe\u201d\uff0c\u5176\u5b9e\u5f88\u53ef\u80fd\u5c31\u662f\u8fd9\u4e9b\u5973\u5996\u3002</p><p>\u201c\u5b9e\u9645\u4e0a\uff0c\u9664\u4e86\u5bfb\u627e\u7cbe\u7075\u738b\u56fd\u7684\u9057\u8ff9\u4e4b\u5916\uff0c\u8fd9\u4e5f\u662f\u6211\u6765\u8fd9\u91cc\u7684\u53e6\u4e00\u4e2a\u76ee\u6807\uff0c\u6216\u8005\u8bf4\u4e00\u7ebf\u5e0c\u671b\u5427\uff0c\u201d\u5854\u7c73\u5a05\u8bf4\uff0c\u201c\u4f20\u8bf4\u8fd9\u4f4d\u5973\u5deb\u5deb\u672f\u9ad8\u5f3a\uff0c\u80fd\u591f\u7834\u9664\u4e00\u5207\u8bc5\u5492\u3002\u5c31\u7b97\u662f\u4e0a\u53e4\u795e\u738b\u6240\u4e0b\u7684\u8bc5\u5492\uff0c\u5979\u4e5f\u80fd\u591f\u4e88\u4ee5\u6d88\u9664\u3002\u867d\u7136\u5973\u5deb\u7684\u6545\u4e8b\u81f3\u5c11\u5df2\u7ecf\u6709\u4e24\u767e\u5e74\u4ee5\u4e0a\u7684\u5386\u53f2\uff0c\u5c31\u7b97\u786e\u6709\u5176\u4eba\uff0c\u5e94\u8be5\u4e5f\u65e9\u5df2\u4f5c\u53e4\uff0c\u9664\u975e\u5979\u5e76\u975e\u4eba\u7c7b\u800c\u662f\u7cbe\u7075\uff0c\u4f46\u6211\u60f3\u5982\u679c\u80fd\u591f\u627e\u5230\u5979\u7684\u4f4f\u5904\uff0c\u6216\u8bb8\u80fd\u591f\u6709\u4ec0\u4e48\u610f\u5916\u6536\u83b7\uff0c\u4e5f\u8bf4\u4e0d\u51c6\u3002\u201d</p><p>\u201c\u90a3\u4f60\u6709\u627e\u5230\u5973\u5deb\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u201d\u5854\u7c73\u5a05\u8bf4\uff0c\u201c\u68ee\u6797\u91cc\u786e\u5b9e\u6709\u4e00\u5ea7\u5c0f\u6728\u5c4b\uff0c\u5f62\u8c8c\u548c\u4f20\u8bf4\u4e2d\u975e\u5e38\u8fd1\u4f3c\uff0c\u5c16\u5c16\u7684\u5c4b\u9876\u548c\u5706\u5f62\u7684\u7a97\u6237\uff0c\u4f46\u4e5f\u5c31\u4ec5\u6b64\u800c\u5df2\uff0c\u66f4\u50cf\u662f\u730e\u4eba\u4e3a\u4e86\u4e34\u65f6\u6816\u606f\u800c\u642d\u5efa\u7684\u5c45\u6240\uff0c\u800c\u4e14\u5e9f\u5f03\u5df2\u4e45\u3002\u91cc\u9762\u7a7a\u7a7a\u8361\u8361\uff0c\u6240\u6709\u7684\u4e1c\u897f\u90fd\u88ab\u5e26\u8d70\u4e86\uff0c\u8fde\u628a\u6905\u5b50\u90fd\u6ca1\u6709\u7559\u4e0b\uff0c\u53ea\u80fd\u52c9\u5f3a\u770b\u51fa\u66fe\u7ecf\u6709\u4eba\u5c45\u4f4f\u8fc7\u3002\u6211\u5728\u5c4b\u5b50\u91cc\u6ca1\u6709\u627e\u5230\u4efb\u4f55\u6709\u4ef7\u503c\u7684\u4e1c\u897f\uff0c\u9644\u8fd1\u4e5f\u6ca1\u6709\u627e\u5230\u4efb\u4f55\u77f3\u50cf\uff0c\u4e5f\u6ca1\u6709\u770b\u5230\u5973\u5996\uff0c\u6216\u8005\u7c7b\u4f3c\u7684\u4e1c\u897f\u3002\u201d</p><p>\u7b80\u5355\u5730\u8bf4\u5c31\u662f\u4e00\u65e0\u6240\u83b7\u3002</p><p>\u4e0d\u8fc7\u743c\u6069\u8f6c\u5ff5\u4e00\u60f3\uff0c\u89c9\u5f97\u4e5f\u8bf4\u4e0d\u51c6\uff0c\u5854\u7c73\u5a05\u4e0d\u662f\u5deb\u5e08\uff0c\u81f3\u5c11\u770b\u8d77\u6765\u4e0d\u50cf\uff0c\u5e94\u8be5\u662f\u8eab\u624b\u77eb\u6377\u7684\u6b66\u8005\uff0c\u5bf9\u9b54\u6cd5\u3001\u5deb\u672f\u7684\u4e86\u89e3\u672a\u5fc5\u6df1\u5165\u3002\u5973\u5deb\u7684\u4f4f\u5904\uff0c\u5728\u5916\u884c\u4eba\u773c\u91cc\u770b\u6765\u6216\u8bb8\u7a7a\u65e0\u4e00\u7269\uff0c\u5728\u5185\u884c\u7684\u89c6\u91ce\u91cc\uff0c\u53ef\u80fd\u5c31\u662f\u8baf\u606f\u904d\u5e03\u4e86\u3002</p><p>\u95ee\u4e86\u95ee\u90a3\u5ea7\u5c0f\u6728\u5c4b\u7684\u4f4d\u7f6e\uff0c\u5854\u7c73\u5a05\u4e5f\u8bb0\u4e0d\u592a\u6e05\u695a\u4e86\uff0c\u6bd5\u7adf\u65f6\u95f4\u4e5f\u8fc7\u53bb\u5f88\u4e45\uff0c\u53ea\u5927\u81f4\u8bb0\u5f97\u662f\u5728\u68ee\u6797\u7684\u4e1c\u5317\u90e8\u3002</p><p>\u838e\u73de\u514b\u4f3c\u4e4e\u9887\u6709\u5174\u8da3\uff0c\u82e5\u5728\u5e73\u65f6\uff0c\u743c\u6069\u4e5f\u4e0d\u4ecb\u610f\u53bb\u8f6c\u8f6c\uff0c\u4e0d\u8fc7\u773c\u4e0b\u6b63\u8981\u53bb\u6d6e\u7a7a\u57ce\uff0c\u8fd8\u662f\u4e0d\u8981\u8282\u5916\u751f\u679d\u3002\u800c\u4e14\u90a3\u53ea\u9e70\u86c7\u4e5f\u8ba9\u4ed6\u5fc3\u751f\u8b66\u60d5\uff0c\u603b\u89c9\u5f97\u8fd9\u5ea7\u68ee\u6797\u5e76\u975e\u770b\u8d77\u6765\u90a3\u6837\u5e73\u9759\uff0c\u6709\u4ec0\u4e48\u4e8b\uff0c\u90fd\u7b49\u5148\u627e\u5230\u6d6e\u7a7a\u57ce\u518d\u8bf4\u5427\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u4f60\u4e5f\u5b66\u8fc7\u9b54\u6cd5\u5417\uff1f\u201d\u7ef4\u82e5\u62c9\u5c06\u4e00\u6839\u6728\u67f4\u4e22\u8fdb\u706b\u5806\u91cc\uff0c\u7528\u968f\u610f\u7684\u53e3\u6c14\u8be2\u95ee\uff0c\u201c\u4f60\u5728\u8fd9\u4e2a\u5bbf\u8425\u5730\u5468\u56f4\u5e03\u7f6e\u7684\u9632\u5fa1\u6cd5\u672f......\u662f\u4e00\u79cd\u8ff7\u5bab\u672f\uff1f\u201d</p><p>\u201c\u8ff7\u5bab\u672f\uff1f\u201d\u5854\u7c73\u5a05\u91cd\u590d\u4e86\u4e00\u904d\u8fd9\u4e2a\u8bcd\uff0c\u5979\u4f1a\u8bf4\u4e2d\u571f\u7684\u901a\u7528\u8bed\uff0c\u4f46\u663e\u7136\u5bf9\u9b54\u6cd5\u672f\u8bed\u5e76\u4e0d\u662f\u7279\u522b\u4e86\u89e3\uff0c\u201c\u4e0d\u662f\uff0c\u55ef\uff0c\u6211\u60f3\u5e94\u8be5\u4e0d\u662f\u3002\u5b9e\u9645\u4e0a\uff0c\u6211\u5e76\u4e0d\u4f1a\u4efb\u4f55\u9b54\u6cd5\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u4f46\u770b\u8d77\u6765\u5f88\u50cf\u3002\u201d</p><p>\u201c\u662f\u5f88\u50cf\uff0c\u4e4b\u524d\u4e5f\u6709\u5deb\u5e08\u95ee\u8fc7\u6211\u7c7b\u4f3c\u7684\u95ee\u9898\uff0c\u4f46\u7684\u786e\u4e0d\u662f\uff0c\u201d\u5854\u7c73\u5a05\u8bf4\uff0c\u201c\u6211\u4ece\u6765\u6ca1\u6709\u5b66\u8fc7\u9b54\u6cd5\uff0c\u4e5f\u5b8c\u5168\u611f\u5e94\u4e0d\u5230\u4f60\u4eec\u53e3\u4e2d\u6240\u8bf4\u7684\u2018\u9b54\u7f51\u2019\u7684\u5b58\u5728\uff0c\u6240\u4ee5\u66f4\u522b\u8c08\u4ec0\u4e48\u6cd5\u672f\u4e86\u3002\u6bcf\u5f53\u6211\u5728\u91ce\u5916\u7684\u65f6\u5019\uff0c\u60f3\u8981\u751f\u56e2\u706b\uff0c\u90fd\u5f97\u8001\u8001\u5b9e\u5b9e\u5730\u53bb\u5212\u706b\u77f3\uff0c\u5b8c\u5168\u505a\u4e0d\u5230\u50cf\u4f60\u4eec\u4e00\u6837\uff0c\u5ff5\u4e00\u53e5\u5492\u8bed\uff0c\u706b\u5c31\u718a\u718a\u71c3\u8d77\uff0c\u53c8\u65b9\u4fbf\uff0c\u53c8\u6c14\u6d3e\uff0c\u4ee4\u4eba\u7fa1\u6155\u5f97\u8981\u547d\u3002\u201d</p><p>\u5176\u5b9e\u5deb\u5e08\u4e5f\u4e0d\u89c1\u5f97\u90fd\u80fd\u51ed\u7a7a\u751f\u706b\uff0c\u81f3\u5c11\u743c\u6069\u5c31\u505a\u4e0d\u5230\uff0c\u4e0d\u8fc7\u8fd9\u4e0d\u662f\u91cd\u70b9\u3002\u201c\u4f60\u611f\u5e94\u4e0d\u5230\u9b54\u7f51\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u8fd9\u5c31\u5f88\u5947\u602a\u4e86\u3002</p><p>\u8fd9\u4e2a\u4e16\u754c\u4e0a\u7684\u9b54\u6cd5\uff0c\u57fa\u672c\u90fd\u662f\u8981\u901a\u8fc7\u4ece\u201c\u9b54\u7f51\u201d\u4e2d\u63d0\u53d6\u80fd\u91cf\u6765\u5b8c\u6210\u7684\u3002\u4e0d\u901a\u8fc7\u9b54\u7f51\u5c31\u80fd\u65bd\u6cd5\u7684\u81ea\u7136\u4e5f\u6709\uff0c\u4f46\u90a3\u90fd\u662f\u8bf8\u5982\u7075\u5438\u602a\u4e4b\u7c7b\uff0c\u8d85\u4e4e\u4eba\u7c7b\u7406\u89e3\u8303\u7574\u7684\u602a\u7269\u3002\u6309\u7167\u666e\u904d\u901a\u884c\u7684\u5b66\u7406\uff0c\u7075\u5438\u602a\u5e76\u975e\u7269\u8d28\u754c\u7684\u751f\u7269\uff0c\u800c\u662f\u5f02\u6b21\u5143\u7684\u6765\u5ba2\u3002\u81f3\u5c11\u5728\u4eba\u7c7b\u8fd9\u4e2a\u8303\u7574\u91cc\uff0c\u8fd8\u4e0d\u66fe\u542c\u8bf4\u8fc7\u4e0d\u501f\u52a9\u9b54\u7f51\uff0c\u5c31\u80fd\u591f\u5b8c\u6210\u6cd5\u672f\u7684\u4f8b\u5b50\u3002\u5854\u7c73\u5a05\u770b\u8d77\u6765\u4e0d\u50cf\u662f\u53ea\u7075\u5438\u602a\uff0c\u5979\u4e3a\u4ec0\u4e48\u53ef\u4ee5\u505a\u5230\uff1f</p><p>\u8fd9\u4e2a\u95ee\u9898\u5f88\u503c\u5f97\u6df1\u7a76\uff0c\u4f46\u5bf9\u4e8e\u840d\u6c34\u76f8\u9022\u7684\u964c\u751f\u4eba\u6765\u8bf4\uff0c\u518d\u7ee7\u7eed\u8ffd\u95ee\u4e0b\u53bb\u5c31\u592a\u4e0d\u793c\u8c8c\u4e86\u3002\u6bcf\u4e2a\u4eba\u90fd\u6709\u81ea\u5df1\u7684\u79d8\u5bc6\uff0c\u4e0d\u89c1\u5f97\u559c\u6b22\u548c\u5927\u5bb6\u5206\u4eab\uff0c\u743c\u6069\u81ea\u5df1\u4e5f\u662f\u5982\u6b64\u3002\u6d45\u5c1d\u8f84\u6b62\u5730\u6253\u542c\u51e0\u53e5\uff0c\u8fd8\u53ef\u4ee5\u8bf4\u662f\u57fa\u4e8e\u5355\u7eaf\u7684\u597d\u5947\u5fc3\uff0c\u82e5\u662f\u8981\u5bfb\u6839\u7a76\u5e95\uff0c\u5c31\u5f88\u5bb9\u6613\u9020\u6210\u8bef\u89e3\u3002</p><p>\u201c\u6765\uff0c\u6211\u4eec\u7ee7\u7eed\u73a9\u6e38\u620f\u5427\uff0c\u201d\u89c1\u5854\u7c73\u5a05\u4f3c\u4e4e\u5728\u72b9\u8c6b\uff0c\u743c\u6069\u660e\u667a\u5730\u8f6c\u79fb\u4e86\u8bdd\u9898\uff0c\u201c\u8fd9\u5c40\u6211\u4e00\u5b9a\u8981\u62bd\u5230\u9884\u8a00\u5bb6\uff01\u201d</p><p>\u7136\u540e\u4ed6\u62bd\u5230\u4e86\u4e00\u5f20\u767d\u75f4\u724c\u3002</p><p>\u867d\u7136\u6bd4\u4e0d\u4e0a\u8bb0\u5fc6\u4e2d\u67d0\u79cd\u201c\u4e0b\u4e00\u56de\u5408\u5c31\u5929\u4eae\u201d\u7684\u7535\u8111\u6e38\u620f\uff0c\u4f46\u72fc\u4eba\u6740\u5176\u5b9e\u4e5f\u662f\u5f88\u5bb9\u6613\u8ba9\u4eba\u6c89\u8ff7\u5176\u4e2d\uff0c\u4ee5\u81f3\u4e8e\u5ffd\u7565\u65f6\u95f4\u7684\u3002\u53c8\u73a9\u4e86\u51e0\u5c40\uff0c\u743c\u6069\u4e00\u770b\u5df2\u7ecf\u8fc7\u4e86\u96f6\u70b9\uff0c\u8d76\u7d27\u5f3a\u884c\u6253\u65ad\u51db\uff0c\u8ba9\u6885\u83f2\u65af\u628a\u5979\u62c9\u53bb\u4f11\u606f\u3002</p><p>\u201c\u518d\u73a9\u4e00\u5c40\u561b\uff0c\u6700\u540e\u4e00\u5c40\uff0c\u5c31\u6700\u540e\u8fd9\u4e00\u5c40\uff01\u201d</p><p>\u201c\u4e0d\u884c\uff01\u660e\u5929\u8fd8\u8981\u4e00\u65e9\u8d76\u8def\u5462\u3002\u201d</p><p>\u6885\u83f2\u65af\u6beb\u4e0d\u52a8\u6447\u5730\u62d2\u7edd\u4e86\u51db\u7684\u6073\u6c42\uff0c\u76f4\u63a5\u5c06\u5979\u516c\u4e3b\u62b1\u8d77\u6765\uff0c\u8d70\u8fdb\u6728\u5c4b\u3002\u4ece\u95e8\u53e3\u9690\u9690\u4f20\u6765\u5979\u4eec\u7684\u5bf9\u8bdd\uff0c\u201c\u597d\u5566\u597d\u5566\uff0c\u7761\u89c9\u4e86\uff0c\u90a3\u6211\u4eec\u53bb\u6d17\u6fa1\u5427\uff0c\u827e\u5f25\u8587\u3002\u201d</p><p>\u201c\u4f60\u5148\u53bb\uff0c\u6211\u628a\u5e8a\u94fa\u6574\u7406\u4e00\u4e0b\u3002\u201d</p><p>\u201c\u624d\u4e0d\u8981\uff0c\u6211\u8981\u548c\u827e\u5f25\u8587\u4e00\u8d77\u6d17\u6ce1\u6ce1\u6d74\u3002\u54c7\u545c\uff0c\u827e\u5f25\u8587\u7684\u76ae\u80a4\u597d\u5149\u6ed1\uff0c\u6478\u7740\u5c31\u8ba9\u4eba\u5fc3\u795e\u8361\u6f3e\uff0c\u5c24\u5176\u662f\u80f8\u53e3\u2014\u2014\u554a\u54df\uff0c\u597d\u75db\u597d\u75db\uff01\u201d</p><p>\u201c\u95ed\u5634\uff01\u201d</p><p>\u201c\u827e\u5f25\u8587\u548c\u51db\u7684\u5173\u7cfb\u771f\u597d\u5462\uff0c\u201d\u5854\u7c73\u5a05\u8bf4\uff0c\u201c\u5c31\u50cf\u4eb2\u59d0\u59b9\u4e00\u6837\u3002\u201d</p><p>\u201c\u55ef\uff0c\u5979\u4eec\u662f\u4ece\u5c0f\u4e00\u8d77\u957f\u5927\u7684\u4f19\u4f34\uff0c\u5c31\u662f\uff0c\u201d\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u627e\u4e86\u4e00\u4e2a\u5408\u9002\u7684\u63aa\u8f9e\uff0c\u201c\u2018\u9752\u6885\u7af9\u9a6c\u2019\u90a3\u79cd\u3002\u201d</p><p>\u201c\u9752\u6885\u7af9\u9a6c\u662f\u7528\u6765\u5f62\u5bb9\u60c5\u4fa3\u7684\u5427\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u8bf4\u4e5f\u6ca1\u9519\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u5979\u4eec\u7684\u786e\u662f\u60c5\u4fa3\u561b\u3002\u201d</p><p>\u201c......\u662f\u5417\uff1f\u201d</p><p>\u7ef4\u82e5\u62c9\u968f\u53e3\u900f\u9732\u7684\u8baf\u606f\u663e\u7136\u8ba9\u5854\u7c73\u5a05\u5403\u4e86\u4e00\u60ca\uff0c\u5979\u4e0b\u610f\u8bc6\u5730\u671d\u743c\u6069\u770b\u53bb\uff0c\u743c\u6069\u6342\u7740\u8138\uff0c\u56de\u907f\u4e86\u5979\u8d28\u8be2\u7684\u76ee\u5149\u3002\u6c14\u6c1b\u4e00\u65f6\u53d8\u5f97\u6709\u4e9b\u5c34\u5c2c\uff0c\u6700\u540e\u8fd8\u662f\u73ca\u5609\u6253\u7834\u50f5\u5c40\u3002\u201c\u7684\u786e\u4e5f\u8be5\u4f11\u606f\u4e86\uff0c\u201d\u5979\u7b11\u7740\u5bf9\u5854\u7c73\u5a05\u8bf4\uff0c\u201c\u53ef\u60dc\u623f\u95f4\u4e0d\u591f\u591a\uff0c\u6ca1\u529e\u6cd5\u7ed9\u5ba2\u4eba\u5b89\u6392\u4e13\u95e8\u7684\u5355\u95f4\uff0c\u53ea\u80fd\u6324\u4e00\u6324\u4e86\uff0c\u5982\u679c\u4e0d\u4ecb\u610f\u7684\u8bdd\uff0c\u4eca\u665a\u548c\u6211\u4f4f\u5728\u4e00\u8d77\u5427\u3002\u201d</p><p>\u54a6\uff1f</p><p>\u743c\u6069\u6014\u4e86\u4e00\u4e0b\uff0c\u4ed6\u5e76\u6ca1\u6709\u8fd9\u4e2a\u6253\u7b97\uff0c\u4e4b\u524d\u73ca\u5609\u4e5f\u6ca1\u6709\u548c\u4ed6\u901a\u8fc7\u6c14\uff0c\u4f46\u65e2\u7136\u59d0\u59d0\u90fd\u5df2\u7ecf\u5f00\u53e3\u4e86\uff0c\u4ed6\u5f53\u7136\u4e0d\u53ef\u80fd\u8868\u793a\u53cd\u5bf9\uff0c\u53ea\u80fd\u88c5\u51fa\u4e00\u526f\u201c\u5b8c\u5168\u540c\u610f\u201d\u7684\u8868\u60c5\u52a0\u4ee5\u9ed8\u8ba4\u3002</p><p>\u201c\u4e0d\uff0c\u4e0d\u4e86\uff0c\u6211\u81ea\u5df1\u5c31\u53ef\u4ee5\u2014\u2014\u201d</p><p>\u201c\u90a3\u4e5f\u592a\u6ca1\u793c\u8c8c\u4e86\uff0c\u201d\u73ca\u5609\u6e29\u67d4\u4f46\u575a\u51b3\u5730\u6253\u65ad\uff0c\u201c\u5854\u7c73\u5a05\u662f\u5ba2\u4eba\uff0c\u800c\u4e14\u4e00\u8def\u4e0a\u4fdd\u62a4\u7740\u6211\u4eec\uff0c\u6211\u4eec\u6709\u623f\u5c4b\u5c45\u4f4f\uff0c\u5374\u8ba9\u5ba2\u4eba\u548c\u670b\u53cb\u9732\u5bbf\u8352\u91ce\uff0c\u8fd9\u662f\u7edd\u5bf9\u4e0d\u884c\u7684\uff0c\u5170\u5c3c\u65af\u7279\u5bb6\u53ef\u6ca1\u6709\u8fd9\u79cd\u5f85\u5ba2\u4e4b\u9053\u3002\u201d</p><p>\u5170\u5c3c\u65af\u7279\u5bb6\u8fd8\u6709\u8fd9\u79cd\u89c4\u77e9\uff1f\u4ece\u5c0f\u5230\u5927\uff0c\u743c\u6069\u538b\u6839\u5c31\u4e0d\u8bb0\u5f97\u5bb6\u91cc\u6765\u8fc7\u51e0\u4e2a\u5ba2\u4eba\uff0c\u518d\u8bf4\u5927\u5bb6\u90fd\u4f4f\u5728\u9634\u9b42\u57ce\u8fd9\u79cd\u5c0f\u5730\u65b9\uff0c\u5c31\u7b97\u5ba2\u4eba\u6765\u8bbf\uff0c\u4e5f\u6ca1\u6709\u7559\u4e0b\u4f4f\u5bbf\u7684\uff0c\u5230\u665a\u4e0a\u5c31\u5404\u56de\u5404\u5bb6\u4e86\u3002\u4e0d\u8fc7\u59d0\u59d0\u6700\u5927\uff0c\u8bf4\u4ec0\u4e48\u90fd\u662f\u5bf9\u7684\uff0c\u743c\u6069\u53ea\u8981\u5728\u65c1\u8fb9\u8d1f\u8d23\u70b9\u5934\u8868\u793a\u8d5e\u540c\u5c31\u53ef\u4ee5\u4e86\u3002</p><p>\u5728\u73ca\u5609\u7684\u575a\u6301\u4e4b\u4e0b\uff0c\u5854\u7c73\u5a05\u8fd8\u662f\u7b54\u5e94\u4e86\uff0c\u5c31\u8fd9\u70b9\u6765\u8bf4\uff0c\u5979\u5e76\u4e0d\u50cf\u662f\u90a3\u79cd\u4e3b\u89c1\u6781\u5f3a\u7684\u4eba\u3002\u743c\u6069\u5fc3\u4e2d\u7591\u60d1\uff0c\u4f46\u5f53\u7740\u5ba2\u4eba\u7684\u9762\uff0c\u4e5f\u5b9e\u5728\u4e0d\u65b9\u4fbf\u8be2\u95ee\u3002</p><p>\u201c\u59d0\u59d0\u5230\u5e95\u5728\u60f3\u4ec0\u4e48\u554a\u3002\u201d\u56de\u5230\u623f\u95f4\u91cc\uff0c\u4ed6\u5fcd\u4e0d\u4f4f\u5bf9\u7ef4\u82e5\u62c9\u62b1\u6028\u3002</p><p>\u5c3d\u7ba1\u5854\u7c73\u5a05\u770b\u8d77\u6765\u4e0d\u50cf\u662f\u574f\u4eba\u2014\u2014\u6216\u8005\u8bf4\uff0c\u4e0d\u50cf\u5bf9\u743c\u6069\u7b49\u4eba\u62b1\u6709\u6076\u610f\uff0c\u4f46\u8fd9\u4e5f\u4ec5\u4ec5\u662f\u770b\u8d77\u6765\u800c\u5df2\u3002\u6bd5\u7adf\u624d\u521a\u521a\u8ba4\u8bc6\u4e00\u5929\uff0c\u5f7c\u6b64\u4e4b\u95f4\u8fd8\u8c08\u4e0d\u4e0a\u771f\u6b63\u7684\u4fe1\u4efb\uff0c\u4eba\u7c7b\u662f\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6700\u4e3a\u72e1\u8bc8\u8be1\u8c32\u7684\u751f\u7269\uff0c\u660e\u660e\u62b1\u6709\u5f88\u6df1\u7684\u654c\u610f\u5374\u4f2a\u88c5\u5f97\u4eb2\u8fd1\u968f\u548c\uff0c\u8fd9\u79cd\u4e8b\u60c5\u4e5f\u5f88\u5e38\u89c1\u3002\u743c\u6069\u4e4b\u6240\u4ee5\u9080\u8bf7\u5979\u8fc7\u6765\uff0c\u662f\u62b1\u7740\u5148\u505a\u4e00\u4e9b\u63a5\u89e6\uff0c\u589e\u8fdb\u4e86\u89e3\u7684\u6253\u7b97\uff0c\u53ef\u6ca1\u60f3\u8fc7\u5173\u7cfb\u8981\u53d1\u5c55\u5f97\u8fd9\u4e48\u5feb\u3002\u73ca\u5609\u548c\u5979\u540c\u5c45\u4e00\u5ba4\uff0c\u5018\u82e5\u5979\u6709\u4ec0\u4e48\u4e0d\u826f\u4f01\u56fe\uff0c\u8fd9\u5c82\u4e0d\u662f\u975e\u5e38\u5371\u9669\u3002\u743c\u6069\u4e0d\u76f8\u4fe1\u73ca\u5609\u8fde\u8fd9\u70b9\u90fd\u60f3\u4e0d\u5230\uff0c\u5c31\u7b97\u60f3\u4e0d\u5230\uff0c\u4ed6\u521a\u624d\u4e5f\u5df2\u7ecf\u7528\u773c\u795e\u793a\u610f\uff0c\u7136\u800c\u73ca\u5609\u7f6e\u4e4b\u4e0d\u7406\uff0c\u8fd9\u5c31\u4ee4\u4ed6\u65e2\u5934\u75bc\u53c8\u8ff7\u7cca\u4e86\u3002</p><p>\u201c\u4e5f\u4e0d\u7528\u592a\u62c5\u5fc3\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u5979\u521a\u521a\u6d17\u5b8c\u6fa1\uff0c\u88f9\u7740\u6d74\u5dfe\uff0c\u5934\u53d1\u6e7f\u6f09\u6f09\u5730\u8d70\u51fa\u6765\uff0c\u201c\u4f60\u4e0d\u662f\u5df2\u7ecf\u8ba9\u838e\u73de\u514b\u6697\u4e2d\u4fdd\u62a4\u73ca\u5609\u4e86\u3002\u201d</p><p>\u743c\u6069\u7ed9\u838e\u73de\u514b\u4e0b\u4e86\u6307\u4ee4\uff0c\u8ba9\u5979\u9690\u5f62\u5b88\u62a4\u5728\u73ca\u5609\u8eab\u65c1\uff0c\u5e76\u4e14\u4e25\u5389\u5730\u544a\u8beb\u5979\u5fc5\u987b\u65f6\u523b\u4fdd\u6301\u8b66\u60d5\uff0c\u4e07\u4e07\u4e0d\u53ef\u6709\u4e1d\u6beb\u677e\u61c8\u3002\u9b45\u9b54\u662f\u6740\u624b\u51fa\u8eab\uff0c\u53cd\u8fc7\u6765\u505a\u8d34\u8eab\u4fdd\u9556\u4e5f\u662f\u5408\u9002\u7684\u3002</p><p>\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u8fd8\u662f\u89c9\u5f97\u6ca1\u5fc5\u8981\u5192\u8fd9\u4e2a\u9669\u3002\u201d\u743c\u6069\u8bf4\uff0c\u6709\u4e9b\u4e0d\u5b89\u5730\u5728\u623f\u95f4\u91cc\u8d70\u6765\u8d70\u53bb\u3002</p><p>\u201c\u653e\u5fc3\u5427\uff0c\u6211\u6084\u6084\u7ed9\u73ca\u5609\u7528\u4e86\u4e00\u4e2a\u89e6\u53d1\u672f\uff0c\u5982\u679c\u5979\u53d7\u5230\u653b\u51fb\uff0c\u5c31\u4f1a\u7acb\u523b\u4f20\u9001\u5230\u6211\u8eab\u8fb9\u3002\u201d</p><p>\u201c\u54a6\uff0c\u4f60\u4ec0\u4e48\u65f6\u5019\u65bd\u6cd5\u7684\uff1f\u6211\u600e\u4e48\u6ca1\u770b\u5230\u3002\u201d</p><p>\u201c\u5728\u4f60\u5429\u5490\u838e\u73de\u514b\u7684\u65f6\u5019\u3002\u201d</p><p>\u743c\u6069\u677e\u4e86\u53e3\u6c14\uff0c\u7ef4\u82e5\u62c9\u529e\u4e8b\u8fd8\u662f\u6bd4\u8f83\u9760\u8c31\u7684\uff0c\u6bd5\u7adf\u662f\u5386\u7ec3\u4e30\u5bcc\u7684\u4f20\u9053\u5deb\u5e08\uff0c\u4e0d\u662f\u90a3\u79cd\u8c61\u7259\u5854\u91cc\u7684\u5b66\u9662\u6d3e\u3002</p><p>\uff08\u60ef\u4f8b\u4e0d\u63cf\u8ff0\uff09</p><p>\u201c\u505a\u5f97\u4e0d\u9519\u3002\u201d\u743c\u6069\u5938\u5956\u3002</p><p>\u201c\u8c22\u8c22\u3002\u201d</p><p>\u8bb0\u5f97\u5c31\u5728\u4e00\u4e24\u4e2a\u6708\u4e4b\u524d\uff0c\u641e\u5979\u7684\u65f6\u5019\u5979\u8fd8\u4e0d\u600e\u4e48\u914d\u5408\uff0c\u73b0\u5728\u5df2\u7ecf\u53d8\u5f97\u8fd9\u4e48\u81ea\u7136\uff0c\u4eff\u4f5b\u76f8\u5904\u591a\u5e74\u7684\u60c5\u4eba\u3002\u8fd9\u79cd\u8fc7\u5206\u81ea\u7136\uff0c\u53cd\u800c\u8ba9\u743c\u6069\u6709\u79cd\u201c\u4e0d\u81ea\u7136\u201d\u7684\u611f\u89c9\uff0c\u603b\u89c9\u5f97......\u7ef4\u82e5\u62c9\u50cf\u662f\u9690\u85cf\u4e86\u4ec0\u4e48\u3002</p><p>\u5802\u5802\u4f20\u9053\u5deb\u5e08\uff0c\u4e0d\u5e94\u8be5\u662f\u8fd9\u4e48\u8f7b\u6613\u5c31\u80fd\u5f81\u670d\u7684\u5427\u3002</p><p>\u743c\u6069\u5728\u5fc3\u91cc\u81ea\u8a00\u81ea\u8bed\u3002\u8fd9\u4e0e\u5176\u8bf4\u662f\u81ea\u77e5\u4e4b\u660e\uff0c\u4e0d\u5982\u8bf4\u662f\u4e0d\u81ea\u4fe1\uff0c\u4ed6\u4ece\u6765\u5c31\u4e0d\u89c9\u5f97\u81ea\u5df1\u6709\u90a3\u6837\u7684\u9b45\u529b\uff0c\u80fd\u591f\u8ba9\u5973\u6027\u4e00\u89c1\u949f\u60c5\uff0c\u5c31\u6b64\u503e\u5fc3\u3002\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\u867d\u7136\u4e0d\u5c11\uff0c\u4f46\u65e0\u4e00\u4f8b\u5916\u90fd\u662f\u8f9b\u82e6\u8ffd\u6c42\u6765\u7684\uff0c\u5982\u679c\u5f97\u624b\u592a\u8f7b\u6613\uff0c\u53cd\u800c\u4f1a\u8ba9\u4ed6\u89c9\u5f97\u4e0d\u9002\u5e94\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u4f60\u5e72\u561b\u8981\u544a\u8bc9\u5854\u7c73\u5a05\u90a3\u79cd\u4e8b\u3002\u201d\u743c\u6069\u8d28\u95ee\u3002</p><p>\u201c\u55ef\uff1f\u4f60\u662f\u8bf4\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u201c\u5c31\u662f\u827e\u5f25\u8587\u548c\u51db\u662f\u60c5\u4fa3\u7684\u4e8b\u554a\u3002\u201d</p><p>\u201c\u54e6\uff0c\u90a3\u4e2a\u554a\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u8fd9\u79cd\u4e8b\u60c5\u4e5f\u6ca1\u6709\u9690\u7792\u7684\u610f\u4e49\u5427\uff0c\u53cd\u6b63\u5979\u4eec\u7684\u5173\u7cfb\uff0c\u4efb\u4f55\u4e00\u4e2a\u667a\u529b\u6b63\u5e38\u7684\u4eba\u90fd\u80fd\u770b\u5f97\u51fa\u6765\u3002\u201d</p><p>\u201c\u90a3\u4e5f\u4e0d\u7528\u4f60\u7279\u610f\u8bf4\u51fa\u6765\u554a\uff01\u201d</p><p>\u5973\u670b\u53cb\u662f\u857e\u4e1d\u8fb9\u2014\u2014\u4e0d\uff0c\u51c6\u786e\u6765\u8bf4\u5e94\u8be5\u662f\u53cc\u6027\u604b\uff0c\u8fd9\u5bf9\u7537\u6027\u800c\u8a00\u867d\u7136\u8bf4\u4e0d\u4e0a\u7f9e\u8fb1\uff0c\u4f46\u603b\u8fd8\u662f\u6709\u4e9b\u96be\u4ee5\u542f\u9f7f\uff0c\u5c31\u597d\u50cf\u662f\u5728\u6697\u793a\u81ea\u5df1\u7f3a\u4e4f\u96c4\u6027\u9b45\u529b\u4f3c\u7684\u3002\u4e0d\u8fc7\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u8fd9\u7ec8\u7a76\u4e5f\u4e0d\u7b97\u591a\u4e48\u4e25\u91cd\u7684\u4e8b\u60c5\uff0c\u5145\u5176\u91cf\u662f\u8ba9\u743c\u6069\u89c9\u5f97\u6709\u4e9b\u4e22\u8138\u7f62\u4e86\u3002</p><p>\u201c\u4f60\u5728\u5e72\u4ec0\u4e48\uff1f\u201d</p><p>\u7ef4\u82e5\u62c9\u53bb\u6f31\u4e86\u53e3\uff0c\u56de\u6765\u53d1\u73b0\u743c\u6069\u6b63\u5728\u5b8c\u6210\u4e00\u4e2a\u6cd5\u672f\uff0c\u6697\u94f6\u8272\u7684\u9b54\u6cd5\u7075\u5149\u5728\u4ed6\u7684\u624b\u6307\u95f4\u6765\u56de\u8df3\u8dc3\uff0c\u6e10\u6e10\u51dd\u805a\u6210\u4e00\u4e2a\u900f\u660e\u7684\u3001\u773c\u7403\u6a21\u6837\u7684\u4e1c\u897f\uff0c\u7136\u540e\u5189\u5189\u6d6e\u8d77\uff0c\u8f6c\u4e86\u51e0\u4e2a\u5708\uff0c\u6162\u60a0\u60a0\u5730\u671d\u95e8\u5916\u98de\u53bb\u3002</p><p>\u201c\u79d8\u6cd5\u773c\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u5e9f\u8bdd\uff0c\u6211\u662f\u95ee\u4f60\u53ec\u5524\u5b83\u5e72\u561b\u2014\u2014\u4f60\u4e0d\u4f1a\u662f\u60f3\u53bb\u5077\u7aa5\u5427\uff1f\u201d</p><p>\u201c\u4e0d\uff0c\u662f\u53bb\u786e\u8ba4\u5b89\u5168\u3002\u201d</p><p>\u5c3d\u7ba1\u6709\u838e\u73de\u514b\u5b88\u536b\uff0c\u4ee5\u53ca\u7ef4\u82e5\u62c9\u7684\u6cd5\u672f\u9632\u5fa1\uff0c\u6309\u7406\u6765\u8bf4\u4e0d\u4f1a\u6709\u95ee\u9898\uff0c\u4f46\u743c\u6069\u4f9d\u7136\u5bf9\u59d0\u59d0\u548c\u5854\u7c73\u5a05\u540c\u5904\u4e00\u5ba4\u8fd9\u4ef6\u4e8b\u4e0d\u592a\u653e\u5fc3\u3002\u95ee\u9898\u662f\u4ed6\u603b\u4e0d\u80fd\u534a\u591c\u8dd1\u5230\u4e24\u4e2a\u5c11\u5973\u7684\u5367\u5ba4\u91cc\u53bb\uff0c\u90a3\u80af\u5b9a\u4f1a\u88ab\u5f53\u6210\u53d8\u6001\u7ed9\u8e39\u51fa\u6765\uff0c\u6240\u4ee5\u53ea\u80fd\u5f04\u53ea\u79d8\u6cd5\u773c\u53bb\u770b\u770b\u60c5\u51b5\u4e86\u3002</p><p>\u201c\u5514\uff0c\u95e8\u9501\u7740\uff0c\u8fdb\u4e0d\u53bb\u554a\uff0c\u90a3\u5c31\u5148\u6682\u65f6\u865a\u5316......\u8fdb\u53bb\u4e86\uff0c\u201d\u743c\u6069\u5c06\u6ce8\u610f\u529b\u5b8c\u5168\u805a\u96c6\u5230\u79d8\u6cd5\u773c\u4f20\u56de\u7684\u753b\u9762\u4e0a\uff0c\u201c\u59d0\u59d0\u5df2\u7ecf\u7761\u7740\u4e86\u3002\u65c1\u8fb9\u662f\u5854\u7c73\u5a05\uff0c\u770b\u8d77\u6765\u4e5f\u7761\u7740\u4e86......\u54a6\uff0c\u80a9\u8180\u548c\u624b\u81c2\u7684\u76ae\u80a4\u5f88\u767d\u561b\uff0c\u611f\u89c9\u6bd4\u8138\u548c\u8116\u9888\u8981\u660e\u663e\u767d\u5f88\u591a\uff0c\u662f\u56e0\u4e3a\u5e73\u5e38\u90fd\u88ab\u8863\u670d\u88f9\u7740\u4e0d\u6652\u592a\u9633\u7684\u7f18\u6545\u4e48......\u201d</p><p>\u7ef4\u82e5\u62c9\u5728\u8033\u8fb9\u54b3\u55fd\u4e86\u4e00\u58f0\u3002</p><p>\u201c\u5443\uff0c\u4e0d\u597d\u610f\u601d\uff0c\u201d\u743c\u6069\u8d76\u7d27\u5207\u65ad\u4e86\u548c\u79d8\u6cd5\u773c\u7684\u7cbe\u795e\u8fde\u63a5\uff0c\u201c\u53ea\u662f\u6070\u597d\u770b\u5230\uff0c\u4e0d\u662f\u6709\u610f\u7684\u3002\u201d</p><p>\u201c\u4f60\u8fd9\u4e2a\u8272\u72fc\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u771f\u662f\u6ca1\u6709\u534a\u70b9\u8bf4\u670d\u529b\u3002\u201d</p><p>\u65e0\u8bba\u5982\u4f55\uff0c\u4eb2\u773c\u786e\u5b9a\u73ca\u5609\u7684\u5b89\u5168\u4e4b\u540e\uff0c\u743c\u6069\u6682\u65f6\u653e\u4e0b\u5fc3\u6765\u2014\u2014\u4f46\u4e5f\u4ec5\u4ec5\u662f\u6682\u65f6\u7684\u3002</p><p>\uff08\u60ef\u4f8b\uff09</p><p>\u7d2f\u6b7b\u4e86\u3002</p><p>\u6210\u719f\u5973\u6027\u8eab\u4f53\u7684\u9700\u7d22\uff0c\u8fdc\u8fdc\u4e0d\u662f\u5341\u51e0\u5c81\u9752\u6da9\u5c11\u5973\u53ef\u6bd4\u7684\uff0c\u63a5\u8fde\u51e0\u5929\u7684\u5bc6\u96c6\u5f81\u6218\uff0c\u743c\u6069\u8fd8\u771f\u6709\u70b9\u5403\u4e0d\u6d88\u3002\u4e4b\u524d\u548c\u6885\u83f2\u65af\u3001\u51db\u73a9\u7684\u65f6\u5019\uff0c\u867d\u7136\u505a\u5f97\u4e5f\u4e0d\u5c11\uff0c\u4f46\u603b\u89c9\u5f97\u6ca1\u6709\u8fd9\u6837\u8017\u8d39\u7cbe\u529b\uff0c\u7ef4\u82e5\u62c9\u7684\u9999\u8f6f\u8089\u4f53\uff0c\u5c31\u50cf\u662f\u9ed1\u6d1e\u6df1\u6e0a\u4e00\u822c\uff0c\u6709\u79cd\u4f1a\u5c06\u4eba\u6574\u4e2a\u90fd\u541e\u566c\u8fdb\u53bb\u7684\u9519\u89c9......\u8be5\u4e0d\u4f1a\u5979\u5176\u5b9e\u6709\u9b45\u9b54\u7684\u8840\u7edf\u5427\u3002</p><p>\u5e26\u7740\u8fd9\u79cd\u7591\u95ee\uff0c\u743c\u6069\u6700\u540e\u4e00\u6b21\u8fde\u63a5\u79d8\u6cd5\u773c\uff0c\u786e\u8ba4\u73ca\u5609\u7684\u5b89\u5168\uff0c\u53c8\u91cd\u590d\u53ee\u5631\u4e86\u4e00\u6b21\u838e\u73de\u514b\uff0c\u7136\u540e\u81ea\u5df1\u4e5f\u8ff7\u8ff7\u7cca\u7cca\u5730\u7761\u7740\u4e86\u3002</p><p>\u4e00\u591c\u5e73\u9759\u5730\u8fc7\u53bb\uff0c\u7b2c\u4e8c\u5929\u65e9\u4e0a\uff0c\u5f53\u743c\u6069\u4e0b\u697c\u53bb\u5403\u65e9\u9910\u7684\u65f6\u5019\uff0c\u88ab\u544a\u77e5\u5854\u7c73\u5a05\u5df2\u7ecf\u79bb\u5f00\u3002</p><p>\u63a5\u4e0b\u6765\u7684\u65c5\u7a0b\u5e73\u6de1\u65e0\u5947\uff0c\u6ca1\u6709\u53d1\u751f\u4e1d\u6beb\u610f\u5916\uff0c\u5230\u4e86\u508d\u665a\u5bbf\u8425\u7684\u65f6\u5019\uff0c\u5854\u7c73\u5a05\u5c31\u4f1a\u81ea\u5df1\u51fa\u73b0\uff0c\u52a0\u5165\u961f\u4f0d\u4e4b\u4e2d\uff0c\u4e00\u8d77\u665a\u9910\u3001\u73a9\u6e38\u620f\u3001\u804a\u5929\u3002\u4ece\u5979\u53e3\u4e2d\uff0c\u743c\u6069\u5f97\u77e5\u4e86\u66f4\u591a\u6709\u5173\u4e1c\u57df\u3001\u6069\u745f\uff0c\u4ee5\u53ca\u5373\u5c06\u8981\u53bb\u7684\u90a3\u5ea7\u201c\u96c5\u5c14\u8d1d\u7433\u5a1c\u57ce\u201d\u7684\u4fe1\u606f\u2014\u2014\u5176\u4e2d\u6700\u540e\u8fd9\u4e00\u9879\u6d89\u53ca\u5f97\u5e76\u4e0d\u591a\uff0c\u56e0\u4e3a\u636e\u5854\u7c73\u5a05\u8bf4\uff0c\u5979\u4e0e\u795e\u59ec\u7684\u7ea6\u5b9a\u4e4b\u4e2d\uff0c\u5305\u542b\u6709\u201c\u4e0d\u80fd\u5411\u65c5\u884c\u8005\u8fc7\u591a\u8bb2\u8ff0\u96c5\u5c14\u8d1d\u7433\u5a1c\u57ce\u7684\u4e8b\u60c5\u201d\u7684\u5185\u5bb9\u3002</p><p>\u4f46\u5728\u6700\u540e\u5206\u624b\u65f6\uff0c\u5854\u7c73\u5a05\u7ed9\u51fa\u4e86\u4e00\u4e2a\u8bed\u6c14\u8bda\u6073\u7684\u544a\u8beb\u3002</p><p>\u201c\u5c3d\u53ef\u80fd\u8fdc\u79bb\u90a3\u4e2a\u4e4c\u9e26\u57ce\u4e3b\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8d8a\u8fdc\u8d8a\u597d\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u4ed6\u5f88\u5371\u9669\u5417\uff1f\u201d</p><p>\u201c\u662f\u5426\u5371\u9669\u4e0d\u597d\u8bf4\uff0c\u4f46\u4ed6\u662f\u4e2a\u53d8\u6001\uff0c\u201d\u5854\u7c73\u5a05\u5f3a\u8c03\uff0c\u201c\u8d85\u7ea7\u5927\u53d8\u6001\u3002\u201d</p><p>\u7a76\u7adf\u5982\u4f55\u53d8\u6001\uff0c\u5854\u7c73\u5a05\u5c31\u4e0d\u80af\u8bf4\u4e86\uff0c\u6216\u8bb8\u662f\u9075\u5b88\u7ea6\u5b9a\u4e0d\u65b9\u4fbf\u900f\u9732\uff0c\u4f46\u743c\u6069\u770b\u5979\u7684\u8868\u60c5\uff0c\u66f4\u50cf\u662f\u8fde\u60f3\u8d77\u90fd\u89c9\u5f97\u6076\u5fc3\u4f3c\u7684\u3002\u76f8\u5904\u6570\u65e5\uff0c\u5f7c\u6b64\u7684\u5173\u7cfb\u4e5f\u53d8\u5f97\u4eb2\u8fd1\u548c\u4fe1\u4efb\u8d77\u6765\uff0c\u867d\u7136\u6ca1\u6709\u5177\u4f53\u8bf4\u660e\u7f18\u7531\uff0c\u4f46\u5854\u7c73\u5a05\u65e2\u7136\u5982\u6b64\u614e\u91cd\u5176\u4e8b\u5730\u63d0\u9192\uff0c\u90a3\u5c31\u80af\u5b9a\u6709\u5176\u9053\u7406\u3002\u4f46\u65e2\u7136\u662f\u8981\u53bb\u4eba\u5bb6\u7684\u5730\u76d8\uff0c\u60f3\u907f\u5f00\u4e5f\u672a\u5fc5\u80fd\u591f\u5982\u613f\uff0c\u53ea\u80fd\u968f\u673a\u5e94\u53d8\u4e86\u3002</p><p>\u201c\u90a3\u4e48\uff0c\u5c31\u6b64\u9053\u522b\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u611f\u8c22\u4f60\u4e00\u8def\u62a4\u9001\u3002\u201d</p><p>\u524d\u65b9\u5c31\u662f\u96c5\u5c14\u8d1d\u7433\u5a1c\u57ce\uff0c\u5df2\u7ecf\u80fd\u591f\u9690\u7ea6\u770b\u89c1\u9ad8\u8038\u5c16\u5854\uff0c\u4f30\u6478\u7740\u4e0d\u5230\u534a\u65e5\u8def\u7a0b\u3002\u5854\u7c73\u5a05\u4e0d\u53bb\u57ce\u91cc\uff0c\u5979\u7684\u5de5\u4f5c\u662f\u62a4\u9001\u4ece\u6069\u745f\u6765\u7684\u65c5\u4eba\uff0c\u4ee5\u53ca\u5728\u68ee\u6797\u91cc\u5bfb\u627e\u80fd\u591f\u89e3\u9664\u5384\u8fd0\u8bc5\u5492\u7684\u7ebf\u7d22\u3002\u201c\u5bf9\u4e86\uff0c\u8fd9\u4e2a\u9001\u7ed9\u4f60\uff0c\u5854\u7c73\u5a05\uff0c\u201c\u73ca\u5609\u4ece\u53e3\u888b\u91cc\u6478\u51fa\u4e00\u4e2a\u623f\u5c4b\u6a21\u578b\u4f3c\u7684\u4e1c\u897f\uff0c\u201c\u6211\u81ea\u5df1\u505a\u7684\uff0c\u53ef\u80fd\u4e0d\u662f\u5f88\u597d\uff0c\u4f46\u5e94\u8be5\u80fd\u6d3e\u4e0a\u70b9\u7528\u573a\u3002\u201d</p><p>\u8fd9\u662f\u4e00\u4e2a\u7c7b\u4f3c\u4e8e\u5965\u6c83\u7ed9\u743c\u6069\u7684\u201c\u9752\u94dc\u8c6a\u5b85\u201d\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u5f53\u7136\u5176\u4ef7\u503c\u548c\u529f\u7528\u76f8\u5dee\u4e0d\u53ef\u4ee5\u9053\u91cc\u8ba1\uff0c\u7528\u9014\u5c31\u662f\u80fd\u591f\u4e34\u65f6\u53d8\u6210\u4e00\u95f4\u72ed\u5c0f\u7684\u6728\u5c4b\uff0c\u4ec0\u4e48\u5bb6\u5177\u90fd\u6ca1\u6709\uff0c\u4f46\u906e\u6321\u98ce\u96e8\u8fd8\u662f\u53ef\u4ee5\u7684\u3002\u5854\u7c73\u5a05\u4e00\u76f4\u5728\u68ee\u6797\u91cc\u6d3b\u52a8\uff0c\u6069\u745f\u7684\u96e8\u5b63\u53c8\u5df2\u7ecf\u6765\u4e34\uff0c\u6709\u4e86\u8fd9\u4e1c\u897f\uff0c\u7684\u786e\u80fd\u591f\u7701\u53bb\u5f88\u591a\u70e6\u607c\u3002</p><p>\u5854\u7c73\u5a05\u5e76\u4e0d\u63a8\u8f9e\uff0c\u4f38\u624b\u63a5\u8fc7\uff0c\u201c\u544a\u8f9e\u4e86\uff0c\u8bf8\u4f4d\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5e0c\u671b\u80fd\u591f\u6709\u7f18\u518d\u89c1\uff0c\u795d\u4f60\u4eec\u597d\u8fd0\u3002\u201d</p><p>\u201c\u518d\u89c1\uff0c\u4e0b\u6b21\u6211\u4eec\u518d\u4e00\u8d77\u73a9\u72fc\u4eba\u6740\u554a\u3002\u201d\u51db\u6325\u7740\u624b\u8bf4\u3002</p><p>\u76ee\u9001\u5854\u7c73\u5a05\u7684\u8eab\u5f71\u5728\u6811\u6797\u4e2d\u6d88\u5931\uff0c\u743c\u6069\u8f6c\u8fc7\u8eab\uff0c\u671b\u5411\u8fdc\u5904\u9ad8\u8038\u7684\u5c16\u5854\u3002</p><p>\u201c\u90a3\u5e94\u8be5\u662f\u9b54\u6cd5\u5b66\u9662\u5427\uff0c\u59d0\u59d0\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u73ca\u5609\u9ed8\u9ed8\u5730\u70b9\u5934\u3002</p><p>\u867d\u7136\u8fd8\u9694\u7740\u4e00\u6bb5\u8ddd\u79bb\uff0c\u770b\u5f97\u5e76\u4e0d\u662f\u5f88\u771f\u5207\uff0c\u4f46\u5bf9\u4e8e\u73ca\u5609\u548c\u743c\u6069\u4e24\u4eba\u6765\u8bf4\uff0c\u4ec5\u4ec5\u53ea\u662f\u8fdc\u8fdc\u671b\u89c1\u8f6e\u5ed3\uff0c\u5c31\u5df2\u7ecf\u8db3\u4ee5\u786e\u5b9a\u4e00\u4ef6\u4e8b\uff1a\u90a3\u662f\u9634\u9b42\u57ce\uff0c\u4e0d\uff0c\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u8010\u745f\u65f6\u4ee3\u7684\u5efa\u7b51\u3002\u90a3\u79cd\u867d\u7136\u8bf4\u4e0d\u4e0a\u9c9c\u660e\u6216\u8005\u72ec\u7279\uff0c\u4f46\u4ee4\u4eba\u7edd\u5bf9\u4e0d\u4f1a\u6df7\u6dc6\u7684\u98ce\u683c\uff0c\u59d0\u5f1f\u4fe9\u5728\u9634\u9b42\u57ce\u751f\u6d3b\u3001\u5c45\u4f4f\u3001\u6210\u957f\u7684\u8fd9\u5341\u51e0\u5e74\u91cc\uff0c\u5df2\u7ecf\u770b\u4e86\u4e0d\u77e5\u65e0\u6570\u904d\uff0c\u719f\u6089\u5f97\u4e0d\u80fd\u518d\u719f\u6089\u3002</p><p>\u6bcb\u5eb8\u7f6e\u7591\uff0c\u90a3\u5c31\u662f\u6b64\u884c\u7684\u76ee\u7684\u5730\uff0c\u5728\u8010\u745f\u5e1d\u56fd\u7684\u6700\u540e\u65f6\u523b\uff0c\u9b54\u7f51\u5f7b\u5e95\u5d29\u6e83\u4e4b\u65f6\uff0c\u67d0\u4f4d\u5927\u5965\u672f\u5e08\u5760\u843d\u5728\u8ff7\u5931\u68ee\u6797\u4e2d\u7684\u6d6e\u7a7a\u57ce\u3002</p></article>"}}}]);
