(window.webpackJsonp=window.webpackJsonp||[]).push([[562],{585:function(p){p.exports={chapterTitle:null,title:"\u7b2c562\u7ae0 \u8f6c\u79fb",content:"<article><h2>\u7b2c562\u7ae0 \u8f6c\u79fb</h2><p>\u3000\u3000\u81ea\u7231\u8fbe\u7535\u5b50\u521b\u7acb\u8d77\uff0c\u76db\u9752\u3001\u90b5\u81f3\u521a\u3001\u5f20\u77e5\u975e\u7b49\u4eba\u5c31\u5f00\u59cb\u505a\u76d7\u7248\u789f\uff0c\u6700\u521d\u662f\u76f4\u63a5\u751f\u4ea7\uff0c\u5728\u5f20\u606a\u7684\u4fc3\u4f7f\u4e0b\uff0c\u5c06\u538b\u789f\u7ebf\u5168\u90e8\u8f6c\u8ba9\u51fa\u53bb\uff0c\u4e13\u6ce8\u4e8e\u76d7\u7248\u4ea7\u4e1a\u88e2\u4e0a\u6e38\u7684\u8bbe\u5907\u8d38\u6613\u4e0e\u6bcd\u76d8\u5236\u4f5c\uff0c\u8fd8\u5728\u6df1\u5733\u7b49\u5730\u6210\u7acb\u5341\u591a\u6570\u8f6f\u4ef6\u5de5\u4f5c\u5ba4\u6027\u8d28\u7684\u516c\u53f8\u4ece\u4e8b\u4e00\u7cfb\u5217\u7684\u6bcd\u76d8\u5de5\u5e8f\u5236\u4f5c\uff0c\u76db\u9752\u3001\u6881\u519b\u8fd9\u4e00\u4e24\u5e74\u7684\u7cbe\u529b\u51e0\u4e4e\u5168\u90e8\u538b\u5728\u6df1\u5733\uff0c\u8fd9\u4e9b\u516c\u53f8\u4e5f\u6e10\u6e10\u8d70\u4e0a\u8f68\u9053\uff0c\u5f20\u606a\u5c31\u5e0c\u671b\u4ed6\u4eec\u4ece\u8fd9\u4e9b\u5de5\u4f5c\u5ba4\u7684\u4e1a\u52a1\u7ba1\u7406\u4e2d\u5b8c\u5168\u64a4\u51fa\u6765\uff0c\u53ea\u4e0e\u8fd9\u4e9b\u516c\u53f8\u4fdd\u6301\u4e00\u5b9a\u7684\u8d22\u52a1\u63a7\u5236\u5173\u7cfb\u3002</p><p>\u3000\u3000\u867d\u7136\u8bf4\u56fd\u5185\u7684\u76d7\u7248\u4ea7\u4e1a\u89c4\u6a21\u4f1a\u8fdb\u4e00\u6b65\u6269\u5927\uff0c\u4f46\u662f\u56fd\u5bb6\u5728\u77e5\u8bc6\u4ea7\u6743\u65b9\u9762\u7684\u7acb\u6cd5\u4e0e\u6267\u6cd5\u4f1a\u9010\u5e74\u4e25\u5389\uff0c\u5f20\u606a\u8fd9\u662f\u8981\u76db\u9752\u4ed6\u4eec\u8fdb\u4e00\u6b65\u4e0e\u76d7\u7248\u4ea7\u4e1a\u8131\u79bb\u3002</p><p>\u3000\u3000\u4e4b\u524d\u5efa\u7acb\u7684\u7ba1\u7406\u56e2\u961f\uff0c\u5927\u90e8\u5206\u4eba\u53c2\u80a1\u6210\u4e3a\u4e0b\u9762\u516c\u53f8\u7684\u8d1f\u8d23\u4eba\uff0c\u76db\u9752\u4e0e\u6881\u519b\u8fd8\u6709\u4e00\u4e9b\u4e0d\u60f3\u5728\u8fd9\u4e2a\u884c\u5f53\u5e72\u4e0b\u53bb\u7684\u9aa8\u5e72\u5c31\u6ca1\u6709\u5fc5\u8981\u7ee7\u7eed\u7559\u5728\u6df1\u5733\u4e86\u3002</p><p>\u3000\u3000\u4e0d\u51fa\u5f20\u606a\u7684\u610f\u6599\uff0c\u5c0f\u53d4\u3001\u90b5\u81f3\u521a\u3001\u76db\u9752\u4ed6\u4eec\u5c06\u9910\u996e\u5a31\u4e50\u516c\u53f8\u4e5f\u51a0\u4ee5\u4e16\u7eaa\u9526\u6e56\u7684\u540d\u79f0\uff0c\u90b5\u81f3\u521a\u7684\u7cbe\u529b\u8f6c\u79fb\u5230\u5546\u4e1a\u5730\u4ea7\u5f00\u53d1\u4e0a\uff0c\u7279\u522b\u5e74\u540e\u4e39\u4e95\u5df7\u6539\u9020\u9879\u76ee\u63d0\u4e0a\u65e5\u7a0b\uff0c\u76db\u9752\u5c31\u7559\u5728\u6d77\u5dde\u4eb2\u81ea\u6293\u8d77\u9910\u996e\u5a31\u4e50\u65b9\u9762\u7684\u7ba1\u7406\u2014\u2014\u8fd9\u672c\u8eab\u5c31\u662f\u4ed6\u7684\u5f3a\u9879\uff0c\u8fd9\u4e24\u5e74\u53c8\u5728\u6df1\u5733\u3001\u9999\u6e2f\u4e24\u5730\u5954\u6ce2\uff0c\u957f\u4e86\u5f88\u591a\u89c1\u8bc6\uff0c\u8fd8\u5c06\u9999\u6e2f\u4e00\u4e2a\u8d1f\u8d23\u7ecf\u8425\u5927\u4f17\u5316\u5a31\u4e50\u573a\u6240\u7684\u56e2\u961f\u76f4\u63a5\u62c9\u4e86\u8fc7\u6765\uff1b\u6df1\u5733\u90a3\u8fb9\u7684\u4e8b\u52a1\u6bd5\u7adf\u4e0d\u80fd\u9a6c\u4e0a\u5c31\u8131\u624b\uff0c\u6881\u519b\u5e74\u540e\u8fd8\u662f\u53bb\u4e86\u6df1\u5733\uff0c\u5f20\u606a\u4fbf\u60f3\u501f\u8fd9\u6b21\u673a\u4f1a\u5c06\u6881\u519b\u4e5f\u5f7b\u5e95\u7684\u4ece\u6df1\u5733\u62c9\u56de\u6765\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u770b\u4e86\u770b\u90b5\u81f3\u521a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u770b\u5c31\u8ba9\u6881\u519b\u56de\u6765\u7b97\u4e86\uff0c\u6df1\u5733\u90a3\u8fb9\u4e5f\u5c31\u5269\u4e0b\u4e9b\u8d22\u52a1\u7ba1\u7406\u4e0a\u7684\u5de5\u4f5c\u3002\u90a3\u5e2e\u9f9f\u5b59\u5b50\uff0c\u770b\u5230\u4f60\u4e00\u4e2a\u4e2a\u53eb\u5927\u7237\u3001\u6bd5\u606d\u6bd5\u656c\u7684\uff0c\u4f46\u662f\u60f3\u4ed6\u4eec\u4e0d\u505a\u5047\u8d26\u662f\u4e0d\u53ef\u80fd\u7684\uff0c\u8d22\u52a1\u7ba1\u7406\uff0c\u600e\u4e48\u7ba1\u7406\uff1f\u80fd\u6709\u94b1\u6536\u5c31\u4e0d\u9519\u4e86\uff1b\u4e5f\u5c31\u6307\u671b\u65e5\u540e\u6c5f\u6e56\u76f8\u89c1\uff0c\u7ed9\u6211\u4eec\u7559\u4e9b\u8584\u9762\u2026\u2026\u201d</p><p>\u3000\u3000\u867d\u8bf4\u4ece\u4e8b\u76d7\u7248\u8fd8\u6709\u6781\u4e30\u539a\u7684\u5229\u6da6\uff0c\u4f46\u662f\u5185\u5730\u7684\u5176\u4ed6\u4e8b\u60c5\u4e5f\u9010\u6e10\u8d77\u6b65\uff0c\u8be5\u653e\u624b\u5c31\u653e\u624b\uff0c\u4eba\u4e0d\u5e94\u8be5\u5c06\u6240\u6709\u7684\u94b1\u90fd\u63fd\u5165\u81ea\u5df1\u7684\u6000\u91cc\uff0c\u8fd9\u662f\u4ed6\u8ddf\u5728\u5f20\u606a\u8eab\u8fb9\u8fd9\u4e9b\u5e74\u6700\u5927\u7684\u9886\u609f\u4e86\u3002</p><p>\u3000\u3000\u60f3\u60f3\u9526\u6e56\uff0c\u9762\u5bf9\u591a\u5c11\u5229\u6da6\u4e30\u539a\u7684\u9886\u57df\u90fd\u7edd\u4e0d\u8d2a\u5fc3\uff0c\u53ea\u4e13\u6ce8\u7535\u5b50\u3001\u9020\u7eb8\u4e24\u4e2a\u884c\u4e1a\uff0c\u5176\u4f59\u7684\u7ec6\u679d\u672b\u8282\u90fd\u6beb\u4e0d\u72b9\u8c6b\u7684\u780d\u6389\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u4ee5\u554a\uff0c\u8ba9\u6881\u519b\u56de\u6765\uff0c\u59d4\u5c48\u4ed6\u7ed9\u6211\u5f53\u52a9\u624b\uff0c\u6211\u4e5f\u53ef\u4ee5\u7701\u4e9b\u529b\u6c14\u3002\u201d\u6881\u519b\u6700\u521d\u5230\u6d77\u5dde\uff0c\u5c31\u7ed9\u4e22\u5230\u5b8f\u8fdc\u5b9e\u4e1a\u91cc\u953b\u70bc\uff0c\u4e09\u4e2a\u6708\u8f6c\u4e00\u4e2a\u804c\u52a1\uff0c\u51e0\u4e4e\u5c06\u5de5\u7a0b\u5efa\u9020\u6240\u6709\u76f8\u5173\u4e8b\u52a1\u90fd\u719f\u6089\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f53\u65f6\u5f20\u77e5\u975e\u5927\u529b\u953b\u70bc\u7684\u63a5\u73ed\u4eba\uff0c\u9664\u4e86\u6881\u519b\u4e4b\u5916\uff0c\u8fd8\u6709\u4ed6\u5927\u54e5\u5f20\u77e5\u5fae\u7684\u513f\u5b50\u5f20\u5955\u3002</p><p>\u3000\u3000\u5948\u4f55\u5f20\u5955\u4e0d\u6210\u5668\uff0c\u7ed9\u5f20\u77e5\u975e\u8d76\u56de\u4e1c\u793e\uff0c\u5f20\u5955\u56de\u4e1c\u793e\u4e4b\u540e\u81ea\u5df1\u5f00\u516c\u53f8\uff0c\u4ece\u4ed6\u8001\u5b50\u6c11\u8d22\u5c40\u632a\u51fa\u516c\u6b3e\u5374\u7ed9\u5408\u4f19\u4eba\u5377\u8d70\u4e86\uff0c\u867d\u8bf4\u51e0\u5341\u4e07\u7684\u94b1\u6b3e\u5bf9\u5f20\u5bb6\u6765\u8bf4\u5b9e\u5728\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u5f20\u77e5\u975e\u66ff\u4ed6\u4eec\u5c06\u94b1\u57ab\u4e0a\uff0c\u4f46\u662f\u5f20\u77e5\u5fae\u6700\u540e\u8fd8\u662f\u7ed9\u8d76\u51fa\u4e1c\u793e\u53bf\u6c11\u653f\u5c40\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u540e\u6765\u53d1\u751f\u7684\u8bb8\u591a\u4e8b\u60c5\uff0c\u90fd\u4e0d\u662f\u7279\u522b\u7684\u5149\u5f69\uff0c\u751a\u81f3\u6bd4\u5f20\u77e5\u5fae\u3001\u5f20\u5955\u632a\u7528\u516c\u6b3e\u8fd8\u7275\u7d2f\u6574\u4e2a\u5bb6\u65cf\u7684\u58f0\u8a89\uff0c\u81ea\u7136\u5c31\u4e0d\u4e3a\u5916\u4eba\u9053\uff0c\u90b5\u81f3\u521a\u4e5f\u4e0d\u6e05\u695a\uff0c\u4ed6\u53ea\u6653\u5f97\u5f20\u77e5\u884c\u3001\u5f20\u606a\u5bf9\u5f20\u77e5\u5fae\u4e00\u5bb6\u5411\u6765\u90fd\u6709\u4e0d\u6ee1\uff0c\u5f20\u606a\u6b64\u65f6\u4e3b\u52a8\u63d0\u8d77\u8981\u7528\u6881\u519b\uff0c\u90a3\u795e\u6001\u95f4\u5374\u662f\u538b\u6839\u513f\u5c06\u5f20\u5955\u8fd9\u4e2a\u4eba\u5fd8\u4e86\u4e00\u5e72\u4e8c\u51c0\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u5f53\u7136\u4e5f\u4e0d\u4f1a\u53bb\u63d2\u624b\u8fc7\u95ee\u5f20\u5bb6\u5185\u90e8\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u5012\u662f\u60f3\u8ddf\u5f20\u606a\u804a\u804a\u5f20\u5955\u7684\u8fd1\u51b5\uff0c\u4e0d\u8fc7\u4e5f\u4e0d\u4f1a\u9002\u5408\u5728\u8fd9\u4e48\u591a\u4eba\u9762\u524d\u63d0\u8d77\uff0c\u5c94\u5f00\u8bdd\u9898\uff0c\u8bf4\u9053\uff1a\u201c\u4ee5\u540e\u5efa\u90ba\u8fd9\u8fb9\u5c31\u8981\u70ed\u95f9\u8d77\u6765\u2026\u2026\u773c\u4e0b\u8981\u628a\u6839\u636e\u5730\u5148\u641e\u8d77\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8bf4\u9053\uff1a\u201c\u65b0\u534e\u90fd\u5927\u53a6\u91cc\u8fd8\u6709\u4e9b\u529e\u516c\u5ba4\u7a7a\u7740\u2026\u2026\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u6447\u5934\u8bf4\uff1a\u201c\u7559\u7ed9\u606a\u5c11\u5427\uff0c\u4e16\u7eaa\u9526\u6e56\u624d\u591a\u5927\u7684\u644a\u5b50\uff0c\u8be5\u8282\u7ea6\u8fd8\u662f\u8981\u8282\u7ea6\u7684\uff0c\u6211\u770b\u5c31\u5728\u4e1c\u5927\u9644\u8fd1\u79df\u4e00\u5c42\u697c\u5148\u7528\u8d77\u6765\u2026\u2026\u201d\u5728\u65b0\u5e02\u8857\u79df\u697c\u529e\u516c\u7684\u7ecf\u8425\u6210\u672c\u592a\u9ad8\uff0c\u867d\u7136\u53f6\u5efa\u658c\u4e5f\u662f\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u7684\u80a1\u4e1c\uff0c\u4f46\u662f\u90b5\u81f3\u521a\u4f5c\u4e3a\u603b\u88c1\u662f\u5bf9\u516c\u53f8\u7ecf\u8425\u6210\u672c\u8d1f\u8d23\u7684\u7b2c\u4e00\u8d23\u4efb\uff0c\u4ed6\u5fd9\u6447\u5934\u62d2\u7edd\u53f6\u5efa\u658c\u7684\u5efa\u8bae\u3002</p><p>\u3000\u3000\u201c\u5c31\u5546\u4e1a\u5730\u4ea7\u6765\u8bf4\uff0c\u5efa\u90ba\u5e02\u91cc\u7684\u5546\u4e1a\u5730\u4ea7\u552e\u4ef7\u6bd4\u6d77\u5dde\u8981\u9ad8\u4e09\u5230\u4e94\u500d\uff0c\u540c\u91cf\u7684\u5de5\u7a0b\u91cf\uff0c\u7ecf\u8425\u89c4\u6a21\u3001\u5229\u6da6\u89c4\u6a21\u90fd\u8981\u5927\u8bb8\u591a\uff0c\u6700\u5173\u952e\u7684\uff0c\u8fd8\u662f\u5efa\u90ba\u5e02\u7684\u5546\u4e1a\u5730\u4ea7\u9700\u6c42\u65fa\u76db\u7edd\u4e0d\u662f\u6d77\u5dde\u80fd\u6bd4\u7684\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u9526\u6e56\u4f1a\u575a\u6301\u5c06\u603b\u90e8\u7559\u5728\u6d77\u5dde\uff0c\u4f46\u662f\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u8981\u5230\u5927\u57ce\u5e02\u624d\u6709\u5927\u7684\u53d1\u5c55\uff0c\u4f60\u4eec\u4e0d\u501f\u8fd9\u6b21\u673a\u4f1a\u5c06\u603b\u90e8\u8fc1\u8fc7\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\u592a\u5fc3\u6025\u4e86\u4e9b\uff1f\u201d\u90b5\u81f3\u521a\u770b\u4e86\u770b\u5f20\u77e5\u975e\u3001\u53f6\u5efa\u658c\uff0c\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u4e0d\u662f\u4ed6\u4e00\u5bb6\uff0c\u53f6\u5bb6\u7684\u76db\u946b\u73af\u7403\u3001\u5f20\u77e5\u975e\u7684\u5b8f\u8fdc\u5b9e\u4e1a\u3001\u8bb8\u601d\u540d\u4e0b\u7684\u7ef4\u57df\u6295\u8d44\u3001\u76db\u9752\u7684\u76db\u4e16\u5e74\u534e\u90fd\u662f\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u7684\u80a1\u4e1c\u3002</p><p>\u3000\u3000\u201c\u5f97\uff0c\u6b32\u62d2\u8fd8\u8fce\u7684\u59ff\u6001\uff0c\u8fd9\u91cc\u53ef\u6ca1\u6709\u4eba\u6b23\u8d4f\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u4f60\u7684\u5fc3\u601d\uff0c\u6211\u80fd\u770b\u900f\u4e5d\u6210\uff0c\u4f60\u4e0d\u4f46\u60f3\u5c06\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u8fc1\u8fc7\u6765\uff0c\u8fd8\u60f3\u7740\u540c\u65f6\u5c06\u4e16\u7eaa\u9526\u6e56\u9910\u996e\u5a31\u4e50\u4e5f\u8fc1\u8fc7\u6765\uff0c\u8981\u505a\u5c31\u4e00\u5757\u505a\u5427\u2026\u2026\u53ea\u8981\u4e0d\u60f3\u7740\u4ece\u6a61\u6811\u56ed\u8ba1\u5212\u91cc\u5360\u5927\u4fbf\u5b9c\uff0c\u7ed9\u4f60\u4eec\u906e\u906e\u836b\uff0c\u8fd8\u662f\u53ef\u4ee5\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u5c31\u8fc1\u8fc7\u6765\u5427\u2026\u2026\u201d\u53f6\u5efa\u658c\u8bf4\u9053\uff0c\u4ed6\u65e9\u5c31\u60f3\u6d89\u8db3\u5546\u4e1a\u5730\u4ea7\u5f00\u53d1\u4e86\uff0c\u7ecf\u5f20\u606a\u4ecb\u7ecd\u53c2\u80a1\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\uff0c\u8981\u662f\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u4e00\u76f4\u7559\u5728\u6d77\u5dde\u5f00\u5c55\u4e1a\u52a1\uff0c\u4ed6\u4e5f\u4e0d\u65b9\u4fbf\u5230\u6d77\u5dde\u53bb\u55a7\u5bbe\u593a\u4e3b\uff0c\u5230\u5efa\u90ba\u5e02\u6765\uff0c\u624d\u6709\u4ed6\u80fd\u529b\u65bd\u5c55\u7684\u7a7a\u95f4\u3002</p><p>\u3000\u3000\u5546\u4e1a\u5730\u4ea7\u5f00\u53d1\uff0c\u5730\u57df\u4e4b\u95f4\u662f\u6709\u95e8\u69db\u9650\u5236\u7684\uff0c\u60f3\u4ece\u5c0f\u5730\u65b9\u8df3\u5230\u5927\u5730\u65b9\u7684\u96be\u5ea6\u5f88\u5927\uff0c\u5c31\u7b97\u65b9\u65b9\u9762\u9762\u7684\u5173\u7cfb\u6446\u5e73\uff0c\u4f46\u662f\u6ca1\u6709\u5728\u5927\u57ce\u5e02\u505a\u5927\u9879\u76ee\u7684\u7ecf\u9a8c\uff0c\u4f9d\u7136\u5f97\u4e0d\u5230\u4fe1\u4efb\uff0c\u4f9d\u7136\u4e0d\u80fd\u79f0\u4e00\u6d41\u7684\u5546\u4e1a\u5730\u4ea7\u5f00\u53d1\u4f01\u4e1a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5411\u5efa\u90ba\u5e02\u5e38\u59d4\u526f\u5e02\u957f\u738b\u7ef4\u5747\u627f\u8bfa\u5bf9\u5b66\u5e9c\u5df7\u7684\u5546\u94fa\u62c6\u8fc1\u4f1a\u91c7\u53d6\u56de\u8fc1\u8865\u507f\u7684\u65b9\u5f0f\uff0c\u90b5\u81f3\u521a\u662f\u6709\u4e9b\u5fc3\u75db\u7684\uff0c\u4e0d\u8fc7\u80fd\u501f\u52a9\u6a61\u6811\u56ed\u8ba1\u5212\u4e00\u4e0b\u5b50\u8df3\u5230\u5efa\u90ba\u5e02\u8fd9\u4e2a\u5e73\u53f0\u4e0a\u6765\uff0c\u5c31\u7b97\u4e0d\u8d5a\u4e00\u5206\u94b1\u4e5f\u662f\u4e50\u610f\u7684\u3002</p><p>\u3000\u3000\u80fd\u60f3\u8c61\u6a61\u6811\u56ed\u8ba1\u5212\u5728\u56fd\u5185\u7684\u5f71\u54cd\u529b\uff0c\u90b5\u81f3\u521a\u53ef\u662f\u60f3\u5c06\u9526\u6e56\u7684\u6a61\u6811\u56ed\u505a\u6210\u6837\u677f\u5de5\u7a0b\uff0c\u90a3\u6837\uff0c\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u5c31\u7b49\u82e5\u4e8e\u62ff\u5230\u8fdb\u519b\u56fd\u5185\u5404\u5927\u57ce\u5e02\u5546\u4e1a\u5730\u4ea7\u5f00\u53d1\u7684\u5165\u95e8\u8bc1\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u76f8\u4fe1\u5f20\u606a\u80fd\u4ee3\u8868\u8bb8\u601d\u7684\u610f\u89c1\uff0c\u53f6\u5efa\u658c\u53c8\u8fd9\u4e48\u8bf4\uff0c\u5f20\u77e5\u975e\u6ca1\u6709\u53cd\u5bf9\uff0c\u76db\u9752\u7684\u610f\u601d\uff0c\u4ed6\u65e9\u5c31\u6709\u6240\u4e86\u89e3\uff0c\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u5927\u5bb6\u90fd\u8fd9\u4e48\u8bf4\uff0c\u8fc1\u8fc7\u6765\u4e5f\u662f\u597d\u7684\u2026\u2026\u5e08\u5927\u5357\u95e8\u5916\u6709\u4e00\u680b\u529e\u516c\u697c\u56e0\u4e3a\u4e4b\u524d\u627f\u79df\u7684\u4f01\u4e1a\u5377\u94fa\u76d6\u6e9c\u8d70\u4e86\uff0c\u5404\u65b9\u9762\u6761\u4ef6\u90fd\u6bd4\u8f83\u9002\u5408\uff0c\u6211\u4eec\u53ef\u4ee5\u5148\u79df\u4e0b\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u5927\u5bb6\u54c8\u54c8\u5927\u7b11\uff0c\u90b5\u81f3\u521a\u90fd\u5df2\u7ecf\u5728\u5efa\u90ba\u7269\u8272\u516c\u53f8\u603b\u90e8\u5927\u697c\u4e86\uff0c\u521a\u624d\u8fd8\u906e\u906e\u63a9\u63a9\u2026\u2026</p><p>\u3000\u3000\u201c\u5e08\u5927\u5357\u95e8\uff1f\u90a3\u5730\u65b9\u4e0d\u9519\uff0c\u201d\u9648\u4fe1\u751f\u95ee\u5f20\u606a\uff0c\u201c\u6a61\u6811\u56ed\u5efa\u6210\u4e4b\u524d\uff0c\u9526\u6e56\u5728\u5efa\u90ba\u5e02\u7684\u529e\u516c\u5730\u70b9\uff0c\u662f\u4e0d\u662f\u8ddf\u4e16\u7eaa\u9526\u6e56\u6324\u4e00\u6324\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6574\u4e2a\u6a61\u6811\u56ed\u8ba1\u5212\u5e9e\u5927\uff0c\u9700\u8981\u5efa\u90ba\u5e02\u62a5\u9001\u56fd\u52a1\u9662\u5ba1\u6279\uff0c\u6709\u82e5\u5e72\u9879\u76ee\u6211\u4eec\u53ef\u4ee5\u5148\u62c6\u51fa\u6765\u5b9e\u65bd\uff0c\u5b66\u5e9c\u5df7\u6539\u9020\u53ef\u4ee5\u884c\u5c06\u62c6\u8fc1\u5de5\u4f5c\u505a\u8d77\u6765\uff0c\u71d5\u5f52\u6e56\u4e1c\u5317\u89d2\u4e0a\u7684\u4e24\u5ea7\u5370\u67d3\u5382\u53ef\u4ee5\u5148\u4e70\u4e0b\u6765\u6539\u9020\u6210\u521b\u4e1a\u56ed\u533a\uff0c\u6539\u9020\u4e5f\u5206\u5757\u5206\u6bb5\u7684\u53bb\u641e\uff0c\u6211\u60f3\u901f\u5ea6\u591f\u5feb\u7684\u8bdd\uff0c\u4e24\u4e2a\u6708\u5c31\u80fd\u5148\u6e05\u51fa\u4e00\u7247\u529e\u516c\u533a\u57df\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u867d\u8bf4\u521a\u53c2\u5165\u9526\u6e56\uff0c\u4f46\u662f\u4e0e\u5f20\u606a\u63a5\u89e6\u7684\u65f6\u95f4\u5f88\u957f\u4e86\uff0c\u77e5\u9053\u9526\u6e56\u529e\u4e8b\u7684\u98ce\u683c\uff0c\u8981\u6ca1\u6709\u8fd9\u70b9\u62a2\u65f6\u95f4\u7684\u98ce\u683c\uff0c\u9526\u6e56\u4e5f\u4e0d\u53ef\u80fd\u5728\u4e24\u4e09\u5e74\u65f6\u95f4\u91cc\u53d1\u5c55\u6210\u4eca\u5929\u7684\u89c4\u6a21\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u6293\u7740\u6276\u624b\u7ad9\u8d77\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u660e\u5929\u5c31\u4e0e\u5efa\u90ba\u5e02\u5f00\u59cb\u6b63\u5f0f\u7684\u8c08\u5224\u5427\uff0c\u5148\u628a\u610f\u5411\u4e66\u7ed9\u7b7e\u4e0b\u6765\uff0c\u5927\u5bb6\u6709\u4e2a\u79ef\u6781\u7684\u59ff\u6001\uff0c\u505a\u4e8b\u603b\u662f\u5b9c\u65e9\u4e0d\u5b9c\u8fdf\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u30004\u67089\u65e5\u4e0a\u5348\uff0c\u5efa\u90ba\u5e02\u59d4\u53ec\u5f00\u4e34\u65f6\u5e38\u59d4\u4f1a\u8bae\uff0c\u8feb\u4e0d\u53ca\u5f85\u7684\u8ba8\u8bba\u4e86\u5efa\u90ba\u5e02\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u65b0\u6218\u7565\u7684\u95ee\u9898\uff0c\u7684\u786e\u53ef\u4ee5\u7528\u8feb\u4e0d\u53ca\u5f85\u8fd9\u4e2a\u8bcd\u6765\u5f62\u5bb9\u3002</p><p>\u3000\u3000\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u5728\u4f1a\u8bae\u4e0a\u6177\u6168\u9648\u8f9e\uff0c\u6307\u51fa\u5efa\u90ba\u5e02\u516b\u5e74\u6765\u5728\u53d1\u5c55\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u4e0a\u754f\u9996\u754f\u5c3e\u7684\u6218\u7565\u65b9\u9488\u4f7f\u5f97\u6574\u4f53\u683c\u5c40\u72ed\u5c0f\uff0c\u4e0d\u6539\u53d8\u5c31\u8981\u7ed9\u5176\u4ed6\u526f\u7701\u5e02\u7ea7\u57ce\u5e02\uff0c\u7279\u522b\u540c\u7701\u3001\u540c\u7ea7\u522b\u7684\u60e0\u5c71\u5e02\u843d\u5728\u540e\u9762\u3002</p><p>\u3000\u3000\u6574\u4e2a\u4f1a\u8bae\uff0c\u5e02\u59d4\u526f\u4e66\u8bb0\u3001\u5e02\u957f\u8096\u660e\u5efa\u5c31\u51e0\u4e4e\u6ca1\u6709\u62ac\u8d77\u5934\u6765\uff0c\u7f57\u541b\u603b\u7a76\u8981\u7ed9\u8096\u660e\u5efa\u7559\u4e9b\u989c\u9762\uff0c\u800c\u662f\u5c06\u4e4b\u524d\u4e00\u76f4\u5206\u7ba1\u8fd9\u65b9\u9762\u5de5\u4f5c\u7684\u5e02\u59d4\u5e38\u59d4\u3001\u5e38\u52a1\u526f\u5e02\u957f\u80e1\u5b97\u5e86\u62ff\u51fa\u6765\u72e0\u72e0\u6279\u8bc4\u4e86\u4e00\u901a\uff0c\u4e5f\u5c06\u4e4b\u524d\u5e02\u59d4\u5e02\u653f\u5e9c\u5bf9\u9526\u6e56\u6a61\u6811\u56ed\u8ba1\u5212\u7684\u8fdf\u949d\u53cd\u5e94\u5f52\u548e\u5230\u80e1\u5b97\u5e86\u7684\u5934\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e2d\u5348\u4e0e\u738b\u7ef4\u5747\u4e00\u8d77\u7528\u9910\u65f6\uff0c\u4ece\u738b\u7ef4\u5747\u90a3\u91cc\u77e5\u9053\u4e0a\u5348\u5efa\u90ba\u5e02\u59d4\u53ec\u5f00\u4f1a\u8bae\u7684\u60c5\u5f62\uff0c\u4ed6\u5bf9\u6b64\u4e5f\u611f\u5230\u5f88\u65e0\u5948\uff0c\u4ed6\u867d\u7136\u60f3\u9762\u9762\u4ff1\u5230\uff0c\u4f46\u662f\u73b0\u5b9e\u7684\u60c5\u51b5\u4e0d\u53ef\u80fd\u5c06\u6bcf\u4e00\u4e2a\u4eba\u90fd\u8ba8\u597d\u5230\uff0c\u4e0d\u6653\u5f97\u80e1\u5b97\u5e86\u5bf9\u9526\u6e56\u4f1a\u4e0d\u4f1a\u6709\u6240\u8fc1\u6068\u3002</p><p>\u3000\u3000\u5efa\u90ba\u5e02\u59d4\u53ec\u5f00\u4e34\u65f6\u5e38\u59d4\u4f1a\u8bae\u4e4b\u540e\uff0c\u4f1a\u8bae\u7eaa\u8981\u4e2d\u5348\u9001\u4ea4\u7701\u653f\u5e9c\u529e\u516c\u5385\uff0c\u9001\u5230\u674e\u8fdc\u6e56\u624b\u91cc\u3002\u4e0b\u5348\uff0c\u674e\u8fdc\u6e56\u5c31\u5728\u7701\u59d4\u7701\u653f\u5e9c\u529e\u516c\u5927\u697c\u6839\u636e\u5efa\u90ba\u5e02\u59d4\u4e0a\u5348\u4e34\u65f6\u5e38\u59d4\u4f1a\u8bae\u7684\u5185\u5bb9\u7eaa\u8981\u53ec\u5f00\u7701\u653f\u5e9c\u529e\u516c\u5ba4\u4f1a\u8bae\uff0c\u8ba8\u8bba\u4e86\u4e1c\u6d77\u7701\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u65b0\u6218\u7565\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u30009\u6708\u5165\u591c\uff0c\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u3001\u8c22\u77bb\u3001\u9648\u9759\u7b49\u4e00\u884c\u4eba\u4ece\u9999\u6e2f\u98de\u62b5\u5efa\u90ba\u3002</p><p>\u3000\u3000\u591c\u8272\u6c89\u5bc2\uff0c\u98d8\u7740\u5c0f\u96e8\uff0c\u8f66\u524d\u706f\u7684\u5149\u67f1\u91cc\u96e8\u4e1d\u98d8\u8fc7\u3002</p><p>\u3000\u3000\u201c\u6628\u5929\u8fd8\u542c\u8c22\u610f\u8bf4\u7701\u91cc\u5929\u5f88\u597d\uff0c\u201d\u8c22\u6c49\u9756\u5750\u5728\u8f66\u91cc\uff0c\u8ddf\u8c22\u5251\u5357\u8bf4\uff0c\u201c\u4e5f\u662f\u5230\u6885\u5b50\u9ec4\u65f6\u96e8\u7684\u5b63\u8282\u4e86\u3002\u201d</p><p>\u3000\u3000\u9648\u9759\u542c\u7740\u6c7d\u8f66\u8c03\u9891\u7535\u53f0\u91cc\u6b63\u64ad\u653e\u4e1c\u6d77\u7701\u4eca\u540e\u51e0\u5929\u90fd\u662f\u9634\u96e8\u5929\u6c14\uff0c\u5012\u5bf9\u7ef5\u7ef5\u6625\u96e8\u6ca1\u6709\u7279\u522b\u7684\u611f\u89e6\u3002</p><p>\u3000\u3000\u8f66\u961f\u9a76\u5165\u5efa\u90ba\u5357\u90ca\u4e00\u5904\u540d\u53eb\u6696\u9999\u697c\u7684\u4f11\u95f2\u5c71\u5e84\uff0c\u8c22\u610f\u7ad9\u5728\u5c71\u5e84\u4e3b\u697c\u524d\u7b49\u5019\uff0c\u770b\u7740\u8f66\u5b50\u8fc7\u6765\uff0c\u5e2e\u5fd9\u6253\u5f00\u8f66\u95e8\uff0c\u8f66\u5b50\u505c\u7684\u4f4d\u7f6e\u6c34\u597d\uff0c\u6a90\u5934\u6ef4\u6c34\u6070\u597d\u843d\u5728\u8f66\u95e8\u524d\uff0c\u9648\u9759\u63a2\u8db3\u4f38\u51fa\u8f66\u95e8\uff0c\u7a7f\u7740\u4eae\u4e1d\u88e4\u889c\u7684\u5c0f\u817f\u6ef4\u4e86\u51e0\u6ef4\u96e8\u6c34\uff0c\u6d78\u67d3\u7740\u663e\u9732\u51fa\u767d\u817b\u5982\u7389\u7684\u8089\u8272\u6765\u3002\u8c22\u610f\u4e0b\u610f\u8bc6\u7684\u4f38\u624b\u8981\u5e2e\u9648\u9759\u6321\u96e8\uff0c\u4e00\u53ea\u624b\u638c\u6321\u5728\u5934\u9876\u6709\u4ec0\u4e48\u7528\uff1f\u9648\u9759\u6025\u8df3\u4e86\u4e24\u6b65\uff0c\u8d70\u5230\u5c4b\u6a90\u4e0b\uff0c\u624d\u56de\u5934\u5bf9\u8c22\u610f\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u54e5\uff0c\u5b50\u5609\u56b7\u7740\u8bf4\u8981\u5230\u5efa\u90ba\u5e02\u6765\uff0c\u5979\u4eba\u6709\u6ca1\u6709\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u7238\u4e0d\u8ddf\u4f60\u4eec\u5750\u540c\u4e00\u73ed\u98de\u673a\uff0c\u5979\u5c31\u6765\u4e86\uff0c\u201d\u8c22\u610f\u770b\u7740\u9648\u9759\u5ae3\u7136\u59e3\u7f8e\u7684\u7b11\u9765\uff0c\u5f53\u5e74\u8ddf\u5728\u5c41\u80a1\u540e\u9762\u8138\u4e0a\u6302\u7740\u9f3b\u6d95\u7684\u5c0f\u5973\u5b69\u5b50\u51fa\u56fd\u56de\u6765\u7adf\u6210\u4e86\u5a77\u5a77\u7389\u7acb\u7684\u4f73\u4eba\uff0c\u867d\u8bf4\u9648\u9759\u56de\u56fd\u6709\u4e00\u5e74\u591a\u65f6\u95f4\uff0c\u53ea\u662f\u8c22\u610f\u9047\u4e0a\u5979\u7684\u673a\u4f1a\u5f88\u5c11\uff0c\u5012\u6ca1\u6709\u8ba4\u8ba4\u771f\u771f\u7684\u7aef\u89c6\u8fc7\u5979\uff0c\u6709\u7740\u8ba9\u4eba\u5fc3\u60b8\u7684\u7f8e\uff0c\u201c\u73b0\u5728\u5979\u54ea\u6709\u8fd9\u4e2a\u80c6\u5b50\uff1f\u6211\u7238\u5c06\u5979\u7684\u8d26\u6237\u90fd\u5c01\u4e86\uff0c\u5c31\u662f\u8981\u5979\u51ed\u81ea\u5df1\u7684\u80fd\u529b\u5728\u5269\u4e0b\u7684\u4e09\u4e2a\u6708\u4e4b\u5185\u8003\u4e0a\u4e1c\u6d77\u5927\u5b66\u3002\u201d</p><p>\u3000\u3000\u8c22\u6c49\u9756\u4ece\u8f66\u91cc\u94bb\u51fa\u6765\uff0c\u770b\u7740\u8c22\u610f\u867d\u7136\u8d70\u8fc7\u6765\u5f00\u8f66\u95e8\uff0c\u5374\u80cc\u5bf9\u7740\u81ea\u5df1\u8ddf\u9648\u9759\u8bf4\u8bdd\uff0c\u63a8\u4e86\u4ed6\u7684\u80a9\u5934\u95ee\u4ed6\uff1a\u201c\u9ad8\u79d8\u4e66\u4e0e\u4f60\u5468\u4f2f\u4f2f\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u5728\u91cc\u9762\u7b49\u4f60\u4eec\u2026\u2026\u201d\u8c22\u610f\u8ba9\u4ed6\u7238\u5148\u4e0a\u53f0\u9636\uff0c\u53c8\u63fd\u7740\u8c22\u5251\u5357\u7684\u80a9\u8180\u4e00\u8d77\u8d70\u4e0a\u53f0\u9636\uff0c\u8fd8\u7ed9\u4ece\u7b2c\u4e8c\u90e8\u8f66\u94bb\u51fa\u6765\u7684\u8c22\u77bb\u80f8\u53e3\u8f7b\u8f7b\u6253\u4e86\u4e00\u62f3\uff0c\u663e\u5f97\u5f88\u4eb2\u70ed\uff0c\u201c\u4f60\u4eec\u8fd9\u6b21\u53bb\u9999\u6e2f\u7684\u6210\u679c\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u5c0f\u5b50\u4f1a\u4e0d\u4f1a\u592a\u8c28\u614e\u4e86\uff1f\u201d\u8c22\u77bb\u56de\u4e86\u8c22\u610f\u4e00\u62f3\uff0c\u201c\u4e0d\u8fc7\uff0c\u4e00\u5207\u90fd\u6309\u8ba1\u5212\u5728\u8d70\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u770b\u554a\uff0c\u4e0d\u7ba1\u591a\u91cd\u89c6\u6211\u4eec\u7684\u8fd9\u4e2a\u5bf9\u624b\u90fd\u4e0d\u4f1a\u8fc7\u5206\u3002\u7231\u8fbe\u7535\u5b50\u8feb\u4f7f\u6211\u4eec\u5728\u79d1\u738b\u589e\u52a0\u6295\u5165\uff0c\u7275\u5236\u6211\u4eec\u7684\u8d44\u91d1\u6d41\u5411\uff0c\u6211\u4eec\u90fd\u9884\u6d4b\u53f6\u5bb6\u7684\u76db\u946b\u7535\u5668\u4f1a\u8d81\u6d77\u6cf0\u65e0\u529b\u6269\u5f20\u4e4b\u9645\u5927\u4e3e\u8fdb\u519b\u7701\u5185\u5bb6\u7535\u8fde\u9501\u5e02\u573a\u3002\u76db\u946b\u504f\u504f\u8fd8\u662f\u5c06\u8fd9\u4e9b\u5e74\u6240\u79ef\u6512\u4e0b\u6765\u7684\u8d44\u91d1\u90fd\u7838\u5230\u65b0\u5e02\u8857\u7684\u65d7\u8230\u5e97\u4e0a\uff0c\u5927\u6709\u63ed\u76c5\u5f00\u5927\u5c0f\u5b9a\u8f93\u8d62\u7684\u8c6a\u6c14\u3002\u8054\u60f3\u5230\u9526\u6e56\u4e0e\u53f6\u5bb6\u7684\u5173\u7cfb\uff0c\u800c\u4e14\u90a3\u6bb5\u65f6\u95f4\u53f6\u5efa\u658c\u4e00\u76f4\u5728\u6d77\u5dde\u8017\u7740\uff0c\u6211\u60f3\u5927\u6982\u662f\u9526\u6e56\u7ed9\u53f6\u5bb6\u6700\u540e\u7684\u9f13\u52b1\u5427\u3002\u7ed3\u679c\u5982\u4f55\uff1f\u8c01\u80fd\u60f3\u8c61\u51fa\u65b0\u5e02\u8857\u6709\u8fd9\u4e48\u5927\u7684\u6f5c\u529b\uff0c\u9999\u6e2f\u90a3\u5bb6\u6240\u8c13\u4e13\u4e1a\u7684\u5e02\u573a\u8c03\u7814\u516c\u53f8\u5c31\u662f\u4ed6\u5988\u7684\u72d7\u5c41\uff0c\u9884\u6d4b\u90a3\u91cc\u5355\u5e97\u7684\u6700\u5927\u6708\u6d41\u91cf\u4e0d\u4f1a\u8d85\u8fc7\u4e94\u5343\u4e07\uff0c\u5b9e\u9645\u60c5\u51b5\u5462\uff1f\u6211\u770b\u5f53\u6708\u5c11\u8bf4\u4e5f\u8981\u4e00\u4e2a\u4ebf\u2026\u2026\u201d\u8c22\u610f\u4f38\u51fa\u820c\u5c16\u8214\u4e86\u8214\u4e0b\u5634\u5507\uff0c\u201c\u9526\u6e56\u56f4\u7ed5\u53f6\u81fb\u6c11\u7684\u6587\u7ae0\u5e03\u8fd9\u4e48\u5927\u7684\u5c40\uff0c\u6211\u4eec\u5c0f\u5fc3\u8c28\u614e\u4e00\u4e9b\u603b\u4e0d\u4f1a\u6709\u4ec0\u4e48\u9519\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5f97\uff0c\u5f97\uff0c\u5f97\uff0c\u201d\u8c22\u77bb\u8bf4\u4e0d\u8fc7\u8c22\u610f\uff0c\u77e5\u9053\u8c22\u610f\u624d\u662f\u7ed9\u8c22\u6c49\u9756\u4f9d\u4e3a\u667a\u56ca\u7684\u6b63\u6cf0\u96c6\u56e2\u7684\u771f\u6b63\u63a5\u73ed\u4eba\uff0c\u4ed6\u5bb6\u8fd8\u6709\u4e2a\u5c0f\u602a\u80ce\u66f4\u8ba9\u4eba\u6bdb\u9aa8\u609a\u7136\uff0c\u8bf4\u9053\uff0c\u201c\u6ca1\u4eba\u8ddf\u4f60\u4f3c\u7684\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u5149\u660e\u6b63\u5927\u7684\u7231\u597d\uff0c\u6574\u5929\u5c31\u77e5\u9053\u8eb2\u5728\u89d2\u843d\u91cc\u7b97\u8ba1\u522b\u4eba\uff0c\u4f60\u5206\u6790\u5f20\u606a\u8fd9\u6bdb\u90fd\u53ef\u80fd\u6ca1\u957f\u9f50\u7684\u5bb6\u4f19\u6709\u597d\u4e00\u9635\u5b50\u4e86\uff0c\u4f60\u9884\u6d4b\u4e00\u4e0b\uff0c\u9526\u6e56\u5728\u6a61\u6811\u56ed\u4e4b\u540e\uff0c\u4f1a\u6709\u4ec0\u4e48\u5927\u52a8\u4f5c\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f53\u7136\u4f1a\u6709\u5927\u52a8\u4f5c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u597d\u4e86\uff0c\u6ca1\u5fc5\u8981\u7ad9\u5728\u5927\u5385\u4e0a\u8ba8\u8bba\u8fd9\u4e9b\u95ee\u9898\uff0c\u201d\u8c22\u6c49\u9756\u5728\u524d\u9762\u505c\u4e0b\u811a\u6b65\uff0c\u201c\u4e0d\u8981\u8ba9\u5ba2\u4eba\u5728\u623f\u95f4\u7b49\u6211\u4eec\u3002\u201d</p><p>\u3000\u3000\u8c22\u610f\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u8ddf\u8c22\u77bb\u5728\u5927\u5385\u91cc\u5570\u55e6\u4ec0\u4e48\uff0c\u524d\u9762\u5e26\u8def\u5c06\u5927\u5bb6\u9886\u8fdb\u4e00\u95f4\u8c6a\u534e\u5305\u53a2\u91cc\u3002\u5468\u747e\u73ba\u4e0e\u9ad8\u771f\u65e9\u5728\u5305\u53a2\u91cc\u7b49\u5019\uff0c\u5750\u5728\u9ad8\u771f\u65c1\u8fb9\u8fd8\u6709\u4e00\u4e2a\u5bb9\u8c8c\u59e3\u7f8e\u3001\u5316\u7740\u7cbe\u81f4\u5986\u5bb9\u7684\u5973\u4eba\uff0c\u4e0e\u9ad8\u771f\u7684\u795e\u6001\u4eb2\u6635\uff0c\u9648\u9759\u770b\u5979\u603b\u4e0d\u50cf\u9ad8\u771f\u7684\u7231\u4eba\u3002</p><p>\u3000\u3000\u90a3\u5973\u4eba\u5206\u53d1\u540d\u7247\u65f6\uff0c\u900f\u51fa\u4e00\u80a1\u6de1\u5e7d\u72ec\u7279\u7684\u9999\u5473\uff0c\u9648\u9759\u4e00\u8fb9\u601d\u91cf\u662f\u4ec0\u4e48\u9999\u6c34\uff0c\u4e00\u8fb9\u770b\u63a5\u5230\u624b\u91cc\u7684\u540d\u7247\uff1a\u7cbe\u5178\u5730\u4ea7\u5f00\u53d1\u6709\u9650\u516c\u53f8\u603b\u88c1\u6797\u96ea\u3002</p><p>\u3000\u3000\u554a\uff1f\u8fd9\u5973\u4eba\u770b\u4e0a\u53bb\u90fd\u6ca1\u6709\u4e8c\u5341\u4e03\u516b\u5c81\uff0c\u624b\u4e0b\u7adf\u6709\u4e00\u5bb6\u5730\u4ea7\u5f00\u53d1\u516c\u53f8\uff0c\u8fd9\u5bb6\u7cbe\u5178\u516c\u53f8\u597d\u50cf\u542c\u522b\u4eba\u8bf4\u8d77\u6765\uff0c\u89c4\u6a21\u4e0d\u5c11\u3002</p><p>\u3000\u3000\u90a3\u5973\u4eba\u540d\u7247\u5206\u653e\u5230\u8c22\u5251\u5357\u524d\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8c22\u603b\u4e0d\u4f1a\u518d\u8d2a\u6211\u4e00\u5f20\u540d\u7247\u5427\uff1f\u201d</p><p>\u3000\u3000\u9648\u9759\u5fc3\u60f3\u539f\u6765\u5251\u5357\u8ddf\u5979\u662f\u8ba4\u8bc6\u7684\uff0c\u8c22\u5251\u5357\u795e\u8272\u81ea\u82e5\u7684\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u8bb0\u6027\u6ca1\u90a3\u4e48\u5dee\uff0c\u4e0d\u8fc7\u4eca\u5929\u6211\u5973\u670b\u53cb\u5728\u573a\uff0c\u5c31\u4e0d\u4f1a\u518d\u606d\u7ef4\u5176\u4ed6\u5973\u4eba\u4e86\uff0c\u6797\u603b\u903c\u6211\u4e5f\u6ca1\u6709\u7528\uff0c\u201d\u8c22\u5251\u5357\u7b11\u4e86\u9648\u9759\u4e00\u773c\uff0c\u53c8\u7b11\u4e86\u7b11\u8ddf\u773c\u524d\u7684\u8fd9\u4e2a\u7f8e\u4e3d\u5973\u4eba\u8bf4\u9053\uff0c\u201c\u542c\u8bf4\u7cbe\u5178\u5730\u4ea7\u4e00\u76f4\u90fd\u5bf9\u5b66\u5e9c\u5df7\u6539\u9020\u5de5\u7a0b\u6709\u4e3e\u8da3\uff0c\u4e0d\u6653\u5f97\u8ddf\u5efa\u90ba\u5e02\u91cc\u4e8b\u60c5\u6709\u6ca1\u6709\u8c08\u59a5\u5462\uff0c\u6211\u4eec\u8fd9\u91cc\u597d\u51e0\u4e2a\u4eba\u90fd\u662f\u4ece\u4e1c\u5927\u6bd5\u4e1a\u7684\uff0c\u53ef\u662f\u671f\u5f85\u6797\u603b\u6709\u8ba9\u4eba\u60ca\u559c\u7684\u8868\u73b0\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u8c22\u603b\u8bf4\u8fd9\u8bdd\u7eaf\u7cb9\u662f\u8981\u6c14\u6211\u554a\uff0c\u201d\u90a3\u5973\u4eba\u773c\u775b\u95ea\u70c1\u7740\uff0c\u4e5f\u8ba9\u4eba\u5206\u4e0d\u6e05\u5979\u7684\u60c5\u7eea\uff0c\u53c8\u62ff\u8fd1\u4f3c\u5a07\u55d4\u7684\u8bed\u6c14\u8ddf\u9ad8\u771f\u8bf4\uff0c\u201c\u9ad8\u79d8\u4e66\uff0c\u4f60\u6765\u8ddf\u8c22\u603b\u8bf4\u8bf4\uff0c\u4eca\u5929\u53d1\u751f\u4ec0\u4e48\u8981\u6c14\u6b7b\u4eba\u7684\u4e8b\u60c5\u4e86\uff1f\u201d</p></article>"}}}]);
