(window.webpackJsonp=window.webpackJsonp||[]).push([[563],{586:function(p){p.exports={chapterTitle:null,title:"\u7b2c418\u7ae0 \u65e0\u6cd5\u633d\u7559",content:"<article><h2>\u7b2c418\u7ae0 \u65e0\u6cd5\u633d\u7559</h2><p>\u3000\u3000\u5f20\u606a\u59cb\u7ec8\u5c06\u5510\u5b66\u8c26\u5f53\u6210\u7236\u6267\u8f88\u6765\u656c\u91cd\uff0c\u6ce8\u610f\u4fdd\u6301\u665a\u8f88\u7684\u8c26\u5351\uff0c\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u672a\u6765\u7ecf\u6d4e\u5f62\u52bf\u7684\u95ee\u9898\u4e0a\uff0c\u4ed6\u4e5f\u662f\u7a0d\u70b9\u5373\u6b62\uff0c\u5e02\u91cc\u7684\u7ecf\u6d4e\u95ee\u9898\uff0c\u5510\u5b66\u8c26\u6709\u95ee\uff0c\u4ed6\u624d\u7b54\uff0c\u8bf4\u4e86\u592a\u591a\uff0c\u672a\u5fc5\u5408\u9002\uff0c\u66f4\u591a\u7684\u5185\u5bb9\uff0c\u53f6\u81fb\u6c11\u5728\u6587\u7ae0\u91cc\u5df2\u7ecf\u9610\u8ff0\u5f7b\u5e95\uff0c\u5373\u4f7f\u4ee5\u53f6\u81fb\u6c11\u7684\u8eab\u4efd\uff0c\u4ed6\u6b64\u65f6\u9610\u8ff0\u8fd9\u79cd\u8bba\u70b9\uff0c\u4e5f\u662f\u975e\u4e3b\u6d41\u7684\u3002</p><p>\u3000\u3000\u5f71\u54cd\u6240\u80fd\u5f71\u54cd\u7684\uff0c\u5269\u4e0b\u7684\u5c31\u8ba9\u65f6\u95f4\u6765\u9a8c\u8bc1\u5427\u3002</p><p>\u3000\u3000\u56db\u70b9\u949f\u5de6\u53f3\uff0c\u5f20\u606a\u8d70\u51fa\u5e02\u653f\u5e9c\uff0c\u7ad9\u7740\u4eba\u884c\u9053\u4e0a\uff0c\u62c2\u9762\u5439\u6765\u5165\u79cb\u7684\u5fae\u98ce\uff0c\u7b49\u7740\u8857\u5bf9\u9762\u7684\u4ea4\u901a\u706f\u53d8\u5316\u7ea2\u7eff\u706f\uff0c\u4ed6\u6ca1\u6709\u8ba9\u5085\u4fca\u5c06\u8f66\u5f00\u8fdb\u5e02\u653f\u5e9c\uff0c\u65e0\u8bba\u662fVOLVO\uff0c\u8fd8\u662f\u6302\u519b\u724c\u7684\u5965\u8fea\uff0c\u90fd\u592a\u663e\u773c\u4e86\uff0c\u79cb\u540e\u7684\u9ec4\u660f\uff0c\u5076\u5c14\u6b65\u884c\u7a7f\u8fc7\u57ce\u5e02\uff0c\u611f\u89c9\u4e5f\u4e0d\u9519\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u60f3\u7740\uff0c\u8e22\u7740\u811a\u4e0b\u4e0d\u6653\u5f97\u600e\u4e48\u4f1a\u51fa\u73b0\u5728\u6c34\u6ce5\u8def\u9762\u4e0a\u7684\u5c0f\u77f3\u5b50\uff0c\u624b\u63d2\u5728\u88e4\u515c\u91cc\uff0c\u60f3\u7740\u662f\u53bb\u5b66\u6821\u63a5\u5510\u5a67\uff0c\u8fd8\u662f\u6253\u7535\u8bdd\u53eb\u5085\u4fca\u5f00\u8f66\u6765\u9001\u4ed6\u53bb\u8c61\u5c71\u770b\u770b\u665a\u6674\u5b66\u6821\u7684\u5efa\u8bbe\u8fdb\u5ea6\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8f6c\u8eab\u770b\u89c1\u674e\u6bc5\u534e\u5c06\u9999\u70df\u8482\u5b50\u5728\u9752\u7070\u8272\u7684\u68a7\u6850\u6811\u8eab\u4e0a\u637b\u7184\uff0c\u95ee\u4ed6\uff1a\u201c\u5728\u8fd9\u91cc\u7b49\u6211\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f97\u786e\u8ba4\u8fd9\u4e24\u5929\u7684\u906d\u9047\u4e0d\u662f\u4e00\u573a\u68a6\uff0c\u201d\u674e\u6bc5\u534e\u5c06\u637b\u7184\u7684\u9999\u70df\u8482\u5f39\u8fdb\u4e00\u8fb9\u7684\u5783\u573e\u6876\u91cc\uff0c\u201c\u4f60\u77e5\u9053\uff0c\u8fd9\u5f88\u96be\u5206\u6e05\u695a\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4e5f\u6709\u4eba\u751f\u82e5\u68a6\u7684\u611f\u89c9\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u901a\u5e38\u4e5f\u4e0d\u4f1a\u50bb\u5230\u7528\u529b\u6253\u81ea\u5df1\u4e24\u5df4\u638c\u3002\u201d</p><p>\u3000\u3000\u201c\u5012\u662f\u771f\u7528\u529b\u6390\u4e86\u81ea\u5df1\u4e24\u4e0b\uff0c\u201d\u674e\u6bc5\u534e\u81ea\u5632\u7684\u8bf4\u9053\uff0c\u201c\u5728\u505c\u8f66\u573a\u6ca1\u770b\u5230\u606a\u5c11\u7684\u8f66\uff0c\u5c31\u8e72\u5728\u8fd9\u91cc\u62bd\u4e00\u4f1a\u513f\u70df\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8d70\u8fc7\u53bb\uff0c\u770b\u89c1\u68a7\u6850\u6811\u7ed9\u637b\u51fa\u4e0d\u5c11\u9ed1\u6591\uff0c\u770b\u6765\u674e\u6bc5\u534e\u5728\u8fd9\u91cc\u62bd\u4e86\u4e0d\u5c11\u70df\uff0c\u4f38\u624b\u5411\u4ed6\u8981\u70df\u3002\u674e\u6bc5\u534e\u5c06\u7a7a\u7684\u70df\u76d2\u638f\u51fa\u6765\uff0c\u63c9\u6210\u4e00\u56e2\u4e22\u5783\u573e\u7b52\u91cc\uff0c\u8dd1\u5230\u65c1\u8fb9\u7684\u70df\u8349\u4e13\u5356\u5e97\u62ff\u4e86\u4e00\u5305\u4e91\u70df\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u6709\u4e9b\u95ee\u9898\u60f3\u8bf7\u6559\u606a\u5c11\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u662f\u6211\uff0c\u4e3a\u4ec0\u4e48\u662f\u4f60\uff1f\u201d\u5f20\u606a\u95ee\u674e\u6bc5\u534e\u3002</p><p>\u3000\u3000\u674e\u6bc5\u534e\u70b9\u70b9\u5934\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u6253\u7b97\u53c2\u80a1\u57ce\u5546\u884c\uff0c\u6211\u6070\u5de7\u662f\u9526\u6e56\u7684\u8001\u677f\uff0c\u542c\u51fa\u6765\u5f88\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\u60c5\uff0c\u6240\u4ee5\u5b9e\u5728\u6ca1\u6709\u5fc5\u8981\u8ba9\u522b\u4eba\u5927\u60ca\u5c0f\u602a\uff1b\u201d\u5f20\u606a\u7b11\u7740\u89e3\u91ca\u8bf4\uff0c\u201c\u4fe1\u7528\u793e\u7684\u4e8b\u60c5\u592a\u9ebb\u70e6\uff0c\u6211\u662f\u4e2a\u7f3a\u4e4f\u8010\u5fc3\u7684\u4eba\uff0c\u6240\u4ee5\u9700\u8981\u4e00\u628a\u950b\u5229\u7684\u5200\uff0c\u4f60\u521a\u624d\u6240\u8bf4\u7684\u90a3\u4e9b\u4e1c\u897f\uff0c\u6070\u5de7\u662f\u4f60\u5728\u57ce\u4e1c\u4fe1\u7528\u793e\u77ed\u671f\u4efb\u804c\u65f6\u7684\u5931\u8d25\u7ecf\u5386\uff0c\u4fe1\u7528\u793e\u5185\u90e8\u66f4\u4e60\u60ef\u5c06\u4f60\u5f53\u6210\u4e00\u4e2a\u4e0d\u52a1\u5b9e\u7684\u7a7a\u60f3\u5bb6\uff0c\u4f46\u662f\u6211\u7684\u60f3\u6cd5\u5e38\u4e0e\u522b\u4eba\u4e0d\u4e00\u6837\u3002\u201d</p><p>\u3000\u3000\u674e\u6bc5\u534e\u5f20\u7740\u5634\uff0c\u53d1\u4e86\u4e00\u4f1a\u513f\u6123\uff0c\u624d\u7b11\u7740\u8bf4\uff1a\u201c\u77e5\u9053\u771f\u76f8\u679c\u7136\u53ea\u6709\u4e00\u5c0f\u64ae\u4eba\u3002\u4ee5\u524d\u4fe1\u7528\u8054\u793e\u4e5f\u5b66\u67d0\u4e9b\u4f01\u4e1a\u7684\u505a\u6cd5\u641e\u7ade\u4e89\u4e0a\u5c97\uff0c\u7ade\u5012\u662f\u7ade\u4e0a\u4e86\uff0c\u4e0d\u8fc7\u60f3\u62ff\u81ea\u5df1\u7684\u60f3\u6cd5\u5bf9\u57ce\u4e1c\u4fe1\u7528\u793e\u5f00\u5200\uff0c\u4e24\u4e09\u4e2a\u6708\u7684\u5de5\u592b\u5c31\u88ab\u51a0\u4ee5\u2018\u4e71\u641e\u2019\u7684\u7f6a\u540d\u7ed9\u8c03\u56de\u8054\u793e\u3002\u201d</p><p>\u3000\u3000\u201c\u56fd\u4eba\u505a\u4e8b\u4e60\u60ef\u62d4\u4e1d\u62bd\u8327\u3001\u5faa\u5e8f\u6e10\u8fdb\uff0c\u201d\u5f20\u606a\u5c06\u9999\u70df\u5939\u5728\u624b\u91cc\uff0c\u8bf4\u9053\uff0c\u201c\u6682\u65f6\u5148\u8ba9\u4f60\u8fdb\u7b79\u5907\u7ec4\uff0c\u6211\u53ef\u4e0d\u5e0c\u671b\u4f60\u5728\u7b79\u5907\u7ec4\u91cc\u4e0e\u4eba\u548c\u7766\u76f8\u5904\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u8d75\u4e3b\u4efb\u4e0d\u4e5f\u5728\u7b79\u5907\u7ec4\uff0c\u201d\u674e\u6bc5\u534e\u81ea\u5632\u7684\u7b11\u4e86\u7b11\uff0c\u201c\u54ea\u53ef\u80fd\u548c\u7766\u76f8\u5904\u554a\uff1f\u201d\u8d75\u77f3\u8981\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u975e\u6349\u72c2\u4e0d\u53ef\uff0c\u8fd9\u65f6\u5019\u5012\u4e5f\u4e0d\u4f1a\u987e\u5fcc\u4ed6\u4e86\uff0c\u5728\u7b79\u5907\u7ec4\u91cc\u5904\u5904\u53d7\u5230\u9488\u5bf9\uff0c\u90a3\u662f\u53ef\u4ee5\u9884\u6599\u5f97\u5230\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u4e0d\u8981\u5e72\u7ad9\u5728\u8fd9\u91cc\u804a\uff0c\u53bb\u8c61\u5c71\uff0c\u201d\u5f20\u606a\u6253\u7535\u8bdd\u8ba9\u5085\u4fca\u8fc7\u6765\u63a5\u4ed6\u4eec\uff0c\u5728\u7b49\u8f66\u8fc7\u6765\u7684\u90a3\u6bb5\u65f6\u95f4\u91cc\uff0c\u5c06\u4ed6\u7684\u4e00\u4e9b\u60f3\u544a\u8bc9\u674e\u6bc5\u534e\uff0c\u201c\u4f60\u5bf9\u6d77\u5dde\u4eba\u4e0e\u4e8b\u7684\u8ba4\u8bc6\u5e94\u8be5\u6bd4\u6211\u8fd9\u4e2a\u9752\u5934\u6123\u5c0f\u5b50\u6df1\u523b\uff0c\u54ea\u4e9b\u4eba\u4e0e\u4f01\u4e1a\u6709\u53d1\u5c55\u7684\u6f5c\u529b\uff0c\u4f60\u63a5\u89e6\u7684\u5e94\u8be5\u6bd4\u6211\u5e7f\u6cdb\uff0c\u6211\u5e0c\u671b\u6210\u7acb\u540e\u7684\u57ce\u5546\u884c\u80fd\u6210\u4e3a\u6d77\u5dde\u4ea7\u4e1a\u6574\u4f53\u6269\u5f20\u7684\u540e\u76fe\uff0c\u6211\u4e0e\u5176\u4ed6\u4e24\u5bb6\u53c2\u80a1\u80a1\u4e1c\u90fd\u5df2\u505a\u51fa\u4e0d\u4ece\u57ce\u5546\u884c\u878d\u8d44\u7684\u627f\u8bfa\uff0c\u81ea\u7136\u4e0d\u5e0c\u671b\u770b\u5230\u57ce\u5546\u884c\u6210\u4e3a\u67d0\u4e00\u5c0f\u64ae\u5229\u76ca\u56e2\u4f53\u624b\u91cc\u7684\u5de5\u5177\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e9b\u8bdd\u4e4b\u5916\u7684\u610f\u601d\uff0c\u674e\u6bc5\u534e\u4e5f\u662f\u6e05\u695a\u7684\uff0c\u5f20\u606a\u6709\u80fd\u529b\u5c06\u4ed6\u9001\u4e0a\u57ce\u5546\u884c\u884c\u957f\u7684\u4f4d\u5b50\uff0c\u4f46\u662f\u4ed6\u6240\u652f\u6301\u7684\u57ce\u5546\u884c\u8981\u6267\u884c\u5f20\u606a\u7684\u91d1\u878d\u7b56\u7565\u3002</p><p>\u3000\u3000\u674e\u6bc5\u534e\u95ee\u9053\uff1a\u201c\u9526\u6e56\u65e2\u7136\u6ca1\u6709\u4ece\u57ce\u5546\u884c\u878d\u8d44\u7684\u9700\u8981\uff0c\u4e3a\u4ec0\u4e48\u8fd8\u5982\u6b64\u7684\u70ed\u5fc3\uff1f\u201d</p><p>\u3000\u3000\u201c\u66f4\u5e7f\u6cdb\u7684\u5f71\u54cd\u529b\uff0c\u610f\u5473\u66f4\u5e7f\u6cdb\u7684\u6743\u529b\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u662f\u4e0d\u662f\u8fd8\u662f\u4e00\u4e2a\u5f88\u6709\u91ce\u5fc3\u7684\u4eba\uff1f\u201d</p><p>\u3000\u3000\u201c\u6216\u8bb8\u4ece\u4e00\u4e2a\u56db\u5341\u5c81\u7684\u4e2d\u5e74\u4eba\u5634\u91cc\u8bf4\u51fa\u6765\uff0c\u66f4\u5bb9\u6613\u8ba9\u4eba\u63a5\u53d7\u3002\u201d\u674e\u6bc5\u534e\u4e0d\u63a9\u9970\u7684\u8fd9\u4e48\u8bf4\u7740\uff0c\u4ed6\u81ea\u5df1\u624d\u4e09\u5341\u516d\u5c81\u800c\u5df2\u3002\u4ed6\u5fc3\u91cc\u867d\u7136\u9707\u60ca\uff0c\u4f46\u662f\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u81ea\u7136\u80fd\u660e\u767d\u4ed6\u7684\u9ad8\u77bb\u8fdc\u77a9\uff0c\u8d22\u5bcc\u5728\u8fd9\u4e2a\u4ec5\u770b\u5e74\u9f84\u8fd8\u5f88\u7a1a\u5ae9\u3001\u4f46\u662f\u8c08\u5410\u3001\u4eea\u8c8c\u5374\u6c89\u9759\u3001\u5353\u5c14\u4e0d\u51e1\u7684\u5e74\u8f7b\u4eba\u773c\u91cc\u770b\u6765\u5df2\u7ecf\u4e0d\u518d\u662f\u6781\u5ea6\u6e34\u6c42\u7684\u76ee\u6807\u4e86\u3002\u5728\u6d77\u5dde\uff0c\u5173\u4e8e\u9526\u6e56\u7684\u4f20\u95fb\u5f88\u591a\uff0c\u674e\u6bc5\u534e\u8fd9\u65f6\u5019\u5012\u6709\u4e9b\u8feb\u4e0d\u53ca\u5f85\u7684\u60f3\u77e5\u9053\u66f4\u6df1\u5c42\u6b21\u7684\u771f\u76f8\u3002</p><p>\u3000\u3000\u5085\u4fca\u5f00\u8f66\u8fc7\u6765\uff0c\u5f20\u606a\u9080\u674e\u6bc5\u534e\u4e00\u8d77\u53bb\u8c61\u5c71\u5357\u5d16\u7684\u5b66\u6821\u5de5\u5730\uff0c\u5bf9\u674e\u6bc5\u534e\u8bf4\uff1a\u201c\u53bb\u89c1\u4e00\u89c1\u57ce\u5546\u884c\u4ee5\u540e\u7684\u5927\u5ba2\u6237\u3002\u201d</p><p>\u3000\u3000\u665a\u6674\u5c06\u5979\u7684\u7cbe\u529b\u90fd\u6295\u5165\u5230\u79c1\u7acb\u5b66\u6821\u4e0e\u690d\u7269\u56ed\u4e0a\uff0c\u4e5f\u5c31\u662f\u8bf4\u6d77\u88d5\u516c\u53f8\u7684\u7ecf\u6d4e\u6d3b\u52a8\u5f88\u5927\u7a0b\u5ea6\u4e0a\u5c06\u9650\u5728\u6d77\u5dde\uff0c\u5c06\u540d\u4e0b\u7684\u8d44\u91d1\u4f5c\u4e3a\u5b58\u6b3e\u8f6c\u5165\u57ce\u5546\u884c\uff0c\u4e0d\u4f1a\u56e0\u4e3a\u57ce\u5546\u884c\u7684\u5730\u57df\u9650\u5236\u800c\u53d7\u591a\u5927\u7684\u5f71\u54cd\uff0c\u57ce\u5546\u884c\u8981\u53d1\u5c55\uff0c\u62c9\u5165\u5927\u91cf\u7684\u5b58\u6b3e\u624d\u80fd\u652f\u6491\u4ed6\u4eec\u5411\u5916\u63d0\u4f9b\u878d\u8d44\u7684\u91d1\u878d\u670d\u52a1\u3002\u521a\u521a\u6210\u7acb\u7684\u57ce\u5546\u884c\u53d7\u5230\u6700\u5927\u7684\u9650\u5236\u5c31\u662f\u53ea\u80fd\u5728\u5f53\u5730\u5f00\u5c55\u91d1\u878d\u4e1a\u52a1\uff0c\u4e00\u76f4\u5230\u96f6\u4e09\u5e74\uff0c\u624d\u9010\u6b65\u653e\u5f00\u5730\u57df\u9650\u5236\u3002\u7231\u8fbe\u3001\u65b0\u5149\u7684\u7ecf\u6d4e\u6d3b\u52a8\u4e3b\u8981\u5728\u6d77\u5dde\u4e4b\u5916\uff0c\u4e0d\u53ef\u80fd\u5c06\u957f\u671f\u8d44\u91d1\u5b58\u5165\u57ce\u5546\u884c\uff0c\u4f46\u662f\u7231\u8fbe\u4e0e\u65b0\u5149\u7684\u7ecf\u6d4e\u6d3b\u52a8\u89c4\u6a21\u5e9e\u5927\uff0c\u4ece\u624b\u6307\u7f1d\u91cc\u6f0f\u4e00\u4e9b\u51fa\u6765\uff0c\u5c31\u662f\u773c\u4e0b\u5b58\u3001\u8d37\u6b3e\u89c4\u6a21\u624d\u4e94\u4e2a\u4ebf\u7684\u4fe1\u7528\u793e\u7684\u5927\u5ba2\u6237\uff0c\u4e0d\u8fc7\u8fd8\u662f\u4e0d\u80fd\u8ddf\u6d77\u88d5\u516c\u53f8\u6bd4\uff0c\u6d77\u88d5\u516c\u53f8\u4eca\u5e74\u4ece\u7231\u8fbe\u5206\u7ea2\u5c31\u63a5\u8fd1\u4e09\u4e2a\u4ebf\uff0c\u53ea\u8981\u534a\u6570\u73b0\u91d1\u6d41\u4ece\u57ce\u5546\u884c\u8d70\uff0c\u5c31\u80fd\u8ba9\u57ce\u5546\u884c\u7684\u4e1a\u52a1\u5f80\u524d\u8de8\u4e00\u4e2a\u5927\u53f0\u9636\u3002\u9664\u4e86\u6d77\u88d5\u516c\u53f8\u4e4b\u5916\uff0c\u76db\u9752\u3001\u90b5\u81f3\u521a\u4ee5\u53ca\u5f20\u77e5\u975e\u7b49\u4eba\u7684\u516c\u53f8\u90fd\u6ca1\u6709\u5927\u89c4\u6a21\u5411\u5916\u6269\u5c55\u7684\u8ba1\u5212\uff0c\u90fd\u53ef\u4ee5\u6210\u4e3a\u57ce\u5546\u884c\u7684\u5927\u5ba2\u6237\u3002</p><p>\u3000\u3000\u5f20\u606a\u8981\u62ac\u674e\u6bc5\u534e\u53bb\u505a\u57ce\u5546\u884c\u7684\u884c\u957f\uff0c\u81ea\u7136\u662f\u6709\u8db3\u591f\u628a\u63e1\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5165\u79cb\u540e\uff0c\u8c61\u5c71\u5357\u5d16\u4e0b\u7684\u665a\u666f\u5c31\u6781\u4e3a\u6d53\u90c1\uff0c\u752c\u9053\u4e24\u4fa7\u7684\u67ab\u6811\u53f6\u7ed9\u5165\u79cb\u540e\u7684\u51c9\u98ce\u6b21\u7b2c\u5439\u7ea2\uff0c\u6797\u95f4\u8349\u4e1b\u8fd8\u8ff8\u53d1\u51fa\u76ce\u7136\u7684\u9752\u7fe0\u3002</p><p>\u3000\u3000\u690d\u7269\u56ed\u9760\u8fd1\u6c5f\u8fb9\uff0c\u8f66\u4ece\u8c61\u5c71\u4e1c\u9e93\u7684\u5de5\u519c\u5357\u8def\u8fc7\u770b\u4e0d\u5230\u8fd9\u8fb9\uff0c\u674e\u6bc5\u534e\u8fd8\u662f\u7b2c\u4e00\u6b21\u770b\u5230\u8fd9\u5ea7\u7cbe\u81f4\u7684\u56ed\u6797\u3002\u89c4\u5212\u4e2d\u7684\u690d\u7269\u56ed\u8981\u4e00\u76f4\u5ef6\u5230\u5de5\u519c\u5357\u8def\uff0c\u73b0\u5728\u4e8c\u767e\u4ea9\u7684\u89c4\u6a21\u8fd8\u6ca1\u6709\u5b8c\u5168\u5efa\u6210\uff0c\u7ecf\u8fc7\u4e00\u5e74\u7684\u7cbe\u5fc3\u4f3a\u5f04\uff0c\u5165\u79cb\u540e\u9887\u4e3a\u58ee\u7f8e\u3002\u4e3a\u5b66\u6821\u7b79\u5907\u6240\u5efa\u7684\u529e\u516c\u697c\u5efa\u9020\u65f6\u5c31\u8003\u8651\u4ee5\u540e\u7ed9\u690d\u7269\u56ed\u7528\uff0c\u5728\u73b0\u5728\u690d\u7269\u56ed\u7684\u5357\u4fa7\uff0c\u5efa\u7b51\u9762\u79ef\u4e0d\u5927\uff0c\u5374\u82b1\u4e86\u4e9b\u5fc3\u601d\uff0c\u4e24\u5c42\u7684\u5c0f\u697c\u5927\u9762\u79ef\u7684\u91c7\u7528\u73bb\u7483\u5e55\u5899\uff0c\u6298\u5c04\u7740\u6c5f\u5929\u90a3\u5904\u7684\u5915\u9633\u971e\u5149\uff0c\u4f7f\u5f97\u6574\u5ea7\u5c0f\u697c\u90fd\u7b3c\u7f69\u8000\u773c\u593a\u76ee\u7684\u5149\u6655\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u8fc7\u6765\u4e86\uff1f\u201d\u665a\u6674\u62ac\u5934\u770b\u89c1\u5f20\u606a\u7ad9\u51dd\u89c6\u7684\u6df1\u9083\u773c\u7738\uff0c\u4e0d\u6653\u5f97\u4ed6\u6ce8\u89c6\u81ea\u5df1\u591a\u4e45\uff0c\u5fc3\u95f4\u8361\u6f3e\u51fa\u4e9b\u8bb8\u7f9e\u6da9\uff0c\u770b\u5230\u5f20\u606a\u8eab\u540e\u8fd8\u7ad9\u7740\u4e00\u4e2a\u4eba\uff0c\u6e29\u5a49\u7684\u7b11\u4e86\u7b11\uff0c\u5374\u5c06\u4e0d\u8be5\u6709\u7684\u795e\u6001\u6536\u655b\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u672c\u6765\u5c31\u60f3\u8fc7\u6765\u770b\u770b\uff0c\u201d\u5f20\u606a\u8d70\u8fc7\u6765\uff0c\u201c\u8fd9\u662f\u4fe1\u5408\u8054\u793e\u7684\u674e\u6bc5\u534e\uff0c\u966a\u4ed6\u8fc7\u6765\u62dc\u8bbf\u4f60\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u4f60\u7684\u6587\u7ae0\uff0c\u6211\u6709\u5728\u8bfb\u2026\u2026\u201d\u8c22\u665a\u6674\u4f38\u624b\u8bf7\u674e\u6bc5\u534e\u3001\u5f20\u606a\u5230\u529e\u516c\u5ba4\u4e34\u7a97\u7684\u6c99\u53d1\u90a3\u5750\u4e0b\uff0c\u5979\u7684\u79c1\u4eba\u52a9\u7406\u5f20\u5ead\u8fd9\u65f6\u5019\u7aef\u6765\u51b2\u597d\u7684\u5496\u5561\u653e\u5230\u73bb\u7483\u51e0\uff0c\u770b\u6765\u5f20\u606a\u7ad9\u5728\u95e8\u53e3\u6709\u51e0\u5206\u949f\u4e86\uff0c\u5012\u662f\u4e0d\u6015\u522b\u4eba\u770b\u51fa\u4e9b\u4ec0\u4e48\uff0c\u665a\u6674\u501f\u7740\u7aef\u5496\u5561\u7684\u65f6\u673a\uff0c\u77aa\u4e86\u4ed6\u4e00\u773c\u3002</p><p>\u3000\u3000\u5f20\u606a\u76ae\u7740\u8138\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u674e\u6bc5\u534e\u62ff\u773c\u89d2\u4f59\u5149\u6253\u91cf\u7740\u8fd9\u4e2a\u636e\u8bf4\u662f\u6d77\u5dde\u6700\u6709\u94b1\u6709\u52bf\u7684\u5973\u4eba\uff0c\u8fd8\u662f\u4e00\u4e2a\u5be1\u5987\uff0c\u5927\u6982\u662f\u826f\u597d\u7684\u5bb6\u5ead\u51fa\u8eab\u4e0e\u957f\u671f\u7684\u5bcc\u8d35\u751f\u6d3b\u8ba9\u773c\u524d\u7684\u5973\u5b50\u4ece\u9aa8\u5b50\u91cc\u900f\u51fa\u5178\u96c5\u9ad8\u8d35\u7684\u6c14\u8d28\u3002</p><p>\u3000\u3000\u6d77\u88d5\u516c\u53f8\u5c06\u53c2\u80a1\u57ce\u5546\u884c\uff0c\u8981\u5c06\u674e\u6bc5\u534e\u9001\u4e0a\u57ce\u5546\u884c\u884c\u957f\u4e4b\u4f4d\uff0c\u54ea\u6015\u662f\u6f14\u620f\u7ed9\u522b\u4eba\u770b\uff0c\u54ea\u6015\u662f\u5f62\u5f0f\u4e0a\u7684\u5c0a\u91cd\uff0c\u4e5f\u8981\u9886\u7740\u4ed6\u8fc7\u6765\u62dc\u8bbf\uff0c\u540c\u6837\u4e5f\u8981\u53bb\u62dc\u8bbf\u53f6\u5efa\u658c\u3002</p><p>\u3000\u3000\u5728\u529e\u516c\u5ba4\u91cc\u4ea4\u8c08\u7247\u523b\uff0c\u5f20\u606a\u8ba9\u5085\u4fca\u9001\u674e\u6bc5\u534e\u56de\u5e02\u533a\uff0c\u4ed6\u7559\u4e86\u4e0b\u6765\u3002</p><p>\u3000\u3000\u201c\u53ea\u662f\u60f3\u8fc7\u6765\u770b\u6211\uff0c\u591c\u91cc\u4f60\u8981\u53bb\u54ea\u91cc\uff1f\u201d\u529e\u516c\u5ba4\u91cc\u6ca1\u6709\u522b\u4eba\uff0c\u665a\u6674\u6021\u7136\u8f7b\u677e\u7684\u653e\u677e\u633a\u76f4\u7684\u8eab\u59ff\uff0c\u5178\u96c5\u6c14\u8d28\u91cc\u6d41\u9732\u51fa\u5a07\u67d4\u3001\u59a9\u5a9a\u3002</p><p>\u3000\u3000\u201c\u51fa\u53bb\u8d70\u8d70\uff0c\u5916\u9762\u8fd9\u4e48\u7f8e\uff0c\u7a9d\u5728\u8fd9\u91cc\u591a\u53ef\u60dc\uff0c\u201d\u5f20\u606a\u5e2e\u665a\u6674\u5c06\u5979\u7684\u6df1\u8272\u5916\u5957\u4ece\u6905\u80cc\u4e0a\u62ff\u8d77\u6765\uff0c\u201c\u8981\u4e0d\u8981\u6211\u4f3a\u5019\u4f60\u7a7f\u4e0a\uff1f\u201d</p><p>\u3000\u3000\u665a\u6674\u900f\u8fc7\u78e8\u7802\u73bb\u7483\u770b\u4e86\u770b\u5916\u95f4\uff0c\u5f20\u5ead\u4eba\u4e0d\u5728\u79d8\u4e66\u95f4\uff0c\u8f6c\u8fc7\u8eab\u5c06\u67d4\u5a49\u7684\u80cc\u7ed9\u5f20\u606a\uff0c\u8ba9\u4ed6\u5e2e\u81ea\u5df1\u5c06\u5916\u5957\uff0c\u7cfb\u6263\u5b50\u91cc\uff0c\u5fc3\u7830\u7830\u7684\u5728\u8df3\uff0c\u5012\u6709\u4e9b\u5077\u60c5\u7684\u523a\u6fc0\u3002</p><p>\u3000\u3000\u7cbe\u81f4\u88c1\u526a\u7684\u85cf\u9752\u8272\u5916\u5957\uff0c\u9886\u53e3\u5fae\u9732\u51fa\u6e56\u84dd\u8272\u7684\u8936\u76b1\u96ea\u7eb1\u7a20\u886c\u886b\u3001\u6df1\u8272\u7684\u725b\u4ed4\u7a84\u88d9\uff0c\u53ca\u819d\u7684\u9cc4\u9c7c\u76ae\u957f\u9774\u90fd\u7ed9\u4eba\u6e05\u51b7\u800c\u4e25\u8c28\u7684\u611f\u89c9\uff0c\u8fd9\u662f\u8c22\u665a\u6674\u4e00\u76f4\u4fdd\u6301\u7684\u5de5\u4f5c\u5f62\u8c61\uff0c\u60df\u6709\u5f20\u606a\u624d\u80fd\u611f\u89e6\u5230\u90a3\u56e2\u7ed9\u5bd2\u51b0\u5305\u88f9\u7740\u7684\u70bd\u70ed\u706b\u7130\u3002</p><p>\u3000\u3000\u201c\u6211\u7b49\u4f1a\u81ea\u5df1\u5f00\u8f66\u56de\u53bb\u3002\u201d\u665a\u6674\u51fa\u529e\u516c\u5ba4\u9047\u5230\u52a9\u7406\u5f20\u5ead\u3002</p><p>\u3000\u3000\u867d\u7136\u5728\u529e\u516c\u5ba4\u900f\u8fc7\u73bb\u7483\u5899\u4e5f\u80fd\u770b\u5230\u5165\u79cb\u540e\u7684\u7f8e\u666f\uff0c\u7ec8\u4e0d\u5982\u5fae\u98ce\u62c2\u5728\u5a07\u5ae9\u7684\u8138\u4e0a\u6e05\u65b0\u6021\u60c5\uff0c\u8fd8\u5728\u56ed\u5b50\u91cc\u4f20\u6765\u7684\u9635\u9635\u82b1\u9999\u3002</p><p>\u3000\u3000\u62d0\u5165\u7af9\u6797\u9e45\u5375\u77f3\u94fa\u6210\u7684\u5c0f\u5f84\uff0c\u8c22\u665a\u6674\u5c06\u624b\u63d2\u5728\u5916\u5957\u5927\u515c\u91cc\uff0c\u8ba9\u5f20\u606a\u4e5f\u4f38\u4e00\u53ea\u624b\u6765\uff0c\u5373\u4f7f\u8ba9\u4eba\u770b\u5230\uff0c\u4e5f\u53ea\u4ee5\u4e3a\u4e24\u4eba\u6328\u5f97\u6bd4\u8f83\u8fd1\u800c\u5df2\u3002</p><p>\u3000\u3000\u201c\u591c\u91cc\u53bb\u65b0\u829c\uff0c\u5e26\u6768\u4e91\u4e0e\u5218\u660e\u8f89\u4e00\u8d77\u53bb\uff0c\u65b0\u829c\u7684\u9152\u5382\u5df2\u7ecf\u7b79\u529e\u597d\u4e86\uff0c\u6211\u61d2\u5f97\u52a8\u8111\u5b50\uff0c\u62c9\u4ed6\u4eec\u4fe9\u53bb\u7ed9\u6211\u7238\u4ed6\u652f\u652f\u62db\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u7528\u4eba\u8fd8\u771f\u5f7b\u5e95\uff0c\u201d\u665a\u6674\u7b11\u4e86\u7b11\uff0c\u4efb\u5f20\u606a\u624b\u6307\u6320\u7740\u81ea\u5df1\u7684\u624b\u5fc3\uff0c\u201c\u79d1\u738b\u6700\u8fd1\u7684\u52a8\u4f5c\u5f88\u5927\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u8c22\u5251\u5357\u505a\u5e02\u573a\u8fd8\u662f\u6709\u4e00\u5957\u7684\uff0c\u4e39\u6d25\u53e3\u670d\u6db2\u73b0\u5728\u8fd8\u662f\u5e2e\u6b63\u6cf0\u6402\u94b1\uff0c\u6d77\u6cf0\u5728\u5546\u54c1\u6d41\u901a\u9886\u57df\u4e5f\u79ef\u7d2f\u4e86\u76f8\u5f53\u7684\u7ecf\u9a8c\uff0c\u773c\u4e0b\u6240\u6709\u7684\u5e02\u573a\u6570\u636e\u90fd\u662f\u79ef\u6781\u7684\uff0c\u4ed6\u4eec\u60f3\u505a\u5927\u79d1\u738b\uff0c\u4e0d\u8db3\u4e3a\u602a\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4f1a\u8ddf\u5218\u660e\u8f89\u518d\u8c08\u8c08\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f1a\uff0c\u4ed6\u6bd5\u7adf\u7ed9\u7231\u8fbe\u505a\u51fa\u8d21\u732e\u7684\uff1b\u53bb\u79d1\u738b\uff0c\u4ed6\u6ca1\u6709\u66f4\u5e7f\u7684\u8def\u53ef\u8d70\u3002\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8981\u4e0d\u662f\u60f3\u6700\u540e\u633d\u7559\u4e00\u4e0b\u5218\u660e\u8f89\uff0c\u8fd9\u6b21\u53bb\u65b0\u829c\uff0c\u6ca1\u6709\u5fc5\u8981\u5f3a\u62c9\u7740\u6768\u4e91\u4e0e\u4ed6\u4e00\u8d77\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u665a\u98ce\u5439\u52a8\u7af9\u68a2\u7c07\u52a8\uff0c\u6c99\u6c99\u4f5c\u54cd\uff0c\u5f20\u606a\u637b\u4e86\u637b\u665a\u6674\u624b\u5fc3\uff0c\u5b9e\u5728\u60f3\u4e0d\u80fd\u4e3a\u4ec0\u4e48\u53ef\u4ee5\u6bcf\u4e00\u5904\u808c\u80a4\u90fd\u8fd9\u4e48\u7684\u5ae9\u6ed1\uff0c\u637b\u634f\u7740\u5c31\u6709\u5f88\u9500\u9b42\u7684\u611f\u89c9\uff0c\u5bf9\u5979\u8bf4\uff1a\u201c\u6709\u4ef6\u4e8b\uff0c\u8981\u8ddf\u4f60\u8bf4\u4e00\u4e0b\uff0c\u6211\u60f3\u8ba9\u7231\u8fbe\u591a\u6253\u51e0\u4e2a\u6708\u7684\u8d27\u6b3e\u53bb\u9999\u6e2f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u8c22\u665a\u6674\u4fa7\u8fc7\u8eab\u6765\u770b\u7740\u5f20\u606a\uff0c\u201c\u4f60\u8fd9\u4e9b\u5929\u4e00\u76f4\u5728\u7814\u7a76\u4e1c\u5357\u4e9a\u7684\u91d1\u878d\u7f3a\u9677\uff0c\u60f3\u53bb\u6d51\u6c34\u6478\u9c7c\uff1f\u201d</p><p>\u3000\u3000\u201c\u6d51\u6c34\u4e5f\u4e0d\u662f\u90a3\u4e48\u597d\u6478\u9c7c\u7684\uff0c\u8d81\u706b\u6253\u52ab\u4f1a\u7ed9\u522b\u4eba\u8bb0\u4ec7\u7684\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4ed6\u53ea\u77e5\u9053\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u4e2d\uff0c\u5728\u9999\u6e2f\u6c47\u5e02\u3001\u80a1\u5e02\u4efb\u4f55\u5927\u8086\u505a\u7a7a\u7684\u4ea4\u6613\u90fd\u8981\u5411\u76d1\u7ba1\u90e8\u95e8\u5907\u6848\uff0c\u8d81\u706b\u6253\u52ab\u5bb9\u6613\uff0c\u4f46\u4e5f\u8981\u9884\u9632\u7ed9\u79cb\u540e\u7b97\u8d26\uff0c\u9526\u6e56\u60f3\u505a\u5927\u5b9e\u4e1a\uff0c\u4e0e\u653f\u5e9c\u4e4b\u95f4\u7684\u5173\u7cfb\u5c31\u4e0d\u80fd\u6076\u5316\uff0c\u8d81\u706b\u6253\u52ab\u7684\u4e8b\u60c5\u5c31\u7b97\u8981\u505a\uff0c\u4e5f\u8981\u505a\u5f97\u5de7\u5999\u624d\u884c\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u660e\u5e74\u590f\u5929\u9999\u6e2f\u56de\u5f52\uff0c\u7ecf\u6d4e\u5f88\u5bb9\u6613\u7ed9\u653f\u6cbb\u7ed1\u67b6\uff0c\u5371\u673a\u4e4b\u524d\uff0c\u603b\u4f1a\u6709\u6700\u75af\u72c2\u7684\u7e41\u8363\uff0c\u5728\u6700\u7e41\u8363\u7684\u90a3\u6bb5\u65f6\u95f4\u91cc\uff0c\u80fd\u987a\u5229\u7684\u5c06\u7231\u8fbe\u7535\u5b50\u7f6e\u5165\u5609\u4fe1\u5b9e\u4e1a\uff0c\u624d\u662f\u6211\u4eec\u6700\u4e3b\u8981\u7684\u76ee\u6807\uff0c\u5176\u4ed6\u554a\uff0c\u53ea\u662f\u6323\u4e9b\u96f6\u82b1\u94b1\u800c\u5df2\u3002\u518d\u8bf4\u4e86\uff0c\u4e24\u4e09\u4ebf\u8d44\u91d1\uff0c\u5728\u9999\u6e2f\u8fd9\u4e2a\u5168\u7403\u91d1\u878d\u4e2d\u5fc3\u6216\u8bb8\u8fde\u6761\u6ce5\u9cc5\u90fd\u7b97\u4e0d\u4e0a\uff0c\u4f60\u60f3\u5403\u4eba\u5bb6\uff0c\u4eba\u5bb6\u8fd8\u5f20\u7740\u53e3\u60f3\u5403\u4f60\u5462\u3002\u201d</p><p>\u3000\u3000\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u8d1f\u8d23\u7231\u8fbe\u4e0eTI\u516c\u53f8\u4e4b\u95f4\u7684\u89e3\u7801\u677f\u8f6c\u53e3\u8d38\u6613\uff0c\u6bcf\u4e2a\u6708\u90fd\u662f\u62ff\u94f6\u884c\u627f\u5151\u6c47\u7968\u7ed3\u7b97\uff0c\u7231\u8fbe\u7535\u5b50\u73b0\u5728\u6bcf\u4e2a\u6708\u91c7\u7528TI\u516c\u53f8\u5341\u4e8c\u4e07\u5957\u89e3\u7801\u677f\uff0c\u6bcf\u4e2a\u6708\u7684\u8d44\u91d1\u6d41\u91cf\u6709\u4e03\u516b\u5343\u4e07\uff0c\u7231\u8fbe\u7535\u8fbe\u5f80\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u591a\u6253\u4e09\u56db\u4e2a\u6708\u8d27\u6b3e\uff0c\u5c31\u76f8\u5f53\u4e8e\u5f80\u9999\u6e2f\u90a3\u91cc\u6253\u5165\u4e24\u4e09\u4e2a\u4ebf\u7684\u8d44\u91d1\u3002\u4e5f\u53ea\u80fd\u5230\u8fd9\u4e2a\u7a0b\u5ea6\uff0c\u8c03\u518d\u591a\u7684\u8d44\u91d1\u8fc7\u53bb\uff0c\u5c31\u4f1a\u5f71\u54cd\u7231\u8fbe\u81ea\u5df1\u7684\u8d44\u91d1\u5468\u8f6c\u4e0e\u5176\u9879\u76ee\u7684\u8fdb\u5ea6\u3002</p><p>\u3000\u3000\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u6bcf\u4e2a\u6708\u867d\u7136\u80fd\u4ece\u89e3\u7801\u677f\u7684\u8f6c\u53e3\u8d38\u6613\u91cc\u5957\u51fa\u5de8\u989d\u7684\u8d62\u5229\uff0c\u4f46\u662f\u4e09\u6708\u521d\u53c2\u4e0e\u5609\u4fe1\u5b9e\u4e1a\u7684\u589e\u53d1\u5c31\u8017\u8d44\u4e09\u5343\u4e07\uff0c\u516d\u6708\u521d\u5609\u4fe1\u5b9e\u4e1a\u4e3a\u63a7\u80a1\u60e0\u5c71\u9999\u96ea\u6d77\u53d1\u884c\u4e09\u4ebf\u6e2f\u5e01\u7684\u516c\u53f8\u503a\uff0c\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u72ec\u529b\u8ba4\u8d2d\u8fd1\u56db\u6210\uff0c\u5f53\u521d\u5c31\u662f\u901a\u8fc7\u7231\u8fbe\u7535\u5b50\u9884\u4ed8\u8d27\u6b3e\u7684\u5f62\u5f0f\u7b79\u7b97\u8d44\u91d1\uff0c\u4e00\u76f4\u5230\u4e0a\u4e2a\u6708\u624d\u5c06\u8fd9\u7b14\u94b1\u62b9\u5e73\u3002\u8ba1\u7b97\u4e0b\u6765\uff0c\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u622a\u6b62\u5230\u4e5d\u6708\u4efd\u5b9e\u9645\u622a\u7559\u5229\u6da6\u5c06\u8fd1\u4e24\u4e2a\u4ebf\u3002</p><p>\u3000\u3000\u4fe9\u4eba\u8d70\u8fdb\u7af9\u6797\u7684\u6df1\u5904\uff0c\u8c22\u665a\u6674\u6367\u7740\u5f20\u606a\u7ebf\u6761\u5206\u660e\u7684\u8138\uff0c\u5ae3\u7136\u7b11\u7740\u8bf4\uff1a\u201c\u771f\u60f3\u628a\u4f60\u7684\u8111\u58f3\u6316\u51fa\u6765\u770b\u770b\uff0c\u5230\u5e95\u8ddf\u666e\u901a\u4eba\u6709\u4ec0\u4e48\u4e0d\u540c\u3002\u201d</p><p>\u3000\u3000\u201c\u628a\u6211\u8eab\u4e0a\u5176\u4ed6\u5730\u65b9\u7814\u7a76\u8fc7\uff0c\u5f00\u59cb\u60f3\u7814\u7a76\u6211\u8111\u888b\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u53bb\uff0c\u80e1\u8bf4\u516b\u9053\u4ec0\u4e48\uff1f\u201d\u665a\u6674\u5a07\u55d4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u8f7b\u8f7b\u5728\u665a\u6674\u5a07\u6da6\u7684\u7ea2\u5507\u4e0a\u8f7b\u8f7b\u5370\u4e86\u4e00\u53e3\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6d77\u5dde\u5230\u65b0\u829c\u7684\u94c1\u8def\uff0c\u8981\u4ece\u5317\u9762\u62d0\u4e00\u4e2a\u5927\u5f2f\uff0c\u591c\u91cc\u4e03\u70b9\u7684\u706b\u8f66\uff0c\u7b2c\u4e8c\u5929\u4e03\u70b9\u624d\u80fd\u5230\u65b0\u829c\uff0c\u8fd8\u8981\u6307\u671b\u706b\u8f66\u4e0d\u665a\u70b9\u3002\u5728\u5f20\u606a\u7684\u5370\u8c61\u91cc\uff0c\u4e5d\u5341\u5e74\u4ee3\uff0c\u597d\u50cf\u6ca1\u9047\u5230\u706b\u8f66\u4e0d\u665a\u70b9\u8fd9\u56de\u4e8b\u3002</p><p>\u3000\u3000\u6d77\u5dde\u4e0e\u65b0\u829c\u4e24\u5730\u7684\u76f4\u7ebf\u8ddd\u79bb\u624d\u53ea\u6709\u4e09\u767e\u591a\u516c\u91cc\uff0c\u7ed5\u9053\u7701\u57ce\uff0c\u81f3\u5c11\u8981\u591a\u7ed5\u4e00\u767e\u516c\u91cc\uff0c\u4f46\u662f\u4e1c\u6d77\u5317\u90e8\u8fd8\u6ca1\u6709\u51e0\u6761\u9ad8\u901f\u516c\u8def\uff0c\u7ed5\u8d70\u56fd\u9053\u6216\u8005\u7701\u9053\uff0c\u8def\u7a0b\u4e5f\u76f8\u5dee\u65e0\u6bd4\uff0c\u82e5\u4e0d\u80fd\u989d\u5916\u53bb\u63a8\u52a8\u4ed6\uff0c\u7701\u9ad8\u901f\u516c\u8def\u7f51\u57fa\u672c\u5efa\u6210\u8981\u62d6\u5230\u4e8c\u96f6\u96f6\u4e09\u5e74\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u53bb\u65b0\u829c\uff0c\u9664\u975e\u8981\u968f\u65f6\u968f\u5730\u51fa\u53d1\u624d\u5f00\u8f66\u53bb\uff0c\u80fd\u63d0\u524d\u5b89\u6392\u597d\uff0c\u5750\u706b\u8f66\u6700\u65b9\u4fbf\uff0c\u7761\u4e00\u591c\u5c31\u5230\uff0c\u5373\u4f7f\u665a\u70b9\u4e00\u4e2a\u5c0f\u65f6\uff0c\u4e5f\u4e0d\u803d\u6401\u767d\u5929\u505a\u4e8b\uff0c\u5728\u8f66\u53a2\u91cc\u4e5f\u53ef\u4ee5\u5904\u7406\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f00\u8f66\u8f7d\u665a\u6674\u56de\u5e02\u533a\uff0c\u6ca1\u8d76\u5f97\u53ca\u5403\u665a\u996d\uff0c\u7b49\u706b\u8f66\u5f00\u52a8\u4e4b\u540e\u8ba9\u5085\u4fca\u5e2e\u4ed6\u6ce1\u4e86\u7897\u65b9\u4fbf\u9762\uff0c\u4ed6\u62ff\u8d77\u5e02\u573a\u90e8\u8d77\u8349\u7684\u660e\u5e74\u7684\u5e02\u573a\u5ba3\u4f20\u8ba1\u5212\u8bfb\u8d77\u6765\u3002</p><p>\u3000\u3000\u4ed6\u5728\u4e5d\u6708\u5e95\u7684\u4f1a\u8bae\u4e0a\uff0c\u7ed9\u7231\u8fbe\u7535\u5b50\u660e\u5e74\u7684\u5e02\u573a\u5ba3\u4f20\u5b9a\u4e0b\u516d\u4e2a\u4ebf\u7684\u9884\u7b97\uff0c\u5e02\u573a\u90e8\u6839\u636e\u9884\u7b97\u5236\u5b9a\u51fa\u6765\u8fd9\u4efd\u8be6\u7ec6\u7684\u8ba1\u5212\u4e66\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u5728\u53bb\u5e74\u5341\u4e00\u6708\u516b\u65e5\u593a\u5f97\u592e\u89c6\u6807\u738b\u4e4b\u540e\u4e00\u4e3e\u6210\u540d\uff0c\u5728\u83b7\u5f97\u5e02\u573a\u77a9\u76ee\u7684\u540c\u65f6\uff0c\u4e5f\u8ba9\u7231\u8fbe\u7535\u5b50\u62db\u4e86\u4e00\u6279\u5e02\u573a\u8425\u9500\u65b9\u9762\u7684\u7cbe\u82f1\u8fdb\u5e02\u573a\u90e8\uff0c\u8fd9\u4efd\u65b9\u6848\u53ef\u8c13\u5c3d\u5584\u5c3d\u7f8e\uff0c\u5f20\u606a\u90fd\u770b\u4e0d\u51fa\u6709\u54ea\u4e9b\u5730\u65b9\u9700\u8981\u53bb\u6539\u3002</p><p>\u3000\u3000\u89c1\u9762\u5dee\u4e0d\u591a\u6ce1\u8f6f\uff0c\u5f20\u606a\u5c06\u7ffb\u4e86\u4e00\u534a\u7684\u8ba1\u5212\u4e66\u4e22\u5230\u4e00\u65c1\uff0c\u5148\u5403\u8d77\u6ce1\u9762\u6765\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\uff0c\u8fd9\u4efd\u65b9\u6848\u89c9\u5f97\u600e\u4e48\u6837\uff1f\u201d\u6768\u4e91\u6c89\u4e0d\u4f4f\u6c14\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u54e6\uff0c\u201d\u5f20\u606a\u809a\u5b50\u997f\u5f97\u5389\u5bb3\uff0c\u53ea\u7ba1\u5f80\u5634\u91cc\u6252\u9762\u6761\uff0c\u6ca1\u7a7a\u642d\u7406\u4ed6\uff0c\u5c06\u534a\u7897\u9762\u6761\u5377\u8fdb\u809a\u5b50\uff0c\u624d\u7f13\u4e86\u4e00\u53e3\u6c14\u8bf4\uff0c\u201c\u5341\u4e00\u70b9\u5403\u7684\u4e2d\u996d\uff0c\u5230\u73b0\u5728\u6709\u4e03\u516b\u4e2a\u5c0f\u65f6\uff0c\u4f60\u4eec\u5148\u7b49\u6211\u5c06\u6ce1\u9762\u5403\u5b8c\u518d\u8c08\u8fd9\u4e9b\u95ee\u9898\u3002\u201d</p><p>\u3000\u3000\u201c\u5728\u8f6f\u5367\u8f66\u53a2\u5403\u6ce1\u9762\u5012\u5f88\u7f55\u89c1\uff0c\u4f60\u4e5f\u4e0d\u6015\u6389\u4f60\u7684\u67b6\u5b50\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u6ca1\u6709\u4ec0\u4e48\u67b6\u5b50\u53ef\u6389\uff0c\u6bd4\u8d77\u6ce1\u9762\uff0c\u9910\u8f66\u63d0\u4f9b\u7684\u98df\u7269\u66f4\u4e0d\u662f\u4eba\u80fd\u5403\u7684\u3002\u201d\u5f20\u606a\u5c06\u6c64\u6c41\u90fd\u5012\u8fdb\u809a\u5b50\uff0c\u5085\u4fca\u8fc7\u6765\u5e2e\u4ed6\u5c06\u5783\u573e\u62ff\u51fa\u53bb\u3002\u5f20\u606a\u5c06\u8ba1\u5212\u4e66\u62ff\u8fc7\u6765\u7ee7\u7eed\u7ffb\u7740\u770b\uff0c\u8fb9\u770b\u8fb9\u8bf4\uff1a\u201c\u7f8e\u56fd\u7684\u90a3\u4e2a\u5e7f\u544a\u5927\u5e08\u514b\u52b3\u5fb7\xb7\u970d\u666e\u91d1\u65af\u5728\u4ed6\u7684\u4e66\u91cc\u6709\u4e00\u6bb5\u5f88\u4e0d\u7ecf\u610f\u7684\u6587\u5b57\uff1a\u6211\u6240\u770b\u5230\u8fc7\u7684\u5f88\u591a\u7ecf\u8425\u5371\u673a\u5927\u90fd\u662f\u56e0\u4e3a\u4e0b\u9762\u7684\u8fd9\u51e0\u79cd\u539f\u56e0\uff1a\u505a\u4e8b\u592a\u8fc7\u5206\uff0c\u5728\u6f5c\u5728\u673a\u9047\u9762\u524d\u4e0d\u591f\u5927\u80c6\uff0c\u770b\u4e0d\u8d77\u4fdd\u5b88\u4f5c\u98ce\u800c\u4ed3\u4fc3\u884c\u4e8b\uff0c\u5bb3\u6015\u7ade\u4e89\u8005\u53ef\u80fd\u6bd4\u81ea\u5df1\u8d70\u5f97\u66f4\u8fdc\u6216\u8005\u722c\u5f97\u66f4\u9ad8\uff0c\u6240\u4ee5\u81ea\u5df1\u4e0d\u60dc\u5728\u4e00\u6761\u672a\u66fe\u5f00\u8f9f\u7684\u5c0f\u8def\u4e0a\u8d38\u7136\u9886\u8dd1\u3002\u4ed6\u7684\u8fd9\u6bb5\u6587\u5b57\u975e\u5e38\u6709\u610f\u601d\uff0c\u6211\u5206\u6790\u8fc7\u516b\u5341\u5e74\u4ee3\u5230\u4eca\u5929\u7684\u7edd\u5927\u591a\u6570\u7684\u4f01\u4e1a\u5371\u673a\u4ecd\u7136\u65e0\u975e\u662f\u970d\u666e\u91d1\u65af\u7f57\u5217\u8fc7\u7684\u8fd9\u4e9b\u539f\u56e0\u5f15\u8d77\u7684\u2026\u2026\u6211\u770b\u4eca\u540e\u53ef\u80fd\u51fa\u6765\u7684\u90a3\u4e9b\u4f01\u4e1a\u5371\u673a\uff0c\u4e5f\u9003\u8131\u4e0d\u51fa\u8fd9\u4e9b\u8303\u7574\u3002\u201d\u5f20\u606a\u770b\u7740\u5218\u660e\u8f89\uff0c\u95ee\u4ed6\uff0c\u201c\u4f60\u600e\u4e48\u770b\u8fd9\u4e9b\u8bdd\uff1f\u201d\u8ddf\u4ed6\u4e00\u8d77\u4e0a\u706b\u8f66\u4e4b\u540e\uff0c\u4ed6\u7684\u8bdd\u5f88\u5c11\uff0c\u5fc3\u4e8b\u91cd\u91cd\u3002</p><p>\u3000\u3000\u201c\u6211\u2026\u2026\u201d\u5218\u660e\u8f89\u62ac\u5934\u770b\u7740\u5f20\u606a\uff0c\u76ee\u5149\u76f8\u63a5\u4e86\u90a3\u4e9b\u4e00\u77ac\u95f4\uff0c\u4ed6\u5c31\u5c06\u76ee\u5149\u79fb\u5f00\uff0c\u4e0d\u6562\u770b\u5f20\u606a\u90a3\u6df1\u9083\u7684\u51e0\u4e4e\u80fd\u770b\u900f\u4eba\u5fc3\u7684\u773c\u795e\uff0c\u4ed6\u8fd8\u4ee5\u4e3a\u5f20\u606a\u662f\u5728\u8c08\u7231\u8fbe\u7684\u95ee\u9898\uff0c\u8bf4\u9053\uff0c\u201c\u73b0\u5728\u8c08\u4f01\u4e1a\u5371\u673a\u8fd8\u592a\u65e9\uff0c\u6211\u5012\u8ba4\u4e3a\u56fd\u5185\u7684\u4f01\u4e1a\u5708\uff0c\u6709\u4e00\u79cd\u7279\u6b8a\u7684\u3001\u5b9c\u4e8e\u5947\u8ff9\u840c\u82bd\u7684\u571f\u58e4\uff0c\u7231\u8fbe\u7684\u547c\u5578\u800c\u8d77\uff0c\u5728\u7edd\u5927\u591a\u6570\u4eba\u773c\u91cc\u90fd\u8981\u89c6\u4e3a\u5947\u8ff9\u5427\uff0c\u6211\u76f8\u4fe1\u7231\u8fbe\u80fd\u7ee7\u7eed\u521b\u9020\u5947\u8ff9\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u62ff\u4e24\u4ebf\u516b\u5343\u4e07\u53bb\u7ee7\u7eed\u521b\u9020\u5947\u8ff9\uff1f\u201d\u5f20\u606a\u5634\u89d2\u6d6e\u51fa\u4e00\u4e1d\u7b11\uff0c\u7231\u8fbe\u7ed9\u4e0b\u4e2a\u6708\u7684\u592e\u89c6\u6807\u738b\u505a\u51fa\u6700\u9ad8\u4e8c\u70b9\u516b\u4ebf\u7684\u9884\u7b97\u3002\u5218\u660e\u8f89\u8fd8\u662f\u8fc7\u5ea6\u91cd\u89c6\u5e02\u573a\u624b\u6bb5\u7684\u5947\u8ff9\u5149\u73af\uff0c\u80fd\u771f\u6b63\u770b\u5230\u7684\u4e1c\u897f\u592a\u72ed\u7a84\uff0c\u5f20\u606a\u62bf\u7740\u5634\uff0c\u4e0d\u7531\u7684\u66ff\u4ed6\u611f\u5230\u4e00\u4e1d\u7684\u60b2\u54c0\uff1a\u4ed6\u6216\u8bb8\u4ee5\u4e3a\u524d\u671f\u65f6\u95f4\u7a0d\u7a0d\u538b\u5236\u5e02\u573a\u90e8\u662f\u9488\u5bf9\u4ed6\u4e2a\u4eba\uff0c\u201c\u5728\u56fd\u5185\u5b9c\u4e8e\u5947\u8ff9\u840c\u82bd\u7684\u571f\u58e4\u4e0a\uff0c\u5229\u7528\u5e02\u573a\u624b\u6bb5\u4e00\u9e23\u60ca\u4eba\u5bb9\u6613\uff0c\u6211\u770b\u73b0\u5728\u7684\u79d1\u738b\u5c31\u5f88\u6709\u4e00\u9e23\u60ca\u4eba\u7684\u51b2\u52a8\uff0c\u201d\u5f20\u606a\u770b\u7740\u5218\u660e\u8f89\u7684\u773c\u775b\uff0c\u624b\u6307\u538b\u7740\u65b9\u6848\u4e66\u4e0a\uff0c\u201c\u4f46\u662f\u5728\u4e00\u9e23\u60ca\u4eba\u4e4b\u540e\uff0c\u904f\u6b62\u5185\u5728\u7684\u975e\u7406\u6027\u5e02\u573a\u51b2\u52a8\uff0c\u8ba9\u4f01\u4e1a\u8fc5\u901f\u7684\u8131\u80ce\u6362\u9aa8\uff0c\u624d\u662f\u6301\u4e45\u7684\u751f\u5b58\u4e4b\u9053\uff0c\u6211\u73b0\u5728\u66f4\u5173\u5fc3\u8fd9\u4e2a\u95ee\u9898\uff0c\u6211\u770b\u79d1\u738b\u4e5f\u5e94\u8be5\u63d0\u524d\u8003\u8651\u4e00\u4e0b\u8fd9\u4e2a\u95ee\u9898\uff0c\u201d\u8fc7\u5ea6\u7684\u4f9d\u8d56\u5e02\u573a\u624b\u6bb5\uff0c\u53ea\u662f\u975e\u7406\u6027\u7684\u51b2\u52a8\u800c\u5df2\uff0c\u51e0\u4e4e\u5c06\u970d\u666e\u91d1\u65af\u6240\u7f57\u5217\u7684\u51e0\u79cd\u53ef\u80fd\u5bfc\u81f4\u4f01\u4e1a\u7ecf\u8425\u5371\u673a\u7684\u539f\u56e0\u90fd\u56ca\u62ec\u8fdb\u53bb\uff0c\u79d1\u738b\u4e89\u4e0b\u592e\u89c6\u6807\u738b\uff0c\u53ef\u60dc\u6b8b\u9177\u7684\u5e02\u573a\u662f\u4e0d\u53ef\u80fd\u7ed9\u4ed6\u4eec\u8db3\u591f\u7684\u65f6\u95f4\u8fdb\u884c\u8c03\u6574\u7684\u3002\u5f20\u606a\u5c06\u65b9\u6848\u4e66\u4e22\u5230\u5218\u660e\u8f89\u9762\u524d\uff0c\u8bdd\u5df2\u7ecf\u8bf4\u5230\u8fd9\u91cc\u4e86\uff0c\u4ed6\u4f9d\u7136\u8ba4\u4e3a\u53bb\u79d1\u738b\u66f4\u6709\u524d\u9014\uff0c\u4e5f\u65e0\u9700\u518d\u633d\u7559\u4ed6\u4e86\u3002\u201c\u660e\u5e74\u7684\u5e02\u573a\u5ba3\u4f20\u8ba1\u5212\uff0c\u4f60\u4eec\u5e02\u573a\u90e8\u51b3\u5b9a\u5c31\u597d\u4e86\uff0c\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u5e7f\u544a\u62db\u6807\u5927\u4f1a\uff0c\u4f60\u4eec\u4fe9\u966a\u6211\u53bb\u5317\u4eac\u2026\u2026\u201d</p></article>"}}}]);
