(window.webpackJsonp=window.webpackJsonp||[]).push([[565],{588:function(p){p.exports={chapterTitle:null,title:"\u7b2c565\u7ae0 \u672a\u53d1\u751f\u7684\u7eb5\u706b\u6848",content:"<article><h2>\u7b2c565\u7ae0 \u672a\u53d1\u751f\u7684\u7eb5\u706b\u6848</h2><p>\u3000\u3000\u6797\u96ea\u5343\u5a07\u767e\u5a9a\u7684\u8138\u50f5\u5728\u90a3\u91cc\uff0c\u5979\u65e2\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u4f1a\u8ba4\u8bc6\u81ea\u5df1\uff0c\u66f4\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u80af\u5b9a\u81ea\u5df1\u8ba4\u8bc6\u4ed6\uff0c\u521a\u624d\u7684\u8fd9\u4e00\u756a\u8868\u6f14\u5728\u4ed6\u773c\u91cc\u4e0d\u8fc7\u662f\u4e00\u51fa\u6ed1\u7a3d\u620f\u800c\u5df2\u3002\u6797\u96ea\u5f3a\u88c5\u9547\u9759\uff0c\u8fce\u7740\u5f20\u606a\u660e\u6f88\u800c\u9510\u5229\u7684\u773c\u795e\uff0c\u624b\u5fae\u5fae\u98a4\u6296\u4e86\u4e00\u4e0b\uff0c\u624d\u610f\u8bc6\u5230\u624b\u8fd8\u642d\u5728\u4eba\u5bb6\u7684\u80a9\u8180\u4e0a\uff0c\u89e6\u7535\u4f3c\u7684\u6536\u4e86\u56de\u53bb\u3002</p><p>\u3000\u3000\u9648\u9759\u4e5f\u5341\u5206\u5947\u602a\uff0c\u6797\u96ea\u8fd9\u4e2a\u5973\u4eba\u7684\u8fd9\u756a\u8868\u6f14\u65e0\u975e\u662f\u8981\u626f\u4e0a\u81ea\u5df1\u8ddf\u5f20\u606a\u201c\u4e0d\u7ecf\u610f\u95f4\u201d\u642d\u4e0a\u5173\u7cfb\uff0c\u5979\u843d\u4e95\u4e0b\u77f3\u7684\u95ee\u9053\uff1a\u201c\u606a\u5c11\u80af\u5b9a\u6797\u603b\u8ba4\u8bc6\u4f60\uff1f\u201d\u5979\u4e5f\u7684\u786e\u597d\u5947\u3002</p><p>\u3000\u3000\u201c\u7cbe\u5178\u5730\u4ea7\u7684\u7f8e\u4eba\u603b\u88c1\u6070\u597d\u8dcc\u5230\u6211\u7684\u6000\u91cc\uff0c\u8981\u771f\u662f\u5de7\u5408\u7684\u8bdd\uff0c\u201d\u5f20\u606a\u8bf4\u5230\u8fd9\u91cc\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u6487\u7740\u5634\u7b11\u8d77\u6765\uff0c\u6709\u7740\u51e0\u5206\u90aa\u6c14\uff0c\u4e5f\u4e1d\u6beb\u4e0d\u63a9\u9970\u5bf9\u6797\u96ea\u7684\u595a\u843d\uff0c\u201c\u6211\u5199\u4e03\u4e2a\u6570\u4f60\u5e2e\u6211\u4e70\u5f20\u4f53\u5f69\u6765\u2026\u2026\u201d\u6311\u8d77\u773c\u5e18\u53c8\u770b\u4e86\u6797\u96ea\u4e00\u773c\uff0c\u201c\u6797\u603b\u521a\u624d\u90a3\u5e45\u6a21\u6837\uff0c\u6211\u60f3\u4efb\u4f55\u7537\u4eba\u770b\u4e86\u90fd\u4f1a\u7830\u7136\u5fc3\u52a8\u7684\uff0c\u53ef\u60dc\u554a\uff0c\u6211\u8fd8\u662f\u7537\u5b69\u2026\u2026\u201d</p><p>\u3000\u3000\u7a81\u7136\u6709\u4e2a\u5973\u4eba\u8d70\u5230\u8ddf\u524d\u8dcc\u4e86\u4e00\u8de4\u6251\u5230\u5f20\u606a\u6000\u91cc\uff0c\u53f6\u5efa\u658c\u7ad9\u8d77\u6765\u8fd8\u60f3\u642d\u628a\u624b\u5c06\u4eba\u6276\u8d77\u6765\uff0c\u7b49\u5f20\u606a\u53eb\u7834\u8fd9\u5973\u4eba\u7684\u8eab\u4efd\uff0c\u4ed6\u624d\u610f\u8bc6\u4e0d\u8fc7\u662f\u573a\u81ea\u5bfc\u81ea\u6f14\u7684\u95f9\u5267\uff0c\u4ed6\u542c\u8bf4\u8fc7\u8fd9\u4e2a\u5973\u4eba\uff0c\u53ea\u662f\u6ca1\u6709\u6253\u8fc7\u4ea4\u9053\uff0c\u4ece\u8d44\u6599\u4e0a\u6216\u8005\u5a92\u4f53\u62a5\u9053\u4e0a\u770b\u8fc7\u8fd9\u4e2a\u5973\u4eba\u7684\u7167\u7247\uff0c\u4f46\u662f\u5370\u8c61\u4e0d\u6df1\u523b\uff0c\u60f3\u5fc5\u5f20\u606a\u5bf9\u8fd9\u4e2a\u5973\u4eba\u7684\u5370\u8c61\u4e5f\u53ea\u5e94\u8be5\u505c\u7559\u5728\u5a92\u4f53\u6216\u8005\u8d44\u6599\u6587\u732e\u4e0a\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u542c\u5230\u8fd9\u91cc\u90fd\u5fcd\u4e0d\u4f4f\u574f\u7b11\u51fa\u58f0\u97f3\u6765\uff0c\u4ed6\u4ece\u6765\u90fd\u6ca1\u6709\u542c\u5f20\u606a\u5bf9\u54ea\u4e2a\u5973\u4eba\u8bf4\u8bdd\u4f1a\u7528\u8fd9\u4e48\u5c16\u9178\u523b\u8584\u7684\u8bed\u6c14\uff0c\u5f20\u606a\u4f3c\u4e4e\u5bf9\u8fd9\u5973\u4eba\u7684\u6028\u6068\u5f88\u6df1\u554a\u3002</p><p>\u3000\u3000\u7ed9\u7aa5\u7834\u8eab\u4efd\uff0c\u867d\u7136\u6709\u4e9b\u5c34\u5c2c\uff0c\u6797\u96ea\u8fd8\u80fd\u626f\u4e24\u53e5\u8bdd\u642a\u585e\u8fc7\u53bb\uff0c\u4e07\u4e07\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u63a5\u4e0b\u6765\u7684\u8bdd\u6beb\u4e0d\u7559\u60c5\u9762\uff0c\u63ed\u5f97\u5979\u8840\u8089\u6dcb\u6f13\u3001\u72fc\u72c8\u4e0d\u582a\uff0c\u8336\u9910\u5385\u91cc\u6324\u6ee1\u4eba\uff0c\u867d\u7136\u542c\u4e0d\u61c2\u4ed6\u4eec\u7684\u5bf9\u8bdd\uff0c\u4e0d\u8fc7\u5934\u90fd\u8f6c\u8fc7\u6765\u770b\u7740\u8fd9\u8fb9\uff0c\u6797\u96ea\u767d\u7699\u7edd\u7f8e\u7684\u8138\u90fd\u6c14\u5f97\u53d8\u5f62\uff0c\u60f3\u8bf4\u4ec0\u4e48\uff0c\u770b\u5230\u5f20\u606a\u4e00\u4ed8\u671f\u5f85\u7684\u6a21\u6837\uff0c\u53ea\u5f97\u94f6\u7259\u54ac\u7740\u5ae3\u7ea2\u7684\u5634\u5507\u626d\u5934\u79bb\u5f00\u3002</p><p>\u3000\u3000\u9648\u9759\u8bf4\u9053\uff1a\u201c\u6797\u5c0f\u59d0\uff0c\u6211\u809a\u5b50\u771f\u662f\u997f\u4e86\uff0c\u5c31\u4e0d\u966a\u4f60\u53e6\u6362\u5730\u65b9\u4e86\u2026\u2026\u201d\u5979\u4e5f\u6025\u6b32\u6446\u8131\u8fd9\u4e2a\u5973\u4eba\u7684\u7ea0\u7f20\uff0c\u5f53\u7136\u66f4\u4e0d\u60f3\u53bb\u5b89\u6170\u5979\u7ed9\u5f20\u606a\u6233\u5f97\u8840\u8089\u6dcb\u6f13\u7684\u5fc3\u3002</p><p>\u3000\u3000\u201c\u55ef\uff01\u201d\u6797\u96ea\u5934\u6ca1\u6709\u56de\uff0c\u8fd8\u662f\u538b\u7740\u55d3\u5b50\u5e94\u4e86\u4e00\u58f0\u624d\u8d70\u51fa\u95e8\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u5085\u4fca\u5f80\u91cc\u632a\u4e00\u4e2a\u4f4d\u5b50\uff0c\u4ed6\u8ddf\u7740\u5f80\u91cc\u632a\u4e86\u4e00\u4e0b\uff0c\u8ba9\u9648\u9759\u5750\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8fc7\u6765\u5403\u996d\u7684\u5427\uff0c\u6211\u63a8\u8350\u8fd9\u91cc\u7684\u8304\u6c41\u6392\u9aa8\u76d6\u6d47\u996d\u7ed9\u4f60\u5c1d\u5c1d\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u542c\u7740\u5f20\u606a\u4e0e\u9648\u9759\u4e4b\u95f4\u670b\u53cb\u4e00\u822c\u7684\u4eb2\u5207\u8bed\u6c14\uff0c\u611f\u5230\u5f88\u5947\u602a\uff0c\u4ed6\u5c31\u77e5\u9053\u5f20\u606a\u4e0e\u9648\u9759\u7b2c\u4e00\u89c1\u9762\u65f6\u88c5\u54d1\u5df4\u620f\u5f04\u4eba\u5bb6\u7684\u6545\u4e8b\uff0c\u8fd8\u4e0d\u6e05\u695a\u4ed6\u4eec\u79c1\u4e0b\u91cc\u8fd8\u6709\u4ea4\u96c6\uff0c\u8fd9\u5c0f\u5b50\u603b\u662f\u8ba8\u5973\u4eba\u559c\u6b22\uff0c\u7fa1\u6155\u90fd\u7fa1\u6155\u4e0d\u6765\u3002\u542c\u89c1\u5f20\u606a\u53c8\u662f\u63a8\u8350\u8304\u6c41\u6392\u9aa8\u76d6\u6d47\u996d\u7ed9\u9648\u9759\uff0c\u53f6\u5efa\u658c\u82e6\u7b11\u8bf4\u9053\uff1a\u201c\u4f60\u4e0d\u8981\u542c\u4ed6\u7684\uff0c\u6211\u966a\u4ed6\u5df2\u7ecf\u8fde\u7eed\u4e94\u4e2a\u4e2d\u5348\u90fd\u5728\u8fd9\u91cc\u5403\u8304\u6c41\u6392\u9aa8\u76d6\u6d47\u996d\u4e86\uff0c\u4ed6\u53ea\u4f1a\u8d1f\u8d23\u63a8\u8350\u4e00\u6837\u83dc\uff0c\u6211\u6000\u7591\u4ed6\u662f\u4e0d\u662f\u770b\u4e0a\u90a3\u4e2a\u4e30\u8174\u72b9\u5b58\u7684\u53a8\u5a18\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u9759\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u6211\u4e5f\u5c1d\u5c1d\u2026\u2026\u201d\u62db\u624b\u8ba9\u670d\u52a1\u5458\u8fc7\u6765\uff0c\u8fd8\u771f\u56de\u5934\u53bb\u770b\u6a71\u7a97\u91cc\u90a3\u4e2a\u4e30\u8174\u72b9\u5b58\u7684\u53a8\u5a18\u3002</p><p>\u3000\u3000\u5f20\u606a\u95ee\u9648\u9759\uff1a\u201c\u4f60\u600e\u4e48\u8ddf\u8fd9\u4e2a\u5973\u4eba\u5728\u4e00\u8d77\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0a\u5348\u5728\u4e1c\u65b9\u5e7f\u573a\u9644\u8fd1\u7684\u5496\u5561\u5385\u559d\u65e9\u8336\u65f6\u78b0\u5230\u5979\uff0c\u5979\u597d\u50cf\u767e\u6cbe\u80f6\u4e00\u6837\uff0c\u6211\u8fd8\u6253\u7b97\u966a\u5979\u5403\u8fc7\u4e2d\u996d\u5c31\u56de\u6d77\u5dde\u2026\u2026\u201d\u4e0d\u8fc7\u4ed6\u6bd4\u8f83\u597d\u5947\u5f20\u606a\u4e3a\u4ec0\u4e48\u4f1a\u5bf9\u7cbe\u5178\u5730\u4ea7\u7684\u90a3\u4e2a\u5973\u4eba\u8fd9\u4e48\u5c16\u9510\uff0c\u201c\u4f60\u8ba4\u8bc6\u5979\uff1f\u201d</p><p>\u3000\u3000\u201c\u77e5\u9053\u4e00\u4e9b\uff0c\u201d\u5f20\u606a\u7b49\u7740\u9648\u9759\u5411\u670d\u52a1\u5458\u70b9\u8fc7\u83dc\uff0c\u624d\u8bf4\u9053\uff0c\u201c\u4e00\u4e2a\u624b\u6bb5\u3001\u5fc3\u80a0\u4e0d\u6bd4\u8d75\u9526\u8363\u5dee\u7684\u6f02\u4eae\u5973\u4eba\u3002\u4e0d\u540c\u662f\uff0c\u8d75\u9526\u8363\u73b0\u5728\u6ca1\u6709\u4e86\u4f9d\u4ed7\uff0c\u662f\u843d\u6c34\u7684\u6ca1\u7259\u72d7\uff0c\u7b97\u8ba1\u4ed6\u7684\u4eba\u591a\u7684\u662f\uff0c\u6211\u90fd\u61d2\u5f97\u6b3a\u8d1f\u4ed6\uff1b\u8fd9\u4e2a\u5973\u4eba\u554a\uff0c\u80cc\u540e\u4e00\u5b9a\u6709\u4f9d\u4ed7\uff0c\u53ea\u662f\u6ca1\u4eba\u77e5\u9053\u662f\u8c01\uff1f\u4f60\u77e5\u9053\u5979\u4e3a\u4ec0\u4e48\u8981\u6f14\u8fd9\u51fa\u620f\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6628\u5929\u521a\u542c\u8bf4\u5efa\u90ba\u5e02\u59d4\u6709\u610f\u5c06\u5b66\u5e9c\u5df7\u6539\u9020\u9879\u76ee\u5e76\u5165\u9526\u6e56\u7684\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u8fd9\u4ef6\u4e8b\u8fd8\u6ca1\u6709\u6700\u540e\u51b3\u5b9a\u5427\uff1f\u8fd8\u542c\u8bf4\u7cbe\u5178\u5730\u4ea7\u4e00\u76f4\u5bf9\u5b66\u5e9c\u5df7\u6539\u9020\u8ba1\u5212\u6709\u5174\u8da3\uff1f\u201d\u9648\u9759\u60f3\u8d77\u65e9\u4e0a\u5728\u5496\u5561\u5385\u9047\u5230\u90a3\u4e2a\u4e2d\u5e74\u7537\u4eba\u7684\u4fa7\u8138\uff0c\u53ea\u662f\u611f\u89c9\u6709\u4e9b\u719f\u6089\uff0c\u6ca1\u6709\u8ba4\u6e05\uff0c\u201c\u6628\u5929\u5979\u4e0e\u674e\u7701\u957f\u7684\u79d8\u4e66\u966a\u6211\u4eec\u4e00\u8d77\u5403\u996d\u6765\u7740\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u8fd9\u6837\u554a\u2026\u2026\u201d\u5f20\u606a\u5e76\u4e0d\u8ba4\u4e3a\u9ad8\u771f\u4f1a\u662f\u6797\u96ea\u80cc\u540e\u771f\u6b63\u7684\u4f9d\u4ed7\uff0c\u7cbe\u5178\u5730\u4ea7\u5728\u5efa\u90ba\u5d1b\u8d77\u6709\u51e0\u5e74\u4e86\uff0c\u9ad8\u771f\u968f\u674e\u8fdc\u6e56\u4ece\u60e0\u5c71\u8c03\u5230\u7701\u91cc\u624d\u4e09\u5e74\u7684\u65f6\u95f4\uff0c\u6307\u4e0d\u5b9a\u9ad8\u771f\u662f\u8fd9\u4e2a\u5973\u4eba\u7684\u76ee\u6807\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u6797\u96ea\u8fd9\u4e2a\u5973\u4eba\u5e76\u4e0d\u719f\u6089\uff0c\u5370\u8c61\u6700\u6df1\u523b\u7684\u83ab\u8fc7\u4e8e\u53d1\u751f\u5728\u4e5d\u516b\u5e74\u51ac\u5929\u7684\u5b66\u5e9c\u5df7\u5546\u94fa\u7eb5\u706b\u6848\u3002</p><p>\u3000\u3000\u5973\u4eba\u53d1\u8d77\u72e0\u6765\uff0c\u771f\u662f\u8981\u8ba9\u6240\u6709\u4eba\u90fd\u5fc3\u751f\u5bd2\u610f\u3002\u90a3\u65f6\u7684\u5b66\u5e9c\u5df7\u5df2\u7ecf\u8ba9\u6ecb\u4e8b\u751f\u975e\u7684\u793e\u4f1a\u9752\u5e74\u6405\u5f97\u4e4c\u70df\u7634\u6c14\uff0c1978\u4e5f\u88ab\u8feb\u7ed3\u4e1a\uff0c\u5f20\u606a\u51cc\u6668\u56de\u5b66\u6821\u65f6\u770b\u5230\u5b66\u5e9c\u5df7\u7130\u706b\u671d\u5929\uff0c\u8d76\u8fc7\u53bb\uff0c\u4e2d\u95f4\u7684\u51e0\u95f4\u94fa\u5b50\u90fd\u5df2\u7ecf\u9677\u5165\u706b\u6d77\u4e4b\u4e2d\uff0c\u5b66\u5e9c\u5df7\u7684\u6d88\u9632\u8bbe\u65bd\u843d\u540e\uff0c\u53ea\u80fd\u7b49\u6d88\u9632\u8f66\u8fc7\u6765\uff0c\u5f20\u606a\u7ad9\u5728\u4e00\u682a\u68a7\u6850\u6811\u4e0b\u89c2\u671b\uff0c\u4e00\u4e2a\u7a7f\u7740\u9ed1\u7ed2\u77ed\u98ce\u8863\u7684\u5973\u4eba\u6328\u7740\u4ed6\u7ad9\u7740\uff0c\u501f\u7740\u8fdc\u5904\u7684\u706b\u5149\uff0c\u5f20\u606a\u89c1\u7740\u5973\u957f\u6761\u987a\u76d8\u9753\uff0c\u638f\u51fa\u6253\u706b\u673a\u80cc\u98ce\u70b9\u70df\u60f3\u501f\u673a\u770b\u4e2a\u6e05\u695a\uff0c\u501f\u7740\u706b\u5149\u5374\u770b\u6e05\u90a3\u6f02\u4eae\u5973\u4eba\u773c\u775b\u51dd\u89c6\u706b\u707e\u73b0\u573a\u7684\u51b0\u5bd2\u773c\u795e\uff0c\u624b\u91cc\u6253\u4e00\u54c6\u55e6\u5dee\u70b9\u70e7\u7740\u81ea\u5df1\u7684\u624b\u6307\u5934\u3002\u6d88\u9632\u8f66\u8d76\u8fc7\u6765\u706d\u4e86\u706b\u4e4b\u540e\u624d\u77e5\u9053\u6b7b\u4e86\u4eba\uff0c\u540e\u6765\u4e8b\u60c5\u95f9\u4e86\u5f88\u5927\uff0c\u9644\u8fd1\u9ad8\u6821\u7684\u5b66\u751f\u4f17\u60c5\u4e49\u6124\u51fa\u6765\u95f9\u4e8b\uff0c\u6700\u7ec8\u7684\u7ed3\u679c\u4e5f\u53ea\u662f\u5b66\u5e9c\u5df7\u6539\u9020\u9879\u76ee\u4e5f\u4e0d\u4e86\u4e86\u4e4b\uff0c\u5e76\u6ca1\u6709\u4eba\u4e3a\u6b64\u7ad9\u51fa\u6765\u8d1f\u8d23\u3002</p><p>\u3000\u3000\u5f20\u606a\u90a3\u65f6\u5e76\u4e0d\u77e5\u9053\u90a3\u4e2a\u7ad9\u5728\u68a7\u6850\u6811\u4e0b\u4e00\u8d77\u966a\u81ea\u5df1\u89c2\u770b\u706b\u707e\u7684\u90a3\u4e2a\u6f02\u4eae\u5973\u4eba\u662f\u8c01\uff1f</p><p>\u3000\u3000\u8fd9\u4e00\u4e16\uff0c\u60f3\u8981\u6536\u96c6\u7cbe\u5178\u5730\u4ea7\u7684\u8d44\u6599\u5c31\u592a\u7b80\u5355\u4e86\uff0c\u770b\u5230\u6797\u96ea\u7684\u7167\u7247\u8d44\u6599\uff0c\u5f20\u606a\u77ac\u65f6\u5c31\u60f3\u5230\u5b66\u5e9c\u5df7\u90a3\u4e2a\u53d1\u751f\u706b\u707e\u7684\u51ac\u591c\uff0c\u5012\u662f\u7559\u610f\u7740\u6253\u542c\u8fd9\u4e2a\u5973\u4eba\u80cc\u540e\u7684\u5173\u7cfb\uff0c\u624d\u53d1\u73b0\u8fd9\u4e2a\u5973\u4eba\u80cc\u540e\u7684\u5173\u7cfb\u5f88\u6a21\u7cca\u3002</p><p>\u3000\u3000\u9526\u6e56\u8fd9\u65f6\u4e0d\u6a2a\u63d2\u4e00\u624b\u7684\u8bdd\uff0c\u5b66\u5e9c\u5df7\u6539\u9020\u9879\u76ee\u5927\u6982\u4f1a\u5faa\u7740\u65e2\u5b9a\u7684\u8f68\u8ff9\u843d\u5165\u8fd9\u4e2a\u5973\u4eba\u7684\u624b\u91cc\uff1b\u9526\u6e56\u8fd9\u65f6\u5019\u6a2a\u63d2\u4e00\u624b\uff0c\u5374\u662f\u8981\u5c06\u8fd9\u4e2a\u5973\u4eba\u5f97\u7f6a\u4e86\uff1b\u5f97\u7f6a\u5c31\u5f97\u7f6a\u5427\uff0c\u4e5f\u4e0d\u80fd\u5fcc\u60ee\u5f97\u7f6a\u8c01\uff0c\u6709\u4e9b\u4e8b\u60c5\u5c31\u4e0d\u505a\u4e86\u3002</p><p>\u3000\u3000\u6797\u96ea\u5dee\u8c34\u53f8\u673a\u5c06\u9648\u9759\u4e0a\u5348\u5728\u4e1c\u65b9\u5e7f\u573a\u8d2d\u4e70\u7684\u7269\u54c1\u9001\u4e0a\u6765\uff0c\u53ef\u89c1\u5979\u5e76\u6ca1\u6709\u7ed9\u5f20\u606a\u6c14\u5f97\u5931\u53bb\u7406\u667a\uff1b\u9648\u9759\u5c06\u8d2d\u7269\u888b\u653e\u5728\u5979\u4e0e\u5f20\u606a\u4e4b\u95f4\uff0c\u5f20\u606a\u77a5\u4e86\u4e24\u773c\uff0c\u4e00\u53ea\u888b\u5b50\u91cc\u5374\u662f\u7ea2\u8272\u857e\u4e1d\u7f15\u7a7a\u7684\u5185\u8863\u3002</p><p>\u3000\u3000\u521d\u6b21\u5728\u9ad8\u901f\u8def\u4e0a\u4e0e\u9648\u9759\u89c1\u9762\u65f6\uff0c\u9648\u9759\u5927\u6982\u521a\u521a\u56de\u5230\u56fd\u5185\uff0c\u7a7f\u7740\u88f9\u81c0\u76ae\u88d9\u3001\u7ec6\u7f51\u773c\u9ed1\u8272\u4e1d\u889c\u5c06\u4fee\u957f\u7f8e\u817f\u7ef7\u5f97\u8f6f\u5f39\u76c8\u6ea2\uff0c\u6027\u611f\u8273\u4e3d\u4e4b\u6781\uff0c\u4e4b\u540e\u518d\u89c1\u5979\u591a\u534a\u804c\u4e1a\u5973\u6027\u88c5\u626e\uff0c\u5012\u662f\u6ca1\u6709\u60f3\u5230\u5265\u53bb\u5979\u804c\u4e1a\u5973\u6027\u88c5\u626e\u4e4b\u540e\u7684\u5185\u5728\u8fd8\u662f\u4e00\u6837\u7684\u6027\u611f\u8273\u4e3d\u3002</p><p>\u3000\u3000\u9648\u9759\u6ce8\u610f\u5230\u5f20\u606a\u7684\u773c\u795e\u5728\u5979\u7684\u8d2d\u7269\u888b\u91cc\u4e71\u6e9c\uff0c\u4f38\u624b\u6293\u7740\u888b\u5b50\u53e3\u634f\u4e86\u4e00\u4e0b\uff0c\u4e0d\u8ba9\u4ed6\u770b\u91cc\u9762\u7684\u4e1c\u897f\uff0c\u60f3\u5230\u5f20\u606a\u521a\u624d\u8146\u7740\u8138\u8ddf\u6797\u96ea\u81ea\u79f0\u81ea\u5df1\u8fd8\u662f\u201c\u7537\u5b69\u201d\u4efb\u537f\u52fe\u5f15\u7684\u65e0\u8d56\u6a21\u6837\uff0c\u5fcd\u4e0d\u4f4f\u5c31\u8981\u7b11\u51fa\u58f0\u6765\uff0c\u5c06\u773c\u89d2\u7684\u4e00\u7f15\u67d4\u8f6f\u53d1\u4e1d\u634b\u5230\u5982\u7389\u67d4\u817b\u7684\u8033\u5eca\u4e4b\u540e\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u4f60\u8fd9\u4e9b\u5929\u4e00\u76f4\u5728\u5efa\u90ba\uff0c\u8bb8\u601d\u4e0d\u5728\u8fd9\u91cc\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u5979\u56de\u9999\u6e2f\u53bb\u4e86\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6211\u65e0\u6240\u4e8b\u4e8b\uff0c\u5c31\u5728\u8fd9\u91cc\u53ae\u6df7\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u5728\u5efa\u90ba\u505a\u5bfc\u6f14\u5427\uff1f\u201d\u9648\u9759\u671d\u8336\u9910\u5385\u4e00\u89d2\u60ac\u5728\u5929\u82b1\u677f\u4e0b\u7684\u7535\u89c6\u8bf4\u9053\uff0c\u7535\u89c6\u91cc\u6b63\u64ad\u653e\u7231\u8fbe\u96c6\u56e2\u603b\u88c1\u9648\u4fe1\u751f\u4e0e\u5206\u7ba1\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u53d1\u5c55\u7684\u5e38\u59d4\u526f\u5e02\u957f\u738b\u7ef4\u5747\u4e0a\u5348\u7b7e\u7f72\u6a61\u6811\u56ed\u5f00\u53d1\u8ba1\u5212\u610f\u5411\u4e66\u7684\u5348\u95f4\u65b0\u95fb\uff0c\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u3001\u526f\u4e66\u8bb0\u8096\u660e\u5efa\u51fa\u5e2d\u4e86\u7b7e\u5b57\u4eea\u5f0f\uff0c\u5efa\u4e1a\u5e02\u59d4\u5e02\u653f\u5e9c\u8fd8\u5ba3\u4f20\u6210\u7acb\u4e86\u4ee5\u5e02\u59d4\u4e66\u7f57\u541b\u3001\u5e02\u957f\u8096\u660e\u5efa\u4e3a\u9996\u7684\u6a61\u6811\u56ed\u8ba1\u5212\u534f\u8c03\u9886\u5bfc\u5c0f\u7ec4\uff0c\u5e38\u59d4\u526f\u5e02\u957f\u738b\u7ef4\u5747\u662f\u5e38\u52a1\u526f\u7ec4\u957f\uff0c\u526f\u5e02\u957f\u9ec4\u514b\u541b\u4ee5\u53ca\u9ad8\u65b0\u533a\u6295\u8d44\u4fc3\u8fdb\u5c40\u526f\u5c40\u957f\u59da\u6587\u76db\u90fd\u662f\u534f\u8c03\u9886\u5bfc\u5c0f\u7ec4\u7684\u6210\u5458\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u4e5f\u5bf9\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4e0d\u7136\u6211\u80fd\u4ec0\u4e48\u5462\uff1f\u6211\u53ef\u4e0d\u559c\u6b22\u505a\u6f14\u5458\u3002\u201d</p><p>\u3000\u3000\u9648\u9759\u62bf\u5634\u7b11\u4e86\u7b11\uff0c\u9526\u6e56\u5728\u4e1c\u6d77\u7701\u7684\u5730\u4f4d\u5df2\u7ecf\u8d85\u8fc7\u6b63\u6cf0\u96c6\u56e2\uff0c\u8c01\u80fd\u60f3\u5230\u4eca\u5929\u7684\u9526\u6e56\u4f1a\u662f\u773c\u524d\u8fd9\u4e2a\u7a7f\u7740\u9ed1\u8272\u4f11\u95f2\u8863\u4e00\u4ed8\u5b66\u751f\u6a21\u6837\u3001\u8138\u4e0a\u8fd8\u6302\u7740\u61d2\u6563\u7b11\u5bb9\u7684\u9752\u5e74\u4e00\u624b\u521b\u7acb\u3002\u53c8\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u4f60\u4f1a\u4e0d\u4f1a\u5728\u56fd\u5185\u4e0a\u5927\u5b66\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f1a\u554a\uff0c\u4e0d\u7136\u6211\u80fd\u5e72\u5565\uff1f\u201d\u5f20\u606a\u63e1\u62f3\u59c6\u6307\u7fd8\u7740\u6307\u4e86\u6307\u4e1c\u5927\u7684\u65b9\u5411\uff0c\u201c\u4f1a\u4e0a\u4e1c\u5927\u5427\uff0c\u8fdb\u5e08\u5927\u3001\u6216\u8005\u8fdb\u97f3\u4e50\u5b66\u9662\u610f\u56fe\u592a\u660e\u663e\u4e86\uff0c\u4e0d\u5408\u9002\u3002\u201d</p><p>\u3000\u3000\u9648\u9759\u6251\u54e7\u7b11\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u5b50\u5609\u73b0\u5728\u4e5f\u536f\u8db3\u4e86\u52b2\u51c6\u5907\u8003\u4e1c\u5927\uff0c\u5979\u73b0\u5728\u8fd8\u4e0d\u6653\u5f97\u4f60\u5c31\u662f\u90a3\u4e2a\u7ed9\u5979\u5728\u9ad8\u901f\u516c\u8def\u4e0a\u6b3a\u8d1f\u7684\u54d1\u5df4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5443\u2026\u2026\u201d\u5f20\u606a\u5fc3\u865a\u7684\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u5bf9\u4e8e\u4e2a\u6027\u7279\u8d28\u9c9c\u660e\u7684\u5973\u5b69\u5b50\uff0c\u4ed6\u5df2\u7ecf\u4ece\u5b59\u9759\u6aac\u8eab\u4e0a\u9886\u6559\u8fc7\u82e6\u5934\u4e86\uff0c\u201c\u8fd9\u4e2a\uff0c\u8c22\u5b50\u5609\u6253\u7b97\u8003\u4ec0\u4e48\u4e13\u4e1a\uff0c\u8d22\u7ecf\uff1f\u6cd5\u5b66\uff1f\u5b9e\u5728\u4e0d\u884c\uff0c\u6211\u8fd8\u662f\u8fdb\u5e08\u5927\u5f97\u7684\u2026\u2026\u201d</p></article>"}}}]);
