(window.webpackJsonp=window.webpackJsonp||[]).push([[568],{591:function(p){p.exports={chapterTitle:null,title:"\u7b2c568\u7ae0 \u85cf\u8eab\u5973\u5395",content:"<article><h2>\u7b2c568\u7ae0 \u85cf\u8eab\u5973\u5395</h2><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u90a3\u5c0f\u5b50\u7ed9\u5b59\u9759\u6aac\u72e0\u72e0\u8e39\u4e86\u4e00\u811a\u6574\u4e2a\u4eba\u5931\u53bb\u91cd\u5fc3\uff0c\u8138\u671d\u4e0b\u72e0\u72e0\u683d\u5230\u6728\u5730\u677f\u4e0a\uff0c\u5fc3\u5934\u90fd\u66ff\u8fd9\u5c0f\u5b50\u62bd\u6410\u4e86\u4e00\u4e0b\uff0c\u8fd9\u90fd\u662f\u53d1\u751f\u5728\u7728\u773c\u95f4\u7684\u4e8b\u60c5\uff0c\u7ad9\u5728\u6276\u624b\u90a3\u91cc\u7684\u51e0\u4e2a\u4f53\u6821\u751f\u624d\u53d1\u73b0\u51fa\u4e86\u72b6\u51b5\u3002</p><p>\u3000\u3000\u770b\u7740\u5b59\u9759\u6aac\u8fd8\u8981\u8d70\u8fc7\u53bb\u671d\u90a3\u4e2a\u5012\u5730\u7684\u5c0f\u5b50\u8e39\u51e0\u811a\uff0c\u5f20\u606a\u5fc3\u91cc\u53eb\u4e86\u58f0\u59d1\u5976\u5976\uff0c\u5c06\u56db\u53ea\u6e9c\u51b0\u978b\u5c06\u6ed1\u8fc7\u6765\u7684\u90a3\u51e0\u4e2a\u4f53\u6821\u751f\u6254\u8fc7\u53bb\u3002\u770b\u7740\u90a3\u51e0\u4e2a\u5c0f\u5b50\u4e00\u8d77\u5931\u53bb\u5e73\u8861\u8dcc\u5012\uff0c\u5f20\u606a\u62c9\u7740\u5b59\u9759\u6aac\u5c31\u5f80\u8dd1\u3002</p><p>\u3000\u3000\u201c\u56de\u5b66\u6821\uff0c\u56de\u5230\u5b66\u6821\u5c31\u4e0d\u6015\u4e86\u3002\u201d\u5b59\u9759\u6aac\u5174\u594b\u5f97\u5927\u53eb\uff0c\u521a\u624d\u5728\u5b66\u5e9c\u5df7\u90fd\u6ca1\u89c1\u5979\u8dd1\u8fd9\u4e48\u5feb\u3002\u5f20\u606a\u90fd\u6ca1\u65f6\u95f4\u95ee\u5979\u4e3a\u4ec0\u4e48\u8981\u5728\u6e9c\u51b0\u573a\u7a81\u7136\u53d1\u98da\u3002</p><p>\u3000\u3000\u4ed3\u4fc3\u95f4\u642c\u6551\u5175\u90fd\u6765\u4e0d\u53ca\uff0c\u90a3\u51e0\u4e2a\u4f53\u6821\u6362\u978b\u4e5f\u5c31\u51e0\u5341\u79d2\u7684\u5de5\u592b\uff0c\u4ece\u6e9c\u51b0\u573a\u8dd1\u56de\u97f3\u4e50\u5b66\u9662\u5c11\u8bf4\u4e5f\u8981\u4e09\u4e94\u5206\u949f\uff0c\u90a3\u51e0\u4e2a\u755c\u751f\u90fd\u662f\u73a9\u4f53\u80b2\u7684\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u4ed6\u4eec\u8fdb\u97f3\u4e50\u5b66\u9662\u5927\u95e8\u4e4b\u524d\u8ffd\u4e0a\u6765\uff1b\u5c31\u7b97\u80fd\u53ca\u65f6\u6e9c\u8fdb\u97f3\u4e50\u5b66\u9662\uff0c\u90fd\u672a\u5fc5\u9003\u5f97\u4e86\u7ed9\u66b4\u6253\u4e00\u987f\uff0c\u8fd9\u5e2e\u4f53\u6821\u751f\u7684\u4f5c\u98ce\u5f88\u86ee\u6a2a\uff0c\u5e73\u65f6\u53ea\u6709\u4ed6\u4eec\u60f9\u522b\u4eba\uff0c\u54ea\u6709\u522b\u4eba\u60f9\u4ed6\u4eec\u7684\uff1f</p><p>\u3000\u3000\u8bf4\u5b9e\u8bdd\uff0c\u5f20\u606a\u5f88\u4e0d\u559c\u6b22\u7ed9\u522b\u4eba\u66b4\u6253\u7684\u611f\u89c9\uff0c\u6e9c\u51b0\u573a\u662f\u5728\u4e8c\u697c\uff0c\u62c9\u7740\u5b59\u9759\u6aac\u51e0\u4e4e\u8df3\u4e0b\u697c\u68af\uff0c\u5b59\u9759\u6aac\u536f\u8db3\u52b2\u8981\u5f80\u8857\u4e0a\u6e9c\uff0c\u5f20\u606a\u5c06\u5979\u4e00\u628a\u62fd\u4f4f\uff0c\u5f80\u697c\u68af\u91cc\u9762\u8d70\u4e86\u51e0\u6b65\uff0c\u6709\u4e00\u95f4\u5395\u6240\u6709\u697c\u68af\u80cc\u540e\uff0c\u5bf9\u8fd9\u91cc\u4e0d\u719f\u6089\u7684\u4eba\u90fd\u627e\u4e0d\u5230\u8fd9\u95f4\u5395\u6240\u3002</p><p>\u3000\u3000\u5395\u6240\u91cc\u6ca1\u6709\u4eba\uff0c\u5f20\u606a\u4e0e\u5b59\u9759\u6aac\u8eb2\u8fdb\u5973\u5395\u7684\u5c0f\u9694\u95f4\u91cc\uff0c\u9694\u95f4\u72ed\u5c0f\uff0c\u8fd8\u6709\u4e00\u53ea\u62bd\u6c34\u9a6c\u6876\uff0c\u4e24\u4eba\u5c31\u53ea\u80fd\u7d27\u7d27\u6328\u8d77\u4e00\u8d77\u5750\u7740\uff0c\u542c\u7740\u5916\u9762\u697c\u68af\u549a\u549a\u7684\u54cd\u58f0\u968f\u5373\u4f20\u6765\u2014\u2014\u8fd9\u5e2e\u755c\u751f\u6362\u978b\u7684\u901f\u5ea6\u591f\u5feb\u7684\u2014\u2014\u7d27\u5f20\u523a\u6fc0\uff0c\u90fd\u610f\u8bc6\u4e0d\u5230\u4e24\u4eba\u8d34\u7740\u8eab\u5b50\u6328\u8fd9\u4e48\u8fd1\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u4f38\u8fdb\u5b59\u9759\u725b\u4ed4\u88e4\u540e\u9762\u7684\u515c\u91cc\uff1b\u5b59\u9759\u6aac\u77aa\u5706\u773c\u775b\uff0c\u4e0d\u6562\u76f8\u4fe1\u5f20\u606a\u8fd9\u65f6\u5019\u7adf\u7136\u6562\u624b\u4f38\u8fc7\u6765\u6478\u5979\u7684\u5c41\u80a1\uff0c\u5979\u4e5f\u4e0d\u5931\u58f0\u5c16\u53eb\uff0c\u53ea\u662f\u62ac\u811a\u8e29\u7740\u5f20\u606a\u7684\u978b\u5c16\u637b\u4e86\u637b\uff0c\u6076\u72e0\u72e0\u7684\u76ef\u7740\u4ed6\u3002</p><p>\u3000\u3000\u5f20\u606a\u5403\u75db\u5438\u7740\u51c9\u6c14\uff0c\u5c06\u5b59\u9759\u6aac\u7684\u624b\u673a\u4ece\u5979\u7684\u88e4\u515c\u91cc\u638f\u51fa\u6765\u76f4\u63a5\u5c06\u7535\u6c60\u6252\u62c9\u4e0b\u6765\uff0c\u4ed6\u7684\u624b\u673a\u90fd\u8c03\u6210\u9707\u52a8\u72b6\u6001\uff0c\u8981\u662f\u8fd9\u65f6\u5019\u6709\u4eba\u6253\u5b59\u9759\u6aac\u7684\u624b\u673a\uff0c\u54ed\u90fd\u6765\u4e0d\u53ca\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u77e5\u9053\u8bef\u4f1a\u5f20\u606a\u4e86\uff0c\u62ac\u8d77\u811a\u632a\u5230\u4e00\u8fb9\uff0c\u773c\u795e\u6311\u89c6\u7740\u5f20\u606a\uff0c\u90a3\u610f\u601d\u662f\u8bf4\u8001\u5a18\u7684\u5c41\u80a1\u4e5f\u8ba9\u4f60\u6478\u4e86\uff0c\u6ca1\u6709\u8981\u9053\u6b49\u7684\u610f\u601d\u3002\u5f20\u606a\u5403\u7740\u75bc\uff0c\u811a\u63d0\u8d77\u6765\u53c8\u63c9\u4e86\u63c9\uff0c\u4e5f\u6ca1\u6cd5\u8ddf\u5979\u5728\u8fd9\u91cc\u8ba1\u8f83\u3002</p><p>\u3000\u3000\u542c\u7740\u5916\u9762\u7684\u811a\u6b65\u6e10\u8fdc\uff0c\u5b59\u9759\u6aac\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u6b63\u5f85\u8981\u51fa\u53bb\uff0c\u53c8\u8ba9\u5f20\u606a\u4e00\u628a\u6293\u4f4f\uff0c\u771f\u8981\u8bf4\u4ec0\u4e48\uff0c\u5f20\u606a\u7684\u624b\u8d34\u7740\u5979\u7684\u5634\uff0c\u773c\u775b\u77aa\u4e86\u77aa\uff0c\u5236\u6b62\u5979\u8bf4\u8bdd\uff0c\u5b59\u9759\u6aac\u8fd9\u624d\u542c\u89c1\u697c\u68af\u8fd8\u6709\u4eba\u5728\u8d70\u52a8\uff0c\u63a5\u7740\u5c31\u542c\u5230\u9a82\u9a82\u51bd\u51bd\u7684\u58f0\u97f3\uff0c\u662f\u90a3\u4e2a\u7ed9\u5b59\u9759\u6aac\u8e22\u4e2d\u7684\u4f53\u6821\u751f\u7ed9\u4eba\u6276\u6b63\u4e00\u6b65\u4e00\u6b65\u7684\u8d70\u4e0b\u697c\u68af\uff0c\u90fd\u80fd\u542c\u5f97\u89c1\u4ed6\u4e0b\u697c\u68af\u7684\u811a\u6b65\u4e00\u53ea\u8f7b\u4e00\u53ea\u91cd\uff0c\u4e00\u8fb9\u9a82\u8fd8\u4e00\u8fb9\u5438\u51c9\u6c14\uff0c\u4e0d\u6653\u5f97\u5b59\u9759\u6aac\u8ddf\u8fd9\u5c0f\u5b50\u4f55\u6028\u4f55\u4ec7\uff0c\u8e22\u8fd9\u4e48\u72e0\u3002</p><p>\u3000\u3000\u90a3\u4e24\u4eba\u7ad9\u5728\u697c\u68af\u53e3\u5c31\u4e0d\u518d\u8d70\u4e86\uff0c\u4e5f\u662f\u90a3\u4e2a\u7ed9\u5b59\u9759\u6aac\u8e22\u4e2d\u7684\u4f53\u6821\u751f\u5c0f\u817f\u53d7\u4f24\u4e0d\u8f7b\uff0c\u5c31\u5750\u5728\u697c\u68af\u53f0\u9636\u4e0a\u7b49\u7740\u3002</p><p>\u3000\u3000\u5b59\u9759\u8033\u6735\u8d34\u7740\u9694\u95f4\u677f\u542c\u5916\u9762\u7684\u52a8\u9759\uff0c\u72ed\u5c0f\u7684\u7a7a\u95f4\u91cc\uff0c\u5c41\u80a1\u5fae\u5fae\u6485\u7740\uff0c\u7ed9\u725b\u4ed4\u88e4\u7ef7\u5f97\u7ed3\u5b9e\u5c0f\u5de7\uff0c\u5341\u5206\u7684\u6027\u611f\uff0c\u95fb\u7740\u5b59\u9759\u6aac\u8eab\u4e0a\u4f20\u6765\u6de1\u6de1\u7684\u9999\u5473\uff0c\u8981\u4e0d\u662f\u5916\u9762\u6709\u4e24\u4e2a\u5927\u715e\u98ce\u666f\u7684\u4eba\uff0c\u8fd8\u771f\u662f\u597d\u4eab\u53d7\u554a\u3002</p><p>\u3000\u3000\u5916\u9762\u6709\u624b\u673a\u94c3\u58f0\u54cd\u8d77\uff0c\u542c\u7740\u5916\u9762\u4eba\u8bb2\u7535\u8bdd\u7684\u5185\u5bb9\uff0c\u524d\u9762\u51e0\u4e2a\u4f53\u6821\u751f\u5df2\u7ecf\u5728\u9a6c\u83b2\u8857\u4e0a\u8dd1\u4e86\u4e00\u5708\u90fd\u6ca1\u6709\u770b\u89c1\u4eba\uff0c\u4e0d\u8fc7\u6709\u4eba\u8ba4\u51fa\u5b59\u9759\u6aac\u662f\u97f3\u4e50\u5b66\u9662\u7684\u5b66\u751f\u3002\u90a3\u4e2a\u7ed9\u8e22\u4e2d\u7684\u4f53\u6821\u751f\u51b2\u7740\u7535\u8bdd\u5927\u53eb\u5927\u56b7\uff0c\u60f3\u5fc5\u7ed9\u6c14\u75af\u4e86\uff0c\u8ba9\u524d\u9762\u7684\u4eba\u8fdb\u97f3\u4e50\u5b66\u9662\u627e\u4eba\uff0c\u4ed6\u8981\u627e\u7535\u8bdd\u518d\u53eb\u4e00\u6279\u4eba\u8fc7\u53bb\u5019\u7740\uff0c\u4eca\u5929\u4e00\u5b9a\u8981\u627e\u56de\u573a\u5b50\u3002</p><p>\u3000\u3000\u8fd9\u51e0\u4e2a\u4f53\u6821\u751f\u4e5d\u4e03\u5e74\u5728\u5b66\u6821\u5c31\u6709\u624b\u673a\uff0c\u770b\u6765\u5bb6\u5883\u90fd\u4e0d\u9519\uff0c\u4e8b\u60c5\u95f9\u5927\u4e86\uff0c\u53cd\u800c\u597d\u5904\u7406\uff0c\u73b0\u5728\u5916\u9762\u7ed9\u4eba\u5835\u7740\uff0c\u5f20\u606a\u5012\u4e5f\u4e0d\u6025\uff0c\u5b59\u9759\u6aac\u8f6c\u8fc7\u8eab\uff0c\u8d34\u7740\u5f20\u606a\u7684\u80f8\u53e3\u7ad9\u7740\uff0c\u4e24\u4eba\u6328\u8fd9\u4e48\u8fd1\uff0c\u80fd\u542c\u5230\u5979\u7684\u80f8\u53e3\u7830\u7830\u76f4\u8df3\uff0c\u4e0d\u8fc7\u8fd9\u59ae\u5b50\u773c\u775b\u91cc\u7adf\u662f\u5174\u594b\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e0\u5948\u7684\u6447\u4e86\u6447\u5934\uff0c\u770b\u7740\u5b59\u9759\u6aac\u6a31\u6843\u5c0f\u5634\u4e00\u5f20\u4e00\u5408\u60f3\u7528\u5634\u5507\u8ddf\u81ea\u5df1\u8bf4\u8bdd\uff0c\u5f20\u606a\u82e6\u7b11\u7684\u6447\u4e86\u6447\u5934\uff0c\u81ea\u5df1\u53c8\u8bfb\u4e0d\u61c2\u5507\u8bed\uff0c\u54ea\u91cc\u77e5\u9053\u5979\u5728\u8bf4\u4ec0\u4e48\uff1f</p><p>\u3000\u3000\u5b59\u9759\u6aac\u4f38\u624b\u4e00\u6839\u624b\u6307\u5728\u5f20\u606a\u7684\u80f8\u53e3\u5199\u7740\u5b57\uff0c\u5f20\u606a\u8ba9\u5979\u7684\u624b\u6307\u6320\u5f97\u75d2\u75d2\u7684\uff0c\u6839\u672c\u5c31\u96c6\u4e2d\u4e0d\u4e86\u7cbe\u529b\u6ce8\u610f\u5979\u5728\u5199\u4ec0\u4e48\u3002\u5979\u7684\u8eab\u9ad8\u6b63\u597d\u8ba9\u5979\u547c\u51fa\u7684\u9f3b\u606f\u6251\u5728\u81ea\u5df1\u7684\u4e0b\u5df4\u4e0a\uff0c\u5f20\u606a\u5fae\u5fae\u8f6c\u8fc7\u5934\uff0c\u644a\u5f00\u624b\u638c\uff0c\u6293\u8fc7\u5979\u7684\u90a3\u6839\u5ae9\u82e5\u9752\u8471\u7684\u624b\u6307\uff0c\u8ba9\u5979\u5728\u81ea\u5df1\u7684\u624b\u638c\u4e0a\u5199\u5b57\u2014\u2014\u5728\u80f8\u53e3\u5199\uff0c\u90fd\u6000\u7591\u5979\u662f\u5728\u6311\u9017\u81ea\u5df1\uff0c\u534a\u7247\u8eab\u5b50\u90fd\u5dee\u70b9\u9ebb\u9165\u4e86\uff0c\u96be\u9053\u81ea\u5df1\u8fd8\u80fd\u5728\u5979\u7684\u80f8\u53e3\u5199\u56de\u53bb\uff1f</p><p>\u3000\u3000\u8fd9\u4e48\u60f3\u7740\uff0c\u5f20\u606a\u4f4e\u5934\u770b\u4e86\u770b\u5b59\u9759\u6aac\u9ad8\u8038\u7684\u80f8\uff0c\u4f5c\u4e3a\u4e00\u4e2a\u4e8c\u5341\u4e00\u5c81\u7684\u5973\u5b69\u5b50\uff0c\u6709\u8fd9\u4e48\u4e30\u633a\u7684\u80f8\u8fd8\u771f\u662f\u4e86\u4e0d\u5f97\uff0c\u53ef\u60dc\u7ed9\u7ea2\u8272\u7f69\u8863\u906e\u7740\uff0c\u53ea\u770b\u5f97\u89c1\u7ec6\u817b\u5982\u7389\u3001\u4fee\u957f\u7684\u9888\u8116\u5b50\u3001\u5706\u6da6\u5fae\u5c16\u7684\u4e0b\u9894\u3001\u79c0\u76f4\u7684\u9f3b\u6881\u3001\u9ad8\u9ad8\u6311\u8d77\u7684\u776b\u6bdb\u2014\u2014\u5443\uff0c\u53c8\u5206\u5fc3\u6ca1\u6709\u6ce8\u610f\u5979\u5728\u81ea\u5df1\u7684\u624b\u5fc3\u5199\u4ec0\u4e48\u4e1c\u897f\u3002</p><p>\u3000\u3000\u5f20\u606a\u793a\u610f\u5b59\u9759\u6aac\u518d\u5199\u4e00\u904d\uff1b\u5b59\u9759\u6aac\u77aa\u773c\u770b\u7740\u5f20\u606a\uff0c\u90a3\u610f\u601d\u662f\u4f60\u518d\u770b\u5c31\u5c06\u4f60\u773c\u775b\u6316\u6389\u3002</p><p>\u3000\u3000\u5f20\u606a\u773c\u775b\u770b\u5230\u522b\u5904\uff0c\u63e1\u7740\u5b59\u9759\u6aac\u7ef5\u6ed1\u67d4\u8f6f\u7684\u5c0f\u624b\uff0c\u8ba9\u5979\u7684\u624b\u5fc3\u644a\u5f00\uff0c\u5979\u7684\u624b\u5fc3\u7279\u522b\u7684\u767d\uff0c\u4eff\u4f5b\u4e00\u5757\u7f8a\u8102\u767d\u7389\uff0c\u63e1\u5728\u624b\u91cc\u51b0\u51b0\u7684\uff0c\u638c\u7eb9\u6e05\u6670\uff0c\u5374\u6709\u4e9b\u6f6e\u6da6\uff0c\u591a\u5c11\u6709\u4e9b\u7d27\u5f20\u7684\u7f18\u6545\uff0c\u5f20\u606a\u5728\u5979\u624b\u5fc3\u91cc\u5199\u5b57\u95ee\u5979\u4e3a\u4ec0\u4e48\u8981\u5bf9\u90a3\u5c0f\u5b50\u4e0b\u8fd9\u4e48\u91cd\u7684\u811a\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u624b\u5fc3\u4e5f\u5403\u4e0d\u75d2\uff0c\u5f20\u606a\u51e0\u4e2a\u5b57\u5199\u5f97\u5979\u7709\u773c\u4e71\u8df3\uff0c\u6251\u51fa\u6765\u7684\u9f3b\u606f\u90fd\u6709\u4e9b\u4e71\u4e86\uff0c\u4f38\u56de\u624b\uff0c\u5728\u5f20\u606a\u624b\u5fc3\u91cc\u5199\u9053\uff1a\u201c\u8131\u8eab\u518d\u8bf4\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u53c8\u5c06\u5979\u5fae\u51c9\u5982\u7389\u7684\u5c0f\u624b\u644a\u5230\u81ea\u5df1\u7684\u624b\u5fc3\u5199\u9053\uff1a\u201c\u8981\u662f\u6211\u7ed9\u62d6\u51fa\u53bb\u66b4\u6253\u4e86\u4e00\u987f\uff0c\u5c82\u4e0d\u662f\u7ed9\u6253\u5f97\u4e0d\u660e\u4e0d\u767d\u7684\uff1f\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u81ea\u5df1\u5de6\u624b\u6293\u7740\u53f3\u624b\u638c\uff0c\u5fcd\u7740\u75d2\u4e0d\u4ece\u5f20\u606a\u7684\u624b\u5fc3\u62bd\u56de\u6765\uff0c\u7b49\u5f20\u606a\u5c06\u4e00\u6bb5\u8bdd\u5199\u5b8c\uff0c\u5979\u9ed1\u767d\u5206\u660e\u7684\u7f8e\u7738\u6a2a\u76fc\uff0c\u8eab\u5b50\u5fcd\u7740\u75d2\u6b20\u7740\uff0c\u4e0a\u534a\u8eab\u51e0\u4e4e\u8981\u8d34\u8fdb\u5f20\u606a\u7684\u6000\u91cc\u6765\uff0c\u5f20\u606a\u8fd8\u8981\u5199\u5b57\uff0c\u5979\u63e1\u7740\u62f3\u5934\u5728\u5f20\u606a\u9762\u524d\u626c\u4e86\u626c\uff0c\u773c\u795e\u575a\u5b9a\uff0c\u518d\u4e5f\u4e0d\u8ba9\u5f20\u606a\u5728\u5979\u7684\u624b\u5fc3\u5199\u5b57\uff0c\u4e5f\u4e0d\u518d\u5f20\u606a\u7684\u624b\u5fc3\u5199\u5b57\u3002</p><p>\u3000\u3000\u8fd9\u671f\u95f4\u6709\u4e24\u4e2a\u5973\u4eba\u8fdb\u5395\u6240\u89e3\u624b\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u6e05\u8106\uff0c\u5e94\u8be5\u662f\u5f88\u5e74\u8f7b\u7684\u5973\u5b69\u5b50\uff0c\u5c31\u5728\u9694\u58c1\u7684\u9694\u95f4\u3002\u542c\u7740\u5c3f\u67f1\u6025\u4fc3\u7684\u6253\u5728\u62bd\u6c34\u9a6c\u6876\u91cc\u7684\u58f0\u97f3\uff0c\u5f20\u606a\u5fc3\u60f3\u9694\u58c1\u7684\u5c11\u5973\u4f1a\u957f\u4ec0\u4e48\u6a21\u6837\uff0c\u542c\u8fd9\u5c3f\u67f1\u6253\u6c34\u7684\u58f0\u97f3\u5012\u4e0d\u662f\u9519\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u7a81\u7136\u4f38\u51fa\u4e24\u53ea\u5c0f\u624b\u6342\u7d27\u5f20\u606a\u7684\u8033\u6735\uff0c\u77aa\u773c\u770b\u7740\u4ed6\uff0c\u4e0d\u8ba9\u4ed6\u542c\u9694\u58c1\u7684\u5c3f\u58f0\u3002</p><p>\u3000\u3000\u5f20\u606a\u773c\u775b\u77aa\u56de\u53bb\uff1a\u8fd9\u4f60\u90fd\u8981\u7ba1\uff0c\u53c8\u4e0d\u662f\u542c\u4f60\u7684\u6492\u5c3f\uff1f\u60f3\u8d77\u4e00\u4e2a\u7b11\u8bdd\uff0c\u4f38\u51fa\u624b\u6307\u5728\u5b59\u9759\u6aac\u7684\u80cc\u80db\u4e0a\u5199\u5b57\uff1a\u201c\u8fd9\u624d\u7b97\u662f\u771f\u6b63\u7684\u542c\u96e8\u9601\uff01\u201d\u8fd9\u59ff\u52bf\u8ddf\u7740\u4e24\u4eba\u5728\u5395\u6240\u95f4\u91cc\u6402\u62b1\u8d77\u4f3c\u7684\uff0c\u5b59\u9759\u6aac\u773c\u775b\u91cc\u9732\u51fa\u5a07\u55d4\u7684\u8584\u6012\uff0c\u4e0d\u6653\u5f97\u662f\u56e0\u4e3a\u4e24\u4eba\u66a7\u6627\u7684\u59ff\u52bf\u8fd8\u662f\u56e0\u4e3a\u5f20\u606a\u5728\u5979\u80cc\u4e0a\u4e71\u5199\u5b57\u3002\u5f20\u606a\u8033\u6735\u7ed9\u5b59\u9759\u6aac\u7d27\u6342\u7740\uff0c\u53c8\u5728\u5979\u7ea4\u67d4\u65e0\u9aa8\u7684\u80cc\u80db\u4e0a\u5199\u9053\uff1a\u201c\u77e5\u4e0d\u77e5\u9053\u4ec0\u4e48\u53eb\u89c2\u96e8\u4ead\uff1f\u201d\u5b59\u9759\u6aac\u53ea\u89c9\u5f97\u80cc\u80db\u75d2\u75d5\u96be\u5fcd\uff0c\u63a7\u5236\u4e0d\u4f4f\u7684\u80cc\u80a9\u8038\u52a8\u7740\u60f3\u8981\u6b62\u4e00\u4e0b\u75d2\uff0c\u5374\u8ba9\u4e30\u633a\u7684\u80f8\u5728\u5f20\u606a\u7684\u6000\u91cc\u8e6d\u4e86\u4e24\u4e0b\u3002\u4e5f\u5c31\u8e6d\u4e86\u4e24\u4e0b\uff0c\u5b59\u9759\u6aac\u5c31\u89e6\u7535\u4f3c\u7684\u677e\u5f00\u6342\u7d27\u5f20\u606a\u8033\u6735\u7684\u624b\uff0c\u62b1\u7d27\u81ea\u5df1\u7684\u80f8\uff0c\u77aa\u7740\u773c\u775b\u4f3c\u8981\u5c06\u5f20\u606a\u6574\u4e2a\u4eba\u751f\u541e\u6d3b\u5265\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u4eab\u53d7\u7740\u534a\u7247\u8eab\u5b50\u7684\u9165\u9ebb\uff0c\u5bf9\u5b59\u9759\u6aac\u7684\u6012\u76ee\u89c6\u800c\u4e0d\u89c1\u3002\u8fc7\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u542c\u7740\u51cc\u4e71\u7684\u811a\u6b65\u58f0\u4f20\u8fc7\u6765\uff0c\u539f\u6765\u662f\u7ed9\u90a3\u4e2a\u7ed9\u8e22\u4e2d\u7684\u4f53\u6821\u751f\u53ec\u96c6\u6765\u7684\u540c\u5b66\uff0c\u5728\u5916\u9762\u9a82\u9a82\u54a7\u54a7\u7684\u8bf4\u4e86\u4e00\u4f1a\u513f\u8bdd\uff0c\u6e10\u884c\u6e10\u8fdc\uff0c\u542c\u58f0\u97f3\u662f\u5230\u97f3\u4e50\u5b66\u9662\u5019\u5b59\u9759\u6aac\u4e0e\u5f20\u606a\u53bb\u4e86\u3002</p><p>\u3000\u3000\u53c8\u7b49\u4e86\u4e00\u4f1a\u513f\uff0c\u6ca1\u542c\u89c1\u5916\u9762\u6709\u4ec0\u4e48\u52a8\u9759\uff0c\u5f20\u606a\u624d\u62c9\u7740\u5b59\u9759\u6aac\u7684\u624b\u4e00\u7638\u4e00\u62d0\u7684\u8d70\u51fa\u5395\u6240\u2014\u2014\u7ad9\u5728\u90a3\u91cc\u4e0d\u89c9\u5f97\u75bc\uff0c\u8d70\u8d77\u6765\u5c31\u89c9\u5f97\u811a\u8dbe\u5934\u94bb\u5fc3\u7684\u75db\uff0c\u90fd\u4e0d\u6653\u5f97\u5b59\u9759\u6aac\u521a\u624d\u8e29\u81ea\u5df1\u7684\u811a\u8dbe\u5c16\u591a\u7528\u529b\u2014\u2014\u770b\u5230\u4e00\u8f86\u51fa\u79df\u8f66\u7ecf\u8fc7\uff0c\u62e6\u4e0b\u6765\u5750\u8f66\u5148\u53bb\u72ee\u5b50\u56ed\u7684\u522b\u5885\u3002</p><p>\u3000\u3000\u5230\u4e86\u72ee\u5b50\u56ed\u7684\u522b\u5885\uff0c\u5f20\u606a\u624d\u8010\u7740\u6027\u5b50\u95ee\u5b59\u9759\u6aac\uff1a\u201c\u59d1\u5976\u5976\uff0c\u7a76\u7adf\u53d1\u751f\u4ec0\u4e48\u4e8b\u8981\u53bb\u90a3\u91cc\u53d1\u98da\uff1f\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8bf4\u9053\uff0c\u201c\u90a3\u51e0\u4e2a\u4eba\u6e23\u6574\u5929\u5c31\u5019\u5728\u6e9c\u51b0\u573a\u91cc\u5403\u5973\u5b69\u5b50\u8c46\u8150\uff0c\u5c0f\u8bd7\u4f60\u8ba4\u8bc6\u5427\uff1f\u5979\u5c31\u5728\u90a3\u91cc\u5403\u8fc7\u90a3\u51e0\u4e2a\u4eba\u6e23\u7684\u4e8f\uff0c\u7279\u522b\u662f\u90a3\u4e2a\u6234\u68d2\u7403\u5e3d\u7684\u5bb6\u4f19\uff0c\u4e0b\u624b\u66f4\u4e0b\u6d41\uff0c\u4f38\u624b\u5230\u5973\u5b69\u5b50\u8863\u670d\u91cc\u4e71\u6263\uff0c\u4e0d\u7ed9\u4ed6\u4eec\u4e9b\u6559\u8bad\uff0c\u9a6c\u83b2\u8857\u4ee5\u540e\u5927\u5bb6\u90fd\u4e0d\u6562\u53bb\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\uff0c\u4e71\u6263\u8fd8\u662f\u4e71\u6478\uff1f\u8fd9\u53ef\u662f\u4e24\u4e2a\u4e0d\u540c\u7684\u52a8\u4f5c\uff01\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u96be\u5f97\u7684\u8138\u7ea2\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u624b\u4f38\u8fdb\u88e4\u5b50\u91cc\uff0c\u4f60\u8bf4\u662f\u4ec0\u4e48\uff1f\u5c0f\u8bd7\u62a5\u4e86\u8b66\uff0c\u90a3\u51e0\u4e2a\u4eba\u6e23\u8ddf\u8fd9\u7247\u533a\u7684\u8b66\u5bdf\u8ba4\u8bc6\uff0c\u53c8\u6ca1\u6709\u4ec0\u4e48\u8bc1\u636e\uff0c\u56de\u6765\u90fd\u54ed\u4e86\u597d\u4e9b\u5929\uff0c\u201d\u53c8\u7a81\u7136\u53d1\u72e0\u7684\u77aa\u7740\u5f20\u606a\uff0c\u201c\u4f60\u4eca\u5929\u5360\u6211\u4e0d\u5c11\u4fbf\u5b9c\uff0c\u54fc\u2026\u2026\u201d\u9f3b\u8154\u91cc\u6076\u72e0\u72e0\u7684\u54fc\u4e86\u4e24\u58f0\uff0c\u5012\u662f\u6ca1\u6709\u8bf4\u4ec0\u4e48\u72e0\u8bdd\u3002</p></article>"}}}]);
