(window.webpackJsonp=window.webpackJsonp||[]).push([[57],{80:function(p){p.exports={chapterTitle:null,title:"\u7b2c57\u7ae0 \u5510\u5b66\u8c26\u7684\u9080\u8bf7",content:"<article><h2>\u7b2c57\u7ae0 \u5510\u5b66\u8c26\u7684\u9080\u8bf7</h2><p>\u3000\u3000\u8fd9\u4f1a\u513f\uff0c\u684c\u4e0a\u7684\u7535\u8bdd\u54cd\u4e86\u8d77\u6765\uff0c\u5f20\u606a\u968f\u624b\u63d0\u4e86\u8d77\u6765\uff0c\u542c\u5230\u5bf9\u65b9\u7684\u58f0\u97f3\uff0c\u5dee\u70b9\u50bb\u4e86\uff1a\u201c\u7238\uff0c\u600e\u4e48\u662f\u4f60\uff1f\u201d</p><p>\u3000\u3000\u201c\u5c0f\u606a\uff1f\u4f60\u5728\u90a3\u91cc\u73a9\uff1f\u54e6\uff0c\u4f60\u5510\u4f2f\u4f2f\u521a\u8ddf\u5f90\u4e66\u8bb0\u901a\u8fc7\u7535\u8bdd\uff0c\u8bf4\u4f60\u665a\u6674\u59d0\u5e26\u7740\u82b7\u5f64\u6765\u6d77\u5dde\u4e86\uff0c\u4f60\u5510\u4f2f\u4f2f\u8ddf\u6211\u5546\u91cf\u8981\u5c3d\u4e00\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff0c\u627e\u4e86\u4e00\u5708\uff0c\u60f3\u4f60\u665a\u6674\u59d0\u53ef\u4ee5\u5728\u8fd9\u8fb9\u7684\u529e\u4e8b\u5904\uff0c\u597d\u4e0d\u5bb9\u6613\u627e\u5230\u7535\u8bdd\uff0c\u4f60\u4eca\u5929\u4e0d\u662f\u8bf4\u8981\u5728\u5b66\u6821\u4e0a\u81ea\u4fee\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5413\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u8fd8\u4ee5\u4e3a\u66b4\u9732\u4e86\u5462\uff0c\u89c1\u8bb8\u601d\u4e5f\u662f\u4e00\u8138\u7684\u7d27\u5f20\uff0c\u5fd9\u7f13\u7740\u8bed\u6c14\u8bf4\uff1a\u201c\u4e0b\u5348\u5728\u5c4b\u5b50\u91cc\u73a9\u5462\uff0c\u521a\u7ed9\u665a\u6674\u59d0\u53eb\u8fc7\u6765\u73a9\uff0c\u8ba9\u4f60\u8ddf\u665a\u6674\u59d0\u8bf4\u8bdd\u3002\u201d\u5c06\u7535\u8bdd\u9012\u7ed9\u665a\u6674\uff0c\u201c\u6211\u7238\uff0c\u8ddf\u5510\u5b66\u8c26\u4e00\u8d77\u8981\u8bf7\u4f60\u5403\u996d\u3002\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u63a5\u8fc7\u7535\u8bdd\uff0c\u55ef\u554a\u54a6\u5440\u4e86\u51e0\u58f0\uff0c\u5c31\u5c06\u7535\u8bdd\u7ed9\u6302\u4e0a\u4e86\uff0c\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u516d\u70b9\u8ba9\u8f66\u6765\u63a5\uff0c\u5510\u5e02\u957f\u7684\u5973\u513f\u4e5f\u6765\uff0c\u542c\u8bf4\u957f\u5f97\u86ee\u6f02\u4eae\u7684\uff0c\u8ddf\u4f60\u540c\u5b66\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u79cd\u5bb6\u5bb4\uff0c\u60f3\u5fc5\u8001\u5988\u5f88\u5174\u594b\uff0c\u542c\u8c22\u665a\u6674\u610f\u6709\u6240\u6307\u7684\u63d0\u8d77\u5510\u5a67\uff0c\u5f20\u606a\u88c5\u50bb\u6123\u4e86\u6123\uff1a\u201c\u8fd8\u884c\u5427\uff0c\u6ca1\u89c9\u5f97\u7279\u522b\u7684\u6f02\u4eae\uff0c\u6574\u5929\u8ddf\u8bb8\u601d\u59d0\u5728\u4e00\u5757\uff0c\u770b\u8c01\u90fd\u9ebb\u6728\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u626f\u4e0a\u6211\u505a\u4ec0\u4e48\uff0c\u6211\u8ddf\u4f60\u5973\u670b\u53cb\u53ef\u6ca1\u6cd5\u6bd4\uff1f\u201d\u8bb8\u601d\u7b11\u7740\u6487\u6e05\u81ea\u5df1\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u5fc3\u91cc\u6015\u5f20\u606a\u5e74\u7eaa\u592a\u5c0f\uff0c\u53d7\u4e0d\u4f4f\u8bb8\u601d\u7684\u8bf1\u60d1\uff0c\u8fd9\u6b21\u8fc7\u6765\u89c1\u8bb8\u601d\u7a7f\u7740\u5f88\u6734\u7d20\uff0c\u8fd8\u523b\u610f\u6234\u4e0a\u9ed1\u8272\u73b3\u7441\u773c\u955c\uff0c\u5fc3\u60f3\u5979\u4e5f\u771f\u4e0d\u5bb9\u6613\u3002\u5bf9\u8521\u7eef\u5a1f\u8bf4\uff1a\u201c\u8521\u59d0\uff0c\u4f60\u4ee3\u8868\u6211\u8bf7\u8bb8\u601d\u8fd8\u6709\u5176\u4ed6\u4eba\u5403\u987f\u996d\uff0c\u4ee5\u540e\u9ebb\u70e6\u4ed6\u4eec\u7684\u4e8b\u60c5\u8fd8\u591a\u7740\u5462\u3002\u201d</p><p>\u3000\u3000\u516d\u70b9\u949f\uff0c\u5f20\u606a\u7275\u7740\u82b7\u5f64\u7684\u624b\uff0c\u4e0e\u8c22\u665a\u6674\u5728\u65b0\u6d77\u901a\u5927\u53a6\u524d\u7684\u5e7f\u573a\u7b49\uff0c\u6ca1\u60f3\u5230\u5218\u6587\u5175\u5f00\u7740\u4e8c\u53f7\u8f66\u8fc7\u6765\u3002\u6ca1\u7b49\u8f66\u505c\u7a33\uff0c\u5510\u5a67\u7cbe\u81f4\u8ff7\u4eba\u7684\u5c0f\u8138\u5c31\u4ece\u8f66\u7a97\u91cc\u9732\u51fa\u6765\uff0c\u4e00\u8fb9\u63a8\u8f66\u95e8\uff0c\u4e00\u8fb9\u57cb\u6028\u5f20\u606a\uff1a\u201c\u4f60\u4e00\u5929\u90fd\u8eb2\u8fd9\u91cc\u4e86\uff1f\u8fd8\u9a97\u4f60\u5988\u8bf4\u5728\u5b66\u6821\u4e0a\u81ea\u4fee\uff0c\u5bb3\u6211\u8981\u8ddf\u4f60\u4e00\u8d77\u8bf4\u8c0e\u3002\u201d\u89c6\u7ebf\u843d\u5230\u7d27\u62fd\u7740\u5f20\u606a\u8863\u89d2\u7684\u82b7\u5f64\u8138\u4e0a\uff0c\u201c\u554a\uff0c\u5c0f\u82b7\u5f64\u662f\u4e0d\u662f\uff1f\u201d\u90a3\u795e\u60c5\u5374\u50cf\u770b\u5230\u65b0\u5947\u7684\u5927\u73a9\u5177\uff0c\u201c\u82b7\u5f64\u771f\u7684\u5f88\u6f02\u4eae\uff0c\u8ba9\u59d0\u59d0\u634f\u4e00\u634f\u2026\u2026\u201d</p><p>\u3000\u3000\u82b7\u5f64\u62ac\u5934\u770b\u4e86\u770b\u5f20\u606a\uff0c\u53c8\u770b\u4e86\u770b\u4fef\u8eab\u671d\u81ea\u5df1\u8dd1\u6765\u7684\u5510\u5a67\uff0c\u4f38\u51fa\u7684\u624b\u4f3c\u4e4e\u771f\u8981\u5728\u81ea\u5df1\u7684\u8138\u86cb\u4e0a\u72e0\u72e0\u634f\u4e00\u628a\uff0c\u5f80\u540e\u9000\u4e86\u4e00\u6b65\uff0c\u6709\u4e9b\u72b9\u8c6b\uff0c\u8fd8\u662f\u7ad9\u5728\u90a3\u91cc\uff0c\u51ed\u5510\u5a67\u5c06\u5979\u5ae9\u6ed1\u7684\u5c0f\u8138\u5f53\u6210\u6a61\u76ae\u6ce5\u634f\u6765\u634f\u53bb\u3002</p><p>\u3000\u3000\u5510\u5a67\u7275\u7740\u82b7\u5f64\u7684\u5c0f\u624b\uff0c\u7ad9\u8d77\u6765\u6709\u4e9b\u7f9e\u6da9\uff0c\u5bf9\u8c22\u665a\u6674\u8bf4\u9053\uff1a\u201c\u665a\u6674\u59d0\uff0c\u6211\u5e38\u542c\u5f20\u606a\u63d0\u8d77\u4f60\uff0c\u4f60\u771f\u6f02\u4eae\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4e5f\u5e38\u5e38\u542c\u5c0f\u606a\u63d0\u8d77\u4ed6\u7684\u5c0f\u5973\u670b\u53cb\u3002\u201d\u8c22\u665a\u6674\u7b11\u7740\u8bf4\uff0c\u5510\u5a67\u74f7\u5668\u4e00\u822c\u7cbe\u7f8e\u7684\u8138\u4e0a\u6d0b\u6ea2\u903c\u4eba\u7684\u9752\u6625\u6c14\u606f\uff0c\u9ad8\u6311\u7684\u8eab\u6750\uff0c\u8fd8\u6ca1\u6709\u5b8c\u5168\u957f\u5f00\uff0c\u5012\u662f\u5341\u5206\u7684\u52a8\u4eba\uff0c\u957f\u5927\u4e4b\u540e\uff0c\u4e5f\u662f\u4e0d\u6bd4\u8bb8\u601d\u5dee\u591a\u5c11\u7684\u5927\u7f8e\u5973\u3002\u4fa7\u5934\u770b\u4e86\u770b\u5f20\u606a\uff0c\u96be\u602a\u4ed6\u770b\u8bb8\u601d\u7684\u773c\u795e\u6ca1\u6709\u6742\u8d28\uff0c\u539f\u6765\u5c0f\u60c5\u4eba\u8fd9\u4e48\u8ff7\u4eba\u3002\u53ea\u5f53\u662f\u5f20\u606a\u662f\u5c11\u5e74\u4eba\u521d\u8d77\u7537\u5973\u60c5\u6b32\uff0c\u5355\u7eaf\u800c\u5fe0\u8d1e\u3002</p><p>\u3000\u3000\u5510\u5a67\u4e00\u8138\u5a07\u7f9e\uff0c\u55d4\u9053\uff1a\u201c\u8c01\u662f\u4ed6\u5c0f\u5973\u670b\u53cb\uff0c\u4ed6\u5bf9\u4eba\u5bb6\u90fd\u7231\u7406\u4e0d\u7406\u7684\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6123\u4e86\u6123\uff0c\u5012\u4e0d\u6653\u5f97\u5510\u5a67\u8fd9\u8bdd\u662f\u5426\u8ba4\u5462\uff0c\u8fd8\u662f\u6492\u5a07\uff0c\u7275\u7740\u82b7\u5f64\u7684\u624b\uff0c\u7ad9\u5728\u90a3\u91cc\u4e0d\u8bf4\u8bdd\uff0c\u4e00\u8138\u7684\u61a8\u539a\uff0c\u659c\u7740\u773c\u775b\u5077\u7785\u5510\u5a67\u5fae\u5fae\u6cdb\u8d77\u7ea2\u6f6e\u7684\u8138\u86cb\uff0c\u5fc3\u60f3\u8bb8\u601d\u5e78\u597d\u6ca1\u6709\u4e0b\u6765\u9001\u4ed6\u4eec\uff0c\u8ba9\u5510\u5a67\u649e\u89c1\uff0c\u5c31\u6709\u9ebb\u70e6\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8fd9\u65f6\u848b\u8587\u4e0e\u6768\u4e91\u63a5\u5230\u805a\u9910\u7684\u901a\u77e5\uff0c\u6b63\u5f80\u529e\u4e8b\u5904\u8fd9\u8fb9\u8d76\uff0c\u770b\u89c1\u5f20\u606a\u94bb\u8fdb\u5c3c\u6851\u8f66\uff0c\u7741\u773c\u770b\u4e86\u770b\u8f66\u724c\uff0c\u848b\u8587\u8be7\u7136\u7684\u5bf9\u5468\u4e00\u5e73\u8bf4\uff1a\u201c\u5c0f\u5468\uff0c\u8fd9\u662f\u5e02\u957f\u5510\u5b66\u8c26\u7684\u8f66\uff0c\u521a\u521a\u8fdb\u53bb\u662f\u5f20\u7ecf\u7406\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u624b\u91cc\u62b1\u7740\u4e0d\u662f\u8c22\u603b\u7684\u5973\u513f\u5417\uff1f\u201d\u5468\u4e00\u5e73\u89c1\u848b\u8587\u660e\u77e5\u6545\u95ee\uff0c\u60f3\u5fc5\u662f\u592a\u60ca\u8bb6\u4e86\uff0c\u201c\u5c0f\u5f20\u7ecf\u7406\u4e0d\u8981\u662f\u5510\u5e02\u957f\u7684\u4eb2\u621a\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u6211\u603b\u89c9\u5f97\u4ed6\u50cf\u4e00\u4e2a\u4eba\uff1f\u201d</p><p>\u3000\u3000\u201c\u8c01\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u51e0\u5929\u65b0\u95fb\u91cc\u603b\u7ad9\u5510\u5b66\u8c26\u8eab\u8fb9\u7684\u90a3\u4e2a\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6ce8\u610f\u770b\u3002\u201d</p><p>\u3000\u3000\u7684\u786e\uff0c\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\u54ea\u6709\u5e02\u957f\u663e\u773c\uff0c\u848b\u8587\u521a\u521a\u8131\u79bb\u884c\u653f\u7cfb\u7edf\uff0c\u52a0\u4e0a\u4e08\u592b\u3001\u516c\u7239\u90fd\u662f\u653f\u5e9c\u5b98\u5458\uff0c\u5bf9\u6d77\u5dde\u5b98\u573a\u7684\u5347\u8fc1\u81ea\u7136\u719f\u6089\uff1a\u201c\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\uff0c\u5f20\u77e5\u884c\uff0c\u5c0f\u5f20\u7ecf\u7406\u5c31\u50cf\u4ed6\uff0c\u600e\u4e48\u770b\u600e\u4e48\u50cf\uff0c\u6211\u524d\u4e9b\u5929\u600e\u4e48\u5c31\u6ca1\u6709\u60f3\u8d77\u6765\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u5c0f\u5f20\u7ecf\u7406\u4e0d\u4f1a\u662f\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\u7684\u513f\u5b50\u5427\uff0c\u770b\u4ed6\u5e74\u7eaa\u8f7b\u8f7b\u7684\uff0c\u80fd\u529b\u6bd4\u6211\u4eec\u5f3a\u591a\u4e86\uff0c\u5bb6\u5ead\u6559\u80b2\u4e00\u5b9a\u5f88\u597d\u3002\u201d</p><p>\u3000\u3000\u201c\u5f00\u4ec0\u4e48\u73a9\u7b11\uff0c\u5f20\u77e5\u884c\u624d\u56db\u5341\u51fa\u5934\uff0c\u80fd\u6709\u8fd9\u4e48\u5927\u7684\u513f\u5b50\uff0c\u4e0d\u5f97\u4e8c\u5341\u5c81\u5c31\u7ed3\u5a5a\uff1f\u201d\u848b\u8587\u7b11\u4e86\u7b11\uff0c\u89c9\u5f97\u4e0d\u53ef\u80fd\uff0c\u5f88\u53ef\u80fd\u662f\u5f20\u77e5\u884c\u7684\u4eb2\u621a\u3002</p><p>\u3000\u3000\u5f88\u663e\u7136\uff0c\u5510\u5b66\u8c26\u6d3e\u8f66\u662f\u6765\u63a5\u8c22\u665a\u6674\u7684\u3002</p><p>\u3000\u3000\u848b\u8587\u4e4b\u524d\u5e94\u8058\u73b0\u5728\u7684\u5de5\u4f5c\uff0c\u662f\u5979\u4ece\u8d22\u653f\u5c40\u5185\u9000\u4e4b\u540e\u4e0d\u7518\u5fc3\u505a\u5168\u804c\u592a\u592a\uff0c\u5bf9\u673a\u5173\u7684\u9f8c\u9f8a\u4e8b\u6709\u4e9b\u538c\u6076\uff0c\u4e0d\u60f3\u91cd\u65b0\u8fdb\u5165\u653f\u5e9c\u673a\u5173\u5de5\u4f5c\uff0c\u624d\u60f3\u5230\u8fdb\u516c\u53f8\u6253\u5de5\uff0c\u521d\u65f6\u662f\u529e\u4e8b\u5904\u5f00\u51fa\u4e30\u539a\u7684\u5f85\u539a\u5438\u5f15\u4e86\u5979\uff0c\u4e4b\u540e\u848b\u8587\u4e0d\u5f97\u4e0d\u627f\u8ba4\u5f20\u606a\u662f\u4e00\u4e2a\u76f8\u5f53\u6709\u611f\u67d3\u529b\u4e0e\u5f71\u54cd\u529b\u7684\u4eba\uff0c\u5728\u4e08\u592b\u5632\u7b11\u5979\u4e94\u5343\u5143\u5de5\u4f5c\u4fdd\u8bc1\u91d1\u4e00\u5b9a\u4f1a\u6253\u6c34\u6f02\uff0c\u5979\u8fd8\u662f\u89c9\u5f97\u5e94\u8be5\u8bd5\u4e00\u8bd5\uff0c\u63a5\u89e6\u624d\u4e00\u5468\u7684\u65f6\u95f4\uff0c\u5979\u5c31\u89c9\u5f97\u81ea\u5df1\u7684\u51b3\u5b9a\u6ca1\u6709\u9519\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u770b\u89c1\u5510\u5b66\u8c26\u6d3e\u8f66\u6765\u63a5\u516c\u53f8\u7684\u603b\u88c1\uff0c\u848b\u8587\u6709\u4e9b\u8be7\u5f02\uff0c\u8c22\u665a\u6674\u5012\u5e95\u662f\u54ea\u4e00\u53f7\u4eba\u7269\uff0c\u9700\u8981\u5510\u5b66\u8c26\u6765\u5df4\u7ed3\uff1f\u6d77\u88d5\u516c\u53f8\u7684\u80cc\u666f\u4e0d\u50cf\u770b\u4e0a\u53bb\u90a3\u4e48\u7b80\u5355\u554a\uff01</p><p>\u3000\u3000\u848b\u8587\u60f3\u7740\u8ba9\u4e08\u592b\u5230\u5708\u5b50\u91cc\u6253\u542c\u4e00\u4e0b\u4e5f\u5c31\u6e05\u695a\u4e86\uff0c\u6000\u7740\u5fc3\u601d\u4e0e\u5468\u4e00\u5e73\u8fdb\u4e86\u5927\u53a6\u3002</p></article>"}}}]);
