(window.webpackJsonp=window.webpackJsonp||[]).push([[572],{595:function(p){p.exports={chapterTitle:null,title:"\u7b2c572\u7ae0 \u6495\u7f51\u4e0e\u7ed3\u7f51",content:"<article><h2>\u7b2c572\u7ae0 \u6495\u7f51\u4e0e\u7ed3\u7f51</h2><p>\u3000\u3000\u201c\u54a6\uff0c\u4e0d\u662f\u8bf4\u4f60\u4eec\u660e\u5929\u624d\u8fc7\u6765\u5417\uff1f\u201d\u5f20\u606a\u8d70\u51fa\u7535\u68af\u770b\u89c1\u6881\u519b\u4e0e\u4ed6\u5973\u670b\u53cb\u5b59\u4e3d\u7ad9\u5728\u7535\u68af\u524d\u8bf4\u8bdd\uff0c\u624b\u91cc\u8fd8\u63d0\u7740\u51e0\u4e2a\u7eb8\u888b\uff0c\u4e0d\u6653\u5f97\u662f\u4ec0\u4e48\u4e1c\u897f\u3002</p><p>\u3000\u3000\u201c\u4e2d\u5348\u5c31\u5c06\u4e8b\u60c5\u6536\u62fe\u5b8c\u4e86\uff0c\u89c1\u4e0b\u5348\u6709\u98de\u673a\uff0c\u5c31\u76f4\u63a5\u8fc7\u6765\u4e86\u3002\u201d\u6881\u519b\u8bf4\u9053\u3002\u4ed6\u4e0e\u5b59\u4e3d\u5728\u7b49\u7535\u68af\uff0c\u770b\u5230\u5f20\u606a\u4e0e\u9a6c\u6d77\u9f99\u4ece\u7535\u68af\u91cc\u8d70\u51fa\u6765\uff0c\u8fd8\u7ed9\u5413\u4e86\u4e00\u8df3\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u770b\u624b\u8868\uff0c\u8bf4\u9053\uff1a\u201c\u9a6c\u4e0a\u5c31\u8981\u5f00\u4f1a\u4e86\uff0c\u4f60\u8fd9\u65f6\u5019\u8981\u8dd1\u54ea\u91cc\u53bb\uff1f\u201d</p><p>\u3000\u3000\u6881\u519b\u521a\u5230\u5efa\u90ba\u5e02\uff0c\u7b97\u662f\u771f\u6b63\u8c03\u5165\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\uff0c\u8bf4\u662f\u7ed9\u90b5\u81f3\u521a\u5f53\u52a9\u624b\uff0c\u4f46\u662f\u5177\u4f53\u7684\u804c\u52a1\u8fd8\u6ca1\u6709\u5b89\u6392\u3002\u624d\u4e09\u56db\u5929\u7684\u4e8b\u60c5\uff0c\u90b5\u81f3\u521a\u5728\u5efa\u90ba\u4e5f\u5fd9\u5f97\u7126\u5934\u70c2\u989d\uff0c\u6ca1\u6709\u65f6\u95f4\u8ba8\u8bba\u6881\u519b\u7684\u804c\u52a1\u95ee\u9898\uff0c\u5148\u8c03\u8fc7\u6765\u7528\u7740\u518d\u8bf4\u3002\u6881\u519b\u77e5\u9053\u78b0\u5934\u4f1a\u7684\u4e8b\u60c5\uff0c\u4e0d\u8fc7\u90a3\u90fd\u662f\u4e16\u7eaa\u9526\u6e56\u4e0e\u9526\u6e56\u7684\u9ad8\u5c42\u4eba\u5458\u624d\u6709\u8d44\u683c\u53c2\u52a0\u7684\u534f\u8c03\u4f1a\uff0c\u4e5f\u6ca1\u6709\u4eba\u901a\u77e5\u4ed6\u53c2\u52a0\u3002\u8fd9\u65f6\u5019\u662f\u4e0b\u73ed\u65f6\u95f4\uff0c\u867d\u7136\u4e0b\u5348\u521a\u8fc7\u6765\uff0c\u6881\u519b\u8fd8\u662f\u7b49\u5230\u4e0b\u73ed\u65f6\u95f4\u624d\u966a\u5b59\u4e3d\u4e00\u8d77\u53bb\u623f\u95f4\u6536\u62fe\u4e00\u4e0b\u4e1c\u897f\uff0c\u6253\u7b97\u957f\u671f\u4f4f\u5bbe\u9986\u4e86\uff0c\u4e00\u4e9b\u751f\u6d3b\u7528\u54c1\u8fd8\u662f\u81ea\u5df1\u51c6\u5907\u7684\u597d\u3002</p><p>\u3000\u3000\u542c\u5230\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u6881\u519b\u8111\u5b50\u8f6c\u77ac\u95f4\u5c31\u8f6c\u8fc7\u5f2f\u6765\u4e86\uff0c\u8c01\u70b9\u540d\u8ba9\u4ed6\u53c2\u52a0\u78b0\u5934\u4f1a\uff0c\u90fd\u6ca1\u6709\u4ed6\u8fd9\u4e2a\u8868\u5f1f\u4e00\u53e3\u65ad\u8a00\u6709\u6548\u529b\u3002\u6881\u519b\u7b11\u7740\u8bf4\uff0c\u201c\u51c6\u5907\u966a\u5b59\u4e3d\u4e0b\u53bb\u4e00\u8d9f\uff0c\u201d\u5c06\u624b\u91cc\u7684\u7eb8\u888b\u9012\u7ed9\u5973\u53cb\u5b59\u4e3d\uff0c\u201c\u4f60\u5148\u53bb\u623f\u95f4\u6536\u62fe\u4e00\u4e0b\u2026\u2026\u201d\u966a\u7740\u5f20\u606a\u53bb\u4e0b\u5348\u624d\u5e03\u7f6e\u597d\u7684\u4f1a\u8bae\u5ba4\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e5d\u4e94\u5e74\u590f\u5929\uff0c\u968f\u7236\u4eb2\u6881\u56fd\u5174\u8f6c\u4e1a\u4e00\u5bb6\u4eba\u8fc1\u56de\u6d77\u5dde\uff0c\u6d77\u5dde\u5bf9\u6881\u519b\u6765\u8bf4\u662f\u4e00\u4e2a\u964c\u751f\u7684\u57ce\u5e02\uff0c\u4f46\u662f\u6bd4\u8d77\u56db\u5ddd\u4e2d\u90e8\u7684\u5c0f\u53bf\u57ce\uff0c\u6d77\u5dde\u66f4\u503c\u5f97\u4eba\u5411\u5f80\u3002</p><p>\u3000\u3000\u90a3\u65f6\u5019\uff0c\u7231\u8fbe\u7535\u5b50\u521a\u521a\u7ec4\u5efa\uff0c\u7231\u8fbe\u7535\u5b50\u4e5d\u516d\u5e74\u4e4b\u540e\u6240\u521b\u9020\u7684\u5e02\u573a\u5947\u8ff9\u662f\u5f53\u65f6\u8c01\u90fd\u60f3\u4e0d\u5230\u7684\uff0c\u4f46\u662f\u9526\u6e56\u5df2\u6210\u6c14\u5019\uff0c\u5f20\u77e5\u975e\u7684\u5b8f\u8fdc\u5b9e\u4e1a\u5728\u6d77\u5dde\u4e5f\u624e\u4e0b\u6839\u57fa\u3002\u6bd4\u8d77\u522b\u4eba\u8f6c\u4e1a\u5230\u5730\u65b9\u9876\u591a\u4eab\u53d7\u76f8\u5e94\u7ea7\u522b\u7684\u5f85\u9047\uff0c\u7236\u4eb2\u6881\u56fd\u5174\u521a\u5230\u6d77\u5dde\u5374\u83b7\u5f97\u52b3\u52a8\u5c40\u526f\u5c40\u957f\u7684\u5b9e\u804c\uff0c\u6881\u519b\u81ea\u7136\u6653\u5f97\u8fd9\u662f\u5c0f\u59d1\u592b\u5bb6\u5728\u6d77\u5dde\u7684\u80cc\u666f\u6df1\u539a\uff1b\u800c\u6574\u4e2a\u5bb6\u65cf\u7684\u7075\u9b42\u4eba\u7269\u7adf\u662f\u4e8c\u5341\u5c81\u4e0d\u5230\u7684\u8868\u5f1f\uff0c\u5374\u662f\u5916\u4eba\u96be\u4ee5\u60f3\u8c61\u7684\u3002</p><p>\u3000\u3000\u540c\u65f6\u6bd5\u4e1a\u7684\u540c\u5b66\u6709\u53bb\u5357\u65b9\u57ce\u5e02\u6253\u5de5\uff0c\u53c8\u7559\u5728\u6210\u90fd\u5c31\u4e1a\u7684\uff0c\u6709\u8fd1\u534a\u7684\u4eba\u8fd8\u662f\u63a5\u53d7\u5de5\u4f5c\u5206\u914d\u56de\u539f\u7c4d\uff0c\u65e0\u8bba\u5404\u4eba\u7684\u80fd\u529b\u5982\u4f55\uff0c\u5927\u591a\u6570\u4eba\u90fd\u5904\u4e8e\u4eba\u751f\u8d77\u6b65\u3001\u63a5\u53d7\u793e\u4f1a\u78e8\u783a\u7684\u9636\u6bb5\uff0c\u50cf\u6881\u519b\u8fd9\u79cd\u521a\u6bd5\u4e1a\u5c31\u63a5\u53d7\u9ad8\u5f3a\u5ea6\u953b\u70bc\u7684\u4eba\u6781\u5c11\u3002</p><p>\u3000\u3000\u4e0d\u5230\u4e24\u5e74\u7684\u65f6\u95f4\u91cc\uff0c\u5728\u5f20\u77e5\u975e\u3001\u76db\u9752\u7b49\u4eba\u523b\u610f\u7684\u57f9\u517b\u4e0b\uff0c\u6881\u519b\u7684\u6210\u957f\u662f\u540c\u65f6\u671f\u6bd5\u4e1a\u7684\u540c\u5b66\u6240\u4e0d\u80fd\u6bd4\u7684\uff0c\u5df2\u7ecf\u6ca1\u6709\u521d\u51fa\u793e\u4f1a\u7684\u6d6e\u8e81\u3001\u7a1a\u5ae9\uff0c\u6709\u7740\u5e74\u5c11\u6709\u6210\u7684\u6c89\u7a33\u4e0e\u6de1\u5b9a\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u4e2d\u53f6\uff0c\u56fd\u5185\u79c1\u8425\u4f01\u4e1a\u8fd8\u5904\u4e8e\u53d1\u5c55\u7684\u8d77\u6b65\u9636\u6bb5\uff0c\u79c1\u4f01\u7684\u7ba1\u7406\u5c42\u591a\u4e3a\u7cbe\u4e8e\u4eba\u4e8b\u3001\u6562\u4e8e\u62fc\u95ef\u7684\u4eba\uff1b\u4f01\u4e1a\u89c4\u6a21\u5316\u53d1\u5c55\uff0c\u4f5c\u4e3a\u521b\u4e1a\u8005\u7684\u79c1\u8425\u4f01\u4e1a\u5bb6\u5374\u77ed\u4e8e\u89c4\u6a21\u4f01\u4e1a\u7684\u7ba1\u7406\uff0c\u81f4\u4f7f\u804c\u4e1a\u7ecf\u7406\u4e0e\u5bb6\u65cf\u4f01\u4e1a\u7684\u7b2c\u4e8c\u4ee3\u63a5\u73ed\u4eba\u8fc5\u901f\u5d1b\u8d77\u3002</p><p>\u3000\u3000\u5e74\u7eaa\u8f7b\u8f7b\u5c31\u62c5\u4efb\u4f01\u4e1a\u9ad8\u7ea7\u7ecf\u7406\u7684\u60c5\u51b5\uff0c\u518d\u8fc7\u5341\u5e74\u516b\u5e74\u5c31\u5f88\u5e38\u89c1\u4e86\uff0c\u5373\u4f7f\u4e8c\u5341\u516d\u4e03\u5c81\u9886\u5bfc\u4e0a\u767e\u4ebf\u89c4\u6a21\u5927\u96c6\u56e2\u7684\u4f8b\u5b50\u4e5f\u4e0d\u9c9c\u89c1\uff0c\u4f46\u5728\u4e5d\u4e03\u5e74\u8fd8\u5f88\u7f55\u89c1\uff0c\u4e0d\u8fc7\u4e5f\u4e0d\u662f\u4e0d\u80fd\u8ba9\u4eba\u63a5\u53d7\u3002</p><p>\u3000\u3000\u8c22\u5bb6\u6b63\u6cf0\u96c6\u56e2\u65d7\u4e0b\u7684\u4f01\u4e1a\u90fd\u7eb7\u7eb7\u7531\u5e74\u8f7b\u7684\u7b2c\u4e8c\u4ee3\u63a5\u624b\uff0c\u8c22\u5251\u5357\u3001\u8c22\u610f\u90fd\u975e\u5e38\u5e74\u8f7b\u3002\u5373\u4f7f\u5728\u7231\u8fbe\u7535\u5b50\u7684\u5149\u8292\u4e4b\u4e0b\uff0c\u79d1\u738b\u9eef\u6de1\u65e0\u8f89\uff0c\u5374\u4e5f\u4e0d\u5f97\u4e0d\u627f\u8ba4\u8c22\u5251\u5357\u3001\u9648\u9759\u8fd9\u4e9b\u79d1\u738b\u7684\u5e74\u8f7b\u7ba1\u7406\u8005\u5728\u4f01\u4e1a\u7ba1\u7406\u8005\u5e76\u6ca1\u6709\u81f4\u547d\u7684\u7f3a\u9677\uff0c\u53cd\u800c\u5728\u5b8c\u5584\u4f01\u4e1a\u7ba1\u7406\u65b9\u9762\uff0c\u4ed6\u4eec\u6bd4\u7b2c\u4e00\u4ee3\u7684\u521b\u4e1a\u8005\u8981\u51fa\u8272\u8bb8\u591a\u3002</p><p>\u3000\u3000\u6574\u4e2a\u5927\u7684\u9526\u6e56\u4f53\u7cfb\u5185\uff0c\u6709\u5f20\u606a\u8fd9\u4e2a\u6781\u7aef\u7684\u4f8b\u5b50\u5728\uff0c\u804c\u52a1\u5347\u8fc1\u5df2\u7ecf\u6253\u7834\u5e74\u9f84\u7684\u5c4f\u969c\uff0c\u80fd\u529b\u8f83\u5f3a\u7684\u5e74\u8f7b\u4eba\u90fd\u80fd\u8fc5\u901f\u586b\u5145\u5230\u91cd\u8981\u5c97\u4f4d\u4e0a\u53bb\uff0c\u6ca1\u6709\u673a\u5173\u4e8b\u4e1a\u5355\u4f4d\u90a3\u79cd\u5148\u7422\u78e8\u4e24\u4e09\u5e74\u3001\u5c06\u68f1\u89d2\u9510\u5fd7\u78e8\u6389\u7684\u505a\u6cd5\u3002\u4f01\u4e1a\u53d1\u5c55\u9636\u6bb5\uff0c\u66f4\u9700\u8981\u8fd9\u4e9b\u5e74\u8f7b\u8005\u7684\u68f1\u89d2\u4e0e\u9510\u5229\u7684\u610f\u5fd7\u53bb\u5f00\u62d3\u4e8b\u4e1a\u3002\u50cf\u62c5\u4efb\u5317\u4eac\u5206\u516c\u53f8\u526f\u603b\u7ecf\u7406\u7684\u59da\u575a\u4eca\u5e74\u4e5f\u4e0d\u8fc7\u4e8c\u5341\u4e03\u5c81\u7684\u672a\u5a5a\u9752\u5e74\uff0c\u82cf\u6d25\u4e1c\u7684\u52a9\u7406\u8d75\u9e4f\u4e5f\u4e0d\u8fc7\u4e8c\u5341\u516d\u5c81\uff0c\u82cf\u6d25\u4e1c\u5c31\u6709\u610f\u5c06\u4ed6\u653e\u51fa\u53bb\u62c5\u4efb\u90e8\u95e8\u4e3b\u7ba1\u953b\u70bc\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u6881\u519b\u5e74\u7eaa\u66f4\u8f7b\u4e00\u4e9b\uff0c\u5f53\u7136\u8fd9\u66f4\u4e0d\u662f\u969c\u788d\u4e86\uff0c\u5148\u5230\u4f1a\u8bae\u5ba4\u7684\u4eba\u770b\u5230\u6881\u519b\u8ddf\u5728\u5f20\u606a\u540e\u9762\u8fdb\u6765\uff0c\u70b9\u5934\u7b11\u4e86\u7b11\uff0c\u4e5f\u6ca1\u6709\u611f\u5230\u60ca\u8bb6\u3002</p><p>\u3000\u3000\u4eca\u5929\u4e0a\u5348\u9648\u4fe1\u751f\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u4e0e\u5efa\u90ba\u5e02\u653f\u5e9c\u7b7e\u7f72\u5b9e\u65bd\u6811\u56ed\u8ba1\u5212\u7684\u610f\u5411\u4e66\uff0c\u660e\u5929\u5c31\u8981\u5c31\u6a61\u6811\u56ed\u8ba1\u5212\u7684\u89c4\u5212\u7ec6\u8282\u4e0e\u5efa\u90ba\u5e02\u653f\u5e9c\u8fdb\u884c\u8c08\u5224\u3002</p><p>\u3000\u3000\u5927\u89c4\u6a21\u7684\u57fa\u5efa\u6bd5\u7adf\u9700\u8981\u76f8\u5f53\u957f\u7684\u65f6\u95f4\u624d\u80fd\u5b8c\u6210\uff0c\u6a61\u6811\u56ed\u8ba1\u5212\u4e0d\u53ef\u80fd\u62d6\u5230\u4e00\u5e74\u4e4b\u540e\u518d\u8fd0\u884c\uff0c\u9664\u4e86\u5c06\u5b66\u5e9c\u5df7\u6539\u9020\u9879\u76ee\u5148\u63fd\u4e0b\u6765\uff0c\u5f20\u606a\u7684\u610f\u89c1\uff0c\u5c31\u662f\u5c06\u71d5\u5f52\u6e56\u4e1c\u5317\u89d2\u7684\u4e24\u5ea7\u5370\u67d3\u5382\u5148\u76d8\u4e0b\u6765\u8fdb\u884c\u6539\u9020\uff0c\u5c06\u6a61\u6811\u56ed\u8ba1\u5212\u5c0f\u89c4\u6a21\u7684\u8fd0\u884c\u8d77\u6765\uff1b\u8fd9\u4e48\u64cd\u4f5c\u8fd8\u53ef\u4ee5\u89c4\u907f\u524d\u671f\u7684\u9879\u76ee\u5ba1\u6279\uff0c\u5f20\u606a\u4e0d\u5e0c\u671b\u56e0\u4e3a\u653f\u5e9c\u7684\u4f4e\u884c\u653f\u6548\u7387\u800c\u727a\u7272\u9526\u6e56\u7684\u6548\u7387\u3002</p><p>\u3000\u3000\u5f20\u606a\u8981\u6c42\u9648\u4fe1\u751f\u3001\u4e01\u69d0\u4ed6\u4eec\u5148\u5c31\u5370\u67d3\u5382\u7684\u95ee\u9898\uff0c\u660e\u5929\u5c31\u4e0e\u5efa\u90ba\u5e02\u653f\u5e9c\u5c55\u5f00\u8c08\u5224\uff0c\u8981\u6c42\u5efa\u90ba\u5e02\u59d4\u5e02\u653f\u5e9c\u5148\u5728\u8fd9\u4ef6\u4e8b\u60c5\u7ed9\u9526\u6e56\u5f00\u7eff\u706f\u3002</p><p>\u3000\u3000\u56db\u5341\u5206\u949f\u7684\u4f1a\u8bae\uff0c\u5c06\u9700\u8981\u534f\u8c03\u89e3\u51b3\u7684\u4e8b\u60c5\u62ff\u51fa\u6765\u8ba8\u8bba\u4e00\u4e0b\uff0c\u660e\u786e\u5404\u4eba\u624b\u5934\u7684\u4efb\u52a1\uff0c\u78b0\u5934\u4f1a\u5c31\u7b97\u7ed3\u675f\u3002</p><p>\u3000\u3000\u73b0\u5728\u6b63\u5904\u4e8e\u4e0e\u5efa\u90ba\u5e02\u59d4\u5e02\u653f\u5e9c\u7684\u871c\u6708\u671f\uff0c\u8981\u4e0e\u7f57\u541b\u3001\u738b\u7ef4\u5747\u3001\u9ec4\u514b\u7fa4\u7b49\u4e00\u73ed\u5e02\u59d4\u5e02\u653f\u5e9c\u9886\u5bfc\u7ef4\u6301\u5f53\u524d\u751c\u871c\u7684\u5173\u7cfb\uff0c\u8fd8\u8981\u7167\u987e\u5230\u5e02\u957f\u8096\u660e\u5efa\u7684\u9762\u5b50\uff0c\u867d\u7136\u7f57\u541b\u6709\u62ff\u6a61\u6811\u56ed\u8ba1\u5212\u6253\u538b\u8096\u660e\u5efa\u7684\u610f\u56fe\uff0c\u4f46\u662f\u8fd9\u8fb9\u8981\u6781\u529b\u907f\u514d\u8fc7\u6df1\u7684\u6d89\u53ca\u7f57\u541b\u4e0e\u8096\u660e\u5efa\u7684\u77db\u76fe\u4e4b\u4e2d\uff0c\u9ad8\u65b0\u4ea7\u4e1a\u56ed\u533a\u4e0e\u79d1\u6280\u5c40\u3001\u4e1c\u534e\u533a\u7684\u9886\u5bfc\u4e5f\u8981\u8fdb\u884c\u4e1a\u52a1\u8054\u7edc\u3002\u4f1a\u8bae\u7ed3\u675f\uff0c\u7a97\u5916\u7684\u5915\u9633\u6709\u4e00\u534a\u6c89\u5165\u897f\u5c71\u4e4b\u4e0b\uff0c\u5bf9\u4e8e\u9526\u6e56\u9ad8\u5c42\u6765\u8bf4\uff0c\u5de5\u4f5c\u65f6\u95f4\u5e76\u6ca1\u6709\u7ed3\u675f\uff0c\u8fd8\u6709\u5f88\u591a\u7684\u5546\u52a1\u5e94\u916c\u8981\u53bb\u5904\u7406\u3002</p><p>\u3000\u3000\u5f20\u606a\u62c9\u4f4f\u5c0f\u53d4\u5f20\u77e5\u975e\uff1a\u201c\u8bb8\u8001\u5e08\u4eca\u5929\u5230\u5efa\u90ba\u4e86\uff0c\u6211\u8981\u8bf7\u4ed6\u5403\u996d\uff0c\u5c0f\u53d4\u4f60\u8ddf\u6881\u519b\u966a\u6211\u8fc7\u53bb\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5750\u8fdb\u9ed1\u8272\u798f\u7279\u8f7f\u8f66\u91cc\uff0c\u4eff\u4f5b\u6700\u540e\u4e00\u7f15\u5915\u9633\u4f59\u6656\u7167\u5728\u8f66\u7a97\u4e0a\uff0c\u62d0\u4e0a\u4e1c\u534e\u5927\u9053\u5c31\u611f\u89c9\u66ae\u8272\u6df1\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u8f66\u91cc\u7ed9\u9648\u6653\u677e\u6253\u4e86\u7535\u8bdd\uff0c\u6bd5\u7adf\u8981\u8ffd\u7a764\u670810\u65e5\u53d1\u751f\u7684\u7325\u4eb5\u6848\u4e0d\u662f\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\u60c5\uff0c\u800c\u4e14\u5e76\u4e0d\u80fd\u9632\u6b62\u5434\u7231\u519b\u5728\u8b66\u8f66\u91cc\u7ed9\u5b59\u83ca\u6770\u9762\u6388\u673a\u8c0a\uff0c\u8b66\u52a1\u7763\u5bdf\u5904\u8981\u4ece\u9a6c\u83b2\u8857\u6d3e\u51fa\u6240\u6c11\u8b66\u7684\u6e0e\u804c\u884c\u4e3a\u67e5\u8d77\uff0c\u4f1a\u6bd4\u8f83\u590d\u6742\uff0c\u5b59\u9759\u6aac\u5979\u4eec\u6b63\u5728\u505a\u7b14\u5f55\uff0c\u4e00\u65f6\u534a\u4f1a\u4e5f\u7ed3\u675f\u4e0d\u4e86\u3002</p><p>\u3000\u3000\u8bf7\u8bb8\u9e3f\u4f2f\u5403\u996d\uff0c\u5f20\u606a\u6ca1\u6709\u53bb\u522b\u7684\u5730\u65b9\uff0c\u76f4\u63a5\u5c06\u8bb8\u9e3f\u4f2f\u4ece\u6797\u4e1a\u516c\u53f8\u63a5\u5230\u72ee\u5b50\u56ed\u522b\u5885\u91cc\uff0c\u5728\u90a3\u91cc\u5403\u996d\uff0c\u6bd4\u53bb\u9ad8\u7ea7\u996d\u9986\u66f4\u4eb2\u5207\u4e9b\u3002\u5b59\u4e3d\u5728\u56db\u5ddd\u5c0f\u53bf\u57ce\u4e5f\u662f\u5bb6\u5883\u4f18\u88d5\u7684\u5bb6\u5ead\u51fa\u8eab\uff0c\u4e0d\u6cbe\u6cb9\u76d0\u9171\u918b\uff0c\u4e0d\u8fc7\u8ddf\u7740\u6881\u519b\u4e4b\u540e\u5012\u662f\u80fd\u505a\u51e0\u9053\u7b80\u5355\u7684\u83dc\uff0c\u4ed6\u4eec\u5728\u6df1\u5733\u4e5f\u4e0d\u53ef\u80fd\u5929\u5929\u4e0b\u9986\u5b50\uff0c\u90a3\u6837\u7684\u751f\u6d3b\u5c31\u592a\u65e0\u8da3\u4e86\u3002\u5b59\u4e3d\u8fd8\u80fd\u5e2e\u7740\u7ed9\u9a6c\u6d77\u9f99\u6253\u4e0b\u624b\uff0c\u5979\u5bf9\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u4f60\u4ee5\u540e\u8981\u5e38\u4f4f\u8fd9\u91cc\u7684\uff0c\u8fd8\u4e0d\u8bf7\u4e9b\u5de5\u4eba\u8fc7\u6765\u5e2e\u5fd9\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u4f1a\uff0c\u201d\u5f20\u606a\u60f3\u7740\u8bb8\u601d\u4ee5\u540e\u5230\u5efa\u90ba\u5e02\u7684\u673a\u4f1a\u5f88\u591a\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u5979\u4f4f\u5230\u8fd9\u91cc\u62c5\u60ca\u53d7\u6015\u7684\uff0c\u8981\u5728\u71d5\u5f52\u6e56\u53e6\u5b89\u6392\u623f\u5b50\uff0c\u201c\u6211\u4e00\u4e2a\u4eba\u4f4f\u8fd9\u91cc\u5b64\u96f6\u96f6\u7684\uff0c\u4e0d\u4e60\u60ef\u3002\u4e0a\u5927\u5b66\u540e\uff0c\u5b66\u6821\u7684\u4f4f\u5bbf\u6761\u4ef6\u597d\u5c31\u4f4f\u5b66\u6821\uff0c\u4f4f\u5bbf\u6761\u4ef6\u4e0d\u597d\uff0c\u5c31\u5728\u9644\u8fd1\u627e\u95f4\u516c\u5bd3\u4f4f\uff0c\u6307\u4e0d\u5b9a\u675c\u98de\u4e09\u5929\u4e24\u5934\u8fc7\u6765\u8ddf\u6211\u6324\uff0c\u201d\u675c\u98de\u8fd8\u662f\u60f3\u51ed\u81ea\u5df1\u7684\u80fd\u529b\u5728\u6700\u540e\u4e09\u4e2a\u6708\u51b2\u523a\u4e00\u4e0b\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u4e00\u8d77\u8fdb\u4e1c\u5927\u662f\u6ca1\u6709\u95ee\u9898\u7684\uff0c\u5f20\u606a\u66f4\u6000\u7591\u4ed6\u662f\u7559\u604b\u4e0e\u674e\u829d\u82b3\u76f8\u5904\u7684\u6700\u540e\u65f6\u5149\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u771f\u5230\u5927\u5b66\u4e4b\u540e\uff0c\u675c\u98de\u5927\u6982\u5c31\u4f1a\u4e0e\u4ed6\u5f62\u5f71\u4e0d\u79bb\u4e86\uff0c\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u4f60\u8ddf\u6881\u519b\u4e5f\u4e0d\u53ef\u80fd\u957f\u671f\u4f4f\u5728\u5bbe\u9986\u91cc\uff0c\u8981\u4e0d\u8fd9\u91cc\u8ba9\u7ed9\u4f60\u4eec\u4f4f\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u4fe9\u4e5f\u4e0d\u9700\u8981\u4f4f\u8fd9\u4e48\u5927\u7684\u623f\u5b50\u554a\uff01\u201d\u5b59\u4e3d\u770b\u4e86\u770b\u8fd9\u623f\u5b50\u8db3\u8db3\u6709\u516d\u4e03\u767e\u5e73\u7c73\uff0c\u4ec5\u4e00\u4e2a\u9910\u5385\u5c31\u8981\u6bd4\u5c0f\u6237\u578b\u7684\u4f4f\u623f\u9762\u79ef\u8fd8\u5927\uff0c\u5411\u5f80\u662f\u5411\u5f80\uff0c\u4f46\u662f\u5979\u4e0e\u6881\u519b\u4e24\u4eba\u8fd8\u6ca1\u6709\u7ed3\u5a5a\uff0c\u62ac\u5934\u770b\u4e86\u770b\u5927\u5385\u7684\u5929\u82b1\uff0c\u201c\u4f4f\u8fd9\u4e48\u5927\u7684\u623f\u5b50\uff0c\u6536\u62fe\u8d77\u6765\u5c31\u591f\u5934\u75bc\u7684\uff0c\u6211\u4eec\u5728\u5e08\u5927\u9644\u8fd1\u627e\u95f4\u516c\u5bd3\u79df\u7740\u4f4f\u5c31\u53ef\u4ee5\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u4eec\u662f\u4eba\u61d2\u554a\uff0c\u8fd9\u680b\u522b\u5885\u8981\u662f\u642c\u5230\u4e1c\u5927\u9644\u8fd1\uff0c\u6307\u4e0d\u5b9a\u6709\u591a\u5c11\u4eba\u62a2\u7740\u4f4f\u8fdb\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u559c\u6b22\u90a3\u79cd\u6563\u6b65\u5c31\u80fd\u5230\u8fbe\u7684\u95f2\u6563\u611f\u89c9\uff0c\u867d\u8bf4\u4e58\u8f66\u4ece\u4e1c\u6d77\u5230\u72ee\u5b50\u56ed\u4e5f\u5c31\u5341\u591a\u5206\u949f\u7684\u8def\u7a0b\uff0c\u53c8\u968f\u65f6\u968f\u5730\u6709\u8f66\u4f3a\u5019\u7740\uff0c\u5fc3\u7406\u4e0a\u7684\u5dee\u8ddd\u603b\u662f\u65e0\u6cd5\u62b9\u6389\u7684\uff0c\u4ed6\u8fdb\u4e1c\u5927\u8bfb\u4e66\uff0c\u8fd8\u662f\u8981\u5728\u4e1c\u5927\u9644\u8fd1\u627e\u4f4f\u5904\u6216\u8005\u76f4\u63a5\u4f4f\u5230\u4e1c\u5927\u7684\u7814\u7a76\u751f\u516c\u5bd3\u91cc\u4e5f\u662f\u4e0d\u9519\u7684\u9009\u62e9\u3002</p><p>\u3000\u3000\u8fd9\u91cc\u7684\u623f\u5b50\u4ee5\u540e\u5c31\u8981\u5f7b\u5e95\u7a7a\u4e0b\u6765\u4e86\uff0c\u5f20\u606a\u5fc3\u60f3\u53ea\u8981\u7238\u7238\u4e0d\u8c03\u5230\u5efa\u90ba\u6216\u7701\u91cc\u4efb\u804c\uff0c\u5bb6\u91cc\u4eba\u4e5f\u51e0\u4e4e\u6ca1\u6709\u4f4f\u8fdb\u6765\u7684\u673a\u4f1a\uff0c\u53ea\u662f\u4f5c\u4e3a\u843d\u811a\u5730\u5c31\u592a\u5962\u4f88\u6d6a\u8d39\u4e86\uff0c\u8fd8\u6ca1\u6709\u5fc3\u601d\u6536\u62fe\uff0c\u4e0a\u6b21\u4f4f\u8fdb\u6765\uff0c\u88ab\u8925\u90fd\u6f6e\u6e7f\u53d1\u9709\u4e86\uff0c\u77e5\u9053\u6881\u519b\u8ddf\u5b59\u4e3d\u8981\u8c03\u5230\u5efa\u90ba\u6765\uff0c\u8fd9\u623f\u5b50\u600e\u4e48\u5904\u7f6e\uff0c\u5f20\u606a\u8ddf\u5988\u5988\u6709\u5546\u91cf\u8fc7\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u5988\u4e00\u76f4\u5728\u8003\u8651\u4f60\u8ddf\u6881\u519b\u7ed3\u5a5a\u8981\u9001\u4ec0\u4e48\u4e1c\u897f\u7ed9\u4f60\u4eec\u624d\u597d\uff0c\u4f60\u4eec\u8981\u662f\u6253\u7b97\u5728\u5efa\u90ba\u957f\u4f4f\uff0c\u8fd9\u680b\u623f\u5b50\u5c31\u7ed9\u4f60\u4eec\u5f53\u5a5a\u623f\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u592a\u8d35\u91cd\u4e86\u2026\u2026\u201d\u6881\u519b\u62d2\u7edd\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u5988\u4e5f\u6015\u4f60\u4eec\u5acc\u8fd9\u662f\u65e7\u623f\u5b50\uff0c\u4f60\u4eec\u53e6\u9009\u4e00\u680b\u4e5f\u53ef\u4ee5\uff0c\u9009\u90e8\u597d\u8f66\u4e5f\u53ef\u4ee5\uff0c\u623f\u5b50\u8ddf\u8f66\u53ea\u80fd\u9009\u4e00\u6837\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u5988\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u6211\u5c31\u8d1f\u8d23\u4f20\u8fbe\uff0c\u4f60\u4eec\u8981\u662f\u5ba2\u6c14\uff0c\u5979\u8001\u4eba\u5bb6\u5c31\u8981\u4eb2\u81ea\u8dd1\u8fc7\u6765\u7763\u529e\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u77e5\u9053\u6881\u519b\u4e0e\u5b59\u4e3d\u4e0d\u597d\u610f\u601d\u6536\u8fd9\u4e48\u91cd\u7684\u793c\uff0c\u5e2e\u4ed6\u4eec\u51b3\u5b9a\u4e0b\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f4f\u5bbe\u9986\u90fd\u662f\u8eab\u5355\u5f71\u53ea\u7684\uff0c\u4f60\u4eec\u5c0f\u4e24\u53e3\u8ddf\u7740\u522b\u4eba\u51d1\u4ec0\u4e48\u70ed\u95f9\uff1f\u642c\u8fc7\u6765\u4f4f\uff01\u6bcf\u4e2a\u661f\u671f\u627e\u4eba\u6536\u62fe\u4e00\u4e0b\uff0c\u4e5f\u4e0d\u662f\u5f88\u9ebb\u70e6\uff0c\u8fd9\u6837\u6211\u4eec\u6bcf\u56de\u5230\u5efa\u90ba\u5e02\uff0c\u591c\u91cc\u4e5f\u6709\u5403\u996d\u7684\u5730\u65b9\uff0c\u6ca1\u4eba\u9ad8\u5174\u6574\u5929\u5728\u996d\u5e97\u91cc\u5403\u996d\u3002\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u62ff\u957f\u8f88\u7684\u53e3\u543b\u5c06\u4e8b\u60c5\u5b9a\u4e0b\u6765\uff0c\u6881\u519b\u4e5f\u4e0d\u4fbf\u62d2\u7edd\uff0c\u4e5f\u4e0d\u5f00\u53e3\u8bf4\u7b54\u5e94\uff0c\u5c31\u7b97\u9ed8\u8ba4\u4e0b\u6765\u3002\u770b\u60c5\u5f62\uff0c\u5f20\u606a\u4e0e\u5f20\u77e5\u975e\u662f\u51b3\u5fc3\u628a\u4ed6\u4e22\u5728\u5efa\u90ba\u53d1\u5c55\u3002\u5bb6\u91cc\u7684\u610f\u601d\u65e2\u7136\u90fd\u5b9a\u5a5a\u4e86\uff0c\u6392\u65e5\u5b50\u4e5f\u8be5\u7ed3\u5a5a\u4e86\u3002\u4ed6\u4e0e\u5b59\u4e3d\u4ece\u5927\u5b66\u5c31\u5f00\u59cb\u8c08\u604b\u7231\uff0c\u4e00\u76f4\u6ca1\u6709\u5206\u5f00\u8fc7\uff0c\u4e5f\u6ca1\u6709\u8981\u5206\u5f00\u7684\u5ff5\u5934\uff0c\u5bf9\u4ed6\u4eec\u6765\u8bf4\uff0c\u65e9\u5a5a\u665a\u5a5a\u662f\u65e0\u6240\u8c13\u7684\uff0c\u53ea\u662f\u4e0d\u4f1a\u8003\u8651\u592a\u65e9\u8981\u5b69\u5b50\u3002\u6bd5\u7adf\u73b0\u5728\u90fd\u8fd8\u5e74\u8f7b\uff0c\u81ea\u5df1\u624d\u4e8c\u5341\u4e94\uff0c\u5b59\u4e3d\u4e5f\u4e0d\u8fc7\u4e8c\u5341\u56db\uff0c\u4e00\u5207\u90fd\u8981\u4ee5\u4e8b\u4e1a\u4e3a\u91cd\uff1b\u4e0d\u8fc7\u8fd9\u4e00\u70b9\u8ddf\u5bb6\u91cc\u4e5f\u662f\u77db\u76fe\u91cd\u91cd\uff0c\u518d\u8bf4\u5427\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u611f\u6168\u6881\u519b\u3001\u5f20\u5955\u4e24\u4eba\u6240\u53d7\u5230\u7684\u5f85\u9047\u5dee\u8ddd\uff0c\u8fd9\u4e3b\u8981\u662f\u6881\u683c\u73cd\u5bf9\u4ed6\u4e24\u4e2a\u54e5\u54e5\u5bb6\u7684\u5c0f\u5b69\u5b50\u989d\u5916\u7684\u75bc\u7231\uff0c\u9526\u6e56\u8fd9\u4e48\u5927\u7684\u5bb6\u4ea7\uff0c\u6881\u683c\u73cd\u4e0d\u7ba1\u516c\u53f8\u7684\u4e8b\u60c5\uff0c\u5e2e\u886c\u4e24\u4e2a\u54e5\u54e5\u5bb6\u8fd8\u662f\u4e0d\u9057\u4f59\u529b\u7684\u3002\u4ed6\u4e00\u76f4\u60f3\u627e\u5f20\u606a\u8bf4\u5f20\u5955\u7684\u4e8b\u60c5\uff0c\u8fd8\u4e00\u76f4\u6ca1\u6709\u627e\u5230\u673a\u4f1a\u3002</p><p>\u3000\u3000\u5f85\u5b59\u4e3d\u5230\u53a8\u623f\u5e2e\u9a6c\u6d77\u9f99\u7684\u5fd9\uff0c\u5f20\u77e5\u975e\u501f\u6545\u8bf4\u9053\uff1a\u201c\u5f20\u5955\u5728\u5317\u5173\u7684\u7eba\u7ec7\u5382\u5012\u662f\u86ee\u597d\u7684\uff0c\u5230\u5382\u91cc\u624d\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u6b63\u8d76\u4e0a\u4ed6\u5e08\u5085\u8df3\u69fd\u5230\u522b\u7684\u5382\u53bb\uff0c\u5382\u957f\u5c31\u8ba9\u4ed6\u5f53\u4e0a\u673a\u4fee\u9886\u73ed\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u770b\u7740\u5c0f\u53d4\u7684\u773c\u795e\u95ea\u70c1\uff0c\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u8981\u662f\u6211\u4eec\u5e2e\u4ed6\u5b89\u6392\uff0c\u4ed6\u9762\u5b50\u80fd\u4e0d\u80fd\u63a5\u53d7\uff1f\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u542c\u5f20\u606a\u8bed\u6c14\u677e\u52a8\uff0c\u5fc3\u91cc\u4e00\u559c\uff0c\u8bf4\u767d\u4e86\uff0c\u5bf9\u8001\u5927\u5f20\u77e5\u5fae\u4e00\u5bb6\uff0c\u5c31\u5f20\u606a\u7684\u6028\u6c14\u6700\u91cd\uff0c\u8001\u4e8c\u5f20\u77e5\u884c\u8fd8\u6709\u6881\u683c\u73cd\u751a\u81f3\u90fd\u89c9\u5f97\u5f20\u606a\u8fd9\u5c0f\u5b50\u7684\u6028\u6c14\u592a\u91cd\u4e86\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u5bf9\u8001\u5927\u5f20\u77e5\u5fae\u4e00\u5bb6\u4e5f\u6ca1\u6709\u597d\u611f\uff0c\u5728\u8fd9\u65b9\u9762\u5c31\u6ca1\u6709\u529d\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u8bf4\u9053\uff1a\u201c\u4ed6\u4e5f\u9047\u5230\u4e0d\u5c11\u4e8b\uff0c\u8fd9\u4e9b\u4e1c\u897f\u5e94\u8be5\u80fd\u770b\u5f00\u4e86\uff0c\u6211\u4e0a\u56de\u627e\u4ed6\u559d\u4e86\u6b21\u9152\uff0c\u6c89\u7a33\u591a\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5c0f\u53d4\u8ddf\u5f20\u5955\u6709\u8fc7\u4ea4\u6d41\u5c31\u884c\uff0c\u5f20\u606a\u8f6c\u5934\u770b\u7740\u8bb8\u9e3f\u4f2f\uff0c\u8bf4\u9053\uff1a\u201c\u8bb8\u8001\u5e08\uff0c\u8fd9\u4e48\u591a\u4eba\u91cc\u4f60\u6700\u8f9b\u82e6\uff0c\u8ba9\u5f20\u5955\u7ed9\u4f60\u5dee\u4f7f\u5dee\u4f7f\uff0c\u600e\u4e48\u6837\uff1f\u201d\u9047\u8fc7\u632b\u6298\u3001\u6709\u8fc7\u53cd\u601d\u7684\u4eba\uff0c\u624d\u80fd\u6df1\u523b\u7406\u89e3\u8bb8\u9e3f\u4f2f\u609f\u900f\u7684\u90a3\u4e00\u5957\uff0c\u8ba9\u9510\u610f\u8fdb\u53d6\u3001\u8840\u6c14\u65b9\u521a\u7684\u6881\u519b\u53bb\u7ed9\u8bb8\u9e3f\u4f2f\u5f53\u52a9\u624b\u90fd\u672a\u5fc5\u5408\u9002\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u5c06\u8bb8\u9e3f\u4f2f\u5f53\u6210\u5bfc\u5e08\u7c7b\u7684\u4eba\u7269\u6765\u5c0a\u91cd\uff0c\u4e0d\u7136\u4e5f\u4e0d\u53ef\u80fd\u8bf7\u8bb8\u9e3f\u4f2f\u5230\u72ee\u5b50\u56ed\u522b\u5885\u6765\u5403\u996d\uff0c\u8fd8\u8981\u9a6c\u6d77\u9f99\u51fa\u53bb\u4e70\u83dc\u3002\u542c\u5f20\u606a\u5b89\u6392\u5f20\u5955\u7ed9\u8bb8\u9e3f\u4f2f\u5f53\u52a9\u624b\uff0c\u5f20\u77e5\u975e\u5c31\u77e5\u9053\u8fd9\u5c0f\u5b50\u662f\u771f\u6b63\u5bf9\u5f20\u5955\u51b0\u91ca\u524d\u5acc\u4e86\u3002</p><p>\u3000\u3000\u5012\u662f\u62c5\u5fc3\u8bb8\u9e3f\u4f2f\u62d2\u7edd\uff0c\u5f20\u77e5\u975e\u5728\u8bb8\u9e3f\u4f2f\u5f00\u53e3\u4e4b\u524d\uff0c\u5c06\u8001\u5927\u5f20\u77e5\u5fae\u4e00\u5bb6\u8fc7\u53bb\u4e00\u5e74\u591a\u6765\u53d1\u751f\u7684\u4e8b\u60c5\u8bf4\u4e86\u51fa\u6765\uff0c\u4e5f\u77e5\u9053\u6881\u519b\u4e4b\u524d\u5bf9\u5f20\u5955\u5fc3\u6709\u4ecb\u8482\uff0c\u5e0c\u671b\u80fd\u5316\u89e3\u4ed6\u5bf9\u5f20\u5955\u7684\u4ecb\u8482\uff0c\u6bd5\u7adf\u9752\u6625\u5e74\u5c11\uff0c\u8c01\u4e0d\u66fe\u6709\u72af\u9519\u8bef\u7684\u65f6\u5019\uff1f\u5f53\u7136\uff0c\u5f20\u606a\u8fd9\u5c0f\u5b50\u53ef\u4ee5\u4e0d\u8981\u62ff\u4e00\u822c\u4eba\u7684\u8303\u7574\u91cc\u8003\u8651\u3002</p><p>\u3000\u3000\u201c\u8fd9\u6837\u5b50\u554a\u2026\u2026\u201d\u8bb8\u9e3f\u4f2f\u70b9\u70b9\u5934\uff0c\u201c\u4ed6\u8981\u613f\u610f\u7ed9\u6211\u5dee\u4f7f\uff0c\u6211\u5012\u662f\u4e0d\u4ecb\u610f\u7684\u3002\u201d\u4ed6\u7684\u90a3\u4e00\u5957\uff0c\u5fc5\u987b\u8981\u5bf9\u4eba\u6027\u6709\u6df1\u523b\u7684\u4f53\u4f1a\uff0c\u624d\u80fd\u7406\u89e3\uff0c\u6bd5\u7adf\u4e0d\u662f\u8c01\u7684\u609f\u6027\u90fd\u80fd\u8ddf\u5f20\u606a\u8fd9\u602a\u80ce\u6bd4\u7684\uff0c\u8981\u662f\u5f20\u5955\u6ca1\u6709\u7ecf\u5386\u8fc7\u632b\u6298\u3001\u6ca1\u6709\u5403\u8fc7\u82e6\u5934\u3001\u53c8\u80fd\u4e0b\u8fd9\u4e48\u5927\u51b3\u5fc3\u505a\u51fa\u5e38\u4eba\u4e0d\u80fd\u505a\u7684\u51b3\u5b9a\uff0c\u8bb8\u9e3f\u4f2f\u662f\u4e0d\u4f1a\u6559\u4ed6\u4ec0\u4e48\u4e1c\u897f\u7684\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u5012\u662f\u6ca1\u6709\u60f3\u5230\u4e8b\u60c5\u8fd9\u4e48\u5bb9\u6613\u5c31\u89e3\u51b3\u4e86\uff0c\u4e4b\u524d\u8fd8\u601d\u524d\u987e\u540e\u5bfb\u601d\u7740\u600e\u4e48\u8ddf\u5f20\u606a\u5f00\u53e3\u624d\u5408\u9002\uff0c\u5927\u6982\u662f\u5f20\u5955\u5bf9\u90a3\u4e2a\u5973\u5b69\u5b50\u4e0d\u5f03\u4e0d\u79bb\u80fd\u6253\u52a8\u4eba\u5fc3\u5427\u3002</p><p>\u3000\u3000\u90a3\u4e00\u591c\uff0c\u5f20\u606a\u53ef\u662f\u96be\u5f97\u559d\u7684\u4e0d\u9192\u4eba\u4e8b\u56de\u53bb\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5b59\u4e3d\u4e0e\u9a6c\u6d77\u9f99\u5728\u53a8\u623f\u91cc\u51c6\u5907\uff0c\u5f20\u606a\u5728\u5ba2\u5385\u91cc\u5c06\u4eca\u5929\u53d1\u751f\u7684\u4e8b\u60c5\u8bf4\u7ed9\u8bb8\u9e3f\u4f2f\u8ddf\u5c0f\u53d4\u542c\u3002\u4eca\u5929\u8fd9\u4e00\u5929\u53d1\u751f\u7684\u4e8b\u60c5\u4e5f\u4e0d\u5c11\uff0c\u7cbe\u5178\u5730\u4ea7\u603b\u8f7d\u6797\u96ea\u8fd8\u60f3\u6f14\u4e00\u51fa\u597d\u620f\uff0c\u53ef\u89c1\u5979\u5bf9\u5b66\u5e9c\u5df7\u6539\u9020\u9879\u76ee\u5e76\u6ca1\u6709\u7518\u5fc3\u4e22\u624b\uff0c\u81f3\u5c11\u8bf4\u6881\u5b50\u662f\u7ed3\u4e0b\u4e86\u3002\u5b59\u9759\u6aac\u679d\u751f\u51fa\u6765\u7684\u4e8b\u60c5\u4e0d\u8fc7\u662f\u5c0f\u63d2\u66f2\u7f62\u4e86\uff0c\u4e0d\u8fc7\u8fd9\u4e2a\u5c0f\u63d2\u66f2\u8bf4\u4e0d\u5b9a\u4f1a\u6709\u4ee4\u4eba\u671f\u5f85\u7684\u6f14\u5316\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u4e2b\u5934\u6709\u610f\u601d\u554a\u2026\u2026\u201d\u8bb8\u9e3f\u4f2f\u542c\u4e86\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u5012\u662f\u4e5f\u5f88\u6b3a\u5f85\u4f60\u54ea\u4e00\u5929\u7ed9\u4eba\u6253\u6210\u732a\u5934\u718a\u8138\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4f4e\u5934\u7b11\u4e86\u7b11\uff0c\u53c8\u8ba4\u771f\u7684\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u7684\u5904\u4e8b\u98ce\u683c\u6bd5\u7adf\u8ddf\u522b\u4eba\u4e0d\u540c\uff0c\u53ef\u4ee5\u8bf4\u662f\u6321\u4e86\u522b\u4eba\u7684\u8d22\u8def\uff0c\u6211\u8fd8\u662f\u6709\u4e9b\u62c5\u5fc3\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6709\u62c5\u5fc3\u662f\u5e94\u8be5\u7684\uff0c\u201d\u8bb8\u9e3f\u4f2f\u8bf4\u9053\uff0c\u201c\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u5728\u5efa\u90ba\u5e02\u6709\u7f57\u541b\u3001\u738b\u7ef4\u5747\u652f\u6301\uff0c\u770b\u8d77\u6765\u662f\u5927\u9f0e\u5df2\u5b9a\uff0c\u4f46\u662f\u4ec5\u8fd9\u6761\u7ebf\u662f\u975e\u5e38\u5355\u8584\u7684\uff1b\u8fd8\u8981\u8003\u8651\u7f57\u541b\u62ff\u6a61\u6811\u56ed\u8ba1\u5212\u6253\u538b\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u7684\u56e0\u7d20\u3002\u7f57\u541b\u662f\u4e0b\u6765\u9540\u4e00\u5c42\u91d1\u7684\uff0c\u968f\u65f6\u53ef\u80fd\u8fdb\u6b65\u8c03\u79bb\u5efa\u90ba\uff0c\u90a3\u65f6\u6a61\u6811\u56ed\u8ba1\u5212\u5728\u5efa\u90ba\u5e02\u7684\u6839\u57fa\u7b80\u76f4\u5c31\u8ddf\u5efa\u6210\u6d6e\u6c99\u4e0a\u4e00\u6837\u4e0d\u7262\u9760\u2026\u2026\u73b0\u5728\u5efa\u90ba\u5e02\u770b\u6765\u4e07\u4f17\u4e00\u5fc3\u6c42\u7740\u9526\u6e56\u6765\u6295\u8d44\uff0c\u5f53\u9526\u6e56\u5de8\u989d\u6295\u8fdb\u6765\uff0c\u7ed9\u5957\u5728\u5efa\u90ba\uff0c\u4ed6\u4eec\u7684\u6001\u5ea6\u81ea\u7136\u4f1a\u6709\u5fae\u5999\u7684\u53d8\u5316\uff0c\u6211\u60f3\u5efa\u90ba\u5e02\u91cc\u4e5f\u4e0d\u4f1a\u771f\u6b63\u5c31\u6709\u5fc3\u7eb5\u5bb9\u7ef5\u6e56\u4e00\u5bb6\u72ec\u5927\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5e76\u4e0d\u80fd\u5c06\u6700\u7ec8\u7684\u5e0c\u671b\u5bc4\u6258\u5728\u7f57\u541b\u3001\u738b\u7ef4\u5747\u7684\u8eab\u4e0a\uff1b\u60c5\u5f62\u6240\u6709\u53d8\u5316\uff0c\u6597\u4e89\u6076\u52a3\uff0c\u987e\u5bb6\u90fd\u968f\u65f6\u6709\u53ef\u80fd\u5c06\u59da\u6587\u76db\u8c03\u51fa\u5efa\u90ba\u62bd\u8eab\u4e8b\u5916\u3002\u6a61\u6811\u56ed\u8ba1\u5212\u8981\u5728\u5efa\u90ba\u624e\u4e0b\u6839\u57fa\uff0c\u4e5f\u662f\u9700\u8981\u4e00\u6bb5\u65f6\u95f4\u7684\uff0c\u5728\u8fd9\u6bb5\u65f6\u95f4\u91cc\uff0c\u8981\u4fdd\u8bc1\u4e0d\u53d7\u7c7b\u540c\u4e8e\u7f57\u541b\u8c03\u51fa\u5efa\u90ba\u4e4b\u7c7b\u7684\u5076\u7136\u56e0\u7d20\u5e72\u6270\uff0c\u6211\u4eec\u8fd8\u8981\u591a\u505a\u4e00\u4e9b\u4e8b\u60c5\u3002\u201d</p><p>\u3000\u3000\u201c\u4ece\u5b98\u573a\u7684\u89d2\u5ea6\u6765\u8bf4\uff0c\u6211\u770b\u4e0d\u5230\u575a\u5b9e\u7684\u57fa\u7840\uff0c\u4e00\u5f20\u5bc6\u96c6\u7684\u7f51\u80fd\u63d0\u4f9b\u76f8\u5bf9\u7a33\u5b9a\u7684\u57fa\u7840\uff0c\u4eca\u5929\u7684\u4e8b\u60c5\uff0c\u4f60\u4e5f\u8bb8\u5f88\u5feb\u5c31\u80fd\u611f\u53d7\u5230\u90a3\u5f20\u7f51\u7684\u5a01\u529b\uff0c\u201d\u8bb8\u9e3f\u4f2f\u8bf4\u9053\uff0c\u201c\u8981\u662f\u6709\u8fd9\u4e48\u4e00\u5f20\u7f51\u5b58\u5728\uff0c\u4f60\u662f\u4e0d\u662f\u5df2\u7ecf\u4e0b\u51b3\u5fc3\u6495\u7834\u5b83\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u7834\u4e0d\u7acb\u554a\uff0c\u201d\u5f20\u606a\u89c1\u8bc6\u8fc7\u5b66\u5e9c\u5df7\u6539\u9020\u9879\u76ee\u843d\u5165\u7cbe\u5178\u5730\u4ea7\u56ca\u4e2d\u4e4b\u540e\u7684\u60c5\u52bf\u6f14\u53d8\uff0c\u8981\u771f\u6709\u4e00\u5f20\u7f51\u5b58\u5728\uff0c\u90a3\u5df2\u7ecf\u5c06\u4e1c\u534e\u533a\u7684\u67d0\u4e9b\u4eba\u7269\u4e0e\u7cbe\u5178\u5730\u4ea7\u8054\u7ed3\u6210\u4e00\u8d77\u4e86\uff0c\u201c\u6211\u4e0d\u80fd\u8ba9\u4e00\u4e9b\u6709\u5a01\u80c1\u7684\u4eba\u5b89\u5fc3\u7684\u6f5c\u85cf\u5728\u80cc\u540e\u9759\u5f85\u65f6\u673a\u2026\u2026\u201d\u9ad8\u771f\u3001\u6797\u96ea\u4e5f\u5df2\u7ecf\u4e0e\u8c22\u5bb6\u642d\u4e0a\u7ebf\u4e86\uff0c\u4ed6\u4eec\u90a3\u4e00\u5f20\u7f51\u4e5f\u662f\u8d8a\u7ec7\u8d8a\u5bc6\u4e86\u3002</p><p>\u3000\u3000\u201c\u771f\u662f\u4e0d\u4f11\u4e0d\u6b62\u7684\u7ea0\u7f20\uff0c\u201d\u8bb8\u9e3f\u4f2f\u611f\u6168\u4e86\u4e00\u58f0\uff0c\u201c\u9526\u6e56\u8d8a\u662f\u58ee\u5927\u8d8a\u662f\u4f1a\u53d1\u73b0\u5bf9\u65b9\u7684\u7f51\u7ec7\u5f97\u8d8a\u5bc6\u3002\u82e5\u662f\u8054\u4fe1\u611f\u53d7\u5230\u9526\u6e56\u7684\u5a01\u80c1\uff0c\u4f60\u8bf4\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u8ddf\u79d1\u738b\u7ea0\u7ed3\u5230\u4e00\u8d77\u53bb\uff1f\u80af\u5b9a\u4f1a\u7684\u3002\u5c31\u7b97\u8054\u4fe1\u4e0d\u627e\u79d1\u738b\uff0c\u79d1\u738b\u4e5f\u4f1a\u4e3b\u52a8\u627e\u8054\u4fe1\uff0c\u9526\u6e56\u7ed9\u4ed6\u4eec\u7684\u538b\u529b\u8d8a\u5927\uff0c\u4ed6\u4eec\u7684\u7f51\u8d8a\u662f\u4f1a\u7d27\u5bc6\u3002\u8096\u745e\u6c11\u7684\u8d44\u6599\u6211\u770b\u8fc7\uff0c\u5e94\u8be5\u662f\u4e00\u4e2a\u5389\u5bb3\u89d2\u8272\uff0c\u80cc\u540e\u8fd8\u7ad9\u7740\u4e00\u7fa4\u987d\u56fa\u4e0d\u5316\u7684\u5b98\u50da\u2026\u2026\u4f60\u554a\uff0c\u6700\u597d\u8fd8\u662f\u62b1\u7740\u2018\u4e0e\u4eba\u6597\u5176\u4e50\u65e0\u7a77\u2019\u7684\u4e50\u89c2\u5fc3\u6001\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u9526\u6e56\u8981\u81ea\u4e3b\u7684\u53d1\u5c55\uff0c\u65e5\u540e\u9047\u5230\u963b\u529b\u4f1a\u66f4\u5927\uff0c\u6211\u4e5f\u77e5\u9053\u4e00\u52b3\u6c38\u9038\u662f\u4e0d\u53ef\u80fd\u7684\u3002\u4e0d\u8fc7\u4ed6\u4eec\u7ed3\u7f51\uff0c\u6211\u4eec\u4e5f\u7ec7\u7f51\uff0c\u5c31\u8ddf\u4e24\u53ea\u8f9b\u52e4\u7684\u8718\u86db\u4e00\u6837\uff0c\u770b\u8c01\u7ec7\u5f97\u66f4\u5bc6\u66f4\u5feb\u4e86\u3002\u5efa\u90ba\u8fd9\u8fb9\uff0c\u6211\u4eec\u6ca1\u6709\u6839\u57fa\uff0c\u53ea\u80fd\u5148\u626f\u7834\u4ed6\u4e00\u9053\u53e3\u5b50\uff0c\u6211\u4eec\u518d\u7ec7\u7f51\u2026\u2026\u7f57\u541b\u73b0\u5728\u8981\u4f9d\u91cd\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u6211\u4eec\u8fd8\u53ef\u4ee5\u501f\u4ed6\u7684\u864e\u5a01\u2026\u2026\u8fd9\u51e0\u5929\uff0c\u8bb8\u8001\u5e08\u5c31\u7559\u5728\u5efa\u90ba\u6b47\u4e00\u6b47\u817f\u2026\u2026\u201d</p><p>\u3000\u3000\u996d\u83dc\u505a\u597d\uff0c\u6e29\u4e86\u4e00\u58f6\u9152\uff0c\u8fb9\u5403\u8fb9\u804a\uff0c\u5f20\u606a\u5c06\u5728\u5efa\u90ba\u5e02\u79cd\u79cd\u53ef\u4ee5\u642d\u4e0a\u7ebf\u7684\u4eba\u8109\u4e0e\u8bb8\u9e3f\u4f2f\u53c8\u68b3\u7406\u4e86\u4e00\u904d\uff0c\u8fd8\u6709\u5305\u62ec\u53f6\u5bb6\u5728\u5efa\u90ba\u7684\u4eba\u8109\u3002</p><p>\u3000\u3000\u53f6\u5bb6\u4e5f\u662f\u51b3\u5fc3\u5c06\u76db\u946b\u7684\u6839\u57fa\u624e\u5728\u5efa\u90ba\uff0c\u53f6\u5efa\u658c\u7684\u8001\u5bb6\u662f\u5efa\u90ba\uff0c\u4f46\u662f\u53f6\u5bb6\u51e0\u4e2a\u4ed5\u9014\u4eba\u7269\u5374\u90fd\u4e0d\u662f\u5728\u4e1c\u6d77\u7701\u5185\u6210\u957f\u8d77\u6765\u4e86\uff0c\u53f6\u5bb6\u5728\u5efa\u90ba\u5e02\u7684\u6839\u57fa\u4e5f\u5f88\u6d6e\u6d45\uff0c\u6ca1\u6709\u5927\u7684\u5229\u76ca\u51b2\u7a81\uff0c\u5efa\u90ba\u5e02\u91cc\u4f1a\u7ed9\u53f6\u5bb6\u7684\u9762\u5b50\uff0c\u6709\u4e86\u5927\u7684\u5229\u76ca\u51b2\u7a81\uff0c\u5c31\u5f88\u96be\u8bf4\u4e86\uff0c\u4e4b\u524d\u53f6\u5efa\u658c\u62ff\u4e0d\u4e0b\u5b66\u5e9c\u5df7\u6539\u9020\u9879\u76ee\uff0c\u4e5f\u662f\u8fd9\u4e2a\u539f\u56e0\u3002</p><p>\u3000\u3000\u65e2\u7136\u8981\u5728\u5efa\u90ba\u7ed3\u7f51\uff0c\u90a3\u5c31\u5c06\u53f6\u5bb6\u7684\u4eba\u8109\u4e5f\u8003\u8651\u8fdb\u53bb\uff0c\u4e5f\u65b9\u4fbf\u4e24\u5bb6\u7684\u52bf\u529b\u5728\u5efa\u90ba\u5e02\u91cc\u771f\u6b63\u7684\u878d\u4e3a\u8363\u8fb1\u4e0e\u5171\u7684\u4e00\u4f53\u3002</p></article>"}}}]);
