(window.webpackJsonp=window.webpackJsonp||[]).push([[578],{601:function(p){p.exports={chapterTitle:null,title:"\u7b2c578\u7ae0 \u6570\u5b57\u957f\u5eca\u6784\u60f3",content:"<article><h2>\u7b2c578\u7ae0 \u6570\u5b57\u957f\u5eca\u6784\u60f3</h2><p>\u3000\u3000\u9999\u6e2f\u7684\u96e8\u5f88\u5927\uff0c\u9999\u6e2f\u8fd9\u4e24\u5929\u4e00\u76f4\u5728\u4e0b\u96e8\uff0c\u65f6\u5927\u65f6\u5c0f\u7684\uff0c\u6ca1\u6709\u6b62\u6b47\u8fc7\u3002\u665a\u6674\u5979\u4eec\u6628\u5929\u5148\u4e58\u98de\u673a\u8fd4\u56de\u5185\u5730\uff0c\u5f20\u606a\u62d6\u4e86\u4e24\u5929\uff0c\u4eca\u5929\u5374\u662f\u4e0e\u5b59\u9759\u6aac\u4e00\u8d77\u4e58\u98de\u673a\u8fd4\u56de\u5efa\u90ba\u3002\u5085\u4fca\u5012\u662f\u8ddf\u665a\u6674\u5979\u4eec\u4e00\u8d77\u8d70\u7684\uff0c\u6bd5\u7adf\u8981\u966a\u4e24\u4e2a\u5c0f\u4e2b\u5934\u3002</p><p>\u3000\u3000\u98de\u673a\u722c\u5230\u4e91\u5c42\u4e4b\u4e0a\uff0c\u5c31\u4eff\u4f5b\u51b2\u51fa\u9999\u6e2f\u6e7f\u6f09\u6f09\u7684\u6c14\u6c1b\uff0c\u5b59\u9759\u6aac\u6e05\u79c0\u59a9\u5a9a\u7684\u5750\u5728\u5ea7\u4f4d\u4e0a\uff0c\u773c\u775b\u95ed\u7740\u4f11\u606f\uff0c\u957f\u957f\u6311\u8d77\u7684\u776b\u6bdb\u8f7b\u8f7b\u7684\u98a4\u52a8\u7740\uff0c\u53cc\u81c2\u73af\u62b1\u5728\u5706\u8038\u7684\u80f8\u524d\uff0c\u7a7f\u7740\u767d\u8272\u540a\u5e26\u886b\uff0c\u88f8\u9732\u51fa\u6765\u7684\u808c\u80a4\u6709\u7740\u4eba\u5fc3\u817b\u6da6\u7684\u767d\uff0c\u53ea\u770b\u5979\u7cbe\u81f4\u7684\u9762\u5bb9\u4e0e\u80f8\u53e3\u7ec6\u5fae\u5e73\u7f13\u7684\u547c\u5438\uff0c\u4f3c\u4e4e\u5904\u4e8e\u4e00\u4e2a\u9759\u5bc2\u65e0\u58f0\u7684\u4e1d\u5e7d\u6697\u89d2\u843d\u91cc\u3002</p><p>\u3000\u3000\u771f\u662f\u5947\u602a\u4e86\uff0c\u5b59\u9759\u6aac\u6b64\u65f6\u7ed9\u4eba\u7684\u611f\u89c9\uff0c\u4e0e\u5979\u7741\u5f00\u773c\u775b\u65f6\u7684\u7280\u5229\u4e0e\u660e\u9510\u622a\u7136\u4e0d\u540c\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c6\u7ebf\u843d\u5230\u62a5\u7aef\uff0c\u4ed6\u624b\u5934\u5806\u7740\u539a\u539a\u4e00\u645e\u5728\u9999\u6e2f\u80fd\u6536\u96c6\u7684\u62a5\u7eb8\u3002</p><p>\u3000\u3000\u8fbe\u62c9\u65af\u4ee5\u53ca\u5fb7\u514b\u8428\u65af\u5dde\u5f53\u5730\u5a92\u4f53\u5bf9\u7231\u8fbe\u4e0e\u5fb7\u4eea\u7b7e\u7f72\u7684\u90a3\u4e00\u4efd\u6280\u672f\u5f15\u8fdb\u534f\u8bae\u5927\u8086\u6e32\u67d3\uff0c\u79f0\u4e4b\u4e3a\u8fd9\u662f\u901a\u8baf\u9886\u57df\u8fd1\u5e74\u6765\u6700\u4e3a\u91cd\u8981\u7684\u4e00\u9879\u6280\u672f\u8f6c\u79fb\uff0c\u751a\u81f3\u547c\u5401\u653f\u5e9c\u5bf9\u8be5\u9879\u534f\u8bae\u8fdb\u884c\u66f4\u52a0\u4e25\u683c\u7684\u5ba1\u67e5\uff0c\u76ee\u7684\u8981\u7981\u6b62\u8fd9\u9879\u6280\u672f\u8f6c\u8ba9\u3002</p><p>\u3000\u3000\u5185\u5730\u4e03\u5341\u591a\u5bb6\u5a92\u4f53\u5728\u62a5\u9053\u5609\u4fe1\u5b9e\u4e1a\u589e\u53d1\u65b0\u80a1\u6536\u8d2d\u7231\u8fbe\u7535\u5b50\u789f\u673a\u8d44\u4ea7\u7684\u540c\u65f6\uff0c\u62a5\u9053\u4e86\u7231\u8fbe\u96c6\u56e2\u4ece\u5fb7\u4eea\u5f15\u8fdb\u624b\u673a\u5236\u9020\u6280\u672f\u7684\u6d88\u606f\uff1b\u60df\u6709\u90ae\u7535\u90e8\u7684\u6bcf\u5468\u7535\u5b50\u901a\u8baf\u6307\u51fa\u7231\u8fbe\u96c6\u56e2\u6b64\u6b21\u5f15\u8fdb\u7684\u624b\u673a\u5236\u9020\u6280\u672f\uff0c\u53ea\u662f\u5373\u5c06\u7ed9\u79fb\u52a8\u901a\u4fe1\u8bbe\u5907\u5546\u6dd8\u6c70\u7684\u4e8c\u6d41\u6280\u672f\u5236\u9020\u800c\u5df2\uff0c\u5e76\u4e14\u4e0d\u5305\u542b\u6700\u6838\u5fc3\u7684\u624b\u673a\u57fa\u5e26\u82af\u7247\u6280\u672f\uff0c\u5e76\u4e0d\u503c\u5f97\u56fd\u5185\u5916\u5a92\u4f53\u5927\u8086\u5ba3\u626c\u3002</p><p>\u3000\u3000\u8fd9\u662f\u90ae\u7535\u90e8\u7684\u4e00\u6b21\u53cd\u51fb\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u6b64\u5374\u6781\u4e3a\u6b23\u6170\uff0c\u90ae\u7535\u90e8\u6ca1\u6709\u6c89\u9ed8\u7740\u7b49\u5230\u4ed6\u4eec\u9012\u4ea4\u7533\u8bf7\u4e4b\u65f6\u7c97\u66b4\u7684\u62d2\u7edd\uff0c\u800c\u9009\u62e9\u5728\u5a92\u4f53\u53d1\u8868\u6279\u8bc4\u6587\u7ae0\u6765\u6253\u51fb\u7231\u8fbe\u96c6\u56e2\u6b64\u6b21\u5f15\u8fdb\u6280\u672f\u7684\u5f71\u54cd\uff0c\u8fd9\u672c\u8eab\u5c31\u662f\u6781\u4e3a\u660e\u663e\u7684\u53d8\u5316\u3002</p><p>\u3000\u3000\u660e\u773c\u4eba\u4e00\u770b\u5c31\u77e5\u9053\u662f\u90ae\u7535\u90e8\u5185\u90e8\u520a\u53d1\u7684\u8fd9\u7bc7\u6587\u7ae0\u752b\u9732\u9762\u5c31\u906d\u5230\u5404\u65b9\u4e25\u5389\u7684\u6279\u8bc4\uff0c\u7535\u5b50\u5de5\u4e1a\u90e8\u7535\u5b50\u7814\u7a76\u9662\u7684\u4e13\u5bb6\u66f4\u6781\u5c3d\u595a\u843d\u4e4b\u80fd\uff0c\u6beb\u4e0d\u7559\u60c5\u7684\u6307\u51fa\u90ae\u7535\u90e8\u4e0b\u5c5e\u7684\u8bbe\u5907\u5546\u6240\u638c\u63e1\u7684\u6700\u9876\u5c16\u6280\u672f\u4e0d\u8fc7\u662f\u5728\u8fdb\u53e3\u7684\u901a\u4fe1\u8bbe\u5907\u4e0a\u8d34\u81ea\u5bb6\u7684\u5546\u6807\u800c\u5df2\u3002</p><p>\u3000\u3000\u4e00\u5207\u5e76\u4e0d\u662f\u7231\u8fbe\u96c6\u56e2\u7684\u52a8\u4f5c\u6709\u591a\u60f9\u706b\uff0c\u800c\u662f\u90ae\u7535\u90e8\u957f\u671f\u4ee5\u6765\u7684\u884c\u653f\u5784\u65ad\uff0c\u4f7f\u5f97\u5404\u65b9\u79ef\u6028\u751a\u6df1\uff1b\u62e5\u62a4\u53cd\u5bf9\u8005\u6240\u53cd\u5bf9\u7684\uff0c\u53ea\u662f\u6e38\u620f\u7684\u89c4\u5219\u800c\u5df2\uff0c\u66f4\u4f55\u51b5\u8fd9\u6bb5\u65f6\u95f4\u6765\uff0c\u6c11\u8425\u7ecf\u6d4e\u5730\u4f4d\u7684\u95ee\u9898\u5df2\u7ecf\u8ba8\u8bba\u591f\u900f\u5f7b\u4e86\uff0c\u7231\u8fbe\u96c6\u56e2\u4ece\u4e0a\u81ea\u4e0b\u7684\u83b7\u5f97\u8206\u8bba\u7684\u62e5\u62a4\uff0c\u5e76\u975e\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u8096\u664b\u6210\u7b49\u4eba\u4e0e\u5fb7\u4eea\u7b7e\u7f72\u534f\u8bae\u4e4b\u540e\u5c31\u4e58\u98de\u673a\u76f4\u98de\u5317\u4eac\uff0c\u5728\u5317\u4eac\u4e3e\u884c\u7684\u8bb0\u8005\u62db\u5f85\u4f1a\u4e0a\u516c\u5e03\u6b64\u6b21\u5f15\u8fdb\u6280\u672f\u7684\u7ec6\u8282\uff0c\u5e76\u8868\u793a\u5c06\u5728\u8bbe\u7acb\u5728\u7f8e\u56fd\u7845\u8c37\u7684\u7814\u53d1\u5b9e\u9a8c\u57fa\u5730ESS\u516c\u53f8\u6210\u7acb\u4e13\u95e8\u7684\u7814\u53d1\u5c0f\u7ec4\u6d88\u5316\u6b64\u6b21\u4ece\u5fb7\u4eea\u5f15\u8fdb\u7684\u624b\u673a\u5236\u9020\u6280\u672f\uff0c\u5728\u65e5\u672c\u4e1c\u4eac\u3001\u97e9\u56fd\u6c49\u57ce\u4ee5\u53ca\u5317\u4eac\u6210\u7acb\u7684\u4ea7\u54c1\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\uff0c\u4e5f\u90fd\u662f\u4e3a\u5f00\u53d1\u6700\u65b0\u578b\u7684\u624b\u673a\u4ea7\u54c1\u6280\u672f\u800c\u51c6\u5907\uff0c\u5728\u5efa\u90ba\u5b9e\u65bd\u7684\u6a61\u6811\u56ed\u8ba1\u5212\u91cc\u8fd8\u5c06\u6210\u7acb\u4e00\u5bb6\u56fd\u5bb6\u7ea7\u7684\u79fb\u52a8\u901a\u8baf\u7ec8\u7aef\u8bbe\u5907\u6280\u672f\u5b9e\u9a8c\u3002\u4e8e\u6b21\u65e5\uff0c\u7231\u8fbe\u96c6\u56e2\u6b63\u5f0f\u5411\u90ae\u7535\u90e8\u6b63\u5f0f\u9012\u4ea4\u624b\u673a\u751f\u4ea7\u8bb8\u53ef\u7684\u7533\u8bf7\uff0c\u90ae\u7535\u90e8\u7ed9\u4e88\u66a7\u6627\u4e0d\u660e\u7684\u56de\u5e94\uff1a\u9700\u8981\u53ec\u5f00\u90e8\u59d4\u4f1a\u8bae\u4e13\u95e8\u8ba8\u8bba\u6b64\u4e8b\u3002</p><p>\u3000\u3000\u7535\u5b50\u5de5\u4e1a\u90e8\u4e0b\u5c5e\u4f01\u4e1a\uff0c\u4e00\u76f4\u4ee5\u6765\u90fd\u79ef\u6781\u7b79\u5212\u4e0a\u624b\u673a\u9879\u76ee\u7684\u4e1c\u5174\u901a\u4fe1\u3001\u666e\u8baf\u96c6\u56e2\u540c\u65e5\u5411\u90ae\u7535\u90e8\u63d0\u51fa\u624b\u673a\u751f\u4ea7\u8bb8\u53ef\u7684\u7533\u8bf7\uff0c\u4e0e\u7231\u8fbe\u96c6\u56e2\u5f62\u6210\u96c6\u4f53\u903c\u5bab\u4e4b\u52bf\u3002</p><p>\u3000\u3000\u5386\u53f2\u4e0a\uff0c\u56fd\u5185\u9996\u6279\u53d1\u653e\u7684\u624b\u673a\u751f\u4ea7\u724c\u7167\u4e5f\u662f\u56db\u5f20\uff0c\u4f46\u662f\u65f6\u95f4\u8981\u62d6\u5ef6\u5230\u4e00\u5e74\u4e4b\u540e\uff0c\u73b0\u5728\u7231\u8fbe\u96c6\u56e2\u3001\u4e1c\u5174\u901a\u4fe1\u3001\u666e\u8baf\u96c6\u56e2\uff0c\u518d\u52a0\u4e0a\u90ae\u7535\u90e8\u5185\u90e8\u65b0\u7ec4\u5efa\u7684\u8bbe\u5907\u5546\u8054\u4fe1\u96c6\u56e2\uff0c\u521a\u597d\u4e5f\u662f\u56db\u5bb6\uff0c\u5c31\u770b\u90ae\u7535\u90e8\u6709\u6ca1\u6709\u80c6\u91cf\u62b5\u4f4f\u5404\u65b9\u7684\u79ef\u6028\u53ea\u5c06\u624b\u673a\u724c\u7167\u53d1\u653e\u7ed9\u8054\u4fe1\u96c6\u56e2\u3002</p><p>\u3000\u3000\u4eca\u5929\u4e0a\u5348\uff0c\u5efa\u90ba\u5e02\u653f\u5e9c\u516c\u5e03\u540d\u4e3a\u201c\u6570\u5b57\u957f\u5eca\u6784\u60f3\u201d\u7684\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u65b0\u6218\u7565\u7684\u7ec6\u8282\uff1a\u5305\u62ec\u501f\u52a9\u71d5\u5f52\u6e56\u533a\u9ad8\u6821\u7fa4\u4e0e\u65b0\u6d66\u5927\u5b66\u57ce\u7684\u6280\u672f\u8d44\u6e90\u4f18\u52bf\uff0c\u63d0\u524d\u4fee\u5efa\u65e9\u5c31\u89c4\u5212\u8bbe\u8ba1\u4e86\u597d\u4e9b\u5e74\u5374\u56e0\u7f3a\u4e4f\u8d44\u91d1\u4e00\u76f4\u672a\u52a8\u5de5\u4fee\u5efa\u7684\u5c0f\u6c5f\u4e8c\u6865\uff0c\u5c06\u65b0\u6d66\u9ad8\u65b0\u533a\u3001\u71d5\u5f52\u6e56\u5730\u533a\u3001\u65b0\u6d66\u5927\u5b66\u57ce\u7b49\u5730\u8854\u63a5\u6210\u5bbd\u516b\u516c\u91cc\u3001\u957f\u4e8c\u5341\u516c\u91cc\u7684\u957f\u5eca\u5730\u5e26\uff0c\u5efa\u90ba\u5e02\u653f\u5e9c\u5c06\u5728\u4e94\u5e74\u5185\u6295\u8d44\u4e00\u767e\u4ebf\u6539\u5584\u8fd9\u4e00\u5730\u533a\u7684\u57fa\u7840\u8bbe\u65bd\uff0c\u4ee5\u9002\u5408\u9ad8\u65b0\u6280\u672f\u4f01\u4e1a\u5728\u6b64\u5730\u7684\u53d1\u5c55\u3002\u201c\u6570\u5b57\u957f\u5eca\u6784\u60f3\u201d\u5c06\u5728\u71d5\u5f52\u6e56\u5730\u533a\u4ee5\u6a61\u6811\u56ed\u8ba1\u5212\u4e2d\u4e24\u4e2a\u56fd\u5bb6\u7ea7\u7684\u5fae\u7535\u5b50\u6280\u672f\u57fa\u7840\u5b9e\u9a8c\u5ba4\u4e0e\u79fb\u52a8\u901a\u8baf\u7ec8\u7aef\u8bbe\u5907\u6280\u672f\u5b9e\u9a8c\u5ba4\u4ee5\u53ca\u4e1c\u6d77\u5927\u5b66\u3001\u5efa\u90ba\u90ae\u7535\u5b66\u9662\u7b49\u9ad8\u6821\u7684\u56fd\u5bb6\u7ea7\u9ad8\u65b0\u6280\u672f\u5b9e\u9a8c\u5ba4\u7684\u5efa\u8bbe\u4e3a\u5148\u5bfc\uff0c\u4ee5\u6a61\u6811\u56ed\u8ba1\u5212\u4e2d\u603b\u989d\u9ad8\u8fbe\u4e09\u4ebf\u7684\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u4e3a\u9a71\u52a8\u529b\uff0c\u8bbe\u7acb\u4ee5\u6280\u672f\u7814\u53d1\u4e3a\u4e3b\u4f53\u7684\u9ad8\u65b0\u4ea7\u4e1a\u65b0\u56ed\u533a\uff0c\u65b0\u56ed\u533a\u7684\u524d\u671f\u6838\u5fc3\u5c31\u662f\u6a61\u6811\u56ed\u8ba1\u5212\u4e2d\u7684\u521b\u4e1a\u56ed\u533a\u3002\u5efa\u90ba\u5e02\u653f\u5e9c\u51b3\u8bae\u5c06\u4e4b\u524d\u4ece\u7231\u8fbe\u96c6\u56e2\u83b7\u5f97\u4e94\u5343\u4e07\u95f2\u7f6e\u8d44\u4ea7\u2014\u2014\u5173\u505c\u4e4b\u540e\u7684\u4e3d\u4e30\u5370\u67d3\u5382\u4e0e\u6f02\u67d3\u5382\u5382\u623f\u53ca\u9644\u5c5e\u571f\u5730\u6210\u4e86\u95f2\u7f6e\u8d44\u4ea7\u2014\u2014\u8f6c\u8ba9\u91d1\u6089\u6570\u8fd4\u56de\u7ed9\u6a61\u6811\u56ed\u8ba1\u5212\u7528\u4e8e\u7f51\u7edc\u5149\u7ea4\u7b49\u57fa\u7840\u5de5\u7a0b\u7684\u6295\u8d44\u3002</p><p>\u3000\u3000\u201c\u6570\u5b57\u957f\u5eca\u6784\u60f3\u201d\u7262\u7262\u7684\u4e0e\u7231\u8fbe\u96c6\u56e2\u7684\u6a61\u6811\u56ed\u8ba1\u5212\u6346\u7ed1\u5728\u4e00\u8d77\uff0c\u4e5f\u662f\u9996\u6b21\u5728\u5730\u65b9\u7ecf\u6d4e\u6218\u7565\u4e2d\uff0c\u8ba9\u6c11\u8425\u4f01\u4e1a\u5360\u636e\u5148\u5bfc\u5730\u4f4d\u3002</p><p>\u3000\u3000\u5c06\u6d77\u5dde\u5e02\u57ce\u5357\u7535\u5b50\u5de5\u4e1a\u56ed\u3001\u60e0\u5c71\u5e02\u6ee8\u6c5f\u5f00\u53d1\u65b0\u533a\u3001\u5efa\u90ba\u5e02\u6570\u5b57\u957f\u5eca\u6784\u60f3\u7684\u89c4\u5212\u6574\u5408\u5230\u4e00\u8d77\uff0c\u4e1c\u6d77\u7701\u653f\u5e9c\u540c\u65f6\u516c\u5e03\u5c0f\u6c5f\u6cbf\u6c5f\u5730\u533a\u9ad8\u65b0\u6280\u672f\u4ea7\u4e1a\u5e26\u53d1\u5c55\u8ba1\u5212\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u62a5\u7eb8\u53e0\u597d\u653e\u5230\u4e00\u65c1\uff0c\u901a\u8fc7\u8237\u7a97\u770b\u7740\u4e0b\u9762\u7684\u96e8\u4e91\uff0c\u7ed9\u4e2a\u5f88\u539a\u5b9e\u7684\u611f\u89c9\uff0c\u8fd9\u65f6\u5019\uff0c\u4e1c\u6d77\u7701\u653f\u5e9c\u3001\u5efa\u90ba\u5e02\u653f\u5e9c\u7b97\u662f\u7ed9\u6346\u4e0a\u9526\u6e56\u7684\u6218\u8f66\u4e86\uff1b\u6709\u5730\u65b9\u653f\u5e9c\u7684\u575a\u5b9a\u652f\u6301\uff0c\u5728\u624b\u673a\u724c\u7167\u7684\u4e89\u593a\u6218\u4e2d\uff0c\u7231\u8fbe\u96c6\u56e2\u5c31\u4e0d\u4f1a\u5f31\u4e8e\u7535\u5b50\u5de5\u4e1a\u90e8\u4e0b\u5c5e\u7684\u4e24\u5bb6\u4f01\u4e1a\u2026\u2026</p><p>\u3000\u3000\u5b59\u9759\u6aac\u7741\u5f00\u773c\u775b\uff0c\u7f8e\u4e3d\u7684\u7738\u5149\u900f\u7740\u6e05\u7eaf\u800c\u59a9\u5a9a\u7684\u98ce\u60c5\uff0c\u521a\u521a\u7761\u9192\u7684\u6a21\u6837\uff0c\u89c1\u5f20\u606a\u51dd\u7738\u770b\u7740\u7a97\u5916\uff0c\u4e0d\u6653\u5f97\u6709\u4ec0\u4e48\u597d\u770b\u7684\u4e1c\u897f\uff0c\u8eab\u5b50\u63a2\u8fc7\u53bb\uff0c\u8138\u51e0\u4e4e\u8981\u8d34\u5230\u5fae\u51c9\u7684\u8237\u7a97\u4e0a\uff1a\u201c\u5728\u770b\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8eab\u5b50\u51e0\u4e4e\u5c31\u4f0f\u5728\u81ea\u5df1\u7684\u5927\u817f\u4e0a\uff0c\u5934\u53d1\u6b6a\u5230\u4e00\u8fb9\uff0c\u9732\u51fa\u96ea\u767d\u4e30\u6da6\u7684\u4fee\u957f\u9888\u8116\u4e0e\u67d4\u5ae9\u9999\u80a9\uff0c\u9f3b\u7aef\u4f20\u6765\u7c7b\u4f3c\u767e\u5408\u5e7d\u5bc2\u7684\u4f53\u9999\uff0c\u5f20\u606a\u4e3a\u514d\u78b0\u5230\u5b59\u9759\u6aac\u7684\u8eab\u4f53\uff0c\u4e24\u53ea\u80f3\u81c2\u53ea\u6709\u4e3e\u8d77\u6765\uff0c\u6297\u8bae\u8bf4\uff1a\u201c\u4f60\u8fd9\u6837\u6211\u600e\u4e48\u5750\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5b59\u9759\u6aac\u8eab\u4f53\u59ff\u52bf\u672a\u53d8\uff0c\u5934\u6b6a\u8fc7\u6765\u770b\u5f20\u606a\uff0c\u624d\u610f\u8bc6\u5230\u4e24\u4eba\u8eab\u4f53\u6328\u5f97\u592a\u8fd1\uff0c\u5c31\u50cf\u6402\u5728\u4e00\u8d77\u4f3c\u7684\uff0c\u4fcf\u8138\u96be\u5f97\u7684\u7ea2\u4e86\u4e00\u4e0b\uff0c\u7f29\u4e86\u56de\u53bb\uff0c\u8eab\u4f53\u5750\u6b63\u5634\u5df4\u53c8\u786c\u4e86\u8d77\u6765\uff0c\u201c\u8ba9\u4f60\u6709\u673a\u4f1a\u5360\u4e9b\u4fbf\u5b9c\uff0c\u8fd8\u5570\u55e6\u4e2a\u4e0d\u505c\uff01\u201d</p><p>\u3000\u3000\u201c\u5f97\uff0c\u7b97\u6211\u8bf4\u9519\u8bdd\u4e86\uff0c\u4f60\u73b0\u5728\u6574\u4e2a\u8eab\u90fd\u8db4\u6211\u5927\u817f\u4e0a\u6765\uff0c\u6211\u90fd\u6ca1\u6709\u610f\u89c1\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8f7b\u54fc\u4e86\u4e00\u58f0\uff0c\u62ff\u7740\u9ed1\u8272\u7684\u9488\u7ec7\u5916\u5957\u7a7f\u4e0a\u3002\u5df2\u7ecf\u80fd\u770b\u89c1\u5c0f\u6c5f\u7684\u6a2a\u4e98\u5728\u4e0b\u9762\u7684\u571f\u5730\u4e0a\uff0c\u542c\u5929\u6c14\u9884\u62a5\uff0c\u5efa\u90ba\u5e02\u7684\u6c14\u6e29\u6bd4\u9999\u6e2f\u8981\u4f4e\u4e94\u516d\u5ea6\uff0c\u5b59\u9759\u6aac\u53ea\u989d\u5916\u51c6\u5907\u4e86\u4e00\u4ef6\u534a\u8896\u7684\u9488\u7ec7\u886b\uff0c\u8fd8\u662f\u5c06\u5ae9\u85d5\u4f3c\u7684\u5c0f\u81c2\u9732\u51fa\u6765\u3002</p><p>\u3000\u3000\u98de\u673a\u964d\u843d\u5230\u673a\u573a\u4e0a\uff0c\u53d1\u73b0\u5efa\u90ba\u4e5f\u662f\u5728\u4e0b\u96e8\uff0c\u4eca\u5929\u7684\u6c14\u6e29\u76f8\u5f53\u4f4e\uff0c\u5f20\u606a\u5c06\u5916\u5957\u8131\u7ed9\u5b59\u9759\u6aac\uff0c\u4ed6\u53ea\u7a7f\u4e00\u4ef6\u77ed\u8896T\u6064\u53cc\u81c2\u62b1\u5728\u80f8\u524d\u5fcd\u7740\u5ba4\u5916\u7684\u5fae\u5bd2\u3002\u4e0e\u9a6c\u6d77\u9f99\u901a\u8fc7\u7535\u8bdd\uff0c\u5357\u90ca\u5feb\u901f\u51fa\u4e86\u8f66\u7978\uff0c\u8f66\u5b50\u7ed9\u5835\u4e86\u4e00\u4f1a\u513f\uff0c\u8fd8\u8981\u8fc7\u4e00\u4f1a\u513f\u624d\u80fd\u8fc7\u6765\u63a5\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u201c\u4f60\u5f88\u4f1a\u4f53\u8d34\u5973\u5b69\u5b50\u554a\u2026\u2026\u201d\u5b59\u9759\u6aac\u7a7f\u7740\u5e73\u5e95\u7684\u978b\u5b50\uff0c\u62ac\u5934\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u6709\u6ca1\u6709\u611f\u52a8\u4e00\u4e0b\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u5462\uff1f\u201d\u5b59\u9759\u6aac\u6ca1\u5fc3\u6ca1\u80ba\u7684\u8bf4\u9053\uff0c\u201c\u672c\u59d1\u5a18\u6536\u5230\u7684\u6bb7\u52e4\u6bd4\u8fd9\u5938\u5f20\u5343\u500d\u7684\u90fd\u6709\uff0c\u8981\u662f\u8f7b\u6613\u5c31\u611f\u52a8\uff0c\u4e0d\u6653\u5f97\u8981\u5931\u8eab\u591a\u5c11\u6b21\u4e86\uff01\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u90a3\u628a\u6211\u7684\u8863\u670d\u8fd8\u7ed9\u6211\u2026\u2026\u201d\u5f20\u606a\u4e5f\u5f88\u6ca1\u6709\u7537\u5b50\u6c14\u6168\u7684\u8981\u5c06\u5916\u5957\u4ece\u5b59\u9759\u6aac\u8eab\u4e0a\u6252\u4e0b\u6765\uff0c\u5b59\u9759\u6aac\u54af\u54af\u7684\u7b11\u7740\u8eb2\u5f00\uff0c\u6323\u624e\u4e86\u4e00\u4f1a\u513f\uff0c\u53c8\u6402\u7740\u5f20\u606a\u7684\u80f3\u818a\u8bf4\uff0c\u201c\u8fd8\u7ed9\u4f60\u4e00\u4e9b\u6e29\u6696\u603b\u53ef\u4ee5\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e48\u591a\u5bf9\u4f60\u732e\u6bb7\u52e4\u7684\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u6311\u4e00\u4e2a\u505a\u7537\u670b\u53cb\uff1f\u201d\u5f20\u606a\u95ee\u5b59\u9759\u6aac\u3002</p><p>\u3000\u3000\u201c\u9047\u4e0d\u5230\u6211\u559c\u6b22\u7684\u554a\uff01\u201d\u5b59\u9759\u6aac\u8bf4\u9053\uff0c\u4f38\u51fa\u811a\u62ff\u5149\u4eae\u7684\u7ea2\u8272\u978b\u9762\u53bb\u63a5\u6ef4\u4e0b\u6765\u7684\u96e8\u5e18\uff0c\u201c\u9047\u4e0d\u5230\u81ea\u5df1\u7684\u559c\u6b22\uff0c\u603b\u4e0d\u80fd\u8ba9\u81ea\u5df1\u7684\u7231\u60c5\u5ec9\u4ef7\u5904\u7406\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u559c\u6b22\u54ea\u79cd\uff1f\u201d\u5f20\u606a\u597d\u5947\u7684\u95ee\uff0c\u5b59\u9759\u6aac\u6240\u8868\u73b0\u51fa\u6765\u7684\u6027\u683c\uff0c\u4e0e\u4ed6\u8ba4\u8bc6\u7684\u5176\u4ed6\u5973\u5b69\u5b50\u5dee\u5f02\u592a\u5927\u4e86\uff0c\u4ed6\u603b\u597d\u5947\u4e0d\u540c\u6027\u683c\u7684\u5973\u5b69\u5b50\u5185\u5fc3\u5728\u60f3\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u201c\u90a3\u5929\u8981\u662f\u7ed9\u62e6\u5728\u6e9c\u51b0\u573a\u8ba9\u4eba\u5bb6\u6253\u4e00\u987f\uff0c\u4f1a\u4e0d\u4f1a\u751f\u6c14\uff1f\u201d</p><p>\u3000\u3000\u90fd\u4e09\u56db\u4e2a\u661f\u671f\u524d\u7684\u4e8b\u60c5\u4e86\uff0c\u8fd8\u4ee5\u4e3a\u5b59\u9759\u6aac\u65e9\u5c31\u5c06\u8fd9\u4ef6\u4e8b\u629b\u4e4b\u8111\u540e\u5462\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u6bd4\u8d77\u8fd9\u4e2a\uff0c\u6211\u66f4\u4ecb\u610f\u7f51\u7403\u88d9\u4e0b\u8fd8\u7a7f\u7740\u725b\u4ed4\u88e4\u8fd9\u4ef6\u4e8b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u771f\u8fd9\u4e48\u4ecb\u610f\uff1f\u201d\u5b59\u9759\u6aac\u5ffd\u53c8\u773c\u795e\u7eaf\u771f\u7684\u770b\u7740\u5f20\u606a\uff0c\u201c\u4e0d\u662f\u8bf4\u94a2\u7ba1\u821e\u4e5f\u53ef\u80fd\u66ff\u4ee3\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u4e48\u773c\u795e\u7eaf\u771f\u7684\u8ba8\u8bba\u7f51\u7403\u88d9\u6216\u8005\u94a2\u7ba1\u821e\u7684\u95ee\u9898\uff0c\u5f20\u606a\u5fc3\u5934\u7830\u7136\u98a4\u52a8\u4e86\u4e00\u4e0b\uff0c\u6487\u5634\u7b11\u4e86\u4e00\u4e0b\uff0c\u5c06\u5fc3\u91cc\u8fd9\u5f02\u6837\u7684\u60c5\u7eea\u63a9\u9970\u6389\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u90fd\u8d56\u6389\u4e86\uff0c\u6211\u8fd8\u6709\u4ec0\u4e48\u529e\u6cd5\u597d\u60f3\uff1f\u201d</p><p>\u3000\u3000\u770b\u7740\u524d\u9762\u5929\u7a7a\u5782\u4e0b\u7684\u96e8\u5e18\uff0c\u5b59\u9759\u6aac\u4f38\u51fa\u811a\u63a5\u7740\u96e8\u6ef4\uff0c\u770b\u7740\u6676\u83b9\u5254\u900f\u7684\u96e8\u6ef4\u5728\u5149\u4eae\u7684\u7ea2\u8272\u978b\u5c16\u4e0a\u6ed1\u5f00\uff0c\u770b\u7740\u5f20\u606a\u7ebf\u6761\u5206\u660e\u7684\u4fa7\u8138\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u65f6\u5019\u501f\u4f60\u7684\u80a9\u8180\u9760\u4e00\u9760\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u6709\u522b\u7684\u60f3\u6cd5\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u201d\u5f20\u606a\u4fa7\u8fc7\u5934\u770b\u7740\u5b59\u9759\u6aac\u7684\u773c\u795e\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u4fdd\u4e0d\u5b9a\u4f1a\u6709\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u5c31\u7b97\u4e86\uff0c\u201d\u5b59\u9759\u6aac\u62bf\u7740\u5634\uff0c\u4e00\u4ed8\u5acc\u5f20\u606a\u5c0f\u6c14\u7684\u6a21\u6837\uff0c\u201c\u6ca1\u60f3\u5230\u4f60\u8fd9\u4e48\u5c0f\u6c14\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e58\u8f66\u5230\u5e02\u533a\uff0c\u5e02\u533a\u7684\u96e8\u66f4\u5927\uff0c\u8fd9\u65f6\u5019\u7a9d\u57281978\u542c\u7740\u96e8\u58f0\u5c0f\u914c\u4e24\u676f\u6700\u662f\u9002\u5408\u3002</p><p>\u3000\u3000\u4f55\u5f26\u53ea\u5c06\u5f20\u606a\u5f53\u6210\u666e\u901a\u8ba4\u8bc6\u7684\u4eba\uff0c\u9664\u4e86\u7b2c\u4e00\u9762\u7684\u6df1\u523b\u5370\u8c61\uff0c\u51e0\u4e4e\u6ca1\u6709\u4e00\u6b21\u7684\u4ea4\u8c08\u662f\u8d85\u8fc7\u5341\u53e5\u8bdd\u7684\uff0c\u9664\u4e86\u57281978\uff0c\u78b0\u9762\u7684\u673a\u4f1a\u51e0\u4e4e\u662f\u6ca1\u6709\uff0c\u5e2e\u4ed6\u7aef\u8fc7\u6765\u9152\u6c34\uff0c\u4fbf\u53bb\u522b\u5904\u4e86\u3002</p><p>\u3000\u30001978\u53ea\u653e\u6c5f\u9edb\u513f\u7684\u6b4c\uff0c\u300a\u521d\u590f\u4e4b\u604b\u300b\uff0c\u58f0\u97f3\u6e05\u6f88\u800c\u6709\u4e9b\u4f4e\u9761\u7684\u610f\u5473\uff0c\u6b63\u9002\u8fd9\u6837\u96e8\u540e\u7684\u4e0b\u5348\uff0c\u6de1\u6de1\u7684\u601d\u7eea\u5728\u98d8\u8361\uff0c\u522b\u4eba\u4e00\u5b9a\u4e0d\u6653\u5f97\u5f20\u606a\u5728\u6000\u5ff5\u8fd8\u672a\u53d1\u751f\u8fc7\u3001\u6781\u53ef\u80fd\u4e0d\u4f1a\u53d1\u751f\u7684\u56de\u5fc6\u3002</p><p>\u3000\u3000\u9999\u6e2f\u4e4b\u884c\u4e5f\u53ea\u5306\u5306\u89c1\u4e86\u6c5f\u9edb\u513f\u4e00\u9762\uff0c\u5979\u4f5c\u4e3a\u7231\u8fbe\u5f71\u789f\u673a\u7684\u5f62\u8c61\u4ee3\u8868\u4eba\u51fa\u5e2d\u4e865\u67084\u65e5\u591c\u5728\u6e2f\u5c9b\u9999\u683c\u91cc\u62c9\u5927\u9152\u5e97\u7684\u7b54\u8c22\u9152\u4f1a\uff0c\u4e4b\u524d\u5c31\u53bb\u53f0\u6e7e\u4f5c\u65b0\u6b4c\u5ba3\u4f20\u4e86\uff0c\u542c\u8bf4\u662f\u5f88\u975e\u5e38\u7684\u8f9b\u82e6\u3002\u4e5d\u4e03\u5e74\uff0c\u5728\u5185\u5730\u7684\u4e92\u8054\u7f51\u4e0a\u8fd8\u770b\u4e0d\u5230\u53f0\u6e7e\u7684\u7efc\u827a\u8282\u76ee\uff0c\u81ea\u7136\u4e5f\u4f53\u4f1a\u4e0d\u5230\u5728\u53f0\u6e7e\u4f5c\u5ba3\u4f20\u662f\u4f55\u7b49\u7684\u8f9b\u82e6\u4e86\u3002</p><p>\u3000\u3000\u201c\u627f\u4f60\u5409\u8a00\u554a\uff0c\u6211\u771f\u662f\u8981\u5931\u4e1a\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u521a\u8981\u70b9\u70df\uff0c\u770b\u7740\u4ee4\u5c0f\u71d5\u7ad9\u5728\u4ed6\u9762\u524d\uff0c\u7b11\u4e86\u7b11\uff0c\u5148\u5c06\u9999\u70df\u70b9\u4e0a\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u65b0\u627e\u5de5\u4f5c\u8fd8\u6765\u5f97\u53ca\u554a\uff0c\u6216\u8005\u968f\u4fbf\u6253\u53d1\u534a\u5e74\u7684\u65f6\u95f4\uff0c\u5b66\u5e9c\u5df7\u6700\u5feb\u4e5f\u9700\u8981\u534a\u5e74\u624d\u80fd\u91cd\u5efa\u8d77\u6765\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u987e\u4e0d\u987e\u4f60\u9ad8\u4e2d\u751f\u7684\u5f62\u8c61\u2026\u2026\u201d\u5b59\u9759\u6aac\u8d70\u8fc7\u6765\u5c06\u5f20\u606a\u5634\u5507\u4e0a\u7684\u9999\u70df\u53d6\u4e0b\u6765\uff0c\u653e\u81ea\u5df1\u5634\u91cc\u62bd\u4e86\u4e00\u53e3\uff0c\u5374\u7ed9\u545b\u4e86\u4e00\u4e0b\uff0c\u76b1\u7740\u7709\u5934\u95ee\uff0c\u201c\u6709\u4ec0\u4e48\u597d\u5438\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u8ba9\u5bc2\u5bde\u71c3\u70e7\u6389\uff0c\u201d\u5f20\u606a\u5b09\u76ae\u7b11\u8138\u7684\u8bf4\uff0c\u201c\u8fd8\u6709\u4e00\u4e2a\u66f4\u6d6a\u6f2b\u7684\u8bf4\u6cd5\uff0c\u5c31\u662f\u5c06\u5bf9\u4f60\u7684\u601d\u5ff5\u63c9\u8fdb\u70df\u4e1d\u91cc\uff0c\u71c3\u70e7\u7740\u62bd\u8fdb\u80ba\u91cc\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u53ea\u53ef\u60dc\u4ed6\u5b09\u76ae\u7b11\u8138\u7684\u7b11\u7740\uff0c\u6beb\u65e0\u8ba9\u5973\u5b69\u5b50\u611f\u52a8\u7684\u6d6a\u6f2b\u6c14\u8d28\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ece\u5b59\u9759\u6aac\u624b\u91cc\u63a5\u8fc7\u70df\u6765\u6390\u7184\u6389\uff0c\u95ee\u4ee4\u5c0f\u71d5\uff1a\u201c\u4f60\u6709\u4ec0\u4e48\u6253\u7b97\u554a\uff1f\u201d\u6731\u5c0f\u519b\u90fd\u5de5\u4f5c\u5de5\u7cfb\u4ece\u7701\u653f\u5e9c\u529e\u516c\u5385\u8c03\u5230\u65b0\u6d66\u9ad8\u65b0\u533a\u6295\u8d44\u4fc3\u8fdb\u5c40\u7ed9\u59da\u6587\u76db\u5f53\u52a9\u624b\u4e86\uff0c\u4ee4\u5c0f\u71d5\u4e5f\u5c31\u968f\u5373\u77e5\u9053\u4e00\u4e9b\u5979\u539f\u672c\u4e0d\u6e05\u695a\u7684\u5185\u60c5\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u7b11\u7740\u8bf4\uff1a\u201c\u4e0d\u662f\u8fd8\u6ca1\u6709\u4e00\u4e2a\u534a\u6708\u624d\u6bd5\u4e1a\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u6653\u5f971978\u8fd8\u4f1a\u4e0d\u4f1a\u7ee7\u7eed\u5f00\u4e0b\u53bb\uff1f\u201d\u5b59\u9759\u6aac\u62c9\u4e86\u6905\u5b50\u5750\u5230\u5f20\u606a\u7684\u8eab\u8fb9\uff0c\u53c8\u62c9\u4e86\u4e00\u5f20\u6905\u5b50\u8ba9\u4ee4\u5c0f\u71d5\u5750\u4e0b\u6765\uff0c\u201c\u4e4b\u524d\u662f1978\u7684\u79df\u7ea6\u6ca1\u6709\u5230\u671f\uff0c\u6211\u59d0\u624d\u540c\u610f\u6211\u63a5\u624b\u7ee7\u7eed\u5f00\u4e0b\u53bb\uff0c\u73b0\u5728\u63d0\u524d\u7ed3\u675f\u79df\u7ea6\uff0c\u6211\u59d0\u4e00\u5b9a\u4e0d\u613f\u610f\u518d\u5f80\u91cc\u6254\u94b1\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e5f\u77e5\u9053\u662f\u6254\u94b1\u554a\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u5728\u201c\u6254\u201d\u5b57\u4e0a\u52a0\u91cd\u4e86\u8bed\u6c14\u3002</p><p>\u3000\u3000\u201c\u8fd9\u80fd\u602a\u6211\uff1f\u201d\u5b59\u9759\u6aac\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u201c\u6211\u59d0\u90fd\u6ca1\u6709\u8ba4\u771f\u7684\u5728\u505a\uff0c\u8981\u8bf4\u8d22\u52a1\u72b6\u51b5\uff0c\u6211\u63a5\u624b\uff0c\u53ef\u6bd4\u5979\u597d\u591a\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u8d22\u52a1\u72b6\u51b5\u8981\u6539\u89c2\u554a\uff0c\u201d\u5f20\u606a\u62ac\u5934\u60f3\u4e86\u60f3\uff0c\u201c\u7ed9\u4ed6\u4eec\u51e0\u4e2a\u5f00\u7684\u5de5\u8d44\uff0c\u90fd\u8981\u4e0b\u8c03\u4e00\u534a\uff0c\u9152\u6c34\u4ef7\u683c\u4e0d\u8c03\u6574\uff0c\u6709\u4e9b\u683c\u8c03\u4e0a\u7684\u8bb2\u7a76\u53ef\u6709\u53ef\u65e0\u7684\uff0c\u7edf\u7edf\u53bb\u9664\u6389\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53bb\u2026\u2026\u201d\u5b59\u9759\u6aac\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u201c\u6211\u5b81\u53ef\u6211\u7684\u96f6\u82b1\u94b1\u90fd\u8d34\u8fdb\u6765\uff0c\u4e5f\u4e0d\u4f1a\u5934\u9876\u524a\u5c16\u4e86\u94bb\u94b1\u773c\u91cc\u53bb\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bdd\u6ca1\u6709\u8bf4\u5b8c\uff0c\u5c31\u6328\u4e86\u5b59\u9759\u6aac\u4e00\u8bb0\u767d\u773c\uff0c\u4ed6\u8fd8\u60f3\u5efa\u8bae\u8ba9\u97f3\u4e50\u5b66\u9662\u7684\u5973\u5b69\u5b50\u8fc7\u6765\u8df3\u94a2\u7ba1\u7434\u5462\uff0c\u8bdd\u6ca1\u6709\u8bf4\u51fa\u53e3\uff0c\u5c31\u7ed9\u5c94\u5f00\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e16\u7eaa\u9526\u6e56\u5bf9\u5b66\u5e9c\u5df7\u91c7\u53d6\u56de\u8fc1\u4e0e\u8d37\u5e01\u8865\u5c1d\u76f8\u63a5\u5408\u7684\u76f8\u5bf9\u5bbd\u677e\u3001\u516c\u5e73\u7684\u62c6\u8fc1\u65b9\u5f0f\uff0c\u524d\u671f\u7684\u5de5\u4f5c\u8fdb\u5c55\u6ca1\u6709\u4ec0\u4e48\u5ef6\u8bef\uff0c\u90a3\u5e74\u5934\uff0c\u4e0d\u7ed9\u5f00\u53d1\u5546\u6572\u8bc8\u5c31\u7b97\u597d\u7684\uff0c\u6562\u6572\u8bc8\u5f00\u53d1\u5546\u4e5f\u662f\u6781\u4e3a\u7f55\u89c1\u3002</p><p>\u3000\u3000\u7edd\u5927\u591a\u6570\u5546\u94fa\u4e1a\u4e3b\u90fd\u5e0c\u671b\u56de\u8fc1\uff0c\u4e5f\u6709\u90e8\u5206\u5546\u94fa\u4e1a\u4e3b\u76f4\u63a5\u62ff\u8d70\u51e0\u5341\u4e07\u7684\u8865\u507f\u6b3e\u8d70\u4eba\u2014\u2014\u4e5d\u4e03\u5e74\uff0c\u4e09\u56db\u5341\u4e07\u90fd\u8981\u7b97\u5de8\u6b3e\u3002\u7531\u4e8e\u4e5d\u4e03\u5e74\u798f\u5229\u4f4f\u623f\u5236\u5ea6\u8fd8\u6ca1\u6709\u5f00\u59cb\u6539\u9769\uff0c\u5b66\u5e9c\u5df7\u91cc\u7684\u4f4f\u5b85\u79c1\u6709\u4ea7\u6743\u7684\u5f88\u5c11\uff0c\u591a\u4e3a\u4e1c\u6d77\u5927\u5b66\u7684\u6559\u804c\u5de5\u4f4f\u5b85\uff0c\u6700\u7ec8\u9700\u8981\u62ff\u51fa\u89c4\u5212\u7684\u516d\u680b\u9ad8\u5c42\u516c\u5bd3\u4e2d\u7684\u4e24\u680b\u8865\u507f\u7ed9\u4e1c\u6d77\u5927\u5b66\u505a\u9752\u5e74\u6559\u5e08\u516c\u5bd3\u3002</p><p>\u3000\u3000\u516d\u6708\u4e4b\u524d\uff0c\u5b66\u5e9c\u5df7\u4e0a\u7684\u5e97\u94fa\u90fd\u8981\u7ed3\u675f\u8425\u4e1a\u4e86\uff0c\u6574\u6761\u5b66\u5e9c\u5df7\u90fd\u5c01\u95ed\u8d77\u6765\u63a8\u5012\u91cd\u5efa\uff0c\u4f5c\u4e3a\u6570\u5b57\u957f\u5eca\u6784\u60f3\u7684\u7b2c\u4e00\u4e2a\u65b0\u5efa\u5de5\u7a0b\uff0c\u4e1c\u6d77\u5927\u5b66\u3001\u5efa\u90ba\u5e08\u8303\u5927\u5b66\u90fd\u8981\u7834\u8d22\u91cd\u5efa\u6bd7\u90bb\u5b66\u5e9c\u5df7\u7684\u4e1c\u5927\u95e8\u4e0e\u897f\u5927\u95e8\uff0c\u4fdd\u6301\u4e0e\u6539\u9020\u540e\u7684\u6574\u4f53\u5f62\u8c61\u4e00\u81f4\u3002</p><p>\u3000\u3000\u5e08\u5927\u5357\u95e8\u7684\u4e3d\u4e30\u5370\u67d3\u5382\u5728\u8bbe\u5907\u4eba\u5458\u6e05\u9664\u4e4b\u540e\u5df2\u7ecf\u7740\u624b\u6539\u5efa\u7684\u3002\u4e16\u7eaa\u9526\u6e56\u662f\u5f00\u53d1\u5546\uff0c\u5b8f\u8fd0\u5b9e\u4e1a\u662f\u5efa\u7b51\u5546\uff0c\u90fd\u662f\u4ece\u6d77\u5dde\u91cc\u62c9\u6765\u7684\u5de5\u7a0b\u961f\u4e0e\u7ba1\u7406\u56e2\u961f\uff1b\u7f51\u7edc\u5149\u7ea4\u7b49\u901a\u4fe1\u57fa\u7840\u5de5\u7a0b\uff0c\u4ece\u5efa\u90ba\u5e02\u5206\u5305\u7ed9\u4e13\u4e1a\u7684\u5de5\u7a0b\u516c\u53f8\uff0c\u90fd\u5df2\u7ecf\u5f00\u59cb\u5f00\u6316\u7535\u7f06\u6c9f\u4e86\u3002\u8fd9\u91cc\u7684\u5de5\u4f5c\u7531\u90b5\u81f3\u521a\u4eb2\u81ea\u4e3b\u6301\uff0c\u6881\u519b\u7ed9\u90b5\u81f3\u521a\u5f53\u526f\u624b\uff0c\u6709\u7740\u5c06\u65b0\u5149\u9020\u7eb8\u5382\u65e7\u5382\u6539\u9020\u6210\u996e\u98df\u5e7f\u573a\u7684\u7ecf\u9a8c\uff0c\u90b5\u81f3\u521a\u4e0e\u4ed6\u62c9\u8fc7\u6765\u7684\u56e2\u961f\u57fa\u672c\u4e0a\u80fd\u6478\u6e05\u5f20\u606a\u7684\u559c\u597d\u3002\u65e7\u5382\u6539\u9020\u5bb9\u6613\uff0c\u6e56\u5cb8\u6e05\u6de4\u662f\u5f52\u5c5e\u4e8e\u5e02\u653f\u5e9c\u5de5\u7a0b\u7684\uff0c\u5e38\u59d4\u526f\u5e02\u957f\u738b\u7ef4\u5747\u4eb2\u81ea\u5728\u6293\uff0c\u71d5\u5f52\u6e56\u6cbf\u5cb8\u7684\u91cd\u6c61\u67d3\u5382\u7edf\u7edf\u5173\u505c\uff0c\u5efa\u90ba\u5e02\u6c11\u4f17\u5bf9\u6b64\u62cd\u624b\u79f0\u5feb\u2014\u2014\u60df\u4e00\u8ba9\u90b5\u81f3\u521a\u5934\u75bc\u7684\uff0c\u5c31\u662f\u4ece\u54ea\u91cc\u627e\u90a3\u4e48\u591a\u7ea2\u6a61\u6811\u79fb\u690d\u8fc7\u6765\uff1f</p></article>"}}}]);
