(window.webpackJsonp=window.webpackJsonp||[]).push([[579],{602:function(p){p.exports={chapterTitle:null,title:"\u7b2c432\u7ae0 \u5f71\u789f\u673a\u5382\u5546\u7684\u76db\u4f1a",content:"<article><h2>\u7b2c432\u7ae0 \u5f71\u789f\u673a\u5382\u5546\u7684\u76db\u4f1a</h2><p>\u3000\u3000\u5dee\u4e0d\u591a\u5230\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u5f00\u59cb\u7684\u65f6\u95f4\uff0c\u8fd8\u6ca1\u89c1\u5f20\u606a\u90a3\u884c\u4eba\u6709\u8c01\u8fc7\u6765\uff0c\u8c22\u5251\u5357\u5728\u56de\u7b54\u8bb0\u8005\u8ffd\u95ee\u7684\u65f6\u5019\uff0c\u6709\u4e9b\u5fc3\u4e0d\u5728\u7109\u4e86\u3002\u8bb0\u8005\u5bf9\u6b64\u4e5f\u611f\u5230\u76f8\u5f53\u7684\u7591\u60d1\uff0c\u4e0d\u65ad\u6709\u4eba\u8dd1\u51fa\u53bb\u770b\uff0c\u4e5f\u4e0d\u65ad\u6709\u8bb0\u8005\u65e0\u5948\u7684\u653e\u5f03\uff0c\u5148\u53bb\u4f1a\u573a\uff0c\u514d\u5f97\u9519\u8fc7\u62db\u5546\u5927\u4f1a\u7684\u5f00\u573a\uff0c\u7a00\u7a00\u758f\u758f\u7684\uff0c\u6700\u540e\u53ea\u5269\u4e0b\u4e24\u5bb6\u62a5\u793e\u7684\u8bb0\u8005\u8fd8\u7559\u5728\u539f\u5730\u8be2\u95ee\u5218\u660e\u8f89\u4ece\u7231\u8fbe\u7535\u5b50\u8df3\u69fd\u5230\u79d1\u738b\u7535\u5668\u7684\u7ec6\u8282\uff0c\u4e0d\u8fc7\u5fc3\u601d\u8fd8\u662f\u5f88\u660e\u663e\u7684\u7559\u5728\u5927\u5802\u7684\u5165\u53e3\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u6b63\u60f3\u4f4e\u58f0\u95ee\u5218\u660e\u8f89\u662f\u600e\u4e48\u56de\u4e8b\u65f6\uff0c\u5c31\u770b\u89c1\u82cf\u6d25\u4e1c\u4e0e\u7231\u8fbe\u7535\u5b50\u7684\u54c1\u724c\u8fd0\u8425\u90e8\u7ecf\u7406\u5218\u6d77\u71d5\uff0c\u8fd8\u6709\u7231\u8fbe\u7535\u5b50\u7684\u8425\u9500\u4e2d\u5fc3\u526f\u603b\u76d1\u8d75\u6d77\u821f\u59d7\u59d7\u6765\u8fdf\uff0c\u5927\u5802\u91cc\u5df2\u7ecf\u5269\u4e0d\u4e0b\u4e0d\u591a\u51e0\u540d\u8bb0\u8005\u5728\uff0c\u4e5f\u6ca1\u6709\u4eba\u76ef\u7740\u767b\u8bb0\u6362\u724c\u5904\uff0c\u7b49\u82cf\u6d25\u4e1c\u4ed6\u4eec\u8d70\u8fc7\u53bb\uff0c\u624d\u6709\u4e00\u540d\u8bb0\u8005\u53cd\u5e94\u8fc7\u6765\uff0c\u4ed6\u4f9d\u7a00\u8bb0\u5f97\u82cf\u6d25\u4e1c\u5728\u5a92\u4f53\u4e0a\u4e25\u8083\u7684\u9732\u9762\uff0c\u8ffd\u8fc7\u53bb\uff0c\u82cf\u6d25\u4e1c\u505c\u4e0b\u6765\u6307\u4e86\u6307\u624b\u8868\uff0c\u793a\u610f\u5feb\u5230\u5f00\u573a\u65f6\u95f4\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u8bbf\u95ee\u7684\u8bdd\uff0c\u80fd\u4e0d\u80fd\u7b49\u5230\u62db\u5546\u5927\u4f1a\u7ed3\u675f\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u51e0\u540d\u8bb0\u8005\u867d\u7136\u8868\u793a\u8c05\u89e3\uff0c\u8ddf\u7740\u540e\u9762\u4e00\u8d77\u53bb\u4f1a\u573a\uff0c\u8fd8\u662f\u4e0d\u5931\u65f6\u673a\u63d0\u95ee\u9898\uff0c\u5728\u7b49\u7535\u68af\u65f6\uff0c\u6709\u4e00\u540d\u8bb0\u8005\u95ee\u9053\uff1a\u201c\u8d35\u516c\u53f8\u5e02\u573a\u90e8\u8d1f\u8d23\u4eba\u5218\u660e\u8f89\u5148\u751f\u5728\u8fd9\u4e2a\u5173\u952e\u8df3\u69fd\u5230\u7ade\u4e89\u516c\u53f8\uff0c\u82cf\u603b\u6709\u4ec0\u4e48\u60f3\u6cd5\uff1f\u201d</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u56de\u5934\u770b\u4e86\u8fd9\u8fb9\u4e00\u773c\uff0c\u8c22\u5251\u5357\u5012\u4e5f\u60f3\u77e5\u9053\u4ed6\u4f1a\u600e\u4e48\u56de\u7b54\u5a92\u4f53\uff0c\u8fd9\u65f6\u5019\u7535\u68af\u95e8\u6253\u5f00\uff0c\u82cf\u6d25\u4e1c\u4e0e\u8bb0\u8005\u4e00\u8d77\u8d70\u5230\u7535\u68af\u95e8\u91cc\uff0c\u8c22\u5251\u5357\u53c8\u5c06\u7535\u68af\u95e8\u64ac\u5f00\u7684\u51b2\u52a8\uff0c\u4ed6\u5f88\u60f3\u77e5\u9053\u5f20\u606a\u4e0e\u4ed6\u7684\u7231\u8fbe\u7535\u5b50\u7ba1\u7406\u5c42\u4eec\u5bf9\u8fd9\u4e8b\u6709\u591a\u5927\u7684\u53cd\u5e94\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u8d70\u5427\uff1f\u201d\u9648\u9759\u633d\u7740\u8c22\u5251\u5357\u7684\u624b\u81c2\uff0c\u5979\u4e5f\u5f88\u5947\u602a\u5f20\u606a\u3001\u8c22\u665a\u6674\u3001\u8fd8\u6709\u8bb8\u601d\u4e3a\u4f55\u6ca1\u6709\u51fa\u73b0\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\u4ed6\u4eec\u4e0d\u53c2\u52a0\u5417\uff1f\u201d\u8d75\u53f8\u660e\u6bd4\u8f83\u85cf\u4e0d\u4f4f\u5fc3\u601d\uff0c\u4ed6\u5728\u77e5\u9053\u4e8b\u60c5\u7684\u5c40\u90e8\u771f\u76f8\u4e4b\u524d\uff0c\u4ed6\u5bf9\u5f20\u606a\u662f\u53c8\u6068\u53c8\u6015\uff0c\u4ece\u8c22\u5251\u5357\u5634\u91cc\u77e5\u9053\u4e00\u4e9b\u771f\u76f8\u4e4b\u540e\uff0c\u5fc3\u91cc\u6240\u53d7\u5230\u7684\u51b2\u51fb\u4e0e\u9707\u8361\u5f88\u96be\u5f62\u5bb9\uff0c\u81f3\u5c11\u6068\u610f\u6ca1\u90a3\u4e48\u6df1\u4e86\uff0c\u8fd9\u662f\u5f88\u5947\u602a\u7684\u5fc3\u6001\uff0c\u6216\u8bb8\u89c9\u5f97\u7ed9\u5f20\u606a\u8fd9\u4e48\u6b3a\u8d1f\uff0c\u4e5f\u7b97\u4e0d\u4e0a\u5341\u5206\u7684\u4e22\u8138\u5427\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\u554a\uff0c\u201d\u8c22\u5251\u5357\u5480\u56bc\u7740\u8fd9\u4e2a\u540d\u5b57\uff0c\u201c\u4ed6\u662f\u6f5c\u85cf\u5728\u9634\u5f71\u4e0b\u7684\u6bd2\u86c7\uff0c\u80cc\u540e\u64cd\u63a7\u522b\u4eba\u4f3c\u4e4e\u662f\u4ed6\u6700\u5927\u7684\u4e50\u8da3\uff0c\u9732\u9762\u672a\u5fc5\u4f1a\uff0c\u6211\u60f3\u4ed6\u4e0d\u4f1a\u4e0d\u53c2\u52a0\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u8fd9\u53e5\u8bdd\u6ca1\u6709\u8bf4\u5b8c\uff0c\u5f20\u606a\u5c31\u51fa\u73b0\u4e86\uff0c\u5f20\u606a\u3001\u4e01\u69d0\u3001\u8c22\u665a\u6674\u3001\u8bb8\u601d\u56db\u4e2a\u4eba\u5728\u5927\u5385\u95e8\u53e3\uff0c\u6ca1\u6709\u62ff\u51fa\u9080\u8bf7\u51fd\u4e4b\u7c7b\u7684\u8bc1\u4ef6\u53bb\u524d\u53f0\u767b\u8bb0\u5904\u6362\u724c\uff0c\u800c\u662f\u76f4\u63a5\u62ff\u51fa\u80f8\u724c\u6302\u5728\u80f8\u524d\u901a\u8fc7\u5927\u5385\u5916\u7684\u8b66\u536b\u8d70\u4e86\u8fdb\u6765\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u770b\u4e0d\u5230\u80f8\u724c\u662f\u4ec0\u4e48\uff0c\u5fc3\u60f3\u5e94\u8be5\u662f\u7279\u9080\u5609\u5bbe\u4e4b\u7c7b\u7684\u8eab\u4efd\uff0c\u80fd\u6709\u8fd9\u4e9b\u80f8\u724c\uff0c\u8bf4\u660e\u4ed6\u4eec\u4e4b\u524d\u6709\u8ddf\u4e2d\u592e\u7535\u89c6\u53f0\u63a5\u89e6\u8fc7\uff0c\u8fd9\u4e5f\u662f\u4e0a\u4e00\u5c4a\u592e\u89c6\u5e7f\u544a\u6807\u738b\u4f01\u4e1a\u5e94\u4eab\u6709\u7684\u7279\u6743\u5427\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u8fd8\u7559\u5728\u8fd9\u91cc\uff1f\u201d\u5f20\u606a\u7279\u610f\u62d6\u5230\u6700\u540e\uff0c\u8003\u8651\u5230\u6700\u540e\u8fd8\u53ef\u80fd\u6709\u4e9b\u8bb0\u8005\u5019\u5728\u5927\u5802\u91cc\uff0c\u8ba9\u82cf\u6d25\u4e1c\u4ed6\u4eec\u5148\u5c06\u8bb0\u8005\u5f15\u8d70\u2014\u2014\u82cf\u6d25\u4e1c\u4ed6\u4eec\u672c\u6765\u5c31\u7b97\u7231\u8fbe\u7535\u5b50\u7684\u6b63\u5f0f\u4ee3\u8868\u2014\u2014\u8d70\u8fdb\u5927\u5385\uff0c\u5012\u6ca1\u6709\u60f3\u5230\u8c22\u5251\u5357\u3001\u9648\u9759\u3001\u5218\u660e\u8f89\u7b49\u79d1\u738b\u7535\u5668\u7684\u4eba\u90fd\u8fd8\u6ca1\u6709\u53bb\u4f1a\u573a\uff0c\u201c\u8be5\u4e0d\u4f1a\u521a\u5728\u8fd9\u91cc\u63a5\u53d7\u8bb0\u8005\u7684\u91c7\u8bbf\uff1f\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u89c1\u5f20\u606a\u7684\u773c\u795e\u6bd2\u8fa3\u7684\u6295\u5411\u5218\u660e\u8f89\uff0c\u5fc3\u60f3\uff1a\u5373\u4f7f\u6709\u6028\u6068\uff0c\u90a3\u4e5f\u662f\u5e94\u8be5\u7684\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u5df2\u7ecf\u7ed9\u8bb0\u8005\u8d28\u95ee\u5f0f\u7684\u5201\u96be\u6298\u817e\u4e86\u7b4b\u75b2\u529b\u5c3d\uff0c\u65e0\u8bba\u4ed6\u5982\u4f55\u4e3a\u81ea\u5df1\u8fa9\u89e3\uff0c\u90fd\u6d17\u4e0d\u53bb\u4ed6\u5728\u5173\u952e\u65f6\u523b\u80cc\u53db\u7231\u8fbe\u7684\u5acc\u7591\uff0c\u4ed6\u5185\u5fc3\u6df1\u5904\u4e5f\u65e0\u6cd5\u5426\u8ba4\u8fd9\u4e00\u70b9\uff0c\u8fd9\u8ba9\u4ed6\u66f4\u96be\u9762\u5bf9\u5f20\u606a\u3002</p><p>\u3000\u3000\u82e5\u8bf4\u4ed6\u5728\u5e02\u573a\u63a8\u5e7f\u3001\u6e20\u9053\u5efa\u8bbe\u4e0a\u6709\u4ec0\u4e48\u4f18\u4e8e\u522b\u4eba\u7684\u7ecf\u9a8c\uff0c\u51e0\u4e4e\u90fd\u662f\u4ece\u5f20\u606a\u90a3\u91cc\u5b66\u8fc7\u6765\u7684\u3002</p><p>\u3000\u3000\u4ee3\u7406\u7231\u7acb\u4fe1\u624b\u673a\u4e14\u4e0d\u53bb\u8bf4\uff0c\u7231\u8fbe\u7535\u5b50\u524d\u671f\u7684\u5e02\u573a\u5ba3\u4f20\uff0c\u5f20\u606a\u90fd\u662f\u4eb2\u624b\u62df\u5b9a\u8ba1\u5212\u3001\u63a8\u52a8\u4eba\u624b\u53bb\u6267\u884c\uff0c\u8fd8\u5206\u6790\u6267\u884c\u540e\u7684\u6548\u679c\u3002\u5f20\u606a\u6709\u4e00\u53e5\u8bdd\uff0c\u5f88\u80fd\u8bf4\u4e2d\u5e7f\u544a\u4eba\u7684\u5fc3\u574e\uff1a\u201c\u8981\u5c3d\u53ef\u80fd\u541d\u556c\u6bcf\u4e00\u5206\u94b1\u7684\u5e7f\u544a\u6295\u5165\uff0c\u4e5f\u8981\u5c3d\u53ef\u80fd\u7684\u62ff\u51fa\u6bcf\u4e00\u5206\u94b1\u53bb\u6295\u5e7f\u544a\u2026\u2026\u201d\u8fd9\u4e00\u70b9\uff0c\u5f80\u5f80\u662f\u8fd9\u4e2a\u65f6\u4ee3\u7684\u8425\u9500\u7cbe\u82f1\u6240\u666e\u904d\u8ba4\u8bc6\u4e0d\u5230\uff0c\u5373\u4f7f\u6709\u8ba4\u8bc6\u5230\u4e5f\u6ca1\u6709\u6548\u529e\u6cd5\u53bb\u63a7\u5236\u7684\u3002</p><p>\u3000\u3000\u5e02\u573a\u90e8\u672c\u90e8\u7684\u4eba\uff0c\u5218\u660e\u8f89\u6ca1\u6709\u4fe1\u5fc3\u62c9\u8d70\uff0c\u56e0\u4e3a\u672c\u90e8\u7684\u5de5\u4f5c\u4eba\u5458\uff0c\u5bf9\u5f20\u606a\u6709\u7740\u8fd1\u4f3c\u76f2\u76ee\u7684\u5d07\u62dc\uff0c\u5218\u660e\u8f89\u5373\u4f7f\u8df3\u69fd\u8d70\u4e86\uff0c\u4e5f\u4e0d\u8ba4\u4e3a\u56fd\u5185\u6709\u8c01\u5728\u8fd9\u4e00\u5757\u4f1a\u6bd4\u5f20\u606a\u4e0e\u751f\u4ff1\u6765\u7684\u5929\u8d4b\u66f4\u51fa\u8272\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5218\u660e\u8f89\u4e0d\u4f1a\u5c06\u8fd9\u4e9b\u90fd\u5982\u5b9e\u7684\u8bf4\u7ed9\u8c22\u5251\u5357\u542c\uff0c\u4e0d\u53ef\u80fd\u4e0d\u5728\u8df3\u5165\u65b0\u4e1c\u5bb6\u4e4b\u9645\u9002\u5f53\u7684\u62ac\u9ad8\u81ea\u5df1\uff0c\u5c06\u4e00\u4e9b\u5f20\u606a\u5929\u624d\u4e00\u6837\u7684\u60ca\u8273\u8bbe\u60f3\u8bf4\u6210\u96c6\u4f53\u7684\u667a\u6167\uff0c\u8fd9\u66f4\u7b26\u5408\u4ed6\u4e2a\u4eba\u7684\u5229\u76ca\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ed9\u5916\u754c\u7684\u5f62\u8c61\uff0c\u672c\u6765\u5c31\u662f\u5f00\u7740\u8dd1\u8f66\u3001\u65e0\u6240\u4e8b\u4e8b\u7684\u9ad8\u8c03\u7ea8\u7ed4\u5b50\u5f1f\u7f62\u4e86\u3002</p><p>\u3000\u3000\u4e0e\u4e4b\u524d\u7684\u8001\u677f\u78b0\u9762\uff0c\u65e0\u8bba\u600e\u4e48\u8bf4\u90fd\u662f\u5c34\u5c2c\u7684\u4e8b\u60c5\uff0c\u4e0d\u8fc7\u5218\u660e\u8f89\u4eca\u5929\u7684\u8868\u73b0\uff0c\u786e\u5b9e\u7f3a\u4e4f\u5927\u5c06\u6c14\u5ea6\uff0c\u8c22\u5251\u5357\u672c\u6765\u5c31\u4e0d\u770b\u4e2d\u4ed6\u8fd9\u4e00\u70b9\uff0c\u81ea\u5df1\u53ef\u4ee5\u901a\u8fc7\u9648\u9759\u76f4\u63a5\u6307\u5bfc\u5e02\u573a\u90e8\u7684\u8fd0\u4f5c\uff0c\u5012\u4e5f\u4e0d\u4f1a\u5c06\u4e0d\u6ee1\u8868\u73b0\u5728\u8138\u4e0a\uff0c\u5f88\u6709\u98ce\u5ea6\u7684\u8bf7\u5f20\u606a\u4e00\u8d77\u4e58\u7535\u68af\u53bb\u4f1a\u573a\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u8d70\u697c\u68af\uff01\u201d\u5f20\u606a\u4e0d\u7406\u4f1a\u8c22\u5251\u5357\u6b64\u65f6\u591a\u4f59\u7684\u5584\u610f\uff0c\u4e0e\u4e01\u69d0\u3001\u8c22\u665a\u6674\u3001\u8bb8\u601d\u771f\u4ece\u697c\u68af\u95f4\u4e0a\u53bb\uff0c\u4f1a\u573a\u5728\u4e09\u697c\uff0c\u53c8\u4e0d\u662f\u5728\u4e09\u5341\u697c\u3002</p><p>\u3000\u3000\u201c\u770b\u6765\u4ed6\u4eec\u662f\u6c14\u6025\u8d25\u574f\u4e86\u3002\u201d\u8c22\u5251\u5357\u65e0\u8c13\u7684\u644a\u4e86\u644a\uff0c\u5728\u9648\u9759\u9762\u524d\uff0c\u4ed6\u90fd\u8981\u8868\u73b0\u51fa\u5e94\u6709\u7684\u6c14\u5ea6\u3002</p><p>\u3000\u3000\u9648\u9759\u62e7\u4e86\u62e7\u7709\u5934\uff0c\u4f3c\u4e4e\u4e5f\u89c9\u5f97\u5f20\u606a\u7684\u6c14\u91cf\u4e0d\u591f\u5b8f\u5927\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4f1a\u573a\u7684\u5165\u53e3\uff0c\u82cf\u6d25\u4e1c\u4e09\u4eba\u7ed9\u5019\u5728\u4f1a\u573a\u5916\u7684\u8bb0\u8005\u5835\u4f4f\uff0c\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u5de5\u4f5c\u4eba\u5458\u6b63\u52aa\u529b\u5c06\u8bb0\u8005\u5206\u5f00\uff0c\u8ba9\u82cf\u6d25\u4e1c\u4e09\u4eba\u987a\u5229\u8fdb\u5165\u4f1a\u573a\uff0c\u5728\u4f1a\u573a\u4e2d\uff0c\u4ed6\u4eec\u4e09\u4eba\u81ea\u7136\u4e5f\u662f\u4f17\u4eba\u77a9\u76ee\u7684\u7126\u70b9\uff0c\u7279\u522b\u51e0\u5bb6\u6709\u5fd7\u4e8e\u4e89\u593a\u660e\u5e74\u592e\u89c6\u6807\u738b\u7684\u4f01\u4e1a\u7684\u4ee3\u8868\uff0c\u90fd\u60c5\u4e0d\u81ea\u7981\u7684\u7ad9\u8d77\u6765\u3002</p><p>\u3000\u3000\u9664\u4e86\u4e2d\u592e\u7535\u89c6\u53f0\u81ea\u5df1\u7684\u8bb0\u8005\u80fd\u8fdb\u5165\u4f1a\u573a\u4e4b\u5916\uff0c\u5176\u4ed6\u62a5\u793e\u3001\u7535\u89c6\u53f0\u7b49\u5a92\u4f53\u7684\u8bb0\u8005\u53ea\u80fd\u5728\u4f1a\u573a\u5916\u7b49\u5019\u6d88\u606f\u3002</p><p>\u3000\u3000\u5f20\u606a\u3001\u4e01\u69d0\u3001\u8c22\u665a\u6674\u3001\u8bb8\u601d\u722c\u697c\u68af\u4e0a\u6765\uff0c\u8c22\u5251\u5357\u4ed6\u4eec\u6b63\u8fdb\u5165\u4f1a\u573a\uff0c\u8bb0\u8005\u4e5f\u6ca1\u6709\u62e5\u4e0a\u6765\u5835\u4f4f\u4ed6\u4eec\uff0c\u5012\u662f\u6709\u8bb0\u8005\u770b\u5230\u8fd9\u8fb9\uff0c\u90fd\u60c5\u4e0d\u81ea\u7981\u7684\u4e3e\u8d77\u7167\u76f8\u673a\u6765\u6293\u62cd\u3002</p><p>\u3000\u3000\u4e5d\u516d\u5e74\uff0c\u7f51\u7edc\u8fd9\u73a9\u827a\u8fd8\u4e0d\u5e7f\u4e3a\u4eba\u77e5\uff0c\u6ca1\u6709\u65b0\u95fb\u4ef7\u503c\u7684\u7167\u7247\u4e5f\u4e0d\u53ef\u80fd\u5728\u520a\u767b\u5728\u5a92\u4f53\u4e0a\uff0c\u7ed9\u8fd9\u4e9b\u6444\u5f71\u8bb0\u8005\u62cd\u5c31\u62cd\u4e86\uff0c\u8c01\u8ba9\u8bb8\u601d\u957f\u5f97\u5982\u6b64\u51fa\u4f17\u5462\uff1f</p><p>\u3000\u3000\u4f1a\u573a\u7684\u5e03\u7f6e\u4e0e\u53bb\u5e74\u6709\u5f88\u5927\u7684\u4e0d\u540c\u4e4b\u5904\u5c31\u5728\u4e8e\u4e3b\u5e2d\u53f0\u7684\u4f4d\u7f6e\uff0c\u6709\u7ade\u6807\u6240\u7528\u7684\u9ad8\u53f0\u3002</p><p>\u3000\u3000\u4e0e\u53bb\u5e74\u4e0d\u540c\uff0c\u4eca\u5e74\u4e2d\u592e\u7535\u89c6\u53f0\u5c06\u9ec4\u91d1\u65f6\u6bb5\u7684\u5e7f\u544a\u8d44\u6e90\u90fd\u62ff\u51fa\u6765\u7ade\u6807\uff0c\u800c\u53bb\u5e74\u53ea\u662f\u5c06\u65b0\u95fb\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u4e4b\u95f4\u53ea\u670965\u79d2\u949f\u7684\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\u62ff\u51fa\u6765\u7ade\u6807\u3002</p><p>\u3000\u3000\u5f53\u7136\u6700\u6709\u4ef7\u503c\u3001\u4e5f\u662f\u4f17\u591a\u5382\u5546\u6700\u671f\u5f85\u7684\u81ea\u7136\u662f\u65b0\u95fb\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u4e4b\u95f465\u79d2\u949f\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\uff0c\u5373\u4f7f\u6ca1\u6709\u5b9e\u529b\u53c2\u52a0\u8fd9\u90e8\u5206\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\u7ade\u593a\u7684\u5382\u5546\u4e5f\u62b1\u7740\u770b\u597d\u620f\u7684\u5fc3\u6001\u3002</p><p>\u3000\u3000\u8fd9\u90e8\u5206\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\u5171\u520613\u6bb55\u79d2\u5e7f\u544a\uff0c\u5176\u4e2d\u7b2c\u4e00\u6bb55\u79d2\u5e7f\u544a\u4e3a\u6807\u738b\u5e7f\u544a\u3002\u8fd913\u6bb5\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\u7684\u7ade\u593a\uff0c\u4e0e\u53bb\u5e74\u5b8c\u5168\u7684\u6697\u6807\u65b9\u5f0f\u4e0d\u540c\uff0c\u4eca\u5e74\u5c06\u91c7\u53d6\u660e\u6807\u3001\u6697\u6807\u76f8\u63a5\u5408\u7684\u65b9\u5f0f\uff0c\u5148\u7531\u4f1a\u573a\u4e0a\u6709\u610f\u53c2\u52a0\u7ade\u593a\u7684\u5382\u5546\u5c06\u62a5\u4ef7\u5199\u5728\u4fe1\u5c01\u91cc\uff0c\u62a5\u4ef7\u6700\u9ad8\u7684\u56db\u5341\u5bb6\u5382\u5546\u518d\u53c2\u52a0\u660e\u6807\u9636\u6bb5\u7684\u7ade\u4ef7\u3002</p><p>\u3000\u3000\u5f53\u7136\u662f\u4f1a\u573a\u6c14\u6c1b\u7092\u70ed\u7684\u9700\u8981\uff0c\u592e\u89c6\u5e7f\u544a\u4fe1\u606f\u90e8\u5148\u64ad\u653e\u4e00\u6bb5\u5bf9\u7231\u8fbe\u7535\u5b50\u7684\u5e7f\u544a\u5ba3\u4f20\u7247\uff0c\u4ecb\u7ecd\u7231\u8fbe\u7535\u5b50\u5728\u53d6\u5f97\u592e\u89c6\u6807\u738b\u4e4b\u540e\u5947\u8ff9\u822c\u7684\u5e02\u573a\u6210\u5c31\uff0c\u4e2d\u592e\u7535\u89c6\u53f0\u5e7f\u544a\u4fe1\u606f\u90e8\u4e3b\u4efb\u8c2d\u5e0c\u677e\u3001\u82cf\u6d25\u4e1c\u5206\u522b\u4ee3\u8868\u592e\u89c6\u4e0e\u7231\u8fbe\u7535\u5b50\u53d1\u8a00\uff0c\u63ed\u5f00\u8fd9\u4e00\u5c4a\u7684\u592e\u89c6\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u3002</p><p>\u3000\u3000\u6700\u5148\u8fdb\u884c\u7684\u5c31\u662f\u65b0\u95fb\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u4e4b\u95f4\u8fd9\u90e8\u5206\u6700\u6709\u4ef7\u503c\u7684\u5e7f\u544a\u65f6\u6bb5\u7684\u7ade\u593a\uff0c\u8fd9\u6837\u624d\u80fd\u8ba9\u6b21\u8981\u9ec4\u91d1\u5e7f\u544a\u65f6\u6bb5\u7684\u4ef7\u683c\u4e5f\u80fd\u6c34\u6da8\u8239\u9ad8\uff0c\u4e5f\u4e0d\u6015\u4e00\u5b9a\u5382\u5546\u4f1a\u7559\u6709\u4f59\u529b\u3002</p><p>\u3000\u3000\u4e2d\u592e\u7535\u89c6\u53f0\u8981\u6c42\u8fd913\u6bb55\u79d2\u5e7f\u544a\uff0c\u540c\u884c\u4e1a\u7684\u5382\u5546\u4e0d\u8d85\u8fc7\u4e09\u5bb6\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u8fdb\u5165\u660e\u6807\u7ade\u4ef7\u9636\u6bb5\u7684\u540c\u884c\u4e1a\u5382\u5546\u4e0d\u8d85\u8fc7\u4e5d\u5bb6\u3002\u8fd9\u4e5f\u662f\u4e3a\u4e86\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u5e7f\u544a\u8d44\u6e90\u5728\u5404\u884c\u4e1a\u5185\u5e73\u8861\u5206\u5e03\u7684\u8981\u6c42\uff0c\u4ee5\u514d\u8fd913\u6bb5\u5e7f\u544a\u90fd\u7ed9\u5f71\u789f\u673a\u5382\u5546\u62a2\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u7b2c\u4e00\u9636\u6bb5\u7684\u6697\u6807\u62a5\u4ef7\u6709\u4e9b\u6ce2\u6f9c\u4e0d\u60ca\u7684\u611f\u89c9\uff0c\u53ea\u662f\u8981\u5708\u8fdb\u53bb\u56db\u5341\u5bb6\u5165\u56f4\u5382\u5546\uff0c\u8c2d\u5e0c\u677e\u62a5\u51fa\u51fa\u5165\u56f4\u62a5\u4ef7\u4e00\u5343\u516b\u767e\u516b\u5341\u516b\u4e07\uff0c\u7531\u4e8e\u9996\u6279\u56db\u5341\u5bb6\u5165\u56f4\u7684\u5382\u5546\u91cc\u6709\u5341\u4e8c\u5bb6\u662f\u5f71\u789f\u673a\u5382\u5546\uff0c\u8981\u5254\u9664\u4e09\u5bb6\u5f71\u789f\u673a\u5382\u5546\uff0c\u786e\u5b9a\u5f71\u789f\u673a\u5382\u5546\u7684\u5165\u56f4\u62a5\u4ef7\u4e3a\u4e8c\u5343\u516d\u767e\u516b\u5341\u516b\u4e07\uff0c\u53c8\u4ece\u843d\u9009\u7684\u5382\u5bb6\u91cc\u6311\u51fa\u62a5\u4ef7\u6700\u9ad8\u7684\u4e09\u5bb6\u3002</p><p>\u3000\u3000\u6ca1\u6709\u516c\u5e03\u56db\u5341\u5bb6\u5382\u5546\u7684\u5177\u4f53\u62a5\u4ef7\uff0c\u6697\u6807\u9636\u6bb5\u7684\u62a5\u4ef7\u53ea\u662f\u786e\u5b9a\u5165\u56f4\u8005\u3002</p><p>\u3000\u3000\u5982\u6b64\u4e00\u6765\u6700\u7ec8\u7684\u5165\u56f4\u62a5\u4ef7\u4e3a\u4e00\u5343\u516d\u767e\u516d\u5341\u516b\u4e07\uff0c\u5f71\u789f\u673a\u884c\u4e1a\u4e0d\u4ec5\u6700\u591a\u5165\u56f4\u4e5d\u5bb6\uff0c\u800c\u4e14\u6700\u4f4e\u5165\u56f4\u4ef7\u6bd4\u5176\u4ed6\u7684\u8981\u9ad8\u51fa\u4e00\u5343\u4e07\uff0c\u5982\u4e8b\u5148\u5a92\u4f53\u9884\u6d4b\u7684\u4e00\u6837\uff0c\u592e\u89c6\u4eca\u5e74\u7684\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u5c31\u662f\u5f71\u789f\u673a\u5382\u5546\u7684\u76db\u4f1a\u3002</p></article>"}}}]);
