(window.webpackJsonp=window.webpackJsonp||[]).push([[580],{603:function(p){p.exports={chapterTitle:null,title:"\u7b2c433\u7ae0 \u660e\u6807\u7ade\u4ef7",content:"<article><h2>\u7b2c433\u7ae0 \u660e\u6807\u7ade\u4ef7</h2><p>\u3000\u3000\u592e\u89c6\u4eca\u5e74\u7684\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u65e0\u7591\u5c31\u662f\u5f71\u789f\u673a\u5382\u5546\u7684\u76db\u4f1a\uff0c\u56db\u5341\u5bb6\u5165\u56f4\u5382\u5546\uff0c\u5f71\u789f\u673a\u5382\u5546\u5c31\u6709\u4e5d\u5bb6\uff0c\u8981\u4e0d\u662f\u4e2d\u592e\u7535\u89c6\u53f0\u4e3a\u4e86\u5e73\u8861\u5404\u884c\u4e1a\u4e4b\u95f4\u7684\u5e7f\u544a\u8d44\u6e90\u5206\u5e03\uff0c\u5f71\u789f\u673a\u5382\u5546\u5c06\u6709\u5341\u4e8c\u5bb6\u5165\u56f4\u3002</p><p>\u3000\u3000\u88ab\u5237\u6389\u7684\u4e09\u5bb6\u5f71\u789f\u673a\u5382\u5546\u4e5f\u6ca1\u6709\u4ec0\u4e48\u9057\u61be\u7684\uff0c\u6ca1\u6709\u5b9e\u529b\u5c31\u662f\u6ca1\u6709\u5b9e\u529b\uff0c\u56e0\u4e3a\u5165\u56f4\u7684\u4e5d\u5bb6\u5f71\u789f\u673a\u5382\u5546\u6700\u7ec8\u8fd8\u5fc5\u987b\u7ed9\u5237\u4e0b\u516d\u5bb6\u6765\u3002</p><p>\u3000\u3000\u5f71\u789f\u673a\u5382\u5546\uff0c\u7231\u8fbe\u4e4b\u5916\u662f\u65b0\u79d1\uff0c\u4e5d\u516d\u5e74\u8fc7\u53bb\u4e86\u5341\u4e2a\u6708\u4e2d\uff0c\u7231\u8fbe\u5360\u54c1\u724c\u5f71\u789f\u673a\u5e02\u573a40\uff05\u7684\u4efd\u989d\uff0c\u65b0\u79d1\u536020\uff05\uff0c\u4e4b\u540e\u624d\u662f\u91d1\u6b63\u3001\u4e07\u5229\u8fbe\u3001\u7231\u591a\u3001\u6b65\u6b65\u9ad8\u7b49\u4f17\u591a\u7684\u54c1\u724c\u5382\u5546\u5206\u4eab\u5269\u4e0b\u768440\uff05\u7684\u4efd\u989d\u3002</p><p>\u3000\u3000\u8981\u4e0d\u662f\u5218\u660e\u8f89\u62c9\u4eba\u96c6\u4f53\u8df3\u69fd\u4e8b\u4ef6\u5df2\u7ecf\u5728\u4e1a\u5185\u3001\u5a92\u4f53\u95f4\u4f20\u64ad\u5f00\uff0c\u79d1\u738b\u7535\u5668\u51fa\u73b0\u5728\u5165\u56f4\u540d\u5355\u4e4b\u540e\uff0c\u4e00\u5b9a\u4f1a\u8ba9\u4eba\u611f\u5230\u4e00\u4e9b\u610f\u5916\u5427\u3002\u4e5f\u5c31\u4e00\u4e9b\u610f\u5916\u800c\u5df2\uff0c\u7231\u591a\u53bb\u5e74\u5728\u593a\u5f97\u592e\u89c6\u6807\u738b\u4e4b\u524d\uff0c\u6708\u4ea7\u91cf\u4e5f\u5c31\u4e00\u4e07\u53f0\u591a\u70b9\u7684\u6837\u5b50\u3002</p><p>\u3000\u3000\u7531\u4e8e\u7b2c\u4e00\u9636\u6bb5\u7684\u6697\u6807\u62a5\u4ef7\u53ea\u662f\u786e\u5b9a\u56db\u5341\u5bb6\u5165\u56f4\u5382\u5bb6\uff0c\u6ca1\u6709\u516c\u5e03\u5177\u4f53\u7684\u62a5\u4ef7\uff0c\u592e\u89c6\u5e7f\u544a\u4fe1\u606f\u90e8\u4e3b\u4efb\u8c2d\u5e0c\u677e\u662f\u4e00\u4e2a\u6234\u7740\u7ec6\u91d1\u8fb9\u773c\u955c\u3001\u5145\u6ee1\u77e5\u6027\u6c14\u8d28\u7684\u5973\u4eba\uff0c\u773c\u775b\u660e\u4eae\u3001\u773c\u795e\u9510\u5229\uff0c\u5979\u7684\u521d\u8877\u4e5f\u662f\u589e\u52a0\u5382\u5546\u4ee3\u8868\u7684\u5fc3\u7406\u538b\u529b\uff0c\u8ba9\u4ed6\u4eec\u5728\u63a5\u4e0b\u6765\u7684\u660e\u6807\u7ade\u4ef7\u9636\u6bb5\u66f4\u51b2\u52a8\u4e00\u4e9b\u3002\u5979\u8fd9\u4e00\u70b9\u7b56\u7565\u7684\u786e\u5f88\u503c\u5f97\u79f0\u9053\u3002</p><p>\u3000\u3000\u786e\u5b9a\u5165\u56f4\u56db\u5341\u5bb6\u5382\u5546\u540d\u5355\u65f6\uff0c\u7231\u8fbe\u7535\u5b50\u5217\u5728\u7b2c\u4e00\u4f4d\uff0c\u5927\u5bb6\u90fd\u4ee5\u4e3a\u7231\u8fbe\u7535\u5b50\u5728\u6697\u6807\u62a5\u4ef7\u9636\u6bb5\u5c31\u7ed9\u51fa\u4e00\u4e2a\u5929\u4ef7\uff0c\u4e5f\u6709\u53ef\u80fd\u662f\u7231\u8fbe\u7535\u5b50\u4f5c\u4e3a\u4e0a\u5c4a\u592e\u89c6\u6807\u738b\u4f01\u4e1a\u6240\u5e94\u4eab\u6709\u7684\u7279\u6743\u3002</p><p>\u3000\u3000\u4e3b\u5e2d\u53f0\u4e0b\u6709\u4e00\u7247\u5c0f\u7247\u533a\u57df\u7a7a\u7740\uff0c\u5165\u56f4\u5382\u5546\u4ee3\u8868\u9274\u8fc7\u786e\u8ba4\u4e66\u4e4b\u540e\uff0c\u90fd\u96c6\u4e2d\u90a3\u7247\u533a\u57df\u91cc\u5165\u5750\uff0c\u8c22\u5251\u5357\u4e0e\u5218\u660e\u8f89\u3001\u9648\u9759\u5750\u8fc7\u53bb\uff0c\u7aa5\u7740\u82cf\u6d25\u4e1c\u4e0e\u7231\u8fbe\u7535\u5b50\u54c1\u724c\u8fd0\u8425\u90e8\u7ecf\u7406\u5218\u6d77\u71d5\u5750\u5728\u5de6\u524d\u65b9\uff0c\u5faa\u7740\u82cf\u6d25\u4e1c\u7684\u76ee\u5149\u53ef\u4ee5\u770b\u89c1\u5750\u5728\u6574\u4e2a\u4f1a\u573a\u5de6\u524d\u65b9\u3001\u80f8\u524d\u6302\u7740\u7279\u9080\u5609\u5bbe\u80f8\u5361\u7684\u5f20\u606a\uff0c\u8bb8\u601d\u5750\u5728\u5f20\u606a\u7684\u5de6\u624b\u8fb9\uff0c\u624b\u91cc\u62ff\u7740\u62db\u5546\u5927\u4f1a\u7684\u5ba3\u4f20\u624b\u518c\uff0c\u597d\u50cf\u4e0e\u4e3b\u5e2d\u53f0\u4e0a\u7684\u8c2d\u5e0c\u677e\u6709\u773c\u795e\u4ea4\u6d41\uff0c\u5f20\u606a\u4ed6\u4eec\u786e\u5207\u4e0e\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u9ad8\u5c42\u6709\u8fc7\u8054\u7cfb\uff0c\u8c22\u665a\u6674\u4e0e\u4e01\u69d0\u597d\u50cf\u5728\u8bf4\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6ce8\u610f\u529b\u6ca1\u6709\u653e\u5728\u4f1a\u573a\u3002</p><p>\u3000\u3000\u7531\u4e8e\u6bcf\u4e2a\u884c\u4e1a\u6700\u591a\u6709\u4e09\u5bb6\u80fd\u593a\u5f97\u65b0\u95fb\u8054\u64ad\u4e0e\u5929\u6c14\u9884\u62a5\u4e4b\u95f4\u76845\u79d2\u5e7f\u544a\u65f6\u6bb5\uff0c\u5e76\u975e\u53ea\u6709\u7b2c\u4e00\u4e2a5\u79d2\u5e7f\u544a\u65f6\u6bb5\u624d\u4f1a\u5f15\u8d77\u6fc0\u70c8\u7684\u4e89\u593a\uff0c\u6bcf\u4e00\u4e2a\u5e7f\u544a\u65f6\u6bb5\u7684\u4e89\u593a\u90fd\u4e0d\u4f1a\u8f7b\u677e\uff0c\u7279\u522b\u662f\u4e5d\u5bb6\u5f71\u789f\u673a\u5382\u5546\u4e4b\u95f4\u7684\u4e89\u593a\u66f4\u89c1\u8840\u8165\u3002</p><p>\u3000\u3000\u8c2d\u5e0c\u677e\u4f5c\u4e3a\u592e\u89c6\u5e7f\u544a\u4fe1\u606f\u90e8\u7684\u638c\u95e8\u4eba\u4eb2\u81ea\u4e3b\u6301\u660e\u6807\u9636\u6bb5\u7684\u7ade\u4ef7\uff0c\u5979\u6276\u4e86\u6276\u5979\u7ec6\u91d1\u8fb9\u773c\u955c\uff0c\u85cf\u7740\u955c\u7247\u7684\u773c\u775b\u95ea\u7740\u72e1\u9ee0\u7684\u5149\u8292\uff0c\u6807\u738b\u6982\u5ff5\u7684\u72c2\u70ed\uff0c\u662f\u5979\u4eba\u751f\u8fc4\u4eca\u4e3a\u6b62\u6700\u5927\u7684\u6210\u5c31\uff0c\u5979\u73b0\u5728\u5c31\u8981\u5c06\u8fd9\u80a1\u72c2\u70ed\u63a8\u5411\u6700\u9ad8\u6f6e\uff1a\u201c\u2026\u2026\u6807\u738b\u5e7f\u544a\u8d77\u62cd\u4ef7\u4ece\u5165\u56f4\u5e95\u4ef7\u4e00\u5343\u516d\u767e\u516d\u5341\u516b\u4e07\u8d77\u62cd\uff0c\u5341\u4e07\u5143\u52a0\u4ef7\u4e00\u6b21\u2026\u2026\u201d</p><p>\u3000\u3000\u4f3c\u4e4e\u5927\u5bb6\u90fd\u610f\u8bc6\u7231\u8fbe\u7535\u5b50\u6b64\u6b21\u52bf\u5728\u5fc5\u5f97\uff0c\u8c2d\u5e0c\u677e\u5973\u58eb\u5ba3\u4f20\u7ade\u4ef7\u5f00\u59cb\uff0c\u5927\u5bb6\u90fd\u4e0b\u610f\u8bc6\u7684\u770b\u5411\u7231\u8fbe\u7535\u5b50\u7684\u4ee3\u8868\u82cf\u6d25\u4e1c\uff0c\u82cf\u6d25\u4e1c\u5374\u51fa\u4e4e\u610f\u6599\u7684\u772f\u8d77\u773c\u775b\uff0c\u8c01\u4e5f\u4e0d\u770b\uff0c\u53ea\u770b\u7740\u624b\u821e\u5c0f\u69cc\u7684\u8c2d\u5e0c\u677e\u3002</p><p>\u3000\u3000\u6c14\u6c1b\u987f\u65f6\u6709\u4e9b\u51dd\u6ede\uff0c\u4f46\u662f\u8f6c\u773c\u95f4\u5927\u5bb6\u90fd\u60f3\u660e\u767d\u4e86\uff0c\u7231\u8fbe\u7535\u5b50\u54ea\u6709\u8010\u5fc3\u8ddf\u522b\u4eba\u5341\u4e07\u5341\u4e07\u7684\u52a0\u7b79\u7801\uff0c\u8fd8\u4e0d\u5982\u8fd9\u4e9b\u6545\u5f04\u7384\u865a\u7ed9\u522b\u4eba\u589e\u52a0\u4e9b\u538b\u529b\u3002</p><p>\u3000\u3000\u201c\u4e00\u4ebf\u4e24\u5343\u4e07\uff01\u201d\u77ed\u6682\u7684\u6c89\u9ed8\u3001\u8fd9\u65f6\u5019\u6c89\u9ed8\u7adf\u4f1a\u8ba9\u4eba\u817f\u811a\u53d1\u6296\uff0c\u4e00\u4e2a\u6e05\u4eae\u7684\u62a5\u4ef7\u58f0\u8ba9\u4f1a\u573a\u91cc\u7684\u4eba\u5fc3\u810f\u4e00\u98a4\uff0c\u90fd\u671b\u5411\u9ad8\u4e3e\u8d77\u6765\u7684\u5706\u53f7\u724c\uff1a67\u53f7\uff0c\u4e3e\u774067\u53f7\u724c\u7684\u4e2d\u5e74\u4eba\u6709\u4e9b\u5fae\u79c3\uff0c\u4ed6\u4e00\u4e0b\u5b50\u4ece\u5165\u56f4\u5e95\u4ef7\u4e00\u5343\u516d\u767e\u516d\u5341\u516b\u4e07\u62a5\u5230\u4e00\u4ebf\u4e24\u5343\u4e07\u7684\u5929\u4ef7\uff0c\u987f\u65f6\u8ba9\u4f1a\u573a\u91cc\u7684\u4eba\u5012\u5438\u4e00\u53e3\u51c9\uff0c\u60df\u6709\u4e2d\u95f4\u524d\u533a\u7684\u56db\u5341\u5bb6\u5f02\u5e38\u7684\u51b7\u9759\uff0c\u8c22\u5251\u5357\u6487\u5634\u5bf9\u9648\u9759\u4f4e\u58f0\u7684\u8bf4\uff1a\u201c\u4ed6\u8fd8\u4e0d\u5982\u76f4\u63a5\u62a5\u4e24\u4ebf\uff0c\u8fd9\u4e2a\u4ef7\u624d\u6709\u53ef\u80fd\u5c06\u5176\u4ed6\u4eba\u76f4\u63a5\u5413\u4f4f\u3002\u201d\u5165\u56f4\u7684\u56db\u5341\u5bb6\u5bf9\u7231\u8fbe\u7535\u5b50\u6210\u529f\u7684\u6a21\u5f0f\u90fd\u6709\u8fc7\u5145\u5206\u7684\u7814\u7a76\uff0c\u81ea\u7136\u77e5\u9053\u592e\u89c6\u6807\u738b\u7684\u4ef7\u503c\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u662f\u9759\u5750\u4e0d\u60ca\uff0c\u8fd9\u4e00\u6bb5\u5386\u53f2\u5df2\u7ecf\u5b8c\u5168\u6539\u53d8\uff0c\u6ca1\u6709\u91cd\u590d\u5386\u53f2\u53ef\u4ee5\u501f\u9274\uff0c\u5e76\u4e0d\u4ee3\u8868\u4ed6\u5c31\u4f1a\u5931\u53bb\u5224\u65ad\u3002\u7231\u8fbe\u7535\u5b50\u4e5d\u516d\u5e74\u7684\u9500\u552e\u989d\u7684\u786e\u6709\u671b\u505a\u5230\u56db\u5341\u4ebf\uff0c\u5bf9\u4e8e\u4e00\u4e2a\u65b0\u5174\u7684\u3001\u89c4\u6a21\u6709\u5341\u4e8c\u4ebf\u4eba\u53e3\u7684\u5e02\u573a\uff0c\u4e5d\u516d\u5e74\uff0c\u592e\u89c6\u6807\u738b\u8fd9\u4e2a\u6982\u5ff5\u6240\u5145\u5206\u53d1\u6398\u51fa\u6765\u7684\u4ef7\u503c\u7edd\u5bf9\u8981\u8fdc\u8fdc\u8d85\u8fc7\u7231\u8fbe\u6240\u6295\u8fdb\u53bb\u7684\u516b\u5343\u4e07\u3002</p><p>\u3000\u3000\u5386\u53f2\u4e0a\u5b54\u5e9c\u3001\u79e6\u6c60\u3001\u7231\u591a\u7b49\u4f01\u4e1a\u5728\u593a\u5f97\u592e\u89c6\u6807\u738b\u90fd\u8fc5\u901f\u7684\u5982\u6d41\u661f\u4e00\u6837\u7684\u5d1b\u8d77\u5e76\u6b92\u843d\uff0c\u5f88\u91cd\u8981\u7684\u4e00\u4e2a\u539f\u56e0\u662f\u5176\u4ed6\u73af\u8282\u65e0\u6cd5\u9002\u5e94\u5176\u54c1\u724c\u9ad8\u901f\u6269\u5f20\u800c\u5f15\u8d77\u77ac\u65f6\u7684\u5d29\u584c\u7f62\u4e86\uff0c\u4f46\u662f\u5e76\u4e0d\u80fd\u5426\u8ba4\u592e\u89c6\u6807\u738b\u6982\u5ff5\u5bf9\u54c1\u724c\u6781\u901f\u6269\u5f20\u6240\u8d77\u5230\u7684\u5de8\u5927\u4f5c\u7528\uff0c\u5173\u952e\u8981\u4f01\u4e1a\u80fd\u9002\u5e94\u54c1\u724c\u6781\u901f\u6269\u5f20\u6240\u5e26\u6765\u7684\u53cd\u7834\u574f\u529b\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u4e00\u5bb6\u751f\u4ea7\u3001\u8425\u9500\u3001\u7269\u6d41\u3001\u91c7\u8d2d\u3001\u7814\u53d1\u3001\u8d22\u52a1\u7b49\u5404\u73af\u8282\u7ba1\u7406\u90fd\u76f8\u5f53\u4f18\u79c0\u7684\u4f01\u4e1a\uff0c\u9700\u8981\u5728\u56fd\u5185\u5e02\u573a\u6253\u77e5\u540d\u5ea6\uff0c\u90a3\u5c31\u5f88\u503c\u5f97\u6295\u8d44\u592e\u89c6\u6807\u738b\u6982\u5ff5\u3002\u5982\u5b9d\u6d01\u516c\u53f8\u5927\u4e3e\u8fdb\u519b\u56fd\u5185\u5e02\u573a\uff0c\u5728\u6807\u738b\u6982\u5ff5\u4e0d\u518d\u72c2\u70ed\u4e4b\u540e\u3001\u5728\u56fd\u5185\u6d88\u8d39\u5e02\u573a\u6709\u51fa\u73b0\u53cd\u601d\u4e4b\u540e\uff0c\u8fd8\u8fde\u7eed\u4e09\u5e74\u4ee5\u8d85\u8fc7\u4e24\u4ebf\u7684\u91cd\u91d1\u62ff\u4e0b\u592e\u89c6\u6807\u738b\u5e7f\u544a\uff0c\u5e76\u8fdb\u884c\u6210\u529f\u7684\u5e02\u573a\u8425\u9500\u8fd0\u4f5c\uff0c\u5373\u4e3a\u8bc1\u660e\u3002</p><p>\u3000\u3000\u773c\u4e0b\u6d88\u8d39\u5e02\u573a\u8fd8\u4e0d\u6210\u719f\uff0c\u6d88\u8d39\u8005\u66f4\u5bb9\u6613\u88ab\u52a8\u7684\u63a5\u53d7\u5e7f\u544a\u4fe1\u606f\uff0c\u6709\u592e\u89c6\u5e7f\u544a\u4fe1\u606f\u90e8\u7684\u64cd\u76d8\u4e4b\u4e0b\uff0c\u6807\u738b\u6982\u5ff5\u6b63\u503c\u706b\u70ed\uff0c\u5404\u5730\u4e3b\u6d41\u5a92\u4f53\u90fd\u79ef\u6781\u914d\u5408\uff0c\u6807\u738b\u6982\u5ff5\u5145\u5206\u6316\u6398\u51fa\u6765\u7684\u4ef7\u503c\u5e94\u8be5\u66f4\u9ad8\uff0c\u5f20\u606a\u4e4b\u524d\u8ba9\u5e02\u573a\u90e8\u505a\u51fa2.8\u4ebf\u7684\u9884\u7b97\uff0c\u5e76\u975e\u6beb\u65e0\u7406\u8bba\u6839\u636e\u3001\u968f\u624b\u62c8\u6765\u7684\u6570\u636e\u3002</p><p>\u3000\u3000\u201c\u4e00\u4ebf\u516b\u5343\u4e07\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u80e1\u601d\u4e71\u60f3\u7740\uff0c\u4ef7\u683c\u5df2\u7ecf\u98de\u7a9c\u5230\u4e00\u4ebf\u516b\u5343\u4e07\uff0c\u6ca1\u4eba\u6709\u8010\u5fc3\u5341\u4e07\u3001\u5341\u4e07\u7684\u52a0\u7b79\uff0c\u8fd9\u51e0\u7b14\u90fd\u662f\u4e24\u767e\u4e07\u3001\u4e24\u767e\u4e07\u7684\u5f80\u4e0a\u53e0\uff0c\u65b0\u79d1\u3001\u7231\u591a\u3001\u6b65\u6b65\u9ad8\u3001\u4e07\u5229\u8fbe\u90fd\u5df2\u7ecf\u4e3e\u724c\u4e86\uff0c\u65b0\u79d1\u4e3e\u724c\u5230\u4e00\u4ebf\u516d\u5343\u4e07\u65f6\u5c31\u505c\u6b62\u7ee7\u7eed\u62a5\u4ef7\uff0c\u53ef\u80fd\u662f\u653e\u5f03\u62a5\u4ef7\uff0c\u8ba4\u4e3a\u6807\u738b\u5bf9\u4ed6\u4eec\u6765\u8bf4\u662f\u4e0d\u503c\u8fd9\u4e48\u4ef7\u4e86\uff1b\u4e5f\u53ef\u80fd\u662f\u6682\u65f6\u505c\u624b\uff0c\u7b49\u4e00\u4f1a\u513f\u7ee7\u7eed\u6740\u51fa\u3002</p><p>\u3000\u3000\u65b0\u79d1\u4f5c\u4e3a\u56fd\u5185\u7535\u5b50\u5de5\u4e1a\u8001\u724c\u7684\u9f99\u5934\u4f01\u4e1a\uff0c\u65b0\u79d1\u54c1\u724c\u4ece\u4e5d\u5341\u5e74\u4ee3\u521d\u5c31\u5df2\u7ecf\u6ee1\u8a89\u5168\u56fd\uff0c\u4ed6\u4eec\u4e5d\u516d\u5e74\u5f88\u8f7b\u677e\u5c31\u62ff\u4e0b\u54c1\u724c\u5f71\u789f\u673a\u5e02\u573a20\uff05\u7684\u4efd\u989d\u3002</p><p>\u3000\u3000\u73b0\u5728\u4e3b\u8981\u7684\u7ade\u4ef7\u8005\uff0c\u8fd8\u6709\u4e24\u5bb6\u5382\u5546\uff0c\u770b\u53f7\u724c\u6570\u5bf9\u5e94\u7684\u662f\u5510\u6cb3\u9152\u4e1a\u4e0e\u7ea2\u6843Q\uff0c\u767d\u9152\u4e1a\u4e0e\u4fdd\u5065\u54c1\u4e1a\u4e5f\u662f\u8425\u9500\u5927\u6218\u7684\u4e3b\u6218\u573a\u3002</p><p>\u3000\u3000\u79d1\u738b\u8fd8\u6ca1\u6709\u4e3e\u724c\uff0c\u62ff\u4f59\u5149\u80fd\u770b\u89c1\u8c22\u5251\u5357\u4e00\u76f4\u5728\u89c2\u5bdf\u8fd9\u8fb9\uff0c\u53f7\u724c\u5148\u524d\u5728\u5218\u660e\u8f89\u624b\u91cc\uff0c\u73b0\u5728\u634f\u5728\u4ed6\u624b\u91cc\uff0c\u5f20\u606a\u51b7\u51b7\u4e00\u7b11\uff1a\u60f3\u593a\u6807\u738b\u7684\u5382\u5546\u591a\u7740\u5462\uff0c\u4f60\u76ef\u7740\u6211\u6709\u5c41\u7528\uff1f</p><p>\u3000\u3000\u5218\u660e\u8f89\u624b\u5fc3\u90fd\u662f\u6c57\uff0c\u53bb\u5e74\u53ea\u6709\u5468\u6e38\u966a\u5f20\u606a\u53c2\u52a0\u5e7f\u544a\u62db\u5546\u5927\u4f1a\uff0c\u5468\u6e38\u90a3\u6b21\u4e5f\u7ed9\u5f20\u606a\u800d\u4e86\u4e00\u628a\uff0c\u56de\u53bb\u81ea\u7136\u4e0d\u4f1a\u5b9e\u8bf4\uff0c\u8f7b\u63cf\u6de1\u5199\u7684\u80e1\u5439\u4e86\u4e00\u628a\uff0c\u5218\u660e\u8f89\u4e5f\u5c31\u5f53\u771f\u4e86\uff0c\u4eba\u4e0d\u5728\u73b0\u573a\uff0c\u4e0d\u80fd\u611f\u89c9\u5230\u8fd9\u79cd\u7d27\u5f20\u3001\u538b\u6291\u3001\u51dd\u6ede\u5230\u6781\u70b9\u7684\u6c14\u6c1b\uff0c\u90a3\u4e9b\u81f3\u5c11\u8fd8\u80fd\u4fdd\u6301\u5e73\u9759\u6c14\u5ea6\u7684\u4eba\uff0c\u8fd8\u6de1\u5b9a\u81ea\u82e5\u9732\u51fa\u7b11\u5bb9\u7684\u4eba\u771f\u662f\u4e0d\u7b80\u5355\uff0c\u53f7\u724c\u5230\u8c22\u5251\u5357\u624b\u91cc\uff0c\u5218\u660e\u8f89\u867d\u7136\u7d27\u5f20\uff0c\u4f46\u53ef\u4ee5\u53bb\u89c2\u5bdf\u522b\u4eba\u7684\u795e\u8272\uff0c\u82cf\u6d25\u4e1c\u5728\u8ddf\u5218\u6d77\u71d5\u3001\u8d75\u6d77\u821f\u8f7b\u58f0\u4ea4\u8c08\uff0c\u597d\u50cf\u662f\u5728\u4ecb\u7ecd\u4e3e\u724c\u7ade\u4ef7\u7684\u5382\u5546\uff0c\u53f7\u724c\u5c31\u968f\u624b\u4e22\u5728\u684c\u4e0a\uff0c\u597d\u50cf\u4e5f\u6ca1\u6709\u62ff\u5230\u624b\u91cc\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u201c\u4e24\u4ebf\u516d\u5343\u4e07\u2026\u2026\u201d\u8c22\u5251\u5357\u5fcd\u4e0d\u4f4f\u4e3e\u724c\u4e86\uff0c\u5728\u6b65\u6b65\u9ad8\u62a5\u51fa\u4e24\u4ebf\u4e24\u5343\u591a\u4e07\u4e4b\u540e\uff0c\u4ed6\u53ea\u63a5\u62a5\u51fa\u4e24\u4ebf\u516d\u5343\u4e07\uff0c\u5e0c\u671b\u5c06\u5176\u4ed6\u7ade\u4e89\u5382\u5546\u51fb\u9000\uff0c\u6765\u76f4\u63a5\u8fce\u63a5\u7231\u8fbe\u7535\u5b50\u7684\u6311\u6218\u3002\u62a5\u4ef7\u8fc7\u4e24\u4ebf\u4e4b\u540e\uff0c\u5927\u5bb6\u5c31\u53d8\u5f97\u8c28\u614e\uff0c\u6709\u52a0\u4e00\u767e\u4e07\u7684\uff0c\u4e5f\u6709\u52a0\u5341\u4e07\uff0c\u7ea0\u7f20\u4e86\u5341\u51e0\u4e2a\u56de\u5408\uff0c\u624d\u7531\u6b65\u6b65\u9ad8\u62a5\u51fa\u4e24\u4ebf\u4e24\u5343\u96f6\u516d\u5341\u4e07\u7684\u62a5\u4ef7\uff0c\u79d1\u738b\u4e4b\u524d\u90fd\u6ca1\u6709\u4e3e\u724c\uff0c\u4e3e\u724c\u4e24\u4ebf\u4e24\u5343\u96f6\u516d\u5341\u4e07\u7684\u57fa\u7840\u4e00\u4e0b\u5b50\u52a0\u4e86\u8fd1\u56db\u5343\u4e07\uff0c\u4eff\u4f5b\u5728\u4f17\u4eba\u7684\u5fc3\u810f\u4e0a\u69cc\u4e86\u4e00\u51fb\uff0c\u90a3\u4e9b\u6b63\u79ef\u6781\u4e3e\u724c\u7684\u5382\u5546\u95fb\u58f0\u4e00\u6014\uff0c\u90a3\u4e9b\u653e\u5f03\u4e3e\u724c\uff0c\u8fd8\u6709\u6ca1\u6709\u5165\u56f4\u8fd9\u6b21\u7ade\u4ef7\u7684\u90a3\u4e9b\u5382\u5546\u70ed\u8840\u4f3c\u4e4e\u7ed9\u70b9\u6cb8\u4e86\uff0c\u90fd\u6709\u4eba\u5fcd\u4e0d\u4f4f\u7ad9\u8d77\u6765\uff0c\u60f3\u770b\u6e05\u695a\u90a3\u4e2a\u9ad8\u9ad8\u4e3e\u774088\u53f7\u724c\u7684\u9752\u5e74\u957f\u4ec0\u4e48\u6837\u5b50\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u53ea\u76ef\u7740\u82cf\u6d25\u4e1c\u770b\uff0c\u4ed6\u770b\u5230\u82cf\u6d25\u4e1c\u5c06\u53f7\u724c\u4ece\u684c\u4e0a\u62ff\u5230\u624b\u91cc\uff0c\u62ac\u5934\u6ce8\u89c6\u7740\u53f0\u4e0a\u4e3b\u6301\u7ade\u4e89\u7684\u8c2d\u5e0c\u677e\u5973\u58eb\uff0c\u770b\u5979\u7684\u624b\u52bf\u4e0e\u8bed\u6c14\uff0c\u5e94\u8be5\u7ecf\u8fc7\u4e13\u95e8\u7684\u57f9\u517b\u3002</p><p>\u3000\u3000\u201c\u4e24\u4ebf\u516d\u5343\u4e07\u2026\u2026\u4e24\u6b21\u2026\u2026\u201d\u8c2d\u5e0c\u677e\u5979\u8ba9\u81ea\u5df1\u4e0b\u964d\u7684\u624b\u7f13\u6162\u7684\u62ac\u8d77\uff0c\u5f53\u5979\u624b\u62ac\u8d77\u5230\u6700\u9ad8\u70b9\u518d\u5207\u4e0b\u65f6\uff0c\u5979\u5c31\u8981\u5ba3\u4f20\u79d1\u738b\u7684\u62a5\u4ef7\u4e3a\u6700\u7ec8\u62a5\u4ef7\u4e86\uff0c\u5979\u7684\u773c\u795e\u90fd\u79fb\u5230\u82cf\u6d25\u4e1c\u7684\u8138\u4e0a\uff0c\u9f13\u52b1\u4ed6\u4e3e\u8d77\u624b\u91cc\u7684\u53f7\u724c\uff0c\u201c\u4e24\u4ebf\u516d\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u52a0\u5341\u4e07\u2026\u2026\u201d\u4e0d\u662f\u82cf\u6d25\u4e1c\uff0c\u90a3\u4e2a\u51e0\u4e4e\u6709\u4e24\u767e\u65a4\u4f53\u91cd\u7684\u7ea2\u6843Q\u7684\u8001\u603b\u4e3e\u8d77\u624b\u4e2d\u53f7\u724c\uff0c\u201c\u4e24\u4ebf\u516d\u5343\u96f6\u4e00\u5341\u4e07\u2026\u2026\u201d</p><p>\u3000\u3000\u4f17\u4eba\u60ac\u8d77\u7684\u5fc3\u53c8\u843d\u56de\u539f\u5904\uff0c\u7b49\u7740\u65b0\u4e00\u8f6e\u7684\u7ea0\u7f20\u6218\u3002</p></article>"}}}]);
