(window.webpackJsonp=window.webpackJsonp||[]).push([[584],{607:function(p){p.exports={chapterTitle:null,title:"\u7b2c584\u7ae0 \u5077\u730e\u5f00\u59cb",content:"<article><h2>\u7b2c584\u7ae0 \u5077\u730e\u5f00\u59cb</h2><p>\u3000\u3000\u524d\u4e16\u5510\u5a67\u51fa\u56fd\u540e\u6570\u5e74\u540e\u7a81\u7136\u53d1\u7535\u5b50\u90ae\u4ef6\u8ddf\u81ea\u5df1\u8054\u7cfb\u4e5f\u53ea\u662f\u8bf4\u8d77\u5979\u540c\u684c\u7684\u8fd1\u51b5\uff0c\u5f20\u606a\u5f53\u65f6\u5f3a\u5fcd\u4f4f\u6ca1\u6709\u56de\u90ae\u4ef6\uff0c\u81ea\u7136\u4e5f\u4e0d\u6653\u5f97\u5510\u5a67\u4ece\u8c01\u90a3\u91cc\u77e5\u9053\u81ea\u5df1\u7684\u90ae\u7bb1\u5730\u5740\uff0c\u8fd9\u65f6\u5019\u60f3\u8d77\u6765\uff0c\u5fc3\u91cc\u6709\u7740\u5f02\u6837\u7684\u60b8\u52a8\uff0c\u7275\u8fc7\u5510\u5a67\u67d4\u8f6f\u5ae9\u6ed1\u7684\u5c0f\u624b\uff0c\u8f7b\u8f7b\u7684\u637b\u4e86\u637b\u3002</p><p>\u3000\u3000\u7a81\u7136\u5176\u6765\u7684\u4eb2\u70ed\u4e3e\u52a8\uff0c\u53ea\u662f\u8ba9\u5510\u5a67\u5fc3\u91cc\u4e00\u7f8e\uff0c\u5e76\u4e0d\u6653\u5f97\u5f20\u606a\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u5f20\u606a\u7a0d\u7a0d\u8e29\u4e86\u4e0b\u6cb9\u95e8\uff0c\u5c06\u8f66\u5b50\u5f00\u5230\u90a3\u4e2a\u7ad9\u5728\u8d70\u5eca\u4e0b\u3001\u9ad8\u4e2d\u4e09\u5e74\u6765\u6ca1\u6709\u4ea4\u96c6\u7684\u5973\u5b69\u5b50\u8ddf\u524d\u2014\u2014\u5979\u4e5f\u4e0d\u662f\u5510\u5a67\u7684\u540c\u684c\u2014\u2014\u6309\u4e0b\u8f66\u7a97\uff0c\u770b\u7740\u5979\u7565\u5e26\u6101\u7eea\u7684\u7ad9\u5728\u8d70\u5eca\u4e0b\uff0c\u5927\u6982\u5df2\u7ecf\u610f\u8bc6\u5230\u81ea\u5df1\u8003\u7684\u4e0d\u597d\uff0c\u4e5f\u6216\u8bb8\u7eaf\u7cb9\u53ea\u662f\u7ed9\u5927\u96e8\u56f0\u4f4f\uff0c\u5f20\u606a\u80f3\u818a\u6401\u5728\u6e7f\u6f09\u6f09\u7684\u5fae\u51c9\u7684\u8f66\u7a97\u73bb\u7483\u4e0a\uff0c\u5bf9\u5979\u8bf4\uff1a\u201c\u6211\u77e5\u9053\u4f60\u5bb6\u5728\u54ea\u91cc\uff0c\u6211\u4eec\u7b49\u4f1a\u513f\u4f1a\u7ecf\u8fc7\u90a3\u91cc\uff0c\u987a\u4fbf\u9001\u4f60\u56de\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u5973\u5b69\u5b50\u8be7\u5f02\u7684\u770b\u7740\u5f20\u606a\uff0c\u90fd\u4e0d\u8bb0\u5f97\u4e00\u4e2d\u8fd9\u4e2a\u98ce\u4e91\u4eba\u7269\u9ad8\u4e2d\u4e09\u5e74\u6709\u6ca1\u6709\u6b63\u773c\u770b\u8fc7\u81ea\u5df1\uff0c\u7a81\u7136\u63d0\u51fa\u8981\u9001\u7ed9\u56f0\u5728\u5927\u96e8\u7684\u81ea\u5df1\u56de\u5bb6\uff0c\u6709\u4e9b\u60f6\u60f6\u4e0d\u5b89\u3002</p><p>\u3000\u3000\u201c\u5feb\u4e0a\u8f66\u5427\uff01\u201d\u5510\u5a67\u5934\u4f38\u8fc7\u6765\uff0c\u5a07\u67d4\u7684\u8eab\u5b50\u6beb\u65e0\u4ecb\u610f\u7684\u538b\u5728\u5f20\u606a\u7684\u817f\u8179\u4e4b\u95f4\u9080\u8bf7\u9694\u58c1\u73ed\u7684\u5973\u540c\u5b66\u4e0a\u8f66\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u6211\u6700\u540e\u4e00\u9053\u8bba\u8ff0\u9898\u7b54\u5f97\u4e0d\u591f\u5b8c\u7f8e\uff01\u201d\u675c\u98de\u6025\u51b2\u51b2\u7684\u4ece\u540e\u9762\u8d76\u8fc7\u6765\u62c9\u5f00\u8f66\u95e8\u94bb\u8fdb\u8f66\u5b50\u91cc\uff0c\u5927\u58f0\u62b1\u6028\u4e0d\u5df2\u3002\u5f20\u606a\u542c\u4ed6\u80fd\u5927\u58f0\u62b1\u6028\uff0c\u5927\u6982\u8003\u8bd5\u4e0d\u4f1a\u592a\u7cdf\u7cd5\u3002\u675c\u98de\u8fd9\u65f6\u5019\u624d\u770b\u89c1\u9694\u58c1\u73ed\u7684\u6f02\u4eae\u5973\u751f\u6ca1\u6709\u4f1e\u5c31\u7ad9\u5728\u8d70\u5eca\u4e0b\uff0c\u6253\u5f00\u8f66\u95e8\uff0c\u8bf4\u9053\uff1a\u201c\u5582\uff0c\u4e0a\u8f66\u4e86\uff0c\u6211\u77e5\u9053\u4f60\u5bb6\u4f4f\u90a3\u91cc\uff0c\u6211\u4eec\u8f66\u5b50\u5f80\u90a3\u91cc\u7ed5\u4e00\u4e0b\u4e5f\u5c31\u51e0\u5206\u949f\u7684\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u5f00\u8f66\u9001\u5973\u5b69\u5230\u5979\u5bb6\u5df7\u5b50\u53e3\uff0c\u5979\u8bf4\u5230\u5df7\u5b50\u53e3\u7684\u4fbf\u5229\u5e97\u80fd\u501f\u5230\u96e8\u4f1e\uff0c\u6ca1\u6709\u8ba9\u5f20\u606a\u5c06\u8f66\u5f00\u8fdb\u72ed\u7a84\u7684\u5e73\u623f\u5df7\u5b50\u91cc\uff0c\u8f66\u5b50\u5c31\u505c\u5728\u4fbf\u5229\u5e97\u95e8\u53e3\uff0c\u5f20\u606a\u53d6\u4e86\u4e00\u5f20\u540d\u7247\u9012\u51fa\u8f66\u7a97\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u662f\u8003\u5f97\u4e0d\u597d\uff0c\u518d\u590d\u4e60\u4e00\u5e74\u5c31\u662f\u4e86\uff0c\u8bb0\u4f4f\u6211\u4eec\u8fd8\u7b97\u662f\u540c\u5b66\u2026\u2026\u201d</p><p>\u3000\u3000\u8f66\u5b50\u5f00\u51fa\u53bb\uff0c\u770b\u7740\u7ad9\u5728\u96e8\u5e18\u4e0b\u6709\u4e9b\u83ab\u660e\u5176\u5999\u62ff\u7740\u5f20\u606a\u540d\u7247\u7684\u5973\u5b69\u5b50\uff0c\u675c\u98de\u8be7\u5f02\u7684\u95ee\u5f20\u606a\uff1a\u201c\u4f60\u8ddf\u5979\u5f88\u719f\u554a\uff1f\u90fd\u6ca1\u770b\u89c1\u4f60\u7ed9\u522b\u4eba\u53d1\u540d\u7247\u554a\u2026\u2026\u201d\u540d\u7247\u5939\u5c31\u6401\u5728\u4eea\u8868\u76d8\u4e0b\u7684\u7a7a\u683c\u91cc\uff0c\u675c\u98de\u53d6\u51fa\u4e00\u5f20\u62ff\u5728\u624b\u91cc\u628a\u73a9\uff0c\u8fd8\u771f\u662f\u7cbe\u7f8e\uff0c\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u604d\u7136\u53d1\u73b0\u672c\u5e94\u8be5\u5370\u540d\u5b57\u7684\u5730\u65b9\u5374\u662f\u7a7a\u767d\uff0c\u201c\u600e\u4e48\u6ca1\u6709\u5370\u540d\u5b57\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u9700\u8981\u7684\u65f6\u5019\u76f4\u63a5\u7b7e\u4e0a\u53bb\uff0c\u201d\u5f20\u606a\u6253\u7740\u65b9\u5411\u76d8\u62d0\u4e0a\u9526\u6e56\u4e1c\u8def\uff0c\u201c\u7ed9\u5979\u7684\u90a3\u5f20\u662f\u7b7e\u8fc7\u540d\u5b57\u7684\u2026\u2026\u201d\u53c8\u8ddf\u5510\u5a67\u3001\u675c\u98de\u8bf4\u9053\uff0c\u201c\u6211\u53bb\u63a5\u4e86\u5085\u4fca\u5c31\u76f4\u63a5\u53bb\u7701\u57ce\u4e58\u98de\u673a\u53bb\u9999\u6e2f\uff0c\u4f60\u4eec\u4fe9\u7684\u7b7e\u8bc1\u3001\u673a\u7968\uff0c\u6211\u90fd\u8ba9\u4eba\u5e2e\u4f60\u4eec\u529e\u59a5\u4e86\uff0c\u8c01\u4e0d\u9ad8\u5174\u53bb\u9999\u6e2f\uff0c\u81ea\u5df1\u4e0b\u8f66\u627e\u51fa\u79df\u8f66\u56de\u5bb6\u3002\u201d</p><p>\u3000\u3000\u201c\u9760\uff0c\u6211\u8fd8\u8ddf\u76db\u590f\u8bf4\u6700\u65e9\u660e\u5929\u624d\u80fd\u5230\u9999\u6e2f\u5462\uff01\u201d\u675c\u98de\u4e0d\u6ee1\u7684\u5600\u5495\u4e86\u4e00\u58f0\uff0c\u8d76\u7d27\u62ff\u51fa\u624b\u673a\u7ed9\u4ed6\u5988\u6253\u7535\u8bdd\u8bf4\u76f4\u63a5\u53bb\u9999\u6e2f\u7684\u4e8b\u60c5\uff0c\u5510\u5a67\u4e5f\u62ff\u4e86\u7535\u8bdd\u8ddf\u5979\u7238\u62a5\u4fe1\uff0c\u5bb6\u91cc\u4eba\u90fd\u5173\u5fc3\u6700\u540e\u4e00\u95e8\u5386\u53f2\u7684\u8003\u8bd5\u60c5\u51b5\uff0c\u7b49\u4ed6\u4eec\u5c06\u7535\u8bdd\u653e\u4e0b\uff0c\u5f20\u606a\u5df2\u7ecf\u5c06\u8f66\u5f00\u5230\u4e94\u91cc\u6eaa\u5e7f\u573a\u7684\u9526\u6e56\u5927\u53a6\u524d\u3002</p><p>\u3000\u3000\u9664\u4e86\u5085\u4fca\u8fd8\u5c06\u6709\u4e00\u540d\u884c\u653f\u79d8\u4e66\u968f\u884c\uff0c\u6362\u4e86\u8f86\u9053\u5947\u516c\u7f8a\uff0c\u516c\u53f8\u522b\u6d3e\u4e86\u540d\u53f8\u673a\u9001\u4ed6\u4eec\u53bb\u5efa\u90ba\u56fd\u9645\u673a\u573a\u3002</p><p>\u3000\u3000\u6d77\u5dde\u7684\u5929\u7ed9\u6345\u7834\u4e00\u4e2a\u7a9f\u7abf\u4f3c\u7684\u5927\u96e8\u503e\u76c6\u800c\u4e0b\uff0c\u79bb\u6d77\u5dde\u5730\u754c\uff0c\u96e8\u5c31\u6536\u4f4f\u4e86\uff0c\u7ee7\u7eed\u5f80\u897f\u884c\u9a76\uff0c\u5929\u8272\u9634\u973e\uff0c\u5373\u5c06\u503e\u76c6\u5927\u96e8\u7684\u6a21\u6837\uff0c\u5374\u4e0d\u59a8\u788d\u8f66\u8f86\u5728\u9ad8\u901f\u516c\u8def\u4e0a\u98de\u9a70\uff0c\u6328\u7740\u65f6\u95f4\u8d76\u5230\u5efa\u90ba\u56fd\u9645\u673a\u573a\uff0c\u4e0d\u9700\u8981\u6539\u7b7e\u4e0b\u4e00\u73ed\u822a\u73ed\uff0c\u591c\u91cc\u4e03\u70b9\u949f\uff0c\u62b5\u8fbe\u9999\u6e2f\u542f\u5fb7\u673a\u573a\u3002</p><p>\u3000\u3000\u9999\u6e2f\u542f\u5fb7\u673a\u573a\u518d\u8fc7\u4e00\u5e74\u5c31\u8981\u505c\u7528\uff0c\u964d\u843d\u524d\u770b\u5230\u68cb\u76d8\u5c71\u7684\u706f\u5149\uff0c\u98de\u673a\u4f1a\u6709\u4e00\u4e2a\u4f4e\u7a7a\u76f4\u89d2\u5927\u8f6c\u5f2f\u7684\u964d\u843d\u52a8\u4f5c\uff0c\u4f1a\u8ba9\u4e58\u5ba2\u6709\u5750\u4e91\u5bb5\u98de\u8f66\u822c\u7684\u523a\u6fc0\uff1b\u5f20\u606a\u662f\u4f53\u4f1a\u4e86\u591a\u6b21\uff0c\u770b\u5230\u68cb\u76d8\u5c71\u7684\u706f\u706b\uff0c\u5fc3\u91cc\u8fd8\u60ac\u60ac\u7684\uff1b\u5510\u5a67\u62fd\u7d27\u5f20\u606a\u7684\u80f3\u818a\uff0c\u8138\u4e0a\u7684\u8868\u60c5\u53c8\u662f\u7d27\u5f20\u53c8\u662f\u671f\u5f85\uff0c\u90fd\u4e0d\u6e05\u695a\u5979\u7d27\u5f20\u4ec0\u4e48\uff0c\u671f\u5f85\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u6ca1\u6709\u4ec0\u4e48\u884c\u674e\uff0c\u5510\u5a67\u3001\u675c\u98de\u8003\u5b8c\u6700\u540e\u4e00\u95e8\u5386\u53f2\u90fd\u6ca1\u6709\u56de\u5bb6\uff0c\u4e2d\u9910\u90fd\u662f\u5728\u9ad8\u901f\u8def\u670d\u52a1\u533a\u4e70\u7684\u5feb\u9910\uff0c\u5012\u662f\u6e2f\u9f99\u822a\u7a7a\u63d0\u4f9b\u7684\u5546\u52a1\u8231\u665a\u9910\u4e0d\u9519\uff1b\u4e58\u7535\u68af\u4e0b\u6765\uff0c\u5c31\u770b\u89c1\u53f6\u5efa\u658c\u3001\u5b59\u5c1a\u4e49\u5c40\u4fc3\u4e0d\u5b89\u7684\u7ad9\u5728\u63a5\u673a\u5927\u5385\u91cc\u7b49\u5019\u3002</p><p>\u3000\u3000\u4e00\u4e2a\u534a\u6708\u6765\uff0c\u53f6\u5efa\u658c\u5c31\u5bf8\u6b65\u6ca1\u79bb\u5f00\u8fc7\u9999\u6e2f\uff0c\u7a9d\u4e00\u4e2a\u57ce\u5e02\u91cc\uff0c\u90fd\u5feb\u957f\u51fa\u8349\u6765\u4e86\uff0c\u504f\u504f\u8fd9\u6bb5\u65f6\u95f4\u5b59\u9759\u9999\u4e0e\u6c5f\u9edb\u513f\u3001\u8bb8\u7ef4\u7b49\u4eba\u4e00\u76f4\u5728\u53f0\u6e7e\u3001\u9a6c\u6765\u897f\u4e9a\u505a\u65b0\u6b4c\u5ba3\u4f20\u3002</p><p>\u3000\u3000\u201c\u597d\u54c7\uff0c\u7ec8\u4e8e\u7b49\u4f60\u8fc7\u6765\u4e86\uff0c\u6211\u5fc3\u91cc\u90fd\u5feb\u957f\u8349\u4e86\u3002\u201d\u53f6\u5efa\u658c\u5927\u58f0\u62b1\u6028\u7684\u8bf4\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u5fc3\u6ca1\u80ba\u7684\u8bf4\u9053\uff1a\u201c\u56db\u4e2a\u4ebf\u90fd\u4e8f\u6389\uff0c\u5bf9\u6211\u4e5f\u4e0d\u4f24\u7b4b\u75db\u9aa8\uff0c\u5b9e\u5728\u6ca1\u5fc5\u8981\u592a\u8ba1\u8f83\u2026\u2026\u201d\u53c8\u6ca1\u5fc3\u6ca1\u80ba\u7684\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u5472\u7259\u54a7\u5634\u7684\u53bb\u634f\u5f20\u606a\u7684\u8116\u5b50\uff0c\u8bf4\u5b9e\u8bdd\uff0c\u770b\u5230\u5f20\u606a\u62b5\u8fbe\u9999\u6e2f\uff0c\u8eab\u4e0a\u7684\u538b\u529b\u611f\u89c9\u5378\u6389\u4e00\u534a\u4f3c\u7684\u8f7b\u677e\u8d77\u6765\u3002</p><p>\u3000\u3000\u5750\u8fdb\u8f66\u91cc\uff0c\u53f6\u5efa\u658c\u5c31\u8feb\u4e0d\u53ca\u5f85\u7684\u5c06\u4e00\u53e0\u8d44\u6599\u4e22\u5230\u5f20\u606a\u7684\u6000\u91cc\uff1a\u201c\u4f60\u770b\u770b\uff0c\u8fd9\u662f\u6700\u8fd1\u4e00\u4e2a\u6708\u4ee5\u6765\u6cf0\u56fd\u6240\u9762\u4e34\u7684\u7ecf\u6d4e\u5904\u5883\uff0c\u201d\u53c8\u770b\u4e86\u770b\u675c\u98de\u3001\u5510\u5a67\uff0c\u95ee\u5f20\u606a\uff0c\u201c\u4e00\u8d77\u53bb\u57fa\u5730\uff1f\u201d</p><p>\u3000\u3000\u201c\u57fa\u5730\uff1f\u201d\u5f20\u606a\u542c\u8fd9\u4e2a\u8bcd\u523a\u8033\uff0c911\u4e8b\u4ef6\u8fd8\u6ca1\u6709\u53d1\u751f\uff0c\u53f6\u5efa\u658c\u4ed6\u4eec\u81ea\u7136\u4e0d\u4f1a\u89c9\u5f97\u8bf4\u201c\u57fa\u5730\u201d\u8fd9\u4e2a\u8bcd\u6709\u4ec0\u4e48\u522b\u7684\u542b\u4e49\uff0c\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u4e00\u8d77\u8fc7\u53bb\u770b\u770b\uff0c\u8ba9\u675c\u98de\u957f\u957f\u89c1\u8bc6\u4e5f\u597d\u2026\u2026\u201d</p><p>\u3000\u3000\u6240\u8c13\u7684\u57fa\u5730\u5728\u4e5d\u9f99\u5c16\u6c99\u5480\u7684\u534a\u5c9b\u9152\u5e97\u91cc\uff0c\u53f6\u5efa\u658c\u5728\u8fd9\u91cc\u4e0e\u8058\u8bf7\u7684\u5bf9\u51b2\u57fa\u91d1\u7ba1\u7406\u56e2\u961f\u901a\u8fc7\u7535\u8baf\u53d1\u51fa\u6307\u4ee4\u6307\u6325\u9999\u6e2f\u3001\u65b0\u52a0\u5761\u5916\u6c47\u4ea4\u6613\u5e02\u573a\u7684\u64cd\u76d8\u624b\u8fdb\u884c\u5916\u6c47\u64cd\u76d8\u3002</p><p>\u3000\u3000\u534a\u5c9b\u9152\u5e97\u662f\u9999\u6e2f\u73b0\u5b58\u5386\u53f2\u6700\u60a0\u4e45\u7684\u9152\u5e97\uff0c\u5f20\u606a\u4ed6\u4eec\u7684\u623f\u95f4\u662f\u5317\u7ffc\u65b0\u697c\u7684\u4e8c\u5341\u516b\u5c42\u6d77\u666f\u5957\u623f\uff0c\u5957\u623f\u6709\u4e09\u4e2a\u623f\u95f4\uff0c\u6709\u8d77\u5c45\u5ba4\u3001\u4f1a\u8bae\u5ba4\u8fd8\u6709\u79c1\u4eba\u9152\u5427\u3002</p><p>\u3000\u3000\u8fd9\u5e76\u4e0d\u662f\u534a\u5c9b\u9152\u5e97\u6700\u8c6a\u534e\u7684\u5957\u623f\uff0c\u9876\u5c42\u7684\u5957\u623f\u8fd8\u6709\u79c1\u4eba\u6237\u5916\u9633\u5149\u82b1\u623f\u4e0e\u6e38\u6cf3\u6c60\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e22\u7ed9\u5510\u5a67\u4e00\u652f\u9999\u6e2f\u5728\u5907\u7528\u7684\u624b\u673a\uff0c\u8ba9\u5979\u8054\u7cfb\u76db\u590f\u8fc7\u6765\u2014\u2014\u603b\u4e4b\u4ec5\u4ec5\u534a\u5c9b\u9152\u5e97\u5c31\u80fd\u8ba9\u5979\u65b0\u5947\u7684\u6e21\u8fc7\u4e24\u4e09\u5929\uff0c\u5f20\u606a\u5219\u8981\u6293\u7d27\u65f6\u95f4\u7ffb\u4e00\u7ffb\u53f6\u5efa\u658c\u4e22\u7ed9\u4ed6\u7684\u4e00\u53e0\u8d44\u6599\uff0c\u675c\u98de\u4e5f\u6ca1\u6709\u95f2\u7740\uff0c\u7ed9\u4e22\u4e86\u4e00\u5806\u8d44\u6599\u5728\u770b\u3002</p><p>\u3000\u3000\u867d\u8bf4\u8fd9\u4e09\u5e74\uff0c\u675c\u98de\u5bf9\u91d1\u878d\u8bc1\u5238\u7b49\u8d22\u7ecf\u77e5\u8bc6\u6709\u4e86\u76f8\u5f53\u7a0b\u5ea6\u7684\u4e86\u89e3\uff0c\u4f46\u662f\u8981\u4ed6\u770b\u5230\u624b\u91cc\u7684\u8fd9\u4e9b\u8d44\u6599\uff0c\u591a\u5c11\u6709\u4e9b\u5f3a\u4eba\u6240\u96be\uff0c\u4e0d\u8fc7\u4e5f\u5f97\u786c\u7740\u5934\u76ae\u770b\u4e0b\u53bb\u3002\u5510\u5a67\u5230\u9999\u6e2f\u6765\u662f\u9ad8\u8003\u540e\u653e\u677e\u81ea\u5df1\uff0c\u675c\u98de\u5374\u662f\u5728\u98de\u673a\u4e0a\u88ab\u544a\u77e5\u4ed6\u7ed9\u62c9\u5230\u9999\u6e2f\u6765\u662f\u8981\u4ed6\u4eb2\u8eab\u4f53\u4f1a\u4e00\u4e0b\u56fd\u9645\u91d1\u878d\u6295\u673a\u7684\u5c16\u5cf0\u65f6\u95f4\uff1a\u76ee\u7779\u91d1\u878d\u5de8\u9cc4\u8054\u5408\u8d77\u6765\u6b82\u51fb\u4e00\u4e2a\u56fd\u5bb6\u7684\u91d1\u878d\u4f53\u7cfb\uff0c\u5e76\u4e0d\u662f\u8c01\u90fd\u6709\u8fd9\u4e2a\u5e78\u8fd0\u673a\u4f1a\u7684\u3002</p><p>\u3000\u3000\u201c\u76db\u590f\u9a6c\u4e0a\u5c31\u4e58\u8f66\u8fc7\u6765\uff0c\u201d\u5510\u5a67\u53cc\u819d\u8dea\u5728\u6c99\u53d1\u4e0a\uff0c\u5c06\u624b\u673a\u4e22\u5230\u6728\u51e0\u4e0a\uff0c\u97e7\u6027\u6781\u4f73\u7684\u8eab\u5b50\u5374\u6273\u8fc7\u6765\u770b\u7740\u5f20\u606a\u624b\u91cc\u7684\u8d44\u6599\uff0c\u80f3\u818a\u73af\u62e5\u7740\u5f20\u606a\u7684\u9888\u8116\u5b50\uff0c\u4e0b\u5df4\u8f7b\u8f7b\u7684\u538b\u7740\u4ed6\u7684\u5934\u53d1\uff0c\u8981\u662f\u6709\u4e00\u5757\u5e03\u8499\u7740\u5979\u626d\u7740\u7684\u5c0f\u86ee\u8170\uff0c\u522b\u4eba\u8fd8\u4ee5\u4e3a\u5979\u7684\u8eab\u4f53\u5bf9\u6298\u4e86\u5462\uff0c\u201c\u6211\u7b49\u4f1a\u513f\u53bb\u697c\u4e0b\u63a5\u5979\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u3001\u5b59\u5c1a\u4e49\u4e0e\u4e00\u540d\u8eab\u6750\u9ad8\u5927\u7684\u5e74\u8f7b\u767d\u4eba\u8d70\u8fdb\u6765\uff0c\u5510\u5a67\u5012\u662f\u6062\u590d\u6dd1\u5973\u6a21\u6837\uff0c\u4e56\u5de7\u7684\u5750\u5728\u5f20\u606a\u7684\u8eab\u8fb9\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4f4d\u662f\u5c3c\u514b\u674e\u68ee\u5148\u751f\u2026\u2026\u201d\u53f6\u5efa\u658c\u5e2e\u5f20\u606a\u4ecb\u7ecd\u4ed6\u4eec\u7684\u57fa\u91d1\u7ecf\u7406\u3002</p><p>\u3000\u3000\u201c\u53eb\u6211\u674e\u68ee\u5c31\u53ef\u4ee5\u4e86\u2026\u2026\u201d\u5c3c\u514b\u674e\u68ee\u7528\u4ed6\u521a\u5b66\u7684\u8e69\u811a\u666e\u901a\u8bed\u6253\u62db\u547c\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u8d77\u6765\u4e0e\u8eab\u9ad8\u8981\u8d85\u8fc7\u4e00\u7c73\u4e5d\u7684\u5c3c\u514b\u674e\u68ee\u63e1\u624b\uff0c\u4ed6\u4e4b\u524d\u770b\u8fc7\u5c3c\u514b\u674e\u68ee\u7684\u8be6\u7ec6\u8d44\u6599\uff0c\u5f88\u96be\u4ece\u5916\u8868\u5224\u65ad\u6b27\u7f8e\u4eba\u7684\u5e74\u9f84\u3002\u4f5c\u4e3a\u66fe\u7ecf\u7684\u5370\u5730\u9ea6\u514b\u94f6\u884c\u9a7b\u4e1c\u4eac\u671f\u8d27\u516c\u53f8\u7684\u603b\u7ecf\u7406\u3001\u9996\u5e2d\u4ea4\u6613\u5458\uff0c\u5c3c\u514b\u674e\u68ee\u6b64\u65f6\u4e5f\u4e0d\u8fc7\u4e8c\u5341\u516b\u5c81\u800c\u5df2\uff1b\u4e0e\u5bfc\u81f4\u5df4\u6797\u94f6\u884c\u5012\u95ed\u7684\u90a3\u4e2a\u5c3c\u514b\u674e\u68ee\u540c\u540d\u540c\u59d3\uff0c\u4e5d\u5341\u5e74\u4ee3\u4e2d\u671f\uff0c\u5728\u4e9a\u6d32\u671f\u8d27\u5e02\u573a\uff0c\u6709\u4e24\u4e2a\u5c3c\u514b\u674e\u68ee\u6bd4\u8f83\u53d7\u5173\u6ce8\uff0c\u4e0d\u8fc7\u8fd9\u4f4d\u5c3c\u514b\u674e\u68ee\u4e00\u76f4\u7ed9\u5df4\u6797\u94f6\u884c\u90a3\u4f4d\u5929\u624d\u4ea4\u6613\u5458\u7684\u5149\u8292\u7f69\u4f4f\uff0c\u5f53\u7136\uff0c\u5df4\u6797\u94f6\u884c\u5012\u95ed\u4e4b\u540e\uff0c\u8981\u8bf4\u540d\u58f0\u7684\u54cd\u4eae\uff0c\u5c31\u66f4\u4e0d\u80fd\u76f8\u6bd4\u4e86\uff0c\u4f46\u662f\u5e76\u4e0d\u80fd\u62b9\u6740\u8fd9\u4f4d\u5c3c\u514b\u674e\u68ee\u5728\u671f\u8d27\u5e02\u573a\u7684\u6770\u51fa\u80fd\u529b\u3002\u53ea\u662f\u5df4\u6797\u94f6\u884c\u5012\u95ed\u6848\uff0c\u5bf9\u5370\u5730\u9ea6\u514b\u94f6\u884c\u7684\u89e6\u52a8\u6781\u5927\uff0c\u540c\u65f6\u8be5\u94f6\u884c\u5728\u4e9a\u6d32\u7684\u671f\u8d27\u516c\u53f8\u4e5f\u7206\u51fa\u8d44\u91d1\u4e11\u95fb\uff0c\u4e3a\u4e86\u89c4\u907f\u98ce\u9669\uff0c\u8be5\u94f6\u884c\u4e8e\u4e5d\u516d\u5e74\u4e0b\u534a\u5e74\u5173\u95ed\u5728\u4e9a\u6d32\u4e09\u5927\u4ea4\u6613\u5e02\u573a\u7684\u671f\u8d27\u516c\u53f8\uff0c\u5728\u4e9a\u6d32\u671f\u8d27\u5e02\u573a\u594b\u6597\u4e86\u591a\u5e74\u7684\u5c3c\u514b\u674e\u68ee\u5c31\u8131\u79bb\u5370\u5730\u9ea6\u514b\u94f6\u884c\uff0c\u76f4\u5230\u53f6\u5efa\u658c\u627e\u5230\u4ed6\u4e4b\u524d\uff0c\u4ed6\u4e00\u76f4\u5728\u4f11\u5047\uff0c\u8fd8\u6ca1\u6709\u51fa\u9762\u7ba1\u7406\u4e00\u5bb6\u79c1\u52df\u57fa\u91d1\u7684\u5ff5\u5934\u3002</p><p>\u3000\u3000\u5728\u4e9a\u6d32\u671f\u8d27\u5e02\u573a\u6709\u5f88\u591a\u6d3b\u8dc3\u7684\u90fd\u662f\u89e6\u89c9\u654f\u9510\u3001\u6562\u4e8e\u5192\u9669\u7684\u5e74\u8f7b\u4eba\uff0c\u5c3c\u514b\u674e\u68ee\u4e5f\u662f\u4e8c\u5341\u4e09\u5c81\u65f6\u5c31\u7ed9\u53d1\u6398\u51fa\u6765\u638c\u63e1\u4e0a\u4ebf\u7f8e\u5143\u7684\u671f\u8d27\u57fa\u91d1\u3002</p><p>\u3000\u3000\u6b64\u6b21\u5728\u5f00\u66fc\u7fa4\u5c9b\u79d8\u5bc6\u6ce8\u518c\u6709\u9650\u5408\u4f19\u4eba\u5236\u7684\u79c1\u52df\u5bf9\u51b2\u57fa\u91d1\u989d\u624d1.4\u4ebf\u7f8e\u5143\uff0c\u9664\u4e86\u5f20\u606a\u4e0e\u53f6\u3001\u5b59\u3001\u845b\u4e09\u5bb6\u7684\u4efd\u989d\u4e4b\u5916\uff0c\u5c3c\u514b\u674e\u68ee\u6240\u7ec4\u7ec7\u8d77\u6765\u7684\u57fa\u91d1\u7ba1\u7406\u56e2\u961f\u4e5f\u6295\u5165\u4e00\u5343\u4e24\u767e\u4e07\u7f8e\u5143\u3002</p></article>"}}}]);
