(window.webpackJsonp=window.webpackJsonp||[]).push([[585],{608:function(p){p.exports={chapterTitle:null,title:"\u7b2c585\u7ae0 \u610f\u5916\u76f8\u9022",content:"<article><h2>\u7b2c585\u7ae0 \u610f\u5916\u76f8\u9022</h2><p>\u3000\u3000\u5728\u5c3c\u514b\u674e\u68ee\u773c\u91cc\uff0c\u5f20\u606a\u3001\u53f6\u5efa\u658c\u3001\u5b59\u5c1a\u4e49\u7b49\u4eba\u662f\u91d1\u4e3b\uff0c\u91d1\u4e3b\u4e00\u822c\u6781\u5c11\u5e72\u9884\u5bf9\u51b2\u57fa\u91d1\u7684\u5177\u4f53\u64cd\u4f5c\uff0c\u4e0d\u8fc7\u53f6\u5efa\u658c\u627e\u5230\u4ed6\u65f6\uff0c\u660e\u786e\u63d0\u51fa\u8981\u53c2\u4e0e\u8fd9\u6b21\u4e1c\u5357\u4e9a\u7684\u72e9\u730e\u3002\u53f6\u5efa\u658c\u6240\u8868\u73b0\u51fa\u6765\u7684\u5bf9\u4e1c\u5357\u4e9a\u91d1\u878d\u3001\u7ecf\u6d4e\u5f62\u52bf\u7684\u6df1\u523b\u89c1\u89e3\uff0c\u867d\u7136\u4e0d\u5e38\u89c1\uff0c\u5c3c\u514b\u674e\u68ee\u8fd8\u662f\u540c\u610f\u53f6\u5efa\u658c\u76f4\u63a5\u53c2\u4e0e\u8fdb\u6765\u3002\u4e0d\u8fc7\u57fa\u91d1\u90fd\u5df2\u7ecf\u5f00\u59cb\u8fd0\u884c\u8fd1\u4e00\u4e2a\u6708\uff0c\u51fa\u8d44\u6700\u591a\u7684\u91d1\u4e3b\u624d\u59d7\u59d7\u9732\u9762\uff0c\u5c3c\u514b\u674e\u68ee\u4e5f\u5f88\u5c11\u542c\u8bf4\u4e1a\u5185\u6709\u8c01\u9047\u5230\u8fd9\u6837\u7684\u60c5\u5f62\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5c3c\u514b\u674e\u68ee\u7b80\u5355\u7684\u804a\u4e86\u804a\uff0c\u5927\u5bb6\u90fd\u5173\u5fc3\u7684\u4e3b\u9898\u5374\u6ca1\u6709\u6d89\u53ca\uff0c\u5728\u5957\u623f\u7684\u79c1\u4eba\u9152\u5427\u91cc\u62c9\u5bb6\u5e38\u4f3c\u7684\u804a\u4e86\u534a\u4e2a\u591a\u5c0f\u65f6\uff0c\u5f85\u76db\u590f\u4ece\u6e2f\u5c9b\u90a3\u8fb9\u4e58\u5730\u94c1\u8d76\u8fc7\u6765\uff0c\u4fbf\u7ed3\u675f\u4e86\u8c08\u8bdd\u3002</p><p>\u3000\u3000\u534a\u5c9b\u9152\u5e97\u91cc\uff0c\u9664\u4e86\u5c3c\u514b\u674e\u6797\u4e4b\u5916\uff0c\u8fd8\u6709\u4ed6\u7684\u4e24\u540d\u4e3b\u8981\u52a9\u624b\uff0c\u4e00\u4e2a\u8d1f\u8d23\u8d44\u6599\u6536\u96c6\uff0c\u4e00\u4e2a\u8d1f\u8d23\u8d44\u6599\u5206\u6790\uff0c\u6700\u7ec8\u7531\u5c3c\u514b\u674e\u68ee\u505a\u51fa\u5224\u65ad\u6307\u6325\u5728\u9999\u6e2f\u3001\u65b0\u52a0\u5761\u5916\u6c47\u4ea4\u6613\u5e02\u573a\u4e00\u5171\u516d\u540d\u4ea4\u6613\u5458\u8fdb\u884c\u4ea4\u6613\u3002</p><p>\u3000\u3000\u534a\u5c9b\u9152\u5e97\u5185\u7684\u9910\u5385\u4e0e\u9152\u5427\u582a\u79f0\u5168\u6e2f\u6700\u5c0a\u8d35\u3001\u6700\u9ad8\u96c5\u7684\u7f8e\u98df\u573a\u6240\uff0c\u9999\u6e2f\u8bb8\u591a\u5f71\u661f\u90fd\u559c\u6b22\u5230\u534a\u5c9b\u6765\u7528\u9910\uff0c\u949f\u695a\u7ea2\u3001\u5f20\u66fc\u7389\u90fd\u662f\u8fd9\u91cc\u7684\u5ea7\u4e0a\u5e38\u52a1\uff0c\u5510\u5a67\u6bd4\u8f83\u8ff7\u5f20\u66fc\u7389\uff0c\u542c\u5f20\u606a\u80e1\u5439\u8fc7\uff0c\u7b49\u5f20\u606a\u4e0e\u5c3c\u514b\u674e\u68ee\u4ed6\u4eec\u8c08\u5b8c\u8bdd\uff0c\u5510\u5a67\u5c31\u6002\u607f\u7740\u53bb\u697c\u4e0b\u7684\u9910\u5427\u53bb\u78b0\u78b0\u8fd0\u6c14\u3002</p><p>\u3000\u3000\u5750\u5728Telix\u9910\u5385\u91cc\uff0c\u53ef\u4ee5\u4eab\u53d7\u7ef4\u591a\u5229\u4e9a\u6e2f\u6e7e\u7684\u7480\u74a8\u591c\u666f\uff0c\u5f20\u606a\u966a\u5510\u5a67\u5979\u4eec\u521a\u8de8\u6b65\u8fc8\u8fdb\u9910\u5385\u7ed5\u8fc7\u9542\u96d5\u7684\u5c4f\u98ce\uff0c\u5c31\u542c\u89c1\u8eab\u540e\u4f20\u6765\u8ba9\u4ed6\u611f\u89c9\u6709\u4e9b\u719f\u6089\u7684\u58f0\u97f3\uff0c\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u773c\u73e0\u5b50\u5dee\u70b9\u90fd\u5413\u6389\u4e86\u3002</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u8c22\u5b50\u5609\u7b49\u5f20\u606a\u56de\u8fc7\u5934\u6765\u770b\u89c1\u4ed6\u7684\u8138\uff0c\u60ca\u559c\u7684\u8f7b\u547c\u4e86\u4e00\u58f0\u3002\u5979\u4e0a\u5348\u8003\u5b8c\u8bd5\u76f4\u63a5\u4ece\u60e0\u5c71\u51fa\u53d1\u5230\u5efa\u90ba\u56fd\u9645\u673a\u573a\u4e58\u4e1c\u65b9\u822a\u7a7a\u7684\u822a\u73ed\u62b5\u8fbe\u9999\u6e2f\uff0c\u8981\u662f\u665a\u4e9b\u65f6\u95f4\u8fd8\u80fd\u4e0e\u5f20\u606a\u4ed6\u4eec\u5750\u6e2f\u9f99\u822a\u7a7a\u7684\u822a\u73ed\uff0c\u8fd9\u4e2a\u5728\u9ad8\u901f\u8def\u9f9f\u901f\u5f00\u7740VOLVO S40\u7684\u82f1\u4fca\u54d1\u5df4\u7ed9\u5979\u7559\u4e0b\u5f88\u6df1\u7684\u5370\u8c61\uff0c\u6709\u65f6\u5019\u9047\u5230\u9648\u9759\u8fd8\u8981\u62ff\u90a3\u5929\u9ad8\u901f\u8def\u4e0a\u53d1\u751f\u7684\u4e8b\u60c5\u8bf4\u7b11\uff0c\u9648\u9759\u53ea\u662f\u7b11\u7b11\u800c\u5df2\uff0c\u6ca1\u60f3\u5230\u4e8b\u9694\u4e24\u5e74\u4f1a\u5728\u534a\u5c9b\u9152\u5e97\u518d\u6b21\u78b0\u9762\u3002\u8c22\u5b50\u5609\u521a\u8981\u5f00\u53e3\u95ee\u5019\u4e00\u58f0\uff0c\u624d\u60f3\u8d77\u773c\u524d\u8fd9\u4eba\u662f\u6b8b\u75be\u4eba\uff0c\u201c\u54e6\u201d\u7684\u4e00\u58f0\uff0c\u4f38\u51fa\u624b\u6bd4\u5212\u4e86\u51e0\u4e0b\uff0c\u4e5f\u4e0d\u6653\u5f97\u62ff\u624b\u52bf\u600e\u4e48\u6bd4\u5212\u54d1\u8bed\uff0c\u89c1\u5f20\u606a\u9732\u51fa\u8ff7\u4eba\u7684\u5fae\u7b11\uff0c\u6b63\u8981\u4f4e\u5934\u4ece\u624b\u63d0\u888b\u91cc\u62ff\u51fa\u7eb8\u7b14\u5199\u51fa\u95ee\u5019\u7684\u8bdd\u2026\u2026</p><p>\u3000\u3000\u5510\u5a67\u6b63\u6402\u7740\u5f20\u606a\u7684\u80f3\u818a\uff0c\u770b\u5230\u8eab\u540e\u7684\u8fd9\u4e2a\u5973\u5b69\u5b50\u53cd\u5e94\u4e5f\u592a\u5947\u602a\u4e86\uff0c\u7591\u60d1\u4e0d\u89e3\u7684\u95ee\u5f20\u606a\uff1a\u201c\u5979\u5e72\u561b\uff0c\u5979\u662f\u54d1\u5df4\u5417\uff1f\u201d\u8bf4\u8fd9\u8bdd\u65f6\uff0c\u5934\u8fd8\u7279\u610f\u504f\u4e86\u504f\uff0c\u5c31\u6015\u5979\u770b\u5230\u81ea\u5df1\u7684\u5634\u5f62\u4f24\u4e86\u8fd9\u4e2a\u770b\u4e0a\u53bb\u8fd8\u6e05\u79c0\u660e\u4e3d\u7684\u5973\u5b69\u5b50\u7684\u81ea\u5c0a\u5fc3\u3002</p><p>\u3000\u3000\u542c\u5230\u5510\u5a67\u8fd9\u53e5\u8bdd\uff0c\u8c22\u5b50\u5609\u8138\u90fd\u7eff\u4e86\u3002</p><p>\u3000\u3000\u6ca1\u60f3\u5230\u4f1a\u5728\u534a\u5c9b\u9152\u5e97\u5de7\u9047\uff0c\u9648\u9759\u4e5f\u662f\u63aa\u624b\u4e0d\u53ca\uff0c\u53ea\u662f\u5c34\u5c2c\u7684\u7b11\u4e86\u7b11\uff1a\u201c\u4f60\u4eec\u4e5f\u4f4f\u534a\u5c9b\u9152\u5e97\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u8fd8\u771f\u662f\u5de7\uff0c\u6211\u6cbb\u597d\u8033\u75be\u7684\u4e8b\u60c5\uff0c\u4f60\u6ca1\u8ddf\u8c22\u5bb6\u5927\u5c0f\u59d0\u8bf4\u8bf4\uff1f\u201d\u5f20\u606a\u5634\u89d2\u5fae\u5fae\u7fd8\u7740\uff0c\u770b\u7740\u8c22\u5b50\u5609\u9634\u6c89\u7740\u8981\u6ef4\u51fa\u6c34\u6765\u7684\u5c0f\u8138\uff0c\u7b11\u4e86\u7b11\uff0c\u201c\u4f60\u4eec\u4e5f\u8fc7\u6765\u7528\u9910\uff1f\u201d</p><p>\u3000\u3000\u9648\u9759\u8981\u8bf4\u4ec0\u4e48\uff0c\u8863\u895f\u7ed9\u5b50\u5609\u626f\u4e86\u626f\uff0c\u53ea\u5f97\u62b1\u8c26\u7684\u7b11\u4e86\u7b11\uff0c\u8ddf\u5b50\u5609\u9000\u51fa\u9910\u5385\uff0c\u89c2\u770b\u7ef4\u591a\u5229\u4e9a\u6e2f\u6e7e\u6d77\u666f\u7684\u6253\u7b97\u53ea\u5f97\u4f5c\u7f62\u3002\u4e58\u7535\u68af\u56de\u5230\u623f\u95f4\uff0c\u8c22\u5b50\u5609\u624d\u5c16\u53eb\u8d77\u6765\uff1a\u201c\u592a\u53ef\u6068\u4e86\uff0c\u7adf\u7136\u7ed9\u9a97\u4e86\u4e24\u5e74\uff01\u201d\u89c1\u9648\u9759\u4f9d\u95e8\u5a77\u5a77\u800c\u7acb\uff0c\u201c\u6c14\u6b7b\u4e86\uff0c\u4f60\u7adf\u7136\u4e5f\u8ddf\u7740\u4eba\u5bb6\u9a97\u6211\u2026\u2026\u201d\u8d70\u8fc7\u6765\uff0c\u6076\u72e0\u72e0\u7684\u770b\u7740\u9648\u9759\uff0c\u201c\u5feb\u8ddf\u6211\u8bf4\uff0c\u4ed6\u5230\u5e95\u662f\u8c01\uff0c\u6c14\u6b7b\u59d1\u5976\u5976\u6211\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u9759\u6291\u4e0d\u4f4f\u8981\u7b11\u8d77\u6765\uff0c\u5979\u542c\u8bf4\u5f20\u606a\u6253\u7b97\u8fdb\u4e1c\u5927\uff0c\u8fd8\u60f3\u7740\u8981\u662f\u5b50\u5609\u5728\u4e1c\u5927\u6821\u56ed\u91cc\u78b0\u5230\u5f20\u606a\u4f1a\u662f\u4ec0\u4e48\u60c5\u5f62\uff0c\u6ca1\u60f3\u5230\u4f1a\u63d0\u524d\u5728\u534a\u5c9b\u9152\u5e97\u78b0\u4e0a\u3002</p><p>\u3000\u3000\u9648\u9759\u8fd8\u6ca1\u6709\u5f00\u53e3\u8bf4\u8bdd\uff0c\u8c22\u5b50\u5609\u53c8\u5c16\u53eb\u8d77\u6765\uff1a\u201c\u662f\u4ed6\uff1f\u6211\u77e5\u9053\u4e86\uff0c\u4e00\u5b9a\u662f\u90a3\u4e2a\u6d51\u86cb\uff01\u201d\u6293\u8d77\u6c99\u53d1\u4e0a\u7684\u9760\u6795\uff0c\u60f3\u8981\u6495\u5f00\u6765\u53d1\u6cc4\u4e00\u4e0b\uff0c\u6495\u4e0d\u52a8\uff0c\u7edf\u7edf\u671d\u7a97\u73bb\u7483\u90a3\u91cc\u4e22\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u4f60\u731c\u5230\u4ed6\u662f\u8c01\uff1f\u201d\u9648\u9759\u5012\u89c9\u5f97\u5947\u602a\uff0c\u8c22\u5b50\u5609\u600e\u4e48\u53ef\u80fd\u8f6c\u773c\u5c31\u80fd\u731c\u51fa\u5f20\u606a\u7684\u8eab\u4efd\uff1f</p><p>\u3000\u3000\u89c1\u9648\u9759\u7b11\u5f97\u5f00\u5fc3\uff0c\u8c22\u5b50\u5609\u771f\u662f\u53c8\u597d\u6c14\u53c8\u597d\u7b11\uff0c\u4e5f\u6ca1\u6709\u529b\u6c14\u518d\u53d1\u6cc4\u4e86\uff0c\u7cbe\u75b2\u529b\u7aed\u7684\u5012\u5728\u6c99\u53d1\u4e0a\uff0c\u4e24\u6761\u4fee\u957f\u96ea\u767d\u7684\u7ea4\u7ec6\u5c0f\u817f\u9ad8\u9ad8\u8df7\u5230\u6c99\u53d1\u9760\u80cc\u4e0a\uff0c\u5934\u6602\u7740\u770b\u9648\u9759\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u597d\u50cf\u8ddf\u4ed6\u5173\u7cfb\u5f88\u719f\u7684\u6837\u5b50\u554a\uff0c\u4ed6\u53ef\u662f\u516d\u54e5\u7684\u5934\u53f7\u5927\u654c\u554a\uff0c\u4f60\u5c31\u4e0d\u6015\u516d\u54e5\u5403\u9163\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u54ea\u6709\u8ddf\u4ed6\u5173\u7cfb\u719f\u4e86\uff1f\u201d\u9648\u9759\u63a9\u9970\u9053\uff0c\u4e0d\u8fc7\u4e5f\u5947\u602a\u5b50\u5609\u80fd\u4e00\u773c\u5c31\u8ba4\u51fa\u4eba\u5bb6\uff0c\u201c\u4f60\u600e\u4e48\u77e5\u9053\u4ed6\u662f\u5f20\u606a\uff1f\u60f3\u4e0d\u900f\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6709\u4ec0\u4e48\u96be\u731c\u7684\uff1f\u731c\u5230\u5c31\u662f\u731c\u5230\uff0c\u8c01\u8ba9\u4f60\u80f8\u5927\u6ca1\u8111\u4e86\uff0c\u504f\u4e0d\u544a\u8bc9\u4f60\uff01\u201d</p><p>\u3000\u3000\u201c\u55ec\uff0c\u4f60\u8111\u5927\u6ca1\u80f8\u5c31\u4e86\u4e0d\u8d77\u554a\uff01\u201d</p><p>\u3000\u3000\u201c\u6c14\u6b7b\u4e86\u6211\uff0c\u4e0d\u8bb8\u4f60\u5632\u7b11\u6211\u80f8\u5c0f\uff0c\u6211\u662f\u8fd8\u6ca1\u6709\u53d1\u80b2\u800c\u5df2\u2026\u2026\u201d\u8c22\u5b50\u5609\u4ece\u6c99\u53d1\u8df3\u8d77\u6765\u8981\u53bb\u6293\u9648\u9759\u9ad8\u8038\u7684\u80f8\uff0c\u201c\u518d\u5632\u7b11\u6211\u5c31\u634f\u7206\u4f60\u7684\u3002\u201d\u9648\u9759\u5c16\u53eb\u7740\u8eb2\u5f00\u3002</p><p>\u3000\u3000\u4e24\u4eba\u5728\u5957\u623f\u91cc\u9e21\u98de\u72d7\u8dd1\u7684\u95f9\u4e86\u534a\u5929\uff0c\u9648\u9759\u6c14\u5598\u5401\u5401\u7684\u8eba\u5230\u623f\u95f4\u7684\u5927\u5e8a\u95ee\u5b50\u5609\uff1a\u201c\u8fd8\u8981\u4e0d\u8981\u53bbTelix\u9910\u5385\u770b\u591c\u666f\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u53bb\u4e86\u8ba9\u90a3\u6d51\u86cb\u5632\u7b11\u5417\uff1f\u201d\u8c22\u5b50\u5609\u9f3b\u5934\u4e00\u55c5\uff0c\u60f3\u8d77\u90a3\u5c0f\u5b50\u90aa\u6c14\u7684\u7b11\u8138\uff0c\u5c31\u4e00\u809a\u5b50\u706b\uff0c\u201c\u6211\u4eec\u9a6c\u4e0a\u642c\u5230\u9999\u683c\u91cc\u62c9\u53bb\u4f4f\uff0c\u8fd9\u5bb6\u4f19\uff0c\u4e00\u8f88\u5b50\u9047\u4e0d\u5230\u6700\u597d\u3002\u201d</p><p>\u3000\u3000\u201c\u4ed6\u597d\u50cf\u4e5f\u4f1a\u8fdb\u4e1c\u5927\u4e0a\u5b66\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ed6\u795e\u7ecf\u75c5\u554a\uff0c\u6709\u8fd9\u4e48\u591a\u94b1\u8fd8\u53bb\u5927\u5b66\u6821\u56ed\u88c5\u9177\u554a\uff01\u201d\u8c22\u5b50\u5609\u795e\u7ecf\u8d28\u7684\u7ad9\u8d77\u6765\uff0c\u201c\u4e0d\u884c\uff0c\u6211\u9a6c\u4e0a\u56de\u60e0\u5c71\u7533\u8bf7\u53bb\u610f\u5927\u5229\u7559\u5b66\uff0c\u8fd9\u6d51\u86cb\u4e00\u8f88\u5b50\u9047\u4e0d\u4e0a\u6700\u597d\uff01\u201d</p><p>\u3000\u3000\u201c\u968f\u4fbf\u554a\uff0c\u201d\u9648\u9759\u5a07\u8eaf\u6a2a\u9648\u7684\u8eba\u5728\u5e8a\u4e0a\u65e0\u52a8\u4e8e\u8877\uff0c\u53ef\u60dc\u8fd9\u4ed8\u7f8e\u666f\u6ca1\u6709\u54ea\u4e2a\u7537\u4eba\u6709\u798f\u770b\u89c1\uff0c\u201c\u53ea\u8981\u4f60\u4e0d\u6015\u5728\u6b27\u6d32\u4f1a\u5bc2\u5bde\u6b7b\uff0c\u4f60\u5c31\u53bb\u610f\u5927\u5229\u7559\u5b66\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u5bb6\u4f19\u65c1\u8fb9\u7684\u5973\u5b69\u5b50\u662f\u8c01\uff1f\u201d\u8c22\u5b50\u5609\u6574\u4e2a\u4eba\u76f4\u633a\u633a\u7684\u5012\u5728\u5f39\u6027\u5341\u8db3\u7684\u5e8a\u8925\u4e0a\uff0c\u6253\u4e86\u4e00\u6eda\uff0c\u8eba\u5230\u9648\u9759\u8eab\u8fb9\uff0c\u6795\u7740\u9648\u9759\u7684\u80f8\u53e3\uff0c\u8fd8\u6ca1\u6709\u7b49\u9648\u9759\u56de\u7b54\uff0c\u5c31\u7f8e\u6ecb\u6ecb\u7684\u8bf4\uff0c\u201c\u9648\u9759\uff0c\u4f60\u7684\u80f8\u597d\u8f6f\u554a\uff01\u6709\u6ca1\u6709\u7ed9\u516d\u54e5\u6478\u8fc7\uff1f\u201d</p><p>\u3000\u3000\u201c\u795e\u7ecf\u75c5\uff01\u201d\u9648\u9759\u7b11\u7740\u5c06\u5b50\u5609\u4ece\u81ea\u5df1\u8eab\u4e0a\u63a8\u5f00\uff0c\u201c\u90a3\u4e2a\u5973\u5b69\u5b50\u554a\uff1f\u662f\u5f20\u606a\u7684\u5c0f\u5973\u670b\u53cb\uff0c\u4eba\u53c8\u6f02\u4eae\u53c8\u806a\u660e\uff0c\u4f60\u8981\u662f\u80fd\u8003\u4e0a\u4e1c\u5927\u7684\u8bdd\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u80fd\u8ddf\u5979\u505a\u6821\u53cb\uff01\u5176\u4ed6\u4e24\u4e2a\u4eba\u4e5f\u5e94\u8be5\u662f\u4ed6\u4eec\u7684\u540c\u5b66\u5427\u2026\u2026\u201d\u9648\u9759\u5e76\u4e0d\u6653\u5f97\u5510\u5a67\u5df2\u7ecf\u5411\u6e2f\u5927\u63d0\u4ea4\u4e86\u5165\u5b66\u7533\u8bf7\u3002</p><p>\u3000\u3000\u201c\u54a6\uff0c\u4ed6\u4eec\u4eca\u5929\u4e0a\u5348\u4e5f\u5e94\u8be5\u5728\u53c2\u52a0\u8003\u8bd5\u554a\uff0c\u201d\u8c22\u5b50\u5609\u7ffb\u8fc7\u8eab\uff0c\u624b\u81c2\u6491\u5728\u88ab\u8925\uff0c\u95ee\u9648\u9759\uff0c\u201c\u4ed6\u4eec\u8003\u5b8c\u8bd5\u8981\u8d76\u4e0a\u6211\u4eec\u4e0b\u9762\u7684\u6e2f\u9f99\u822a\u73ed\uff0c\u65f6\u95f4\u4f3c\u4e4e\u5f88\u4ed3\u4fc3\u554a\uff0c\u4f60\u8bf4\u4ed6\u4eec\u8fd9\u4e48\u7126\u6025\u8d76\u5230\u9999\u6e2f\u6765\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u521a\u624d\u88ab\u4f60\u8bf4\u80f8\u5927\u65e0\u8111\uff0c\u4f24\u4e86\u81ea\u5c0a\uff0c\u4f60\u5c0f\u8111\u888b\u6162\u6162\u60f3\u60f3\u5427\uff0c\u6211\u54ea\u91cc\u77e5\u9053\u4eba\u5bb6\u6025\u7740\u8d76\u8fc7\u6765\u505a\u4ec0\u4e48\uff01\u201d</p><p>\u3000\u3000\u201c\u4ed6\u662f\u51b2\u7740\u6cf0\u94e2\u6765\u7684\uff01\u201d\u8c22\u5b50\u5609\u7a81\u7136\u62ff\u4e00\u79cd\u575a\u5b9a\u7684\u58f0\u97f3\u8bf4\u9053\uff0c\u7ffb\u8eab\u5750\u4e86\u8d77\u6765\uff0c\u201c\u770b\u770b\u5427\uff0c\u6211\u8bf4\u7684\u4e0d\u9519\u554a\uff0c\u6cf0\u56fd\u91d1\u878d\u7ecf\u6d4e\u5feb\u8981\u6491\u4e0d\u4f4f\u5d29\u76d8\u4e86\uff0c\u5927\u5bb6\u90fd\u55c5\u7740\u8840\u8165\u6c14\u56f4\u8fc7\u6765\u4e3e\u7740\u5200\u53c9\u51c6\u5907\u7528\u9910\u4e86\uff0c\u4ed6\u4e5f\u662f\u51b2\u7740\u6cf0\u94e2\u6765\u8d81\u706b\u6253\u52ab\u7684\uff01\u201d</p><p>\u3000\u3000\u201c\u53ef\u80fd\u554a\u2026\u2026\u201d\u9648\u9759\u65e0\u6cd5\u80af\u5b9a\u5f20\u606a\u6b64\u65f6\u51fa\u73b0\u5728\u9999\u6e2f\u7684\u610f\u56fe\uff0c\u72b9\u72b9\u8c6b\u8c6b\u7684\u56de\u5e94\u5b50\u5609\u7684\u8bdd\u3002</p><p>\u3000\u3000\u516d\u6708\u4e2d\u4e0b\u5348\u56fd\u9645\u6e38\u8d44\u7b2c\u4e8c\u6ce2\u653b\u51fb\u6cf0\u94e2\u88ab\u6cf0\u56fd\u653f\u5e9c\u51fb\u9000\uff0c\u4f46\u662f\u6cf0\u56fd\u56fd\u5185\u7684\u7ecf\u6d4e\u72b6\u51b5\u5c31\u4e00\u6ed1\u5343\u4e08\uff0c\u80a1\u5e02\u3001\u697c\u5e02\u7686\u6709\u5d29\u76d8\u7684\u8ff9\u8c61\uff0c\u4e4b\u524d\u5bf9\u53f6\u81fb\u6c11\u629b\u51fa\u7684\u89c2\u70b9\u90fd\u62b1\u6709\u9119\u5937\u59ff\u6001\u7684\u4eba\u6b64\u65f6\u90fd\u4e0d\u5f97\u4e0d\u56de\u8fc7\u5934\u6765\u91cd\u65b0\u5ba1\u89c6\u53f6\u81fb\u6c11\u65e9\u5728\u4e00\u5e74\u524d\u5c31\u5bf9\u4e9a\u6d32\u7ecf\u6d4e\u52bf\u6001\u505a\u51fa\u7684\u5224\u65ad\uff0c\u66f4\u4f55\u51b5\u4e00\u76f4\u5173\u6ce8\u9526\u6e56\u3001\u76db\u946b\u52a8\u5411\u7684\u4ed6\u4eec\u5462\uff1f</p><p>\u3000\u3000\u53f6\u81fb\u6c11\u5728\u4e00\u5468\u524d\u4ee5\u5916\u7ecf\u8d38\u90e8\u526f\u90e8\u957f\u7684\u8eab\u4efd\u517c\u4efb\u4e2d\u592e\u9a7b\u6e2f\u529e\u516c\u5ba4\u526f\u4e3b\u4efb\uff0c\u53ef\u89c1\u6e2f\u5e9c\u5f53\u5c40\u4ee5\u53ca\u56fd\u5185\u9ad8\u5c42\u5bf9\u56fd\u9645\u6e38\u8d44\u6c79\u6d8c\u805a\u96c6\u4e1c\u5357\u4e9a\u72b6\u51b5\u5df2\u7ecf\u5f15\u8d77\u8db3\u591f\u7684\u8b66\u60d5\u3002</p><p>\u3000\u3000\u9648\u9759\u8fd9\u6b21\u5230\u9999\u6e2f\u662f\u6765\u8003\u5bdf\u5e76\u8bc4\u4f30\u79d1\u738b\u5728\u4e1c\u5357\u4e9a\u7684\u5e02\u573a\u53ef\u80fd\u906d\u53d7\u5230\u7684\u51b2\u51fb\u7a0b\u5ea6\u3002\u60df\u4e00\u8ba9\u4eba\u5b89\u6170\u7684\u662f\uff0c\u4ed6\u4eec\u5728\u6d77\u5916\u516c\u53f8\u7684\u80a1\u4efd\u5df2\u7ecf\u7f29\u51cf\u81f315\uff05\uff0c\u9648\u9759\u8bb0\u8d77\u4e09\u6708\u9999\u6e2f\u5df7\u5c3e\u76f8\u9047\u4e4b\u540e\u5f20\u606a\u7684\u63d0\u9192\uff0c\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u6e29\u6696\u7684\u611f\u89c9\uff1b\u845b\u5bb6\u5bb6\u5927\u4e1a\u5927\uff0c\u5728\u6d77\u5916\u516c\u53f8\u6295\u5165\u4e09\u4ebf\u591a\u6e2f\u5143\u63a7\u80a185\uff05\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\uff0c\u4ed6\u4eec\u6297\u51b2\u51fb\u7684\u80fd\u529b\u5f3a\uff0c\u4f46\u662f\u4e1c\u5357\u4e9a\u7684\u789f\u673a\u5e02\u573a\u840e\u7f29\u592a\u5389\u5bb3\uff0c\u79d1\u738b\u4e00\u6837\u96be\u4ee5\u627f\u53d7\uff0c\u81f3\u4e8e\u56fd\u9645\u91d1\u878d\u5de8\u9cc4\u62ff\u7740\u5200\u53c9\u770b\u7740\u6cf0\u94e2\u8822\u8822\u6b32\u52a8\uff0c\u9648\u9759\u611f\u89c9\u8ddd\u79bb\u81ea\u5df1\u592a\u9065\u8fdc\u4e86\u3002</p><p>\u3000\u3000\u9648\u9759\u5bf9\u8bc1\u5238\u671f\u8d27\u4e4b\u7c7b\u7684\u5174\u8da3\u4e4f\u4e4f\uff0c\u7559\u5b66\u65f6\u88ab\u8feb\u5b66\u4e86\u4e00\u4e9b\u57fa\u7840\u77e5\u8bc6\uff0c\u7814\u7a76\u5c31\u8c08\u4e0d\u4e0a\u4e86\uff0c\u5b50\u5609\u5374\u662f\u4e2a\u602a\u80ce\uff0c\u4e5f\u662f\u8c22\u6c49\u9756\u4ece\u5c0f\u57f9\u517b\u7684\u7ed3\u679c\uff0c\u76f4\u5230\u9ad8\u8003\u524d\u524d\u4e09\u4e2a\u6708\uff0c\u624d\u5c06\u5b50\u5609\u7684\u8bc1\u5238\u8d26\u6237\u51bb\u7ed3\u8ba9\u5979\u9759\u4e0b\u5fc3\u6765\u590d\u4e60\u8fce\u8003\u3002</p><p>\u3000\u3000\u201c\u5feb\uff0c\u966a\u6211\u53bb\u627e\u90a3\u4e2a\u5bb6\u4f19\uff01\u201d\u8c22\u5b50\u5609\u53c8\u5e8a\u4e0a\u8df3\u5230\u5730\u677f\u4e0a\uff0c\u62fd\u7740\u9648\u9759\u767d\u5ae9\u7684\u80f3\u818a\uff0c\u8981\u5c06\u5979\u4ece\u5e8a\u4e0a\u62fd\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u4e0d\u662f\u5bf9\u4eba\u5bb6\u4e0d\u5f85\u89c1\u5417\uff1f\u201d\u9648\u9759\u5947\u602a\u7684\u95ee\uff0c\u201c\u96be\u9053\u8dd1\u4e0b\u53bb\u6307\u671b\u522b\u4eba\u4f1a\u544a\u8bc9\u4f60\u4ed6\u5230\u9999\u6e2f\u6765\u7684\u771f\u5b9e\u610f\u56fe\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u522b\u7ba1\u6211\uff0c\u966a\u6211\u4e0b\u53bb\u5c31\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u9759\u53ea\u5f97\u966a\u7740\u5b50\u5609\u4e0b\u697c\u6298\u817e\uff0c\u8dd1\u5230Telix\u9910\u5385\u6ca1\u770b\u5230\u5f20\u606a\u4ed6\u4eec\uff0c\u95ee\u5f85\u5e94\u751f\u624d\u6653\u5f97\u521a\u624d\u5ea7\u6ee1\uff0c\u5f20\u606a\u4ed6\u4eec\u8dd1\u5230\u522b\u5bb6\u9910\u5385\u53bb\u4e86\u3002\u534a\u5c9b\u9152\u5e97\u697c\u4e0b\u6709\u4e03\u516b\u5bb6\u9910\u5385\uff0c\u6307\u4e0d\u5b9a\u5f20\u606a\u4ed6\u4eec\u8dd1\u5230\u534a\u5c9b\u9152\u5e97\u5916\u9762\u53bb\u5403\u591c\u5bb5\uff0c\u9648\u9759\u5c31\u6ca1\u6709\u5fc3\u60c5\u966a\u7740\u5b50\u5609\u75af\uff0c\u8981\u62c9\u7740\u5979\u56de\u623f\u95f4\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u5c31\u5750\u5728\u9694\u58c1\u7684\u5409\u58eb\u5730\u6cd5\u56fd\u9910\u5385\u91cc\u7528\u9910\uff0c\u8bf4\u8d77\u4e5d\u4e94\u5e74\u51ac\u5b63\u5728\u9ad8\u901f\u8def\u88c5\u54d1\u5df4\u7684\u4e8b\u60c5\uff0c\u8fd8\u5f97\u538b\u6291\u7740\u7b11\u58f0\uff0c\u514d\u5f97\u60ca\u6270\u9694\u58c1\u9910\u684c\u4e0a\u7684\u5ba2\u4eba\u3002\u521a\u70b9\u5b8c\u83dc\u5c06\u83dc\u5355\u4ea4\u7ed9\u4f8d\u5e94\u751f\uff0c\u653e\u5728\u684c\u4e0a\u7684\u624b\u673a\u9707\u52a8\u4e86\u8d77\u6765\uff0c\u5f20\u606a\u6309\u4e86\u7535\u8bdd\uff0c\u5374\u542c\u5230\u53f6\u5efa\u658c\u5174\u594b\u7684\u58f0\u97f3\uff1a\u201c\u6cf0\u56fd\u8d22\u653f\u90e8\u957f\u521a\u521a\u901a\u8fc7\u56fd\u5bb6\u7535\u89c6\u53f0\u5ba3\u5e03\u8f9e\u804c\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u662f\u6cf0\u94e2\u5f7b\u5e95\u5d29\u6e83\u3001\u6cf0\u56fd\u653f\u5e9c\u653e\u5f03\u62b5\u6297\u7684\u7b2c\u4e00\u6b65\uff0c\u4e0d\u6653\u5f97\u6709\u591a\u5c11\u4eba\u4f1a\u56e0\u4e3a\u542c\u5230\u8fd9\u4e2a\u6d88\u606f\u800c\u5728\u623f\u95f4\u91cc\u538b\u6291\u7740\u72c2\u547c\uff01\u5f20\u606a\u5c06\u624b\u673a\u5408\u4e0a\uff0c\u60f3\u8981\u966a\u5510\u5a67\u5403\u5b8c\u591c\u5bb5\u518d\u4e0a\u697c\u53bb\uff0c\u77dc\u6301\u4e86\u4e24\u5206\u949f\uff0c\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u5c06\u7cfb\u5728\u9886\u895f\u7684\u9910\u5dfe\u626f\u4e0b\u6765\uff0c\u5c06\u76ae\u5939\u53d6\u51fa\u6765\u7ed9\u5510\u5a67\uff1a\u201c\u4f60\u4e0e\u76db\u590f\u5403\u5b8c\u591c\u5bb5\uff0c\u5e2e\u6211\u4eec\u4e70\u4e00\u4e0b\u6362\u6d17\u7684\u8863\u670d\u2026\u2026\u201d\u9886\u7740\u675c\u98de\u5306\u5306\u5c31\u79bb\u5f00\u4e86\u9910\u5385\uff0c\u770b\u5230\u9648\u9759\u4e0e\u8c22\u5b50\u5609\u7ad9\u5728\u9910\u5385\u95e8\u5916\u5f98\u5f8a\uff0c\u70b9\u5934\u5fae\u7b11\u4e86\u4e00\u4e0b\uff0c\u811a\u4e0b\u5374\u6ca1\u6709\u505c\u4e0b\u6765\uff0c\u5f80\u7535\u68af\u90a3\u8fb9\u8d70\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u5582\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u5230\u8c22\u5b50\u5609\u7684\u558a\u4ed6\uff0c\u5f20\u606a\u811a\u6b65\u505c\u4e0b\u6765\uff0c\u56de\u5934\u770b\u4e86\u4e24\u79d2\u949f\uff0c\u89c1\u5979\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5c31\u8f6c\u5934\u8fdb\u4e86\u7535\u68af\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u561b\uff01\u201d\u8c22\u5b50\u5609\u89c1\u5f20\u606a\u4e00\u4ed8\u7231\u7406\u4e0d\u7406\u7684\u6a21\u6837\u5306\u5306\u8d70\u6389\uff0c\u6c14\u6124\u7684\u8ddf\u9648\u9759\u62b1\u6028\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u2026\u2026\u4f26\u6566\u5916\u6c47\u4ea4\u6613\u5bf9\u8fd9\u4e2a\u6d88\u606f\u6709\u4ec0\u4e48\u53cd\u5e94\uff1f\u201d</p><p>\u3000\u3000\u770b\u7740\u53f6\u5efa\u658c\u5e2e\u4ed6\u6253\u5f00\u95e8\uff0c\u5f20\u606a\u5c31\u95ee\u4ed6\u6700\u65b0\u7684\u60c5\u51b5\uff0c\u8fd9\u4e2a\u65f6\u95f4\u70b9\u6b63\u503c\u4f26\u6566\u4e0b\u5348\u65f6\u5206\uff0c\u7ebd\u7ea6\u5916\u6c47\u4ea4\u6613\u5e02\u573a\u8fd8\u6ca1\u6709\u5f00\u59cb\u4e00\u5929\u7684\u4ea4\u6613\u3002</p><p>\u3000\u3000\u201c\u9664\u4e86\u524d\u8d22\u653f\u90e8\u957f\u8f9e\u804c\uff0c\u6cf0\u56fd\u653f\u5e9c\u8fd8\u521a\u521a\u901a\u8fc7\u56fd\u5bb6\u7535\u89c6\u53f0\u516c\u5e03\u4e00\u6761\u654f\u611f\u6d88\u606f\uff0c\u518d\u5ea6\u8981\u6c42\u94f6\u884c\u7b49\u91d1\u878d\u673a\u6784\u5c06\u574f\u8d26\u51c6\u5907\u91d1\u7684\u6bd4\u7387\u63d0\u9ad815\uff05\uff0c\u4ed6\u4eec\u662f\u60f3\u52a0\u5f3a\u91d1\u878d\u4f53\u7cfb\u7684\u7a33\u5b9a\u6027\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u65f6\u5019\u505a\u8fd9\u4e9b\uff0c\u771f\u662f\u611a\u8822\u7684\u4e3e\u52a8\uff0c\u201d\u5c3c\u514b\u674e\u68ee\u4e0d\u518d\u62ff\u4ed6\u8e69\u811a\u7684\u666e\u901a\u8bdd\u6298\u78e8\u81ea\u5df1\u3001\u6298\u78e8\u522b\u4eba\u4e86\uff0c\u5927\u5bb6\u7684\u82f1\u8bed\u90fd\u5f88\u6d41\u5229\uff0c\u5e2e\u5f20\u606a\u4ed6\u4eec\u5173\u4e0a\u623f\u95f4\u95e8\uff0c\u5c31\u8feb\u4e0d\u53ca\u5f85\u7684\u8bc4\u4ef7\u8fd9\u4e24\u6761\u654f\u611f\u6d88\u606f\uff0c\u201c\u4ed6\u4eec\u662f\u60f3\u8fdb\u4e00\u6b65\u63a8\u6bc1\u4eba\u4eec\u5bf9\u6cf0\u56fd\u91d1\u878d\u4f53\u7cfb\u7684\u4fe1\u5fc3\u2026\u2026\u201d</p><p>\u3000\u3000\u674e\u68ee\u8d1f\u8d23\u8d44\u6599\u5206\u6790\u7684\u52a9\u624b\u773c\u775b\u79bb\u5f00\u7535\u8111\u5c4f\u5e55\uff0c\u4ed6\u521a\u521a\u542c\u674e\u68ee\u8bf4\u4ed6\u4eec\u8fd9\u6b21\u6700\u5927\u7684\u91d1\u4e3b\u7adf\u662f\u4e00\u4e2a\u4e8c\u5341\u5c81\u5de6\u53f3\u7684\u9752\u5e74\uff0c\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u505a\u4e3a\u7537\u4eba\uff0c\u4f3c\u4e4e\u592a\u82f1\u4fca\u4e86\u4e9b\uff0c\u4ed6\u4eec\u8fd9\u4e00\u884c\u7684\u89c4\u77e9\uff0c\u662f\u4e0d\u53ef\u4ee5\u6253\u542c\u91d1\u4e3b\u8d44\u91d1\u6765\u6e90\u7684\uff0c\u4f5c\u4e3a\u52a9\u624b\u80fd\u89c1\u5230\u91d1\u4e3b\u672c\u4eba\uff0c\u5c31\u8bf4\u660e\u8fd9\u7b14\u8d44\u91d1\u4e0d\u4f1a\u5b58\u5728\u592a\u5927\u7684\u95ee\u9898\uff0c\u4e0d\u7136\u9664\u4e86\u5934\u513f\uff0c\u8c01\u90fd\u522b\u60f3\u89c1\u5230\u91d1\u4e3b\u672c\u4eba\uff0c\u6307\u7740\u7535\u8111\u5c4f\u5e55\u8bf4\uff1a\u201c\u4f26\u6566\u90a3\u8fb9\u5df2\u7ecf\u6709\u4e86\u53cd\u5e94\uff0c\u73b0\u8d27\u5e02\u573a\u4e0a\u6cf0\u94e2\u629b\u538b\u5f88\u5927\uff0c\u5bf9\u6cf0\u94e2\u7684\u8d2c\u503c\u62c5\u5fe7\u662f\u5c11\u4e0d\u4e86\uff0c\u6211\u60f3\u90a3\u4e9b\u5728\u6cf0\u56fd\u5883\u5185\u6709\u91d1\u878d\u8d44\u4ea7\u7684\u6295\u8d44\u3001\u91d1\u878d\u673a\u6784\u82e5\u662f\u4e0d\u60f3\u56e0\u4e3a\u6cf0\u94e2\u8d2c\u503c\u800c\u8499\u53d7\u635f\u5931\uff0c\u90fd\u4f1a\u5927\u91cf\u7684\u629b\u552e\u6cf0\u94e2\u8fdc\u671f\u5408\u7ea6\u4f5c\u4fdd\u503c\u4ea4\u6613\u2026\u2026\u4e0d\u8fc7\u6cf0\u94e2\u5728\u4f26\u6566\u5916\u6c47\u4ea4\u6613\u5e02\u573a\u7684\u4ea4\u6613\u91cf\u5f88\u5c0f\uff0c\u8fd8\u8981\u7b49\u770b\u660e\u5929\u65b0\u52a0\u5761\u5916\u6c47\u4ea4\u6613\u5e02\u573a\u7684\u60c5\u51b5\u2026\u2026\u201d</p></article>"}}}]);
