(window.webpackJsonp=window.webpackJsonp||[]).push([[587],{610:function(p){p.exports={chapterTitle:null,title:"\u7b2c587\u7ae0 \u81ea\u63ed\u5176\u4e11",content:"<article><h2>\u7b2c587\u7ae0 \u81ea\u63ed\u5176\u4e11</h2><p>\u3000\u3000\u201c\u73b0\u8d27\u5e02\u573a\u7684\u6cf0\u94e2\u7834\u4e8626.94\uff01\u201d\u53f6\u5efa\u658c\u63a8\u95e8\u8fdb\u6765\uff0c\u58f0\u97f3\u538b\u6291\u7740\uff0c\u4f46\u662f\u4ed6\u7684\u5174\u594b\u4e4b\u60c5\u538b\u6291\u4e0d\u4f4f\uff0c\u6025\u901f\u7684\u8bf4\uff0c\u201c\u8fdc\u671f\u5408\u7ea6\u8dcc\u5e45\u66f4\u6df1\uff0c\u5341\u5206\u949f\u5185\u629b\u76d8\u6c79\u6d8c\uff0c\u4e00\u4e0b\u5c31\u523a\u7834\u524d\u671f\u4f4e\u70b9\u2026\u2026\u201d</p><p>\u3000\u3000\u56fd\u9645\u6e38\u8d44\u5bf9\u6cf0\u94e2\u7684\u7b2c\u4e8c\u6ce2\u653b\u51fb\u6240\u9020\u6210\u7684\u6cf0\u94e2\u5151\u7f8e\u5143\u6c47\u7387\u4f4e\u70b9\u5c31\u662f26.94\uff0c\u8fd9\u4e00\u4e2a\u70b9\u4f4d\u53ef\u4ee5\u8bf4\u662f\u6295\u8d44\u8005\u5bf9\u6cf0\u94e2\u6700\u540e\u7684\u5fc3\u7406\u9632\u5fa1\u70b9\u4f4d\uff0c\u6cf0\u56fd\u592e\u884c\u4e0d\u80fd\u625b\u4f4f\u8fd9\u4e2a\u70b9\u4f4d\uff0c\u6cf0\u94e2\u629b\u76d8\u4f1a\u52a0\u500d\u7684\u6c79\u6d8c\u3002</p><p>\u3000\u3000\u5373\u4f7f\u4eca\u5929\u625b\u4f4f\u4e86\u53c8\u80fd\u5982\u4f55\uff1f\u5f20\u606a\u5c06\u624b\u91cc\u7684\u300a\u9999\u6e2f\u7ecf\u6d4e\u65e5\u62a5\u300b\u653e\u5230\u684c\u89d2\u3002</p><p>\u3000\u3000\u4eca\u65e9\u9999\u6e2f\u7ecf\u6d4e\u65e5\u62a5\u7684\u8d22\u7ecf\u4e13\u680f\u6587\u7ae0\u5c31\u4e00\u9488\u89c1\u8840\u7684\u6307\u51fa\u6628\u665a\u6cf0\u56fd\u8d22\u653f\u90e8\u957f\u5728\u6b64\u523b\u8f9e\u804c\u3001\u6cf0\u56fd\u592e\u884c\u63d0\u9ad8\u574f\u8d26\u51c6\u5907\u91d1\u6bd4\u4f8b\uff0c\u5c31\u662f\u523a\u6fc0\u5927\u5bb6\u53bb\u8df3\u697c\uff0c\u9884\u6d4b\u6cf0\u94e2\u6cbd\u76d8\u6d8c\u73b0\u5c06\u523a\u6fc0\u4e9a\u592a\u533a\u80a1\u5e02\u91d1\u7ebf\u4e0b\u632b\uff0c\u800c\u6cf0\u56fd\u80a1\u5e02\u5c06\u9996\u5f53\u671f\u51b2\u3002</p><p>\u3000\u3000\u5f20\u606a\u62ff\u7740\u684c\u89d2\u4e0a\u7684\u5496\u5561\uff0c\u95ee\u4e86\u53f6\u5efa\u658c\u4e00\u58f0\uff1a\u201c\u8981\u4e0d\u8981\u6765\u676f\u5496\u5561\u63d0\u63d0\u795e\uff1f\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u773c\u775b\u91cc\u6709\u4e9b\u8840\u4e1d\uff0c\u770b\u6765\u4ed6\u6628\u591c\u6ca1\u6709\u7761\u597d\u6216\u8005\u6839\u672c\u5c31\u5728\u5c3c\u514b\u674e\u68ee\u7684\u623f\u95f4\u76ef\u7740\u7ebd\u7ea6\u5916\u6c47\u5e02\u573a\u7684\u76d8\u53e3\u6ca1\u6709\u7761\u89c9\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u54d1\u7136\u5931\u7b11\uff0c\u5f20\u606a\u8fd9\u78e8\u4eba\u7684\u813e\u6c14\uff0c\u8fd8\u771f\u662f\u8981\u5c06\u522b\u4eba\u7684\u6027\u5b50\u4e5f\u78e8\u6162\u4e0b\u6765\u3002</p><p>\u3000\u3000\u201c\u7b49\u4f1a\u513f\u6cf0\u56fd\u592e\u884c\u5c31\u8981\u6709\u6240\u52a8\u4f5c\uff0c\u6c47\u7387\u4e0a\u4e0a\u4e0b\u4e0b\u7684\u8df3\u52a8\uff0c\u4f60\u7559\u5728\u90a3\u91cc\u5fc3\u810f\u80fd\u53d7\u5f97\u4e86\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\u53f6\u5efa\u658c\uff0c\u201c\u4e0a\u4e0a\u4e0b\u4e0b\u90fd\u662f\u51e0\u767e\u4e07\u7f8e\u91d1\u7684\u8df3\u52a8\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u62cd\u62cd\u4e86\u540e\u8111\u52fa\uff0c\u60f3\u60f3\u4e5f\u662f\uff0c\u6709\u6240\u9884\u6599\u7684\u4e8b\u60c5\uff0c\u4f3c\u4e4e\u4e0d\u5e94\u8be5\u8fd9\u4e48\u5174\u594b\uff0c\u7559\u5728\u5c3c\u514b\u674e\u68ee\u624b\u91cc\u53cd\u800c\u59a8\u788d\u4ed6\u4eec\u64cd\u4f5c\uff1b\u95fb\u7740\u5496\u5561\u9999\u6c14\uff0c\u8d70\u5230\u5427\u53f0\u7ed9\u81ea\u5df1\u5012\u4e86\u4e00\u676f\u5496\u5561\uff0c\u5f20\u606a\u8fd9\u79cd\u5bf9\u8d22\u5bcc\u7684\u5e73\u6de1\u6c14\u5ea6\uff0c\u7684\u786e\u4e0d\u662f\u4efb\u4f55\u4eba\u80fd\u5177\u5907\u7684\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u4ece\u5f20\u606a\u624b\u91cc\u5206\u8fc7\u4e00\u534a\u62a5\u7eb8\uff0c\u7ffb\u5230\u8d22\u7ecf\u7248\u9762\uff0c\u4e0a\u9762\u6709\u7231\u8fbe\u7535\u5b50\u901a\u8fc7\u9999\u6e2f\u7ecf\u6d4e\u65e5\u62a5\u53d1\u5e03\u7684\u6d88\u606f\u3002</p><p>\u3000\u3000\u5609\u4fe1\u5b9e\u4e1a\u589e\u53d1\u5e76\u8d2d\u4e4b\u540e\u5c31\u66f4\u540d\u4e3a\u7231\u8fbe\u7535\u5b50\u5728\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u6240\u4e0a\u5e02\u4ea4\u6613\uff0c\u8463\u4e8b\u4f1a\u53ca\u516c\u53f8\u7ba1\u7406\u5c42\u90fd\u7ecf\u8fc7\u91cd\u7ec4\u4ee5\u4fbf\u5c06\u789f\u673a\u4e1a\u52a1\u5b8c\u5168\u878d\u5408\u8fdb\u4e0a\u5e02\u516c\u53f8\uff1b\u800c\u53f6\u5bb6\u3001\u5b59\u3001\u845b\u5219\u80a1\u7968\u51cf\u6301\u5b8c\u7b97\u662f\u6682\u65f6\u7684\u5f7b\u5e95\u4e0e\u7231\u8fbe\u7535\u5b50\u8131\u79bb\u5173\u7cfb\u3002\u5373\u4f7f\u8981\u91cd\u65b0\u53c2\u4e0e\u5230\u7231\u8fbe\u7535\u5b50\u7684\u8fd0\u8425\uff0c\u4e5f\u8981\u7b49\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5439\u88ad\u5230\u6700\u72c2\u8c11\u7684\u65f6\u5019\u4ee5\u6551\u5e02\u7684\u59ff\u6001\u8fdb\u884c\u6301\u80a1\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u73b0\u5728\u4e00\u822c\u4e0d\u4e3b\u52a8\u95ee\u7231\u8fbe\u7535\u5b50\u7684\u4e8b\u60c5\uff0c\u65f6\u95f4\u8d76\u5f97\u771f\u5de7\uff0c\u4eca\u5929\u53d1\u5e03\u6d88\u606f\uff0c\u53f6\u5efa\u658c\u8ba4\u771f\u770b\u4e86\u8d77\u6765\uff0c\u5374\u662f\u7231\u8fbe\u7535\u5b50\u9884\u8ba1\u4e1c\u5357\u4e9a\u5e02\u573a\u5728\u672a\u6765\u76f8\u5f53\u957f\u7684\u65f6\u95f4\u91cc\u4f1a\u56e0\u4e3a\u7ecf\u6d4e\u8870\u9000\u800c\u840e\u7f29\uff0c\u7231\u8fbe\u7535\u5b50\u5c06\u6536\u7f29\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u789f\u673a\u4e1a\u52a1\uff0c\u4e3a\u6b64\u8c03\u4f4e\u7231\u8fbe\u7535\u5b50\u4e0b\u534a\u5e74\u5ea6\u7684\u76c8\u5229\u9884\u671f\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u81ea\u63ed\u5176\u4e11\u554a\uff01\u201d\u53f6\u5efa\u658c\u6307\u7740\u62a5\u7eb8\u4e0a\u7684\u6d88\u606f\u95ee\u5f20\u606a\uff0c\u201c\u4f60\u662f\u60f3\u5c06\u7231\u8fbe\u7535\u5b50\u7684\u80a1\u4ef7\u62c9\u4e0b\u6765\uff0c\u8fd8\u662f\u8bf4\u60f3\u501f\u673a\u63d0\u9192\u4e00\u4e9b\u4eba\uff1f\u201d</p><p>\u3000\u3000\u201c\u82e5\u80fd\u8d81\u90a3\u4e9b\u91d1\u878d\u5de8\u9cc4\u8fd8\u6ca1\u6709\u8fdb\u5165\u9999\u6e2f\u5f00\u59cb\u5efa\u7acb\u7a7a\u5934\u5934\u5bf8\u4e4b\u524d\uff0c\u5148\u5c06\u80a1\u6307\u6253\u4e0b\u6765\uff0c\u65e5\u540e\u7684\u7834\u574f\u529b\u5c31\u6ca1\u6709\u90a3\u4e48\u5927\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4e3b\u52a8\u964d\u4e0b\u6765\uff0c\u8ddf\u88ab\u56fd\u9645\u6e38\u8d44\u6253\u4e0b\u6765\uff0c\u51c4\u60e8\u4e4b\u72b6\u51b5\u662f\u4e0d\u80fd\u540c\u65e5\u800c\u8bed\u7684\u3002\u53ef\u4ee5\u9884\u89c1\u6e2f\u80a1\u5728\u76f8\u5f53\u957f\u7684\u65f6\u95f4\u91cc\u4f1a\u957f\u671f\u5411\u4e0b\uff0c\u53ef\u80fd\u8981\u7ecf\u5386\u4e00\u5e74\u4ee5\u4e0a\u7684\u8c03\u6574\u671f\uff0c\u6b64\u65f6\u7684\u9ad8\u4f4d\u53ea\u662f\u4e00\u4e2a\u6613\u9192\u3001\u6613\u788e\u7684\u7f8e\u68a6\u800c\u5df2\uff0c\u4f46\u662f\u8c01\u8981\u505a\u8fd9\u4e2a\u6253\u7834\u7f8e\u68a6\u7684\u4eba\uff0c\u53ea\u6015\u4f1a\u7ed9\u9999\u6e2f\u7684\u80a1\u6c11\u8bb0\u6068\u4e00\u8f88\u5b50\u2026\u2026\u6240\u4ee5\u8fd9\u4e2a\u998a\u4e3b\u610f\uff0c\u6211\u6628\u5929\u5c31\u6ca1\u6709\u8ddf\u4e8c\u53d4\u63d0\u3002\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6e2f\u5e9c\u5f53\u5c40\u76f4\u63a5\u5e72\u9884\u80a1\u5e02\uff0c\u53ea\u6015\u4e5f\u8981\u7ed9\u653b\u8bd8\uff0c\u66f4\u4e0d\u7528\u8bf4\u4e2d\u8054\u529e\u4e86\uff0c\u4f60\u4e0d\u63d0\u8fd8\u597d\uff0c\u63d0\u51fa\u6765\uff0c\u4e8c\u53d4\u786e\u5b9e\u4f1a\u6bd4\u8f83\u68d8\u624b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4eca\u5929\uff0c\u9999\u6e2f\u80a1\u5e02\u5e94\u8be5\u4f1a\u5927\u632b\u5427\uff0c\u6211\u8fd9\u6837\u5b89\u6392\uff0c\u662f\u8ba9\u90a3\u4e9b\u7a0d\u5fae\u6709\u70b9\u654f\u9510\u6027\u7684\u6295\u8d44\u4eba\u77e5\u9053\u5927\u632b\u7684\u7f18\u7531\uff0c\u8fd9\u4e0d\u4ec5\u4ec5\u662f\u6cf0\u94e2\u88ab\u653b\u51fb\uff0c\u800c\u662f\u4e9a\u6d32\u6b63\u9762\u4e34\u4e00\u573a\u6781\u4e3a\u6df1\u91cd\u7684\u7ecf\u6d4e\u5371\u673a\u2026\u2026\u8be5\u51fa\u5c40\u5c31\u51fa\u5c40\uff0c\u604b\u6808\u4e0d\u820d\uff0c\u8bf4\u4e0d\u5b9a\u5c38\u9ab8\u4e0d\u5b58\u3002\u201d</p><p>\u3000\u3000\u201c\u8981\u662f\u6240\u6709\u5728\u9999\u6e2f\u4e0a\u5e02\u7684\u5185\u8d44\u516c\u53f8\u90fd\u53d1\u5e03\u76f8\u4f3c\u7684\u516c\u544a\u5462\uff0c\u8fd9\u76c6\u51b7\u6c34\u4f1a\u4e0d\u4f1a\u6cfc\u5f97\u592a\u5f3a\u70c8\u4e00\u4e9b\uff1f\u201d\u53f6\u5efa\u658c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u64cd\u7eb5\u7684\u75d5\u8ff9\u4f1a\u4e0d\u4f1a\u592a\u660e\u663e\u4e86\u4e00\u4e9b\uff1f\u201d\u5f20\u606a\u6709\u4e9b\u72b9\u8c6b\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u5012\u4e0d\u6015\uff0c\u6216\u8bb8\u8ba9\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u6709\u4e1a\u52a1\u7684\u5185\u8d44\u4e0a\u5e02\u516c\u53f8\u53d1\u5e03\u76f8\u5173\u516c\u544a\u66f4\u5408\u9002\u4e00\u4e9b\uff0c\u4e5f\u4e0d\u8981\u96c6\u4e2d\u5230\u4e00\u5929\u516c\u5e03\uff0c\u4e89\u53d6\u4e00\u5468\u4e4b\u5185\u5c06\u8fd9\u76c6\u51c9\u6c34\u7ed9\u6d47\u4e0b\u53bb\uff1b\u53ef\u80fd\u4f1a\u6709\u4e9b\u975e\u8bae\uff0c\u4f46\u662f\u60f3\u505a\u4e8b\u600e\u4e48\u80fd\u6015\u522b\u4eba\u6307\u4e1c\u8bf4\u897f\u5462\uff1f\u201d\u5728\u8fd9\u4e0a\u9762\uff0c\u53f6\u5efa\u658c\u5012\u662f\u6bd4\u5f20\u606a\u5e72\u8106\uff0c\u6bd5\u7adf\u4ed6\u4e0e\u53f6\u81fb\u6c11\u4e4b\u95f4\u66f4\u6ca1\u6709\u9694\u9602\uff0c\u201c\u6211\u5148\u8ddf\u4e8c\u53d4\u8bf4\u4e00\u4e0b\uff0c\u770b\u4ed6\u4ec0\u4e48\u610f\u89c1\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u6478\u51fa\u624b\u673a\uff0c\u7ed9\u4e8c\u53d4\u53f6\u81fb\u6c11\u901a\u7535\u8bdd\uff0c\u624d\u63a5\u901a\u7535\u8bdd\uff0c\u5c31\u6342\u7740\u8bdd\u7b52\uff0c\u6307\u7740\u684c\u4e0a\u7684\u62a5\u7eb8\u8ddf\u5f20\u606a\u8bf4\uff1a\u201c\u4e8c\u53d4\u4ed6\u770b\u5230\u8fd9\u4efd\u62a5\u7eb8\u4e86\uff0c\u8bf4\u4f60\u7ed9\u4ed6\u641e\u7a81\u88ad\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u82e6\u7b11\u4e00\u4e0b\uff0c\u4ed6\u53ef\u6ca1\u6709\u8981\u7a81\u88ad\u53f6\u81fb\u6c11\u7684\u610f\u601d\uff0c\u4e2d\u8054\u529e\u5728\u9999\u6e2f\u7684\u5730\u4f4d\u6bd4\u8f83\u7a98\u8feb\uff0c\u81ea\u5df1\u8fd8\u662f\u5f88\u80fd\u4f53\u4f1a\u53f6\u81fb\u6c11\u5728\u9999\u6e2f\u7684\u5904\u5883\u2014\u2014\u53ea\u80fd\u7b97\u662f\u4e00\u4e2a\u987e\u95ee\u7684\u89d2\u8272\uff0c\u6240\u4ee5\u4ed6\u7ed9\u53f6\u81fb\u6c11\u4ec0\u4e48\u5efa\u8bae\u65f6\uff0c\u4e5f\u4f1a\u5c3d\u53ef\u80fd\u8003\u8651\u5230\u4ed6\u7684\u5904\u5883\uff0c\u4e0d\u4f1a\u7ed9\u51fa\u8fc7\u4e8e\u6fc0\u70c8\u7684\u5efa\u8bae\u3002</p><p>\u3000\u3000\u4ed6\u53ea\u662f\u60f3\u5c3d\u53ef\u80fd\u7684\u591a\u505a\u4e00\u4e9b\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u9999\u6e2f\u80a1\u5e02\u4ece\u4e09\u6708\u4e2d\u65ec\u4ee5\u6765\u7684\u98da\u6da8\u5176\u5b9e\u662f\u6295\u8d44\u673a\u6784\u5728\u7092\u4f5c\u9999\u6e2f\u56de\u5f52\u9898\u6750\uff0c\u4ee5\u5185\u8d44\u53c2\u80a1\u6216\u63a7\u80a1\u7684\u7ea2\u7b79\u80a1\u589e\u5e45\u9ad8\u8fbe80\uff05\uff0c\u5176\u4ed6\u4e0a\u5e02\u516c\u53f8\u7684\u80a1\u7968\u6da8\u5e45\u53ea\u6709\u4e00\u534a\uff0c\u7231\u8fbe\u7535\u5b50\uff08\u5609\u4fe1\u5b9e\u4e1a\uff09\u80a1\u4ef7\u56e0\u4e3a\u6709\u91cd\u7ec4\u5e76\u8d2d\u9898\u6750\uff0c\u534a\u5e74\u98da\u6da8\u4e86\u4e09\u500d\u8fd8\u591a\uff1b\u800c\u540c\u671f\u4e1c\u5357\u4e9a\u5176\u4ed6\u56fd\u5bb6\u7684\u80a1\u5e02\u5219\u76f8\u5bf9\u60e8\u6de1\u4e0d\u582a\u3002\u5728\u56fd\u9645\u6e38\u8d44\u8fd8\u6ca1\u6709\u8fdb\u5165\u6e2f\u80a1\u3001\u671f\u6307\u5e02\u573a\u5efa\u7acb\u5927\u91cf\u7684\u7a7a\u5934\u5934\u5bf8\u4e4b\u524d\uff0c\u5148\u7ed9\u8fc7\u70ed\u7684\u7ea2\u7b79\u80a1\u6cfc\u4e00\u76c6\u51b7\u6c34\uff0c\u8ba9\u6e2f\u80a1\u6307\u6570\u5148\u884c\u5927\u5e45\u6ed1\u843d\uff0c\u5c31\u80fd\u964d\u4f4e\u6e2f\u80a1\u6307\u6570\u91cc\u8574\u6db5\u7684\u8d85\u5f3a\u7834\u574f\u529b\u3002</p><p>\u3000\u3000\u201c\u4e8c\u53d4\u4f1a\u5148\u5728\u4e2d\u8054\u529e\u5185\u90e8\u8ba8\u8bba\u4e00\u4e0b\uff0c\u4e0d\u8fc7\u6700\u540e\u8fd8\u662f\u8981\u8ddf\u6e2f\u5e9c\u5f53\u5c40\u6253\u62db\u547c\u624d\u4f1a\u53bb\u5b9e\u65bd\u2026\u2026\u201d\u53f6\u5efa\u658c\u5c06\u624b\u673a\u653e\u5230\u684c\u89d2\uff0c\u8bf4\u9053\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9648\u9759\u8fd4\u56de\u9152\u5e97\uff0c\u5b50\u5609\u5b9a\u775b\u76ef\u7740\u7535\u8111\u5c4f\u5e55\uff0c\u4f3c\u4e4e\u81ea\u5df1\u4e0a\u5348\u79bb\u5f00\u9152\u5e97\u65f6\uff0c\u5979\u4e5f\u662f\u8fd9\u4e2a\u59ff\u6001\uff0c\u95ee\u5979\uff1a\u201c\u4e00\u5929\u6ca1\u6709\u632a\u7a9d\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u201d\u8c22\u5b50\u5609\u5e94\u4e86\u4e00\u58f0\uff0c\u773c\u775b\u5374\u6ca1\u6709\u79fb\u52a8\u7535\u8111\u5c4f\u5e55\u3002</p><p>\u3000\u3000\u9648\u9759\u8d70\u8fdb\u53bb\u5c06\u7b14\u8bb0\u672c\u5f80\u684c\u89d2\u79fb\u4e86\u79fb\uff0c\u5c4f\u5e55\u4e0a\u7684\u6e2f\u80a1\u6307\u6570\u5267\u70c8\u632f\u8361\uff0c\u6bd4\u8d77\u4e1c\u5357\u4e9a\u5176\u4ed6\u56fd\u5bb6\u4eca\u5929\u60e8\u6de1\u4e0d\u582a\u7684\u80a1\u6307\u8868\u73b0\uff0c\u5df2\u7ecf\u7b97\u597d\u770b\u591a\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u90fd\u8fd9\u4e2a\u70b9\u4e86\uff0c\u8fd8\u770b\u4ec0\u4e48\u770b\uff1f\u4f60\u8fd8\u771f\u662f\u8c22\u5bb6\u7684\u602a\u80ce\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u6cf0\u94e2\u4eca\u5929\u91cd\u632b\u554a\uff01\u201d\u8c22\u5b50\u5609\u62ac\u5934\u770b\u7740\u9648\u9759\uff0c\u201c\u4f60\u8bf4\u90a3\u5bb6\u4f19\u662f\u4e0d\u662f\u51b2\u6cf0\u94e2\u6765\u7684\uff1f\u8981\u662f\u4ed6\u662f\u51b2\u6cf0\u94e2\u6765\u7684\uff0c\u4f3c\u4e4e\u6765\u4e0d\u53ca\u4e0b\u624b\u554a\uff1b\u8fd8\u662f\u8bf4\u4ed6\u4eec\u65e9\u5c31\u6709\u51c6\u5907\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u6211\u54ea\u91cc\u77e5\u9053\uff1f\u6211\u53c8\u4e0d\u80fd\u6293\u4f4f\u4ed6\u4ece\u4ed6\u809a\u5b50\u91cc\u5c06\u8bdd\u7ed9\u638f\u51fa\u6765\u3002\u201d\u9648\u9759\u5fae\u5fae\u4e00\u7b11\u3002</p><p>\u3000\u3000\u201c\u4f60\u8ddf\u4ed6\u5173\u7cfb\u770b\u8d77\u6765\u5f88\u719f\u554a\uff01\u201d\u8c22\u5b50\u5609\u72d0\u7591\u7684\u770b\u4e86\u9648\u9759\u4e00\u773c\u3002</p><p>\u3000\u3000\u201c\u54ea\u6709\uff1f\u201d\u9648\u9759\u62cd\u4e86\u62cd\u989d\u5934\uff0c\u501f\u673a\u773c\u775b\u95ea\u8fc7\u5b50\u5609\u72d0\u7591\u7684\u773c\u795e\uff0c\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u4f60\u8fd9\u4e48\u5173\u5fc3\u8fd9\u4e2a\u5bb6\u4f19\uff0c\u201d\u5979\u8fd8\u7279\u610f\u5728\u201c\u5bb6\u4f19\u201d\u8fd9\u4e24\u4e2a\u5b57\u4e0a\u52a0\u91cd\u8bed\u6c14\uff0c\u201c\u62a5\u7eb8\u4e0a\u6b63\u597d\u520a\u767b\u4e86\u8fd9\u4e2a\u5bb6\u4f19\u7684\u6d88\u606f\uff0c\u4f60\u62ff\u53bb\u770b\u770b\u2026\u2026\u201d\u4ece\u7b14\u8bb0\u672c\u5305\u91cc\u62ff\u51fa\u4eca\u5929\u7684\u9999\u6e2f\u7ecf\u6d4e\u65e5\u62a5\u9012\u7ed9\u5b50\u5609\uff0c\u201c\u7231\u8fbe\u7535\u5b50\u901a\u8fc7\u9999\u6e2f\u7ecf\u6d4e\u65e5\u62a5\u53d1\u5e03\u6d88\u606f\u6307\u51fa\u4e1c\u5357\u4e9a\u5730\u533a\u5c06\u4f1a\u6709\u81f3\u5c11\u6301\u7eed\u4e00\u5e74\u7684\u7ecf\u6d4e\u8870\u9000\uff0c\u4ed6\u4eec\u5c06\u6536\u7f29\u5728\u4e1c\u5357\u4e9a\u7684\u4e1a\u52a1\uff0c\u8fd8\u8c03\u4f4e\u4e0b\u534a\u5e74\u7684\u8425\u4e1a\u76c8\u5229\u9884\u671f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5f88\u5947\u602a\u554a\u2026\u2026\u201d\u8c22\u5b50\u5609\u54ac\u7740\u81ea\u5df1\u7684\u624b\u6307\uff0c\u62ac\u5934\u7741\u773c\u770b\u7740\u9648\u9759\uff0c\u773c\u775b\u6709\u8bb8\u591a\u7684\u7591\u60d1\u4e0d\u89e3\u3002</p><p>\u3000\u3000\u9648\u9759\u5fae\u5fae\u4e00\u7b11\uff0c\u5f20\u606a\u672c\u8eab\u5c31\u662f\u4e00\u4e2a\u8ba9\u4eba\u5f88\u96be\u7422\u78e8\u7684\u4eba\u3002</p><p>\u3000\u3000\u8fd9\u5927\u6982\u662f\u9996\u5bb6\u4e0a\u5e02\u516c\u53f8\u63d0\u524d\u5bf9\u5b8f\u89c2\u7ecf\u6d4e\u505a\u51fa\u81ea\u5df1\u7684\u5224\u65ad\u5427\uff1f\u4eca\u5929\u6e2f\u80a1\u5267\u70c8\u632f\u8361\uff0c\u4f46\u662f\u7231\u8fbe\u7535\u5b50\u7684\u80a1\u4ef7\u5374\u56e0\u4e3a\u8fd9\u6761\u5927\u6cfc\u51b7\u6c34\u7684\u6d88\u606f\u4e0b\u632b\u5e45\u5ea6\u8f83\u6df1\u3002</p><p>\u3000\u3000\u4eca\u5929\u7684\u5f62\u52bf\u4f3c\u4e4e\u9661\u7136\u95f4\u6076\u52a3\u8d77\u6765\u4e86\uff0c\u53f6\u81fb\u6c11\u65e9\u5728\u4e00\u5e74\u524d\u7684\u9884\u6d4b\u4f3c\u4e4e\u5c31\u8981\u6210\u4e3a\u73b0\u5b9e\uff0c\u79d1\u738b\u8be5\u5982\u4f55\u8c03\u6574\u81ea\u5df1\u7684\u4e1a\u52a1\u6765\u5e94\u5bf9\u51b2\u51fb\uff1f\u8981\u662f\u7d27\u968f\u7231\u8fbe\u7535\u5b50\u4e4b\u540e\u6536\u7f29\u4e1c\u5357\u4e9a\u7684\u4e1a\u52a1\uff0c\u4eca\u5e74\u79d1\u738b\u5c31\u8fbe\u4e0d\u5230\u5e74\u524d\u8bbe\u7acb\u7684\u76c8\u5229\u76ee\u6807\uff0c\u8981\u662f\u4e0d\u53ca\u65f6\u8c03\u6574\u4e1c\u5357\u4e9a\u7684\u4e1a\u52a1\uff0c\u5c31\u53ef\u80fd\u5b58\u5728\u5e93\u5b58\u79ef\u538b\u7684\u989d\u5916\u98ce\u9669\uff1b\u5f53\u7136\uff0c\u6d77\u5916\u516c\u53f8\u8fd9\u91cc\u6240\u627f\u62c5\u7684\u98ce\u9669\u4f1a\u66f4\u5927\u4e00\u4e9b\uff0c\u5173\u952e\u8fd8\u662f\u8981\u770b\u845b\u836b\u5747\u7684\u6001\u5ea6\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u5012\u6709\u82e6\u8bf4\u4e0d\u51fa\u3002</p><p>\u3000\u3000\u5e94\u8be5\u8bf4\u4e1c\u5357\u4e9a\u7684\u7ecf\u6d4e\u5f62\u52bf\u662f\u5728\u6cf0\u94e2\u906d\u53d7\u7b2c\u4e8c\u6ce2\u653b\u51fb\u4e4b\u540e\u624d\u660e\u6717\u5316\u7684\uff0c\u90a3\u5df2\u7ecf\u662f\u516d\u6708\u4e2d\u4e0b\u65ec\u7684\u4e8b\u60c5\u4e86\uff0c\u6d77\u5916\u516c\u53f8\u80a1\u4efd\u8c03\u6574\u5374\u662f\u56db\u6708\u521d\u5c31\u8fdb\u884c\u4e86\uff0c\u5c31\u4e24\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u845b\u836b\u5747\u4ee5\u4e2a\u4eba\u540d\u4e49\u5728\u6d77\u5916\u516c\u53f8\u5360\u7684\u80a1\u4efd\u4ece40\uff05\u63d0\u9ad8\u523085\uff05\uff0c\u8fd8\u989d\u5916\u6ce8\u5165\u8fd1\u4e24\u4ebf\u6e2f\u5143\u7684\u8d44\u91d1\uff0c\u603b\u4e0d\u80fd\u8bf4\u8c22\u5bb6\u6545\u610f\u7ed9\u4ed6\u8bbe\u9677\u9631\u5427\uff1f</p></article>"}}}]);
