(window.webpackJsonp=window.webpackJsonp||[]).push([[593],{616:function(p){p.exports={chapterTitle:null,title:"\u7b2c593\u7ae0 \u68a6\u60f3\u53ef\u4ee5\u5962\u4f88",content:"<article><h2>\u7b2c593\u7ae0 \u68a6\u60f3\u53ef\u4ee5\u5962\u4f88</h2><p>\u3000\u3000\u5f20\u606a\u5c06\u53f6\u5efa\u658c\u3001\u675c\u98de\u4ed6\u4eec\u4e22\u5728\u534a\u5c9b\u9152\u5e97\uff0c\u4ed6\u4e58\u5730\u94c1\u5230\u4e2d\u73af\u9644\u8fd1\u4e0b\u8f66\uff0c\u706f\u706b\u7480\u74a8\u7684\u591c\u8272\u8ff7\u79bb\uff0c\u590f\u5b63\u707c\u70ed\u7684\u8def\u9762\u5728\u6e10\u6df1\u7684\u591c\u8272\u8fd8\u4e1d\u4e1d\u84b8\u817e\u51fa\u70ed\u6c14\uff0c\u5728\u57ce\u5e02\u91cc\u6e38\u8361\u7684\u6d77\u98ce\u5e26\u7740\u4e9b\u5fae\u8165\u3002</p><p>\u3000\u3000\u8d70\u8fdb\u6b27\u5409\u58eb\u9910\u5385\uff0c\u8033\u7554\u4f20\u6765\u4f4e\u8ff7\u7684\u5c0f\u63d0\u7434\u4e50\u58f0\uff0c\u9910\u5385\u7684\u5185\u89d2\u821e\u53f0\uff0c\u5c0f\u63d0\u7434\u624b\u7a7f\u7740\u6697\u7ea2\u953b\u5b50\u7684\u665a\u793c\u670d\u88d9\uff0c\u59ff\u6001\u4f18\u96c5\u800c\u8ff7\u4eba\u3002</p><p>\u3000\u3000\u91d1\u878d\u98ce\u66b4\u7206\u53d1\u4ee5\u6765\uff0c\u4e2d\u73af\u4e00\u5e26\u7684\u9ad8\u7ea7\u9910\u5385\u91cc\u5012\u663e\u5f97\u7a00\u843d\u4e86\u3002</p><p>\u3000\u3000\u6b27\u5409\u58eb\u9910\u5385\u5bbd\u655e\u800c\u660e\u4eae\uff0c\u88c5\u6ee1\u53e4\u6734\u800c\u5178\u96c5\uff0c\u8f6c\u89d2\u6276\u624b\u90fd\u5d4c\u7740\u78e8\u5f97\u956b\u4eae\u7684\u9752\u94dc\u6d6e\u96d5\uff0c\u8bb8\u601d\u5750\u5728\u9760\u7a97\u7684\u9910\u684c\u524d\uff0c\u7a7f\u7740\u6854\u8272\u7684\u6d0b\u88c5\uff0c\u9999\u80a9\u67d4\u817b\u5149\u6ed1\uff0c\u4e24\u81c2\u5c48\u7740\u652f\u5728\u684c\u4e0a\uff0c\u4e0e\u84dd\u8272\u9910\u5e03\u76f8\u6620\uff0c\u6108\u53d1\u7684\u6d01\u767d\u5982\u7389\uff0c\u7ea4\u67d4\u6676\u83b9\u7684\u7d20\u8155\u8f7b\u6258\u7740\u5706\u6da6\u7684\u4e0b\u9894\uff0c\u4f3c\u4e4e\u5728\u770b\u7a97\u5916\u7684\u7480\u74a8\u591c\u8272\uff0c\u53f3\u624b\u62ff\u7740\u4e00\u652f\u94c5\u7b14\uff0c\u5f20\u606a\u8d70\u8fc7\u53bb\uff0c\u770b\u89c1\u9910\u5dfe\u7eb8\u4e0a\u5df2\u7ecf\u5ed6\u5ed6\u843d\u4e86\u51e0\u7b14\uff0c\u52fe\u52d2\u4e00\u6761\u957f\u8857\u7684\u6a21\u6837\u3002\u8f7b\u8f7b\u7740\u4ece\u80cc\u540e\u5c06\u8bb8\u601d\u6402\u5165\u6000\u91cc\u51e0\u79d2\u949f\uff0c\u624d\u8d70\u5230\u5bf9\u9762\u7684\u4f4d\u5b50\u4e0a\uff0c\u95ee\u5979\uff1a\u201c\u7b49\u6211\u5f88\u4e45\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u554a\uff0c\u201d\u8bb8\u601d\u62ff\u8d77\u684c\u89d2\u4e0a\u7684\u624b\u673a\u770b\u4e86\u4e00\u4e0b\u65f6\u95f4\uff0c\u201c\u554a\uff0c\u90fd\u4e5d\u70b9\u949f\uff0c\u4e1c\u60f3\u897f\u60f3\uff0c\u90fd\u611f\u89c9\u4e0d\u5230\u5f88\u4e45\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5728\u753b\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8981\u5c06\u8bb8\u601d\u9762\u524d\u52fe\u52d2\u4e86\u51e0\u7b14\u7684\u9910\u5dfe\u7eb8\u62ff\u8fc7\u6765\uff0c\u8bb8\u601d\u6309\u7740\u5f20\u606a\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u6ca1\u6709\u5b8c\uff0c\u4f60\u5148\u5e2e\u6211\u51b3\u5b9a\u83dc\u5355\u597d\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u62db\u6765\u4f8d\u5e94\u751f\u2014\u2014\u5728\u534a\u5c9b\u9152\u5e97\u8ba8\u8bba\u4e86\u8bb8\u4e45\uff0c\u4ed6\u4e5f\u662f\u7b49\u5230\u73b0\u5728\u6ca1\u6709\u7528\u665a\u9910\u2014\u2014\u70b9\u8fc7\u9910\uff0c\u770b\u7740\u8bb8\u601d\u7ebf\u6761\u9c9c\u660e\u7684\u773c\u7ebf\u4e0e\u957f\u957f\u5fae\u98a4\u7684\u776b\u6bdb\uff0c\u53c8\u6b6a\u8fc7\u5934\u53bb\u770b\u5979\u5728\u753b\u4ec0\u4e48\uff0c\u7ebf\u6761\u6e10\u4e30\u5bcc\u8d77\u6765\uff0c\u6709\u7a7f\u8857\u9a91\u697c\u7684\u590d\u5f0f\u957f\u8857\uff0c\u8bb8\u601d\u89c1\u5f20\u606a\u51dd\u795e\u770b\u7740\uff0c\u5979\u5c31\u5206\u5fc3\u753b\u4e0d\u4e0b\u53bb\uff0c\u5c06\u9910\u5dfe\u7eb8\u5f80\u5f20\u606a\u8ddf\u524d\u4e00\u63a8\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u65f6\u95f4\u4f60\u90fd\u4e0d\u5173\u5fc3\u6a61\u6811\u56ed\u90a3\u8fb9\uff0c\u8fd9\u662f\u5b66\u5e9c\u5df7\u7684\u8bbe\u8ba1\u7a3f\uff0c\u90fd\u5df2\u7ecf\u5f00\u59cb\u65bd\u5de5\uff0c\u4f60\u60f3\u4fee\u6539\u90fd\u6765\u4e0d\u53ca\uff0c\u4f60\u89c9\u5f97\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u201c\u60f3\u4fee\u6539\u90fd\u4e0d\u53ca\u4e86\uff0c\u8fd9\u65f6\u5019\u62ff\u7ed9\u6211\u770b\uff0c\u4e0d\u662f\u903c\u7740\u6211\u5938\u4f60\u5417\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u90b5\u81f3\u521a\u4e94\u6708\u5e95\u3001\u516d\u6708\u521d\u4e24\u6b21\u5c06\u5b66\u5e9c\u5df7\u53ca\u9644\u5c5e\u9752\u5e74\u516c\u5bd3\u7684\u8bbe\u8ba1\u65b9\u6848\u62ff\u7ed9\u4ed6\u770b\uff0c\u4e24\u6b21\u88ab\u4ed6\u9000\u4e86\u56de\u53bb\uff0c\u540e\u6765\u5f20\u606a\u8ba9\u90b5\u81f3\u521a\u4ed6\u4eec\u51e0\u4e2a\u5408\u4f19\u4eba\u4e4b\u95f4\u5546\u91cf\uff0c\u4e0d\u8981\u4e8b\u4e8b\u90fd\u6765\u6d88\u78e8\u4ed6\u5b9d\u8d35\u7684\u60a0\u95f2\u65f6\u5149\uff0c\u540e\u6765\u542c\u8bf4\u662f\u8bb8\u601d\u8bbe\u8ba1\u4e86\u65b0\u7684\u65b9\u6848\uff0c\u4e0d\u8fc7\u4ed6\u662f\u771f\u505a\u51fa\u653e\u624b\u4e0d\u7ba1\u4e16\u7eaa\u9526\u6e56\u4e8b\u60c5\u7684\u6837\u5b50\uff0c\u5373\u4f7f\u90fd\u5f00\u59cb\u65bd\u5de5\u4e86\uff0c\u5f20\u606a\u8fd8\u6ca1\u6709\u770b\u5230\u8bb8\u601d\u8bbe\u8ba1\u7684\u65b9\u6848\u662f\u4ec0\u4e48\u5462\uff0c\u4ed6\u62ff\u8fc7\u9910\u5dfe\u7eb8\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u8f7b\u7b11\u4e86\u4e24\u58f0\uff0c\u8bf4\uff1a\u201c\u8fd9\u662f\u6b63\u89e3\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u771f\u7684\uff1f\u201d\u8bb8\u601d\u5e26\u7740\u96c0\u8dc3\u7684\u5fc3\u60c5\u770b\u7740\u5f20\u606a\uff0c\u201c\u4e0d\u662f\u54c4\u6211\u5f00\u5fc3\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u554a\u2026\u2026\u201d\u5f20\u606a\u62d6\u957f\u58f0\u8c03\uff0c\u201c\u53cd\u6b63\u90fd\u5df2\u7ecf\u65bd\u5de5\u4e86\uff0c\u4f60\u8fd8\u5728\u4e4e\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ec\uff01\u201d\u8bb8\u601d\u4f6f\u88c5\u8584\u6012\u7684\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u6d01\u767d\u7684\u8138\u988a\u4eff\u4f5b\u51ac\u65e5\u7684\u521d\u96ea\u6cdb\u7740\u5915\u9633\u5149\uff0c\u84d3\u857e\u4f3c\u7684\u5a07\u8273\u53cc\u5507\u4eff\u4f5b\u53e6\u4e00\u79cd\u6709\u751f\u547d\u7684\u4e1c\u897f\u5728\u6297\u8bae\u5f20\u606a\u620f\u5f04\u5979\uff0c\u201c\u8f85\u4fee\u8fc7\u5efa\u7b51\u8bbe\u8ba1\u65b9\u9762\u7684\u5b66\u4e1a\uff0c\u53ea\u662f\u8f85\u4fee\u3001\u4f5c\u4e3a\u7231\u597d\u800c\u5df2\uff0c\u5374\u5b8c\u5168\u6ca1\u6709\u81ea\u4fe1\uff0c\u4f60\u8bf4\u8bf4\u770b\u2026\u2026\u201d\u5374\u662f\u96be\u5f97\u7684\u5a07\u67d4\u8bed\u97f3\uff0c\u542c\u5f97\u5f20\u606a\u8033\u6839\u5b50\u53d1\u9165\u3002</p><p>\u3000\u3000\u201c\u71d5\u5f52\u6e56\u533a\u7684\u9ad8\u6821\u7fa4\u53ca\u6a61\u6811\u56ed\u8ba1\u5212\u589e\u52a0\u51fa\u6765\u7684\u6d88\u8d39\u4eba\u7fa4\uff0c\u90fd\u652f\u6491\u5b66\u5e9c\u5df7\u6269\u5efa\u51fa\u66f4\u591a\u7684\u5546\u4e1a\u7528\u623f\u51fa\u6765\uff1b\u5b66\u5e9c\u5df7\u7684\u5546\u94fa\u91c7\u53d6\u56de\u8fc1\u8865\u5c1d\uff0c\u4ece\u8d62\u5229\u89d2\u5ea6\u8003\u8651\uff0c\u4e5f\u8981\u6c42\u6269\u5efa\u51fa\u66f4\u591a\u7684\u5546\u4e1a\u9762\u79ef\u2014\u2014\u5546\u94fa\u7684\u552e\u4ef7\u8981\u8fdc\u9ad8\u8fc7\u4f4f\u5b85\uff0c\u53d7\u9650\u4e8e\u5c42\u9ad8\uff0c\u53ea\u80fd\u6269\u5927\u5360\u5730\u9762\u79ef\uff0c\u5b66\u5e9c\u5df7\u7684\u8bbe\u8ba1\u601d\u8def\u4fbf\u5e94\u8be5\u662f\u8fd9\u6837\uff0c\u504f\u504f\u90b5\u81f3\u521a\u624b\u4e0b\u90a3\u4e9b\u8bbe\u8ba1\u5e08\u4e24\u6b21\u90fd\u6ca1\u6709\u6293\u51c6\u91cd\u70b9\u2026\u2026\u201d\u5f20\u606a\u4f38\u624b\u6478\u4e86\u6478\u8bb8\u601d\u5ae9\u6ed1\u7684\u8138\u988a\uff0c\u201c\u590d\u5f0f\u957f\u8857\uff0c\u76f8\u5f53\u5c06\u5b66\u5e9c\u5df7\u5ef6\u957f\u4e86\u4e00\u500d\uff0c\u8de8\u8857\u9a91\u697c\u8bbe\u8ba1\uff0c\u53c8\u518d\u4e00\u6b65\u589e\u52a0\u4e86\u5efa\u7b51\u9762\u79ef\uff0c\u8fd9\u5f53\u7136\u662f\u6b63\u89e3\u7684\u8bbe\u8ba1\u601d\u8def\uff0c\u4e0d\u8bf7\u4f60\u53bb\u505a\u8bbe\u8ba1\u5e08\uff0c\u771f\u662f\u4ed6\u4eec\u778e\u4e86\u773c\uff0c\u8fd8\u975e\u8981\u6211\u6697\u793a\u4e24\u6b21\u2026\u2026\u201d\u5f20\u606a\u88c5\u51fa\u4e00\u4ed8\u65e0\u53ef\u5948\u4f55\u7684\u6a21\u6837\u6447\u5934\u82e6\u7b11\u3002</p><p>\u3000\u3000\u8bb8\u601d\u6342\u5507\u8f7b\u7b11\uff0c\u90a3\u77ac\u95f4\u795e\u91c7\u98de\u626c\u3001\u5a07\u8273\u5982\u82b1\u3002</p><p>\u3000\u3000\u7528\u8fc7\u9910\uff0c\u4e24\u4eba\u76f8\u4f9d\u8d70\u5728\u4e2d\u73af\u706f\u706b\u8ff7\u79bb\u7684\u591c\u8272\u4e4b\u4e0b\u3002</p><p>\u3000\u3000\u4e0d\u4ec5\u5b66\u5e9c\u5df7\u7684\u6539\u9020\uff0c\u6a61\u6811\u56ed\u56ed\u533a\u7684\u5efa\u8bbe\u89c4\u5212\uff0c\u5f20\u606a\u65e9\u5c31\u8bf4\u8fc7\u8981\u9075\u4ece\u8bb8\u601d\u7684\u610f\u56fe\uff0c\u8fd9\u4e24\u4e2a\u6708\uff0c\u8bb8\u601d\u5979\u4eba\u591a\u534a\u65f6\u95f4\u7559\u5728\u5efa\u90ba\uff0c\u5012\u662f\u5f20\u606a\u4e24\u6b21\u5306\u5306\u5fd9\u5fd9\u7684\u7ecf\u8fc7\u5efa\u90ba\uff0c\u4e24\u6b21\u90fd\u662f\u966a\u5510\u5a67\u7ecf\u8fc7\u5efa\u90ba\uff0c\u4e5f\u5c31\u6ca1\u6709\u505a\u505c\u7559\uff0c\u4e5f\u4e0d\u8981\u8bf4\u53bb\u4eb2\u773c\u770b\u770b\u6a61\u6811\u56ed\u7684\u8fdb\u5c55\u4e86\u3002</p><p>\u3000\u3000\u9996\u5148\u8981\u5c06\u5e08\u5927\u5357\u7aef\u7684\u4e00\u5bb6\u5370\u67d3\u5382\u4e0e\u4e00\u5bb6\u6f02\u67d3\u5382\u7684\u5382\u533a\u6539\u5efa\u6210\u521b\u4e1a\u56ed\u533a\uff0c\u5148\u5c06\u6a61\u6811\u56ed\u8ba1\u5212\u5206\u6b65\u9aa4\u5b9e\u65bd\u8d77\u6765\u2014\u2014\u521d\u521b\u671f\u672a\u6210\u89c4\u6a21\uff0c\u5360\u5730\u4e5f\u4e0d\u4f1a\u592a\u591a\u2014\u2014\u7136\u540e\u624d\u6709\u5145\u88d5\u7684\u65f6\u95f4\u53bb\u5b9e\u65bd\u66f4\u5927\u89c4\u6a21\u7684\u57fa\u5efa\u3002</p><p>\u3000\u3000\u8bb8\u601d\u5012\u662f\u6709\u5341\u8db3\u7684\u5174\u8da3\u8c08\u8bba\u8fd9\u4e9b\u8bdd\u9898\uff0c\u5f20\u606a\u4fbf\u4e0e\u5979\u4e00\u8fb9\u901b\u8857\u4e00\u8fb9\u542c\u5979\u8bf4\u90a3\u4e9b\u5947\u601d\u5999\u60f3\u4f3c\u7684\u6784\u60f3\u3002</p><p>\u3000\u3000\u5f20\u606a\u6700\u521d\u7684\u60f3\u6cd5\u5c31\u662f\u8981\u8ba9\u6a61\u6811\u56ed\u8ba1\u5212\u6fc0\u53d1\u3001\u6210\u5c31\u56fd\u4eba\u521b\u65b0\u7684\u610f\u5fd7\uff0c\u56ed\u533a\u81ea\u7136\u4e0d\u80fd\u641e\u5f97\u8ddf\u5de5\u4e1a\u5efa\u7b51\u90a3\u822c\u6b7b\u6c14\u6c89\u6c89\uff0c\u800c\u4e14\u8bb8\u601d\u90a3\u4e9b\u770b\u6765\u4e0d\u5207\u5b9e\u9645\u7684\u68a6\u60f3\uff0c\u5f20\u606a\u66f4\u613f\u610f\u53bb\u627f\u8f7d\u3002</p><p>\u3000\u3000\u201c\u8bf4\u4e0d\u5b9a\u6211\u80fd\u6210\u7acb\u4e00\u4e2a\u8bbe\u8ba1\u5e08\u5de5\u4f5c\u5ba4\u5462\u3002\u201d\u8bb8\u601d\u4e0d\u786e\u5b9a\u7684\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u8bb8\u601d\u773c\u775b\u4f3c\u4e4e\u71c3\u70e7\u7740\u67d0\u79cd\u6df1\u8272\u7684\u706b\u7130\uff0c\u637b\u7740\u5979\u6709\u4e9b\u6c57\u6e7f\u5fae\u51c9\u7684\u624b\u5fc3\uff0c\u867d\u7136\u4e0a\u56de\u5728\u60e0\u5c71\u5979\u66fe\u8bf4\u8fc7\u8981\u5728\u81ea\u5df1\u5e38\u9a7b\u8db3\u7684\u57ce\u5e02\u5f00\u8bbe\u5496\u5561\u5385\u6216\u8bb8\u9152\u5427\uff0c\u5979\u5927\u6982\u5f88\u5c11\u4e3a\u81ea\u5df1\u71c3\u70e7\u8d77\u4e00\u79cd\u68a6\u60f3\u5427\u3002</p><p>\u3000\u3000\u201c\u4f3c\u4e4e\u4e0d\u884c\u554a\uff0c\u53ea\u662f\u8f85\u4fee\u8fc7\u5efa\u7b51\u8bbe\u8ba1\uff0c\u603b\u4e0d\u80fd\u8ddf\u6b63\u89c4\u7684\u5efa\u7b51\u8bbe\u8ba1\u5e08\u62a2\u996d\u7897\uff0c\u201d\u770b\u7740\u5f20\u606a\u4e0d\u8bf4\u8bdd\uff0c\u8bb8\u601d\u81ea\u5df1\u6253\u4e86\u9000\u5802\u9f13\uff0c\u201c\u53c8\u62a2\u4e0d\u8fc7\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u5728\u60f3\u4f60\u4f1a\u4e0d\u4f1a\u540c\u610f\u8ba9\u6211\u505a\u5408\u4f19\u4eba\uff0c\u201d\u5f20\u606a\u624b\u7f20\u8fc7\u8bb8\u601d\u5a07\u8f6f\u7684\u8170\u80a2\uff0c\u5c06\u5979\u6402\u5728\u81ea\u5df1\u7684\u8eab\u524d\uff0c\u51dd\u89c6\u7740\u5979\u7f8e\u4e3d\u7684\u7738\u5b50\uff0c\u6620\u7740\u8fd9\u7e41\u8363\u90fd\u5e02\u5bc2\u5bde\u7684\u706f\u706b\uff0c\u201c\u82e5\u662f\u8981\u5c06\u68a6\u60f3\u4e2d\u7684\u5efa\u7b51\u7fa4\u4f53\u63cf\u7ed8\u51fa\u6765\uff0c\u800c\u4e13\u4e1a\u7684\u90e8\u5206\u7531\u4e13\u4e1a\u7684\u4eba\u58eb\u53bb\u5904\u7406\uff0c\u6211\u4e5f\u662f\u5f88\u5411\u5f80\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53ef\u4ee5\u5417\uff1f\u201d\u8bb8\u601d\u773c\u775b\u4e00\u4eae\u2026\u2026</p><p>\u3000\u3000\u201c\u8981\u6211\u8bf4\u5b9e\u8bdd\u554a\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u201c\u55ef\uff01\u201d\u8bb8\u601d\u671f\u76fc\u7684\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u6bd4\u8d77\u522b\u4eba\uff0c\u6211\u66f4\u559c\u6b22\u4f60\u5bf9\u5efa\u7b51\u7684\u68a6\u60f3\uff1b\u867d\u7136\u6211\u4eec\u8fd8\u4e0d\u80fd\u5b8c\u5168\u7684\u968f\u5fc3\u6240\u6b32\uff0c\u5728\u8fd9\u4e2a\u68a6\u60f3\u4e0a\u5962\u4f88\u4e00\u4e9b\uff0c\u6211\u6ca1\u89c9\u5f97\u6709\u4ec0\u4e48\u4e0d\u60f3\u3002\u201d\u5f20\u606a\u5fc3\u60f3\u5bf9\u5efa\u7b51\u6709\u7740\u8d85\u5947\u5e7b\u60f3\u7684\u5730\u57df\u5e94\u8be5\u662f\u4e2d\u4e1c\u90a3\u91cc\u7684\u4ea7\u6cb9\u5bcc\u56fd\u3002</p><p>\u3000\u3000\u201c\u8d8a\u79c0\u8fd9\u8fb9\uff0c\u600e\u4e48\u529e\uff1f\u201d\u8bb8\u601d\u53c8\u6709\u4e9b\u72b9\u8c6b\uff0c\u5979\u51e0\u4e4e\u80fd\u60f3\u8c61\u81ea\u5df1\u5168\u8eab\u5fc3\u6295\u5165\u8bbe\u8ba1\u5e08\u5de5\u4f5c\u5ba4\u4e4b\u540e\u7684\u60c5\u5f62\u3002</p><p>\u3000\u3000\u201c\u6ca1\u5173\u7cfb\uff0c\u4e22\u7ed9\u522b\u4eba\u5c31\u662f\u2026\u2026\u201d\u5f20\u606a\u77e5\u9053\u8bb8\u601d\u662f\u4e3a\u81ea\u5df1\u624d\u5230\u9999\u6e2f\u6765\u4e00\u4eba\u652f\u6491\u7740\u8d8a\u79c0\uff0c\u540e\u6765\u81ea\u5df1\u5c06\u80a1\u4efd\u5927\u90e8\u5206\u90fd\u8f6c\u7ed9\u665a\u6674\uff0c\u5927\u6982\u8bb8\u601d\u4e5f\u4e0d\u597d\u610f\u601d\u76f4\u63a5\u5c31\u8bf4\u8981\u79bb\u5f00\u3002\u65e2\u7136\u5979\u60f3\u5230\u6709\u81ea\u5df1\u559c\u6b22\u505a\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u5f53\u7136\u4e0d\u4f1a\u5c06\u5979\u518d\u7cfb\u5728\u8d8a\u79c0\u3002</p><p>\u3000\u3000\u8bb8\u7ef4\u516d\u6708\u4e0b\u65ec\u56de\u4e86\u4e00\u8d9f\u5efa\u90ba\u901a\u5e38\u6bd5\u4e1a\u8bba\u6587\u7b54\u8fa9\u53c8\u53bb\u53f0\u6e7e\u4e0e\u5b59\u9759\u9999\u3001\u6c5f\u9edb\u513f\u5979\u4eec\u6c47\u5408\uff0c\u8fd9\u6bb5\u65f6\u95f4\u4eba\u4e00\u76f4\u4e0d\u5728\u9999\u6e2f\uff0c\u5f20\u606a\u4fbf\u966a\u8bb8\u601d\u53bb\u6e2f\u5927\u9644\u8fd1\u7684\u516c\u5bd3\u8fc7\u591c\u3002</p><p>\u3000\u3000\u95e8\u6249\u5173\u4e0a\u7684\u90a3\u4e00\u523b\uff0c\u8eab\u4f53\u5185\u7684\u60c5\u6b32\u6fc0\u71c3\uff0c\u5c31\u62b5\u7740\u95e8\u6b7b\u529b\u7684\u4eb2\u543b\uff0c\u8fc7\u4e86\u597d\u4e45\uff0c\u8bb8\u601d\u624d\u4ece\u8ff7\u7729\u7684\u610f\u8bc6\u91cc\u6e05\u9192\u8fc7\u6765\uff0c\u5979\u5e2e\u5f20\u606a\u8fdb\u6d74\u5ba4\u653e\u6c34\uff0c\u5f20\u606a\u8ba9\u5979\u5148\u8fdb\u53bb\u6d17\uff0c\u4ece\u78e8\u7802\u73bb\u7483\u91cc\u770b\u7740\u8bb8\u601d\u5728\u91cc\u9762\u5c06\u6d0b\u88c5\u8131\u4e0b\u540e\u7684\u66fc\u5999\u8eab\u59ff\uff0c\u5f20\u606a\u6293\u7740\u6d74\u5ba4\u7684\u78e8\u7802\u73bb\u7483\u95e8\u8981\u8fdb\u53bb\u3002\u8bb8\u601d\u8f6c\u8eab\u6293\u7d27\u95e8\u628a\u624b\u8981\u963b\u6b62\u4ed6\uff0c\u53ea\u7a7f\u7740\u857e\u4e1d\u8fb9\u7684\u7ea2\u8272\u5e95\u88e4\uff0c\u9c9c\u5ae9\u7684\u79c0\u8db3\u8d64\u8e0f\u5728\u9632\u6ed1\u5730\u7816\u5730\u4e0a\uff0c\u4e00\u53ea\u624b\u8fd8\u6342\u7740\u80f8\u53e3\uff0c\u54ea\u91cc\u6342\u5f97\u8fc7\u6765\uff1f\u8eab\u4e0a\u5a07\u5ae9\u767d\u7699\u5982\u96ea\u7684\u808c\u80a4\u6216\u8bb8\u662f\u56e0\u4e3a\u6d74\u5ba4\u91cc\u7684\u70ed\u6c7d\u4e5f\u6216\u8bb8\u56e0\u4e3a\u603b\u4e9b\u6709\u7f9e\u602f\u67d3\u7740\u6843\u7ea2\u8272\uff0c\u9ad8\u6311\u7684\u8eab\u4f53\u66fc\u5999\u6709\u81f4\uff0c\u9ad8\u9ad8\u7684\u80f8\u3001\u7ea4\u7ec6\u7684\u8170\uff0c\u4fee\u957f\u767d\u5ae9\u7684\u817f\uff0c\u65e0\u4e0d\u6563\u53d1\u51fa\u6210\u719f\u7684\u6027\u611f\u9b45\u529b\u3002\u5f20\u606a\u770b\u4e86\u5fc3\u733f\u610f\u9a6c\uff0c\u54ea\u91cc\u7b49\u5f97\u53ca\u5728\u7b49\u8bb8\u601d\u5148\u6d17\u6fa1\u5b8c\u81ea\u5df1\u518d\u53bb\u6d17\u6fa1\uff0c\u624b\u63d2\u5728\u95e8\u7f1d\u91cc\u4e0d\u8ba9\u8bb8\u601d\u5173\u5b9e\uff0c\u6d8e\u7740\u8138\u8bf4\uff1a\u201c\u4f60\u8981\u4e0d\u8ba9\u6211\u8fdb\u53bb\uff0c\u521a\u624d\u5728\u8857\u4e0a\u8bf4\u7684\u8bdd\uff0c\u6211\u5c31\u4e0d\u627f\u8ba4\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e2a\u65e0\u8d56\uff01\u201d</p><p>\u3000\u3000\u8bb8\u601d\u62ff\u5f20\u606a\u6ca1\u529e\u6cd5\uff0c\u6342\u7740\u80f8\u53e3\u8f6c\u8fc7\u8eab\u53bb\uff0c\u54ea\u91cc\u60f3\u5230\u5f20\u606a\u5728\u540e\u9762\u62e8\u5979\u7684\u5185\u88e4\u8fb9\uff0c\u62e8\u6210\u7a84\u7a84\u7684\u4e00\u8fb9\u52d2\u8fdb\u81c0\u6c9f\u91cc\uff0c\u8bb8\u601d\u53ea\u5f97\u8fde\u7740\u5185\u88e4\u8eb2\u5230\u6d74\u7f38\u91cc\uff0c\u8eab\u4f53\u6d78\u5230\u6c34\u91cc\uff0c\u624d\u5c06\u5185\u88e4\u8131\u4e0b\u6765\u3002</p><p>\u3000\u3000\u51e0\u756a\u62b5\u6b7b\u7f20\u9526\u4e4b\u540e\uff0c\u4e24\u4eba\u4f9d\u504e\u800c\u5367\uff0c\u7a97\u6237\u6253\u5f00\uff0c\u51cc\u6668\u6e05\u51c9\u3001\u5e26\u7740\u6d77\u8165\u5473\u7684\u98ce\u5439\u900f\u5c4b\u5ba4\uff0c\u94c5\u84dd\u5982\u6d17\u7684\u591c\u8272\u91cc\u7e41\u661f\u95ea\u70c1\u3002</p><p>\u3000\u3000\u201c\u6211\u6ca1\u6709\u592a\u719f\u6089\u7684\u4eba\uff0c\u771f\u8981\u6210\u7acb\u5de5\u4f5c\u5ba4\uff0c\u6211\u8ba9\u5468\u9038\u8fc7\u53bb\u5e2e\u6211\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u601d\u6700\u521d\u5230\u9999\u6e2f\u6765\uff0c\u5468\u9038\u5c31\u966a\u5979\u8fc7\u6765\u4e86\uff0c\u4e00\u4e2a\u5f88\u5e72\u7ec3\u7684\u6e05\u79c0\u5973\u5b69\u5b50\uff0c\u6027\u683c\u5f88\u597d\u5f3a\uff0c\u80fd\u5e2e\u8bb8\u601d\u5f88\u591a\u5fd9\u3002\u613f\u610f\u627f\u8f7d\u8bb8\u601d\u7684\u68a6\u60f3\u662f\u4e00\u56de\u4e8b\uff0c\u5f20\u606a\u8fd8\u662f\u4e0d\u60f3\u8bb8\u601d\u4f1a\u592a\u8f9b\u82e6\uff1b\u8bb8\u601d\u4e0d\u8bf4\uff0c\u5f20\u606a\u4e5f\u4f1a\u4eb2\u81ea\u6311\u9009\u51e0\u4e2a\u80fd\u529b\u5f3a\u53c8\u4fe1\u5f97\u8fc7\u7684\u4eba\u53bb\u5e2e\u5979\u3002\u7ffb\u7740\u8bb8\u601d\u5e8a\u8fb9\u7684\u4e66\u7c4d\uff0c\u90fd\u662f\u5efa\u7b51\u8bbe\u8ba1\u65b9\u9762\u7684\u4e13\u8457\uff0c\u542c\u8bb8\u601d\u7684\u610f\u601d\uff0c\u8fd8\u4f1a\u53bb\u9999\u6e2f\u4e2d\u6587\u5927\u5b66\u91cd\u4fee\u8fd9\u4e00\u90e8\u5206\u7684\u5b66\u4e1a\u3002\u8bdd\u8bf4\u56de\u6765\uff0c\u5728\u9999\u6e2f\uff0c\u5012\u662f\u9999\u6e2f\u5927\u5b66\u7684\u5efa\u7b51\u7cfb\u6700\u5f3a\uff0c\u8bb8\u601d\u662f\u60f3\u907f\u514d\u4ee5\u540e\u5728\u9999\u6e2f\u4e0e\u5510\u5a67\u5c34\u5c2c\u78b0\u9762\uff0c\u4e0d\u8fc7\u9999\u6e2f\u4e2d\u6587\u4e2d\u5b66\u7684\u5efa\u7b51\u7cfb\u662f\u7eaf\u8bbe\u8ba1\u7684\uff0c\u6452\u5f03\u7269\u7406\u77e5\u8bc6\u65b9\u9762\u7684\u5185\u5bb9\uff0c\u8fd9\u4e00\u70b9\u5012\u4e5f\u9002\u5408\u8bb8\u601d\u3002</p><p>\u3000\u3000\u9192\u6765\u65f6\uff0c\u8bb8\u601d\u4e0d\u5728\u8eab\u8fb9\uff0c\u95fb\u7740\u53a8\u623f\u95f4\u4f20\u6765\u6e05\u7ca5\u5f25\u6f2b\u8fc7\u6765\u7684\u9999\u6c14\uff0c\u5df2\u7ecf\u4e0a\u5348\u4e5d\u70b9\u949f\u4e86\uff0c\u592a\u9633\u900f\u8fc7\u7a97\u5e18\u900f\u5c04\u8fdb\u6765\uff0c\u5c4b\u91cc\u5f00\u7740\u7a7a\u8c03\uff0c\u611f\u89c9\u4e0d\u5230\u9999\u6e2f\u590f\u5929\u7684\u708e\u70ed\u3002</p><p>\u3000\u3000\u4f9d\u5728\u5e8a\u5934\uff0c\u62ff\u8d77\u5e8a\u5934\u7684\u5ea7\u673a\u7ed9\u665a\u6674\u62e8\u7535\u8bdd\uff0c\u51c6\u5907\u8ddf\u5979\u8bf4\u8d8a\u79c0\u516c\u53f8\u7684\u4eba\u4e8b\u5b89\u6392\u3002</p><p>\u3000\u3000\u201c\u4f60\u5728\u8bb8\u601d\u90a3\u91cc\u7ed9\u6211\u6253\u7535\u8bdd\u554a\u2026\u2026\u201d\u665a\u6674\u6e29\u5a49\u5982\u6c34\u7684\u58f0\u97f3\u4ece\u7535\u8bdd\u91cc\u4f20\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u4f60\u77e5\u9053\u8fd9\u91cc\u7684\u5ea7\u673a\u53f7\uff1f\u201d\u5f20\u606a\u5fc3\u91cc\u6709\u9b3c\uff0c\u65e9\u77e5\u9053\u62ff\u624b\u673a\u6253\u8fc7\u53bb\u597d\u4e86\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e0d\u77e5\u9053\uff0c\u8bb8\u601d\u524d\u5929\u56de\u9999\u6e2f\u8fd8\u62ff\u8fd9\u4e2a\u7535\u8bdd\u6253\u6211\u624b\u673a\u5462\u3002\u8fd9\u4e48\u65e9\u6253\u7535\u8bdd\u7ed9\u6211\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u65e9\uff1f\u201d\u5f20\u606a\u7591\u60d1\u7684\u770b\u7740\u5e8a\u5934\u67dc\u4e0a\u7684\u8155\u8868\u4e0e\u4ece\u7a97\u5e18\u7f1d\u91cc\u900f\u8fdb\u6765\u7684\u9633\u5149\uff0c\u786e\u5b9a\u662f\u8fc7\u4e86\u4e0a\u5348\u4e5d\u70b9\u949f\u65e0\u7591\uff0c\u604d\u7136\u95f4\u6653\u5f97\u665a\u6674\u662f\u6697\u6307\u4ed6\u6628\u591c\u80af\u5b9a\u8ddf\u8bb8\u601d\u660f\u5929\u9ed1\u5730\u8fd9\u65f6\u5019\u9192\u8fc7\u6765\u76f8\u5bf9\u6765\u8bf4\u6709\u4e9b\u65e9\uff0c\u4ed6\u4e0d\u6e05\u695a\u665a\u6674\u5fc3\u91cc\u4f1a\u600e\u4e48\u60f3\uff0c\u542c\u8bed\u8c03\u5012\u6ca1\u6709\u4ec0\u4e48\u5f02\u6837\uff0c\u5482\u4e86\u5482\u5634\uff0c\u5e72\u7b11\u4e86\u4e24\u58f0\uff0c\u5c06\u5c34\u5c2c\u7684\u60c5\u7eea\u63a9\u9970\u8fc7\u53bb\uff0c\u8ddf\u5979\u8bf4\u8d77\u8bb8\u601d\u8981\u53e6\u5916\u6210\u7acb\u8bbe\u8ba1\u5e08\u5de5\u4f5c\u5ba4\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u6211\u77e5\u9053\u554a\uff0c\u4e94\u6708\u521d\u5979\u5c31\u8ddf\u6211\u8bf4\u8fc7\u8fd9\u4e2a\u60f3\u6cd5\uff0c\u6211\u8fd8\u4ee5\u4e3a\u5979\u65e9\u5c31\u8ddf\u4f60\u8bf4\u4e86\u5462\u2026\u2026\u8d8a\u79c0\u90a3\u8fb9\uff0c\u4f60\u89c9\u5f97\u4ea4\u7ed9\u8c01\u8d1f\u8d23\u5408\u9002\uff1f\u603b\u4e4b\u8981\u5c06\u8d8a\u79c0\u3001\u7231\u8fbe\u7535\u5b50\u7684\u6d77\u5916\u516c\u53f8\u5206\u90e8\u8fd8\u662f\u5728\u9999\u6e2f\u7684\u7814\u53d1\u4e2d\u5fc3\u2014\u2014\u540d\u4e49\u4e0a\u5404\u5f52\u5404\u7684\u2014\u2014\u603b\u4e4b\u8981\u4e00\u5e76\u7ba1\u7406\u8d77\u6765\u3002\u8fd9\u8fb9\u7684\u4e8b\u60c5\uff0c\u6211\u90fd\u7126\u5934\u70c2\u989d\u4e86\uff0c\u6211\u53ef\u4e0d\u60f3\u518d\u4e3a\u90a3\u8fb9\u7684\u4e8b\u60c5\u8d39\u795e\u4e86\uff0c\u8463\u4e8b\u957f\u4ec0\u4e48\u7684\uff0c\u4e5f\u627e\u522b\u4eba\u517c\u4e86\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u53ef\u6ca1\u6709\u60f3\u5230\u8bb8\u601d\u65e9\u5c31\u8ddf\u665a\u6674\u8bf4\u8d77\u8fc7\u8fd9\u4e8b\uff0c\u8fd8\u4ee5\u4e3a\u5979\u5fc3\u91cc\u6709\u82a5\u8482\uff0c\u54ea\u91cc\u60f3\u5230\u5979\u4eec\u5973\u4eba\u5374\u5148\u8054\u5408\u8d77\u6765\uff0c\u5c06\u81ea\u5df1\u8499\u5728\u9f13\u91cc\uff1f\u4ed6\u62ff\u7535\u8bdd\u7b52\u6572\u4e86\u6572\u989d\u5934\uff0c\u5728\u60f3\u8c01\u8c03\u5230\u9999\u6e2f\u6765\u5408\u9002\u3002\u8bf4\u5b9e\u8bdd\uff0c\u5f20\u606a\u4e00\u76f4\u60f3\u5c06\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u518d\u62c9\u8fdb\u6765\uff0c\u4f46\u662f\u4ee5\u4ed6\u4eec\u4e24\u4eba\u7684\u8d44\u5386\uff0c\u8bf7\u5b59\u5c1a\u4e49\u6216\u845b\u660e\u5fb7\u62c5\u4efb\u8d8a\u79c0\u63a7\u80a1\u7684\u8463\u4e8b\u5bf9\u8fd9\u8fb9\u7684\u4e1a\u52a1\u8fdb\u884c\u6307\u5bfc\u53ef\u4ee5\uff0c\u8bf7\u4ed6\u4eec\u8d1f\u8d23\u5177\u4f53\u800c\u5fae\u7684\u65e5\u5e38\u7ba1\u7406\uff0c\u5c31\u6709\u4e9b\u8f7b\u6162\u4e86\u3002</p><p>\u3000\u3000\u9999\u6e2f\u8fd9\u8fb9\u4e3b\u8981\u8d1f\u8d23\u534f\u8c03\u4e1c\u5357\u4e9a\u7684\u5e02\u573a\u4e0e\u83f2\u5f8b\u5bbe\u3001\u9a6c\u6765\u897f\u4e9a\u4e24\u5bb6\u5de5\u5382\u7684\u751f\u4ea7\u7ba1\u7406\uff0c\u8fd8\u8981\u8d1f\u8d23\u8d8a\u79c0\u63a7\u80a1\u4e0e\u4ea7\u54c1\u5f00\u53d1\u4e2d\u5fc3\u7684\u65e5\u5e38\u7ba1\u7406\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u6b63\u60f3\u4ec0\u4e48\u4eba\u5408\u9002\uff0c\u5176\u5b9e\u4ed6\u4e4b\u524d\u6709\u51c6\u5907\u5c06\u5218\u660e\u8f89\u63a8\u5230\u8fd9\u4e2a\u4f4d\u5b50\u4e0a\uff0c\u5f53\u7136\uff0c\u8fd9\u65f6\u5019\u662f\u7edd\u65e0\u53ef\u80fd\u7684\uff0c\u8c01\u8fd8\u4f1a\u7ba1\u5218\u660e\u8f89\u6b64\u65f6\u7684\u6b7b\u6d3b\uff1f\u8bb8\u601d\u8d70\u8fdb\u6765\uff0c\u8170\u95f4\u7cfb\u7740\u788e\u82b1\u56f4\u88d9\uff0c\u8fd8\u662f\u6d77\u5dde\u7279\u6709\u7684\u90a3\u79cd\u84dd\u5370\u67d3\u5e03\uff0c\u7cfb\u5f97\u8170\u7ec6\u80f8\u633a\u81c0\u5706\uff0c\u5a40\u5a1c\u591a\u59ff\uff0c\u89c1\u5f20\u606a\u62ff\u7740\u7535\u8bdd\u6572\u989d\u5934\uff0c\u95ee\u4ed6\uff1a\u201c\u662f\u5728\u7ed9\u665a\u6674\u59d0\u6253\u7535\u8bdd\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u5e94\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff0c\u201c\u5728\u60f3\u9999\u6e2f\u8fd9\u8fb9\u8ba9\u8c01\u8fc7\u6765\u8d1f\u8d23\u5408\u9002\u2026\u2026\u201d</p></article>"}}}]);
