(window.webpackJsonp=window.webpackJsonp||[]).push([[597],{620:function(p){p.exports={chapterTitle:null,title:"\u7b2c597\u7ae0 \u518d\u6b21\u76f8\u9022",content:"<article><h2>\u7b2c597\u7ae0 \u518d\u6b21\u76f8\u9022</h2><p>\u3000\u3000\u201c\u6211\u5728\u60f3\u5b66\u5e9c\u5df7\u7684\u4ee5\u540e\uff0c\u201d\u5f20\u606a\u7684\u786e\u5728\u60f3\u5b66\u5e9c\u5df7\u4ee5\u540e\u7684\u60c5\u5f62\uff0c\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff0c\u201c\u7269\u975e\u4eba\u662f\u5374\u4e0d\u8bc6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5b59\u9759\u6aac\u4e0d\u660e\u767d\u5f20\u606a\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u4e48\u7a81\u7136\u7684\u611f\u6168\uff0c\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u6ca1\u60f3\u660e\u767d\uff0c\u7ee7\u7eed\u7f8e\u6ecb\u6ecb\u7684\u80cc\u62b5\u7740\u62b5\u80cc\uff0c\u767d\u5ae9\u5ae9\u7684\u811a\u4e2b\u5b50\u6d78\u5728\u6e05\u51c9\u7684\u6d77\u6c34\u91cc\uff0c\u4eab\u53d7\u7740\u6e05\u51c9\u5fae\u8165\u7684\u6d77\u98ce\u2026\u2026</p><p>\u3000\u3000\u201c\u5582\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u542c\u7740\u80cc\u540e\u6709\u4e2a\u5a07\u67d4\u7684\u58f0\u97f3\u5728\u547c\u5524\uff0c\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u6d77\u6c34\u91cc\u6d78\u7740\u4e00\u4e2a\u5c0f\u5de7\u7cbe\u81f4\u7684\u8138\u86cb\uff0c\u957f\u767d\u4f3c\u7684\u8eab\u8eaf\u50cf\u9c7c\u4e00\u6837\u5728\u6e05\u6f88\u7684\u6d77\u6c34\u91cc\u6e38\u8361\uff0c\u767d\u6d6a\u8f7b\u9010\uff0c\u4e0d\u662f\u5f88\u957f\u7684\u79c0\u53d1\u8d34\u7740\u534a\u7247\u8138\uff0c\u5f20\u606a\u786e\u5b9a\u4e0d\u662f\u5728\u53eb\u81ea\u5df1\uff0c\u9876\u4e86\u9876\u5b59\u9759\u6aac\u7684\u8170\uff1a\u201c\u6709\u4eba\u5728\u53eb\u4f60\uff1f\u201d\u5de6\u53f3\u53c8\u6ca1\u6709\u5176\u4ed6\u4eba\uff0c\u5fc3\u60f3\u9664\u4e86\u5b59\u9759\u6aac\uff0c\u90a3\u4e2a\u5973\u5b69\u5b50\u4e0d\u4f1a\u627e\u522b\u4eba\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u770b\u4e86\u534a\u5929\u4e5f\u7591\u60d1\u3002</p><p>\u3000\u3000\u201c\u5582\uff0c\u4f60\u4e0d\u8ba4\u8bc6\u6211\u4e86\uff1f\u201d\u90a3\u4e2a\u5973\u5b69\u5b50\u6e38\u8fc7\u6765\u6293\u4f4f\u7901\u77f3\uff0c\u4e0a\u534a\u8eab\u9732\u51fa\u6c34\u9762\uff0c\u7075\u52a8\u7684\u773c\u775b\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u9996\u5148\u6ce8\u610f\u5230\u8fd9\u5973\u5b69\u5b50\u80f8\u90e8\u4e0d\u662f\u5f88\u5927\uff0c\u521a\u51fa\u6c34\uff0c\u6bd4\u8f83\u4fdd\u5b88\u7684\u6cf3\u8863\u6e7f\u8d34\u5728\u5a07\u5c0f\u8fd8\u6ca1\u6709\u53d1\u80b2\u5f00\u7684\u8eab\u4f53\u4e0a\uff0c\u81c0\u90e8\u7684\u6cf3\u8863\u8fd8\u6709\u957f\u8936\u8fb9\u5782\u4e0b\u6765\u2014\u2014\u8bb8\u601d\u51c6\u5907\u7684\u5c31\u662f\u8fd9\u79cd\u4fdd\u5b88\u7684\u6cf3\u8863\uff0c\u518d\u770b\u5979\u5c06\u6e7f\u5e16\u7684\u5934\u53d1\u634b\u5230\u8033\u540e\uff0c\u624d\u8ba4\u51fa\u6765\u662f\u98de\u673a\u4e0a\u76f8\u9047\u7684\u90a3\u4e2a\u7ea2\u8863\u5973\u5b69\uff0c\u5e74\u524d\u5728\u5f71\u6e7e\u56ed\u516c\u5bd3\u4e0e\u5b59\u9759\u6aac\u727a\u7272\u201c\u8272\u76f8\u201d\u4e5e\u8ba8\uff0c\u8fd9\u5973\u5b69\u8fd8\u51fa\u624b\u9614\u7ef0\u7684\u6350\u4e86\u4e00\u5f20\u5341\u4e8c\u4e07\u6e2f\u5e01\u7684\u652f\u7968\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u4f60\u8131\u4e86\u8863\u670d\u5dee\u70b9\u8ba4\u4e0d\u51fa\u4f60\u6765\u2026\u2026\u201d\u5f20\u606a\u5634\u91cc\u80e1\u4e71\u8bf4\u7740\u7ed9\u5b59\u9759\u6aac\u8098\u51fb\u4e86\u4e00\u4e0b\uff0c\u7ad9\u8d77\u6765\u4f38\u624b\u5c06\u5973\u5b69\u5b50\u4ece\u6c34\u91cc\u62c9\u4e0a\u7901\u77f3\uff0c\u201c\u8fd8\u662f\u5de7\u554a\uff0c\u5728\u832b\u832b\u5927\u6d77\u4e2d\u4e5f\u80fd\u76f8\u9047\uff0c\u6211\u4ee5\u524d\u4e0d\u4fe1\u673a\u7f18\u7684\uff0c\u73b0\u5728\u60f3\u4e0d\u4fe1\u4e5f\u4e0d\u884c\u554a\u3002\u201d\u5c06\u5b59\u9759\u6aac\u8eab\u8fb9\u90a3\u70b9\u5e73\u5766\u7684\u7a7a\u9699\u7559\u7ed9\u5979\u5750\uff0c\u4ed6\u5c31\u8e72\u5728\u4e00\u65c1\u784c\u811a\u7684\u7901\u77f3\u4e0a\u3002</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u54ea\u6709\u8fd9\u4e48\u5de7\u7684\u4e8b\uff1f\u6211\u5dee\u4e0d\u591a\u6bcf\u5e74\u5047\u671f\u90fd\u6765\u8fd9\u91cc\u6e21\u5047\u7684\uff0c\u4e5f\u4f4f\u5728\u5f71\u6e7e\u56ed\uff0c\u6211\u8fd8\u5947\u602a\u600e\u4e48\u5230\u4eca\u5929\u624d\u9047\u5230\u4f60\u4eec\u5462\uff1f\u201d\u5c0f\u5973\u5b69\u5b50\u5c31\u662f\u597d\u9a97\uff0c\u90a3\u5973\u5b69\u5b50\u5a07\u7b11\u7740\u8bf4\uff0c\u201c\u521a\u624d\u5728\u5cb8\u8fb9\u78b0\u5de7\u770b\u5230\u5085\u5927\u54e5\uff0c\u8fd8\u89c9\u5f97\u5947\u602a\u5462\uff0c\u4ed6\u8bf4\u4f60\u5728\u8fd9\u8fb9\u5b66\u51b2\u6d6a\u2026\u2026\u201d\u5f88\u4e56\u5de7\u7684\u8ddf\u5b59\u9759\u6aac\u6253\u62db\u547c\uff0c\u53c8\u8f6c\u8fc7\u5934\u95ee\u5f20\u606a\uff0c\u201c\u4f60\u5b66\u5f97\u600e\u4e48\u6837\uff0c\u8981\u4e0d\u8981\u6211\u6559\u4f60\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5dee\u70b9\u8ba9\u8fd9\u53e5\u8bdd\u7ed9\u6253\u4e0b\u6c34\uff0c\u5b59\u9759\u6aac\u8f7b\u8511\u7684\u7b11\u4e86\u7b11\uff0c\u8fd8\u8f7b\u8f7b\u7684\u54fc\u4e86\u4e00\u58f0\uff1a\u201c\u6559\u4e86\u534a\u5929\u8fde\u51b2\u6d6a\u677f\u90fd\u722c\u4e0d\u4e0a\u53bb\u2026\u2026\u201d\u5c06\u51b2\u6d6a\u677f\u4ece\u5f20\u606a\u624b\u91cc\u62a2\u8fc7\u6765\u4e22\u6c34\u91cc\uff0c\u6307\u5728\u6d77\u6c34\u91cc\u6f02\u8361\u7684\u51b2\u6d6a\u677f\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u6709\u672c\u9886\u5728\u592a\u9633\u843d\u5c71\u4e4b\u524d\u722c\u4e0a\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6015\u8fd9\u5973\u5b69\u5b50\u8ddf\u4ed6\u91cd\u63d0\u65e7\u4e8b\uff0c\u8bf4\u8d77\u6765\u81ea\u5df1\u8fd8\u6b20\u5979\u4e00\u573a\u6cd5\u8bed\u804a\u5929\u5462\uff0c\u4ed6\u8fd9\u65f6\u5019\u54ea\u6709\u65f6\u95f4\u53bb\u5b66\u6cd5\u8bed\uff1f\u8df3\u4e0b\u6c34\uff0c\u5e26\u7740\u51b2\u6d6a\u677f\u5230\u7a0d\u8fdc\u7684\u5730\u65b9\u7ec3\u4e60\uff0c\u7ec8\u6ca1\u6709\u5728\u592a\u9633\u843d\u5c71\u4e4b\u524d\u51ed\u501f\u81ea\u5df1\u7684\u80fd\u529b\u987a\u987a\u5f53\u5f53\u7684\u722c\u4e0a\u51b2\u6d6a\u677f\uff0c\u7b28\u62d9\u7684\u52a8\u4f5c\u5374\u60f9\u5f97\u4e24\u4e2a\u5973\u5b69\u5b50\u4fef\u6291\u5927\u7b11\u3002</p><p>\u3000\u3000\u8bb8\u601d\u4e0b\u5348\u56db\u70b9\u4ece\u516c\u53f8\u8d76\u4e86\u8fc7\u6765\uff0c\u5979\u6709\u4e9b\u5fe4\u6c34\uff0c\u5f20\u606a\u4fbf\u53bb\u966a\u5979\u6e38\u79bb\u6d77\u5cb8\uff0c\u53c8\u6362\u4e86\u4e00\u5757\u7a0d\u5927\u7684\u7901\u77f3\uff0c\u4e09\u4e2a\u5973\u4eba\u5927\u591a\u6570\u65f6\u95f4\u5c31\u5750\u5728\u7901\u77f3\u4e0a\u620f\u6c34\u770b\u7740\u5f20\u606a\u7b28\u62d9\u7684\u7ec3\u4e60\u51b2\u6d6a\u3002</p><p>\u3000\u3000\u9ec4\u660f\u65e5\u843d\u65f6\u5206\uff0c\u675c\u98de\u3001\u76db\u590f\u3001\u53f6\u5efa\u658c\u7b49\u4eba\u4e5f\u76f8\u805a\u8d76\u4e86\u8fc7\u6765\uff0c\u8fde\u5c3c\u514b\u674e\u68ee\u8ddf\u4ed6\u7684\u4e24\u540d\u52a9\u624b\u4e5f\u8ddf\u7740\u8fc7\u6765\u4f11\u95f2\uff0c\u5b59\u5c1a\u4e49\u592b\u5987\u3001\u52a0\u4e0a\u845b\u660e\u5fb7\u592b\u5987\u4e0e\u4ed6\u4eec\u817c\u8146\u7684\u53ea\u6562\u8eb2\u5728\u5988\u5988\u8eab\u540e\u4e0d\u8bf4\u8bdd\u7684\u513f\u5b50\uff0c\u665a\u4e0a\u8fc7\u6765\u5403\u70e7\u70e4\u7684\u4eba\u662f\u591f\u591a\u7684\u3002</p><p>\u3000\u3000\u53f0\u6e7e\u5973\u5b69\u5b50\u53eb\u67f3\u8317\uff0c\u8317\u8336\u7684\u8317\uff0c\u4e66\u9999\u6c14\u5f88\u91cd\u7684\u540d\u5b57\uff0c\u5979\u4e5f\u4e0d\u662f\u4e00\u4e2a\u4eba\u5728\u9999\u6e2f\u6e21\u5047\u3002\u6bcf\u6b21\u5047\u671f\u8981\u4e48\u662f\u4e0e\u670b\u53cb\u4e00\u8d77\u8fc7\u6765\uff0c\u8981\u4e48\u4e0e\u5bb6\u4eba\u4e00\u8d77\u8fc7\u6765\u2014\u2014\u6240\u8c13\u7684\u670b\u53cb\u4e5f\u662f\u4ed6\u7236\u4eb2\u540c\u4e8b\u7684\u5973\u513f\uff0c\u8bfb\u540c\u4e00\u6240\u5b66\u6821\uff0c\u5173\u7cfb\u5c31\u6bd4\u8f83\u4eb2\u8fd1\uff0c\u6709\u65f6\u5019\u4f1a\u4e00\u8d77\u5750\u98de\u673a\u53bb\u5927\u9646\uff0c\u5c31\u662f\u5f20\u606a\u4e0a\u56de\u5728\u98de\u673a\u4e0a\u770b\u5230\u90a3\u4e2a\u5706\u8138\u5973\u5b69\u5b50\u3002\u8fd9\u6b21\u662f\u5979\u7236\u4eb2\u5230\u9999\u6e2f\u6765\u51fa\u5dee\uff0c\u5979\u4e0e\u90a3\u4e2a\u5706\u8138\u5973\u5b69\u5b50\u5c31\u8ddf\u7740\u8fc7\u6765\u6e21\u5047\uff1b\u5979\u7236\u4eb2\u767d\u5929\u8981\u5de5\u4f5c\uff0c\u767d\u5929\u4e24\u4e2a\u5973\u5b69\u5b50\u5c31\u5728\u6d45\u6c34\u6e7e\u7684\u6d77\u6ee9\u95f2\u901b\u3002</p><p>\u3000\u3000\u51fa\u4e8e\u793c\u8c8c\uff0c\u665a\u4e0a\u7684\u70e7\u70e4\u81ea\u7136\u9080\u8bf7\u67f3\u8317\u4e0e\u5979\u7684\u670b\u53cb\u8fd8\u662f\u5979\u7684\u7236\u4eb2\u4e00\u8d77\u53c2\u52a0\uff0c\u67f3\u8317\u4e0e\u5979\u670b\u53cb\u2014\u2014\u90a3\u4e2a\u8138\u6709\u4e9b\u80d6\u3001\u76ae\u80a4\u6709\u4e9b\u9ed1\u7684\u5973\u5b69\u5b50\u56de\u5f71\u6e7e\u56ed\u6d17\u6fa1\u6362\u8863\u670d\uff1b\u5f20\u606a\u8fd8\u7279\u610f\u62c9\u7740\u675c\u98de\u3001\u5085\u4fca\u51c6\u5907\u4e86\u4e00\u5806\u8425\u706b\u51c6\u5907\u591c\u91cc\u6765\u4e2a\u771f\u6b63\u7684\u91ce\u708a\u3002</p><p>\u3000\u3000\u5f20\u606a\u518d\u5ea6\u7ed9\u5b59\u9759\u6aac\u5dee\u9063\u5230\u5c0f\u5356\u90e8\u642c\u5564\u9152\u65f6\uff0c\u4ed6\u62c9\u7740\u675c\u98de\u8d70\u5411\u5c0f\u5356\u90e8\uff0c\u770b\u5230\u8fdc\u5904\u7684\u8def\u706f\u4e0b\u505c\u7740\u4e00\u8f86\u5954\u9a70\uff0c\u67f3\u8317\u8fd8\u662f\u5979\u7684\u670b\u53cb\u8ddf\u4e00\u4e2a\u4e2d\u5e74\u4eba\u7ad9\u5728\u4e00\u8d77\u8bf4\u8bdd\uff0c\u5e94\u8be5\u662f\u67f3\u8317\u7684\u7236\u4eb2\u5427\u3002</p><p>\u3000\u3000\u67f3\u8317\u770b\u89c1\u4e86\u5f20\u606a\u62db\u624b\u8ba9\u4ed6\u8fc7\u53bb\uff0c\u5f20\u606a\u5c31\u8dd1\u8fc7\u53bb\u6253\u62db\u547c\u3002</p><p>\u3000\u3000\u67f3\u8317\u7684\u7236\u4eb2\u662f\u4e2a\u7626\u9ad8\u4e2a\u5b50\u7684\u4e2d\u5e74\u4eba\uff0c\u4e24\u9b13\u7684\u5934\u53d1\u90fd\u5df2\u7ecf\u82b1\u767d\uff0c\u6234\u7740\u949b\u91d1\u534a\u6846\u7684\u6811\u8102\u773c\u955c\uff0c\u4e00\u770b\u5c31\u77e5\u9053\u52b3\u5fc3\u7684\u77e5\u8bc6\u5206\u5b50\uff0c\u6709\u7740\u5f88\u660e\u663e\u7684\u53f0\u6e7e\u4eba\u7684\u6c14\u8d28\uff0c\u8ddf\u7f57\u5927\u4f51\u957f\u5f97\u6709\u51e0\u5206\u76f8\u8096\uff0c\u6709\u4e9b\u719f\u6089\uff0c\u5fc3\u60f3\u5e94\u8be5\u662f\u53f0\u6e7e\u7684\u67d0\u5bcc\u8c6a\u7ea7\u4eba\u7269\u5427\uff0c\u51fa\u5dee\u52a0\u6e21\u5047\u4f4f\u8fdb\u5f71\u6e7e\u56ed\u9152\u5e97\u516c\u5bd3\u7684\u4eba\u90fd\u662f\u975e\u5bcc\u5373\u8d35\u7684\u5bb6\u4f19\u3002</p><p>\u3000\u3000\u67f3\u8317\u7684\u7236\u4eb2\u665a\u4e0a\u8fd8\u6709\u5e94\u916c\u8131\u4e0d\u5f00\u8eab\uff0c\u4ed6\u53ea\u628a\u5f20\u606a\u5f53\u6210\u5973\u513f\u5728\u9999\u6e2f\u8ba4\u8bc6\u7684\u73a9\u4f34\uff0c\u8b66\u60d5\u7684\u770b\u4e86\u5f20\u606a\u51e0\u773c\uff0c\u53ef\u80fd\u89c9\u5f97\u5f20\u606a\u8fd8\u86ee\u987a\u773c\uff0c\u624d\u4eb2\u5207\u5429\u5490\u4ed6\u4eec\u665a\u4e0a\u73a9\u5f97\u5f00\u5fc3\u4e00\u4e9b\uff0c\u8bf4\u5b8c\u5c31\u94bb\u8fdb\u8f66\u91cc\u79bb\u5f00\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u771f\u89c9\u67f3\u94ed\u7684\u7236\u4eb2\u5f88\u9762\u719f\uff0c\u4e0d\u662f\u56e0\u4e3a\u8ddf\u7f57\u5927\u4f30\u957f\u5f97\u50cf\uff0c\u73b0\u5b9e\u4e2d\u4e0d\u8ba4\u8bc6\uff0c\u5e94\u8be5\u4ece\u54ea\u672c\u8d22\u7ecf\u6742\u5fd7\u4e0a\u770b\u5230\u8fc7\uff0c\u770b\u7740\u5954\u9a70\u8f66\u8fdc\u884c\uff0c\u5e26\u7740\u67f3\u8317\u4e0e\u5979\u90a3\u4e2a\u8138\u6709\u4e9b\u5c0f\u80a5\u3001\u76ae\u80a4\u53c8\u6709\u4e9b\u9ed1\u7684\u670b\u53cb\u4e00\u8d77\u53bb\u642c\u5564\u9152\u56de\u70e7\u70e4\u533a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u675c\u98de\u4e00\u4eba\u642c\u4e00\u7bb1\uff0c\u67f3\u8317\u4e0e\u5979\u670b\u53cb\u4e24\u4eba\u642c\u4e00\u7bb1\uff0c\u653e\u4e0b\u6765\u65f6\uff0c\u5f20\u606a\u968f\u610f\u7684\u95ee\u4e86\u4e00\u58f0\uff1a\u201c\u67f3\u8317\uff0c\u4f60\u7238\u662f\u8c01\uff0c\u770b\u4e0a\u53bb\u5f88\u9762\u719f\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u771f\u7684\u5417\uff1f\u201d\u67f3\u8317\u6beb\u65e0\u5fc3\u673a\u7684\u7b11\u4e86\u7b11\uff0c\u6b63\u8981\u8bf4\u51fa\u6765\uff0c\u5374\u7ed9\u5979\u670b\u53cb\u62c9\u4e86\u4e00\u4e0b\u8863\u895f\u3002</p><p>\u3000\u3000\u5f20\u606a\u54d1\u7136\u5931\u7b11\uff0c\u5728\u573a\u6709\u54ea\u4e2a\u8eab\u5bb6\u4e0d\u662f\u4e0a\u4ebf\u7684\uff0c\u5c31\u8fde\u5c3c\u514b\u674e\u68ee\u90fd\u6709\u5927\u628a\u82b1\u4e0d\u5b8c\u7684\u7092\u7968\uff0c\u6ca1\u6709\u4eba\u6253\u5c0f\u59d1\u5a18\u7684\u4e3b\u610f\u3002</p><p>\u3000\u3000\u67f3\u8317\u7709\u5934\u5fae\u5fae\u4e00\u76b1\uff0c\u6709\u4e9b\u53cd\u611f\u5979\u670b\u53cb\u8fd9\u4e48\u505a\uff0c\u5f88\u4f24\u4eba\u7684\u5fc3\uff0c\u53ea\u662f\u5f20\u606a\u5df2\u7ecf\u8f6c\u8fc7\u8eab\u53bb\u5c06\u51b0\u9547\u5564\u9152\u5206\u53d1\u7ed9\u522b\u4eba\uff0c\u5979\u4e5f\u6ca1\u529e\u6cd5\u89e3\u91ca\u4ec0\u4e48\uff0c\u5fc3\u91cc\u5374\u6709\u4e9b\u4e0d\u5b89\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u67f3\u5fd7\u6210\u2026\u2026\u201d\u5f20\u606a\u604d\u7136\u60f3\u8d77\u6765\uff0c\u8f6c\u8fc7\u5934\u5bf9\u67f3\u8317\u8bf4\u9053\uff0c\u201c\u4f60\u7238\u7238\u662f\u67f3\u5fd7\u6210\uff0c\u6211\u60f3\u8d77\u6765\uff0c\u4e94\u6708\u5e95\u90a3\u671f\u300aT3\u300b\u7684\u5c01\u9762\u4eba\u7269\u5c31\u662f\u4f60\u7238\uff0c\u6211\u8bf4\u600e\u4e48\u89c9\u5f97\u8138\u719f\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u67f3\u5fd7\u6210\uff1f\u54ea\u4e2a\u67f3\u5fd7\u6210\uff1f\u201d\u5b59\u5c1a\u4e49\u5c31\u7ad9\u5728\u5f20\u606a\u65c1\u8fb9\uff0c\u7a81\u7136\u542c\u5f20\u606a\u5410\u9732\u4e00\u4e2a\u540d\u5b57\uff0c\u4e0b\u610f\u8bc6\u7684\u89c9\u5f97\u719f\u6089\uff0c\u89c1\u5f20\u606a\u53c8\u56de\u5934\u8ddf\u4ed6\u4eec\u5728\u6d77\u6ee9\u8ba4\u8bc6\u7684\u4e24\u4e2a\u5973\u5b69\u5b50\u8bf4\u8bdd\uff0c\u624d\u77e5\u9053\u5f20\u606a\u662f\u8bf4\u5176\u4e2d\u4e00\u4e2a\u5973\u5b69\u5b50\u7684\u7236\u4eb2\u53eb\u67f3\u5fd7\u6210\uff0c\u5f71\u6e7e\u56ed\u662f\u9999\u6e2f\u6709\u540d\u7684\u9ad8\u7ea7\u9152\u5e97\u516c\u5bd3\uff0c\u79df\u4f4f\u8d39\u7528\u4e0d\u83f2\uff0c\u5973\u5b69\u5b50\u4ece\u53f0\u6e7e\u8fc7\u6765\u6e21\u5047\u7684\uff0c\u975e\u5bcc\u5373\u8d35\uff0c\u9876\u4e86\u9876\u5f20\u606a\u8170\uff0c\u5c0f\u58f0\u95ee\u4ed6\uff0c\u201c\u8fd9\u5973\u5b69\u5b50\u7684\u7236\u4eb2\u662f\u53f0\u79ef\u7535\u7684\u8fd0\u8425\u6267\u884c\u957f\u67f3\u5fd7\u6210\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u77e5\u9053\u6211\u7238\u7238\uff1f\u201d\u67f3\u8317\u96be\u514d\u6709\u4e9b\u5f97\u610f\u7684\u95ee\u5f20\u606a\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6211\u4eec\u8fd9\u91cc\u4e0d\u77e5\u9053\u7684\u8fd8\u771f\u4e0d\u591a\u2026\u2026\u201d\u5f20\u606a\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u6210\u4e0e\u53f0\u79ef\u7535\u8463\u4e8b\u957f\u3001CEO\u5f20\u5fe0\u8c0b\u7b49\u4eba\u5171\u540c\u521b\u7acb\u7684\u53f0\u79ef\u7535\u662f\u5168\u7403\u7b2c\u4e00\u5bb6\u4e13\u4e1a\u7684\u96c6\u6210\u7535\u8def\u5236\u9020\u670d\u52a1\u516c\u53f8\uff0c\u4e5f\u662f\u6700\u5927\u7684\u6676\u5706\u4ee3\u5de5\u516c\u53f8\uff0c\u4e3a\u53f0\u6e7e\u7535\u5b50\u5de5\u4e1a\u7684\u9ad8\u901f\u53d1\u5c55\u5960\u5b9a\u57fa\u7840\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u6210\u4f5c\u4e3a\u9ad8\u7ea7\u5408\u4f19\u4eba\u4e4b\u4e00\uff0c\u81ea\u7136\u4e5f\u7b97\u662f\u65b0\u751f\u4ee3\u7684\u5bcc\u8c6a\uff0c\u4e0d\u8fc7\u53f0\u79ef\u7535\u5f88\u591a\u4eba\u90fd\u7ed9\u5f20\u5fe0\u8c0b\u7684\u5149\u8292\u76d6\u4f4f\uff0c\u4f46\u662f\u67f3\u5fd7\u6210\u5728\u4e1a\u5185\u7684\u540d\u6c14\u8fd8\u662f\u6709\u4e9b\u540d\u6c14\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u81ea\u5df1\u4e5f\u5e94\u8be5\u7b97\u4e1a\u5185\u4eba\u58eb\u5427\uff0c\u5927\u89c4\u6a21\u96c6\u6210\u7535\u8def\uff08\u6676\u5706\uff09\u5236\u9020\u662f\u73b0\u4ee3\u7535\u5b50\u5de5\u4e1a\u7684\u57fa\u7840\uff0c\u53ea\u662f\u8fd9\u79cd\u8d44\u4ea7\u5bc6\u96c6\u6027\u3001\u6280\u672f\u5bc6\u96c6\u6027\u7684\u5927\u4ea7\u4e1a\uff0c\u4ed6\u624b\u91cc\u5c31\u7b97\u634f\u4e86\u56db\u5341\u4ebf\uff0c\u90fd\u672a\u5fc5\u80fd\u6709\u628a\u63e1\u73a9\u8f6c\uff1b\u6b63\u56e0\u4e3a\u73a9\u4e0d\u8f6c\uff0c\u4ed6\u624d\u803f\u803f\u4e8e\u6000\u3002\u5bf9\u53f0\u79ef\u7535\u3001\u4e16\u5927\u4ee5\u53ca\u65b0\u52a0\u5761\u7684\u51e0\u5bb6\u6676\u5706\u5382\u7684\u8d44\u6599\uff0c\u4ed6\u90fd\u70c2\u719f\u4e8e\u5fc3\u4e86\uff0c\u4e0d\u8981\u8bf4\u53f0\u79ef\u7535\u7684\u8fd0\u8425\u6267\u884c\u957f\uff0c\u5c31\u7b97\u6bd4\u8f83\u51fa\u540d\u7684\u9ad8\u7ea7\u5de5\u7a0b\u5e08\uff0c\u4ed6\u624b\u91cc\u90fd\u6709\u76f8\u5f53\u9f50\u7684\u8d44\u6599\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u77e5\u9053\u53f0\u79ef\u7535\u4e5d\u4e5d\u5e74\u6536\u8d2d\u4e16\u5927\u79ef\u7535\u4f53\u65f6\u5185\u90e8\u6709\u4e9b\u52a8\u8361\uff0c\u5bfc\u81f4\u591a\u540d\u9ad8\u7ea7\u7ba1\u7406\u4eba\u5458\u79bb\u5f00\uff0c\u67f3\u5fd7\u6210\u4fbf\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u4e0d\u8fc7\u67f3\u5fd7\u6210\u5e76\u975e\u53f0\u79ef\u4f53\u6700\u8000\u773c\u7684\u4eba\u7269\uff0c\u5f20\u606a\u5012\u662f\u4e5f\u6ca1\u6709\u8ffd\u8e2a\u5a92\u4f53\u5bf9\u4ed6\u7684\u62a5\u9053\uff0c\u4e5f\u4e0d\u6653\u5f97\u4ed6\u6700\u7ec8\u7684\u53bb\u5411\uff0c\u6216\u8bb8\u540e\u6765\u7ed9\u5f20\u6c5d\u4eac\u62c9\u53bb\u4e00\u540c\u521b\u7acb\u4e2d\u82af\u56fd\u9645\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u3001\u8096\u664b\u6210\u3001\u6210\u6c49\u748b\u7b49\u4eba\u5bf9\u67f3\u5fd7\u6210\u90fd\u4e0d\u4f1a\u964c\u751f\uff0c\u9648\u4fe1\u751f\u751a\u81f3\u4e0e\u67f3\u5fd7\u6210\u5728\u5fb7\u5dde\u4eea\u5668\u6709\u77ed\u6682\u7684\u5171\u4e8b\u7ecf\u5386\uff0c\u53ea\u4e0d\u8fc7\u9648\u4fe1\u751f\u521a\u521a\u8fdb\u5fb7\u5dde\u4eea\u5668\u4e0d\u4e45\uff0c\u67f3\u5fd7\u6210\u5c31\u968f\u5f20\u5fe0\u8c0b\u7b49\u4eba\u4e00\u8d77\u79bb\u5f00\u5fb7\u5dde\u4eea\u5668\u56de\u53f0\u6e7e\u6765\u641e\u53f0\u79ef\u7535\u4f53\u4e86\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u65e9\u5e74\u62c5\u4efb\u5609\u5174\u7535\u5b50\u884c\u653f\u603b\u88c1\u65f6\u8fd8\u4e0e\u67f3\u5fd7\u6210\u6253\u8fc7\u4ea4\u9053\uff0c\u53ea\u662f\u5f88\u5947\u602a\u4f5c\u4e3a\u53f0\u79ef\u7535\u7684\u8fd0\u8425\u6267\u884c\u957f\uff0c\u67f3\u5fd7\u6210\u8fd9\u65f6\u5019\u5230\u9999\u6e2f\u6765\u505a\u4ec0\u4e48\uff1f</p></article>"}}}]);
