(window.webpackJsonp=window.webpackJsonp||[]).push([[604],{627:function(p){p.exports={chapterTitle:null,title:"\u7b2c455\u7ae0 \u8ba9\u4f60\u4eec\u5598\u53e3\u6c14",content:"<article><h2>\u7b2c455\u7ae0 \u8ba9\u4f60\u4eec\u5598\u53e3\u6c14</h2><p>\u3000\u300011\u670826\u65e5\uff0c\u7231\u8fbe\u7535\u5b50\u5728\u57ce\u5357\u533a\u7535\u5b50\u5de5\u4e1a\u56ed\u5185\u53ec\u5f00\u65b0\u95fb\u53d1\u5e03\u4f1a\uff0c\u82cf\u6d25\u4e1c\u4ee3\u8868\u7231\u8fbe\u7535\u5b50\u5411\u5a92\u4f53\u516c\u5e03\u4e0eTI\u5408\u8d44\u5efa\u8bbe\u673a\u82af\u5de5\u5382\u7684\u4e00\u7cfb\u5217\u6d88\u606f\uff0c\u5408\u8d44\u5de5\u5382\u6240\u9700\u8981\u7684\u5355\u789f\u673a\u82af\u5236\u9020\u6280\u672f\uff0c\u9664\u4e86\u98de\u5229\u6d66\u8f6c\u8ba9\u7684\u90e8\u5206\u4e13\u5229\u6280\u672f\u4e4b\u5916\uff0c\u5176\u4f59\u5168\u90e8\u6765\u81ea\u4e8e\u7231\u8fbe\u4e0eTI\u516c\u53f8\u5728\u4e00\u5e74\u524d\u5408\u8d44\u6210\u7acb\u7684\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\uff0c\u5305\u62ec\u6fc0\u5149\u5438\u53d6\u5934\u5728\u5185\u7684\u6240\u6709\u5236\u9020\u6280\u672f\uff0c\u4eca\u540e\u76f8\u5173\u673a\u82af\u6280\u672f\u7684\u7814\u53d1\u4e5f\u5c06\u5168\u6743\u59d4\u6258\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\uff0c\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\u9996\u5e2d\u4e3b\u4efb\u5de5\u7a0b\u5e08\u5c06\u540c\u65f6\u62c5\u4efb\u673a\u82af\u5de5\u5382\u603b\u7ecf\u7406\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u3001\u5f20\u4e9a\u5e73\u5206\u522b\u4ee3\u8868TI\u4e0e\u98de\u5229\u6d66\u53d1\u8868\u5408\u4f5c\u58f0\u660e\uff0c\u65af\u9ad8\u67cf\u516c\u53f8\u4ee3\u8868\u827e\u9ed8\u4e5f\u5ba3\u79f0\u8981\u4e0e\u7231\u8fbe\u7535\u5b50\u52a0\u5f3a\u5728\u789f\u673a\u5e94\u7528\u6280\u672f\u7814\u53d1\u4e0a\u7684\u5408\u4f5c\u3002</p><p>\u3000\u3000\u7ecf\u82cf\u6d25\u4e1c\u7b49\u4eba\u7684\u5982\u6b64\u89e3\u91ca\uff0c\u7eb7\u62e5\u800c\u6765\u7684\u5a92\u4f53\u8bb0\u8005\u3001\u7ecf\u9500\u5546\u3001\u540c\u4e1a\u5382\u5546\u4ee3\u8868\u4ee5\u53ca\u7d22\u5c3c\u516c\u53f8\u7684\u4ee3\u8868\u6e21\u8fb9\u656c\u4e00\u7b49\u4eba\u5fc3\u91cc\u7684\u7591\u60d1\u624d\u7a0d\u7a0d\u5f97\u5230\u89e3\u7b54\uff1a\u7231\u8fbe\u7535\u5b50\u65e9\u5c31\u5728\u7b79\u5907\u673a\u82af\u5de5\u5382\u7684\u4e8b\u60c5\uff0c\u6fc0\u5149\u8bfb\u53d6\u5934\u7b49\u6838\u5fc3\u6280\u672f\u8fd8\u662f\u6765\u81ea\u4e8e\u7231\u8fbe\u4e0eTI\u5408\u8d44\u8bbe\u7acb\u7684\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\uff1b\u4e88\u4ee5\u6b64\u65f6\u516c\u5e03\uff0c\u5c31\u8981\u56de\u5e94\u79d1\u738b\u593a\u5f97\u592e\u89c6\u5e7f\u544a\u6807\u738b\u4e4b\u540e\u53ef\u80fd\u5bf9\u7231\u8fbe\u7535\u5b50\u7684\u51b2\u51fb\u3002</p><p>\u3000\u3000\u4e5f\u80fd\u63a8\u60f3\u51fa\uff0c\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\u7684\u4e3b\u8981\u6280\u672f\u5b9e\u9645\u4e5f\u5e94\u8be5\u662f\u6765\u81ea\u4e8eTI\u516c\u53f8\uff0c\u81f3\u4e8eTI\u516c\u53f8\u4e3a\u4ec0\u4e48\u613f\u610f\u5c06\u9ad8\u529f\u7387\u6fc0\u5149\u5e94\u7528\u65b9\u9762\u7684\u6838\u5fc3\u6280\u672f\u62ff\u51fa\u6765\uff0c\u4f17\u4eba\u5fc3\u91cc\u8fd8\u6709\u4e9b\u7591\u95ee\uff0c\u4f46\u662f\u4e5f\u65e0\u5173\u7d27\u8981\u4e86\uff0c\u6216\u8bb8TI\u6ca1\u6709\u628a\u63e1\u4e0e\u98de\u5229\u6d66\u3001\u7d22\u5c3c\u76f4\u63a5\u7ade\u4e89\uff0c\u800c\u4e0e\u7231\u8fbe\u5408\u8d44\uff0c\u5c31\u53ef\u4ee5\u76f4\u63a5\u5206\u4eab\u4e00\u5927\u5757\u7684\u673a\u82af\u5e02\u573a\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u4e5f\u6709\u4eba\u6ce8\u610f\u5230\uff0c\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\uff0c\u7231\u8fbe\u4e0eTI\u5404\u6301\u534a\u6570\u80a1\u4efd\uff0c\u8c01\u4e5f\u4e0d\u5bf9\u7814\u7a76\u9662\u63a7\u80a1\uff0c\u7531\u4e8e\u98de\u5229\u6d66\u5c06\u4e00\u90e8\u5206\u4e13\u5229\u6280\u672f\u8f6c\u8ba9\u7ed9\u7231\u8fbe\u7535\u5b50\uff0c\u6700\u7ec8\u9020\u6210\u7231\u8fbe\u4e0eTI\u5408\u8d44\u6210\u7acb\u673a\u82af\u5de5\u5382\u65f6\u63a7\u80a1\u3002</p><p>\u3000\u3000\u673a\u82af\u5de5\u5382\u6240\u9700\u8981\u7684\u751f\u4ea7\u8bbe\u5907\u5206\u522b\u5411TI\u4e0e\u98de\u5229\u6d66\u4e24\u5bb6\u516c\u53f8\u5f15\u8fdb\uff0c\u56db\u5341\u591a\u540d\u751f\u4ea7\u7ba1\u7406\u4eba\u5458\u4e5f\u5c06\u7531\u98de\u5229\u6d66\u4e0a\u6d77\u5de5\u5382\u8d1f\u8d23\u5206\u9636\u6bb5\u57f9\u8bad\u4e24\u4e2a\u6708\u7684\u65f6\u95f4\u3002</p><p>\u3000\u3000\u98de\u5229\u6d66\u4e0d\u4e3b\u52a8\u538b\u5236\u7231\u8fbe\u7535\u5b50\uff0c\u53cd\u800c\u4e00\u4ed8\u79ef\u6781\u652f\u6301\u914d\u5408\u7684\u59ff\u6001\uff0c\u8ba9\u4eba\u7591\u60d1\u4e0d\u89e3\uff0c\u7231\u8fbe\u7535\u5b50\u80fd\u505a\u5230\u8fd9\u4e00\u6b65\uff0c\u80cc\u540e\u7684\u6545\u4e8b\u4e00\u6837\u8010\u4eba\u5bfb\u5473\uff0c\u8d8a\u60f3\u8d8a\u8010\u4eba\u5bfb\u5473\uff0c\u5a92\u4f53\u8bb0\u8005\u4e5f\u524a\u5c16\u4e86\u8111\u888b\u60f3\u6316\u6398\u5e55\u540e\u7684\u6545\u4e8b\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u9762\u5bf9\u5a92\u4f53\u7684\u653b\u52bf\uff0c\u4e00\u6982\u8868\u793a\u6ca1\u6709\u66f4\u591a\u7684\u5e55\u540e\u6545\u4e8b\uff1b\u4f46\u662f\u4ed6\u7684\u8bdd\u8fde\u4e09\u5c81\u5c0f\u5b69\u90fd\u4e0d\u4f1a\u4fe1\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u6837\uff0c\u5a92\u4f53\u3001\u7ecf\u9500\u5546\u4e0e\u5f71\u789f\u673a\u5382\u5546\u4ee3\u8868\u90fd\u8ba4\u8bc6\u5230\u7231\u8fbe\u7535\u5b50\u5728\u5f71\u789f\u673a\u4ea7\u4e1a\u7684\u5730\u4f4d\u5df2\u7ecf\u8fdc\u8fdc\u8d85\u8d8a\u540c\u884c\u4e1a\u7684\u5176\u4ed6\u5382\u5546\uff0c\u5f53\u4e4b\u65e0\u69d0\u662f\u789f\u673a\u4ea7\u4e1a\u7684\u738b\u8005\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u5730\u4f4d\u5e76\u4e0d\u662f\u8bf4\u7231\u8fbe\u7535\u5b50\u73b0\u5728\u8fd8\u5360\u636e\u7740\u54c1\u724c\u789f\u673a\u5e02\u573a40\uff05\u7684\u4efd\u989d\uff0c\u5e02\u573a\u5f62\u52bf\u7684\u53d8\u5316\u65e0\u5e38\u53c8\u8fc7\u4e8e\u8fc5\u6377\uff0c\u800c\u7231\u8fbe\u7535\u5b50\u6839\u672c\u6027\u7684\u4ea7\u4e1a\u4f18\u52bf\u4e0e\u6280\u672f\u4f18\u52bf\u5f62\u6210\u6700\u7ec8\u7684\u6210\u672c\u4f18\u52bf\uff0c\u4ee5\u53ca\u4e0e\u4e0a\u6e38\u5784\u65ad\u6027\u8d28\u7684\u4f9b\u5e94\u5546\u8be1\u5f02\u7684\u4eb2\u5bc6\u5173\u7cfb\uff0c\u624d\u771f\u6b63\u8ba9\u540c\u884c\u5f71\u789f\u673a\u7ec4\u88c5\u5382\u5546\u6218\u6218\u5162\u5162\uff0c\u5fc3\u4e2d\u90fd\u4e0d\u6562\u751f\u51fa\u6311\u6218\u7231\u8fbe\u7535\u5b50\u5e02\u573a\u5730\u4f4d\u7684\u5984\u60f3\u3002</p><p>\u3000\u3000\u79d1\u738b\u4e0d\u662f\u81ea\u4ee5\u4e3a\u62ff\u4e0b\u592e\u89c6\u5e7f\u544a\u6807\u738b\u5c31\u60f3\u8981\u6311\u6218\u7231\u8fbe\u7535\u5b50\u7684\u5730\u4f4d\u5417\uff1f\u8fd9\u4e00\u4e0b\u7b97\u662f\u4e00\u51fb\u54cd\u4eae\u7684\u8033\u5149\u5427\uff01\u5df2\u7ecf\u6709\u5a92\u4f53\u8bb0\u8005\u5728\u8bb0\u5f55\u672c\u5199\u4e0b\u201c\u79d1\u738b\u4e0e\u7231\u8fbe\u540c\u57ce\u7ade\u6280\uff0c\u592e\u89c6\u6807\u738b\u4f01\u4e1a\u88ab\u7529\u54cd\u4eae\u8033\u5149\u201d\u8fd9\u53e5\u8bdd\u51c6\u5907\u4f5c\u4e3a\u4eca\u5929\u4e13\u9898\u62a5\u9053\u7684\u5927\u6807\u9898\u3002</p><p>\u3000\u3000\u81f3\u4e8e\u4e89\u62a2\u7231\u8fbe\u7535\u5b50\u7684\u6e20\u9053\u5546\u8d44\u6e90\uff0c\u90a3\u66f4\u662f\u5962\u60f3\u4e86\uff0c\u7231\u8fbe\u7535\u5b50\u4e5d\u516d\u5e74\u7ed9\u6574\u4e2a\u7ecf\u9500\u5546\u4f53\u7cfb\u81f3\u5c11\u5e26\u6765\u4e09\u4e2a\u4ebf\u7684\u5229\u6da6\u3002\u4e4b\u524d\u60f3\u62a2\u7231\u8fbe\u7535\u5b50\u7684\u6e20\u9053\u5546\uff0c\u662f\u9884\u6d4b\u7231\u8fbe\u7535\u5b50\u5728\u5931\u53bb\u592e\u89c6\u5e7f\u544a\u6807\u738b\u4e4b\u540e\u5176\u5e02\u573a\u53ef\u80fd\u4f1a\u5927\u5e45\u5ea6\u7684\u840e\u7f29\uff0c\u8fd9\u79cd\u9884\u6d4b\u5df2\u7ecf\u6ca1\u6709\u5b58\u5728\u7684\u73b0\u5b9e\u57fa\u7840\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u7ecf\u9500\u5546\u4f1a\u653e\u5f00\u7231\u8fbe\u7535\u5b50\u7684\u7c97\u5927\u817f\uff1f\u9664\u975e\u4ed6\u4eec\u770b\u4e0d\u6e05\u5f62\u52bf\u3002</p><p>\u3000\u3000\u73b0\u5728\uff0c\u540c\u4e1a\u5f71\u789f\u673a\u7ec4\u88c5\u5382\u5546\u5c31\u6015\u7231\u8fbe\u7535\u5b50\u5229\u7528\u79cd\u79cd\u4f18\u52bf\u75af\u72c2\u7684\u8fdb\u884c\u5e02\u573a\u6269\u5f20\uff0c\u53ea\u6015\u8c01\u4e5f\u963b\u6321\u4e0d\u4f4f\u7231\u8fbe\u7535\u5b50\u7684\u5f3a\u52bf\u653b\u51fb\uff0c\u5728\u65b0\u95fb\u53d1\u5e03\u4f1a\u4e0a\uff0c\u5c31\u6709\u8bb0\u8005\u76f4\u63a5\u63d0\u51fa\u540c\u4e1a\u8005\u7684\u62c5\u5fe7\uff1a\u201c\u5404\u5927\u5a92\u4f53\u90fd\u5728\u9884\u6d4b\u7231\u8fbe\u7535\u5b50\u4e5d\u516d\u5e74\u80fd\u62ff\u4e0b\u54c1\u724c\u789f\u673a\u5e02\u573a40\uff05\u7684\u4efd\u989d\uff0c\u8bf7\u95ee\u7231\u8fbe\u7535\u5b50\u4e5d\u4e03\u5e74\u7684\u76ee\u6807\u662f\u591a\u5c11\uff0c\u662f\u4fdd\u4f4f\u773c\u4e0b\u7684\u5e02\u573a\u4efd\u989d\uff0c\u8fd8\u662f\u60f3\u62ff\u5230\u66f4\u591a\uff1f\u201d</p><p>\u3000\u3000\u6768\u4e91\u4ee3\u8868\u7231\u8fbe\u7535\u5b50\u5ba3\u5e03\u4e0b\u4e00\u5e74\u5ea6\u7684\u5e02\u573a\u76ee\u6807\uff1a\u201c\u6211\u4eec\u770b\u5230\u79d1\u738b\u3001\u6b65\u6b65\u9ad8\u3001\u7231\u591a\u3001\u91d1\u6b63\u7b49\u540c\u4e1a\u5382\u5546\u5728\u8fc5\u901f\u7684\u6210\u957f\uff0c\u65b0\u79d1\u7535\u5b50\u5728\u789f\u673a\u7cfb\u7edf\u63a7\u5236\u65b9\u9762\u7684\u6df1\u5165\u7814\u7a76\u540c\u6837\u8ba9\u4eba\u656c\u4f69\uff0c\u9762\u5bf9\u4f17\u591a\u4f18\u79c0\u540c\u4e1a\u5382\u5546\u7684\u524d\u56f4\u540e\u5835\uff0c\u6211\u4eec\u7231\u8fbe\u7535\u5b50\u5185\u90e8\u4e5f\u5728\u8ba8\u8bba\uff0c\u660e\u5e74\u80fd\u4fdd\u4f4f30\uff05\u7684\u5e02\u573a\u4efd\u989d\u5c31\u7b97\u662f\u4e0d\u9519\u7684\u6210\u7ee9\u2026\u2026\u201d</p><p>\u3000\u3000\u770b\u7740\u6768\u4e91\u7ed9\u957f\u77ed\u955c\u5934\u3001\u8bdd\u7b52\u3001\u5f55\u97f3\u7b14\u5305\u56f4\u7684\u6a21\u6837\uff0c\u5218\u660e\u8f89\u5fc3\u91cc\u6781\u597d\u4e0d\u53d7\uff0c\u4ed6\u751a\u81f3\u6ca1\u6709\u52c7\u6c14\u7ad9\u5230\u7a0d\u524d\u4e00\u4e9b\u7684\u4f4d\u7f6e\uff0c\u5c31\u6015\u9047\u5230\u5728\u7231\u8fbe\u7684\u719f\u4eba\u3002</p><p>\u3000\u3000\u6768\u4e91\u4ee3\u8868\u7231\u8fbe\u7535\u5b50\u516c\u5e03\u4e0b\u4e00\u5e74\u5ea6\u7684\u5e02\u573a\u76ee\u6807\uff0c\u5bf9\u79d1\u738b\u6765\u8bf4\uff0c\u65e0\u7591\u662f\u4e00\u4e2a\u80fd\u8ba9\u4eba\u677e\u53e3\u6c14\u7684\u597d\u6d88\u606f\u3002\u79d1\u738b\u62ff\u4e0b\u592e\u89c6\u6807\u738b\u5e7f\u544a\uff0c\u4f46\u662f\u4e5f\u62b5\u6321\u4e0d\u4f4f\u7231\u8fbe\u5728\u6210\u672c\u4e0a\u7684\u5de8\u5927\u4f18\u52bf\uff0c\u66f4\u4f55\u51b5\uff0c\u9664\u4e86\u4e2d\u592e\u7535\u89c6\u53f0\uff0c\u5c31\u6ca1\u6709\u5176\u4ed6\u6295\u653e\u7684\u5e7f\u544a\u6e20\u9053\u4e86\uff1f\u5218\u660e\u8f89\u5728\u7231\u8fbe\u65f6\uff0c\u5c31\u542c\u5f20\u606a\u8bf4\u8fc7\u4e00\u6574\u5957\u5730\u6bef\u5f0f\u5c65\u76d6\u5730\u65b9\u7701\u5e02\u5a92\u4f53\u5168\u65b9\u9762\u7684\u5e7f\u544a\u8f70\u70b8\u7b56\u7565\u3002</p><p>\u3000\u3000\u7231\u8fbe\u5c06\u4e0b\u4e00\u5e74\u5ea6\u7684\u5e02\u573a\u76ee\u6807\u4e0b\u8c03\u523030\uff05\uff0c\u65e0\u7591\u7ed9\u4eca\u5e74\u5728\u4e2d\u592e\u7535\u89c6\u53f0\u6295\u5165\u5de8\u8d44\u62ff\u4e0b\u9ec4\u91d1\u65f6\u6bb5\u7535\u5b50\u699c\u524d\u4e09\u7684\u4f01\u4e1a\u4ee5\u5598\u6c14\u7684\u673a\u4f1a\uff0c\u79d1\u738b\u65e0\u7591\u4e5f\u662f\u53d7\u76ca\u8005\u3002\u4f46\u662f\uff0c\u6709\u4e00\u70b9\u662f\u80af\u5b9a\u7684\uff0c\u7231\u8fbe\u662f\u4e0d\u4f1a\u5141\u8bb8\u522b\u4eba\u76f4\u63a5\u4fb5\u72af\u4ed6\u4eec\u5e02\u573a\u5730\u4f4d\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9648\u9759\u3001\u5218\u660e\u8f89\u5c06\u65b0\u95fb\u53d1\u5e03\u4f1a\u73b0\u573a\u7684\u60c5\u51b5\u53ca\u65f6\u4f20\u56de\u6587\u5c71\u996d\u5e97\uff0c\u5728\u6587\u5c71\u996d\u5e97\u5efa\u4e8e\u6e56\u7554\u7684\u4e1c\u6c5f\u697c\u4e8c\u697c\uff0c\u8c22\u5251\u5357\u8fd8\u662f\u4f9d\u7740\u7a97\u53f0\u800c\u7ad9\uff0c\u6e56\u6c34\u53cd\u5c04\u6765\u7684\u5149\u6655\u5728\u7a97\u73bb\u7483\u4e0a\u6643\u52a8\u3002</p><p>\u3000\u3000\u201c\u5e02\u573a\u7ade\u4e89\u8fc7\u4e8e\u6b8b\u9177\u5bf9\u7231\u8fbe\u7535\u5b50\u81ea\u8eab\u4e5f\u6ca1\u6709\u592a\u5927\u7684\u597d\u5904\uff0c\u201d\u8c22\u6c49\u9756\u8ba4\u771f\u601d\u8003\u7231\u8fbe\u4e3b\u8981\u4e0b\u8c03\u660e\u5e74\u7684\u5e02\u573a\u76ee\u6807\u80cc\u540e\u7684\u6df1\u5c42\u6b21\u539f\u56e0\uff0c\u201c\u6d77\u5dde\u5e02\u91cc\u4e4b\u524d\u7edf\u4e00\u610f\u89c1\uff0c\u8fd8\u6709\u8d77\u4e00\u5b9a\u4f5c\u7528\u7684\uff1b\u5510\u5b66\u8c26\u8fd9\u4e2a\u4eba\uff0c\u6211\u770b\u8fd8\u662f\u5f88\u987e\u5168\u5927\u5c40\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u4e2a\u623f\u95f4\u91cc\u7684\u4eba\uff0c\u4e5f\u60f3\u4e0d\u5230\u66f4\u5408\u7406\u7684\u89e3\u91ca\u4e86\uff0c\u65e2\u7136\u7231\u8fbe\u7535\u5b50\u6709\u540e\u62db\uff0c\u5c06\u5218\u660e\u8f89\u4ee5\u53ca\u5341\u540d\u9ad8\u7ea7\u9500\u552e\u4e3b\u7ba1\u6316\u51fa\u6765\uff0c\u4e5f\u4e0d\u80fd\u7b97\u5f7b\u5e95\u6fc0\u6012\u5f20\u606a\u5427\u3002</p><p>\u3000\u3000\u53ea\u662f\u5218\u660e\u8f89\u8ddf\u8fd9\u5341\u540d\u9ad8\u7ea7\u9500\u552e\u4e3b\u7ba1\u5728\u79d1\u738b\u8fd8\u80fd\u8d77\u4ec0\u4e48\u4f5c\u7528\uff1f\u5f53\u521d\u53ef\u662f\u6307\u671b\u4ed6\u4eec\u80fd\u5c06\u7231\u8fbe\u7535\u5b50\u5728\u534e\u5357\u3001\u534e\u4e2d\u7684\u6e20\u9053\u5546\u7ed9\u76f4\u63a5\u62c9\u8fc7\u6765\u7684\u3002</p><p>\u3000\u3000\u8d75\u9526\u8363\u8138\u8272\u7a0d\u9701\uff0c\u5374\u8fd8\u6ca1\u6709\u592a\u591a\u7684\u8f7b\u677e\uff0c\u8c22\u6c49\u9756\u3001\u8c22\u6c49\u660e\u3001\u9648\u5bb6\u5584\u3001\u8c22\u77bb\u3001\u8c22\u5251\u5357\u7b49\u4eba\u8138\u4e0a\u90fd\u662f\u5982\u6b64\uff0c\u73b0\u5728\u5df2\u7ecf\u4e0d\u53ef\u80fd\u518d\u53bb\u62a2\u7231\u8fbe\u7535\u5b50\u7684\u6e20\u9053\u5546\uff0c\u4e5f\u62a2\u4e0d\u8fc7\u6765\uff0c\u90a3\u4e4b\u524d\u8bb8\u591a\u8ba1\u5212\u90fd\u9700\u8981\u8c03\u6574\u3002\u600e\u4e48\u8c03\u6574\uff0c\u600e\u4e48\u5efa\u7acb\u81ea\u5df1\u7684\u7ecf\u9500\u5546\u7f51\u7edc\uff0c\u6bd4\u7167\u7231\u8fbe\u4ece\u7ecf\u9500\u5546\u90a3\u91cc\u6536\u53d6\u540c\u7b49\u89c4\u683c\u7684\u4fdd\u8bc1\u91d1\u662f\u4e0d\u53ef\u80fd\u7684\uff0c\u5c06\u4fdd\u8bc1\u91d1\u6570\u989d\u8c03\u6574\u5230\u54ea\u4e2a\u7a0b\u5ea6\u5408\u9002\uff0c\u4eca\u5929\u7684\u5e02\u573a\u7b56\u7565\u8be5\u5982\u4f55\u907f\u514d\u4e0e\u7231\u8fbe\u7535\u5b50\u76f4\u63a5\u5bf9\u6297\uff0c\u7b49\u7b49\u8bf8\u5982\u6b64\u7c7b\u7684\u5185\u5bb9\uff0c\u4eca\u5929\u5c31\u8981\u8ba8\u8bba\u6e05\u695a\uff0c\u8fd8\u8981\u62ff\u51fa\u65b9\u6848\u6765\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u4eca\u5929\u5c31\u8981\u7ed3\u675f\uff0c\u660e\u5929\uff0c\u79d1\u738b\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u8fd8\u8981\u7ee7\u7eed\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u201c\u662f\u4e0d\u662f\u8ba9\u9648\u9759\u8ddf\u5218\u660e\u8f89\u5148\u56de\u6765\u2026\u2026\u201d\u8c22\u5251\u5357\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8ba9\u90a3\u4e2a\u6ca1\u7528\u7684\u5bb6\u4f19\u8fc7\u6765\u505a\u4ec0\u4e48\uff0c\u8fd8\u6307\u671b\u4ed6\u80fd\u7ed9\u4ec0\u4e48\u5e2e\u52a9\uff1f\u201d\u8d75\u9526\u8363\u4e0d\u5f85\u89c1\u5218\u660e\u8f89\uff0c\u201c\u8bf7\u9648\u5c0f\u59d0\u5148\u56de\u6765\uff0c\u8fd8\u6709\u8d75\u526f\u603b\u4e0e\u90c1\u7ecf\u7406\u4e00\u8d77\u53eb\u8fc7\u6765\u7814\u7a76\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u8fd8\u4e0d\u60f3\u5c31\u8fd9\u6837\u5c06\u5218\u660e\u8f89\u4e00\u811a\u8e22\u5f00\uff0c\u6bd5\u7adf\u5218\u660e\u8f89\u4e0e\u4ece\u7231\u8fbe\u4e00\u8d77\u8df3\u8fc7\u6765\u7684\u9500\u552e\u4e3b\u7ba1\u5728\u5b9e\u9645\u5de5\u4f5c\u4e2d\u6709\u5f88\u4e30\u5bcc\u7684\u7ecf\u9a8c\uff0c\u5df2\u7ecf\u5c06\u4e00\u5e74\u7684\u85aa\u6c34\u90fd\u5148\u5f00\u4e86\u51fa\u53bb\uff0c\u90a3\u8fd9\u4e00\u5e74\u5c31\u8981\u5c06\u4ed6\u4eec\u7528\u8db3\u4e86\uff0c\u4e0d\u8fc7\u4e5f\u4e0d\u60f3\u8fd9\u65f6\u5019\u9006\u8d75\u9526\u8363\u7684\u610f\u601d\uff0c\u5de5\u4f5c\u4e00\u76f4\u662f\u81ea\u5df1\u5728\u4e3b\u6301\uff0c\u51fa\u4e86\u8fd9\u4e48\u5927\u7684\u7eb0\u6f0f\uff0c\u8d75\u9526\u8363\u6ca1\u6709\u76f4\u63a5\u5c06\u6012\u529b\u6d12\u5230\u4ed6\u5934\u4e0a\uff0c\u5df2\u7ecf\u5f88\u7ed9\u9762\u5b50\u4e86\u3002</p><p>\u3000\u3000\u9648\u9759\u90a3\u8fb9\u63a5\u5230\u7535\u8bdd\u65f6\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u65b0\u95fb\u53d1\u5e03\u4f1a\u4e5f\u5df2\u7ecf\u7ed3\u675f\uff0c\u4e0b\u5348\u7231\u8fbe\u7535\u5b50\u4f1a\u53ec\u96c6\u7ecf\u9500\u5546\u5c01\u95ed\u5f00\u4f1a\uff0c\u8ba8\u538c\u4e0b\u4e00\u5e74\u5ea6\u7684\u4ee3\u7406\u6743\u4e0e\u4fdd\u8bc1\u91d1\u7684\u95ee\u9898\uff0c\u5df2\u7ecf\u6ca1\u6709\u60ac\u5ff5\u4e86\uff0c\u5a92\u4f53\u8bb0\u8005\u4f1a\u8ffd\u9010\u82cf\u6d25\u4e1c\u3001\u9648\u4fe1\u751f\u3001\u5f20\u4e9a\u5e73\u3001\u827e\u9ed8\u7b49\u4eba\uff0c\u79d1\u738b\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u53ea\u80fd\u5ef6\u540e\u5230\u660e\u5929\u624d\u4e3e\u884c\uff0c\u5bf9\u6b64\u4e5f\u8981\u53ec\u5f00\u4e00\u4e2a\u5c0f\u578b\u7684\u53d1\u5e03\u4f1a\u4e88\u4ee5\u516c\u544a\u3002</p><p>\u3000\u3000\u65e0\u8bba\u662f\u9648\u9759\u8fd8\u662f\u5218\u660e\u8f89\uff0c\u8fd9\u65f6\u5019\u90fd\u627e\u4e0d\u5230\u673a\u4f1a\u4e0e\u5f20\u606a\u63a5\u89e6\u7684\uff0c\u53ea\u80fd\u5148\u56de\u6587\u5c71\u996d\u5e97\u5546\u91cf\u5bf9\u7b56\u518d\u8bf4\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u88ab\u544a\u77e5\u8ba9\u4ed6\u7559\u5728\u524d\u697c\u5904\u7406\u5584\u540e\u7684\u4e8b\u60c5\uff0c\u534f\u52a9\u9152\u5e97\u91cd\u65b0\u5e03\u7f6e\u4f1a\u573a\uff0c\u96be\u8fc7\u5c31\u60f3\u54ed\u51fa\u6765\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u540c\u6837\u662f\u7ed9\u6392\u65a5\u5728\u79d1\u738b\u6838\u5fc3\u5c42\u4e4b\u5916\u4e86\u3002</p></article>"}}}]);
