(window.webpackJsonp=window.webpackJsonp||[]).push([[605],{628:function(p){p.exports={chapterTitle:null,title:"\u7b2c456\u7ae0 \u597d\u65f6\u673a",content:"<article><h2>\u7b2c456\u7ae0 \u597d\u65f6\u673a</h2><p>\u3000\u3000\u8003\u8651\u5230\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u5176\u4ed6\u5165\u9a7b\u4f01\u4e1a\u914d\u5957\u80fd\u529b\u5dee\uff0c\u7231\u8fbe\u7535\u5b50\u5728\u5efa\u5458\u5de5\u751f\u6d3b\u8f85\u52a9\u8bbe\u65bd\u65f6\uff0c\u589e\u52a0\u4e86\u8bbe\u8ba1\u4f59\u91cf\uff0c\u4e0d\u8fc7\uff0c\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u7684\u53d1\u5c55\u901f\u5ea6\u8d85\u4e4e\u60f3\u8c61\uff0c\u76ca\u9686\u9547\u7d27\u90bb\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u7684\u65b0\u533a\u89c4\u5212\u8fd8\u521a\u521a\u6572\u5b9a\uff0c\u4ec5\u9760\u7231\u8fbe\u7535\u5b50\u4e4b\u524d\u5efa\u8bbe\u7684\u751f\u6d3b\u8f85\u52a9\u8bbe\u65bd\u6765\u652f\u6491\u6574\u4e2a\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u7684\u540e\u52e4\u4f9b\u5e94\u5c31\u6709\u592a\u7d27\u8feb\u4e86\u3002</p><p>\u3000\u3000\u4e2d\u5348\u4f11\u606f\u65f6\u95f4\uff0c\u5458\u5de5\u9910\u5385\u6324\u6ee1\u4eba\uff0c\u8fd8\u6709\u66f4\u591a\u7684\u7528\u9910\u8f66\u4f1a\u5c06\u5feb\u9910\u76f4\u63a5\u9001\u5230\u5382\u533a\u8f66\u95f4\u91cc\u53bb\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u3001\u91d1\u56fd\u6d77\u7b49\u5df2\u7ecf\u8fdb\u4e86\u5458\u5de5\u9910\u5385\u4e8c\u697c\u7684\u5305\u53a2\uff0c\u5510\u5b66\u8c26\u4e0e\u675c\u5c0f\u5c71\u3001\u5b8b\u57f9\u660e\u8d70\u5f97\u6162\u4e9b\uff0c\u5f20\u606a\u4e5f\u8ddf\u7740\u4ed6\u4eec\uff0c\u8fd9\u65f6\u5019\u7ad9\u5728\u4e8c\u697c\u8fc7\u5385\u7684\u73bb\u7483\u524d\uff0c\u770b\u7740\u9910\u5385\u5927\u95e8\u8fdb\u8fdb\u51fa\u51fa\u7684\u4eba\u7fa4\uff0c\u5bf9\u5b8b\u57f9\u660e\u8bf4\u9053\uff1a\u201c\u76ca\u9686\u9547\u7684\u65b0\u533a\u5efa\u8bbe\u8981\u63d0\u901f\uff0c\u56ed\u533a\u53d1\u5c55\u52bf\u5934\u8fd9\u4e48\u597d\uff0c\u540e\u52e4\u4fdd\u969c\u4e0d\u4e86\uff0c\u5c31\u662f\u4f60\u5b8b\u57f9\u660e\u7684\u5931\u804c\u4e86\uff0c\u4e0d\u8981\u4ee5\u540e\u8fc7\u51e0\u5929\u5c31\u53ef\u4ee5\u4e22\u5305\u88b1\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u5f53\u7136\u60f3\u5c06\u5305\u88b1\u7ee7\u7eed\u625b\u5728\u80a9\u4e0a\uff0c\u201d\u5b8b\u57f9\u660e\u7b11\u7740\u8bf4\uff1a\u201c\u540e\u52e4\u4fdd\u969c\u4e0d\u4e86\uff0c\u6211\u81ea\u5df1\u5f00\u8f66\u7ed9\u804c\u5de5\u9001\u5feb\u9910\u3002\u4e0d\u8fc7\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u5357\u8fb9\u4e00\u5343\u4ea9\u5730\u662f\u7231\u8fbe\u65e9\u5c31\u8bf4\u597d\u4e86\uff0c\u673a\u82af\u5de5\u5382\u4e00\u671f\u5c31\u7528\u6389\u4e24\u767e\u4ea9\uff0c\u6211\u4f30\u8ba1\u5f88\u5feb\u5c31\u8981\u7528\u5149\uff0c\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u897f\u8fb9\u662f\u76ca\u9686\u9547\u89c4\u5212\u4e2d\u7684\u65b0\u533a\uff0c\u518d\u5f80\u897f\u8981\u89c4\u5212\u8fdb\u6cbf\u6c5f\u98ce\u666f\u533a\uff0c\u4e1c\u8fb9\u5c31\u5269\u4e0b\u4e0d\u5230\u4e24\u5343\u4ea9\u7684\u53d1\u5c55\u7a7a\u95f4\uff0c\u79d1\u738b\u6709\u4ec0\u4e48\u52a8\u4f5c\u8fd8\u4e0d\u77e5\u9053\uff0c\u79d1\u738b\u8981\u5730\uff0c\u6211\u4eec\u4e5f\u4e0d\u80fd\u4e0d\u7ed9\u2026\u2026\u201d</p><p>\u3000\u3000\u8981\u8ba9\u79d1\u738b\u4e3a\u6d77\u5dde\u5e02\u7ecf\u6d4e\u5efa\u8bbe\u505a\u8d21\u732e\u5df2\u6210\u5171\u8bc6\uff0c\u5f20\u606a\u5374\u662f\u6253\u5b9a\u6ce8\u610f\u5c06\u8c22\u5bb6\u3001\u8d75\u9526\u8363\u3001\u9648\u5bb6\u5584\u8fd9\u4e9b\u4eba\u90fd\u540a\u5728\u79d1\u738b\u8fd9\u68f5\u6811\u4e0a\uff0c\u4ee5\u540e\u60f3\u600e\u4e48\u8e42\u8e8f\u5c31\u600e\u4e48\u8e42\u8e8f\uff0c\u603b\u6bd4\u5728\u5176\u4ed6\u9886\u57df\u97ad\u957f\u83ab\u53ca\u7684\u597d\uff1b\u6240\u4ee5\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u7684\u89c4\u5212\u4e0a\u8fd8\u662f\u8981\u7ed9\u79d1\u738b\u7559\u4e0b\u4e00\u5b9a\u7684\u7a7a\u95f4\u3002</p><p>\u3000\u3000\u201c\u4f60\u60f3\u8bf4\u4ec0\u4e48\u5427\uff0c\u4f60\u5c3d\u7ba1\u8bf4\u2026\u2026\u201d\u5510\u5b66\u8c26\u7b11\u5475\u5475\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u79d1\u738b\u4eca\u5929\u7b97\u662f\u5f97\u4e86\u4e00\u4e2a\u6559\u8bad\uff0c\u4e4b\u524d\u4ed6\u4eec\u5728\u56ed\u533a\u91cc\u53ea\u5efa\u7ec4\u88c5\u5de5\u5382\uff0c\u6211\u60f3\u4e4b\u540e\u5927\u6982\u4f1a\u4e8b\u4e8b\u90fd\u4f1a\u5b66\u7231\u8fbe\uff0c\u4e0d\u53ef\u80fd\u5355\u8584\u7684\u53ea\u505a\u7ec4\u88c5\uff0c\u90a3\u4e1c\u8fb9\u5c31\u8981\u7ed9\u4ed6\u4eec\u7559\u4e00\u7247\u5730\u51fa\u6765\uff0c\u6211\u4eec\u603b\u8981\u7ed9\u79d1\u738b\u4e3a\u6d77\u5dde\u7ecf\u6d4e\u5efa\u8bbe\u505a\u8d21\u732e\u7684\u673a\u4f1a\uff0c\u90a3\u7535\u5b50\u5de5\u4e1a\u56ed\u7684\u53d1\u5c55\u4f59\u5730\u5c31\u5f88\u7a84\u4e86\uff0c\u201d\u5b8b\u57f9\u660e\u8bf4\u9053\uff0c\u201c\u65b0\u6865\u9547\u7684\u9020\u7eb8\u5de5\u4e1a\u56ed\u533a\u4e5f\u53ea\u5269\u4e0b\u4e94\u767e\u4ea9\u89c4\u5212\u5730\uff0c\u65b0\u5149\u7eb8\u4e1a\u8fd9\u6b21\u9879\u76ee\u8981\u8c08\u4e0b\u6765\uff0c\u4e94\u767e\u4ea9\u5730\u7acb\u9a6c\u5c31\u8981\u5212\u7ed9\u4ed6\u4eec\uff0c\u5176\u4ed6\u4f01\u4e1a\u8981\u5730\u600e\u4e48\u529e\uff1f\u603b\u4e0d\u80fd\u62c9\u6765\u4e00\u4e2a\u4f01\u4e1a\uff0c\u5c31\u8ddf\u5e02\u91cc\u8981\u4e00\u5757\u5730\uff0c\u90a3\u5ba1\u6279\u8d77\u6765\u591a\u9ebb\u70e6\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u8bf4\u7d22\u6027\u7ed9\u4f60\u4e00\u5927\u5757\u5730\uff0c\u5c06\u65b0\u6865\u9547\u538b\u7684\u9020\u7eb8\u5de5\u4e1a\u56ed\u533a\u4e0e\u76ca\u9686\u9547\u7684\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u8fde\u6210\u4e00\u7247\uff1f\u201d</p><p>\u3000\u3000\u201c\u53ef\u4ee5\u5c06\u8c61\u5c71\u9547\u4e5f\u5e76\u8fdb\u53bb\uff0c\u201d\u5b8b\u57f9\u660e\u8bf4\u9053\uff0c\u201c\u5f00\u53d1\u533a\u90a3\u8fb9\u65e2\u6709\u7684\u5de5\u4e1a\u89c4\u5212\uff0c\u6211\u4eec\u4e0d\u53bb\u78b0\u4ed6\uff0c\u65b0\u6865\u3001\u76ca\u9686\u3001\u8c61\u5c71\u53ef\u4ee5\u7b79\u5efa\u4e00\u4e2a\u7701\u7ea7\u5f00\u53d1\u533a\u57df\uff0c\u4e00\u6b21\u6027\u5c06\u4ee5\u540e\u7684\u53d1\u5c55\u95ee\u9898\u90fd\u8003\u8651\u5230\uff0c\u4e0d\u8981\u7b49\u5230\u4e8b\u60c5\u53d1\u751f\u518d\u8ddf\u4e0a\u9762\u8981\u653f\u7b56\u3001\u8981\u4f18\u60e0\uff0c\u641e\u5f97\u8ddf\u54ed\u95f9\u5f97\u8981\u5403\u5976\u7684\u5b69\u5b50\u4f3c\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u6015\u57ce\u5357\u533a\u7684\u4eba\u9a82\u4f60\u5403\u91cc\u722c\u5916\uff1f\u201d\u675c\u5c0f\u5c71\u5f00\u73a9\u7b11\u8bf4\uff0c\u201c\u4f60\u8981\u79bb\u5f00\u57ce\u5357\u533a\u4e86\uff0c\u5c06\u57ce\u5357\u533a\u6700\u80a5\u7684\u4e24\u5757\u8089\u4e5f\u8981\u4ece\u7897\u91cc\u6252\u8d70\uff1f\u201d</p><p>\u3000\u3000\u5c31\u5b8b\u57f9\u660e\u8fdb\u5e38\u59d4\u7684\u4e8b\u60c5\uff0c\u7701\u59d4\u7ec4\u7ec7\u90e8\u7684\u8003\u5bdf\u7a0b\u5e8f\u5df2\u7ecf\u8d70\u5b8c\uff0c\u5c31\u7b49\u7740\u5e02\u59d4\u4e0e\u7701\u59d4\u7684\u4e24\u7eb8\u901a\u77e5\u4e86\uff0c\u526f\u5e02\u957f\u7684\u804c\u52a1\u8fd8\u8981\u5e02\u4eba\u5927\u8d70\u4e00\u4e0b\u8865\u9009\u7a0b\u5e8f\u3002</p><p>\u3000\u3000\u7701\u7ea7\u5f00\u53d1\u533a\u5728\u884c\u653f\u7f16\u5236\u4e0a\u5c5e\u4e8e\u53bf\u533a\u4e00\u7ea7\uff0c\u65b0\u6865\u9547\u7684\u9020\u7eb8\u5de5\u4e1a\u56ed\u533a\u5012\u4e5f\u7f62\u4e86\uff0c\u76ca\u9686\u9547\u7535\u5b50\u5de5\u4e1a\u56ed\u4ea7\u503c\u4eca\u5e74\u5c31\u80fd\u7834\u4e94\u5341\u4ebf\uff0c\u867d\u7136\u7ed3\u6784\u4e0a\u672a\u5fc5\u6709\u4e9b\u592a\u5931\u8c03\uff0c\u4f46\u662f\u5728\u56fd\u5185\u8fd8\u662f\u4e3b\u8981\u770b\u6570\u636e\uff0c\u76f8\u6bd4\u8f83\u8d77\u6765\uff0c\u6d77\u5dde\u5f00\u53d1\u533a\u4eca\u5e74\u5341\u4e94\u4ebf\u7684\u751f\u4ea7\u603b\u503c\u76ee\u6807\u5c31\u672a\u514d\u592a\u843d\u9b44\u4e86\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u4fa7\u5934\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u95ee\u4ed6\uff1a\u201c\u5c06\u4e09\u4e2a\u9547\u5355\u72ec\u51fa\u6765\u641e\u4e00\u4e2a\u7701\u7ea7\u5f00\u53d1\u533a\uff0c\u662f\u4e00\u4e2a\u65b9\u6848\uff0c\u8fd8\u6709\u4e00\u79cd\u610f\u89c1\uff0c\u5c31\u662f\u5c06\u65b0\u6865\u9547\u4e0e\u76ca\u9686\u9547\u90fd\u5e76\u5165\u5f00\u53d1\u533a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5e02\u91cc\u7684\u4e8b\u60c5\uff0c\u6211\u54ea\u91cc\u80fd\u63d2\u4e0a\u5634\uff1f\u201d\u5f20\u606a\u6487\u5634\u7b11\u7740\u8bf4\uff0c\u201c\u4e0d\u7ba1\u5f52\u5230\u54ea\u4e00\u7247\uff0c\u6211\u53ea\u5e0c\u671b\u653f\u5e9c\u7684\u516c\u5171\u670d\u52a1\u610f\u8bc6\u80fd\u8ddf\u4e0a\u3002\u4e0d\u8fc7\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u5e02\u91cc\u591a\u4e00\u4e2a\u7701\u57ce\u5f00\u53d1\u533a\uff0c\u90a3\u5c31\u53c8\u591a\u4e86\u4e00\u4e2a\u5b8c\u6574\u7684\u53bf\u7ea7\u884c\u653f\u7f16\u5236\uff0c\u5927\u6982\u5f88\u591a\u4eba\u4e50\u610f\u8fd9\u4e48\u641e\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u66f4\u503e\u5411\u5c06\u8c61\u5c71\u9547\u3001\u65b0\u6865\u9547\u3001\u76ca\u9686\u9547\u4e09\u4e2a\u9547\u62ff\u51fa\u6765\u5355\u72ec\u641e\u4e00\u4e2a\u533a\uff0c\u5c06\u8c61\u5c71\u9547\u3001\u76ca\u9686\u9547\u897f\u3001\u897f\u5357\u7684\u7edf\u4e00\u89c4\u5212\u4e3a\u98ce\u666f\u56ed\u533a\uff0c\u66f4\u6709\u5229\u76ca\u6d77\u5dde\u5e02\u7684\u6574\u4f53\u5e03\u5c40\uff0c\u5c31\u7b97\u8981\u5c06\u76ca\u9686\u9547\u5e76\u5165\u5f00\u53d1\u533a\uff0c\u81f3\u5c11\u8981\u5c06\u76ca\u9686\u9547\u897f\u7247\u6cbf\u5c0f\u6c5f\u6c5f\u6e7e\u7684\u533a\u57df\u5212\u7ed9\u8c61\u5c71\u9547\uff0c\u90a3\u4e00\u5757\u98ce\u666f\u4f18\u7f8e\u7684\u571f\u5730\u8981\u62ff\u53bb\u53d1\u5c55\u5de5\u4f5c\uff0c\u5c31\u6709\u4e9b\u592a\u66b4\u6b84\u5929\u7269\u4e86\u3002</p><p>\u3000\u3000\u201c\u589e\u52a0\u4e00\u4e2a\u53bf\u7ea7\u884c\u653f\u7f16\u5236\uff0c\u54ea\u662f\u90a3\u4e48\u5bb9\u6613\u7684\u4e8b\u60c5\uff1f\u201d\u5510\u5b66\u8c26\u611f\u6168\u9053\u3002</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u5728\u57ce\u5357\u533a\u5f53\u4e66\u8bb0\u517c\u533a\u957f\uff0c\u8c01\u4e5f\u65e0\u6cd5\u5c06\u9020\u7eb8\u5de5\u4e1a\u56ed\u533a\u3001\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u4e24\u5757\u80a5\u8089\u4ece\u57ce\u5357\u533a\u7897\u91cc\u6252\u8d70\uff0c\u8fd9\u4e5f\u662f\u4ed6\u80fd\u8fd9\u4e48\u5feb\u8fdb\u4e00\u6b65\u8df3\u5230\u5e02\u91cc\u7684\u4e00\u4e2a\u91cd\u8981\u56e0\u7d20\u3002\u4e0d\u8fc7\u4ed6\u8d70\u540e\uff0c\u57ce\u5357\u533a\u5c31\u7a7a\u51fa\u4e24\u4e2a\u4f4d\u7f6e\uff0c\u4e66\u8bb0\u4e0e\u533a\u957f\u4e0d\u53ef\u80fd\u518d\u517c\u5230\u4e00\u4e2a\u4eba\u5934\u4e0a\uff0c\u81f3\u4e8e\u5c06\u9020\u7eb8\u5de5\u4e1a\u56ed\u4ee5\u53ca\u7535\u5b50\u5de5\u4e1a\u56ed\u4e24\u5757\u80a5\u8089\u4ece\u57ce\u5357\u533a\u6316\u6389\uff0c\u7ee7\u4efb\u8005\u4e5f\u6ca1\u6709\u80fd\u529b\u8ddf\u5e02\u91cc\u8f83\u52b2\u7684\uff0c\u5c31\u7b97\u674e\u4e49\u6c5f\u5230\u57ce\u5357\u533a\u5f53\u4e66\u8bb0\uff0c\u8fd9\u65f6\u5019\u4e5f\u4e0d\u53ef\u80fd\u8003\u8651\u5230\u4ed6\uff1b\u773c\u4e0b\u7684\u7126\u70b9\u53ea\u662f\u600e\u4e48\u6316\u80a5\u8089\u7684\u95ee\u9898\u3002\u6709\u4e00\u4e2a\u6709\u5229\u7684\u6761\u4ef6\uff0c\u5468\u5bcc\u660e\u8fd9\u6b21\u7ed9\u95f7\u5934\u6253\u4e86\u4e00\u68cd\uff0c\u5728\u8fd9\u4e2a\u95ee\u9898\u5c31\u4e0d\u4f1a\u592a\u6d3b\u8dc3\uff0c\u4e3b\u52a8\u6743\u90fd\u4ed6\u4eec\u8fd9\u4e00\u8fb9\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5458\u5de5\u9910\u5385\u4eba\u6ee1\u4e3a\u60a3\uff0c\u7231\u8fbe\u7684\u7ecf\u9500\u5546\u3001\u540c\u4e1a\u5f71\u789f\u673a\u5382\u5546\u4ee3\u8868\u4ee5\u53ca\u5a92\u4f53\u8bb0\u8005\u90fd\u5df2\u7ecf\u5206\u6d41\u5230\u897f\u57ce\u996d\u5e97\u7528\u9910\u3002\u66f4\u591a\u8d76\u5230\u6d77\u5dde\u6765\u5bfb\u627e\u5546\u673a\u7684\u7535\u5668\u7ecf\u9500\u5546\u4eec\u53ea\u80fd\u81ea\u884c\u89e3\u51b3\u7528\u9910\uff0c\u773c\u4e0b\u770b\u6765\u6ca1\u6709\u54ea\u5bb6\u7231\u8fbe\u7535\u5b50\u7684\u7ecf\u9500\u5546\u4f1a\u653e\u5f03\u4ee3\u7406\u6743\uff0c\u4ed6\u4eec\u8fd8\u662f\u53ea\u80fd\u53bb\u4e89\u53d6\u62ff\u79d1\u738b\u7684\u4ee3\u7406\u6743\u7684\uff0c\u53ea\u662f\u4eca\u5929\u4e0a\u5348\u7b97\u662f\u7ed9\u6cfc\u4e86\u4e00\u76c6\u51b7\u6c34\uff0c\u70ed\u5ea6\u964d\u4e86\u4e0b\u6765\uff0c\u90fd\u8ba4\u8bc6\u5230\u79d1\u738b\u5728\u4e5d\u4e03\u5e74\u5ea6\u60f3\u5168\u9762\u8d85\u8d8a\u7231\u8fbe\u53ea\u662f\u5984\u60f3\u3002</p><p>\u3000\u3000\u4e0b\u5348\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u7531\u6768\u4e91\u3001\u5468\u4e00\u5e73\u4e24\u4eba\u4e3b\u6301\u5c31\u53ef\u4ee5\uff0c\u4f46\u5e76\u4e0d\u610f\u5473\u7740\u82cf\u6d25\u4e1c\u80a9\u4e0a\u7684\u62c5\u4f1a\u8f7b\u677e\uff0c\u65b0\u79d1\u7535\u5b50\u3001\u6b65\u6b65\u9ad8\u3001\u7231\u591a\u7b49\u540c\u4e1a\u789f\u673a\u5382\u5546\u4ee3\u8868\u90fd\u6b63\u5f0f\u63d0\u51fa\u63a5\u89e6\u7684\u8981\u6c42\uff0c\u7d22\u5c3c\u516c\u53f8\u7684\u4ee3\u8868\u6e21\u8fb9\u656c\u4e00\u4e5f\u8981\u6c42\u63a5\u89e6\uff0c\u65e2\u7136\u98de\u5229\u6d66\u80fd\u5411\u7231\u8fbe\u7535\u5b50\u8f6c\u8ba9\u673a\u82af\u5236\u9020\u6280\u672f\uff0c\u7d22\u5c3c\u7edd\u4e0d\u53ef\u80fd\u5c06\u81ea\u5df1\u5173\u4e8e\u95e8\u5916\uff0c\u4e0d\u8ddf\u7231\u8fbe\u7535\u5b50\u63a5\u89e6\uff0c\u6b64\u5916\u8fd8\u6709\u4f17\u591a\u5a92\u4f53\u8bb0\u8005\u7684\u91c7\u8bbf\u8981\u6c42\uff0c\u8fd8\u6709\u4e0eTI\u3001\u98de\u5229\u6d66\u3001\u65af\u9ad8\u67cf\u7684\u5408\u4f5c\u90fd\u8981\u8fc5\u901f\u5c55\u5f00\u6765\uff0c\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u3001\u848b\u8587\u3001\u9c81\u5e86\u751f\u7b49\u4eba\u7684\u65e5\u7a0b\u8868\u6392\u5f97\u5bc6\u5bc6\u9ebb\u9ebb\uff0c\u6709\u4e00\u79cd\u8ba9\u4eba\u900f\u4e0d\u8fc7\u6c14\u6765\u7684\u7d27\u5bc6\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u3001\u827e\u9ed8\u3001\u5f20\u4e9a\u5e73\u7b49\u4eba\u4eca\u5929\u4e0b\u5348\u5c31\u8981\u79bb\u5f00\u6d77\u5dde\uff0c\u4e2d\u5348\u5c31\u5728\u5458\u5de5\u9910\u5385\u7684\u4e8c\u697c\u5305\u53a2\u91cc\u529e\u4e86\u4e24\u684c\u9152\u5e2d\uff0c\u5510\u5b66\u8c26\u3001\u675c\u5c0f\u5c71\u3001\u91d1\u56fd\u6d77\u3001\u5b8b\u57f9\u660e\u7b49\u4eba\u4ee3\u8868\u5e02\u91cc\u5bb4\u8bf7\u4ed6\u4eec\uff0c\u540c\u65f6\u5bb4\u8bf7\u53f6\u5efa\u658c\u3001\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u5085\u5bb6\u4fca\u7b49\u4eba\u3002</p><p>\u3000\u3000\u4eca\u5929\u4e0a\u5348\u7684\u8868\u6f14\u53ef\u4ee5\u7b97\u662f\u7cbe\u5f69\u7eb7\u5448\uff0c\u53ea\u662f\u9152\u5e2d\u95f4\u4f17\u4eba\u90fd\u81ea\u6301\u8eab\u4efd\uff0c\u4e0d\u53ef\u80fd\u5f97\u610f\u5fd8\u5f62\u5938\u5938\u5176\u8c08\u3002\u7528\u8fc7\u9910\uff0c\u5f20\u4e9a\u5e73\u4e0e\u52a9\u624b\u76f4\u63a5\u5f00\u8f66\u4ece\u76ca\u9686\u9547\u8fd4\u56de\u4e0a\u6d77\uff0c\u5f20\u606a\u59d4\u6258\u4e01\u69d0\u3001\u4e01\u78ca\u3001\u848b\u8587\u4e09\u4eba\u9001\u9648\u4fe1\u751f\u3001\u827e\u9ed8\u4ed6\u4eec\u53bb\u673a\u573a\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u3001\u91d1\u56fd\u6d77\u3001\u675c\u5c0f\u5c71\u3001\u5b8b\u57f9\u660e\u7b49\u4eba\u7528\u8fc7\u9910\u76f4\u63a5\u56de\u5e02\u91cc\uff0c\u82cf\u6d25\u4e1c\u8fd8\u8981\u5728\u4e0b\u5348\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u4e0a\u9732\u4e2a\u9762\u3002</p><p>\u3000\u3000\u5c31\u5269\u4e0b\u53f6\u5efa\u658c\u3001\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u5085\u5bb6\u4fca\u7b49\u4eba\u966a\u5f20\u606a\u7559\u5728\u56ed\u533a\u91cc\uff0c\u8fd9\u65f6\u8bf4\u8bdd\u5c31\u6ca1\u6709\u592a\u5927\u7684\u987e\u8651\uff0c\u5085\u5bb6\u4fca\u4e0d\u77e5\u9053\u5f20\u606a\u7684\u901a\u76d8\u8003\u8651\uff0c\u8e69\u5230\u73b0\u5728\uff0c\u624d\u5fcd\u4e0d\u4f4f\u95ee\u51fa\u53e3\uff1a\u201c\u7231\u8fbe\u7535\u5b50\u4e3a\u4ec0\u4e48\u8981\u4e3b\u52a8\u7ed9\u5bf9\u624b\u5598\u606f\u7684\u673a\u4f1a\uff1f\u5373\u4f7f\u4e0d\u5f3a\u52bf\u7684\u6269\u5f20\u5e02\u573a\uff0c\u6211\u60f3\u4fdd\u4f4f\u5f53\u524d\u7684\u5e02\u573a\u4efd\u989d\u662f\u6ca1\u6709\u95ee\u9898\u7684\u3002\u201d</p><p>\u3000\u3000\u5458\u5de5\u9910\u5385\u4e09\u697c\u6709\u4e13\u95e8\u7684\u5065\u8eab\u623f\u3001\u68cb\u724c\u5ba4\u3001\u7535\u8111\u5ba4\u7b49\u5404\u79cd\u5a31\u4e50\u5ba4\uff0c\u4f9b\u5458\u5de5\u4e2d\u5348\u7528\u9910\u540e\u4f11\u606f\u5a31\u4e50\u6240\u7528\uff0c\u5f20\u606a\u4ed6\u4eec\u4e2d\u5348\u7528\u9910\u5c31\u665a\uff0c\u5f53\u73ed\u7684\u5458\u5de5\u5df2\u7ecf\u8fd4\u56de\u5de5\u4f5c\u5c97\u4f4d\uff0c\u5a31\u4e50\u5ba4\u91cc\u90fd\u662f\u4e0a\u4e2d\u73ed\u3001\u591c\u73ed\u7684\u5458\u5de5\uff0c\u5f20\u606a\u4ed6\u4eec\u5360\u4e86\u9605\u89c8\u5ba4\u7684\u4e00\u89d2\u559d\u8336\u804a\u5929\uff0c\u73bb\u7483\u5899\u5916\u80fd\u770b\u89c1\u7070\u9ec4\u7684\u5c0f\u6c5f\u6c5f\u6c34\uff1b\u5085\u4fca\u4e0e\u9a6c\u6d77\u9f99\u966a\u5728\u5916\u56f4\uff0c\u6ce8\u610f\u4e0d\u8ba9\u4eba\u6253\u6270\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u5085\u5bb6\u4fca\u95ee\u51fa\u7684\u95ee\u9898\uff0c\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u503e\u7740\u8eab\u5b50\uff0c\u5c06\u624b\u91cc\u7684\u8336\u676f\u653e\u5230\u73bb\u7483\u51e0\u4e0a\uff0c\u7b11\u7740\u95ee\uff1a\u201c\u9999\u6e2f\u7684\u6295\u8d44\u4eba\u662f\u5e0c\u671b\u6295\u8d44\u4e00\u4e2a\u7a33\u5b9a\u7684\u3001\u524d\u666f\u53ef\u4ee5\u9884\u671f\u7684\u5e02\u573a\uff0c\u8fd8\u662f\u4e00\u4e2a\u6740\u4f10\u8840\u8165\u3001\u524d\u666f\u96be\u4ee5\u9884\u6d4b\u7684\u5e02\u573a\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5085\u5bb6\u4fca\u8be7\u5f02\u7684\u8bf4\uff0c\u201c\u606a\u5c11\u8fd9\u4e48\u5feb\u5c31\u60f3\u5c06\u7231\u8fbe\u7535\u5b50\u5305\u88c5\u5230\u9999\u6e2f\u4e0a\u5e02\uff1f\u73b0\u5728\u53ea\u80fd\u501f\u58f3\u4e0a\u5e02\uff0c\u4e0d\u5982\u7b49\u4e24\u5e74\u76f4\u63a5\u4e0a\u5e02\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u7531\u5609\u4fe1\u5b9e\u4e1a\u5728\u9999\u6e2f\u80a1\u5e02\u516c\u5f00\u589e\u53d1\u80a1\u7968\u5e76\u8d2d\u7231\u8fbe\u7535\u5b50\u7684\u8d44\u4ea7\uff0c\u8fd9\u4e2a\u65b9\u6848\u6709\u591a\u5927\u7684\u53ef\u884c\u6027\uff1f\u201d\u73b0\u5728\u5c31\u662f\u76f4\u63a5\u8ddf\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u8c08\u8fd9\u4e2a\u95ee\u9898\u7684\u597d\u65f6\u673a\u3002</p></article>"}}}]);
