(window.webpackJsonp=window.webpackJsonp||[]).push([[612],{635:function(p){p.exports={chapterTitle:null,title:"\u7b2c612\u7ae0 \u5927\u5b66\u751f\u6d3b",content:"<article><h2>\u7b2c612\u7ae0 \u5927\u5b66\u751f\u6d3b</h2><p>\u3000\u3000\u73ed\u4f1a\u7ed3\u675f\uff0c\u5f20\u606a\u4e0e\u675c\u98de\u8d70\u51fa\u6559\u5ba4\uff0c\u542c\u5230\u9646\u5929\u53c8\u5728\u540e\u9762\u5524\u4ed6\u4eec\uff0c\u56de\u5934\u89c1\u6559\u5ba4\u91cc\u53ea\u5269\u4e0b\u5b66\u751f\u5e72\u90e8\uff0c\u9646\u5929\u53c8\u8ddf\u6c88\u7b71\u7ad9\u5728\u4e00\u8d77\u8bf4\u8bdd\uff0c\u95ee\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u9b4f\u4e3b\u4efb\u8ba9\u6211\u627e\u4f60\u4eec\u4fe9\u804a\u4e00\u804a\u2026\u2026\u201d\u9646\u5929\u53c8\u8d70\u8fc7\u6765\uff0c\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u201d\u5f20\u606a\u5e94\u4e86\u4e00\u58f0\uff0c\u4fbf\u4e00\u8d77\u8d70\u51fa\u6559\u5ba4\uff0c\u8fd9\u4f1a\u513f\u6b63\u662f\u4e0b\u591c\u81ea\u4fee\u7684\u65f6\u95f4\uff0c\u5de5\u5b57\u697c\u91cc\u4eba\u6765\u4eba\u5f80\uff0c\u89c1\u9646\u5929\u53c8\u4e0d\u6025\u7740\u8bf4\u4ec0\u4e48\u4e8b\uff0c\u5f20\u606a\u3001\u675c\u98de\u4fbf\u4e0e\u4ed6\u4e00\u8d77\u4e0b\u697c\u3002</p><p>\u3000\u3000\u51fa\u4e86\u5de5\u5b57\u697c\u8d70\u4e86\u4e00\u6bb5\u8def\uff0c\u9646\u5929\u53c8\u659f\u914c\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u2026\u2026\u542c\u9b4f\u4e3b\u4efb\u8bf4\uff0c\u6211\u4eec\u73ed\uff0c\u6211\u4eec\u4e09\u4e2a\u4eba\u7684\u5bb6\u5883\u6bd4\u8f83\u7279\u6b8a\u4e00\u4e9b\uff0c\u6211\u60f3\u6211\u4eec\u4e09\u4e2a\u4eba\u53ef\u4ee5\u505a\u4e00\u4e2a\u597d\u7684\u8868\u7387\u3002\u4f60\u4eec\u4e0d\u80fd\u53c2\u52a0\u519b\u8bad\u7684\u4e8b\u60c5\uff0c\u6211\u4e5f\u542c\u8bf4\u4e86\uff0c\u6211\u60f3\u8bf7\u4f60\u4eec\u5e2e\u6211\u4e00\u8d77\u5c06\u65b0\u751f\u519b\u8bad\u7b80\u62a5\u529e\u8d77\u6765\uff0c\u4f60\u4eec\u770b\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u597d\u7684\u3002\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8fd9\u5e94\u8be5\u662f\u9b4f\u4e1c\u5f3a\u7684\u610f\u601d\u5427\uff0c\u4e1c\u5927\uff0c\u53c2\u52a0\u519b\u8bad\u80fd\u8ba1\u516b\u4e2a\u5b66\u5206\uff0c\u8fd9\u5927\u6982\u662f\u4e2a\u4e92\u76f8\u7ed9\u53f0\u9636\u7684\u505a\u6cd5\uff0c\u5f20\u606a\u5c31\u4e00\u53e3\u7b54\u5e94\u4e0b\u6765\uff0c\u968f\u7740\u519b\u8bad\u7684\u5c55\u5f00\uff0c\u56e0\u4e3a\u8eab\u4f53\u539f\u56e0\u6216\u8005\u5176\u4ed6\u56e0\u7d20\u4e0d\u80fd\u53c2\u52a0\u519b\u8bad\u7684\u65b0\u751f\u4f1a\u8d8a\u6765\u8d8a\u591a\uff0c\u4e5f\u5c31\u662f\u8bf4\u7ed9\u585e\u5230\u9662\u529e\u8d1f\u8d23\u51fa\u519b\u8bad\u7b80\u62a5\u7684\u5b66\u751f\u4e5f\u8d8a\u6765\u8d8a\u591a\u2014\u2014\u8fd9\u5c06\u662f\u4e00\u9879\u5f88\u8f7b\u677e\u7684\u6d3b\u3002</p><p>\u3000\u3000\u201c\u6211\u8fd8\u8981\u8ddf\u7740\u4e00\u8d77\u519b\u8bad\uff0c\u5e2d\u4e3b\u4efb\u4f1a\u6559\u4f60\u4eec\u600e\u4e48\u5236\u4f5c\u519b\u8bad\u7b80\u62a5\uff0c\u4f60\u4eec\u660e\u5929\u4e0a\u5348\u53bb\u5e74\u7ea7\u529e\u627e\u5979\u62a5\u9053\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u5fcd\u4e0d\u4f4f\u8981\u8f7b\u677e\u7684\u5439\u4e00\u58f0\u53e3\u54e8\u4e86\uff0c\u9662\u7cfb\u4e4b\u95f4\u7684\u519b\u8bad\u8bc4\u6bd4\uff0c\u9664\u4e86\u5bbf\u820d\u536b\u751f\u4e4b\u5916\uff0c\u519b\u8bad\u7b80\u62a5\u4e5f\u662f\u5f88\u91cd\u8981\u7684\u4e00\u9879\uff0c\u8fd9\u79cd\u7ba1\u7406\u6a21\u5f0f\u6709\u7740\u5de8\u5927\u7684\u4f20\u7edf\u60ef\u6027\uff0c\u867d\u7136\u90fd\u77e5\u9053\u5f0a\u7aef\u91cd\u91cd\uff0c\u4f46\u662f\u56fd\u5185\u8fd8\u6ca1\u6709\u54ea\u4e00\u4e2a\u6559\u80b2\u5bb6\u6765\u63a8\u52a8\u9ad8\u6821\u6559\u80b2\u7ba1\u7406\u7684\u6539\u9769\uff0c\u5f20\u606a\u5f53\u7136\u4e5f\u4e0d\u4f1a\u95f2\u5230\u8981\u53bb\u64cd\u8fd9\u4efd\u95f2\u5fc3\uff0c\u5fc3\u60f3\u5927\u6982\u76ee\u524d\u80fd\u6293\u8fc7\u53bb\u529e\u7b80\u62a5\u7684\u65b0\u4e01\u5c31\u4ed6\u4eec\u4fe9\uff0c\u4e0d\u7136\u5f88\u96be\u60f3\u8c61\u5e2d\u82e5\u7433\u4f1a\u6311\u4e0a\u4ed6\u4eec\u4fe9\u5f53\u52a9\u624b\uff0c\u4e0d\u8fc7\u660e\u5929\u4e0a\u5348\u8981\u53bb\u673a\u573a\u9001\u5510\u5a67\u5979\u4eec\uff0c\u5f20\u606a\u7709\u5934\u5fae\u5fae\u76b1\u8d77\u6765\uff0c\u4e3a\u96be\u7684\u8bf4\uff0c\u201c\u6211\u4eec\u660e\u5929\u4e0a\u5348\u53ef\u80fd\u8981\u8bf7\u4e00\u4e0b\u5047\uff0c\u4f60\u90a3\u91cc\u6709\u6ca1\u6709\u5e2d\u82e5\u7433\u7684\u5bbf\u820d\u7535\u8bdd\uff1f\u6211\u7b49\u4f1a\u513f\u8ddf\u5979\u8bf4\u8fd9\u4e8b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e00\u820d415\u2026\u2026\u201d\u4e1c\u5927\u7684\u5bbf\u820d\u7535\u8bdd\u4e0e\u5bbf\u820d\u6709\u4e00\u5b9a\u7684\u8054\u7cfb\uff0c\u9646\u5929\u53c8\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u544a\u8bc9\u5f20\u606a\u5e2d\u82e5\u7433\u7684\u5bbf\u820d\u53f7\u7801\uff0c\u201c2701415\uff0c\u90a3\u4f60\u4eec\u81ea\u5df1\u8ddf\u5e2d\u4e3b\u4efb\u8bf7\u5047\u5427\u3002\u201d</p><p>\u3000\u3000\u65e2\u7136\u9646\u5929\u53c8\u90fd\u4e3b\u52a8\u8bf4\u5230\u4e5d\u4e03\u7ecf\u6d4e\u5c31\u4ed6\u4eec\u4e09\u4e2a\u4eba\u5bb6\u5883\u6bd4\u8f83\u7279\u6b8a\u4e00\u4e9b\uff0c\u675c\u98de\u4fbf\u95ee\u4ed6\uff1a\u201c\u4f60\u5bb6\u91cc\u662f\u505a\u4ec0\u4e48\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u5443\uff0c\u201d\u9646\u5929\u53c8\u5e94\u4e86\u4e00\u58f0\uff0c\u5374\u53c8\u8f7b\u63cf\u6de1\u5199\u7684\u8bf4\u9053\uff0c\u201c\u6211\u7238\u7238\u4e5f\u662f\u653f\u5e9c\u5de5\u4f5c\u4eba\u5458\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u675c\u98de\u7b49\u4e86\u534a\u664c\u6ca1\u89c1\u9646\u5929\u53c8\u8bf4\u4ec0\u4e48\u4e0b\u6587\uff0c\u8be7\u5f02\u7684\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u4fbf\u8ddf\u5f20\u606a\u8bf4\uff0c\u201c\u554a\uff0c\u6211\u597d\u50cf\u6709\u4e1c\u897f\u5fd8\u6559\u5ba4\u91cc\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u6211\u966a\u4f60\u56de\u53bb\u62ff\u2026\u2026\u201d\u4e24\u4eba\u4e8c\u8bdd\u4e0d\u8bf4\u5c06\u9646\u5929\u53c8\u6487\u4e0b\uff0c\u8f6c\u8eab\u4ece\u53e6\u4e00\u6761\u5c94\u9053\u5f80\u7814\u7a76\u751f\u516c\u5bd3\u8d70\u53bb\u3002</p><p>\u3000\u3000\u201c\u8fd9\u5bb6\u4f19\u4e5f\u592a\u4e0d\u5730\u9053\u5427\uff0c\u201d\u675c\u98de\u7262\u9a9a\u6ee1\u8179\uff0c\u201c\u4ed6\u4ee5\u4e3a\u4ed6\u662f\u4ec0\u4e48\u4eba\uff0c\u2018\u6211\u7238\u7238\u4e5f\u662f\u653f\u5e9c\u5de5\u4f5c\u4eba\u5458\u2019\uff0c\u201d\u62ff\u9646\u5929\u53c8\u7684\u90a3\u79cd\u8bed\u8c03\u5b66\u820c\u4ed6\u8bf4\u7684\u90a3\u53e5\u8bdd\uff0c\u201c\u597d\u50cf\u8bf4\u51fa\u6765\u6015\u6211\u4eec\u6500\u9644\u4ed6\u5bb6\u4f3c\u7684\u3002\u8fd9\u5c0f\u5b50\u8981\u771f\u662f\u4f4e\u8c03\u7684\u4eba\uff0c\u5c31\u522b\u8ba9\u5d14\u56fd\u6052\u9001\u4ed6\u53bb\u5bbf\u820d\uff0c\u201d\u95ee\u5f20\u606a\uff0c\u201c\u8fd9\u79cd\u4eba\u662f\u4e0d\u662f\u65e2\u60f3\u505a\u5a4a\u5b50\u53c8\u60f3\u7acb\u724c\u574a\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u7684\u5fc3\u6001\u4e0d\u6210\u719f\u5427\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u4ed6\u8981\u88c5\u5c31\u8ba9\u4ed6\u88c5\u53bb\u5427\uff0c\u5c11\u642d\u7406\u4ed6\u5c31\u662f\u3002\u201d</p><p>\u3000\u3000\u867d\u7136\u65b0\u751f\u519b\u8bad\u52a8\u5458\u4f1a\u4e0a\uff0c\u5e2d\u82e5\u7433\u5bf9\u4ed6\u4eec\u8981\u4e48\u51b7\u82e5\u51b0\u971c\uff0c\u8981\u4e48\u89c6\u82e5\u65e0\u7779\uff0c\u4e0d\u8fc7\u660e\u5929\u8bf7\u5047\u7684\u4e8b\u60c5\u5374\u662f\u8981\u8ddf\u5979\u8bf4\u4e00\u58f0\uff0c\u5f20\u606a\u6253\u7535\u8bdd\u8fc7\u53bb\uff0c\u5e2d\u82e5\u7433\u521a\u4ece\u5916\u9762\u56de\u5bbf\u820d\uff0c\u4ece\u5bbf\u820d\u540c\u5b66\u90a3\u91cc\u62ff\u6765\u7535\u8bdd\uff0c\u5a07\u67d4\u59a9\u5a9a\u7684\u58f0\u97f3\u4ece\u8bdd\u7b52\u91cc\u4f20\u8fc7\u6765\uff1a\u201c\u77e5\u9053\u8fd9\u65f6\u5019\u7ed9\u6211\u6253\u7535\u8bdd\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u5443\u2026\u2026\u201d\u5f20\u606a\u6123\u4e86\u4e00\u4e0b\uff0c\u5fc3\u60f3\uff1a\u5e2d\u82e5\u7433\u83ab\u975e\u662f\u90a3\u79cd\u5916\u8868\u51b7\u82e5\u51b0\u971c\u5185\u5fc3\u5374\u85cf\u7740\u706b\u5c71\u7684\u5996\u5a9a\u5973\u5b50\uff1f\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u9053\u7406\u4e00\u773c\u5c31\u770b\u4e0a\u81ea\u5df1\u554a\u3002</p><p>\u3000\u3000\u201c\u90fd\u51e0\u5929\u4e86\uff0c\u624d\u60f3\u8d77\u7ed9\u6211\u6253\u7535\u8bdd\u4e86\uff1f\u201d\u80fd\u542c\u89c1\u5e2d\u82e5\u7433\u8d70\u5230\u8fc7\u9053\u91cc\u5c06\u5bbf\u820d\u95e8\u5e26\u4e0a\uff0c\u58f0\u97f3\u8d8a\u53d1\u7684\u8f7b\u67d4\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u5443\u2026\u2026\u201d\u5f20\u606a\u786e\u5b9a\u5e2d\u82e5\u7433\u8bef\u4f1a\u4e86\uff0c\u9690\u7ea6\u542c\u89c1\u5979\u5bbf\u820d\u540c\u5b66\u521a\u624d\u5c06\u7535\u8bdd\u9012\u7ed9\u5979\u65f6\u8bf4\u4e86\u53e5\u201c\u4f60\u90a3\u4f4d\u201d\uff0c\u6307\u4e0d\u5b9a\u5e2d\u82e5\u7433\u7b49\u67d0\u4eba\u7684\u8fd9\u901a\u7535\u8bdd\u6b63\u5fc3\u7126\u3002</p><p>\u3000\u3000\u201c\u5443\u4ec0\u4e48\u5443\uff0c\u4f60\u600e\u4e48\u4e0d\u8bf4\u8bdd\uff1f\u6211\u8fd8\u4ee5\u4e3a\u4f60\u4ee5\u540e\u518d\u4e5f\u4e0d\u7406\u6211\u4e86\u5462\uff0c\u5475\u5475\uff0c\u4f60\u8fd8\u662f\u5fcd\u4e0d\u4f4f\u5148\u7ed9\u6211\u6253\u7535\u8bdd\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6beb\u4e0d\u72b9\u8c6b\u7684\u5c06\u7535\u8bdd\u6302\u6389\uff0c\u6ca1\u6709\u518d\u542d\u4e00\u58f0\u3002\u53d7\u4f24\u7684\u6bcd\u517d\u60f9\u4e0d\u5f97\uff0c\u53d7\u60c5\u4f24\u7684\u5973\u4eba\u66f4\u60f9\u4e0d\u5f97\u3002\u53cd\u6b63\u5bbf\u820d\u7535\u8bdd\u53c8\u6ca1\u6709\u6765\u7535\u8bdd\u663e\u793a\uff0c\u4e5d\u4e03\u5e74\u5efa\u90ba\u90ae\u7535\u5c40\u4e5f\u4e0d\u652f\u6301\u6765\u7535\u56de\u67e5\u529f\u80fd\uff0c\u5c31\u5f53\u662f\u4e00\u901a\u6253\u9519\u7684\u7535\u8bdd\u5427\u3002</p><p>\u3000\u3000\u8d70\u56de\u5230\u5e08\u5927\u4e8c\u62db\uff0c\u5f20\u606a\u624d\u8ba9\u675c\u98de\u6253\u7535\u8bdd\u7ed9\u5e2d\u82e5\u7433\u8bf4\u53ca\u660e\u5929\u4e0a\u5348\u8bf7\u5047\u7684\u4e8b\u60c5\uff0c\u770b\u675c\u98de\u76b1\u7709\u54a7\u5634\u7684\u795e\u8272\uff0c\u5e2d\u82e5\u7433\u5728\u7535\u8bdd\u91cc\u7684\u58f0\u97f3\u4e00\u5b9a\u51b7\u5230\u8ba9\u4eba\u611f\u89c9\u51b0\u6e23\u5b50\u5728\u5f80\u8033\u6735\u91cc\u94bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5510\u5a67\u5979\u4eec\u662f\u4e0a\u5348\u5341\u70b9\u7684\u98de\u673a\u76f4\u63a5\u9999\u6e2f\uff0c\u848b\u8587\u4f1a\u76f4\u63a5\u5230\u542f\u5fb7\u673a\u573a\u63a5\u5979\u4eec\uff0c\u8fd9\u8fb9\u5c31\u4e0d\u6d3e\u4eba\u968f\u884c\uff0c\u9648\u5983\u84c9\u4e5f\u9003\u4e86\u8bfe\u5230\u673a\u573a\u7ed9\u5510\u5a67\u9001\u884c\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u4e00\u65e9\u5c31\u8ba9\u9a6c\u6d77\u9f99\u5f00\u8f66\u9001\u4ed6\u56de\u65b0\u829c\uff1b\u5230\u673a\u573a\u6765\u9001\u884c\uff0c\u4e5f\u5c31\u5f20\u606a\u3001\u675c\u98de\u4e0e\u9648\u5983\u84c9\u4e09\u4eba\uff1b\u76db\u9752\u6ca1\u90a3\u4e2a\u95f2\u5de5\u592b\u4e13\u95e8\u62bd\u65f6\u95f4\u51fa\u6765\u7ed9\u76db\u590f\u9001\u884c\uff0c\u6709\u675c\u98de\u505a\u4ee3\u8868\u5c31\u591f\u4e86\u3002</p><p>\u3000\u3000\u8bf4\u8d77\u6765\uff0c\u9999\u6e2f\u4e0e\u5efa\u90ba\u5c31\u9694\u7740\u4e24\u5c0f\u65f6\u7684\u98de\u884c\u8ddd\u79bb\uff0c\u6bd4\u4ece\u5efa\u90ba\u5230\u6d77\u5dde\u7684\u65f6\u95f4\u8fd8\u8981\u77ed\uff0c\u5fc3\u7406\u4e0a\u7684\u611f\u89c9\u5374\u662f\u8fe5\u7136\u4e0d\u540c\uff0c\u6362\u767b\u673a\u724c\u65f6\uff0c\u5510\u5a67\u773c\u775b\u5c31\u6709\u4e9b\u7ea2\u4e86\uff0c\u5f20\u606a\u4f38\u624b\u53bb\u62b9\u5979\u7684\u773c\u76ae\u5b50\uff0c\u6676\u83b9\u5254\u900f\u7684\u6cea\u73e0\u5b50\u5c31\u6389\u4e0b\u6765\uff0c\u8fc7\u5b89\u68c0\u95e8\u65f6\uff0c\u53c8\u7a81\u7136\u8dd1\u56de\u6765\u62b1\u4f4f\u5f20\u606a\uff0c\u8138\u988a\u505c\u5728\u4ed6\u80f8\u53e3\u542c\u4e865\u79d2\u949f\uff0c\u624d\u786e\u5b9a\u7684\u8bf4\uff1a\u201c\u539f\u6765\u4f60\u771f\u7684\u820d\u4e0d\u5f97\u6211\u8d70\u2026\u2026\u201d</p><p>\u3000\u3000\u53e6\u4e00\u4e16\uff0c\u5fc3\u91cc\u4f1a\u66f4\u75db\u4e0a\u4e07\u5206\uff0c\u4e0d\u4e00\u6837\u7684\u75db\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u62cd\u4e86\u62cd\u5510\u5a67\u7684\u80a9\u8180\uff0c\u793a\u610f\u6709\u4e24\u4f4d\u4e2d\u5e74\u5987\u5973\u7ad9\u5728\u5b89\u68c0\u95e8\u90a3\u91cc\u65e0\u5948\u7684\u770b\u7740\u8fd9\u8fb9\u3002</p><p>\u3000\u3000\u5510\u5a67\u7f9e\u6da9\u7684\u7b11\u4e86\u7b11\uff0c\u62c9\u4e86\u62c9\u9648\u5983\u84c9\u7684\u624b\uff0c\u624d\u604b\u604b\u4e0d\u820d\u7684\u5f80\u5b89\u68c0\u95e8\u90a3\u8fb9\u8d70\u53bb\u3002</p><p>\u3000\u3000\u770b\u7740\u5979\u4eec\u6d88\u5931\u5728\u767b\u673a\u901a\u9053\u91cc\uff0c\u5f20\u606a\u4e0e\u675c\u98de\u3001\u9648\u5983\u84c9\u8f6c\u8eab\u51fa\u4e86\u5927\u5385\uff0c\u5c06\u8f66\u5f00\u5230\u673a\u573a\u4e1c\u4fa7\u7684\u6797\u836b\u9053\u4e0a\uff0c\u901a\u8fc7\u94c1\u4e1d\u7f51\uff0c\u80fd\u591f\u6e05\u6670\u7684\u770b\u89c1\u6bcf\u4e00\u67b6\u98de\u673a\u7684\u8d77\u843d\u3002</p><p>\u3000\u3000\u8584\u9634\u5929\u6c14\uff0c\u6bd5\u7adf\u662f\u5165\u4e86\u79cb\uff0c\u6ca1\u6709\u6bd2\u8fa3\u7684\u592a\u9633\uff0c\u5929\u6c14\u5c31\u4f1a\u9634\u51c9\u4e00\u4e9b\uff0c\u5f20\u606a\u5c06\u80f3\u81c2\u6401\u5728\u8f66\u7a97\u4e0a\uff0c\u770b\u7740\u6e2f\u9f99\u822a\u7a7a\u7684\u90a3\u73ed\u98de\u673a\u5728\u8dd1\u9053\u4e0a\u79fb\u52a8\u3001\u52a0\u901f\u3001\u62c9\u5347\u3001\u611f\u89c9\u51e0\u4e4e\u662f\u8d34\u7740\u897f\u5357\u4fa7\u7684\u94c1\u4e1d\u7f51\u8dc3\u5165\u7a7a\u4e2d\uff0c\u5728\u89c6\u91ce\u91cc\u8d8a\u6765\u8d8a\u5c0f\uff0c\u6700\u7ec8\u6d88\u5931\u5728\u6de1\u6de1\u7684\u767d\u4e91\u4e4b\u5916\u3002</p><p>\u3000\u3000\u5c06\u8f66\u5f00\u56de\u5e08\u5927\u4e8c\u62db\uff0c\u4e22\u5728\u90a3\u91cc\uff0c\u9648\u5983\u84c9\u5728\u5b66\u6821\u6709\u4e00\u8f86\u6854\u9ec4\u8272\u7684\u5c0f\u81ea\u884c\u8f66\uff0c\u65e9\u4e0a\u9a91\u8fc7\u6765\u505c\u5728\u5e08\u5927\u4e8c\u62db\uff0c\u65e0\u8bba\u600e\u4e48\u770b\uff0c\u9648\u5983\u84c9\u8fd9\u8f86\u81ea\u884c\u8f66\u90fd\u4e0d\u50cf\u80fd\u627f\u53d7\u4e09\u4e2a\u4eba\u5206\u91cf\u7684\u6837\u5b50\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u8d77\u6765\uff0c\u8981\u5728\u4e1c\u5927\u8282\u7ea6\u811a\u529b\uff0c\u81ea\u884c\u8f66\u5c11\u4e0d\u4e86\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u63a8\u7740\u81ea\u884c\u8f66\uff0c\u966a\u5f20\u606a\u3001\u675c\u98de\u8d70\u56de\u5b66\u6821\uff0c\u4e2d\u5348\u5728\u6559\u5de5\u98df\u5802\u5403\u996d\u2014\u2014\u6559\u5de5\u98df\u5802\u7684\u5c0f\u7092\u7684\u786e\u4e0d\u9519\uff0c\u5728\u5b66\u5e9c\u5df7\u771f\u6b63\u5efa\u8d77\u6765\u4e4b\u524d\uff0c\u6559\u5de5\u98df\u5802\u8fd8\u662f\u4e00\u4e2a\u53ef\u4ee5\u957f\u671f\u4fe1\u8d56\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u7ecf\u8fc7\u5973\u751f\u5bbf\u820d\u697c\u524d\uff0c\u4ee4\u4eba\u8be7\u5f02\u7684\uff0c\u90a3\u516b\u53ea\u6c14\u7403\u8fd8\u7cfb\u5728\u7bee\u7403\u573a\u8fb9\u4e0a\u7684\u94c1\u4e1d\u7f51\u4e0a\uff0c\u5b57\u8ff9\u6709\u4e9b\u51cf\u6de1\uff0c\u4e0d\u8fc7\u8fd8\u80fd\u8fa9\u8bc6\uff0c\u9648\u5983\u84c9\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u662f\u6bcf\u53bb\u4e00\u6b21\u5b54\u5e99\u5c31\u4e70\u51e0\u53ea\u6c14\u7403\u56de\u6765\uff0c\u4e0d\u7528\u591a\u4e45\u5f97\u56f4\u6ee1\u94c1\u4e1d\u7f51\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u4eba\u6765\u4eba\u5f80\u7684\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u52c7\u6c14\u8dd1\u8fc7\u53bb\u5c06\u6c14\u7403\u89e3\u5f00\u653e\u98de\uff0c\u4e0e\u9648\u5983\u84c9\u6325\u4e86\u6325\u624b\uff0c\u770b\u7740\u5979\u56de\u5bbf\u820d\uff0c\u624d\u4e0e\u675c\u98de\u5f80\u7814\u7a76\u751f\u697c\u8d70\u3002</p><p>\u3000\u3000\u201c\u5443\uff0c\u7b97\u662f\u771f\u6b63\u5f00\u59cb\u5927\u5b66\u751f\u6d3b\u4e86\uff0c\u201d\u5f20\u606a\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u7ad9\u5728\u7f51\u7403\u573a\u524d\uff0c\u6293\u7f51\u7403\u573a\u8fb9\u4e0a\u7684\u94c1\u4e1d\u7f51\uff0c\u770b\u7740\u91cc\u9762\u6709\u4e24\u4e2a\u5973\u5b69\u5b50\u7a7f\u7740\u77ed\u88e4\u5728\u6253\u7f51\u7403\uff0c\u867d\u7136\u4e0d\u662f\u90a3\u79cd\u88d9\u89d2\u98de\u626c\u4f1a\u9732\u51fa\u5185\u88e4\u7684\u7f51\u7403\u88d9\uff0c\u5927\u817f\u4e5f\u4e0d\u591f\u767d\u7699\uff0c\u5230\u5e95\u662f\u9752\u6625\u5c11\u5973\u7684\u5927\u817f\uff0c\u5f20\u606a\u62ff\u7740\u4e00\u7f50\u53ef\u4e50\uff0c\u770b\u7740\u7f51\u7403\u573a\u4e0a\u5954\u8dd1\u63a5\u53d1\u7403\u7684\u4eba\u4eec\uff0c\u5fc3\u60f3\u662f\u4e0d\u662f\u53ef\u80fd\u7ea6\u5b59\u9759\u6aac\u8fc7\u6765\u6253\u7f51\u7403\uff0c\u53c8\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u8f6c\u8fc7\u5934\u8ddf\u675c\u98de\u8bf4\uff0c\u201c\u8981\u5728\u8fd9\u91cc\u6e21\u8fc7\u56db\u5e74\u65f6\u5149\uff0c\u4f60\u6709\u4ec0\u4e48\u8ba1\u5212\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u201c\u8ba1\u5212\u554a\uff0c\u201d\u675c\u98de\u5fae\u5fae\u6c89\u541f\uff0c\u8fd9\u5927\u6982\u4e0d\u662f\u4e00\u4e2a\u8f7b\u677e\u7684\u8bdd\u9898\uff0c\u201c\u8ddf\u4f60\u53bb\u9999\u6e2f\uff0c\u53d7\u5230\u5f88\u5927\u7684\u51b2\u51fb\uff0c\u4ee5\u524d\u6ca1\u53d1\u89c9\u81ea\u5df1\u6709\u4ec0\u4e48\u4e0d\u80fd\u505a\u7684\uff0c\u8fd9\u65f6\u5019\u6ca1\u53d1\u89c9\u81ea\u5df1\u6709\u4ec0\u4e48\u80fd\u505a\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u770b\u6765\u675c\u98de\u5df2\u7ecf\u610f\u8bc6\u81ea\u5df1\u5904\u4e8e\u649e\u5899\u671f\u4e86\uff0c\u6709\u7740\u770b\u5c71\u4e0d\u662f\u5c71\u7684\u8ff7\u832b\uff0c\u8bf4\u9053\uff0c\u201c\u6709\u4ef6\u4e8b\uff0c\u6211\u60f3\u8ddf\u4f60\u8bf4\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u6df1\u5733\u90a3\u8fb9\u7684\u80a1\u4efd\uff0c\u6211\u770b\u4f60\u8fd8\u662f\u653e\u5f03\u6389\u597d\u3002\u5f53\u7136\uff0c\u8981\u4e0d\u8981\u653e\u5f03\uff0c\u5168\u5728\u4f60\u3002\u201d</p><p>\u3000\u3000\u5f53\u521d\uff0c\u76db\u9752\u4ed6\u4eec\u641e\u76d7\u7248\u5236\u789f\u673a\uff0c\u675c\u98de\u8ddf\u7740\u53bb\u6da8\u89c1\u8bc6\uff0c\u6240\u8c13\u89c1\u8005\u6709\u4efd\uff0c\u53e3\u5934\u91cc\u4e5f\u8bf4\u7ed9\u4ed6\u5360\u5e72\u80a1\u3002\u8fd9\u4e24\u5e74\u8fc7\u53bb\uff0c\u76db\u9752\u4ed6\u4eec\u4ece\u90a3\u4e2a\u76d7\u7248\u4ea7\u4e1a\u94fe\u91cc\u81f3\u5c11\u6c72\u53d6\u4e24\u4e2a\u4ebf\u7684\u51c0\u5229\u6295\u5230\u4e16\u7eaa\u9526\u6e56\u91cc\u6765\uff0c\u53e3\u5934\u4e0a\u7b54\u5e94\u7ed9\u675c\u98de\u7684\u90a3\u90e8\u5206\u81ea\u7136\u4e5f\u662f\u5341\u5206\u7684\u5de8\u5927\u3002\u5f20\u606a\u5012\u662f\u5e0c\u671b\u675c\u98de\u5c06\u8fd9\u56de\u4e8b\u5fd8\u8bb0\u6389\u5bf9\u4ed6\u66f4\u6709\u597d\u5904\u3002</p><p>\u3000\u3000\u201c\u5443\uff0c\u4e4b\u524d\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u6216\u8bb8\u4f1a\u89c9\u5f97\u81ea\u5df1\u5f97\u90a3\u4efd\u4e5f\u662f\u5e94\u8be5\uff0c\u8fd9\u65f6\u5019\u7b97\u662f\u660e\u767d\u8fc7\u6765\u4e86\uff0c\u4e0d\u662f\u6211\u8be5\u5f97\u7684\uff0c\u201d\u675c\u98de\u54c2\u7136\u4e00\u7b11\uff0c\u4f38\u624b\u4e00\u63e1\uff0c\u201c\u6211\u5bb6\u8001\u5934\u5b50\u5176\u4ed6\u8bdd\u6ca1\u600e\u4e48\u5429\u5490\uff0c\u5c31\u8ddf\u6211\u8bf4\u4e0d\u662f\u81ea\u5df1\u7684\u4e1c\u897f\u5c31\u4e0d\u8be5\u4f38\u624b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u62cd\u4e86\u62cd\u675c\u98de\u7684\u80a9\u8180\uff0c\u675c\u98de\u80fd\u8fd9\u4e48\u60f3\u6700\u597d\u4e86\uff0c\u770b\u89c1\u7f51\u7403\u573a\u5317\u9762\u8d70\u8fdb\u6765\u4e00\u7537\u4e00\u5973\uff0c\u5973\u7684\u770b\u4e0a\u53bb\u76ae\u767d\u8089\u5ae9\uff0c\u9887\u4e3a\u8010\u770b\uff0c\u5f80\u90a3\u8fb9\u770b\u4e86\u4e24\u773c\uff0c\u624d\u5bf9\u675c\u98de\u8bf4\u9053\uff0c\u201c\u603b\u4e4b\uff0c\u4f60\u5728\u8fd9\u91cc\u60f3\u600e\u4e48\u6298\u817e\u90fd\u884c\uff0c\u6211\u4f1a\u652f\u6301\u4f60\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e5f\u8981\u5236\u8ba2\u5546\u4e1a\u8ba1\u5212\uff1f\u201d\u675c\u98de\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u201c\u6709\u4e5f\u884c\uff0c\u6ca1\u6709\u4e5f\u884c\uff0c\u6211\u79c1\u4eba\u7ed9\u4f60\u878d\u8d44\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u5e76\u975e\u6bcf\u4e2a\u4eba\u90fd\u662f\u4ee5\u5199\u5546\u4e1a\u8ba1\u5212\u4e66\u4f5c\u4e3a\u521b\u4e1a\u7684\u542f\u7aef\uff0c\u5f53\u7136\uff0c\u6709\u4e00\u4efd\u8ba1\u5212\u4e66\u80af\u5b9a\u4f1a\u6709\u6240\u5e2e\u52a9\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u6211\u53ef\u80fd\u505a\u4e0d\u4e86\u4ec0\u4e48\u5927\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u6b63\u597d\uff0c\u6211\u8fd9\u8fb9\u672a\u6765\u4e8f\u635f\u989d\u53ef\u80fd\u4f1a\u5c11\u4e00\u4e9b\u3002\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u63a8\u4e86\u675c\u98de\u7684\u80a9\u8180\u4e00\u4e0b\uff0c\u201c\u4e0d\u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u6211\u6b63\u6253\u7b97\u4ee5\u9526\u6e56\u7684\u540d\u4e49\u8d5e\u52a9\u4e00\u4e2a\u9ad8\u6821\u751f\u53c2\u52a0\u7684\u521b\u4e1a\u8ba1\u5212\u5927\u8d5b\uff0c\u53ef\u80fd\u5148\u4ece\u5efa\u90ba\u5e02\u7684\u9ad8\u6821\u505a\u8bd5\u70b9\uff0c\u8d75\u9e4f\u5728\u63a8\u52a8\u76f8\u5f53\u65b9\u9762\u7684\u5de5\u4f5c\u2026\u2026\u5f53\u7136\uff0c\u8fd9\u4e2a\u521b\u4e1a\u8ba1\u5212\u5927\u8d5b\uff0c\u4e0e\u4f60\u60f3\u600e\u4e48\u6298\u817e\u6ca1\u6709\u592a\u5927\u7684\u5173\u7cfb\uff1b\u5c31\u662f\u8ba9\u4f60\u53c2\u4e0e\uff0c\u4e5f\u4e0d\u4f1a\u8ba9\u4f60\u4ee5\u53c2\u8d5b\u8005\u7684\u8eab\u4efd\u53c2\u4e0e\uff0c\u4e0d\u7136\uff0c\u5bf9\u5176\u4ed6\u4eba\u6765\u8bf4\uff0c\u5c31\u592a\u4e0d\u516c\u5e73\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u6211\u8be5\u4ee5\u600e\u6837\u7684\u65b9\u5f0f\u53c2\u4e0e\uff1f\u201d\u675c\u98de\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u95ee\u9898\u4e0d\u8981\u95ee\u6211\uff0c\u4f60\u81ea\u5df1\u7422\u78e8\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u5f20\u606a\u4e0d\u8d1f\u8d23\u7684\u8fd9\u4e48\u8bf4\uff0c\u675c\u98de\u53ef\u5c31\u90c1\u95f7\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5510\u5a67\u5979\u4eec\u4e0b\u5348\u4e00\u70b9\u62b5\u8fbe\u9999\u6e2f\u542f\u5fb7\u673a\u573a\uff0c\u90a3\u8fb9\u5230\u673a\u573a\u8fce\u63a5\u7684\u4eba\u4e0d\u5c11\uff0c\u7535\u8bdd\u91cc\u58f0\u97f3\u70ed\u70ed\u95f9\u95f9\u7684\uff0c\u542c\u5230\u5510\u5a67\u5a07\u67d4\u6e05\u8d8a\u7684\u58f0\u97f3\uff0c\u5fc3\u91cc\u4e0d\u6653\u5f97\u60ac\u7740\u7684\u4ec0\u4e48\u60c5\u7eea\u5c31\u9661\u7136\u677e\u4e0b\u3002</p><p>\u3000\u3000\u7b49\u5348\u4f11\u65f6\u95f4\u8fc7\u53bb\uff0c\u65b0\u751f\u90fd\u8981\u5bbf\u820d\u524d\u5217\u961f\u96c6\u5408\u51c6\u5907\u5230\u64cd\u573a\u53bb\u6328\u64cd\uff0c\u770b\u5230\u9b4f\u4e1c\u5f3a\u3001\u5e2d\u82e5\u7433\u7b49\u4eba\u7ad9\u5728\u5bbf\u820d\u697c\u8d70\u5eca\u91cc\u770b\u5217\u961f\u96c6\u5408\u7684\u60c5\u51b5\uff0c\u5f20\u606a\u4e0e\u675c\u98de\u4e5f\u6ca1\u6709\u6253\u62db\u547c\uff0c\u5c31\u5f80\u5bbf\u820d\u697c\u91cc\u94bb\uff0c\u8dd1\u5230\u4e09\u697c\u7684\u5e74\u7ea7\u529e\u516c\u5ba4\uff0c\u63a8\u5f00\u95e8\u770b\u89c1\u8c22\u5b50\u5609\u5750\u5728\u91cc\u9762\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u8be7\u5f02\u7684\u76ef\u4e86\u5f20\u606a\u4ed6\u4eec\u4e00\u4f1a\u513f\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u5e2d\u82e5\u7433\u8bf4\u7684\u4e24\u4e2a\u6df7\u86cb\u5bb6\u4f19\u5c31\u662f\u4f60\u4eec\u4fe9\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u5979\u6709\u8fd9\u4e48\u8bf4\uff1f\u201d\u5f20\u606a\u5750\u5230\u7a97\u524d\uff0c\u5f80\u697c\u4e0b\u671b\u7740\uff0c\u5e2d\u82e5\u7433\u7ad9\u5728\u8d70\u5eca\u524d\uff0c\u4ece\u8fd9\u91cc\u5f80\u4e0b\u770b\uff0c\u76ee\u8096\u63d2\u7740\u5979\u5149\u6ed1\u7684\u989d\u5934\uff0c\u80fd\u770b\u89c1\u5979\u9876\u8d77\u6765\u7684\u80f8\u53e3\uff0c\u770b\u6765\u8fd8\u4e0d\u6b6236D\uff0c\u542c\u6628\u5929\u591c\u91cc\u7535\u8bdd\u91cc\u7684\u8bed\u6c14\uff0c\u4f3c\u4e4e\u7ed9\u521a\u521a\u7ed9\u67d0\u4eba\u7537\u4eba\u7529\u3002</p><p>\u3000\u3000\u5e74\u7ea7\u529e\u516c\u5ba4\u4f4d\u4e8e\u5bbf\u820d\u697c\u4e09\u5c42\u7684\u9876\u7aef\uff0c\u6bd4\u4e00\u822c\u5bbf\u820d\u7565\u5927\uff0c\u4e34\u5357\u7a97\u6709\u4e24\u5f20\u529e\u516c\u684c\u62fc\u653e\u5728\u4e00\u8d77\uff0c\u9760\u4e1c\u5899\u653e\u7740\u4e00\u53f0\u7535\u8111\uff0c\u4ed6\u4eec\u8fdb\u6765\u65f6\uff0c\u8c22\u5b50\u5609\u6b63\u624b\u6258\u7740\u8111\u888b\u671b\u5929\u8fb9\u7684\u4e91\u5f69\uff0c\u5979\u7684\u684c\u524d\u653e\u5728\u4e00\u53f0\u7b14\u8bb0\u672c\u7535\u8111\uff0c\u60f3\u5fc5\u662f\u5979\u81ea\u5df1\u968f\u8eab\u643a\u5e26\u7684\uff1b\u9694\u58c1\u8fd8\u6709\u5e74\u7ea7\u6d3b\u52a8\u5ba4\uff0c\u5236\u4f5c\u6240\u8c13\u7684\u7b80\u62a5\uff0c\u5e94\u8be5\u5728\u9694\u58c1\u7684\u6d3b\u52a8\u5ba4\u8fdb\u884c\u3002</p><p>\u3000\u3000\u675c\u98de\u89c1\u9760\u5899\u58c1\u7684\u90a3\u53f0\u7535\u8111\u6253\u5f00\u7740\uff0c\u9000\u51fa\u5c4f\u4fdd\uff0c\u626b\u96f7\u6e38\u620f\u6b63\u8fdb\u884c\u5230\u4e00\u534a\uff0c\u4ed6\u4fbf\u63a5\u7740\u73a9\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u201c\u5443\uff0c\u201d\u8c22\u5b50\u5609\u7ea4\u7ec6\u767d\u7699\u5982\u74f7\u5668\u7684\u80f3\u81c2\u653e\u5728\u529e\u516c\u684c\u4e0a\uff0c\u6574\u4e2a\u8eab\u5b50\u503e\u8fc7\u6765\uff0c\u671b\u7740\u5f20\u606a\uff0c\u201c\u6211\u4eec\u4ee5\u540e\u7b97\u662f\u540c\u5b66\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u7b97\u662f\u5427\u3002\u201d\u5f20\u606a\u5fc3\u60f3\u8ddf\u8fd9\u4e2b\u5934\u4e5f\u6ca1\u6709\u4ec0\u4e48\u6069\u6028\u53ef\u8a00\uff0c\u4e0d\u6653\u5f97\u5979\u8981\u8bf4\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u201c\u6cf0\u94e2\u6c47\u7387\u8fd8\u4f1a\u8fdb\u4e00\u6b65\u4e0b\u632b\uff0c\u4e0d\u8fc7\u6cf0\u94e2\u4e0a\u5df2\u7ecf\u6ca1\u6709\u592a\u5927\u7684\u72e9\u730e\u673a\u4f1a\uff0c\u4e0b\u4e00\u6ce2\u4f1a\u662f\u97e9\u56fd\u8fd8\u662f\u53f0\u6e7e\uff1f\u4e00\u822c\u8bf4\u6765\uff0c\u8fdb\u4e0d\u53bb\u5176\u56fd\u5185\u7684\u91d1\u878d\u5e02\u573a\uff0c\u5728\u56fd\u9645\u5916\u6c47\u5e02\u573a\u4e0a\uff0c\u72e9\u730e\u54ea\u4e00\u56fd\u8d27\u5e01\u7684\u628a\u63e1\u6700\u5927\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5f20\u606a\u7728\u4e86\u7728\u773c\uff0c\u54ea\u91cc\u60f3\u5230\u8fd9\u59ae\u5b50\u4e00\u89c1\u9762\u5c31\u95ee\u8fd9\u4e2a\u95ee\u9898\uff0c\u90fd\u8bf4\u8fd9\u59ae\u5b50\u662f\u602a\u80ce\uff0c\u770b\u6765\u534a\u70b9\u90fd\u88c5\u4e0d\u5047\uff0c\u675c\u98de\u5750\u5728\u4e00\u65c1\u542c\u4e86\u624b\u90fd\u6296\u4e86\u4e00\u4e0b\uff0c\u8e29\u51fa\u96f7\u4e0d\u5f97\u4e0d\u91cd\u5f00\u4e00\u76d8\uff0c\u5f20\u606a\u53ea\u662f\u88c5\u75f4\u5f04\u50bb\uff0c\u201c\u542c\u4e0d\u660e\u767d\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0d\u80af\u8bf4\u62c9\u5012\uff0c\u201d\u8c22\u5b50\u5609\u9f3b\u5b50\u54fc\u4e86\u54fc\uff0c\u58f0\u97f3\u5374\u53c8\u8f6f\u4e86\u4e00\u4e0b\uff0c\u201c\u53ea\u662f\u8ba8\u8bba\u8ba8\u8bba\u4e5f\u4e0d\u884c\uff1f\u201d</p><p>\u3000\u3000\u201c\u542c\u4e0d\u61c2\u554a\uff0c\u600e\u4e48\u8ba8\u8bba\uff1f\u4f60\u5728\u8bf4\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u644a\u4e86\u644a\u624b\uff0c\u4e00\u822c\u4eba\u7edd\u770b\u4e0d\u51fa\u4ed6\u8138\u4e0a\u7591\u60d1\u795e\u60c5\u7684\u7834\u7efd\uff0c\u53c8\u95ee\u8c22\u5b50\u5609\uff0c\u201c\u4f60\u600e\u4e48\u5750\u5728\u8fd9\u91cc\uff0c\u4e0d\u7528\u53c2\u52a0\u519b\u8bad\uff1f\u201d</p><p>\u3000\u3000\u201c\u5c31\u8bb8\u4f60\u4eec\u5f97\u7f8e\u5c14\u5c3c\u65af\u7efc\u5408\u75c7\uff1f\u201d</p><p>\u3000\u3000\u770b\u6765\u8c22\u5b50\u5609\u4e0a\u5348\u5df2\u7ecf\u4ece\u5e2d\u82e5\u7433\u90a3\u91cc\u77e5\u9053\u8db3\u591f\u591a\u7684\u4e1c\u897f\uff0c\u4e0d\u6653\u5f97\u5979\u6709\u6ca1\u6709\u5c06\u81ea\u5df1\u5356\u5e72\u51c0\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u5230\u697c\u4e0b\u7684\u961f\u5217\u5f80\u64cd\u573a\u884c\u8fdb\uff0c\u63d0\u9192\u4e86\u675c\u98de\u4e00\u58f0\uff0c\u675c\u98de\u9a6c\u4e0a\u5c31\u9000\u51fa\u6e38\u620f\uff0c\u7ad9\u5230\u7a97\u6237\u8fd9\u8fb9\u770b\u529e\u516c\u684c\u4e0a\u7684\u6821\u520a\u3002</p><p>\u3000\u3000\u9664\u4e86\u5b66\u672f\u671f\u520a\u4e4b\u5916\uff0c\u4e1c\u6d77\u5927\u5b66\u8fd8\u529e\u4e86\u4e24\u4efd\u6821\u520a\uff0c\u4e00\u4efd\u662f\u4e1c\u6d77\u5927\u5b66\u62a5\uff0c\u5bf9\u5916\u6b63\u5f0f\u53d1\u884c\u7684\uff0c\u6709\u4e13\u804c\u7684\u5de5\u4f5c\u4eba\u5458\uff0c\u4e00\u4efd\u662f\u9752\u5e74\u56e2\uff0c\u662f\u6821\u56e2\u59d4\u7ec4\u7ec7\u5b66\u751f\u9aa8\u5e72\u5185\u90e8\u53d1\u884c\uff0c\u4e4b\u5916\u8fd8\u6709\u5404\u7c7b\u9662\u520a\u3001\u7cfb\u520a\u3002</p><p>\u3000\u3000\u9b4f\u4e1c\u5f3a\u4ed6\u4eec\u8d70\u8fdb\u6765\uff0c\u9664\u4e86\u9b4f\u4e1c\u5f3a\uff0c\u5176\u4ed6\u4e09\u4f4d\u4e3b\u4efb\u52a9\u7406\u90fd\u8fd8\u662f\u5927\u56db\u7684\u5b66\u751f\uff0c\u9b4f\u4e1c\u5f3a\u4ece\u529e\u516c\u684c\u4e0b\u62ff\u4e86\u7b14\u8bb0\u672c\uff0c\u8981\u53bb\u9662\u529e\u5f00\u4f1a\uff0c\u4ed6\u521a\u8d70\u5230\u95e8\u53e3\uff0c\u8170\u95f4\u7684\u4f20\u547c\u673a\u5c31\u54cd\u4e86\uff0c\u4ed6\u660e\u663e\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u8fd8\u662f\u7ad9\u5728\u95e8\u53e3\u4ece\u8170\u95f4\u53d6\u4e0b\u4f20\u547c\u673a\u770b\u4e86\u4e00\u4e0b\uff0c\u4ed6\u5c06\u4f20\u547c\u673a\u653e\u56de\u53bb\uff0c\u8d70\u56de\u6765\u62ff\u8d77\u529e\u516c\u684c\u4e0a\u7684\u7535\u8bdd\uff0c\u624b\u6307\u843d\u7684\u6309\u952e\u4e0a\uff0c\u5374\u505c\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c9\u5f97\u5947\u602a\uff0c\u4ed6\u662f\u8981\u6253\u7535\u8bdd\u8fd8\u662f\u4e0d\u8981\u6253\u7535\u8bdd\uff0c\u8fd8\u662f\u987e\u5fcc\u529e\u516c\u5ba4\u91cc\u4eba\u591a\u4e0d\u65b9\u4fbf\u6253\u8fd9\u4e2a\u7535\u8bdd\uff1f\u6ca1\u60f3\u5230\u9b4f\u4e1c\u5f3a\u8fdf\u7591\u4e86\u4e00\u4f1a\u513f\u53c8\u638f\u51fa\u4f20\u547c\u673a\u7ffb\u770b\u4e86\u4e00\u904d\uff0c\u8eab\u4f53\u5374\u6070\u6070\u6321\u4f4f\u5f20\u606a\u7684\u76ee\u5149\u3002\u5f20\u606a\u53ea\u770b\u5230\u8c22\u5b50\u5609\u4e0d\u7ecf\u610f\u95f4\u4f20\u9012\u6765\u4e00\u4e2a\u63f6\u63c4\u610f\u5473\u7684\u773c\u8272\uff0c\u660e\u767d\u4e86\uff1a\u539f\u6765\u9b4f\u4e1c\u5f3a\u6ca1\u8bb0\u4f4f\u7535\u8bdd\u53f7\u7801\uff0c\u5374\u53c8\u4e0d\u5e0c\u671b\u7ed9\u5f20\u606a\u770b\u89c1\u4ed6\u90a3\u53ea\u7684\u5bfb\u547c\u673a\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u5728\u9b4f\u4e1c\u5f3a\u7684\u80cc\u540e\u5fae\u5fae\u7b11\u7740\uff0c\u9b4f\u4e1c\u5f3a\u54ea\u6015\u6293\u4f4f\u5bfb\u547c\u673a\u53ea\u9732\u51fa\u4e00\u53ea\u89d2\uff0c\u4ed6\u4e5f\u80fd\u77e5\u9053\u8fd9\u53ea\u5bfb\u547c\u673a\u53ea\u662f\u8054\u4fe1\u65e9\u671f\u7684\u4ea7\u54c1\uff0c\u6ca1\u6709\u6c49\u663e\uff0c\u800c\u5e02\u573a\u4e0a\u65e9\u5c31\u4e0d\u518d\u6709\u5356\uff0c\u9b4f\u4e1c\u5f3a\u4ece\u54ea\u91cc\u6dd8\u6765\u7684\u4e8c\u624b\u8d27\u5427\u3002</p><p>\u3000\u3000\u6709\u4e9b\u4e8b\u60c5\uff0c\u522b\u4eba\u4e0d\u4f1a\u4ecb\u610f\uff0c\u504f\u504f\u662f\u4ed6\u4eec\u81ea\u5df1\u4ecb\u610f\u3002</p><p>\u3000\u3000\u9b4f\u4e1c\u5f3a\u6253\u4e86\u7535\u8bdd\u5c31\u8d70\u4e86\uff0c\u9664\u4e86\u5e2d\u82e5\u7433\uff0c\u5176\u4ed6\u4e24\u4e2a\u4e3b\u4efb\u52a9\u7406\u8fd8\u8981\u8d76\u53bb\u4e0a\u8bfe\u3002</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u60f3\u5fc5\u4e5f\u77e5\u9053\u591a\u51b7\u82e5\u51b0\u971c\u7684\u795e\u8272\uff0c\u5bf9\u5f20\u606a\u3001\u675c\u98de\u4e24\u4eba\u6765\u8bf4\uff0c\u6ca1\u6709\u4ec0\u4e48\u7528\u5904\uff0c\u4e0d\u8fc7\u751f\u6027\u4f7f\u7136\uff0c\u4e5f\u662f\u6ca1\u6709\u627e\u5230\u53f0\u9636\u53ef\u4e0b\uff0c\u773c\u795e\u4f9d\u65e7\u4e0d\u5584\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u4f60\u4eec\u6709\u8c01\u4f1a\u5199\u6bdb\u7b14\u5b57\uff1f\u201d</p><p>\u3000\u3000\u201c\u90fd\u4f1a\u4e00\u70b9\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002\u675c\u98de\u5199\u5f97\u6bd4\u4ed6\u597d\u4e00\u4e9b\uff0c\u8fd9\u4e3b\u8981\u5f97\u76ca\u4e8e\u4ed6\u5f53\u9ad8\u4e2d\u6559\u5e08\u7684\u5988\u5988\u4ece\u5c0f\u903c\u7740\u4ed6\u5199\u5927\u5b57\uff0c\u4e00\u76f4\u575a\u6301\u5230\u9ad8\u4e2d\uff1b\u5f20\u606a\u5c0f\u65f6\u5019\u4e5f\u6709\u7ec3\u8fc7\uff0c\u4e0d\u8fc7\u5f88\u65e9\u5c31\u4e22\u4e0b\u4e86\u3002\u5927\u5b66\u91cc\uff0c\u5b66\u751f\u4f1a\u91cc\u6700\u5403\u9999\u7684\u4e00\u9879\u6280\u80fd\u5c31\u662f\u4f1a\u5199\u5927\u5b57\u62a5\uff0c\u5f20\u606a\u81ea\u8c13\u5e94\u4ed8\u5927\u5b57\u62a5\u4ed6\u8fd8\u6ca1\u6709\u95ee\u9898\u3002</p><p>\u3000\u3000\u201c\u90a3\u5c31\u597d\uff0c\u201d\u5e2d\u82e5\u7433\u8138\u8272\u7f13\u548c\u4e86\u4e00\u4e0b\uff0c\u201c\u7b49\u4f1a\u513f\u8fd9\u91cc\u6709\u51e0\u5f20\u6d77\u62a5\u4f60\u4eec\u5e2e\u7740\u5199\u4e00\u4e0b\uff0c\u5199\u597d\u4e4b\u540e\uff0c\u6211\u5e26\u4f60\u4eec\u53bb\u4e00\u98df\u5802\u90a3\u9762\u7684\u6d77\u62a5\u5899\u8d34\u4e00\u4e0b\uff0c\u4ee5\u540e\u4f60\u4eec\u5728\u8fd9\u91cc\u7684\u4e3b\u8981\u4efb\u52a1\u5c31\u662f\u534f\u52a9\u505a\u5e74\u7ea7\u529e\u7684\u5ba3\u4f20\u5de5\u4f5c\uff0c\u5236\u5b9a\u519b\u8bad\u7b80\u62a5\u5c55\u677f\uff0c\u8fd8\u6709\u5c31\u662f\u5ba1\u9605\u6bcf\u5929\u7684\u519b\u8bad\u5fc3\u5f97\uff0c\u6311\u4e00\u4e9b\u597d\u7684\u6587\u7ae0\u5236\u4f5c\u5230\u7b80\u62a5\u5c55\u677f\u4e0a\u6216\u6295\u5230\u6821\u7535\u89c6\u53f0\u3001\u6821\u5e7f\u64ad\u7ad9\u3001\u6821\u62a5\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u4e1c\u5927\u65b0\u751f\u519b\u8bad\u8fd8\u6709\u4e00\u4e2a\u53e4\u602a\u7684\u4f20\u7edf\uff0c\u5c31\u662f\u65b0\u751f\u5fc5\u987b\u6bcf\u5929\u90fd\u4ea4\u4e00\u4efd\u4e0d\u5c11\u4e8e\u4e94\u767e\u5b57\u7684\u519b\u8bad\u5fc3\u5f97\u3002\u4ec5\u8fd9\u4e00\u9879\uff0c\u4e1c\u5927\u4e5d\u4e03\u5e74\u5171\u6709\u4e94\u5343\u540d\u672c\u79d1\u65b0\u751f\uff0c\u56db\u5468\u7684\u519b\u8bad\u4e0b\u6765\uff0c\u81f3\u5c11\u8981\u4ea7\u751f\u5341\u4e8c\u4e09\u4e07\u7684\u5e9f\u7eb8\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u5199\u6d77\u62a5\uff0c\u5f20\u606a\u5012\u4e5f\u4e0d\u6392\u65a5\uff0c\u5c31\u5f53\u662f\u590f\u65e5\u5348\u540e\u7ec3\u7ec3\u6bdb\u7b14\u5b57\u4fee\u8eab\u517b\u6027\u3002</p><p>\u3000\u3000\u5728\u95e8\u7a97\u6d1e\u5f00\u7684\u6d3b\u52a8\u5ba4\uff0c\u6e05\u98ce\u5f90\u98ce\uff0c\u5f20\u606a\u7167\u7740\u5e2d\u82e5\u7433\u62ff\u7ed9\u4ed6\u7684\u7a3f\u5b50\u8a8a\u5199\u5230\u6d77\u62a5\u7eb8\u4e0a\uff0c\u4ed6\u660e\u77e5\u9053\u7a3f\u5b50\u4e0a\u7684\u5b57\u662f\u5e2d\u82e5\u7433\u5199\u7684\uff0c\u8fd8\u4e0d\u5fd8\u8bf4\u4e00\u53e5\uff1a\u201c\u8fd9\u5b57\u8c01\u5199\u7684\uff1f\u597d\u4e11\u3002\u201d</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u60f3\u8981\u4e0d\u52a8\u58f0\u8272\uff0c\u5374\u5fcd\u4e0d\u4f4f\u6ee1\u8138\u7f9e\u7ea2\uff0c\u62ff\u7740\u8336\u676f\u5047\u88c5\u53bb\u9694\u58c1\u7684\u529e\u516c\u5ba4\u5012\u6c34\uff0c\u79bb\u5f00\u8ba9\u5979\u5c34\u5c2c\u4e0d\u5df2\u7684\u7a7a\u95f4\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u50cf\u770b\u602a\u7269\u4f3c\u7684\u770b\u7740\u5f20\u606a\uff1a\u201c\u4f60\u600e\u4e48\u5c31\u80af\u5b9a\u662f\u5979\u5199\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u5c31\u80af\u5b9a\u6211\u521a\u624d\u4e0d\u662f\u65e0\u610f\u95f4\u8bf4\u9519\u8bdd\u4e86\uff1f\u201d\u5f20\u606a\u95ee\u8c22\u5b50\u5609\u3002</p><p>\u3000\u3000\u201c\u4e0d\u613f\u610f\u8bf4\u62c9\u5012\u3002\u201d\u8c22\u5b50\u5609\u4e5f\u53ea\u80fd\u5982\u6b64\u4e00\u8bf4\uff0c\u5979\u4f4e\u5934\u5199\u5979\u7684\u6d77\u62a5\u53bb\u4e86\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u4e5f\u5c06\u8fd9\u91cc\u5f53\u6210\u9003\u907f\u519b\u8bad\u7684\u5e87\u62a4\u6240\uff0c\u8ddf\u7740\u5f20\u606a\u3001\u675c\u98de\u4ed6\u4eec\u6162\u60a0\u60a0\u7684\u5c06\u6d77\u62a5\u5199\u597d\uff0c\u5199\u5b8c\u4e4b\u540e\uff0c\u8fd8\u6311\u51fa\u6765\u6bd4\u6bd4\u8c01\u7684\u5b57\uff0c\u8bc4\u5934\u8bba\u8db3\u4e00\u756a\uff0c\u624d\u5230\u9694\u58c1\u529e\u516c\u5ba4\u627e\u5e2d\u82e5\u7433\u5e26\u4ed6\u4eec\u51fa\u53bb\u627e\u5730\u65b9\u5f20\u8d34\u3002</p><p>\u3000\u3000\u4e0b\u5348\u7684\u5929\u7a7a\u53c8\u6674\u6717\u8d77\u6765\uff0c\u6bd4\u8d77\u90a3\u4e9b\u5728\u4e09\u5341\u4e94\u5ea6\u9ad8\u6e29\u5929\u6c14\u3001\u9876\u7740\u706b\u8fa3\u8fa3\u7684\u6bd2\u592a\u9633\u5728\u64cd\u573a\u53cd\u590d\u8d70\u6b63\u6b65\u7684\u65b0\u751f\u4eec\uff0c\u5f20\u606a\u4ed6\u4eec\u7684\u751f\u6d3b\u53ef\u8981\u6ecb\u6da6\u591a\u4e86\u3002\u5f20\u8d34\u5b8c\u6d77\u62a5\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u8ddf\u5e2d\u82e5\u7433\u56de\u5e74\u7ea7\u529e\u7684\u610f\u601d\uff0c\u76f4\u63a5\u8ddf\u5979\u8bf4\uff1a\u201c\u6211\u4eec\u8fd8\u6709\u5f88\u591a\u50cf\u7f51\u7403\u62cd\u4e4b\u7c7b\u7684\u4e1c\u897f\u6ca1\u6709\u4e70\uff0c\u5e74\u7ea7\u529e\u8981\u6709\u4ec0\u4e48\u4e8b\uff0c\u4f60\u76f4\u63a5\u6253\u675c\u98de\u7684\u624b\u673a\uff0c\u6211\u4eec\u5c31\u5148\u8d70\u4e86\u2026\u2026\u201d\u62ff\u51fa\u7b14\u5728\u521a\u521a\u8d34\u4e0a\u53bb\u7684\u6d77\u62a5\u4e00\u89d2\u5199\u4e0b\u4e00\u4e32\u53f7\u7801\uff0c\u6495\u4e0b\u6765\u9012\u7ed9\u5e2d\u82e5\u7433\uff0c\u201c\u8981\u4e0d\u7b49\u4f1a\u513f\u4e00\u8d77\u6253\u7f51\u7403\uff1f\u201d</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u8003\u8651\u5230\u5927\u6982\u65e0\u8bba\u591a\u4e25\u5389\u7684\u8bed\u6c14\u4e5f\u53ea\u662f\u8ba9\u81ea\u5df1\u9677\u5165\u66f4\u5c34\u5c2c\u7684\u5883\u5730\uff0c\u4fbf\u7d22\u6027\u4e0d\u8bf4\u4ec0\u4e48\uff0c\u51b7\u8273\u7740\u4e00\u5f20\u8138\u63d0\u7740\u6d46\u7cca\u6876\u4e0e\u8c22\u5b50\u5609\u4e00\u8d77\u56de\u5e74\u7ea7\u529e\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u5f53\u771f\u8981\u8d76\u65f6\u95f4\u4e0e\u675c\u98de\u53bb\u6821\u5916\u7684\u8f66\u884c\u5404\u9009\u8d2d\u4e00\u8f86\u5c71\u5730\u8f66\uff0c\u9664\u4e86\u7f51\u7403\u62cd\u4e4b\u5916\uff0c\u8fd8\u6709\u8bb8\u591a\u96f6\u788e\u7684\u4e1c\u897f\u8981\u4e70\uff0c\u56de\u5230\u5b66\u6821\u542c\u8bf4\u8096\u6625\u94ed\u7b2c\u4e00\u5929\u5c31\u4e2d\u6691\u5012\u5728\u64cd\u573a\uff0c\u8dd1\u5230\u6821\u533b\u9662\u770b\u5230\u4ed6\u8eba\u5728\u75c5\u5e8a\u4e0a\u6302\u6c34\uff0c\u9664\u4e86\u8138\u8272\u6709\u4e9b\u82cd\u767d\u4e4b\u5916\uff0c\u5012\u6ca1\u6709\u522b\u7684\u4e0d\u59a5\uff0c\u5f20\u606a\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u8180\uff0c\u95ee\u4ed6\uff1a\u201c\u4e0d\u662f\u6545\u610f\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u53ef\u80fd\uff1f\u4e00\u5934\u683d\u571f\u7599\u7629\u4e0a\uff0c\u5934\u8fd8\u8d77\u4e00\u4e2a\u5305\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u7b2c\u4e00\u5929\u4e2d\u6691\uff0c\u603b\u6bd4\u519b\u8bad\u7ed3\u675f\u65f6\u4e2d\u6691\u597d\uff0c\u4f60\u4e0d\u4f1a\u8fd8\u8981\u575a\u6301\u53bb\u53c2\u52a0\u519b\u8bad\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u7684\u601d\u60f3\u89c9\u609f\u8fd8\u6ca1\u6709\u90a3\u4e48\u9ad8\uff0c\u4e0d\u7136\u5934\u4e0d\u662f\u767d\u8d77\u5305\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4ee5\u540e\u6709\u8096\u6625\u94ed\u5728\u5e74\u7ea7\u529e\u9876\u7740\uff0c\u66f4\u65b9\u4fbf\u4ed6\u4e0e\u675c\u98de\u5728\u5916\u9762\u6df7\u65e5\u5b50\uff0c\u4e0d\u51fa\u51e0\u5929\uff0c\u56e0\u4e3a\u8eab\u4f53\u7b49\u5404\u79cd\u539f\u56e0\u5230\u5e74\u7ea7\u529e\u6df7\u65e5\u5b50\u7684\u4eba\u4f1a\u8d8a\u6765\u8d8a\u591a\uff0c\u90a3\u4e9b\u5973\u751f\u4f8b\u5047\u671f\u4e5f\u53ef\u4ee5\u8bf7\u5047\u4e0d\u53c2\u52a0\u519b\u8bad\uff0c\u53ea\u662f\u7b2c\u4e00\u5929\u8fd8\u6ca1\u6709\u4eba\u610f\u8bc6\u5230\u8fd9\u70b9\u7f62\u4e86\u3002</p><p>\u3000\u3000\u89c1\u8096\u6625\u94ed\u6ca1\u4ec0\u4e48\u5927\u788d\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u4ed6\u5973\u670b\u53cb\u5f20\u857e\u4ece\u5e08\u5927\u8d76\u8fc7\u6765\uff0c\u5f20\u606a\u4fbf\u4e0e\u675c\u98de\u5148\u79bb\u5f00\u6821\u533b\u9662\u53bb\u6253\u4e24\u573a\u7403\u70ed\u70ed\u8eab\u3002</p><p>\u3000\u3000\u665a\u996d\u524d\u63a5\u5230\u9648\u4fe1\u751f\u7684\u7535\u8bdd\uff0c\u8bf4\u662f\u6a61\u6811\u56ed\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u7ba1\u7406\u4e2d\u5fc3\u6536\u5230\u51e0\u767e\u4efd\u878d\u8d44\u5165\u56ed\u7533\u8bf7\uff0c\u5df2\u7ecf\u521d\u6b65\u7b5b\u9009\u51fa\u5341\u4e8c\u4efd\u5546\u4e1a\u8ba1\u5212\u4e66\uff0c\u90fd\u662f\u5e0c\u671b\u6a61\u6811\u56ed\u80fd\u63d0\u4f9b\u878d\u8d44\u7684\uff0c\u521b\u6295\u7ba1\u7406\u4e2d\u5fc3\u7684\u5de5\u4f5c\u4eba\u5458\u4e5f\u5df2\u7ecf\u8ddf\u8fd9\u5341\u4e8c\u4efd\u5546\u4e1a\u8ba1\u5212\u7684\u63d0\u4ea4\u4eba\u63a5\u89e6\u8fc7\uff0c\u9648\u4fe1\u751f\u5e0c\u671b\u7531\u5f20\u606a\u6700\u7ec8\u6765\u9009\u62e9\u7b2c\u4e00\u6279\u5165\u56ed\u7684\u4f01\u4e1a\u540d\u5355\u3002</p><p>\u3000\u3000\u867d\u7136\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u7684\u7ba1\u7406\u56e2\u961f\u662f\u76f4\u63a5\u5411\u9648\u4fe1\u751f\u8d1f\u8d23\u7684\uff0c\u9648\u4fe1\u751f\u5012\u662f\u5e0c\u671b\u5f20\u606a\u80fd\u4eb2\u81ea\u53c2\u4e0e\u8fd9\u4e00\u5757\uff0c\u6bd5\u7adf\u521b\u6295\u4e0e\u98ce\u6295\u7684\u6982\u5ff5\u6ca1\u6709\u592a\u5927\u7684\u5dee\u5f02\uff0c\u9700\u8981\u6781\u5176\u654f\u9510\u7684\u5e02\u573a\u76ee\u5149\u4e0e\u5224\u65ad\u529b\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u65b9\u9762\uff0c\u5f20\u606a\u4e00\u5411\u7ed9\u522b\u4eba\u5f88\u5f3a\u7684\u4fe1\u5fc3\u3002</p><p>\u3000\u3000\u5f20\u606a\u62b1\u6028\u9053\uff1a\u201c\u603b\u628a\u6700\u6b8b\u9177\u7684\u4e8b\u60c5\u7559\u7ed9\u6211\u505a\u3002\u201d</p><p>\u3000\u3000\u201c\u8d75\u6893\u7433\u4e00\u76f4\u62b1\u6028\u672a\u6709\u673a\u4f1a\u8ddf\u606a\u5c11\u4f60\u8fdb\u884c\u5145\u5206\u7684\u4ea4\u6d41\uff0c\u6211\u8ba9\u5979\u5c06\u8d44\u6599\u7ed9\u4f60\u9001\u53bb\uff1f\u201d\u9648\u4fe1\u751f\u5728\u7535\u8bdd\u95ee\u3002</p><p>\u3000\u3000\u8d75\u6893\u7433\u5728\u65e5\u672c\u3001\u65b0\u52a0\u5761\u6709\u53c2\u52a0\u8fd0\u8425\u9ad8\u79d1\u6280\u4f01\u4e1a\u5b75\u5316\u56ed\u7684\u7ecf\u9a8c\uff0c\u4e09\u5341\u56db\u5c81\uff0c\u9648\u4fe1\u751f\u5728\u65b0\u52a0\u5761\u5bfb\u627e\u521b\u6295\u57fa\u91d1\u7ba1\u7406\u56e2\u961f\u65f6\uff0c\u901a\u8fc7\u730e\u5934\u516c\u53f8\u627e\u5230\u5979\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u4e0d\u662f\u4e0d\u91cd\u89c6\u8fd9\u4e00\u5757\uff0c\u53ea\u662f\u90a3\u6bb5\u65f6\u95f4\u4ed6\u7ed9\u53f6\u5efa\u658c\u6293\u53bb\u9999\u6e2f\uff0c\u53ea\u5728\u9999\u6e2f\u4e0e\u8d75\u6893\u7433\u89c1\u8fc7\u4e00\u9762\uff0c\u5403\u8fc7\u4e00\u6b21\u996d\uff0c\u89c9\u5f97\u8fd9\u5973\u4eba\u884c\uff0c\u5c31\u51b3\u5b9a\u8058\u8bf7\u5979\uff0c\u7531\u5979\u7387\u9886\u4e00\u4e2a\u4e94\u4eba\u56e2\u961f\u52a0\u76df\u9526\u6e56\u7684\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u4e3b\u8981\u8d1f\u8d23\u521b\u4e1a\u56ed\u53ca\u521b\u6295\u57fa\u91d1\u7684\u8fd0\u8425\u4e0e\u7ba1\u7406\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u51b3\u65ad\uff0c\u5728\u8d75\u6893\u7433\u770b\u8fc7\uff0c\u6709\u4e9b\u8349\u7387\u7684\u610f\u5473\uff0c\u800c\u504f\u504f\u5f20\u606a\u8fd9\u6bb5\u65f6\u95f4\u7684\u7cbe\u529b\u4e3b\u8981\u7ed9\u5bf9\u51b2\u57fa\u91d1\u3001\u6676\u5706\u5382\u6295\u8d44\u9879\u76ee\u8fd8\u6709\u4ed6\u79c1\u4eba\u7684\u4e8b\u60c5\u7f20\u4f4f\uff0c\u8fd8\u6ca1\u6709\u65f6\u95f4\u4e0e\u8d75\u6893\u7433\u8fdb\u884c\u5145\u5206\u7684\u4ea4\u6d41\u3002</p><p>\u3000\u3000\u5f20\u606a\u4fbf\u8ba9\u9a6c\u6d77\u9f99\u5c06\u8d75\u6893\u7433\u9886\u8fc7\u6765\uff0c\u4ed6\u8bf7\u5979\u5728\u6559\u5de5\u98df\u5802\u5403\u665a\u996d\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8d75\u6893\u7433\u770b\u5230\u7a7f\u7740\u8fd0\u52a8\u77ed\u88e4\u4e0eT\u6064\u3001\u62ff\u7740\u7f51\u7403\u62cd\u521a\u521a\u5267\u70c8\u8fd0\u52a8\u8fc7\u3001\u5934\u53d1\u80e1\u4e71\u5728\u81ea\u6765\u6c34\u9f99\u5934\u51b2\u6d17\u8fc7\u7684\u5f20\u606a\u51fa\u73b0\u5728\u9762\u524d\uff0c\u5934\u8111\u6709\u4e9b\u53d1\u80c0\uff0c\u5979\u4ee5\u540e\u8981\u5e94\u4ed8\u7684\u8001\u677f\u5e76\u4e0d\u662f\u90a3\u79cd\u5faa\u89c4\u8e48\u77e9\u7684\u5929\u624d\u6027\u4eba\u7269\uff0c\u770b\u4ed6\u8fd9\u5e45\u6a21\u6837\uff0c\u4f3c\u4e4e\u4e0d\u4f1a\u4e3a\u4e0b\u5c5e\u592a\u591a\u7684\u8d23\u4efb\u3002</p><p>\u3000\u3000\u6240\u8c13\u5927\u9690\u9690\u4e8e\u5e02\uff0c\u8c01\u80fd\u76f8\u4fe1\u8fd9\u4e48\u4e00\u4e2a\u795e\u60c5\u60eb\u61d2\u7684\u9ad8\u6821\u9752\u5e74\u638c\u63e1\u7740\u6570\u5341\u4ebf\u7684\u5de8\u989d\u8d22\u5bcc\uff1f</p><p>\u3000\u3000\u201c\u606a\u5c11\u8fd8\u771f\u662f\u60a0\u95f2\u5462\uff1f\u201d</p><p>\u3000\u3000\u89c1\u9762\u673a\u4f1a\u4e0d\u591a\uff0c\u8d75\u6893\u7433\u5012\u4e5f\u5b66\u522b\u4eba\u79f0\u4ed6\u606a\u5c11\u3002</p><p>\u3000\u3000\u201c\u9648\u4fe1\u751f\u6253\u7535\u8bdd\u7ed9\u6211\u665a\u4e86\uff0c\u4e0d\u7136\u6211\u5c31\u8bf7\u4f60\u4e00\u8d77\u6253\u7f51\u7403\u4e86\u2026\u2026\u201d\u5f20\u606a\u5c06\u7f51\u7403\u62cd\u4e22\u684c\u5b50\u4e0a\uff0c\u95ee\u8d75\u6893\u7433\u5e0c\u671b\u5403\u4ec0\u4e48\uff0c\u4ed6\u8ddf\u675c\u98de\u53bb\u70b9\u83dc\u3002</p><p>\u3000\u3000\u5403\u60ef\u9ad8\u7ea7\u9910\u5385\uff0c\u5076\u5c14\u5230\u5927\u5b66\u6821\u56ed\u91cc\u6765\u5403\u4e00\u5403\u98df\u5802\u4e5f\u662f\u86ee\u6709\u8da3\u5473\u7684\uff0c\u8d75\u6893\u7433\u5fc3\u91cc\u53ea\u80fd\u8fd9\u4e48\u60f3\u3002</p><p>\u3000\u3000\u6559\u5de5\u98df\u5802\u5411\u6765\u90fd\u4e0d\u62e5\u6324\uff0c\u5f20\u606a\u4ed6\u4eec\u5360\u636e\u4e86\u89d2\u843d\u7684\u4e00\u5f20\u957f\u684c\u5b50\uff0c\u5012\u4e5f\u4e0d\u6101\u522b\u4eba\u4f1a\u8dd1\u8fc7\u6765\u59a8\u788d\u4ed6\u4eec\u8c08\u8bdd\u3002\u9b4f\u4e1c\u5f3a\u3001\u5e2d\u82e5\u7433\u7b49\u51e0\u4e2a\u5e74\u7ea7\u4e3b\u4efb\u8fd8\u6709\u6797\u51b0\u8fdb\u6765\u5403\u996d\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u770b\u5230\u5f20\u606a\u90fd\u8fdc\u8fdc\u7684\u9009\u4e86\u53e6\u5916\u4e00\u5904\u89d2\u843d\u5403\u996d\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u8fc7\u6765\u6253\u6270\u7684\u610f\u601d\u3002\u6797\u51b0\u9887\u4e3a\u5c34\u5c2c\uff0c\u56e0\u4e3a\u9b4f\u4e1c\u5f3a\u7684\u5173\u7cfb\uff0c\u5979\u5012\u4e5f\u4e0d\u4fbf\u4e3b\u52a8\u5f20\u606a\u6253\u62db\u547c\uff0c\u53ea\u662f\u52c9\u5f3a\u7684\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u5f20\u606a\u6d51\u4e0d\u4ecb\u610f\uff0c\u53ea\u662f\u542c\u8d75\u6893\u7433\u503e\u8bc9\u5979\u5bf9\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u4e0e\u521b\u4e1a\u56ed\u8fd0\u8425\u7684\u4e00\u4e9b\u60f3\u6cd5\u3002</p><p>\u3000\u3000\u6a61\u6811\u56ed\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u7684\u989d\u5ea6\u4e00\u6b21\u6027\u5c31\u8fbe\u5230\u4e09\u4e2a\u4ebf\uff0c\u662f\u76ee\u524d\u56fd\u5185\u89c4\u6a21\u6700\u5927\u7684\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u2014\u2014\u4e5d\u4e03\u5e74\u56fd\u5185\u7531\u5730\u65b9\u653f\u5e9c\u4e3b\u5bfc\u800c\u6210\u7acb\u7684\u521b\u6295\u57fa\u91d1\u89c4\u6a21\u5343\u4e07\u7ea7\u7684\u5c31\u6ca1\u6709\u51e0\u5bb6\uff0c\u800c\u5168\u56fd\u4e0d\u6653\u5f97\u6709\u51e0\u5343\u51e0\u4e07\u4e2a\u6280\u672f\u7814\u53d1\u9879\u76ee\u7531\u4e8e\u7f3a\u4e4f\u8db3\u591f\u7684\u8d44\u91d1\u652f\u6301\u800c\u65e0\u6cd5\u6301\u7eed\u4e0b\u53bb\uff0c\u4e5f\u4e0d\u6653\u5f97\u6709\u51e0\u5343\u51e0\u4e07\u4e2a\u6280\u672f\u6210\u679c\u7f3a\u4e4f\u5546\u5316\u6027\u8f6c\u5316\u7684\u6e20\u9053\u3002\u5c31\u662f\u5efa\u90ba\u5e02\u4e09\u5341\u4e03\u516b\u6240\u9ad8\u6821\u3001\u5dee\u4e0d\u591a\u540c\u7b49\u89c4\u6a21\u7684\u79d1\u7814\u9662\u6240\u4e5f\u79ef\u7d2f\u4e86\u5927\u91cf\u5f85\u5f00\u53d1\u7684\u3001\u9700\u8981\u6301\u7eed\u5f00\u53d1\u7684\u6216\u6709\u4e00\u5b9a\u6210\u679c\u9700\u8981\u8fdb\u884c\u5546\u4e1a\u8f6c\u5316\u7684\u6280\u672f\u9879\u76ee\uff0c\u4e5f\u6ca1\u6709\u5927\u8086\u5ba3\u626c\uff0c\u6a61\u6811\u56ed\u521b\u6295\u4e2d\u5fc3\u6b63\u5f0f\u6302\u724c\u524d\u5c31\u63a5\u5230\u6570\u767e\u4efd\u7533\u8bf7\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u81ea\u5df1\u4e0d\u53ef\u80fd\u4e00\u4e0b\u5b50\u5c31\u5c06\u6240\u6709\u7684\u4e8b\u60c5\u505a\u5b8c\uff0c\u6a61\u6811\u56ed\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u4e3b\u8981\u652f\u6301\u5fae\u7535\u5b50\u6280\u672f\u9886\u57df\u6838\u5fc3\u6280\u672f\u7684\u5b55\u80b2\u4e0e\u6280\u672f\u521b\u65b0\uff0c\u5f20\u606a\u751a\u81f3\u62b1\u7740\u5b81\u7f3a\u52ff\u6ee5\u7684\u4fe1\u5ff5\uff0c\u5bf9\u7b2c\u4e00\u6279\u5165\u56ed\u7684\u4f01\u4e1a\u53ea\u7ed9\u51fa\u4e94\u4e2a\u673a\u4f1a\u3002</p><p>\u3000\u3000\u8d75\u6893\u7433\u4e0e\u5979\u7684\u56e2\u961f\u5c31\u662f\u8981\u4ece\u6570\u4ee5\u767e\u8ba1\u3001\u6570\u4ee5\u5343\u8ba1\u7684\u7533\u8bf7\u91cc\u6311\u9009\u6700\u6709\u6f5c\u529b\u4e0e\u5e02\u573a\u524d\u666f\u7684\u6280\u672f\u9879\u76ee\u4e88\u4ee5\u6276\u6301\u4e0e\u6295\u8d44\u3002</p></article>"}}}]);
