(window.webpackJsonp=window.webpackJsonp||[]).push([[616],{639:function(p){p.exports={chapterTitle:null,title:"\u7b2c466\u7ae0 \u845b\u666f\u8bda\u53bb\u4e16",content:"<article><h2>\u7b2c466\u7ae0 \u845b\u666f\u8bda\u53bb\u4e16</h2><p>\u3000\u3000\u79d1\u738b\u7535\u5668\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u5341\u4e8c\u6708\u4e8c\u65e5\u6b63\u5f0f\u7ed3\u675f\uff0c\u53ca\u65f6\u8c03\u6574\u5e94\u5bf9\u7b56\u7565\uff0c\u5728\u5a92\u4f53\u773c\u91cc\uff0c\u4ed6\u4eec\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u4e5f\u53d6\u5f97\u5706\u6ee1\u7684\u6210\u529f\u3002</p><p>\u3000\u3000\u539f\u6709\u7684\u3001\u7c97\u7cd9\u7684\u7ecf\u9500\u5546\u4f53\u7cfb\u5f97\u5230\u5347\u7ea7\uff0c\u901a\u8fc7\u4ece\u7ecf\u9500\u5546\u90a3\u91cc\u6536\u53d6\u4fdd\u8bc1\u91d1\u7684\u5f62\u5f0f\uff0c\u79d1\u738b\u7535\u5668\u5dee\u4e0d\u591a\u80fd\u52df\u96c6\u4e00\u4ebf\u4e24\u5343\u4e07\u4ee5\u4e0a\u7684\u8d44\u91d1\uff1b\u5f53\u7136\uff0c\u8fd9\u4e00\u6570\u989d\u8981\u8fdc\u8fdc\u5c11\u4e8e\u8c22\u5251\u5357\u3001\u8d75\u9526\u8363\u7b49\u4eba\u4e4b\u524d\u7684\u671f\u5f85\uff0c\u4ec5\u4ec5\u80fd\u586b\u8865\u4e0b\u4e00\u9636\u6bb5\u7684\u8d44\u91d1\u7a7a\u7f3a\uff0c\u5404\u5bb6\u80a1\u4e1c\u4e4b\u524d\u586b\u8fdb\u53bb\u7684\u8d44\u91d1\u662f\u65e0\u6cd5\u62bd\u8eab\u51fa\u6765\u3002\u79d1\u738b\u7684\u5404\u5bb6\u80a1\u4e1c\u4e4b\u4e2d\uff0c\u6b63\u6cf0\u3001\u6d77\u6cf0\u3001\u5927\u96c5\u90fd\u6ca1\u6709\u8d44\u91d1\u4e0a\u7684\u538b\u529b\uff0c\u9526\u57ce\u5c31\u6709\u4e9b\u591f\u545b\uff0c\u8d75\u9526\u8363\u4e5f\u53ea\u80fd\u5403\u95f7\u5934\u4e8f\uff0c\u4ed6\u8fd8\u4e0d\u80fd\u8ba9\u8c22\u5bb6\u501f\u6b64\u673a\u4f1a\u6e17\u5165\u9526\u57ce\u7684\u5730\u4ea7\u4e1a\u52a1\u4e2d\u6765\u3002</p><p>\u3000\u3000\u5341\u4e00\u6708\uff0c\u4e2d\u592e\u7535\u89c6\u53f0\u8054\u5408\u5168\u56fd\u5a92\u4f53\u5bf9\u592e\u89c6\u6807\u738b\u5e7f\u544a\u7684\u7092\u4f5c\uff0c\u6781\u5927\u7684\u4fc3\u8fdb\u4e86\u79d1\u738b\u789f\u673a\u7684\u9500\u552e\uff0c\u6839\u636e\u6700\u65b0\u7ed3\u7b97\u51fa\u6765\u7684\u6570\u636e\uff0c\u5341\u4e00\u6708\u9500\u552e\u91cf\u5c31\u7a81\u7834\u4e09\u4e07\u53f0\uff0c\u592e\u89c6\u6807\u738b\u7684\u5e7f\u544a\u6548\u5e94\u518d\u4e00\u6b21\u8bc1\u660e\u4e0d\u5bb9\u5ffd\u89c6\uff1b\u867d\u7136\u4e0e\u7231\u8fbe\u7535\u5b50\u4e8c\u5341\u4e8c\u4e07\u53f0\u7684\u6708\u9500\u91cf\u51a0\u519b\u65e0\u6cd5\u6bd4\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e5f\u6e05\u695a\uff0c\u5373\u4f7f\u79d1\u738b\u7684\u9500\u91cf\u80fd\u4e0a\u53bb\uff0c\u5355\u673a\u76c8\u5229\u4e5f\u8fdc\u8fdc\u65e0\u6cd5\u8ddf\u7231\u8fbe\u76f8\u63d0\u5e76\u8bba\uff0c\u9664\u4e86\u5236\u9020\u6210\u672c\u4e0a\u6709\u4e9b\u52a3\u52bf\u5916\uff0c\u8425\u9500\u6210\u672c\u4e5f\u5c06\u8fdc\u8fdc\u9ad8\u8fc7\u7231\u8fbe\u7535\u5b50\uff1a\u4e24\u5bb6\u5728\u4e2d\u592e\u7535\u89c6\u53f0\u6295\u7684\u5e7f\u544a\u8d39\u5c31\u76f8\u5dee\u4e24\u4ebf\u591a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u80fd\u770b\u5230\u79d1\u738b\u5728\u67d0\u4e9b\u65b9\u9762\u7684\u52aa\u529b\uff0c\u5fc3\u60f3\u8c22\u5251\u5357\u5927\u6982\u4e5f\u60f3\u5229\u7528\u8fd9\u4e00\u5e74\u7684\u65f6\u95f4\u5e03\u5c40\uff0c\u5230\u4e5d\u516b\u5e74\u518d\u8003\u8651\u4e0e\u7231\u8fbe\u7535\u5b50\u76f4\u63a5\u7ade\u4e89\u7684\u95ee\u9898\uff0c\u6b64\u65f6\u901a\u8fc7\u79ef\u6781\u5f00\u62d3\u4e1c\u5357\u4e9a\u5e02\u573a\u6765\u7f29\u77ed\u4e0e\u7231\u8fbe\u7535\u5b50\u4e4b\u95f4\u7684\u5dee\u8ddd\uff0c\u4e0d\u5f97\u4e0d\u627f\u8ba4\uff0c\u8fd9\u662f\u4e00\u4e2a\u806a\u660e\u7684\u7b56\u7565\uff0c\u5f20\u606a\u5374\u4e0d\u65e0\u6076\u610f\u7684\u60f3\uff1a\u8c22\u5251\u5357\u722c\u8fc7\u4e00\u5ea7\u5c71\u5cf0\uff0c\u53d1\u73b0\u524d\u9762\u8fd8\u6709\u4e00\u5ea7\u66f4\u9ad8\u7684\u5c71\u6a2a\u5728\u9762\u524d\uff0c\u5fc3\u91cc\u4f1a\u662f\u4ec0\u4e48\u6ecb\u5473\uff1f</p><p>\u3000\u3000\u4e5f\u8981\u627f\u8ba4\u8c22\u5bb6\u4eba\u8109\u5173\u7cfb\u5f88\u6df1\u539a\uff0c\u79d1\u738b\u60f3\u4ee5\u9999\u6e2f\u4e3a\u8df3\u677f\u5f00\u62d3\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u5e02\u573a\uff0c\u90a3\u4ed6\u4eec\u4f1a\u627e\u8c01\u5408\u4f5c\uff1f</p><p>\u3000\u3000\u60f3\u5230\u8fd9\u4e2a\u95ee\u9898\uff0c\u7b2c\u4e00\u4e2a\u8e66\u5230\u5f20\u606a\u5ff5\u5934\u91cc\u7684\u4eba\u5c31\u662f\u845b\u836b\u5747\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u4e0e\u8c22\u5251\u5357\u5728\u5251\u6865\u7559\u5b66\u671f\u95f4\u7684\u6821\u53cb\uff0c\u79c1\u8c0a\u4e0d\u9519\u3002</p><p>\u3000\u3000\u845b\u5bb6\u540d\u4e0b\uff0c\u5728\u6d88\u8d39\u7535\u5b50\u7c7b\u9886\u57df\uff0c\u9664\u4e86\u5609\u4fe1\u5b9e\u4e1a\u4e4b\u5916\uff0c\u8fd8\u6709\u5609\u4fe1\u7535\u5b50\u96c6\u56e2\uff0c\u5609\u4fe1\u7535\u5b50\u4e0e\u5609\u4fe1\u5b9e\u4e1a\u4e4b\u95f4\u4e1a\u52a1\u5206\u5de5\u5f88\u660e\u786e\uff0c\u4f46\u5230\u845b\u660e\u4fe1\u63a7\u5236\u5609\u4fe1\u7535\u5b50\u4e4b\uff0c\u5f00\u59cb\u4e89\u593a\u5609\u4fe1\u5b9e\u4e1a\u5728\u5c0f\u5bb6\u7535\u65b9\u9762\u7684\u4e1a\u52a1\u3001\u6324\u538b\u5609\u4fe1\u5b9e\u4e1a\u7684\u5e02\u573a\uff0c\u8fd9\u79cd\u5bb6\u65cf\u5185\u90e8\u7684\u4e89\u6597\uff0c\u624d\u5f7b\u5e95\u4f7f\u5609\u4fe1\u5b9e\u4e1a\u6ca6\u4e3a\u4e0d\u5165\u6d41\u7684\u4e0a\u5e02\u516c\u53f8\uff0c\u6bd4\u8d77\u5609\u4fe1\u5b9e\u4e1a\u7684\u51c4\u60e8\u2014\u2014\u5e02\u503c\u4e00\u5ea6\u8dcc\u81f3\u4e00\u4ebf\u6e2f\u5143\u4ee5\u4e0b\u2014\u2014\u5e02\u503c\u8fbe\u516b\u5341\u591a\u4ebf\u6e2f\u5143\u7684\u9999\u6e2f\u5609\u4fe1\u7535\u5b50\u7b97\u5f97\u4e0a\u5e9e\u7136\u5de8\u7269\uff0c\u5728\u8ba1\u7b97\u673a\u3001\u6d88\u8d39\u7535\u5b50\u7b49\u9ad8\u79d1\u6280\u5173\u952e\u96f6\u914d\u4ef6\u4e0e\u7cfb\u7edf\u9886\u57df\uff0c\u6709\u7740\u76f8\u5f53\u96c4\u539a\u7684\u6280\u672f\u5b9e\u529b\uff0c\u5e76\u5728\u6df1\u5733\u5efa\u6709\u79d1\u6280\u5de5\u4e1a\u56ed\u533a\u3002</p><p>\u3000\u3000\u9664\u4e86\u8ba1\u5212\u5f00\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u5e02\u573a\uff0c\u79d1\u738b\u4e0d\u662f\u8fd8\u65b0\u8bbe\u7acb\u4e86\u7814\u53d1\u90e8\u5417\uff1f\u4ed6\u4eec\u4e00\u5f00\u59cb\u4e5f\u5c06\u662f\u7814\u53d1\u789f\u673a\u7684\u7cfb\u7edf\u63a7\u5236\u5427\uff0c\u5c06\u6240\u6709\u63a7\u5236\u7ec4\u4ef6\u96c6\u6210\u5230\u5927\u82af\u7247\u4e0a\uff0c\u662f\u666e\u904d\u789f\u673a\u5382\u5546\u80fd\u770b\u5230\u7684\u672a\u6765\u8d8b\u52bf\uff0c\u4f46\u8fd9\u53c8\u4e0d\u662f\u79d1\u738b\u51ed\u501f\u81ea\u8eab\u7684\u6280\u672f\u529b\u91cf\u6216\u8005\u4ec5\u5411\u6b63\u6cf0\u96c6\u56e2\u6c42\u63f4\u5c31\u80fd\u7acb\u9a6c\u89e3\u51b3\u7684\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e4b\u524d\u662f\u4e0d\u53ef\u80fd\u8003\u8651\u8ddf\u5609\u4fe1\u7535\u5b50\u5408\u4f5c\uff0c\u9996\u5148\u662f\u5bf9\u5e02\u573a\u5ba3\u4f20\u624b\u6bb5\u8fc7\u4e8e\u91cd\u89c6\uff0c\u5176\u6b21\u8fd8\u62c5\u5fc3\u5609\u4fe1\u7535\u5b50\u7684\u5f3a\u52bf\uff0c\u4f1a\u5f71\u54cd\u5230\u8c22\u5bb6\u5bf9\u79d1\u738b\u7684\u63a7\u5236\u6743\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u60f3\uff1a\u8c22\u5bb6\u8fd9\u65f6\u5019\u4f1a\u6709\u4ec0\u4e48\u9009\u62e9\uff1f</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6d77\u5dde\u5546\u4e1a\u533a\u4e3b\u8981\u96c6\u4e2d\u5728\u524d\u95e8\u4e00\u5e26\uff0c\u4e5d\u5341\u5e74\u4ee3\u4e2d\u671f\u4e4b\u95f4\uff0c\u8fd9\u4e00\u7247\u7684\u9ad8\u5c42\u5efa\u7b51\u6781\u5c11\uff0c\u8fd1\u51e0\u5e74\u65b0\u5efa\u7684\u51e0\u680b\u9ad8\u697c\uff0c\u867d\u8bf4\u662f\u9ad8\u697c\uff0c\u5374\u591a\u662f\u5341\u591a\u5c42\uff0c\u8d85\u8fc7\u4e8c\u5341\u5c42\u7684\u9ad8\u697c\u90fd\u6ca1\u6709\u4e00\u680b\uff0c\u7ad9\u5728\u4e09\u5341\u5c42\u7684\u5e9c\u5929\u5927\u53a6\u9876\u4e0a\u5927\u6982\u4f1a\u6709\u4e00\u89c8\u4f17\u697c\u5c0f\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u5927\u53a6\u697c\u9876\u7684\u98ce\u5f88\u5927\uff0c\u9648\u9759\u6709\u4e9b\u8f7b\u5fae\u7684\u6050\u9ad8\u75c7\uff0c\u4f46\u662f\u5de5\u4f5c\u5f97\u8111\u888b\u53d1\u80c0\u4e4b\u65f6\uff0c\u5979\u90fd\u4e60\u60ef\u5230\u697c\u9876\u6765\u5439\u4e00\u4e0b\u98ce\uff0c\u5979\u63a8\u5f00\u4e0a\u5929\u53f0\u7684\u5c0f\u95e8\uff0c\u770b\u89c1\u8c22\u5251\u5357\u5df2\u7ecf\u7ad9\u5728\u4e0a\u9762\u8ddf\u522b\u4eba\u901a\u7535\u8bdd\uff0c\u624b\u91cc\u8fd8\u5939\u7740\u70df\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e5f\u4e0d\u662f\u6765\u4f53\u4f1a\u4e00\u89c8\u4f17\u697c\u5c0f\u7684\u611f\u89c9\uff0c\u6709\u5f62\u7684\u9ad8\u5ea6\u5f88\u5bb9\u6613\u8d85\u8d8a\uff0c\u4f46\u662f\u5728\u4ed6\u9762\u524d\u6a2a\u7740\u4e00\u5ea7\u65e0\u5f62\u7684\u9ad8\u5c71\uff0c\u4ed6\u90fd\u4e0d\u77e5\u9053\u90a3\u5ea7\u5c71\u6709\u591a\u9ad8\u3002</p><p>\u3000\u3000\u9648\u9759\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u8c22\u5251\u5357\u8f6c\u8fc7\u8eab\u770b\u89c1\u5979\uff0c\u62db\u624b\u8ba9\u5979\u8fc7\u53bb\uff0c\u6342\u7740\u624b\u673a\u901a\u8bdd\u5b54\u8ddf\u9648\u9759\uff1a\u201c\u4e8c\u54e5\u7684\u7535\u8bdd\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5b50\u5609\u7684\u54e5\u54e5\uff1f\u201d\u9648\u9759\u53ea\u7528\u53e3\u5f62\u6765\u8868\u793a\u8fd9\u51e0\u4e2a\u5b57\uff0c\u4eca\u5e74\u624d\u4e09\u5341\u56db\u5c81\u7684\u8c22\u610f\uff0c\u5728\u8c22\u5bb6\u5144\u59b9\u4e4b\u95f4\u6392\u884c\u7b2c\u4e8c\uff0c\u662f\u8c22\u6c49\u9756\u7684\u513f\u5b50\uff0c\u65e9\u5c31\u6b63\u6cf0\u96c6\u56e2\u7684\u6838\u5fc3\u4eba\u7269\uff0c\u8c22\u5251\u5357\u4e13\u5fc3\u8d1f\u8d23\u79d1\u738b\uff0c\u6d77\u6cf0\u7684\u4e8b\u52a1\u5c31\u8f6c\u4ea4\u7ed9\u8c22\u610f\u5904\u7406\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u70b9\u70b9\u5934\uff0c\u53c8\u8bf4\u4e86\u51e0\u53e5\u8bdd\u5c31\u5c06\u7535\u8bdd\u6302\u6389\uff1a\u201c\u4e8c\u54e5\u521a\u5728\u7535\u8bdd\u91cc\u8bf4\uff1a\u6628\u5929\u76db\u946b\u4e0e\u65b0\u534e\u90fd\u96c6\u56e2\u8c08\u6210\u5408\u4f5c\uff0c\u613f\u610f\u638f2.6\u4ebf\u5c06\u65b0\u5e02\u8857\u65b0\u534e\u90fd\u5927\u53a6\u88d9\u697c\u52a0\u4e00\u5c42\u529e\u516c\u697c\u7269\u4e1a\u5403\u4e0b\u53bb\u5f00\u8bbe\u4ed6\u4eec\u5728\u7701\u5185\u7684\u7b2c\u4e8c\u5bb6\u5356\u573a\u2026\u2026\u201d</p><p>\u3000\u3000\u76db\u946b\u5728\u4e1c\u6d77\u5f00\u8bbe\u7684\u7b2c\u4e00\u5bb6\u5356\u573a\u5c31\u662f\u4f4d\u4e8e\u897f\u57ce\u5e02\u7684\u897f\u57ce\u5bb6\u7535\u5356\u573a\uff0c\u4e5f\u662f\u76db\u946b\u8fdb\u5165\u5bb6\u7535\u8fde\u9501\u4e1a\u7684\u7b2c\u4e00\u6b21\u8bd5\u6c34\uff0c\u8bd5\u6c34\u6210\u529f\u540e\uff0c\u4e5d\u4e94\u5e74\u79cb\u540e\uff0c\u76db\u946b\u7535\u5668\u5374\u51fa\u4eba\u610f\u6599\u7684\u8f6c\u6218\u4e0a\u6d77\u3001\u6d59\u6c5f\uff0c\u5c06\u7701\u5185\u7684\u5e02\u573a\u8ba9\u7ed9\u6d77\u6cf0\u3002\u76db\u946b\u7535\u5668\u5229\u7528\u4e00\u5e74\u591a\u7684\u65f6\u95f4\uff0c\u4e0e\u4e0a\u6d77\u3001\u6d59\u6c5f\u5f53\u5730\u7684\u5bb6\u7535\u7ecf\u8425\u5546\u5408\u4f5c\uff0c\u8fde\u7eed\u5f00\u4e86\u5341\u4e94\u5bb6\u95e8\u5e97\uff0c\u5f00\u5e97\u901f\u5ea6\u8981\u6bd4\u6d77\u6cf0\u5feb\u4e86\u8fd1\u4e00\u500d\uff1b\u6d77\u6cf0\u7535\u5668\u662f\u4eca\u5e74\u521d\u624d\u8fdb\u5165\u6d77\u5dde\u7684\uff0c\u4e0d\u8fc7\u51ed\u501f\u76f8\u5bf9\u4f18\u8d8a\u7684\u5730\u7406\u73af\u5883\u4e0e\u89c4\u6a21\uff0c\u5f88\u5feb\u5c31\u4e0e\u897f\u57ce\u5356\u573a\u5e73\u5206\u79cb\u8272\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u77e5\u9053\u76db\u946b\u603b\u6709\u4e00\u5929\u8fd8\u662f\u4f1a\u6253\u56de\u7701\u5185\u5e02\u573a\u7684\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u9648\u9759\u80cc\u7740\u98ce\u800c\u7ad9\uff0c\u53d1\u4e1d\u8ba9\u5bd2\u51c9\u7684\u98ce\u5439\u4e71\uff0c\u201c\u4f60\u4eec\u4e4b\u524d\u4e0d\u662f\u62c5\u5fc3\u76db\u946b\u4f1a\u501f\u7740\u673a\u4f1a\u6b65\u6b65\u8fdb\u903c\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u63a5\u5230\u8c22\u610f\u7684\u7535\u8bdd\uff0c\u662f\u6709\u4e9b\u5947\u602a\uff0c\u201d\u8c22\u5251\u5357\u5c06\u98ce\u8863\u9886\u5b50\u7ad6\u8d77\u6765\uff0c\u6321\u4f4f\u98ce\u704c\u8fdb\u8116\u5b50\u91cc\uff0c\u4ed6\u4eba\u7ad9\u5728\u9648\u9759\u7684\u4fa7\u5934\uff0c\u5e2e\u5979\u906e\u4f4f\u4e9b\u98ce\uff0c\u8fd9\u79cd\u4e0d\u9732\u75d5\u8ff9\u7684\u7ec6\u5fae\u5904\u7684\u4f53\u8d34\uff0c\u5f88\u80fd\u8ba8\u5973\u5b69\u5b50\u6b22\u5fc3\uff0c\u201c\u53f6\u5efa\u658c\u8fd9\u6bb5\u65f6\u95f4\u4f3c\u4e4e\u5f88\u6709\u95f2\u65f6\u95f4\u5728\u6d77\u5dde\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u5728\u7231\u8fbe\u7ecf\u9500\u5546\u5927\u4f1a\u7ed3\u675f\u4e4b\u540e\uff0c\u4eba\u8fd8\u7559\u6d77\u5dde\u4e09\u56db\u5929\u7684\u65f6\u95f4\uff0c\u4ed6\u662f\u60f3\u8d81\u5b59\u9759\u9999\u5728\u6d77\u5dde\u5de5\u4f5c\u7684\u65f6\u673a\u591a\u966a\u4f34\u5979\u51e0\u5929\uff0c\u8c22\u5251\u5357\u4e0e\u9648\u9759\u81ea\u7136\u4e0d\u6e05\u695a\u8fd9\u91cc\u9762\u7684\u4e1c\u897f\uff0c\u5f88\u81ea\u7136\u7684\u5c06\u7126\u70b9\u90fd\u6307\u5411\u5f20\u606a\uff0c\u4ee5\u4e3a\u53f6\u5efa\u658c\u7559\u5728\u6d77\u5dde\u8fd9\u4e9b\u5929\u90fd\u8ddf\u5f20\u606a\u8eb2\u5728\u80cc\u540e\u7b97\u8ba1\u4ec0\u4e48\u3002\u56e0\u4e3a\u76db\u946b\u662f\u5728\u53f6\u5efa\u658c\u4ece\u6d77\u5dde\u56de\u53bb\u540e\u624d\u4e0b\u5b9a\u51b3\u5fc3\u6536\u8d2d\u65b0\u534e\u90fd\u5927\u697c\u88d9\u697c\u7269\u6765\u7684\u3002</p><p>\u3000\u3000\u9648\u9759\u4e5f\u80fd\u660e\u767d\u8c22\u5251\u5357\u7591\u60e7\u8c01\uff0c\u60f3\u8d77\u90a3\u9762\u660e\u4fca\u6302\u7740\u65e0\u5bb3\u7b11\u5bb9\u7684\u8138\uff0c\u7adf\u4f1a\u8e29\u5239\u8f66\u7528\u529b\u8fc7\u731b\u5d34\u5230\u811a\uff0c\u9648\u9759\u4e0d\u81ea\u89c9\u7684\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u7b11\u4ec0\u4e48\uff1f\u201d\u8c22\u5251\u5357\u5947\u602a\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u6211\u6709\u5728\u7b11\u5417\uff1f\u201d\u9648\u9759\u6478\u4e86\u6478\u81ea\u5df1\u7684\u5634\u89d2\uff0c\u6709\u4e9b\u4e8b\u60c5\u4e0d\u597d\u89e3\u91ca\uff0c\u4e5f\u89e3\u91ca\u4e0d\u6e05\uff0c\u90a3\u5929\u8fd8\u662f\u8c22\u5251\u5357\u4f53\u8c05\u8ba9\u81ea\u5df1\u5148\u4ece\u6587\u5c71\u996d\u5e97\u56de\u6765\u4f11\u606f\uff0c\u81ea\u5df1\u5374\u966a\u5f20\u606a\u5728\u533b\u9662\u803d\u6401\u5230\u5341\u70b9\u591a\uff1b\u5927\u6982\u5f20\u606a\u4e5f\u4e0d\u60f3\u4ed6\u8e29\u5239\u8f66\u7528\u529b\u5d34\u5230\u811a\u7684\u4e8b\u60c5\u7ed9\u522b\u4eba\u77e5\u9053\u5427\uff0c\u201c\u6211\u5728\u60f3\u76db\u946b\u7684\u4e8b\u60c5\u8ba9\u4eba\u5f88\u96be\u7422\u78e8\u554a\uff0c\u4e0d\u8fc7\u4f60\u4eec\u4e0d\u6b63\u62c5\u5fc3\u76db\u946b\u4f1a\u5728\u7701\u5185\u5927\u4e3e\u6269\u5f20\u5417\uff1f\u73b0\u5728\u597d\u4e86\uff0c\u4ed6\u4eec\u8981\u5728\u65b0\u5e02\u8857\u6574\u90a3\u4e48\u5927\u7684\u5e97\u9762\uff0c\u6ca1\u6709\u4e00\u5e74\u7684\u65f6\u95f4\uff0c\u53ea\u6015\u5f88\u96be\u6709\u522b\u7684\u5927\u52a8\u4f5c\uff01\u201d</p><p>\u3000\u3000\u201c\u8c01\u77e5\u9053\u4ed6\u4eec\u5728\u600e\u4e48\u60f3\uff0c\u6216\u8bb8\u4ed6\u4eec\u4e5f\u611f\u5230\u5feb\u901f\u6269\u5f20\u8ba9\u6574\u4e2a\u516c\u53f8\u7684\u7ba1\u7406\u4f53\u7cfb\u3001\u7269\u6d41\u4f53\u7cfb\u3001\u552e\u540e\u670d\u52a1\u4f53\u7cfb\u7b49\u7b49\u90fd\u627f\u53d7\u7740\u6781\u5927\u7684\u538b\u529b\uff0c\u4ed6\u4eec\u4e5f\u9700\u8981\u65f6\u95f4\u8c03\u6574\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u7b79\u96c6\u4e09\u4ebf\u591a\u7684\u8d44\u91d1\uff0c\u53ef\u4ee5\u503e\u529b\u6253\u9020\u4e00\u5bb6\u65d7\u8230\u5e97\uff0c\u4e5f\u53ef\u4ee5\u904d\u5730\u5f00\u82b1\uff0c\u5168\u9762\u6269\u5f20\u7701\u5185\u5e02\u573a\uff0c\u4f46\u662f\u4e24\u79cd\u4e0d\u540c\u7684\u65b9\u6848\uff0c\u5bf9\u516c\u53f8\u7684\u7ba1\u7406\u3001\u7269\u6d41\u3001\u552e\u540e\u7b49\u4f53\u7cfb\u6240\u9020\u6210\u7684\u538b\u529b\u662f\u6709\u5f88\u5927\u5dee\u522b\u7684\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e4b\u524d\u5f88\u62c5\u5fc3\u53f6\u5efa\u658c\u4f1a\u5927\u8086\u653b\u57ce\u63a0\u571f\u62a2\u593a\u7701\u5185\u5bb6\u7535\u8fde\u9501\u5e02\u573a\uff0c\u76db\u946b\u65e2\u7136\u638f\u51fa\u5de8\u8d44\u62ff\u4e0b\u65b0\u534e\u90fd\u5927\u697c\u7684\u88d9\u697c\u7269\u4e1a\uff0c\u8fd9\u4e48\u8bf4\u8fd9\u4e00\u6bb5\u65f6\u95f4\u91cc\u5c31\u4e0d\u7528\u62c5\u5fc3\u76db\u946b\u8fd8\u8981\u591a\u5927\u7684\u52a8\u4f5c\uff0c\u4f46\u662f\u8fd9\u5c42\u62c5\u5fc3\u6ca1\u6709\u4e86\uff0c\u5176\u4ed6\u62c5\u5fc3\u5374\u8ddf\u7740\u6d6e\u73b0\u51fa\u6765\uff1a\u201c\u76db\u946b\u6216\u8bb8\u60f3\u501f\u6d77\u6cf0\u4e5f\u65e0\u529b\u6269\u5f20\u7684\u65f6\u673a\u8fdb\u884c\u5185\u90e8\u8c03\u6574\uff0c\u4f46\u662f\u4ed6\u4eec\u4e0d\u4f1a\u6c38\u8fdc\u8c03\u6574\u4e0b\u53bb\u7684\uff0c\u53f6\u5efa\u658c\u8fd9\u6b21\u5728\u6d77\u5dde\u8fd9\u4e48\u957f\u65f6\u95f4\uff0c\u8bf4\u4e0d\u5b9a\u9526\u6e56\u6709\u53ef\u80fd\u5411\u76db\u946b\u6ce8\u8d44\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f1a\u5417\uff1f\u201d\u9648\u9759\u7591\u60d1\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u5f88\u6709\u53ef\u80fd\u7684\uff0c\u201d\u8c22\u5251\u5357\u62c9\u7740\u9648\u9759\u7684\u624b\u8fdb\u4e86\u697c\u68af\u95f4\uff0c\u5916\u9762\u98ce\u592a\u5927\uff0c\u201c\u6211\u4eec\u8fd9\u6b21\u6ca1\u80fd\u5a01\u80c1\u5230\u7231\u8fbe\u7684\u5e02\u573a\u5730\u4f4d\uff0c\u7231\u8fbe\u655b\u96c6\u8d22\u5bcc\u7684\u901f\u5ea6\u53c8\u592a\u60ca\u4eba\u4e86\uff0c\u7b49\u4ed6\u4eec\u7684\u673a\u82af\u5de5\u5382\u6295\u4ea7\uff0c\u5229\u6da6\u7387\u66f4\u662f\u5413\u4eba\uff0c\u6211\u60f3\u5728\u4e0d\u4e45\u7684\u672a\u6765\uff0c\u5f20\u606a\u4ee5\u9526\u6e56\u7684\u540d\u4e49\u5411\u76db\u946b\u6ce8\u8d44\u5e76\u4e0d\u662f\u4ec0\u4e48\u8ba9\u4eba\u610f\u5916\u7684\u4e8b\u60c5\u3002\u53e6\u5916\uff0c\u4ed6\u4eec\u4e4b\u95f4\u7684\u5173\u7cfb\u5e94\u8be5\u5f88\u4eb2\u5bc6\u5427\u2026\u2026\u7559\u7ed9\u6211\u4eec\u7684\u65f6\u95f4\u4e0d\u7b97\u592a\u591a\uff0c\u5149\u51ed\u6211\u4eec\u81ea\u5df1\u7684\u52aa\u529b\uff0c\u4e00\u5b9a\u4f1a\u7ed9\u4ed6\u4eec\u8fdc\u8fdc\u7684\u7529\u5728\u80cc\u540e\uff0c\u4e8c\u54e5\u60f3\u5c06\u6d77\u6cf0\u7684\u603b\u90e8\u8fc1\u5230\u7701\u57ce\u53bb\uff0c\u62c9\u6211\u53bb\u9009\u5730\u65b9\uff0c\u6211\u6b63\u597d\u7ea6\u845b\u836b\u5747\u5728\u7701\u57ce\u78b0\u9762\uff0c\u4f60\u8981\u4e0d\u8981\u4e00\u8d77\u8fc7\u53bb\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u8fb9\u600e\u4e48\u529e\uff1f\u201d\u9648\u9759\u7741\u7740\u773c\u775b\u95ee\u3002</p><p>\u3000\u3000\u201c\u5c31\u8d70\u4e00\u4e24\u5929\uff0c\u5929\u8e4b\u4e0d\u4e0b\u6765\u3002\u201d\u8c22\u5251\u5357\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u201c\u8ba9\u5b50\u5609\u7684\u54e5\u54e5\u4ee3\u66ff\u4f60\u53bb\u8d1f\u8d23\u6d77\u6cf0\uff0c\u4f60\u4eec\u5bb6\u662f\u51b3\u610f\u8981\u505a\u5f3a\u8fd9\u4e24\u5757\uff1f\u201d\u9648\u9759\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u662f\u7684\uff0c\u201d\u8c22\u5251\u5357\u70b9\u70b9\u5934\uff0c\u201c\u5c06\u8d44\u91d1\u4ece\u4f20\u7edf\u4ea7\u4e1a\u91cc\u9010\u6b65\u8f6c\u79fb\u51fa\u6765\uff0c\u6295\u5165\u5230\u65b0\u5174\u7684\u4ea7\u4e1a\u4e2d\uff0c\u8fd9\u662f\u5fc5\u8981\u7684\u8d8b\u52bf\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8c22\u5251\u5357\u7ea6\u845b\u836b\u5747\u5728\u5efa\u90ba\u78b0\u9762\uff0c\u845b\u836b\u574712\u67086\u65e5\u5148\u884c\u62b5\u8fbe\u5efa\u90ba\uff0c\u8c22\u5251\u5357\u4e34\u65f6\u7ed9\u4e8b\u60c5\u803d\u6401\u4e86\uff0c\u5f85\u4ed67\u65e5\u4e0a\u5348\u5904\u7406\u624b\u5934\u4e8b\u52a1\u4e0e\u9648\u9759\u8d76\u5230\u7701\u57ce\u4e0e\u845b\u836b\u5747\u89c1\u9762\u65f6\uff0c\u845b\u836b\u5747\u56e0\u4e3a\u7a81\u7136\u53d8\u6545\u8981\u5148\u4e00\u6b65\u8fd4\u56de\u9999\u6e2f\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e0e\u9648\u9759\u5750\u8f66\u5230\u7701\u57ce\u65f6\u5df2\u7ecf\u662f\u4e0b\u5348\uff0c\u6ca1\u6709\u4e0e\u845b\u836b\u5747\u89c1\u5230\u9762\uff0c\u845b\u836b\u5747\u4e58\u98de\u673a\u62b5\u8fbe\u9999\u6e2f\u4e4b\u540e\uff0c\u624d\u60f3\u5230\u8981\u544a\u8bc9\u8c22\u5251\u5357\u4e00\u58f0\uff0c\u8c22\u5251\u5357\u5df2\u7ecf\u5148\u4e00\u6b65\u4ece\u7535\u89c6\u65b0\u95fb\u91cc\u770b\u5230\u9999\u6e2f\u5341\u5927\u5bcc\u8c6a\u4e4b\u4e00\u7684\u845b\u666f\u8bda\u901d\u4e16\u7684\u6d88\u606f\u3002</p><p>\u3000\u300012\u67087\u65e5\u4e2d\u5348\uff0c\u7d20\u6709\u9999\u6e2f\u7ecf\u8425\u4e4b\u738b\u8457\u79f0\u7684\u845b\u666f\u8bda\u5728\u5bb6\u65cf\u957f\u8475\u533b\u9662\u5348\u4f11\u65f6\u7761\u68a6\u4e2d\u901d\u4e16\uff0c\u5f97\u5e7483\u5c81\uff0c\u7531\u4e8e\u5609\u4fe1\u96c6\u56e2\u5e76\u6ca1\u6709\u53ca\u65f6\u5411\u5916\u754c\u516c\u5e03\u845b\u666f\u8bda\u4e25\u91cd\u7684\u75c5\u60c5\uff0c\u845b\u666f\u8bda\u7684\u79bb\u4e16\uff0c\u5bf9\u5916\u754c\u6765\u8bf4\u6709\u4e9b\u7a81\u7136\uff0c\u5609\u4fe1\u96c6\u56e2\u65d7\u4e0b\u516d\u5bb6\u4e0a\u5e02\u516c\u53f8\u7684\u80a1\u4ef7\u5168\u7ebf\u4e0b\u632b\uff0c\u5609\u4fe1\u5b9e\u4e1a\u4e5f\u5728\u5176\u4e2d\u53d7\u5230\u7275\u7d2f\uff0c\u80a1\u4ef7\u5728\u534a\u5929\u4e4b\u5185\u4e0b\u632b\u4e8612\uff05\u3002</p><p>\u3000\u300012\u67088\u65e5\u4e0a\u5348\uff0c\u845b\u666f\u8bda\u957f\u5b50\uff0c\u4e5f\u662f\u5609\u4fe1\u96c6\u56e2\u73b0\u4efb\u603b\u88c1\u845b\u660e\u4fe1\u4e3a\u4e86\u7a33\u5b9a\u519b\u5fc3\uff0c\u5728\u845b\u666f\u8bda\u7075\u5802\u6240\u5728\u5730\u9999\u6e2f\u957f\u8475\u533b\u5b66\u9662\u6821\u56ed\u5185\u4e3e\u884c\u8bb0\u8005\u4f1a\u5ba3\u5e03\u6cbb\u4e27\u4e8b\u5b9c\uff0c\u8868\u793a\u5609\u4fe1\u96c6\u56e2\u7ecf\u8425\u4e0d\u4f1a\u56e0\u4e3a\u845b\u666f\u8bda\u8fc7\u4e16\u800c\u6709\u91cd\u5927\u6539\u53d8\uff0c\u53ea\u4f1a\u4f9d\u636e\u65f6\u95f4\u8ddf\u7a7a\u95f4\u7684\u4e0d\u540c\u8fdb\u884c\u9002\u65f6\u7684\u8f7b\u5fae\u8c03\u6574\uff0c\u5e76\u5ba3\u5e03\u6cbb\u4e27\u4e8b\u5b9c\uff0c\u8bbe\u5728\u845b\u5bb6\u79c1\u7acb\u5927\u5b66\u957f\u8475\u533b\u5b66\u9662\u7684\u7075\u5802\u4e5f\u5c06\u4e8e8\u65e5\u4e0b\u5348\u5bf9\u5916\u5f00\u653e\u65b9\u4fbf\u5404\u754c\u4eba\u58eb\u524d\u6765\u540a\u5501\u3002</p><p>\u3000\u3000\u867d\u7136\u4e0d\u539a\u9053\uff0c\u5f20\u606a\u4e0e\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u53f6\u5efa\u658c\u7d27\u6025\u78cb\u5546\u540e\uff0c\u540c\u610f\u4ee5\u8d8a\u79c0\u3001\u53f6\u5bb6\u5728\u9999\u6e2f\u7684\u63a7\u80a1\u516c\u53f8\u3001\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u7b49\u5609\u4fe1\u5b9e\u4e1a\u7684\u524d\u56db\u5927\u80a1\u4e1c\u8054\u5408\u53d1\u5e03\u589e\u6301\u58f0\u660e\uff0c\u4ee5\u4e0d\u8d85\u8fc7\u80a1\u4ef7\u4e0b\u632b\u4e4b\u524d\u7684\u4ef7\u683c\u4e0d\u9650\u91cf\u589e\u6301\u5609\u4fe1\u5b9e\u4e1a\u7684\u80a1\u7968\uff0c\u4ee5\u7ef4\u6301\u9999\u6e2f\u6295\u8d44\u4eba\u5bf9\u5609\u4fe1\u5b9e\u4e1a\u7684\u4fe1\u5fc3\u3002</p><p>\u3000\u3000\u5728\u5609\u4fe1\u96c6\u56e2\u65d7\u4e0b\u5176\u4ed6\u4e0a\u5e02\u516c\u53f8\u80a1\u4ef7\u6301\u7eed\u4e0b\u632b\u4e4b\u65f6\uff0c\u5609\u4fe1\u5b9e\u4e1a\u572812\u67088\u65e5\u4e0a\u5348\u56db\u5927\u80a1\u4e1c\u53d1\u5e03\u8054\u5408\u58f0\u660e\u4e4b\u540e\uff0c\u5c31\u8fc5\u901f\u6b62\u8dcc\u53cd\u5f39\u3002</p><p>\u3000\u3000\u5f20\u606a\u8981\u60f3\u501f\u589e\u53d1\u5e76\u8d2d\u7684\u65b9\u6848\u5c06\u7231\u8fbe\u7535\u5b50\u7684\u789f\u673a\u8d44\u4ea7\u7f6e\u5165\u5609\u4fe1\u5b9e\u4e1a\uff0c\u5609\u4fe1\u5b9e\u4e1a\u7ef4\u6301\u8d8a\u9ad8\u7684\u80a1\u4ef7\uff0c\u5b9e\u65bd\u6b64\u65b9\u6848\u8d8a\u6709\u5229\uff0c\u867d\u7136\u6709\u8fd9\u4e48\u597d\u7684\u501f\u53e3\u53ef\u4ee5\u4f4e\u4ef7\u5438\u6536\u80a1\u7968\uff0c\u4f46\u662f\u773c\u4e0b\u7ef4\u6301\u9999\u6e2f\u6295\u8d44\u4eba\u5bf9\u5609\u4fe1\u5b9e\u4e1a\u7684\u4fe1\u5fc3\u6700\u4e3a\u91cd\u8981\uff0c\u524d\u56db\u5927\u80a1\u4e1c\u8fd8\u662f\u5bc6\u96c6\u7684\u5927\u5355\u4e70\u5165\uff0c\u76f4\u63a5\u5c06\u80a1\u4ef7\u62c9\u9ad8\u53cd\u5f39\u3002\u524d\u56db\u5927\u80a1\u4e1c\u6301\u80a1\u6bd4\u4f8b\u9ad8\u8fbe73\uff05\uff0c\u5206\u6563\u5728\u5176\u4ed6\u6295\u8d44\u4eba\u624b\u4e2d\u7684\u80a1\u7968\u5e02\u503c\u4e0d\u8d85\u8fc7\u4e09\u4ebf\u6e2f\u5143\uff0c\u65e9\u5728\u4e4b\u524d\u5f20\u606a\u5c31\u501f\u9884\u4ed8\u89e3\u7801\u677f\u8d27\u6b3e\u7684\u5f62\u5f0f\uff0c\u8f6c\u4e86\u4e09\u4e2a\u4ebf\u5230\u9999\u6e2f\u516c\u53f8\u7684\u8d26\u6237\u4e0a\uff0c\u4e0d\u6015\u5609\u4fe1\u5b9e\u4e1a\u6491\u4e0d\u5230\u845b\u666f\u8bda\u79bb\u4e16\u524d\u4e00\u5929\u7684\u4ef7\u4f4d\u4e0a\u3002\u76ee\u524d\u4f1a\u7528\u9999\u6e2f\u516c\u53f8\u7684\u8d44\u91d1\u589e\u6301\u80a1\u7968\uff0c\u4e4b\u540e\u518d\u6309\u6bd4\u4f8b\u5c06\u589e\u6301\u7684\u80a1\u7968\u8f6c\u8ba9\u7ed9\u5176\u4ed6\u4e09\u5bb6\u3002</p><p>\u3000\u3000\u817f\u4f24\u867d\u7136\u8fd8\u6ca1\u6709\u597d\uff0c\u4f46\u662f\u5f20\u606a\u51b3\u5b9a\u4e0e\u8bb8\u601d\u4e00\u8d77\u8d76\u5f80\u9999\u6e2f\u540a\u5501\uff0c\u53f3\u811a\u6253\u7740\u77f3\u818f\uff0c\u5c31\u6015\u5085\u4fca\u4e00\u4eba\u7167\u987e\u4e0d\u4e86\u592a\u591a\uff0c\u9a6c\u6d77\u9f99\u4e5f\u4e00\u540c\u524d\u5f80\uff0c\u56e0\u4e3a\u53f6\u5efa\u658c\u4e5f\u4f1a\u524d\u5f80\u9999\u6e2f\u540a\u5501\uff0c\u673a\u7968\u90fd\u4e00\u5e76\u6258\u4ed6\u4ee3\u8ba2\u30028\u65e5\u4e0b\u5348\uff0c\u5f20\u606a\u4e0e\u8bb8\u601d\u4ed6\u4eec\u8f66\u4e0b\u9ad8\u901f\uff0c\u5c31\u76f4\u63a5\u7ed5\u4e0a\u57ce\u5357\u5feb\u901f\u901a\u9053\u524d\u5f80\u673a\u573a\u4e0e\u53f6\u5efa\u658c\u5728\u673a\u573a\u78b0\u9762\u3002\u8f66\u5b50\u8fd8\u5728\u5efa\u90ba\u57ce\u5357\u5feb\u901f\u901a\u9053\u4e0a\uff0c\u53f6\u5efa\u658c\u5174\u51b2\u51b2\u7684\u6253\u6765\u7535\u8bdd\uff1a\u201c\u4f60\u77e5\u9053\u90fd\u6709\u8c01\u8ddf\u6211\u4eec\u4e58\u540c\u4e00\u73ed\u98de\u673a\uff1f\u201d</p><p>\u3000\u3000\u201c\u8c22\u5bb6\u67d0\u67d0\u67d0\u2026\u2026\u201d\u4e00\u70b9\u96be\u5ea6\u90fd\u6ca1\u60f3\u6709\uff0c\u60f3\u5fc5\u53f6\u5efa\u658c\u5728\u673a\u573a\u78b0\u5230\u8c22\u5bb6\u8c01\u4e86\uff0c\u201c\u53c8\u4e0d\u662f\u53ea\u6709\u6211\u4eec\u60f3\u53bb\u51d1\u8fd9\u4e2a\u70ed\u95f9\u3002\u201d</p><p>\u3000\u3000\u201c\u8c22\u6c49\u9756\u4e5f\u4eb2\u81ea\u51fa\u52a8\u4e86\uff0c\u8fd8\u6709\u5c31\u662f\u4f60\u7684\u8001\u5bf9\u5934\u8c22\u5251\u5357\u8ddf\u4ed6\u59d8\u5934\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4ee5\u4e3a\u53f6\u5efa\u658c\u662f\u8bf4\u9648\u9759\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4eba\u5bb6\u6b63\u800c\u516b\u7ecf\u7684\u8c08\u604b\u7231\uff0c\u4f60\u4ee5\u4e3a\u4f60\u8ddf\u5b59\u59d0\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u53c8\u6ca1\u8bf4\u9648\u9759\u90a3\u4e2a\u59d8\u5934\uff0c\u9648\u9759\u4e5f\u5728\uff0c\u4e0d\u8fc7\u6211\u8bf4\u7684\u59d8\u5934\u597d\u50cf\u662f\u8c22\u5251\u5357\u5728\u6d77\u6cf0\u65f6\u7684\u79d8\u4e66\uff0c\u6211\u5077\u542c\u4ed6\u4eec\u7684\u53e3\u6c14\uff0c\u597d\u50cf\u90a3\u5973\u7684\u73b0\u5728\u7ed9\u8c22\u6c49\u9756\u5f53\u52a9\u7406\uff0c\u90b5\u5fc3\u6587\u6709\u4e9b\u5370\u8c61\u3002\u5728\u5019\u673a\u5ba4\u4e24\u4eba\u4e00\u672c\u6b63\u7ecf\u7684\uff0c\u8c22\u5251\u5357\u8fd8\u8ddf\u9648\u9759\u5728\u8bf4\u7b11\uff0c\u8d76\u5de7\u6211\u8fc7\u6765\u65f6\u770b\u5230\u8c22\u5251\u5357\u4e0e\u4ed6\u90a3\u4e2a\u79d8\u4e66\u624b\u522b\u5728\u6905\u5b50\u80cc\u540e\u624b\u6307\u7ede\u5728\u4e00\u8d77\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6487\u4e86\u6487\u5634\uff0c\u6ca1\u6709\u5174\u81f4\u8c08\u8fd9\u79cd\u4e8b\u60c5\uff0c\u4e0d\u8fc7\u4f69\u670d\u8c22\u5251\u5357\u633a\u6562\u73a9\u523a\u6fc0\u7684\uff0c\u5f53\u7740\u9648\u9759\u7684\u9762\u8ddf\u60c5\u4eba\u8c03\u60c5\u4e0d\u662f\u4e00\u822c\u4eba\u5fc3\u7406\u80fd\u627f\u53d7\u7684\uff0c\u95ee\u9053\uff1a\u201c\u8001\u90b5\u5728\u4f60\u8eab\u8fb9\uff0c\u4ed6\u4e5f\u53bb\u9999\u6e2f\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u5c31\u9001\u6211\u5230\u673a\u573a\uff0c\u7b49\u4f1a\u513f\u8fd8\u8981\u56de\u53bb\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u8d76\u5230\u673a\u573a\u65f6\u65f6\u521a\u521a\u597d\uff0c\u5f20\u606a\u90a3\u652f\u62d0\u6756\u4e34\u65f6\u529e\u6258\u8fd0\u90fd\u6765\u4e0d\u53ca\uff0c\u53ea\u5f97\u4ea4\u7ed9\u53f8\u673a\u5e26\u56de\u53bb\uff0c\u8ba9\u9999\u6e2f\u516c\u53f8\u8d1f\u8d23\u5230\u673a\u573a\u63a5\u673a\u7684\u5de5\u4f5c\u4eba\u5458\u8d76\u7d27\u518d\u53bb\u4e70\u4e00\u652f\u5355\u62d0\u3002\u4ed6\u4eec\u6ca1\u6709\u4e70\u5230\u5546\u52a1\u8231\uff0c\u53ea\u6709\u51d1\u548c\u5750\u7ecf\u6d4e\u8231\uff0c\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u4ed6\u4eec\u8fd9\u4e9b\u5546\u52a1\u8231\u7684\u4e58\u5ba2\u90fd\u5df2\u7ecf\u63d0\u524d\u767b\u8bb0\uff0c\u5f20\u606a\u4ed6\u4eec\u8981\u8d70\u5230\u673a\u8231\u7684\u540e\u6bb5\uff0c\u52bf\u5fc5\u8981\u7ecf\u8fc7\u5546\u52a1\u8231\u3002\u5750\u5728\u5546\u52a1\u8231\u7684\u4e58\u5ba2\u90fd\u4e60\u60ef\u62ff\u6de1\u6f20\u7684\u773c\u795e\u626b\u8fc7\u4ece\u4e2d\u95f4\u8fc7\u9053\u7ecf\u8fc7\u7684\u4eba\uff0c\u8c22\u5251\u5357\u5230\u5ea7\u4f4d\u4e0a\u5df2\u7ecf\u62ff\u8d77\u6765\u4eca\u5929\u7684\u62a5\u7eb8\u770b\u8d77\u6765\uff0c\u4eca\u65e5\u7ecf\u6d4e\u62a5\u9053\u5934\u7248\u5168\u7bc7\u5e45\u62a5\u9053\u845b\u666f\u8bda\u901d\u4e16\u7684\u6d88\u606f\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u65e0\u610f\u62ac\u5934\u770b\u89c1\u5f20\u606a\u5728\u5085\u4fca\u7684\u6400\u6276\u4e0b\u8d70\u8fdb\u6765\uff0c\u53f6\u5efa\u658c\u3001\u8bb8\u601d\u7b49\u4eba\u8ddf\u5728\u540e\u9762\uff0c\u6709\u4e9b\u9519\u6115\uff0c\u65cb\u5373\u60f3\u5230\u4ed6\u4eec\u5e94\u8be5\u4e5f\u662f\u53bb\u9999\u6e2f\u540a\u5501\uff0c\u800c\u4e14\u4e5f\u4e0d\u4f1a\u662f\u540a\u5501\u8fd9\u4e48\u7b80\u5355\uff0c\u8138\u4e0a\u7b3c\u7740\u4e00\u5c42\u8584\u9634\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u5230\u8c22\u5251\u5357\u3001\u9648\u9759\u8fd8\u6709\u8c22\u6c49\u9756\u5750\u5728\u540c\u6392\uff0c\u53f6\u5efa\u658c\u770b\u5230\u90a3\u4e2a\u8c22\u5251\u5357\u7684\u524d\u79d8\u4e66\u4e0e\u5176\u4ed6\u968f\u884c\u4eba\u5458\u5927\u6982\u4e5f\u5750\u5728\u540e\u9762\u7684\u7ecf\u6d4e\u8231\u91cc\uff0c\u4e0d\u5728\u8fd9\u91cc\u3002</p></article>"}}}]);
