(window.webpackJsonp=window.webpackJsonp||[]).push([[617],{640:function(p){p.exports={chapterTitle:null,title:"\u7b2c467\u7ae0 \u9694\u5cb8\u89c2\u706b",content:"<article><h2>\u7b2c467\u7ae0 \u9694\u5cb8\u89c2\u706b</h2><p>\u3000\u3000\u5f20\u606a\u77a5\u89c1\u8c22\u5251\u5357\u6b63\u9605\u8bfb\u7684\u62a5\u7eb8\u4e0e\u81ea\u5df1\u624b\u91cc\u662f\u540c\u4e00\u671f\u7684\u4eca\u65e5\u7ecf\u6d4e\u62a5\u9053\uff0c\u5927\u6982\u6b63\u5728\u770b\u6709\u5173\u845b\u666f\u8bda\u9057\u4ea7\u5206\u6b67\u7684\u62a5\u9053\uff0c\u5f20\u606a\u5c06\u624b\u91cc\u7684\u62a5\u7eb8\u9012\u5230\u53f6\u5efa\u658c\u524d\u9762\uff0c\u793a\u610f\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u4ed6\u4eec\u6b64\u884c\u7684\u76ee\u7684\u4e5f\u5e94\u8ddf\u81ea\u5df1\u4e00\u6837\uff1a\u6709\u7740\u4e0d\u53ef\u544a\u4eba\u7684\u79d8\u5bc6\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u54c2\u7136\u4e00\u7b11\uff0c\u8868\u793a\u5e94\u662f\u5982\u6b64\uff0c\u4e0d\u7136\u8c01\u4f1a\u4e13\u7a0b\u4ece\u5185\u5730\u8d76\u5f80\u9999\u6e2f\u540a\u5501\uff1f</p><p>\u3000\u3000\u8c22\u5251\u5357\u770b\u5230\u5f20\u606a\u624b\u91cc\u4e5f\u62ff\u7740\u540c\u6837\u7684\u62a5\u7eb8\uff0c\u4e0d\u52a8\u58f0\u8272\u7684\u5c06\u624b\u91cc\u7684\u62a5\u7eb8\u653e\u4e0b\uff0c\u5fc3\u91cc\u5374\u5728\u60f3\uff1a\u845b\u666f\u8bda\u5c38\u9aa8\u672a\u5bd2\uff0c\u4e0d\u7ba1\u845b\u5bb6\u5185\u90e8\u4e3a\u9057\u4ea7\u4e4b\u4e8b\u4e89\u5f97\u5929\u7ffb\u5730\u8986\uff0c\u4f5c\u4e3a\u5973\u5a7f\u7684\u5b59\u5c1a\u4e49\u4e0e\u4f5c\u4e3a\u5802\u4f84\u7684\u845b\u660e\u5fb7\u90fd\u4e0d\u4f1a\u662f\u4e3b\u89d2\uff0c\u5f20\u606a\u4ed6\u4eec\u8dd1\u8fc7\u53bb\u51d1\u4ec0\u4e48\u70ed\u95f9\uff1f</p><p>\u3000\u3000\u5927\u5bb6\u51b7\u6f20\u7684\u8fde\u70b9\u5934\u5fae\u7b11\u90fd\u541d\u556c\uff0c\u60df\u6709\u8c22\u6c49\u9756\u4ece\u5bb9\u4e0d\u8feb\u7684\u671d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u5f20\u606a\u7565\u4e00\u9894\u9996\u7b97\u662f\u6253\u8fc7\u62db\u547c\uff0c\u5c31\u7a7f\u8fc7\u5546\u52a1\u8231\u7684\u5ea7\u5e2d\u8fdb\u5165\u7ecf\u6d4e\u8231\u5185\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u4ed6\u4eec\u7684\u968f\u884c\u4eba\u5458\u5750\u5728\u5de6\u673a\u7ffc\u5904\u7684\u4f4d\u7f6e\uff0c\u53f6\u5efa\u658c\u6307\u7740\u4e00\u540d\u5a07\u67d4\u751c\u7f8e\u7684\u521a\u5c06\u5916\u5957\u8131\u4e0b\u3001\u53ea\u7a7f\u7740\u6d0b\u7ea2\u8272\u6bdb\u8863\u5c55\u9732\u51fa\u50b2\u4eba\u8eab\u6750\u7684\u5973\u4eba\u544a\u8bc9\u5f20\u606a\u5979\u5c31\u662f\u8c22\u5251\u5357\u5728\u6d77\u6cf0\u65f6\u7684\u79d8\u4e66\u3002</p><p>\u3000\u3000\u5f20\u606a\u7531\u5085\u4fca\u6400\u7740\uff0c\u53f6\u5efa\u658c\u5934\u8fd8\u51d1\u8fc7\u6765\u8bf4\u8bdd\uff0c\u4e24\u4eba\u7684\u773c\u775b\u5374\u53c8\u540c\u65f6\u7785\u5411\u4e00\u4e2a\u7a7f\u6d0b\u7ea2\u8272\u6bdb\u8863\u5927\u80f8\u90e8\u7684\u5973\u4eba\uff0c\u8fd9\u6a21\u6837\u4e5f\u591f\u7325\u4eb5\u7684\u3002</p><p>\u3000\u3000\u98de\u5230\u9999\u6e2f\u5929\u8fd8\u4e0d\u4f1a\u9ed1\uff0c\u5dee\u4e0d\u591a\u662f\u9ec4\u660f\u65f6\u5206\uff0c\u4f46\u662f\u98de\u673a\u4e0a\u6709\u665a\u9910\u4f9b\u5e94\uff0c\u7ecf\u6d4e\u8231\u81ea\u7136\u6ca1\u6709\u7279\u522b\u7ec6\u81f4\u7684\u670d\u52a1\uff0c\u6ca1\u6709\u897f\u9910\u53ef\u70b9\uff0c\u5957\u9910\u6709\u9521\u7eb8\u5305\u88f9\u7684\u725b\u6392\u53e3\u5473\u76f8\u5f53\u4e0d\u9519\uff0c\u5f20\u606a\u4ed6\u4eec\u4e5f\u6ca1\u6709\u60f3\u7740\u5c06\u8fd9\u4e9b\u6d6a\u8d39\u6389\uff0c\u98de\u4e34\u9999\u6e2f\u4e0a\u7a7a\uff0c\u5728\u98de\u673a\u4e0a\u53ef\u4ee5\u770b\u89c1\u897f\u8fb9\u7684\u5915\u9633\u8fd8\u53ea\u5269\u4e0b\u4e00\u7709\u8fb9\uff0c\u5468\u8fb9\u4e91\u5c42\u5343\u53d8\u4e07\u5316\u3001\u971e\u5149\u7480\u74a8\uff0c\u6ce2\u5149\u7cbc\u7cbc\u7684\u6d77\u6c34\u8fd8\u8df3\u8dc3\u7740\u4ebf\u4e07\u679a\u91d1\u5e01\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u8fd8\u6709\u5085\u5bb6\u4fca\u3001\u8bb8\u601d\u5728\u9999\u6e2f\u8fd9\u8fb9\u7684\u603b\u7ecf\u7406\u52a9\u7406\u5468\u9038\u4e0e\u76db\u946b\u6df1\u5733\u5206\u516c\u53f8\u7684\u5de5\u4f5c\u4eba\u5458\u65e9\u5c31\u5728\u51fa\u53e3\u5927\u5385\u7b49\u5019\uff0c\u529e\u7406\u5b8c\u624b\u7eed\u4e00\u8d77\u8d70\u51fa\u5927\u5385\uff0c\u7ad9\u5728\u53f0\u9636\u4e0a\u7b49\u53f8\u673a\u5c06\u8f66\u5b50\u5f00\u8fc7\u6765\u3002\u6309\u8bf4\u5b59\u9759\u9999\u59d0\u59b9\u8fd9\u65f6\u5019\u6ca1\u6709\u7a7a\u8fc7\u6765\u63a5\u673a\uff0c\u5b59\u9759\u6aac\u5fc3\u76f4\u53e3\u5feb\uff0c\u4e1d\u6beb\u4e0d\u63a9\u9970\u5bf9\u6cbb\u4e27\u4e2d\u602a\u5f02\u7684\u6c14\u6c1b\u7684\u4e0d\u6ee1\uff0c\u5979\u4e0d\u4ec5\u62c9\u7740\u5b59\u9759\u9999\u5f00\u8f66\u51fa\u6765\u900f\u6c14\uff0c\u987a\u4fbf\u5230\u542f\u5fb7\u673a\u573a\u6765\u63a5\u673a\uff0c\u8fd8\u6253\u7b97\u591c\u91cc\u966a\u5f20\u606a\u4ed6\u4eec\u4e00\u8d77\u4f4f\u5728\u5bbe\u9986\u91cc\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u7b49\u4eba\u5219\u5728\u51fa\u79df\u8f66\u5019\u8f66\u70b9\u6392\u961f\u5019\u8f66\uff0c\u5e76\u672a\u89c1\u845b\u5bb6\u6709\u6d3e\u4e13\u8f66\u6765\u63a5\u4ed6\u4eec\uff1b\u4ece\u6628\u5929\u4e0b\u5348\u8d77\uff0c\u5404\u754c\u4eba\u58eb\u5c31\u9646\u7eed\u4e0d\u7edd\u5230\u8bbe\u5728\u9999\u6e2f\u957f\u8475\u533b\u5b66\u9662\u6821\u56ed\u91cc\u7684\u7075\u5802\u540a\u5501\uff0c\u845b\u666f\u8bda\u4e09\u623f\u592a\u592a\u6240\u751f\u7684\u4f17\u591a\u5b50\u5973\u643a\u5bb6\u4eba\u90fd\u8981\u5728\u7075\u5802\u5411\u540a\u5501\u8005\u7b54\u793c\uff0c\u81ea\u7136\u6ca1\u6709\u8c01\u987e\u5f97\u4e0a\u6765\u7ba1\u4ed6\u4eec\u8fd9\u4e9b\u4ece\u5185\u5730\u81ea\u53d1\u8d76\u8fc7\u6765\u540a\u5501\u7684\u670b\u53cb\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u65f6\u95f4\u6bb5\u51fa\u673a\u573a\u7684\u4eba\u5f88\u591a\uff0c\u800c\u4e14\u90fd\u6709\u5927\u5305\u5c0f\u5305\u7684\u884c\u674e\uff0c\u4e0a\u8f66\u7684\u901f\u5ea6\u5f88\u6162\uff0c\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u4ed6\u4eec\u624d\u79fb\u5230\u961f\u4f0d\u4e2d\u95f4\uff0c\u770b\u5230\u5f20\u606a\u4ed6\u4eec\u94bb\u8fdb\u81ea\u5bb6\u5206\u516c\u53f8\u7684\u8f66\u8fdc\u53bb\uff0c\u8c22\u6c49\u9756\u95ee\u8c22\u5251\u5357\uff1a\u201c\u7231\u8fbe\u5e94\u8be5\u53bb\u5e74\u5c31\u5728\u9999\u6e2f\u8bbe\u7acb\u5206\u516c\u53f8\u4e86\uff0c\u4ee5\u8d8a\u79c0\uff08\u9999\u6e2f\uff09\u7684\u540d\u4e49\u5c06\u7231\u8fbe\u789f\u673a\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u54c1\u724c\u4ee3\u7406\u6388\u7ed9\u5609\u4fe1\u5b9e\u4e1a\uff0c\u4f46\u662f\u5609\u4fe1\u5b9e\u4e1a\u8fd9\u4e00\u5e74\u6765\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u52a8\u4f5c\u597d\u50cf\u5e76\u4e0d\u5927\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u53ea\u6709\u83f2\u5f8b\u5bbe\u4e0e\u8d8a\u5357\u7684\u4e24\u95f4\u5de5\u5382\u5728\u751f\u4ea7\u789f\u673a\uff0c\u6211\u770b\u4e00\u5e74\u7684\u4ea7\u9500\u91cf\u90fd\u4e0d\u8db3\u7231\u8fbe\u5728\u56fd\u5185\u5e02\u573a\u534a\u4e2a\u6708\u7684\u4ea7\u9500\u91cf\u2026\u2026\u56fd\u5185\u4e5f\u662f\u4e07\u71d5\u6700\u5148\u4e8e\u4e5d\u56db\u5e74\u6700\u5148\u751f\u4ea7\u789f\u673a\uff0c\u4f46\u662f\u789f\u673a\u5e02\u573a\u7684\u6f5c\u529b\u771f\u6b63\u6316\u6398\u51fa\u6765\u8fd8\u662f\u53bb\u5e74\u4e0b\u534a\u5e74\u4e0e\u4eca\u5e74\uff0c\u662f\u5728\u7247\u6e90\u95ee\u9898\u5f7b\u5e95\u89e3\u51b3\u6389\u4e4b\u540e\uff0c\u789f\u673a\u5e02\u573a\u624d\u771f\u6b63\u53d1\u5c55\u8d77\u6765\uff0c\u6211\u4eec\u4e1c\u5357\u4e9a\u5e02\u573a\u5e94\u8be5\u4e5f\u6709\u8fd9\u4e48\u4e00\u4e2a\u8fc7\u7a0b\uff0c\u660e\u5e74\u5e94\u8be5\u662f\u5927\u4e3e\u8fdb\u5165\u7684\u597d\u65f6\u673a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u603b\u89c9\u5f97\u6bd4\u8d77\u7231\u8fbe\u7535\u5b50\u5728\u56fd\u5185\u5e02\u573a\u7684\u52a8\u4f5c\u76f8\u6bd4\uff0c\u5609\u4fe1\u5b9e\u4e1a\u5728\u4e1c\u5357\u4e9a\u5c31\u6709\u4e9b\u592a\u9eef\u7136\u5931\u8272\u4e86\uff0c\u96be\u9053\u8bf4\u7231\u8fbe\u7535\u5b50\u5bf9\u5609\u4fe1\u5b9e\u4e1a\u7684\u63a7\u5236\u529b\u5f88\u6709\u9650\uff1f\u201d\u8c22\u6c49\u9756\u8fd8\u662f\u5f88\u7591\u60d1\u4e0d\u89e3\u3002</p><p>\u3000\u3000\u6240\u8c13\u5236\u7ea6\u789f\u673a\u5e02\u573a\u6210\u719f\u7684\u9650\u5236\u6761\u4ef6\uff0c\u5728\u7231\u8fbe\u7535\u5b50\u9762\u524d\u5e94\u8be5\u7b97\u4e0d\u4e0a\u4ec0\u4e48\uff0c\u7231\u8fbe\u7535\u5b50\u5e94\u8be5\u6709\u4e00\u5207\u624b\u6bb5\u6765\u5f00\u62d3\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u5e02\u573a\u3002\u5176\u4ed6\u4eba\u4e0d\u77e5\u9053\uff0c\u8c22\u5bb6\u5f53\u7136\u77e5\u9053\u6e90\u5934\u51fa\u81ea\u4e1c\u793e\u53bf\u7684\u76d7\u7248\u789f\u4ea7\u4e1a\u88e2\u4e4b\u6240\u4ee5\u80fd\u591f\u8fc5\u901f\u58ee\u5927\u8d77\u6765\uff0c\u4e0e\u7231\u8fbe\u7535\u5b50\u5728\u5e55\u540e\u7684\u652f\u6301\u662f\u65ad\u65ad\u5206\u4e0d\u5f00\u7684\u3002\u5609\u4fe1\u5b9e\u4e1a\u5bf9\u5f00\u53d1\u4e1c\u5357\u4e9a\u7684\u789f\u673a\u5e02\u573a\u6709\u4e9b\u6f2b\u4e0d\u7ecf\u5fc3\uff0c\u60df\u4e00\u7684\u89e3\u91ca\u5c31\u662f\u5f20\u606a\u53ea\u662f\u53c2\u80a1\u80a1\u4e1c\u800c\u5df2\uff0c\u5e76\u6ca1\u6709\u62ff\u5230\u5609\u4fe1\u5b9e\u4e1a\u7684\u771f\u6b63\u63a7\u5236\u6743\uff1b\u800c\u5609\u4fe1\u5b9e\u4e1a\u7684\u7ba1\u7406\u5c42\u76ee\u5149\u77ed\u6d45\u4e86\u4e00\u4e9b\uff0c\u5728\u5e74\u521d\u7684\u65f6\u95f4\u8fd8\u6ca1\u6709\u8ba4\u8bc6\u5230\u789f\u673a\u7684\u91cd\u8981\u6027\u3002</p><p>\u3000\u3000\u5c31\u7b97\u5f20\u606a\u73b0\u5728\u5c06\u771f\u6b63\u7684\u613f\u610f\u544a\u8bc9\u4ed6\u4eec\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u6bd5\u7adf\u8fd9\u7c7b\u4eba\u7269\uff0c\u5bf9\u672a\u6765\u7684\u7ecf\u6d4e\u5f62\u52bf\u90fd\u6709\u81ea\u5df1\u660e\u786e\u7684\u5224\u65ad\uff0c\u4e0d\u4f1a\u8f7b\u6613\u7ed9\u522b\u4eba\u6240\u5de6\u53f3\u3002</p><p>\u3000\u3000\u5c3d\u7ba1\u5609\u4fe1\u5b9e\u4e1a\u5bf9\u4e1c\u5357\u4e9a\u7684\u789f\u673a\u4e1a\u52a1\u6709\u4e9b\u6f2b\u4e0d\u7ecf\u5fc3\uff0c\u4f46\u662f\u4e09\u5b63\u5ea6\u7684\u5b63\u62a5\u4e5f\u7ed9\u51fa\u5f88\u6f02\u4eae\u7684\u7b54\u5377\uff0c\u4ec5\u789f\u673a\u8fd9\u4e00\u5757\u4e1a\u52a1\u7684\u51c0\u76c8\u5229\u8fbe\u5230\u56db\u5343\u4e07\uff0c\u5df2\u7ecf\u5f88\u597d\u7684\u652f\u6491\u4f4f\u5609\u4fe1\u5b9e\u4e1a\u76ee\u524d\u7684\u80a1\u4ef7\uff0c\u76f8\u4fe1\u5609\u4fe1\u5b9e\u4e1a\u5728\u63a5\u4e0b\u6765\u7684\u8d22\u5b63\u4f1a\u9010\u6b65\u91cd\u89c6\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u65b0\u5174\u5e02\u573a\u3002</p><p>\u3000\u3000\u4e0d\u8981\u8bf4\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u65b0\u5174\u5e02\u573a\u4e86\uff0c\u9999\u6e2f\u672c\u5730\uff0c\u7531\u4e8e\u5185\u5730\u7684\u76d7\u7248VCD\u789f\u7247\u6d41\u5165\u9999\u6e2f\u7684\u6e20\u9053\u76f8\u5bf9\u6765\u8bf4\u5f88\u4fbf\u6377\u5e76\u4e14\u4ef7\u683c\u4f4e\u5ec9\uff0c\u540c\u6837\u6709\u7740\u76f8\u5bf9\u8f83\u5927\u7684\u789f\u673a\u5e02\u573a\u5b58\u5728\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u4e00\u884c\u4eba\u5165\u4f4f\u6d45\u6c34\u6e7e\u7684\u5f71\u6e7e\u56ed\u9152\u5e97\u516c\u5bd3\u3002\u6ca1\u6709\u4f4f\u5bbe\u9986\u800c\u662f\u4f4f\u9152\u5e97\u516c\u5bd3\uff0c\u662f\u5b59\u9759\u6aac\u5f3a\u70c8\u5efa\u8bae\u7684\u3002</p><p>\u3000\u3000\u6cbf\u7740\u6e2f\u5c9b\u897f\u6d77\u5cb8\u7ebf\u4e00\u8def\u5357\u884c\uff0c\u8d70\u8fc7\u9ec4\u7af9\u5751\uff0c\u822c\u4e0b\u5d0e\u5c96\u7684\u5c71\u8def\uff0c\u7a7f\u8fc7\u4e00\u7247\u8302\u5bc6\u7684\u6811\u6797\uff0c\u9661\u7136\u51fa\u73b0\u5728\u773c\u524d\u7684\u662f\u66ae\u8272\u4e0b\u4e00\u7247\u5bbd\u9614\u7684\u6d77\u9762\u4e0e\u6c99\u6ee9\uff0c\u4e5f\u80fd\u591f\u770b\u89c1\u4e00\u5ea7\u9762\u5411\u851a\u84dd\u5927\u6d77\u7684\u5f27\u7ebf\u5f62\u5efa\u7b51\uff1a\u5f71\u6e7e\u56ed\u3002</p><p>\u3000\u3000\u5f71\u6e7e\u56ed\u662f\u6d45\u6c34\u6e7e\u7684\u5730\u6807\u5efa\u7b51\uff0c\u4f4d\u4e8e\u9999\u6e2f\u5c9b\u5357\u533a\u6d45\u6c34\u6e7e\u9053109\u53f7\uff0c\u5185\u90e8\u9664\u4e86\u9ad8\u6863\u4f4f\u5b85\u3001\u5546\u573a\u3001\u4ff1\u4e50\u90e8\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u9ad8\u6863\u7684\u9152\u5e97\u670d\u52a1\u5f0f\u516c\u5bd3\uff0c\u63d0\u4f9b\u5404\u79cd\u89c4\u683c\u7684\u9152\u5e97\u516c\u5bd3\u79df\u4f4f\u670d\u52a1\uff0c\u4f46\u662f\u81f3\u5c11\u9700\u8981\u79df\u4f4f\u4e00\u4e2a\u6708\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u4e00\u884c\u4eba\u5f53\u7136\u4e0d\u53ef\u80fd\u5728\u9999\u6e2f\u6ede\u7559\u4e00\u4e2a\u6708\uff0c\u800c\u662f\u5b59\u9759\u6aac\u5f3a\u70c8\u63a8\u8350\u8fd9\u91cc\uff0c\u53cd\u6b63\u5979\u4e0e\u5979\u59d0\u59d0\u5b59\u9759\u9999\u8981\u662f\u4eba\u5728\u9999\u6e2f\u4e5f\u53ea\u4f1a\u4f4f\u5728\u8fd9\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u8bb8\u601d\u8bf4\u5728\u539f\u574082\u5e74\u62c6\u9664\u7684\u6d45\u6c34\u6e7e\u5927\u9152\u5e97\u662f\u5f20\u7231\u73b2\u5c0f\u8bf4\u300a\u503e\u57ce\u4e4b\u604b\u300b\u4e2d\u7537\u5973\u4e3b\u89d2\u7b2c\u4e00\u6b21\u9082\u9005\u7684\u5730\u70b9\uff0c\u65b0\u5efa\u5f71\u6e7e\u56ed\u65f6\uff0c\u4e3a\u4e86\u633d\u7559\u300a\u503e\u57ce\u4e4b\u604b\u300b\u4e2d\u7684\u98ce\u60c5\uff0c\u7279\u610f\u5c06\u6d45\u6c34\u6e7e\u5927\u9152\u5e97\u7684\u9732\u53f0\u90e8\u5206\u4fdd\u7559\uff0c\u4eca\u65e5\u5f71\u6e7e\u56ed\u9732\u53f0\u9910\u5385\u5df2\u662f\u9a70\u540d\u4e2d\u5916\u7684\u9ad8\u7ea7\u4e0b\u5348\u8336\u53bb\u5904\u3002</p><p>\u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u5f71\u6e7e\u56ed\u8ddd\u845b\u5b85\u5f00\u8f66\u53ea\u6709\u4e0d\u5230\u5341\u5206\u949f\u7684\u8f66\u7a0b\uff0c\u4f4f\u5728\u8fd9\u91cc\u5c31\u6709\u4e00\u79cd\u9694\u5cb8\u89c2\u706b\u7684\u723d\u5229\u611f\u89c9\u3002</p><p>\u3000\u3000\u5c31\u7b97\u591a\u6d6a\u8d39\u4e00\u4e9b\u94b1\uff0c\u5f20\u606a\u4ee5\u4e3a\u4f4f\u5728\u8fd9\u91cc\u4e5f\u6ca1\u6709\u4ec0\u4e48\u4e0d\u5408\u9002\uff0c\u4f55\u51b5\u4ed6\u8fd8\u6253\u7b97\u5728\u9999\u6e2f\u591a\u4fee\u517b\u51e0\u5929\uff0c\u533b\u7597\u6c34\u5e73\uff0c\u9999\u6e2f\u603b\u6bd4\u6d77\u5dde\u8981\u9ad8\u51fa\u8bb8\u591a\u3002\u79df\u4e0b\u4e24\u5957\u5927\u5957\u578b\u9152\u5e97\u516c\u5bd3\uff0c\u6bcf\u4e2a\u6708\u516b\u4e07\u6e2f\u5e01\u7684\u79df\u91d1\u4e5f\u4e0d\u4f1a\u592a\u96be\u627f\u53d7\u3002</p><p>\u3000\u3000\u53ea\u662f\u5b59\u5b85\u79bb\u8fd9\u91cc\u5f88\u8fd1\uff0c\u5f20\u606a\u4e0d\u660e\u767d\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u59d0\u59b9\u4e3a\u4f55\u4e0d\u4f4f\u56de\u5bb6\u91cc\u53bb\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u9732\u51fa\u4e00\u4e1d\u82e6\u7b11\uff0c\u591a\u5c11\u6709\u4e9b\u5bb6\u4e11\u4e0d\u53ef\u5916\u626c\u7684\u65e0\u5948\uff0c\u5b59\u9759\u6aac\u5374\u6ca1\u6709\u90a3\u4e48\u591a\u987e\u5fcc\uff0c\u63a8\u5f00\u7a97\u6237\uff0c\u671b\u7740\u8fdc\u5904\u6d77\u6ee9\u4e0a\u7684\u706f\u5854\u4e0e\u6e38\u8f6e\u4e0a\u7684\u706f\u706b\uff0c\u8bf4\u9053\uff1a\u201c\u518d\u4e0d\u627e\u5730\u65b9\u4f4f\uff0c\u5c31\u8981\u6ca6\u843d\u8857\u5934\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u9999\u6e2f\u7684\u5929\u5df2\u7ecf\u5b8c\u5168\u6697\u4e0b\u6765\uff0c\u5f71\u6e7e\u56ed\u5916\u7684\u6d77\u6ee9\u4e0e\u6d77\u5728\u591c\u8272\u4e0b\u5374\u683c\u5916\u7684\u9759\u7f8e\u3002</p><p>\u3000\u3000\u5f20\u606a\u3001\u53f6\u5efa\u658c\u90fd\u4e0d\u7406\u89e3\u5b59\u9759\u6aac\u5e26\u7740\u4e9b\u6028\u6c14\u7684\u8bdd\uff1a\u4ec0\u4e48\u53eb\u518d\u4e0d\u627e\u5730\u65b9\u4f4f\uff0c\u5c31\u8981\u6ca6\u843d\u8857\u5934\uff1f\u5b59\u9759\u9999\u82e6\u7b11\u7740\u8ddf\u5f20\u606a\u3001\u53f6\u5efa\u658c\u8fd8\u6709\u8bb8\u601d\u89e3\u91ca\u3002</p><p>\u3000\u3000\u9664\u4e86\u5df2\u7ecf\u5206\u7ed9\u5404\u623f\u5b50\u5973\u4e0e\u65cf\u4e2d\u5b50\u4f84\u7684\u8d44\u4ea7\u5916\uff0c\u845b\u666f\u8bda\u7559\u4e0b\u7684\u9057\u4ea7\u53ef\u4ee5\u5206\u6210\u4e09\u5757\uff0c\u4e00\u5757\u662f\u4ee5\u9999\u6e2f\u957f\u8475\u533b\u5b66\u9662\u53ca\u9644\u5c5e\u533b\u9662\u4e3a\u4e3b\u4f53\u5e76\u5305\u62ec\u845b\u666f\u8bda\u793e\u4f1a\u798f\u5229\u57fa\u91d1\u4f1a\u516c\u76ca\u4fe1\u6258\u5728\u5185\u7684\u8d44\u4ea7\uff0c\u8fd9\u4e00\u5757\u8d44\u4ea7\u8fd8\u542b\u6709\u5609\u4fe1\u96c6\u56e2\u65d7\u4e0b\u4e0a\u5e02\u516c\u53f8\u8fd1\u767e\u4ebf\u7684\u80a1\u6743\u8d44\u4ea7\uff0c\u8fd9\u4e00\u90e8\u5206\u8d44\u4ea7\u4f5c\u4e3a\u6148\u5584\u516c\u4e49\u7528\u9014\uff0c\u800c\u4e0d\u4f1a\u4f5c\u4e3a\u9057\u4ea7\u5904\u7f6e\uff1b\u7b2c\u4e8c\u5757\u662f\u4ee5\u845b\u6c0f\u4fe1\u6258\u57fa\u91d1\u4f1a\u5f62\u5f0f\u5b58\u5728\u7684\u5bb6\u65cf\u4fe1\u6258\u57fa\u91d1\uff0c\u4e3a\u845b\u6c0f\u5bb6\u65cf\u6210\u5458\u5171\u540c\u6240\u6709\uff0c\u5171\u62e5\u6709\u8fd1\u4e94\u5341\u4ebf\u7684\u8d44\u4ea7\uff0c\u9664\u975e\u5f97\u5230\u6240\u6709\u53d7\u76ca\u4eba\u7684\u540c\u610f\uff0c\u8fd9\u90e8\u5206\u8d44\u4ea7\u662f\u4e0d\u53ef\u4ee5\u5957\u73b0\u74dc\u5206\u7684\uff0c\u4e00\u822c\u8bf4\u6765\u5bb6\u65cf\u6210\u5458\u53ea\u80fd\u83b7\u5229\u4e4b\u540e\u62ff\u5206\u7ea2\uff1b\u7b2c\u4e09\u5757\u5c31\u662f\u845b\u666f\u8bda\u76f4\u63a5\u63a7\u5236\u7684\u6cd5\u4eba\u6301\u80a1\u516c\u53f8\u5609\u4fe1\u96c6\u56e2\uff0c\u5609\u4fe1\u96c6\u56e2\u540d\u4e0b\u6240\u6301\u5404\u4e0a\u5e02\u516c\u53f8\u7684\u80a1\u6743\u5171\u8d85\u8fc7\u4e24\u767e\u4ebf\u7684\u5de8\u989d\u8d44\u4ea7\u3002</p><p>\u3000\u3000\u9664\u4e86\u5df2\u7ecf\u505a\u8fc7\u5904\u7406\u7684\u8d44\u4ea7\uff0c\u845b\u666f\u8bda\u79bb\u4e16\u524d\u518d\u6ca1\u6709\u5176\u4ed6\u6709\u5173\u9057\u5631\u7684\u6587\u4ef6\u7559\u4e0b\u3002</p><p>\u3000\u3000\u773c\u4e0b\u845b\u6c0f\u5bb6\u65cf\u6210\u5458\u6240\u4f4f\u7684\u522b\u5885\u3001\u6d0b\u697c\u4e0e\u9ad8\u7ea7\u516c\u5bd3\uff0c\u9664\u4e86\u5c11\u90e8\u5206\u662f\u81ea\u5df1\u6240\u8d2d\u5165\u7684\u4ea7\u4e1a\uff0c\u7edd\u5927\u591a\u6570\u8fd8\u662f\u5c5e\u4e8e\u5609\u4fe1\u96c6\u56e2\u7684\u8d44\u4ea7\u3002\u845b\u666f\u8bda\u901d\u4e16\u524d\u6709\u610f\u5c06\u8fd9\u4e9b\u7269\u4e1a\u8d44\u4ea7\u90fd\u79fb\u5230\u5bb6\u65cf\u4fe1\u6258\u57fa\u91d1\u540d\u4e0b\uff0c\u8fd9\u6837\u9664\u4e86\u5927\u5bb6\u90fd\u540c\u610f\u5f7b\u5e95\u7684\u5206\u5bb6\uff0c\u81f3\u5c11\u80fd\u4fdd\u969c\u73b0\u6709\u7684\u5bb6\u65cf\u6210\u5458\u4e0d\u4f1a\u7ed9\u522b\u4eba\u4ece\u4f4f\u5b85\u91cc\u8d76\u51fa\u53bb\uff0c\u4f46\u662f\u845b\u666f\u8bda\u867d\u7136\u5728\u751f\u524d\u900f\u9732\u8fd9\u5c42\u610f\u601d\uff0c\u5374\u6ca1\u6709\u53ca\u65f6\u7559\u4e0b\u4ec0\u4e48\u9057\u5631\u6587\u4ef6\u3002</p><p>\u3000\u3000\u6216\u8bb8\u845b\u666f\u8bda\u8fd9\u5bb6\u4f19\u66f4\u4e50\u610f\u770b\u5230\u4ed6\u7684\u5b50\u5973\u4e3a\u4e86\u4e89\u8d22\u4ea7\u7684\u4e8b\u60c5\u626f\u7834\u4e00\u5f20\u5f20\u865a\u4f2a\u7684\u8138\u3002</p><p>\u3000\u3000\u6628\u5929\u591c\u91cc\uff0c\u845b\u666f\u8bda\u4e09\u623f\u5b50\u5973\u90fd\u805a\u96c6\u5728\u845b\u5bb6\u5927\u5b85\u91cc\u8ba8\u8bba\u76ee\u524d\u53ef\u4ee5\u5904\u7f6e\u7684\u7b2c\u4e09\u5757\u8d22\u4ea7\uff0c\u4e5f\u662f\u6700\u5927\u7684\u4e00\u5757\uff1a\u5609\u4fe1\u96c6\u56e2\u81ea\u8eab\u7684\u8d44\u4ea7\u4e0e\u540d\u4e0b\u6240\u6301\u5404\u4e0a\u5e02\u516c\u53f8\u80a1\u6743\u8d44\u4ea7\u3002</p><p>\u3000\u3000\u845b\u660e\u4fe1\u60f3\u5b8c\u5168\u638c\u63e1\u5609\u4fe1\u96c6\u56e2\u7684\u63a7\u5236\u6743\uff0c\u5176\u4ed6\u4eba\u81ea\u7136\u4e0d\u613f\u610f\uff0c\u56b7\u7740\u8981\u5206\u5bb6\uff0c\u4e89\u5435\u5230\u6700\u540e\uff0c\u845b\u660e\u4fe1\u5a01\u80c1\u8981\u5c06\u5404\u5bb6\u4f4f\u7684\u623f\u4ea7\u5148\u6536\u56de\u6765\u518d\u8ba8\u8bba\u8d44\u4ea7\u5206\u914d\u7684\u95ee\u9898\uff0c\u867d\u7136\u845b\u660e\u4fe1\u4e8b\u540e\u51b7\u9759\u4e0b\u6765\u6709\u8fc7\u9053\u6b49\u5e76\u60f3\u6536\u56de\u5931\u8a00\uff0c\u5374\u628a\u845b\u5bb6\u7684\u5176\u4ed6\u6210\u5458\u7ed9\u6fc0\u6012\u4e86\u3002</p><p>\u3000\u3000\u4eca\u5929\u4e0a\u5348\u5df2\u7ecf\u597d\u51e0\u4e2a\u845b\u5bb6\u6210\u5458\u642c\u8fdb\u9152\u5e97\u4f4f\uff0c\u6253\u7b97\u8981\u6f14\u4e00\u51fa\u9aa8\u8089\u76f8\u6b8b\u7684\u82e6\u8089\u8ba1\u620f\u7ed9\u9999\u6e2f\u7684\u5a92\u4f53\u4e0e\u516c\u4f17\u770b\uff0c\u5b59\u5c1a\u4e49\u592b\u5987\u5012\u6ca1\u6709\u505a\u79c0\u7684\u610f\u613f\uff0c\u8fd8\u4f4f\u5728\u539f\u6765\u7684\u5b85\u5b50\u6ca1\u6709\u642c\u51fa\u53bb\uff0c\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u59d0\u59b9\u6b63\u60f3\u8131\u79bb\u6b64\u65f6\u845b\u5bb6\u538b\u6291\u800c\u602a\u5f02\u7684\u6c1b\u56f4\uff0c\u8fd9\u624d\u60f3\u7740\u642c\u5230\u5f71\u6e7e\u57ce\u9152\u5e97\u516c\u5bd3\u6765\u4f4f\u3002</p><p>\u3000\u3000\u845b\u5bb6\u6210\u5458\u5728\u9999\u6e2f\u5927\u591a\u90fd\u6709\u81ea\u5df1\u7684\u4f4f\u5b85\uff0c\u4f46\u662f\u5f88\u5c11\u80fd\u6709\u6bd4\u5bb6\u65cf\u5728\u6d45\u6c34\u6e7e\u3001\u6df1\u6c34\u6e7e\u8fd9\u4e9b\u9ad8\u6863\u4f4f\u5b85\u533a\u63d0\u4f9b\u7684\u6d77\u666f\u522b\u5885\u66f4\u52a0\u7684\u9ad8\u6863\uff0c\u6240\u4ee5\u5927\u591a\u4f4f\u5728\u5bb6\u65cf\u63d0\u4f9b\u7684\u623f\u4ea7\u5185\u3002</p><p>\u3000\u3000\u845b\u660e\u4fe1\u90a3\u53e5\u5a01\u80c1\u7684\u8bdd\u81ea\u7136\u4e0d\u80fd\u5a01\u80c1\u5230\u522b\u4eba\uff0c\u53cd\u800c\u5c06\u81ea\u5df1\u903c\u5165\u56f0\u5883\uff0c\u4e00\u65e6\u7ed9\u5a92\u4f53\u62a5\u5bfc\u8fd9\u4e8b\uff0c\u5c31\u4f1a\u7ed9\u516c\u4f17\u7559\u4e0b\u8001\u7236\u521a\u6b7b\u5c31\u6025\u4e8e\u903c\u8feb\u5f1f\u59b9\u7684\u6076\u52a3\u5370\u8c61\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u6b21\u5c31\u662f\u6765\u6247\u98ce\u70b9\u706b\u7684\uff0c\u6ca1\u60f3\u5230\u5927\u706b\u65e9\u5df2\u718a\u718a\u71c3\u70e7\u8d77\u6765\uff0c\u8981\u662f\u6709\u5a92\u4f53\u62a5\u5bfc\u845b\u5bb6\u6210\u5458\u88ab\u845b\u666f\u8bda\u957f\u5b50\u845b\u660e\u4fe1\u903c\u8feb\u9646\u7eed\u642c\u51fa\u4f4f\u5b85\u7684\u6d88\u606f\u62a5\u9053\uff0c\u5609\u4fe1\u96c6\u56e2\u65d7\u4e0b\u7684\u4e0a\u5e02\u516c\u53f8\u80a1\u4ef7\u5c06\u518d\u4e00\u6b21\u7684\u53d7\u5230\u91cd\u632b\uff0c\u845b\u660e\u4fe1\u8981\u6ca1\u6709\u4e00\u70b9\u597d\u624b\u6bb5\uff0c\u4e0d\u8981\u8bf4\u5b8c\u5168\u63a7\u5236\u5609\u4fe1\u96c6\u56e2\u4e86\uff0c\u53ea\u6015\u8fde\u4ed6\u73b0\u5728\u7684\u4f4d\u5b50\u4e5f\u4fdd\u4e0d\u4f4f\u3002</p><p>\u3000\u3000\u5f20\u606a\u6413\u4e86\u6413\u624b\uff0c\u95ee\u5b59\u9759\u9999\uff1a\u201c\u5b59\u59d0\uff0c\u8ddf\u4f60\u59b9\u59b9\u5bf9\u521a\u6b7b\u6389\u7684\u8001\u7237\u5b50\u611f\u60c5\u6df1\u4e0d\u6df1\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5b59\u9759\u9999\u5f88\u7591\u60d1\u5f20\u606a\u7a81\u7136\u8fd9\u4e48\u95ee\u3002</p><p>\u3000\u3000\u201c\u4f60\u95ee\u8fd9\u4e2a\u505a\u4ec0\u4e48\uff1f\u201d\u5b59\u9759\u6aac\u770b\u7740\u5f20\u606a\uff0c\u5947\u602a\u7684\u95ee\uff0c\u201c\u5f88\u6ca1\u826f\u5fc3\u7684\u8bf4\uff0c\u6211\u8ddf\u6211\u59d0\u59d0\u7684\u751f\u6d3b\uff0c\u4f1a\u8ba9\u5f88\u591a\u5973\u5b69\u5b50\u7fa1\u6155\uff0c\u4f46\u8fd9\u4e9b\u90fd\u4e0d\u662f\u6211\u8ddf\u6211\u59d0\u59d0\u6240\u60f3\u8981\u7684\uff0c\u6211\u4eec\u5230\u9999\u6e2f\u4e4b\u540e\uff0c\u90a3\u4e2a\u4eba\u5c31\u50cf\u4e00\u9053\u6c89\u91cd\u7684\u9634\u5f71\u538b\u5728\u6240\u6709\u845b\u5bb6\u4eba\u7684\u5fc3\u91cc\uff0c\u4f60\u8bf4\u4f1a\u6709\u591a\u6df1\u7684\u611f\u60c5\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u6df1\u5c31\u597d\uff0c\u201d\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u514d\u5f97\u8ba9\u4f60\u4eec\u8bef\u4f1a\u6211\u662f\u90a3\u79cd\u843d\u4e95\u4e0b\u77f3\u7684\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u4f60\u60f3\u600e\u4e48\u505a\uff1f\u201d\u5b59\u9759\u6aac\u6b6a\u7740\u5934\u95ee\uff0c\u5012\u5728\u731c\u5f20\u606a\u4f1a\u600e\u4e48\u843d\u4e95\u4e0b\u77f3\u3002</p><p>\u3000\u3000\u6709\u4ec0\u4e48\u4e8b\u60c5\u4e5f\u4e0d\u4f1a\u8ddf\u5b59\u9759\u6aac\u5546\u91cf\uff0c\u5f20\u606a\u6478\u4e86\u6478\u9f3b\u5934\uff0c\u793a\u610f\u53f6\u5efa\u658c\u5230\u9694\u58c1\u623f\u95f4\u8bf4\u8bdd\uff0c\u5b59\u9759\u6aac\u6c14\u9f13\u9f13\u3001\u9f13\u8d77\u816e\u7ed1\u5b50\u7684\u8bf4\uff1a\u201c\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\uff0c\u4e0d\u542c\u5c31\u4e0d\u542c\u5457\u2026\u2026\u201d\u663e\u7136\u5bf9\u8fd9\u79cd\u4e8b\u4e8b\u90fd\u7792\u7740\u5979\u7684\u884c\u4e3a\u5f88\u662f\u90c1\u95f7\u3002</p><p>\u3000\u3000\u4e0a\u56de\u5927\u5bb6\u501f\u7231\u8fbe\u7535\u5b50\u7ecf\u9500\u5546\u5927\u4f1a\u7684\u673a\u4f1a\u8d76\u5230\u6d77\u5dde\u6e38\u73a9\uff0c\u5b59\u9759\u6aac\u8981\u7167\u987e\u9152\u5427\uff0c\u6839\u672c\u8131\u4e0d\u5f00\u8eab\uff0c\u5168\u4e22\u7ed9\u4ee4\u5c0f\u71d5\u4e00\u4e2a\u4eba\uff0c\u5979\u81ea\u5df1\u4e5f\u6709\u4e9b\u4e8e\u5fc3\u4e0d\u5fcd\uff1b\u8fd9\u6b21\u8d76\u56de\u9999\u6e2f\u662f\u7279\u6b8a\u60c5\u51b5\uff0c\u4f55\u51b5\u4ee4\u5c0f\u71d5\u53ef\u4ee5\u62c9\u8bb8\u7ef4\u5e2e\u5fd9\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u4e45\u8d1f\u76db\u540d\u7684\u5f71\u6e7e\u56ed\u9732\u53f0\u9910\u5385\u7528\u4e8e\u665a\u9910\uff0c\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u7531\u5085\u5bb6\u4fca\u966a\u540c\u7740\u524d\u5f80\u957f\u8475\u533b\u9662\u540a\u5501\uff0c\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u59d0\u59b9\u4e5f\u6ca1\u6709\u966a\u540c\u524d\u5f80\u7684\u610f\u601d\uff0c\u8bb8\u601d\u4fbf\u4e5f\u7559\u4e0b\u6765\u966a\u5979\u4eec\u3002\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u672c\u6765\u5c31\u662f\u4e0d\u8bf7\u81ea\u6765\uff0c\u53ea\u662f\u4e8b\u524d\u901a\u77e5\u8fc7\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u4e24\u4eba\u3002</p><p>\u3000\u3000\u6d77\u5dde\u7684\u6c14\u6e29\u8fdb\u5165\u5341\u4e8c\u6708\u5c31\u964d\u5230\u96f6\u4e0b\u4e86\uff0c\u9999\u6e2f\u7684\u6c14\u5019\u5374\u6e29\u6696\u6e7f\u6da6\uff0c\u665a\u98ce\u62c2\u5728\u8138\u4e0a\u6b63\u662f\u6021\u4eba\uff0c\u53ea\u4f1a\u5728\u6625\u8282\u524d\u540e\u4e00\u4e2a\u6708\uff0c\u6e29\u5ea6\u4f1a\u6709\u660e\u663e\u7684\u4e0b\u964d\uff1b\u73b0\u5728\u79bb\u4e5d\u4e03\u5e74\u7684\u6625\u8282\u8fd8\u6709\u56db\u4e94\u5341\u5929\u3002</p><p>\u3000\u3000\u8bb8\u601d\u966a\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u59d0\u59b9\u5728\u623f\u95f4\u91cc\u804a\u5929\uff0c\u611f\u89c9\u6ca1\u8fc7\u591a\u4e45\uff0c\u5f20\u606a\u4ed6\u4eec\u5c31\u56de\u6765\u4e86\uff0c\u8bb8\u601d\u62ac\u7740\u770b\u4e86\u770b\u5899\u58c1\u4e0a\u60ac\u6302\u7740\u7684\u949f\u8868\uff0c\u624d\u4e5d\u70b9\u949f\uff0c\u5b59\u9759\u6aac\u53bb\u6253\u5f00\u623f\u95f4\uff0c\u770b\u89c1\u5979\u7236\u4eb2\u5b59\u5c1a\u4e49\uff0c\u8fd8\u6709\u5802\u8205\u845b\u660e\u5fb7\u4e5f\u4e00\u8d77\u8ddf\u7740\u8fc7\u6765\uff0c\u5947\u602a\u7684\u95ee\uff1a\u201c\u4f60\u4eec\u5230\u8fd9\u91cc\u6765\u505a\u4ec0\u4e48\uff0c\u4eca\u5929\u591c\u91cc\u4e0d\u9700\u8981\u5b88\u7075\uff1f\u201d</p><p>\u3000\u3000\u845b\u660e\u5fb7\u6ca1\u6709\u7406\u4f1a\u5b59\u9759\u6aac\u7684\u8bdd\uff0c\u53ea\u662f\u82e6\u7b11\u7740\u5bf9\u5b59\u5c1a\u4e49\u8bf4\uff1a\u201c\u4f60\u5efa\u8bae\u4e25\u52a0\u675c\u7edd\u5a92\u4f53\u8bb0\u8005\u6df7\u5165\u7075\u5802\u662f\u5b8c\u5168\u6b63\u786e\u7684\uff0c\u8981\u8ba9\u5a92\u4f53\u62cd\u4e0b\u4eca\u665a\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u660e\u5929\u7684\u80a1\u4ef7\u8fd8\u8981\u518d\u964d\u4e2a20\uff05\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff0c\u5728\u7075\u5802\u4e0a\u6253\u8d77\u6765\u4e86\uff1f\u201d\u5b59\u9759\u6aac\u5bf9\u6b7b\u8005\u53ef\u6ca1\u6709\u592a\u591a\u7684\u656c\u610f\uff0c\u5979\u8fd9\u4e48\u95ee\uff0c\u591a\u5c11\u6709\u4e9b\u5e78\u707e\u4e50\u7978\u7684\u610f\u5473\uff0c\u201c\u771f\u8981\u6253\u8d77\u6765\uff0c\u8fc7\u53bb\u540a\u5501\u7684\u4eba\u4e0d\u4f1a\u8ddf\u5a92\u4f53\u6cc4\u6f0f\u6d88\u606f\u554a\uff1f\u201d</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u77aa\u4e86\u5979\u4e00\u773c\uff0c\u8ba9\u5979\u4e0d\u8981\u80e1\u8bf4\u516b\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u5634\u89d2\u59cb\u7ec8\u6302\u7740\u4e00\u4e1d\u6d45\u7b11\uff0c\u8fd9\u65f6\u5019\u8bf4\u9053\uff1a\u201c\u8fd8\u597d\uff0c\u5f53\u65f6\u6ca1\u6709\u591a\u5c11\u4eba\uff0c\u9664\u4e86\u6211\u4eec\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u62e8\u5c31\u662f\u8ddf\u6211\u4eec\u4e00\u8d77\u4e58\u98de\u673a\u8fc7\u6765\u540a\u5501\u7684\u5ba2\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u54ea\u91cc\u77e5\u9053\u9526\u6e56\u4e0e\u6b63\u6cf0\u4e4b\u95f4\u7684\u6069\u6028\uff0c\u4f55\u51b5\u5728\u673a\u573a\u65f6\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u5c06\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u7b49\u4eba\u6307\u51fa\u6765\u7ed9\u5979\u770b\u3002</p><p>\u3000\u3000\u845b\u660e\u5fb7\u5634\u89d2\u62bd\u52a8\u4e86\u4e00\u4e0b\uff0c\u7b11\u5bb9\u6781\u5176\u7684\u82e6\u6da9\uff1a\u201c\u771f\u4e0d\u6653\u5f97\u963f\u4f2f\u5728\u4e5d\u6cc9\u4f1a\u600e\u4e48\u60f3\uff0c\u4ed6\u751f\u524d\u53ef\u4ee5\u5343\u65b9\u767e\u8ba1\u7684\u60f3\u5c06\u6574\u4e2a\u845b\u5bb6\u90fd\u7ef4\u7cfb\u5728\u4e00\u4e2a\u6574\u4f53\u4e4b\u5185\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u53ea\u662f\u4ed6\u7684\u4e00\u53a2\u60c5\u613f\u7f62\u4e86\uff0c\u8fd9\u4e48\u591a\u5e74\u6765\uff0c\u5927\u5bb6\u90fd\u6d3b\u5728\u4ed6\u7684\u9634\u5f71\u4e4b\u4e0b\uff0c\u5c31\u7b97\u6ca1\u6709\u8d22\u4ea7\u7684\u7eb7\u4e89\uff0c\u6211\u60f3\u5f88\u591a\u4eba\u90fd\u4f1a\u6025\u4e0d\u53ef\u8010\u7684\u79bb\u5f00\u5427\u2026\u2026\u201d\u5b59\u9759\u6aac\u4e0d\u5c51\u7684\u8bf4\u9053\u3002</p></article>"}}}]);
