(window.webpackJsonp=window.webpackJsonp||[]).push([[618],{641:function(p){p.exports={chapterTitle:null,title:"\u7b2c618\u7ae0 \u5370\u5c3c\u5927\u706b",content:"<article><h2>\u7b2c618\u7ae0 \u5370\u5c3c\u5927\u706b</h2><p>\u3000\u3000\u5c06\u5b59\u9759\u6aac\u9001\u56de\u97f3\u4e50\u5b66\u9662\uff0c\u5b59\u9759\u6aac\u4e5f\u6ca1\u8bf4\u8981\u5c06\u886c\u886b\u8fd8\u4ed6\uff0c\u5f20\u606a\u81ea\u7136\u4e0d\u80fd\u5c06\u8863\u670d\u4ece\u5979\u8eab\u4e0a\u6252\u4e0b\u6765\u2014\u2014\u5012\u662f\u6709\u8fd9\u4e2a\u5ff5\u5934\uff0c\u9a91\u8f66\u56de\u4e1c\u5927\uff0c\u675c\u98de\u4e0e\u8499\u4e50\u8fd8\u5728\u6559\u5de5\u98df\u5802\u559d\u9152\uff0c\u4fbf\u8fc7\u53bb\u518d\u966a\u4ed6\u4eec\u4e00\u8d77\u559d\uff0c\u6240\u8c13\u6027\u60c5\u76f8\u6295\uff0c\u675c\u98de\u4e0e\u8499\u4e50\u5012\u4e5f\u662f\u76f8\u8c08\u751a\u6b22\u3002</p><p>\u3000\u3000\u63a5\u4e0b\u6765\u51e0\u5929\u5012\u662f\u5b89\u9759\uff0c\u5b59\u9759\u6aac\u8981\u5fd9\u7740\u9152\u5427\u7684\u88c5\u6f62\uff0c\u675c\u98de\u8bf4\u4e86\u8981\u5728\u5b66\u5e9c\u5df7\u7b79\u529e\u4e00\u95f4\u7f51\u5427\uff0c\u4ed6\u8981\u82b1\u65f6\u95f4\u51c6\u5907\u7684\u4e8b\u60c5\u5c31\u66f4\u591a\u4e86\u3002</p><p>\u3000\u3000\u8fc7\u5341\u5e74\u518d\u56de\u5934\u6765\u770b\uff0c\u4e5d\u4e03\u5e74\u4e5d\u6708\u5efa\u90ba\u5e02\u5df2\u6709\u7684\u51e0\u5bb6\u7f51\u5427\u90fd\u80fd\u79f0\u5f97\u4e0a\u8fdc\u53e4\u7684\u8bb0\u5fc6\uff0c\u5f53\u65f6\u5efa\u90ba\u5e02\u8f83\u4e3a\u51fa\u540d\u4e14\u8f83\u4e3a\u6b63\u89c4\u7684\u7f51\u662f\u5e02\u4e2d\u5fc3\u4e00\u5bb6\u540d\u53eb\u5a01\u5c14\u7279\u7684\u7f51\u5427\uff0c\u7531\u4e00\u5bb6\u79d1\u6280\u516c\u53f8\u4e0e\u90ae\u7535\u5c40\u51fa\u8d44\u5408\u529e\uff0c\u6700\u65e9\u6210\u7acb\u4e8e\u4e5d\u516d\u5e74\u5e95\u3002</p><p>\u3000\u3000\u675c\u98de\u5e94\u8058\u53bb\u5a01\u5c14\u7279\u5f53\u7f51\u5427\u7ba1\u7406\u5458\u4e86\u3002</p><p>\u3000\u3000\u6ca1\u6709\u4eb2\u8eab\u53bb\u6478\u7d22\uff0c\u5f88\u96be\u5c06\u4e00\u4e2a\u884c\u4e1a\u7406\u89e3\u900f\u5f7b\uff1b\u7ec6\u8282\u4e0a\u7684\u4e8b\u9879\uff0c\u4e0d\u662f\u51ed\u60f3\u8c61\u5c31\u80fd\u6784\u5efa\u5b8c\u6574\u7684\u3002\u7279\u522b\u662f\u4e5d\u4e03\u5e74\uff0c\u5728\u56fd\u5185\uff0c\u7f51\u5427\u884c\u4e1a\u5185\u8fd8\u90fd\u5904\u4e8e\u9ed1\u6697\u4e2d\u7684\u6478\u7d22\u671f\uff0c\u6ca1\u6709\u592a\u591a\u7684\u7ecf\u9a8c\u6559\u8bad\u53ef\u4ee5\u501f\u9274\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ee5\u4e3a\u675c\u98de\u7684\u89c1\u8bc6\u4e0e\u6c14\u9b44\u80fd\u8ba9\u4ed6\u8d85\u524d\u522b\u4eba\u4e00\u6b65\uff0c\u4e5f\u5c31\u6ca1\u6709\u60f3\u7740\u989d\u5916\u7684\u53bb\u6307\u624b\u753b\u811a\u3002</p><p>\u3000\u3000\u675c\u98de\u4e0d\u89c1\u4eba\u5f71\uff0c\u5f20\u606a\u4fbf\u575a\u6301\u7740\u6bcf\u5929\u53bb\u5e74\u7ea7\u529e\u62a5\u5230\u5e94\u536f\u5199\u6d77\u62a5\u80e1\u6df7\u65e5\u5b50\uff0c\u5012\u662f\u6709\u673a\u4f1a\u627e\u8499\u4e50\u4e00\u8d77\u559d\u9152\u804a\u5929\uff0c\u5bf9\u5916\u754c\u7684\u6d88\u606f\u4e5f\u6709\u4e9b\u6f20\u4e0d\u5173\u5fc3\u3002</p><p>\u3000\u3000\u4e5d\u6708\u5341\u516d\u65e5\uff0c\u6cf0\u56fd\u64a4\u6362\u7b2c\u4e09\u4efb\u8d22\u653f\u90e8\uff0c\u4ee5\u653e\u5f03\u4e00\u90e8\u5206\u7ecf\u6d4e\u4e3b\u6743\u4e3a\u6761\u4ef6\u6b63\u5f0f\u5411\u56fd\u9645\u8d27\u5e01\u57fa\u91d1\u7ec4\u7ec7\u63d0\u8bf7\u6551\u63f4\uff0c\u4f46\u662f\u5f20\u606a\u77e5\u9053\u6cf0\u56fd\u7684\u7ecf\u6d4e\u5d29\u6e83\u4f1a\u6765\u5f97\u66f4\u8fc5\u901f\u3001\u66f4\u5f7b\u5e95\u3002</p><p>\u3000\u3000\u4e09\u4e2a\u6708\u524d\uff0c\u6cf0\u56fd\u7684\u4e2d\u5c42\u4eba\u58eb\u8fd8\u6234\u7740\u745e\u58eb\u540d\u8868\uff0c\u7a7f\u7740\u6cd5\u56fd\u540d\u724c\u8863\u88e4\uff0c\u559d\u7740\u77e5\u540d\u9152\u5e84\u51fa\u7684\u8461\u8404\u9152\uff0c\u5468\u672b\u4e00\u5bb6\u4eba\u4f1a\u53bb\u6d77\u8fb9\u522b\u5885\u6e21\u5047\uff0c\u73b0\u5728\u4ed6\u4eec\u5374\u4e0d\u5f97\u4e0d\u5c06\u8f66\u5b50\u62ff\u53bb\u62b5\u62bc\u4ee5\u5f25\u8865\u4e00\u90e8\u5206\u635f\u5931\u2014\u2014\u5373\u4f7f\u6709\u8f66\u5b50\u4e5f\u4f9b\u517b\u4e0d\u8d77\u3002\u91d1\u878d\u53ca\u5b9e\u4e1a\u673a\u6784\u8fde\u7eed\u5012\u95ed\uff0c\u5931\u4e1a\u4eba\u5458\u5de8\u589e\uff0c\u4e5d\u6708\u521a\u5f00\u5b66\u7684\u79c1\u7acb\u5b66\u6821\u7684\u62a5\u540d\u7387\u4e5f\u9aa4\u7136\u4e0b\u964d\u2014\u2014\u4ee5\u524d\u662f\u6324\u4e5f\u6324\u4e0d\u8fdb\u53bb\u7684\uff0c\u79c1\u7acb\u533b\u9662\u4e5f\u9762\u4e34\u540c\u6837\u7684\u7a98\u51b5\uff0c\u6cf0\u56fd\u7684\u9ad8\u6d88\u8d39\u4eba\u7fa4\u4eff\u4f5b\u7ed9\u7a81\u7136\u5176\u5982\u7684\u91d1\u878d\u98ce\u66b4\u88ad\u5377\u8d70\u846c\u6ca1\u6d77\u5e95\u4f3c\u7684\u6d88\u5931\u4e86\uff0c\u9ec4\u91d1\u4e0e\u94bb\u77f3\u7684\u9500\u552e\u4e0b\u964d\u516b\u4e5d\u6210\uff0c\u8c6a\u534e\u5546\u5e97\u5185\u95e8\u53ef\u7f57\u96c0\u2026\u2026</p><p>\u3000\u3000\u8fd9\u4e9b\u90fd\u662f\u9648\u4fe1\u4e1c\u3001\u848b\u8587\u7b49\u4eba\u4e1c\u5357\u4e9a\u4e4b\u884c\u901a\u8fc7\u7535\u5b50\u90ae\u4ef6\u5411\u5f20\u606a\u63cf\u8ff0\u7684\u6cf0\u56fd\u793e\u4f1a\u73b0\u72b6\uff1b\u9648\u4fe1\u751f\u5728\u90ae\u4ef6\u91cc\u5199\u9053\uff1a\u201c\u6b64\u65f6\u7684\u6cf0\u56fd\u53ef\u4ee5\u8fd9\u4e48\u5f62\u5bb9\uff1a\u2018\u4eca\u5929\u5f00\u51fa\u516c\u8def\u7684\u8f66\u6bd4\u5f00\u4e0a\u516c\u8def\u7684\u8f66\u591a\uff01\u2019\u201d</p><p>\u3000\u3000\u8bb8\u591a\u4eba\u90fd\u4f1a\u5c06\u4f4e\u7eac\u5ea6\u5730\u533a\u590f\u5b63\u68ee\u6797\u7684\u6797\u706b\u89c6\u4e3a\u6b63\u5e38\u73b0\u8c61\uff0c\u7b49\u7740\u96e8\u5b63\u6765\u4e34\uff0c\u81ea\u7136\u4f1a\u5c06\u56e0\u590f\u5b63\u5e72\u65f1\u800c\u71c3\u8d77\u7684\u6797\u706b\u6d47\u706d\uff0c\u603b\u4e4b\uff0c\u5370\u5c3c\u82cf\u95e8\u7b54\u814a\u5c9b\u4e0e\u52a0\u91cc\u66fc\u4e39\u70ed\u5e26\u96e8\u6797\u7684\u6797\u706b\u6ca1\u6709\u4e00\u5e74\u4f1a\u7edd\u8ff9\u4e0d\u51fa\u73b0\u3002</p><p>\u3000\u3000\u516b\u6708\u4e2d\u65ec\uff0c\u5370\u5c3c\u82cf\u95e8\u7b54\u814a\u5c9b\u4e0e\u52a0\u91cc\u66fc\u4e39\u70ed\u5e26\u96e8\u6797\u7684\u718a\u718a\u5927\u706b\u53c8\u5f00\u59cb\u71c3\u70e7\u8d77\u6765\uff0c\u5370\u5c3c\u4eba\u5f53\u65f6\u8fd8\u4e0d\u6e05\u695a\u8fd9\u5c06\u7ed9\u4ed6\u4eec\u7684\u672a\u6765\u5e26\u53bb\u591a\u5927\u7684\u9634\u5f71\u3002</p><p>\u3000\u3000\u4e00\u76f4\u5230\u4e5d\u6708\u4e2d\u65ec\uff0c\u5728\u96e8\u5b63\u7684\u7279\u5927\u66b4\u96e8\u6d47\u900f\u5927\u5730\u597d\u51e0\u56de\u4e4b\u540e\uff0c\u4e24\u5730\u7684\u6797\u706b\u8fd8\u6ca1\u6709\u7ed9\u6d47\u706d\uff0c\u5370\u5c3c\u4eba\u5f00\u59cb\u610f\u8bc6\u5230\u8fd9\u6b21\u53ef\u80fd\u4f1a\u6bd4\u8f83\u9ebb\u70e6\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4f5c\u4e3a\u56fd\u5185\u4f01\u4e1a\u5bb6\u4ee3\u8868\u5341\u4e03\u65e5\u968f\u5916\u7ecf\u8d38\u90e8\u5e38\u52a1\u526f\u90e8\u957f\u3001\u4e2d\u8054\u529e\u526f\u4e3b\u4efb\u53f6\u81fb\u6c11\u51fa\u8bbf\u65b0\u52a0\u5761\u3002\u5370\u5c3c\u68ee\u6797\u706b\u707e\u4ea7\u751f\u7684\u70df\u5c18\u5df2\u7ecf\u5f71\u54cd\u5230\u65b0\u52a0\u5761\u4e86\uff0c\u9648\u4fe1\u751f\u5728\u7535\u5b50\u90ae\u4ef6\u91cc\u63cf\u8ff0\u5230\u4ed6\u4eec\u4e58\u5750\u5230\u65b0\u52a0\u5761\u7684\u98de\u673a\u7531\u4e8e\u7ed9\u7a81\u5982\u5176\u6765\u7684\u5927\u96fe\u4e0d\u5f97\u4e0d\u5728\u65b0\u52a0\u5761\u4e0a\u7a7a\u591a\u76d8\u65cb\u4e86\u4e00\u4e2a\u591a\u5c0f\u65f6\u624d\u5f97\u4ee5\u964d\u843d\u7684\u60c5\u5f62\u3002</p><p>\u3000\u3000\u65e9\u5728\u4e00\u5e74\u524d\uff0c\u5c31\u65d7\u5e1c\u9c9c\u660e\u7684\u6307\u51fa\u4e1c\u5357\u4e9a\u7ecf\u6d4e\u4f53\u7cfb\u4e2d\u7684\u5f0a\u7aef\u5e76\u9884\u8a00\u5230\u8fd9\u8d77\u91d1\u878d\u98ce\u66b4\u7684\u53f6\u81fb\u6c11\u5728\u4e1c\u5357\u4e9a\u91d1\u878d\u98ce\u66b4\u771f\u6b63\u66b4\u53d1\u4e4b\u540e\u58f0\u540d\u9e4a\u8d77\uff0c\u771f\u6b63\u6700\u65e9\u5bf9\u4e1c\u5357\u4e9a\u7ecf\u6d4e\u4f53\u7cfb\u5371\u9762\u6709\u6240\u8b66\u60d5\u7684\u7f8e\u56fd\u7ecf\u6d4e\u5b66\u5bb6\u683c\u9c81\u683c\u66fc\u53cd\u800c\u4e0d\u662f\u5f71\u54cd\u6700\u5927\u7684\u4eba\u7269\u3002</p><p>\u3000\u3000\u9999\u6e2f\u521a\u521a\u56de\u5f52\uff0c\u4e2d\u592e\u653f\u5e9c\u6700\u5927\u7684\u52aa\u529b\u5c31\u662f\u8981\u4fdd\u6301\u9999\u6e2f\u7ecf\u6d4e\u7a33\u5b9a\uff0c\u4e0e\u4e1c\u4e9a\u5404\u56fd\u653f\u5e9c\u8054\u624b\u62b5\u6297\u91d1\u878d\u5de8\u9cc4\u4e5f\u662f\u5fc5\u7136\u7684\u9009\u62e9\uff0c\u4e5f\u662f\u9002\u5f53\u53d1\u6325\u4e0e\u5c55\u793a\u5927\u56fd\u529b\u91cf\u7684\u65f6\u5019\uff0c\u53f6\u81fb\u6c11\u81ea\u7136\u662f\u63a8\u52a8\u8fd9\u65b9\u9762\u5de5\u4f5c\u7684\u6700\u5408\u9002\u51fa\u9762\u7684\u4eba\u9009\u3002</p><p>\u3000\u3000\u5728\u56fd\u9645\u8d27\u5e01\u57fa\u91d1\u7ec4\u7ec7\u8fd8\u5728\u5201\u96be\u6cf0\u56fd\u653f\u5e9c\u3001\u63d0\u51fa\u5404\u79cd\u82db\u523b\u6761\u4ef6\u4e4b\u65f6\uff0c\u53f6\u81fb\u6c11\u5411\u56fd\u5185\u653f\u5e9c\u5efa\u8bae\u5411\u6cf0\u56fd\u653f\u5e9c\u63d0\u4f9b20\u7f8e\u5143\u7684\u653f\u5e9c\u8d37\u6b3e\u4ee5\u8d44\u52a9\u5176\u71ac\u8fc7\u6700\u8270\u96be\u7684\u4e00\u523b\u3002</p><p>\u3000\u3000\u65b0\u52a0\u5761\u5f53\u5c40\u4e00\u76f4\u4ee5\u6765\u662f\u6cf0\u56fd\u653f\u5e9c\u6700\u575a\u5b9a\u7684\u652f\u6301\u8005\uff0c\u53ea\u662f\u65b0\u52a0\u5761\u5f53\u5c40\u8981\u987e\u8651\u5230\u5927\u91cf\u7684\u56fd\u9645\u6e38\u8d44\u4e00\u76f4\u5728\u6301\u7eed\u4e0d\u65ad\u7684\u51b2\u51fb\u4ed6\u4eec\u7684\u8d27\u5e01\u4f53\u7cfb\uff0c\u62bd\u4e0d\u51fa\u529b\u91cf\u53bb\u63f4\u52a9\u6cf0\u56fd\u653f\u5e9c\u4e0e\u5176\u4ed6\u5468\u8fb9\u56fd\u5bb6\uff0c\u56fd\u5185\u6b64\u65f6\u63f4\u624b\u6cf0\u56fd\u653f\u5e9c\uff0c\u8fd8\u80fd\u591f\u8fdb\u4e00\u6b65\u52a0\u5f3a\u4e0e\u65b0\u52a0\u5761\u5f53\u5c40\u7684\u5173\u7cfb\uff0c\u4e5f\u4e3a\u6676\u5706\u5382\u9879\u76ee\u4ece\u65b0\u52a0\u5761\u5f15\u8fdb\u5236\u9020\u6280\u672f\u94fa\u5e73\u9053\u8def\u3002</p><p>\u3000\u3000\u53f6\u81fb\u6c11\u51fa\u8bbf\u65b0\u52a0\u5761\u4e4b\u540e\uff0c\u8fd8\u4f1a\u524d\u5f80\u83f2\u5f8b\u5bbe\uff0c\u9648\u4fe1\u751f\u5148\u4e00\u6b65\u8fd4\u56de\u5efa\u90ba\uff0c\u5411\u5f20\u606a\u6c47\u62a5\u4ed6\u6b64\u6b21\u4e1c\u5357\u4e9a\u4e4b\u884c\u7684\u4e00\u4e9b\u7ec6\u8282\u53ca\u6210\u679c\u3002</p><p>\u3000\u3000\u5927\u6982\u662f\u5370\u5c3c\u68ee\u6797\u706b\u707e\u5f25\u6f2b\u51fa\u56fd\u5883\u7684\u70df\u5c18\u7ed9\u9648\u4fe1\u751f\u7559\u5728\u592a\u591a\u6df1\u523b\u7684\u5370\u8c61\uff0c\u9648\u4fe1\u751f\u8ddf\u5f20\u606a\u89c1\u5230\u9762\uff0c\u518d\u6b21\u63d0\u53ca\u7ed9\u56f0\u5728\u65b0\u52a0\u5761\u4e0a\u7a7a\u4e00\u4e2a\u591a\u5c0f\u65f6\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5e94\u8be5\u662f\u65b0\u52a0\u5761\u7684\u96e8\u5b63\uff0c\u5927\u96fe\u5929\u6c14\u5012\u662f\u5f88\u5c11\u89c1\uff0c\u5f20\u606a\u4e5f\u5c31\u53bb\u8fc7\u4e24\u56de\u65b0\u52a0\u5761\uff0c\u5bf9\u90a3\u4e2a\u82b1\u56ed\u57ce\u5e02\u578b\u7684\u8896\u73cd\u56fd\u5bb6\uff0c\u5e76\u6ca1\u6709\u7279\u522b\u6df1\u523b\u7684\u5370\u8c61\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u6b64\u53ea\u662f\u6de1\u6de1\u7684\u4e00\u7b11\u3002</p><p>\u3000\u3000\u201c\u4f60\u4f30\u8ba1\u5370\u5c3c\u7684\u68ee\u6797\u5927\u706b\u4f1a\u70e7\u591a\u4e45\uff1f\u201d\u5f20\u606a\u660e\u77e5\u6545\u95ee\uff0c\u6ca1\u6709\u4eba\u6bd4\u66f4\u6e05\u695a\u8fd9\u573a\u5927\u706b\u8fd8\u4f1a\u6301\u7eed\u591a\u4e45\uff0c\u81f3\u5c11\u8981\u6301\u7eed\u5230\u5341\u4e8c\u6708\u5e95\u5927\u7684\u706b\u6e90\u624d\u4f1a\u7184\u706d\u3002\u8fd9\u65f6\u5019\u9664\u4e86\u4ed6\uff0c\u5927\u6982\u6ca1\u6709\u4eba\u610f\u8bc6\u5230\u8fd9\u662f\u82cf\u95e8\u7b54\u814a\u5c9b\u4e0e\u52a0\u91cc\u66fc\u4e39\u7684\u5730\u4e0b\u7164\u5c42\u5df2\u7ecf\u71c3\u70e7\u8d77\u6765\u4e86\u5427\uff1f\u5373\u4f7f\u5730\u4e0a\u706b\u7130\u7ed9\u66b4\u96e8\u6d47\u706d\uff0c\u5730\u4e0b\u706b\u6e90\u4e5f\u4f1a\u5e38\u5e74\u4e0d\u7184\uff0c\u9664\u6b64\u5730\u8868\u7684\u70ed\u5e26\u96e8\u6797\u7ed9\u70e7\u5b8c\u3002</p><p>\u3000\u3000\u201c\u8fd8\u771f\u96be\u5224\u5b9a\uff0c\u201d\u9648\u4fe1\u751f\u5634\u5507\u6324\u7740\uff0c\u201c\u6ca1\u6709\u65f6\u95f4\u8dd1\u5230\u5370\u5c3c\u53bb\u770b\u4e00\u773c\u2026\u2026\u201d</p><p>\u3000\u3000\u4e9a\u6d32\u56db\u5c0f\u864e\u4e2d\uff0c\u5370\u5c3c\u662f\u7ecf\u6d4e\u6700\u843d\u540e\u7684\u4e00\u53ea\u8f6f\u5f31\u5c0f\u864e\uff0c\u4e5f\u662f\u7ecf\u6d4e\u906d\u53d7\u6467\u6b8b\u6700\u5f7b\u5e95\u3001\u6700\u5f97\u4e0d\u5230\u5c0a\u91cd\u7684\u4e00\u53ea\u5c0f\u864e\uff0c\u5f20\u606a\u751a\u81f3\u5728\u516d\u6708\u521d\u5c31\u6307\u793a\u7231\u8fbe\u7535\u5b50\u5f7b\u5e95\u653e\u5f03\u5370\u5c3c\u7684\u5e02\u573a\uff0c\u9648\u4fe1\u751f\u4e1c\u5357\u4e9a\u4e4b\u884c\u81ea\u7136\u5c31\u6ca1\u6709\u5c06\u5370\u5c3c\u5b89\u6392\u5728\u884c\u7a0b\u4e4b\u5185\u3002</p><p>\u3000\u3000\u201c\u6211\u770b\u4f1a\u8fd8\u4f1a\u6301\u7eed\u76f8\u5f53\u957f\u7684\u65f6\u95f4\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6211\u8fd9\u4e9b\u5929\u4e00\u76f4\u5728\u770b\u76f8\u5f53\u65b9\u9762\u7684\u65b0\u95fb\u62a5\u9053\u3002\u5370\u5c3c\u662f\u4e9a\u6d32\u5730\u533a\u6700\u5927\u7684\u6728\u6d46\u4f9b\u5e94\u56fd\uff0c\u5370\u5c3c\u7684\u70ed\u5e26\u68ee\u6797\u706b\u707e\u7ee7\u7eed\u8513\u5ef6\u4e0b\u53bb\uff0c\u53ef\u80fd\u4f1a\u8f83\u957f\u65f6\u95f4\u7684\u5f71\u54cd\u5230\u4e9a\u6d32\u5730\u533a\u7684\u6728\u6d46\u4f9b\u5e94\u2026\u2026\u201d\u5f20\u606a\u90fd\u89c9\u5f97\u81ea\u5df1\u8fd9\u4e48\u8bf4\u5f88\u865a\u4f2a\uff0c\u8fd9\u6839\u672c\u5c31\u662f\u4e00\u573a\u957f\u671f\u76f4\u63a5\u5f71\u54cd\u5168\u7403\u6797\u4e1a\u8d44\u6e90\u4f9b\u6c42\u5173\u7cfb\u7684\u68ee\u6797\u5927\u706b\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u56de\u5efa\u90ba\u5411\u5f20\u606a\u6c47\u62a5\u4e1c\u5357\u4e9a\u4e4b\u884c\u7684\u60c5\u51b5\uff0c\u5efa\u90ba\u8fd9\u8fb9\u4e5f\u53ea\u6709\u5468\u6e38\u6709\u8d44\u683c\u8fc7\u6765\u51d1\u70ed\u95f9\uff0c\u5468\u6e38\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u5fae\u8e59\u7740\u7709\u5934\uff0c\u8ba4\u771f\u7684\u8003\u8651\u8fd9\u79cd\u53ef\u80fd\u6027\u2014\u2014\u5370\u5c3c\u68ee\u6797\u5927\u707e\u5df2\u7ecf\u8feb\u4f7f\u5370\u5c3c\u4e34\u8fd1\u70ed\u5e26\u96e8\u6797\u8fb9\u7f18\u7684\u8bb8\u591a\u5236\u6d46\u5de5\u5382\u505c\u5de5\uff0c\u4f7f\u5f97\u4e9a\u6d32\u5730\u533a\u7684\u6728\u6d46\u4f9b\u5e94\u504f\u7d27\uff0c\u53ea\u662f\u5927\u5bb6\u90fd\u6ca1\u6709\u610f\u8bc6\u5230\u8fd9\u79cd\u60c5\u51b5\u4f1a\u4e00\u76f4\u52a0\u5267\u5e76\u957f\u65f6\u95f4\u7684\u6301\u7eed\u4e0b\u53bb\uff0c\u90fd\u4ee5\u4e3a\u968f\u96e8\u5b63\u6765\u4e34\u7684\u66b4\u96e8\u4f1a\u6d47\u706d\u5370\u5c3c\u7684\u68ee\u6797\u5927\u706b\uff0c\u5c06\u4fc3\u4f7f\u4e9a\u6d32\u5730\u533a\u7684\u6728\u6d46\u4f9b\u5e94\u6062\u590d\u5e73\u8861\u3002</p><p>\u3000\u3000\u4ee5\u65b0\u5149\u7eb8\u4e1a\u7684\u89c4\u6a21\uff0c\u5927\u91cf\u7684\u56e4\u79ef\u6728\u6d46\u4e5f\u4e0d\u53ef\u80fd\uff0c\u5f20\u606a\u9009\u5b9a\u7684\u9053\u8def\u5c31\u662f\u79ef\u6781\u8d70\u51fa\u6765\uff0c\u8d81\u7740\u91d1\u878d\u98ce\u66b4\u5439\u6b8b\u4e1c\u5357\u4e9a\u7ecf\u6d4e\u3001\u4f01\u4e1a\u4e25\u91cd\u7f3a\u4e4f\u878d\u8d44\u6e20\u9053\u4e4b\u9645\uff0c\u51fa\u8d44\u6536\u8d2d\u4e00\u5230\u4e24\u5bb6\u7684\u5236\u6d46\u4f01\u4e1a\u7684\u80a1\u6743\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u95ee\u9648\u4fe1\u751f\uff1a\u201c\u90ed\u677e\u5ef6\u5148\u751f\u4e0a\u56de\u6765\u5efa\u90ba\u65f6\uff0c\u4e5f\u8bf4\u660e\u90ed\u6c0f\u96c6\u56e2\u624b\u91cc\u7684\u8d44\u91d1\u504f\u7d27\uff0c\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u613f\u610f\u51fa\u552e\u65d7\u4e0b\u5236\u6d46\u4f01\u4e1a\u7684\u80a1\u6743\uff1f\u201d</p><p>\u3000\u3000\u90ed\u6c0f\u4e91\u6e90\u96c6\u56e2\u65d7\u4e0b\u6700\u5927\u89c4\u6a21\u7684\u662f\u98df\u54c1\u4f01\u4e1a\uff0c\u4e0d\u8fc7\u5176\u4e91\u6e90\u7eb8\u4e1a\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u4e5f\u9887\u6709\u540d\u6c14\u3002</p><p>\u3000\u3000\u201c\u4ec5\u4ec5\u662f\u5236\u6d46\u4f01\u4e1a\uff1f\u201d\u5468\u6e38\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u9020\u7eb8\u6280\u672f\u4ed6\u4eec\u613f\u610f\u8f6c\u8ba9\uff0c\u6211\u4eec\u4e5f\u53ef\u4ee5\u8ddf\u4ed6\u4eec\u8c08\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4e0d\u8003\u8651\u6728\u6d46\u4f9b\u5e94\u53ef\u80fd\u957f\u671f\u504f\u7d27\u7684\u60c5\u51b5\uff0c\u6211\u4eec\u4e5f\u8981\u5728\u6d77\u5916\u5bfb\u627e\u7a33\u5b9a\u7684\u6728\u6d46\u4f9b\u5e94\u6e90\u3002\u65b0\u5149\u7eb8\u4e1a\u7684\u89c4\u6a21\u65e5\u76ca\u5e9e\u5927\uff0c\u4e09\u5e74\u65f6\u95f4\uff0c\u4ec5\u7eb8\u4e1a\u56fa\u5b9a\u8d44\u4ea7\u7684\u6295\u8d44\u5c31\u8d85\u8fc7\u5341\u4e8c\u4ebf\uff0c\u7279\u522b\u662f\u65b0\u6295\u8d44\u7684\u4e8c\u671f\u5de5\u7a0b\u5373\u5c06\u6295\u4ea7\uff0c\u52a0\u4e0a\u4e4b\u540e\u8fd8\u4f1a\u6301\u7eed\u4e0d\u65ad\u7684\u6269\u5f20\uff0c\u9700\u8981\u5728\u6d77\u5916\u5bfb\u6c42\u7a33\u5b9a\u7684\u6728\u6d46\u4f9b\u8d27\u6e90\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u6211\u4eec\u5728\u56fd\u5185\u7684\u8425\u6797\u901f\u5ea6\u4e5f\u5728\u4e0d\u65ad\u7684\u63d0\u9ad8\uff0c\u540e\u671f\u52bf\u5fc5\u8981\u5efa\u5236\u6d46\u5de5\u5382\uff0c\u6b64\u65f6\u6536\u8d2d\u6d77\u5916\u5236\u6d46\u4f01\u4e1a\u80a1\u6743\uff0c\u4e5f\u7b97\u662f\u4e3a\u65e5\u540e\u505a\u4e9b\u51c6\u5907\u2026\u2026\u201d</p><p>\u3000\u3000\u516d\u6708\uff0c\u5f20\u606a\u5c06\u8d8a\u79c0\u63a7\u80a1\u53bb\u5e74\u7684\u6536\u76ca\u8f6c\u79fb\u5230\u56fd\u5185\u51d1\u4e86\u4e2a\u6574\u6570\u4e00\u5171\u5411\u65b0\u5149\u7eb8\u4e1a\u7684\u8425\u6797\u9879\u76ee\u8ffd\u52a0\u4e86\u56db\u4e2a\u4ebf\u7684\u6295\u8d44\uff0c\u5982\u6b64\u5927\u89c4\u6a21\u7684\u8425\u6797\uff0c\u540e\u671f\u52bf\u5fc5\u8981\u7b79\u5efa\u5236\u6d46\u5de5\u5382\u6765\u6d88\u5316\u5927\u91cf\u7684\u5de5\u4e1a\u6797\u8d44\u6e90\uff0c\u6700\u7ec8\u5f62\u6210\u5b8c\u6574\u7684\u6797\u4e1a\u4e00\u4f53\u5316\u7684\u5927\u7eb8\u4e1a\u4f53\u7cfb\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u8fd8\u4e0d\u80fd\u7b97\u56fd\u5185\u6700\u5927\u89c4\u6a21\u7684\u7eb8\u4e1a\u96c6\u56e2\uff0c\u4f46\u662f\u8425\u6797\u89c4\u6a21\u5374\u8981\u8fdc\u8fdc\u8d85\u8fc7\u540c\u884c\u4e1a\u8005\uff0c\u56fd\u5185\u7684\u6797\u7eb8\u4e00\u4f53\u5316\u5de5\u7a0b\u624d\u521a\u521a\u8d77\u6b65\uff0c\u65b0\u5149\u7eb8\u4e1a\u7684\u9b44\u529b\u4e0e\u51b3\u5b9a\u4e5f\u662f\u4ee4\u4eba\u53f9\u4e3a\u89c2\u6b62\u7684\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u5468\u6e38\u70b9\u70b9\u5934\uff0c\u4e0d\u8fc7\u8fd8\u662f\u6709\u4e9b\u8fdf\u7591\uff0c\u201c\u9a6c\u6765\u897f\u4e9a\u7684\u8d27\u5e01\u8fd8\u6709\u8fdb\u4e00\u6b65\u8d2c\u503c\u7684\u53ef\u80fd\uff0c\u6b64\u65f6\u51fa\u624b\u4f1a\u4e0d\u4f1a\u7a0d\u5acc\u65e9\u4e86\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u4e5f\u8ba4\u4e3a\u9a6c\u6765\u897f\u4e9a\u7684\u8d27\u5e01\u4f1a\u6709\u8fdb\u4e00\u6b65\u8d2c\u503c\u7684\u53ef\u80fd\uff0c\u4e0d\u8fc7\u9a6c\u6765\u897f\u4e9a\u7684\u7ecf\u6d4e\u57fa\u7840\uff0c\u662f\u4e9a\u6d32\u56db\u5c0f\u864e\u5f53\u4e2d\u6700\u597d\u7684\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u5f53\u91d1\u878d\u98ce\u66b4\u8fc7\u53bb\uff0c\u9a6c\u6765\u897f\u4e9a\u7684\u7ecf\u6d4e\u6709\u53ef\u80fd\u6700\u65e9\u590d\u82cf\uff0c\u8d27\u5e01\u6c47\u7387\u81ea\u7136\u4e5f\u4f1a\u56de\u5347\u5230\u6b63\u5e38\u6c34\u5e73\uff0c\u51fa\u8d44\u6536\u8d2d\u80a1\u6743\u53ef\u4ee5\u4e0d\u9700\u8981\u8003\u8651\u77ed\u671f\u7684\u5229\u76ca\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u6211\u4eec\u8fd9\u65f6\u5019\u8ddf\u90ed\u6c0f\u96c6\u56e2\u6d3d\u8c08\uff0c\u4e0d\u662f\u6070\u6070\u53ef\u4ee5\u5229\u7528\u8fd9\u70b9\u4f5c\u4e3a\u538b\u4f4e\u5bf9\u65b9\u51fa\u4ef7\u7684\u7b79\u7801\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u80fd\u538b\u4f4e\u591a\u5c11\uff1f\u201d\u5468\u6e38\u5bf9\u6b64\u8868\u793a\u6000\u7591\uff0c\u5173\u952e\u9526\u6e56\u6b64\u65f6\u8fd8\u79d8\u5bc6\u59d4\u4ed8\u90ed\u6c0f\u4e91\u6e90\u96c6\u56e2\u8463\u4e8b\u5c40\u4e3b\u5e2d\u90ed\u677e\u5ef6\u5171\u540c\u7b79\u63aa\u6295\u8d44\u6676\u5706\u5382\u7684\u9879\u76ee\u3002</p><p>\u3000\u3000\u201c\u80fd\u5360\u5230\u4e24\u8fb9\u7684\u4fbf\u5b9c\uff0c\u8fd8\u662f\u8981\u575a\u6301\u5360\u7684\u3002\u201d\u5f20\u606a\u65e0\u826f\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u5177\u4f53\u7684\u4e8b\u60c5\uff0c\u4f60\u4eec\u53bb\u8c08\uff0c\u603b\u4e4b\u9526\u6e56\u8fd9\u8fb9\u4e0e\u90ed\u6c0f\u96c6\u56e2\u9891\u7e41\u63a5\u89e6\uff0c\u4e5f\u9700\u8981\u4e00\u4e2a\u80fd\u63a9\u4eba\u8033\u76ee\u7684\u501f\u53e3\u2026\u2026\u201d</p><p>\u3000\u3000\u6676\u5706\u5382\u7684\u6295\u8d44\u9879\u76ee\uff0c\u6b64\u65f6\u7edd\u4e0d\u80fd\u60ca\u52a8\u53f0\u6e7e\u5f53\u5c40\uff0c\u4e5f\u4e0d\u9002\u5b9c\u60ca\u52a8\u9a6c\u6765\u897f\u4e9a\u5f53\u5c40\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u4e0e\u9648\u4fe1\u751f\u5728\u4e1a\u5185\u9887\u6709\u540d\u6c14\uff0c\u9891\u7e41\u4e0e\u90ed\u677e\u5ef6\u63a5\u89e6\uff0c\u6307\u4e0d\u5b9a\u4ec0\u4e48\u65f6\u5019\u4f1a\u5f15\u8d77\u76f8\u5173\u65b9\u9762\u7684\u6ce8\u610f\u529b\uff0c\u63a9\u62a4\u5de5\u4f5c\u8981\u505a\u597d\uff0c\u4e00\u65e6\u4e8b\u5148\u60ca\u52a8\u53f0\u6e7e\u5f53\u5c40\uff0c\u8fd9\u4e8b\u5341\u6709\u516b\u4e5d\u4f1a\u6ce1\u6c64\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u67f3\u5fd7\u6210\u987e\u8651\u96be\u6d88\uff0c\u662f\u6676\u5706\u5382\u6295\u8d44\u8ba1\u5212\u76ee\u524d\u6700\u5927\u7684\u969c\u788d\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u968f\u53f6\u81fb\u6c11\u51fa\u8bbf\u65b0\u52a0\u5761\uff0c\u5c31\u6b64\u4e8b\u4e0e\u65b0\u52a0\u5761\u5f53\u5c40\u79d8\u5bc6\u5546\u8c08\u7684\u8fc7\u7a0b\u8fd8\u662f\u76f8\u5f53\u7684\u6109\u5feb\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u9648\u4fe1\u751f\u80fd\u968f\u53f6\u81fb\u6c11\u51fa\u8bbf\u65b0\u52a0\u5761\uff0c\u4e5f\u4ee3\u8868\u56fd\u5185\u653f\u5e9c\u575a\u5b9a\u7684\u652f\u6301\u6001\u5ea6\uff0c\u8fd9\u4e5f\u7ed9\u4e86\u67f3\u5fd7\u6210\u3001\u90ed\u677e\u5ef6\u7b49\u4eba\u5f88\u5f3a\u7684\u4fe1\u5fc3\u3002</p><p>\u3000\u3000\u4e5f\u4e0d\u662f\u770b\u4e0d\u5230\u67f3\u5fd7\u6210\u7684\u52aa\u529b\uff0c\u4ed6\u9996\u5148\u5c06\u4ed6\u59bb\u5b50\u8d76\u5230\u65b0\u52a0\u5761\u7ed9\u5728\u65b0\u52a0\u5761\u56fd\u7acb\u5927\u5b66\u8bfb\u4e66\u7684\u67f3\u8317\u966a\u8bfb\uff0c\u4e3a\u4ed6\u6700\u540e\u80fd\u8131\u8eab\u505a\u7740\u51c6\u5907\u3002</p><p>\u3000\u3000\u987e\u8651\u662f\u80af\u5b9a\u4f1a\u6709\u7684\uff0c\u4e00\u65e6\u8fd9\u6837\u79bb\u5f00\u53f0\u79ef\u7535\uff0c\u4ed6\u672c\u4eba\u5e94\u80fd\u4ece\u53f0\u79ef\u7535\u7684\u671f\u6743\u81ea\u7136\u5c31\u4e00\u7b14\u52fe\u6d88\uff0c\u5728\u53f0\u79ef\u7535\u7684\u80a1\u6743\u4e5f\u4f1a\u7ed9\u51bb\u7ed3\uff0c\u4ed6\u4e5f\u4e0d\u53ef\u80fd\u5c06\u4ed6\u6240\u6709\u7684\u4eb2\u670b\u597d\u53cb\u90fd\u8fc1\u51fa\u53f0\u6e7e\uff1b\u53e6\u5916\uff0c\u4ed6\u8fd8\u8981\u6627\u7740\u826f\u5fc3\u5148\u8981\u5c06\u624b\u4e0b\u90a3\u6279\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u4e0e\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u7684\u5bb6\u5c5e\u5148\u62d0\u9a97\u5230\u9a6c\u6765\u897f\u4e9a\u2014\u2014\u4e4b\u524d\u6bd5\u7adf\u53ea\u662f\u8bf4\u8981\u5728\u5409\u9686\u5761\u7b79\u5efa\u6676\u5706\u5382\uff0c\u7b49\u4ed6\u4eec\u52a0\u5165\u4e8b\u5b9e\u4e0a\u5df2\u7ecf\u7ed9\u9526\u6e56\u79d8\u5bc6\u63a7\u80a1\u3001\u8bbe\u7acb\u5728\u5409\u9686\u5761\u7684\u79d1\u6280\u516c\u53f8\u4e4b\u540e\uff0c\u624d\u80fd\u8ba9\u4ed6\u4eec\u9009\u62e9\u53bb\u7559\u2014\u2014\u6bd5\u7adf\u4e0d\u662f\u6240\u6709\u4eba\u90fd\u613f\u610f\u5230\u5185\u5730\u5b9a\u5c45\u7684\uff1b\u53e6\u5916\uff0c\u4ed6\u8fd8\u62c5\u5fc3\u5185\u5730\u4f1a\u6709\u653f\u7b56\u53d8\u52a8\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u6761\u4ef6\u90fd\u597d\u8c08\uff0c\u8981\u5c3d\u4e00\u5207\u53ef\u80fd\u7684\u6253\u6d88\u4ed6\u7684\u987e\u8651\uff0c\u201d\u542c\u9648\u4fe1\u751f\u8bf4\u53ca\u8fd9\u4e9b\u60c5\u51b5\uff0c\u5f20\u606a\u76b1\u7740\u7709\u5934\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u8bf4\u9053\uff0c\u201c\u5173\u952e\u8981\u4ed6\u4eec\u7684\u4eba\u7ed9\u62d0\u8fc7\u6765\u518d\u8bf4\u2026\u2026\u5f53\u7136\uff0c\u6211\u4eec\u4e5f\u8981\u5229\u7528\u4ed6\u4eec\u7684\u987e\u8651\u62b5\u5236\u653f\u5e9c\u51fa\u8d44\u6301\u80a1\u7684\u53ef\u80fd\uff0c\u6211\u8fd9\u4eba\u4e5f\u4e0d\u5927\u60f3\u53d7\u5230\u4ec0\u4e48\u9650\u5236\u3002\u201d</p></article>"}}}]);
