(window.webpackJsonp=window.webpackJsonp||[]).push([[621],{644:function(p){p.exports={chapterTitle:null,title:"\u7b2c470\u7ae0 \u4e0d\u51fa\u6240\u6599",content:"<article><h2>\u7b2c470\u7ae0 \u4e0d\u51fa\u6240\u6599</h2><p>\u3000\u3000\u4eea\u5f0f\u7ed3\u675f\uff0c\u845b\u660e\u4fe1\u5c31\u5728\u5893\u56ed\u91cc\u4e0e\u524d\u6765\u540a\u5501\u7684\u5bbe\u670b\u9053\u522b\uff0c\u845b\u836b\u5747\u8fc7\u6765\u9080\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u3001\u9648\u9759\u4e09\u4eba\u4e00\u540c\u4e58\u8f66\u56de\u845b\u5bb6\u5927\u5b85\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u3001\u9648\u9759\u90fd\u4e00\u540c\u5750\u4e0a\u845b\u660e\u4fe1\u7684\u5bbe\u5229\u8f66\uff0c\u845b\u660e\u4fe1\u7684\u56db\u540d\u4fdd\u9556\u4ee5\u53ca\u968f\u8c22\u6c49\u9756\u4ed6\u4eec\u8fc7\u6765\u7684\u4e24\u540d\u5de5\u4f5c\u4eba\u5458\u5206\u522b\u5750\u4e0a\u56db\u9a71\u5b9d\u9a6c\uff0c\u8fd9\u6b3e\u4e94\u5ea7\u7684\u5bbe\u5229\uff0c\u8c22\u6c49\u9756\u5e74\u521d\u4e5f\u60f3\u8d2d\u8fdb\u4e00\u8f86\uff0c\u540e\u6765\u8003\u8651\u5230\u624b\u5934\u7684\u8d44\u91d1\u672a\u5fc5\u771f\u5bbd\u88d5\u5230\u62ff\u8fd1\u5343\u4e07\u7684\u94b1\u53bb\u4e70\u4e00\u90e8\u8f66\u7684\u7a0b\u5ea6\uff0c\u9042\u4f5c\u7f62\u3002\u6628\u5929\u542c\u845b\u836b\u5747\u8bf4\u4ed6\u7236\u4eb2\u845b\u660e\u4fe1\u7684\u4fdd\u9556\u4e58\u7684\u8f66\u4e5f\u662f\u5411\u5382\u5546\u5b9a\u5236\u9632\u5f39\u6027\u80fd\u7edd\u4f73\u7684\u56db\u9a71\u5b9d\u9a6c\uff0c\u6697\u53f9\u9999\u6e2f\u5bcc\u8c6a\u5962\u79fb\u4e00\u65f6\u8fd8\u4e0d\u662f\u5185\u5730\u6240\u80fd\u76f8\u6bd4\u7684\u3002</p><p>\u3000\u3000\u845b\u660e\u4fe1\u5e74\u5c11\u5c31\u662f\u9999\u6e2f\u6709\u540d\u7684\u82b1\u82b1\u516c\u5b50\uff0c\u5373\u4f7f\u73b0\u5728\u4e5f\u4e0d\u51cf\u8272\u5f53\u5e74\uff0c\u4e0e\u4ed6\u4f20\u51fa\u7eef\u95fb\u7684\u5973\u661f\u4e0d\u77e5\u591a\u5c11\uff0c\u73b0\u5728\u7684\u845b\u660e\u4fe1\u53cc\u9b13\u7565\u6709\u6591\u767d\uff0c\u4e5f\u4e0d\u5c51\u62ff\u67d3\u53d1\u5242\u67d3\u9ed1\uff0c\u6c14\u5ea6\u3001\u67b6\u52bf\u9887\u8db3\uff0c\u7709\u76ee\u95f4\u591a\u5c11\u6709\u4e9b\u8f7b\u6d6e\u7684\u610f\u6001\uff0c\u6bd5\u7adf\u662f\u9999\u6e2f\u8c6a\u65cf\u7684\u7b2c\u4e8c\u4ee3\u5b50\u5f1f\uff0c\u4e0d\u53ca\u66fe\u4e3a\u4e2d\u5b66\u6559\u5e08\u3001\u767d\u624b\u8d77\u5bb6\u521b\u4e0b\u5de8\u5bcc\u5bb6\u4e1a\u7684\u8c22\u6c49\u9756\u6c14\u5ea6\u6de1\u5b9a\u4ece\u5bb9\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5fc3\u91cc\u5bf9\u845b\u660e\u4fe1\u5e76\u6ca1\u6709\u592a\u9ad8\u7684\u8bc4\u4ef7\uff0c\u5904\u7406\u5bb6\u65cf\u77db\u76fe\u7684\u624b\u6bb5\u4e5f\u9887\u4e3a\u4e0d\u529b\uff0c\u4ee5\u81f4\u5904\u5904\u9677\u5165\u88ab\u52a8\uff0c\u4f46\u662f\u4e5f\u8ba4\u4e3a\u845b\u660e\u4fe1\u521b\u4e1a\u867d\u4e0d\u6210\u3001\u5b88\u4e1a\u5374\u6709\u4f59\uff1b\u845b\u836b\u5747\u5728\u67d0\u79cd\u7a0b\u5ea6\u6bd4\u4ed6\u8001\u5b50\u8fd8\u7565\u6709\u4e0d\u8db3\u3002</p><p>\u3000\u3000\u845b\u660e\u4fe1\u4e5f\u610f\u8bc6\u5230\u81ea\u5df1\u5728\u5bb6\u65cf\u5185\u6709\u4e9b\u7ed9\u4f17\u53db\u4eb2\u79bb\u4e86\uff0c\u8f7b\u6613\u5c31\u7ed9\u5e73\u65f6\u90fd\u4e0d\u653e\u5728\u773c\u91cc\u7684\u845b\u660e\u5fb7\u3001\u5b59\u5c1a\u4e49\u903c\u5165\u56f0\u5883\uff0c\u8fd9\u5927\u6982\u4e5f\u662f\u5e73\u65e5\u4e0d\u591f\u91cd\u89c6\u4ed6\u4eec\u7684\u7ed3\u679c\uff0c\u5bf9\u6b64\uff0c\u4ed6\u6709\u6240\u53cd\u601d\u3002</p><p>\u3000\u3000\u4e8c\u623f\u3001\u4e09\u623f\u4e0d\u8fc7\u56b7\u7740\u8981\u5206\u5bb6\u800c\u8a00\uff0c\u773c\u4e0b\u60f3\u8981\u6536\u62fe\u6b8b\u5c40\uff0c\u53ea\u6709\u75db\u75db\u5feb\u5feb\u7684\u5206\u5bb6\uff0c\u4e0d\u80fd\u8ba9\u5bb6\u65cf\u77db\u76fe\u65e0\u4f11\u6b62\u7684\u7ea0\u7f20\u4e0b\u53bb\uff0c\u845b\u5bb6\u5728\u534e\u4eba\u5546\u5708\u5185\u5e76\u975e\u6ca1\u6709\u7ad6\u654c\uff0c\u5609\u4fe1\u96c6\u56e2\u5bf9\u65d7\u4e0b\u4e0a\u5e02\u516c\u53f8\u7684\u63a7\u80a1\u5927\u591a\u7ef4\u6301\u572830\uff05\uff0c\u8fd9\u65f6\u5019\u8ba9\u522b\u4eba\u8d81\u865a\u800c\u5165\u5c31\u6709\u4e9b\u5f97\u4e0d\u507f\u5931\u4e86\u3002</p><p>\u3000\u3000\u4f46\u662f\u5bf9\u4e8e\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u6562\u7b2c\u4e00\u4e2a\u8df3\u51fa\u6765\u6345\u4ed6\u4e00\u5200\u7684\u4eba\uff0c\u845b\u660e\u4fe1\u4e0d\u4f1a\u5fc3\u6148\u624b\u8f6f\uff0c\u5546\u4eba\u81ea\u6709\u5546\u4eba\u7684\u51c6\u5219\uff0c\u4ed6\u4eec\u4e0d\u8981\u5ba3\u79f0\u8981\u52a0\u5927\u5bf9\u4e1c\u5357\u4e9a\u5730\u533a\u789f\u673a\u4e1a\u52a1\u7684\u6295\u8d44\u5417\uff0c\u4ed6\u4eec\u4e0d\u662f\u4f9d\u8d56\u4e1c\u5357\u4e9a\u7684\u789f\u673a\u4e1a\u52a1\u624d\u7ffb\u8eab\u7684\u5417\uff1f\u90a3\u5c31\u518d\u8ba9\u4ed6\u4eec\u611f\u53d7\u5230\u5e02\u573a\u7ade\u4e89\u7684\u8840\u8165\u4e0e\u51f6\u6b8b\u5427\u3002</p><p>\u3000\u3000\u845b\u660e\u4fe1\u4e4b\u524d\u5bf9\u4e0e\u79d1\u738b\u7535\u5668\u7684\u5408\u4f5c\u6ca1\u6709\u592a\u5927\u7684\u5174\u8da3\uff0c\u4f46\u4e5f\u4e0d\u662f\u6ca1\u6709\u5174\u8da3\uff0c\u53ea\u662f\u4e0d\u4f1a\u64cd\u4e4b\u8fc7\u6025\uff0c\u5927\u4f01\u4e1a\u81ea\u6709\u5927\u4f01\u4e1a\u505a\u4e8b\u7684\u7a0b\u5f0f\u3002</p><p>\u3000\u3000\u5609\u4fe1\u7535\u5b50\u65d7\u4e0b\u7684\u7814\u53d1\u4e3b\u7ba1\u66fe\u63d0\u4ea4\u8fc7\u4e00\u4efd\u9879\u76ee\u62a5\u544a\uff0c\u5efa\u8bae\u4e0a\u789f\u673a\u63a7\u5236\u82af\u7247\u9879\u76ee\uff0c\u4e0d\u7ba1\u662f\u73b0\u5728\u7684VCD\u789f\u673a\uff0c\u8fd8\u662f\u672a\u6765\u7684DVD\u789f\u673a\uff0c\u4e3a\u5185\u5730\u6280\u672f\u529b\u91cf\u4e0d\u8db3\u7684\u789f\u673a\u5382\u5546\u4f9b\u5e94\u7535\u8def\u82af\u7247\u3001\u7cfb\u7edf\u63a7\u5236\u82af\u7247\uff0c\u90fd\u6709\u76f8\u5f53\u4e0d\u9519\u7684\u5e02\u573a\u6f5c\u529b\uff0c\u76ee\u524d\u5df2\u6709\u51e0\u5bb6\u53f0\u6e7e\u7535\u5b50\u5382\u5546\u8ddf\u8fdb\uff0c\u845b\u660e\u4fe1\u4e5f\u540c\u610f\u8fdb\u884c\u8fdb\u4e00\u6b65\u7684\u5e02\u573a\u8c03\u7814\u3002\u8c22\u5251\u5357\u4e0e\u845b\u836b\u5747\u8054\u7cfb\u4e4b\u540e\uff0c\u8fd9\u4e2a\u9879\u76ee\u5c31\u7ed9\u845b\u836b\u5747\u4e3b\u52a8\u63fd\u4e86\u8fc7\u53bb\uff0c\u9879\u76ee\u7684\u76ee\u6807\u5df2\u7ecf\u4e0d\u4ec5\u4ec5\u662f\u5609\u4fe1\u7535\u5b50\u505a\u7535\u8def\u82af\u7247\u4e0e\u7cfb\u7edf\u63a7\u5236\u82af\u7247\u8fd9\u4e48\u5355\u4e00\uff0c\u800c\u662f\u8981\u4e0e\u79d1\u738b\u5408\u4f5c\uff0c\u76f4\u63a5\u5229\u7528\u5609\u4fe1\u7535\u5b50\u7684\u6280\u672f\u3001\u8d44\u91d1\u3001\u4eba\u529b\u7684\u4f18\u52bf\u63d0\u9ad8\u79d1\u738b\u7535\u5668\u5728\u789f\u673a\u5e02\u573a\u7684\u7ade\u4e89\u529b\u3002</p><p>\u3000\u3000\u592e\u89c6\u6807\u738b\u5e7f\u544a\u7684\u4ef7\u503c\uff0c\u65e9\u5df2\u6709\u7231\u8fbe\u7535\u5b50\u4eca\u5e74\u7684\u5de8\u5927\u6210\u529f\u4f5c\u6ce8\u811a\uff0c\u81f3\u4e8e\u54c1\u724c\u5feb\u901f\u6269\u5f20\u53ef\u80fd\u5bfc\u81f4\u5404\u7ba1\u7406\u73af\u8282\u4e0a\u7684\u8d1f\u9762\u6548\u5e94\uff0c\u8c22\u5251\u5357\u3001\u845b\u836b\u5747\u8fd9\u4e9b\u5251\u6865\u7ba1\u7406\u5b66\u9662\u5b66\u6210\u7684\u4eba\u81ea\u7136\u4e5f\u6709\u4fe1\u5fc3\u63a7\u5236\u3002</p><p>\u3000\u3000\u845b\u836b\u57477\u65e5\u98de\u5f80\u5efa\u90ba\u4e0e\u8c22\u5251\u5357\u6c47\u9762\uff0c\u5c31\u662f\u60f3\u5c31\u5408\u4f5c\u7684\u8bdd\u9898\u6df1\u8c08\u4e0b\u53bb\uff0c\u6ca1\u60f3\u5230\u8001\u5934\u5b50\u8fd9\u65f6\u5019\u54bd\u6c14\u3002\u6bd4\u8d77\u5bb6\u65cf\u4f01\u4e1a\u63a7\u5236\u6743\u7684\u4e89\u593a\uff0c\u4e0e\u79d1\u738b\u7684\u5408\u4f5c\u5f53\u7136\u7b97\u4e0d\u4e0a\u4ec0\u4e48\uff0c\u845b\u660e\u4fe1\u6839\u672c\u6ca1\u6709\u601d\u5fc3\u5173\u6ce8\u8fd9\u4e2a\uff0c\u4e5f\u4e0d\u60f3\u5609\u4fe1\u7535\u5b50\u8fd9\u65f6\u5019\u76f2\u76ee\u4e0a\u4ec0\u4e48\u9879\u76ee\uff0c\u4e07\u4e00\u6709\u6240\u9057\u8bef\uff0c\u53cd\u800c\u6210\u4e3a\u4e8c\u623f\u3001\u4e09\u623f\u653b\u51fb\u4ed6\u7684\u628a\u67c4\u3002</p><p>\u3000\u3000\u4f46\u662f\u773c\u4e0b\u7684\u60c5\u51b5\u53c8\u4e0d\u4e00\u6837\u7684\uff0c\u4e0e\u79d1\u738b\u7535\u5668\u7684\u5408\u4f5c\uff0c\u5df2\u7ecf\u4e0e\u5bb6\u65cf\u5185\u90e8\u7684\u77db\u76fe\u626f\u4e0a\u8fb9\u4e86\uff0c\u845b\u660e\u4fe1\u8fd8\u60f3\u501f\u52a9\u4e0e\u79d1\u738b\u7535\u5668\u5408\u4f5c\u8fd9\u4ef6\u4e8b\u6765\u6446\u8131\u773c\u4e0b\u7684\u56f0\u5883\u3002</p><p>\u3000\u3000\u9648\u9759\u5750\u526f\u9a7e\u9a76\u4f4d\uff0c\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u4e24\u4eba\u4e0e\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u5728\u540e\u8f66\u53a2\u9762\u5411\u5750\u7740\uff0c\u845b\u660e\u4fe1\u5ba3\u6684\u4e4b\u540e\uff0c\u76f4\u63a5\u95ee\u9053\uff1a\u201cVCD\u5728\u5185\u5730\u5f88\u706b\u70ed\uff1f\u201d\u4ed6\u4e4b\u524d\u5bf9\u56fd\u5185\u7684\u789f\u673a\u5e02\u573a\u5173\u5fc3\u4e0d\u591f\uff0c\u5609\u4fe1\u7535\u5b50\u6709\u4e0a\u789f\u673a\u7cfb\u7edf\u63a7\u5236\u82af\u7247\u7684\u610f\u56fe\uff0c\u4f46\u662f\u4e24\u4e09\u5343\u4e07\u6e2f\u5143\u7684\u89c4\u6a21\uff0c\u8fd8\u4e0d\u80fd\u8ba9\u4ed6\u6295\u5165\u592a\u591a\u7684\u7cbe\u529b\u53bb\u7814\u7a76\uff0c\u5f88\u591a\u4e8b\u60c5\u8fd8\u662f\u845b\u836b\u5747\u8fd9\u51e0\u5929\u544a\u8bc9\u4ed6\u7684\u3002</p><p>\u3000\u3000\u201c\u9999\u6e2f\u4e5f\u6709\u5f88\u591a\u5bb6\u5ead\u8d2d\u4e70VCD\uff0c\u5341\u4e00\u6708\u4efd\u6570\u636e\uff0c\u789f\u673a\u5728\u9999\u6e2f\u7684\u9500\u91cf\u5df2\u7ecf\u8d85\u8fc7\u6df1\u5733\u4e86\uff0c\u6211\u770bVCR\u5728\u9999\u6e2f\u7684\u9500\u91cf\u90fd\u8fdc\u4e0d\u5982\uff0c\u201d\u8c22\u5251\u5357\u7b11\u7740\u56de\u7b54\u8bf4\uff0c\u201c\u6bd5\u7adfVCD\u789f\u7247\u4ece\u6df1\u5733\u6d41\u8fc7\u6765\u5f88\u5bb9\u6613\uff0cVCR\u5361\u5e26\u65e2\u8d35\u53c8\u4e0d\u6613\u4fdd\u5b58\u3002\u201d</p><p>\u3000\u3000VCR\u662f\u90a3\u79cd\u65e9\u5c31\u5728\u76f8\u5bf9\u53d1\u8fbe\u5730\u533a\u666e\u53ca\u7684\u6a21\u62df\u5f0f\u78c1\u5e26\u5f55\u50cf\u673a\uff0c\u9999\u6e2f\u4eba\u4e60\u60ef\u79f0VCR\uff0c\u8c22\u5251\u5357\u81ea\u7136\u62ff\u9999\u6e2f\u4eba\u7684\u4e60\u60ef\u8bed\u6c14\u8ddf\u845b\u660e\u4fe1\u4ea4\u8c08\u3002VCR\u7684\u64ad\u653e\u56fe\u50cf\u8d28\u91cf\u4e0eVCD\u76f8\u5f53\uff0c\u4e00\u822c\u8bf4\u6765VCD\u4e0d\u53ef\u80fd\u5728\u53d1\u8fbe\u5730\u533a\u4e0e\u56fd\u5bb6\u5f7b\u5e95\u7684\u6dd8\u6c70VCR\uff0c\u4f46\u662f\u5728\u9999\u6e2f\uff0c\u5927\u91cf\u4ece\u5185\u5730\u6d41\u5165\u7684\u5ec9\u4ef7VCD\u789f\u7247\u6210\u4e3aVCD\u53ef\u4ee5\u66ff\u4ee3VCR\u7684\u5173\u952e\u56e0\u7d20\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u53c8\u8bf4\u9053\uff1a\u201c\u5f53\u7136\u4e86\uff0c\u9999\u6e2f\u5e02\u573a\u4e0d\u662f\u91cd\u70b9\uff0c\u4e1c\u5357\u4e9a\u90a3\u4e9b\u8fd8\u6ca1\u6709\u666e\u53caVCR\u7684\u65b0\u5174\u5e02\u573a\uff0c\u624d\u662f\u6211\u4eec\u7684\u76ee\u6807\u2026\u2026\u201d</p><p>\u3000\u3000\u4e1c\u5357\u4e9a\u8fd9\u4e9b\u65b0\u5174\u56fd\u5bb6\u4e0e\u5730\u533a\uff0c\u5728\u77e5\u8bc6\u4ea7\u6743\u4fdd\u62a4\u65b9\u9762\u7684\u529b\u5ea6\u4e0e\u56fd\u5185\u76f8\u4f3c\uff0c\u8fd9\u624d\u662fVCD\u80fd\u8fc5\u901f\u63a8\u5e7f\u7684\u6839\u672c\u539f\u56e0\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u845b\u660e\u4fe1\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u4e9b\u4e8b\u60c5\uff0c\u836b\u5747\u90fd\u6709\u8ddf\u6211\u8bf4\u8fc7\uff0c\u5bf9\u5609\u4fe1\u7535\u5b50\u4e0e\u79d1\u738b\u7684\u5408\u4f5c\uff0c\u6211\u4e5f\u6709\u8fc7\u8003\u8651\uff0c\u6211\u73b0\u5728\u8003\u8651\uff0c\u662f\u4e0d\u662f\u5408\u4f5c\u7684\u6b65\u4f10\u53ef\u4ee5\u518d\u5927\u4e00\u4e9b\uff1f\u201d</p><p>\u3000\u3000\u201c\u53ef\u4ee5\u7684\u2026\u2026\u201d\u8c22\u6c49\u9756\u6de1\u6de1\u7684\u4e00\u7b11\uff0c\u201c\u6211\u8fd9\u6b21\u8fc7\u6765\uff0c\u5c31\u662f\u60f3\u4e0e\u845b\u5bb6\u7ed3\u4e0b\u53cb\u8c0a\uff0c\u65b9\u4fbf\u65e5\u540e\u5c55\u5f00\u66f4\u5bc6\u5207\u3001\u66f4\u5e7f\u6cdb\u7684\u5408\u4f5c\uff0c\u845b\u5148\u751f\u5bf9\u6211\u4eec\u7684\u8bda\u610f\u8fd9\u4e48\u4fe1\u4efb\uff0c\u613f\u610f\u4e00\u6b65\u5230\u4f4d\uff0c\u6211\u4eec\u81ea\u7136\u9f0e\u529b\u652f\u6301\u3002\u201d</p><p>\u3000\u3000\u8c22\u6c49\u9756\u77e5\u9053\u845b\u660e\u4fe1\u5982\u6b64\u6025\u5207\u7684\u539f\u56e0\u6709\u4e24\u4e2a\uff1a\u5176\u4e00\uff0c\u4ed6\u5f88\u60f3\u7acb\u5373\u5bf9\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u6709\u9488\u950b\u76f8\u5bf9\u7684\u53cd\u51fb\uff1b\u5176\u4ed6\uff0c\u4ed6\u9700\u8981\u6709\u4e00\u4e2a\u5927\u7684\u52a8\u4f5c\uff0c\u80fd\u591f\u632f\u594b\u4eba\u5fc3\u7684\u52a8\u4f5c\uff0c\u6765\u8f6c\u79fb\u9999\u6e2f\u5a92\u4f53\u5bf9\u845b\u5bb6\u5185\u90e8\u77db\u76fe\u7684\u5173\u6ce8\uff0c\u63d0\u632f\u9999\u6e2f\u6295\u8d44\u4eba\u5bf9\u5609\u4fe1\u96c6\u56e2\u7684\u4fe1\u5fc3\u3002</p><p>\u3000\u3000\u4e24\u4e09\u5343\u4e07\u6e2f\u5143\u89c4\u6a21\u7684\u9879\u76ee\uff0c\u6839\u672c\u8fbe\u4e0d\u5230\u4ed6\u6240\u60f3\u8981\u7684\u6548\u679c\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u795e\u8272\u4ece\u5bb9\u6de1\u5b9a\uff0c\u845b\u660e\u4fe1\u8fd9\u4e48\u6025\u5207\uff0c\u5bf9\u4ed6\u4eec\u8c22\u5bb6\u603b\u662f\u6709\u597d\u5904\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u53f6\u5efa\u658c\u770b\u7740\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u7b49\u4eba\u94bb\u8fdb\u845b\u660e\u4fe1\u7684\u8f66\u91cc\uff0c\u62ff\u624b\u8098\u9876\u4e86\u9876\u5f20\u606a\uff1a\u201c\u8bfa\uff0c\u53ef\u601c\u7684\u4eba\u53c8\u843d\u5165\u4f60\u7684\u5751\u91cc\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8f7b\u7b11\u8d77\u6765\uff0c\u4ed6\u4eec\u7684\u8f66\u505c\u5728\u5893\u56ed\u5916\u9762\uff0c\u7b49\u7740\u4e0d\u8fdc\u5904\u7684\u5b59\u5c1a\u4e49\u4e0e\u845b\u660e\u5fb7\u8d70\u8fc7\u6765\uff0c\u518d\u4e00\u8d77\u5f80\u5893\u56ed\u5916\u8d70\u53bb\u3002</p><p>\u3000\u3000\u8981\u4e0d\u662f\u7559\u5728\u5893\u56ed\u91cc\u6563\u6b65\u7ed9\u4eba\u592a\u8be1\u5f02\u7684\u611f\u89c9\uff0c\u79d1\u58eb\u5170\u5893\u56ed\u5348\u540e\u7684\u98ce\u60c5\u771f\u662f\u4e0d\u9519\uff0c\u6709\u7740\u9999\u6e2f\u7e41\u534e\u90fd\u5e02\u5c11\u89c1\u7684\u5bc2\u9759\u6df1\u5e7d\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u4e5f\u770b\u5230\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u7b49\u4eba\u94bb\u8fdb\u845b\u660e\u4fe1\u7684\u5bbe\u5229\u8f66\u91cc\uff0c\u8d70\u8fc7\u6765\uff0c\u6709\u4e9b\u62c5\u5fe7\u7684\u8bf4\uff1a\u201c\u79d1\u738b\u59cb\u7ec8\u5bf9\u7231\u8fbe\u6709\u4e00\u5b9a\u7684\u5a01\u80c1\uff0c\u4ed6\u4eec\u8054\u5408\u8d77\u6765\u5927\u8086\u5f00\u62d3\u4e1c\u5357\u4e9a\u7684\u789f\u673a\u5e02\u573a\uff0c\u5bf9\u5609\u4fe1\u5b9e\u4e1a\u4e5f\u6709\u4e00\u5b9a\u7684\u5f71\u54cd\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u4e1c\u5357\u4e9a\u7684\u789f\u673a\u5e02\u573a\u603b\u8981\u6709\u4eba\u53bb\u5f00\u62d3\uff0c\u90a3\u91cc\u7684\u789f\u673a\u5e02\u573a\u6ca1\u6709\u4e00\u5e74\u7684\u65f6\u95f4\u4e0d\u4f1a\u9971\u548c\uff0c\u6240\u4ee5\u6682\u65f6\u8fd8\u4e0d\u591f\u6210\u76f4\u63a5\u7684\u7ade\u4e89\uff1b\u76f8\u53cd\u7684\uff0c\u4ed6\u4eec\u5927\u529b\u5f00\u62d3\u789f\u673a\u5e02\u573a\uff0c\u4f7f\u5f97\u4e1c\u5357\u4e9a\u7684\u789f\u673a\u9700\u6c42\u5927\u589e\uff0c\u5728\u5e02\u573a\u9700\u6c42\u9971\u548c\u4e4b\u524d\uff0c\u5bf9\u6211\u4eec\u8fd8\u662f\u6709\u5229\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u789f\u673a\u5e02\u573a\u7684\u65b0\u5f00\u62d3\u8981\u89e3\u51b3\u7247\u6e90\u4e0e\u4ea7\u54c1\u6982\u5ff5\u7684\u5ba3\u4f20\uff0c\u8fd9\u672c\u8eab\u5c31\u4f1a\u8ba9\u5176\u4ed6\u540c\u4e1a\u789f\u673a\u5382\u5546\u53d7\u76ca\u3002\u5c31\u50cf\u53bb\u5e74\u5e74\u5e95\u7231\u8fbe\u7535\u5b50\u5229\u7528\u592e\u89c6\u6807\u738b\u7684\u6548\u5e94\u5728\u5185\u5730\u5e02\u573a\u63a8\u5e7f\u789f\u673a\u6982\u5ff5\uff0c\u4e5f\u540c\u6837\u4f7f\u5f97\u5176\u4ed6\u5382\u5546\u7684\u9500\u91cf\u540c\u671f\u5927\u589e\uff0c\u53ea\u662f\u4e0d\u53ca\u7231\u8fbe\u7535\u5b50\u90a3\u4e48\u5927\u7684\u5e45\u5ea6\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u8fd8\u662f\u62c5\u5fe7\u7684\u8bf4\uff1a\u201c\u524d\u671f\u80fd\u501f\u529b\u8fd8\u662f\u597d\u7684\uff0c\u4e0d\u8fc7\u4e4b\u540e\u8fd8\u662f\u8981\u76f4\u63a5\u7ade\u4e89\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7ade\u4e89\u5c31\u7ade\u4e89\uff0c\u4e00\u5bb6\u4f01\u4e1a\u6ca1\u6709\u7ade\u4e89\uff0c\u5c31\u65e0\u6cd5\u4fdd\u6301\u5e94\u6709\u7684\u6d3b\u529b\u3002\u201d\u845b\u660e\u5fb7\u5df4\u4e0d\u5f97\u5982\u6b64\uff0c\u8fd9\u4e9b\u5e74\u53d7\u591f\u845b\u660e\u4fe1\u7684\u95f2\u6c14\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u65e0\u5948\u7684\u6447\u4e86\u6447\u5934\uff0c\u9664\u4e86\u4ed6\uff0c\u5176\u4ed6\u4eba\u90fd\u662f\u597d\u6218\u5206\u5b50\uff0c\u4e0d\u518d\u591a\u8bf4\u4ec0\u4e48\uff0c\u4e00\u8d77\u4e58\u8f66\u8fd4\u56de\u9999\u6e2f\u5c9b\u5357\u533a\u7684\u5f71\u6e7e\u56ed\uff0c\u65e2\u7136\u95f9\u5f00\u4e86\uff0c\u5b59\u5c1a\u4e49\u592b\u5987\u4e5f\u7d22\u6027\u4f4f\u8fdb\u5f71\u6e7e\u56ed\u7684\u9152\u5e97\u516c\u5bd3\uff0c\u53ea\u662f\u845b\u660e\u73cf\u8fd8\u8981\u53bb\u8001\u5b85\u5904\u7406\u4e00\u4e9b\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u845b\u660e\u5fb7\u3001\u5b59\u5c1a\u4e49\u3001\u53f6\u5efa\u658c\u7b49\u4eba\u63a5\u4e0b\u6765\u7684\u65f6\u95f4\u5c31\u662f\u5728\u8ba8\u8bba\u5609\u4fe1\u5b9e\u4e1a\u589e\u53d1\u5e76\u8d2d\u7231\u8fbe\u7535\u5b50\u789f\u673a\u8d44\u4ea7\u7684\u65b9\u6848\uff0c\u8fd9\u65f6\u5019\u5c31\u8981\u8003\u8651\u5230\u845b\u660e\u4fe1\u53ef\u80fd\u4f1a\u5236\u9020\u7684\u989d\u5916\u963b\u529b\u3002</p><p>\u3000\u3000\u7528\u8fc7\u665a\u9910\uff0c\u5b59\u5c1a\u4e49\u63a5\u5230\u59bb\u5b50\u845b\u660e\u73cf\u7684\u7535\u8bdd\uff0c\u8bf4\u662f\u845b\u660e\u4fe1\u53ec\u96c6\u4e09\u623f\u7684\u4eba\uff0c\u8981\u5c31\u8d22\u4ea7\u5206\u5272\u8fdb\u884c\u5546\u8c08\uff0c\u542c\u53e3\u6c14\u845b\u660e\u4fe1\u7684\u6001\u5ea6\u5df2\u7ecf\u8f6f\u4e86\u4e0b\u6765\uff0c\u5373\u4f7f\u5c06\u5609\u4fe1\u96c6\u56e2\u6301\u6709\u7684\u65d7\u4e0b\u4e0a\u5e02\u516c\u53f8\u80a1\u6743\u5404\u5bb6\u8fdb\u884c\u5206\u5272\u4e5f\u53ef\u4ee5\u5546\u91cf\u3002</p><p>\u3000\u3000\u845b\u660e\u5fb7\u662f\u5916\u7cfb\uff0c\u845b\u666f\u8bda\u540d\u4e0b\u7684\u9057\u4ea7\u5206\u5272\u4e0e\u4ed6\u6ca1\u6709\u592a\u5927\u7684\u5173\u7cfb\uff0c\u9876\u591a\u505a\u4e2a\u89c1\u8bc1\u4eba\uff0c\u5b59\u5c1a\u4e49\u4e5f\u4e0d\u5c06\u845b\u5bb6\u5185\u90e8\u7684\u77db\u76fe\u7792\u7740\u5f20\u606a\u3001\u53f6\u5efa\u658c\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u845b\u660e\u4fe1\u73b0\u5728\u77e5\u9053\u5feb\u5200\u65a9\u4e71\u9ebb\u8fd9\u4e2a\u9053\u7406\uff1b\u4ed6\u540c\u610f\u5206\u5272\u8d22\u4ea7\uff0c\u800c\u80fd\u5206\u5272\u7684\u8d22\u4ea7\u4e3b\u8981\u5c31\u662f\u5609\u4fe1\u96c6\u56e2\u6240\u6301\u5404\u4e0a\u5e02\u516c\u53f8\u7684\u80a1\u6743\u8d44\u4ea7\uff0c\u73b0\u5728\uff0c\u7ef4\u6301\u3001\u63d0\u632f\u9999\u6e2f\u6295\u8d44\u4eba\u5bf9\u5609\u4fe1\u96c6\u56e2\u7684\u4fe1\u5fc3\uff0c\u53c8\u6210\u4e3a\u5927\u5bb6\u5171\u540c\u7684\u76ee\u6807\u4e86\u3002\u4e0d\u8fc7\u8fd9\u51e0\u5929\u65f6\u95f4\u91cc\uff0c\u5609\u4fe1\u96c6\u56e2\u53d7\u5230\u7684\u6253\u51fb\u4e5f\u7684\u786e\u591f\u6df1\uff0c\u8981\u60f3\u6709\u6548\u7684\u63d0\u632f\u5e02\u573a\u4fe1\u5fc3\uff0c\u845b\u660e\u4fe1\u5927\u6982\u8fd8\u8981\u53e6\u8f9f\u6377\u5f84\uff0c\u770b\u6765\u79d1\u738b\u5bf9\u4ed6\u8fd8\u662f\u6709\u5f88\u5927\u5e2e\u52a9\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u79d1\u738b\u4e0e\u5609\u4fe1\u7535\u5b50\u4e4b\u95f4\u7684\u5408\u4f5c\u89c4\u6a21\u5e94\u8be5\u4e0d\u5c0f\u5427\u3002\u201d\u5b59\u5c1a\u4e49\u63a8\u6d4b\u9053\u3002</p><p>\u3000\u3000\u201c\u5e94\u8be5\u4e0d\u5c0f\u3002\u4ece\u4eca\u5929\u5609\u4fe1\u5b9e\u4e1a\u7684\u80a1\u5e02\u8868\u73b0\u6765\u770b\uff0c\u5e02\u573a\u5bf9\u4e1c\u5357\u4e9a\u7684\u789f\u673a\u4e1a\u52a1\u8fd8\u662f\u5bc4\u4e88\u5f88\u5f3a\u7684\u4fe1\u5fc3\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u5085\u5bb6\u4fca\u58f0\u660e\u4e4b\u540e\uff0c\u5609\u4fe1\u96c6\u56e2\u65d7\u4e0b\u4e0a\u5e02\u516c\u53f8\u96c6\u4f53\u505c\u76d8\uff0c\u5609\u4fe1\u5b9e\u4e1a\u4e3a\u4e0e\u845b\u5bb6\u5212\u6e05\u754c\u9650\uff0c\u6b63\u5e38\u5f00\u76d8\uff0c\u4e0a\u5348\u7684\u80a1\u4ef7\u5267\u70c8\u6ce2\u52a8\uff0c\u5230\u4e0b\u5348\u5c31\u7a33\u5b9a\u4e0a\u626c\uff0c\u800c\u4ed6\u4eec\u5e76\u6ca1\u6709\u5b9e\u73b0\u589e\u6301\u52a8\u4f5c\uff0c\u53ef\u89c1\u4e0a\u5348\u7684\u90a3\u5219\u58f0\u660e\u867d\u7136\u6709\u4e00\u4e9b\u98ce\u9669\u8fd8\u662f\u83b7\u5f97\u521d\u6b65\u7684\u6210\u529f\uff0c\u845b\u660e\u4fe1\u4e0d\u4f1a\u6ce8\u610f\u4e0d\u5230\u8fd9\u4e00\u70b9\uff0c\u201c\u5609\u4fe1\u5b9e\u4e1a\u6301\u80a1\u5609\u4fe1\u7535\u5b5028\uff05\u7684\u80a1\u4efd\uff0c\u845b\u660e\u4fe1\u540c\u610f\u80a1\u6743\u8d44\u4ea7\u5206\u5272\u4e4b\u540e\uff0c\u845b\u660e\u4fe1\u624b\u91cc\u8fd8\u80fd\u6301\u6709\u591a\u5c11\u80a1\u4efd\uff1f\u201d</p><p>\u3000\u3000\u201c\u845b\u660e\u4fe1\u539f\u6765\u5c31\u6301\u6709\u5609\u4fe1\u7535\u5b506\uff05\u7684\u80a1\u4efd\uff0c\u5206\u5272\u4e0b\u6765\uff0c\u4ed6\u624b\u91cc\u7ea6\u670920\uff05\uff0c\u5609\u4fe1\u7535\u5b50\u80a1\u4e1c\u5927\u4f1a\u76ee\u524d\u6388\u6743\u8463\u4e8b\u4f1a\u7684\u6295\u8d44\u989d\u5ea6\u4e3a\u4e24\u4ebf\u6e2f\u5143\uff0c\u8d85\u8fc7\u4e24\u4ebf\u6e2f\u5143\u7684\u6295\u8d44\u9879\u76ee\u5c31\u8981\u53ec\u5f00\u4e34\u65f6\u80a1\u4e1c\u5927\u4f1a\u6295\u7968\u51b3\u5b9a\u2026\u2026\u4e0d\u8fc7\uff0c\u201d\u5b59\u5c1a\u4e49\u5bf9\u845b\u660e\u5fb7\u82e6\u7b11\u9053\uff0c\u201c\u6211\u8ddf\u4f60\u73b0\u5728\u597d\u50cf\u6210\u5c11\u6570\u6d3e\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u4e0e\u845b\u660e\u5fb7\u5148\u6345\u845b\u660e\u4fe1\u4e00\u5200\uff0c\u903c\u4f7f\u845b\u660e\u4fe1\u540c\u610f\u5176\u4ed6\u4eba\u5206\u5272\u8d22\u4ea7\u7684\u8981\u6c42\uff0c\u4f46\u662f\u7b49\u845b\u660e\u4fe1\u540c\u610f\u5e72\u8106\u5229\u843d\u7684\u5206\u5272\u8d22\u4ea7\uff0c\u5b59\u5c1a\u4e49\u4e0e\u845b\u660e\u5fb7\u7684\u884c\u52a8\uff0c\u53c8\u5c06\u635f\u5bb3\u5176\u4ed6\u4eba\u7684\u5229\u76ca\u3002</p><p>\u3000\u3000\u845b\u660e\u5fb7\u7b11\u9053\uff1a\u201c\u90a3\u5206\u5272\u8d22\u4ea7\u65f6\uff0c\u4f60\u5bb6\u5c31\u4e0d\u8981\u62ff\u5609\u4fe1\u7535\u5b50\u7684\u80a1\u4efd\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e2a\u662f\u5f53\u7136\u2026\u2026\u201d\u5b59\u5c1a\u4e49\u82e6\u7b11\u7740\u8bf4\uff0c\u201c\u845b\u660e\u4fe1\u4e5f\u4e0d\u4f1a\u8ba9\u6211\u5bb6\u53bb\u5206\u5609\u4fe1\u5b9e\u4e1a\u7684\u80a1\u4efd\u7684\u3002\u201d</p><p>\u3000\u3000\u53f8\u673a\u5c06\u8f66\u4ece\u505c\u8f66\u573a\u5f00\u5230\u697c\u4e0b\u8fc7\u6765\u901a\u77e5\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u5c31\u5148\u53bb\u5927\u5b85\uff0c\u5f88\u5feb\u5c31\u4f20\u56de\u6d88\u606f\uff1a\u845b\u660e\u4fe1\u8981\u5609\u4fe1\u7535\u5b50\u4e0e\u79d1\u738b\u8fdb\u884c\u5168\u9762\u7684\u5408\u4f5c\uff0c\u63d0\u4f9b\u6280\u672f\u3001\u4eba\u529b\u3001\u8d44\u91d1\u7b49\u5168\u65b9\u9762\u7684\u63f4\u52a9\uff0c\u5e2e\u52a9\u79d1\u738b\u63d0\u9ad8\u5e02\u573a\u7ade\u4e89\u529b\u3001\u5f00\u62d3\u4e1c\u5357\u4e9a\u5e02\u573a\uff0c\u5609\u4fe1\u7535\u5b50\u7c4d\u6b64\u81f3\u5c11\u8981\u83b7\u5f97\u79d1\u738b40\uff05\u7684\u80a1\u4efd\uff0c\u845b\u660e\u4fe1\u540c\u610f\u5206\u5272\u4e0e\u4e8c\u623f\u3001\u4e09\u623f\u5e72\u8106\u5229\u843d\u7684\u5206\u5272\u8d22\u4ea7\uff0c\u4f46\u662f\u8981\u4e8c\u623f\u3001\u4e09\u623f\u540c\u610f\u4ed6\u7684\u8ba1\u5212\uff0c\u5171\u540c\u63d0\u632f\u5e02\u573a\u5bf9\u5609\u4fe1\u96c6\u56e2\u7684\u4fe1\u5fc3\u3002\u867d\u7136\u8fd9\u4e2a\u8ba1\u5212\u4f1a\u7ed9\u5609\u4fe1\u5b9e\u4e1a\u9020\u6210\u5f88\u5927\u7684\u538b\u529b\uff0c\u4f46\u662f\u6b63\u5982\u5b59\u5c1a\u4e49\u81ea\u5df1\u8bf4\u7684\uff0c\u4ed6\u4e0e\u845b\u660e\u5fb7\u5df2\u7ecf\u6210\u5c11\u6570\u6d3e\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u5230\u8fd9\u6d88\u606f\uff0c\u5374\u5927\u4e3a\u5174\u594b\uff0c\u5fcd\u4e0d\u4f4f\u8981\u8ddf\u53f6\u5efa\u658c\u51fb\u638c\u76f8\u5e86\uff0c\u5927\u7b11\u9053\uff1a\u201c\u679c\u7136\u4e0d\u51fa\u6211\u6240\u6599\uff0c\u8c22\u5bb6\u8981\u5c06\u9526\u57ce\u629b\u5f03\u6389\u4e86\uff0c\u8fd9\u624d\u662f\u4ed6\u4eec\u4e00\u60ef\u7684\u4f5c\u98ce\u3002\u201d</p></article>"}}}]);
