(window.webpackJsonp=window.webpackJsonp||[]).push([[622],{645:function(p){p.exports={chapterTitle:null,title:"\u7b2c622\u7ae0 \u8981\u8d70\u7684\u9053\u8def",content:"<article><h2>\u7b2c622\u7ae0 \u8981\u8d70\u7684\u9053\u8def</h2><p>\u3000\u3000\u5750\u5728\u5e2d\u82e5\u7433\u5bf9\u9762\u7684\u7537\u4eba\u770b\u5230\u5e2d\u82e5\u7433\u51dd\u7709\u770b\u7740\u7a97\u5916\u7f13\u7f13\u542f\u52a8\u7684\u5954\u9a70\uff0c\u9887\u4e3a\u611f\u6168\u7684\u5bf9\u5e2d\u82e5\u7433\u8bf4\uff1a\u201c\u4f60\u7b49\u7740\u5427\uff0c\u7ec8\u6709\u4e00\u5929\u6211\u4e5f\u4f1a\u5f00\u7740\u8fd9\u6837\u7684\u8f66\u5230\u5b66\u6821\u95e8\u53e3\u6765\u63a5\u4f60\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8c01\u7a00\u7f55\uff1f\u201d\u5e2d\u82e5\u7433\u773c\u795e\u4ece\u7a97\u5916\u6536\u56de\u6765\uff0c\u5a07\u55d4\u7684\u8bf4\u9053\uff0c\u201c\u8fd9\u8f66\u5c31\u5f88\u4e86\u4e0d\u8d77\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u5f53\u7136\uff0c\u5bf9\u7edd\u5927\u591a\u6570\u7537\u4eba\u6765\u8bf4\uff0c\u80fd\u62e5\u6709\u8fd9\u4e48\u4e00\u8f86\u8f66\uff0c\u7b97\u662f\u7ec8\u6781\u68a6\u60f3\u4e86\u2026\u2026\u201d\u90a3\u7537\u4eba\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u5bf9\u6765\u8bf4\uff0c\u4e5f\u662f\u5982\u6b64\u2026\u2026\u201d</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u7741\u773c\u770b\u4e86\u4ed6\u51e0\u79d2\u949f\uff0c\u90fd\u4e0d\u77e5\u9053\u8ddf\u4ed6\u600e\u4e48\u8bf4\u521a\u624d\u4f4f\u8fdb\u8f66\u91cc\u53bb\u7684\u90a3\u4e2a\u7537\u5b69\u662f\u5979\u7684\u5b66\u751f\uff0c\u6628\u5929\u591c\u91cc\u4ece\u5c71\u4e0a\u4ed3\u60f6\u7684\u9003\u4e0b\u6765\uff0c\u4e5f\u6ca1\u6709\u8ddf\u4ed6\u8bf4\u5750\u5728\u53f0\u9636\u4e0a\u559d\u9152\u804a\u5929\u7684\u4e24\u4e2a\u7537\u751f\u90fd\u662f\u81ea\u5df1\u8ba4\u8bc6\u7684\u3002</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u5bf9\u8f7f\u8f66\u4e0d\u611f\u5174\u8da3\uff0c\u5979\u5750\u7684\u89d2\u5ea6\u53c8\u770b\u4e0d\u5230\u8f66\u6807\uff0c\u53ea\u89c9\u5f97\u8fd9\u8f66\u602a\u602a\u7684\uff0c\u7ebf\u6761\u504f\u786c\uff0c\u4ece\u5973\u6027\u7684\u89d2\u5ea6\u6765\u770b\uff0c\u8f66\u8eab\u53c8\u592a\u5927\u4e86\u4e00\u4e9b\uff0c\u8272\u5f69\u4e5f\u4e0d\u591f\u660e\u8273\uff0c\u4e5f\u4e0d\u4ee5\u4e3a\u4ec0\u4e48\u4e86\u4e0d\u8d77\u7684\u8f66\uff0c\u542c\u7537\u53cb\u5c06\u8fd9\u8f66\u79f0\u4e4b\u4e3a\u7edd\u5927\u591a\u6570\u7537\u4eba\u7684\u7ec8\u6781\u68a6\u60f3\uff0c\u624d\u610f\u8bc6\u8fd9\u8f66\u5e94\u8be5\u5f88\u9ad8\u7ea7\u5427\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9b4f\u4e1c\u5f3a\u4e0e\u5176\u4ed6\u4e09\u540d\u4e3b\u4efb\u52a9\u7406\u68c0\u67e5\u8fc7\u4e94\u4e2a\u5b66\u751f\u5bdd\u5ba4\u7684\u536b\u751f\uff0c\u8dd1\u5230\u7814\u7a76\u751f\u516c\u5bd3\u53bb\u627e\u6797\u51b0\uff0c\u7ecf\u8fc7\u4e8c\u697c\u65f6\uff0c\u811a\u4e0b\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u5f80\u8fc7\u9053\u4e1c\u9762\u671b\u4e86\u4e00\u773c\uff0c\u7709\u5934\u76b1\u7740\uff0c\u53c8\u7ee7\u7eed\u5f80\u697c\u4e0a\u8d70\uff0c\u8d70\u5230\u6797\u51b0\u5bbf\u820d\u697c\u524d\u6572\u95e8\u3002\u51e0\u4e4e\u6ca1\u6709\u803d\u6401\uff0c\u6797\u51b0\u5c31\u5728\u91cc\u9762\u6253\u5f00\u95e8\u2014\u2014\u5979\u6b63\u62ff\u7740\u6302\u5728\u5899\u58c1\u4e0a\u7684\u7535\u8bdd\u8ddf\u522b\u4eba\u901a\u7535\u8bdd\u3002</p><p>\u3000\u3000\u9b4f\u4e1c\u5f3a\u542c\u4e86\u4e00\u4f1a\u513f\uff0c\u6797\u51b0\u662f\u8ddf\u4ee4\u5c0f\u71d5\u5728\u901a\u7535\u8bdd\uff0c\u597d\u50cf\u5728\u7ea6\u665a\u4e0a\u4e00\u8d77\u5403\u996d\u7684\u4e8b\u60c5\uff0c\u6362\u4f5c\u5f80\u65f6\uff0c\u4ed6\u662f\u4e0d\u9ad8\u5174\u8ddf\u7740\u4e00\u8d77\u53bb\u51d1\u70ed\u95f9\u7684\uff0c\u770b\u7740\u522b\u4eba\u8863\u51a0\u9c9c\u695a\uff0c\u5fc3\u91cc\u603b\u4e0d\u662f\u6ecb\u5473\u3002\u4ed6\u5728\u4e1c\u6d77\u5927\u5b66\u7559\u6821\u7684\u6708\u5de5\u8d44\u8fd8\u4e0d\u5230\u56db\u767e\u5757\uff0c\u7b2c\u4e00\u5e74\u5c97\u4f4d\u6d25\u8d34\u4e5f\u7b49\u540c\u4e8e\u65e0\uff0c\u600e\u4e48\u8ddf\u4eba\u5bb6\u5e73\u7b49\u7684\u4ea4\u5f80\u3002</p><p>\u3000\u3000\u6797\u51b0\u4e5f\u6015\u9b4f\u4e1c\u5f3a\u4e0d\u9ad8\u5174\u53bb\uff0c\u5f81\u8be2\u7684\u770b\u4e86\u4ed6\u4e00\u773c\uff1b\u9b4f\u4e1c\u5f3a\u522b\u8fc7\u8138\u53bb\uff0c\u4e5f\u4e0d\u8bf4\u4e0d\u540c\u610f\u3002</p><p>\u3000\u3000\u6731\u5c0f\u519b\u4e0e\u4ee4\u5c0f\u71d5\u8fc7\u6765\u63a5\u4ed6\u4eec\uff1b\u6797\u51b0\u4e0e\u9b4f\u4e1c\u5f3a\u4e0b\u697c\uff0c\u770b\u89c1\u6731\u5c0f\u519b\u5750\u5728\u4e00\u8f86\u9ed1\u8272\u7684\u5965\u8fea\u8f7f\u8f66\u91cc\uff0c\u5934\u624b\u4f38\u51fa\u8f66\u7a97\u8ddf\u5979\u4eec\u6253\u62db\u547c\uff0c\u6797\u51b0\u7fa1\u6155\u7684\u8bf4\uff1a\u201c\u6731\u5927\u79d1\u957f\u90fd\u6709\u4e13\u8f66\u4e86\uff1f\u914d\u7f6e\u4e0d\u9519\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u54ea\u6709\uff1f\u521a\u624d\u9001\u6211\u4eec\u5c40\u957f\u53bb\u673a\u573a\uff0c\u4eab\u53d7\u4e00\u4e0b\u7279\u6b8a\u798f\u5229\uff0c\u8981\u60f3\u6709\u4e13\u8f66\uff0c\u4e5f\u8981\u6df7\u5230\u6211\u4eec\u5c40\u957f\u90a3\u7ea7\u522b\uff1b\u518d\u8bf4\u8fd9\u8f66\u4e5f\u662f\u8d85\u914d\uff0c\u5e02\u91cc\u7279\u6279\u7684\uff0c\u6211\u4eec\u5c40\u63a5\u5f85\u7684\u5ba2\u4eba\u89c4\u683c\u9ad8\uff0c\u6ca1\u6709\u4e00\u8f86\u597d\u8f66\u4e0d\u884c\u3002\u201d\u6731\u5c0f\u519b\u4f38\u624b\u5e2e\u4ed6\u4eec\u6253\u5f00\u8f66\u95e8\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u62ac\u5934\u770b\u4e86\u770b\u4e8c\u697c\uff0c\u95ee\u6797\u51b0\uff1a\u201c\u5f20\u606a\u90a3\u5c0f\u5b50\u5c31\u4f4f\u4e8c\u697c\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u8981\u4e0d\u8981\u8bf7\u4ed6\u4e00\u8d77\u5403\u996d\uff1f\u201d\u6797\u51b0\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u53bb\u8bf7\u4e00\u4e0b\u3002\u201d\u6731\u5c0f\u519b\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u6253\u7535\u8bdd\u5f97\u4e86\u3002\u201d\u9b4f\u4e1c\u5f3a\u4e0d\u60f3\u722c\u4e0a\u722c\u4e0b\u7684\u3002</p><p>\u3000\u3000\u201c\u4e5f\u4e0d\u5dee\u8fd9\u4e24\u6b65\u8def\uff0c\u4f60\u4eec\u5728\u8f66\u91cc\u7b49\u6211\u3002\u201d\u6731\u5c0f\u519b\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u4e0d\u8ddf\u9b4f\u4e1c\u5f3a\u591a\u8bf4\u4ec0\u4e48\uff0c\u76f4\u63a5\u6253\u5f00\u8f66\u95e8\u8d70\u4e86\u51fa\u53bb\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u5750\u5728\u526f\u9a7e\u9a76\u4f4d\u4e0a\u56de\u8fc7\u5934\u8ddf\u9b4f\u4e1c\u5f3a\u5f00\u73a9\u7b11\u8bf4\uff1a\u201c\u542c\u8bf4\u4f60\u505a\u5f20\u606a\u90a3\u5c0f\u5b50\u7684\u5e74\u7ea7\u4e3b\u4efb\uff0c\u611f\u89c9\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u8fd9\u4e2a\u732a\u5934\uff0c\u4eba\u5bb6\u4e00\u8fc7\u6765\u62a5\u540d\u5c31\u60f3\u7ed9\u4eba\u5bb6\u4e0b\u9a6c\u5a01\uff0c\u90fd\u4e0d\u6653\u5f97\u4ed6\u5fc3\u91cc\u600e\u4e48\u60f3\u7684\u3002\u201d\u6797\u51b0\u627e\u5230\u8bc9\u82e6\u7684\u5bf9\u8c61\uff0c\u62b1\u6028\u4e4b\u524d\u53d1\u751f\u7684\u5c34\u5c2c\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u4ee4\u5c0f\u71d5\u7591\u60d1\u7684\u770b\u4e86\u9b4f\u4e1c\u5f3a\u4e00\u773c\uff0c\u6797\u51b0\u8fd9\u4e48\u8bf4\uff0c\u8ba9\u4ed6\u5f88\u4e0d\u81ea\u5728\uff0c\u4ee4\u5c0f\u71d5\u561f\u561f\u5634\uff0c\u8f6c\u8fc7\u6765\u5750\u7740\u7b49\u6731\u5c0f\u519b\u3002</p><p>\u3000\u3000\u201c\u5bbf\u820d\u91cc\u6ca1\u4eba\u2026\u2026\u201d\u6731\u5c0f\u519b\u8d70\u4e0b\u697c\u6765\uff0c\u6253\u5f00\u8f66\u95e8\u4e0a\u4e86\u8f66\uff0c\u201c\u6211\u4eec\u8d70\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u6ca1\u6709\u4ed6\u7684\u624b\u673a\u53f7\u7801\u2026\u2026\u76f4\u63a5\u6253\u7535\u8bdd\u7ed9\u4ed6\u4e0d\u5c31\u5f97\u4e86\uff1f\u201d\u9b4f\u4e1c\u5f3a\u7591\u60d1\u7684\u95ee\u4e86\u4e00\u53e5\uff0c\u611f\u89c9\u4ed6\u4eec\u8ddf\u5f20\u606a\u8d70\u5f97\u5f88\u8fd1\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u54ea\u6709\u90a3\u8d44\u683c\u2026\u2026\u201d\u6731\u5c0f\u519b\u8fd8\u60f3\u518d\u8bf4\u4ec0\u4e48\uff0c\u4ee4\u5c0f\u71d5\u4f38\u8fc7\u624b\u6765\u6390\u4ed6\u7684\u5927\u817f\uff0c\u5c31\u56de\u5934\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u9b4f\u4e1c\u5f3a\u89c1\u6731\u5c0f\u519b\u6b32\u8a00\u53c8\u6b62\uff0c\u4ec0\u4e48\u8d44\u683c\u4e0d\u8d44\u683c\uff0c\u96be\u9053\u8fde\u77e5\u9053\u5f20\u606a\u624b\u673a\u53f7\u7801\u7684\u8d44\u683c\u90fd\u6ca1\u6709\uff1f\u9b4f\u4e1c\u5f3a\u6000\u7591\u662f\u4e0d\u662f\u81ea\u5df1\u592a\u8fc7\u654f\u4e86\uff0c\u53ea\u662f\u4eca\u665a\u6731\u5c0f\u519b\u3001\u4ee4\u5c0f\u71d5\u7edd\u53e3\u4e0d\u63d0\u5f20\u606a\u7684\u4e8b\u60c5\uff0c\u5373\u4f7f\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u59d0\u59b9\u7684\u4e8b\u60c5\u4e5f\u5f88\u5c11\u63d0\u53ca\u3002</p><p>\u3000\u3000\u5012\u662f\u5403\u5b8c\u996d\u9001\u9b4f\u4e1c\u5f3a\u3001\u6797\u51b0\u56de\u5bbf\u820d\u65f6\uff0c\u4ee4\u5c0f\u71d5\u627e\u5230\u5355\u72ec\u8ddf\u6797\u51b0\u8bf4\u8bdd\u7684\u65f6\u673a\uff0c\u8ddf\u5979\u8bf4\uff1a\u201c\u5f20\u606a\u90a3\u5c0f\u5b50\u597d\u6b79\u7b97\u662f\u5927\u5bb6\u7684\u670b\u53cb\uff0c\u4f60\u4eec\u5728\u5b66\u6821\u91cc\u80fd\u7167\u987e\u8fd8\u662f\u8981\u591a\u7167\u987e\u4eba\u5bb6\u4e00\u4e0b\u7684\uff1b\u9b4f\u4e1c\u5f3a\u8fd8\u771f\u80fd\u628a\u4eba\u5bb6\u5f53\u6210\u65b0\u751f\u7ba1\u5f97\u6b7b\u6b7b\u7684\uff1f\u201d\u8bdd\u4e5f\u5c31\u53ea\u80fd\u8bf4\u5230\u8fd9\u4efd\u4e0a\u3002</p><p>\u3000\u3000\u770b\u7740\u6797\u51b0\u4e0e\u9b4f\u4e1c\u5f3a\u8fdb\u6821\u95e8\uff0c\u6731\u5c0f\u519b\u624d\u95ee\u4ee4\u5c0f\u71d5\u4e3a\u4ec0\u4e48\u4e00\u5f00\u59cb\u8981\u6390\u4ed6\u3002</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u89c9\u5f97\u9b4f\u4e1c\u5f3a\u4e00\u76f4\u770b\u5f20\u606a\u4e0d\u987a\u773c\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u6709\u8fd9\u8d44\u683c\u5417\uff1f\u201d\u6731\u5c0f\u519b\u7b11\u4e86\u7b11\uff0c\u5bf9\u6b64\u4e5f\u4e0d\u4f5c\u4ec0\u4e48\u8bc4\u4ef7\uff0c\u6253\u7740\u65b9\u5411\u76d8\uff0c\u5f00\u7740\u8f66\u5f80\u4f4f\u5904\u9a76\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6797\u51b0\u8bb0\u7740\u4ee4\u5c0f\u71d5\u7684\u8bdd\uff0c\u8d70\u8fdb\u6821\u95e8\uff0c\u5c31\u8ddf\u9b4f\u4e1c\u5f3a\u62b1\u6028\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e3a\u4eba\u5904\u4e8b\u5c31\u4e0d\u80fd\u6e29\u548c\u4e00\u4e9b\uff0c\u9700\u8981\u7ef7\u7740\u90a3\u5f20\u8138\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u600e\u4e48\u4e86\uff1f\u201d\u9b4f\u4e1c\u5f3a\u95ee\u9053\uff0c\u201c\u6211\u8fd8\u662f\u4ed6\u7684\u5e74\u7ea7\u4e3b\u4efb\u4e0d\u662f\uff0c\u6211\u603b\u4e0d\u80fd\u5351\u8eac\u5c48\u819d\u53bb\u8ba8\u597d\u4ed6\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u8c01\u8ba9\u4f60\u53bb\u8ba8\u597d\u4eba\u5bb6\u4e86\uff0c\u8c01\u8ba9\u4f60\u5351\u8eac\u5c48\u819d\u4e86\uff1f\u201d\u6797\u51b0\u5fc3\u91cc\u7684\u59d4\u5c48\u6108\u76db\uff0c\u4e0e\u5f20\u606a\u540c\u4f4f\u4e00\u680b\u697c\uff0c\u8fd9\u4e9b\u5929\u90fd\u6ca1\u597d\u610f\u601d\u8ddf\u4eba\u5bb6\u6253\u58f0\u62db\u547c\uff0c\u201c\u770b\u4f60\u5728\u5d14\u56fd\u6052\u9762\u524d\u5012\u5b66\u4f1a\u4e86\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u5f20\u606a\u597d\u6b79\u4e5f\u8981\u7b97\u6211\u4eec\u7684\u670b\u53cb\u5427\uff0c\u4f60\u6709\u5fc5\u8981\u8fd9\u6837\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u2026\u2026\u201d\u9b4f\u4e1c\u5f3a\u89c1\u6797\u51b0\u8bbd\u523a\u4ed6\u5728\u5d14\u56fd\u6052\u9762\u524d\u5351\u8eac\u5c48\u819d\uff0c\u867d\u7136\u8fd9\u4e5f\u662f\u4e8b\u5b9e\uff0c\u4f46\u662f\u4ed6\u6bd5\u7adf\u662f\u8981\u9762\u5b50\u7684\u4eba\uff0c\u8fd8\u5bb9\u4e0d\u5f97\u522b\u4eba\u8fd9\u4e48\u6233\u4ed6\u7684\u81ea\u5c0a\u5fc3\uff0c\u6c14\u4e0d\u6253\u4e00\u5904\uff0c\u51b7\u773c\u77aa\u7740\u6797\u51b0\uff0c\u201c\u4f60\u4ee5\u4e3a\u6211\u4e50\u610f\u8fd9\u6837\uff0c\u6211\u4ee5\u4e3a\u6211\u4e50\u610f\u5351\u8eac\u5c48\u819d\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u597d\u4e86\uff0c\u6211\u4eec\u4e0d\u8981\u4e3a\u8fd9\u4e9b\u4e8b\u4e89\u5435\u4e86\uff0c\u201d\u6797\u51b0\u6001\u5ea6\u5148\u8f6f\u4e0b\u6765\uff0c\u4e24\u4e2a\u4eba\u7684\u5bb6\u5883\u90fd\u4e0d\u597d\uff0c\u9664\u4e86\u81ea\u5df1\u52aa\u529b\u4e4b\u5916\uff0c\u6ca1\u6709\u5176\u4ed6\u9014\u5f84\u53ef\u8d70\uff0c\u4e5f\u5934\u75bc\u9b4f\u4e1c\u5f3a\u7684\u8f9b\u82e6\uff0c\u67d4\u58f0\u8bf4\u9053\uff0c\u201c\u6211\u4e5f\u5c31\u5e0c\u671b\u4f60\u5f85\u4eba\u5bb6\u7684\u6001\u5ea6\u7a0d\u5fae\u7f13\u548c\u4e00\u4e0b\uff0c\u5f20\u606a\u4e5f\u4e0d\u662f\u90a3\u79cd\u4f1a\u8e6c\u9f3b\u5b50\u5c31\u4e0a\u8138\u7684\u4eba\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u90fd\u8bf4\u5f20\u606a\u600e\u4e48\u600e\u4e48\u6837\uff0c\u4f60\u4eec\u5230\u5e95\u4e86\u89e3\u4ed6\u591a\u5c11\uff1f\u201d\u9b4f\u4e1c\u5f3a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u6709\u94b1\u6709\u52bf\u4eba\u5bb6\u7684\u5c0f\u5b69\u5457\uff0c\u4f60\u4e0d\u662f\u8bf4\u5f20\u606a\u4ed6\u7238\u7238\u5728\u65b0\u829c\u5f53\u526f\u5e02\u957f\u5417\uff1f\u201d\u5728\u6797\u51b0\u770b\u6765\uff0c\u53bf\u957f\u90fd\u662f\u5929\u5927\u7684\u5b98\u4e86\u3002</p><p>\u3000\u3000\u6c89\u9ed8\u7684\u8d70\u4e86\u4e00\u6bb5\u8def\uff0c\u9b4f\u4e1c\u5f3a\u88e4\u515c\u91cc\u7684\u5bfb\u547c\u673a\u201c\u54d4\u54d4\u54d4\u201d\u7684\u54cd\u4e86\u8d77\u6765\uff0c\u4ed6\u638f\u51fa\u6765\u770b\u4e86\u773c\uff0c\u8ddf\u6797\u51b0\u8bf4\uff1a\u201c\u5d14\u9662\u547c\u6211\uff0c\u4f60\u5148\u56de\u5bbf\u820d\u5427\uff0c\u6211\u76f4\u63a5\u53bb\u5d14\u9662\u957f\u5bb6\u95ee\u4e00\u4e0b\u662f\u4ec0\u4e48\u4e8b\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u60e0\u5c71\u5357\u90ca\u5c71\u4e0a\u662f\u5904\u5e7d\u9759\u7684\u6240\u5728\uff0c\u7d2b\u7af9\u56ed\u81ea\u7136\u6ca1\u6709\u7d2b\u8272\u7684\u7af9\u5b50\uff0c\u4f46\u662f\u8fd9\u5468\u8fb9\u90fd\u662f\u7af9\u6797\u7af9\u6d77\uff0c\u591c\u6765\u98ce\u8d77\uff0c\u542c\u7740\u7af9\u68a2\u7c07\u52a8\uff0c\u6070\u5982\u7af9\u6d9b\u5728\u6d8c\u3002</p><p>\u3000\u3000\u5f20\u606a\u5230\u60e0\u5c71\uff0c\u5c31\u4f4f\u5230\u7d2b\u7af9\u56ed\u6765\uff0c\u4e00\u6765\u4e0e\u7d2b\u7af9\u56ed\u7684\u4e3b\u4eba\u8d75\u6893\u4e5f\u76f8\u719f\uff0c\u8fd9\u91cc\u73af\u5883\u5e7d\u9759\uff0c\u4e5f\u5408\u9002\u4e0e\u60e0\u5c71\u5e02\u59d4\u4e66\u8bb0\u8d75\u9633\u89c1\u9762\u5403\u996d\u3002</p><p>\u3000\u3000\u8d75\u9633\u5165\u591c\u8d76\u8fc7\u6765\uff0c\u9664\u4e86\u65e9\u5c31\u8ddf\u5f20\u606a\u89c1\u8fc7\u9762\u7684\u5e02\u59d4\u79d8\u4e66\u957f\uff0c\u8fd8\u62c9\u4e0a\u5e02\u516c\u5b89\u5c40\u7684\u5b8b\u6653\uff1b\u4ed6\u540e\u6765\u624d\u77e5\u9053\u5f20\u606a\u5bf9\u5b8b\u6653\u7b97\u662f\u6709\u6551\u547d\u4e4b\u6069\uff0c\u62c9\u4e0a\u5b8b\u6653\uff0c\u53cc\u65b9\u5173\u7cfb\u80fd\u66f4\u4eb2\u5bc6\u4e9b\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u5230\u60e0\u5c71\u6765\u5df2\u7ecf\u6709\u597d\u4e9b\u5929\u4e86\uff0c\u9999\u96ea\u6d77\u90a3\u9762\uff0c\u5c31\u9676\u884c\u5065\u3001\u53f6\u5c0f\u6850\uff0c\u53e6\u5916\u8fd8\u6709\u53e6\u5916\u4e00\u4e2a\u526f\u603b\u90ed\u5efa\u51fa\u5e2d\u3002</p><p>\u3000\u3000\u9999\u96ea\u6d77\u773c\u4e0b\u7684\u80a1\u6743\u7ed3\u6784\uff0c\u7231\u8fbe\u7535\u5b50\u6301\u80a151\uff05\uff0c\u662f\u63a7\u80a1\u80a1\u4e1c\uff0c\u60e0\u5c71\u5e02\u8f7b\u5de5\u5c40\u4ee3\u8868\u5e02\u91cc\u6301\u80a149\uff05\uff0c\u53bb\u5e74\u968f\u7701\u7ecf\u8d38\u56e2\u4e00\u9053\uff0c\u4ece\u65e5\u672c\u83b7\u5f97\u4e94\u5343\u4e07\u7f8e\u5143\u7684\u8d37\u6b3e\uff0c\u5df2\u7ecf\u7528\u4e8e\u6ee8\u6c5f\u5bb6\u7535\u4ea7\u4e1a\u56ed\u533a\u7684\u5efa\u8bbe\u3002</p><p>\u3000\u3000\u4eca\u5929\u5927\u5bb6\u5750\u5728\u4e00\u8d77\uff0c\u5012\u4e0d\u662f\u8c08\u8bba\u5165\u8d44\u9999\u96ea\u6d77\u7684\u4e8b\u60c5\uff0c\u5c31\u7b97\u7231\u8fbe\u7535\u5b50\u4e0d\u5bf9\u9999\u96ea\u6d77\u63a7\u80a1\uff0c\u60e0\u5c71\u5e02\u65b9\u9762\u8fd8\u80fd\u62d2\u7edd\u516b\u4ebf\u6e2f\u5e01\u7684\u6295\u8d44\uff1f</p><p>\u3000\u3000\u8d75\u9633\u6bd4\u8f83\u5173\u5fc3\u60e0\u5c71\u5728\u4e1c\u6d77\u7701\u9ad8\u65b0\u4ea7\u4e1a\u65b0\u6218\u7565\u91cc\u7684\u5730\u4f4d\u95ee\u9898\u3002</p><p>\u3000\u3000\u7701\u91cc\u4e0e\u5efa\u90ba\u5e02\u91cc\u76f8\u7ee7\u5236\u5b9a\u7684\u9ad8\u65b0\u4ea7\u4e1a\u53d1\u5c55\u7684\u65b0\u6218\u7565\uff0c\u9526\u6e56\u90fd\u65e0\u53ef\u66ff\u4ee3\u7684\u5360\u636e\u6781\u5176\u91cd\u8981\u7684\u4f4d\u7f6e\u3002\u60e0\u5c71\u5e02\u7684\u52a8\u4f5c\u5df2\u7ecf\u6162\u4e86\u534a\u62cd\uff0c\u5373\u4f7f\u60e0\u5c71\u5e02\u5c06\u878d\u5165\u5168\u7403\u4ea7\u4e1a\u94fe\u4f5c\u4e3a\u672a\u6765\u53d1\u5c55\u7684\u91cd\u70b9\uff0c\u4e5f\u8981\u6839\u636e\u7701\u91cc\u7684\u7edf\u4e00\u89c4\u5212\u5236\u5b9a\u51fa\u53d1\u5c55\u7684\u8be6\u7ec6\u65b9\u6848\u6765\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u518d\u6b21\u8d70\u5230\u53f0\u524d\u3001\u9886\u961f\u6e05\u67e5\u5404\u7701\u7684\u56fd\u6295\u516c\u53f8\uff0c\u662f\u5f88\u591a\u4eba\u90fd\u9884\u60f3\u4e0d\u5230\u7684\u4e8b\u60c5\u3002\u53f6\u5bb6\u7684\u53f6\u81fb\u6c11\u56e0\u4e3a\u4e1c\u5357\u4e9a\u91d1\u878d\u5371\u673a\u4e5f\u8000\u773c\u5f97\u8fc7\u5206\uff0c\u6210\u4e3a\u65b0\u5174\u7684\u653f\u6cbb\u660e\u661f\u3002\u8fd9\u6b21\u867d\u8bf4\u53f6\u81fb\u6c11\u5f88\u53ef\u80fd\u4f1a\u7f3a\u5e2d\u5317\u4eac\u7684\u4f1a\u8bae\uff0c\u4f46\u4ed6\u5374\u4f1a\u5728\u8fd9\u6b21\u5317\u4eac\u4f1a\u8bae\u4e0a\u518d\u5347\u4e00\u7ea7\uff0c\u5b9e\u5728\u8ba9\u4eba\u7fa1\u715e\uff1b\u8fd9\u6b21\u7684\u5317\u4eac\u4f1a\u8bae\uff0c\u5f90\u5b66\u5e73\u7684\u5730\u4f4d\u4e5f\u4e0d\u53ef\u80fd\u4f1a\u7ed9\u524a\u5f31\u3002</p><p>\u3000\u3000\u5efa\u90ba\u5e02\u9700\u8981\u501f\u52a9\u9526\u6e56\u7684\u529b\u91cf\u771f\u6b63\u7684\u632f\u5174\u5efa\u90ba\u7684\u9ad8\u65b0\u4ea7\u4e1a\uff0c\u7f57\u541b\u751a\u81f3\u5c31\u5dee\u4e3e\u5757\u724c\u8bf4\u660e\u4ed6\u5bf9\u9526\u6e56\u7684\u652f\u6301\u3002</p><p>\u3000\u3000\u9676\u664b\u3001\u674e\u8fdc\u6e56\u90fd\u662f\u52a1\u5b9e\u7684\u5b98\u5458\uff0c\u81ea\u7136\u4e0d\u53ef\u80fd\u4e0d\u652f\u6301\u9526\u6e56\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u60c5\u5f62\u4e0b\uff0c\u5f88\u6709\u5fc5\u8981\u501f\u9999\u96ea\u6d77\u5165\u8d44\u8c08\u5224\u7684\u673a\u4f1a\u7ea6\u5f20\u606a\u5230\u60e0\u5c71\u6765\u8c08\u4e00\u8c08\u3002</p><p>\u3000\u3000\u867d\u7136\u5c31\u7b97\u5f20\u606a\u968f\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u4ee5\u53ca\u53f6\u5bb6\u518d\u6b21\u5165\u80a1\u8d8a\u79c0\u63a7\u80a1\uff0c\u4ed6\u540d\u4e0b\u7684\u80a1\u6743\u8fd8\u53ea\u6709\u533a\u533a9\uff05\u800c\u5df2\uff1b\u8d75\u9633\u4e5f\u4e0d\u7ba1\u4ec0\u4e48\u80a1\u6743\u4e0d\u80a1\u6743\uff0c\u4ed6\u76f8\u4fe1\u5f20\u606a\u8fd9\u4e2a\u5e74\u8f7b\u5f97\u8fc7\u5206\u7684\u9752\u5e74\uff0c\u662f\u771f\u6b63\u80fd\u5bf9\u9999\u96ea\u6d77\u53d1\u6325\u51b3\u5b9a\u6027\u5f71\u54cd\u7684\u4eba\uff0c\u6d77\u88d5\u7684\u8c22\u665a\u6674\u51e0\u5e74\u6765\u884c\u4e8b\u4e00\u5411\u4f4e\u8c03\uff0c\u60f3\u5fc5\u4e5f\u4e0d\u60f3\u7ed9\u5f90\u5b66\u5e73\u60f9\u53bb\u989d\u5916\u7684\u9ebb\u70e6\uff0c\u90a3\u66f4\u6709\u7406\u7531\u76f8\u4fe1\u5f20\u606a\u662f\u8c22\u665a\u6674\u7684\u4ee3\u7406\u4eba\u3002</p><p>\u3000\u3000\u201c\u542c\u8bf4\u8fd9\u6b21\u4f60\u7236\u4eb2\u6709\u671b\u5f80\u524d\u518d\u8fdb\u4e00\u6b65\uff1f\u201d\u5165\u5ea7\u65f6\uff0c\u8d75\u9633\u62c9\u5f20\u606a\u5750\u5230\u8eab\u8fb9\uff0c\u4eba\u5bb6\u90fd\u8bf4\u864e\u7236\u65e0\u72ac\u5b50\uff0c\u5f20\u77e5\u884c\u7236\u5b50\u6216\u8bb8\u8981\u8bf4\u864e\u5b50\u65e0\u72ac\u7236\uff0c\u4e0d\u8fc7\u771f\u8981\u8fd9\u4e48\u8bf4\uff0c\u542c\u4e0a\u53bb\u8fd8\u771f\u662f\u522b\u626d\u3002\u5f20\u77e5\u884c\u53bb\u65b0\u829c\u62c5\u4efb\u526f\u5e02\u957f\uff0c\u6210\u7ee9\u6590\u7136\u4e5f\u662f\u4f17\u76ee\u6240\u7779\u7684\u4e8b\u5b9e\uff0c\u66f4\u4e0d\u7528\u8bf4\u9526\u6e56\u5728\u7701\u91cc\u7684\u5f71\u54cd\u529b\u4e86\u3002</p><p>\u3000\u3000\u201c\u8fd9\u90fd\u8981\u770b\u7701\u91cc\u7684\u610f\u601d\uff0c\u6211\u4e5f\u4e0d\u5927\u6e05\u695a\uff0c\u6211\u603b\u4e0d\u80fd\u66ff\u6211\u7238\u7238\u64cd\u5fc3\u8fd9\u4e9b\u6709\u7684\u6ca1\u7684\uff0c\u90a3\u6837\u5c31\u6709\u4e9b\u4e0d\u5927\u50cf\u6837\u4e86\u3002\u201d\u5f20\u606a\u5b09\u7b11\u7740\u56de\u9053\uff0c\u5fc3\u60f3\u7238\u7238\u518d\u8fdb\u4e00\u6b65\uff0c\u4e5f\u5c31\u662f\u8fdb\u65b0\u829c\u5e02\u5e38\u59d4\uff0c\u8d75\u9633\u662f\u80fd\u8bf4\u5f97\u4e0a\u8bdd\u7684\u4eba\u7269\uff0c\u4f46\u662f\u7f57\u541b\u53c8\u4f55\u5c1d\u8bf4\u4e0d\u4e0a\u8bdd\uff1f\u674e\u8fdc\u6e56\u7684\u5f71\u54cd\u529b\u8fd8\u66f4\u5927\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5f20\u606a\u5176\u5b9e\u4e5f\u5f88\u5934\u75bc\uff0c\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u4ed6\u53ea\u80fd\u5cbf\u7136\u4e0d\u52a8\u5750\u770b\u522b\u4eba\u7684\u5fc3\u601d\uff0c\u4e5f\u5e0c\u671b\u8d75\u9633\u80fd\u660e\u767d\u81ea\u5df1\u7684\u5fc3\u601d\u3002</p><p>\u3000\u3000\u5728\u4e1c\u6d77\u7701\u5185\uff0c\u7f57\u541b\u3001\u8d75\u9633\u3001\u674e\u8fdc\u6e56\u4e09\u4e2a\u4eba\uff0c\u5728\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u662f\u6784\u6210\u7ade\u4e89\u5173\u7cfb\u7684\uff0c\u50cf\u8d75\u9633\u5c31\u662f\u9876\u66ff\u674e\u8fdc\u6e56\u5750\u4e0a\u60e0\u5c71\u5e02\u59d4\u4e66\u8bb0\u7684\u4f4d\u5b50\uff0c\u674e\u8fdc\u6e56\u8981\u662f\u5728\u7701\u957f\u7684\u4f4d\u5b50\u6ca1\u4ec0\u4e48\u5efa\u6811\uff0c\u5c31\u4f1a\u7ed9\u6324\u8d70\u8ba9\u8d24\u4e8e\u4eba\u3002\u4e0d\u8fc7\u7f57\u541b\u4e0e\u8d75\u9633\u7684\u7ade\u4e89\u5173\u7cfb\u66f4\u5f3a\u70c8\u4e00\u4e9b\uff0c\u60e0\u5c71\u5e02\u3001\u5efa\u90ba\u5e02\u7684\u653f\u5e9c\u5730\u4f4d\u7279\u6b8a\uff0c\u8c01\u80fd\u66f4\u51fa\u8272\u4e00\u4e9b\uff0c\u5c31\u80fd\u5927\u8e0f\u6b65\u7684\u8de8\u8fdb\u7701\u90e8\u7ea7\u6b63\u804c\u7684\u5e8f\u5217\uff0c\u5bf9\u4e8e\u4ed6\u4eec\u6765\u8bf4\uff0c\u8fd9\u662f\u4ed5\u9014\u91cc\u6700\u91cd\u8981\u7684\u4e00\u6b65\uff0c\u53ea\u6709\u8d70\u51fa\u8fd9\u4e00\u6b65\uff0c\u624d\u6709\u8d44\u683c\u5165\u9601\u62dc\u76f8\u3002\u673a\u4f1a\u603b\u662f\u6709\u9650\u7684\uff0c\u8981\u8bf4\u8c01\u80fd\u5f97\u5230\u7684\u652f\u6301\u66f4\u5f3a\u4e5f\u8bf4\u4e0d\u51c6\uff0c\u4e00\u65b9\u9762\u8981\u83b7\u5f97\u66f4\u591a\u7684\u652f\u6301\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u4e3a\u5b98\u4e00\u4efb\u7684\u653f\u7ee9\u5c31\u6210\u4e3a\u660e\u9762\u8fc7\u786c\u7684\u6307\u6807\u662f\u8c01\u4e5f\u65e0\u6cd5\u5ffd\u7565\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u9526\u6e56\u8981\u53d1\u5c55\uff0c\u79bb\u4e0d\u5f00\u653f\u6cbb\u8d44\u6e90\u7684\u652f\u6301\uff0c\u6709\u65f6\u5019\u4e5f\u6015\u5939\u5728\u5f53\u4e2d\u96be\u505a\u4eba\uff0c\u60df\u6709\u5e0c\u671b\u8d75\u9633\u3001\u7f57\u541b\u4e0d\u8981\u5c06\u76ee\u5149\u653e\u5728\u7701\u5185\uff0c\u7701\u5916\u7701\u90e8\u7ea7\u6b63\u804c\u7684\u4f4d\u5b50\u66f4\u5145\u88d5\u4e00\u4e9b\u3002\u8981\u4e89\u7701\u91cc\u4f4d\u5b50\uff0c\u53ea\u6015\u8981\u4e89\u5f97\u5934\u7834\u8840\u6d41\u90fd\u65e0\u6cd5\u7518\u4f11\u554a\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u662f\u9526\u6e56\u8981\u4e0e\u674e\u8fdc\u6e56\u3001\u7f57\u541b\u3001\u8d75\u9633\u8fd9\u6837\u7684\u4eba\u7269\u4fdd\u6301\u4e00\u5b9a\u8ddd\u79bb\u7684\u539f\u56fd\uff0c\u5f53\u521d\u4e5f\u662f\u5192\u7740\u4e0e\u674e\u8fdc\u6e56\u51b3\u88c2\u7684\u5371\u9669\u65ad\u7136\u62d2\u7edd\u9526\u6e56\u6210\u4e3a\u4ed6\u624b\u91cc\u7684\u653f\u7ee9\u5de5\u5177\u3002</p><p>\u3000\u3000\u5728\u56fd\u5185\uff0c\u4e00\u5bb6\u6c11\u8425\u4f01\u4e1a\u8981\u4fdd\u6301\u5145\u5206\u7684\u72ec\u7acb\u6027\u3001\u81ea\u4e3b\u6027\uff0c\u672c\u8eab\u5c31\u662f\u5728\u8d70\u4e00\u6761\u8d8a\u8d70\u4f3c\u4e4e\u8d8a\u72ed\u9698\u7684\u8def\u3002</p><p>\u3000\u3000\u4e0e\u5f90\u5b66\u5e73\u5230\u5e95\u662f\u4eb2\u60c5\u591a\u4e8e\u5176\u4ed6\uff0c\u6ca1\u6709\u592a\u591a\u7684\u5229\u7528\u5fc3\u601d\u5728\u5185\uff1b\u4e0e\u53f6\u5efa\u658c\u7684\u5173\u7cfb\uff0c\u4e5f\u8db3\u591f\u4fdd\u6301\u4e0e\u53f6\u5bb6\u7684\u5bc6\u5207\u8054\u7cfb\uff0c\u7279\u522b\u662f\u5728\u5546\u4e1a\u4e0a\u643a\u624b\u5171\u8fdb\uff0c\u66f4\u5229\u4e8e\u53cc\u65b9\u5173\u7cfb\u7684\u7a33\u5065\u53d1\u5c55\uff1b\u6b64\u5916\uff0c\u8fd8\u9009\u62e9\u4e0e\u987e\u5bb6\u4ea4\u597d\uff0c\u4e00\u65b9\u9762\u662f\u987e\u5bb6\u4e0e\u53f6\u5bb6\u5173\u7cfb\u5bc6\u5207\uff0c\u53e6\u4e00\u65b9\u9762\u59da\u6587\u76db\u6b64\u65f6\u4e0d\u9732\u5c71\u4e0d\u663e\u6c34\uff0c\u6613\u4e8e\u53d1\u5c55\u5bc6\u5207\u7684\u5173\u7cfb\uff0c\u800c\u4e0d\u81f3\u4e8e\u7acb\u5373\u53d7\u5236\u4e8e\u4eba\u3002</p></article>"}}}]);
