(window.webpackJsonp=window.webpackJsonp||[]).push([[628],{651:function(p){p.exports={chapterTitle:null,title:"\u7b2c477\u7ae0 \u6d77\u5916\u79c1\u52df",content:"<article><h2>\u7b2c477\u7ae0 \u6d77\u5916\u79c1\u52df</h2><p>\u3000\u3000\u4e2d\u94f6\u5927\u53a6\u7684\u4e1c\u897f\u4e24\u4fa7\u5404\u6709\u4e00\u4e2a\u5ead\u56ed\uff0c\u56ed\u4e2d\u6709\u6d41\u6c34\u3001\u7011\u5e03\u3001\u5947\u77f3\u4e0e\u6811\u6728\uff0c\u6d41\u6c34\u987a\u7740\u5730\u52bf\u6f7a\u6f7a\u800c\u4e0b\uff0c\u4ece\u5b9e\u7528\u89d2\u5ea6\u6765\u8bf4\uff0c\u6f7a\u6f7a\u7684\u6d41\u6c34\u58f0\u4e5f\u53ef\u4ee5\u5438\u6536\u5468\u56f4\u9ad8\u67b6\u9053\u8def\u4f20\u6765\u7684\u4ea4\u901a\u566a\u97f3\uff0c\u4f46\u662f\u50cf\u845b\u660e\u5fb7\u8fd9\u6837\u7684\u4f20\u7edf\u9999\u6e2f\u4eba\uff0c\u5374\u4ee5\u4e3a\u6f7a\u6f7a\u7684\u6d41\u6c34\u5bd3\u610f\u7740\u751f\u751f\u4e0d\u606f\u7684\u8d22\u6e90\uff0c\u5927\u53a6\u897f\u5357\u89d2\u8038\u7acb\u7684\u201c\u548c\u8c10\u76f8\u5904\u201d\u7684\u94dc\u5851\uff0c\u4e24\u4e2a\u5de8\u5927\u7684\u9752\u94dc\u5851\u50cf\u5728\u7070\u8272\u7684\u82b1\u5c97\u5ca9\u886c\u6258\u4e0b\u751a\u662f\u62a2\u773c\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u5728\u4e2d\u94f6\u5927\u53a6\u524d\uff0c\u671b\u7740\u6709\u4e09\u5c42\u697c\u9ad8\u7684\u77f3\u8d28\u58a9\u5ea7\uff0c\u518d\u5f80\u4e0a\u5c31\u662f\u6620\u7740\u851a\u8272\u5929\u7a7a\u989c\u8272\u7684\u73bb\u7483\u5e55\u5899\uff0c\u51dd\u89c6\u7740\u4e00\u4f1a\u513f\u697c\u9876\u7684\u4e24\u53ea\u9876\u6746\uff0c\u8ba9\u4eba\u76ee\u7729\u795e\u79fb\u3002</p><p>\u3000\u3000\u4f3c\u4e4e\u53ea\u8981\u518d\u5f80\u524d\u8de8\u4e00\u6b65\uff0c\u5c31\u610f\u8bc6\u7740\u771f\u6b63\u8981\u8de8\u5165\u5de8\u5bcc\u534e\u5546\u4e4b\u5217\uff0c\u5f20\u606a\u5374\u662f\u9887\u6709\u610f\u5473\u7684\u6b23\u8d4f\u8d77\u8fd9\u5ea7\u4e5d\u516d\u5e74\u4e9a\u6d32\u6700\u4e3a\u51fa\u540d\u7684\u9ad8\u5c42\u5efa\u7b51\u6765\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u5468\u6e38\u3001\u8bb8\u601d\u4ed6\u4eec\u90fd\u5f88\u6709\u8010\u5fc3\u7684\u7b49\u7740\u4ed6\u6292\u53d1\u65e0\u8c13\u7684\u95f2\u60c5\u9038\u81f4\u3002</p><p>\u3000\u3000\u94f6\u4ea8\u8bc1\u5238\u9ad8\u7ea7\u526f\u603b\u88c1\u9646\u5f8b\u9526\u4e0e\u52a9\u624b\u5df2\u7ecf\u5728\u4e2d\u94f6\u5927\u53a6\u7684\u8425\u4e1a\u5385\u524d\u5e7f\u573a\u606d\u5019\u5f20\u606a\u3001\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u4e00\u884c\u4eba\u7684\u5230\u6765\u3002</p><p>\u3000\u3000\u9646\u5f8b\u9526\u4e0e\u5b59\u5c1a\u4e49\u901a\u8fc7\u7535\u8bdd\u770b\u5230\u4ed6\u4eec\u7684\u8f66\u9a76\u5165\u5927\u53a6\u524d\u7684\u4e34\u65f6\u505c\u8f66\u573a\uff0c\u770b\u5230\u5b59\u5c1a\u4e49\u4e00\u884c\u4eba\u4ece\u5546\u52a1\u8f66\u91cc\u94bb\u51fa\u6765\u5374\u6ca1\u6709\u7740\u6025\u671d\u4ed6\u4eec\u8fd9\u8fb9\u8d76\u8fc7\u6765\uff0c\u800c\u662f\u8d70\u5230\u4e2d\u94f6\u5927\u53a6\u897f\u5357\u89d2\u7ec6\u7ec6\u89c2\u8d4f\u4e9b\u8fd9\u680b\u5b8f\u4f1f\u7684\u5efa\u7b51\u3002</p><p>\u3000\u3000\u94f6\u4ea8\u8bc1\u5238\u7684\u5de5\u4f5c\u4eba\u5458\u90fd\u4e0d\u7531\u7684\u6487\u8d77\u5634\u6765\uff0c\u8fd9\u4e2a\u5e74\u4ee3\uff0c\u8fdb\u5165\u8457\u540d\u8bc1\u5238\u516c\u53f8\u5de5\u4f5c\u7684\u9ad8\u7ea7\u804c\u5458\uff0c\u90fd\u53ef\u4ee5\u79f0\u5f97\u4e0a\u8fd9\u4e2a\u65f6\u4ee3\u7684\u9a84\u5b50\uff0c\u8fd8\u771f\u7684\u6709\u4e9b\u4e0d\u628a\u5185\u5730\u6765\u7684\u571f\u8c79\u5b50\u5bcc\u8c6a\u770b\u5728\u773c\u91cc\u3002\u9646\u5f8b\u9526\u5374\u5f88\u6709\u8010\u5fc3\u7684\u7ad9\u5728\u90a3\u91cc\u7b49\u5b59\u5c1a\u4e49\u4e00\u884c\u4eba\u8fc7\u6765\uff0c\u4ed6\u542c\u5b59\u5c1a\u4e49\u8bf4\u8d77\u8fc7\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u521b\u529e\u4eba\u5e74\u8f7b\u5f97\u8fc7\u5206\uff0c\u7b49\u770b\u6e05\u62c4\u7740\u62d0\u62c4\u7f13\u6162\u8d70\u6765\u7684\u5f20\u606a\uff0c\u4ed6\u5fc3\u91cc\u60f3\uff1a\u7684\u786e\u5e74\u8f7b\u5f97\u8fc7\u5206\u800c\u4e14\u98ce\u5ea6\u7fe9\u7fe9\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u5929\uff0c\u4ed6\u5c06\u624b\u5934\u4e0a\u80fd\u6536\u96c6\u5230\u7684\u8d44\u6599\u90fd\u8ba4\u771f\u770b\u8fc7\u3001\u5206\u6790\u8fc7\uff0c\u8fd9\u662f\u4e00\u4e2a\u4e24\u5e74\u95f4\u501f\u52a9\u5185\u5730\u789f\u673a\u5e02\u573a\u5174\u8d77\u800c\u5d1b\u8d77\u7684\u65b0\u5174\u9ad8\u79d1\u6280\u4f01\u4e1a\uff0c\u4e0d\u540c\u4ee5\u5f80\u4ed6\u5370\u8c61\u4e2d\u7684\u5185\u5730\u6c11\u8425\u4f01\u4e1a\uff0c\u5c31\u7b97\u62ff\u4e25\u683c\u773c\u5149\u53bb\u6311\u5254\uff0c\u4e5f\u770b\u4e0d\u51fa\u8fd9\u5bb6\u516c\u53f8\u5728\u7ba1\u7406\u73af\u8282\u4e0a\u5b58\u5728\u53ef\u4ee5\u6307\u8d23\u7684\u660e\u663e\u7f3a\u9677\uff1b\u5f53\u7136\uff0c\u8fd9\u8fd8\u53ea\u662f\u8d44\u6599\u4e0e\u6587\u4ef6\u4e0a\u5f97\u6765\u7684\u611f\u89c2\uff0c\u6240\u4ee5\u9646\u5f8b\u9526\u66f4\u60f3\u5c3d\u5feb\u4eb2\u81ea\u53bb\u6d77\u5dde\u5b9e\u5730\u89c2\u5bdf\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u8fd1\u65e5\uff0c\u79d1\u738b\u7535\u5668\u56e0\u4e3a\u4e0e\u5609\u4fe1\u7535\u5b50\u7684\u5408\u4f5c\u800c\u5728\u9999\u6e2f\u540d\u58f0\u5927\u566a\uff0c\u4f46\u662f\u771f\u6b63\u6bd4\u8f83\u8d77\u6765\uff0c\u79d1\u738b\u7535\u5668\u5dee\u8ddd\u7231\u8fbe\u7535\u5b50\u592a\u591a\uff0c\u4ece\u7231\u8fbe\u7535\u5b50\u63d0\u4ea4\u7684\u5b8c\u6574\u8d44\u6599\u91cc\uff0c\u7231\u8fbe\u7535\u5b50\u5728\u7cfb\u7edf\u63a7\u5236\u82af\u7247\u3001\u7535\u6e90\u63a7\u5236\u82af\u7247\u7b49\u4e13\u5229\u6280\u672f\u65b9\u9762\u6709\u7740\u8d85\u524d\u7684\u51c6\u5907\uff0c\u5f53\u524d\u4e3b\u6d41\u789f\u673a\u5916\u5f62\u8fd8\u6709\u4e9b\u7c97\u7b28\u7684\u60c5\u51b5\u4e0b\uff0c\u7231\u8fbe\u7535\u5b50\u4e0d\u4ec5\u6709\u80fd\u529b\u505a\u51fa\u8d85\u8584\u673a\u4f53\uff0c\u8fd8\u63d0\u51fa\u81ea\u5e26\u6db2\u6676\u5c4f\u7684\u79fb\u52a8VCD\u6982\u5ff5\uff0c\u76f8\u5bf9\u5e94\u7684\u4e13\u5229\u6280\u672f\u7814\u53d1\u4e5f\u8fdb\u884c\u5927\u534a\uff0c\u7279\u522b\u662f\u6700\u8fd1\u4e0e\u7f8e\u56fdTI\u516c\u53f8\u5408\u8d44\u7684\u673a\u82af\u5de5\u5382\u9879\u76ee\uff0c\u66f4\u662f\u6beb\u65e0\u7591\u4e49\u7684\u5960\u5b9a\u7231\u8fbe\u7535\u5b50\u5728\u789f\u673a\u4ea7\u4e1a\u7684\u9f99\u5934\u5730\u4f4d\u3002</p><p>\u3000\u3000\u6709\u7f8e\u56fdTI\u516c\u53f8\u8fd9\u6837\u7684\u6d77\u5916\u7535\u5b50\u5de8\u5934\u53c2\u4e0e\u7684\u5408\u4f5c\uff0c\u66f4\u80fd\u5f97\u5230\u9999\u6e2f\u6295\u8d44\u4eba\u7684\u4fe1\u4efb\uff0c\u8fd9\u5012\u662f\u4e0d\u4e89\u7684\u4e8b\u5b9e\uff0c\u800c\u4e14\u673a\u82af\u5de5\u5382\u6240\u9700\u8981\u7684\u90e8\u5206\u4e13\u5229\u6280\u672f\u7adf\u7136\u6765\u81f3\u53e6\u4e00\u5bb6\u6d77\u5916\u7535\u5b50\u5de8\u5934\u98de\u5229\u6d66\uff0c\u81ea\u7136\u66f4\u8ba9\u4eba\u5bf9\u8fd9\u5bb6\u4f01\u4e1a\u5145\u6ee1\u671f\u5f85\uff0c\u6709\u4ec0\u4e48\u7279\u6b8a\u7684\u9b44\u529b\u80fd\u5c06\u8fd9\u4e24\u5bb6\u58f0\u540d\u663e\u8d6b\u7684\u6d77\u5916\u7535\u5b50\u5de8\u5934\u62c9\u5230\u65d7\u4e0b\u4e00\u8d77\u5408\u4f5c\uff1f</p><p>\u3000\u3000\u4e00\u5bb6\u5185\u5730\u7684\u6c11\u8425\u4f01\u4e1a\u5728\u4e00\u5ea7\u5e76\u4e0d\u5e7f\u4e3a\u4eba\u77e5\u7684\u57ce\u5e02\u6709\u5982\u5947\u8ff9\u822c\u8fc5\u901f\u7684\u5d1b\u8d77\uff0c\u8fd9\u8fc7\u7a0b\u672c\u8eab\u5c31\u8ba9\u4eba\u5145\u6ee1\u597d\u5947\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8fd9\u662f\u5f20\u606a\u4e0e\u9999\u6e2f\u8bc1\u5238\u754c\u4eba\u58eb\u7b2c\u4e00\u6b21\u63a5\u89e6\uff0c\u51e0\u4e4e\u662f\u5e26\u7740\u601c\u60af\u7684\u76ee\u5149\u770b\u7740\u4ed6\u4eec\uff0c\u522b\u770b\u4ed6\u4eec\u73b0\u5728\u8863\u51a0\u9c9c\u695a\uff0c\u4e5f\u5c31\u53ea\u5269\u4e0b\u534a\u5e74\u591a\u7684\u597d\u65f6\u5149\uff0c\u4ece\u6574\u4e2a\u7ecf\u6d4e\u5f62\u52bf\u6765\u770b\uff0c\u4ed6\u4eec\u8981\u6371\u5230\u4e03\u5e74\u4e4b\u540e\u624d\u80fd\u591f\u677e\u53e3\u6c14\u3002</p><p>\u3000\u3000\u521d\u6b65\u5546\u8c08\u7684\u7ed3\u679c\u662f\u7531\u94f6\u4ea8\u8bc1\u5238\u5bf9\u5609\u4fe1\u5b9e\u4e1a\u7684\u6574\u4e2a\u589e\u53d1\u65b9\u6848\u8fdb\u884c\u64cd\u4f5c\u6307\u5bfc\u548c\u4e1a\u52a1\u670d\u52a1\uff0c\u8fd8\u9700\u8981\u4f1a\u8ba1\u5e08\u3001\u8d44\u4ea7\u8bc4\u4f30\u4e8b\u52a1\u6240\u5bf9\u7231\u8fbe\u7535\u5b50\u789f\u673a\u4e1a\u52a1\u8fdb\u884c\u5168\u9762\u8003\u5bdf\u5e76\u7ed9\u51fa\u6743\u5a01\u7684\u8bc4\u4f30\u62a5\u544a\u4e4b\u540e\uff0c\u624d\u8fdb\u4e00\u6b65\u8c08\u5224\u5546\u8bae\u627f\u9500\u65b9\u5f0f\u4e0e\u627f\u9500\u8d39\u7528\u7684\u95ee\u9898\uff0c\u5f53\u7136\uff0c\u94f6\u4ea8\u8bc1\u5238\u4e5f\u63d0\u51fa\u4e3a\u4e86\u8fdb\u4e00\u6b65\u5b8c\u5584\u7231\u8fbe\u7535\u5b50\u7684\u8d22\u52a1\u7ed3\u6784\uff0c\u4f1a\u63a8\u8350\u4e24\u5bb6\u9999\u6e2f\u6295\u8d44\u673a\u6784\u53c2\u80a1\u7231\u8fbe\u7535\u5b50\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u4ece\u4e00\u5f00\u59cb\u6ce8\u518c\u4e3a\u80a1\u4efd\u516c\u53f8\u4ee5\u6765\uff0c\u603b\u80a1\u6570\u53d8\u5316\u4e0d\u5927\uff0c\u5305\u62ec\u5468\u6e38\u3001\u848b\u8587\u3001\u9c81\u5e86\u751f\u7b49\u4eba\u5728\u5185\u7684\u80a1\u6743\u6fc0\u52b1\uff0c\u603b\u80a1\u6570\u7531\u4e00\u5343\u4e07\u80a1\u624d\u589e\u52a0\u5230\u4e00\u5343\u96f6\u56db\u5341\u4e07\u80a1\u5de6\u53f3\uff0c\u6839\u636e\u7231\u8fbe\u7535\u5b50\u4e5d\u516d\u5e74\u7684\u8d22\u52a1\u6c34\u5e73\uff0c\u6bcf\u80a1\u51c0\u8d44\u4ea7\u8fbe\u5230\u56db\u5341\u5143\uff0c\u6bcf\u80a1\u76c8\u5229\u66f4\u662f\u8fbe\u5230\u767e\u5143\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f88\u6e05\u695a\u8981\u60f3\u589e\u53d1\u65b9\u6848\u987a\u5229\u5b9e\u65bd\uff0c\u5411\u53d1\u884c\u5238\u5546\u8ba9\u51fa\u66f4\u591a\u7684\u5229\u76ca\u90a3\u662f\u5fc5\u7136\u7684\uff0c\u8fd9\u51e0\u4e4e\u662f\u56fd\u5185\u4f01\u4e1a\u8d70\u5411\u6d77\u5916\u8d44\u672c\u5e02\u573a\u5fc5\u987b\u7ed9\u5265\u524a\u7684\u4e00\u5757\uff0c\u94f6\u4ea8\u8bc1\u5238\u63d0\u8350\u7684\u6240\u8c13\u7684\u4e24\u5bb6\u9999\u6e2f\u6295\u8d44\u673a\u6784\uff0c\u81ea\u7136\u5c31\u662f\u94f6\u4ea8\u8bc1\u5238\u7684\u5229\u76ca\u5173\u8054\u673a\u6784\uff0c\u5f20\u606a\u540c\u610f\u4ee5\u6bcf\u80a1\u56db\u5341\u7f8e\u5143\u7684\u4ef7\u683c\u5411\u8fd9\u4e24\u5bb6\u673a\u6784\u53d1\u884c\u4e0d\u8d85\u8fc7\u4e00\u767e\u4e07\u80a1\u7684\u80a1\u7968\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u975e\u4e0a\u5e02\u80a1\u4efd\u516c\u53f8\u5411\u6d77\u5916\u6295\u8d44\u4eba\u6216\u673a\u6784\u5b9a\u5411\u589e\u53d1\u80a1\u7968\u7684\u884c\u4e3a\u5c31\u662f\u5bfb\u5e38\u610f\u4e49\u4e0a\u7684\u6d77\u5916\u79c1\u52df\uff0c\u6839\u636e\u9999\u6e2f\u6cd5\u5f8b\u5e08\u5bf9\u80a1\u4efd\u516c\u53f8\u7684\u7ea6\u5b9a\uff0c\u6d77\u5916\u6295\u8d44\u673a\u6784\u53c2\u4e0e\u5b9a\u5411\u589e\u53d1\u7684\u80a1\u7968\u53ef\u4ee5\u5728\u516d\u4e2a\u6708\u4e4b\u540e\u8f6c\u8ba9\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u516d\u4e2a\u6708\u4e4b\u540e\uff0c\u589e\u53d1\u65b9\u6848\u987a\u5229\u5b9e\u65bd\uff0c\u8fd9\u90e8\u5206\u80a1\u7968\u8f6c\u6362\u6210\u5609\u4fe1\u5b9e\u4e1a\u516c\u5f00\u53d1\u884c\u7684\u80a1\u7968\uff0c\u6295\u8d44\u673a\u6784\u5c31\u53ef\u4ee5\u5728\u9999\u6e2f\u80a1\u5e02\u51cf\u6301\u83b7\u5229\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u90e8\u5206\u7684\u83b7\u5229\u901a\u5e38\u662f\u53d1\u884c\u8d39\u7528\u7684\u6570\u500d\u3002</p><p>\u3000\u3000\u8fd9\u5bf9\u7231\u8fbe\u7535\u5b50\u4e5f\u662f\u6709\u597d\u5904\u7684\uff0c\u6709\u516c\u540c\u5229\u76ca\u9a71\u52a8\uff0c\u94f6\u4ea8\u8bc1\u5238\u81ea\u7136\u4f1a\u66f4\u79ef\u6781\u7684\u63a8\u52a8\u589e\u53d1\u65b9\u6848\u7684\u5b9e\u884c\uff1b\u5b9a\u5411\u589e\u53d1\u4e00\u767e\u4e07\u80a1\uff0c\u5bf9\u5176\u4f59\u80a1\u4efd\u7684\u76c8\u5229\u644a\u8584\u4f5c\u7528\u5e76\u4e0d\u660e\u663e\uff0c\u7231\u8fbe\u7535\u5b50\u80fd\u83b7\u5f97\u56db\u5343\u4e07\u7f8e\u91d1\u7684\u6d41\u52a8\u8d44\u91d1\uff0c\u5219\u80fd\u5f7b\u5e95\u89e3\u51b3\u7231\u8fbe\u7535\u5b50\u7684\u8d44\u91d1\u95ee\u9898\uff0c\u5c06\u6bcf\u80a1\u51c0\u8d44\u4ea7\u63d0\u9ad8\u5230\u4e03\u5341\u5143\u5de6\u53f3\u3002</p><p>\u3000\u3000\u4e5f\u7531\u4e8e\u6709\u56fd\u9645\u673a\u6784\u7684\u53c2\u80a1\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u4ef7\u503c\u81ea\u7136\u4e5f\u5c31\u6709\u53c2\u8003\u6807\u6746\uff0c\u6263\u9664\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u4e0e\u6cf0\u5fd2\u5149\u7535\u6280\u672f\u7814\u7a76\u9662\u4ee5\u53ca\u7231\u8fbe\u7535\u5b50\u5546\u6807\u90e8\u5206\u975e\u89c6\u542c\u789f\u673a\u7c7b\u7684\u65e0\u5f62\u8d44\u4ea7\uff0c\u7231\u8fbe\u7535\u5b50\u2014\u2014\u5305\u62ec\u7535\u5b50\u5143\u5668\u4ef6\u5b50\u516c\u53f8\u3001\u96c6\u56e2\u516c\u53f8\u3001\u7ec4\u88c5\u5de5\u5382\u3001\u7cbe\u51c6\u6a21\u5177\u5de5\u5382\u3001\u673a\u82af\u5de5\u5382\u5408\u8d44\u9879\u76ee\u7b49\u5b9e\u4f53\u8d44\u4ea7\uff0c\u4e5f\u5305\u62ec\u5bf9\u96f6\u90e8\u4ef6\u4f9b\u5e94\u4f01\u4e1a\u6301\u80a1\u7684\u80a1\u6743\u8d44\u4ea7\uff0c\u4e5f\u5305\u62ec\u7231\u8fbe\u7535\u5b50\u5546\u6807\u89c6\u542c\u789f\u673a\u7c7b\u7684\u6c38\u4e45\u6388\u6743\u8bb8\u53ef\u3001\u5404\u7c7b\u89c6\u542c\u4e13\u5229\u6280\u672f\u5305\u62ec\u7cfb\u7edf\u63a7\u5236\u82af\u7247\u6280\u672f\u3001\u89c6\u9891\u7f16\u7801\u82af\u7247\u6280\u672f\u3001\u97f3\u9891\u4fe1\u53f7\u5904\u7406\u82af\u7247\u6280\u672f\u3001\u97f3\u54cd\u6548\u679c\u82af\u7247\u6280\u672f\u3001\u6570\u5b57\u4fe1\u53f7\u5904\u7406\u82af\u7247\u6280\u672f\u4ee5\u53ca\u5b8c\u6574\u7684\u5355\u789f\u3001\u4e09\u789f\u673a\u82af\u5236\u9020\u4e13\u5229\u6280\u672f\u7b49\u65e0\u5f62\u8d44\u4ea7\u2014\u2014\u7684\u8d44\u4ea7\u4ef7\u503c\u8fbe\u5230\u56db\u4ebf\u7f8e\u91d1\u3002</p><p>\u3000\u3000\u81ea\u7136\uff0c\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u901a\u8fc7\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u5e02\u503c\u8981\u8fdc\u8fdc\u8d85\u8fc7\u8fd9\u4e2a\u6570\uff0c\u94f6\u4ea8\u8bc1\u5238\u63a8\u8350\u7684\u6295\u8d44\u673a\u6784\u6b64\u65f6\u82b1\u8d39\u5de8\u8d44\u53c2\u80a1\uff0c\u5c31\u662f\u5e0c\u671b\u80fd\u5728\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u5b9e\u65bd\u4e4b\u540e\u83b7\u5f97\u81f3\u5c11\u7ffb\u500d\u7684\u6536\u76ca\u3002</p><p>\u3000\u3000\u5f20\u606a\u6b64\u524d\u5df2\u7ecf\u8ba9\u56fd\u5185\u4f1a\u8ba1\u5e08\u4e8b\u52a1\u505a\u51fa\u7684\u5b8c\u6574\u8d22\u52a1\u62a5\u544a\uff0c\u5e76\u5c06\u8d8a\u79c0\uff08\u516c\u53f8\uff09\u5bf9\u5609\u4fe1\u5b9e\u4e1a\u768430\uff05\u3001\u5e02\u503c\u8fd1\u4e09\u4ebf\u6e2f\u5143\u7684\u6301\u80a1\u51b0\u7ed3\u8d77\u6765\u7ed9\u8fd9\u4efd\u8d22\u52a1\u62a5\u544a\u505a\u8bda\u4fe1\u62c5\u4fdd\uff0c\u4e8e12\u670830\u65e5\uff0c\u5c31\u4e0e\u94f6\u4ea8\u8bc1\u5238\u63a8\u8350\u7684\u4e24\u5bb6\u6295\u8d44\u673a\u6784\u7b7e\u7f72\u52df\u8d44\u534f\u8bae\u3002</p><p>\u3000\u3000\u5982\u6b64\u4ece\u6743\uff0c\u4e3b\u8981\u662f\u5f20\u606a\u5f3a\u70c8\u8981\u6c42\u589e\u53d1\u65b9\u6848\u5728\u4e5d\u4e03\u5e74\u516d\u6708\u4efd\u4e4b\u524d\u5b8c\u6210\uff0c\u800c\u6839\u636e\u9999\u6e2f\u5bf9\u53c2\u80a1\u516c\u53f8\u5fc5\u987b\u5728\u6301\u80a1\u516d\u4e2a\u6708\u4e4b\u540e\u624d\u80fd\u8f6c\u8ba9\u7684\u6cd5\u5f8b\u89c4\u5b9a\uff0c\u6295\u8d44\u673a\u6784\u82e5\u60f3\u5728\u5609\u4fe1\u5b9e\u4e1a\u516c\u5f00\u589e\u53d1\u80a1\u7968\u65f6\u5c06\u6240\u6301\u80a1\u4efd\u629b\u552e\u83b7\u5229\uff0c\u5c31\u5fc5\u987b\u7acb\u5373\u8ddf\u7231\u8fbe\u7535\u5b50\u7b7e\u7f72\u53c2\u80a1\u534f\u8bae\u3002\u6240\u5e78\u5f20\u606a\u80fd\u63d0\u4f9b\u8db3\u989d\u4efd\u91cf\u7684\u4fe1\u7528\u62c5\u4fdd\u8d44\u4ea7\uff0c\u4e0d\u7136\u5c31\u8fd9\u4e00\u6761\u53cc\u65b9\u5c31\u5f88\u96be\u8fbe\u6210\u4e00\u81f4\u610f\u89c1\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8981\u5b8c\u6210\u6574\u4e2a\u7684\u589e\u53d1\u65b9\u6848\uff0c\u8fd8\u9700\u8981\u94f6\u4ea8\u8bc1\u5238\u63a8\u8350\u3001\u5609\u4fe1\u5b9e\u4e1a\u4e0e\u7231\u8fbe\u7535\u5b50\u5171\u540c\u9009\u5b9a\u7684\u4f1a\u8ba1\u5e08\u4e8b\u52a1\u6240\u3001\u8d44\u4ea7\u8bc4\u4f30\u4e8b\u52a1\u6240\u3001\u5f8b\u5e08\u4e8b\u52a1\u6240\u5bf9\u7231\u8fbe\u7535\u5b50\u51fa\u5177\u5b8c\u6574\u7684\u8d22\u52a1\u3001\u8d44\u4ea7\u8bc4\u4f30\u7b49\u65b9\u9762\u7684\u62a5\u544a\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u9700\u8981\u6709\u789f\u673a\u89c6\u542c\u4e13\u5229\u6280\u672f\u4ef7\u503c\u65b9\u9762\u7684\u4e13\u4e1a\u8bc4\u4f30\u62a5\u544a\uff0c\u603b\u4e4b\u662f\u4e00\u9879\u5f88\u4e13\u4e1a\u5e76\u4e14\u7e41\u7410\u7684\u6d3b\uff0c\u867d\u7136\u5f20\u606a\u4e4b\u524d\u505a\u4e86\u8bb8\u591a\u5de5\u4f5c\uff0c\u4f46\u662f\u5e76\u4e0d\u80fd\u4f7f\u6574\u4e2a\u7a0b\u5e8f\u7684\u73af\u8282\u6709\u6240\u7f29\u51cf\uff0c\u8981\u8d76\u5230\u4e5d\u4e03\u5e74\u7684\u4e94\u516d\u6708\u95f4\u5b9e\u65bd\u8fd9\u4e00\u65b9\u6848\uff0c\u65f6\u95f4\u8fd8\u662f\u5f88\u7d27\u8feb\u7684\u3002</p><p>\u3000\u30001\u67083\u65e5\uff0c\u4f5c\u4e3a\u53c2\u4e0e\u6b64\u6b21\u5b9a\u5411\u589e\u53d1\u6295\u8d44\u7684\u3001\u5728\u82f1\u56fd\u4f26\u6566\u4ea4\u6613\u6240\u6302\u724c\u4e0a\u5e02\u7684\u9999\u6e2f\u516c\u53f8\u56fd\u88d5\u6295\u8d44\u53d1\u5e03\u516c\u544a\u5ba3\u79f0\u76ee\u524d\u5df2\u7ecf\u7b7e\u7f72\u534f\u8bae\u5411\u56fd\u5185\u789f\u673a\u5236\u9020\u9f99\u5934\u4f01\u4e1a\u7231\u8fbe\u7535\u5b50\u6ce8\u8d44\u4e24\u5343\u4e07\u7f8e\u91d1\u4ee5\u6362\u53d6\u4e94\u5341\u4e07\u80a1\u7684\u80a1\u7968\u3002</p><p>\u3000\u3000\u8fd9\u672c\u662f\u5f88\u4e0d\u8d77\u773c\u7684\u4e00\u5219\u6d88\u606f\uff0c\u4f46\u662f\u56fd\u88d5\u516c\u53f8\u7684\u63a7\u80a1\u6bcd\u516c\u53f8\u56fd\u88d5\u96c6\u56e2\u6709\u5728\u9999\u6e2f\u8054\u5408\u4ea4\u6613\u6240\u6302\u724c\u4e0a\u5e02\uff0c\u56fd\u88d5\u96c6\u56e2\u4e5f\u540c\u65f6\u53d1\u5e03\u516c\u544a\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5f53\u65f6\u6b63\u518d\u6b21\u4ece\u6d77\u5dde\u98de\u5f80\u9999\u6e2f\u9080\u8bf7\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u5230\u6d77\u5dde\u53c2\u52a0\u79d1\u738b\u7814\u53d1\u4e2d\u5fc3\u7684\u5960\u57fa\u4eea\u5f0f\uff0c\u542c\u5230\u8fd9\u5219\u6d88\u606f\u5185\u5fc3\u767e\u611f\u4ea4\u96c6\uff0c\u4ed6\u4eec\u4e0d\u60dc\u51fa\u5356\u76df\u53cb\u9526\u57ce\u96c6\u56e2\u7684\u5229\u76ca\uff0c\u79d1\u738b\u4e4b\u540e\u518d\u5411\u5609\u4fe1\u7535\u5b50\u8f6c\u8ba910\uff05\u7684\u80a1\u4efd\u4e5f\u624d\u83b7\u5f97\u4e0d\u5230\u4e94\u5343\u4e07\u6e2f\u5e01\u7684\u6ce8\u8d44\uff0c\u800c\u7231\u8fbe\u7535\u5b50\u53ea\u8f6c\u8ba9\u4e0d\u52305\uff05\u7684\u80a1\u4efd\u5c31\u83b7\u5f97\u4e24\u5343\u4e07\u7f8e\u91d1\u7684\u6ce8\u8d44\uff0c\u4e5f\u5c31\u662f\u8fd9\u5bb6\u82f1\u56fd\u4e0a\u5e02\u7684\u9999\u6e2f\u516c\u53f8\u56fd\u88d5\u6295\u8d44\u5bf9\u7231\u8fbe\u7535\u5b50\u7ed9\u51fa\u56db\u4ebf\u7f8e\u91d1\u3001\u4e09\u5341\u4e8c\u4ebf\u4eba\u6c11\u5e01\u7684\u4f30\u503c\u3002</p><p>\u3000\u3000\u867d\u8bf4\u7231\u8fbe\u7535\u5b50\u5728\u4e5d\u516d\u5e74\u83b7\u5f97\u5de8\u989d\u7684\u76c8\u5229\uff0c\u4f46\u662f\u9526\u6e56\u3001\u6d77\u88d5\u5c06\u5927\u90e8\u4efd\u76c8\u5229\u90fd\u8f6c\u79fb\u51fa\u53bb\uff0c\u8c22\u5251\u5357\u4f30\u8ba1\u7231\u8fbe\u7535\u5b50\u7684\u51c0\u8d44\u4ea7\u5728\u4e09\u5230\u56db\u4ebf\u4e4b\u95f4\uff0c\u800c\u4ed6\u4eec\u5404\u5bb6\u80a1\u4e1c\u5f80\u79d1\u738b\u91cc\u6295\u5165\u7684\u771f\u91d1\u767d\u94f6\u4e5f\u8d85\u8fc7\u4e09\u4e2a\u4ebf\uff0c\u8fd9\u5de8\u5927\u7684\u4f30\u503c\u843d\u5dee\uff0c\u79d1\u738b\u751a\u81f3\u4e0d\u53ca\u7231\u8fbe\u7535\u5b50\u7684\u516d\u5206\u4e4b\u4e00\uff0c\u96be\u9053\u4ec5\u4ec5\u662f\u56e0\u4e3a\u843d\u540e\u4e00\u5e74\u9020\u6210\u7684\u5417\uff1f</p><p>\u3000\u3000\u8c22\u5251\u5357\u5230\u9999\u6e2f\u4e4b\u540e\u7684\u5fc3\u60c5\u771f\u662f\u7cdf\u7cd5\u900f\u4e86\u3002</p><p>\u3000\u3000\u4ed6\u4e4b\u524d\u5bf9\u4e0e\u5609\u4fe1\u7535\u5b50\u7684\u5408\u4f5c\u5145\u6ee1\u4fe1\u5fc3\uff0c\u4ed6\u4e4b\u524d\u8ba4\u4e3a\u53ea\u8981\u83b7\u5f97\u5609\u4fe1\u7535\u5b50\u7684\u6280\u672f\u3001\u4eba\u529b\u8d44\u6e90\u3001\u8d44\u91d1\u7b49\u5404\u65b9\u9762\u7684\u63f4\u52a9\uff0c\u79d1\u738b\u80fd\u5f88\u5feb\u7684\u7f29\u77ed\u4e0e\u7231\u8fbe\u7535\u5b50\u4e4b\u95f4\u7684\u5dee\u8ddd\uff0c\u800c\u4e14\u4e5d\u4e03\u5e74\u5e95\uff0c\u79d1\u738b\u4f5c\u4e3a\u592e\u89c6\u5e7f\u544a\u6807\u738b\u5c31\u5360\u636e\u5e02\u573a\u4fe1\u606f\u8d44\u6e90\u4e0a\u7684\u660e\u663e\u4f18\u52bf\u3002\u53ea\u662f\u79d1\u738b\u5728\u52aa\u529b\uff0c\u7231\u8fbe\u7535\u5b50\u4e5f\u6ca1\u6709\u505c\u4e0b\u811a\u6b65\uff0c\u79d1\u738b\u5f15\u5165\u4e94\u5343\u5143\u6e2f\u5143\u7684\u6ce8\u8d44\uff0c\u7231\u8fbe\u7535\u5b50\u7d27\u8ddf\u7740\u5c31\u5f15\u5165\u4e24\u5343\u4e07\u7f8e\u91d1\u7684\u6ce8\u8d44\u3002</p><p>\u3000\u3000\u5f88\u53ef\u60dc\uff0c\u8c22\u5251\u5357\u665a\u4e0a\u5c31\u63a5\u5230\u56fd\u5185\u5f20\u6653\u5065\u7684\u7535\u8bdd\uff0c\u7231\u8fbe\u7535\u5b50\u5411\u7701\u5916\u6c47\u7ba1\u7406\u5206\u5c40\u53ca\u5e02\u5de5\u5546\u5c40\u7b49\u90e8\u95e8\u6c47\u62a5\u7684\u91d1\u989d\u4e0d\u6b62\u4e24\u5343\u4e07\u7f8e\u91d1\uff0c\u800c\u662f\u7ffb\u4e86\u4e00\u500d\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u30001\u67085\u65e5\uff0c\u5f20\u606a\u5728\u845b\u5bb6\u6350\u8d60\u7684\u9999\u6e2f\u957f\u8475\u533b\u9662\u62c6\u9664\u53f3\u811a\u77f3\u818f\uff0c\u6709\u4e00\u4e2a\u6708\u6ca1\u6709\u52a8\u5f39\u53f3\u811a\uff0c\u808c\u8089\u6709\u4e9b\u840e\u7f29\uff0c\u4e24\u53ea\u811a\u5e76\u5728\u4e00\u8d77\uff0c\u6709\u660e\u663e\u7684\u533a\u522b\uff0c\u5b59\u5c1a\u4e49\u8fd8\u63a8\u8350\u957f\u8475\u533b\u9662\u7684\u5eb7\u590d\u533b\u751f\u7ed9\u5f20\u606a\uff0c\u5373\u4f7f\u5f20\u606a\u56de\u6d77\u5dde\uff0c\u5eb7\u590d\u533b\u751f\u4e5f\u53ef\u4ee5\u968f\u884c\u6307\u5bfc\u4ed6\u505a\u5eb7\u590d\u8fd0\u52a8\u3002</p><p>\u3000\u3000\u5728\u9999\u6e2f\u98de\u5f80\u5efa\u90ba\u7684\u98de\u673a\u4e0a\uff0c\u5b59\u5c1a\u4e49\u8fd8\u8bf4\u8d77\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u5012\u662f\u5750\u4e0a\u5546\u52a1\u8231\u4e86\uff0c\u9664\u4e86\u4ed6\u4eec\u4e00\u884c\u4eba\u4e4b\u5916\uff0c\u8fd8\u6709\u94f6\u4ea8\u8bc1\u5238\u7684\u9ad8\u7ea7\u526f\u603b\u88c1\u9646\u5f8b\u9526\u4e00\u884c\u4eba\uff0c\u9664\u4e86\u5f20\u606a\u3001\u5085\u4fca\u3001\u9a6c\u6d77\u9f99\uff0c\u5176\u4ed6\u4eba\u90fd\u662f\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u5c0f\u7ec4\u7684\u6210\u5458\uff0c\u6b64\u5916\u8fd8\u8981\u52a0\u4e0a\u8fd8\u5728\u6d77\u5dde\u7684\u82cf\u6d25\u4e1c\u3001\u848b\u8587\u3001\u5468\u4e00\u5e73\u7b49\u4eba\u3002</p><p>\u3000\u3000\u4ed6\u4eec\u5c31\u5c06\u8fd9\u73ed\u98de\u5f80\u5efa\u90ba\u822a\u73ed\u7684\u5546\u52a1\u8231\u7ed9\u5305\u4e86\u4e0b\u6765\uff0c\u5012\u4e0d\u662f\u5962\u4f88\uff0c\u5173\u952e\u4ed6\u4eec\u4e00\u884c\u5750\u4e0b\u6765\u4e5f\u591a\u4e0d\u4e86\u51e0\u5f20\u4f4d\u7f6e\uff0c\u804a\u5929\u4e5f\u4e0d\u6015\u522b\u4eba\u542c\u5230\u4ec0\u4e48\u5546\u4e1a\u673a\u5bc6\u3002</p><p>\u3000\u3000\u9646\u5f8b\u9526\u542c\u5230\u4ed6\u4eec\u7684\u8c08\u8bdd\uff0c\u5f88\u5947\u602a\u7684\u8bf4\u9053\uff1a\u201c\u606a\u5c11\u6ca1\u6709\u4e13\u95e8\u7684\u5eb7\u590d\u533b\u5e08\uff0c\u6211\u53ef\u80fd\u5e2e\u606a\u5c11\u63a8\u8350\u2026\u2026\u201d\u77ed\u77ed\u5341\u591a\u5929\u7684\u63a5\u89e6\uff0c\u4ed6\u4e5f\u5f88\u4e3a\u5f20\u606a\u672c\u4eba\u7684\u6c14\u5ea6\u3001\u5546\u4e1a\u4e0a\u7684\u5929\u8d4b\u4ee5\u53ca\u8868\u73b0\u51fa\u6765\u5bf9\u91d1\u878d\u3001\u5546\u52a1\u77e5\u8bc6\u3001\u8c08\u5224\u6280\u5de7\u7684\u7cbe\u901a\u76f8\u5f53\u7684\u94a6\u4f69\uff0c\u76f8\u4fe1\u6709\u4e9b\u4eba\u751f\u6765\u5c31\u662f\u8ba9\u788c\u788c\u51e1\u4eba\u671b\u5c18\u83ab\u53ca\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u62d2\u7edd\u8bf4\uff1a\u201c\u6211\u6ca1\u6709\u90a3\u4e48\u5a07\u8d35\uff0c\u6d77\u5dde\u4e5f\u4e0d\u662f\u6ca1\u6709\u5eb7\u590d\u533b\u751f\uff0c\u6211\u770b\u8fd9\u811a\u6162\u6162\u591a\u8d70\u51e0\u5929\u8def\u5c31\u6062\u590d\u8fc7\u6765\uff0c\u53c8\u4e0d\u662f\u4ee5\u540e\u7684\u4eba\u751f\u505a\u77ed\u8dd1\u8fd0\u52a8\u5458\uff0c\u4e00\u70b9\u5dee\u6c60\u90fd\u51fa\u4e0d\u5f97\u2026\u2026\u201d\u8f6c\u8eab\u5374\u8ddf\u5b59\u5c1a\u4e49\u8bf4\u9053\uff0c\u201c\u9999\u6e2f\u7684\u4eba\u529b\u8d44\u6e90\u6210\u672c\u6700\u9ad8\u8d35\u4e86\uff0c\u5927\u5b66\u6bd5\u4e1a\u751f\u8d77\u85aa\u5c31\u8981\u4e00\u4e07\u6e2f\u5e01\uff0c\u8bf7\u4e2a\u4e13\u95e8\u7684\u5eb7\u590d\u533b\u751f\uff0c\u52a0\u4e0a\u5176\u4ed6\u8d39\u7528\uff0c\u56de\u5230\u6d77\u5dde\uff0c\u5c31\u8db3\u591f\u8bf7\u4e8c\u5341\u540d\u9ad8\u7ea7\u533b\u5e08\u56f4\u7740\u6211\u8f6c\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u6e38\u5728\u65c1\u8fb9\u7b11\u7740\u8bf4\uff1a\u201c\u56de\u5230\u6d77\u5dde\uff0c\u8bf7\u4e24\u4e2a\u5e74\u8f7b\u8c8c\u7f8e\u7684\u62a4\u58eb\u966a\u7740\u8dd1\u8dd1\u8d70\u4e5f\u5c31\u591f\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u5012\u662f\u4e0d\u9519\u7684\u5efa\u8bae\u3002\u201d\u5f20\u606a\u9644\u548c\u7740\u8bf4\u7b11\u3002</p><p>\u3000\u3000\u8bb8\u601d\u7f8e\u7738\u5fae\u7741\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u5012\u4e5f\u60f3\u770b\u770b\u4f60\u56de\u6d77\u5dde\u627e\u4e24\u4e2a\u7f8e\u5973\u62a4\u58eb\u966a\u7740\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e5f\u5c31\u5728\u8fd9\u91cc\u60f3\u60f3\u800c\u5df2\u2026\u2026\u201d\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u8bb8\u601d\u4e0d\u4f1a\u5728\u6d77\u5dde\u6ede\u7559\u592a\u957f\u65f6\u95f4\uff0c\u4e0d\u8fc7\u60f3\u5230\u5510\u5a67\u3001\u665a\u6674\u4f1a\u966a\u81ea\u5df1\u505a\u5eb7\u590d\u8fd0\u52a8\uff0c\u5012\u4e0d\u6bd4\u627e\u4e24\u7f8e\u5973\u62a4\u58eb\u7a0d\u5dee\u3002</p><p>\u3000\u3000\u9646\u5f8b\u9526\u5df2\u7ecf\u4e60\u60ef\u5f20\u606a\u4e0d\u4f1a\u592a\u4e25\u8083\u7684\u8bed\u6c14\uff0c\u65e0\u5948\u7684\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u73b0\u5728\u603b\u53ef\u80fd\u4eab\u53d7\u66f4\u7cbe\u81f4\u7684\u751f\u6d3b\uff0c\u5468\u603b\u73b0\u5728\u4e5f\u7b97\u8eab\u5bb6\u6570\u5343\u4e07\u4e86\uff0c\u6bd4\u6211\u53d1\u8fbe\u2026\u2026\u201d\u9646\u5f8b\u9526\u662f\u516b\u5341\u5e74\u4ee3\u521d\u671f\u5c31\u5230\u9999\u6e2f\u7684\u5e7f\u5dde\u4eba\uff0c\u5230\u9999\u6e2f\u5341\u591a\u5e74\uff0c\u5750\u4e0a\u9ad8\u7ea7\u804c\u4e1a\u7ecf\u7406\u4eba\u7684\u4f4d\u5b50\u4e5f\u6709\u4e94\u516d\u5e74\u7684\u65f6\u95f4\uff0c\u6bcf\u5e74\u8fd1\u5343\u4e07\u6e2f\u5143\u7684\u6536\u5165\uff0c\u8ba9\u4ed6\u5df2\u7ecf\u4e60\u60ef\u5f88\u4eab\u53d7\u9999\u6e2f\u5bcc\u8c6a\u7684\u7cbe\u81f4\u751f\u6d3b\uff0c\u4e0d\u8fc7\u4ed6\u4e5f\u6e05\u695a\u4e0b\u4e00\u4e2a\u5341\u5e74\u5c06\u662f\u5185\u5730\u9020\u5bcc\u7684\u9ec4\u91d1\u65f6\u4ee3\uff0c\u50cf\u5468\u6e38\u8fd9\u4e9b\u5c31\u7b97\u5728\u9999\u6e2f\u5546\u754c\u90fd\u8981\u7b97\u62d4\u5c16\u4eba\u624d\u7684\u7cbe\u82f1\uff0c\u5173\u952e\u4ed6\u4eec\u53c8\u6709\u8db3\u591f\u7684\u8fdb\u53d6\u4e0e\u5403\u82e6\u8010\u52b3\u7684\u7cbe\u795e\uff0c\u83b7\u5f97\u8d22\u5bcc\u53ea\u662f\u65f6\u95f4\u95ee\u9898\uff0c\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u8fd9\u4e00\u6b21\uff0c\u7231\u8fbe\u7535\u5b50\u7ed9\u6d77\u5dde\u50ac\u751f\u51fa\u4e00\u6279\u7684\u5bcc\u7fc1\u554a\uff0c\u4eab\u53d7\u7cbe\u81f4\u751f\u6d3b\u4e5f\u662f\u5e94\u8be5\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u4e5d\u4e94\u5e74\u5e95\u5b9e\u65bd\u7684\u80a1\u6743\u6fc0\u52b1\u8ba1\u5212\u5b9e\u65bd\u540e\uff0c\u5468\u6e38\u83b7\u5f97\u7231\u8fbe\u7535\u5b50\u5341\u4e07\u80a1\u7684\u5956\u52b1\uff0c\u8fd9\u5341\u4e07\u80a1\u80a1\u7968\u4e5d\u516d\u5e74\u5206\u7ea2\u5c31\u5c06\u8fbe\u5230\u516d\u767e\u4e07\u5143\uff0c\u8981\u62ff\u8fd9\u6b21\u79c1\u52df\u7684\u80a1\u4ef7\u6765\u53c2\u8003\uff0c\u5468\u6e38\u540d\u4e0b\u8fd9\u7231\u8fbe\u7535\u5b50\u8fd9\u5341\u4e07\u80a1\u80a1\u7968\u7684\u4ef7\u503c\u5c31\u8fbe\u5230\u56db\u767e\u4e07\u7f8e\u5143\uff0c\u8521\u7eef\u5a1f\u3001\u848b\u8587\u3001\u9c81\u5e86\u751f\u4ee5\u53ca\u5143\u5668\u4ef6\u5206\u516c\u53f8\u7684\u603b\u7ecf\u7406\u6768\u4e4b\u6d66\u3001\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u5bf9\u89c6\u542c\u6280\u672f\u7814\u53d1\u505a\u51fa\u5353\u8d8a\u8d21\u732e\u7684\u4e24\u540d\u4e3b\u4efb\u5de5\u7a0b\u5e08\u7b49\u7b49\u90fd\u5c06\u987a\u5229\u8de8\u5165\u5343\u4e07\u5bcc\u7fc1\u7684\u884c\u5217\uff1b\u4e5d\u516d\u5e74\u5e95\uff0c\u4e5f\u5c31\u662f\u5f20\u606a\u8fd9\u6b21\u56de\u6d77\u5dde\u5c31\u5c06\u7b7e\u7f72\u7684\u80a1\u6743\u6fc0\u52b1\u65b9\u6848\uff0c\u4f1a\u5411\u5305\u62ec\u6768\u4e91\u3001\u5468\u4e00\u5e73\u7b49\u4e00\u7ea7\u90e8\u95e8\u4e3b\u7ba1\u5728\u5185\u7684\u7ba1\u7406\u4eba\u5458\u5956\u52b1\u6570\u767e\u5230\u4e00\u4e07\u80a1\u4e0d\u7b49\u7684\u80a1\u7968\uff0c\u53c8\u80fd\u50ac\u751f\u51fa\u4e00\u6279\u767e\u4e07\u5bcc\u7fc1\u6765\u3002\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u624b\u91cc\u5171\u6301\u6709\u4e94\u5341\u4e07\u80a1\uff0c\u4e0d\u8ba1\u4e5d\u516d\u5e74\u7684\u5206\u7ea2\uff0c\u5404\u4eba\u540d\u4e0b\u7684\u8d44\u4ea7\u5c31\u8fbe\u5230\u4e24\u5343\u4e07\u7f8e\u5143\u3002\u8fd9\u4e9b\u8d22\u52a1\u4e0a\u7684\u7ec6\u8282\uff0c\u81ea\u7136\u8981\u5411\u589e\u53d1\u5e76\u8d2d\u65b9\u6848\u6267\u884c\u5c0f\u7ec4\u7684\u6210\u5458\u516c\u5f00\uff0c\u5c0f\u7ec4\u65f6\u4e00\u534a\u4eba\u662f\u9999\u6e2f\u4f1a\u8ba1\u5e08\u4e8b\u52a1\u6240\u3001\u5f8b\u5e08\u4e8b\u52a1\u6240\u3001\u8d44\u4ea7\u8bc4\u4f30\u4e8b\u52a1\u6240\u7684\u9ad8\u7ea7\u804c\u5458\uff0c\u4ed6\u4eec\u5728\u9999\u6e2f\u4e5f\u7b97\u662f\u62ff\u9ad8\u85aa\u7684\u91d1\u85aa\u4eba\u5458\uff0c\u4e5f\u7fa1\u6155\u7231\u8fbe\u7535\u5b50\u90a3\u4e48\u591a\u80fd\u62ff\u5230\u6fc0\u52b1\u80a1\u6743\u7684\u7ba1\u7406\u4eba\u5458\uff0c\u5fc3\u60f3\u8fd9\u4e9b\u4eba\u5728\u4e00\u4e2a\u751f\u6d3b\u6c34\u5e73\u6bd4\u9999\u6e2f\u5dee\u5f88\u591a\u7684\u5730\u65b9\u5374\u62ff\u5230\u4e0e\u4ed6\u4eec\u76f8\u5f53\uff0c\u751a\u81f3\u8981\u8fdc\u8fdc\u8d85\u8fc7\u4ed6\u4eec\u7684\u85aa\u6c34\uff0c\u81ea\u7136\u80fd\u8fc7\u4e0a\u66f4\u4f53\u9762\u3001\u66f4\u6709\u5c0a\u4e25\u7684\u7cbe\u81f4\u751f\u6d3b\u3002</p><p>\u3000\u3000\u201c\u592a\u7cbe\u81f4\u7684\u751f\u6d3b\uff0c\u53ea\u662f\u5c06\u81ea\u5df1\u7684\u675f\u7f1a\u5728\u4e00\u4e2a\u5f88\u7a84\u7684\u5708\u5b50\u91cc\uff0c\u201d\u5f20\u606a\u611f\u6168\u4e86\u4e00\u4e0b\uff0c\u5012\u4e5f\u4e0d\u4f1a\u523b\u610f\u9a73\u65a5\u9646\u5f8b\u9526\u7684\u89c2\u70b9\uff0c\u4e24\u5730\u7684\u751f\u6d3b\u89c2\u5ff5\u5dee\u5f02\u592a\u5927\u4e86\uff0c\u968f\u4fbf\u62ff\u81ea\u5df1\u7684\u751f\u6d3b\u89c2\u5ff5\u53bb\u9a73\u65a5\u522b\u4eba\u5c31\u592a\u5931\u793c\u4e86\uff0c\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u6d77\u5dde\u7684\u57fa\u7840\u592a\u5dee\uff0c\u5c31\u7b97\u60f3\u4eab\u53d7\u7cbe\u81f4\u7684\u751f\u6d3b\uff0c\u4e5f\u6ca1\u5730\u65b9\u4eab\u53d7\u53bb\u3002\u201d</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u8bf4\u9053\uff1a\u201c\u606a\u5c11\u8bf4\u8fc7\uff0c\u52e4\u594b\u52a1\u5b9e\u7684\u4eba\u5728\u7231\u8fbe\u7535\u5b50\u7ec8\u80fd\u8fc7\u4e0a\u4f53\u9762\u800c\u6709\u5c0a\u4e25\u7684\u751f\u6d3b\uff0c\u6211\u770b\u6d77\u5dde\u5f88\u5feb\u5c31\u4f1a\u4ee5\u9526\u6e56\u4e3a\u4e2d\u5fc3\u5f62\u6210\u4e00\u4e2a\u5927\u7684\u767d\u9886\u4e2d\u4ea7\u9636\u5c42\u7684\u793e\u533a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u63a5\u89e6\u8fc7\u597d\u51e0\u5bb6\u5185\u5730\u7684\u4f01\u4e1a\uff0c\u6709\u56fd\u4f01\uff0c\u6709\u6c11\u4f01\uff0c\u8fd9\u4e9b\u4f01\u4e1a\u7684\u7ba1\u7406\u8005\uff0c\u8ddf\u606a\u5c11\u7684\u60f3\u6cd5\u5c31\u5927\u4e3a\u4e0d\u540c\u2026\u2026\u201d\u9646\u5f8b\u9526\u8bf4\u9053\uff0c\u201c\u4ed6\u4eec\u4e00\u76f4\u5f88\u5f3a\u8c03\u5185\u5730\u4f4e\u5ec9\u7684\u52b3\u52a8\u529b\u6210\u672c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u662f\u5185\u5730\u7684\u4e00\u4e2a\u4f18\u52bf\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u8bed\u6c14\u4e00\u8f6c\uff0c\u201c\u4f4e\u5ec9\u7684\u4eba\u529b\u8d44\u6e90\u6210\u672c\uff0c\u7684\u786e\u662f\u76ee\u524d\u5438\u5f15\u6295\u8d44\u6700\u4e3b\u8981\u7684\u4e00\u4e2a\u4f18\u52bf\uff0c\u4f46\u662f\u5de5\u4eba\u9636\u7ea7\u6709\u529b\u91cf\uff0c\u8d44\u672c\u5bb6\u7684\u5265\u524a\u603b\u662f\u4f1a\u53d8\u5f97\u6e29\u60c5\u8109\u8109\u7684\uff0c\u8bdd\u5e94\u8be5\u600e\u4e48\u8bf4\u5f97\u51a0\u5195\u5802\u7687\u4e00\u4e9b\u2026\u2026\u201d\u5f20\u606a\u4fa7\u8fc7\u5934\u95ee\u5468\u6e38\uff0c\u5468\u6e38\u8fd8\u6ca1\u6709\u60f3\u5230\u5408\u9002\u7684\u8bcd\uff0c\u5f20\u606a\u5374\u5148\u60f3\u5230\u5408\u9002\u7684\u8868\u8fbe\uff0c\u201c\u8ba9\u5fe0\u8bda\u4e8e\u5de5\u4f5c\u7684\u5458\u5de5\u80fd\u8fc7\u4e0a\u4f53\u9762\u800c\u6709\u5c0a\u4e25\u7684\u751f\u6d3b\uff0c\u5e94\u8be5\u662f\u4e00\u5bb6\u73b0\u4ee3\u4f01\u4e1a\u7684\u6838\u5fc3\u793e\u4f1a\u804c\u8d23\uff0c\u8fd9\u4e48\u8bf4\uff0c\u4f1a\u4e0d\u4f1a\u51a0\u5195\u5802\u7687\u4e00\u4e9b\uff1f\u201d\u65c1\u8fb9\u542c\u5230\u4eba\u90fd\u7b11\u4e86\u8d77\u6765\uff0c\u5f20\u606a\u53c8\u81ea\u5632\u9053\uff0c\u201c\u7231\u8fbe\u7535\u5b50\u673a\u9047\u6bd4\u8f83\u597d\uff0c\u76c8\u5229\u6bd4\u8f83\u5145\u8db3\uff0c\u6240\u8c13\u56fe\u7a77\u5315\u89c1\uff0c\u8fd9\u65f6\u5019\u5f53\u7136\u6ca1\u6709\u5fc5\u8981\u5c31\u8d64\u88f8\u88f8\u7684\u9732\u51fa\u72f0\u72de\u9762\u76ee\uff0c\u6240\u4ee5\u5c31\u8499\u4e0a\u6e29\u60c5\u8109\u8109\u7684\u906e\u7f9e\u9762\u7eb1\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u6e38\u7b11\u7740\u8bf4\uff1a\u201c\u6ca1\u6709\u54ea\u4e2a\u8d44\u672c\u5bb6\u4f1a\u50cf\u4f60\u8fd9\u6837\u5c06\u81ea\u5df1\u63ed\u5f97\u8840\u6dcb\u6dcb\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u4e3a\u4e86\u9632\u6b62\u589e\u53d1\u5e76\u8d2d\u5f15\u8d77\u5a92\u4f53\u8fc7\u5ea6\u7684\u5173\u6ce8\uff0c\u5f20\u606a\u65e9\u5c31\u5c06\u9526\u6e56\u540d\u4e0b\u5bf9\u7231\u8fbe\u7535\u5b50\u7684\u6301\u80a1\u5206\u6563\u5230\u65b0\u5149\u7eb8\u4e1a\u4e0e\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u7b49\u591a\u5bb6\u516c\u53f8\u7684\u540d\u4e0b\uff0c\u4f46\u77e5\u6089\u5185\u60c5\u7684\u4eba\u90fd\u77e5\u9053\u5f20\u606a\u5b9e\u9645\u63e1\u7740\u7231\u8fbe\u7535\u5b50\u4e03\u767e\u4e07\u80a1\uff0c\u4ee5\u6b64\u6b21\u79c1\u52df\u534f\u8bae\u8bae\u4ef7\u8ba1\u7b97\uff0c\u4ef7\u503c\u9ad8\u8fbe\u4e24\u4ebf\u516b\u5343\u4e07\u7f8e\u91d1\uff0c\u8fd9\u4e48\u505a\u4e5f\u65b9\u4fbf\u589e\u53d1\u65b9\u6848\u5b9e\u65bd\u65f6\uff0c\u7531\u65b0\u5149\u7eb8\u4e1a\u4e0e\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u6765\u5404\u81ea\u83b7\u5f97\u4e00\u5b9a\u6bd4\u4f8b\u7684\u53d1\u5c55\u8d44\u91d1\uff0c\u7531\u4e8e\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u6b64\u65f6\u7684\u4e3b\u8981\u8d44\u4ea7\u4f53\u73b0\u5728\u5bf9\u7231\u8fbe\u7535\u5b50\u9ad8\u8fbe\u4e94\u767e\u4e07\u80a1\u7684\u6301\u80a1\u4e0a\uff0c\u8fd9\u90e8\u5206\u80a1\u4efd\u53c8\u90fd\u662f\u9526\u6e56\u540d\u4e0b\u8f6c\u8fc7\u53bb\u7684\uff0c\u9526\u6e56\u5bf9\u9526\u6e56\u7535\u5b50\u7814\u7a76\u9662\u7684\u6301\u80a1\u4ece70\uff05\u63d0\u9ad8\u523090\uff05\u3002</p><p>\u3000\u3000\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u4e5f\u662f\u6ce8\u518c\u7684\u80a1\u4efd\u6709\u9650\u516c\u53f8\uff0c\u4e00\u5343\u4e07\u80a1\uff0c\u9526\u6e56\u6301\u670970\uff05\u3001\u4e03\u767e\u4e07\u80a1\uff0c\u7814\u7a76\u9662\u4e3b\u8981\u62e5\u6709\u7231\u8fbe\u7535\u5b50\u5546\u6807\u9664\u89c6\u542c\u789f\u673a\u7c7b\u5916\u7684\u6240\u6709\u6743\u4ee5\u53ca\u6f5c\u529b\u5de8\u5927\u7684\u65e0\u5f62\u8d44\u4ea7\uff0c\u5f53\u7136\uff0c\u8c22\u665a\u6674\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u90fd\u4e0d\u53ef\u80fd\u8ddf\u5f20\u606a\u4e89\u4ec0\u4e48\uff0c\u5f20\u606a\u8bf4\u8fd9\u90e8\u5206\u8d44\u4ea7\u503c\u4e00\u4ebf\u7f8e\u91d1\uff0c\u7136\u540e\u4ed6\u518d\u5c06\u4ef7\u503c\u4e24\u4ebf\u7f8e\u91d1\u7684\u7231\u8fbe\u7535\u5b50\u7684\u4e03\u767e\u4e07\u80a1\u80a1\u6743\u6ce8\u5165\u7814\u7a76\u9662\uff0c\u90a3\u7814\u7a76\u9662\u5c31\u8981\u5411\u9526\u6e56\u5b9a\u5411\u589e\u5411\u4e24\u5343\u4e07\u80a1\uff0c\u603b\u80a1\u6570\u589e\u52a0\u5230\u4e09\u5343\u4e07\uff0c\u5176\u4ed6\u4e09\u767e\u4e07\u80a1\uff0c\u6d77\u88d5\u63e1\u6709\u4e24\u767e\u4e00\u5341\u4e07\u80a1\uff0c\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u5404\u63e1\u6709\u56db\u5341\u4e94\u4e07\u80a1\u3002</p><p>\u3000\u3000\u73b0\u5728\u7814\u7a76\u9662\u5c31\u7b49\u7740\u540d\u4e0b\u6240\u6301\u7684\u4e94\u767e\u4e07\u80a1\u7231\u8fbe\u7684\u80a1\u7968\u8f6c\u53d8\u6210\u8d44\u91d1\u5927\u8086\u8fdb\u884c\u6280\u672f\u6269\u5f20\u3002</p></article>"}}}]);
