(window.webpackJsonp=window.webpackJsonp||[]).push([[63],{85:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e00\u8282 \u796d\u7940\u5f00\u59cb",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e00\u8282 \u796d\u7940\u5f00\u59cb</h2><p>\u6885\u83f2\u65af\u5728\u57c3\u65af\u8003\u903c\u95ee\u90a3\u53ea\u4eba\u9762\u72ee\uff0c\u5f97\u77e5\u8fd9\u4f4d\u53eb\u8482\u5a1c\u5c91\u7684\u5973\u4eba\u9762\u72ee\u662f\u4e2a\u201c\u672f\u58eb\u201d\u3002\u4eba\u9762\u72ee\u4e2d\u5deb\u5e08\u751a\u5c11\uff0c\u4f46\u5b83\u4eec\u6bd5\u7adf\u4e5f\u662f\u6709\u9ad8\u7b49\u667a\u6167\u7684\u751f\u7269\uff0c\u53c8\u5e38\u548c\u4eba\u7c7b\u6253\u4ea4\u9053\uff0c\u5e94\u8be5\u8fd8\u662f\u5f88\u6e05\u695a\u201c\u672f\u58eb\u201d\u8fd9\u4e2a\u8bcd\u7684\u542b\u4e49\u7684\u3002</p><p>\u6240\u8c13\u672f\u58eb\uff0c\u7b80\u5355\u8bf4\u5c31\u662f\u5929\u751f\u7684\u5965\u672f\u8005\uff0c\u6216\u8005\u8bf4\u662f\u5929\u751f\u7684\u5deb\u5e08\u3002\u4ed6\u4eec\u5929\u751f\u4f1a\u8bf4\u9f99\u8bed\uff0c\u5929\u751f\u80fd\u611f\u5e94\u5230\u9b54\u7f51\u7684\u5b58\u5728\uff0c\u7a0d\u52a0\u8bad\u7ec3\u2014\u2014\u751a\u81f3\u4e0d\u52a0\u8bad\u7ec3\u2014\u2014\u4fbf\u53ef\u4ee5\u4ece\u9b54\u7f51\u4e2d\u63d0\u53d6\u80fd\u91cf\u65bd\u6cd5\uff0c\u6700\u4ee4\u4eba\u6124\u6168\u7684\u662f\uff0c\u4ed6\u4eec\u5c45\u7136\u65e0\u9700\u9b54\u6cd5\u4e66\uff0c\u65e0\u9700\u51c6\u5907\u9b54\u6cd5\u2014\u2014\u672f\u58eb\u65bd\u6cd5\u4ece\u6765\u90fd\u662f\u4e34\u65f6\u4ece\u9b54\u7f51\u4e2d\u8c03\u7528\u5851\u6210\u7684\uff0c\u4e0d\u50cf\u5deb\u5e08\u5f97\u4e8b\u5148\u8d39\u529f\u592b\u51c6\u5907\u59a5\u5f53\u3002</p><p>\u4f20\u8bf4\u672f\u58eb\u6709\u9f99\u6216\u8005\u795e\u7947\u7684\u8840\u8109\uff0c\u6240\u4ee5\u624d\u6709\u8fd9\u7b49\u5965\u6cd5\u5929\u8d4b\uff0c\u4e0d\u8fc7\u8fd9\u4e5f\u4ec5\u4ec5\u53ea\u662f\u4f20\u8bf4\u4e86\uff0c\u5e76\u65e0\u5b9e\u8bc1\u3002\u743c\u6069\u5728\u5deb\u5e08\u5b66\u6821\u65f6\uff0c\u540c\u5b66\u4e2d\u5c31\u6709\u51e0\u4e2a\u672f\u58eb\uff0c\u4e0d\u8fc7\u9634\u9b42\u57ce\u592a\u8fc7\u4e8e\u8bb2\u6c42\u7cbe\u82f1\u5316\uff0c\u5c31\u7b97\u662f\u5929\u8d4b\u9ad8\u660e\u7684\u672f\u58eb\uff0c\u4e5f\u8981\u88ab\u9001\u8fdb\u5b66\u6821\u63a5\u53d7\u6b63\u89c4\u7cfb\u7edf\u5316\u7684\u8bad\u7ec3\uff0c\u4e0d\u4f1a\u5bb9\u5fcd\u672f\u58eb\u81ea\u7531\u53d1\u5c55\u7684\u3002</p><p>\u6885\u83f2\u65af\u5012\u6ca1\u548c\u672f\u58eb\u6253\u8fc7\u4ea4\u9053\uff0c\u4f46\u4e5f\u5927\u4f53\u542c\u8bf4\u8fc7\u4e00\u4e9b\u60c5\u51b5\u3002\u5982\u679c\u90a3\u53ea\u4eba\u9762\u72ee\u6240\u8bf4\u4e0d\u9519\uff0c\u8fd9\u4e2a\u53eb\u8482\u5a1c\u5c91\u7684\u9996\u9886\u771f\u7684\u662f\u4e2a\u201c\u672f\u58eb\u201d\uff0c\u90a3\u5c31\u6bd4\u8f83\u96be\u5bf9\u4ed8\u4e86\u3002</p><p>\u4e00\u822c\u7684\u4eba\u9762\u72ee\u8d35\u65cf\uff0c\u4e0d\u8fc7\u662f\u5728\u7cbe\u795e\u63a7\u5236\u548c\u5236\u9020\u5e7b\u8c61\u4e0a\u6709\u5929\u8d4b\uff0c\u5e76\u4e0d\u662f\u771f\u6b63\u7684\u5deb\u5e08\uff0c\u5bf9\u5176\u4ed6\u9b54\u6cd5\u662f\u4e00\u7a8d\u4e0d\u901a\u7684\u3002\u800c\u8fd9\u4e24\u79cd\u7c7b\u578b\u7684\u9b54\u6cd5\uff0c\u5bf9\u4e8e\u6885\u83f2\u65af\u5176\u5b9e\u5a01\u80c1\u5e76\u4e0d\u5f88\u5927\u3002\u8eab\u4e3a\u5723\u6b66\u58eb\uff0c\u5979\u81ea\u7136\u62e5\u6709\u65e0\u6bd4\u575a\u6bc5\u521a\u5f3a\u7684\u610f\u5fd7\uff0c\u5bfb\u5e38\u7684\u7cbe\u795e\u63a7\u5236\u9b54\u6cd5\u4e8e\u5979\u800c\u8a00\u57fa\u672c\u6ca1\u7528\uff1b\u5e7b\u672f\u867d\u7136\u96be\u7f20\u4e9b\uff0c\u4f46\u51ed\u501f\u7740\u4e30\u5bcc\u7684\u5192\u9669\u7ecf\u9a8c\u548c\u6e05\u9192\u5934\u8111\uff0c\u4e5f\u8fd8\u662f\u53ef\u4ee5\u5c3d\u53ef\u80fd\u4e0d\u53d7\u8ff7\u60d1\u7684\u3002</p><p>\u201c\u53ef\u60dc\uff0c\u81ea\u5df1\u4e0d\u80fd\u4f7f\u7528\u771f\u5b9e\u4e4b\u773c\u3002\u201d\u5979\u5728\u5fc3\u4e2d\u9057\u61be\u7740\u3002</p><p>\u771f\u5b9e\u4e4b\u773c\u662f\u63d0\u5c14\u8d50\u4e88\u4ed6\u7684\u5723\u6b66\u58eb\u7684\u4e00\u79cd\u6280\u80fd\uff0c\u6216\u8005\u8bf4\u795e\u672f\u3002\u4f5c\u4e3a\u516c\u7406\u4e0e\u6b63\u4e49\u4e4b\u795e\uff0c\u63d0\u5c14\u4e00\u76f4\u88ab\u4fe1\u5f92\u5ba3\u79f0\u4e3a\u201c\u773c\u775b\u6c38\u8fdc\u4e0d\u88ab\u8499\u853d\u7684\u516c\u6b63\u8005\u201d\u3002\u8fd9\u542c\u8d77\u6765\u975e\u5e38\u8bbd\u523a\uff0c\u56e0\u4e3a\u4f17\u6240\u5468\u77e5\uff0c\u8fd9\u4f4d\u795e\u7947\u662f\u4e2a\u778e\u5b50\u3002\u8d70\u8fdb\u4efb\u4f55\u4e00\u5ea7\u63d0\u5c14\u795e\u6bbf\uff0c\u4f60\u5c31\u4f1a\u770b\u5230\u8fd9\u4f4d\u6b8b\u5e9f\u795e\u7947\u7684\u5851\u50cf\uff0c\u7a7a\u7a7a\u6d1e\u6d1e\u7684\u773c\u7736\u4e0a\u8499\u7740\u767d\u8272\u5e03\u5e26\uff0c\u4ed6\u7684\u4fe1\u5f92\u4e5f\u5f80\u5f80\u5982\u6b64\u6253\u626e\u3002</p><p>\u4e0d\u8fc7\uff0c\u63d0\u5c14\u8d50\u4e88\u4fe1\u5f92\u7684\u795e\u672f\u4e2d\uff0c\u786e\u5b9e\u5305\u62ec\u4e00\u4e9b\u4e13\u95e8\u7528\u4e8e\u7834\u9664\u5e7b\u8c61\u770b\u6e05\u771f\u5b9e\u7684\u9b54\u6cd5\uff0c\u5176\u4e2d\u6700\u9876\u7ea7\u6700\u5f3a\u5927\u7684\uff0c\u5c31\u662f\u4e13\u95e8\u8d50\u4e88\u4ed6\u7684\u5723\u6b66\u58eb\u7684\u201c\u771f\u5b9e\u4e4b\u773c\u201d\u2014\u2014\u4ece\u8fd9\u4e2a\u610f\u4e49\u4e0a\u6765\u8bf4\uff0c\u4e0a\u9762\u7684\u8d5e\u8a89\u4e5f\u5e76\u975e\u6ca1\u6709\u9053\u7406\u3002</p><p>\u4f46\u6885\u83f2\u65af\u65e0\u6cd5\u4f7f\u7528\u201c\u771f\u5b9e\u4e4b\u773c\u201d\uff0c\u5979\u786e\u5b9e\u662f\u63d0\u5c14\u7684\u5723\u6b66\u58eb\uff0c\u662f\u201c\u795e\u9009\u8005\u201d\uff0c\u4f46\u5979\u6ca1\u6709\u8fd9\u4e2a\u80fd\u529b\u3002</p><p>\u8fd9\u5e76\u4e0d\u662f\u4e00\u4e2a\u4ee4\u4eba\u6109\u5feb\u7684\u4e8b\u5b9e\uff0c\u4f46\u8fd9\u786e\u5b9e\u662f\u4e8b\u5b9e\u3002</p><p>\u597d\u5427\uff0c\u867d\u7136\u6ca1\u6709\u771f\u5b9e\u4e4b\u773c\u7684\u80fd\u529b\uff0c\u4f46\u51ed\u501f\u4e30\u5bcc\u7684\u5192\u9669\u7ecf\u9a8c\u3001\u6e05\u9192\u7684\u5934\u8111\u548c\u9510\u5229\u7684\u773c\u5149\uff0c\u5979\u81ea\u4fe1\u4f9d\u7136\u53ef\u4ee5\u5e94\u4ed8\u5927\u591a\u6570\u5e7b\u672f\u3002</p><p>\u4f46\u8fd9\u4e2a\u4eba\u9762\u72ee\u9996\u9886\u8482\u5a1c\u5c91\u662f\u4e2a\u201c\u672f\u58eb\u201d\uff0c\u90a3\u5c31\u610f\u5473\u7740\uff0c\u5979\u4e0d\u4ec5\u4ec5\u53ea\u662f\u9760\u5929\u8d4b\u7684\u5e7b\u672f\u548c\u9644\u9b54\u80fd\u529b\u5403\u996d\uff0c\u5979\u662f\u4e2a\u771f\u6b63\u7684\u5965\u6cd5\u8005\uff0c\u80fd\u4f7f\u7528\u5404\u79cd\u5404\u6837\u7684\u6cd5\u672f\uff0c\u8fd9\u5c31\u5f88\u9ebb\u70e6\u4e86\u3002</p><p>\u89c1\u673a\u884c\u4e8b\u5427\u3002</p><p>\u4eba\u9762\u72ee\u770b\u8d77\u6765\u4f4d\u9636\u5e76\u4e0d\u5982\u4f55\u68ee\u4e25\uff0c\u7b49\u7ea7\u5212\u5206\u5f88\u7c97\u758f\u3002\u796d\u575b\u4e2d\u5fc3\u7684\u8482\u5a1c\u5c91\u662f\u9996\u9886\uff0c\u7ad9\u5728\u516d\u6839\u624b\u6307\u4e0a\u7684\u8d35\u65cf\u5904\u4e8e\u7b2c\u4e8c\u9636\u5c42\uff0c\u5269\u4e0b\u4e00\u767e\u4f59\u540d\u4eba\u9762\u72ee\uff0c\u5305\u62ec\u6885\u83f2\u65af\u5728\u5185\uff0c\u5168\u7b97\u662f\u7b2c\u4e09\u9636\u5c42\uff0c\u5168\u90fd\u5728\u796d\u575b\u4e0b\u7ad9\u7740\uff0c\u65e0\u5206\u9ad8\u4e0b\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5934\u76ee\u51fa\u6765\u7ef4\u6301\u79e9\u5e8f\u3002</p><p>\u6b63\u5385\u975e\u5e38\u5927\uff0c\u867d\u7136\u4e2d\u95f4\u8bbe\u4e86\u796d\u575b\uff0c\u53c8\u585e\u8fdb\u4e00\u767e\u4f59\u540d\u4eba\u9762\u72ee\uff0c\u4f9d\u65e7\u663e\u5f97\u6709\u4e9b\u7a7a\u65f7\u3002\u4e0d\u8fc7\u5f88\u5feb\uff0c\u8fd9\u79cd\u611f\u89c9\u4fbf\u6ca1\u6709\u4e86\u3002</p><p>\u4e00\u6247\u5c0f\u95e8\u6084\u65e0\u58f0\u606f\u5730\u6253\u5f00\uff0c\u51e0\u767e\u4e2a\u4eba\u7c7b\u88ab\u63a8\u6421\u7740\uff0c\u9a71\u8d76\u7740\uff0c\u4ece\u95e8\u91cc\u8e09\u8dc4\u8dcc\u649e\u51fa\u6765\uff0c\u8d70\u5230\u796d\u575b\u4e0b\u3002\u4ed6\u4eec\u90fd\u624e\u7740\u767d\u8272\u7684\u5934\u5dfe\uff0c\u7a7f\u7740\u9ed1\u8272\u5c16\u5934\u7fd8\u8d77\u7684\u957f\u9774\u3002</p><p>\u662f\u8d1d\u6234\u86ee\u65cf\u3002</p><p>\u4ece\u6570\u91cf\u4e0a\u770b\uff0c\u5e94\u8be5\u662f\u51e0\u4e4e\u6240\u6709\u7684\u8d1d\u6234\u86ee\u65cf\u90e8\u843d\u90fd\u5728\u8fd9\u91cc\u4e86\u3002\u6885\u83f2\u65af\u4f4e\u7740\u5934\uff0c\u7528\u773c\u89d2\u4f59\u5149\u626b\u53bb\uff0c\u5979\u770b\u89c1\u4e86\u51e0\u5f20\u6709\u4e9b\u773c\u719f\u7684\u9762\u5b54\uff0c\u6b63\u662f\u8fd9\u5927\u534a\u4e2a\u6708\u6765\u4e00\u8def\u540c\u884c\u7684\u86ee\u65cf\u90e8\u843d\u4e2d\u7684\u51e0\u4e2a\u3002\u770b\u8d77\u6765\uff0c\u4ed6\u4eec\u5e76\u6ca1\u6709\u5728\u57c3\u65af\u8003\u57ce\u9003\u8131\uff0c\u8fd8\u662f\u88ab\u4eba\u9762\u72ee\u6293\u4f4f\u4e86\u3002\u4e0d\u8fc7\u8fd9\u5e76\u4e0d\u51fa\u4e4e\u6885\u83f2\u65af\u7684\u610f\u6599\uff0c\u5979\u65e9\u5c31\u731c\u5230\uff0c\u53cd\u6b63\u5979\u4e5f\u5e76\u4e0d\u5728\u4e4e\u8fd9\u4e9b\u8d1d\u6234\u86ee\u65cf\u7684\u6b7b\u6d3b\u3002</p><p>\u4eba\u9762\u72ee\u662f\u51c6\u5907\u5411\u683c\u62c9\u5179\u7279\u732e\u796d\uff0c\u683c\u62c9\u5179\u7279\u662f\u4e2a\u6076\u9b54\u9886\u4e3b\u2014\u2014\u6885\u83f2\u65af\u5bf9\u683c\u62c9\u5179\u7279\u7684\u4e86\u89e3\uff0c\u5dee\u4e0d\u591a\u4e5f\u5c31\u8fd9\u70b9\u4e86\uff0c\u5979\u4e0d\u50cf\u743c\u6069\uff0c\u591a\u5c11\u8fd8\u5728\u5b66\u6821\u91cc\u8bfb\u8fc7\u4f4d\u9762\u5b66\uff0c\u91cc\u9762\u6709\u4e00\u4e9b\u5173\u4e8e\u6df1\u6e0a\u6076\u9b54\u7684\u4ecb\u7ecd\u3002\u683c\u62c9\u5179\u7279\u867d\u7136\u662f\u6df1\u6e0a\u4e09\u5927\u9886\u4e3b\u4e4b\u4e00\uff0c\u4f46\u5728\u7269\u8d28\u754c\u5374\u5e76\u6ca1\u6709\u591a\u5c11\u540d\u58f0\u548c\u5f71\u54cd\u529b\uff0c\u6885\u83f2\u65af\u80fd\u542c\u8bf4\u8fc7\u540d\u5b57\uff0c\u5df2\u7ecf\u7b97\u5f88\u4e0d\u9519\u4e86\u3002</p><p>\u65e2\u7136\u662f\u5411\u6076\u9b54\u732e\u796d\uff0c\u4e0d\u7528\u60f3\u90fd\u77e5\u9053\uff0c\u80af\u5b9a\u9700\u8981\u5927\u91cf\u7684\u751f\u547d\u4f5c\u4e3a\u796d\u54c1\u3002\u8fd9\u4e9b\u8d1d\u6234\u86ee\u65cf\u4eba\uff0c\u5927\u6982\u5c31\u662f\u8fd9\u79cd\u7528\u9014\u5427\u3002</p><p>\u51e0\u767e\u4e2a\u86ee\u65cf\u80c6\u6218\u5fc3\u60ca\u5730\u7ad9\u5728\u4e00\u65c1\uff0c\u7b49\u5f85\u7740\u81ea\u5df1\u7684\u547d\u8fd0\u3002\u5f88\u5feb\uff0c\u51e0\u5341\u4e2a\u5e74\u7eaa\u8001\u8fc8\u8005\u88ab\u6311\u9009\u51fa\u6765\uff0c\u9a71\u8d76\u4e0a\u796d\u575b\u3002</p><p>\u4ed6\u4eec\u5bb3\u6015\u5730\u4f5d\u507b\u7740\u8eab\u4f53\uff0c\u5728\u4eba\u9762\u72ee\u9996\u9886\u8482\u5a1c\u5c91\u9762\u524d\u6218\u6817\u7740\uff0c\u4ed6\u4eec\u5df2\u7ecf\u611f\u89c9\u5230\u6b7b\u4ea1\u7684\u51b0\u51b7\u89e6\u624b\u5728\u629a\u6478\u81ea\u5df1\u3002\u8482\u5a1c\u5c91\u6e38\u52a8\u7740\u86c7\u5c3e\uff0c\u626b\u89c6\u7740\u8001\u4eba\u4eec\u3002</p><p>\u201c\u770b\u7740\u6211\uff01\u201d\u5979\u547d\u4ee4\uff0c\u58f0\u97f3\u5c16\u5229\u3002</p><p>\u4eff\u4f5b\u88ab\u63a7\u5236\u4e00\u822c\uff0c\u6240\u6709\u7684\u8001\u4eba\u90fd\u9f50\u9f50\u62ac\u8d77\u5934\u6765\uff0c\u4ed6\u4eec\u7684\u773c\u795e\u4e2d\u4e00\u7247\u832b\u7136\u7a7a\u6d1e\uff0c\u8138\u4e0a\u795e\u60c5\u5446\u5446\u6ede\u6ede\uff0c\u4eff\u4f5b\u5239\u90a3\u95f4\u88ab\u593a\u53d6\u4e86\u5fc3\u667a\u3002</p><p>\u8482\u5a1c\u5c91\u4ece\u8170\u95f4\u62d4\u51fa\u86c7\u5f62\u77ed\u5251\uff0c\u8f7b\u8bf5\u5492\u8bed\uff0c\u94f6\u8272\u7684\u5251\u8eab\u4e0a\u6cdb\u51fa\u4e4c\u9eef\u7684\u9ed1\u5149\u3002\u5979\u626d\u52a8\u8170\u80a2\uff0c\u6b3e\u6b3e\u800c\u884c\uff0c\u8f7b\u8f7b\u6325\u52a8\u7740\u77ed\u5251\uff0c\u5212\u5f00\u6240\u6709\u4eba\u7684\u54bd\u5589\uff0c\u52a8\u4f5c\u4f18\u7f8e\u5f97\u4eff\u4f5b\u821e\u8e48\u3002</p><p>\u867d\u7136\u5df2\u7ecf\u65ad\u6c14\uff0c\u4f46\u8001\u4eba\u4eec\u7684\u5c38\u4f53\u4f9d\u7136\u76f4\u76f4\u5730\u50f5\u7acb\u7740\uff0c\u9c9c\u8840\u4ece\u88ab\u5272\u65ad\u7684\u54bd\u5589\u5904\u6e85\u51fa\u6765\uff0c\u843d\u5728\u5730\u4e0a\uff0c\u5316\u4f5c\u9ed1\u8272\u7684\u706b\u7130\uff0c\u6e10\u6e10\u6c47\u805a\u6210\u817e\u817e\u706b\u6d77\uff0c\u5c06\u8482\u5a1c\u5c91\u56f4\u5728\u5176\u4e2d\u3002</p><p>\u201c\u5494\uff01\u201d</p><p>\u6c89\u95f7\u7684\u96f7\u9e23\u9aa4\u7136\u5728\u5934\u4e0a\u54cd\u8d77\uff0c\u4e00\u9053\u94f6\u8272\u95ea\u7535\u6495\u88c2\u591c\u7a7a\uff0c\u7d27\u63a5\u7740\uff0c\u9ed1\u8272\u7684\u96e8\u70b9\u74e2\u6cfc\u843d\u4e0b\uff0c\u7838\u5728\u796d\u575b\u4e0a\u3002\u706b\u7130\u4e0d\u4f46\u6ca1\u6709\u88ab\u6d47\u7184\uff0c\u53cd\u800c\u8d8a\u52a0\u65fa\u76db\u5347\u817e\uff0c\u718a\u718a\u71c3\u70e7\u3002\u8001\u4eba\u4eec\u7684\u5c38\u4f53\u6d3b\u52a8\u8d77\u6765\uff0c\u50f5\u76f4\u5730\u8d70\u8fdb\u706b\u6d77\u4e2d\uff0c\u77ac\u95f4\u88ab\u9ed1\u8272\u7684\u70c8\u7130\u541e\u6ca1\u3002</p><p>\u8fd9\u662f\u683c\u62c9\u5179\u7279\u796d\u7940\u4eea\u5f0f\u7684\u7b2c\u4e00\u6b65\uff0c\u79fd\u96e8\u3002</p><p>\u683c\u62c9\u5179\u7279\u5f88\u559c\u6b22\u201c\u516d\u201d\u8fd9\u4e2a\u6570\u5b57\uff0c\u5927\u7ea6\u662f\u56e0\u4e3a\u4ed6\u7684\u624b\u6307\u662f\u516d\u6839\u3002\u636e\u8bf4\u4ed6\u5728\u6df1\u6e0a\u4e2d\u7684\u94f6\u8272\u738b\u5bab\u6709\u516d\u5341\u516d\u5ea7\u8c61\u7259\u5854\uff0c\u4ed6\u7684\u4eb2\u4fe1\u6570\u76ee\u603b\u662f\u4fdd\u6301\u516d\u4e2a\uff08\u4e0d\u65ad\u6709\u4eba\u52a0\u5165\uff0c\u4e0d\u65ad\u6709\u4eba\u9000\u51fa\uff0c\u6076\u9b54\u662f\u559c\u6b22\u80cc\u53db\u7684\u751f\u7269\uff09\uff0c\u4ed6\u8eab\u8fb9\u603b\u662f\u966a\u4f34\u7740\u516d\u4e2a\u62a4\u536b\uff08\u5176\u4e2d\u5305\u62ec\u4e24\u4e2a\u516d\u81c2\u86c7\u9b54\uff09\uff0c\u751a\u81f3\u6709\u4f20\u8a00\u8bf4\uff0c\u4ed6\u6bcf\u5929\u90fd\u53ea\u548c\u516d\u4e2a\u5973\u6027\u4e0a\u5e8a\uff0c\u7edd\u4e0d\u8d85\u8fc7\uff0c\u4e5f\u7edd\u4e0d\u51cf\u5c11\u3002</p><p>\u540c\u6837\u7684\uff0c\u4ed6\u7684\u796d\u7940\u4eea\u5f0f\uff0c\u4e5f\u88ab\u4fe1\u5f92\u4eec\u6295\u5176\u6240\u597d\u5730\u5206\u6210\u516d\u4e2a\u6b65\u9aa4\uff0c\u79fd\u96e8\u662f\u7b2c\u4e00\u6b65\u3002</p><p>\u5f88\u5feb\u7684\uff0c\u4eba\u9762\u72ee\u4eec\u53c8\u5c06\u86ee\u65cf\u4e2d\u7684\u5b69\u5b50\u6311\u51fa\u6765\uff0c\u5927\u7ea6\u4e5f\u6709\u51e0\u5341\u4e2a\uff0c\u63a8\u4e0a\u796d\u575b\u3002\u540c\u6837\u7684\u6b65\u9aa4\uff0c\u8482\u5a1c\u5c91\u518d\u6b21\u5272\u65ad\u4e86\u4ed6\u4eec\u7684\u54bd\u5589\uff0c\u9c9c\u8840\u6e85\u5c04\u51fa\u6765\uff0c\u5316\u4f5c\u6697\u7eff\u8272\u7684\u6f6e\u6e7f\u4e91\u96fe\uff0c\u6e10\u6e10\u6269\u6563\u5f00\u6765\uff0c\u7b3c\u7f69\u7740\u5927\u534a\u4e2a\u796d\u575b\u3002\u5b69\u5b50\u4eec\u7684\u5c38\u4f53\u88ab\u7eff\u96fe\u6d78\u6ca1\uff0c\u53d1\u51fa\u55e4\u55e4\u7684\u54cd\u52a8\uff0c\u4eff\u4f5b\u9178\u6db2\u7684\u8150\u8680\u58f0\uff0c\u5f88\u5feb\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u7b2c\u4e8c\u4e2a\u6b65\u9aa4\u201c\u7eff\u96fe\u201d\u5b8c\u6210\u3002</p></article>"}}}]);
