(window.webpackJsonp=window.webpackJsonp||[]).push([[630],{653:function(p){p.exports={chapterTitle:null,title:"\u7b2c630\u7ae0 \u7f51\u5427\u7b56\u5212",content:"<article><h2>\u7b2c630\u7ae0 \u7f51\u5427\u7b56\u5212</h2><p>\u3000\u3000\u5728\u7d2b\u971e\u6e56\u897f\u9910\u5385\u5403\u725b\u6392\uff0c\u8dd1\u5230\u6559\u5de5\u98df\u5802\u5c31\u6bcf\u4eba\u70b9\u4e86\u4e00\u4efd\u7092\u9762\uff1b\u7fdf\u4e39\u9752\u5473\u80c3\u53e3\u5c0f\uff0c\u4e5f\u53ef\u80fd\u662f\u4e3a\u4e86\u4fdd\u6301\u5996\u5a06\u6027\u611f\u7684\u4f53\u578b\uff0c\u53ea\u62ff\u4e86\u4e00\u7f50\u82ac\u8fbe\u542c\u675c\u98de\u4ed6\u4eec\u8bf4\u5f00\u7f51\u5427\u7684\u4e8b\u60c5\u3002\u521a\u5230\u4e5d\u70b9\u949f\uff0c\u5403\u591c\u5bb5\u7684\u5b66\u751f\u8fd8\u6ca1\u6709\u6d8c\u8fc7\u6765\uff0c\u6559\u5de5\u98df\u5802\u91cc\u5012\u662f\u4e0d\u591a\uff0c\u5012\u662f\u65b9\u4fbf\u4ed6\u4eec\u6e05\u9759\u7684\u8c08\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u897f\u9910\u5385\u5403\u4e86\u534a\u9971\u90fd\u4e0d\u5230\uff0c\u5012\u6ca1\u6709\u89c9\u5f97\u6559\u5de5\u98df\u5802\u7684\u7092\u9762\u6bd4\u725b\u6392\u5dee\u591a\u5c11\uff0c\u5403\u5f97\u6d25\u6d25\u6709\u5473\uff0c\u8fb9\u5403\u8fb9\u542c\u675c\u98de\u8bf4\uff0c\u542c\u675c\u98de\u8003\u8651\u6d88\u8d39\u5206\u5c42\u7684\u95ee\u9898\uff0c\u77e5\u9053\u4ed6\u4eec\u7b97\u662f\u521d\u6b65\u627e\u5230\u8fd9\u4e2a\u884c\u4e1a\u7684\u95e8\u5f84\u4e86\u3002</p><p>\u3000\u3000\u201c\u8981\u8bf4\u672a\u6765\u5b66\u5e9c\u5df7\u5546\u94fa\u7684\u79df\u91d1\uff0c\u8fd8\u771f\u4e0d\u4f1a\u592a\u4f4e\uff0c\u5e95\u5c42\u5546\u94fa\u6bcf\u5e73\u65b9\u7c73\u7684\u5e74\u79df\u91d1\u5c11\u8bf4\u4e5f\u8981\u5343\u5143\u8d77\uff0c\u201d\u5f20\u606a\u7b77\u5b50\u8fd8\u5377\u7740\u9762\u6761\u5f80\u5634\u91cc\u585e\uff0c\u8bf4\u8bdd\u7684\u58f0\u97f3\u542c\u8d77\u6765\u5c31\u6709\u4e9b\u542b\u7cca\uff0c\u8fd8\u597d\u80fd\u542c\u6e05\u695a\uff0c\u201c\u8981\u662f\u5230\u4e1c\u5927\u5317\u95e8\u627e\u4e2a\u6c11\u5b85\uff0c\u540c\u7b49\u9762\u79ef\u7684\u5e97\u79df\u5927\u6982\u90fd\u4e0d\u9700\u8981\u4e94\u516d\u5206\u4e4b\u4e00\uff0c\u5c31\u7b97\u5230\u9a6c\u83b2\u8857\u4e0a\u627e\u5e97\u9762\u5f00\u5bb6\u7f51\u5427\uff0c\u5e97\u79df\u4e5f\u8981\u4fbf\u5b9c\u8bb8\u591a\u2026\u2026\u8981\u5c06\u7f51\u5427\u5f00\u5728\u5b66\u5e9c\u5df7\uff0c\u4f60\u4eec\u7684\u786e\u4e0d\u5177\u4f53\u4ec0\u4e48\u6210\u672c\u4f18\u52bf\u3002\u4e0d\u8fc7\u540c\u4e00\u6240\u5b66\u6821\u91cc\u7684\u5b66\u751f\uff0c\u6709\u4eba\u56e0\u4e3a\u5b66\u8d39\u4f1a\u81ea\u6740\uff0c\u6709\u4eba\u5374\u5e26\u7740\u4fdd\u59c6\u5165\u5b66\uff0c\u8fd9\u5927\u6982\u662f\u6d88\u8d39\u5206\u5c42\u7684\u6781\u7aef\u4f53\u73b0\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0c\u9664\u519c\u6797\u7267\u77ff\u53ca\u5e08\u8303\u7b49\u5c11\u6570\u7279\u6b8a\u9662\u6821\u5916\uff0c\u56fd\u5185\u5176\u4ed6\u9ad8\u6821\u90fd\u5b9e\u884c\u5e76\u8f68\u6536\u8d39\uff0c\u5e73\u5747\u6536\u8d39\u6807\u51c6\u4e5f\u8d85\u8fc7\u6bcf\u5b66\u5e74\u4e24\u5343\u56db\u767e\u5143\uff0c\u7b97\u662f\u6b63\u5f0f\u8fdb\u5165\u9ad8\u6821\u9ad8\u6536\u8d39\u65f6\u671f\uff0c\u5bf9\u4e8e\u8fd9\u4e00\u4e9b\uff0c\u675c\u98de\u3001\u8499\u4e50\u4ed6\u4eec\u6b64\u65f6\u662f\u6ca1\u6709\u591a\u6df1\u7684\u611f\u89e6\u7684\uff0c\u50cf\u6c88\u7b71\u8fd9\u4e9b\u4ece\u8d2b\u56f0\u5730\u533a\u51fa\u6765\u7684\u5927\u5b66\u751f\u5c31\u8eab\u6709\u611f\u89e6\u3002</p><p>\u3000\u3000\u5f20\u606a\u62ff\u8f7b\u677e\u7684\u8bed\u8c03\u4e3e\u51fa\u8fd9\u6837\u7684\u4e8b\u5b9e\u4e5f\u4e0d\u8fc7\u4e3a\u4e86\u65b9\u4fbf\u8bf4\u660e\u6d88\u8d39\u5206\u5c42\u72b6\u51b5\u7684\u5b58\u5728\u3002</p><p>\u3000\u3000\u675c\u98de\u521a\u8fdb\u5927\u5b66\u5c31\u6709\u521b\u4e1a\u7684\u5ff5\u5934\uff0c\u7fdf\u4e39\u9752\u542c\u4e86\u4e5f\u9887\u611f\u5174\u8da3\uff0c\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u7f8e\u7738\u987e\u76fc\u7684\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u6240\u8c13\u5e26\u7740\u4fdd\u59c6\u5165\u5b66\u7684\u90a3\u7c7b\u4eba\u5927\u6982\u662f\u8bf4\u4ed6\u81ea\u5df1\u5427\u3002</p><p>\u3000\u3000\u5f20\u606a\u8214\u4e86\u8214\u5634\u5507\u4e0a\u7684\u6cb9\u6c34\uff0c\u62ff\u8d77\u684c\u4e0a\u7684\u74f6\u88c5\u53ef\u53e3\u731b\u704c\u4e86\u4e24\u53e3\u2014\u2014\u5403\u7092\u9762\u5403\u6025\u4e86\u6709\u4e9b\u564e\uff0c\u624d\u793a\u610f\u675c\u98de\u3001\u8499\u4e50\u7ee7\u7eed\u8bf4\u3002</p><p>\u3000\u3000\u8499\u4e50\u8bf4\u9053\uff1a\u201c\u8fd8\u8981\u8003\u8651\u6027\u522b\u7684\u5dee\u5f02\uff0c\u6211\u4eec\u505a\u8fc7\u4e00\u4e9b\u8c03\u67e5\uff0c\u6211\u4eec\u6628\u5929\u53ea\u6765\u5f97\u53ca\u5bf9\u4e1c\u5927\u3001\u5e08\u5728\u8fd8\u6709\u97f3\u4e50\u5b66\u9662\u7684\u5b66\u751f\u505a\u62bd\u6837\u8c03\u67e5\uff0c\u5973\u5b69\u5b50\u66f4\u5411\u5f80\u8212\u9002\u5b89\u5168\u7684\u73af\u5883\u3002\u50cf\u4e1c\u5927\u5317\u95e8\u7684\u7535\u8111\u623f\u91cc\u770b\u4e0d\u5230\u5973\u5b69\u5b50\u7684\u8e2a\u5f71\uff0c\u56e0\u7d20\u662f\u591a\u91cd\u7684\uff0c\u5f88\u91cd\u8981\u7684\u4e00\u70b9\uff0c\u8fd8\u662f\u7535\u8111\u623f\u73af\u5883\u7684\u810f\u3001\u4e71\u3001\u5dee\uff0c\u5c06\u8fd9\u4e9b\u5a07\u6ef4\u6ef4\u7684\u5973\u5b69\u5b50\u963b\u6321\u5728\u5916\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u4e0d\u9519\uff0c\u5c31\u5e94\u8be5\u8fd9\u6837\u8003\u8651\u554a\uff0c\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u675c\u98de\u3001\u8499\u4e50\u8fd8\u5f53\u4ed6\u6709\u4ec0\u4e48\u9ad8\u89c1\uff0c\u90fd\u770b\u7740\u4ed6\uff0c\u6ca1\u60f3\u5230\u5f20\u606a\u4e0b\u53e5\u8bdd\u5374\u8bf4\uff0c\u201c\u591a\u4e9b\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u6211\u6ca1\u4e8b\u4e5f\u53ef\u4ee5\u53bb\u4f60\u4eec\u7f51\u5427\u4e0a\u7f51\u73a9\u3002\u201d\u675c\u98de\u3001\u8499\u4e50\u4ed6\u4eec\u8003\u8651\u8fd9\u4e00\u70b9\uff0c\u4ed6\u5f88\u559c\u6b22\u3002\u7f51\u5427\u91cc\u8981\u90fd\u662f\u90a3\u4e9b\u7a7f\u7740\u62d6\u978b\u811a\u8df7\u5728\u6905\u5b50\u89d2\u4e0a\u62a0\u811a\u4e2b\u5b50\u7684\u7537\u751f\uff0c\u5f20\u606a\u5f53\u7136\u4e0d\u4f1a\u6709\u591a\u5927\u7684\u5174\u8da3\uff0c\u8981\u662f\u5851\u9020\u7684\u73af\u5883\u80fd\u591f\u5438\u5f15\u97f3\u4e50\u5b66\u9662\u53ca\u5e08\u5927\u7684\u5973\u751f\uff0c\u60f3\u60f3\u90fd\u4f1a\u89c9\u5f97\u8212\u5766\u3002\u8bdd\u8bf4\u56de\u6765\uff0c\u83ba\u83ba\u71d5\u71d5\u7684\u6f02\u4eae\u5973\u5b69\u5b50\u591a\u4e86\uff0c\u8fd8\u6015\u4e0d\u80fd\u62db\u8702\u5f15\u8776\u5438\u5f15\u90a3\u4e9b\u7537\u5b69\u5b50\u6765\u7f51\u5427\u5417\uff1f\u60f3\u5f53\u5e74\uff0c\u8fd8\u4e0d\u662f\u5e38\u62c9\u7740\u8499\u4e50\u3001\u675c\u98de\u53bb\u5bfb\u89c5\u90a3\u4e9b\u97f3\u4e50\u5b66\u9662\u3001\u5e08\u5927\u5973\u751f\u5e38\u53bb\u7684\u7f51\u5427\uff1f\u5f53\u7136\uff0c\u8499\u4e50\u3001\u675c\u98de\u4ed6\u4eec\u4e0d\u53ef\u80fd\u6709\u8fd9\u6837\u7684\u8bb0\u5fc6\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u5c31\u8fd9\u6837\uff1f\u201d\u5f20\u606a\u7684\u5efa\u8bae\u771f\u7684\u8ba9\u4eba\u5f88\u65e0\u5948\uff0c\u675c\u98de\u4f3c\u4e4e\u4e5f\u8ba4\u6e05\u5f20\u606a\u653e\u624b\u8ba9\u4ed6\u53bb\u8fd0\u4f5c\u7684\u610f\u56fe\uff0c\u4e5f\u5c31\u5bf9\u5f20\u606a\u6ca1\u6709\u592a\u591a\u7684\u6307\u671b\uff0c\u5176\u5b9e\u4ed6\u5185\u5fc3\u6df1\u5904\u4e5f\u60f3\u8ba4\u8ba4\u771f\u771f\u7684\u5c1d\u8bd5\u4e00\u4e0b\uff1b\u6307\u671b\u5f20\u606a\u8ba4\u771f\u7684\u5e2e\u4ed6\uff0c\u591a\u5c11\u6709\u4e9b\u4f5c\u5f0a\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u201c\u5c31\u8fd9\u6837\u554a\uff0c\u6211\u8fd8\u80fd\u6709\u4ec0\u4e48\u66f4\u9ad8\u5c1a\u4e0a\u7684\u60f3\u6cd5\u4e0d\u6210\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u644a\u5f00\u624b\uff0c\u95ee\u675c\u98de\uff0c\u201c\u6709\u521d\u6b65\u7684\u9884\u7b97\u51fa\u6765\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u6b63\u5728\u505a\uff0c\u201d\u675c\u98de\u8bf4\u9053\uff0c\u201c\u8499\u4e50\u8fd8\u60f3\u5728\u5a01\u5c14\u7279\u591a\u4f53\u4f1a\u51e0\u5929\uff0c\u6211\u6253\u7b97\u5148\u64a4\u51fa\u6765\uff0c\u6709\u4e9b\u60f3\u6cd5\u8fd8\u6ca1\u6709\u8ddf\u8499\u4e50\u5408\u8ba1\u5462\uff0c\u4f60\u65e2\u7136\u95ee\uff0c\u6211\u5c31\u5148\u62a5\u4e00\u4e2a\u5927\u6982\u7684\u6570\u5b57\u2026\u2026\u201d</p><p>\u3000\u3000\u675c\u98de\u6df7\u8fdb\u5a01\u5c14\u7279\u7f51\u5427\u4e5f\u5feb\u6709\u534a\u4e2a\u6708\u4e86\uff0c\u609f\u6027\u9ad8\u53c8\u52e4\u594b\u7684\u4eba\uff0c\u6709\u534a\u4e2a\u6708\u7684\u65f6\u95f4\u4e5f\u5c31\u591f\u4e86\uff0c\u518d\u8bf4\u5a01\u5c14\u7279\u4f5c\u4e3a\u5efa\u90ba\u7b2c\u4e00\u5bb6\u6b63\u89c4\u7684\u7f51\u5427\uff0c\u672c\u8eab\u5c31\u6709\u8bb8\u591a\u8bd5\u9a8c\u6027\u8d28\u7684\u4e1c\u897f\u5b58\u5728\uff0c\u5a01\u5c14\u7279\u73b0\u6709\u7684\u6a21\u5f0f\u672c\u8eab\u5c31\u6709\u5f88\u591a\u4e0d\u6210\u719f\u7684\u5730\u65b9\uff0c\u5173\u952e\u8fd8\u662f\u8981\u770b\u675c\u98de\u4ed6\u4eec\u81ea\u5df1\u6478\u7d22\u4f53\u4f1a\u7ecf\u8425\u4e4b\u9053\u3002</p><p>\u3000\u3000\u675c\u98de\u4e0d\u518d\u53bb\u5a01\u5c14\u7279\u7f51\u5427\u6253\u5de5\u3001\u5148\u64a4\u51fa\u6765\uff0c\u5c31\u662f\u60f3\u8981\u6b63\u5f0f\u5c06\u7f51\u5427\u5f53\u6210\u9879\u76ee\u6765\u8fd0\u4f5c\u4e86\u3002</p><p>\u3000\u3000\u675c\u98de\u4ece\u968f\u8eab\u7684\u630e\u5305\u91cc\u638f\u51fa\u4e00\u53ea\u5c0f\u672c\u5b50\uff0c\u4e0a\u9762\u51cc\u4e71\u7684\u5199\u6ee1\u4e00\u4e9b\u6570\u5b57\u4e0e\u6587\u5b57\uff0c\u8fd8\u6709\u4e71\u4e03\u516b\u7cdf\u7684\u793a\u610f\u56fe\uff0c\u8fd9\u4e9b\u4e1c\u897f\uff0c\u9664\u4e86\u675c\u98de\u672c\u4eba\uff0c\u5176\u4ed6\u4eba\u5f88\u96be\u770b\u660e\u767d\uff0c\u675c\u98de\u7ffb\u4e86\u7ffb\u4e86\u4e00\u756a\uff0c\u5927\u6982\u5c06\u4ed6\u8fd9\u4e9b\u5929\u6765\u7684\u60f3\u6cd5\u518d\u56de\u987e\u4e00\u4e0b\uff0c\u786e\u5207\u4e4b\u540e\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u4e00\u5343\u5e73\u7c73\u7684\u5e97\uff0c\u8981\u56db\u767e\u4e07\u7684\u9884\u7b97\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5443\uff01\u201d\u8499\u4e50\u8fd9\u4e24\u5929\u6ca1\u65e5\u6ca1\u591c\u7684\u5174\u594b\u7684\u8ba8\u8bba\u4e86\u5f88\u597d\u5f00\u7f51\u5427\u7684\u7ec6\u8282\uff0c\u6709\u4e00\u4e2a\u68a6\u60f3\uff0c\u5ffd\u7136\u5728\u5927\u5b66\u91cc\u5c31\u53ef\u4ee5\u53bb\u5b9e\u8df5\u3001\u53bb\u8ffd\u9010\uff0c\u603b\u6709\u7740\u96be\u4ee5\u6291\u5236\u7684\u5174\u594b\uff0c\u8499\u4e50\u90fd\u611f\u89c9\u81ea\u5df1\u8fd9\u4e24\u5929\u7684\u8bed\u901f\u6bd4\u5e73\u65f6\u8981\u5feb\u4e86\u8bb8\u591a\uff0c\u4e0d\u8fc7\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u8ddf\u675c\u98de\u8ba8\u8bba\u672a\u6765\u7f51\u5427\u7684\u89c4\u6a21\u3002\u4ed6\u603b\u4ee5\u4e3a\u5728\u5b66\u5e9c\u5df7\u5f00\u95f4\u767e\u5341\u5e73\u7c73\u7684\u7f51\u5427\u653e\u56db\u4e94\u5341\u53f0\u673a\u5b50\uff0c\u5df2\u7ecf\u76f8\u5f53\u4e86\u4e0d\u5f97\u4e86\uff0c\u6ca1\u60f3\u5230\u675c\u98de\u5f20\u53e3\u5c31\u662f\u56db\u767e\u4e07\u7684\u9884\u7b97\uff0c\u4ed6\u90fd\u6709\u4e9b\u7684\u72af\u50bb\uff0c\u5f20\u4e86\u5f20\u5634\uff0c\u90fd\u4e0d\u77e5\u9053\u600e\u4e48\u63d2\u8bdd\u624d\u597d\u3002\u8fd9\u4e9b\u5929\u63a5\u89e6\u4e0b\u6765\uff0c\u4ed6\u4e5f\u6653\u5f97\u5f20\u606a\u3001\u675c\u98de\u4e0d\u662f\u5bfb\u5e38\u4eba\u5bb6\u51fa\u8eab\uff0c\u4f46\u662f\u56db\u767e\u4e07\uff0c\u5bf9\u4e8e\u4e5d\u4e03\u5e74\u7684\u5927\u5b66\u751f\u6765\u8bf4\uff0c\u4f3c\u4e4e\u4e5f\u592a\u6709\u4e9b\u4e0d\u53ef\u601d\u8bae\u4e86\uff0c\u8c01\u5bb6\u91cc\u4f1a\u62ff\u56db\u767e\u4e07\u4e0d\u5f53\u56de\u4e8b\uff1f</p><p>\u3000\u3000\u89c1\u8499\u4e50\u7ed9\u5413\u7740\u7684\u6837\u5b50\uff0c\u5f20\u606a\u62cd\u4e86\u62cd\u4ed6\u7684\u80a9\u8180\uff0c\u671d\u7fdf\u4e39\u9752\u5476\u5476\u5634\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6ca1\u5fc5\u8981\u8003\u8651\u8d44\u91d1\u7684\u6765\u6e90\uff0c\u7fdf\u59d0\u5c31\u662f\u4e00\u5bcc\u5a46\u2026\u2026\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5a9a\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u53c8\u788d\u7740\u6211\u4ec0\u4e48\u4e8b\uff0c\u6211\u5750\u5728\u8fd9\u91cc\u5c31\u5f53\u542c\u6545\u4e8b\u5462\u3002\u201d</p><p>\u3000\u3000\u89c1\u5f20\u606a\u3001\u7fdf\u4e39\u9752\u90fd\u5bf9\u56db\u767e\u4e07\u8fd9\u4e2a\u6570\u5b57\u5f88\u4e0d\u4ee5\u4e3a\u7136\u7684\u6837\u5b50\uff0c\u8499\u4e50\u5fc3\u601d\u5012\u4e5f\u5b89\u5b9a\u4e0b\u6765\uff0c\u4ed6\u4e4b\u524d\u8fd8\u6253\u7b97\u8981\u4e0e\u675c\u98de\u5408\u4f5c\u7ecf\u8425\u8fd9\u5bb6\u7f51\u5427\uff0c\u542c\u5230\u675c\u98de\u6b64\u65f6\u8bf4\u51fa\u4ed6\u5fc3\u91cc\u771f\u6b63\u7684\u89c4\u5212\uff0c\u4e5f\u6653\u5f97\u6b64\u65f6\u7684\u81ea\u5df1\u53ea\u80fd\u662f\u534f\u52a9\u4ed6\u800c\u5df2\uff0c\u6bd5\u7adf\u56db\u767e\u4e07\u7684\u9879\u76ee\uff0c\u8fd8\u4e0d\u662f\u6b64\u65f6\u7684\u4ed6\u80fd\u8ddf\u522b\u4eba\u4e00\u8d77\u5171\u540c\u625b\u8d77\u6765\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u671d\u675c\u98de\u8bf4\uff1a\u201c\u8bf4\u7684\u8be6\u7ec6\u4e00\u4e9b\uff0c\u7fdf\u59d0\u4e5f\u5728\uff0c\u6307\u4e0d\u5b9a\u5174\u8da3\u6765\u4e86\u4f1a\u6307\u70b9\u4f60\u4eec\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u5e97\u79df\u8d39\u7528\u6bcf\u5e74\u970080\u4e07\uff0c\u6bd4\u7167\u5b59\u9759\u6aac\u90a3\u91cc\u7684\u88c5\u6f62\u9884\u7b97\u964d\u4e24\u683c\uff0c\u88c5\u6f62\u8d39\u7528\u970040\u4e07\uff0c\u56db\u767e\u5957\u7ec8\u7aef\u8bbe\u5907\u53ca\u5bb6\u4ff1\u8d2d\u7f6e\u8d39\u7528\u9700200\u4e07\uff0c\u7f51\u7edc\u8bbe\u5907\u548c\u7efc\u5408\u5e03\u7ebf15\u4e07\uff0c\u884c\u4e1a\u76d1\u7ba1\u8d39\u7528\u53ca\u4e13\u7ebf\u63a5\u516535\u4e07\uff0c\u8fd9\u4e9b\u90fd\u662f\u4e3b\u9879\uff0c\u53e6\u5916\u8fd8\u9700\u8981\u670920\u4e07\u7684\u6d41\u52a8\u8d44\u91d1\u3002\u201d\u675c\u98de\u5c06\u5927\u6982\u7684\u9884\u7b97\u6570\u5b57\u62a5\u4e86\u51fa\u6765\uff0c\u5dee\u4e0d\u591a\u56db\u767e\u4e07\u5de6\u53f3\uff0c\u53c8\u89c1\u4ed6\u5c06\u4ed6\u624b\u91cc\u7684\u5c0f\u672c\u5b50\u7ffb\u8fc7\u51e0\u9875\uff0c\u7ee7\u7eed\u8bf4\u9053\uff0c\u201c\u8fd0\u8425\u652f\u51fa\u5305\u62ec\u5458\u5de5\u5de5\u8d44\u3001\u6c34\u7535\u8d39\u3001\u4e13\u7ebf\u63a5\u5165\u8d39\u3001\u5e97\u79df\u3001\u516c\u5173\u3001\u7a0e\u8d39\u3001\u8bbe\u5907\u6298\u65e7\u7b49\u7b49\uff0c\u8fd9\u4e9b\u8fd8\u6ca1\u6709\u65f6\u95f4\u7ec6\u7b97\u3002\u8fd0\u8425\u641e\u4f1a\u5458\u5236\u8fd0\u8425\uff0c\u7f51\u8d39\u5206\u4f1a\u5458\u4e0e\u975e\u4f1a\u5458\u4e24\u7c7b\u6807\u51c6\uff0c\u9664\u4e86\u4e3b\u8425\u7f51\u8d39\u6536\u5165\u4e4b\u5916\uff0c\u8fd8\u6709\u996e\u6599\u9500\u552e\u7684\u6536\u5165\u4e5f\u662f\u5f88\u5927\u4e00\u5757\uff0c\u6211\u4eec\u5728\u5a01\u5c14\u7279\u90a3\u91cc\u89c2\u5bdf\uff0c\u5dee\u4e0d\u591a\u4f1a\u670940\uff05\u591a\u7684\u987e\u5ba2\u9700\u8981\u8d2d\u4e70\u996e\u6599\uff1b\u53e6\u5916\uff0c\u6211\u4e0e\u8499\u4e50\u5408\u8ba1\u7740\uff0c\u5b66\u5e9c\u5df7\u5e94\u8be5\u7b97\u5546\u4e1a\u8857\u533a\uff0c\u800c\u4e14\u6211\u4eec\u7684\u7f51\u5427\u4e3b\u8981\u9488\u5bf9\u9644\u8fd1\u9ad8\u6821\u4eba\u7fa4\uff0c\u5f88\u6709\u6295\u5165\u5e7f\u544a\u7684\u4ef7\u503c\uff0c\u5e7f\u544a\u4f4d\u79df\u552e\uff0c\u53ef\u4ee5\u4f5c\u4e3a\u53ef\u80fd\u7684\u8fd0\u8425\u6536\u5165\u6765\u8003\u8651\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u8003\u8651\u5230\u5e7f\u544a\u4f4d\u62db\u79df\u554a\uff0c\u201d\u5f20\u606a\u4e5f\u6ca1\u60f3\u5230\u675c\u98de\u4e0e\u8499\u4e50\u8fd8\u771f\u80fd\u4e24\u4e2a\u81ed\u76ae\u5320\u62b5\u4e00\u4e2a\u8bf8\u845b\u4eae\u5462\uff0c\u4e5f\u5e94\u8be5\u662f\u675c\u98de\u7684\u773c\u754c\u8ba9\u4ed6\u4eec\u7684\u601d\u7ef4\u66f4\u53d1\u6563\uff0c\u4ed6\u4eec\u6240\u8bbe\u60f3\u7684\u7f51\u5427\u8fd8\u771f\u6709\u96f6\u96f6\u5e74\u4e4b\u540e\u56fd\u5185\u624d\u51fa\u6765\u7684\u6982\u5ff5\u7f51\u5427\u7684\u96cf\u5f62\uff0c\u53ef\u60dc\u7231\u8fbe\u76ee\u524d\u7684\u4ea7\u54c1\u4e0d\u662f\u9488\u5bf9\u9ad8\u6821\u7fa4\u4f53\u7684\uff0c\u8fd9\u4e0a\u9762\u8fd8\u4e0d\u80fd\u76f4\u63a5\u5e2e\u4ed6\u4eec\u4e00\u628a\uff0c\u65e2\u7136\u6709\u8fd9\u4e2a\u5ff5\u5934\uff0c\u5de5\u4f5c\u603b\u80fd\u4e00\u70b9\u70b9\u505a\u8d77\u6765\uff0c\u70b9\u70b9\u5934\u79f0\u8d5e\u675c\u98de\u4ed6\u4eec\uff0c\u201c\u4e0d\u9519\uff0c\u4e0d\u9519\uff0c\u8fd9\u4e2a\u60f3\u6cd5\u5f88\u4e0d\u9519\uff0c\u542c\u5230\u73b0\u5728\uff0c\u6211\u90fd\u89c9\u5f97\u8fd9\u4e2a\u7f51\u5427\u5f88\u6709\u641e\u5934\u3002\u201d</p><p>\u3000\u3000\u8499\u4e50\u4e0d\u6653\u5f97\u5f97\u5230\u5f20\u606a\u7684\u79f0\u8d5e\u6709\u591a\u5f97\u610f\uff0c\u675c\u98de\u5374\u662f\u4fe1\u5fc3\u500d\u589e\uff0c\u8bf4\u9053\uff1a\u201c\u867d\u7136\u6709\u4e2a\u7b79\u5212\uff0c\u4e0d\u8fc7\u8981\u505a\u7684\u4e8b\u60c5\u5343\u5934\u4e07\u7eea\uff0c\u8d2d\u673a\u3001\u88c5\u6f62\u3001\u8425\u4e1a\u624b\u7eed\u7b49\u7b49\uff0c\u6211\u8fd8\u60f3\u627e\u4fe1\u606f\u5b66\u9662\u7684\u4eba\u505a\u4e00\u4e2a\u8ba1\u8d39\u7ba1\u7406\u4ee5\u53ca\u4f1a\u5458\u7ba1\u7406\u7cfb\u7edf\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u6709\u5f88\u591a\u6ca1\u6709\u8003\u8651\u5230\u7684\u4e8b\u60c5\u6307\u4e0d\u5b9a\u4ec0\u4e48\u65f6\u5019\u5c31\u5192\u51fa\u6765\u3002\u660e\u5929\uff0c\u4f60\u6709\u65f6\u95f4\u966a\u6211\u4eec\u53bb\u770b\u5e97\u9762\uff1f\u201d</p><p>\u3000\u3000\u201c\u884c\uff0c\u53cd\u6b63\u6211\u5927\u628a\u65f6\u95f4\u95f2\u4e5f\u662f\u95f2\u7740\u2026\u2026\u201d\u5f20\u606a\u723d\u5feb\u7684\u7b54\u5e94\u4e0b\u6765\uff0c\u53cd\u6b63\u4e5f\u662f\u8dd1\u8fc7\u53bb\u6311\u4e2a\u5730\u65b9\u8ddf\u90b5\u81f3\u521a\u62db\u547c\u4e00\u58f0\u800c\u5df2\uff0c\u5c31\u7b97\u4e0d\u80fd\u7ed9\u5e97\u79df\uff0c\u90b5\u81f3\u521a\u53c8\u80fd\u8bf4\u4ec0\u4e48\uff1f</p><p>\u3000\u3000\u201c\u8fd8\u662f\u4f60\u4eec\u597d\u554a\uff0c\u5e74\u7eaa\u8f7b\u8f7b\u5c31\u80fd\u5b9e\u8df5\u81ea\u5df1\u7684\u68a6\u60f3\u2026\u2026\u201d\u7fdf\u4e39\u9752\u611f\u6168\u7684\u4e00\u58f0\uff0c\u675c\u98de\u4e4b\u6240\u4ee5\u80fd\u505a\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u8fd8\u5728\u4e8e\u80cc\u540e\u6709\u5f20\u606a\u8fd9\u4e48\u4e00\u4e2a\u5f3a\u529b\u652f\u6491\u5728\uff0c\u4e0d\u7136\u7684\u8bdd\uff0c\u675c\u98de\u80fd\u529b\u518d\u5f3a\uff0c\u4ed6\u4ece\u54ea\u91cc\u53bb\u641e\u56db\u767e\u4e07\u7684\u542f\u52a8\u8d44\u91d1\uff1f</p><p>\u3000\u3000\u201c\u8fd8\u8981\u8bf7\u7fdf\u59d0\u4f60\u6307\u6559\u5462\u3002\u201d\u675c\u98de\u7b11\u7740\u8ba8\u5de7\u7684\u8bf4\u3002</p></article>"}}}]);
