(window.webpackJsonp=window.webpackJsonp||[]).push([[635],{658:function(p){p.exports={chapterTitle:null,title:"\u7b2c483\u7ae0 \u8bb0\u8005\u4f1a",content:"<article><h2>\u7b2c483\u7ae0 \u8bb0\u8005\u4f1a</h2><p>\u3000\u3000\u8ddf\u5341\u4e00\u6708\u5e95\u7ecf\u9500\u5546\u5927\u4f1a\u65f6\u7684\u60c5\u5f62\u4e00\u6837\uff0c\u79d1\u738b\u8981\u641e\u4ec0\u4e48\u6d3b\u52a8\uff0c\u8bb0\u8005\u5374\u5168\u7ed9\u7231\u8fbe\u90a3\u8fb9\u62c9\u8fc7\u53bb\u4e86\uff0c\u771f\u662f\u8981\u6c14\u6b7b\u4eba\u624d\u7518\u4f11\u3002\u8c22\u5251\u5357\u4e0d\u5f97\u4e0d\u51b3\u5b9a\u5c06\u4e0b\u5348\u7684\u6350\u8d60\u6d3b\u52a8\u79fb\u5230\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u8fdb\u884c\uff0c\u65f6\u95f4\u5b89\u6392\u4e0a\u8fd8\u8981\u7b49\u7231\u8fbe\u7535\u5b50\u7684\u8bb0\u8005\u4f1a\u53ec\u5f00\u5b8c\u6bd5\uff0c\u5fc3\u91cc\u7684\u82e6\u6c34\u90fd\u65e0\u6cd5\u627e\u4eba\u8bc9\u8bf4\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u3001\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u3001\u9648\u5bb6\u5584\u3001\u5468\u747e\u73ba\u7b49\u4eba\u966a\u540c\u82cf\u9038\u7fa4\u3001\u5468\u5bcc\u660e\u3001\u9646\u6ea2\u5149\u3001\u5f20\u6653\u5065\u518d\u4ece\u6587\u5c71\u996d\u5e97\u4e58\u8f66\u8d76\u5f80\u76ca\u9686\u9547\u3002\u81ea\u7136\u4e0d\u4f1a\u53bb\u7231\u8fbe\u7535\u5b50\u4e22\u4eba\uff0c\u4f46\u662f\u5f20\u606a\u641e\u51fa\u8fd9\u4e48\u5927\u7684\u67b6\u5f0f\uff0c\u4ed6\u4eec\u53c8\u4e0d\u53ef\u80fd\u88c5\u4f5c\u4e0d\u5173\u5fc3\uff0c\u4eba\u90fd\u5230\u4e1c\u8fb9\u7684\u79d1\u738b\u7535\u5668\u7ec4\u88c5\u5de5\u5382\u529e\u516c\u5ba4\u91cc\u7b49\u5019\u6d88\u606f\uff1b\u8c22\u77bb\u4e0e\u90c1\u840d\u4eba\u5728\u8bb0\u8005\u4f1a\u73b0\u573a\uff0c\u4f1a\u968f\u65f6\u5c06\u60c5\u51b5\u53cd\u9988\u8fc7\u6765\u3002</p><p>\u3000\u3000\u82cf\u9038\u7fa4\u591a\u5c11\u5bf9\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u5b58\u7740\u4e9b\u82a5\u8482\uff0c\u4e0d\u8fc7\u4ed6\u77e5\u9053\u8981\u60f3\u987a\u5229\u8c03\u5230\u6d77\u5dde\u5e76\u5728\u6d77\u5dde\u7ad9\u7a33\u811a\u8ddf\uff0c\u79bb\u5f00\u8c22\u5bb6\u662f\u4e0d\u53ef\u80fd\u7684\u3002\u4ed6\u4eca\u5e74\u4e94\u5341\u4e09\u4e86\uff0c\u518d\u4e0d\u4e0b\u53bb\u5c31\u6ca1\u4e0b\u53bb\u7684\u673a\u4f1a\u4e86\uff0c\u6bd4\u8d77\u7701\u653f\u5e9c\u79d8\u4e66\u957f\uff0c\u4ed6\u66f4\u5e0c\u671b\u5230\u5730\u65b9\u5f53\u51e0\u5e74\u5e02\u957f\u6216\u8005\u5e02\u59d4\u4e66\u8bb0\u66f4\u5b9e\u60e0\u4e9b\uff0c\u505a\u5b98\u662f\u8981\u8bb2\u7a76\u5b9e\u60e0\u7684\u3002\u60e0\u5c71\u3001\u5efa\u90ba\uff0c\u4ed6\u4e0d\u5962\u60f3\uff0c\u8fd1\u5e74\u6765\uff0c\u5c31\u6d77\u5dde\u7684\u7ecf\u6d4e\u53d1\u5c55\u6700\u4e3a\u8fc5\u901f\uff0c\u81ea\u4ece\u786e\u5b9a\u5f90\u5b66\u5e73\u4f1a\u79bb\u5f00\u4e1c\u6d77\uff0c\u4ed6\u5c31\u5728\u52a8\u8fd9\u4e2a\u5fc3\u601d\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u7efc\u5408\u884c\u653f\u697c\u7684\u4f1a\u8bae\u5927\u5385\uff0c\u6b64\u65f6\u7ed9\u5e03\u7f6e\u5728\u8bb0\u8005\u4f1a\u73b0\u573a\uff0c\u5de5\u4f5c\u4eba\u5458\u6b63\u6821\u51c6\u6295\u5f71\u4eea\uff0c\u8fde\u63a5\u7740\u7b14\u8bb0\u672c\u7535\u8111\uff0c\u4e5d\u516d\u5e74\uff0c\u8fd9\u5728\u56fd\u5185\u90fd\u662f\u65b0\u9c9c\u73a9\u827a\uff0c\u5f88\u591a\u5a92\u4f53\u8bb0\u8005\u90fd\u6ca1\u6709\u89c1\u8fc7\u7b14\u8bb0\u672c\u7535\u8111\u4e0e\u89c6\u9891\u6295\u5f71\u4eea\uff0c\u80f6\u7247\u7684\u5012\u662f\u89c1\u8fc7\u3002</p><p>\u3000\u3000\u90c1\u840d\u53cc\u817f\u4ea4\u53e0\u5750\u5728\u6700\u540e\u4e00\u6392\uff0c\u7ed9\u7231\u8fbe\u7684\u8b66\u536b\u8d34\u8eab\u76ef\u7740\uff0c\u5fc3\u91cc\u5f88\u4e0d\u8212\u670d\uff0c\u4eba\u5bb6\u5bf9\u81ea\u5df1\u6709\u6212\u5fc3\uff0c\u53c8\u4e0d\u80fd\u5c06\u8b66\u536b\u8d76\u8d70\uff0c\u53ea\u80fd\u6545\u4f5c\u9547\u5b9a\u7684\u62bb\u7740\u4fee\u957f\u7684\u8116\u5b50\u770b\u7740\u524d\u9762\uff0c\u82cf\u6d25\u4e1c\u3001\u9648\u4fe1\u751f\u3001\u5f20\u4e9a\u5e73\u3001\u8d75\u6709\u4f26\u3001\u90a3\u4e2a\u597d\u8272\u7684\u7f8e\u56fd\u4f6c\uff0c\u5f88\u591a\u4eba\u90fd\u4e0d\u8ba4\u8bc6\uff0c\u5979\u4f4e\u5934\u95ee\u8c22\u77bb\uff0c\u8c22\u77bb\u4e5f\u4e0d\u8ba4\u8bc6\uff0c\u51fa\u73b0\u5f88\u591a\u65b0\u9c9c\u9762\u5b54\u3002</p><p>\u3000\u3000\u201c\u7a76\u7adf\u8c01\u77e5\u9053\u7231\u8fbe\u8981\u641e\u4ec0\u4e48\u52a8\u4f5c\uff1f\u201d\u90c1\u840d\u90c1\u95f7\u7684\u62b1\u6028\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u201c\u90c1\u840d\u5c0f\u59d0\u8feb\u5207\u60f3\u77e5\u9053\u554a\uff0c\u6211\u53ef\u80fd\u4e3a\u4f60\u7b54\u7591\u89e3\u60d1\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u90c1\u840d\u4fa7\u5934\u770b\u89c1\u5f20\u606a\u5634\u89d2\u6302\u7740\u82e5\u6709\u82e5\u65e0\u7684\u90aa\u9b45\u7b11\u5bb9\u5750\u8fc7\u6765\uff0c\u90fd\u6709\u4e9b\u4e0d\u77e5\u6240\u63aa\uff0c\u8f6c\u5934\u770b\u4e86\u8c22\u77bb\u4e00\u773c\uff1b\u8c22\u77bb\u8138\u8272\u9634\u6c89\uff0c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5728\u4eba\u5bb6\u7684\u5730\u76d8\uff0c\u8fd9\u5c0f\u5b50\u6ca1\u6709\u51fa\u6076\u58f0\u6ca1\u5c06\u81ea\u5df1\u8d76\u51fa\u53bb\u5c31\u7b97\u597d\u7684\uff1b\u8c22\u77bb\u4e00\u70b9\u90fd\u4e0d\u6000\u7591\u8fd9\u5c0f\u5b50\u80fd\u505a\u51fa\u8fd9\u4e9b\u4e8b\u60c5\u6765\u3002</p><p>\u3000\u3000\u6ca1\u6709\u770b\u8c22\u77bb\uff0c\u5f20\u606a\u5fae\u4fa7\u7740\u5934\uff0c\u6ca1\u6709\u592a\u591a\u987e\u5fcc\u7684\u6253\u91cf\u7740\u8fd9\u4e2a\u5468\u5bcc\u660e\u7684\u5730\u4e0b\u60c5\u4eba\uff0c\u7a7f\u7740\u7d2b\u8272\u7ec7\u7f8a\u7ed2\u5916\u5957\uff0c\u770b\u6765\u5979\u5728\u79d1\u738b\u7684\u6536\u5165\u8fd8\u7b97\u4e0d\u9519\uff0c\u8fd9\u4ef6\u53e4\u5947\u5916\u5957\u5c11\u8bf4\u4e5f\u8981\u4e0a\u4e07\uff0c\u80a4\u8272\u767d\u5ae9\uff0c\u8138\u5f62\u4e5f\u662f\u6807\u51c6\u7684\u5375\u5f62\u8138\uff0c\u660e\u7738\u7693\u9f7f\uff0c\u5750\u5728\u90a3\u91cc\u4f53\u59ff\u5300\u4ead\u3001\u6c14\u8d28\u4f18\u96c5\uff0c\u7b97\u5f97\u4e0a\u5c11\u89c1\u7684\u7f8e\u4eba\uff0c\u5012\u4e0d\u6653\u5f97\u5979\u4f1a\u4e0d\u4f1a\u5728\u80cc\u540e\u4f7f\u529b\u8bf4\u670d\u5468\u5bcc\u660e\u7ed9\u82cf\u9038\u7fa4\u8ba9\u4f4d\u5b50\u3002</p><p>\u3000\u3000\u201c\u90c1\u5c0f\u59d0\u8feb\u5207\u60f3\u77e5\u9053\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u4fa7\u7740\u5934\u95ee\uff0c\u9f3b\u7aef\u80fd\u95fb\u89c1\u90c1\u840d\u8eab\u4e0a\u90a3\u79cd\u4e0d\u662f\u5f88\u660e\u663e\u5374\u8426\u7ed5\u4e0d\u6563\u7684\u9999\u6c14\uff0c\u5f88\u9ad8\u7aef\u7684\u9999\u6c34\u554a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5927\u6982\u7b97\u5f97\u4e0a\u6d77\u5dde\u6700\u6709\u6743\u52bf\u7684\u7537\u4eba\u4e4b\u4e00\u554a\uff0c\u8981\u662f\u77e5\u9053\u8fd9\u4e2a\u4eba\u7684\u8bdd\u3002</p><p>\u3000\u3000\u90c1\u840d\u5728\u773c\u524d\u8fd9\u4e2a\u5e74\u8f7b\u7684\u7537\u4eba\u9762\u524d\u6ca1\u6709\u592a\u591a\u7684\u81ea\u4fe1\uff0c\u4ed6\u7684\u773c\u775b\u9510\u5229\u800c\u6e05\u6f88\uff0c\u4f3c\u4e4e\u641c\u80a0\u522e\u809a\u7684\u5c06\u4eba\u5fc3\u770b\u4e86\u900f\u5f7b\uff0c\u867d\u7136\u4e0d\u6e05\u695a\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u5e74\u8f7b\u7684\u7537\u4eba\u4f1a\u7ed9\u81ea\u5df1\u8fd9\u79cd\u611f\u89c9\uff0c\u4f46\u662f\u77e5\u9053\u4ed6\u65e2\u7136\u80fd\u8ba9\u8fd9\u4e48\u591a\u4eba\u5fcc\u60ee\uff0c\u4e00\u5b9a\u6709\u4ed6\u975e\u51e1\u7684\u5730\u65b9\uff0c\u81ea\u5df1\u53ea\u662f\u7f3a\u4e4f\u4e86\u89e3\u7f62\u4e86\u3002</p><p>\u3000\u3000\u90a3\u8fb9\u82cf\u6d25\u4e1c\u8d70\u5230\u53f0\u4e0a\uff0c\u8138\u4e0a\u7684\u795e\u8272\u6ce2\u6f9c\u4e0d\u60ca\uff0c\u6709\u5982\u51fa\u73b0\u5728\u5bfb\u5e38\u7684\u8bb0\u8005\u4f1a\u4e00\u6837\uff0c\u8fb9\u4e0a\u53f0\u8fd8\u4e00\u8fb9\u8ddf\u5750\u5728\u53f0\u4e0b\u7684\u79d8\u4e66\u8bf4\u7740\u4ec0\u4e48\uff0c\u4f3c\u4e4e\u8fd8\u6709\u4ec0\u4e48\u4e8b\u60c5\u6ca1\u6709\u51c6\u5907\u59a5\u5f53\uff0c\u770b\u8d77\u6765\u8fd8\u771f\u50cf\u4ed3\u4fc3\u51c6\u5907\u7684\u8bb0\u8005\u4f1a\u554a\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u8bd5\u4e86\u8bd5\u8bdd\u7b52\uff0c\u5e03\u5728\u5929\u82b1\u677f\u7684\u97f3\u54cd\u53d1\u51fa\u6ecb\u6ecb\u7684\u54cd\u58f0\uff0c\u82cf\u6d25\u4e1c\u70b9\u4e86\u70b9\u5934\uff0c\u4f3c\u4e4e\u5728\u80af\u5b9a\u8bdd\u7b52\u6ca1\u6709\u95ee\u9898\uff0c\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\u5e9f\u8bdd\uff0c\u5c31\u76f4\u63a5\u8fdb\u5165\u4e3b\u9898\uff1a\u201c\u7231\u8fbe\u8fc7\u53bb\u4e00\u5e74\u591a\u6765\u505a\u7684\u4e00\u4e9b\u5de5\u4f5c\uff0c\u4e0d\u80fd\u8ba9\u67d0\u4e9b\u4eba\u6ee1\u610f\uff0c\u4eca\u5929\u53d7\u5230\u4e00\u4e9b\u6279\u8bc4\uff0c\u6211\u4eec\u865a\u5fc3\u63a5\u53d7\u6279\u8bc4\uff0c\u53ec\u96c6\u8bb0\u8005\u4f1a\u516c\u5e03\u4e24\u6761\u6d88\u606f\uff0c\u53ef\u80fd\u654f\u611f\u7684\u8bb0\u8005\u670b\u53cb\u5df2\u7ecf\u63d0\u524d\u77e5\u9053\u5176\u4e2d\u7684\u4e00\u6761\u6d88\u606f\uff0c\u7231\u8fbe\u6708\u524d\u4e0e\u9999\u6e2f\u56fd\u88d5\u6295\u8d44\u3001\u56fd\u5bcc\u63a7\u80a1\u4e24\u5bb6\u516c\u53f8\u7b7e\u7f72\u878d\u8d44\u534f\u8bae\uff0c\u7231\u8fbe\u7535\u5b50\u5411\u8fd9\u4e24\u5bb6\u516c\u53f8\u603b\u5171\u5b9a\u5411\u589e\u53d1\u4e00\u767e\u4e07\u80a1\u516c\u53f8\u80a1\u7968\uff0c\u52df\u96c6\u56db\u5343\u4e07\u7f8e\u91d1\u7684\u8d44\u91d1\uff0c\u878d\u8d44\u534f\u8bae\u5df2\u7ecf\u5230\u76f8\u5173\u653f\u5e9c\u90e8\u95e8\u5907\u6863\uff0c\u53ef\u80fd\u4e00\u4e9b\u5a92\u4f53\u670b\u53cb\u90fd\u6ca1\u6709\u5173\u5fc3\u5230\u8fd9\u4e2a\u6d88\u606f\uff0c\u8fd9\u91cc\u501f\u673a\u4f1a\u516c\u5e03\u4e00\u4e0b\uff0c\u52df\u96c6\u7684\u8d44\u91d1\uff0c\u5c06\u7528\u4e8e\u673a\u82af\u5de5\u5382\u9879\u76ee\u3001\u6280\u672f\u7814\u53d1\u4ee5\u53ca\u8865\u5145\u6d41\u52a8\u8d44\u91d1\uff0c\u7531\u4e8e\u8fd9\u6b21\u8bb0\u8005\u4f1a\u662f\u5728\u4ed3\u4fc3\u4e4b\u95f4\u4e3e\u884c\uff0c\u8d44\u91d1\u5206\u914d\u60c5\u51b5\u5c31\u4e0d\u5728\u8fd9\u91cc\u516c\u5e03\uff0c\u4f46\u662f\u9274\u4e8e\u67d0\u4e9b\u6279\u8bc4\u7684\u5b58\u5728\uff0c\u6211\u5728\u8fd9\u91cc\u4ee3\u8868\u7231\u8fbe\u7535\u5b50\u5ba3\u5e03\uff0c\u7528\u4e8e\u6280\u672f\u7814\u53d1\u7684\u8d44\u91d1\u4e0d\u4f1a\u4f4e\u4e8e\u4e00\u5343\u4e94\u767e\u4e07\u7f8e\u91d1\uff0c\u6211\u4eec\u4f1a\u5728\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u8f9f\u5730\u7b79\u5efa\u4e13\u95e8\u7684\u89c6\u542c\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\uff0c\u6211\u4eec\u8ba4\u4e3a\u8fd9\u662f\u4e00\u5bb6\u73b0\u4ee3\u4f01\u4e1a\u5e94\u505a\u7684\u6295\u8d44\uff0c\u4e0d\u503c\u5f97\u5927\u60ca\u5c0f\u602a\uff0c\u5960\u57fa\u4e4b\u65f6\u5c31\u4e0d\u4f1a\u989d\u5916\u7684\u53ec\u5f00\u65b0\u95fb\u53d1\u5e03\u4f1a\uff0c\u8fd8\u8bf7\u8bb0\u8005\u670b\u53cb\u4eec\u8c05\u89e3\uff0c\u4f60\u4eec\u53ef\u80fd\u8981\u5931\u53bb\u4e00\u6b21\u65b0\u95fb\u62a5\u9053\u7684\u673a\u4f1a\u2026\u2026\u201d</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u8bed\u8c03\u5e73\u6de1\uff0c\u4e0b\u9762\u7684\u8bb0\u8005\u90fd\u4f1a\u610f\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u7231\u8fbe\u7535\u5b50\u5ba3\u5e03\u8fd9\u6761\u6d88\u606f\uff0c\u5f53\u7136\u662f\u8ddf\u79d1\u738b\u4eca\u5929\u641e\u5f97\u7814\u53d1\u4e2d\u5fc3\u5960\u57fa\u5178\u793c\u9488\u950b\u76f8\u5bf9\uff0c\u79d1\u738b\u5c31\u6295\u8d44\u4e94\u5343\u4e07\u6e2f\u5e01\u5efa\u7814\u53d1\u4e2d\u5fc3\u5c31\u5927\u5f20\u65d7\u9f13\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u6295\u8d44\u989d\u662f\u79d1\u738b\u7684\u4e24\u500d\u591a\uff0c\u5374\u6709\u4e0d\u5c51\u4e00\u8c08\u7684\u6de1\u6f20\u3002\u4f46\u662f\u7231\u8fbe\u7535\u5b50\u589e\u53d1\u4e00\u767e\u4e07\u80a1\u65b0\u80a1\u5c31\u878d\u5f97\u56db\u5343\u4e07\u7f8e\u91d1\u7684\u5de8\u8d44\uff0c\u5413\u4e86\u5728\u573a\u7684\u8bb0\u8005\u4e00\u8df3\uff1a\u7231\u8fbe\u975e\u516c\u5f00\u53d1\u884c\u7684\u65b0\u80a1\u8fd9\u4e48\u503c\u94b1\uff0c\u4e00\u80a1\u5f97\u8981\u56db\u5341\u7f8e\u91d1\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u603b\u80a1\u672c\u662f\u591a\u5c11\uff1f</p><p>\u3000\u3000\u56fd\u5bcc\u63a7\u80a1\u662f\u975e\u4e0a\u5e02\u516c\u53f8\uff0c\u6295\u8d44\u6d88\u606f\u4e0d\u9700\u8981\u516c\u5f00\uff1b\u56fd\u88d5\u6295\u8d44\u662f\u9999\u6e2f\u516c\u53f8\uff0c\u5374\u5728\u82f1\u56fd\u4f26\u6566\u4ea4\u6613\u6240\u516c\u5f00\u4e0a\u5e02\uff0c\u5176\u6bcd\u516c\u53f8\u56fd\u88d5\u96c6\u56e2\u5728\u9999\u6e2f\u8054\u5408\u4ea4\u6613\u6240\u516c\u5f00\u4e0a\u5e02\uff0c\u56fd\u88d5\u6295\u8d44\u5411\u7231\u8fbe\u7535\u5b50\u63d0\u4f9b\u878d\u8d44\u7684\u6d88\u606f\uff0c\u56fd\u88d5\u6295\u8d44\u4e0e\u5176\u6bcd\u516c\u53f8\u56fd\u88d5\u96c6\u56e2\u90fd\u6709\u8fc7\u516c\u544a\uff0c\u4f46\u662f\u4e5d\u516d\u5e74\u5185\u5730\u7684\u8d22\u653f\u8bb0\u8005\u8fd8\u4e0d\u591f\u4e13\u4e1a\uff0c\u4e5f\u662f\u5f20\u606a\u4ed6\u4eec\u672c\u610f\u5c31\u662f\u60f3\u4f4e\u8c03\u4e00\u4e9b\uff0c\u4e5f\u6ca1\u6709\u4e3b\u52a8\u5411\u5a92\u4f53\u900f\u9732\u8fd9\u4e00\u6761\u6d88\u606f\uff0c\u5185\u5730\u7684\u5a92\u4f53\u8bb0\u8005\u90fd\u6ca1\u6709\u6ce8\u610f\u5230\u8fd9\u4e00\u70b9\uff0c\u8fd9\u65f6\u5019\u624d\u7ed9\u5413\u4e86\u4e00\u8df3\uff0c\u4e5f\u6ca1\u6709\u4eba\u5173\u5fc3\u8fc7\u7231\u8fbe\u7535\u5b50\u4f5c\u4e3a\u80a1\u4efd\u6709\u9650\u516c\u53f8\u7684\u603b\u80a1\u672c\u4e4b\u7c7b\u7684\u95ee\u9898\uff0c\u53c8\u4e0d\u662f\u516c\u5f00\u4e0a\u5e02\u7684\u80a1\u4efd\u516c\u53f8\uff0c\u4e00\u822c\u4eba\u53ea\u5173\u5fc3\u80a1\u4e1c\u4e4b\u95f4\u7684\u80a1\u6743\u6bd4\u4f8b\uff0c\u8c01\u4f1a\u5173\u5fc3\u603b\u80a1\u672c\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5374\u4e0d\u5f97\u4e0d\u5173\u5fc3\u8d77\u6765\uff0c\u8fd9\u6d89\u53ca\u5230\u7231\u8fbe\u7535\u5b50\u5230\u5e95\u503c\u591a\u5c11\u94b1\u7684\u95ee\u9898\uff0c\u5728\u53f0\u4e0b\u7684\u8bb0\u8005\u90fd\u5fcd\u4e0d\u4f4f\u7a83\u7a83\u79c1\u8bed\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u5750\u5728\u8fd9\u91cc\uff0c\u8c22\u77bb\u81ea\u7136\u5c31\u4e0d\u80fd\u53ca\u65f6\u7684\u7ed9\u8c22\u5251\u5357\u4ed6\u4eec\u6253\u7535\u8bdd\u901a\u62a5\u6d88\u606f\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u90c1\u840d\u3001\u8c22\u77bb\u90fd\u5f88\u5403\u60ca\u7684\u6837\u5b50\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u4eec\u5f88\u60ca\u8bb6\uff1f\u8c22\u5251\u5357\u524d\u4e9b\u5929\u4e0d\u662f\u4eb2\u81ea\u53bb\u9999\u6e2f\u9080\u8bf7\u845b\u660e\u4fe1\u3001\u845b\u840c\u5747\u7236\u5b50\u5417\uff1f\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u5728\u9999\u6e2f\u5c31\u77e5\u9053\u8fd9\u4e8b\u5462\uff0c\u8fd9\u6761\u6d88\u606f\uff0c\u9999\u6e2f\u5a92\u4f53\u6709\u520a\u767b\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u77bb\u542c\u5230\u5f20\u606a\u8fd9\u8bdd\uff0c\u7709\u5934\u76b1\u4e86\u76b1\uff0c\u8d77\u8eab\u6293\u8d77\u6401\u5728\u6905\u4e0a\u7684\u5916\u5957\uff0c\u8d70\u5230\u5916\u9762\u7ed9\u8c22\u5251\u5357\u6253\u7535\u8bdd\uff0c\u542c\u5230\u8c22\u5251\u5357\u5e73\u9759\u7684\u53e3\u6c14\uff0c\u663e\u7136\u4ed6\u662f\u77e5\u9053\u8fd9\u4ef6\u4e8b\u7684\u3002</p><p>\u3000\u3000\u90c1\u840d\u4fa7\u5934\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5f20\u606a\u5750\u76f4\u8eab\u5b50\u6b63\u6ce8\u89c6\u7740\u524d\u9762\uff0c\u5b8c\u5168\u60f3\u8c61\u4e0d\u51fa\u8fd9\u4e48\u5e74\u8f7b\u7684\u4e00\u4e2a\u7537\u5b50\uff0c\u7adf\u7136\u62e5\u6709\u5982\u6b64\u5e9e\u5927\u7684\u8d22\u5bcc\u3002</p><p>\u3000\u3000\u90c1\u840d\u5f53\u7136\u6e05\u695a\u8fd9\u4e2a\u5e74\u8f7b\u5f97\u8fc7\u5206\u7684\u7537\u4eba\u4e0e\u9526\u6e56\u3001\u4e0e\u7231\u8fbe\u7535\u5b50\u3001\u4e0e\u65b0\u5149\u7eb8\u4e1a\u7684\u5173\u7cfb\uff0c\u5c31\u7b97\u9526\u6e56\u6700\u65b0\u7684\u80a1\u6743\u5173\u7cfb\u8fd8\u6ca1\u6709\u53ca\u65f6\u5411\u5e02\u59d4\u62a5\u5907\uff0c\u4f46\u4e5f\u76f8\u5dee\u4e0d\u4e86\u591a\u5c11\uff0c\u8ba4\u771f\u8ba1\u7b97\u4e0b\u6765\uff0c\u8fd9\u4e2a\u5e74\u8f7b\u5f97\u8fc7\u5206\u7684\u7537\u4eba\u540d\u4e0b\u7adf\u7136\u81f3\u5c11\u6709\u4e8c\u4ebf\u516b\u5343\u4e07\u7f8e\u91d1\u7684\u8d44\u4ea7\uff01</p><p>\u3000\u3000\u4e8c\u4ebf\u516b\u5343\u4e07\u7f8e\u91d1\uff0c\u90c1\u840d\u5728\u5fc3\u91cc\u9ed8\u7b97\u6362\u6210\u4eba\u6c11\u5e01\u662f\u51e0\u4f4d\u6570\uff0c\u6d77\u5dde\u5e02\u4e5d\u516d\u5e74\u7684\u8d22\u653f\u6536\u5165\u80fd\u6709\u591a\u5c11\uff1f</p><p>\u3000\u3000\u8d22\u5bcc\u662f\u4e00\u4e2a\u7537\u4eba\u6700\u5177\u9b45\u529b\u7684\u7279\u8d28\uff0c\u90c1\u840d\u51dd\u89c6\u7740\u5f20\u606a\u7ebf\u6761\u5206\u660e\u7684\u660e\u4fca\u4fa7\u8138\uff0c\u7626\u524a\u7684\u9f3b\u7ffc\uff0c\u4ece\u4fa7\u9762\u770b\u8fc7\u53bb\u9644\u7740\u660e\u9510\u5149\u8292\u7684\u7738\u5b50\uff0c\u90fd\u5341\u5206\u7684\u8010\u770b\uff0c\u8fd9\u4e9b\u770b\u7740\u90fd\u6709\u4e9b\u5931\u795e\uff0c\u672a\u514d\u4f1a\u60f3\uff0c\u5230\u5e95\u54ea\u4e2a\u5973\u4eba\u624d\u80fd\u4eab\u53d7\u8fd9\u4e48\u7cbe\u81f4\u800c\u4f18\u96c5\u7684\u7537\u4eba\u3002\u5510\u5b66\u8c26\u7684\u5973\u513f\u5417\uff1f\u592a\u7a1a\u5ae9\u4e86\u3002\u60f3\u5230\u8c22\u665a\u6674\u5230\u6d77\u5dde\u540e\u4e00\u76f4\u4e0e\u5f20\u5bb6\u662f\u90bb\u5c45\uff0c\u800c\u5f20\u77e5\u884c\u592b\u5987\u53c8\u957f\u671f\u5728\u65b0\u829c\uff0c\u8c22\u665a\u6674\u8fd9\u4e2a\u7f8e\u8273\u7684\u5be1\u5987\u4f1a\u4e0d\u4f1a\u5c06\u8fd9\u4e48\u7f8e\u5c11\u5e74\u8bf1\u60d1\u5230\u5e8a\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u90c1\u840d\u8fd9\u4e48\u60f3\u7740\uff0c\u53cc\u817f\u4ea4\u53e0\u7740\u5750\u7740\uff0c\u4e24\u817f\u4e4b\u95f4\u7adf\u7136\u6d8c\u51fa\u4e9b\u75d2\u75d5\uff0c\u8eab\u5b50\u632a\u6b63\uff0c\u53cc\u817f\u53c8\u4e92\u6362\u4e86\u59ff\u52bf\u4ea4\u53e0\u5728\u4e00\u8d77\uff0c\u8f7b\u8f7b\u7684\u6469\u4e86\u4e00\u4e0b\uff0c\u5012\u6709\u4e9b\u6b62\u75d2\uff0c\u5979\u90fd\u5fcd\u4e0d\u4f4f\u62b1\u6028\u81ea\u5df1\u600e\u4e48\u53ef\u4ee5\u8fd9\u65f6\u5019\u6709\u8fd9\u79cd\u611f\u89c9\uff0c\u90fd\u6ca1\u6709\u5728\u610f\u542c\u82cf\u6d25\u4e1c\u5728\u53f0\u4e0a\u8bf4\u4ec0\u4e48\uff0c\u53ea\u770b\u89c1\u524d\u9762\u7684\u8bb0\u8005\u7a81\u7136\u90fd\u7ad9\u4e86\u8d77\u6765\uff0c\u5f80\u524d\u9762\u6d8c\u53bb\uff0c\u53ea\u542c\u89c1\u524d\u9762\u6709\u4eba\u5728\u8bf4\uff1a\u201c\u8d85\u7ea7VCD\uff0c\u56db\u5de8\u5934\u8054\u5408\u7814\u53d1\u51fa\u65b0\u4e00\u4ee3\u7684\u5f71\u789f\u673a\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u77bb\u521a\u5728\u5916\u9762\u7ed9\u8c22\u5251\u5357\u6253\u7535\u8bdd\uff0c\u8fd9\u65f6\u5019\u8d70\u8fdb\u6765\uff0c\u770b\u89c1\u8bb0\u8005\u90fd\u56f4\u5728\u524d\u9762\uff0c\u4e0d\u6653\u5f97\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u95ee\u90c1\u840d\uff1a\u201c\u53d1\u751f\u4ec0\u4e48\u4e8b\u4e86\uff1f\u201d</p></article>"}}}]);
