(window.webpackJsonp=window.webpackJsonp||[]).push([[636],{659:function(p){p.exports={chapterTitle:null,title:"\u7b2c484\u7ae0 \u4e00\u4e2a\u6559\u8bad",content:"<article><h2>\u7b2c484\u7ae0 \u4e00\u4e2a\u6559\u8bad</h2><p>\u3000\u3000\u90c1\u840d\u521a\u624d\u770b\u7740\u5f20\u606a\u8d70\u795e\uff0c\u4e0b\u9762\u90fd\u6709\u4e9b\u6e7f\u4e86\uff0c\u5fc3\u60f3\u81ea\u5df1\u662f\u7ecf\u4e0d\u4f4f\u8fd9\u7537\u4eba\u5f15\u8bf1\u7684\uff0c\u82cf\u6d25\u4e1c\u5728\u53f0\u4e0a\u8bf4\u4ec0\u4e48\uff0c\u5979\u6ca1\u6709\u542c\u6e05\u695a\uff0c\u8c22\u77bb\u8fd9\u65f6\u5019\u8dd1\u8fdb\u6765\u95ee\u5979\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5979\u90fd\u6709\u4e9b\u614c\u4e71\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5b89\u82e5\u6cf0\u5c71\u7684\u5750\u5728\u90a3\u91cc\uff0c\u89c6\u7ebf\u7ed9\u524d\u9762\u7ad9\u8d77\u6765\u7684\u8bb0\u8005\u6321\u4f4f\uff0c\u4e1d\u6beb\u4e0d\u4ecb\u7ecd\uff0c\u4fa7\u8fc7\u8eab\u5b50\u770b\u4e86\u90c1\u840d\u4e0e\u521a\u8d70\u8fdb\u6765\u7684\u8c22\u77bb\u4e00\u773c\uff0c\u89c1\u90c1\u840d\u7c89\u8138\u6709\u4e9b\u8273\u7ea2\uff0c\u773c\u775b\u91cc\u6d41\u9732\u51fa\u4e9b\u5a9a\u6001\uff0c\u5fc3\u60f3\u8fd9\u5973\u4eba\u5012\u662f\u4f1a\u5229\u7528\u81ea\u5df1\u7684\u8d44\u672c\uff0c\u5012\u4e5f\u6ca1\u6709\u610f\u8bc6\u90c1\u840d\u521a\u624d\u62ff\u4ed6\u5728\u610f\u6deb\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u53ea\u662f\u4e00\u6b3e\u6837\u673a\u800c\u5df2\uff0c\u6ca1\u6709\u4ec0\u4e48\u503c\u5f97\u5927\u60ca\u5c0f\u602a\u7684\u3002\u201d</p><p>\u3000\u3000\u521a\u624d\u82cf\u6d25\u4e1c\u5ba3\u5e03\u7231\u8fbe\u7535\u5b50\u83b7\u5f97\u56db\u5343\u4e07\u7f8e\u91d1\u7684\u878d\u8d44\uff0c\u90fd\u6ca1\u770b\u89c1\u8bb0\u90fd\u62e5\u5230\u524d\u9762\u53bb\uff0c\u8fd9\u65f6\u5019\u63a8\u51fa\u4e00\u6b3e\u789f\u673a\u6837\u673a\u5c31\u5f15\u8d77\u8fd9\u4e48\u5927\u7684\u8f70\u52a8\uff0c\u8c22\u77bb\u9690\u9690\u89c9\u5f97\u6709\u4e9b\u4e0d\u5999\uff0c\u5c31\u542c\u89c1\u7ed9\u8bb0\u8005\u56f4\u5728\u91cc\u9762\u7684\u82cf\u6d25\u4e1c\u62ff\u7740\u8bdd\u7b52\u5728\u8bf4\uff1a\u201c\u8bb0\u8005\u670b\u53cb\u4eec\uff0c\u8bf7\u5927\u5bb6\u7a0d\u6709\u4e9b\u8010\u5fc3\uff0c\u6211\u4eec\u5373\u5c06\u8054\u5408\u63a8\u51fa\u7684\u8fd9\u6b3e\u789f\u673a\u529f\u80fd\u5c06\u5168\u9762\u8d85\u8d8a\u5e02\u573a\u4e0a\u73b0\u6709\u7684\u4e3b\u6d41\u789f\u673a\u3002\u6211\u63a5\u4e0b\u6765\u4f1a\u62ab\u9732\u65b0\u789f\u673a\u6280\u672f\u5f00\u53d1\u7684\u7ec6\u8282\uff0c\u5404\u4f4d\u4e5f\u770b\u5230\uff0c\u98de\u5229\u6d66\u5143\u5668\u4ef6\u90e8\u4e2d\u56fd\u533a\u603b\u88c1\u5f20\u4e9a\u5e73\u5148\u751f\u3001\u7f8e\u56fdTI\u516c\u53f8\u4e2d\u56fd\u516c\u53f8\u603b\u88c1\u9648\u4fe1\u751f\u5148\u751f\u3001\u65af\u9ad8\u67cf\u516c\u53f8\u9ad8\u7ea7\u526f\u603b\u88c1\u3001\u5143\u5668\u4ef6\u90e8\u603b\u88c1\u827e\u9ed8\u5148\u751f\u90fd\u5728\u8fd9\u91cc\u3002\u65e9\u5728\u6570\u6708\u4e4b\u524d\uff0cTI\u516c\u53f8\u4e0e\u65af\u9ad8\u67cf\u516c\u53f8\u5c31\u6709\u8054\u5408\u5f00\u53d1\u65b0\u4e00\u4ee3\u89e3\u7801\u82af\u7247\u7684\u8ba1\u5212\uff0c\u5e76\u5404\u81ea\u5728\u76f8\u5173\u7814\u7a76\u9886\u57df\u53d6\u5f97\u4e00\u4e9b\u7a81\u7834\uff0c\u7279\u610f\u5728\u5341\u4e00\u6708\u521d\u8054\u5408\u7231\u8fbe\u7535\u5b50\u4e0e\u98de\u5229\u6d66\u5143\u5668\u4ef6\u90e8\u5171\u540c\u5f00\u53d1\u5916\u5c42\u652f\u6301\u6280\u672f\u3002\u76ee\u524d\uff0c\u7231\u8fbe\u5df2\u7ecf\u4e3a\u7814\u53d1\u7684\u5916\u5c42\u5e94\u7528\u6280\u672f\u5728\u4e3b\u8981\u56fd\u5bb6\u90fd\u7533\u8bf7\u4e86\u4e13\u5229\u4fdd\u62a4\uff0c\u4e3a\u4e86\u52a0\u5feb\u65b0\u789f\u673a\u7684\u63a8\u5e7f\uff0c\u7231\u8fbe\u7535\u5b50\u4f1a\u5c06\u8fd9\u4e9b\u5305\u62ec\u7cfb\u7edf\u63a7\u5236\u82af\u7247\u5728\u5185\u7684\u4e13\u5229\u6280\u672f\u8f6c\u8ba9\u7ed9\u53f0\u6e7e\u660e\u4fca\u7535\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u77bb\u6709\u4e9b\u53d1\u8499\uff0c\u4e00\u65f6\u90fd\u7406\u4f1a\u4e0d\u900f\u82cf\u6d25\u4e1c\u8bdd\u91cc\u7684\u610f\u601d\uff0c\u53ea\u662f\u5728\u60f3\u7231\u8fbe\u4e0eTI\u3001\u98de\u5229\u6d66\u3001\u65af\u9ad8\u67cf\u8054\u5408\u7814\u53d1\u51fa\u529f\u80fd\u5168\u9762\u8d85\u8d8a\u5f53\u524d\u5e02\u573a\u4e3b\u6d41\u789f\u673a\u7684\u4ea7\u54c1\uff0c\u8111\u888b\u55e1\u55e1\u4f5c\u54cd\uff0c\u52a0\u4e0a\u524d\u9762\u7684\u8bb0\u8005\u6839\u672c\u5c31\u6ca1\u6709\u505c\u6b62\u8bae\u8bba\uff0c\u73b0\u573a\u4e00\u7247\u5608\u6742\uff0c\u82cf\u6d25\u4e1c\u540e\u9762\u7684\u8bdd\u4ed6\u90fd\u6ca1\u6709\u542c\u6e05\u695a\u3002</p><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u2026\u2026\u201d\u4e00\u540d\u8bb0\u8005\u9996\u5148\u62a2\u8fc7\u8bdd\u7b52\uff0c\u5927\u5bb6\u90fd\u6ca1\u6709\u5750\u4e0b\u6765\uff0c\u4ed6\u5c31\u5750\u5230\u6905\u5b50\u4e0a\u53bb\u8bf4\u8bdd\uff0c\u201c\u6211\u662f\u7535\u5b50\u4ea7\u4e1a\u901a\u8baf\u7684\u8bb0\u8005\uff0c\u8bf7\u95ee\u82cf\u603b\uff0c\u7231\u8fbe\u7535\u5b50\u540c\u610f\u5c06\u5916\u5c42\u652f\u6301\u6280\u672f\u8f6c\u8ba9\u7ed9\u53f0\u6e7e\u660e\u4fca\u7535\u5b50\uff0c\u8bf7\u95ee\u662f\u51fa\u4e8e\u600e\u6837\u7684\u8003\u8651\uff1f\u6309\u7167\u4e00\u822c\u4eba\u7684\u7406\u89e3\uff0c\u5373\u4f7f\u5176\u4ed6\u789f\u673a\u5382\u5546\u60f3\u5f00\u53d1\u540c\u7b49\u6c34\u5e73\u7684\u5916\u5c42\u652f\u6301\u6280\u672f\uff0c\u4e5f\u81f3\u5c11\u9700\u8981\u534a\u5e74\u7684\u65f6\u95f4\uff0c\u4f55\u51b5\u8fd9\u6b21\u63d0\u51fa\u7684\u53c8\u662f\u6280\u672f\u5168\u9762\u5347\u7ea7\u7684\u65b0\u789f\u673a\uff0c\u6709\u8fd9\u81f3\u5c11\u534a\u5e74\u7684\u65f6\u95f4\uff0c\u7231\u8fbe\u7535\u5b50\u96be\u9053\u4e0d\u80fd\u5c06\u5e02\u573a\u4efd\u989d\u4ece\u76ee\u524d40\uff05\u63d0\u9ad8\u523050\uff05\u300160\uff05\u751a\u81f3\u66f4\u9ad8\uff1f\u201d</p><p>\u3000\u3000\u4ec0\u4e48\uff0c\u8c22\u77bb\u51e0\u4e4e\u6000\u7591\u81ea\u5df1\u662f\u542c\u9519\u4e86\uff0c\u7231\u8fbe\u7535\u5b50\u6ca1\u6709\u8981\u60f3\u7740\u51ed\u501f\u4e13\u5229\u6280\u672f\u72ec\u5360\u5e02\u573a\uff1f</p><p>\u3000\u3000\u4e0d\u9519\uff0c\u7231\u8fbe\u72ec\u5360\u5916\u5c42\u652f\u6301\u6280\u672f\uff0c\u5176\u4ed6\u789f\u673a\u5382\u5546\u8981\u7814\u53d1\u51fa\u66ff\u4ee3\u7684\u6280\u672f\uff0c\u81f3\u5c11\u9700\u8981\u534a\u5e74\u7684\u65f6\u95f4\uff0c\u8c22\u77bb\u90fd\u4e0d\u6e05\u695a\u79d1\u738b\u80fd\u4e0d\u80fd\u625b\u8fc7\u534a\u5e74\u7684\u65f6\u95f4\u3002</p><p>\u3000\u3000\u8c22\u77bb\u4e5f\u987e\u4e0d\u4e0a\u5f20\u606a\u5728\u4e00\u65c1\u864e\u89c6\u7708\u7708\uff0c\u6276\u7740\u6905\u5b50\u80cc\u5c31\u8981\u7ad9\u5230\u6905\u5b50\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4ed6\u5f88\u4e0d\u987a\u773c\uff0c\u7ed9\u540e\u9762\u7684\u8b66\u536b\u4f7f\u4e86\u773c\u8272\uff1b\u90a3\u8b66\u536b\u4e5f\u662f\u806a\u660e\uff0c\u4e0a\u53bb\u6293\u4f4f\u8c22\u77bb\u7684\u80f3\u812f\uff0c\u5c06\u4ed6\u62d6\u4e0b\u6765\uff0c\u8c22\u77bb\u4e5f\u6025\u4e86\uff0c\u56de\u5934\u4f4e\u543c\uff1a\u201c\u4f60\u5e72\u561b\u62fd\u4eba\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5750\u5728\u6905\u5b50\u8212\u670d\u7684\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u62ac\u773c\u770b\u7740\u8c22\u77bb\uff0c\u6de1\u6de1\u7684\u8bf4\u9053\uff1a\u201c\u4f3c\u4e4e\u8131\u6389\u978b\u5b50\u518d\u7ad9\u5230\u6905\u5b50\u4e0a\u4f1a\u7a0d\u4e3a\u793c\u8c8c\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u77bb\u773c\u775b\u90fd\u6c14\u7eff\u4e86\uff0c\u5728\u4eba\u5bb6\u7684\u5730\u76d8\u4e0a\u4e5f\u6ca1\u6709\u813e\u6c14\uff0c\u4f3c\u4e4e\u542c\u5230\u82cf\u6d25\u4e1c\u5728\u524d\u9762\u8bf4\u8bdd\uff0c\u4e0d\u6653\u5f97\u662f\u8bdd\u7b52\u51fa\u4e86\u95ee\u9898\u8fd8\u662f\u97f3\u54cd\u6709\u4e9b\u4e0d\u5bf9\u52b2\uff0c\u58f0\u97f3\u6ca1\u6709\u4f20\u51fa\u6765\uff0c\u8bb0\u8005\u4eec\u90fd\u56f4\u5728\u524d\u9762\u6ca1\u6709\u5b89\u9759\u4e0b\u6765\uff0c\u8c22\u77bb\u770b\u4e86\u90c1\u840d\u4e00\u773c\uff0c\u90c1\u840d\u8e2e\u7740\u811a\u5728\u770b\uff0c\u867d\u7136\u5979\u8eab\u6750\u9ad8\u6311\uff0c\u4f46\u7ec8\u7a76\u7ed9\u524d\u9762\u7684\u4eba\u6321\u4f4f\u89c6\u7ebf\uff0c\u4ec0\u4e48\u90fd\u770b\u4e0d\u5230\uff0c\u4e00\u8138\u7684\u7126\u6025\u3002\u8c22\u77bb\u53ea\u5f97\u89c4\u89c4\u77e9\u77e9\u8131\u4e86\u978b\u7ad9\u5230\u6905\u5b50\u4e0a\u5f80\u524d\u770b\uff0c\u82cf\u6d25\u4e1c\u4f4e\u5934\u5728\u8bd5\u8bdd\u7b52\uff0c\u4f3c\u4e4e\u771f\u662f\u8bdd\u7b52\u51fa\u4e86\u6bdb\u75c5\uff0c\u82cf\u6d25\u4e1c\u7684\u79d8\u4e66\u77ee\u7740\u8eab\u5b50\u2014\u2014\u4e0d\u60f3\u6321\u7740\u540e\u9762\u6444\u5f71\u7684\u2014\u2014\u8d70\u4e0a\u524d\uff0c\u62ff\u8fc7\u8bdd\u7b52\u8bd5\u4e86\u8bd5\uff0c\u53c8\u6709\u58f0\u9999\u4f20\u51fa\u6765\uff0c\u82cf\u6d25\u4e1c\u5c34\u5c2c\u7684\u7b11\u4e86\u7b11\uff1a\u201c\u8fd9\u79cd\u8bdd\u7b52\u6ca1\u600e\u4e48\u7528\u8fc7\uff0c\u4e00\u4e0d\u5c0f\u5fc3\u5c06\u5f00\u5173\u63ff\u4e86\u4e00\u4e0b\uff0c\u521a\u521a\u90a3\u4f4d\u8bb0\u8005\u670b\u53cb\u7684\u95ee\u9898\u2026\u2026\u201d\u8c22\u77bb\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u82cf\u6d25\u4e1c\u6280\u672f\u51fa\u8eab\uff0c\u4f1a\u4e0d\u6653\u5f97\u8bdd\u7b52\u600e\u4e48\u7528\uff1f\u88e4\u515c\u91cc\u7684\u624b\u673a\u5374\u5728\u8fd9\u65f6\u5019\u54cd\u4e86\uff0c\u624b\u673a\u94c3\u58f0\u5f88\u54cd\uff0c\u6709\u51e0\u540d\u8bb0\u8005\u56de\u5934\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u610f\u6307\u4ed6\u4f1a\u573a\u7adf\u7136\u4e0d\u4f1a\u5c06\u624b\u673a\u8c03\u5230\u632f\u52a8\u3002\u8c22\u77bb\u62ff\u51fa\u6765\u4e00\u770b\uff0c\u662f\u8c22\u5251\u5357\u7684\u7535\u8bdd\uff0c\u4ed6\u770b\u4e86\u770b\u90c1\u840d\uff0c\u8e2e\u7740\u811a\u53c8\u770b\u4e0d\u5230\u4ec0\u4e48\uff0c\u6015\u5979\u542c\u6f0f\u4e86\uff0c\u4e5f\u4e0d\u7ba1\u5f20\u606a\u8fd9\u5c0f\u5b50\u5c31\u5728\u8eab\u8fb9\uff0c\u76f4\u63a5\u63a5\u901a\u8c22\u5251\u5357\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u7231\u8fbe\u7814\u53d1\u51fa\u7684\u65b0\u789f\u673a\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u8c22\u5251\u5357\u6025\u5207\u7684\u58f0\u97f3\u4f20\u8fc7\u6765\uff0c\u4e5f\u4e0d\u6653\u5f97\u73b0\u573a\u6709\u54ea\u4f4d\u8bb0\u8005\u6025\u7740\u5c06\u6d88\u606f\u4f20\u4e86\u8fc7\u53bb\uff0c\u6d88\u606f\u652f\u79bb\u7834\u788e\uff0c\u90a3\u8fb9\u518d\u4e5f\u6309\u5948\u4e0d\u4f4f\u6253\u7535\u8bdd\u8fc7\u6765\u95ee\u7a76\u7adf\u3002</p><p>\u3000\u3000\u8c22\u77bb\u8fd8\u6ca1\u6709\u641e\u6e05\u695a\u72b6\u51b5\uff0c\u90fd\u4e0d\u6653\u5f97\u600e\u4e48\u8ddf\u4ed6\u89e3\u91ca\uff1b\u5f20\u606a\u53c8\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u624b\u5374\u6ca1\u6709\u6536\u56de\u53bb\uff1a\u201c\u8c22\u5bb6\u5c0f\u516d\u7684\u7535\u8bdd\uff1f\u6211\u521a\u521a\u90fd\u8bf4\u4e86\uff0c\u4f60\u4eec\u6709\u4ec0\u4e48\u4e0d\u4e86\u89e3\uff0c\u6211\u53ef\u4ee5\u7b54\u7591\u89e3\u60d1\u7684\u2026\u2026\u201d\u5f20\u606a\u7684\u5634\u89d2\u8fd8\u6302\u7740\u90a3\u79cd\u7edd\u5bf9\u662f\u6b20\u63cd\u3001\u8ba9\u4eba\u770b\u4e86\u4f1a\u65e0\u6bd4\u7a9d\u5fc3\u7684\u6d45\u7b11\u3002</p><p>\u3000\u3000\u8f93\u9635\u4e0d\u8f93\u4eba\uff0c\u8c22\u77bb\u8fd9\u70b9\u9aa8\u6c14\u8fd8\u662f\u6709\u7684\uff0c\u6ca1\u6709\u7406\u4f1a\u5f20\u606a\u7684\u6311\u8845\uff0c\u4e00\u53ea\u624b\u6276\u7740\u6905\u80cc\uff0c\u5c06\u624b\u673a\u5939\u7740\u5934\u80a9\u4e4b\u95f4\uff0c\u542c\u5230\u8c22\u5251\u5357\u53c8\u5728\u7535\u8bdd\u90a3\u5934\u6025\u5207\u95ee\u8d77\uff0c\u5b9a\u4e86\u5b9a\u795e\uff0c\u56de\u7b54\u9053\uff1a\u201c\u60c5\u51b5\u4e0d\u662f\u5f88\u6e05\u695a\uff0cTI\u4e0e\u65af\u9ad8\u67cf\u8054\u5408\u5f00\u53d1\u51fa\u65b0\u4e00\u4ee3\u7684\u89e3\u7801\u82af\u7247\uff0c\u53c8\u8054\u5408\u7231\u8fbe\u3001\u98de\u5229\u6d66\u5f00\u53d1\u51fa\u4e86\u6240\u6709\u5916\u5c42\u5e94\u7528\u6280\u672f\uff0c\u4ed6\u4eec\u5df2\u7ecf\u505a\u51fa\u6027\u80fd\u7b26\u5408\u8981\u6c42\u7684\u6837\u673a\uff0c\u7231\u8fbe\u4f3c\u4e4e\u4e0d\u4f1a\u72ec\u5360\u5916\u5c42\u6280\u672f\uff0c\u540c\u610f\u5c06\u5916\u5c42\u5e94\u7528\u6280\u672f\u8f6c\u8ba9\u7ed9\u660e\u4fca\u7535\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u641e\u6e05\u695a\u60c5\u51b5\u4e4b\u540e\u518d\u6253\u7535\u8bdd\u56de\u6765\u5427\uff1f\u201d\u7535\u8bdd\u90a3\u5934\u7684\u8c22\u5251\u5357\u4f3c\u4e4e\u4e5f\u610f\u8bc6\u5230\u81ea\u5df1\u6709\u4e9b\u5931\u6001\u4e86\uff0c\u58f0\u97f3\u8f6c\u7f13\u4e86\u4e00\u4e9b\uff0c\u5c31\u5c06\u7535\u8bdd\u6302\u6389\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u4e00\u4e2a\u534a\u6708\u524d\uff0c\u6211\u4eec\u5c31\u8868\u660e\u4e86\u7acb\u573a\uff0c\u4e0d\u4f1a\u5229\u7528\u6280\u672f\u4e0a\u3001\u6210\u672c\u4e0a\u7684\u4f18\u52bf\u53bb\u503e\u8f67\u540c\u884c\u5382\u5546\uff0c\u8fd9\u53e5\u8bdd\u4e0d\u662f\u8bf4\u8bf4\u800c\u5df2\uff0c\u201d\u82cf\u6d25\u4e1c\u7684\u58f0\u97f3\u4f20\u8fc7\u6765\uff0c\u201c\u5373\u4f7f\u73b0\u5728\uff0c\u7231\u8fbe\u7535\u5b50\u51ed\u501f\u81ea\u8eab\u7684\u6280\u672f\u529b\u91cf\u5f00\u53d1\u51fa\u65b0\u789f\u673a\u6240\u6709\u5916\u5c42\u6280\u672f\u4e2d\u7684\u7edd\u5927\u90e8\u5206\uff0c\u4e5f\u4e0d\u4f1a\u968f\u610f\u8c03\u6574\u5e74\u524d\u5c31\u62df\u5b9a\u597d\u7684\u5e02\u573a\u76ee\u6807\u2026\u2026\u4e0e\u660e\u4fca\u7535\u5b50\u5206\u4eab\u6211\u4eec\u6240\u7814\u53d1\u51fa\u6765\u7684\u4e13\u5229\u6280\u672f\uff0c\u4e5f\u5c31\u662f\u4e0e\u540c\u884c\u789f\u673a\u5382\u5546\u5206\u4eab\u6211\u4eec\u6240\u7814\u53d1\u51fa\u6765\u7684\u4e13\u5229\u6280\u672f\uff0c\u8fd9\u4e48\u505a\uff0c\u9996\u5148\u6709\u5229\u4e8e\u65b0\u789f\u673a\u5728\u5e02\u573a\u4e0a\u7684\u5e94\u7528\u63a8\u5e7f\uff0c\u8fd8\u6709\u4e00\u4e2a\uff0c\u7231\u8fbe\u7535\u5b50\u4e0e\u660e\u4fca\u7535\u5b50\u5728\u5408\u4f5c\u4e2d\u4f1a\u76f8\u4e92\u5207\u78cb\uff0c\u8fd9\u80fd\u63d0\u9ad8\u7231\u8fbe\u7535\u5b50\u7684\u7814\u53d1\u6c34\u5e73\u2026\u2026\u201d</p><p>\u3000\u3000\u4e71\u4e86\u4e00\u9635\uff0c\u8fd9\u65f6\u5019\u573a\u9762\u7a0d\u7a0d\u53d7\u63a7\u4e00\u4e9b\uff0c\u4e0d\u8fc7\u5f88\u591a\u8bb0\u8005\u90fd\u4f38\u51fa\u624b\u8981\u63a5\u82cf\u6d25\u4e1c\u79d8\u4e66\u624b\u91cc\u7684\u8bdd\u7b52\uff0c\u82cf\u6d25\u4e1c\u79d8\u4e66\u5c06\u8bdd\u7b52\u9012\u5230\u4e00\u540d\u957f\u76f8\u6e05\u79c0\u7684\u5973\u8bb0\u8005\u624b\u91cc\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u4e1c\u65b9\u591a\u5a92\u4f53\u5468\u520a\u7684\u8bb0\u8005\uff0c\u82cf\u603b\u80fd\u4e0d\u80fd\u8fdb\u4e00\u6b65\u900f\u9732\u4e00\u4e0b\u7231\u8fbe\u7535\u5b50\u4e0e\u53f0\u6e7e\u660e\u4f26\u7535\u5b50\u7684\u5408\u4f5c\u7ec6\u8282\uff0c\u6211\u60f3\u7231\u8fbe\u7535\u5b50\u4e0d\u4f1a\u514d\u8d39\u5c06\u4e13\u5229\u6280\u672f\u8f6c\u8ba9\u51fa\u53bb\uff0c\u6211\u4eec\u4e1c\u65b9\u591a\u5a92\u4f53\u5468\u520a\u9884\u6d4b\u7231\u8fbe\u7535\u5b50\u8c03\u6574\u5e02\u573a\u76ee\u6807\u4e4b\u540e\uff0c\u7531\u4e8e\u789f\u673a\u552e\u4ef7\u7684\u81ea\u7136\u4e0b\u6ed1\uff0c\u5373\u4f7f\u7231\u8fbe\u7535\u5b50\u7684\u673a\u82af\u5de5\u5382\u80fd\u5728\u4e0a\u534a\u5e74\u987a\u5229\u6295\u8d44\uff0c\u4e5d\u4e03\u5e74\u7684\u8d62\u5229\u4e5f\u4f1a\u6709\u5c0f\u5e45\u5ea6\u7684\u4e0b\u964d\uff0c\u8fd9\u6b21\u6280\u672f\u8f6c\u8ba9\uff0c\u4f1a\u4e0d\u4f1a\u5f97\u5230\u4e00\u4e9b\u8865\u507f\u2026\u2026\u201d</p><p>\u3000\u3000\u8bdd\u7b52\u4ece\u5973\u8bb0\u8005\u624b\u91cc\u53c8\u4f20\u56de\u5230\u82cf\u6d25\u4e1c\u624b\u91cc\uff0c\u5176\u4ed6\u8bb0\u8005\u53ea\u662f\u5c3d\u53ef\u80fd\u5c06\u5f55\u97f3\u7b14\u9012\u5230\u82cf\u6d25\u4e1c\u5634\u524d\uff1a\u201c\u6709\u4e00\u70b9\u6211\u8981\u7ea0\u6b63\u4e00\u4e0b\uff0c\u7231\u8fbe\u7535\u5b50\u4e00\u76f4\u8ba1\u5212\u673a\u82af\u5de5\u5382\u662f\u80fd\u5728\u7b2c\u4e00\u5b63\u5ea6\u5c31\u5b8c\u6210\u6295\u4ea7\u3002\u673a\u82af\u5de5\u5382\u7684\u5382\u623f\u65e9\u5728\u5e74\u524d\u5c31\u5df2\u7ecf\u5efa\u8bbe\u5b8c\u6210\uff0c\u751f\u4ea7\u8bbe\u5907\u5df2\u7ecf\u5230\u5382\u5f00\u59cb\u5b89\u88c5\uff0c\u5c31\u7b49\u7740\u6211\u4eec\u5728\u98de\u5229\u6d66\u4e0a\u6d77\u5de5\u5382\u57f9\u8bad\u7684\u5458\u5de5\u987a\u5229\u7ed3\u4e1a\uff0c\u5c31\u53ef\u4ee5\u5f00\u673a\u8c03\u8bd5\u3002\u6211\u4eec\u6709TI\u3001\u98de\u5229\u6d66\u63d0\u4f9b\u7684\u6210\u719f\u6280\u672f\uff0c\u6211\u4eec\u673a\u82af\u5de5\u5382\u7684\u7ba1\u7406\u4eba\u5458\u6709\u76f8\u5f53\u4e00\u90e8\u5206\u6709\u8fc7\u76f8\u5173\u5728\u7814\u53d1\u4e0a\u3001\u751f\u4ea7\u7ba1\u7406\u4e0a\u7684\u7ecf\u9a8c\uff0c\u6211\u60f3\u6211\u4eec\u7684\u673a\u82af\u5de5\u5382\u9879\u76ee\u4f1a\u6bd4\u8f83\u987a\u5229\u7684\u63a8\u8fdb\uff1b\u81f3\u4e8e\u4e0e\u660e\u4fca\u7535\u5b50\u7684\u5408\u4f5c\u7ec6\u8282\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u8bf7\u660e\u4fca\u7535\u5b50\u7684\u8d75\u603b\u7ed9\u5927\u5bb6\u6765\u4ecb\u7ecd\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u8c22\u77bb\u770b\u7740\u82cf\u6d25\u4e1c\u5c06\u8bdd\u7b52\u9012\u7ed9\u8d75\u6709\u4f26\uff0c\u5373\u4f7f\u7231\u8fbe\u5c06\u6280\u672f\u8f6c\u8ba9\u7ed9\u660e\u4fca\uff0c\u5bf9\u79d1\u738b\u4e5f\u9887\u4e3a\u4e0d\u5229\u3002\u79d1\u738b\u63a5\u6536\u5609\u4fe1\u7535\u5b50\u6ce8\u8d44\u4e4b\u540e\u5c31\u4e2d\u6b62\u4e0e\u660e\u4fca\u7535\u5b50\u5728\u5916\u5c42\u6280\u672f\u5f00\u53d1\u4e0a\u7684\u5408\u4f5c\uff0c\u4e0e\u660e\u4fca\u7535\u5b50\u6062\u590d\u5230\u4e00\u822c\u8d38\u6613\u5173\u7cfb\uff0c\u4ed6\u8fd8\u5728\u8003\u8651\u7231\u8fbe\u7535\u5b50\u4e0e\u660e\u4fca\u7535\u5b50\u4e4b\u95f4\u4f1a\u7b7e\u600e\u6837\u7684\u8f6c\u8ba9\u8bae\u534f\uff0c\u8003\u8651\u8981\u5982\u4f55\u4e0e\u660e\u4fca\u7535\u5b50\u6062\u590d\u5173\u7cfb\uff0c\u5374\u542c\u5230\u5f20\u606a\u5728\u4e00\u65c1\u5356\u5f04\u7684\u4fa7\u7740\u5934\u8ddf\u90c1\u840d\u8bf4\u9053\uff1a\u201c\u4e0d\u7528\u542c\u90a3\u4e2a\u8d75\u9ebb\u5b50\u8bf4\u5b98\u9762\u8bdd\uff0c\u4f60\u4eec\u4ec0\u4e48\u6709\u7528\u7684\u4fe1\u606f\u90fd\u542c\u4e0d\u51fa\u6765\uff0c\u201d\u8d75\u6709\u4f26\u8138\u4e0a\u6709\u51e0\u7c92\u767d\u9ebb\u70b9\uff0c\u4e0d\u6ce8\u610f\u8fd8\u770b\u4e0d\u51fa\u6765\uff0c\u5f20\u606a\u5728\u4e0b\u9762\u4e5f\u4e0d\u865e\u4ed6\u4f1a\u542c\u89c1\uff0c\u201c\u6211\u6765\u544a\u8bc9\u4f60\u4eec\u6280\u672f\u8f6c\u8ba9\u534f\u8bae\u7684\u7ec6\u8282\u5427\uff0c\u65e0\u975e\u5c31\u662f\u4e24\u70b9\uff0c\u660e\u4fca\u7535\u5b50\u6839\u636e\u7231\u8fbe\u7535\u5b50\u63d0\u4f9b\u7684\u5916\u5c42\u652f\u6301\u6280\u672f\u751f\u4ea7\u63a7\u5236\u82af\u7247\uff0c\u7231\u8fbe\u7535\u5b50\u4ece\u6bcf\u7247\u63a7\u5236\u82af\u7247\u91cc\u62bd\u53d6\u552e\u4ef712\uff05\u7684\u91d1\u989d\u4f5c\u4e3a\u6280\u672f\u8f6c\u8ba9\u8d39\u7528\uff0c\u540c\u65f6\u7ea6\u5b9a\u660e\u4fca\u7535\u5b50\u6700\u591a\u540c\u65f6\u5411\u516d\u5bb6\u789f\u673a\u5382\u5546\u63d0\u4f9b\u63a7\u5236\u82af\u7247\u7684\u6d4b\u8bd5\u6837\u54c1\u2026\u2026\u81f3\u4e8e\u660e\u4fca\u7535\u5b50\u4f1a\u4e0d\u4f1a\u5c06\u79d1\u738b\u6392\u9664\u5728\u8fd9\u516d\u5bb6\u4e4b\u5916\uff0c\u7231\u8fbe\u7535\u5b50\u4e0e\u660e\u4fca\u7535\u5b50\u79c1\u4e0b\u91cc\u771f\u7684\u6ca1\u6709\u7acb\u4ec0\u4e48\u79d8\u5bc6\u534f\u8bae\u2026\u2026\u201d\u5f20\u606a\u8bf4\u5230\u8fd9\u91cc\uff0c\u5c31\u5f88\u671f\u5f85\u7684\u770b\u7740\u8c22\u77bb\u3001\u90c1\u840d\u4e24\u4eba\u8138\u4e0a\u7684\u8868\u60c5\u6709\u4ec0\u4e48\u53d8\u5316\u3002</p><p>\u3000\u3000\u4e5f\u5c31\u662f\u8bf4\u79d1\u738b\u5411\u660e\u4fca\u7535\u5b50\u91c7\u8d2d\u63a7\u5236\u82af\u7247\uff0c\u5c06\u670912\uff05\u7684\u91c7\u8d2d\u6b3e\u4f1a\u6d41\u5165\u7231\u8fbe\u7535\u5b50\u7684\u8170\u5305\uff0c\u8fd9\u4e00\u70b9\u8fd8\u597d\uff0c\u63a7\u5236\u82af\u7247\u7684\u552e\u4ef7\u8f83\u4f4e\uff0c\u53ea\u5360\u5230\u6574\u673a\u751f\u4ea7\u6210\u672c\u76845-8\uff05\uff0c\u4f46\u662f\u4e00\u589e\u4e00\u964d\uff0c\u53c8\u8fdb\u4e00\u6b65\u62c9\u5f00\u4e24\u5bb6\u4e4b\u95f4\u7684\u6210\u672c\u5dee\u8ddd\u3002\u770b\u7740\u5f20\u606a\u8fd9\u4ed8\u8868\u60c5\uff0c\u8c22\u77bb\u5fc3\u91cc\u7684\u786e\u90c1\u95f7\uff0c\u4e0d\u8fc7\u6700\u8ba9\u4ed6\u96be\u53d7\u7684\u662f\uff0c\u7231\u8fbe\u7535\u5b50\u7adf\u7136\u9650\u5236\u660e\u4fca\u7535\u5b50\u6700\u591a\u5411\u516d\u5bb6\u789f\u673a\u5382\u5546\u63d0\u4f9b\u6d4b\u8bd5\u82af\u7247\uff0c\u5374\u662f\u72e0\u72e0\u7684\u6446\u4e86\u79d1\u738b\u4e00\u9053\u3002</p><p>\u3000\u3000\u65b0\u789f\u673a\u4e4b\u5e02\u4e4b\u524d\uff0c\u5143\u4ef6\u5668\u4f9b\u5e94\u5382\u5546\u82e5\u80fd\u63d0\u4f9b\u6d4b\u8bd5\u7528\u7684\u5143\u5668\u4ef6\u6837\u54c1\uff0c\u80fd\u6700\u5927\u7a0b\u5ea6\u7684\u7f29\u77ed\u6574\u673a\u7ec4\u88c5\u8c03\u8bd5\u7684\u65f6\u95f4\uff0c\u540c\u6b65\u5de5\u4f5c\u505a\u5f97\u597d\uff0c\u51e0\u4e4e\u53ea\u8981\u6709\u8d28\u91cf\u7a33\u5b9a\u7684\u5143\u5668\u4ef6\u63d0\u4f9b\uff0c\u789f\u673a\u5382\u5546\u4e5f\u80fd\u540c\u4e00\u5929\u7ec4\u88c5\u51fa\u8d28\u91cf\u7a33\u5b9a\u7684\u789f\u673a\u51fa\u6765\u3002\u8981\u662f\u4e0d\u80fd\u63d0\u524d\u5f97\u5230\u5143\u5668\u4ef6\u6837\u54c1\u8fdb\u884c\u7ec4\u88c5\u6d4b\u8bd5\uff0c\u5c31\u8981\u5728\u90a3\u4e9b\u4f9b\u5e94\u5546\u6b63\u5f0f\u5411\u5e02\u573a\u63a8\u51fa\u4ea7\u54c1\u4e4b\u540e\u624d\u91c7\u8d2d\u8fdb\u5382\u8fdb\u884c\u7ec4\u88c5\u6d4b\u8bd5\uff0c\u7ec4\u88c5\u6d4b\u8bd5\u7684\u65f6\u95f4\u5c31\u8981\u767d\u767d\u7684\u803d\u6401\u6389\u3002\u79d1\u738b\u5c06\u4e0d\u4ec5\u4ec5\u843d\u540e\u4e8e\u7231\u8fbe\uff0c\u8fd8\u8981\u843d\u540e\u4e8e\u5176\u4ed6\u516d\u5bb6\u80fd\u63d0\u524d\u5f97\u5230\u6d4b\u8bd5\u5143\u4ef6\u7684\u540c\u884c\u5382\u5546\u3002</p><p>\u3000\u3000\u5f20\u606a\u51b7\u773c\u770b\u7740\u8c22\u77bb\uff0c\u6ca1\u6709\u592a\u591a\u8868\u60c5\u7684\u7b11\u4e86\u7b11\uff0c\u6216\u8bb8\u53ea\u662f\u5634\u89d2\u62bd\u6410\u4e86\u4e00\u4e0b\uff1a\u201c\u6211\u505a\u4eba\u4ece\u6765\u90fd\u4e0d\u4f1a\u8d76\u5c3d\u6740\u7edd\uff0c\u4f46\u662f\u4f60\u4eec\u4e09\u756a\u6570\u6b21\u7684\u6b3a\u5230\u6211\u5934\u4e0a\uff0c\u4e0d\u7ed9\u4f60\u4eec\u6559\u8bad\u4e5f\u4e0d\u884c\uff0c\u7ec4\u88c5\u6d4b\u8bd5\u7684\u65f6\u95f4\u4e5f\u4e0d\u957f\uff0c\u4f60\u4eec\u80fd\u4ece\u5609\u4fe1\u7535\u5b50\u62bd\u5230\u4e13\u4e1a\u7684\u6280\u672f\u4eba\u5458\uff0c\u4e5f\u5c31\u803d\u6401\u534a\u4e2a\u6708\u7684\u65f6\u95f4\u2026\u2026\u201d\u8bf4\u5b8c\u8fd9\u53e5\u8bdd\uff0c\u5c31\u6ca1\u6709\u518d\u770b\u8c22\u77bb\u3001\u90c1\u840d\u4e00\u773c\uff0c\u7529\u624b\u8d70\u51fa\u8bb0\u8005\u4f1a\u73b0\u573a\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u8fd9\u53e5\u8bdd\uff0c\u5c31\u50cf\u4e00\u5df4\u638c\u6beb\u4e0d\u7559\u60c5\u7684\u6247\u5230\u8c22\u77bb\u7684\u8138\uff0c\u4ed6\u90fd\u6ca1\u6709\u62db\u67b6\u7684\u4f59\u529b\u3002</p><p>\u3000\u3000\u90c1\u840d\u5fc3\u91cc\u5374\u8361\u6f3e\u51fa\u4e00\u80a1\u5f02\u6837\u7684\u60c5\u7eea\uff0c\u5979\u6ca1\u6709\u60f3\u5230\u8fd9\u4e48\u5e74\u8f7b\u7684\u7537\u4eba\u53d1\u8d77\u5a01\u6765\uff0c\u5373\u4f7f\u5634\u89d2\u542b\u7740\u7b11\uff0c\u5374\u771f\u7684\u80fd\u7ed9\u522b\u4eba\u6781\u5927\u7684\u3001\u51e0\u4e4e\u65e0\u6cd5\u627f\u53d7\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u63a5\u4e0b\u6765\uff0c\u6709\u8bb0\u8005\u8981\u6c42TI\u3001\u65af\u9ad8\u67cf\u7684\u4ee3\u8868\u4ecb\u7ecd\u8054\u5408\u7814\u53d1\u89e3\u7801\u82af\u7247\u7684\u4e00\u4e9b\u7ec6\u8282\uff0c\u4e5f\u8981\u6c42\u7231\u8fbe\u7535\u5b50\u7684\u5de5\u4f5c\u4eba\u5458\u5f53\u4f17\u6f14\u793a\u65b0\u789f\u673a\uff0c\u9648\u4fe1\u751f\u3001\u827e\u58a8\u3001\u82cf\u6d25\u4e1c\u90fd\u4e00\u4e00\u505a\u5230\u3002\u867d\u7136\u4e0d\u77e5\u9053\u6280\u672f\u4e0a\u7684\u7ec6\u8282\uff0c\u4ec5\u4ec5\u662f\u76ee\u6d4b\uff0c\u4e5f\u80fd\u5224\u65ad\u51fa\u8fd9\u6b3e\u65b0\u789f\u673a\u7684\u6837\u673a\uff0c\u4ece\u753b\u9762\u7684\u6e05\u6670\u5ea6\u3001\u7a33\u5b9a\u5ea6\u90fd\u8981\u8d85\u8fc7\u5f53\u524d\u5e02\u573a\u4e0a\u7684\u4e3b\u6d41\u789f\u673a\u8bb8\u591a\uff0c\u5728\u529f\u80fd\u4e0a\u4e5f\u5168\u9762\u8d85\u8d8aVCD2.0\uff0c\u5230\u8fd9\u65f6\u5019\u8c22\u77bb\u624d\u77e5\u9053\u6240\u8c13\u7684\u201c\u56db\u5de8\u5934\u201d\u5c06\u65b0\u789f\u673a\u5b9a\u540d\u4e3a\u8d85\u7ea7VCD\u3002</p></article>"}}}]);
