(window.webpackJsonp=window.webpackJsonp||[]).push([[637],{660:function(p){p.exports={chapterTitle:null,title:"\u7b2c485\u7ae0 \u771f\u76f8\u6251\u6714\u8ff7\u79bb",content:"<article><h2>\u7b2c485\u7ae0 \u771f\u76f8\u6251\u6714\u8ff7\u79bb</h2><p>\u3000\u3000\u8bb0\u8005\u4f1a\u4e24\u70b9\u949f\u5f00\u59cb\uff0c\u4e00\u76f4\u62d6\u5230\u4e94\u70b9\u949f\u90fd\u6ca1\u6709\u7ed3\u675f\uff0c\u4e0d\u662f\u82cf\u6d25\u4e1c\u4ed6\u4eec\u6ed4\u6ed4\u4e0d\u7edd\u8bf4\u4e2a\u4e0d\u505c\uff0c\u800c\u662f\u5230\u573a\u7684\u4e8c\u5341\u591a\u5bb6\u5a92\u4f53\u7684\u8bb0\u8005\u7ea0\u7f20\u7740\u4e0d\u653e\u82cf\u6d25\u4e1c\u4ed6\u4eec\u79bb\u5f00\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u63a5\u5230\u79d1\u738b\u9080\u8bf7\u51fd\u8d76\u5230\u6d77\u5dde\u7684\u591a\u4e3a\u4e13\u4e1a\u6216\u51c6\u4e13\u4e1a\u7684\u7535\u5b50\u901a\u8baf\u5a92\u4f53\uff0c\u8bb0\u8005\u5bf9\u7535\u5b50\u9886\u57df\u7684\u6280\u672f\u591a\u5c11\u6709\u4e00\u4e9b\u7684\u4e86\u89e3\uff0c\u65e2\u7136\u56db\u5de8\u5934\u8054\u5408\u5f00\u53d1\u7684\u65b0\u789f\u673a\u53f7\u79f0\u6027\u80fd\u5168\u9762\u8d85\u8d8aVCD2.0\uff0c\u8bb0\u8005\u4eec\u4e0d\u4ec5\u8981\u6c42\u7231\u8fbe\u7684\u6280\u672f\u4eba\u5458\u6f14\u793a\u65b0\u789f\u673a\u6837\u673a\uff0c\u8fd8\u8981\u6c42\u5c06\u5e02\u573a\u4e0a\u51e0\u6b3e\u4e3b\u6d41\u789f\u673a\u6446\u51fa\u6765\uff0c\u4e00\u4e00\u6bd4\u8f83\u6027\u80fd\u4f18\u8d8a\u5728\u54ea\u91cc\uff0c\u6280\u672f\u7ec6\u8282\u4e0a\u4e5f\u8be2\u95ee\u5f97\u76f8\u5f53\u7684\u8be6\u7ec6\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u5012\u4e5f\u4e0d\u541d\u556c\uff0c\u751a\u81f3\u5e94\u8bb0\u8005\u7684\u8981\u6c42\u5c06\u673a\u58f3\u62c6\u5f00\u7ed9\u8fd1\u8ddd\u79bb\u62cd\u7167\uff0c\u4e00\u70b9\u90fd\u6ca1\u6709\u6280\u672f\u6f0f\u5bc6\u7684\u62c5\u5fe7\uff0c\u6700\u540eTI\u3001\u65af\u9ad8\u67cf\u3001\u98de\u5229\u6d66\u3001\u7231\u8fbe\u7535\u5b50\u3001\u660e\u4fca\u7535\u5b50\u8054\u5408\u53d1\u5e03\u58f0\u660e\uff0c\u4e3a\u4e86\u5c3d\u5feb\u5c06\u65b0\u789f\u673a\u63a8\u5411\u5e02\u573a\uff0c\u4e94\u5bb6\u516c\u53f8\u5c06\u4e8e\u8fd1\u671f\u5411\u53e6\u5916\u7b26\u5408\u6761\u4ef6\u7684\u516d\u5bb6\u789f\u673a\u5382\u5546\u63d0\u4f9b\u7231\u8fbe\u7535\u5b50\u5b9e\u9a8c\u7ec4\u88c5\u7684\u6837\u673a\u4e0e\u4e3b\u8981\u7ec4\u88c5\u6d4b\u8bd5\u7528\u7684\u5143\u5668\u4ef6\u3002</p><p>\u3000\u3000\u7b49\u5230\u8bb0\u8005\u4f1a\u4e0a\u8bb0\u8005\u5f00\u59cb\u8ffd\u95ee\u9648\u4fe1\u751f\u3001\u827e\u9ed8\u3001\u5f20\u4e9a\u5e73\u3001\u82cf\u6d25\u4e1c\u4ed6\u4eec\u4e00\u4e9b\u79c1\u4eba\u7684\u95ee\u9898\uff0c\u8c22\u77bb\u4e0e\u90c1\u840d\u624d\u79bb\u5f00\uff0c\u73b0\u5728\u5df2\u7ecf\u6ca1\u6709\u4eba\u5173\u5fc3\u79d1\u738b\u4e0b\u5348\u8fd8\u6709\u6240\u8c13\u7684\u6350\u8d60\u6d3b\u52a8\uff0c\u9664\u4e86\u6d77\u5dde\u5e02\u7684\u5a92\u4f53\u4e0d\u6562\u9a6c\u864e\u4e4b\u5916\uff0c\u5916\u5730\u7684\u5a92\u4f53\u8bb0\u8005\u5c31\u6beb\u65e0\u987e\u5fcc\u7684\u7ee7\u7eed\u7d27\u8ddf\u7740\u56db\u5de8\u5934\u7684\u80cc\u540e\uff0c\u60f3\u8981\u591a\u5957\u51fa\u4e00\u4e9b\u65b0\u95fb\u70b9\u51fa\u6765\uff0c\u90a3\u6015\u80fd\u5957\u4e9b\u8f76\u4e8b\u8f76\u95fb\u5199\u7bc7\u6587\u7ae0\uff0c\u4e5f\u7edd\u5bf9\u6bd4\u79d1\u738b\u4ec0\u4e48\u6350\u8d60\u6d3b\u52a8\u7cbe\u5f69\uff0c\u81f3\u4e8e\u6536\u5230\u79d1\u738b\u7684\u8584\u793c\uff0c\u90a3\u662f\u4e0a\u5348\u5960\u57fa\u5178\u793c\u7684\u76f8\u5173\u62a5\u9053\u3002\u8bb0\u8005\u4eec\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u8fd8\u662f\u591a\u5c11\u6709\u4e9b\u4e8f\u5fc3\uff0c\u597d\u5728\u79d1\u738b\u90a3\u8fb9\u5f88\u5feb\u4f20\u6765\u6d88\u606f\uff1a\u79d1\u738b\u7684\u6350\u8d60\u6d3b\u52a8\u53d6\u6d88\u4e86\u3002</p><p>\u3000\u3000\u8c22\u77bb\u4ece\u5f20\u606a\u5634\u91cc\u5f97\u5230\u660e\u786e\u7684\u7b54\u590d\uff0c\u79d1\u738b\u5c06\u4e0d\u5728\u8fd9\u516d\u5bb6\u83b7\u5f97\u6837\u673a\u4e0e\u6d4b\u8bd5\u5143\u4ef6\u7684\u5382\u5546\u4e4b\u5217\uff0c\u7535\u8bdd\u662f\u65e9\u5c31\u6253\u7ed9\u8c22\u5251\u5357\u4ed6\u4eec\u4e86\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u6709\u6ca1\u6709\u5546\u8bae\u51fa\u4ec0\u4e48\u5bf9\u7b56\u3002</p><p>\u3000\u3000\u8c22\u77bb\u8d76\u5230\u4e1c\u8fb9\u79d1\u738b\u7684\u5de5\u5382\uff0c\u5468\u5bcc\u660e\u3001\u9646\u6ea2\u5149\u3001\u5f20\u6653\u5065\u7b49\u5e02\u59d4\u9886\u5bfc\u5df2\u7ecf\u79bb\u5f00\uff0c\u5468\u747e\u73ba\u4e5f\u966a\u540c\u82cf\u9038\u7fa4\u56de\u5e02\u533a\u4e86\uff0c\u82cf\u9038\u7fa4\u4e5f\u6653\u5f97\u7231\u8fbe\u7535\u5b50\u8fd9\u6b21\u7ed9\u79d1\u738b\u7684\u53cd\u51fb\u76f8\u5f53\u51cc\u5389\uff0c\u4e0d\u4f1a\u518d\u8ba1\u8f83\u4ec0\u4e48\u62db\u5f85\u5468\u4e0d\u5468\u5230\u7684\u7ec6\u8282\uff0c\u518d\u8bf4\u4ed6\u4e5f\u6709\u4e9b\u4e8b\u60c5\u8981\u8ddf\u5468\u747e\u73ba\u5355\u72ec\u5546\u8bae\u3002</p><p>\u3000\u3000\u8fd9\u8fb9\u53ea\u662f\u79d1\u738b\u7684\u7ec4\u88c5\u5de5\u5382\uff0c\u4eca\u5929\u5960\u57fa\u7684\u7814\u53d1\u4e2d\u5fc3\u5176\u5b9e\u8ddf\u884c\u653f\u4e2d\u5fc3\u5171\u7528\u4e00\u680b\u697c\uff0c\u5efa\u6210\u4e4b\u540e\uff0c\u79d1\u738b\u7684\u603b\u90e8\u4e5f\u4f1a\u4ece\u5e9c\u5929\u5927\u53a6\u642c\u8fc7\u6765\u3002\u8c22\u77bb\u4e0e\u90c1\u840d\u8d76\u56de\u6765\u65f6\uff0c\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u3001\u9648\u5bb6\u5584\u3001\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u3001\u9648\u9759\u7b49\u4eba\u90fd\u5728\u5de5\u5382\u529e\u516c\u697c\u7684\u5c0f\u4f1a\u8bae\u5ba4\u91cc\u5546\u8bae\u5bf9\u7b56\uff0c\u8fd9\u6b21\uff0c\u5218\u660e\u8f89\u5012\u4e5f\u6709\u673a\u4f1a\u5217\u5e2d\uff0c\u5bf9\u4ed6\u610f\u89c1\u6700\u5927\u7684\u8d75\u9526\u8363\u5df2\u7ecf\u7ed9\u6324\u51fa\u79d1\u738b\u4e86\u3002</p><p>\u3000\u3000\u770b\u7740\u8c22\u77bb\u3001\u90c1\u840d\u56de\u6765\uff0c\u8fd9\u8fb9\u505c\u6b62\u8ba8\u8bba\uff0c\u8c22\u6c49\u9756\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u5728\u90a3\u91cc\u770b\u5230\u7684\u6837\u673a\u7684\u786e\u80fd\u53d6\u4ee3\u5f53\u524d\u5e02\u573a\u4e0a\u7684\u4e3b\u6d41\u789f\u673a\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\u2026\u2026\u201d\u8c22\u77bb\u5f88\u6cae\u4e27\u7684\u70b9\u70b9\u5934\uff0c\u201c\u4ed6\u4eec\u5e76\u4e0d\u5728\u4e4e\u6280\u672f\u4e0a\u4f1a\u6cc4\u5bc6\uff0c\u80fd\u63d0\u4f9b\u6837\u673a\u7ed9\u5176\u4ed6\u5382\u5546\uff0c\u4ed6\u4eec\u4e5f\u7684\u786e\u4e0d\u5728\u4e4e\u6280\u672f\u4e0a\u4f1a\u6cc4\u5bc6\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u6c49\u9756\u70b9\u70b9\u5934\uff0c\u8ba9\u8c22\u77bb\u3001\u90c1\u840d\u5750\u4e0b\u6765\u8bf4\u8bdd\uff0c\u4ed6\u6765\u4ecb\u7ecd\u521a\u624d\u4ed6\u4eec\u5728\u5c0f\u4f1a\u8bae\u5ba4\u5546\u8bae\u7684\u60c5\u51b5\uff1a\u201c\u6211\u4eec\u4e5f\u81f4\u7535\u65b0\u79d1\u3001\u7d22\u5c3c\u516c\u53f8\uff0c\u8ba8\u8bba\u4e86\u76f8\u5173\u95ee\u9898\uff0c\u8981\u77e5\u9053\uff0c\u7231\u8fbe\u8054\u5408TI\u3001\u65af\u9ad8\u67cf\u3001\u98de\u5229\u6d66\u641e\u51fa\u8fd9\u4e48\u5927\u7684\u52a8\u4f5c\uff0c\u65b0\u79d1\u3001\u7d22\u5c3c\u53d7\u5230\u7684\u76f4\u63a5\u51b2\u51fb\u6bd4\u6211\u4eec\u8981\u5927\u5f97\u591a\u2026\u2026\u6211\u4e5f\u521a\u521a\u5f97\u77e5\uff0c\u65b0\u79d1\u4e00\u76f4\u5728\u652f\u6301\u4e00\u5bb6\u53f0\u6e7e\u4eba\u5728\u7f8e\u56fd\u7845\u8c37\u7684\u79d1\u6280\u516c\u53f8\u5f00\u53d1\u89e3\u7801\u82af\u7247\uff0c\u8981\u907f\u5f00\u65af\u9ad8\u67cf\u3001TI\u5728\u89e3\u7801\u82af\u7247\u4e0a\u7684\u4e13\u5229\u4fdd\u62a4\uff0c\u7814\u53d1\u66ff\u4ee3\u6280\u672f\u4f1a\u56f0\u96be\u4e00\u4e9b\uff0c\u4f46\u662f\u8fd9\u5bb6\u516c\u53f8\u8fd8\u662f\u53d6\u5f97\u4e00\u4e9b\u6210\u7ee9\uff0c\u636e\u65b0\u79d1\u8001\u603b\u81ea\u5df1\u8bf4\uff0c\u90a3\u5bb6\u516c\u53f8\u6700\u591a\u518d\u8fc7\u4e00\u5e74\u5c31\u80fd\u5f00\u53d1\u51fa\u6280\u672f\u6210\u719f\u7684\u89e3\u7801\u82af\u7247\u6765\uff0c\u4f60\u77e5\u9053\uff0c\u7b2c\u4e8c\u4ee3\u6280\u672f\u8fd8\u6709\u4e00\u5e74\u7684\u5dee\u8ddd\uff0cTI\u4e0e\u65af\u9ad8\u67cf\u53c8\u8054\u5408\u641e\u51fa\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\uff0c\u73b0\u5728\u8fd8\u4e0d\u6e05\u695a\u90a3\u5bb6\u53f0\u6e7e\u4eba\u5f00\u7684\u516c\u53f8\u4f1a\u4e0d\u4f1a\u76f4\u63a5\u9009\u62e9\u653e\u5f03\uff0c\u603b\u4e4b\u65b0\u79d1\u4e0d\u4f1a\u5bf9\u90a3\u5bb6\u516c\u53f8\u62b1\u592a\u5927\u7684\u671f\u5f85\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u6c49\u9756\u8fd9\u4e48\u4e00\u8bf4\uff0c\u8c22\u77bb\u5f53\u7136\u80fd\u660e\u767d\u65b0\u79d1\u6240\u53d7\u5230\u7684\u6253\u51fb\uff0c\u95ee\u9053\uff1a\u201c\u7d22\u5c3c\u5462\uff0c\u4e3a\u4ec0\u4e48\u8bf4\u7d22\u5c3c\u516c\u53f8\u4e5f\u53d7\u5230\u6253\u51fb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u4e00\u76f4\u60f3\u4e0d\u660e\u767d\u4e3a\u4ec0\u4e48TI\u4e0e\u7231\u8fbe\u7535\u5b50\u5408\u8d44\u7b79\u5efa\u673a\u82af\u5de5\u5382\uff0c\u98de\u5229\u6d66\u4f1a\u8fd9\u4e48\u79ef\u6781\u7684\u63d0\u4f9b\u5e2e\u52a9\uff0c\u8981\u77e5\u9053\u7231\u8fbe\u7535\u5b50\u6709\u673a\u82af\u5de5\u5382\uff0c\u4f1a\u538b\u7f29\u98de\u5229\u6d66\u7684\u5e02\u573a\u4efd\u989d\uff1b\u6211\u4eec\u4e5f\u4e00\u76f4\u4e0d\u660e\u767d\u7231\u8fbe\u7535\u5b50\u4f1a\u4e3b\u52a8\u964d\u4f4e\u5e02\u573a\u76ee\u6807\uff0c\u73b0\u5728\u8fd9\u4e00\u5207\u90fd\u6709\u4e86\u89e3\u91ca\uff0c\u201d\u8c22\u6c49\u9756\u5c06\u4ed6\u4eec\u4e4b\u524d\u5546\u8bae\u7684\u7ed3\u8bba\u544a\u8bc9\u8c22\u77bb\uff0c\u201c\u5728\u7d22\u5c3c\u4e0e\u7231\u8fbe\u7535\u5b50\u4e4b\u95f4\uff0c\u98de\u5229\u6d66\u9009\u62e9\u7231\u8fbe\u7535\u5b50\u505a\u7ade\u4e89\u5bf9\u624b\u3002\u6240\u8c13TI\u3001\u65af\u9ad8\u67cf\u8054\u5408\u7231\u8fbe\u7535\u5b50\u3001\u98de\u5229\u6d66\u8054\u5408\u5f00\u53d1\u5916\u5c42\u5e94\u7528\u6280\u672f\uff0c\u5b9e\u9645\u60c5\u51b5\u662fTI\u4e0e\u65af\u9ad8\u67cf\u53ea\u8054\u5408\u7231\u8fbe\u7535\u5b50\u5f00\u53d1\u5916\u5c42\u5e94\u7528\u6280\u672f\uff0c\u98de\u5229\u6d66\u662f\u88ab\u8feb\u9009\u62e9\u8ddf\u4ed6\u4eec\u5408\u4f5c\uff0c\u4e0d\u7136\u4ed6\u4eec\u5c31\u4f1a\u5728\u7d22\u5c3c\u673a\u82af\u7684\u57fa\u7840\u4e0a\u7814\u53d1\u8d85\u7ea7VCD\u7684\u5916\u5c42\u5e94\u7528\u6280\u672f\uff0c\u8fd9\u5bf9\u98de\u5229\u6d66\u5c06\u662f\u81f4\u547d\u7684\uff1b\u98de\u5229\u6d66\u5c48\u4ece\u4e86\uff0c\u90a3\u7d22\u5c3c\u5c31\u8981\u906d\u53d7\u6253\u51fb\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u77bb\u95ee\u9053\uff1a\u201c\u65e2\u7136\u5982\u6b64\uff0c\u7231\u8fbe\u7535\u5b50\u4e3a\u4ec0\u4e48\u4e0d\u501f\u673a\u5784\u65ad\u8d85\u7ea7VCD\u7684\u5e02\u573a\uff0c\u800c\u8981\u5c06\u5916\u5c42\u5e94\u7528\u6280\u672f\u4e0e\u53f0\u6e7e\u660e\u4fca\u516c\u53f8\u5206\u4eab\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e00\u70b9\uff0c\u8fd8\u662f\u845b\u5148\u751f\u60f3\u5f97\u900f\u5f7b\uff0c\u5728\u6280\u672f\u5f00\u53d1\u4e2d\uff0c\u5e94\u8be5\u662fTI\u4e0e\u65af\u9ad8\u67cf\u5360\u636e\u4e3b\u5bfc\u5730\u4f4d\uff0c\u7231\u8fbe\u7535\u5b50\u5904\u4e8e\u4ece\u5c5e\u5730\u4f4d\uff0cTI\u4e0e\u65af\u9ad8\u67cf\u7684\u76ee\u6807\u662f\u8ba9\u8d85\u7ea7VCD\u8fc5\u901f\u5728\u5e02\u573a\u4e0a\u5f97\u5230\u63a8\u5e7f\u3002\u6211\u4eec\u5206\u6790\u8fc7\uff0c\u4e0d\u7ba1\u8d85\u7ea7VCD\u5728\u6280\u672f\u6709\u591a\u63a5\u8fd1DVD\uff0c\u4f46\u662f\u79bbDVD\u8fd8\u6709\u4e00\u4e9b\u5dee\u8ddd\uff0c\u4f46\u662f\u53ea\u8981\u811a\u6b65\u591f\u5feb\uff0c\u5c31\u53ef\u4ee5\u5728DVD\u63a8\u51fa\u4e4b\u524d\u62a2\u5148\u5360\u9886\u789f\u673a\u5e02\u573a\u3002\u5728\u56fd\u5185\u4ee5\u53ca\u4e1c\u5357\u4e9a\u7b49\u65b0\u5174\u56fd\u5bb6\u63a8\u5e7f\u8d85\u7ea7VCD\uff0c\u53ef\u4ee5\u5b8c\u5168\u4e0d\u53d7\u77e5\u8bc6\u7248\u6743\u7684\u9650\u5236\uff0c\u800cDVD\u56e0\u4e3a\u51e0\u5bb6\u7535\u5f71\u5236\u7247\u5546\u3001\u53d1\u884c\u5546\u62c5\u5fe7\u76d7\u7248\u95ee\u9898\u8fdf\u8fdf\u672a\u80fd\u8fbe\u6210\u6709\u6548\u534f\u8bae\uff0cDVD\u6807\u51c6\u90fd\u51fa\u6765\u597d\u4e9b\u5e74\uff0c\u90fd\u73b0\u5728\u90fd\u6ca1\u6709\u63a8\u51fa\u6210\u719f\u7684\u673a\u578b\uff0c\u5c31\u7b97\u6709DVD\u95ee\u4e16\uff0c\u9996\u5148\u4e5f\u662f\u5728\u6b27\u7f8e\u5730\u533a\u53d1\u884c\uff0c\u7b49\u5230\u56fd\u5185\u4e0e\u4e1c\u5357\u4e9a\u90a3\u4e9b\u65b0\u5174\u5730\u533a\u4e0e\u56fd\u5bb6\uff0c\u81f3\u5c11\u8981\u7b49\u5230\u4e5d\u4e5d\u751a\u81f3\u4e24\u5343\u5e74\u4e4b\u540e\uff0c\u8981\u63a8\u5e7f\u8d77\u6765\uff0c\u5219\u66f4\u6162\u3002\u4ece\u8fd9\u91cc\u770b\u51fa\uff0cTI\u4e0e\u65af\u9ad8\u67cf\u662f\u5728\u62a2\u65f6\u95f4\uff0c\u6839\u672c\u5c31\u4e0d\u4f1a\u5141\u8bb8\u7231\u8fbe\u7535\u5b50\u4e00\u5bb6\u72ec\u5360\u8d85\u7ea7VCD\u7684\u5e02\u573a\uff0c\u90a3\u6837\u7684\u8bdd\uff0c\u4f1a\u6781\u5927\u7684\u5f71\u54cd\u5230\u63a8\u5e7f\u901f\u5ea6\u2026\u2026\u201d</p><p>\u3000\u3000\u845b\u660e\u4fe1\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e2a\u539f\u56e0\uff0cTI\u3001\u65af\u9ad8\u67cf\u8fd9\u4e9b\u516c\u53f8\uff0c\u4f1a\u5f88\u6ce8\u91cd\u5728\u7ef4\u62a4\u77e5\u8bc6\u7248\u6743\u4e0a\u7684\u58f0\u8a89\uff0c\u662f\u90a3\u79cd\u65e2\u505a\u5a4a\u5b50\u53c8\u60f3\u7acb\u724c\u574a\u7684\u4e3b\uff0c\u82e5\u4ec5\u4ec5\u662f\u4ed6\u4eec\u56db\u5bb6\u516c\u53f8\u8054\u5408\u8d77\u6765\u72ec\u5360\u8d85\u7ea7VCD\uff0c\u90a3\u7247\u6e90\u95ee\u9898\u4ed6\u4eec\u5982\u4f55\u89e3\u51b3\uff0c\u4ed6\u4eec\u5c31\u4e0d\u6015\u5343\u592b\u6240\u6307\uff1f\u6240\u4ee5\u4ed6\u4eec\u5b81\u53ef\u8ba9\u6280\u672f\u6563\u64ad\u51fa\u53bb\uff0c\u4e4b\u540e\u518d\u51fa\u73b0\u4ec0\u4e48\u76d7\u7248\u4fb5\u6743\u7684\u884c\u4e3a\uff0c\u4e5f\u53ef\u4ee5\u63a8\u5f97\u4e00\u5e72\u4e8c\u51c0\u3002\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53ef\u80fd\u8fc7\u5ea6\u62c5\u5fc3\u4e86\uff0cTI\u4e0e\u65af\u9ad8\u67cf\u662f\u4e3b\u5bfc\u53ef\u4ee5\u80af\u5b9a\uff0c\u7231\u8fbe\u7535\u5b50\u8fd9\u6b21\u6240\u80fd\u5f97\u5230\u7684\u597d\u5904\u5e76\u6ca1\u6709\u6211\u4eec\u4e00\u5f00\u59cb\u60f3\u8c61\u4e2d\u90a3\u4e48\u5927\uff0c\u6211\u4eec\u73b0\u5728\u8fd8\u662f\u60f3\u529e\u6cd5\u89e3\u51b3\u6837\u673a\u4e0e\u6d4b\u8bd5\u5143\u4ef6\u7684\u95ee\u9898\u2026\u2026\u201d</p><p>\u3000\u3000\u96c6\u4e2d\u6700\u597d\u7684\u6280\u672f\u4e0e\u7ec4\u88c5\u8c03\u8bd5\u4eba\u5458\uff0c\u80fd\u62ff\u5230\u5143\u5668\u4ef6\u6210\u5668\u518d\u8fdb\u884c\u7ec4\u88c5\u8c03\u8bd5\uff0c\u81f3\u5c11\u4e5f\u8981\u803d\u6401\u534a\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u6700\u5173\u952e\u8fd8\u4e0d\u6e05\u695a\u96f6\u914d\u4ef6\u9700\u4e0d\u9700\u8981\u66f4\u6539\uff0c\u8fd8\u8981\u91cd\u65b0\u5f00\u6a21\u751f\u4ea7\u65b0\u578b\u53f7\u7684\u96f6\u914d\u4ef6\uff0c\u803d\u6401\u7684\u65f6\u95f4\u5c31\u4e0d\u6b62\u534a\u4e2a\u6708\u4e86\uff0c\u6240\u4ee5\u5c31\u7b97\u4e0d\u80fd\u516c\u5f00\u7684\u62ff\u5230\u6837\u673a\u4e0e\u6d4b\u8bd5\u5143\u4ef6\uff0c\u4e5f\u8981\u8bf4\u670d\u4e00\u5bb6\u789f\u673a\u5382\u5546\u8ba9\u79d1\u738b\u6697\u4e2d\u53c2\u4e0e\u7ec4\u88c5\u8c03\u8bd5\uff0c\u54ea\u6015\u51fa\u4e9b\u94b1\u4e5f\u5728\u6240\u4e0d\u60dc\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u4ed6\u4eec\u8fd8\u5728\u7ed9\u5a92\u4f53\u8bb0\u8005\u7ea0\u7f20\u4e0d\u5f97\u8131\u8eab\uff0c\u5f20\u606a\u6559\u8bad\u8fc7\u8c22\u77bb\u4e4b\u540e\uff0c\u5fc3\u60c5\u6109\u5feb\uff0c\u65e9\u5c31\u79bb\u5f00\u4e86\u8bb0\u8005\u4f1a\u73b0\u573a\uff0c\u53f6\u5efa\u658c\u4e0e\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u6768\u660e\u9526\u4ed6\u4eec\u5728\u4e00\u8d77\uff0c\u53f6\u5efa\u658c\u597d\u6b79\u4ee3\u8868\u53f6\u5bb6\u6301\u6709\u5609\u4fe1\u5b9e\u4e1a10\uff05\u7684\u80a1\u4efd\u3002</p><p>\u3000\u3000\u5f20\u606a\u53bb\u89c1\u53f6\u5efa\u658c\u4ed6\u4eec\uff0c\u53f6\u5efa\u658c\u770b\u5230\u5f20\u606a\u51fa\u73b0\uff0c\u5c31\u5174\u594b\u7684\u5927\u56b7\uff1a\u201c\u6211\u7ec8\u4e8e\u60f3\u4f60\u5c0f\u5b50\u846b\u82a6\u91cc\u5356\u4ec0\u4e48\u836f\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u846b\u82a6\u5356\u4ec0\u4e48\u836f\u4e86\uff1f\u201d\u5f20\u606a\u8ba9\u53f6\u5efa\u658c\u7a81\u7136\u4e00\u58f0\u5927\u53eb\u5413\u4e86\u4e00\u8df3\u3002</p><p>\u3000\u3000\u201c\u4f60\u5c0f\u5b50\u538b\u6839\u513f\u5c31\u662f\u5728\u62c9\u5609\u4fe1\u7535\u5b50\u8fdb\u5751\uff0c\u4f60\u5c0f\u5b50\u559c\u6b22\u85cf\u4e00\u624b\u9732\u4e00\u624b\uff0c\u8fd9\u4e00\u6b21\u4e00\u5b9a\u6709\u4ec0\u4e48\u4e1c\u897f\u85cf\u5f97\u4e25\u5b9e\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6709\u4ec0\u4e48\u5947\u602a\u7684\uff0c\u8c01\u4f1a\u5c06\u516c\u53f8\u7684\u5546\u4e1a\u673a\u5bc6\u90fd\u516c\u5e03\u4e8e\u4f17\uff1f\u201d</p><p>\u3000\u3000\u201c\u5173\u952e\u6211\u731c\u5230\u4f60\u85cf\u5728\u80cc\u540e\u662f\u4ec0\u4e48\u4e86\u2026\u2026\u201d\u53f6\u5efa\u658c\u5f97\u610f\u6d0b\u6d0b\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u85cf\u7740\u4ec0\u4e48\uff0c\u201d\u5f20\u606a\u62b1\u80f8\u4f9d\u5728\u95e8\u8fb9\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u5c31\u5728\u8fd9\u91cc\u542c\u4f60\u968f\u4fbf\u731c\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0d\u9700\u8981\u731c\uff0c\u6211\u80fd\u80af\u5b9a\u4e86\u3002\u201d\u53f6\u5efa\u658c\u8bed\u6c14\u76f8\u5f53\u80af\u5b9a\uff0c\u89c1\u5f20\u606a\u773c\u795e\u91cc\u900f\u7740\u6000\u7591\uff0c\u529e\u516c\u5ba4\u91cc\u4eba\u591a\u5634\u6742\uff0c\u4ed6\u7ad9\u8d77\u6765\u62c9\u7740\u5f20\u606a\u5f80\u5916\u8d70\uff0c\u8e72\u5230\u4e00\u5904\u7a7a\u8349\u576a\u4e0a\u62ff\u51fa\u70df\u6765\u62bd\uff0c\u201c\u4f60\u6700\u5927\u7684\u76ee\u7684\u5c31\u662f\u8ba9\u8d85\u7ea7VCD\u8fc5\u901f\u5360\u636e\u56fd\u5185\u4ee5\u53ca\u4e1c\u5357\u4e9a\u7684\u5e02\u573a\uff0c\u8d85\u7ea7VCD\u789f\u673a\u63a8\u5e7f\u8d8a\u8fc5\u901f\u3001\u63a8\u5e7f\u5730\u533a\u8d8a\u5e7f\u6cdb\uff0c\u5c31\u8d8a\u80fd\u963b\u6b62DVD\u789f\u673a\u7684\u51fa\u73b0\uff0c\u4f46\u662f\u4f60\u5bf9\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u7ecf\u6d4e\u5f62\u52bf\u5f88\u662f\u62c5\u5fe7\uff0c\u4f60\u4e0d\u5e0c\u671b\u6211\u4eec\u53bb\u5192\u9669\u5f00\u53d1\u4e1c\u5357\u4e9a\u5e02\u573a\uff0c\u5374\u5343\u65b9\u767e\u8ba1\u7684\u5f15\u5bfc\u3001\u523a\u6fc0\u79d1\u738b\u8054\u5408\u5609\u4fe1\u7535\u5b50\u53bb\u5f00\u53d1\u4e1c\u5357\u4e9a\u5e02\u573a\u3002\u4ece\u4eca\u5929\u7684\u60c5\u5f62\u6765\u770b\uff0c\u4f60\u6839\u672c\u5c31\u4e0d\u662f\u60f3\u79d1\u738b\u5f00\u53d1\u4e1c\u5357\u4e9aVCD\u789f\u673a\u5e02\u573a\uff0c\u800c\u662f\u5e0c\u671b\u79d1\u738b\u5f00\u53d1\u4e1c\u5357\u4e9a\u7684\u8d85\u7ea7VCD\u789f\u673a\u5e02\u573a\u3002\u4f60\u5c0f\u5b50\u635f\u4eba\u5229\u5df1\u7684\u4e8b\u4f1a\u505a\uff0c\u635f\u4eba\u4e0d\u5229\u5df1\u7684\u4e8b\u60c5\uff0c\u591a\u534a\u4e0d\u4f1a\u505a\uff0c\u4f46\u662f\u79d1\u738b\u5f00\u53d1\u4e1c\u5357\u4e9a\u7684\u8d85\u7ea7VCD\u789f\u673a\u5e02\u573a\uff0c\u5bf9\u4f60\u6709\u4ec0\u4e48\u597d\u5904\u5462\uff1f\u5c31\u7b97\u7231\u8fbe\u7535\u5b50\u80fd\u4ece\u660e\u4fca\u7684\u63a7\u5236\u82af\u7247\u5206\u4eab\u4e00\u90e8\u5206\u5229\u6da6\uff0c\u4f46\u662f\u63a7\u5236\u82af\u7247\u5728\u5236\u9020\u6210\u672c\u91cc\u6240\u5360\u7684\u4efd\u989d\u592a\u5c0f\uff0c\u8fd9\u90e8\u5206\u7684\u5229\u76ca\u4e0d\u4f1a\u8ba9\u4f60\u5fc3\u52a8\uff1b\u79d1\u738b\u53c8\u4e0d\u4f1a\u5411\u7231\u8fbe\u7535\u5b50\u8d2d\u4e70\u673a\u82af\uff0c\u7231\u8fbe\u7535\u5b50\u4e5f\u4e0d\u53ef\u80fd\u4ece\u98de\u5229\u6d66\u90a3\u91cc\u5206\u4eab\u4ed6\u4eec\u7684\u673a\u82af\u5229\u6da6\uff0c\u90a3\u5c31\u5269\u4e0b\u89e3\u7801\u82af\u7247\u2026\u2026\u201d\u53f6\u5efa\u658c\u52fe\u8fc7\u5f20\u606a\u7684\u8116\u5b50\uff0c\u201c\u7231\u8fbe\u7535\u5b50\u4f1a\u4ece\u89e3\u7801\u82af\u7247\u91cc\u5206\u4eab\u591a\u5c11\u597d\u5904\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5927\u7b11\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u7b80\u5355\u4e0d\u7b80\u5355\uff0c\u81f3\u5c11\u845b\u660e\u4fe1\u3001\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u4ed6\u4eec\u731c\u4e0d\u51fa\u6211\u7684\u7528\u610f\uff0c\u4e0d\u662f\u7231\u8fbe\u7535\u5b50\uff0c\u662f\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u80fd\u5206\u5230\u597d\u5904\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u731c\u4e0d\u900f\uff0c\u662f\u4ed6\u4eec\u6839\u672c\u5c31\u4e0d\u91cd\u89c6\u4e8c\u53d4\u7684\u90a3\u7bc7\u6587\u7ae0\uff0c\u201d\u53f6\u5efa\u658c\u89c1\u5f20\u606a\u627f\u8ba4\uff0c\u4e5f\u9ad8\u5174\u5f97\u5927\u7b11\uff0c\u201c\u8fd8\u4ee5\u4e3a\u4f60\u987e\u4e0d\u4e0a\u4e1c\u5357\u4e9a\u7684\u5e02\u573a\u3002\u201d</p><p>\u3000\u3000\u4e00\u5207\u90fd\u6d89\u53ca\u5230\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\u7684\u5f00\u53d1\u4e0e\u8d85\u7ea7VCD\u7684\u63a8\u5e7f\uff0c\u8fd9\u65f6\u5019\u8bf4\u51fa\u6765\u4e5f\u65e0\u59a8\uff0c\u53f6\u5efa\u658c\u8fd8\u662f\u503c\u5f97\u4fe1\u4efb\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u628a\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\u5f52\u5c5e\u4ed6\u8fd9\u8fb9\u7684\u6743\u76ca\u90fd\u5168\u90e8\u653e\u5728\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u540d\u4e0b\uff0c\u4e5f\u5c31\u662f\u8bf4\u9526\u6e56\u773c\u4e0b\u6700\u5927\u7684\u4e00\u5757\u5229\u76ca\u4e0e\u8d85\u7ea7VCD\u5bc6\u5207\u5173\u8fde\uff0c\u8d85\u7ea7VCD\u63a8\u5e7f\u8d8a\u8fc5\u901f\u3001\u8d8a\u5e7f\u6cdb\uff0c\u5360\u9886\u5e02\u573a\u7684\u65f6\u4ee3\u8d8a\u4e45\uff0c\u9526\u6e56\u4ece\u7b2c\u4e09\u4ee3\u89e3\u7801\u82af\u7247\u4e2d\u76f4\u63a5\u5206\u5f97\u7684\u5229\u76ca\u4e5f\u5c31\u8d8a\u591a\u3002</p><p>\u3000\u3000\u7d22\u7f57\u65af\u8fd9\u5bb6\u4f19\u8981\u662f\u6536\u624b\u4e0d\u5e72\uff0c\u4e1c\u5357\u4e9a\u4e0d\u53d1\u751f\u7ecf\u6d4e\u5371\u673a\uff0c\u5f20\u606a\u4e5f\u53ea\u80fd\u81ea\u8ba4\u5012\u9709\uff0c\u771f\u4ed6\u5976\u5976\u7684\u4fbf\u5b9c\u4e86\u79d1\u738b\u4e00\u628a\uff1b\u4f46\u662f\u4e1c\u5357\u4e9a\u5730\u533a\u4ee5\u53ca\u6574\u4e2a\u4e9a\u6d32\u53ca\u4fc4\u7f57\u65af\u7684\u7ecf\u6d4e\u53d1\u5c55\u5e76\u6ca1\u6709\u8131\u79bb\u5386\u53f2\u7684\u8f68\u8ff9\uff0c\u7d22\u7f57\u65af\u8fd9\u4e9b\u56fd\u9645\u91d1\u878d\u5de8\u9cc4\u663e\u7136\u4e5f\u4e0d\u4f1a\u653e\u8fc7\u773c\u524d\u7684\u7f8e\u98df\u3002</p><p>\u3000\u3000\u4e1c\u5357\u4e9a\u7684\u7ecf\u6d4e\u6ce8\u5b9a\u8981\u6709\u4e00\u52ab\uff0c\u5f20\u606a\u4e0d\u4f1a\u8ba9\u5609\u4fe1\u5b9e\u4e1a\u53bb\u5192\u8fd9\u4e2a\u9669\uff0c\u4f46\u662fVCD\u3001\u8d85\u7ea7VCD\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u65b0\u5174\u5e02\u573a\u7684\u5927\u89c4\u6a21\u63a8\u5e7f\u53c8\u4e0d\u80fd\u62d6\u5ef6\u5230\u7ecf\u6d4e\u5371\u673a\u8fc7\u540e\uff0c\u62d6\u5230\u90a3\u65f6\uff0c\u8d85\u7ea7VCD\u5c31\u4f1a\u5f7b\u5e95\u4e27\u5931\u8fd9\u4e9b\u5730\u533a\u7684\u65b0\u5174\u5e02\u573a\uff0c\u6700\u597d\u7684\u65b9\u6cd5\u5c31\u662f\u8ba9\u5609\u4fe1\u7535\u5b50\u4ee3\u52b3\u4e86\uff01</p><p>\u3000\u3000\u65e0\u8bba\u4ece\u89c4\u6a21\u8fd8\u662f\u5f71\u54cd\u529b\uff0c\u5609\u4fe1\u7535\u5b50\u90fd\u6709\u80fd\u529b\u8fc5\u901f\u5f00\u62d3\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u5e02\u573a\uff0c\u66f4\u4f55\u51b5\uff0c\u5f20\u606a\u5bf9\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u4e5f\u5b9e\u5728\u6ca1\u6709\u592a\u5927\u7684\u597d\u611f\uff0c\u4e0d\u62d6\u4ed6\u4eec\u5165\u5751\u62d6\u8c01\uff1f</p><p>\u3000\u3000\u81f3\u4e8e\u8c22\u5bb6\u4e5f\u8bb8\u80fd\u4ece\u4e2d\u5f97\u5230\u4e9b\u597d\u5904\u2014\u2014\u90a3\u7b80\u76f4\u662f\u4e00\u5b9a\u7684\uff0c\u79d1\u738b\u8981\u662f\u4e0d\u80fd\u8ddf\u5609\u4fe1\u7535\u5b50\u8fd9\u6837\u6709\u5b9e\u529b\u7684\u7535\u5b50\u5382\u5546\u5408\u4f5c\uff0c\u4e0d\u5c06\u5236\u9020\u6210\u672c\u964d\u4e0b\u6765\uff0c\u91d1\u878d\u98ce\u66b4\u6765\u88ad\uff0c\u4f1a\u6b7b\u5f97\u5f88\u96be\u770b\u2014\u2014\u90a3\u662f\u4e5f\u6ca1\u6709\u529e\u6cd5\u7684\u4e8b\u60c5\uff0c\u6216\u8005\u8bf4\u4e0d\u4e00\u68cd\u5b50\u5c06\u79d1\u738b\u6253\u6b7b\uff0c\u8ba9\u79d1\u738b\u5728\u5b58\u4ea1\u8fb9\u7f18\u82e6\u82e6\u4e89\u624e\u2014\u2014\u91d1\u878d\u98ce\u66b4\u6765\u88ad\uff0c\u79d1\u738b\u5373\u4f7f\u80fd\u4e0d\u6b7b\uff0c\u892a\u51e0\u5c42\u76ae\u4e5f\u662f\u514d\u4e0d\u4e86\u7684\u2014\u2014\u8fd9\u66f4\u7b26\u5408\u5f20\u606a\u72ec\u7279\u7684\u5ba1\u7f8e\u60c5\u8da3\u3002</p></article>"}}}]);
