(window.webpackJsonp=window.webpackJsonp||[]).push([[64],{87:function(p){p.exports={chapterTitle:null,title:"\u7b2c64\u7ae0 \u6ce1\u599e\u9700\u8981\u597d\u53a8\u827a",content:"<article><h2>\u7b2c64\u7ae0 \u6ce1\u599e\u9700\u8981\u597d\u53a8\u827a</h2><p>\u3000\u3000\u6ce8\u518c\u516c\u53f8\u7684\u624b\u7eed\u7531\u8bb8\u601d\u53bb\u5904\u7406\uff0c\u5f20\u606a\u6682\u65f6\u4e0d\u4f1a\u53bb\u7406\u4f1a\u6d77\u6cf0\u516c\u53f8\u7684\u4e8b\u60c5\uff0c\u5468\u6e38\u6b63\u4fe1\u5fc3\u5341\u8db3\u7684\u8d1f\u8d23\u5e02\u573a\u63a8\u5e7f\u5de5\u4f5c\uff0c\u60f3\u5fc5\u5fcd\u53d7\u4e0d\u4e86\u4e00\u4e2a\u5c11\u5e74\u5bf9\u4ed6\u6307\u624b\u5212\u811a\u3002\u5728\u6559\u5ba4\u6e21\u8fc7\u65e0\u804a\u7684\u4e00\u5929\uff0c\u6700\u540e\u4e00\u8282\u4f53\u80b2\u8bfe\u6ca1\u6709\u53bb\u4e0a\uff0c\u5f20\u606a\u7ed9\u8bb8\u601d\u6302\u4e86\u7535\u8bdd\uff0c\u8ba9\u5979\u5230\u5bcc\u8d35\u56ed\u6765\u3002</p><p>\u3000\u3000\u79bb\u8bb8\u601d\u4e0b\u73ed\u8fd8\u6709\u4e00\u6bb5\u65f6\u95f4\uff0c\u5f20\u606a\u5fc3\u60f3\u53bb\u98df\u5802\u4e70\u996d\u83dc\uff0c\u8fd8\u4e0d\u5982\u4eb2\u624b\u505a\u4e00\u987f\u3002\u5f20\u606a\u4ece\u5c0f\u5230\u5927\u5bf9\u5403\u98df\u6bd4\u8f83\u6311\u5254\uff0c\u5c31\u7422\u78e8\u51fa\u4e00\u624b\u597d\u53a8\u827a\uff0c\u4e0d\u8fc7\u7b49\u5f53\u4e0a\u90e8\u95e8\u4e3b\u7ba1\u3001\u5206\u516c\u53f8\u7ecf\u7406\u4e4b\u540e\uff0c\u624b\u827a\u5c31\u8352\u5e9f\u4e86\u3002\u5373\u4f7f\u5982\u6b64\uff0c\u505a\u51fa\u6765\u7684\u83dc\u4e5f\u5e94\u6bd4\u5b66\u6821\u98df\u5802\u7684\u5927\u9505\u83dc\u5f3a\u4e9b\u3002\u56de\u5230\u4e5d\u56db\u5e74\uff0c\u9664\u4e86\u5076\u5c14\u4e0b\u7897\u9e21\u86cb\u9762\uff0c\u6ca1\u6709\u5174\u8d77\u8fc7\u4e0b\u53a8\u623f\u7684\u5ff5\u5934\uff0c\u51fa\u79df\u5c4b\u7684\u4e1c\u897f\u4e00\u5e94\u4ff1\u5168\uff0c\u4e0d\u5229\u7528\u8d77\u6765\uff0c\u771f\u6709\u4e9b\u6d6a\u8d39\u3002</p><p>\u3000\u3000\u5929\u8272\u8fd8\u65e9\uff0c\u6ca1\u6709\u5230\u653e\u665a\u5b66\u6216\u4e0b\u73ed\u7684\u65f6\u95f4\uff0c\u83dc\u5e02\u573a\u91cc\u4eba\u6d41\u4e0d\u591a\uff0c\u5f20\u606a\u6311\u4e86\u4e9b\u4e94\u82b1\u8089\u3001\u7af9\u86cf\u3001\u8c46\u8150\u3001\u9752\u7b0b\u3001\u9e21\u86cb\u3001\u9752\u6912\u3001\u80e1\u841d\u535c\u3001\u9762\u7b4b\uff0c\u56de\u5230\u51fa\u79df\u5c4b\u6162\u6761\u4e1d\u7406\u7684\u51c6\u5907\u7740\u3002</p><p>\u3000\u3000\u8bb8\u601d\u62ff\u94a5\u5319\u6253\u5f00\u95e8\uff0c\u5c31\u95fb\u5230\u6251\u9f3b\u7684\u9999\u6c14\uff0c\u770b\u7740\u5e73\u65f6\u8fde\u7897\u90fd\u61d2\u5f97\u6d17\u4e00\u53ea\u7684\u5f20\u606a\u7cfb\u7740\u56f4\u88d9\u7ad9\u5728\u53a8\u623f\u91cc\uff0c\u624b\u91cc\u6b63\u5265\u9752\u7b0b\uff0c\u5634\u5507\u64ae\u7740\u6b63\u5439\u62a5\u559c\u9e4a\u7684\u8c03\u5b50\uff0c\u811a\u4e0b\u8fd8\u8f7b\u8f7b\u7684\u8e29\u7740\u8282\u62cd\uff0c\u70ed\u9505\u6b63\u6251\u6251\u7684\u5192\u7740\u767d\u6c7d\uff0c\u5f80\u5367\u5ba4\u91cc\u63a2\u5934\u770b\u4e86\u4e24\u773c\uff0c\u6ca1\u6709\u5916\u4eba\u3002</p><p>\u3000\u3000\u8bb8\u601d\u4f9d\u7740\u95e8\u6cbf\uff0c\u770b\u7740\u5f20\u606a\u4e00\u8138\u4e25\u8083\u7684\u5265\u7740\u9752\u7b0b\uff0c\u660e\u4fca\u7684\u8138\u5f62\uff0c\u7ed9\u4eba\u5f02\u5e38\u6e29\u67d4\u7684\u611f\u89c9\uff0c\u4e00\u9897\u73b2\u73d1\u5fc3\u5dee\u70b9\u7ed9\u5f20\u606a\u6b64\u65f6\u7684\u5f62\u8c61\u7ed9\u878d\u5316\u4e86\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u6837\uff0c\u610f\u5916\u5427\uff1f\u201d\u5f20\u606a\u4fa7\u8fc7\u8138\u770b\u4e86\u770b\u8bb8\u601d\uff0c\u7b11\u4e86\u7b11\uff0c\u201c\u9a6c\u4e0a\u5c31\u597d\uff0c\u4f60\u7a0d\u5fae\u7b49\u4e00\u4f1a\u513f\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u7b11\u7740\u5750\u5230\u5367\u5ba4\u91cc\uff0c\u5c06\u6298\u53e0\u9910\u684c\u5c55\u5f00\uff0c\u5fc3\u91cc\u60f3\u7740\u8981\u6709\u4e00\u4e2a\u7537\u4eba\u7ed9\u81ea\u5df1\u8fd9\u6837\u7684\u611f\u89c9\uff0c\u6b7b\u90fd\u503c\u5f97\u4e86\uff0c\u770b\u7740\u51cc\u4e71\u7684\u5367\u5ba4\uff0c\u53c8\u53f9\u6c14\u7684\u6447\u4e86\u6447\u5934\uff0c\u722c\u8fc7\u53bb\u5e2e\u4ed6\u6536\u62fe\u3002</p><p>\u3000\u3000\u5f20\u606a\u62ff\u7740\u7897\u7b77\u51fa\u6765\uff0c\u89c1\u8bb8\u601d\u8db4\u5728\u5e8a\u4e0a\u66ff\u81ea\u5df1\u53e0\u8863\u670d\uff0c\u81c0\u90e8\u7ed9\u7b52\u88d9\u7ef7\u5f97\u6d51\u5706\u4e30\u7fd8\uff0c\u8170\u5374\u60ca\u4eba\u7684\u7ec6\u4e0b\u53bb\uff0c\u4e30\u633a\u7684\u80f8\u90e8\u4e0b\u538b\u5728\u8863\u895f\u4e0a\uff0c\u5760\u51fa\u5b8c\u7f8e\u8bf1\u4eba\u7684\u66f2\u7ebf\u3002\u5f20\u606a\u7b11\u4e86\u7b11\uff1a\u201c\u54a6\uff0c\u8bb8\u601d\u59d0\u4eca\u5929\u7684\u5185\u88e4\u662f\u7ea2\u8272\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u201d\u8bb8\u601d\u7ffb\u8fc7\u8eab\u6765\uff0c\u624b\u538b\u7740\u88d9\u6446\uff0c\u89c1\u5f20\u606a\u7b11\u563b\u563b\u7684\u5c06\u7897\u7b77\u653e\u9910\u684c\u4e0a\uff0c\u624d\u60f3\u4eca\u5929\u4e0d\u662f\u7a7f\u7ea2\u8272\u5185\u88e4\uff0c\u518d\u8bf4\u7b52\u88d9\u53ca\u819d\uff0c\u4eba\u8db4\u7740\u600e\u4e48\u53ef\u4ee5\u80fd\u770b\u5230\u5185\u88e4\uff0c\u55d4\u9a82\u9053\uff1a\u201c\u627e\u6b7b\u554a\u3002\u201d\u7ad9\u8d77\u6765\u5e2e\u5f20\u606a\u53bb\u7aef\u996d\u83dc\uff0c\u9762\u7b4b\u7096\u8089\uff0c\u5f88\u666e\u901a\u7684\u5bb6\u5e38\u83dc\uff0c\u4f46\u662f\u52a0\u4e0a\u9752\u6912\u4e01\u3001\u80e1\u841d\u535c\u4e1d\uff0c\u9999\u6c14\u6251\u9f3b\uff0c\u8272\u5f69\u4e5f\u5f88\u9c9c\u8273\uff0c\u7af9\u86cf\u8c46\u8150\u6c64\uff0c\u6ca1\u6709\u6742\u8d28\u7684\u6e05\u6c64\u6d6e\u7740\u86cb\u76ae\u4e1d\u4e0e\u9752\u7b0b\u4e1d\uff0c\u6ca1\u6709\u4e00\u70b9\u70b9\u7684\u70df\u706b\u6c14\u3002</p><p>\u3000\u3000\u201c\u4e00\u83dc\u4e00\u6c64\uff0c\u672c\u6765\u60f3\u505a\u4e00\u4e2a\u9762\u7b4b\u70e7\u8089\u7cca\u5f04\u4e00\u4e0b\u7b97\u4e86\uff0c\u5fc3\u60f3\u4f60\u4e0d\u4e00\u5b9a\u559c\u6b22\uff0c\u86cf\u5b50\u6e05\u6c64\u5012\u662f\u4e0d\u9519\uff0c\u7c73\u996d\u91cc\u6211\u52a0\u4e86\u70b9\u8377\u53f6\uff0c\u8fd9\u65f6\u5019\u8377\u53f6\u4e0d\u597d\u627e\uff0c\u6ca1\u6709\u65b0\u53f6\u5b50\uff0c\u9999\u6c14\u8fd8\u4e0d\u591f\u51bd\u2026\u2026\u201d\u6253\u5f00\u7535\u996d\u7172\u5e2e\u8bb8\u601d\u76db\u996d\u3002</p><p>\u3000\u3000\u867d\u7136\u53ea\u6709\u4e00\u83dc\u4e00\u6c64\uff0c\u5012\u662f\u82b1\u4e86\u597d\u591a\u5fc3\u601d\uff0c\u8bb8\u601d\u6328\u4e2a\u507f\u4e86\u507f\uff0c\u6b6a\u7740\u5934\u770b\u7740\u5f20\u606a\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u957f\u5927\u4e86\uff0c\u80af\u5b9a\u8ff7\u6b7b\u4eba\u4e0d\u507f\u547d\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u60f3\u5230\u5927\u5b66\u6bd5\u4e1a\u540e\uff0c\u9648\u5b81\u6bcf\u5929\u7f20\u7740\u81ea\u5df1\u505a\u9999\u8fa3\u87f9\u5403\u5230\u60f3\u5410\uff0c\u8fd8\u4e0d\u662f\u90a3\u6837\u7684\u7ed3\u679c\uff1f</p><p>\u3000\u3000\u60df\u4e00\u7684\u6905\u5b50\u5806\u6ee1\u4e66\u4e0e\u8d44\u6599\uff0c\u5f20\u606a\u6328\u7740\u8bb8\u601d\u5750\u5728\u5e8a\u6cbf\u4e0a\uff0c\u5979\u4eca\u5929\u7a7f\u7740\u5ae9\u9ec4\u8272\u7684\u8863\u88f3\uff0c\u706f\u5149\u663e\u5f97\u5206\u5916\u7684\u67d4\u548c\u59a9\u5a9a\uff0c\u808c\u80a4\u66f4\u767d\u66f4\u5ae9\u3002\u8bb8\u601d\u89c1\u5f20\u606a\u76ef\u7740\u81ea\u5df1\u770b\uff0c\u7aef\u7740\u996d\u7897\u4fa7\u7740\u8eab\u5b50\u5750\uff0c\u5f20\u606a\u89c1\u5979\u7684\u8584\u5462\u7b52\u88d9\u5c06\u5927\u817f\u7ef7\u7d27\uff0c\u900f\u7740\u5973\u6027\u7684\u9971\u6ee1\uff0c\u80f8\u90e8\u4e0e\u8170\u80a2\u7684\u66f2\u7ebf\u975e\u5e38\u5b8c\u7f8e\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u770b\u4e2a\u6ca1\u5b8c\u4e86\u2026\u2026\u201d\u8bb8\u601d\u653e\u4e0b\u7897\u7b77\uff0c\u7f9e\u6012\u7684\u56de\u5934\u77aa\u7740\u5f20\u606a\uff0c\u7a81\u7136\u95f4\u7535\u8bdd\u94c3\u54cd\u8d77\uff0c\u5413\u5f97\u6342\u8d77\u80f8\u53e3\uff0c\u5fc3\u865a\u7684\u770b\u4e86\u770b\u4e66\u684c\u4e0a\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u8d77\u63a5\u8fc7\u7535\u8bdd\uff0c\u662f\u5988\u5988\u7684\u58f0\u97f3\u3002</p><p>\u3000\u3000\u6881\u683c\u73cd\u5728\u7535\u8bdd\u91cc\u544a\u8bc9\u5f20\u606a\uff0c\u4eca\u5929\u966a\u5510\u5b66\u8c26\u53bb\u6324\u516c\u4ea4\u8f66\u7684\u51e0\u4e2a\u4ea4\u901a\u90e8\u95e8\u7684\u9886\u5bfc\uff0c\u516d\u4e2a\u4eba\u4e2d\u6709\u4e09\u4e2a\u88ab\u5c0f\u5077\u5212\u4e86\u53e3\u888b\uff0c\u8fd8\u6709\u4e00\u4e2a\u5efa\u59d4\u526f\u4e3b\u4efb\u88e4\u5b50\u540e\u9762\u7ed9\u5212\u7834\uff0c\u9732\u51fa\u5927\u534a\u4e2a\u5c41\u80a1\uff0c\u7b49\u4e0b\u4e86\u8f66\u597d\u4e45\u624d\u53d1\u73b0\uff0c\u516c\u5b89\u5c40\u957f\u7ed9\u53eb\u5230\u5e02\u59d4\u7ed9\u5927\u9a82\u4e86\u4e00\u901a\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u7740\u65e9\u4e0a\u6324\u516c\u4ea4\u5dee\u70b9\u8ddf\u793e\u4f1a\u4e0a\u5c0f\u9752\u5e74\u8d77\u51b2\u7a81\u7684\u4e8b\uff0c\u5fcd\u7740\u7b11\u95ee\u4ed6\u5988\uff1a\u201c\u8001\u7238\u5462\uff0c\u6211\u8bb0\u5f97\u4ed6\u4e5f\u53bb\u6324\u516c\u4ea4\u8f66\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4f1a\u513f\u5728\u5e02\u59d4\u5f00\u4f1a\u5462\uff0c\u4ed6\u6ca1\u8bf4\u81ea\u5df1\uff0c\u53ef\u80fd\u53e3\u888b\u4e5f\u88ab\u5212\u4e86\uff0c\u4e0d\u77e5\u9053\u6709\u6ca1\u6709\u9732\u8089\u3002\u201d\u6881\u683c\u73cd\u8bf4\u7740\u5728\u7535\u8bdd\u90a3\u5934\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u63a5\u4e0b\u6765\u53c8\u95ee\u4e86\u4e00\u4e9b\u5b66\u6821\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u81ea\u7136\u4e0d\u4f1a\u5982\u5b9e\u76f8\u544a\uff0c\u80e1\u626f\u4e86\u4e00\u4e9b\u8bdd\u7cca\u5f04\u8001\u5988\uff0c\u653e\u4e0b\u7535\u8bdd\uff0c\u53c8\u5c06\u8fd9\u4e8b\u544a\u8bc9\u8bb8\u601d\u542c\uff1a\u201c\u516c\u4ea4\u6d89\u53ca\u5230\u7684\u5e02\u653f\u90e8\u95e8\u5f88\u591a\uff0c\u4e0d\u4ec5\u4ec5\u516c\u4ea4\u603b\u516c\u53f8\uff0c\u4ec0\u4e48\u5efa\u59d4\u3001\u4ea4\u901a\u5c40\u3001\u5e02\u653f\u5c40\u3001\u516c\u5b89\u5c40\u7b49\u7b49\u90fd\u9003\u4e0d\u4e86\uff0c\u6d77\u5dde\u5b98\u573a\u4e00\u6f6d\u5b50\u6b7b\u6c34\uff0c\u5510\u5b66\u8c26\u6b63\u60f3\u627e\u4e9b\u4e8b\u60c5\u7a81\u7834\u4e00\u4e0b\uff0c\u70e7\u4ed6\u4e0a\u4efb\u6765\u7684\u7b2c\u4e00\u628a\u706b\uff0c\u6211\u770b\u554a\uff0c\u6d77\u5dde\u4ee5\u540e\u7684\u4ea4\u901a\u3001\u6cbb\u5b89\u4f1a\u597d\u8bb8\u591a\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u542c\u5f20\u606a\u8bf4\u5efa\u59d4\u526f\u4e3b\u4efb\u9732\u5c41\u80a1\u7684\u4e8b\u62bf\u7740\u5634\u76f4\u7b11\uff0c\u6367\u7740\u809a\u5b50\uff0c\u5dee\u70b9\u7b11\u5c94\u4e86\u6c14\uff0c\u4fcf\u8138\u6f6e\u7ea2\uff0c\u5374\u89c1\u5f20\u606a\u8bf4\u5b8c\u8fd9\u4e9b\u8bdd\u4e4b\u540e\uff0c\u4e00\u4ed8\u82e5\u6709\u6240\u601d\u7684\u6837\u5b50\uff0c\u4f3c\u4e4e\u5728\u60f3\u522b\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u8bb8\u601d\u62ff\u624b\u5728\u5f20\u606a\u773c\u524d\u6447\u4e86\u6447\uff0c\u53d1\u73b0\u4ed6\u4e60\u60ef\u6027\u8d70\u795e\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u8d77\u4ed6\u4e0e\u5510\u5a67\u4e3a\u5347\u5b66\u6446\u9152\u7684\u4e8b\uff0c\u90a3\u6b21\uff0c\u6d77\u5dde\u5e02\u4e3b\u8981\u515a\u653f\u5b98\u5458\u53ea\u8981\u4eba\u5728\u6d77\u5dde\u90fd\u6709\u5217\u5e2d\uff0c\u5374\u504f\u504f\u5176\u4ed6\u51e0\u4e2a\u5e38\u59d4\u6ca1\u6709\u51fa\u73b0\uff0c\u5e02\u59d4\u5185\u90e8\u7684\u4e0d\u548c\u8c10\uff0c\u660e\u773c\u4eba\u90fd\u80fd\u770b\u5230\u89c1\u3002\u5510\u5b66\u8c26\u8ddf\u5468\u5bcc\u660e\u4e0d\u540c\uff0c\u4ed6\u6709\u8fdb\u53d6\u5fc3\uff0c\u5373\u4f7f\u5e02\u91cc\u6709\u5f88\u5f3a\u7684\u963b\u529b\uff0c\u4ed6\u4e5f\u4e0d\u662f\u90a3\u79cd\u4f1a\u88ab\u963b\u529b\u5413\u5012\u7684\u4eba\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u95f9\u51fa\u8fd9\u4e48\u4e22\u9762\u5b50\u7684\u4e8b\uff0c\u6070\u597d\u7ed9\u4e86\u4ed6\u6253\u4eba\u7684\u5927\u68d2\uff0c\u6d77\u5dde\u5b98\u573a\u5e94\u8be5\u6709\u52a8\u4f5c\u4e86\u5427\uff1f</p><p>\u3000\u3000\u770b\u7740\u8bb8\u601d\u5ae9\u767d\u5982\u7389\u7684\u5c0f\u624b\u5728\u773c\u524d\u6643\u52a8\uff0c\u6293\u5728\u624b\u5fc3\u91cc\u637b\u4e86\u4e00\u628a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5728\u60f3\u5510\u5b66\u8c26\u6b64\u65f6\u7684\u82e6\u607c\u3002\u201d</p><p>\u3000\u3000\u201c\u5510\u5b66\u8c26\u7684\u82e6\u607c\uff1f\u201d\u8bb8\u601d\u5fd8\u4e86\u8d23\u602a\u5f20\u606a\u5360\u5979\u4fbf\u5b9c\u7684\u4e3e\u52a8\uff0c\u4ed6\u7684\u624b\u638c\u5bbd\u5927\u6e29\u70ed\uff0c\u5f88\u6709\u529b\u91cf\uff0c\u8fde\u81ea\u5df1\u90fd\u4e0d\u60f3\u62bd\u56de\u624b\uff0c\u53ea\u662f\u624b\u7ed9\u4ed6\u6293\u7740\uff0c\u611f\u89c9\u5f88\u602a\u5f02\u3002</p><p>\u3000\u3000\u201c\u6574\u6cbb\u6cbb\u5b89\u3001\u4ea4\u901a\u554a\uff0c\u53ea\u662f\u758f\u7406\u4e4b\u524d\u9057\u7559\u4e0b\u6765\u7684\u95ee\u9898\uff0c\u5510\u5b66\u8c26\u53ef\u662f\u90a3\u79cd\u60f3\u5f00\u521b\u65b0\u683c\u5c40\u7684\u4eba\uff0c\u60f3\u5fc5\u4ed6\u5f88\u60f3\u5728\u7b2c\u4e8c\u628a\u3001\u7b2c\u4e09\u628a\u706b\u4e0a\u5bfb\u627e\u7a81\u7834\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8003\u8651\u8fd9\u4e48\u505a\u4ec0\u4e48\uff0c\u53c8\u4e0d\u5173\u4f60\u7684\u4e8b\uff1f\u201d\u8bb8\u601d\u7b11\u7740\u8bf4\uff0c\u201c\u665a\u775b\u59d0\u4eca\u5929\u4e2d\u5348\u56de\u7701\u57ce\u4e86\uff0c\u8bf4\u8d77\u4f60\u7684\u5c0f\u60c5\u4eba\uff0c\u4f60\u662f\u5173\u5fc3\u4f60\u7684\u5c0f\u60c5\u4eba\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u638c\u5fc3\u91cc\u5ae9\u767d\u5982\u7389\u7684\u5c0f\u624b\uff0c\u7ef5\u8f6f\u6e29\u51c9\uff0c\u767d\u5ae9\u7684\u624b\u6307\u809a\u4e2d\u95f4\u51f8\u51fa\u6765\uff0c\u6307\u5934\u5c16\u5c16\u7684\uff0c\u534a\u900f\u660e\u5448\u7c89\u7ea2\u8272\u7684\u6307\u7532\u6676\u83b9\u5254\u900f\uff0c\u5341\u5206\u7684\u7f8e\uff1b\u8bb8\u601d\u672c\u6765\u52aa\u529b\u5fd8\u6389\u624b\u7ed9\u5f20\u606a\u63e1\u7740\u8fd9\u4ef6\u4e8b\uff0c\u89c1\u4ed6\u773c\u775b\u76ef\u7740\u81ea\u5df1\u7684\u624b\uff0c\u5fc3\u614c\u7684\u62bd\u56de\u6765\uff0c\u55d4\u9a82\u9053\uff1a\u201c\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u6562\u7ee7\u7eed\u8c03\u620f\u8bb8\u601d\uff0c\u5728\u8fd9\u5996\u7cbe\u9762\u524d\uff0c\u53ea\u6015\u81ea\u5df1\u5148\u53d7\u4e0d\u4e86\uff0c\u63a5\u7740\u521a\u624d\u7684\u8bdd\u9898\u8bf4\uff1a\u201c\u5510\u5a67\u624d\u4e0d\u7528\u6211\u62c5\u5fc3\u5462\u3002\u5510\u5b66\u8c26\u6025\u7740\u627e\u7a81\u7834\uff0c\u6211\u8001\u5b50\u8eab\u4e0a\u7684\u538b\u529b\u4e5f\u5927\uff0c\u4ed6\u521a\u8c03\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\uff0c\u5f88\u60f3\u505a\u4e00\u756a\u4e8b\u4e1a\u7ee7\u7eed\u5f80\u4e0a\u722c\uff0c\u4f60\u60f3\u60f3\u770b\uff0c\u6211\u8001\u5b50\u5565\u65f6\u5019\u5f53\u4e0a\u5e02\u957f\u3001\u5e02\u59d4\u4e66\u8bb0\uff0c\u6211\u5c31\u662f\u8859\u5185\u4e86\uff0c\u8859\u5185\u7684\u4eba\u751f\u591a\u4e48\u7f8e\u597d\uff0c\u4f60\u77e5\u9053\u4e0d\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e48\u70b9\u5e74\u7eaa\u5c31\u8272\u54aa\u54aa\u7684\uff0c\u771f\u8ba9\u4f60\u5f53\u4e0a\u8859\u5185\uff0c\u6d77\u5dde\u7684\u5973\u5b69\u5b50\uff0c\u53ef\u8981\u5012\u9709\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u563f\u563f\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u7b2c\u4e00\u4e2a\u5012\u9709\u7684\u662f\u4f60\u3002\u201d</p><p>\u3000\u3000\u201c\u5478\uff0c\u72d7\u5634\u91cc\u5410\u4e0d\u51fa\u8c61\u7259\u2026\u2026\u201d\u8bb8\u601d\u5a07\u67d4\u7684\u9a82\u4e86\u4e00\u58f0\uff0c\u4e0d\u518d\u7406\u4ed6\u3002</p><p>\u3000\u3000\u8bb8\u601d\u5728\u53a8\u623f\u91cc\u5e2e\u5f20\u606a\u6536\u62fe\u7897\u7b77\uff0c\u5f20\u606a\u5bf9\u8bb8\u601d\u8bf4\uff1a\u201c\u665a\u4e0a\u53bb\u4f60\u5bb6\u90a3\u8fb9\u8d70\u8d70\u3002\u201d</p><p>\u3000\u3000\u201c\u6709\u4ec0\u4e48\u597d\u8d70\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u8ddf\u4f60\u7ea6\u4f1a\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u518d\u80e1\u8bf4\u516b\u9053\uff0c\u6211\u5c31\u4e0d\u7406\u4f60\u4e86\u3002\u201d\u8bb8\u601d\u89c1\u5f20\u606a\u8146\u7740\u8138\uff0c\u4e00\u4ed8\u65e0\u8d56\u5c11\u5e74\u7684\u6837\u5b50\uff0c\u6a2a\u4e86\u4ed6\u4e00\u773c\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u60f3\u5510\u5b66\u8c26\u4e0d\u662f\u90a3\u79cd\u88ab\u963b\u529b\u5413\u9000\u7684\u4eba\uff0c\u4ed6\u4e00\u5b9a\u60f3\u6709\u4f5c\u4e3a\uff0c\u6309\u65e2\u5b9a\u7684\u5386\u53f2\u8f68\u8ff9\uff0c\u6d77\u5dde\u5e02\u57ce\u5e02\u5efa\u8bbe\u603b\u4f53\u89c4\u5212\u8bbe\u8ba1\u7a3f\u8981\u7b49\u5230\u4e5d\u4e03\u5e74\u624d\u80fd\u5b8c\u7a3f\uff0c\u5927\u6982\u5510\u5b66\u8c26\u7b49\u4e0d\u53ca\u90a3\u4e48\u4e45\uff0c\u4ed6\u8981\u65bd\u5c55\u624b\u817f\uff0c\u4e00\u5b9a\u4f1a\u8ba9\u57ce\u5efa\u603b\u4f53\u89c4\u5212\u8bbe\u8ba1\u63d0\u524d\u5b8c\u7a3f\uff0c\u4f5c\u4e3a\u4ed6\u5927\u4e3e\u65bd\u5c55\u624b\u811a\u7684\u84dd\u56fe\u3002</p><p>\u3000\u3000\u8bb8\u601d\u5bb6\u4f4f\u5728\u6c99\u7530\uff0c\u90a3\u91cc\u662f\u6d77\u5dde\u5e02\u6700\u65e9\u7684\u57ce\u4e2d\u6751\u4e4b\u4e00\uff0c\u90a3\u5757\u533a\u57df\u4e0d\u5927\uff0c\u7d27\u6328\u7740\u524d\u95e8\u5546\u8d38\u533a\uff0c\u9664\u4e86\u6574\u7247\u4f4e\u77ee\u7684\u6c11\u623f\u5916\uff0c\u4e5f\u6709\u6d77\u5dde\u5e02\u4fdd\u5b58\u6700\u591a\u6700\u5b8c\u6574\u7684\u6c11\u6e05\u5efa\u7b51\u7fa4\u3002</p><p>\u3000\u3000\u6d77\u5dde\u5e02\u57ce\u5e02\u5efa\u8bbe\u603b\u4f53\u89c4\u5212\u8bbe\u8ba1\u4e5d\u4e03\u5e74\u6574\u4f53\u51fa\u7089\u4e4b\u540e\uff0c\u6700\u65e9\u5c06\u90a3\u4e00\u7247\u533a\u57df\u6574\u4f53\u62c6\u9664\uff0c\u4e1c\u6d77\u5927\u5b66\u3001\u6d77\u5dde\u5927\u5b66\u6709\u5341\u51e0\u540d\u6559\u6388\u8054\u540d\u4e0a\u4e66\u7701\u653f\u5e9c\uff0c\u8fd8\u662f\u672a\u80fd\u5c06\u6d77\u5dde\u6700\u540e\u6210\u7247\u7684\u53e4\u5efa\u7b51\u7fa4\u4fdd\u5b58\u4e0b\u6765\uff0c\u8ba9\u4eba\u89c9\u5f97\u5f88\u60cb\u60dc\u3002</p><p>\u3000\u3000\u8fd9\u4e0d\u4ec5\u4ec5\u662f\u5f00\u53d1\u5546\u5bf9\u653f\u5e9c\u51b3\u7b56\u5f88\u5f3a\u5927\u7684\u5f71\u54cd\u529b\uff0c\u6700\u4e3b\u8981\u8fd8\u662f\u653f\u5e9c\u5bf9\u5982\u4f55\u5229\u7528\u3001\u4fdd\u62a4\u53e4\u5efa\u7b51\u7fa4\u6ca1\u6709\u5f88\u597d\u7684\u8ba4\u8bc6\uff0c\u5f53\u65f6\u7684\u5730\u65b9\u653f\u5e9c\u53ea\u770b\u5230\u4fdd\u7559\u53e4\u5efa\u7b51\u7fa4\u5728\u4e0e\u5371\u623f\u6539\u9020\u3001\u57ce\u5e02\u5efa\u8bbe\u4e4b\u95f4\u5b58\u5728\u4e00\u4e9b\u77db\u76fe\uff0c\u4e3a\u4e86\u7ed9\u57ce\u5e02\u5efa\u8bbe\u8ba9\u8def\uff0c\u5c31\u8fde\u5e73\u9065\u8fd9\u4e9b\u5927\u540d\u9f0e\u9f0e\u7684\u53e4\u57ce\u90fd\u5dee\u70b9\u7ed9\u8fde\u9505\u7aef\u6389\u3002</p></article>"}}}]);
