(window.webpackJsonp=window.webpackJsonp||[]).push([[64],{87:function(p){p.exports={chapterTitle:null,title:"\u7b2c1052\u7ae0 \u8d44\u91d1\u538b\u529b",content:"<article><h2>\u7b2c1052\u7ae0 \u8d44\u91d1\u538b\u529b</h2><p>\u3000\u3000\u5728\u9526\u6e56\u7684\u63a7\u5236\u4e4b\u4e0b\uff0c\u6b64\u65f6\u7684\u4e2d\u6676\u5fae\u82af\u5373\u4f7f\u53ea\u80fd\u4ece\u4e8b\u4e2d\u4f4e\u7aef\u82af\u7247\u4ee3\u5de5\uff0c\u4f46\u662f\u5229\u6da6\u5e76\u4e0d\u4f4e\u3002</p><p>\u3000\u3000SVCD\u89e3\u7801\u82af\u7247\u5bf9\u6676\u5706\u5236\u7a0b\u5de5\u827a\u7684\u8981\u6c42\u5e76\u4e0d\u9ad8\uff0c\u4f46\u662f\u6838\u5fc3\u77e5\u8bc6\u4ea7\u6743\u88ab\u9526\u6e56\u3001\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u4e09\u5bb6\u516c\u53f8\u6240\u638c\u63e1\uff0c\u5373\u4f7f\u4e00\u76f4\u8c03\u6574\u4ef7\u683c\uff0cSVCD\u89e3\u7801\u82af\u7247\u51fa\u8d27\u4ef7\u683c\u90fd\u7ef4\u6301\u5728\u6bcf\u7247\u4e8c\u5341\u7f8e\u5143\u4ee5\u4e0a\uff0c\u4e2d\u6676\u5fae\u82af\u81f3\u5c11\u80fd\u4eceSVCD\u89e3\u7801\u82af\u7247\u4ee3\u5de5\u8ba2\u5355\u4e2d\u6bcf\u7247\u83b7\u5f97\u8d85\u8fc7\u4e00\u7f8e\u5143\u7684\u5229\u6da6\u3002</p><p>\u3000\u3000\u95ea\u5b58\u82af\u7247\u5e02\u573a\uff0c\u9526\u6e56\u3001\u4e1c\u829d\u3001\u4e09\u661f\u4e09\u5206\u5929\u4e0b\uff0c\u4e09\u5bb6\u90fd\u5f88\u4e50\u610f\u4eab\u53d7\u95ea\u5b58\u82af\u7247\u5e26\u6765\u7684\u66b4\u5229\u3002\u9526\u6e56\u5b8c\u5168\u6d88\u5316\u6389\u95ea\u5b58\u82af\u7247\u6280\u672f\u4e4b\u540e\uff0c\u9664\u4e86\u6ee1\u8db3\u5185\u90e8\u9700\u6c42\u4e4b\u5916\uff0c\u4e3b\u8981\u5411\u65af\u9ad8\u67cf\u4f9b\u8d27\uff0c\u5c06\u5176\u4ed6\u7684\u5e02\u573a\u4efd\u989d\u4e3b\u52a8\u8ba9\u7ed9\u4e1c\u829d\u4e0e\u4e09\u661f\u53bb\u6d88\u5316\uff0c\u5168\u740332 M\u50a8\u5b58\u89c4\u683c\u7684\u95ea\u5b58\u82af\u7247\u51fa\u8d27\u4ef7\u683c\u4ecd\u7ef4\u6301\u5728\u6bcf\u724760\u7f8e\u5143\u4ee5\u4e0a\u2014\u2014\u4ee5\u81f4\u65af\u9ad8\u67cf\u7684\u6267\u884c\u603b\u88c1\u827e\u9ed8\u6bcf\u56de\u770b\u5230\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u7b49\u9526\u6e56\u9ad8\u5c42\u90fd\u54ac\u7259\u5207\u9f7f\uff0c\u9526\u6e56\u5728\u95ea\u5b58\u82af\u7247\u6478\u5230\u624b\u7684\u5229\u6da6\u751a\u81f3\u8981\u8fdc\u8fdc\u9ad8\u8fbeiplayer\u4ea7\u54c1\u9500\u552e\u7684\u5229\u6da6\u2014\u2014\u6bd5\u7adfiplayer\u5728\u6d77\u5916\u9500\u552e\u7684\u8425\u9500\u6210\u672c\u5de8\u5927\uff0c\u95ea\u5b58\u82af\u7247\u7684\u9500\u552e\u9664\u5236\u9020\u4e0e\u7814\u53d1\u4e4b\u5916\u51e0\u4e4e\u6ca1\u6709\u4ec0\u4e48\u989d\u5916\u7684\u8d39\u7528\u3002\u9526\u6e56\u95ea\u5b58\u82af\u7247\u6708\u9500\u552e\u989d\u8d85\u8fc7\u4e24\u5343\u56db\u767e\u4e07\u7f8e\u5143\uff0c\u6bdb\u5229\u6da6\u7387\u8981\u9ad8\u8fc760\uff05\uff0c\u751a\u81f3\u66f4\u9ad8\u3002</p><p>\u3000\u3000\u5982\u6b64\u9ed8\u5951\u7684\u914d\u5408\uff0c\u4e5f\u4f7f\u5f97\u4e09\u661f\u5728\u534e\u5e02\u573a\u53d7\u9526\u6e56\u538b\u5236\u4e4b\u65f6\u4ecd\u80fd\u4fdd\u6301\u7406\u6027\u7684\u514b\u5236\uff0c\u4e2d\u6676\u5fae\u82af\u4e5f\u80fd\u4ece\u95ea\u5b58\u82af\u7247\u7684\u4ee3\u5de5\u8ba2\u5355\u4e0a\u83b7\u5f97\u76f8\u5f53\u591a\u7684\u8865\u8d34\u5229\u6da6\u3002</p><p>\u3000\u3000\u4ec5\u8fd9\u4e24\u9879\uff0c\u4e2d\u6676\u5fae\u82af\u76f8\u6bd4\u8f83\u5176\u4ed6\u7684\u6676\u5706\u4ee3\u5de5\u4f01\u4e1a\u6bcf\u5e74\u81f3\u5c11\u80fd\u83b7\u5f97\u8fd1\u4e24\u5343\u4e07\u7f8e\u5143\u7684\u989d\u5916\u6536\u76ca\uff0c\u8fd9\u5bf9\u6b63\u5904\u4e8e\u6210\u957f\u521d\u671f\u7684\u4e2d\u6676\u5fae\u82af\u663e\u5f97\u5c24\u5176\u7684\u91cd\u8981\uff1b\u8fd9\u4e5f\u4f7f\u5f97\u4e2d\u6676\u5fae\u82af\u5728\u6210\u957f\u521d\u671f\u7684\u751f\u5b58\u538b\u529b\u4e4b\u4e0b\uff0c\u72b9\u6709\u4f59\u529b\u5728\u6676\u5706\u5236\u7a0b\u5de5\u827a\u7814\u53d1\u8fdb\u884c\u957f\u671f\u6301\u7eed\u7684\u6295\u5165\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u5fb7\u4eea\u9996\u5e2d\u6267\u884c\u5b98\u5b89\u5409\u4f2f\u5bb6\u4e2d\u7684\u79c1\u5bb4\u8fc7\u540e\uff0c\u5f20\u606a\u3001\u9648\u4fe1\u751f\u3001\u67f3\u5fd7\u6210\u7b49\u4eba\u90fd\u4e58\u8f66\u8fd4\u56de\u5bbe\u9986\uff0c\u5b89\u5409\u4f2f\u7279\u610f\u5c06\u5fb7\u4eea\u526f\u603b\u88c1\u5fb7\u5c14\u6cd5\u897f\u4e0e\u5fb7\u4eea\u4e2d\u56fd\u533a\u603b\u88c1\u5468\u6b63\u9752\u7559\u4e0b\u6765\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u201c\u6839\u636e\u79d1\u6280\u60c5\u62a5\u641c\u7d22\u90e8\u95e8\u7684\u89c2\u6d4b\uff0cESS\u6700\u8fd1\u51e0\u6b21\u63d0\u4ea4\u7684\u53d1\u660e\u4e13\u5229\u7533\u8bf7\u5e94\u8be5\u662f\u4e0e\u57fa\u5e26\u82af\u7247\u7814\u53d1\u5bc6\u5207\u76f8\u5173\u7684\uff0c\u201d\u5fb7\u5c14\u6cd5\u897f\u5728\u5b89\u5409\u4f2f\u7684\u79c1\u5b85\u91cc\u6ca1\u6709\u4ec0\u4e48\u62d8\u675f\uff0c\u7aef\u7740\u7ea2\u9152\u676f\uff0c\u7ad9\u5728\u7a97\u53f0\u524d\u770b\u7740\u5e7f\u9614\u5ead\u9662\u91cc\u7684\u53c2\u5929\u5927\u6811\uff0c\u4e0e\u5b89\u5409\u4f2f\u8bf4\u9053\uff0c\u201c\u770b\u6765\u9526\u6e56\u6700\u65e9\u4ece\u6211\u4eec\u8fd9\u91cc\u63a5\u53d7\u6570\u5b57\u624b\u673a\u5236\u9020\u6280\u672f\u7684\u65f6\u5019\uff0c\u5c31\u5728\u7b79\u5212\u5f00\u53d1\u57fa\u5e26\u82af\u7247\u7684\u4e8b\u60c5\u4e86\u2026\u2026\u5c31\u662f\u4e0d\u6e05\u695a\u4ed6\u4eec\u505a\u5230\u54ea\u4e00\u6b65\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5468\u6b63\u9752\u5750\u5728\u4e00\u8fb9\u4e0d\u542d\u58f0\uff0c\u5fc3\u91cc\u5374\u662f\u5f02\u6837\u7684\u5403\u60ca\u3002</p><p>\u3000\u3000\u4ed6\u4eba\u5728\u4e2d\u56fd\uff0c\u53c8\u4e0d\u8d1f\u8d23\u7814\u53d1\u4e0a\u7684\u4e8b\u60c5\uff0c\u5bf9\u6709\u4e9b\u6d88\u606f\u8fd8\u771f\u662f\u4e0d\u5927\u6e05\u695a\uff0c\u4ed6\u6ca1\u6709\u60f3\u5230\u9526\u6e56\u7adf\u7136\u6709\u52c7\u6c14\u4e0a\u57fa\u5e26\u82af\u7247\u9879\u76ee\uff0c\u4e0d\u8981\u8bf4\u4e09\u661f\u5728\u57fa\u5e26\u82af\u7247\u4e0a\u6298\u8fc7\u4e24\u6b21\uff0c\u5c31\u8fdeIBM\u90fd\u5728\u57fa\u5e26\u82af\u7247\u4e0a\u7684\u5175\u8d25\u800c\u8fd4\u3001\u5403\u8fc7\u5927\u4e8f\uff1b\u9526\u6e56\u4e0d\u5355\u6709\u52c7\u6c14\u4e0a\u57fa\u5e26\u82af\u7247\u7684\u5f00\u53d1\u9879\u76ee\uff0c\u800c\u4e14\u65e9\u5c31\u4e0a\u4e86\uff1f</p><p>\u3000\u3000\u5b89\u5409\u4f2f\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u5f20\u606a\uff0c\u5927\u6982\u662f\u6211\u63a5\u89e6\u5230\u7684\u6700\u91ce\u5fc3\u52c3\u52c3\u7684\u4e2d\u56fd\u4eba\u4e86\uff0c\u201d\u53c8\u7709\u5934\u5fae\u5fae\u76b1\u7740\u56de\u5fc6\u9053\uff0c\u201c\u81f3\u4eca\uff0c\u6211\u7684\u7535\u8111\u91cc\uff0c\u8fd8\u4fdd\u987e\u7740\u4ed6\u53d1\u7ed9\u6211\u7684\u7b2c\u4e00\u5c01\u7535\u90ae\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u6b63\u9752\u8ba8\u597d\u7684\u8bf4\u9053\uff1a\u201c\u662f\u5b89\u5409\u4f2f\u5148\u751f\u60a8\u7684\u8fd9\u5c01\u7535\u90ae\u7f14\u9020\u4e86\u6b64\u65f6\u7684\u9526\u6e56\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0d\u80fd\u8fd9\u4e48\u8bf4\uff0c\u201d\u5b89\u5409\u4f2f\u6447\u5934\u800c\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u4e2d\u56fd\u6709\u53e5\u8bdd\u53e4\u8bdd\uff0c\u53eb\u4ec0\u4e48\u2018\u91d1\u9cde\u5c82\u662f\u6c60\u4e2d\u7269\u2019\uff1f\u201d\u5b89\u5409\u4f2f\u4e0d\u786e\u8ba4\u7684\u770b\u7740\u5468\u6b63\u9752\uff0c\u201c\u8981\u5d1b\u8d77\u603b\u5f52\u8981\u5d1b\u8d77\u7684\uff0c\u8981\u6ca1\u6709\u90a3\u5c01\u7535\u90ae\uff0c\u6216\u8bb8\u53ea\u662f\u9519\u8fc7\u5fb7\u4eea\u4e0e\u9526\u6e56\u5408\u4f5c\u7684\u673a\u4f1a\u3002\u201d</p><p>\u3000\u3000\u5468\u6b63\u9752\u5fc3\u60f3\u4e0d\u77e5\u9053\u5b89\u5409\u4f2f\u662f\u4ece\u54ea\u91cc\u542c\u6765\u4e2d\u56fd\u6709\u8fd9\u4e48\u4e00\u53e5\u53e4\u8bdd\uff0c\u610f\u601d\u8fd8\u662f\u80fd\u660e\u767d\uff0c\u53ea\u662f\u6709\u4e9b\u7422\u78e8\u4e0d\u900f\u5b89\u5409\u4f2f\u6df1\u5c42\u6b21\u7684\u610f\u601d\uff0c\u770b\u4ed6\u5982\u6b64\u8d5e\u8d4f\u9526\u6e56\u7684\u6210\u529f\uff0c\u96be\u9053\u662f\u503e\u5411\u5c060.18\u5fae\u7c73\u7684\u6676\u5706\u5236\u9020\u5de5\u827a\u8fd9\u6b21\u4e00\u5e76\u8f6c\u8ba9\u7ed9\u4e2d\u6676\u5fae\u82af\uff1f</p><p>\u3000\u3000\u5fb7\u5c14\u6cd5\u897f\u5012\u662f\u80fd\u7406\u89e3\u5b89\u5409\u4f2f\u7684\u610f\u601d\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u53ea\u6015\u4ed6\u4eec\u8fd8\u662f\u85cf\u5934\u7f29\u5c3e\u7684\uff0c\u4e0d\u597d\u5224\u65ad\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u5fb7\u5c14\u6cd5\u897f\u8fd9\u4e48\u8bf4\uff0c\u5468\u6b63\u9752\u5012\u662f\u660e\u767d\u4e86\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5fb7\u4eea\u59cb\u7ec8\u4e13\u6ce8\u4e8e\u662f\u9ad8\u9636\u82af\u7247\u7684\u5f00\u53d1\uff0c\u5c31\u7b97\u9526\u6e56\u6210\u529f\u7684\u5f00\u53d1\u51fa\u6765\u57fa\u5e26\u82af\u7247\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5bf9\u5fb7\u4eea\u7684\u9ad8\u7aef\u57fa\u5e26\u82af\u7247\u9020\u6210\u4ec0\u4e48\u5a01\u80c1\uff0c\u771f\u6b63\u75db\u82e6\u7684\u8fd8\u662f\u90a3\u4e9b\u8ddf\u9526\u6e56\u76f4\u63a5\u5728\u7ec8\u7aef\u5e02\u573a\u7ade\u4e89\u7684\u5382\u5546\u3002\u82e5\u662f\u8ba9\u9526\u6e56\u6210\u529f\u5f00\u53d1\u51fa\u4e2d\u4f4e\u7aef\u7684\u57fa\u5e26\u82af\u7247\uff0c\u90a3\u9526\u6e56\u5728\u4e2d\u4f4e\u7aef\u624b\u673a\u5e02\u573a\u7684\u6210\u672c\u4f18\u52bf\u5c31\u592a\u5927\u4e86\u2014\u2014\u51e0\u4e4e\u4e0d\u96be\u60f3\u8c61\u9664\u6b27\u7f8e\u65e5\u672c\u7b49\u4e3b\u8981\u9ad8\u7aef\u673a\u578b\u5e02\u573a\u4e4b\u5916\uff0c\u8fd8\u6709\u8c01\u6709\u80fd\u529b\u5f7b\u5e95\u7684\u538b\u5236\u4f4f\u9526\u6e56\uff1f</p><p>\u3000\u3000\u9526\u6e56\u8fd9\u4e9b\u5e74\u6765\u5230\u5e95\u5728\u6280\u672f\u7814\u53d1\u4e0a\u6295\u5165\u4e86\u591a\u5c11\uff1f\u5468\u6b63\u9752\u5fc3\u91cc\u5728\u7422\u78e8\u8fd9\u4e2a\u95ee\u9898\uff0c\u660e\u5929\u662f\u4e0d\u662f\u76f4\u622a\u4e86\u5f53\u7684\u5c31\u8ddf\u9648\u4fe1\u751f\u8ba8\u8bba\u8fd9\u4e2a\u95ee\u9898\uff1f\u7701\u5f97\u5fc3\u91cc\u4e00\u76f4\u90fd\u7eb3\u95f7\u7740\u3002\u5b89\u5409\u4f2f\u4e0e\u5fb7\u5c14\u6cd5\u897f\u6b64\u65f6\u662f\u65e0\u6cd5\u5224\u65ad\u9526\u6e56\u5bf9\u57fa\u5e26\u82af\u7247\u7684\u5f00\u53d1\u8fdb\u7a0b\uff0c\u82e5\u662f\u9526\u6e56\u5df2\u7ecf\u6210\u529f\u5f00\u53d1\u51fa\u4e2d\u4f4e\u7aef\u7684\u57fa\u5e26\u82af\u7247\uff0c\u4e0e\u9526\u6e56\u5728\u4e2d\u4f4e\u7aef\u82af\u7247\u751f\u4ea7\u9886\u57df\u7684\u5408\u4f5c\uff0c\u5df2\u7ecf\u4e0d\u662f\u5fb7\u4eea\u80fd\u591f\u62d2\u7edd\u7684\u8bf1\u60d1\u4e86\uff0c\u9664\u975e\u662f\u5bf9\u4e2d\u4f4e\u7aef\u82af\u7247\u5e02\u573a\u5f7b\u5e95\u7684\u653e\u624b\uff0c\u4e0d\u7136\u7684\u8bdd\uff0c\u4ee5\u6280\u672f\u6362\u589e\u6301\uff0c\u662f\u5fb7\u4eea\u6700\u4f73\u7684\u9009\u62e9\u2014\u2014\u82e5\u9526\u6e56\u5728\u57fa\u5e26\u82af\u7247\u7814\u53d1\u4e0a\u8fd8\u53ea\u662f\u534a\u540a\u5b50\uff0c\u751a\u81f3\u6781\u53ef\u80fd\u91cd\u8e48\u4e09\u661f\u3001IBM\u8fd9\u4e9b\u5382\u5546\u7684\u65e7\u8def\uff0c\u90a3\u5fb7\u4eea\u5c31\u6ca1\u6709\u5fc5\u8981\u9876\u4f4f\u5a92\u4f53\u4e0e\u516c\u4f17\u7684\u538b\u529b\u5c060.18\u5fae\u7c73\u7684\u6676\u5706\u5236\u7a0b\u6280\u672f\u4e00\u5e76\u62ff\u51fa\u6765\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u8fbe\u62c9\u65af\u80e1\u82cf\u59c6\u56fd\u9645\u9152\u5e97\u91cc\uff0c\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u3001\u67f3\u5fd7\u6210\u4e5f\u5728\u8003\u8651\u5982\u4f55\u8ba9\u5fb7\u4eea\u505a\u51fa\u8ba9\u6b65\uff0c\u5f53\u7136\u4e0d\u4f1a\u5c06\u6280\u672f\u7684\u5e95\u724c\u5411\u5fb7\u4eea\u5b8c\u5168\u644a\u51fa\u6765\u3002</p><p>\u3000\u3000\u5c06\u5e95\u724c\u4eae\u51fa\u6765\uff0c\u867d\u7136\u80fd\u591f\u8bf4\u670d\u5fb7\u4eea\u5c060.18\u5fae\u7c73\u7684\u6676\u5706\u5236\u7a0b\u6280\u672f\u4e00\u5e76\u8f6c\u8ba9\uff0c\u4f46\u662f\u5fb7\u4eea\u52bf\u5fc5\u4f1a\u8981\u6c42\u589e\u6301\u66f4\u591a\u7684\u80a1\u4efd\uff0c\u5f20\u606a\u8fd9\u65f6\u5019\u5b81\u53ef\u82b1\u73b0\u91d1\u4ece\u5fb7\u4eea\u624b\u91cc\u8d2d\u4e70\u6280\u672f\uff0c\u800c\u4e0d\u60f3\u8ba9\u592a\u591a\u7684\u4e2d\u6676\u5fae\u82af\u7684\u80a1\u6743\u843d\u5165\u5fb7\u4eea\u7684\u624b\u4e2d\u3002</p><p>\u3000\u3000\u5fb7\u4eea\u6b64\u65f6\u5df2\u7ecf\u5bf9\u4e2d\u6676\u5fae\u82af\u7247\u6301\u80a110\uff05\uff0c\u9526\u6e56\u5bf9\u4e2d\u6676\u5fae\u82af\u7684\u6301\u80a1\u4e5f\u4e0d\u8fc7\u521a\u521a\u5728\u63a7\u80a1\u94b1\u4ee5\u4e0a\uff0c\u4e2d\u6676\u5fae\u82af\u5176\u4ed6\u7684\u80a1\u4efd\u90fd\u5206\u6563\u5728\u4e91\u6e90\u96c6\u56e2\u3001\u65b0\u52a0\u5761\u91d1\u7ba1\u5c40\u4ee5\u53ca\u7ba1\u7406\u5c42\u624b\u4e2d\uff0c\u8ba9\u5fb7\u4eea\u7684\u6301\u80a1\u6bd4\u4f8b\u5230\u8fbe20\uff05\u5c31\u5df2\u7ecf\u662f\u6781\u9650\uff0c\u5c31\u662f\u4e0d\u6e05\u695a20\uff05\u80fd\u4e0d\u80fd\u6ee1\u8db3\u5fb7\u4eea\u7684\u80c3\u53e3\u3002</p><p>\u3000\u3000\u9526\u6e56\u653e\u5f03\u7edd\u5bf9\u63a7\u80a1\u6743\u6ca1\u6709\u4ec0\u4e48\uff0c\u4f46\u662f\u6700\u7ec8\u7684\u63a7\u5236\u6743\u4e0d\u80fd\u843d\u5165\u5916\u8d44\u7684\u624b\u91cc\u3002</p><p>\u3000\u3000\u5f15\u8fdb0.25\u30010.18\u5fae\u7c73\u6676\u5706\u5236\u7a0b\u6280\u672f\uff0c\u80af\u5b9a\u8fd8\u8981\u518d\u5efa\u4e00\u5ea7\u9ad8\u89c4\u683c\u7684\u5927\u578b\u6676\u5706\u5382\uff0c\u6bd4\u8d77\u7b2c\u4e00\u5ea7\u6676\u5706\u5382\uff0c\u4e2d\u6676\u5fae\u82af\u518d\u5efa\u7b2c\u4e8c\u5ea7\u6676\u5706\u5382\u80fd\u591f\u5f97\u5230\u4e2d\u592e\u4e0e\u5730\u65b9\u7684\u8d44\u91d1\u652f\u6301\u5c31\u76f8\u5bf9\u6709\u9650\uff0c\u5341\u591a\u4ebf\u7f8e\u5143\u7684\u5de8\u989d\u8d44\u91d1\uff0c\u8981\u9526\u6e56\u627f\u62c5\u4e00\u534a\u4ee5\u4e0a\uff0c\u9526\u6e56\u4e5f\u4f1a\u500d\u611f\u538b\u529b\u7684\u3002</p><p>\u3000\u3000\u9526\u6e56\u5546\u4e8b\u524d\u524d\u540e\u540e\u603b\u5171\u62e2\u8fc7\u6765\u8d85\u8fc7\u4e8c\u5341\u4ebf\u7f8e\u5143\u7684\u8d44\u91d1\uff0c\u8fd9\u4e9b\u8d44\u91d1\u770b\u4e0a\u53bb\u5341\u5206\u7684\u5e9e\u5927\uff0c\u5c31\u662f\u5e94\u4ed8\u5df2\u6709\u7684\u9879\u76ee\u8fd8\u662f\u4e0d\u591f\u3002</p><p>\u3000\u3000\u5343\u4e07\u5428\u7ea7\u94a2\u94c1\u4ea7\u4e1a\u57fa\u5730\u7684\u524d\u671f\u586b\u6d77\u9879\u76ee\u5df2\u7ecf\u5b8c\u6210\u8fd1\u534a\uff0c\u63a5\u4e0b\u6765\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u4e0e\u4e1c\u5c71\u94a2\u94c1\u5c31\u8981\u6b63\u5f0f\u7684\u5408\u5e76\u542f\u52a8\u5343\u4e07\u5428\u7ea7\u94a2\u94c1\u4ea7\u4e1a\u57fa\u5730\u7684\u5efa\u8bbe\uff0c\u4e09\u56db\u767e\u4ebf\u7684\u5927\u6295\u8d44\uff0c\u9526\u6e56\u5546\u4e8b\u5c31\u7b97\u5728\u53ea\u536020\uff05\u7684\u80a1\u4efd\uff0c\u4e5f\u8981\u81ea\u884c\u89e3\u51b3\u516d\u4e03\u5341\u4ebf\u7684\u8d44\u91d1\u3002\u8fd8\u6709\u897f\u6fb3\u6d32\u73c0\u65af\u7684\u94c1\u77ff\u9879\u76ee\uff0c\u5341\u4ebf\u7f8e\u5143\u4e5f\u53ea\u662f\u6253\u4e2a\u5e95\u3002</p><p>\u3000\u3000\u5f20\u606a\u73b0\u5728\u4e00\u70b9\u90fd\u4e0d\u96be\u7406\u89e3\u4e09\u661f\u5728\u4e5d\u4e03\u5e74\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u65f6\u4e3a\u4ec0\u4e48\u4f1a\u6b20\u4e0b\u8fd1\u4e24\u767e\u4ebf\u7f8e\u5143\u7684\u5916\u503a\u2014\u2014\u9526\u6e56\u8981\u662f\u6709\u673a\u4f1a\uff0c\u4e5f\u4e0d\u4ecb\u610f\u6b20\u4e0b\u4e24\u767e\u4ebf\u7f8e\u5143\u7684\u5916\u503a\u7528\u4e8e\u6269\u5f20\u3002</p><p>\u3000\u3000\u6b64\u65f6\u7684\u9526\u6e56\u53ea\u5962\u671b\u56fd\u5bb6\u4e0d\u8981\u523b\u610f\u7684\u538b\u5236\uff0c\u4e0d\u5962\u671b\u56fd\u5bb6\u4f1a\u5728\u878d\u8d44\u4e0a\u63d0\u4f9b\u5927\u529b\u7684\u652f\u6301\uff0c\u60f3\u53d1\u5c55\uff0c\u8d44\u91d1\u7684\u95ee\u9898\u8fd8\u662f\u8981\u81ea\u5df1\u89e3\u51b3\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c2\u4e0e\u6280\u672f\u5f15\u8fdb\u6700\u540e\u9636\u6bb5\u7684\u8c08\u5224\u5728\u8fbe\u62c9\u65af\u4e00\u76f4\u505c\u7559\u5230\u516b\u6708\u5e95\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u671f\u95f4\uff0cESS\u4e5f\u6b63\u5f0f\u6302\u724c\u6210\u7acb\u624b\u673a\u82af\u7247\u7814\u7a76\u90e8\u95e8\u5411\u5fb7\u4eea\u6697\u793a\u4ed6\u4eec\u5728\u57fa\u5e26\u82af\u7247\u5f00\u53d1\u5df2\u7ecf\u53d6\u5f97\u5173\u952e\u6027\u7684\u8fdb\u5c55\uff0c\u4e0d\u518d\u4ecb\u610f\u8ba9\u5916\u754c\u77e5\u9053\u4ed6\u4eec\u5bf9\u57fa\u5e26\u82af\u7247\u7684\u91ce\u5fc3\u3002\u867d\u7136\u4e1a\u5185\u5bf9\u9526\u6e56\u80fd\u5426\u6210\u529f\u5f00\u53d1\u51fa\u57fa\u5e26\u82af\u7247\u5145\u6ee1\u4e86\u8d28\u7591\u58f0\uff0c\u4f46\u662f\u8fd9\u79cd\u6697\u793a\u662f\u5411\u5fb7\u4eea\u53d1\u51fa\u7684\u3002</p><p>\u3000\u3000\u5fb7\u4eea\u6700\u7ec8\u540c\u610f\u4ee5\u80a1\u6743\u52a0\u73b0\u91d1\u7684\u6a21\u5f0f\u5411\u4e2d\u6676\u5fae\u82af\u8f6c\u8ba90.25\u30010.18\u5fae\u7c73\u6676\u5706\u5236\u7a0b\u5de5\u827a\u6280\u672f\uff0c\u4e2d\u6676\u5fae\u82af\u83b7\u5f97\u76f8\u5173\u6676\u5706\u5236\u7a0b\u827a\u6280\u672f\u6388\u6743\u8bb8\u53ef\uff0c\u5fb7\u4eea\u5bf9\u4e2d\u6676\u5fae\u82af\u7684\u6301\u80a1\u589e\u52a0\u523015\uff05\u5916\u52a0\u4e94\u5343\u4e07\u7f8e\u5143\u7684\u73b0\u91d1\uff0c\u53e6\u5916\u8fd8\u67095\uff05\u7684\u80a1\u7968\u8ba4\u8d2d\u6743\u9650\u3002</p><p>\u3000\u3000\u8fd9\u8fd8\u53ea\u662f\u7b2c\u4e00\u6b65\uff0c\u4ea4\u6613\u8fd8\u9700\u8981\u5f97\u5230\u5fb7\u4eea\u8463\u4e8b\u4f1a\u4ee5\u53ca\u7f8e\u56fd\u6280\u672f\u51fa\u53e3\u76d1\u7ba1\u90e8\u95e8\u7684\u6279\u51c6\uff0c\u8fd8\u9700\u8981\u4e2d\u6676\u5fae\u82af\u8463\u4e8b\u4f1a\u7684\u6279\u51c6\uff1b\u53e6\u5916\u5728\u56fd\u5185\u518d\u6295\u8d44\u5341\u4ebf\u7f8e\u5143\u4ee5\u4e0a\u5efa\u9020\u4e00\u5ea7\u6676\u5706\u5de5\u5382\uff0c\u4e5f\u9700\u8981\u56fd\u52a1\u9662\u8ba1\u5212\u53d1\u6539\u59d4\u7b49\u90e8\u95e8\u7684\u5ba1\u6279\uff0c\u7b49\u79cd\u79cd\u5ba1\u6279\u624b\u7eed\u90fd\u5b8c\u6210\u4e4b\u540e\uff0c\u8fd9\u7b14\u4ea4\u6613\u624d\u7b97\u6210\u7acb\u3002</p><p>\u3000\u3000\u867d\u7136\u4e0d\u6e05\u695a\u4e8b\u60c5\u4f1a\u4e0d\u4f1a\u6709\u53cd\u590d\uff0c\u6446\u5728\u9526\u6e56\u9762\u524d\u6025\u9700\u8981\u505a\u7684\u4e8b\u60c5\u8fd8\u6709\u5f88\u591a\uff0c\u9996\u5148\u8981\u8bf4\u670d\u65b0\u52a0\u5761\u91d1\u7ba1\u5c40\u4e0e\u4e91\u6e90\u96c6\u56e2\u4e24\u4e2a\u5927\u80a1\u4e1c\u540c\u610f\u589e\u8d44\u5efa\u9020\u7b2c\u4e8c\u5ea7\u6676\u5706\u5382\u3002\u6709\u4e00\u5bb6\u4e0d\u613f\u610f\uff0c\u9526\u6e56\u5c31\u8981\u591a\u627f\u62c5\u4e00\u4e24\u4ebf\u7f8e\u5143\u7684\u8d44\u91d1\u538b\u529b\u3002\u65b0\u52a0\u5761\u91d1\u7ba1\u5c40\u8d44\u91d1\u867d\u7136\u5145\u6c9b\uff0c\u4f46\u662f\u53d7\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u6253\u51fb\u4e4b\u540e\uff0c\u6295\u8d44\u8fd8\u6709\u4e9b\u754f\u624b\u754f\u811a\uff0c\u4e0d\u4e00\u5b9a\u5c31\u613f\u610f\u5bf9\u4e2d\u6676\u5fae\u82af\u7247\u589e\u8d44\uff1b\u4e91\u6e90\u96c6\u56e2\u6b63\u5904\u4e8e\u4ea7\u4e1a\u7ed3\u6784\u8c03\u6574\u7684\u5f53\u513f\uff0c\u8d44\u91d1\u538b\u529b\u6bd4\u8f83\u5927\uff1b\u53e6\u5916\uff0c\u4e2d\u6676\u5fae\u82af\u6301\u80a1\u7ba1\u7406\u5c42\u96c6\u4f53\u4e5f\u62ff\u4e0d\u51fa\u591a\u5c11\u8d44\u91d1\u51fa\u6765\uff1b\u5fb7\u4eea\u5012\u662f\u4e0d\u4ecb\u610f\u5230\u65f6\u5019\u6309\u7167\u6301\u80a1\u6bd4\u4f8b\u6ce8\u8d44\uff0c\u4f46\u662f\u5fb7\u4eea\u603b\u5171\u624d\u6301\u670915\uff05\u7684\u80a1\u6743\uff1b\u53e6\u591685\uff05\u7684\u538b\u529b\u51e0\u4e4e\u90fd\u538b\u5728\u9526\u6e56\u7684\u5934\u4e0a\u3002</p></article>"}}}]);
