(window.webpackJsonp=window.webpackJsonp||[]).push([[640],{663:function(p){p.exports={chapterTitle:null,title:"\u7b2c639\u7ae0 \u98ce\u96e8\u98d8\u6447",content:"<article><h2>\u7b2c639\u7ae0 \u98ce\u96e8\u98d8\u6447</h2><p>\u3000\u3000\u62d4\u51fa\u841d\u535c\u8fde\u7740\u6ce5\uff0c\u8c2d\u4e91\u677e\u9886\u5bfc\u65b0\u52a0\u5761\u56fd\u7acb\u5927\u5b66\u4e0e\u5bcc\u58eb\u901a\u96c6\u56e2\u8054\u5408\u521b\u7acb\u7684\u7535\u5b50\u6280\u672f\u5b9e\u9a8c\u5ba4\u591a\u5e74\uff0c\u5728\u4e1c\u5357\u4e9a\u5b66\u672f\u79d1\u7814\u9886\u57df\u5185\u58f0\u671b\u3001\u4eba\u8109\u6df1\u539a\uff0c\u8ba9\u4ed6\u5230\u5efa\u90ba\u9886\u5bfc\u6a61\u6811\u56ed\u5fae\u7535\u5b50\u57fa\u7840\u5b9e\u9a8c\u5ba4\uff0c\u81ea\u7136\u5c31\u5e0c\u671b\u4ed6\u80fd\u5728\u65b0\u52a0\u5761\u3001\u5728\u4e1c\u5357\u4e9a\u5c31\u4eb2\u81ea\u642d\u5efa\u8d77\u6574\u4e2a\u5b9e\u9a8c\u5ba4\u79d1\u7814\u56e2\u961f\u7684\u4e3b\u4f53\u6846\u67b6\u6765\uff0c\u7136\u540e\u4ece\u56fd\u5185\u5bfb\u627e\u4f18\u79c0\u7684\u6280\u672f\u4eba\u624d\u586b\u8865\u5230\u8fd9\u4e2a\u6846\u67b6\u4e4b\u4e2d\uff0c\u5229\u7528\u4e09\u5230\u4e94\u5e74\u7684\u65f6\u95f4\uff0c\u57f9\u517b\u51fa\u4e00\u5927\u6279\u4f18\u79c0\u7684\u7814\u53d1\u4eba\u5458\uff0c\u521b\u9020\u6027\u7684\u4e0e\u4e1c\u5927\u7b49\u9ad8\u6821\u8054\u5408\u63a8\u884c\u53cc\u5bfc\u5e08\u5236\uff0c\u50a8\u5907\u4e00\u6279\u540e\u5907\u4eba\u624d\uff0c\u6574\u4e2a\u5b9e\u9a8c\u5ba4\u7684\u4eba\u624d\u5efa\u8bbe\u6846\u67b6\u624d\u80fd\u7b97\u521d\u6b65\u7684\u5b8c\u5584\u8d77\u6765\u3002</p><p>\u3000\u3000\u540c\u65f6\uff0c\u5f20\u606a\u5e0c\u671b\u5f15\u8fdb\u4f18\u79c0\u6d77\u5916\u534e\u4eba\u79d1\u5b66\u5bb6\u4e0e\u6280\u672f\u7814\u53d1\u4eba\u5458\u7684\u201c\u767e\u4eba\u884c\u52a8\u201d\u8ba1\u5212\uff0c\u80fd\u540c\u6837\u5bf9\u6676\u5706\u5382\u6295\u8d44\u9879\u76ee\u6709\u5e2e\u52a9\u3002\u6bd5\u7adf\u8981\u4ece\u53f0\u79ef\u7535\u501f\u9053\u5728\u9a6c\u6765\u897f\u4e9a\u6ce8\u518c\u8bbe\u7acb\u7684\u79d1\u6280\u516c\u53f8\u5f15\u5165\u5927\u91cf\u7684\u5de5\u7a0b\u6280\u672f\u4eba\u5458\uff0c\u4e5f\u8981\u8f83\u5f7b\u5e95\u89e3\u9664\u8fd9\u4e9b\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u7684\u540e\u987e\u4e4b\u5fe7\u3002</p><p>\u3000\u3000\u8c2d\u4e91\u677e\u592b\u5987\u8f6c\u4e58\u4e0b\u5348\u7684\u98de\u673a\u56de\u65b0\u52a0\u5761\uff0c\u5f20\u606a\u5c06\u4ed6\u4eec\u63a5\u5230\u4e2d\u73af\u7684\u9152\u5e97\u7a0d\u4f5c\u4f11\u606f\uff0c\u7528\u8fc7\u9910\u4e4b\u540e\u518d\u4e13\u7a0b\u9001\u4ed6\u4eec\u8fd4\u56de\u673a\u573a\u642d\u4e58\u98de\u673a\u79bb\u5f00\u9999\u6e2f\uff0c\u5728\u673a\u573a\u7b49\u5019\u90ed\u6c0f\u4e91\u6e90\u96c6\u56e2\u8463\u4e8b\u5c40\u4e3b\u5e2d\u90ed\u677e\u5ef6\u4e00\u884c\u4eba\u7684\u5230\u6765\uff1b\u5b59\u5c1a\u4e49\u4e5f\u5728\u4e0b\u5348\u4ece\u5efa\u90ba\u8f6c\u673a\u98de\u56de\u9999\u6e2f\uff0c\u5b59\u5c1a\u4e49\u6bd4\u90ed\u677e\u5ef6\u7b49\u4eba\u65e9\u4e00\u523b\u62b5\u8fbe\u542f\u5fb7\u673a\u573a\u3002</p><p>\u3000\u3000\u8d8a\u79c0\u63a7\u80a1\u6ce8\u8d44\u9999\u96ea\u6d77\u5df2\u4e0e\u60e0\u5c71\u5e02\u653f\u5e9c\u8fbe\u6210\u534f\u8bae\uff0c\u7531\u8d8a\u79c0\u63a7\u80a1\u6ce8\u8d44\u516b\u4e2a\u4ebf\uff0c\u83b7\u53d651\uff05\u7684\u80a1\u6743\uff0c\u52a0\u4e0a\u7231\u8fbe\u7535\u5b50\u5bf9\u9999\u96ea\u6d77\u6240\u6301\u7684\u80a1\u6743\uff0c\u8d8a\u79c0\u5b9e\u9645\u5bf9\u9999\u96ea\u6d77\u6301\u80a1\u8d85\u8fc775\uff05\u3002</p><p>\u3000\u3000\u4e3a\u4e86\u8fd9\u4e8b\uff0c\u5b59\u5c1a\u4e49\u5728\u60e0\u5c71\u524d\u540e\u5dee\u4e0d\u591a\u8c08\u4e86\u534a\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u5f20\u606a\u5c31\u6709\u524d\u4e9b\u5929\u5230\u60e0\u5c71\u8f6c\u4e86\u4e00\u5708\uff0c\u4eba\u5c31\u53c8\u56de\u5efa\u90ba\u4eab\u53d7\u60a0\u95f2\u81ea\u5f97\u7684\u5927\u5b66\u751f\u6d3b\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u7cbe\u795e\u6296\u64de\uff0c\u4e24\u9b13\u7684\u53d1\u4e1d\u6709\u4e9b\u67d3\u767d\uff0c\u770b\u5230\u7ad9\u5728\u660e\u4eae\u7684\u63a5\u673a\u5927\u5385\u91cc\u7684\u5f20\u606a\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8d76\u7740\u8fd9\u4e48\u6025\uff0c\u6211\u7ecf\u8fc7\u5efa\u90ba\u5c31\u62bd\u5f97\u51fa\u65f6\u95f4\u966a\u9759\u6aac\u5403\u4e86\u4e00\u987f\u4e2d\u996d\uff0c\u8ba9\u5979\u597d\u4e00\u987f\u57cb\u6028\uff0c\u4e0d\u6653\u5f97\u9152\u5427\u4ec0\u4e48\u65f6\u5019\u80fd\u8425\u4e1a\uff0c\u6211\u8fd8\u8981\u8d76\u56de\u53bb\u7ed9\u5979\u6367\u573a\uff0c\u7b97\u662f\u966a\u7f6a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e2a\u554a\uff0c\u6ca1\u6709\u4e09\u4e24\u4e2a\u6708\u4e0d\u5f97\u6210\uff0c\u90a3\u4e48\u9065\u8fdc\u7684\u4e8b\u60c5\uff0c\u8c01\u4f1a\u53bb\u60f3\uff1f\u201d\u5f20\u606a\u54a7\u7740\u5634\uff0c\u8ddf\u5b59\u5c1a\u4e49\u7b11\u7740\u8bf4\u8bdd\uff0c\u201c\u6211\u4eca\u5929\u5c31\u5fd9\u7740\u63a5\u673a\u4e86\uff0c\u8fd8\u5f97\u5c06\u5b59\u5148\u751f\u60a8\u63a5\u56de\u6765\u4e3b\u6301\u5de5\u4f5c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c31\u7559\u4f60\u5728\u5b66\u6821\u91cc\u9b3c\u6df7\uff1f\u201d\u5b59\u5c1a\u4e49\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u9a6c\u54c8\u8482\u5c14\uff0c\u54ea\u9700\u8981\u6574\u65e5\u5fd9\u788c\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u4e24\u5929\uff0c\u4e16\u754c\u94f6\u884c\u4e0e\u56fd\u9645\u8d27\u5e01\u57fa\u91d1\u7ec4\u7ec7\u5728\u9999\u6e2f\u4e3e\u529e\u4e00\u4e2a\u7814\u8ba8\u4f1a\uff0c\u5927\u9a6c\u7684\u9996\u76f8\u9a6c\u54c8\u8482\u5c14\u5c31\u5728\u7a0d\u524d\u4e00\u6bb5\u65f6\u95f4\u4e58\u4e13\u673a\u62b5\u8fbe\u9999\u6e2f\uff0c\u5f20\u606a\u8fd8\u5f97\u77e5\u7d22\u7f57\u65af\u4e5f\u4f1a\u5728\u8fd9\u4e2a\u7814\u8ba8\u4f1a\u7684\u9996\u65e5\u5927\u4f1a\u4e0a\u6f14\u8bb2\u3002\u9a6c\u54c8\u8482\u5c14\u5bf9\u7d22\u7f57\u65af\u6068\u4e4b\u5165\u9aa8\uff0c\u4ece\u4e1c\u5357\u4e9a\u7206\u53d1\u91d1\u878d\u98ce\u66b4\u4ee5\u6765\uff0c\u6ce8\u91cd\u5a01\u6743\u7684\u9a6c\u54c8\u8482\u5c14\u591a\u6b21\u516c\u5f00\u6307\u8d23\u7d22\u7f57\u65af\u8fd9\u4e9b\u8d2a\u5a6a\u7684\u56fd\u9645\u8d27\u5e01\u6295\u8d44\u8005\uff0c\u53c8\u524d\u4e9b\u5929\u66fc\u8c37\u7684\u4e9a\u592a\u8d22\u957f\u8054\u5e2d\u4f1a\u5e2d\uff0c\u518d\u6b21\u58f0\u660e\u7d22\u7f57\u65af\u8fd9\u4e9b\u56fd\u9645\u8d27\u5e01\u6295\u8d44\u8005\u90fd\u662f\u4e0d\u53d7\u9a6c\u6765\u897f\u4e9a\u6b22\u8fce\u7684\u4eba\u3002</p><p>\u3000\u3000\u5948\u4f55\u9a6c\u54c8\u8482\u5c14\u8bf4\u72e0\u8bdd\u7684\u673a\u4f1a\uff0c\u53d1\u6325\u7684\u4f5c\u7528\u5374\u4e0d\u5927\uff0c\u4e24\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u9a6c\u6765\u897f\u4e9a\u8d27\u5e01\u8d2c\u503c\u8d85\u8fc730\uff05\uff0c\u5f53\u7136\u8fd8\u4e0d\u662f\u4e1c\u5357\u4e9a\u6700\u51c4\u60e8\u7684\u56fd\u5bb6\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u90ed\u677e\u5ef6\u4e58\u5750\u7684\u98de\u673a\u968f\u540e\u62b5\u8fbe\u673a\u573a\uff0c\u4e0a\u6b21\u76f8\u89c1\u662f\u5728\u4e00\u4e2a\u6708\u524d\uff0c\u6b64\u65f6\u7684\u90ed\u677e\u5ef6\u7565\u663e\u75b2\u6001\uff0c\u4e0b\u773c\u76ae\u5b50\u6709\u4e9b\u6d6e\u80bf\uff0c\u5b59\u5c1a\u4e49\u70ed\u5207\u7684\u8d70\u8fc7\u53bb\u633d\u7740\u4ed6\u7684\u624b\uff1a\u201c\u90ed\u5148\u751f\u6ca1\u6709\u8fd9\u4e9b\u5929\u6ca1\u6709\u4f11\u606f\u597d\uff1f\u201d</p><p>\u3000\u3000\u90ed\u6c0f\u5728\u4e1c\u5357\u4e9a\u534e\u5546\u5708\u5185\u5730\u4f4d\u663e\u8d6b\uff0c\u6bd4\u8d77\u845b\u5bb6\u7684\u5730\u4f4d\u4e0d\u76f8\u8ba9\uff1b\u845b\u5bb6\u81ea\u845b\u666f\u8bda\u53bb\u4e16\u4e4b\u540e\uff0c\u5c31\u5206\u5d29\u79bb\u6790\uff0c\u845b\u660e\u4fe1\u867d\u7136\u6293\u4f4f\u5609\u4fe1\u7535\u5b50\u3001\u5609\u4fe1\u5730\u4ea7\u8fd9\u4e24\u5904\u5927\u57fa\u4e1a\uff0c\u5948\u4f55\u5f71\u54cd\u529b\u90fd\u65e0\u6cd5\u8ddf\u845b\u666f\u8bda\u65f6\u671f\u7684\u845b\u5bb6\u76f8\u63d0\u5e76\u8bba\u3002\u90ed\u6c0f\u96c6\u56e2\u5230\u90ed\u677e\u5ef6\u624b\u91cc\u4e5f\u662f\u7b2c\u4e8c\u4ee3\uff0c\u4e0d\u8fc7\u90ed\u677e\u5ef6\u7684\u7236\u4eb2\u901d\u4e16\u524d\u8f83\u597d\u7684\u5904\u7406\u4e86\u5bb6\u65cf\u7684\u7ee7\u7eed\u95ee\u9898\uff0c\u90ed\u6c0f\u96c6\u56e2\u8fd8\u80fd\u8f83\u7d27\u5bc6\u7684\u805a\u96c6\u6210\u90ed\u677e\u5ef6\u5468\u56f4\u3002</p><p>\u3000\u3000\u773c\u4e0b\u538b\u529b\u4e5f\u4e0d\u5c11\uff0c\u5174\u76db\u65f6\uff0c\u6ca1\u6709\u8c01\u4f1a\u8df3\u51fa\u6765\u8bf4\u8bdd\uff0c\u8fd9\u65f6\u5019\u7ecf\u6d4e\u5371\u673a\u8513\u5ef6\uff0c\u5bb6\u65cf\u91cc\u96be\u514d\u6709\u4eba\u53d1\u7262\u9a9a\u8d23\u5907\u90ed\u677e\u5ef6\u6cbb\u4f01\u4e0d\u5584\u3001\u5c06\u5927\u73af\u5883\u7684\u95ee\u9898\u5f52\u7ed3\u4e86\u90ed\u677e\u5ef6\u4e2a\u4eba\u7684\u5934\u4e0a\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u80fd\u4f11\u606f\u597d\uff1f\u201d\u90ed\u677e\u5ef6\u82e6\u7b11\u7684\u8bf4\uff0c\u201c\u8fd9\u4e24\u5929\u7684\u5e02\u51b5\uff0c\u90fd\u4e0d\u5fcd\u76ee\u7779\u4e86\u2026\u2026\u201d\u53c8\u4e0e\u5f20\u606a\u63e1\u624b\uff0c\u611f\u6168\u9053\uff0c\u201c\u4e0d\u80fd\u63d0\u524d\u6570\u6708\u8ddf\u4f60\u76f8\u89c1\uff0c\u771f\u662f\u6094\u4e4b\u65e0\u53ca\u2026\u2026\u201d</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u4e94\u6708\u501f\u58f3\u4e0a\u5e02\u3001\u5b59\u5c1a\u4e49\u516d\u6708\u4e2d\u4e0a\u65ec\u5168\u9762\u51cf\u6301\u5609\u4fe1\u5730\u4ea7\u7684\u80a1\u7968\uff0c\u65e0\u4e00\u4e0d\u662f\u6781\u51c6\u7684\u8e29\u4e2d\u91d1\u878d\u5e02\u573a\u6ce2\u6f9c\u4e0d\u5b9a\u7684\u8282\u62cd\uff0c\u50cf\u7231\u8fbe\u7535\u5b50\u4e03\u6708\u4e2d\u65ec\u5229\u7528\u4e0a\u5e02\u516c\u53f8\u516c\u544a\u7684\u5f62\u5f0f\u9996\u6b21\u5bf9\u4e1c\u5357\u4e9a\u7ecf\u6d4e\u5371\u673a\u505a\u51fa\u4e1a\u7ee9\u9884\u8b66\uff0c\u90fd\u8ba9\u53f9\u670d\u3002</p><p>\u3000\u3000\u90ed\u677e\u5ef6\u6709\u4e9b\u4e5f\u6709\u60f3\uff0c\u53ea\u6709\u80fd\u63d0\u524d\u6708\u4f59\u505a\u4e9b\u51c6\u5907\uff0c\u7edd\u4e0d\u81f3\u4e8e\u6b64\u65f6\u8fd9\u4e48\u72fc\u72c8\u4e0d\u582a\u3002</p><p>\u3000\u3000\u5f20\u606a\u6de1\u6de1\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u9a6c\u7684\u7ecf\u6d4e\u8981\u597d\u8fc7\u5370\u3001\u83f2\u3001\u6cf0\u4e09\u56fd\uff0c\u7ed9\u4eba\u5174\u98ce\u4f5c\u6d6a\u53d7\u5230\u7275\u7d2f\uff0c\u4e5f\u5c31\u4e00\u4e24\u5e74\u7684\u5149\u666f\u96be\u6328\uff0c\u76f8\u4fe1\u5f88\u5feb\u5c31\u4f1a\u98ce\u6b62\u96e8\u6563\u3002\u4e0d\u7136\uff0c\u9526\u6e56\u4e5f\u4e0d\u4f1a\u6025\u5207\u8ddf\u90ed\u5148\u751f\u4f60\u4eec\u5408\u4f5c\uff0c\u5c31\u662f\u60f3\u8d81\u7740\u8fd9\u673a\u4f1a\uff0c\u5360\u90ed\u5148\u751f\u4f60\u4eec\u4e00\u4e9b\u4fbf\u5b9c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\u2026\u2026\u201d\u90ed\u677e\u5ef6\u89c1\u5f20\u606a\u5c11\u5e74\u65e0\u8d56\u7684\u5c06\u5360\u4fbf\u5b9c\u7684\u5fc3\u601d\u6446\u4e86\u8138\u4e0a\uff0c\u5fc3\u4e0a\u7684\u79ef\u90c1\u987f\u65f6\u8f7b\u677e\u4e0d\u5c11\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9a6c\u6765\u897f\u4e9a\u7684\u7ecf\u6d4e\u5371\u673a\u8fd8\u6709\u8fdb\u4e00\u6b65\u7684\u8513\u5ef6\uff0c\u56fd\u9645\u91d1\u878d\u3001\u4ea7\u4e1a\u8d44\u672c\u6b63\u52a0\u901f\u4ece\u4e1c\u5357\u4e9a\u5730\u533a\u64a4\u79bb\u3002</p><p>\u3000\u3000\u5bf9\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u4e3b\u8981\u8d44\u672c\u8f93\u51fa\u56fd\u662f\u65e5\u672c\uff0c\u65e5\u672c\u7684\u91d1\u878d\u673a\u6784\u7ecf\u5386\u516b\u5341\u5e74\u4ee3\u540e\u671f\u7ecf\u6d4e\u6ce1\u6cab\u7684\u7834\u706d\u4e0e\u5d29\u6e83\u8fd8\u6ca1\u6709\u591a\u4e45\uff0c\u5bf9\u6ce1\u6cab\u7ecf\u6d4e\u5371\u673a\u72b9\u5982\u60ca\u5f13\u4e4b\u9e1f\uff0c\u91d1\u878d\u98ce\u66b4\u9996\u5148\u5728\u6cf0\u56fd\u7206\u53d1\u4e4b\u540e\uff0c\u771f\u6b63\u5bf9\u4e1c\u5357\u4e9a\u5730\u533a\u7ecf\u6d4e\u6301\u7eed\u9020\u6210\u5de8\u5927\u6467\u6b8b\u7684\uff0c\u6070\u6070\u662f\u4ee5\u65e5\u672c\u91d1\u878d\u673a\u6784\u4e3a\u9996\u7684\u8d44\u672c\u5927\u64a4\u79bb\u3002</p><p>\u3000\u3000\u50cf\u65b0\u52a0\u5761\u65b0\u57ce\u96c6\u56e2\u4ece\u9526\u57ce\u5730\u4ea7\u64a4\u51fa\u8d44\u672c\uff0c\u4e0d\u8fc7\u662f\u88ab\u6ce2\u53ca\u5230\u7684\u4e00\u4e2a\u5c0f\u5c0f\u7f29\u5f71\u800c\u5df2\u3002</p><p>\u3000\u3000\u90ed\u6c0f\u4e91\u6e90\u96c6\u56e2\u8fd9\u6bb5\u65f6\u95f4\u4e5f\u6b63\u7ecf\u5386\u7740\u8d44\u4ea7\u7f29\u6c34\u7684\u9635\u75db\uff0c\u6700\u9f0e\u76db\u65f6\u671f\uff0c\u5f52\u5c5e\u90ed\u6c0f\u5bb6\u65cf\u65d7\u4e0b\u7684\u8d44\u4ea7\u603b\u503c\u9ad8\u8fbe\u56db\u5341\u4ebf\u7f8e\u5143\uff0c\u77ed\u77ed\u4e24\u4e09\u4e2a\u6708\uff0c\u7f29\u6c34\u8d85\u8fc7\u4e09\u5206\u4e4b\u4e00\uff0c\u800c\u4e14\u770b\u60c5\u5f62\uff0c\u60c5\u52bf\u8fd8\u4f1a\u6076\u5316\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u8d44\u4ea7\u6709\u65f6\u5019\u53ea\u662f\u6570\u5b57\u6e38\u620f\u800c\u5df2\uff0c\u5173\u952e\u8981\u80fd\u8ba9\u4f01\u4e1a\u8f7b\u677e\u6e21\u8fc7\u5f53\u524d\u7684\u56f0\u5883\u3002</p><p>\u3000\u3000\u6bd4\u8d77\u589e\u53d1\u5e76\u8d2d\u4e4b\u540e\u7684\u6700\u9ad8\u767e\u4ebf\u5e02\u503c\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u7f29\u6c34\u60c5\u51b5\u4e5f\u76f8\u5f53\u4e25\u91cd\uff0c\u53ea\u53ca\u6700\u9ad8\u5cf0\u65f6\u516d\u4e03\u6210\uff0c\u4e0d\u8fc7\u7231\u8fbe\u7535\u5b50\u501f\u589e\u5e76\u8d2d\u4e4b\u9645\uff0c\u989d\u5916\u83b7\u5f97\u516d\u4ebf\u6e2f\u5143\u7684\u8d44\u91d1\u4e00\u76f4\u6ca1\u6709\u52a8\u7528\uff0c\u80a1\u4ef7\u8d77\u8d77\u843d\u843d\uff0c\u5bf9\u6295\u8d44\u8005\u6709\u76f8\u5f53\u5927\u7684\u5f71\u54cd\uff0c\u5bf9\u7231\u8fbe\u7535\u5b50\u7684\u65e5\u5e38\u8fd0\u8425\uff0c\u5374\u662f\u4e1d\u6beb\u4e0d\u53d7\u5f71\u54cd\u7684\u3002</p><p>\u3000\u3000\u90ed\u6c0f\u4e91\u6e90\u96c6\u56e2\u5374\u4e0d\u540c\uff0c\u5728\u8fc7\u53bb\u51e0\u5e74\u65f6\u95f4\u91cc\uff0c\u9a6c\u6765\u897f\u4e9a\u7684\u7ecf\u6d4e\u53d1\u5c55\u975e\u5e38\u7684\u8fc5\u901f\uff0c\u90ed\u6c0f\u4ece\u5916\u8d44\u91d1\u878d\u673a\u6784\u624b\u91cc\u501f\u5f97\u5927\u91cf\u8d37\u6b3e\u7528\u4e8e\u53d1\u5c55\u65d7\u4e0b\u4f01\u4e1a\uff0c\u7ecf\u6d4e\u5371\u673a\u6765\u4e34\uff0c\u5230\u671f\u7684\u503a\u52a1\u8981\u5f52\u8fd8\uff0c\u5374\u65e0\u6cd5\u7ee7\u7eed\u4ece\u8fd9\u4e9b\u91d1\u878d\u673a\u6784\u501f\u51fa\u8d37\u6b3e\u6765\u7ef4\u6301\u6d41\u52a8\u8d44\u91d1\u7684\u5145\u8db3\u91cf\u3002</p><p>\u3000\u3000\u4fdd\u8bc1\u5145\u8db3\u7684\u73b0\u91d1\uff0c\u662f\u8bb8\u591a\u4f01\u4e1a\u5bb6\u7c4d\u4e4b\u6e21\u8fc7\u7ecf\u6d4e\u5371\u673a\u7684\u4e0d\u4e8c\u6cd5\u95e8\uff0c\u90ed\u677e\u5ef6\u4e5f\u662f\u5982\u6b64\u7684\u6001\u5ea6\uff0c\u4e0d\u6653\u5f97\u7ecf\u6d4e\u5371\u673a\u8fd8\u8981\u6301\u7eed\u591a\u5c11\u65f6\u95f4\uff0c\u63a7\u5236\u73b0\u91d1\u6d41\u662f\u6700\u9700\u8981\u6ce8\u610f\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u9526\u6e56\u63d0\u51fa\u8981\u5bf9\u4e91\u6e90\u96c6\u56e2\u65d7\u4e0b\u7684\u5236\u6d46\u4f01\u4e1a\u8fdb\u884c\u80a1\u6743\u6295\u8d44\uff0c\u4e0d\u725f\u53d6\u63a7\u80a1\u5730\u4f4d\uff0c\u65e0\u7591\u662f\u5f88\u53d7\u90ed\u6c0f\u6b22\u8fce\u7684\u3002</p><p>\u3000\u3000\u90ed\u6c0f\u80fd\u591f\u4ece\u80a1\u6743\u4ea4\u6613\u4e2d\u56de\u7b3c\u8fd1\u4ebf\u7f8e\u5143\u7684\u8d44\u91d1\uff0c\u8fd9\u662f\u4e00\u65b9\u9762\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u4e0e\u9526\u6e56\u5c55\u5f00\u5408\u4f5c\uff0c\u8fd8\u53ef\u4ee5\u8d62\u5f97\u65b0\u5149\u7eb8\u4e1a\u8fd9\u4e48\u4e00\u4e2a\u7a33\u5b9a\u7684\u5927\u5ba2\u6237\uff0c\u4e8c\u671f\u5de5\u7a0b\u5b8c\u6210\u540e\u7684\u65b0\u5149\u7eb8\u4e1a\u51e0\u4e4e\u80fd\u6d88\u5316\u4e91\u6e90\u5236\u6d46\u4e00\u534a\u7684\u4ea7\u80fd\uff0c\u4e0d\u7528\u62c5\u5fc3\u7ecf\u6d4e\u5371\u673a\u540e\u7eb8\u5236\u9700\u6c42\u7684\u840e\u7f29\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8c01\u80fd\u610f\u8bc6\u5230\u5370\u5c3c\u7684\u68ee\u6797\u5927\u706b\u718a\u718a\u71c3\u70e7\u4e86\u4e00\u4e2a\u591a\u6708\u540e\u8fd8\u8981\u6301\u7eed\u4e09\u4e2a\u6708\u624d\u4f1a\u9010\u6e10\u7184\u706d\uff0c\u8fd9\u573a\u5927\u706b\u76f4\u63a5\u5bfc\u81f4\u4e9a\u6d32\u7eb8\u6d46\u4f9b\u5e94\u5403\u7d27\uff0c\u4fc3\u4f7f\u4e9a\u6d32\u7eb8\u6d46\u4ee5\u53ca\u7eb8\u5f20\u4ef7\u683c\u6301\u7eed\u4e0a\u6da8\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9080\u8bf7\u90ed\u677e\u5ef6\u5230\u9999\u6e2f\u7684\u4e3b\u8981\u76ee\u7684\uff0c\u4e0d\u662f\u5bf9\u4e91\u6e90\u5236\u6d46\u7684\u80a1\u6743\u6295\u8d44\uff0c\u800c\u662f\u8fdb\u4e00\u6b65\u5546\u91cf\u6676\u5706\u5382\u6295\u8d44\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u4ece\u673a\u573a\u63a5\u90ed\u677e\u5ef6\u4e00\u884c\u5b89\u6392\u5165\u4f4f\u6e2f\u5c9b\u9999\u683c\u91cc\u62c9\u5927\u9152\u5e97\uff0c\u66ae\u8272\u5df2\u669d\uff0c\u5230\u4e0b\u5348\u65f6\uff0c\u9999\u6e2f\u7684\u5929\u7a7a\u6536\u6674\uff0c\u5929\u9645\u8fd8\u6b8b\u7559\u7740\u4e00\u4e1d\u8273\u7ea2\u7470\u5947\u7684\u665a\u971e\u3002</p><p>\u3000\u3000\u8f66\u8fc7\u4e2d\u73af\u65f6\uff0c\u524d\u65b9\u5927\u585e\u8f66\uff0c\u4e0d\u6653\u5f97\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u7ad9\u5728\u8f66\u5b50\u8fce\u5bbe\u8e0f\u677f\u4e0a\u5f80\u524d\u5f20\u671b\uff0c\u770b\u4f3c\u4e00\u5ea7\u767e\u8d27\u5546\u573a\u5efa\u7b51\u524d\u7684\u62e5\u5835\u4e86\u65e0\u6570\u7684\u9999\u6e2f\u5e02\u6c11\u3002</p><p>\u3000\u3000\u62e6\u7740\u770b\u8fc7\u70ed\u95f9\u5f80\u56de\u8d70\u7684\u8def\u4eba\uff0c\u624d\u6653\u5f97\u6709\u98ce\u58f0\u8bf4\u90a3\u5bb6\u65e5\u672c\u4eba\u6295\u8d44\u7684\u767e\u8d27\u5546\u573a\u5f88\u5feb\u5c31\u8981\u57ae\u6389\uff0c\u8fd9\u5bb6\u5546\u573a\u91cc\u6709\u4e00\u5bb6\u86cb\u7cd5\u5e97\uff0c\u9999\u6e2f\u4eba\u559c\u6b22\u5c06\u86cb\u7cd5\u5238\u9001\u4eba\u505a\u793c\u7269\uff0c\u542c\u5230\u5546\u5e97\u8981\u5012\u95ed\u7684\u6d88\u606f\uff0c\u90a3\u4e9b\u624b\u91cc\u6709\u86cb\u7cd5\u5238\u7684\u4eba\u81ea\u7136\u8981\u8d76\u8fc7\u6765\u5c06\u793c\u5238\u6362\u5728\u86cb\u7cd5\u624d\u80fd\u7f62\u4f11\uff0c\u867d\u66fe\u60f3\u5230\u7adf\u6709\u6570\u5343\u4eba\u95fb\u98ce\u800c\u52a8\uff0c\u5dee\u70b9\u5c06\u86cb\u7cd5\u5e97\u90fd\u6324\u7206\u4e86\uff0c\u86cb\u7cd5\u5e97\u7684\u804c\u5458\u5df2\u7ecf\u5413\u5f97\u8eb2\u8d77\u6765\uff0c\u8b66\u5bdf\u9a7b\u5b88\u8fc7\u6765\u758f\u6563\u4eba\u7fa4\u3002</p><p>\u3000\u3000\u5835\u5728\u8fd9\u6bb5\u8def\u4e0a\u5dee\u4e0d\u591a\u8fd1\u4e00\u4e2a\u5c0f\u65f6\u624d\u5f97\u4ee5\u901a\u8fc7\uff0c\u5374\u662f\u5728\u51fa\u673a\u573a\u4e4b\u540e\uff0c\u6d3e\u53bb\u6e2f\u5927\u63a5\u5510\u5a67\u7684\u8f66\u5148\u62b5\u8fbe\u9999\u683c\u91cc\u62c9\u9152\u5e97\u3002</p><p>\u3000\u3000\u4e1c\u5357\u4e9a\u5c38\u6a2a\u904d\u91ce\uff0c\u9999\u6e2f\u8fd9\u8fb9\u4e5f\u98ce\u96e8\u98d8\u6447\u3002</p></article>"}}}]);
